INSERT INTO category (id, name, basket) Select 'annya_chanting_for_meditators', 'Chanting', 'annya' WHERE NOT EXISTS(SELECT 1 FROM category WHERE id = 'annya_chanting_for_meditators');
INSERT INTO books (id, basket, category, name, firstpage, lastpage, pagecount) VALUES ('annya_chanting_for_meditators', 'annya', 'annya_chanting_for_meditators', 'Chanting_for_Meditators', 1, 76, 76);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Daily Morning Chants ', 'title', 1);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Anekajāti Pāḷi ', 'title', 1);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 1, '<h3>Daily Morning Chants </h3><p class="p3"><span class="t9"> </span></p><h4>Anekajāti Pāḷi </h4><p class="p3"><span class="t10">Through Countless Births Text</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Namo tassa Bhagavato Arahato Sammāsambuddhassa</span><span class="t5"> (3x)</span></p><p class="p3"><span class="t1">Reverence to him, the Fortunate One, the Worthy One, the Perfect Sambuddha </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"><span class="t5">Anekajātisaṁsāraṁ sandhāvissaṁ anibbisaṁ</span> </span></p><p class="p3"><span class="t1">Through countless births in saṁsāra I have wandered without finding </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gahakārakaṁ gavesanto: dukkhā jāti punappunaṁ.</span></p><p class="p3"><span class="t1">the housebuilder I was seeking: born and suffering again and again. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gahakāraka diṭṭhosi! Puna gehaṁ na kāhasi:</span></p><p class="p3"><span class="t1">O housebuilder, now you are seen! You will not build the house again: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbā te phāsukā bhaggā, gahakūṭaṁ visaṅkhataṁ,</span></p><p class="p3"><span class="t1">all your rafters have been broken, and the ridgepole has been destroyed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">visaṅkhāragataṁ cittaṁ, taṇhānaṁ khayam-ajjhagā.</span></p><p class="p3"><span class="t1">my mind has reached the unconditioned, and craving’s end has been achieved. </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Iti imasmiṁ sati idaṁ hoti; imassuppādā idaṁ uppajjati,</span></p><p class="p3"><span class="t1">This being so, that is; from the arising of this, that arises, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yadidaṁ: avijjāpaccayā saṅkhārā,</span></p><p class="p3"><span class="t1">that is to say: with ignorance as condition there are (volitional) processes, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saṅkhārapaccayā viññāṇaṁ,</span></p><p class="p3"><span class="t1">with (volitional) processes as condition: consciousness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">viññāṇapaccayā nāmarūpaṁ,</span></p><p class="p3"><span class="t1">with consciousness as condition: mind and body, </span></p>', '-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 2, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāmarūpapaccayā saḷāyatanaṁ,</span></p><p class="p3"><span class="t1">with mind and body as condition: the six sense spheres, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saḷāyatanapaccayā phasso,</span></p><p class="p3"><span class="t1">with the six sense spheres as condition: contact, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">phassapaccayā vedanā,</span></p><p class="p3"><span class="t1">with contact as condition: feeling, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vedanāpaccayā taṇhā,</span></p><p class="p3"><span class="t1">with feeling as condition: craving, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṇhāpaccayā upādānaṁ,</span></p><p class="p3"><span class="t1">with craving as condition: attachment, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upādānapaccayā bhavo,</span></p><p class="p3"><span class="t1">with attachment as condition: continuation, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhavapaccayā jāti,</span></p><p class="p3"><span class="t1">with continuation as condition: birth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">jātipaccayā jarāmaraṇaṁ,</span></p><p class="p3"><span class="t1">with birth as condition: old age, death, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sokaparidevadukkhadomanassupāyāsā sambhavanti,</span></p><p class="p3"><span class="t1">grief, lamentation, pain, sorrow, and despair (all) arise, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evam-etassa kevalassa dukkhakkhandhassa samudayo hoti.</span></p><p class="p3"><span class="t1">and so there is an origination of this whole mass of suffering. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Yadā have pātubhavanti dhammā</span></p><p class="p3"><span class="t1">When (the nature of) things becomes really manifest </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ātāpino jhāyato brāhmaṇassa,</span></p><p class="p3"><span class="t1">to the ardent meditating brāhmaṇa, </span></p>', '-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 3, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">athassa kaṅkhā vapayanti sabbā,</span></p><p class="p3"><span class="t1">then all his doubts disappear, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yato pajānāti sahetudhammaṁ</span></p><p class="p3"><span class="t1">since he knows (the nature of a) thing and its cause. </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Iti imasmiṁ asati idaṁ na hoti; imassa nirodhā idaṁ nirujjhati,</span></p><p class="p3"><span class="t1">This not being so, that is not; from the ceasing of this, that ceases, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yadidaṁ: avijjānirodhā saṅkhāranirodho,</span></p><p class="p3"><span class="t1">that is to say: from the cessation of ignorance, there is the cessation of (volitional) processes, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saṅkhāranirodhā viññāṇanirodho,</span></p><p class="p3"><span class="t1">from the cessation of (volitional) processes, the cessation of consciousness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">viññāṇanirodhā nāmarūpanirodho,</span></p><p class="p3"><span class="t1">from the cessation of consciousness, the cessation of mind and body, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāmarūpanirodhā saḷāyatananirodho,</span></p><p class="p3"><span class="t1">from the cessation of mind and body, the cessation of the six sense spheres, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saḷāyatananirodhā phassanirodho,</span></p><p class="p3"><span class="t1">from the cessation of the six sense spheres, the cessation of contact, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">phassanirodhā vedanānirodho,</span></p><p class="p3"><span class="t1">from the cessation of contact, the cessation of feeling, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vedanānirodhā taṇhānirodho,</span></p><p class="p3"><span class="t1">from the cessation of feeling, the cessation of craving, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṇhānirodhā upādānanirodho,</span></p><p class="p3"><span class="t1">from the cessation of craving, the cessation of attachment, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upādānanirodhā bhavanirodho,</span></p>', '-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 4, '<p class="p3"><span class="t1">from the cessation of attachment, the cessation of continuation, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhavanirodhā jātinirodho,</span></p><p class="p3"><span class="t1">from the cessation of continuation, the cessation of birth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">jātinirodhā jarāmaraṇaṁ,</span></p><p class="p3"><span class="t1">from the cessation of birth, old age, death, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sokaparidevadukkhadomanassupāyāsā nirujjhanti,</span></p><p class="p3"><span class="t1">grief, lamentation, pain, sorrow, and despair (all) cease, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evam-etassa kevalassa dukkhakkhandhassa nirodho hoti.</span></p><p class="p3"><span class="t1">and so there is a cessation of this whole mass of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yadā have pātubhavanti dhammā</span></p><p class="p3"><span class="t1">When (the nature of) things becomes really manifest </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ātāpino jhāyato brāhmaṇassa,</span></p><p class="p3"><span class="t1">to the ardent meditating brāhmaṇa, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">athassa kaṅkhā vapayanti sabbā,</span></p><p class="p3"><span class="t1">then all his doubts disappear, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yato khayaṁ paccayānaṁ avedi.</span></p><p class="p3"><span class="t1">since the destruction of causes has been understood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Iti imasmiṁ sati idaṁ hoti, imassuppādā idaṁ uppajjati,</span></p><p class="p3"><span class="t1">This being so, that is; from the arising of this, that arises, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">imasmiṁ asati idaṁ na hoti, imassa nirodhā idaṁ nirujjhati,</span></p><p class="p3"><span class="t1">this not being so, that is not; from the ceasing of this, that ceases, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yadidaṁ: avijjāpaccayā saṅkhārā,</span></p><p class="p3"><span class="t1">that is to say: with ignorance as condition there are (volitional) processes, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saṅkhārapaccayā viññāṇaṁ,</span></p>', '-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 5, '<p class="p3"><span class="t1">with (volitional) processes as condition: consciousness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">viññāṇapaccayā nāmarūpaṁ,</span></p><p class="p3"><span class="t1">with consciousness as condition: mind and body, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāmarūpapaccayā saḷāyatanaṁ,</span></p><p class="p3"><span class="t1">with mind and body as condition: the six sense spheres, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saḷāyatanapaccayā phasso,</span></p><p class="p3"><span class="t1">with the six sense spheres as condition: contact, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">phassapaccayā vedanā,</span></p><p class="p3"><span class="t1">with contact as condition: feeling, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vedanāpaccayā taṇhā,</span></p><p class="p3"><span class="t1">with feeling as condition: craving, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṇhāpaccayā upādānaṁ,</span></p><p class="p3"><span class="t1">with craving as condition: attachment, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upādānapaccayā bhavo,</span></p><p class="p3"><span class="t1">with attachment as condition: continuation, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhavapaccayā jāti,</span></p><p class="p3"><span class="t1">with continuation as condition: birth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">jātipaccayā jarāmaraṇaṁ,</span></p><p class="p3"><span class="t1">with birth as condition: old age, death, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sokaparidevadukkhadomanassupāyāsā sambhavanti,</span></p><p class="p3"><span class="t1">grief, lamentation, pain, sorrow, and despair (all) arise, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evam-etassa kevalassa dukkhakkhandhassa samudayo hoti.</span></p><p class="p3"><span class="t1">and so there is an origination of this whole mass of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p>', '-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 6, '<p class="p3"><span class="t5">Avijjāya tveva asesavirāganirodhā saṅkhāranirodho,</span></p><p class="p3"><span class="t1">But from the complete fading away and cessation of ignorance, there is the cessation of (volitional) processes, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saṅkhāranirodhā viññāṇanirodho,</span></p><p class="p3"><span class="t1">from the cessation of (volitional) processes, the cessation of consciousness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">viññāṇanirodhā nāmarūpanirodho,</span></p><p class="p3"><span class="t1">from the cessation of consciousness, the cessation of mind and body, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāmarūpanirodhā saḷāyatananirodho,</span></p><p class="p3"><span class="t1">from the cessation of mind and body, the cessation of the six sense spheres, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saḷāyatananirodhā phassanirodho,</span></p><p class="p3"><span class="t1">from the cessation of the six sense spheres, the cessation of contact, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">phassanirodhā vedanānirodho,</span></p><p class="p3"><span class="t1">from the cessation of contact, the cessation of feeling, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vedanānirodhā taṇhānirodho,</span></p><p class="p3"><span class="t1">from the cessation of feeling, the cessation of craving, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṇhānirodhā upādānanirodho,</span></p><p class="p3"><span class="t1">from the cessation of craving, the cessation of attachment, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upādānanirodhā bhavanirodho,</span></p><p class="p3"><span class="t1">from the cessation of attachment, the cessation of continuation, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhavanirodhā jātinirodho,</span></p><p class="p3"><span class="t1">from the cessation of continuation, the cessation of birth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">jātinirodhā jarāmaraṇaṁ,</span></p><p class="p3"><span class="t1">from the cessation of birth, old age, death, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sokaparidevadukkhadomanassupāyāsā nirujjhanti,</span></p><p class="p3"><span class="t1">grief, lamentation, pain, sorrow, and despair (all) cease, </span></p><p class="p3"><span class="t1">  </span></p>', '-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 7, '<p class="p3"><span class="t5">evam-etassa kevalassa dukkhakkhandhassa nirodho hoti.</span></p><p class="p3"><span class="t1">and so there is a cessation of this whole mass of suffering. </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Yadā have pātubhavanti dhammā</span></p><p class="p3"><span class="t1">When (the nature of) things becomes really manifest </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ātāpino jhāyato brāhmaṇassa,</span></p><p class="p3"><span class="t1">to the ardent meditating brāhmaṇa, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vidhūpayaṁ tiṭṭhati Mārasenaṁ,</span></p><p class="p3"><span class="t1">he dwells dispelling Māra''s army, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">suriyo va obhāsayam-antalikkhaṁ.</span></p><p class="p3"><span class="t1">as the sun (dwells) lighting up the firmament. </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Hetupaccayo, ārammaṇapaccayo, adhipatipaccayo,</span></p><p class="p3"><span class="t1">Root condition, object condition, predominance condition, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anantarapaccayo, samanantarapaccayo,</span></p><p class="p3"><span class="t1">preceding condition, prior condition, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sahajātapaccayo, aññamaññapaccayo,</span></p><p class="p3"><span class="t1">arising together condition, mutuality condition, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nissayapaccayo, upanissayapaccayo,</span></p><p class="p3"><span class="t1">support condition, strong support condition, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">purejātapaccayo, pacchājātapaccayo, āsevanapaccayo,</span></p><p class="p3"><span class="t1">arising before condition, arising later condition, habitual condition, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kammapaccayo, vipākapaccayo,</span></p><p class="p3"><span class="t1">(intentional) deed condition, result condition, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">āhārapaccayo, indriyapaccayo,</span></p><p class="p3"><span class="t1">nutriment condition, faculty condition, </span></p><p class="p3"><span class="t1">  </span></p>', '-7-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Jayaparittaṁ ', 'title', 8);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 8, '<p class="p3"><span class="t5">jhānapaccayo, maggapaccayo,</span></p><p class="p3"><span class="t1">absorption condition, path condition, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sampayuttapaccayo, vippayuttapaccayo,</span></p><p class="p3"><span class="t1">association condition, disassociation condition, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">atthipaccayo, natthipaccayo,</span></p><p class="p3"><span class="t1">presence condition, absence condition, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vigatapaccayo, avigatapaccayo ti.</span></p><p class="p3"><span class="t1">disappearance condition, non-disappearance condition. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><h4>Jayaparittaṁ </h4><p class="p3"><span class="t10">The Success Safeguard</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Jayanto Bodhiyā mūle Sakyānaṁ nandivaḍḍhano -</span></p><p class="p3"><span class="t1">Succeeding at the root of the Bodhi tree he furthered the Sakyans’ joy - </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">evam-eva jayo hotu, jayassu jayamaṅgale!</span></p><p class="p3"><span class="t1">so may you be successful, may you succeed with the blessing of success! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Aparājitapallaṅke sīse puthuvipukkhale,</span></p><p class="p3"><span class="t1">Seated cross-legged and undefeated on the top of the world </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">abhiseke sabba-Buddhānaṁ, aggappatto pamodati.</span></p><p class="p3"><span class="t1">which was consecrated by all Buddhas, he rejoices in the highest attainment. </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Sunakkhattaṁ sumaṅgalaṁ suppabhātaṁ suhuṭṭhitaṁ,</span></p><p class="p3"><span class="t1">(May there be) good planets, good blessings, good daybreaks, good gifts, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sukhaṇo sumuhutto ca suyiṭṭhaṁ brahmacārisu,</span></p><p class="p3"><span class="t1">good moments, good instants and good sacrifices in the spiritual lives, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Padakkhiṇaṁ kāyakammaṁ, vācākammaṁ padakkhiṇe,</span></p><p class="p3"><span class="t1">(May there be) favourable body-actions, favourable speech-actions </span></p>', '-8-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Paccavekkhaṇā ', 'title', 9);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 9, '<p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">padakkhiṇaṁ manokammaṁ paṇidhi te padakkhiṇe.</span></p><p class="p3"><span class="t1">favourable mind-actions, and favourable aspiration for you. </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Padakkhiṇāni katvāna, labhantatthe padakkhiṇe,</span></p><p class="p3"><span class="t1">And after making these favourable things, (may there be) favourable gains, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">te atthaladdhā sukhitā viruḷhā Buddhasāsane,</span></p><p class="p3"><span class="t1">and (with these) favourable gains (may there be) for you happy growth in the Buddha’s Dispensation, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">arogā sukhitā hotha, saha sabbehi ñātibhi!</span></p><p class="p3"><span class="t1">may you be well and happy, together with all your relatives! </span></p><p class="p3"><span class="t1"><span class="t13"> </span> </span></p><p class="p3"><span class="t13"> </span></p><p class="p3"><span class="t1"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span> </span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t1"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span> </span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><h4>Paccavekkhaṇā </h4><p class="p3"><span class="t10">The Reflections (on the Requisites)</span></p><p class="p3"><span class="t9"> </span></p>', '-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 10, '<p class="p3"><span class="t5">Paṭisaṅkhā yoniso cīvaraṁ paṭisevāmi,</span></p><p class="p3"><span class="t1">With proper discernment I make use of the robe, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yāvad-eva sītassa paṭighātāya, uṇhassa paṭighātāya,</span></p><p class="p3"><span class="t1">only to ward off the cold, to ward off the heat, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ḍaṁsamakasavātātapasarīṁsapasamphassānaṁ paṭighātāya,</span></p><p class="p3"><span class="t1">to ward off contact with gadflies, mosquitoes, wind, the heat (of the sun), and creeping things, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yāvad-eva hirikopīnappaṭicchādanatthaṁ. </span></p><p class="p3"><span class="t1">only as a cover for the shameful parts. [1] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Paṭisaṅkhā yoniso piṇḍapātaṁ paṭisevāmi,</span></p><p class="p3"><span class="t1">With proper discernment I make use of almsfood, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">neva davāya, na madāya, na maṇḍanāya, na vibhūsanāya,</span></p><p class="p3"><span class="t1">not for sport, or for showing off, not for ornament, or for adornment, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yāvad-eva imassa kāyassa ṭhitiyā yāpanāya,</span></p><p class="p3"><span class="t1">but only to maintain this body, and to carry on, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vihiṁsūparatiyā brahmacariyānuggahāya,</span></p><p class="p3"><span class="t1">to inhibit annoyance, and to assist in the spiritual life, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iti purāṇañ-ca vedanaṁ paṭihaṅkhāmi,</span></p><p class="p3"><span class="t1">and so I will get rid of any old feeling, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">navañ-ca vedanaṁ na uppādessāmi,</span></p><p class="p3"><span class="t1">and not produce any new feeling, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yātrā ca me bhavissati, anavajjatā ca phāsuvihāro ca. </span></p><p class="p3"><span class="t1">and there will be support for me, being blameless, and living comfortably. [2] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Paṭisaṅkhā yoniso senāsanaṁ paṭisevāmi,</span></p>', '-10-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Daily Evening Chants ', 'title', 11);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Mahā Namakkārapāḷi ', 'title', 11);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 11, '<p class="p3"><span class="t1">With proper discernment I make use of a dwelling, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yāvad-eva sītassa paṭighātāya, uṇhassa paṭighātāya,</span></p><p class="p3"><span class="t1">only to ward off the cold, to ward off the heat, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ḍaṁsamakasavātātapasarīṁsapasamphassānaṁ paṭighātāya,</span></p><p class="p3"><span class="t1">to ward off contact with gadflies, mosquitoes, wind, the heat (of the sun), and serpents, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yāvad-eva utuparissayavinodanapaṭisallānārāmatthaṁ. </span></p><p class="p3"><span class="t1">only to dispel the trouble of the (varying) seasons, and so as to delight in seclusion. [3] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t15">Paṭisaṅkhā yoniso gilānapaccayabhesajjaparikkhāraṁ paṭisevāmi,</span></p><p class="p3"><span class="t1">With proper discernment I make use of the requisite of medicine for support when sick, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yāvad-eva uppannānaṁ veyyābādhikānaṁ vedanānaṁ paṭighātāya</span></p><p class="p3"><span class="t1">only to ward off oppressive feelings that have arisen </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abyāpajjhaparamatāya. </span></p><p class="p3"><span class="t1">and at least be free from oppression. [4] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Daily Evening Chants </h3><p class="p3"><span class="t9"> </span></p><h4>Mahā Namakkārapāḷi </h4><p class="p3"><span class="t10">The Great Reverence Text</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Namo tassa Bhagavato Arahato Sammāsambuddhassa</span><span class="t5"> (3x)</span></p><p class="p3"><span class="t1">Reverence to him, the Fortunate One, the Worthy One, the Perfect Sambuddha </span></p><p class="p3"><span class="t16"> </span></p><p class="p3"><span class="t5">Sugataṁ sugataṁ seṭṭhaṁ, kusalaṁ ’kusalaṁ jahaṁ,</span></p><p class="p3"><span class="t1">The one who is happy and happily excellent, who has given up wholesome and unwholesome deeds, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Amataṁ amataṁ santaṁ, Asamaṁ Asamaṁ dadaṁ,</span></p><p class="p3"><span class="t1">Who found the deathless peace of the Deathless, who found the Matchless (Nibbāna), and gives the Matchless (to others), </span></p>', '-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 12, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saraṇaṁ saraṇaṁ lokaṁ, araṇaṁ araṇaṁ karaṁ</span></p><p class="p3"><span class="t1">Who found the Refuge, and is the refuge for the world, the one without passions, who makes the passions fade, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Abhayaṁ abhayaṁ ṭhānaṁ nāyakaṁ: Nāyakaṁ name. </span></p><p class="p3"><span class="t1">The one without fear, who leads to that fearless place: I will revere (Lord Buddha), the Leader. [1] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nayanasubhagakāyaṅgaṁ,</span></p><p class="p3"><span class="t1">Having body and limbs lovely to the eyes, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Madhuravarasaropetaṁ,</span></p><p class="p3"><span class="t1">Endowed with a noble and sweet voice, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Amitaguṇagaṇādhāraṁ:</span></p><p class="p3"><span class="t1">The receptacle of many measureless virtues: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dasabalam-atulaṁ vande. </span></p><p class="p3"><span class="t1">I worship that incomparable One of Ten Powers. [2] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Yo Buddho dhitimāññadhārako,</span></p><p class="p3"><span class="t1">He who is Buddha, the bearer of steadfast knowledge, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saṁsāre anubhosi kāyikaṁ</span></p><p class="p3"><span class="t1">Who underwent in the continual round of existence, both bodily </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dukkhaṁ cetasikañ-ca lokato:</span></p><p class="p3"><span class="t1">And mental suffering for the sake of the world: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Taṁ vande naradevamaṅgalaṁ. </span></p><p class="p3"><span class="t1">I worship Him, who is a blessing for men and gods. [3] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Bāttiṁsatilakkhaṇacitradehaṁ,</span></p><p class="p3"><span class="t1">He who has the thirty-two beautiful marks on his body, </span></p>', '-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 13, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dehajjutiniggatapajjalantaṁ</span></p><p class="p3"><span class="t1">Whose body shines with an effulgence of light, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Paññādhitisīlaguṇoghavindaṁ:</span></p><p class="p3"><span class="t1">Who possesses an ocean of wisdom, steadfastness, morality and virtue: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vande Munim-antimajātiyuttaṁ. </span></p><p class="p3"><span class="t1">I worship that Sage who is in his final birth. [4] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Pātodayaṁ bāladivākaraṁ va,</span></p><p class="p3"><span class="t1">Like a fresh sun rising in the morning, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Majjhe yatīnaṁ lalitaṁ sirīhi,</span></p><p class="p3"><span class="t1">With charming grace in the midst of those who strive, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Puṇṇindusaṅkāsamukhaṁ, anejaṁ:</span></p><p class="p3"><span class="t1">Having a face like a full moon, lust-free: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vandāmi sabbaññum-ahaṁ Munindaṁ. </span></p><p class="p3"><span class="t1">I worship the omniscient Lord of Sages. [5] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Upetapuñño, varabodhimūle</span></p><p class="p3"><span class="t1">Endowed with merit, at the root of the Bodhi tree </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sasenamāraṁ Sugato jinitvā,</span></p><p class="p3"><span class="t1">the Happy One, after defeating Māra and his army, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Abojjhi Bodhiṁ aruṇodayamhi:</span></p><p class="p3"><span class="t1">Awoke to Wisdom as the dawn broke: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Namāmi Taṁ mārajinaṁ abhaṅgaṁ. </span></p><p class="p3"><span class="t1">I revere Him, the unbroken one, who defeated the (five) deaths. [6] </span></p><p class="p3"><span class="t9"> </span></p>', '-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 14, '<p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Rāgādichedāmalañāṇakhaggaṁ,</span></p><p class="p3"><span class="t1">That one having the sword of undefiled knowledge that cuts off passion and so on, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Satīsamaññāphalakābhigāhaṁ,</span></p><p class="p3"><span class="t1">Who holds up the shield known as mindfulness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sīloghalaṅkāravibhūsitaṁ: Taṁ</span></p><p class="p3"><span class="t1">Who is adorned with the decoration of an ocean of morality: Him </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Namāmi ''bhiññāvaram-iddhupetaṁ. </span></p><p class="p3"><span class="t1">I revere, the one endowed with noble knowledge and psychic power. [7] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Dayālayaṁ sabbadhi dukkaraṁ karaṁ,</span></p><p class="p3"><span class="t1">That abode of sympathy, who (overcoming) difficulties everywhere, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhavaṇṇavātikkamam-aggataṁ gataṁ,</span></p><p class="p3"><span class="t1">And, overcoming the ocean of existence, attained the highest, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tilokanāthaṁ susamāhitaṁ hitaṁ:</span></p><p class="p3"><span class="t1">The well-composed Lord of the Three Worlds who is a benefit (to others): </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Samantacakkhuṁ paṇamāmi Taṁ ''mitaṁ. </span></p><p class="p3"><span class="t1">I bow down to the measureless All-Seeing One. [8] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Tahiṁ tahiṁ pāramisañcayaṁ cayaṁ,</span></p><p class="p3"><span class="t1">In this place and that place he accumulated a store of perfections, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gataṁ gataṁ sabbhisukhappadaṁ padaṁ,</span></p><p class="p3"><span class="t1">He has attained the happy resting place of the virtuous, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Narānarānaṁ sukhasambhavaṁ bhavaṁ,</span></p><p class="p3"><span class="t1">And is the origin and source of happiness for men and gods, </span></p><p class="p3"><span class="t1">  </span></p>', '-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 15, '<p class="p3"><span class="t5">Namānamānaṁ Jinapuṅgavaṁ gavaṁ. </span></p><p class="p3"><span class="t1">I revere the noble Victor, who (overcame) the disrespectful fools. [9] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Maggaṅganāvaṁ Munidakkhanāviko,</span></p><p class="p3"><span class="t1">The Sage and skilful mariner ascended the ship of the Path-factors, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Īhāphiyaṁ ñāṇakarena gāhako,</span></p><p class="p3"><span class="t1">Grasped with the hand of knowledge the oar of exertion, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Āruyha yo tāya bahū bhavaṇṇavā:</span></p><p class="p3"><span class="t1">And crossed over the manifold ocean of existence: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tāresi Taṁ buddham-aghappahaṁ name. </span></p><p class="p3"><span class="t1">I revere Him, who understood, abandoned distress and crossed over. [10] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Samatiṁsatipāramisambharaṇaṁ,</span></p><p class="p3"><span class="t1">He who fulfilled the thirty perfections equally, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Varabodhidume Catusaccadasaṁ,</span></p><p class="p3"><span class="t1">Who, under the noble Bodhi tree, saw the Four Truths, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Varam-iddhigataṁ naradevahitaṁ:</span></p><p class="p3"><span class="t1">Attained the noble psychic powers, which benefit gods and men: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tibhavūpasamaṁ paṇamāmi Jinaṁ. </span></p><p class="p3"><span class="t1">I bow down to the Victor, who stilled (rebirth in) the three (planes of) existence. [11] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Satapuññajalakkhaṇikaṁ, virajaṁ,</span></p><p class="p3"><span class="t1">Having the marks that arose because of hundreds of merits, passionless, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gaganūpamadhiṁ, dhiti-Merusamaṁ,</span></p><p class="p3"><span class="t1">Superior like the sky, firm like (Mount) Meru, </span></p><p class="p3"><span class="t1">  </span></p>', '-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 16, '<p class="p3"><span class="t5">Jalajūpamasītalasīlayutaṁ,</span></p><p class="p3"><span class="t1">Cool, like the lotus, endowed with morality, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pathavīsahanaṁ: paṇamāmi Jinaṁ. </span></p><p class="p3"><span class="t1">who endures (like) the Earth: I bow down to the Victor. [12] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Yo Buddho sumati, dive divākaro va,</span></p><p class="p3"><span class="t1">He who is the wise Buddha, like the sun shining in the day, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sobhanto ratijanane silāsanamhi,</span></p><p class="p3"><span class="t1">* Resplendent, producing delight, while sitting on his stone throne, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Āsīno sivasukhadaṁ adesi Dhammaṁ,</span></p><p class="p3"><span class="t1">Taught the Abhidhamma, which gives the happiness of safety, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Devānaṁ: Tam-Asadisaṁ namāmi niccaṁ. </span></p><p class="p3"><span class="t1">to the gods: I always revere Him who is Matchless. [13] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Yo pādapaṅkajamuduttalarājikehi,</span></p><p class="p3"><span class="t1">He who has tender skin on his lotus feet, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Lokehi tīhi ''vikalehi nirākulehi,</span></p><p class="p3"><span class="t1">Who is unconfused, not lacking (in knowledge) about the three worlds, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sampāpuṇe nirupameyyatam-eva, Nātho</span></p><p class="p3"><span class="t1">Who has attained the incomparable, the Lord </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Taṁ sabbalokamahitaṁ asamaṁ namāmi. </span></p><p class="p3"><span class="t1">Who is unequalled and honoured by the whole world: I revere Him. [14] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Buddhaṁ narānarasamosaraṇaṁ dhitattaṁ,</span></p><p class="p3"><span class="t1">The Buddha with steady mind, who brought together men and gods, </span></p><p class="p3"><span class="t1">  </span></p>', '-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 17, '<p class="p3"><span class="t5">Paññāpadīpajutiyā vihatandhakāraṁ,</span></p><p class="p3"><span class="t1">Who, with the lustre of the light of wisdom destroyed the darkness (of ignorance), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atthābhikāmanaradevahitāvahaṁ, Taṁ</span></p><p class="p3"><span class="t1">He who greatly desires the welfare and benefit of men and gods, Him </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vandāmi, kāruṇikam-aggam-anantañāṇaṁ. </span></p><p class="p3"><span class="t1">I worship, the great compassionate (Buddha), with infinite knowledge. [15] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Akhilaguṇanidhāno yo Munindopagantvā</span></p><p class="p3"><span class="t1">The deposit of all virtues, the Lord of Sages, who, after approaching </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vanam-Isipatanavhaṁ, saññatānaṁ niketaṁ,</span></p><p class="p3"><span class="t1">The Wood called Isipatana, the abode of those restrained, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tahim-akusalachedaṁ Dhammacakkaṁ pavatto:</span></p><p class="p3"><span class="t1">And rolling the Dhamma-Wheel, which cut off unwholesomeness in that place: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tam-atulam-abhikantaṁ vandaneyyaṁ namāmi. </span></p><p class="p3"><span class="t1">I revere Him of incomparable beauty, who should be worshipped. [16] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Suciparivāritaṁ, surucirappabhāhi rattaṁ,</span></p><p class="p3"><span class="t1">Surrounded by the pure, brilliant with beautiful radiance, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sirivisarālayaṁ gupitam-indriyeh'' upetaṁ,</span></p><p class="p3"><span class="t1">The abode of many glories, endowed with guarded senses, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ravisasimaṇḍalappabhutilakkhaṇopacittaṁ:</span></p><p class="p3"><span class="t1">And beautiful marks beginning with the disk of the sun and the moon: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Suranarapūjitaṁ Sugatam-ādaraṁ namāmi. </span></p><p class="p3"><span class="t1">I revere with respect the Happy One, worshipped by gods and men. [17] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p>', '-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 18, '<p class="p3"><span class="t5">Maggoḷumpena muhapaṭighāsādi-ullolavīciṁ</span></p><p class="p3"><span class="t1">* With the raft of the Path, he crossed over the waves and currents of delusion, anger and desire </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saṁsāroghaṁ tari; tam-abhayaṁ pārapattaṁ, pajānaṁ</span></p><p class="p3"><span class="t1">* The ocean of existences; the fearless one fully crossed over, and he established humanity''s </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tāṇaṁ leṇaṁ asamasaraṇaṁ ekatitthaṁ patiṭṭhaṁ:</span></p><p class="p3"><span class="t1">Shelter, safety and unequalled refuge, the one (true) haven: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Puññakkhettaṁ paramasukhadaṁ Dhammarājaṁ namāmi. </span></p><p class="p3"><span class="t1">I revere the King of the Dhamma, that field of merit, who gives the ultimate bliss. [18] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Kaṇḍambaṁ mūle parahitakaro yo Munindo nisinno,</span></p><p class="p3"><span class="t1">The Lord of Sages sitting at the root of the Kaṇḍamba tree, seeking the benefit of others, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Accheraṁ sīghaṁ nayanasubhagaṁ ākulaṇṇaggijālaṁ,</span></p><p class="p3"><span class="t1">* Quickly wrought a wonder by scattering water and fire that was pleasing to the eyes, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dujjāladdhaṁsaṁ, Munibhi ''jahitaṁ pāṭiheraṁ akāsi:</span></p><p class="p3"><span class="t1">Destroying the false net (of views), this miracle was not left undone by the Sage: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vande Taṁ, seṭṭhaṁ paramaratijaṁ iddhidhammeh'' upetaṁ. </span></p><p class="p3"><span class="t1">I worship Him, the praised origin of supreme delight, endowed with psychic power. [19] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Munindakko yeko dayudayaruṇo ñāṇavitthiṇṇabimbo,</span></p><p class="p3"><span class="t1">That clever Sage, who is the rising dawn of compassion, having extensive knowledge, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vineyyappāṇoghaṁ kamalakathitaṁ Dhammaraṁsīvarehi,</span></p><p class="p3"><span class="t1">Who leads over the watery ocean what are called lotuses with the noble rays of his Dhamma, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Subodhesī suddhe tibhavakuhare byāpitakkittinañ-ca,</span></p><p class="p3"><span class="t1">And awakens the pure of heart in the pit of the three worlds, widely-renowned, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tilokekaccakkhuṁ dukham-asahanaṁ: Taṁ Mahesiṁ namāmi. </span></p>', '-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 19, '<p class="p3"><span class="t1">The one (wisdom) eye of the three worlds, who endured suffering: I revere Him, the Great Sage. [20] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Yo Jino anekajātiyaṁ, saputtadāram-aṅgajīvitam-pi,</span></p><p class="p3"><span class="t1">* That Victor who, in countless lives, gave both his children and wife, his limbs and life, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bodhipemato alaggamānaso adāsi yeva atthikassa,</span></p><p class="p3"><span class="t1">Without a clinging mind, for love of Awakening, to he who desired it, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dānapāramiṁ tato paraṁ apūri sīlapāramādikam-pi,</span></p><p class="p3"><span class="t1">And after the perfection of generosity fulfilled the others, like the perfection of virtue and so on, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tāsam-iddhiyopayātam-Aggataṁ: Tam-Ekadīpakaṁ namāmi. </span></p><p class="p3"><span class="t1">And reached the Highest through their (inherent) power: I revere Him, the one (true) Island. [21] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Devādevātidevaṁ nidhanavapudharaṁ mārabhaṅgaṁ abhaṅgaṁ</span></p><p class="p3"><span class="t1">The Great God above all gods, bearing his last body, having broken the (five) killers, unbroken (himself), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dīpaṁ dīpaṁ pajānaṁ, jayavarasayane Bodhipattaṁ ''dhipattaṁ</span></p><p class="p3"><span class="t1">Who spread the light of wisdom on people, and attained Awakening on the noble victory seat, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Brahmābrahmāgatānaṁ varagirakathikaṁ pāpahīnaṁ pahīnaṁ</span></p><p class="p3"><span class="t1">Surrounded by brahmas and others, speaking with a noble voice, having given up low actions, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Lokālokābhirāmaṁ: satatam-abhiname Taṁ Munindaṁ Munindaṁ. </span></p><p class="p3"><span class="t1">Greatly delighting the worlds: I always greatly revere Him, the Lord of Sages, the Lord of the (five) Sages. [22] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Buddho nigrodhabimbo mudukaracaraṇo brahmaghoseṇijaṅgho,</span></p><p class="p3"><span class="t1">The Buddha, having a form like the banyan tree, soft hands and feet, a voice like brahma, calves like an antelope, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kosacchādaṅgajāto, punar-api Sugato suppatiṭṭhitapādo,</span></p><p class="p3"><span class="t1">And a foreskin that covers the organ, the Happy One, with steady feet, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mūdodātuṇṇalomo, atham-api Sugato brahmujuggattabhāvo,</span></p>', '-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 20, '<p class="p3"><span class="t1">Soft hair between the brows, the Happy One, straight-limbed (like) a brahma, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nīlakkhī dīghapaṇhī sukhumamalachavī, thomyarasaggasaggī, </span></p><p class="p3"><span class="t1">Having blue eyes, long heels, exquisite skin, whose taste buds are the very best, [23] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Cattālīsaggadanto samakalapanajo antaraṁsappapīno,</span></p><p class="p3"><span class="t1">Having forty of the best teeth, even teeth, a full back between the shoulders, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Cakkenaṅkītapādo aviraḷadasano, Māraj'' ussaṅkhapādo,</span></p><p class="p3"><span class="t1">Wheel-marked feet, unflawed teeth, He who defeated the killers, having high ankles, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tiṭṭhanto nonamanto ''bhayakaramudunā jaṇṇukānāmasanto,</span></p><p class="p3"><span class="t1">Who, while standing, without bending, with both his soft hands, can touch his knees, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vaṭṭakkhandho, Jino gotaruṇapakhumako sīhapubbaḍḍhakāyo, </span></p><p class="p3"><span class="t1">Whose body is round, the Victor, having eyelashes like an ox, the upper half of whose body is like a lion, [24] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Sattappīno ca dīghaṅguli-m-atha Sugato lomakūpekalomo,</span></p><p class="p3"><span class="t1">Having seven fat areas, long fingers, the Happy One, with one hair to a vesicle, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sampannodātadāṭho kanakasamataco nīlamuddhaggalomo,</span></p><p class="p3"><span class="t1">Full white teeth, smooth, golden skin, with black hair on the top of his head, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sambuddho, thūlajivho atha sīhahanuko jālikappādahattho,</span></p><p class="p3"><span class="t1">The Sambuddha, having a broad tongue, then a lion-like jaw, webbed feet and hands, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nātho uṇhīsasīso itiguṇasahitaṁ: Taṁ Mahesiṁ namāmi. </span></p><p class="p3"><span class="t1">Whose head has a crown, the Lord endowed with these (thirty-two) qualities: I revere Him, the Great Sage. [25] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Buddho, Buddho” ti ghoso atidulabhataro kā kathā Buddhabhāvo?</span></p><p class="p3"><span class="t1">* The sound, “Buddha, Buddha” is extremely rare in the world, what to say of Buddhahood? </span></p><p class="p3"><span class="t17"> </span></p><p class="p3"><span class="t5">Loke tasmā vibhāvī vividhahitasukhaṁ sādhavo patthayantā,</span></p>', '-20-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day One ', 'title', 21);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 21, '<p class="p3"><span class="t1">Therefore the wise and good, wishing for various sorts of benefit and happiness, </span></p><p class="p3"><span class="t17"> </span></p><p class="p3"><span class="t5">Iṭṭhaṁ atthaṁ vahantaṁ suranaramahitaṁ nibbhayaṁ dakkhiṇeyyaṁ,</span></p><p class="p3"><span class="t1">* Desiring their (true) welfare, should always revere Him, who is fearless, worthy, honoured by gods and men, </span></p><p class="p3"><span class="t17"> </span></p><p class="p3"><span class="t5">Lokānaṁ nandivaḍḍhaṁ Dasabalam-asamaṁ, Taṁ namassantu niccaṁ. </span></p><p class="p3"><span class="t1">The incomparable One of Ten Powers, who increases the happiness of the world. [26] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Evening Paritta Chants, Day One </h3><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Samantā cakkavāḷesu atrāgacchantu devatā</span></p><p class="p3"><span class="t1">May the gods from all over the universe assemble here </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saddhammaṁ Munirājassa suṇantu saggamokkhadaṁ:</span></p><p class="p3"><span class="t1">and listen to the King of the Sage’s true Dhamma about heaven and release: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dhammassavanakālo ayaṁ bhadantā! (3x)</span></p><p class="p3"><span class="t1">Reverend Sirs, this is the time for hearing the Dhamma! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Namo tassa Bhagavato Arahato Sammāsambuddhassa</span><span class="t5"> (3x)</span></p><p class="p3"><span class="t1">Reverence to him, the Fortunate One, the Worthy One, the Perfect Sambuddha </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Ye santā santacittā, tisaraṇasaraṇā, ettha lokantare vā,</span></p><p class="p3"><span class="t1">Those (gods) who are peaceful, with peaceful minds, who have taken refuge in the triple refuge, whether here, or above the worlds, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhummā bhummā ca devā, guṇagaṇagahaṇā, byāvaṭā sabbakālaṁ,</span></p><p class="p3"><span class="t1">the various earth gods, that group who have taken up, and are engaged in, virtuous deeds all of the time, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ete āyantu devā, varakanakamaye, Merurāje vasanto,</span></p><p class="p3"><span class="t1">may these gods come, those who dwell on the majestic Mt. Meru, that excellent golden mountain, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santo santo sahetuṁ Munivaravacanaṁ sotumaggaṁ samaggaṁ.</span></p>', '-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 22, '<p class="p3"><span class="t1">peacefully, and with good reason, (to hear) the Sage’s excellent word about entering the stream, and harmony. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Sabbesu cakkavāḷesu yakkhā devā ca brahmano,</span></p><p class="p3"><span class="t1">May all yakkhas, gods, and deities, from the whole universe, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ amhehi kataṁ puññaṁ sabbasampattisādhukaṁ</span></p><p class="p3"><span class="t1">° After partaking of the merits, and of all the thoroughly good fortune </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbe taṁ anumoditvā samaggā sāsane ratā,</span></p><p class="p3"><span class="t1">We have acquired, being in harmony, and delighting in the teaching, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pamādarahitā hontu ārakkhāsu visesato.</span></p><p class="p3"><span class="t1">Be not heedless and grant us complete protection. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sāsanassa ca lokassa vuḍḍhī bhavatu sabbadā,</span></p><p class="p3"><span class="t1">May the teaching and the world be on the increase every day, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sāsanam-pi ca lokañ-ca devā rakkhantu sabbadā.</span></p><p class="p3"><span class="t1">And may the gods every day protect the teaching and the world. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saddhiṁ hontu sukhī sabbe parivārehi attano,</span></p><p class="p3"><span class="t1">° May you, and all those who are around you, together with </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Anīghā sumanā hontu, saha sabbehi ñātibhi.</span></p><p class="p3"><span class="t1">All your relatives, be untroubled, happy, and easy in mind. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Rājato vā, corato vā, manussato vā, amanussato vā,</span></p><p class="p3"><span class="t1">(May you be protected) from the king, thieves, humans, and non-humans, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">aggito vā, udakato vā, pisācato vā, khāṇukato vā, kaṇṭakato vā,</span></p><p class="p3"><span class="t1">from fire and water, demons, stumps, and thorns, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nakkhattato vā, janapadarogato vā,</span></p>', '-22-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Maṅgalasuttaṁ ', 'title', 23);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 23, '<p class="p3"><span class="t1">from unlucky stars, and epidemics, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asaddhammato vā, asandiṭṭhito vā, asappurisato vā,</span></p><p class="p3"><span class="t1">from what is not the true dhamma, not right view, not a good person, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">caṇḍa-hatthi-assa-miga-goṇa-kukkura-ahivicchika-maṇisappa-</span></p><p class="p3"><span class="t1">and from fierce elephants, horses, antelopes, bulls, dogs, snakes, scorpions, poisonous serpents, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dīpi-accha-taraccha-sukara-mahisa-yakkha-rakkhasādīhi</span></p><p class="p3"><span class="t1">panthers, bears, hyenas, wild boars, buffaloes, yakkhas, rakkhasas, and so on, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nānā bhayato vā, nānā rogato vā, nānā upaddavato vā, ārakkhaṁ gaṇhantu!</span></p><p class="p3"><span class="t1">from the manifold fears, the manifold diseases, the manifold calamities, (from all of these troubles) may you receive protection! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><h4>Maṅgalasuttaṁ </h4><p class="p3"><span class="t10">The Discourse on the Blessings</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">Introductory Verses</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ maṅgalaṁ dvādasahi cintayiṁsu sadevakā,</span></p><p class="p3"><span class="t1">What a blessing is was thought about by gods and men for twelve (years), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sotthānaṁ nādhigacchanti; aṭṭhatiṁsañ-ca maṅgalaṁ</span></p><p class="p3"><span class="t1">but they did not attain (true) safety; the thirty-eight blessings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">desitaṁ devadevena sabbapāpavināsanaṁ.</span></p><p class="p3"><span class="t1">which were preached by the god of gods make all that is bad perish. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbalokahitatthāya maṅgalaṁ parittaṁ taṁ bhaṇāmahe.</span></p><p class="p3"><span class="t1">For the whole world’s benefit and welfare we will recite that blessed safeguard. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">The Safeguard</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ me sutaṁ:</span></p><p class="p3"><span class="t1">Thus I have heard: </span></p>', '-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 24, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Sāvatthiyaṁ viharati</span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling near Sāvatthi </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jetavane Anāthapiṇḍikassa ārāme.</span></p><p class="p3"><span class="t1">at Anāthapiṇḍika’s grounds in Jeta’s Wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho aññatarā devatā abhikkantāya rattiyā,</span></p><p class="p3"><span class="t1">Then a certain god, towards the end of the night, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abhikkantavaṇṇā kevalakappaṁ Jetavanaṁ obhāsetvā,</span></p><p class="p3"><span class="t1">whose surpassing beauty lit up the whole of Jeta’s Wood, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yena Bhagavā tenupasaṅkami,</span></p><p class="p3"><span class="t1">approached the Fortunate One, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">upasaṅkamitvā Bhagavantaṁ abhivādetvā, ekam-antaṁ aṭṭhāsi.</span></p><p class="p3"><span class="t1">and after approaching and worshipping the Fortunate One, he stood on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ekam-antaṁ ṭhitā kho sā devatā Bhagavantaṁ gāthāya ajjhabhāsi:</span></p><p class="p3"><span class="t1">While standing on one side that god addressed the Fortunate One with a verse: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Bahū devā manussā ca maṅgalāni acintayuṁ</span></p><p class="p3"><span class="t1">“Many are the gods and the men who have thought about the blessings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ākaṅkhamānā sotthānaṁ: brūhi maṅgalam-uttamaṁ.” </span></p><p class="p3"><span class="t1">hoping for safety: now please say what is the supreme blessing.” [1] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Asevanā ca bālānaṁ, paṇḍitānañ-ca sevanā,</span></p><p class="p3"><span class="t1">“Not associating with fools, but associating with the wise, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pūjā ca pūjanīyānaṁ: etaṁ maṅgalam-uttamaṁ. </span></p><p class="p3"><span class="t1">honouring those worthy of honour: this is the supreme blessing. [2] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Patirūpadesavāso ca, pubbe ca katapuññatā,</span></p><p class="p3"><span class="t1">Living in a suitable place, formerly having done good deeds, </span></p>', '-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 25, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">attasammāpaṇidhi ca: etaṁ maṅgalam-uttamaṁ.</span></p><p class="p3"><span class="t1">aspiring in a right way oneself: this is the supreme blessing.  [3] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bāhusaccañ-ca sippañ-ca, vinayo ca susikkhito,</span></p><p class="p3"><span class="t1">Having great learning and craft, and being disciplined and well trained, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">subhāsitā ca yā vācā: etaṁ maṅgalam-uttamaṁ. </span></p><p class="p3"><span class="t1">and whatever words are well spoken: this is the supreme blessing. [4] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mātāpitu-upaṭṭhānaṁ, puttadārassa saṅgaho,</span></p><p class="p3"><span class="t1">Attendance on one''s mother and father, looking after one’s wife and children, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anākulā ca kammantā: etaṁ maṅgalam-uttamaṁ. </span></p><p class="p3"><span class="t1">with works that are not agitating: this is the supreme blessing. [5] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dānañ-ca Dhammacariyā ca, ñātakānañ-ca saṅgaho,</span></p><p class="p3"><span class="t1">Giving, and living by the Dhamma, and looking after one’s relatives, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anavajjāni kammāni: etaṁ maṅgalam-uttamaṁ. </span></p><p class="p3"><span class="t1">(performing) actions that are blameless: this is the supreme blessing. [6] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ārati virati pāpā, majjapānā ca saṁyamo,</span></p><p class="p3"><span class="t1">Abstinence, avoidance of bad deeds, restraint from intoxicating drink, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appamādo ca dhammesu: etaṁ maṅgalam-uttamaṁ. </span></p><p class="p3"><span class="t1">being heedful regarding (all) things: this is the supreme blessing. [7] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gāravo ca nivāto ca, santuṭṭhī ca kataññutā,</span></p><p class="p3"><span class="t1">Having respect, being humble, being satisfied and grateful, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kālena Dhammassavaṇaṁ: etaṁ maṅgalam-uttamaṁ. </span></p><p class="p3"><span class="t1">listening to Dhamma at the right time: this is the supreme blessing. [8] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Khantī ca sovacassatā, samaṇānañ-ca dassanaṁ,</span></p><p class="p3"><span class="t1">Being patient and easily spoken to, seeing ascetics, </span></p>', '-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 26, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kālena Dhammasākacchā: etaṁ maṅgalam-uttamaṁ. </span></p><p class="p3"><span class="t1">discussing Dhamma at the right time: this is the supreme blessing. [9] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tapo ca brahmacariyañ-ca, ariyasaccānadassanaṁ,</span></p><p class="p3"><span class="t1">Austere, living spiritually, insight into the noble truths, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nibbānasacchikiriyā ca: etaṁ maṅgalam-uttamaṁ. </span></p><p class="p3"><span class="t1">the experience of Nibbāna: this is the supreme blessing. [10] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Phuṭṭhassa lokadhammehi, cittaṁ yassa na kampati,</span></p><p class="p3"><span class="t1">He whose mind does not waver, when it is touched by things of this world, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asokaṁ virajaṁ khemaṁ: etaṁ maṅgalam-uttamaṁ. </span></p><p class="p3"><span class="t1">(being) griefless, dustless, and secure: this is the supreme blessing. [11] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etādisāni katvāna, sabbattha-m-aparājitā,</span></p><p class="p3"><span class="t1">Having done as here directed, they are undefeated everywhere, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbattha sotthiṁ gacchanti: taṁ tesaṁ maṅgalam-uttamaṁ.” </span></p><p class="p3"><span class="t1">they go everywhere in safety: for them this is the supreme blessing.” [12] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t19">Maṅgalasuttaṁ Niṭṭhitaṁ</span></p><p class="p3"><span class="t18">The Discourse on the Blessings is Finished</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p>', '-26-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Two ', 'title', 27);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 27, '<p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Evening Paritta Chants, Day Two </h3><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Paṇidhānato paṭṭhāya, Tathāgatassa dasapāramiyo,</span></p><p class="p3"><span class="t1">Beginning with the aspiration (for Buddhahood, and thinking of) the Realised One’s ten perfections, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dasa-upapāramiyo, dasaparamatthapāramiyo ti - samatiṁsapāramiyo</span></p><p class="p3"><span class="t1">ten minor perfections, ten great perfections - altogether thirty perfections, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pañcamahāpariccāge, lokatthacariyaṁ </span></p><p class="p3"><span class="t1">his five great sacrifices, life of benefitting the world, life of benefitting his relatives, life of benefitting (by becoming) Buddha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ñātatthacariyaṁ Buddhatthacariyan-ti</span></p><p class="p3"><span class="t1">life of benefitting his relatives, life of benefitting (by becoming) Buddha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tisso cariyāyo, pacchimabhave gabbhāvokkantiṁ, jātiṁ,</span></p><p class="p3"><span class="t1">three ways of conduct, descent into the womb for his final existence, birth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abhinikkhamanaṁ, padhānacariyaṁ, Bodhipallaṅke, </span></p><p class="p3"><span class="t1">great renunciation, life of effort, sitting cross legged at the Bodhi tree,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"><span class="t5">Māravijayaṁ, Dhammacakkappavattanam, </span> </span></p><p class="p3"><span class="t1">victory over Māra, turning of the Wheel of Dhamma,  </span></p><p class="p3"><span class="t20"> </span></p><p class="p3"><span class="t5">sabbaññutañāṇappaṭivedhaṁ, navalokuttaradhamme ti,</span></p><p class="p3"><span class="t1">his penetration and knowledge of omniscience, and his nine supermundane attainments, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe pime Buddhaguṇe āvajjitvā, Vesāliyā tīsu pākārantaresu,</span></p>', '-27-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Ratanasuttaṁ ', 'title', 28);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 28, '<p class="p3"><span class="t1">and after considering all these Buddha qualities, (going around) the three walls of Vesāli, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tiyāmarattiṁ, parittaṁ karonto āyasmā Ānandatthero</span></p><p class="p3"><span class="t1">during the three watches of the night, the venerable elder Ānanda, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">viya kāruññacittaṁ upaṭṭhapetvā:</span></p><p class="p3"><span class="t1">after establishing great compassion in his heart, made this safeguard: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Koṭīsatasahassesu cakkavāḷesu devatā</span></p><p class="p3"><span class="t1">The gods in the ten hundred thousand million universes </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yassānaṁ paṭiggaṇhanti yañ-ca Vesāliyā pure,</span></p><p class="p3"><span class="t1">accepted the order (given) in the city of Vesāli, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">rogāmanusssadubbhikkhasambhūtaṁ tividhaṁ bhayaṁ,</span></p><p class="p3"><span class="t1">and disease, non-human beings, and famine, the threefold fear that had arisen, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">khippam-antaradhāpesi, parittaṁ taṁ bhaṇāmahe:</span></p><p class="p3"><span class="t1">quickly disappeared, we will recite that safeguard: </span></p><p class="p3"><span class="t1"><span class="t9"> </span> </span></p><p class="p3"><span class="t9"> </span></p><h4>Ratanasuttaṁ </h4><p class="p3"><span class="t10">The Discourse on the Treasures</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yānīdha bhūtāni samāgatāni, Bhummāni vā yāni va antalikkhe,</span></p><p class="p3"><span class="t1">Whatever beings have come together here, whether of the earth or in the firmament, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe va bhūtā sumanā bhavantu, atho pi sakkacca suṇantu bhāsitaṁ. </span></p><p class="p3"><span class="t1">may the minds of all those beings be happy, and may they listen carefully to what is said. [1] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tasmā hi bhūtā nisāmetha sabbe, mettaṁ karotha mānusiyā pajāya,</span></p><p class="p3"><span class="t1">Therefore, all of you beings, be attentive, be friendly towards this generation of men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">divā ca ratto ca haranti ye baliṁ, tasmā hi ne rakkhatha appamattā. </span></p><p class="p3"><span class="t1">they who bring offerings by day and by night, therefore, being heedful, you must protect them. [2] </span></p>', '-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 29, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ kiñci vittaṁ - idha vā huraṁ vā saggesu vā - yaṁ ratanaṁ paṇītaṁ</span></p><p class="p3"><span class="t1">Whatever riches there are - here or elsewhere or in the heavens - that excellent treasure </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na no samaṁ atthi Tathāgatena, idam-pi Buddhe ratanaṁ paṇītaṁ:</span></p><p class="p3"><span class="t1">is not equal unto the Realised One, this excellent treasure is in the Buddha: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">etena saccena suvatthi hotu! </span></p><p class="p3"><span class="t1">by virtue of this truth may there be safety! [3] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Khayaṁ virāgaṁ amataṁ paṇītaṁ yad-ajjhagā Sakyamunī samāhito,</span></p><p class="p3"><span class="t1">(Craving’s) end, dispassion, deathlessness, excellence which the concentrated Sakyan sage attained, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na tena dhammena samatthi kiñci idam-pi Dhamme ratanaṁ paṇītaṁ:</span></p><p class="p3"><span class="t1">there is nothing that is equal to that state this excellent treasure is in the Dhamma: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">etena saccena suvatthi hotu! </span></p><p class="p3"><span class="t1">by virtue of this truth may there be safety! [4] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yam-Buddhaseṭṭho parivaṇṇayī suciṁ samādhim-ānantarikañ-ñam-āhu,</span></p><p class="p3"><span class="t1">That which the Buddha, the Great One, praised as pure, the concentration said to have prompt (result), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">samādhinā tena samo na vijjati idam-pi Dhamme ratanaṁ paṇītaṁ:</span></p><p class="p3"><span class="t1">No equal to that concentration is found, this excellent treasure is in the Dhamma: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">etena saccena suvatthi hotu! </span></p><p class="p3"><span class="t1">by virtue of this truth may there be safety! [5] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye puggalā aṭṭha sataṁ pasatthā cattāri etāni yugāni honti,</span></p><p class="p3"><span class="t1">Those eight individuals praised by the good there are these four pairs (of individuals), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te dakkhiṇeyyā Sugatassa sāvakā, etesu dinnāni mahapphalāni.</span></p>', '-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 30, '<p class="p3"><span class="t1">those disciples of the Happy One are worthy of gifts, those things that have been given to them have great fruit. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span></p><p class="p3"><span class="t1">This excellent treasure is in the Saṅgha: by virtue of this truth may there be safety! [6] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye suppayuttā manasā daḷhena nikkāmino Gotamasāsanamhi,</span></p><p class="p3"><span class="t1">Those who have firm and devoted minds, without sense desire in Gotama''s dispensation, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te pattipattā amataṁ vigayha laddhā mudhā nibbutiṁ bhuñjamānā.</span></p><p class="p3"><span class="t1">those who have attained, and entered the deathless, are enjoying emancipation, gained for free. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span></p><p class="p3"><span class="t1">This excellent treasure is in the Saṅgha: by virtue of this truth may there be safety! [7] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yathindakhīlo pathavissito siyā catubbhi vātehi asampakampiyo,</span></p><p class="p3"><span class="t1">Just as a locking post stuck fast in the earth does not waver on account of the four winds, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tathūpamaṁ sappurisaṁ vadāmi, yo ariyasaccāni avecca passati.</span></p><p class="p3"><span class="t1">in the same way, I say, is the true person, the one who sees the noble truths completely. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span></p><p class="p3"><span class="t1">This excellent treasure is in the Saṅgha: by virtue of this truth may there be safety! [8] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye ariyasaccāni vibhāvayanti, gambhīrapaññena sudesitāni,</span></p><p class="p3"><span class="t1">Those who clearly distinguish the noble truths, which were well preached by the one with deep wisdom, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kiñcāpi te honti bhusappamattā na te bhavaṁ aṭṭhamaṁ ādiyanti.</span></p><p class="p3"><span class="t1">however great they become in heedlessness still they do not take up an eighth existence. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span></p><p class="p3"><span class="t1">This excellent treasure is in the Saṅgha: by virtue of this truth may there be safety! [9] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p>', '-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 31, '<p class="p3"><span class="t5">Sahā vassa dassanasampadāya tayassu dhammā jahitā bhavanti:</span></p><p class="p3"><span class="t1">With his attainment of (liberating) insight there are a triad of things that are given up: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sakkāyadiṭṭhi vicikicchitañ-ca sīlabbataṁ vāpi yad-atthi kiñci.</span></p><p class="p3"><span class="t1">embodiment view, uncertainty, and whatever (grasping at) virtue and practices there is. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">catūhapāyehi ca vippamutto, chaccābhiṭhānāni abhabba’ kātuṁ.</span></p><p class="p3"><span class="t1">he is free from (rebirth in) the four lower worlds, he is incapable of the six great crimes. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span></p><p class="p3"><span class="t1">This excellent treasure is in the Saṅgha: by virtue of this truth may there be safety! [10] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kiñcāpi so kamma’ karoti pāpakaṁ kāyena vācā uda cetasā vā,</span></p><p class="p3"><span class="t1">Whatever bad action there is that he performs  by way of body, or of speech, or of mind, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abhabba so tassa paṭicchadāya abhabbatā diṭṭhapadassa vuttā.</span></p><p class="p3"><span class="t1">he is incapable of covering it up: this incapacity is said of one who has seen the state (of peace). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span></p><p class="p3"><span class="t1">This excellent treasure is in the Saṅgha: by virtue of this truth may there be safety! [11] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vanappagumbe yatha phussitagge gimhānamāse paṭhamasmiṁ gimhe,</span></p><p class="p3"><span class="t1">Just like a tall woodland tree crowned with blossom in the summer months, in the early summer, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tathūpamaṁ Dhammavaraṁ adesayi, Nibbānagāmiṁ paramaṁhitāya.</span></p><p class="p3"><span class="t1">in the same way he preached the Dhamma which is best, which goes to Nibbāna, the highest benefit. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-pi Buddhe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span></p><p class="p3"><span class="t1">This excellent treasure is in the Buddha: by virtue of this truth may there be safety! [12] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Varo varaññū varado varāharo, anuttaro Dhammavaraṁ adesayī.</span></p><p class="p3"><span class="t1">The best one, knowing the best, giving the best, brought the best, unsurpassed he preached the best Dhamma. </span></p><p class="p3"><span class="t1">  </span></p>', '-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 32, '<p class="p3"><span class="t5">Idam-pi Buddhe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span></p><p class="p3"><span class="t1">This excellent treasure is in the Buddha: by virtue of this truth may there be safety! [13] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t21">Khīṇaṁ purāṇaṁ nava’ natthi sambhavaṁ, virattacittāyatike bhavasmiṁ,</span></p><p class="p3"><span class="t1">The old is destroyed, and nothing new is produced, (their) minds are unexcited by future rebirth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te khīṇabījā avirūḷhichandā, nibbanti dhīrā yathāyam-padīpo.</span></p><p class="p3"><span class="t1">they have destroyed the seeds, and have no desire for growth, the wise are still, just as this lamp (is still). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu!</span><span class="t21"> </span></p><p class="p3"><span class="t1">This excellent treasure is in the Saṅgha: by virtue of this truth may there be safety! <span class="t22">[14]</span> </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t19">(Spoken by Sakka, lord of the gods:)</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yānīdha bhūtāni samāgatāni, bhummāni vā yāni va antalikkhe,</span></p><p class="p3"><span class="t1">Whatever beings have come together here, whether of the earth or in the firmament, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tathāgataṁ devamanussapūjitaṁ, Buddhaṁ namassāma - suvatthi hotu! </span></p><p class="p3"><span class="t1">the Realised One is revered by gods and men, we honour the Buddha - may there be safety! [15] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yānīdha bhūtāni samāgatāni, bhummāni vā yāni va antalikkhe,</span></p><p class="p3"><span class="t1">Whatever beings have come together here, whether of the earth or in the firmament, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t23">Tathāgataṁ devamanussapūjitaṁ, Dhammaṁ namassāma - suvatthi hotu!</span><span class="t5"> </span></p><p class="p3"><span class="t1">the Realised One is revered by gods and men, we honour the Dhamma - may there be safety! [16] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yānīdha bhūtāni samāgatāni, bhummāni vā yāni va antalikkhe,</span></p><p class="p3"><span class="t1">Whatever beings have come together here, whether of the earth or in the firmament, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tathāgataṁ devamanussapūjitaṁ, Saṅghaṁ namassāma - suvatthi hotu! </span></p><p class="p3"><span class="t1">the Realised One is revered by gods and men, we honour the Saṅgha - may there be safety! [17] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t19">Ratanasuttaṁ Niṭṭhitaṁ</span></p>', '-32-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Three ', 'title', 33);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Mettasuttaṁ ', 'title', 33);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 33, '<p class="p3"><span class="t18">The Discourse on the Treasures is Finished</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Evening Paritta Chants, Day Three </h3><p class="p3"><span class="t9"> </span></p><h4>Mettasuttaṁ </h4><p class="p3"><span class="t10">The Discourse on Friendliness Meditation</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">Introductory Verses</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yassānubhāvato yakkhā neva dassenti bhīsanaṁ,</span></p><p class="p3"><span class="t1">Because of the power of that (safeguard) the yakkhas could not make fearful (sights) appear, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yamhi cevānuyuñjanto rattindivam-antandito</span></p><p class="p3"><span class="t1">by practising that diligently night and day </span></p><p class="p3"><span class="t1">  </span></p>', '-33-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 34, '<p class="p3"><span class="t5">sukhaṁ supati sutto ca pāpaṁ kiñci na passati -</span></p><p class="p3"><span class="t1">he sleeps happily, and while sleeping does not see anything bad - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evam-ādiguṇopetaṁ, parittaṁ taṁ bhaṇāmahe.</span></p><p class="p3"><span class="t1">it is endowed with such qualities, we will recite that safeguard. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">The Safeguard</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Karaṇīyam-atthakusalena, yan-ta’ santaṁ padaṁ abhisamecca:</span></p><p class="p3"><span class="t1">What should be done by one skilled in goodness, who has comprehended the state of peace: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sakko ujū ca suhujū ca, suvaco cassa mudu anatimānī, </span></p><p class="p3"><span class="t1">° he ought to be able, straight, and upright, easy to speak to, meek, without conceit, [1] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santussako ca subharo ca, appakicco ca sallahukavutti,</span></p><p class="p3"><span class="t1">satisfied (with little), easy to support, free from duties, and light in living, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santindriyo ca nipako ca, appagabbho kulesvananugiddho, </span></p><p class="p3"><span class="t1">with faculties at peace, prudent, not forward, and greedless among the families, [2] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na ca khuddam-ācare kiñci yena viññū pare upavadeyyuṁ.</span></p><p class="p3"><span class="t1">he should not do the slightest thing whereby others who are wise might find fault (with him). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sukhino va khemino hontu, sabbasattā bhavantu sukhitattā! </span></p><p class="p3"><span class="t1">“(May all beings) be happy and secure, may all beings in their hearts be happy! [3] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye keci pāṇabhūtatthi tasā vā thāvarā v’ anavasesā,</span></p><p class="p3"><span class="t1">Whatsoever breathing beings there are - trembling, firm, or any other (beings), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dīghā vā yeva mahantā, majjhimā rassakā aṇukathūlā, </span></p><p class="p3"><span class="t1">whether they be long or great, of middle (size), short, tiny, or of compact (body), [4] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">diṭṭhā vā ye va adiṭṭhā, ye va dūre vasanti avidūre,</span></p><p class="p3"><span class="t1">those who are seen, and those who are unseen, those who live far away, those who are near, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhūtā va sambhavesī vā sabbasattā bhavantu sukhitattā!” </span></p>', '-34-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 35, '<p class="p3"><span class="t1">those who are born, and those who still seek birth may all beings in their hearts be happy!” [5] </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Na paro paraṁ nikubbetha, nātimaññetha katthaci na’ kañci,</span></p><p class="p3"><span class="t1">No one should cheat another, nor should he despise anyone wherever he is, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">byārosanā paṭighasaññā nāññam-aññassa dukkham-iccheyya. </span></p><p class="p3"><span class="t1">° he should not long for suffering for another because of anger or resentment. [6] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mātā yathā niyaṁ puttam-āyusā ekaputtam-anurakkhe,</span></p><p class="p3"><span class="t1">° In the same way as a mother would protect her child, her only child, with her life, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evam-pi sabbabhūtesu mānasaṁ bhāvaye aparimāṇaṁ. </span></p><p class="p3"><span class="t1">so too towards all beings one should develop the measureless thought (of friendliness). [7] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mettañ-ca sabbalokasmi’ mānasaṁ bhāvaye aparimāṇaṁ,</span></p><p class="p3"><span class="t1">Towards the entire world he should develop the measureless thought of friendliness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">uddhaṁ adho ca tiriyañ-ca, asambādhaṁ averam-asapattaṁ. </span></p><p class="p3"><span class="t1">above, below, and across (the middle), without barriers, hate, or enmity. [8] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tiṭṭhaṁ caraṁ nisinno va, sayāno vā yāvatassa vitamiddho,</span></p><p class="p3"><span class="t1">Standing, walking, sitting, lying, for as long as he is without torpor, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">etaṁ satiṁ adhiṭṭheyya, brahmam-etaṁ vihāram-idha-m-āhu. </span></p><p class="p3"><span class="t1">he should be resolved on this mindfulness, for this, they say here, is the (true) spiritual life. [9] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Diṭṭhiñ-ca anupaggamma, sīlavā dassanena sampanno,</span></p><p class="p3"><span class="t1">Without going near a (wrong) view, virtuous, and endowed with (true) insight, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kāmesu vineyya gedhaṁ, na hi jātuggabbhaseyya’ punar-eti. </span></p><p class="p3"><span class="t1">having removed greed towards sense pleasures, he does not come to lie in a womb again. [10] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t19">Mettasuttaṁ niṭṭhitaṁ</span></p><p class="p3"><span class="t18">The Discourse on Friendliness Meditation is Finished</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p>', '-35-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Four ', 'title', 36);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Khandhasuttaṁ ', 'title', 36);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 36, '<p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Evening Paritta Chants, Day Four </h3><p class="p3"><span class="t9"> </span></p><h4>Khandhasuttaṁ </h4><p class="p3"><span class="t10">The Discourse about the Constituent Groups (of Mind &amp; Body)</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">Introductory Verses</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbāsīvisajātīnaṁ dibbamantāgadaṁ viya,</span></p><p class="p3"><span class="t1">To all kinds of poisonous snakes it is like the medicine of a divine charm, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yaṁ nāseti visaṁ ghoraṁ sesañ-cāpi parissayaṁ.</span></p><p class="p3"><span class="t1">it destroys terrible poisons and all other dangers. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Āṇākkhettamhi sabbattha, sabbadā sabbapāṇinaṁ,</span></p><p class="p3"><span class="t1">In this order’s domain, (which is) everywhere, always, for all living creatures, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"><span class="t5">sabbaso pi nivāreti, parittaṁ taṁ bhaṇāmahe.</span> </span></p>', '-36-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 37, '<p class="p3"><span class="t1">it thoroughly wards off (all dangers), we will recite that safeguard. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">The Safeguard</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Virūpakkhehi me mettaṁ, mettaṁ Erāpathehi me,</span></p><p class="p3"><span class="t1">I am friendly with the Virūpakkhas, with the Erāpathas I am friendly, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Chabyāputtehi me mettaṁ, mettaṁ Kaṇhāgotamakehi ca. </span></p><p class="p3"><span class="t1">I am friendly with the Chabyāputtas, and friendly with the Kaṇhāgotamakas. [1] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Apādakehi me mettaṁ, mettaṁ dvipādakehi me,</span></p><p class="p3"><span class="t1">I am friendly with those without feet, with those with two feet I am friendly, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">catuppadehi me mettaṁ, mettaṁ bahuppadehi me. </span></p><p class="p3"><span class="t1">I am friendly with those with four feet, with those with many feet I am friendly. [2] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mā maṁ apādako hiṁsi, mā maṁ hiṁsi dvipādako,</span></p><p class="p3"><span class="t1">May the one without feet not hurt me, may the one with two feet not hurt me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mā maṁ catuppado hiṁsi, mā maṁ hiṁsi bahuppado. </span></p><p class="p3"><span class="t1">may the one with four feet not hurt me, may the one with many feet not hurt me. [3] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbe sattā, sabbe pāṇā, sabbe bhūtā ca kevalā,</span></p><p class="p3"><span class="t1">May all beings, all living creatures, all who are born, in their entirety, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe bhadrāni passantu, mā kañci pāpam-āgamā. </span></p><p class="p3"><span class="t1">may all see prosperity, may nothing bad come to anyone. [4] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appamāṇo Buddho!</span></p><p class="p3"><span class="t1">The Buddha is measureless! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appamāṇo Dhammo!</span></p><p class="p3"><span class="t1">The Dhamma is measureless! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appamāṇo Saṅgho!</span></p><p class="p3"><span class="t1">The Saṅgha is measureless! </span></p>', '-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 38, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pamāṇavantāni sirīsapāni,</span></p><p class="p3"><span class="t1">Measurable are creeping things, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ahivicchikā, satapadī, uṇṇānābhī, sarabū, mūsikā.</span></p><p class="p3"><span class="t1">(such as) snakes, scorpions, centipedes, spiders, lizards, and rats. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katā me rakkhā, katā me parittā, paṭikkamantu bhūtāni!</span></p><p class="p3"><span class="t1">I have made this protection, I have made this safeguard, may (all these) beings go away! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t23">Sohaṁ namo Bhagavato, namo sattannaṁ Sammāsambuddhānaṁ.</span></p><p class="p3"><span class="t1">I revere the Fortunate One, I revere the seven Perfect Sambuddhas. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t19">Khandhasuttaṁ Niṭṭhitaṁ</span></p><p class="p3"><span class="t18">The Discourse about the Constituent Groups (of Mind &amp; Body) is Finished</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p>', '-38-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Five ', 'title', 39);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Morasuttaṁ ', 'title', 39);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 39, '<h3>Evening Paritta Chants, Day Five </h3><p class="p3"><span class="t9"> </span></p><h4>Morasuttaṁ </h4><p class="p3"><span class="t10">The Discourse about the Peacock</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">Introductory Verses</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pūrentaṁ Bodhisambhāre nibbattaṁ morayoninaṁ.</span></p><p class="p3"><span class="t1">While fulfilling the conditions for Awakening he was born from a peahen’s womb. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yena saṁvihitārakkhaṁ Mahāsattaṁ vane carā</span></p><p class="p3"><span class="t1">As the Great Being was protected with (a safeguard) the forest dwellers </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">cirassaṁ vāyamantā pi, neva sakkhiṁsu gaṇhituṁ.</span></p><p class="p3"><span class="t1">were unable to catch him, though they endeavoured for a long time. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Brahmamantan”-ti akkhātaṁ, parittaṁ taṁ bhaṇāmahe.</span></p><p class="p3"><span class="t1">It is declared to be “Brahma’s Charm”, we will recite that safeguard. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">The Safeguard</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Udetayaṁ Cakkhumā ekarājā,</span></p><p class="p3"><span class="t1">“That One who gives Vision, the sole king, comes up, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">harissavaṇṇo pathavippabhāso,</span></p><p class="p3"><span class="t1">he is golden coloured, he enlightens the earth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṁ taṁ namassāmi harissavaṇṇaṁ pathavippabhāsaṁ,</span></p><p class="p3"><span class="t1">therefore I revere the golden coloured one, who enlightens the earth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tayājja guttā viharemu divasaṁ. </span></p><p class="p3"><span class="t1">guarded by you today, we will live out the day. [1] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye brāhmaṇā vedagū sabbadhamme</span></p><p class="p3"><span class="t1">° I revere those brahmins who have true understanding </span></p>', '-39-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 40, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te me namo - te ca maṁ pālayantu!</span></p><p class="p3"><span class="t1">of all things - may they keep watch over me! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Namatthu Buddhānaṁ! Namatthu Bodhiyā!</span></p><p class="p3"><span class="t1">Revere the Buddhas! Revere Awakening! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Namo Vimuttānaṁ! Namo Vimuttiyā!” </span></p><p class="p3"><span class="t1">Reverence to the Free! Reverence to Freedom!” [2] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Imaṁ so parittaṁ katvā moro carati esanā.</span></p><p class="p3"><span class="t1">After making this safeguard the peacock roams about seeking (for food). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Apetayaṁ Cakkhumā ekarājā,</span></p><p class="p3"><span class="t1">“That One who gives Vision, the sole king, goes down, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">harissavaṇṇo pathavippabhāso,</span></p><p class="p3"><span class="t1">he is golden coloured, he enlightens the earth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṁ taṁ namassāmi harissavaṇṇaṁ pathavippabhāsaṁ,</span></p><p class="p3"><span class="t1">therefore I revere the golden coloured one, who enlightens the earth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tayājja guttā viharemu rattiṁ. </span></p><p class="p3"><span class="t1">guarded by you today, we will live out the night. [3] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye brāhmaṇā vedagū sabbadhamme</span></p><p class="p3"><span class="t1">° I revere those brahmins who have true understanding </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te me namo - te ca maṁ pālayantu!</span></p><p class="p3"><span class="t1">of all things - may they keep watch over me! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Namatthu Buddhānaṁ! Namatthu Bodhiyā!</span></p><p class="p3"><span class="t1">Revere the Buddhas! Revere Awakening! </span></p><p class="p3"><span class="t1">  </span></p>', '-40-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Six ', 'title', 41);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Vaṭṭasuttaṁ ', 'title', 41);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 41, '<p class="p3"><span class="t5">Namo Vimuttānaṁ! Namo Vimuttiyā!”</span></p><p class="p3"><span class="t1">Reverence to the Free! Reverence to Freedom!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Imaṁ so parittaṁ katvā moro vāsam-akappayi. </span></p><p class="p3"><span class="t1">After making this safeguard the peacock dwells on (without fear). [4] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t19">Morasuttaṁ Niṭṭhitaṁ</span></p><p class="p3"><span class="t18">The Discourse about the Peacock is Finished</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Evening Paritta Chants, Day Six </h3><p class="p3"><span class="t9"> </span></p><h4>Vaṭṭasuttaṁ </h4><p class="p3"><span class="t10">The Discourse about the Quail</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t18">Introductory Verses</span></p><p class="p3"><span class="t1">  </span></p>', '-41-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 42, '<p class="p3"><span class="t5">Pūrentaṁ Bodhisambhāre nibbattaṁ vaṭṭajātiyaṁ,</span></p><p class="p3"><span class="t1">While fulfilling the conditions for Awakening he was born as a kind of quail, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa tejena dāvaggi Mahāsattaṁ vivajjayi.</span></p><p class="p3"><span class="t1">and through his spiritual power the Great Being avoided the forest-fire. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Therassa Sāriputtassa Lokanāthena bhāsitaṁ,</span></p><p class="p3"><span class="t1">This was spoken by the Lord of the World to the Elder Sāriputta, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kappaṭṭhāyiṁ mahātejaṁ: parittaṁ taṁ bhaṇamahe.</span></p><p class="p3"><span class="t1">it has great power for a world-cycle: we will recite that safeguard. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t18">The Safeguard</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atthi loke sīlaguṇo saccaṁ soceyyanuddayā,</span></p><p class="p3"><span class="t1">There is in the world virtue, truth, purity and empathy, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tena saccena kāhāmi, saccakiriyam-uttamaṁ,</span></p><p class="p3"><span class="t1">by the truth of that I will make an asseveration of supreme truth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Āvajjetvā Dhammabalaṁ, saritvā pubbake Jine,</span></p><p class="p3"><span class="t1">After reflecting on the strength of the Dhamma, and recalling the former Victors, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saccabalam-avassāya, saccakiriyam-akāsahaṁ:</span></p><p class="p3"><span class="t1">relying on the strength of truth, I made an asseveration of truth: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Santi pakkhā apatanā, santi pādā avañcanā,</span></p><p class="p3"><span class="t1">“There are wings which fly not, there are feet which walk not, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mātāpitā ca nikkhantā, Jātaveda, paṭikkama!”</span></p><p class="p3"><span class="t1">Mother and Father have gone out, Fire, please depart!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sahasacce kate mayhaṁ, mahāpajjalito sikhī</span></p><p class="p3"><span class="t1">This (asseveration) was made by me with truth, and the great blazing fire </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vajjesi soḷasakarīsāni, udakaṁ patvā yathā sikhī,</span></p>', '-42-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Seven ', 'title', 43);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Dhajaggasuttaṁ ', 'title', 43);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 43, '<p class="p3"><span class="t1">avoided (this area for) sixteen measures, like fire after reaching water, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saccena me samo natthi, esā me saccapāramī!</span></p><p class="p3"><span class="t1">there is no one my equal for truth, such is my perfection of truth! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t19">Vaṭṭasuttaṁ Nitthitaṁ</span></p><p class="p3"><span class="t18">The Discourse about the Quail</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Evening Paritta Chants, Day Seven </h3><p class="p3"><span class="t9"> </span></p><h4>Dhajaggasuttaṁ </h4><p class="p3"><span class="t10">The Discourse about the Top of a Banner</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">Introductory Verses</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yassānussaraṇenāpi antalikkhe pi pāṇino</span></p>', '-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 44, '<p class="p3"><span class="t1">By the recollection of that (safeguard) living creatures in the firmament </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">patiṭṭham-adhigacchanti, bhūmiyaṁ viya sabbathā.</span></p><p class="p3"><span class="t1">obtain complete support, just like (living creatures) everywhere on earth. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbupaddavajālamhā yakkhacorārisambhavā,</span></p><p class="p3"><span class="t1">From the net of all adversity arising from yakkhas, thieves, and rivals, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gaṇanā na ca muttānaṁ, parittaṁ taṁ bhaṇāmahe.</span></p><p class="p3"><span class="t1">those who were released cannot be numbered, we will recite that safeguard. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">The Safeguard</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ me sutaṁ:</span></p><p class="p3"><span class="t1">Thus I have heard: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Sāvatthiyaṁ viharati</span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling near Sāvatthi </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jetavane Anāthapiṇḍikassa ārāme.</span></p><p class="p3"><span class="t1">at Anāthapiṇḍika’s grounds in Jeta’s Wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tatra kho Bhagavā bhikkhū āmantesi:</span></p><p class="p3"><span class="t1">There it was that the Fortunate One addressed the monks, saying: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Bhikkhavo!” ti, “Bhadante!” ti te bhikkhū Bhagavato paccassosuṁ,</span></p><p class="p3"><span class="t1">“Monks!”, “Reverend Sir!” those monks replied to the Fortunate One, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Bhagavā etad-avoca:</span></p><p class="p3"><span class="t1">and the Fortunate One said this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Bhūtapubbaṁ bhikkhave devāsurasaṅgāmo samupabyūḷho ahosi.</span></p><p class="p3"><span class="t1">“Formerly, monks, there was a massed battle between the gods and the asuras. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho bhikkhave Sakko devānamindo deve Tāvatiṁse āmantesi:</span></p><p class="p3"><span class="t1">Then, monks, the lord of the gods Sakka addressed the Tāvatiṁsa gods, saying: </span></p>', '-44-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 45, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sace mārisā devānaṁ saṅgāmagatānaṁ</span></p><p class="p3"><span class="t1">“If, dear Sirs, to the gods who have gone into battle </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">uppajjeyya bhayaṁ vā chambhitattaṁ vā lomahaṁso vā,</span></p><p class="p3"><span class="t1">there should arise fear, or terror, or horror, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mameva tasmiṁ samaye dhajaggaṁ ullokeyyātha.</span></p><p class="p3"><span class="t1">at that time you could look for the top of my banner. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mamaṁ hi vo dhajaggaṁ ullokayataṁ</span></p><p class="p3"><span class="t1">For to those who look to the top of my banner </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā, lomahaṁso vā so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">No ce me dhajaggaṁ ullokeyyātha,</span></p><p class="p3"><span class="t1">If you cannot look to the top of my banner, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">atha Pajāpatissa devarājassa dhajaggaṁ ullokeyyātha.</span></p><p class="p3"><span class="t1">then you could look for the top of the god-king Pajāpati’s banner. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pajāpatissa hi vo devarājassa dhajaggaṁ ullokayataṁ</span></p><p class="p3"><span class="t1">For to those who look to the top of the god-king Pajāpati’s banner </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā, lomahaṁso vā so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">No ce Pajāpatissa devarājassa dhajaggaṁ ullokeyyātha,</span></p><p class="p3"><span class="t1">If you cannot look to the top of the god-king Pajāpati’s banner, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">atha Varuṇassa devarājassa dhajaggaṁ ullokeyyātha.</span></p><p class="p3"><span class="t1">then you could look for the top of the god-king Varuṇa’s banner. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Varuṇassa hi vo devarājassa dhajaggaṁ ullokayataṁ</span></p><p class="p3"><span class="t1">For to those who look to the top of the god-king Varuṇa’s banner </span></p>', '-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 46, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā, lomahaṁso vā, so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">No ce Varuṇassa devarājassa dhajaggaṁ ullokeyyātha,</span></p><p class="p3"><span class="t1">If you cannot look to the top of the god-king Varuṇa’s banner, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">atha Īsānassa devarājassa dhajaggaṁ ullokeyyātha.</span></p><p class="p3"><span class="t1">then you could look for the top of the god-king Īsāna’s banner. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Īsānassa hi vo devarājassa dhajaggaṁ ullokayataṁ</span></p><p class="p3"><span class="t1">For to those who look to the top of the god-king Īsāna’s banner </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t23">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā, lomahaṁso vā so pahīyissatī ti.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Taṁ kho pana bhikkhave </span></p><p class="p3"><span class="t1">But, monks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sakkassa vā devānamindassa dhajaggaṁ ullokayataṁ,</span></p><p class="p3"><span class="t1">to he who looks to the top of the lord of the gods Sakka’s banner, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pajāpatissa vā devarājassa dhajaggaṁ ullokayataṁ,</span></p><p class="p3"><span class="t1">or who looks to the top of the god-king Pajāpati’s banner, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Varuṇassa vā devarājassa dhajaggaṁ ullokayataṁ,</span></p><p class="p3"><span class="t1">or who looks to the top of the god-king Varuṇa’s banner, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Īsānassa vā devarājassa dhajaggaṁ ullokayataṁ,</span></p><p class="p3"><span class="t1">or who looks to the top of the god-king Īsāna’s banner, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā lomahaṁso vā,</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">so pahīyethāpi no pi pahīyetha.</span></p><p class="p3"><span class="t1">may be given up, and may not be given up. </span></p>', '-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 47, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Taṁ kissa hetu?</span></p><p class="p3"><span class="t1">What is the reason for that? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sakko hi bhikkhave devānamindo</span></p><p class="p3"><span class="t1">The lord of the gods Sakka, monks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">avītarāgo avītadoso avītamoho, bhīru chambhi utrāsi palāyī ti.</span></p><p class="p3"><span class="t1">being fearful, terrified, scared, he ran away. is not free from passion, is not free from hatred, is not free from delusion, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ahañ-ca kho bhikkhave evaṁ vadāmi:</span></p><p class="p3"><span class="t1">But I say this, monks: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sace tumhākaṁ bhikkhave araññagatānaṁ vā,</span></p><p class="p3"><span class="t1">If to you, monks, after going to the wilderness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">rukkhamūlagatānaṁ vā, suññāgāragatānaṁ vā,</span></p><p class="p3"><span class="t1">or to the root of a tree, or to an empty place, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">uppajjeyya bhayaṁ vā chambhitattaṁ vā lomahaṁso vā,</span></p><p class="p3"><span class="t1">there should arise fear, or terror, or horror, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mameva tasmiṁ samaye anussareyyātha:</span></p><p class="p3"><span class="t1">at that time you could recollect me thus: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti pi so Bhagavā Arahaṁ Sammāsambuddho,</span></p><p class="p3"><span class="t1">“Such is he, the Fortunate One, the Worthy One, the Perfect Sambuddha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vijjācaraṇasampanno Sugato lokavidū,</span></p><p class="p3"><span class="t1">the one endowed with understanding and good conduct, the Happy One, the One who understands the worlds, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaro purisadammasārathi,</span></p><p class="p3"><span class="t1">the unsurpassed guide for those people who need taming, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Satthā devamanussānaṁ Buddho Bhagavā” ti.</span></p><p class="p3"><span class="t1">the Teacher of gods and men, the Buddha, the Fortunate One.” </span></p>', '-47-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 48, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mamaṁ hi vo bhikkhave anussarataṁ</span></p><p class="p3"><span class="t1">For, monks, to those who recollect me </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā lomahaṁso vā so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">No ce maṁ anussareyyātha, atha Dhammaṁ anussareyyātha:</span></p><p class="p3"><span class="t1">If you cannot recollect me, then recollect the Dhamma thus: </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">“Svākkhāto Bhagavatā Dhammo,</span></p><p class="p3"><span class="t1">“The Dhamma has been well-proclaimed by the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sandiṭṭhiko, akāliko, ehipassiko, opaneyyiko,</span></p><p class="p3"><span class="t1">it is visible, not subject to time, inviting inspection, onward leading, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">paccattaṁ veditabbo viññūhī” ti.</span></p><p class="p3"><span class="t1">and can be understood by the wise for themselves.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dhammaṁ hi vo bhikkhave anussarataṁ</span></p><p class="p3"><span class="t1">For, monks, to those who recollect the Dhamma </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā lomahaṁso vā so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">No ce Dhammaṁ anussareyyātha, atha Saṅghaṁ anussareyyātha:</span></p><p class="p3"><span class="t1">If you cannot recollect the Dhamma, then recollect the Saṅgha thus: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Suppaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">“The Fortunate One’s Saṅgha of disciples are good in their practice, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ujuppaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">the Fortunate One’s Saṅgha of disciples are upright in their practice, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">ñāyappaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">the Fortunate One’s Saṅgha of disciples are systematic in their practice, </span></p>', '-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 49, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sāmīcippaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">the Fortunate One’s Saṅgha of disciples are correct in their practice, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yad-idaṁ cattāri purisayugāni aṭṭha purisapuggalā,</span></p><p class="p3"><span class="t1">that is to say, the four pairs of persons, the eight individual persons, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">esa Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">this is the Fortunate One’s Saṅgha of disciples, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">āhuneyyo, pāhuneyyo, dakkhiṇeyyo, añjalikaranīyo,</span></p><p class="p3"><span class="t1">they are worthy of offerings, of hospitality, of gifts, and of reverential salutation, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ puññakkhettaṁ lokassā” ti.</span></p><p class="p3"><span class="t1">they are an unsurpassed field of merit for the world.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saṅghaṁ hi vo bhikkhave anussarataṁ</span></p><p class="p3"><span class="t1">For, monks, to those who recollect the Saṅgha </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā lomahaṁso vā so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Taṁ kissa hetu?</span></p><p class="p3"><span class="t1">What is the reason for that? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tathāgato hi bhikkhave Arahaṁ Sammāsambuddho,</span></p><p class="p3"><span class="t1">The Realised One, monks, the Worthy One, the Perfect Sambuddha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vītarāgo, vītadoso, vītamoho, abhīru acchambhī anutrāsī apalāyī” ti.</span></p><p class="p3"><span class="t1">is free from passion, is free from hatred, is free from delusion, not being fearful, not terrified, not scared, he did not run away.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-avoca Bhagavā, idaṁ vatvā Sugato athāparaṁ etad-avoca Satthā:</span></p><p class="p3"><span class="t1">The Fortunate One said this, and after saying this, the Happy One, the Teacher, said something more: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Araññe rukkhamūle vā, suññāgāre va bhikkhavo,</span></p><p class="p3"><span class="t1">“In the wilds, at the root of a tree, or in an empty place, monks, </span></p>', '-49-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 50, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anussaretha Sambuddhaṁ, bhayaṁ tumhāka’ no siyā. </span></p><p class="p3"><span class="t1">you must recollect the Sambuddha, and there will be no fear for you. [1] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">No ce Buddhaṁ sareyyātha, lokajeṭṭhaṁ narāsabhaṁ,</span></p><p class="p3"><span class="t1">if you can’t remember the Buddha, the world’s elder, the man-bull, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">atha Dhammaṁ sareyyātha, niyyānikaṁ sudesitaṁ. </span></p><p class="p3"><span class="t1">then you must remember the Dhamma, which leads out, which is well preached. [2] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">No ce Dhammaṁ sareyyātha, niyyānikaṁ sudesitaṁ,</span></p><p class="p3"><span class="t1">If you can’t remember the Dhamma, which leads out, which is well preached, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">atha Saṅghaṁ sareyyātha, puññakkhettaṁ anuttaraṁ. </span></p><p class="p3"><span class="t1">then you must remember the Saṅgha, which is an unsurpassed field of merit. [3] </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ Buddhaṁ sarantānaṁ, Dhammaṁ Saṅghañ-ca bhikkhavo,</span></p><p class="p3"><span class="t1">Thus for those who remember the Buddha, the Dhamma, or the Saṅgha, monks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhayaṁ vā chambhitattaṁ vā, lomahaṁso na hessati. </span></p><p class="p3"><span class="t1">(there will be no) fear, or terror, there will be no horror. [4] </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t19">Dhajaggasuttaṁ Niṭṭhitaṁ</span></p><p class="p3"><span class="t18">The Discourse about the Top of a Banner is Finished</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p>', '-50-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Eight ', 'title', 51);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Āṭānāṭiyasuttaṁ ', 'title', 51);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 51, '<p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Evening Paritta Chants, Day Eight </h3><p class="p3"><span class="t9"> </span></p><h4>Āṭānāṭiyasuttaṁ </h4><p class="p3"><span class="t10">The Āṭānāṭiya Discourse</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t18">Introductory Verses</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Appasannehi Nāthassa Sāsane sādhusammate,</span></p><p class="p3"><span class="t1">About those having little faith in the Lord’s well-honoured Dispensation, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">amanussehi caṇḍehi, sadā kibbisakāribhi,</span></p><p class="p3"><span class="t1">about violent non-human beings, who are always criminals, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">parisānaṁ catassannaṁ ahiṁsāya ca guttiyā,</span></p><p class="p3"><span class="t1">* this is what was preached by the Great Hero to the four assemblies, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yaṁ desesi Mahāvīro: parittaṁ taṁ bhaṇāmahe.</span></p><p class="p3"><span class="t1">about non-violence and watchfulness: we will recite that safeguard. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">The Safeguard</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vipassissa namatthu, Cakkhumantassa sirīmato,</span></p><p class="p3"><span class="t1">May you revere Vipassī, the glorious Visionary, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sikhissa pi namatthu, sabbabhūtānukampino,</span></p><p class="p3"><span class="t1">may you revere Sikhī, who has pity on all beings, </span></p><p class="p3"><span class="t1">  </span></p>', '-51-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 52, '<p class="p3"><span class="t5">Vessabhussa ca namatthu, nhātakassa tapassino,</span></p><p class="p3"><span class="t1">may you revere Vessabhū, the austere one, cleansed (of corruptions), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">namatthu Kakusandhassa, Mārasenāpamaddino,</span></p><p class="p3"><span class="t1">may you revere Kakusandha, who has crushed Māra’s army, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Koṇāgamanassa namatthu, brāhmaṇassa vusīmato,</span></p><p class="p3"><span class="t1">may you revere Koṇāgamana, the accomplished brahmin, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kassapassa ca namatthu, vippamuttassa sabbadhi.</span></p><p class="p3"><span class="t1">may you revere Kassapa, who is free in every respect. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Aṅgīrasassa namatthu, Sakyaputtassa sirīmato,</span></p><p class="p3"><span class="t1">May you revere Aṅgīrasa, the glorious son of the Sakyans, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yo imaṁ Dhammam-adesesi, sabbadukkhapanūdanaṁ.</span></p><p class="p3"><span class="t1">he who preached this Dhamma, which is the dispelling of all suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye cāpi nibbutā loke, yathābhūtaṁ vipassisuṁ,</span></p><p class="p3"><span class="t1">Those who are emancipated in the world, who have insight (into things) as they are, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te janā apisuṇātha, mahantā vītasāradā,</span></p><p class="p3"><span class="t1">those people free from malicious speech, who are great and fully mature, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hitaṁ devamanussānaṁ, yaṁ namassanti Gotamaṁ,</span></p><p class="p3"><span class="t1">they will revere that Gotama, who is of benefit to gods and men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vijjācaraṇasampannaṁ, mahantaṁ vītasāradaṁ.</span></p><p class="p3"><span class="t1">who has understanding and good conduct, who is great and fully mature. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ete caññe ca Sambuddhā, anekasatakotiyo,</span></p><p class="p3"><span class="t1">Those other Buddhas, countless hundreds of millions, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe Buddhā ’samasamā, sabbe Buddhā mahiddhikā,</span></p><p class="p3"><span class="t1">all those unequalled Buddhas, all those Buddhas of great power, </span></p><p class="p3"><span class="t1">  </span></p>', '-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 53, '<p class="p3"><span class="t5">sabbe Dasabalūpetā, vesārajjehupāgatā,</span></p><p class="p3"><span class="t1">all those endowed with Ten Powers, with (supreme) confidence, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe te paṭijānanti Āsabhaṁ ṭhānam-uttamaṁ.</span></p><p class="p3"><span class="t1">they all acknowledge the Bull in the supreme abode. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sīhanādaṁ nadantete parisāsu visāradā,</span></p><p class="p3"><span class="t1">These roar the Lion’s Roar confidently amongst the assemblies, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">brahmacakkaṁ pavattenti, loke appaṭivattiyaṁ.</span></p><p class="p3"><span class="t1">and turn the supreme (Dhamma) wheel, which cannot be turned back in the world. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Upetā Buddhadhammehi aṭṭhārasahi nāyakā,</span></p><p class="p3"><span class="t1">They are Leaders endowed with eighteen things pertaining to the Buddhas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bāttiṁsalakkhaṇūpetā, sītānubyañjanādharā,</span></p><p class="p3"><span class="t1">endowed with thirty-two marks, and bearing eighty minor characteristics, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">byāmappabhāya suppabhā, sabbe te munikuñjarā.</span></p><p class="p3"><span class="t1">radiating a halo all round, they are all elephant-like sages. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Buddhā sabbaññuno ete sabbe khīṇāsavā Jinā,</span></p><p class="p3"><span class="t1">These omniscient Buddha are all Victors who are pollutant-free, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mahāpabhā mahātejā, mahāpaññā mahabbalā,</span></p><p class="p3"><span class="t1">having great radiance, great power, great wisdom, great strength, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mahākāruṇikā, dhīrā sabbesānaṁ sukhāvahā.</span></p><p class="p3"><span class="t1">great compassion, wise men bringing happiness to all. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dīpā Nāthā patiṭṭhā ca tāṇā lenā ca pāṇinaṁ,</span></p><p class="p3"><span class="t1">These Lords establish support, shelter and protection for (all) breathing beings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gatī bandhū mahassāsā saraṇā ca hitesino,</span></p><p class="p3"><span class="t1">They seek the benefit of all kinsmen who have a great desire for refuge, </span></p><p class="p3"><span class="t1">  </span></p>', '-53-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 54, '<p class="p3"><span class="t5">sadevakassa lokassa, sabbe ete parāyaṇā.</span></p><p class="p3"><span class="t1">in the world with its gods and men, they have all crossed over. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tesāhaṁ sirasā pāde vandāmi purisuttame,</span></p><p class="p3"><span class="t1">I worship the feet of these supreme persons with my head, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vacasā manasā ceva, vandāmete Tathāgate,</span></p><p class="p3"><span class="t1">with my words, with my mind, I worship these Realised Ones, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sayane āsane ṭhāne gamane cāpi sabbadā.</span></p><p class="p3"><span class="t1">while lying, sitting, standing and going everyday. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sadā sukhena rakkhantu, Buddhā santikarā tuvaṁ,</span></p><p class="p3"><span class="t1">May the Buddhas, who are peacemakers, always happily protect you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tehi tvaṁ rakkhito santo mutto sabbabhayehi ca,</span></p><p class="p3"><span class="t1">and may you be protected by them, peaceful and free from all fear. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbarogā vinimutto, sabbasantappavajjito,</span></p><p class="p3"><span class="t1">may you be free of all diseases, avoid all grief, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbaveram-atikkanto, nibbuto ca tuvaṁ bhava!</span></p><p class="p3"><span class="t1">overcome all enemies, and may you attain nibbāna! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tesaṁ saccena sīlena, khantimettābalena ca,</span></p><p class="p3"><span class="t1">Through their truth and virtue, and the strength of their patience and friendliness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te pi </span><span class="t5">tumhe </span><span class="t24">’</span><span class="t5">nurakkhantu</span><span class="t5"> arogena sukhena ca!</span></p><p class="p3"><span class="t1">may the (Buddhas) always protect you with health and happiness! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Puratthimasmiṁ disābhāge santi bhūtā mahiddhikā,</span></p><p class="p3"><span class="t1">In the easterly direction there are beings of great power, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te pi tumhe </span><span class="t24">’</span><span class="t5">nurakkhantu arogena sukhena ca</span><span class="t5">!</span></p><p class="p3"><span class="t1">may the (Buddhas) always protect you with health and happiness! </span></p><p class="p3"><span class="t1">  </span></p>', '-54-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 55, '<p class="p3"><span class="t5">Dakkhinasmiṁ disābhāge santi devā mahiddhikā,</span></p><p class="p3"><span class="t1">In the southerly direction there are gods of great power, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te pi tumhe </span><span class="t24">’</span><span class="t5">nurakkhantu arogena sukhena ca!</span></p><p class="p3"><span class="t1">may the (Buddhas) always protect you with health and happiness! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pacchimasmiṁ disābhāge santi nāgā mahiddhikā,</span></p><p class="p3"><span class="t1">In the westerly direction there are nāgas of great power, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Te pi tumhe </span><span class="t24">’</span><span class="t5">nurakkhantu arogena sukhena ca</span><span class="t5">!</span></p><p class="p3"><span class="t1">may the (Buddhas) always protect you with health and happiness! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Uttarasmiṁ disābhāge santi yakkhā mahiddhikā,</span></p><p class="p3"><span class="t1">In the northerly direction there are yakkhas of great power, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te pi tumhe </span><span class="t24">’</span><span class="t5">nurakkhantu arogena sukhena ca</span><span class="t5">!</span></p><p class="p3"><span class="t1">may the (Buddhas) always protect you with health and happiness! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Puratthimena Dhataraṭṭho, Dakkhiṇena Virūḷhako,</span></p><p class="p3"><span class="t1">To the East there is Dhataraṭṭha, in the South there is Virūḷhaka, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pacchimena Virūpakkho, Kuvero Uttaraṁ disaṁ.</span></p><p class="p3"><span class="t1">in the West there is Virūpakkha, to the North there is Kuvera. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Cattāro te Mahārājā, </span><span class="t5">lokapālā yasassino,</span></p><p class="p3"><span class="t1">These are the Four Great Kings, who are famous and protect the world, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te pi tumhe </span><span class="t24">’</span><span class="t5">nurakkhantu arogena sukhena ca</span><span class="t5">!</span></p><p class="p3"><span class="t1">may these (Buddhas) always protect you with health and happiness! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ākāsaṭṭhā ca bhummaṭṭhā devā nāgā mahiddhikā,</span></p><p class="p3"><span class="t1">(There are) powerful gods and nāgas stationed in the sky or on the earth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te pi tumhe </span><span class="t24">’</span><span class="t5">nurakkhantu arogena sukhena ca.</span></p><p class="p3"><span class="t1">may these (Buddhas) always protect you with health and happiness! </span></p>', '-55-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 56, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Iddhimanto ca ye devā vasantā idha Sāsane,</span></p><p class="p3"><span class="t1">There are gods endowed with psychic power dwelling here in the Dispensation, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te pi tumhe </span><span class="t24">’</span><span class="t5">nurakkhantu arogena sukhena ca</span><span class="t5">!</span></p><p class="p3"><span class="t1">may these (Buddhas) always protect you with health and happiness! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbītiyo vivajjantu soko rogo vinassatu,</span></p><p class="p3"><span class="t1">May you avoid all calamities, may grief and disease be destroyed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mā te bhavatvantarāyo, sukhī dīghāyuko bhava!</span></p><p class="p3"><span class="t1">may there be no obstacles for you, may you be happy and live long! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Abhivādanasīlissa niccaṁ vuḍḍhā pacāyino,</span></p><p class="p3"><span class="t1">For one in the habit of constantly worshipping respectable elders, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">cattāro dhammā vaḍḍhanti: āyu, vaṇṇo, sukhaṁ, balaṁ.</span></p><p class="p3"><span class="t1">four things increase: length of life, beauty, happiness, and strength. </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p>', '-56-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Nine ', 'title', 57);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Aṅgulimālasuttaṁ ', 'title', 57);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 57, '<p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Evening Paritta Chants, Day Nine </h3><p class="p3"><span class="t9"> </span></p><h4>Aṅgulimālasuttaṁ </h4><p class="p3"><span class="t10">The Discourse about Aṅgulimāla</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t18">Introductory Verses</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Parittaṁ yaṁ bhaṇantassa nisinnaṭṭhānadhovanaṁ</span></p><p class="p3"><span class="t1">When this safeguard is being recited (even) the washing water </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">udakam-pi vināseti sabbam-eva parissayaṁ.</span></p><p class="p3"><span class="t1">(taken) from the sitting place will destroy all dangers. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sotthinā gabbhavuṭṭhānaṁ yaṁ ca sādheti taṁ khaṇe,”</span></p><p class="p3"><span class="t1">“For sure this will effect a safe birth from the womb in a moment,” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Therass’ Aṅgulimālassa Lokanāthena bhāsitaṁ,</span></p><p class="p3"><span class="t1">this was spoken by the Lord of the World to the Elder Aṅgulimāla, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kappaṭṭhāyiṁ mahātejaṁ, parittaṁ taṁ bhaṇāmahe.</span></p><p class="p3"><span class="t1">it has great power for a world-cycle, we will recite that safeguard. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t18">The Safeguard</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yatohaṁ bhagini Ariyāya jātiyā jāto</span></p><p class="p3"><span class="t1">Sister, from the time I was born in a Noble birth </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">nābhijānāmi sañcicca pāṇaṁ jīvitā voropetā,</span></p><p class="p3"><span class="t1">I am not aware of having intentionally deprived a being of life, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tena saccena sotthi te hotu sotthi gabbhassa!</span></p><p class="p3"><span class="t1">through this truth may you be safe, and may your foetus be safe! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t19">Aṅgulimālasuttaṁ Niṭṭhitaṁ</span></p>', '-57-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Paṭhamagilānasuttaṁ ', 'title', 58);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 58, '<p class="p3"><span class="t18">The Discourse about Aṅgulimāla is Finished</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><h4>Paṭhamagilānasuttaṁ </h4><p class="p3"><span class="t10">The First Discourse about being Sick</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t25">Ekaṁ samayaṁ Bhagavā Rājagahe viharati</span></p><p class="p3"><span class="t1">At one time the Fortunate One was dwelling near Rājagaha </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t25">Veḷuvane Kalandakanivāpe.</span></p><p class="p3"><span class="t1">at the Squirrels’ Feeding Place in Bamboo Wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t25">Tena kho pana samayena āyasmā Mahākassapo</span></p><p class="p3"><span class="t1">Then at that time venerable Mahā Kassapa </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t25">Pipphaliguhāyaṁ viharati, ābādhiko dukkhito bāḷhagilāno.</span></p><p class="p3"><span class="t1">was dwelling at the Pepper Cave, and was afflicted, suffering, and very sick. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t25">Atha kho Bhagavā sāyanhasamayaṁ patisallānā vuṭṭhito,</span></p><p class="p3"><span class="t1">Then the Fortunate One, rising from seclusion in the evening time, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">yenāyasmā Mahākassapo tenupasaṅkami,</span></p><p class="p3"><span class="t1">approached venerable Mahā Kassapa, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upasaṅkamitvā paññatte āsane nisīdi.</span></p><p class="p3"><span class="t1">and after approaching he sat down on the prepared seat. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nisajja kho Bhagavā āyasmantaṁ Mahākassapaṁ etad-avoca:</span></p><p class="p3"><span class="t1">Having sat down the Fortunate One said this to venerable Mahā Kassapa: </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">“Kacci te Kassapa khamanīyaṁ? Kacci yāpanīyaṁ?</span></p><p class="p3"><span class="t1">“Can you bear up, Kassapa? Can you carry on? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kacci dukkhā vedanā paṭikkamanti no abhikkamanti?</span></p><p class="p3"><span class="t1">Do painful feelings decrease and not increase? </span></p><p class="p3"><span class="t1">  </span></p>', '-58-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 59, '<p class="p3"><span class="t5">Paṭikkamosānaṁ paññāyati no abhikkamo?” ti</span></p><p class="p3"><span class="t1">Is it known that they are decreasing and not increasing?” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Na me bhante khamanīyaṁ na yāpanīyaṁ,</span></p><p class="p3"><span class="t1">“No, reverend Sir, I cannot bear up, nor can I carry on, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bāḷhā me dukkhā vedanā abhikkamanti no paṭikkamanti,</span></p><p class="p3"><span class="t1">painful feelings greatly increase for me and do not decrease, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abhikkamosānaṁ paññāyati no paṭikkamo” ti.</span></p><p class="p3"><span class="t1">and it is known that they increase and do not decrease.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sattime Kassapa Bojjhaṅgā mayā sammad-akkhātā,</span></p><p class="p3"><span class="t1">“There are these seven factors of Awakening, Kassapa, that have been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvitā bahulīkatā abhiññāya Sambodhāya Nibbānāya saṁvattanti.</span></p><p class="p3"><span class="t1">when developed and made much of they lead to deep knowledge, Complete Awakening, and Nibbāna. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katame satta?</span></p><p class="p3"><span class="t1">What are the seven? </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Satisambojjhaṅgo kho Kassapa mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is mindfulness, Kassapa, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[1]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t15">Dhammavicayasambojjhaṅgo kho Kassapa mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is investigation of (the nature of) things, Kassapa, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[2]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vīriyasambojjhaṅgo kho Kassapa mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is energy, Kassapa, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p>', '-59-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 60, '<p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[3]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pītisambojjhaṅgo kho Kassapa mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is rapture, Kassapa, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[4]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Passaddhisambojjhaṅgo kho Kassapa mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is calm, Kassapa, has been fully explained by me, </span></p><p class="p3"><span class="t14"> </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[5]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Samādhisambojjhaṅgo kho Kassapa mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is concentration, Kassapa, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[6]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Upekkhāsambojjhaṅgo kho Kassapa mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is equanimity, Kassapa, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[7]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ime kho Kassapa satta Bojjhaṅgā mayā sammad-akkhātā,</span></p><p class="p3"><span class="t1">These are the seven factors of Awakening, Kassapa, which have been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t26">bhāvitā bahulīkatā abhiññāya Sambodhāya Nibbānāya saṁvattantī” ti.</span></p><p class="p3"><span class="t1">when developed and made much of they lead to deep knowledge, Complete Awakening, and Nibbāna.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Taggha Bhagava Bojjhaṅgā!</span></p><p class="p3"><span class="t1">“Surely, Fortunate One, (these are) the factors of Awakening! </span></p><p class="p3"><span class="t1">  </span></p>', '-60-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 61, '<p class="p3"><span class="t5">Taggha Sugata Bojjhaṅgā!” ti.</span></p><p class="p3"><span class="t1">Surely, Happy One, (these are) the factors of Awakening!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-avoca Bhagavā,</span></p><p class="p3"><span class="t1">The Fortunate One said this, </span></p><p class="p3"><span class="t14"> </span></p><p class="p3"><span class="t5">attamano āyasmā Mahākassapo Bhagavato bhāsitaṁ abhinandi,</span></p><p class="p3"><span class="t1">and venerable Mahā Kassapa was uplifted and greatly rejoiced in what was said by the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vuṭṭhahi cāyasmā Mahākassapo tamhā ābādhā,</span></p><p class="p3"><span class="t1">and venerable Mahā Kassapa recovered from that affliction, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tathā pahīno cāyasmato Mahākassapassa so ābādho ahosī ti.</span></p><p class="p3"><span class="t1">and by that venerable Mahā Kassapa’s affliction was brought to an end. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p>', '-61-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Ten ', 'title', 62);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Dutiyagilānasuttaṁ ', 'title', 62);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 62, '<h3>Evening Paritta Chants, Day Ten </h3><p class="p3"><span class="t9"> </span></p><h4>Dutiyagilānasuttaṁ </h4><p class="p3"><span class="t10">The Second Discourse about being Sick</span></p><p class="p3"><span class="t27"> </span></p><p class="p3"><span class="t5">Ekaṁ samayaṁ Bhagavā Rājagahe viharati</span></p><p class="p3"><span class="t1">At one time the Fortunate One was dwelling near Rājagaha </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Veḷuvane Kalandakanivāpe.</span></p><p class="p3"><span class="t1">at the Squirrels’ Feeding Place in Bamboo Wood. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Tena kho pana samayena āyasmā Mahāmoggallāno</span></p><p class="p3"><span class="t1">Then at that time venerable Mahā Moggallāna </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gijjhakūṭe pabbate viharati, ābādhiko dukkhito bāḷhagilāno.</span></p><p class="p3"><span class="t1">was dwelling at the Vultures'' Peak mountain, and was afflicted, suffering, and very sick. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Bhagavā sāyanhasamayaṁ patisallānā vuṭṭhito,</span></p><p class="p3"><span class="t1">Then the Fortunate One, rising from seclusion in the evening time, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yenāyasmā Mahāmoggallāno tenupasaṅkami,</span></p><p class="p3"><span class="t1">approached venerable Mahā Moggallāna, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upasaṅkamitvā paññatte āsane nisīdi.</span></p><p class="p3"><span class="t1">and after approaching he sat down on the prepared seat. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nisajja kho Bhagavā āyasmantaṁ Mahāmoggallānaṁ etad-avoca:</span></p><p class="p3"><span class="t1">Having sat down the Fortunate One said this to venerable Mahā Moggallāna: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Kacci te Moggallāna khamanīyaṁ? Kacci yāpanīyaṁ?</span></p><p class="p3"><span class="t1">“Can you bear up, Moggallāna? Can you carry on? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kacci dukkhā vedanā paṭikkamanti no abhikkamanti?</span></p><p class="p3"><span class="t1">Do painful feelings decrease and not increase? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Paṭikkamosānaṁ paññāyati no abhikkamo?” ti</span></p>', '-62-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 63, '<p class="p3"><span class="t1">Is it known that they are decreasing and not increasing?” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Na me bhante khamanīyaṁ na yāpanīyaṁ,</span></p><p class="p3"><span class="t1">“No, reverend Sir, I cannot bear up, nor can I carry on, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bāḷhā me dukkhā vedanā abhikkamanti no paṭikkamanti,</span></p><p class="p3"><span class="t1">painful feelings greatly increase for me and do not decrease, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abhikkamosānaṁ paññāyati no paṭikkamo” ti.</span></p><p class="p3"><span class="t1">and it is known that they increase and do not decrease.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sattime Moggallāna Bojjhaṅgā mayā sammad-akkhātā,</span></p><p class="p3"><span class="t1">“There are these seven factors of Awakening, Moggallāna, that have been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvitā bahulīkatā abhiññāya Sambodhāya Nibbānāya saṁvattanti.</span></p><p class="p3"><span class="t1">when developed and made much of they lead to deep knowledge, Complete Awakening, and Nibbāna. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katame satta?</span></p><p class="p3"><span class="t1">What are the seven? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Satisambojjhaṅgo kho Moggallāna mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is mindfulness, Moggallāna, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[1]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t26">Dhammavicayasambojjhaṅgo kho Moggallāna mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is investigation of (the nature of) things, Moggallāna, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[2]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vīriyasambojjhaṅgo kho Moggallāna mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is energy, Moggallāna, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p>', '-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 64, '<p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[3]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pītisambojjhaṅgo kho Moggallāna mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is rapture, Moggallāna, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[4]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Passaddhisambojjhaṅgo kho Moggallāna mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is calm, Moggallāna, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[5]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Samādhisambojjhaṅgo kho Moggallāna mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is concentration, Moggallāna, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[6]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Upekkhāsambojjhaṅgo kho Moggallāna mayā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is equanimity, Moggallāna, has been fully explained by me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[7]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ime kho Moggallāna satta Bojjhaṅgā mayā sammad-akkhātā,</span></p><p class="p3"><span class="t1">These are the seven factors of Awakening, Moggallāna, which have been fully explained by me, </span></p><p class="p3"><span class="t25"> </span></p><p class="p3"><span class="t28">bhāvitā bahulīkatā abhiññāya Sambodhāya Nibbānāya saṁvattantī” ti.</span></p><p class="p3"><span class="t1">when developed and made much of they lead to deep knowledge, Complete Awakening, and Nibbāna.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t25">“Taggha Bhagava Bojjhaṅgā!</span></p><p class="p3"><span class="t1">“Surely, Fortunate One, (these are) the factors of Awakening! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t25">Taggha Sugata Bojjhaṅgā!” ti.</span></p>', '-64-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Eleven ', 'title', 65);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 65, '<p class="p3"><span class="t1">Surely, Happy One, (these are) the factors of Awakening!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t25">Idam-avoca Bhagavā,</span></p><p class="p3"><span class="t1">The Fortunate One said this, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t25">attamano āyasmā Mahāmoggallāno Bhagavato bhāsitaṁ abhinandi,</span></p><p class="p3"><span class="t1">and venerable Mahā Moggallāna was uplifted and greatly rejoiced in what was said by the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t25">vuṭṭhahi cāyasmā Mahāmoggallāno tamhā ābādhā,</span></p><p class="p3"><span class="t1">and venerable Mahā Moggallāna recovered from that affliction, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t25">tathā pahīno cāyasmato Mahāmoggallānassa so ābādho ahosī ti.</span></p><p class="p3"><span class="t1">and by that venerable Mahā Moggallāna’s affliction was brought to an end. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Evening Paritta Chants, Day Eleven </h3>', '-65-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Tatiyagilānasuttaṁ ', 'title', 66);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 66, '<p class="p3"><span class="t9"> </span></p><h4>Tatiyagilānasuttaṁ </h4><p class="p3"><span class="t10">The Third Discourse about being Sick</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Ekaṁ samayaṁ Bhagavā Rājagahe viharati</span></p><p class="p3"><span class="t1">At one time the Fortunate One was dwelling near Rājagaha </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Veḷuvane Kalandakanivāpe.</span></p><p class="p3"><span class="t1">at the Squirrels’ Feeding Place in Bamboo Wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t26">Tena kho pana samayena Bhagavā ābādhiko hoti dukkhito bāḷhagilāno.</span></p><p class="p3"><span class="t1">Then at that time the Fortunate One was afflicted, suffering, and very sick. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t23">Atha kho āyasmā Mahācundo </span><span class="t5">yena Bhagavā tenupasaṅkami,</span></p><p class="p3"><span class="t1">Then venerable Mahā Cunda approached the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upasaṅkamitvā Bhagavantaṁ abhivādetvā, ekam-antaṁ nisīdi.</span></p><p class="p3"><span class="t1">and after approaching and worshipping the Fortunate One, he sat down on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ekam-antaṁ nissinaṁ kho āyasmantaṁ Mahācundaṁ Bhagavā etad-avoca:</span></p><p class="p3"><span class="t1">While he was sitting on one side the Fortunate One said this to venerable Mahā Cunda: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Paṭibhantu taṁ Cunda Bojjhaṅgā” ti.</span></p><p class="p3"><span class="t1">“May the factors of Awakening occur to you, Cunda.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sattime bhante Bojjhaṅgā Bhagavatā sammad-akkhātā,</span></p><p class="p3"><span class="t1">“There are these seven factors of Awakening, reverend Sir, that have been fully explained by the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvitā bahulīkatā abhiññāya Sambodhāya Nibbānāya saṁvattanti.</span></p><p class="p3"><span class="t1">when developed and made much of they lead to deep knowledge, Complete Awakening, and Nibbāna. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Katame satta?</span></p><p class="p3"><span class="t1">What are the seven? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Satisambojjhaṅgo kho bhante Bhagavatā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is mindfulness, reverend Sir, has been fully explained by the Fortunate One, </span></p>', '-66-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 67, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[1]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t26">Dhammavicayasambojjhaṅgo kho bhante Bhagavatā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is investigation of (the nature of) things, reverend Sir, has been fully explained by the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[2]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vīriyasambojjhaṅgo kho bhante Bhagavatā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is energy, reverend Sir, has been fully explained by the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[3]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pītisambojjhaṅgo kho bhante Bhagavatā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is rapture, reverend Sir, has been fully explained by the Fortunate One, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[4]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Passaddhisambojjhaṅgo kho bhante Bhagavatā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is calm, reverend Sir, has been fully explained by the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[5]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Samādhisambojjhaṅgo kho bhante Bhagavatā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is concentration, reverend Sir, has been fully explained by the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[6]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Upekkhāsambojjhaṅgo kho bhante Bhagavatā sammad-akkhāto,</span></p><p class="p3"><span class="t1">The factor of Complete Awakening that is equanimity, reverend Sir, has been fully explained by the Fortunate One, </span></p>', '-67-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 68, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvito bahulīkato abhiññāya Sambodhāya Nibbānāya saṁvattati. </span></p><p class="p3"><span class="t17">when developed and made much of it leads to deep knowledge, Complete Awakening, and Nibbāna. </span><span class="t17">[7]</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ime kho bhante satta Bojjhaṅgā Bhagavatā sammad-akkhātā,</span></p><p class="p3"><span class="t1">These are the seven factors of Awakening, reverend Sir, which have been fully explained by the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t26">bhāvitā bahulīkatā abhiññāya Sambodhāya Nibbānāya saṁvattantī” ti.</span></p><p class="p3"><span class="t1">when developed and made much of they lead to deep knowledge, Complete Awakening, and Nibbāna.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Taggha Cunda Bojjhaṅgā!</span></p><p class="p3"><span class="t1">“Surely, Cunda, (these are) the factors of Awakening! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Taggha Cunda Bojjhaṅgā!” ti.</span></p><p class="p3"><span class="t1">Surely, Cunda, (these are) the factors of Awakening!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-avoca āyasmā Mahācundo,</span></p><p class="p3"><span class="t1">Venerable Mahā Cunda said this, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">samanuñño Satthā ahosi,</span></p><p class="p3"><span class="t1">and the Teacher was in agreement, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vuṭṭhahi ca Bhagavā tamhā ābādhā,</span></p><p class="p3"><span class="t1">and the Fortunate One recovered from that affliction, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tathā pahīno ca Bhagavato so ābādho ahosī ti.</span></p><p class="p3"><span class="t1">and by that the Fortunate One’s affliction was brought to an end. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p>', '-68-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Twelve ', 'title', 69);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Bojjhaṅgasuttaṁ ', 'title', 69);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 69, '<p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Evening Paritta Chants, Day Twelve </h3><p class="p3"><span class="t9"> </span></p><h4>Bojjhaṅgasuttaṁ </h4><p class="p3"><span class="t10">The Discourse about the Factors of Awakening</span><span class="t10">  (</span><span class="t29">from</span><span class="t10"> the Eleven Great Paritta Discourses)</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t18">Introductory Verses</span></p><p class="p3"><span class="t18"> </span></p><p class="p3"><span class="t5">Saṁsāre saṁsarantānaṁ sabbadukkhavināsane</span></p><p class="p3"><span class="t1">While rolling on through the rounds of birth and death all suffering is destroyed </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sattadhamme ca Bojjhaṅge Mārasenapamaddane.</span></p><p class="p3"><span class="t1">(by) the seven Factors of Awakening, which crush Māra’s army. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bujjhitvā ye cime sattā tibhavā muttakuttamā,</span></p><p class="p3"><span class="t1">Having Awakened to these seven they are supremely released from the three worlds, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ajātim-ajarābyādhim amataṁ nibbhayaṁ gatā.</span></p><p class="p3"><span class="t1">and have gone to (that state where there is) no birth, no ageing, no sickness, no death and which is free from fear. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evam-ādiguṇūpetaṁ anekaguṇasaṅgahaṁ</span></p><p class="p3"><span class="t1">Being endowed with this quality and a collection of countless other qualities </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Osadhañ-ca imaṁ mantaṁ: Bojjhaṅgañ-ca bhaṇāmahe.</span></p>', '-69-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 70, '<p class="p3"><span class="t1">it is a medicinal mantra: we will recite those factors of Awakening. </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t18">The Safeguard</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bojjhaṅgo sati-saṅkhāto dhammānaṁ vicayo tathā,</span></p><p class="p3"><span class="t1">The Factors of Awakening are agreed upon as mindfulness, and then investigation of the (nature of) things, </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Vīriyaṁ pīti-passaddhi Bojjhaṅgā ca tathā-pare,</span></p><p class="p3"><span class="t1">And after that the Factors of Awakening of energy, joyful-interest and calm, </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Samādhupekkha-bojjhaṅgā: satte te Sabba-dassinā</span></p><p class="p3"><span class="t1">(Then) the Factors of Awakening of concentration and equanimity: these seven </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Muninā sammad-akkhātā, bhāvitā bahulī-katā</span></p><p class="p3"><span class="t1">were well-proclaimed by the All-Seeing Sage, when developed and practiced </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Saṁvattanti abhiññāya Nibbānāya ca Bodhiyā:</span></p><p class="p3"><span class="t1">They lead to deep knowledge, Emancipation and Awakening: </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Etena sacca-vajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Ekasmiṁ samaye Nātho Moggallānañ-ca Kassapaṁ</span></p><p class="p3"><span class="t1">At one time The Lord, having seen that Moggallāna and Kassapa </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Gilāne dukkhite disvā, Bojjhaṅge satta desayi,</span></p><p class="p3"><span class="t1">Were sick and suffering, taught these seven Factors of Awakening, </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Te ca taṁ abhinanditvā rogā mucciṁsu taṅkhane:</span></p><p class="p3"><span class="t1">And having rejoiced in them, they were freed from illness in an instant: </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Etena sacca-vajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Ekadā Dhamma-rājā pi gelaññenābhipīḷito,</span></p><p class="p3"><span class="t1">One day the Dhamma-King was oppressed by sickness, </span></p>', '-70-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 71, '<p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Cundattherena taṁ yeva bhaṇāpetvāna sādaraṁ,</span></p><p class="p3"><span class="t1">And after the Elder Cunda had recited them with respect, </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Sammoditvāna ābādhā tamhā vuṭṭhāsi ṭhānaso:</span></p><p class="p3"><span class="t1">Having rejoiced he rose from that affliction on the spot: </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Etena sacca-vajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Pahinā te ca ābādhā tiṇṇannam-pi Mahesinaṁ</span></p><p class="p3"><span class="t1">These afflictions were abandoned by these three Great Seers, </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Magga-hatā-kilesā va pattānupatti-dhammataṁ:</span></p><p class="p3"><span class="t1">Just as the defilements are destroyed by the path, attained in accordance with nature: </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Etena sacca-vajjena</span><span class="t5"> </span><span class="t5">sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t12"> </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p>', '-71-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Evening Paritta Chants, Day Thirteen ', 'title', 72);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_chanting_for_meditators', 'Pubbaṇhasuttaṁ ', 'title', 72);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 72, '<p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t1"> </span></p><h3>Evening Paritta Chants, Day Thirteen </h3><p class="p3"><span class="t9"> </span></p><h4>Pubbaṇhasuttaṁ </h4><p class="p3"><span class="t10">The Discourse about the Forenoon</span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Yan-dunnimittaṁ avamaṅgalañ-ca, yo cāmanāpo sakuṇassa saddo,</span></p><p class="p3"><span class="t1">Whatever bad sign, inauspicious event, or whatever ominous bird shriek there is, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pāpaggaho dussupinaṁ akantaṁ Buddhānubhāvena vināsamentu!</span></p><p class="p3"><span class="t1">also evil planets, and unpleasant dreams may they perish through the power of the Buddha! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yan-dunnimittaṁ avamaṅgalañ-ca, yo cāmanāpo sakuṇassa saddo,</span></p><p class="p3"><span class="t1">Whatever bad sign, inauspicious event, or whatever ominous bird shriek there is, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pāpaggaho dussupinaṁ akantaṁ Dhammānubhāvena vināsamentu!</span></p><p class="p3"><span class="t1">also evil planets, and unpleasant dreams may they perish through the power of the Dhamma! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yan-dunnimittaṁ avamaṅgalañ-ca, yo cāmanāpo sakuṇassa saddo,</span></p><p class="p3"><span class="t1">whatever bad sign, inauspicious event, or whatever ominous bird shriek there is, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pāpaggaho dussupinaṁ akantaṁ Saṅghānubhāvena vināsamentu!</span></p><p class="p3"><span class="t1">also evil planets, and unpleasant dreams may they perish through the power of the Saṅgha! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dukkhappattā ca niddukkhā, bhayappattā ca nibbhayā,</span></p><p class="p3"><span class="t1">May those who suffer be without suffering, may those who fear be without fear, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sokappattā ca nissokā - hontu sabbe pi pāṇino!</span></p><p class="p3"><span class="t1">may those who grieve be without grief - may all living creatures be so! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ettāvatā ca amhehi sambhataṁ puñña-sampadaṁ</span></p><p class="p3"><span class="t1">For as far as success in merit has been achieved by us </span></p><p class="p3"><span class="t1">  </span></p>', '-72-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 73, '<p class="p3"><span class="t5">sabbe devānumodantu sabbasampattisiddhiyā!</span></p><p class="p3"><span class="t1">may all gods rejoice in the accomplishment of all attainments! </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Dānaṁ dadantu saddhāya, sīlaṁ rakkhantu sabbadā,</span></p><p class="p3"><span class="t1">You should give gifts with confidence, protect your virtue at all times, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāvanābhiratā hontu, gacchantu devatāgatā.</span></p><p class="p3"><span class="t1">find delight in meditation, and (after death) go to the gods. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbe Buddhā balappattā, Paccekānañ-ca yaṁ balaṁ</span></p><p class="p3"><span class="t1">All the Buddhas’ strength, and whatever strength the Independent Buddhas </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Arahantānañ-ca tejena rakkhaṁ bandhāmi sabbaso!</span></p><p class="p3"><span class="t1">and Worthy Ones have, by that power I bind this protection in every way! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Yaṁ kiñci vittaṁ - idha vā huraṁ vā saggesu vā - yaṁ ratanaṁ paṇītaṁ</span></p><p class="p3"><span class="t1">Whatever riches there are - here or elsewhere or in the heavens - that excellent treasure </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na no samaṁ atthi Tathāgatena idam-pi Buddhe ratanaṁ paṇītaṁ:</span></p><p class="p3"><span class="t1">is not equal unto the Realised One this excellent treasure is in the Buddha: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">etena saccena suvatthi hotu!</span></p><p class="p3"><span class="t1">by virtue of this truth may there be safety! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ kiñci vittaṁ - idha vā huraṁ vā saggesu vā - yaṁ ratanaṁ paṇītaṁ</span></p><p class="p3"><span class="t1">Whatever riches there are - here or elsewhere or in the heavens - that excellent treasure </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na no samaṁ atthi Tathāgatena idam-pi Dhamme ratanaṁ paṇītaṁ:</span></p><p class="p3"><span class="t1">is not equal unto the Realised One this excellent treasure is in the Dhamma: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">etena saccena suvatthi hotu!</span></p><p class="p3"><span class="t1">by virtue of this truth may there be safety! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ kiñci vittaṁ - idha vā huraṁ vā saggesu vā - yaṁ ratanaṁ paṇītaṁ</span></p><p class="p3"><span class="t1">Whatever riches there are - here or elsewhere or in the heavens - that excellent treasure </span></p><p class="p3"><span class="t1">  </span></p>', '-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 74, '<p class="p3"><span class="t5">na no samaṁ atthi Tathāgatena idam-pi Saṅghe ratanaṁ paṇītaṁ:</span></p><p class="p3"><span class="t1">is not equal unto the Realised One this excellent treasure is in the Saṅgha: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">etena saccena suvatthi hotu!</span></p><p class="p3"><span class="t1">by virtue of this truth may there be safety! </span></p><p class="p3"><span class="t9"> </span></p><p class="p3"><span class="t5">Mahākāruṇiko nātho, hitāya sabbapāṇinaṁ,</span></p><p class="p3"><span class="t1">The lord of great compassion, for the benefit of all living creatures, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pūretvā pāramī sabbā patto Sambodhim-uttamaṁ.</span></p><p class="p3"><span class="t1">having fulfilled all the perfections has attained supreme and Complete Awakening. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe everyday! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jayanto Bodhiyā mūle Sakyānaṁ nandivaḍḍhano -</span></p><p class="p3"><span class="t1">Succeeding at the root of the Bodhi tree he furthered the Sakyans’ joy - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evam-eva jayo hotu, jayassu jayamaṅgale!</span></p><p class="p3"><span class="t1">so may you be successful, may you succeed with the blessing of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Aparājitapallaṅke sīse puthuvipukkhale,</span></p><p class="p3"><span class="t1">Seated cross-legged and undefeated on the top of the world </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abhiseke sabba-Buddhānaṁ, aggappatto pamodati.</span></p><p class="p3"><span class="t1">which was consecrated by all Buddhas, he rejoices in the highest attainment. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sunakkhattaṁ sumaṅgalaṁ suppabhātaṁ suhuṭṭhitaṁ,</span></p><p class="p3"><span class="t1">(May there be) good planets, good blessings, good daybreaks, good gifts, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sukhaṇo sumuhutto ca suyiṭṭhaṁ brahmacārisu,</span></p><p class="p3"><span class="t1">good moments, good instants and good sacrifices in the spiritual lives, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Padakkhiṇaṁ kāyakammaṁ, vācākammaṁ padakkhiṇe,</span></p><p class="p3"><span class="t1">(May there be) favourable body-actions, favourable speech-actions </span></p><p class="p3"><span class="t1">  </span></p>', '-74-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_chanting_for_meditators', 75, '<p class="p3"><span class="t5">padakkhiṇaṁ manokammaṁ paṇidhi te padakkhiṇe.</span></p><p class="p3"><span class="t1">favourable mind-actions, and favourable aspiration for you. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Padakkhiṇāni katvāna, labhantatthe padakkhiṇe,</span></p><p class="p3"><span class="t1">And after making these favourable things, (may there be) favourable gains, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te atthaladdhā sukhitā viruḷhā Buddhasāsane,</span></p><p class="p3"><span class="t1">and (with these) favourable gains (may there be) for you happy growth in the Buddha’s Dispensation, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">arogā sukhitā hotha, saha sabbehi ñātibhi!</span></p><p class="p3"><span class="t1">may you be well and happy, together with all your relatives! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabba-Buddhānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be well forever! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabba-Dhammānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be well forever! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabba-Saṅghānubhāvena sadā sukhī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be well forever! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sādhu! Sādhu! Sādhu!</span></p><p class="p3"><span class="t1">Well said! Well said! Well said! </span></p>', '-75-');
