INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Worshipping the Virtues of the Buddha ', 'title', 1);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Worshipping the Virtues of the Dhamma ', 'title', 1);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 1, '<h2>Worship </h2><p class="p3"><span class="t3">Namakkāraṁ</span></p><h3>Reverence </h3><p class="p3"><span class="t5">Namo tassa Bhagavato Arahato Sammāsambuddhassa</span></p><p class="p3"><span class="t1">Reverence to him, the Fortunate One, the Worthy One, the Perfect Sambuddha </span></p><p class="p3"><span class="t5">Namo tassa Bhagavato Arahato Sammāsambuddhassa</span></p><p class="p3"><span class="t1">Reverence to him, the Fortunate One, the Worthy One, the Perfect Sambuddha </span></p><p class="p3"><span class="t5">Namo tassa Bhagavato Arahato Sammāsambuddhassa</span></p><p class="p3"><span class="t1">Reverence to him, the Fortunate One, the Worthy One, the Perfect Sambuddha </span></p><p class="p3"><span class="t3">Buddhaguṇavandanā</span></p><h3>Worshipping the Virtues of the Buddha </h3><p class="p3"><span class="t5">Iti pi so Bhagavā Arahaṁ Sammāsambuddho,</span></p><p class="p3"><span class="t1">Such is he, the Fortunate One, the Worthy One, the Perfect Sambuddha, </span></p><p class="p3"><span class="t5">vijjācaraṇasampanno Sugato lokavidū,</span></p><p class="p3"><span class="t1">the one endowed with understanding and good conduct, the Happy One, the one who understands the worlds, </span></p><p class="p3"><span class="t5">anuttaro purisadammasārathī, </span></p><p class="p3"><span class="t1">the unsurpassed guide for those people who need taming, </span></p><p class="p3"><span class="t5">Satthā devamanussānaṁ Buddho Bhagavā ti.</span></p><p class="p3"><span class="t1">the Teacher of gods and men, the Buddha, the Fortunate One. </span></p><p class="p3"><span class="t3">Buddhaṁ jīvitaṁ yāva Nibbānaṁ saraṇaṁ gacchāmi.</span></p><p class="p3"><span class="t1">I go for life-long refuge to the Buddha right up until Nibbāna. </span></p><p class="p3"><span class="t3">Ye ca Buddhā atītā ca, ye ca Buddhā anāgatā,</span></p><p class="p3"><span class="t1">Those who were Buddhas in the past, those who will be Buddhas in the future, </span></p><p class="p3"><span class="t3">paccuppannā ca ye Buddhā, ahaṁ vandāmi sabbadā!</span></p><p class="p3"><span class="t1">and those who are Buddhas in the present, I worship them every day! </span></p><p class="p3"><span class="t3">Natthi me saraṇaṁ aññaṁ, Buddho me saraṇaṁ varaṁ!</span></p><p class="p3"><span class="t1">For me there is no other refuge, for me the Buddha is the best refuge! </span></p><p class="p3"><span class="t3">Etena saccavajjena hotu te jayamaṅgalaṁ!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t3">Uttamaṅgena vandehaṁ pādapaṁsu varuttamaṁ,</span></p><p class="p3"><span class="t1">With my head I worship the most excellent dust of his feet, </span></p><p class="p3"><span class="t3">Buddhe yo khalito doso Buddho khamatu taṁ mamaṁ!</span></p><p class="p3"><span class="t1">for any fault or wrong against the Buddha may the Buddha forgive me for that! </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Dhammaguṇavandanā</span></p><h3>Worshipping the Virtues of the Dhamma </h3>', '-1-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Worshipping the Virtues of the Saṅgha ', 'title', 2);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 2, '<p class="p3"><span class="t5">Svākkhāto Bhagavatā Dhammo, sandiṭṭhiko, akāliko,</span></p><p class="p3"><span class="t1">The Dhamma has been well-proclaimed by the Fortunate One, it is visible, not subject to time, </span></p><p class="p3"><span class="t5">ehipassiko, opanayiko, paccattaṁ veditabbo viññūhī ti.</span></p><p class="p3"><span class="t1">inviting inspection, onward leading, and can be understood by the wise for themselves. </span></p><p class="p3"><span class="t3">Dhammaṁ jīvitaṁ yāva Nibbānaṁ saraṇaṁ gacchāmi.</span></p><p class="p3"><span class="t1">I go for life-long refuge to the Dhamma right up until Nibbāna. </span></p><p class="p3"><span class="t3">Ye ca Dhammā atītā ca, ye ca Dhammā anāgatā,</span></p><p class="p3"><span class="t1">That which was Dhamma in the past, that which will be Dhamma in the future, </span></p><p class="p3"><span class="t3">paccuppannā ca ye Dhammā, ahaṁ vandāmi sabbadā!</span></p><p class="p3"><span class="t1">and that which is Dhamma in the present, I worship it every day! </span></p><p class="p3"><span class="t3">Natthi me saraṇaṁ aññaṁ, Dhammo me saraṇaṁ varaṁ!</span></p><p class="p3"><span class="t1">For me there is no other refuge, for me the Dhamma is the best refuge! </span></p><p class="p3"><span class="t3">Etena saccavajjena hotu te jayamaṅgalaṁ!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t3">Uttamaṅgena vandehaṁ Dhammaṁ ca tividhaṁ varaṁ,</span></p><p class="p3"><span class="t1">With my head I worship the excellent threefold Dhamma, </span></p><p class="p3"><span class="t3">Dhamme yo khalito doso Dhammo khamatu taṁ mamaṁ!</span></p><p class="p3"><span class="t1">for any fault or wrong against the Dhamma may the Dhamma forgive me for that! </span></p><p class="p3"><span class="t3">Saṅghaguṇavandanā</span></p><h3>Worshipping the Virtues of the Saṅgha </h3><p class="p3"><span class="t5">Supaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">The Fortunate One’s Saṅgha of disciples are good in their practice, </span></p><p class="p3"><span class="t5">ujupaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">the Fortunate One’s Saṅgha of disciples are upright in their practice, </span></p><p class="p3"><span class="t5">ñāyapaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">the Fortunate One’s Saṅgha of disciples are systematic in their practice, </span></p><p class="p3"><span class="t5">sāmīcipaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">the Fortunate One’s Saṅgha of disciples are correct in their practice, </span></p><p class="p3"><span class="t5">yad-idaṁ cattāri purisayugāni aṭṭha purisapuggalā,</span></p><p class="p3"><span class="t1">that is to say, the four pairs of persons, the eight individual persons, </span></p><p class="p3"><span class="t5">esa Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">this is the Fortunate One’s Saṅgha of disciples, </span></p><p class="p3"><span class="t5">āhuneyyo, pāhuneyyo, dakkhiṇeyyo, añjalikaranīyo,</span></p><p class="p3"><span class="t1">they are worthy of offerings, of hospitality, of gifts, and of reverential salutation, </span></p><p class="p3"><span class="t5">anuttaraṁ puññakkhettaṁ lokassā ti.</span></p><p class="p3"><span class="t1">they are an unsurpassed field of merit for the world. </span></p>', '-2-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Verses on Obeisance ', 'title', 3);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 3, '<p class="p3"><span class="t3">Saṅghaṁ jīvitaṁ yāva Nibbānaṁ saraṇaṁ gacchāmi.</span></p><p class="p3"><span class="t1">I go for life-long refuge to the Sangha right up until Nibbāna. </span></p><p class="p3"><span class="t3">Ye ca Saṅghā atītā ca, ye ca Saṅghā anāgatā,</span></p><p class="p3"><span class="t1">Those who were the Sangha in the past, those who will be the Sangha in the future, </span></p><p class="p3"><span class="t3">paccuppannā ca ye Saṅghā, ahaṁ vandāmi sabbadā!</span></p><p class="p3"><span class="t1">and those who are the Sangha in the present, I worship them every day! </span></p><p class="p3"><span class="t3">Natthi me saraṇaṁ aññaṁ, Saṅgho me saraṇaṁ varaṁ!</span></p><p class="p3"><span class="t1">For me there is no other refuge, for me the Sangha is the best refuge! </span></p><p class="p3"><span class="t3">Etena saccavajjena hotu te jayamaṅgalaṁ!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t3">Uttamaṅgena vandehaṁ Saṅghaṁ ca tividhottamaṁ,</span></p><p class="p3"><span class="t1">With my head I worship the Sangha who are supreme in three ways, </span></p><p class="p3"><span class="t3">Saṅghe yo khalito doso Saṅgho khamatu taṁ mamaṁ!</span></p><p class="p3"><span class="t1">for any fault or wrong against the Sangha may the Sangha forgive me for that! </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Paṇāmagāthā</span></p><h3>Verses on Obeisance </h3><p class="p3"><span class="t3">Buddhadhammā ca Paccekabuddhā Saṅghā ca sāmikā,</span></p><p class="p3"><span class="t1">The Buddhas, Dhamma, Independent Buddhas, and the revered Sangha, </span></p><p class="p3"><span class="t3">dāsoham-asmi me tesaṁ, guṇaṁ ṭhātu sire sadā!</span></p><p class="p3"><span class="t1">I am their servant, may that good quality always be on my head! </span></p><p class="p3"><span class="t3">Tisaraṇaṁ tilakkhaṇūpekkhaṁ Nibbānam-antimaṁ,</span></p><p class="p3"><span class="t1">The three refuges, equanimity about the three signs, and final Nibbāna, </span></p><p class="p3"><span class="t3">suvande sirasā niccaṁ labhāmi tividhā-m-ahaṁ.</span></p><p class="p3"><span class="t1">I always worship these with my head and I receive threefold (return). </span></p><p class="p3"><span class="t3">Tisaraṇaṁ ca sire ṭhātu, sire ṭhātu tilakkhaṇaṁ,</span></p><p class="p3"><span class="t1">May the three refuges be placed on my head, may the three signs be placed on my head, </span></p><p class="p3"><span class="t3">upekkhā ca sire ṭhātu, Nibbānaṁ ṭhātu me sire!</span></p><p class="p3"><span class="t1">may equanimity be placed on my head, and may Nibbāna be placed on my head! </span></p><p class="p3"><span class="t3">Buddhe sakaruṇe vande, Dhamme Paccekasambuddhe,</span></p><p class="p3"><span class="t1">I worship the compassionate Buddhas, the Dhamma, the Independent Sambuddhas, </span></p><p class="p3"><span class="t3">Saṅghe ca sirisā yeva, tidhā niccaṁ namāmyahaṁ.</span></p><p class="p3"><span class="t1">and the Sangha with my head, I constantly bow down three times. </span></p><p class="p3"><span class="t3">Namāmi Satthuno vādā appamādavacantimaṁ,</span></p><p class="p3"><span class="t1">I bow down to the words of the Teacher, and the last words on heedfulness, </span></p><p class="p3"><span class="t3">sabbe pi cetiye vande, upajjhāyācariye mamaṁ,</span></p>', '-3-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Material Offerings ', 'title', 4);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 4, '<p class="p3"><span class="t1">and also to all the shrines, to my preceptor and teacher, </span></p><p class="p3"><span class="t3">mayhaṁ paṇāmatejena cittaṁ pāpehi muñcatan-ti!</span></p><p class="p3"><span class="t1">by the power of this obeisance may my mind be free from evil! </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Āmisapūjā</span></p><h3>Material Offerings </h3><p class="p3"><span class="t11">(first worshipping the main objects of veneration)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t3">Vandāmi cetiyaṁ sabbaṁ sabbaṭṭhānesu patiṭṭhitaṁ,</span></p><p class="p3"><span class="t1">I worship all the shrines in all of the places that they stand, </span></p><p class="p3"><span class="t3">sārīrikadhātu Mahā Bodhiṁ, Buddharūpaṁ sakalaṁ sadā!</span></p><p class="p3"><span class="t1">the bodily relics, the Great Bodhi Tree, and all the Buddha images forever! </span></p><p class="p3"><span class="t3">Iccevam-accantanamassaneyyaṁ namassamāno Ratanattayaṁ yaṁ,</span></p><p class="p3"><span class="t1">In this way I can revere the Three Treasures without end, and while revering them, </span></p><p class="p3"><span class="t3">puññābhisandhaṁ vipulaṁ alatthaṁ, tassānubhāvena hatantarāyo!</span></p><p class="p3"><span class="t1">I have received an abundant overflow of merit, by that power may (any) obstacle be destroyed! </span></p><p class="p3"><span class="t11">(only chant verses for those things you are offering)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t11">(lights)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t3">Ghanasārappadittena dīpena tamadhaṁsinā,</span></p><p class="p3"><span class="t1">With a lamp that burns intensely, destroying the darkness, </span></p><p class="p3"><span class="t3">tilokadīpaṁ Sambuddhaṁ pūjayāmi tamonudaṁ.</span></p><p class="p3"><span class="t1">I worship the Sambuddha, the light of the three worlds, the darkness-dispeller. </span></p><p class="p3"><span class="t11">(incense)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t3">Sugandhikāyavadanaṁ, anantaguṇagandhinaṁ,</span></p><p class="p3"><span class="t1">° With this fragrance and perfume I worship the Realised One, </span></p><p class="p3"><span class="t3">Sugandhināhaṁ gandhena pūjayāmi Tathāgataṁ.</span></p><p class="p3"><span class="t1">who is fragrant in body and speech, and has fragrant endless virtues. </span></p><p class="p3"><span class="t11">(water)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t3">Sugandhaṁ sītalaṁ kappaṁ, pasannamadhuraṁ subhaṁ,</span></p><p class="p3"><span class="t1">° Please accept this fragrant, cool, clear, sweet, and attractive drink </span></p><p class="p3"><span class="t3">pānīyam-etaṁ Bhagavā, paṭiggaṇhātu-m-uttama!</span></p><p class="p3"><span class="t1">that has been prepared, O Fortunate One supreme! </span></p>', '-4-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Safeguard Recitals ', 'title', 5);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Invitation to the Gods ', 'title', 5);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 5, '<p class="p3"><span class="t11">(medicinal drink)</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t3">Bhesajjehi samāyuttaṁ gilānapaccayaṁ imaṁ,</span></p><p class="p3"><span class="t1">° Please accept this medicine together with this herbal drink, </span></p><p class="p3"><span class="t3">anukampaṁ upādāya, paṭiggaṇhātu-m-uttama!</span></p><p class="p3"><span class="t1">having compassion on us, O Fortunate One supreme! </span></p><p class="p3"><span class="t11">(flowers)</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t3">Vaṇṇagandhaguṇopetaṁ, etaṁ kusumasantatiṁ,</span></p><p class="p3"><span class="t1">° With these long lasting flowers, endowed with the qualities of beauty </span></p><p class="p3"><span class="t3">pūjayāmi Munindassa siripādasaroruhe.</span></p><p class="p3"><span class="t1">and fragrance, I worship the glorious lotus feet of the lord of Sages. </span></p><p class="p3"><span class="t3">Pūjemi Buddhaṁ kusamenanena, puññena-m-etena labhāmi mokkhaṁ.</span></p><p class="p3"><span class="t1">I worship the Awakened One with these flowers, may I gain release with (the help of) this merit. </span></p><p class="p3"><span class="t3">Pupphaṁ milāyāti yathā idaṁ me, kāyo tathā yāti vināsabhāvaṁ.</span></p><p class="p3"><span class="t1">Just as a flower withers and fades away, so too this my body will go to destruction. </span></p><p class="p3"><span class="t11">(aspiration)</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t3">Imāya Buddhapūjāya katāya suddhacetasā,</span></p><p class="p3"><span class="t1">By this worship of the Buddha, performed with a pure mind, </span></p><p class="p3"><span class="t3">ciraṁ tiṭṭhatu Saddhammo, loko hotu sukhī sadā!</span></p><p class="p3"><span class="t1">may the True Dhamma last a long time, and may the world be always happy! </span></p><p class="p3"><span class="t1"> </span></p><h2>Safeguard Recitals </h2><p class="p3"><span class="t3">Devārādhanā</span></p><h3>The Invitation to the Gods </h3><p class="p3"><span class="t11">(Chanted by One Person)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t5">Samantā cakkavāḷesu atrāgacchantu devatā</span></p><p class="p3"><span class="t1">May the gods from all over the universe assemble here </span></p><p class="p3"><span class="t5">saddhammaṁ Munirājassa suṇantu saggamokkhadaṁ:</span></p><p class="p3"><span class="t1">and listen to the King of the Sage’s true Dhamma about heaven and release: </span></p><p class="p3"><span class="t5">Parittassavaṇakālo ayaṁ bhadantā!</span></p><p class="p3"><span class="t1">Reverend Sirs, this is the time for hearing the safeguard! </span></p><p class="p3"><span class="t5">Parittassavaṇakālo ayaṁ bhadantā!</span></p><p class="p3"><span class="t1">Reverend Sirs, this is the time for hearing the safeguard! </span></p>', '-5-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Reverence ', 'title', 6);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Monday ', 'title', 6);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Safeguard in this Order’s Domain ', 'title', 6);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 6, '<p class="p3"><span class="t5">Dhammaparittassavaṇakālo ayaṁ bhadantā!</span></p><p class="p3"><span class="t1">Reverend Sirs, this is the time for hearing the Dhamma safeguard! </span></p><p class="p3"><span class="t3">Namakkāraṁ</span></p><h3>Reverence </h3><p class="p3"><span class="t11">(Chanted by All Present)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t5">Namo tassa Bhagavato Arahato Sammāsambuddhassa</span></p><p class="p3"><span class="t1">Reverence to him, the Fortunate One, the Worthy One, the Perfect Sambuddha </span></p><p class="p3"><span class="t5">Namo tassa Bhagavato Arahato Sammāsambuddhassa</span></p><p class="p3"><span class="t1">Reverence to him, the Fortunate One, the Worthy One, the Perfect Sambuddha </span></p><p class="p3"><span class="t5">Namo tassa Bhagavato Arahato Sammāsambuddhassa</span></p><p class="p3"><span class="t1">Reverence to him, the Fortunate One, the Worthy One, the Perfect Sambuddha </span></p><p class="p3"><span class="t11">(Now follow the Schedule according to the Day)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t11">(monastics can chant the Reflections found in the appendix here)</span></p><p class="p3"><span class="t1"> </span></p><h2>Monday </h2><p class="p3"><span class="t3">Āṇakkhettaparittaṁ, 1</span></p><h3>Safeguard in this Order’s Domain </h3><p class="p3"><span class="t3">Ye santā santacittā, tisaraṇasaraṇā, ettha lokantare vā,</span></p><p class="p3"><span class="t1">Those (gods) who are peaceful, with peaceful minds, who have taken refuge in the triple refuge, whether here, or above the worlds, </span></p><p class="p3"><span class="t3">bhummā bhummā ca devā, guṇagaṇagahaṇā, byāvaṭā sabbakālaṁ,</span></p><p class="p3"><span class="t1">the various earth gods, that group who have taken up, and are engaged in, virtuous deeds all of the time, </span></p><p class="p3"><span class="t3">ete āyantu devā, varakanakamaye, Merurāje vasanto,</span></p><p class="p3"><span class="t1">may these gods come, those who dwell on the majestic Mt. Meru, that excellent golden mountain, </span></p><p class="p3"><span class="t3">santo santo sahetuṁ Munivaravacanaṁ sotumaggaṁ samaggaṁ.</span></p><p class="p3"><span class="t1">peacefully, and with good reason, ( to hear) the Sage’s excellent word about entering the stream, and harmony. </span></p><p class="p3"><span class="t3">Sabbesu cakkavāḷesu yakkhā devā ca brahmuno,</span></p><p class="p3"><span class="t1">May all yakkhas, gods, and deities, from the whole universe, </span></p><p class="p3"><span class="t3">Yaṁ amhehi kataṁ puññaṁ sabbasampattisādhukaṁ</span></p><p class="p3"><span class="t1">° After partaking of the merits, and of all the thoroughly good fortune </span></p><p class="p3"><span class="t3">Sabbe taṁ anumoditvā samaggā sāsane ratā,</span></p><p class="p3"><span class="t1">We have acquired, being in harmony, and delighting in the teaching, </span></p><p class="p3"><span class="t3">Pamādarahitā hontu ārakkhāsu visesato.</span></p><p class="p3"><span class="t1">Be not heedless and grant us complete protection. </span></p><p class="p3"><span class="t3">Sāsanassa ca lokassa vuḍḍhi bhavatu sabbadā,</span></p>', '-6-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Discourse on the Ten Things ', 'title', 7);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 7, '<p class="p3"><span class="t1">May the teaching and the world be on the increase every day, </span></p><p class="p3"><span class="t3">Sāsanam-pi ca lokañ-ca devā rakkhantu sabbadā.</span></p><p class="p3"><span class="t1">And may the gods every day protect the teaching and the world. </span></p><p class="p3"><span class="t3">Saddhiṁ hontu sukhī sabbe parivārehi attano,</span></p><p class="p3"><span class="t1">° May you, and all those who are around you, together with </span></p><p class="p3"><span class="t3">Anīghā sumanā hontu, saha sabbehi ñātibhi.</span></p><p class="p3"><span class="t1">All your relatives, be untroubled, happy, and easy in mind. </span></p><p class="p3"><span class="t5">Rājato vā, corato vā, manussato vā, amanussato vā,</span></p><p class="p3"><span class="t1">(May you be protected) from the king, thieves, humans, and non-humans, </span></p><p class="p3"><span class="t5">aggito vā, udakato vā, pisācato vā, khāṇukato vā, kaṇṭakato vā,</span></p><p class="p3"><span class="t1">from fire and water, demons, stumps, and thorns, </span></p><p class="p3"><span class="t5">nakkhattato vā, janapadarogato vā, </span></p><p class="p3"><span class="t1">from unlucky stars, and epidemics, </span></p><p class="p3"><span class="t5">asaddhammato vā, asandiṭṭhito vā, asappurisato vā,</span></p><p class="p3"><span class="t1">from what is not the true dhamma, not right view, not a good person, </span></p><p class="p3"><span class="t5">caṇḍa-hatthi-assa-miga-goṇa-kukkura-ahivicchika-maṇisappa-</span></p><p class="p3"><span class="t1">and from fierce elephants, horses, antelopes, bulls, dogs, snakes, scorpions, poisonous serpents, </span></p><p class="p3"><span class="t5">dīpi-accha-taraccha-sukara-mahisa-yakkha-rakkhasādihi</span></p><p class="p3"><span class="t1">panthers, bears, hyenas, wild boars, buffaloes, yakkhas, rakkhasas, and so on, </span></p><p class="p3"><span class="t5">nānā bhayato vā, nānā rogato vā, </span></p><p class="p3"><span class="t1">from the manifold fears, the manifold diseases, </span></p><p class="p3"><span class="t5">nānā upaddavato vā, ārakkhaṁ gaṇhantu!</span></p><p class="p3"><span class="t1">the manifold calamities, (from all of these troubles) may you receive protection! </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Dasadhammasuttaṁ</span></p><h3>The Discourse on the Ten Things </h3><p class="p3"><span class="t5">Evaṁ me sutaṁ:</span></p><p class="p3"><span class="t1">Thus I have heard: </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Sāvatthiyaṁ viharati</span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling near Sāvatthī </span></p><p class="p3"><span class="t5">Jetavane Anāthapiṇḍikassa ārāme.</span></p><p class="p3"><span class="t1">at Anāthapiṇḍika’s grounds in Jeta’s Wood. </span></p><p class="p3"><span class="t5">Tatra kho Bhagavā bhikkhū āmantesi:</span></p><p class="p3"><span class="t1">There it was that the Fortunate One addressed the monks, saying: </span></p><p class="p3"><span class="t5">“Bhikkhavo!” ti, “Bhadante!” ti te bhikkhū Bhagavato paccassosuṁ,</span></p><p class="p3"><span class="t1">“Monks!”, “Reverend Sir!” those monks replied to the Fortunate One, </span></p>', '-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 8, '<p class="p3"><span class="t5">Bhagavā etad-avoca:</span></p><p class="p3"><span class="t1">and the Fortunate One said this: </span></p><p class="p3"><span class="t5">“Dasa ime bhikkhave dhammā</span></p><p class="p3"><span class="t1">“There are these ten things, monks,  </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbā.</span></p><p class="p3"><span class="t1">that one who has gone forth should frequently reflect on. </span></p><p class="p3"><span class="t5">Katame dasa?</span></p><p class="p3"><span class="t1">What are the ten? </span></p><p class="p3"><span class="t5">“Vevaṇṇiyamhi ajjhupagato” ti,</span></p><p class="p3"><span class="t1">“I have become one who has (distinctive) appearance”, </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbaṁ. </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">one who has gone forth should frequently reflect on this. </span></p><p class="p3"><span class="t5">“Parapaṭibaddhā me jīvikā” ti,</span></p><p class="p3"><span class="t1">“I am bound to others for my livelihood”, </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbaṁ. </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">one who has gone forth should frequently reflect on this. </span></p><p class="p3"><span class="t5">“Añño me ākappo karaṇīyo” ti,</span></p><p class="p3"><span class="t1">“I should comport myself differently”, </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbaṁ. </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">one who has gone forth should frequently reflect on this. </span></p><p class="p3"><span class="t5">“Kacci nu kho me attā sīlato na upavadatī?” ti</span></p><p class="p3"><span class="t1">“Can I myself find no fault with my virtue?” </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbaṁ. </span><span class="t12">[4]</span></p><p class="p3"><span class="t1">one who has gone forth should frequently reflect on this. </span></p><p class="p3"><span class="t5">“Kacci nu kho maṁ anuvicca viññū sabrahmacārī, sīlato na upavadantī?” ti</span></p><p class="p3"><span class="t1">“Will my wise companions in the spiritual life, after testing me, find no fault with my virtue?” </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbaṁ. </span><span class="t12">[5]</span></p><p class="p3"><span class="t1">one who has gone forth should frequently reflect on this. </span></p><p class="p3"><span class="t5">“Sabbehi me piyehi manāpehi nānābhāvo vinābhāvo” ti,</span></p><p class="p3"><span class="t1">“There is alteration in, and separation from, all that is dear and appealing to me”, </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbaṁ. </span><span class="t12">[6]</span></p><p class="p3"><span class="t1">one who has gone forth should frequently reflect on this. </span></p><p class="p3"><span class="t5">“Kammassakomhi, kammadāyādo, </span></p><p class="p3"><span class="t1">“It is actions that I own, it is actions that I am heir to, </span></p><p class="p3"><span class="t5">kammayoni, kammabandhu, kammapaṭisaraṇo,</span></p><p class="p3"><span class="t1">it is actions that I am born from, actions are my kinsfolk, actions are my refuge, </span></p>', '-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 9, '<p class="p3"><span class="t5">yaṁ kammaṁ karissāmi, kalyāṇaṁ vā pāpakaṁ vā, </span></p><p class="p3"><span class="t1">whatever actions I perform, whether good or bad, </span></p><p class="p3"><span class="t5">tassa dāyādo bhavissāmī” ti,</span></p><p class="p3"><span class="t1">to that I will be the heir”, </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbaṁ. </span><span class="t12">[7]</span></p><p class="p3"><span class="t1">one who has gone forth should frequently reflect on this. </span></p><p class="p3"><span class="t5">“Kathaṁ bhūtassa me rattiṁdivā vītipatantī?” ti</span></p><p class="p3"><span class="t1">“In what way do the nights and days pass for me?” </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbaṁ. </span><span class="t12">[8]</span></p><p class="p3"><span class="t1">one who has gone forth should frequently reflect on this. </span></p><p class="p3"><span class="t5">“Kacci nu khohaṁ suññāgāre abhiramāmī?” ti</span></p><p class="p3"><span class="t1">“Do I delight in empty places?” </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbaṁ. </span><span class="t12">[9]</span></p><p class="p3"><span class="t1">one who has gone forth should frequently reflect on this. </span></p><p class="p3"><span class="t5">“Atthi nu kho me uttarimanussadhammā,</span></p><p class="p3"><span class="t1">“Has a state beyond (ordinary) human beings, </span></p><p class="p3"><span class="t5">alam-ariyañāṇadassanaviseso adhigato?</span></p><p class="p3"><span class="t1">the distinction of what is truly noble knowledge and seeing been attained by me? </span></p><p class="p3"><span class="t5">Soham pacchime kāle sabrahmacārīhi puṭṭho, </span></p><p class="p3"><span class="t1">Will I at the end, when questioned by my companions in the spiritual life, </span></p><p class="p3"><span class="t5">na maṅku bhavissāmī?” ti</span></p><p class="p3"><span class="t1">not be embarrassed?” </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbaṁ. </span><span class="t12">[10]</span></p><p class="p3"><span class="t1">one who has gone forth should frequently reflect on this. </span></p><p class="p3"><span class="t5">Ime kho bhikkhave dasadhammā, </span></p><p class="p3"><span class="t1">These are the ten things, monks, </span></p><p class="p3"><span class="t5">pabbajitena abhiṇhaṁ paccavekkhitabbā” ti.</span></p><p class="p3"><span class="t1">that one who has gone forth should frequently reflect on. </span></p><p class="p3"><span class="t5">Idam-avoca Bhagavā, </span></p><p class="p3"><span class="t1">The Fortunate One said this, </span></p><p class="p3"><span class="t5">attamanā te bhikkhū Bhagavato bhāsitaṁ abhinandun-ti.</span></p><p class="p3"><span class="t1">and those monks were uplifted and greatly rejoiced in the Fortunate One’s words. </span></p><p class="p3"><span class="t5">Āsīvāda</span></p><p class="p3"><span class="t5">Blessing</span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p>', '-9-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Tuesday ', 'title', 10);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Protective Meditations ', 'title', 10);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Recollection of the Buddha ', 'title', 10);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 10, '<p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t5">Etena saccavajjena sabbarogo vinassatu!</span></p><p class="p3"><span class="t1">By this declaration of the truth may all disease be destroyed! </span></p><p class="p3"><span class="t1">Meditation... then go to Conclusion part 1:  <span class="t11">Sabbapāpassa akaraṇaṁ. p. 44</span> </span></p><p class="p3"><span class="t1"> </span></p><h2>Tuesday </h2><p class="p3"><span class="t3">Ārakkhā Bhāvanā</span></p><h3>Protective Meditations </h3><p class="p3"><span class="t3">Buddhānussati, mettā ca, asubhaṁ, maraṇassati,</span></p><p class="p3"><span class="t1">Recollection of the Buddha, friendliness, unattractiveness, and mindfulness of death, </span></p><p class="p3"><span class="t3">iti imā caturārakkhā bhikkhu bhāveyya sīlavā.</span></p><p class="p3"><span class="t1">these are the four protective meditations that a virtuous monk should develop. </span></p><p class="p3"><span class="t3">Anantavitthāraguṇaṁ guṇatonussaraṁ Muniṁ,</span></p><p class="p3"><span class="t1">Recollecting the Sage’s virtue, and his endless, extensive, good qualities, </span></p><p class="p3"><span class="t3">bhāveyya buddhimā bhikkhu Buddhānussati-m-ādito.</span></p><p class="p3"><span class="t1">the wise monk should develop the recollection of the Buddha first. </span></p><p class="p3"><span class="t3">Buddhānussati</span></p><h3>Recollection of the Buddha </h3><p class="p3"><span class="t11">(Ārakkhā Bhāvanā 1)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t3">Savāsane kilese so eko sabbe nighātiya,</span></p><p class="p3"><span class="t1">Alone he destroyed all the corruptions, and (bad) predispositions, </span></p><p class="p3"><span class="t3">ahū susuddhasantāno pūjānaṁ ca sadāraho. </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">and being continually and fully pure he is worthy of worship at all times.  </span></p><p class="p3"><span class="t3">Sabbakālagate dhamme sabbe sammā sayaṁ Muni,</span></p><p class="p3"><span class="t1">The Sage by himself has, throughout the whole of time in every way,  </span></p><p class="p3"><span class="t3">sabbākārena bujjhitvā, eko sabbaññutaṁ gato. </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">completely awakened to all things, and alone he has arrived at omniscience.  </span></p><p class="p3"><span class="t3">Vipassanādi vijjāhi sīlādi caraṇehi ca,</span></p><p class="p3"><span class="t1">° Being endowed with great psychic power, good conduct, virtue, and so on, </span></p><p class="p3"><span class="t3">susamiddhehi sampanno, gaganābhehi nāyako. </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">true understanding, insight, and so on, the leader was like the shining sky.  </span></p><p class="p3"><span class="t3">Sammāgato subhaṁ ṭhānaṁ amoghavacano ca so,</span></p><p class="p3"><span class="t1">He who never spoke foolish words, has arrived at that glorious state (Nibbāna), </span></p><p class="p3"><span class="t3">tividhassāpi lokassa ñātā niravasesato. </span><span class="t12">[4]</span></p>', '-10-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Discourse on the Great Blessings ', 'title', 11);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 11, '<p class="p3"><span class="t1">he knew the threefold world system (completely) without leaving anything out.  </span></p><p class="p3"><span class="t3">Anekehi guṇoghehi sabbasattuttamo ahū,</span></p><p class="p3"><span class="t1">Overflowing with countless good qualities he is supreme among all beings, </span></p><p class="p3"><span class="t3">Anekehi upāyehi naradamme damesi ca. </span><span class="t12">[5]</span></p><p class="p3"><span class="t1">with countless skilful means he tamed those men who could be tamed.  </span></p><p class="p3"><span class="t3">Eko sabbassa lokassa sabbam-atthānusāsako,</span></p><p class="p3"><span class="t1">He alone, to the whole world was the teacher of everything good,  </span></p><p class="p3"><span class="t3">Bhāgya-issariyādīnaṁ guṇānaṁ paramo nidhī. </span><span class="t12">[6]</span></p><p class="p3"><span class="t1">he is the highest treasure, having qualities such as good fortune, mastery, and so on.  </span></p><p class="p3"><span class="t3">Paññāssa sabbadhammesu karuṇā sabbajantusu,</span></p><p class="p3"><span class="t1">Being wise in regard to all things compassionate to everybody, </span></p><p class="p3"><span class="t3">attatthānaṁ paratthānaṁ sādhikā guṇajeṭṭhikā. </span><span class="t12">[7]</span></p><p class="p3"><span class="t1">he exceeded (others) in the best qualities, (knowing) what was for his own and others’ good.  </span></p><p class="p3"><span class="t3">Dayāya pāramī citvā paññāyattānam-uddharī,</span></p><p class="p3"><span class="t1">Through sympathy he set his mind on the perfections through wisdom he raised himself up, </span></p><p class="p3"><span class="t3">uddharī sabbadhamme ca, dayāyaññe ca uddharī. </span><span class="t12">[8]</span></p><p class="p3"><span class="t1">he raised himself above all things, through sympathy he raised others too.  </span></p><p class="p3"><span class="t3">Dissamāno pi tāvassa rūpakāyo acintayo,</span></p><p class="p3"><span class="t1">Even his visible form-body was beyond thought, </span></p><p class="p3"><span class="t3">asādhāraṇañāṇaḍḍhe dhammakāye kathā va kā ti? </span><span class="t12">[9]</span></p><p class="p3"><span class="t1">what can be said of his spiritual body, which was unique, having powerful knowledge?  </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Mahāmaṅgalasuttaṁ</span></p><h3>The Discourse on the Great Blessings </h3><p class="p3"><span class="t5">Evaṁ me sutaṁ:</span></p><p class="p3"><span class="t1">Thus I have heard: </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Sāvatthiyaṁ viharati</span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling near Sāvatthī </span></p><p class="p3"><span class="t5">Jetavane Anāthapiṇḍikassa ārāme.</span></p><p class="p3"><span class="t1">at Anāthapiṇḍika’s grounds in Jeta’s Wood. </span></p><p class="p3"><span class="t5">Atha kho aññatarā devatā abhikkantāya rattiyā,</span></p><p class="p3"><span class="t1">Then a certain god, towards the end of the night, </span></p><p class="p3"><span class="t5">abhikkantavaṇṇā kevalakappaṁ Jetavanaṁ obhāsetvā,</span></p><p class="p3"><span class="t1">whose surpassing beauty lit up the whole of Jeta’s Wood, </span></p><p class="p3"><span class="t5">yena Bhagavā tenupasaṅkami, </span></p><p class="p3"><span class="t1">approached the Fortunate One,  </span></p>', '-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 12, '<p class="p3"><span class="t5">upasaṅkamitvā Bhagavantaṁ abhivādetvā, ekam-antaṁ aṭṭhāsi.</span></p><p class="p3"><span class="t1">and after approaching and worshipping the Fortunate One, he stood on one side. </span></p><p class="p3"><span class="t5">Ekam-antaṁ ṭhitā kho sā devatā Bhagavantaṁ gāthāya ajjhabhāsi:</span></p><p class="p3"><span class="t1">While standing on one side that god addressed the Fortunate One with a verse: </span></p><p class="p3"><span class="t3">“Bahū devā manussā ca maṅgalāni acintayuṁ</span></p><p class="p3"><span class="t1">“Many are the gods and the men who have thought about the blessings </span></p><p class="p3"><span class="t3">Ākaṅkhamānā sotthānaṁ: brūhi maṅgalam-uttamaṁ.” </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">Hoping for safety: now please say what is the supreme blessing.”  </span></p><p class="p3"><span class="t3">“Asevanā ca bālānaṁ, paṇḍitānañ-ca sevanā,</span></p><p class="p3"><span class="t1">“Not associating with fools, but associating with the wise,  </span></p><p class="p3"><span class="t3">Pūjā ca pūjanīyānaṁ: etaṁ maṅgalam-uttamaṁ. </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">Honouring those worthy of honour: this is the supreme blessing. </span></p><p class="p3"><span class="t3">Paṭirūpadesavāso ca, pubbe ca katapuññatā, </span></p><p class="p3"><span class="t1">Living in a suitable place, formerly having done good deeds, </span></p><p class="p3"><span class="t3">Attasammāpaṇidhi ca: etaṁ maṅgalam-uttamaṁ. </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">Aspiring in a right way oneself: this is the supreme blessing.  </span></p><p class="p3"><span class="t3">Bāhusaccañ-ca sippañ-ca, vinayo ca susikkhito,</span></p><p class="p3"><span class="t1">Having great learning and craft, and being disciplined and well trained, </span></p><p class="p3"><span class="t3">Subhāsitā ca yā vācā: etaṁ maṅgalam-uttamaṁ. </span><span class="t12">[4]</span></p><p class="p3"><span class="t1">And whatever words are well spoken: this is the supreme blessing.  </span></p><p class="p3"><span class="t3">Mātāpitu-upaṭṭhānaṁ, puttadārassa saṅgaho,</span></p><p class="p3"><span class="t1">Attendance on one''s mother and father, looking after one’s wife and children, </span></p><p class="p3"><span class="t3">Anākulā ca kammantā: etaṁ maṅgalam-uttamaṁ. </span><span class="t12">[5]</span></p><p class="p3"><span class="t1">With works that are not agitating: this is the supreme blessing.  </span></p><p class="p3"><span class="t3">Dānañ-ca Dhammacariyā ca, ñātakānañ-ca saṅgaho,</span></p><p class="p3"><span class="t1">Giving, and living by the Dhamma, and looking after one’s relatives, </span></p><p class="p3"><span class="t3">Anavajjāni kammāni: etaṁ maṅgalam-uttamaṁ. </span><span class="t12">[6]</span></p><p class="p3"><span class="t1">(Performing) actions that are blameless: this is the supreme blessing.  </span></p><p class="p3"><span class="t3">Ārati virati pāpā, majjapānā ca saññamo,</span></p><p class="p3"><span class="t1">Abstinence, avoidance of bad deeds, restraint from intoxicating drink, </span></p><p class="p3"><span class="t3">Appamādo ca dhammesu: etaṁ maṅgalam-uttamaṁ. </span><span class="t12">[7]</span></p><p class="p3"><span class="t1">Being heedful regarding (all) things: this is the supreme blessing.  </span></p><p class="p3"><span class="t3">Gāravo ca nivāto ca, santuṭṭhī ca kataññutā,</span></p><p class="p3"><span class="t1">Having respect, being humble, being satisfied and grateful, </span></p><p class="p3"><span class="t3">Kālena Dhammasavaṇaṁ: etaṁ maṅgalam-uttamaṁ. </span><span class="t12">[8]</span></p><p class="p3"><span class="t1">Listening to Dhamma at the right time: this is the supreme blessing.  </span></p>', '-12-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Wednesday ', 'title', 13);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Safeguard in this Order’s Domain ', 'title', 13);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 13, '<p class="p3"><span class="t3">Khantī ca sovacassatā, samaṇānañ-ca dassanaṁ,</span></p><p class="p3"><span class="t1">Being patient and easily spoken to, seeing ascetics, </span></p><p class="p3"><span class="t3">Kālena Dhammasākacchā: etaṁ maṅgalam-uttamaṁ. </span><span class="t12">[9]</span></p><p class="p3"><span class="t1">Discussing Dhamma at the right time: this is the supreme blessing.  </span></p><p class="p3"><span class="t3">Tapo ca brahmacariyañ-ca, ariyasaccāna’ dassanaṁ,</span></p><p class="p3"><span class="t1">Austere, living spiritually, insight into the noble truths, </span></p><p class="p3"><span class="t3">Nibbānasacchikiriyā ca: etaṁ maṅgalam-uttamaṁ. </span><span class="t12">[10]</span></p><p class="p3"><span class="t1">The experience of Nibbāna: this is the supreme blessing.  </span></p><p class="p3"><span class="t3">Phuṭṭhassa lokadhammehi, cittaṁ yassa na kampati,</span></p><p class="p3"><span class="t1">He whose mind does not waver, when it is touched by things of this world, </span></p><p class="p3"><span class="t3">Asokaṁ virajaṁ khemaṁ: etaṁ maṅgalam-uttamaṁ. </span><span class="t12">[11]</span></p><p class="p3"><span class="t1">(being) griefless, dustless, and secure: this is the supreme blessing.  </span></p><p class="p3"><span class="t3">Etādisāni katvāna, sabbattha-m-aparājitā,</span></p><p class="p3"><span class="t1">Having done as here directed, they are undefeated everywhere, </span></p><p class="p3"><span class="t3">sabbattha sotthiṁ gacchanti: taṁ tesaṁ maṅgalam-uttaman”-ti. </span><span class="t12">[12]</span></p><p class="p3"><span class="t1">They go everywhere in safety: for them this is the supreme blessing.”  </span></p><p class="p3"><span class="t5">Āsīvāda</span></p><p class="p3"><span class="t5">Blessing</span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t5">Etena saccavajjena sabbarogo vinassatu!</span></p><p class="p3"><span class="t1">By this declaration of the truth may all disease be destroyed! </span></p><p class="p3"><span class="t11">Meditation... then go to Conclusion part 1:  </span><span class="t11">Sabbapāpassa akaraṇaṁ. p. 44</span></p><p class="p3"><span class="t1"> </span></p><h2>Wednesday </h2><p class="p3"><span class="t3">Āṇakkhettaparittaṁ, 2</span></p><h3>Safeguard in this Order’s Domain </h3><p class="p3"><span class="t5">Paṇidhānato paṭṭhāya, Tathāgatassa dasapāramiyo, </span></p><p class="p3"><span class="t1">Beginning with the aspiration (for Buddhahood, and thinking of) the Realised One’s ten perfections, </span></p><p class="p3"><span class="t5">dasa-upapāramiyo, dasaparamatthapāramiyo - samatiṁsapāramiyo - </span></p><p class="p3"><span class="t1">ten minor perfections, ten great perfections - altogether thirty perfections - </span></p><p class="p3"><span class="t5">pañcamahāpariccāge, tisso cariyā, pacchimabhave gabbhāvakkantiṁ, jātiṁ,</span></p><p class="p3"><span class="t1">his five great sacrifices, three ways of conduct, descent into the womb for his final existence, birth, </span></p><p class="p3"><span class="t5">Abhinikkhamanaṁ, padhānacariyaṁ, Bodhipallaṅkaṁ, Māravijayaṁ,</span></p>', '-13-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Discourse on the Treasures ', 'title', 14);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 14, '<p class="p3"><span class="t1">Great Renunciation, life of effort, sitting cross legged at the Bodhi tree, victory over Māra, </span></p><p class="p3"><span class="t5">sabbaññutañāṇapaṭivedhaṁ, navalokuttaradhamme ti,</span></p><p class="p3"><span class="t1">his penetration and knowledge of omniscience, and his nine supermundane attainments, </span></p><p class="p3"><span class="t5">sabbe pime Buddhaguṇe āvajjitvā, Vesāliyaṁ tīsu pākārantaresu,</span></p><p class="p3"><span class="t1">and after considering all these Buddha qualities, (going round) the three walls of Vesāli, </span></p><p class="p3"><span class="t5">tiyāmarattiṁ, parittaṁ karonto āyasmā Ānandatthero,</span></p><p class="p3"><span class="t1">during the three watches of the night, the venerable elder Ānanda, </span></p><p class="p3"><span class="t5">viya kāruññacittaṁ upaṭṭhapetvā:</span></p><p class="p3"><span class="t1">after establishing great compassion in his heart, made this safeguard: </span></p><p class="p3"><span class="t3">Koṭisatasahassesu cakkavāḷesu devatā</span></p><p class="p3"><span class="t1">The gods in the ten hundred thousand million universes </span></p><p class="p3"><span class="t3">yassāṇaṁ patigaṇhanti yañ-ca Vesāliyaṁ pure,</span></p><p class="p3"><span class="t1">accepted the order (given) in the city of Vesāli, </span></p><p class="p3"><span class="t3">rogāmanussadubbhikkha sambhūtaṁ tividhaṁ bhayaṁ,</span></p><p class="p3"><span class="t1">and disease, non-human beings, and famine, the threefold fear that had arisen, </span></p><p class="p3"><span class="t3">khippam-antaradhāpesi, parittaṁ taṁ bhaṇāmahe:</span></p><p class="p3"><span class="t1">quickly disappeared, we will recite that safeguard: </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Ratanasuttaṁ</span></p><h3>The Discourse on the Treasures </h3><p class="p3"><span class="t3">Yānīdha bhūtāni samāgatāni, bhummāni vā yāni va antalikkhe,</span></p><p class="p3"><span class="t1">Whatever beings have come together here, whether of the earth or in the firmament, </span></p><p class="p3"><span class="t3">sabbe va bhūtā sumanā bhavantu, atho pi sakkacca suṇantu bhāsitaṁ. </span><span class="t5">[1]</span></p><p class="p3"><span class="t1">may the minds of all those beings be happy, and may they listen carefully to what is said.  </span></p><p class="p3"><span class="t3">Tasmā hi bhūtā nisāmetha sabbe, mettaṁ karotha mānusiyā pajāya,</span></p><p class="p3"><span class="t1">Therefore, all of you beings, be attentive, be friendly towards this generation of men, </span></p><p class="p3"><span class="t3">divā ca ratto ca haranti ye baliṁ, tasmā hi ne rakkhatha appamattā. </span><span class="t5">[2]</span></p><p class="p3"><span class="t1">they who bring offerings by day and by night, therefore, being heedful, you must protect them. </span></p><p class="p3"><span class="t13">Yaṁ kiñci vittaṁ - idha vā huraṁ vā saggesu vā - yaṁ ratanaṁ paṇītaṁ</span></p><p class="p3"><span class="t1">Whatever riches there are - here or elsewhere or in the heavens - that excellent treasure </span></p><p class="p3"><span class="t3">na no samaṁ atthi Tathāgatena, idam-pi Buddhe ratanaṁ paṇītaṁ:</span></p><p class="p3"><span class="t1">is not equal unto the Realised One, this excellent treasure is in the Buddha: </span></p><p class="p3"><span class="t3">etena saccena suvatthi hotu! </span><span class="t5">[3]</span></p><p class="p3"><span class="t1">by virtue of this truth may there be safety! </span></p><p class="p3"><span class="t3">Khayaṁ virāgaṁ amataṁ paṇītaṁ yad-ajjhagā Sakyamunī samāhito,</span></p><p class="p3"><span class="t1">(Pollutants'') end, dispassion, deathlessness, excellence: which the concentrated Sakyan sage attained, </span></p>', '-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 15, '<p class="p3"><span class="t3">na tena dhammena samatthi kiñci, idam-pi Dhamme ratanaṁ paṇītaṁ:</span></p><p class="p3"><span class="t1">there is nothing that is equal to that state, this excellent treasure is in the Dhamma: </span></p><p class="p3"><span class="t3">etena saccena suvatthi hotu! </span><span class="t5">[4]</span></p><p class="p3"><span class="t1">by virtue of this truth may there be safety!  </span></p><p class="p3"><span class="t3">Yam-Buddhaseṭṭho parivaṇṇayī suciṁ, samādhim-ānantarikañ-ñam-āhu,</span></p><p class="p3"><span class="t1">That which the Buddha, the Great One, praised as pure, the concentration said to have prompt (result), </span></p><p class="p3"><span class="t3">samādhinā tena samo na vijjati, idam-pi Dhamme ratanaṁ paṇītaṁ:</span></p><p class="p3"><span class="t1">no equal to that concentration is found, this excellent treasure is in the Dhamma: </span></p><p class="p3"><span class="t3">etena saccena suvatthi hotu! </span><span class="t5">[5]</span></p><p class="p3"><span class="t1">by virtue of this truth may there be safety!  </span></p><p class="p3"><span class="t3">Ye puggalā aṭṭha sataṁ pasatthā, cattāri etāni yugāni honti,</span></p><p class="p3"><span class="t1">Those eight individuals praised by the good, there are these four pairs (of individuals), </span></p><p class="p3"><span class="t3">te dakkhiṇeyyā Sugatassa sāvakā, etesu dinnāni mahapphalāni,</span></p><p class="p3"><span class="t1">those disciples of the Happy One are worthy of gifts, those things that have been given to them have great fruits, </span></p><p class="p3"><span class="t3">idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span><span class="t5">[6]</span></p><p class="p3"><span class="t1">this excellent treasure is in the Saṅgha: by virtue of this truth may there be safety!  </span></p><p class="p3"><span class="t3">Ye suppayuttā manasā daḷhena, nikkāmino Gotamasāsanamhi,</span></p><p class="p3"><span class="t1">Those who have firm and devoted minds, without sense desire in Gotama''s dispensation, </span></p><p class="p3"><span class="t3">te pattipattā amataṁ vigayha, laddhā mudhā nibbutiṁ bhuñjamānā,</span></p><p class="p3"><span class="t1">those who have attained, and entered the deathless, are enjoying emancipation, gained for free, </span></p><p class="p3"><span class="t3">idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span><span class="t5">[7]</span></p><p class="p3"><span class="t1">this excellent treasure is in the Saṅgha: by virtue of this truth may there be safety!  </span></p><p class="p3"><span class="t3">Yathindakhīlo paṭhaviṁ sito siyā, catubbhi vātehi asampakampiyo,</span></p><p class="p3"><span class="t1">Just as a locking post stuck fast in the earth does not waver on account of the four winds, </span></p><p class="p3"><span class="t3">Tathūpamaṁ sappurisaṁ vadāmi, yo ariyasaccāni avecca passati,</span></p><p class="p3"><span class="t1">in the same way, I say, is the true person, the one who sees the noble truths completely, </span></p><p class="p3"><span class="t3">idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span><span class="t5">[8]</span></p><p class="p3"><span class="t1">this excellent treasure is in the Saṅgha: by virtue of this truth may there be safety!  </span></p><p class="p3"><span class="t3">Ye ariyasaccāni vibhāvayanti, gambhīrapaññena sudesitāni,</span></p><p class="p3"><span class="t1">Those who clearly distinguish the noble truths, which were well preached by the one with deep wisdom, </span></p><p class="p3"><span class="t3">kiñcāpi te honti bhusappamattā, na te bhavaṁ aṭṭhamaṁ ādiyanti,</span></p><p class="p3"><span class="t1">however great they become in heedlessness still they do not take up an eighth existence, </span></p><p class="p3"><span class="t3">idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span><span class="t5">[9]</span></p><p class="p3"><span class="t1">this excellent treasure is in the Saṅgha: by virtue of this truth may there be safety!  </span></p><p class="p3"><span class="t3">Sahā vassa dassanasampadāya tayassu dhammā jahitā bhavanti:</span></p><p class="p3"><span class="t1">With his attainment of (liberating) insight there are a triad of things that are given up: </span></p>', '-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 16, '<p class="p3"><span class="t3">Sakkāyadiṭṭhi vicikicchitañ-ca sīlabbataṁ vāpi yad-atthi kiñci.</span></p><p class="p3"><span class="t1">embodiment view, uncertainty, and whatever (grasping at) virtue and practices there is. </span></p><p class="p3"><span class="t3">Catūhapāyehi ca vippamutto, cha cābhiṭhānāni abhabbo kātuṁ,</span></p><p class="p3"><span class="t1">He is free from (rebirth in) the four lower worlds, he is incapable of the six great crimes. </span></p><p class="p3"><span class="t3">idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span><span class="t5">[10]</span></p><p class="p3"><span class="t1">this excellent treasure is in the Saṅgha: by virtue of this truth may there be safety! </span></p><p class="p3"><span class="t3">Kiñcāpi so kammaṁ karoti pāpakaṁ, kāyena vācā uda cetasā vā,</span></p><p class="p3"><span class="t1">Whatever bad action there is that he performs by way of body, or of speech, or of mind, </span></p><p class="p3"><span class="t3">abhabbo so tassa paṭicchādāya, abhabbatā diṭṭhapadassa vuttā,</span></p><p class="p3"><span class="t1">he is incapable of covering it up, this incapacity is said of one who has seen the state (of peace), </span></p><p class="p3"><span class="t3">idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span><span class="t5">[11]</span></p><p class="p3"><span class="t1">this excellent treasure is in the Saṅgha: by virtue of this truth may there be safety!  </span></p><p class="p3"><span class="t3">Vanappagumbe yathā phussitagge gimhānamāse paṭhamasmiṁ gimhe,</span></p><p class="p3"><span class="t1">Just like a tall woodland tree crowned with blossom in the summer months, in the early summer, </span></p><p class="p3"><span class="t3">Tathūpamaṁ Dhammavaraṁ adesayī, Nibbānagāmiṁ paramaṁhitāya,</span></p><p class="p3"><span class="t1">in the same way he preached the Dhamma which is best, which goes to Nibbāna, the highest benefit, </span></p><p class="p3"><span class="t3">idam-pi Buddhe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span><span class="t5">[12]</span></p><p class="p3"><span class="t1">this excellent treasure is in the Buddha: by virtue of this truth may there be safety!  </span></p><p class="p3"><span class="t3">Varo varaññū varado varāharo, anuttaro Dhammavaraṁ adesayī.</span></p><p class="p3"><span class="t1">The best one, knowing the best, giving the best, brought the best, brought the best, unsurpassed he preached the best Dhamma. </span></p><p class="p3"><span class="t3">idam-pi Buddhe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu! </span><span class="t5">[13]</span></p><p class="p3"><span class="t1">this excellent treasure is in the Buddha: by virtue of this truth may there be safety!  </span></p><p class="p3"><span class="t14">Khīṇaṁ purāṇaṁ navaṁ natthi sambhavaṁ, virattacittā āyatike bhavasmiṁ,</span></p><p class="p3"><span class="t1">The old is destroyed, and nothing new is produced, (their) minds are unexcited by future rebirth, </span></p><p class="p3"><span class="t3">te khīṇabījā avirūḷhicchandā, nibbanti dhīrā yathāyam-padīpo,</span></p><p class="p3"><span class="t1">they have destroyed the seeds, and have no desire for growth, the wise are still, just as this lamp (is still), </span></p><p class="p3"><span class="t3">idam-pi Saṅghe ratanaṁ paṇītaṁ: etena saccena suvatthi hotu!</span><span class="t14"> </span><span class="t5">[14]</span></p><p class="p3"><span class="t1">this excellent treasure is in the Saṅgha: by virtue of this truth may there be safety! </span></p><p class="p3"><span class="t11"> (Spoken by Sakka, lord of the gods:)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t3">Yānīdha bhūtāni samāgatāni, bhummāni vā yāni va antalikkhe,</span></p><p class="p3"><span class="t1">Whatever beings have come together here, whether of the earth or in the firmament, </span></p><p class="p3"><span class="t15">Tathāgataṁ devamanussapūjitaṁ, Buddhaṁ namassāma suvatthi hotu! </span><span class="t16">[15]</span></p><p class="p3"><span class="t1">the Realised One is revered by gods and men, we honour the Buddha - may there be safety!  </span></p><p class="p3"><span class="t3">Yānīdha bhūtāni samāgatāni, bhummāni vā yāni va antalikkhe,</span></p><p class="p3"><span class="t1">Whatever beings have come together here, whether of the earth or in the firmament, </span></p>', '-16-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Thursday ', 'title', 17);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Development of Friendliness Meditation ', 'title', 17);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 17, '<p class="p3"><span class="t17">Tathāgataṁ devamanussapūjitaṁ, Dhammaṁ namassāma suvatthi hotu! </span><span class="t18">[16]</span></p><p class="p3"><span class="t1">the Realised One is revered by gods and men, we honour the Dhamma - may there be safety!  </span></p><p class="p3"><span class="t3">Yānīdha bhūtāni samāgatāni, bhummāni vā yāni va antalikkhe,</span></p><p class="p3"><span class="t1">Whatever beings have come together here, whether of the earth or in the firmament, </span></p><p class="p3"><span class="t15">Tathāgataṁ devamanussapūjitaṁ, Saṅghaṁ namassāma suvatthi hotu! </span><span class="t16">[17]</span></p><p class="p3"><span class="t1">the Realised One is revered by gods and men, we honour the Saṅgha - may there be safety!  </span></p><p class="p3"><span class="t5">Āsīvāda</span></p><p class="p3"><span class="t5">Blessing</span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t5">Etena saccavajjena sabbarogo vinassatu!</span></p><p class="p3"><span class="t1">By this declaration of the truth may all disease be destroyed! </span></p><p class="p3"><span class="t11">Meditation... then go to Conclusion part 1:  </span><span class="t11">Sabbapāpassa akaraṇaṁ. p. 44</span></p><p class="p3"><span class="t1"> </span></p><h2>Thursday </h2><p class="p3"><span class="t3">Mettā Bhāvanā</span></p><h3>The Development of Friendliness Meditation </h3><p class="p3"><span class="t11">(Ārakkhā Bhāvanā 2)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t3">Attūpamāya sabbesaṁ sattānaṁ sukhakāmataṁ,</span></p><p class="p3"><span class="t1">In a similar way to oneself, all beings desire happiness, </span></p><p class="p3"><span class="t3">passitvā kamato mettaṁ sabbasattesu bhāvaye. </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">having seen that one should gradually develop friendliness towards all beings.  </span></p><p class="p3"><span class="t3">Sukhī bhaveyyaṁ niddukkho, ahaṁ niccaṁ, ahaṁ viya</span></p><p class="p3"><span class="t1">May I constantly be happy, free from suffering, and like myself </span></p><p class="p3"><span class="t3">hitā ca me sukhī hontu, majjhattā catha verino. </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">may my benefactors be happy, neutral persons, and foes also. </span></p><p class="p3"><span class="t3">Imamhi gāmakkhettamhi, sattā hontu sukhī sadā,</span></p><p class="p3"><span class="t1">In this village, and its surrounding fields, may beings always be happy, </span></p><p class="p3"><span class="t3">tato parañ-ca rajjesu, cakkavāḷesu jantuno. </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">and those in other countries, and people throughout the universe.  </span></p><p class="p3"><span class="t3">Samantā cakkavāḷesu sattānantesu pāṇino,</span></p><p class="p3"><span class="t1">° Throughout the entire universe may the countless beings, creatures, </span></p><p class="p3"><span class="t3">sukhino puggalā bhūtā attabhāvagatā siyuṁ, </span><span class="t12">[4]</span></p>', '-17-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Discourse on how Friendliness Meditation should be Done ', 'title', 18);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 18, '<p class="p3"><span class="t1">persons, and bhūtas, who have attained individuality, be happy,  </span></p><p class="p3"><span class="t3">tathā itthī pumā ceva ariyā anariyā pi ca,</span></p><p class="p3"><span class="t1">and so too (all) women, men, noble ones, and also ignoble ones, </span></p><p class="p3"><span class="t3">devā narā apāyaṭṭhā, tathā dasadisāsu cā ti. </span><span class="t12">[5]</span></p><p class="p3"><span class="t1">gods, humans, and fallen creatures, and likewise (all beings) in the ten directions.  </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Karaṇīyamettasuttaṁ</span></p><h3>The Discourse on how Friendliness Meditation should be Done </h3><p class="p3"><span class="t3">Karaṇīyam-atthakusalena, yan-taṁ santaṁ padaṁ abhisamecca:</span></p><p class="p3"><span class="t1">What should be done by one skilled in goodness, who has comprehended the state of peace: </span></p><p class="p3"><span class="t3">sakko ujū ca sūjū ca, suvaco cassa mudu anatimānī, </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">he ought to be able, straight, and upright, easy to speak to, meek, without conceit, </span></p><p class="p3"><span class="t3">santussako ca subharo ca, appakicco ca sallahukavutti,</span></p><p class="p3"><span class="t1">satisfied (with little), easy to support, free from duties, and light in living, </span></p><p class="p3"><span class="t3">santindriyo ca nipako ca, appagabbho kulesu ananugiddho, </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">with faculties at peace, prudent, not forward, and greedless among the families, </span></p><p class="p3"><span class="t3">na ca khuddaṁ samācare kiñci yena viññū pare upavadeyyuṁ.</span></p><p class="p3"><span class="t1">he should not do the slightest thing whereby others who are wise might find fault (with him). </span></p><p class="p3"><span class="t3">“Sukhino vā khemino hontu, sabbe sattā bhavantu sukhitattā! </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">“(May all beings) be happy and secure, may all beings in their hearts be happy! </span></p><p class="p3"><span class="t3">Ye keci pāṇabhūtatthi tasā vā thāvarā vā anavasesā,</span></p><p class="p3"><span class="t1">Whatsoever breathing beings there are, trembling, firm, or any other (beings), </span></p><p class="p3"><span class="t3">dīghā vā ye mahantā vā, majjhimā rassakāṇukathūlā, </span><span class="t12">[4]</span></p><p class="p3"><span class="t1">whether they be long or great, of middle (size), short, tiny, or of compact (body), </span></p><p class="p3"><span class="t3">diṭṭhā vā ye ca addiṭṭhā, ye ca dūre vasanti avidūre,</span></p><p class="p3"><span class="t1">those who are seen, and those who are unseen, those who live far away, those who are near, </span></p><p class="p3"><span class="t3">bhūtā vā sambhavesī vā sabbe sattā bhavantu sukhitattā!” </span><span class="t12">[5]</span></p><p class="p3"><span class="t1">those who are born, and those who still seek birth, may all beings in their hearts be happy!” </span></p><p class="p3"><span class="t3">Na paro paraṁ nikubbetha, nātimaññetha katthaci naṁ kañci,</span></p><p class="p3"><span class="t1">No one should cheat another, nor should he despise anyone wherever he is, </span></p><p class="p3"><span class="t3">byārosanā paṭighasaññā nāññam-aññassa dukkham-iccheyya. </span><span class="t12">[6]</span></p><p class="p3"><span class="t1">he should not long for suffering for another because of anger or resentment. </span></p><p class="p3"><span class="t3">Mātā yathā niyaṁ puttaṁ āyusā ekaputtam-anurakkhe,</span></p><p class="p3"><span class="t1">In the same way as a mother would protect her child, her only child, with her life, </span></p><p class="p3"><span class="t3">evam-pi sabbabhūtesu mānasaṁ bhāvaye aparimāṇaṁ. </span><span class="t12">[7]</span></p><p class="p3"><span class="t1">so too towards all beings one should develop the measureless thought (of friendliness). </span></p>', '-18-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Verses from the Dhammapada ', 'title', 19);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 19, '<p class="p3"><span class="t3">Mettañ-ca sabbalokasmiṁ mānasaṁ bhāvaye aparimāṇaṁ,</span></p><p class="p3"><span class="t1">Towards the entire world he should develop the measureless thought of friendliness, </span></p><p class="p3"><span class="t3">uddhaṁ adho ca tiriyañ-ca, asambādhaṁ averaṁ asapattaṁ. </span><span class="t12">[8]</span></p><p class="p3"><span class="t1">above, below, and across (the middle), without barriers, hate, or enmity. </span></p><p class="p3"><span class="t3">Tiṭṭhaṁ caraṁ nisinno vā, sayāno vā yāvatassa vigatamiddho,</span></p><p class="p3"><span class="t1">Standing, walking, sitting, lying, for as long as he is without torpor, </span></p><p class="p3"><span class="t3">etaṁ satiṁ adhiṭṭheyya, brahmam-etaṁ vihāraṁ idha-m-āhu. </span><span class="t12">[9]</span></p><p class="p3"><span class="t1">he should be resolved on this mindfulness, for this, they say here, is the (true) spiritual life. </span></p><p class="p3"><span class="t3">Diṭṭhiñ-ca anupagamma, sīlavā dassanena sampanno,</span></p><p class="p3"><span class="t1">Without going near a (wrong) view, virtuous, and endowed with (true) insight, </span></p><p class="p3"><span class="t3">kāmesu vineyya gedhaṁ, na hi jātu gabbhaseyyaṁ punar-etī ti. </span><span class="t12">[10]</span></p><p class="p3"><span class="t1">having removed greed towards sense pleasures, he does not come to lie in a womb again.  </span></p><p class="p3"><span class="t5">Āsīvāda</span></p><p class="p3"><span class="t5">Blessing</span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t5">Etena saccavajjena sabbarogo vinassatu!</span></p><p class="p3"><span class="t1">By this declaration of the truth may all disease be destroyed! </span></p><p class="p3"><span class="t19">Meditation... then...</span></p><p class="p3"><span class="t20"> </span></p><p class="p3"><span class="t3">Dhammapadagāthā</span></p><h3>Verses from the Dhammapada </h3><p class="p3"><span class="t3">Sabbapāpassa akaraṇaṁ, kusalassa upasampadā,</span></p><p class="p3"><span class="t1">Not doing any bad deeds, undertaking wholesome deeds, </span></p><p class="p3"><span class="t3">sacittapariyodapanaṁ etaṁ Buddhāna’ sāsanaṁ. </span><span class="t12">[183]</span></p><p class="p3"><span class="t1">and purifying one’s mind this is the teaching of the Buddhas. </span></p><p class="p3"><span class="t3">Khantī paramaṁ tapo titikkhā, Nibbānaṁ paramaṁ vadanti Buddhā.</span></p><p class="p3"><span class="t1">Forbearing patience is the supreme austerity, Nibbāna is supreme say the Buddhas. </span></p><p class="p3"><span class="t3">Na hi pabbajito parūpaghāti, samaṇo hoti paraṁ viheṭhayanto. </span><span class="t12">[184]</span></p><p class="p3"><span class="t1">One gone forth does not hurt another, (nor does) an ascetic harass another. </span></p><p class="p3"><span class="t3">Anūpavādo, anūpaghāto, pātimokkhe ca saṁvaro,</span></p><p class="p3"><span class="t1">Not finding fault, not hurting, restraint in regard to the precepts, </span></p><p class="p3"><span class="t3">mattaññutā ca bhattasmiṁ, pantañ-ca sayanāsanaṁ,</span></p><p class="p3"><span class="t1">knowing the correct measure in food, (living in) a remote dwelling place, </span></p>', '-19-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Verses on the Great Blessings of Success ', 'title', 20);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 20, '<p class="p3"><span class="t3">adhicitte ca āyogo etaṁ Buddhāna’ sāsanaṁ. </span><span class="t12">[185]</span></p><p class="p3"><span class="t1">being devoted to meditation this is the teaching of the Buddhas. </span></p><p class="p3"><span class="t3">Sabbe saṅkhārā aniccā ti, yadā paññāya passati,</span></p><p class="p3"><span class="t1">All conditioned things are impermanent, when one sees this with wisdom, </span></p><p class="p3"><span class="t3">atha nibbindati dukkhe, esa maggo visuddhiyā. </span><span class="t12">[277]</span></p><p class="p3"><span class="t1">then one grows tired of suffering, this is the path to purity. </span></p><p class="p3"><span class="t3">Sabbe saṅkhārā dukkhā ti, yadā paññāya passati,</span></p><p class="p3"><span class="t1">All conditioned things are suffering, when one sees this with wisdom, </span></p><p class="p3"><span class="t3">atha nibbindati dukkhe, esa maggo visuddhiyā. </span><span class="t12">[278]</span></p><p class="p3"><span class="t1">then one grows tired of suffering, this is the path to purity. </span></p><p class="p3"><span class="t3">Sabbe dhammā anattā ti, yadā paññāya passati,</span></p><p class="p3"><span class="t1">All things are without a self, when one sees this with wisdom, </span></p><p class="p3"><span class="t3">atha nibbindati dukkhe, esa maggo visuddhiyā. </span><span class="t12">[279]</span></p><p class="p3"><span class="t1">then one grows tired of suffering, this is the path to purity. </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Mahājayamaṅgalagāthā</span></p><h3>The Verses on the Great Blessings of Success </h3><p class="p3"><span class="t3">Mahākāruṇiko nātho, hitāya sabbapāṇinaṁ,</span></p><p class="p3"><span class="t1">The lord of great compassion, for the benefit of all living creatures, </span></p><p class="p3"><span class="t3">pūretvā pāramī sabbā patto Sambodhim-uttamaṁ.</span></p><p class="p3"><span class="t1">having fulfilled all the perfections has attained supreme and Complete Awakening. </span></p><p class="p3"><span class="t3">Etena saccavajjena hotu te jayamaṅgalaṁ! </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t3">Jayanto Bodhiyā mūle Sakyānaṁ nandivaddhano,</span></p><p class="p3"><span class="t1">Succeeding at the root of the Bodhi tree he furthered the Sakyans’ joy, </span></p><p class="p3"><span class="t3">evaṁ tuyhaṁ jayo hotu, jayassu jayamaṅgalaṁ! </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">so may you be successful, may you succeed with the blessing of success! </span></p><p class="p3"><span class="t3">Sakkatvā Buddharatanaṁ, osadhaṁ uttamaṁ varaṁ,</span></p><p class="p3"><span class="t1">Having honoured the Buddha treasure, the best and supreme medicine, </span></p><p class="p3"><span class="t3">hitaṁ devamanussānaṁ, Buddhatejena sotthinā</span></p><p class="p3"><span class="t1">of benefit to gods and men, by the blessed power of the Buddha </span></p><p class="p3"><span class="t3">nassantupaddavā sabbe, dukkhā vūpasamentu te! </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">may all adversities perish, and all suffering come to an end for you! </span></p><p class="p3"><span class="t3">Sakkatvā Dhammaratanaṁ, osadhaṁ uttamaṁ varaṁ,</span></p><p class="p3"><span class="t1">Having honoured the Dhamma treasure, the best and supreme medicine, </span></p><p class="p3"><span class="t3">pariḷāhūpasamanaṁ, Dhammatejena sotthinā</span></p>', '-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 21, '<p class="p3"><span class="t1">which brings fevers to an end, by the blessed power of the Dhamma </span></p><p class="p3"><span class="t3">nassantupaddavā sabbe, bhayā vūpasamentu te! </span><span class="t12">[4]</span></p><p class="p3"><span class="t1">may all adversities perish, and all fear come to an end for you! </span></p><p class="p3"><span class="t3">Sakkatvā Saṅgharatanaṁ, osadhaṁ uttamaṁ varaṁ,</span></p><p class="p3"><span class="t1">Having honoured the Saṅgha treasure, the best and supreme medicine, </span></p><p class="p3"><span class="t3">āhuneyyaṁ pāhuneyyaṁ, Saṅghatejena sotthinā</span></p><p class="p3"><span class="t1">worthy of gifts and hospitality, by the blessed power of the Saṅgha </span></p><p class="p3"><span class="t3">nassantupaddavā sabbe, rogā vūpasamentu te! </span><span class="t12">[5]</span></p><p class="p3"><span class="t1">may all adversities perish, and all disease come to an end for you! </span></p><p class="p3"><span class="t3">Yaṁ kiñci ratanaṁ loke vijjati vividhā puthū</span></p><p class="p3"><span class="t1">Whatever the many and various treasures there are in the world </span></p><p class="p3"><span class="t3">ratanaṁ Buddhasamaṁ natthi: tasmā sotthī bhavantu te! </span><span class="t12">[6]</span></p><p class="p3"><span class="t1">no treasure is equal to the Buddha: through this (truth) may you be safe! </span></p><p class="p3"><span class="t3">Yaṁ kiñci ratanaṁ loke vijjati vividhā puthū</span></p><p class="p3"><span class="t1">Whatever the many and various treasures there are in the world </span></p><p class="p3"><span class="t3">ratanaṁ Dhammasamaṁ natthi: tasmā sotthī bhavantu te! </span><span class="t12">[7]</span></p><p class="p3"><span class="t1">no treasure is equal to the Dhamma: through this (truth) may you be safe! </span></p><p class="p3"><span class="t3">Yaṁ kiñci ratanaṁ loke vijjati vividhā puthū</span></p><p class="p3"><span class="t1">Whatever the many and various treasures there are in the world </span></p><p class="p3"><span class="t3">ratanaṁ Saṅghasamaṁ natthi: tasmā sotthī bhavantu te! </span><span class="t12">[8]</span></p><p class="p3"><span class="t1">no treasure is equal to the Saṅgha: through this (truth) may you be safe! </span></p><p class="p3"><span class="t3">Natthi me saraṇaṁ aññaṁ, Buddho me saraṇaṁ varaṁ!</span></p><p class="p3"><span class="t1">For me there is no other refuge, for me the Buddha is the best refuge! </span></p><p class="p3"><span class="t3">Etena saccavajjena hotu te jayamaṅgalaṁ! </span><span class="t12">[9]</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t3">Natthi me saraṇaṁ aññaṁ, Dhammo me saraṇaṁ varaṁ!</span></p><p class="p3"><span class="t1">For me there is no other refuge, for me the Dhamma is the best refuge! </span></p><p class="p3"><span class="t3">Etena saccavajjena hotu te jayamaṅgalaṁ! </span><span class="t12">[10]</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t3">Natthi me saraṇaṁ aññaṁ, Saṅgho me saraṇaṁ varaṁ!</span></p><p class="p3"><span class="t1">For me there is no other refuge, for me the Saṅgha is the best refuge! </span></p><p class="p3"><span class="t3">Etena saccavajjena hotu te jayamaṅgalaṁ! </span><span class="t12">[11]</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t3">Sabbītiyo vivajjantu sabbarogo vinassatu,</span></p><p class="p3"><span class="t1">May you avoid all calamities and may all disease be destroyed, </span></p><p class="p3"><span class="t3">mā te bhavatvantarāyo, sukhī dīghāyuko bhava!</span></p>', '-21-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Friday ', 'title', 22);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Perception of the Unattractive ', 'title', 22);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Protection of Mind and Body ', 'title', 22);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 22, '<p class="p3"><span class="t1">may there be no obstacles for you, may you be happy and live long! </span></p><p class="p3"><span class="t11">Now go to Conclusion part 2:  </span><span class="t11">Bhavatu sabbamaṅgalaṁ. p. 45</span></p><p class="p3"><span class="t1"> </span></p><h2>Friday </h2><p class="p3"><span class="t3">Asubhasaññā</span></p><h3>Perception of the Unattractive </h3><p class="p3"><span class="t11">(Ārakkhā Bhāvanā 3)</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t3">Aviññāṇasubhanibhaṁ saviññāṇasubhaṁ imaṁ,</span></p><p class="p3"><span class="t1">With consciousness it is unattractive, just as it is without (i.e. dead), </span></p><p class="p3"><span class="t3">kāyaṁ asubhato passaṁ, asubhaṁ bhāvaye yati. </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">seeing this body as unattractive, a monk should develop (the recollection) of the unattractive. </span></p><p class="p3"><span class="t3">Vaṇṇasaṇṭhānagandhehi āsayokāsato tathā,</span></p><p class="p3"><span class="t1">Thus by way of colour, shape, smell, location, and appearance, </span></p><p class="p3"><span class="t3">paṭikkūlāni kāye me kuṇapāni dvisoḷasa. </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">the thirty-two (parts) in my body (are like) repulsive corpses. </span></p><p class="p3"><span class="t3">Patitamhā pi kuṇapā, jegucchaṁ kāyanissitaṁ,</span></p><p class="p3"><span class="t1">The (parts) that depend on this body are contemptible, as is what falls from a corpse, </span></p><p class="p3"><span class="t3">ādhāro hi ‘suci tassa, kāye tu kuṇape ṭhitaṁ. </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">for their receptacle is impure, they are situated in this corpse of a body. </span></p><p class="p3"><span class="t3">Mīlhe kimi va kāyoyaṁ asucimhi samuṭṭhito,</span></p><p class="p3"><span class="t1">Like a worm in excrement this body arose in what is impure (i.e. the womb), </span></p><p class="p3"><span class="t3">anto asucisampuṇṇo puṇṇavaccakuṭī viya. </span><span class="t12">[4]</span></p><p class="p3"><span class="t1">inside it is full of impurities just like a full lavatory. </span></p><p class="p3"><span class="t3">Asucisandate niccaṁ yathā medakathālikā,</span></p><p class="p3"><span class="t1">Always the impurities overflow like fat (overflows) from a frying pan, </span></p><p class="p3"><span class="t3">nānākimikulāvāso, pakkhacandanikā viya. </span><span class="t12">[5]</span></p><p class="p3"><span class="t1">various kinds of worms dwell in it, the same as in a cesspool. </span></p><p class="p3"><span class="t3">Gaṇḍabhūto, rogabhūto, vaṇabhūto, samussayo,</span></p><p class="p3"><span class="t1">This bodily heap is like a boil, like a disease, or like a sore, </span></p><p class="p3"><span class="t3">atekiccho ti jeguccho pabhinnakuṇapūpamo ti. </span><span class="t12">[6]</span></p><p class="p3"><span class="t1">it is incurable, contemptible, just like a rotting corpse. </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Khandhaparittaṁ (part)</span></p><h3>The Protection of Mind and Body </h3><p class="p3"><span class="t3">“Virūpakkhehi me mettaṁ, mettaṁ Erāpathehi me,</span></p>', '-22-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Conditional Arising ', 'title', 23);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 23, '<p class="p3"><span class="t1">“I am friendly with the Virūpakkhas, with the Erāpathas I am friendly, </span></p><p class="p3"><span class="t3">Chabyāputtehi me mettaṁ, mettaṁ Kaṇhāgotamakehi ca. </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">I am friendly with the Chabyāputtas, and friendly with the Kaṇhāgotamakas. </span></p><p class="p3"><span class="t3">Apādakehi me mettaṁ, mettaṁ dipādakehi me,</span></p><p class="p3"><span class="t1">I am friendly with those without feet, with those with two feet I am friendly, </span></p><p class="p3"><span class="t3">catuppadehi me mettaṁ, mettaṁ bahuppadehi me. </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">I am friendly with those with four feet, with those with many feet I am friendly. </span></p><p class="p3"><span class="t3">Mā maṁ apādako hiṁsi, mā maṁ hiṁsi dipādako,</span></p><p class="p3"><span class="t1">May the one without feet not hurt me, may the one with two feet not hurt me, </span></p><p class="p3"><span class="t3">mā maṁ catuppado hiṁsi, mā maṁ hiṁsi bahuppado. </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">may the one with four feet not hurt me, may the one with many feet not hurt me. </span></p><p class="p3"><span class="t3">Sabbe sattā, sabbe pāṇā, sabbe bhūtā ca kevalā,</span></p><p class="p3"><span class="t1">May all beings, all living creatures, all who are born, in their entirety, </span></p><p class="p3"><span class="t3">sabbe bhadrāni passantu, mā kañci pāpam-āgamā. </span><span class="t12">[4]</span></p><p class="p3"><span class="t1">may all see prosperity, may nothing bad come to anyone. </span></p><p class="p3"><span class="t5">Appamāṇo Buddho! Appamāṇo Dhammo! Appamāṇo Saṅgho!</span></p><p class="p3"><span class="t1">The Buddha is measureless! The Dhamma is measureless! The Saṅgha is measureless! </span></p><p class="p3"><span class="t5">Pamāṇavantāni siriṁsapāni, ahivicchikā, </span></p><p class="p3"><span class="t1">Measurable are creeping things, (such as) snakes, scorpions, </span></p><p class="p3"><span class="t5">satapadī, uṇṇānābhi, sarabhū, mūsikā.</span></p><p class="p3"><span class="t1">centipedes, spiders, lizards, and rats. </span></p><p class="p3"><span class="t5">Katā me rakkhā, katā me parittā, paṭikkamantu bhūtāni!</span></p><p class="p3"><span class="t1">I have made this protection, I have made this safeguard, may (all these) beings go away! </span></p><p class="p3"><span class="t5">Sohaṁ namo Bhagavato, namo sattannaṁ Sammāsambuddhānan”-ti.</span></p><p class="p3"><span class="t1">I revere the Fortunate One, I revere the seven Perfect Sambuddhas.” </span></p><p class="p3"><span class="t5">Āsīvāda</span></p><p class="p3"><span class="t5">Blessing</span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t5">Etena saccavajjena sabbarogo vinassatu!</span></p><p class="p3"><span class="t1">By this declaration of the truth may all disease be destroyed! </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Paṭiccasamuppādaṁ</span></p><h3>Conditional Arising </h3>', '-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 24, '<p class="p3"><span class="t5">Avijjāpaccayā saṅkhārā, </span></p><p class="p3"><span class="t1">With ignorance as condition there are (volitional) processes, </span></p><p class="p3"><span class="t5">saṅkhārapaccayā viññāṇaṁ,</span></p><p class="p3"><span class="t1">with (volitional) processes as condition: consciousness, </span></p><p class="p3"><span class="t5">viññāṇapaccayā nāmarūpaṁ, </span></p><p class="p3"><span class="t1">with consciousness as condition: mind and body, </span></p><p class="p3"><span class="t5">nāmarūpapaccayā saḷāyatanaṁ,</span></p><p class="p3"><span class="t1">with mind as condition and body: the six sense spheres, </span></p><p class="p3"><span class="t5">saḷāyatanapaccayā phasso, </span></p><p class="p3"><span class="t1">with the six sense spheres as condition: contact, </span></p><p class="p3"><span class="t5">phassapaccayā vedanā,</span></p><p class="p3"><span class="t1">with contact as condition: feeling, </span></p><p class="p3"><span class="t5">vedanāpaccayā taṇhā, </span></p><p class="p3"><span class="t1">with feeling as condition: craving, </span></p><p class="p3"><span class="t5">taṇhāpaccayā upādānaṁ,</span></p><p class="p3"><span class="t1">with craving as condition: attachment, </span></p><p class="p3"><span class="t5">upādānapaccayā bhavo, </span></p><p class="p3"><span class="t1">with attachment as condition: continuation, </span></p><p class="p3"><span class="t5">bhavapaccayā jāti,</span></p><p class="p3"><span class="t1">with continuation as condition: birth, </span></p><p class="p3"><span class="t5">jātipaccayā jarāmaraṇaṁ, </span></p><p class="p3"><span class="t1">with birth as condition: old age, death, </span></p><p class="p3"><span class="t5">sokaparidevadukkhadomanassupāyāsā sambhavanti,</span></p><p class="p3"><span class="t1">grief, lamentation, pain, sorrow, and despair (all) originate, </span></p><p class="p3"><span class="t5">evam-etassa kevalassa dukkhakkhandhassa samudayo hoti.</span></p><p class="p3"><span class="t1">and so this whole mass of suffering comes to arise. </span></p><p class="p3"><span class="t5">Avijjāya tveva asesavirāganirodhā saṅkhāranirodho,</span></p><p class="p3"><span class="t1">But with the complete fading away and cessation of ignorance, there is the cessation of (volitional) processes, </span></p><p class="p3"><span class="t5">saṅkhāranirodhā viññāṇanirodho,</span></p><p class="p3"><span class="t1">with the cessation of (volitional) processes, the cessation of consciousness, </span></p><p class="p3"><span class="t5">viññāṇanirodhā nāmarūpanirodho,</span></p><p class="p3"><span class="t1">with the cessation of consciousness, the cessation of mind and body, </span></p><p class="p3"><span class="t5">nāmarūpanirodhā saḷāyatananirodho,</span></p><p class="p3"><span class="t1">with the cessation of mind and body, the cessation of the six sense spheres, </span></p><p class="p3"><span class="t5">saḷāyatananirodhā phassanirodho,</span></p><p class="p3"><span class="t1">with the cessation of the six sense spheres, the cessation of contact, </span></p>', '-24-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The First Words of the Buddha ', 'title', 25);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Saturday  ', 'title', 25);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Recollection of Death ', 'title', 25);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 25, '<p class="p3"><span class="t5">phassanirodhā vedanānirodho,</span></p><p class="p3"><span class="t1">with the cessation of contact, the cessation of feeling, </span></p><p class="p3"><span class="t5">vedanānirodhā taṇhānirodho,</span></p><p class="p3"><span class="t1">with the cessation of feeling, the cessation of craving, </span></p><p class="p3"><span class="t5">taṇhānirodhā upādānanirodho,</span></p><p class="p3"><span class="t1">with the cessation of craving, the cessation of attachment,  </span></p><p class="p3"><span class="t5">upādānanirodhā bhavanirodho,</span></p><p class="p3"><span class="t1">with the cessation of attachment, the cessation of continuation, </span></p><p class="p3"><span class="t5">bhavanirodhā jātinirodho,</span></p><p class="p3"><span class="t1">with the cessation of continuation, the cessation of birth, </span></p><p class="p3"><span class="t5">jātinirodhā jarāmaraṇaṁ </span></p><p class="p3"><span class="t1">grief, lamentation, pain, sorrow, and despair (all) cease, </span></p><p class="p3"><span class="t5">sokaparidevadukkhadomanassupāyāsā nirujjhanti,</span></p><p class="p3"><span class="t1">with the cessation of birth, old age, death,  </span></p><p class="p3"><span class="t5">evam-etassa kevalassa dukkhakkhandhassa nirodho hoti.</span></p><p class="p3"><span class="t1">and so there is a cessation of this whole mass of suffering. </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Paṭhama-Buddhavacanaṁ</span></p><h3>The First Words of the Buddha </h3><p class="p3"><span class="t3">Anekajātisaṁsāraṁ sandhāvissaṁ anibbisaṁ</span></p><p class="p3"><span class="t1">Through the round of countless births I have wandered without finding </span></p><p class="p3"><span class="t3">gahakārakaṁ gavesanto: dukkhā jāti punappunaṁ.</span></p><p class="p3"><span class="t1">the housebuilder I was seeking: born and suffering again and again. </span></p><p class="p3"><span class="t3">Gahakāraka diṭṭhosi! Puna gehaṁ na kāhasi:</span></p><p class="p3"><span class="t1">O housebuilder, now you are seen! You will not build the house again: </span></p><p class="p3"><span class="t3">sabbā te phāsukā bhaggā, gahakūṭaṁ visaṅkhitaṁ,</span></p><p class="p3"><span class="t1">all your rafters have been broken, and the ridgepole has been destroyed, </span></p><p class="p3"><span class="t3">visaṅkhāragataṁ cittaṁ, taṇhānaṁ khayam-ajjhagā ti.</span></p><p class="p3"><span class="t1">my mind has reached the unconditioned, and craving’s end has been achieved. </span></p><p class="p3"><span class="t11">Meditation... then go to Conclusion part 1:  </span><span class="t11">Sabbapāpassa akaraṇaṁ. p. 44</span></p><p class="p3"><span class="t1"> </span></p><h2>Saturday  </h2><p class="p3"><span class="t3">Maraṇānussati</span></p><h3>Recollection of Death </h3><p class="p3"><span class="t11">(Ārakkhā Bhāvanā 4)</span></p><p class="p3"><span class="t1">  </span></p>', '-25-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Eight Bases for Urgency ', 'title', 26);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 26, '<p class="p3"><span class="t3">Pavātadīpatulyāya, sāyusantatiyā khayaṁ,</span></p><p class="p3"><span class="t1">Comparing a lamp in a breeze, and the destruction of the life continuum, </span></p><p class="p3"><span class="t3">parūpamāya sampassaṁ bhāvaye maraṇassatiṁ. </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">seeing oneself as similar to others one should develop mindfulness of death. </span></p><p class="p3"><span class="t3">Mahāsampattisampattā yathā sattā matā idha,</span></p><p class="p3"><span class="t1">Just as people who have attained great good fortune here have died, </span></p><p class="p3"><span class="t3">tathā ahaṁ marissāmi maraṇaṁ mama hessati. </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">in the same way will I die death is (surely) coming to me. </span></p><p class="p3"><span class="t3">Uppattiyā sahevedaṁ maraṇaṁ āgataṁ sadā,</span></p><p class="p3"><span class="t1">Indeed death always comes along with that which has arisen, </span></p><p class="p3"><span class="t3">maraṇatthāya okāsaṁ vadhako viya esati. </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">it is like a murderer who is seeking an opportunity to kill. </span></p><p class="p3"><span class="t3">Īsakaṁ anivattaṁ taṁ satataṁ gamanussukaṁ,</span></p><p class="p3"><span class="t1">° This life is slowly, without turning back, continually, eagerly, </span></p><p class="p3"><span class="t3">jīvitaṁ udayā atthaṁ suriyo viya dhāvati. </span><span class="t12">[4]</span></p><p class="p3"><span class="t1">going its way, it rises and falls just as the sun runs its course (and sets). </span></p><p class="p3"><span class="t3">Vijjububbula-ussāva, jalarājī parikkhayaṁ,</span></p><p class="p3"><span class="t1">(Like) lightning, a bubble, dew, a line drawn on water, life (quickly goes to) destruction, </span></p><p class="p3"><span class="t3">ghātako varipūtassa sabbatthā pi avāriyo. </span><span class="t12">[5]</span></p><p class="p3"><span class="t1">like an executioner in regard to his rival (death) can never be constrained. </span></p><p class="p3"><span class="t3">Suyasatthāmapuññiddhī buddhivuddhī Jinadvayaṁ,</span></p><p class="p3"><span class="t1">Even the two kinds of Victors, who are famous, strong, meritorious, powerful, and of great intelligence, </span></p><p class="p3"><span class="t3">ghātesi maraṇaṁ khippaṁ, kā tu mādisake kathā? </span><span class="t12">[6]</span></p><p class="p3"><span class="t1">were quickly slaughtered by death, so what to say about one like me? </span></p><p class="p3"><span class="t3">Paccayānañ-ca vekalyā bāhirajjhattupaddavā,</span></p><p class="p3"><span class="t1">When conditions fail there are internal and external adversities, </span></p><p class="p3"><span class="t3">marāmoraṁ nimesā pi maramāno anukkhaṇan-ti. </span><span class="t12">[7]</span></p><p class="p3"><span class="t1">dying at each and every moment I will die in less (time) than a blink of an eye. </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Aṭṭhasaṁvegavatthūni</span></p><h3>The Eight Bases for Urgency </h3><p class="p3"><span class="t3">Bhāvetvā caturārakkhā āvajjeyya anantaraṁ</span></p><p class="p3"><span class="t1">Having developed these four protections one should consider next </span></p><p class="p3"><span class="t3">mahāsaṁvegavatthūni, aṭṭha aṭṭhitavīriyo. </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">the eight great bases for urgency, having non-stop energy. </span></p><p class="p3"><span class="t3">Jātijarāvyādhicutī apāyā, atīta-appattakavaṭṭadukkhaṁ,</span></p>', '-26-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Discourse on the Advantages of Friendliness Meditation ', 'title', 27);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 27, '<p class="p3"><span class="t1">Birth, old age, sickness, death, the lower realms, the past and future suffering in the round, </span></p><p class="p3"><span class="t3">idāni āhāragaveṭṭhidukkhaṁ saṁvegavatthūni imāni aṭṭha. </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">the suffering in having to seek food in the present, these are the eight bases for urgency. </span></p><p class="p3"><span class="t3">Pāto ca sāyam-api ceva imaṁ vidhiñño,</span></p><p class="p3"><span class="t1">If, in the morning and in the evening, one who knows the way, </span></p><p class="p3"><span class="t3">āsevate satatam-attahitābhilāsi,</span></p><p class="p3"><span class="t1">who desires his own benefit, practises (these meditations) continually, </span></p><p class="p3"><span class="t3">pappoti so ti vipulaṁ hatapāripantho,</span></p><p class="p3"><span class="t1">° then after destroying (even) extensive obstacles, that sage </span></p><p class="p3"><span class="t3">seṭṭhaṁ sukhaṁ muni visiṭṭhamataṁ sukhena cā ti. </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">easily attains great happiness, and the distinction of the deathless. </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Mettānisaṁsasuttaṁ</span></p><h3>The Discourse on the Advantages of Friendliness Meditation </h3><p class="p3"><span class="t5">Evaṁ me sutaṁ:</span></p><p class="p3"><span class="t1">Thus I have heard: </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Sāvatthiyaṁ viharati</span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling near Sāvatthī </span></p><p class="p3"><span class="t5">Jetavane Anāthapiṇḍikassa ārāme.</span></p><p class="p3"><span class="t1">at Anāthapiṇḍika’s grounds in Jeta’s Wood. </span></p><p class="p3"><span class="t5">Tatra kho Bhagavā bhikkhū āmantesi:</span></p><p class="p3"><span class="t1">There it was that the Fortunate One addressed the monks, saying: </span></p><p class="p3"><span class="t5">“Bhikkhavo!” ti, “Bhadante!” ti te bhikkhū Bhagavato paccassosuṁ,</span></p><p class="p3"><span class="t1">“Monks!”, “Reverend Sir!” those monks replied to the Fortunate One, </span></p><p class="p3"><span class="t5">Bhagavā etad-avoca:</span></p><p class="p3"><span class="t1">and the Fortunate One said this: </span></p><p class="p3"><span class="t5">“Mettāya bhikkhave cetovimuttiyā,</span></p><p class="p3"><span class="t1">“Monks, from the freedom of mind by friendliness meditation, </span></p><p class="p3"><span class="t5">āsevitāya, bhāvitāya, bahulīkatāya, </span></p><p class="p3"><span class="t1">when practised, developed, made much of, </span></p><p class="p3"><span class="t5">yānīkatāya, vatthukatāya, anuṭṭhitāya, paricitāya, susamāraddhāya,</span></p><p class="p3"><span class="t1">carried on, established, maintained, augmented, and properly instigated, </span></p><p class="p3"><span class="t5">ekādasānisaṁsā pāṭikaṅkhā. Katame ekādasa?</span></p><p class="p3"><span class="t1">eleven advantages are to be expected. What are the eleven? </span></p><p class="p3"><span class="t5">Sukhaṁ supati, sukhaṁ paṭibujjhati, na pāpakaṁ supinaṁ passati,</span></p><p class="p3"><span class="t1">He sleeps happily, he wakes up happily, he does not see bad dreams, </span></p>', '-27-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Sunday ', 'title', 28);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Safeguard through the Top of a Banner ', 'title', 28);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 28, '<p class="p3"><span class="t5">Manussānaṁ piyo hoti, amanussānaṁ piyo hoti,</span></p><p class="p3"><span class="t1">He is dear to human beings, he is dear to non-human beings, </span></p><p class="p3"><span class="t5">Devatā rakkhanti, nāssa aggi vā visaṁ vā satthaṁ vā kamati,</span></p><p class="p3"><span class="t1">The gods protect him, neither fire, poison, or sword can affect him, </span></p><p class="p3"><span class="t5">Tuvataṁ cittaṁ samādhiyati, mukhavaṇṇo vippasīdati,</span></p><p class="p3"><span class="t1">He can concentrate his mind quickly, his complexion becomes clear, </span></p><p class="p3"><span class="t5">asammūḷho kālaṁ karoti, </span></p><p class="p3"><span class="t1">he dies unbewildered,  </span></p><p class="p3"><span class="t5">uttariṁ appaṭivijjhanto Brahmalokūpago hoti.</span></p><p class="p3"><span class="t1">and if he penetrates no further, he will (at least) go to the Brahma world. </span></p><p class="p3"><span class="t5">Mettāya bhikkhave cetovimuttiyā,</span></p><p class="p3"><span class="t1">Monks, from the freedom of mind by friendliness meditation, </span></p><p class="p3"><span class="t5">āsevitāya, bhāvitāya, bahulīkatāya, </span></p><p class="p3"><span class="t1">when practised, developed, made much of, </span></p><p class="p3"><span class="t5">yānīkatāya, vatthukatāya, anuṭṭhitāya, paricitāya, susamāraddhāya,</span></p><p class="p3"><span class="t1">carried on, established, maintained, augmented, and properly instigated, </span></p><p class="p3"><span class="t5">ime ekādasānisaṁsā pāṭikaṅkhā” ti.</span></p><p class="p3"><span class="t1">there are these eleven advantages to be expected.” </span></p><p class="p3"><span class="t5">Idam-avoca Bhagavā, </span></p><p class="p3"><span class="t1">The Fortunate One said this, </span></p><p class="p3"><span class="t5">attamanā te bhikkhū Bhagavato bhāsitaṁ abhinandun-ti.</span></p><p class="p3"><span class="t1">and those monks were uplifted and greatly rejoiced in the Fortunate One’s words. </span></p><p class="p3"><span class="t5">Āsīvāda</span></p><p class="p3"><span class="t5">Blessing</span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t5">Etena saccavajjena sabbarogo vinassatu!</span></p><p class="p3"><span class="t1">By this declaration of the truth may all disease be destroyed! </span></p><p class="p3"><span class="t11">Meditation... then go to Conclusion part 1:  </span><span class="t11">Sabbapāpassa akaraṇaṁ. p. 44</span></p><p class="p3"><span class="t1"> </span></p><h2>Sunday </h2><p class="p3"><span class="t3">Dhajaggaparittaṁ</span></p><h3>Safeguard through the Top of a Banner </h3><p class="p3"><span class="t5">Evaṁ me sutaṁ:</span></p>', '-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 29, '<p class="p3"><span class="t1">Thus I have heard: </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Sāvatthiyaṁ viharati</span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling near Sāvatthī </span></p><p class="p3"><span class="t5">Jetavane Anāthapiṇḍikassa ārāme.</span></p><p class="p3"><span class="t1">at Anāthapiṇḍika’s grounds in Jeta’s Wood. </span></p><p class="p3"><span class="t5">Tatra kho Bhagavā bhikkhū āmantesi:</span></p><p class="p3"><span class="t1">There it was that the Fortunate One addressed the monks, saying: </span></p><p class="p3"><span class="t5">“Bhikkhavo!” ti, “Bhadante!” ti te bhikkhū Bhagavato paccassosuṁ,</span></p><p class="p3"><span class="t1">“Monks!”, “Reverend Sir!” those monks replied to the Fortunate One, </span></p><p class="p3"><span class="t5">Bhagavā etad-avoca:</span></p><p class="p3"><span class="t1">and the Fortunate One said this: </span></p><p class="p3"><span class="t5">“Bhūtapubbaṁ bhikkhave devāsurasaṅgāmo samūpabbūḷho ahosi.</span></p><p class="p3"><span class="t1">“Formerly, monks, there was a massed battle between the gods and the asuras. </span></p><p class="p3"><span class="t5">Atha kho bhikkhave Sakko devānamindo deve Tāvatiṁse āmantesi: </span></p><p class="p3"><span class="t1">Then, monks, the lord of the gods Sakka addressed the Tāvatiṁsa gods, saying: </span></p><p class="p3"><span class="t5">“Sace mārisā devānaṁ saṅgāmagatānaṁ</span></p><p class="p3"><span class="t1">“If, dear Sirs, to the gods who have gone into battle </span></p><p class="p3"><span class="t5">uppajjeyya bhayaṁ vā chambhitattaṁ vā lomahaṁso vā,</span></p><p class="p3"><span class="t1">there should arise fear, or terror, or horror, </span></p><p class="p3"><span class="t5">mameva tasmiṁ samaye dhajaggaṁ ullokeyyātha. </span></p><p class="p3"><span class="t1">at that time you could look for the top of my banner. </span></p><p class="p3"><span class="t5">Mamaṁ hi vo dhajaggaṁ ullokayataṁ </span></p><p class="p3"><span class="t1">For to those who look to the top of my banner </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā, lomahaṁso vā so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t5">No ce me dhajaggaṁ ullokeyyātha, </span></p><p class="p3"><span class="t1">If you cannot look to the top of my banner, </span></p><p class="p3"><span class="t5">atha Pajāpatissa devarājassa dhajaggaṁ ullokeyyātha. </span></p><p class="p3"><span class="t1">then you could look for the top of the god-king Pajāpati’s banner. </span></p><p class="p3"><span class="t5">Pajāpatissa hi vo devarājassa dhajaggaṁ ullokayataṁ </span></p><p class="p3"><span class="t1">For to those who look to the top of the god-king Pajāpati’s banner </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā, lomahaṁso vā so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t5">No ce Pajāpatissa devarājassa dhajaggaṁ ullokeyyātha, </span></p><p class="p3"><span class="t1">If you cannot look to the top of the god-king Pajāpati’s banner, </span></p><p class="p3"><span class="t5">atha Varuṇassa devarājassa dhajaggaṁ ullokeyyātha. </span></p>', '-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 30, '<p class="p3"><span class="t1">then you could look for the top of the god-king Varuṇa’s banner. </span></p><p class="p3"><span class="t5">Varuṇassa hi vo devarājassa dhajaggaṁ ullokayataṁ </span></p><p class="p3"><span class="t1">For to those who look to the top of the god-king Varuṇa’s banner </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā, lomahaṁso vā, so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t5">No ce Varuṇassa devarājassa dhajaggaṁ ullokeyyātha, </span></p><p class="p3"><span class="t1">If you cannot look to the top of the god-king Varuṇa’s banner, </span></p><p class="p3"><span class="t5">atha Īsānassa devarājassa dhajaggaṁ ullokeyyātha. </span></p><p class="p3"><span class="t1">then you could look for the top of the god-king Īsāna’s banner. </span></p><p class="p3"><span class="t5">Īsānassa hi vo devarājassa dhajaggaṁ ullokayataṁ </span></p><p class="p3"><span class="t1">For to those who look to the top of the god-king Īsāna’s banner </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā, lomahaṁso vā so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t21">Taṁ kho pana bhikkhave Sakkassa vā devānamindassa dhajaggaṁ ullokayataṁ,</span></p><p class="p3"><span class="t1">But, monks, to he who looks to the top of the lord of the gods Sakka’s banner, </span></p><p class="p3"><span class="t5">Pajāpatissa vā devarājassa dhajaggaṁ ullokayataṁ, </span></p><p class="p3"><span class="t1">or who looks to the top of the god-king Pajāpati’s banner, </span></p><p class="p3"><span class="t5">Varuṇassa vā devarājassa dhajaggaṁ ullokayataṁ, </span></p><p class="p3"><span class="t1">or who looks to the top of the god-king Varuṇa’s banner, </span></p><p class="p3"><span class="t5">Īsānassa vā devarājassa dhajaggaṁ ullokayataṁ, </span></p><p class="p3"><span class="t1">or who looks to the top of the god-king Īsāna’s banner, </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā lomahaṁso vā, </span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror,  </span></p><p class="p3"><span class="t5">so pahīyethāpi no pi pahīyetha. </span></p><p class="p3"><span class="t1">may be given up, and may not be given up. </span></p><p class="p3"><span class="t5">Taṁ kissa hetu? </span></p><p class="p3"><span class="t1">What is the reason for that? </span></p><p class="p3"><span class="t5">Sakko bhikkhave devānamindo </span></p><p class="p3"><span class="t1">The lord of the gods Sakka, monks,  </span></p><p class="p3"><span class="t5">avītarāgo avītadoso avītamoho, bhīrucchambhi utrāsi palāyi.</span></p><p class="p3"><span class="t1">being fearful, terrified, scared, he ran away. is not free from passion, is not free from hatred, is not free from delusion, </span></p><p class="p3"><span class="t5">Ahañ-ca kho bhikkhave evaṁ vadāmi: </span></p><p class="p3"><span class="t1">But I say this, monks: </span></p><p class="p3"><span class="t5">Sace tumhākaṁ bhikkhave araññagatānaṁ vā,</span></p><p class="p3"><span class="t1">If to you, monks, after going to the wilderness, </span></p><p class="p3"><span class="t5">rukkhamūlagatānaṁ vā, suññāgāragatānaṁ vā,</span></p>', '-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 31, '<p class="p3"><span class="t1">or to the root of a tree, or to an empty place, </span></p><p class="p3"><span class="t5">uppajjeyya bhayaṁ vā chambhitattaṁ vā lomahaṁso vā, </span></p><p class="p3"><span class="t1">there should arise fear, or terror, or horror, </span></p><p class="p3"><span class="t5">mameva tasmiṁ samaye anussareyyātha:</span></p><p class="p3"><span class="t1">at that time you could recollect me thus: </span></p><p class="p3"><span class="t5">“Iti pi so Bhagavā Arahaṁ Sammāsambuddho,</span></p><p class="p3"><span class="t1">“Such is he, the Fortunate One, the Worthy One, the Perfect Sambuddha, </span></p><p class="p3"><span class="t5">vijjācaraṇasampanno Sugato lokavidū,</span></p><p class="p3"><span class="t1">the one endowed with understanding and good conduct, the Happy One, the One who understands the worlds, </span></p><p class="p3"><span class="t5">anuttaro purisadammasārathī, </span></p><p class="p3"><span class="t1">the unsurpassed guide for those people who need taming, </span></p><p class="p3"><span class="t5">Satthā devamanussānaṁ Buddho Bhagavā” ti.</span></p><p class="p3"><span class="t1">the Teacher of gods and men, the Buddha, the Fortunate One.” </span></p><p class="p3"><span class="t5">Mamaṁ hi vo bhikkhave anussarataṁ </span></p><p class="p3"><span class="t1">For, monks, to those who recollect me  </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā lomahaṁso vā so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be, or terror, or horror, will be given up. </span></p><p class="p3"><span class="t5">No ce maṁ anussareyyātha, atha Dhammaṁ anussareyyātha:</span></p><p class="p3"><span class="t1">If you cannot recollect me, then recollect the Dhamma thus: </span></p><p class="p3"><span class="t5">“Svākkhāto Bhagavatā Dhammo, sandiṭṭhiko, akāliko,</span></p><p class="p3"><span class="t1">“The Dhamma has been well-proclaimed by the Fortunate One, it is visible, not subject to time, </span></p><p class="p3"><span class="t5">ehipassiko, opanayiko, paccattaṁ veditabbo viññūhī” ti.</span></p><p class="p3"><span class="t1">inviting inspection, onward leading, and can be understood by the wise for themselves.” </span></p><p class="p3"><span class="t5">Dhammaṁ hi vo bhikkhave anussarataṁ </span></p><p class="p3"><span class="t1">For, monks, to those who recollect the Dhamma  </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā lomahaṁso vā so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be or terror, or horror, will be given up. </span></p><p class="p3"><span class="t5">No ce Dhammaṁ anussareyyātha, atha Saṅghaṁ anussareyyātha:</span></p><p class="p3"><span class="t1">If you cannot recollect the Dhamma, then recollect the Saṅgha thus: </span></p><p class="p3"><span class="t5">“Supaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">“The Fortunate One’s Saṅgha of disciples are good in their practice, </span></p><p class="p3"><span class="t5">ujupaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">the Fortunate One’s Saṅgha of disciples are upright in their practice, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">ñāyapaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">the Fortunate One’s Saṅgha of disciples are systematic in their practice, </span></p>', '-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 32, '<p class="p3"><span class="t5">sāmīcipaṭipanno Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">the Fortunate One’s Saṅgha of disciples are correct in their practice, </span></p><p class="p3"><span class="t5">yad-idaṁ cattāri purisayugāni aṭṭha purisapuggalā,</span></p><p class="p3"><span class="t1">that is to say, the four pairs of persons, the eight individual persons, </span></p><p class="p3"><span class="t5">esa Bhagavato sāvakasaṅgho,</span></p><p class="p3"><span class="t1">this is the Fortunate One’s Saṅgha of disciples, </span></p><p class="p3"><span class="t5">āhuneyyo, pāhuneyyo, dakkhiṇeyyo, añjalikaranīyo,</span></p><p class="p3"><span class="t1">they are worthy of offerings, of hospitality, of gifts, and of reverential salutation, </span></p><p class="p3"><span class="t5">anuttaraṁ puññakkhettaṁ lokassā” ti.</span></p><p class="p3"><span class="t1">they are an unsurpassed field of merit for the world.” </span></p><p class="p3"><span class="t5">Saṅghaṁ hi vo bhikkhave anussarataṁ </span></p><p class="p3"><span class="t1">For, monks, to those who recollect the Saṅgha </span></p><p class="p3"><span class="t5">yaṁ bhavissati bhayaṁ vā chambhitattaṁ vā lomahaṁso vā so pahīyissati.</span></p><p class="p3"><span class="t1">whatever fear there may be or terror, or horror, will be given up. </span></p><p class="p3"><span class="t5">Taṁ kissa hetu? </span></p><p class="p3"><span class="t1">What is the reason for that? </span></p><p class="p3"><span class="t5">Tathāgato bhikkhave Arahaṁ Sammāsambuddho,</span></p><p class="p3"><span class="t1">The Realised One, monks, the Worthy One, the Perfect Sambuddha, </span></p><p class="p3"><span class="t5">vītarāgo, vītadoso, vītamoho, abhīru acchambhi anutrāsi apalāyī” ti.</span></p><p class="p3"><span class="t1">is free from passion, is free from hatred, is free from delusion, not being fearful, not terrified, not scared, he did not run away.” </span></p><p class="p3"><span class="t5">Idam-avoca Bhagavā, </span></p><p class="p3"><span class="t1">The Fortunate One said this, </span></p><p class="p3"><span class="t5">idaṁ vatvā Sugato athāparaṁ etad-avoca Satthā:</span></p><p class="p3"><span class="t1">and after saying this, the Happy One, the Teacher, said something more: </span></p><p class="p3"><span class="t3">“Araññe rukkhamūle vā, suññāgāre va bhikkhavo, </span></p><p class="p3"><span class="t1">“In the wilds, at the root of a tree, or in an empty place, monks, </span></p><p class="p3"><span class="t3">anussaretha Sambuddhaṁ, bhayaṁ tumhāka’ no siyā. </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">you must recollect the Sambuddha, and there will be no fear for you. </span></p><p class="p3"><span class="t3">No ce Buddhaṁ sareyyātha, lokajeṭṭhaṁ narāsabhaṁ, </span></p><p class="p3"><span class="t1">If you can’t remember the Buddha, the world’s elder, the man-bull, </span></p><p class="p3"><span class="t3">atha Dhammaṁ sareyyātha, niyyānikaṁ sudesitaṁ. </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">then you must remember the Dhamma, which leads out, which is well preached. </span></p><p class="p3"><span class="t3">No ce Dhammaṁ sareyyātha, niyyānikaṁ sudesitaṁ, </span></p><p class="p3"><span class="t1">If you can’t remember the Dhamma, which leads out, which is well preached, </span></p><p class="p3"><span class="t3">atha Saṅghaṁ sareyyātha, puññakkhettaṁ anuttaraṁ. </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">then you must remember the Saṅgha, which is an unsurpassed field of merit. </span></p>', '-32-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Verses from the Dhammapada ', 'title', 33);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 33, '<p class="p3"><span class="t3">Evaṁ Buddhaṁ sarantānaṁ, Dhammaṁ Saṅghañ-ca bhikkhavo,</span></p><p class="p3"><span class="t1">Thus for those who remember the Buddha, the Dhamma, or the Saṅgha, monks, </span></p><p class="p3"><span class="t3">bhayaṁ vā chambhitattaṁ vā, lomahaṁso na hessatī” ti. </span><span class="t12">[4]</span></p><p class="p3"><span class="t1">(there will be no) fear, or terror, there will be no horror.” </span></p><p class="p3"><span class="t5">Āsīvāda</span></p><p class="p3"><span class="t5">Blessing</span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t5">Etena saccavajjena sabbarogo vinassatu!</span></p><p class="p3"><span class="t1">By this declaration of the truth may all disease be destroyed! </span></p><p class="p3"><span class="t11">Meditation... then...</span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Dhammapadagāthā</span></p><h3>Verses from the Dhammapada </h3><p class="p3"><span class="t3">Sabbapāpassa akaraṇaṁ, kusalassa upasampadā,</span></p><p class="p3"><span class="t1">Not doing any bad deeds, undertaking wholesome deeds, </span></p><p class="p3"><span class="t3">sacittapariyodapanaṁ etaṁ Buddhāna’ sāsanaṁ. </span><span class="t12">[183]</span></p><p class="p3"><span class="t1">and purifying one’s mind this is the teaching of the Buddhas. </span></p><p class="p3"><span class="t3">Khantī paramaṁ tapo titikkhā, Nibbānaṁ paramaṁ vadanti Buddhā.</span></p><p class="p3"><span class="t1">Forbearing patience is the supreme austerity, Nibbāna is supreme say the Buddhas. </span></p><p class="p3"><span class="t3">Na hi pabbajito parūpaghāti, samaṇo hoti paraṁ viheṭhayanto. </span><span class="t12">[184]</span></p><p class="p3"><span class="t1">One gone forth does not hurt another, (nor does) an ascetic harass another. </span></p><p class="p3"><span class="t3">Anūpavādo, anūpaghāto, pātimokkhe ca saṁvaro,</span></p><p class="p3"><span class="t1">Not finding fault, not hurting, restraint in regard to the precepts, </span></p><p class="p3"><span class="t3">mattaññutā ca bhattasmiṁ, pantañ-ca sayanāsanaṁ,</span></p><p class="p3"><span class="t1">knowing the correct measure in food, (living in) a remote dwelling place, </span></p><p class="p3"><span class="t3">adhicitte ca āyogo etaṁ Buddhāna’ sāsanaṁ. </span><span class="t12">[185]</span></p><p class="p3"><span class="t1">being devoted to meditation, this is the teaching of the Buddhas. </span></p><p class="p3"><span class="t3">Sabbe saṅkhārā aniccā ti, yadā paññāya passati,</span></p><p class="p3"><span class="t1">All conditioned things are impermanent, when one sees this with wisdom, </span></p><p class="p3"><span class="t3">atha nibbindati dukkhe, esa maggo visuddhiyā. </span><span class="t12">[277]</span></p><p class="p3"><span class="t1">then one grows tired of suffering, this is the path to purity. </span></p><p class="p3"><span class="t3">Sabbe saṅkhārā dukkhā ti, yadā paññāya passati,</span></p><p class="p3"><span class="t1">All conditioned things are suffering, when one sees this with wisdom, </span></p>', '-33-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Fearless Safeguard ', 'title', 34);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 34, '<p class="p3"><span class="t3">atha nibbindati dukkhe, esa maggo visuddhiyā. </span><span class="t12">[278]</span></p><p class="p3"><span class="t1">then one grows tired of suffering, this is the path to purity. </span></p><p class="p3"><span class="t3">Sabbe dhammā anattā ti, yadā paññāya passati,</span></p><p class="p3"><span class="t1">All things are without a self, when one sees this with wisdom, </span></p><p class="p3"><span class="t3">atha nibbindati dukkhe, esa maggo visuddhiyā. </span><span class="t12">[279]</span></p><p class="p3"><span class="t1">then one grows tired of suffering, this is the path to purity. </span></p><p class="p3"><span class="t3">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t3">sabbabuddhānubhāvena sadā sotthī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be safe forever! </span></p><p class="p3"><span class="t3">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t3">sabbadhammānubhāvena sadā sotthī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be safe forever! </span></p><p class="p3"><span class="t3">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t3">sabbasaṅghānubhāvena sadā sotthī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be safe forever! </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Abhayaparittaṁ</span></p><h3>The Fearless Safeguard </h3><p class="p3"><span class="t3">Yan-dunnimittaṁ avamaṅgalañ-ca, yo cāmanāpo sakunassa saddo,</span></p><p class="p3"><span class="t1">Whatever bad sign, inauspicious event, or whatever ominous bird shriek there is, </span></p><p class="p3"><span class="t3">pāpaggaho dussupinaṁ akantaṁ Buddhānubhāvena vināsamentu!</span></p><p class="p3"><span class="t1">also evil planets, and unpleasant dreams, may they perish through the power of the Buddha! </span></p><p class="p3"><span class="t3">Yan-dunnimittaṁ avamaṅgalañ-ca, yo cāmanāpo sakunassa saddo,</span></p><p class="p3"><span class="t1">Whatever bad sign, inauspicious event, or whatever ominous bird shriek there is, </span></p><p class="p3"><span class="t3">pāpaggaho dussupinaṁ akantaṁ Dhammānubhāvena vināsamentu!</span></p><p class="p3"><span class="t1">also evil planets, and unpleasant dreams, may they perish through the power of the Dhamma! </span></p><p class="p3"><span class="t3">Yan-dunnimittaṁ avamaṅgalañ-ca, yo cāmanāpo sakunassa saddo,</span></p><p class="p3"><span class="t1">Whatever bad sign, inauspicious event, or whatever ominous bird shriek there is, </span></p><p class="p3"><span class="t3">pāpaggaho dussupinaṁ akantaṁ Saṅghānubhāvena vināsamentu!</span></p><p class="p3"><span class="t1">also evil planets, and unpleasant dreams, may they perish through the power of the Sangha! </span></p><p class="p3"><span class="t3">Dukkhappattā ca niddukkhā, bhayappattā ca nibbhayā,</span></p><p class="p3"><span class="t1">° May those who suffer be without suffering, may those who fear be without fear,  </span></p><p class="p3"><span class="t3">sokappattā ca nissokā hontu sabbe pi pāṇino!</span></p>', '-34-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Conclusion ', 'title', 35);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Conclusion Part One ', 'title', 35);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Verses from the Dhammapada ', 'title', 35);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 35, '<p class="p3"><span class="t1">may those who grieve be without grief, may all living creatures be so! </span></p><p class="p3"><span class="t3">Dānaṁ dadantu saddhāya, sīlaṁ rakkhantu sabbadā,</span></p><p class="p3"><span class="t1">You should give gifts with confidence, protect your virtue at all times, </span></p><p class="p3"><span class="t3">bhāvanābhiratā hontu, gacchantu devatāgatā.</span></p><p class="p3"><span class="t1">find delight in meditation, and (after death) go to the gods. </span></p><p class="p3"><span class="t3">Sabbe Buddhā balappattā, Paccekānañ-ca yaṁ balaṁ</span></p><p class="p3"><span class="t1">All the Buddhas’ strength, and whatever strength the Individual Buddhas </span></p><p class="p3"><span class="t3">Arahantānañ-ca tejena rakkhaṁ bandhāma sabbaso!</span></p><p class="p3"><span class="t1">and Worthy Ones have, by that power we bind this protection in every way! </span></p><p class="p3"><span class="t11">Now go to Conclusion part 3:  </span><span class="t11">Ākāsaṭṭhā ca bhummaṭṭhā. p. 45</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"> </span></p><h2>Conclusion </h2><h3>Conclusion Part One </h3><p class="p3"><span class="t3">Dhammapadagāthā</span></p><h3>Verses from the Dhammapada </h3><p class="p3"><span class="t3">Sabbapāpassa akaraṇaṁ, kusalassa upasampadā,</span></p><p class="p3"><span class="t1">Not doing any bad deeds, undertaking wholesome deeds, </span></p><p class="p3"><span class="t3">sacittapariyodapanaṁ etaṁ Buddhāna’ sāsanaṁ. </span><span class="t12">[183]</span></p><p class="p3"><span class="t1">and purifying one’s mind, this is the teaching of the Buddhas. </span></p><p class="p3"><span class="t3">Khantī paramaṁ tapo titikkhā, Nibbānaṁ paramaṁ vadanti Buddhā.</span></p><p class="p3"><span class="t1">Forbearing patience is the supreme austerity, Nibbāna is supreme say the Buddhas. </span></p><p class="p3"><span class="t3">Na hi pabbajito parūpaghāti, samaṇo hoti paraṁ viheṭhayanto. </span><span class="t12">[184]</span></p><p class="p3"><span class="t1">One gone forth does not hurt another, (nor does) an ascetic harass another. </span></p><p class="p3"><span class="t3">Anūpavādo, anūpaghāto, pātimokkhe ca saṁvaro,</span></p><p class="p3"><span class="t1">Not finding fault, not hurting, restraint in regard to the precepts, </span></p><p class="p3"><span class="t3">mattaññutā ca bhattasmiṁ, pantañ-ca sayanāsanaṁ,</span></p><p class="p3"><span class="t1">knowing the correct measure in food, (living in) a remote dwelling place, </span></p><p class="p3"><span class="t3">adhicitte ca āyogo etaṁ Buddhāna’ sāsanaṁ. </span><span class="t12">[185]</span></p><p class="p3"><span class="t1">being devoted to meditation, this is the teaching of the Buddhas. </span></p><p class="p3"><span class="t3">Sabbe saṅkhārā aniccā ti, yadā paññāya passati,</span></p><p class="p3"><span class="t1">All conditioned things are impermanent, when one sees this with wisdom, </span></p><p class="p3"><span class="t3">atha nibbindati dukkhe, esa maggo visuddhiyā. </span><span class="t12">[277]</span></p><p class="p3"><span class="t1">then one grows tired of suffering, this is the path to purity. </span></p><p class="p3"><span class="t3">Sabbe saṅkhārā dukkhā ti, yadā paññāya passati,</span></p><p class="p3"><span class="t1">All conditioned things are suffering, when one sees this with wisdom, </span></p>', '-35-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Conclusion Part Two ', 'title', 36);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Conclusion Part Three ', 'title', 36);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 36, '<p class="p3"><span class="t3">atha nibbindati dukkhe, esa maggo visuddhiyā. </span><span class="t12">[278]</span></p><p class="p3"><span class="t1">then one grows tired of suffering, this is the path to purity. </span></p><p class="p3"><span class="t3">Sabbe dhammā anattā ti, yadā paññāya passati,</span></p><p class="p3"><span class="t1">All things are without a self, when one sees this with wisdom, </span></p><p class="p3"><span class="t3">atha nibbindati dukkhe, esa maggo visuddhiyā. </span><span class="t12">[279]</span></p><p class="p3"><span class="t1">then one grows tired of suffering, this is the path to purity. </span></p><p class="p3"><span class="t1"> </span></p><h3>Conclusion Part Two </h3><p class="p3"><span class="t3">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t3">sabbabuddhānubhāvena sadā sotthī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all the Buddhas may you be safe forever! </span></p><p class="p3"><span class="t3">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t3">sabbadhammānubhāvena sadā sotthī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of all that is Dhamma may you be safe forever! </span></p><p class="p3"><span class="t3">Bhavatu sabbamaṅgalaṁ, rakkhantu sabbadevatā,</span></p><p class="p3"><span class="t1">May there be every blessing, and may all of the gods protect you, </span></p><p class="p3"><span class="t3">sabbasaṅghānubhāvena sadā sotthī bhavantu te!</span></p><p class="p3"><span class="t1">by the power of the whole Sangha may you be safe forever! </span></p><p class="p3"><span class="t1"> </span></p><h3>Conclusion Part Three </h3><p class="p3"><span class="t3">Ākāsaṭṭhā ca bhummaṭṭhā devā nāgā mahiddhikā,</span></p><p class="p3"><span class="t1">May those powerful gods and nāgas stationed in the sky or on the earth, </span></p><p class="p3"><span class="t3">puññaṁ taṁ anumoditvā ciraṁ rakkhantu sāsanaṁ!</span></p><p class="p3"><span class="t1">having rejoiced in this merit protect the teaching for a long time! </span></p><p class="p3"><span class="t3">Ākāsaṭṭhā ca bhummaṭṭhā devā nāgā mahiddhikā,</span></p><p class="p3"><span class="t1">May those powerful gods and nāgas stationed in the sky or on the earth, </span></p><p class="p3"><span class="t3">puññaṁ taṁ anumoditvā ciraṁ rakkhantu desanaṁ!</span></p><p class="p3"><span class="t1">having rejoiced in this merit protect the preaching for a long time! </span></p><p class="p3"><span class="t3">Ākāsaṭṭhā ca bhummaṭṭhā devā nāgā mahiddhikā,</span></p><p class="p3"><span class="t1">May those powerful gods and nāgas stationed in the sky or on the earth, </span></p><p class="p3"><span class="t3">puññaṁ taṁ anumoditvā ciraṁ rakkhantu maṁ paran!-ti</span></p><p class="p3"><span class="t1">having rejoiced in this merit protect me and others for a long time! </span></p><p class="p3"><span class="t3">Idaṁ me ñātīnaṁ hotu, sukhitā hontu ñātayo!</span></p><p class="p3"><span class="t1">May this (merit) go to my relatives, may my relatives be happy! </span></p>', '-36-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Verses of Determination ', 'title', 37);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 37, '<p class="p3"><span class="t3">Idaṁ me ñātīnaṁ hotu, sukhitā hontu ñātayo!</span></p><p class="p3"><span class="t1">May this (merit) go to my relatives, may my relatives be happy! </span></p><p class="p3"><span class="t3">Idaṁ me ñātīnaṁ hotu, sukhitā hontu ñātayo!</span></p><p class="p3"><span class="t1">May this (merit) go to my relatives, may my relatives be happy! </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Adhiṭṭhānagāthā</span></p><h3>Verses of Determination </h3><p class="p3"><span class="t3">Iminā puññakammena upajjhāyā guṇuttarā,</span></p><p class="p3"><span class="t1">By this meritorious deed may my highly virtuous preceptors, </span></p><p class="p3"><span class="t3">ācariyūpakārā ca, mātā pitā piyā mamaṁ, </span><span class="t12">[1]</span></p><p class="p3"><span class="t1">teachers, and other helpers, my dear mother and father, </span></p><p class="p3"><span class="t3">Suriyo Candimā rājā, guṇavantā narā pi ca,</span></p><p class="p3"><span class="t1">the Sun and Moon kings, and also other virtuous beings, </span></p><p class="p3"><span class="t3">brahmā mārā ca indrā ca, lokapālā ca devatā, </span><span class="t12">[2]</span></p><p class="p3"><span class="t1">brahmās, māras, and indras, and (all) world-protecting gods, </span></p><p class="p3"><span class="t3">Yamo mittā manussā ca majjhaṭṭhā verikāpi ca,</span></p><p class="p3"><span class="t1">Yama, friendly humans, neutral persons, and also foes, </span></p><p class="p3"><span class="t3">sabbe sattā sukhī hontu puññāni pakatāni me. </span><span class="t12">[3]</span></p><p class="p3"><span class="t1">may all beings be happy with (all) the merits that I have made. </span></p><p class="p3"><span class="t3">Sukhañ-ca tividhaṁ dentu khippaṁ pāpe yathā mataṁ,</span></p><p class="p3"><span class="t1">° By these meritorious deeds, by this dedication, may bad deeds </span></p><p class="p3"><span class="t3">iminā puññakammena iminā uddisena ca. </span><span class="t12">[4]</span></p><p class="p3"><span class="t1">be as though dead, and may (these merits) give the threefold happiness. </span></p><p class="p3"><span class="t3">Khippāhaṁ sulabhe ceva taṇhupādānachedana,</span></p><p class="p3"><span class="t1">May I quickly and easily (see the) cutting off of craving and attachment, </span></p><p class="p3"><span class="t3">ye santāne hīnā dhammā yāva Nibbānato mamaṁ, </span><span class="t12">[5]</span></p><p class="p3"><span class="t1">and the whole succession of low things until I arrive at Nibbāna, </span></p><p class="p3"><span class="t3">nassantu sabbadā yeva yattha jāto bhave bhave.</span></p><p class="p3"><span class="t1">may (these things) be destroyed every day in whatever existence I am born. </span></p><p class="p3"><span class="t3">Ujucitto satipañño, sallekho viriyavāminā, </span><span class="t12">[6]</span></p><p class="p3"><span class="t1">Upright mind, mindfulness, wisdom, austerity, and energy, by these (qualities), </span></p><p class="p3"><span class="t3">mārā labhantu nokāsaṁ kātuñ-ca viriyesu me,</span></p><p class="p3"><span class="t1">may the māras find no room to do (anything) about my energy, </span></p><p class="p3"><span class="t3">Buddho dīpavaro nātho, Dhammo nātho varuttamo, </span><span class="t12">[7]</span></p><p class="p3"><span class="t1">The Buddha is an excellent light, a protector, the Dhamma is the greatest, most excellent protector, </span></p><p class="p3"><span class="t3">nātho Paccekasambuddho, Saṅgho nāthottaro mamaṁ,</span></p>', '-37-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Advice ', 'title', 38);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Confession of Faults ', 'title', 38);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 38, '<p class="p3"><span class="t1">the Independent Sambuddha is my protector, the Sangha is the greatest protector for me, </span></p><p class="p3"><span class="t3">tejottamānubhāvena mārokāsaṁ labhantu mā. </span><span class="t12">[8]</span></p><p class="p3"><span class="t1">by this highly resplendent power may the māras find no room </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Ovādaṁ</span></p><h3>Advice </h3><p class="p3"><span class="t11">(one person only)</span></p><p class="p3"><span class="t11"> </span></p><p class="p3"><span class="t5">Appamādena bhikkhave sampādetha:</span></p><p class="p3"><span class="t1">Strive on, monks, with heedfulness: </span></p><p class="p3"><span class="t5">Buddhuppādo dullabho lokasmiṁ,</span></p><p class="p3"><span class="t1">the arising of a Buddha in this world is rare, </span></p><p class="p3"><span class="t5">manussattā paṭilābho dullabho,</span></p><p class="p3"><span class="t1">acquiring a human existence is rare, </span></p><p class="p3"><span class="t5">dullabhā saddhā sampatti,</span></p><p class="p3"><span class="t1">gaining confidence is rare, </span></p><p class="p3"><span class="t5">pabbajitabhāvo dullabho,</span></p><p class="p3"><span class="t1">being one gone forth is rare, </span></p><p class="p3"><span class="t5">Saddhammasavanaṁ atidullabhaṁ.</span></p><p class="p3"><span class="t1">hearing the True Dhamma is extremely rare. </span></p><p class="p3"><span class="t5">Evaṁ divase divase ovādī:</span></p><p class="p3"><span class="t1">So day in and day out he advised them thus (saying): </span></p><p class="p3"><span class="t5">“Handa dāni bhikkhave āmantayāmi vo vayadhammā saṅkhārā,</span></p><p class="p3"><span class="t1">“Come now, monks, for I tell you (all) conditioned things are subject to decay, </span></p><p class="p3"><span class="t5">appamādena sampādetha!”</span></p><p class="p3"><span class="t1">strive on with heedfulness!” </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t3">Vajjapakāsanaṁ</span></p><h3>Confession of Faults </h3><p class="p3"><span class="t3">Kāyena vācā cittena pamādena mayā kataṁ,</span></p><p class="p3"><span class="t1">° For any transgression I have committed through heedlessness, by way of body, </span></p><p class="p3"><span class="t3">accayaṁ khama me bhante, bhūripañña Tathāgata.</span></p><p class="p3"><span class="t1">speech, or mind, please forgive me, Venerable Sir, Realised One, O greatly wise. </span></p><p class="p3"><span class="t3">Kāyena vācā cittena pamādena mayā kataṁ,</span></p><p class="p3"><span class="t1">° For any transgression I have committed through heedlessness, by way of body, </span></p><p class="p3"><span class="t3">accayaṁ khama me Dhamma sandiṭṭhika, akālika.</span></p>', '-38-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Benediction ', 'title', 39);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Appendix ', 'title', 39);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'The Reflections ', 'title', 39);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 39, '<p class="p3"><span class="t1">speech, or mind, please forgive me, O Dhamma, which leads on, not subject to time. </span></p><p class="p3"><span class="t3">Kāyena vācā cittena pamādena mayā kataṁ,</span></p><p class="p3"><span class="t1">° For any transgression I have committed through heedlessness, by way of body, </span></p><p class="p3"><span class="t3">accayaṁ khama me Saṅgha, puññakkhetta anuttara.</span></p><p class="p3"><span class="t1">speech, or mind, please forgive me, O Sangha unsurpassed field of merit. </span></p><p class="p3"><span class="t1"><span class="t11">(if a senior monk is present the  Asking for Forgiveness found in the appendix, p. 50 can be recited here)</span> </span></p><p class="p3"><span class="t3">Vuddhipatthānaṁ</span></p><h3>Benediction </h3><p class="p3"><span class="t3">Abhivādanasīlissa niccaṁ vaddhā pacāyino,</span></p><p class="p3"><span class="t1">For one in the habit of constantly worshipping respectable elders, </span></p><p class="p3"><span class="t3">cattāro dhammā vaḍḍhanti āyu, vaṇṇo, sukhaṁ, balaṁ.</span></p><p class="p3"><span class="t1">four things increase length of life, beauty, happiness, and strength. </span></p><p class="p3"><span class="t3">Āyurārogyasampatti saggasampatti-m-eva ca,</span></p><p class="p3"><span class="t1">The attainment of long life and health, the attainment of heaven, </span></p><p class="p3"><span class="t3">atho Nibbānasampatti iminā te samijjhatu!</span></p><p class="p3"><span class="t1">and then the attainment of Nibbāna may you be successful in this! </span></p><p class="p3"><span class="t1"> </span></p><h2>Appendix </h2><p class="p3"><span class="t1"><span class="t3">Paccavekkhaṇā</span> </span></p><h3>The Reflections </h3><p class="p3"><span class="t5">Paṭisaṅkhā yoniso cīvaraṁ paṭisevāmi,</span></p><p class="p3"><span class="t1">With proper discernment I make use of the robe, </span></p><p class="p3"><span class="t5">yāvad-eva sītassa paṭighātāya, uṇhassa paṭighātāya,</span></p><p class="p3"><span class="t1">only to ward off the cold, to ward off the heat, </span></p><p class="p3"><span class="t5">ḍaṁsamakasavātātapasiriṁsapasamphassānaṁ paṭighātāya,</span></p><p class="p3"><span class="t1">to ward off contact with gadflies, mosquitoes, wind, the heat (of the sun), and creeping things, </span></p><p class="p3"><span class="t5">yāvad-eva hirikopīnapaṭicchādanatthaṁ. </span>[1]</p><p class="p3"><span class="t1">only as a cover for the shameful parts. </span></p><p class="p3"><span class="t5">Paṭisaṅkhā yoniso piṇḍapātaṁ paṭisevāmi,</span></p><p class="p3"><span class="t1">With proper discernment I make use of almsfood, </span></p><p class="p3"><span class="t5">neva davāya, na madāya, na maṇḍanāya, na vibhūsanāya,</span></p><p class="p3"><span class="t1">not for sport, or for showing off, not for ornament, or for adornment, </span></p><p class="p3"><span class="t5">yāvad-eva imassa kāyassa ṭhitiyā yāpanāya,</span></p><p class="p3"><span class="t1">but only to maintain this body, and to carry on, </span></p><p class="p3"><span class="t5">vihiṁsūparatiyā brahmacariyānuggahāya,</span></p><p class="p3"><span class="t1">to inhibit annoyance, and to assist in the spiritual life, </span></p>', '-39-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_daily_chanting', 'Asking for Forgiveness ', 'title', 40);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 40, '<p class="p3"><span class="t5">iti purāṇañ-ca vedanaṁ paṭihaṅkhāmi,</span></p><p class="p3"><span class="t1">and so I will get rid of any old feeling, </span></p><p class="p3"><span class="t5">navañ-ca vedanaṁ na uppādessāmi,</span></p><p class="p3"><span class="t1">and not produce any new feeling, </span></p><p class="p3"><span class="t5">yātrā ca me bhavissati, anavajjatā ca phāsuvihāro cā ti. </span>[2]</p><p class="p3"><span class="t1">and there will be support for me, being blameless, and living comfortably. </span></p><p class="p3"><span class="t5">Paṭisaṅkhā yoniso senāsanaṁ paṭisevāmi,</span></p><p class="p3"><span class="t1">With proper discernment I make use of a dwelling, </span></p><p class="p3"><span class="t5">yāvad-eva sītassa paṭighātāya, uṇhassa paṭighātāya,</span></p><p class="p3"><span class="t1">only to ward off the cold, to ward off the heat, </span></p><p class="p3"><span class="t5">ḍaṁsamakasavātātapasiriṁsapasamphassānaṁ paṭighātāya,</span></p><p class="p3"><span class="t1">to ward off contact with gadflies, mosquitoes, wind, the heat (of the sun), and serpents, </span></p><p class="p3"><span class="t5">yāvad-eva utuparissayavinodanaṁ paṭisallānārāmatthaṁ. </span>[3]</p><p class="p3"><span class="t1">only to dispel the trouble of the (varying) seasons, and so as to delight in seclusion. </span></p><p class="p3"><span class="t21">Paṭisaṅkhā yoniso gilānapaccayabhesajjaparikkhāraṁ paṭisevāmi,</span></p><p class="p3"><span class="t1">With proper discernment I make use of the requisite of medicine for support when sick, </span></p><p class="p3"><span class="t5">yāvad-eva uppannānaṁ veyyābādhikānaṁ vedanānaṁ paṭighātāya</span></p><p class="p3"><span class="t1">only to ward off oppressive feelings that have arisen </span></p><p class="p3"><span class="t5">abyāpajjhaparamatāyā ti. </span>[4]</p><p class="p3"><span class="t1">and at least be free from oppression. </span></p><p class="p3"><span class="t3">Khamāpanā</span></p><h3>Asking for Forgiveness </h3><p class="p3"><span class="t5">Okāsa vandāmi bhante,</span></p><p class="p3"><span class="t1">With your consent, Venerable Sir, I worship you, </span></p><p class="p3"><span class="t5"> (Sukhi hotha!)</span></p><p class="p3"><span class="t1">(May you be happy!) </span></p><p class="p3"><span class="t5">Mayā kataṁ puññaṁ sāminā anumoditabbaṁ,</span></p><p class="p3"><span class="t1">If any merit has been made by me I share it with my lord, </span></p><p class="p3"><span class="t5">(Sādhu! Sādhu! Anumodāmi,)</span></p><p class="p3"><span class="t1">(Surely! Surely! I share in it,) </span></p><p class="p3"><span class="t5">Saminā kataṁ puññam mayaṁ dātabbaṁ,</span></p><p class="p3"><span class="t1">The merit made by my lord should be shared with us, </span></p><p class="p3"><span class="t5">(Sādhu! Anumoditabbaṁ,)</span></p><p class="p3"><span class="t1">(Surely! You should share in it,) </span></p><p class="p3"><span class="t5">Sādhu! Sādhu! Anumodāmi,</span></p><p class="p3"><span class="t1">Surely! Surely! I share in it, </span></p>', '-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_daily_chanting', 41, '<p class="p3"><span class="t5">Okāsa dvarattayena kataṁ sabbaṁ aparādaṁ khamatha me bhante,</span></p><p class="p3"><span class="t1">Please consent to forgive me, Venerable Sir, for any offences I have committed by way of the three doors (of body, speech, or mind), </span></p><p class="p3"><span class="t5">(Khamāmi, khamitabbaṁ!)</span></p><p class="p3"><span class="t1">(I forgive you, you should forgive me!) </span></p><p class="p3"><span class="t5">Okāsa khamāmi bhante!</span></p><p class="p3"><span class="t1">With consent, I forgive you, Venerable Sir! </span></p>', '-41-');
