INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 1, '<p class="p3"><h1>Mahāparittaṁ The Great Safeguard</h1></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><h3>1. Opening Ceremony </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"><span class="t12">Namakkāraṁ</span> </span></p><p class="p3"><h4>Reverence </h4></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">(A senior monk first establishes the lay people  in the refuges and virtuous practices:) </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t9">Namo tassa Bhagavato Arahato Sammāsambuddhassa x 3</span></p><p class="p3"><span class="t4">Reverence to him, the Fortunate One, the Worthy One, the Perfect Sambuddha</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"><span class="t12">Saraṇagamanaṁ</span> </span></p><p class="p3"><h4>Going for Refuge </h4></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Buddhaṁ saraṇaṁ gacchāmi</span></p><p class="p3"><span class="t1">I go to the Buddha for refuge </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dhammaṁ saraṇaṁ gacchāmi</span></p><p class="p3"><span class="t1">I go to the Dhamma for refuge </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saṅghaṁ saraṇaṁ gacchāmi</span></p><p class="p3"><span class="t1">I go to the Saṅgha for refuge </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dutiyam-pi Buddhaṁ saraṇaṁ gacchāmi</span></p><p class="p3"><span class="t1">For a second time I go to the Buddha for refuge </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dutiyam-pi Dhammaṁ saraṇaṁ gacchāmi</span></p><p class="p3"><span class="t1">For a second time I go to the Dhamma for refuge </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dutiyam-pi Saṅghaṁ saraṇaṁ gacchāmi</span></p><p class="p3"><span class="t1">For a second time I go to the Saṅgha for refuge </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tatiyam-pi Buddhaṁ saraṇaṁ gacchāmi</span></p><p class="p3"><span class="t1">For a third time I go to the Buddha for refuge </span></p>', '-1-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', 'The Five Training Rules ', 'title', 2);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', 'The Invitation ', 'title', 2);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 2, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tatiyam-pi Dhammaṁ saraṇaṁ gacchāmi</span></p><p class="p3"><span class="t1">For a third time I go to the Dhamma for refuge </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tatiyam-pi Saṅghaṁ saraṇaṁ gacchāmi</span></p><p class="p3"><span class="t1">For a third time I go to the Saṅgha for refuge </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"><span class="t12">Pañcasikkhāpadāni</span> </span></p><p class="p3"><h4>The Five Training Rules </h4></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pāṇātipātā veramaṇīsikkhāpadaṁ </span><span class="t5">samādiyāmi</span> [1]</p><p class="p3"><span class="t1">I undertake the training rule of refraining from killing living creatures </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Adinnādānā veramaṇīsikkhāpadaṁ </span><span class="t5">samādiyāmi</span> [2]</p><p class="p3"><span class="t1">I undertake the training rule of refraining from taking what has not been given </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kāmesumicchācārā veramaṇīsikkhāpadaṁ </span><span class="t5">samādiyāmi</span> [3]</p><p class="p3"><span class="t1">I undertake the training rule of refraining from sexual misconduct </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Musāvādā veramaṇīsikkhāpadaṁ </span><span class="t5">samādiyāmi</span> [4]</p><p class="p3"><span class="t1">I undertake the training rule of refraining from false speech </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Surāmerayamajjapamādaṭṭhānā veramaṇīsikkhāpadaṁ </span><span class="t5">samādiyāmi</span> [5]</p><p class="p3"><span class="t1">I undertake the training rule of refraining from liquor, wines, or intoxicants which cause heedlessness. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">(Then the leading monk encourages the laypeople with a teaching appropriate  to the occasion, after which the leading layman requests the recital:) </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"><span class="t12">Ārādhanā</span> </span></p><p class="p3"><h4>The Invitation </h4></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vipattipaṭibāhāya, sabbasampattisiddhiyā,</span></p><p class="p3"><span class="t1">For the warding off of misfortune, for the accomplishment of all good fortune, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbadukkhavināsāya: parittaṁ brūtha maṅgalaṁ.</span></p><p class="p3"><span class="t1">for the destruction of all suffering: please chant the auspicious safeguard. </span></p><p class="p3"><span class="t1">  </span></p>', '-2-');
