INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 108, '<p class="p3"><span class="t5">“Etaṁ Bhagavatā Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">“The Fortunate One, while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmin”-ti. </span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ābhassarānaṁ devānaṁ saddaṁ sutvā </span></p><p class="p3"><span class="t1">Having heard the cry of the Ābhassara gods </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Parittasubhā devā saddam-anussāvesuṁ:</span></p><p class="p3"><span class="t1">the Parittasubha gods let loose a cry: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Etaṁ Bhagavatā Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">“The Fortunate One, while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmin”-ti. </span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Parittasubhānaṁ devānaṁ saddaṁ sutvā </span></p><p class="p3"><span class="t1">Having heard the cry of the Parittasubha gods </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appamāṇasubhā devā saddam-anussāvesuṁ:</span></p><p class="p3"><span class="t1">the Appamāṇasubha gods let loose a cry: </span></p><p class="p3"><span class="t1">  </span></p>', '-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 109, '<p class="p3"><span class="t5">“Etaṁ Bhagavatā Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">“The Fortunate One, while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmin”-ti. </span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world.” </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Appamāṇasubhānaṁ devānaṁ saddaṁ sutvā </span></p><p class="p3"><span class="t1">Having heard the cry of the Appamāṇasubha gods </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Subhakiṇhakā</span>{74}<span class="t5"> devā saddam-anussāvesuṁ:</span></p><p class="p3"><span class="t1">the Subhakiṇha gods let loose a cry: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Etaṁ Bhagavatā Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">“The Fortunate One, while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmin”-ti. </span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Subhakiṇhakānaṁ</span>{75}<span class="t5"> devānaṁ saddaṁ sutvā </span></p><p class="p3"><span class="t1">Having heard the cry of the Subhakiṇha gods </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vehapphalā devā saddam-anussāvesuṁ:</span></p><p class="p3"><span class="t1">the Vehapphala gods let loose a cry: </span></p><p class="p3"><span class="t1">  </span></p>', '-109-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 110, '<p class="p3"><span class="t5">“Etaṁ Bhagavatā Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">“The Fortunate One, while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmin”-ti. </span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The Gods of the Pure Abodes</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vehapphalānaṁ devānaṁ saddaṁ sutvā </span></p><p class="p3"><span class="t1">Having heard the cry of the Vehapphala gods </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Avihā devā saddam-anussāvesuṁ:</span></p><p class="p3"><span class="t1">the Aviha gods let loose a cry: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Etaṁ Bhagavatā Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">“The Fortunate One, while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmin”-ti. </span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Avihānaṁ devānaṁ saddaṁ sutvā </span></p><p class="p3"><span class="t1">Having heard the cry of the Aviha gods </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atappā devā saddam-anussāvesuṁ:</span></p>', '-110-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 111, '<p class="p3"><span class="t1">the Atappa gods let loose a cry: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Etaṁ Bhagavatā Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">“The Fortunate One, while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmin”-ti. </span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world.” </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Atappānaṁ devānaṁ saddaṁ sutvā </span></p><p class="p3"><span class="t1">Having heard the cry of the Atappa gods </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sudassā devā saddam-anussāvesuṁ:</span></p><p class="p3"><span class="t1">the Sudassa gods let loose a cry: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Etaṁ Bhagavatā Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">“The Fortunate One, while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmin”-ti. </span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sudassānaṁ devānaṁ saddaṁ sutvā </span></p><p class="p3"><span class="t1">Having heard the cry of the Sudassa gods </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sudassī devā saddam-anussāvesuṁ:</span></p>', '-111-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 112, '<p class="p3"><span class="t1">the Sudassī gods let loose a cry: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Etaṁ Bhagavatā Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">“The Fortunate One, while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmin”-ti. </span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sudassīnaṁ devānaṁ saddaṁ sutvā </span></p><p class="p3"><span class="t1">Having heard the cry of the Sudassī gods </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Akiṇiṭṭhakā devā saddam-anussāvesuṁ:</span></p><p class="p3"><span class="t1">the Akiṇiṭṭhaka gods let loose a cry: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Etaṁ Bhagavatā Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">“The Fortunate One, while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmin”-ti. </span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Iti ha tena khaṇena tena muhuttena, yāva Brahmalokā saddo abbhuggañchi,</span></p><p class="p3"><span class="t1">Thus at that moment, at that second, that cry reached as far as the Brahma worlds, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayañ-ca dasasahassī</span>{76}<span class="t5"> lokadhātu saṅkampi, sampakampi, sampavedhi,</span></p>', '-112-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '23. Mahāsamayasuttaṁ The Discourse on the Great Convention ', 'title', 113);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 113, '<p class="p3"><span class="t1">and this ten thousand world-element moved, wavered, and shook, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appamāṇo ca uḷāro obhāso loke pātur-ahosi,</span></p><p class="p3"><span class="t1">and great and measureless light became manifest in the world, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">atikkamma devānaṁ devānubhāvan-ti. </span></p><p class="p3"><span class="t1">transcending the psychic power of the gods. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Bhagavā udānaṁ udānesi: </span></p><p class="p3"><span class="t1">Then the Fortunate One uttered this inspired utterance: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Aññāsi vata bho Koṇḍañño, aññāsi vata bho Koṇḍañño” ti.</span></p><p class="p3"><span class="t1">“Koṇḍañña surely knows, Koṇḍañña surely knows.” </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Iti hidaṁ āyasmato Koṇḍaññassa </span></p><p class="p3"><span class="t1">Thus to the venerable Koṇḍañña  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Aññā Koṇḍañño tveva nāmaṁ ahosī ti.</span></p><p class="p3"><span class="t1">came the name Aññā Koṇḍañña (Koṇḍañña, he-who-knows). </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>23. Mahāsamayasuttaṁ The Discourse on the Great Convention </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ me sutaṁ: </span></p><p class="p3"><span class="t1">Thus I have heard: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Sakkesu viharati </span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling amongst the Sakyans </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kapilavatthusmiṁ Mahāvane mahatā bhikkhusaṅghena saddhiṁ</span></p><p class="p3"><span class="t1">in the Great Wood near Kapilavatthu with a great Saṅgha of monks </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pañcamattehi bhikkhusatehi sabbeheva Arahantehi,</span></p><p class="p3"><span class="t1">consisting of five hundred monks, all of them Worthy Ones, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dasahi ca lokadhātūhi</span>{77}<span class="t5"> devatā yebhuyyena sannipatitā honti</span></p><p class="p3"><span class="t1">and almost all of the gods from the ten world-elements had assembled </span></p>', '-113-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 114, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavantaṁ dassanāya bhikkhusaṅghañ-ca. </span></p><p class="p3"><span class="t1">to see the Fortunate One and the Saṅgha of monks. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho catunnaṁ Suddhāvāsakāyikānaṁ devānaṁ etad-ahosi: </span></p><p class="p3"><span class="t1">Then this occurred to four gods from among the hosts in the Pure Abodes: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Ayaṁ kho Bhagavā Sakkesu viharati </span></p><p class="p3"><span class="t1">“The Fortunate One is dwelling amongst the Sakyans </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Kapilavatthusmiṁ Mahāvane mahatā bhikkhusaṅghena saddhiṁ </span></p><p class="p3"><span class="t1">in the Great Wood near Kapilavatthu with a great Saṅgha of moṅks </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">pañcamattehi bhikkhusatehi sabbeheva Arahantehi,</span></p><p class="p3"><span class="t1">consisting of five hundred monks, all of them Worthy Ones, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dasahi ca lokadhātūhi</span>{78}<span class="t5"> devatā yebhuyyena sannipatitā honti</span></p><p class="p3"><span class="t1">and almost all of the gods from the ten world elements have assembled </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavantaṁ dassanāya bhikkhusaṅghañ-ca. </span></p><p class="p3"><span class="t1">to see the Fortunate One and the Saṅgha of monks. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yan-nūna mayam-pi yena Bhagavā tenupasaṅkameyyāma,</span></p><p class="p3"><span class="t1">Well now, we too could approach the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upasaṅkamitvā Bhagavato santike paccekagāthaṁ bhāseyyāmā” ti. </span></p><p class="p3"><span class="t1">and after approaching the Fortunate One we could recite a verse each in his presence.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho tā devatā seyyathā pi nāma balavā puriso </span></p><p class="p3"><span class="t1">Then those gods, just as a strong man </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sammiñjitaṁ vā bāhaṁ pasāreyya, pasāritaṁ vā bāhaṁ sammiñjeyya, </span></p><p class="p3"><span class="t1">might stretch out a bent arm, or bend in an outstretched arm, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evam-evaṁ Suddhāvāsesu devesu antarahitā </span></p><p class="p3"><span class="t1">in the same way disappeared from among the gods of the Pure Abodes </span></p>', '-114-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 115, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavato purato pātur-ahesuṁ.</span></p><p class="p3"><span class="t1">and appeared in front of the Fortunate One. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho tā devatā Bhagavantaṁ abhivādetvā, ekam-antaṁ aṭṭhaṁsu. </span></p><p class="p3"><span class="t1">Then those gods after worshipping the Fortunate One, stood on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ekam-antaṁ ṭhitā kho ekā devatā </span></p><p class="p3"><span class="t1">While standing on one side one of the gods </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavato santike imaṁ gāthaṁ abhāsi:</span></p><p class="p3"><span class="t1">recited this verse in the presence of the Fortunate One: </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">“Mahāsamayo pavanasmiṁ, devakāyā samāgatā, </span></p><p class="p3"><span class="t1">“There is a great convention in the wood, and a host of gods have come, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">āgatamha imaṁ Dhammasamayaṁ dakkhitāye aparājitasaṅghan”-ti. </span>[1]</p><p class="p3"><span class="t1">we have come to this Dhamma convention to see the Saṅgha, who are undefeated.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho aparā devatā Bhagavato santike imaṁ gāthaṁ abhāsi:</span></p><p class="p3"><span class="t1">Then another god recited this verse in the presence of the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Tatra bhikkhavo samādahaṁsu, cittamattano ujukaṁ akaṁsu,</span>{79}</p><p class="p3"><span class="t1">“In this place the monks are concentrated, they have straightened out their minds by themselves, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sārathīva nettāni gahetvā, indriyāni rakkhanti paṇḍitā” ti. </span>[2]</p><p class="p3"><span class="t1">like a charioteer who has taken the reins, the wise protect their sense faculties.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho aparā devatā Bhagavato santike imaṁ gāthaṁ abhāsi: </span></p><p class="p3"><span class="t1">Then another god recited this verse in the presence of the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Chetvā khīlaṁ chetvā palighaṁ, indakhīlaṁ ūhacca-m-anejā,</span></p><p class="p3"><span class="t1">“Having cut off the hindrance and obstacle, and dug up the locking post, they are unmoved, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">te caranti suddhā vimalā, Cakkhumatā sudantā susunāgā” ti. </span>[3]</p><p class="p3"><span class="t1">they live pure and unstained, young nāgas, well-tamed by the Visionary.” </span></p>', '-115-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 116, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho aparā devatā Bhagavato santike imaṁ gāthaṁ abhāsi: </span></p><p class="p3"><span class="t1">Then another god recited this verse in the presence of the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Ye keci Buddhaṁ saraṇaṁ gatāse na te gamissanti apāyaṁ.</span>{80}</p><p class="p3"><span class="t1">“Whoever has gone to the Buddha for refuge will not go to the lower worlds. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pahāya mānusaṁ dehaṁ devakāyaṁ paripūressantī” ti. </span>[4]</p><p class="p3"><span class="t1">After giving up the human body they will fill up the ranks of the gods.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Bhagavā bhikkhū āmantesi:</span></p><p class="p3"><span class="t1">Then the Fortunate One addressed the monks, saying: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Yebhuyyena bhikkhave dasasu lokadhātūsu devatā sannipatitā</span></p><p class="p3"><span class="t1">“Monks, almost all of the gods from the ten world-elements have assembled </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tathāgataṁ dassanāya bhikkhusaṅghañ-ca.</span></p><p class="p3"><span class="t1">to see the Realised One and the Saṅgha of monks. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye pi te bhikkhave ahesuṁ atītam-addhānaṁ Arahanto,</span></p><p class="p3"><span class="t1">Monks, those who in the past were Worthy Ones,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sammāsambuddhā, tesam-pi Bhagavantānaṁ </span></p><p class="p3"><span class="t1">Perfect Sambuddhas, for those Fortunate Ones also  </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">etaparamā yeva devatā sannipatitā ahesuṁ seyyathā pi mayhaṁ etarahi.</span></p><p class="p3"><span class="t1">at least as many gods have assembled as for me at the present time. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye pi te bhikkhave bhavissanti anāgatam-addhānaṁ Arahanto,</span></p><p class="p3"><span class="t1">Monks, those who in the future will be Worthy Ones,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sammāsambuddhā, tesam-pi Bhagavantānaṁ </span></p><p class="p3"><span class="t1">Perfect Sambuddhas, for those Fortunate Ones also  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">etaparamā yeva devatā sannipatitā bhavissanti seyyathā pi mayhaṁ etarahi.</span></p><p class="p3"><span class="t1">at least as many gods will assemble as for me at the present time. </span></p>', '-116-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 117, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ācikkhissāmi bhikkhave devakāyānaṁ nāmāni, </span></p><p class="p3"><span class="t1">Monks, I will declare the names of the hosts of gods, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kittayissāmi bhikkhave devakāyānaṁ nāmāni, </span></p><p class="p3"><span class="t1">monks, I will proclaim the names of the hosts of gods, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">desissāmi</span>{81}<span class="t5"> bhikkhave devakāyānaṁ nāmāni. </span></p><p class="p3"><span class="t1">monks, I will reveal the names of the hosts of gods. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Taṁ suṇātha sādhukaṁ manasikarotha bhāsissāmī” ti. </span></p><p class="p3"><span class="t1">Listen to it, apply your minds well, and I will speak.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Evaṁ bhante” ti kho te bhikkhū Bhagavato paccassosuṁ, </span></p><p class="p3"><span class="t1">“Yes, reverend Sir,” those monks replied to the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavā etad-avoca: </span></p><p class="p3"><span class="t1">and the Fortunate One said this: </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">“Silokam-anukassāmi yattha Bhummā tad-assitā:</span></p><p class="p3"><span class="t1">“I will repeat a famous verse (a Siloka) wherever the gods of the Earth live: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye sitā girigabbharaṁ, pahitattā samāhitā, </span>[5]</p><p class="p3"><span class="t1">There are (monks) who live in a hill cave, who are resolute, composed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puthū</span>{82}<span class="t5"> sīhā va sallīnā, lomahaṁsābhisambhuno, </span></p><p class="p3"><span class="t1">who are like lions crouching, who have overcome (all) horror, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">odātamanasā suddhā, vippasanna-m-anāvilā”. </span>[6]</p><p class="p3"><span class="t1">with minds that are cleansed, and purified, which are clear, and undisturbed”. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhiyyo</span>{83}<span class="t5"> pañcasate ñatvā vane Kāpilavatthave, </span></p><p class="p3"><span class="t1">He knew there were more than five hundred in the wood near Kapilavatthu (like this), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tato āmantayī Satthā sāvake sāsane rate: </span>[7]</p><p class="p3"><span class="t1">therefore the Teacher addressed those disciples who delight in the teaching: </span></p>', '-117-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 118, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Devakāyā abhikkantā, te vijānātha bhikkhavo!”</span></p><p class="p3"><span class="t1">“A host of gods have drawn near (to us), you should know who they are,  </span></p><p class="p3"><span class="t1">monks!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Te ca ātappam-akaruṁ, sutvā Buddhassa sāsanaṁ, </span>[8]</p><p class="p3"><span class="t1">Then (those monks) became ardent, after hearing the Buddha’s teaching, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tesaṁ pātur-ahū</span>{84}<span class="t5"> ñāṇaṁ, amanussānadassanaṁ -</span></p><p class="p3"><span class="t1">and knowledge was manifest to them, seeing those non-human beings -  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appeke satam-addakkhuṁ, sahassaṁ atha sattatiṁ. </span>[9]</p><p class="p3"><span class="t1">and some of them saw a hundred, a thousand, or seventy (thousand). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sataṁ eke sahassānaṁ amanussānam-addasuṁ,</span>{85}<span class="t5"> </span></p><p class="p3"><span class="t1">Some of them saw a hundred thousand of those non-human beings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appekenantam-addakkhuṁ disā sabbā phuṭā ahū.</span>{86}<span class="t5"> </span>[10]</p><p class="p3"><span class="t1">and some saw an endless number spread out in every direction. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tañ-ca sabbaṁ abhiññāya vavakkhitvāna</span>{87}<span class="t5"> Cakkhumā, </span></p><p class="p3"><span class="t1">Having deep knowledge of all that the Visionary desired to speak, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tato āmantayī Satthā sāvake sāsane rate: </span>[11]</p><p class="p3"><span class="t1">therefore the Teacher addressed those disciples who delight in the teaching: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Devakāyā abhikkantā, te vijānātha bhikkhavo!</span>{88}</p><p class="p3"><span class="t1">“A host of gods have drawn near (to us), you should know who they are,  </span></p><p class="p3"><span class="t1">monks! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye vohaṁ kittayissāmi girāhi anupubbaso. </span>[12]</p><p class="p3"><span class="t1">I will proclaim their (names) to you with lyrics in regular order. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yakkhas</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sattasahassā te yakkhā,</span>{89}<span class="t5"> Bhummā Kāpilavatthavā, </span></p>', '-118-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 119, '<p class="p3"><span class="t1">There are seven thousand yakkhas, Earth gods from Kapilavatthu, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto,</span>{90}<span class="t5"> vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[13]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Chasahassā Hemavatā yakkhā nānattavaṇṇino,</span></p><p class="p3"><span class="t1">From Hemavant there are six thousand yakkhas, diverse in colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[14]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sātāgirā tisahassā yakkhā nānattavaṇṇino,</span></p><p class="p3"><span class="t1">From Sātāgira there are three thousand yakkhas, diverse in colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[15]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Iccete soḷasasahassā yakkhā nānattavaṇṇino,</span></p><p class="p3"><span class="t1">Like this there are sixteen thousand yakkhas, diverse in colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[16]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vessāmittā pañcasatā yakkhā nānattavaṇṇino,</span></p>', '-119-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 120, '<p class="p3"><span class="t1">From Vessāmitta there are five hundred yakkhas, diverse in colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[17]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kumbhīro</span>{91}<span class="t5"> Rājagahiko, Vepullassa nivesanaṁ,</span></p><p class="p3"><span class="t1">From Rājagaha there is Kumbīra, who is settled on Vepulla, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhiyyo</span>{92}<span class="t5"> naṁ satasahassaṁ yakkhānaṁ payirupāsati,</span></p><p class="p3"><span class="t1">and more than a hundred thousand yakkhas who gather round him, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kumbhīro Rājagahiko so pāga samitiṁ vanaṁ. </span>[18]</p><p class="p3"><span class="t1">From Rājagaha there is Kumbīra he also came to the gathering in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The Four Great Kings</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Purimañ-ca disaṁ, rājā Dhataraṭṭho taṁ</span>{93}<span class="t5"> pasāsati,</span></p><p class="p3"><span class="t1">The Easterly direction, the king Dhataraṭṭha rules over that, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbānaṁ ādhipati, Mahārājā yasassi so, </span>[19]</p><p class="p3"><span class="t1">he is master of the Gandhabbas, he is a resplendent Great King, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, Indanāmā mahabbalā,</span></p><p class="p3"><span class="t1">also there are his many sons, Inda by name, ones of great strength, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[20]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dakkhiṇañ-ca disaṁ, rājā Virūḷho taṁ pasāsati,</span></p><p class="p3"><span class="t1">The Southerly direction, the king Virūḷha rules over that, </span></p>', '-120-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 121, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍānaṁ ādhipati, Mahārājā yasassi so, </span>[21]</p><p class="p3"><span class="t1">he is master of the Kumbhaṇḍas, he is a resplendent Great King, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, Indanāmā mahabbalā,</span></p><p class="p3"><span class="t1">also there are his many sons, Inda by name, ones of great strength, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[22]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pacchimañ-ca disaṁ, rājā Virūpakkho taṁ pasāsati,</span></p><p class="p3"><span class="t1">The Westerly direction, the king Virūpakkha rules over that, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgānaṁ ādhipati, Mahārājā yasassi so, </span>[23]</p><p class="p3"><span class="t1">he is master of the nāgas, he is a resplendent Great King, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, Indanāmā mahabbalā,</span></p><p class="p3"><span class="t1">also there are his many sons, Inda by name, ones of great strength, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino, </span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[24]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Uttarañ-ca disaṁ, rājā Kuvero taṁ pasāsati,</span></p><p class="p3"><span class="t1">The Northerly direction, the king Kuvera rules over that, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhānaṁ ādhipati, Mahārājā yasassi so, </span>[25]</p><p class="p3"><span class="t1">he is master of the yakkhas, he is a resplendent Great King, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, Indanāmā mahabbalā,</span></p><p class="p3"><span class="t1">also there are his many sons, Inda by name, ones of great strength, </span></p>', '-121-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 122, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[26]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Purimaṁ disaṁ Dhataraṭṭho,</span>{94}<span class="t5"> Dakkhiṇena Virūḷhako, </span></p><p class="p3"><span class="t1">To the East there is Dhataraṭṭha, in the South there is Virūḷhaka, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pacchimena Virūpakkho, Kuvero Uttaraṁ disaṁ. </span>[27]</p><p class="p3"><span class="t1">in the West there is Virūpakkha, to the North there is Kuvera.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Cattāro te Mahārājā, samantā caturo disā,</span></p><p class="p3"><span class="t1">These are the Four Great Kings, from all sides, the four directions, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">daddallamānā aṭṭhaṁsu vane Kāpilavatthave. </span>[28]</p><p class="p3"><span class="t1">they stood blazing brilliantly in the wood near Kapilavatthu. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tesaṁ māyāvino dāsā āgu</span>{95}<span class="t5"> vañcanikā saṭhā:</span></p><p class="p3"><span class="t1">Their deceitful servants came, who are fraudulent and treacherous: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">māyā Kuṭeṇḍu Veṭeṇḍu, Viṭucca Viṭuḍo saha, </span>[29]</p><p class="p3"><span class="t1">the deceitful Kuteṇḍu, Veteṇḍu, Viṭucca, and Viṭuḍa, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Candano Kāmaseṭṭho ca, Kinnughaṇḍu Nighaṇḍu ca, </span></p><p class="p3"><span class="t1">Candana and Kāmaseṭṭha, Kinnughaṇḍu and Nighaṇḍu, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Panādo Opamañño ca, devasūto ca Mātalī, </span>[30]</p><p class="p3"><span class="t1">Panāda and Opamañña, and Mātali, the gods’ charioteer, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Cittaseno</span>{96}<span class="t5"> ca gandhabbo, Nalo rājā Janesabho</span></p><p class="p3"><span class="t1">The gandhabbas Citta and Sena, the kings Nala and Janesabha </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">āgu Pañcasikho ceva, Timbarū Suriyavaccasā. </span>[31]</p><p class="p3"><span class="t1">came, and also Pañcasikha, Timbaru, and Suriyavaccasā. </span></p>', '-122-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 123, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ete caññe ca rājāno, gandhabbā saha rājubhi,</span></p><p class="p3"><span class="t1">These and also other kings, and gandhabbas along with their kings,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[32]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nāgas and Supaṇṇas</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Athāgu Nābhasā nāgā,</span>{97}<span class="t5"> Vesālā saha Tacchakā,</span></p><p class="p3"><span class="t1">Then came Nāgas from Nābhasa, and from Vesāli, and the Tacchakas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kambal’ Assatarā āgu, Pāyāgā saha ñātibhi. </span>[33]</p><p class="p3"><span class="t1">the Kambalas, and Assataras came, and (nāgas) from Pāyāga with their kin. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yāmunā Dhataraṭṭhā</span>{98}<span class="t5"> ca āgu nāgā yasassino,</span></p><p class="p3"><span class="t1">The Yāmuna, and the Dhataraṭṭha nāgas came, resplendent ones, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Erāvaṇo mahānāgo, so pāga samitiṁ vanaṁ. </span>[34]</p><p class="p3"><span class="t1">and Erāvaṇa, the great nāga, he also came to the gathering in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye nāgarāje sahasā haranti dibbā dijā pakkhivisuddhacakkhū -</span></p><p class="p3"><span class="t1">Those who carry away the nāga kings by force the divine, twice-born, winged ones, with clear vision - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vehāsayā te vanam-ajjhapattā, Citrā Supaṇṇā iti tesaṁ nāmāni. </span>[35]</p><p class="p3"><span class="t1">through the air they (came and) arrived in the wood, the Citras and Supaṇṇas, such are their names. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Abhayaṁ tadā nāgarājānam-āsi, supaṇṇato khemam-akāsi Buddho.</span></p><p class="p3"><span class="t1">But at that time the nāga kings were without fear, for the Buddha made them safe from the supaṇṇas. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">Saṇhāhi vācāhi upavhayantā, nāgā supaṇṇā saraṇam-agaṁsu Buddhaṁ.</span>{99}<span class="t20"> </span><span class="t31">[36]</span></p><p class="p3"><span class="t1">Calling on (one another) with gentle words, the nāgas and supaṇṇas took refuge in the Buddha. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Asuras</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jitā Vajirahatthena, samuddaṁ asurā sitā,</span></p>', '-123-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 124, '<p class="p3"><span class="t1">Defeated by Vajirahattha, the asuras live in the ocean, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhātaro Vāsavassete,</span><span class="t32"> </span>{100}<span class="t5"> iddhimanto yasassino, </span>[37]</p><p class="p3"><span class="t1">they are brothers of Vāsava, having psychic power, resplendent,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kālakañjā mahābhiṁsā, asurā Dānaveghasā,</span></p><p class="p3"><span class="t1">the very fearful Kālakañjas, the Dānaveghasa asuras, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vepacittī</span>{101}<span class="t5"> Sucittī ca, Pahārādo Namucī saha, </span>[38]</p><p class="p3"><span class="t1">Vepacitti and Sucitti, Pahārāda, together with Namuci, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">satañ-ca Baliputtānaṁ, sabbe Verocanāmakā.</span></p><p class="p3"><span class="t1">and a hundred of Bali’s sons, all of them named after Veroca. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sannayhitvā Baliṁ senaṁ Rāhubhaddam-upāgamuṁ:</span></p><p class="p3"><span class="t1">Having armed themselves, Bali’s army came to lucky Rāhu (and said): </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Samayo dāni bhaddante, bhikkhūnaṁ samitiṁ vanaṁ.” </span>[39]</p><p class="p3"><span class="t1">“Now is the time, reverend Sir, for the gathering of the monks in the wood.”  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Devas</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Āpo ca devā Paṭhavī ca,</span>{102}<span class="t5"> Tejo Vāyo tad-āgamuṁ,</span></p><p class="p3"><span class="t1">The Water gods, and the Earth gods, the Fire, and the Wind (gods all) came there, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Varuṇā Vāruṇā devā, Somo ca Yasasā saha, </span>[40]</p><p class="p3"><span class="t1">the Varuṇa, and Vāruṇa gods, Soma followed by Yasa, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mettākaruṇākāyikā āgu devā yasassino. </span></p><p class="p3"><span class="t1">and a host of Friendly and Compassionate gods came, resplendent ones. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dasete dasadhā kāyā, sabbe nānattavaṇṇino, </span>[41]</p><p class="p3"><span class="t1">All of these ten hosts, ten kinds (of gods), who are diverse in colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino. </span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent. </span></p>', '-124-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 125, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[42]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Veṇhū devā</span>{103}<span class="t5"> Sahalī ca Asamā ca duve Yamā.</span></p><p class="p3"><span class="t1">The Veṇhu and Sahali and Asama gods, and the two Yama gods (came). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Candassūpanisā</span>{104}<span class="t5"> devā Candam-āgu purakkhatvā. </span>[43]</p><p class="p3"><span class="t1">The gods who depend on the Moon came, with the Moon in front of them. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Suriyassūpanisā</span>{105}<span class="t5"> devā Suriyam-āgu</span>{106}<span class="t5"> purakkhatvā,</span></p><p class="p3"><span class="t1">The gods who depend on the Sun came, with the Sun in front of them, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nakkhattāni purakkhatvā, āgu mandavalāhakā. </span>[44]</p><p class="p3"><span class="t1">with the stars in front of them, also the foolish Rain Cloud (gods) came. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vasūnaṁ Vāsavo seṭṭho, Sakko pāga Purindado.</span></p><p class="p3"><span class="t1">Sakka also came, (who is called) Vāsava, the best of the Vasū, and Purindada. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dasete dasadhā kāyā, sabbe nānattavaṇṇino, </span>[45]</p><p class="p3"><span class="t1">All of these ten hosts, ten kinds (of gods), who are diverse in colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[46]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Athāgu Sahabhū devā, jalam-aggisikhāriva,</span></p><p class="p3"><span class="t1">Then came the Sahabhu gods, blazing forth, like the crest of a fire, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ariṭṭhakā ca Rojā ca Ummāpupphanibhāsino. </span>[47]</p><p class="p3"><span class="t1">and the Ariṭṭhakas and Rojas and the splendid Ummapuppha (gods). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Varuṇā Sahadhammā ca, Accutā ca Anejakā,</span></p><p class="p3"><span class="t1">The Varuṇas and Sahadhammas, the Accutas and Anejakas, </span></p>', '-125-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 126, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sūleyyarucirā</span>{107}<span class="t5"> āgu; āgu</span>{108}<span class="t5"> Vāsavanesino. </span>[48]</p><p class="p3"><span class="t1">the Sūleyyas and Ruciras (all) came; the Vāsavanesi (gods) came. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dasete dasadhā kāyā, sabbe nānattavaṇṇino,</span></p><p class="p3"><span class="t1">All of these ten hosts, ten kinds (of gods), who are diverse in colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[49]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Samānā Mahāsamānā, Mānusā Mānusuttamā,</span></p><p class="p3"><span class="t1">The Samānas, Mahasamānas, Mānusas, Mānusuttamas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Khiḍḍāpadūsikā āgu; āgu</span>{109}<span class="t5"> Manopadūsikā. </span>[50]</p><p class="p3"><span class="t1">and the Khiḍḍāpadūsikas came; the Manopadūsikas came. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Athāgu Harayo devā, ye ca Lohitavāsino.</span></p><p class="p3"><span class="t1">Then the Hari gods came, and those (known as) the Lohitavāsī. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pāragā Mahapāragā</span>{110}<span class="t5"> āgu devā yasassino. </span>[51]</p><p class="p3"><span class="t1">The Pāragas, and Mahapāragas came, gods who are resplendent. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dasete dasadhā kāyā, sabbe nānattavaṇṇino,</span></p><p class="p3"><span class="t1">All of these ten hosts, ten kinds (of gods), who are diverse in colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[52]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sukkā Karumhā</span>{111}<span class="t5"> Aruṇā āgu Veghanasā saha.</span></p><p class="p3"><span class="t1">The Sukkas, Karumhas, and Aruṇas came, with the Veghanasas. </span></p>', '-126-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 127, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Odātagayhā pāmokkhā āgu devā Vicakkhaṇā. </span>[53]</p><p class="p3"><span class="t1">The Vicakkhaṇa gods came with the Odātagayhas in front. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sadāmattā Hāragajā, Missakā ca yasassino.</span></p><p class="p3"><span class="t1">The Sadāmattas, Hāragajas, and the resplendent Missakas. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Thanayaṁ āga Pajjunno, yo disā abhivassati. </span>[54]</p><p class="p3"><span class="t1">Pajjunna came thundering, he who pours down rain in (all) directions. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dasete dasadhā kāyā, sabbe nānattavaṇṇino,</span></p><p class="p3"><span class="t1">All of these ten hosts, ten kinds (of gods), who are diverse in colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[55]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Khemiyā Tusitā Yāmā, Kaṭṭhakā ca yasassino.</span></p><p class="p3"><span class="t1">The Khemiyas, Tusitas, Yāmas,and resplendent Kaṭṭhakas (came). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Lambītakā Lāmaseṭṭhā Jotināmā ca Āsavā, </span>[56]</p><p class="p3"><span class="t1">The Lambītakas, Lāmaseṭṭhas, those called the Jotis, and Āsavas,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nimmāṇaratino āgu; athāgu Paranimmitā.</span></p><p class="p3"><span class="t1">and the Nimmāṇaratis came; then came the Paranimittas.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dasete dasadhā kāyā, sabbe nānattavaṇṇino, </span>[57]</p><p class="p3"><span class="t1">All of these ten hosts, ten kinds (of gods), who are diverse in colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhimanto, jutīmanto, vaṇṇavanto, yasassino,</span></p><p class="p3"><span class="t1">who have psychic power, who are bright, beautiful, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modamānā abhikkāmuṁ bhikkhūnaṁ samitiṁ vanaṁ. </span>[58]</p><p class="p3"><span class="t1">rejoicing they have drawn near the gathering of monks in the wood. </span></p>', '-127-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 128, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saṭṭhete devanikāyā, sabbe nānattavaṇṇino,</span></p><p class="p3"><span class="t1">All of these sixty hosts of gods, who are diverse in colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāmanvayena āgañchuṁ; ye caññe sadisā saha: </span>[59]</p><p class="p3"><span class="t1">in conformity with their names came; these along with others similar (thinking):  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Pavutthajātiṁ akhilaṁ, oghatiṇṇam-anāsavaṁ,</span></p><p class="p3"><span class="t1">° “We shall see the casteless (Saṅgha), unhindered, flood-crossers, pollution-free, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dakkhemoghataraṁ nāgaṁ, Candaṁ va asitātigaṁ. </span>[60]</p><p class="p3"><span class="t1">and the nāga, who is beyond the flood, who, like the Moon, has overcome the darkness. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Brahmās</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Subrahmā Paramatto ca, puttā iddhimato saha.</span></p><p class="p3"><span class="t1">Subrahmā and Paramatta, along with the sons of the powerful one (came). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sanaṅkumāro Tisso ca so pāga samitiṁ vanaṁ. </span>[61]</p><p class="p3"><span class="t1">Sanaṅkumāra and Tissa also came to the gathering in the wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sahassaṁ brahmalokānaṁ Mahābrahmābhitiṭṭhati,</span></p><p class="p3"><span class="t1">In the thousand brahma worlds a Great Brahma has arisen, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upapanno jutīmanto, bhismākāyo yasassi so. </span>[62]</p><p class="p3"><span class="t1">a bright one, who stands out, whose awesome body is resplendent.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dasettha Issarā āgu, paccekavasavattino,</span></p><p class="p3"><span class="t1">The ten Issarā (brahmās) came, who individually wield power, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tesañ-ca majjhato āga Hārito parivārito. </span>[63]</p><p class="p3"><span class="t1">and in the midst of them came Hārita with his entourage. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Māra and his Army </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Te ca sabbe abhikkante, sa-Inde deve sabrahmake,</span></p>', '-128-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 129, '<p class="p3"><span class="t1">Now when they had all drawn near, Inda with the gods and the brahmās,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mārasenā abhikkāmi:</span>{112}<span class="t5"> passa Kaṇhassa mandiyaṁ! </span>[64]</p><p class="p3"><span class="t1">Māra’s army also drew near: behold the foolishness of the Dark One!  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Etha gaṇhatha bandhatha, rāgena bandham-atthu ve,</span>{113}</p><p class="p3"><span class="t1">“Come now, seize them, and bind them (he said), let them be bound by passion, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">samantā parivāretha, mā vo muñcittha koci naṁ!” </span>[65]</p><p class="p3"><span class="t1">surround them on all sides, do not let anyone of them go free!”  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Iti tattha mahāseno kaṇhasenaṁ apesayī,</span>{114}</p><p class="p3"><span class="t1">Thus in that place the great army leader sent forth his dark army, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pāṇinā thalam-āhacca, saraṁ katvāna bheravaṁ, </span>[66]</p><p class="p3"><span class="t1">after striking the ground with his hand, and making a fearful noise,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yathā pāvussako megho, thanayanto savijjuko -</span></p><p class="p3"><span class="t1">like a storm cloud shedding rain, thundering, with flashes of lightning -  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tadā so paccudāvatti, saṅkuddho asayaṁvasī. </span>[67]</p><p class="p3"><span class="t1">but then he retreated, angry, and unable to control himself.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tañ-ca sabbaṁ abhiññāya vavakkhitvāna Cakkhumā, </span></p><p class="p3"><span class="t1">Knowing all that (was happening) the Visionary desired to speak, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tato āmantayī Satthā sāvake sāsane rate: </span>[68]</p><p class="p3"><span class="t1">therefore the Teacher addressed those disciples who delight in the teaching: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Mārasenā abhikkantā, te vijānātha bhikkhavo!”</span></p><p class="p3"><span class="t1">“Māra’s army has drawn near (to us), you should know who they are, monks!”  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Te ca ātappam-akaruṁ, sutvā Buddhassa sāsanaṁ.</span></p><p class="p3"><span class="t1">Then (those monks) became ardent, after hearing the Buddha’s teaching. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vītarāgehapakkāmuṁ,</span>{115}<span class="t5"> nesaṁ lomam-pi iñjayuṁ!</span>{116}<span class="t5"> </span>[69]</p>', '-129-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '24. Ālavakasuttaṁ The Discourse concerniṅg Ālavaka ', 'title', 130);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 130, '<p class="p3"><span class="t1">They withdrew from the passionless (monks), not one of their hairs was  </span></p><p class="p3"><span class="t1">shaken!  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Spoken by Māra</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sabbe vijitasaṅgāmā, bhayātītā</span>{117}<span class="t5"> yasassino,</span></p><p class="p3"><span class="t1">“They are all victorious in battle, beyond fear, and resplendent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">modanti saha bhūtehi, sāvakā te jane sutā” ti. </span>[70]</p><p class="p3"><span class="t1">those disciples, famous among men, along with (all) beings, are glad.”  </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>24. Ālavakasuttaṁ The Discourse concerniṅg Ālavaka </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ me sutaṁ: </span></p><p class="p3"><span class="t1">Thus I have heard: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Ālaviyaṁ viharati</span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling near Ālavī </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ālavakassa yakkhassa bhavane.</span></p><p class="p3"><span class="t1">in the domicile of the yakkha Ālavaka. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Ālavako yakkho yena Bhagavā tenupasaṅkami, </span></p><p class="p3"><span class="t1">Then the yakkha Ālavaka approached the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upasaṅkamitvā Bhagavantaṁ etad-avoca: </span></p><p class="p3"><span class="t1">and after approaching he said this to the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Nikkhama samaṇā!” ti, </span></p><p class="p3"><span class="t1">“Get out, ascetic!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sādhāvuso” ti Bhagavā nikkhami.</span></p><p class="p3"><span class="t1">“Yes, friend”, said the Fortunate One, and he got out.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Pavisa samaṇā!” ti,</span></p><p class="p3"><span class="t1">“Go in, ascetic!” </span></p>', '-130-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 131, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sādhāvuso” ti Bhagavā pāvisi.</span></p><p class="p3"><span class="t1">“Yes, friend”, said the Fortunate One, and he went in.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dutiyam-pi kho Ālavako yakkho Bhagavantaṁ etad-avoca:</span></p><p class="p3"><span class="t1">For a second time the yakkha Ālavaka said this to the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Nikkhama samaṇā!” ti, </span></p><p class="p3"><span class="t1">“Get out, ascetic!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sādhāvuso” ti Bhagavā nikkhami.</span></p><p class="p3"><span class="t1">“Yes, friend”, said the Fortunate One, and he got out.  </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">“Pavisa samaṇā!” ti,</span></p><p class="p3"><span class="t1">“Go in, ascetic!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sādhāvuso” ti Bhagavā pāvisi.</span></p><p class="p3"><span class="t1">“Yes, friend”, said the Fortunate One, and he went in.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tatiyam-pi kho Ālavako yakkho Bhagavantaṁ etad-avoca:</span></p><p class="p3"><span class="t1">For a third time the yakkha Ālavaka said this to the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Nikkhama samaṇā!” ti, </span></p><p class="p3"><span class="t1">“Get out, ascetic!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sādhāvuso” ti Bhagavā nikkhami.</span></p><p class="p3"><span class="t1">“Yes, friend”, said the Fortunate One, and he got out.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Pavisa samaṇā!” ti,</span></p><p class="p3"><span class="t1">“Go in, ascetic!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sādhāvuso” ti Bhagavā pāvisi.</span></p><p class="p3"><span class="t1">“Yes, friend”, said the Fortunate One, and he went in.  </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Catuttham-pi kho Ālavako yakkho Bhagavantaṁ etad-avoca:</span></p><p class="p3"><span class="t1">For a fourth time the yakkha Ālavaka said this to the Fortunate One: </span></p>', '-131-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 132, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Nikkhama samaṇā!” ti, </span></p><p class="p3"><span class="t1">“Get out, ascetic!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Na khvāhaṁ āvuso</span>{118}<span class="t5"> nikkhamissāmi,</span></p><p class="p3"><span class="t1">“I certainly will not get out, friend, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yaṁ te karaṇīyaṁ taṁ karohī” ti.</span></p><p class="p3"><span class="t1">so please do whatever it is you must do.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Pañhaṁ taṁ samaṇa pucchissāmi, sace me na vyākarissasi,</span></p><p class="p3"><span class="t1">“I will ask you a question, ascetic, and if you do not answer me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">cittaṁ vā te khipissāmi, hadayaṁ vā te phālessāmi, </span></p><p class="p3"><span class="t1">I will overthrow your mind, or split your heart, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pādesu vā gahetvā pāragaṅgāya khipissāmī” ti.</span></p><p class="p3"><span class="t1">or seize you by the feet and throw you across the river.”  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Na khvāhaṁ</span>{119}<span class="t5"> taṁ āvuso passāmi,</span></p><p class="p3"><span class="t1">“I certainly do not see, friend, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sadevake loke samārake sabrahmake, </span></p><p class="p3"><span class="t1">in the world with its gods, Māra, and Brahma, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sassamaṇabrāhmaṇiyā pajāya sadevamanussāya,</span></p><p class="p3"><span class="t1">in this generation with its ascetics and brahmins, princes and men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yo me cittaṁ vā khipeyya, hadayaṁ vā phāleyya,</span></p><p class="p3"><span class="t1">anyone who could overthrow my mind, or split my heart, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pādesu vā gahetvā pāragaṅgāya khipeyya.</span></p><p class="p3"><span class="t1">or seize me by the feet and throw me across the river. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Api ca tvaṁ āvuso puccha yad-ākaṅkhasī” ti. </span></p><p class="p3"><span class="t1">Still, you may ask what you want, friend.” </span></p>', '-132-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 133, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Ālavako yakkho Bhagavantaṁ gāthāya ajjhabhāsi: </span></p><p class="p3"><span class="t1">Then the yakkha Ālavaka recited a verse to the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Kiṁ sūdha vittaṁ purisassa seṭṭhaṁ? Kiṁ su suciṇṇo sukham-āvahāti?</span></p><p class="p3"><span class="t1">“What is the greatest wealth for a person here? What, when accomplished, brings about happiness? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">Kiṁ su have sādutaraṁ</span>{120}<span class="t14"> rasānaṁ? Kathaṁ jīviṁ jīvitam-āhu seṭṭhan?” ti </span>{121}<span class="t14"> </span><span class="t17">[1]</span></p><p class="p3"><span class="t1">What, for sure, is the sweetest of tastes? Living what life do they say is the greatest?” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Saddhīdha vittaṁ purisassa seṭṭhaṁ. Dhammo suciṇṇo sukham-āvahāti.</span></p><p class="p3"><span class="t1">“Confidence is a person’s greatest wealth here. The Dhamma, when accomplished, brings about happiness. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saccaṁ have sādutaraṁ rasānaṁ. Paññājīviṁ jīvitam-āhu seṭṭhan”-ti. </span>[2]</p><p class="p3"><span class="t1">Truth, for sure, is the sweetest of tastes. Living a wise life they say is the greatest.”  </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">“Kathaṁ su taratī oghaṁ? Kathaṁ su tarati</span>{122}<span class="t5"> aṇṇavaṁ?</span></p><p class="p3"><span class="t1">“How does one cross over the flood? How does one cross over the sea? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kathaṁ su dukkhaṁ acceti? Kathaṁ su parisujjhati?” </span>[3]</p><p class="p3"><span class="t1">How does one overcome suffering? How does one become purified?”  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Saddhāya taratī</span>{123}<span class="t5"> oghaṁ. Appamādena aṇṇavaṁ.</span></p><p class="p3"><span class="t1">“Through confidence one crosses the flood. Through heedfulness (one crosses) the sea. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Viriyena dukkhaṁ acceti. Paññāya parisujjhati.” </span>[4]</p><p class="p3"><span class="t1">Through energy one overcomes suffering. Through wisdom one is purified.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Kathaṁ su labhate paññaṁ? Kathaṁ su vindate dhanaṁ?</span></p><p class="p3"><span class="t1">“How is it one gains true wisdom? How is it one finds true wealth? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kathaṁ su kittiṁ pappoti? Kathaṁ mittāni ganthati? </span></p><p class="p3"><span class="t1">How does one acquire good repute? How does one bind friends (to  </span></p><p class="p3"><span class="t1">oneself)? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Asmā lokā paraṁ lokaṁ kathaṁ pecca na socati?” </span>[5]</p>', '-133-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 134, '<p class="p3"><span class="t1">How, after passing from this world to the next world, does one not grieve?”  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Saddahāno Arahataṁ Dhammaṁ Nibbānapattiyā,</span></p><p class="p3"><span class="t1">“Confident in the Worthy Ones’ Dhamma (taught) for attaining Nibbāna, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sussūsā labhate paññaṁ, appamatto vicakkhaṇo. </span>[6]</p><p class="p3"><span class="t1">one gains wisdom through wanting to hear it, heedfulness, and application. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Patirūpakārī dhuravā, uṭṭhātā vindate dhanaṁ.</span></p><p class="p3"><span class="t1">Doing what is suitable, bearing up, aroused, one finds true wealth. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saccena kittiṁ pappoti. Dadaṁ mittāni ganthati. </span>[7]</p><p class="p3"><span class="t1">Through truth one acquires good repute. (Through) giving one binds friends (to oneself).  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yassete caturo dhammā saddhassa gharamesino:</span></p><p class="p3"><span class="t1">That confident householder who has these four things: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saccaṁ, Dhammo, dhitī,</span>{124}<span class="t5"> cāgo, sa ve pecca na socati,</span></p><p class="p3"><span class="t1">truth, Dhamma, courage, liberality, after passing away he does not grieve, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asmā lokā paraṁ lokaṁ evaṁ pecca na socati.</span>{125}<span class="t5"> </span>[8]</p><p class="p3"><span class="t1">thus, after passing from this world to the next world, one does not grieve. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Iṅgha aññe pi pucchassu, puthū</span>{126}<span class="t5"> samaṇabrāhmaṇe, </span></p><p class="p3"><span class="t1">Come now, ask others also, the many ascetics and brahmins, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yadi saccā damā cāgā khantyā bhiyyo na vijjati.”</span>{127}<span class="t5"> </span>[9]</p><p class="p3"><span class="t1">if there is something more than truth, self-control, liberality, and patience.”  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Kathan nu dāni puccheyyaṁ, puthū samaṇabrāhmaṇe? </span></p><p class="p3"><span class="t1">“Why should I ask (others) now, the many ascetics and brahmins? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sohaṁ ajja pajānāmi yo attho</span>{128}<span class="t5"> samparāyiko. </span>[10]</p><p class="p3"><span class="t1">Today I have come to know what is the next life’s true welfare.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atthāya vata me Buddho vāsāy’ Ālavim-āgamī.</span>{129}</p>', '-134-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 135, '<p class="p3"><span class="t1">Indeed for my welfare did the Buddha come and spend time in Ālavī. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sohaṁ ajja pajānāmi yattha dinnaṁ mahapphalaṁ. </span>[11]</p><p class="p3"><span class="t1">When today I have come to know where that which is given bears great fruit. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">So ahaṁ vicarissāmi gāmā gāmaṁ purā puraṁ, </span></p><p class="p3"><span class="t1">I will roam about from village to village, city to city,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">namassamāno Sambuddhaṁ Dhammassa ca sudhammatan”-ti.</span><span class="t5"> </span>[12]</p><p class="p3"><span class="t1">revering the Sambuddha and the good qualities of the Dhamma.”  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ vatvā</span>{130}<span class="t5"> Ālavako yakkho Bhagavantaṁ etad-avoca:</span></p><p class="p3"><span class="t1">After saying this, the yakkha Ālavaka said this to the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Abhikkantaṁ bho Gotama! Abhikkantaṁ bho Gotama!</span></p><p class="p3"><span class="t1">“Excellent, reverend Gotama! Excellent, reverend Gotama! </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Seyyathā pi bho Gotama nikkujjitaṁ vā ukkujjeyya,</span></p><p class="p3"><span class="t1">Just as, reverend Gotama, one might set upright what has been overturned, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">paṭicchannaṁ vā vivareyya, mūḷhassa vā maggaṁ ācikkheyya,</span></p><p class="p3"><span class="t1">or open up what has been closed, or show a path to one who is lost, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">andhakāre vā telapajjotaṁ dhāreyya cakkhumanto rūpāni dakkhintī ti,</span></p><p class="p3"><span class="t1">or bear an oil lamp in the darkness so that one who has eyes can see forms, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evam-evaṁ bhotā Gotamena anekapariyāyena Dhammo pakāsito.</span></p><p class="p3"><span class="t1">just so has the Dhamma been made clear by reverend Gotama in more than one way. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Esāhaṁ bhavantaṁ Gotamaṁ saraṇaṁ gacchāmi, </span></p><p class="p3"><span class="t1">I go to the reverend Gotama for refuge, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dhammañ-ca bhikkhusaṅghañ-ca. </span></p><p class="p3"><span class="t1">and to the Dhamma, and to the Saṅgha of monks. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Upāsakaṁ maṁ bhavaṁ Gotamo dhāretu</span></p>', '-135-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '25. Kasībhāradvājasuttaṁ The Discourse concerning Kasībhāradvāja ', 'title', 136);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 136, '<p class="p3"><span class="t1">Please bear it in mind, reverend Gotama, that I am a lay disciple </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ajjatagge pāṇupetaṁ saraṇaṁ gatan”-ti. </span></p><p class="p3"><span class="t1">who has gone for refuge from today forward for as long as I have the breath of life.” </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>25. Kasībhāradvājasuttaṁ The Discourse concerning Kasībhāradvāja </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ me sutaṁ: </span></p><p class="p3"><span class="t1">Thus I have heard: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Magadhesu viharati</span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling amongst the Magadhans </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dakkhiṇāgirismiṁ Ekanālāyaṁ brāhmaṇagāme.</span></p><p class="p3"><span class="t1">on the Southern Hill near the brahmin village Ekanālā. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Tena kho pana samayena Kasībhāradvājassa brāhmaṇassa </span></p><p class="p3"><span class="t1">Then at that time the brahmin Kasībhāradvāja </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pañcamattāni naṅgalasatāni payuttāni honti vappakāle. </span></p><p class="p3"><span class="t1">had about five hundred ploughs yoked up during the sowing season. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">Atha kho Bhagavā pubbanhasamayaṁ nivāsetvā, pattacīvaram-ādāya,</span></p><p class="p3"><span class="t1">Then the Fortunate One, having dressed in the morning time, after picking up his bowl and robe, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yena Kasībhāradvājassa brāhmaṇassa kammanto tenupasaṅkami.</span>{131}</p><p class="p3"><span class="t1">approached the place where the brahmin Kasībhāradvāja was working. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">Tena kho pana samayena Kasībhāradvājassa brāhmaṇassa parivesanā vattati.</span></p><p class="p3"><span class="t1">Then at that time the brahmin Kasībhāradvāja’s food distribution was proceeding. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Bhagavā yena parivesanā tenupasaṅkami,</span></p><p class="p3"><span class="t1">Then the Fortunate One approached the food distribution, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upasaṅkamitvā ekam-antaṁ aṭṭhāsi.</span></p><p class="p3"><span class="t1">and after approaching he stood on one side. </span></p>', '-136-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 137, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t15">Addasā kho Kasībhāradvājo brāhmaṇo Bhagavantaṁ piṇḍāya ṭhitaṁ,</span></p><p class="p3"><span class="t1">The brahmin Kasībhāradvāja saw the Fortunate One standing for alms, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">disvāna Bhagavantaṁ etad-avoca: </span></p><p class="p3"><span class="t1">and after seeing him he said this to the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Ahaṁ kho samaṇa kasāmi ca vapāmi ca, </span></p><p class="p3"><span class="t1">“Ascetic, I plough and I sow,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kasitvā ca vapitvā ca bhuñjāmi.</span></p><p class="p3"><span class="t1">and after ploughing and sowing I eat. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Tvam-pi samaṇa kasassu ca vapassu ca, </span></p><p class="p3"><span class="t1">You also, ascetic, should plough and sow,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kasitvā ca vapitvā ca bhuñjassū” ti.</span></p><p class="p3"><span class="t1">and after ploughing and sowing you can eat.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t15">“Aham-pi kho brāhmaṇa kasāmi ca vapāmi ca, </span></p><p class="p3"><span class="t1">“Brahmin, I also plough and sow,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t15">kasitvā ca vapitvā ca bhuñjāmī” ti.</span></p><p class="p3"><span class="t1">and after ploughing and sowing I eat.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Na kho pana mayaṁ passāma bhoto Gotamassa </span></p><p class="p3"><span class="t1">“But we do not see the reverend Gotama’s </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yugaṁ vā naṅgalaṁ vā phālaṁ vā pācanaṁ vā balivadde vā,</span></p><p class="p3"><span class="t1">yoke or plough or ploughshare or goad or oxen, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">atha ca pana bhavaṁ Gotamo evam-āha: </span></p><p class="p3"><span class="t1">but still the reverend Gotama said this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">‘Aham-pi kho brāhmaṇa kasāmi ca vapāmi ca, </span></p><p class="p3"><span class="t1">‘Brahmin, I also plough and sow,  </span></p>', '-137-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 138, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">kasitvā ca vapitvā ca bhuñjāmī’ ” ti.</span></p><p class="p3"><span class="t1">and after ploughing and sowing I eat.’ ” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Kasībhāradvājo brāhmaṇo Bhagavantaṁ gāthāya ajjhabhāsi:</span></p><p class="p3"><span class="t1">Then the brahmin Kasībhāradvāja recited a verse to the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Kassako paṭijānāsi na ca passāma te kasiṁ.</span></p><p class="p3"><span class="t1">“You claim that you are a ploughman, but we do not see your ploughing. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kasiṁ no pucchito brūhi yathā jānemu te kasiṁ.”</span>{132}<span class="t5"> </span>[1]</p><p class="p3"><span class="t1">Please tell us about ploughing when asked so that we may know your ploughing.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Saddhā bījaṁ, tapo vuṭṭhi, paññā me yuganaṅgalaṁ, </span></p><p class="p3"><span class="t1">“Confidence the seed, austerity the rain, wisdom is my yoke and plough, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hiri īsā, mano yottaṁ, sati me phālapācanaṁ. </span>[2]</p><p class="p3"><span class="t1">conscience the pole, mind is the strap, mindfulness is my ploughshare and goad. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kāyagutto, vacīgutto, āhāre udare yato, </span></p><p class="p3"><span class="t1">With body guarded, speech guarded, and stomach restrained regarding food, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saccaṁ karomi niddānaṁ, soraccaṁ me pamocanaṁ. </span>[3]</p><p class="p3"><span class="t1">I make truth the mower, temperance the setting free (of the yoke).  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Viriyaṁ me dhuradhorayhaṁ, yogakkhemādhivāhanaṁ -</span></p><p class="p3"><span class="t1">Energy my beast of burden, the vehicle yoked to security - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gacchati anivattantaṁ yattha gantvā na socati. </span>[4]</p><p class="p3"><span class="t1">without turning back it goes to that place where, having gone, one does not grieve. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evam-esā kasī kaṭṭhā sā hoti amatapphalā,</span></p><p class="p3"><span class="t1">Thus this ploughing has been ploughed that produces the fruit of the deathless, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">etaṁ kasiṁ kasitvāna sabbadukkhā pamuccatī” ti. </span>[5]</p><p class="p3"><span class="t1">and after ploughing this ploughing one is set free from all suffering.” </span></p>', '-138-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 139, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Kasībhāradvājo brāhmaṇo</span></p><p class="p3"><span class="t1">Then the brahmin Kasībhāradvāja </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mahatiyā kaṁsapātiyā pāyāsaṁ vaḍḍhetvā Bhagavato upanāmesi:</span></p><p class="p3"><span class="t1">having arranged milk-rice in a large bronze bowl presented it to the Fortunate One, saying: </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">“Bhuñjatu bhavaṁ Gotamo pāyāsaṁ,</span></p><p class="p3"><span class="t1">“Eat this milk-rice, reverend Gotama, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kassako bhavaṁ, yaṁ hi bhavaṁ Gotamo</span>{133}<span class="t5"> </span></p><p class="p3"><span class="t1">the reverend one is a ploughman, for the reverend Gotama </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">amataphalaṁ kasiṁ kasatī” ti. </span></p><p class="p3"><span class="t1">ploughs the ploughing which has the deathless as fruit.”  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">“Gāthābhigītaṁ me abhojaneyyaṁ,</span>{134}<span class="t20"> sampassataṁ brāhmaṇa nesa Dhammo,</span></p><p class="p3"><span class="t1">“I will not eat what is gained through chanting verses, that is not the Dhamma, brahmin, of those who see, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gāthābhigītaṁ panudanti Buddhā, Dhamme satī brāhmaṇa vutti-r-esā. </span>[6]</p><p class="p3"><span class="t1">Buddhas reject what is gained through chanting verses, while Dhamma lasts, brahmin, this is their way of life.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Aññena ca kevalinaṁ</span>{135}<span class="t5"> mahesiṁ, khīṇāsavaṁ kukkuccavūpasantaṁ,</span></p><p class="p3"><span class="t1">Another great sage, who is fully adept, pollutant-free, at peace regarding worry (about wrong-doing), </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">annena pānena upaṭṭhahassu, khettaṁ hi taṁ puññapekhassa hotī” ti. </span>[7]</p><p class="p3"><span class="t1">attend to with food and drink, for that is the field for those who look for merit.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Atha kassa cāhaṁ bho Gotama imaṁ pāyāsaṁ dammī?” ti</span></p><p class="p3"><span class="t5">“</span>Then to whom, reverend Gotama, should I give this milk-rice?”</p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Na khvāhaṁ taṁ</span>{136}<span class="t5"> brāhmaṇa passāmi, sadevake loke samārake sabrahmake,</span></p><p class="p3"><span class="t1">“I certainly do not see, brahmin, in the world with its gods, Māra, and Brahma, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sassamaṇabrāhmaṇiyā pajāya sadevamanussāya,</span></p><p class="p3"><span class="t1">in this generation with its ascetics and brahmins, princes and men, </span></p>', '-139-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 140, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa so pāyāso bhutto sammā pariṇāmaṁ gaccheyya, </span></p><p class="p3"><span class="t1">anyone who, having eaten this milk-rice, could properly digest it, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">aññatra Tathāgatassa vā Tathāgatasāvakassa vā.</span></p><p class="p3"><span class="t1">except a Realised One or a Realised One’s disciple. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tena hi tvaṁ brāhmaṇa taṁ pāyāsaṁ appaharite vā chaḍḍehi,</span></p><p class="p3"><span class="t1">Therefore, brahmin, cast away that milk-rice where there is but little grass, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appāṇake vā udake opilāpehī” ti.</span></p><p class="p3"><span class="t1">or drop it into water which has no breathing creatures.”  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Kasībhāradvājo brāhmaṇo taṁ pāyāsaṁ appāṇake udake opilāpesi.</span></p><p class="p3"><span class="t1">Then the brahmin Kasībhāradvāja dropped that milk-rice into water which had no breathing creatures. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Atha kho so pāyāso udake pakkhitto </span></p><p class="p3"><span class="t1">Then that milk-rice being thrown into water </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">cicciṭāyati ciṭiciṭāyati sandhūpāyati sampadhūpāyati,</span></p><p class="p3"><span class="t1">did hiss and sizzle and steam and smoke, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">seyyathā pi nāma phālo divasasantatto udake pakkhitto </span></p><p class="p3"><span class="t1">just as a ploughshare heated for a day when thrown into water </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">cicciṭāyati ciṭiciṭāyati sandhūpāyati sampadhūpāyati, </span></p><p class="p3"><span class="t1">hisses and sizzles and steams and smokes, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">evam-eva so pāyāso udake pakkhitto </span></p><p class="p3"><span class="t1">so that milk-rice being thrown into water </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">cicciṭāyati ciṭiciṭāyati sandhūpāyati sampadhūpāyati.</span></p><p class="p3"><span class="t1">did hiss and sizzle and steam and smoke. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Kasībhāradvājo brāhmaṇo, saṁviggo, lomahaṭṭhajāto,</span></p><p class="p3"><span class="t1">Then the brahmin Kasībhāradvāja, anxious, and horrified, </span></p>', '-140-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 141, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yena Bhagavā tenupasaṅkami, </span></p><p class="p3"><span class="t1">approached the Fortunate One,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upasaṅkamitvā Bhagavato pādesu sirasā nipatitvā,</span></p><p class="p3"><span class="t1">and after approaching he fell down placing his head at the Fortunate One’s feet, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavantaṁ etad-avoca:</span></p><p class="p3"><span class="t1">and said this to the Fortunate One:  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Abhikkantaṁ bho Gotama! Abhikkantaṁ bho Gotama!</span></p><p class="p3"><span class="t1">“Excellent, reverend Gotama! Excellent, reverend Gotama! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Seyyathā pi bho Gotama nikkujjitaṁ vā ukkujjeyya,</span></p><p class="p3"><span class="t1">Just as, reverend Gotama, one might set upright what has been overturned, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">paṭicchannaṁ vā vivareyya, mūḷhassa vā maggaṁ ācikkheyya,</span></p><p class="p3"><span class="t1">or open up what has been closed, or show a path to one who is lost, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">andhakāre vā telapajjotaṁ dhāreyya cakkhumanto rūpāni dakkhintī ti,</span></p><p class="p3"><span class="t1">or bear an oil lamp in the darkness so that one who has eyes can see forms, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evam-evaṁ bhotā Gotamena anekapariyāyena Dhammo pakāsito.</span></p><p class="p3"><span class="t1">just so has the Dhamma been made clear by reverend Gotama in more than one way. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Esāhaṁ bhavantaṁ Gotamaṁ saraṇaṁ gacchāmi, </span></p><p class="p3"><span class="t1">I go to the reverend Gotama for refuge, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Dhammañ-ca bhikkhusaṅghañ-ca. </span></p><p class="p3"><span class="t1">and to the Dhamma, and to the Saṅgha of monks. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Labheyyāhaṁ bhoto Gotamassa santike pabbajjaṁ, </span></p><p class="p3"><span class="t1">May I receive the going forth in the presence of reverend Gotama,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">labheyyaṁ upasampadan” ti.</span></p><p class="p3"><span class="t1">may I receive the full ordination.” </span></p>', '-141-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 142, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Alattha kho Kasībhāradvājo brāhmaṇo </span></p><p class="p3"><span class="t1">The brahmin Kasībhāradvāja received </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavato santike pabbajjaṁ, alattha upasampadaṁ.</span></p><p class="p3"><span class="t1">the going forth in the presence of the Fortunate One, received full ordination. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Acirūpasampanno kho panāyasmā Bhāradvājo,</span></p><p class="p3"><span class="t1">Then not long after ordination, venerable Bhāradvāja, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">eko, vūpakaṭṭho, appamatto, ātāpī, pahitatto viharanto, </span></p><p class="p3"><span class="t1">while dwelling solitary, secluded, heedful, ardent, and resolute, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na cirasseva yassatthāya kulaputtā </span></p><p class="p3"><span class="t1">after no long time (attained) that good for which young gentlemen </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sammad-eva agārasmā anagāriyaṁ pabbajanti,</span></p><p class="p3"><span class="t1">rightly go forth from the house to the houseless life, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tad-anuttaraṁ brahmacariyapariyosānaṁ, </span></p><p class="p3"><span class="t1">that unsurpassed conclusion to the spiritual life, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">diṭṭheva dhamme sayaṁ abhiññā, sacchikatvā, upasampajja vihāsi. </span></p><p class="p3"><span class="t1">and dwelt having known, experienced, and attained it himself in this very life. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Khīṇā jāti </span></p><p class="p3"><span class="t1">Destroyed is (re)birth </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vusitaṁ brahmacariyaṁ </span></p><p class="p3"><span class="t1">accomplished is the spiritual life </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">kataṁ karaṇīyaṁ </span></p><p class="p3"><span class="t1">done is what ought to be done </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāparaṁ itthattāyā ti abbhaññāsi.</span></p><p class="p3"><span class="t1">there is no more of this mundane state - this he knew. </span></p>', '-142-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '26. Parābhavasuttaṁ The Discourse on Ruin ', 'title', 143);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 143, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Aññataro ca kho panāyasmā Bhāradvājo Arahataṁ ahosī ti. </span></p><p class="p3"><span class="t1">And venerable Bhāradvāja became another of the Worthy Ones.  </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>26. Parābhavasuttaṁ The Discourse on Ruin </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ me sutaṁ:</span></p><p class="p3"><span class="t1">Thus I have heard: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Sāvatthiyaṁ viharati </span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling near Sāvatthī </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jetavane Anāthapiṇḍikassa ārāme. </span></p><p class="p3"><span class="t1">at Anāthapiṇḍika’s grounds in Jeta’s Wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho aññatarā devatā abhikkantāya rattiyā,</span></p><p class="p3"><span class="t1">Then a certain god, towards the end of the night, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abhikkantavaṇṇā kevalakappaṁ Jetavanaṁ obhāsetvā,</span></p><p class="p3"><span class="t1">whose surpassing beauty lit up the whole of Jeta’s Wood, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yena Bhagavā tenupasaṅkami,</span></p><p class="p3"><span class="t1">approached the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upasaṅkamitvā Bhagavantaṁ abhivādetvā ekam-antaṁ aṭṭhāsi.</span></p><p class="p3"><span class="t1">and after approaching and worshipping the Fortunate One, he stood on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ekam-antaṁ ṭhitā kho sā devatā Bhagavantaṁ gāthāya ajjhabhāsi:</span></p><p class="p3"><span class="t1">While standing on one side that god recited a verse to the Fortunate One: </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">“Parābhavantaṁ purisaṁ mayaṁ pucchāma Gotamaṁ -</span></p><p class="p3"><span class="t1">“We ask (reverend) Gotama regarding the man going to ruin - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavantaṁ puṭṭham-āgamma, kiṁ parābhavato mukhaṁ?” </span>[1]</p><p class="p3"><span class="t1">we have come to ask the Fortunate One, what is the cause of ruination?” </span></p><p class="p3"><span class="t1">  </span></p>', '-143-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 144, '<p class="p3"><span class="t5">“Suvijāno bhavaṁ hoti, suvijāno parābhavo, </span></p><p class="p3"><span class="t1">“The thriving man is easy to know, the ruining man is easy to know, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dhammakāmo bhavaṁ hoti, Dhammadessī parābhavo.” </span>[2]</p><p class="p3"><span class="t1">the thriving man loves the Dhamma, the ruining man hates the Dhamma.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti hetaṁ vijānāma, paṭhamo so parābhavo.</span></p><p class="p3"><span class="t1">“Indeed it is so, this we know, this is the first ruin (of man). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dutiyaṁ Bhagavā brūhi, kiṁ parābhavato mukhaṁ?” </span>[3]</p><p class="p3"><span class="t1">Please tell us, Fortunate One, what is the second cause of ruin?” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Asantassa piyā honti, sante na kurute piyaṁ,</span></p><p class="p3"><span class="t1">“The bad are dear to him, he does not endear the good, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asataṁ Dhammaṁ roceti, taṁ parābhavato mukhaṁ.” </span>[4]</p><p class="p3"><span class="t1">he is pleased with the Dhamma of the bad, that is a cause of ruination.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti hetaṁ vijānāma, dutiyo so parābhavo.</span></p><p class="p3"><span class="t1">“Indeed it is so, this we know, this is the second ruin (of man). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tatiyaṁ Bhagavā brūhi, kiṁ parābhavato mukhaṁ?” </span>[5]</p><p class="p3"><span class="t1">Please tell us, Fortunate One, what is the third cause of ruin?” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Niddāsīlī sabhāsīlī, anuṭṭhātā ca yo naro, </span></p><p class="p3"><span class="t1">“That man who is fond of sleep, fond of society, unaroused, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">alaso kodhapaññāṇo, taṁ parābhavato mukhaṁ.”</span>[6]</p><p class="p3"><span class="t1">lazy, and marked by anger, that is a cause of ruination.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti hetaṁ vijānāma, tatiyo so parābhavo.</span></p><p class="p3"><span class="t1">“Indeed it is so, this we know, this is the third ruin (of man). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Catutthaṁ Bhagavā brūhi, kiṁ parābhavato mukhaṁ?” </span>[7]</p><p class="p3"><span class="t1">Please tell us, Fortunate One, what is the fourth cause of ruin?” </span></p><p class="p3"><span class="t1">  </span></p>', '-144-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 145, '<p class="p3"><span class="t5">“Yo mātaraṁ vā pitaraṁ vā jiṇṇakaṁ gatayobbanaṁ, </span></p><p class="p3"><span class="t1">“He who, when his mother or father has grown old, with their youth gone, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pahusanto</span>{137}<span class="t5"> na bharati, taṁ parābhavato mukhaṁ.” </span>[8]</p><p class="p3"><span class="t1">though having much, does not support them, that is a cause of ruination.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti hetaṁ vijānāma, catuttho so parābhavo.</span></p><p class="p3"><span class="t1">“Indeed it is so, this we know, this is the fourth ruin (of man). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pañcamaṁ Bhagavā brūhi, kiṁ parābhavato mukhaṁ?” </span>[9]</p><p class="p3"><span class="t1">Please tell us, Fortunate One, what is the fifth cause of ruin?” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Yo brāhmaṇaṁ vā samaṇaṁ vā, aññaṁ vā pi vaṇibbakaṁ</span></p><p class="p3"><span class="t1">° “He who deceives a brahmin, or an ascetic, or another mendicant </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">musāvādena vañceti, taṁ parābhavato mukhaṁ.” </span>[10]</p><p class="p3"><span class="t1">with words that are false, that is a cause of ruination.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti hetaṁ vijānāma, pañcamo so parābhavo.</span></p><p class="p3"><span class="t1">“Indeed it is so, this we know, this is the fifth ruin (of man). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Chaṭṭhamaṁ Bhagavā brūhi, kiṁ parābhavato mukhaṁ?” </span>[11]</p><p class="p3"><span class="t1">Please tell us, Fortunate One, what is the sixth cause of ruin?” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Pahūtavitto puriso, sahirañño sabhojano, </span></p><p class="p3"><span class="t1">“That man who has abundant wealth, who has gold, who has food, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">eko bhuñjati sādūni, taṁ parābhavato mukhaṁ.” </span>[12]</p><p class="p3"><span class="t1">but who eats his sweetmeats alone, that is a cause of ruination.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti hetaṁ vijānāma, chaṭṭhamo so parābhavo.</span></p><p class="p3"><span class="t1">“Indeed it is so, this we know, this is the sixth ruin (of man). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sattamaṁ Bhagavā brūhi, kiṁ parābhavato mukhaṁ?” </span>[13]</p><p class="p3"><span class="t1">Please tell us, Fortunate One, what is the seventh cause of ruin?” </span></p><p class="p3"><span class="t1">  </span></p>', '-145-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 146, '<p class="p3"><span class="t5">Jātitthaddho dhanatthaddho, gottatthaddho ca yo naro,</span></p><p class="p3"><span class="t1">“That man who is proud of his birth, his wealth, and his ancestry, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sañ-ñātiṁ atimaññeti, taṁ parābhavato mukhaṁ.” </span>[14]</p><p class="p3"><span class="t1">and who despises his kinsfolk, that is a cause of ruination.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti hetaṁ vijānāma, sattamo so parābhavo.</span></p><p class="p3"><span class="t1">“Indeed it is so, this we know, this is the seventh ruin (of man). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Aṭṭhamaṁ Bhagavā brūhi, kiṁ parābhavato mukhaṁ?” </span>[15]</p><p class="p3"><span class="t1">Please tell us, Fortunate One, what is the eighth cause of ruin?” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Itthidhutto, surādhutto, akkhadhutto ca yo naro, </span></p><p class="p3"><span class="t1">“That man who is a womanizer, a drunkard, and a gambler, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">laddhaṁ laddhaṁ vināseti, taṁ parābhavato mukhaṁ.” </span>[16]</p><p class="p3"><span class="t1">who destroys whatever is gained, that is a cause of ruination.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti hetaṁ vijānāma, aṭṭhamo so parābhavo.</span></p><p class="p3"><span class="t1">“Indeed it is so, this we know, this is the eighth ruin (of man). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Navamaṁ Bhagavā brūhi, kiṁ parābhavato mukhaṁ?” </span>[17]</p><p class="p3"><span class="t1">Please tell us, Fortunate One, what is the ninth cause of ruin?” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sehi dārehi</span>{138}<span class="t5"> ’santuṭṭho, vesiyāsu padissati,</span></p><p class="p3"><span class="t1">“(He who is) not satisfied with his own wives, and is seen with prostitutes, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dissati paradāresu, taṁ parābhavato mukhaṁ.” </span>[18]</p><p class="p3"><span class="t1">and is seen with other mens’ wives, that is a cause of ruination.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti hetaṁ vijānāma, navamo so parābhavo.</span></p><p class="p3"><span class="t1">“Indeed it is so, this we know, this is the ninth ruin (of man). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dasamaṁ Bhagavā brūhi, kiṁ parābhavato mukhaṁ?” </span>[19]</p><p class="p3"><span class="t1">Please tell us, Fortunate One, what is the tenth cause of ruin?” </span></p><p class="p3"><span class="t1">  </span></p>', '-146-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 147, '<p class="p3"><span class="t5">“Atītayobbano poso, āneti timbarutthaniṁ, </span></p><p class="p3"><span class="t1">“A man who, being past his youth, brings home a young girl (for his wife),{139} </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tassā issā na supati, taṁ parābhavato mukhaṁ.” </span>[20]</p><p class="p3"><span class="t1">and cannot sleep through jealousy, that is a cause of ruination.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti hetaṁ vijānāma, dasamo so parābhavo.</span></p><p class="p3"><span class="t1">“Indeed it is so, this we know, this is the tenth ruin (of man). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ekādasamaṁ Bhagavā brūhi, kiṁ parābhavato mukhaṁ?” </span>[21]</p><p class="p3"><span class="t1">Please tell us, Fortunate One, what is the eleventh cause of ruin?” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Itthisoṇḍiṁ vikiraṇiṁ, purisaṁ vā pi tādisaṁ,</span></p><p class="p3"><span class="t1">“A woman given to drink, a spendthrift, or a man of similar sort, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">issariyasmiṁ ṭhāpāpeti,</span>{140}<span class="t5"> taṁ parābhavato mukhaṁ.” </span>[22]</p><p class="p3"><span class="t1">appointing (such a one) as overlord, that is a cause of ruination.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Iti hetaṁ vijānāma, ekādasamo so parābhavo.</span></p><p class="p3"><span class="t1">“Indeed it is so, this we know, this is the eleventh ruin (of man). </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dvādasamaṁ Bhagavā brūhi, kiṁ parābhavato mukhaṁ?” </span>[23]</p><p class="p3"><span class="t1">Please tell us, Fortunate One, what is the twelfth cause of ruin?” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Appabhogo mahātaṇho, khattiye jāyate kule, </span></p><p class="p3"><span class="t1">“One who is poor, but has great craving, who is born in a family of nobles, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">so ca</span>{141}<span class="t5"> rajjaṁ patthayati, taṁ parābhavato mukhaṁ. </span>[24]</p><p class="p3"><span class="t1">and who wants to be king, that is a cause of ruination. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ete parābhave loke paṇḍito samavekkhiya, </span></p><p class="p3"><span class="t1">The wise man who reflects on (the cause of) these ruins in the world, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ariyo dassanasampanno, sa lokaṁ bhajate sivan” ti. </span>[25]</p><p class="p3"><span class="t1">a noble one, endowed with insight, partakes of the auspicious world.” </span></p><p class="p3"><span class="t1"> </span></p>', '-147-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '27. Vasalasuttaṁ{142} The Discourse on the Outcastes ', 'title', 148);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 148, '<p class="p3"><h3>27. Vasalasuttaṁ{142} The Discourse on the Outcastes </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t33">Evaṁ me sutaṁ:</span></p><p class="p3"><span class="t7">Thus I have heard:</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Sāvatthiyaṁ viharati </span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling near Sāvatthī </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jetavane Anāthapiṇḍikassa ārāme. </span></p><p class="p3"><span class="t1">at Anāthapiṇḍika’s grounds in Jeta’s Wood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Bhagavā pubbanhasamayaṁ nivāsetvā,</span></p><p class="p3"><span class="t1">Then the Fortunate One, having dressed in the morning time, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pattacīvaram-ādāya Sāvatthiyaṁ piṇḍāya pāvisi. </span></p><p class="p3"><span class="t1">after picking up his bowl and robe, entered Sāvatthī for alms. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Tena kho pana samayena Aggikabhāradvājassa brāhmaṇassa </span></p><p class="p3"><span class="t1">Then at that time in the brahmin Aggikabhāradvāja’s </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nivesane aggi pajjalito hoti āhuti paggahitā.</span>{143}<span class="t5"> </span></p><p class="p3"><span class="t1">residence the (sacrificial) fire was burning, and the offering had been taken up. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Atha kho Bhagavā Sāvatthiyaṁ sapadānaṁ piṇḍāya caramāno, </span></p><p class="p3"><span class="t1">Then, as the Fortunate One was going from house to house for alms in Sāvatthī, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yena Aggikabhāradvājassa brāhmaṇassa nivesanaṁ tenupasaṅkami. </span></p><p class="p3"><span class="t1">he approached the brahmin Aggikabhāradvāja’s residence. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t15">Addasā kho Aggikabhāradvājo brāhmaṇo </span></p><p class="p3"><span class="t1">The brahmin Aggikabhāradvāja </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t15">Bhagavantaṁ dūrato va</span>{144}<span class="t15"> āgacchantaṁ,</span></p><p class="p3"><span class="t1">saw the Fortunate One coming from afar, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">disvāna Bhagavantaṁ etad-avoca: </span></p>', '-148-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 149, '<p class="p3"><span class="t1">and having seen him, he said this to the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Tatreva muṇḍaka! Tatreva samaṇaka! Tatreva vasalaka! Tiṭṭhāhī!”ti.</span></p><p class="p3"><span class="t1">“Right there, shaveling! Right there, little ascetic! Right there, outcaste! Halt!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ vutte Bhagavā Aggikabhāradvājaṁ brāhmaṇaṁ etad-avoca: </span></p><p class="p3"><span class="t1">After this was said, the Fortunate One said this to the brahmin Aggikabhāradvāja: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Jānāsi pana tvaṁ brāhmaṇa vasalaṁ vā vasalakaraṇe vā dhamme?” ti</span></p><p class="p3"><span class="t5">“</span>But do you know, brahmin, what an outcaste is, or what things make one an outcaste?”</p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Na khvāhaṁ bho Gotama jānāmi, vasalaṁ vā vasalakaraṇe vā dhamme.</span></p><p class="p3"><span class="t1">“I certainly do not know, reverend Gotama, what an outcaste is, or what things make one an outcaste. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Sādhu me bhavaṁ Gotamo tathā Dhammaṁ desetu </span></p><p class="p3"><span class="t1">It would be good for me if the reverend Gotama would preach the Dhamma </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yathāhaṁ jāneyyaṁ vasalaṁ vā vasalakaraṇe vā dhamme” ti. </span></p><p class="p3"><span class="t1">so that I may know what an outcaste is, and what things make one an outcaste.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Tena hi brāhmaṇa suṇāhi, sādhukaṁ manasikarohi bhāsissāmī” ti.</span></p><p class="p3"><span class="t1">“Then listen, brahmin, apply your mind well, and I will speak.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Evaṁ bho,” ti kho Aggikabhāradvājo brāhmaṇo </span></p><p class="p3"><span class="t1">“Yes, reverend,” the brahmin Aggikabhāradvāja </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavato paccassosi, Bhagavā etad-avoca: </span></p><p class="p3"><span class="t1">replied to the Fortunate One, and the Fortunate One said this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Kodhano upanāhī ca, pāpamakkhī ca yo naro, </span></p><p class="p3"><span class="t1">“That man who is angry, who has enmity, is bad, and insolent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vipannadiṭṭhi māyāvī, taṁ jaññā vasalo iti. </span>[1]</p><p class="p3"><span class="t1">who has wrong views, and is deceitful, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p>', '-149-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 150, '<p class="p3"><span class="t5">Ekajaṁ vā dvijaṁ vā pi yodha pāṇāni hiṁsati, </span></p><p class="p3"><span class="t1">Whether once born, or twice born, he who is violent to creatures here, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa pāṇe dayā natthi, taṁ jaññā vasalo iti. </span>[2]</p><p class="p3"><span class="t1">who has no sympathy with creatures, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo hanti parirundhati gāmāni nigamāni ca,</span></p><p class="p3"><span class="t1">He who beseiges and destroys villages and market towns, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">niggāhako samaññāto, taṁ jaññā vasalo iti. </span>[3]</p><p class="p3"><span class="t1">who is well known as an oppressor, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gāme vā yadi vāraññe, yaṁ paresaṁ mamāyitaṁ, </span></p><p class="p3"><span class="t1">° (He who), whether in the village or the wilderness, takes by theft what is not given, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">theyyā adinnaṁ ādiyati,</span>{145}<span class="t5"> taṁ jaññā vasalo iti. </span>[4]</p><p class="p3"><span class="t1">those things loved by others, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo have iṇam-ādāya, cujjamāno palāyati, </span></p><p class="p3"><span class="t1">He who has taken on a debt, and when pressed (to pay it) runs away, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Na hi te iṇam-atthī” ti, taṁ jaññā vasalo iti. </span>[5]</p><p class="p3"><span class="t1">saying: “There is no debt due to you,” he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo ve kiñcikkhakamyatā, panthasmiṁ vajataṁ janaṁ, </span></p><p class="p3"><span class="t1">° He who desiring a trifle, after slaying a person going </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hantvā kiñcikkham-ādeti, taṁ jaññā vasalo iti. </span>[6]</p><p class="p3"><span class="t1">along the path, takes that trifle, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo attahetu parahetu dhanahetu ca yo naro,</span></p><p class="p3"><span class="t1">That man who, for his own sake, for the sake of others, or for wealth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sakkhipuṭṭho musā brūti, taṁ jaññā vasalo iti. </span>[7]</p><p class="p3"><span class="t1">when asked to bear witness, speaks falsely, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p>', '-150-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 151, '<p class="p3"><span class="t5">Yo ñātīnaṁ sakhānaṁ vā dāresu patidissati, </span></p><p class="p3"><span class="t1">He who is seen (misbehaving) with the wives of friends or relatives, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sahasā sampiyena vā, taṁ jaññā vasalo iti. </span>[8]</p><p class="p3"><span class="t1">whether forcibly, or with consent, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo mātaraṁ vā pitaraṁ vā jiṇṇakaṁ gatayobbanaṁ, </span></p><p class="p3"><span class="t1">He who, when his mother or father has grown old, with their youth gone, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pahusanto</span>{146}<span class="t5"> na bharati, taṁ jaññā vasalo iti. </span>[9]</p><p class="p3"><span class="t1">though having much, does not support them, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo mātaraṁ vā pitaraṁ vā, bhātaraṁ</span>{147}<span class="t5"> bhaginiṁ sasuṁ</span></p><p class="p3"><span class="t1">° He who strikes, or annoys with words, his mother or father, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hanti roseti vācāya, taṁ jaññā vasalo iti. </span>[10]</p><p class="p3"><span class="t1">brother or sister, or mother-in-law, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo atthaṁ pucchito santo, anattham-anusāsati,</span></p><p class="p3"><span class="t1">He who, when he is asked what is good, teaches that which is not good, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">paṭicchannena manteti, taṁ jaññā vasalo iti. </span>[11]</p><p class="p3"><span class="t1">who conceals things when he gives advice, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo katvā pāpakaṁ kammaṁ, “Mā maṁ jaññā” ti icchati, </span></p><p class="p3"><span class="t1">He who, having done a bad deed, longs thus: “May they not know about me,” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo paṭicchannakammanto, taṁ jaññā vasalo iti. </span>[12]</p><p class="p3"><span class="t1">he who conceals what he is doing, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo ve parakulaṁ gantvā, bhutvāna sucibhojanaṁ, </span></p><p class="p3"><span class="t1">He who, having gone to another’s family, and eaten good food, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">āgataṁ na paṭipūjeti, taṁ jaññā vasalo iti. </span>[13]</p><p class="p3"><span class="t1">does not return the honour when (his benefactor) comes, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p>', '-151-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 152, '<p class="p3"><span class="t5">Yo brāhmaṇaṁ vā samaṇaṁ vā, aññaṁ vā pi vaṇibbakaṁ</span></p><p class="p3"><span class="t1">° He who deceives a brahmin, or an ascetic, or another mendicant </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">musāvādena vañceti, taṁ jaññā vasalo iti. </span>[14]</p><p class="p3"><span class="t1">with words that are false, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo brāhmaṇaṁ vā samaṇaṁ vā, bhattakāle upaṭṭhite, </span></p><p class="p3"><span class="t1">° He who annoys with words a brahmin, or an ascetic, when mealtime is at hand, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">roseti vācā na ca deti, taṁ jaññā vasalo iti. </span>[15]</p><p class="p3"><span class="t1">and does not give (him food), he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Asataṁ yodha pabrūti, mohena paḷiguṇṭhito, </span></p><p class="p3"><span class="t1">He who speaks to those who are bad here, being entangled by delusion, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kiñcikkhaṁ nijigiṁsāno, taṁ jaññā vasalo iti. </span>[16]</p><p class="p3"><span class="t1">(because of) coveting a trifle, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo cattānaṁ samukkaṁse, parañ-ca-m-avajānati, </span></p><p class="p3"><span class="t1">He who (speaks and) extols himself, and yet despises another, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nihīno sena mānena, taṁ jaññā vasalo iti. </span>[17]</p><p class="p3"><span class="t1">who is debased through his own conceit, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Rosako, kadariyo ca pāpiccho, maccharī, saṭho,</span></p><p class="p3"><span class="t1">(He who) is annoying, stingy, who has bad longings, is selfish, crafty, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ahiriko, anottāpī,</span>{148}<span class="t5"> taṁ jaññā vasalo iti. </span>[18]</p><p class="p3"><span class="t1">unconscientious, and shameless, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo Buddhaṁ paribhāsati, atha vā tassa sāvakaṁ, </span></p><p class="p3"><span class="t1">He who abuses a Buddha, or else one of his disciples, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">paribbājaṁ gahaṭṭhaṁ vā, taṁ jaññā vasalo iti. </span>[19]</p><p class="p3"><span class="t1">a wanderer, or a householder, he one should know as an outcaste. </span></p><p class="p3"><span class="t1">  </span></p>', '-152-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 153, '<p class="p3"><span class="t5">Yo ve anarahā santo Arahaṁ paṭijānati, </span></p><p class="p3"><span class="t1">He who, not being a Worthy One claims that he is a Worthy One,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">coro sabrahmake loke, esa kho vasalādhamo.</span></p><p class="p3"><span class="t1">is a thief in the world with its brahmās, he is the worst of the outcastes. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ete kho vasalā vuttā, mayā vo ye pakāsitā. </span>[20]</p><p class="p3"><span class="t1">These, indeed, are said to be outcastes, those whom I have explained to you. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na jaccā vasalo hoti, na jaccā hoti brāhmaṇo, </span></p><p class="p3"><span class="t1">One is not an outcaste by birth, by birth one is not a brahmin, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kammanā vasalo hoti kammanā hoti brāhmaṇo. </span>[21]</p><p class="p3"><span class="t1">by deeds one becomes an outcaste, one becomes a brahmin by deeds. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tad-aminā pi jānātha,</span>{149}<span class="t5"> yathā medaṁ nidassanaṁ:</span></p><p class="p3"><span class="t1">You can know it also by this, as I (give you) this example: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">caṇḍālaputto Sopāko Mātaṅgo iti vissuto. </span>[22]</p><p class="p3"><span class="t1">Sopaka, an untouchable’s son, was renowned as Mātaṅga. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">So yasaṁ paramaṁ patto, Mātaṅgoyaṁ sudullabhaṁ, </span></p><p class="p3"><span class="t1">This Mātaṅga attained the highest repute, which was very hard to obtain, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">āgañchuṁ tassupaṭṭhānaṁ khattiyā brāhmaṇā bahū. </span>[23]</p><p class="p3"><span class="t1">and many nobles and brahmins came to attend on him. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">So devayānam-āruyha, virajaṁ so mahāpathaṁ, </span></p><p class="p3"><span class="t1">He ascended the way going to the gods, that dust-free great path, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kāmarāgaṁ virājetvā, brahmalokūpago ahu. </span></p><p class="p3"><span class="t1">and having put away sensual desire, he went to the brahma worlds. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na naṁ jāti nivāresi brahmalokūpapattiyā. </span>[24]</p><p class="p3"><span class="t1">Birth did not prevent him from being reborn in the brahma worlds. </span></p><p class="p3"><span class="t1">  </span></p>', '-153-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 154, '<p class="p3"><span class="t5">Ajjhāyakakule jātā, brāhmaṇā mantabandhuno,</span></p><p class="p3"><span class="t1">Brahmins, though born in a family of scholars, and acquainted with the lore, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te ca pāpesu kammesu abhiṇham-upadissare. </span>[25]</p><p class="p3"><span class="t1">are continuously to be seen (doing) deeds that are bad. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Diṭṭheva dhamme gārayhā samparāye ca duggatiṁ.</span>{150}<span class="t5"> </span></p><p class="p3"><span class="t1">Reproachable in this life, in the next life they have an unhappy destiny. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na te jāti nivāreti duggaccā garahāya vā. </span>[26]</p><p class="p3"><span class="t1">Birth does not prevent reproach or an unhappy destiny.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na jaccā vasalo hoti, na jaccā hoti brāhmaṇo, </span></p><p class="p3"><span class="t1">One is not an outcaste by birth, by birth one is not a brahmin, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kammanā vasalo hoti kammanā hoti brāhmaṇo” ti. </span>[27]</p><p class="p3"><span class="t1">by deeds one becomes an outcaste, one becomes a brahmin by deeds.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ vutte Aggikabhāradvājo brāhmaṇo Bhagavantaṁ etad-avoca: </span></p><p class="p3"><span class="t1">After this was said, the brahmin Aggikabhāradvāja said this to the Fortunate One:  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Abhikkantaṁ bho Gotama! Abhikkantaṁ bho Gotama!</span></p><p class="p3"><span class="t1">“Excellent, reverend Gotama! Excellent, reverend Gotama! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Seyyathā pi bho Gotama nikkujjitaṁ vā ukkujjeyya,</span></p><p class="p3"><span class="t1">Just as, reverend Gotama, one might set upright what has been overturned, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">paṭicchannaṁ vā vivareyya, mūḷhassa vā maggaṁ ācikkheyya,</span></p><p class="p3"><span class="t1">or open up what has been closed, or show a path to one who is lost, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">andhakāre vā telapajjotaṁ dhāreyya cakkhumanto rūpāni dakkhintī ti,</span></p><p class="p3"><span class="t1">or bear an oil lamp in the darkness so that one who has eyes can see forms, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evam-evaṁ bhotā Gotamena anekapariyāyena Dhammo pakāsito.</span>{151}</p><p class="p3"><span class="t1">just so has the Dhamma been made clear by reverend Gotama in more than one way. </span></p><p class="p3"><span class="t1">  </span></p>', '-154-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '28. Saccavibhaṅgasuttaṁ The Discourse giving the Analysis of the Truths ', 'title', 155);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 155, '<p class="p3"><span class="t5">Esāhaṁ bhavantaṁ</span>{152}<span class="t5"> Gotamaṁ saraṇaṁ gacchāmi, </span></p><p class="p3"><span class="t1">I go to the reverend Gotama for refuge, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dhammañ-ca bhikkhusaṅghañ-ca. </span></p><p class="p3"><span class="t1">and to the Dhamma, and to the Saṅgha of monks. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Upāsakaṁ maṁ bhavaṁ Gotamo dhāretu</span></p><p class="p3"><span class="t1">Please bear it in mind, reverend Gotama, that I am a lay disciple </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ajjatagge</span>{153}<span class="t5"> pāṇupetaṁ saraṇaṁ gatan”-ti. </span></p><p class="p3"><span class="t1">who has gone for refuge from today forward for as long as I have the breath of life.” </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>28. Saccavibhaṅgasuttaṁ The Discourse giving the Analysis of the Truths </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ me sutaṁ:</span></p><p class="p3"><span class="t1">Thus I have heard: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Bārāṇasiyaṁ viharati Isipatane Migadāye. </span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling near Bārāṇasī in the Deer Park at Isipatana. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tatra kho Bhagavā bhikkhū āmantesi:</span></p><p class="p3"><span class="t1">There it was that the Fortunate One addressed the monks, saying: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Bhikkhavo!” ti, “Bhadante!” ti te bhikkhū Bhagavato paccassosuṁ,</span></p><p class="p3"><span class="t1">“Monks!”, “Reverend Sir!” those monks replied to the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavā etad-avoca:</span></p><p class="p3"><span class="t1">and the Fortunate One said this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Tathāgatena bhikkhave Arahatā Sammāsambuddhena,</span></p><p class="p3"><span class="t1">“The Realised One, monks, the Worthy One, the Perfect Sambuddha, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p>', '-155-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 156, '<p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmiṁ.</span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yad-idaṁ catunnaṁ ariyasaccānaṁ ācikkhanā, </span></p><p class="p3"><span class="t1">° That is to say, the explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">desanā, paññāpanā,</span>{154}<span class="t20"> paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkammaṁ. </span></p><p class="p3"><span class="t1">opening up, analysing, and making plain, of the four noble truths. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamesaṁ catunnaṁ?</span></p><p class="p3"><span class="t1">Of which four? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dukkhassa ariyasaccassa ācikkhanā, </span></p><p class="p3"><span class="t1">° The explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">desanā, paññāpanā, paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkammaṁ. </span></p><p class="p3"><span class="t1">opening up, analysing, and making plain, of the noble truth of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dukkhasamudayassa ariyasaccassa ācikkhanā, </span></p><p class="p3"><span class="t1">° The explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">desanā, paññāpanā, paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkammaṁ. </span></p><p class="p3"><span class="t1">opening up, analysing, and making plain, of the noble truth of the arising of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dukkhanirodhassa ariyasaccassa ācikkhanā, </span></p><p class="p3"><span class="t1">° The explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t20">desanā, paññāpanā, paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkammaṁ. </span></p><p class="p3"><span class="t1">opening up, analysing, and making plain, of the noble truth of the cessation of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dukkhanirodhagāminiyā paṭipadāya</span>{155}<span class="t5"> ariyasaccassa ācikkhanā, </span></p>', '-156-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 157, '<p class="p3"><span class="t1">° The explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">desanā, paññāpanā, paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkammaṁ. </span></p><p class="p3"><span class="t1">opening up, analysing, and making plain, of the noble truth of the practice going to the cessation of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Tathāgatena bhikkhave Arahatā Sammāsambuddhena,</span></p><p class="p3"><span class="t1">“The Realised One, monks, the Worthy One, the Perfect Sambuddha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmiṁ.</span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yad-idaṁ imesaṁ catunnaṁ ariyasaccānaṁ ācikkhanā, </span></p><p class="p3"><span class="t1">° That is to say, the explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">desanā, paññāpanā, paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkammaṁ. </span></p><p class="p3"><span class="t1">opening up, analysing, and making plain, of these four noble truths. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sevetha bhikkhave Sāriputtamoggallāne, </span></p><p class="p3"><span class="t1">Associate with Sāriputta and Moggallāna, monks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhajatha bhikkhave Sāriputtamoggallāne,</span></p><p class="p3"><span class="t1">keep company with Sāriputta and Moggallāna, monks, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">paṇḍitā bhikkhū anuggāhakā sabrahmacārīnaṁ. </span></p><p class="p3"><span class="t1">they are wise monks who assist those living the spiritual life. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Seyyathā pi bhikkhave janettī evaṁ Sāriputto,</span></p>', '-157-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 158, '<p class="p3"><span class="t1">Sāriputta is just like one who gives birth, monks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">seyyathā pi jātassa āpādetā evaṁ Moggallāno;</span></p><p class="p3"><span class="t1">Moggallāna is just like a wet nurse to the one new-born, monks; </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sāriputto bhikkhave sotāpattiphale vineti,</span></p><p class="p3"><span class="t1">Sāriputta guides to the fruit of stream entry, monks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Moggallāno uttamatthe vineti.</span></p><p class="p3"><span class="t1">Moggallāna guides to the supreme good. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sāriputto bhikkhave pahoti </span></p><p class="p3"><span class="t1">Sāriputta is able, monks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">cattāri ariyasaccāni vitthārena ācikkhituṁ desetuṁ</span></p><p class="p3"><span class="t1">° to explain, reveal, make known, set forth, open up, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">paññāpetuṁ</span>{156}<span class="t5"> paṭṭhapetuṁ vivarituṁ vibhajituṁ uttānīkātun”-ti.</span></p><p class="p3"><span class="t1">analyse, and make plain, the four noble truths in detail.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-avoca Bhagavā, </span></p><p class="p3"><span class="t1">The Fortunate One said this, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">idaṁ vatvā Sugato uṭṭhāyāsanā vihāraṁ pāvisi.</span></p><p class="p3"><span class="t1">and after saying this, the Happy One rose from his seat and went into his living quarters. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tatra kho āyasmā Sāriputto acirapakkantassa Bhagavato, </span></p><p class="p3"><span class="t1">Then venerable Sāriputta, not long after the Fortunate One had gone, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhikkhū āmantesi: “Āvuso bhikkhavo!” ti. </span></p><p class="p3"><span class="t1">addressed the monks, saying: “Venerable monks!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Āvuso!” ti kho te bhikkhū āyasmato Sāriputtassa paccassosuṁ,</span></p><p class="p3"><span class="t1">“Venerable friend!” those monks replied to venerable Sāriputta, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">āyasmā Sāriputto etad-avoca:</span></p>', '-158-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 159, '<p class="p3"><span class="t1">and venerable Sāriputta said this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Tathāgatena āvuso Arahatā Sammāsambuddhena,</span></p><p class="p3"><span class="t1">“The Realised One, friends, the Worthy One, the Perfect Sambuddha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmiṁ.</span></p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yad-idaṁ catunnaṁ ariyasaccānaṁ ācikkhanā, </span></p><p class="p3"><span class="t1">° That is to say, the explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">desanā, paññāpanā, paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkammaṁ. </span></p><p class="p3"><span class="t1">opening up, analysing, and making plain, of the four noble truths. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamesaṁ catunnaṁ?</span></p><p class="p3"><span class="t1">Of which four? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dukkhassa ariyasaccassa ācikkhanā, </span></p><p class="p3"><span class="t1">° The explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">desanā, paññāpanā, paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkammaṁ. </span></p><p class="p3"><span class="t1">opening up, analysing, and making plain, of the noble truth of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dukkhasamudayassa ariyasaccassa ācikkhanā, </span></p><p class="p3"><span class="t1">° The explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">desanā, paññāpanā, paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkammaṁ. </span></p>', '-159-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 160, '<p class="p3"><span class="t1">opening up, analysing, and making plain, of the noble truth of the arising of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dukkhanirodhassa ariyasaccassa ācikkhanā, </span></p><p class="p3"><span class="t1">° The explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">desanā, paññāpanā, paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkammaṁ. </span></p><p class="p3"><span class="t1">opening up, analysing, and making plain, of the noble truth of the cessation of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dukkhanirodhagāminiyā paṭipadāya ariyasaccassa ācikkhanā, </span></p><p class="p3"><span class="t1">° The explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">desanā, paññāpanā, paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkammaṁ. </span></p><p class="p3"><span class="t1">opening up, analysing, and making plain, of the noble truth of the practice going to the cessation of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The First Noble Truth and its Analysis</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamañ-cāvuso dukkhaṁ ariyasaccaṁ?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is the noble truth of suffering? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jāti pi dukkhā</span></p><p class="p3"><span class="t1">Birth is suffering </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">jarā pi dukkhā</span></p><p class="p3"><span class="t1">also<span class="t5"> </span>old age is suffering </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vyādhi pi dukkho</span>{157}</p><p class="p3"><span class="t1">also<span class="t5"> </span>sickness is suffering </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">maraṇam-pi dukkhaṁ</span></p><p class="p3"><span class="t1">also<span class="t5"> </span>death is suffering </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sokaparidevadukkhadomanassupāyāsā pi dukkhā</span>{158}</p><p class="p3"><span class="t1">also<span class="t5"> </span>grief, lamentation, pain, sorrow, and despair, is suffering </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yam-picchaṁ na labhati tam-pi dukkhaṁ</span>{159}</p><p class="p3"><span class="t1">also<span class="t5"> </span>not to obtain what one longs for is suffering </span></p>', '-160-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 161, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saṅkhittena pañcupādānakkhandhā dukkhā.</span></p><p class="p3"><span class="t1">in brief, the five constituent groups (of mind and body) that provide fuel for attachment are suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamā cāvuso jāti?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is birth? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yā tesaṁ tesaṁ sattānaṁ tamhi tamhi sattanikāye </span></p><p class="p3"><span class="t1">For the various beings in the various classes of beings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">jāti, sañjāti, okkanti, abhinibbatti;</span>{160}<span class="t5"> </span></p><p class="p3"><span class="t1">there is birth, being born, appearing, turning up; </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">khandhānaṁ pātubhāvo, āyatanānaṁ paṭilābho: </span></p><p class="p3"><span class="t1">the manifestation of the constituent groups (of mind and body), the acquisition of the sense spheres: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">ayaṁ vuccatāvuso jāti.</span></p><p class="p3"><span class="t1">this venerable friends, is called birth. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">Katamā cāvuso jarā?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is old age? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">Yā tesaṁ tesaṁ sattānaṁ tamhi tamhi sattanikāye </span></p><p class="p3"><span class="t1">For the various beings in the various classes of beings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">jarā, jīraṇatā, khaṇḍiccaṁ, pāliccaṁ, valittacatā; </span></p><p class="p3"><span class="t1">there is aging, agedness, broken teeth, greying hair, and wrinkled skin; </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">āyuno saṁhāni, indriyānaṁ paripāko:</span></p><p class="p3"><span class="t1">the dwindling away of the life span, the decay of the sense faculties: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">ayaṁ vuccatāvuso jarā.</span></p><p class="p3"><span class="t1">this venerable friends, is called old age. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">Katamañ-cāvuso maraṇaṁ?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is death? </span></p>', '-161-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 162, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">Yā</span>{161}<span class="t34"> tesaṁ tesaṁ sattānaṁ tamhā tamhā sattanikāyā </span></p><p class="p3"><span class="t1">For the various beings in the various classes of beings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">cuti, cavanatā, bhedo, antaradhānaṁ, maccu, maraṇaṁ, kālakiriyā; </span></p><p class="p3"><span class="t1">there is a fall, a falling away, a breaking up, a disappearance, a dying, a death, a making of time; </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">khandhānaṁ bhedo, kalebarassa nikkhepo:</span></p><p class="p3"><span class="t1">the break up of the constituent groups (of mind and body), the throwing off of the body, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">idaṁ vuccatāvuso maraṇaṁ.</span></p><p class="p3"><span class="t1">this, venerable friends, is called death. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">Katamo cāvuso soko?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is grief? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">Yo kho āvuso aññataraññatarena vyasanena samannāgatassa,</span></p><p class="p3"><span class="t1">For he who has, venerable friends, some sort of misfortune or other, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t34">aññataraññatarena dukkhadhammena phuṭṭhassa,</span></p><p class="p3"><span class="t1">who is touched by some sort of pain or another, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">soko, socanā, socitattaṁ, antosoko, antoparisoko:</span></p><p class="p3"><span class="t1">there is grief, grieving, the state of grieving, inner grief, great inner grief: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ vuccatāvuso soko.</span></p><p class="p3"><span class="t1">this, venerable friends, is called grief. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamo cāvuso paridevo?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is lamenation? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo kho āvuso aññataraññatarena vyasanena samannāgatassa,</span></p><p class="p3"><span class="t1">For he who has, venerable friends, some sort of misfortune or other, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">aññataraññatarena dukkhadhammena phuṭṭhassa,</span></p><p class="p3"><span class="t1">who is touched by some sort of pain or another, </span></p>', '-162-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 163, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ādevo, paridevo, ādevanā, paridevanā, ādevitattaṁ, paridevitattaṁ:</span></p><p class="p3"><span class="t1">there are laments, great laments, lamenting, great lamenting, the state of lamenting, great lamentation: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ vuccatāvuso paridevo.</span></p><p class="p3"><span class="t1">this, venerable friends, is called lamentation. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamañ-cāvuso dukkhaṁ?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is pain? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ kho āvuso kāyikaṁ dukkhaṁ, kāyikaṁ asātaṁ,</span>{162}</p><p class="p3"><span class="t1">That, venerable friends, which is bodily pain, bodily disagreeableness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kāyasamphassajaṁ dukkhaṁ, asātaṁ vedayitaṁ:</span></p><p class="p3"><span class="t1">pain born of contact with the body, disagreeable feeling: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">idaṁ vuccatāvuso dukkhaṁ.</span></p><p class="p3"><span class="t1">this, venerable friends, is called pain. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamañ-cāvuso domanassaṁ?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is sorrow? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ kho āvuso cetasikaṁ dukkhaṁ, cetasikaṁ asātaṁ,</span>{163}</p><p class="p3"><span class="t1">That, venerable friends, which is mental pain, mental disagreeableness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vedayitaṁ manosamphassajaṁ dukkhaṁ, asātaṁ vedayitaṁ:</span></p><p class="p3"><span class="t1">pain born of contact with the mind, disagreeable feeling: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">idaṁ vuccatāvuso domanassaṁ. </span></p><p class="p3"><span class="t1">this, venerable friends, is called sorrow. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamo cāvuso upāyāso?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is despair? </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Yo kho āvuso aññataraññatarena vyasanena samannāgatassa,</span></p><p class="p3"><span class="t1">For he who has, venerable friends, some sort of misfortune or other, </span></p>', '-163-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 164, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">aññataraññatarena dukkhadhammena phuṭṭhassa,</span></p><p class="p3"><span class="t1">who is touched by some sort of pain or another, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">āyāso, upāyāso, āyāsitattaṁ, upāyāsitattaṁ:</span></p><p class="p3"><span class="t1">there is desponding, despairing, despondency, despair: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ vuccatāvuso upāyāso.</span></p><p class="p3"><span class="t1">this, venerable friends, is called despair. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamañ-cāvuso yam-picchaṁ na labhati tam-pi dukkhaṁ?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is the suffering from not obtaining what one longs for? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jātidhammānaṁ āvuso sattānaṁ evaṁ icchā uppajjati: </span></p><p class="p3"><span class="t1">To those beings subject to birth, venerable friends, this longing arises: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Aho vata mayaṁ na jātidhammā assāma, </span></p><p class="p3"><span class="t1">“Oh, might we not be subject to birth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na ca vata no jāti āgaccheyyā!” ti </span></p><p class="p3"><span class="t1">and may birth surely not come to us!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na kho panetaṁ icchāya pattabbaṁ:</span></p><p class="p3"><span class="t1">But that cannot be attained merely by longing for it: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">idaṁ pi yam-picchaṁ na labhati tam-pi dukkhaṁ. </span></p><p class="p3"><span class="t1">this is the suffering from not obtaining what one longs for. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jarādhammānaṁ āvuso sattānaṁ evaṁ icchā uppajjati: </span></p><p class="p3"><span class="t1">To those beings subject to old age, venerable friends, this longing arises: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Aho vata mayaṁ na jarādhammā assāma, </span></p><p class="p3"><span class="t1">“Oh, might we not be subject to old age, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na ca vata no jarā āgaccheyyā!” ti </span></p><p class="p3"><span class="t1">may old age surely not come to us!” </span></p>', '-164-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 165, '<p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Na kho panetaṁ icchāya pattabbaṁ:</span></p><p class="p3"><span class="t1">But that cannot be attained merely by longing for it: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">idaṁ pi yam-picchaṁ na labhati tam-pi dukkhaṁ. </span></p><p class="p3"><span class="t1">this is the suffering from not obtaining what one longs for. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vyādhidhammānaṁ āvuso sattānaṁ evaṁ icchā uppajjati: </span></p><p class="p3"><span class="t1">To those beings subject to sickness, venerable friends, this longing arises: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Aho vata mayaṁ na vyādhidhammā assāma, </span></p><p class="p3"><span class="t1">“Oh, might we not be subject to sickness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na ca vata no vyādhi āgaccheyyā!” ti </span></p><p class="p3"><span class="t1">may old age surely not come to us!” </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Na kho panetaṁ icchāya pattabbaṁ:</span></p><p class="p3"><span class="t1">But that cannot be attained merely by longing for it: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">idaṁ pi yam-picchaṁ na labhati tam-pi dukkhaṁ. </span></p><p class="p3"><span class="t1">this is the suffering from not obtaining what one longs for. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Maraṇadhammānaṁ āvuso sattānaṁ evaṁ icchā uppajjati: </span></p><p class="p3"><span class="t1">To those beings subject to death, venerable friends, this longing arises: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Aho vata mayaṁ na maraṇadhammā assāma, </span></p><p class="p3"><span class="t1">“Oh, might we not be subject to death, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na ca vata no maraṇaṁ āgaccheyyā!” ti </span></p><p class="p3"><span class="t1">may death surely not come to us!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na kho panetaṁ icchāya pattabbaṁ:</span></p><p class="p3"><span class="t1">But that cannot be attained merely by longing for it: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">idaṁ pi yam-picchaṁ na labhati tam-pi dukkhaṁ. </span></p><p class="p3"><span class="t1">this is the suffering from not obtaining what one longs for. </span></p>', '-165-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 166, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sokaparidevadukkhadomanassupāyāsadhammānaṁ</span></p><p class="p3"><span class="t1">To those beings subject to grief, lamentation, pain, sorrow, and despair,  </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">āvuso sattānaṁ evaṁ icchā uppajjati: </span></p><p class="p3"><span class="t1">venerable friends, this longing arises: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Aho vata mayaṁ na sokaparidevadukkha-</span></p><p class="p3"><span class="t1">° “Oh, might we not be subject to grief, lamentation, pain, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">domanassupāyāsadhammā assāma,</span></p><p class="p3"><span class="t1">sorrow, and despair, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na ca vata no sokaparidevadukkha-</span></p><p class="p3"><span class="t1">may grief, lamentation, pain, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">domanassupāyāsa āgaccheyyun!”-ti. </span></p><p class="p3"><span class="t1">sorrow, and despair surely not come to us!” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na kho panetaṁ icchāya pattabbaṁ:</span></p><p class="p3"><span class="t1">But that cannot be attained merely by longing for it: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">idaṁ pi yam-picchaṁ na labhati tam-pi dukkhaṁ. </span></p><p class="p3"><span class="t1">this is the suffering from not obtaining what one longs for. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamā</span>{164}<span class="t5"> cāvuso saṅkhittena pañcupādānakkhandhā dukkhā?</span></p><p class="p3"><span class="t1">Now what, venerable friends, in brief, are the constituent groups (of mind and body) that provide fuel for attachment that are suffering? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Seyyathīdaṁ: </span></p><p class="p3"><span class="t1">They are as follows: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">rūpūpādānakkhandho</span></p><p class="p3"><span class="t1">the form constituent group that is attached to  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vedanūpādānakkhandho </span></p><p class="p3"><span class="t1">the feelings constituent group that is attached to  </span></p>', '-166-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 167, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saññūpādānakkhandho</span></p><p class="p3"><span class="t1">the perceptions constituent group that is attached to  </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">saṅkhārūpādānakkhandho</span></p><p class="p3"><span class="t1">the (mental) processes constituent group that is attached to  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">viññāṇūpādānakkhandho. </span></p><p class="p3"><span class="t1">the consciousness constituent group that is attached to.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ime vuccantāvuso saṅkhittena pañcupādānakkhandhā dukkhā.</span></p><p class="p3"><span class="t1">These, venerable friends, in brief, are called the constituent groups (of mind and body) that provide fuel for attachment and that are suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idaṁ vuccatāvuso dukkhaṁ ariyasaccaṁ.</span></p><p class="p3"><span class="t1">This, venerable friends, is called the noble truth of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The Second Noble Truth</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamañ-cāvuso, dukkhasamudayaṁ</span>{165}<span class="t5"> ariyasaccaṁ?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is the noble truth of the arising of suffering? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yā yaṁ taṇhā ponobhavikā, </span></p><p class="p3"><span class="t1">It is that craving which leads to continuation in existence, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nandirāgasahagatā, tatratatrābhinandinī, seyyathīdaṁ: </span></p><p class="p3"><span class="t1">which is connected with enjoyment and passion, greatly enjoying this and that, as follows: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kāmataṇhā </span></p><p class="p3"><span class="t1">craving for sense pleasures </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhavataṇhā </span></p><p class="p3"><span class="t1">craving for continuation </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">vibhavataṇhā. </span></p><p class="p3"><span class="t1">craving for discontinuation. </span></p><p class="p3"><span class="t1">  </span></p>', '-167-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 168, '<p class="p3"><span class="t5">Idaṁ vuccatāvuso dukkhasamudayaṁ ariyasaccaṁ.</span></p><p class="p3"><span class="t1">This, venerable friends, is called the noble truth of the arising of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The Third Noble Truth</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamañ-cāvuso dukkhanirodhaṁ</span>{166}<span class="t5"> ariyasaccaṁ?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is the noble truth of the cessation of suffering? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo tassā yeva taṇhāya asesavirāganirodho -</span></p><p class="p3"><span class="t1">It is the complete fading away and cessation without remainder of that craving - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">cāgo, paṭinissaggo, mutti, anālayo. </span></p><p class="p3"><span class="t1">liberation, letting go, release, and non-adherence. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idaṁ vuccatāvuso dukkhanirodhaṁ ariyasaccaṁ.</span></p><p class="p3"><span class="t1">This, venerable friends, is called the noble truth of the cessation of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The Fourth Noble Truth and its Analysis</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamañ-cāvuso dukkhanirodhagāminī paṭipadā ariyasaccaṁ?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is the noble truth of the practice leading to the end of suffering? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayam-eva ariyo aṭṭhaṅgiko maggo, seyyathīdaṁ:</span></p><p class="p3"><span class="t1">It is this noble path with eight factors, as follows: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sammādiṭṭhi </span>[1]</p><p class="p3"><span class="t1">right view </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sammāsaṅkappo </span>[2]</p><p class="p3"><span class="t1">right thought </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sammāvācā </span>[3]</p><p class="p3"><span class="t1">right speech </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sammākammanto </span>[4]</p><p class="p3"><span class="t1">right action </span></p>', '-168-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 169, '<p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">sammā-ājīvo </span>[5]</p><p class="p3"><span class="t1">right livelihood </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sammāvāyāmo </span>[6]</p><p class="p3"><span class="t1">right endeavour </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sammāsati </span>[7]</p><p class="p3"><span class="t1">right mindfulness </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sammāsamādhi. </span>[8]</p><p class="p3"><span class="t1">right concentration. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamā cāvuso sammādiṭṭhi?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is right view? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ kho āvuso dukkhe ñāṇaṁ</span></p><p class="p3"><span class="t1">That, venerable friends, which is knowledge about suffering </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dukkhasamudaye ñāṇaṁ</span></p><p class="p3"><span class="t1">knowledge about the arising of suffering </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dukkhanirodhe ñāṇaṁ</span></p><p class="p3"><span class="t1">knowledge about the cessation of suffering </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dukkhanirodhagāminiyā paṭipadāya</span>{167}<span class="t5"> ñāṇaṁ.</span></p><p class="p3"><span class="t1">knowledge about the practice going to the cessation of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ vuccatāvuso sammādiṭṭhi.</span></p><p class="p3"><span class="t1">This, venerable friends, is called right view. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamo cāvuso sammāsaṅkappo?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is right thought? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nekkhammasaṅkappo </span></p><p class="p3"><span class="t1">The thought of renunciation </span></p>', '-169-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 170, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">avyāpādasaṅkappo</span></p><p class="p3"><span class="t1">the thought of good will </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">avihiṁsāsaṅkappo.</span></p><p class="p3"><span class="t1">the thought of non-violence. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ vuccatāvuso sammāsaṅkappo.</span></p><p class="p3"><span class="t1">This, venerable friends, is called right thought. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamā cāvuso sammāvācā?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is right speech? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Musāvādā veramaṇī</span></p><p class="p3"><span class="t1">Refraining from false speech </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pisuṇāvācā</span>{168}<span class="t5"> veramaṇī</span></p><p class="p3"><span class="t1">refraining from malicious speech </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pharusāvācā</span>{169}<span class="t5"> veramaṇī</span></p><p class="p3"><span class="t1">refraining from rough speech </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">samphappalāpā veramaṇī.</span></p><p class="p3"><span class="t1">refraining from frivolous talk. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ vuccatāvuso sammāvācā.</span></p><p class="p3"><span class="t1">This, venerable friends, is called right speech. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamo cāvuso sammākammanto?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is right action? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pāṇātipātā veramaṇī</span></p><p class="p3"><span class="t1">Refraining from killing living creatures </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">adinnādānā veramaṇī</span></p><p class="p3"><span class="t1">refraining from taking what has not been given </span></p>', '-170-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 171, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kāmesu micchācārā veramaṇī.</span></p><p class="p3"><span class="t1">refraining from sexual misconduct. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ vuccatāvuso sammākammanto.</span></p><p class="p3"><span class="t1">This, venerable friends, is called right action. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamo cāvuso sammā-ājīvo?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is right livelihood? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idhāvuso ariyasāvako micchā-ājīvaṁ pahāya,</span></p><p class="p3"><span class="t1">Here, venerable friends, a noble disciple, having given up wrong ways of livelihood,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sammā-ājīvena jīvikaṁ kappeti.</span></p><p class="p3"><span class="t1">earns his living by a right way of livelihood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ vuccatāvuso sammā-ājīvo.</span></p><p class="p3"><span class="t1">This, venerable friends, is called right livelihood. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamo cāvuso sammāvāyāmo?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is right endeavour? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idhāvuso bhikkhu anuppannānaṁ pāpakānaṁ</span></p><p class="p3"><span class="t1">° Here, venerable friends, a monk generates desire for the non-arising of bad  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">akusalānaṁ dhammānaṁ anuppādāya chandaṁ janeti,</span></p><p class="p3"><span class="t1">and unwholesome things that have not yet arisen, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vāyamati viriyaṁ ārabhati cittaṁ paggaṇhāti padahati.</span></p><p class="p3"><span class="t1">(in this regard) he endeavours, instigates energy, exerts his mind, and makes an effort. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Uppannānaṁ pāpakānaṁ akusalānaṁ dhammānaṁ</span></p><p class="p3"><span class="t1">° He generates desire to give up bad and unwholesome things  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pahānāya chandaṁ janeti,</span></p><p class="p3"><span class="t1">that have already arisen, </span></p>', '-171-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 172, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vāyamati viriyaṁ ārabhati cittaṁ paggaṇhāti padahati.</span></p><p class="p3"><span class="t1">(in this regard) he endeavours, instigates energy, exerts his mind, and makes an effort. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Anuppannānaṁ kusalānaṁ dhammānaṁ uppādāya chandaṁ janeti,</span></p><p class="p3"><span class="t1">He generates desire for the arising of wholesome things that have not yet arisen, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">vāyamati viriyaṁ ārabhati cittaṁ paggaṇhāti padahati.</span></p><p class="p3"><span class="t1">(in this regard) he endeavours, instigates energy, exerts his mind, and makes an effort. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Uppannānaṁ kusalānaṁ dhammānaṁ ṭhitiyā, asammosāya,</span></p><p class="p3"><span class="t1">° He generates desire for the endurance of wholesome things that have arisen, their non-forgetting, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhiyyobhāvāya,</span>{170}<span class="t5"> vepullāya, bhāvanāya, pāripūriyā</span>{171}<span class="t5"> chandaṁ janeti,</span></p><p class="p3"><span class="t1">multiplicaton, extension, development, and fulfilment, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vāyamati viriyaṁ ārabhati cittaṁ paggaṇhāti padahati.</span></p><p class="p3"><span class="t1">(in this regard) he endeavours, instigates energy, exerts his mind, and makes an effort. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ vuccatāvuso sammāvāyāmo.</span></p><p class="p3"><span class="t1">This, venerable friends, is called right endeavour. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamā cāvuso sammāsati?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is right mindfulness? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idhāvuso bhikkhu kāye kāyānupassī viharati,</span></p><p class="p3"><span class="t1">Here, venerable friends, a monk dwells contemplating (the nature of) the body in the body, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ātāpī, sampajāno, satimā, vineyya loke abhijjhādomanassaṁ.</span></p><p class="p3"><span class="t1">ardent, clearly knowing, and mindful, after removing avarice and sorrow regarding the world. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vedanāsu vedanānupassī viharati,</span></p><p class="p3"><span class="t1">He dwells contemplating (the nature of) feelings in feelings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ātāpī, sampajāno, satimā, vineyya loke abhijjhādomanassaṁ.</span></p><p class="p3"><span class="t1">ardent, clearly knowing, and mindful, after removing avarice and sorrow regarding the world. </span></p>', '-172-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 173, '<p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Citte cittānupassī viharati,</span></p><p class="p3"><span class="t1">He dwells contemplating (the nature of) the mind in the mind, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ātāpī, sampajāno, satimā, vineyya loke abhijjhādomanassaṁ.</span></p><p class="p3"><span class="t1">ardent, clearly knowing, and mindful, after removing avarice and sorrow regarding the world. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dhammesu dhammānupassī viharati,</span></p><p class="p3"><span class="t1">He dwells contemplating (the nature of) things in (various) things </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ātāpī, sampajāno, satimā, vineyya loke abhijjhādomanassaṁ.</span></p><p class="p3"><span class="t1">ardent, clearly knowing, and mindful, after removing avarice and sorrow regarding the world. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ vuccatāvuso sammāsati.</span></p><p class="p3"><span class="t1">This, venerable friends, is called right mindfulness. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamo</span>{172}<span class="t5"> cāvuso sammāsamādhi?</span></p><p class="p3"><span class="t1">Now what, venerable friends, is right concentration? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idhāvuso bhikkhu vivicceva kāmehi, vivicca akusalehi dhammehi,</span></p><p class="p3"><span class="t1">Here, venerable friends, a monk, quite secluded from sense desires, secluded from unwholesome things, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">savitakkaṁ, savicāraṁ, vivekajaṁ pītisukhaṁ,</span></p><p class="p3"><span class="t1">having thinking, reflection, and the happiness and rapture born of seclusion, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">paṭhamaṁ jhānaṁ upasampajja viharati.</span></p><p class="p3"><span class="t1">dwells having attained the first absorption. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vitakkavicārānaṁ vūpasamā </span></p><p class="p3"><span class="t1">With the ending of thinking, and reflection, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ajjhattaṁ sampasādanaṁ, cetaso ekodibhāvaṁ,</span></p><p class="p3"><span class="t1">with internal clarity, and one-pointedness of mind, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">avitakkaṁ, avicāraṁ, samādhijaṁ pītisukhaṁ,</span></p><p class="p3"><span class="t1">being without thinking, without reflection, having the happiness and rapture born of concentration, </span></p>', '-173-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 174, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dutiyaṁ jhānaṁ upasampajja viharati.</span></p><p class="p3"><span class="t1">he dwells having attained the second absorption. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pītiyā ca virāgā upekkhako</span>{173}<span class="t5"> ca viharati,</span></p><p class="p3"><span class="t1">With the fading away of rapture he dwells equanimous, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sato ca sampajāno, sukhaṁ ca kāyena paṭisaṁvedeti,</span></p><p class="p3"><span class="t1">mindful, clearly knowing, experiencing happiness through the body, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yan-taṁ Ariyā ācikkhanti: “Upekkhako</span>{174}<span class="t5"> satimā sukhavihārī” ti,</span></p><p class="p3"><span class="t1">about which the Noble Ones declare: “He lives pleasantly, mindful, and equanimous,” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tatiyaṁ jhānaṁ upasampajja viharati. </span></p><p class="p3"><span class="t1">(thus) he dwells having attained the third absorption. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sukhassa ca pahānā, dukkhassa ca pahānā,</span></p><p class="p3"><span class="t1">Having given up pleasure, given up pain, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pubbeva somanassadomanassānaṁ atthaṅgamā</span></p><p class="p3"><span class="t1">and with the previous disappearence of mental well-being and sorrow, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">adukkhaṁ, asukhaṁ, upekkhā</span>{175}<span class="t5">-satipārisuddhiṁ </span></p><p class="p3"><span class="t1">without pain, without pleasure, and with complete purity of mindfulness owing to equanimity, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">catutthaṁ jhānaṁ</span>{176}<span class="t5"> upasampajja viharati.</span></p><p class="p3"><span class="t1">he dwells having attained the fourth absorption. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ vuccatāvuso sammāsamādhi.</span></p><p class="p3"><span class="t1">This, venerable friends, is called right concentration. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Idaṁ vuccatāvuso dukkhanirodhagāminī paṭipadā ariyasaccaṁ.</span></p><p class="p3"><span class="t1">This, venerable friends, is called the noble truth of the practice going to the cessation of suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tathāgatena āvuso Arahatā Sammāsambuddhena,</span></p><p class="p3"><span class="t1">The Realised One, friends, the Worthy One, the Perfect Sambuddha, </span></p>', '-174-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', 'Tatiyakabhāṇavāraṁ The Third Recital ', 'title', 175);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '29a. Āṭānāṭiyasuttaṁ The Āṭānāṭiya Discourse ', 'title', 175);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 175, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bārāṇasiyaṁ Isipatane Migadāye,</span></p><p class="p3"><span class="t1">while near Bārāṇasī, in the Deer Park at Isipatana, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anuttaraṁ Dhammacakkaṁ pavattitaṁ,</span></p><p class="p3"><span class="t1">has set rolling the unsurpassed Dhamma Wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appativattiyaṁ samaṇena vā brāhmaṇena vā </span></p><p class="p3"><span class="t1">and it cannot be rolled back by an ascetic or by a brahmin </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devena vā mārena vā brahmunā vā kenaci vā lokasmiṁ.</span>{177}</p><p class="p3"><span class="t1">or by a god or by a demon or by a deity or by anyone in the world. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yad-idaṁ imesaṁ catunnaṁ ariyasaccānaṁ ācikkhanā, </span></p><p class="p3"><span class="t1">° That is to say, the explanation, revealing, making known, setting forth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t16">desanā, paññāpanā, paṭṭhapanā, vivaraṇā, vibhajanā, uttānīkamman”-ti. </span></p><p class="p3"><span class="t1">opening up, analysing, and making plain, of these four noble truths.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-avoc’</span>{178}<span class="t5"> āyasmā Sāriputto, </span></p><p class="p3"><span class="t1">Venerable Sāriputta said this, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">attamanā te bhikkhū āyasmato Sāriputtassa bhāsitaṁ abhinandun-ti.</span></p><p class="p3"><span class="t1">and those monks were uplifted and greatly rejoiced in venerable Sāriputta’s words. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Niṭṭhitāni Atirekāni Sattasuttāni</span>{179}</p><p class="p3"><span class="t37">The Seven </span><span class="t37">Extra</span><span class="t37"> Discourses are Finished</span></p><p class="p3"><span class="t37"> </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h2>Tatiyakabhāṇavāraṁ The Third Recital </h2></p><p class="p3"><span class="t1">  </span></p><p class="p3"><h3>29a. Āṭānāṭiyasuttaṁ The Āṭānāṭiya Discourse </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">(<span class="t5">Part One)</span> </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The Occasion</span></p>', '-175-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 176, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evaṁ me sutaṁ: </span></p><p class="p3"><span class="t1">Thus I have heard: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ekaṁ samayaṁ Bhagavā Rājagahe viharati Gijjhakūṭe pabbate.</span></p><p class="p3"><span class="t1">at one time the Fortunate One was dwelling near Rājagaha on the Vultures’ Peak mountain. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Cattāro Mahārājā,</span>{180}</p><p class="p3"><span class="t1">Then the Four Great Kings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mahatiyā ca yakkhasenāya, mahatiyā ca gandhabbasenāya,</span></p><p class="p3"><span class="t1">with a great army of yakkhas, with a great army of gandhabbas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mahatiyā ca kumbhaṇḍasenāya, mahatiyā ca nāgasenāya, </span></p><p class="p3"><span class="t1">with a great army of kumbhaṇḍas, with a great army of nāgas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">catuddisaṁ rakkhaṁ ṭhapetvā, catuddisaṁ gumbaṁ ṭhapetvā, </span></p><p class="p3"><span class="t1">having set up a protection over the four quarters, having set serried troops at the four directions, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">catuddisaṁ ovaraṇaṁ ṭhapetvā, abhikkantāya rattiyā,</span></p><p class="p3"><span class="t1">having set up a barricade at the four directions, at the end of the night, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abhikkantavaṇṇā kevalakappaṁ Gijjhakūṭaṁ obhāsetvā, </span></p><p class="p3"><span class="t1">having lit up the whole of Vultures’ Peak with their surpassing beauty, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">yena Bhagavā tenupasaṅkamiṁsu,</span>{181}<span class="t5"> upasaṅkamitvā</span></p><p class="p3"><span class="t1">approached the Fortunate One, and after approaching </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">Bhagavantaṁ abhivādetvā, ekam-antaṁ nisīdiṁsu. Te pi kho yakkhā,</span></p><p class="p3"><span class="t1">and worshipping the Fortunate One, they sat down on one side. Then of those yakkhas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appekacce Bhagavantaṁ abhivādetvā ekam-antaṁ nisīdiṁsu.</span></p><p class="p3"><span class="t1">some, after worshipping the Fortunate One, sat down on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce Bhagavatā saddhiṁ sammodiṁsu,</span></p><p class="p3"><span class="t1">Some exchanged greetings with the Fortunate One,  </span></p>', '-176-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 177, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">sammodanīyaṁ kathaṁ sārānīyaṁ vītisāretvā, ekam-antaṁ nisīdiṁsu. </span></p><p class="p3"><span class="t1">and after exchanging polite and courteous greetings, sat down on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce yena Bhagavā tenañjaliṁ paṇāmetvā, ekam-antaṁ nisīdiṁsu.</span></p><p class="p3"><span class="t1">Some, after raising their hands in respectful salutation to the Fortunate One, sat down on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce nāmagottaṁ sāvetvā,</span><span class="t16"> ekam-antaṁ nisīdiṁsu.</span></p><p class="p3"><span class="t1">Some, after announcing their name and family, sat down on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t16">Appekacce tuṇhībhūtā ekam-antaṁ nisīdiṁsu.</span></p><p class="p3"><span class="t1">Some, while keeping silent, sat down on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ekam-antaṁ nisinno kho Vessavaṇo Mahārājā Bhagavantaṁ etad-avoca:</span></p><p class="p3"><span class="t1">Then, while sitting on one side, the Great King Vessavaṇa said this to the Fortunate One: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Santi hi bhante uḷārā yakkhā Bhagavato appasannā,</span></p><p class="p3"><span class="t1">“There are, reverend Sir, some high ranking yakkhas who are not pleased with the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santi hi bhante uḷārā yakkhā Bhagavato pasannā,</span></p><p class="p3"><span class="t1">there are, reverend Sir, some high ranking yakkhas who are pleased with the Fortunate One, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">santi hi bhante majjhimā yakkhā Bhagavato appasannā,</span></p><p class="p3"><span class="t1">there are, reverend Sir, some middle ranking yakkhas who are not pleased with the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santi hi bhante majjhimā yakkhā Bhagavato pasannā,</span></p><p class="p3"><span class="t1">there are, reverend Sir, some middle ranking yakkhas who are pleased with the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santi hi bhante nīcā yakkhā Bhagavato appasannā,</span></p><p class="p3"><span class="t1">there are, reverend Sir, some low ranking yakkhas who are not pleased with the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santi hi bhante nīcā yakkhā Bhagavato pasannā.</span></p><p class="p3"><span class="t1">there are, reverend Sir, some low ranking yakkhas who are pleased with the Fortunate One. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yebhuyyena kho pana bhante yakkhā appasannā yeva Bhagavato.</span></p><p class="p3"><span class="t1">But, reverend Sir, almost all of the yakkhas are not pleased with the Fortunate One. </span></p>', '-177-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 178, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Taṁ kissa hetu?</span></p><p class="p3"><span class="t1">What is the reason for that? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavā hi bhante pāṇātipātā veramaṇiyā Dhammaṁ deseti, </span></p><p class="p3"><span class="t1">Because, reverend Sir, the Fortunate One preaches the Dhamma of refraining from killing living creatures, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">adinnādānā veramaṇiyā Dhammaṁ deseti, </span></p><p class="p3"><span class="t1">preaches the Dhamma of refraining from taking what has not been given, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kāmesu micchācārā veramaṇiyā Dhammaṁ deseti, </span></p><p class="p3"><span class="t1">preaches the Dhamma of refraining from sexual misconduct, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">musāvādā veramaṇiyā Dhammaṁ deseti,</span>{182}<span class="t5"> </span></p><p class="p3"><span class="t1">preaches the Dhamma of refraining from false speech, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">surāmerayamajjapamādaṭṭhānā veramaṇiyā Dhammaṁ deseti.</span></p><p class="p3"><span class="t1">preaches the Dhamma of refraining from liquor, wines, or intoxicants which cause heedlessness. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Yebhuyyena kho pana bhante yakkhā appaṭiviratā yeva pāṇātipātā, </span></p><p class="p3"><span class="t1">But, reverend Sir, almost all of the yakkhas do not refrain from killing living creatures, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appaṭiviratā adinnādānā, </span></p><p class="p3"><span class="t1">do not refrain from taking what has not been given, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appaṭiviratā kāmesu micchācārā, </span></p><p class="p3"><span class="t1">do not refrain from sexual misconduct, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appaṭiviratā musāvādā, </span></p><p class="p3"><span class="t1">do not refrain from false speech, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appaṭiviratā surāmerayamajjapamādaṭṭhānā. </span></p><p class="p3"><span class="t1">do not refrain from liquors, wines, or intoxicants which cause heedlessness. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tesaṁ taṁ hoti appiyaṁ amanāpaṁ. </span></p><p class="p3"><span class="t1">To them that is neither dear nor appealing. </span></p>', '-178-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 179, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Santi hi bhante Bhagavato sāvakā araññe,</span></p><p class="p3"><span class="t1">There are, reverend Sir, disciples of the Fortunate One in the wilderness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vanapatthāni pantāni senāsanāni paṭisevanti,</span></p><p class="p3"><span class="t1">who are practising in remote jungle dwelling places, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appasaddāni, appanigghosāni, vijanavātāni,</span></p><p class="p3"><span class="t1">where there is little sound, little noise, which have a lonely atmosphere, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">manussarāhaseyyakāni, paṭisallānasāruppāni. </span></p><p class="p3"><span class="t1">lying hidden away from men, which are suitable for seclusion. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tattha santi uḷārā yakkhā nivāsino </span></p><p class="p3"><span class="t1">And there are high ranking yakkhas settled there </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ye imasmiṁ Bhagavato pāvacane appasannā. </span></p><p class="p3"><span class="t1">who are not pleased with the word of the Fortunate One,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tesaṁ pasādāya uggaṇhātu bhante Bhagavā “Āṭānāṭiyaṁ” rakkhaṁ,</span></p><p class="p3"><span class="t1">To please then, reverend Sir, may the Fortunate One learn this “Āṭānāṭiya” protection, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">bhikkhūnaṁ, bhikkhuṇīnaṁ, upāsakānaṁ, upāsikānaṁ,</span></p><p class="p3"><span class="t1">for the monks’, nuns’, laymen’s, and laywomen’s </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">guttiyā, rakkhāya, avihiṁsāya, phāsuvihārāyā” ti.</span></p><p class="p3"><span class="t1">guard, protection, freedom from harm, and comfortable living.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Adhivāsesi Bhagavā tuṇhībhāvena. </span></p><p class="p3"><span class="t1">The Fortunate One by keeping silent gave consent. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Vessavaṇo Mahārājā Bhagavato adhivāsanaṁ viditvā</span></p><p class="p3"><span class="t1">Then the Great King Vessavaṇa having understood the Fortunate One’s consent </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tāyaṁ velāyaṁ imaṁ “Āṭānāṭiyaṁ” rakkhaṁ abhāsi:</span></p><p class="p3"><span class="t1">on that occasion recited this “Āṭānāṭiya” protection: </span></p>', '-179-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 180, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The Seven Buddhas</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Vipassissa namatthu, Cakkhumantassa sirīmato,</span></p><p class="p3"><span class="t1">“May you revere Vipassī, the glorious Visionary, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sikhissa pi namatthu, sabbabhūtānukampino, </span>[1]</p><p class="p3"><span class="t1">may you revere Sikhī, who has pity on all beings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vessabhussa namatthu, nahātakassa tapassino,</span></p><p class="p3"><span class="t1">may you revere Vessabhū, the austere one, cleansed (of corruptions), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">namatthu Kakusandhassa, Mārasenāpamaddino, </span>[2]</p><p class="p3"><span class="t1">may you revere Kakusandha, who has crushed Māra’s army, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Koṇāgamanassa namatthu, brāhmaṇassa vusīmato,</span></p><p class="p3"><span class="t1">may you revere Koṇāgamana, the accomplished brahmin, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kassapassa namatthu, vippamuttassa sabbadhi. </span>[3]</p><p class="p3"><span class="t1">may you revere Kassapa, who is free in every respect. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Aṅgīrasassa namatthu, Sakyaputtassa sirīmato,</span></p><p class="p3"><span class="t1">May you revere Aṅgīrasa, the glorious son of the Sakyans, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yo imaṁ Dhammam-adesesi, sabbadukkhā panūdanaṁ. </span>[4]</p><p class="p3"><span class="t1">he who preached this Dhamma, which is the dispelling of all suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye cāpi nibbutā loke, yathābhūtaṁ vipassisuṁ,</span></p><p class="p3"><span class="t1">Those who are emancipated in the world, who have insight (into things) as they are, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te janā apisuṇā,</span>{183}<span class="t5"> mahantā vītasāradā, </span>[5]</p><p class="p3"><span class="t1">those people free from malicious speech, who are great and fully mature, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hitaṁ devamanussānaṁ, yaṁ namassanti Gotamaṁ,</span></p><p class="p3"><span class="t1">they will revere that Gotama, who is of benefit to gods and men, </span></p><p class="p3"><span class="t1">  </span></p>', '-180-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 181, '<p class="p3"><span class="t5">vijjācaraṇasampannaṁ, mahantaṁ vītasāradaṁ. </span>[6]</p><p class="p3"><span class="t1">who has understanding and good conduct, who is great and fully mature. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The East</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yato uggacchatī</span>{184}<span class="t5"> suriyo, Ādicco maṇḍalī mahā,</span></p><p class="p3"><span class="t1">From where the sun comes up, the son of Aditi, the great circle, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa cuggacchamānassa, saṁvarī pi nirujjhati, </span>[7]</p><p class="p3"><span class="t1">while that one is coming up, the darkness of night comes to an end, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa cuggate suriye, divaso ti pavuccati,</span></p><p class="p3"><span class="t1">and after the sun has come up, it is said to be the daytime, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">rahado pi tattha gambhīro, samuddo saritodako, </span>[8]</p><p class="p3"><span class="t1">there is a deep lake in that place, an ocean where the waters have flowed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evaṁ naṁ tattha jānanti samuddo saritodako,</span></p><p class="p3"><span class="t1">thus in that place they know there is an ocean where the waters have flowed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ito sā Purimā disā, iti naṁ ācikkhatī</span>{185}<span class="t5"> jano. </span>[9]</p><p class="p3"><span class="t1">from here that is the Easterly direction, so the people declare. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ disaṁ abhipāleti Mahārājā yasassi so,</span></p><p class="p3"><span class="t1">That direction is watched over by a resplendent Great King, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbānaṁ ādhipati, Dhataraṭṭho iti nāma so. </span>[10]</p><p class="p3"><span class="t1">he is the master of the gandhabbas, Dhataraṭṭha, such is his name. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ramatī</span>{186}<span class="t5"> naccagītehi, gandhabbehi purakkhato,</span></p><p class="p3"><span class="t1">He delights in song and dance, he is honoured by gandhabbas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, ekanāmā ti me sutaṁ, </span>[11]</p><p class="p3"><span class="t1">he also has a great many sons, all of one name, so I have heard, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asītiṁ dasa eko ca, Indanāmā mahabbalā,</span></p>', '-181-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 182, '<p class="p3"><span class="t1">they are eighty and ten and one, Inda by name, ones of great strength, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te cāpi Buddhaṁ disvāna, Buddhaṁ ādiccabandhunaṁ, </span>[12]</p><p class="p3"><span class="t1">they, having seen the Awakened One, the Buddha, kinsman of the sun, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dūrato va namassanti, mahantaṁ vītasāradaṁ:</span></p><p class="p3"><span class="t1">from afar, do reverence him, who is great and fully mature: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Namo te purisājañña! Namo te purisuttama! </span>[13]</p><p class="p3"><span class="t1">“Reverence to you, excellent one! Reverence to you, supreme one! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kusalena samekkhasi, amanussā pi taṁ vandanti,</span></p><p class="p3"><span class="t1">You have looked on us with goodness, the non-human beings worship you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sutaṁ netaṁ abhiṇhaso, tasmā evaṁ vademase: </span>[14]</p><p class="p3"><span class="t1">we have heard this repeatedly, therefore we should speak like this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Jinaṁ vandatha Gotamaṁ, Jinaṁ vandāma Gotamaṁ,</span></p><p class="p3"><span class="t1">“You should worship the victor Gotama, we should worship the victor Gotama, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vijjācaraṇasampannaṁ, Buddhaṁ vandāma Gotamaṁ!”” </span>[15]</p><p class="p3"><span class="t1">who has understanding and good conduct, we should worship the Buddha Gotama!”” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The South</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yena petā pavuccanti, pisuṇā piṭṭhimaṁsikā,</span></p><p class="p3"><span class="t1">There they say (go) the departed, who speak maliciously, backbiters, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pāṇātipātino luddā, corā nekatikā janā,</span></p><p class="p3"><span class="t1">killers of creatures, hunters, thieves, and fraudulent people, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ito sā Dakkhiṇā disā, iti naṁ ācikkhatī jano. </span>[16]</p><p class="p3"><span class="t1">from here that is the Southerly direction, so the people declare. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ disaṁ abhipāleti Mahārājā yasassi so,</span></p><p class="p3"><span class="t1">That direction is watched over by a resplendent Great King, </span></p>', '-182-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 183, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍhānaṁ ādhipati, Virūḷho iti nāma so. </span>[17]</p><p class="p3"><span class="t1">he is the master of the kumbhaṇḍhas, Virūḷha, such is his name.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ramatī naccagītehi, kumbhaṇḍhehi purakkhato,</span></p><p class="p3"><span class="t1">He delights in song and dance, he is honoured by kumbhaṇḍhas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, ekanāmā ti me sutaṁ, </span>[18]</p><p class="p3"><span class="t1">he also has a great many sons, all of one name, so I have heard, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asītiṁ dasa eko ca, Indanāmā mahabbalā,</span></p><p class="p3"><span class="t1">they are eighty and ten and one, Inda by name, ones of great strength, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te cāpi Buddhaṁ disvāna, Buddhaṁ ādiccabandhunaṁ, </span>[19]</p><p class="p3"><span class="t1">they, having seen the Awakened One, the Buddha, kinsman of the sun, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dūrato va namassanti, mahantaṁ vītasāradaṁ:</span></p><p class="p3"><span class="t1">from afar, do reverence him, who is great and fully mature: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Namo te purisājañña! Namo te purisuttama! </span>[20]</p><p class="p3"><span class="t1">“Reverence to you, excellent one! Reverence to you, supreme one! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kusalena samekkhasi, amanussā pi taṁ vandanti,</span></p><p class="p3"><span class="t1">You have looked on us with goodness, the non-human beings worship you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sutaṁ netaṁ abhiṇhaso, tasmā evaṁ vademase: </span>[21]</p><p class="p3"><span class="t1">we have heard this repeatedly, therefore we should speak like this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Jinaṁ vandatha Gotamaṁ, Jinaṁ vandāma Gotamaṁ,</span></p><p class="p3"><span class="t1">“You should worship the victor Gotama, we should worship the victor Gotama, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vijjācaraṇasampannaṁ, Buddhaṁ vandāma Gotamaṁ!”” </span>[22]</p><p class="p3"><span class="t1">who has understanding and good conduct, we should worship the Buddha Gotama!”” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The West</span></p><p class="p3"><span class="t1">  </span></p>', '-183-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 184, '<p class="p3"><span class="t5">Yattha coggacchati</span>{187}<span class="t5"> suriyo, Ādicco maṇḍalī mahā,</span></p><p class="p3"><span class="t1">That place where the sun goes down, the son of Aditi, the great circle, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa coggacchamānassa divaso pi nirujjhati, </span>[23]</p><p class="p3"><span class="t1">while that one is going down the light of day comes to an end, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa coggate suriye, saṁvarī ti pavuccati,</span></p><p class="p3"><span class="t1">and after the sun has gone down, it is said to be night-time, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">rahado pi tattha gambhīro, samuddo saritodako, </span>[24]</p><p class="p3"><span class="t1">there is a deep lake in that place, an ocean where the waters have flowed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evaṁ naṁ tattha jānanti samuddo saritodako,</span></p><p class="p3"><span class="t1">thus in that place they know there is an ocean where the waters have flowed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ito sā Pacchimā disā, iti naṁ ācikkhatī jano. </span>[25]</p><p class="p3"><span class="t1">from here that is the Westerly direction, so the people declare. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ disaṁ abhipāleti Mahārājā yasassi so,</span></p><p class="p3"><span class="t1">That direction is watched over by a resplendent Great King, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgānaṁ ādhipati, Virūpakkho iti nāma so. </span>[26]</p><p class="p3"><span class="t1">he is the master of the nāgas, Virūpakkha, such is his name. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ramatī naccagītehi, nāgehi purakkhato,</span></p><p class="p3"><span class="t1">He delights in song and dance, he is honoured by nāgas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, ekanāmā ti me sutaṁ, </span>[27]</p><p class="p3"><span class="t1">he also has a great many sons, all of one name, so I have heard, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asītiṁ dasa eko ca, Indanāmā mahabbalā,</span></p><p class="p3"><span class="t1">they are eighty and ten and one, Inda by name, ones of great strength, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te cāpi Buddhaṁ disvāna, Buddhaṁ ādiccabandhunaṁ, </span>[28]</p><p class="p3"><span class="t1">they, having seen the Awakened One, the Buddha, kinsman of the sun, </span></p><p class="p3"><span class="t1">  </span></p>', '-184-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 185, '<p class="p3"><span class="t5">dūrato</span>{188}<span class="t5"> va namassanti, mahantaṁ vītasāradaṁ:</span></p><p class="p3"><span class="t1">from afar, do reverence him, who is great and fully mature: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Namo te purisājañña! Namo te purisuttama! </span>[29]</p><p class="p3"><span class="t1">“Reverence to you, excellent one! Reverence to you, supreme one! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kusalena samekkhasi, amanussā pi taṁ vandanti,</span></p><p class="p3"><span class="t1">You have looked on us with goodness, the non-human beings worship you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sutaṁ netaṁ abhiṇhaso, tasmā evaṁ vademase: </span>[30]</p><p class="p3"><span class="t1">we have heard this repeatedly, therefore we should speak like this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Jinaṁ vandatha Gotamaṁ, Jinaṁ vandāma Gotamaṁ,</span></p><p class="p3"><span class="t1">“You should worship the victor Gotama, we should worship the victor Gotama, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vijjācaraṇasampannaṁ, Buddhaṁ vandāma Gotamaṁ!””</span>{189}<span class="t5"> </span>[31]</p><p class="p3"><span class="t1">who has understanding and good conduct, we should worship the Buddha Gotama!”” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The North</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yena Uttarakurū rammā, Mahāneru sudassano,</span></p><p class="p3"><span class="t1">Where delightful Uttarakuru is, and the good looking Mount Neru, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">manussā tattha jāyanti amamā apariggahā. </span>[32]</p><p class="p3"><span class="t1">the men who are born in that place are unselfish, without possessions. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na te bījaṁ pavapanti, na pi nīyanti naṅgalā,</span></p><p class="p3"><span class="t1">They do not sow the seed, and nor are there ploughs led along, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">akaṭṭhapākimaṁ sāliṁ paribhuñjanti mānusā, </span>[33]</p><p class="p3"><span class="t1">the rice that men enjoy the use of there matures in untilled ground, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">akaṇaṁ athusaṁ suddhaṁ, sugandhaṁ taṇḍulapphalaṁ,</span></p><p class="p3"><span class="t1">without husk powder or chaff, pure, sweet smelling grains of the finest rice, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tuṇḍikīre pacitvāna, tato bhuñjanti bhojanaṁ. </span>[34]</p>', '-185-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 186, '<p class="p3"><span class="t1">having cooked it on hot rocks (without smoke), they then enjoy their food. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gāviṁ ekakhuraṁ katvā anuyanti disodisaṁ,</span></p><p class="p3"><span class="t1">Having made their cows solid-hooved they go about from place to place, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pasuṁ ekakhuraṁ katvā anuyanti disodisaṁ, </span>[35]</p><p class="p3"><span class="t1">having made their kine solid-hooved they go about from place to place, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">itthivāhanaṁ katvā anuyanti disodisaṁ,</span></p><p class="p3"><span class="t1">having made women their vehicle they go about from place to place, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">purisavāhanaṁ katvā anuyanti disodisaṁ, </span>[36]</p><p class="p3"><span class="t1">having made men their vehicle they go about from place to place, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumārivāhanaṁ katvā anuyanti disodisaṁ,</span></p><p class="p3"><span class="t1">having made girls their vehicle they go about from place to place, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumāravāhanaṁ katvā anuyanti disodisaṁ. </span>[37]</p><p class="p3"><span class="t1">having made boys their vehicle they go about from place to place. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Te yāne abhirūhitvā,</span></p><p class="p3"><span class="t1">Having mounted their (various) carriages, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbā disā anupariyanti pacārā tassa rājino,</span></p><p class="p3"><span class="t1">the messengers of that King go around in all directions, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hatthiyānaṁ assayānaṁ dibbaṁ yānaṁ upaṭṭhitaṁ. </span>[38]</p><p class="p3"><span class="t1">being furnished with elephant- and horse- and divine-carriages. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pāsādā sivikā ceva Mahārājassa yasassino,</span></p><p class="p3"><span class="t1">And for that Great and resplendent King there are palaces and palanquins, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tassa ca nagarā ahū, antalikkhe sumāpitā: </span>[39]</p><p class="p3"><span class="t1">and there are cities for him also, that are well built in the heavens (called): </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Āṭānāṭā, Kusināṭā, Parakusināṭā, Nāṭapuriyā, Parakusitanāṭā.</span></p>', '-186-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 187, '<p class="p3"><span class="t1">Āṭānāṭā, Kusināṭā, Parakusināṭā, Nāṭapuriyā, Parakusitanāṭā. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Uttarena Kapīvanto, Janogham-aparena ca,</span></p><p class="p3"><span class="t1">To the North is Kapīvanta, and on the other side is Janogha, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Navanavatiyo, Ambara-ambaravatiyo, Ālakamandā nāma rājadhāni.</span></p><p class="p3"><span class="t1">Navanavatiya, Ambara-ambaravatiya, and the king’s capital named Ālakamandā. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kuverassa kho pana mārisa Mahārājassa Visāṇā nāma rājadhāni,</span></p><p class="p3"><span class="t1">The Great King Kuvera’s capital, dear Sir, is named Visāṇā, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tasmā Kuvero Mahārājā Vessavaṇo ti pavuccati.</span></p><p class="p3"><span class="t1">therefore is the Great King also called Vessavaṇa. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Paccesanto pakāsenti: Tatolā, Tattalā, Tatotalā,</span></p><p class="p3"><span class="t1">These each individually inform (the King): Tatolā, Tattalā, Tatotalā, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ojasi, Tejasi, Tatojasi, Sūro, Rājā, Ariṭṭho, Nemi.</span></p><p class="p3"><span class="t1">Ojasi, Tejasi, Tatojasi, Sūra, Rājā, Ariṭṭha, Nemi. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Rahado pi tattha Dharaṇī nāma,</span></p><p class="p3"><span class="t1">There is a lake in that place also, named Dharaṇī, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yato meghā pavassanti, vassā yato patāyanti.</span></p><p class="p3"><span class="t1">and from there the clouds rain down, from there the rains spread. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabhā pi tattha Bhagalavatī nāma yattha yakkhā payirupāsanti.</span></p><p class="p3"><span class="t1">There also is the public hall named Bhagalavatī where the yakkhas assemble. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tattha niccaphalā rukkhā, nānādijagaṇāyutā,</span></p><p class="p3"><span class="t1">There the trees are always in fruit, and have a crowd of birds of diverse kinds, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mayūrakoñcābhirudā, kokilābhi hi vaggubhi, </span>[40]</p><p class="p3"><span class="t1">resounding with peacocks and hens, and also with the lovely cuckoos, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jīvaṁjīvakasaddettha, atho oṭṭhavacittakā,</span></p>', '-187-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 188, '<p class="p3"><span class="t1">there is the pheasant who calls out “live on!”, and the bird who calls out “lift up your minds!”, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kukutthakā kuḷīrakā, vane pokkharasātakā, </span>[41]</p><p class="p3"><span class="t1">there are the jungle fowl, and golden cocks, and in the wood the lotus crane, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sukasāḷikasaddettha, daṇḍamāṇavakāni ca,</span></p><p class="p3"><span class="t1">there are the sounds of parrot and mynah, and birds who have young mens’ faces, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sobhati sabbakālaṁ sā Kuveranalinī sadā,</span></p><p class="p3"><span class="t1">and Kuvera’s lotus pond is ever attractive, all of the time, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ito sā Uttarā disā, iti naṁ ācikkhatī jano. </span>[42]</p><p class="p3"><span class="t1">from here that is the Northerly direction, so the people declare. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ disaṁ abhipāleti Mahārājā yasassi so,</span></p><p class="p3"><span class="t1">That direction is watched over by a resplendent Great King, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhānaṁ ādhipati, Kuvero iti nāma so. </span>[43]</p><p class="p3"><span class="t1">he is the master of the yakkhas, Kuvera, such is his name. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ramatī naccagītehi, yakkhehi purakkhato,</span></p><p class="p3"><span class="t1">He delights in song and dance, he is honoured by yakkhas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, ekanāmā ti me sutaṁ, </span>[44]</p><p class="p3"><span class="t1">he also has a great many sons, all of one name, so I have heard, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asītiṁ dasa eko ca, Indanāmā mahabbalā,</span></p><p class="p3"><span class="t1">they are eighty and ten and one, Inda by name, ones of great strength, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te cāpi Buddhaṁ disvāna, Buddhaṁ ādiccabandhunaṁ, </span>[45]</p><p class="p3"><span class="t1">they, having seen the Awakened One, the Buddha, kinsman of the sun, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dūrato va namassanti, mahantaṁ vītasāradaṁ:</span></p><p class="p3"><span class="t1">from afar, do reverence him, who is great and fully mature: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Namo te purisājañña! Namo te purisuttama! </span>[46]</p>', '-188-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 189, '<p class="p3"><span class="t1">“Reverence to you, excellent one! Reverence to you, supreme one! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kusalena samekkhasi, amanussā pi taṁ vandanti,</span></p><p class="p3"><span class="t1">You have looked on us with goodness, the non-human beings worship you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sutaṁ netaṁ abhiṇhaso, tasmā evaṁ vademase: </span>[47]</p><p class="p3"><span class="t1">we have heard this repeatedly, therefore we should speak like this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Jinaṁ vandatha Gotamaṁ, Jinaṁ vandāma Gotamaṁ,</span></p><p class="p3"><span class="t1">“You should worship the victor Gotama, we should worship the victor Gotama, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vijjācaraṇasampannaṁ, Buddhaṁ vandāma Gotaman!””-ti </span>[48]</p><p class="p3"><span class="t1">who has understanding and good conduct, we should worship the Buddha Gotama!”” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ kho sā mārisa “Āṭānāṭiyā” rakkhā,</span></p><p class="p3"><span class="t1">This, dear Sir, is the “Āṭānāṭiya” protection, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhikkhūnaṁ, bhikkhuṇīnaṁ, upāsakānaṁ, upāsikānaṁ,</span></p><p class="p3"><span class="t1">for the monks’, nuns’, laymen’s, and laywomen’s </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">guttiyā, rakkhāya, avihiṁsāya, phāsuvihārāyā ti.</span></p><p class="p3"><span class="t1">guard, protection, freedom from harm, and comfortable living. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yassa kassaci mārisa - bhikkhussa vā bhikkhuṇiyā vā </span></p><p class="p3"><span class="t1">Whoever, dear Sir - be they monk or nun </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upāsakassa vā upāsikāya vā - ayaṁ “Āṭānāṭiyā” rakkhā suggahitā,</span>{190}</p><p class="p3"><span class="t1">or<span class="t5"> </span>layman or laywoman - learns this “Āṭānāṭiya” protection well, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhavissati samattā pariyāputā, tañ-ce amanusso -</span></p><p class="p3"><span class="t1">and will master it in its entirety, then if a non-human being - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkho vā yakkhiṇī vā yakkhapotako vā</span></p><p class="p3"><span class="t1">be they male yakkha or female yakkha or yakkha boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhapotikā vā yakkhamahāmatto vā</span></p>', '-189-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 190, '<p class="p3"><span class="t1">or yakkha girl or yakkha minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhapārisajjo vā yakkhapacāro vā</span></p><p class="p3"><span class="t1">or yakkha councillor or yakkha messenger </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbo vā gandhabbī vā gandhabbapotako vā</span></p><p class="p3"><span class="t1">or male gandhabba or female gandhabba or gandhabba boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbapotikā vā gandhabbamahāmatto vā</span></p><p class="p3"><span class="t1">or gandhabba girl or gandhabba minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbapārisajjo vā gandhabbapacāro vā</span></p><p class="p3"><span class="t1">or gandhabba councillor or gandhabba messenger </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍo vā kumbhaṇḍī vā kumbhaṇḍapotako vā</span></p><p class="p3"><span class="t1">or male kumbhaṇḍa or female kumbhaṇḍa or kumbhaṇḍa boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍapotikā vā kumbhaṇḍamahāmatto vā</span></p><p class="p3"><span class="t1">or kumbhaṇḍa girl or kumbhaṇḍa minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍapārisajjo vā kumbhaṇḍapacāro vā</span></p><p class="p3"><span class="t1">or kumbhaṇḍa councillor or kumbhaṇḍa messenger </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgo vā nāginī vā nāgapotako vā</span></p><p class="p3"><span class="t1">or male nāga or female nāga or nāga boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgapotikā vā nāgamahāmatto vā</span></p><p class="p3"><span class="t1">or nāga girl or nāga minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgapārisajjo vā nāgapacāro vā -</span></p><p class="p3"><span class="t1">or nāga councillor or nāga messenger - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t15">paduṭṭhacitto gacchantaṁ vā anugaccheyya, ṭhitaṁ vā upatiṭṭheyya, </span></p><p class="p3"><span class="t1">with a wicked mind come near while they are going, or stand near while they are standing, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nisinnaṁ vā upanisīdeyya, nipannaṁ vā upanipajjeyya, </span></p>', '-190-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 191, '<p class="p3"><span class="t1">or sit near while they are sitting, or lie near while they are lying, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">na me so mārisa amanusso labheyya</span></p><p class="p3"><span class="t1">then that non-human being, dear Sir, would not receive </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gāmesu vā nigamesu vā sakkāraṁ vā garukāraṁ vā. </span></p><p class="p3"><span class="t1">in my villages and towns, honour or respect. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na me so mārisa amanusso labheyya</span></p><p class="p3"><span class="t1">That non-human being, dear Sir, would not receive </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Ālakamandāya rājadhāniyā vatthuṁ vā vāsaṁ vā.</span></p><p class="p3"><span class="t1">in my royal city Ālakamandā, ground or dwelling. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na me so mārisa amanusso labheyya</span></p><p class="p3"><span class="t1">That non-human being, dear Sir, would not be able </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhānaṁ samitiṁ gantuṁ. </span></p><p class="p3"><span class="t1">to go to the gathering of the yakkhas. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Api-ssu naṁ mārisa amanussā</span></p><p class="p3"><span class="t1">Further, dear Sir, non-human beings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anavayham-pi naṁ kareyyuṁ avivayhaṁ. </span></p><p class="p3"><span class="t1">would not give or take him in marriage. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Api-ssu naṁ mārisa amanussā </span></p><p class="p3"><span class="t1">Further, dear Sir, non-human beings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">attāhi pi paripuṇṇāhi paribhāsāhi paribhāseyyuṁ.</span></p><p class="p3"><span class="t1">would abuse him with very personal abuse. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Api-ssu naṁ mārisa amanussā</span></p><p class="p3"><span class="t1">Further, dear Sir, non-human beings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">rittam-pi pattaṁ sīse nikkujjeyyuṁ. </span></p>', '-191-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 192, '<p class="p3"><span class="t1">would drop an empty bowl over his head. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Api-ssu naṁ mārisa amanussā</span></p><p class="p3"><span class="t1">Further, dear Sir, non-human beings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sattadhā pissa muddhaṁ phāleyyuṁ.</span></p><p class="p3"><span class="t1">would split his head into seven pieces. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Santi hi mārisa amanussā caṇḍā, ruddā, rabhasā,</span></p><p class="p3"><span class="t1">There are non-human beings, dear Sir, who are fierce, cruel, and violent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te neva Mahārājānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na Mahārājānaṁ purisakānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings’ men, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">na Mahārājānaṁ purisakānaṁ purisakānaṁ ādiyanti,</span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings’ mens’ men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te kho te mārisa amanussā Mahārājānaṁ avaruddhā nāma vuccanti.</span></p><p class="p3"><span class="t1">and those non-human beings, dear Sir, are said to be in revolt against the Great Kings. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Seyyathā pi mārisa rañño Māgadhassa vijite corā, </span></p><p class="p3"><span class="t1">Just as, dear Sir, there are thieves in the king of Magadha’s realm, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te neva rañño Māgadhassa ādiyanti, </span></p><p class="p3"><span class="t1">who do not take notice of the king of Magadha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na rañño Māgadhassa purisakānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">who do not take notice of the king of Magadha’s men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na rañño Māgadhassa purisakānaṁ purisakānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">who do not take notice of the king of Magadha’s mens’ men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te kho te mārisa mahācorā,</span><span class="t5"> rañño Māgadhassa avaruddhā nāma vuccanti.</span></p>', '-192-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 193, '<p class="p3"><span class="t1">and those great thieves, dear Sir, are said to be in revolt against the king of Magadha. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evam-eva kho mārisa santi hi amanussā caṇḍā, ruddā, rabhasā, </span></p><p class="p3"><span class="t1">Even so, dear Sir, there are non-human beings who are fierce, cruel, and violent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te neva Mahārājānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na Mahārājānaṁ purisakānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings’ men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na Mahārājānaṁ purisakānaṁ purisakānaṁ ādiyanti,</span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings’ mens’ men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te kho te mārisa amanussā Mahārājānaṁ avaruddhā nāma vuccanti.</span></p><p class="p3"><span class="t1">and those non-human beings, dear Sir, are said to be in revolt against the Great Kings. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Yo hi koci mārisa amanusso -</span></p><p class="p3"><span class="t1">Now, dear Sir, whatever non-human beings - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkho vā yakkhiṇī vā yakkhapotako vā</span></p><p class="p3"><span class="t1">be they male yakkha or female yakkha or yakkha boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhapotikā vā yakkhamahāmatto vā</span></p><p class="p3"><span class="t1">or yakkha girl or yakkha minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhapārisajjo vā yakkhapacāro vā</span></p><p class="p3"><span class="t1">or yakkha councillor or yakkha messenger </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">gandhabbo vā gandhabbī vā gandhabbapotako vā</span></p><p class="p3"><span class="t1">or male gandhabba or female gandhabba or gandhabba boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbapotikā vā gandhabbamahāmatto vā</span></p><p class="p3"><span class="t1">or gandhabba girl or gandhabba minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbapārisajjo vā gandhabbapacāro vā</span></p>', '-193-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 194, '<p class="p3"><span class="t1">or gandhabba councillor or gandhabba messenger </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍo vā kumbhaṇḍī vā kumbhaṇḍapotako vā</span></p><p class="p3"><span class="t1">or male kumbhaṇḍa or female kumbhaṇḍa or kumbhaṇḍa boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍapotikā vā kumbhaṇḍamahāmatto vā</span></p><p class="p3"><span class="t1">or kumbhaṇḍa girl or kumbhaṇḍa minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍapārisajjo vā kumbhaṇḍapacāro vā</span></p><p class="p3"><span class="t1">or kumbhaṇḍa councillor or kumbhaṇḍa messenger </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgo vā nāginī vā nāgapotako vā</span></p><p class="p3"><span class="t1">or male nāga or female nāga or nāga boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgapotikā vā nāgamahāmatto vā</span></p><p class="p3"><span class="t1">or nāga girl or nāga minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgapārisajjo vā nāgapacāro vā -</span></p><p class="p3"><span class="t1">or nāga councillor or nāga messenger - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">paduṭṭhacitto bhikkhuṁ vā bhikkhuṇiṁ vā upāsakaṁ vā upāsikaṁ vā,</span></p><p class="p3"><span class="t1">with a wicked mind should come near a monk or a nun or a layman or a laywoman </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">gacchantaṁ vā anugaccheyya, ṭhitaṁ vā upatiṭṭheyya, </span></p><p class="p3"><span class="t1">while they are going, or stand near while they are standing, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nisinnaṁ vā upanisīdeyya, nipannaṁ vā upanipajjeyya, </span></p><p class="p3"><span class="t1">or sit near while they are sitting, or lie near while they are lying, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">imesaṁ yakkhānaṁ mahāyakkhānaṁ senāpatīnaṁ mahāsenāpatīnaṁ, </span></p><p class="p3"><span class="t1">then to these yakkhas, great yakkhas, generals, great generals, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ujjhāpetabbaṁ, vikkanditabbaṁ, viravitabbaṁ: </span></p><p class="p3"><span class="t1">one should call out, one should shout out, one should cry out: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Ayaṁ yakkho gaṇhāti, ayaṁ yakkho āvisati, </span></p>', '-194-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 195, '<p class="p3"><span class="t1">“This yakkha has seized me, this yakkha has grabbed me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ yakkho heṭheti, ayaṁ yakkho viheṭheti, </span></p><p class="p3"><span class="t1">this yakkha annoys me, this yakkha harasses me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ yakkho hiṁsati, ayaṁ yakkho vihiṁsati, </span></p><p class="p3"><span class="t1">this yakkha hurts me, this yakkha injures me, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">ayaṁ yakkho na muñcatī” ti.</span></p><p class="p3"><span class="t1">this yakkha will not release me.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamesaṁ yakkhānaṁ, mahāyakkhānaṁ, senāpatīnaṁ mahāsenāpatīnaṁ?</span></p><p class="p3"><span class="t1">To which yakkhas, great yakkhas, generals, and great generals? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Indo, Somo, Varuṇo ca, Bhāradvājo, Pajāpatī,</span></p><p class="p3"><span class="t1">Inda, Soma, and Varuṇa, Bhāradvāja, Pajāpati, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Candano, Kāmaseṭṭho ca, Kinnughaṇḍu, Nighaṇḍu ca,</span></p><p class="p3"><span class="t1">Candana, and Kāmaseṭṭha, Kinnughaṇḍu, and Nighaṇḍu, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Panādo, Opamañño ca, devasūto ca Mātalī. </span>[49]</p><p class="p3"><span class="t1">Panāda, and Opamañña, and Mātali, the gods’ charioteer. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Cittaseno ca gandhabbo, Nalo rājā Janesabho,</span></p><p class="p3"><span class="t1">The gandhabbas Citta and Sena, the kings Nala and Janesabha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sātāgiro, Hemavato, Puṇṇako, Karatiyo, Guḷo, </span>[50]</p><p class="p3"><span class="t1">Sātāgira, Hemavata, Puṇṇaka, Karatiya, Guḷa, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sīvako, Mucalindo ca, Vessāmitto, Yugandharo,</span></p><p class="p3"><span class="t1">Sīvaka, and Mucalinda, Vessāmitta, Yugandhara, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gopālo,</span>{191}<span class="t5"> Suppagedho ca, Hirinettī ca Mandiyo, </span>[51]</p><p class="p3"><span class="t1">Gopāla, and Suppagedha, Hiri, Netti, and Mandiya, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">Pañcālacaṇḍo Ālavako Pajjuno Sumano Sumukho Dadhīmukho,</span></p>', '-195-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 196, '<p class="p3"><span class="t1">Pañcālacaṇḍa, Ālavaka, Pajjuna, Sumana, Sumukha, Dadhīmukha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Maṇi, Māṇi, Caro, Dīgho, atho Serissako saha - </span>[52]</p><p class="p3"><span class="t1">Maṇi, Māṇi, Cara, Dīgha, together with Serissaka - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">imesaṁ yakkhānaṁ mahāyakkhānaṁ senāpatīnaṁ mahāsenāpatīnaṁ, </span></p><p class="p3"><span class="t1">then to these yakkhas, great yakkhas, generals, and great generals, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ujjhāpetabbaṁ, vikkanditabbaṁ, viravitabbaṁ: </span></p><p class="p3"><span class="t1">one should call out, one should shout out, one should cry out: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Ayaṁ yakkho gaṇhāti, ayaṁ yakkho āvisati, </span></p><p class="p3"><span class="t1">“This yakkha has seized me, this yakkha has grabbed me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ yakkho heṭheti, ayaṁ yakkho viheṭheti, </span></p><p class="p3"><span class="t1">this yakkha annoys me, this yakkha harasses me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ yakkho hiṁsati, ayaṁ yakkho vihiṁsati, </span></p><p class="p3"><span class="t1">this yakkha hurts me, this yakkha injures me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ yakkho na muñcatī” ti.</span></p><p class="p3"><span class="t1">this yakkha will not release me.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ kho sā mārisa “Āṭānāṭiyā” rakkhā,</span></p><p class="p3"><span class="t1">This, dear Sir, is the “Āṭānāṭiyā” protection, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhikkhūnaṁ, bhikkhuṇīnaṁ, upāsakānaṁ, upāsikānaṁ,</span></p><p class="p3"><span class="t1">for the monks’, nuns’, laymen’s, and laywomen’s </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">guttiyā, rakkhāya, avihiṁsāya, phāsuvihārāyā ti.</span></p><p class="p3"><span class="t1">guard, protection, freedom from harm, and comfortable living. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Handa ca dāni mayaṁ mārisa gacchāma,</span></p><p class="p3"><span class="t1">And now, dear Sir, we shall go, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bahukiccā mayaṁ bahukaraṇīyā” ti.</span></p>', '-196-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 197, '<p class="p3"><span class="t1">as we have many duties, and there is much which ought to be done.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Yassa dāni tumhe Mahārājāno kālaṁ maññathā” ti.</span></p><p class="p3"><span class="t1">“Now is the time for whatever you Great Kings are thinking.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Cattāro Mahārājā</span>{192}<span class="t5"> uṭṭhāyāsanā Bhagavantaṁ</span></p><p class="p3"><span class="t1">° Then the Four Great Kings rose from their seats and after </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abhivādetvā padakkhiṇaṁ katvā, tatthevantaradhāyiṁsu. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t1">worshipping and circumambulating the Fortunate One, vanished right there. </span></p><p class="p3"><span class="t5">Te pi kho yakkhā uṭṭhāyāsanā appekacce Bhagavantaṁ </span></p><p class="p3"><span class="t1">° Then some yakkhas rose from their seats and after  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abhivādetvā padakkhiṇaṁ katvā, tatthevantaradhāyiṁsu.</span></p><p class="p3"><span class="t1">worshipping and circumambulating the Fortunate One, vanished right there. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce Bhagavatā saddhiṁ sammodiṁsu, sammodanīyaṁ</span></p><p class="p3"><span class="t1">° Some exchanged greetings with the Fortunate One, and after </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kathaṁ sārānīyaṁ vītisāretvā tatthevantaradhāyiṁsu.</span></p><p class="p3"><span class="t1">exchanging polite and courteous greetings, vanished right there. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce yena Bhagavā tenañjaliṁ </span></p><p class="p3"><span class="t1">° Some, after raising their hands in respectful salutation </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">paṇāmetvā tatthevantaradhāyiṁsu.</span></p><p class="p3"><span class="t1">to the Fortunate One, vanished right there. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Appekacce nāmagottaṁ sāvetvā tatthevantaradhāyiṁsu.</span></p><p class="p3"><span class="t1">Some, after announcing their name and family vanished right there. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce tuṇhībhūtā tatthevantaradhāyiṁsū ti.</span></p><p class="p3"><span class="t1">And some, while keeping silent, vanished right there. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tatiyakabhāṇavāraṁ Niṭṭhitaṁ</span>{193}</p>', '-197-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', 'Catutthakabhāṇavāraṁ The Fourth Recital ', 'title', 198);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '29b. Āṭānāṭiyasuttaṁ The Āṭānāṭiya Discourse ', 'title', 198);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 198, '<p class="p3"><span class="t5">The Third Recital is Finished</span></p><p class="p3"><span class="t3"> </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h2>Catutthakabhāṇavāraṁ The Fourth Recital </h2></p><p class="p3"><span class="t1">  </span></p><p class="p3"><h3>29b. Āṭānāṭiyasuttaṁ The Āṭānāṭiya Discourse </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">(<span class="t5">Part Two)</span> </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho Bhagavā tassā</span>{194}<span class="t5"> rattiyā accayena bhikkhū āmantesi:</span></p><p class="p3"><span class="t1">Then the Fortunate One when that night had passed addressed the monks, saying: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Imaṁ bhikkhave rattiṁ Cattāro Mahārājā,</span>{195}</p><p class="p3"><span class="t1">“This night, monks, the Four Great Kings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mahatiyā ca yakkhasenāya, mahatiyā ca gandhabbasenāya,</span></p><p class="p3"><span class="t1">with a great army of yakkhas, with a great army of gandhabbas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mahatiyā ca kumbhaṇḍasenāya, mahatiyā ca nāgasenāya, </span></p><p class="p3"><span class="t1">with a great army of kumbhaṇḍas, with a great army of nāgas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">catuddisaṁ rakkhaṁ ṭhapetvā, catuddisaṁ gumbaṁ ṭhapetvā, </span></p><p class="p3"><span class="t1">having set up a protection over the four quarters, having set serried troops at the four directions, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">catuddisaṁ ovaraṇaṁ ṭhapetvā, abhikkantāya rattiyā,</span></p><p class="p3"><span class="t1">having set up a barricade at the four directions, at the end of the night, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abhikkantavaṇṇā kevalakappaṁ Gijjhakūṭaṁ obhāsetvā, </span></p><p class="p3"><span class="t1">having lit up the whole of Vultures’ Peak with their surpassing beauty, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yenāhaṁ tenupasaṅkamiṁsu, upasaṅkamitvā</span></p><p class="p3"><span class="t1">approached me, and after approaching </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">maṁ abhivādetvā, ekam-antaṁ nisīdiṁsu. </span></p><p class="p3"><span class="t1">and worshipping me, they sat down on one side.  </span></p><p class="p3"><span class="t5"> </span></p>', '-198-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 199, '<p class="p3"><span class="t5">Te pi kho, bhikkhave,</span>{196}<span class="t5"> yakkhā,</span></p><p class="p3"><span class="t1">Then of those yakkhas, monks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appekacce maṁ abhivādetvā ekam-antaṁ nisīdiṁsu.</span></p><p class="p3"><span class="t1">some, after worshipping me, sat down on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce mama</span>{197}<span class="t5"> saddhiṁ sammodiṁsu,</span></p><p class="p3"><span class="t1">Some exchanged greetings with me,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">sammodanīyaṁ kathaṁ sārānīyaṁ vītisāretvā, ekam-antaṁ nisīdiṁsu. </span></p><p class="p3"><span class="t1">and after exchanging polite and courteous greetings, sat down on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce yenāhaṁ tenañjaliṁ paṇāmetvā, ekam-antaṁ nisīdiṁsu.</span></p><p class="p3"><span class="t1">Some, after raising their hands in respectful salutation to me, sat down on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce nāmagottaṁ sāvetvā,</span><span class="t16"> ekam-antaṁ nisīdiṁsu.</span></p><p class="p3"><span class="t1">Some, after announcing their name and family, sat down on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t16">Appekacce tuṇhībhūtā ekam-antaṁ nisīdiṁsu.</span></p><p class="p3"><span class="t1">Some, while keeping silent, sat down on one side. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ekam-antaṁ nisinno kho bhikkhave</span>{198}<span class="t5"> Vessavaṇo Mahārājā maṁ etad-avoca:</span></p><p class="p3"><span class="t1">Then, while sitting on one side, monks, the Great King Vessavaṇa said this to me: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Santi hi bhante uḷārā yakkhā Bhagavato appasannā,</span></p><p class="p3"><span class="t1">“There are, reverend Sir, some high ranking yakkhas who are not pleased with the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santi hi bhante uḷārā yakkhā Bhagavato pasannā,</span></p><p class="p3"><span class="t1">there are, reverend Sir, some high ranking yakkhas who are pleased with the Fortunate One, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">santi hi bhante majjhimā yakkhā Bhagavato appasannā,</span></p><p class="p3"><span class="t1">there are, reverend Sir, some middle ranking yakkhas who are not pleased with the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santi hi bhante majjhimā yakkhā Bhagavato pasannā,</span></p><p class="p3"><span class="t1">there are, reverend Sir, some middle ranking yakkhas who are pleased with the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p>', '-199-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 200, '<p class="p3"><span class="t5">santi hi bhante nīcā yakkhā Bhagavato appasannā,</span></p><p class="p3"><span class="t1">there are, reverend Sir, some low ranking yakkhas who are not pleased with the Fortunate One, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santi hi bhante nīcā yakkhā Bhagavato pasannā.</span></p><p class="p3"><span class="t1">there are, reverend Sir, some low ranking yakkhas who are pleased with the Fortunate One. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yebhuyyena kho pana bhante yakkhā appasannā yeva Bhagavato.</span></p><p class="p3"><span class="t1">But, reverend Sir, almost all of the yakkhas are not pleased with the Fortunate One. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Taṁ kissa hetu?</span></p><p class="p3"><span class="t1">What is the reason for that? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhagavā hi bhante pāṇātipātā veramaṇiyā Dhammaṁ deseti, </span></p><p class="p3"><span class="t1">Because, reverend Sir, the Fortunate One preaches the Dhamma of refraining from killing living creatures, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">adinnādānā veramaṇiyā Dhammaṁ deseti, </span></p><p class="p3"><span class="t1">preaches the Dhamma of refraining from taking what has not been given, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kāmesu micchācārā veramaṇiyā Dhammaṁ deseti, </span></p><p class="p3"><span class="t1">preaches the Dhamma of refraining from sexual misconduct, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">musāvādā veramaṇiyā Dhammaṁ deseti, </span></p><p class="p3"><span class="t1">preaches the Dhamma of refraining from false speech, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">surāmerayamajjapamādaṭṭhānā veramaṇiyā Dhammaṁ deseti.</span></p><p class="p3"><span class="t1">preaches the Dhamma of refraining from liquor, wines, or intoxicants which cause heedlessness. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Yebhuyyena kho pana bhante yakkhā appaṭiviratā yeva pāṇātipātā, </span></p><p class="p3"><span class="t1">But, reverend Sir, almost all of the yakkhas do not refrain from killing living creatures, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appaṭiviratā adinnādānā, </span></p><p class="p3"><span class="t1">do not refrain from taking what has not been given, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appaṭiviratā kāmesu micchācārā, </span></p><p class="p3"><span class="t1">do not refrain from sexual misconduct, </span></p><p class="p3"><span class="t1">  </span></p>', '-200-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 201, '<p class="p3"><span class="t5">appaṭiviratā musāvādā, </span></p><p class="p3"><span class="t1">do not refrain from false speech, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appaṭiviratā surāmerayamajjapamādaṭṭhānā. </span></p><p class="p3"><span class="t1">do not refrain from liquors, wines, or intoxicants which cause heedlessness. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tesaṁ taṁ hoti appiyaṁ amanāpaṁ. </span></p><p class="p3"><span class="t1">To them that is neither dear nor appealing. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Santi hi bhante Bhagavato sāvakā araññe,</span></p><p class="p3"><span class="t1">There are, reverend Sir, disciples of the Fortunate One in the wilderness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vanapatthāni pantāni senāsanāni paṭisevanti,</span></p><p class="p3"><span class="t1">who are practising in remote jungle dwelling places, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">appasaddāni, appanigghosāni, vijanavātāni,</span></p><p class="p3"><span class="t1">where there is little sound, little noise, which have a lonely atmosphere, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">manussarāhaseyyakāni, paṭisallānasāruppāni. </span></p><p class="p3"><span class="t1">lying hidden away from men, which are suitable for seclusion. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tattha santi uḷārā yakkhā nivāsino </span></p><p class="p3"><span class="t1">And there are high ranking yakkhas settled there </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ye imasmiṁ Bhagavato pāvacane appasannā,</span></p><p class="p3"><span class="t1">who are not pleased with the word of the Fortunate One,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tesaṁ pasādāya uggaṇhātu bhante Bhagavā “Āṭānāṭiyaṁ” rakkhaṁ,</span></p><p class="p3"><span class="t1">To please then, reverend Sir, may the Fortunate One learn this “Āṭānāṭiya” protection, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">bhikkhūnaṁ, bhikkhuṇīnaṁ, upāsakānaṁ, upāsikānaṁ,</span></p><p class="p3"><span class="t1">for the monks’, nuns’, laymen’s, and laywomen’s </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">guttiyā, rakkhāya, avihiṁsāya, phāsuvihārāyā” ti.</span></p><p class="p3"><span class="t1">guard, protection, freedom from harm, and comfortable living.” </span></p><p class="p3"><span class="t1">  </span></p>', '-201-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 202, '<p class="p3"><span class="t5">Adhivāsesiṁ kho ahaṁ bhikkhave tuṇhībhāvena. </span></p><p class="p3"><span class="t1">And I, monks, by keeping silent, gave consent. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t15">Atha kho bhikkhave</span>{199}<span class="t15"> Vessavaṇo Mahārājā maṁ adhivāsanaṁ viditvā</span></p><p class="p3"><span class="t1">Then the Great King Vessavaṇa, monks, having understood my consent </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tāyaṁ velāyaṁ imaṁ “Āṭānāṭiyaṁ” rakkhaṁ abhāsi:</span></p><p class="p3"><span class="t1">on that occasion recited this “Āṭānāṭiya” protection: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The Seven Buddhas</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Vipassissa namatthu, Cakkhumantassa sirīmato,</span></p><p class="p3"><span class="t1">“May you revere Vipassī, the glorious Visionary, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sikhissa pi namatthu, sabbabhūtānukampino, </span>[53]</p><p class="p3"><span class="t1">may you revere Sikhī, who has pity on all beings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vessabhussa namatthu, nahātakassa tapassino,</span></p><p class="p3"><span class="t1">may you revere Vessabhū, the austere one, cleansed (of corruptions), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">namatthu Kakusandhassa, Mārasenāpamaddino, </span>[54]</p><p class="p3"><span class="t1">may you revere Kakusandha, who has crushed Māra’s army, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Koṇāgamanassa namatthu, brāhmaṇassa vusīmato,</span></p><p class="p3"><span class="t1">may you revere Koṇāgamana, the accomplished brahmin, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kassapassa namatthu, vippamuttassa sabbadhi. </span>[55]</p><p class="p3"><span class="t1">may you revere Kassapa, who is free in every respect. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Aṅgīrasassa namatthu, Sakyaputtassa sirīmato,</span></p><p class="p3"><span class="t1">May you revere Aṅgīrasa, the glorious son of the Sakyans, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yo imaṁ Dhammam-adesesi, sabbadukkhā panūdanaṁ. </span>[56]</p><p class="p3"><span class="t1">he who preached this Dhamma, which is the dispelling of all suffering. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye cāpi nibbutā loke, yathābhūtaṁ vipassisuṁ,</span></p>', '-202-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 203, '<p class="p3"><span class="t1">Those who are emancipated in the world, who have insight (into things) as they are, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te janā apisuṇā,</span>{200}<span class="t5"> mahantā vītasāradā, </span>[57]</p><p class="p3"><span class="t1">those people who are free from malicious speech, who are great and fully mature, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hitaṁ devamanussānaṁ, yaṁ namassanti Gotamaṁ,</span></p><p class="p3"><span class="t1">they will revere that Gotama, who is of benefit to gods and men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vijjācaraṇasampannaṁ, mahantaṁ vītasāradaṁ. </span>[58]</p><p class="p3"><span class="t1">who has understanding and good conduct, who is great and fully mature. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The East</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yato uggacchatī</span>{201}<span class="t5"> suriyo, Ādicco maṇḍalī mahā,</span></p><p class="p3"><span class="t1">From where the sun comes up, the son of Aditi, the great circle, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa cuggacchamānassa, saṁvarī pi nirujjhati, </span>[59]</p><p class="p3"><span class="t1">while that one is coming up, the darkness of night comes to an end, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa cuggate suriye, divaso ti pavuccati,</span></p><p class="p3"><span class="t1">and after the sun has come up, it is said to be the daytime, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">rahado pi tattha gambhīro, samuddo saritodako, </span>[60]</p><p class="p3"><span class="t1">there is a deep lake in that place, an ocean where the waters have flowed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evaṁ naṁ tattha jānanti samuddo saritodako,</span></p><p class="p3"><span class="t1">thus in that place they know there is an ocean where the waters have flowed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ito sā Purimā disā, iti naṁ ācikkhatī</span>{202}<span class="t5"> jano. </span>[61]</p><p class="p3"><span class="t1">from here that is the Easterly direction, so the people declare. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ disaṁ abhipāleti Mahārājā yasassi so,</span></p><p class="p3"><span class="t1">That direction is watched over by a resplendent Great King, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbānaṁ ādhipati, Dhataraṭṭho iti nāma so. </span>[62]</p><p class="p3"><span class="t1">he is the master of the gandhabbas, Dhataraṭṭha, such is his name. </span></p>', '-203-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 204, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ramatī</span>{203}<span class="t5"> naccagītehi, gandhabbehi purakkhato,</span></p><p class="p3"><span class="t1">He delights in song and dance, he is honoured by gandhabbas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, ekanāmā ti me sutaṁ, </span>[63]</p><p class="p3"><span class="t1">he also has a great many sons, all of one name, so I have heard, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asītiṁ dasa eko ca, Indanāmā mahabbalā,</span></p><p class="p3"><span class="t1">they are eighty and ten and one, Inda by name, ones of great strength, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te cāpi Buddhaṁ disvāna, Buddhaṁ ādiccabandhunaṁ, </span>[64]</p><p class="p3"><span class="t1">they, having seen the Awakened One, the Buddha, kinsman of the sun, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dūrato va namassanti, mahantaṁ vītasāradaṁ:</span></p><p class="p3"><span class="t1">from afar, do reverence him, who is great and fully mature: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Namo te purisājañña! Namo te purisuttama! </span>[65]</p><p class="p3"><span class="t1">“Reverence to you, excellent one! Reverence to you, supreme one! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kusalena samekkhasi, amanussā pi taṁ vandanti,</span></p><p class="p3"><span class="t1">You have looked on us with goodness, the non-human beings worship you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sutaṁ netaṁ abhiṇhaso, tasmā evaṁ vademase: </span>[66]</p><p class="p3"><span class="t1">we have heard this repeatedly, therefore we should speak like this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Jinaṁ vandatha Gotamaṁ, Jinaṁ vandāma Gotamaṁ,</span></p><p class="p3"><span class="t1">“You should worship the victor Gotama, we should worship the victor Gotama, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vijjācaraṇasampannaṁ, Buddhaṁ vandāma Gotamaṁ!”” </span>[67]</p><p class="p3"><span class="t1">who has understanding and good conduct, we should worship the Buddha Gotama!”” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The South</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yena petā pavuccanti, pisuṇā piṭṭhimaṁsikā,</span></p><p class="p3"><span class="t1">There they say (go) the departed, who speak maliciously, backbiters, </span></p><p class="p3"><span class="t1">  </span></p>', '-204-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 205, '<p class="p3"><span class="t5">pāṇātipātino luddā, corā nekatikā janā,</span></p><p class="p3"><span class="t1">killers of creatures, hunters, thieves, and fraudulent people, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ito sā Dakkhiṇā disā, iti naṁ ācikkhatī jano. </span>[68]</p><p class="p3"><span class="t1">from here that is the Southerly direction, so the people declare. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ disaṁ abhipāleti Mahārājā yasassi so,</span></p><p class="p3"><span class="t1">That direction is watched over by a resplendent Great King, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍhānaṁ ādhipati, Virūḷho iti nāma so. </span>[69]</p><p class="p3"><span class="t1">he is the master of the kumbhaṇḍhas, Virūḷha, such is his name. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ramatī naccagītehi, kumbhaṇḍhehi purakkhato,</span></p><p class="p3"><span class="t1">He delights in song and dance, he is honoured by kumbhaṇḍhas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, ekanāmā ti me sutaṁ, </span>[70]</p><p class="p3"><span class="t1">he also has a great many sons, all of one name, so I have heard, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asītiṁ dasa eko ca, Indanāmā mahabbalā,</span></p><p class="p3"><span class="t1">they are eighty and ten and one, Inda by name, ones of great strength, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te cāpi Buddhaṁ disvāna, Buddhaṁ ādiccabandhunaṁ, </span>[71]</p><p class="p3"><span class="t1">they, having seen the Awakened One, the Buddha, kinsman of the sun, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dūrato</span>{204}<span class="t5"> va namassanti, mahantaṁ vītasāradaṁ:</span></p><p class="p3"><span class="t1">from afar, do reverence him, who is great and fully mature: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Namo te purisājañña! Namo te purisuttama! </span>[72]</p><p class="p3"><span class="t1">“Reverence to you, excellent one! Reverence to you, supreme one! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kusalena samekkhasi, amanussā pi taṁ vandanti,</span></p><p class="p3"><span class="t1">You have looked on us with goodness, the non-human beings worship you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sutaṁ netaṁ abhiṇhaso, tasmā evaṁ vademase: </span>[73]</p><p class="p3"><span class="t1">we have heard this repeatedly, therefore we should speak like this: </span></p><p class="p3"><span class="t1">  </span></p>', '-205-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 206, '<p class="p3"><span class="t5">“Jinaṁ vandatha Gotamaṁ, Jinaṁ vandāma Gotamaṁ,</span></p><p class="p3"><span class="t1">“You should worship the victor Gotama, we should worship the victor Gotama, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vijjācaraṇasampannaṁ, Buddhaṁ vandāma Gotamaṁ!”” </span>[74]</p><p class="p3"><span class="t1">who has understanding and good conduct, we should worship the Buddha Gotama!”” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The West</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yattha coggacchati suriyo, Ādicco maṇḍalī mahā,</span></p><p class="p3"><span class="t1">That place where the sun goes down, the son of Aditi, the great circle, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa coggacchamānassa divaso pi nirujjhati, </span>[75]</p><p class="p3"><span class="t1">while that one is going down the light of day comes to an end, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa coggate suriye, saṁvarī ti pavuccati,</span></p><p class="p3"><span class="t1">and after the sun has gone down, it is said to be night-time, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">rahado pi tattha gambhīro, samuddo saritodako, </span>[76]</p><p class="p3"><span class="t1">there is a deep lake in that place, an ocean where the waters have flowed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evaṁ naṁ tattha jānanti samuddo saritodako,</span></p><p class="p3"><span class="t1">thus in that place they know there is an ocean where the waters have flowed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ito sā Pacchimā disā, iti naṁ ācikkhatī jano. </span>[77]</p><p class="p3"><span class="t1">from here that is the Westerly direction, so the people declare. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ disaṁ abhipāleti Mahārājā yasassi so,</span></p><p class="p3"><span class="t1">That direction is watched over by a resplendent Great King, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgānaṁ ādhipati, Virūpakkho iti nāma so. </span>[78]</p><p class="p3"><span class="t1">he is the master of the nāgas, Virūpakkha, such is his name. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ramatī naccagītehi, nāgehi purakkhato,</span></p><p class="p3"><span class="t1">He delights in song and dance, he is honoured by nāgas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, ekanāmā ti me sutaṁ, </span>[79]</p>', '-206-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 207, '<p class="p3"><span class="t1">he also has a great many sons, all of one name, so I have heard, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asītiṁ dasa eko ca, Indanāmā mahabbalā,</span></p><p class="p3"><span class="t1">they are eighty and ten and one, Inda by name, ones of great strength, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te cāpi Buddhaṁ disvāna, Buddhaṁ ādiccabandhunaṁ, </span>[80]</p><p class="p3"><span class="t1">they, having seen the Awakened One, the Buddha, kinsman of the sun, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dūrato va namassanti, mahantaṁ vītasāradaṁ:</span></p><p class="p3"><span class="t1">from afar, do reverence him, who is great and fully mature: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Namo te purisājañña! Namo te purisuttama! </span>[81]</p><p class="p3"><span class="t1">“Reverence to you, excellent one! Reverence to you, supreme one! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kusalena samekkhasi, amanussā pi taṁ vandanti,</span></p><p class="p3"><span class="t1">You have looked on us with goodness, the non-human beings worship you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sutaṁ netaṁ abhiṇhaso, tasmā evaṁ vademase: </span>[82]</p><p class="p3"><span class="t1">we have heard this repeatedly, therefore we should speak like this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Jinaṁ vandatha Gotamaṁ, Jinaṁ vandāma Gotamaṁ,</span></p><p class="p3"><span class="t1">“You should worship the victor Gotama, we should worship the victor Gotama, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vijjācaraṇasampannaṁ, Buddhaṁ vandāma Gotamaṁ!”” </span>[83]</p><p class="p3"><span class="t1">who has understanding and good conduct, we should worship the Buddha Gotama!”” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The North</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yena Uttarakuru rammā, Mahāneru sudassano,</span></p><p class="p3"><span class="t1">Where delightful Uttarakuru is, and the good looking Mount Neru, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">manussā tattha jāyanti amamā apariggahā. </span>[84]</p><p class="p3"><span class="t1">the men who are born in that place are unselfish, without possessions. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na te bījaṁ pavapanti, na pi nīyanti naṅgalā,</span></p><p class="p3"><span class="t1">They do not sow the seed, and nor are there ploughs led along, </span></p>', '-207-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 208, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">akaṭṭhapākimaṁ sāliṁ paribhuñjanti mānusā, </span>[85]</p><p class="p3"><span class="t1">the rice that men enjoy the use of there matures in untilled ground, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">akaṇaṁ athusaṁ suddhaṁ, sugandhaṁ taṇḍulapphalaṁ,</span></p><p class="p3"><span class="t1">without husk powder or chaff, pure, sweet smelling grains of the finest rice, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tuṇḍikīre pacitvāna, tato bhuñjanti bhojanaṁ. </span>[86]</p><p class="p3"><span class="t1">having cooked it on hot rocks (without smoke), they then enjoy their food. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gāviṁ ekakhuraṁ katvā anuyanti disodisaṁ,</span></p><p class="p3"><span class="t1">Having made their cows solid-hooved they go about from place to place, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pasuṁ ekakhuraṁ katvā anuyanti disodisaṁ, </span>[87]</p><p class="p3"><span class="t1">having made their kine solid-hooved they go about from place to place, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">itthivāhanaṁ katvā anuyanti disodisaṁ,</span></p><p class="p3"><span class="t1">having made women their vehicle they go about from place to place, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">purisavāhanaṁ katvā anuyanti disodisaṁ, </span>[88]</p><p class="p3"><span class="t1">having made men their vehicle they go about from place to place, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumārivāhanaṁ katvā anuyanti disodisaṁ,</span></p><p class="p3"><span class="t1">having made girls their vehicle they go about from place to place, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumāravāhanaṁ katvā anuyanti disodisaṁ. </span>[89]</p><p class="p3"><span class="t1">having made boys their vehicle they go about from place to place. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Te yāne abhirūhitvā,</span></p><p class="p3"><span class="t1">Having mounted their (various) carriages, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbā disā anupariyanti pacārā tassa rājino,</span></p><p class="p3"><span class="t1">the messengers of that King go around in all directions, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hatthiyānaṁ assayānaṁ dibbaṁ yānaṁ upaṭṭhitaṁ. </span>[90]</p><p class="p3"><span class="t1">being furnished with elephant- and horse- and divine-carriages. </span></p>', '-208-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 209, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pāsādā sivikā ceva Mahārājassa yasassino,</span></p><p class="p3"><span class="t1">And for that Great and resplendent King there are palaces and palanquins, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tassa ca nagarā ahū, antalikkhe sumāpitā: </span>[91]</p><p class="p3"><span class="t1">and there are cities for him also, that are well built in the heavens (called): </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Āṭānāṭā, Kusināṭā, Parakusināṭā, Nāṭapuriyā, Parakusitanāṭā.</span></p><p class="p3"><span class="t1">Āṭānāṭā, Kusināṭā, Parakusināṭā, Nāṭapuriyā, Parakusitanāṭā. </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Uttarena Kapīvanto, Janogham-aparena ca,</span></p><p class="p3"><span class="t1">To the North is Kapīvanta, and on the other side is Janogha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Navanavatiyo, Ambara-ambaravatiyo, Ālakamandā nāma rājadhāni.</span></p><p class="p3"><span class="t1">Navanavatiya, Ambara-ambaravatiya, and the king’s capital named Ālakamandā. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kuverassa kho pana mārisa Mahārājassa Visāṇā nāma rājadhāni,</span></p><p class="p3"><span class="t1">The Great King Kuvera’s capital, dear Sir, is named Visāṇā, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tasmā Kuvero Mahārājā Vessavaṇo ti pavuccati.</span></p><p class="p3"><span class="t1">therefore is the Great King also called Vessavaṇa. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Paccesanto pakāsenti: Tatolā, Tattalā, Tatotalā,</span></p><p class="p3"><span class="t1">These each individually inform (the King): Tatolā, Tattalā, Tatotalā, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ojasi, Tejasi, Tatojasi, Sūro, Rājā, Ariṭṭho, Nemi.</span></p><p class="p3"><span class="t1">Ojasi, Tejasi, Tatojasi, Sūra, Rājā, Ariṭṭha, Nemi. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Rahado pi tattha Dharaṇī nāma,</span></p><p class="p3"><span class="t1">There is a lake in that place also, named Dharaṇī, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yato meghā pavassanti, vassā yato patāyanti.</span></p><p class="p3"><span class="t1">and from there the clouds rain down, from there the rains spread. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabhā pi tattha Bhagalavatī nāma yattha yakkhā payirupāsanti.</span></p><p class="p3"><span class="t1">There also is the public hall named Bhagalavatī where the yakkhas assemble. </span></p>', '-209-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 210, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tattha niccaphalā rukkhā, nānādijagaṇāyutā,</span></p><p class="p3"><span class="t1">There the trees are always in fruit, and have a crowd of birds of diverse kinds, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mayūrakoñcābhirudā, kokilābhi hi vaggubhi, </span>[92]</p><p class="p3"><span class="t1">resounding with peacocks and hens, and also with the lovely cuckoos, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jīvaṁjīvakasaddettha, atho oṭṭhavacittakā,</span></p><p class="p3"><span class="t1">there is the pheasant who calls out “live on!”, and the bird who calls out “lift up your minds!”, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kukutthakā kuḷīrakā, vane pokkharasātakā, </span>[93]</p><p class="p3"><span class="t1">there are the jungle fowl, and golden cocks, and in the wood the lotus crane, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sukasāḷikasaddettha, daṇḍamāṇavakāni ca,</span></p><p class="p3"><span class="t1">there are the sounds of parrot and mynah, and birds who have young mens’ faces, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sobhati sabbakālaṁ sā Kuveranalinī sadā,</span></p><p class="p3"><span class="t1">and Kuvera’s lotus pond is ever attractive, all of the time, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ito sā Uttarā disā, iti naṁ ācikkhatī jano. </span>[94]</p><p class="p3"><span class="t1">from here that is the Northerly direction, so the people declare. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ disaṁ abhipāleti Mahārājā yasassi so,</span></p><p class="p3"><span class="t1">That direction is watched over by a resplendent Great King, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhānaṁ ādhipati, Kuvero iti nāma so. </span>[95]</p><p class="p3"><span class="t1">he is the master of the yakkhas, Kuvera, such is his name. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ramatī naccagītehi, yakkhehi purakkhato,</span></p><p class="p3"><span class="t1">He delights in song and dance, he is honoured by yakkhas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttā pi tassa bahavo, ekanāmā ti me sutaṁ, </span>[96]</p><p class="p3"><span class="t1">he also has a great many sons, all of one name, so I have heard, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asītiṁ dasa eko ca, Indanāmā mahabbalā,</span></p><p class="p3"><span class="t1">they are eighty and ten and one, Inda by name, ones of great strength, </span></p>', '-210-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 211, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te cāpi Buddhaṁ disvāna, Buddhaṁ ādiccabandhunaṁ, </span>[97]</p><p class="p3"><span class="t1">they, having seen the Awakened One, the Buddha, kinsman of the sun, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dūrato</span>{205}<span class="t5"> va namassanti, mahantaṁ vītasāradaṁ:</span></p><p class="p3"><span class="t1">from afar, do reverence him, who is great and fully mature: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Namo te purisājañña! Namo te purisuttama! </span>[98]</p><p class="p3"><span class="t1">“Reverence to you, excellent one! Reverence to you, supreme one! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kusalena samekkhasi, amanussā pi taṁ vandanti,</span></p><p class="p3"><span class="t1">You have looked on us with goodness, the non-human beings worship you, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sutaṁ netaṁ abhiṇhaso, tasmā evaṁ vademase: </span>[99]</p><p class="p3"><span class="t1">we have heard this repeatedly, therefore we should speak like this: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jinaṁ vandatha Gotamaṁ, Jinaṁ vandāma Gotamaṁ,</span></p><p class="p3"><span class="t1">“You should worship the victor Gotama, we should worship the victor Gotama, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vijjācaraṇasampannaṁ, Buddhaṁ vandāma Gotaman!””-ti </span><span class="t5">[100]</span></p><p class="p3"><span class="t1">who has understanding and good conduct, we should worship the Buddha Gotama!”” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ kho sā mārisa “Āṭānāṭiyā” rakkhā,</span></p><p class="p3"><span class="t1">This, dear Sir, is the “Āṭānāṭiya” protection, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhikkhūnaṁ, bhikkhuṇīnaṁ, upāsakānaṁ, upāsikānaṁ,</span></p><p class="p3"><span class="t1">for the monks’, nuns’, laymen’s, and laywomen’s </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">guttiyā, rakkhāya, avihiṁsāya, phāsuvihārāyā ti.</span></p><p class="p3"><span class="t1">guard, protection, freedom from harm, and comfortable living. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yassa kassaci mārisa - bhikkhussa vā bhikkhuṇiyā vā </span></p><p class="p3"><span class="t1">Whoever, dear Sir - be they monk or nun </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">upāsakassa vā upāsikāya vā - ayaṁ “Āṭānāṭiyā” rakkhā suggahitā,</span></p><p class="p3"><span class="t1">or<span class="t5"> </span>layman or laywoman - learns this “Āṭānāṭiya” protection well, </span></p>', '-211-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 212, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhavissati samattā pariyāputā, tañ-ce amanusso -</span></p><p class="p3"><span class="t1">and will master it in its entirety, then if a non-human being - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkho vā yakkhiṇī vā yakkhapotako vā</span></p><p class="p3"><span class="t1">be they male yakkha or female yakkha or yakkha boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhapotikā vā yakkhamahāmatto vā</span></p><p class="p3"><span class="t1">or yakkha girl or yakkha minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhapārisajjo vā yakkhapacāro vā</span></p><p class="p3"><span class="t1">or yakkha councillor or yakkha messenger </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbo vā gandhabbī vā gandhabbapotako vā</span></p><p class="p3"><span class="t1">or male gandhabba or female gandhabba or gandhabba boy </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">gandhabbapotikā vā gandhabbamahāmatto vā</span></p><p class="p3"><span class="t1">or gandhabba girl or gandhabba minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbapārisajjo vā gandhabbapacāro vā</span></p><p class="p3"><span class="t1">or gandhabba councillor or gandhabba messenger </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍo vā kumbhaṇḍī vā kumbhaṇḍapotako vā</span></p><p class="p3"><span class="t1">or male kumbhaṇḍa or female kumbhaṇḍa or kumbhaṇḍa boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍapotikā vā kumbhaṇḍamahāmatto vā</span></p><p class="p3"><span class="t1">or kumbhaṇḍa girl or kumbhaṇḍa minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍapārisajjo vā kumbhaṇḍapacāro vā</span></p><p class="p3"><span class="t1">or kumbhaṇḍa councillor or kumbhaṇḍa messenger </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgo vā nāginī vā nāgapotako vā</span></p><p class="p3"><span class="t1">or male nāga or female nāga or nāga boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgapotikā vā nāgamahāmatto vā</span></p><p class="p3"><span class="t1">or nāga girl or nāga minister </span></p>', '-212-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 213, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgapārisajjo vā nāgapacāro vā -</span></p><p class="p3"><span class="t1">or nāga councillor or nāga messenger - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t15">paduṭṭhacitto gacchantaṁ vā anugaccheyya, ṭhitaṁ vā upatiṭṭheyya, </span></p><p class="p3"><span class="t1">with a wicked mind should come near while they are going, or stand near while they are standing, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nisinnaṁ vā upanisīdeyya, nipannaṁ vā upanipajjeyya, </span></p><p class="p3"><span class="t1">or sit near while they are sitting, or lie near while they are lying, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">na me so mārisa amanusso labheyya</span></p><p class="p3"><span class="t1">then that non-human being, dear Sir, would not receive </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gāmesu vā nigamesu vā sakkāraṁ vā garukāraṁ vā. </span></p><p class="p3"><span class="t1">in my villages and towns, honour or respect. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na me so mārisa amanusso labheyya</span></p><p class="p3"><span class="t1">That non-human being, dear Sir, would not receive </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Ālakamandāya rājadhāniyā vatthuṁ vā vāsaṁ vā.</span></p><p class="p3"><span class="t1">in my royal city Ālakamandā, ground or dwelling. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Na me so mārisa amanusso</span>{206}<span class="t5"> labheyya</span></p><p class="p3"><span class="t1">That non-human being, dear Sir, would not be able </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhānaṁ samitiṁ gantuṁ. </span></p><p class="p3"><span class="t1">to go to the gathering of the yakkhas. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Api-ssu naṁ mārisa amanussā</span></p><p class="p3"><span class="t1">Further, dear Sir, non-human beings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anavayham-pi naṁ kareyyuṁ avivayhaṁ. </span></p><p class="p3"><span class="t1">would not give or take him in marriage. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Api-ssu naṁ mārisa amanussā </span></p><p class="p3"><span class="t1">Further, dear Sir, non-human beings </span></p>', '-213-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 214, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">attāhi pi paripuṇṇāhi paribhāsāhi paribhāseyyuṁ.</span></p><p class="p3"><span class="t1">would abuse him with very personal abuse. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Api-ssu naṁ mārisa amanussā</span></p><p class="p3"><span class="t1">Further, dear Sir, non-human beings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">rittam-pi pattaṁ sīse nikkujjeyyuṁ. </span></p><p class="p3"><span class="t1">would drop an empty bowl over his head. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Api-ssu naṁ mārisa amanussā</span></p><p class="p3"><span class="t1">Further, dear Sir, non-human beings </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sattadhā pissa muddhaṁ phāleyyuṁ.</span></p><p class="p3"><span class="t1">would split his head into seven pieces. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Santi hi mārisa amanussā caṇḍā, ruddā, rabhasā,</span></p><p class="p3"><span class="t1">There are non-human beings, dear Sir, who are fierce, cruel, and violent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te neva Mahārājānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">na Mahārājānaṁ purisakānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings’ men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na Mahārājānaṁ purisakānaṁ purisakānaṁ ādiyanti,</span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings’ mens’ men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te kho te mārisa amanussā Mahārājānaṁ avaruddhā nāma vuccanti.</span></p><p class="p3"><span class="t1">and those non-human beings, dear Sir, are said to be in revolt against the Great Kings. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Seyyathā pi mārisa rañño Māgadhassa vijite corā, </span></p><p class="p3"><span class="t1">Just as, dear Sir, there are thieves in the king of Magadha’s realm, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te neva rañño Māgadhassa ādiyanti, </span></p><p class="p3"><span class="t1">who do not take notice of the king of Magadha, </span></p>', '-214-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 215, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na rañño Māgadhassa purisakānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">who do not take notice of the king of Magadha’s men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na rañño Māgadhassa purisakānaṁ purisakānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">who do not take notice of the king of Magadha’s mens’ men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te kho te mārisa mahācorā </span></p><p class="p3"><span class="t1">and those great thieves, dear Sir, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">rañño Māgadhassa avaruddhā nāma vuccanti.</span></p><p class="p3"><span class="t1">are said to be in revolt against the king of Magadha. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Evam-eva kho mārisa santi hi amanussā caṇḍā, ruddā, rabhasā, </span></p><p class="p3"><span class="t1">Even so, dear Sir, there are non-human beings who are fierce, cruel, and violent, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te neva Mahārājānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na Mahārājānaṁ purisakānaṁ ādiyanti, </span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings’ men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">na Mahārājānaṁ purisakānaṁ purisakānaṁ ādiyanti,</span></p><p class="p3"><span class="t1">they do not take notice of the Great Kings’ mens’ men, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">te kho te mārisa amanussā Mahārājānaṁ avaruddhā nāma vuccanti.</span></p><p class="p3"><span class="t1">and those non-human beings, dear Sir, are said to be in revolt against the Great Kings. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yo hi koci mārisa amanusso -</span></p><p class="p3"><span class="t1">Now, dear Sir, whatever non-human beings - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkho vā yakkhiṇī vā yakkhapotako vā</span></p><p class="p3"><span class="t1">be they male yakkha or female yakkha or yakkha boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yakkhapotikā vā yakkhamahāmatto vā</span></p><p class="p3"><span class="t1">or yakkha girl or yakkha minister </span></p>', '-215-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 216, '<p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">yakkhapārisajjo vā yakkhapacāro vā</span></p><p class="p3"><span class="t1">or yakkha councillor or yakkha messenger </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbo vā gandhabbī vā gandhabbapotako vā</span></p><p class="p3"><span class="t1">or male gandhabba or female gandhabba or gandhabba boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbapotikā vā gandhabbamahāmatto vā</span></p><p class="p3"><span class="t1">or gandhabba girl or gandhabba minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gandhabbapārisajjo vā gandhabbapacāro vā</span></p><p class="p3"><span class="t1">or gandhabba councillor or gandhabba messenger </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍo vā kumbhaṇḍī vā kumbhaṇḍapotako vā</span></p><p class="p3"><span class="t1">or male kumbhaṇḍa or female kumbhaṇḍa or kumbhaṇḍa boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍapotikā vā kumbhaṇḍamahāmatto vā</span></p><p class="p3"><span class="t1">or kumbhaṇḍa girl or kumbhaṇḍa minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kumbhaṇḍapārisajjo vā kumbhaṇḍapacāro vā</span></p><p class="p3"><span class="t1">or kumbhaṇḍa councillor or kumbhaṇḍa messenger </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgo vā nāginī vā nāgapotako vā</span></p><p class="p3"><span class="t1">or male nāga or female nāga or nāga boy </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgapotikā vā nāgamahāmatto vā</span></p><p class="p3"><span class="t1">or nāga girl or nāga minister </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nāgapārisajjo vā nāgapacāro vā -</span></p><p class="p3"><span class="t1">or nāga councillor or nāga messenger - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t20">paduṭṭhacitto bhikkhuṁ vā bhikkhuṇiṁ vā upāsakaṁ vā upāsikaṁ vā,</span></p><p class="p3"><span class="t1">with a wicked mind should come near a monk or a nun or a layman or a laywoman </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gacchantaṁ vā anugaccheyya, ṭhitaṁ vā upatiṭṭheyya, </span></p><p class="p3"><span class="t1">while they are going, or stand near while they are standing, </span></p>', '-216-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 217, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nisinnaṁ vā upanisīdeyya, nipannaṁ vā upanipajjeyya, </span></p><p class="p3"><span class="t1">or sit near while they are sitting, or lie near while they are lying, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">imesaṁ yakkhānaṁ mahāyakkhānaṁ senāpatīnaṁ mahāsenāpatīnaṁ, </span></p><p class="p3"><span class="t1">then to these yakkhas, great yakkhas, generals, great generals, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ujjhāpetabbaṁ, vikkanditabbaṁ, viravitabbaṁ: </span></p><p class="p3"><span class="t1">one should call out, one should shout out, one should cry out: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Ayaṁ yakkho gaṇhāti, ayaṁ yakkho āvisati, </span></p><p class="p3"><span class="t1">“This yakkha has seized me, this yakkha has grabbed me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ yakkho heṭheti, ayaṁ yakkho viheṭheti, </span></p><p class="p3"><span class="t1">this yakkha annoys me, this yakkha harasses me, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">ayaṁ yakkho hiṁsati, ayaṁ yakkho vihiṁsati, </span></p><p class="p3"><span class="t1">this yakkha hurts me, this yakkha injures me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ yakkho na muñcatī” ti.</span></p><p class="p3"><span class="t1">this yakkha will not release me.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katamesaṁ yakkhānaṁ, mahāyakkhānaṁ, </span></p><p class="p3"><span class="t1">To which yakkhas, great yakkhas, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">senāpatīnaṁ mahāsenāpatīnaṁ?</span></p><p class="p3"><span class="t1">generals, and great generals? </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Indo, Somo, Varuṇo ca, Bhāradvājo, Pajāpatī,</span>{207}</p><p class="p3"><span class="t1">Inda, Soma, and Varuṇa, Bhāradvāja, Pajāpati, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Candano, Kāmaseṭṭho ca, Kinnughaṇḍu, Nighaṇḍu ca,</span></p><p class="p3"><span class="t1">Candana, and Kāmaseṭṭha, Kinnughaṇḍu, and Nighaṇḍu, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Panādo, Opamañño ca, devasūto ca Mātalī.</span><span class="t5"> [101]</span></p><p class="p3"><span class="t1">Panāda, and Opamañña, and Mātali, the gods’ charioteer. </span></p>', '-217-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 218, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Cittaseno ca gandhabbo, Nalo rājā Janesabho,</span></p><p class="p3"><span class="t1">The gandhabbas Citta and Sena, the kings Nala and Janesabha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sātāgiro, Hemavato, Puṇṇako, Karatiyo, Guḷo, </span><span class="t5">[102]</span></p><p class="p3"><span class="t1">Sātāgira, Hemavata, Puṇṇaka, Karatiya, Guḷa, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sīvako, Mucalindo ca, Vessāmitto, Yugandharo,</span></p><p class="p3"><span class="t1">Sīvaka, and Mucalinda, Vessāmitta, Yugandhara, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gopālo,</span>{208}<span class="t5"> Suppagedho ca, Hiri Nettī ca Mandiyo, </span><span class="t5">[103]</span></p><p class="p3"><span class="t1">Gopāla, and Suppagedha, Hiri, Netti, and Mandiya, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">Pañcālacaṇḍo Ālavako Pajjuno Sumano Sumukho Dadhīmukho,</span>{209}</p><p class="p3"><span class="t1">Pañcālacaṇḍa, Ālavaka, Pajjuna, Sumana, Sumukha, Dadhīmukha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Maṇi, Māṇi, Caro, Dīgho, atho Serissako saha - </span><span class="t5">[104]</span></p><p class="p3"><span class="t1">Maṇi, Māṇi, Cara, Dīgha, together with Serissaka - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">imesaṁ yakkhānaṁ mahāyakkhānaṁ senāpatīnaṁ mahāsenāpatīnaṁ, </span></p><p class="p3"><span class="t1">then to these yakkhas, great yakkhas, generals, and great generals, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ujjhāpetabbaṁ, vikkanditabbaṁ, viravitabbaṁ: </span></p><p class="p3"><span class="t1">one should call out, one should shout out, one should cry out: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Ayaṁ yakkho gaṇhāti, ayaṁ yakkho āvisati, </span></p><p class="p3"><span class="t1">“This yakkha has seized me, this yakkha has grabbed me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ yakkho heṭheti, ayaṁ yakkho viheṭheti, </span></p><p class="p3"><span class="t1">this yakkha annoys me, this yakkha harasses me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ yakkho hiṁsati, ayaṁ yakkho vihiṁsati, </span></p><p class="p3"><span class="t1">this yakkha hurts me, this yakkha injures me, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ayaṁ yakkho na muñcatī” ti.</span></p><p class="p3"><span class="t1">this yakkha will not release me.” </span></p>', '-218-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 219, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ayaṁ kho sā mārisa “Āṭānāṭiyā” rakkhā,</span></p><p class="p3"><span class="t1">This, dear Sir, is the “Āṭānāṭiyā” protection, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhikkhūnaṁ, bhikkhuṇīnaṁ, upāsakānaṁ, upāsikānaṁ,</span></p><p class="p3"><span class="t1">for the monks’, nuns’, laymen’s, and laywomen’s </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">guttiyā, rakkhāya, avihiṁsāya, phāsuvihārāyā ti.</span></p><p class="p3"><span class="t1">guard, protection, freedom from harm, and comfortable living. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Handa ca dāni mayaṁ mārisa gacchāma,</span></p><p class="p3"><span class="t1">And now, dear Sir, we shall go, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bahukiccā mayaṁ bahukaraṇīyā” ti. </span></p><p class="p3"><span class="t1">as we have many duties, and there is much which ought to be done.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Yassa dāni tumhe Mahārājāno kālaṁ maññathā” ti.</span></p><p class="p3"><span class="t1">“Now is the time for whatever you Great Kings are thinking.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atha kho bhikkhave Cattāro Mahārājā</span>{210}<span class="t5"> uṭṭhāyāsanā </span></p><p class="p3"><span class="t1">Then the Four Great Kings, monks, rose from their seats  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">maṁ abhivādetvā padakkhiṇaṁ katvā, tatthevantaradhāyiṁsu. </span></p><p class="p3"><span class="t1">and after worshipping and circumambulating me, vanished right there. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">{211}<span class="t5">Te pi kho bhikkhave</span>{212}<span class="t5"> yakkhā uṭṭhāyāsanā appekacce </span> </span></p><p class="p3"><span class="t1">Then some yakkhas, monks, rose from their seats  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">maṁ abhivādetvā padakkhiṇaṁ katvā, tatthevantaradhāyiṁsu.</span></p><p class="p3"><span class="t1">and after worshipping and circumambulating me, vanished right there. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce mama saddhiṁ sammodiṁsu, </span></p><p class="p3"><span class="t1">Some exchanged greetings with me,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">sammodanīyaṁ kathaṁ sārānīyaṁ vītisāretvā tatthevantaradhāyiṁsu.</span></p><p class="p3"><span class="t1">and after exchanging polite and courteous greetings, vanished right there. </span></p>', '-219-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 220, '<p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t15">Appekacce yenāhaṁ tenañjaliṁ paṇāmetvā tatthevantaradhāyiṁsu.</span></p><p class="p3"><span class="t1">Some, after raising their hands in respectful salutation to me, vanished right there. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce nāmagottaṁ sāvetvā tatthevantaradhāyiṁsu.</span></p><p class="p3"><span class="t1">Some, after announcing their name and family vanished right there. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Appekacce tuṇhībhūtā tatthevantaradhāyiṁsū ti.</span></p><p class="p3"><span class="t1">And some, while keeping silent, vanished right there. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Uggaṇhātha bhikkhave “Āṭānāṭiyaṁ” rakkhaṁ,</span></p><p class="p3"><span class="t1">Learn the “Āṭānāṭiya” protection, monks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pariyāpuṇātha bhikkhave “Āṭānāṭiyaṁ” rakkhaṁ.</span></p><p class="p3"><span class="t1">master the “Āṭānāṭiya” protection, monks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dhāretha bhikkhave “Āṭānāṭiyaṁ” rakkhaṁ,</span></p><p class="p3"><span class="t1">bear in mind the “Āṭānāṭiya” protection, monks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">atthasaṁhitāya bhikkhave “Āṭānāṭiyā” rakkhā, </span></p><p class="p3"><span class="t1">the “Āṭānāṭiya” protection, monks, is for your welfare and benefit, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhikkhūnaṁ, bhikkhuṇīnaṁ, upāsakānaṁ, upāsikānaṁ, </span></p><p class="p3"><span class="t1">for the monks’, nuns’, laymen’s, and laywomen’s </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">guttiyā, rakkhāya, avihiṁsāya, phāsuvihārāyā” ti.</span></p><p class="p3"><span class="t1">guard, protection, freedom from harm, and comfortable living.” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idam-avoca Bhagavā,</span></p><p class="p3"><span class="t1">The Fortunate One said this, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">attamanā te bhikkhū Bhagavato bhāsitaṁ abhinandun-ti.</span></p><p class="p3"><span class="t1">and those monks were uplifted and greatly rejoiced in what was said by the Fortunate One.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Verse of Blessing</span></p><p class="p3"><span class="t1">  </span></p>', '-220-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', 'Puṇṇānumodanaṁ Rejoicing in Merits ', 'title', 221);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 221, '<p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena sabbarogo vinassatu!</span></p><p class="p3"><span class="t1">By this declaration of the truth may all disease be destroyed! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Catutthabhāṇavāraṁ Niṭṭhitaṁ</span>{213}</p><p class="p3"><span class="t5">The Fourth Recital is Finished</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Catubhāṇavārapāḷi Niṭṭhitaṁ</span>{214}</p><p class="p3"><span class="t5">The Text of the Four Recitals is Finished</span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>Puṇṇānumodanaṁ Rejoicing in Merits </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ākāsaṭṭhā ca bhummaṭṭhā devā nāgā mahiddhikā,</span></p><p class="p3"><span class="t1">May those powerful gods and nāgas stationed in the sky or on the earth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puññaṁ taṁ anumoditvā ciraṁ rakkhantu sāsanaṁ!</span></p><p class="p3"><span class="t1">having shared in this merit protect the teaching for a long time! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ākāsaṭṭhā ca bhummaṭṭhā devā nāgā mahiddhikā,</span></p><p class="p3"><span class="t1">May those powerful gods and nāgas stationed in the sky or on the earth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puññaṁ taṁ anumoditvā ciraṁ rakkhantu desanaṁ!</span></p><p class="p3"><span class="t1">having shared in this merit protect the preaching for a long time! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ākāsaṭṭhā ca bhummaṭṭhā devā nāgā mahiddhikā,</span></p><p class="p3"><span class="t1">May those powerful gods and nāgas stationed in the sky or on the earth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puññaṁ taṁ anumoditvā ciraṁ rakkhantu maṁ paran!-ti</span></p><p class="p3"><span class="t1">having shared in this merit protect me and others for a long time! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p>', '-221-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', 'Upagantho Supplementary Texts ', 'title', 222);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '1. Jayamaṅgalagāthā Verses on the Blessings of Success ', 'title', 222);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 222, '<p class="p3"><span class="t5">Idaṁ me ñātīnaṁ hotu, sukhitā hontu ñātayo!</span></p><p class="p3"><span class="t1">May this (merit) go to my relatives, may my relatives be happy! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idaṁ me ñātīnaṁ hotu, sukhitā hontu ñātayo!</span></p><p class="p3"><span class="t1">May this (merit) go to my relatives, may my relatives be happy! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idaṁ me ñātīnaṁ hotu, sukhitā hontu ñātayo!</span></p><p class="p3"><span class="t1">May this (merit) go to my relatives, may my relatives be happy! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Devo vassatu kālena, sabbasampatti hetu ca,</span></p><p class="p3"><span class="t1">May the sky-god rain down in due season, and (may there be) the right conditions for all good fortune, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">phīto bhavatu loko ca, rājā bhavatu dhammiko!</span></p><p class="p3"><span class="t1">may the world be prosperous, may the king always be righteous. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h1>Upagantho Supplementary Texts </h1></p><p class="p3"><span class="t37"> </span></p><p class="p3"><h3>1. Jayamaṅgalagāthā Verses on the Blessings of Success </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bāhuṁ sahassam-abhinimmitasāyudhaṁ taṁ,</span></p><p class="p3"><span class="t1">° Māra, the terrible one, who had created a thousand arms with weapons, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Girimekhalaṁ uditaghorasasenamāraṁ,</span></p><p class="p3"><span class="t1">and had mounted on (the elephant) Girimekhala, with his army, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dānādidhammavidhinā jitavā munindo -</span></p><p class="p3"><span class="t1">did the lord of sages defeat by such things as generosity and so forth - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṁ tejasā bhavatu te jayamaṅgalāni! </span>[1]</p><p class="p3"><span class="t1">through that resplendent power may you have the blessings of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mārātirekam-abhiyujjhita sabbarattiṁ,</span></p><p class="p3"><span class="t1">° Even more terrible than Māra was the impatient and obdurate yakkha Ālavaka, </span></p><p class="p3"><span class="t1">  </span></p>', '-222-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 223, '<p class="p3"><span class="t5">ghoraṁ pan’ Ālavakam-akkhamathaddhayakkhaṁ,</span></p><p class="p3"><span class="t1">who fought (with the Buddha) throughout the whole night, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">khantīsudantavidhinā jitavā munindo -</span></p><p class="p3"><span class="t1">(him) did the lord of sages defeat by his patience and self-control </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṁ tejasā bhavatu te jayamaṅgalāni! </span>[2]</p><p class="p3"><span class="t1">through that resplendent power may you have the blessings of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nālāgiriṁ gajavaraṁ atimattabhūtaṁ,</span></p><p class="p3"><span class="t1">The great elephant Nālāgiri, who was completely intoxicated, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dāvaggicakkam-asanīva sudāruṇaṁ taṁ,</span></p><p class="p3"><span class="t1">fearful, like a jungle fire, a flaming discus, or a flash of lightning, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mettambusekavidhinā jitavā munindo -</span></p><p class="p3"><span class="t1">did the lord of sages defeat by sprinkling (him) with the waters of friendliness - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṁ tejasā bhavatu te jayamaṅgalāni! </span>[3]</p><p class="p3"><span class="t1">through that resplendent power may you have the blessings of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ukkhittakhaggam-atihatthasudāruṇaṁ taṁ</span></p><p class="p3"><span class="t1">With a sword that was lifted high in his outstretched hand, that very fearsome (bandit) </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dhāvaṁ tiyojanapathaṅgulimālavantaṁ:</span></p><p class="p3"><span class="t1">the one with a finger necklace (Aṅgulimāla) ran for (the whole of) three leagues up the path: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhībhisaṅkhatamano jitavā munindo -</span></p><p class="p3"><span class="t1">(him) did the lord of sages defeat with the psychic powers created in his mind -  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṁ tejasā bhavatu te jayamaṅgalāni! </span>[4]</p><p class="p3"><span class="t1">through that resplendent power may you have the blessings of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Katvāna kaṭṭham-udaraṁ iva gabbhinīyā,</span></p><p class="p3"><span class="t1">Having made her midriff (look large) with a piece of wood, like one who is pregnant, </span></p><p class="p3"><span class="t1">  </span></p>', '-223-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 224, '<p class="p3"><span class="t5">Ciñcāya duṭṭhavacanaṁ janakāyamajjhe:</span></p><p class="p3"><span class="t1">Ciñcā (then spoke) wicked words in the midst of a group of people: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santena somavidhinā jitavā munindo -</span></p><p class="p3"><span class="t1">(her) did the lord of sages defeat by means of his gentleness and peacefulness - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṁ tejasā bhavatu te jayamaṅgalāni! </span>[5]</p><p class="p3"><span class="t1">through that resplendent power may you have the blessings of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saccaṁ vihāya mati-Saccakavādaketuṁ,</span></p><p class="p3"><span class="t1">The arrogant Saccaka, who had given up the truth, and had challenged (the Buddha) to a debate, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vādābhiropitamanaṁ ati-andhabhūtaṁ,</span></p><p class="p3"><span class="t1">with his mind fixed on his argument, completely blind to the truth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">paññāpadīpajalito jitavā munindo -</span></p><p class="p3"><span class="t1">(him) did the lord of sages defeat with the shining lamp of true wisdom -  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṁ tejasā bhavatu te jayamaṅgalāni! </span>[6]</p><p class="p3"><span class="t1">through that resplendent power may you have the blessings of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nandopanandabhujagaṁ vibudhaṁ mahiddhiṁ,</span></p><p class="p3"><span class="t1">° Taming the wise dragon Nandopananda, who had great power, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puttena therabhujagena damāpayanto,</span></p><p class="p3"><span class="t1">by way of his son (Moggallāna), who was a dragon of an elder, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iddhūpadesavidhinā jitavā munindo -</span></p><p class="p3"><span class="t1">(thus) did the lord of sages defeat (him) by instructing (his son to use) psychic power - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṁ tejasā bhavatu te jayamaṅgalāni! </span>[7]</p><p class="p3"><span class="t1">through that resplendent power may you have the blessings of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Duggāhadiṭṭhibhujagena sudaṭṭhahatthaṁ,</span></p><p class="p3"><span class="t1">He whose hand was badly bitten by the serpent of wrongly taken up views, </span></p><p class="p3"><span class="t1">  </span></p>', '-224-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '2. Mahājayamaṅgalagāthā The Verses on the Great Blessings of Success ', 'title', 225);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 225, '<p class="p3"><span class="t5">brahmaṁ visuddhijutim-iddhi-Bakābhidhānaṁ,</span></p><p class="p3"><span class="t1">the brahma Baka, who was clear and bright, and who possessed psychic powers, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ñāṇāgadena vidhinā jitavā munindo -</span></p><p class="p3"><span class="t1">did the lord of sages defeat with the medicine of his super-knowledge - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">taṁ tejasā bhavatu te jayamaṅgalāni! </span>[8]</p><p class="p3"><span class="t1">through that resplendent power may you have the blessings of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etā pi Buddhajayamaṅgala-aṭṭhagāthā,</span></p><p class="p3"><span class="t1">° He who recites these eight verses each and every day, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yo vācako dinadine sarate-m-atandī,</span></p><p class="p3"><span class="t1">which are about the Buddha’s (many) blessed successes, and remembers them with diligence, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hitvānanekavividhāni cupaddavāni,</span></p><p class="p3"><span class="t1">after giving up (all) the many and various kinds of adversity, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mokkhaṁ sukhaṁ adhigameyya naro sapañño. </span>[9]</p><p class="p3"><span class="t1">that wise man will (soon thereafter) attain both freedom and happiness. </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>2. Mahājayamaṅgalagāthā The Verses on the Great Blessings of Success </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mahākāruṇiko nātho, hitāya sabbapāṇinaṁ,</span></p><p class="p3"><span class="t1">The lord of great compassion, for the benefit of all living creatures, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pūretvā pāramī sabbā patto Sambodhim-uttamaṁ.</span></p><p class="p3"><span class="t1">having fulfilled all the perfections has attained supreme and Complete Awakening. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ! </span>[1]</p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jayanto Bodhiyā mūle Sakyānaṁ nandivaddhano -</span></p><p class="p3"><span class="t1">Succeeding at the root of the Bodhi tree he furthered the Sakyans’ joy - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">evaṁ tuyhaṁ jayo hotu, jayassu jayamaṅgalaṁ! </span>[2]</p>', '-225-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 226, '<p class="p3"><span class="t1">so may you be successful, may you succeed with the blessing of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sakkatvā Buddharatanaṁ, osadhaṁ uttamaṁ varaṁ,</span></p><p class="p3"><span class="t1">Having honoured the Buddha treasure, the best and supreme medicine, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hitaṁ devamanussānaṁ, Buddhatejena sotthinā</span></p><p class="p3"><span class="t1">of benefit to gods and men, by the blessed power of the Buddha </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nassantupaddavā sabbe, dukkhā vūpasamentu te!</span><span class="t5"> </span>[3]</p><p class="p3"><span class="t1">may all adversities perish, and all suffering come to an end for you! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sakkatvā Dhammaratanaṁ, osadhaṁ uttamaṁ varaṁ,</span></p><p class="p3"><span class="t1">Having honoured the Dhamma treasure, the best and supreme medicine, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pariḷāhūpasamanaṁ, Dhammatejena sotthinā</span></p><p class="p3"><span class="t1">which brings fevers to an end, by the blessed power of the Dhamma </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nassantupaddavā sabbe, bhayā vūpasamentu te! </span>[4]</p><p class="p3"><span class="t1">may all adversities perish, and all fear come to an end for you! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sakkatvā Saṅgharatanaṁ, osadhaṁ uttamaṁ varaṁ,</span></p><p class="p3"><span class="t1">Having honoured the Saṅgha treasure, the best and supreme medicine, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">āhuneyyaṁ pāhuneyyaṁ, Saṅghatejena sotthinā</span></p><p class="p3"><span class="t1">worthy of gifts and hospitality, by the blessed power of the Saṅgha </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nassantupaddavā sabbe, rogā vūpasamentu te! </span>[5]</p><p class="p3"><span class="t1">may all adversities perish, and all disease come to an end for you! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ kiñci ratanaṁ loke vijjati vividhā puthū</span></p><p class="p3"><span class="t1">Whatever the many and various treasures there are in the world </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ratanaṁ Buddhasamaṁ natthi: tasmā sotthī bhavantu te! </span>[6]</p><p class="p3"><span class="t1">no treasure is equal to the Buddha: through this (truth) may you be safe! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ kiñci ratanaṁ loke vijjati vividhā puthū</span></p>', '-226-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '3. Āṇakkhettaparittaṁ Safeguard in this Order’s Domain ', 'title', 227);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 227, '<p class="p3"><span class="t1">Whatever the many and various treasures there are in the world </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ratanaṁ Dhammasamaṁ natthi: tasmā sotthī bhavantu te! </span>[7]</p><p class="p3"><span class="t1">no treasure is equal to the Dhamma: through this (truth) may you be safe! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ kiñci ratanaṁ loke vijjati vividhā puthū</span></p><p class="p3"><span class="t1">Whatever the many and various treasures there are in the world </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ratanaṁ Saṅghasamaṁ natthi: tasmā sotthī bhavantu te! </span>[8]</p><p class="p3"><span class="t1">no treasure is equal to the Saṅgha: through this (truth) may you be safe! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Natthi me saraṇaṁ aññaṁ, Buddho me saraṇaṁ varaṁ!</span></p><p class="p3"><span class="t1">For me there is no other refuge, for me the Buddha is the best refuge! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ! </span>[9]</p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Natthi me saraṇaṁ aññaṁ, Dhammo me saraṇaṁ varaṁ!</span></p><p class="p3"><span class="t1">For me there is no other refuge, for me the Dhamma is the best refuge! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ! </span>[10]</p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Natthi me saraṇaṁ aññaṁ, Saṅgho me saraṇaṁ varaṁ!</span></p><p class="p3"><span class="t1">For me there is no other refuge, for me the Saṅgha is the best refuge! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena hotu te jayamaṅgalaṁ! </span>[11]</p><p class="p3"><span class="t1">By this declaration of the truth may you have the blessing of success! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbītiyo vivajjantu sabbarogo vinassatu,</span></p><p class="p3"><span class="t1">May you avoid all calamities and may all disease be destroyed, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mā te bhavatvantarāyo, sukhī dīghāyuko bhava!</span></p><p class="p3"><span class="t1">may there be no obstacles for you, may you be happy and live long! </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>3. Āṇakkhettaparittaṁ Safeguard in this Order’s Domain </h3></p>', '-227-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 228, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ye santā santacittā, tisaraṇasaraṇā, ettha lokantare vā,</span></p><p class="p3"><span class="t1">Those (gods) who are peaceful, with peaceful minds, who have taken refuge in the triple refuge, whether here, or above the worlds, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhummā bhummā ca devā, guṇagaṇagahaṇā, byāvaṭā sabbakālaṁ,</span></p><p class="p3"><span class="t1">the various earth gods, that group who have taken up, and are engaged in, virtuous deeds all of the time, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ete āyantu devā, varakanakamaye, Merurāje vasanto,</span></p><p class="p3"><span class="t1">may these gods come, those who dwell on the majestic Mt. Meru, that excellent golden mountain, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">santo santo sahetuṁ Munivaravacanaṁ sotumaggaṁ samaggaṁ.</span></p><p class="p3"><span class="t1">peacefully, and with good reason, ( to hear) the Sage’s excellent word about entering the stream, and harmony. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbesu cakkavāḷesu yakkhā devā ca brahmuno,</span></p><p class="p3"><span class="t39">May all yakkhas, gods, and deities, from the whole universe,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">yaṁ amhehi kataṁ puññaṁ sabbasampattisādhukaṁ</span></p><p class="p3"><span class="t39">° after partaking of the merits, and of all the thoroughly good fortune</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">sabbe taṁ anumoditvā samaggā sāsane ratā,</span></p><p class="p3"><span class="t39">we have acquired, being in harmony, and delighting in the teaching,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">pamādarahitā hontu ārakkhāsu visesato.</span></p><p class="p3"><span class="t39">be not heedless and grant us complete protection.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Sāsanassa ca lokassa vuḍḍhi bhavatu sabbadā,</span></p><p class="p3"><span class="t39">May the teaching and the world be on the increase every day,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Sāsanam-pi ca lokañ-ca devā rakkhantu sabbadā.</span></p><p class="p3"><span class="t39">And may the gods every day protect the teaching and the world.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Saddhiṁ hontu sukhī sabbe parivārehi attano,</span></p><p class="p3"><span class="t39">° May you, and all those who are around you, together with</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">anīghā sumanā hontu, saha sabbehi ñātibhi.</span></p><p class="p3"><span class="t39">all your relatives, be untroubled, happy, and easy in mind.</span></p>', '-228-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 229, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">rājato vā, corato vā, manussato vā, amanussato vā,</span></p><p class="p3"><span class="t1">(may you be protected) from the king, thieves, humans, and non-humans, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">aggito vā, udakato vā, pisācato vā, khāṇukato vā, kaṇṭakato vā,</span></p><p class="p3"><span class="t1">from fire and water, demons, stumps, and thorns, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nakkhattato vā, janapadarogato vā, </span></p><p class="p3"><span class="t1">from unlucky stars, and epidemics, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asaddhammato vā, asandiṭṭhito vā, asappurisato vā,</span></p><p class="p3"><span class="t1">from what is not the true dhamma, not right view, not a good person, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">caṇḍa-hatthi-assa-miga-goṇa-kukkura-ahivicchika-maṇisappa-</span></p><p class="p3"><span class="t1">and from fierce elephants, horses, antelopes, bulls, dogs, snakes, scorpions, poisonous serpents, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dīpi-accha-taraccha-sukara-mahisa-yakkha-rakkhasādihi</span></p><p class="p3"><span class="t1">panthers, bears, hyenas, wild boars, buffaloes, yakkhas, rakkhasas, and so on, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nānā bhayato vā, nānā rogato vā,</span></p><p class="p3"><span class="t1">from the manifold fears, the manifold diseases,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nānā upaddavato vā, ārakkhaṁ gaṇhantu!</span></p><p class="p3"><span class="t1">the manifold calamities, (from all of these troubles) may you receive protection! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Paṇidhānato paṭṭhāya, Tathāgatassa dasapāramiyo, </span></p><p class="p3"><span class="t1">Beginning with the aspiration (for Buddhahood, and thinking of) the Realised One’s ten perfections, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">dasa-upapāramiyo, dasaparamatthapāramiyo - samatiṁsapāramiyo </span></p><p class="p3"><span class="t1">ten minor perfections, ten great perfections - altogether thirty perfections, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pañcamahāpariccāge, tisso cariyā, </span></p><p class="p3"><span class="t1">his five great sacrifices, three ways of conduct,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pacchimabhave gabbhāvakkantiṁ, jātiṁ,</span></p><p class="p3"><span class="t1">descent into the womb for his final existence, birth, </span></p>', '-229-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '4. Jinapañjaraṁ The Victors’ Armour ', 'title', 230);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 230, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Abhinikkhamanaṁ, padhānacariyaṁ, Bodhipallaṅkaṁ, Māravijayaṁ,</span></p><p class="p3"><span class="t1">Great Renunciation, life of effort, sitting cross legged at the Bodhi tree, victory over Māra, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbaññutañāṇapaṭivedhaṁ, navalokuttaradhamme ti,</span></p><p class="p3"><span class="t1">his penetration and knowledge of omniscience, and his nine supermundane attainments, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe pime Buddhaguṇe āvajjitvā, Vesāliyaṁ tīsu pākārantaresu,</span></p><p class="p3"><span class="t1">and after considering all these Buddha qualities, (going around) the three walls of Vesāli, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tiyāmarattiṁ, parittaṁ karonto āyasmā Ānandatthero,</span></p><p class="p3"><span class="t1">during the three watches of the night, the venerable elder Ānanda, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">viya kāruññacittaṁ upaṭṭhapetvā.</span></p><p class="p3"><span class="t1">after establishing great compassion in his heart, made this safeguard. </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>4. Jinapañjaraṁ The Victors’ Armour </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jayāsanagatā vīrā, jetvā Māraṁ savāhiniṁ,</span></p><p class="p3"><span class="t1">Those heroes, sat on their thrones, after defeating Māra and his host, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">catusaccāmatarasaṁ ye piviṁsu narāsabhā. </span>[1]</p><p class="p3"><span class="t1">those who are bulls of men, drank of the ambrosia of the four truths. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Taṇhaṅkarādayo Buddhā aṭṭhavīsatināyakā,</span></p><p class="p3"><span class="t1">Those twenty eight leaders, Buddhas, beginning with Taṇhaṅkara, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe patiṭṭhitā tuyhaṁ matthake te munissarā. </span>[2]</p><p class="p3"><span class="t1">are all established on the crown of your head, they are lords of sages. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sire patiṭṭhitā Buddhā, Dhammo ca tava locane,</span></p><p class="p3"><span class="t1">The Buddhas are established on your head, and the Dhamma on your eyes, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saṅgho patiṭṭhito tuyhaṁ ure sabbaguṇākaro, </span>[3]</p><p class="p3"><span class="t1">the Saṅgha, the fount of all good qualities is established on your chest, </span></p><p class="p3"><span class="t1">  </span></p>', '-230-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 231, '<p class="p3"><span class="t5">Hadaye Anuruddho ca, Sāriputto ca dakkhiṇe,</span></p><p class="p3"><span class="t1">On your heart is Anuruddha, and Sāriputta on your right, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Koṇḍañño piṭṭhibhāgasmiṁ, Moggallānosi vāmake. </span>[4]</p><p class="p3"><span class="t1">Koṇḍañña is on your back, and Moggallāna on your left. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dakkhiṇe savaṇe tuyhaṁ, āhuṁ Ānandarāhulā,</span></p><p class="p3"><span class="t1">On your right ear (are established these two) Ānanda and Rāhula, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kassapo ca Mahānāmo, ubhosuṁ vāmasotake. </span>[5]</p><p class="p3"><span class="t1">Kassapa and Mahānāma, these two (are established) on your left ear. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kesante piṭṭhibhāgasmiṁ suriyo viya pabhaṅkaro -</span></p><p class="p3"><span class="t1">At the end of your hair at the back like the sun, the light maker - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nisinno sirisampanno Sobhito munipuṅgavo. </span>[6]</p><p class="p3"><span class="t1">is seated the glorious Sobhita, who is a bull of a sage. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kumārakassapo nāma, mahesī citravādako,</span></p><p class="p3"><span class="t1">The one named Kumārakassapa, great sage, and beautiful speaker, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">so tuyhaṁ vadane niccaṁ, patiṭṭhāsi guṇākaro. </span>[7]</p><p class="p3"><span class="t1">that fount of good qualities is permanently established on your mouth. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Puṇṇo Aṅgulimālo ca, Upāli Nandasīvalī -</span></p><p class="p3"><span class="t1">Puṇṇa, Aṅgulimāla, Upāli, Nanda, and Sivalī - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">therā pañca ime jātā lalāṭe tilakā tava. </span>[8]</p><p class="p3"><span class="t1">these five true elders (are established like) auspicious signs on your forehead. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sesāsīti mahātherā, vijitā jinasāvakā,</span></p><p class="p3"><span class="t1">The remaining eighty great elders, victors, disciples of the victor, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">jalantā sīlatejena, aṅga-m-aṅgesu saṇṭhitā. </span>[9]</p><p class="p3"><span class="t1">shining with the power of virtue, are established on your limbs. </span></p><p class="p3"><span class="t1">  </span></p>', '-231-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 232, '<p class="p3"><span class="t5">“Ratanaṁ” purato āsi, dakkhiṇe “Mettasuttakaṁ”,</span></p><p class="p3"><span class="t1">To the front is “The Treasures” on the right “The Friendliness Discourse”, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Dhajaggaṁ” pacchato āsi, vāme “Aṅgulimālakaṁ”, </span>[10]</p><p class="p3"><span class="t1">“The Banner” is at the back, and “Aṅgulimāla” is on the left, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Khandhamoraparittañ”-ca, “Āṭānāṭiyasuttakaṁ”,</span></p><p class="p3"><span class="t1">The “Constituent Groups” and “Peacock” safeguards, and the “Āṭānāṭiya Discourse”, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ākāsacchadanaṁ āsi, sesā pākārasaññitā. </span>[11]</p><p class="p3"><span class="t1">cover the space (around you), the remainder are a fence, so to speak. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jināṇābalasaṁyutte, Dhammapākāralaṅkate,</span></p><p class="p3"><span class="t1">With the strength of the victor’s order, equipped with the fence of the Dhamma, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vasato te catukiccena, sadā Sambuddhapañjare, </span>[12]</p><p class="p3"><span class="t1">engaged in the four duties, in the Sambuddhas’ armour forever, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vātapittādisañjātā bāhirajjhattupaddavā, </span></p><p class="p3"><span class="t1">may all internal and external adversities that arise, beginning with wind and bile, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asesā vilayaṁ yantu anantaguṇatejasā. </span>[13]</p><p class="p3"><span class="t1">through the power of (the Buddhas’) endless virtues, come to a complete end. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jinapañjaramajjhaṭṭhaṁ viharantaṁ mahītale,</span></p><p class="p3"><span class="t1">While living on this great earth, standing in the midst of the victors’ armour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sadā pālentu tvaṁ sabbe te mahāpurisāsabhā. </span>[14]</p><p class="p3"><span class="t1">may all of those great bulls of men watch over you forever. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Iccevam-accantakato surakkho, jinānubhāvena jitūpapaddavo,</span></p><p class="p3"><span class="t1">Thus being continuously well protected, with adversity defeated through the victors’ power, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Buddhānubhāvena hatārisaṅgho, carāhi saddhammanubhāvapālito! </span>[15]</p><p class="p3"><span class="t1">with the crowd of foes destroyed through the Buddhas’ power, live on, guarded by the power of the true Dhamma! </span></p><p class="p3"><span class="t1">  </span></p>', '-232-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 233, '<p class="p3"><span class="t5">Iccevam-accantakato surakkho, jinānubhāvena jitūpapaddavo,</span></p><p class="p3"><span class="t1">Thus being continuously well protected, with adversity defeated through the victors’ power, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dhammānubhāvena hatārisaṅgho, carāhi saddhammanubhāvapālito! </span>[16]</p><p class="p3"><span class="t1">with the crowd of foes destroyed through the Dhamma’s power, live on, guarded by the power of the true Dhamma! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Iccevam-accantakato surakkho, jinānubhāvena jitūpapaddavo,</span></p><p class="p3"><span class="t1">Thus being continuously well protected, with adversity defeated through the victors’ power, </span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Saṅghānubhāvena hatārisaṅgho, carāhi saddhammanubhāvapālito! </span>[17]</p><p class="p3"><span class="t1">with the crowd of foes destroyed through the Saṅgha’s power, live on, guarded by the power of the true Dhamma! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saddhammapākāraparikkhitosi, aṭṭhāriyā aṭṭhadisāsu honti,</span></p><p class="p3"><span class="t1">You are defended by the fence of the true Dhamma, with the eight noble ones in the eight directions, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">etthantare aṭṭhanāthā bhavanti, uddhaṁ vitānaṁ va jinā ṭhitā te. </span>[18]</p><p class="p3"><span class="t1">and in between these (directions) are the eight lords, and above, like a canopy, stand the victors. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">Bhindanto Mārasenaṁ, tava sirasi ṭhito, Bodhim-āruyha Satthā.</span></p><p class="p3"><span class="t1">Breaking Māra’s army, the Teacher who sat under the Bodhi tree now rests on your head. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Moggallānosi vāme vasati bhujataṭe, dakkhiṇe Sāriputto.</span></p><p class="p3"><span class="t1">You have Moggallāna dwelling on your left hand side, and Sāriputta on your right. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t14">Dhammo majjhe urasmiṁ viharati bhavato mokkhato morayoniṁ,</span></p><p class="p3"><span class="t1">° The Dhamma dwells in the middle of your chest, the Awakening One who took birth from </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sampatto Bodhisatto caraṇayugagato, bhānu lokekanātho. </span>[19]</p><p class="p3"><span class="t1">out of a peahen’s womb has come to your feet, he is brilliant, the world’s sole protector. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbāvamaṅgalam-upaddavadunnimittaṁ,</span></p><p class="p3"><span class="t1">All inauspicious events, adversities, and bad signs, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbītirogagahadosa-m-asesanindā,</span></p><p class="p3"><span class="t1">all calamities, diseases, blame, bad planets, bar none, </span></p><p class="p3"><span class="t1">  </span></p>', '-233-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '5. Aṭṭhavīsatiparittaṁ Safeguard through the Twenty-Eight Buddhas ', 'title', 234);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 234, '<p class="p3"><span class="t5">sabbantarāyabhayadussupinaṁ akantaṁ -</span></p><p class="p3"><span class="t1">all obstacles, fears, and unpleasant dreams - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Buddhānubhāvapavarena payātu nāsaṁ! </span>[20]</p><p class="p3"><span class="t1">may (all of them), through the excellent power of the Buddha, go to destruction! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbāvamaṅgalam-upaddavadunnimittaṁ,</span></p><p class="p3"><span class="t1">All inauspicious events, adversities, and bad signs, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbītirogagahadosa-m-asesanindā,</span></p><p class="p3"><span class="t1">all calamities, diseases, blame, bad planets, bar none, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbantarāyabhayadussupinaṁ akantaṁ -</span></p><p class="p3"><span class="t1">all obstacles, fears, and unpleasant dreams - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dhammānubhāvapavarena payātu nāsaṁ! </span>[21]</p><p class="p3"><span class="t1">may (all of them), through the excellent power of the Dhamma, go to destruction! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbāvamaṅgalam-upaddavadunnimittaṁ,</span></p><p class="p3"><span class="t1">All inauspicious events, adversities, and bad signs, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbītirogagahadosa-m-asesanindā,</span></p><p class="p3"><span class="t1">all calamities, diseases, blame, bad planets, bar none, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbantarāyabhayadussupinaṁ akantaṁ -</span></p><p class="p3"><span class="t1">all obstacles, fears, and unpleasant dreams - </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saṅghānubhāvapavarena payātu nāsaṁ! </span>[22]</p><p class="p3"><span class="t1">may (all of them), through the excellent power of the Saṅgha, go to destruction! </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>5. Aṭṭhavīsatiparittaṁ Safeguard through the Twenty-Eight Buddhas </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Taṇhaṅkaro mahāvīro, Medhaṅkaro mahāyaso,</span></p><p class="p3"><span class="t1">Taṇhaṅkara, the great hero, Medhaṅkara, of great repute, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saraṇaṅkaro lokahito, Dīpaṅkaro jutindharo, </span>[1]</p>', '-234-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 235, '<p class="p3"><span class="t1">Saraṇaṅkara, who benefits the world, Dīpaṅkara, the bright one, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Koṇḍañño janapāmokkho, Maṅgalo purisāsabho,</span></p><p class="p3"><span class="t1">Koṇḍañña, head of the people, Maṅgala, a bull of a man, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sumano sumano dhīro, Revato rativaddhano, </span>[2]</p><p class="p3"><span class="t1">The wise one Sumana, whose mind is well, Revata, who increases delight, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sobhito guṇasampanno, Anomadassī januttamo,</span></p><p class="p3"><span class="t1">Sobhita, endowed with good qualities, Anomadassī, supreme man, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Padumo lokapajjoto, Nārado varasārathī, </span>[3]</p><p class="p3"><span class="t1">Paduma, the lamp of the world, Nārada, the best of guides, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Padumuttaro sattasāro, Sumedho aggapuggalo,</span></p><p class="p3"><span class="t1">Padumuttara, an excellent being, Sumedha, the greatest person, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sujāto sabbalokaggo, Piyadassī narāsabho, </span>[4]</p><p class="p3"><span class="t1">Sujāta, greatest in all the world, Piyadassī, a bull of a man, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Atthadassī kāruṇiko, Dhammadassī tamonudo,</span></p><p class="p3"><span class="t1">Atthadassī, the compassionate one, Dhammadassī, darkness-dispeller, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Siddhattho asamo loke, Tisso varadasaṁvaro, </span>[5]</p><p class="p3"><span class="t1">Siddhattha, unequalled in the world, Tissa, who was most restrained, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Phusso varadasambuddho, Vipassī ca anūpamo,</span></p><p class="p3"><span class="t1">Phussa, excellent Sambuddha, Vipassī, who has no likeness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sikhī sabbahito Satthā, Vessabhū sukhadāyako, </span>[6]</p><p class="p3"><span class="t1">Sikhī, the Teacher who is a benefit to all, Vessabhū, who gives happiness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kakusandho satthavāho, Koṇāgamano raṇañjaho,</span></p><p class="p3"><span class="t1">Kakusandha, the leader of the pack, Koṇāgamana, who gave up what is harmful, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kassapo sirisampanno, Gotamo Sakyapuṅgavo. </span>[7]</p>', '-235-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '6. Caturārakkhā Bhāvanā Four Protective Meditations ', 'title', 236);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', 'Recollection of the Buddha ', 'title', 236);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 236, '<p class="p3"><span class="t1">Kassapa, the glorious one, and Gotama, the Sakyan bull. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tesaṁ saccena sīlena, khantimettabalena ca,</span></p><p class="p3"><span class="t1">Through their truth and virtue, and the strength of their patience and friendliness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te pi tvaṁ anurakkhantu ārogyena sukhena cā! ti </span>[8]</p><p class="p3"><span class="t1">may these (Buddhas) always protect you with good health and happiness! </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>6. Caturārakkhā Bhāvanā Four Protective Meditations </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Buddhānussati, mettā ca, asubhaṁ, maraṇassati -</span></p><p class="p3"><span class="t39">Recollection of the Buddha, friendliness, unattractiveness, and mindfulness of death -</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">iti imā caturārakkhā bhikkhu bhāveyya sīlavā.</span></p><p class="p3"><span class="t39">these are the four protective meditations that a virtuous monk should develop.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Anantavitthāraguṇaṁ guṇatonussaraṁ Muniṁ,</span></p><p class="p3"><span class="t39">Recollecting the Sage’s virtue, and his endless, extensive, good qualities,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāveyya buddhimā bhikkhu Buddhānussati-m-ādito.</span></p><p class="p3"><span class="t39">the wise monk should develop the recollection of the Buddha first.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"><span class="t12">Buddhānussati</span> </span></p><p class="p3"><h4>Recollection of the Buddha </h4></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Savāsane kilese so eko sabbe nighātiya,</span></p><p class="p3"><span class="t39">Alone he destroyed all the corruptions, and (bad) predispositions,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ahū susuddhasantāno pūjānaṁ ca sadāraho. </span><span class="t40">[1]</span></p><p class="p3"><span class="t39">and being continually and fully pure he is worthy of worship at all times.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbakālagate dhamme sabbe sammā sayaṁ Muni</span></p><p class="p3"><span class="t39">° The Sage by himself has, in every way, completely awakened to all things</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbākārena bujjhitvā, eko sabbaññutaṁ gato. </span><span class="t40">[2]</span></p><p class="p3"><span class="t39">throughout the whole of time, and alone he has arrived at omniscience.</span></p>', '-236-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 237, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vipassanādi vijjāhi sīlādi caraṇehi ca,</span></p><p class="p3"><span class="t39">° Being endowed with great psychic power, good conduct, virtue, and so on,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">susamiddhehi sampanno, gaganābhehi nāyako. </span><span class="t40">[3]</span></p><p class="p3"><span class="t39">true understanding, insight, and so on, the leader was like the shining sky.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sammāgato subhaṁ ṭhānaṁ amoghavacano ca so,</span></p><p class="p3"><span class="t39">He who never spoke foolish words, has arrived at that glorious state (Nibbāna),</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tividhassāpi lokassa ñātā niravasesato. </span><span class="t40">[4]</span></p><p class="p3"><span class="t39">he knew the threefold world system (completely) without leaving anything out.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Anekehi guṇoghehi sabbasattuttamo ahū,</span></p><p class="p3"><span class="t39">Overflowing with countless good qualities he is supreme among all beings,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anekehi upāyehi naradamme damesi ca. </span><span class="t40">[5]</span></p><p class="p3"><span class="t39">with countless skilful means he tamed those men who could be tamed.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Eko sabbassa lokassa sabbamatthānusāsako,</span></p><p class="p3"><span class="t39">He alone, to the whole world was the teacher of everything good, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">bhāgya-issariyādīnaṁ guṇānaṁ paramo nidhī. </span><span class="t40">[6]</span></p><p class="p3"><span class="t39">he is the highest treasure, having qualities such as good fortune, mastery, and so on.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Paññāssa sabbadhammesu karuṇā sabbajantusu,</span></p><p class="p3"><span class="t39">Being wise in regard to all things compassionate to everybody,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">attatthānaṁ paratthānaṁ sādhikā guṇajeṭṭhikā. </span><span class="t40">[7]</span></p><p class="p3"><span class="t39">he exceeded (others) in the best qualities, (knowing) what was for his own and others’ good.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dayāya pāramī citvā paññāyattānam-uddharī,</span></p><p class="p3"><span class="t39">Through sympathy he set his mind on the perfections through wisdom he raised himself up,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">uddharī sabbadhamme ca, dayāyaññe ca uddharī. </span><span class="t40">[8]</span></p><p class="p3"><span class="t39">he raised himself above all things, through sympathy he raised others too.</span></p>', '-237-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', 'The Development of Friendliness Meditation ', 'title', 238);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 238, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Dissamāno pi tāvassa rūpakāyo acintayo,</span></p><p class="p3"><span class="t39">Even his visible form-body was beyond thought,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asādhāraṇañāṇaḍḍhe dhammakāye kathā va kā ti? </span><span class="t40">[9]</span></p><p class="p3"><span class="t39">what can be said of his spiritual body, which was unique, having powerful knowledge?</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t1"><span class="t12">Mettabhāvanā</span> </span></p><p class="p3"><h4>The Development of Friendliness Meditation </h4></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Attūpamāya sabbesaṁ sattānaṁ sukhakāmataṁ,</span></p><p class="p3"><span class="t39">In a similar way to oneself, all beings desire happiness,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">passitvā kamato mettaṁ sabbasattesu bhāvaye. </span><span class="t40">[1]</span></p><p class="p3"><span class="t39">having seen that one should gradually develop friendliness towards all beings.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sukhī bhaveyyaṁ niddukkho, ahaṁ niccaṁ, aham viya</span></p><p class="p3"><span class="t39">May I constantly be happy, free from suffering, and like myself</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hitā ca me sukhī hontu, majjhattā cātha verino. </span><span class="t40">[2]</span></p><p class="p3"><span class="t39">may my benefactors be happy, neutral persons, and foes also.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Imamhi gāmakkhettamhi, sattā hontu sukhī sadā,</span></p><p class="p3"><span class="t39">In this village, and its surrounding fields, may beings always be happy,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tato parañ-ca rajjesu, cakkavālesu jantuno. </span><span class="t40">[3]</span></p><p class="p3"><span class="t39">and those in other countries, and people throughout the universe.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Samantā cakkavālesu sattānantesu pāṇino,</span></p><p class="p3"><span class="t39">° Throughout the entire universe may the countless beings, creatures,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sukhino puggalā bhūtā attabhāvagatā siyuṁ, </span><span class="t40">[4]</span></p><p class="p3"><span class="t39">persons, and bhūtas, who have attained individuality, be happy,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tathā itthī pumā ceva ariyā anariyā pi ca,</span></p><p class="p3"><span class="t39">and so too (all) women, men, noble ones, and also ignoble ones,</span></p>', '-238-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', 'Perception of the Unattractive ', 'title', 239);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 239, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devā narā apāyaṭṭhā, tathā dasadisāsu cā ti. </span><span class="t40">[5]</span></p><p class="p3"><span class="t39">gods, humans, and fallen creatures, and likewise (all beings) in the ten directions.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t1"><span class="t12">Asubhasaññā</span> </span></p><p class="p3"><h4>Perception of the Unattractive </h4></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Aviññāṇasubhanibhaṁ saviññāṇasubhaṁ imaṁ,</span></p><p class="p3"><span class="t39">With consciousness it is unattractive, just as it is without (i.e. dead),</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kāyaṁ asubhato passaṁ, asubhaṁ bhāvaye yati. </span><span class="t40">[1]</span></p><p class="p3"><span class="t39">seeing this body as unattractive, a monk should develop (the recollection) of the unattractive.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vaṇṇasaṇṭhānagandhehi āsayokāsato tathā,</span></p><p class="p3"><span class="t39">Thus by way of colour, shape, smell, location, and appearance,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">paṭikkūlāni kāye me kuṇapāni dvisoḷasa. </span><span class="t40">[2]</span></p><p class="p3"><span class="t39">the thirty-two (parts) in my body (are like) repulsive corpses.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Patitamhā pi kuṇapā, jegucchaṁ kāyanissitaṁ,</span></p><p class="p3"><span class="t39">The (parts) that depend on this body are contemptible, as is what falls from a corpse,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ādhāro hi ’suci tassa, kāye tu kuṇape ṭhitaṁ. </span><span class="t40">[3]</span></p><p class="p3"><span class="t39">for their receptacle is impure, they are situated in this corpse of a body.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mīlhe kimi va kāyoyaṁ asucimhi samuṭṭhito,</span></p><p class="p3"><span class="t39">Like a worm in excrement this body arose in what is impure (i.e. the womb),</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">anto asucisampuṇṇo puṇṇavaccakuṭī viya. </span><span class="t40">[4]</span></p><p class="p3"><span class="t39">inside it is full of impurities just like a full lavatory.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Asucisandate niccaṁ yathā medakathālikā,</span></p><p class="p3"><span class="t39">Always the impurities overflow like fat (overflows) from a frying pan,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">nānākimikulāvāso, pakkhacandanikā viya. </span><span class="t40">[5]</span></p><p class="p3"><span class="t39">various kinds of worms dwell in it, the same as in a cesspool.</span></p>', '-239-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', 'Recollection of Death ', 'title', 240);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 240, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gaṇḍabhūto, rogabhūto, vaṇabhūto, samussayo,</span></p><p class="p3"><span class="t39">This bodily heap is like a boil, like a disease, or like a sore,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">atekiccho ti jeguccho pabhinnakuṇapūpamo ti. </span><span class="t40">[6]</span></p><p class="p3"><span class="t39">it is incurable, contemptible, just like a rotting corpse.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t1"><span class="t12">Maraṇānussati</span> </span></p><p class="p3"><h4>Recollection of Death </h4></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pavātadīpatulyāya, sāyusantatiyā khayaṁ,</span></p><p class="p3"><span class="t39">By comparing a lamp in a breeze, with the destruction of the life continuum,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">parūpamāya sampassaṁ, bhāvaye maraṇassatiṁ. </span><span class="t40">[1]</span></p><p class="p3"><span class="t39">through seeing oneself as similar to others, one should develop mindfulness of death.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mahāsampattisampattā yathā sattā matā idha,</span></p><p class="p3"><span class="t39">Just as people who have attained great good fortune here have died,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tathā ahaṁ marissāmi maranaṁ mama hessati. </span><span class="t40">[2]</span></p><p class="p3"><span class="t39">in the same way will I die death is (surely) coming to me.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Uppattiyā sahevedaṁ maraṇaṁ āgataṁ sadā,</span></p><p class="p3"><span class="t39">Indeed death always comes along with that which has arisen,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">maraṇatthāya okāsaṁ vadhako viya esati. </span><span class="t40">[3]</span></p><p class="p3"><span class="t39">it is like a murderer who is seeking an opportunity to kill.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Īsakaṁ anivattaṁ taṁ satataṁ gamanussukaṁ,</span></p><p class="p3"><span class="t39">° This life is slowly, without turning back, continually, eagerly,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">jīvitaṁ udayā atthaṁ suriyo viya dhāvati. </span><span class="t40">[4]</span></p><p class="p3"><span class="t39">going its way, it rises and falls just as the sun runs its course (and sets).</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vijjububbula-ussāva, jalarājī parikkhayaṁ,</span></p><p class="p3"><span class="t39">(Like) lightning, a bubble, dew, a line drawn on water, life (quickly goes to) destruction,</span></p>', '-240-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', 'The Eight Bases for Urgency ', 'title', 241);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 241, '<p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">ghātako varipūtassa sabbatthā pi avāriyo. </span><span class="t40">[5]</span></p><p class="p3"><span class="t39">like an executioner in regard to his rival (death) can never be constrained.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Suyasatthāmapuññiddhī buddhivuddhī Jinadvayaṁ,</span></p><p class="p3"><span class="t39">Even the two kinds of Victors, who are famous, strong, meritorious, powerful, and of great intelligence,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">ghātesi maraṇaṁ khippaṁ, kā tu mādisake kathā? </span><span class="t40">[6]</span></p><p class="p3"><span class="t39">were quickly slaughtered by death, so what to say about one like me?</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Paccayānañ-ca vekalyā bāhirajjhattupaddavā,</span></p><p class="p3"><span class="t39">When conditions fail there are internal and external adversities,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">marāmoraṁ nimesā pi maramāno anukkhaṇan-ti. </span><span class="t40">[7]</span></p><p class="p3"><span class="t39">dying at each and every moment I will die in less (time) than a blink of an eye.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t1"><span class="t12">Aṭṭhasaṁvegavatthūni</span> </span></p><p class="p3"><h4>The Eight Bases for Urgency </h4></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bhāvetvā caturārakkhā āvajjeyya anantaraṁ</span></p><p class="p3"><span class="t39">Having developed these four protections one should consider next</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mahāsaṁvegavatthūni, aṭṭha aṭṭhitavīriyo. </span><span class="t40">[1]</span></p><p class="p3"><span class="t39">the eight great bases for urgency, having non-stop energy.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Jātijarāvyādhicutī apāyā,</span></p><p class="p3"><span class="t39">Birth, old age, sickness, death, the lower realms,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">atīta-appattakavaṭṭadukkhaṁ,</span></p><p class="p3"><span class="t39">the past and future suffering in the round,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">idāni āhāragaveṭṭhidukkhaṁ - </span></p><p class="p3"><span class="t39">the suffering in having to seek food in the present -</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">saṁvegavatthūni imāni aṭṭha. </span><span class="t40">[2]</span></p><p class="p3"><span class="t39">these are the eight bases for urgency.</span></p>', '-241-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '7. Cakkavāḷamettabhāvanā Universal Friendliness Meditation ', 'title', 242);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 242, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pāto ca sāyam-api ceva imaṁ vidhiñño,</span></p><p class="p3"><span class="t39">If, in the morning and in the evening, one who knows the way,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">āsevate satatamatta hitābhilāsi,</span></p><p class="p3"><span class="t39">who desires his own benefit, practises (these meditations) continually,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pappoti so ti vipulaṁ hata pāripantho,</span></p><p class="p3"><span class="t39">° then after destroying (even) extensive obstacles, that sage</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">seṭṭhaṁ sukhaṁ muni visiṭṭhamataṁ sukhena cā ti. </span><span class="t40">[3]</span></p><p class="p3"><span class="t39">easily attains great happiness, and the distinction of the deathless.</span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>7. Cakkavāḷamettabhāvanā Universal Friendliness Meditation </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbe sattā, sabbe pāṇā, sabbe bhūtā,</span></p><p class="p3"><span class="t1">May all creatures, all breathing creatures, all beings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe puggalā, sabbe attabhāvapariyāpannā,</span></p><p class="p3"><span class="t1">all persons, all individuals, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbā itthiyo, sabbe purisā,</span></p><p class="p3"><span class="t1">may all women, all men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe ariyā, sabbe anariyā,</span></p><p class="p3"><span class="t1">all those who are Noble, all those who are not (yet) Noble, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe devā, sabbe manussā, sabbe vinipātikā,</span></p><p class="p3"><span class="t1">all gods, all human beings, all who have fallen (into the lower worlds), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">averā hontu, abyāpajjhā hontu, anīghā hontu,</span></p><p class="p3"><span class="t1">be free from hatred, free from oppression, free from trouble, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sukhī attānaṁ pariharantu, dukkhā muccantu,</span></p><p class="p3"><span class="t1">may they take care of themselves and be happy, may they be free from suffering, </span></p><p class="p3"><span class="t1">  </span></p>', '-242-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 243, '<p class="p3"><span class="t5">yathāladdhasampattito mā vigacchantu kammassakā.</span></p><p class="p3"><span class="t1">being ones who own their actions, may they not lose whatever prosperity they have gained. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t15">Puratthimāya disāya, pacchimāya disāya, </span></p><p class="p3"><span class="t1">In in the Eastern direction, in the Western direction,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">uttarāya disāya, dakkhiṇāya disāya,</span></p><p class="p3"><span class="t1">in the Northern direction, in the Southern direction, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">puratthimāya anudisāya, pacchimāya anudisāya,</span></p><p class="p3"><span class="t1">in the South-Eastern direction, in the North-West direction, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">uttarāya anudisāya, dakkhiṇāya anudisāya,</span></p><p class="p3"><span class="t1">in the North-East direction, in the South-West direction, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">heṭṭhimāya disāya, uparimāya disāya.</span></p><p class="p3"><span class="t1">in the lower direction, in the upper direction. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sabbe sattā, sabbe pāṇā, sabbe bhūtā,</span></p><p class="p3"><span class="t1">May all creatures, all breathing creatures, all beings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe puggalā, sabbe attabhāvapariyāpannā,</span></p><p class="p3"><span class="t1">all persons, all individuals, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbā itthiyo, sabbe purisā,</span></p><p class="p3"><span class="t1">may all women, all men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe ariyā, sabbe anariyā,</span></p><p class="p3"><span class="t1">all those who are Noble, all those who are not (yet) Noble, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe devā, sabbe manussā, sabbe vinipātikā,</span></p><p class="p3"><span class="t1">all gods, all human beings, all who have fallen (into the lower worlds), </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">averā hontu, abyāpajjhā hontu, anīghā hontu,</span></p><p class="p3"><span class="t1">be free from hatred, free from oppression, free from trouble, </span></p><p class="p3"><span class="t1">  </span></p>', '-243-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 244, '<p class="p3"><span class="t5">sukhī attānaṁ pariharantu, dukkhā muccantu,</span></p><p class="p3"><span class="t1">may they take care of themselves and be happy, may they be free from suffering, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yathāladdhasampattito mā vigacchantu kammassakā.</span></p><p class="p3"><span class="t1">being ones who own their actions, may they not lose whatever prosperity they have gained. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"><span class="t5">Uddhaṁ yāva bhavaggā ca adho yāva Avīcito</span> </span></p><p class="p3"><span class="t1">From the top of existence down, and from the Avīci hell up, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">samantā cakkavāḷesu, ye sattā pathavī carā,</span></p><p class="p3"><span class="t1">everywhere in the universe, whatever beings live on the earth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abyāpajjhā niverā ca niddukkhā cānupaddavā.</span></p><p class="p3"><span class="t1">(may they be) free from oppression, free from hatred, free from suffering, and free from danger. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Uddhaṁ yāva bhavaggā ca adho yāva Avīcito</span></p><p class="p3"><span class="t1">From the top of existence down, and from the Avīci hell up, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">samantā cakkavāḷesu, ye sattā udake carā,</span></p><p class="p3"><span class="t1">everywhere in the universe, whatever beings live in the water, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abyāpajjhā niverā ca niddukkhā cānupaddavā.</span></p><p class="p3"><span class="t1">(may they be) free from oppression, free from hatred, free from suffering, and free from danger. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Uddhaṁ yāva bhavaggā ca adho yāva Avīcito</span></p><p class="p3"><span class="t1">From the top of existence down, and from the Avīci hell up, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">samantā cakkavāḷesu, ye sattā ākāse carā,</span></p><p class="p3"><span class="t1">everywhere in the universe, whatever beings live in the sky, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">abyāpajjhā niverā ca niddukkhā cānupaddavā.</span></p><p class="p3"><span class="t1">(may they be) free from oppression, free from hatred, free from suffering, and free from danger. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yaṁ pattaṁ kusalaṁ, tassa ānubhāvena pāṇino,</span></p><p class="p3"><span class="t1">I have attained what is wholesome, by the power of this, may all beings, </span></p>', '-244-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 245, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe Saddhammarājassa ñatvā Dhammaṁ, sukhāvahaṁ,</span></p><p class="p3"><span class="t1">having understood the King of the True Dhamma’s Doctrine, be led to happiness, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">pāpuṇantu visuddhāya, sukhāya paṭipattiyā,</span></p><p class="p3"><span class="t1">attain to purity, practise comfortably, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">asokam-anupāyāsaṁ, Nibbānasukham-uttamaṁ.</span></p><p class="p3"><span class="t1">be without grief, without continuing despair, (and attain) the supreme happiness of Emancipation. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ciraṁ tiṭṭhatu Saddhammo, Dhamme hontu sagāravā,</span></p><p class="p3"><span class="t1">May the True Dhamma last long, may all beings have respect for the Dhamma, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sabbe pi sattā, kālena sammā devo pavassatu.</span></p><p class="p3"><span class="t1">may the (rain)-gods rain down at the right time. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yathā rakkhiṁsu porāṇā Surājāno, tathevimaṁ</span></p><p class="p3"><span class="t1">Just like the Good Kings of old gave protection, likewise </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Rājā rakkhatu dhammena attano va pajaṁ pajaṁ.</span></p><p class="p3"><span class="t1">may the (present) King protect the people righteously like (he protects) himself. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Imāya Dhammānudhammapaṭipattiyā Buddhaṁ pūjemi,</span></p><p class="p3"><span class="t1">By this practice in conformity with the Dhamma I worship the Buddha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">imāya Dhammānudhammapaṭipattiyā Dhammaṁ pūjemi,</span></p><p class="p3"><span class="t1">by this practice in conformity with the Dhamma I worship the Dhamma, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">imāya Dhammānudhammapaṭipattiyā Saṅghaṁ pūjemi,</span></p><p class="p3"><span class="t1">by this practice in conformity with the Dhamma I worship the Saṅgha, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">addhā imāya paṭipadāya jātijarābyādhimaraṇamhā parimuccisāmi.</span></p><p class="p3"><span class="t1">by this practice may I be freed from birth, old age, sickness and death. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idaṁ me puññaṁ āsavakkhayāvahaṁ hotu.</span></p><p class="p3"><span class="t1">May my merit bring about the destruction of the pollutants. </span></p>', '-245-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '8. Aṅgulimālaparittaṁ The Aṅgulimāla Safeguard ', 'title', 246);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 246, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Idaṁ me puññaṁ Nibbānassa paccayo hotu.</span></p><p class="p3"><span class="t1">May my merit be a condition for (gaining) Emacipation. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Mama puññabhāgaṁ sabbasattānaṁ bhājemi,</span></p><p class="p3"><span class="t1">I share my portion of merits with all beings, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">te sabbe me samaṁ puññabhāgaṁ labhantu.</span></p><p class="p3"><span class="t1">may all of them share my portion of merits evenly. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>8. Aṅgulimālaparittaṁ The Aṅgulimāla Safeguard </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Parittaṁ yaṁ bhaṇantassa, nisinnaṭṭhānadhovanaṁ </span></p><p class="p3"><span class="t1">° The water that washed the seat of the one reciting </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">udakam-pi vināseti, sabbam-eva parissayaṁ,</span></p><p class="p3"><span class="t1">that safeguard destroys all dangers, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">“Sotthinā gabbhavuṭṭhānaṁ, yaṁ ca sādheti taṁ bhaṇe,”</span></p><p class="p3"><span class="t1">“Reciting it will acomplish safety for the child in the womb,” </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Therass'' Aṅgulimālassa, Lokanāthena bhāsitaṁ.</span></p><p class="p3"><span class="t1">The Elder Aṅgulimāla was told by the Friend of the World. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Kappaṭṭhāyimahātejaṁ, parittaṁ taṁ bhaṇāmahe:</span></p><p class="p3"><span class="t1">We will recite that safeguard of great power that lasts for an aeon: </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Yatohaṁ bhagini ariyāya jātiyā jāto</span></p><p class="p3"><span class="t1">Sister, since my birth in the Ariyan birth </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Nābhijānāmi sañcicca pāṇaṁ jīvitā voropetā,</span></p><p class="p3"><span class="t1">I am not aware of having intentionally taken the life of any living being, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Tena saccena sotthi te hotu sotthi gabbhassā ti.</span></p>', '-246-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '9. Bojjhaṅgaparittaṁ The Safeguard about the Factors of Awakening  ', 'title', 247);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 247, '<p class="p3"><span class="t1">By this truth, may you be be safe, and may the child in your womb be safe. </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>9. Bojjhaṅgaparittaṁ The Safeguard about the Factors of Awakening  </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Introductory Verses</span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">Saṁsāre saṁsarantānaṁ sabbadukkhavināsane</span></p><p class="p3"><span class="t39">While rolling on through the rounds of birth and death all suffering is destroyed</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Sattadhamme ca Bojjhaṅge Mārasenapamaddane.</span></p><p class="p3"><span class="t39">(by) the seven Factors of Awakening, which crush Māra’s army.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Bujjhitvā ye cime sattā tibhavā muttakuttamā,</span></p><p class="p3"><span class="t39">Having Awakened to these seven they are supremely released from the three worlds,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Ajātim-ajarābyādhim amataṁ nibbhayaṁ gatā.</span></p><p class="p3"><span class="t39">and have gone to (that state where there is) no birth, no ageing, no sickness, no death and which is free from fear.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Evam-ādiguṇūpetaṁ anekaguṇasaṅgahaṁ</span></p><p class="p3"><span class="t39">Being endowed with this quality and a collection of countless other qualities</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Osadhañ-ca imaṁ mantaṁ: Bojjhaṅgañ-ca bhaṇāmahe.</span></p><p class="p3"><span class="t39">it is a medicinal mantra: we will recite those factors of Awakening.</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">The Safeguard</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Bojjhaṅgo satisaṅkhāto dhammānaṁ vicayo tathā,</span></p><p class="p3"><span class="t39">The Factors of Awakening are agreed upon as mindfulness, and then investigation of the (nature of) things,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vīriyaṁ pīti-passaddhi Bojjhaṅgā ca tathāpare,</span></p><p class="p3"><span class="t39">And after that the Factors of Awakening of energy, joyful-interest and calm,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Samādhupekkhabojjhaṅgā: satte te Sabbadassinā</span></p><p class="p3"><span class="t39">(Then) the Factors of Awakening of concentration and equanimity: these seven</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Muninā sammad-akkhātā, bhāvitā bahulīkatā</span></p>', '-247-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 248, '<p class="p3"><span class="t39">were well-proclaimed by the All-Seeing Sage, when developed and practiced</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Saṁvattanti abhiññāya Nibbānāya ca Bodhiyā:</span></p><p class="p3"><span class="t39">They lead to deep knowledge, Emancipation and Awakening:</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ekasmiṁ samaye Nātho Moggallānañ-ca Kassapaṁ</span></p><p class="p3"><span class="t39">At one time The Lord, having seen that Moggallāna and Kassapa</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gilāne dukkhite disvā, Bojjhaṅge satta desayi,</span></p><p class="p3"><span class="t39">Were sick and suffering, taught these seven Factors of Awakening,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Te ca taṁ abhinanditvā rogā mucciṁsu taṅkhane:</span></p><p class="p3"><span class="t39">And having rejoiced in them, they were freed from illness in an instant:</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Ekadā Dhammarājā pi gelaññenābhipīḷito,</span></p><p class="p3"><span class="t39">One day the Dhamma-King was oppressed by sickness,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Cundattherena taṁ yeva bhaṇāpetvāna sādaraṁ,</span></p><p class="p3"><span class="t39">And after the Elder Cunda had recited them with respect,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sammoditvāna ābādhā tamhā vuṭṭhāsi ṭhānaso:</span></p><p class="p3"><span class="t39">Having rejoiced he rose from that affliction on the spot:</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pahinā te ca ābādhā tiṇṇannam-pi Mahesinaṁ</span></p><p class="p3"><span class="t39">These afflictions were abandoned by these three Great Seers,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Maggahatākilesā va pattānupattidhammataṁ:</span></p>', '-248-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '10. Vaṭṭakaparittaṁ The Discourse about the Quail ', 'title', 249);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 249, '<p class="p3"><span class="t39">Just as the defilements are destroyed by the path, attained in accordance with nature:</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>10. Vaṭṭakaparittaṁ The Discourse about the Quail </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Introductory Verses</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Pūrentaṁ Bodhisambhāre nibbattaṁ vaṭṭakajātiyaṁ,</span></p><p class="p3"><span class="t39">While fulfilling the conditions for Awakening he was born as a kind of quail,</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">yassa tejena dāvaggi Mahāsattaṁ vivajjayi.</span></p><p class="p3"><span class="t39">and through his spiritual power the Great Being avoided the forest-fire.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Therassa Sāriputtassa Lokanāthena bhāsitaṁ,</span></p><p class="p3"><span class="t39">This was spoken by the Lord of the World to the Elder Sāriputta,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Kappaṭṭhāyimahātejaṁ: parittaṁ taṁ bhaṇamahe.</span></p><p class="p3"><span class="t39">it has great power for a world-cycle: we will recite that safeguard.</span></p><p class="p3"><span class="t5"> </span></p><p class="p3"><span class="t5">The Safeguard</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Atthi loke sīlaguṇo saccaṁ soceyyanuddayā,</span></p><p class="p3"><span class="t39">There is in the world virtue, truth, purity and empathy,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">tena saccena kāhāmi, saccakiriyam-uttamaṁ.</span><span class="t5"> </span><span class="t40">[1]</span></p><p class="p3"><span class="t39">by the truth of that I will make an asseveration of supreme truth.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Āvajjetvā Dhammabalaṁ, saritvā pubbake Jine,</span></p><p class="p3"><span class="t39">After reflecting on the strength of the Dhamma, and recalling the former Victors,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">saccabalam-avassāya, saccakiriyam-akāsahaṁ:</span><span class="t37"> </span><span class="t40">[2]</span></p><p class="p3"><span class="t39">relying on the strength of truth, I made an asseveration of truth:</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">“Santi pakkhā apatanā, santi pādā avañcanā,</span></p>', '-249-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '11. Phandanaparittaṁ The Tree Safeguard ', 'title', 250);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 250, '<p class="p3"><span class="t39">“There are wings which fly not, there are feet which walk not,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Mātāpitā ca nikkhantā, Jātaveda, paṭikkama!”</span><span class="t37"> </span><span class="t40">[3]</span></p><p class="p3"><span class="t39">Mother and Father have gone out, Fire, please depart!”</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Sahasaccakate mayhaṁ, mahāpajjalito sikhī</span></p><p class="p3"><span class="t39">This (asseveration) was made by me with truth, and the great blazing fire</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">vajjesi soḷasakarīsāni, udakaṁ patvā yathā sikhī,</span></p><p class="p3"><span class="t39">avoided (this area for) sixteen measures, like fire after reaching water,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">saccena me samo natthi, esā me saccapāramī!</span><span class="t37"> </span><span class="t40">[4]</span></p><p class="p3"><span class="t39">there is no one my equal for truth, such is my perfection of truth!</span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Etena saccavajjena sotthi te hotu sabbadā!</span></p><p class="p3"><span class="t1">By this declaration of the truth may you be safe at all times! </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>11. Phandanaparittaṁ The Tree Safeguard </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Iccevaṁ phandano issaṁ, isso ca pana phandanaṁ</span></p><p class="p3"><span class="t39">Thus the tree and the lord, and the lord and the tree</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Aññam-aññaṁ vivādena, aññam-aññam-aghātayuṁ. </span><span class="t40">[1]</span></p><p class="p3"><span class="t39">With each other quarrel, and each other destroy.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Evam-eva manussānaṁ vivādo yattha jāyati, </span></p><p class="p3"><span class="t39">So it is with men, wherever a quarrel starts,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Mayūranaccaṁ naccanti, yathā te issaphandanā. </span><span class="t40">[2]</span></p><p class="p3"><span class="t39">They dance the peacock dance, like the lord and the tree.</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Taṁ vo vadāmi, bhaddaṁ vo, yāvantettha samāgatā, </span></p><p class="p3"><span class="t39">I say this to you, hearken, all assembled here, </span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Sammodatha mā vivadatha, mā hotha issaphandanā,</span><span class="t37"> </span><span class="t40">[3]</span></p>', '-250-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '12. Narasīhagāthā Verses about a Lion of a Man ', 'title', 251);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 251, '<p class="p3"><span class="t39">Be friendly, don''t quarrel, be not like lord and tree,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Sāmaggim-eva sikkhetha, Buddhehetaṁ pasaṁsitaṁ,</span></p><p class="p3"><span class="t39">Train in peace, this is praised by the Awakened Ones,</span></p><p class="p3"><span class="t39"> </span></p><p class="p3"><span class="t5">Sāmaggirato dhammaṭṭho, yogakkhemā na dhaṁsati.</span><span class="t37"> </span><span class="t40">[4]</span></p><p class="p3"><span class="t39">Delight in peace, righteousness, safety will not be destroyed.</span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>12. Narasīhagāthā Verses about a Lion of a Man </h3></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Cakkavaraṅkitarattasupādo, </span></p><p class="p3"><span class="t1">Having beautiful feet marked with a fine wheel, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">lakkhaṇamaṇḍita-āyatapaṇhī, </span></p><p class="p3"><span class="t1">his long heels are a characteristic mark,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">cāmarachattavibhūsitapādo, </span></p><p class="p3"><span class="t1">his feet decorated with chowrie and sunshade, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">esa hi tuyha’ pitā narasīho. </span><span class="t5">[1]</span></p><p class="p3"><span class="t1">that indeed is your father, a lion of a man. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Sakyakumāravaro sukhumālo, </span></p><p class="p3"><span class="t1">An excellent and graceful Sakyan prince, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">lakkhaṇacittikapuṇṇasarīro, </span></p><p class="p3"><span class="t1">with a body full of characteristic marks, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">lokahitāya gato naravīro, </span></p><p class="p3"><span class="t1">a hero who has come for the world’s benefit,  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">esa hi tuyha’ pitā narasīho. </span><span class="t5">[2]</span></p><p class="p3"><span class="t1">that indeed is your father, a lion of a man. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Puṇṇasasaṅkanibho mukhavaṇṇo, </span></p><p class="p3"><span class="t1">Just like the full moon is his beautiful face, </span></p>', '-251-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 252, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Devanarāna’ piyo naranāgo, </span></p><p class="p3"><span class="t1">dear to gods and men, a Nāga amongst men, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">mattagajindavilāsitagāmī, </span></p><p class="p3"><span class="t1">walking like a charming lord of elephants, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">esa hi tuyha’ pitā narasīho. </span><span class="t5">[3]</span></p><p class="p3"><span class="t1">that indeed is your father, a lion of a man. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Khattiyasambhava-aggakulīno, </span></p><p class="p3"><span class="t1">Born into a leading noble family, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">devamanussanamassitapādo, </span></p><p class="p3"><span class="t1">gods and men revere his feet, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sīlasamādhipatiṭṭhitacitto, </span></p><p class="p3"><span class="t1">having virtue and concentration established in his mind, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">esa hi tuyha’ pitā narasīho. </span><span class="t5">[4]</span></p><p class="p3"><span class="t1">that indeed is your father, a lion of a man. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Āyatatuṅgasusaṇṭhitanāso, </span></p><p class="p3"><span class="t1">Having a long, prominent and well-formed nose, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">gopakhumo abhinīlasunetto, </span></p><p class="p3"><span class="t1">cow-like eyelashes, beautiful deep black eyes, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">indadhanū-abhinīlabhamūko, </span></p><p class="p3"><span class="t1">his deep black eyebrows (are just like) Indra’s bow, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">esa hi tuyha’ pitā narasīho. </span><span class="t5">[5]</span></p><p class="p3"><span class="t1">that indeed is your father, a lion of a man.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Vaṭṭasumaṭṭasusaṇṭhitagīvo, </span></p><p class="p3"><span class="t1">Having a round, sizeable, and well-formed neck, </span></p>', '-252-');
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 253, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sīhahanū migarājasarīro, </span></p><p class="p3"><span class="t1">a lion’s jaw, a body like the king of beasts, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kañcanasucchavi-uttamavaṇṇo, </span></p><p class="p3"><span class="t1">beautiful golden skin, of supreme colour, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">esa hi tuyha’ pitā narasīho. </span><span class="t5">[6]</span></p><p class="p3"><span class="t1">that indeed is your father, a lion of a man. </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Siniddhasugambhiramañjusaghoso, </span></p><p class="p3"><span class="t1">Having a very deep, charming, and sweet voice, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">hiṅgulabandhusurattasujivho, </span></p><p class="p3"><span class="t1">with a beautiful tongue, red like vermillion, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">vīsativīsatisetasudanto,</span></p><p class="p3"><span class="t1">with twenty by twenty beautiful white teeth, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">esa hi tuyha’ pitā narasīho.</span><span class="t5"> </span><span class="t5">[7]</span></p><p class="p3"><span class="t1">that indeed is your father, a lion of a man.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Añjanavaṇṇasunīlasukeso, </span></p><p class="p3"><span class="t1">Having beautiful, glossy, black-coloured hair, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">kañcanapaṭṭavisuddhanalāṭo, </span></p><p class="p3"><span class="t1">a forehead like a plate of purified gold, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">osadhipaṇḍarasuddhasu-uṇṇo, </span></p><p class="p3"><span class="t1">and hair between the brows like a pure white star, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">esa hi tuyha’ pitā narasīho. </span><span class="t5">[8]</span></p><p class="p3"><span class="t1">that indeed is your father, a lion of a man.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">Gacchati nīlapathe viya cando, </span></p><p class="p3"><span class="t1">Like the moon going along the dark night path, </span></p>', '-253-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_safeguard_recitals', '* * * Further Reading * * *', 'title', 254);
INSERT INTO pages (bookid, page, content, paranum) VALUES ('annya_safeguard_recitals', 254, '<p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">tāragaṇāpariveṭhitarūpo, </span></p><p class="p3"><span class="t1">surrounded by a body of stars, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">sāvakamajjhagato Samaṇindo,</span></p><p class="p3"><span class="t1">is the Lord of Ascetics in the midst of his disciples, </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t5">esa hi tuyha’ pitā narasīho ti.</span><span class="t5"> </span><span class="t5">[9]</span></p><p class="p3"><span class="t1">that indeed is your father, a lion of a man.  </span></p><p class="p3"><span class="t1">  </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><span class="t1"> </span></p><p class="p3"><h3>* * * Further Reading * * *</h3></p>', '-254-');
