INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('baddha','<p class="definition">(pp. của bandhati) cột, trói, cột cho chắc, để chung lại, gài bẫy. --ñjalika a. hai tay chắp lại đưa ra với sự tôn kính. --rāva m. sự kêu la của vật bị mắc bẫy, hay bị bắt. --vera nt. gom kẻ địch lại, bao vây quân địch.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('badhira','<p class="definition">a. điếc, người điếc.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('badālatā','<p class="definition">f. một loại cây dây có chất ngọt.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bahala','<p class="definition">a. dày, đông đặc. --latta sự dày đặc.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bahi','<p class="definition">in. bên ngoài, phía ngoài, thuộc về bên ngoài. --nagara nt. bên ngoài châu thành, ngoại ô. --nikkhamana nt. đi ra khỏi. --gata a. đi ra ngoài.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bahiddhā','<p class="definition">in. phía ngoài, ở ngoài.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bahivādeti','<p class="definition">(abhi + vad + e) cúi mình xuống, vái chào. aor. --esi. pp. --vādiṭa.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bahu','<p class="definition">a. nhiều, đầy đủ, dồi dào. pl. nhiều người. --ka a. nhiều. --karanīya, kicca a. có nhiều việc phải làm, bận rộn. --kāra a. cần dùng lắm, công việc lớn lao. --kkhattuṃ ad. nhiều lần. --jana m. quần chúng, công chúng. --jāgara a. có đầy đủ sự thức tỉnh. --dhana a. nhiều của cải. --ppada a. nhiều dấu chân, cho nhiều. --bbīhi nhiều chữ liên hệ, ráp lại. --bhanda a. có nhiều hàng hóa. --bhānī a. nói nhiều quá, già chuyện. --bhāva m. sự dồi dào đầy đủ. --mata a. nhiều người ưa thích, chấp nhận do phần đông. --māna m., --mānana nt. sự kính trọng, sự mến thích, sự tôn sùng. --vacana nt. số nhiều. --vidha a. nhiều lần, nhiều cách. --ssuta a. bác học.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bahudhā','<p class="definition">ad. trong nhiều cách thế, đường lối.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bahula','<p class="definition">a. đầy đủ, thường xuyên. --tā f. --tta nt. sự dồi dào, đầy đủ. --laṃ a. phần nhiều, thường thừơng. --likāta a. thực hành luôn luôn. --likaraṇa, līkamma nt. --līkāra m. thực hành liên tiếp, siêng năng tập luyện.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bahulīkaroti','<p class="definition">(bahula + ī + kar + o) đưa lên một cách thận trọng, làm cho gia tăng. aor --kari. pp. --kala.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bahuso','<p class="definition">ad. phần nhiều, thường xuyên, lập đi lập lại.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bahutta','<p class="definition">nt. sự có nhiều lần, nhiều thế cách.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bahūpakāra','<p class="definition">a. có nhiều sự giúp đỡ lắm, có nhiều lợi ích.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bajjhati','<p class="definition">(pass. của bandhati) bị trói, bị cầm giữ, bị bắt (trong một cái bẫy).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('baka','<p class="definition">m. con hạc, con cò.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bala','<p class="definition">nt. sức lực, uy quyền, sức mạnh, một toán quân, quân lực. --kāya m. một toán quân. --koṭṭhaka nt. đồn binh, hầm trú của quân đội. --kkāra m. sự hung bạo, sự dùng võ lực. --da a. cho sức lực, tặng cho quyền lực. --dāna nt. sự ban cho quyền lực. --ppatta a. trở nên có quyền lực, trở nên mạnh mẽ. --vantu a. có sức lực. --vāhana nt. bộ binh và xa binh.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('balaṭṭha','<p class="definition">BALATTHA m. người lính, nhân viên quân đội.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bali','<p class="definition">m. sự cúng dường của tôn giáo, lợi tức, thuế. --kamma sự hiến dâng. --paṭiggāhaka a. lãnh lấy sự hiến dâng, hay thọ lãnh một lợi tức. --puṭṭha m. con quạ. --harana nt. sự thâu thuế.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('balivadda','<p class="definition">m. con bò đực.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('balākā','<p class="definition">f. con hạc nâu.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('balī','<p class="definition">a. có uy quyền, sức mạnh.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bandha','<p class="definition">m., --dhana nt. dây, cột, sự dính líu, sự bị cầm tù.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bandhana','<p class="definition">nt. sự cột lại, dính lại, đóng (sách) lại. --nāgāra nt. khám đường. --nāgārika m. tội nhân, người tù, người giữ ngục.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bandhati','<p class="definition">(bandh + a) cột trói, dính liền, thống nhất, cột chung lại, bỏ tù, bắt chiếm, hợp thành, hợp nhất. aor --bandhi. pp. baddha. pr.p. band, --hanta. abs. --dhitvā, --dhiya. inf. --dhituṃ. pt.p. --dhitabba, --dhanīya.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bandhava','<p class="definition">m. người quen thuộc, bà con, thân quyến.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bandhu','<p class="definition">như BANDHAVA. --jīvaka m. cây bông hường của Tàu. --mantu a. có bà con thân quyến, có nhiều thân quyến.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bandhāpeti','<p class="definition">(caus. của bandhati) sai biểu người cột trói. aor. --esi. pp. --pita.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('battiṃsati','<p class="definition">f. số ba mươi hai.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bavhābādha','<p class="definition">a. nhiều thứ bịnh.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('beluva','<p class="definition">m. cây marmelos. --pakka nt. trái chín marmelos. --laṭṭhi f. cây marmelos con. --salāṭuka nt. trái marmelos chưa chín.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhabba','<p class="definition">a. có thể, nên, đáng. --tā f. sự có thể, đáng làm, sự thuận tiện.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhacca','<p class="definition">m. người giúp việc, người hầu, đầy tớ. adj. được nuôi dưỡng.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhadanta','<p class="definition">BHADDANTA a. bực đáng kính, kính trọng. m. người đáng kính.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhadda','<p class="definition">a. oai nghiêm, điềm lành, may mắn, tốt đẹp. --ka, nt. vật tốt hay may mắn. adj. tài hay đồ tốt. --kaccānā f. một tên khác của mẹ La hầu la, Yasodharā. --kumbha m. đầy bình, hũ (là tượng trưng cho điềm tốt). --ghaṭa m. cái thùng dùng để rút số trong cách xổsố. --dāru m. một loại cây thông hay cây bách hương (ở Hy Mã Lạp Sơn). --padā f. tên một ngôi sao. --pīṭha nt. ghế mây. --mukha a. có gương mặt đẹp, địa chỉ bổ khuyết. --yuga nt. một cặp, một đôi tốt nhất.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaddā','<p class="definition">--ddikā f. người phụ nữ có hạnh kiểm tốt, đứng đắn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhadra','<p class="definition">như chữ bhadda.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaga','<p class="definition">nt. sự may mắn, hên, bộ phận phụ nữ (cơ thể). --ndalā f. âm sang, ống thông (cá voi). --vantu a. sự may mắn. m. đức Phật.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhagga','<p class="definition">pp. của bhañjati : bị bể.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaginī','<p class="definition">f. chị, em gái.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhajati','<p class="definition">(bhaj + a) cộng sự với, chung bọn với. aor. bhaji. pp. bhajita. pr.p. bhajamāna. ads. bhajitvā. pt.p. --jitabba.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhajjati','<p class="definition">(bhaj + a) nung, nướng, hơ. aor. bhajji. pp. bhajjita. pr.p. --jamāna. abs. --jitvā. pass. bhajjiyati.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhakkha','<p class="definition">a. đáng, nên ăn, ăn được. nt. vật thực, mồi. in cpds. nuôi dưỡng.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhakkhana','<p class="definition">nt. đang ăn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhakkhati','<p class="definition">(bhakkh + a) ăn, thọ thực, dùng bữa. aor. --kkhi. pp. --khita. inf. --khituṃ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhakkheti','<p class="definition">(bhakkh + e) như bhakkhati.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhallātaka','<p class="definition">m. cây có hột để làm dầu.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhama','<p class="definition">m. vật quay tròn, xoáy nước, sự rẽ tách ra. --kāra m. thợ tiện.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhamara','<p class="definition">m. con ong vò vẽ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhamati','<p class="definition">(bham + a) xoay quanh, quây quẩn, đi quanh quẩn, vơ vẩn. aor. bhami. pp. bhanta. pr.p. bhamanta. abs. --mitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhamu','<p class="definition">--mukā f. lông mày.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhamārikā','<p class="definition">f. chóp đỉnh kêu vo vo.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhanta','<p class="definition">(pp. của bhamati) sự lung lay, sự rẽ tách ra. --tatta nt. tình trạng rối loạn, lộn xộn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhante','<p class="definition">(voc. của bhadanta) bạch, kính thưa, dạ thưa, bạch ngài.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhara','<p class="definition">a. sự nâng đỡ. mātāpettibhara người phụng dưỡng cha mẹ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bharati','<p class="definition">(bhar + a) chịu đựng, chống đỡ, duy trì, bảo tồn. aor. bhari. pp. bhata. abs. bharitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bharaṇa','<p class="definition">sự duy trì, sự chịu đựng.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bharita','<p class="definition">pp. đầy, làm cho lan tràn, bảo tồn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhariyā','<p class="definition">f. người vợ,</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhasma','<p class="definition">nt. tro. --cohanna a. vùi trong tro.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhassa','<p class="definition">nt. nói vô ích. --sārāmatā f. sự bận rộn, sự dính vào sự nói vô ích.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhassara','<p class="definition">a. chói sáng, chiếu sáng, rực rỡ, chói lọi.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhassati','<p class="definition">(bhas + ya) rớt, té xuống, nhỏ giọt, đi xuống. aor. bhassi. pp. bhattha. pr.p. santa, --samāna. abs. --sitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhastā','<p class="definition">f. túi da, bễ (ống bễ thổi lò).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhata','<p class="definition">(pp. của bharati) nuôi dưỡng duy trì, chăn nuôi. m. người giúp việc, người đầy tớ. --ka m. người ở mướn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhati','<p class="definition">f. giá, tiền thuê, tổn phí.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhatta','<p class="definition">nt. cơm, vật thực, bữa ăn. --kicca nt. đang ăn cơm. --kāraka m. người nấu ăn, người làm bếp. --kilamatha, --sammada m. sự buồn ngủ sau khi ăn. --gāma m. một làng phải nạp cống lễ. --gga nt. phòng ăn nơi tu viện, trai đường. --puṭa nt. một gói cơm. --vissagga m. sự dọn bữa ăn. --vetana nt. vật thực và giá cả. --velā f. giờ ăn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhatti','<p class="definition">f. sự tôn sùng, sự tin tưởng, sự ái mộ. --ka, --mantu. a. tôn sùng, tin tưởng.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhattu','<p class="definition">m. người chồng, người nuôi dưỡng, nâng đỡ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhava','<p class="definition">m. cảnh giới, hữu (trong nhân quả liên quan). --gga m. chỗ cao nhất của cảnh giới (hay thế giới). --ṅga nt. chỗ trú (tâm, chỗ tâm nghỉ không hoạt động nữa) giòng sống của tâm. --cakka nt. bánh xe luân hồi. --taṇhā, --netti f. ước muốn sự tái sanh trong một cảnh giới, dục sanh về sắc giới. --ntaga, --ntagū a. đến cảnh cuối cùng của cảnh giới, chấm dứt cảnh giới. --ntara nt. cảnh giới khác. --saṃyojana nt. dây cột vào cảnh giới tái sanh. --vābhava m. đời này hoặc đời khác. --vesanā f. ước mong tái sanh. --vogha m. hầm hay vực sâu của cảnh giới.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhavana','<p class="definition">nt. sự trở thành, chỗ ở, nơi cư trú.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhavanta','<p class="definition">a. thịnh vượng (tiếng dùng cách lễ độ cho chữ ‘anh, ông, v.v..’</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhavati','<p class="definition">(bhū + a) trở thành, trở nên có sinh tồn. aor. bhavi, abhavi. pp. bhūta. pr.p. bhavanta, bhavamāna. pt.p. bhavitabba. abs. bhavitvā. inf. bhavituṃ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaya','<p class="definition">nt. sợ sệt, kinh khủng. --ṅkana a. sự ghê sợ, sự kinh hoàng. --dassāvī, --dessi a. gặp sự kinh sợ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhayānaka','<p class="definition">-- yāyaha a. kinh sợ, ghê tởm.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhañjaka','<p class="definition">3. người làm bể, làm hư.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhañjana','<p class="definition">nt. sự đập bể, sự phá tan. --naka a. sự bể nát, sự phá hoại.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhañjati','<p class="definition">(bha ñj + a) làm bể, phá hoại. aor. bha ñji. pp. bhagga, bha ñjita. pr.p. --janta, jamāna. abs. bha ñjitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaṅga','<p class="definition">m. sự bể tan, sự tan rã. nt. vải gai. --kkha ṇa m. trong khi đang tan rã. --gānupassanā f. huệ thấy rõ sự tan rã của pháp hành.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaṇati','<p class="definition">(bhan + a) nói, thuật lại, thuyết. aor. bhani. pp. bhanita. pr.p. --nanta. pt.p. --nitabba. abs. --bhaṇitvā. inf. bhaṇituṃ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaṇe','<p class="definition">in. cách nói với người hạ cấp.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaṇḍa','<p class="definition">--daka nt. hàng hóa, dụng cụ, phẩm vật. --dāgāra nt. kho chứa phẩm vật, kho bạc. --dāgārika m. người giữ kho, quan kho bạc.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaṇḍana','<p class="definition">nt. sự cãi cọ gây gỗ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaṇḍati','<p class="definition">(bhaṇ + a) bhandeti, (bhad + e) gây lộn. aor. bhandi, --esi. abs. --detvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaṇḍikā','<p class="definition">f. một bó, một gói.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaṇḍu','<p class="definition">m. người đã cạo tóc. --kamma nt. sự cạo tóc.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('bhaṭa','<p class="definition">m. người lính, nguyên soái, người làm thuê làm mướn. --senā một bộ binh.</p>',71);
