INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('brahma','<p class="definition">m. Trời Phạm Thiên, tạo hóa. --kāyika a. thuộc về tùy tùng của Trời Phạm Thiên. --ghosa a. có tiếng nói giống tiếng Phạm Thiên. --cariyā f. phạm hạnh, triết hạnh. --cārī f. sự thực hành theo phạm hạnh. --jacca a. thuộc về giòng Bà la môn. --ñña nt. -- ññatā f. đời sống trong sạch, bản tính của Bà la môn. --daṇṇa m. một cách phạt không nói chuyện. --deyya nt. sự ban tặng của nhà Vua. --ppatta a. đạt đến trạng thái cao nhất. --bandhu m. thân quyến của Phạm Thiên, là Bà la môn. --bhūta a. cao quí nhất. --loka m. cõi Phạm Thiên. --vimāna nt. đền đài của Trời Phạm Thiên. --vihāra m. tứ vô lượng tâm, là : từ, bi, hỉ, xả.</p>',71);
