INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('buddha','<p class="definition">m. người đã giác ngộ (đức Phật). --karaka-dhamma m. sự thực hành cho trở thành một vị Phật. --kāla m. thời kỳ có Phật ra đời. --kolāhala m. sự tuyên bố cho hay sẽ có đức Phật ra đời. --kkhetta nt. ranh giới dưới quyền lực của một vị Phật. --guṇa m. ân đức Phật. --ṅkura m. bậc sẽ có số phận thành một vị Phật. --cakkhu nt. Phật nhãn. --ñāṇa nt. tuệ giác của đức Phật. --antara nt. khoảng cách từ đức Phật này đến đức kế ra đời.. --putta m. đệ tử Phật (học trò của Phật). --bala nt. Phật lực. --bhāva m. bản tính của đức Phật. --bhūmi f. căn bản của Phật tính. --māmaka a. chân thành với đức Phật --rasmi, --raṃsi f. hào quang của đức Phật. --līḷhā f. sự khoan dung của đức Phật. --vacana nt. giáo lý của đức Phật. --visaya m. năng lực của Phật. --veneyya a. được đức Phật chuyển hóa. --sāsana nt. Phật pháp. --ānubhāva m. uy lực của Phật. --ānussati f. niệm ân đức Phật. --āramma ṇa, --ālambana a. lấy đức Phật làm cảnh giới (căn bản). --upaṭṭhāka a. hầu hạ đức Phật. --uppāda m. thời kỳ đức Phật ra đời (giáng sinh).</p>',71);
