INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('deva','<p class="definition">m. chư Thiên, trời, mây mưa, ông vua. --kaññā f. tiên nữ. --kāya m. một nhóm chư Thiên. --kumāra m. hoàng tử. --kusuma nt. cây đinh hương. --gaṇa m. một toán chư Thiên. --cārikā f. đi trên hư không (hay trên trời). --ccharā f. nữ thần, mỹ nhân. --ññatara a. chư Thiên bậc thấp. --ṭṭhāna nt. một đền thờ chư Thiên. --ttabhāva m. thân hình của chư Thiên. --dattika, datiya a. chư Thiên ban cho. --dun, dubhi f. sấm sét. --dūta sứ mạng chư Thiên. --deva m. chư Thiên của hạng chư Thiên. --dhamma m. đức hạnh chư Thiên (sợ tội lỗi). --dhītu f. nữ thần còn tơ. --nagara nt. cảnh chư Thiên. --nikāya a. một phái chư Thiên. --purisā aṣự hội họp của chư Thiên (Thần Tiên hội). --putta m. Thiên tử (con của chư Thiên). --pura nt. cung trời. --bhavana nt. chỗ ở của chư Thiên Yāna. nt. con đường về Trời, xe đi về Trời. --rāja m. chúa chư Thiên (vua cõi Trời). --rukkha m. một thần. --rūpa nt. hình ảnh chư Thiên. --loka m. cõi Trời. --vimāna nt đền đài ở cung Trời.</p>',71);
