INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('dhamma','<p class="definition">m. giáo lý, thiên nhiên, chân lý, chủ nghĩa, luân lý, hạnh kiểm tốt đẹp. --kathā f. sự nói về đạo đức, luận đạo. --kkhāna nt. sự thuyết giáo lý. --kathika 3. pháp sư, người thuyết pháp. --kamma nt. sự hành động hợp pháp, công việc tiến hành đúng theo luật lệ. --karaka m. cái phích hay vải lọc nước. --kāma a. mến thích chân lý. --kāya a. pháp thân. --kkhandha m. một pháp môn (của giáo pháp). --gaṇṇikā f. thớt cày công lý (sự đem ra hành hình). --garu a. lấy lệ luật làm trọng đại. --gutta a. bảo vệ giáo lý. --ghosaka m. người tuyên bố về sự thuyết pháp. --cakka nt. bánh xe pháp. --cakkappavattana nt. chuyển pháp luân, quay bánh xe pháp, thuyết giảng về chân lý của vũ trụ. --cakkhu nt. pháp nhãn. --cariyā f. sự giữ theo, sự tu theo giáo pháp. --cārī 3. người hành theo giáo lý. adj. đức hạnh. --cetiya nt. pháp tháp. --jīvī a. sống ở theo giáo pháp. --ññū a. người thông hiểu giáo lý. --ṭṭha a. đứng đắn, chân chánh. --ṭṭhitī f. bản chất thật sự của giáo lý. --takka m. lý luận đúng đắn, --dāna nt. bố thí pháp. --dāyāda a. lấy giáo pháp làm gia tài. --dīpa a. lấy giáo pháp là nơi nương nhờ chắc chắn. --desanā f. sự thuyết pháp. --dessī 3. người ghét giáo lý. --dhaja a. lấy giáo lý làm kim chỉ nam (là cờ hiệu). --dhara a. người thuộc lòng giáo lý. --niyāma m. định luật của chân lý. --paṇñākāra m. vật tặng gồm có giáo pháp. --pada nt. một câu của giáo pháp. --ppamāṇa a. so sánh hay thí dụ với giáo lý. --bhaṇṇāgārika m. người chứa kho pháp. --bheri f. sự gióng trống giáo pháp. --rakkhita a. hộ trì bởi giáo pháp. --rata a. vui thích giáo lý. --rati f. sự vui thích giáo lý. --rasa m. hương vị giáo pháp. --rāja m. pháp vương. --laddha a. được gíao pháp, theo lẽ đạo. --vara m. giáo lý quí báu. --vādī 3. nói đúng theo giáo pháp. --vicaya m. sự tìm hiểu giáo lý. --vidū a. người hiểu giáo lý.--vinicchaya m. sự quyết định theo giáo lý. --vihārī a. ở theo giáo lý. --saṃvibhāga m. sự phân phát giáo pháp. --saṅgīti f. sự kết tập giáo pháp. --saṅgāhaka m. người biên soạn giáo lý. --samādāna nt. thọ trì giáo pháp. --saraṇa nt. qui y pháp, nương theo giáo pháp. --savaṇa nt. nghe pháp.--sākacohā f. sự hỏi, luận đạo lý. --sālā f. giảng đường. --senāpati m. người nắm quyền giáo pháp. --soṇṇa a. ưa thích giáo lý. --ssāmī m. làm chủ giáo pháp. --ādhipati tôn kính giáo pháp là bậc hướng dẫn. --ānudhamma m. sự theo đúng giáo lý. --ānuvuttī, ānusārī a. thực hành đúng theo giáo pháp. --ābhisamaya m. sự tỏ ngộ giáo lý. --āmata nt. hương vị của giáo pháp. --ādāsa m. tấm gương của giáo pháp. --ādhāra a. nâng đỡ giáo pháp. --āsana nt. pháp tọa, chỗ ngồi thuyết pháp.</p>',71);
