INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('kamma','<p class="definition">nt. sự làm, hành vi, việc làm. --kara, --karā m. nhân công, lao công. --karaṇa nt. công việc, lao công, việc làm. --kāraṇā nt. hình phạt, khổ sai. --kāraka nt. trường hợp về đối tượng, mục tiêu. --kkhaya m. tiêu tan, dứt nghiệp. --ja a. do nghiệp tạo ra. --jāta nt. nhiều loại nghiệp, nhiều nghề nghiệp. --dā, --yāda a. thọ lãnh kết quả nghiệp do mình tạo. --nānatta nt. có nhiều loại nghiệp. --nibbatta a. do nghiệp tạo ra. --patha m. con đường của nghiệp. --ppaccaya a. có nghiệp là nguyên nhân, căn bản. --phala nt. kết quả của nghiệp. --bandhu a. nghiệp là thân bằng quyến thuộc. --bala nt. nghiệp lực. --yoni a. nghiệp là nơi sanh ra. --vāda m. lý thuyết tin về nghiệp lực. --vādī a. tin về nghiệp. --vipāka m. sự kết quả của nghiệp mình đã tạo. --vaga m. sự xô đẩy, thúc giục của nghiệp. --samuṭṭhā, --na a. phát sanh lên do nghiệp. --sambhava a. sản xuất do nơi nghiệp --sarikkhaka a. giống nhau trong kết quả của hành vi. --saka a. nghiệp là gia tài của mình. --javātā m. việc khổ sở, gió nghiệp của đứa trẻ khi sanh a. --ānurūpa a. tùy theo duyên nghiệp. --aỳuhana nt. sự đầy dẫy của nghiệp tạo. --upaccaya m. sự tích trữ nghiệp.</p>',71);
