INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('kata','<p class="definition">pp. của karoti đã làm, làm xong, hết, dứt rồi, đầy đủ rồi. --kamma a. đã thực hành, đã làm xong việc của mình. --kalyāṇa a. người đã làm được việc tốt, lành. --kicca a. phận sự đã làm xong. --añjalī a. đã chấp tay vái chào. --paṭisanthāra a. được tiếp đãi tử tế. --paricaya a. đã làm quen với. --pātarāsa a. đã dùng xong buổi ăn sáng. --puñña a. người đã làm được việc lành.--puññatā f. công việc phước đức. --pubba a. đã làm khi trước. --bhattakicca a. người đã dùng cơm xong, đã thọ thực xong. --bhāva m. việc đã làm xong. --vedī a. biết trả ơn. --veditā f. sự biết trả ơn. --sakkāra. --saṅgaha a. người được sự tiếp đãi hay cúng dường. --saṅketa nt. một dấu hiệu khởi đầu. --sikkha a. người đã thực nghiệm. --anugaha a. được giúp đỡ do. --adhikāra, --abhinīhāra, --abhinivesa a. người đề đạt một nguyện vọng. --aparādha a. bị lỗi, phạm tội. 3. người phạm tội. --abhiseka a. người được xức dầu thánh, được tôn vương.</p>',71);
