INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('lūkha','<p class="definition">a. thô kệch, nhám nhúa, khốn khó. --civara a. sự dệt vải thô làm y. --tā f. sự thô to. --ppasanna a. trung thành với người đê tiện, đáng khinh bỉ. --ājīvī a. sống đời một cách khốn khổ.</p>',71);
