INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('mada','<p class="definition">m. sự kiêu hãn, sự say mê, sự say đắm tình dục, tự kiêu. --nīya a. say mê, sự làm quyến luyến, dính líu.</p>',71);
