INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('maddati','<p class="definition">(madd + a) nghiền nát, đạp lên, chế phục. aor. maddi. pp. --dita. pr.p. --danta. abs. --ditvā, maddiya.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('maddava','<p class="definition">nt. sự mềm mại, sự dịu dàng, vật mềm. adj. dịu dàng, lễ độ, mềm mỏng.</p>',71);
