INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('madhu','<p class="definition">nt. mật, rượu vang làm bằng thứ bông Bassia. --ka m. cây Latifolia, lá lớn có chất ngọt. --kara m. con ong. --gaṇṇa, --paṭala m. ổ ong, bánh mật. --pa m. con ong, loại hút mật. --piṇṇikā f. bánh tròn bằng bột mật. --bhata con ong. --makkhita a. thoa trét mật. --meha m. bịnh tiểu đường. --laṭṭhikā f. cam thảo. --lāja m. cốm dẹp, hay lúa mạch chiên trộn với mật. --līha m. con ong. --ssva a. chảy, nhỏ từng giọt mật.</p>',71);
