INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('magga','<p class="definition">m. con đường, đạo, đường đi. --kilanta a. mệt mỏi bởi đi bộ. --kusala a. người rành mạch về con đường. --kkhāỳi a. người chỉ con đường chân chánh, ngay thẳng. --ṅga nt. con đường gồm có nhiều nẻo (là bát chánh đạo). --ñāna nt. đắc đạo. --ññū, --vidū a. người biết rõ con đường. --ṭṭha a. người đang đắc đạo hay đang đi đến con đường đạo. --maggadūsī m. ăn cướp theo đường xa lộ. --desaka a. người chỉ đường. --paṭipanna a. người đi đường, người đã đi vào con đường. --bhāvanā f. sự tham thiền hay cho đắc đạo. --mūḷha a. người lạc đường. --sacca nt. đạo diệu đế.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('maggana','<p class="definition">nt., --nā f. sự tìm kiếm, sự vạch ra.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('maggati','<p class="definition">(maga + a) tìm kiếm đi theo con đường, vạch ra con đường. aor. maggi. pp. --gīla. abs. --jitvā.</p>',71);
