INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('mahallaka','<p class="definition">a. già cả, trưởng lão. m. người già cả. --tara a. càng già. --llikā f. người phụ nữ già, bà già.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('mahanta','<p class="definition">a. to, lớn, vĩ đại. (f. mahanti, mahati). --tara a. càng to lớn, càng rộng ra. --tā f. --bhāva m. sự to lớn.</p>',71);
