INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('mahoraga','<p class="definition">m. Long Vương, chúa rồng.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('mahā','<p class="definition">(chữ mahanta trở thành maha) --upāsaka m. thiện nam tín nữ, đệ tử Phật. --upāsikā f. đại tín nữ. --karuṇā f. đại bi. --kāya a. người to hay mập mạp. --gaṇa m. đại chúng. --gaṇī 3. có nhiều tín đồ hướng theo. --jana m. công chúng. --taṇha a. tham lam quá độ. --tala nt. sân thượng trên các đền đài. --dhana nt. của cải to lớn (nhiều). --naraka, --niraya m. đại địa ngục. --nasa nt. nhà bếp. --nubhāva a. uy quyền, to lớn. --pañña a. đại trí tuệ. --patha m. đại lộ. --pitu m. bác (anh của cha). --purisa m. đại nhân. --bhūta nt. tứ đại. --bhoga a. có nhiều tài sản. --mati m. người thượng trí thức. --matta, --macca m. thủ tướng, đại thần. --muni m. bậc đại giác. --megha m. trận mưa to. --mahayanna, --yāga m. đại cúng dường (sinh mạng) của đạo Bàlamôn. --yasa a. vang danh, danh tiếng, lừng lẫy. --raha a. quí lắm, vật quí trọng, hiếm có. --rājā m. đại vương. -- latāpasādhana nt. đồ nữ trang bằng dây to có nhận nhiều hột ngọc. --satta m. đại chúng sinh. --samudda m. đại dương. --sara nt. đại hồ. --sāla a. có nhiều tài sản quá (đại bá hộ). --sāvaka m. đại đệ tử.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('mahī','<p class="definition">f. mặt đất, tên con sông (Ấn Độ). --tala nt. sự bằng phẳng của mặt đất. --dhara m. trái núi. --pati, --pāla m. ông Vua. --ruha m. cây (cối).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('majja','<p class="definition">nt. vật làm cho say mê. --na nt. sự làm cho say mê, cho hờ hững, bơ thờ. --pa 3. người uống rượu mạnh. --pāna nt. sự uống rượu, thức uống có chất say. --pāyī như majjapa. --vikkayī m. người bán rượu.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('majjanā','<p class="definition">f. đánh bóng, quét dọn, sự vuốt ve.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('majjati','<p class="definition">(mad + ya) bị say mê (maj + a) làm cho láng, đánh bóng, quét dọn, làm cho sạch sẽ. aor. ajji. pp. 1. --matta. 2. --mattha hay majjita. pr.p. majjanta. abs. majjitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('majjha','<p class="definition">m. chính giữa, eo lưng. adj. trung bình. --ṭṭha, --tta a. trung lập, không tự vị, trung bình. --ṇha m. buổi trưa, giữa ngày. --ttatā f. sự trung thực, sự ngay thẳng, tâm trung bình. --desa m. xứ trung lập --ntika, --ntikasa, maya m. trưa, nửa ngày.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('majjhima','<p class="definition">a. trung bình, chính giữa, trung tâm, có điều độ. --desa m. miền trung Ấn Độ (luôn cả con sông Hằng). --purisa m. người cao bực trung, ngôi thứ hai trong văn phạm. --ỳama m. canh giữa của đêm (từ 10 giờ đêm đến 2 giờ khuya). --vaya m. tuổi trung niên.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('majjāra','<p class="definition">m. con mèo. --jārī f. mèo cái.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('makaci','<p class="definition">m. dây cung bằng chỉ gai. --vāka nt. thớ cây gai. --vattha nt. vải bố (để làm buồm).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('makara','<p class="definition">m. con cá đao, quái vật ở biển. --dantaka nt. một bức họa giống hình răng cá đao.</p>',71);
