INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pacināti','<p class="definition">(pa + ci + nā) lặt, bẻ, hái, tom góp, tích trữ. aor. pacini. pr.p. pacinanta.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pacura','<p class="definition">a. dồi dào, đầy đủ, nhiều, khác nhau.</p>',71);
