INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pacāpeti','<p class="definition">(caus. của pacati) sai, biểu nấu. aor. --esi. abs. pacāpetvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pacāraka','<p class="definition">3. người trông nom, cai quản, làm cho người khác biết, nhà xuất bản.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pacāreti','<p class="definition">(pa + car + e) cai quản, truyền thanh, thông cáo. aor. --esi. pp. --rita. abs. --retvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pada','<p class="definition">nt. bàn chân, bước chân, một lời nói, địa vị, chỗ, lý do, nguyên nhân một câu kệ, sự an nghỉ cuối cùng. --tthāna nt. nguyên nhân gần nhất, kế cận. --cetaya nt. bàn chân, dấu chân thánh nhân (như đức Phật). --jāta nt. nhiều kiểu dấu chân. --pūrāna nt. một phần nhỏ trợ từ. --bhājana nt. sự phân chia tiếng nói, phân mỗi tiếng riêng ra. --bhānaka a. người đọc những lời lẽ trong kinh. --vaṇṇanā f. giải thích từ tiếng. --valañja nt. dấu vết, dấu chân. --vihhāga m. sự rời riêng của tiếng. --sadda m. tiếng chân bước đi.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padahati','<p class="definition">(pa + dah + a) cố gắng, ham mê, bắt đầu, chạm trán. aor. padahi. pp. --hita. abs. --hitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padakkhiṇā','<p class="definition">f. sự đi chung quanh, sự đi vòng phía tay mặt của bậc đáng tôn kính hay vật đáng tôn sùng (tháp), đi quanh (nhiễu Phật ba vòng).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padara','<p class="definition">nt. tấm ván, cái bàn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padatta','<p class="definition">(pp. của padāti) trao cho, phân phát cho.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padavītihāra','<p class="definition">m. sự đổi bước đi.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padaṃsa','<p class="definition">m. --sana nt. sự phá hủy, sự vi phạm, sự phạm luật, sự cướp giật.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padaṃsiya','<p class="definition">a. chịu trách nhiệm, bị vi phạm hay tấn công trước hay cướp giật.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padesa','<p class="definition">m. miền, xứ, chỗ, địa phận. --ñāṇa nt. sự hiểu biết có hạn chế. --rajja nt. sự uy quyền của địa phận. --rāja m. tiểu vương, quận tước.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padhaṃseti','<p class="definition">(pa + dhaṃs + e) phá hủy, tấn công, cướp giật, vi phạm. aor. --esi. pp. --sita. abs. --setvā. pr.p. --senta.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padhota','<p class="definition">(pp. của padhovati) rửa sạch, làm cho bén, nhọn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padhāna','<p class="definition">padahana nt. cố gắng, ráng sức, tinh tấn. --ghara nt. cái nhà dùng để tham thiền. --nika a. cố gắng tham thiền.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padhāvana','<p class="definition">nt. sự chạy ra.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padhāvati','<p class="definition">(pa + dhāva + a) chạy ra, đến. aor --padhāvi.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padhūpeti','<p class="definition">(pa + dhup + e) làm lên khói, xông khói, coi dhūpeti. pp. padhūpita.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padika','<p class="definition">a. gồm có những hàng thơ. m. người đi bộ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padippati','<p class="definition">(pa + dip + ya) làm lên ngọn, làm cho cháy thêm. aor. --ppi. pr.p. --pamāna.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padissati','<p class="definition">(pa + dis + ya) thấy được, hiện ra, tỏ ra. aor. --dissi. pp. padiṭṭha. pr.p. --samāna.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paditta','<p class="definition">pp. của padippati.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padma','<p class="definition">như paduma.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padosa','<p class="definition">m. hoàng hôn, sự sân hận, sự không đủ, thiếu, khiếm khuyết.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padubbhati','<p class="definition">(pa + dubh + ya) âm mưu chống lại. aor. --bhi. pp. --bhita. abs. --bhitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paduma','<p class="definition">nt. hoa sen, tên một cảnh địa ngục (nơi để luận tội) và nơi đó có nhiều lắm (không kể xiết). --kāṇṇikā f. vỏ bông sen. --kalāpa m. một bó hoa sen. --gabbha m. trong lòng hoa sen. --patta nt. cánh hoa sen. --rāga m. hộ rubi. --sara m. ao, hồ sen. --minī f. cọng sen. --minīpatta nt. lá sen.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padumī','<p class="definition">a. có những bông sen, có đốm, có vằn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padussana','<p class="definition">nt. sự phạm luật, âm mưu.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padussati','<p class="definition">(pa + dus + ya) làm sái, đồi bại, hư thúi, phạm lỗi. aor. --esi. pp. paduṭṭha abs. --ssitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paduṭṭha','<p class="definition">(pp. của padussati) độc ác, đồi bại, hư thúi.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padāleti','<p class="definition">(pa + dar + e) chẻ, bửa ra, tách ra, mở tung ra. aor. --esi. pp. --ḷita. pr.p. --ḷenta. abs. --ḷetvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padāna','<p class="definition">nt. sự cho, sự tặng.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padātave','<p class="definition">inf. cho, tặng, bố thí.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padātu','<p class="definition">m. người cho, người chia cho.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padāḷana','<p class="definition">nt. sự tách ra, xé ra, sự chẻ, bửa ra (củi).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padāḷetu','<p class="definition">m. người chẻ, bửa ra.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padīpa','<p class="definition">m. cái đèn, ánh sáng. --padippaya nt. nhiên liệu cho ánh sáng. --kāla m. giờ đốt đèn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padīpeti','<p class="definition">(pa + dip + e) thắp đèn, giải nghĩa, làm cho hăng hái. aor. --esi. pp. --pitā. pr.p. --penta. abs. padipetvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padīyati','<p class="definition">(pa + dā + i + ya) bỏ ra, hay là biếu tặng, cho. aor. --padīyi. pp. padinna.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padūdara','<p class="definition">m. con rắn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('padūseti','<p class="definition">(pa + dus + e) làm nhơ bẩn, làm hư thúi, làm tồi bại, xấu xa. aor. --esi. pp. padūsita. abs. --setvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pagabbha','<p class="definition">a. dạn dĩ, dám làm, táo bạo, không lo lắng.</p>',71);
