INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paggaṇhāti','<p class="definition">(pa + gah + ṇhā) đưa lên, lấy lên, nâng đỡ, được ân huệ, nới rộng, giăng ra. aor. --ṇhi. pp. paggahita. pr.p. --hanta. abs. --hetvā, paggagha. pt.p. paggahelabba.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paggharati','<p class="definition">(pa + ghar + a) chảy tới, rỉ ra, nhỏ từ giọt, chảy từ giọt. aor. --ghari. pp. --rita. pr.p ranta. abs. --ritvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paggharaṇa','<p class="definition">nt. sự chảy từng giọt, rỉ chảy ra, nhỏ từ giọt. --ṇaka a. sự chảy, rỉ ra, nhểu từng giọt.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paghaṇa','<p class="definition">m. sân trước nhà có che lợp.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pagiddha','<p class="definition">(pp của pagijjhati) bám vào, thèm khát theo, ham ăn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pagumba','<p class="definition">m. bụi rậm, rừng rậm, dày.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paguṇa','<p class="definition">a. thực hiện tốt đẹp, hiểu biết rành rẽ, quen thuộc, thuộc lòng. --ṇatā f. sự đầy đủ, sung túc.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pagāhati','<p class="definition">(pa + gāh + a) lặn xuống, lún xuống, hụp xuống, phóng, lao xuống. aor. pagāhi. pr.p. pagāhanta. abs. pagāhitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pagāḷha','<p class="definition">pp. của pagāhati.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paharati','<p class="definition">(pa + har + a) đánh đập, gõ. aor. --pahari. pr.p. --ranta. abs. --ritvā. inf. --rituṃ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paharaṇa','<p class="definition">nt. sự đánh đập, chém bằng khí giới. --ṇaka a. đánh đập.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahata','<p class="definition">pp. của paharati.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahaṭṭha','<p class="definition">(pp. của pahaṃsati) vui vẻ, vui mừng quá, thích quá.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paheṇaka','<p class="definition">nt. quà tặng đáng (nên) gởi biếu cho ai đó.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahināti','<p class="definition">(pa + hī + ṇà) gởi đi. aor. pahiṇi. pr.p. --ṇanta. abs. --pahīṇitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahita','<p class="definition">pp. của pahināti.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahiṇagamana','<p class="definition">nt. sự đi sứ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahiṇana','<p class="definition">nt. sự gởi, gởi hàng hóa.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahonaka','<p class="definition">a. đủ rồi, đầy đủ lắm.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahoti','<p class="definition">(pa + hū + a) có thể, đúng, đầy đủ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahāna','<p class="definition">nt. dẹp, dời đi, bỏ đi, từ bỏ, dứt bỏ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahāra','<p class="definition">m. cái đánh, sự thổi, cái đập, gõ. --dāna nt. cho một hơi thổi, xông vào đánh. --ekappa hārena đánh một cái thình lình.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahāsa','<p class="definition">m. vui thích nhất, điều khóai lạc.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahāseti','<p class="definition">(pa + has + e) làm cho cười, làm cho vui vẻ. aor. --esi. pp.--pahāsita.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahāya','<p class="definition">(abs. của pajahati) đã bỏ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahāyī','<p class="definition">3. người đã dứt bỏ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahīna','<p class="definition">(pp. của pajahati) bị bỏ cuộc, từ bỏ, phá hoại.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahīyati','<p class="definition">(pa + hā + ī + ya) biến mất, đã quá vãng, bị từ bỏ. aor. pahīyi. pp. pahiṅa. pr.p. yamāna. abs. --yitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahū','<p class="definition">a. có thể, có khả năng.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pahūta','<p class="definition">a. dồi dào, đầy đủ, nhiều, rộng rãi. --jivha a. có cái lưỡi lớn. --bhakkha a. ăn nhiều quá (có nhiều thức ăn).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pajagghati','<p class="definition">(pa + jaggh + a) cười lớn. aor. --ghi. ger. --ghana.</p>',71);
