INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pajappati','<p class="definition">(pa + japp + a) nói lảm nhảm van xin. aor. --ppi.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pajja','<p class="definition">nt. một câu thi, một bài thi, cái chi làm êm chân. m. con đường đi.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pajjalana','<p class="definition">nt. sự cháy lên ngọn, sáng chói.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pajjalati','<p class="definition">(pa + jal + a) đốt lên ngọn, đốt cho sáng luôn. aor. --jati. pp. --lita. pr.p. --lanta. abs. --litvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pajjhāyati','<p class="definition">(pa + jha + ya) vượt qua sự buồn rầu hay đau khổ, suy xét kỹ lại. aor. --āyi. pr.p --yanta.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pajjota','<p class="definition">m. cây đèn, ánh sáng, bóng láng.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pajjunna','<p class="definition">m. mây mưa, thần làm mưa.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pajā','<p class="definition">f. tổ tiên, dòng giống, con cháu, nhân loại. --pati m. tạo hóa.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pajānanā','<p class="definition">f. sự hiểu biết, học thức, sự minh mẫn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pajāyati','<p class="definition">(pa + jan + ya) đựợc sanh ra hay sản xuất. aor. --payāji. --na nt. sự sanh ra, có sinh mạng.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pakampati','<p class="definition">(pa + kamp + a) run rẩy, rung rinh. aor. --mpi, pp. --mpita. ger. --mpana.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pakappanā','<p class="definition">f. sự có lý, kế hoạch, sự sắp xếp, điều đình.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pakappeti','<p class="definition">(pa + kapp + e) suy nghĩ, điều đình, tính tới tính lui. aor. --esi. pp. --pita. abs. --petvā định cho.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pakaraṇa','<p class="definition">nt. một dịp, một tiếng thuộc về văn chương.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pakata','<p class="definition">m. làm, tạo xong. --atta a. hạnh kiểm tốt, trạng thái mạnh khỏe.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pakati','<p class="definition">f. tự nhiện, thiên nhiên, nguồn gốc, căn nguyên, hình thức. --gamana nt. quen đi bách bộ. --citta nt. tâm bình thường. adj. tâm lành mạnh. --sīla nt. thường giới (giữ giới luôn luôn).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pakatika','<p class="definition">a. được sự tự nhiên.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pakaṭṭha','<p class="definition">a. quí nhất, sang trọng.</p>',71);
