INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('papāta','<p class="definition">m. vực thẳm, dốc cao của một khối đá. --taṭa m. sự dốc xuống.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('para','<p class="definition">a. cái khác, kẻ, vật khác, người ngoại quốc, người bên ngoài. --kata a. do người khác làm. --kāra m. hành vi của kẻ khác. --jana m. người lạ mặt, người ngoài. --attha m. lợi ích của kẻ khác. --dattūpajī vī a. sống do nhờ nơi, sự bố thí của kẻ khác. --neyya a. do người khác dắt dẫn. --paccaya, --pattiya a. nương nhờ người khác. --pessa a. giúp đỡ kẻ khác. --bhāga m. phần người khác, phần phía ngoài. --loka m. cảnh giới khác. --vambhana nt. sự khi dễ kẻ khác. --vāda m. sự bất đồng ý kiến. --vādī 3. m. sự chống đối trong khi thảo luận. --visaya m. ngoại quốc, miền của người khác. --senā f. quân lính, kẻ đich. --hatthagata a. bị quân địch bắt. --hīta m. sự tấn hóa cho kẻ khác. --hetu ad. do bởi kẻ khác.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paradāra','<p class="definition">m. vợ người nào khác.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paradārakamma','<p class="definition">nt. ngoại tình với vợ người khác, gian dâm. --dārika m. người ngoại tình với vợ kẻ khác.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parakkama','<p class="definition">m. --mana nt. cố gắng, ráng sức, siêng năng.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parakkamati','<p class="definition">(parā + kam + a) cố gắng, tỏ ra can đảm. aor. --kami. pp. parakkanta. pr.p. --kamanta. abs. --mitvā, parakkamma.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parama','<p class="definition">a. cao cả, tốt nhất, cao quí nhất, ưu tú. --matā f. một số lượng cao nhất, tột đỉnh. --nāḷiko-danaparamatāya sự thấy của nhà tiên tri cơm gạo là cao quí nhất. --mattha m. quan niệm cao nhất, chân lý tuyệt đối. --māyu nt. sự hạn định của tuổi thọ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parammuka','<p class="definition">a. quay măt đi nơi khác. --khā ad. sự vắng mặt của mình.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paramparā','<p class="definition">f. dòng dõi, gia thống, truyền thống, hệ thống.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paramānu','<p class="definition">m. phần thứ ba mươi sáu của một Anu (vi trần).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paramāsati','<p class="definition">(pari + ā + mas + a) đụng chạm, bám vào, bị dính líu, vuốt ve. aor. --masi. pp. --masita, maṭṭha. pr.p. santa. abs. --masitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parasuve','<p class="definition">ad. ngày mốt.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parato','<p class="definition">in. phía trước, trước hết.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parattha','<p class="definition">in. một nơi khác, từ đây về sau.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paraṃ','<p class="definition">ad. sau, qua khỏi, xa hơn nữa, phía bờ kia (bỉ ngạn). --maraṇā sau khi chết.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pareta','<p class="definition">a. ưu phiền vì, vượt qua bởi, tiếp tục đến.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pari','<p class="definition">(tiếp đầu ngữ có nghĩa trọn vẹn, hoàn toàn), tất cả chung quanh, trọn cả, hoàn toàn, trọn vẹn.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribbajjati','<p class="definition">(pari + vaj + a) đi ta bà, đi bềnh bồng. aor --baji.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribbaya','<p class="definition">m. chi, xài, xài phí, sự chi phí, tiền lương.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribbhamati','<p class="definition">(pari + bham + a) đi loanh quanh, đi lang thang, vơ vẩn. aor. --bhami. pp. --bhanta. pr.p. --bhamanta abs.--bhamitvā. --na nt. sự đi lang thang, đi vơ vẩn, đi loạng choạng.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribbhameti','<p class="definition">(caus. của parib-bhamati) lam cho quay cuồng, loạng choạng. aor. --esi. pp. --bhamita. abs. --bhametvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribbājaka','<p class="definition">m. đạo sĩ thả đi ta bà xin ăn. --jikā f. nữ tu sĩ đi ta bà.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribbūḷha','<p class="definition">pp. bao vây, rào, bao quanh.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhava','<p class="definition">m. --vana nt. lạm dụng, khi dễ, khinh bỉ, phỉ báng, chưởi rủa.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhavati','<p class="definition">(pari + bhū + a) đối đãi,, sựkhinh miệt, khinh dể, phỉ báng, chưởi rủa. aor. --bhavi. pp. --bhūta. pr.p. --bhavanta, --vamāna. abs. --bhavitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhaṇḍa','<p class="definition">m. sự đánh láng sàn nhà, một sự bao vây. --kata a. trét, tô đắp.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhaṭṭha','<p class="definition">pp. rớt xuống, nhểu, bỏ xuống.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhinna','<p class="definition">(pp. của paridhindati) gãy bể, chẻ tách ra, để riêng ra.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhoga','<p class="definition">m. sự dùng xài, sự thưởngthức, sự cho ăn, sự vui hưởng vật chất. --cetaya nt. bảp tháp thờ những vật dụng do đức Phật dùng xài (như y).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhojanīya','<p class="definition">a. đáng cần dùng. --ukaka nt. nước để dùng xài (không uống).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhutta','<p class="definition">pp. như paribhuñjati.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhuñjati','<p class="definition">(pari + bhuj + ṃ + a) ăn, dùng, thưởng thức (món ăn), dùng, thưởng thức (món ăn). aor. --ñji. pp. --bhutta. pr.p. --janta jamāna. abs. --jitvā, --bhutvā. --ñjiya. pt.p. --jitabba.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhāsa','<p class="definition">m. --sana nt. chưởi mắng, quở trách, khiển trách. --saka a. người chưởi mắng, chưởi rủa.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhāsati','<p class="definition">(pari + bhās + a) chưởi mắng, rầy la, làm mất giá trị (tên tuổi). aor. --bhāsi. pp --sita. pr.p. --bhāsamāna. abs. --sitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhāvita','<p class="definition">(pp. của paribhāveti) huấn luyện, thực hành, pha lộn với nuôi, nâng đỡ, hiểu thấu, thấm thía.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribhūta','<p class="definition">pp. của paribhavati.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paribāhira','<p class="definition">a. bên ngoài, lạ, trái với, người lạ, ngoại quốc.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricarati','<p class="definition">(pari + car + a) đi quanh quẩn, săn sóc, chăm nom, làm thỏa mãn giác quan, thọ trì hay thực hiện. aor. --cari. abs. --caritvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricaraṇa','<p class="definition">nt. theo sau, hầu cận, săn sóc, sự vui thích.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricaya','<p class="definition">m. sự thực hành, quyến thuộc, sự quen biết.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricca','<p class="definition">abs. có sự đặc biệt, nhận ra, hiểu biết.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pariccajati','<p class="definition">(pari + caj + a) dứt bỏ, từ bỏ, bỏ lại phía sau, ban cho , để, đặt tiền. aor. --caji. pp. --ccatta. pr.p. --cajanta. abs. --cajitvā. inf. --cajituṃ. --na nt. piriccāga m. dứt bỏ, từ bỏ, sự cho, sự xuất gia.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricchanna','<p class="definition">(pp. của paricchādeti) che đậy, phủ qua, bao bọc quanh.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pariccheda','<p class="definition">m. sự đo lường, ranh giới, sự phân ranh, địa phận, một chương (sách).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricchindana','<p class="definition">nt. sự quyết định, sự làm dấu, sự phân ranh, sự phân tách.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricchindati','<p class="definition">(pari + chid + ṃ + a) làm dấu hiệu, làm ranh, quyết định. aor. --ndi. pp. --chinna. abs. --chindiya, --cchijja.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricchādanā','<p class="definition">f. sự bao trùm, sự che đậy.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pariciṇṇa','<p class="definition">PARICITA (pp. của chữ paricināti) sự thực hành, hầu hạ, sự quen với, thói quen, sự tích trữ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricumbati','<p class="definition">(pari + cub + ṃ + a) ôm hôn, bao ôm. aor. --mbi. pp. --bita. abs. --bitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricāraka','<p class="definition">a. sự giúp đỡ, sự hầu hạ. m. người hầu, người giúp việc.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricāraṇā','<p class="definition">f. sự săn sóc, sự trông nom, sự khoản đãi.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricāreti','<p class="definition">(caus. của paricarati) giúp đỡ, hầu hạ, tự tiêu khiển, giải trí. aor. --esi. pp. --rita. abs. --retvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paricārikā','<p class="definition">f. người hầu gái, người vợ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paridahana','<p class="definition">nt. sự để lên (mình), sự tự mặc quần áo.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paridahati','<p class="definition">(pari + dah + a) để lên (mình), mặc đồ vào, tự mặc y phục. aor. --dahi. pp. --dahita. abs. --dahitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parideva','<p class="definition">m. --vanā f. sự kể lể, sự than khóc.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paridevati','<p class="definition">(pari + dev + a) khóc than, rên rỉ. aor. --devi. pp. --vita. abs. --vitvā. pr.p. --devanta, --devamāna.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paridhaṃsaka','<p class="definition">a. sự tàn phá, sựsụp đổ, người nói cho tiêu diệt, hư hại.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paridhota','<p class="definition">pp. của paridhovati.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paridhovati','<p class="definition">(pari + dhov + a) rửa sạch sẽ chung quanh, làm cho sạch sẽ. aor. --dhovi.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paridhāvati','<p class="definition">(pari + dhāv + a) chạy theo sau. aor. --dhāvī. pp. --vita. abs. --vitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paridīpaka','<p class="definition">a. giải nghĩa, làm cho sáng tỏ, giải minh (cho một vấn đề).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paridīpana','<p class="definition">nt. --nā f. sự cắt nghĩa, sự thí dụ, sự thuyết minh.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('paridīpeti','<p class="definition">(pari + dip + e) làm cho rõ rệt, giải thích, làm cho sáng tỏ. aor. --esi. pp. --sita. abs. --setvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parigaṇhana','<p class="definition">nt. sự sưu tầm, sự hiểu biết, sự bao hàm.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parigaṇhāti','<p class="definition">(pari + gah + ṇā) xem xét, tìm kiếm, hiểu biết, thám hiểm, nhận lãnh (vật sở hữu). aor. --gaṇhi. pp. --ggahita. pr.p. --gaṇhanta. abs. --ganhitvā, --gahetvā, --ggayha.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pariggaha','<p class="definition">m. sự nhận lấy, vật sở hữu, sự bám níu vào, những vật sở hữu, vợ, sự cần thiết.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pariggahita','<p class="definition">(pp. của pariganhāti) nhận lấy vật sở hữu, làm chủ, chiếm cứ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parigilati','<p class="definition">(pari + gil + a) nuốt vào. aor. --gili. pp. --gilita. abs.--gilitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parigūhati','<p class="definition">(pari + guh + a) giấu, che, giấu cất. aor --gūhi. ph. --gūhita, --gūṭha. abs. --gūhitvā, --gūhiya.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pariharati','<p class="definition">(pari + har + a) gìn giữ, bảo vệ, đem đi, tránh xa. aor. --hari. pp. --harita, --haṭa. abs. --haritvā. pt.p. --paritabba.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pariharaṇa','<p class="definition">nt. nā f. sự tiếp tục gìn giữ, bảo vệ, sự chú ý đến.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parihassati','<p class="definition">(pari + has + a) kiêu ngạo, chế nhạo, cười nhạo. aor. --hasi. abs. --sitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parihāni','<p class="definition">f. sự mất, suy đồi, sụp đổ, sự hao mòn lần. --niya a. làm cho mất mát, suy đồi.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parihāpeti','<p class="definition">(pari + hā + āpe) làm cho suy nhược, đem lại sự sụp đổ, bỏ lơ, bỏ quên, quên lãng. aor. --esi. pp. --hāpita. abs. --petvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parihāra','<p class="definition">m. sự chăm nom, sự chú ý, sự bảo vệ, sự xa lánh, giá trị, phẩm hạnh. --raka a. sự bảo vệ, sự trông nom. --patha m. đường đi chung quanh, đường vòng tròn. --rika a. nâng đỡ, gìn giữ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parihāsa','<p class="definition">m. sự cười chê, chế nhạo.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parihāyati','<p class="definition">(pari + hā + ya) làm suy nhược, làm cho hao mòn, rớt ra khỏi. aor. --hāyi. pp. parihīna. pr.p. --hayamāna. abs. --hāyitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parihīna','<p class="definition">(pp. của parithāyati) rớt ra khỏi, thiếu thốn, không có, bần cùng, làm gầy ốm.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parijana','<p class="definition">m. người tùy tùng, người theo sau, người hầu hạ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parijjiṇṇa','<p class="definition">(pp. của parijīyati) hao mòn, già nua, hư cũ.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parijānana','<p class="definition">nt. --nā f. sự hiểu biết, sự nhận thức.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parijānāti','<p class="definition">(pari + ñā + nā) hiểu biết chắc chắn, rõ rệt, thông thạo. aor. --jāni. pp. pariññāta. pr.p. --jānanta. abs. --jānitvā, pariññāya.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parikamma','<p class="definition">nt. sự sắp xếp thứ tự, sửa soạn, chuẩn bị, sự khởi đầu. --kata a. trét, tô với. --kāraka 3. người sửa soạn chuẩn bị làm một việc gì.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parikantati','<p class="definition">(pari + kant + a) mổ ra, cắt ngang. aor. --nti, --ntita. abs. --ntitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parikappa','<p class="definition">m. sựcố ý, sự chú ý, giả thuyết, sự lập luận.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parikappeti','<p class="definition">(pari + kap + e) cố ý, tỷ dụ, lập luận, ức đoán. aor. --esi. pp. --pita. abs. --petvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parikassati','<p class="definition">(pari + kas + a) kéo lôi, quét đi, thối lui. aor. --esi. pp. --sita.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parikathā','<p class="definition">f. sự trình bày, triển lãm, sự giới thiệu, sự nói tới lui (để quảng cáo).</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parikaḍḍhana','<p class="definition">nt. kéo qua, sự lôi kéo.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parikaḍḍhati','<p class="definition">(pari + kaṇṇh + a) kéo ngang qua hay kéo vô mình, lôi kéo. aor. --ṇhi. pp. --ṇhita. abs.--ṇhitvā.</p>',71);
INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parikhaṇati','<p class="definition">paḷikha ṇati (pari + khan + a) đào chung quanh. aor. --khaṇi. pp. --khata. abs. --ṇitvā.</p>',71);
