INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('parikitteti','<p class="definition">(pari + kitt + e) giải thích tán dương, khen ngợi, làm cho rõ ra. aor. --esi. pp. --kittita.</p>',71);
