INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('pubba','<p class="definition">a. trước, cựu, trước hết, hướng đông. in. cpds. có lúc trước, như gatapubba, đã đi trước. --kamma nt. nghiệp từ kiếp trước. --kicca nt. công việc trước hết. --ṅgaman a. đi trước, dẫn đầu. --carita nt. chuyện kiếp trước. --aṇha m. trước buổi ngọ. --deva m. những chư thiên trước kia là Asuras (atula). --nimitta nt. triệu chứng, điềm báo trước. --anta m. quá khứ, thời điểm cuối cùng của trước kia --purisa m. tổ tiên, ông bà trứơc --peta m. ngạ quỉ (hồn người đã quá vãng). --bhāga m. phần trước. adj. trước hết. --yoga m. sự liên quan với trước kia. --videha m. tên một châu ở hướnng đông là Đông thắng thần châu.</p>',71);
