INSERT INTO "dictionary" ("word","definition","book_id") VALUES ('rāja','<p class="definition">m. đức Vua. --kakudhabhaṇda nt. huy hiệu của hoàng tộc, biểu chương của vua. --kathā f. sự nói về đức Vua. --kammika m. quan chức, công chức của chính phủ. --kūmara m. hoàng tử. --kumārī, --kaññā f. công chúa. --kula nt. hoàng tộc, cung điện nhà vua, --geha, --bhavana. --mandira nt. đền vua. --ṅgaṇa nt. sân chầu trước ngai vua. --daṇda m. hình phạt của nhà vua ban ra. --dāya m. sự ban thưởng của triều đình. --dūta m. sứ thần của vua, khâm sai. --devī f. cung tần của vua hay vợ vua. --dhamma m. phận sự của vua. --dhāni f. kinh đô. --dhīta, --putta f. con gái vua, công chúa. --nivesana nt. chỗ vua ngự. --ntepura nt. khuê phòng của vua, hoàng triều cương thổ. --parisā f. quần thần của vua, chính phủ. --puttī m. hoàng tử. --purisa m. công chức, chức tước trong hoàng triều. --bali m. thuế phải nộp cho vua. --bhaṭa m. binh lính. --porisa nt. công chức của chính phủ. --bhaya nt. sợ từ đức vua. --bhogga a. đáng được đức vua dùng. --mahāmatta m. thủ tướng đại thần. --mahesī f. hoàng hậu. --muddā f. ấn vua. --ratha m. long xa, xe vua. --vara m. đức vua cao quí. --vallabha a. thân thiện với vua, được đức vua ưa chuộng. --sampatti f. sự huy hoàng của vua.</p>',71);
