INSERT INTO pages VALUES(3040,'mula_ma_01',1,replace(replace('<p class="centered"> namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="nikaya">majjhimanikāyo</p>\r\n<p class="book">mūlapaṇṇāsapāḷi</p>\r\n<p class="chapter">1. mūlapariyāyavaggo</p>\r\n<p class="subhead">1. mūlapariyāyasuttaṃ</p>\r\n<p class="bodytext"><a name="para1"></a><a name="para1_mn1"></a><span class="paranum">1</span>. evaṃ <a name="M1.0001"></a><a name="V1.0001"></a><a name="P1.0001"></a><a name="T1.0001"></a> me sutaṃ – ekaṃ samayaṃ bhagavā ukkaṭṭhāyaṃ viharati subhagavane sālarājamūle. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca – "sabbadhammamūlapariyāyaṃ vo, bhikkhave, desessāmi. taṃ suṇātha, sādhukaṃ manasi karotha, bhāsissāmī"ti. "evaṃ, bhante"ti kho te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext"><a name="para2"></a><a name="para2_mn1"></a><span class="paranum">2</span>. idha, bhikkhave, assutavā puthujjano ariyānaṃ adassāvī ariyadhammassa akovido <a name="V1.0002"></a> ariyadhamme avinīto, sappurisānaṃ adassāvī sappurisadhammassa akovido sappurisadhamme avinīto – pathaviṃ <span class="note">[paṭhaviṃ (sī. syā. kaṃ. pī.)]</span> pathavito sañjānāti, pathaviṃ pathavito saññatvā pathaviṃ maññati, pathaviyā maññati, pathavito maññati, pathaviṃ meti maññati<a name="T1.0002"></a>, pathaviṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">āpaṃ āpato sañjānāti, āpaṃ āpato saññatvā āpaṃ maññati, āpasmiṃ maññati, āpato maññati, āpaṃ meti maññati, āpaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n','\r',char(13)),'\n',char(10)),'-1-2-');
INSERT INTO pages VALUES(3041,'mula_ma_01',2,replace(replace('<p class="bodytext">tejaṃ <a name="M1.0002"></a> tejato sañjānāti, tejaṃ tejato saññatvā tejaṃ maññati, tejasmiṃ maññati, tejato maññati, tejaṃ meti maññati, tejaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">vāyaṃ vāyato sañjānāti, vāyaṃ vāyato saññatvā vāyaṃ maññati, vāyasmiṃ maññati, vāyato maññati, vāyaṃ meti maññati, vāyaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti <a name="P1.0002"></a> vadāmi.</p>\r\n<p class="bodytext"><a name="para3"></a><a name="para3_mn1"></a><span class="paranum">3</span>. bhūte bhūtato sañjānāti, bhūte bhūtato saññatvā bhūte maññati, bhūtesu maññati, bhūtato maññati, bhūte meti maññati, bhūte abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">deve devato sañjānāti, deve devato saññatvā deve maññati, devesu maññati, devato maññati, deve meti maññati, deve abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">pajāpatiṃ pajāpatito sañjānāti, pajāpatiṃ pajāpatito saññatvā pajāpatiṃ maññati, pajāpatismiṃ maññati, pajāpatito maññati, pajāpatiṃ meti maññati, pajāpatiṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">brahmaṃ <a name="V1.0003"></a> brahmato sañjānāti, brahmaṃ brahmato saññatvā brahmaṃ maññati<a name="T1.0003"></a>, brahmasmiṃ maññati, brahmato maññati, brahmaṃ meti maññati, brahmaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">ābhassare ābhassarato sañjānāti, ābhassare ābhassarato saññatvā ābhassare maññati, ābhassaresu maññati, ābhassarato maññati, ābhassare meti maññati, ābhassare abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">subhakiṇhe subhakiṇhato sañjānāti, subhakiṇhe subhakiṇhato saññatvā subhakiṇhe maññati, subhakiṇhesu maññati, subhakiṇhato maññati, subhakiṇhe meti maññati, subhakiṇhe abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages VALUES(3042,'mula_ma_01',3,replace(replace('<p class="bodytext">vehapphale <a name="M1.0003"></a> vehapphalato sañjānāti, vehapphale vehapphalato saññatvā vehapphale maññati, vehapphalesu maññati, vehapphalato maññati, vehapphale meti maññati, vehapphale abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">abhibhuṃ abhibhūto sañjānāti, abhibhuṃ abhibhūto saññatvā abhibhuṃ maññati, abhibhusmiṃ maññati, abhibhūto maññati, abhibhuṃ meti maññati, abhibhuṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext"><a name="para4"></a><a name="para4_mn1"></a><span class="paranum">4</span>. ākāsānañcāyatanaṃ ākāsānañcāyatanato sañjānāti, ākāsānañcāyatanaṃ ākāsānañcāyatanato saññatvā ākāsānañcāyatanaṃ maññati, ākāsānañcāyatanasmiṃ maññati, ākāsānañcāyatanato maññati, ākāsānañcāyatanaṃ meti maññati, ākāsānañcāyatanaṃ abhinandati. taṃ <a name="T1.0004"></a> kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">viññāṇañcāyatanaṃ viññāṇañcāyatanato sañjānāti, viññāṇañcāyatanaṃ viññāṇañcāyatanato saññatvā viññāṇañcāyatanaṃ <a name="P1.0003"></a> maññati, viññāṇañcāyatanasmiṃ maññati, viññāṇañcāyatanato <a name="V1.0004"></a> maññati, viññāṇañcāyatanaṃ meti maññati, viññāṇañcāyatanaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">ākiñcaññāyatanaṃ ākiñcaññāyatanato sañjānāti, ākiñcaññāyatanaṃ ākiñcaññāyatanato saññatvā ākiñcaññāyatanaṃ maññati, ākiñcaññāyatanasmiṃ maññati, ākiñcaññāyatanato maññati, ākiñcaññāyatanaṃ meti maññati, ākiñcaññāyatanaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">nevasaññānāsaññāyatanaṃ nevasaññānāsaññāyatanato sañjānāti, nevasaññānāsaññāyatanaṃ nevasaññānāsaññāyatanato saññatvā nevasaññānāsaññāyatanaṃ maññati, nevasaññānāsaññāyatanasmiṃ maññati, nevasaññānāsaññāyatanato maññati, nevasaññānāsaññāyatanaṃ meti maññati, nevasaññānāsaññāyatanaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages VALUES(3043,'mula_ma_01',4,replace(replace('<p class="bodytext"><a name="para5"></a><a name="para5_mn1"></a><span class="paranum">5</span>. diṭṭhaṃ <a name="M1.0004"></a> diṭṭhato sañjānāti, diṭṭhaṃ diṭṭhato saññatvā diṭṭhaṃ maññati, diṭṭhasmiṃ maññati, diṭṭhato maññati, diṭṭhaṃ meti maññati, diṭṭhaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">sutaṃ sutato sañjānāti, sutaṃ sutato saññatvā sutaṃ <a name="T1.0005"></a> maññati, sutasmiṃ maññati, sutato maññati, sutaṃ meti maññati, sutaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">mutaṃ mutato sañjānāti, mutaṃ mutato saññatvā mutaṃ maññati, mutasmiṃ maññati, mutato maññati, mutaṃ meti maññati, mutaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">viññātaṃ viññātato sañjānāti, viññātaṃ viññātato saññatvā viññātaṃ maññati, viññātasmiṃ maññati, viññātato maññati, viññātaṃ meti maññati, viññātaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext"><a name="para6"></a><a name="para6_mn1"></a><span class="paranum">6</span>. ekattaṃ <a name="V1.0005"></a> ekattato sañjānāti, ekattaṃ ekattato saññatvā ekattaṃ maññati, ekattasmiṃ maññati, ekattato maññati, ekattaṃ meti maññati, ekattaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">nānattaṃ nānattato sañjānāti, nānattaṃ nānattato saññatvā nānattaṃ maññati, nānattasmiṃ maññati, nānattato maññati, nānattaṃ meti maññati, nānattaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">sabbaṃ sabbato sañjānāti, sabbaṃ sabbato saññatvā sabbaṃ maññati, sabbasmiṃ <a name="P1.0004"></a> maññati, sabbato maññati, sabbaṃ meti maññati, sabbaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">nibbānaṃ nibbānato sañjānāti, nibbānaṃ nibbānato saññatvā nibbānaṃ maññati, nibbānasmiṃ maññati<a name="T1.0006"></a>, nibbānato maññati, nibbānaṃ meti maññati, nibbānaṃ abhinandati. taṃ kissa hetu? ‘apariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="centered">puthujjanavasena paṭhamanayabhūmiparicchedo niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'-5-6-');
INSERT INTO pages VALUES(3044,'mula_ma_01',5,replace(replace('<p class="bodytext"><a name="para7"></a><a name="para7_mn1"></a><span class="paranum">7</span>. yopi <a name="M1.0005"></a> so, bhikkhave, bhikkhu sekkho <span class="note">[sekho (sī. syā. kaṃ. pī.)]</span> appattamānaso anuttaraṃ yogakkhemaṃ patthayamāno viharati, sopi pathaviṃ pathavito abhijānāti, pathaviṃ pathavito abhiññāya <span class="note">[abhiññatvā (ka.)]</span> pathaviṃ mā maññi <span class="note">[vā maññati]</span>, pathaviyā mā maññi, pathavito mā maññi, pathaviṃ meti mā maññi, pathaviṃ mābhinandi <span class="note">[vā abhinandati (sī.) ṭīkā oloketabbā]</span>. taṃ kissa hetu? ‘pariññeyyaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">āpaṃ…pe… tejaṃ… vāyaṃ… bhūte… deve… pajāpatiṃ… brahmaṃ… ābhassare… subhakiṇhe… vehapphale… abhibhuṃ… ākāsānañcāyatanaṃ… viññāṇañcāyatanaṃ… ākiñcaññāyatanaṃ… nevasaññānāsaññāyatanaṃ… diṭṭhaṃ… sutaṃ… mutaṃ… viññātaṃ… ekattaṃ… nānattaṃ… sabbaṃ… nibbānaṃ nibbānato abhijānāti, nibbānaṃ nibbānato abhiññāya <a name="V1.0006"></a> nibbānaṃ mā maññi, nibbānasmiṃ mā maññi, nibbānato mā maññi, nibbānaṃ meti mā maññi, nibbānaṃ mābhinandi. taṃ kissa hetu? ‘pariññeyyaṃ tassā’ti vadāmi.</p>\r\n<p class="centered">sekkhavasena <span class="note">[satthāravasena (sī.), satthuvasena (syā. ka.)]</span> dutiyanayabhūmiparicchedo niṭṭhito.</p>\r\n<p class="bodytext"><a name="para8"></a><a name="para8_mn1"></a><span class="paranum">8</span>. yopi so, bhikkhave, bhikkhu arahaṃ khīṇāsavo vusitavā katakaraṇīyo ohitabhāro anuppattasadattho parikkhīṇabhavasaṃyojano sammadaññā vimutto, sopi pathaviṃ pathavito abhijānāti, pathaviṃ pathavito <a name="T1.0007"></a> abhiññāya pathaviṃ na maññati, pathaviyā na maññati, pathavito na maññati, pathaviṃ meti na maññati, pathaviṃ nābhinandati. taṃ kissa hetu? ‘pariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="bodytext">āpaṃ…pe… tejaṃ… vāyaṃ… bhūte… deve… pajāpatiṃ… brahmaṃ… ābhassare… subhakiṇhe… vehapphale… abhibhuṃ… ākāsānañcāyatanaṃ… viññāṇañcāyatanaṃ… ākiñcaññāyatanaṃ… nevasaññānāsaññāyatanaṃ… diṭṭhaṃ… sutaṃ… mutaṃ… viññātaṃ… ekattaṃ… nānattaṃ… sabbaṃ… nibbānaṃ nibbānato abhijānāti, nibbānaṃ nibbānato abhiññāya nibbānaṃ na maññati, nibbānasmiṃ na maññati, nibbānato na maññati, nibbānaṃ meti na maññati, nibbānaṃ nābhinandati. taṃ kissa hetu? ‘pariññātaṃ tassā’ti vadāmi.</p>\r\n<p class="centered">khīṇāsavavasena tatiyanayabhūmiparicchedo niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'-7-8-');
INSERT INTO pages VALUES(3045,'mula_ma_01',6,replace(replace('<p class="bodytext"><a name="para9"></a><a name="para9_mn1"></a><span class="paranum">9</span>. yopi <a name="M1.0006"></a> so, bhikkhave, bhikkhu arahaṃ khīṇāsavo vusitavā katakaraṇīyo ohitabhāro anuppattasadattho parikkhīṇabhavasaṃyojano sammadaññā vimutto, sopi pathaviṃ pathavito <a name="P1.0005"></a> abhijānāti, pathaviṃ pathavito abhiññāya pathaviṃ na maññati, pathaviyā na maññati, pathavito na maññati, pathaviṃ meti na maññati, pathaviṃ nābhinandati. taṃ kissa hetu? khayā rāgassa, vītarāgattā.</p>\r\n<p class="bodytext">āpaṃ…pe… tejaṃ… vāyaṃ… bhūte… deve… pajāpatiṃ… brahmaṃ… ābhassare… subhakiṇhe… vehapphale… abhibhuṃ… ākāsānañcāyatanaṃ… viññāṇañcāyatanaṃ… ākiñcaññāyatanaṃ <a name="V1.0007"></a>… nevasaññānāsaññāyatanaṃ <a name="T1.0008"></a>… diṭṭhaṃ… sutaṃ… mutaṃ… viññātaṃ… ekattaṃ… nānattaṃ… sabbaṃ… nibbānaṃ nibbānato abhijānāti, nibbānaṃ nibbānato abhiññāya nibbānaṃ na maññati, nibbānasmiṃ na maññati, nibbānato na maññati, nibbānaṃ meti na maññati, nibbānaṃ nābhinandati. taṃ kissa hetu? khayā rāgassa, vītarāgattā.</p>\r\n<p class="centered">khīṇāsavavasena catutthanayabhūmiparicchedo niṭṭhito.</p>\r\n<p class="bodytext"><a name="para10"></a><a name="para10_mn1"></a><span class="paranum">10</span>. yopi so, bhikkhave, bhikkhu arahaṃ khīṇāsavo vusitavā katakaraṇīyo ohitabhāro anuppattasadattho parikkhīṇabhavasaṃyojano sammadaññā vimutto, sopi pathaviṃ pathavito abhijānāti, pathaviṃ pathavito abhiññāya pathaviṃ na maññati, pathaviyā na maññati, pathavito na maññati, pathaviṃ meti na maññati, pathaviṃ nābhinandati. taṃ kissa hetu? khayā dosassa, vītadosattā.</p>\r\n<p class="bodytext">āpaṃ…pe… tejaṃ… vāyaṃ… bhūte… deve… pajāpatiṃ… brahmaṃ… ābhassare… subhakiṇhe… vehapphale… abhibhuṃ… ākāsānañcāyatanaṃ… viññāṇañcāyatanaṃ… ākiñcaññāyatanaṃ… nevasaññānāsaññāyatanaṃ… diṭṭhaṃ… sutaṃ… mutaṃ… viññātaṃ… ekattaṃ… nānattaṃ… sabbaṃ… nibbānaṃ nibbānato abhijānāti, nibbānaṃ nibbānato abhiññāya nibbānaṃ na maññati, nibbānasmiṃ na maññati, nibbānato na maññati, nibbānaṃ meti na maññati, nibbānaṃ nābhinandati. taṃ kissa hetu? khayā dosassa, vītadosattā.</p>\r\n<p class="centered">khīṇāsavavasena pañcamanayabhūmiparicchedo niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'-9-10-');
INSERT INTO pages VALUES(3046,'mula_ma_01',7,replace(replace('<p class="bodytext"><a name="para11"></a><a name="para11_mn1"></a><span class="paranum">11</span>. yopi <a name="T1.0009"></a><a name="M1.0007"></a> so, bhikkhave, bhikkhu arahaṃ khīṇāsavo vusitavā katakaraṇīyo ohitabhāro anuppattasadattho parikkhīṇabhavasaṃyojano sammadaññā vimutto, sopi pathaviṃ pathavito abhijānāti, pathaviṃ pathavito abhiññāya pathaviṃ na maññati, pathaviyā na maññati, pathavito na maññati, pathaviṃ meti na maññati, pathaviṃ nābhinandati. taṃ kissa hetu? khayā mohassa, vītamohattā.</p>\r\n<p class="bodytext">āpaṃ…pe… tejaṃ… vāyaṃ… bhūte… deve… pajāpatiṃ… brahmaṃ… ābhassare… subhakiṇhe… vehapphale… abhibhuṃ… ākāsānañcāyatanaṃ… viññāṇañcāyatanaṃ… ākiñcaññāyatanaṃ <a name="V1.0008"></a>… nevasaññānāsaññāyatanaṃ… diṭṭhaṃ… sutaṃ… mutaṃ… viññātaṃ… ekattaṃ… nānattaṃ… sabbaṃ… nibbānaṃ nibbānato abhijānāti, nibbānaṃ nibbānato abhiññāya nibbānaṃ na maññati, nibbānasmiṃ na maññati, nibbānato na maññati, nibbānaṃ meti na maññati, nibbānaṃ nābhinandati. taṃ kissa hetu? khayā mohassa, vītamohattā.</p>\r\n<p class="centered">khīṇāsavavasena chaṭṭhanayabhūmiparicchedo niṭṭhito.</p>\r\n<p class="bodytext"><a name="para12"></a><a name="para12_mn1"></a><span class="paranum">12</span>. tathāgatopi, bhikkhave, arahaṃ sammāsambuddho pathaviṃ pathavito abhijānāti, pathaviṃ pathavito abhiññāya pathaviṃ na maññati, pathaviyā na maññati, pathavito na maññati, pathaviṃ meti na maññati, pathaviṃ nābhinandati<a name="P1.0006"></a>. taṃ kissa hetu? ‘pariññātantaṃ tathāgatassā’ti vadāmi.</p>\r\n<p class="bodytext">āpaṃ…pe… tejaṃ… vāyaṃ… bhūte… deve… pajāpatiṃ… brahmaṃ… ābhassare… subhakiṇhe… vehapphale… abhibhuṃ… ākāsānañcāyatanaṃ… viññāṇañcāyatanaṃ <a name="T1.0010"></a>… ākiñcaññāyatanaṃ… nevasaññānāsaññāyatanaṃ… diṭṭhaṃ… sutaṃ… mutaṃ… viññātaṃ… ekattaṃ… nānattaṃ… sabbaṃ… nibbānaṃ nibbānato abhijānāti, nibbānaṃ nibbānato abhiññāya nibbānaṃ na maññati, nibbānasmiṃ na maññati, nibbānato na maññati, nibbānaṃ meti na maññati, nibbānaṃ nābhinandati. taṃ kissa hetu? ‘pariññātantaṃ tathāgatassā’ti vadāmi.</p>\r\n<p class="centered">tathāgatavasena sattamanayabhūmiparicchedo niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'-11-12-');
INSERT INTO pages VALUES(3047,'mula_ma_01',8,replace(replace('<p class="bodytext"><a name="para13"></a><a name="para13_mn1"></a><span class="paranum">13</span>. tathāgatopi<a name="M1.0008"></a>, bhikkhave, arahaṃ sammāsambuddho pathaviṃ pathavito abhijānāti, pathaviṃ pathavito abhiññāya pathaviṃ na maññati, pathaviyā na maññati, pathavito na maññati, pathaviṃ meti na maññati, pathaviṃ nābhinandati. taṃ kissa hetu? ‘nandī <span class="note">[nandi (sī. syā.)]</span> dukkhassa mūla’nti – iti viditvā ‘bhavā jāti bhūtassa jarāmaraṇa’nti. tasmātiha, bhikkhave, ‘tathāgato sabbaso taṇhānaṃ khayā virāgā nirodhā cāgā paṭinissaggā anuttaraṃ sammāsambodhiṃ abhisambuddho’ti vadāmi.</p>\r\n<p class="bodytext">āpaṃ <a name="V1.0009"></a> …pe… tejaṃ… vāyaṃ… bhūte… deve… pajāpatiṃ… brahmaṃ… ābhassare… subhakiṇhe… vehapphale… abhibhuṃ… ākāsānañcāyatanaṃ… viññāṇañcāyatanaṃ… ākiñcaññāyatanaṃ… nevasaññānāsaññāyatanaṃ… diṭṭhaṃ… sutaṃ… mutaṃ… viññātaṃ… ekattaṃ… nānattaṃ… sabbaṃ… nibbānaṃ nibbānato abhijānāti, nibbānaṃ nibbānato abhiññāya nibbānaṃ na maññati, nibbānasmiṃ na maññati, nibbānato na maññati, nibbānaṃ meti na maññati, nibbānaṃ nābhinandati. taṃ <a name="T1.0011"></a> kissa hetu? ‘nandī dukkhassa mūla’nti – iti viditvā ‘bhavā jāti bhūtassa jarāmaraṇa’nti. tasmātiha, bhikkhave, ‘tathāgato sabbaso taṇhānaṃ khayā virāgā nirodhā cāgā paṭinissaggā anuttaraṃ sammāsambodhiṃ abhisambuddho’ti vadāmī"ti.</p>\r\n<p class="centered">tathāgatavasena aṭṭhamanayabhūmiparicchedo niṭṭhito.</p>\r\n<p class="bodytext">idamavoca bhagavā. na te bhikkhū <span class="note">[na attamanā tebhikkhū (syā.), te bhikkhū (pī. ka.)]</span> bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">mūlapariyāyasuttaṃ niṭṭhitaṃ paṭhamaṃ.</p>\r\n<p class="subhead">2. sabbāsavasuttaṃ</p>\r\n<p class="bodytext"><a name="para14"></a><a name="para14_mn1"></a><span class="paranum">14</span>. evaṃ <a name="T1.0012"></a><a name="V1.0010"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca – "sabbāsavasaṃvarapariyāyaṃ vo, bhikkhave, desessāmi. taṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-13-14-');
INSERT INTO pages VALUES(3048,'mula_ma_01',9,replace(replace('<p class="bodytext">suṇātha<a name="M1.0009"></a><a name="P1.0007"></a>, sādhukaṃ manasi karotha, bhāsissāmī"ti. "evaṃ, bhante"ti kho te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext"><a name="para15"></a><a name="para15_mn1"></a><span class="paranum">15</span>. jānato ahaṃ, bhikkhave, passato āsavānaṃ khayaṃ vadāmi, no ajānato no apassato. kiñca, bhikkhave, jānato kiñca passato āsavānaṃ khayaṃ vadāmi? yoniso ca manasikāraṃ ayoniso ca manasikāraṃ. ayoniso, bhikkhave, manasikaroto anuppannā ceva āsavā uppajjanti, uppannā ca āsavā pavaḍḍhanti, yoniso ca kho, bhikkhave, manasikaroto anuppannā ceva āsavā na uppajjanti, uppannā ca āsavā pahīyanti.</p>\r\n<p class="bodytext"><a name="para16"></a><a name="para16_mn1"></a><span class="paranum">16</span>. atthi, bhikkhave, āsavā dassanā pahātabbā, atthi āsavā saṃvarā pahātabbā, atthi āsavā paṭisevanā pahātabbā, atthi āsavā adhivāsanā pahātabbā, atthi āsavā parivajjanā pahātabbā, atthi āsavā vinodanā pahātabbā, atthi āsavā bhāvanā pahātabbā.</p>\r\n<p class="subhead">dassanā pahātabbāsavā</p>\r\n<p class="bodytext"><a name="para17"></a><a name="para17_mn1"></a><span class="paranum">17</span>. katame ca, bhikkhave, āsavā dassanā pahātabbā? idha, bhikkhave<a name="T1.0013"></a>, assutavā puthujjano – ariyānaṃ adassāvī ariyadhammassa akovido ariyadhamme avinīto, sappurisānaṃ adassāvī sappurisadhammassa akovido sappurisadhamme avinīto – manasikaraṇīye dhamme nappajānāti, amanasikaraṇīye dhamme nappajānāti. so manasikaraṇīye dhamme appajānanto amanasikaraṇīye dhamme appajānanto, ye dhammā na manasikaraṇīyā, te dhamme manasi karoti, ye dhammā manasikaraṇīyā te dhamme na manasi karoti.</p>\r\n<p class="bodytext">katame <a name="V1.0011"></a> ca, bhikkhave, dhammā na manasikaraṇīyā ye dhamme manasi karoti? yassa, bhikkhave, dhamme manasikaroto anuppanno vā kāmāsavo uppajjati, uppanno vā kāmāsavo pavaḍḍhati, anuppanno vā bhavāsavo uppajjati, uppanno vā bhavāsavo pavaḍḍhati, anuppanno vā avijjāsavo uppajjati, uppanno vā avijjāsavo pavaḍḍhati – ime dhammā na manasikaraṇīyā ye dhamme manasi karoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-15-16-17-');
INSERT INTO pages VALUES(3049,'mula_ma_01',10,replace(replace('<p class="bodytext">katame <a name="M1.0010"></a> ca, bhikkhave, dhammā manasikaraṇīyā ye dhamme na manasi karoti? yassa, bhikkhave, dhamme manasikaroto anuppanno vā kāmāsavo na uppajjati, uppanno vā kāmāsavo pahīyati, anuppanno vā bhavāsavo na uppajjati, uppanno vā bhavāsavo pahīyati, anuppanno vā avijjāsavo na uppajjati, uppanno vā avijjāsavo pahīyati – ime dhammā manasikaraṇīyā ye dhamme na manasi karoti.</p>\r\n<p class="bodytext">tassa amanasikaraṇīyānaṃ <a name="P1.0008"></a> dhammānaṃ manasikārā manasikaraṇīyānaṃ dhammānaṃ amanasikārā anuppannā ceva āsavā uppajjanti uppannā ca āsavā <a name="T1.0014"></a> pavaḍḍhanti.</p>\r\n<p class="bodytext"><a name="para18"></a><a name="para18_mn1"></a><span class="paranum">18</span>. so evaṃ ayoniso manasi karoti – ‘ahosiṃ nu kho ahaṃ atītamaddhānaṃ? na nu kho ahosiṃ atītamaddhānaṃ? kiṃ nu kho ahosiṃ atītamaddhānaṃ? kathaṃ nu kho ahosiṃ atītamaddhānaṃ? kiṃ hutvā kiṃ ahosiṃ nu kho ahaṃ atītamaddhānaṃ? bhavissāmi nu kho ahaṃ anāgatamaddhānaṃ? na nu kho bhavissāmi anāgatamaddhānaṃ? kiṃ nu kho bhavissāmi anāgatamaddhānaṃ? kathaṃ nu kho bhavissāmi anāgatamaddhānaṃ? kiṃ hutvā kiṃ bhavissāmi nu kho ahaṃ anāgatamaddhāna’nti? etarahi vā paccuppannamaddhānaṃ <span class="note">[paccuppannamaddhānaṃ ārabbha (syā.)]</span> ajjhattaṃ kathaṃkathī hoti – ‘ahaṃ nu khosmi? no nu khosmi? kiṃ nu khosmi? kathaṃ nu khosmi? ayaṃ nu kho satto kuto āgato? so kuhiṃ gāmī bhavissatī’ti?</p>\r\n<p class="bodytext"><a name="para19"></a><a name="para19_mn1"></a><span class="paranum">19</span>. tassa evaṃ ayoniso manasikaroto channaṃ diṭṭhīnaṃ aññatarā diṭṭhi uppajjati. ‘atthi me attā’ti vā assa <span class="note">[vāssa (sī. syā. pī.)]</span> saccato thetato diṭṭhi uppajjati, ‘natthi me attā’ti vā assa saccato thetato diṭṭhi uppajjati, ‘attanāva attānaṃ sañjānāmī’ti vā assa saccato thetato <a name="V1.0012"></a> diṭṭhi uppajjati, ‘attanāva anattānaṃ sañjānāmī’ti vā assa saccato thetato diṭṭhi uppajjati, ‘anattanāva attānaṃ sañjānāmī’ti vā assa saccato thetato diṭṭhi uppajjati, atha vā panassa evaṃ diṭṭhi hoti – ‘yo me ayaṃ attā vado vedeyyo tatra tatra kalyāṇapāpakānaṃ kammānaṃ vipākaṃ paṭisaṃvedeti so kho pana me ayaṃ attā nicco dhuvo sassato</p>\r\n','\r',char(13)),'\n',char(10)),'-18-19-');
INSERT INTO pages VALUES(3050,'mula_ma_01',11,replace(replace('<p class="bodytext">avipariṇāmadhammo <a name="M1.0011"></a> sassatisamaṃ tatheva ṭhassatī’ti. idaṃ vuccati, bhikkhave<a name="T1.0015"></a>, diṭṭhigataṃ diṭṭhigahanaṃ diṭṭhikantāraṃ diṭṭhivisūkaṃ diṭṭhivipphanditaṃ diṭṭhisaṃyojanaṃ. diṭṭhisaṃyojanasaṃyutto, bhikkhave, assutavā puthujjano na parimuccati jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, ‘na parimuccati dukkhasmā’ti vadāmi.</p>\r\n<p class="bodytext"><a name="para20"></a><a name="para20_mn1"></a><span class="paranum">20</span>. sutavā ca kho, bhikkhave, ariyasāvako – ariyānaṃ dassāvī ariyadhammassa kovido ariyadhamme suvinīto, sappurisānaṃ dassāvī sappurisadhammassa kovido sappurisadhamme suvinīto – manasikaraṇīye dhamme pajānāti amanasikaraṇīye dhamme pajānāti. so manasikaraṇīye dhamme pajānanto amanasikaraṇīye dhamme pajānanto ye dhammā na <a name="P1.0009"></a> manasikaraṇīyā te dhamme na manasi karoti, ye dhammā manasikaraṇīyā te dhamme manasi karoti.</p>\r\n<p class="bodytext">katame ca, bhikkhave, dhammā na manasikaraṇīyā ye dhamme na manasi karoti? yassa, bhikkhave, dhamme manasikaroto anuppanno vā kāmāsavo uppajjati, uppanno vā kāmāsavo pavaḍḍhati, anuppanno vā bhavāsavo uppajjati, uppanno vā bhavāsavo pavaḍḍhati, anuppanno vā avijjāsavo uppajjati, uppanno vā avijjāsavo pavaḍḍhati – ime dhammā na manasikaraṇīyā, ye dhamme na manasi karoti.</p>\r\n<p class="bodytext">katame ca, bhikkhave, dhammā manasikaraṇīyā ye dhamme manasi karoti? yassa, bhikkhave, dhamme manasikaroto anuppanno vā kāmāsavo na uppajjati, uppanno vā kāmāsavo pahīyati, anuppanno vā bhavāsavo na uppajjati<a name="T1.0016"></a>, uppanno vā bhavāsavo pahīyati, anuppanno vā avijjāsavo na uppajjati, uppanno vā avijjāsavo pahīyati – ime dhammā manasikaraṇīyā ye dhamme manasi karoti.</p>\r\n<p class="bodytext">tassa amanasikaraṇīyānaṃ dhammānaṃ amanasikārā manasikaraṇīyānaṃ dhammānaṃ manasikārā anuppannā ceva āsavā na uppajjanti, uppannā ca āsavā pahīyanti.</p>\r\n<p class="bodytext"><a name="para21"></a><a name="para21_mn1"></a><span class="paranum">21</span>. so <a name="V1.0013"></a> ‘idaṃ dukkha’nti yoniso manasi karoti, ‘ayaṃ dukkhasamudayo’ti yoniso manasi karoti, ‘ayaṃ dukkhanirodho’ti</p>\r\n','\r',char(13)),'\n',char(10)),'-20-21-');
INSERT INTO pages VALUES(3051,'mula_ma_01',12,replace(replace('<p class="bodytext">yoniso <a name="M1.0012"></a> manasi karoti, ‘ayaṃ dukkhanirodhagāminī paṭipadā’ti yoniso manasi karoti. tassa evaṃ yoniso manasikaroto tīṇi saṃyojanāni pahīyanti – sakkāyadiṭṭhi, vicikicchā, sīlabbataparāmāso. ime vuccanti, bhikkhave, āsavā dassanā pahātabbā.</p>\r\n<p class="subhead">saṃvarā pahātabbāsavā</p>\r\n<p class="bodytext"><a name="para22"></a><a name="para22_mn1"></a><span class="paranum">22</span>. katame ca, bhikkhave, āsavā saṃvarā pahātabbā? idha, bhikkhave, bhikkhu paṭisaṅkhā yoniso cakkhundriyasaṃvarasaṃvuto viharati. yañhissa, bhikkhave, cakkhundriyasaṃvaraṃ asaṃvutassa viharato uppajjeyyuṃ āsavā vighātapariḷāhā, cakkhundriyasaṃvaraṃ saṃvutassa viharato evaṃsa te āsavā vighātapariḷāhā na honti. paṭisaṅkhā yoniso sotindriyasaṃvarasaṃvuto viharati…pe… ghānindriyasaṃvarasaṃvuto viharati…pe… jivhindriyasaṃvarasaṃvuto viharati…pe… kāyindriyasaṃvarasaṃvuto viharati…pe… manindriyasaṃvarasaṃvuto viharati. yañhissa, bhikkhave<a name="T1.0017"></a>, manindriyasaṃvaraṃ asaṃvutassa viharato uppajjeyyuṃ āsavā vighātapariḷāhā, manindriyasaṃvaraṃ saṃvutassa viharato evaṃsa te āsavā vighātapariḷāhā na honti.</p>\r\n<p class="bodytext">yañhissa, bhikkhave, saṃvaraṃ asaṃvutassa viharato uppajjeyyuṃ āsavā vighātapariḷāhā<a name="P1.0010"></a>, saṃvaraṃ saṃvutassa viharato evaṃsa te āsavā vighātapariḷāhā na honti. ime vuccanti, bhikkhave, āsavā saṃvarā pahātabbā.</p>\r\n<p class="subhead">paṭisevanā pahātabbāsavā</p>\r\n<p class="bodytext"><a name="para23"></a><a name="para23_mn1"></a><span class="paranum">23</span>. katame ca, bhikkhave, āsavā paṭisevanā pahātabbā? idha, bhikkhave, bhikkhu paṭisaṅkhā yoniso cīvaraṃ paṭisevati – ‘yāvadeva sītassa paṭighātāya, uṇhassa paṭighātāya, ḍaṃsamakasavātātapasarīṃsapa- <span class="note">[siriṃsapa (sī. syā. pī.)]</span> samphassānaṃ paṭighātāya, yāvadeva hirikopīnappaṭicchādanatthaṃ’.</p>\r\n<p class="bodytext">paṭisaṅkhā yoniso piṇḍapātaṃ paṭisevati – ‘neva davāya, na madāya, na maṇḍanāya, na <a name="V1.0014"></a> vibhūsanāya, yāvadeva imassa kāyassa ṭhitiyā yāpanāya, vihiṃsūparatiyā, brahmacariyānuggahāya, iti purāṇañca vedanaṃ paṭihaṅkhāmi</p>\r\n','\r',char(13)),'\n',char(10)),'-22-23-');
INSERT INTO pages VALUES(3052,'mula_ma_01',13,replace(replace('<p class="bodytext">navañca <a name="M1.0013"></a> vedanaṃ na uppādessāmi, yātrā ca me bhavissati anavajjatā ca phāsuvihāro ca’ <span class="note">[cāti (sī.)]</span>.</p>\r\n<p class="bodytext">paṭisaṅkhā yoniso senāsanaṃ paṭisevati – ‘yāvadeva sītassa paṭighātāya, uṇhassa paṭighātāya, ḍaṃsamakasavātātapasarīṃsapasamphassānaṃ paṭighātāya, yāvadeva utuparissayavinodanapaṭisallānārāmatthaṃ’.</p>\r\n<p class="bodytext">paṭisaṅkhā yoniso gilānappaccayabhesajjaparikkhāraṃ paṭisevati – ‘yāvadeva uppannānaṃ veyyābādhikānaṃ vedanānaṃ paṭighātāya, abyābajjhaparamatāya’ <span class="note">[abyāpajjhaparamatāya (sī. syā. pī.), abyāpajjaparamatāya (ka.)]</span>.</p>\r\n<p class="bodytext">yañhissa, bhikkhave, appaṭisevato uppajjeyyuṃ āsavā <a name="T1.0018"></a> vighātapariḷāhā, paṭisevato evaṃsa te āsavā vighātapariḷāhā na honti. ime vuccanti, bhikkhave, āsavā paṭisevanā pahātabbā.</p>\r\n<p class="subhead">adhivāsanā pahātabbāsavā</p>\r\n<p class="bodytext"><a name="para24"></a><a name="para24_mn1"></a><span class="paranum">24</span>. katame ca, bhikkhave, āsavā adhivāsanā pahātabbā? idha, bhikkhave, bhikkhu paṭisaṅkhā yoniso khamo hoti sītassa uṇhassa, jighacchāya pipāsāya. ḍaṃsamakasavātātapasarīṃsapasamphassānaṃ, duruttānaṃ durāgatānaṃ vacanapathānaṃ, uppannānaṃ sārīrikānaṃ vedanānaṃ dukkhānaṃ tibbānaṃ <span class="note">[tippānaṃ (sī. syā. pī.)]</span> kharānaṃ kaṭukānaṃ asātānaṃ amanāpānaṃ pāṇaharānaṃ adhivāsakajātiko hoti.</p>\r\n<p class="bodytext">yañhissa, bhikkhave, anadhivāsayato uppajjeyyuṃ āsavā vighātapariḷāhā, adhivāsayato evaṃsa te āsavā vighātapariḷāhā na honti. ime vuccanti, bhikkhave, āsavā adhivāsanā pahātabbā.</p>\r\n<p class="subhead">parivajjanā pahātabbāsavā</p>\r\n<p class="bodytext"><a name="para25"></a><a name="para25_mn1"></a><span class="paranum">25</span>. katame ca, bhikkhave, āsavā parivajjanā pahātabbā? idha, bhikkhave, bhikkhu <a name="V1.0015"></a> paṭisaṅkhā yoniso caṇḍaṃ hatthiṃ parivajjeti, caṇḍaṃ assaṃ parivajjeti, caṇḍaṃ goṇaṃ parivajjeti, caṇḍaṃ kukkuraṃ parivajjeti, ahiṃ khāṇuṃ kaṇṭakaṭṭhānaṃ <a name="P1.0011"></a> sobbhaṃ papātaṃ candanikaṃ oḷigallaṃ. yathārūpe anāsane</p>\r\n','\r',char(13)),'\n',char(10)),'-24-25-');
INSERT INTO pages VALUES(3053,'mula_ma_01',14,replace(replace('<p class="bodytext">nisinnaṃ <a name="M1.0014"></a> yathārūpe agocare carantaṃ yathārūpe pāpake mitte bhajantaṃ viññū sabrahmacārī pāpakesu ṭhānesu okappeyyuṃ, so tañca anāsanaṃ tañca agocaraṃ te ca <a name="T1.0019"></a> pāpake mitte paṭisaṅkhā yoniso parivajjeti.</p>\r\n<p class="bodytext">yañhissa, bhikkhave, aparivajjayato uppajjeyyuṃ āsavā vighātapariḷāhā, parivajjayato evaṃsa te āsavā vighātapariḷāhā na honti. ime vuccanti, bhikkhave, āsavā parivajjanā pahātabbā.</p>\r\n<p class="subhead">vinodanā pahātabbāsavā</p>\r\n<p class="bodytext"><a name="para26"></a><a name="para26_mn1"></a><span class="paranum">26</span>. katame ca, bhikkhave, āsavā vinodanā pahātabbā? idha, bhikkhave, bhikkhu paṭisaṅkhā yoniso uppannaṃ kāmavitakkaṃ nādhivāseti pajahati vinodeti byantīkaroti anabhāvaṃ gameti, uppannaṃ byāpādavitakkaṃ…pe… uppannaṃ vihiṃsāvitakkaṃ…pe… uppannuppanne pāpake akusale dhamme nādhivāseti pajahati vinodeti byantīkaroti anabhāvaṃ gameti.</p>\r\n<p class="bodytext">yañhissa, bhikkhave, avinodayato uppajjeyyuṃ āsavā vighātapariḷāhā, vinodayato evaṃsa te āsavā vighātapariḷāhā na honti. ime vuccanti, bhikkhave, āsavā vinodanā pahātabbā.</p>\r\n<p class="subhead">bhāvanā pahātabbāsavā</p>\r\n<p class="bodytext"><a name="para27"></a><a name="para27_mn1"></a><span class="paranum">27</span>. katame ca, bhikkhave, āsavā bhāvanā pahātabbā? idha, bhikkhave, bhikkhu paṭisaṅkhā yoniso satisambojjhaṅgaṃ bhāveti vivekanissitaṃ virāganissitaṃ nirodhanissitaṃ vossaggapariṇāmiṃ, paṭisaṅkhā yoniso dhammavicayasambojjhaṅgaṃ bhāveti…pe… vīriyasambojjhaṅgaṃ bhāveti… pītisambojjhaṅgaṃ bhāveti… <a name="V1.0016"></a> passaddhisambojjhaṅgaṃ bhāveti… samādhisambojjhaṅgaṃ bhāveti… upekkhāsambojjhaṅgaṃ bhāveti vivekanissitaṃ virāganissitaṃ nirodhanissitaṃ vossaggapariṇāmiṃ.</p>\r\n<p class="bodytext">yañhissa, bhikkhave<a name="T1.0020"></a>, abhāvayato uppajjeyyuṃ āsavā vighātapariḷāhā, bhāvayato evaṃsa te āsavā vighātapariḷāhā na honti. ime vuccanti, bhikkhave, āsavā bhāvanā pahātabbā.</p>\r\n<p class="bodytext"><a name="para28"></a><a name="para28_mn1"></a><span class="paranum">28</span>. yato kho, bhikkhave, bhikkhuno ye āsavā dassanā pahātabbā te dassanā pahīnā honti, ye āsavā saṃvarā</p>\r\n','\r',char(13)),'\n',char(10)),'-26-27-28-');
INSERT INTO pages VALUES(3054,'mula_ma_01',15,replace(replace('<p class="bodytext">pahātabbā <a name="M1.0015"></a> te saṃvarā pahīnā honti, ye āsavā paṭisevanā pahātabbā te paṭisevanā pahīnā honti, ye āsavā adhivāsanā pahātabbā te adhivāsanā pahīnā honti, ye āsavā parivajjanā pahātabbā <a name="P1.0012"></a> te parivajjanā pahīnā honti, ye āsavā vinodanā pahātabbā te vinodanā pahīnā honti, ye āsavā bhāvanā pahātabbā te bhāvanā pahīnā honti, ayaṃ vuccati, bhikkhave – ‘bhikkhu sabbāsavasaṃvarasaṃvuto viharati, acchecchi <span class="note">[acchejji (ka.)]</span> taṇhaṃ, vivattayi <span class="note">[vāvattayi (sī. pī.)]</span> saṃyojanaṃ, sammā mānābhisamayā antamakāsi dukkhassā"’ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">sabbāsavasuttaṃ niṭṭhitaṃ dutiyaṃ.</p>\r\n<p class="subhead">3. dhammadāyādasuttaṃ</p>\r\n<p class="bodytext"><a name="para29"></a><a name="para29_mn1"></a><span class="paranum">29</span>. <a name="T1.0021"></a><a name="V1.0017"></a> evaṃ me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext">dhammadāyādā me, bhikkhave, bhavatha, mā āmisadāyādā. atthi me tumhesu anukampā – ‘kinti me sāvakā dhammadāyādā bhaveyyuṃ, no āmisadāyādā’ti. tumhe ca me, bhikkhave, āmisadāyādā bhaveyyātha no dhammadāyādā, tumhepi tena ādiyā <span class="note">[ādissā (sī. syā. pī.)]</span> bhaveyyātha – ‘āmisadāyādā satthusāvakā viharanti, no dhammadāyādā’ti, ahampi tena ādiyo bhaveyyaṃ – ‘āmisadāyādā satthusāvakā viharanti, no dhammadāyādā’ti. tumhe ca me, bhikkhave, dhammadāyādā bhaveyyātha, no āmisadāyādā, tumhepi tena na ādiyā bhaveyyātha – ‘dhammadāyādā satthusāvakā viharanti, no āmisadāyādā’ti, ahampi tena na ādiyo bhaveyyaṃ – ‘dhammadāyādā satthusāvakā viharanti, no āmisadāyādā’ti. tasmātiha me, bhikkhave, dhammadāyādā bhavatha, mā āmisadāyādā. atthi</p>\r\n','\r',char(13)),'\n',char(10)),'-29-');
INSERT INTO pages VALUES(3055,'mula_ma_01',16,replace(replace('<p class="bodytext">me <a name="M1.0016"></a> tumhesu anukampā – ‘kinti me sāvakā dhammadāyādā bhaveyyuṃ, no āmisadāyādā’ti.</p>\r\n<p class="bodytext"><a name="para30"></a><a name="para30_mn1"></a><span class="paranum">30</span>. idhāhaṃ, bhikkhave, bhuttāvī assaṃ pavārito paripuṇṇo pariyosito <a name="T1.0022"></a> suhito yāvadattho, siyā ca me piṇḍapāto atirekadhammo chaḍḍanīyadhammo <span class="note">[chaḍḍiyadhammo (sī. syā. pī.)]</span>. atha dve bhikkhū āgaccheyyuṃ jighacchādubbalya- <span class="note">[jighacchādubballa (sī. pī.)]</span> paretā<a name="P1.0013"></a>. tyāhaṃ evaṃ vadeyyaṃ – ‘ahaṃ khomhi, bhikkhave, bhuttāvī pavārito paripuṇṇo pariyosito suhito yāvadattho, atthi ca me ayaṃ piṇḍapāto atirekadhammo chaḍḍanīyadhammo. sace ākaṅkhatha, bhuñjatha, no ce tumhe bhuñjissatha <span class="note">[sace tumhe na bhuñjissatha (sī. syā. pī.)]</span>, idānāhaṃ appaharite vā chaḍḍessāmi, appāṇake vā udake opilāpessāmī’ti. tatrekassa bhikkhuno evamassa – ‘bhagavā kho bhuttāvī pavārito paripuṇṇo pariyosito suhito yāvadattho, atthi cāyaṃ bhagavato piṇḍapāto atirekadhammo chaḍḍanīyadhammo. sace mayaṃ na bhuñjissāma, idāni bhagavā appaharite vā chaḍḍessati, appāṇake vā udake opilāpessati’<a name="V1.0018"></a>. vuttaṃ kho panetaṃ bhagavatā – ‘dhammadāyādā me, bhikkhave, bhavatha, mā āmisadāyādā’ti. āmisaññataraṃ kho panetaṃ, yadidaṃ piṇḍapāto. yaṃnūnāhaṃ imaṃ piṇḍapātaṃ abhuñjitvā imināva jighacchādubbalyena evaṃ imaṃ rattindivaṃ <span class="note">[rattidivaṃ (ka.)]</span> vītināmeyya"nti. so taṃ piṇḍapātaṃ abhuñjitvā teneva jighacchādubbalyena evaṃ taṃ rattindivaṃ vītināmeyya. atha dutiyassa bhikkhuno evamassa – ‘bhagavā kho bhuttāvī pavārito paripuṇṇo pariyosito suhito yāvadattho, atthi cāyaṃ bhagavato piṇḍapāto atirekadhammo chaḍḍanīyadhammo. sace mayaṃ na bhuñjissāma, idāni bhagavā appaharite vā chaḍḍessati, appāṇake <a name="T1.0023"></a> vā udake opilāpessati. yaṃnūnāhaṃ imaṃ piṇḍapātaṃ bhuñjitvā jighacchādubbalyaṃ paṭivinodetvā <span class="note">[paṭivinetvā (sī. syā. pī.)]</span> evaṃ imaṃ rattindivaṃ vītināmeyya’nti. so taṃ piṇḍapātaṃ bhuñjitvā jighacchādubbalyaṃ paṭivinodetvā evaṃ taṃ rattindivaṃ vītināmeyya. kiñcāpi so, bhikkhave, bhikkhu taṃ piṇḍapātaṃ bhuñjitvā jighacchādubbalyaṃ paṭivinodetvā evaṃ taṃ rattindivaṃ vītināmeyya, atha kho asuyeva me purimo bhikkhu pujjataro ca pāsaṃsataro ca. taṃ kissa hetu? tañhi tassa, bhikkhave, bhikkhuno</p>\r\n','\r',char(13)),'\n',char(10)),'-30-');
INSERT INTO pages VALUES(3056,'mula_ma_01',17,replace(replace('<p class="bodytext">dīgharattaṃ <a name="M1.0017"></a> appicchatāya santuṭṭhiyā sallekhāya subharatāya vīriyārambhāya saṃvattissati. tasmātiha me, bhikkhave, dhammadāyādā bhavatha, mā āmisadāyādā. atthi me tumhesu anukampā – ‘kinti me sāvakā dhammadāyādā bhaveyyuṃ, no āmisadāyādā"’ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. idaṃ vatvāna <span class="note">[vatvā (sī. pī.) evamīdisesu ṭhānesu]</span> sugato uṭṭhāyāsanā vihāraṃ pāvisi.</p>\r\n<p class="bodytext"><a name="para31"></a><a name="para31_mn1"></a><span class="paranum">31</span>. tatra kho āyasmā sāriputto acirapakkantassa bhagavato bhikkhū āmantesi – "āvuso bhikkhave"ti. "āvuso"ti kho te <a name="P1.0014"></a> bhikkhū āyasmato sāriputtassa paccassosuṃ. āyasmā sāriputto etadavoca –</p>\r\n<p class="bodytext">kittāvatā nu kho, āvuso, satthu pavivittassa viharato sāvakā vivekaṃ nānusikkhanti, kittāvatā ca pana satthu pavivittassa viharato sāvakā vivekamanusikkhantī"ti? "dūratopi kho mayaṃ, āvuso, āgacchāma <a name="T1.0024"></a> āyasmato sāriputtassa santike etassa bhāsitassa atthamaññātuṃ. sādhu vatāyasmantaṃyeva sāriputtaṃ paṭibhātu etassa bhāsitassa attho, āyasmato sāriputtassa sutvā bhikkhū dhāressantī"ti. "tena <a name="V1.0019"></a> hāvuso, suṇātha, sādhukaṃ manasi karotha, bhāsissāmī"ti. "evamāvuso"ti kho te bhikkhū āyasmato sāriputtassa paccassosuṃ. āyasmā sāriputto etadavoca –</p>\r\n<p class="bodytext">kittāvatā nu kho, āvuso, satthu pavivittassa viharato sāvakā vivekaṃ nānusikkhanti? idhāvuso, satthu pavivittassa viharato sāvakā vivekaṃ nānusikkhanti, yesañca dhammānaṃ satthā pahānamāha, te ca dhamme nappajahanti, bāhulikā <span class="note">[bāhullikā (syā.)]</span> ca honti, sāthalikā, okkamane pubbaṅgamā, paviveke nikkhittadhurā. tatrāvuso, therā bhikkhū tīhi ṭhānehi gārayhā bhavanti. ‘satthu pavivittassa viharato sāvakā vivekaṃ nānusikkhantī’ti – iminā paṭhamena ṭhānena therā bhikkhū gārayhā bhavanti. ‘yesañca dhammānaṃ satthā pahānamāha te ca dhamme nappajahantī’ti – iminā</p>\r\n','\r',char(13)),'\n',char(10)),'-31-');
INSERT INTO pages VALUES(3057,'mula_ma_01',18,replace(replace('<p class="bodytext">dutiyena <a name="M1.0018"></a> ṭhānena therā bhikkhū gārayhā bhavanti. ‘bāhulikā ca, sāthalikā, okkamane pubbaṅgamā, paviveke nikkhittadhurā’ti – iminā tatiyena ṭhānena therā bhikkhū gārayhā bhavanti. therā, āvuso, bhikkhū imehi tīhi ṭhānehi gārayhā bhavanti. tatrāvuso, majjhimā bhikkhū…pe… navā bhikkhū tīhi ṭhānehi gārayhā bhavanti. ‘satthu pavivittassa viharato <a name="T1.0025"></a> sāvakā vivekaṃ nānusikkhantī’ti – iminā paṭhamena ṭhānena navā bhikkhū gārayhā bhavanti. ‘yesañca dhammānaṃ satthā pahānamāha te ca dhamme nappajahantī’ti – iminā dutiyena ṭhānena navā bhikkhū gārayhā bhavanti. ‘bāhulikā ca honti, sāthalikā, okkamane pubbaṅgamā, paviveke nikkhittadhurā’ti – iminā tatiyena ṭhānena navā bhikkhū gārayhā bhavanti. navā, āvuso, bhikkhū imehi tīhi ṭhānehi gārayhā bhavanti. ettāvatā kho, āvuso, satthu pavivittassa viharato sāvakā vivekaṃ nānusikkhanti.</p>\r\n<p class="bodytext"><a name="para32"></a><a name="para32_mn1"></a><span class="paranum">32</span>. kittāvatā ca, panāvuso, satthu pavivittassa viharato sāvakā vivekamanusikkhanti<a name="P1.0015"></a>? idhāvuso, satthu pavivittassa viharato sāvakā vivekamanusikkhanti – yesañca dhammānaṃ satthā pahānamāha te ca dhamme pajahanti, na ca bāhulikā honti, na sāthalikā okkamane nikkhittadhurā paviveke pubbaṅgamā. tatrāvuso, therā bhikkhū tīhi ṭhānehi pāsaṃsā bhavanti. ‘satthu pavivittassa viharato sāvakā vivekamanusikkhantī’ti – iminā paṭhamena ṭhānena therā bhikkhū pāsaṃsā bhavanti. ‘yesañca dhammānaṃ satthā pahānamāha te ca dhamme pajahantī’ti – iminā dutiyena ṭhānena therā bhikkhū pāsaṃsā <a name="V1.0020"></a> bhavanti. ‘na ca bāhulikā, na sāthalikā okkamane nikkhittadhurā paviveke pubbaṅgamā’ti – iminā tatiyena ṭhānena therā bhikkhū pāsaṃsā bhavanti. therā, āvuso, bhikkhū imehi tīhi ṭhānehi pāsaṃsā bhavanti<a name="T1.0026"></a>. tatrāvuso, majjhimā bhikkhū…pe… navā bhikkhū tīhi ṭhānehi pāsaṃsā bhavanti. ‘satthu pavivittassa viharato sāvakā vivekamanusikkhantī’ti – iminā paṭhamena ṭhānena navā bhikkhū pāsaṃsā bhavanti. ‘yesañca dhammānaṃ satthā pahānamāha te ca dhamme pajahantī’ti – iminā dutiyena ṭhānena navā bhikkhū pāsaṃsā bhavanti. ‘na ca bāhulikā, na sāthalikā okkamane nikkhittadhurā paviveke pubbaṅgamā’ti – iminā tatiyena ṭhānena navā bhikkhū pāsaṃsā bhavanti. navā, āvuso, bhikkhū</p>\r\n','\r',char(13)),'\n',char(10)),'-32-');
INSERT INTO pages VALUES(3058,'mula_ma_01',19,replace(replace('<p class="bodytext">imehi <a name="M1.0019"></a> tīhi ṭhānehi pāsaṃsā bhavanti. ettāvatā kho, āvuso, satthu pavivittassa viharato sāvakā vivekamanusikkhanti.</p>\r\n<p class="bodytext"><a name="para33"></a><a name="para33_mn1"></a><span class="paranum">33</span>. tatrāvuso, lobho ca pāpako doso ca pāpako. lobhassa ca pahānāya dosassa ca pahānāya atthi majjhimā paṭipadā cakkhukaraṇī ñāṇakaraṇī upasamāya abhiññāya sambodhāya nibbānāya saṃvattati. katamā ca sā, āvuso, majjhimā paṭipadā cakkhukaraṇī ñāṇakaraṇī upasamāya abhiññāya sambodhāya nibbānāya saṃvattati? ayameva ariyo aṭṭhaṅgiko maggo, seyyathidaṃ <span class="note">[seyyathīdaṃ (sī. syā. pī.)]</span> – sammādiṭṭhi sammāsaṅkappo sammāvācā sammākammanto sammāājīvo sammāvāyāmo sammāsati sammāsamādhi. ayaṃ kho sā, āvuso, majjhimā paṭipadā cakkhukaraṇī ñāṇakaraṇī upasamāya abhiññāya sambodhāya nibbānāya saṃvattati.</p>\r\n<p class="bodytext">tatrāvuso, kodho ca pāpako upanāho ca <a name="T1.0027"></a> pāpako…pe… makkho ca pāpako paḷāso ca pāpako, issā ca pāpikā maccherañca pāpakaṃ, māyā ca pāpikā sāṭheyyañca pāpakaṃ, thambho ca pāpako sārambho <a name="P1.0016"></a> ca pāpako, māno ca pāpako atimāno ca pāpako, mado ca pāpako pamādo ca pāpako. madassa ca pahānāya pamādassa ca pahānāya atthi majjhimā paṭipadā cakkhukaraṇī ñāṇakaraṇī upasamāya abhiññāya sambodhāya nibbānāya saṃvattati. katamā ca sā, āvuso, majjhimā paṭipadā cakkhukaraṇī ñāṇakaraṇī upasamāya abhiññāya sambodhāya nibbānāya saṃvattati? ayameva ariyo aṭṭhaṅgiko maggo, seyyathidaṃ – sammādiṭṭhi sammāsaṅkappo sammāvācā sammākammanto sammāājīvo sammāvāyāmo sammāsati sammāsamādhi. ayaṃ kho sā, āvuso, majjhimā <a name="V1.0021"></a> paṭipadā cakkhukaraṇī ñāṇakaraṇī upasamāya abhiññāya sambodhāya nibbānāya saṃvattatī"ti.</p>\r\n<p class="bodytext">idamavocāyasmā sāriputto. attamanā te bhikkhū āyasmato sāriputtassa bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">dhammadāyādasuttaṃ niṭṭhitaṃ tatiyaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-33-');
INSERT INTO pages VALUES(3059,'mula_ma_01',20,replace(replace('<p class="subhead">4. bhayabheravasuttaṃ</p>\r\n<p class="bodytext"><a name="para34"></a><a name="para34_mn1"></a><span class="paranum">34</span>. evaṃ <a name="M1.0020"></a><a name="V1.0022"></a><a name="T1.0028"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. atha kho jāṇussoṇi brāhmaṇo yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavatā saddhiṃ sammodi. sammodanīyaṃ kathaṃ sāraṇīyaṃ <span class="note">[sārāṇīyaṃ (sī. syā. pī.)]</span> vītisāretvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho jāṇussoṇi brāhmaṇo bhagavantaṃ etadavoca – "yeme, bho gotama, kulaputtā bhavantaṃ gotamaṃ uddissa saddhā agārasmā anagāriyaṃ pabbajitā, bhavaṃ tesaṃ gotamo pubbaṅgamo, bhavaṃ tesaṃ gotamo bahukāro, bhavaṃ tesaṃ gotamo samādapetā <span class="note">[samādāpetā (?)]</span>, bhoto ca pana gotamassa sā janatā diṭṭhānugatiṃ āpajjatī"ti. "evametaṃ, brāhmaṇa, evametaṃ, brāhmaṇa! ye te, brāhmaṇa, kulaputtā mamaṃ uddissa saddhā agārasmā anagāriyaṃ pabbajitā, ahaṃ tesaṃ pubbaṅgamo, ahaṃ tesaṃ bahukāro, ahaṃ tesaṃ samādapetā, mama ca pana sā janatā diṭṭhānugatiṃ āpajjatī"ti. "durabhisambhavāni hi kho, bho gotama, araññavanapatthāni pantāni senāsanāni, dukkaraṃ pavivekaṃ, durabhiramaṃ ekatte, haranti maññe mano vanāni samādhiṃ alabhamānassa bhikkhuno"ti<a name="P1.0017"></a>. evametaṃ, brāhmaṇa, evametaṃ, brāhmaṇa! durabhisambhavāni hi kho, brāhmaṇa, araññavanapatthāni pantāni <a name="T1.0029"></a> senāsanāni, dukkaraṃ pavivekaṃ, durabhiramaṃ ekatte, haranti maññe mano vanāni samādhiṃ alabhamānassa bhikkhuno"ti.</p>\r\n<p class="bodytext"><a name="para35"></a><a name="para35_mn1"></a><span class="paranum">35</span>. mayhampi kho, brāhmaṇa, pubbeva sambodhā anabhisambuddhassa bodhisattasseva sato etadahosi – ‘durabhisambhavāni hi kho araññavanapatthāni pantāni senāsanāni, dukkaraṃ pavivekaṃ, durabhiramaṃ ekatte, haranti maññe mano vanāni samādhiṃ alabhamānassa bhikkhuno’ti. tassa mayhaṃ brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā aparisuddhakāyakammantā araññavanapatthāni pantāni senāsanāni paṭisevanti, aparisuddhakāyakammantasandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ aparisuddhakāyakammanto araññavanapatthāni pantāni senāsanāni paṭisevāmi, parisuddhakāyakammantohamasmi. ye hi vo ariyā</p>\r\n','\r',char(13)),'\n',char(10)),'-34-35-');
INSERT INTO pages VALUES(3060,'mula_ma_01',21,replace(replace('<p class="bodytext">parisuddhakāyakammantā <a name="M1.0021"></a> araññavanapatthāni <a name="V1.0023"></a> pantāni senāsanāni paṭisevanti tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, parisuddhakāyakammataṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para36"></a><a name="para36_mn1"></a><span class="paranum">36</span>. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā aparisuddhavacīkammantā…pe… aparisuddhamanokammantā <a name="T1.0030"></a> …pe… aparisuddhājīvā araññavanapatthāni pantāni senāsanāni paṭisevanti, aparisuddhājīvasandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ aparisuddhājīvo araññavanapatthāni pantāni senāsanāni paṭisevāmi, parisuddhājīvohamasmi. ye hi vo ariyā parisuddhājīvā araññavanapatthāni pantāni senāsanāni paṭisevanti tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, parisuddhājīvataṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para37"></a><a name="para37_mn1"></a><span class="paranum">37</span>. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā abhijjhālū kāmesu tibbasārāgā araññavanapatthāni pantāni senāsanāni paṭisevanti, abhijjhālukāmesutibbasārāgasandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ abhijjhālu kāmesu tibbasārāgo araññavanapatthāni pantāni senāsanāni paṭisevāmi, anabhijjhālūhamasmi. ye hi vo ariyā anabhijjhālū araññavanapatthāni pantāni senāsanāni paṭisevanti<a name="P1.0018"></a>, tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, anabhijjhālutaṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para38"></a><a name="para38_mn1"></a><span class="paranum">38</span>. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā byāpannacittā paduṭṭhamanasaṅkappā araññavanapatthāni <a name="T1.0031"></a> pantāni senāsanāni paṭisevanti, byāpannacittapaduṭṭhamanasaṅkappasandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ byāpannacitto paduṭṭhamanasaṅkappo araññavanapatthāni pantāni senāsanāni paṭisevāmi, mettacittohamasmi. ye hi vo ariyā mettacittā araññavanapatthāni pantāni senāsanāni paṭisevanti tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, mettacittataṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n','\r',char(13)),'\n',char(10)),'-36-37-38-');
INSERT INTO pages VALUES(3061,'mula_ma_01',22,replace(replace('<p class="bodytext"><a name="para39"></a><a name="para39_mn1"></a><span class="paranum">39</span>. tassa <a name="M1.0022"></a><a name="V1.0024"></a> mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā thīnamiddhapariyuṭṭhitā araññavanapatthāni pantāni senāsanāni paṭisevanti, thīnamiddhapariyuṭṭhānasandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ thīnamiddhapariyuṭṭhito araññavanapatthāni pantāni senāsanāni paṭisevāmi, vigatathīnamiddhohamasmi. ye hi vo ariyā vigatathīnamiddhā araññavanapatthāni pantāni senāsanāni paṭisevanti tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, vigatathīnamiddhataṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para40"></a><a name="para40_mn1"></a><span class="paranum">40</span>. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā uddhatā avūpasantacittā araññavanapatthāni pantāni senāsanāni paṭisevanti, uddhataavūpasantacittasandosahetu <a name="T1.0032"></a> have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ uddhato avūpasantacitto araññavanapatthāni pantāni senāsanāni paṭisevāmi, vūpasantacittohamasmi. ye hi vo ariyā vūpasantacittā araññavanapatthāni pantāni senāsanāni paṭisevanti, tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, vūpasantacittataṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para41"></a><a name="para41_mn1"></a><span class="paranum">41</span>. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā kaṅkhī vicikicchī araññavanapatthāni pantāni senāsanāni paṭisevanti, kaṅkhivicikicchisandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ kaṅkhī vicikicchī araññavanapatthāni pantāni senāsanāni paṭisevāmi, tiṇṇavicikicchohamasmi. ye hi vo ariyā tiṇṇavicikicchā araññavanapatthāni pantāni senāsanāni paṭisevanti tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, tiṇṇavicikicchataṃ attani sampassamāno <a name="P1.0019"></a> bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para42"></a><a name="para42_mn1"></a><span class="paranum">42</span>. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā attukkaṃsakā paravambhī araññavanapatthāni pantāni senāsanāni paṭisevanti, attukkaṃsanaparavambhanasandosahetu have te</p>\r\n','\r',char(13)),'\n',char(10)),'-39-40-41-42-');
INSERT INTO pages VALUES(3062,'mula_ma_01',23,replace(replace('<p class="bodytext">bhonto <a name="M1.0023"></a> samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti<a name="T1.0033"></a>. na kho panāhaṃ attukkaṃsako paravambhī araññavanapatthāni pantāni senāsanāni paṭisevāmi<a name="V1.0025"></a>, anattukkaṃsako aparavambhīhamasmi. ye hi vo ariyā anattukkaṃsakā aparavambhī araññavanapatthāni pantāni senāsanāni paṭisevanti tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, anattukkaṃsakataṃ aparavambhitaṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para43"></a><a name="para43_mn1"></a><span class="paranum">43</span>. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā chambhī bhīrukajātikā araññavanapatthāni pantāni senāsanāni paṭisevanti, chambhibhīrukajātikasandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ chambhī bhīrukajātiko araññavanapatthāni pantāni senāsanāni paṭisevāmi, vigatalomahaṃsohamasmi. ye hi vo ariyā vigatalomahaṃsā araññavanapatthāni pantāni senāsanāni paṭisevanti tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, vigatalomahaṃsataṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para44"></a><a name="para44_mn1"></a><span class="paranum">44</span>. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā lābhasakkārasilokaṃ nikāmayamānā araññavanapatthāni pantāni senāsanāni paṭisevanti, lābhasakkārasilokanikāmana <span class="note">[nikāmayamāna (sī. syā.)]</span> sandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ lābhasakkārasilokaṃ nikāmayamāno <a name="T1.0034"></a> araññavanapatthāni pantāni senāsanāni paṭisevāmi, appicchohamasmi. ye hi vo ariyā appicchā araññavanapatthāni pantāni senāsanāni paṭisevanti tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, appicchataṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para45"></a><a name="para45_mn1"></a><span class="paranum">45</span>. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā kusītā hīnavīriyā araññavanapatthāni pantāni senāsanāni</p>\r\n','\r',char(13)),'\n',char(10)),'-43-44-45-');
INSERT INTO pages VALUES(3063,'mula_ma_01',24,replace(replace('<p class="bodytext">paṭisevanti<a name="M1.0024"></a>, kusītahīnavīriyasandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ kusīto hīnavīriyo araññavanapatthāni pantāni senāsanāni paṭisevāmi, āraddhavīriyohamasmi. ye hi vo ariyā āraddhavīriyā araññavanapatthāni pantāni senāsanāni paṭisevanti tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, āraddhavīriyataṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para46"></a><a name="para46_mn1"></a><span class="paranum">46</span>. tassa <a name="V1.0026"></a> mayhaṃ, brāhmaṇa, etadahosi – ‘ye <a name="P1.0020"></a> kho keci samaṇā vā brāhmaṇā vā muṭṭhassatī asampajānā araññavanapatthāni pantāni senāsanāni paṭisevanti, muṭṭhassatiasampajānasandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ muṭṭhassati asampajāno araññavanapatthāni pantāni senāsanāni paṭisevāmi, upaṭṭhitassatihamasmi. ye <a name="T1.0035"></a> hi vo ariyā upaṭṭhitassatī araññavanapatthāni pantāni senāsanāni paṭisevanti tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, upaṭṭhitassatitaṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para47"></a><a name="para47_mn1"></a><span class="paranum">47</span>. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā asamāhitā vibbhantacittā araññavanapatthāni pantāni senāsanāni paṭisevanti, asamāhitavibbhantacittasandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ asamāhito vibbhantacitto araññavanapatthāni pantāni senāsanāni paṭisevāmi, samādhisampannohamasmi. ye hi vo ariyā samādhisampannā araññavanapatthāni pantāni senāsanāni paṭisevanti tesamahaṃ aññataro’ti. etamahaṃ, brāhmaṇa, samādhisampadaṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="bodytext"><a name="para48"></a><a name="para48_mn1"></a><span class="paranum">48</span>. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘ye kho keci samaṇā vā brāhmaṇā vā duppaññā eḷamūgā araññavanapatthāni pantāni senāsanāni paṭisevanti, duppaññaeḷamūgasandosahetu have te bhonto samaṇabrāhmaṇā akusalaṃ bhayabheravaṃ avhāyanti. na kho panāhaṃ duppañño</p>\r\n','\r',char(13)),'\n',char(10)),'-46-47-48-');
INSERT INTO pages VALUES(3064,'mula_ma_01',25,replace(replace('<p class="bodytext">eḷamūgo <a name="M1.0025"></a> araññavanapatthāni pantāni senāsanāni paṭisevāmi, paññāsampannohamasmi. ye hi vo ariyā paññāsampannā araññavanapatthāni pantāni senāsanāni paṭisevanti tesamahaṃ <a name="T1.0036"></a> aññataro’ti. etamahaṃ, brāhmaṇa, paññāsampadaṃ attani sampassamāno bhiyyo pallomamāpādiṃ araññe vihārāya.</p>\r\n<p class="centered">soḷasapariyāyaṃ niṭṭhitaṃ.</p>\r\n<p class="bodytext"><a name="para49"></a><a name="para49_mn1"></a><span class="paranum">49</span>. tassa <a name="V1.0027"></a> mayhaṃ, brāhmaṇa, etadahosi – ‘yaṃnūnāhaṃ yā tā rattiyo abhiññātā abhilakkhitā – cātuddasī pañcadasī aṭṭhamī ca pakkhassa – tathārūpāsu rattīsu yāni tāni ārāmacetiyāni vanacetiyāni rukkhacetiyāni bhiṃsanakāni salomahaṃsāni tathārūpesu senāsanesu vihareyyaṃ appeva nāmāhaṃ bhayabheravaṃ passeyya’nti. so kho ahaṃ, brāhmaṇa, aparena samayena yā tā rattiyo abhiññātā abhilakkhitā – cātuddasī pañcadasī aṭṭhamī ca pakkhassa – tathārūpāsu rattīsu yāni tāni ārāmacetiyāni vanacetiyāni rukkhacetiyāni bhiṃsanakāni salomahaṃsāni tathārūpesu senāsanesu viharāmi. tattha ca me, brāhmaṇa, viharato mago vā āgacchati, moro vā kaṭṭhaṃ <a name="P1.0021"></a> pāteti, vāto vā paṇṇakasaṭaṃ <span class="note">[paṇṇasaṭaṃ (sī. pī.)]</span> ereti, tassa mayhaṃ brāhmaṇa etadahosi <span class="note">[tassa mayhaṃ evaṃ hoti (sī. syā.)]</span> – ‘etaṃ nūna taṃ bhayabheravaṃ āgacchatī’ti. tassa mayhaṃ, brāhmaṇa, etadahosi – ‘kiṃ nu kho ahaṃ aññadatthu bhayapaṭikaṅkhī <span class="note">[bhayapāṭikaṅkhī (sī.)]</span> viharāmi? yaṃnūnāhaṃ yathābhūtaṃ yathābhūtassa <span class="note">[yathābhūtassa yathābhūtassa (sī. syā.)]</span> me taṃ bhayabheravaṃ āgacchati, tathābhūtaṃ tathābhūtova <span class="note">[yathābhūto yathābhūtova (sī. syā.)]</span> taṃ bhayabheravaṃ paṭivineyya’nti. tassa mayhaṃ, brāhmaṇa, caṅkamantassa taṃ bhayabheravaṃ āgacchati. so kho ahaṃ, brāhmaṇa, neva tāva tiṭṭhāmi <a name="T1.0037"></a> na nisīdāmi na nipajjāmi, yāva caṅkamantova taṃ bhayabheravaṃ paṭivinemi. tassa mayhaṃ, brāhmaṇa, ṭhitassa taṃ bhayabheravaṃ āgacchati. so kho ahaṃ, brāhmaṇa, neva tāva caṅkamāmi na nisīdāmi na nipajjāmi. yāva ṭhitova taṃ bhayabheravaṃ paṭivinemi. tassa mayhaṃ, brāhmaṇa, nisinnassa taṃ bhayabheravaṃ āgacchati. so kho ahaṃ, brāhmaṇa, neva tāva nipajjāmi na</p>\r\n','\r',char(13)),'\n',char(10)),'-49-');
INSERT INTO pages VALUES(3065,'mula_ma_01',26,replace(replace('<p class="bodytext">tiṭṭhāmi <a name="M1.0026"></a> na caṅkamāmi, yāva nisinnova taṃ bhayabheravaṃ paṭivinemi. tassa mayhaṃ, brāhmaṇa, nipannassa taṃ bhayabheravaṃ āgacchati. so kho ahaṃ, brāhmaṇa, neva tāva nisīdāmi na tiṭṭhāmi na caṅkamāmi, yāva nipannova taṃ bhayabheravaṃ paṭivinemi.</p>\r\n<p class="bodytext"><a name="para50"></a><a name="para50_mn1"></a><span class="paranum">50</span>. santi kho pana, brāhmaṇa, eke samaṇabrāhmaṇā rattiṃyeva samānaṃ divāti sañjānanti, divāyeva samānaṃ rattīti sañjānanti. idamahaṃ tesaṃ samaṇabrāhmaṇānaṃ sammohavihārasmiṃ vadāmi. ahaṃ kho pana, brāhmaṇa, rattiṃyeva samānaṃ rattīti sañjānāmi, divāyeva samānaṃ divāti sañjānāmi. yaṃ kho taṃ, brāhmaṇa, sammā vadamāno vadeyya – ‘asammohadhammo satto loke uppanno bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussāna’nti, mameva taṃ sammā vadamāno vadeyya – ‘asammohadhammo satto loke uppanno bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussāna’nti.</p>\r\n<p class="bodytext"><a name="para51"></a><a name="para51_mn1"></a><span class="paranum">51</span>. āraddhaṃ <a name="T1.0038"></a><a name="V1.0028"></a> kho pana me, brāhmaṇa, vīriyaṃ ahosi asallīnaṃ, upaṭṭhitā sati asammuṭṭhā <span class="note">[appammuṭṭhā (syā.)]</span>, passaddho kāyo asāraddho, samāhitaṃ cittaṃ ekaggaṃ. so kho ahaṃ, brāhmaṇa, vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja vihāsiṃ. vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ <a name="P1.0022"></a> avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja vihāsiṃ. pītiyā ca virāgā upekkhako ca vihāsiṃ, sato ca sampajāno sukhañca kāyena paṭisaṃvedesiṃ, yaṃ taṃ ariyā ācikkhanti – ‘upekkhako satimā sukhavihārī’ti tatiyaṃ jhānaṃ upasampajja vihāsiṃ. sukhassa ca pahānā dukkhassa ca pahānā pubbeva somanassadomanassānaṃ atthaṅgamā adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja vihāsiṃ.</p>\r\n<p class="bodytext"><a name="para52"></a><a name="para52_mn1"></a><span class="paranum">52</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte pubbenivāsānussatiñāṇāya cittaṃ abhininnāmesiṃ. so anekavihitaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-50-51-52-');
INSERT INTO pages VALUES(3066,'mula_ma_01',27,replace(replace('<p class="bodytext">pubbenivāsaṃ <a name="M1.0027"></a> anussarāmi, seyyathidaṃ – ekampi jātiṃ dvepi jātiyo tissopi jātiyo catassopi jātiyo pañcapi jātiyo dasapi jātiyo vīsampi jātiyo tiṃsampi jātiyo cattālīsampi jātiyo paññāsampi jātiyo jātisatampi jātisahassampi jātisatasahassampi anekepi saṃvaṭṭakappe <a name="T1.0039"></a> anekepi vivaṭṭakappe anekepi saṃvaṭṭavivaṭṭakappe – ‘amutrāsiṃ evaṃnāmo evaṃgotto evaṃvaṇṇo evamāhāro evaṃsukhadukkhappaṭisaṃvedī evamāyupariyanto, so tato cuto amutra udapādiṃ, tatrāpāsiṃ evaṃnāmo evaṃgotto evaṃvaṇṇo evamāhāro evaṃsukhadukkhappaṭisaṃvedī evamāyupariyanto, so tato cuto idhūpapanno’ti. iti sākāraṃ sauddesaṃ anekavihitaṃ pubbenivāsaṃ anussarāmi. ayaṃ kho me, brāhmaṇa, rattiyā paṭhame yāme paṭhamā vijjā adhigatā, avijjā vihatā vijjā uppannā, tamo vihato āloko uppanno, yathā taṃ appamattassa ātāpino pahitattassa viharato.</p>\r\n<p class="bodytext"><a name="para53"></a><a name="para53_mn1"></a><span class="paranum">53</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte sattānaṃ cutūpapātañāṇāya cittaṃ abhininnāmesiṃ. so dibbena cakkhunā visuddhena atikkantamānusakena satte passāmi cavamāne upapajjamāne hīne paṇīte suvaṇṇe dubbaṇṇe sugate duggate yathākammūpage satte pajānāmi – ‘ime vata bhonto sattā kāyaduccaritena samannāgatā vacīduccaritena samannāgatā manoduccaritena samannāgatā ariyānaṃ <a name="V1.0029"></a> upavādakā micchādiṭṭhikā micchādiṭṭhikammasamādānā, te kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapannā. ime vā pana bhonto sattā kāyasucaritena samannāgatā <a name="P1.0023"></a> vacīsucaritena samannāgatā manosucaritena samannāgatā ariyānaṃ anupavādakā sammādiṭṭhikā <a name="T1.0040"></a> sammādiṭṭhikammasamādānā, te kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapannā’ti. iti dibbena cakkhunā visuddhena atikkantamānusakena satte passāmi cavamāne upapajjamāne hīne paṇīte suvaṇṇe dubbaṇṇe sugate duggate yathākammūpage satte pajānāmi. ayaṃ kho me, brāhmaṇa, rattiyā majjhime yāme dutiyā vijjā adhigatā, avijjā vihatā vijjā uppannā, tamo vihato āloko uppanno, yathā taṃ appamattassa ātāpino pahitattassa viharato.</p>\r\n','\r',char(13)),'\n',char(10)),'-53-');
INSERT INTO pages VALUES(3067,'mula_ma_01',28,replace(replace('<p class="bodytext"><a name="para54"></a><a name="para54_mn1"></a><span class="paranum">54</span>. so <a name="M1.0028"></a> evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte āsavānaṃ khayañāṇāya cittaṃ abhininnāmesiṃ. so ‘idaṃ dukkha’nti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ dukkhasamudayo’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ dukkhanirodho’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ dukkhanirodhagāminī paṭipadā’ti yathābhūtaṃ abbhaññāsiṃ. ‘ime āsavā’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ āsavasamudayo’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ āsavanirodho’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ āsavanirodhagāminī paṭipadā’ti yathābhūtaṃ abbhaññāsiṃ. tassa me evaṃ jānato evaṃ passato kāmāsavāpi cittaṃ vimuccittha, bhavāsavāpi cittaṃ vimuccittha, avijjāsavāpi cittaṃ vimuccittha. vimuttasmiṃ vimuttamiti ñāṇaṃ ahosi. ‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti abbhaññāsiṃ. ayaṃ <a name="T1.0041"></a> kho me, brāhmaṇa, rattiyā pacchime yāme tatiyā vijjā adhigatā, avijjā vihatā vijjā uppannā, tamo vihato āloko uppanno, yathā taṃ appamattassa ātāpino pahitattassa viharato.</p>\r\n<p class="bodytext"><a name="para55"></a><a name="para55_mn1"></a><span class="paranum">55</span>. siyā kho pana te, brāhmaṇa, evamassa – ‘ajjāpi nūna samaṇo gotamo avītarāgo avītadoso avītamoho, tasmā araññavanapatthāni pantāni senāsanāni paṭisevatī’ti. na kho panetaṃ, brāhmaṇa, evaṃ daṭṭhabbaṃ. dve kho ahaṃ, brāhmaṇa, atthavase sampassamāno araññavanapatthāni pantāni senāsanāni paṭisevāmi – attano ca diṭṭhadhammasukhavihāraṃ sampassamāno, pacchimañca janataṃ anukampamāno"ti.</p>\r\n<p class="bodytext"><a name="para56"></a><a name="para56_mn1"></a><span class="paranum">56</span>. anukampitarūpā <a name="V1.0030"></a> vatāyaṃ bhotā gotamena pacchimā janatā<a name="P1.0024"></a>, yathā taṃ arahatā sammāsambuddhena. abhikkantaṃ, bho gotama! abhikkantaṃ, bho gotama! seyyathāpi, bho gotama, nikkujjitaṃ vā ukkujjeyya, paṭicchannaṃ vā vivareyya, mūḷhassa vā maggaṃ ācikkheyya, andhakāre vā telapajjotaṃ dhāreyya – ‘cakkhumanto rūpāni dakkhantī’ti, evamevaṃ bhotā gotamena anekapariyāyena dhammo pakāsito. esāhaṃ bhavantaṃ gotamaṃ saraṇaṃ gacchāmi dhammañca bhikkhusaṅghañca. upāsakaṃ maṃ bhavaṃ gotamo dhāretu ajjatagge pāṇupetaṃ saraṇaṃ gata"nti.</p>\r\n<p class="centered">bhayabheravasuttaṃ niṭṭhitaṃ catutthaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-54-55-56-');
INSERT INTO pages VALUES(3068,'mula_ma_01',29,replace(replace('<p class="subhead">5. anaṅgaṇasuttaṃ</p>\r\n<p class="bodytext"><a name="para57"></a><a name="para57_mn1"></a><span class="paranum">57</span>. evaṃ <a name="M1.0029"></a><a name="V1.0031"></a><a name="T1.0042"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho āyasmā sāriputto bhikkhū āmantesi – "āvuso, bhikkhave"ti. "āvuso"ti kho te bhikkhū āyasmato sāriputtassa paccassosuṃ. āyasmā sāriputto etadavoca –</p>\r\n<p class="bodytext">cattārome, āvuso, puggalā santo saṃvijjamānā lokasmiṃ. katame cattāro? idhāvuso, ekacco puggalo sāṅgaṇova samāno ‘atthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ nappajānāti. idha panāvuso, ekacco puggalo sāṅgaṇova samāno ‘atthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ pajānāti. idhāvuso, ekacco puggalo anaṅgaṇova samāno ‘natthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ nappajānāti. idha panāvuso, ekacco puggalo anaṅgaṇova samāno ‘natthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ pajānāti. tatrāvuso, yvāyaṃ puggalo sāṅgaṇova samāno ‘atthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ nappajānāti, ayaṃ imesaṃ dvinnaṃ puggalānaṃ sāṅgaṇānaṃyeva sataṃ hīnapuriso akkhāyati. tatrāvuso, yvāyaṃ puggalo sāṅgaṇova samāno ‘atthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ pajānāti, ayaṃ imesaṃ dvinnaṃ puggalānaṃ sāṅgaṇānaṃyeva sataṃ seṭṭhapuriso akkhāyati<a name="T1.0043"></a>. tatrāvuso, yvāyaṃ puggalo anaṅgaṇova <a name="P1.0025"></a> samāno ‘natthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ nappajānāti, ayaṃ imesaṃ dvinnaṃ puggalānaṃ anaṅgaṇānaṃyeva sataṃ hīnapuriso akkhāyati. tatrāvuso, yvāyaṃ puggalo anaṅgaṇova samāno ‘natthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ pajānāti, ayaṃ imesaṃ dvinnaṃ puggalānaṃ anaṅgaṇānaṃyeva sataṃ seṭṭhapuriso akkhāyatī"ti.</p>\r\n<p class="bodytext"><a name="para58"></a><a name="para58_mn1"></a><span class="paranum">58</span>. evaṃ vutte, āyasmā mahāmoggallāno āyasmantaṃ sāriputtaṃ etadavoca –</p>\r\n<p class="bodytext">ko nu kho, āvuso sāriputta, hetu ko paccayo yenimesaṃ dvinnaṃ puggalānaṃ sāṅgaṇānaṃyeva sataṃ eko hīnapuriso akkhāyati, eko seṭṭhapuriso akkhāyati? ko panāvuso <a name="V1.0032"></a> sāriputta, hetu</p>\r\n','\r',char(13)),'\n',char(10)),'-57-58-');
INSERT INTO pages VALUES(3069,'mula_ma_01',30,replace(replace('<p class="bodytext">ko <a name="M1.0030"></a> paccayo yenimesaṃ dvinnaṃ puggalānaṃ anaṅgaṇānaṃyeva sataṃ eko hīnapuriso akkhāyati, eko seṭṭhapuriso akkhāyatī"ti?</p>\r\n<p class="bodytext"><a name="para59"></a><a name="para59_mn1"></a><span class="paranum">59</span>. tatrāvuso, yvāyaṃ puggalo sāṅgaṇova samāno ‘atthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ nappajānāti, tassetaṃ pāṭikaṅkhaṃ – na chandaṃ janessati na vāyamissati na vīriyaṃ ārabhissati tassaṅgaṇassa pahānāya, so sarāgo sadoso samoho sāṅgaṇo saṃkiliṭṭhacitto kālaṃ karissati. seyyathāpi, āvuso, kaṃsapāti ābhatā āpaṇā vā kammārakulā vā rajena ca malena ca pariyonaddhā. tamenaṃ sāmikā na ceva paribhuñjeyyuṃ na ca pariyodapeyyuṃ <span class="note">[pariyodāpeyyuṃ (?)]</span>, rajāpathe ca naṃ nikkhipeyyuṃ. evañhi sā, āvuso, kaṃsapāti aparena samayena saṃkiliṭṭhatarā <a name="T1.0044"></a> assa malaggahitā"ti? "evamāvuso"ti. "evameva kho, āvuso, yvāyaṃ puggalo sāṅgaṇova samāno ‘atthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ nappajānāti, tassetaṃ pāṭikaṅkhaṃ – na chandaṃ janessati na vāyamissati na vīriyaṃ ārabhissati tassaṅgaṇassa pahānāya, so sarāgo sadoso samoho sāṅgaṇo saṃkiliṭṭhacitto kālaṃ karissati.</p>\r\n<p class="bodytext">tatrāvuso, yvāyaṃ puggalo sāṅgaṇova samāno ‘atthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ pajānāti, tassetaṃ pāṭikaṅkhaṃ – chandaṃ janessati vāyamissati vīriyaṃ ārabhissati tassaṅgaṇassa pahānāya, so arāgo adoso amoho anaṅgaṇo asaṃkiliṭṭhacitto kālaṃ karissati. seyyathāpi, āvuso, kaṃsapāti ābhatā āpaṇā vā kammārakulā vā rajena ca malena ca pariyonaddhā. tamenaṃ sāmikā paribhuñjeyyuñceva pariyodapeyyuñca, na ca naṃ rajāpathe nikkhipeyyuṃ. evañhi <a name="P1.0026"></a> sā, āvuso, kaṃsapāti aparena samayena parisuddhatarā assa pariyodātā"ti? "evamāvuso"ti. "evameva kho, āvuso, yvāyaṃ puggalo sāṅgaṇova samāno ‘atthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ pajānāti, tassetaṃ pāṭikaṅkhaṃ – chandaṃ janessati vāyamissati vīriyaṃ ārabhissati tassaṅgaṇassa pahānāya, so arāgo adoso amoho anaṅgaṇo asaṃkiliṭṭhacitto kālaṃ karissati.</p>\r\n','\r',char(13)),'\n',char(10)),'-59-');
INSERT INTO pages VALUES(3070,'mula_ma_01',31,replace(replace('<p class="bodytext">tatrāvuso<a name="M1.0031"></a>, yvāyaṃ puggalo anaṅgaṇova samāno ‘natthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ nappajānāti, tassetaṃ pāṭikaṅkhaṃ <a name="T1.0045"></a> – subhanimittaṃ manasi karissati, tassa subhanimittassa manasikārā rāgo cittaṃ anuddhaṃsessati, so sarāgo sadoso samoho sāṅgaṇo saṃkiliṭṭhacitto <a name="V1.0033"></a> kālaṃ karissati. seyyathāpi, āvuso, kaṃsapāti ābhatā āpaṇā vā kammārakulā vā parisuddhā pariyodātā. tamenaṃ sāmikā na ceva paribhuñjeyyuṃ na ca pariyodapeyyuṃ, rajāpathe ca naṃ nikkhipeyyuṃ. evañhi sā, āvuso, kaṃsapāti aparena samayena saṃkiliṭṭhatarā assa malaggahitā"ti? "evamāvuso"ti. "evameva kho, āvuso, yvāyaṃ puggalo anaṅgaṇova samāno ‘natthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ nappajānāti, tassetaṃ pāṭikaṅkhaṃ – subhanimittaṃ manasi karissati, tassa subhanimittassa manasikārā rāgo cittaṃ anuddhaṃsessati,so sarāgo sadoso samoho sāṅgaṇo saṃkiliṭṭhacittokālaṃkarissati.</p>\r\n<p class="bodytext">tatrāvuso, yvāyaṃ puggalo anaṅgaṇova samāno ‘natthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ pajānāti, tassetaṃ pāṭikaṅkhaṃ – subhanimittaṃ na manasi karissati, tassa subhanimittassa amanasikārā rāgo cittaṃ nānuddhaṃsessati, so arāgo adoso amoho anaṅgaṇo asaṃkiliṭṭhacitto kālaṃ karissati. seyyathāpi, āvuso, kaṃsapāti ābhatā āpaṇā vā kammārakulā vā parisuddhā pariyodātā. tamenaṃ sāmikā paribhuñjeyyuñceva pariyodapeyyuñca, na ca naṃ rajāpathe nikkhipeyyuṃ. evañhi sā, āvuso, kaṃsapāti aparena samayena parisuddhatarā assa pariyodātā"ti? "evamāvuso"ti. "evameva <a name="T1.0046"></a> kho, āvuso, yvāyaṃ puggalo anaṅgaṇova samāno ‘natthi me ajjhattaṃ aṅgaṇa’nti yathābhūtaṃ pajānāti, tassetaṃ pāṭikaṅkhaṃ – subhanimittaṃ na manasi karissati, tassa subhanimittassa amanasikārā rāgo cittaṃ nānuddhaṃsessati, so arāgo adoso amoho anaṅgaṇo asaṃkiliṭṭhacitto kālaṃ karissati.</p>\r\n<p class="bodytext">ayaṃ kho, āvuso moggallāna<a name="P1.0027"></a>, hetu ayaṃ paccayo yenimesaṃ dvinnaṃ puggalānaṃ sāṅgaṇānaṃyeva sataṃ eko hīnapuriso akkhāyati, eko seṭṭhapuriso akkhāyati. ayaṃ panāvuso moggallāna, hetu</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3071,'mula_ma_01',32,replace(replace('<p class="bodytext">ayaṃ <a name="M1.0032"></a> paccayo yenimesaṃ dvinnaṃ puggalānaṃ anaṅgaṇānaṃyeva sataṃ eko hīnapuriso akkhāyati, eko seṭṭhapuriso akkhāyatī"ti.</p>\r\n<p class="bodytext"><a name="para60"></a><a name="para60_mn1"></a><span class="paranum">60</span>. aṅgaṇaṃ aṅgaṇanti, āvuso, vuccati. kissa nu kho etaṃ, āvuso, adhivacanaṃ yadidaṃ aṅgaṇa"nti? "pāpakānaṃ kho etaṃ, āvuso, akusalānaṃ icchāvacarānaṃ adhivacanaṃ, yadidaṃ aṅgaṇa"nti.</p>\r\n<p class="bodytext">ṭhānaṃ <a name="V1.0034"></a> kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘āpattiñca vata āpanno assaṃ, na ca maṃ bhikkhū jāneyyuṃ āpattiṃ āpanno’ti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ taṃ bhikkhuṃ bhikkhū jāneyyuṃ – ‘āpattiṃ āpanno’ti. ‘jānanti maṃ bhikkhū āpattiṃ āpanno’ti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ <a name="T1.0047"></a> kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘āpattiñca vata āpanno assaṃ, anuraho maṃ bhikkhū codeyyuṃ, no saṅghamajjhe’ti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ taṃ bhikkhuṃ bhikkhū saṅghamajjhe codeyyuṃ, no anuraho. ‘saṅghamajjhe maṃ bhikkhū codenti, no anuraho’ti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘āpattiñca vata āpanno assaṃ, sappaṭipuggalo maṃ codeyya, no appaṭipuggalo’ti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ taṃ bhikkhuṃ appaṭipuggalo codeyya, no sappaṭipuggalo. ‘appaṭipuggalo maṃ codeti, no sappaṭipuggalo’ti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘aho vata mameva satthā paṭipucchitvā paṭipucchitvā bhikkhūnaṃ dhammaṃ deseyya, na aññaṃ bhikkhuṃ satthā paṭipucchitvā paṭipucchitvā bhikkhūnaṃ dhammaṃ deseyyā’ti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ aññaṃ bhikkhuṃ satthā</p>\r\n','\r',char(13)),'\n',char(10)),'-60-');
INSERT INTO pages VALUES(3072,'mula_ma_01',33,replace(replace('<p class="bodytext">paṭipucchitvā <a name="M1.0033"></a> paṭipucchitvā bhikkhūnaṃ dhammaṃ deseyya, na taṃ bhikkhuṃ <a name="P1.0028"></a> satthā <a name="T1.0048"></a> paṭipucchitvā paṭipucchitvā bhikkhūnaṃ dhammaṃ deseyya. ‘aññaṃ bhikkhuṃ satthā paṭipucchitvā paṭipucchitvā bhikkhūnaṃ dhammaṃ deseti, na maṃ satthā paṭipucchitvā paṭipucchitvā bhikkhūnaṃ dhammaṃ desetī’ti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ <a name="V1.0035"></a> kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘aho vata mameva bhikkhū purakkhatvā purakkhatvā gāmaṃ bhattāya paviseyyuṃ, na aññaṃ bhikkhuṃ bhikkhū purakkhatvā purakkhatvā gāmaṃ bhattāya paviseyyu’nti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ aññaṃ bhikkhuṃ bhikkhū purakkhatvā purakkhatvā gāmaṃ bhattāya paviseyyuṃ, na taṃ bhikkhuṃ bhikkhū purakkhatvā purakkhatvā gāmaṃ bhattāya paviseyyuṃ. ‘aññaṃ bhikkhuṃ bhikkhū purakkhatvā purakkhatvā gāmaṃ bhattāya pavisanti, na maṃ bhikkhū purakkhatvā purakkhatvā gāmaṃ bhattāya pavisantī’ti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘aho vata ahameva labheyyaṃ bhattagge aggāsanaṃ aggodakaṃ aggapiṇḍaṃ, na añño bhikkhu labheyya bhattagge aggāsanaṃ aggodakaṃ aggapiṇḍa’nti. ṭhānaṃ kho <a name="T1.0049"></a> panetaṃ, āvuso, vijjati yaṃ añño bhikkhu labheyya bhattagge aggāsanaṃ aggodakaṃ aggapiṇḍaṃ, na so bhikkhu labheyya bhattagge aggāsanaṃ aggodakaṃ aggapiṇḍaṃ. ‘añño bhikkhu labhati bhattagge aggāsanaṃ aggodakaṃ aggapiṇḍaṃ, nāhaṃ labhāmi bhattagge aggāsanaṃ aggodakaṃ aggapiṇḍa’nti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘aho vata ahameva bhattagge bhuttāvī anumodeyyaṃ, na añño bhikkhu bhattagge bhuttāvī anumodeyyā’ti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ añño bhikkhu bhattagge bhuttāvī anumodeyya, na so bhikkhu bhattagge bhuttāvī anumodeyya. ‘añño bhikkhu bhattagge</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3073,'mula_ma_01',34,replace(replace('<p class="bodytext">bhuttāvī <a name="M1.0034"></a> anumodati, nāhaṃ bhattagge bhuttāvī anumodāmī’ti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘aho vata ahameva ārāmagatānaṃ bhikkhūnaṃ dhammaṃ deseyyaṃ, na añño bhikkhu ārāmagatānaṃ bhikkhūnaṃ dhammaṃ deseyyā’ti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ añño bhikkhu <a name="V1.0036"></a> ārāmagatānaṃ bhikkhūnaṃ dhammaṃ deseyya, na so bhikkhu ārāmagatānaṃ <a name="P1.0029"></a> bhikkhūnaṃ dhammaṃ deseyya. ‘añño bhikkhu ārāmagatānaṃ bhikkhūnaṃ dhammaṃ <a name="T1.0050"></a> deseti, nāhaṃ ārāmagatānaṃ bhikkhūnaṃ dhammaṃ desemī’ti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘aho vata ahameva ārāmagatānaṃ bhikkhunīnaṃ dhammaṃ deseyyaṃ…pe… upāsakānaṃ dhammaṃ deseyyaṃ…pe… upāsikānaṃ dhammaṃ deseyyaṃ, na añño bhikkhu ārāmagatānaṃ upāsikānaṃ dhammaṃ deseyyā’ti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ añño bhikkhu ārāmagatānaṃ upāsikānaṃ dhammaṃ deseyya, na so bhikkhu ārāmagatānaṃ upāsikānaṃ dhammaṃ deseyya. ‘añño bhikkhu ārāmagatānaṃ upāsikānaṃ dhammaṃ deseti, nāhaṃ ārāmagatānaṃ upāsikānaṃ dhammaṃ desemī’ti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘aho vata mameva bhikkhū sakkareyyuṃ garuṃ kareyyuṃ <span class="note">[garukareyyuṃ (sī. syā. pī.)]</span> māneyyuṃ pūjeyyuṃ, na aññaṃ bhikkhuṃ bhikkhū sakkareyyuṃ garuṃ kareyyuṃ māneyyuṃ pūjeyyu’nti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ aññaṃ bhikkhuṃ bhikkhū sakkareyyuṃ garuṃ kareyyuṃ māneyyuṃ pūjeyyuṃ, na taṃ bhikkhuṃ bhikkhū sakkareyyuṃ garuṃ kareyyuṃ māneyyuṃ pūjeyyuṃ. ‘aññaṃ bhikkhuṃ bhikkhū sakkaronti</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3074,'mula_ma_01',35,replace(replace('<p class="bodytext">garuṃ <a name="M1.0035"></a> karonti mānenti pūjenti<a name="T1.0051"></a>, na maṃ bhikkhū sakkaronti garuṃ karonti mānenti pūjentī’ti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘aho vata mameva bhikkhuniyo…pe… upāsakā…pe… upāsikā sakkareyyuṃ garuṃ kareyyuṃ māneyyuṃ pūjeyyuṃ, na aññaṃ bhikkhuṃ upāsikā sakkareyyuṃ garuṃ kareyyuṃ māneyyuṃ pūjeyyu’nti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ aññaṃ bhikkhuṃ upāsikā sakkareyyuṃ garuṃ kareyyuṃ māneyyuṃ pūjeyyuṃ, na taṃ bhikkhuṃ upāsikā sakkareyyuṃ garuṃ <a name="V1.0037"></a> kareyyuṃ māneyyuṃ pūjeyyuṃ. ‘aññaṃ bhikkhuṃ upāsikā sakkaronti garuṃ karonti mānenti pūjenti, na maṃ upāsikā sakkaronti garuṃ karonti mānenti pūjentī’ti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘aho vata ahameva lābhī assaṃ paṇītānaṃ cīvarānaṃ, na añño bhikkhu lābhī assa paṇītānaṃ cīvarāna’nti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ añño <a name="P1.0030"></a> bhikkhu lābhī assa paṇītānaṃ cīvarānaṃ, na so bhikkhu lābhī assa paṇītānaṃ cīvarānaṃ. ‘añño bhikkhu lābhī <span class="note">[lābhī assa (ka.)]</span> paṇītānaṃ cīvarānaṃ, nāhaṃ lābhī <a name="T1.0052"></a> <span class="note">[lābhī assaṃ (ka.)]</span> paṇītānaṃ cīvarāna’nti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ idhekaccassa bhikkhuno evaṃ icchā uppajjeyya – ‘aho vata ahameva lābhī assaṃ paṇītānaṃ piṇḍapātānaṃ…pe… paṇītānaṃ senāsanānaṃ…pe… paṇītānaṃ gilānappaccayabhesajjaparikkhārānaṃ, na añño bhikkhu lābhī assa paṇītānaṃ gilānappaccayabhesajjaparikkhārāna’nti. ṭhānaṃ kho panetaṃ, āvuso, vijjati yaṃ añño bhikkhu lābhī assa paṇītānaṃ gilānappaccayabhesajjaparikkhārānaṃ, na so bhikkhu lābhī assa paṇītānaṃ gilānappaccayabhesajjaparikkhārānaṃ. ‘añño</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3075,'mula_ma_01',36,replace(replace('<p class="bodytext">bhikkhu <a name="M1.0036"></a> lābhī <span class="note">[lābhī assa (ka.)]</span> paṇītānaṃ gilānappaccayabhesajjaparikkhārānaṃ, nāhaṃ lābhī <span class="note">[lābhī assaṃ (ka.)]</span> paṇītānaṃ gilānappaccayabhesajjaparikkhārāna’nti – iti so kupito hoti appatīto. yo ceva kho, āvuso, kopo yo ca appaccayo – ubhayametaṃ aṅgaṇaṃ.</p>\r\n<p class="bodytext">imesaṃ kho etaṃ, āvuso, pāpakānaṃ akusalānaṃ icchāvacarānaṃ adhivacanaṃ, yadidaṃ aṅgaṇa"nti.</p>\r\n<p class="bodytext"><a name="para61"></a><a name="para61_mn1"></a><span class="paranum">61</span>. yassa kassaci, āvuso, bhikkhuno ime pāpakā akusalā icchāvacarā appahīnā dissanti ceva sūyanti ca, kiñcāpi so hoti āraññiko pantasenāsano piṇḍapātiko <a name="V1.0038"></a> sapadānacārī paṃsukūliko lūkhacīvaradharo, atha kho naṃ sabrahmacārī na ceva sakkaronti na garuṃ karonti na mānenti na pūjenti. taṃ kissa hetu? te hi tassa <a name="T1.0053"></a> āyasmato pāpakā akusalā icchāvacarā appahīnā dissanti ceva sūyanti ca. seyyathāpi, āvuso, kaṃsapāti ābhatā āpaṇā vā kammārakulā vā parisuddhā pariyodātā. tamenaṃ sāmikā ahikuṇapaṃ vā kukkurakuṇapaṃ vā manussakuṇapaṃ vā racayitvā aññissā kaṃsapātiyā paṭikujjitvā antarāpaṇaṃ paṭipajjeyyuṃ. tamenaṃ jano disvā evaṃ vadeyya – ‘ambho, kimevidaṃ harīyati jaññajaññaṃ viyā’ti? tamenaṃ uṭṭhahitvā apāpuritvā <span class="note">[avāpuritvā (sī.)]</span> olokeyya. tassa sahadassanena amanāpatā ca saṇṭhaheyya, pāṭikulyatā <span class="note">[paṭikūlatā (ka.), pāṭikūlyatā (syā.)]</span> ca saṇṭhaheyya, jegucchatā ca <span class="note">[jegucchitā ca (pī. ka.)]</span> saṇṭhaheyya, jighacchitānampi na bhottukamyatā assa, pageva suhitānaṃ. evameva kho, āvuso, yassa kassaci bhikkhuno ime pāpakā akusalā icchāvacarā appahīnā dissanti ceva sūyanti ca, kiñcāpi so hoti āraññiko pantasenāsano piṇḍapātiko sapadānacārī paṃsukūliko lūkhacīvaradharo, atha kho naṃ sabrahmacārī na ceva sakkaronti na garuṃ karonti <a name="P1.0031"></a> na mānenti na pūjenti. taṃ kissa hetu? te hi tassa āyasmato pāpakā akusalā icchāvacarā appahīnā dissanti ceva sūyanti ca.</p>\r\n<p class="bodytext"><a name="para62"></a><a name="para62_mn1"></a><span class="paranum">62</span>. yassa kassaci, āvuso, bhikkhuno ime pāpakā akusalā icchāvacarā pahīnā dissanti ceva sūyanti ca, kiñcāpi so hoti</p>\r\n','\r',char(13)),'\n',char(10)),'-61-62-');
INSERT INTO pages VALUES(3076,'mula_ma_01',37,replace(replace('<p class="bodytext">gāmantavihārī <a name="M1.0037"></a> nemantaniko gahapaticīvaradharo, atha kho naṃ sabrahmacārī sakkaronti garuṃ karonti mānenti pūjenti. taṃ kissa hetu<a name="T1.0054"></a>? te hi tassa āyasmato pāpakā akusalā icchāvacarā pahīnā dissanti ceva sūyanti ca. seyyathāpi, āvuso, kaṃsapāti ābhatā āpaṇā vā kammārakulā vā parisuddhā pariyodātā. tamenaṃ sāmikā sālīnaṃ odanaṃ vicitakāḷakaṃ <span class="note">[vicinitakāḷakaṃ (ka.)]</span> anekasūpaṃ anekabyañjanaṃ racayitvā aññissā kaṃsapātiyā paṭikujjitvā antarāpaṇaṃ paṭipajjeyyuṃ. tamenaṃ jano disvā evaṃ vadeyya – ‘ambho, kimevidaṃ harīyati jaññajaññaṃ viyā’ti? tamenaṃ uṭṭhahitvā apāpuritvā olokeyya. tassa saha dassanena manāpatā ca saṇṭhaheyya, appāṭikulyatā ca saṇṭhaheyya, ajegucchatā ca saṇṭhaheyya, suhitānampi bhottukamyatā assa, pageva jighacchitānaṃ. evameva kho, āvuso, yassa kassaci bhikkhuno ime pāpakā akusalā icchāvacarā pahīnā dissanti ceva sūyanti ca, kiñcāpi so hoti gāmantavihārī nemantaniko gahapaticīvaradharo, atha kho naṃ sabrahmacārī sakkaronti garuṃ karonti mānenti pūjenti<a name="V1.0039"></a>. taṃ kissa hetu? te hi tassa āyasmato pāpakā akusalā icchāvacarā pahīnā dissanti ceva sūyanti cā"ti.</p>\r\n<p class="bodytext"><a name="para63"></a><a name="para63_mn1"></a><span class="paranum">63</span>. evaṃ vutte, āyasmā mahāmoggallāno āyasmantaṃ sāriputtaṃ etadavoca – "upamā maṃ, āvuso sāriputta, paṭibhātī"ti. "paṭibhātu taṃ, āvuso moggallānā"ti. "ekamidāhaṃ, āvuso, samayaṃ rājagahe viharāmi giribbaje. atha khvāhaṃ, āvuso, pubbaṇhasamayaṃ nivāsetvā <a name="T1.0055"></a> pattacīvaramādāya rājagahaṃ piṇḍāya pāvisiṃ. tena kho pana samayena samīti yānakāraputto rathassa nemiṃ tacchati. tamenaṃ paṇḍuputto ājīvako purāṇayānakāraputto paccupaṭṭhito hoti. atha kho, āvuso, paṇḍuputtassa ājīvakassa purāṇayānakāraputtassa evaṃ cetaso parivitakko udapādi – ‘aho vatāyaṃ samīti yānakāraputto imissā nemiyā imañca vaṅkaṃ imañca jimhaṃ imañca dosaṃ taccheyya, evāyaṃ nemi apagatavaṅkā apagatajimhā apagatadosā suddhā assa <span class="note">[suddhāssa (sī. pī.), suddhā (ka.)]</span> sāre patiṭṭhitā’ti<a name="P1.0032"></a>. yathā yathā kho, āvuso, paṇḍuputtassa ājīvakassa purāṇayānakāraputtassa cetaso parivitakko hoti, tathā</p>\r\n','\r',char(13)),'\n',char(10)),'-63-');
INSERT INTO pages VALUES(3077,'mula_ma_01',38,replace(replace('<p class="bodytext">tathā <a name="M1.0038"></a> samīti yānakāraputto tassā nemiyā tañca vaṅkaṃ tañca jimhaṃ tañca dosaṃ tacchati. atha kho, āvuso, paṇḍuputto ājīvako purāṇayānakāraputto attamano attamanavācaṃ nicchāresi – ‘hadayā hadayaṃ maññe aññāya tacchatī’ti.</p>\r\n<p class="bodytext">evameva kho, āvuso, ye te puggalā assaddhā, jīvikatthā na saddhā agārasmā anagāriyaṃ pabbajitā, saṭhā māyāvino ketabino <span class="note">[keṭubhino (bahūsu)]</span> uddhatā unnaḷā capalā mukharā vikiṇṇavācā, indriyesu aguttadvārā, bhojane amattaññuno, jāgariyaṃ ananuyuttā, sāmaññe anapekkhavanto, sikkhāya na tibbagāravā, bāhulikā sāthalikā, okkamane pubbaṅgamā, paviveke nikkhittadhurā, kusītā hīnavīriyā muṭṭhassatī asampajānā asamāhitā <a name="T1.0056"></a> vibbhantacittā duppaññā eḷamūgā, tesaṃ āyasmā sāriputto iminā dhammapariyāyena hadayā hadayaṃ maññe aññāya tacchati.</p>\r\n<p class="bodytext">ye pana te kulaputtā saddhā agārasmā anagāriyaṃ pabbajitā, asaṭhā amāyāvino aketabino anuddhatā anunnaḷā acapalā amukharā avikiṇṇavācā, indriyesu guttadvārā, bhojane mattaññuno, jāgariyaṃ anuyuttā, sāmaññe apekkhavanto, sikkhāya tibbagāravā, na bāhulikā <a name="V1.0040"></a> na sāthalikā, okkamane nikkhittadhurā, paviveke pubbaṅgamā, āraddhavīriyā pahitattā upaṭṭhitassatī sampajānā samāhitā ekaggacittā paññavanto aneḷamūgā, te āyasmato sāriputtassa imaṃ dhammapariyāyaṃ sutvā pivanti maññe, ghasanti maññe vacasā ceva manasā ca – ‘sādhu vata, bho, sabrahmacārī akusalā vuṭṭhāpetvā kusale patiṭṭhāpetī’ti. seyyathāpi, āvuso, itthī vā puriso vā daharo yuvā maṇḍanakajātiko sīsaṃnhāto uppalamālaṃ vā vassikamālaṃ vā atimuttakamālaṃ <span class="note">[adhimuttakamālaṃ (syā.)]</span> vā labhitvā ubhohi hatthehi paṭiggahetvā uttamaṅge sirasmiṃ patiṭṭhapeyya, evameva kho, āvuso, ye te kulaputtā saddhā agārasmā anagāriyaṃ pabbajitā, asaṭhā amāyāvino aketabino anuddhatā anunnaḷā acapalā amukharā avikiṇṇavācā, indriyesu guttadvārā, bhojane mattaññuno, jāgariyaṃ anuyuttā, sāmaññe apekkhavanto, sikkhāya tibbagāravā, na <a name="T1.0057"></a> bāhulikā na sāthalikā, okkamane nikkhittadhurā, paviveke pubbaṅgamā, āraddhavīriyā pahitattā</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3078,'mula_ma_01',39,replace(replace('<p class="bodytext">upaṭṭhitassatī <a name="M1.0039"></a> sampajānā samāhitā ekaggacittā paññavanto aneḷamūgā, te āyasmato sāriputtassa imaṃ dhammapariyāyaṃ sutvā pivanti maññe, ghasanti maññe vacasā ceva manasā ca – ‘sādhu vata, bho, sabrahmacārī akusalā vuṭṭhāpetvā kusale patiṭṭhāpetī’ti. itiha te ubho mahānāgā aññamaññassa subhāsitaṃ samanumodiṃsū"ti.</p>\r\n<p class="centered">anaṅgaṇasuttaṃ niṭṭhitaṃ pañcamaṃ.</p>\r\n<p class="subhead">6. ākaṅkheyyasuttaṃ</p>\r\n<p class="bodytext"><a name="para64"></a><a name="para64_mn1"></a><span class="paranum">64</span>. evaṃ <a name="T1.0058"></a><a name="V1.0041"></a><a name="P1.0033"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext">sampannasīlā, bhikkhave, viharatha sampannapātimokkhā, pātimokkhasaṃvarasaṃvutā viharatha ācāragocarasampannā aṇumattesu vajjesu bhayadassāvino, samādāya sikkhatha sikkhāpadesu.</p>\r\n<p class="bodytext"><a name="para65"></a><a name="para65_mn1"></a><span class="paranum">65</span>. ākaṅkheyya ce, bhikkhave, bhikkhu – ‘sabrahmacārīnaṃ piyo ca assaṃ manāpo ca garu ca bhāvanīyo cā’ti <span class="note">[manāpo garubhāvaniyo cāti (sī.)]</span>, sīlesvevassa paripūrakārī ajjhattaṃ cetosamathamanuyutto anirākatajjhāno vipassanāya samannāgato brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">ākaṅkheyya ce, bhikkhave, bhikkhu – ‘lābhī assaṃ cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārāna’nti, sīlesvevassa paripūrakārī ajjhattaṃ cetosamathamanuyutto anirākatajjhāno vipassanāya samannāgato brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">ākaṅkheyya ce, bhikkhave, bhikkhu – ‘yesāhaṃ cīvarapiṇḍapātasenāsana gilānappaccayabhesajjaparikkhāraṃ paribhuñjāmi tesaṃ te kārā mahapphalā</p>\r\n','\r',char(13)),'\n',char(10)),'-64-65-');
INSERT INTO pages VALUES(3079,'mula_ma_01',40,replace(replace('<p class="bodytext">assu <a name="M1.0040"></a> mahānisaṃsā’ti, sīlesvevassa paripūrakārī ajjhattaṃ cetosamathamanuyutto anirākatajjhāno vipassanāya samannāgato brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">ākaṅkheyya <a name="T1.0059"></a> ce, bhikkhave, bhikkhu – ‘ye maṃ <span class="note">[ye me (sī. syā.)]</span> ñātī sālohitā petā kālaṅkatā <span class="note">[kālakatā (sī. syā. pī.)]</span> pasannacittā anussaranti tesaṃ taṃ mahapphalaṃ assa mahānisaṃsa’nti, sīlesvevassa paripūrakārī ajjhattaṃ cetosamathamanuyutto anirākatajjhāno vipassanāya samannāgato brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext"><a name="para66"></a><a name="para66_mn1"></a><span class="paranum">66</span>. ākaṅkheyya <a name="V1.0042"></a> ce, bhikkhave, bhikkhu – ‘aratiratisaho assaṃ, na ca maṃ arati saheyya, uppannaṃ aratiṃ abhibhuyya abhibhuyya vihareyya’nti, sīlesvevassa paripūrakārī…pe… brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">ākaṅkheyya ce, bhikkhave, bhikkhu – ‘bhayabheravasaho assaṃ, na ca maṃ bhayabheravaṃ saheyya, uppannaṃ bhayabheravaṃ abhibhuyya abhibhuyya vihareyya’nti, sīlesvevassa paripūrakārī…pe… brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">ākaṅkheyya ce, bhikkhave, bhikkhu – ‘catunnaṃ jhānānaṃ ābhicetasikānaṃ diṭṭhadhammasukhavihārānaṃ nikāmalābhī assaṃ akicchalābhī akasiralābhī’ti, sīlesvevassa paripūrakārī…pe… brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">ākaṅkheyya ce, bhikkhave, bhikkhu – ‘ye te santā vimokkhā atikkamma rūpe āruppā, te kāyena phusitvā vihareyya’nti, sīlesvevassa paripūrakārī…pe… brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext"><a name="para67"></a><a name="para67_mn1"></a><span class="paranum">67</span>. ākaṅkheyya <a name="P1.0034"></a> ce, bhikkhave, bhikkhu – ‘tiṇṇaṃ saṃyojanānaṃ parikkhayā sotāpanno assaṃ avinipātadhammo niyato sambodhiparāyaṇo’ti, sīlesvevassa paripūrakārī…pe… brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">ākaṅkheyya <a name="T1.0060"></a> ce, bhikkhave, bhikkhu – ‘tiṇṇaṃ saṃyojanānaṃ parikkhayā rāgadosamohānaṃ tanuttā sakadāgāmī assaṃ sakideva imaṃ lokaṃ āgantvā dukkhassantaṃ kareyya’nti, sīlesvevassa paripūrakārī…pe… brūhetā suññāgārānaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-66-67-');
INSERT INTO pages VALUES(3080,'mula_ma_01',41,replace(replace('<p class="bodytext">ākaṅkheyya <a name="M1.0041"></a> ce, bhikkhave, bhikkhu – ‘pañcannaṃ orambhāgiyānaṃ saṃyojanānaṃ parikkhayā opapātiko assaṃ tattha parinibbāyī anāvattidhammo tasmā lokā’ti, sīlesvevassa paripūrakārī…pe… <a name="V1.0043"></a> brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext"><a name="para68"></a><a name="para68_mn1"></a><span class="paranum">68</span>. ākaṅkheyya ce, bhikkhave, bhikkhu – ‘anekavihitaṃ iddhividhaṃ paccanubhaveyyaṃ – ekopi hutvā bahudhā assaṃ, bahudhāpi hutvā eko assaṃ, āvibhāvaṃ tirobhāvaṃ, tirokuṭṭaṃ tiropākāraṃ tiropabbataṃ asajjamāno gaccheyyaṃ, seyyathāpi ākāse, pathaviyāpi ummujjanimujjaṃ kareyyaṃ, seyyathāpi udake, udakepi abhijjamāne gaccheyyaṃ, seyyathāpi pathaviyaṃ, ākāsepi pallaṅkena kameyyaṃ, seyyathāpi pakkhī sakuṇo, imepi candimasūriye evaṃmahiddhike evaṃmahānubhāve pāṇinā parāmaseyyaṃ parimajjeyyaṃ, yāva brahmalokāpi kāyena vasaṃ vatteyya’nti, sīlesvevassa paripūrakārī…pe… brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">ākaṅkheyya <a name="T1.0061"></a> ce, bhikkhave, bhikkhu – ‘dibbāya sotadhātuyā visuddhāya atikkantamānusikāya ubho sadde suṇeyyaṃ – dibbe ca mānuse ca ye dūre santike cā’ti, sīlesvevassa paripūrakārī…pe… brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">ākaṅkheyya ce, bhikkhave, bhikkhu – ‘parasattānaṃ parapuggalānaṃ cetasā ceto paricca pajāneyyaṃ – sarāgaṃ vā cittaṃ sarāgaṃ cittanti pajāneyyaṃ, vītarāgaṃ vā cittaṃ vītarāgaṃ cittanti pajāneyyaṃ, sadosaṃ vā cittaṃ sadosaṃ cittanti pajāneyyaṃ, vītadosaṃ vā cittaṃ vītadosaṃ cittanti pajāneyyaṃ, samohaṃ vā cittaṃ samohaṃ cittanti pajāneyyaṃ, vītamohaṃ vā cittaṃ vītamohaṃ cittanti pajāneyyaṃ, saṃkhittaṃ vā cittaṃ saṃkhittaṃ cittanti pajāneyyaṃ, vikkhittaṃ vā cittaṃ vikkhittaṃ cittanti pajāneyyaṃ, mahaggataṃ vā cittaṃ mahaggataṃ cittanti pajāneyyaṃ, amahaggataṃ vā cittaṃ amahaggataṃ cittanti pajāneyyaṃ, sauttaraṃ vā cittaṃ sauttaraṃ cittanti pajāneyyaṃ, anuttaraṃ vā cittaṃ anuttaraṃ cittanti pajāneyyaṃ, samāhitaṃ vā cittaṃ samāhitaṃ <a name="P1.0035"></a> cittanti pajāneyyaṃ, asamāhitaṃ vā cittaṃ asamāhitaṃ cittanti pajāneyyaṃ, vimuttaṃ vā cittaṃ vimuttaṃ cittanti pajāneyyaṃ, avimuttaṃ vā cittaṃ avimuttaṃ cittanti <a name="V1.0044"></a> pajāneyya’nti, sīlesvevassa paripūrakārī…pe… brūhetā suññāgārānaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-68-');
INSERT INTO pages VALUES(3081,'mula_ma_01',42,replace(replace('<p class="bodytext">ākaṅkheyya <a name="T1.0062"></a><a name="M1.0042"></a> ce, bhikkhave, bhikkhu – ‘anekavihitaṃ pubbenivāsaṃ anussareyyaṃ, seyyathidaṃ – ekampi jātiṃ dvepi jātiyo tissopi jātiyo catassopi jātiyo pañcapi jātiyo dasapi jātiyo vīsampi jātiyo tiṃsampi jātiyo cattālīsampi jātiyo paññāsampi jātiyo jātisatampi jātisahassampi jāti satasahassampi anekepi saṃvaṭṭakappe anekepi vivaṭṭakappe anekepi saṃvaṭṭavivaṭṭakappe – amutrāsiṃ evaṃnāmo evaṃgotto evaṃvaṇṇo evamāhāro evaṃsukhadukkhappaṭisaṃvedī evamāyupariyanto, so tato cuto amutra udapādiṃ, tatrāpāsiṃ evaṃnāmo evaṃgotto evaṃvaṇṇo evamāhāro evaṃsukhadukkhappaṭisaṃvedī evamāyupariyanto, so tato cuto idhūpapannoti. iti sākāraṃ sauddesaṃ anekavihitaṃ pubbenivāsaṃ anussareyya’nti, sīlesvevassa paripūrakārī…pe… brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">ākaṅkheyya ce, bhikkhave, bhikkhu – ‘dibbena cakkhunā visuddhena atikkantamānusakena satte passeyyaṃ cavamāne upapajjamāne hīne paṇīte suvaṇṇe dubbaṇṇe sugate duggate yathākammūpage satte pajāneyyaṃ – ime vata bhonto sattā kāyaduccaritena samannāgatā vacīduccaritena samannāgatā manoduccaritena samannāgatā ariyānaṃ upavādakā micchādiṭṭhikā micchādiṭṭhikammasamādānā, te kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapannā, ime vā pana bhonto <a name="T1.0063"></a> sattā kāyasucaritena samannāgatā vacīsucaritena samannāgatā manosucaritena samannāgatā ariyānaṃ anupavādakā sammādiṭṭhikā sammādiṭṭhikammasamādānā, te kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapannāti, iti dibbena cakkhunā visuddhena atikkantamānusakena satte passeyyaṃ cavamāne upapajjamāne hīne paṇīte suvaṇṇe dubbaṇṇe sugate duggate yathākammūpage satte pajāneyya’nti, sīlesvevassa paripūrakārī ajjhattaṃ cetosamathamanuyutto anirākatajjhāno vipassanāya samannāgato brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext"><a name="para69"></a><a name="para69_mn1"></a><span class="paranum">69</span>. ākaṅkheyya <a name="V1.0045"></a> ce, bhikkhave, bhikkhu – ‘āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭhevadhamme sayaṃ abhiññā sacchikatvā <a name="P1.0036"></a></p>\r\n','\r',char(13)),'\n',char(10)),'-69-');
INSERT INTO pages VALUES(3082,'mula_ma_01',43,replace(replace('<p class="bodytext">upasampajja <a name="M1.0043"></a> vihareyya’nti, sīlesvevassa paripūrakārī ajjhattaṃ cetosamathamanuyutto anirākatajjhāno vipassanāya samannāgato brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">sampannasīlā, bhikkhave, viharatha sampannapātimokkhā, pātimokkhasaṃvarasaṃvutā viharatha ācāragocarasampannā aṇumattesu vajjesu bhayadassāvino, samādāya sikkhatha sikkhāpadesū"ti – iti yaṃ taṃ vuttaṃ idametaṃ paṭicca vutta"nti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">ākaṅkheyyasuttaṃ niṭṭhitaṃ chaṭṭhaṃ.</p>\r\n<p class="subhead">7. vatthasuttaṃ</p>\r\n<p class="bodytext"><a name="para70"></a><a name="para70_mn1"></a><span class="paranum">70</span>. evaṃ <a name="T1.0064"></a><a name="V1.0046"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, vatthaṃ saṃkiliṭṭhaṃ malaggahitaṃ, tamenaṃ rajako yasmiṃ yasmiṃ raṅgajāte upasaṃhareyya – yadi nīlakāya yadi pītakāya yadi lohitakāya yadi mañjiṭṭhakāya <span class="note">[mañjeṭṭhakāya (sī. pī.), mañjeṭṭhikāya (syā.)]</span> durattavaṇṇamevassa aparisuddhavaṇṇamevassa. taṃ kissa hetu? aparisuddhattā, bhikkhave, vatthassa. evameva kho, bhikkhave, citte saṃkiliṭṭhe, duggati pāṭikaṅkhā. seyyathāpi, bhikkhave, vatthaṃ parisuddhaṃ pariyodātaṃ, tamenaṃ rajako yasmiṃ yasmiṃ raṅgajāte upasaṃhareyya – yadi nīlakāya yadi pītakāya yadi lohitakāya yadi mañjiṭṭhakāya – surattavaṇṇamevassa parisuddhavaṇṇamevassa. taṃ kissa hetu? parisuddhattā, bhikkhave, vatthassa. evameva kho, bhikkhave, citte asaṃkiliṭṭhe, sugati pāṭikaṅkhā.</p>\r\n<p class="bodytext"><a name="para71"></a><a name="para71_mn1"></a><span class="paranum">71</span>. katame ca, bhikkhave, cittassa upakkilesā? abhijjhāvisamalobho cittassa upakkileso, byāpādo cittassa upakkileso, kodho cittassa upakkileso, upanāho cittassa upakkileso,</p>\r\n','\r',char(13)),'\n',char(10)),'-70-71-');
INSERT INTO pages VALUES(3083,'mula_ma_01',44,replace(replace('<p class="bodytext">makkho <a name="M1.0044"></a> cittassa upakkileso, paḷāso cittassa upakkileso, issā <a name="T1.0065"></a> cittassa upakkileso, macchariyaṃ cittassa upakkileso, māyā cittassa upakkileso, sāṭheyyaṃ cittassa upakkileso, thambho cittassa upakkileso, sārambho cittassa upakkileso, māno cittassa upakkileso, atimāno cittassa upakkileso, mado cittassa <a name="P1.0037"></a> upakkileso, pamādo cittassa upakkileso.</p>\r\n<p class="bodytext"><a name="para72"></a><a name="para72_mn1"></a><span class="paranum">72</span>. sa kho so, bhikkhave, bhikkhu ‘abhijjhāvisamalobho cittassa upakkileso’ti – iti viditvā abhijjhāvisamalobhaṃ cittassa upakkilesaṃ pajahati, ‘byāpādo cittassa upakkileso’ti – iti viditvā byāpādaṃ cittassa upakkilesaṃ pajahati<a name="V1.0047"></a>, ‘kodho cittassa upakkileso’ti – iti viditvā kodhaṃ cittassa upakkilesaṃ pajahati, ‘upanāho cittassa upakkileso’ti – iti viditvā upanāhaṃ cittassa upakkilesaṃ pajahati, ‘makkho cittassa upakkileso’ti – iti viditvā makkhaṃ cittassa upakkilesaṃ pajahati, ‘paḷāso cittassa upakkileso’ti – iti viditvā paḷāsaṃ cittassa upakkilesaṃ pajahati, ‘issā cittassa upakkileso’ti – iti viditvā issaṃ cittassa upakkilesaṃ pajahati, ‘macchariyaṃ cittassa upakkileso’ti – iti viditvā macchariyaṃ cittassa upakkilesaṃ pajahati, ‘māyā cittassa upakkileso’ti – iti viditvā māyaṃ cittassa upakkilesaṃ pajahati, ‘sāṭheyyaṃ cittassa upakkileso’ti – iti viditvā sāṭheyyaṃ cittassa upakkilesaṃ pajahati, ‘thambho cittassa upakkileso’ti – iti viditvā thambhaṃ cittassa upakkilesaṃ pajahati, ‘sārambho cittassa upakkileso’ti <a name="T1.0066"></a> – iti viditvā sārambhaṃ cittassa upakkilesaṃ pajahati, ‘māno cittassa upakkileso’ti – iti viditvā mānaṃ cittassa upakkilesaṃ pajahati, ‘atimāno cittassa upakkileso’ti – iti viditvā atimānaṃ cittassa upakkilesaṃ pajahati, ‘mado cittassa upakkileso’ti – iti viditvā madaṃ cittassa upakkilesaṃ pajahati, ‘pamādo cittassa upakkileso’ti – iti viditvā pamādaṃ cittassa upakkilesaṃ pajahati.</p>\r\n<p class="bodytext"><a name="para73"></a><a name="para73_mn1"></a><span class="paranum">73</span>. yato kho <span class="note">[yato ca kho (sī. syā.)]</span>, bhikkhave, bhikkhuno ‘abhijjhāvisamalobho cittassa upakkileso’ti – iti viditvā abhijjhāvisamalobho cittassa</p>\r\n','\r',char(13)),'\n',char(10)),'-72-73-');
INSERT INTO pages VALUES(3084,'mula_ma_01',45,replace(replace('<p class="bodytext">upakkileso <a name="M1.0045"></a> pahīno hoti, ‘byāpādo cittassa upakkileso’ti – iti viditvā byāpādo cittassa upakkileso pahīno hoti, ‘kodho cittassa upakkileso’ti – iti viditvā kodho cittassa upakkileso pahīno hoti, ‘upanāho cittassa upakkileso’ti – iti viditvā upanāho cittassa upakkileso pahīno hoti, ‘makkho cittassa upakkileso’ti – iti viditvā makkho cittassa upakkileso pahīno hoti, ‘paḷāso cittassa upakkileso’ti – iti viditvā paḷāso cittassa upakkileso pahīno hoti, ‘issā cittassa upakkileso’ti – iti viditvā issā cittassa upakkileso pahīno hoti, ‘macchariyaṃ cittassa upakkileso’ti – iti viditvā macchariyaṃ cittassa upakkileso pahīno hoti, ‘māyā cittassa upakkileso’ti – iti viditvā māyā cittassa upakkileso pahīno hoti, ‘sāṭheyyaṃ cittassa upakkileso’ti <a name="T1.0067"></a> – iti viditvā sāṭheyyaṃ cittassa upakkileso pahīno hoti, ‘thambho cittassa upakkileso’ti – iti viditvā thambho cittassa upakkileso pahīno hoti, ‘sārambho cittassa upakkileso’ti – iti viditvā sārambho cittassa upakkileso pahīno <a name="V1.0048"></a> hoti, ‘māno cittassa upakkileso’ti – iti viditvā māno cittassa upakkileso pahīno hoti, ‘atimāno cittassa upakkileso’ti – iti viditvā atimāno cittassa upakkileso pahīno hoti, ‘mado cittassa upakkileso’ti – iti viditvā mado cittassa upakkileso pahīno hoti, ‘pamādo cittassa upakkileso’ti – iti viditvā pamādo cittassa upakkileso pahīno hoti.</p>\r\n<p class="bodytext"><a name="para74"></a><a name="para74_mn1"></a><span class="paranum">74</span>. so buddhe aveccappasādena samannāgato hoti – ‘itipi so bhagavā arahaṃ sammāsambuddho vijjācaraṇasampanno sugato lokavidū anuttaro purisadammasārathi satthā devamanussānaṃ buddho bhagavā’ti, dhamme aveccappasādena samannāgato hoti – ‘svākkhāto bhagavatā dhammo sandiṭṭhiko akāliko ehipassiko opaneyyiko paccattaṃ veditabbo viññūhī’ti, saṅghe aveccappasādena samannāgato hoti – ‘suppaṭipanno bhagavato sāvakasaṅgho, ujuppaṭipanno bhagavato sāvakasaṅgho, ñāyappaṭipanno bhagavato sāvakasaṅgho,</p>\r\n','\r',char(13)),'\n',char(10)),'-74-');
INSERT INTO pages VALUES(3085,'mula_ma_01',46,replace(replace('<p class="bodytext">sāmīcippaṭipanno <a name="M1.0046"></a> bhagavato sāvakasaṅgho, yadidaṃ cattāri purisayugāni, aṭṭha purisapuggalā. esa bhagavato sāvakasaṅgho āhuneyyo pāhuneyyo dakkhiṇeyyo añjalikaraṇīyo<a name="T1.0068"></a>, anuttaraṃ puññakkhettaṃ lokassā’ti.</p>\r\n<p class="bodytext"><a name="para75"></a><a name="para75_mn1"></a><span class="paranum">75</span>. yathodhi <span class="note">[yatodhi (aṭṭhakathāyaṃ pāṭhantaraṃ)]</span> kho panassa cattaṃ hoti vantaṃ muttaṃ pahīnaṃ paṭinissaṭṭhaṃ, so ‘buddhe aveccappasādena samannāgatomhī’ti labhati atthavedaṃ, labhati dhammavedaṃ, labhati dhammūpasaṃhitaṃ pāmojjaṃ. pamuditassa pīti jāyati, pītimanassa kāyo passambhati, passaddhakāyo sukhaṃ vedeti, sukhino cittaṃ samādhiyati, ‘dhamme…pe… saṅghe <a name="P1.0038"></a> aveccappasādena samannāgatomhī’ti labhati atthavedaṃ, labhati dhammavedaṃ, labhati dhammūpasaṃhitaṃ pāmojjaṃ, pamuditassa pīti jāyati, pītimanassa kāyo passambhati, passaddhakāyo sukhaṃ vedeti, sukhino cittaṃ samādhiyati. ‘yathodhi kho pana me cattaṃ vantaṃ muttaṃ pahīnaṃ paṭinissaṭṭha’nti labhati atthavedaṃ, labhati dhammavedaṃ, labhati dhammūpasaṃhitaṃ pāmojjaṃ, pamuditassa pīti jāyati, pītimanassa kāyo passambhati, passaddhakāyo sukhaṃ vedeti, sukhino cittaṃ samādhiyati.</p>\r\n<p class="bodytext"><a name="para76"></a><a name="para76_mn1"></a><span class="paranum">76</span>. sa kho so, bhikkhave, bhikkhu evaṃsīlo evaṃdhammo evaṃpañño sālīnaṃ cepi piṇḍapātaṃ bhuñjati vicitakāḷakaṃ anekasūpaṃ anekabyañjanaṃ, nevassa taṃ hoti antarāyāya. seyyathāpi, bhikkhave, vatthaṃ saṃkiliṭṭhaṃ malaggahitaṃ acchodakaṃ āgamma parisuddhaṃ hoti pariyodātaṃ<a name="V1.0049"></a>, ukkāmukhaṃ vā panāgamma jātarūpaṃ parisuddhaṃ hoti pariyodātaṃ, evameva kho, bhikkhave, bhikkhu evaṃsīlo evaṃdhammo evaṃpañño sālīnaṃ cepi piṇḍapātaṃ bhuñjati vicitakāḷakaṃ anekasūpaṃ anekabyañjanaṃ<a name="T1.0069"></a>, nevassa taṃ hoti antarāyāya.</p>\r\n<p class="bodytext"><a name="para77"></a><a name="para77_mn1"></a><span class="paranum">77</span>. so mettāsahagatena cetasā ekaṃ disaṃ pharitvā viharati, tathā dutiyaṃ, tathā tatiyaṃ, tathā catutthaṃ <span class="note">[catutthiṃ (sī. pī.)]</span>. iti uddhamadho tiriyaṃ sabbadhi sabbattatāya sabbāvantaṃ lokaṃ mettāsahagatena cetasā vipulena mahaggatena appamāṇena averena abyāpajjena pharitvā viharati, karuṇāsahagatena cetasā…pe… muditāsahagatena cetasā…pe…</p>\r\n','\r',char(13)),'\n',char(10)),'-75-76-77-');
INSERT INTO pages VALUES(3086,'mula_ma_01',47,replace(replace('<p class="bodytext">upekkhāsahagatena <a name="M1.0047"></a> cetasā ekaṃ disaṃ pharitvā viharati, tathā dutiyaṃ, tathā tatiyaṃ, tathā catutthaṃ. iti uddhamadho tiriyaṃ sabbadhi sabbattatāya sabbāvantaṃ lokaṃ upekkhāsahagatena cetasā vipulena mahaggatena appamāṇena averena abyāpajjena pharitvā viharati.</p>\r\n<p class="bodytext"><a name="para78"></a><a name="para78_mn1"></a><span class="paranum">78</span>. so ‘atthi idaṃ, atthi hīnaṃ, atthi paṇītaṃ, atthi imassa saññāgatassa uttariṃ nissaraṇa’nti pajānāti. tassa evaṃ jānato evaṃ passato kāmāsavāpi cittaṃ vimuccati, bhavāsavāpi cittaṃ vimuccati, avijjāsavāpi cittaṃ vimuccati. vimuttasmiṃ vimuttamiti ñāṇaṃ hoti. ‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti pajānāti<a name="P1.0039"></a>. ayaṃ vuccati, bhikkhave – ‘bhikkhu sināto antarena sinānenā"’ti.</p>\r\n<p class="bodytext"><a name="para79"></a><a name="para79_mn1"></a><span class="paranum">79</span>. tena kho pana samayena sundarikabhāradvājo brāhmaṇo bhagavato avidūre nisinno hoti. atha kho sundarikabhāradvājo brāhmaṇo <a name="T1.0070"></a> bhagavantaṃ etadavoca – "gacchati pana bhavaṃ gotamo bāhukaṃ nadiṃ sināyitu"nti? "kiṃ, brāhmaṇa, bāhukāya nadiyā? kiṃ bāhukā nadī karissatī"ti? "lokkhasammatā <span class="note">[lokhyasammatā (sī.), mokkhasammatā (pī.)]</span> hi, bho gotama, bāhukā nadī bahujanassa, puññasammatā hi, bho gotama, bāhukā nadī bahujanassa, bāhukāya pana nadiyā bahujano pāpakammaṃ kataṃ pavāhetī"ti. atha kho bhagavā sundarikabhāradvājaṃ brāhmaṇaṃ gāthāhi ajjhabhāsi –</p>\r\n<p class="gatha1">bāhukaṃ adhikakkañca, gayaṃ sundarikaṃ mapi <span class="note">[sundarikāmapi (sī. syā. pī.), sundarikaṃ mahiṃ (itipi)]</span>,</p>\r\n<p class="gatha2">sarassatiṃ payāgañca, atho bāhumatiṃ nadiṃ,</p>\r\n<p class="gathalast">niccampi bālo pakkhando <span class="note">[pakkhanno (sī. syā. pī.)]</span>, kaṇhakammo na sujjhati.</p>\r\n<p class="gatha1">kiṃ <a name="V1.0050"></a> sundarikā karissati, kiṃ payāgā <span class="note">[payāgo (sī. syā. pī.)]</span> kiṃ bāhukā nadī,</p>\r\n<p class="gathalast">veriṃ katakibbisaṃ naraṃ, na hi naṃ sodhaye pāpakamminaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-78-79-');
INSERT INTO pages VALUES(3087,'mula_ma_01',48,replace(replace('<p class="gatha1">suddhassa <a name="M1.0048"></a> ve sadā phaggu, suddhassuposatho sadā,</p>\r\n<p class="gatha2">suddhassa sucikammassa, sadā sampajjate vataṃ,</p>\r\n<p class="gathalast">idheva sināhi brāhmaṇa, sabbabhūtesu karohi khemataṃ.</p>\r\n<p class="gatha1">sace musā na bhaṇasi, sace pāṇaṃ na hiṃsasi,</p>\r\n<p class="gatha2">sace adinnaṃ nādiyasi, saddahāno amaccharī,</p>\r\n<p class="gathalast">kiṃ kāhasi gayaṃ gantvā, udapānopi te gayā"ti.</p>\r\n<p class="bodytext"><a name="para80"></a><a name="para80_mn1"></a><span class="paranum">80</span>. evaṃ <a name="T1.0071"></a> vutte, sundarikabhāradvājo brāhmaṇo bhagavantaṃ etadavoca – "abhikkantaṃ, bho gotama, abhikkantaṃ, bho gotama! seyyathāpi, bho gotama, nikkujjitaṃ vā ukkujjeyya, paṭicchannaṃ vā vivareyya, mūḷhassa vā maggaṃ ācikkheyya, andhakāre vā telapajjotaṃ dhāreyya – cakkhumanto rūpāni dakkhantīti, evamevaṃ bhotā gotamena anekapariyāyena dhammo pakāsito. esāhaṃ bhavantaṃ gotamaṃ saraṇaṃ gacchāmi dhammañca bhikkhusaṅghañca. labheyyāhaṃ bhoto gotamassa santike pabbajjaṃ, labheyyaṃ upasampada"nti. alattha kho sundarikabhāradvājo brāhmaṇo bhagavato santike pabbajjaṃ, alattha upasampadaṃ. acirūpasampanno <a name="P1.0040"></a> kho panāyasmā bhāradvājo eko vūpakaṭṭho appamatto ātāpī pahitatto viharanto nacirasseva – yassatthāya kulaputtā sammadeva agārasmā anagāriyaṃ pabbajanti tadanuttaraṃ – brahmacariyapariyosānaṃ diṭṭhevadhamme sayaṃ abhiññā sacchikatvā upasampajja vihāsi. "khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā"ti abbhaññāsi. aññataro kho panāyasmā bhāradvājo arahataṃ ahosīti.</p>\r\n<p class="centered">vatthasuttaṃ niṭṭhitaṃ sattamaṃ.</p>\r\n<p class="subhead">8. sallekhasuttaṃ</p>\r\n<p class="bodytext"><a name="para81"></a><a name="para81_mn1"></a><span class="paranum">81</span>. evaṃ <a name="V1.0051"></a><a name="T1.0072"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. atha kho āyasmā mahācundo sāyanhasamayaṃ paṭisallānā vuṭṭhito yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinno</p>\r\n','\r',char(13)),'\n',char(10)),'-80-81-');
INSERT INTO pages VALUES(3088,'mula_ma_01',49,replace(replace('<p class="bodytext">kho <a name="M1.0049"></a> āyasmā mahācundo bhagavantaṃ etadavoca – "yā imā, bhante, anekavihitā diṭṭhiyo loke uppajjanti – attavādapaṭisaṃyuttā vā lokavādapaṭisaṃyuttā vā – ādimeva nu kho, bhante, bhikkhuno manasikaroto evametāsaṃ diṭṭhīnaṃ pahānaṃ hoti, evametāsaṃ diṭṭhīnaṃ paṭinissaggo hotī"ti?</p>\r\n<p class="bodytext"><a name="para82"></a><a name="para82_mn1"></a><span class="paranum">82</span>. yā imā, cunda, anekavihitā diṭṭhiyo loke uppajjanti – attavādapaṭisaṃyuttā vā lokavādapaṭisaṃyuttā vā – yattha cetā diṭṭhiyo uppajjanti yattha ca anusenti yattha ca samudācaranti taṃ ‘netaṃ mama, nesohamasmi, na me so attā’ti – evametaṃ yathābhūtaṃ sammappaññā passato evametāsaṃ diṭṭhīnaṃ pahānaṃ hoti, evametāsaṃ diṭṭhīnaṃ paṭinissaggo hoti.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, cunda, vijjati yaṃ idhekacco bhikkhu vivicceva <a name="T1.0073"></a> kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja vihareyya. tassa evamassa – ‘sallekhena viharāmī’ti. na kho panete, cunda, ariyassa vinaye sallekhā vuccanti. diṭṭhadhammasukhavihārā ete <a name="P1.0041"></a> ariyassa vinaye vuccanti.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, cunda, vijjati yaṃ idhekacco bhikkhu vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja vihareyya. tassa evamassa – ‘sallekhena viharāmī’ti. na kho panete, cunda, ariyassa vinaye sallekhā vuccanti. diṭṭhadhammasukhavihārā ete ariyassa vinaye vuccanti.</p>\r\n<p class="bodytext">ṭhānaṃ <a name="V1.0052"></a> kho panetaṃ, cunda, vijjati yaṃ idhekacco bhikkhu pītiyā ca virāgā upekkhako ca vihareyya, sato ca sampajāno sukhañca kāyena paṭisaṃvedeyya, yaṃ taṃ ariyā ācikkhanti – ‘upekkhako satimā sukhavihārī’ti tatiyaṃ jhānaṃ upasampajja vihareyya. tassa evamassa – ‘sallekhena viharāmī’ti. na kho panete, cunda, ariyassa vinaye sallekhā <a name="T1.0074"></a> vuccanti. diṭṭhadhammasukhavihārā ete ariyassa vinaye vuccanti.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, cunda, vijjati yaṃ idhekacco bhikkhu sukhassa ca pahānā dukkhassa ca pahānā pubbeva somanassadomanassānaṃ atthaṅgamā adukkhamasuṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-82-');
INSERT INTO pages VALUES(3089,'mula_ma_01',50,replace(replace('<p class="bodytext">upekkhāsatipārisuddhiṃ <a name="M1.0050"></a> catutthaṃ jhānaṃ upasampajja vihareyya. tassa evamassa – ‘sallekhena viharāmī’ti. na kho panete, cunda, ariyassa vinaye sallekhā vuccanti. diṭṭhadhammasukhavihārā ete ariyassa vinaye vuccanti.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, cunda, vijjati yaṃ idhekacco bhikkhu sabbaso rūpasaññānaṃ samatikkamā, paṭighasaññānaṃ atthaṅgamā, nānattasaññānaṃ amanasikārā, ‘ananto ākāso’ti ākāsānañcāyatanaṃ upasampajja vihareyya. tassa evamassa – ‘sallekhena viharāmī’ti. na kho panete, cunda, ariyassa vinaye sallekhā vuccanti. santā ete vihārā ariyassa vinaye vuccanti.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, cunda, vijjati yaṃ idhekacco bhikkhu sabbaso ākāsānañcāyatanaṃ samatikkamma ‘anantaṃ viññāṇa’nti viññāṇañcāyatanaṃ upasampajja vihareyya. tassa evamassa – ‘sallekhena viharāmī’ti. na kho panete, cunda, ariyassa vinaye sallekhā vuccanti. santā ete vihārā ariyassa vinaye vuccanti.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, cunda, vijjati yaṃ idhekacco bhikkhu sabbaso viññāṇañcāyatanaṃ samatikkamma ‘natthi kiñcī’ti ākiñcaññāyatanaṃ upasampajja vihareyya. tassa evamassa – ‘sallekhena viharāmī’ti. na kho panete, cunda, ariyassa vinaye sallekhā <a name="T1.0075"></a> vuccanti. santā ete vihārā ariyassa vinaye vuccanti.</p>\r\n<p class="bodytext">ṭhānaṃ kho panetaṃ, cunda, vijjati yaṃ idhekacco bhikkhu sabbaso ākiñcaññāyatanaṃ samatikkamma nevasaññānāsaññāyatanaṃ upasampajja vihareyya. tassa evamassa – ‘sallekhena viharāmī’ti<a name="V1.0053"></a>. na <a name="P1.0042"></a> kho panete, cunda, ariyassa vinaye sallekhā vuccanti. santā ete vihārā ariyassa vinaye vuccanti.</p>\r\n<p class="bodytext"><a name="para83"></a><a name="para83_mn1"></a><span class="paranum">83</span>. idha kho pana vo, cunda, sallekho karaṇīyo. ‘pare vihiṃsakā bhavissanti, mayamettha avihiṃsakā bhavissāmā’ti sallekho karaṇīyo. ‘pare pāṇātipātī bhavissanti, mayamettha pāṇātipātā paṭiviratā bhavissāmā’ti sallekho karaṇīyo. ‘pare adinnādāyī bhavissanti, mayamettha adinnādānā paṭiviratā bhavissāmā’ti sallekho karaṇīyo.</p>\r\n','\r',char(13)),'\n',char(10)),'-83-');
INSERT INTO pages VALUES(3090,'mula_ma_01',51,replace(replace('<p class="bodytext">‘pare <a name="M1.0051"></a> abrahmacārī bhavissanti, mayamettha brahmacārī bhavissāmā’ti sallekho karaṇīyo. ‘pare musāvādī bhavissanti, mayamettha musāvādā paṭiviratā bhavissāmā’ti sallekho karaṇīyo. ‘pare pisuṇavācā <span class="note">[pisuṇā vācā (sī. pī.)]</span> bhavissanti, mayamettha pisuṇāya vācāya paṭiviratā bhavissāmā’ti sallekho karaṇīyo. ‘pare pharusavācā <span class="note">[pharusā vācā (sī. pī.)]</span> bhavissanti, mayamettha pharusāya vācāya paṭiviratā bhavissāmā’ti sallekho karaṇīyo. ‘pare samphappalāpī bhavissanti, mayamettha samphappalāpā paṭiviratā bhavissāmā’ti sallekho karaṇīyo. ‘pare abhijjhālū bhavissanti, mayamettha anabhijjhālū bhavissāmā’ti sallekho karaṇīyo. ‘pare byāpannacittā bhavissanti, mayamettha abyāpannacittā bhavissāmā’ti sallekho karaṇīyo. ‘pare micchādiṭṭhī bhavissanti, mayamettha sammādiṭṭhī bhavissāmā’ti sallekho karaṇīyo. ‘pare micchāsaṅkappā <a name="T1.0076"></a> bhavissanti, mayamettha sammāsaṅkappā bhavissāmā’ti sallekho karaṇīyo. ‘pare micchāvācā bhavissanti, mayamettha sammāvācā bhavissāmā’ti sallekho karaṇīyo. ‘pare micchākammantā bhavissanti, mayamettha sammākammantā bhavissāmā’ti sallekho karaṇīyo. ‘pare micchāājīvā bhavissanti, mayamettha sammāājīvā bhavissāmā’ti sallekho karaṇīyo. ‘pare micchāvāyāmā bhavissanti, mayamettha sammāvāyāmā bhavissāmā’ti sallekho karaṇīyo. ‘pare micchāsatī bhavissanti, mayamettha sammāsatī bhavissāmā’ti sallekho karaṇīyo. ‘pare micchāsamādhi bhavissanti, mayamettha sammāsamādhī bhavissāmā’ti sallekho karaṇīyo. ‘pare micchāñāṇī bhavissanti, mayamettha sammāñāṇī bhavissāmā’ti sallekho karaṇīyo. ‘pare micchāvimuttī bhavissanti, mayamettha sammāvimuttī bhavissāmā’ti sallekho karaṇīyo.</p>\r\n<p class="bodytext">‘pare thīnamiddhapariyuṭṭhitā bhavissanti, mayamettha vigatathīnamiddhā bhavissāmā’ti sallekho karaṇīyo<a name="V1.0054"></a>. ‘pare uddhatā bhavissanti, mayamettha anuddhatā bhavissāmā’ti sallekho karaṇīyo. ‘pare vicikicchī <span class="note">[vecikicchī (sī. pī. ka.)]</span> bhavissanti, mayamettha tiṇṇavicikicchā bhavissāmā’ti sallekho karaṇīyo. ‘pare kodhanā bhavissanti, mayamettha akkodhanā bhavissāmā’ti sallekho karaṇīyo. ‘pare upanāhī bhavissanti, mayamettha anupanāhī bhavissāmā’ti sallekho karaṇīyo. ‘pare <a name="P1.0043"></a> makkhī</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3091,'mula_ma_01',52,replace(replace('<p class="bodytext">bhavissanti<a name="M1.0052"></a>, mayamettha amakkhī bhavissāmā’ti sallekho karaṇīyo. ‘pare <a name="T1.0077"></a> paḷāsī bhavissanti, mayamettha apaḷāsī bhavissāmā’ti sallekho karaṇīyo. ‘pare issukī bhavissanti, mayamettha anissukī bhavissāmā’ti sallekho karaṇīyo. ‘pare maccharī bhavissanti, mayamettha amaccharī bhavissāmā’ti sallekho karaṇīyo. ‘pare saṭhā bhavissanti, mayamettha asaṭhā bhavissāmā’ti sallekho karaṇīyo. ‘pare māyāvī bhavissanti, mayamettha amāyāvī bhavissāmā’ti sallekho karaṇīyo. ‘pare thaddhā bhavissanti, mayamettha atthaddhā bhavissāmā’ti sallekho karaṇīyo. ‘pare atimānī bhavissanti, mayamettha anatimānī bhavissāmā’ti sallekho karaṇīyo. ‘pare dubbacā bhavissanti, mayamettha suvacā bhavissāmā’ti sallekho karaṇīyo. ‘pare pāpamittā bhavissanti, mayamettha kalyāṇamittā bhavissāmā’ti sallekho karaṇīyo. ‘pare pamattā bhavissanti, mayamettha appamattā bhavissāmā’ti sallekho karaṇīyo. ‘pare assaddhā bhavissanti, mayamettha saddhā bhavissāmā’ti sallekho karaṇīyo. ‘pare ahirikā bhavissanti, mayamettha hirimanā bhavissāmā’ti sallekho karaṇīyo. ‘pare anottāpī <span class="note">[anottappī (ka.)]</span> bhavissanti, mayamettha ottāpī bhavissāmā’ti sallekho karaṇīyo. ‘pare appassutā bhavissanti, mayamettha bahussutā bhavissāmā’ti sallekho karaṇīyo. ‘pare kusītā bhavissanti, mayamettha āraddhavīriyā bhavissāmā’ti sallekho karaṇīyo. ‘pare muṭṭhassatī bhavissanti, mayamettha upaṭṭhitassatī bhavissāmā’ti <a name="T1.0078"></a> sallekho karaṇīyo. ‘pare duppaññā bhavissanti, mayamettha paññāsampannā bhavissāmā’ti sallekho karaṇīyo. ‘pare sandiṭṭhiparāmāsī ādhānaggāhī duppaṭinissaggī bhavissanti, mayamettha asandiṭṭhiparāmāsī anādhānaggāhī suppaṭinissaggī bhavissāmā’ti sallekho karaṇīyo.</p>\r\n<p class="bodytext"><a name="para84"></a><a name="para84_mn1"></a><span class="paranum">84</span>. cittuppādampi kho ahaṃ, cunda, kusalesu dhammesu bahukāraṃ <span class="note">[bahūpakāraṃ (ka.)]</span> vadāmi, ko pana vādo kāyena vācāya anuvidhīyanāsu! tasmātiha, cunda, ‘pare vihiṃsakā bhavissanti, mayamettha avihiṃsakā bhavissāmā’ti cittaṃ uppādetabbaṃ. ‘pare pāṇātipātī bhavissanti, mayamettha pāṇātipātā</p>\r\n','\r',char(13)),'\n',char(10)),'-84-');
INSERT INTO pages VALUES(3092,'mula_ma_01',53,replace(replace('<p class="bodytext">paṭiviratā <a name="M1.0053"></a> bhavissāmā’ti cittaṃ uppādetabbaṃ…‘pare sandiṭṭhiparāmāsī <a name="V1.0055"></a> ādhānaggāhī duppaṭinissaggī bhavissanti, mayamettha asandiṭṭhiparāmāsī anādhānaggāhī suppaṭinissaggī bhavissāmā’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext"><a name="para85"></a><a name="para85_mn1"></a><span class="paranum">85</span>. seyyathāpi, cunda, visamo maggo assa, tassa <span class="note">[maggo tassāssa (sī. syā. pī.)]</span> añño samo maggo parikkamanāya, seyyathā vā pana, cunda, visamaṃ titthaṃ assa, tassa aññaṃ samaṃ titthaṃ parikkamanāya, evameva <a name="P1.0044"></a> kho, cunda, vihiṃsakassa purisapuggalassa avihiṃsā hoti parikkamanāya, pāṇātipātissa purisapuggalassa pāṇātipātā veramaṇī hoti parikkamanāya, adinnādāyissa purisapuggalassa adinnādānā veramaṇī hoti parikkamanāya, abrahmacārissa purisapuggalassa abrahmacariyā veramaṇī hoti parikkamanāya<a name="T1.0079"></a>, musāvādissa purisapuggalassa musāvādā veramaṇī hoti parikkamanāya, pisuṇavācassa purisapuggalassa pisuṇāya vācāya veramaṇī hoti parikkamanāya, pharusavācassa purisapuggalassa pharusāya vācāya veramaṇī hoti parikkamanāya, samphappalāpissa purisapuggalassa samphappalāpā veramaṇī hoti parikkamanāya, abhijjhālussa purisapuggalassa anabhijjhā hoti parikkamanāya, byāpannacittassa purisapuggalassa abyāpādo hoti parikkamanāya, micchādiṭṭhissa purisapuggalassa sammādiṭṭhi hoti parikkamanāya, micchāsaṅkappassa purisapuggalassa sammāsaṅkappo hoti parikkamanāya, micchāvācassa purisapuggalassa sammāvācā hoti parikkamanāya, micchākammantassa purisapuggalassa sammākammanto hoti parikkamanāya, micchāājīvassa purisapuggalassa sammāājīvo hoti parikkamanāya, micchāvāyāmassa purisapuggalassa sammāvāyāmo hoti parikkamanāya, micchāsatissa purisapuggalassa sammāsati hoti parikkamanāya, micchāsamādhissa purisapuggalassa sammāsamādhi hoti parikkamanāya, micchāñāṇissa purisapuggalassa sammāñāṇaṃ hoti parikkamanāya, micchāvimuttissa purisapuggalassa sammāvimutti hoti parikkamanāya.</p>\r\n<p class="bodytext">thīnamiddhapariyuṭṭhitassa purisapuggalassa vigatathinamiddhatā hoti parikkamanāya, uddhatassa purisapuggalassa anuddhaccaṃ <a name="T1.0080"></a> hoti parikkamanāya, vicikicchissa purisapuggalassa tiṇṇavicikicchatā hoti parikkamanāya, kodhanassa</p>\r\n','\r',char(13)),'\n',char(10)),'-85-');
INSERT INTO pages VALUES(3093,'mula_ma_01',54,replace(replace('<p class="bodytext">purisapuggalassa <a name="M1.0054"></a> akkodho hoti parikkamanāya, upanāhissa purisapuggalassa anupanāho hoti parikkamanāya, makkhissa purisapuggalassa amakkho hoti parikkamanāya, paḷāsissa purisapuggalassa apaḷāso hoti parikkamanāya<a name="V1.0056"></a>, issukissa purisapuggalassa anissukitā hoti parikkamanāya, maccharissa purisapuggalassa amacchariyaṃ hoti parikkamanāya, saṭhassa purisapuggalassa asāṭheyyaṃ hoti parikkamanāya, māyāvissa purisapuggalassa amāyā <span class="note">[amāyāvitā (ka.)]</span> hoti parikkamanāya, thaddhassa purisapuggalassa atthaddhiyaṃ hoti parikkamanāya, atimānissa purisapuggalassa anatimāno hoti parikkamanāya, dubbacassa purisapuggalassa sovacassatā hoti parikkamanāya, pāpamittassa purisapuggalassa kalyāṇamittatā hoti parikkamanāya, pamattassa purisapuggalassa appamādo hoti parikkamanāya, assaddhassa purisapuggalassa saddhā hoti parikkamanāya, ahirikassa purisapuggalassa hirī hoti parikkamanāya, anottāpissa purisapuggalassa ottappaṃ hoti parikkamanāya, appassutassa purisapuggalassa bāhusaccaṃ hoti parikkamanāya, kusītassa purisapuggalassa vīriyārambho hoti parikkamanāya, muṭṭhassatissa purisapuggalassa upaṭṭhitassatitā hoti parikkamanāya, duppaññassa purisapuggalassa paññāsampadā hoti parikkamanāya<a name="T1.0081"></a>, sandiṭṭhiparāmāsi-ādhānaggāhi-duppaṭinissaggissa purisapuggalassa asandiṭṭhiparāmāsianādhānaggāhi-suppaṭinissaggitā hoti parikkamanāya.</p>\r\n<p class="bodytext"><a name="para86"></a><a name="para86_mn1"></a><span class="paranum">86</span>. seyyathāpi, cunda, ye keci akusalā dhammā sabbe te adhobhāgaṅgamanīyā <span class="note">[adhobhāvaṅgamanīyā (sī. syā. pī.)]</span>, ye keci kusalā dhammā sabbe te uparibhāgaṅgamanīyā <span class="note">[uparibhāvaṅgamanīyā (sī. syā. pī.)]</span>, evameva kho, cunda, vihiṃsakassa purisapuggalassa avihiṃsā hoti uparibhāgāya <span class="note">[uparibhāvāya (sī. syā. ka.)]</span>, pāṇātipātissa purisapuggalassa pāṇātipātā veramaṇī hoti uparibhāgāya…pe… sandiṭṭhiparāmāsi-ādhānaggāhi-duppaṭinissaggissa <a name="P1.0045"></a> purisapuggalassa asandiṭṭhiparāmāsi-anādhānaggāhi-suppaṭinissaggitā hoti uparibhāgāya.</p>\r\n<p class="bodytext"><a name="para87"></a><a name="para87_mn1"></a><span class="paranum">87</span>. so vata, cunda, attanā palipapalipanno paraṃ palipapalipannaṃ uddharissatīti netaṃ ṭhānaṃ vijjati. so vata, cunda, attanā apalipapalipanno</p>\r\n','\r',char(13)),'\n',char(10)),'-86-87-');
INSERT INTO pages VALUES(3094,'mula_ma_01',55,replace(replace('<p class="bodytext">paraṃ <a name="M1.0055"></a> palipapalipannaṃ uddharissatīti ṭhānametaṃ vijjati. so vata, cunda, attanā adanto avinīto aparinibbuto paraṃ damessati vinessati parinibbāpessatīti netaṃ ṭhānaṃ vijjati. so vata<a name="V1.0057"></a>, cunda, attanā danto vinīto parinibbuto paraṃ damessati vinessati parinibbāpessatīti ṭhānametaṃ vijjati. evameva kho, cunda, vihiṃsakassa purisapuggalassa avihiṃsā hoti parinibbānāya, pāṇātipātissa purisapuggalassa pāṇātipātā veramaṇī hoti parinibbānāya. adinnādāyissa purisapuggalassa adinnādānā veramaṇī <a name="T1.0082"></a> hoti parinibbānāya. abrahmacārissa purisapuggalassa abrahmacariyā veramaṇī hoti parinibbānāya. musāvādissa purisapuggalassa musāvādā veramaṇī hoti parinibbānāya. pisuṇavācassa purisapuggalassa pisuṇāya vācāya veramaṇī hoti parinibbānāya. pharusavācassa purisapuggalassa pharusāya vācāya veramaṇī hoti parinibbānāya. samphappalāpissa purisapuggalassa samphappalāpā veramaṇī hoti parinibbānāya. abhijjhālussa purisapuggalassa anabhijjhā hoti parinibbānāya. byāpannacittassa purisapuggalassa abyāpādo hoti parinibbānāya. micchādiṭṭhissa purisapuggalassa sammādiṭṭhi hoti parinibbānāya. micchāsaṅkappassa purisapuggalassa sammāsaṅkappo hoti parinibbānāya. micchāvācassa purisapuggalassa sammāvācā hoti parinibbānāya. micchākammantassa purisapuggalassa sammākammanto hoti parinibbānāya. micchāājīvassa purisapuggalassa sammāājīvo hoti parinibbānāya. micchāvāyāmassa purisapuggalassa sammāvāyāmo hoti parinibbānāya. micchāsatissa purisapuggalassa sammāsati hoti parinibbānāya. micchāsamādhissa purisapuggalassa sammāsamādhi hoti parinibbānāya. micchāñāṇissa purisapuggalassa sammāñāṇaṃ hoti parinibbānāya. micchāvimuttissa purisapuggalassa sammāvimutti hoti parinibbānāya.</p>\r\n<p class="bodytext">thīnamiddhapariyuṭṭhitassa <a name="T1.0083"></a> purisapuggalassa vigatathinamiddhatā hoti parinibbānāya. uddhatassa purisapuggalassa anuddhaccaṃ hoti parinibbānāya. vicikicchissa purisapuggalassa tiṇṇavicikicchatā hoti parinibbānāya. kodhanassa purisapuggalassa akkodho hoti parinibbānāya. upanāhissa purisapuggalassa anupanāho hoti parinibbānāya. makkhissa purisapuggalassa amakkho hoti parinibbānāya. paḷāsissa purisapuggalassa</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3095,'mula_ma_01',56,replace(replace('<p class="bodytext">apaḷāso <a name="M1.0056"></a> hoti parinibbānāya. issukissa purisapuggalassa anissukitā hoti parinibbānāya. maccharissa purisapuggalassa amacchariyaṃ hoti parinibbānāya. saṭhassa purisapuggalassa asāṭheyyaṃ hoti parinibbānāya. māyāvissa purisapuggalassa amāyā hoti parinibbānāya. thaddhassa purisapuggalassa atthaddhiyaṃ hoti parinibbānāya. atimānissa purisapuggalassa anatimāno hoti parinibbānāya. dubbacassa purisapuggalassa sovacassatā hoti parinibbānāya. pāpamittassa purisapuggalassa kalyāṇamittatā hoti parinibbānāya. pamattassa purisapuggalassa appamādo <a name="V1.0058"></a> hoti parinibbānāya. assaddhassa purisapuggalassa saddhā hoti parinibbānāya. ahirikassa purisapuggalassa hirī hoti parinibbānāya. anottāpissa purisapuggalassa ottappaṃ hoti parinibbānāya. appassutassa purisapuggalassa bāhusaccaṃ hoti parinibbānāya. kusītassa purisapuggalassa vīriyārambho hoti parinibbānāya. muṭṭhassatissa purisapuggalassa <a name="T1.0084"></a> upaṭṭhitassatitā hoti parinibbānāya. duppaññassa purisapuggalassa <a name="P1.0046"></a> paññāsampadā hoti parinibbānāya. sandiṭṭhiparāmāsi-ādhānaggāhi-duppaṭinissaggissa purisapuggalassa asandiṭṭhiparāmāsi-anādhānaggāhi-suppaṭinissaggitā hoti parinibbānāya.</p>\r\n<p class="bodytext"><a name="para88"></a><a name="para88_mn1"></a><span class="paranum">88</span>. iti kho, cunda, desito mayā sallekhapariyāyo, desito cittuppādapariyāyo, desito parikkamanapariyāyo, desito uparibhāgapariyāyo, desito parinibbānapariyāyo. yaṃ kho, cunda, satthārā karaṇīyaṃ sāvakānaṃ hitesinā anukampakena anukampaṃ upādāya, kataṃ vo taṃ mayā. ‘etāni, cunda, rukkhamūlāni, etāni suññāgārāni, jhāyatha, cunda, mā pamādattha, mā pacchāvippaṭisārino ahuvattha’ – ayaṃ kho amhākaṃ anusāsanī"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamano āyasmā mahācundo bhagavato bhāsitaṃ abhinandīti.</p>\r\n<p class="gatha1">catuttālīsapadā vuttā, sandhayo pañca desitā,</p>\r\n<p class="gathalast">sallekho nāma suttanto, gambhīro sāgarūpamoti.</p>\r\n<p class="centered">sallekhasuttaṃ niṭṭhitaṃ aṭṭhamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-88-');
INSERT INTO pages VALUES(3096,'mula_ma_01',57,replace(replace('<p class="subhead">9. sammādiṭṭhisuttaṃ</p>\r\n<p class="bodytext"><a name="para89"></a><a name="para89_mn1"></a><span class="paranum">89</span>. evaṃ <a name="T1.0085"></a><a name="M1.0057"></a><a name="V1.0059"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho āyasmā sāriputto bhikkhū āmantesi – "āvuso bhikkhave"ti. "āvuso"ti kho te bhikkhū āyasmato sāriputtassa paccassosuṃ. āyasmā sāriputto etadavoca –</p>\r\n<p class="bodytext">‘sammādiṭṭhi <span class="note">[sammādiṭṭhī (sī. syā.)]</span> sammādiṭṭhī’ti, āvuso, vuccati. kittāvatā nu kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti?</p>\r\n<p class="bodytext">dūratopi kho mayaṃ, āvuso, āgaccheyyāma āyasmato sāriputtassa santike etassa bhāsitassa atthamaññātuṃ. sādhu vatāyasmantaṃyeva sāriputtaṃ paṭibhātu etassa bhāsitassa attho. āyasmato sāriputtassa sutvā bhikkhū dhāressantī"ti. "tena hi, āvuso, suṇātha, sādhukaṃ manasi karotha, bhāsissāmī"ti. "evamāvuso"ti kho te bhikkhū āyasmato sāriputtassa paccassosuṃ. āyasmā sāriputto etadavoca –</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako akusalañca pajānāti, akusalamūlañca pajānāti, kusalañca pajānāti, kusalamūlañca pajānāti <a name="P1.0047"></a> – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena <a name="T1.0086"></a> samannāgato, āgato imaṃ saddhammaṃ. katamaṃ panāvuso, akusalaṃ, katamaṃ akusalamūlaṃ, katamaṃ kusalaṃ, katamaṃ kusalamūlaṃ? pāṇātipāto kho, āvuso, akusalaṃ, adinnādānaṃ akusalaṃ, kāmesumicchācāro akusalaṃ, musāvādo akusalaṃ, pisuṇā vācā <span class="note">[pisuṇavācā (ka.)]</span> akusalaṃ, pharusā vācā <span class="note">[pharusavācā (ka.)]</span> akusalaṃ, samphappalāpo akusalaṃ, abhijjhā akusalaṃ, byāpādo akusalaṃ, micchādiṭṭhi akusalaṃ – idaṃ vuccatāvuso akusalaṃ. katamañcāvuso, akusalamūlaṃ? lobho akusalamūlaṃ, doso akusalamūlaṃ, moho akusalamūlaṃ – idaṃ vuccatāvuso, akusalamūlaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-89-');
INSERT INTO pages VALUES(3097,'mula_ma_01',58,replace(replace('<p class="bodytext">katamañcāvuso<a name="M1.0058"></a><a name="V1.0060"></a>, kusalaṃ? pāṇātipātā veramaṇī kusalaṃ, adinnādānā veramaṇī kusalaṃ, kāmesumicchācārā veramaṇī kusalaṃ, musāvādā veramaṇī kusalaṃ, pisuṇāya vācāya veramaṇī kusalaṃ, pharusāya vācāya veramaṇī kusalaṃ, samphappalāpā veramaṇī kusalaṃ, anabhijjhā kusalaṃ, abyāpādo kusalaṃ, sammādiṭṭhi kusalaṃ – idaṃ vuccatāvuso, kusalaṃ. katamañcāvuso, kusalamūlaṃ? alobho kusalamūlaṃ, adoso kusalamūlaṃ, amoho kusalamūlaṃ – idaṃ vuccatāvuso, kusalamūlaṃ.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ akusalaṃ pajānāti, evaṃ akusalamūlaṃ pajānāti, evaṃ kusalaṃ pajānāti, evaṃ kusalamūlaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya, paṭighānusayaṃ paṭivinodetvā, ‘asmī’ti diṭṭhimānānusayaṃ samūhanitvā, avijjaṃ pahāya vijjaṃ uppādetvā, diṭṭhevadhamme dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena <a name="T1.0087"></a> samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para90"></a><a name="para90_mn1"></a><span class="paranum">90</span>. sādhāvuso"ti kho te bhikkhū āyasmato sāriputtassa bhāsitaṃ abhinanditvā anumoditvā āyasmantaṃ sāriputtaṃ uttari <span class="note">[uttariṃ (sī. syā. pī.)]</span> pañhaṃ apucchuṃ <span class="note">[apucchiṃsu (syā.)]</span> – "siyā panāvuso, aññopi pariyāyo yathā ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti?</p>\r\n<p class="bodytext">siyā, āvuso. yato kho, āvuso, ariyasāvako āhārañca pajānāti, āhārasamudayañca pajānāti, āhāranirodhañca pajānāti, āhāranirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ <a name="P1.0048"></a> saddhammaṃ. katamo panāvuso, āhāro, katamo āhārasamudayo, katamo āhāranirodho, katamā āhāranirodhagāminī paṭipadā? cattārome, āvuso, āhārā bhūtānaṃ vā sattānaṃ ṭhitiyā, sambhavesīnaṃ vā anuggahāya. katame cattāro? kabaḷīkāro āhāro</p>\r\n','\r',char(13)),'\n',char(10)),'-90-');
INSERT INTO pages VALUES(3098,'mula_ma_01',59,replace(replace('<p class="bodytext">oḷāriko <a name="M1.0059"></a> vā sukhumo vā, phasso dutiyo, manosañcetanā tatiyā, viññāṇaṃ catutthaṃ. taṇhāsamudayā āhārasamudayo, taṇhānirodhā āhāranirodho, ayameva ariyo aṭṭhaṅgiko maggo āhāranirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi <a name="V1.0061"></a> sammāsaṅkappo sammāvācā sammākammanto<a name="T1.0088"></a>, sammāājīvo sammāvāyāmo sammāsati sammāsamādhi’.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ āhāraṃ pajānāti, evaṃ āhārasamudayaṃ pajānāti, evaṃ āhāranirodhaṃ pajānāti, evaṃ āhāranirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya, paṭighānusayaṃ paṭivinodetvā, ‘asmī’ti diṭṭhimānānusayaṃ samūhanitvā, avijjaṃ pahāya vijjaṃ uppādetvā, diṭṭhevadhamme dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para91"></a><a name="para91_mn1"></a><span class="paranum">91</span>. sādhāvuso"ti kho te bhikkhū āyasmato sāriputtassa bhāsitaṃ abhinanditvā anumoditvā āyasmantaṃ sāriputtaṃ uttari pañhaṃ apucchuṃ – "siyā panāvuso, aññopi pariyāyo yathā ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti?</p>\r\n<p class="bodytext">siyā, āvuso. yato kho, āvuso, ariyasāvako dukkhañca pajānāti, dukkhasamudayañca pajānāti, dukkhanirodhañca pajānāti, dukkhanirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhammaṃ. katamaṃ panāvuso, dukkhaṃ, katamo dukkhasamudayo, katamo dukkhanirodho, katamā dukkhanirodhagāminī paṭipadā? jātipi dukkhā, jarāpi dukkhā, maraṇampi dukkhaṃ, sokaparidevadukkhadomanassupāyāsāpi dukkhā, appiyehi sampayogopi dukkho, piyehi vippayogopi dukkho, yampicchaṃ na <a name="T1.0089"></a> labhati tampi dukkhaṃ, saṃkhittena pañcupādānakkhandhā <span class="note">[pañcupādānakkhandhāpi (ka.)]</span> dukkhā – idaṃ vuccatāvuso, dukkhaṃ. katamo cāvuso, dukkhasamudayo? yāyaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-91-');
INSERT INTO pages VALUES(3099,'mula_ma_01',60,replace(replace('<p class="bodytext">taṇhā <a name="M1.0060"></a> ponobbhavikā nandīrāgasahagatā <span class="note">[ponobhavikā (sī. pī.)]</span> tatratatrābhinandinī <span class="note">[nandirāgasahagatā (sī. pī.)]</span>, seyyathidaṃ, kāmataṇhā bhavataṇhā <a name="P1.0049"></a> vibhavataṇhā – ayaṃ vuccatāvuso, dukkhasamudayo. katamo cāvuso, dukkhanirodho? yo tassāyeva taṇhāya asesavirāganirodho cāgo paṭinissaggo mutti anālayo – ayaṃ vuccatāvuso, dukkhanirodho. katamā cāvuso, dukkhanirodhagāminī paṭipadā? ayameva <a name="V1.0062"></a> ariyo aṭṭhaṅgiko maggo, seyyathidaṃ, sammādiṭṭhi…pe… sammāsamādhi – ayaṃ vuccatāvuso, dukkhanirodhagāminī paṭipadā.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ dukkhaṃ pajānāti, evaṃ dukkhasamudayaṃ pajānāti, evaṃ dukkhanirodhaṃ pajānāti, evaṃ dukkhanirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya, paṭighānusayaṃ paṭivinodetvā, ‘asmī’ti diṭṭhimānānusayaṃ samūhanitvā, avijjaṃ pahāya vijjaṃ uppādetvā, diṭṭhevadhamme dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para92"></a><a name="para92_mn1"></a><span class="paranum">92</span>. sādhāvuso"ti kho te bhikkhū āyasmato sāriputtassa bhāsitaṃ abhinanditvā anumoditvā āyasmantaṃ sāriputtaṃ uttari pañhaṃ apucchuṃ – "siyā panāvuso, aññopi pariyāyo yathā ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena <a name="T1.0090"></a> samannāgato, āgato imaṃ saddhamma"nti?</p>\r\n<p class="bodytext">siyā, āvuso. yato kho, āvuso, ariyasāvako jarāmaraṇañca pajānāti, jarāmaraṇasamudayañca pajānāti, jarāmaraṇanirodhañca pajānāti, jarāmaraṇanirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhammaṃ. katamaṃ panāvuso, jarāmaraṇaṃ, katamo jarāmaraṇasamudayo, katamo jarāmaraṇanirodho, katamā jarāmaraṇanirodhagāminī paṭipadā? yā tesaṃ tesaṃ sattānaṃ tamhi tamhi sattanikāye jarā jīraṇatā khaṇḍiccaṃ pāliccaṃ valittacatā āyuno saṃhāni indriyānaṃ paripāko – ayaṃ vuccatāvuso, jarā. katamañcāvuso, maraṇaṃ? yā <span class="note">[yaṃ (pī. ka.), satipaṭṭhānasuttepi]</span> tesaṃ tesaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-92-');
INSERT INTO pages VALUES(3100,'mula_ma_01',61,replace(replace('<p class="bodytext">sattānaṃ <a name="M1.0061"></a> tamhā tamhā sattanikāyā cuti cavanatā bhedo antaradhānaṃ maccu maraṇaṃ kālaṃkiriyā khandhānaṃ bhedo, kaḷevarassa nikkhepo, jīvitindriyassupacchedo – idaṃ vuccatāvuso, maraṇaṃ. iti ayañca jarā idañca maraṇaṃ – idaṃ vuccatāvuso, jarāmaraṇaṃ. jātisamudayā jarāmaraṇasamudayo, jātinirodhā jarāmaraṇanirodho, ayameva ariyo aṭṭhaṅgiko maggo jarāmaraṇanirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato <a name="V1.0063"></a> kho, āvuso, ariyasāvako evaṃ jarāmaraṇaṃ pajānāti, evaṃ jarāmaraṇasamudaṃ pajānāti, evaṃ jarāmaraṇanirodhaṃ pajānāti, evaṃ <a name="T1.0091"></a> jarāmaraṇanirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya…pe… dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para93"></a><a name="para93_mn1"></a><span class="paranum">93</span>. sādhāvuso"ti kho…pe… apucchuṃ – siyā panāvuso…pe… <a name="P1.0050"></a> "siyā, āvuso. yato kho, āvuso, ariyasāvako jātiñca pajānāti, jātisamudayañca pajānāti, jātinirodhañca pajānāti, jātinirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhammaṃ. katamā panāvuso, jāti, katamo jātisamudayo, katamo jātinirodho, katamā jātinirodhagāminī paṭipadā? yā tesaṃ tesaṃ sattānaṃ tamhi tamhi sattanikāye jāti sañjāti okkanti abhinibbatti khandhānaṃ pātubhāvo, āyatanānaṃ paṭilābho – ayaṃ vuccatāvuso, jāti. bhavasamudayā jātisamudayo, bhavanirodhā jātinirodho, ayameva ariyo aṭṭhaṅgiko maggo jātinirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ jātiṃ pajānāti, evaṃ jātisamudayaṃ pajānāti, evaṃ jātinirodhaṃ pajānāti, evaṃ jātinirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya…pe… dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako</p>\r\n','\r',char(13)),'\n',char(10)),'-93-');
INSERT INTO pages VALUES(3101,'mula_ma_01',62,replace(replace('<p class="bodytext">sammādiṭṭhi <a name="M1.0062"></a> hoti, ujugatāssa <a name="T1.0092"></a> diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para94"></a><a name="para94_mn1"></a><span class="paranum">94</span>. sādhāvuso"ti kho…pe… apucchuṃ – siyā panāvuso…pe… "siyā, āvuso. yato kho, āvuso, ariyasāvako bhavañca pajānāti, bhavasamudayañca pajānāti, bhavanirodhañca pajānāti, bhavanirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ <a name="V1.0064"></a> saddhammaṃ. katamo panāvuso, bhavo, katamo bhavasamudayo, katamo bhavanirodho, katamā bhavanirodhagāminī paṭipadā? tayome, āvuso, bhavā – kāmabhavo, rūpabhavo, arūpabhavo. upādānasamudayā bhavasamudayo, upādānanirodhā bhavanirodho, ayameva ariyo aṭṭhaṅgiko maggo bhavanirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ bhavaṃ pajānāti, evaṃ bhavasamudayaṃ pajānāti, evaṃ bhavanirodhaṃ pajānāti, evaṃ bhavanirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya…pe… dukkhassantakaro hoti. ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para95"></a><a name="para95_mn1"></a><span class="paranum">95</span>. sādhāvuso"ti kho…pe… apucchuṃ – siyā panāvuso…pe… "siyā, āvuso. yato kho, āvuso, ariyasāvako upādānañca pajānāti, upādānasamudayañca <a name="T1.0093"></a> pajānāti, upādānanirodhañca pajānāti, upādānanirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhammaṃ. katamaṃ panāvuso, upādānaṃ, katamo upādānasamudayo, katamo upādānanirodho, katamā upādānanirodhagāminī paṭipadā? cattārimāni, āvuso, upādānāni <a name="P1.0051"></a> – kāmupādānaṃ, diṭṭhupādānaṃ, sīlabbatupādānaṃ, attavādupādānaṃ. taṇhāsamudayā upādānasamudayo, taṇhānirodhā upādānanirodho, ayameva</p>\r\n','\r',char(13)),'\n',char(10)),'-94-95-');
INSERT INTO pages VALUES(3102,'mula_ma_01',63,replace(replace('<p class="bodytext">ariyo <a name="M1.0063"></a> aṭṭhaṅgiko maggo upādānanirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ upādānaṃ pajānāti, evaṃ upādānasamudayaṃ pajānāti, evaṃ upādānanirodhaṃ pajānāti, evaṃ upādānanirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya…pe… dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para96"></a><a name="para96_mn1"></a><span class="paranum">96</span>. sādhāvuso"ti <a name="V1.0065"></a> kho…pe… apucchuṃ – siyā panāvuso…pe… "siyā, āvuso. yato kho, āvuso, ariyasāvako taṇhañca pajānāti, taṇhāsamudayañca pajānāti, taṇhānirodhañca pajānāti, taṇhānirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena <a name="T1.0094"></a> samannāgato, āgato imaṃ saddhammaṃ. katamā panāvuso, taṇhā, katamo taṇhāsamudayo, katamo taṇhānirodho, katamā taṇhānirodhagāminī paṭipadā? chayime, āvuso, taṇhākāyā – rūpataṇhā, saddataṇhā, gandhataṇhā, rasataṇhā, phoṭṭhabbataṇhā, dhammataṇhā. vedanāsamudayā taṇhāsamudayo, vedanānirodhā taṇhānirodho, ayameva ariyo aṭṭhaṅgiko maggo taṇhānirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ taṇhaṃ pajānāti, evaṃ taṇhāsamudayaṃ pajānāti, evaṃ taṇhānirodhaṃ pajānāti, evaṃ taṇhānirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya…pe… dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para97"></a><a name="para97_mn1"></a><span class="paranum">97</span>. sādhāvuso"ti kho…pe… apucchuṃ – siyā panāvuso…pe… "siyā, āvuso. yato kho, āvuso, ariyasāvako vedanañca pajānāti, vedanāsamudayañca pajānāti, vedanānirodhañca pajānāti, vedanānirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso,</p>\r\n','\r',char(13)),'\n',char(10)),'-96-97-');
INSERT INTO pages VALUES(3103,'mula_ma_01',64,replace(replace('<p class="bodytext">ariyasāvako <a name="M1.0064"></a> sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhammaṃ. katamā panāvuso, vedanā, katamo vedanāsamudayo, katamo vedanānirodho, katamā vedanānirodhagāminī <a name="T1.0095"></a> paṭipadā? chayime, āvuso, vedanākāyā – cakkhusamphassajā vedanā, sotasamphassajā vedanā, ghānasamphassajā vedanā, jivhāsamphassajā vedanā, kāyasamphassajā vedanā, manosamphassajā vedanā. phassasamudayā vedanāsamudayo, phassanirodhā vedanānirodho, ayameva ariyo aṭṭhaṅgiko maggo vedanānirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato <a name="V1.0066"></a><a name="P1.0052"></a> kho, āvuso, ariyasāvako evaṃ vedanaṃ pajānāti, evaṃ vedanāsamudayaṃ pajānāti, evaṃ vedanānirodhaṃ pajānāti, evaṃ vedanānirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya…pe… dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para98"></a><a name="para98_mn1"></a><span class="paranum">98</span>. sādhāvuso"ti kho…pe… apucchuṃ – siyā panāvuso…pe… "siyā, āvuso. yato kho, āvuso, ariyasāvako phassañca pajānāti, phassasamudayañca pajānāti, phassanirodhañca pajānāti, phassanirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhammaṃ. katamo panāvuso, phasso, katamo phassasamudayo, katamo phassanirodho, katamā phassanirodhagāminī paṭipadā? chayime, āvuso, phassakāyā – cakkhusamphasso, sotasamphasso<a name="T1.0096"></a>, ghānasamphasso, jivhāsamphasso, kāyasamphasso, manosamphasso. saḷāyatanasamudayā phassasamudayo, saḷāyatananirodhā phassanirodho, ayameva ariyo aṭṭhaṅgiko maggo phassanirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ phassaṃ pajānāti, evaṃ phassasamudayaṃ pajānāti, evaṃ phassanirodhaṃ pajānāti, evaṃ phassanirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya…pe…</p>\r\n','\r',char(13)),'\n',char(10)),'-98-');
INSERT INTO pages VALUES(3104,'mula_ma_01',65,replace(replace('<p class="bodytext">dukkhassantakaro <a name="M1.0065"></a> hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para99"></a><a name="para99_mn1"></a><span class="paranum">99</span>. sādhāvuso"ti kho…pe… apucchuṃ – siyā panāvuso…pe… "siyā, āvuso. yato kho, āvuso, ariyasāvako saḷāyatanañca pajānāti, saḷāyatanasamudayañca pajānāti, saḷāyatananirodhañca pajānāti, saḷāyatananirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena <a name="V1.0067"></a> samannāgato, āgato imaṃ saddhammaṃ. katamaṃ panāvuso, saḷāyatanaṃ, katamo saḷāyatanasamudayo, katamo saḷāyatananirodho, katamā saḷāyatananirodhagāminī paṭipadā<a name="T1.0097"></a>? chayimāni, āvuso, āyatanāni – cakkhāyatanaṃ, sotāyatanaṃ, ghānāyatanaṃ, jivhāyatanaṃ, kāyāyatanaṃ, manāyatanaṃ. nāmarūpasamudayā saḷāyatanasamudayo, nāmarūpanirodhā saḷāyatananirodho, ayameva ariyo aṭṭhaṅgiko maggo saḷāyatananirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ saḷāyatanaṃ pajānāti, evaṃ saḷāyatanasamudayaṃ pajānāti, evaṃ saḷāyatananirodhaṃ pajānāti<a name="P1.0053"></a>, evaṃ saḷāyatananirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya…pe… dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para100"></a><a name="para100_mn1"></a><span class="paranum">100</span>. sādhāvuso"ti kho…pe… apucchuṃ – siyā panāvuso…pe… "siyā, āvuso. yato kho, āvuso, ariyasāvako nāmarūpañca pajānāti, nāmarūpasamudayañca pajānāti, nāmarūpanirodhañca pajānāti, nāmarūpanirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhammaṃ. katamaṃ panāvuso, nāmarūpaṃ, katamo nāmarūpasamudayo, katamo nāmarūpanirodho, katamā nāmarūpanirodhagāminī paṭipadā? vedanā, saññā, cetanā, phasso,</p>\r\n','\r',char(13)),'\n',char(10)),'-99-100-');
INSERT INTO pages VALUES(3105,'mula_ma_01',66,replace(replace('<p class="bodytext">manasikāro <a name="M1.0066"></a> – idaṃ vuccatāvuso, nāmaṃ, cattāri ca mahābhūtāni, catunnañca mahābhūtānaṃ upādāyarūpaṃ – idaṃ vuccatāvuso, rūpaṃ. iti idañca nāmaṃ idañca rūpaṃ – idaṃ vuccatāvuso, nāmarūpaṃ. viññāṇasamudayā nāmarūpasamudayo, viññāṇanirodhā nāmarūpanirodho, ayameva ariyo aṭṭhaṅgiko maggo nāmarūpanirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ <a name="T1.0098"></a> nāmarūpaṃ pajānāti, evaṃ nāmarūpasamudayaṃ pajānāti, evaṃ nāmarūpanirodhaṃ pajānāti, evaṃ nāmarūpanirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso <a name="V1.0068"></a> rāgānusayaṃ pahāya…pe… dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para101"></a><a name="para101_mn1"></a><span class="paranum">101</span>. sādhāvuso"ti kho…pe… apucchuṃ – siyā panāvuso…pe… "siyā, āvuso. yato kho, āvuso, ariyasāvako viññāṇañca pajānāti, viññāṇasamudayañca pajānāti, viññāṇanirodhañca pajānāti, viññāṇanirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhammaṃ. katamaṃ panāvuso, viññāṇaṃ, katamo viññāṇasamudayo, katamo viññāṇanirodho, katamā viññāṇanirodhagāminī paṭipadā? chayime, āvuso, viññāṇakāyā – cakkhuviññāṇaṃ, sotaviññāṇaṃ, ghānaviññāṇaṃ, jivhāviññāṇaṃ, kāyaviññāṇaṃ, manoviññāṇaṃ. saṅkhārasamudayā viññāṇasamudayo, saṅkhāranirodhā viññāṇanirodho, ayameva ariyo aṭṭhaṅgiko maggo viññāṇanirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ viññāṇaṃ pajānāti, evaṃ viññāṇasamudayaṃ pajānāti, evaṃ viññāṇanirodhaṃ pajānāti, evaṃ viññāṇanirodhagāminiṃ paṭipadaṃ pajānāti<a name="P1.0054"></a>, so sabbaso <a name="T1.0099"></a> rāgānusayaṃ pahāya…pe… dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n','\r',char(13)),'\n',char(10)),'-101-');
INSERT INTO pages VALUES(3106,'mula_ma_01',67,replace(replace('<p class="bodytext"><a name="para102"></a><a name="para102_mn1"></a><span class="paranum">102</span>. sādhāvuso"ti <a name="M1.0067"></a> kho…pe… apucchuṃ – siyā panāvuso…pe… "siyā, āvuso. yato kho, āvuso, ariyasāvako saṅkhāre ca pajānāti, saṅkhārasamudayañca pajānāti, saṅkhāranirodhañca pajānāti, saṅkhāranirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhammaṃ. katame panāvuso, saṅkhārā, katamo saṅkhārasamudayo, katamo <a name="V1.0069"></a> saṅkhāranirodho, katamā saṅkhāranirodhagāminī paṭipadā? tayome, āvuso, saṅkhārā – kāyasaṅkhāro, vacīsaṅkhāro, cittasaṅkhāro. avijjāsamudayā saṅkhārasamudayo, avijjānirodhā saṅkhāranirodho, ayameva ariyo aṭṭhaṅgiko maggo saṅkhāranirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ saṅkhāre pajānāti, evaṃ saṅkhārasamudayaṃ pajānāti, evaṃ saṅkhāranirodhaṃ pajānāti, evaṃ saṅkhāranirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya, paṭighānusayaṃ paṭivinodetvā, ‘asmī’ti diṭṭhimānānusayaṃ samūhanitvā, avijjaṃ pahāya vijjaṃ uppādetvā, diṭṭheva dhamme dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa <a name="T1.0100"></a> diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para103"></a><a name="para103_mn1"></a><span class="paranum">103</span>. sādhāvuso"ti kho…pe… apucchuṃ – siyā panāvuso…pe… "siyā, āvuso. yato kho, āvuso, ariyasāvako avijjañca pajānāti, avijjāsamudayañca pajānāti, avijjānirodhañca pajānāti, avijjānirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhammaṃ. katamā panāvuso, avijjā, katamo avijjāsamudayo, katamo avijjānirodho, katamā avijjānirodhagāminī paṭipadā? yaṃ kho, āvuso, dukkhe aññāṇaṃ, dukkhasamudaye aññāṇaṃ, dukkhanirodhe aññāṇaṃ, dukkhanirodhagāminiyā paṭipadāya aññāṇaṃ – ayaṃ vuccatāvuso, avijjā. āsavasamudayā avijjāsamudayo, āsavanirodhā avijjānirodho, ayameva ariyo aṭṭhaṅgiko maggo avijjānirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n','\r',char(13)),'\n',char(10)),'-102-103-');
INSERT INTO pages VALUES(3107,'mula_ma_01',68,replace(replace('<p class="bodytext">yato <a name="M1.0068"></a> kho, āvuso, ariyasāvako evaṃ avijjaṃ pajānāti, evaṃ avijjāsamudayaṃ pajānāti, evaṃ avijjānirodhaṃ pajānāti, evaṃ avijjānirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya, paṭighānusayaṃ paṭivinodetvā, ‘asmī’ti diṭṭhimānānusayaṃ samūhanitvā, avijjaṃ pahāya vijjaṃ uppādetvā, diṭṭheva dhamme dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako <a name="T1.0101"></a> sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext"><a name="para104"></a><a name="para104_mn1"></a><span class="paranum">104</span>. sādhāvuso"ti <a name="V1.0070"></a> kho te bhikkhū āyasmato sāriputtassa bhāsitaṃ abhinanditvā anumoditvā āyasmantaṃ sāriputtaṃ uttari pañhaṃ apucchuṃ – "siyā panāvuso, aññopi pariyāyo <a name="P1.0055"></a> yathā ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti?</p>\r\n<p class="bodytext">siyā, āvuso. yato kho, āvuso, ariyasāvako āsavañca pajānāti, āsavasamudayañca pajānāti, āsavanirodhañca pajānāti, āsavanirodhagāminiṃ paṭipadañca pajānāti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi hoti, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhammaṃ. katamo panāvuso, āsavo, katamo āsavasamudayo, katamo āsavanirodho, katamā āsavanirodhagāminī paṭipadāti? tayome, āvuso, āsavā – kāmāsavo, bhavāsavo, avijjāsavo. avijjāsamudayā āsavasamudayo, avijjānirodhā āsavanirodho, ayameva ariyo aṭṭhaṅgiko maggo āsavanirodhagāminī paṭipadā, seyyathidaṃ – sammādiṭṭhi…pe… sammāsamādhi.</p>\r\n<p class="bodytext">yato kho, āvuso, ariyasāvako evaṃ āsavaṃ pajānāti, evaṃ āsavasamudayaṃ pajānāti, evaṃ āsavanirodhaṃ <a name="T1.0102"></a> pajānāti, evaṃ āsavanirodhagāminiṃ paṭipadaṃ pajānāti, so sabbaso rāgānusayaṃ pahāya, paṭighānusayaṃ paṭivinodetvā, ‘asmī’ti diṭṭhimānānusayaṃ samūhanitvā, avijjaṃ pahāya vijjaṃ uppādetvā, diṭṭheva dhamme dukkhassantakaro hoti – ettāvatāpi kho, āvuso, ariyasāvako sammādiṭṭhi</p>\r\n','\r',char(13)),'\n',char(10)),'-104-');
INSERT INTO pages VALUES(3108,'mula_ma_01',69,replace(replace('<p class="bodytext">hoti<a name="M1.0069"></a>, ujugatāssa diṭṭhi, dhamme aveccappasādena samannāgato, āgato imaṃ saddhamma"nti.</p>\r\n<p class="bodytext">idamavocāyasmā sāriputto. attamanā te bhikkhū āyasmato sāriputtassa bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">sammādiṭṭhisuttaṃ niṭṭhitaṃ navamaṃ. <span class="note">[ito paraṃ kesuci potthakesu imāpi gāthāyo§evaṃ dissanti –§dukkhaṃ jarāmaraṇaṃ upādānaṃ, saḷāyatanaṃ nāmarūpaṃ. viññāṇaṃ yā sā pare, katamā panāvuso padānaṃ§kiṃ jāti taṇhā ca vedanā, avijjāya catukkanayo. cattāri pare katamā, panāvuso padānaṃ kevalaṃ§āhāro ca bhavo phasso, saṅkhāro āsavapañcamo. yāva pañca pare katamā, panāvuso padānaṃ kiṃ§katamanti chabbidhā vuttaṃ, katamāni catubbidhāni. katamo pañcavidho vutto, sabbesaṃ ekasaṅkhānaṃ pañcanayapadāni cāti ]</span></p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3109,'mula_ma_01',70,replace(replace('<p class="subhead">10. mahāsatipaṭṭhānasuttaṃ</p>\r\n<p class="bodytext"><a name="para105"></a><a name="para105_mn1"></a><span class="paranum">105</span>. evaṃ <a name="M1.0070"></a><a name="V1.0071"></a><a name="T1.0103"></a> me sutaṃ – ekaṃ samayaṃ bhagavā kurūsu viharati kammāsadhammaṃ nāma kurūnaṃ nigamo. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="subhead">uddeso</p>\r\n<p class="bodytext"><a name="para106"></a><a name="para106_mn1"></a><span class="paranum">106</span>. ekāyano ayaṃ, bhikkhave, maggo sattānaṃ visuddhiyā, sokaparidevānaṃ <a name="P1.0056"></a> <span class="note">[pariddavānaṃ (sī. pī.)]</span> samatikkamāya, dukkhadomanassānaṃ atthaṅgamāya, ñāyassa adhigamāya, nibbānassa sacchikiriyāya, yadidaṃ cattāro satipaṭṭhānā.</p>\r\n<p class="bodytext">katame cattāro? idha, bhikkhave, bhikkhu kāye kāyānupassī viharati ātāpī sampajāno satimā, vineyya loke abhijjhādomanassaṃ, vedanāsu vedanānupassī viharati ātāpī sampajāno satimā, vineyya loke abhijjhādomanassaṃ, citte cittānupassī viharati ātāpī sampajāno satimā, vineyya loke abhijjhādomanassaṃ, dhammesu dhammānupassī viharati ātāpī sampajāno satimā, vineyya loke abhijjhādomanassaṃ.</p>\r\n<p class="centered">uddeso niṭṭhito.</p>\r\n<p class="subhead">kāyānupassanā ānāpānapabbaṃ</p>\r\n<p class="bodytext"><a name="para107"></a><a name="para107_mn1"></a><span class="paranum">107</span>. kathañca, bhikkhave, bhikkhu kāye kāyānupassī viharati? idha, bhikkhave, bhikkhu araññagato vā rukkhamūlagato vā suññāgāragato vā nisīdati, pallaṅkaṃ ābhujitvā, ujuṃ kāyaṃ paṇidhāya, parimukhaṃ satiṃ upaṭṭhapetvā. so satova assasati, satova <span class="note">[sato (sī. syā.)]</span> passasati. dīghaṃ <a name="T1.0104"></a> vā assasanto ‘dīghaṃ assasāmī’ti pajānāti, dīghaṃ vā passasanto ‘dīghaṃ passasāmī’ti pajānāti, rassaṃ vā assasanto ‘rassaṃ assasāmī’ti pajānāti, rassaṃ vā passasanto <a name="V1.0072"></a> ‘rassaṃ passasāmī’ti pajānāti, ‘sabbakāyapaṭisaṃvedī assasissāmī’ti sikkhati, ‘sabbakāyapaṭisaṃvedī passasissāmī’ti</p>\r\n','\r',char(13)),'\n',char(10)),'-105-106-107-');
INSERT INTO pages VALUES(3110,'mula_ma_01',71,replace(replace('<p class="bodytext">sikkhati<a name="M1.0071"></a>, ‘passambhayaṃ kāyasaṅkhāraṃ assasissāmī’ti sikkhati, ‘passambhayaṃ kāyasaṅkhāraṃ passasissāmī’ti sikkhati.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, dakkho bhamakāro vā bhamakārantevāsī vā dīghaṃ vā añchanto ‘dīghaṃ añchāmī’ti pajānāti, rassaṃ vā añchanto ‘rassaṃ añchāmī’ti pajānāti, evameva kho, bhikkhave, bhikkhu dīghaṃ vā assasanto ‘dīghaṃ assasāmī’ti pajānāti, dīghaṃ vā passasanto ‘dīghaṃ passasāmī’ti pajānāti, rassaṃ vā assasanto ‘rassaṃ assasāmī’ti pajānāti, rassaṃ vā passasanto ‘rassaṃ passasāmī’ti pajānāti, ‘sabbakāyapaṭisaṃvedī assasissāmī’ti sikkhati, ‘sabbakāyapaṭisaṃvedī passasissāmī’ti sikkhati, ‘passambhayaṃ kāyasaṅkhāraṃ assasissāmī’ti sikkhati, ‘passambhayaṃ kāyasaṅkhāraṃ passasissāmī’ti sikkhati. iti ajjhattaṃ vā kāye kāyānupassī viharati, bahiddhā vā kāye kāyānupassī viharati, ajjhattabahiddhā vā kāye kāyānupassī viharati, samudayadhammānupassī vā kāyasmiṃ viharati, vayadhammānupassī vā kāyasmiṃ viharati, samudayavayadhammānupassī vā kāyasmiṃ viharati. ‘atthi kāyo’ti vā panassa sati paccupaṭṭhitā hoti. yāvadeva <a name="T1.0105"></a> ñāṇamattāya paṭissatimattāya anissito ca viharati, na ca kiñci loke upādiyati. evampi kho <span class="note">[evampi (sī. syā. pī.)]</span>, bhikkhave, bhikkhu kāye kāyānupassī viharati.</p>\r\n<p class="centered">ānāpānapabbaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">kāyānupassanā iriyāpathapabbaṃ</p>\r\n<p class="bodytext"><a name="para108"></a><a name="para108_mn1"></a><span class="paranum">108</span>. puna caparaṃ, bhikkhave, bhikkhu gacchanto vā ‘gacchāmī’ti pajānāti, ṭhito vā ‘ṭhitomhī’ti pajānāti, nisinno vā ‘nisinnomhī’ti <a name="P1.0057"></a> pajānāti, sayāno vā ‘sayānomhī’ti pajānāti. yathā yathā vā panassa kāyo paṇihito hoti tathā tathā naṃ pajānāti. iti ajjhattaṃ vā kāye kāyānupassī viharati, bahiddhā vā kāye kāyānupassī viharati, ajjhattabahiddhā vā kāye kāyānupassī viharati, samudayadhammānupassī vā kāyasmiṃ viharati, vayadhammānupassī vā kāyasmiṃ viharati, samudayavayadhammānupassī vā kāyasmiṃ viharati.</p>\r\n','\r',char(13)),'\n',char(10)),'-108-');
INSERT INTO pages VALUES(3111,'mula_ma_01',72,replace(replace('<p class="bodytext">‘atthi <a name="M1.0072"></a><a name="V1.0073"></a> kāyo’ti vā panassa sati paccupaṭṭhitā hoti. yāvadeva ñāṇamattāya paṭissatimattāya anissito ca viharati, na ca kiñci loke upādiyati. evampi kho, bhikkhave, bhikkhu kāye kāyānupassī viharati.</p>\r\n<p class="centered">iriyāpathapabbaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">kāyānupassanā sampajānapabbaṃ</p>\r\n<p class="bodytext"><a name="para109"></a><a name="para109_mn1"></a><span class="paranum">109</span>. puna caparaṃ, bhikkhave, bhikkhu abhikkante paṭikkante sampajānakārī hoti, ālokite vilokite sampajānakārī hoti, samiñjite pasārite sampajānakārī hoti, saṅghāṭipattacīvaradhāraṇe sampajānakārī hoti, asite pīte khāyite sāyite sampajānakārī hoti, uccārapassāvakamme sampajānakārī hoti, gate ṭhite nisinne sutte jāgarite bhāsite tuṇhībhāve sampajānakārī hoti. iti ajjhattaṃ vā kāye kāyānupassī viharati…pe… evampi kho, bhikkhave, bhikkhu kāye kāyānupassī viharati.</p>\r\n<p class="centered">sampajānapabbaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">kāyānupassanā paṭikūlamanasikārapabbaṃ</p>\r\n<p class="bodytext"><a name="para110"></a><a name="para110_mn1"></a><span class="paranum">110</span>. puna caparaṃ, bhikkhave, bhikkhu imameva kāyaṃ uddhaṃ pādatalā, adho kesamatthakā, tacapariyantaṃ pūraṃ nānappakārassa asucino paccavekkhati – ‘atthi imasmiṃ kāye kesā lomā nakhā dantā taco maṃsaṃ <a name="T1.0106"></a> nhāru <span class="note">[nahāru (sī. syā. pī.)]</span> aṭṭhi aṭṭhimiñjaṃ vakkaṃ hadayaṃ yakanaṃ kilomakaṃ pihakaṃ papphāsaṃ antaṃ antaguṇaṃ udariyaṃ karīsaṃ pittaṃ semhaṃ pubbo lohitaṃ sedo medo assu vasā kheḷo siṅghāṇikā lasikā mutta’nti <span class="note">[muttaṃ matthaluṅganti (ka.)]</span>.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, ubhatomukhā putoḷi <span class="note">[mūtoḷī (sī. syā. pī.)]</span> pūrā nānāvihitassa dhaññassa, seyyathidaṃ – sālīnaṃ <a name="V1.0074"></a> vīhīnaṃ muggānaṃ māsānaṃ tilānaṃ taṇḍulānaṃ. tamenaṃ cakkhumā puriso muñcitvā paccavekkheyya – ‘ime sālī ime vīhī</p>\r\n','\r',char(13)),'\n',char(10)),'-109-110-');
INSERT INTO pages VALUES(3112,'mula_ma_01',73,replace(replace('<p class="bodytext">ime <a name="M1.0073"></a> muggā ime māsā ime tilā ime taṇḍulā’ti. evameva kho, bhikkhave, bhikkhu imameva kāyaṃ uddhaṃ pādatalā, adho kesamatthakā, tacapariyantaṃ pūraṃ nānappakārassa asucino paccavekkhati – ‘atthi imasmiṃ kāye kesā lomā…pe… mutta’nti.</p>\r\n<p class="bodytext">iti ajjhattaṃ vā kāye kāyānupassī viharati…pe… evampi kho, bhikkhave, bhikkhu kāye kāyānupassī viharati.</p>\r\n<p class="centered">paṭikūlamanasikārapabbaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">kāyānupassanā dhātumanasikārapabbaṃ</p>\r\n<p class="bodytext"><a name="para111"></a><a name="para111_mn1"></a><span class="paranum">111</span>. puna caparaṃ, bhikkhave, bhikkhu imameva kāyaṃ yathāṭhitaṃ yathāpaṇihitaṃ dhātuso paccavekkhati – ‘atthi imasmiṃ kāye pathavīdhātu āpodhātu tejodhātu vāyodhātū’ti.</p>\r\n<p class="bodytext">seyyathāpi<a name="P1.0058"></a>, bhikkhave, dakkho goghātako vā goghātakantevāsī vā gāviṃ vadhitvā catumahāpathe <span class="note">[cātummahāpathe (sī. syā. pī.)]</span> bilaso vibhajitvā nisinno assa. evameva kho, bhikkhave, bhikkhu imameva kāyaṃ yathāṭhitaṃ yathāpaṇihitaṃ dhātuso paccavekkhati – ‘atthi imasmiṃ kāye pathavīdhātu āpodhātu tejodhātu vāyodhātū’ti. iti ajjhattaṃ vā kāye kāyānupassī viharati…pe… <a name="V1.0075"></a> evampi kho, bhikkhave<a name="T1.0107"></a>, bhikkhu kāye kāyānupassī viharati.</p>\r\n<p class="centered">dhātumanasikārapabbaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">kāyānupassanā navasivathikapabbaṃ</p>\r\n<p class="bodytext"><a name="para112"></a><a name="para112_mn1"></a><span class="paranum">112</span>. puna caparaṃ, bhikkhave, bhikkhu seyyathāpi passeyya sarīraṃ sivathikāya chaḍḍitaṃ ekāhamataṃ vā dvīhamataṃ vā tīhamataṃ vā uddhumātakaṃ vinīlakaṃ vipubbakajātaṃ. so imameva kāyaṃ upasaṃharati – ‘ayampi kho kāyo evaṃdhammo evaṃbhāvī evaṃanatīto’ti <span class="note">[etaṃ anatītoti (sī. pī.)]</span>. iti ajjhattaṃ vā kāye kāyānupassī viharati…pe… evampi kho, bhikkhave, bhikkhu kāye kāyānupassī viharati.</p>\r\n','\r',char(13)),'\n',char(10)),'-111-112-');
INSERT INTO pages VALUES(3113,'mula_ma_01',74,replace(replace('<p class="bodytext">puna <a name="M1.0074"></a> caparaṃ, bhikkhave, bhikkhu seyyathāpi passeyya sarīraṃ sivathikāya chaḍḍitaṃ kākehi vā khajjamānaṃ kulalehi vā khajjamānaṃ gijjhehi vā khajjamānaṃ kaṅkehi vā khajjamānaṃ sunakhehi vā khajjamānaṃ byagghehi vā khajjamānaṃ dīpīhi vā khajjamānaṃ siṅgālehi vā <span class="note">[gijjhehi vā khajjamānaṃ, suvānehi vā khajjamānaṃ, sigālehi vā (syā. pī.)]</span> khajjamānaṃ vividhehi vā pāṇakajātehi khajjamānaṃ. so imameva kāyaṃ upasaṃharati – ‘ayampi kho kāyo evaṃdhammo evaṃbhāvī evaṃanatīto’ti. iti ajjhattaṃ vā kāye kāyānupassī viharati…pe… evampi kho, bhikkhave, bhikkhu kāye kāyānupassī viharati.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu seyyathāpi passeyya sarīraṃ sivathikāya chaḍḍitaṃ aṭṭhikasaṅkhalikaṃ samaṃsalohitaṃ nhārusambandhaṃ…pe… aṭṭhikasaṅkhalikaṃ nimaṃsalohitamakkhitaṃ nhārusambandhaṃ…pe… aṭṭhikasaṅkhalikaṃ apagatamaṃsalohitaṃ nhārusambandhaṃ…pe… aṭṭhikāni apagatasambandhāni <a name="V1.0076"></a> <span class="note">[apagatanhārusambandhāni (syā.)]</span> disā vidisā vikkhittāni, aññena hatthaṭṭhikaṃ aññena pādaṭṭhikaṃ <a name="T1.0108"></a> aññena gopphakaṭṭhikaṃ <span class="note">["aññena gopphakaṭṭhika"nti idaṃ sī. syā. pī. potthakesu natthi]</span> aññena jaṅghaṭṭhikaṃ aññena ūruṭṭhikaṃ aññena kaṭiṭṭhikaṃ <span class="note">[aññena kaṭaṭṭhikaṃ aññena piṭṭhaṭṭhikaṃ aññena kaṇṭakaṭṭhikaṃ aññena phāsukaṭṭhikaṃ aññena uraṭṭhikaṃ aññena aṃsaṭṭhikaṃ aññena bāhuṭṭhikaṃ (syā.)]</span> aññena phāsukaṭṭhikaṃ aññena piṭṭhiṭṭhikaṃ aññena khandhaṭṭhikaṃ <span class="note">[aññena kaṭaṭṭhikaṃ aññena piṭṭhaṭṭhikaṃ aññena kaṇṭakaṭṭhikaṃ aññena phāsukaṭṭhikaṃ aññena uraṭṭhikaṃ aññena aṃsaṭṭhikaṃ aññena bāhuṭṭhikaṃ (syā.)]</span> aññena gīvaṭṭhikaṃ aññena hanukaṭṭhikaṃ aññena dantaṭṭhikaṃ aññena sīsakaṭāhaṃ. so imameva kāyaṃ upasaṃharati – ‘ayampi kho kāyo evaṃdhammo evaṃbhāvī evaṃanatīto’ti. iti ajjhattaṃ vā kāye kāyānupassī viharati…pe… evampi kho, bhikkhave, bhikkhu kāye kāyānupassī viharati.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu seyyathāpi passeyya sarīraṃ sivathikāya chaḍḍitaṃ, aṭṭhikāni setāni saṅkhavaṇṇapaṭibhāgāni <span class="note">[saṅkhavaṇṇūpanibhāni (sī. syā. pī.)]</span> …pe… aṭṭhikāni puñjakitāni terovassikāni…pe… aṭṭhikāni pūtīni cuṇṇakajātāni<a name="P1.0059"></a>. so imameva kāyaṃ upasaṃharati – ‘ayampi kho kāyo evaṃdhammo evaṃbhāvī evaṃanatīto’ti. iti ajjhattaṃ vā kāye</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3114,'mula_ma_01',75,replace(replace('<p class="bodytext">kāyānupassī <a name="M1.0075"></a> viharati, bahiddhā vā kāye kāyānupassī viharati, ajjhattabahiddhā vā kāye kāyānupassī viharati, samudayadhammānupassī vā kāyasmiṃ viharati, vayadhammānupassī vā kāyasmiṃ viharati, samudayavayadhammānupassī vā kāyasmiṃ viharati. ‘atthi kāyo’ti vā panassa sati paccupaṭṭhitā hoti. yāvadeva ñāṇamattāya paṭissatimattāya anissito ca viharati, na ca kiñci loke upādiyati. evampi kho, bhikkhave, bhikkhu kāye kāyānupassī viharati.</p>\r\n<p class="centered">navasivathikapabbaṃ niṭṭhitaṃ.</p>\r\n<p class="centered">cuddasakāyānupassanā niṭṭhitā.</p>\r\n<p class="subhead">vedanānupassanā</p>\r\n<p class="bodytext"><a name="para113"></a><a name="para113_mn1"></a><span class="paranum">113</span>. kathañca <a name="T1.0109"></a><a name="V1.0077"></a> pana, bhikkhave, bhikkhu vedanāsu vedanānupassī viharati? idha, bhikkhave, bhikkhu sukhaṃ vā <span class="note">[sukhaṃ, dukkhaṃ, adukkhamasukhaṃ (sī. syā. pī. ka.)]</span> vedanaṃ vedayamāno ‘sukhaṃ vedanaṃ vedayāmī’ti pajānāti, dukkhaṃ vā <span class="note">[sukhaṃ, dukkhaṃ adukkhamasukhaṃ (sī. syā. pī. ka.)]</span> vedanaṃ vedayamāno ‘dukkhaṃ vedanaṃ vedayāmī’ti pajānāti, adukkhamasukhaṃ vā vedanaṃ vedayamāno ‘adukkhamasukhaṃ vedanaṃ vedayāmī’ti pajānāti, sāmisaṃ vā sukhaṃ vedanaṃ vedayamāno ‘sāmisaṃ sukhaṃ vedanaṃ vedayāmī’ti pajānāti, nirāmisaṃ vā sukhaṃ vedanaṃ vedayamāno ‘nirāmisaṃ sukhaṃ vedanaṃ vedayāmī’ti pajānāti, sāmisaṃ vā dukkhaṃ vedanaṃ vedayamāno ‘sāmisaṃ dukkhaṃ vedanaṃ vedayāmī’ti pajānāti, nirāmisaṃ vā dukkhaṃ vedanaṃ vedayamāno ‘nirāmisaṃ dukkhaṃ vedanaṃ vedayāmī’ti pajānāti, sāmisaṃ vā adukkhamasukhaṃ vedanaṃ vedayamāno ‘sāmisaṃ adukkhamasukhaṃ vedanaṃ vedayāmī’ti pajānāti, nirāmisaṃ vā adukkhamasukhaṃ vedanaṃ vedayamāno ‘nirāmisaṃ adukkhamasukhaṃ vedanaṃ vedayāmī’ti pajānāti, iti ajjhattaṃ vā vedanāsu vedanānupassī viharati, bahiddhā vā vedanāsu vedanānupassī viharati, ajjhattabahiddhā vā vedanāsu vedanānupassī viharati, samudayadhammānupassī vā vedanāsu viharati, vayadhammānupassī vā vedanāsu viharati, samudayavayadhammānupassī vā vedanāsu viharati. ‘atthi vedanā’ti vā panassa sati paccupaṭṭhitā hoti. yāvadeva ñāṇamattāya paṭissatimattāya anissito ca</p>\r\n','\r',char(13)),'\n',char(10)),'-113-');
INSERT INTO pages VALUES(3115,'mula_ma_01',76,replace(replace('<p class="bodytext">viharati<a name="M1.0076"></a>, na ca kiñci loke upādiyati. evampi kho, bhikkhave, bhikkhu vedanāsu vedanānupassī viharati.</p>\r\n<p class="centered">vedanānupassanā niṭṭhitā.</p>\r\n<p class="subhead">cittānupassanā</p>\r\n<p class="bodytext"><a name="para114"></a><a name="para114_mn1"></a><span class="paranum">114</span>. kathañca <a name="T1.0110"></a> pana, bhikkhave, bhikkhu citte cittānupassī viharati? idha, bhikkhave, bhikkhu sarāgaṃ vā cittaṃ ‘sarāgaṃ citta’nti pajānāti, vītarāgaṃ vā cittaṃ ‘vītarāgaṃ citta’nti pajānāti, sadosaṃ vā cittaṃ ‘sadosaṃ citta’nti pajānāti, vītadosaṃ vā cittaṃ ‘vītadosaṃ citta’nti pajānāti, samohaṃ vā cittaṃ ‘samohaṃ citta’nti pajānāti, vītamohaṃ vā cittaṃ ‘vītamohaṃ citta’nti pajānāti, saṃkhittaṃ vā cittaṃ ‘saṃkhittaṃ citta’nti pajānāti, vikkhittaṃ vā cittaṃ ‘vikkhittaṃ citta’nti pajānāti, mahaggataṃ vā cittaṃ ‘mahaggataṃ citta’nti pajānāti, amahaggataṃ vā cittaṃ ‘amahaggataṃ citta’nti pajānāti, sauttaraṃ <a name="V1.0078"></a> vā cittaṃ ‘sauttaraṃ citta’nti pajānāti, anuttaraṃ vā cittaṃ ‘anuttaraṃ citta’nti pajānāti, samāhitaṃ vā cittaṃ ‘samāhitaṃ citta’nti pajānāti, asamāhitaṃ vā cittaṃ ‘asamāhitaṃ citta’nti pajānāti, vimuttaṃ vā cittaṃ ‘vimuttaṃ citta’nti pajānāti, avimuttaṃ vā cittaṃ ‘avimuttaṃ citta’nti pajānāti. iti ajjhattaṃ vā citte cittānupassī viharati, bahiddhā vā citte cittānupassī viharati, ajjhattabahiddhā vā citte cittānupassī viharati, samudayadhammānupassī <a name="P1.0060"></a> vā cittasmiṃ viharati, vayadhammānupassī vā cittasmiṃ viharati, samudayavayadhammānupassī vā cittasmiṃ viharati. ‘atthi citta’nti vā panassa sati paccupaṭṭhitā hoti. yāvadeva ñāṇamattāya paṭissatimattāya anissito ca viharati, na ca kiñci loke upādiyati<a name="T1.0111"></a>. evampi kho, bhikkhave, bhikkhu citte cittānupassī viharati.</p>\r\n<p class="centered">cittānupassanā niṭṭhitā.</p>\r\n<p class="subhead">dhammānupassanā nīvaraṇapabbaṃ</p>\r\n<p class="bodytext"><a name="para115"></a><a name="para115_mn1"></a><span class="paranum">115</span>. kathañca, bhikkhave, bhikkhu dhammesu dhammānupassī viharati? idha, bhikkhave, bhikkhu dhammesu dhammānupassī viharati pañcasu nīvaraṇesu. kathañca pana, bhikkhave, bhikkhu dhammesu dhammānupassī viharati pañcasu nīvaraṇesu?</p>\r\n','\r',char(13)),'\n',char(10)),'-114-115-');
INSERT INTO pages VALUES(3116,'mula_ma_01',77,replace(replace('<p class="bodytext">idha<a name="M1.0077"></a>, bhikkhave, bhikkhu santaṃ vā ajjhattaṃ kāmacchandaṃ ‘atthi me ajjhattaṃ kāmacchando’ti pajānāti, asantaṃ vā ajjhattaṃ kāmacchandaṃ ‘natthi me ajjhattaṃ kāmacchando’ti pajānāti, yathā ca anuppannassa kāmacchandassa uppādo hoti tañca pajānāti, yathā ca uppannassa kāmacchandassa pahānaṃ hoti tañca pajānāti, yathā ca pahīnassa kāmacchandassa āyatiṃ anuppādo hoti tañca pajānāti.</p>\r\n<p class="bodytext">santaṃ vā ajjhattaṃ byāpādaṃ ‘atthi me ajjhattaṃ byāpādo’ti pajānāti, asantaṃ vā ajjhattaṃ byāpādaṃ ‘natthi me ajjhattaṃ byāpādo’ti pajānāti, yathā ca anuppannassa byāpādassa <a name="V1.0079"></a> uppādo hoti tañca pajānāti, yathā ca uppannassa byāpādassa pahānaṃ hoti tañca pajānāti, yathā ca pahīnassa byāpādassa āyatiṃ anuppādo hoti tañca pajānāti.</p>\r\n<p class="bodytext">santaṃ vā ajjhattaṃ thīnamiddhaṃ ‘atthi me ajjhattaṃ thīnamiddha’nti pajānāti, asantaṃ vā ajjhattaṃ thīnamiddhaṃ ‘natthi me ajjhattaṃ thīnamiddha’nti pajānāti, yathā ca anuppannassa thīnamiddhassa uppādo hoti tañca <a name="T1.0112"></a> pajānāti, yathā ca uppannassa thīnamiddhassa pahānaṃ hoti tañca pajānāti, yathā ca pahīnassa thīnamiddhassa āyatiṃ anuppādo hoti tañca pajānāti.</p>\r\n<p class="bodytext">santaṃ vā ajjhattaṃ uddhaccakukkuccaṃ ‘atthi me ajjhattaṃ uddhaccakukkucca’nti pajānāti, asantaṃ vā ajjhattaṃ uddhaccakukkuccaṃ ‘natthi me ajjhattaṃ uddhaccakukkucca’nti pajānāti, yathā ca anuppannassa uddhaccakukkuccassa uppādo hoti tañca pajānāti, yathā ca uppannassa uddhaccakukkuccassa pahānaṃ hoti tañca pajānāti, yathā ca pahīnassa uddhaccakukkuccassa āyatiṃ anuppādo hoti tañca pajānāti.</p>\r\n<p class="bodytext">santaṃ vā ajjhattaṃ vicikicchaṃ ‘atthi me ajjhattaṃ vicikicchā’ti pajānāti, asantaṃ vā ajjhattaṃ vicikicchaṃ ‘natthi me ajjhattaṃ vicikicchā’ti pajānāti, yathā ca anuppannāya vicikicchāya uppādo hoti tañca pajānāti, yathā ca uppannāya vicikicchāya pahānaṃ hoti tañca pajānāti, yathā ca pahīnāya vicikicchāya āyatiṃ anuppādo hoti tañca pajānāti.</p>\r\n<p class="bodytext">iti ajjhattaṃ vā dhammesu dhammānupassī viharati, bahiddhā vā dhammesu dhammānupassī viharati, ajjhattabahiddhā vā dhammesu dhammānupassī viharati, samudayadhammānupassī vā dhammesu viharati, vayadhammānupassī vā dhammesu</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3117,'mula_ma_01',78,replace(replace('<p class="bodytext">viharati<a name="M1.0078"></a>, samudayavayadhammānupassī vā dhammesu viharati. ‘atthi dhammā’ti vā panassa sati paccupaṭṭhitā hoti. yāvadeva ñāṇamattāya paṭissatimattāya <a name="T1.0113"></a> anissito ca viharati, na ca kiñci loke upādiyati. evampi kho, bhikkhave, bhikkhu dhammesu dhammānupassī viharati pañcasu nīvaraṇesu.</p>\r\n<p class="centered">nīvaraṇapabbaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">dhammānupassanā khandhapabbaṃ</p>\r\n<p class="bodytext"><a name="para116"></a><a name="para116_mn1"></a><span class="paranum">116</span>. puna <a name="V1.0080"></a> caparaṃ, bhikkhave, bhikkhu dhammesu dhammānupassī <a name="P1.0061"></a> viharati pañcasu upādānakkhandhesu. kathañca pana, bhikkhave, bhikkhu dhammesu dhammānupassī viharati pañcasu upādānakkhandhesu? idha, bhikkhave, bhikkhu – ‘iti rūpaṃ, iti rūpassa samudayo, iti rūpassa atthaṅgamo, iti vedanā, iti vedanāya samudayo, iti vedanāya atthaṅgamo, iti saññā, iti saññāya samudayo, iti saññāya atthaṅgamo, iti saṅkhārā, iti saṅkhārānaṃ samudayo, iti saṅkhārānaṃ atthaṅgamo, iti viññāṇaṃ, iti viññāṇassa samudayo, iti viññāṇassa atthaṅgamo’ti, iti ajjhattaṃ vā dhammesu dhammānupassī viharati, bahiddhā vā dhammesu dhammānupassī viharati, ajjhattabahiddhā vā dhammesu dhammānupassī viharati, samudayadhammānupassī vā dhammesu viharati, vayadhammānupassī vā dhammesu viharati, samudayavayadhammānupassī vā dhammesu viharati. ‘atthi dhammā’ti vā panassa sati paccupaṭṭhitā hoti. yāvadeva ñāṇamattāya paṭissatimattāya anissito ca viharati, na ca kiñci loke upādiyati. evampi kho, bhikkhave, bhikkhu dhammesu dhammānupassī viharati pañcasu upādānakkhandhesu.</p>\r\n<p class="centered">khandhapabbaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">dhammānupassanā āyatanapabbaṃ</p>\r\n<p class="bodytext"><a name="para117"></a><a name="para117_mn1"></a><span class="paranum">117</span>. puna caparaṃ, bhikkhave, bhikkhu dhammesu dhammānupassī viharati chasu ajjhattikabāhiresu āyatanesu. kathañca pana, bhikkhave, bhikkhu dhammesu dhammānupassī viharati chasu <a name="T1.0114"></a> ajjhattikabāhiresu āyatanesu?</p>\r\n','\r',char(13)),'\n',char(10)),'-116-117-');
INSERT INTO pages VALUES(3118,'mula_ma_01',79,replace(replace('<p class="bodytext">idha<a name="M1.0079"></a>, bhikkhave, bhikkhu cakkhuñca pajānāti, rūpe ca pajānāti, yañca tadubhayaṃ paṭicca uppajjati saṃyojanaṃ tañca pajānāti, yathā ca anuppannassa saṃyojanassa uppādo hoti tañca pajānāti, yathā ca uppannassa saṃyojanassa pahānaṃ hoti tañca pajānāti, yathā ca pahīnassa saṃyojanassa āyatiṃ anuppādo hoti tañca pajānāti.</p>\r\n<p class="bodytext">sotañca <a name="V1.0081"></a> pajānāti, sadde ca pajānāti, yañca tadubhayaṃ paṭicca uppajjati saṃyojanaṃ tañca pajānāti, yathā ca anuppannassa saṃyojanassa uppādo hoti tañca pajānāti, yathā ca uppannassa saṃyojanassa pahānaṃ hoti tañca pajānāti, yathā ca pahīnassa saṃyojanassa āyatiṃ anuppādo hoti tañca pajānāti.</p>\r\n<p class="bodytext">ghānañca pajānāti, gandhe ca pajānāti, yañca tadubhayaṃ paṭicca uppajjati saṃyojanaṃ tañca pajānāti, yathā ca anuppannassa saṃyojanassa uppādo hoti tañca pajānāti, yathā ca uppannassa saṃyojanassa pahānaṃ hoti tañca pajānāti, yathā ca pahīnassa saṃyojanassa āyatiṃ anuppādo hoti tañca pajānāti.</p>\r\n<p class="bodytext">jivhañca pajānāti, rase ca pajānāti, yañca tadubhayaṃ paṭicca uppajjati saṃyojanaṃ tañca pajānāti, yathā ca anuppannassa saṃyojanassa uppādo hoti tañca pajānāti, yathā ca uppannassa saṃyojanassa pahānaṃ hoti tañca pajānāti, yathā ca pahīnassa saṃyojanassa āyatiṃ anuppādo hoti tañca pajānāti.</p>\r\n<p class="bodytext">kāyañca pajānāti, phoṭṭhabbe ca pajānāti, yañca tadubhayaṃ paṭicca uppajjati saṃyojanaṃ tañca pajānāti, yathā ca anuppannassa saṃyojanassa uppādo hoti tañca pajānāti, yathā ca uppannassa saṃyojanassa pahānaṃ hoti tañca pajānāti, yathā ca pahīnassa saṃyojanassa āyatiṃ anuppādo hoti tañca pajānāti.</p>\r\n<p class="bodytext">manañca pajānāti, dhamme ca pajānāti, yañca tadubhayaṃ paṭicca uppajjati saṃyojanaṃ tañca pajānāti, yathā ca anuppannassa saṃyojanassa uppādo hoti tañca pajānāti, yathā ca uppannassa saṃyojanassa pahānaṃ hoti tañca pajānāti, yathā ca pahīnassa saṃyojanassa āyatiṃ anuppādo hoti tañca pajānāti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3119,'mula_ma_01',80,replace(replace('<p class="bodytext">iti <a name="M1.0080"></a> ajjhattaṃ vā dhammesu dhammānupassī viharati, bahiddhā vā dhammesu dhammānupassī viharati, ajjhattabahiddhā vā dhammesu dhammānupassī viharati, samudayadhammānupassī vā dhammesu viharati, vayadhammānupassī vā dhammesu viharati, samudayavayadhammānupassī vā dhammesu viharati. ‘atthi dhammā’ti vā panassa sati paccupaṭṭhitā hoti. yāvadeva ñāṇamattāya <a name="T1.0115"></a> paṭissatimattāya anissito <a name="V1.0082"></a> ca viharati na ca kiñci loke upādiyati. evampi kho, bhikkhave, bhikkhu dhammesu dhammānupassī viharati chasu ajjhattikabāhiresu āyatanesu.</p>\r\n<p class="centered">āyatanapabbaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">dhammānupassanā bojjhaṅgapabbaṃ</p>\r\n<p class="bodytext"><a name="para118"></a><a name="para118_mn1"></a><span class="paranum">118</span>. puna caparaṃ, bhikkhave, bhikkhu dhammesu dhammānupassī viharati sattasu bojjhaṅgesu. kathañca pana, bhikkhave, bhikkhu dhammesu dhammānupassī viharati sattasu bojjhaṅgesu? idha, bhikkhave, bhikkhu santaṃ vā ajjhattaṃ satisambojjhaṅgaṃ ‘atthi me ajjhattaṃ satisambojjhaṅgo’ti pajānāti, asantaṃ vā ajjhattaṃ satisambojjhaṅgaṃ ‘natthi me <a name="P1.0062"></a> ajjhattaṃ satisambojjhaṅgo’ti pajānāti, yathā ca anuppannassa satisambojjhaṅgassa uppādo hoti tañca pajānāti, yathā ca uppannassa satisambojjhaṅgassa bhāvanāya pāripūrī hoti tañca pajānāti.</p>\r\n<p class="bodytext">santaṃ vā ajjhattaṃ dhammavicayasambojjhaṅgaṃ ‘atthi me ajjhattaṃ dhammavicayasambojjhaṅgo’ti pajānāti, asantaṃ vā ajjhattaṃ dhammavicayasambojjhaṅgaṃ ‘natthi me ajjhattaṃ dhammavicayasambojjhaṅgo’ti pajānāti, yathā ca anuppannassa dhammavicayasambojjhaṅgassa uppādo hoti tañca pajānāti, yathā ca uppannassa dhammavicayasambojjhaṅgassa bhāvanāya pāripūrī hoti tañca pajānāti.</p>\r\n<p class="bodytext">santaṃ vā ajjhattaṃ vīriyasambojjhaṅgaṃ ‘atthi me ajjhattaṃ vīriyasambojjhaṅgo’ti pajānāti, asantaṃ vā ajjhattaṃ vīriyasambojjhaṅgaṃ ‘natthi me ajjhattaṃ vīriyasambojjhaṅgo’ti pajānāti, yathā ca anuppannassa vīriyasambojjhaṅgassa uppādo hoti tañca pajānāti, yathā ca uppannassa vīriyasambojjhaṅgassa bhāvanāya pāripūrī hoti tañca pajānāti.</p>\r\n','\r',char(13)),'\n',char(10)),'-118-');
INSERT INTO pages VALUES(3120,'mula_ma_01',81,replace(replace('<p class="bodytext">santaṃ <a name="M1.0081"></a> vā ajjhattaṃ pītisambojjhaṅgaṃ ‘atthi me ajjhattaṃ pītisambojjhaṅgo’ti pajānāti, asantaṃ vā ajjhattaṃ pītisambojjhaṅgaṃ ‘natthi me ajjhattaṃ pītisambojjhaṅgo’ti pajānāti<a name="V1.0083"></a>, yathā ca anuppannassa pītisambojjhaṅgassa uppādo hoti tañca pajānāti, yathā ca uppannassa pītisambojjhaṅgassa bhāvanāya pāripūrī hoti tañca pajānāti.</p>\r\n<p class="bodytext">santaṃ vā ajjhattaṃ passaddhisambojjhaṅgaṃ ‘atthi me ajjhattaṃ passaddhisambojjhaṅgo’ti pajānāti, asantaṃ vā ajjhattaṃ passaddhisambojjhaṅgaṃ ‘natthi me ajjhattaṃ passaddhisambojjhaṅgo’ti pajānāti, yathā ca anuppannassa passaddhisambojjhaṅgassa uppādo hoti tañca pajānāti, yathā ca uppannassa passaddhisambojjhaṅgassa bhāvanāya pāripūrī hoti tañca pajānāti.</p>\r\n<p class="bodytext">santaṃ vā ajjhattaṃ samādhisambojjhaṅgaṃ ‘atthi me ajjhattaṃ samādhisambojjhaṅgo’ti pajānāti, asantaṃ vā ajjhattaṃ samādhisambojjhaṅgaṃ ‘natthi me ajjhattaṃ samādhisambojjhaṅgo’ti pajānāti, yathā ca anuppannassa samādhisambojjhaṅgassa uppādo hoti tañca pajānāti, yathā ca uppannassa samādhisambojjhaṅgassa bhāvanāya pāripūrī hoti tañca pajānāti.</p>\r\n<p class="bodytext">santaṃ vā ajjhattaṃ upekkhāsambojjhaṅgaṃ ‘atthi me ajjhattaṃ upekkhāsambojjhaṅgo’ti pajānāti, asantaṃ vā ajjhattaṃ upekkhāsambojjhaṅgaṃ ‘natthi me ajjhattaṃ upekkhāsambojjhaṅgo’ti pajānāti, yathā ca anuppannassa upekkhāsambojjhaṅgassa uppādo hoti tañca pajānāti, yathā ca uppannassa upekkhāsambojjhaṅgassa bhāvanāya pāripūrī hoti tañca pajānāti<a name="T1.0116"></a>.</p>\r\n<p class="bodytext">iti ajjhattaṃ vā dhammesu dhammānupassī viharati, bahiddhā vā dhammesu dhammānupassī viharati, ajjhattabahiddhā vā dhammesu dhammānupassī viharati, samudayadhammānupassī vā dhammesu viharati, vayadhammānupassī vā dhammesu viharati, samudayavayadhammānupassī vā dhammesu viharati. ‘atthi dhammā’ti vā panassa sati paccupaṭṭhitā hoti. yāvadeva ñāṇamattāya paṭissatimattāya anissito ca viharati, na ca kiñci loke upādiyati. evampi kho, bhikkhave, bhikkhu dhammesu dhammānupassī viharati sattasu bojjhaṅgesu.</p>\r\n<p class="centered">bojjhaṅgapabbaṃ niṭṭhitaṃ <span class="note">[bojjhaṅgapabbaṃ niṭṭhitaṃ. paṭhamabhāṇavāraṃ (syā.)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3121,'mula_ma_01',82,replace(replace('<p class="subhead">dhammānupassanā saccapabbaṃ</p>\r\n<p class="bodytext"><a name="para119"></a><a name="para119_mn1"></a><span class="paranum">119</span>. puna <a name="M1.0082"></a><a name="V1.0084"></a> caparaṃ, bhikkhave, bhikkhu dhammesu dhammānupassī viharati catūsu ariyasaccesu. kathañca pana, bhikkhave, bhikkhu dhammesu dhammānupassī viharati catūsu ariyasaccesu? idha, bhikkhave, bhikkhu ‘idaṃ dukkha’nti yathābhūtaṃ pajānāti, ‘ayaṃ dukkhasamudayo’ti yathābhūtaṃ pajānāti, ‘ayaṃ dukkhanirodho’ti yathābhūtaṃ pajānāti, ‘ayaṃ dukkhanirodhagāminī paṭipadā’ti yathābhūtaṃ pajānāti.</p>\r\n<p class="centered">paṭhamabhāṇavāro niṭṭhito.</p>\r\n<p class="subhead">dukkhasaccaniddeso</p>\r\n<p class="bodytext"><a name="para120"></a><a name="para120_mn1"></a><span class="paranum">120</span>. katamañca<a name="T1.0117"></a>, bhikkhave, dukkhaṃ ariyasaccaṃ? jātipi dukkhā, jarāpi dukkhā, maraṇampi dukkhaṃ, sokaparidevadukkhadomanassupāyāsāpi dukkhā, appiyehi sampayogopi dukkho, piyehi vippayogopi dukkho <span class="note">["appiyehi…pe… vippayogopi dukkho"ti pāṭho ceva taṃniddeso ca sī. pī. potthakesu na dissati, sumaṅgalavilāsiniyaṃpi taṃsaṃvaṇṇanā natthi]</span>, yampicchaṃ na labhati tampi dukkhaṃ, saṃkhittena pañcupādānakkhandhā <span class="note">[pañcupādānakkhandhāpi (ka.)]</span> dukkhā.</p>\r\n<p class="bodytext"><a name="para121"></a><a name="para121_mn1"></a><span class="paranum">121</span>. katamā ca, bhikkhave, jāti? yā tesaṃ tesaṃ sattānaṃ tamhi tamhi sattanikāye jāti sañjāti okkanti abhinibbatti khandhānaṃ pātubhāvo āyatanānaṃ paṭilābho, ayaṃ vuccati, bhikkhave, jāti.</p>\r\n<p class="bodytext"><a name="para122"></a><a name="para122_mn1"></a><span class="paranum">122</span>. katamā ca, bhikkhave, jarā? yā tesaṃ tesaṃ sattānaṃ tamhi tamhi sattanikāye jarā jīraṇatā khaṇḍiccaṃ pāliccaṃ valittacatā āyuno saṃhāni indriyānaṃ paripāko, ayaṃ vuccati, bhikkhave, jarā.</p>\r\n<p class="bodytext"><a name="para123"></a><a name="para123_mn1"></a><span class="paranum">123</span>. katamañca, bhikkhave, maraṇaṃ? yaṃ <span class="note">[sumaṅgalavilāsinī oloketabbā]</span> tesaṃ tesaṃ sattānaṃ tamhā tamhā sattanikāyā cuti cavanatā bhedo antaradhānaṃ maccu maraṇaṃ kālaṅkiriyā khandhānaṃ bhedo kaḷevarassa nikkhepo jīvitindriyassupacchedo, idaṃ vuccati, bhikkhave, maraṇaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-119-120-121-122-123-');
INSERT INTO pages VALUES(3122,'mula_ma_01',83,replace(replace('<p class="bodytext"><a name="para124"></a><a name="para124_mn1"></a><span class="paranum">124</span>. katamo <a name="M1.0083"></a> ca, bhikkhave, soko? yo kho, bhikkhave, aññataraññatarena byasanena <a name="V1.0085"></a> samannāgatassa aññataraññatarena <a name="T1.0118"></a> dukkhadhammena phuṭṭhassa soko socanā socitattaṃ antosoko antoparisoko, ayaṃ vuccati, bhikkhave, soko.</p>\r\n<p class="bodytext"><a name="para125"></a><a name="para125_mn1"></a><span class="paranum">125</span>. katamo ca, bhikkhave, paridevo? yo kho, bhikkhave, aññataraññatarena byasanena samannāgatassa aññataraññatarena dukkhadhammena phuṭṭhassa ādevo paridevo ādevanā paridevanā ādevitattaṃ paridevitattaṃ, ayaṃ vuccati, bhikkhave, paridevo.</p>\r\n<p class="bodytext"><a name="para126"></a><a name="para126_mn1"></a><span class="paranum">126</span>. katamañca, bhikkhave, dukkhaṃ? yaṃ kho, bhikkhave, kāyikaṃ dukkhaṃ kāyikaṃ asātaṃ kāyasamphassajaṃ dukkhaṃ asātaṃ vedayitaṃ, idaṃ vuccati, bhikkhave, dukkhaṃ.</p>\r\n<p class="bodytext"><a name="para127"></a><a name="para127_mn1"></a><span class="paranum">127</span>. katamañca, bhikkhave, domanassaṃ? yaṃ kho, bhikkhave, cetasikaṃ dukkhaṃ cetasikaṃ asātaṃ manosamphassajaṃ dukkhaṃ asātaṃ vedayitaṃ, idaṃ vuccati, bhikkhave, domanassaṃ.</p>\r\n<p class="bodytext"><a name="para128"></a><a name="para128_mn1"></a><span class="paranum">128</span>. katamo ca, bhikkhave, upāyāso? yo kho, bhikkhave, aññataraññatarena byasanena samannāgatassa aññataraññatarena dukkhadhammena phuṭṭhassa āyāso upāyāso āyāsitattaṃ upāyāsitattaṃ, ayaṃ vuccati, bhikkhave, upāyāso.</p>\r\n<p class="bodytext"><a name="para129"></a><a name="para129_mn1"></a><span class="paranum">129</span>. katamo ca, bhikkhave, appiyehi sampayogo dukkho? idha yassa te honti aniṭṭhā akantā amanāpā rūpā saddā gandhā rasā phoṭṭhabbā dhammā, ye vā panassa te honti anatthakāmā ahitakāmā aphāsukakāmā ayogakkhemakāmā, yā tehi saddhiṃ saṅgati samāgamo samodhānaṃ missībhāvo, ayaṃ vuccati, bhikkhave, appiyehi sampayogo dukkho.</p>\r\n<p class="bodytext"><a name="para130"></a><a name="para130_mn1"></a><span class="paranum">130</span>. katamo ca, bhikkhave, piyehi vippayogo dukkho? idha yassa te honti iṭṭhā kantā manāpā rūpā saddā gandhā <a name="T1.0119"></a> rasā phoṭṭhabbā dhammā, ye vā panassa te honti atthakāmā hitakāmā phāsukakāmā yogakkhemakāmā mātā vā pitā vā bhātā vā bhaginī vā mittā vā amaccā vā ñātisālohitā vā, yā tehi saddhiṃ asaṅgati</p>\r\n','\r',char(13)),'\n',char(10)),'-124-125-126-127-128-129-130-');
INSERT INTO pages VALUES(3123,'mula_ma_01',84,replace(replace('<p class="bodytext">asamāgamo <a name="M1.0084"></a> asamodhānaṃ amissībhāvo, ayaṃ vuccati, bhikkhave, piyehi vippayogo dukkho.</p>\r\n<p class="bodytext"><a name="para131"></a><a name="para131_mn1"></a><span class="paranum">131</span>. katamañca, bhikkhave, yampicchaṃ na labhati tampi dukkhaṃ? jātidhammānaṃ, bhikkhave<a name="V1.0086"></a>, sattānaṃ evaṃ icchā uppajjati – ‘aho vata mayaṃ na jātidhammā assāma, na ca vata no jāti āgaccheyyā’ti. na kho panetaṃ icchāya pattabbaṃ, idampi yampicchaṃ na labhati tampi dukkhaṃ. jarādhammānaṃ, bhikkhave, sattānaṃ evaṃ icchā uppajjati – ‘aho vata mayaṃ na jarādhammā assāma, na ca vata no jarā āgaccheyyā’ti. na kho panetaṃ icchāya pattabbaṃ, idampi yampicchaṃ na labhati tampi dukkhaṃ. byādhidhammānaṃ, bhikkhave, sattānaṃ evaṃ icchā uppajjati – ‘aho vata mayaṃ na byādhidhammā assāma, na ca vata no byādhi āgaccheyyā’ti. na kho panetaṃ icchāya pattabbaṃ, idampi yampicchaṃ na labhati tampi dukkhaṃ. maraṇadhammānaṃ, bhikkhave, sattānaṃ evaṃ icchā uppajjati – ‘aho vata mayaṃ na maraṇadhammā assāma, na ca vata no maraṇaṃ āgaccheyyā’ti. na kho panetaṃ icchāya pattabbaṃ, idampi yampicchaṃ na labhati tampi dukkhaṃ. sokaparidevadukkhadomanassupāyāsadhammānaṃ, bhikkhave, sattānaṃ evaṃ icchā uppajjati – ‘aho vata mayaṃ na sokaparidevadukkhadomanassupāyāsadhammā assāma, na ca vata no sokaparidevadukkhadomanassupāyāsadhammā āgaccheyyu’nti. na kho panetaṃ icchāya pattabbaṃ, idampi yampicchaṃ na labhati tampi dukkhaṃ.</p>\r\n<p class="bodytext"><a name="para132"></a><a name="para132_mn1"></a><span class="paranum">132</span>. katame ca, bhikkhave, saṃkhittena pañcupādānakkhandhā dukkhā? seyyathidaṃ – rūpupādānakkhandho, vedanupādānakkhandho, saññupādānakkhandho, saṅkhārupādānakkhandho, viññāṇupādānakkhandho. ime vuccanti, bhikkhave, saṃkhittena pañcupādānakkhandhā dukkhā. idaṃ vuccati, bhikkhave, dukkhaṃ ariyasaccaṃ.</p>\r\n<p class="subhead">samudayasaccaniddeso</p>\r\n<p class="bodytext"><a name="para133"></a><a name="para133_mn1"></a><span class="paranum">133</span>. katamañca, bhikkhave, dukkhasamudayaṃ <span class="note">[dukkhasamudayo (syā.)]</span> ariyasaccaṃ? yāyaṃ taṇhā <a name="T1.0120"></a> ponobbhavikā <span class="note">[ponobhavikā (sī. pī.)]</span> nandīrāgasahagatā <span class="note">[nandirāgasahagatā (sī. syā. pī.)]</span> tatratatrābhinandinī. seyyathidaṃ – kāmataṇhā bhavataṇhā vibhavataṇhā.</p>\r\n','\r',char(13)),'\n',char(10)),'-131-132-133-');
INSERT INTO pages VALUES(3124,'mula_ma_01',85,replace(replace('<p class="bodytext">sā <a name="M1.0085"></a> kho panesā, bhikkhave, taṇhā kattha uppajjamānā uppajjati, kattha nivisamānā nivisati? yaṃ loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati.</p>\r\n<p class="bodytext">kiñca loke piyarūpaṃ sātarūpaṃ? cakkhu loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati. sotaṃ loke…pe… ghānaṃ loke… jivhā <a name="V1.0087"></a> loke… kāyo loke… mano loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati.</p>\r\n<p class="bodytext">rūpā loke… saddā loke… gandhā loke… rasā loke… phoṭṭhabbā loke… dhammā loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati.</p>\r\n<p class="bodytext">cakkhuviññāṇaṃ loke… sotaviññāṇaṃ loke… ghānaviññāṇaṃ loke… jivhāviññāṇaṃ loke… kāyaviññāṇaṃ loke… manoviññāṇaṃ loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati.</p>\r\n<p class="bodytext">cakkhusamphasso loke… sotasamphasso loke… ghānasamphasso loke… jivhāsamphasso loke… kāyasamphasso loke… manosamphasso loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati.</p>\r\n<p class="bodytext">cakkhusamphassajā vedanā loke… sotasamphassajā vedanā loke… ghānasamphassajā vedanā loke… jivhāsamphassajā vedanā loke… kāyasamphassajā vedanā loke… manosamphassajā vedanā loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati.</p>\r\n<p class="bodytext">rūpasaññā loke… saddasaññā <a name="T1.0121"></a> loke… gandhasaññā loke… rasasaññā loke… phoṭṭhabbasaññā loke… dhammasaññā loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3125,'mula_ma_01',86,replace(replace('<p class="bodytext">rūpasañcetanā <a name="M1.0086"></a> loke… saddasañcetanā loke… gandhasañcetanā loke… rasasañcetanā loke… phoṭṭhabbasañcetanā loke… dhammasañcetanā loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati.</p>\r\n<p class="bodytext">rūpataṇhā loke… saddataṇhā loke… gandhataṇhā loke… rasataṇhā loke… phoṭṭhabbataṇhā <a name="V1.0088"></a> loke… dhammataṇhā loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati.</p>\r\n<p class="bodytext">rūpavitakko loke… saddavitakko loke… gandhavitakko loke… rasavitakko loke… phoṭṭhabbavitakko loke… dhammavitakko loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati.</p>\r\n<p class="bodytext">rūpavicāro loke… saddavicāro loke… gandhavicāro loke… rasavicāro loke… phoṭṭhabbavicāro loke… dhammavicāro loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā uppajjamānā uppajjati, ettha nivisamānā nivisati. idaṃ vuccati, bhikkhave, dukkhasamudayaṃ ariyasaccaṃ.</p>\r\n<p class="subhead">nirodhasaccaniddeso</p>\r\n<p class="bodytext"><a name="para134"></a><a name="para134_mn1"></a><span class="paranum">134</span>. katamañca, bhikkhave, dukkhanirodhaṃ <span class="note">[dukkhanirodho (syā.)]</span> ariyasaccaṃ? yo tassāyeva taṇhāya asesavirāganirodho cāgo paṭinissaggo mutti anālayo.</p>\r\n<p class="bodytext">sā kho panesā, bhikkhave, taṇhā kattha pahīyamānā pahīyati, kattha nirujjhamānā nirujjhati? yaṃ loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā pahīyamānā pahīyati, ettha nirujjhamānā nirujjhati.</p>\r\n<p class="bodytext">kiñca loke piyarūpaṃ sātarūpaṃ? cakkhu loke piyarūpaṃ sātarūpaṃ, etthesā <a name="T1.0122"></a> taṇhā pahīyamānā pahīyati, ettha nirujjhamānā nirujjhati. sotaṃ loke…pe… ghānaṃ loke… jivhā loke… kāyo loke…</p>\r\n','\r',char(13)),'\n',char(10)),'-134-');
INSERT INTO pages VALUES(3126,'mula_ma_01',87,replace(replace('<p class="bodytext">mano <a name="M1.0087"></a> loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā pahīyamānā pahīyati, ettha nirujjhamānā nirujjhati.</p>\r\n<p class="bodytext">rūpā loke… saddā loke… gandhā loke… rasā loke… phoṭṭhabbā loke… dhammā loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā pahīyamānā pahīyati, ettha nirujjhamānā nirujjhati.</p>\r\n<p class="bodytext">cakkhuviññāṇaṃ <a name="V1.0089"></a> loke… sotaviññāṇaṃ loke… ghānaviññāṇaṃ loke… jivhāviññāṇaṃ loke… kāyaviññāṇaṃ loke… manoviññāṇaṃ loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā pahīyamānā pahīyati, ettha nirujjhamānā nirujjhati.</p>\r\n<p class="bodytext">cakkhusamphasso loke… sotasamphasso loke… ghānasamphasso loke… jivhāsamphasso loke… kāyasamphasso loke… manosamphasso loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā pahīyamānā pahīyati, ettha nirujjhamānā nirujjhati.</p>\r\n<p class="bodytext">cakkhusamphassajā vedanā loke… sotasamphassajā vedanā loke… ghānasamphassajā vedanā loke… jivhāsamphassajā vedanā loke… kāyasamphassajā vedanā loke… manosamphassajā vedanā loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā pahīyamānā pahīyati, ettha nirujjhamānā nirujjhati.</p>\r\n<p class="bodytext">rūpasaññā loke… saddasaññā loke… gandhasaññā loke… rasasaññā loke… phoṭṭhabbasaññā loke… dhammasaññā loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā pahīyamānā pahīyati, ettha nirujjhamānā nirujjhati.</p>\r\n<p class="bodytext">rūpasañcetanā loke… saddasañcetanā loke… gandhasañcetanā loke… rasasañcetanā loke… phoṭṭhabbasañcetanā loke… dhammasañcetanā loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā pahīyamānā pahīyati, ettha <a name="T1.0123"></a> nirujjhamānā nirujjhati.</p>\r\n<p class="bodytext">rūpataṇhā loke… saddataṇhā loke… gandhataṇhā loke… rasataṇhā loke… phoṭṭhabbataṇhā loke… dhammataṇhā loke</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3127,'mula_ma_01',88,replace(replace('<p class="bodytext">piyarūpaṃ <a name="M1.0088"></a> sātarūpaṃ, etthesā taṇhā pahīyamānā pahīyati, ettha nirujjhamānā nirujjhati.</p>\r\n<p class="bodytext">rūpavitakko loke… saddavitakko loke… gandhavitakko loke… rasavitakko loke… phoṭṭhabbavitakko loke… dhammavitakko loke piyarūpaṃ sātarūpaṃ, etthesā taṇhā pahīyamānā pahīyati, ettha nirujjhamānā nirujjhati.</p>\r\n<p class="bodytext">rūpavicāro loke… saddavicāro loke… gandhavicāro loke… rasavicāro loke… phoṭṭhabbavicāro <a name="V1.0090"></a> loke… dhammavicāro loke piyarūpaṃ sātarūpaṃ. etthesā taṇhā pahīyamānā pahīyati, ettha nirujjhāmānā nirujjhati. idaṃ vuccati, bhikkhave, dukkhanirodhaṃ ariyasaccaṃ.</p>\r\n<p class="subhead">maggasaccaniddeso</p>\r\n<p class="bodytext"><a name="para135"></a><a name="para135_mn1"></a><span class="paranum">135</span>. katamañca, bhikkhave, dukkhanirodhagāminī paṭipadā ariyasaccaṃ? ayameva ariyo aṭṭhaṅgiko maggo seyyathidaṃ – sammādiṭṭhi sammāsaṅkappo sammāvācā sammākammanto sammāājīvo sammāvāyāmo sammāsati sammāsamādhi.</p>\r\n<p class="bodytext">katamā ca, bhikkhave, sammādiṭṭhi? yaṃ kho, bhikkhave, dukkhe ñāṇaṃ, dukkhasamudaye ñāṇaṃ, dukkhanirodhe ñāṇaṃ, dukkhanirodhagāminiyā paṭipadāya ñāṇaṃ. ayaṃ vuccati, bhikkhave, sammādiṭṭhi.</p>\r\n<p class="bodytext">katamo ca, bhikkhave, sammāsaṅkappo? nekkhammasaṅkappo abyāpādasaṅkappo avihiṃsāsaṅkappo. ayaṃ vuccati, bhikkhave, sammāsaṅkappo.</p>\r\n<p class="bodytext">katamā ca, bhikkhave, sammāvācā? musāvādā veramaṇī <span class="note">[veramaṇi (ka.)]</span>, pisuṇāya vācāya veramaṇī, pharusāya vācāya veramaṇī, samphappalāpā veramaṇī<a name="T1.0124"></a>. ayaṃ vuccati, bhikkhave, sammāvācā.</p>\r\n<p class="bodytext">katamo ca, bhikkhave, sammākammanto? pāṇātipātā veramaṇī, adinnādānā veramaṇī, kāmesumicchācārā veramaṇī. ayaṃ vuccati, bhikkhave, sammākammanto.</p>\r\n','\r',char(13)),'\n',char(10)),'-135-');
INSERT INTO pages VALUES(3128,'mula_ma_01',89,replace(replace('<p class="bodytext">katamo <a name="M1.0089"></a> ca, bhikkhave, sammāājīvo? idha, bhikkhave, ariyasāvako micchāājīvaṃ pahāya sammāājīvena jīvitaṃ kappeti. ayaṃ vuccati, bhikkhave, sammāājīvo.</p>\r\n<p class="bodytext">katamo ca, bhikkhave, sammāvāyāmo? idha, bhikkhave, bhikkhu anuppannānaṃ pāpakānaṃ akusalānaṃ dhammānaṃ anuppādāya chandaṃ janeti vāyamati vīriyaṃ ārabhati cittaṃ paggaṇhāti padahati, uppannānaṃ pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya chandaṃ janeti vāyamati vīriyaṃ ārabhati cittaṃ paggaṇhāti padahati, anuppannānaṃ kusalānaṃ dhammānaṃ uppādāya chandaṃ janeti vāyamati vīriyaṃ ārabhati cittaṃ paggaṇhāti padahati, uppannānaṃ kusalānaṃ dhammānaṃ ṭhitiyā asammosāya bhiyyobhāvāya vepullāya bhāvanāya pāripūriyā chandaṃ janeti vāyamati vīriyaṃ ārabhati cittaṃ paggaṇhāti padahati. ayaṃ vuccati, bhikkhave, sammāvāyāmo.</p>\r\n<p class="bodytext">katamā ca, bhikkhave, sammāsati? idha, bhikkhave, bhikkhu kāye kāyānupassī viharati ātāpī <a name="V1.0091"></a> sampajāno satimā vineyya loke abhijjhādomanassaṃ, vedanāsu vedanānupassī viharati ātāpī sampajāno satimā vineyya loke abhijjhādomanassaṃ, citte cittānupassī viharati ātāpī sampajāno satimā vineyya loke abhijjhādomanassaṃ, dhammesu dhammānupassī viharati ātāpī sampajāno <a name="T1.0125"></a> satimā vineyya loke abhijjhādomanassaṃ. ayaṃ vuccati, bhikkhave, sammāsati.</p>\r\n<p class="bodytext">katamo ca, bhikkhave, sammāsamādhi? idha, bhikkhave, bhikkhu vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati. vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja viharati. pītiyā ca virāgā upekkhako ca viharati, sato ca sampajāno, sukhañca kāyena paṭisaṃvedeti, yaṃ taṃ ariyā ācikkhanti ‘upekkhako satimā sukhavihārī’ti tatiyaṃ jhānaṃ upasampajja viharati. sukhassa ca pahānā dukkhassa ca pahānā pubbeva somanassadomanassānaṃ atthaṅgamā adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave, sammāsamādhi. idaṃ vuccati, bhikkhave, dukkhanirodhagāminī paṭipadā ariyasaccaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3129,'mula_ma_01',90,replace(replace('<p class="bodytext"><a name="para136"></a><a name="para136_mn1"></a><span class="paranum">136</span>. iti <a name="M1.0090"></a> ajjhattaṃ vā dhammesu dhammānupassī viharati, bahiddhā vā dhammesu dhammānupassī viharati, ajjhattabahiddhā vā dhammesu dhammānupassī viharati, samudayadhammānupassī vā dhammesu viharati, vayadhammānupassī vā dhammesu viharati, samudayavayadhammānupassī vā dhammesu viharati. ‘atthi dhammā’ti vā panassa sati paccupaṭṭhitā hoti. yāvadeva ñāṇamattāya paṭissatimattāya anissito ca viharati, na ca kiñci loke upādiyati. evampi kho, bhikkhave, bhikkhu dhammesu <a name="T1.0126"></a> dhammānupassī viharati catūsu ariyasaccesu.</p>\r\n<p class="centered">saccapabbaṃ niṭṭhitaṃ.</p>\r\n<p class="centered">dhammānupassanā niṭṭhitā.</p>\r\n<p class="bodytext"><a name="para137"></a><a name="para137_mn1"></a><span class="paranum">137</span>. yo <a name="V1.0092"></a> hi koci, bhikkhave, ime cattāro satipaṭṭhāne evaṃ bhāveyya satta vassāni, tassa dvinnaṃ phalānaṃ aññataraṃ phalaṃ pāṭikaṅkhaṃ diṭṭheva dhamme aññā, sati vā upādisese anāgāmitā.</p>\r\n<p class="bodytext">tiṭṭhantu, bhikkhave, satta vassāni. yo hi koci<a name="P1.0063"></a>, bhikkhave, ime cattāro satipaṭṭhāne evaṃ bhāveyya cha vassāni…pe… pañca vassāni… cattāri vassāni… tīṇi vassāni… dve vassāni… ekaṃ vassaṃ… tiṭṭhatu, bhikkhave, ekaṃ vassaṃ. yo hi koci, bhikkhave, ime cattāro satipaṭṭhāne evaṃ bhāveyya satta māsāni, tassa dvinnaṃ phalānaṃ aññataraṃ phalaṃ pāṭikaṅkhaṃ diṭṭheva dhamme aññā, sati vā upādisese anāgāmitā. tiṭṭhantu, bhikkhave, satta māsāni. yo hi koci, bhikkhave, ime cattāro satipaṭṭhāne evaṃ bhāveyya cha māsāni…pe… pañca māsāni… cattāri māsāni… tīṇi māsāni… dve māsāni… ekaṃ māsaṃ… aḍḍhamāsaṃ… tiṭṭhatu, bhikkhave, aḍḍhamāso. yo hi koci, bhikkhave, ime cattāro satipaṭṭhāne evaṃ bhāveyya sattāhaṃ, tassa dvinnaṃ phalānaṃ aññataraṃ phalaṃ pāṭikaṅkhaṃ diṭṭheva dhamme aññā sati vā upādisese anāgāmitā"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-136-137-');
INSERT INTO pages VALUES(3130,'mula_ma_01',91,replace(replace('<p class="bodytext"><a name="para138"></a><a name="para138_mn1"></a><span class="paranum">138</span>. ‘ekāyano <a name="M1.0091"></a> ayaṃ, bhikkhave, maggo sattānaṃ visuddhiyā sokaparidevānaṃ samatikkamāya dukkhadomanassānaṃ atthaṅgamāya ñāyassa adhigamāya <a name="T1.0127"></a> nibbānassa sacchikiriyāya yadidaṃ cattāro satipaṭṭhānā’ti. iti yaṃ taṃ vuttaṃ, idametaṃ paṭicca vutta"nti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">mahāsatipaṭṭhānasuttaṃ niṭṭhitaṃ dasamaṃ.</p>\r\n<p class="bodytext">tassuddānaṃ – <span class="note">[ito paraṃ kesuci potthakesu imāpi gāthāyo evaṃ dissanti –§ajaraṃ amataṃ amatādhigamaṃ, phalamagganidassanaṃ dukkhanudaṃ. sahitattaṃ mahārasahassakaraṃ, bhūtamiti sāraṃ vividhaṃ suṇātha.§taḷākaṃ vasupūritaṃ ghammapathe, tividhaggipiḷesitanibbāpanaṃ. byādhipanudanaosadhayo, pacchimasuttapavarā ṭhapitā.§madhumandavarasāmadānaṃ, khiḍḍārati jananimanusaṅghātaṃ. tathā sutte veyyākaraṇā ṭhapitā, sakyaputtānamabhidamanatthāya.§paññāsaṃ ca diyaḍḍhasataṃ, dve ca veyyākaraṇaṃ apare ca. tevanāmagataṃ ca anupubbaṃ, ekamanā nisāmetha mudaggaṃ.]</span></p>\r\n<p class="gatha1">mūlasusaṃvaradhammadāyādā, bheravānaṅgaṇākaṅkheyyavatthaṃ,</p>\r\n<p class="gathalast">sallekhasammādiṭṭhisatipaṭṭhaṃ, vaggavaro asamo susamatto.</p>\r\n','\r',char(13)),'\n',char(10)),'-138-');
INSERT INTO pages VALUES(3131,'mula_ma_01',92,replace(replace('<p class="chapter">2. sīhanādavaggo</p>\r\n<p class="subhead">1. cūḷasīhanādasuttaṃ</p>\r\n<p class="bodytext"><a name="para139"></a><a name="para139_mn1"></a><span class="paranum">139</span>. evaṃ <a name="T1.0128"></a><a name="M1.0092"></a><a name="V1.0093"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext">idheva, bhikkhave, samaṇo, idha dutiyo samaṇo, idha tatiyo samaṇo, idha catuttho samaṇo, suññā parappavādā samaṇebhi <a name="P1.0064"></a> aññehīti <span class="note">[samaṇehi aññeti (sī. pī. ka.) ettha aññehīti sakāya paṭiññāya saccābhiññehīti attho veditabbo]</span>. evametaṃ <span class="note">[evameva (syā. ka.)]</span>, bhikkhave, sammā sīhanādaṃ nadatha.</p>\r\n<p class="bodytext"><a name="para140"></a><a name="para140_mn1"></a><span class="paranum">140</span>. ṭhānaṃ kho panetaṃ, bhikkhave, vijjati yaṃ aññatitthiyā paribbājakā evaṃ vadeyyuṃ – ‘ko panāyasmantānaṃ assāso, kiṃ balaṃ, yena tumhe āyasmanto evaṃ vadetha – idheva samaṇo, idha dutiyo samaṇo, idha tatiyo samaṇo, idha catuttho samaṇo, suññā parappavādā samaṇebhi aññehī’ti? evaṃvādino, bhikkhave, aññatitthiyā paribbājakā evamassu vacanīyā – ‘atthi kho no, āvuso, tena bhagavatā jānatā passatā arahatā sammāsambuddhena cattāro dhammā akkhātā ye mayaṃ attani sampassamānā evaṃ vadema – idheva samaṇo, idha dutiyo samaṇo, idha tatiyo samaṇo, idha <a name="T1.0129"></a> catuttho samaṇo, suññā parappavādā samaṇebhi aññehīti. katame cattāro? atthi kho no, āvuso, satthari pasādo, atthi dhamme pasādo, atthi sīlesu paripūrakāritā, sahadhammikā kho pana piyā manāpā – gahaṭṭhā ceva pabbajitā ca. ime kho no, āvuso, tena bhagavatā jānatā passatā arahatā sammāsambuddhena cattāro dhammā akkhātā ye mayaṃ attani sampassamānā evaṃ vadema – idheva samaṇo, idha dutiyo samaṇo, idha tatiyo samaṇo, idha catuttho samaṇo, suññā parappavādā samaṇebhi aññehī’ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-139-140-');
INSERT INTO pages VALUES(3132,'mula_ma_01',93,replace(replace('<p class="bodytext"><a name="para141"></a><a name="para141_mn1"></a><span class="paranum">141</span>. ṭhānaṃ <a name="M1.0093"></a><a name="V1.0094"></a> kho panetaṃ, bhikkhave, vijjati yaṃ aññatitthiyā paribbājakā evaṃ vadeyyuṃ – ‘amhākampi kho, āvuso, atthi satthari pasādo yo amhākaṃ satthā, amhākampi atthi dhamme pasādo yo amhākaṃ dhammo, mayampi sīlesu paripūrakārino yāni amhākaṃ sīlāni, amhākampi sahadhammikā piyā manāpā – gahaṭṭhā ceva pabbajitā ca. idha no, āvuso, ko viseso ko adhippayāso <span class="note">[adhippāyo (ka. sī. syā. pī.), adhippayogo (ka.)]</span> kiṃ nānākaraṇaṃ yadidaṃ tumhākañceva amhākañcā’ti?</p>\r\n<p class="bodytext">evaṃvādino, bhikkhave, aññatitthiyā paribbājakā evamassu vacanīyā – ‘kiṃ panāvuso, ekā niṭṭhā, udāhu puthu niṭṭhā’ti? sammā byākaramānā, bhikkhave, aññatitthiyā paribbājakā evaṃ byākareyyuṃ – ‘ekāvuso, niṭṭhā, na puthu niṭṭhā’ti.</p>\r\n<p class="bodytext">‘sā panāvuso, niṭṭhā sarāgassa udāhu vītarāgassā’ti? sammā byākaramānā, bhikkhave, aññatitthiyā paribbājakā <a name="T1.0130"></a> evaṃ byākareyyuṃ – ‘vītarāgassāvuso, sā niṭṭhā, na sā niṭṭhā sarāgassā’ti.</p>\r\n<p class="bodytext">‘sā panāvuso, niṭṭhā sadosassa udāhu vītadosassā’ti? sammā byākaramānā, bhikkhave, aññatitthiyā paribbājakā evaṃ byākareyyuṃ – ‘vītadosassāvuso, sā niṭṭhā, na sā niṭṭhā sadosassā’ti.</p>\r\n<p class="bodytext">‘sā panāvuso, niṭṭhā samohassa udāhu vītamohassā’ti? sammā byākaramānā, bhikkhave, aññatitthiyā paribbājakā evaṃ byākareyyuṃ – ‘vītamohassāvuso, sā niṭṭhā, na sā niṭṭhā samohassā’ti.</p>\r\n<p class="bodytext">‘sā panāvuso, niṭṭhā sataṇhassa udāhu vītataṇhassā’ti? sammā <a name="P1.0065"></a> byākaramānā, bhikkhave, aññatitthiyā paribbājakā evaṃ byākareyyuṃ – ‘vītataṇhassāvuso, sā niṭṭhā, na sā niṭṭhā sataṇhassā’ti.</p>\r\n<p class="bodytext">‘sā panāvuso, niṭṭhā saupādānassa udāhu anupādānassā’ti? sammā byākaramānā, bhikkhave<a name="V1.0095"></a>, aññatitthiyā paribbājakā evaṃ byākareyyuṃ – ‘anupādānassāvuso, sā niṭṭhā, na sā niṭṭhā saupādānassā’ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-141-');
INSERT INTO pages VALUES(3133,'mula_ma_01',94,replace(replace('<p class="bodytext">‘sā <a name="M1.0094"></a> panāvuso, niṭṭhā viddasuno udāhu aviddasuno’ti? sammā byākaramānā, bhikkhave, aññatitthiyā paribbājakā evaṃ byākareyyuṃ – ‘viddasuno, āvuso, sā niṭṭhā, na sā niṭṭhā aviddasuno’ti.</p>\r\n<p class="bodytext">‘sā panāvuso, niṭṭhā anuruddhappaṭiviruddhassa udāhu ananuruddhaappaṭiviruddhassā’ti? sammā byākaramānā, bhikkhave, aññatitthiyā paribbājakā evaṃ byākareyyuṃ – ‘ananuruddhaappaṭiviruddhassāvuso, sā niṭṭhā, na sā niṭṭhā anuruddhappaṭiviruddhassā’ti.</p>\r\n<p class="bodytext">‘sā <a name="T1.0131"></a> panāvuso, niṭṭhā papañcārāmassa papañcaratino udāhu nippapañcārāmassa nippapañcaratino’ti? sammā byākaramānā, bhikkhave, aññatitthiyā paribbājakā evaṃ byākareyyuṃ – ‘nippapañcārāmassāvuso, sā niṭṭhā nippapañcaratino, na sā niṭṭhā papañcārāmassa papañcaratino’ti.</p>\r\n<p class="bodytext"><a name="para142"></a><a name="para142_mn1"></a><span class="paranum">142</span>. dvemā, bhikkhave, diṭṭhiyo – bhavadiṭṭhi ca vibhavadiṭṭhi ca. ye hi keci, bhikkhave, samaṇā vā brāhmaṇā vā bhavadiṭṭhiṃ allīnā bhavadiṭṭhiṃ upagatā bhavadiṭṭhiṃ ajjhositā, vibhavadiṭṭhiyā te paṭiviruddhā. ye hi keci, bhikkhave, samaṇā vā brāhmaṇā vā vibhavadiṭṭhiṃ allīnā vibhavadiṭṭhiṃ upagatā vibhavadiṭṭhiṃ ajjhositā, bhavadiṭṭhiyā te paṭiviruddhā. ye hi keci, bhikkhave, samaṇā vā brāhmaṇā vā imāsaṃ dvinnaṃ diṭṭhīnaṃ samudayañca atthaṅgamañca assādañca ādīnavañca nissaraṇañca yathābhūtaṃ nappajānanti, ‘te sarāgā te sadosā te samohā te sataṇhā te saupādānā te aviddasuno te anuruddhappaṭiviruddhā te papañcārāmā papañcaratino, te na parimuccanti jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, na parimuccanti dukkhasmā’ti vadāmi. ye ca kho keci, bhikkhave, samaṇā vā brāhmaṇā vā imāsaṃ dvinnaṃ diṭṭhīnaṃ samudayañca atthaṅgamañca assādañca ādīnavañca nissaraṇañca yathābhūtaṃ pajānanti, ‘te vītarāgā te vītadosā te vītamohā te vītataṇhā te <a name="T1.0132"></a> anupādānā te viddasuno te ananuruddhaappaṭiviruddhā te nippapañcārāmā nippapañcaratino, te parimuccanti jātiyā jarāya <a name="V1.0096"></a> maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, parimuccanti dukkhasmā’ti vadāmi.</p>\r\n','\r',char(13)),'\n',char(10)),'-142-');
INSERT INTO pages VALUES(3134,'mula_ma_01',95,replace(replace('<p class="bodytext"><a name="para143"></a><a name="para143_mn1"></a><span class="paranum">143</span>. cattārimāni<a name="M1.0095"></a><a name="P1.0066"></a>, bhikkhave, upādānāni. katamāni cattāri? kāmupādānaṃ, diṭṭhupādānaṃ, sīlabbatupādānaṃ, attavādupādānaṃ. santi, bhikkhave, eke samaṇabrāhmaṇā sabbupādānapariññāvādā paṭijānamānā. te na sammā sabbupādānapariññaṃ paññapenti – kāmupādānassa pariññaṃ paññapenti, na diṭṭhupādānassa pariññaṃ paññapenti, na sīlabbatupādānassa pariññaṃ paññapenti, na attavādupādānassa pariññaṃ paññapenti. taṃ kissa hetu? imāni hi te bhonto samaṇabrāhmaṇā tīṇi ṭhānāni yathābhūtaṃ nappajānanti. tasmā te bhonto samaṇabrāhmaṇā sabbupādānapariññāvādā paṭijānamānā, te na sammā sabbupādānapariññaṃ paññapenti – kāmupādānassa pariññaṃ paññapenti, na diṭṭhupādānassa pariññaṃ paññapenti, na sīlabbatupādānassa pariññaṃ paññapenti, na attavādupādānassa pariññaṃ paññapenti.</p>\r\n<p class="bodytext">santi, bhikkhave, eke samaṇabrāhmaṇā sabbupādānapariññāvādā paṭijānamānā. te na sammā sabbupādānapariññaṃ paññapenti – kāmupādānassa pariññaṃ paññapenti, diṭṭhupādānassa pariññaṃ paññapenti, na sīlabbatupādānassa <a name="T1.0133"></a> pariññaṃ paññapenti, na attavādupādānassa pariññaṃ paññapenti. taṃ kissa hetu? imāni hi te bhonto samaṇabrāhmaṇā dve ṭhānāni yathābhūtaṃ nappajānanti. tasmā te bhonto samaṇabrāhmaṇā sabbupādānapariññāvādā paṭijānamānā, te na sammā <span class="note">[paṭijānamānā na sammā (?)]</span> sabbupādānapariññaṃ paññapenti – kāmupādānassa pariññaṃ paññapenti, diṭṭhupādānassa pariññaṃ paññapenti, na sīlabbatupādānassa pariññaṃ paññapenti, na attavādupādānassa pariññaṃ paññapenti.</p>\r\n<p class="bodytext">santi, bhikkhave, eke samaṇabrāhmaṇā sabbupādānapariññāvādā paṭijānamānā. te na sammā sabbupādānapariññaṃ paññapenti – kāmupādānassa pariññaṃ paññapenti, diṭṭhupādānassa pariññaṃ paññapenti, sīlabbatupādānassa pariññaṃ paññapenti, na attavādupādānassa pariññaṃ paññapenti. taṃ kissa hetu? imañhi te bhonto samaṇabrāhmaṇā ekaṃ ṭhānaṃ yathābhūtaṃ nappajānanti. tasmā te bhonto samaṇabrāhmaṇā sabbupādānapariññāvādā paṭijānamānā, te na sammā <span class="note">[paṭijānamānā na sammā (?)]</span> sabbupādānapariññaṃ paññapenti – kāmupādānassa pariññaṃ paññapenti, diṭṭhupādānassa pariññaṃ <a name="V1.0097"></a> paññapenti, sīlabbatupādānassa pariññaṃ paññapenti, na attavādupādānassa pariññaṃ paññapenti.</p>\r\n','\r',char(13)),'\n',char(10)),'-143-');
INSERT INTO pages VALUES(3135,'mula_ma_01',96,replace(replace('<p class="bodytext">evarūpe <a name="M1.0096"></a> kho, bhikkhave, dhammavinaye yo satthari pasādo so na sammaggato akkhāyati, yo <a name="T1.0134"></a> dhamme pasādo so na sammaggato akkhāyati, yā sīlesu paripūrakāritā sā na sammaggatā akkhāyati, yā sahadhammikesu piyamanāpatā sā na sammaggatā akkhāyati. taṃ kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ durakkhāte <a name="P1.0067"></a> dhammavinaye duppavedite aniyyānike anupasamasaṃvattanike asammāsambuddhappavedite.</p>\r\n<p class="bodytext"><a name="para144"></a><a name="para144_mn1"></a><span class="paranum">144</span>. tathāgato ca kho, bhikkhave, arahaṃ sammāsambuddho sabbupādānapariññāvādo paṭijānamāno sammā sabbupādānapariññaṃ paññapeti – kāmupādānassa pariññaṃ paññapeti, diṭṭhupādānassa pariññaṃ paññapeti, sīlabbatupādānassa pariññaṃ paññapeti, attavādupādānassa pariññaṃ paññapeti. evarūpe kho, bhikkhave, dhammavinaye yo satthari pasādo so sammaggato akkhāyati, yo dhamme pasādo so sammaggato akkhāyati, yā sīlesu paripūrakāritā sā sammaggatā akkhāyati, yā sahadhammikesu piyamanāpatā sā sammaggatā akkhāyati. taṃ kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ svākkhāte dhammavinaye suppavedite niyyānike upasamasaṃvattanike sammāsambuddhappavedite.</p>\r\n<p class="bodytext"><a name="para145"></a><a name="para145_mn1"></a><span class="paranum">145</span>. ime ca, bhikkhave, cattāro upādānā. kiṃnidānā kiṃsamudayā kiṃjātikā kiṃpabhavā? ime cattāro upādānā taṇhānidānā taṇhāsamudayā <a name="T1.0135"></a> taṇhājātikā taṇhāpabhavā. taṇhā cāyaṃ, bhikkhave, kiṃnidānā kiṃsamudayā kiṃjātikā kiṃpabhavā? taṇhā vedanānidānā vedanāsamudayā vedanājātikā vedanāpabhavā. vedanā cāyaṃ, bhikkhave, kiṃnidānā kiṃsamudayā kiṃjātikā kiṃpabhavā? vedanā phassanidānā phassasamudayā phassajātikā phassapabhavā. phasso cāyaṃ, bhikkhave, kiṃnidāno kiṃsamudayo kiṃjātiko kiṃpabhavo? phasso saḷāyatananidāno saḷāyatanasamudayo saḷāyatanajātiko saḷāyatanapabhavo. saḷāyatanañcidaṃ, bhikkhave, kiṃnidānaṃ kiṃsamudayaṃ kiṃjātikaṃ kiṃpabhavaṃ? saḷāyatanaṃ nāmarūpanidānaṃ nāmarūpasamudayaṃ nāmarūpajātikaṃ nāmarūpapabhavaṃ. nāmarūpañcidaṃ, bhikkhave, kiṃnidānaṃ kiṃsamudayaṃ kiṃjātikaṃ kiṃpabhavaṃ? nāmarūpaṃ viññāṇanidānaṃ viññāṇasamudayaṃ viññāṇajātikaṃ viññāṇapabhavaṃ. viññāṇañcidaṃ,</p>\r\n','\r',char(13)),'\n',char(10)),'-144-145-');
INSERT INTO pages VALUES(3136,'mula_ma_01',97,replace(replace('<p class="bodytext">bhikkhave<a name="M1.0097"></a>, kiṃnidānaṃ kiṃsamudayaṃ kiṃjātikaṃ <a name="V1.0098"></a> kiṃpabhavaṃ? viññāṇaṃ saṅkhāranidānaṃ saṅkhārasamudayaṃ saṅkhārajātikaṃ saṅkhārapabhavaṃ. saṅkhārā cime, bhikkhave, kiṃnidānā kiṃsamudayā kiṃjātikā kiṃpabhavā? saṅkhārā avijjānidānā avijjāsamudayā avijjājātikā avijjāpabhavā.</p>\r\n<p class="bodytext">yato ca kho, bhikkhave, bhikkhuno avijjā pahīnā hoti vijjā uppannā, so avijjāvirāgā vijjuppādā neva kāmupādānaṃ upādiyati, na diṭṭhupādānaṃ upādiyati, na sīlabbatupādānaṃ upādiyati, na attavādupādānaṃ upādiyati. anupādiyaṃ na paritassati, aparitassaṃ paccattaññeva parinibbāyati. ‘khīṇā jāti, vusitaṃ <a name="T1.0136"></a> brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti pajānātī"ti.</p>\r\n<p class="bodytext">idamavoca <a name="P1.0068"></a> bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">cūḷasīhanādasuttaṃ niṭṭhitaṃ paṭhamaṃ.</p>\r\n<p class="subhead">2. mahāsīhanādasuttaṃ</p>\r\n<p class="bodytext"><a name="para146"></a><a name="para146_mn1"></a><span class="paranum">146</span>. evaṃ <a name="T1.0137"></a><a name="V1.0099"></a> me sutaṃ – ekaṃ samayaṃ bhagavā vesāliyaṃ viharati bahinagare aparapure vanasaṇḍe. tena kho pana samayena sunakkhatto licchaviputto acirapakkanto hoti imasmā dhammavinayā. so vesāliyaṃ parisati <span class="note">[parisatiṃ (sī. pī.)]</span> evaṃ <span class="note">[etaṃ (pī. ka.)]</span> vācaṃ bhāsati – "natthi samaṇassa gotamassa uttari <span class="note">[uttariṃ (pī.)]</span> manussadhammā alamariyañāṇadassanaviseso. takkapariyāhataṃ samaṇo gotamo dhammaṃ deseti vīmaṃsānucaritaṃ sayaṃpaṭibhānaṃ. yassa ca khvāssa atthāya dhammo desito so niyyāti takkarassa sammā dukkhakkhayāyā"ti.</p>\r\n<p class="bodytext">atha kho āyasmā sāriputto pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya vesāliṃ piṇḍāya pāvisi. assosi kho āyasmā sāriputto sunakkhattassa licchaviputtassa vesāliyaṃ parisati evaṃ vācaṃ bhāsamānassa – "natthi samaṇassa gotamassa uttarimanussadhammā alamariyañāṇadassanaviseso. takkapariyāhataṃ samaṇo gotamo dhammaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-146-');
INSERT INTO pages VALUES(3137,'mula_ma_01',98,replace(replace('<p class="bodytext">deseti <a name="M1.0098"></a> vīmaṃsānucaritaṃ sayaṃpaṭibhānaṃ. yassa ca khvāssa atthāya dhammo desito so niyyāti takkarassa sammā dukkhakkhayāyā"ti.</p>\r\n<p class="bodytext">atha kho āyasmā sāriputto vesāliyaṃ piṇḍāya caritvā <a name="T1.0138"></a> pacchābhattaṃ piṇḍapātapaṭikkanto yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho āyasmā sāriputto bhagavantaṃ etadavoca – "sunakkhatto, bhante, licchaviputto acirapakkanto imasmā dhammavinayā. so vesāliyaṃ parisati evaṃ vācaṃ bhāsati – ‘natthi samaṇassa gotamassa uttarimanussadhammā alamariyañāṇadassanaviseso. takkapariyāhataṃ samaṇo gotamo dhammaṃ deseti vīmaṃsānucaritaṃ sayaṃpaṭibhānaṃ. yassa ca khvāssa atthāya dhammo desito so niyyāti takkarassa sammā dukkhakkhayāyā"’ti.</p>\r\n<p class="bodytext"><a name="para147"></a><a name="para147_mn1"></a><span class="paranum">147</span>. kodhano heso, sāriputta, sunakkhatto moghapuriso. kodhā ca panassa esā vācā bhāsitā. ‘avaṇṇaṃ bhāsissāmī’ti kho, sāriputta, sunakkhatto moghapuriso vaṇṇaṃyeva tathāgatassa <a name="V1.0100"></a> bhāsati<a name="P1.0069"></a>. vaṇṇo heso, sāriputta, tathāgatassa yo evaṃ vadeyya – ‘yassa ca khvāssa atthāya dhammo desito so niyyāti takkarassa sammā dukkhakkhayāyā’ti.</p>\r\n<p class="bodytext">ayampi hi nāma, sāriputta, sunakkhattassa moghapurisassa mayi dhammanvayo na bhavissati – ‘itipi so bhagavā arahaṃ sammāsambuddho vijjācaraṇasampanno sugato lokavidū anuttaro purisadammasārathi, satthā devamanussānaṃ, buddho bhagavā’ti.</p>\r\n<p class="bodytext">ayampi hi nāma, sāriputta, sunakkhattassa moghapurisassa mayi <a name="T1.0139"></a> dhammanvayo na bhavissati – ‘itipi so bhagavā anekavihitaṃ iddhividhaṃ paccanubhoti – ekopi hutvā bahudhā hoti, bahudhāpi hutvā eko hoti, āvibhāvaṃ, tirobhāvaṃ, tirokuṭṭaṃ tiropākāraṃ tiropabbataṃ asajjamāno gacchati, seyyathāpi ākāse, pathaviyāpi ummujjanimujjaṃ karoti, seyyathāpi udake, udakepi abhijjamāno gacchati, seyyathāpi pathaviyaṃ, ākāsepi pallaṅkena kamati, seyyathāpi pakkhī sakuṇo, imepi candimasūriye evaṃmahiddhike evaṃmahānubhāve pāṇinā parimasati parimajjati, yāva brahmalokāpi kāyena vasaṃ vattetī’ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-147-');
INSERT INTO pages VALUES(3138,'mula_ma_01',99,replace(replace('<p class="bodytext">ayampi <a name="M1.0099"></a> hi nāma, sāriputta, sunakkhattassa moghapurisassa mayi dhammanvayo na bhavissati – ‘itipi so bhagavā dibbāya sotadhātuyā visuddhāya atikkantamānusikāya ubho sadde suṇāti – dibbe ca mānuse ca, ye dūre santike cā’ti.</p>\r\n<p class="bodytext">ayampi hi nāma, sāriputta, sunakkhattassa moghapurisassa mayi dhammanvayo na bhavissati – ‘itipi so bhagavā parasattānaṃ parapuggalānaṃ cetasā ceto paricca pajānāti – sarāgaṃ vā cittaṃ sarāgaṃ cittanti pajānāti, vītarāgaṃ vā cittaṃ vītarāgaṃ cittanti pajānāti, sadosaṃ vā cittaṃ sadosaṃ cittanti pajānāti, vītadosaṃ vā cittaṃ vītadosaṃ cittanti pajānāti, samohaṃ vā cittaṃ samohaṃ cittanti pajānāti, vītamohaṃ vā cittaṃ vītamohaṃ cittanti pajānāti, saṃkhittaṃ vā cittaṃ saṃkhittaṃ cittanti pajānāti<a name="T1.0140"></a>, vikkhittaṃ vā cittaṃ vikkhittaṃ cittanti pajānāti, mahaggataṃ vā cittaṃ mahaggataṃ cittanti pajānāti, amahaggataṃ vā cittaṃ amahaggataṃ cittanti pajānāti, sauttaraṃ vā cittaṃ sauttaraṃ cittanti pajānāti, anuttaraṃ vā cittaṃ anuttaraṃ cittanti pajānāti, samāhitaṃ vā cittaṃ samāhitaṃ cittanti <a name="V1.0101"></a> pajānāti, asamāhitaṃ vā cittaṃ asamāhitaṃ cittanti pajānāti, vimuttaṃ vā cittaṃ vimuttaṃ cittanti pajānāti, avimuttaṃ vā cittaṃ avimuttaṃ cittanti pajānātī’ti.</p>\r\n<p class="bodytext"><a name="para148"></a><a name="para148_mn1"></a><span class="paranum">148</span>. dasa kho panimāni, sāriputta, tathāgatassa tathāgatabalāni yehi balehi samannāgato tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti. katamāni dasa?</p>\r\n<p class="bodytext">idha, sāriputta, tathāgato ṭhānañca ṭhānato aṭṭhānañca aṭṭhānato yathābhūtaṃ pajānāti. yampi, sāriputta, tathāgato ṭhānañca ṭhānato aṭṭhānañca aṭṭhānato yathābhūtaṃ pajānāti, idampi, sāriputta, tathāgatassa <a name="P1.0070"></a> tathāgatabalaṃ hoti yaṃ balaṃ āgamma tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext">puna caparaṃ, sāriputta, tathāgato atītānāgatapaccuppannānaṃ kammasamādānānaṃ ṭhānaso hetuso vipākaṃ yathābhūtaṃ pajānāti. yampi, sāriputta, tathāgato atītānāgatapaccuppannānaṃ kammasamādānānaṃ ṭhānaso hetuso vipākaṃ yathābhūtaṃ pajānāti, idampi, sāriputta, tathāgatassa tathāgatabalaṃ hoti yaṃ balaṃ āgamma tathāgato</p>\r\n','\r',char(13)),'\n',char(10)),'-148-');
INSERT INTO pages VALUES(3139,'mula_ma_01',100,replace(replace('<p class="bodytext">āsabhaṃ <a name="M1.0100"></a> ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext">puna caparaṃ, sāriputta, tathāgato sabbatthagāminiṃ paṭipadaṃ yathābhūtaṃ pajānāti. yampi<a name="T1.0141"></a>, sāriputta, tathāgato sabbatthagāminiṃ paṭipadaṃ yathābhūtaṃ pajānāti, idampi, sāriputta, tathāgatassa tathāgatabalaṃ hoti yaṃ balaṃ āgamma tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext">puna caparaṃ, sāriputta, tathāgato anekadhātunānādhātulokaṃ yathābhūtaṃ pajānāti. yampi, sāriputta, tathāgato anekadhātunānādhātulokaṃ yathābhūtaṃ pajānāti, idampi, sāriputta, tathāgatassa tathāgatabalaṃ hoti yaṃ balaṃ āgamma tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext">puna <a name="V1.0102"></a> caparaṃ, sāriputta, tathāgato sattānaṃ nānādhimuttikataṃ yathābhūtaṃ pajānāti. yampi, sāriputta, tathāgato sattānaṃ nānādhimuttikataṃ yathābhūtaṃ pajānāti, idampi, sāriputta, tathāgatassa tathāgatabalaṃ hoti yaṃ balaṃ āgamma tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext">puna caparaṃ, sāriputta, tathāgato parasattānaṃ parapuggalānaṃ indriyaparopariyattaṃ yathābhūtaṃ pajānāti. yampi, sāriputta, tathāgato parasattānaṃ parapuggalānaṃ indriyaparopariyattaṃ yathābhūtaṃ pajānāti, idampi, sāriputta, tathāgatassa tathāgatabalaṃ hoti yaṃ balaṃ āgamma tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext">puna caparaṃ, sāriputta, tathāgato jhānavimokkhasamādhisamāpattīnaṃ saṃkilesaṃ vodānaṃ vuṭṭhānaṃ yathābhūtaṃ pajānāti. yampi, sāriputta, tathāgato jhānavimokkhasamādhisamāpattīnaṃ <a name="T1.0142"></a> saṃkilesaṃ vodānaṃ vuṭṭhānaṃ yathābhūtaṃ pajānāti, idampi, sāriputta, tathāgatassa tathāgatabalaṃ hoti yaṃ balaṃ āgamma tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3140,'mula_ma_01',101,replace(replace('<p class="bodytext">puna <a name="M1.0101"></a> caparaṃ, sāriputta, tathāgato anekavihitaṃ pubbenivāsaṃ anussarati, seyyathidaṃ – ekampi jātiṃ dvepi jātiyo tissopi jātiyo catassopi jātiyo pañcapi jātiyo dasapi jātiyo vīsampi jātiyo tiṃsampi jātiyo cattālīsampi jātiyo paññāsampi jātiyo jātisatampi jātisahassampi jātisatasahassampi anekepi saṃvaṭṭakappe anekepi vivaṭṭakappe anekepi saṃvaṭṭavivaṭṭakappe – ‘amutrāsiṃ evaṃnāmo evaṃgotto evaṃvaṇṇo evamāhāro evaṃsukhadukkhappaṭisaṃvedī evamāyupariyanto, so tato cuto amutra udapādiṃ, tatrāpāsiṃ evaṃnāmo evaṃgotto evaṃvaṇṇo evamāhāro evaṃsukhadukkhappaṭisaṃvedī evamāyupariyanto, so tato cuto idhūpapanno’ti. iti sākāraṃ sauddesaṃ anekavihitaṃ pubbenivāsaṃ anussarati. yampi, sāriputta, tathāgato anekavihitaṃ pubbenivāsaṃ anussarati, seyyathidaṃ – ekampi jātiṃ dvepi jātiyo…pe… iti sākāraṃ sauddesaṃ anekavihitaṃ pubbenivāsaṃ anussarati, idampi, sāriputta, tathāgatassa tathāgatabalaṃ hoti yaṃ balaṃ āgamma tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext">puna caparaṃ, sāriputta, tathāgato dibbena cakkhunā visuddhena atikkantamānusakena <a name="T1.0143"></a> satte passati cavamāne upapajjamāne hīne paṇīte suvaṇṇe dubbaṇṇe sugate duggate yathākammūpage <a name="V1.0103"></a> satte pajānāti – ‘ime vata bhonto sattā kāyaduccaritena samannāgatā vacīduccaritena samannāgatā manoduccaritena samannāgatā ariyānaṃ upavādakā micchādiṭṭhikā micchādiṭṭhikammasamādānā, te kāyassa bhedā <a name="P1.0071"></a> paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapannā. ime vā pana bhonto sattā kāyasucaritena samannāgatā vacīsucaritena samannāgatā manosucaritena samannāgatā ariyānaṃ anupavādakā sammādiṭṭhikā sammādiṭṭhikammasamādānā, te kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapannā’ti. iti dibbena cakkhunā visuddhena atikkantamānusakena satte passati cavamāne upapajjamāne</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3141,'mula_ma_01',102,replace(replace('<p class="bodytext">hīne <a name="M1.0102"></a> paṇīte suvaṇṇe dubbaṇṇe sugate duggate yathākammūpage satte pajānāti. yampi, sāriputta, tathāgato dibbena cakkhunā visuddhena atikkantamānusakena satte passati cavamāne upapajjamāne hīne paṇīte suvaṇṇe dubbaṇṇe sugate duggate yathākammūpage satte pajānāti – ‘ime vata bhonto sattā kāyaduccaritena samannāgatā vacīduccaritena samannāgatā manoduccaritena samannāgatā ariyānaṃ upavādakā micchādiṭṭhikā micchādiṭṭhikammasamādānā, te kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapannā. ime vā pana bhonto sattā kāyasucaritena samannāgatā vacīsucaritena samannāgatā manosucaritena samannāgatā ariyānaṃ anupavādakā sammādiṭṭhikā sammādiṭṭhikammasamādānā, te kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapannā’ti. iti dibbena cakkhunā visuddhena atikkantamānusakena satte passati cavamāne upapajjamāne hīne paṇīte suvaṇṇe dubbaṇṇe sugate duggate yathākammūpage satte pajānāti. idampi, sāriputta, tathāgatassa tathāgatabalaṃ hoti yaṃ balaṃ āgamma tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext">puna caparaṃ, sāriputta, tathāgato āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja viharati. yampi, sāriputta, tathāgato āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja viharati, idampi, sāriputta, tathāgatassa tathāgatabalaṃ hoti yaṃ balaṃ āgamma tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext">imāni kho, sāriputta, dasa tathāgatassa tathāgatabalāni yehi balehi samannāgato tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext"><a name="para149"></a><a name="para149_mn1"></a><span class="paranum">149</span>. yo <a name="V1.0104"></a> kho maṃ, sāriputta, evaṃ jānantaṃ evaṃ passantaṃ evaṃ vadeyya – ‘natthi samaṇassa gotamassa uttarimanussadhammā alamariyañāṇadassanaviseso, takkapariyāhataṃ <a name="T1.0144"></a> samaṇo gotamo dhammaṃ deseti vīmaṃsānucaritaṃ sayaṃpaṭibhāna’nti, taṃ, sāriputta, vācaṃ appahāya taṃ cittaṃ appahāya taṃ diṭṭhiṃ appaṭinissajjitvā yathābhataṃ nikkhitto evaṃ niraye. seyyathāpi, sāriputta, bhikkhu sīlasampanno samādhisampanno paññāsampanno diṭṭheva</p>\r\n','\r',char(13)),'\n',char(10)),'-149-');
INSERT INTO pages VALUES(3142,'mula_ma_01',103,replace(replace('<p class="bodytext">dhamme <a name="M1.0103"></a> aññaṃ ārādheyya, evaṃ sampadamidaṃ, sāriputta, vadāmi. taṃ vācaṃ appahāya, taṃ cittaṃ appahāya taṃ diṭṭhiṃ appaṭinissajjitvā yathābhataṃ nikkhitto evaṃ niraye.</p>\r\n<p class="bodytext"><a name="para150"></a><a name="para150_mn1"></a><span class="paranum">150</span>. cattārimāni, sāriputta, tathāgatassa vesārajjāni yehi vesārajjehi samannāgato tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti. katamāni cattāri?</p>\r\n<p class="bodytext">‘sammāsambuddhassa te paṭijānato ime dhammā anabhisambuddhā’ti. tatra vata maṃ samaṇo vā brāhmaṇo vā devo vā māro vā brahmā vā koci vā lokasmiṃ sahadhammena <a name="P1.0072"></a> paṭicodessatīti nimittametaṃ, sāriputta, na samanupassāmi. etamahaṃ <span class="note">[etampahaṃ (sī. pī.)]</span>, sāriputta, nimittaṃ asamanupassanto khemappatto abhayappatto vesārajjappatto viharāmi.</p>\r\n<p class="bodytext">‘khīṇāsavassa te paṭijānato ime āsavā aparikkhīṇā’ti. tatra vata maṃ samaṇo vā brāhmaṇo vā devo vā māro vā brahmā vā koci vā lokasmiṃ sahadhammena paṭicodessatīti nimittametaṃ, sāriputta, na samanupassāmi. etamahaṃ, sāriputta, nimittaṃ asamanupassanto khemappatto abhayappatto <a name="T1.0145"></a> vesārajjappatto viharāmi.</p>\r\n<p class="bodytext">‘ye kho pana te antarāyikā dhammā vuttā, te paṭisevato nālaṃ antarāyāyā’ti. tatra vata maṃ samaṇo vā brāhmaṇo vā devo vā māro vā brahmā vā koci vā lokasmiṃ sahadhammena paṭicodessatīti nimittametaṃ, sāriputta, na samanupassāmi. etamahaṃ, sāriputta, nimittaṃ asamanupassanto khemappatto abhayappatto vesārajjappatto viharāmi.</p>\r\n<p class="bodytext">‘yassa kho pana te atthāya dhammo desito, so na niyyāti takkarassa sammā dukkhakkhayāyā’ti<a name="V1.0105"></a>. tatra vata maṃ samaṇo vā brāhmaṇo vā devo vā māro vā brahmā vā koci vā lokasmiṃ sahadhammena paṭicodessatī’ti nimittametaṃ, sāriputta, na samanupassāmi. etamahaṃ, sāriputta, nimittaṃ asamanupassanto khemappatto abhayappatto vesārajjappatto viharāmi.</p>\r\n','\r',char(13)),'\n',char(10)),'-150-');
INSERT INTO pages VALUES(3143,'mula_ma_01',104,replace(replace('<p class="bodytext">imāni <a name="M1.0104"></a> kho, sāriputta, cattāri tathāgatassa vesārajjāni yehi vesārajjehi samannāgato tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext">yo kho maṃ, sāriputta, evaṃ jānantaṃ evaṃ passantaṃ evaṃ vadeyya – ‘natthi samaṇassa gotamassa uttarimanussadhammā alamariyañāṇadassanaviseso, takkapariyāhataṃ samaṇo gotamo dhammaṃ deseti vīmaṃsānucaritaṃ sayaṃpaṭibhāna’nti, taṃ, sāriputta, vācaṃ appahāya taṃ cittaṃ appahāya taṃ diṭṭhiṃ appaṭinissajjitvā yathābhataṃ nikkhitto evaṃ niraye. seyyathāpi, sāriputta, bhikkhu sīlasampanno samādhisampanno paññāsampanno diṭṭheva dhamme <a name="T1.0146"></a> aññaṃ ārādheyya, evaṃ sampadamidaṃ, sāriputta, vadāmi. taṃ vācaṃ appahāya taṃ cittaṃ appahāya taṃ diṭṭhiṃ appaṭinissajjitvā yathābhataṃ nikkhitto evaṃ niraye.</p>\r\n<p class="bodytext"><a name="para151"></a><a name="para151_mn1"></a><span class="paranum">151</span>. aṭṭha kho imā, sāriputta, parisā. katamā aṭṭha? khattiyaparisā, brāhmaṇaparisā, gahapatiparisā, samaṇaparisā, cātumahārājikaparisā <span class="note">[cātummahārājikā (sī. syā. pī.)]</span>, tāvatiṃsaparisā, māraparisā, brahmaparisā – imā kho, sāriputta, aṭṭha parisā. imehi kho, sāriputta, catūhi vesārajjehi samannāgato tathāgato imā aṭṭha parisā upasaṅkamati ajjhogāhati. abhijānāmi kho panāhaṃ, sāriputta, anekasataṃ khattiyaparisaṃ upasaṅkamitā. tatrapi mayā sannisinnapubbañceva, sallapitapubbañca, sākacchā ca samāpajjitapubbā. tatra vata maṃ bhayaṃ vā sārajjaṃ vā okkamissatīti nimittametaṃ, sāriputta, na samanupassāmi. etamahaṃ, sāriputta, nimittaṃ asamanupassanto khemappatto abhayappatto vesārajjappatto viharāmi.</p>\r\n<p class="bodytext">abhijānāmi kho panāhaṃ, sāriputta, anekasataṃ brāhmaṇaparisaṃ…pe… gahapatiparisaṃ… samaṇaparisaṃ… cātumahārājikaparisaṃ… tāvatiṃsaparisaṃ… māraparisaṃ… brahmaparisaṃ upasaṅkamitā. tatrapi mayā sannisinnapubbañceva, sallapitapubbañca, sākacchā ca samāpajjitapubbā. tatra vata maṃ bhayaṃ vā sārajjaṃ vā okkamissatīti nimittametaṃ, sāriputta<a name="V1.0106"></a>, na samanupassāmi. etamahaṃ, sāriputta, nimittaṃ asamanupassanto khemappatto abhayappatto vesārajjappatto <a name="P1.0073"></a> viharāmi.</p>\r\n','\r',char(13)),'\n',char(10)),'-151-');
INSERT INTO pages VALUES(3144,'mula_ma_01',105,replace(replace('<p class="bodytext">yo <a name="M1.0105"></a> kho maṃ, sāriputta, evaṃ jānantaṃ evaṃ passantaṃ evaṃ vadeyya – ‘natthi samaṇassa gotamassa uttarimanussadhammā <a name="T1.0147"></a> alamariyañāṇadassanaviseso, takkapariyāhataṃ samaṇo gotamo dhammaṃ deseti vīmaṃsānucaritaṃ sayaṃpaṭibhāna’nti, taṃ, sāriputta, vācaṃ appahāya taṃ cittaṃ appahāya taṃ diṭṭhiṃ appaṭinissajjitvā yathābhataṃ nikkhitto evaṃ niraye. seyyathāpi, sāriputta, bhikkhu sīlasampanno samādhisampanno paññāsampanno diṭṭheva dhamme aññaṃ ārādheyya, evaṃ sampadamidaṃ, sāriputta, vadāmi. taṃ vācaṃ appahāya taṃ cittaṃ appahāya taṃ diṭṭhiṃ appaṭinissajjitvā yathābhataṃ nikkhitto evaṃ niraye.</p>\r\n<p class="bodytext"><a name="para152"></a><a name="para152_mn1"></a><span class="paranum">152</span>. catasso kho imā, sāriputta, yoniyo. katamā catasso? aṇḍajā yoni, jalābujā yoni, saṃsedajā yoni, opapātikā yoni. katamā ca, sāriputta, aṇḍajā yoni? ye kho te, sāriputta, sattā aṇḍakosaṃ abhinibbhijja jāyanti – ayaṃ vuccati, sāriputta, aṇḍajā yoni. katamā ca, sāriputta, jalābujā yoni? ye kho te, sāriputta, sattā vatthikosaṃ abhinibbhijja jāyanti – ayaṃ vuccati, sāriputta, jalābujā yoni. katamā ca, sāriputta, saṃsedajā yoni? ye kho te, sāriputta, sattā pūtimacche vā jāyanti pūtikuṇape vā pūtikummāse vā candanikāye vā oḷigalle vā jāyanti – ayaṃ vuccati, sāriputta, saṃsedajā yoni. katamā ca, sāriputta, opapātikā yoni? devā, nerayikā, ekacce ca manussā, ekacce ca vinipātikā – ayaṃ vuccati, sāriputta, opapātikā yoni. imā kho, sāriputta, catasso yoniyo.</p>\r\n<p class="bodytext">yo <a name="T1.0148"></a> kho maṃ, sāriputta, evaṃ jānantaṃ evaṃ passantaṃ evaṃ vadeyya – ‘natthi samaṇassa gotamassa uttarimanussadhammā alamariyañāṇadassanaviseso, takkapariyāhataṃ samaṇo gotamo dhammaṃ deseti vīmaṃsānucaritaṃ sayaṃpaṭibhāna’nti, taṃ, sāriputta, vācaṃ appahāya taṃ cittaṃ appahāya taṃ diṭṭhiṃ appaṭinissajjitvā yathābhataṃ nikkhitto evaṃ niraye. seyyathāpi, sāriputta, bhikkhu sīlasampanno samādhisampanno paññāsampanno diṭṭheva dhamme aññaṃ ārādheyya, evaṃ <a name="V1.0107"></a> sampadamidaṃ, sāriputta, vadāmi. taṃ vācaṃ appahāya taṃ cittaṃ appahāya taṃ diṭṭhiṃ appaṭinissajjitvā yathābhataṃ nikkhitto evaṃ niraye.</p>\r\n','\r',char(13)),'\n',char(10)),'-152-');
INSERT INTO pages VALUES(3145,'mula_ma_01',106,replace(replace('<p class="bodytext"><a name="para153"></a><a name="para153_mn1"></a><span class="paranum">153</span>. pañca <a name="M1.0106"></a> kho imā, sāriputta, gatiyo. katamā pañca? nirayo, tiracchānayoni, pettivisayo, manussā, devā. nirayañcāhaṃ, sāriputta, pajānāmi, nirayagāmiñca maggaṃ, nirayagāminiñca paṭipadaṃ, yathā paṭipanno ca kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjati tañca pajānāmi. tiracchānayoniñcāhaṃ, sāriputta, pajānāmi, tiracchānayonigāmiñca maggaṃ, tiracchānayonigāminiñca paṭipadaṃ, yathā paṭipanno ca kāyassa bhedā paraṃ maraṇā tiracchānayoniṃ upapajjati tañca pajānāmi. pettivisayaṃ cāhaṃ, sāriputta, pajānāmi, pettivisayagāmiñca maggaṃ, pettivisayagāminiñca paṭipadaṃ, yathā paṭipanno ca kāyassa bhedā paraṃ maraṇā pettivisayaṃ upapajjati tañca pajānāmi. manusse cāhaṃ, sāriputta, pajānāmi, manussalokagāmiñca maggaṃ<a name="T1.0149"></a>, manussalokagāminiñca paṭipadaṃ, yathā paṭipanno ca kāyassa bhedā paraṃ maraṇā manussesu upapajjati tañca pajānāmi. deve cāhaṃ, sāriputta, pajānāmi, devalokagāmiñca maggaṃ, devalokagāminiñca paṭipadaṃ, yathā paṭipanno ca kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjati tañca pajānāmi. nibbānañcāhaṃ, sāriputta, pajānāmi, nibbānagāmiñca maggaṃ, nibbānagāminiñca <a name="P1.0074"></a> paṭipadaṃ, yathā paṭipanno ca āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja viharati tañca pajānāmi.</p>\r\n<p class="bodytext"><a name="para154"></a><a name="para154_mn1"></a><span class="paranum">154</span>. idhāhaṃ, sāriputta, ekaccaṃ puggalaṃ evaṃ cetasā ceto paricca pajānāmi – tathāyaṃ puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjissatīti. tamenaṃ passāmi aparena samayena dibbena cakkhunā visuddhena atikkantamānusakena kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapannaṃ, ekantadukkhā tibbā kaṭukā vedanā vedayamānaṃ. seyyathāpi, sāriputta, aṅgārakāsu sādhikaporisā pūrā aṅgārānaṃ vītaccikānaṃ vītadhūmānaṃ. atha puriso āgaccheyya ghammābhitatto ghammapareto kilanto tasito pipāsito ekāyanena maggena tameva aṅgārakāsuṃ paṇidhāya. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘tathāyaṃ bhavaṃ puriso paṭipanno tathā ca <a name="T1.0150"></a> iriyati tañca maggaṃ samārūḷho, yathā imaṃyeva aṅgārakāsuṃ āgamissatī’ti<a name="V1.0108"></a>. tamenaṃ passeyya aparena samayena tassā</p>\r\n','\r',char(13)),'\n',char(10)),'-153-154-');
INSERT INTO pages VALUES(3146,'mula_ma_01',107,replace(replace('<p class="bodytext">aṅgārakāsuyā <a name="M1.0107"></a> patitaṃ, ekantadukkhā tibbā kaṭukā vedanā vedayamānaṃ. evameva kho ahaṃ, sāriputta, idhekaccaṃ puggalaṃ evaṃ cetasā ceto paricca pajānāmi – tathāyaṃ puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho yathā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjissatīti. tamenaṃ passāmi aparena samayena dibbena cakkhunā visuddhena atikkantamānusakena kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapannaṃ, ekantadukkhā tibbā kaṭukā vedanā vedayamānaṃ.</p>\r\n<p class="bodytext">idha panāhaṃ, sāriputta, ekaccaṃ puggalaṃ evaṃ cetasā ceto paricca pajānāmi – tathāyaṃ puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā kāyassa bhedā paraṃ maraṇā tiracchānayoniṃ upapajjissatīti. tamenaṃ passāmi aparena samayena dibbena cakkhunā visuddhena atikkantamānusakena kāyassa bhedā paraṃ maraṇā tiracchānayoniṃ upapannaṃ, dukkhā tibbā kaṭukā vedanā vedayamānaṃ. seyyathāpi, sāriputta, gūthakūpo sādhikaporiso, pūro gūthassa. atha puriso āgaccheyya ghammābhitatto ghammapareto <a name="P1.0075"></a> kilanto tasito pipāsito ekāyanena maggena tameva gūthakūpaṃ paṇidhāya. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘tathāyaṃ bhavaṃ puriso paṭipanno tathā <a name="T1.0151"></a> ca iriyati tañca maggaṃ samārūḷho yathā imaṃyeva gūthakūpaṃ āgamissatī’ti. tamenaṃ passeyya aparena samayena tasmiṃ gūthakūpe patitaṃ, dukkhā tibbā kaṭukā vedanā vedayamānaṃ. evameva kho ahaṃ, sāriputta, idhekaccaṃ puggalaṃ evaṃ cetasā ceto paricca pajānāmi – tathāyaṃ puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā kāyassa bhedā paraṃ maraṇā tiracchānayoniṃ upapajjissatīti. tamenaṃ passāmi aparena samayena dibbena cakkhunā visuddhena atikkantamānusakena kāyassa bhedā paraṃ maraṇā tiracchānayoniṃ upapannaṃ, dukkhā tibbā kaṭukā vedanā vedayamānaṃ.</p>\r\n<p class="bodytext">idha panāhaṃ, sāriputta, ekaccaṃ puggalaṃ evaṃ cetasā ceto paricca pajānāmi – tathāyaṃ puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā kāyassa bhedā paraṃ maraṇā pettivisayaṃ upapajjissatīti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3147,'mula_ma_01',108,replace(replace('<p class="bodytext">tamenaṃ <a name="M1.0108"></a> passāmi aparena samayena dibbena cakkhunā visuddhena atikkantamānusakena kāyassa bhedā paraṃ maraṇā pettivisayaṃ upapannaṃ, dukkhabahulā vedanā vedayamānaṃ. seyyathāpi, sāriputta, rukkho visame bhūmibhāge jāto tanupattapalāso kabaracchāyo<a name="V1.0109"></a>. atha puriso āgaccheyya ghammābhitatto ghammapareto kilanto tasito pipāsito ekāyanena maggena tameva rukkhaṃ paṇidhāya. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘tathāyaṃ bhavaṃ puriso paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā imaṃyeva rukkhaṃ āgamissatī’ti. tamenaṃ passeyya<a name="T1.0152"></a>, aparena samayena tassa rukkhassa chāyāya nisinnaṃ vā nipannaṃ vā dukkhabahulā vedanā vedayamānaṃ. evameva kho ahaṃ, sāriputta, idhekaccaṃ puggalaṃ evaṃ cetasā ceto paricca pajānāmi – tathāyaṃ puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā kāyassa bhedā paraṃ maraṇā pettivisayaṃ upapajjissatīti. tamenaṃ passāmi aparena samayena dibbena cakkhunā visuddhena atikkantamānusakena kāyassa bhedā paraṃ maraṇā pettivisayaṃ upapannaṃ, dukkhabahulā vedanā vedayamānaṃ.</p>\r\n<p class="bodytext">idha panāhaṃ, sāriputta, ekaccaṃ puggalaṃ evaṃ cetasā ceto paricca pajānāmi – tathāyaṃ puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho yathā kāyassa bhedā paraṃ maraṇā manussesu upapajjissatīti. tamenaṃ passāmi aparena samayena dibbena cakkhunā visuddhena atikkantamānusakena kāyassa bhedā paraṃ maraṇā manussesu upapannaṃ, sukhabahulā vedanā vedayamānaṃ. seyyathāpi, sāriputta, rukkho same bhūmibhāge jāto bahalapattapalāso sandacchāyo <span class="note">[saṇḍacchāyo (syā.), santacchāyo (ka.)]</span>. atha puriso āgaccheyya ghammābhitatto ghammapareto kilanto tasito pipāsito ekāyanena maggena tameva rukkhaṃ paṇidhāya. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘tathāyaṃ bhavaṃ puriso paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā imameva rukkhaṃ āgamissatī’ti. tamenaṃ passeyya aparena samayena tassa rukkhassa chāyāya nisinnaṃ vā nipannaṃ vā sukhabahulā vedanā vedayamānaṃ. evameva kho ahaṃ, sāriputta, idhekaccaṃ <a name="T1.0153"></a> puggalaṃ evaṃ cetasā ceto paricca pajānāmi – tathāyaṃ puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho yathā kāyassa bhedā paraṃ</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3148,'mula_ma_01',109,replace(replace('<p class="bodytext">maraṇā <a name="M1.0109"></a> manussesu upapajjissatīti. tamenaṃ passāmi aparena samayena dibbena cakkhunā visuddhena atikkantamānusakena kāyassa bhedā paraṃ maraṇā manussesu upapannaṃ, sukhabahulā vedanā vedayamānaṃ.</p>\r\n<p class="bodytext">idha <a name="P1.0076"></a> panāhaṃ, sāriputta, ekaccaṃ puggalaṃ evaṃ cetasā ceto paricca pajānāmi – tathāyaṃ puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjissatī’ti. tamenaṃ passāmi aparena samayena dibbena cakkhunā <a name="V1.0110"></a> visuddhena atikkantamānusakena kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapannaṃ, ekantasukhā vedanā vedayamānaṃ. seyyathāpi, sāriputta, pāsādo, tatrāssa kūṭāgāraṃ ullittāvalittaṃ nivātaṃ phusitaggaḷaṃ pihitavātapānaṃ. tatrāssa pallaṅko gonakatthato paṭikatthato paṭalikatthato kadalimigapavarapaccattharaṇo sauttaracchado ubhatolohitakūpadhāno. atha puriso āgaccheyya ghammābhitatto ghammapareto kilanto tasito pipāsito ekāyanena maggena tameva pāsādaṃ paṇidhāya. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘tathāyaṃ bhavaṃ puriso paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā imaṃyeva pāsādaṃ āgamissatī’ti. tamenaṃ passeyya aparena samayena tasmiṃ pāsāde tasmiṃ kūṭāgāre tasmiṃ pallaṅke nisinnaṃ vā nipannaṃ vā ekantasukhā vedanā vedayamānaṃ. evameva kho ahaṃ, sāriputta, idhekaccaṃ puggalaṃ evaṃ cetasā ceto paricca pajānāmi – tathāyaṃ <a name="T1.0154"></a> puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho yathā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjissatīti. tamenaṃ passāmi aparena samayena dibbena cakkhunā visuddhena atikkantamānusakena kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapannaṃ, ekantasukhā vedanā vedayamānaṃ.</p>\r\n<p class="bodytext">idha panāhaṃ, sāriputta, ekaccaṃ puggalaṃ cetasā ceto paricca pajānāmi – tathāyaṃ puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā āsavānaṃ khayā anāsaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja viharissatīti. tamenaṃ passāmi aparena samayena āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja viharantaṃ, ekantasukhā vedanā vedayamānaṃ. seyyathāpi, sāriputta, pokkharaṇī</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3149,'mula_ma_01',110,replace(replace('<p class="bodytext">acchodakā <a name="M1.0110"></a> sātodakā sītodakā setakā supatitthā ramaṇīyā. avidūre cassā tibbo vanasaṇḍo. atha puriso āgaccheyya ghammābhitatto ghammapareto kilanto tasito pipāsito ekāyanena maggena tameva pokkharaṇiṃ paṇidhāya. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘tathā bhavaṃ puriso paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā imaṃyeva pokkharaṇiṃ āgamissatī’ti. tamenaṃ passeyya aparena samayena taṃ pokkharaṇiṃ ogāhetvā nhāyitvā ca pivitvā ca sabbadarathakilamathapariḷāhaṃ paṭippassambhetvā <a name="T1.0155"></a> paccuttaritvā tasmiṃ vanasaṇḍe nisinnaṃ vā nipannaṃ <a name="P1.0077"></a> vā, ekantasukhā vedanā vedayamānaṃ. evameva kho ahaṃ, sāriputta, idhekaccaṃ puggalaṃ evaṃ cetasā ceto paricca pajānāmi – tathāyaṃ puggalo paṭipanno tathā ca iriyati tañca maggaṃ samārūḷho, yathā āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ <a name="V1.0111"></a> abhiññā sacchikatvā upasampajja viharissatī’ti. tamenaṃ passāmi aparena samayena āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja viharantaṃ, ekantasukhā vedanā vedayamānaṃ. imā kho, sāriputta, pañca gatiyo.</p>\r\n<p class="bodytext">yo kho maṃ, sāriputta, evaṃ jānantaṃ evaṃ passantaṃ evaṃ vadeyya – ‘natthi samaṇassa gotamassa uttarimanussadhammā alamariyañāṇadassanaviseso, takkapariyāhataṃ samaṇo gotamo dhammaṃ deseti vīmaṃsānucaritaṃ sayaṃpaṭibhāna’nti taṃ, sāriputta, vācaṃ appahāya taṃ cittaṃ appahāya taṃ diṭṭhiṃ appaṭinissajjitvā yathābhataṃ nikkhitto evaṃ niraye. seyyathāpi, sāriputta, bhikkhu sīlasampanno samādhisampanno paññāsampanno diṭṭheva dhamme aññaṃ ārādheyya, evaṃ sampadamidaṃ, sāriputta, vadāmi ‘taṃ vācaṃ appahāya taṃ cittaṃ appahāya taṃ diṭṭhiṃ appaṭinissajjitvā yathābhataṃ nikkhitto evaṃ niraye’.</p>\r\n<p class="bodytext"><a name="para155"></a><a name="para155_mn1"></a><span class="paranum">155</span>. abhijānāmi kho panāhaṃ, sāriputta, caturaṅgasamannāgataṃ brahmacariyaṃ <a name="T1.0156"></a> caritā <span class="note">[caritvā (ka.)]</span> – tapassī sudaṃ homi paramatapassī, lūkho sudaṃ <span class="note">[lūkhassudaṃ (sī. pī.)]</span> homi paramalūkho, jegucchī sudaṃ homi paramajegucchī, pavivitto sudaṃ <span class="note">[pavivittassudaṃ (sī. pī.)]</span> homi</p>\r\n','\r',char(13)),'\n',char(10)),'-155-');
INSERT INTO pages VALUES(3150,'mula_ma_01',111,replace(replace('<p class="bodytext">paramapavivitto<a name="M1.0111"></a>. tatrāssu me idaṃ, sāriputta, tapassitāya hoti – acelako homi muttācāro hatthāpalekhano <span class="note">[hatthāvalekhano (syā.)]</span>, na ehibhaddantiko na tiṭṭhabhaddantiko, nābhihaṭaṃ na uddissakataṃ na nimantanaṃ sādiyāmi. so na kumbhimukhā paṭiggaṇhāmi, na kaḷopimukhā paṭiggaṇhāmi, na eḷakamantaraṃ, na daṇḍamantaraṃ, na musalamantaraṃ, na dvinnaṃ bhuñjamānānaṃ, na gabbhiniyā, na pāyamānāya <span class="note">[pāyantiyā (ka.)]</span>, na purisantaragatāya, na saṅkittīsu, na yattha sā upaṭṭhito hoti, na yattha makkhikā saṇḍasaṇḍacārinī, na macchaṃ na maṃsaṃ na suraṃ na merayaṃ na thusodakaṃ pivāmi, so ekāgāriko vā homi ekālopiko, dvāgāriko <a name="P1.0078"></a> vā homi dvālopiko…pe… sattāgāriko vā homi sattālopiko, ekissāpi dattiyā yāpemi, dvīhipi dattīhi yāpemi…pe… sattahipi dattīhi yāpemi, ekāhikampi āhāraṃ āhāremi, dvīhikampi āhāraṃ āhāremi…pe… sattāhikampi āhāraṃ āhāremi, iti evarūpaṃ addhamāsikampi pariyāyabhattabhojanānuyogamanuyutto viharāmi.</p>\r\n<p class="bodytext">so sākabhakkho vā homi, sāmākabhakkho vā homi, nīvārabhakkho vā homi, daddulabhakkho vā homi, haṭabhakkho vā homi, kaṇabhakkho vā <a name="T1.0157"></a> homi, ācāmabhakkho vā homi<a name="V1.0112"></a>, piññākabhakkho vā homi, tiṇabhakkho vā homi, gomayabhakkho vā homi, vanamūlaphalāhāro yāpemi pavattaphalabhojī.</p>\r\n<p class="bodytext">so sāṇānipi dhāremi, masāṇānipi dhāremi, chavadussānipi dhāremi, paṃsukūlānipi dhāremi, tirīṭānipi dhāremi, ajinampi dhāremi, ajinakkhipampi dhāremi, kusacīrampi dhāremi, vākacīrampi dhāremi, phalakacīrampi dhāremi, kesakambalampi dhāremi, vāḷakambalampi dhāremi, ulūkapakkhampi dhāremi, kesamassulocakopi homi kesamassulocanānuyogamanuyutto, ubbhaṭṭhakopi homi āsanapaṭikkhitto, ukkuṭikopi homi ukkuṭikappadhānamanuyutto, kaṇṭakāpassayikopi homi kaṇṭakāpassaye seyyaṃ kappemi <span class="note">[imassānantare aññopi koci pāṭhapadeso aññesu ājīvakavatadīpakasuttesu dissati]</span>, sāyatatiyakampi udakorohanānuyogamanuyutto viharāmi – iti evarūpaṃ anekavihitaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3151,'mula_ma_01',112,replace(replace('<p class="bodytext">kāyassa <a name="M1.0112"></a> ātāpanaparitāpanānuyogamanuyutto viharāmi. idaṃsu me, sāriputta, tapassitāya hoti.</p>\r\n<p class="bodytext"><a name="para156"></a><a name="para156_mn1"></a><span class="paranum">156</span>. tatrāssu me idaṃ, sāriputta, lūkhasmiṃ hoti – nekavassagaṇikaṃ rajojallaṃ kāye sannicitaṃ hoti papaṭikajātaṃ. seyyathāpi, sāriputta, tindukakhāṇu nekavassagaṇiko sannicito hoti papaṭikajāto, evamevāssu me, sāriputta, nekavassagaṇikaṃ rajojallaṃ kāye sannicitaṃ hoti papaṭikajātaṃ. tassa mayhaṃ, sāriputta, na evaṃ hoti – ‘aho vatāhaṃ imaṃ rajojallaṃ pāṇinā parimajjeyyaṃ, aññe vā pana me imaṃ rajojallaṃ pāṇinā parimajjeyyu’nti. evampi me, sāriputta<a name="T1.0158"></a>, na hoti. idaṃsu me, sāriputta, lūkhasmiṃ hoti.</p>\r\n<p class="bodytext">tatrāssu me idaṃ, sāriputta, jegucchismiṃ hoti – so kho ahaṃ, sāriputta, satova abhikkamāmi, satova paṭikkamāmi, yāva udakabindumhipi me dayā paccupaṭṭhitā hoti – ‘māhaṃ khuddake pāṇe visamagate saṅghātaṃ āpādesi’nti. idaṃsu me, sāriputta, jegucchismiṃ hoti.</p>\r\n<p class="bodytext">tatrāssu me idaṃ, sāriputta, pavivittasmiṃ hoti – so kho <a name="P1.0079"></a> ahaṃ, sāriputta, aññataraṃ araññāyatanaṃ ajjhogāhetvā viharāmi. yadā passāmi gopālakaṃ vā pasupālakaṃ vā tiṇahārakaṃ vā kaṭṭhahārakaṃ vā vanakammikaṃ vā, vanena vanaṃ gahanena gahanaṃ ninnena ninnaṃ thalena thalaṃ saṃpatāmi <span class="note">[papatāmi (sī. syā. pī.)]</span>. taṃ kissa hetu? mā maṃ te addasaṃsu ahañca mā te addasanti. seyyathāpi, sāriputta, āraññako mago manusse disvā vanena vanaṃ gahanena gahanaṃ ninnena ninnaṃ <a name="V1.0113"></a> thalena thalaṃ saṃpatati, evameva kho ahaṃ, sāriputta, yadā passāmi gopālakaṃ vā pasupālakaṃ vā tiṇahārakaṃ vā kaṭṭhahārakaṃ vā vanakammikaṃ vā vanena vanaṃ gahanena gahanaṃ ninnena ninnaṃ thalena thalaṃ saṃpatāmi. taṃ kissa hetu? mā maṃ te addasaṃsu ahañca mā te addasanti. idaṃsu me, sāriputta, pavivittasmiṃ hoti.</p>\r\n<p class="bodytext">so kho ahaṃ, sāriputta, ye te goṭṭhā paṭṭhitagāvo apagatagopālakā, tattha catukkuṇḍiko upasaṅkamitvā yāni tāni vacchakānaṃ <a name="T1.0159"></a> taruṇakānaṃ dhenupakānaṃ gomayāni tāni sudaṃ āhāremi. yāvakīvañca</p>\r\n','\r',char(13)),'\n',char(10)),'-156-');
INSERT INTO pages VALUES(3152,'mula_ma_01',113,replace(replace('<p class="bodytext">me<a name="M1.0113"></a>, sāriputta, sakaṃ muttakarīsaṃ apariyādinnaṃ hoti, sakaṃyeva sudaṃ muttakarīsaṃ āhāremi. idaṃsu me, sāriputta, mahāvikaṭabhojanasmiṃ hoti.</p>\r\n<p class="bodytext"><a name="para157"></a><a name="para157_mn1"></a><span class="paranum">157</span>. so kho ahaṃ, sāriputta, aññataraṃ bhiṃsanakaṃ vanasaṇḍaṃ ajjhogāhetvā viharāmi. tatrāssudaṃ, sāriputta, bhiṃsanakassa vanasaṇḍassa bhiṃsanakatasmiṃ hoti – yo koci avītarāgo taṃ vanasaṇḍaṃ pavisati, yebhuyyena lomāni haṃsanti. so kho ahaṃ, sāriputta, yā tā rattiyo sītā hemantikā antaraṭṭhakā himapātasamayā <span class="note">[antaraṭṭhake himapātasamaye (sī. pī.)]</span> tathārūpāsu rattīsu rattiṃ abbhokāse viharāmi, divā vanasaṇḍe, gimhānaṃ pacchime māse divā abbhokāse viharāmi, rattiṃ vanasaṇḍe. apissu maṃ, sāriputta, ayaṃ anacchariyagāthā paṭibhāsi pubbe assutapubbā –</p>\r\n<p class="gatha1">sotatto sosinno <span class="note">[sosīno (sī. pī. ka.), sosino (syā.), sosindo (saddanīti)]</span> ceva, eko bhiṃsanake vane,</p>\r\n<p class="gathalast">naggo na caggimāsīno, esanāpasuto munī"ti.</p>\r\n<p class="bodytext">so kho ahaṃ, sāriputta, susāne seyyaṃ kappemi chavaṭṭhikāni upadhāya. apissu maṃ, sāriputta, gāmaṇḍalā <span class="note">[gomaṇḍalā (bahūsu) cariyāpiṭakaaṭṭhakathā oloketabbā]</span> upasaṅkamitvā oṭṭhubhantipi, omuttentipi, paṃsukenapi okiranti, kaṇṇasotesupi salākaṃ pavesenti. na kho panāhaṃ, sāriputta, abhijānāmi tesu pāpakaṃ cittaṃ uppādetā. idaṃsu me, sāriputta, upekkhāvihārasmiṃ hoti.</p>\r\n<p class="bodytext"><a name="para158"></a><a name="para158_mn1"></a><span class="paranum">158</span>. santi <a name="T1.0160"></a><a name="P1.0080"></a> kho pana, sāriputta, eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino – ‘āhārena <a name="V1.0114"></a> suddhī’ti. te evamāhaṃsu – ‘kolehi yāpemā’ti. te kolampi khādanti, kolacuṇṇampi khādanti, kolodakampi pivanti – anekavihitampi kolavikatiṃ paribhuñjanti. abhijānāmi kho panāhaṃ, sāriputta, ekaṃyeva kolaṃ āhāraṃ āhāritā. siyā kho pana te, sāriputta, evamassa – ‘mahā nūna tena samayena kolo ahosī’ti. na kho panetaṃ, sāriputta, evaṃ daṭṭhabbaṃ. tadāpi etaparamoyeva kolo ahosi seyyathāpi etarahi. tassa mayhaṃ, sāriputta,</p>\r\n','\r',char(13)),'\n',char(10)),'-157-158-');
INSERT INTO pages VALUES(3153,'mula_ma_01',114,replace(replace('<p class="bodytext">ekaṃyeva <a name="M1.0114"></a> kolaṃ āhāraṃ āhārayato adhimattakasimānaṃ patto kāyo hoti. seyyathāpi nāma āsītikapabbāni vā kāḷapabbāni vā, evamevassu me aṅgapaccaṅgāni bhavanti tāyevappāhāratāya. seyyathāpi nāma oṭṭhapadaṃ, evamevassu me ānisadaṃ hoti tāyevappāhāratāya. seyyathāpi nāma vaṭṭanāvaḷī, evamevassu me piṭṭhikaṇṭako unnatāvanato hoti tāyevappāhāratāya. seyyathāpi nāma jarasālāya gopānasiyo oluggaviluggā bhavanti, evamevassu me phāsuḷiyo oluggaviluggā bhavanti tāyevappāhāratāya. seyyathāpi nāma gambhīre udapāne udakatārakā gambhīragatā okkhāyikā dissanti, evamevassu me akkhikūpesu akkhitārakā gambhīragatā okkhāyikā dissanti tāyevappāhāratāya. seyyathāpi nāma tittakālābuāmakacchinno vātātapena saṃphuṭito <span class="note">[samphusito (syā.), saṃpuṭito (pī. ka.) ettha saṃphuṭitoti saṅkucitoti attho]</span> hoti sammilāto, evamevassu <a name="T1.0161"></a> me sīsacchavi saṃphuṭitā hoti sammilātā tāyevappāhāratāya. so kho ahaṃ, sāriputta, ‘udaracchaviṃ parimasissāmī’ti piṭṭhikaṇṭakaṃyeva pariggaṇhāmi, ‘piṭṭhikaṇṭakaṃ parimasissāmī’ti udaracchaviṃyeva pariggaṇhāmi, yāvassu me, sāriputta, udaracchavi piṭṭhikaṇṭakaṃ allīnā hoti tāyevappāhāratāya. so kho ahaṃ, sāriputta, ‘vaccaṃ vā muttaṃ vā karissāmī’ti tattheva avakujjo papatāmi tāyevappāhāratāya. so kho ahaṃ, sāriputta, tameva kāyaṃ assāsento pāṇinā gattāni anomajjāmi. tassa mayhaṃ, sāriputta, pāṇinā gattāni anomajjato pūtimūlāni lomāni kāyasmā patanti tāyevappāhāratāya.</p>\r\n<p class="bodytext"><a name="para159"></a><a name="para159_mn1"></a><span class="paranum">159</span>. santi kho pana, sāriputta, eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino – ‘āhārena suddhī’ti. te evamāhaṃsu – ‘muggehi yāpema…pe… tilehi yāpema…pe… taṇḍulehi yāpemā’ti. te taṇḍulampi khādanti, taṇḍulacuṇṇampi khādanti, taṇḍulodakampi <a name="P1.0081"></a> pivanti – anekavihitampi taṇḍulavikatiṃ paribhuñjanti. abhijānāmi kho panāhaṃ, sāriputta, ekaṃyeva taṇḍulaṃ āhāraṃ āhāritā. siyā kho pana te, sāriputta, evamassa – ‘mahā nūna tena samayena taṇḍulo ahosī’ti. na kho panetaṃ, sāriputta, evaṃ daṭṭhabbaṃ. tadāpi etaparamoyeva taṇḍulo ahosi<a name="V1.0115"></a>,</p>\r\n','\r',char(13)),'\n',char(10)),'-159-');
INSERT INTO pages VALUES(3154,'mula_ma_01',115,replace(replace('<p class="bodytext">seyyathāpi <a name="M1.0115"></a> etarahi. tassa mayhaṃ, sāriputta, ekaṃyeva taṇḍulaṃ āhāraṃ āhārayato adhimattakasimānaṃ <a name="T1.0162"></a> patto kāyo hoti. seyyathāpi nāma āsītikapabbāni vā kāḷapabbāni vā, evamevassu me aṅgapaccaṅgāni bhavanti tāyevappāhāratāya. seyyathāpi nāma oṭṭhapadaṃ, evamevassu me ānisadaṃ hoti tāyevappāhāratāya. seyyathāpi nāma vaṭṭanāvaḷī, evamevassu me piṭṭhikaṇṭako unnatāvanato hoti tāyevappāhāratāya. seyyathāpi nāma jarasālāya gopānasiyo oluggaviluggā bhavanti, evamevassu me phāsuḷiyo oluggaviluggā bhavanti tāyevappāhāratāya. seyyathāpi nāma gambhīre udapāne udakatārakā gambhīragatā okkhāyikā dissanti, evamevassu me akkhikūpesu akkhitārakā gambhīragatā okkhāyikā dissanti tāyevappāhāratāya. seyyathāpi nāma tittakālābu āmakacchinno vātātapena saṃphuṭito hoti sammilāto, evamevassu me sīsacchavi saṃphuṭitā hoti sammilātā tāyevappāhāratāya. so kho ahaṃ, sāriputta, ‘udaracchaviṃ parimasissāmī’ti piṭṭhikaṇṭakaṃyeva pariggaṇhāmi, ‘piṭṭhikaṇṭakaṃ parimasissāmī’ti udaracchaviṃyeva pariggaṇhāmi. yāvassu me, sāriputta, udaracchavi piṭṭhikaṇṭakaṃ allīnā hoti tāyevappāhāratāya. so kho ahaṃ, sāriputta, ‘vaccaṃ vā muttaṃ vā karissāmī’ti tattheva avakujjo papatāmi tāyevappāhāratāya. so kho ahaṃ, sāriputta, tameva kāyaṃ assāsento pāṇinā gattāni anomajjāmi. tassa mayhaṃ, sāriputta, pāṇinā gattāni anomajjato pūtimūlāni lomāni kāyasmā patanti tāyevappāhāratāya.</p>\r\n<p class="bodytext">tāyapi kho ahaṃ, sāriputta, iriyāya tāya paṭipadāya tāya dukkarakārikāya nājjhagamaṃ uttariṃ manussadhammā alamariyañāṇadassanavisesaṃ. taṃ kissa hetu? imissāyeva ariyāya paññāya anadhigamā, yāyaṃ ariyā paññā adhigatā ariyā niyyānikā, niyyāti takkarassa sammā dukkhakkhayāya.</p>\r\n<p class="bodytext"><a name="para160"></a><a name="para160_mn1"></a><span class="paranum">160</span>. santi kho pana, sāriputta, eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino – ‘saṃsārena suddhī’ti. na kho pana so <span class="note">[na kho paneso (sī. syā.)]</span>, sāriputta, saṃsāro</p>\r\n','\r',char(13)),'\n',char(10)),'-160-');
INSERT INTO pages VALUES(3155,'mula_ma_01',116,replace(replace('<p class="bodytext">sulabharūpo <a name="M1.0116"></a> yo mayā asaṃsaritapubbo <a name="P1.0082"></a> iminā dīghena addhunā, aññatra suddhāvāsehi devehi. suddhāvāse cāhaṃ, sāriputta, deve saṃsareyyaṃ, nayimaṃ lokaṃ punarāgaccheyyaṃ.</p>\r\n<p class="bodytext">santi kho pana, sāriputta, eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino – ‘upapattiyā <a name="V1.0116"></a> suddhī’ti. na kho pana sā, sāriputta<a name="T1.0163"></a>, upapatti sulabharūpā yā mayā anupapannapubbā iminā dīghena addhunā, aññatra suddhāvāsehi devehi. suddhāvāse cāhaṃ, sāriputta, deve upapajjeyyaṃ, nayimaṃ lokaṃ punarāgaccheyyaṃ.</p>\r\n<p class="bodytext">santi kho pana, sāriputta, eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino – ‘āvāsena suddhī’ti. na kho pana so, sāriputta, āvāso sulabharūpo yo mayā anāvuṭṭhapubbo <span class="note">[anāvutthapubbo (sī. pī.)]</span> iminā dīghena addhunā, aññatra suddhāvāsehi devehi. suddhāvāse cāhaṃ, sāriputta, deve āvaseyyaṃ, nayimaṃ lokaṃ punarāgaccheyyaṃ.</p>\r\n<p class="bodytext">santi kho pana, sāriputta, eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino – ‘yaññena suddhī’ti. na kho pana so, sāriputta, yañño sulabharūpo yo mayā ayiṭṭhapubbo iminā dīghena addhunā, tañca kho raññā vā satā khattiyena muddhāvasittena brāhmaṇena vā mahāsālena.</p>\r\n<p class="bodytext">santi kho pana, sāriputta, eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino – ‘aggiparicariyāya suddhī’ti. na kho pana so, sāriputta, aggi sulabharūpo yo mayā apariciṇṇapubbo iminā dīghena addhunā, tañca kho raññā vā satā khattiyena muddhāvasittena brāhmaṇena vā mahāsālena.</p>\r\n<p class="bodytext"><a name="para161"></a><a name="para161_mn1"></a><span class="paranum">161</span>. santi kho pana, sāriputta, eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino – ‘yāvadevāyaṃ bhavaṃ puriso daharo hoti yuvā <a name="T1.0164"></a> susukāḷakeso bhadrena yobbanena samannāgato paṭhamena vayasā tāvadeva paramena paññāveyyattiyena samannāgato hoti. yato ca kho ayaṃ bhavaṃ puriso jiṇṇo hoti vuddho mahallako addhagato vayoanuppatto, āsītiko vā nāvutiko vā vassasatiko vā jātiyā, atha tamhā paññāveyyattiyā, parihāyatī’ti. na kho panetaṃ,</p>\r\n','\r',char(13)),'\n',char(10)),'-161-');
INSERT INTO pages VALUES(3156,'mula_ma_01',117,replace(replace('<p class="bodytext">sāriputta<a name="M1.0117"></a>, evaṃ daṭṭhabbaṃ. ahaṃ kho pana, sāriputta, etarahi jiṇṇo vuddho mahallako addhagato vayoanuppatto, āsītiko me vayo vattati. idha me assu, sāriputta, cattāro sāvakā vassasatāyukā vassasatajīvino, paramāya satiyā ca gatiyā ca dhitiyā ca samannāgatā paramena ca paññāveyyattiyena. seyyathāpi, sāriputta, daḷhadhammā <span class="note">[daḷhadhammo (bahūsu) ṭīkā ca moggallānabyākaraṇaṃ ca oloketabbaṃ]</span> dhanuggaho sikkhito katahattho katūpāsano lahukena asanena appakasireneva tiriyaṃ tālacchāyaṃ atipāteyya, evaṃ adhimattasatimanto <a name="V1.0117"></a> evaṃ adhimattagatimanto evaṃ <a name="P1.0083"></a> adhimattadhitimanto evaṃ paramena paññāveyyattiyena samannāgatā. te maṃ catunnaṃ satipaṭṭhānānaṃ upādāyupādāya pañhaṃ puccheyyuṃ, puṭṭho puṭṭho cāhaṃ tesaṃ byākareyyaṃ, byākatañca me byākatato dhāreyyuṃ, na ca maṃ dutiyakaṃ uttari paṭipuccheyyuṃ. aññatra asitapītakhāyitasāyitā aññatra uccārapassāvakammā, aññatra niddākilamathapaṭivinodanā apariyādinnāyevassa, sāriputta, tathāgatassa dhammadesanā, apariyādinnaṃyevassa tathāgatassa dhammapadabyañjanaṃ, apariyādinnaṃyevassa tathāgatassa pañhapaṭibhānaṃ<a name="T1.0165"></a>. atha me te cattāro sāvakā vassasatāyukā vassasatajīvino vassasatassa accayena kālaṃ kareyyuṃ. mañcakena cepi maṃ, sāriputta, pariharissatha, nevatthi tathāgatassa paññāveyyattiyassa aññathattaṃ. yaṃ kho taṃ <span class="note">[yaṃ kho panetaṃ (sī.)]</span>, sāriputta, sammā vadamāno vadeyya – ‘asammohadhammo satto loke uppanno bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussāna’nti, mameva taṃ sammā vadamāno vadeyya ‘asammohadhammo satto loke uppanno bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussāna"’nti.</p>\r\n<p class="bodytext"><a name="para162"></a><a name="para162_mn1"></a><span class="paranum">162</span>. tena kho pana samayena āyasmā nāgasamālo bhagavato piṭṭhito ṭhito hoti bhagavantaṃ bījayamāno. atha kho āyasmā nāgasamālo bhagavantaṃ etadavoca – "acchariyaṃ, bhante, abbhutaṃ, bhante! api hi me, bhante, imaṃ dhammapariyāyaṃ sutvā lomāni haṭṭhāni. konāmo ayaṃ, bhante, dhammapariyāyo"ti? "tasmātiha tvaṃ, nāgasamāla, imaṃ dhammapariyāyaṃ lomahaṃsanapariyāyo tveva naṃ dhārehī"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-162-');
INSERT INTO pages VALUES(3157,'mula_ma_01',118,replace(replace('<p class="bodytext">idamavoca <a name="M1.0118"></a> bhagavā. attamano āyasmā nāgasamālo bhagavato bhāsitaṃ abhinandīti.</p>\r\n<p class="centered">mahāsīhanādasuttaṃ niṭṭhitaṃ dutiyaṃ.</p>\r\n<p class="subhead">3. mahādukkhakkhandhasuttaṃ</p>\r\n<p class="bodytext"><a name="para163"></a><a name="para163_mn1"></a><span class="paranum">163</span>. evaṃ <a name="T1.0166"></a><a name="V1.0118"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. atha kho sambahulā bhikkhū pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya <a name="P1.0084"></a> sāvatthiṃ piṇḍāya pāvisiṃsu. atha kho tesaṃ bhikkhūnaṃ etadahosi – "atippago kho tāva sāvatthiyaṃ piṇḍāya carituṃ, yaṃ nūna mayaṃ yena aññatitthiyānaṃ paribbājakānaṃ ārāmo tenupasaṅkameyyāmā"ti. atha kho te bhikkhū yena aññatitthiyānaṃ paribbājakānaṃ ārāmo tenupasaṅkamiṃsu, upasaṅkamitvā tehi aññatitthiyehi paribbājakehi saddhiṃ sammodiṃsu, sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdiṃsu. ekamantaṃ nisinne kho te bhikkhū te aññatitthiyā paribbājakā etadavocuṃ – "samaṇo, āvuso, gotamo kāmānaṃ pariññaṃ paññapeti, mayampi kāmānaṃ pariññaṃ paññapema, samaṇo, āvuso, gotamo rūpānaṃ pariññaṃ paññapeti, mayampi rūpānaṃ pariññaṃ paññapema, samaṇo, āvuso, gotamo vedanānaṃ pariññaṃ paññapeti, mayampi vedanānaṃ pariññaṃ paññapema, idha no, āvuso, ko viseso, ko adhippayāso, kiṃ nānākaraṇaṃ samaṇassa vā gotamassa amhākaṃ vā – yadidaṃ dhammadesanāya vā dhammadesanaṃ, anusāsaniyā vā anusāsani"nti? atha kho te bhikkhū tesaṃ aññatitthiyānaṃ paribbājakānaṃ bhāsitaṃ neva <a name="T1.0167"></a> abhinandiṃsu, nappaṭikkosiṃsu, anabhinanditvā appaṭikkositvā uṭṭhāyāsanā pakkamiṃsu – "bhagavato santike etassa bhāsitassa atthaṃ ājānissāmā"ti.</p>\r\n<p class="bodytext"><a name="para164"></a><a name="para164_mn1"></a><span class="paranum">164</span>. atha kho te bhikkhū sāvatthiyaṃ piṇḍāya caritvā pacchābhattaṃ piṇḍapātapaṭikkantā yena bhagavā tenupasaṅkamiṃsu, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu. ekamantaṃ nisinnā kho te bhikkhū bhagavantaṃ etadavocuṃ – "idha mayaṃ, bhante, pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya</p>\r\n','\r',char(13)),'\n',char(10)),'-163-164-');
INSERT INTO pages VALUES(3158,'mula_ma_01',119,replace(replace('<p class="bodytext">sāvatthiṃ <a name="M1.0119"></a> piṇḍāya pāvisimha. tesaṃ no, bhante, amhākaṃ etadahosi – ‘atippago kho tāva sāvatthiyaṃ piṇḍāya carituṃ, yaṃ nūna mayaṃ yena aññatitthiyānaṃ paribbājakānaṃ ārāmo tenupasaṅkameyyāmā’ti. atha kho mayaṃ, bhante, yena aññatitthiyānaṃ paribbājakānaṃ ārāmo tenupasaṅkamimha, upasaṅkamitvā tehi aññatitthiyehi <a name="V1.0119"></a> paribbājakehi saddhiṃ sammodimha, sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdimha. ekamantaṃ nisinne kho amhe, bhante, te aññatitthiyā paribbājakā etadavocuṃ – ‘samaṇo, āvuso, gotamo kāmānaṃ pariññaṃ paññapeti, mayampi kāmānaṃ pariññaṃ paññapema<a name="P1.0085"></a>. samaṇo, āvuso, gotamo rūpānaṃ pariññaṃ paññapeti, mayampi rūpānaṃ pariññaṃ paññapema. samaṇo, āvuso, gotamo vedanānaṃ pariññaṃ paññapeti, mayampi vedanānaṃ pariññaṃ paññapema. idha no, āvuso, ko viseso, ko adhippayāso, kiṃ nānākaraṇaṃ <a name="T1.0168"></a> samaṇassa vā gotamassa amhākaṃ vā, yadidaṃ dhammadesanāya vā dhammadesanaṃ anusāsaniyā vā anusāsani’nti. atha kho mayaṃ, bhante, tesaṃ aññatitthiyānaṃ paribbājakānaṃ bhāsitaṃ neva abhinandimha, nappaṭikkosimha, anabhinanditvā appaṭikkositvā uṭṭhāyāsanā pakkamimha – ‘bhagavato santike etassa bhāsitassa atthaṃ ājānissāmā"’ti.</p>\r\n<p class="bodytext"><a name="para165"></a><a name="para165_mn1"></a><span class="paranum">165</span>. evaṃvādino, bhikkhave, aññatitthiyā paribbājakā evamassu vacanīyā – ‘ko panāvuso, kāmānaṃ assādo, ko ādīnavo, kiṃ nissaraṇaṃ? ko rūpānaṃ assādo, ko ādīnavo, kiṃ nissaraṇaṃ? ko vedanānaṃ assādo, ko ādīnavo, kiṃ nissaraṇa’nti? evaṃ puṭṭhā, bhikkhave, aññatitthiyā paribbājakā na ceva sampāyissanti, uttariñca vighātaṃ āpajjissanti. taṃ kissa hetu? yathā taṃ, bhikkhave, avisayasmiṃ. nāhaṃ taṃ, bhikkhave, passāmi sadevake loke samārake sabrahmake sassamaṇabrāhmaṇiyā pajāya sadevamanussāya yo imesaṃ pañhānaṃ veyyākaraṇena cittaṃ ārādheyya, aññatra tathāgatena vā tathāgatasāvakena vā, ito vā pana sutvā.</p>\r\n<p class="bodytext"><a name="para166"></a><a name="para166_mn1"></a><span class="paranum">166</span>. ko ca, bhikkhave, kāmānaṃ assādo? pañcime, bhikkhave, kāmaguṇā. katame pañca? cakkhuviññeyyā rūpā iṭṭhā kantā manāpā piyarūpā kāmūpasaṃhitā rajanīyā, sotaviññeyyā saddā…pe… ghānaviññeyyā</p>\r\n','\r',char(13)),'\n',char(10)),'-165-166-');
INSERT INTO pages VALUES(3159,'mula_ma_01',120,replace(replace('<p class="bodytext">gandhā <a name="M1.0120"></a>… jivhāviññeyyā rasā… kāyaviññeyyā phoṭṭhabbā iṭṭhā kantā manāpā piyarūpā kāmūpasaṃhitā rajanīyā – ime <a name="T1.0169"></a> kho, bhikkhave, pañca kāmaguṇā. yaṃ kho, bhikkhave, ime pañca kāmaguṇe paṭicca uppajjati sukhaṃ somanassaṃ – ayaṃ kāmānaṃ assādo.</p>\r\n<p class="bodytext"><a name="para167"></a><a name="para167_mn1"></a><span class="paranum">167</span>. ko ca, bhikkhave, kāmānaṃ ādīnavo? idha, bhikkhave, kulaputto yena sippaṭṭhānena <a name="V1.0120"></a> jīvikaṃ kappeti – yadi muddāya yadi gaṇanāya yadi saṅkhānena <span class="note">[saṅkhāya (ka.)]</span> yadi kasiyā yadi vaṇijjāya yadi gorakkhena yadi issatthena yadi rājaporisena yadi sippaññatarena – sītassa purakkhato uṇhassa purakkhato ḍaṃsamakasavātātapasarīṃsapasamphassehi rissamāno <span class="note">[īrayamāno (ka.), samphassamāno (cūḷani. khaggavisāṇasutta 136)]</span> khuppipāsāya mīyamāno, ayampi, bhikkhave, kāmānaṃ ādīnavo sandiṭṭhiko, dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ <a name="P1.0086"></a> kāmānameva hetu.</p>\r\n<p class="bodytext">tassa ce, bhikkhave, kulaputtassa evaṃ uṭṭhahato ghaṭato vāyamato te bhogā nābhinipphajjanti. so socati kilamati paridevati urattāḷiṃ kandati, sammohaṃ āpajjati – ‘moghaṃ vata me uṭṭhānaṃ, aphalo vata me vāyāmo’ti. ayampi, bhikkhave, kāmānaṃ ādīnavo sandiṭṭhiko dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext">tassa ce, bhikkhave, kulaputtassa evaṃ uṭṭhahato ghaṭato vāyamato te bhogā abhinipphajjanti. so tesaṃ bhogānaṃ ārakkhādhikaraṇaṃ dukkhaṃ domanassaṃ paṭisaṃvedeti – ‘kinti me bhoge neva rājāno hareyyuṃ, na corā hareyyuṃ, na aggi daheyya, na udakaṃ vaheyya <span class="note">[vāheyya (ka.)]</span>, na appiyā dāyādā hareyyu’nti. tassa <a name="T1.0170"></a> evaṃ ārakkhato gopayato te bhoge rājāno vā haranti, corā vā haranti, aggi vā dahati, udakaṃ vā vahati, appiyā vā dāyādā haranti. so socati kilamati paridevati urattāḷiṃ kandati, sammohaṃ āpajjati – ‘yampi me ahosi tampi no natthī’ti. ayampi, bhikkhave, kāmānaṃ ādīnavo sandiṭṭhiko, dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext"><a name="para168"></a><a name="para168_mn1"></a><span class="paranum">168</span>. puna caparaṃ, bhikkhave, kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu rājānopi rājūhi vivadanti, khattiyāpi khattiyehi</p>\r\n','\r',char(13)),'\n',char(10)),'-167-168-');
INSERT INTO pages VALUES(3160,'mula_ma_01',121,replace(replace('<p class="bodytext">vivadanti<a name="M1.0121"></a>, brāhmaṇāpi brāhmaṇehi vivadanti, gahapatīpi gahapatīhi vivadanti, mātāpi puttena vivadati, puttopi mātarā vivadati, pitāpi puttena vivadati, puttopi pitarā vivadati, bhātāpi bhātarā vivadati, bhātāpi bhaginiyā vivadati, bhaginīpi bhātarā vivadati, sahāyopi sahāyena vivadati. te tattha kalahaviggahavivādāpannā aññamaññaṃ pāṇīhipi upakkamanti, leḍḍūhipi upakkamanti, daṇḍehipi upakkamanti, satthehipi upakkamanti. te tattha maraṇampi nigacchanti, maraṇamattampi dukkhaṃ<a name="V1.0121"></a>. ayampi, bhikkhave, kāmānaṃ ādīnavo sandiṭṭhiko, dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu asicammaṃ gahetvā, dhanukalāpaṃ sannayhitvā, ubhatobyūḷhaṃ saṅgāmaṃ pakkhandanti usūsupi khippamānesu<a name="T1.0171"></a>, sattīsupi khippamānāsu, asīsupi vijjotalantesu. te tattha usūhipi vijjhanti, sattiyāpi vijjhanti, asināpi sīsaṃ chindanti. te tattha maraṇampi nigacchanti, maraṇamattampi dukkhaṃ. ayampi, bhikkhave, kāmānaṃ ādīnavo sandiṭṭhiko, dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu asicammaṃ gahetvā, dhanukalāpaṃ sannayhitvā, addāvalepanā <span class="note">[aṭṭāvalepanā (syā. ka.)]</span> upakāriyo pakkhandanti usūsupi khippamānesu, sattīsupi khippamānāsu<a name="P1.0087"></a>, asīsupi vijjotalantesu. te tattha usūhipi vijjhanti, sattiyāpi vijjhanti, chakaṇakāyapi <span class="note">[pakaṭṭhiyāpi (sī.)]</span> osiñcanti, abhivaggenapi omaddanti, asināpi sīsaṃ chindanti. te tattha maraṇampi nigacchanti, maraṇamattampi dukkhaṃ. ayampi, bhikkhave, kāmānaṃ ādīnavo sandiṭṭhiko, dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext"><a name="para169"></a><a name="para169_mn1"></a><span class="paranum">169</span>. puna caparaṃ, bhikkhave, kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu sandhimpi chindanti, nillopampi haranti, ekāgārikampi karonti, paripanthepi tiṭṭhanti, paradārampi gacchanti. tamenaṃ rājāno gahetvā vividhā kammakāraṇā kārenti – kasāhipi tāḷenti, vettehipi tāḷenti, aḍḍhadaṇḍakehipi tāḷenti, hatthampi chindanti, pādampi chindanti, hatthapādampi <a name="T1.0172"></a> chindanti, kaṇṇampi chindanti, nāsampi chindanti, kaṇṇanāsampi chindanti, bilaṅgathālikampi</p>\r\n','\r',char(13)),'\n',char(10)),'-169-');
INSERT INTO pages VALUES(3161,'mula_ma_01',122,replace(replace('<p class="bodytext">karonti<a name="M1.0122"></a>, saṅkhamuṇḍikampi karonti, rāhumukhampi karonti, jotimālikampi karonti, hatthapajjotikampi karonti, erakavattikampi karonti, cīrakavāsikampi karonti, eṇeyyakampi karonti, baḷisamaṃsikampi karonti, kahāpaṇikampi karonti, khārāpatacchikampi karonti, palighaparivattikampi karonti, palālapīṭhakampi karonti, tattenapi telena osiñcanti, sunakhehipi khādāpenti, jīvantampi sūle uttāsenti, asināpi sīsaṃ chindanti<a name="V1.0122"></a>. te tattha maraṇampi nigacchanti, maraṇamattampi dukkhaṃ. ayampi, bhikkhave, kāmānaṃ ādīnavo sandiṭṭhiko, dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu kāyena duccaritaṃ caranti, vācāya duccaritaṃ caranti, manasā duccaritaṃ caranti. te kāyena duccaritaṃ caritvā, vācāya duccaritaṃ caritvā, manasā duccaritaṃ caritvā, kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti. ayampi, bhikkhave, kāmānaṃ ādīnavo samparāyiko, dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext"><a name="para170"></a><a name="para170_mn1"></a><span class="paranum">170</span>. kiñca, bhikkhave, kāmānaṃ nissaraṇaṃ? yo kho, bhikkhave, kāmesu chandarāgavinayo chandarāgappahānaṃ – idaṃ kāmānaṃ nissaraṇaṃ.</p>\r\n<p class="bodytext">ye hi keci, bhikkhave, samaṇā vā brāhmaṇā vā evaṃ <a name="T1.0173"></a> kāmānaṃ assādañca assādato ādīnavañca ādīnavato nissaraṇañca nissaraṇato yathābhūtaṃ nappajānanti te vata sāmaṃ vā kāme parijānissanti, paraṃ vā tathattāya samādapessanti yathā paṭipanno kāme parijānissatīti – netaṃ ṭhānaṃ vijjati. ye ca kho keci, bhikkhave, samaṇā vā brāhmaṇā vā <a name="P1.0088"></a> evaṃ kāmānaṃ assādañca assādato ādīnavañca ādīnavato nissaraṇañca nissaraṇato yathābhūtaṃ pajānanti, te vata sāmaṃ vā kāme parijānissanti paraṃ vā tathattāya samādapessanti yathā paṭipanno kāme parijānissatīti – ṭhānametaṃ vijjati.</p>\r\n<p class="bodytext"><a name="para171"></a><a name="para171_mn1"></a><span class="paranum">171</span>. ko ca, bhikkhave, rūpānaṃ assādo? seyyathāpi, bhikkhave, khattiyakaññā vā brāhmaṇakaññā vā gahapatikaññā vā pannarasavassuddesikā vā soḷasavassuddesikā vā, nātidīghā nātirassā nātikisā nātithūlā nātikāḷī nāccodātā paramā sā, bhikkhave, tasmiṃ samaye</p>\r\n','\r',char(13)),'\n',char(10)),'-170-171-');
INSERT INTO pages VALUES(3162,'mula_ma_01',123,replace(replace('<p class="bodytext">subhā <a name="M1.0123"></a> vaṇṇanibhāti? ‘evaṃ, bhante’. yaṃ kho, bhikkhave, subhaṃ vaṇṇanibhaṃ paṭicca uppajjati sukhaṃ somanassaṃ – ayaṃ rūpānaṃ assādo.</p>\r\n<p class="bodytext">ko ca, bhikkhave, rūpānaṃ ādīnavo? idha, bhikkhave, tameva bhaginiṃ passeyya aparena samayena āsītikaṃ vā nāvutikaṃ vā vassasatikaṃ vā jātiyā, jiṇṇaṃ gopānasivaṅkaṃ <a name="V1.0123"></a> bhoggaṃ daṇḍaparāyanaṃ pavedhamānaṃ gacchantiṃ āturaṃ gatayobbanaṃ khaṇḍadantaṃ <span class="note">[khaṇḍadantiṃ (sī. pī.)]</span> palitakesaṃ <span class="note">[palitakesiṃ]</span>, vilūnaṃ khalitasiraṃ valinaṃ tilakāhatagattaṃ <span class="note">[tilakāhatagattiṃ (bahūsu) aṭṭhakathā ṭīkā oloketabbā]</span>. taṃ kiṃ maññatha, bhikkhave, yā purimā subhā vaṇṇanibhā <a name="T1.0174"></a> sā antarahitā, ādīnavo pātubhūtoti? ‘evaṃ, bhante’. ayampi, bhikkhave, rūpānaṃ ādīnavo.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, tameva bhaginiṃ passeyya ābādhikaṃ dukkhitaṃ bāḷhagilānaṃ, sake muttakarīse palipannaṃ semānaṃ <span class="note">[seyyamānaṃ (ka.)]</span>, aññehi vuṭṭhāpiyamānaṃ, aññehi saṃvesiyamānaṃ. taṃ kiṃ maññatha, bhikkhave, yā purimā subhā vaṇṇanibhā sā antarahitā, ādīnavo pātubhūtoti? ‘evaṃ, bhante’. ayampi, bhikkhave, rūpānaṃ ādīnavo.</p>\r\n<p class="bodytext"><a name="para172"></a><a name="para172_mn1"></a><span class="paranum">172</span>. puna caparaṃ, bhikkhave, tameva bhaginiṃ passeyya sarīraṃ sivathikāya chaḍḍitaṃ – ekāhamataṃ vā dvīhamataṃ vā tīhamataṃ vā, uddhumātakaṃ vinīlakaṃ vipubbakajātaṃ. taṃ kiṃ maññatha, bhikkhave, yā purimā subhā vaṇṇanibhā sā antarahitā, ādīnavo pātubhūtoti? ‘evaṃ, bhante’. ayampi, bhikkhave, rūpānaṃ ādīnavo.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, tameva bhaginiṃ passeyya sarīraṃ sivathikāya chaḍḍitaṃ – kākehi vā khajjamānaṃ, kulalehi vā khajjamānaṃ, gijjhehi vā khajjamānaṃ, kaṅkehi vā khajjamānaṃ, sunakhehi vā khajjamānaṃ, byagghehi vā khajjamānaṃ, dīpīhi vā khajjamānaṃ, siṅgālehi vā khajjamānaṃ, vividhehi vā pāṇakajātehi khajjamānaṃ. taṃ kiṃ maññatha, bhikkhave<a name="P1.0089"></a>, yā purimā subhā vaṇṇanibhā sā antarahitā, ādīnavo pātubhūtoti? ‘evaṃ, bhante’. ayampi, bhikkhave, rūpānaṃ ādīnavo.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, tameva bhaginiṃ passeyya sarīraṃ sivathikāya chaḍḍitaṃ – aṭṭhikasaṅkhalikaṃ samaṃsalohitaṃ nhārusambandhaṃ, aṭṭhikasaṅkhalikaṃ <a name="T1.0175"></a></p>\r\n','\r',char(13)),'\n',char(10)),'-172-');
INSERT INTO pages VALUES(3163,'mula_ma_01',124,replace(replace('<p class="bodytext">nimaṃsalohitamakkhitaṃ <a name="M1.0124"></a> nhārusambandhaṃ, aṭṭhikasaṅkhalikaṃ apagatamaṃsalohitaṃ nhārusambandhaṃ, aṭṭhikāni apagatasambandhāni disāvidisāvikkhittāni – aññena hatthaṭṭhikaṃ, aññena pādaṭṭhikaṃ, aññena gopphakaṭṭhikaṃ, aññena jaṅghaṭṭhikaṃ, aññena ūruṭṭhikaṃ, aññena kaṭiṭṭhikaṃ, aññena phāsukaṭṭhikaṃ, aññena piṭṭhiṭṭhikaṃ, aññena khandhaṭṭhikaṃ, aññena gīvaṭṭhikaṃ, aññena hanukaṭṭhikaṃ, aññena dantaṭṭhikaṃ, aññena <a name="V1.0124"></a> sīsakaṭāhaṃ. taṃ kiṃ maññatha, bhikkhave, yā purimā subhā vaṇṇanibhā sā antarahitā, ādīnavo pātubhūtoti? ‘evaṃ, bhante’. ayampi, bhikkhave, rūpānaṃ ādīnavo.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, tameva bhaginiṃ passeyya sarīraṃ sivathikāya chaḍḍitaṃ – aṭṭhikāni setāni saṅkhavaṇṇapaṭibhāgāni, aṭṭhikāni puñjakitāni terovassikāni, aṭṭhikāni pūtīni cuṇṇakajātāni. taṃ kiṃ maññatha, bhikkhave, yā purimā subhā vaṇṇanibhā sā antarahitā, ādīnavo pātubhūtoti? ‘evaṃ, bhante’. ayampi, bhikkhave, rūpānaṃ ādīnavo.</p>\r\n<p class="bodytext">kiñca, bhikkhave, rūpānaṃ nissaraṇaṃ? yo, bhikkhave, rūpesu chandarāgavinayo chandarāgappahānaṃ – idaṃ rūpānaṃ nissaraṇaṃ.</p>\r\n<p class="bodytext">ye hi keci, bhikkhave, samaṇā vā brāhmaṇā vā evaṃ rūpānaṃ assādañca assādato ādīnavañca ādīnavato nissaraṇañca nissaraṇato yathābhūtaṃ nappajānanti te vata sāmaṃ vā rūpe parijānissanti, paraṃ vā tathattāya samādapessanti yathā paṭipanno rūpe parijānissatīti – netaṃ ṭhānaṃ vijjati. ye ca kho keci, bhikkhave, samaṇā vā brāhmaṇā vā evaṃ rūpānaṃ assādañca assādato ādīnavañca ādīnavato nissaraṇañca nissaraṇato yathābhūtaṃ pajānanti te vata sāmaṃ vā rūpe parijānissanti paraṃ vā tathattāya samādapessanti yathā paṭipanno rūpe parijānissatīti <a name="T1.0176"></a> – ṭhānametaṃ vijjati.</p>\r\n<p class="bodytext"><a name="para173"></a><a name="para173_mn1"></a><span class="paranum">173</span>. ko ca, bhikkhave, vedanānaṃ assādo? idha, bhikkhave, bhikkhu vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati. yasmiṃ samaye, bhikkhave, bhikkhu vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati, neva tasmiṃ samaye attabyābādhāyapi ceteti, na parabyābādhāyapi ceteti, na ubhayabyābādhāyapi <a name="P1.0090"></a></p>\r\n','\r',char(13)),'\n',char(10)),'-173-');
INSERT INTO pages VALUES(3164,'mula_ma_01',125,replace(replace('<p class="bodytext">ceteti<a name="M1.0125"></a>, abyābajjhaṃyeva tasmiṃ samaye vedanaṃ vedeti. abyābajjhaparamāhaṃ, bhikkhave, vedanānaṃ assādaṃ vadāmi.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ <a name="V1.0125"></a> avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja viharati…pe… yasmiṃ samaye, bhikkhave, bhikkhu pītiyā ca virāgā, upekkhako ca viharati, sato ca sampajāno sukhañca kāyena paṭisaṃvedeti yaṃ taṃ ariyā <a name="T1.0177"></a> ācikkhanti – ‘upekkhako satimā sukhavihārī’ti tatiyaṃ jhānaṃ upasampajja viharati…pe… yasmiṃ samaye, bhikkhave, bhikkhu sukhassa ca pahānā dukkhassa ca pahānā pubbeva somanassadomanassānaṃ atthaṅgamā adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja viharati, neva tasmiṃ samaye attabyābādhāyapi ceteti, na parabyābādhāyapi ceteti, na ubhayabyābādhāyapi ceteti, abyābajjhaṃyeva tasmiṃ samaye vedanaṃ vedeti. abyābajjhaparamāhaṃ, bhikkhave, vedanānaṃ assādaṃ vadāmi.</p>\r\n<p class="bodytext"><a name="para174"></a><a name="para174_mn1"></a><span class="paranum">174</span>. ko ca, bhikkhave, vedanānaṃ ādīnavo? yaṃ, bhikkhave, vedanā aniccā dukkhā vipariṇāmadhammā – ayaṃ vedanānaṃ ādīnavo.</p>\r\n<p class="bodytext">kiñca, bhikkhave, vedanānaṃ nissaraṇaṃ? yo, bhikkhave, vedanāsu chandarāgavinayo, chandarāgappahānaṃ – idaṃ vedanānaṃ nissaraṇaṃ.</p>\r\n<p class="bodytext">ye hi keci, bhikkhave, samaṇā vā brāhmaṇā vā evaṃ vedanānaṃ assādañca assādato ādīnavañca ādīnavato nissaraṇañca nissaraṇato yathābhūtaṃ nappajānanti, te vata sāmaṃ vā vedanaṃ parijānissanti, paraṃ vā tathattāya samādapessanti yathā paṭipanno vedanaṃ parijānissatīti – netaṃ ṭhānaṃ vijjati. ye ca kho <a name="T1.0178"></a> keci, bhikkhave, samaṇā vā brāhmaṇā vā evaṃ vedanānaṃ assādañca assādato ādīnavañca ādīnavato nissaraṇañca nissaraṇato yathābhūtaṃ pajānanti te vata sāmaṃ vā vedanaṃ parijānissanti, paraṃ vā tathattāya samādapessanti yathā paṭipanno vedanaṃ parijānissatīti – ṭhānametaṃ vijjatī"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">mahādukkhakkhandhasuttaṃ niṭṭhitaṃ tatiyaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-174-');
INSERT INTO pages VALUES(3165,'mula_ma_01',126,replace(replace('<p class="subhead">4. cūḷadukkhakkhandhasuttaṃ</p>\r\n<p class="bodytext"><a name="para175"></a><a name="para175_mn1"></a><span class="paranum">175</span>. evaṃ <a name="M1.0126"></a><a name="P1.0091"></a><a name="V1.0126"></a><a name="T1.0179"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sakkesu viharati kapilavatthusmiṃ nigrodhārāme. atha kho mahānāmo sakko yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho mahānāmo sakko bhagavantaṃ etadavoca – "dīgharattāhaṃ, bhante, bhagavatā evaṃ dhammaṃ desitaṃ ājānāmi – ‘lobho cittassa upakkileso, doso cittassa upakkileso, moho cittassa upakkileso’ti. evañcāhaṃ <span class="note">[evaṃpāhaṃ (ka.)]</span>, bhante, bhagavatā dhammaṃ desitaṃ ājānāmi – ‘lobho cittassa upakkileso, doso cittassa upakkileso, moho cittassa upakkileso’ti. atha ca pana me ekadā lobhadhammāpi cittaṃ pariyādāya tiṭṭhanti, dosadhammāpi cittaṃ pariyādāya tiṭṭhanti, mohadhammāpi cittaṃ pariyādāya tiṭṭhanti. tassa mayhaṃ, bhante, evaṃ hoti – ‘kosu nāma me dhammo ajjhattaṃ appahīno yena me ekadā lobhadhammāpi cittaṃ pariyādāya tiṭṭhanti, dosadhammāpi cittaṃ pariyādāya tiṭṭhanti, mohadhammāpi cittaṃ pariyādāya tiṭṭhantī"’ti.</p>\r\n<p class="bodytext"><a name="para176"></a><a name="para176_mn1"></a><span class="paranum">176</span>. so eva kho te, mahānāma, dhammo ajjhattaṃ appahīno yena te ekadā lobhadhammāpi cittaṃ pariyādāya tiṭṭhanti, dosadhammāpi cittaṃ pariyādāya tiṭṭhanti, mohadhammāpi cittaṃ pariyādāya tiṭṭhanti. so ca hi te, mahānāma, dhammo ajjhattaṃ pahīno abhavissa, na tvaṃ agāraṃ ajjhāvaseyyāsi, na kāme paribhuñjeyyāsi. yasmā ca kho te, mahānāma, so eva dhammo ajjhattaṃ appahīno tasmā tvaṃ agāraṃ ajjhāvasasi, kāme paribhuñjasi.</p>\r\n<p class="bodytext"><a name="para177"></a><a name="para177_mn1"></a><span class="paranum">177</span>. ‘appassādā <a name="T1.0180"></a> kāmā bahudukkhā bahupāyāsā, ādīnavo <span class="note">[bahūpāyāsā (sī. syā. pī.)]</span> ettha bhiyyo’ti – iti cepi, mahānāma, ariyasāvakassa yathābhūtaṃ sammappaññāya sudiṭṭhaṃ hoti, so ca <span class="note">[sova (ka.)]</span> aññatreva kāmehi aññatra akusalehi dhammehi pītisukhaṃ nādhigacchati, aññaṃ vā tato santataraṃ, atha kho so neva tāva anāvaṭṭī kāmesu hoti. yato ca kho, mahānāma, ariyasāvakassa ‘appassādā kāmā bahudukkhā bahupāyāsā,</p>\r\n','\r',char(13)),'\n',char(10)),'-175-176-177-');
INSERT INTO pages VALUES(3166,'mula_ma_01',127,replace(replace('<p class="bodytext">ādīnavo <a name="M1.0127"></a> ettha bhiyyo’ti – evametaṃ yathābhūtaṃ sammappaññāya <a name="V1.0127"></a> sudiṭṭhaṃ hoti, so ca aññatreva kāmehi aññatra akusalehi dhammehi pītisukhaṃ adhigacchati aññaṃ vā tato santataraṃ, atha kho so anāvaṭṭī kāmesu hoti.</p>\r\n<p class="bodytext">mayhampi kho, mahānāma<a name="P1.0092"></a>, pubbeva sambodhā, anabhisambuddhassa bodhisattasseva sato, ‘appassādā kāmā bahudukkhā bahupāyāsā, ādīnavo ettha bhiyyo’ti – evametaṃ yathābhūtaṃ sammappaññāya sudiṭṭhaṃ hoti, so ca aññatreva kāmehi aññatra akusalehi dhammehi pītisukhaṃ nājjhagamaṃ, aññaṃ vā tato santataraṃ, atha khvāhaṃ neva tāva anāvaṭṭī kāmesu paccaññāsiṃ. yato ca kho me, mahānāma, ‘appassādā kāmā bahudukkhā bahupāyāsā, ādīnavo ettha bhiyyo’ti – evametaṃ yathābhūtaṃ sammappaññāya sudiṭṭhaṃ ahosi, so ca <span class="note">[sova (ka.)]</span> aññatreva kāmehi aññatra akusalehi dhammehi pītisukhaṃ ajjhagamaṃ, aññaṃ vā tato santataraṃ, athāhaṃ anāvaṭṭī kāmesu paccaññāsiṃ.</p>\r\n<p class="bodytext"><a name="para178"></a><a name="para178_mn1"></a><span class="paranum">178</span>. ko ca, mahānāma, kāmānaṃ assādo? pañcime, mahānāma, kāmaguṇā. katame pañca? cakkhuviññeyyā rūpā iṭṭhā kantā manāpā piyarūpā kāmūpasaṃhitā rajanīyā, sotaviññeyyā saddā…pe… ghānaviññeyyā gandhā… jivhāviññeyyā rasā… kāyaviññeyyā phoṭṭhabbā iṭṭhā kantā manāpā piyarūpā kāmūpasaṃhitā rajanīyā – ime kho, mahānāma, pañca kāmaguṇā. yaṃ kho, mahānāma, ime pañca kāmaguṇe paṭicca uppajjati sukhaṃ somanassaṃ – ayaṃ kāmānaṃ assādo.</p>\r\n<p class="bodytext">ko ca, mahānāma, kāmānaṃ ādīnavo? idha, mahānāma, kulaputto yena sippaṭṭhānena jīvikaṃ kappeti – yadi muddāya yadi gaṇanāya yadi saṅkhānena yadi kasiyā yadi vaṇijjāya yadi gorakkhena yadi issatthena yadi rājaporisena yadi sippaññatarena, sītassa purakkhato uṇhassa purakkhato ḍaṃsamakasavātātapasarīṃsapasamphassehi rissamāno khuppipāsāya mīyamāno, ayampi, mahānāma, kāmānaṃ ādīnavo sandiṭṭhiko dukkhakkhandho kāmahetu kāmanidānaṃ <a name="T1.0182"></a> kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n','\r',char(13)),'\n',char(10)),'-178-');
INSERT INTO pages VALUES(3167,'mula_ma_01',128,replace(replace('<p class="bodytext">tassa <a name="M1.0128"></a> ce mahānāma kulaputtassa evaṃ uṭṭhahato ghaṭato vāyamato te bhogā nābhinipphajjanti, so socati kilamati paridevati urattāḷiṃ kandati sammohaṃ āpajjati ‘moghaṃ <a name="V1.0128"></a> vata me uṭṭhānaṃ, aphalo vata me vāyāmo’ti. ayampi, mahānāma, kāmānaṃ ādīnavo sandiṭṭhiko dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext">tassa ce, mahānāma, kulaputtassa evaṃ uṭṭhahato ghaṭato vāyamato te bhogā abhinipphajjanti. so tesaṃ bhogānaṃ ārakkhādhikaraṇaṃ dukkhaṃ domanassaṃ paṭisaṃvedeti – ‘kinti me bhoge neva rājāno hareyyuṃ, na corā hareyyuṃ, na aggi daheyya, na udakaṃ vaheyya, na appiyā vā dāyādā hareyyu’nti. tassa evaṃ ārakkhato gopayato te bhoge rājāno vā haranti, corā vā haranti, aggi vā dahati, udakaṃ vā vahati, appiyā vā dāyādā haranti. so socati kilamati paridevati urattāḷiṃ kandati sammohaṃ āpajjati – ‘yampi me ahosi tampi no natthī’ti. ayampi, mahānāma, kāmānaṃ ādīnavo sandiṭṭhiko dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext">puna caparaṃ, mahānāma, kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu rājānopi rājūhi vivadanti, khattiyāpi khattiyehi vivadanti, brāhmaṇāpi brāhmaṇehi vivadanti, gahapatīpi gahapatīhi vivadanti, mātāpi puttena vivadati, puttopi mātarā vivadati, pitāpi puttena vivadati, puttopi pitarā vivadati, bhātāpi bhātarā vivadati, bhātāpi bhaginiyā vivadati, bhaginīpi bhātarā vivadati, sahāyopi sahāyena vivadati. te tattha kalahaviggahavivādāpannā aññamaññaṃ pāṇīhipi upakkamanti, leḍḍūhipi upakkamanti, daṇḍehipi upakkamanti, satthehipi upakkamanti. te tattha maraṇampi nigacchanti, maraṇamattampi dukkhaṃ<a name="T1.0183"></a>. ayampi, mahānāma, kāmānaṃ ādīnavo sandiṭṭhiko dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext">puna caparaṃ, mahānāma, kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu asicammaṃ gahetvā, dhanukalāpaṃ sannayhitvā, ubhatobyūḷhaṃ saṅgāmaṃ pakkhandanti usūsupi khippamānesu, sattīsupi khippamānāsu, asīsupi vijjotalantesu. te tattha usūhipi vijjhanti, sattiyāpi vijjhanti, asināpi</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3168,'mula_ma_01',129,replace(replace('<p class="bodytext">sīsaṃ <a name="M1.0129"></a> chindanti. te tattha maraṇampi nigacchanti, maraṇamattampi dukkhaṃ. ayampi, mahānāma, kāmānaṃ ādīnavo sandiṭṭhiko dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext">puna caparaṃ, mahānāma, kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu asicammaṃ <a name="V1.0129"></a> gahetvā, dhanukalāpaṃ sannayhitvā, addāvalepanā upakāriyo pakkhandanti usūsupi khippamānesu, sattīsupi khippamānāsu, asīsupi vijjotalantesu. te tattha usūhipi vijjhanti, sattiyāpi vijjhanti, chakaṇakāyapi osiñcanti, abhivaggenapi omaddanti, asināpi sīsaṃ chindanti. te tattha maraṇampi nigacchanti, maraṇamattampi dukkhaṃ. ayampi, mahānāma, kāmānaṃ ādīnavo sandiṭṭhiko dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext">puna caparaṃ, mahānāma, kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu sandhimpi chindanti, nillopampi haranti, ekāgārikampi <a name="T1.0184"></a> karonti, paripanthepi tiṭṭhanti, paradārampi gacchanti. tamenaṃ rājāno gahetvā vividhā kammakāraṇā kārenti – kasāhipi tāḷenti, vettehipi tāḷenti, aḍḍhadaṇḍakehipi tāḷenti, hatthampi chindanti, pādampi chindanti, hatthapādampi chindanti, kaṇṇampi chindanti, nāsampi chindanti, kaṇṇanāsampi chindanti, bilaṅgathālikampi karonti, saṅkhamuṇḍikampi karonti, rāhumukhampi karonti, jotimālikampi karonti, hatthapajjotikampi karonti, erakavattikampi karonti, cīrakavāsikampi karonti, eṇeyyakampi karonti, baḷisamaṃsikampi karonti, kahāpaṇikampi karonti, khārāpatacchikampi karonti, palighaparivattikampi karonti, palālapīṭhakampi karonti, tattenapi telena osiñcanti, sunakhehipi khādāpenti, jīvantampi sūle uttāsenti, asināpi sīsaṃ chindanti. te tattha maraṇampi nigacchanti, maraṇamattampi dukkhaṃ. ayampi, mahānāma, kāmānaṃ ādīnavo sandiṭṭhiko dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext">puna caparaṃ, mahānāma, kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu kāyena duccaritaṃ caranti, vācāya duccaritaṃ caranti, manasā duccaritaṃ caranti. te kāyena duccaritaṃ caritvā, vācāya duccaritaṃ caritvā, manasā duccaritaṃ caritvā, kāyassa bhedā paraṃ maraṇā, apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti. ayampi, mahānāma, kāmānaṃ ādīnavo</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3169,'mula_ma_01',130,replace(replace('<p class="bodytext">samparāyiko<a name="M1.0130"></a>, dukkhakkhandho kāmahetu kāmanidānaṃ kāmādhikaraṇaṃ kāmānameva hetu.</p>\r\n<p class="bodytext"><a name="para179"></a><a name="para179_mn1"></a><span class="paranum">179</span>. ekamidāhaṃ, mahānāma, samayaṃ rājagahe viharāmi gijjhakūṭe pabbate. tena kho pana samayena sambahulā nigaṇṭhā <span class="note">[niganthā (syā. ka.)]</span> isigilipasse kāḷasilāyaṃ <a name="T1.0185"></a> ubbhaṭṭhakā honti āsanapaṭikkhittā, opakkamikā dukkhā tibbā kharā kaṭukā vedanā vedayanti. atha khvāhaṃ, mahānāma, sāyanhasamayaṃ paṭisallānā vuṭṭhito yena isigilipasse kāḷasilā yena te nigaṇṭhā <a name="V1.0130"></a> tenupasaṅkamiṃ, upasaṅkamitvā te nigaṇṭhe etadavocaṃ – ‘kinnu tumhe, āvuso, nigaṇṭhā ubbhaṭṭhakā āsanapaṭikkhittā, opakkamikā dukkhā tibbā kharā kaṭukā vedanā vedayathā’ti? evaṃ vutte, mahānāma, te nigaṇṭhā maṃ etadavocuṃ – ‘nigaṇṭho, āvuso, nāṭaputto <span class="note">[nāthaputto (sī. pī.)]</span> sabbaññū sabbadassāvī aparisesaṃ ñāṇadassanaṃ paṭijānāti – "carato ca me tiṭṭhato ca suttassa ca jāgarassa <a name="P1.0093"></a> ca satataṃ samitaṃ ñāṇadassanaṃ paccupaṭṭhita"nti. so evamāha – "atthi kho vo <span class="note">[atthi kho bho (syā. ka.)]</span>, nigaṇṭhā, pubbe pāpakammaṃ kataṃ, taṃ imāya kaṭukāya dukkarakārikāya nijjīretha <span class="note">[nijjaretha (sī. syā. pī.)]</span>, yaṃ panettha <span class="note">[mayaṃ panettha (ka.)]</span> etarahi kāyena saṃvutā vācāya saṃvutā manasā saṃvutā taṃ āyatiṃ pāpassa kammassa akaraṇaṃ, iti purāṇānaṃ kammānaṃ tapasā byantibhāvā, navānaṃ kammānaṃ akaraṇā, āyatiṃ anavassavo, āyatiṃ anavassavā kammakkhayo, kammakkhayā dukkhakkhayo, dukkhakkhayā vedanākkhayo, vedanākkhayā sabbaṃ dukkhaṃ nijjiṇṇaṃ bhavissatī"ti. tañca panamhākaṃ ruccati ceva khamati ca, tena camha attamanā’ti.</p>\r\n<p class="bodytext"><a name="para180"></a><a name="para180_mn1"></a><span class="paranum">180</span>. evaṃ <a name="T1.0186"></a> vutte, ahaṃ, mahānāma, te nigaṇṭhe etadavocaṃ – ‘kiṃ pana tumhe, āvuso nigaṇṭhā, jānātha – ahuvamheva mayaṃ pubbe na nāhuvamhā’ti? ‘no hidaṃ, āvuso’. ‘kiṃ pana tumhe, āvuso nigaṇṭhā, jānātha – akaramheva mayaṃ pubbe pāpakammaṃ na nākaramhā’ti? ‘no hidaṃ, āvuso’. ‘kiṃ pana tumhe, āvuso nigaṇṭhā, jānātha – evarūpaṃ vā evarūpaṃ vā pāpakammaṃ akaramhā’ti? ‘no hidaṃ, āvuso’. ‘kiṃ pana tumhe, āvuso nigaṇṭhā, jānātha – ettakaṃ vā dukkhaṃ nijjiṇṇaṃ, ettakaṃ vā dukkhaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-179-180-');
INSERT INTO pages VALUES(3170,'mula_ma_01',131,replace(replace('<p class="bodytext">nijjīretabbaṃ<a name="M1.0131"></a>, ettakamhi vā dukkhe nijjiṇṇe sabbaṃ dukkhaṃ nijjiṇṇaṃ bhavissatī’ti? ‘no hidaṃ, āvuso’. ‘kiṃ pana tumhe, āvuso nigaṇṭhā, jānātha – diṭṭheva dhamme akusalānaṃ dhammānaṃ pahānaṃ, kusalānaṃ dhammānaṃ upasampada’nti? ‘no hidaṃ, āvuso’.</p>\r\n<p class="bodytext">‘iti kira tumhe, āvuso nigaṇṭhā, na jānātha – ahuvamheva mayaṃ pubbe na nāhuvamhāti, na jānātha – akaramheva mayaṃ pubbe pāpakammaṃ na nākaramhāti, na jānātha – evarūpaṃ vā evarūpaṃ vā pāpakammaṃ akaramhāti, na jānātha – ettakaṃ vā dukkhaṃ nijjiṇṇaṃ, ettakaṃ vā dukkhaṃ nijjīretabbaṃ, ettakamhi vā dukkhe nijjiṇṇe sabbaṃ dukkhaṃ nijjiṇṇaṃ bhavissatīti. na jānātha – diṭṭheva dhamme akusalānaṃ dhammānaṃ pahānaṃ, kusalānaṃ dhammānaṃ upasampadaṃ. evaṃ sante, āvuso nigaṇṭhā, ye loke luddā lohitapāṇino kurūrakammantā manussesu <a name="V1.0131"></a> paccājātā te nigaṇṭhesu pabbajantī’ti? ‘na <a name="T1.0187"></a> kho, āvuso gotama, sukhena sukhaṃ adhigantabbaṃ, dukkhena kho sukhaṃ adhigantabbaṃ, sukhena cāvuso <a name="P1.0094"></a> gotama, sukhaṃ adhigantabbaṃ abhavissa, rājā māgadho seniyo bimbisāro sukhaṃ adhigaccheyya, rājā māgadho seniyo bimbisāro sukhavihāritaro āyasmatā gotamenā’ti.</p>\r\n<p class="bodytext">‘addhāyasmantehi nigaṇṭhehi sahasā appaṭisaṅkhā vācā bhāsitā – na kho, āvuso gotama, sukhena sukhaṃ adhigantabbaṃ, dukkhena kho sukhaṃ adhigantabbaṃ, sukhena cāvuso gotama, sukhaṃ adhigantabbaṃ abhavissa, rājā māgadho seniyo bimbisāro sukhaṃ adhigaccheyya, rājā māgadho seniyo bimbisāro sukhavihāritaro āyasmatā gotamenā"ti. api ca ahameva tattha paṭipucchitabbo – ko nu kho āyasmantānaṃ sukhavihāritaro rājā vā māgadho seniyo bimbisāro āyasmā vā gotamo’ti? addhāvuso gotama, amhehi sahasā appaṭisaṅkhā vācā bhāsitā, na kho, āvuso gotama, sukhena sukhaṃ adhigantabbaṃ, dukkhena kho sukhaṃ adhigantabbaṃ, sukhena cāvuso gotama, sukhaṃ adhigantabbaṃ abhavissa, rājā māgadho seniyo bimbisāro sukhaṃ adhigaccheyya, rājā māgadho seniyo bimbisāro sukhavihāritaro āyasmatā gotamenāti. api ca tiṭṭhatetaṃ, idānipi mayaṃ āyasmantaṃ gotamaṃ pucchāma – ko nu kho āyasmantānaṃ sukhavihāritaro rājā vā māgadho seniyo bimbisāro āyasmā vā gotamo’ti?</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3171,'mula_ma_01',132,replace(replace('<p class="bodytext">‘tena <a name="M1.0132"></a> hāvuso nigaṇṭhā, tumheva tattha paṭipucchissāmi, yathā vo khameyya tathā naṃ byākareyyātha. taṃ kiṃ maññathāvuso nigaṇṭhā, pahoti rājā māgadho seniyo bimbisāro, aniñjamāno <a name="T1.0188"></a> kāyena, abhāsamāno vācaṃ, satta rattindivāni ekantasukhaṃ paṭisaṃvedī viharitu’nti? ‘no hidaṃ, āvuso’.</p>\r\n<p class="bodytext">‘taṃ kiṃ maññathāvuso nigaṇṭhā, pahoti rājā māgadho seniyo bimbisāro, aniñjamāno kāyena, abhāsamāno vācaṃ, cha rattindivāni…pe… pañca rattindivāni… cattāri rattindivāni… tīṇi rattindivāni… dve rattindivāni… ekaṃ rattindivaṃ ekantasukhaṃ paṭisaṃvedī viharitu’nti? ‘no hidaṃ, āvuso’.</p>\r\n<p class="bodytext">‘ahaṃ <a name="V1.0132"></a> kho, āvuso nigaṇṭhā, pahomi aniñjamāno kāyena, abhāsamāno vācaṃ, ekaṃ rattindivaṃ ekantasukhaṃ paṭisaṃvedī viharituṃ. ahaṃ kho, āvuso nigaṇṭhā, pahomi aniñjamāno kāyena, abhāsamāno vācaṃ, dve rattindivāni… tīṇi rattindivāni… cattāri rattindivāni… pañca rattindivāni… cha rattindivāni… satta rattindivāni ekantasukhaṃ paṭisaṃvedī viharituṃ. taṃ kiṃ maññathāvuso nigaṇṭhā, evaṃ sante ko sukhavihāritaro rājā vā māgadho seniyo bimbisāro ahaṃ vā’ti? ‘evaṃ sante āyasmāva gotamo <a name="P1.0095"></a> sukhavihāritaro raññā māgadhena seniyena bimbisārenā"’ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamano mahānāmo sakko bhagavato bhāsitaṃ abhinandīti.</p>\r\n<p class="centered">cūḷadukkhakkhandhasuttaṃ niṭṭhitaṃ catutthaṃ.</p>\r\n<p class="subhead">5. anumānasuttaṃ</p>\r\n<p class="bodytext"><a name="para181"></a><a name="para181_mn1"></a><span class="paranum">181</span>. evaṃ <a name="T1.0189"></a><a name="V1.0133"></a> me sutaṃ – ekaṃ samayaṃ āyasmā mahāmoggallāno bhaggesu viharati susumāragire <span class="note">[suṃsumāragire (sī. syā. pī.)]</span> bhesakaḷāvane migadāye. tatra kho āyasmā mahāmoggallāno bhikkhū āmantesi – "āvuso, bhikkhavo"ti. "āvuso"ti kho te bhikkhū āyasmato mahāmoggallānassa paccassosuṃ. āyasmā mahāmoggallāno etadavoca –</p>\r\n','\r',char(13)),'\n',char(10)),'-181-');
INSERT INTO pages VALUES(3172,'mula_ma_01',133,replace(replace('<p class="bodytext">pavāreti <a name="M1.0133"></a> cepi, āvuso, bhikkhu – ‘vadantu maṃ āyasmanto, vacanīyomhi āyasmantehī’ti, so ca hoti dubbaco, dovacassakaraṇehi dhammehi samannāgato, akkhamo appadakkhiṇaggāhī anusāsaniṃ, atha kho naṃ sabrahmacārī na ceva vattabbaṃ maññanti, na ca anusāsitabbaṃ maññanti, na ca tasmiṃ puggale vissāsaṃ āpajjitabbaṃ maññanti.</p>\r\n<p class="bodytext">katame cāvuso, dovacassakaraṇā dhammā? idhāvuso, bhikkhu pāpiccho hoti, pāpikānaṃ icchānaṃ vasaṃ gato. yampāvuso, bhikkhu pāpiccho hoti, pāpikānaṃ icchānaṃ vasaṃ gato – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu attukkaṃsako hoti paravambhī. yampāvuso, bhikkhu attukkaṃsako hoti paravambhī – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu kodhano hoti kodhābhibhūto. yampāvuso, bhikkhu kodhano hoti kodhābhibhūto – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso<a name="T1.0190"></a>, bhikkhu kodhano hoti kodhahetu upanāhī. yampāvuso, bhikkhu kodhano hoti kodhahetu upanāhī – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu kodhano hoti kodhahetu abhisaṅgī. yampāvuso, bhikkhu kodhano hoti kodhahetu abhisaṅgī – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna <a name="V1.0134"></a> caparaṃ, āvuso, bhikkhu kodhano hoti kodhasāmantā <span class="note">[kodhasāmantaṃ (syā. pī. ka.)]</span> vācaṃ nicchāretā. yampāvuso, bhikkhu kodhano hoti kodhasāmantā vācaṃ nicchāretā – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu codito <span class="note">[cudito (sī. syā. pī.)]</span> codakena codakaṃ paṭippharati. yampāvuso, bhikkhu codito codakena codakaṃ paṭippharati – ayampi dhammo dovacassakaraṇo.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3173,'mula_ma_01',134,replace(replace('<p class="bodytext">puna <a name="M1.0134"></a> caparaṃ, āvuso, bhikkhu codito codakena codakaṃ apasādeti. yampāvuso, bhikkhu codito codakena codakaṃ apasādeti – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ<a name="P1.0096"></a>, āvuso, bhikkhu codito codakena codakassa paccāropeti. yampāvuso, bhikkhu codito codakena codakassa paccāropeti – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu codito codakena aññenaññaṃ paṭicarati, bahiddhā kathaṃ apanāmeti, kopañca dosañca appaccayañca pātukaroti. yampāvuso, bhikkhu codito codakena aññenaññaṃ paṭicarati, bahiddhā kathaṃ apanāmeti, kopañca dosañca appaccayañca pātukaroti – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu codito codakena apadāne <a name="T1.0191"></a> na sampāyati. yampāvuso, bhikkhu codito codakena apadāne na sampāyati – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu makkhī hoti paḷāsī. yampāvuso, bhikkhu makkhī hoti paḷāsī – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu issukī hoti maccharī. yampāvuso, bhikkhu issukī hoti maccharī – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu saṭho hoti māyāvī. yampāvuso, bhikkhu saṭho hoti māyāvī – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna <a name="V1.0135"></a> caparaṃ, āvuso, bhikkhu thaddho hoti atimānī. yampāvuso, bhikkhu thaddho hoti atimānī – ayampi dhammo dovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu sandiṭṭhiparāmāsī hoti ādhānaggāhī duppaṭinissaggī. yampāvuso, bhikkhu sandiṭṭhiparāmāsī hoti ādhānaggāhī duppaṭinissaggī – ayampi dhammo dovacassakaraṇo. ime vuccantāvuso, dovacassakaraṇā dhammā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3174,'mula_ma_01',135,replace(replace('<p class="bodytext"><a name="para182"></a><a name="para182_mn1"></a><span class="paranum">182</span>. no <a name="M1.0135"></a> cepi, āvuso, bhikkhu pavāreti – ‘vadantu maṃ āyasmanto, vacanīyomhi āyasmantehī’ti, so ca hoti suvaco, sovacassakaraṇehi dhammehi samannāgato, khamo padakkhiṇaggāhī anusāsaniṃ, atha kho naṃ sabrahmacārī vattabbañceva maññanti, anusāsitabbañca maññanti, tasmiñca puggale vissāsaṃ āpajjitabbaṃ maññanti.</p>\r\n<p class="bodytext">katame cāvuso, sovacassakaraṇā dhammā? idhāvuso, bhikkhu na pāpiccho hoti, na pāpikānaṃ icchānaṃ vasaṃ gato. yampāvuso, bhikkhu na pāpiccho hoti na <a name="T1.0192"></a> pāpikānaṃ icchānaṃ vasaṃ gato – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu anattukkaṃsako hoti aparavambhī. yampāvuso, bhikkhu anattukkaṃsako hoti aparavambhī – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu na kodhano hoti na kodhābhibhūto. yampāvuso, bhikkhu na kodhano hoti na kodhābhibhūto – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu na kodhano hoti na kodhahetu upanāhī. yampāvuso, bhikkhu na kodhano hoti na kodhahetu upanāhī – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu na kodhano hoti na kodhahetu abhisaṅgī. yampāvuso, bhikkhu na kodhano hoti na kodhahetu abhisaṅgī – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu na kodhano hoti na kodhasāmantā vācaṃ nicchāretā. yampāvuso<a name="V1.0136"></a>, bhikkhu na kodhano hoti na kodhasāmantā vācaṃ nicchāretā – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu codito codakena codakaṃ nappaṭippharati. yampāvuso, bhikkhu codito codakena codakaṃ nappaṭippharati – ayampi dhammo sovacassakaraṇo<a name="T1.0193"></a>.</p>\r\n','\r',char(13)),'\n',char(10)),'-182-');
INSERT INTO pages VALUES(3175,'mula_ma_01',136,replace(replace('<p class="bodytext">puna <a name="M1.0136"></a> caparaṃ, āvuso, bhikkhu codito codakena codakaṃ na apasādeti. yampāvuso, bhikkhu codito codakena codakaṃ na apasādeti – ayampi dhammo <a name="P1.0097"></a> sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu codito codakena codakassa na paccāropeti. yampāvuso, bhikkhu codito codakena codakassa na paccāropeti – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu codito codakena na aññenaññaṃ paṭicarati, na bahiddhā kathaṃ apanāmeti, na kopañca dosañca appaccayañca pātukaroti. yampāvuso, bhikkhu codito codakena na aññenaññaṃ paṭicarati, na bahiddhā kathaṃ apanāmeti, na kopañca dosañca appaccayañca pātukaroti – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu codito codakena apadāne sampāyati. yampāvuso, bhikkhu codito codakena apadāne sampāyati – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu amakkhī hoti apaḷāsī. yampāvuso, bhikkhu amakkhī hoti apaḷāsī – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu anissukī hoti amaccharī. yampāvuso, bhikkhu anissukī hoti amaccharī – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna <a name="V1.0137"></a> caparaṃ, āvuso, bhikkhu asaṭho hoti amāyāvī. yampāvuso, bhikkhu asaṭho hoti amāyāvī – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu atthaddho hoti anatimānī. yampāvuso, bhikkhu atthaddho hoti anatimānī – ayampi dhammo sovacassakaraṇo.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhu asandiṭṭhiparāmāsī hoti anādhānaggāhī suppaṭinissaggī. yampāvuso, bhikkhu asandiṭṭhiparāmāsī hoti, anādhānaggāhī</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3176,'mula_ma_01',137,replace(replace('<p class="bodytext">suppaṭinissaggī <a name="M1.0137"></a> – ayampi dhammo sovacassakaraṇo. ime vuccantāvuso, sovacassakaraṇā dhammā.</p>\r\n<p class="bodytext"><a name="para183"></a><a name="para183_mn1"></a><span class="paranum">183</span>. tatrāvuso<a name="T1.0194"></a>, bhikkhunā attanāva attānaṃ evaṃ anuminitabbaṃ <span class="note">[anumānitabbaṃ (sī.)]</span> – ‘yo khvāyaṃ puggalo pāpiccho, pāpikānaṃ icchānaṃ vasaṃ gato, ayaṃ me puggalo appiyo amanāpo, ahañceva kho panassaṃ pāpiccho pāpikānaṃ icchānaṃ vasaṃ gato, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘na pāpiccho bhavissāmi, na pāpikānaṃ icchānaṃ vasaṃ gato’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo attukkaṃsako paravambhī, ayaṃ me puggalo appiyo amanāpo, ahañceva kho panassaṃ attukkaṃsako paravambhī, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘anattukkaṃsako bhavissāmi aparavambhī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo kodhano kodhābhibhūto, ayaṃ me puggalo appiyo amanāpo. ahañceva kho panassaṃ kodhano kodhābhibhūto, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘na kodhano bhavissāmi na kodhābhibhūto’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo kodhano kodhahetu upanāhī, ayaṃ me puggalo appiyo amanāpo, ahañceva kho panassaṃ kodhano kodhahetu upanāhī, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘na kodhano bhavissāmi na kodhahetu upanāhī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo <a name="V1.0138"></a> khvāyaṃ puggalo kodhano kodhahetu abhisaṅgī, ayaṃ me puggalo appiyo amanāpo, ahañceva <a name="T1.0195"></a> kho panassaṃ kodhano kodhahetu abhisaṅgī, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘na kodhano bhavissāmi na kodhahetu abhisaṅgī’ti cittaṃ uppādetabbaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-183-');
INSERT INTO pages VALUES(3177,'mula_ma_01',138,replace(replace('<p class="bodytext">‘yo <a name="M1.0138"></a> khvāyaṃ puggalo kodhano kodhasāmantā vācaṃ nicchāretā, ayaṃ me puggalo appiyo amanāpo, ahañceva kho panassaṃ kodhano kodhasāmantā vācaṃ nicchāretā, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘na kodhano bhavissāmi na kodhasāmantā vācaṃ nicchāressāmī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo codito codakena codakaṃ paṭippharati, ayaṃ me puggalo appiyo amanāpo, ahañceva kho pana codito codakena codakaṃ paṭipphareyyaṃ<a name="P1.0098"></a>, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘codito codakena codakaṃ nappaṭippharissāmī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo codito codakena codakaṃ apasādeti, ayaṃ me puggalo appiyo amanāpo, ahañceva kho pana codito codakena codakaṃ apasādeyyaṃ, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘codito codakena codakaṃ na apasādessāmī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo codito codakena codakassa paccāropeti, ayaṃ me puggalo appiyo amanāpo, ahañceva kho pana codito codakena codakassa paccāropeyyaṃ, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ <a name="T1.0196"></a> jānantenāvuso, bhikkhunā ‘codito codakena codakassa na paccāropessāmī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo codito codakena aññenaññaṃ paṭicarati, bahiddhā kathaṃ apanāmeti, kopañca dosañca appaccayañca pātukaroti, ayaṃ me puggalo appiyo amanāpo, ahañceva kho pana codito codakena aññenaññaṃ paṭicareyyaṃ, bahiddhā kathaṃ apanāmeyyaṃ<a name="V1.0139"></a>, kopañca dosañca appaccayañca pātukareyyaṃ, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘codito codakena na aññenaññaṃ paṭicarissāmi, na bahiddhā kathaṃ apanāmessāmi, na kopañca dosañca appaccayañca pātukarissāmī’ti cittaṃ uppādetabbaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3178,'mula_ma_01',139,replace(replace('<p class="bodytext">‘yo <a name="M1.0139"></a> khvāyaṃ puggalo codito codakena apadāne na sampāyati, ayaṃ me puggalo appiyo amanāpo, ahañceva kho pana codito codakena apadāne na sampāyeyyaṃ, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘codito codakena apadāne sampāyissāmī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo makkhī paḷāsī, ayaṃ me puggalo appiyo amanāpo, ahañceva kho panassaṃ makkhī paḷāsī, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘amakkhī bhavissāmi apaḷāsī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo issukī maccharī, ayaṃ me puggalo appiyo amanāpo, ahañceva kho panassaṃ <a name="T1.0197"></a> issukī maccharī, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘anissukī bhavissāmi amaccharī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo saṭho māyāvī, ayaṃ me puggalo appiyo amanāpo, ahañceva kho panassaṃ saṭho māyāvī, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘asaṭho bhavissāmi amāyāvī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo thaddho atimānī, ayaṃ me puggalo appiyo amanāpo, ahañceva kho panassaṃ thaddho atimānī, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘atthaddho bhavissāmi anatimānī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext">‘yo khvāyaṃ puggalo sandiṭṭhiparāmāsī ādhānaggāhī duppaṭinissaggī, ayaṃ me puggalo appiyo amanāpo, ahañceva kho panassaṃ sandiṭṭhiparāmāsī ādhānaggāhī duppaṭinissaggī, ahaṃpāssaṃ paresaṃ appiyo amanāpo’ti. evaṃ jānantenāvuso, bhikkhunā ‘asandiṭṭhiparāmāsī bhavissāmi anādhānaggāhī suppaṭinissaggī’ti cittaṃ uppādetabbaṃ.</p>\r\n<p class="bodytext"><a name="para184"></a><a name="para184_mn1"></a><span class="paranum">184</span>. tatrāvuso<a name="V1.0140"></a>, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi pāpiccho, pāpikānaṃ icchānaṃ vasaṃ gato’ti? sace,</p>\r\n','\r',char(13)),'\n',char(10)),'-184-');
INSERT INTO pages VALUES(3179,'mula_ma_01',140,replace(replace('<p class="bodytext">āvuso<a name="M1.0140"></a>, bhikkhu paccavekkhamāno evaṃ jānāti – ‘pāpiccho khomhi, pāpikānaṃ icchānaṃ vasaṃ gato’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno <a name="T1.0198"></a> evaṃ jānāti – ‘na khomhi pāpiccho, na pāpikānaṃ icchānaṃ vasaṃ gato’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi attukkaṃsako paravambhī’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘attukkaṃsako khomhi paravambhī’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘anattukkaṃsako <a name="P1.0099"></a> khomhi aparavambhī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi kodhano kodhābhibhūto’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘kodhano khomhi kodhābhibhūto’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘na khomhi kodhano kodhābhibhūto’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi kodhano kodhahetu upanāhī’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti ‘kodhano khomhi kodhahetu upanāhī’ti, tenāvuso, bhikkhunā tesaṃyeva <a name="T1.0199"></a> pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti ‘na khomhi kodhano kodhahetu upanāhī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3180,'mula_ma_01',141,replace(replace('<p class="bodytext">puna <a name="M1.0141"></a><a name="V1.0141"></a> caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi kodhano kodhahetu abhisaṅgī’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘kodhano khomhi kodhahetu abhisaṅgī’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘na khomhi kodhano kodhahetu abhisaṅgī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi kodhano kodhasāmantā vācaṃ nicchāretā’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘kodhano khomhi kodhasāmantā vācaṃ nicchāretā’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘na khomhi kodhano kodhasāmantā vācaṃ nicchāretā’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi codito codakena codakaṃ <a name="T1.0200"></a> paṭippharāmī’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti ‘codito khomhi codakena codakaṃ paṭippharāmī’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘codito khomhi codakena codakaṃ nappaṭippharāmī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi codito codakena codakaṃ apasādemī’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti ‘codito khomhi codakena codakaṃ apasādemī’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘codito khomhi codakena</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3181,'mula_ma_01',142,replace(replace('<p class="bodytext">codakaṃ <a name="M1.0142"></a> na apasādemī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi codito codakena codakassa paccāropemī’ti? sace, āvuso, bhikkhu paccavekkhamāno <a name="V1.0142"></a> evaṃ jānāti – ‘codito khomhi codakena codakassa paccāropemī’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘codito khomhi codakena codakassa na paccāropemī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena <a name="T1.0201"></a> vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi codito codakena aññenaññaṃ paṭicarāmi, bahiddhā kathaṃ apanāmemi, kopañca dosañca appaccayañca pātukaromī’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘codito khomhi codakena aññenaññaṃ paṭicarāmi, bahiddhā kathaṃ apanāmemi, kopañca dosañca appaccayañca pātukaromī’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘codito khomhi codakena na aññenaññaṃ paṭicarāmi, na bahiddhā kathaṃ apanāmemi, na kopañca dosañca appaccayañca pātukaromī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi codito codakena apadāne na sampāyāmī’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘codito khomhi codakena apadāne na sampāyāmī’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘codito khomhi codakena apadāne sampāyāmī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3182,'mula_ma_01',143,replace(replace('<p class="bodytext">puna <a name="M1.0143"></a> caparaṃ<a name="T1.0202"></a>, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi makkhī paḷāsī’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘makkhī khomhi paḷāsī’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘amakkhī khomhi apaḷāsī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna <a name="V1.0143"></a> caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi issukī maccharī’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘issukī khomhi maccharī’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘anissukī khomhi amaccharī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi saṭho māyāvī’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘saṭho khomhi māyāvī’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘asaṭho khomhi amāyāvī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ <a name="T1.0203"></a> – ‘kiṃ nu khomhi thaddho atimānī’ti? sace, āvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘thaddho khomhi atimānī’ti, tenāvuso, bhikkhunā tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘atthaddho khomhi anatimānī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">puna caparaṃ, āvuso, bhikkhunā attanāva attānaṃ evaṃ paccavekkhitabbaṃ – ‘kiṃ nu khomhi sandiṭṭhiparāmāsī ādhānaggāhī duppaṭinissaggī’ti? sace,</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3183,'mula_ma_01',144,replace(replace('<p class="bodytext">āvuso<a name="M1.0144"></a>, bhikkhu paccavekkhamāno evaṃ jānāti – ‘sandiṭṭhiparāmāsī khomhi ādhānaggāhī duppaṭinissaggī’ti, tenāvuso, bhikkhunā <a name="P1.0100"></a> tesaṃyeva pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno evaṃ jānāti – ‘asandiṭṭhiparāmāsī khomhi anādhānaggāhī suppaṭinissaggī’ti, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">sace<a name="V1.0144"></a>, āvuso, bhikkhu paccavekkhamāno sabbepime pāpake akusale dhamme appahīne attani samanupassati, tenāvuso, bhikkhunā sabbesaṃyeva imesaṃ pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno sabbepime pāpake akusale dhamme pahīne attani samanupassati, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ, ahorattānusikkhinā kusalesu dhammesu.</p>\r\n<p class="bodytext">seyyathāpi, āvuso, itthī <a name="T1.0204"></a> vā puriso vā, daharo yuvā maṇḍanajātiko, ādāse vā parisuddhe pariyodāte, acche vā udakapatte, sakaṃ mukhanimittaṃ paccavekkhamāno, sace tattha passati rajaṃ vā aṅgaṇaṃ vā, tasseva rajassa vā aṅgaṇassa vā pahānāya vāyamati, no ce tattha passati rajaṃ vā aṅgaṇaṃ vā, teneva attamano hoti – ‘lābhā vata me, parisuddhaṃ vata me’ti. evameva kho, āvuso, sace bhikkhu paccavekkhamāno sabbepime pāpake akusale dhamme appahīne attani samanupassati, tenāvuso, bhikkhunā sabbesaṃyeva imesaṃ pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya vāyamitabbaṃ. sace panāvuso, bhikkhu paccavekkhamāno sabbepime pāpake akusale dhamme pahīne attani samanupassati, tenāvuso, bhikkhunā teneva pītipāmojjena vihātabbaṃ, ahorattānusikkhinā kusalesu dhammesū"ti.</p>\r\n<p class="bodytext">idamavocāyasmā mahāmoggallāno. attamanā te bhikkhū āyasmato mahāmoggallānassa bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">anumānasuttaṃ niṭṭhitaṃ pañcamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3184,'mula_ma_01',145,replace(replace('<p class="subhead">6. cetokhilasuttaṃ</p>\r\n<p class="bodytext"><a name="para185"></a><a name="para185_mn1"></a><span class="paranum">185</span>. evaṃ <a name="T1.0205"></a><a name="M1.0145"></a><a name="V1.0145"></a><a name="P1.0101"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext">yassa kassaci, bhikkhave, bhikkhuno pañca cetokhilā appahīnā, pañca cetasovinibandhā <span class="note">[cetasovinibaddhā (sī.), cetovinibaddhā (sāratthadīpanīṭīkā)]</span> asamucchinnā, so vatimasmiṃ dhammavinaye vuddhiṃ virūḷhiṃ vepullaṃ āpajjissatīti – netaṃ ṭhānaṃ vijjati.</p>\r\n<p class="bodytext">katamāssa pañca cetokhilā appahīnā honti? idha, bhikkhave, bhikkhu satthari kaṅkhati vicikicchati nādhimuccati na sampasīdati. yo so, bhikkhave, bhikkhu satthari kaṅkhati vicikicchati nādhimuccati na sampasīdati tassa cittaṃ na namati ātappāya anuyogāya sātaccāya padhānāya. yassa cittaṃ na namati ātappāya anuyogāya sātaccāya padhānāya, evamassāyaṃ paṭhamo cetokhilo appahīno hoti.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu dhamme kaṅkhati vicikicchati nādhimuccati na sampasīdati…pe… evamassāyaṃ dutiyo cetokhilo appahīno hoti.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu saṅghe kaṅkhati vicikicchati nādhimuccati na sampasīdati…pe… evamassāyaṃ tatiyo cetokhilo appahīno hoti.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sikkhāya kaṅkhati vicikicchati nādhimuccati na sampasīdati. yo so, bhikkhave, bhikkhu sikkhāya kaṅkhati vicikicchati nādhimuccati na sampasīdati, tassa cittaṃ na namati <a name="T1.0206"></a> ātappāya anuyogāya sātaccāya padhānāya. yassa cittaṃ <a name="V1.0146"></a> na namati ātappāya anuyogāya sātaccāya padhānāya, evamassāyaṃ catuttho cetokhilo appahīno hoti.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sabrahmacārīsu kupito hoti anattamano āhatacitto khilajāto. yo so, bhikkhave, bhikkhu sabrahmacārīsu kupito hoti anattamano āhatacitto khilajāto, tassa cittaṃ na namati ātappāya anuyogāya sātaccāya padhānāya. yassa cittaṃ na</p>\r\n','\r',char(13)),'\n',char(10)),'-185-');
INSERT INTO pages VALUES(3185,'mula_ma_01',146,replace(replace('<p class="bodytext">namati <a name="M1.0146"></a> ātappāya anuyogāya sātaccāya padhānāya, evamassāyaṃ pañcamo cetokhilo appahīno hoti. imāssa pañca cetokhilā appahīnā honti.</p>\r\n<p class="bodytext"><a name="para186"></a><a name="para186_mn1"></a><span class="paranum">186</span>. katamāssa pañca cetasovinibandhā asamucchinnā honti? idha, bhikkhave, bhikkhu kāme avītarāgo <span class="note">[avigatarāgo (katthaci)]</span> hoti avigatacchando avigatapemo avigatapipāso avigatapariḷāho avigatataṇho. yo so, bhikkhave, bhikkhu kāme avītarāgo hoti avigatacchando avigatapemo avigatapipāso avigatapariḷāho avigatataṇho, tassa cittaṃ na namati ātappāya anuyogāya sātaccāya padhānāya. yassa cittaṃ na namati ātappāya anuyogāya sātaccāya padhānāya, evamassāyaṃ paṭhamo cetasovinibandho asamucchinno hoti.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu kāye avītarāgo hoti…pe… evamassāyaṃ dutiyo cetasovinibandho asamucchinno hoti.</p>\r\n<p class="bodytext">puna <a name="P1.0102"></a> caparaṃ, bhikkhave, bhikkhu rūpe avītarāgo hoti…pe… evamassāyaṃ tatiyo cetasovinibandho asamucchinno <a name="T1.0207"></a> hoti.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu yāvadatthaṃ udarāvadehakaṃ bhuñjitvā seyyasukhaṃ passasukhaṃ middhasukhaṃ anuyutto viharati. yo so, bhikkhave, bhikkhu yāvadatthaṃ udarāvadehakaṃ bhuñjitvā seyyasukhaṃ passasukhaṃ middhasukhaṃ anuyutto viharati, tassa cittaṃ na namati ātappāya anuyogāya sātaccāya padhānāya. yassa cittaṃ na namati ātappāya anuyogāya sātaccāya padhānāya, evamassāyaṃ catuttho cetasovinibandho asamucchinno hoti.</p>\r\n<p class="bodytext">puna <a name="V1.0147"></a> caparaṃ, bhikkhave, bhikkhu aññataraṃ devanikāyaṃ paṇidhāya brahmacariyaṃ carati – ‘imināhaṃ sīlena vā vatena vā tapena vā brahmacariyena vā devo vā bhavissāmi devaññataro vā’ti. yo so, bhikkhave, bhikkhu aññataraṃ devanikāyaṃ paṇidhāya brahmacariyaṃ carati – ‘imināhaṃ sīlena vā vatena vā tapena vā brahmacariyena vā devo vā bhavissāmi devaññataro vā’ti, tassa cittaṃ na namati ātappāya anuyogāya sātaccāya</p>\r\n','\r',char(13)),'\n',char(10)),'-186-');
INSERT INTO pages VALUES(3186,'mula_ma_01',147,replace(replace('<p class="bodytext">padhānāya<a name="M1.0147"></a>. yassa cittaṃ na namati ātappāya anuyogāya sātaccāya padhānāya, evamassāyaṃ pañcamo cetasovinibandho asamucchinno hoti. imāssa pañca cetasovinibandhā asamucchinnā honti.</p>\r\n<p class="bodytext">yassa kassaci, bhikkhave, bhikkhuno ime pañca cetokhilā appahīnā, ime pañca cetasovinibandhā asamucchinnā, so vatimasmiṃ dhammavinaye vuddhiṃ virūḷhiṃ vepullaṃ āpajjissatīti – netaṃ ṭhānaṃ vijjati.</p>\r\n<p class="bodytext"><a name="para187"></a><a name="para187_mn1"></a><span class="paranum">187</span>. yassa <a name="T1.0208"></a> kassaci, bhikkhave, bhikkhuno pañca cetokhilā pahīnā, pañca cetasovinibandhā susamucchinnā, so vatimasmiṃ dhammavinaye vuddhiṃ virūḷhiṃ vepullaṃ āpajjissatīti – ṭhānametaṃ vijjati.</p>\r\n<p class="bodytext">katamāssa pañca cetokhilā pahīnā honti? idha, bhikkhave, bhikkhu satthari na kaṅkhati na vicikicchati adhimuccati sampasīdati. yo so, bhikkhave, bhikkhu satthari na kaṅkhati na vicikicchati adhimuccati sampasīdati, tassa cittaṃ namati ātappāya anuyogāya sātaccāya padhānāya. yassa cittaṃ namati ātappāya anuyogāya sātaccāya padhānāya, evamassāyaṃ paṭhamo cetokhilo pahīno hoti.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu dhamme na kaṅkhati na vicikicchati adhimuccati sampasīdati…pe… evamassāyaṃ dutiyo cetokhilo pahīno hoti.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu saṅghe na kaṅkhati na vicikicchati adhimuccati sampasīdati…pe… evamassāyaṃ tatiyo cetokhilo pahīno hoti.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sikkhāya na kaṅkhati na vicikicchati adhimuccati sampasīdati…pe… evamassāyaṃ catuttho cetokhilo pahīno hoti.</p>\r\n<p class="bodytext">puna <a name="V1.0148"></a> caparaṃ, bhikkhave, bhikkhu sabrahmacārīsu na kupito hoti na anattamano <span class="note">[attamano (sī. pī.)]</span> anāhatacitto akhilajāto. yo so, bhikkhave, bhikkhu sabrahmacārīsu na kupito hoti na anattamano anāhatacitto akhilajāto, tassa cittaṃ namati ātappāya <a name="P1.0103"></a> anuyogāya sātaccāya padhānāya. yassa cittaṃ namati ātappāya anuyogāya sātaccāya padhānāya, evamassāyaṃ <a name="T1.0209"></a> pañcamo cetokhilo pahīno hoti. imāssa pañca cetokhilā pahīnā honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-187-');
INSERT INTO pages VALUES(3187,'mula_ma_01',148,replace(replace('<p class="bodytext"><a name="para188"></a><a name="para188_mn1"></a><span class="paranum">188</span>. katamāssa <a name="M1.0148"></a> pañca cetasovinibandhā susamucchinnā honti? idha, bhikkhave, bhikkhu kāme vītarāgo hoti vigatacchando vigatapemo vigatapipāso vigatapariḷāho vigatataṇho. yo so, bhikkhave, bhikkhu kāme vītarāgo hoti vigatacchando vigatapemo vigatapipāso vigatapariḷāho vigatataṇho, tassa cittaṃ namati ātappāya anuyogāya sātaccāya padhānāya. yassa cittaṃ namati ātappāya anuyogāya sātaccāya padhānāya, evamassāyaṃ paṭhamo cetasovinibandho susamucchinno hoti.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu kāye vītarāgo hoti…pe… rūpe vītarāgo hoti…pe… na yāvadatthaṃ udarāvadehakaṃ bhuñjitvā seyyasukhaṃ passasukhaṃ middhasukhaṃ anuyutto viharati. yo so, bhikkhave, bhikkhu na yāvadatthaṃ udarāvadehakaṃ bhuñjitvā seyyasukhaṃ passasukhaṃ middhasukhaṃ anuyutto viharati, tassa cittaṃ namati ātappāya anuyogāya sātaccāya padhānāya. yassa cittaṃ namati ātappāya anuyogāya sātaccāya padhānāya, evamassāyaṃ catuttho cetasovinibandho susamucchinno hoti.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu na aññataraṃ devanikāyaṃ paṇidhāya brahmacariyaṃ carati – ‘imināhaṃ sīlena vā vatena vā tapena vā brahmacariyena vā devo vā bhavissāmi devaññataro <a name="T1.0210"></a> vā’ti. yo so, bhikkhave, bhikkhu na aññataraṃ devanikāyaṃ paṇidhāya brahmacariyaṃ carati – ‘imināhaṃ sīlena vā vatena vā tapena vā brahmacariyena vā devo vā bhavissāmi devaññataro vā’ti, tassa cittaṃ namati ātappāya anuyogāya sātaccāya padhānāya. yassa cittaṃ namati ātappāya anuyogāya sātaccāya padhānāya, evamassāyaṃ pañcamo cetasovinibandho susamucchinno hoti. imāssa pañca cetasovinibandhā susamucchinnā honti.</p>\r\n<p class="bodytext">yassa <a name="V1.0149"></a> kassaci, bhikkhave, bhikkhuno ime pañca cetokhilā pahīnā, ime pañca cetasovinibandhā susamucchinnā, so vatimasmiṃ dhammavinaye vuddhiṃ virūḷhiṃ vepullaṃ āpajjissatīti – ṭhānametaṃ vijjati.</p>\r\n<p class="bodytext"><a name="para189"></a><a name="para189_mn1"></a><span class="paranum">189</span>. so chandasamādhipadhānasaṅkhārasamannāgataṃ iddhipādaṃ bhāveti, vīriyasamādhipadhānasaṅkhārasamannāgataṃ iddhipādaṃ bhāveti,</p>\r\n','\r',char(13)),'\n',char(10)),'-188-189-');
INSERT INTO pages VALUES(3188,'mula_ma_01',149,replace(replace('<p class="bodytext">cittasamādhipadhānasaṅkhārasamannāgataṃ <a name="M1.0149"></a> iddhipādaṃ bhāveti, vīmaṃsāsamādhipadhānasaṅkhārasamannāgataṃ iddhipādaṃ bhāveti, ussoḷhīyeva pañcamī. sa kho so, bhikkhave, evaṃ ussoḷhīpannarasaṅgasamannāgato <a name="P1.0104"></a> bhikkhu bhabbo abhinibbidāya, bhabbo sambodhāya, bhabbo anuttarassa yogakkhemassa adhigamāya. seyyathāpi, bhikkhave, kukkuṭiyā aṇḍāni aṭṭha vā dasa vā dvādasa vā. tānassu kukkuṭiyā sammā adhisayitāni sammā pariseditāni sammā paribhāvitāni. kiñcāpi tassā kukkuṭiyā na evaṃ icchā uppajjeyya – ‘aho vatime kukkuṭapotakā pādanakhasikhāya vā mukhatuṇḍakena vā aṇḍakosaṃ <a name="T1.0211"></a> padāletvā sotthinā abhinibbhijjeyyu’nti. atha kho bhabbāva te kukkuṭapotakā pādanakhasikhāya vā mukhatuṇḍakena vā aṇḍakosaṃ padāletvā sotthinā abhinibbhijjituṃ. evameva kho, bhikkhave, evaṃ ussoḷhipannarasaṅgasamannāgato bhikkhu bhabbo abhinibbidāya, bhabbo sambodhāya, bhabbo anuttarassa yogakkhemassa adhigamāyā"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">cetokhilasuttaṃ niṭṭhitaṃ chaṭṭhaṃ.</p>\r\n<p class="subhead">7. vanapatthasuttaṃ</p>\r\n<p class="bodytext"><a name="para190"></a><a name="para190_mn1"></a><span class="paranum">190</span>. evaṃ <a name="V1.0150"></a><a name="T1.0212"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca – "vanapatthapariyāyaṃ vo, bhikkhave, desessāmi, taṃ suṇātha, sādhukaṃ manasikarotha, bhāsissāmī"ti. "evaṃ, bhante"ti kho te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext"><a name="para191"></a><a name="para191_mn1"></a><span class="paranum">191</span>. idha, bhikkhave, bhikkhu aññataraṃ vanapatthaṃ upanissāya viharati. tassa taṃ vanapatthaṃ upanissāya viharato anupaṭṭhitā ceva sati na upaṭṭhāti,</p>\r\n','\r',char(13)),'\n',char(10)),'-190-191-');
INSERT INTO pages VALUES(3189,'mula_ma_01',150,replace(replace('<p class="bodytext">asamāhitañca <a name="M1.0150"></a> cittaṃ na samādhiyati, aparikkhīṇā ca āsavā na parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ nānupāpuṇāti. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te kasirena samudāgacchanti. tena, bhikkhave, bhikkhunā iti paṭisañcikkhitabbaṃ <a name="P1.0105"></a> – ‘ahaṃ kho imaṃ vanapatthaṃ upanissāya viharāmi, tassa me imaṃ vanapatthaṃ upanissāya viharato anupaṭṭhitā ceva sati na upaṭṭhāti, asamāhitañca cittaṃ na samādhiyati, aparikkhīṇā ca āsavā na parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ nānupāpuṇāmi. ye ca kho ime pabbajitena jīvitaparikkhārā <a name="T1.0213"></a> samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te kasirena samudāgacchantī’ti. tena, bhikkhave, bhikkhunā rattibhāgaṃ vā divasabhāgaṃ vā tamhā vanapatthā pakkamitabbaṃ, na vatthabbaṃ.</p>\r\n<p class="bodytext"><a name="para192"></a><a name="para192_mn1"></a><span class="paranum">192</span>. idha pana, bhikkhave, bhikkhu aññataraṃ vanapatthaṃ upanissāya viharati. tassa taṃ vanapatthaṃ upanissāya viharato anupaṭṭhitā ceva sati na upaṭṭhāti, asamāhitañca cittaṃ na samādhiyati, aparikkhīṇā ca āsavā na parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ nānupāpuṇāti. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te appakasirena samudāgacchanti. tena<a name="V1.0151"></a>, bhikkhave, bhikkhunā iti paṭisañcikkhitabbaṃ – ‘ahaṃ kho imaṃ vanapatthaṃ upanissāya viharāmi. tassa me imaṃ vanapatthaṃ upanissāya viharato anupaṭṭhitā ceva sati na upaṭṭhāti asamāhitañca cittaṃ na samādhiyati, aparikkhīṇā ca āsavā na parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ nānupāpuṇāmi. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te appakasirena samudāgacchanti. na kho panāhaṃ cīvarahetu agārasmā anagāriyaṃ pabbajito na piṇḍapātahetu…pe… na senāsanahetu…pe… na gilānappaccayabhesajjaparikkhārahetu agārasmā anagāriyaṃ pabbajito. atha ca pana me imaṃ <a name="T1.0214"></a> vanapatthaṃ upanissāya viharato anupaṭṭhitā ceva sati na upaṭṭhāti, asamāhitañca cittaṃ na samādhiyati, aparikkhīṇā ca āsavā na parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ nānupāpuṇāmī’ti. tena, bhikkhave, bhikkhunā saṅkhāpi tamhā vanapatthā pakkamitabbaṃ, na vatthabbaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-192-');
INSERT INTO pages VALUES(3190,'mula_ma_01',151,replace(replace('<p class="bodytext"><a name="para193"></a><a name="para193_mn1"></a><span class="paranum">193</span>. idha <a name="M1.0151"></a> pana, bhikkhave, bhikkhu aññataraṃ vanapatthaṃ upanissāya viharati. tassa taṃ vanapatthaṃ upanissāya viharato anupaṭṭhitā ceva sati upaṭṭhāti, asamāhitañca cittaṃ samādhiyati, aparikkhīṇā ca āsavā parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ anupāpuṇāti. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā, te kasirena samudāgacchanti. tena, bhikkhave, bhikkhunā iti paṭisañcikkhitabbaṃ <a name="P1.0106"></a> – ‘ahaṃ kho imaṃ vanapatthaṃ upanissāya viharāmi. tassa me imaṃ vanapatthaṃ upanissāya viharato anupaṭṭhitā ceva sati upaṭṭhāti asamāhitañca cittaṃ samādhiyati, aparikkhīṇā ca āsavā parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ anupāpuṇāmi. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te kasirena samudāgacchanti. na kho panāhaṃ cīvarahetu agārasmā anagāriyaṃ pabbajito, na piṇḍapātahetu…pe… na senāsanahetu…pe… na gilānappaccayabhesajjaparikkhārahetu agārasmā anagāriyaṃ pabbajito<a name="T1.0215"></a>. atha ca pana me imaṃ vanapatthaṃ upanissāya viharato anupaṭṭhitā ceva sati upaṭṭhāti, asamāhitañca cittaṃ samādhiyati, aparikkhīṇā ca āsavā parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ anupāpuṇāmī’ti. tena, bhikkhave, bhikkhunā saṅkhāpi tasmiṃ vanapatthe vatthabbaṃ, na pakkamitabbaṃ.</p>\r\n<p class="bodytext"><a name="para194"></a><a name="para194_mn1"></a><span class="paranum">194</span>. idha <a name="V1.0152"></a> pana, bhikkhave, bhikkhu aññataraṃ vanapatthaṃ upanissāya viharati. tassa taṃ vanapatthaṃ upanissāya viharato anupaṭṭhitā ceva sati upaṭṭhāti, asamāhitañca cittaṃ samādhiyati, aparikkhīṇā ca āsavā parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ anupāpuṇāti. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te appakasirena samudāgacchanti. tena, bhikkhave, bhikkhunā iti paṭisañcikkhitabbaṃ – ‘ahaṃ kho imaṃ vanapatthaṃ upanissāya viharāmi. tassa me imaṃ vanapatthaṃ upanissāya viharato anupaṭṭhitā ceva sati upaṭṭhāti asamāhitañca cittaṃ samādhiyati, aparikkhīṇā ca āsavā parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ anupāpuṇāmi. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te appakasirena</p>\r\n','\r',char(13)),'\n',char(10)),'-193-194-');
INSERT INTO pages VALUES(3191,'mula_ma_01',152,replace(replace('<p class="bodytext">samudāgacchantī’ti<a name="M1.0152"></a>. tena, bhikkhave, bhikkhunā yāvajīvampi tasmiṃ vanapatthe vatthabbaṃ, na pakkamitabbaṃ.</p>\r\n<p class="bodytext"><a name="para195"></a><a name="para195_mn1"></a><span class="paranum">195</span>. idha, bhikkhave, bhikkhu aññataraṃ gāmaṃ upanissāya viharati <a name="T1.0216"></a> …pe… aññataraṃ nigamaṃ upanissāya viharati…pe… aññataraṃ nagaraṃ upanissāya viharati…pe… aññataraṃ janapadaṃ upanissāya viharati…pe… aññataraṃ puggalaṃ upanissāya viharati. tassa taṃ puggalaṃ upanissāya viharato anupaṭṭhitā ceva sati na upaṭṭhāti, asamāhitañca cittaṃ na samādhiyati, aparikkhīṇā ca āsavā na parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ nānupāpuṇāti. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te kasirena samudāgacchanti. tena, bhikkhave, bhikkhunā iti paṭisañcikkhitabbaṃ – ‘ahaṃ kho imaṃ puggalaṃ upanissāya viharāmi. tassa me imaṃ puggalaṃ upanissāya viharato anupaṭṭhitā ceva sati na upaṭṭhāti, asamāhitañca cittaṃ na samādhiyati, aparikkhīṇā ca āsavā na parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ nānupāpuṇāmi. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te kasirena samudāgacchantī’ti. tena, bhikkhave, bhikkhunā rattibhāgaṃ vā divasabhāgaṃ vā so puggalo anāpucchā pakkamitabbaṃ, nānubandhitabbo.</p>\r\n<p class="bodytext"><a name="para196"></a><a name="para196_mn1"></a><span class="paranum">196</span>. idha pana, bhikkhave, bhikkhu aññataraṃ puggalaṃ upanissāya viharati. tassa taṃ <a name="V1.0153"></a> puggalaṃ upanissāya viharato anupaṭṭhitā <a name="P1.0107"></a> ceva sati na upaṭṭhāti, asamāhitañca cittaṃ na samādhiyati, aparikkhīṇā ca <a name="T1.0217"></a> āsavā na parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ nānupāpuṇāti. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā, te appakasirena samudāgacchanti. tena, bhikkhave, bhikkhunā iti paṭisañcikkhitabbaṃ – ‘ahaṃ kho imaṃ puggalaṃ upanissāya viharāmi. tassa me imaṃ puggalaṃ upanissāya viharato anupaṭṭhitā ceva sati na upaṭṭhāti, asamāhitañca cittaṃ na samādhiyati, aparikkhīṇā ca āsavā na parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ nānupāpuṇāmi. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā –</p>\r\n','\r',char(13)),'\n',char(10)),'-195-196-');
INSERT INTO pages VALUES(3192,'mula_ma_01',153,replace(replace('<p class="bodytext">cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā <a name="M1.0153"></a> – te appakasirena samudāgacchanti. na kho panāhaṃ cīvarahetu agārasmā anagāriyaṃ pabbajito, na piṇḍapātahetu…pe… na senāsanahetu…pe… na gilānappaccayabhesajjaparikkhārahetu agārasmā anagāriyaṃ pabbajito. atha ca pana me imaṃ puggalaṃ upanissāya viharato anupaṭṭhitā ceva sati na upaṭṭhāti, asamāhitañca cittaṃ na samādhiyati, aparikkhīṇā ca āsavā na parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ nānupāpuṇāmī’ti. tena, bhikkhave, bhikkhunā saṅkhāpi so puggalo āpucchā pakkamitabbaṃ, nānubandhitabbo.</p>\r\n<p class="bodytext"><a name="para197"></a><a name="para197_mn1"></a><span class="paranum">197</span>. idha pana, bhikkhave, bhikkhu aññataraṃ puggalaṃ upanissāya viharati. tassa taṃ puggalaṃ upanissāya viharato anupaṭṭhitā ceva sati upaṭṭhāti, asamāhitañca <a name="T1.0218"></a> cittaṃ samādhiyati, aparikkhīṇā ca āsavā parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ anupāpuṇāti. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te kasirena samudāgacchanti. tena, bhikkhave, bhikkhunā iti paṭisañcikkhitabbaṃ – ‘ahaṃ kho imaṃ puggalaṃ upanissāya viharāmi. tassa me imaṃ puggalaṃ upanissāya viharato anupaṭṭhitā ceva sati upaṭṭhāti, asamāhitañca cittaṃ samādhiyati, aparikkhīṇā ca āsavā parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ anupāpuṇāmi. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te kasirena samudāgacchanti. na kho panāhaṃ cīvarahetu agārasmā anagāriyaṃ pabbajito, na piṇḍapātahetu…pe… na senāsanahetu…pe… na gilānappaccayabhesajjaparikkhārahetu agārasmā <a name="V1.0154"></a> anagāriyaṃ pabbajito. atha ca pana me imaṃ puggalaṃ upanissāya viharato anupaṭṭhitā ceva sati upaṭṭhāti, asamāhitañca cittaṃ samādhiyati, aparikkhīṇā ca āsavā parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ anupāpuṇāmī’ti. tena, bhikkhave, bhikkhunā saṅkhāpi so puggalo anubandhitabbo, na pakkamitabbaṃ.</p>\r\n<p class="bodytext"><a name="para198"></a><a name="para198_mn1"></a><span class="paranum">198</span>. idha pana, bhikkhave, bhikkhu aññataraṃ puggalaṃ upanissāya viharati. tassa taṃ puggalaṃ upanissāya viharato anupaṭṭhitā ceva sati <a name="T1.0219"></a> upaṭṭhāti, asamāhitañca cittaṃ samādhiyati, aparikkhīṇā ca āsavā parikkhayaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-197-198-');
INSERT INTO pages VALUES(3193,'mula_ma_01',154,replace(replace('<p class="bodytext">gacchanti<a name="M1.0154"></a>, ananuppattañca anuttaraṃ yogakkhemaṃ anupāpuṇāti. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te appakasirena samudāgacchanti. tena, bhikkhave, bhikkhunā iti paṭisañcikkhitabbaṃ – ‘ahaṃ kho imaṃ puggalaṃ upanissāya viharāmi<a name="P1.0108"></a>. tassa me imaṃ puggalaṃ upanissāya viharato anupaṭṭhitā ceva sati upaṭṭhāti, asamāhitañca cittaṃ samādhiyati, aparikkhīṇā ca āsavā parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ anupāpuṇāmi. ye ca kho ime pabbajitena jīvitaparikkhārā samudānetabbā – cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārā – te appakasirena samudāgacchantī’ti. tena, bhikkhave, bhikkhunā yāvajīvampi so puggalo anubandhitabbo, na pakkamitabbaṃ, api panujjamānenapī"ti <span class="note">[api paṇujjamānenāti (?)]</span>.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">vanapatthasuttaṃ niṭṭhitaṃ sattamaṃ.</p>\r\n<p class="subhead">8. madhupiṇḍikasuttaṃ</p>\r\n<p class="bodytext"><a name="para199"></a><a name="para199_mn1"></a><span class="paranum">199</span>. evaṃ <a name="T1.0220"></a><a name="V1.0155"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sakkesu viharati kapilavatthusmiṃ nigrodhārāme. atha kho bhagavā pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya kapilavatthuṃ piṇḍāya pāvisi. kapilavatthusmiṃ piṇḍāya caritvā pacchābhattaṃ piṇḍapātapaṭikkanto yena mahāvanaṃ tenupasaṅkami divāvihārāya. mahāvanaṃ ajjhogāhetvā beluvalaṭṭhikāya mūle divāvihāraṃ nisīdi. daṇḍapāṇipi kho sakko jaṅghāvihāraṃ <span class="note">[jaṅghavihāraṃ (ka.)]</span> anucaṅkamamāno anuvicaramāno yena mahāvanaṃ tenupasaṅkami. mahāvanaṃ ajjhogāhetvā yena beluvalaṭṭhikā yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavatā saddhiṃ sammodi. sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā daṇḍamolubbha ekamantaṃ aṭṭhāsi. ekamantaṃ ṭhito kho daṇḍapāṇi sakko bhagavantaṃ etadavoca – "kiṃvādī samaṇo kimakkhāyī"ti? "yathāvādī kho, āvuso, sadevake loke samārake sabrahmake sassamaṇabrāhmaṇiyā</p>\r\n','\r',char(13)),'\n',char(10)),'-199-');
INSERT INTO pages VALUES(3194,'mula_ma_01',155,replace(replace('<p class="bodytext">pajāya <a name="M1.0155"></a> sadevamanussāya na kenaci loke viggayha tiṭṭhati, yathā ca pana kāmehi visaṃyuttaṃ viharantaṃ taṃ brāhmaṇaṃ akathaṃkathiṃ chinnakukkuccaṃ bhavābhave vītataṇhaṃ saññā nānusenti – evaṃvādī kho ahaṃ, āvuso, evamakkhāyī"ti.</p>\r\n<p class="bodytext">evaṃ vutte daṇḍapāṇi sakko sīsaṃ okampetvā<a name="P1.0109"></a>, jivhaṃ nillāḷetvā, tivisākhaṃ nalāṭikaṃ <a name="T1.0221"></a> nalāṭe vuṭṭhāpetvā daṇḍamolubbha pakkāmi.</p>\r\n<p class="bodytext"><a name="para200"></a><a name="para200_mn1"></a><span class="paranum">200</span>. atha kho bhagavā sāyanhasamayaṃ paṭisallānā vuṭṭhito yena nigrodhārāmo tenupasaṅkami, upasaṅkamitvā paññatte āsane nisīdi. nisajja kho bhagavā bhikkhū āmantesi – "idhāhaṃ, bhikkhave, pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya kapilavatthuṃ piṇḍāya pāvisiṃ. kapilavatthusmiṃ piṇḍāya caritvā pacchābhattaṃ piṇḍapātapaṭikkanto yena mahāvanaṃ tenupasaṅkamiṃ divāvihārāya. mahāvanaṃ ajjhogāhetvā beluvalaṭṭhikāya mūle divāvihāraṃ nisīdiṃ. daṇḍapāṇipi kho, bhikkhave, sakko jaṅghāvihāraṃ anucaṅkamamāno anuvicaramāno yena mahāvanaṃ tenupasaṅkami. mahāvanaṃ ajjhogāhetvā yena beluvalaṭṭhikā yenāhaṃ tenupasaṅkami, upasaṅkamitvā mayā saddhiṃ sammodi. sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā daṇḍamolubbha ekamantaṃ <a name="V1.0156"></a> aṭṭhāsi. ekamantaṃ ṭhito kho, bhikkhave, daṇḍapāṇi sakko maṃ etadavoca – ‘kiṃvādī samaṇo kimakkhāyī’ti?</p>\r\n<p class="bodytext">evaṃ vutte ahaṃ, bhikkhave, daṇḍapāṇiṃ sakkaṃ etadavocaṃ – yathāvādī kho, āvuso, sadevake loke samārake sabrahmake sassamaṇabrāhmaṇiyā pajāya sadevamanussāya na kenaci loke viggayha tiṭṭhati, yathā ca pana kāmehi visaṃyuttaṃ viharantaṃ taṃ brāhmaṇaṃ akathaṃkathiṃ chinnakukkuccaṃ bhavābhave vītataṇhaṃ saññā nānusenti – evaṃvādī kho ahaṃ, āvuso, evamakkhāyī"ti. "evaṃ vutte bhikkhave, daṇḍapāṇi sakko sīsaṃ <a name="T1.0222"></a> okampetvā, jivhaṃ nillāḷetvā, tivisākhaṃ nalāṭikaṃ nalāṭe vuṭṭhāpetvā daṇḍamolubbha pakkāmī"ti.</p>\r\n<p class="bodytext"><a name="para201"></a><a name="para201_mn1"></a><span class="paranum">201</span>. evaṃ vutte aññataro bhikkhu bhagavantaṃ etadavoca – "kiṃvādī pana, bhante, bhagavā sadevake loke samārake sabrahmake sassamaṇabrāhmaṇiyā pajāya sadevamanussāya na kenaci loke viggayha tiṭṭhati? kathañca pana, bhante, bhagavantaṃ kāmehi visaṃyuttaṃ viharantaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-200-201-');
INSERT INTO pages VALUES(3195,'mula_ma_01',156,replace(replace('<p class="bodytext">taṃ <a name="M1.0156"></a> brāhmaṇaṃ akathaṃkathiṃ chinnakukkuccaṃ bhavābhave vītataṇhaṃ saññā nānusentī"ti? "yatonidānaṃ, bhikkhu, purisaṃ papañcasaññāsaṅkhā samudācaranti. ettha ce natthi abhinanditabbaṃ abhivaditabbaṃ ajjhositabbaṃ. esevanto rāgānusayānaṃ, esevanto paṭighānusayānaṃ, esevanto diṭṭhānusayānaṃ<a name="P1.0110"></a>, esevanto vicikicchānusayānaṃ, esevanto mānānusayānaṃ, esevanto bhavarāgānusayānaṃ, esevanto avijjānusayānaṃ, esevanto daṇḍādāna-satthādāna-kalaha-viggaha-vivāda-tuvaṃtuvaṃ-pesuñña-musāvādānaṃ. etthete pāpakā akusalā dhammā aparisesā nirujjhantī’ti. idamavoca bhagavā. idaṃ vatvāna sugato uṭṭhāyāsanā vihāraṃ pāvisi.</p>\r\n<p class="bodytext"><a name="para202"></a><a name="para202_mn1"></a><span class="paranum">202</span>. atha kho tesaṃ bhikkhūnaṃ acirapakkantassa bhagavato etadahosi – "idaṃ kho no, āvuso, bhagavā saṃkhittena uddesaṃ uddisitvā, vitthārena atthaṃ avibhajitvā, uṭṭhāyāsanā vihāraṃ paviṭṭho – ‘yatonidānaṃ, bhikkhu, purisaṃ papañcasaññāsaṅkhā samudācaranti. ettha ce natthi abhinanditabbaṃ <a name="T1.0223"></a> abhivaditabbaṃ ajjhositabbaṃ. esevanto rāgānusayānaṃ…pe… etthete pāpakā akusalā dhammā aparisesā nirujjhantī’ti<a name="V1.0157"></a>. ko nu kho imassa bhagavatā saṃkhittena uddesassa uddiṭṭhassa vitthārena atthaṃ avibhattassa vitthārena atthaṃ vibhajeyyā"ti? atha kho tesaṃ bhikkhūnaṃ etadahosi – "ayaṃ kho āyasmā mahākaccāno satthu ceva saṃvaṇṇito sambhāvito ca viññūnaṃ sabrahmacārīnaṃ. pahoti cāyasmā mahākaccāno imassa bhagavatā saṃkhittena uddesassa uddiṭṭhassa vitthārena atthaṃ avibhattassa vitthārena atthaṃ vibhajituṃ. yaṃnūna mayaṃ yenāyasmā mahākaccāno tenupasaṅkameyyāma, upasaṅkamitvā āyasmantaṃ mahākaccānaṃ etamatthaṃ paṭipuccheyyāmā"ti.</p>\r\n<p class="bodytext">atha kho te bhikkhū yenāyasmā mahākaccāno tenupasaṅkamiṃsu, upasaṅkamitvā āyasmatā mahākaccānena saddhiṃ sammodiṃsu. sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdiṃsu. ekamantaṃ nisinnā kho te bhikkhū āyasmantaṃ mahākaccānaṃ etadavocuṃ – "idaṃ kho no, āvuso kaccāna, bhagavā saṃkhittena uddesaṃ uddisitvā vitthārena atthaṃ avibhajitvā uṭṭhāyāsanā vihāraṃ paviṭṭho – ‘yatonidānaṃ, bhikkhu, purisaṃ papañcasaññāsaṅkhā samudācaranti. ettha ce natthi abhinanditabbaṃ abhivaditabbaṃ ajjhositabbaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-202-');
INSERT INTO pages VALUES(3196,'mula_ma_01',157,replace(replace('<p class="bodytext">esevanto <a name="M1.0157"></a> rāgānusayānaṃ…pe… etthete pāpakā akusalā dhammā aparisesā nirujjhantī’ti. tesaṃ no, āvuso kaccāna, amhākaṃ acirapakkantassa bhagavato etadahosi – ‘idaṃ kho no, āvuso, bhagavā saṃkhittena uddesaṃ uddisitvā vitthārena atthaṃ avibhajitvā uṭṭhāyāsanā vihāraṃ paviṭṭho – "yatonidānaṃ, bhikkhu, purisaṃ papañcasaññāsaṅkhā samudācaranti. ettha <a name="T1.0224"></a> ce natthi abhinanditabbaṃ abhivaditabbaṃ ajjhositabbaṃ. esevanto rāgānusayānaṃ…pe… etthete pāpakā akusalā dhammā aparisesā nirujjhantī"ti. ko nu kho imassa bhagavatā saṃkhittena uddesassa uddiṭṭhassa vitthārena atthaṃ avibhattassa vitthārena atthaṃ vibhajeyyāti? tesaṃ no, āvuso kaccāna, amhākaṃ etadahosi – ‘ayaṃ kho āyasmā mahākaccāno satthu ceva saṃvaṇṇito sambhāvito ca viññūnaṃ sabrahmacārīnaṃ, pahoti <a name="P1.0111"></a> cāyasmā mahākaccāno imassa bhagavatā saṃkhittena <a name="V1.0158"></a> uddesassa uddiṭṭhassa vitthārena atthaṃ avibhattassa vitthārena atthaṃ vibhajituṃ. yaṃnūna mayaṃ yenāyasmā mahākaccāno tenupasaṅkameyyāma, upasaṅkamitvā āyasmantaṃ mahākaccānaṃ etamatthaṃ paṭipuccheyyāmā’ti. vibhajatāyasmā mahākaccāno"ti.</p>\r\n<p class="bodytext"><a name="para203"></a><a name="para203_mn1"></a><span class="paranum">203</span>. seyyathāpi, āvuso, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva mūlaṃ, atikkamma khandhaṃ, sākhāpalāse sāraṃ pariyesitabbaṃ maññeyya, evaṃsampadamidaṃ āyasmantānaṃ satthari sammukhībhūte, taṃ bhagavantaṃ atisitvā<a name="T1.0225"></a>, amhe etamatthaṃ paṭipucchitabbaṃ maññatha. so hāvuso, bhagavā jānaṃ jānāti, passaṃ passati, cakkhubhūto ñāṇabhūto dhammabhūto brahmabhūto, vattā pavattā, atthassa ninnetā, amatassa dātā, dhammassāmī tathāgato. so ceva panetassa kālo ahosi, yaṃ bhagavantaṃyeva etamatthaṃ paṭipuccheyyātha. yathā vo bhagavā byākareyya tathā naṃ dhāreyyāthā"ti. "addhāvuso kaccāna, bhagavā jānaṃ jānāti, passaṃ passati, cakkhubhūto ñāṇabhūto dhammabhūto brahmabhūto, vattā pavattā, atthassa ninnetā, amatassa dātā, dhammassāmī tathāgato. so ceva panetassa kālo ahosi, yaṃ bhagavantaṃyeva etamatthaṃ paṭipuccheyyāma. yathā no bhagavā byākareyya tathā naṃ dhāreyyāma. api cāyasmā mahākaccāno satthu ceva saṃvaṇṇito sambhāvito ca viññūnaṃ sabrahmacārīnaṃ,</p>\r\n','\r',char(13)),'\n',char(10)),'-203-');
INSERT INTO pages VALUES(3197,'mula_ma_01',158,replace(replace('<p class="bodytext">pahoti <a name="M1.0158"></a> cāyasmā mahākaccāno imassa bhagavatā saṃkhittena uddesassa uddiṭṭhassa vitthārena atthaṃ avibhattassa vitthārena atthaṃ vibhajituṃ. vibhajatāyasmā mahākaccāno agaruṃ katvā"ti <span class="note">[agarukatvā (sī.), agarukaritvā (syā. pī.)]</span>. "tena hāvuso, suṇātha, sādhukaṃ manasikarotha, bhāsissāmī"ti. "evamāvuso"ti kho te bhikkhū āyasmato mahākaccānassa paccassosuṃ. āyasmā mahākaccāno etadavoca –</p>\r\n<p class="bodytext"><a name="para204"></a><a name="para204_mn1"></a><span class="paranum">204</span>. yaṃ kho no, āvuso, bhagavā saṃkhittena uddesaṃ uddisitvā vitthārena atthaṃ avibhajitvā uṭṭhāyāsanā vihāraṃ paviṭṭho – ‘yatonidānaṃ, bhikkhu, purisaṃ papañcasaññāsaṅkhā samudācaranti<a name="T1.0226"></a>. ettha ce natthi abhinanditabbaṃ abhivaditabbaṃ ajjhositabbaṃ, esevanto rāgānusayānaṃ…pe… etthete pāpakā akusalā dhammā aparisesā nirujjhantī’ti, imassa kho ahaṃ, āvuso, bhagavatā saṃkhittena uddesassa uddiṭṭhassa vitthārena atthaṃ avibhattassa evaṃ vitthārena atthaṃ ājānāmi –</p>\r\n<p class="bodytext">cakkhuñcāvuso, paṭicca rūpe ca uppajjati cakkhuviññāṇaṃ, tiṇṇaṃ saṅgati phasso, phassapaccayā <a name="V1.0159"></a> vedanā, yaṃ vedeti taṃ sañjānāti<a name="P1.0112"></a>, yaṃ sañjānāti taṃ vitakketi, yaṃ vitakketi taṃ papañceti, yaṃ papañceti tatonidānaṃ purisaṃ papañcasaññāsaṅkhā samudācaranti atītānāgatapaccuppannesu cakkhuviññeyyesu rūpesu. sotañcāvuso, paṭicca sadde ca uppajjati sotaviññāṇaṃ…pe… ghānañcāvuso, paṭicca gandhe ca uppajjati ghānaviññāṇaṃ…pe… jivhañcāvuso, paṭicca rase ca uppajjati jivhāviññāṇaṃ…pe… kāyañcāvuso, paṭicca phoṭṭhabbe ca uppajjati kāyaviññāṇaṃ…pe… manañcāvuso, paṭicca dhamme ca uppajjati manoviññāṇaṃ, tiṇṇaṃ saṅgati phasso, phassapaccayā vedanā, yaṃ vedeti taṃ sañjānāti, yaṃ sañjānāti taṃ vitakketi, yaṃ vitakketi taṃ papañceti, yaṃ papañceti tatonidānaṃ purisaṃ papañcasaññāsaṅkhā samudācaranti atītānāgatapaccuppannesu manoviññeyyesu dhammesu.</p>\r\n<p class="bodytext">so vatāvuso, cakkhusmiṃ sati rūpe sati cakkhuviññāṇe sati phassapaññattiṃ paññāpessatīti – ṭhānametaṃ vijjati. phassapaññattiyā sati vedanāpaññattiṃ paññāpessatīti – ṭhānametaṃ vijjati. vedanāpaññattiyā sati saññāpaññattiṃ paññāpessatīti – ṭhānametaṃ vijjati. saññāpaññattiyā</p>\r\n','\r',char(13)),'\n',char(10)),'-204-');
INSERT INTO pages VALUES(3198,'mula_ma_01',159,replace(replace('<p class="bodytext">sati <a name="M1.0159"></a> vitakkapaññattiṃ paññāpessatīti – ṭhānametaṃ vijjati. vitakkapaññattiyā sati papañcasaññāsaṅkhāsamudācaraṇapaññattiṃ paññāpessatīti – ṭhānametaṃ vijjati. so vatāvuso, sotasmiṃ sati sadde sati…pe… <a name="V1.0160"></a><a name="T1.0227"></a> ghānasmiṃ sati gandhe sati…pe… jivhāya sati rase sati…pe… kāyasmiṃ sati phoṭṭhabbe sati…pe… manasmiṃ sati dhamme sati manoviññāṇe sati phassapaññattiṃ paññāpessatīti – ṭhānametaṃ vijjati. phassapaññattiyā sati vedanāpaññattiṃ paññāpessatīti – ṭhānametaṃ vijjati. vedanāpaññattiyā sati saññāpaññattiṃ paññāpessatīti – ṭhānametaṃ vijjati. saññāpaññattiyā sati vitakkapaññattiṃ paññāpessatīti – ṭhānametaṃ vijjati. vitakkapaññattiyā sati papañcasaññāsaṅkhāsamudācaraṇapaññattiṃ paññāpessatīti – ṭhānametaṃ vijjati.</p>\r\n<p class="bodytext">so vatāvuso, cakkhusmiṃ asati rūpe asati cakkhuviññāṇe asati phassapaññattiṃ paññāpessatīti – netaṃ ṭhānaṃ vijjati. phassapaññattiyā asati vedanāpaññattiṃ paññāpessatīti – netaṃ ṭhānaṃ vijjati. vedanāpaññattiyā asati saññāpaññattiṃ paññāpessatīti – netaṃ ṭhānaṃ vijjati. saññāpaññattiyā asati vitakkapaññattiṃ paññāpessatīti – netaṃ ṭhānaṃ vijjati. vitakkapaññattiyā asati papañcasaññāsaṅkhāsamudācaraṇapaññattiṃ paññāpessatīti – netaṃ ṭhānaṃ vijjati. so vatāvuso, sotasmiṃ <a name="T1.0229"></a> asati sadde asati…pe… ghānasmiṃ asati gandhe asati…pe… jivhāya asati rase asati…pe… kāyasmiṃ asati phoṭṭhabbe asati…pe… manasmiṃ asati dhamme asati manoviññāṇe <a name="V1.0161"></a> asati phassapaññattiṃ paññāpessatīti – netaṃ ṭhānaṃ vijjati. phassapaññattiyā asati vedanāpaññattiṃ paññāpessatīti – netaṃ ṭhānaṃ vijjati. vedanāpaññattiyā asati saññāpaññattiṃ paññāpessatīti – netaṃ ṭhānaṃ vijjati. saññāpaññattiyā asati vitakkapaññattiṃ paññāpessatīti – netaṃ ṭhānaṃ vijjati. vitakkapaññattiyā asati papañcasaññāsaṅkhāsamudācaraṇapaññattiṃ paññāpessatīti – netaṃ ṭhānaṃ vijjati.</p>\r\n<p class="bodytext">yaṃ kho no, āvuso, bhagavā saṃkhittena <a name="P1.0113"></a> uddesaṃ uddisitvā vitthārena atthaṃ avibhajitvā uṭṭhāyāsanā vihāraṃ paviṭṭho – ‘yatonidānaṃ, bhikkhu, purisaṃ papañcasaññāsaṅkhā samudācaranti ettha ce natthi abhinanditabbaṃ abhivaditabbaṃ ajjhositabbaṃ esevanto rāgānusayānaṃ…pe… etthete pāpakā akusalā dhammā aparisesā nirujjhantī’ti, imassa kho ahaṃ, āvuso,</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3199,'mula_ma_01',160,replace(replace('<p class="bodytext">bhagavatā <a name="M1.0160"></a> saṃkhittena uddesassa uddiṭṭhassa vitthārena atthaṃ avibhattassa evaṃ vitthārena atthaṃ ājānāmi. ākaṅkhamānā ca pana tumhe āyasmanto bhagavantaṃyeva upasaṅkamitvā etamatthaṃ paṭipuccheyyātha. yathā no bhagavā byākaroti tathā naṃ dhāreyyāthā"ti.</p>\r\n<p class="bodytext"><a name="para205"></a><a name="para205_mn1"></a><span class="paranum">205</span>. atha kho te bhikkhū āyasmato mahākaccānassa bhāsitaṃ abhinanditvā anumoditvā uṭṭhāyāsanā yena bhagavā tenupasaṅkamiṃsu, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu. ekamantaṃ nisinnā kho te bhikkhū bhagavantaṃ etadavocuṃ – "yaṃ kho no, bhante, bhagavā saṃkhittena uddesaṃ uddisitvā vitthārena atthaṃ avibhajitvā uṭṭhāyāsanā vihāraṃ paviṭṭho – ‘yatonidānaṃ, bhikkhu, purisaṃ papañcasaññāsaṅkhā samudācaranti. ettha ce natthi abhinanditabbaṃ abhivaditabbaṃ ajjhositabbaṃ. esevanto rāgānusayānaṃ…pe… etthete pāpakā akusalā dhammā aparisesā nirujjhantī’ti. tesaṃ no, bhante, amhākaṃ acirapakkantassa bhagavato etadahosi – ‘idaṃ kho no, āvuso, bhagavā saṃkhittena uddesaṃ uddisitvā vitthārena atthaṃ avibhajitvā uṭṭhāyāsanā vihāraṃ paviṭṭho – "yatonidānaṃ, bhikkhu, purisaṃ papañcasaññāsaṅkhā samudācaranti. ettha ce natthi abhinanditabbaṃ abhivaditabbaṃ ajjhositabbaṃ. esevanto rāgānusayānaṃ, esevanto paṭighānusayānaṃ, esevanto diṭṭhānusayānaṃ, esevanto vicikicchānusayānaṃ, esevanto mānānusayānaṃ, esevanto bhavarāgānusayānaṃ<a name="V1.0162"></a>, esevanto avijjānusayānaṃ, esevanto daṇḍādāna-satthādāna-kalaha-viggaha-vivādatuvaṃtuvaṃ-pesuñña-musāvādānaṃ. etthete pāpakā akusalā dhammā aparisesā nirujjhantī"ti. ko nu kho imassa bhagavatā saṃkhittena uddesassa uddiṭṭhassa vitthārena atthaṃ avibhattassa vitthārena atthaṃ vibhajeyyā’ti? tesaṃ no, bhante, amhākaṃ etadahosi – ‘ayaṃ kho āyasmā mahākaccāno satthu ceva saṃvaṇṇito sambhāvito ca viññūnaṃ sabrahmacārīnaṃ, pahoti cāyasmā mahākaccāno <a name="T1.0230"></a> imassa bhagavatā saṃkhittena uddesassa uddiṭṭhassa vitthārena atthaṃ avibhattassa vitthārena atthaṃ vibhajituṃ, yaṃnūna mayaṃ yenāyasmā mahākaccāno tenupasaṅkameyyāma, upasaṅkamitvā āyasmantaṃ mahākaccānaṃ etamatthaṃ paṭipuccheyyāmā’ti. atha kho mayaṃ, bhante, yenāyasmā mahākaccāno tenupasaṅkamimha, upasaṅkamitvā</p>\r\n','\r',char(13)),'\n',char(10)),'-205-');
INSERT INTO pages VALUES(3200,'mula_ma_01',161,replace(replace('<p class="bodytext">āyasmantaṃ <a name="M1.0161"></a> mahākaccānaṃ <a name="P1.0114"></a> etamatthaṃ paṭipucchimha. tesaṃ no, bhante, āyasmatā mahākaccānena imehi ākārehi imehi padehi imehi byañjanehi attho vibhatto"ti. "paṇḍito, bhikkhave, mahākaccāno, mahāpañño, bhikkhave, mahākaccāno. maṃ cepi tumhe, bhikkhave, etamatthaṃ paṭipuccheyyātha, ahampi taṃ evamevaṃ byākareyyaṃ yathā taṃ mahākaccānena byākataṃ. eso cevetassa attho. evañca <span class="note">[evemeva ca (ka.)]</span> naṃ dhārethā"ti.</p>\r\n<p class="bodytext">evaṃ vutte āyasmā ānando bhagavantaṃ etadavoca – "seyyathāpi, bhante, puriso jighacchādubbalyapareto madhupiṇḍikaṃ adhigaccheyya, so yato yato sāyeyya, labhetheva sādurasaṃ asecanakaṃ. evameva kho, bhante, cetaso bhikkhu dabbajātiko, yato yato imassa dhammapariyāyassa paññāya atthaṃ upaparikkheyya, labhetheva attamanataṃ, labhetheva cetaso pasādaṃ. ko nāmo ayaṃ <span class="note">[ko nāmāyaṃ (syā.)]</span>, bhante, dhammapariyāyo"ti? "tasmātiha tvaṃ, ānanda, imaṃ dhammapariyāyaṃ madhupiṇḍikapariyāyo tveva naṃ dhārehī"ti.</p>\r\n<p class="bodytext">idamavoca <a name="T1.0231"></a> bhagavā. attamano āyasmā ānando bhagavato bhāsitaṃ abhinandīti.</p>\r\n<p class="centered">madhupiṇḍikasuttaṃ niṭṭhitaṃ aṭṭhamaṃ.</p>\r\n<p class="subhead">9. dvedhāvitakkasuttaṃ</p>\r\n<p class="bodytext"><a name="para206"></a><a name="para206_mn1"></a><span class="paranum">206</span>. evaṃ <a name="V1.0163"></a><a name="T1.0232"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext">pubbeva me, bhikkhave, sambodhā anabhisambuddhassa bodhisattasseva sato etadahosi – ‘yaṃnūnāhaṃ dvidhā katvā dvidhā katvā vitakke vihareyya’nti. so kho ahaṃ, bhikkhave, yo cāyaṃ kāmavitakko yo ca byāpādavitakko yo ca vihiṃsāvitakko – imaṃ ekaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-206-');
INSERT INTO pages VALUES(3201,'mula_ma_01',162,replace(replace('<p class="bodytext">bhāgamakāsiṃ<a name="M1.0162"></a>, yo cāyaṃ nekkhammavitakko yo ca abyāpādavitakko yo ca avihiṃsāvitakko – imaṃ dutiyaṃ bhāgamakāsiṃ.</p>\r\n<p class="bodytext"><a name="para207"></a><a name="para207_mn1"></a><span class="paranum">207</span>. tassa mayhaṃ, bhikkhave, evaṃ appamattassa ātāpino pahitattassa viharato uppajjati <a name="P1.0115"></a> kāmavitakko. so evaṃ pajānāmi – ‘uppanno kho me ayaṃ kāmavitakko. so ca kho attabyābādhāyapi saṃvattati, parabyābādhāyapi saṃvattati, ubhayabyābādhāyapi saṃvattati, paññānirodhiko vighātapakkhiko anibbānasaṃvattaniko’ <span class="note">[anibbānasaṃvattaniko"ti (?)]</span>. ‘attabyābādhāya saṃvattatī’tipi me, bhikkhave, paṭisañcikkhato abbhatthaṃ gacchati, ‘parabyābādhāya saṃvattatī’tipi me, bhikkhave, paṭisañcikkhato abbhatthaṃ gacchati, ‘ubhayabyābādhāya saṃvattatī’tipi me, bhikkhave, paṭisañcikkhato abbhatthaṃ gacchati, <a name="T1.0233"></a> ‘paññānirodhiko vighātapakkhiko anibbānasaṃvattaniko’tipi me, bhikkhave, paṭisañcikkhato abbhatthaṃ gacchati. so kho ahaṃ, bhikkhave, uppannuppannaṃ kāmavitakkaṃ pajahameva <span class="note">[atītakālikakiriyāpadāniyeva]</span> vinodameva <span class="note">[atītakālikakiriyāpadāniyeva]</span> byantameva <span class="note">[byanteva (sī. syā. pī.)]</span> naṃ akāsiṃ.</p>\r\n<p class="bodytext"><a name="para208"></a><a name="para208_mn1"></a><span class="paranum">208</span>. tassa mayhaṃ, bhikkhave, evaṃ appamattassa ātāpino pahitattassa viharato uppajjati byāpādavitakko…pe… uppajjati vihiṃsāvitakko. so evaṃ pajānāmi – ‘uppanno kho me ayaṃ vihiṃsāvitakko. so ca kho attabyābādhāyapi saṃvattati, parabyābādhāyapi <a name="V1.0164"></a> saṃvattati, ubhayabyābādhāyapi saṃvattati, paññānirodhiko vighātapakkhiko anibbānasaṃvattaniko’. ‘attabyābādhāya saṃvattatī’tipi me, bhikkhave, paṭisañcikkhato abbhatthaṃ gacchati, ‘parabyābādhāya saṃvattatī’tipi me, bhikkhave, paṭisañcikkhato abbhatthaṃ gacchati, ‘ubhayabyābādhāya saṃvattatī’tipi me, bhikkhave, paṭisañcikkhato abbhatthaṃ gacchati, ‘paññānirodhiko vighātapakkhiko anibbānasaṃvattaniko’tipi me, bhikkhave, paṭisañcikkhato abbhatthaṃ gacchati. so kho ahaṃ, bhikkhave, uppannuppannaṃ vihiṃsāvitakkaṃ pajahameva vinodameva byantameva naṃ akāsiṃ.</p>\r\n<p class="bodytext">yaññadeva, bhikkhave, bhikkhu bahulamanuvitakketi anuvicāreti, tathā tathā nati hoti cetaso. kāmavitakkaṃ ce, bhikkhave, bhikkhu</p>\r\n','\r',char(13)),'\n',char(10)),'-207-208-');
INSERT INTO pages VALUES(3202,'mula_ma_01',163,replace(replace('<p class="bodytext">bahulamanuvitakketi <a name="M1.0163"></a> anuvicāreti, pahāsi nekkhammavitakkaṃ, kāmavitakkaṃ bahulamakāsi, tassa taṃ <a name="T1.0234"></a> kāmavitakkāya cittaṃ namati. byāpādavitakkaṃ ce, bhikkhave…pe… vihiṃsāvitakkaṃ ce, bhikkhave, bhikkhu bahulamanuvitakketi anuvicāreti, pahāsi avihiṃsāvitakkaṃ, vihiṃsāvitakkaṃ bahulamakāsi, tassa taṃ vihiṃsāvitakkāya cittaṃ namati. seyyathāpi, bhikkhave, vassānaṃ pacchime māse saradasamaye kiṭṭhasambādhe gopālako gāvo rakkheyya. so tā gāvo tato tato daṇḍena ākoṭeyya paṭikoṭeyya sannirundheyya sannivāreyya. taṃ kissa hetu? passati hi so, bhikkhave, gopālako tatonidānaṃ vadhaṃ vā bandhanaṃ vā jāniṃ vā garahaṃ vā. evameva kho ahaṃ, bhikkhave, addasaṃ akusalānaṃ dhammānaṃ ādīnavaṃ okāraṃ saṃkilesaṃ, kusalānaṃ dhammānaṃ nekkhamme ānisaṃsaṃ vodānapakkhaṃ.</p>\r\n<p class="bodytext"><a name="para209"></a><a name="para209_mn1"></a><span class="paranum">209</span>. tassa <a name="P1.0116"></a> mayhaṃ, bhikkhave, evaṃ appamattassa ātāpino pahitattassa viharato uppajjati nekkhammavitakko. so evaṃ pajānāmi – ‘uppanno kho me ayaṃ nekkhammavitakko. so ca kho nevattabyābādhāya saṃvattati, na parabyābādhāya saṃvattati, na ubhayabyābādhāya saṃvattati, paññāvuddhiko avighātapakkhiko nibbānasaṃvattaniko’. rattiṃ cepi naṃ, bhikkhave, anuvitakkeyyaṃ anuvicāreyyaṃ, neva tatonidānaṃ bhayaṃ samanupassāmi. divasaṃ cepi naṃ, bhikkhave, anuvitakkeyyaṃ anuvicāreyyaṃ, neva tatonidānaṃ bhayaṃ samanupassāmi. rattindivaṃ cepi naṃ, bhikkhave, anuvitakkeyyaṃ anuvicāreyyaṃ, neva tatonidānaṃ bhayaṃ samanupassāmi. api ca kho me aticiraṃ anuvitakkayato anuvicārayato kāyo kilameyya<a name="V1.0165"></a>. kāye kilante <span class="note">[kilamante (ka.)]</span> cittaṃ <a name="T1.0235"></a> ūhaññeyya. ūhate citte ārā cittaṃ samādhimhāti. so kho ahaṃ, bhikkhave, ajjhattameva cittaṃ saṇṭhapemi sannisādemi ekodiṃ karomi <span class="note">[ekodi karomi (pī.)]</span> samādahāmi. taṃ kissa hetu? ‘mā me cittaṃ ūhaññī’ti <span class="note">[ugghāṭīti (syā. ka.), ūhanīti (pī.)]</span>.</p>\r\n<p class="bodytext"><a name="para210"></a><a name="para210_mn1"></a><span class="paranum">210</span>. tassa mayhaṃ, bhikkhave, evaṃ appamattassa ātāpino pahitattassa viharato uppajjati abyāpādavitakko…pe… uppajjati avihiṃsāvitakko. so evaṃ pajānāmi – ‘uppanno kho me ayaṃ avihiṃsāvitakko. so ca kho nevattabyābādhāya saṃvattati, na parabyābādhāya saṃvattati, na</p>\r\n','\r',char(13)),'\n',char(10)),'-209-210-');
INSERT INTO pages VALUES(3203,'mula_ma_01',164,replace(replace('<p class="bodytext">ubhayabyābādhāya <a name="M1.0164"></a> saṃvattati, paññāvuddhiko avighātapakkhiko nibbānasaṃvattaniko’. rattiṃ cepi naṃ, bhikkhave, anuvitakkeyyaṃ anuvicāreyyaṃ, neva tatonidānaṃ bhayaṃ samanupassāmi. divasaṃ cepi naṃ, bhikkhave, anuvitakkeyyaṃ anuvicāreyyaṃ, neva tatonidānaṃ bhayaṃ samanupassāmi. rattindivaṃ cepi naṃ, bhikkhave, anuvitakkeyyaṃ anuvicāreyyaṃ, neva tatonidānaṃ bhayaṃ samanupassāmi. api ca kho me aticiraṃ anuvitakkayato anuvicārayato kāyo kilameyya. kāye kilante cittaṃ ūhaññeyya. ūhate citte ārā cittaṃ samādhimhāti. so kho ahaṃ, bhikkhave, ajjhattameva cittaṃ saṇṭhapemi, sannisādemi, ekodiṃ karomi samādahāmi. taṃ kissa hetu? ‘mā me cittaṃ ūhaññī’ti.</p>\r\n<p class="bodytext">yaññadeva, bhikkhave, bhikkhu bahulamanuvitakketi anuvicāreti, tathā tathā nati hoti cetaso. nekkhammavitakkañce, bhikkhave, bhikkhu bahulamanuvitakketi anuvicāreti, pahāsi <a name="T1.0236"></a> kāmavitakkaṃ, nekkhammavitakkaṃ bahulamakāsi, tassaṃ taṃ nekkhammavitakkāya cittaṃ namati. abyāpādavitakkañce, bhikkhave…pe… avihiṃsāvitakkañce, bhikkhave, bhikkhu bahulamanuvitakketi anuvicāreti, pahāsi vihiṃsāvitakkaṃ, avihiṃsāvitakkaṃ bahulamakāsi, tassa taṃ avihiṃsāvitakkāya cittaṃ namati. seyyathāpi, bhikkhave, gimhānaṃ pacchime māse sabbasassesu gāmantasambhatesu gopālako gāvo rakkheyya<a name="P1.0117"></a>, tassa rukkhamūlagatassa vā abbhokāsagatassa vā satikaraṇīyameva hoti – ‘etā <span class="note">[ete (ka.)]</span> gāvo’ti. evamevaṃ kho, bhikkhave, satikaraṇīyameva ahosi – ‘ete dhammā’ti.</p>\r\n<p class="bodytext"><a name="para211"></a><a name="para211_mn1"></a><span class="paranum">211</span>. āraddhaṃ kho pana me, bhikkhave, vīriyaṃ ahosi asallīnaṃ, upaṭṭhitā sati asammuṭṭhā<a name="V1.0166"></a>, passaddho kāyo asāraddho, samāhitaṃ cittaṃ ekaggaṃ. so kho ahaṃ, bhikkhave, vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja vihāsiṃ. vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja vihāsiṃ. pītiyā ca virāgā upekkhako ca vihāsiṃ sato ca sampajāno, sukhañca kāyena paṭisaṃvedesiṃ, yaṃ taṃ ariyā ācikkhanti ‘upekkhako</p>\r\n','\r',char(13)),'\n',char(10)),'-211-');
INSERT INTO pages VALUES(3204,'mula_ma_01',165,replace(replace('<p class="bodytext">satimā <a name="M1.0165"></a> sukhavihārī’ti, tatiyaṃ jhānaṃ upasampajja vihāsiṃ. sukhassa ca pahānā dukkhassa ca pahānā pubbeva somanassadomanassānaṃ atthaṅgamā adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja vihāsiṃ.</p>\r\n<p class="bodytext"><a name="para212"></a><a name="para212_mn1"></a><span class="paranum">212</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte pubbenivāsānussatiñāṇāya cittaṃ abhininnāmesiṃ. so anekavihitaṃ pubbenivāsaṃ anussarāmi. seyyathidaṃ, ekampi jātiṃ…pe… iti sākāraṃ sauddesaṃ anekavihitaṃ pubbenivāsaṃ anussarāmi. ayaṃ kho <a name="T1.0237"></a> me, bhikkhave, rattiyā paṭhame yāme paṭhamā vijjā adhigatā, avijjā vihatā vijjā uppannā, tamo vihato āloko uppanno, yathā taṃ appamattassa ātāpino pahitattassa viharato.</p>\r\n<p class="bodytext"><a name="para213"></a><a name="para213_mn1"></a><span class="paranum">213</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte sattānaṃ cutūpapātañāṇāya cittaṃ abhininnāmesiṃ. so dibbena cakkhunā visuddhena atikkantamānusakena satte passāmi cavamāne upapajjamāne…pe… ime vata bhonto sattā kāyaduccaritena samannāgatā…pe… iti dibbena cakkhunā visuddhena atikkantamānusakena satte passāmi cavamāne upapajjamāne hīne paṇīte suvaṇṇe dubbaṇṇe sugate duggate, yathākammūpage satte pajānāmi. ayaṃ kho me, bhikkhave, rattiyā majjhime yāme dutiyā vijjā adhigatā, avijjā vihatā vijjā uppannā, tamo vihato āloko uppanno, yathā taṃ appamattassa ātāpino pahitattassa viharato.</p>\r\n<p class="bodytext"><a name="para214"></a><a name="para214_mn1"></a><span class="paranum">214</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte āsavānaṃ khayañāṇāya cittaṃ abhininnāmesiṃ. so ‘idaṃ dukkha’nti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ dukkhasamudayo’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ dukkhanirodho’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ dukkhanirodhagāminī paṭipadā’ti yathābhūtaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-212-213-214-');
INSERT INTO pages VALUES(3205,'mula_ma_01',166,replace(replace('<p class="bodytext">abbhaññāsiṃ<a name="M1.0166"></a>. ‘ime āsavā’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ āsavasamudayo’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ <a name="V1.0167"></a> āsavanirodho’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ <a name="T1.0238"></a> āsavanirodhagāminī paṭipadā’ti yathābhūtaṃ abbhaññāsiṃ. tassa me evaṃ jānato evaṃ passato kāmāsavāpi cittaṃ vimuccittha, bhavāsavāpi cittaṃ vimuccittha, avijjāsavāpi cittaṃ vimuccittha, vimuttasmiṃ vimuttamiti ñāṇaṃ ahosi – ‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti abbhaññāsiṃ. ayaṃ kho me, bhikkhave, rattiyā pacchime yāme tatiyā vijjā adhigatā, avijjā vihatā vijjā uppannā, tamo vihato āloko uppanno, yathā taṃ appamattassa ātāpino pahitattassa viharato.</p>\r\n<p class="bodytext"><a name="para215"></a><a name="para215_mn1"></a><span class="paranum">215</span>. seyyathāpi, bhikkhave, araññe pavane mahantaṃ ninnaṃ pallalaṃ. tamenaṃ mahāmigasaṅgho upanissāya vihareyya. tassa kocideva puriso uppajjeyya anatthakāmo ahitakāmo ayogakkhemakāmo. so yvāssa maggo khemo sovatthiko pītigamanīyo taṃ maggaṃ pidaheyya, vivareyya kummaggaṃ, odaheyya okacaraṃ, ṭhapeyya okacārikaṃ. evañhi so, bhikkhave, mahāmigasaṅgho aparena samayena <a name="T1.0239"></a> anayabyasanaṃ <span class="note">[anayabyasanaṃ tanuttaṃ (sī. syā. pī.)]</span> āpajjeyya. tasseva kho pana, bhikkhave, mahato migasaṅghassa kocideva puriso uppajjeyya atthakāmo hitakāmo yogakkhemakāmo. so yvāssa maggo khemo sovatthiko pītigamanīyo taṃ maggaṃ vivareyya, pidaheyya kummaggaṃ, ūhaneyya okacaraṃ, nāseyya okacārikaṃ. evañhi so, bhikkhave, mahāmigasaṅgho aparena samayena vuddhiṃ virūḷhiṃ vepullaṃ āpajjeyya.</p>\r\n<p class="bodytext">upamā kho me ayaṃ, bhikkhave, katā atthassa viññāpanāya<a name="P1.0118"></a>. ayaṃ cevettha attho – mahantaṃ ninnaṃ pallalanti kho, bhikkhave, kāmānametaṃ adhivacanaṃ. mahāmigasaṅghoti kho, bhikkhave, sattānametaṃ adhivacanaṃ. puriso anatthakāmo ahitakāmo ayogakkhemakāmoti kho, bhikkhave, mārassetaṃ pāpimato adhivacanaṃ. kummaggoti kho, bhikkhave, aṭṭhaṅgikassetaṃ micchāmaggassa adhivacanaṃ, seyyathidaṃ – micchādiṭṭhiyā micchāsaṅkappassa micchāvācāya micchākammantassa micchāājīvassa micchāvāyāmassa micchāsatiyā micchāsamādhissa. okacaroti kho, bhikkhave, nandīrāgassetaṃ adhivacanaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-215-');
INSERT INTO pages VALUES(3206,'mula_ma_01',167,replace(replace('<p class="bodytext">okacārikāti <a name="M1.0167"></a> kho, bhikkhave, avijjāyetaṃ adhivacanaṃ. puriso atthakāmo hitakāmo yogakkhemakāmoti kho, bhikkhave, tathāgatassetaṃ adhivacanaṃ arahato sammāsambuddhassa. khemo maggo sovatthiko pītigamanīyoti kho<a name="V1.0168"></a>, bhikkhave, ariyassetaṃ aṭṭhaṅgikassa maggassa adhivacanaṃ, seyyathidaṃ – sammādiṭṭhiyā sammāsaṅkappassa sammāvācāya sammākammantassa sammāājīvassa sammāvāyāmassa sammāsatiyā sammāsamādhissa.</p>\r\n<p class="bodytext">iti kho, bhikkhave, vivaṭo mayā khemo maggo sovatthiko pītigamanīyo, pihito kummaggo, ūhato okacaro, nāsitā okacārikā. yaṃ, bhikkhave, satthārā karaṇīyaṃ sāvakānaṃ hitesinā anukampakena anukampaṃ upādāya, kataṃ vo taṃ mayā. etāni, bhikkhave<a name="T1.0240"></a>, rukkhamūlāni, etāni suññāgārāni, jhāyatha, bhikkhave, mā pamādattha, mā pacchā vippaṭisārino ahuvattha. ayaṃ vo amhākaṃ anusāsanī"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">dvedhāvitakkasuttaṃ niṭṭhitaṃ navamaṃ.</p>\r\n<p class="subhead">10. vitakkasaṇṭhānasuttaṃ</p>\r\n<p class="bodytext"><a name="para216"></a><a name="para216_mn1"></a><span class="paranum">216</span>. evaṃ <a name="T1.0241"></a><a name="V1.0169"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti <a name="P1.0119"></a> te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext">adhicittamanuyuttena, bhikkhave, bhikkhunā pañca nimittāni kālena kālaṃ manasi kātabbāni. katamāni pañca? idha, bhikkhave, bhikkhuno yaṃ nimittaṃ āgamma yaṃ nimittaṃ manasikaroto uppajjanti pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi, tena, bhikkhave, bhikkhunā tamhā nimittā aññaṃ nimittaṃ manasi kātabbaṃ kusalūpasaṃhitaṃ. tassa tamhā nimittā aññaṃ nimittaṃ manasikaroto kusalūpasaṃhitaṃ ye pāpakā</p>\r\n','\r',char(13)),'\n',char(10)),'-216-');
INSERT INTO pages VALUES(3207,'mula_ma_01',168,replace(replace('<p class="bodytext">akusalā <a name="M1.0168"></a> vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti <span class="note">[ekodibhoti (syā. ka.)]</span> samādhiyati. seyyathāpi, bhikkhave, dakkho palagaṇḍo vā palagaṇḍantevāsī vā sukhumāya āṇiyā oḷārikaṃ āṇiṃ abhinihaneyya abhinīhareyya abhinivatteyya <span class="note">[abhinivajjeyya (sī. pī.)]</span>, evameva kho, bhikkhave, bhikkhuno yaṃ nimittaṃ āgamma yaṃ nimittaṃ manasikaroto uppajjanti pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi <a name="T1.0242"></a> mohūpasaṃhitāpi, tena, bhikkhave, bhikkhunā tamhā nimittā aññaṃ nimittaṃ manasi kātabbaṃ kusalūpasaṃhitaṃ. tassa tamhā nimittā aññaṃ nimittaṃ manasikaroto kusalūpasaṃhitaṃ ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati.</p>\r\n<p class="bodytext"><a name="para217"></a><a name="para217_mn1"></a><span class="paranum">217</span>. tassa ce, bhikkhave, bhikkhuno tamhā nimittā aññaṃ nimittaṃ manasikaroto kusalūpasaṃhitaṃ uppajjanteva pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi, tena, bhikkhave, bhikkhunā tesaṃ vitakkānaṃ ādīnavo upaparikkhitabbo – ‘itipime vitakkā akusalā, itipime vitakkā sāvajjā, itipime vitakkā <a name="V1.0170"></a> dukkhavipākā’ti. tassa tesaṃ vitakkānaṃ ādīnavaṃ upaparikkhato ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati. seyyathāpi, bhikkhave, itthī vā puriso vā daharo yuvā maṇḍanakajātiko ahikuṇapena vā kukkurakuṇapena vā manussakuṇapena <a name="P1.0120"></a> vā kaṇṭhe āsattena aṭṭiyeyya harāyeyya jiguccheyya, evameva kho, bhikkhave, tassa ce bhikkhuno tamhāpi nimittā aññaṃ nimittaṃ <a name="T1.0243"></a> manasikaroto kusalūpasaṃhitaṃ uppajjanteva pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi, tena, bhikkhave, bhikkhunā tesaṃ vitakkānaṃ ādīnavo upaparikkhitabbo – ‘itipime vitakkā akusalā,</p>\r\n','\r',char(13)),'\n',char(10)),'-217-');
INSERT INTO pages VALUES(3208,'mula_ma_01',169,replace(replace('<p class="bodytext">itipime <a name="M1.0169"></a> vitakkā sāvajjā, itipime vitakkā dukkhavipākā’ti. tassa tesaṃ vitakkānaṃ ādīnavaṃ upaparikkhato ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati.</p>\r\n<p class="bodytext"><a name="para218"></a><a name="para218_mn1"></a><span class="paranum">218</span>. tassa ce, bhikkhave, bhikkhuno tesampi vitakkānaṃ ādīnavaṃ upaparikkhato uppajjanteva pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi, tena, bhikkhave, bhikkhunā tesaṃ vitakkānaṃ asatiamanasikāro āpajjitabbo. tassa tesaṃ vitakkānaṃ asatiamanasikāraṃ āpajjato ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati. seyyathāpi, bhikkhave, cakkhumā puriso āpāthagatānaṃ rūpānaṃ adassanakāmo assa, so nimīleyya vā aññena vā apalokeyya. evameva kho, bhikkhave, tassa ce bhikkhuno tesampi vitakkānaṃ ādīnavaṃ upaparikkhato uppajjanteva pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi, te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati.</p>\r\n<p class="bodytext"><a name="para219"></a><a name="para219_mn1"></a><span class="paranum">219</span>. tassa <a name="T1.0244"></a> ce, bhikkhave, bhikkhuno tesampi vitakkānaṃ asatiamanasikāraṃ āpajjato <a name="V1.0171"></a> uppajjanteva pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi, tena, bhikkhave, bhikkhunā tesaṃ vitakkānaṃ vitakkasaṅkhārasaṇṭhānaṃ manasikātabbaṃ. tassa tesaṃ vitakkānaṃ vitakkasaṅkhārasaṇṭhānaṃ manasikaroto ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati. seyyathāpi, bhikkhave, puriso sīghaṃ gaccheyya. tassa evamassa – ‘kiṃ nu kho ahaṃ sīghaṃ gacchāmi? yaṃnūnāhaṃ saṇikaṃ gaccheyya’nti. so saṇikaṃ gaccheyya. tassa evamassa – ‘kiṃ nu kho ahaṃ saṇikaṃ gacchāmi? yaṃnūnāhaṃ tiṭṭheyya’nti. so</p>\r\n','\r',char(13)),'\n',char(10)),'-218-219-');
INSERT INTO pages VALUES(3209,'mula_ma_01',170,replace(replace('<p class="bodytext">tiṭṭheyya<a name="M1.0170"></a>. tassa evamassa – ‘kiṃ nu kho ahaṃ ṭhito? yaṃnūnāhaṃ nisīdeyya’nti. so nisīdeyya. tassa evamassa – ‘kiṃ nu kho ahaṃ nisinno? yaṃnūnāhaṃ nipajjeyya’nti. so nipajjeyya. evañhi so, bhikkhave, puriso oḷārikaṃ oḷārikaṃ iriyāpathaṃ abhinivajjetvā <span class="note">[abhinissajjetvā (syā.)]</span> sukhumaṃ sukhumaṃ iriyāpathaṃ kappeyya. evameva kho, bhikkhave, tassa ce bhikkhuno tesampi vitakkānaṃ asatiamanasikāraṃ āpajjato uppajjanteva pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati.</p>\r\n<p class="bodytext"><a name="para220"></a><a name="para220_mn1"></a><span class="paranum">220</span>. tassa <a name="T1.0245"></a> ce, bhikkhave, bhikkhuno tesampi vitakkānaṃ vitakkasaṅkhārasaṇṭhānaṃ manasikaroto uppajjanteva pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi. tena, bhikkhave, bhikkhunā dantebhidantamādhāya <span class="note">[dante + abhidantaṃ + ādhāyāti ṭīkāyaṃ padacchedo, dantebhīti panettha karaṇattho yutto viya dissati]</span> jivhāya tāluṃ āhacca cetasā cittaṃ abhiniggaṇhitabbaṃ abhinippīḷetabbaṃ abhisantāpetabbaṃ<a name="P1.0121"></a>. tassa dantebhidantamādhāya jivhāya tāluṃ āhacca cetasā cittaṃ abhiniggaṇhato abhinippīḷayato abhisantāpayato ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati. seyyathāpi, bhikkhave, balavā puriso dubbalataraṃ purisaṃ sīse vā gale vā khandhe vā gahetvā abhiniggaṇheyya abhinippīḷeyya abhisantāpeyya, evameva kho, bhikkhave, tassa ce bhikkhuno tesampi vitakkānaṃ vitakkasaṅkhārasaṇṭhānaṃ manasikaroto uppajjanteva pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi. tena, bhikkhave, bhikkhunā dantebhidantamādhāya <a name="V1.0172"></a> jivhāya tāluṃ āhacca cetasā cittaṃ abhiniggaṇhitabbaṃ abhinippīḷetabbaṃ abhisantāpetabbaṃ. tassa dantebhidantamādhāya jivhāya tāluṃ āhacca cetasā cittaṃ abhiniggaṇhato abhinippīḷayato abhisantāpayato ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi <a name="T1.0246"></a></p>\r\n','\r',char(13)),'\n',char(10)),'-220-');
INSERT INTO pages VALUES(3210,'mula_ma_01',171,replace(replace('<p class="bodytext">mohūpasaṃhitāpi <a name="M1.0171"></a> te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati.</p>\r\n<p class="bodytext"><a name="para221"></a><a name="para221_mn1"></a><span class="paranum">221</span>. yato kho <span class="note">[yato ca kho (syā. ka.)]</span>, bhikkhave, bhikkhuno yaṃ nimittaṃ āgamma yaṃ nimittaṃ manasikaroto uppajjanti pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi, tassa tamhā nimittā aññaṃ nimittaṃ manasikaroto kusalūpasaṃhitaṃ ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati. tesampi vitakkānaṃ ādīnavaṃ upaparikkhato ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati. tesampi vitakkānaṃ asatiamanasikāraṃ āpajjato ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati. tesampi vitakkānaṃ vitakkasaṅkhārasaṇṭhānaṃ manasikaroto ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi <a name="T1.0247"></a> te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti samādhiyati. dantebhidantamādhāya jivhāya tāluṃ āhacca cetasā cittaṃ abhiniggaṇhato abhinippīḷayato abhisantāpayato ye pāpakā akusalā vitakkā chandūpasaṃhitāpi dosūpasaṃhitāpi mohūpasaṃhitāpi te pahīyanti te abbhatthaṃ gacchanti. tesaṃ pahānā ajjhattameva cittaṃ santiṭṭhati sannisīdati ekodi hoti <a name="P1.0122"></a> samādhiyati. ayaṃ vuccati, bhikkhave, bhikkhu vasī vitakkapariyāyapathesu. yaṃ vitakkaṃ ākaṅkhissati taṃ vitakkaṃ vitakkessati, yaṃ vitakkaṃ nākaṅkhissati na taṃ vitakkaṃ vitakkessati. acchecchi taṇhaṃ, vivattayi <span class="note">[vāvattayi (sī. pī.)]</span> saṃyojanaṃ, sammā mānābhisamayā antamakāsi dukkhassā"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-221-');
INSERT INTO pages VALUES(3211,'mula_ma_01',172,replace(replace('<p class="bodytext">idamavoca <a name="M1.0172"></a><a name="V1.0173"></a> bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">vitakkasaṇṭhānasuttaṃ niṭṭhitaṃ dasamaṃ.</p>\r\n<p class="bodytext">tassuddānaṃ –</p>\r\n<p class="gatha1">cūḷasīhanādalomahaṃsavaro, mahācūḷadukkhakkhandhaanumānikasuttaṃ,</p>\r\n<p class="gathalast">khilapatthamadhupiṇḍikadvidhāvitakka, pañcanimittakathā puna vaggo.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3212,'mula_ma_01',173,replace(replace('<p class="chapter">3. opammavaggo</p>\r\n<p class="subhead">1. kakacūpamasuttaṃ</p>\r\n<p class="bodytext"><a name="para222"></a><a name="para222_mn1"></a><span class="paranum">222</span>. evaṃ <a name="T1.0248"></a><a name="M1.0173"></a><a name="V1.0174"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tena kho pana samayena āyasmā moḷiyaphagguno bhikkhunīhi saddhiṃ ativelaṃ saṃsaṭṭho viharati. evaṃ saṃsaṭṭho āyasmā moḷiyaphagguno bhikkhunīhi saddhiṃ viharati – sace koci bhikkhu āyasmato moḷiyaphaggunassa sammukhā tāsaṃ bhikkhunīnaṃ avaṇṇaṃ bhāsati, tenāyasmā moḷiyaphagguno kupito anattamano adhikaraṇampi karoti. sace pana koci bhikkhu tāsaṃ bhikkhunīnaṃ sammukhā āyasmato moḷiyaphaggunassa avaṇṇaṃ bhāsati, tena tā bhikkhuniyo kupitā anattamanā adhikaraṇampi karonti. evaṃ saṃsaṭṭho āyasmā moḷiyaphagguno bhikkhunīhi saddhiṃ viharati. atha kho aññataro bhikkhu yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho so bhikkhu bhagavantaṃ etadavoca – "āyasmā, bhante, moḷiyaphagguno bhikkhunīhi saddhiṃ ativelaṃ saṃsaṭṭho viharati. evaṃ saṃsaṭṭho, bhante, āyasmā moḷiyaphagguno bhikkhunīhi saddhiṃ viharati – sace koci bhikkhu āyasmato moḷiyaphaggunassa sammukhā tāsaṃ bhikkhunīnaṃ avaṇṇaṃ bhāsati, tenāyasmā moḷiyaphagguno kupito anattamano <a name="T1.0249"></a> adhikaraṇampi karoti. sace pana koci bhikkhu tāsaṃ bhikkhunīnaṃ sammukhā āyasmato moḷiyaphaggunassa avaṇṇaṃ bhāsati, tena tā bhikkhuniyo kupitā anattamanā adhikaraṇampi karonti. evaṃ saṃsaṭṭho, bhante, āyasmā moḷiyaphagguno bhikkhunīhi saddhiṃ viharatī"ti.</p>\r\n<p class="bodytext"><a name="para223"></a><a name="para223_mn1"></a><span class="paranum">223</span>. atha kho bhagavā aññataraṃ bhikkhuṃ āmantesi – "ehi tvaṃ<a name="P1.0123"></a>, bhikkhu, mama vacanena moḷiyaphaggunaṃ bhikkhuṃ āmantehi – ‘satthā taṃ, āvuso phagguna, āmantetī"’ti. "evaṃ<a name="V1.0175"></a>, bhante"ti kho so bhikkhu bhagavato paṭissutvā yenāyasmā moḷiyaphagguno tenupasaṅkami, upasaṅkamitvā āyasmantaṃ moḷiyaphaggunaṃ etadavoca – "satthā taṃ, āvuso phagguna, āmantetī"ti. "evamāvuso"ti kho āyasmā moḷiyaphagguno</p>\r\n','\r',char(13)),'\n',char(10)),'-222-223-');
INSERT INTO pages VALUES(3213,'mula_ma_01',174,replace(replace('<p class="bodytext">tassa <a name="M1.0174"></a> bhikkhuno paṭissutvā yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinnaṃ kho āyasmantaṃ moḷiyaphaggunaṃ bhagavā etadavoca –</p>\r\n<p class="bodytext">saccaṃ kira tvaṃ, phagguna, bhikkhunīhi saddhiṃ ativelaṃ saṃsaṭṭho viharasi? evaṃ saṃsaṭṭho kira tvaṃ, phagguna, bhikkhunīhi saddhiṃ viharasi – sace koci bhikkhu tuyhaṃ sammukhā tāsaṃ bhikkhunīnaṃ avaṇṇaṃ bhāsati, tena tvaṃ kupito anattamano adhikaraṇampi karosi. sace pana koci bhikkhu tāsaṃ bhikkhunīnaṃ sammukhā tuyhaṃ avaṇṇaṃ bhāsati, tena tā bhikkhuniyo kupitā anattamanā adhikaraṇampi karonti. evaṃ saṃsaṭṭho kira tvaṃ, phagguna, bhikkhunīhi saddhiṃ viharasī"ti? "evaṃ, bhante"ti. "nanu tvaṃ, phagguna, kulaputto saddhā <a name="T1.0250"></a> agārasmā anagāriyaṃ pabbajito"ti? "evaṃ, bhante"ti.</p>\r\n<p class="bodytext"><a name="para224"></a><a name="para224_mn1"></a><span class="paranum">224</span>. na kho te etaṃ, phagguna, patirūpaṃ kulaputtassa saddhā agārasmā anagāriyaṃ pabbajitassa, yaṃ tvaṃ bhikkhunīhi saddhiṃ ativelaṃ saṃsaṭṭho vihareyyāsi. tasmātiha, phagguna, tava cepi koci sammukhā tāsaṃ bhikkhunīnaṃ avaṇṇaṃ bhāseyya, tatrāpi tvaṃ, phagguna, ye gehasitā <span class="note">[gehassitā (?)]</span> chandā ye gehasitā vitakkā te pajaheyyāsi. tatrāpi te, phagguna, evaṃ sikkhitabbaṃ – ‘na ceva me cittaṃ vipariṇataṃ bhavissati, na ca pāpikaṃ vācaṃ nicchāressāmi, hitānukampī ca viharissāmi mettacitto, na dosantaro’ti. evañhi te, phagguna, sikkhitabbaṃ.</p>\r\n<p class="bodytext">tasmātiha, phagguna, tava cepi koci sammukhā tāsaṃ bhikkhunīnaṃ pāṇinā pahāraṃ dadeyya, leḍḍunā pahāraṃ dadeyya, daṇḍena pahāraṃ dadeyya, satthena pahāraṃ dadeyya. tatrāpi tvaṃ, phagguna, ye gehasitā chandā ye gehasitā vitakkā te pajaheyyāsi. tatrāpi te, phagguna, evaṃ sikkhitabbaṃ ‘na ceva me cittaṃ vipariṇataṃ bhavissati, na ca pāpikaṃ vācaṃ nicchāressāmi, hitānukampī ca viharissāmi mettacitto, na dosantaro’ti. evañhi te, phagguna, sikkhitabbaṃ.</p>\r\n<p class="bodytext">tasmātiha, phagguna, tava cepi koci sammukhā avaṇṇaṃ bhāseyya, tatrāpi tvaṃ, phagguna<a name="V1.0176"></a>, ye gehasitā chandā ye gehasitā vitakkā</p>\r\n','\r',char(13)),'\n',char(10)),'-224-');
INSERT INTO pages VALUES(3214,'mula_ma_01',175,replace(replace('<p class="bodytext">te <a name="M1.0175"></a> pajaheyyāsi. tatrāpi te, phagguna, evaṃ sikkhitabbaṃ ‘na ceva me cittaṃ vipariṇataṃ bhavissati, na ca pāpikaṃ vācaṃ nicchāressāmi, hitānukampī ca viharissāmi mettacitto, na dosantaro’ti. evañhi <a name="T1.0251"></a> te, phagguna, sikkhitabbaṃ.</p>\r\n<p class="bodytext">tasmātiha, phagguna, tava cepi koci pāṇinā pahāraṃ dadeyya, leḍḍunā pahāraṃ dadeyya, daṇḍena pahāraṃ dadeyya, satthena pahāraṃ <a name="P1.0124"></a> dadeyya, tatrāpi tvaṃ, phagguna, ye gehasitā chandā ye gehasitā vitakkā te pajaheyyāsi. tatrāpi te, phagguna, evaṃ sikkhitabbaṃ ‘na ceva me cittaṃ vipariṇataṃ bhavissati, na ca pāpikaṃ vācaṃ nicchāressāmi, hitānukampī ca viharissāmi mettacitto, na dosantaro’ti. evañhi te, phagguna, sikkhitabba"nti.</p>\r\n<p class="bodytext"><a name="para225"></a><a name="para225_mn1"></a><span class="paranum">225</span>. atha kho bhagavā bhikkhū āmantesi – "ārādhayiṃsu vata me, bhikkhave, bhikkhū ekaṃ samayaṃ cittaṃ. idhāhaṃ, bhikkhave, bhikkhū āmantesiṃ – ahaṃ kho, bhikkhave, ekāsanabhojanaṃ bhuñjāmi. ekāsanabhojanaṃ kho ahaṃ, bhikkhave, bhuñjamāno appābādhatañca sañjānāmi appātaṅkatañca lahuṭṭhānañca balañca phāsuvihārañca. etha tumhepi, bhikkhave, ekāsanabhojanaṃ bhuñjatha. ekāsanabhojanaṃ kho, bhikkhave, tumhepi bhuñjamānā appābādhatañca sañjānissatha appātaṅkatañca lahuṭṭhānañca balañca phāsuvihārañcāti. na me, bhikkhave, tesu bhikkhūsu anusāsanī karaṇīyā ahosi, satuppādakaraṇīyameva me, bhikkhave, tesu bhikkhūsu ahosi.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, subhūmiyaṃ catumahāpathe ājaññaratho yutto assa ṭhito odhastapatodo. tamenaṃ dakkho yoggācariyo assadammasārathi abhiruhitvā, vāmena hatthena rasmiyo gahetvā, dakkhiṇena hatthena patodaṃ gahetvā, yenicchakaṃ yadicchakaṃ <a name="T1.0252"></a> sāreyyapi paccāsāreyyapi. evameva kho, bhikkhave, na me tesu bhikkhūsu anusāsanī karaṇīyā ahosi, satuppādakaraṇīyameva me, bhikkhave, tesu bhikkhūsu ahosi. tasmātiha, bhikkhave, tumhepi akusalaṃ pajahatha, kusalesu dhammesu āyogaṃ karotha. evañhi tumhepi imasmiṃ dhammavinaye vuddhiṃ virūḷhiṃ vepullaṃ āpajjissatha.</p>\r\n','\r',char(13)),'\n',char(10)),'-225-');
INSERT INTO pages VALUES(3215,'mula_ma_01',176,replace(replace('<p class="bodytext">seyyathāpi<a name="M1.0176"></a><a name="V1.0177"></a>, bhikkhave, gāmassa vā nigamassa vā avidūre mahantaṃ sālavanaṃ. tañcassa eḷaṇḍehi sañchannaṃ. tassa kocideva puriso uppajjeyya atthakāmo hitakāmo yogakkhemakāmo. so yā tā sālalaṭṭhiyo kuṭilā ojāpaharaṇiyo <span class="note">[ojaharaṇiyo (ka.)]</span> tā chetvā <span class="note">[tacchetvā (sī. syā. pī.)]</span> bahiddhā nīhareyya, antovanaṃ suvisodhitaṃ visodheyya. yā pana tā sālalaṭṭhiyo ujukā sujātā tā sammā parihareyya. evañhetaṃ, bhikkhave, sālavanaṃ aparena samayena vuddhiṃ virūḷhiṃ vepullaṃ āpajjeyya. evameva kho, bhikkhave, tumhepi akusalaṃ pajahatha, kusalesu dhammesu āyogaṃ karotha. evañhi <a name="P1.0125"></a> tumhepi imasmiṃ dhammavinaye vuddhiṃ virūḷhiṃ vepullaṃ āpajjissatha.</p>\r\n<p class="bodytext"><a name="para226"></a><a name="para226_mn1"></a><span class="paranum">226</span>. bhūtapubbaṃ, bhikkhave, imissāyeva sāvatthiyā vedehikā nāma gahapatānī ahosi. vedehikāya, bhikkhave, gahapatāniyā evaṃ kalyāṇo kittisaddo abbhuggato – ‘soratā vedehikā gahapatānī, nivātā vedehikā gahapatānī, upasantā vedehikā gahapatānī’ti. vedehikāya kho pana, bhikkhave, gahapatāniyā kāḷī nāma dāsī ahosi dakkhā analasā <a name="T1.0253"></a> susaṃvihitakammantā.</p>\r\n<p class="bodytext">atha kho, bhikkhave, kāḷiyā dāsiyā etadahosi – ‘mayhaṃ kho ayyāya evaṃ kalyāṇo kittisaddo abbhuggato – "soratā vedehikā gahapatānī, nivātā vedehikā gahapatānī, upasantā vedehikā gahapatānī"ti. kiṃ nu kho me ayyā santaṃyeva nu kho ajjhattaṃ kopaṃ na pātukaroti udāhu asantaṃ udāhu mayhamevete <span class="note">[mayhevete (sī. pī.)]</span> kammantā susaṃvihitā yena me ayyā santaṃyeva ajjhattaṃ kopaṃ na pātukaroti, no asantaṃ? yaṃnūnāhaṃ ayyaṃ vīmaṃseyya’nti. atha kho, bhikkhave, kāḷī dāsī divā uṭṭhāsi. atha kho, bhikkhave, vedehikā gahapatānī kāḷiṃ dāsiṃ etadavoca – ‘he je kāḷī’ti. ‘kiṃ, ayye’ti? ‘kiṃ, je, divā uṭṭhāsī’ti? ‘na khvayye <span class="note">[na kho ayye (sī. pī.)]</span>, kiñcī’ti. ‘no vata re kiñci, pāpi dāsi <span class="note">[pāpadāsi (syā. ka.)]</span>, divā uṭṭhāsī’ti kupitā anattamanā bhākuṭiṃ <span class="note">[bhūkuṭiṃ (sī. pī.), bhakuṭīṃ (syā.)]</span> akāsi. atha kho, bhikkhave, kāḷiyā</p>\r\n','\r',char(13)),'\n',char(10)),'-226-');
INSERT INTO pages VALUES(3216,'mula_ma_01',177,replace(replace('<p class="bodytext">dāsiyā <a name="M1.0177"></a> etadahosi – ‘santaṃyeva kho me ayyā ajjhattaṃ kopaṃ na pātukaroti, no asantaṃ, mayhamevete kammantā susaṃvihitā, yena me ayyā santaṃyeva ajjhattaṃ kopaṃ na pātukaroti, no asantaṃ. yaṃnūnāhaṃ bhiyyosomattāya ayyaṃ vīmaṃseyya"’nti.</p>\r\n<p class="bodytext">atha <a name="V1.0178"></a> kho, bhikkhave, kāḷī dāsī divātaraṃyeva uṭṭhāsi. atha kho, bhikkhave, vedehikā gahapatānī kāḷiṃ dāsiṃ etadavoca – ‘he je, kāḷī’ti. ‘kiṃ, ayye’ti? ‘kiṃ, je, divātaraṃ uṭṭhāsī’ti? ‘na khvayye, kiñcī’ti. ‘no vata re kiñci, pāpi dāsi, divātaraṃ <a name="T1.0254"></a> uṭṭhāsī’ti kupitā anattamanā anattamanavācaṃ nicchāresi. atha kho, bhikkhave, kāḷiyā dāsiyā etadahosi – ‘santaṃyeva kho me ayyā ajjhattaṃ kopaṃ na pātukaroti, no asantaṃ. mayhamevete kammantā susaṃvihitā, yena me ayyā santaṃyeva ajjhattaṃ kopaṃ na pātukaroti, no asantaṃ. yaṃnūnāhaṃ bhiyyosomattāya ayyaṃ vīmaṃseyya’nti.</p>\r\n<p class="bodytext">atha kho, bhikkhave, kāḷī dāsī divātaraṃyeva uṭṭhāsi. atha kho, bhikkhave, vedehikā gahapatānī kāḷiṃ dāsiṃ etadavoca <a name="P1.0126"></a> – ‘he je, kāḷī’ti. ‘kiṃ, ayye’ti? ‘kiṃ, je, divā uṭṭhāsī’ti? ‘na khvayye, kiñcī’ti. ‘no vata re kiñci, pāpi dāsi, divā uṭṭhāsī’ti kupitā anattamanā aggaḷasūciṃ gahetvā sīse pahāraṃ adāsi, sīsaṃ vobhindi <span class="note">[vi + ava + bhindi = vobhindi]</span>. atha kho, bhikkhave, kāḷī dāsī bhinnena sīsena lohitena galantena paṭivissakānaṃ ujjhāpesi – ‘passathayye, soratāya kammaṃ, passathayye, nivātāya kammaṃ, passathayye, upasantāya kammaṃ! kathañhi nāma ekadāsikāya divā uṭṭhāsīti kupitā anattamanā aggaḷasūciṃ gahetvā sīse pahāraṃ dassati, sīsaṃ vobhindissatī’ti.</p>\r\n<p class="bodytext">atha kho, bhikkhave, vedehikāya gahapatāniyā aparena samayena evaṃ pāpako kittisaddo abbhuggacchi – ‘caṇḍī vedehikā gahapatānī, anivātā vedehikā gahapatānī, anupasantā vedehikā gahapatānī’ti.</p>\r\n<p class="bodytext">evameva kho, bhikkhave, idhekacco bhikkhu tāvadeva soratasorato hoti nivātanivāto <a name="T1.0255"></a> hoti upasantūpasanto hoti yāva na amanāpā vacanapathā phusanti. yato ca, bhikkhave,</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3217,'mula_ma_01',178,replace(replace('<p class="bodytext">bhikkhuṃ <a name="M1.0178"></a> amanāpā vacanapathā phusanti, atha bhikkhu ‘sorato’ti veditabbo, ‘nivāto’ti veditabbo, ‘upasanto’ti veditabbo. nāhaṃ taṃ, bhikkhave, bhikkhuṃ ‘suvaco’ti vadāmi yo cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārahetu suvaco hoti, sovacassataṃ āpajjati. taṃ kissa hetu? tañhi so, bhikkhave, bhikkhu cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhāraṃ <a name="V1.0179"></a> alabhamāno na suvaco hoti, na sovacassataṃ āpajjati. yo ca kho, bhikkhave, bhikkhu dhammaṃyeva sakkaronto, dhammaṃ garuṃ karonto, dhammaṃ mānento, dhammaṃ pūjento, dhammaṃ apacāyamāno <span class="note">[dhammaṃ yeva sakkaronto dhammaṃ garukaronto dhammaṃ apacāyamāno (sī. syā. pī.)]</span> suvaco hoti, sovacassataṃ āpajjati, tamahaṃ ‘suvaco’ti vadāmi. tasmātiha, bhikkhave, ‘dhammaṃyeva sakkarontā, dhammaṃ garuṃ karontā, dhammaṃ mānentā, dhammaṃ pūjentā, dhammaṃ apacāyamānā suvacā bhavissāma, sovacassataṃ āpajjissāmā’ti. evañhi vo, bhikkhave, sikkhitabbaṃ.</p>\r\n<p class="bodytext"><a name="para227"></a><a name="para227_mn1"></a><span class="paranum">227</span>. pañcime, bhikkhave, vacanapathā yehi vo pare vadamānā vadeyyuṃ – kālena vā akālena vā, bhūtena vā abhūtena vā, saṇhena vā pharusena vā, atthasaṃhitena vā anatthasaṃhitena vā, mettacittā vā dosantarā vā. kālena vā, bhikkhave, pare vadamānā vadeyyuṃ akālena vā, bhūtena vā, bhikkhave, pare vadamānā vadeyyuṃ abhūtena vā, saṇhena vā, bhikkhave, pare vadamānā vadeyyuṃ pharusena vā, atthasaṃhitena vā, bhikkhave, pare vadamānā vadeyyuṃ anatthasaṃhitena <a name="P1.0127"></a> vā<a name="T1.0256"></a>, mettacittā vā, bhikkhave, pare vadamānā vadeyyuṃ dosantarā vā. tatrāpi vo, bhikkhave, evaṃ sikkhitabbaṃ – ‘na ceva no cittaṃ vipariṇataṃ bhavissati, na ca pāpikaṃ vācaṃ nicchāressāma, hitānukampī ca viharissāma mettacittā, na dosantarā. tañca puggalaṃ mettāsahagatena cetasā pharitvā viharissāma, tadārammaṇañca sabbāvantaṃ lokaṃ mettāsahagatena cittena vipulena mahaggatena appamāṇena averena abyābajjhena <span class="note">[abyāpajjhena (sī. syā. pī.), abyāpajjena (ka.) aṅguttaratikanipātaṭīkā oloketabbā]</span> pharitvā viharissāmā’ti. evañhi vo, bhikkhave, sikkhitabbaṃ.</p>\r\n<p class="bodytext"><a name="para228"></a><a name="para228_mn1"></a><span class="paranum">228</span>. seyyathāpi, bhikkhave, puriso āgaccheyya kudālapiṭakaṃ <span class="note">[kuddālapiṭakaṃ (sī. syā. pī.)]</span> ādāya. so evaṃ vadeyya – ‘ahaṃ imaṃ mahāpathaviṃ apathaviṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-227-228-');
INSERT INTO pages VALUES(3218,'mula_ma_01',179,replace(replace('<p class="bodytext">karissāmī’ti<a name="M1.0179"></a>. so tatra tatra vikhaṇeyya <span class="note">[khaṇeyya (sī. syā. pī.)]</span>, tatra tatra vikireyya, tatra tatra oṭṭhubheyya, tatra tatra omutteyya – ‘apathavī bhavasi, apathavī bhavasī’ti. taṃ kiṃ maññatha, bhikkhave, api nu so puriso imaṃ mahāpathaviṃ apathaviṃ kareyyā"ti? "no hetaṃ, bhante". "taṃ kissa hetu"? "ayañhi, bhante, mahāpathavī gambhīrā appameyyā. sā na sukarā apathavī kātuṃ, yāvadeva ca pana so puriso kilamathassa vighātassa bhāgī assā"ti. "evameva kho, bhikkhave, pañcime vacanapathā yehi vo pare vadamānā vadeyyuṃ – kālena vā akālena vā, bhūtena vā abhūtena vā, saṇhena vā pharusena vā, atthasaṃhitena vā anatthasaṃhitena vā, mettacittā vā dosantarā vā. kālena vā<a name="V1.0180"></a>, bhikkhave, pare vadamānā vadeyyuṃ akālena <a name="T1.0257"></a> vā, bhūtena vā bhikkhave, pare vadamānā vadeyyuṃ abhūtena vā, saṇhena vā, bhikkhave, pare vadamānā vadeyyuṃ pharusena vā, atthasaṃhitena vā, bhikkhave, pare vadamānā vadeyyuṃ anatthasaṃhitena vā, mettacittā vā, bhikkhave, pare vadamānā vadeyyuṃ dosantarā vā. tatrāpi vo, bhikkhave, evaṃ sikkhitabbaṃ – ‘na ceva no cittaṃ vipariṇataṃ bhavissati, na ca pāpikaṃ vācaṃ nicchāressāma, hitānukampī ca viharissāma mettacittā na dosantarā. tañca puggalaṃ mettāsahagatena cetasā pharitvā viharissāma, tadārammaṇañca sabbāvantaṃ lokaṃ pathavisamena cetasā vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā viharissāmā’ti. evañhi vo, bhikkhave, sikkhitabbaṃ.</p>\r\n<p class="bodytext"><a name="para229"></a><a name="para229_mn1"></a><span class="paranum">229</span>. seyyathāpi, bhikkhave, puriso āgaccheyya lākhaṃ vā haliddiṃ vā nīlaṃ vā mañjiṭṭhaṃ vā ādāya. so evaṃ vadeyya – ‘ahaṃ imasmiṃ ākāse rūpaṃ likhissāmi, rūpapātubhāvaṃ karissāmī’ti. taṃ kiṃ maññatha, bhikkhave, api nu so puriso imasmiṃ ākāse rūpaṃ likheyya, rūpapātubhāvaṃ kareyyā"ti? "no hetaṃ, bhante". "taṃ kissa hetu"? "ayañhi, bhante, ākāso arūpī anidassano. tattha na sukaraṃ rūpaṃ likhituṃ, rūpapātubhāvaṃ kātuṃ, yāvadeva ca pana <a name="P1.0128"></a> so puriso kilamathassa vighātassa bhāgī assā"ti. "evameva kho, bhikkhave, pañcime vacanapathā yehi vo pare vadamānā vadeyyuṃ kālena vā akālena vā <a name="T1.0258"></a> …pe… ‘na ceva…</p>\r\n','\r',char(13)),'\n',char(10)),'-229-');
INSERT INTO pages VALUES(3219,'mula_ma_01',180,replace(replace('<p class="bodytext">tadārammaṇañca <a name="M1.0180"></a> sabbāvantaṃ lokaṃ ākāsasamena cetasā vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā viharissāmā’ti. evañhi vo, bhikkhave, sikkhitabbaṃ.</p>\r\n<p class="bodytext"><a name="para230"></a><a name="para230_mn1"></a><span class="paranum">230</span>. seyyathāpi, bhikkhave, puriso āgaccheyya ādittaṃ tiṇukkaṃ ādāya. so evaṃ vadeyya – ‘ahaṃ imāya ādittāya tiṇukkāya gaṅgaṃ nadiṃ santāpessāmi saṃparitāpessāmī’ti. taṃ kiṃ maññatha, bhikkhave, api nu so puriso ādittāya tiṇukkāya gaṅgaṃ nadiṃ santāpeyya saṃparitāpeyyā"ti? "no hetaṃ, bhante". "taṃ kissa hetu"? "gaṅgā hi, bhante, nadī gambhīrā appameyyā. sā na sukarā ādittāya tiṇukkāya santāpetuṃ saṃparitāpetuṃ, yāvadeva ca pana so puriso kilamathassa vighātassa bhāgī assā"ti. "evameva kho, bhikkhave, pañcime vacanapathā yehi vo pare vadamānā vadeyyuṃ kālena vā akālena <a name="V1.0181"></a> vā…pe… ‘na ceva… tadārammaṇañca sabbāvantaṃ lokaṃ gaṅgāsamena cetasā vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā viharissāmā"ti. evañhi vo, bhikkhave, sikkhitabbaṃ.</p>\r\n<p class="bodytext"><a name="para231"></a><a name="para231_mn1"></a><span class="paranum">231</span>. seyyathāpi, bhikkhave, biḷārabhastā madditā sumadditā suparimadditā, mudukā tūlinī chinnasassarā chinnabhabbharā. atha puriso āgaccheyya kaṭṭhaṃ vā kathalaṃ <span class="note">[kaṭhalaṃ (sī. syā. pī.)]</span> vā ādāya. so evaṃ vadeyya – ‘ahaṃ imaṃ biḷārabhastaṃ madditaṃ sumadditaṃ suparimadditaṃ, mudukaṃ tūliniṃ, chinnasassaraṃ chinnabhabbharaṃ kaṭṭhena vā kathalena vā sarasaraṃ karissāmi bharabharaṃ karissāmī’ti<a name="T1.0259"></a>. taṃ kiṃ maññatha, bhikkhave, api nu so puriso amuṃ biḷārabhastaṃ madditaṃ sumadditaṃ suparimadditaṃ, mudukaṃ tūliniṃ, chinnasassaraṃ chinnabhabbharaṃ kaṭṭhena vā kathalena vā sarasaraṃ kareyya, bharabharaṃ kareyyā"ti? "no hetaṃ, bhante". "taṃ kissa hetu"? "amu hi, bhante, biḷārabhastā madditā sumadditā suparimadditā, mudukā tūlinī, chinnasassarā chinnabhabbharā. sā na sukarā kaṭṭhena vā kathalena vā sarasaraṃ kātuṃ bharabharaṃ kātuṃ, yāvadeva ca pana so puriso kilamathassa vighātassa bhāgī assā"ti. "evameva kho, bhikkhave, pañcime vacanapathā yehi vo pare vadamānā vadeyyuṃ kālena vā <a name="P1.0129"></a> akālena vā, bhūtena vā abhūtena vā,</p>\r\n','\r',char(13)),'\n',char(10)),'-230-231-');
INSERT INTO pages VALUES(3220,'mula_ma_01',181,replace(replace('<p class="bodytext">saṇhena <a name="M1.0181"></a> vā pharusena vā, atthasaṃhitena vā anatthasaṃhitena vā, mettacittā vā dosantarā vā. kālena vā bhikkhave pare vadamānā vadeyyuṃ akālena vā, bhūtena vā, bhikkhave, pare vadamānā vadeyyuṃ abhūtena vā, saṇhena vā, bhikkhave, pare vadamānā vadeyyuṃ pharusena vā, atthasaṃhitena vā, bhikkhave, pare vadamānā vadeyyuṃ anatthasaṃhitena vā, mettacittā vā, bhikkhave, pare vadamānā vadeyyuṃ dosantarā vā. tatrāpi vo, bhikkhave, evaṃ sikkhitabbaṃ – ‘na ceva no cittaṃ vipariṇataṃ bhavissati, na ca pāpikaṃ vācaṃ nicchāressāma hitānukampī ca viharissāma mettacittā na dosantarā. tañca puggalaṃ mettāsahagatena cetasā pharitvā viharissāma, tadārammaṇañca sabbāvantaṃ lokaṃ biḷārabhastāsamena cetasā <a name="T1.0260"></a> vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā viharissāmā’ti. evañhi vo, bhikkhave, sikkhitabbaṃ.</p>\r\n<p class="bodytext"><a name="para232"></a><a name="para232_mn1"></a><span class="paranum">232</span>. ubhatodaṇḍakena cepi, bhikkhave, kakacena corā ocarakā aṅgamaṅgāni okanteyyuṃ, tatrāpi yo mano padūseyya, na me so tena sāsanakaro. tatrāpi vo, bhikkhave<a name="V1.0182"></a>, evaṃ sikkhitabbaṃ – ‘na ceva no cittaṃ vipariṇataṃ bhavissati, na ca pāpikaṃ vācaṃ nicchāressāma, hitānukampī ca viharissāma mettacittā na dosantarā. tañca puggalaṃ mettāsahagatena cetasā pharitvā viharissāma tadārammaṇañca sabbāvantaṃ lokaṃ mettāsahagatena cetasā vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā viharissāmā’ti. evañhi vo, bhikkhave, sikkhitabbaṃ.</p>\r\n<p class="bodytext"><a name="para233"></a><a name="para233_mn1"></a><span class="paranum">233</span>. imañca <span class="note">[imañce (?)]</span> tumhe, bhikkhave, kakacūpamaṃ ovādaṃ abhikkhaṇaṃ manasi kareyyātha. passatha no tumhe, bhikkhave, taṃ vacanapathaṃ, aṇuṃ vā thūlaṃ vā, yaṃ tumhe nādhivāseyyāthā"ti? "no hetaṃ, bhante". "tasmātiha, bhikkhave, imaṃ kakacūpamaṃ ovādaṃ abhikkhaṇaṃ manasikarotha. taṃ vo bhavissati dīgharattaṃ hitāya sukhāyā"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">kakacūpamasuttaṃ niṭṭhitaṃ paṭhamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-232-233-');
INSERT INTO pages VALUES(3221,'mula_ma_01',182,replace(replace('<p class="subhead">2. alagaddūpamasuttaṃ</p>\r\n<p class="bodytext"><a name="para234"></a><a name="para234_mn1"></a><span class="paranum">234</span>. evaṃ <a name="T1.0261"></a><a name="M1.0182"></a><a name="V1.0183"></a><a name="P1.0130"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tena kho pana samayena ariṭṭhassa nāma bhikkhuno gaddhabādhipubbassa <span class="note">[gandhabādhipubbassa (ka.)]</span> evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ hoti – "tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā"ti. assosuṃ kho sambahulā bhikkhū – "ariṭṭhassa kira nāma bhikkhuno gaddhabādhipubbassa evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ – ‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā"’ti. atha kho te bhikkhū yena ariṭṭho bhikkhu gaddhabādhipubbo tenupasaṅkamiṃsu, upasaṅkamitvā ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ etadavocuṃ – "saccaṃ kira te, āvuso ariṭṭha, evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ – ‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā"’ti. "evaṃbyākho <span class="note">[evaṃ kho (?) bhagavato sammukhāyevassa "evaṃbyākho"ti]</span> ahaṃ, āvuso, bhagavatā dhammaṃ desitaṃ ājānāmi yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā"ti.</p>\r\n<p class="bodytext">atha kho tepi bhikkhū ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ etasmā <a name="T1.0262"></a> pāpakā diṭṭhigatā vivecetukāmā samanuyuñjanti samanugāhanti <span class="note">[samanuggāhanti (syā.)]</span> samanubhāsanti – "mā hevaṃ, āvuso ariṭṭha, avaca, mā bhagavantaṃ abbhācikkhi, na hi sādhu bhagavato abbhakkhānaṃ <span class="note">[abbhācikkhanaṃ (ka.)]</span>, na hi bhagavā evaṃ vadeyya. anekapariyāyenāvuso ariṭṭha, antarāyikā dhammā antarāyikā vuttā bhagavatā, alañca pana te paṭisevato antarāyāya. appassādā kāmā vuttā bhagavatā bahudukkhā bahupāyāsā,ādīnavo ettha bhiyyo. aṭṭhikaṅkalūpamā kāmā vuttā bhagavatā…pe… maṃsapesūpamā kāmā vuttā bhagavatā… tiṇukkūpamā kāmā vuttā bhagavatā… aṅgārakāsūpamā kāmā vuttā bhagavatā… supinakūpamā kāmā vuttā bhagavatā… yācitakūpamā kāmā vuttā bhagavatā… rukkhaphalūpamā kāmā vuttā bhagavatā… asisūnūpamā kāmā vuttā bhagavatā… sattisūlūpamā kāmā vuttā bhagavatā… sappasirūpamā kāmā</p>\r\n','\r',char(13)),'\n',char(10)),'-234-');
INSERT INTO pages VALUES(3222,'mula_ma_01',183,replace(replace('<p class="bodytext">vuttā <a name="M1.0183"></a> bhagavatā bahudukkhā bahupāyāsā, ādīnavo ettha bhiyyo"ti. evampi kho ariṭṭho bhikkhu gaddhabādhipubbo <a name="V1.0184"></a> tehi bhikkhūhi samanuyuñjiyamāno samanugāhiyamāno <span class="note">[samanuggāhiyamāno (syā. vinayepi)]</span> samanubhāsiyamāno tadeva <span class="note">[tatheva taṃ (vinaye)]</span> pāpakaṃ diṭṭhigataṃ thāmasā parāmāsā abhinivissa voharati – "evaṃbyākho ahaṃ, āvuso, bhagavatā dhammaṃ desitaṃ ājānāmi yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā"ti.</p>\r\n<p class="bodytext"><a name="para235"></a><a name="para235_mn1"></a><span class="paranum">235</span>. yato kho te bhikkhū nāsakkhiṃsu ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ <a name="T1.0263"></a><a name="P1.0131"></a> etasmā pāpakā diṭṭhigatā vivecetuṃ, atha kho te bhikkhū yena bhagavā tenupasaṅkamiṃsu, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu. ekamantaṃ nisinnā kho te bhikkhū bhagavantaṃ etadavocuṃ – "ariṭṭhassa nāma, bhante, bhikkhuno gaddhabādhipubbassa evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ – ‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā’ti. assumha kho mayaṃ, bhante – ‘ariṭṭhassa kira nāma bhikkhuno gaddhabādhipubbassa evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ – tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā’ti. atha kho mayaṃ, bhante, yena ariṭṭho bhikkhu gaddhabādhipubbo tenupasaṅkamimha, upasaṅkamitvā ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ etadavocumha – ‘saccaṃ kira te, āvuso ariṭṭha, evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ – tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā’ti?</p>\r\n<p class="bodytext">evaṃ vutte, bhante, ariṭṭho bhikkhu gaddhabādhipubbo amhe etadavoca – ‘evaṃbyākho ahaṃ, āvuso, bhagavatā dhammaṃ desitaṃ ājānāmi yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā’ti. atha kho mayaṃ, bhante, ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ etasmā pāpakā diṭṭhigatā vivecetukāmā samanuyuñjimha samanugāhimha samanubhāsimha <a name="T1.0264"></a> – ‘mā hevaṃ, āvuso ariṭṭha, avaca, mā bhagavantaṃ abbhācikkhi, na hi sādhu bhagavato</p>\r\n','\r',char(13)),'\n',char(10)),'-235-');
INSERT INTO pages VALUES(3223,'mula_ma_01',184,replace(replace('<p class="bodytext">abbhakkhānaṃ<a name="M1.0184"></a>, na hi bhagavā evaṃ vadeyya. anekapariyāyenāvuso ariṭṭha, antarāyikā dhammā antarāyikā vuttā bhagavatā, alañca pana te paṭisevato antarāyāya. appassādā kāmā vuttā bhagavatā bahudukkhā bahupāyāsā, ādīnavo ettha bhiyyo. aṭṭhikaṅkalūpamā kāmā vuttā bhagavatā…pe… sappasirūpamā kāmā vuttā bhagavatā bahudukkhā bahupāyāsā, ādīnavo ettha bhiyyo’ti. evampi kho, bhante, ariṭṭho bhikkhu gaddhabādhipubbo amhehi samanuyuñjiyamāno samanugāhiyamāno <a name="V1.0185"></a> samanubhāsiyamāno tadeva pāpakaṃ diṭṭhigataṃ thāmasā parāmāsā abhinivissa voharati – ‘evaṃbyākho ahaṃ, āvuso, bhagavatā dhammaṃ desitaṃ ājānāmi yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā’ti. yato kho mayaṃ, bhante, nāsakkhimha ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ etasmā pāpakā diṭṭhigatā vivecetuṃ, atha mayaṃ etamatthaṃ bhagavato ārocemā"ti.</p>\r\n<p class="bodytext"><a name="para236"></a><a name="para236_mn1"></a><span class="paranum">236</span>. atha kho bhagavā aññataraṃ bhikkhuṃ āmantesi – "ehi tvaṃ, bhikkhu, mama vacanena ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ āmantehi – ‘satthā taṃ, āvuso ariṭṭha, āmantetī"’ti. "evaṃ<a name="P1.0132"></a>, bhante"ti kho so bhikkhu bhagavato paṭissutvā, yena ariṭṭho bhikkhu gaddhabādhipubbo tenupasaṅkami, upasaṅkamitvā ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ etadavoca – "satthā <a name="T1.0265"></a> taṃ, āvuso ariṭṭha, āmantetī"ti. "evamāvuso"ti kho ariṭṭho bhikkhu gaddhabādhipubbo tassa bhikkhuno paṭissutvā yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinnaṃ kho ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ bhagavā etadavoca – "saccaṃ kira te, ariṭṭha, evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ – ‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā"’ti?</p>\r\n<p class="bodytext">evaṃbyākho ahaṃ, bhante, bhagavatā dhammaṃ desitaṃ ājānāmi – ‘yathā yeme antarāyikā dhammā vuttā bhagavatā te paṭisevato nālaṃ antarāyāyā"’ti. "kassa kho nāma tvaṃ, moghapurisa, mayā evaṃ dhammaṃ desitaṃ ājānāsi? nanu mayā, moghapurisa, anekapariyāyena antarāyikā dhammā antarāyikā vuttā? alañca pana te paṭisevato antarāyāya. appassādā kāmā vuttā mayā, bahudukkhā bahupāyāsā, ādīnavo ettha bhiyyo. aṭṭhikaṅkalūpamā kāmā vuttā mayā…</p>\r\n','\r',char(13)),'\n',char(10)),'-236-');
INSERT INTO pages VALUES(3224,'mula_ma_01',185,replace(replace('<p class="bodytext">maṃsapesūpamā <a name="M1.0185"></a> kāmā vuttā mayā… tiṇukkūpamā kāmā vuttā mayā… aṅgārakāsūpamā kāmā vuttā mayā… supinakūpamā kāmā vuttā mayā… yācitakūpamā kāmā vuttā mayā… rukkhaphalūpamā kāmā vuttā mayā… asisūnūpamā kāmā vuttā mayā… sattisūlūpamā kāmā vuttā mayā… sappasirūpamā kāmā vuttā mayā, bahudukkhā bahupāyāsā, ādīnavo ettha bhiyyo. atha ca pana tvaṃ, moghapurisa, attanā duggahitena <a name="T1.0266"></a> amhe ceva abbhācikkhasi, attānañca khanasi, bahuñca apuññaṃ pasavasi. tañhi te, moghapurisa, bhavissati dīgharattaṃ ahitāya dukkhāyā"ti.</p>\r\n<p class="bodytext">atha <a name="V1.0186"></a> kho bhagavā bhikkhū āmantesi – "taṃ kiṃ maññatha, bhikkhave, api nāyaṃ ariṭṭho bhikkhu gaddhabādhipubbo usmīkatopi imasmiṃ dhammavinaye"ti? "kiñhi <span class="note">[kiṃti (ka.)]</span> siyā, bhante, no hetaṃ, bhante"ti. evaṃ vutte, ariṭṭho bhikkhu gaddhabādhipubbo tuṇhībhūto maṅkubhūto pattakkhandho adhomukho pajjhāyanto appaṭibhāno nisīdi. atha kho bhagavā ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ tuṇhībhūtaṃ maṅkubhūtaṃ pattakkhandhaṃ adhomukhaṃ pajjhāyantaṃ appaṭibhānaṃ viditvā ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ etadavoca – "paññāyissasi kho tvaṃ, moghapurisa, etena sakena pāpakena diṭṭhigatena. idhāhaṃ bhikkhū paṭipucchissāmī"ti.</p>\r\n<p class="bodytext"><a name="para237"></a><a name="para237_mn1"></a><span class="paranum">237</span>. atha kho bhagavā bhikkhū āmantesi – "tumhepi me, bhikkhave<a name="P1.0133"></a>, evaṃ dhammaṃ desitaṃ ājānātha yathāyaṃ ariṭṭho bhikkhu gaddhabādhipubbo attanā duggahitena amhe ceva abbhācikkhati, attānañca khanati, bahuñca apuññaṃ pasavatī"ti? "no hetaṃ, bhante. anekapariyāyena hi no, bhante, antarāyikā dhammā antarāyikā vuttā bhagavatā, alañca pana te paṭisevato antarāyāya. appassādā kāmā vuttā bhagavatā bahudukkhā bahupāyāsā, ādīnavo ettha bhiyyo. aṭṭhikaṅkalūpamā kāmā vuttā bhagavatā…pe… sappasirūpamā kāmā vuttā bhagavatā bahudukkhā bahupāyāsā<a name="T1.0267"></a>, ādīnavo ettha bhiyyo"ti. "sādhu sādhu, bhikkhave, sādhu, kho me tumhe, bhikkhave, evaṃ dhammaṃ desitaṃ ājānātha. anekapariyāyena hi kho, bhikkhave, antarāyikā dhammā vuttā mayā, alañca pana te paṭisevato antarāyāya. appassādā kāmā vuttā</p>\r\n','\r',char(13)),'\n',char(10)),'-237-');
INSERT INTO pages VALUES(3225,'mula_ma_01',186,replace(replace('<p class="bodytext">mayā<a name="M1.0186"></a>, bahudukkhā bahupāyāsā, ādīnavo ettha bhiyyo. aṭṭhikaṅkalūpamā kāmā vuttā mayā…pe… sappasirūpamā kāmā vuttā mayā, bahudukkhā bahupāyāsā, ādīnavo ettha bhiyyo. atha ca panāyaṃ ariṭṭho bhikkhu gaddhabādhipubbo attanā duggahitena amhe ceva abbhācikkhati, attānañca khanati, bahuñca apuññaṃ pasavati. tañhi tassa moghapurisassa bhavissati dīgharattaṃ ahitāya dukkhāya. so vata, bhikkhave, aññatreva kāmehi aññatra kāmasaññāya aññatra kāmavitakkehi kāme paṭisevissatīti – netaṃ ṭhānaṃ vijjati".</p>\r\n<p class="bodytext"><a name="para238"></a><a name="para238_mn1"></a><span class="paranum">238</span>. idha, bhikkhave, ekacce moghapurisā dhammaṃ pariyāpuṇanti – suttaṃ, geyyaṃ, veyyākaraṇaṃ, gāthaṃ, udānaṃ, itivuttakaṃ, jātakaṃ, abbhutadhammaṃ, vedallaṃ. te taṃ dhammaṃ pariyāpuṇitvā tesaṃ dhammānaṃ paññāya atthaṃ na upaparikkhanti. tesaṃ te dhammā paññāya atthaṃ anupaparikkhataṃ <a name="V1.0187"></a> na nijjhānaṃ khamanti. te upārambhānisaṃsā ceva dhammaṃ pariyāpuṇanti itivādappamokkhānisaṃsā ca. yassa catthāya dhammaṃ pariyāpuṇanti tañcassa atthaṃ <a name="T1.0268"></a> nānubhonti. tesaṃ te dhammā duggahitā dīgharattaṃ ahitāya dukkhāya saṃvattanti. taṃ kissa hetu? duggahitattā, bhikkhave, dhammānaṃ.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, puriso alagaddatthiko alagaddagavesī alagaddapariyesanaṃ caramāno. so passeyya mahantaṃ alagaddaṃ. tamenaṃ bhoge vā naṅguṭṭhe vā gaṇheyya. tassa so alagaddo paṭiparivattitvā <span class="note">[paṭinivattitvā (syā. ka.)]</span> hatthe vā bāhāya vā aññatarasmiṃ vā aṅgapaccaṅge ḍaṃseyya <span class="note">[ḍaseyya (sī. pī.)]</span>. so tatonidānaṃ <a name="P1.0134"></a> maraṇaṃ vā nigaccheyya maraṇamattaṃ vā dukkhaṃ. taṃ kissa hetu? duggahitattā, bhikkhave, alagaddassa. evameva kho, bhikkhave, idhekacce moghapurisā dhammaṃ pariyāpuṇanti – suttaṃ, geyyaṃ, veyyākaraṇaṃ, gāthaṃ, udānaṃ, itivuttakaṃ, jātakaṃ, abbhutadhammaṃ, vedallaṃ. te taṃ dhammaṃ pariyāpuṇitvā tesaṃ dhammānaṃ paññāya atthaṃ na upaparikkhanti. tesaṃ te dhammā paññāya atthaṃ anupaparikkhataṃ na nijjhānaṃ khamanti. te upārambhānisaṃsā ceva dhammaṃ pariyāpuṇanti itivādappamokkhānisaṃsā ca. yassa catthāya dhammaṃ pariyāpuṇanti tañcassa atthaṃ nānubhonti. tesaṃ te dhammā duggahitā dīgharattaṃ ahitāya dukkhāya saṃvattanti. taṃ kissa hetu? duggahitattā bhikkhave dhammānaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-238-');
INSERT INTO pages VALUES(3226,'mula_ma_01',187,replace(replace('<p class="bodytext"><a name="para239"></a><a name="para239_mn1"></a><span class="paranum">239</span>. idha <a name="M1.0187"></a> pana, bhikkhave, ekacce kulaputtā dhammaṃ pariyāpuṇanti – suttaṃ, geyyaṃ, veyyākaraṇaṃ, gāthaṃ, udānaṃ, itivuttakaṃ, jātakaṃ, abbhutadhammaṃ, vedallaṃ. te taṃ dhammaṃ pariyāpuṇitvā tesaṃ dhammānaṃ paññāya atthaṃ upaparikkhanti. tesaṃ te dhammā paññāya atthaṃ upaparikkhataṃ nijjhānaṃ <a name="T1.0269"></a> khamanti. te na ceva upārambhānisaṃsā dhammaṃ pariyāpuṇanti na itivādappamokkhānisaṃsā ca <span class="note">[na ca itivādappamokkhānisaṃsā (?)]</span>. yassa catthāya dhammaṃ pariyāpuṇanti tañcassa atthaṃ anubhonti. tesaṃ te dhammā suggahitā dīgharattaṃ hitāya sukhāya saṃvattanti. taṃ kissa hetu? suggahitattā bhikkhave dhammānaṃ.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, puriso alagaddatthiko alagaddagavesī alagaddapariyesanaṃ caramāno. so passeyya mahantaṃ alagaddaṃ. tamenaṃ ajapadena daṇḍena suniggahitaṃ niggaṇheyya. ajapadena daṇḍena suniggahitaṃ niggahitvā, gīvāya suggahitaṃ gaṇheyya. kiñcāpi so, bhikkhave<a name="V1.0188"></a>, alagaddo tassa purisassa hatthaṃ vā bāhaṃ vā aññataraṃ vā aṅgapaccaṅgaṃ bhogehi paliveṭheyya, atha kho so neva tatonidānaṃ maraṇaṃ vā nigaccheyya maraṇamattaṃ vā dukkhaṃ. taṃ kissa hetu? suggahitattā, bhikkhave, alagaddassa. evameva kho, bhikkhave, idhekacce kulaputtā dhammaṃ pariyāpuṇanti – suttaṃ, geyyaṃ, veyyākaraṇaṃ, gāthaṃ, udānaṃ, itivuttakaṃ, jātakaṃ, abbhutadhammaṃ, vedallaṃ. te taṃ dhammaṃ pariyāpuṇitvā tesaṃ dhammānaṃ paññāya atthaṃ upaparikkhanti. tesaṃ te dhammā paññāya atthaṃ upaparikkhataṃ nijjhānaṃ khamanti. te na ceva upārambhānisaṃsā dhammaṃ pariyāpuṇanti, na itivādappamokkhānisaṃsā ca. yassa catthāya dhammaṃ pariyāpuṇanti, tañcassa atthaṃ anubhonti. tesaṃ te dhammā suggahitā dīgharattaṃ atthāya hitāya sukhāya saṃvattanti. taṃ kissa hetu? suggahitattā, bhikkhave, dhammānaṃ. tasmātiha, bhikkhave, yassa <a name="T1.0270"></a> me bhāsitassa atthaṃ ājāneyyātha, tathā naṃ dhāreyyātha. yassa ca pana me bhāsitassa atthaṃ na ājāneyyātha, ahaṃ vo tattha paṭipucchitabbo, ye vā panāssu viyattā bhikkhū.</p>\r\n<p class="bodytext"><a name="para240"></a><a name="para240_mn1"></a><span class="paranum">240</span>. kullūpamaṃ vo, bhikkhave, dhammaṃ desessāmi nittharaṇatthāya, no gahaṇatthāya. taṃ suṇātha, sādhukaṃ manasikarotha, bhāsissāmī"ti. "evaṃ, bhante"ti kho te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n','\r',char(13)),'\n',char(10)),'-239-240-');
INSERT INTO pages VALUES(3227,'mula_ma_01',188,replace(replace('<p class="bodytext">seyyathāpi<a name="M1.0188"></a>, bhikkhave, puriso addhānamaggappaṭipanno. so passeyya mahantaṃ udakaṇṇavaṃ, orimaṃ tīraṃ sāsaṅkaṃ sappaṭibhayaṃ, pārimaṃ tīraṃ khemaṃ appaṭibhayaṃ, na cassa nāvā santāraṇī uttarasetu vā apārā pāraṃ gamanāya. tassa evamassa <a name="P1.0135"></a> – ‘ayaṃ kho mahāudakaṇṇavo, orimaṃ tīraṃ sāsaṅkaṃ sappaṭibhayaṃ, pārimaṃ tīraṃ khemaṃ appaṭibhayaṃ, natthi ca nāvā santāraṇī uttarasetu vā apārā pāraṃ gamanāya. yaṃnūnāhaṃ tiṇakaṭṭhasākhāpalāsaṃ saṃkaḍḍhitvā, kullaṃ bandhitvā, taṃ kullaṃ nissāya hatthehi ca pādehi ca vāyamamāno sotthinā pāraṃ uttareyya’nti. atha kho so, bhikkhave, puriso tiṇakaṭṭhasākhāpalāsaṃ saṃkaḍḍhitvā, kullaṃ bandhitvā taṃ kullaṃ nissāya hatthehi ca pādehi ca vāyamamāno sotthinā pāraṃ uttareyya. tassa purisassa uttiṇṇassa <span class="note">[tiṇṇassa (pī. ka.)]</span> pāraṅgatassa evamassa – ‘bahukāro kho me ayaṃ kullo, imāhaṃ kullaṃ nissāya hatthehi ca pādehi ca vāyamamāno sotthinā pāraṃ uttiṇṇo. yaṃnūnāhaṃ imaṃ kullaṃ sīse vā āropetvā khandhe <a name="T1.0271"></a> vā uccāretvā <span class="note">[uccopetvā (ka.)]</span> yena kāmaṃ pakkameyya’nti. taṃ kiṃ maññatha, bhikkhave, api nu so puriso evaṃkārī tasmiṃ kulle kiccakārī assā"ti? "no hetaṃ, bhante". "kathaṃkārī ca so, bhikkhave, puriso tasmiṃ kulle kiccakārī assa? idha, bhikkhave, tassa purisassa uttiṇṇassa pāraṅgatassa evamassa – ‘bahukāro kho me ayaṃ kullo, imāhaṃ kullaṃ nissāya <a name="V1.0189"></a> hatthehi ca pādehi ca vāyamamāno sotthinā pāraṃ uttiṇṇo. yaṃnūnāhaṃ imaṃ kullaṃ thale vā ussādetvā <span class="note">[ussāretvā (ka.)]</span> udake vā opilāpetvā yena kāmaṃ pakkameyya’nti. evaṃkārī kho so, bhikkhave, puriso tasmiṃ kulle kiccakārī assa. evameva kho, bhikkhave, kullūpamo mayā dhammo desito nittharaṇatthāya, no gahaṇatthāya. kullūpamaṃ vo, bhikkhave, dhammaṃ desitaṃ, ājānantehi dhammāpi vo pahātabbā pageva adhammā.</p>\r\n<p class="bodytext"><a name="para241"></a><a name="para241_mn1"></a><span class="paranum">241</span>. chayimāni, bhikkhave, diṭṭhiṭṭhānāni. katamāni cha? idha, bhikkhave, assutavā puthujjano ariyānaṃ adassāvī ariyadhammassa akovido ariyadhamme avinīto, sappurisānaṃ adassāvī sappurisadhammassa akovido sappurisadhamme avinīto, rūpaṃ ‘etaṃ mama, esohamasmi, eso me</p>\r\n','\r',char(13)),'\n',char(10)),'-241-');
INSERT INTO pages VALUES(3228,'mula_ma_01',189,replace(replace('<p class="bodytext">attā’ti <a name="M1.0189"></a> samanupassati, vedanaṃ ‘etaṃ mama, esohamasmi, eso me attā’ti samanupassati, saññaṃ ‘etaṃ mama, esohamasmi, eso me attā’ti samanupassati, saṅkhāre ‘etaṃ mama, esohamasmi, eso <a name="T1.0272"></a> me attā’ti samanupassati, yampi taṃ diṭṭhaṃ sutaṃ mutaṃ viññātaṃ pattaṃ pariyesitaṃ, anuvicaritaṃ manasā tampi ‘etaṃ mama, esohamasmi, eso me attā’ti samanupassati, yampi taṃ diṭṭhiṭṭhānaṃ – so loko so attā, so pecca bhavissāmi nicco dhuvo sassato avipariṇāmadhammo<a name="P1.0136"></a>, sassatisamaṃ tatheva ṭhassāmīti – tampi ‘etaṃ mama, esohamasmi, eso me attā’ti samanupassati. sutavā ca kho, bhikkhave, ariyasāvako ariyānaṃ dassāvī ariyadhammassa kovido ariyadhamme suvinīto, sappurisānaṃ dassāvī sappurisadhammassa kovido sappurisadhamme suvinīto, rūpaṃ ‘netaṃ mama, nesohamasmi, na meso attā’ti samanupassati, vedanaṃ ‘netaṃ mama, nesohamasmi, na meso attā’ti samanupassati, saññaṃ ‘netaṃ mama, nesohamasmi, na meso attā’ti samanupassati, saṅkhāre ‘netaṃ mama, nesohamasmi, na meso attā’ti samanupassati, yampi taṃ diṭṭhaṃ sutaṃ mutaṃ viññātaṃ pattaṃ pariyesitaṃ, anuvicaritaṃ manasā, tampi ‘netaṃ mama, nesohamasmi, na meso attā’ti samanupassati, yampi taṃ diṭṭhiṭṭhānaṃ – so loko so attā, so pecca bhavissāmi nicco dhuvo sassato avipariṇāmadhammo, sassatisamaṃ tatheva ṭhassāmīti – tampi ‘netaṃ mama, nesohamasmi, na meso attā’ti samanupassati. so evaṃ samanupassanto asati na paritassatī"ti.</p>\r\n<p class="bodytext"><a name="para242"></a><a name="para242_mn1"></a><span class="paranum">242</span>. evaṃ vutte, aññataro bhikkhu bhagavantaṃ etadavoca – "siyā nu kho, bhante, bahiddhā asati paritassanā"ti? "siyā, bhikkhū"ti – bhagavā <a name="T1.0273"></a> avoca. "idha bhikkhu ekaccassa <a name="V1.0190"></a> evaṃ hoti – ‘ahu vata me, taṃ vata me natthi, siyā vata me, taṃ vatāhaṃ na labhāmī’ti. so socati kilamati paridevati urattāḷiṃ kandati sammohaṃ āpajjati. evaṃ kho, bhikkhu, bahiddhā asati paritassanā hotī"ti.</p>\r\n<p class="bodytext">siyā pana, bhante, bahiddhā asati aparitassanā"ti? "siyā, bhikkhū"ti – bhagavā avoca. "idha bhikkhu ekaccassa na evaṃ hoti – ‘ahu vata me, taṃ vata me natthi, siyā vata me, taṃ vatāhaṃ na labhāmī’ti. so na</p>\r\n','\r',char(13)),'\n',char(10)),'-242-');
INSERT INTO pages VALUES(3229,'mula_ma_01',190,replace(replace('<p class="bodytext">socati <a name="M1.0190"></a> na kilamati na paridevati na urattāḷiṃ kandati na sammohaṃ āpajjati. evaṃ kho, bhikkhu, bahiddhā asati aparitassanā hotī"ti.</p>\r\n<p class="bodytext">siyā nu kho, bhante, ajjhattaṃ asati paritassanā"ti? "siyā, bhikkhū"ti – bhagavā avoca. "idha, bhikkhu, ekaccassa evaṃ diṭṭhi hoti – ‘so loko so attā, so pecca bhavissāmi nicco dhuvo sassato avipariṇāmadhammo, sassatisamaṃ tatheva ṭhassāmī’ti. so suṇāti tathāgatassa vā tathāgatasāvakassa vā sabbesaṃ diṭṭhiṭṭhānādhiṭṭhānapariyuṭṭhānābhinivesānusayānaṃ samugghātāya sabbasaṅkhārasamathāya sabbūpadhipaṭinissaggāya taṇhākkhayāya virāgāya nirodhāya nibbānāya dhammaṃ desentassa. tassa evaṃ hoti <a name="P1.0137"></a> – ‘ucchijjissāmi nāmassu, vinassissāmi nāmassu, nassu nāma bhavissāmī’ti. so socati kilamati paridevati urattāḷiṃ kandati sammohaṃ āpajjati. evaṃ kho, bhikkhu, ajjhattaṃ asati paritassanā hotī"ti.</p>\r\n<p class="bodytext">siyā pana, bhante, ajjhattaṃ asati aparitassanā"ti? "siyā, bhikkhū"ti <a name="T1.0274"></a> bhagavā avoca. "idha, bhikkhu, ekaccassa na evaṃ diṭṭhi hoti – ‘so loko so attā, so pecca bhavissāmi nicco dhuvo sassato avipariṇāmadhammo, sassatisamaṃ tatheva ṭhassāmī’ti. so suṇāti tathāgatassa vā tathāgatasāvakassa vā sabbesaṃ diṭṭhiṭṭhānādhiṭṭhānapariyuṭṭhānābhinivesānusayānaṃ samugghātāya sabbasaṅkhārasamathāya sabbūpadhipaṭinissaggāya taṇhākkhayāya virāgāya nirodhāya nibbānāya dhammaṃ desentassa. tassa na evaṃ hoti – ‘ucchijjissāmi nāmassu, vinassissāmi nāmassu, nassu nāma bhavissāmī’ti. so na socati <a name="V1.0191"></a> na kilamati na paridevati na urattāḷiṃ kandati na sammohaṃ āpajjati. evaṃ kho, bhikkhu, ajjhattaṃ asati aparitassanā hoti".</p>\r\n<p class="bodytext"><a name="para243"></a><a name="para243_mn1"></a><span class="paranum">243</span>. taṃ <span class="note">[tañca (ka.)]</span>, bhikkhave, pariggahaṃ pariggaṇheyyātha, yvāssa <span class="note">[yvāssu (ka.)]</span> pariggaho nicco dhuvo sassato avipariṇāmadhammo, sassatisamaṃ tatheva tiṭṭheyya. passatha no tumhe, bhikkhave, taṃ pariggahaṃ yvāssa pariggaho nicco dhuvo sassato avipariṇāmadhammo, sassatisamaṃ tatheva tiṭṭheyyā"ti? "no hetaṃ, bhante". "sādhu, bhikkhave. ahampi kho taṃ, bhikkhave, pariggahaṃ na</p>\r\n','\r',char(13)),'\n',char(10)),'-243-');
INSERT INTO pages VALUES(3230,'mula_ma_01',191,replace(replace('<p class="bodytext">samanupassāmi <a name="M1.0191"></a> yvāssa pariggaho nicco dhuvo sassato avipariṇāmadhammo sassatisamaṃ tatheva tiṭṭheyya.</p>\r\n<p class="bodytext">taṃ, bhikkhave, attavādupādānaṃ upādiyetha, yaṃsa <span class="note">[yassa (syā. ka.)]</span> attavādupādānaṃ upādiyato na uppajjeyyuṃ sokaparidevadukkhadomanassupāyāsā. passatha no tumhe, bhikkhave, taṃ attavādupādānaṃ <a name="T1.0275"></a> yaṃsa attavādupādānaṃ upādiyato na uppajjeyyuṃ sokaparidevadukkhadomanassupāyāsā"ti? "no hetaṃ, bhante". "sādhu, bhikkhave. ahampi kho taṃ, bhikkhave, attavādupādānaṃ na samanupassāmi yaṃsa attavādupādānaṃ upādiyato na uppajjeyyuṃ sokaparidevadukkhadomanassupāyāsā.</p>\r\n<p class="bodytext">taṃ, bhikkhave, diṭṭhinissayaṃ nissayetha yaṃsa diṭṭhinissayaṃ nissayato na uppajjeyyuṃ sokaparidevadukkhadomanassupāyāsā. passatha no tumhe, bhikkhave, taṃ diṭṭhinissayaṃ yaṃsa diṭṭhinissayaṃ nissayato na uppajjeyyuṃ sokaparidevadukkhadomanassupāyāsā"ti? "no hetaṃ, bhante". "sādhu, bhikkhave. ahampi kho taṃ, bhikkhave, diṭṭhinissayaṃ na samanupassāmi yaṃsa diṭṭhinissayaṃ nissayato <a name="P1.0138"></a> na uppajjeyyuṃ sokaparidevadukkhadomanassupāyāsā".</p>\r\n<p class="bodytext"><a name="para244"></a><a name="para244_mn1"></a><span class="paranum">244</span>. attani vā, bhikkhave, sati attaniyaṃ me ti assā"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">attaniye vā, bhikkhave, sati attā me ti assā"ti? "evaṃ, bhante".</p>\r\n<p class="bodytext">attani <a name="V1.0192"></a> ca, bhikkhave, attaniye ca saccato thetato anupalabbhamāne, yampi taṃ diṭṭhiṭṭhānaṃ – ‘so loko so attā, so pecca bhavissāmi nicco dhuvo sassato avipariṇāmadhammo, sassatisamaṃ tatheva ṭhassāmī’ti – nanāyaṃ <span class="note">[na ca khoyaṃ (ka.)]</span>, bhikkhave, kevalo paripūro bāladhammo"’ti?</p>\r\n<p class="bodytext">kiñhi no siyā, bhante, kevalo hi, bhante, paripūro <span class="note">[kevalo paripūro (sī. pī.)]</span> bāladhammo"ti.</p>\r\n<p class="bodytext">taṃ kiṃ maññatha, bhikkhave, rūpaṃ niccaṃ vā aniccaṃ vā"ti?</p>\r\n<p class="bodytext">aniccaṃ, bhante"<a name="T1.0276"></a>.</p>\r\n<p class="bodytext">yaṃ panāniccaṃ, dukkhaṃ vā taṃ sukhaṃ vā"ti?</p>\r\n<p class="bodytext">dukkhaṃ, bhante".</p>\r\n<p class="bodytext">yaṃ panāniccaṃ dukkhaṃ vipariṇāmadhammaṃ, kallaṃ nu taṃ samanupassituṃ – etaṃ mama, esohamasmi, eso me attā"ti?</p>\r\n<p class="bodytext">no hetaṃ, bhante".</p>\r\n<p class="bodytext">taṃ kiṃ maññatha, bhikkhave, vedanā…pe… saññā… saṅkhārā… viññāṇaṃ niccaṃ vā aniccaṃ vā"ti?</p>\r\n<p class="bodytext">aniccaṃ, bhante".</p>\r\n<p class="bodytext">yaṃ panāniccaṃ,</p>\r\n','\r',char(13)),'\n',char(10)),'-244-');
INSERT INTO pages VALUES(3231,'mula_ma_01',192,replace(replace('<p class="bodytext">dukkhaṃ <a name="M1.0192"></a> vā taṃ sukhaṃ vā"ti?</p>\r\n<p class="bodytext">dukkhaṃ, bhante".</p>\r\n<p class="bodytext">yaṃ <a name="V1.0193"></a> panāniccaṃ dukkhaṃ vipariṇāmadhammaṃ, kallaṃ nu taṃ samanupassituṃ – etaṃ mama, esohamasmi, eso me attā"ti?</p>\r\n<p class="bodytext">no hetaṃ, bhante".</p>\r\n<p class="bodytext">tasmātiha, bhikkhave, yaṃ kiñci rūpaṃ atītānāgatapaccuppannaṃ, ajjhattaṃ vā bahiddhā vā<a name="P1.0139"></a>, oḷārikaṃ vā sukhumaṃ vā, hīnaṃ vā paṇītaṃ vā, yaṃ dūre santike vā, sabbaṃ rūpaṃ ‘netaṃ mama, nesohamasmi, na meso attā’ti – evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabbaṃ. yā kāci vedanā…pe… yā kāci saññā… ye keci saṅkhārā… yaṃ kiñci viññāṇaṃ atītānāgatapaccuppannaṃ, ajjhattaṃ vā bahiddhā vā, oḷārikaṃ vā sukhumaṃ vā, hīnaṃ vā paṇītaṃ vā, yaṃ dūre santike vā, sabbaṃ viññāṇaṃ ‘netaṃ mama, nesohamasmi, na meso attā’ti – evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabbaṃ".</p>\r\n<p class="bodytext"><a name="para245"></a><a name="para245_mn1"></a><span class="paranum">245</span>. evaṃ passaṃ, bhikkhave, sutavā ariyasāvako rūpasmiṃ nibbindati, vedanāya nibbindati, saññāya nibbindati, saṅkhāresu nibbindati, viññāṇasmiṃ nibbindati, nibbidā virajjati <span class="note">[nibbindaṃ virajjati (sī. syā. pī.)]</span>, virāgā vimuccati<a name="T1.0277"></a>, vimuttasmiṃ vimuttamiti ñāṇaṃ hoti. ‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti pajānāti. ayaṃ vuccati, bhikkhave, bhikkhu ukkhittapaligho itipi, saṃkiṇṇaparikkho itipi, abbūḷhesiko itipi, niraggaḷo itipi, ariyo pannaddhajo pannabhāro visaṃyutto itipi.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu ukkhittapaligho hoti? idha, bhikkhave, bhikkhuno avijjā pahīnā hoti, ucchinnamūlā tālāvatthukatā anabhāvaṃkatā, āyatiṃ anuppādadhammā. evaṃ kho, bhikkhave, bhikkhu ukkhittapaligho hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu saṃkiṇṇaparikkho hoti? idha, bhikkhave, bhikkhuno ponobbhaviko <a name="V1.0194"></a> jātisaṃsāro pahīno hoti, ucchinnamūlo tālāvatthukato anabhāvaṃkato, āyatiṃ anuppādadhammo. evaṃ kho, bhikkhave, bhikkhu saṃkiṇṇaparikkho hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu abbūḷhesiko hoti? idha, bhikkhave, bhikkhuno taṇhā pahīnā hoti, ucchinnamūlā tālāvatthukatā anabhāvaṃkatā, āyatiṃ anuppādadhammā. evaṃ kho, bhikkhave, bhikkhu abbūḷhesiko hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu niraggaḷo hoti? idha, bhikkhave, bhikkhuno pañca orambhāgiyāni saṃyojanāni pahīnāni honti, ucchinnamūlāni tālāvatthukatāni anabhāvaṃkatāni, āyatiṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-245-');
INSERT INTO pages VALUES(3232,'mula_ma_01',193,replace(replace('<p class="bodytext">anuppādadhammāni<a name="M1.0193"></a>. evaṃ kho, bhikkhave, bhikkhu niraggaḷo hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu ariyo pannaddhajo pannabhāro visaṃyutto hoti? idha, bhikkhave, bhikkhuno asmimāno pahīno hoti, ucchinnamūlo tālāvatthukato anabhāvaṃkato<a name="T1.0278"></a>, āyatiṃ anuppādadhammo<a name="P1.0140"></a>. evaṃ kho, bhikkhave, bhikkhu ariyo pannaddhajo pannabhāro visaṃyutto hoti.</p>\r\n<p class="bodytext"><a name="para246"></a><a name="para246_mn1"></a><span class="paranum">246</span>. evaṃ vimuttacittaṃ kho, bhikkhave, bhikkhuṃ saindā devā sabrahmakā sapajāpatikā anvesaṃ nādhigacchanti – ‘idaṃ nissitaṃ tathāgatassa viññāṇa’nti. taṃ kissa hetu? diṭṭhevāhaṃ, bhikkhave, dhamme tathāgataṃ ananuvijjoti vadāmi. evaṃvādiṃ kho maṃ, bhikkhave, evamakkhāyiṃ eke samaṇabrāhmaṇā asatā tucchā musā abhūtena abbhācikkhanti – ‘venayiko samaṇo gotamo, sato sattassa ucchedaṃ vināsaṃ vibhavaṃ paññāpetī’ti. yathā cāhaṃ na, bhikkhave <span class="note">[bhikkhave na (sī. syā. pī.)]</span>, yathā cāhaṃ na vadāmi, tathā maṃ te bhonto samaṇabrāhmaṇā asatā tucchā musā abhūtena abbhācikkhanti – ‘venayiko samaṇo gotamo, sato sattassa ucchedaṃ vināsaṃ vibhavaṃ paññāpetī’ti. pubbe cāhaṃ bhikkhave, etarahi ca dukkhañceva paññāpemi, dukkhassa ca nirodhaṃ. tatra ce, bhikkhave, pare tathāgataṃ akkosanti paribhāsanti rosenti vihesenti, tatra, bhikkhave, tathāgatassa na hoti āghāto na appaccayo na cetaso anabhiraddhi.</p>\r\n<p class="bodytext">tatra ce, bhikkhave, pare tathāgataṃ sakkaronti garuṃ karonti mānenti pūjenti, tatra, bhikkhave, tathāgatassa na hoti ānando na somanassaṃ na cetaso uppilāvitattaṃ. tatra ce, bhikkhave<a name="V1.0195"></a>, pare <a name="T1.0279"></a> vā tathāgataṃ sakkaronti garuṃ karonti mānenti pūjenti, tatra, bhikkhave, tathāgatassa evaṃ hoti – ‘yaṃ kho idaṃ pubbe pariññātaṃ tattha me evarūpā kārā <span class="note">[sakkārā (ka.)]</span> karīyantī’ti. tasmātiha, bhikkhave, tumhe cepi pare akkoseyyuṃ paribhāseyyuṃ roseyyuṃ viheseyyuṃ, tatra tumhe hi na āghāto na appaccayo na cetaso anabhiraddhi karaṇīyā. tasmātiha, bhikkhave, tumhe cepi pare sakkareyyuṃ garuṃ kareyyuṃ māneyyuṃ pūjeyyuṃ, tatra tumhehi na ānando na somanassaṃ na cetaso uppilāvitattaṃ karaṇīyaṃ. tasmātiha, bhikkhave, tumhe cepi pare sakkareyyuṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-246-');
INSERT INTO pages VALUES(3233,'mula_ma_01',194,replace(replace('<p class="bodytext">garuṃ <a name="M1.0194"></a> kareyyuṃ māneyyuṃ pūjeyyuṃ, tatra tumhākaṃ evamassa – ‘yaṃ kho idaṃ pubbe pariññātaṃ, tattha me <span class="note">[tattha no (ka.) tattha + imeti padacchedo]</span> evarūpā kārā karīyantī’ti.</p>\r\n<p class="bodytext"><a name="para247"></a><a name="para247_mn1"></a><span class="paranum">247</span>. tasmātiha, bhikkhave, yaṃ na tumhākaṃ taṃ pajahatha, taṃ vo pahīnaṃ dīgharattaṃ hitāya sukhāya bhavissati. kiñca, bhikkhave, na tumhākaṃ? rūpaṃ, bhikkhave, na tumhākaṃ, taṃ pajahatha, taṃ vo pahīnaṃ dīgharattaṃ hitāya sukhāya bhavissati. vedanā, bhikkhave, na tumhākaṃ, taṃ <a name="P1.0141"></a> pajahatha, sā vo pahīnā dīgharattaṃ hitāya sukhāya bhavissati. saññā, bhikkhave, na tumhākaṃ, taṃ pajahatha, sā vo pahīnā dīgharattaṃ hitāya sukhāya bhavissati. saṅkhārā, bhikkhave, na tumhākaṃ, te pajahatha, te vo pahīnā dīgharattaṃ hitāya sukhāya bhavissanti. viññāṇaṃ, bhikkhave, na tumhākaṃ, taṃ pajahatha, taṃ vo pahīnaṃ dīgharattaṃ hitāya sukhāya bhavissati. taṃ kiṃ maññatha, bhikkhave, yaṃ <a name="T1.0280"></a> imasmiṃ jetavane tiṇakaṭṭhasākhāpalāsaṃ, taṃ jano hareyya vā daheyya vā yathāpaccayaṃ vā kareyya. api nu tumhākaṃ evamassa – ‘amhe jano harati vā dahati vā yathāpaccayaṃ vā karotī’ti? "no hetaṃ, bhante". "taṃ kissa hetu"? "na hi no etaṃ, bhante, attā vā attaniyaṃ vā"ti. "evameva kho, bhikkhave, yaṃ na tumhākaṃ taṃ pajahatha, taṃ vo pahīnaṃ dīgharattaṃ hitāya sukhāya bhavissati. kiñca, bhikkhave, na tumhākaṃ? rūpaṃ, bhikkhave, na tumhākaṃ, taṃ pajahatha, taṃ vo pahīnaṃ dīgharattaṃ hitāya sukhāya bhavissati. vedanā, bhikkhave…pe… saññā, bhikkhave… saṅkhārā, bhikkhave…pe… viññāṇaṃ, bhikkhave, na tumhākaṃ, taṃ pajahatha, taṃ vo pahīnaṃ dīgharattaṃ hitāya sukhāya bhavissati.</p>\r\n<p class="bodytext"><a name="para248"></a><a name="para248_mn1"></a><span class="paranum">248</span>. evaṃ <a name="V1.0196"></a> svākkhāto, bhikkhave, mayā dhammo uttāno vivaṭo pakāsito chinnapilotiko. evaṃ svākkhāte, bhikkhave, mayā dhamme uttāne vivaṭe pakāsite chinnapilotike ye te bhikkhū arahanto khīṇāsavā vusitavanto katakaraṇīyā ohitabhārā anuppattasadatthā parikkhīṇabhavasaṃyojanā sammadaññā vimuttā, vaṭṭaṃ tesaṃ natthi paññāpanāya. evaṃ svākkhāto, bhikkhave, mayā dhammo uttāno vivaṭo pakāsito chinnapilotiko. evaṃ svākkhāte, bhikkhave, mayā dhamme uttāne vivaṭe pakāsite chinnapilotike yesaṃ bhikkhūnaṃ pañcorambhāgiyāni</p>\r\n','\r',char(13)),'\n',char(10)),'-247-248-');
INSERT INTO pages VALUES(3234,'mula_ma_01',195,replace(replace('<p class="bodytext">saṃyojanāni <a name="M1.0195"></a> pahīnāni, sabbe te opapātikā, tattha <a name="T1.0281"></a> parinibbāyino, anāvattidhammā tasmā lokā. evaṃ svākkhāto, bhikkhave, mayā dhammo uttāno vivaṭo pakāsito chinnapilotiko. evaṃ svākkhāte, bhikkhave, mayā dhamme uttāne vivaṭe pakāsite chinnapilotike yesaṃ bhikkhūnaṃ tīṇi saṃyojanāni pahīnāni, rāgadosamohā tanubhūtā, sabbe te sakadāgāmino, sakideva imaṃ lokaṃ āgantvā dukkhassantaṃ karissanti. evaṃ svākkhāto, bhikkhave, mayā dhammo uttāno vivaṭo pakāsito chinnapilotiko. evaṃ svākkhāte, bhikkhave, mayā dhamme uttāne vivaṭe pakāsite chinnapilotike yesaṃ bhikkhūnaṃ tīṇi saṃyojanāni pahīnāni, sabbe te sotāpannā, avinipātadhammā<a name="P1.0142"></a>, niyatā sambodhiparāyanā. evaṃ svākkhāto, bhikkhave, mayā dhammo uttāno vivaṭo pakāsito chinnapilotiko. evaṃ svākkhāte, bhikkhave, mayā dhamme uttāne vivaṭe pakāsite chinnapilotike ye te bhikkhū dhammānusārino saddhānusārino sabbe te sambodhiparāyanā. evaṃ svākkhāto, bhikkhave, mayā dhammo uttāno vivaṭo pakāsito chinnapilotiko. evaṃ svākkhāte, bhikkhave, mayā dhamme uttāne vivaṭe pakāsite chinnapilotike yesaṃ mayi saddhāmattaṃ pemamattaṃ sabbe te saggaparāyanā"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">alagaddūpamasuttaṃ niṭṭhitaṃ dutiyaṃ.</p>\r\n<p class="subhead">3. vammikasuttaṃ</p>\r\n<p class="bodytext"><a name="para249"></a><a name="para249_mn1"></a><span class="paranum">249</span>. evaṃ <a name="T1.0282"></a><a name="V1.0197"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tena kho pana samayena āyasmā kumārakassapo andhavane viharati. atha kho aññatarā devatā abhikkantāya rattiyā abhikkantavaṇṇā kevalakappaṃ andhavanaṃ obhāsetvā yenāyasmā kumārakassapo tenupasaṅkami, upasaṅkamitvā ekamantaṃ aṭṭhāsi. ekamantaṃ ṭhitā kho sā devatā āyasmantaṃ kumārakassapaṃ etadavoca –</p>\r\n','\r',char(13)),'\n',char(10)),'-249-');
INSERT INTO pages VALUES(3235,'mula_ma_01',196,replace(replace('<p class="bodytext">bhikkhu <a name="M1.0196"></a> bhikkhu, ayaṃ vammiko <span class="note">[vammīko (katthaci) sakkatānurūpaṃ]</span> rattiṃ dhūmāyati, divā pajjalati. brāhmaṇo evamāha – ‘abhikkhaṇa, sumedha, satthaṃ ādāyā’ti. abhikkhaṇanto sumedho satthaṃ ādāya addasa laṅgiṃ ‘laṅgī, bhadante’ti. brāhmaṇo evamāha – ‘ukkhipa laṅgiṃ, abhikkhaṇa, sumedha, satthaṃ ādāyā’ti. abhikkhaṇanto sumedho satthaṃ ādāya addasa uddhumāyikaṃ. ‘uddhumāyikā, bhadante’ti. brāhmaṇo evamāha – ‘ukkhipa uddhumāyikaṃ, abhikkhaṇa, sumedha, satthaṃ ādāyā’ti. abhikkhaṇanto sumedho satthaṃ ādāya addasa dvidhāpathaṃ. ‘dvidhāpatho, bhadante’ti. brāhmaṇo evamāha – ‘ukkhipa dvidhāpathaṃ, abhikkhaṇa, sumedha, satthaṃ ādāyā’ti. abhikkhaṇanto sumedho satthaṃ ādāya addasa caṅgavāraṃ <span class="note">[paṅkavāraṃ (syā.), caṅkavāraṃ (ka.)]</span>. ‘caṅgavāro, bhadante’ti. brāhmaṇo evamāha – ‘ukkhipa <a name="P1.0143"></a> caṅgavāraṃ, abhikkhaṇa, sumedha, satthaṃ ādāyā’ti. abhikkhaṇanto sumedho satthaṃ ādāya addasa kummaṃ. ‘kummo<a name="T1.0283"></a>, bhadante’ti. brāhmaṇo evamāha – ‘ukkhipa kummaṃ, abhikkhaṇa, sumedha, satthaṃ ādāyā’ti. abhikkhaṇanto sumedho satthaṃ ādāya addasa asisūnaṃ. ‘asisūnā, bhadante’ti. brāhmaṇo evamāha – ‘ukkhipa asisūnaṃ, abhikkhaṇa, sumedha, satthaṃ ādāyā’ti. abhikkhaṇanto sumedho satthaṃ ādāya addasa maṃsapesiṃ. ‘maṃsapesi, bhadante’ti. brāhmaṇo evamāha – ‘ukkhipa maṃsapesiṃ, abhikkhaṇa, sumedha, satthaṃ ādāyā’ti. abhikkhaṇanto sumedho satthaṃ ādāya addasa nāgaṃ. ‘nāgo, bhadante’ti. brāhmaṇo evamāha – ‘tiṭṭhatu nāgo, mā nāgaṃ ghaṭṭesi, namo karohi nāgassā"’ti.</p>\r\n<p class="bodytext">ime <a name="V1.0198"></a> kho tvaṃ, bhikkhu, pañhe bhagavantaṃ upasaṅkamitvā puccheyyāsi, yathā ca te bhagavā byākaroti tathā naṃ dhāreyyāsi. nāhaṃ taṃ, bhikkhu, passāmi sadevake loke samārake sabrahmake sassamaṇabrāhmaṇiyā pajāya sadevamanussāya, yo imesaṃ pañhānaṃ veyyākaraṇena cittaṃ ārādheyya aññatra tathāgatena vā, tathāgatasāvakena vā, ito vā pana sutvā"ti – idamavoca sā devatā. idaṃ vatvā tatthevantaradhāyi.</p>\r\n<p class="bodytext"><a name="para250"></a><a name="para250_mn1"></a><span class="paranum">250</span>. atha kho āyasmā kumārakassapo tassā rattiyā accayena yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho āyasmā kumārakassapo</p>\r\n','\r',char(13)),'\n',char(10)),'-250-');
INSERT INTO pages VALUES(3236,'mula_ma_01',197,replace(replace('<p class="bodytext">bhagavantaṃ <a name="M1.0197"></a> etadavoca – "imaṃ, bhante, rattiṃ aññatarā devatā abhikkantāya rattiyā abhikkantavaṇṇā kevalakappaṃ andhavanaṃ obhāsetvā <a name="T1.0284"></a> yenāhaṃ tenupasaṅkami, upasaṅkamitvā ekamantaṃ aṭṭhāsi. ekamantaṃ ṭhitā kho, bhante, sā devatā maṃ etadavoca – ‘bhikkhu bhikkhu, ayaṃ vammiko rattiṃ dhūmāyati, divā pajjalati’. brāhmaṇo evamāha – ‘abhikkhaṇa, sumedha, satthaṃ ādāyā’ti. abhikkhaṇanto sumedho satthaṃ ādāya…pe… ito vā pana sutvāti. idamavoca, bhante, sā devatā. idaṃ vatvā tatthevantaradhāyi. ‘ko nu kho, bhante, vammiko, kā rattiṃ dhūmāyanā, kā divā pajjalanā, ko brāhmaṇo, ko sumedho, kiṃ satthaṃ, kiṃ abhikkhaṇaṃ, kā laṅgī, kā uddhumāyikā, ko dvidhāpatho, kiṃ caṅgavāraṃ, ko kummo, kā asisūnā<a name="T1.0285"></a>, kā maṃsapesi, ko nāgo"’ti?</p>\r\n<p class="bodytext"><a name="para251"></a><a name="para251_mn1"></a><span class="paranum">251</span>. ‘vammiko’ti <a name="P1.0144"></a> kho, bhikkhu, imassetaṃ cātumahābhūtikassa <span class="note">[cātummahābhūtikassa (sī. syā. pī.)]</span> kāyassa adhivacanaṃ, mātāpettikasambhavassa odanakummāsūpacayassa aniccucchādana-parimaddanabhedana-viddhaṃsana-dhammassa.</p>\r\n<p class="bodytext">yaṃ kho, bhikkhu, divā kammante <span class="note">[kammantaṃ (ka.)]</span> ārabbha rattiṃ anuvitakketi anuvicāreti – ayaṃ rattiṃ dhūmāyanā. yaṃ kho, bhikkhu, rattiṃ anuvitakketvā anuvicāretvā divā kammante payojeti kāyena vācāya ‘manasā’ <span class="note">[( ) natthi (sī. syā.)]</span> – ayaṃ divā pajjalanā.</p>\r\n<p class="bodytext">‘brāhmaṇo’ti <a name="V1.0199"></a> kho, bhikkhu, tathāgatassetaṃ adhivacanaṃ arahato sammāsambuddhassa. ‘sumedho’ti kho bhikkhu sekkhassetaṃ bhikkhuno adhivacanaṃ.</p>\r\n<p class="bodytext">‘sattha’nti kho, bhikkhu, ariyāyetaṃ paññāya adhivacanaṃ. ‘abhikkhaṇa’nti kho, bhikkhu, vīriyārambhassetaṃ adhivacanaṃ.</p>\r\n<p class="bodytext">‘laṅgī’ti kho, bhikkhu, avijjāyetaṃ adhivacanaṃ. ukkhipa laṅgiṃ, pajaha avijjaṃ, abhikkhaṇa, sumedha, satthaṃ ādāyāti ayametassa attho.</p>\r\n<p class="bodytext">‘uddhumāyikā’ti kho, bhikkhu, kodhūpāyāsassetaṃ adhivacanaṃ. ukkhipa uddhumāyikaṃ, pajaha kodhūpāyāsaṃ, abhikkhaṇa, sumedha, satthaṃ ādāyāti ayametassa attho.</p>\r\n','\r',char(13)),'\n',char(10)),'-251-');
INSERT INTO pages VALUES(3237,'mula_ma_01',198,replace(replace('<p class="bodytext">‘dvidhāpatho’ti <a name="M1.0198"></a> kho, bhikkhu, vicikicchāyetaṃ adhivacanaṃ. ukkhipa dvidhāpathaṃ, pajaha vicikicchaṃ, abhikkhaṇa, sumedha, satthaṃ ādāyāti ayametassa attho.</p>\r\n<p class="bodytext">‘caṅgavāra’nti kho, bhikkhu, pañcannetaṃ nīvaraṇānaṃ adhivacanaṃ, seyyathidaṃ – kāmacchandanīvaraṇassa, byāpādanīvaraṇassa, thīnamiddhanīvaraṇassa, uddhaccakukkuccanīvaraṇassa, vicikicchānīvaraṇassa. ukkhipa caṅgavāraṃ, pajaha pañca nīvaraṇe<a name="T1.0286"></a>, abhikkhaṇa, sumedha, satthaṃ ādāyāti ayametassa attho.</p>\r\n<p class="bodytext">‘kummo’ti kho, bhikkhu, pañcannetaṃ upādānakkhandhānaṃ adhivacanaṃ, seyyathidaṃ – rūpupādānakkhandhassa, vedanupādānakkhandhassa, saññupādānakkhandhassa, saṅkhārupādānakkhandhassa, viññāṇupādānakkhandhassa. ukkhipa kummaṃ, pajaha pañcupādānakkhandhe, abhikkhaṇa, sumedha, satthaṃ ādāyāti ayametassa attho.</p>\r\n<p class="bodytext">‘asisūnā’ti kho, bhikkhu, pañcannetaṃ kāmaguṇānaṃ adhivacanaṃ – cakkhuviññeyyānaṃ rūpānaṃ iṭṭhānaṃ kantānaṃ manāpānaṃ piyarūpānaṃ kāmūpasaṃhitānaṃ rajanīyānaṃ, sotaviññeyyānaṃ saddānaṃ…pe… ghānaviññeyyānaṃ gandhānaṃ…pe… jivhāviññeyyānaṃ rasānaṃ…pe… kāyaviññeyyānaṃ <a name="V1.0200"></a> phoṭṭhabbānaṃ iṭṭhānaṃ kantānaṃ manāpānaṃ piyarūpānaṃ kāmūpasaṃhitānaṃ <a name="P1.0145"></a> rajanīyānaṃ. ukkhipa asisūnaṃ, pajaha pañca kāmaguṇe, abhikkhaṇa, sumedha, satthaṃ ādāyāti ayametassa attho.</p>\r\n<p class="bodytext">‘maṃsapesī’ti kho, bhikkhu, nandīrāgassetaṃ adhivacanaṃ. ukkhipa maṃsapesiṃ, pajaha nandīrāgaṃ, abhikkhaṇa, sumedha, satthaṃ ādāyāti ayametassa attho.</p>\r\n<p class="bodytext">‘nāgo’ti kho, bhikkhu, khīṇāsavassetaṃ bhikkhuno adhivacanaṃ. tiṭṭhatu nāgo, mā nāgaṃ ghaṭṭesi, namo karohi nāgassāti ayametassa attho"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamano āyasmā kumārakassapo bhagavato bhāsitaṃ abhinandīti.</p>\r\n<p class="centered">vammikasuttaṃ niṭṭhitaṃ tatiyaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3238,'mula_ma_01',199,replace(replace('<p class="subhead">4. rathavinītasuttaṃ</p>\r\n<p class="bodytext"><a name="para252"></a><a name="para252_mn1"></a><span class="paranum">252</span>. evaṃ <a name="T1.0287"></a><a name="M1.0199"></a><a name="V1.0201"></a> me sutaṃ – ekaṃ samayaṃ bhagavā rājagahe viharati veḷuvane kalandakanivāpe. atha kho sambahulā jātibhūmakā bhikkhū jātibhūmiyaṃ vassaṃvuṭṭhā yena bhagavā tenupasaṅkamiṃsu, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu. ekamantaṃ nisinne kho te bhikkhū bhagavā etadavoca –</p>\r\n<p class="bodytext">ko nu kho, bhikkhave, jātibhūmiyaṃ jātibhūmakānaṃ bhikkhūnaṃ sabrahmacārīnaṃ evaṃ sambhāvito – ‘attanā ca appiccho appicchakathañca bhikkhūnaṃ kattā, attanā ca santuṭṭho santuṭṭhikathañca bhikkhūnaṃ kattā, attanā ca pavivitto pavivekakathañca bhikkhūnaṃ kattā, attanā ca asaṃsaṭṭho asaṃsaggakathañca bhikkhūnaṃ kattā, attanā ca āraddhavīriyo vīriyārambhakathañca bhikkhūnaṃ kattā, attanā ca sīlasampanno sīlasampadākathañca bhikkhūnaṃ kattā, attanā ca samādhisampanno samādhisampadākathañca bhikkhūnaṃ kattā, attanā ca paññāsampanno paññāsampadākathañca bhikkhūnaṃ kattā, attanā ca vimuttisampanno vimuttisampadākathañca bhikkhūnaṃ kattā, attanā ca vimuttiñāṇadassanasampanno vimuttiñāṇadassanasampadākathañca bhikkhūnaṃ kattā, ovādako viññāpako sandassako samādapako samuttejako <a name="P1.0146"></a> sampahaṃsako sabrahmacārīna"’nti? "puṇṇo nāma, bhante, āyasmā mantāṇiputto <a name="T1.0288"></a> jātibhūmiyaṃ jātibhūmakānaṃ bhikkhūnaṃ sabrahmacārīnaṃ evaṃ sambhāvito – ‘attanā ca appiccho appicchakathañca bhikkhūnaṃ kattā, attanā ca santuṭṭho…pe… ovādako viññāpako sandassako samādapako samuttejako sampahaṃsako sabrahmacārīna"’nti.</p>\r\n<p class="bodytext"><a name="para253"></a><a name="para253_mn1"></a><span class="paranum">253</span>. tena kho pana samayena āyasmā sāriputto bhagavato avidūre nisinno hoti. atha kho āyasmato sāriputtassa etadahosi – "lābhā āyasmato puṇṇassa mantāṇiputtassa, suladdhalābhā āyasmato puṇṇassa mantāṇiputtassa, yassa viññū sabrahmacārī satthu sammukhā anumassa anumassa vaṇṇaṃ bhāsanti, tañca satthā abbhanumodati. appeva nāma mayampi kadāci <a name="V1.0202"></a> karahaci āyasmatā puṇṇena mantāṇiputtena saddhiṃ samāgaccheyyāma <span class="note">[samāgamaṃ gaccheyya (ka.)]</span>, appeva nāma siyā kocideva kathāsallāpo"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-252-253-');
INSERT INTO pages VALUES(3239,'mula_ma_01',200,replace(replace('<p class="bodytext"><a name="para254"></a><a name="para254_mn1"></a><span class="paranum">254</span>. atha <a name="M1.0200"></a> kho bhagavā rājagahe yathābhirantaṃ viharitvā yena sāvatthi tena cārikaṃ pakkāmi. anupubbena cārikaṃ caramāno yena sāvatthi tadavasari. tatra sudaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa <a name="T1.0289"></a> ārāme. assosi kho āyasmā puṇṇo mantāṇiputto – "bhagavā kira sāvatthiṃ anuppatto, sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme"ti.</p>\r\n<p class="bodytext"><a name="para255"></a><a name="para255_mn1"></a><span class="paranum">255</span>. atha kho āyasmā puṇṇo mantāṇiputto senāsanaṃ saṃsāmetvā pattacīvaramādāya yena sāvatthi tena cārikaṃ pakkāmi. anupubbena cārikaṃ caramāno yena sāvatthi jetavanaṃ anāthapiṇḍikassa ārāmo yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinnaṃ kho āyasmantaṃ puṇṇaṃ mantāṇiputtaṃ bhagavā dhammiyā kathāya sandassesi samādapesi samuttejesi sampahaṃsesi. atha kho āyasmā puṇṇo mantāṇiputto bhagavatā dhammiyā kathāya sandassito samādapito samuttejito sampahaṃsito bhagavato bhāsitaṃ abhinanditvā anumoditvā uṭṭhāyāsanā bhagavantaṃ abhivādetvā padakkhiṇaṃ katvā yena andhavanaṃ tenupasaṅkami divāvihārāya.</p>\r\n<p class="bodytext"><a name="para256"></a><a name="para256_mn1"></a><span class="paranum">256</span>. atha kho aññataro bhikkhu yenāyasmā sāriputto tenupasaṅkami, upasaṅkamitvā āyasmantaṃ sāriputtaṃ etadavoca – "yassa kho tvaṃ, āvuso sāriputta, puṇṇassa nāma bhikkhuno mantāṇiputtassa abhiṇhaṃ kittayamāno ahosi, so bhagavatā <a name="P1.0147"></a> dhammiyā kathāya sandassito samādapito samuttejito sampahaṃsito bhagavato bhāsitaṃ abhinanditvā anumoditvā uṭṭhāyāsanā bhagavantaṃ abhivādetvā padakkhiṇaṃ katvā yena andhavanaṃ tena pakkanto divāvihārāyā"ti.</p>\r\n<p class="bodytext">atha <a name="T1.0290"></a> kho āyasmā sāriputto taramānarūpo nisīdanaṃ ādāya āyasmantaṃ puṇṇaṃ mantāṇiputtaṃ piṭṭhito piṭṭhito anubandhi sīsānulokī. atha kho āyasmā puṇṇo mantāṇiputto andhavanaṃ ajjhogāhetvā aññatarasmiṃ rukkhamūle divāvihāraṃ nisīdi. āyasmāpi <a name="V1.0203"></a> kho sāriputto andhavanaṃ ajjhogāhetvā aññatarasmiṃ rukkhamūle divāvihāraṃ nisīdi.</p>\r\n','\r',char(13)),'\n',char(10)),'-254-255-256-');
INSERT INTO pages VALUES(3240,'mula_ma_01',201,replace(replace('<p class="bodytext">atha <a name="M1.0201"></a> kho āyasmā sāriputto sāyanhasamayaṃ paṭisallānā vuṭṭhito yenāyasmā puṇṇo mantāṇiputto tenupasaṅkami, upasaṅkamitvā āyasmatā puṇṇena mantāṇiputtena saddhiṃ sammodi. sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho āyasmā sāriputto āyasmantaṃ puṇṇaṃ mantāṇiputtaṃ etadavoca –</p>\r\n<p class="bodytext"><a name="para257"></a><a name="para257_mn1"></a><span class="paranum">257</span>. bhagavati no, āvuso, brahmacariyaṃ vussatī"ti?</p>\r\n<p class="bodytext">evamāvuso"ti.</p>\r\n<p class="bodytext">kiṃ nu kho, āvuso, sīlavisuddhatthaṃ bhagavati brahmacariyaṃ vussatī"ti?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ panāvuso, cittavisuddhatthaṃ bhagavati brahmacariyaṃ vussatī"ti?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ nu kho, āvuso, diṭṭhivisuddhatthaṃ bhagavati brahmacariyaṃ vussatī"ti?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ panāvuso, kaṅkhāvitaraṇavisuddhatthaṃ bhagavati brahmacariyaṃ vussatī"ti?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ nu kho, āvuso, maggāmaggañāṇadassanavisuddhatthaṃ bhagavati brahmacariyaṃ vussatī"ti?</p>\r\n<p class="bodytext">no <a name="V1.0204"></a> hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ panāvuso, paṭipadāñāṇadassanavisuddhatthaṃ bhagavati brahmacariyaṃ vussatī"ti?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ nu kho, āvuso, ñāṇadassanavisuddhatthaṃ <a name="T1.0291"></a> bhagavati brahmacariyaṃ vussatī"ti?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">‘kiṃ nu kho, āvuso, sīlavisuddhatthaṃ bhagavati brahmacariyaṃ vussatī’ti iti puṭṭho samāno ‘no hidaṃ, āvuso’ti vadesi. ‘kiṃ panāvuso, cittavisuddhatthaṃ bhagavati brahmacariyaṃ vussatī’ti iti puṭṭho samāno ‘no hidaṃ, āvuso’ti vadesi. ‘kiṃ nu kho, āvuso, diṭṭhivisuddhatthaṃ…pe… kaṅkhāvitaraṇavisuddhatthaṃ…pe… maggāmaggañāṇadassanavisuddhatthaṃ…pe… paṭipadāñāṇadassanavisuddhatthaṃ…pe… kiṃ nu kho, āvuso, ñāṇadassanavisuddhatthaṃ bhagavati brahmacariyaṃ vussatī’ti iti puṭṭho samāno ‘no hidaṃ āvuso’ti vadesi. kimatthaṃ carahāvuso, bhagavati brahmacariyaṃ <a name="P1.0148"></a> vussatī"ti? "anupādāparinibbānatthaṃ kho, āvuso, bhagavati brahmacariyaṃ vussatī"ti.</p>\r\n<p class="bodytext">kiṃ nu kho, āvuso, sīlavisuddhi anupādāparinibbāna"nti?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ panāvuso, cittavisuddhi anupādāparinibbāna"nti?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ nu kho, āvuso, diṭṭhivisuddhi anupādāparinibbāna"nti?</p>\r\n<p class="bodytext">no <a name="V1.0205"></a> hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ panāvuso kaṅkhāvitaraṇavisuddhi</p>\r\n','\r',char(13)),'\n',char(10)),'-257-');
INSERT INTO pages VALUES(3241,'mula_ma_01',202,replace(replace('<p class="bodytext">anupādāparinibbāna"nti<a name="M1.0202"></a>?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ nu kho, āvuso, maggāmaggañāṇadassanavisuddhi anupādāparinibbāna"nti?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ panāvuso, paṭipadāñāṇadassanavisuddhi anupādāparinibbāna"nti?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">kiṃ nu kho, āvuso, ñāṇadassanavisuddhi anupādāparinibbāna"nti?</p>\r\n<p class="bodytext">no hidaṃ<a name="T1.0292"></a>, āvuso".</p>\r\n<p class="bodytext">kiṃ panāvuso, aññatra imehi dhammehi anupādāparinibbāna"nti?</p>\r\n<p class="bodytext">no hidaṃ, āvuso".</p>\r\n<p class="bodytext">‘kiṃ nu kho, āvuso, sīlavisuddhi anupādāparinibbāna’nti iti puṭṭho samāno ‘no hidaṃ, āvuso’ti vadesi. ‘kiṃ panāvuso, cittavisuddhi anupādāparinibbāna’nti iti puṭṭho samāno ‘no hidaṃ, āvuso’ti vadesi. ‘kiṃ nu kho, āvuso, diṭṭhivisuddhi anupādāparinibbāna’nti…pe… kaṅkhāvitaraṇavisuddhi… maggāmaggañāṇadassanavisuddhi… paṭipadāñāṇadassanavisuddhi… ‘kiṃ nu kho, āvuso, ñāṇadassanavisuddhi anupādāparinibbāna’nti iti puṭṭho samāno ‘no hidaṃ, āvuso’ti vadesi. ‘kiṃ panāvuso, aññatra imehi dhammehi anupādāparinibbāna’nti iti puṭṭho samāno ‘no hidaṃ, āvuso’ti vadesi. yathākathaṃ panāvuso, imassa bhāsitassa attho daṭṭhabbo"ti?</p>\r\n<p class="bodytext"><a name="para258"></a><a name="para258_mn1"></a><span class="paranum">258</span>. sīlavisuddhiṃ <a name="V1.0206"></a> ce, āvuso, bhagavā anupādāparinibbānaṃ paññapeyya, saupādānaṃyeva samānaṃ anupādāparinibbānaṃ paññapeyya <span class="note">[paññāpessa (sī. syā.) evamaññatthapi]</span>. cittavisuddhiṃ ce, āvuso, bhagavā anupādāparinibbānaṃ paññapeyya, saupādānaṃyeva samānaṃ anupādāparinibbānaṃ paññapeyya. diṭṭhivisuddhiṃ ce, āvuso, bhagavā anupādāparinibbānaṃ paññapeyya, saupādānaṃyeva samānaṃ anupādāparinibbānaṃ paññapeyya. kaṅkhāvitaraṇavisuddhiṃ ce, āvuso, bhagavā anupādāparinibbānaṃ paññapeyya, saupādānaṃyeva samānaṃ anupādāparinibbānaṃ paññapeyya<a name="T1.0293"></a>. maggāmaggañāṇadassanavisuddhiṃ ce, āvuso, bhagavā anupādāparinibbānaṃ paññapeyya, saupādānaṃyeva samānaṃ anupādāparinibbānaṃ paññapeyya. paṭipadāñāṇadassanavisuddhiṃ ce, āvuso, bhagavā anupādāparinibbānaṃ paññapeyya, saupādānaṃyeva samānaṃ anupādāparinibbānaṃ paññapeyya. ñāṇadassanavisuddhiṃ ce, āvuso, bhagavā anupādāparinibbānaṃ paññapeyya,</p>\r\n','\r',char(13)),'\n',char(10)),'-258-');
INSERT INTO pages VALUES(3242,'mula_ma_01',203,replace(replace('<p class="bodytext">saupādānaṃyeva <a name="M1.0203"></a> samānaṃ anupādāparinibbānaṃ paññapeyya. aññatra ce, āvuso, imehi dhammehi anupādāparinibbānaṃ abhavissa, puthujjano parinibbāyeyya. puthujjano hi, āvuso, aññatra imehi dhammehi. tena hāvuso, upamaṃ te karissāmi, upamāyapidhekacce viññū purisā bhāsitassa atthaṃ ājānanti.</p>\r\n<p class="bodytext"><a name="para259"></a><a name="para259_mn1"></a><span class="paranum">259</span>. seyyathāpi, āvuso, rañño pasenadissa kosalassa sāvatthiyaṃ paṭivasantassa <a name="P1.0149"></a> sākete kiñcideva accāyikaṃ karaṇīyaṃ uppajjeyya. tassa antarā ca sāvatthiṃ antarā ca sāketaṃ satta rathavinītāni upaṭṭhapeyyuṃ. atha kho, āvuso, rājā pasenadi kosalo sāvatthiyā nikkhamitvā antepuradvārā paṭhamaṃ rathavinītaṃ abhiruheyya, paṭhamena rathavinītena dutiyaṃ rathavinītaṃ pāpuṇeyya, paṭhamaṃ rathavinītaṃ vissajjeyya dutiyaṃ rathavinītaṃ abhiruheyya. dutiyena rathavinītena tatiyaṃ rathavinītaṃ pāpuṇeyya, dutiyaṃ rathavinītaṃ vissajjeyya, tatiyaṃ rathavinītaṃ <a name="T1.0294"></a> abhiruheyya. tatiyena rathavinītena catutthaṃ rathavinītaṃ pāpuṇeyya, tatiyaṃ rathavinītaṃ vissajjeyya, catutthaṃ rathavinītaṃ abhiruheyya. catutthena rathavinītena pañcamaṃ rathavinītaṃ pāpuṇeyya, catutthaṃ rathavinītaṃ vissajjeyya, pañcamaṃ rathavinītaṃ abhiruheyya. pañcamena rathavinītena chaṭṭhaṃ rathavinītaṃ pāpuṇeyya, pañcamaṃ rathavinītaṃ vissajjeyya, chaṭṭhaṃ rathavinītaṃ abhiruheyya. chaṭṭhena rathavinītena sattamaṃ rathavinītaṃ pāpuṇeyya, chaṭṭhaṃ rathavinītaṃ vissajjeyya, sattamaṃ rathavinītaṃ abhiruheyya. sattamena rathavinītena sāketaṃ anupāpuṇeyya antepuradvāraṃ. tamenaṃ antepuradvāragataṃ samānaṃ mittāmaccā ñātisālohitā evaṃ puccheyyuṃ – ‘iminā tvaṃ, mahārāja, rathavinītena sāvatthiyā sāketaṃ anuppatto antepuradvāra’nti<a name="V1.0207"></a>? kathaṃ byākaramāno nu kho, āvuso, rājā pasenadi kosalo sammā byākaramāno byākareyyā"ti?</p>\r\n<p class="bodytext">evaṃ byākaramāno kho, āvuso, rājā pasenadi kosalo sammā byākaramāno byākareyya – ‘idha me sāvatthiyaṃ paṭivasantassa sākete kiñcideva accāyikaṃ karaṇīyaṃ uppajji <span class="note">[uppajjati (ka.)]</span>. tassa me antarā ca sāvatthiṃ antarā ca sāketaṃ satta rathavinītāni upaṭṭhapesuṃ. atha khvāhaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-259-');
INSERT INTO pages VALUES(3243,'mula_ma_01',204,replace(replace('<p class="bodytext">sāvatthiyā <a name="M1.0204"></a> nikkhamitvā antepuradvārā paṭhamaṃ rathavinītaṃ abhiruhiṃ. paṭhamena rathavinītena dutiyaṃ rathavinītaṃ pāpuṇiṃ, paṭhamaṃ rathavinītaṃ vissajjiṃ dutiyaṃ rathavinītaṃ abhiruhiṃ. dutiyena rathavinītena tatiyaṃ rathavinītaṃ pāpuṇiṃ, dutiyaṃ rathavinītaṃ vissajjiṃ, tatiyaṃ rathavinītaṃ abhiruhiṃ. tatiyena rathavinītena catutthaṃ rathavinītaṃ pāpuṇiṃ, tatiyaṃ rathavinītaṃ vissajjiṃ, catutthaṃ rathavinītaṃ abhiruhiṃ. catutthena rathavinītena pañcamaṃ rathavinītaṃ pāpuṇiṃ, catutthaṃ rathavinītaṃ vissajjiṃ, pañcamaṃ <a name="T1.0295"></a> rathavinītaṃ abhiruhiṃ. pañcamena rathavinītena chaṭṭhaṃ rathavinītaṃ pāpuṇiṃ, pañcamaṃ rathavinītaṃ vissajjiṃ, chaṭṭhaṃ rathavinītaṃ abhiruhiṃ. chaṭṭhena rathavinītena sattamaṃ rathavinītaṃ pāpuṇiṃ, chaṭṭhaṃ rathavinītaṃ vissajjiṃ, sattamaṃ rathavinītaṃ abhiruhiṃ. sattamena rathavinītena sāketaṃ anuppatto antepuradvāra’nti. evaṃ byākaramāno kho, āvuso, rājā pasenadi kosalo sammā byākaramāno byākareyyā"ti.</p>\r\n<p class="bodytext">evameva kho, āvuso, sīlavisuddhi yāvadeva cittavisuddhatthā, cittavisuddhi yāvadeva diṭṭhivisuddhatthā, diṭṭhivisuddhi yāvadeva kaṅkhāvitaraṇavisuddhatthā, kaṅkhāvitaraṇavisuddhi <a name="P1.0150"></a> yāvadeva maggāmaggañāṇadassanavisuddhatthā, maggāmaggañāṇadassanavisuddhi yāvadeva paṭipadāñāṇadassanavisuddhatthā, paṭipadāñāṇadassanavisuddhi yāvadeva ñāṇadassanavisuddhatthā, ñāṇadassanavisuddhi yāvadeva anupādāparinibbānatthā. anupādāparinibbānatthaṃ kho, āvuso, bhagavati brahmacariyaṃ vussatī"ti.</p>\r\n<p class="bodytext"><a name="para260"></a><a name="para260_mn1"></a><span class="paranum">260</span>. evaṃ vutte, āyasmā sāriputto āyasmantaṃ puṇṇaṃ mantāṇiputtaṃ etadavoca – "konāmo āyasmā, kathañca panāyasmantaṃ sabrahmacārī jānantī"ti? "puṇṇoti kho me, āvuso, nāmaṃ, mantāṇiputtoti ca pana maṃ sabrahmacārī jānantī"ti. "acchariyaṃ, āvuso, abbhutaṃ, āvuso! yathā taṃ sutavatā sāvakena sammadeva satthusāsanaṃ ājānantena, evameva āyasmatā <a name="V1.0208"></a> puṇṇena mantāṇiputtena gambhīrā <a name="T1.0296"></a> gambhīrapañhā anumassa anumassa byākatā. lābhā sabrahmacārīnaṃ, suladdhalābhā sabrahmacārīnaṃ, ye āyasmantaṃ puṇṇaṃ mantāṇiputtaṃ labhanti dassanāya, labhanti payirūpāsanāya. celaṇḍukena <span class="note">[celaṇḍakena (ka.), celaṇḍupekena (?)]</span> cepi sabrahmacārī āyasmantaṃ puṇṇaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-260-');
INSERT INTO pages VALUES(3244,'mula_ma_01',205,replace(replace('<p class="bodytext">mantāṇiputtaṃ <a name="M1.0205"></a> muddhanā pariharantā labheyyuṃ dassanāya, labheyyuṃ payirūpāsanāya, tesampi lābhā tesampi suladdhaṃ, amhākampi lābhā amhākampi suladdhaṃ, ye mayaṃ āyasmantaṃ puṇṇaṃ mantāṇiputtaṃ labhāma dassanāya, labhāma payirūpāsanāyā"ti.</p>\r\n<p class="bodytext">evaṃ vutte, āyasmā puṇṇo mantāṇiputto āyasmantaṃ sāriputtaṃ etadavoca – "ko nāmo āyasmā, kathañca panāyasmantaṃ sabrahmacārī jānantī"ti? "upatissoti kho me, āvuso, nāmaṃ, sāriputtoti ca pana maṃ sabrahmacārī jānantī"ti. "satthukappena vata kira, bho <span class="note">[kho (ka.)]</span>, sāvakena saddhiṃ mantayamānā na jānimha – ‘āyasmā sāriputto’ti. sace hi mayaṃ jāneyyāma ‘āyasmā sāriputto’ti, ettakampi no nappaṭibhāseyya <span class="note">[nappaṭibheyya (?)]</span>. acchariyaṃ, āvuso, abbhutaṃ, āvuso! yathā taṃ sutavatā sāvakena sammadeva satthusāsanaṃ ājānantena, evameva āyasmatā sāriputtena gambhīrā gambhīrapañhā anumassa anumassa pucchitā. lābhā sabrahmacārīnaṃ suladdhalābhā sabrahmacārīnaṃ, ye āyasmantaṃ sāriputtaṃ labhanti dassanāya, labhanti payirūpāsanāya. celaṇḍukena cepi sabrahmacārī āyasmantaṃ sāriputtaṃ muddhanā pariharantā <a name="T1.0297"></a> labheyyuṃ dassanāya, labheyyuṃ payirūpāsanāya, tesampi <a name="P1.0151"></a> lābhā tesampi suladdhaṃ, amhākampi lābhā amhākampi suladdhaṃ, ye mayaṃ āyasmantaṃ sāriputtaṃ labhāma dassanāya, labhāma payirūpāsanāyā"ti.</p>\r\n<p class="bodytext">itiha te ubhopi mahānāgā aññamaññassa subhāsitaṃ samanumodiṃsūti.</p>\r\n<p class="centered">rathavinītasuttaṃ niṭṭhitaṃ catutthaṃ.</p>\r\n<p class="subhead">5. nivāpasuttaṃ</p>\r\n<p class="bodytext"><a name="para261"></a><a name="para261_mn1"></a><span class="paranum">261</span>. evaṃ <a name="T1.0298"></a><a name="V1.0209"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n','\r',char(13)),'\n',char(10)),'-261-');
INSERT INTO pages VALUES(3245,'mula_ma_01',206,replace(replace('<p class="bodytext">na<a name="M1.0206"></a>, bhikkhave, nevāpiko nivāpaṃ nivapati migajātānaṃ – ‘imaṃ me nivāpaṃ nivuttaṃ migajātā paribhuñjantā dīghāyukā vaṇṇavanto ciraṃ dīghamaddhānaṃ yāpentū’ti. evañca kho, bhikkhave, nevāpiko nivāpaṃ nivapati migajātānaṃ – ‘imaṃ me nivāpaṃ nivuttaṃ migajātā anupakhajja mucchitā bhojanāni bhuñjissanti, anupakhajja mucchitā bhojanāni bhuñjamānā madaṃ āpajjissanti, mattā samānā pamādaṃ āpajjissanti, pamattā samānā yathākāmakaraṇīyā bhavissanti imasmiṃ nivāpe’ti.</p>\r\n<p class="bodytext"><a name="para262"></a><a name="para262_mn1"></a><span class="paranum">262</span>. tatra, bhikkhave, paṭhamā migajātā amuṃ nivāpaṃ nivuttaṃ nevāpikassa anupakhajja mucchitā bhojanāni bhuñjiṃsu, te tattha anupakhajja mucchitā bhojanāni bhuñjamānā madaṃ āpajjiṃsu, mattā samānā pamādaṃ āpajjiṃsu, pamattā samānā yathākāmakaraṇīyā ahesuṃ nevāpikassa amusmiṃ nivāpe. evañhi te, bhikkhave, paṭhamā migajātā na parimucciṃsu nevāpikassa iddhānubhāvā.</p>\r\n<p class="bodytext"><a name="para263"></a><a name="para263_mn1"></a><span class="paranum">263</span>. tatra, bhikkhave, dutiyā migajātā evaṃ samacintesuṃ – ‘ye kho <a name="T1.0299"></a> te paṭhamā migajātā amuṃ nivāpaṃ nivuttaṃ nevāpikassa anupakhajja mucchitā bhojanāni bhuñjiṃsu. te tattha anupakhajja mucchitā bhojanāni bhuñjamānā madaṃ āpajjiṃsu, mattā samānā pamādaṃ āpajjiṃsu, pamattā samānā yathākāmakaraṇīyā <a name="P1.0152"></a> ahesuṃ nevāpikassa amusmiṃ nivāpe. evañhi te paṭhamā migajātā na parimucciṃsu nevāpikassa iddhānubhāvā. yaṃnūna mayaṃ sabbaso nivāpabhojanā paṭivirameyyāma, bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihareyyāmā’ti. te sabbaso nivāpabhojanā paṭiviramiṃsu, bhayabhogā paṭiviratā araññāyatanāni <a name="V1.0210"></a> ajjhogāhetvā vihariṃsu. tesaṃ gimhānaṃ pacchime māse, tiṇodakasaṅkhaye, adhimattakasimānaṃ patto kāyo hoti. tesaṃ adhimattakasimānaṃ pattakāyānaṃ balavīriyaṃ parihāyi. balavīriye parihīne tameva nivāpaṃ nivuttaṃ nevāpikassa paccāgamiṃsu. te tattha anupakhajja mucchitā bhojanāni bhuñjiṃsu. te tattha anupakhajja mucchitā bhojanāni bhuñjamānā madaṃ āpajjiṃsu, mattā samānā pamādaṃ āpajjiṃsu, pamattā samānā yathākāmakaraṇīyā ahesuṃ nevāpikassa amusmiṃ nivāpe. evañhi te, bhikkhave, dutiyāpi migajātā na parimucciṃsu nevāpikassa iddhānubhāvā.</p>\r\n','\r',char(13)),'\n',char(10)),'-262-263-');
INSERT INTO pages VALUES(3246,'mula_ma_01',207,replace(replace('<p class="bodytext"><a name="para264"></a><a name="para264_mn1"></a><span class="paranum">264</span>. tatra<a name="M1.0207"></a>, bhikkhave, tatiyā migajātā evaṃ samacintesuṃ – ‘ye kho te paṭhamā migajātā amuṃ nivāpaṃ nivuttaṃ nevāpikassa…pe… evañhi te paṭhamā migajātā na parimucciṃsu nevāpikassa iddhānubhāvā. yepi te dutiyā migajātā evaṃ samacintesuṃ – ye kho te paṭhamā migajātā <a name="T1.0300"></a> amuṃ nivāpaṃ nivuttaṃ nevāpikassa…pe… evañhi te paṭhamā migajātā na parimucciṃsu nevāpikassa iddhānubhāvā. yaṃnūna mayaṃ sabbaso nivāpabhojanā paṭivirameyyāma, bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihareyyāmāti. te sabbaso nivāpabhojanā paṭiviramiṃsu, bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihariṃsu. tesaṃ gimhānaṃ pacchime māse tiṇodakasaṅkhaye adhimattakasimānaṃ patto kāyo hoti. tesaṃ adhimattakasimānaṃ pattakāyānaṃ balavīriyaṃ parihāyi. balavīriye parihīne tameva nivāpaṃ nivuttaṃ nevāpikassa paccāgamiṃsu. te tattha anupakhajja mucchitā bhojanāni bhuñjiṃsu. te tattha anupakhajja mucchitā bhojanāni bhuñjamānā madaṃ āpajjiṃsu, mattā samānā pamādaṃ āpajjiṃsu, pamattā samānā yathākāmakaraṇīyā ahesuṃ nevāpikassa amusmiṃ nivāpe. evañhi te dutiyāpi migajātā na parimucciṃsu nevāpikassa iddhānubhāvā. yaṃnūna mayaṃ amuṃ nivāpaṃ nivuttaṃ nevāpikassa upanissāya <a name="P1.0153"></a> āsayaṃ kappeyyāma. tatrāsayaṃ kappetvā amuṃ nivāpaṃ nivuttaṃ nevāpikassa ananupakhajja amucchitā bhojanāni bhuñjissāma, ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjissāma, amattā samānā na pamādaṃ āpajjissāma, appamattā samānā na yathākāmakaraṇīyā bhavissāma nevāpikassa amusmiṃ nivāpe’ti. te amuṃ nivāpaṃ nivuttaṃ nevāpikassa upanissāya āsayaṃ kappayiṃsu. tatrāsayaṃ kappetvā amuṃ nivāpaṃ nivuttaṃ nevāpikassa ananupakhajja <a name="T1.0301"></a> amucchitā bhojanāni bhuñjiṃsu, te tattha ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjiṃsu, amattā samānā <a name="V1.0211"></a> na pamādaṃ āpajjiṃsu, appamattā samānā na yathākāmakaraṇīyā ahesuṃ nevāpikassa amusmiṃ nivāpe.</p>\r\n<p class="bodytext">tatra, bhikkhave, nevāpikassa ca nevāpikaparisāya ca etadahosi – ‘saṭhāssunāmime tatiyā migajātā ketabino, iddhimantāssunāmime tatiyā migajātā parajanā, imañca nāma nivāpaṃ nivuttaṃ paribhuñjanti, na ca</p>\r\n','\r',char(13)),'\n',char(10)),'-264-');
INSERT INTO pages VALUES(3247,'mula_ma_01',208,replace(replace('<p class="bodytext">nesaṃ <a name="M1.0208"></a> jānāma āgatiṃ vā gatiṃ vā. yaṃnūna mayaṃ imaṃ nivāpaṃ nivuttaṃ mahatīhi daṇḍavākarāhi <span class="note">[daṇḍavāgurāhi (syā.)]</span> samantā sappadesaṃ anuparivāreyyāma – appeva nāma tatiyānaṃ migajātānaṃ āsayaṃ passeyyāma, yattha te gāhaṃ gaccheyyu’nti. te amuṃ nivāpaṃ nivuttaṃ mahatīhi daṇḍavākarāhi samantā sappadesaṃ anuparivāresuṃ. addasaṃsu kho, bhikkhave, nevāpiko ca nevāpikaparisā ca tatiyānaṃ migajātānaṃ āsayaṃ, yattha te gāhaṃ agamaṃsu. evañhi te, bhikkhave, tatiyāpi migajātā na parimucciṃsu nevāpikassa iddhānubhāvā.</p>\r\n<p class="bodytext"><a name="para265"></a><a name="para265_mn1"></a><span class="paranum">265</span>. tatra, bhikkhave, catutthā migajātā evaṃ samacintesuṃ – ‘ye kho te paṭhamā migajātā…pe… evañhi te paṭhamā migajātā na parimucciṃsu nevāpikassa iddhānubhāvā. yepi te dutiyā migajātā evaṃ samacintesuṃ ‘ye kho te paṭhamā migajātā…pe… evañhi te paṭhamā migajātā na parimucciṃsu nevāpikassa iddhānubhāvā. yaṃnūna mayaṃ <a name="T1.0302"></a> sabbaso nivāpabhojanā paṭivirameyyāma, bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihareyyāmā’ti. te sabbaso nivāpabhojanā paṭiviramiṃsu…pe… evañhi te dutiyāpi migajātā na parimucciṃsu nevāpikassa iddhānubhāvā. yepi te tatiyā migajātā evaṃ samacintesuṃ ‘ye kho te paṭhamā <a name="P1.0154"></a> migajātā…pe… evañhi te paṭhamā migajātā na parimucciṃsu nevāpikassa iddhānubhāvā. yepi te dutiyā migajātā evaṃ samacintesuṃ ‘ye kho te paṭhamā migajātā…pe… evañhi te paṭhamā migajātā na parimucciṃsu nevāpikassa iddhānubhāvā. yaṃnūna mayaṃ sabbaso nivāpabhojanā paṭivirameyyāma, bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihareyyāmā’ti. te sabbaso nivāpabhojanā paṭiviramiṃsu…pe… evañhi te dutiyāpi migajātā na parimucciṃsu nevāpikassa iddhānubhāvā. yaṃnūna mayaṃ amuṃ nivāpaṃ nivuttaṃ nevāpikassa upanissāya āsayaṃ kappeyyāma, tatrāsayaṃ kappetvā amuṃ nivāpaṃ nivuttaṃ nevāpikassa ananupakhajja amucchitā bhojanāni bhuñjissāma, ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjissāma, amattā <a name="V1.0212"></a> samānā na pamādaṃ āpajjissāma, appamattā samānā na yathākāmakaraṇīyā bhavissāma</p>\r\n','\r',char(13)),'\n',char(10)),'-265-');
INSERT INTO pages VALUES(3248,'mula_ma_01',209,replace(replace('<p class="bodytext">nevāpikassa <a name="M1.0209"></a> amusmiṃ nivāpe’ti. te amuṃ nivāpaṃ nivuttaṃ nevāpikassa upanissāya āsayaṃ kappayiṃsu, tatrāsayaṃ kappetvā amuṃ nivāpaṃ nivuttaṃ nevāpikassa ananupakhajja amucchitā bhojanāni bhuñjiṃsu, te tattha ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjiṃsu, amattā samānā na pamādaṃ āpajjiṃsu, appamattā samānā na yathākāmakaraṇīyā ahesuṃ nevāpikassa amusmiṃ nivāpe.</p>\r\n<p class="bodytext">tatra nevāpikassa ca nevāpikaparisāya ca etadahosi – ‘saṭhāssunāmime tatiyā migajātā ketabino, iddhimantāssunāmime tatiyā <a name="T1.0303"></a> migajātā parajanā, imañca nāma nivāpaṃ nivuttaṃ paribhuñjanti. na ca nesaṃ jānāma āgatiṃ vā gatiṃ vā. yaṃnūna mayaṃ imaṃ nivāpaṃ nivuttaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3249,'mula_ma_01',210,replace(replace('<p class="bodytext">mahatīti <a name="M1.0210"></a> daṇḍavākarāhi samantā sappadesaṃ anuparivāreyyāma, appeva nāma tatiyānaṃ migajātānaṃ āsayaṃ passeyyāma, yattha te gāhaṃ gaccheyyu’nti. te amuṃ nivāpaṃ nivuttaṃ mahatīti daṇḍavākarāhi samantā sappadesaṃ anuparivāresuṃ. addasaṃsu kho nevāpiko ca nevāpikaparisā ca tatiyānaṃ migajātānaṃ āsayaṃ, yattha te gāhaṃ agamaṃsu. evañhi te tatiyāpi migajātā na parimucciṃsu nevāpikassa iddhānubhāvā. yaṃnūna mayaṃ yattha agati nevāpikassa ca nevāpikaparisāya ca tatrāsayaṃ kappeyyāma, tatrāsayaṃ kappetvā amuṃ nivāpaṃ nivuttaṃ nevāpikassa ananupakhajja amucchitā bhojanāni bhuñjissāma, ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjissāma, amattā samānā na pamādaṃ āpajjissāma, appamattā samānā na yathākāmakaraṇīyā <a name="P1.0155"></a> bhavissāma nevāpikassa amusmiṃ nivāpe’ti. te yattha agati nevāpikassa ca nevāpikaparisāya ca tatrāsayaṃ kappayiṃsu. tatrāsayaṃ kappetvā amuṃ nivāpaṃ nivuttaṃ nevāpikassa ananupakhajja amucchitā bhojanāni bhuñjiṃsu, te tattha ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjiṃsu, amattā samānā na pamādaṃ āpajjiṃsu, appamattā samānā na yathākāmakaraṇīyā ahesuṃ nevāpikassa amusmiṃ nivāpe.</p>\r\n<p class="bodytext">tatra, bhikkhave, nevāpikassa ca nevāpikaparisāya ca etadahosi – ‘saṭhāssunāmime catutthā migajātā ketabino, iddhimantāssunāmime catutthā migajātā parajanā. imañca nāma nivāpaṃ nivuttaṃ paribhuñjanti, na ca nesaṃ jānāma āgatiṃ vā gatiṃ vā. yaṃnūna mayaṃ imaṃ nivāpaṃ <a name="V1.0213"></a> nivuttaṃ mahatīhi daṇḍavākarāhi samantā sappadesaṃ anuparivāreyyāma, appeva nāma catutthānaṃ migajātānaṃ āsayaṃ passeyyāma yattha te gāhaṃ gaccheyyu’nti. te <a name="T1.0304"></a> amuṃ nivāpaṃ nivuttaṃ mahatīhi daṇḍavākarāhi samantā sappadesaṃ anuparivāresuṃ. neva kho, bhikkhave, addasaṃsu nevāpiko ca nevāpikaparisā ca catutthānaṃ migajātānaṃ āsayaṃ, yattha te gāhaṃ gaccheyyuṃ. tatra, bhikkhave, nevāpikassa ca nevāpikaparisāya ca etadahosi – ‘sace kho mayaṃ catutthe migajāte ghaṭṭessāma, te ghaṭṭitā aññe ghaṭṭissanti te ghaṭṭitā aññe ghaṭṭissanti. evaṃ imaṃ nivāpaṃ nivuttaṃ sabbaso migajātā parimuñcissanti. yaṃnūna mayaṃ catutthe migajāte ajjhupekkheyyāmā’ti. ajjhupekkhiṃsu kho, bhikkhave, nevāpiko ca nevāpikaparisā ca catutthe migajāte. evañhi te, bhikkhave, catutthā migajātā parimucciṃsu nevāpikassa iddhānubhāvā.</p>\r\n<p class="bodytext"><a name="para266"></a><a name="para266_mn1"></a><span class="paranum">266</span>. upamā kho me ayaṃ, bhikkhave, katā atthassa viññāpanāya. ayaṃ cevettha attho – nivāpoti kho, bhikkhave, pañcannetaṃ kāmaguṇānaṃ adhivacanaṃ. nevāpikoti kho, bhikkhave, mārassetaṃ pāpimato adhivacanaṃ. nevāpikaparisāti kho, bhikkhave, māraparisāyetaṃ adhivacanaṃ. migajātāti kho, bhikkhave, samaṇabrāhmaṇānametaṃ adhivacanaṃ.</p>\r\n<p class="bodytext"><a name="para267"></a><a name="para267_mn1"></a><span class="paranum">267</span>. tatra, bhikkhave, paṭhamā samaṇabrāhmaṇā amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni anupakhajja mucchitā <a name="P1.0156"></a> bhojanāni bhuñjiṃsu. te tattha anupakhajja mucchitā bhojanāni bhuñjamānā madaṃ āpajjiṃsu, mattā samānā pamādaṃ āpajjiṃsu, pamattā samānā yathākāmakaraṇīyā ahesuṃ mārassa amusmiṃ nivāpe amusmiñca lokāmise<a name="T1.0305"></a>. evañhi te, bhikkhave, paṭhamā samaṇabrāhmaṇā na parimucciṃsu mārassa iddhānubhāvā. seyyathāpi te, bhikkhave, paṭhamā migajātā tathūpame ahaṃ ime paṭhame samaṇabrāhmaṇe vadāmi.</p>\r\n<p class="bodytext"><a name="para268"></a><a name="para268_mn1"></a><span class="paranum">268</span>. tatra, bhikkhave, dutiyā samaṇabrāhmaṇā evaṃ samacintesuṃ – ‘ye kho te paṭhamā samaṇabrāhmaṇā amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni anupakhajja mucchitā bhojanāni bhuñjiṃsu. te tattha anupakhajja mucchitā bhojanāni bhuñjamānā madaṃ āpajjiṃsu, mattā samānā</p>\r\n','\r',char(13)),'\n',char(10)),'-266-267-268-');
INSERT INTO pages VALUES(3250,'mula_ma_01',211,replace(replace('<p class="bodytext">pamādaṃ <a name="M1.0211"></a> āpajjiṃsu, pamattā samānā yathākāmakaraṇīyā ahesuṃ mārassa amusmiṃ nivāpe amusmiñca lokāmise. evañhi te paṭhamā samaṇabrāhmaṇā na parimucciṃsu <a name="V1.0214"></a> mārassa iddhānubhāvā. yaṃnūna mayaṃ sabbaso nivāpabhojanā lokāmisā paṭivirameyyāma, bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihareyyāmā’ti. te sabbaso nivāpabhojanā lokāmisā paṭiviramiṃsu, bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihareyyāmāti. te sabbaso nivāpabhojanā lokāmisā paṭiviramiṃsu, bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihariṃsu. te tattha sākabhakkhāpi ahesuṃ, sāmākabhakkhāpi ahesuṃ, nīvārabhakkhāpi ahesuṃ, daddulabhakkhāpi ahesuṃ, haṭabhakkhāpi ahesuṃ, kaṇabhakkhāpi ahesuṃ, ācāmabhakkhāpi ahesuṃ, piññākabhakkhāpi ahesuṃ, tiṇabhakkhāpi ahesuṃ, gomayabhakkhāpi ahesuṃ, vanamūlaphalāhārā yāpesuṃ pavattaphalabhojī.</p>\r\n<p class="bodytext">tesaṃ gimhānaṃ pacchime māse, tiṇodakasaṅkhaye, adhimattakasimānaṃ patto kāyo hoti. tesaṃ adhimattakasimānaṃ <a name="T1.0306"></a> pattakāyānaṃ balavīriyaṃ parihāyi. balavīriye parihīne cetovimutti parihāyi. cetovimuttiyā parihīnāya tameva nivāpaṃ nivuttaṃ mārassa paccāgamiṃsu tāni ca lokāmisāni. te tattha anupakhajja mucchitā bhojanāni bhuñjiṃsu. te tattha anupakhajja mucchitā bhojanāni bhuñjamānā madaṃ āpajjiṃsu, mattā samānā pamādaṃ āpajjiṃsu, pamattā samānā yathākāmakaraṇīyā ahesuṃ mārassa amusmiṃ nivāpe amusmiñca lokāmise. evañhi te, bhikkhave, dutiyāpi samaṇabrāhmaṇā na parimucciṃsu mārassa <a name="P1.0157"></a> iddhānubhāvā. seyyathāpi te, bhikkhave, dutiyā migajātā tathūpame ahaṃ ime dutiye samaṇabrāhmaṇe vadāmi.</p>\r\n<p class="bodytext"><a name="para269"></a><a name="para269_mn1"></a><span class="paranum">269</span>. tatra, bhikkhave, tatiyā samaṇabrāhmaṇā evaṃ samacintesuṃ – ‘ye kho te paṭhamā samaṇabrāhmaṇā amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni…pe…. evañhi te paṭhamā samaṇabrāhmaṇā na parimucciṃsu mārassa iddhānubhāvā. yepi te dutiyā samaṇabrāhmaṇā evaṃ samacintesuṃ – ‘ye kho te paṭhamā samaṇabrāhmaṇā amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni…pe…. evañhi te paṭhamā samaṇabrāhmaṇā na parimucciṃsu mārassa iddhānubhāvā. yaṃnūna mayaṃ sabbaso nivāpabhojanā lokāmisā paṭivirameyyāma, bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihareyyāmā’ti. te sabbaso nivāpabhojanā</p>\r\n','\r',char(13)),'\n',char(10)),'-269-');
INSERT INTO pages VALUES(3251,'mula_ma_01',212,replace(replace('<p class="bodytext">lokāmisā <a name="M1.0212"></a> paṭiviramiṃsu. bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihariṃsu. te tattha sākabhakkhāpi ahesuṃ…pe… pavattaphalabhojī. tesaṃ gimhānaṃ <a name="V1.0215"></a> pacchime māse tiṇodakasaṅkhaye adhimattakasimānaṃ <a name="T1.0307"></a> patto kāyo hoti. tesaṃ adhimattakasimānaṃ pattakāyānaṃ balavīriyaṃ parihāyi, balavīriye parihīne cetovimutti parihāyi, cetovimuttiyā parihīnāya tameva nivāpaṃ nivuttaṃ mārassa paccāgamiṃsu tāni ca lokāmisāni. te tattha anupakhajja mucchitā bhojanāni bhuñjiṃsu. te tattha anupakhajja mucchitā bhojanāni bhuñjamānā madaṃ āpajjiṃsu, mattā samānā pamādaṃ āpajjiṃsu, pamattā samānā yathākāmakaraṇīyā ahesuṃ mārassa amusmiṃ nivāpe amusmiñca lokāmise. evañhi te dutiyāpi samaṇabrāhmaṇā na parimucciṃsu mārassa iddhānubhāvā. yaṃnūna mayaṃ amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni upanissāya āsayaṃ kappeyyāma, tatrāsayaṃ kappetvā amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni ananupakhajja amucchitā bhojanāni bhuñjissāma, ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjissāma, amattā samānā na pamādaṃ āpajjissāma, appamattā samānā na yathākāmakaraṇīyā bhavissāma mārassa amusmiṃ nivāpe amusmiñca lokāmise"ti.</p>\r\n<p class="bodytext">te amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni upanissāya āsayaṃ kappayiṃsu. tatrāsayaṃ kappetvā amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni ananupakhajja amucchitā bhojanāni bhuñjiṃsu. te tattha ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjiṃsu, amattā samānā na pamādaṃ āpajjiṃsu, appamattā samānā na yathākāmakaraṇīyā ahesuṃ mārassa amusmiṃ nivāpe amusmiñca lokāmise<a name="T1.0308"></a>. api ca kho evaṃdiṭṭhikā ahesuṃ – sassato loko itipi, asassato loko itipi, antavā loko itipi, anantavā loko itipi, taṃ jīvaṃ taṃ sarīraṃ itipi, aññaṃ jīvaṃ aññaṃ sarīraṃ itipi, hoti tathāgato paraṃ maraṇā itipi, na hoti tathāgato paraṃ maraṇā itipi, hoti ca na ca hoti tathāgato paraṃ maraṇā itipi, neva hoti na na hoti tathāgato paraṃ maraṇā itipi<a name="P1.0158"></a>. evañhi te, bhikkhave, tatiyāpi</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3252,'mula_ma_01',213,replace(replace('<p class="bodytext">samaṇabrāhmaṇā <a name="M1.0213"></a> na parimucciṃsu mārassa iddhānubhāvā. seyyathāpi te, bhikkhave, tatiyā migajātā tathūpame ahaṃ ime tatiye samaṇabrāhmaṇe vadāmi.</p>\r\n<p class="bodytext"><a name="para270"></a><a name="para270_mn1"></a><span class="paranum">270</span>. tatra, bhikkhave, catutthā samaṇabrāhmaṇā evaṃ samacintesuṃ – ‘ye kho te paṭhamā samaṇabrāhmaṇā amuṃ nivāpaṃ nivuttaṃ mārassa…pe…. evañhi te paṭhamā samaṇabrāhmaṇā na parimucciṃsu <a name="V1.0216"></a> mārassa iddhānubhāvā. yepi te dutiyā samaṇabrāhmaṇā evaṃ samacintesuṃ – ‘ye kho te paṭhamā samaṇabrāhmaṇā…pe…. evañhi te paṭhamā samaṇabrāhmaṇā na parimucciṃsu mārassa iddhānubhāvā. yaṃnūna mayaṃ sabbaso nivāpabhojanā lokāmisā paṭivirameyyāma bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihareyyāmā’ti. te sabbaso nivāpabhojanā lokāmisā paṭiviramiṃsu…pe…. evañhi te dutiyāpi samaṇabrāhmaṇā na parimucciṃsu mārassa iddhānubhāvā. yepi te tatiyā samaṇabrāhmaṇā evaṃ samacintesuṃ ye kho te paṭhamā samaṇabrāhmaṇā <a name="T1.0309"></a> …pe…. evañhi te paṭhamā samaṇabrāhmaṇā na parimucciṃsu mārassa iddhānubhāvā. yepi te dutiyā samaṇabrāhmaṇā evaṃ samacintesuṃ ye kho te paṭhamā samaṇabrāhmaṇā…pe…. evañhi te paṭhamā samaṇabrāhmaṇā na parimucciṃsu mārassa iddhānubhāvā. yaṃnūna mayaṃ sabbaso nivāpabhojanā lokāmisā paṭivirameyyāma, bhayabhogā paṭiviratā araññāyatanāni ajjhogāhetvā vihareyyāmā’ti. te sabbaso nivāpabhojanā lokāmisā paṭiviramiṃsu…pe…. evañhi te dutiyāpi samaṇabrāhmaṇā na parimucciṃsu mārassa iddhānubhāvā. yaṃnūna mayaṃ amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni upanissāya āsayaṃ kappeyyāma. tatrāsayaṃ kappetvā amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni ananupakhajja amucchitā bhojanāni bhuñjissāma, ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjissāma, amattā samānā na pamādaṃ āpajjissāma, appamattā samānā na yathākāmakaraṇīyā bhavissāma mārassa amusmiṃ nivāpe amusmiñca lokāmiseti.</p>\r\n<p class="bodytext">te amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni upanissāya āsayaṃ kappayiṃsu. tatrāsayaṃ kappetvā amuṃ nivāpaṃ nivuttaṃ mārassa amūni ca lokāmisāni ananupakhajja amucchitā bhojanāni bhuñjiṃsu. te tattha ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjiṃsu. amattā</p>\r\n','\r',char(13)),'\n',char(10)),'-270-');
INSERT INTO pages VALUES(3253,'mula_ma_01',214,replace(replace('<p class="bodytext">samānā <a name="M1.0214"></a> na pamādaṃ āpajjiṃsu. appamattā samānā na yathākāmakaraṇīyā ahesuṃ mārassa amusmiṃ nivāpe amusmiñca lokāmise. api ca kho evaṃdiṭṭhikā ahesuṃ sassato loko itipi…pe… neva hoti na na hoti tathāgato paraṃ maraṇā itipi. evañhi te tatiyāpi samaṇabrāhmaṇā na parimucciṃsu mārassa iddhānubhāvā. yaṃnūna mayaṃ yattha agati mārassa ca māraparisāya ca tatrāsayaṃ kappeyāma. tatrāsayaṃ kappetvā amuṃ nivāpaṃ nivuttaṃ mārassa amūni <a name="T1.0310"></a> ca lokāmisāni ananupakhajja amucchitā bhojanāni bhuñjissāma, ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjissāma, amattā samānā na pamādaṃ āpajjissāma, appamattā samānā na yathākāmakaraṇīyā bhavissāma mārassa amusmiṃ nivāpe amusmiñca lokāmiseti.</p>\r\n<p class="bodytext">te <a name="V1.0217"></a> yattha agati mārassa ca māraparisāya ca tatrāsayaṃ kappayiṃsu. tatrāsayaṃ kappetvā amuṃ nivāpaṃ <a name="P1.0159"></a> nivuttaṃ mārassa amūni ca lokāmisāni ananupakhajja amucchitā bhojanāni bhuñjiṃsu, te tattha ananupakhajja amucchitā bhojanāni bhuñjamānā na madaṃ āpajjiṃsu, amattā samānā na pamādaṃ āpajjiṃsu, appamattā samānā na yathākāmakaraṇīyā ahesuṃ mārassa amusmiṃ nivāpe amusmiñca lokāmise. evañhi te, bhikkhave, catutthā samaṇabrāhmaṇā parimucciṃsu mārassa iddhānubhāvā. seyyathāpi te, bhikkhave, catutthā migajātā tathūpame ahaṃ ime catutthe samaṇabrāhmaṇe vadāmi.</p>\r\n<p class="bodytext"><a name="para271"></a><a name="para271_mn1"></a><span class="paranum">271</span>. kathañca, bhikkhave, agati mārassa ca māraparisāya ca? idha, bhikkhave, bhikkhu vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave, bhikkhu andhamakāsi māraṃ, apadaṃ vadhitvā māracakkhuṃ adassanaṃ gato pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu pītiyā ca virāgā upekkhako ca viharati sato ca sampajāno, sukhañca kāyena paṭisaṃvedeti yaṃ taṃ ariyā</p>\r\n','\r',char(13)),'\n',char(10)),'-271-');
INSERT INTO pages VALUES(3254,'mula_ma_01',215,replace(replace('<p class="bodytext">ācikkhanti <a name="M1.0215"></a> ‘upekkhako satimā sukhavihārī’ti tatiyaṃ jhānaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sukhassa ca pahānā dukkhassa ca pahānā, pubbeva somanassadomanassānaṃ atthaṅgamā, adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ <a name="T1.0311"></a> upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sabbaso rūpasaññānaṃ samatikkamā paṭighasaññānaṃ atthaṅgamā <a name="V1.0218"></a> nānattasaññānaṃ amanasikārā ‘ananto ākāso’ti ākāsānañcāyatanaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sabbaso ākāsānañcāyatanaṃ samatikkamma ‘anantaṃ viññāṇa’nti viññāṇañcāyatanaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sabbaso viññāṇañcāyatanaṃ samatikkamma <a name="P1.0160"></a> ‘natthi kiñcī’ti ākiñcaññāyatanaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sabbaso ākiñcaññāyatanaṃ samatikkamma nevasaññānāsaññāyatanaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sabbaso nevasaññānāsaññāyatanaṃ samatikkamma saññāvedayitanirodhaṃ upasampajja viharati. paññāya cassa disvā āsavā parikkhīṇā honti. ayaṃ vuccati, bhikkhave, bhikkhu andhamakāsi māraṃ, apadaṃ vadhitvā māracakkhuṃ adassanaṃ gato pāpimato tiṇṇo loke visattika"nti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">nivāpasuttaṃ niṭṭhitaṃ pañcamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3255,'mula_ma_01',216,replace(replace('<p class="subhead">6. pāsarāsisuttaṃ</p>\r\n<p class="bodytext"><a name="para272"></a><a name="para272_mn1"></a><span class="paranum">272</span>. evaṃ <a name="M1.0216"></a><a name="V1.0219"></a><a name="T1.0312"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. atha kho bhagavā pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya sāvatthiṃ piṇḍāya pāvisi. atha kho sambahulā bhikkhū yenāyasmā ānando tenupasaṅkamiṃsu, upasaṅkamitvā āyasmantaṃ ānandaṃ etadavocuṃ – "cirassutā no, āvuso ānanda, bhagavato sammukhā dhammī kathā. sādhu mayaṃ, āvuso ānanda, labheyyāma bhagavato sammukhā dhammiṃ kathaṃ savanāyā"ti. "tena hāyasmanto yena rammakassa brāhmaṇassa assamo tenupasaṅkamatha, appeva nāma labheyyātha bhagavato sammukhā dhammiṃ kathaṃ savanāyā"ti. "evamāvuso"ti kho te bhikkhū āyasmato ānandassa paccassosuṃ.</p>\r\n<p class="bodytext">atha kho bhagavā sāvatthiyaṃ piṇḍāya caritvā pacchābhattaṃ piṇḍapātapaṭikkanto āyasmantaṃ ānandaṃ āmantesi – "āyāmānanda, yena pubbārāmo migāramātupāsādo tenupasaṅkamissāma divāvihārāyā"ti. "evaṃ, bhante"ti kho āyasmā ānando bhagavato paccassosi. atha <a name="P1.0161"></a> kho bhagavā āyasmatā ānandena saddhiṃ yena pubbārāmo migāramātupāsādo tenupasaṅkami divāvihārāya. atha kho bhagavā sāyanhasamayaṃ paṭisallānā vuṭṭhito āyasmantaṃ ānandaṃ āmantesi – "āyāmānanda, yena <a name="T1.0313"></a> pubbakoṭṭhako tenupasaṅkamissāma gattāni parisiñcitu"nti. "evaṃ, bhante"ti kho āyasmā ānando bhagavato paccassosi.</p>\r\n<p class="bodytext"><a name="para273"></a><a name="para273_mn1"></a><span class="paranum">273</span>. atha kho bhagavā āyasmatā ānandena saddhiṃ yena pubbakoṭṭhako tenupasaṅkami gattāni parisiñcituṃ. pubbakoṭṭhake gattāni parisiñcitvā paccuttaritvā ekacīvaro aṭṭhāsi gattāni pubbāpayamāno. atha kho āyasmā ānando bhagavantaṃ etadavoca – "ayaṃ, bhante, rammakassa brāhmaṇassa assamo avidūre. ramaṇīyo, bhante, rammakassa brāhmaṇassa assamo, pāsādiko, bhante, rammakassa brāhmaṇassa assamo. sādhu, bhante, bhagavā yena rammakassa brāhmaṇassa assamo tenupasaṅkamatu anukampaṃ upādāyā"ti. adhivāsesi bhagavā tuṇhībhāvena.</p>\r\n','\r',char(13)),'\n',char(10)),'-272-273-');
INSERT INTO pages VALUES(3256,'mula_ma_01',217,replace(replace('<p class="bodytext">atha <a name="M1.0217"></a><a name="V1.0220"></a> kho bhagavā yena rammakassa brāhmaṇassa assamo tenupasaṅkami. tena kho pana samayena sambahulā bhikkhū rammakassa brāhmaṇassa assame dhammiyā kathāya sannisinnā honti. atha kho bhagavā bahidvārakoṭṭhake aṭṭhāsi kathāpariyosānaṃ āgamayamāno. atha kho bhagavā kathāpariyosānaṃ viditvā ukkāsitvā aggaḷaṃ ākoṭesi. vivariṃsu kho te bhikkhū bhagavato dvāraṃ. atha kho bhagavā rammakassa brāhmaṇassa assamaṃ pavisitvā paññatte āsane nisīdi. nisajja kho bhagavā bhikkhū āmantesi – "kāyanuttha, bhikkhave, etarahi kathāya sannisinnā? kā ca pana vo antarākathā vippakatā"ti<a name="T1.0314"></a>? "bhagavantameva kho no, bhante, ārabbha dhammī kathā vippakatā, atha bhagavā anuppatto"ti. "sādhu, bhikkhave! etaṃ kho, bhikkhave, tumhākaṃ patirūpaṃ kulaputtānaṃ saddhā agārasmā anagāriyaṃ pabbajitānaṃ yaṃ tumhe dhammiyā kathāya sannisīdeyyātha. sannipatitānaṃ vo, bhikkhave, dvayaṃ karaṇīyaṃ – dhammī vā kathā, ariyo vā tuṇhībhāvo".</p>\r\n<p class="bodytext"><a name="para274"></a><a name="para274_mn1"></a><span class="paranum">274</span>. dvemā, bhikkhave, pariyesanā – ariyā ca pariyesanā, anariyā ca pariyesanā.</p>\r\n<p class="bodytext">katamā ca, bhikkhave, anariyā pariyesanā? idha, bhikkhave, ekacco attanā jātidhammo samāno jātidhammaṃyeva pariyesati, attanā jarādhammo samāno jarādhammaṃyeva <a name="P1.0162"></a> pariyesati, attanā byādhidhammo samāno byādhidhammaṃyeva pariyesati, attanā maraṇadhammo samāno maraṇadhammaṃyeva pariyesati, attanā sokadhammo samāno sokadhammaṃyeva pariyesati, attanā saṃkilesadhammo samāno saṃkilesadhammaṃyeva pariyesati.</p>\r\n<p class="bodytext">kiñca, bhikkhave, jātidhammaṃ vadetha? puttabhariyaṃ, bhikkhave, jātidhammaṃ, dāsidāsaṃ jātidhammaṃ, ajeḷakaṃ jātidhammaṃ, kukkuṭasūkaraṃ jātidhammaṃ, hatthigavāssavaḷavaṃ jātidhammaṃ, jātarūparajataṃ jātidhammaṃ. jātidhammā hete, bhikkhave, upadhayo. etthāyaṃ gathito <span class="note">[gadhīto (syā. ka.)]</span> mucchito ajjhāpanno attanā jātidhammo samāno jātidhammaṃyeva pariyesati.</p>\r\n<p class="bodytext">kiñca, bhikkhave, jarādhammaṃ vadetha? puttabhariyaṃ, bhikkhave, jarādhammaṃ, dāsidāsaṃ jarādhammaṃ, ajeḷakaṃ jarādhammaṃ, kukkuṭasūkaraṃ <a name="T1.0315"></a> jarādhammaṃ, hatthigavāssavaḷavaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-274-');
INSERT INTO pages VALUES(3257,'mula_ma_01',218,replace(replace('<p class="bodytext">jarādhammaṃ<a name="M1.0218"></a>, jātarūparajataṃ <a name="V1.0221"></a> jarādhammaṃ. jarādhammā hete, bhikkhave, upadhayo. etthāyaṃ gathito mucchito ajjhāpanno attanā jarādhammo samāno jarādhammaṃyeva pariyesati.</p>\r\n<p class="bodytext">kiñca, bhikkhave, byādhidhammaṃ vadetha? puttabhariyaṃ, bhikkhave, byādhidhammaṃ, dāsidāsaṃ byādhidhammaṃ, ajeḷakaṃ byādhidhammaṃ, kukkuṭasūkaraṃ byādhidhammaṃ, hatthigavāssavaḷavaṃ byādhidhammaṃ. byādhidhammā hete, bhikkhave, upadhayo. etthāyaṃ gathito mucchito ajjhāpanno attanā byādhidhammo samāno byādhidhammaṃyeva pariyesati.</p>\r\n<p class="bodytext">kiñca, bhikkhave, maraṇadhammaṃ vadetha? puttabhariyaṃ, bhikkhave, maraṇadhammaṃ, dāsidāsaṃ maraṇadhammaṃ, ajeḷakaṃ maraṇadhammaṃ, kukkuṭasūkaraṃ maraṇadhammaṃ, hatthigavāssavaḷavaṃ maraṇadhammaṃ. maraṇadhammā hete, bhikkhave, upadhayo. etthāyaṃ gathito mucchito ajjhāpanno attanā maraṇadhammo samāno maraṇadhammaṃyeva pariyesati.</p>\r\n<p class="bodytext">kiñca, bhikkhave, sokadhammaṃ vadetha? puttabhariyaṃ, bhikkhave, sokadhammaṃ, dāsidāsaṃ sokadhammaṃ, ajeḷakaṃ sokadhammaṃ, kukkuṭasūkaraṃ sokadhammaṃ, hatthigavāssavaḷavaṃ sokadhammaṃ. sokadhammā hete, bhikkhave, upadhayo. etthāyaṃ gathito mucchito ajjhāpanno attanā sokadhammo samāno sokadhammaṃyeva pariyesati.</p>\r\n<p class="bodytext">kiñca, bhikkhave, saṃkilesadhammaṃ vadetha? puttabhariyaṃ, bhikkhave, saṃkilesadhammaṃ, dāsidāsaṃ saṃkilesadhammaṃ, ajeḷakaṃ saṃkilesadhammaṃ<a name="T1.0316"></a>, kukkuṭasūkaraṃ saṃkilesadhammaṃ, hatthigavāssavaḷavaṃ saṃkilesadhammaṃ, jātarūparajataṃ saṃkilesadhammaṃ. saṃkilesadhammā hete, bhikkhave, upadhayo. etthāyaṃ gathito mucchito ajjhāpanno attanā saṃkilesadhammo samāno saṃkilesadhammaṃyeva pariyesati. ayaṃ, bhikkhave, anariyā pariyesanā.</p>\r\n<p class="bodytext"><a name="para275"></a><a name="para275_mn1"></a><span class="paranum">275</span>. katamā ca, bhikkhave, ariyā pariyesanā? idha, bhikkhave, ekacco attanā jātidhammo samāno jātidhamme ādīnavaṃ viditvā <a name="P1.0163"></a> ajātaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesati, attanā jarādhammo samāno jarādhamme ādīnavaṃ viditvā ajaraṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesati, attanā byādhidhammo samāno byādhidhamme ādīnavaṃ viditvā abyādhiṃ anuttaraṃ <a name="V1.0222"></a> yogakkhemaṃ nibbānaṃ pariyesati, attanā maraṇadhammo samāno</p>\r\n','\r',char(13)),'\n',char(10)),'-275-');
INSERT INTO pages VALUES(3258,'mula_ma_01',219,replace(replace('<p class="bodytext">maraṇadhamme <a name="M1.0219"></a> ādīnavaṃ viditvā amataṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesati, attanā sokadhammo samāno sokadhamme ādīnavaṃ viditvā asokaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesati, attanā saṃkilesadhammo samāno saṃkilesadhamme ādīnavaṃ viditvā asaṃkiliṭṭhaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesati. ayaṃ, bhikkhave, ariyā pariyesanā.</p>\r\n<p class="bodytext"><a name="para276"></a><a name="para276_mn1"></a><span class="paranum">276</span>. ahampi sudaṃ, bhikkhave, pubbeva sambodhā anabhisambuddho bodhisattova samāno attanā jātidhammo samāno jātidhammaṃyeva pariyesāmi, attanā jarādhammo samāno jarādhammaṃyeva pariyesāmi, attanā <a name="T1.0317"></a> byādhidhammo samāno byādhidhammaṃyeva pariyesāmi, attanā maraṇadhammo samāno maraṇadhammaṃyeva pariyesāmi, attanā sokadhammo samāno sokadhammaṃyeva pariyesāmi, attanā saṃkilesadhammo samāno saṃkilesadhammaṃyeva pariyesāmi. tassa mayhaṃ, bhikkhave, etadahosi – ‘kiṃ nu kho ahaṃ attanā jātidhammo samāno jātidhammaṃyeva pariyesāmi, attanā jarādhammo samāno…pe… byādhidhammo samāno… maraṇadhammo samāno… sokadhammo samāno… attanā saṃkilesadhammo samāno saṃkilesadhammaṃyeva pariyesāmi? yaṃnūnāhaṃ attanā jātidhammo samāno jātidhamme ādīnavaṃ viditvā ajātaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyeseyyaṃ, attanā jarādhammo samāno jarādhamme ādīnavaṃ viditvā ajaraṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyeseyyaṃ, attanā byādhidhammo samāno byādhidhamme ādīnavaṃ viditvā abyādhiṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyeseyyaṃ, attanā maraṇadhammo samāno maraṇadhamme ādīnavaṃ viditvā amataṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyeseyyaṃ, attanā sokadhammo samāno sokadhamme ādīnavaṃ viditvā asokaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyeseyyaṃ, attanā saṃkilesadhammo samāno saṃkilesadhamme ādīnavaṃ viditvā asaṃkiliṭṭhaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyeseyya’nti.</p>\r\n<p class="bodytext"><a name="para277"></a><a name="para277_mn1"></a><span class="paranum">277</span>. so kho ahaṃ, bhikkhave, aparena samayena daharova samāno susukāḷakeso<a name="T1.0318"></a>, bhadrena yobbanena samannāgato paṭhamena vayasā akāmakānaṃ mātāpitūnaṃ assumukhānaṃ rudantānaṃ kesamassuṃ ohāretvā</p>\r\n','\r',char(13)),'\n',char(10)),'-276-277-');
INSERT INTO pages VALUES(3259,'mula_ma_01',220,replace(replace('<p class="bodytext">kāsāyāni <a name="M1.0220"></a> vatthāni acchādetvā agārasmā anagāriyaṃ pabbajiṃ. so evaṃ pabbajito samāno kiṃ kusalagavesī <span class="note">[kiṃkusalaṃgavesī (ka.)]</span> anuttaraṃ santivarapadaṃ pariyesamāno yena āḷāro kālāmo tenupasaṅkamiṃ. upasaṅkamitvā āḷāraṃ kālāmaṃ etadavocaṃ – ‘icchāmahaṃ, āvuso kālāma, imasmiṃ dhammavinaye <a name="V1.0223"></a> brahmacariyaṃ caritu’nti. evaṃ vutte, bhikkhave, āḷāro kālāmo maṃ etadavoca – ‘viharatāyasmā, tādiso ayaṃ dhammo yattha viññū puriso <a name="P1.0164"></a> nacirasseva sakaṃ ācariyakaṃ sayaṃ abhiññā sacchikatvā upasampajja vihareyyā’ti. so kho ahaṃ, bhikkhave, nacirasseva khippameva taṃ dhammaṃ pariyāpuṇiṃ. so kho ahaṃ, bhikkhave, tāvatakeneva oṭṭhapahatamattena lapitalāpanamattena ñāṇavādañca vadāmi theravādañca, ‘jānāmi passāmī’ti ca paṭijānāmi ahañceva aññe ca. tassa mayhaṃ, bhikkhave, etadahosi – ‘na kho āḷāro kālāmo imaṃ dhammaṃ kevalaṃ saddhāmattakena sayaṃ abhiññā sacchikatvā upasampajja viharāmīti pavedeti, addhā āḷāro kālāmo imaṃ dhammaṃ jānaṃ passaṃ viharatī’ti.</p>\r\n<p class="bodytext">atha khvāhaṃ, bhikkhave, yena āḷāro kālāmo tenupasaṅkamiṃ, upasaṅkamitvā āḷāraṃ kālāmaṃ etadavocaṃ – ‘kittāvatā no, āvuso kālāma, imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharāmīti pavedesī’ti <span class="note">[upasampajja pavedesīti (sī. syā. pī.)]</span>? evaṃ vutte, bhikkhave, āḷāro kālāmo ākiñcaññāyatanaṃ <a name="T1.0319"></a> pavedesi. tassa mayhaṃ, bhikkhave, etadahosi – ‘na kho āḷārasseva kālāmassa atthi saddhā, mayhaṃpatthi saddhā, na kho āḷārasseva kālāmassa atthi vīriyaṃ, mayhaṃpatthi vīriyaṃ, na kho āḷārasseva kālāmassa atthi sati, mayhaṃpatthi sati, na kho āḷārasseva kālāmassa atthi samādhi, mayhaṃpatthi samādhi, na kho āḷārasseva kālāmassa atthi paññā, mayhaṃpatthi paññā. yaṃnūnāhaṃ yaṃ dhammaṃ āḷāro kālāmo sayaṃ abhiññā sacchikatvā upasampajja viharāmīti pavedeti, tassa dhammassa sacchikiriyāya padaheyya’nti. so kho ahaṃ, bhikkhave, nacirasseva khippameva taṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja vihāsiṃ.</p>\r\n<p class="bodytext">atha khvāhaṃ, bhikkhave, yena āḷāro kālāmo tenupasaṅkamiṃ, upasaṅkamitvā āḷāraṃ kālāmaṃ etadavocaṃ –</p>\r\n<p class="bodytext">‘ettāvatā no, āvuso kālāma, imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja pavedesī’ti?</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3260,'mula_ma_01',221,replace(replace('<p class="bodytext">‘ettāvatā <a name="M1.0221"></a> kho ahaṃ, āvuso, imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja pavedemī’ti.</p>\r\n<p class="bodytext">‘ahampi <a name="V1.0224"></a> kho, āvuso, ettāvatā imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharāmī’ti.</p>\r\n<p class="bodytext">‘lābhā no, āvuso, suladdhaṃ no, āvuso, ye mayaṃ āyasmantaṃ tādisaṃ sabrahmacāriṃ passāma. iti yāhaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja pavedemi taṃ tvaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharasi. yaṃ tvaṃ <a name="P1.0165"></a> dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharasi tamahaṃ dhammaṃ sayaṃ abhiññā <a name="T1.0320"></a> sacchikatvā upasampajja pavedemi. iti yāhaṃ dhammaṃ jānāmi taṃ tvaṃ dhammaṃ jānāsi, yaṃ tvaṃ dhammaṃ jānāsi tamahaṃ dhammaṃ jānāmi. iti yādiso ahaṃ tādiso tuvaṃ, yādiso tuvaṃ tādiso ahaṃ. ehi dāni, āvuso, ubhova santā imaṃ gaṇaṃ pariharāmā’ti. iti kho, bhikkhave, āḷāro kālāmo ācariyo me samāno (attano) <span class="note">[( ) natthi (sī. syā. pī.)]</span> antevāsiṃ maṃ samānaṃ attanā <span class="note">[attano (sī. pī.)]</span> samasamaṃ ṭhapesi, uḷārāya ca maṃ pūjāya pūjesi. tassa mayhaṃ, bhikkhave, etadahosi – ‘nāyaṃ dhammo nibbidāya na virāgāya na nirodhāya na upasamāya na abhiññāya na sambodhāya na nibbānāya saṃvattati, yāvadeva ākiñcaññāyatanūpapattiyā’ti. so kho ahaṃ, bhikkhave, taṃ dhammaṃ analaṅkaritvā tasmā dhammā nibbijja apakkamiṃ.</p>\r\n<p class="bodytext"><a name="para278"></a><a name="para278_mn1"></a><span class="paranum">278</span>. so kho ahaṃ, bhikkhave, kiṃ kusalagavesī anuttaraṃ santivarapadaṃ pariyesamāno yena udako <span class="note">[uddako (sī. syā. pī.)]</span> rāmaputto tenupasaṅkamiṃ, upasaṅkamitvā udakaṃ rāmaputtaṃ etadavocaṃ – ‘icchāmahaṃ, āvuso <span class="note">[āvuso rāma (sī. syā. ka.) mahāsatto rāmaputtameva avoca, na rāmaṃ, rāmo hi tattha gaṇācariyo bhaveyya, tadā ca kālaṅkato asanto. tenevettha rāmāyattāni kriyapadāni atītakālavasena āgatāni, udako ca rāmaputto mahāsattassa sabrahmacārītveva vutto, na ācariyoti. ṭīkāyaṃ ca "pāḷiyaṃ rāmasseva samāpattilābhitā āgatā na udakassā"ti ādi pacchābhāge pakāsitā]</span>, imasmiṃ dhammavinaye brahmacariyaṃ caritu’nti. evaṃ vutte, bhikkhave, udako rāmaputto maṃ etadavoca – ‘viharatāyasmā, tādiso ayaṃ dhammo yattha viññū puriso nacirasseva sakaṃ ācariyakaṃ sayaṃ abhiññā sacchikatvā upasampajja vihareyyā’ti. so kho ahaṃ, bhikkhave, nacirasseva khippameva taṃ dhammaṃ pariyāpuṇiṃ. so</p>\r\n','\r',char(13)),'\n',char(10)),'-278-');
INSERT INTO pages VALUES(3261,'mula_ma_01',222,replace(replace('<p class="bodytext">kho <a name="M1.0222"></a> ahaṃ, bhikkhave, tāvatakeneva oṭṭhapahatamattena lapitalāpanamattena ñāṇavādañca vadāmi <a name="T1.0321"></a> theravādañca, ‘jānāmi passāmī’ti ca paṭijānāmi ahañceva aññe ca. tassa mayhaṃ, bhikkhave, etadahosi – ‘na kho rāmo imaṃ dhammaṃ kevalaṃ saddhāmattakena sayaṃ abhiññā sacchikatvā upasampajja viharāmīti pavedesi, addhā rāmo imaṃ dhammaṃ jānaṃ passaṃ vihāsī’ti.</p>\r\n<p class="bodytext">atha khvāhaṃ, bhikkhave, yena udako rāmaputto tenupasaṅkamiṃ, upasaṅkamitvā udakaṃ rāmaputtaṃ etadavocaṃ – ‘kittāvatā no, āvuso, rāmo imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja <a name="V1.0225"></a> viharāmīti pavedesī’ti? evaṃ vutte, bhikkhave, udako rāmaputto nevasaññānāsaññāyatanaṃ pavedesi. tassa mayhaṃ, bhikkhave, etadahosi – ‘na kho rāmasseva ahosi saddhā, mayhaṃpatthi saddhā, na kho rāmasseva ahosi vīriyaṃ<a name="P1.0166"></a>, mayhaṃpatthi vīriyaṃ, na kho rāmasseva ahosi sati, mayhaṃpatthi sati, na kho rāmasseva ahosi samādhi, mayhaṃpatthi samādhi, na kho rāmasseva ahosi paññā, mayhaṃpatthi paññā. yaṃnūnāhaṃ yaṃ dhammaṃ rāmo sayaṃ abhiññā sacchikatvā upasampajja viharāmīti pavedesi, tassa dhammassa sacchikiriyāya padaheyya’nti. so kho ahaṃ, bhikkhave, nacirasseva khippameva taṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja vihāsiṃ.</p>\r\n<p class="bodytext">atha khvāhaṃ, bhikkhave, yena udako rāmaputto tenupasaṅkamiṃ, upasaṅkamitvā udakaṃ rāmaputtaṃ etadavocaṃ –</p>\r\n<p class="bodytext">‘ettāvatā no, āvuso, rāmo imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja pavedesī’ti?</p>\r\n<p class="bodytext">‘ettāvatā kho, āvuso, rāmo imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja pavedesī’ti.</p>\r\n<p class="bodytext">‘ahampi kho, āvuso, ettāvatā imaṃ <a name="T1.0322"></a> dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharāmī’ti.</p>\r\n<p class="bodytext">‘lābhā no, āvuso, suladdhaṃ no, āvuso, ye mayaṃ āyasmantaṃ tādisaṃ sabrahmacāriṃ passāma. iti yaṃ dhammaṃ rāmo sayaṃ abhiññā sacchikatvā upasampajja pavedesi, taṃ tvaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharasi. yaṃ tvaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharasi, taṃ dhammaṃ rāmo sayaṃ abhiññā sacchikatvā upasampajja pavedesi. iti yaṃ dhammaṃ rāmo abhiññāsi taṃ tvaṃ dhammaṃ jānāsi, yaṃ tvaṃ dhammaṃ jānāsi, taṃ dhammaṃ rāmo abhiññāsi. iti yādiso rāmo ahosi tādiso tuvaṃ, yādiso tuvaṃ tādiso rāmo ahosi. ehi dāni, āvuso, tuvaṃ imaṃ gaṇaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3262,'mula_ma_01',223,replace(replace('<p class="bodytext">pariharā’ti<a name="M1.0223"></a>. iti kho, bhikkhave<a name="V1.0226"></a>, udako rāmaputto sabrahmacārī me samāno ācariyaṭṭhāne maṃ ṭhapesi, uḷārāya ca maṃ pūjāya pūjesi. tassa mayhaṃ, bhikkhave, etadahosi – ‘nāyaṃ dhammo nibbidāya na virāgāya na nirodhāya na upasamāya na abhiññāya na sambodhāya na nibbānāya saṃvattati, yāvadeva nevasaññānāsaññāyatanūpapattiyā’ti. so kho ahaṃ, bhikkhave, taṃ dhammaṃ analaṅkaritvā tasmā dhammā nibbijja apakkamiṃ.</p>\r\n<p class="bodytext"><a name="para279"></a><a name="para279_mn1"></a><span class="paranum">279</span>. so kho ahaṃ, bhikkhave, kiṃ kusalagavesī anuttaraṃ santivarapadaṃ pariyesamāno magadhesu anupubbena cārikaṃ caramāno yena uruvelā senānigamo tadavasariṃ. tatthaddasaṃ <a name="P1.0167"></a> ramaṇīyaṃ bhūmibhāgaṃ, pāsādikañca vanasaṇḍaṃ, nadiñca sandantiṃ setakaṃ supatitthaṃ ramaṇīyaṃ, samantā <span class="note">[sāmantā (?)]</span> ca gocaragāmaṃ<a name="T1.0323"></a>. tassa mayhaṃ, bhikkhave, etadahosi – ‘ramaṇīyo vata, bho, bhūmibhāgo, pāsādiko ca vanasaṇḍo, nadī ca sandati setakā supatitthā ramaṇīyā, samantā ca gocaragāmo. alaṃ vatidaṃ kulaputtassa padhānatthikassa padhānāyā’ti. so kho ahaṃ, bhikkhave, tattheva nisīdiṃ – alamidaṃ padhānāyāti.</p>\r\n<p class="bodytext"><a name="para280"></a><a name="para280_mn1"></a><span class="paranum">280</span>. so kho ahaṃ, bhikkhave, attanā jātidhammo samāno jātidhamme ādīnavaṃ viditvā ajātaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesamāno ajātaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ ajjhagamaṃ, attanā jarādhammo samāno jarādhamme ādīnavaṃ viditvā ajaraṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesamāno ajaraṃ anuttaraṃ yogakkhemaṃ nibbānaṃ ajjhagamaṃ, attanā byādhidhammo samāno byādhidhamme ādīnavaṃ viditvā abyādhiṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesamāno abyādhiṃ anuttaraṃ yogakkhemaṃ nibbānaṃ ajjhagamaṃ, attanā maraṇadhammo samāno maraṇadhamme ādīnavaṃ viditvā amataṃ anuttaraṃ yogakkhemaṃ nibbānaṃ ajjhagamaṃ, attanā sokadhammo samāno sokadhamme ādīnavaṃ viditvā asokaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ ajjhagamaṃ, attanā saṃkilesadhammo samāno saṃkilesadhamme ādīnavaṃ viditvā asaṃkiliṭṭhaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesamāno asaṃkiliṭṭhaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ ajjhagamaṃ. ñāṇañca pana me dassanaṃ udapādi – ‘akuppā me vimutti, ayamantimā jāti, natthi dāni punabbhavo’ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-279-280-');
INSERT INTO pages VALUES(3263,'mula_ma_01',224,replace(replace('<p class="bodytext"><a name="para281"></a><a name="para281_mn1"></a><span class="paranum">281</span>. tassa <a name="M1.0224"></a> mayhaṃ, bhikkhave, etadahosi – ‘adhigato kho myāyaṃ dhammo gambhīro duddaso duranubodho santo paṇīto atakkāvacaro nipuṇo paṇḍitavedanīyo. ālayarāmā kho panāyaṃ pajā ālayaratā ālayasammuditā. ālayarāmā kho panāyaṃ <a name="V1.0227"></a> pajā ālayaratāya ālayasammuditāya duddasaṃ idaṃ ṭhānaṃ yadidaṃ – idappaccayatā paṭiccasamuppādo. idampi kho ṭhānaṃ duddasaṃ yadidaṃ – sabbasaṅkhārasamatho sabbūpadhipaṭinissaggo taṇhākkhayo <a name="T1.0324"></a> virāgo nirodho nibbānaṃ. ahañceva <a name="P1.0168"></a> kho pana dhammaṃ deseyyaṃ, pare ca me na ājāneyyuṃ, so mamassa kilamatho, sā mamassa vihesā’ti. apissu maṃ, bhikkhave, imā anacchariyā gāthāyo paṭibhaṃsu pubbe assutapubbā –</p>\r\n<p class="gatha1">‘kicchena me adhigataṃ, halaṃ dāni pakāsituṃ,</p>\r\n<p class="gathalast">rāgadosaparetehi, nāyaṃ dhammo susambudho.</p>\r\n<p class="gatha1">‘paṭisotagāmiṃ nipuṇaṃ, gambhīraṃ duddasaṃ aṇuṃ,</p>\r\n<p class="gathalast">rāgarattā na dakkhanti, tamokhandhena āvuṭā"’ti <span class="note">[āvaṭāti (sī.), āvutā (syā.)]</span>.</p>\r\n<p class="bodytext"><a name="para282"></a><a name="para282_mn1"></a><span class="paranum">282</span>. itiha me, bhikkhave, paṭisañcikkhato appossukkatāya cittaṃ namati, no dhammadesanāya. atha kho, bhikkhave, brahmuno sahampatissa mama cetasā cetoparivitakkamaññāya etadahosi – ‘nassati vata bho loko, vinassati vata bho loko, yatra hi nāma tathāgatassa arahato sammāsambuddhassa appossukkatāya cittaṃ namati <span class="note">[namissati (?)]</span>, no dhammadesanāyā’ti. atha kho, bhikkhave, brahmā sahampati – seyyathāpi nāma balavā puriso samiñjitaṃ vā bāhaṃ pasāreyya, pasāritaṃ vā bāhaṃ samiñjeyya, evameva – brahmaloke antarahito mama purato pāturahosi. atha kho, bhikkhave, brahmā sahampati ekaṃsaṃ uttarāsaṅgaṃ karitvā yenāhaṃ tenañjaliṃ paṇāmetvā maṃ etadavoca – ‘desetu, bhante, bhagavā dhammaṃ, desetu <a name="T1.0325"></a> sugato dhammaṃ. santi sattā apparajakkhajātikā, assavanatā dhammassa parihāyanti. bhavissanti dhammassa aññātāro’ti. idamavoca, bhikkhave, brahmā sahampati. idaṃ vatvā athāparaṃ etadavoca –</p>\r\n','\r',char(13)),'\n',char(10)),'-281-282-');
INSERT INTO pages VALUES(3264,'mula_ma_01',225,replace(replace('<p class="gatha1">‘pāturahosi <a name="M1.0225"></a> magadhesu pubbe,</p>\r\n<p class="gatha2">dhammo asuddho samalehi cintito,</p>\r\n<p class="gatha3">apāpuretaṃ <span class="note">[avāpuretaṃ (sī.)]</span> amatassa dvāraṃ,</p>\r\n<p class="gathalast">suṇantu dhammaṃ vimalenānubuddhaṃ.</p>\r\n<p class="gatha1">‘sele <a name="V1.0228"></a> yathā pabbatamuddhaniṭṭhito,</p>\r\n<p class="gatha2">yathāpi passe janataṃ samantato,</p>\r\n<p class="gatha3">tathūpamaṃ dhammamayaṃ sumedha,</p>\r\n<p class="gatha2">pāsādamāruyha samantacakkhu,</p>\r\n<p class="gatha3">sokāvatiṇṇaṃ <span class="note">[sokāvakiṇṇaṃ (syā.)]</span> janatamapetasoko,</p>\r\n<p class="gathalast">avekkhassu jātijarābhibhūtaṃ.</p>\r\n<p class="gatha1">‘uṭṭhehi <a name="P1.0169"></a> vīra vijitasaṅgāma,</p>\r\n<p class="gatha2">satthavāha aṇaṇa vicara loke,</p>\r\n<p class="gatha3">desassu <span class="note">[desetu (syā. ka.)]</span> bhagavā dhammaṃ,</p>\r\n<p class="gathalast">aññātāro bhavissantī"’ti.</p>\r\n<p class="bodytext"><a name="para283"></a><a name="para283_mn1"></a><span class="paranum">283</span>. atha kho ahaṃ, bhikkhave, brahmuno ca ajjhesanaṃ viditvā sattesu ca kāruññataṃ paṭicca buddhacakkhunā lokaṃ volokesiṃ. addasaṃ kho ahaṃ, bhikkhave, buddhacakkhunā lokaṃ volokento satte apparajakkhe <a name="T1.0326"></a> mahārajakkhe, tikkhindriye mudindriye, svākāre dvākāre, suviññāpaye duviññāpaye, appekacce paralokavajjabhayadassāvine <span class="note">[dassāvino (syā. kaṃ. ka.)]</span> viharante, appekacce na paralokavajjabhayadassāvine <span class="note">[dassāvino (syā. kaṃ. ka.)]</span> viharante. seyyathāpi nāma uppaliniyaṃ vā paduminiyaṃ vā puṇḍarīkiniyaṃ vā appekaccāni uppalāni vā padumāni vā puṇḍarīkāni vā udake jātāni udake saṃvaḍḍhāni udakānuggatāni antonimuggaposīni, appekaccāni uppalāni vā padumāni vā puṇḍarīkāni vā udake jātāni udake saṃvaḍḍhāni udakānuggatāni samodakaṃ ṭhitāni, appekaccāni uppalāni vā padumāni vā puṇḍarīkāni vā udake jātāni udake saṃvaḍḍhāni udakaṃ accuggamma ṭhitāni <span class="note">[tiṭṭhanti (sī. syā. pī.)]</span> anupalittāni udakena, evameva kho ahaṃ, bhikkhave,</p>\r\n','\r',char(13)),'\n',char(10)),'-283-');
INSERT INTO pages VALUES(3265,'mula_ma_01',226,replace(replace('<p class="bodytext">buddhacakkhunā <a name="M1.0226"></a> lokaṃ volokento addasaṃ satte apparajakkhe mahārajakkhe, tikkhindriye mudindriye, svākāre dvākāre, suviññāpaye duviññāpaye, appekacce paralokavajjabhayadassāvine viharante, appekacce na paralokavajjabhayadassāvine viharante. atha khvāhaṃ, bhikkhave, brahmānaṃ sahampatiṃ gāthāya paccabhāsiṃ –</p>\r\n<p class="gatha1">‘apārutā <a name="V1.0229"></a> tesaṃ amatassa dvārā,</p>\r\n<p class="gatha2">ye sotavanto pamuñcantu saddhaṃ,</p>\r\n<p class="gatha3">vihiṃsasaññī paguṇaṃ na bhāsiṃ,</p>\r\n<p class="gathalast">dhammaṃ paṇītaṃ manujesu brahme"’ti.</p>\r\n<p class="bodytext">atha kho, bhikkhave, brahmā sahampati ‘katāvakāso khomhi bhagavatā dhammadesanāyā’ti maṃ abhivādetvā padakkhiṇaṃ katvā tatthevantaradhāyi.</p>\r\n<p class="bodytext"><a name="para284"></a><a name="para284_mn1"></a><span class="paranum">284</span>. tassa <a name="T1.0327"></a> mayhaṃ, bhikkhave, etadahosi – ‘kassa nu kho ahaṃ paṭhamaṃ dhammaṃ deseyyaṃ, ko imaṃ dhammaṃ khippameva ājānissatī’ti? tassa mayhaṃ, bhikkhave, etadahosi – ‘ayaṃ kho āḷāro kālāmo paṇḍito viyatto medhāvī dīgharattaṃ apparajakkhajātiko. yaṃnūnāhaṃ āḷārassa kālāmassa <a name="P1.0170"></a> paṭhamaṃ dhammaṃ deseyyaṃ. so imaṃ dhammaṃ khippameva ājānissatī’ti. atha kho maṃ, bhikkhave, devatā upasaṅkamitvā etadavoca – ‘sattāhakālaṅkato, bhante, āḷāro kālāmo’ti. ñāṇañca pana me dassanaṃ udapādi – ‘sattāhakālaṅkato āḷāro kālāmo’ti. tassa mayhaṃ, bhikkhave, etadahosi – ‘mahājāniyo kho āḷāro kālāmo. sace hi so imaṃ dhammaṃ suṇeyya, khippameva ājāneyyā’ti.</p>\r\n<p class="bodytext">tassa mayhaṃ, bhikkhave, etadahosi – ‘kassa nu kho ahaṃ paṭhamaṃ dhammaṃ deseyyaṃ, ko imaṃ dhammaṃ khippameva ājānissatī’ti? tassa mayhaṃ, bhikkhave, etadahosi – ‘ayaṃ kho udako rāmaputto paṇḍito viyatto medhāvī dīgharattaṃ apparajakkhajātiko. yaṃnūnāhaṃ udakassa rāmaputtassa paṭhamaṃ dhammaṃ deseyyaṃ. so imaṃ dhammaṃ khippameva ājānissatī’ti. atha kho maṃ, bhikkhave, devatā upasaṅkamitvā etadavoca – ‘abhidosakālaṅkato, bhante, udako rāmaputto’ti. ñāṇañca pana me dassanaṃ udapādi – ‘abhidosakālaṅkato udako rāmaputto’ti. tassa mayhaṃ, bhikkhave, etadahosi – ‘mahājāniyo kho udako rāmaputto. sace hi so imaṃ dhammaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-284-');
INSERT INTO pages VALUES(3266,'mula_ma_01',227,replace(replace('<p class="bodytext">suṇeyya<a name="M1.0227"></a>, khippameva ājāneyyā’ti.</p>\r\n<p class="bodytext">tassa mayhaṃ<a name="T1.0328"></a>, bhikkhave, etadahosi – ‘kassa nu kho ahaṃ paṭhamaṃ dhammaṃ deseyyaṃ, ko imaṃ dhammaṃ khippameva ājānissatī’ti? tassa mayhaṃ, bhikkhave, etadahosi – ‘bahukārā <a name="V1.0230"></a> kho me pañcavaggiyā bhikkhū, ye maṃ padhānapahitattaṃ upaṭṭhahiṃsu. yaṃnūnāhaṃ pañcavaggiyānaṃ bhikkhūnaṃ paṭhamaṃ dhammaṃ deseyya’nti. tassa mayhaṃ, bhikkhave, etadahosi – ‘kahaṃ nu kho etarahi pañcavaggiyā bhikkhū viharantī’ti? addasaṃ kho ahaṃ, bhikkhave, dibbena cakkhunā visuddhena atikkantamānusakena pañcavaggiye bhikkhū bārāṇasiyaṃ viharante isipatane migadāye. atha khvāhaṃ, bhikkhave, uruvelāyaṃ yathābhirantaṃ viharitvā yena bārāṇasī tena cārikaṃ pakkamiṃ <span class="note">[pakkāmiṃ (syā. pī. ka.)]</span>.</p>\r\n<p class="bodytext"><a name="para285"></a><a name="para285_mn1"></a><span class="paranum">285</span>. addasā kho maṃ, bhikkhave, upako ājīvako antarā <span class="note">[ājīviko (sī. pī. ka.)]</span> ca gayaṃ antarā ca bodhiṃ addhānamaggappaṭipannaṃ. disvāna maṃ etadavoca – ‘vippasannāni kho te, āvuso, indriyāni, parisuddho chavivaṇṇo pariyodāto! kaṃsi tvaṃ, āvuso, uddissa pabbajito, ko vā te satthā, kassa vā tvaṃ dhammaṃ rocesī’ti<a name="P1.0171"></a>? evaṃ vutte, ahaṃ, bhikkhave, upakaṃ ājīvakaṃ gāthāhi ajjhabhāsiṃ –</p>\r\n<p class="gatha1">‘sabbābhibhū sabbavidūhamasmi, sabbesu dhammesu anūpalitto,</p>\r\n<p class="gathalast">sabbañjaho taṇhākkhaye vimutto, sayaṃ abhiññāya kamuddiseyyaṃ.</p>\r\n<p class="gatha1">‘na <a name="T1.0329"></a> me ācariyo atthi, sadiso me na vijjati,</p>\r\n<p class="gathalast">sadevakasmiṃ lokasmiṃ, natthi me paṭipuggalo.</p>\r\n<p class="gatha1">‘ahañhi arahā loke, ahaṃ satthā anuttaro,</p>\r\n<p class="gathalast">ekomhi sammāsambuddho, sītibhūtosmi nibbuto.</p>\r\n<p class="gatha1">‘dhammacakkaṃ pavattetuṃ, gacchāmi kāsinaṃ puraṃ,</p>\r\n<p class="gathalast">andhībhūtasmiṃ <span class="note">[andhabhūtasmiṃ (sī. syā. pī.)]</span> lokasmiṃ, āhañchaṃ amatadundubhi’nti.</p>\r\n<p class="bodytext">‘yathā kho tvaṃ, āvuso, paṭijānāsi, arahasi anantajino’ti!</p>\r\n<p class="gatha1">‘mādisā <a name="V1.0231"></a> ve jinā honti, ye pattā āsavakkhayaṃ,</p>\r\n<p class="gathalast">jitā me pāpakā dhammā, tasmāhamupaka jino’ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-285-');
INSERT INTO pages VALUES(3267,'mula_ma_01',228,replace(replace('<p class="bodytext">evaṃ <a name="M1.0228"></a> vutte, bhikkhave, upako ājīvako ‘hupeyyapāvuso’ti <span class="note">[huveyyapāvuso (sī. pī.), huveyyāvuso (syā.)]</span> vatvā sīsaṃ okampetvā ummaggaṃ gahetvā pakkāmi.</p>\r\n<p class="bodytext"><a name="para286"></a><a name="para286_mn1"></a><span class="paranum">286</span>. atha khvāhaṃ, bhikkhave, anupubbena cārikaṃ caramāno yena bārāṇasī isipatanaṃ migadāyo yena pañcavaggiyā bhikkhū tenupasaṅkamiṃ. addasaṃsu kho maṃ, bhikkhave, pañcavaggiyā bhikkhū dūrato āgacchantaṃ. disvāna aññamaññaṃ saṇṭhapesuṃ <span class="note">[aññamaññaṃ katikaṃ saṇṭhapesuṃ (vinayapiṭake mahāvagge)]</span> – ‘ayaṃ kho, āvuso, samaṇo gotamo āgacchati bāhulliko <span class="note">[bāhuliko (sī. pī.) sāratthadīpanīṭīkāya sameti]</span> padhānavibbhanto āvatto bāhullāya. so neva abhivādetabbo, na paccuṭṭhātabbo, nāssa pattacīvaraṃ paṭiggahetabbaṃ. api ca kho āsanaṃ ṭhapetabbaṃ, sace ākaṅkhissati nisīdissatī’ti. yathā yathā kho ahaṃ, bhikkhave, upasaṅkamiṃ tathā <a name="T1.0330"></a> tathā pañcavaggiyā bhikkhū nāsakkhiṃsu sakāya katikāya saṇṭhātuṃ. appekacce maṃ paccuggantvā pattacīvaraṃ paṭiggahesuṃ, appekacce āsanaṃ paññapesuṃ, appekacce pādodakaṃ upaṭṭhapesuṃ. api ca kho maṃ nāmena ca āvusovādena ca samudācaranti.</p>\r\n<p class="bodytext">evaṃ vutte, ahaṃ, bhikkhave, pañcavaggiye bhikkhū etadavocaṃ – ‘mā, bhikkhave, tathāgataṃ nāmena ca āvusovādena ca samudācaratha <span class="note">[samudācarittha (sī. syā. pī.)]</span>. arahaṃ, bhikkhave, tathāgato sammāsambuddho<a name="P1.0172"></a>. odahatha, bhikkhave, sotaṃ, amatamadhigataṃ, ahamanusāsāmi, ahaṃ dhammaṃ desemi. yathānusiṭṭhaṃ tathā paṭipajjamānā nacirasseva – yassatthāya kulaputtā sammadeva agārasmā anagāriyaṃ pabbajanti tadanuttaraṃ – brahmacariyapariyosānaṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja viharissathā’ti. evaṃ vutte, bhikkhave, pañcavaggiyā bhikkhū maṃ etadavocuṃ – ‘tāyapi kho tvaṃ, āvuso gotama, iriyāya tāya paṭipadāya tāya dukkarakārikāya nājjhagamā uttarimanussadhammā alamariyañāṇadassanavisesaṃ, kiṃ pana tvaṃ etarahi bāhulliko padhānavibbhanto āvatto bāhullāya adhigamissasi uttarimanussadhammā alamariyañāṇadassanavisesa’nti? evaṃ vutte, ahaṃ, bhikkhave, pañcavaggiye bhikkhū etadavocaṃ – ‘na, bhikkhave, tathāgato bāhulliko, na padhānavibbhanto, na āvatto</p>\r\n','\r',char(13)),'\n',char(10)),'-286-');
INSERT INTO pages VALUES(3268,'mula_ma_01',229,replace(replace('<p class="bodytext">bāhullāya<a name="M1.0229"></a><a name="V1.0232"></a>. arahaṃ, bhikkhave, tathāgato sammāsambuddho. odahatha, bhikkhave, sotaṃ, amatamadhigataṃ, ahamanusāsāmi, ahaṃ dhammaṃ desemi. yathānusiṭṭhaṃ tathā paṭipajjamānā nacirasseva – yassatthāya kulaputtā <a name="T1.0331"></a> sammadeva agārasmā anagāriyaṃ pabbajanti tadanuttaraṃ – brahmacariyapariyosānaṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja viharissathā’ti. dutiyampi kho, bhikkhave, pañcavaggiyā bhikkhū maṃ etadavocuṃ – ‘tāyapi kho tvaṃ, āvuso gotama, iriyāya tāya paṭipadāya tāya dukkarakārikāya nājjhagamā uttarimanussadhammā alamariyañāṇadassanavisesaṃ, kiṃ pana tvaṃ etarahi bāhulliko padhānavibbhanto āvatto bāhullāya adhigamissasi uttarimanussadhammā alamariyañāṇadassanavisesa’nti? dutiyampi kho ahaṃ, bhikkhave, pañcavaggiye bhikkhū etadavocaṃ – ‘na, bhikkhave, tathāgato bāhulliko…pe… upasampajja viharissathā’ti. tatiyampi kho, bhikkhave, pañcavaggiyā bhikkhū maṃ etadavocuṃ – ‘tāyapi kho tvaṃ, āvuso gotama, iriyāya tāya paṭipadāya tāya dukkarakārikāya nājjhagamā uttarimanussadhammā alamariyañāṇadassanavisesaṃ, kiṃ pana tvaṃ etarahi bāhulliko padhānavibbhanto āvatto bāhullāya adhigamissasi uttarimanussadhammā alamariyañāṇadassanavisesa’nti?</p>\r\n<p class="bodytext">evaṃ vutte, ahaṃ, bhikkhave, pañcavaggiye bhikkhū etadavocaṃ – ‘abhijānātha me no tumhe, bhikkhave, ito pubbe evarūpaṃ pabhāvitameta’nti <span class="note">[bhāsitametanti (sī. syā. vinayepi)]</span>? ‘no hetaṃ, bhante’. ‘arahaṃ, bhikkhave, tathāgato sammāsambuddho. odahatha, bhikkhave, sotaṃ, amatamadhigataṃ, ahamanusāsāmi, ahaṃ dhammaṃ desemi. yathānusiṭṭhaṃ tathā paṭipajjamānā nacirasseva <a name="T1.0332"></a> – yassatthāya kulaputtā sammadeva agārasmā anagāriyaṃ pabbajanti tadanuttaraṃ – brahmacariyapariyosānaṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā <a name="P1.0173"></a> upasampajja viharissathā’ti.</p>\r\n<p class="bodytext">asakkhiṃ kho ahaṃ, bhikkhave, pañcavaggiye bhikkhū saññāpetuṃ. dvepi sudaṃ, bhikkhave, bhikkhū ovadāmi, tayo bhikkhū piṇḍāya caranti. yaṃ tayo bhikkhū piṇḍāya caritvā āharanti tena chabbaggiyā <span class="note">[chabbaggā (sī. syā.)]</span> yāpema. tayopi sudaṃ, bhikkhave, bhikkhū ovadāmi, dve bhikkhū piṇḍāya caranti. yaṃ dve bhikkhū piṇḍāya caritvā āharanti tena chabbaggiyā yāpema. atha kho, bhikkhave,</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3269,'mula_ma_01',230,replace(replace('<p class="bodytext">pañcavaggiyā <a name="M1.0230"></a> bhikkhū mayā evaṃ ovadiyamānā evaṃ anusāsiyamānā attanā jātidhammā samānā jātidhamme ādīnavaṃ viditvā ajātaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesamānā ajātaṃ anuttaraṃ <a name="V1.0233"></a> yogakkhemaṃ nibbānaṃ ajjhagamaṃsu, attanā jarādhammā samānā jarādhamme ādīnavaṃ viditvā ajaraṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesamānā ajaraṃ anuttaraṃ yogakkhemaṃ nibbānaṃ ajjhagamaṃsu, attanā byādhidhammā samānā…pe… attanā maraṇadhammā samānā… attanā sokadhammā samānā… attanā saṃkilesadhammā samānā saṃkilesadhamme ādīnavaṃ viditvā asaṃkiliṭṭhaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ pariyesamānā asaṃkiliṭṭhaṃ anuttaraṃ yogakkhemaṃ nibbānaṃ ajjhagamaṃsu. ñāṇañca pana nesaṃ dassanaṃ udapādi – ‘akuppā no <a name="T1.0333"></a> vimutti <span class="note">[akuppā nesaṃ vimutti (ka.)]</span>, ayamantimā jāti, natthi dāni punabbhavo’ti.</p>\r\n<p class="bodytext"><a name="para287"></a><a name="para287_mn1"></a><span class="paranum">287</span>. pañcime, bhikkhave, kāmaguṇā. katame pañca? cakkhuviññeyyā rūpā iṭṭhā kantā manāpā piyarūpā kāmūpasaṃhitā rajanīyā, sotaviññeyyā saddā…pe… ghānaviññeyyā gandhā… jivhāviññeyyā rasā… kāyaviññeyyā phoṭṭhabbā iṭṭhā kantā manāpā piyarūpā kāmūpasaṃhitā rajanīyā. ime kho, bhikkhave, pañca kāmaguṇā. ye hi keci, bhikkhave, samaṇā vā brāhmaṇā vā ime pañca kāmaguṇe gathitā mucchitā ajjhopannā anādīnavadassāvino anissaraṇapaññā paribhuñjanti, te evamassu veditabbā – ‘anayamāpannā byasanamāpannā yathākāmakaraṇīyā pāpimato’ <span class="note">[pāpimato"ti (?)]</span>. ‘seyyathāpi, bhikkhave, āraññako mago baddho pāsarāsiṃ adhisayeyya. so evamassa veditabbo – anayamāpanno byasanamāpanno yathākāmakaraṇīyo luddassa. āgacchante ca pana ludde yena kāmaṃ na pakkamissatī’ti. evameva kho, bhikkhave, ye hi keci samaṇā vā brāhmaṇā vā ime pañca kāmaguṇe gathitā mucchitā ajjhopannā anādīnavadassāvino anissaraṇapaññā paribhuñjanti, te evamassu veditabbā – ‘anayamāpannā byasanamāpannā yathākāmakaraṇīyā pāpimato’. ye ca kho keci, bhikkhave, samaṇā vā brāhmaṇā vā ime pañca kāmaguṇe agathitā amucchitā anajjhopannā ādīnavadassāvino nissaraṇapaññā <a name="P1.0174"></a> paribhuñjanti, te evamassu veditabbā – ‘na anayamāpannā na byasanamāpannā</p>\r\n','\r',char(13)),'\n',char(10)),'-287-');
INSERT INTO pages VALUES(3270,'mula_ma_01',231,replace(replace('<p class="bodytext">na <a name="M1.0231"></a> yathākāmakaraṇīyā pāpimato’.</p>\r\n<p class="bodytext">seyyathāpi<a name="V1.0234"></a>, bhikkhave, āraññako mago abaddho pāsarāsiṃ adhisayeyya. so evamassa veditabbo – ‘na anayamāpanno na byasanamāpanno na yathākāmakaraṇīyo luddassa. āgacchante ca pana ludde yena kāmaṃ pakkamissatī’ti. evameva kho, bhikkhave, ye hi keci samaṇā vā brāhmaṇā vā ime pañca kāmaguṇe agathitā amucchitā anajjhopannā ādīnavadassāvino nissaraṇapaññā paribhuñjanti, te evamassu veditabbā – ‘na anayamāpannā na byasanamāpannā na <a name="T1.0334"></a> yathākāmakaraṇīyā pāpimato’.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, āraññako mago araññe pavane caramāno vissattho gacchati, vissattho tiṭṭhati, vissattho nisīdati, vissattho seyyaṃ kappeti. taṃ kissa hetu? anāpāthagato, bhikkhave, luddassa. evameva kho, bhikkhave, bhikkhu vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave, bhikkhu andhamakāsi māraṃ apadaṃ, vadhitvā māracakkhuṃ adassanaṃ gato pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu pītiyā ca virāgā upekkhako ca viharati, sato ca sampajāno, sukhañca kāyena paṭisaṃvedeti yaṃ taṃ ariyā ācikkhanti ‘upekkhako satimā sukhavihārī’ti tatiyaṃ jhānaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sukhassa ca pahānā dukkhassa ca pahānā pubbeva somanassadomanassānaṃ atthaṅgamā adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sabbaso rūpasaññānaṃ samatikkamā paṭighasaññānaṃ atthaṅgamā <a name="T1.0335"></a> nānattasaññānaṃ amanasikārā ‘ananto ākāso’ti ākāsānañcāyatanaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3271,'mula_ma_01',232,replace(replace('<p class="bodytext">puna <a name="M1.0232"></a><a name="V1.0235"></a> caparaṃ, bhikkhave, bhikkhu sabbaso ākāsānañcāyatanaṃ samatikkamma ‘anantaṃ viññāṇa’nti viññāṇañcāyatanaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sabbaso viññāṇañcāyatanaṃ samatikkamma ‘natthi kiñcī’ti ākiñcaññāyatanaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sabbaso ākiñcaññāyatanaṃ samatikkamma <a name="P1.0175"></a> nevasaññānāsaññāyatanaṃ upasampajja viharati. ayaṃ vuccati, bhikkhave…pe… pāpimato.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu sabbaso nevasaññānāsaññāyatanaṃ samatikkamma saññāvedayitanirodhaṃ upasampajja viharati, paññāya cassa disvā āsavā parikkhīṇā honti. ayaṃ vuccati, bhikkhave, bhikkhu andhamakāsi māraṃ apadaṃ, vadhitvā māracakkhuṃ adassanaṃ gato pāpimato. tiṇṇo loke visattikaṃ vissattho gacchati, vissattho tiṭṭhati, vissattho nisīdati, vissattho seyyaṃ kappeti. taṃ kissa hetu? anāpāthagato, bhikkhave, pāpimato"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">pāsarāsisuttaṃ niṭṭhitaṃ chaṭṭhaṃ.</p>\r\n<p class="subhead">7. cūḷahatthipadopamasuttaṃ</p>\r\n<p class="bodytext"><a name="para288"></a><a name="para288_mn1"></a><span class="paranum">288</span>. evaṃ <a name="T1.0336"></a><a name="V1.0236"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tena kho pana samayena jāṇussoṇi brāhmaṇo sabbasetena vaḷavābhirathena <span class="note">[vaḷabhīrathena (sī. pī.)]</span> sāvatthiyā niyyāti divādivassa. addasā kho jāṇussoṇi brāhmaṇo pilotikaṃ paribbājakaṃ dūratova āgacchantaṃ. disvāna pilotikaṃ paribbājakaṃ etadavoca –</p>\r\n<p class="bodytext">handa, kuto nu bhavaṃ vacchāyano āgacchati divādivassā"ti?</p>\r\n<p class="bodytext">ito hi kho</p>\r\n','\r',char(13)),'\n',char(10)),'-288-');
INSERT INTO pages VALUES(3272,'mula_ma_01',233,replace(replace('<p class="bodytext">ahaṃ<a name="M1.0233"></a>, bho, āgacchāmi samaṇassa gotamassa santikā"ti.</p>\r\n<p class="bodytext">taṃ kiṃ maññati, bhavaṃ vacchāyano, samaṇassa gotamassa paññāveyyattiyaṃ?</p>\r\n<p class="bodytext">paṇḍito maññe"ti.</p>\r\n<p class="bodytext">ko cāhaṃ, bho, ko ca samaṇassa gotamassa paññāveyyattiyaṃ jānissāmi! sopi nūnassa tādisova yo samaṇassa gotamassa paññāveyyattiyaṃ jāneyyā"ti.</p>\r\n<p class="bodytext">uḷārāya khalu bhavaṃ vacchāyano samaṇaṃ gotamaṃ pasaṃsāya pasaṃsatī"ti.</p>\r\n<p class="bodytext">ko cāhaṃ, bho, ko ca samaṇaṃ gotamaṃ pasaṃsissāmi?</p>\r\n<p class="bodytext">pasatthapasatthova so bhavaṃ gotamo seṭṭho devamanussāna"nti.</p>\r\n<p class="bodytext">kaṃ pana bhavaṃ vacchāyano atthavasaṃ sampassamāno samaṇe gotame evaṃ abhippasanno"ti <span class="note">[abhippasanno hotīti (syā.)]</span>?</p>\r\n<p class="bodytext">seyyathāpi, bho, kusalo nāgavaniko nāgavanaṃ paviseyya. so passeyya nāgavane <a name="T1.0337"></a> mahantaṃ <a name="V1.0237"></a><a name="P1.0176"></a> hatthipadaṃ, dīghato ca āyataṃ, tiriyañca vitthataṃ. so niṭṭhaṃ gaccheyya – ‘mahā vata, bho, nāgo’ti. evameva kho ahaṃ, bho, yato addasaṃ samaṇe gotame cattāri padāni athāhaṃ niṭṭhamagamaṃ – ‘sammāsambuddho bhagavā, svākkhāto bhagavatā dhammo, suppaṭipanno bhagavato sāvakasaṅgho’ti.</p>\r\n<p class="bodytext"><a name="para289"></a><a name="para289_mn1"></a><span class="paranum">289</span>. katamāni cattāri? idhāhaṃ, bho, passāmi ekacce khattiyapaṇḍite nipuṇe kataparappavāde vālavedhirūpe, te bhindantā <span class="note">[vobhindantā (sī. pī.) vi + ava + bhindantā]</span> maññe caranti paññāgatena diṭṭhigatāni. te suṇanti – ‘samaṇo khalu, bho, gotamo amukaṃ nāma gāmaṃ vā nigamaṃ vā osarissatī’ti. te pañhaṃ abhisaṅkharonti – ‘imaṃ mayaṃ pañhaṃ samaṇaṃ gotamaṃ upasaṅkamitvā pucchissāma. evaṃ ce no puṭṭho evaṃ byākarissati, evamassa mayaṃ vādaṃ āropessāma. evaṃ cepi no puṭṭho evaṃ byākarissati, evampissa mayaṃ vādaṃ āropessāmā’ti. te suṇanti – ‘samaṇo khalu, bho, gotamo amukaṃ nāma gāmaṃ vā nigamaṃ vā osaṭo’ti. te yena samaṇo gotamo tenupasaṅkamanti. te samaṇo gotamo dhammiyā kathāya sandasseti samādapeti samuttejeti sampahaṃseti. te samaṇena gotamena dhammiyā kathāya sandassitā samādapitā samuttejitā sampahaṃsitā na ceva samaṇaṃ gotamaṃ pañhaṃ pucchanti, kutossa <span class="note">[kutassa (sī. syā. pī.)]</span> vādaṃ āropessanti?</p>\r\n','\r',char(13)),'\n',char(10)),'-289-');
INSERT INTO pages VALUES(3273,'mula_ma_01',234,replace(replace('<p class="bodytext">aññadatthu <a name="M1.0234"></a> samaṇasseva gotamassa sāvakā sampajjanti. yadāhaṃ, bho, samaṇe gotame imaṃ paṭhamaṃ <a name="T1.0338"></a> padaṃ addasaṃ athāhaṃ niṭṭhamagamaṃ – ‘sammāsambuddho bhagavā, svākkhāto bhagavatā dhammo, suppaṭipanno bhagavato sāvakasaṅgho’ti.</p>\r\n<p class="bodytext">puna caparāhaṃ, bho, passāmi idhekacce brāhmaṇapaṇḍite…pe… gahapatipaṇḍite…pe… samaṇapaṇḍite nipuṇe kataparappavāde vālavedhirūpe te bhindantā maññe caranti paññāgatena diṭṭhigatāni. te suṇanti – ‘samaṇo khalu bho gotamo amukaṃ nāma <a name="P1.0177"></a> gāmaṃ vā nigamaṃ vā osarissatī’ti. te pañhaṃ abhisaṅkharonti ‘imaṃ mayaṃ pañhaṃ samaṇaṃ gotamaṃ upasaṅkamitvā pucchissāma. evaṃ ce no puṭṭho evaṃ byākarissati, evamassa mayaṃ vādaṃ āropessāma. evaṃ cepi no puṭṭho evaṃ byākarissati, evaṃpissa mayaṃ vādaṃ āropessāmā’ti. te suṇanti ‘samaṇo khalu bho gotamo amukaṃ nāma gāmaṃ vā nigamaṃ vā osaṭo’ti. te yena samaṇo gotamo tenupasaṅkamanti. te samaṇo gotamo dhammiyā kathāya sandasseti samādapeti samuttejeti sampahaṃseti. te samaṇena gotamena dhammiyā kathāya sandassitā samādapitā samuttejitā sampahaṃsitā na ceva samaṇaṃ gotamaṃ pañhaṃ pucchanti, kutossa <a name="V1.0238"></a> vādaṃ āropessanti? aññadatthu samaṇaṃyeva gotamaṃ okāsaṃ yācanti agārasmā anagāriyaṃ pabbajjāya. te samaṇo gotamo pabbājeti <span class="note">[pabbājeti upasampādeti (sī.)]</span>. te tattha pabbajitā samānā vūpakaṭṭhā appamattā ātāpino pahitattā viharantā <a name="T1.0339"></a> nacirasseva – yassatthāya kulaputtā sammadeva agārasmā anagāriyaṃ pabbajanti tadanuttaraṃ – brahmacariyapariyosānaṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja viharanti. te evamāhaṃsu – ‘manaṃ vata, bho, anassāma, manaṃ vata, bho, panassāma, mayañhi pubbe assamaṇāva samānā samaṇamhāti paṭijānimha, abrāhmaṇāva samānā brāhmaṇamhāti paṭijānimha, anarahantova samānā arahantamhāti paṭijānimha. idāni khomha samaṇā, idāni khomha brāhmaṇā, idāni khomha arahanto’ti. yadāhaṃ, bho, samaṇe gotame imaṃ catutthaṃ padaṃ addasaṃ athāhaṃ niṭṭhamagamaṃ – ‘sammāsambuddho bhagavā, svākkhāto bhagavatā dhammo, suppaṭipanno bhagavato sāvakasaṅgho"’ti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3274,'mula_ma_01',235,replace(replace('<p class="bodytext">yato <a name="M1.0235"></a> kho ahaṃ, bho, samaṇe gotame imāni cattāri padāni addasaṃ athāhaṃ niṭṭhamagamaṃ – ‘sammāsambuddho bhagavā, svākkhāto bhagavatā dhammo, suppaṭipanno bhagavato sāvakasaṅgho"’ti.</p>\r\n<p class="bodytext"><a name="para290"></a><a name="para290_mn1"></a><span class="paranum">290</span>. evaṃ vutte, jāṇussoṇi brāhmaṇo sabbasetā vaḷavābhirathā orohitvā ekaṃsaṃ uttarāsaṅgaṃ karitvā yena bhagavā tenañjaliṃ paṇāmetvā tikkhattuṃ udānaṃ udānesi – "namo tassa bhagavato arahato sammāsambuddhassa, namo tassa bhagavato arahato sammāsambuddhassa, namo tassa bhagavato arahato sammāsambuddhassa. appeva nāma mayampi kadāci karahaci <a name="P1.0178"></a> tena bhotā gotamena saddhiṃ samāgaccheyyāma, appeva nāma siyā kocideva kathāsallāpo"ti! atha kho jāṇussoṇi brāhmaṇo yena bhagavā tenupasaṅkami<a name="T1.0340"></a>, upasaṅkamitvā bhagavatā saddhiṃ sammodi. sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho jāṇussoṇi brāhmaṇo yāvatako ahosi pilotikena paribbājakena saddhiṃ kathāsallāpo taṃ sabbaṃ bhagavato ārocesi. evaṃ vutte, bhagavā jāṇussoṇiṃ brāhmaṇaṃ etadavoca – "na kho, brāhmaṇa, ettāvatā hatthipadopamo vitthārena paripūro hoti. api ca, brāhmaṇa, yathā hatthipadopamo vitthārena paripūro hoti taṃ suṇāhi, sādhukaṃ manasi karohi, bhāsissāmī"ti. "evaṃ, bho"ti kho jāṇussoṇi brāhmaṇo bhagavato paccassosi. bhagavā etadavoca –</p>\r\n<p class="bodytext"><a name="para291"></a><a name="para291_mn1"></a><span class="paranum">291</span>. seyyathāpi, brāhmaṇa, nāgavaniko nāgavanaṃ paviseyya. so passeyya nāgavane mahantaṃ <a name="V1.0239"></a> hatthipadaṃ, dīghato ca āyataṃ, tiriyañca vitthataṃ. yo hoti kusalo nāgavaniko neva tāva niṭṭhaṃ gacchati – ‘mahā vata, bho, nāgo’ti. taṃ kissa hetu? santi hi, brāhmaṇa, nāgavane vāmanikā nāma hatthiniyo mahāpadā, tāsaṃ petaṃ padaṃ assāti.</p>\r\n<p class="bodytext">so tamanugacchati. tamanugacchanto passati nāgavane mahantaṃ hatthipadaṃ, dīghato ca āyataṃ, tiriyañca vitthataṃ, uccā ca nisevitaṃ. yo hoti kusalo nāgavaniko neva tāva niṭṭhaṃ gacchati – ‘mahā vata, bho, nāgo’ti. taṃ kissa hetu? santi hi, brāhmaṇa, nāgavane uccā kāḷārikā nāma hatthiniyo mahāpadā, tāsaṃ petaṃ padaṃ assāti.</p>\r\n','\r',char(13)),'\n',char(10)),'-290-291-');
INSERT INTO pages VALUES(3275,'mula_ma_01',236,replace(replace('<p class="bodytext">so <a name="M1.0236"></a> tamanugacchati. tamanugacchanto passati nāgavane <a name="T1.0341"></a> mahantaṃ hatthipadaṃ, dīghato ca āyataṃ, tiriyañca vitthataṃ, uccā ca nisevitaṃ, uccā ca dantehi ārañjitāni. yo hoti kusalo nāgavaniko neva tāva niṭṭhaṃ gacchati – ‘mahā vata, bho, nāgo’ti. taṃ kissa hetu? santi hi, brāhmaṇa, nāgavane uccā kaṇerukā nāma hatthiniyo mahāpadā, tāsaṃ petaṃ padaṃ assāti.</p>\r\n<p class="bodytext">so tamanugacchati. tamanugacchanto passati nāgavane mahantaṃ hatthipadaṃ, dīghato ca āyataṃ, tiriyañca vitthataṃ, uccā ca nisevitaṃ, uccā ca dantehi ārañjitāni, uccā ca sākhābhaṅgaṃ. tañca nāgaṃ passati rukkhamūlagataṃ vā abbhokāsagataṃ vā gacchantaṃ vā tiṭṭhantaṃ vā nisinnaṃ vā nipannaṃ vā. so niṭṭhaṃ gacchati – ‘ayameva so mahānāgo’ti.</p>\r\n<p class="bodytext">evameva kho<a name="P1.0179"></a>, brāhmaṇa, idha tathāgato loke uppajjati arahaṃ sammāsambuddho vijjācaraṇasampanno sugato lokavidū anuttaro purisadammasārathi satthā devamanussānaṃ buddho bhagavā. so imaṃ lokaṃ sadevakaṃ samārakaṃ sabrahmakaṃ sassamaṇabrāhmaṇiṃ pajaṃ sadevamanussaṃ sayaṃ abhiññā sacchikatvā pavedeti. so dhammaṃ deseti ādikalyāṇaṃ majjhekalyāṇaṃ pariyosānakalyāṇaṃ sātthaṃ sabyañjanaṃ, kevalaparipuṇṇaṃ parisuddhaṃ brahmacariyaṃ pakāseti. taṃ dhammaṃ suṇāti gahapati vā gahapatiputto vā aññatarasmiṃ vā kule paccājāto. so taṃ dhammaṃ sutvā tathāgate saddhaṃ paṭilabhati. so tena saddhāpaṭilābhena samannāgato iti paṭisañcikkhati – ‘sambādho gharāvāso rajopatho<a name="T1.0342"></a>, abbhokāso pabbajjā. nayidaṃ sukaraṃ agāraṃ <a name="V1.0240"></a> ajjhāvasatā ekantaparipuṇṇaṃ ekantaparisuddhaṃ saṅkhalikhitaṃ brahmacariyaṃ carituṃ. yaṃnūnāhaṃ kesamassuṃ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anagāriyaṃ pabbajeyya’nti. so aparena samayena appaṃ vā bhogakkhandhaṃ pahāya mahantaṃ vā bhogakkhandhaṃ pahāya appaṃ vā ñātiparivaṭṭaṃ pahāya mahantaṃ vā ñātiparivaṭṭaṃ pahāya kesamassuṃ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anagāriyaṃ pabbajati.</p>\r\n<p class="bodytext"><a name="para292"></a><a name="para292_mn1"></a><span class="paranum">292</span>. so evaṃ pabbajito samāno bhikkhūnaṃ sikkhāsājīvasamāpanno pāṇātipātaṃ pahāya pāṇātipātā paṭivirato hoti,</p>\r\n','\r',char(13)),'\n',char(10)),'-292-');
INSERT INTO pages VALUES(3276,'mula_ma_01',237,replace(replace('<p class="bodytext">nihitadaṇḍo <a name="M1.0237"></a> nihitasattho lajjī dayāpanno sabbapāṇabhūtahitānukampī viharati.</p>\r\n<p class="bodytext">adinnādānaṃ pahāya adinnādānā paṭivirato hoti dinnādāyī dinnapāṭikaṅkhī. athenena sucibhūtena attanā viharati.</p>\r\n<p class="bodytext">abrahmacariyaṃ pahāya brahmacārī hoti ārācārī virato methunā gāmadhammā.</p>\r\n<p class="bodytext">musāvādaṃ pahāya musāvādā paṭivirato hoti saccavādī saccasandho theto <span class="note">[ṭheto (syā. kaṃ.)]</span> paccayiko avisaṃvādako lokassa.</p>\r\n<p class="bodytext">pisuṇaṃ vācaṃ pahāya pisuṇāya vācāya paṭivirato hoti, ito sutvā na amutra akkhātā imesaṃ bhedāya, amutra vā sutvā na imesaṃ akkhātā amūsaṃ bhedāya. iti bhinnānaṃ vā sandhātā sahitānaṃ vā anuppadātā, samaggārāmo samaggarato samagganandī samaggakaraṇiṃ vācaṃ bhāsitā hoti<a name="T1.0343"></a>.</p>\r\n<p class="bodytext">pharusaṃ vācaṃ pahāya pharusāya vācāya paṭivirato hoti. yā sā vācā nelā kaṇṇasukhā pemanīyā hadayaṅgamā porī bahujanakantā bahujanamanāpā <a name="P1.0180"></a> tathārūpiṃ vācaṃ bhāsitā hoti.</p>\r\n<p class="bodytext">samphappalāpaṃ pahāya samphappalāpā paṭivirato hoti kālavādī bhūtavādī atthavādī dhammavādī vinayavādī, nidhānavatiṃ vācaṃ bhāsitā kālena sāpadesaṃ pariyantavatiṃ atthasaṃhitaṃ.</p>\r\n<p class="bodytext"><a name="para293"></a><a name="para293_mn1"></a><span class="paranum">293</span>. so <a name="V1.0241"></a> bījagāmabhūtagāmasamārambhā paṭivirato hoti, ekabhattiko hoti rattūparato, virato vikālabhojanā, naccagītavāditavisūkadassanā paṭivirato hoti, mālāgandhavilepanadhāraṇamaṇḍanavibhūsanaṭṭhānā paṭivirato hoti, uccāsayanamahāsayanā paṭivirato hoti, jātarūparajatapaṭiggahaṇā paṭivirato hoti, āmakadhaññapaṭiggahaṇā paṭivirato hoti, āmakamaṃsapaṭiggahaṇā paṭivirato hoti, itthikumārikapaṭiggahaṇā paṭivirato hoti,</p>\r\n','\r',char(13)),'\n',char(10)),'-293-');
INSERT INTO pages VALUES(3277,'mula_ma_01',238,replace(replace('<p class="bodytext">dāsidāsapaṭiggahaṇā <a name="M1.0238"></a> paṭivirato hoti, ajeḷakapaṭiggahaṇā paṭivirato hoti, kukkuṭasūkarapaṭiggahaṇā paṭivirato hoti, hatthigavāssavaḷavāpaṭiggahaṇā paṭivirato hoti, khettavatthupaṭiggahaṇā paṭivirato hoti, dūteyyapahiṇagamanānuyogā paṭivirato hoti, kayavikkayā paṭivirato hoti, tulākūṭakaṃsakūṭamānakūṭā paṭivirato hoti, ukkoṭanavañcananikatisāciyogā paṭivirato hoti, chedanavadhabandhanaviparāmosaālopasahasākārā <span class="note">[sāhasākārā (ka.)]</span> paṭivirato hoti <span class="note">[imassa anantaraṃ "so iminā ariyena sīlakkhandhena samannāgato ajjhattaṃ anavajjasukhaṃ paṭisaṃvedetī"ti vacanaṃ dīghanikāye āgataṃ, taṃ idha santosakathāvasāne āgataṃ, sā ca santosakathā tattha satisampajaññānantarameva āgatā]</span>.</p>\r\n<p class="bodytext"><a name="para294"></a><a name="para294_mn1"></a><span class="paranum">294</span>. so santuṭṭho hoti kāyaparihārikena cīvarena kucchiparihārikena piṇḍapātena. so yena yeneva <a name="T1.0344"></a> pakkamati samādāyeva pakkamati. seyyathāpi nāma pakkhī sakuṇo yena yeneva ḍeti sapattabhārova ḍeti, evameva bhikkhu santuṭṭho hoti kāyaparihārikena cīvarena kucchiparihārikena piṇḍapātena. so yena yeneva pakkamati samādāyeva pakkamati. so iminā ariyena sīlakkhandhena samannāgato ajjhattaṃ anavajjasukhaṃ paṭisaṃvedeti.</p>\r\n<p class="bodytext"><a name="para295"></a><a name="para295_mn1"></a><span class="paranum">295</span>. so cakkhunā rūpaṃ disvā na nimittaggāhī hoti nānubyañjanaggāhī. yatvādhikaraṇamenaṃ cakkhundriyaṃ asaṃvutaṃ viharantaṃ abhijjhādomanassā pāpakā akusalā dhammā anvāssaveyyuṃ tassa saṃvarāya paṭipajjati, rakkhati cakkhundriyaṃ, cakkhundriye saṃvaraṃ āpajjati. sotena saddaṃ sutvā…pe… ghānena gandhaṃ ghāyitvā… jivhāya rasaṃ sāyitvā… kāyena phoṭṭhabbaṃ phusitvā… manasā dhammaṃ viññāya na nimittaggāhī hoti nānubyañjanaggāhī. yatvādhikaraṇamenaṃ manindriyaṃ asaṃvutaṃ viharantaṃ abhijjhādomanassā pāpakā akusalā dhammā anvāssaveyyuṃ tassa saṃvarāya paṭipajjati, rakkhati <a name="P1.0181"></a> manindriyaṃ, manindriye saṃvaraṃ āpajjati. so iminā ariyena indriyasaṃvarena samannāgato ajjhattaṃ abyāsekasukhaṃ paṭisaṃvedeti.</p>\r\n<p class="bodytext">so abhikkante paṭikkante sampajānakārī hoti, ālokite vilokite sampajānakārī hoti<a name="V1.0242"></a>, samiñjite pasārite sampajānakārī hoti, saṃghāṭipattacīvaradhāraṇe sampajānakārī hoti, asite</p>\r\n','\r',char(13)),'\n',char(10)),'-294-295-');
INSERT INTO pages VALUES(3278,'mula_ma_01',239,replace(replace('<p class="bodytext">pīte <a name="M1.0239"></a> khāyite sāyite sampajānakārī hoti, uccārapassāvakamme sampajānakārī hoti, gate ṭhite nisinne sutte jāgarite bhāsite tuṇhībhāve <a name="T1.0345"></a> sampajānakārī hoti.</p>\r\n<p class="bodytext"><a name="para296"></a><a name="para296_mn1"></a><span class="paranum">296</span>. so iminā ca ariyena sīlakkhandhena samannāgato, (imāya ca ariyāya santuṭṭhiyā samannāgato) <span class="note">[( ) etthantare pāṭho idha nadissati, catukkaṅguttare pana imasmiṃ ṭhāne dissati, aṭṭhakathāṭīkāsu ca tadattho pakāsito. tasmā so ettha paṭīpūrito]</span> iminā ca ariyena indriyasaṃvarena samannāgato, iminā ca ariyena satisampajaññena samannāgato vivittaṃ senāsanaṃ bhajati araññaṃ rukkhamūlaṃ pabbataṃ kandaraṃ giriguhaṃ susānaṃ vanapatthaṃ abbhokāsaṃ palālapuñjaṃ. so pacchābhattaṃ piṇḍapātapaṭikkanto nisīdati pallaṅkaṃ ābhujitvā, ujuṃ kāyaṃ paṇidhāya, parimukhaṃ satiṃ upaṭṭhapetvā. so abhijjhaṃ loke pahāya vigatābhijjhena cetasā viharati, abhijjhāya cittaṃ parisodheti. byāpādappadosaṃ pahāya abyāpannacitto viharati, sabbapāṇabhūtahitānukampī byāpādappadosā cittaṃ parisodheti. thinamiddhaṃ pahāya vigatathinamiddho viharati ālokasaññī sato sampajāno, thinamiddhā cittaṃ parisodheti. uddhaccakukkuccaṃ pahāya anuddhato viharati, ajjhattaṃ vūpasantacitto uddhaccakukkuccā cittaṃ parisodheti. vicikicchaṃ pahāya tiṇṇavicikiccho viharati akathaṃkathī kusalesu dhammesu, vicikicchāya cittaṃ parisodheti.</p>\r\n<p class="bodytext"><a name="para297"></a><a name="para297_mn1"></a><span class="paranum">297</span>. so ime pañca nīvaraṇe pahāya cetaso upakkilese paññāya dubbalīkaraṇe, vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati. idampi vuccati, brāhmaṇa, tathāgatapadaṃ itipi, tathāgatanisevitaṃ itipi, tathāgatārañjitaṃ itipi. na tveva tāva ariyasāvako niṭṭhaṃ gacchati – ‘sammāsambuddho <a name="T1.0346"></a> bhagavā, svākkhāto bhagavatā dhammo, suppaṭipanno bhagavato sāvakasaṅgho’ti.</p>\r\n<p class="bodytext">puna caparaṃ, brāhmaṇa, bhikkhu vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja viharati. idampi vuccati, brāhmaṇa…pe… <a name="P1.0182"></a> suppaṭipanno bhagavato sāvakasaṅgho’ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-296-297-');
INSERT INTO pages VALUES(3279,'mula_ma_01',240,replace(replace('<p class="bodytext">puna <a name="M1.0240"></a> caparaṃ, brāhmaṇa, bhikkhu pītiyā ca virāgā upekkhako ca viharati sato ca sampajāno, sukhañca kāyena paṭisaṃvedeti, yaṃ taṃ ariyā ācikkhanti ‘upekkhako satimā sukhavihārī’ti <a name="V1.0243"></a> tatiyaṃ jhānaṃ upasampajja viharati. idampi vuccati, brāhmaṇa…pe… suppaṭipanno bhagavato sāvakasaṅgho’ti.</p>\r\n<p class="bodytext">puna caparaṃ, brāhmaṇa, bhikkhu sukhassa ca pahānā dukkhassa ca pahānā, pubbeva somanassadomanassānaṃ atthaṅgamā, adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja viharati. idampi vuccati, brāhmaṇa, tathāgatapadaṃ itipi, tathāgatanisevitaṃ itipi, tathāgatārañjitaṃ itipi. na tveva tāva ariyasāvako niṭṭhaṃ gacchati – ‘sammāsambuddho bhagavā, svākkhāto bhagavatā dhammo, suppaṭipanno bhagavato sāvakasaṅgho’ti.</p>\r\n<p class="bodytext"><a name="para298"></a><a name="para298_mn1"></a><span class="paranum">298</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte pubbenivāsānussatiñāṇāya cittaṃ abhininnāmeti. so anekavihitaṃ pubbenivāsaṃ anussarati, seyyathidaṃ – ekampi jātiṃ, dvepi jātiyo…pe… iti sākāraṃ sauddesaṃ anekavihitaṃ pubbenivāsaṃ anussarati. idampi vuccati, brāhmaṇa, tathāgatapadaṃ itipi, tathāgatanisevitaṃ itipi, tathāgatārañjitaṃ itipi. na tveva tāva ariyasāvako niṭṭhaṃ gacchati – ‘sammāsambuddho bhagavā, svākkhāto bhagavatā dhammo, suppaṭipanno bhagavato sāvakasaṅgho’ti.</p>\r\n<p class="bodytext">so <a name="P1.0183"></a> evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte sattānaṃ cutūpapātañāṇāya cittaṃ abhininnāmeti. so dibbena cakkhunā <a name="T1.0347"></a> visuddhena atikkantamānusakena…pe… yathākammūpage satte pajānāti. idampi vuccati, brāhmaṇa, tathāgatapadaṃ itipi, tathāgatanisevitaṃ itipi, tathāgatārañjitaṃ itipi. na tveva tāva ariyasāvako niṭṭhaṃ gacchati – ‘sammāsambuddho bhagavā, svākkhāto bhagavatā dhammo, suppaṭipanno bhagavato sāvakasaṅgho’ti.</p>\r\n<p class="bodytext"><a name="para299"></a><a name="para299_mn1"></a><span class="paranum">299</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte āsavānaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-298-299-');
INSERT INTO pages VALUES(3280,'mula_ma_01',241,replace(replace('<p class="bodytext">khayañāṇāya <a name="M1.0241"></a> cittaṃ abhininnāmeti. so ‘idaṃ dukkha’nti yathābhūtaṃ pajānāti, ‘ayaṃ dukkhasamudayo’ti yathābhūtaṃ pajānāti, ‘ayaṃ dukkhanirodho’ti yathābhūtaṃ pajānāti, ‘ayaṃ dukkhanirodhagāminī paṭipadā’ti yathābhūtaṃ pajānāti. ‘ime āsavā’ti <a name="V1.0244"></a> yathābhūtaṃ pajānāti, ‘ayaṃ āsavasamudayo’ti yathābhūtaṃ pajānāti, ‘ayaṃ āsavanirodho’ti yathābhūtaṃ pajānāti, ‘ayaṃ āsavanirodhagāminī paṭipadā’ti yathābhūtaṃ pajānāti. idampi vuccati, brāhmaṇa, tathāgatapadaṃ itipi, tathāgatanisevitaṃ itipi, tathāgatārañjitaṃ itipi. na tveva tāva ariyasāvako niṭṭhaṃ gato hoti, api ca kho niṭṭhaṃ gacchati – ‘sammāsambuddho bhagavā, svākkhāto bhagavatā dhammo, suppaṭipanno bhagavato sāvakasaṅgho’ti.</p>\r\n<p class="bodytext">tassa evaṃ jānato evaṃ passato kāmāsavāpi cittaṃ vimuccati<a name="P1.0184"></a>, bhavāsavāpi cittaṃ vimuccati, avijjāsavāpi cittaṃ vimuccati. vimuttasmiṃ vimuttamiti ñāṇaṃ hoti. ‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti <a name="T1.0348"></a> pajānāti. idampi vuccati, brāhmaṇa, tathāgatapadaṃ itipi, tathāgatanisevitaṃ itipi, tathāgatārañjitaṃ itipi. ettāvatā kho, brāhmaṇa, ariyasāvako niṭṭhaṃ gato hoti – ‘sammāsambuddho bhagavā, svākkhāto bhagavatā dhammo, suppaṭipanno bhagavato sāvakasaṅgho’ti. ettāvatā kho, brāhmaṇa, hatthipadopamo vitthārena paripūro hotī"ti.</p>\r\n<p class="bodytext">evaṃ vutte, jāṇussoṇi brāhmaṇo bhagavantaṃ etadavoca – "abhikkantaṃ, bho gotama, abhikkantaṃ, bho gotama! seyyathāpi, bho gotama, nikkujjitaṃ vā ukkujjeyya, paṭicchannaṃ vā vivareyya, mūḷhassa vā maggaṃ ācikkheyya, andhakāre vā telapajjotaṃ dhāreyya – cakkhumanto rūpāni dakkhantīti, evamevaṃ bhotā gotamena anekapariyāyena dhammo pakāsito. esāhaṃ bhavantaṃ gotamaṃ saraṇaṃ gacchāmi, dhammañca, bhikkhusaṅghañca. upāsakaṃ maṃ bhavaṃ gotamo dhāretu ajjatagge pāṇupetaṃ saraṇaṃ gata"nti.</p>\r\n<p class="centered">cūḷahatthipadopamasuttaṃ niṭṭhitaṃ sattamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3281,'mula_ma_01',242,replace(replace('<p class="subhead">8. mahāhatthipadopamasuttaṃ</p>\r\n<p class="bodytext"><a name="para300"></a><a name="para300_mn1"></a><span class="paranum">300</span>. evaṃ <a name="M1.0242"></a><a name="V1.0245"></a><a name="T1.0349"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho āyasmā sāriputto bhikkhū āmantesi – "āvuso bhikkhave"ti. "āvuso"ti kho te bhikkhū āyasmato sāriputtassa paccassosuṃ. āyasmā sāriputto etadavoca – "seyyathāpi, āvuso, yāni kānici jaṅgalānaṃ pāṇānaṃ padajātāni sabbāni tāni hatthipade samodhānaṃ gacchanti, hatthipadaṃ tesaṃ aggamakkhāyati yadidaṃ mahantattena, evameva kho, āvuso, ye keci kusalā dhammā sabbete catūsu ariyasaccesu saṅgahaṃ gacchanti. katamesu catūsu? dukkhe ariyasacce<a name="P1.0185"></a>, dukkhasamudaye ariyasacce, dukkhanirodhe ariyasacce, dukkhanirodhagāminiyā paṭipadāya ariyasacce".</p>\r\n<p class="bodytext"><a name="para301"></a><a name="para301_mn1"></a><span class="paranum">301</span>. katamañcāvuso, dukkhaṃ ariyasaccaṃ? jātipi dukkhā, jarāpi dukkhā, maraṇampi dukkhaṃ, sokaparidevadukkhadomanassupāyāsāpi dukkhā, yampicchaṃ na labhati tampi dukkhaṃ, saṃkhittena, pañcupādānakkhandhā dukkhā. katame cāvuso, pañcupādānakkhandhā? seyyathidaṃ – rūpupādānakkhandho, vedanupādānakkhandho, saññupādānakkhandho, saṅkhārupādānakkhandho, viññāṇupādānakkhandho.</p>\r\n<p class="bodytext">katamo cāvuso, rūpupādānakkhandho? cattāri ca mahābhūtāni, catunnañca mahābhūtānaṃ upādāya rūpaṃ.</p>\r\n<p class="bodytext">katamā cāvuso, cattāro mahābhūtā? pathavīdhātu, āpodhātu<a name="T1.0350"></a>, tejodhātu, vāyodhātu.</p>\r\n<p class="bodytext"><a name="para302"></a><a name="para302_mn1"></a><span class="paranum">302</span>. katamā cāvuso, pathavīdhātu? pathavīdhātu siyā ajjhattikā, siyā bāhirā. katamā cāvuso, ajjhattikā pathavīdhātu? yaṃ ajjhattaṃ paccattaṃ kakkhaḷaṃ kharigataṃ upādinnaṃ, seyyathidaṃ – kesā lomā nakhā dantā taco maṃsaṃ nhāru aṭṭhi aṭṭhimiñjaṃ vakkaṃ hadayaṃ yakanaṃ kilomakaṃ pihakaṃ papphāsaṃ antaṃ antaguṇaṃ udariyaṃ karīsaṃ, yaṃ vā panaññampi kiñci ajjhattaṃ <a name="V1.0246"></a> paccattaṃ kakkhaḷaṃ kharigataṃ upādinnaṃ. ayaṃ vuccatāvuso, ajjhattikā pathavīdhātu. yā ceva kho pana ajjhattikā pathavīdhātu, yā ca bāhirā pathavīdhātu, pathavīdhāturevesā. ‘taṃ netaṃ mama, nesohamasmi, na meso attā’ti – evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabbaṃ. evametaṃ yathābhūtaṃ sammappaññāya disvā pathavīdhātuyā nibbindati, pathavīdhātuyā cittaṃ virājeti.</p>\r\n','\r',char(13)),'\n',char(10)),'-300-301-302-');
INSERT INTO pages VALUES(3282,'mula_ma_01',243,replace(replace('<p class="bodytext">hoti <a name="M1.0243"></a> kho so, āvuso, samayo yaṃ bāhirā āpodhātu pakuppati <span class="note">[pathavīdhātu pakuppati (ka.)]</span>. antarahitā tasmiṃ samaye bāhirā pathavīdhātu hoti. tassā hi nāma, āvuso, bāhirāya pathavīdhātuyā tāva mahallikāya aniccatā paññāyissati, khayadhammatā paññāyissati, vayadhammatā paññāyissati, vipariṇāmadhammatā paññāyissati. kiṃ panimassa mattaṭṭhakassa kāyassa taṇhupādinnassa ‘ahanti vā mamanti vā asmī’ti vā? atha khvāssa notevettha hoti.</p>\r\n<p class="bodytext">tañce, āvuso, bhikkhuṃ pare akkosanti paribhāsanti rosenti vihesenti, so evaṃ pajānāti – ‘uppannā kho me ayaṃ sotasamphassajā dukkhavedanā<a name="T1.0351"></a>. sā ca kho paṭicca, no apaṭicca. kiṃ paṭicca? phassaṃ <a name="P1.0186"></a> paṭicca’. so <span class="note">[sopikho (syā.), sopi (ka.)]</span> phasso aniccoti passati, vedanā aniccāti passati, saññā aniccāti passati, saṅkhārā aniccāti passati, viññāṇaṃ aniccanti passati. tassa dhātārammaṇameva cittaṃ pakkhandati pasīdati santiṭṭhati adhimuccati.</p>\r\n<p class="bodytext">tañce, āvuso, bhikkhuṃ pare aniṭṭhehi akantehi amanāpehi samudācaranti – pāṇisamphassenapi leḍḍusamphassenapi daṇḍasamphassenapi satthasamphassenapi. so evaṃ pajānāti – ‘tathābhūto kho ayaṃ kāyo yathābhūtasmiṃ kāye pāṇisamphassāpi kamanti, leḍḍusamphassāpi kamanti, daṇḍasamphassāpi kamanti, satthasamphassāpi kamanti. vuttaṃ kho panetaṃ bhagavatā kakacūpamovāde – "ubhatodaṇḍakena cepi, bhikkhave, kakacena corā ocarakā aṅgamaṅgāni okanteyyuṃ, tatrāpi yo mano padūseyya na me so tena sāsanakaro"ti. āraddhaṃ kho pana me vīriyaṃ bhavissati asallīnaṃ, upaṭṭhitā sati asammuṭṭhā, passaddho kāyo asāraddho, samāhitaṃ cittaṃ ekaggaṃ. kāmaṃ dāni imasmiṃ kāye pāṇisamphassāpi kamantu, leḍḍusamphassāpi kamantu, daṇḍasamphassāpi kamantu, satthasamphassāpi kamantu, karīyati hidaṃ buddhānaṃ sāsana’nti.</p>\r\n<p class="bodytext">tassa ce, āvuso, bhikkhuno evaṃ buddhaṃ anussarato evaṃ dhammaṃ anussarato evaṃ saṅghaṃ <a name="V1.0247"></a> anussarato upekkhā kusalanissitā na saṇṭhāti. so tena <a name="T1.0352"></a> saṃvijjati saṃvegaṃ āpajjati – ‘alābhā vata me, na</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3283,'mula_ma_01',244,replace(replace('<p class="bodytext">vata <a name="M1.0244"></a> me lābhā, dulladdhaṃ vata me, na vata me suladdhaṃ, yassa me evaṃ buddhaṃ anussarato, evaṃ dhammaṃ anussarato, evaṃ saṅghaṃ anussarato, upekkhā kusalanissitā na saṇṭhātī’ti. seyyathāpi, āvuso, suṇisā sasuraṃ disvā saṃvijjati saṃvegaṃ āpajjati, evameva kho, āvuso, tassa ce bhikkhuno evaṃ buddhaṃ anussarato, evaṃ dhammaṃ anussarato, evaṃ saṅghaṃ anussarato, upekkhā kusalanissitā na saṇṭhāti, so tena saṃvijjati saṃvegaṃ āpajjati – ‘alābhā vata me na vata me lābhā, dulladdhaṃ vata me, na vata me suladdhaṃ, yassa me evaṃ buddhaṃ anussarato evaṃ dhammaṃ anussarato, evaṃ saṅghaṃ anussarato, upekkhā kusalanissitā na saṇṭhātī’ti. tassa ce, āvuso, bhikkhuno evaṃ buddhaṃ anussarato, evaṃ dhammaṃ anussarato, evaṃ saṅghaṃ anussarato upekkhā kusalanissitā saṇṭhāti, so tena attamano <a name="P1.0187"></a> hoti. ettāvatāpi kho, āvuso, bhikkhuno bahukataṃ hoti.</p>\r\n<p class="bodytext"><a name="para303"></a><a name="para303_mn1"></a><span class="paranum">303</span>. katamā cāvuso, āpodhātu? āpodhātu siyā ajjhattikā, siyā bāhirā. katamā cāvuso ajjhattikā āpodhātu? yaṃ ajjhattaṃ paccattaṃ āpo āpogataṃ upādinnaṃ, seyyathidaṃ – pittaṃ semhaṃ pubbo lohitaṃ sedo medo assu vasā kheḷo siṅghāṇikā lasikā muttaṃ, yaṃ vā panaññampi kiñci ajjhattaṃ paccattaṃ āpo āpogataṃ upādinnaṃ – ayaṃ vuccatāvuso, ajjhattikā āpodhātu. yā <a name="T1.0353"></a> ceva kho pana ajjhattikā āpodhātu yā ca bāhirā āpodhātu, āpodhāturevesā. ‘taṃ netaṃ mama, nesohamasmi, na meso attā’ti evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabbaṃ. evametaṃ yathābhūtaṃ sammappaññāya disvā āpodhātuyā nibbindati, āpodhātuyā cittaṃ virājeti.</p>\r\n<p class="bodytext">hoti kho so, āvuso, samayo yaṃ bāhirā āpodhātu pakuppati. sā gāmampi vahati, nigamampi vahati, nagarampi vahati, janapadampi vahati, janapadapadesampi vahati. hoti kho so, āvuso, samayo yaṃ mahāsamudde yojanasatikānipi udakāni ogacchanti, dviyojanasatikānipi udakāni ogacchanti, tiyojanasatikānipi udakāni ogacchanti, catuyojanasatikānipi udakāni ogacchanti, pañcayojanasatikānipi udakāni ogacchanti, chayojanasatikānipi udakāni ogacchanti, sattayojanasatikānipi udakāni ogacchanti. hoti kho so, āvuso, samayo yaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-303-');
INSERT INTO pages VALUES(3284,'mula_ma_01',245,replace(replace('<p class="bodytext">mahāsamudde <a name="M1.0245"></a> sattatālampi udakaṃ saṇṭhāti, chattālampi udakaṃ <a name="V1.0248"></a> saṇṭhāti, pañcatālampi udakaṃ saṇṭhāti, catuttālampi udakaṃ saṇṭhāti, titālampi udakaṃ saṇṭhāti, dvitālampi udakaṃ saṇṭhāti, tālamattampi <span class="note">[tālaṃpi (sī.)]</span> udakaṃ saṇṭhāti. hoti kho so, āvuso, samayo yaṃ mahāsamudde sattaporisampi udakaṃ saṇṭhāti, chapporisampi udakaṃ saṇṭhāti, pañcaporisampi udakaṃ saṇṭhāti, catupporisampi udakaṃ saṇṭhāti, tiporisampi udakaṃ saṇṭhāti, dviporisampi udakaṃ saṇṭhāti, porisamattampi <span class="note">[porisaṃpi (sī.)]</span> udakaṃ saṇṭhāti. hoti kho so, āvuso, samayo yaṃ mahāsamudde <a name="T1.0354"></a> aḍḍhaporisampi udakaṃ saṇṭhāti, kaṭimattampi udakaṃ saṇṭhāti, jāṇukamattampi udakaṃ saṇṭhāti, gopphakamattampi udakaṃ saṇṭhāti. hoti kho so, āvuso, samayo, yaṃ mahāsamudde aṅgulipabbatemanamattampi udakaṃ na hoti. tassā hi nāma, āvuso, bāhirāya āpodhātuyā tāva mahallikāya aniccatā <a name="P1.0188"></a> paññāyissati, khayadhammatā paññāyissati, vayadhammatā paññāyissati, vipariṇāmadhammatā paññāyissati. kiṃ panimassa mattaṭṭhakassa kāyassa taṇhupādinnassa ‘ahanti vā mamanti vā asmīti’ vā? atha khvāssa notevettha hoti…pe… tassa ce, āvuso, bhikkhuno evaṃ buddhaṃ anussarato, evaṃ dhammaṃ anussarato, evaṃ saṅghaṃ anussarato upekkhā kusalanissitā saṇṭhāti. so tena attamano hoti. ettāvatāpi kho, āvuso, bhikkhuno bahukataṃ hoti.</p>\r\n<p class="bodytext"><a name="para304"></a><a name="para304_mn1"></a><span class="paranum">304</span>. katamā cāvuso, tejodhātu? tejodhātu siyā ajjhattikā, siyā bāhirā. katamā cāvuso, ajjhattikā tejodhātu? yaṃ ajjhattaṃ paccattaṃ tejo tejogataṃ upādinnaṃ, seyyathidaṃ – yena ca santappati, yena ca jīrīyati, yena ca pariḍayhati, yena ca asitapītakhāyitasāyitaṃ sammā pariṇāmaṃ gacchati, yaṃ vā panaññampi kiñci ajjhattaṃ paccattaṃ tejo tejogataṃ upādinnaṃ – ayaṃ vuccatāvuso, ajjhattikā tejodhātu. yā ceva kho pana ajjhattikā tejodhātu yā ca bāhirā tejodhātu, tejodhāturevesā. ‘taṃ netaṃ mama<a name="T1.0355"></a>, nesohamasmi, na meso attā’ti evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabbaṃ. evametaṃ yathābhūtaṃ sammappaññāya disvā tejodhātuyā nibbindati, tejodhātuyā cittaṃ virājeti.</p>\r\n','\r',char(13)),'\n',char(10)),'-304-');
INSERT INTO pages VALUES(3285,'mula_ma_01',246,replace(replace('<p class="bodytext">hoti <a name="M1.0246"></a> kho so, āvuso, samayo yaṃ bāhirā tejodhātu pakuppati. sā gāmampi dahati, nigamampi dahati, nagarampi dahati, janapadampi dahati, janapadapadesampi dahati. sā haritantaṃ vā panthantaṃ vā selantaṃ vā udakantaṃ vā ramaṇīyaṃ vā bhūmibhāgaṃ āgamma anāhārā nibbāyati. hoti kho so, āvuso, samayo yaṃ kukkuṭapattenapi nhārudaddulenapi aggiṃ gavesanti<a name="V1.0249"></a>. tassā hi nāma, āvuso, bāhirāya tejodhātuyā tāva mahallikāya aniccatā paññāyissati, khayadhammatā paññāyissati, vayadhammatā paññāyissati, vipariṇāmadhammatā paññāyissati. kiṃ panimassa mattaṭṭhakassa kāyassa taṇhupādinnassa ‘ahanti vā mamanti vā asmī’ti vā? atha khvāssa notevettha hoti…pe… tassa ce, āvuso, bhikkhuno evaṃ buddhaṃ anussarato evaṃ dhammaṃ anussarato evaṃ saṅghaṃ anussarato upekkhā kusalanissitā saṇṭhāti, so tena attamano hoti. ettāvatāpi kho, āvuso, bhikkhuno bahukataṃ hoti.</p>\r\n<p class="bodytext"><a name="para305"></a><a name="para305_mn1"></a><span class="paranum">305</span>. katamā cāvuso, vāyodhātu? vāyodhātu siyā ajjhattikā, siyā bāhirā. katamā cāvuso, ajjhattikā vāyodhātu? yaṃ ajjhattaṃ paccattaṃ vāyo vāyogataṃ upādinnaṃ, seyyathidaṃ – uddhaṅgamā vātā, adhogamā vātā, kucchisayā vātā, koṭṭhāsayā <span class="note">[koṭṭhasayā (sī. pī.)]</span> vātā, aṅgamaṅgānusārino <a name="T1.0356"></a> vātā, assāso passāso iti, yaṃ vā panaññampi kiñci ajjhattaṃ paccattaṃ vāyo vāyogataṃ upādinnaṃ – ayaṃ vuccatāvuso, ajjhattikā vāyodhātu. yā ceva kho pana ajjhattikā vāyodhātu, yā ca bāhirā vāyodhātu, vāyodhāturevesā. ‘taṃ netaṃ mama nesohamasmi na meso attā’ti evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabbaṃ. evametaṃ yathābhūtaṃ sammappaññāya disvā vāyodhātuyā nibbindati vāyodhātuyā cittaṃ virājeti.</p>\r\n<p class="bodytext">hoti <a name="P1.0189"></a> kho so, āvuso, samayo yaṃ bāhirā vāyodhātu pakuppati. sā gāmampi vahati, nigamampi vahati, nagarampi vahati, janapadampi vahati, janapadapadesampi vahati. hoti kho so, āvuso, samayo yaṃ gimhānaṃ pacchime māse tālavaṇṭenapi vidhūpanenapi vātaṃ pariyesanti,</p>\r\n','\r',char(13)),'\n',char(10)),'-305-');
INSERT INTO pages VALUES(3286,'mula_ma_01',247,replace(replace('<p class="bodytext">ossavanepi <a name="M1.0247"></a> tiṇāni na icchanti. tassā hi nāma, āvuso, bāhirāya vāyodhātuyā tāva mahallikāya aniccatā paññāyissati, khayadhammatā paññāyissati, vayadhammatā paññāyissati, vipariṇāmadhammatā paññāyissati. kiṃ panimassa mattaṭṭhakassa kāyassa taṇhupādinnassa ‘ahanti vā mamanti vā asmī’ti vā? atha khvāssa notevettha hoti.</p>\r\n<p class="bodytext">tañce, āvuso, bhikkhuṃ pare akkosanti paribhāsanti rosenti vihesenti. so evaṃ pajānāti, uppannā kho me ayaṃ sotasamphassajā dukkhā vedanā. sā ca kho paṭicca, no apaṭicca. kiṃ paṭicca? phassaṃ paṭicca. sopi phasso aniccoti passati, vedanā aniccāti <a name="T1.0357"></a> passati<a name="V1.0250"></a>, saññā aniccāti passati, saṅkhārā aniccāti passati, viññāṇaṃ aniccanti passati. tassa dhātārammaṇameva cittaṃ pakkhandati pasīdati santiṭṭhati adhimuccati.</p>\r\n<p class="bodytext">tañce, āvuso, bhikkhuṃ pare aniṭṭhehi akantehi amanāpehi samudācaranti, pāṇisamphassenapi leḍḍusamphassenapi daṇḍasamphassenapi satthasamphassenapi. so evaṃ pajānāti ‘tathābhūto kho ayaṃ kāyo yathābhūtasmiṃ kāye pāṇisamphassāpi kamanti, leḍḍusamphassāpi kamanti, daṇḍasamphassāpi kamanti, satthasamphassāpi kamanti. vuttaṃ kho panetaṃ bhagavatā kakacūpamovāde "ubhatodaṇḍakena cepi, bhikkhave, kakacena corā ocarakā aṅgamaṅgāni okanteyyuṃ. tatrāpi yo mano padūseyya, na me so tena sāsanakaro"ti. āraddhaṃ kho pana me vīriyaṃ bhavissati asallīnaṃ, upaṭṭhitā sati asammuṭṭhā, passaddho kāyo asāraddho, samāhitaṃ cittaṃ ekaggaṃ. kāmaṃ dāni imasmiṃ kāye pāṇisamphassāpi kamantu, leḍḍusamphassāpi kamantu, daṇḍasamphassāpi kamantu, satthasamphassāpi kamantu. karīyati hidaṃ buddhānaṃ sāsana’nti.</p>\r\n<p class="bodytext">tassa ce, āvuso, bhikkhuno evaṃ buddhaṃ anussarato, evaṃ dhammaṃ anussarato, evaṃ saṅghaṃ anussarato upekkhā kusalanissitā na saṇṭhāti. so tena saṃvijjati saṃvegaṃ āpajjati – ‘alābhā vata me, na vata me lābhā, dulladdhaṃ vata me, na vata me suladdhaṃ. yassa me evaṃ buddhaṃ <a name="P1.0190"></a> anussarato, evaṃ dhammaṃ anussarato, evaṃ saṅghaṃ anussarato upekkhā kusalanissitā <a name="T1.0358"></a> na saṇṭhātī’ti. seyyathāpi, āvuso, suṇisā sasuraṃ disvā saṃvijjati saṃvegaṃ āpajjati, evameva kho, āvuso, tassa</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3287,'mula_ma_01',248,replace(replace('<p class="bodytext">ce <a name="M1.0248"></a> bhikkhuno evaṃ buddhaṃ anussarato, evaṃ dhammaṃ anussarato, evaṃ saṅghaṃ anussarato, upekkhā kusalanissitā na saṇṭhāti. so tena saṃvijjati saṃvegaṃ āpajjati – ‘alābhā vata me, na vata me lābhā, dulladdhaṃ vata me, na vata me suladdhaṃ. yassa me evaṃ buddhaṃ anussarato, evaṃ dhammaṃ anussarato, evaṃ saṅghaṃ anussarato, upekkhā kusalanissitā na saṇṭhātī’ti. tassa ce, āvuso, bhikkhuno evaṃ buddhaṃ anussarato, evaṃ dhammaṃ anussarato, evaṃ saṅghaṃ anussarato, upekkhā kusalanissitā saṇṭhāti, so tena attamano hoti. ettāvatāpi kho, āvuso, bhikkhuno bahukataṃ hoti.</p>\r\n<p class="bodytext"><a name="para306"></a><a name="para306_mn1"></a><span class="paranum">306</span>. seyyathāpi, āvuso, kaṭṭhañca paṭicca valliñca paṭicca tiṇañca paṭicca mattikañca paṭicca ākāso parivārito agāraṃ tveva saṅkhaṃ gacchati, evameva kho, āvuso, aṭṭhiñca <a name="V1.0251"></a> paṭicca nhāruñca paṭicca maṃsañca paṭicca cammañca paṭicca ākāso parivārito rūpaṃ tveva saṅkhaṃ gacchati. ajjhattikañceva, āvuso, cakkhuṃ aparibhinnaṃ hoti, bāhirā ca rūpā na āpāthaṃ āgacchanti, no ca tajjo samannāhāro hoti, neva tāva tajjassa viññāṇabhāgassa pātubhāvo hoti. ajjhattikañceva <span class="note">[ajjhattikañce (sī. syā. pī.), ajjhattikañcepi (?)]</span>, āvuso, cakkhuṃ aparibhinnaṃ hoti bāhirā ca rūpā āpāthaṃ āgacchanti, no ca tajjo samannāhāro hoti, neva <a name="T1.0359"></a> tāva tajjassa viññāṇabhāgassa pātubhāvo hoti. yato ca kho, āvuso, ajjhattikañceva cakkhuṃ aparibhinnaṃ hoti, bāhirā ca rūpā āpāthaṃ āgacchanti, tajjo ca samannāhāro hoti. evaṃ tajjassa viññāṇabhāgassa pātubhāvo hoti. yaṃ tathābhūtassa rūpaṃ taṃ rūpupādānakkhandhe saṅgahaṃ gacchati, yā tathābhūtassa vedanā sā vedanupādānakkhandhe saṅgahaṃ gacchati, yā tathābhūtassa saññā sā saññupādānakkhandhe saṅgahaṃ gacchati, ye tathābhūtassa saṅkhārā te saṅkhārupādānakkhandhe saṅgahaṃ gacchanti, yaṃ tathābhūtassa viññāṇaṃ taṃ viññāṇupādānakkhandhe saṅgahaṃ gacchati.</p>\r\n<p class="bodytext">so evaṃ pajānāti – ‘evañhi kira imesaṃ pañcannaṃ upādānakkhandhānaṃ saṅgaho sannipāto samavāyo hoti. vuttaṃ kho panetaṃ bhagavatā – ‘yo paṭiccasamuppādaṃ passati <a name="P1.0191"></a> so dhammaṃ passati, yo dhammaṃ passati so paṭiccasamuppādaṃ passatīti. paṭiccasamuppannā kho panime yadidaṃ pañcupādānakkhandhā. yo imesu</p>\r\n','\r',char(13)),'\n',char(10)),'-306-');
INSERT INTO pages VALUES(3288,'mula_ma_01',249,replace(replace('<p class="bodytext">pañcasu <a name="M1.0249"></a> upādānakkhandhesu chando ālayo anunayo ajjhosānaṃ so dukkhasamudayo. yo imesu pañcasu upādānakkhandhesu chandarāgavinayo chandarāgappahānaṃ, so dukkhanirodho’ti. ettāvatāpi kho, āvuso, bhikkhuno bahukataṃ hoti.</p>\r\n<p class="bodytext">ajjhattikañceva, āvuso, sotaṃ aparibhinnaṃ hoti…pe… ghānaṃ aparibhinnaṃ hoti… jivhā aparibhinnā hoti… kāyo aparibhinno hoti… mano aparibhinno hoti, bāhirā ca dhammā na āpāthaṃ āgacchanti <a name="T1.0360"></a> no ca tajjo samannāhāro hoti, neva tāva tajjassa viññāṇabhāgassa pātubhāvo hoti. ajjhattiko ceva, āvuso, mano aparibhinno hoti, bāhirā ca dhammā āpāthaṃ āgacchanti, no ca tajjo samannāhāro hoti, neva tāva tajjassa viññāṇabhāgassa pātubhāvo hoti. yato ca kho, āvuso, ajjhattiko ceva mano aparibhinno hoti, bāhirā ca dhammā āpāthaṃ āgacchanti, tajjo ca samannāhāro hoti, evaṃ tajjassa viññāṇabhāgassa pātubhāvo hoti. yaṃ tathābhūtassa rūpaṃ taṃ rūpupādānakkhandhe saṅgahaṃ gacchati, yā tathābhūtassa vedanā sā vedanupādānakkhandhe saṅgahaṃ gacchati, yā tathābhūtassa saññā <a name="V1.0252"></a> sā saññupādānakkhandhe saṅgahaṃ gacchati, ye tathābhūtassa saṅkhārā te saṅkhārupādānakkhandhe saṅgahaṃ gacchanti, yaṃ tathābhūtassa viññāṇaṃ taṃ viññāṇupādānakkhandhe saṅgahaṃ gacchati. so evaṃ pajānāti – ‘evañhi kira imesaṃ pañcannaṃ upādānakkhandhānaṃ saṅgaho sannipāto samavāyo hoti. vuttaṃ kho panetaṃ bhagavatā – "yo paṭiccasamuppādaṃ passati so dhammaṃ passati, yo dhammaṃ passati so paṭiccasamuppādaṃ passatī"ti. paṭiccasamuppannā kho panime yadidaṃ pañcupādānakkhandhā. yo imesu pañcasu upādānakkhandhesu chando ālayo anunayo ajjhosānaṃ so dukkhasamudayo. yo imesu pañcasu upādānakkhandhesu chandarāgavinayo chandarāgappahānaṃ so dukkhanirodho’ti. ettāvatāpi kho, āvuso, bhikkhuno bahukataṃ hotī"ti.</p>\r\n<p class="bodytext">idamavoca <a name="T1.0361"></a> āyasmā sāriputto. attamanā te bhikkhū āyasmato sāriputtassa bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">mahāhatthipadopamasuttaṃ niṭṭhitaṃ aṭṭhamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3289,'mula_ma_01',250,replace(replace('<p class="subhead">9. mahāsāropamasuttaṃ</p>\r\n<p class="bodytext"><a name="para307"></a><a name="para307_mn1"></a><span class="paranum">307</span>. evaṃ <a name="T1.0362"></a><a name="M1.0250"></a><a name="V1.0253"></a><a name="P1.0192"></a> me sutaṃ – ekaṃ samayaṃ bhagavā rājagahe viharati gijjhakūṭe pabbate acirapakkante devadatte. tatra kho bhagavā devadattaṃ ārabbha bhikkhū āmantesi –</p>\r\n<p class="bodytext">idha, bhikkhave, ekacco kulaputto saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā’ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena attamano hoti paripuṇṇasaṅkappo. so tena lābhasakkārasilokena attānukkaṃseti paraṃ vambheti – ‘ahamasmi lābhasakkārasilokavā <span class="note">[lābhī silokavā (sī. pī.), lābhī sakkāra silokavā (syā.)]</span>, ime panaññe bhikkhū appaññātā appesakkhā’ti. so tena lābhasakkārasilokena majjati pamajjati pamādaṃ āpajjati, pamatto samāno dukkhaṃ viharati.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ atikkamma tacaṃ atikkamma papaṭikaṃ, sākhāpalāsaṃ chetvā ādāya pakkameyya ‘sāra’nti maññamāno. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘na vatāyaṃ bhavaṃ puriso aññāsi <a name="T1.0363"></a> sāraṃ, na aññāsi phegguṃ, na aññāsi tacaṃ, na aññāsi papaṭikaṃ, na aññāsi sākhāpalāsaṃ. tathā hayaṃ <span class="note">[tathāpāyaṃ (ka.)]</span> bhavaṃ puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ atikkamma tacaṃ atikkamma papaṭikaṃ, sākhāpalāsaṃ chetvā ādāya pakkanto ‘sāra’nti maññamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ nānubhavissatī’ti. evameva kho, bhikkhave, idhekacco kulaputto saddhā agārasmā anagāriyaṃ pabbajito hoti – otiṇṇomhi jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo</p>\r\n','\r',char(13)),'\n',char(10)),'-307-');
INSERT INTO pages VALUES(3290,'mula_ma_01',251,replace(replace('<p class="bodytext">dukkhapareto<a name="M1.0251"></a>, appeva nāma imassa kevalassa antakiriyā paññāyethā’ti. so evaṃ pabbajito <a name="V1.0254"></a> samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena attamano hoti paripuṇṇasaṅkappo. so tena lābhasakkārasilokena attānukkaṃseti, paraṃ vambheti <a name="P1.0193"></a> ‘ahamasmi lābhasakkārasilokavā, ime panaññe bhikkhū appaññātā appesakkhā’ti. so tena lābhasakkārasilokena majjati pamajjati pamādaṃ āpajjati, pamatto samāno dukkhaṃ viharati. ayaṃ vuccati, bhikkhave, bhikkhu sākhāpalāsaṃ aggahesi brahmacariyassa, tena ca vosānaṃ āpādi.</p>\r\n<p class="bodytext"><a name="para308"></a><a name="para308_mn1"></a><span class="paranum">308</span>. idha pana, bhikkhave, ekacco kulaputto saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi <a name="T1.0364"></a> paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā’ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena na attamano hoti na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. so tena lābhasakkārasilokena na majjati nappamajjati na pamādaṃ āpajjati. appamatto samāno sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti paripuṇṇasaṅkappo. so tāya sīlasampadāya attānukkaṃseti, paraṃ vambheti – ‘ahamasmi sīlavā kalyāṇadhammo, ime panaññe bhikkhū dussīlā pāpadhammā’ti. so tāya sīlasampadāya majjati pamajjati pamādaṃ āpajjati, pamatto samāno dukkhaṃ viharati.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ atikkamma tacaṃ, papaṭikaṃ chetvā ādāya pakkameyya ‘sāra’nti maññamāno. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘na vatāyaṃ bhavaṃ puriso aññāsi sāraṃ, na aññāsi phegguṃ, na aññāsi tacaṃ, na aññāsi papaṭikaṃ, na aññāsi sākhāpalāsaṃ. tathā hayaṃ bhavaṃ puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva</p>\r\n','\r',char(13)),'\n',char(10)),'-308-');
INSERT INTO pages VALUES(3291,'mula_ma_01',252,replace(replace('<p class="bodytext">sāraṃ <a name="M1.0252"></a> atikkamma phegguṃ atikkamma <a name="T1.0365"></a> tacaṃ, papaṭikaṃ chetvā ādāya pakkanto ‘sāra’nti maññamāno, yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ nānubhavissatī’ti.</p>\r\n<p class="bodytext">evameva <a name="V1.0255"></a> kho, bhikkhave, idhekacco kulaputto saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā"ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena na attamano hoti na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. so tena lābhasakkārasilokena na majjati nappamajjati na pamādaṃ āpajjati. appamatto samāno sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti paripuṇṇasaṅkappo. so tāya sīlasampadāya attānukkaṃseti, paraṃ vambheti – ‘ahamasmi sīlavā kalyāṇadhammo, ime panaññe bhikkhū dussīlā pāpadhammā’ti. so tāya sīlasampadāya majjati pamajjati pamādaṃ āpajjati, pamatto samāno dukkhaṃ viharati. ayaṃ <a name="P1.0194"></a> vuccati, bhikkhave, bhikkhu papaṭikaṃ aggahesi brahmacariyassa, tena ca vosānaṃ āpādi.</p>\r\n<p class="bodytext"><a name="para309"></a><a name="para309_mn1"></a><span class="paranum">309</span>. idha pana, bhikkhave, ekacco kulaputto saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi <a name="T1.0366"></a> paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā’ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena na attamano hoti na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. so tena lābhasakkārasilokena na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti no ca kho paripuṇṇasaṅkappo. so tāya sīlasampadāya na attānukkaṃseti, na paraṃ vambheti. so tāya sīlasampadāya na majjati nappamajjati na pamādaṃ āpajjati. appamatto samāno samādhisampadaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-309-');
INSERT INTO pages VALUES(3292,'mula_ma_01',253,replace(replace('<p class="bodytext">ārādheti<a name="M1.0253"></a>. so tāya samādhisampadāya attamano hoti paripuṇṇasaṅkappo. so tāya samādhisampadāya attānukkaṃseti, paraṃ vambheti – ‘ahamasmi samāhito ekaggacitto, ime panaññe bhikkhū asamāhitā vibbhantacittā’ti. so tāya samādhisampadāya majjati pamajjati pamādaṃ āpajjati, pamatto samāno dukkhaṃ viharati.</p>\r\n<p class="bodytext">seyyathāpi<a name="V1.0256"></a>, bhikkhave, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ tacaṃ chetvā ādāya pakkameyya ‘sāra’nti maññamāno. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya ‘na vatāyaṃ bhavaṃ puriso aññāsi sāraṃ<a name="T1.0367"></a>, na aññāsi phegguṃ, na aññāsi tacaṃ, na aññāsi papaṭikaṃ, na aññāsi sākhāpalāsaṃ. tathā hayaṃ bhavaṃ puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ tacaṃ chetvā ādāya pakkanto ‘sāra’nti maññamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ nānubhavissatī’ti.</p>\r\n<p class="bodytext">evameva kho, bhikkhave, idhekacco kulaputto saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā"ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena na attamano hoti na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. so tena lābhasakkārasilokena na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti no ca kho paripuṇṇasaṅkappo. so tāya sīlasampadāya na attānukkaṃseti, na paraṃ vambheti. so tāya sīlasampadāya na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno samādhisampadaṃ ārādheti. so tāya samādhisampadāya attamano hoti paripuṇṇasaṅkappo. so tāya samādhisampadāya attānukkaṃseti, paraṃ vambheti – ‘ahamasmi <a name="T1.0368"></a> samāhito ekaggacitto, ime panaññe bhikkhū asamāhitā vibbhantacittā’ti. so</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3293,'mula_ma_01',254,replace(replace('<p class="bodytext">tāya <a name="M1.0254"></a> samādhisampadāya majjati pamajjati pamādaṃ āpajjati, pamatto samāno dukkhaṃ viharati. ayaṃ vuccati<a name="P1.0195"></a>, bhikkhave, bhikkhu tacaṃ aggahesi brahmacariyassa, tena ca vosānaṃ āpādi.</p>\r\n<p class="bodytext"><a name="para310"></a><a name="para310_mn1"></a><span class="paranum">310</span>. idha pana, bhikkhave, ekacco kulaputto saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa <a name="V1.0257"></a> antakiriyā paññāyethā’ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena na attamano hoti na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. so tena lābhasakkārasilokena na majjati nappamajjati na pamādaṃ āpajjati. appamatto samāno sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya sīlasampadāya na attānukkaṃseti, na paraṃ vambheti. so tāya sīlasampadāya na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno samādhisampadaṃ ārādheti. so tāya samādhisampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya samādhisampadāya na attānukkaṃseti, na paraṃ vambheti. so tāya samādhisampadāya na majjati nappamajjati <a name="T1.0369"></a> na pamādaṃ āpajjati appamatto samāno ñāṇadassanaṃ ārādheti. so tena ñāṇadassanena attamano hoti paripuṇṇasaṅkappo. so tena ñāṇadassanena attānukkaṃseti, paraṃ vambheti – ‘ahamasmi jānaṃ passaṃ viharāmi. ime panaññe bhikkhū ajānaṃ apassaṃ viharantī’ti. so tena ñāṇadassanena majjati pamajjati pamādaṃ āpajjati, pamatto samāno dukkhaṃ viharati.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ phegguṃ chetvā ādāya pakkameyya ‘sāra’nti maññamāno. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘na vatāyaṃ bhavaṃ puriso aññāsi sāraṃ na aññāsi phegguṃ na aññāsi tacaṃ na aññāsi papaṭikaṃ na aññāsi sākhāpalāsaṃ. tathā hayaṃ bhavaṃ puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato</p>\r\n','\r',char(13)),'\n',char(10)),'-310-');
INSERT INTO pages VALUES(3294,'mula_ma_01',255,replace(replace('<p class="bodytext">rukkhassa <a name="M1.0255"></a> tiṭṭhato sāravato atikkammeva sāraṃ phegguṃ chetvā ādāya pakkanto ‘sāra’nti maññamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ nānubhavissatī’ti. evameva <a name="P1.0196"></a> kho, bhikkhave, idhekacco kulaputto saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā’ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena <a name="T1.0370"></a> na attamano hoti na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. so <a name="V1.0258"></a> tena lābhasakkārasilokena na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya sīlasampadāya na attānukkaṃseti, na paraṃ vambheti. so tāya sīlasampadāya na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno samādhisampadaṃ ārādheti. so tāya samādhisampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya samādhisampadāya na attānukkaṃseti, na paraṃ vambheti. so tāya samādhisampadāya na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno ñāṇadassanaṃ ārādheti. so tena ñāṇadassanena attamano hoti paripuṇṇasaṅkappo. so tena ñāṇadassanena attānukkaṃseti, paraṃ vambheti – ‘ahamasmi jānaṃ passaṃ viharāmi, ime panaññe bhikkhū ajānaṃ apassaṃ viharantī’ti. so tena ñāṇadassanena majjati pamajjati pamādaṃ āpajjati, pamatto samāno dukkhaṃ viharati. ayaṃ vuccati, bhikkhave, bhikkhu phegguṃ aggahesi brahmacariyassa, tena ca vosānaṃ āpādi.</p>\r\n<p class="bodytext"><a name="para311"></a><a name="para311_mn1"></a><span class="paranum">311</span>. idha pana, bhikkhave, ekacco kulaputto saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi <a name="T1.0371"></a> paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā’ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena na attamano</p>\r\n','\r',char(13)),'\n',char(10)),'-311-');
INSERT INTO pages VALUES(3295,'mula_ma_01',256,replace(replace('<p class="bodytext">hoti<a name="M1.0256"></a>, na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. so tena lābhasakkārasilokena na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya sīlasampadāya na attānukkaṃseti, na paraṃ vambheti. so tāya sīlasampadāya na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno samādhisampadaṃ ārādheti. so tāya samādhisampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya samādhisampadāya na attānukkaṃseti, na paraṃ vambheti. so tāya samādhisampadāya na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno ñāṇadassanaṃ ārādheti. so tena ñāṇadassanena attamano hoti, no ca kho paripuṇṇasaṅkappo. so tena ñāṇadassanena na attānukkaṃseti, na paraṃ vambheti. so tena ñāṇadassanena na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno asamayavimokkhaṃ <a name="V1.0259"></a> ārādheti. aṭṭhānametaṃ <span class="note">[aṭṭhānaṃ kho panetaṃ (ka.)]</span>, bhikkhave, anavakāso yaṃ so bhikkhu tāya asamayavimuttiyā parihāyetha.</p>\r\n<p class="bodytext">seyyathāpi<a name="T1.0372"></a>, bhikkhave, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato sāraññeva chetvā ādāya pakkameyya ‘sāra’nti jānamāno. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘aññāsi vatāyaṃ bhavaṃ puriso sāraṃ, aññāsi phegguṃ, aññāsi tacaṃ, aññāsi papaṭikaṃ, aññāsi sākhāpalāsaṃ. tathā hayaṃ bhavaṃ puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato <a name="P1.0197"></a> rukkhassa tiṭṭhato sāravato sāraññeva chetvā ādāya pakkanto ‘sāra’nti jānamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ anubhavissatī’ti.</p>\r\n<p class="bodytext">evameva kho, bhikkhave, idhekacco kulaputto saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā’ti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3296,'mula_ma_01',257,replace(replace('<p class="bodytext">so <a name="M1.0257"></a> evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena na attamano hoti, na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. so tena lābhasakkārasilokena na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya sīlasampadāya na attānukkaṃseti, na paraṃ vambheti. so tāya sīlasampadāya na majjati nappamajjati <a name="T1.0373"></a> na pamādaṃ āpajjati, appamatto samāno samādhisampadaṃ ārādheti. so tāya samādhisampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya samādhisampadāya na attānukkaṃseti, na paraṃ vambheti. so tāya samādhisampadāya na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno ñāṇadassanaṃ ārādheti. so tena ñāṇadassanena attamano hoti, no ca kho paripuṇṇasaṅkappo. so tena ñāṇadassanena na attānukkaṃseti, na paraṃ vambheti. so tena ñāṇadassanena na majjati nappamajjati na pamādaṃ āpajjati, appamatto samāno asamayavimokkhaṃ <a name="V1.0260"></a> ārādheti. aṭṭhānametaṃ, bhikkhave, anavakāso yaṃ so bhikkhu tāya asamayavimuttiyā parihāyetha.</p>\r\n<p class="bodytext">iti kho, bhikkhave, nayidaṃ brahmacariyaṃ lābhasakkārasilokānisaṃsaṃ, na sīlasampadānisaṃsaṃ, na samādhisampadānisaṃsaṃ, na ñāṇadassanānisaṃsaṃ. yā ca kho ayaṃ, bhikkhave, akuppā cetovimutti – etadatthamidaṃ, bhikkhave, brahmacariyaṃ, etaṃ sāraṃ etaṃ pariyosāna"nti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">mahāsāropamasuttaṃ niṭṭhitaṃ navamaṃ.</p>\r\n<p class="subhead">10. cūḷasāropamasuttaṃ</p>\r\n<p class="bodytext"><a name="para312"></a><a name="para312_mn1"></a><span class="paranum">312</span>. evaṃ <a name="T1.0374"></a><a name="V1.0261"></a><a name="P1.0198"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. atha kho piṅgalakoccho brāhmaṇo</p>\r\n','\r',char(13)),'\n',char(10)),'-312-');
INSERT INTO pages VALUES(3297,'mula_ma_01',258,replace(replace('<p class="bodytext">yena <a name="M1.0258"></a> bhagavā tenupasaṅkami, upasaṅkamitvā bhagavatā saddhiṃ sammodi. sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho piṅgalakoccho brāhmaṇo bhagavantaṃ etadavoca – "yeme, bho gotama, samaṇabrāhmaṇā saṅghino gaṇino gaṇācariyā ñātā yasassino titthakarā sādhusammatā, bahujanassa, seyyathidaṃ – pūraṇo kassapo, makkhali gosālo, ajito kesakambalo, pakudho kaccāyano, sañcayo <span class="note">[sañjayo (sī. syā. pī. ka.)]</span> belaṭṭhaputto, nigaṇṭho nāṭaputto, sabbete sakāya paṭiññāya abbhaññaṃsu sabbeva nābbhaññaṃsu, udāhu ekacce abbhaññaṃsu ekacce nābbhaññaṃsū"ti? "alaṃ, brāhmaṇa, tiṭṭhatetaṃ – sabbete sakāya paṭiññāya abbhaññaṃsu sabbeva nābbhaññaṃsu, udāhu ekacce abbhaññaṃsu ekacce nābbhaññaṃsūti. dhammaṃ te, brāhmaṇa, desessāmi, taṃ suṇāhi, sādhukaṃ manasi karohi, bhāsissāmī"ti. "evaṃ, bho"ti kho piṅgalakoccho brāhmaṇo bhagavato paccassosi. bhagavā etadavoca –</p>\r\n<p class="bodytext"><a name="para313"></a><a name="para313_mn1"></a><span class="paranum">313</span>. seyyathāpi, brāhmaṇa, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ atikkamma tacaṃ <a name="T1.0375"></a> atikkamma papaṭikaṃ, sākhāpalāsaṃ chetvā ādāya pakkameyya ‘sāra’nti maññamāno. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘na vatāyaṃ bhavaṃ puriso aññāsi sāraṃ, na aññāsi phegguṃ, na aññāsi tacaṃ, na aññāsi papaṭikaṃ, na aññāsi sākhāpalāsaṃ. tathā hayaṃ bhavaṃ puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ atikkamma tacaṃ atikkamma papaṭikaṃ, sākhāpalāsaṃ chetvā ādāya pakkanto ‘sāra’nti maññamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ nānubhavissatī’ti.</p>\r\n<p class="bodytext"><a name="para314"></a><a name="para314_mn1"></a><span class="paranum">314</span>. seyyathāpi vā pana, brāhmaṇa, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno <a name="V1.0262"></a> mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ atikkamma tacaṃ<a name="P1.0199"></a>, papaṭikaṃ chetvā</p>\r\n','\r',char(13)),'\n',char(10)),'-313-314-');
INSERT INTO pages VALUES(3298,'mula_ma_01',259,replace(replace('<p class="bodytext">ādāya <a name="M1.0259"></a> pakkameyya ‘sāra’nti maññamāno. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘na vatāyaṃ bhavaṃ puriso aññāsi sāraṃ, na aññāsi phegguṃ, na aññāsi tacaṃ, na aññāsi papaṭikaṃ, na aññāsi sākhāpalāsaṃ. tathā hayaṃ bhavaṃ puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ atikkamma tacaṃ papaṭikaṃ chetvā ādāya pakkanto ‘sāra’nti maññamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ nānubhavissatī’ti.</p>\r\n<p class="bodytext"><a name="para315"></a><a name="para315_mn1"></a><span class="paranum">315</span>. seyyathāpi vā pana, brāhmaṇa, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato <a name="T1.0376"></a> sāravato atikkammeva sāraṃ atikkamma phegguṃ, tacaṃ chetvā ādāya pakkameyya ‘sāra’nti maññamāno. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘na vatāyaṃ bhavaṃ puriso aññāsi sāraṃ, na aññāsi phegguṃ, na aññāsi tacaṃ, na aññāsi papaṭikaṃ, na aññāsi sākhāpalāsaṃ. tathā hayaṃ bhavaṃ puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ, tacaṃ chetvā ādāya pakkanto ‘sāra’nti maññamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ nānubhavissatī’ti.</p>\r\n<p class="bodytext"><a name="para316"></a><a name="para316_mn1"></a><span class="paranum">316</span>. seyyathāpi vā pana, brāhmaṇa, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ, phegguṃ chetvā ādāya pakkameyya ‘sāra’nti maññamāno. tamenaṃ cakkhumā puriso disvā evaṃ vadeyya – ‘na vatāyaṃ bhavaṃ puriso aññāsi sāraṃ, na aññāsi phegguṃ, na aññāsi tacaṃ, na aññāsi papaṭikaṃ, na aññāsi sākhāpalāsaṃ. tathā hayaṃ bhavaṃ puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ, phegguṃ chetvā ādāya pakkanto ‘sāra’nti maññamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ nānubhavissatī’ti.</p>\r\n<p class="bodytext"><a name="para317"></a><a name="para317_mn1"></a><span class="paranum">317</span>. seyyathāpi vā pana, brāhmaṇa, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno <a name="V1.0263"></a> mahato rukkhassa tiṭṭhato sāravato</p>\r\n','\r',char(13)),'\n',char(10)),'-315-316-317-');
INSERT INTO pages VALUES(3299,'mula_ma_01',260,replace(replace('<p class="bodytext">sāraññeva <a name="M1.0260"></a> chetvā ādāya pakkameyya ‘sāra’nti jānamāno. tamenaṃ cakkhumā<a name="T1.0377"></a> puriso disvā evaṃ vadeyya – ‘aññāsi vatāyaṃ bhavaṃ puriso sāraṃ, aññāsi phegguṃ, aññāsi tacaṃ, aññāsi papaṭikaṃ, aññāsi sākhāpalāsaṃ. tathā hayaṃ bhavaṃ puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato sāraññeva chetvā ādāya <a name="P1.0200"></a> pakkanto ‘sāra’nti jānamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ anubhavissatī’ti.</p>\r\n<p class="bodytext"><a name="para318"></a><a name="para318_mn1"></a><span class="paranum">318</span>. evameva kho, brāhmaṇa, idhekacco puggalo saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā’ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena attamano hoti paripuṇṇasaṅkappo. so tena lābhasakkārasilokena attānukkaṃseti, paraṃ vambheti – ‘ahamasmi lābhasakkārasilokavā, ime panaññe bhikkhū appaññātā appesakkhā’ti. lābhasakkārasilokena ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ sacchikiriyāya na chandaṃ janeti, na vāyamati, olīnavuttiko ca hoti sāthaliko. seyyathāpi so, brāhmaṇa, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ atikkamma tacaṃ atikkamma papaṭikaṃ, sākhāpalāsaṃ chetvā <a name="T1.0378"></a> ādāya pakkanto ‘sāra’nti maññamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ nānubhavissati. tathūpamāhaṃ, brāhmaṇa, imaṃ puggalaṃ vadāmi.</p>\r\n<p class="bodytext"><a name="para319"></a><a name="para319_mn1"></a><span class="paranum">319</span>. idha pana, brāhmaṇa, ekacco puggalo saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā’ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena</p>\r\n','\r',char(13)),'\n',char(10)),'-318-319-');
INSERT INTO pages VALUES(3300,'mula_ma_01',261,replace(replace('<p class="bodytext">na <a name="M1.0261"></a> attamano hoti na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. lābhasakkārasilokena ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ sacchikiriyāya chandaṃ janeti, vāyamati<a name="V1.0264"></a>, anolīnavuttiko ca hoti asāthaliko. so sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti, paripuṇṇasaṅkappo. so tāya sīlasampadāya attānukkaṃseti, paraṃ vambheti – ‘ahamasmi sīlavā kalyāṇadhammo, ime panaññe bhikkhū dussīlā pāpadhammā’ti. sīlasampadāya ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ <a name="P1.0201"></a> sacchikiriyāya na chandaṃ janeti, na vāyamati, olīnavuttiko ca hoti sāthaliko. seyyathāpi so, brāhmaṇa, puriso sāratthiko sāragavesī <a name="T1.0379"></a> sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ atikkamma tacaṃ, papaṭikaṃ chetvā ādāya pakkanto ‘sāra’nti maññamāno. yañcassa sārena sārakaraṇīyaṃ, tañcassa atthaṃ nānubhavissati. tathūpamāhaṃ, brāhmaṇa, imaṃ puggalaṃ vadāmi.</p>\r\n<p class="bodytext"><a name="para320"></a><a name="para320_mn1"></a><span class="paranum">320</span>. idha pana, brāhmaṇa, ekacco puggalo saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, dukkhotiṇṇo dukkhapareto, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā’ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena na attamano hoti, na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. lābhasakkārasilokena ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ sacchikiriyāya chandaṃ janeti, vāyamati, anolīnavuttiko ca hoti asāthaliko. so sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti no ca kho paripuṇṇasaṅkappo. so tāya sīlasampadāya na attānukkaṃseti, na paraṃ vambheti. sīlasampadāya ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ sacchikiriyāya chandaṃ janeti, vāyamati, anolīnavuttiko ca hoti</p>\r\n','\r',char(13)),'\n',char(10)),'-320-');
INSERT INTO pages VALUES(3301,'mula_ma_01',262,replace(replace('<p class="bodytext">asāthaliko<a name="M1.0262"></a>. so samādhisampadaṃ ārādheti. so tāya samādhisampadāya attamano hoti, paripuṇṇasaṅkappo. so tāya samādhisampadāya attānukkaṃseti, paraṃ vambheti – ‘ahamasmi samāhito ekaggacitto, ime panaññe bhikkhū <a name="T1.0380"></a> asamāhitā vibbhantacittā’ti. samādhisampadāya ca ye aññe dhammā uttaritarā ca paṇītatarā ca, tesaṃ dhammānaṃ sacchikiriyāya na chandaṃ janeti, na vāyamati, olīnavuttiko ca hoti sāthaliko. seyyathāpi so, brāhmaṇa, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ atikkamma phegguṃ, tacaṃ chetvā ādāya pakkanto ‘sāra’nti maññamāno. yañcassa <a name="V1.0265"></a> sārena sārakaraṇīyaṃ tañcassa atthaṃ nānubhavissati. tathūpamāhaṃ, brāhmaṇa, imaṃ puggalaṃ vadāmi.</p>\r\n<p class="bodytext"><a name="para321"></a><a name="para321_mn1"></a><span class="paranum">321</span>. idha pana, brāhmaṇa, ekacco puggalo saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena…pe… <a name="P1.0202"></a> antakiriyā paññāyethā’ti. so evaṃ pabbajito samāno lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena na attamano hoti na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. lābhasakkārasilokena ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ sacchikiriyāya chandaṃ janeti, vāyamati, anolīnavuttiko ca hoti asāthaliko. so sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya sīlasampadāya na attānukkaṃseti, na paraṃ vambheti. sīlasampadāya ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ sacchikiriyāya chandaṃ janeti, vāyamati, anolīnavuttiko ca hoti asāthaliko. so samādhisampadaṃ ārādheti. so tāya samādhisampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya samādhisampadāya na attānukkaṃseti, na paraṃ vambheti. samādhisampadāya ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ <a name="T1.0381"></a> sacchikiriyāya chandaṃ janeti, vāyamati, anolīnavuttiko ca hoti asāthaliko. so ñāṇadassanaṃ ārādheti. so tena ñāṇadassanena attamano hoti,</p>\r\n','\r',char(13)),'\n',char(10)),'-321-');
INSERT INTO pages VALUES(3302,'mula_ma_01',263,replace(replace('<p class="bodytext">paripuṇṇasaṅkappo<a name="M1.0263"></a>. so tena ñāṇadassanena attānukkaṃseti, paraṃ vambheti – ‘ahamasmi jānaṃ passaṃ viharāmi, ime panaññe bhikkhū ajānaṃ apassaṃ viharantī’ti. ñāṇadassanena ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ sacchikiriyāya na chandaṃ janeti, na vāyamati, olīnavuttiko ca hoti sāthaliko. seyyathāpi so, brāhmaṇa, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato atikkammeva sāraṃ, phegguṃ chetvā ādāya pakkanto ‘sāra’nti maññamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ nānubhavissati. tathūpamāhaṃ, brāhmaṇa, imaṃ puggalaṃ vadāmi.</p>\r\n<p class="bodytext"><a name="para322"></a><a name="para322_mn1"></a><span class="paranum">322</span>. idha pana, brāhmaṇa, ekacco puggalo saddhā agārasmā anagāriyaṃ pabbajito hoti – ‘otiṇṇomhi jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi<a name="V1.0266"></a>, dukkhotiṇṇo dukkhapareto<a name="T1.0382"></a>, appeva nāma imassa kevalassa dukkhakkhandhassa antakiriyā paññāyethā’ti. so evaṃ pabbajito samāno <a name="P1.0203"></a> lābhasakkārasilokaṃ abhinibbatteti. so tena lābhasakkārasilokena na attamano hoti, na paripuṇṇasaṅkappo. so tena lābhasakkārasilokena na attānukkaṃseti, na paraṃ vambheti. lābhasakkārasilokena ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ sacchikiriyāya chandaṃ janeti, vāyamati, anolīnavuttiko ca hoti asāthaliko. so sīlasampadaṃ ārādheti. so tāya sīlasampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya sīlasampadāya na attānukkaṃseti, na paraṃ vambheti. sīlasampadāya ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ sacchikiriyāya chandaṃ janeti, vāyamati, anolīnavuttiko ca hoti asāthaliko. so samādhisampadaṃ ārādheti. so tāya samādhisampadāya attamano hoti, no ca kho paripuṇṇasaṅkappo. so tāya samādhisampadāya na attānukkaṃseti, na paraṃ vambheti. samādhisampadāya ca ye aññe dhammā uttaritarā ca paṇītatarā ca tesaṃ dhammānaṃ sacchikiriyāya chandaṃ janeti, vāyamati, anolīnavuttiko ca hoti asāthaliko. so ñāṇadassanaṃ ārādheti. so tena ñāṇadassanena attamano hoti, no ca kho paripuṇṇasaṅkappo. so tena ñāṇadassanena na attānukkaṃseti,</p>\r\n','\r',char(13)),'\n',char(10)),'-322-');
INSERT INTO pages VALUES(3303,'mula_ma_01',264,replace(replace('<p class="bodytext">na <a name="M1.0264"></a> paraṃ vambheti. ñāṇadassanena ca ye aññe dhammā uttaritarā ca paṇītatarā <a name="T1.0383"></a> ca tesaṃ dhammānaṃ sacchikiriyāya chandaṃ janeti, vāyamati, anolīnavuttiko ca hoti asāthaliko.</p>\r\n<p class="bodytext"><a name="para323"></a><a name="para323_mn1"></a><span class="paranum">323</span>. katame ca, brāhmaṇa, dhammā ñāṇadassanena uttaritarā ca paṇītatarā ca? idha, brāhmaṇa, bhikkhu vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati. ayampi kho, brāhmaṇa, dhammo ñāṇadassanena uttaritaro ca paṇītataro ca.</p>\r\n<p class="bodytext">puna caparaṃ, brāhmaṇa, bhikkhu vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja viharati. ayampi kho, brāhmaṇa, dhammo ñāṇadassanena uttaritaro ca paṇītataro ca.</p>\r\n<p class="bodytext">puna caparaṃ, brāhmaṇa, bhikkhu pītiyā ca virāgā upekkhako ca viharati, sato ca sampajāno <a name="V1.0267"></a> sukhañca kāyena paṭisaṃvedeti, yaṃ taṃ ariyā ācikkhanti – ‘upekkhako satimā sukhavihārī’ti tatiyaṃ jhānaṃ upasampajja viharati. ayampi kho<a name="P1.0204"></a>, brāhmaṇa, dhammo ñāṇadassanena uttaritaro ca paṇītataro ca.</p>\r\n<p class="bodytext">puna caparaṃ, brāhmaṇa, bhikkhu sukhassa ca pahānā dukkhassa ca pahānā pubbeva somanassadomanassānaṃ atthaṅgamā adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja viharati. ayampi kho, brāhmaṇa, dhammo ñāṇadassanena uttaritaro ca paṇītataro ca.</p>\r\n<p class="bodytext">puna caparaṃ, brāhmaṇa, bhikkhu sabbaso rūpasaññānaṃ samatikkamā paṭighasaññānaṃ atthaṅgamā nānattasaññānaṃ amanasikārā ‘ananto ākāso’ti ākāsānañcāyatanaṃ upasampajja viharati. ayampi kho, brāhmaṇa, dhammo ñāṇadassanena uttaritaro ca paṇītataro ca.</p>\r\n<p class="bodytext">puna caparaṃ, brāhmaṇa, bhikkhu sabbaso ākāsānañcāyatanaṃ samatikkamma ‘anantaṃ viññāṇa’nti viññāṇañcāyatanaṃ upasampajja viharati. ayampi kho, brāhmaṇa, dhammo ñāṇadassanena uttaritaro ca paṇītataro ca.</p>\r\n<p class="bodytext">puna caparaṃ, brāhmaṇa, bhikkhu sabbaso viññāṇañcāyatanaṃ samatikkamma ‘natthi kiñcī’ti ākiñcaññāyatanaṃ upasampajja viharati. ayampi kho, brāhmaṇa, dhammo ñāṇadassanena uttaritaro ca paṇītataro ca.</p>\r\n','\r',char(13)),'\n',char(10)),'-323-');
INSERT INTO pages VALUES(3304,'mula_ma_01',265,replace(replace('<p class="bodytext">puna <a name="M1.0265"></a> caparaṃ, brāhmaṇa, bhikkhu sabbaso ākiñcaññāyatanaṃ samatikkamma nevasaññānāsaññāyatanaṃ upasampajja viharati. ayampi kho, brāhmaṇa, dhammo ñāṇadassanena uttaritaro ca <a name="T1.0384"></a> paṇītataro ca.</p>\r\n<p class="bodytext">puna caparaṃ, brāhmaṇa, bhikkhu sabbaso nevasaññānāsaññāyatanaṃ samatikkamma saññāvedayitanirodhaṃ upasampajja viharati, paññāya cassa disvā āsavā parikkhīṇā honti. ayampi kho, brāhmaṇa, dhammo ñāṇadassanena uttaritaro ca paṇītataro ca. ime kho, brāhmaṇa, dhammā ñāṇadassanena uttaritarā ca paṇītatarā ca.</p>\r\n<p class="bodytext"><a name="para324"></a><a name="para324_mn1"></a><span class="paranum">324</span>. seyyathāpi <a name="V1.0268"></a> so, brāhmaṇa, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno mahato rukkhassa tiṭṭhato sāravato sāraṃyeva chetvā ādāya pakkanto ‘sāra’nti jānamāno. yañcassa sārena sārakaraṇīyaṃ tañcassa atthaṃ anubhavissati. tathūpamāhaṃ, brāhmaṇa, imaṃ puggalaṃ vadāmi.</p>\r\n<p class="bodytext">iti kho, brāhmaṇa, nayidaṃ brahmacariyaṃ lābhasakkārasilokānisaṃsaṃ, na sīlasampadānisaṃsaṃ, na samādhisampadānisaṃsaṃ, na ñāṇadassanānisaṃsaṃ. yā ca kho ayaṃ<a name="P1.0205"></a>, brāhmaṇa, akuppā cetovimutti – etadatthamidaṃ, brāhmaṇa, brahmacariyaṃ, etaṃ sāraṃ etaṃ pariyosāna"nti.</p>\r\n<p class="bodytext">evaṃ vutte, piṅgalakoccho brāhmaṇo bhagavantaṃ etadavoca – "abhikkantaṃ, bho gotama, abhikkantaṃ, bho gotama…pe… upāsakaṃ maṃ bhavaṃ gotamo dhāretu ajjatagge pāṇupetaṃ saraṇaṃ gata"nti.</p>\r\n<p class="centered">cūḷasāropamasuttaṃ niṭṭhitaṃ dasamaṃ.</p>\r\n<p class="bodytext">tassuddānaṃ –</p>\r\n<p class="gatha1">moḷiyaphaggunariṭṭhañca <a name="T1.0385"></a> nāmo, andhavane kathipuṇṇaṃ nivāpo,</p>\r\n<p class="gathalast">rāsikaṇerumahāgajanāmo, sārūpamo <span class="note">[sāravaro (syā.), sāravano (ka.)]</span> puna piṅgalakoccho.</p>\r\n','\r',char(13)),'\n',char(10)),'-324-');
INSERT INTO pages VALUES(3305,'mula_ma_01',266,replace(replace('<p class="chapter">4. mahāyamakavaggo</p>\r\n<p class="subhead">1. cūḷagosiṅgasuttaṃ</p>\r\n<p class="bodytext"><a name="para325"></a><a name="para325_mn1"></a><span class="paranum">325</span>. evaṃ <a name="M1.0266"></a><a name="V1.0269"></a><a name="T1.0386"></a> me sutaṃ – ekaṃ samayaṃ bhagavā nātike <span class="note">[nādike (sī. syā. pī.), ñātike (ka.)]</span> viharati giñjakāvasathe. tena kho pana samayena āyasmā ca anuruddho āyasmā ca nandiyo āyasmā ca kimilo <span class="note">[kimbilo (sī. pī. ka.)]</span> gosiṅgasālavanadāye viharanti. atha kho bhagavā sāyanhasamayaṃ paṭisallānā vuṭṭhito yena gosiṅgasālavanadāyo tenupasaṅkami. addasā kho dāyapālo bhagavantaṃ dūratova āgacchantaṃ. disvāna bhagavantaṃ etadavoca – "mā, samaṇa, etaṃ dāyaṃ pāvisi. santettha tayo kulaputtā attakāmarūpā viharanti. mā tesaṃ aphāsumakāsī"ti.</p>\r\n<p class="bodytext">assosi kho āyasmā anuruddho dāyapālassa bhagavatā saddhiṃ mantayamānassa. sutvāna dāyapālaṃ etadavoca – "mā, āvuso dāyapāla, bhagavantaṃ vāresi. satthā no bhagavā anuppatto"ti. atha kho āyasmā anuruddho yenāyasmā ca nandiyo āyasmā ca kimilo tenupasaṅkami, upasaṅkamitvā āyasmantañca nandiyaṃ āyasmantañca kimilaṃ etadavoca – "abhikkamathāyasmanto, abhikkamathāyasmanto, satthā no bhagavā <a name="P1.0206"></a> anuppatto"ti. atha <a name="T1.0387"></a> kho āyasmā ca anuruddho āyasmā ca nandiyo āyasmā ca kimilo bhagavantaṃ paccuggantvā – eko bhagavato pattacīvaraṃ paṭiggahesi, eko āsanaṃ paññapesi, eko pādodakaṃ upaṭṭhāpesi. nisīdi bhagavā paññatte āsane. nisajja kho bhagavā pāde pakkhālesi. tepi kho āyasmanto bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu. ekamantaṃ nisinnaṃ kho āyasmantaṃ anuruddhaṃ bhagavā etadavoca –</p>\r\n<p class="bodytext"><a name="para326"></a><a name="para326_mn1"></a><span class="paranum">326</span>. kacci vo, anuruddhā, khamanīyaṃ, kacci yāpanīyaṃ, kacci piṇḍakena na kilamathā"ti<a name="V1.0270"></a>? "khamanīyaṃ, bhagavā, yāpanīyaṃ, bhagavā, na ca mayaṃ, bhante, piṇḍakena kilamāmā"ti. "kacci pana vo, anuruddhā, samaggā sammodamānā avivadamānā khīrodakībhūtā aññamaññaṃ piyacakkhūhi sampassantā viharathā"ti? "taggha</p>\r\n','\r',char(13)),'\n',char(10)),'-325-326-');
INSERT INTO pages VALUES(3306,'mula_ma_01',267,replace(replace('<p class="bodytext">mayaṃ<a name="M1.0267"></a>, bhante, samaggā sammodamānā avivadamānā khīrodakībhūtā aññamaññaṃ piyacakkhūhi sampassantā viharāmā"ti. "yathā kathaṃ pana tumhe, anuruddhā, samaggā sammodamānā avivadamānā khīrodakībhūtā aññamaññaṃ piyacakkhūhi sampassantā viharathā"ti? "idha mayhaṃ, bhante, evaṃ hoti – ‘lābhā vata me, suladdhaṃ vata me, yohaṃ evarūpehi sabrahmacārīhi saddhiṃ viharāmī’ti. tassa mayhaṃ, bhante, imesu āyasmantesu mettaṃ kāyakammaṃ paccupaṭṭhitaṃ āvi ceva raho ca, mettaṃ vacīkammaṃ paccupaṭṭhitaṃ āvi ceva raho ca, mettaṃ manokammaṃ paccupaṭṭhitaṃ āvi ceva raho ca. tassa mayhaṃ, bhante, evaṃ hoti – ‘yaṃnūnāhaṃ sakaṃ cittaṃ nikkhipitvā <a name="T1.0388"></a> imesaṃyeva āyasmantānaṃ cittassa vasena vatteyya’nti. so kho ahaṃ, bhante, sakaṃ cittaṃ nikkhipitvā imesaṃyeva āyasmantānaṃ cittassa vasena vattāmi. nānā hi kho no, bhante, kāyā ekañca pana maññe citta"nti.</p>\r\n<p class="bodytext">āyasmāpi kho nandiyo…pe… āyasmāpi kho kimilo bhagavantaṃ etadavoca – "mayhampi, bhante, evaṃ hoti – ‘lābhā vata me, suladdhaṃ vata me, yohaṃ evarūpehi sabrahmacārīhi saddhiṃ viharāmī’ti. tassa mayhaṃ, bhante, imesu āyasmantesu mettaṃ kāyakammaṃ paccupaṭṭhitaṃ āvi ceva raho ca, mettaṃ vacīkammaṃ paccupaṭṭhitaṃ āvi ceva raho ca, mettaṃ manokammaṃ paccupaṭṭhitaṃ āvi ceva raho ca. tassa mayhaṃ, bhante, evaṃ hoti – ‘yaṃnūnāhaṃ sakaṃ <a name="P1.0207"></a> cittaṃ nikkhipitvā imesaṃyeva āyasmantānaṃ cittassa vasena vatteyya’nti. so kho ahaṃ, bhante, sakaṃ cittaṃ nikkhipitvā imesaṃyeva āyasmantānaṃ cittassa vasena vattāmi. nānā hi kho no, bhante, kāyā ekañca pana maññe cittanti.</p>\r\n<p class="bodytext">evaṃ kho mayaṃ, bhante, samaggā sammodamānā avivadamānā khīrodakībhūtā aññamaññaṃ piyacakkhūhi sampassantā viharāmā"ti.</p>\r\n<p class="bodytext"><a name="para327"></a><a name="para327_mn1"></a><span class="paranum">327</span>. sādhu sādhu, anuruddhā! kacci pana vo, anuruddhā, appamattā ātāpino pahitattā viharathā"ti? "taggha mayaṃ, bhante, appamattā ātāpino pahitattā viharāmā"ti. "yathā <a name="V1.0271"></a> kathaṃ pana tumhe, anuruddhā, appamattā ātāpino pahitattā viharathā"ti? "idha, bhante, amhākaṃ yo paṭhamaṃ gāmato piṇḍāya paṭikkamati so āsanāni <a name="T1.0389"></a> paññapeti, pānīyaṃ paribhojanīyaṃ upaṭṭhāpeti, avakkārapātiṃ upaṭṭhāpeti. yo pacchā</p>\r\n','\r',char(13)),'\n',char(10)),'-327-');
INSERT INTO pages VALUES(3307,'mula_ma_01',268,replace(replace('<p class="bodytext">gāmato <a name="M1.0268"></a> piṇḍāya paṭikkamati, sace hoti bhuttāvaseso sace ākaṅkhati bhuñjati, no ce ākaṅkhati appaharite vā chaḍḍeti, appāṇake vā udake opilāpeti. so āsanāni paṭisāmeti, pānīyaṃ paribhojanīyaṃ paṭisāmeti, avakkārapātiṃ paṭisāmeti, bhattaggaṃ sammajjati. yo passati pānīyaghaṭaṃ vā paribhojanīyaghaṭaṃ vā vaccaghaṭaṃ vā rittaṃ tucchaṃ so upaṭṭhāpeti. sacassa hoti avisayhaṃ, hatthavikārena dutiyaṃ āmantetvā hatthavilaṅghakena upaṭṭhāpema, na tveva mayaṃ, bhante, tappaccayā vācaṃ bhindāma. pañcāhikaṃ kho pana mayaṃ, bhante, sabbarattikaṃ dhammiyā kathāya sannisīdāma. evaṃ kho mayaṃ, bhante, appamattā ātāpino pahitattā viharāmā"ti.</p>\r\n<p class="bodytext"><a name="para328"></a><a name="para328_mn1"></a><span class="paranum">328</span>. sādhu sādhu, anuruddhā! atthi pana vo, anuruddhā, evaṃ appamattānaṃ ātāpīnaṃ pahitattānaṃ viharantānaṃ uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti? "kiñhi no siyā, bhante! idha mayaṃ, bhante, yāvadeva ākaṅkhāma vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharāma. ayaṃ kho no, bhante, amhākaṃ appamattānaṃ ātāpīnaṃ pahitattānaṃ viharantānaṃ uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti.</p>\r\n<p class="bodytext">sādhu sādhu, anuruddhā! etassa <a name="T1.0390"></a> pana vo, anuruddhā, vihārassa samatikkamāya etassa vihārassa <a name="P1.0208"></a> paṭippassaddhiyā atthañño uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti? "kiñhi no siyā, bhante! idha mayaṃ, bhante, yāvadeva ākaṅkhāma vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja viharāma. etassa, bhante, vihārassa samatikkamāya etassa vihārassa paṭippassaddhiyā ayamañño uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti.</p>\r\n<p class="bodytext">sādhu <a name="V1.0272"></a> sādhu, anuruddhā! etassa pana vo, anuruddhā, vihārassa samatikkamāya etassa vihārassa paṭippassaddhiyā atthañño uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti?</p>\r\n','\r',char(13)),'\n',char(10)),'-328-');
INSERT INTO pages VALUES(3308,'mula_ma_01',269,replace(replace('<p class="bodytext">kiñhi <a name="M1.0269"></a> no siyā, bhante! idha mayaṃ, bhante, yāvadeva ākaṅkhāma pītiyā ca virāgā upekkhakā ca viharāma, satā ca sampajānā, sukhañca kāyena paṭisaṃvedema, yaṃ taṃ ariyā ācikkhanti – ‘upekkhako satimā sukhavihārī’ti tatiyaṃ jhānaṃ upasampajja viharāma. etassa, bhante, vihārassa samatikkamāya etassa vihārassa paṭippassaddhiyā ayamañño uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti.</p>\r\n<p class="bodytext">sādhu sādhu, anuruddhā! etassa pana vo, anuruddhā, vihārassa samatikkamāya etassa vihārassa paṭippassaddhiyā atthañño uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti<a name="T1.0391"></a>? "kiñhi no siyā, bhante! idha mayaṃ, bhante, yāvadeva ākaṅkhāma sukhassa ca pahānā dukkhassa ca pahānā, pubbeva somanassadomanassānaṃ atthaṅgamā, adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja viharāma. etassa, bhante, vihārassa samatikkamāya etassa vihārassa paṭippassaddhiyā ayamañño uttari manussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti.</p>\r\n<p class="bodytext">sādhu sādhu, anuruddhā! etassa pana vo, anuruddhā, vihārassa samatikkamāya etassa vihārassa paṭippassaddhiyā atthañño uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti? "kiñhi no siyā, bhante! idha mayaṃ, bhante, yāvadeva ākaṅkhāma sabbaso rūpasaññānaṃ samatikkamā paṭighasaññānaṃ atthaṅgamā nānattasaññānaṃ amanasikārā ‘ananto ākāso’ti ākāsānañcāyatanaṃ <a name="P1.0209"></a> upasampajja viharāma. etassa, bhante, vihārassa samatikkamāya etassa vihārassa paṭippassaddhiyā ayamañño uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti.</p>\r\n<p class="bodytext">sādhu sādhu, anuruddhā! etassa pana vo, anuruddhā, vihārassa samatikkamāya etassa vihārassa paṭippassaddhiyā atthañño uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti? "kiñhi no siyā, bhante! idha mayaṃ, bhante, yāvadeva ākaṅkhāma <a name="V1.0273"></a> sabbaso ākāsānañcāyatanaṃ samatikkamma ‘anantaṃ viññāṇa’nti viññāṇañcāyatanaṃ upasampajja viharāma…pe… sabbaso viññāṇañcāyatanaṃ <a name="T1.0392"></a> samatikkamma ‘natthi kiñcī’ti ākiñcaññāyatanaṃ upasampajja viharāma…pe… sabbaso ākiñcaññāyatanaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3309,'mula_ma_01',270,replace(replace('<p class="bodytext">samatikkamma <a name="M1.0270"></a> nevasaññānāsaññāyatanaṃ upasampajja viharāma. etassa, bhante, vihārassa samatikkamāya etassa vihārassa paṭippassaddhiyā ayamañño uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti.</p>\r\n<p class="bodytext"><a name="para329"></a><a name="para329_mn1"></a><span class="paranum">329</span>. sādhu sādhu, anuruddhā! etassa pana vo, anuruddhā, vihārassa samatikkamāya etassa vihārassa paṭippassaddhiyā atthañño uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro"ti? "kiñhi no siyā, bhante! idha mayaṃ, bhante, yāvadeva ākaṅkhāma sabbaso nevasaññānāsaññāyatanaṃ samatikkamma saññāvedayitanirodhaṃ upasampajja viharāma, paññāya ca no disvā āsavā parikkhīṇā. etassa, bhante, vihārassa samatikkamāya etassa vihārassa paṭippassaddhiyā ayamañño uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāro. imamhā ca mayaṃ, bhante, phāsuvihārā aññaṃ phāsuvihāraṃ uttaritaraṃ vā paṇītataraṃ vā na samanupassāmā"ti. "sādhu sādhu, anuruddhā! imamhā phāsuvihārā uttaritaro vā paṇītataro vā phāsuvihāro natthī"ti.</p>\r\n<p class="bodytext"><a name="para330"></a><a name="para330_mn1"></a><span class="paranum">330</span>. atha kho bhagavā āyasmantañca anuruddhaṃ āyasmantañca nandiyaṃ āyasmantañca kimilaṃ dhammiyā kathāya sandassetvā samādapetvā <a name="T1.0393"></a> samuttejetvā sampahaṃsetvā uṭṭhāyāsanā pakkāmi. atha kho āyasmā ca anuruddho āyasmā ca nandiyo āyasmā ca kimilo bhagavantaṃ anusaṃyāyitvā <span class="note">[anusaṃsāvetvā (sī.), anusāvetvā (ṭīkā)]</span> tato paṭinivattitvā āyasmā ca nandiyo <a name="P1.0210"></a> āyasmā ca kimilo āyasmantaṃ anuruddhaṃ etadavocuṃ – "kiṃ nu kho mayaṃ āyasmato anuruddhassa evamārocimha – ‘imāsañca imāsañca vihārasamāpattīnaṃ mayaṃ lābhino’ti, yaṃ no āyasmā anuruddho bhagavato sammukhā yāva āsavānaṃ khayā pakāsetī"ti? "na kho me āyasmanto evamārocesuṃ – ‘imāsañca imāsañca vihārasamāpattīnaṃ mayaṃ lābhino’ti, api ca me āyasmantānaṃ cetasā ceto paricca vidito – ‘imāsañca imāsañca vihārasamāpattīnaṃ ime āyasmanto lābhino’ti. devatāpi</p>\r\n','\r',char(13)),'\n',char(10)),'-329-330-');
INSERT INTO pages VALUES(3310,'mula_ma_01',271,replace(replace('<p class="bodytext">me <a name="M1.0271"></a> etamatthaṃ ārocesuṃ – ‘imāsañca imāsañca vihārasamāpattīnaṃ ime āyasmanto lābhino’ti. tamenaṃ bhagavatā pañhābhipuṭṭhena byākata"nti.</p>\r\n<p class="bodytext"><a name="para331"></a><a name="para331_mn1"></a><span class="paranum">331</span>. atha <a name="V1.0274"></a> kho dīgho parajano yakkho yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ aṭṭhāsi. ekamantaṃ ṭhito kho dīgho parajano yakkho bhagavantaṃ etadavoca – "lābhā vata, bhante, vajjīnaṃ, suladdhalābhā vajjipajāya, yattha tathāgato viharati arahaṃ sammāsambuddho, ime ca tayo kulaputtā – āyasmā ca anuruddho, āyasmā ca nandiyo, āyasmā ca kimilo"ti. dīghassa parajanassa yakkhassa saddaṃ sutvā bhummā devā saddamanussāvesuṃ – ‘lābhā <a name="T1.0394"></a> vata, bho, vajjīnaṃ, suladdhalābhā vajjipajāya, yattha tathāgato viharati arahaṃ sammāsambuddho, ime ca tayo kulaputtā – āyasmā ca anuruddho, āyasmā ca nandiyo, āyasmā ca kimilo’ti. bhummānaṃ devānaṃ saddaṃ sutvā cātumahārājikā devā…pe… tāvatiṃsā devā…pe… yāmā devā…pe… tusitā devā…pe… nimmānaratī devā…pe… paranimmitavasavattī devā…pe… brahmakāyikā devā saddamanussāvesuṃ – "lābhā vata, bho, vajjīnaṃ, suladdhalābhā vajjipajāya, yattha tathāgato viharati arahaṃ sammāsambuddho, ime ca tayo kulaputtā – āyasmā ca anuruddho, āyasmā ca nandiyo, āyasmā ca kimilo"ti. itiha te āyasmanto tena khaṇena (tena layena) <span class="note">[( ) sī. syā. pī. potthakesu natthi]</span> tena muhuttena yāvabrahmalokā viditā <span class="note">[saṃviditā (ka.)]</span> ahesuṃ.</p>\r\n<p class="bodytext">evametaṃ, dīgha, evametaṃ, dīgha! yasmāpi, dīgha, kulā ete tayo kulaputtā agārasmā anagāriyaṃ pabbajitā, tañcepi kulaṃ ete tayo kulaputte pasannacittaṃ anussareyya, tassapāssa kulassa dīgharattaṃ hitāya sukhāya. yasmāpi, dīgha, kulaparivaṭṭā ete tayo kulaputtā agārasmā anagāriyaṃ <a name="P1.0211"></a> pabbajitā, so cepi kulaparivaṭṭo ete tayo kulaputte pasannacitto anussareyya, tassapāssa kulaparivaṭṭassa dīgharattaṃ hitāya sukhāya. yasmāpi, dīgha, gāmā ete tayo kulaputtā agārasmā anagāriyaṃ pabbajitā, so cepi gāmo ete tayo</p>\r\n','\r',char(13)),'\n',char(10)),'-331-');
INSERT INTO pages VALUES(3311,'mula_ma_01',272,replace(replace('<p class="bodytext">kulaputte <a name="M1.0272"></a> pasannacitto anussareyya, tassapāssa gāmassa <a name="T1.0395"></a> dīgharattaṃ hitāya sukhāya. yasmāpi, dīgha, nigamā ete tayo kulaputtā agārasmā anagāriyaṃ pabbajitā, so cepi nigamo ete tayo kulaputte pasannacitto anussareyya, tassapāssa nigamassa dīgharattaṃ hitāya sukhāya. yasmāpi, dīgha, nagarā ete tayo kulaputtā agārasmā anagāriyaṃ pabbajitā, tañcepi nagaraṃ ete tayo kulaputte pasannacittaṃ anussareyya, tassapāssa nagarassa dīgharattaṃ hitāya sukhāya. yasmāpi, dīgha, janapadā ete tayo kulaputtā agārasmā anagāriyaṃ pabbajitā, so cepi janapado ete tayo kulaputte pasannacitto anussareyya, tassapāssa janapadassa dīgharattaṃ hitāya sukhāya. sabbe cepi, dīgha, khattiyā ete tayo kulaputte pasannacittā <a name="V1.0275"></a> anussareyyuṃ, sabbesānaṃpāssa khattiyānaṃ dīgharattaṃ hitāya sukhāya. sabbe cepi, dīgha, brāhmaṇā…pe… sabbe cepi, dīgha, vessā…pe… sabbe cepi, dīgha, suddā ete tayo kulaputte pasannacittā anussareyyuṃ, sabbesānaṃpāssa suddānaṃ dīgharattaṃ hitāya sukhāya. sadevako cepi, dīgha, loko samārako sabrahmako sassamaṇabrāhmaṇī pajā sadevamanussā ete tayo kulaputte pasannacittā anussareyya, sadevakassapāssa lokassa samārakassa sabrahmakassa sassamaṇabrāhmaṇiyā pajāya sadevamanussāya dīgharattaṃ hitāya sukhāya. passa, dīgha, yāva ete tayo kulaputtā bahujanahitāya paṭipannā bahujanasukhāya lokānukampāya, atthāya hitāya sukhāya devamanussāna"nti.</p>\r\n<p class="bodytext">idamavoca <a name="T1.0396"></a> bhagavā. attamano dīgho parajano yakkho bhagavato bhāsitaṃ abhinandīti.</p>\r\n<p class="centered">cūḷagosiṅgasuttaṃ niṭṭhitaṃ paṭhamaṃ.</p>\r\n<p class="subhead">2. mahāgosiṅgasuttaṃ</p>\r\n<p class="bodytext"><a name="para332"></a><a name="para332_mn1"></a><span class="paranum">332</span>. evaṃ <a name="T1.0397"></a><a name="V1.0276"></a><a name="P1.0212"></a> me sutaṃ – ekaṃ samayaṃ bhagavā gosiṅgasālavanadāye viharati sambahulehi abhiññātehi abhiññātehi therehi sāvakehi saddhiṃ – āyasmatā ca sāriputtena āyasmatā ca mahāmoggallānena</p>\r\n','\r',char(13)),'\n',char(10)),'-332-');
INSERT INTO pages VALUES(3312,'mula_ma_01',273,replace(replace('<p class="bodytext">āyasmatā <a name="M1.0273"></a> ca mahākassapena āyasmatā ca anuruddhena āyasmatā ca revatena āyasmatā ca ānandena, aññehi ca abhiññātehi abhiññātehi therehi sāvakehi saddhiṃ. atha kho āyasmā mahāmoggallāno sāyanhasamayaṃ paṭisallānā vuṭṭhito yenāyasmā mahākassapo tenupasaṅkami, upasaṅkamitvā āyasmantaṃ mahākassapaṃ etadavoca – "āyāmāvuso, kassapa, yenāyasmā sāriputto tenupasaṅkamissāma dhammassavanāyā"ti. "evamāvuso"ti kho āyasmā mahākassapo āyasmato mahāmoggallānassa paccassosi. atha kho āyasmā ca mahāmoggallāno āyasmā ca mahākassapo āyasmā ca anuruddho yenāyasmā sāriputto tenupasaṅkamiṃsu dhammassavanāya. addasā kho āyasmā ānando āyasmantañca mahāmoggallānaṃ āyasmantañca mahākassapaṃ āyasmantañca anuruddhaṃ yenāyasmā sāriputto tenupasaṅkamante dhammassavanāya. disvāna yenāyasmā revato tenupasaṅkami, upasaṅkamitvā āyasmantaṃ revataṃ etadavoca – "upasaṅkamantā kho amū, āvuso <span class="note">[āyasmantāvuso (ka.)]</span> revata, sappurisā yenāyasmā sāriputto <a name="T1.0398"></a> tena dhammassavanāya. āyāmāvuso revata, yenāyasmā sāriputto tenupasaṅkamissāma dhammassavanāyā"ti. "evamāvuso"ti kho āyasmā revato āyasmato ānandassa paccassosi. atha kho āyasmā ca revato āyasmā ca ānando yenāyasmā sāriputto tenupasaṅkamiṃsu dhammassavanāya.</p>\r\n<p class="bodytext"><a name="para333"></a><a name="para333_mn1"></a><span class="paranum">333</span>. addasā kho āyasmā sāriputto āyasmantañca revataṃ āyasmantañca ānandaṃ dūratova āgacchante. disvāna āyasmantaṃ ānandaṃ etadavoca – "etu kho āyasmā ānando! svāgataṃ āyasmato ānandassa bhagavato upaṭṭhākassa bhagavato santikāvacarassa! ramaṇīyaṃ, āvuso ānanda, gosiṅgasālavanaṃ, dosinā ratti, sabbaphāliphullā <span class="note">[sabbapāliphullā (sī.)]</span> sālā, dibbā, maññe, gandhā sampavanti, kathaṃrūpena, āvuso ānanda, bhikkhunā gosiṅgasālavanaṃ sobheyyā"ti? "idhāvuso sāriputta<a name="P1.0213"></a>, bhikkhu bahussuto hoti sutadharo sutasannicayo. ye te dhammā ādikalyāṇā majjhekalyāṇā</p>\r\n','\r',char(13)),'\n',char(10)),'-333-');
INSERT INTO pages VALUES(3313,'mula_ma_01',274,replace(replace('<p class="bodytext">pariyosānakalyāṇā <a name="M1.0274"></a> sātthā sabyañjanā, kevalaparipuṇṇaṃ <a name="V1.0277"></a> parisuddhaṃ brahmacariyaṃ abhivadanti, tathārūpāssa dhammā bahussutā honti, dhātā <span class="note">[dhatā (sī. syā. kaṃ. pī.)]</span>, vacasā paricitā, manasānupekkhitā, diṭṭhiyā suppaṭividdhā. so catassannaṃ parisānaṃ dhammaṃ deseti parimaṇḍalehi padabyañjanehi anuppabandhehi <span class="note">[appabaddhehi (sī. pī.)]</span> anusayasamugghātāya. evarūpena kho, āvuso sāriputta, bhikkhunā gosiṅgasālavanaṃ sobheyyā"ti.</p>\r\n<p class="bodytext"><a name="para334"></a><a name="para334_mn1"></a><span class="paranum">334</span>. evaṃ vutte, āyasmā sāriputto āyasmantaṃ revataṃ etadavoca <a name="T1.0399"></a> – "byākataṃ kho, āvuso revata, āyasmatā ānandena yathāsakaṃ paṭibhānaṃ. tattha dāni mayaṃ āyasmantaṃ revataṃ pucchāma – ‘ramaṇīyaṃ, āvuso revata, gosiṅgasālavanaṃ, dosinā ratti, sabbaphāliphullā sālā, dibbā, maññe, gandhā sampavanti, kathaṃrūpena, āvuso revata, bhikkhunā gosiṅgasālavanaṃ sobheyyā"’ti? "idhāvuso sāriputta, bhikkhu paṭisallānārāmo hoti paṭisallānarato, ajjhattaṃ cetosamathamanuyutto anirākatajjhāno, vipassanāya samannāgato, brūhetā suññāgārānaṃ. evarūpena kho, āvuso sāriputta, bhikkhunā gosiṅgasālavanaṃ sobheyyā"ti.</p>\r\n<p class="bodytext"><a name="para335"></a><a name="para335_mn1"></a><span class="paranum">335</span>. evaṃ vutte, āyasmā sāriputto āyasmantaṃ anuruddhaṃ etadavoca – "byākataṃ kho, āvuso anuruddha, āyasmatā revatena yathāsakaṃ paṭibhānaṃ. tattha dāni mayaṃ āyasmantaṃ anuruddhaṃ pucchāma – ‘ramaṇīyaṃ, āvuso anuruddha, gosiṅgasālavanaṃ, dosinā ratti, sabbaphāliphullā sālā, dibbā, maññe, gandhā sampavanti, kathaṃrūpena, āvuso anuruddha, bhikkhunā gosiṅgasālavanaṃ sobheyyā"’ti? "idhāvuso sāriputta, bhikkhu dibbena cakkhunā visuddhena atikkantamānusakena sahassaṃ lokānaṃ voloketi. seyyathāpi, āvuso sāriputta, cakkhumā puriso uparipāsādavaragato sahassaṃ nemimaṇḍalānaṃ volokeyya, evameva kho, āvuso sāriputta, bhikkhu dibbena cakkhunā visuddhena atikkantamānusakena sahassaṃ lokānaṃ voloketi. evarūpena kho, āvuso sāriputta, bhikkhunā gosiṅgasālavanaṃ sobheyyā"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-334-335-');
INSERT INTO pages VALUES(3314,'mula_ma_01',275,replace(replace('<p class="bodytext"><a name="para336"></a><a name="para336_mn1"></a><span class="paranum">336</span>. evaṃ <a name="T1.0400"></a><a name="M1.0275"></a> vutte, āyasmā sāriputto āyasmantaṃ mahākassapaṃ etadavoca – "byākataṃ kho, āvuso kassapa, āyasmatā anuruddhena yathāsakaṃ paṭibhānaṃ. tattha dāni mayaṃ āyasmantaṃ mahākassapaṃ pucchāma – ‘ramaṇīyaṃ, āvuso kassapa, gosiṅgasālavanaṃ, dosinā ratti, sabbaphāliphullā sālā, dibbā, maññe, gandhā sampavanti, kathaṃrūpena, āvuso kassapa, bhikkhunā <a name="P1.0214"></a> gosiṅgasālavanaṃ sobheyyā"’ti? "idhāvuso sāriputta, bhikkhu attanā ca āraññiko <a name="V1.0278"></a> hoti āraññikattassa ca vaṇṇavādī, attanā ca piṇḍapātiko hoti piṇḍapātikattassa ca vaṇṇavādī, attanā ca paṃsukūliko hoti paṃsukūlikattassa ca vaṇṇavādī, attanā ca tecīvariko hoti tecīvarikattassa ca vaṇṇavādī, attanā ca appiccho hoti appicchatāya ca vaṇṇavādī, attanā ca santuṭṭho hoti santuṭṭhiyā ca vaṇṇavādī, attanā ca pavivitto hoti pavivekassa ca vaṇṇavādī, attanā ca asaṃsaṭṭho hoti asaṃsaggassa ca vaṇṇavādī, attanā ca āraddhavīriyo hoti vīriyārambhassa ca vaṇṇavādī, attanā ca sīlasampanno hoti sīlasampadāya ca vaṇṇavādī, attanā ca samādhisampanno hoti samādhisampadāya ca vaṇṇavādī, attanā ca paññāsampanno hoti paññāsampadāya ca vaṇṇavādī, attanā ca vimuttisampanno hoti vimuttisampadāya ca vaṇṇavādī, attanā ca vimuttiñāṇadassanasampanno hoti vimuttiñāṇadassanasampadāya ca vaṇṇavādī. evarūpena kho, āvuso sāriputta<a name="T1.0401"></a>, bhikkhunā gosiṅgasālavanaṃ sobheyyā"ti.</p>\r\n<p class="bodytext"><a name="para337"></a><a name="para337_mn1"></a><span class="paranum">337</span>. evaṃ vutte, āyasmā sāriputto āyasmantaṃ mahāmoggallānaṃ etadavoca – "byākataṃ kho, āvuso moggallāna, āyasmatā mahākassapena yathāsakaṃ paṭibhānaṃ. tattha dāni mayaṃ āyasmantaṃ mahāmoggallānaṃ pucchāma – ‘ramaṇīyaṃ, āvuso moggallāna, gosiṅgasālavanaṃ, dosinā ratti, sabbaphāliphullā sālā, dibbā, maññe, gandhā sampavanti, kathaṃrūpena, āvuso moggallāna, bhikkhunā gosiṅgasālavanaṃ sobheyyā"’ti? "idhāvuso sāriputta, dve bhikkhū abhidhammakathaṃ kathenti, te aññamaññaṃ pañhaṃ pucchanti, aññamaññassa pañhaṃ puṭṭhā vissajjenti, no ca saṃsādenti <span class="note">[saṃsārenti (ka.)]</span>, dhammī ca nesaṃ kathā pavattinī hoti. evarūpena kho, āvuso sāriputta, bhikkhunā gosiṅgasālavanaṃ sobheyyā"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-336-337-');
INSERT INTO pages VALUES(3315,'mula_ma_01',276,replace(replace('<p class="bodytext"><a name="para338"></a><a name="para338_mn1"></a><span class="paranum">338</span>. atha <a name="M1.0276"></a> kho āyasmā mahāmoggallāno āyasmantaṃ sāriputtaṃ etadavoca – "byākataṃ kho, āvuso sāriputta, amhehi sabbeheva yathāsakaṃ paṭibhānaṃ. tattha dāni mayaṃ āyasmantaṃ sāriputtaṃ pucchāma – ‘ramaṇīyaṃ, āvuso sāriputta, gosiṅgasālavanaṃ, dosinā ratti, sabbaphāliphullā sālā, dibbā, maññe, gandhā sampavanti, kathaṃrūpena, āvuso sāriputta, bhikkhunā gosiṅgasālavanaṃ sobheyyā"’ti? "idhāvuso moggallāna, bhikkhu cittaṃ vasaṃ vatteti, no ca bhikkhu cittassa vasena vattati. so yāya vihārasamāpattiyā ākaṅkhati <a name="P1.0215"></a> pubbaṇhasamayaṃ viharituṃ, tāya vihārasamāpattiyā pubbaṇhasamayaṃ viharati, yāya vihārasamāpattiyā <a name="T1.0402"></a> ākaṅkhati majjhanhikasamayaṃ <span class="note">[majjhantikasamayaṃ (sī. syā. kaṃ. pī. ka.)]</span> viharituṃ, tāya vihārasamāpattiyā majjhanhikasamayaṃ <a name="V1.0279"></a> viharati, yāya vihārasamāpattiyā ākaṅkhati sāyanhasamayaṃ viharituṃ, tāya vihārasamāpattiyā sāyanhasamayaṃ viharati. seyyathāpi, āvuso moggallāna, rañño vā rājamahāmattassa vā nānārattānaṃ dussānaṃ dussakaraṇḍako pūro assa. so yaññadeva dussayugaṃ ākaṅkheyya pubbaṇhasamayaṃ pārupituṃ, taṃ tadeva dussayugaṃ pubbaṇhasamayaṃ pārupeyya, yaññadeva dussayugaṃ ākaṅkheyya majjhanhikasamayaṃ pārupituṃ, taṃ tadeva dussayugaṃ majjhanhikasamayaṃ pārupeyya, yaññadeva dussayugaṃ ākaṅkheyya sāyanhasamayaṃ pārupituṃ, taṃ tadeva dussayugaṃ sāyanhasamayaṃ pārupeyya. evameva kho, āvuso moggallāna, bhikkhu cittaṃ vasaṃ vatteti, no ca bhikkhu cittassa vasena vattati. so yāya vihārasamāpattiyā ākaṅkhati pubbaṇhasamayaṃ viharituṃ, tāya vihārasamāpattiyā pubbaṇhasamayaṃ viharati, yāya vihārasamāpattiyā ākaṅkhati majjhanhikasamayaṃ viharituṃ, tāya vihārasamāpattiyā majjhanhikasamayaṃ viharati, yāya vihārasamāpattiyā ākaṅkhati sāyanhasamayaṃ viharituṃ, tāya vihārasamāpattiyā sāyanhasamayaṃ viharati. evarūpena kho, āvuso moggallāna, bhikkhunā gosiṅgasālavanaṃ sobheyyā"ti.</p>\r\n<p class="bodytext"><a name="para339"></a><a name="para339_mn1"></a><span class="paranum">339</span>. atha kho āyasmā sāriputto te āyasmante etadavoca – "byākataṃ kho, āvuso, amhehi sabbeheva yathāsakaṃ paṭibhānaṃ. āyāmāvuso, yena bhagavā tenupasaṅkamissāma, upasaṅkamitvā</p>\r\n','\r',char(13)),'\n',char(10)),'-338-339-');
INSERT INTO pages VALUES(3316,'mula_ma_01',277,replace(replace('<p class="bodytext">etamatthaṃ <a name="M1.0277"></a> bhagavato <a name="T1.0403"></a> ārocessāma. yathā no bhagavā byākarissati tathā naṃ dhāressāmā"ti. "evamāvuso"ti kho te āyasmanto āyasmato sāriputtassa paccassosuṃ. atha kho te āyasmanto yena bhagavā tenupasaṅkamiṃsu, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu. ekamantaṃ nisinno kho āyasmā sāriputto bhagavantaṃ etadavoca – "addasaṃ kho ahaṃ, bhante, āyasmantañca revataṃ āyasmantañca ānandaṃ dūratova āgacchante. disvāna āyasmantaṃ <a name="P1.0216"></a> ānandaṃ etadavocaṃ – ‘etu kho āyasmā ānando! svāgataṃ āyasmato ānandassa bhagavato upaṭṭhākassa bhagavato santikāvacarassa! ramaṇīyaṃ, āvuso ānanda, gosiṅgasālavanaṃ, dosinā ratti, sabbaphāliphullā sālā, dibbā, maññe, gandhā sampavanti, kathaṃrūpena, āvuso ānanda, bhikkhunā gosiṅgasālavanaṃ sobheyyā’ti? evaṃ vutte, bhante, āyasmā ānando maṃ etadavoca – ‘idhāvuso, sāriputta, bhikkhu bahussuto hoti sutadharo…pe… anusayasamugghātāya. evarūpena kho, āvuso sāriputta, bhikkhunā gosiṅgasālavanaṃ <a name="V1.0280"></a> sobheyyā"’ti. "sādhu sādhu, sāriputta! yathā taṃ ānandova sammā byākaramāno byākareyya. ānando hi, sāriputta, bahussuto sutadharo sutasannicayo. ye te dhammā ādikalyāṇā majjhekalyāṇā pariyosānakalyāṇā sātthā sabyañjanā, kevalaparipuṇṇaṃ parisuddhaṃ <a name="T1.0404"></a> brahmacariyaṃ abhivadanti, tathārūpāssa dhammā bahussutā honti, dhātā, vacasā paricitā, manasānupekkhitā, diṭṭhiyā suppaṭividdhā. so catassannaṃ parisānaṃ dhammaṃ deseti parimaṇḍalehi padabyañjanehi anuppabandhehi anusayasamugghātāyā"ti.</p>\r\n<p class="bodytext"><a name="para340"></a><a name="para340_mn1"></a><span class="paranum">340</span>. evaṃ vutte, ahaṃ, bhante, āyasmantaṃ revataṃ etadavocaṃ – ‘byākataṃ kho, āvuso revata āyasmatā ānandena yathāsakaṃ paṭibhānaṃ. tattha dāni mayaṃ āyasmantaṃ revataṃ pucchāma – ramaṇīyaṃ, āvuso revata, gosiṅgasālavanaṃ, dosinā ratti, sabbaphāliphullā sālā, dibbā maññe gandhā sampavanti. kathaṃrūpena, āvuso revata, bhikkhunā gosiṅgasālavanaṃ sobheyyā’ti? evaṃ vutte, bhante, āyasmā revato maṃ etadavoca – ‘idhāvuso sāriputta bhikkhu paṭisallānārāmo hoti</p>\r\n','\r',char(13)),'\n',char(10)),'-340-');
INSERT INTO pages VALUES(3317,'mula_ma_01',278,replace(replace('<p class="bodytext">paṭisallānarato<a name="M1.0278"></a>, ajjhattaṃ cetosamathamanuyutto, anirākatajjhāno, vipassanāya samannāgato, brūhetā suññāgārānaṃ. evarūpena kho, āvuso sāriputta, bhikkhunā gosiṅgasālavanaṃ sobheyyā"’ti. "sādhu sādhu, sāriputta! yathā taṃ revatova sammā byākaramāno byākareyya. revato hi, sāriputta, paṭisallānārāmo paṭisallānarato, ajjhattaṃ cetosamathamanuyutto anirākatajjhāno, vipassanāya samannāgato brūhetā suññāgārāna"nti.</p>\r\n<p class="bodytext"><a name="para341"></a><a name="para341_mn1"></a><span class="paranum">341</span>. evaṃ <a name="P1.0217"></a> vutte, ahaṃ, bhante, āyasmantaṃ anuruddhaṃ etadavocaṃ – ‘byākataṃ kho āvuso anuruddha āyasmatā revatena…pe… kathaṃrūpena, āvuso <a name="T1.0405"></a> anuruddha, bhikkhunā gosiṅgasālavanaṃ sobheyyā’ti. evaṃ vutte, bhante, āyasmā anuruddho maṃ etadavoca – ‘idhāvuso sāriputta, bhikkhu dibbena cakkhunā visuddhena atikkantamānusakena sahassaṃ lokānaṃ voloketi. seyyathāpi, āvuso sāriputta, cakkhumā puriso…pe… evarūpena kho āvuso sāriputta bhikkhunā gosiṅgasālavanaṃ sobheyyā"’ti. "sādhu sādhu, sāriputta, yathā taṃ anuruddhova sammā byākaramāno byākareyya. anuruddho hi, sāriputta, dibbena cakkhunā visuddhena atikkantamānusakena sahassaṃ lokānaṃ voloketī"ti.</p>\r\n<p class="bodytext"><a name="para342"></a><a name="para342_mn1"></a><span class="paranum">342</span>. evaṃ vutte, ahaṃ, bhante, āyasmantaṃ mahākassapaṃ etadavocaṃ – ‘byākataṃ kho, āvuso <a name="V1.0281"></a> kassapa āyasmatā anuruddhena yathāsakaṃ paṭibhānaṃ. tattha dāni mayaṃ āyasmantaṃ mahākassapaṃ pucchāma…pe… kathaṃ rūpena kho, āvuso kassapa, bhikkhunā gosiṅgasālavanaṃ sobheyyā’ti? evaṃ vutte bhante, āyasmā mahākassapo maṃ etadavoca – ‘idhāvuso sāriputta, bhikkhu attanā ca āraññiko hoti āraññikattassa ca vaṇṇavādī, attanā ca piṇḍapātiko hoti…pe… attanā ca paṃsukūliko hoti…pe… attanā ca tecīvariko hoti…pe… attanā ca appiccho hoti…pe… attanā ca santuṭṭho hoti…pe… attanā ca pavivitto hoti…pe… attanā ca asaṃsaṭṭho hoti…pe… attanā ca āraddhavīriyo hoti…pe… attanā ca sīlasampanno hoti…pe… attanā ca samādhisampanno hoti…pe… attanā ca paññāsampanno hoti… attanā ca vimuttisampanno <a name="T1.0406"></a></p>\r\n','\r',char(13)),'\n',char(10)),'-341-342-');
INSERT INTO pages VALUES(3318,'mula_ma_01',279,replace(replace('<p class="bodytext">hoti… <a name="M1.0279"></a> attanā ca vimuttiñāṇadassanasampanno hoti vimuttiñāṇadassanasampadāya ca vaṇṇavādī. evarūpena kho, āvuso sāriputta, bhikkhunā gosiṅgasālavanaṃ sobheyyā"’ti<a name="P1.0218"></a>. "sādhu sādhu, sāriputta! yathā taṃ kassapova sammā byākaramāno byākareyya. kassapo hi, sāriputta, attanā ca āraññiko āraññikattassa ca vaṇṇavādī, attanā ca piṇḍapātiko piṇḍapātikattassa ca vaṇṇavādī, attanā ca paṃsukūliko paṃsukūlikattassa ca vaṇṇavādī, attanā ca tecīvariko tecīvarikattassa ca vaṇṇavādī, attanā ca appiccho appicchatāya ca vaṇṇavādī, attanā ca santuṭṭho santuṭṭhiyā ca vaṇṇavādī, attanā ca pavivitto pavivekassa ca vaṇṇavādī, attanā ca asaṃsaṭṭho asaṃsaggassa ca vaṇṇavādī, attanā ca āraddhavīriyo vīriyārambhassa ca vaṇṇavādī, attanā ca sīlasampanno sīlasampadāya ca vaṇṇavādī, attanā ca samādhisampanno samādhisampadāya ca vaṇṇavādī, attanā ca paññāsampanno paññāsampadāya ca vaṇṇavādī, attanā ca vimuttisampanno vimuttisampadāya ca vaṇṇavādī, attanā ca vimuttiñāṇadassanasampanno vimuttiñāṇadassanasampadāya ca vaṇṇavādī"ti.</p>\r\n<p class="bodytext"><a name="para343"></a><a name="para343_mn1"></a><span class="paranum">343</span>. evaṃ vutte, ahaṃ bhante āyasmantaṃ mahāmoggallānaṃ etadavocaṃ – ‘byākataṃ kho, āvuso moggallāna, āyasmatā mahākassapena <a name="T1.0407"></a> yathāsakaṃ paṭibhānaṃ. tattha dāni mayaṃ āyasmantaṃ mahāmoggallānaṃ pucchāma…pe… kathaṃrūpena, āvuso moggallāna, bhikkhunā gosiṅgasālavanaṃ sobheyyā’ti? evaṃ vutte, bhante, āyasmā mahāmoggallāno maṃ etadavoca – ‘idhāvuso sāriputta, dve bhikkhū abhidhammakathaṃ kathenti. te aññamaññaṃ pañhaṃ pucchanti, aññamaññassa <a name="V1.0282"></a> pañhaṃ puṭṭhā vissajjenti, no ca saṃsādenti, dhammī ca nesaṃ kathā pavattinī hoti. evarūpena kho, āvuso sāriputta, bhikkhunā gosiṅgasālavanaṃ sobheyyā"’ti. "sādhu sādhu, sāriputta, yathā taṃ moggallānova sammā byākaramāno byākareyya. moggallāno hi, sāriputta, dhammakathiko"ti.</p>\r\n<p class="bodytext"><a name="para344"></a><a name="para344_mn1"></a><span class="paranum">344</span>. evaṃ vutte, āyasmā mahāmoggallāno bhagavantaṃ etadavoca – "atha khvāhaṃ, bhante, āyasmantaṃ sāriputtaṃ etadavocaṃ – ‘byākataṃ kho, āvuso sāriputta, amhehi sabbeheva yathāsakaṃ paṭibhānaṃ. tattha</p>\r\n','\r',char(13)),'\n',char(10)),'-343-344-');
INSERT INTO pages VALUES(3319,'mula_ma_01',280,replace(replace('<p class="bodytext">dāni <a name="M1.0280"></a> mayaṃ āyasmantaṃ sāriputtaṃ pucchāma – ramaṇīyaṃ, āvuso sāriputta, gosiṅgasālavanaṃ, dosinā ratti, sabbaphāliphullā sālā, dibbā, maññe, gandhā sampavanti. kathaṃrūpena, āvuso sāriputta, bhikkhunā gosiṅgasālavanaṃ sobheyyā’ti? evaṃ vutte, bhante, āyasmā sāriputto maṃ etadavoca – ‘idhāvuso, moggallāna, bhikkhu cittaṃ vasaṃ vatteti no ca bhikkhu cittassa vasena vattati. so yāya vihārasamāpattiyā ākaṅkhati pubbaṇhasamayaṃ viharituṃ, tāya vihārasamāpattiyā pubbaṇhasamayaṃ <a name="T1.0408"></a> viharati, yāya vihārasamāpattiyā ākaṅkhati majjhanhikasamayaṃ viharituṃ, tāya vihārasamāpattiyā majjhanhikasamayaṃ viharati, yāya vihārasamāpattiyā ākaṅkhati sāyanhasamayaṃ viharituṃ, tāya vihārasamāpattiyā sāyanhasamayaṃ viharati. seyyathāpi, āvuso moggallāna, rañño vā rājamahāmattassa vā nānārattānaṃ dussānaṃ dussakaraṇḍako pūro assa. so yaññadeva dussayugaṃ ākaṅkheyya pubbaṇhasamayaṃ pārupituṃ<a name="P1.0219"></a>, taṃ tadeva dussayugaṃ pubbaṇhasamayaṃ pārupeyya, yaññadeva dussayugaṃ ākaṅkheyya majjhanhikasamayaṃ pārupituṃ, taṃ tadeva dussayugaṃ majjhanhikasamayaṃ pārupeyya, yaññadeva dussayugaṃ ākaṅkheyya sāyanhasamayaṃ pārupituṃ, taṃ tadeva dussayugaṃ sāyanhasamayaṃ pārupeyya. evameva kho, āvuso moggallāna, bhikkhu cittaṃ vasaṃ vatteti, no ca bhikkhu cittassa vasena vattati. so yāya vihārasamāpattiyā ākaṅkhati pubbaṇhasamayaṃ viharituṃ, tāya vihārasamāpattiyā pubbaṇhasamayaṃ viharati, yāya vihārasamāpattiyā ākaṅkhati majjhanhikasamayaṃ viharituṃ, tāya vihārasamāpattiyā majjhanhikasamayaṃ viharati, yāya vihārasamāpattiyā ākaṅkhati sāyanhasamayaṃ viharituṃ, tāya vihārasamāpattiyā sāyanhasamayaṃ viharati. evarūpena kho, āvuso moggallāna, bhikkhunā gosiṅgasālavanaṃ sobheyyā"’ti. "sādhu sādhu, moggallāna! yathā taṃ sāriputtova sammā byākaramāno byākareyya. sāriputto hi, moggallāna, cittaṃ vasaṃ vatteti no ca sāriputto cittassa vasena vattati. so yāya vihārasamāpattiyā <a name="T1.0409"></a><a name="V1.0283"></a> ākaṅkhati pubbaṇhasamayaṃ viharituṃ, tāya vihārasamāpattiyā pubbaṇhasamayaṃ viharati, yāya vihārasamāpattiyā ākaṅkhati majjhanhikasamayaṃ viharituṃ, tāya vihārasamāpattiyā majjhanhikasamayaṃ viharati, yāya vihārasamāpattiyā ākaṅkhati sāyanhasamayaṃ viharituṃ, tāya vihārasamāpattiyā sāyanhasamayaṃ viharatī"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3320,'mula_ma_01',281,replace(replace('<p class="bodytext"><a name="para345"></a><a name="para345_mn1"></a><span class="paranum">345</span>. evaṃ <a name="M1.0281"></a> vutte, āyasmā sāriputto bhagavantaṃ etadavoca – "kassa nu kho, bhante, subhāsita"nti? "sabbesaṃ vo, sāriputta, subhāsitaṃ pariyāyena. api ca mamapi suṇātha yathārūpena bhikkhunā gosiṅgasālavanaṃ sobheyya. idha, sāriputta, bhikkhu pacchābhattaṃ piṇḍapātapaṭikkanto nisīdati pallaṅkaṃ ābhujitvā ujuṃ kāyaṃ paṇidhāya parimukhaṃ satiṃ upaṭṭhapetvā – ‘na tāvāhaṃ imaṃ pallaṅkaṃ bhindissāmi yāva me nānupādāya āsavehi cittaṃ vimuccissatī’ti. evarūpena kho, sāriputta, bhikkhunā gosiṅgasālavanaṃ sobheyyā"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te āyasmanto <span class="note">[te bhikkhū (ka.)]</span> bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">mahāgosiṅgasuttaṃ niṭṭhitaṃ dutiyaṃ.</p>\r\n<p class="subhead">3. mahāgopālakasuttaṃ</p>\r\n<p class="bodytext"><a name="para346"></a><a name="para346_mn1"></a><span class="paranum">346</span>. evaṃ <a name="T1.0410"></a><a name="V1.0284"></a><a name="P1.0220"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext">ekādasahi, bhikkhave, aṅgehi samannāgato gopālako abhabbo gogaṇaṃ pariharituṃ phātiṃ kātuṃ <span class="note">[phātikattuṃ (sī. pī.), phātikātuṃ (syā. kaṃ.)]</span>. katamehi ekādasahi? idha, bhikkhave, gopālako na rūpaññū hoti, na lakkhaṇakusalo hoti, na āsāṭikaṃ hāretā <span class="note">[sāṭetā (sī. syā. kaṃ. pī.)]</span> hoti, na vaṇaṃ paṭicchādetā hoti, na dhūmaṃ kattā hoti, na titthaṃ jānāti, na pītaṃ jānāti, na vīthiṃ jānāti, na gocarakusalo hoti anavasesadohī ca hoti. ye te usabhā gopitaro gopariṇāyakā te na atirekapūjāya pūjetā hoti. imehi kho, bhikkhave, ekādasahi aṅgehi samannāgato gopālako abhabbo gogaṇaṃ pariharituṃ phātiṃ kātuṃ. evameva kho, bhikkhave, ekādasahi dhammehi samannāgato bhikkhu abhabbo imasmiṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-345-346-');
INSERT INTO pages VALUES(3321,'mula_ma_01',282,replace(replace('<p class="bodytext">dhammavinaye <a name="M1.0282"></a> vuddhiṃ virūḷhiṃ vepullaṃ āpajjituṃ. katamehi ekādasahi? idha, bhikkhave, bhikkhu na rūpaññū hoti, na lakkhaṇakusalo hoti, na āsāṭikaṃ hāretā hoti, na vaṇaṃ paṭicchādetā hoti, na dhūmaṃ kattā hoti, na titthaṃ jānāti, na pītaṃ jānāti, na vīthiṃ jānāti, na gocarakusalo hoti, anavasesadohī ca hoti. ye te <a name="T1.0411"></a> bhikkhū therā rattaññū cirapabbajitā saṅghapitaro saṅghapariṇāyakā te na atirekapūjāya pūjetā hoti.</p>\r\n<p class="bodytext"><a name="para347"></a><a name="para347_mn1"></a><span class="paranum">347</span>. kathañca, bhikkhave, bhikkhu na rūpaññū hoti? idha, bhikkhave, bhikkhu yaṃ kiñci rūpaṃ sabbaṃ rūpaṃ ‘cattāri mahābhūtāni, catunnañca mahābhūtānaṃ upādāyarūpa’nti yathābhūtaṃ nappajānāti. evaṃ kho, bhikkhave, bhikkhu na rūpaññū hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu na lakkhaṇakusalo hoti? idha, bhikkhave, bhikkhu ‘kammalakkhaṇo <a name="V1.0285"></a> bālo, kammalakkhaṇo paṇḍito’ti yathābhūtaṃ nappajānāti. evaṃ kho, bhikkhave, bhikkhu na lakkhaṇakusalo hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu na āsāṭikaṃ hāretā hoti? idha, bhikkhave, bhikkhu uppannaṃ kāmavitakkaṃ adhivāseti, nappajahati na vinodeti na byantī karoti na anabhāvaṃ gameti. uppannaṃ byāpādavitakkaṃ…pe… uppannaṃ vihiṃsāvitakkaṃ…pe… uppannuppanne pāpake akusale dhamme adhivāseti, nappajahati <a name="P1.0221"></a> na vinodeti na byantī karoti na anabhāvaṃ gameti. evaṃ kho, bhikkhave, bhikkhu na āsāṭikaṃ hāretā hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu na vaṇaṃ paṭicchādetā hoti? idha, bhikkhave, bhikkhu cakkhunā rūpaṃ disvā nimittaggāhī hoti anubyañjanaggāhī. yatvādhikaraṇamenaṃ cakkhundriyaṃ asaṃvutaṃ viharantaṃ abhijjhādomanassā pāpakā akusalā dhammā anvāssaveyyuṃ, tassa saṃvarāya na paṭipajjati, na rakkhati <a name="T1.0412"></a> cakkhundriyaṃ, cakkhundriye na saṃvaraṃ āpajjati. sotena saddaṃ sutvā…pe… ghānena gandhaṃ ghāyitvā…pe… jivhāya rasaṃ sāyitvā…pe… kāyena phoṭṭhabbaṃ phusitvā…pe… manasā dhammaṃ viññāya nimittaggāhī hoti anubyañjanaggāhī. yatvādhikaraṇamenaṃ manindriyaṃ asaṃvutaṃ viharantaṃ abhijjhādomanassā pāpakā akusalā dhammā anvāssaveyyuṃ, tassa saṃvarāya na paṭipajjati,</p>\r\n','\r',char(13)),'\n',char(10)),'-347-');
INSERT INTO pages VALUES(3322,'mula_ma_01',283,replace(replace('<p class="bodytext">na <a name="M1.0283"></a> rakkhati manindriyaṃ, manindriye na saṃvaraṃ āpajjati. evaṃ kho, bhikkhave, bhikkhu na vaṇaṃ paṭicchādetā hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu na dhūmaṃ kattā hoti? idha, bhikkhave, bhikkhu yathāsutaṃ yathāpariyattaṃ dhammaṃ na vitthārena paresaṃ desetā hoti. evaṃ kho, bhikkhave, bhikkhu na dhūmaṃ kattā hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu na titthaṃ jānāti? idha, bhikkhave, bhikkhu ye te bhikkhū bahussutā āgatāgamā dhammadharā vinayadharā mātikādharā, te kālena kālaṃ upasaṅkamitvā na paripucchati, na paripañhati – ‘idaṃ, bhante, kathaṃ? imassa ko attho’ti? tassa te āyasmanto avivaṭañceva na vivaranti, anuttānīkatañca na uttānī karonti, anekavihitesu <a name="V1.0286"></a> ca kaṅkhāṭhānīyesu dhammesu kaṅkhaṃ na paṭivinodenti. evaṃ kho, bhikkhave, bhikkhu na titthaṃ jānāti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu na pītaṃ jānāti? idha, bhikkhave, bhikkhu tathāgatappavedite dhammavinaye desiyamāne na labhati atthavedaṃ, na labhati dhammavedaṃ, na labhati dhammūpasaṃhitaṃ pāmojjaṃ. evaṃ kho, bhikkhave, bhikkhu na pītaṃ jānāti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu na <a name="T1.0413"></a> vīthiṃ jānāti? idha, bhikkhave, bhikkhu ariyaṃ aṭṭhaṅgikaṃ maggaṃ yathābhūtaṃ nappajānāti. evaṃ kho, bhikkhave, bhikkhu na vīthiṃ jānāti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu na gocarakusalo hoti? idha, bhikkhave, bhikkhu cattāro satipaṭṭhāne yathābhūtaṃ nappajānāti. evaṃ kho, bhikkhave<a name="P1.0222"></a>, bhikkhu na gocarakusalo hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu anavasesadohī hoti? idha, bhikkhave, bhikkhuṃ saddhā gahapatikā abhihaṭṭhuṃ pavārenti cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārehi, tatra bhikkhu mattaṃ na jānāti paṭiggahaṇāya. evaṃ kho, bhikkhave, bhikkhu anavasesadohī hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu ye te bhikkhū therā rattaññū cirapabbajitā saṅghapitaro saṅghapariṇāyakā te na atirekapūjāya pūjetā</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3323,'mula_ma_01',284,replace(replace('<p class="bodytext">hoti<a name="M1.0284"></a>? idha, bhikkhave, bhikkhu ye te bhikkhū therā rattaññū cirapabbajitā saṅghapitaro saṅghapariṇāyakā, tesu na mettaṃ kāyakammaṃ paccupaṭṭhāpeti āvi ceva raho ca, na mettaṃ vacīkammaṃ paccupaṭṭhāpeti āvi ceva raho ca, na mettaṃ manokammaṃ paccupaṭṭhāpeti āvi ceva raho ca. evaṃ kho, bhikkhave, bhikkhu ye te bhikkhū therā rattaññū cirapabbajitā saṅghapitaro saṅghapariṇāyakā te na atirekapūjāya pūjetā hoti.</p>\r\n<p class="bodytext">imehi kho bhikkhave ekādasahi dhammehi samannāgato bhikkhu abhabbo imasmiṃ dhammavinaye vuddhiṃ virūḷhiṃ vepullaṃ āpajjituṃ.</p>\r\n<p class="bodytext"><a name="para348"></a><a name="para348_mn1"></a><span class="paranum">348</span>. ekādasahi<a name="V1.0287"></a>, bhikkhave, aṅgehi samannāgato gopālako bhabbo gogaṇaṃ pariharituṃ phātiṃ kātuṃ. katamehi ekādasahi? idha, bhikkhave<a name="T1.0414"></a>, gopālako rūpaññū hoti, lakkhaṇakusalo hoti, āsāṭikaṃ hāretā hoti, vaṇaṃ paṭicchādetā hoti, dhūmaṃ kattā hoti, titthaṃ jānāti, pītaṃ jānāti, vīthiṃ jānāti, gocarakusalo hoti, sāvasesadohī ca hoti. ye te usabhā gopitaro gopariṇāyakā te atirekapūjāya pūjetā hoti. imehi kho, bhikkhave, ekādasahi aṅgehi samannāgato gopālako bhabbo gogaṇaṃ pariharituṃ phātiṃ kātuṃ. evameva kho, bhikkhave, ekādasahi dhammehi samannāgato bhikkhu bhabbo imasmiṃ dhammavinaye vuddhiṃ virūḷhiṃ vepullaṃ āpajjituṃ. katamehi ekādasahi? idha, bhikkhave, bhikkhu rūpaññū hoti, lakkhaṇakusalo hoti, āsāṭikaṃ hāretā hoti, vaṇaṃ paṭicchādetā hoti, dhūmaṃ kattā hoti, titthaṃ jānāti, pītaṃ jānāti, vīthiṃ jānāti, gocarakusalo hoti, sāvasesadohī ca hoti. ye te bhikkhū therā rattaññū cirapabbajitā saṅghapitaro saṅghapariṇāyakā te atirekapūjāya pūjetā hoti.</p>\r\n<p class="bodytext"><a name="para349"></a><a name="para349_mn1"></a><span class="paranum">349</span>. kathañca, bhikkhave, bhikkhu rūpaññū hoti? idha, bhikkhave, bhikkhu yaṃ kiñci rūpaṃ sabbaṃ rūpaṃ ‘cattāri mahābhūtāni<a name="P1.0223"></a>, catunnañca mahābhūtānaṃ upādāyarūpa’nti yathābhūtaṃ pajānāti. evaṃ kho, bhikkhave, bhikkhu rūpaññū hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu lakkhaṇakusalo hoti? idha, bhikkhave, bhikkhu kammalakkhaṇo bālo, kammalakkhaṇo paṇḍitoti yathābhūtaṃ pajānāti. evaṃ kho, bhikkhave, bhikkhu lakkhaṇakusalo hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-348-349-');
INSERT INTO pages VALUES(3324,'mula_ma_01',285,replace(replace('<p class="bodytext">kathañca<a name="M1.0285"></a>, bhikkhave, bhikkhu āsāṭikaṃ hāretā hoti? idha, bhikkhave, bhikkhu uppannaṃ <a name="T1.0415"></a> kāmavitakkaṃ nādhivāseti, pajahati vinodeti byantī karoti anabhāvaṃ gameti. uppannaṃ byāpādavitakkaṃ…pe… uppannaṃ vihiṃsāvitakkaṃ…pe… uppannuppanne pāpake akusale dhamme nādhivāseti, pajahati vinodeti byantī karoti anabhāvaṃ gameti. evaṃ kho, bhikkhave, bhikkhu āsāṭikaṃ hāretā hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu vaṇaṃ paṭicchādetā hoti? idha, bhikkhave, bhikkhu cakkhunā <a name="V1.0288"></a> rūpaṃ disvā na nimittaggāhī hoti nānubyañjanaggāhī. yatvādhikaraṇamenaṃ cakkhundriyaṃ asaṃvutaṃ viharantaṃ abhijjhādomanassā pāpakā akusalā dhammā anvāssaveyyuṃ tassa saṃvarāya paṭipajjati, rakkhati cakkhundriyaṃ, cakkhundriye saṃvaraṃ āpajjati. sotena saddaṃ sutvā…pe… ghānena gandhaṃ ghāyitvā…pe… jivhāya rasaṃ sāyitvā…pe… kāyena phoṭṭhabbaṃ phusitvā…pe… manasā dhammaṃ viññāya na nimittaggāhī hoti nānubyañjanaggāhī. yatvādhikaraṇamenaṃ manindriyaṃ asaṃvutaṃ viharantaṃ abhijjhādomanassā pāpakā akusalā dhammā anvāssaveyyuṃ tassa saṃvarāya paṭipajjati, rakkhati manindriyaṃ, manindriye saṃvaraṃ āpajjati. evaṃ kho, bhikkhave, bhikkhu vaṇaṃ paṭicchādetā hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu dhūmaṃ kattā hoti? idha, bhikkhave, bhikkhu yathāsutaṃ yathāpariyattaṃ dhammaṃ vitthārena paresaṃ desetā hoti. evaṃ kho, bhikkhave, bhikkhu dhūmaṃ kattā hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu titthaṃ jānāti? idha, bhikkhave, bhikkhu ye te bhikkhū bahussutā āgatāgamā dhammadharā vinayadharā mātikādharā te kālena kālaṃ <a name="T1.0416"></a> upasaṅkamitvā paripucchati, paripañhati – ‘idaṃ, bhante, kathaṃ? imassa ko attho’ti? tassa te āyasmanto avivaṭañceva vivaranti, anuttānīkatañca uttānī karonti, anekavihitesu ca kaṅkhāṭhānīyesu dhammesu kaṅkhaṃ paṭivinodenti. evaṃ kho, bhikkhave, bhikkhu titthaṃ jānāti.</p>\r\n<p class="bodytext">kathañca bhikkhave, bhikkhu <a name="P1.0224"></a> pītaṃ jānāti? idha, bhikkhave, bhikkhu tathāgatappavedite dhammavinaye desiyamāne labhati atthavedaṃ, labhati dhammavedaṃ, labhati dhammūpasaṃhitaṃ pāmojjaṃ. evaṃ kho, bhikkhave, bhikkhu pītaṃ jānāti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3325,'mula_ma_01',286,replace(replace('<p class="bodytext">kathañca<a name="M1.0286"></a>, bhikkhave, bhikkhu vīthiṃ jānāti? idha, bhikkhave, bhikkhu ariyaṃ aṭṭhaṅgikaṃ maggaṃ yathābhūtaṃ pajānāti. evaṃ kho, bhikkhave, bhikkhu vīthiṃ jānāti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu gocarakusalo hoti? idha, bhikkhave, bhikkhu cattāro satipaṭṭhāne yathābhūtaṃ pajānāti. evaṃ kho, bhikkhave, bhikkhu gocarakusalo hoti.</p>\r\n<p class="bodytext">kathañca <a name="V1.0289"></a> bhikkhave, bhikkhu sāvasesadohī hoti? idha, bhikkhave, bhikkhuṃ saddhā gahapatikā abhihaṭṭhuṃ pavārenti cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhārehi. tatra bhikkhu mattaṃ jānāti paṭiggahaṇāya. evaṃ kho, bhikkhave, bhikkhu sāvasesadohī hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu ye te bhikkhū therā rattaññū cirapabbajitā saṅghapitaro saṅghapariṇāyakā, te atirekapūjāya pūjetā hoti? idha, bhikkhave, bhikkhu ye te bhikkhū therā rattaññū cirapabbajitā saṅghapitaro saṅghapariṇāyakā tesu mettaṃ kāyakammaṃ paccupaṭṭhāpeti āvi ceva raho <a name="T1.0417"></a> ca, mettaṃ vacīkammaṃ paccupaṭṭhāpeti āvi ceva raho ca, mettaṃ manokammaṃ paccupaṭṭhāpeti āvi ceva raho ca. evaṃ kho, bhikkhave, bhikkhu ye te bhikkhū therā rattaññū cirapabbajitā saṅghapitaro saṅghapariṇāyakā te atirekapūjāya pūjetā hoti.</p>\r\n<p class="bodytext">imehi kho, bhikkhave, ekādasahi dhammehi samannāgato bhikkhu bhabbo imasmiṃ dhammavinaye vuddhiṃ virūḷhiṃ vepullaṃ āpajjitu"nti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">mahāgopālakasuttaṃ niṭṭhitaṃ tatiyaṃ.</p>\r\n<p class="subhead">4. cūḷagopālakasuttaṃ</p>\r\n<p class="bodytext"><a name="para350"></a><a name="para350_mn1"></a><span class="paranum">350</span>. evaṃ <a name="T1.0418"></a><a name="V1.0290"></a><a name="P1.0225"></a> me sutaṃ – ekaṃ samayaṃ bhagavā vajjīsu viharati ukkacelāyaṃ gaṅgāya nadiyā tīre. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n','\r',char(13)),'\n',char(10)),'-350-');
INSERT INTO pages VALUES(3326,'mula_ma_01',287,replace(replace('<p class="bodytext">bhūtapubbaṃ<a name="M1.0287"></a>, bhikkhave, māgadhako gopālako duppaññajātiko, vassānaṃ pacchime māse saradasamaye, asamavekkhitvā gaṅgāya nadiyā orimaṃ tīraṃ, asamavekkhitvā pārimaṃ tīraṃ, atittheneva gāvo patāresi uttaraṃ tīraṃ suvidehānaṃ. atha kho, bhikkhave, gāvo majjhegaṅgāya nadiyā sote āmaṇḍaliyaṃ karitvā tattheva anayabyasanaṃ āpajjiṃsu. taṃ kissa hetu? tathā hi so, bhikkhave, māgadhako gopālako duppaññajātiko, vassānaṃ pacchime māse saradasamaye, asamavekkhitvā gaṅgāya nadiyā orimaṃ tīraṃ, asamavekkhitvā pārimaṃ tīraṃ, atittheneva gāvo patāresi uttaraṃ tīraṃ suvidehānaṃ. evameva kho, bhikkhave, ye hi keci <span class="note">[ye keci (syā. kaṃ.)]</span> samaṇā vā brāhmaṇā vā akusalā imassa lokassa akusalā parassa lokassa, akusalā māradheyyassa akusalā amāradheyyassa, akusalā maccudheyyassa akusalā amaccudheyyassa, tesaṃ ye sotabbaṃ saddahātabbaṃ maññissanti, tesaṃ taṃ bhavissati dīgharattaṃ ahitāya dukkhāya.</p>\r\n<p class="bodytext"><a name="para351"></a><a name="para351_mn1"></a><span class="paranum">351</span>. bhūtapubbaṃ, bhikkhave, māgadhako gopālako sappaññajātiko, vassānaṃ <a name="T1.0419"></a> pacchime māse saradasamaye, samavekkhitvā gaṅgāya nadiyā orimaṃ tīraṃ, samavekkhitvā pārimaṃ tīraṃ, tittheneva gāvo patāresi uttaraṃ tīraṃ suvidehānaṃ. so paṭhamaṃ patāresi ye te usabhā gopitaro gopariṇāyakā. te tiriyaṃ gaṅgāya sotaṃ chetvā sotthinā pāraṃ agamaṃsu. athāpare patāresi balavagāvo dammagāvo. tepi tiriyaṃ gaṅgāya sotaṃ chetvā sotthinā pāraṃ agamaṃsu. athāpare patāresi vacchatare vacchatariyo. tepi tiriyaṃ gaṅgāya sotaṃ chetvā sotthinā pāraṃ agamaṃsu. athāpare patāresi vacchake kisābalake <span class="note">[kisabalake (sī. syā. pī.)]</span>. tepi tiriyaṃ gaṅgāya sotaṃ chetvā sotthinā pāraṃ agamaṃsu. bhūtapubbaṃ, bhikkhave, vacchako taruṇako tāvadeva jātako mātugoravakena vuyhamāno, sopi tiriyaṃ gaṅgāya sotaṃ chetvā sotthinā pāraṃ agamāsi. taṃ kissa hetu? tathā <a name="V1.0291"></a> hi so, bhikkhave, māgadhako gopālako sappaññajātiko<a name="P1.0226"></a>, vassānaṃ pacchime māse saradasamaye, samavekkhitvā gaṅgāya nadiyā</p>\r\n','\r',char(13)),'\n',char(10)),'-351-');
INSERT INTO pages VALUES(3327,'mula_ma_01',288,replace(replace('<p class="bodytext">orimaṃ <a name="M1.0288"></a> tīraṃ, samavekkhitvā pārimaṃ tīraṃ, tittheneva gāvo patāresi uttaraṃ tīraṃ suvidehānaṃ. evameva kho, bhikkhave, ye hi keci samaṇā vā brāhmaṇā vā kusalā imassa lokassa kusalā parassa lokassa, kusalā māradheyyassa kusalā amāradheyyassa, kusalā maccudheyyassa kusalā amaccudheyyassa, tesaṃ ye sotabbaṃ saddahātabbaṃ maññissanti, tesaṃ taṃ bhavissati dīgharattaṃ hitāya sukhāya.</p>\r\n<p class="bodytext"><a name="para352"></a><a name="para352_mn1"></a><span class="paranum">352</span>. seyyathāpi<a name="T1.0420"></a>, bhikkhave, ye te usabhā gopitaro gopariṇāyakā te tiriyaṃ gaṅgāya sotaṃ chetvā sotthinā pāraṃ agamaṃsu, evameva kho, bhikkhave, ye te bhikkhū arahanto khīṇāsavā vusitavanto katakaraṇīyā ohitabhārā anuppattasadatthā parikkhīṇabhavasaṃyojanā sammadaññā vimuttā, te tiriyaṃ mārassa sotaṃ chetvā sotthinā pāraṃ gatā.</p>\r\n<p class="bodytext">seyyathāpi te, bhikkhave, balavagāvo dammagāvo tiriyaṃ gaṅgāya sotaṃ chetvā sotthinā pāraṃ agamaṃsu, evameva kho, bhikkhave, ye te bhikkhū pañcannaṃ orambhāgiyānaṃ saṃyojanānaṃ parikkhayā opapātikā tattha parinibbāyino anāvattidhammā tasmā lokā, tepi tiriyaṃ mārassa sotaṃ chetvā sotthinā pāraṃ gamissanti.</p>\r\n<p class="bodytext">seyyathāpi te, bhikkhave, vacchatarā vacchatariyo tiriyaṃ gaṅgāya sotaṃ chetvā sotthinā pāraṃ agamaṃsu, evameva kho, bhikkhave, ye te bhikkhū tiṇṇaṃ saṃyojanānaṃ parikkhayā rāgadosamohānaṃ tanuttā sakadāgāmino sakiṃdeva imaṃ lokaṃ āgantvā dukkhassantaṃ karissanti, tepi tiriyaṃ mārassa sotaṃ chetvā sotthinā pāraṃ gamissanti.</p>\r\n<p class="bodytext">seyyathāpi te, bhikkhave, vacchakā kisābalakā tiriyaṃ gaṅgāya sotaṃ chetvā sotthinā pāraṃ agamaṃsu, evameva kho, bhikkhave, ye te bhikkhū tiṇṇaṃ saṃyojanānaṃ parikkhayā sotāpannā avinipātadhammā niyatā sambodhiparāyanā, tepi tiriyaṃ mārassa sotaṃ chetvā sotthinā pāraṃ gamissanti.</p>\r\n<p class="bodytext">seyyathāpi so, bhikkhave, vacchako taruṇako tāvadeva jātako mātugoravakena <a name="T1.0421"></a> vuyhamāno tiriyaṃ <a name="V1.0292"></a> gaṅgāya sotaṃ chetvā</p>\r\n','\r',char(13)),'\n',char(10)),'-352-');
INSERT INTO pages VALUES(3328,'mula_ma_01',289,replace(replace('<p class="bodytext">sotthinā <a name="M1.0289"></a> pāraṃ agamāsi, evameva kho, bhikkhave, ye te bhikkhū dhammānusārino saddhānusārino, tepi tiriyaṃ mārassa sotaṃ chetvā sotthinā pāraṃ gamissanti.</p>\r\n<p class="bodytext">ahaṃ kho pana, bhikkhave, kusalo <a name="P1.0227"></a> imassa lokassa kusalo parassa lokassa, kusalo māradheyyassa kusalo amāradheyyassa, kusalo maccudheyyassa kusalo amaccudheyyassa. tassa mayhaṃ, bhikkhave, ye sotabbaṃ saddahātabbaṃ maññissanti, tesaṃ taṃ bhavissati dīgharattaṃ hitāya sukhāyā"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. idaṃ vatvā sugato athāparaṃ etadavoca satthā –</p>\r\n<p class="gatha1">ayaṃ loko paro loko, jānatā suppakāsito,</p>\r\n<p class="gathalast">yañca mārena sampattaṃ, appattaṃ yañca maccunā.</p>\r\n<p class="gatha1">sabbaṃ lokaṃ abhiññāya, sambuddhena pajānatā,</p>\r\n<p class="gathalast">vivaṭaṃ amatadvāraṃ, khemaṃ nibbānapattiyā.</p>\r\n<p class="gatha1">chinnaṃ pāpimato sotaṃ, viddhastaṃ vinaḷīkataṃ,</p>\r\n<p class="gathalast">pāmojjabahulā hotha, khemaṃ pattattha <span class="note">[patthetha (syā. kaṃ. ka. aṭṭhakathāyaṃ saṃvaṇṇetabbapāṭho)]</span> bhikkhavo"ti.</p>\r\n<p class="centered">cūḷagopālakasuttaṃ niṭṭhitaṃ catutthaṃ.</p>\r\n<p class="subhead">5. cūḷasaccakasuttaṃ</p>\r\n<p class="bodytext"><a name="para353"></a><a name="para353_mn1"></a><span class="paranum">353</span>. evaṃ <a name="T1.0422"></a><a name="V1.0293"></a> me sutaṃ – ekaṃ samayaṃ bhagavā vesāliyaṃ viharati mahāvane kūṭāgārasālāyaṃ. tena kho pana samayena saccako nigaṇṭhaputto vesāliyaṃ paṭivasati bhassappavādako paṇḍitavādo sādhusammato bahujanassa. so vesāliyaṃ parisati evaṃ vācaṃ bhāsati – "nāhaṃ taṃ passāmi samaṇaṃ vā brāhmaṇaṃ vā, saṅghiṃ gaṇiṃ gaṇācariyaṃ, api arahantaṃ sammāsambuddhaṃ paṭijānamānaṃ, yo mayā vādena vādaṃ samāraddho na saṅkampeyya na sampakampeyya na sampavedheyya, yassa na kacchehi sedā mucceyyuṃ. thūṇaṃ cepāhaṃ acetanaṃ vādena vādaṃ samārabheyyaṃ, sāpi mayā vādena vādaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-353-');
INSERT INTO pages VALUES(3329,'mula_ma_01',290,replace(replace('<p class="bodytext">samāraddhā <a name="M1.0290"></a> saṅkampeyya sampakampeyya sampavedheyya. ko pana vādo manussabhūtassā"ti?</p>\r\n<p class="bodytext">atha kho āyasmā assaji pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya vesāliṃ piṇḍāya pāvisi. addasā kho saccako nigaṇṭhaputto vesāliyaṃ jaṅghāvihāraṃ anucaṅkamamāno anuvicaramāno <a name="P1.0228"></a> āyasmantaṃ assajiṃ dūratova āgacchantaṃ. disvāna yenāyasmā assaji tenupasaṅkami, upasaṅkamitvā āyasmatā assajinā saddhiṃ sammodi. sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ aṭṭhāsi. ekamantaṃ ṭhito kho saccako nigaṇṭhaputto āyasmantaṃ assajiṃ etadavoca – "kathaṃ pana, bho assaji, samaṇo gotamo sāvake vineti, kathaṃbhāgā <a name="T1.0423"></a> ca pana samaṇassa gotamassa sāvakesu anusāsanī bahulā pavattatī"ti? "evaṃ kho, aggivessana, bhagavā sāvake vineti, evaṃbhāgā ca pana bhagavato sāvakesu anusāsanī bahulā pavattati – ‘rūpaṃ, bhikkhave, aniccaṃ, vedanā aniccā, saññā aniccā, saṅkhārā aniccā, viññāṇaṃ aniccaṃ. rūpaṃ, bhikkhave, anattā, vedanā anattā, saññā anattā, saṅkhārā anattā, viññāṇaṃ anattā. sabbe saṅkhārā aniccā, sabbe dhammā anattā’ti. evaṃ kho, aggivessana, bhagavā sāvake vineti, evaṃbhāgā ca pana bhagavato sāvakesu anusāsanī bahulā pavattatī"ti. "dussutaṃ vata, bho assaji, assumha ye mayaṃ evaṃvādiṃ samaṇaṃ gotamaṃ assumha. appeva nāma mayaṃ kadāci karahaci tena bhotā gotamena saddhiṃ samāgaccheyyāma<a name="V1.0294"></a>, appeva nāma siyā kocideva kathāsallāpo, appeva nāma tasmā pāpakā diṭṭhigatā viveceyyāmā"ti.</p>\r\n<p class="bodytext"><a name="para354"></a><a name="para354_mn1"></a><span class="paranum">354</span>. tena kho pana samayena pañcamattāni licchavisatāni santhāgāre <span class="note">[sandhāgāre (ka.)]</span> sannipatitāni honti kenacideva karaṇīyena. atha kho saccako nigaṇṭhaputto yena te licchavī tenupasaṅkami, upasaṅkamitvā te licchavī etadavoca – "abhikkamantu bhonto licchavī, abhikkamantu bhonto licchavī, ajja me samaṇena gotamena saddhiṃ kathāsallāpo bhavissati. sace me samaṇo gotamo tathā patiṭṭhissati yathā ca me <span class="note">[yathāssa me (sī. pī.)]</span> ñātaññatarena sāvakena assajinā nāma bhikkhunā patiṭṭhitaṃ, seyyathāpi <a name="T1.0424"></a> nāma balavā puriso dīghalomikaṃ eḷakaṃ lomesu gahetvā ākaḍḍheyya parikaḍḍheyya</p>\r\n','\r',char(13)),'\n',char(10)),'-354-');
INSERT INTO pages VALUES(3330,'mula_ma_01',291,replace(replace('<p class="bodytext">samparikaḍḍheyya<a name="M1.0291"></a>, evamevāhaṃ samaṇaṃ gotamaṃ vādena vādaṃ ākaḍḍhissāmi parikaḍḍhissāmi samparikaḍḍhissāmi. seyyathāpi nāma balavā soṇḍikākammakāro mahantaṃ soṇḍikākiḷañjaṃ gambhīre udakarahade pakkhipitvā kaṇṇe gahetvā ākaḍḍheyya parikaḍḍheyya samparikaḍḍheyya, evamevāhaṃ samaṇaṃ gotamaṃ vādena vādaṃ ākaḍḍhissāmi parikaḍḍhissāmi samparikaḍḍhissāmi. seyyathāpi nāma balavā soṇḍikādhutto vālaṃ <a name="P1.0229"></a> <span class="note">[thālaṃ (ka.)]</span> kaṇṇe gahetvā odhuneyya niddhuneyya nipphoṭeyya <span class="note">[nicchādeyya (sī. pī. ka.), nicchoṭeyya (ka.), nippoṭheyya (syā. kaṃ.)]</span> evamevāhaṃ samaṇaṃ gotamaṃ vādena vādaṃ odhunissāmi niddhunissāmi nipphoṭessāmi. seyyathāpi nāma kuñjaro saṭṭhihāyano gambhīraṃ pokkharaṇiṃ ogāhetvā sāṇadhovikaṃ nāma kīḷitajātaṃ kīḷati, evamevāhaṃ samaṇaṃ gotamaṃ sāṇadhovikaṃ maññe kīḷitajātaṃ kīḷissāmi. abhikkamantu bhonto licchavī, abhikkamantu bhonto licchavī, ajja me samaṇena gotamena saddhiṃ kathāsallāpo bhavissatī"ti. tatrekacce licchavī evamāhaṃsu – "kiṃ samaṇo gotamo saccakassa nigaṇṭhaputtassa vādaṃ āropessati, atha kho saccako nigaṇṭhaputto samaṇassa gotamassa vādaṃ āropessatī"ti? ekacce licchavī evamāhaṃsu – "kiṃ so bhavamāno saccako nigaṇṭhaputto yo bhagavato vādaṃ āropessati, atha kho bhagavā saccakassa nigaṇṭhaputtassa vādaṃ āropessatī"ti? atha kho saccako <a name="T1.0425"></a> nigaṇṭhaputto pañcamattehi licchavisatehi parivuto yena mahāvanaṃ kūṭāgārasālā tenupasaṅkami.</p>\r\n<p class="bodytext"><a name="para355"></a><a name="para355_mn1"></a><span class="paranum">355</span>. tena kho pana samayena sambahulā bhikkhū abbhokāse caṅkamanti. atha kho saccako nigaṇṭhaputto yena te bhikkhū tenupasaṅkami, upasaṅkamitvā te bhikkhū etadavoca – "kahaṃ nu kho, bho, etarahi so bhavaṃ gotamo viharati? dassanakāmā hi mayaṃ taṃ bhavantaṃ gotama"nti<a name="V1.0295"></a>. esa, aggivessana, bhagavā mahāvanaṃ ajjhogāhetvā aññatarasmiṃ rukkhamūle divāvihāraṃ nisinno"ti. atha kho saccako nigaṇṭhaputto mahatiyā licchaviparisāya saddhiṃ mahāvanaṃ ajjhogāhetvā yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavatā saddhiṃ sammodi. sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdi. tepi kho licchavī appekacce bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu, appekacce</p>\r\n','\r',char(13)),'\n',char(10)),'-355-');
INSERT INTO pages VALUES(3331,'mula_ma_01',292,replace(replace('<p class="bodytext">bhagavatā <a name="M1.0292"></a> saddhiṃ sammodiṃsu, sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdiṃsu. appekacce yena bhagavā tenañjaliṃ paṇāmetvā ekamantaṃ nisīdiṃsu, appekacce bhagavato santike nāmagottaṃ sāvetvā ekamantaṃ nisīdiṃsu, appekacce tuṇhībhūtā ekamantaṃ nisīdiṃsu.</p>\r\n<p class="bodytext"><a name="para356"></a><a name="para356_mn1"></a><span class="paranum">356</span>. ekamantaṃ nisinno kho saccako nigaṇṭhaputto bhagavantaṃ etadavoca – "puccheyyāhaṃ bhavantaṃ gotamaṃ kiñcideva desaṃ, sace me bhavaṃ gotamo okāsaṃ karoti pañhassa veyyākaraṇāyā"ti. "puccha, aggivessana<a name="T1.0426"></a>, yadākaṅkhasī"ti<a name="P1.0230"></a>. kathaṃ pana bhavaṃ gotamo sāvake vineti, kathaṃbhāgā ca pana bhoto gotamassa sāvakesu anusāsanī bahulā pavattatī"ti? "evaṃ kho ahaṃ, aggivessana, sāvake vinemi, evaṃbhāgā ca pana me sāvakesu anusāsanī bahulā pavattati – ‘rūpaṃ, bhikkhave, aniccaṃ, vedanā aniccā, saññā aniccā, saṅkhārā aniccā, viññāṇaṃ aniccaṃ. rūpaṃ, bhikkhave, anattā, vedanā anattā, saññā anattā, saṅkhārā anattā, viññāṇaṃ anattā. sabbe saṅkhārā aniccā, sabbe dhammā anattā’ti. evaṃ kho ahaṃ, aggivessana, sāvake vinemi, evaṃbhāgā ca pana me sāvakesu anusāsanī bahulā pavattatī"ti.</p>\r\n<p class="bodytext">upamā maṃ, bho gotama, paṭibhātī"ti. "paṭibhātu taṃ, aggivessanā"ti bhagavā avoca.</p>\r\n<p class="bodytext">seyyathāpi, bho gotama, ye kecime bījagāmabhūtagāmā vuddhiṃ virūḷhiṃ vepullaṃ āpajjanti, sabbe te pathaviṃ nissāya pathaviyaṃ patiṭṭhāya. evamete bījagāmabhūtagāmā vuddhiṃ virūḷhiṃ vepullaṃ āpajjanti. seyyathāpi vā pana, bho gotama, ye kecime balakaraṇīyā kammantā karīyanti, sabbe te pathaviṃ nissāya pathaviyaṃ patiṭṭhāya. evamete balakaraṇīyā kammantā karīyanti. evameva kho, bho gotama, rūpattāyaṃ purisapuggalo rūpe patiṭṭhāya puññaṃ vā apuññaṃ vā pasavati, vedanattāyaṃ purisapuggalo vedanāyaṃ patiṭṭhāya puññaṃ vā <a name="V1.0296"></a> apuññaṃ vā pasavati, saññattāyaṃ purisapuggalo saññāyaṃ patiṭṭhāya puññaṃ vā apuññaṃ vā pasavati, saṅkhārattāyaṃ purisapuggalo saṅkhāresu <a name="T1.0427"></a> patiṭṭhāya puññaṃ vā apuññaṃ vā pasavati, viññāṇattāyaṃ purisapuggalo viññāṇe patiṭṭhāya puññaṃ vā apuññaṃ vā pasavatī"ti.</p>\r\n<p class="bodytext">nanu tvaṃ, aggivessana, evaṃ vadesi – ‘rūpaṃ me attā, vedanā me attā, saññā me attā, saṅkhārā me attā, viññāṇaṃ me attā"’ti? "ahañhi, bho</p>\r\n','\r',char(13)),'\n',char(10)),'-356-');
INSERT INTO pages VALUES(3332,'mula_ma_01',293,replace(replace('<p class="bodytext">gotama<a name="M1.0293"></a>, evaṃ vadāmi – ‘rūpaṃ me attā, vedanā me attā, saññā me attā, saṅkhārā me attā, viññāṇaṃ me attā’ti, ayañca mahatī janatā"ti.</p>\r\n<p class="bodytext">kiñhi te, aggivessana, mahatī janatā karissati? iṅgha tvaṃ, aggivessana, sakaññeva vādaṃ nibbeṭhehī"ti. "ahañhi, bho gotama, evaṃ vadāmi – ‘rūpaṃ me attā, vedanā me attā, saññā me attā, saṅkhārā me attā, viññāṇaṃ me attā"’ti.</p>\r\n<p class="bodytext"><a name="para357"></a><a name="para357_mn1"></a><span class="paranum">357</span>. tena hi, aggivessana, taññevettha paṭipucchissāmi, yathā te khameyya tathā naṃ <span class="note">[tathā taṃ (ka.)]</span> byākareyyāsi. taṃ kiṃ maññasi<a name="P1.0231"></a>, aggivessana, vatteyya rañño khattiyassa muddhāvasittassa sakasmiṃ vijite vaso – ghātetāyaṃ vā ghātetuṃ, jāpetāyaṃ vā jāpetuṃ, pabbājetāyaṃ vā pabbājetuṃ, seyyathāpi rañño pasenadissa kosalassa, seyyathāpi vā pana rañño māgadhassa ajātasattussa vedehiputtassā"ti? "vatteyya, bho gotama, rañño khattiyassa muddhāvasittassa sakasmiṃ vijite vaso – ghātetāyaṃ vā ghātetuṃ, jāpetāyaṃ vā jāpetuṃ, pabbājetāyaṃ vā pabbājetuṃ, seyyathāpi rañño pasenadissa kosalassa, seyyathāpi vā pana rañño māgadhassa ajātasattussa vedehiputtassa. imesampi hi, bho gotama, saṅghānaṃ gaṇānaṃ – seyyathidaṃ, vajjīnaṃ mallānaṃ – vattati sakasmiṃ vijite vaso – ghātetāyaṃ vā ghātetuṃ, jāpetāyaṃ vā jāpetuṃ, pabbājetāyaṃ vā pabbājetuṃ. kiṃ pana rañño khattiyassa muddhāvasittassa, seyyathāpi rañño pasenadissa kosalassa<a name="T1.0428"></a>, seyyathāpi vā pana rañño māgadhassa ajātasattussa vedehiputtassa? vatteyya, bho gotama, vattituñca marahatī"ti.</p>\r\n<p class="bodytext">taṃ kiṃ maññasi, aggivessana, yaṃ tvaṃ evaṃ vadesi – ‘rūpaṃ me attā’ti, vattati te <a name="V1.0297"></a> tasmiṃ rūpe vaso – evaṃ me rūpaṃ hotu, evaṃ me rūpaṃ mā ahosī"ti? evaṃ vutte, saccako nigaṇṭhaputto tuṇhī ahosi. dutiyampi kho bhagavā saccakaṃ nigaṇṭhaputtaṃ etadavoca – "taṃ kiṃ maññasi, aggivessana, yaṃ tvaṃ evaṃ vadesi – ‘rūpaṃ me attā’ti, vattati te tasmiṃ rūpe vaso – evaṃ me rūpaṃ hotu, evaṃ me rūpaṃ mā ahosī"ti?</p>\r\n','\r',char(13)),'\n',char(10)),'-357-');
INSERT INTO pages VALUES(3333,'mula_ma_01',294,replace(replace('<p class="bodytext">dutiyampi <a name="M1.0294"></a> kho saccako nigaṇṭhaputto tuṇhī ahosi. atha kho bhagavā saccakaṃ nigaṇṭhaputtaṃ etadavoca – "byākarohi dāni, aggivessana, na dāni te tuṇhībhāvassa kālo. yo koci, aggivessana tathāgatena yāvatatiyaṃ sahadhammikaṃ pañhaṃ puṭṭho na byākaroti, etthevassa sattadhā muddhā phalatī"ti.</p>\r\n<p class="bodytext">tena kho pana samayena vajirapāṇi yakkho āyasaṃ vajiraṃ ādāya ādittaṃ sampajjalitaṃ sajotibhūtaṃ saccakassa nigaṇṭhaputtassa uparivehāsaṃ ṭhito hoti – ‘sacāyaṃ <a name="T1.0429"></a> saccako nigaṇṭhaputto bhagavatā yāvatatiyaṃ sahadhammikaṃ pañhaṃ puṭṭho na byākarissati etthevassa sattadhā muddhaṃ phālessāmī’ti. taṃ kho pana vajirapāṇiṃ yakkhaṃ bhagavā ceva passati saccako ca nigaṇṭhaputto. atha kho saccako nigaṇṭhaputto bhīto saṃviggo lomahaṭṭhajāto bhagavantaṃyeva <a name="P1.0232"></a> tāṇaṃ gavesī bhagavantaṃyeva leṇaṃ gavesī bhagavantaṃyeva saraṇaṃ gavesī bhagavantaṃ etadavoca – "pucchatu maṃ bhavaṃ gotamo, byākarissāmī"ti.</p>\r\n<p class="bodytext"><a name="para358"></a><a name="para358_mn1"></a><span class="paranum">358</span>. taṃ kiṃ maññasi, aggivessana, yaṃ tvaṃ evaṃ vadesi – ‘rūpaṃ me attā’ti, vattati te tasmiṃ rūpe vaso – evaṃ me rūpaṃ hotu, evaṃ me rūpaṃ mā ahosī"ti? "no hidaṃ, bho gotama".</p>\r\n<p class="bodytext">manasi karohi, aggivessana, manasi karitvā kho, aggivessana, byākarohi. na kho te sandhiyati purimena vā pacchimaṃ pacchimena vā purimaṃ. taṃ kiṃ maññasi, aggivessana, yaṃ tvaṃ evaṃ vadesi – ‘vedanā me attā’ti, vattati te tissaṃ vedanāyaṃ <span class="note">[tāyaṃ vedanāyaṃ (sī. syā.)]</span> vaso – evaṃ me vedanā hotu, evaṃ me vedanā mā ahosī"ti? "no hidaṃ, bho gotama".</p>\r\n<p class="bodytext">manasi karohi, aggivessana, manasi karitvā kho, aggivessana, byākarohi. na kho te sandhiyati purimena vā pacchimaṃ, pacchimena vā purimaṃ. taṃ kiṃ maññasi, aggivessana<a name="V1.0298"></a>, yaṃ tvaṃ evaṃ vadesi – ‘saññā me attā’ti, vattati te tissaṃ saññāyaṃ vaso – evaṃ me saññā hotu, evaṃ me saññā mā ahosī"ti? "no hidaṃ, bho gotama".</p>\r\n','\r',char(13)),'\n',char(10)),'-358-');
INSERT INTO pages VALUES(3334,'mula_ma_01',295,replace(replace('<p class="bodytext">manasi <a name="M1.0295"></a> karohi, aggivessana<a name="T1.0430"></a>, manasi karitvā kho, aggivessana, byākarohi. na kho te sandhiyati purimena vā pacchimaṃ, pacchimena vā purimaṃ. taṃ kiṃ maññasi, aggivessana, yaṃ tvaṃ evaṃ vadesi – ‘saṅkhārā me attā’ti, vattati te tesu saṅkhāresu vaso – evaṃ me saṅkhārā hontu, evaṃ me saṅkhārā mā ahesu"nti? "no hidaṃ, bho gotama".</p>\r\n<p class="bodytext">manasi karohi, aggivessana, manasi karitvā kho, aggivessana, byākarohi. na kho te sandhiyati purimena vā pacchimaṃ, pacchimena vā purimaṃ. taṃ kiṃ maññasi, aggivessana, yaṃ tvaṃ evaṃ vadesi – ‘viññāṇaṃ me attā’ti, vattati te tasmiṃ viññāṇe vaso – evaṃ me viññāṇaṃ hotu, evaṃ me viññāṇaṃ mā ahosī"ti? "no hidaṃ, bho gotama".</p>\r\n<p class="bodytext">manasi karohi, aggivessana, manasi karitvā kho, aggivessana, byākarohi. na kho te sandhiyati purimena vā pacchimaṃ, pacchimena vā purimaṃ. taṃ kiṃ maññasi, aggivessana, rūpaṃ niccaṃ vā aniccaṃ vā"ti? "aniccaṃ, bho gotama". "yaṃ panāniccaṃ dukkhaṃ vā taṃ sukhaṃ vā"ti? "dukkhaṃ, bho gotama". "yaṃ panāniccaṃ dukkhaṃ vipariṇāmadhammaṃ, kallaṃ nu taṃ samanupassituṃ – ‘etaṃ mama, esohamasmi, eso <a name="P1.0233"></a> me attā"’ti? "no hidaṃ, bho gotama".</p>\r\n<p class="bodytext">taṃ kiṃ maññasi, aggivessana, vedanā…pe… saññā…pe… saṅkhārā…pe… taṃ kiṃ maññasi, aggivessana, viññāṇaṃ niccaṃ vā aniccaṃ vā"ti? "aniccaṃ, bho gotama". "yaṃ panāniccaṃ dukkhaṃ vā taṃ sukhaṃ vā"ti? "dukkhaṃ, bho gotama". "yaṃ panāniccaṃ dukkhaṃ vipariṇāmadhammaṃ, kallaṃ nu taṃ samanupassituṃ <a name="T1.0431"></a> – ‘etaṃ mama, esohamasmi, eso me attā"’ti? "no hidaṃ, bho gotama".</p>\r\n<p class="bodytext">taṃ kiṃ maññasi, aggivessana, yo nu kho dukkhaṃ allīno dukkhaṃ upagato dukkhaṃ ajjhosito<a name="V1.0299"></a>, dukkhaṃ ‘etaṃ mama, esohamasmi, eso me attā’ti samanupassati, api nu kho so sāmaṃ vā dukkhaṃ parijāneyya, dukkhaṃ vā parikkhepetvā vihareyyā"ti? "kiñhi siyā, bho gotama? no hidaṃ, bho gotamā"ti.</p>\r\n<p class="bodytext">taṃ kiṃ maññasi, aggivessana, nanu tvaṃ evaṃ sante dukkhaṃ allīno dukkhaṃ upagato dukkhaṃ ajjhosito, dukkhaṃ – ‘etaṃ mama, esohamasmi, eso me attā’ti samanupassasī"ti? "kiñhi no siyā, bho gotama? evametaṃ bho gotamā"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3335,'mula_ma_01',296,replace(replace('<p class="bodytext"><a name="para359"></a><a name="para359_mn1"></a><span class="paranum">359</span>. seyyathāpi<a name="M1.0296"></a>, aggivessana, puriso sāratthiko sāragavesī sārapariyesanaṃ caramāno tiṇhaṃ kuṭhāriṃ <span class="note">[kudhāriṃ (syā. kaṃ. ka.)]</span> ādāya vanaṃ paviseyya. so tattha passeyya mahantaṃ kadalikkhandhaṃ ujuṃ navaṃ akukkukajātaṃ <span class="note">[akukkuṭajātaṃ (syā. kaṃ.)]</span>. tamenaṃ mūle chindeyya, mūle chetvā agge chindeyya, agge chetvā pattavaṭṭiṃ vinibbhujeyya <span class="note">[vinibbhujjeyya (ka.)]</span>. so tattha pattavaṭṭiṃ vinibbhujanto pheggumpi nādhigaccheyya, kuto sāraṃ? evameva kho tvaṃ, aggivessana, mayā sakasmiṃ vāde samanuyuñjiyamāno samanugāhiyamāno samanubhāsiyamāno ritto tuccho aparaddho. bhāsitā kho pana te esā, aggivessana, vesāliyaṃ parisati vācā – ‘nāhaṃ taṃ passāmi samaṇaṃ vā brāhmaṇaṃ vā, saṅghiṃ gaṇiṃ gaṇācariyaṃ, api arahantaṃ sammāsambuddhaṃ paṭijānamānaṃ, yo mayā vādena vādaṃ samāraddho na saṅkampeyya na sampakampeyya na <a name="T1.0432"></a> sampavedheyya, yassa na kacchehi sedā mucceyyuṃ. thūṇaṃ cepāhaṃ acetanaṃ vādena vādaṃ samārabheyyaṃ sāpi mayā vādena vādaṃ samāraddhā saṅkampeyya sampakampeyya sampavedheyya. ko pana vādo manussabhūtassā’ti? tuyhaṃ kho pana, aggivessana, appekaccāni sedaphusitāni nalāṭā muttāni, uttarāsaṅgaṃ vinibhinditvā bhūmiyaṃ patiṭṭhitāni. mayhaṃ kho pana, aggivessana, natthi etarahi kāyasmiṃ sedo"ti. iti bhagavā tasmiṃ <span class="note">[tassaṃ (?)]</span> parisati suvaṇṇavaṇṇaṃ kāyaṃ vivari. evaṃ <a name="P1.0234"></a> vutte, saccako nigaṇṭhaputto tuṇhībhūto maṅkubhūto pattakkhandho adhomukho pajjhāyanto appaṭibhāno nisīdi.</p>\r\n<p class="bodytext"><a name="para360"></a><a name="para360_mn1"></a><span class="paranum">360</span>. atha kho dummukho licchaviputto saccakaṃ nigaṇṭhaputtaṃ tuṇhībhūtaṃ maṅkubhūtaṃ pattakkhandhaṃ adhomukhaṃ pajjhāyantaṃ appaṭibhānaṃ viditvā bhagavantaṃ etadavoca – "upamā maṃ, bhagavā, paṭibhātī"ti. "paṭibhātu taṃ, dummukhā"ti bhagavā avoca. "seyyathāpi, bhante, gāmassa vā nigamassa vā avidūre pokkharaṇī. tatrāssa kakkaṭako. atha kho, bhante, sambahulā <a name="V1.0300"></a> kumārakā vā kumārikā vā tamhā gāmā vā nigamā vā nikkhamitvā yena sā pokkharaṇī tenupasaṅkameyyuṃ, upasaṅkamitvā taṃ pokkharaṇiṃ ogāhetvā taṃ kakkaṭakaṃ udakā uddharitvā thale patiṭṭhāpeyyuṃ. yaññadeva hi so, bhante, kakkaṭako aḷaṃ abhininnāmeyya taṃ tadeva te kumārakā vā kumārikā vā kaṭṭhena vā kathalena vā sañchindeyyuṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-359-360-');
INSERT INTO pages VALUES(3336,'mula_ma_01',297,replace(replace('<p class="bodytext">sambhañjeyyuṃ <a name="M1.0297"></a> sampalibhañjeyyuṃ. evañhi <a name="T1.0433"></a> so, bhante, kakkaṭako sabbehi aḷehi sañchinnehi sambhaggehi sampalibhaggehi abhabbo taṃ pokkharaṇiṃ puna otarituṃ, seyyathāpi pubbe. evameva kho, bhante, yāni saccakassa nigaṇṭhaputtassa visūkāyitāni visevitāni vipphanditāni tānipi sabbāni <span class="note">[vipphanditāni kānici kānici tāni (sī. syā. kaṃ. pī.)]</span> bhagavatā sañchinnāni sambhaggāni sampalibhaggāni, abhabbo ca dāni, bhante, saccako nigaṇṭhaputto puna bhagavantaṃ upasaṅkamituṃ yadidaṃ vādādhippāyo"ti. evaṃ vutte, saccako nigaṇṭhaputto dummukhaṃ licchaviputtaṃ etadavoca – "āgamehi tvaṃ, dummukha, āgamehi tvaṃ, dummukha ( ) <span class="note">[(mukharosi tvaṃ dummukha) (syā. kaṃ.)]</span> na mayaṃ tayā saddhiṃ mantema, idha mayaṃ bhotā gotamena saddhiṃ mantema.</p>\r\n<p class="bodytext"><a name="para361"></a><a name="para361_mn1"></a><span class="paranum">361</span>. tiṭṭhatesā, bho gotama, amhākañceva aññesañca puthusamaṇabrāhmaṇānaṃ vācā. vilāpaṃ vilapitaṃ maññe. kittāvatā ca nu kho bhoto gotamassa sāvako sāsanakaro hoti ovādapatikaro tiṇṇavicikiccho vigatakathaṃkatho vesārajjappatto aparappaccayo satthusāsane viharatī"ti? "idha, aggivessana, mama sāvako yaṃ kiñci rūpaṃ atītānāgatapaccuppannaṃ ajjhattaṃ vā bahiddhā vā oḷārikaṃ vā sukhumaṃ vā hīnaṃ vā paṇītaṃ vā yaṃ dūre santike vā, sabbaṃ rūpaṃ ‘netaṃ mama, nesohamasmi, na meso attā’ti evametaṃ <a name="P1.0235"></a> yathābhūtaṃ sammappaññāya passati, yā kāci vedanā…pe… yā kāci saññā…pe… ye keci saṅkhārā…pe… yaṃ kiñci viññāṇaṃ atītānāgatapaccuppannaṃ ajjhattaṃ vā bahiddhā vā oḷārikaṃ vā sukhumaṃ vā hīnaṃ vā paṇītaṃ vā, yaṃ dūre santike vā, sabbaṃ viññāṇaṃ ‘netaṃ mama, nesohamasmi, na meso attā’ti evametaṃ yathābhūtaṃ sammappaññāya passati. ettāvatā kho, aggivessana, mama sāvako sāsanakaro hoti ovādapatikaro tiṇṇavicikiccho vigatakathaṃkatho vesārajjappatto aparappaccayo satthusāsane viharatī"ti.</p>\r\n<p class="bodytext">kittāvatā <a name="V1.0301"></a> pana, bho gotama, bhikkhu arahaṃ hoti khīṇāsavo vusitavā katakaraṇīyo ohitabhāro anuppattasadattho parikkhīṇabhavasaṃyojano sammadaññā vimutto"ti? "idha, aggivessana, bhikkhu yaṃ kiñci rūpaṃ atītānāgatapaccuppannaṃ ajjhattaṃ vā bahiddhā vā oḷārikaṃ vā sukhumaṃ vā hīnaṃ vā paṇītaṃ vā yaṃ dūre santike vā sabbaṃ rūpaṃ ‘netaṃ mama, nesohamasmi,</p>\r\n','\r',char(13)),'\n',char(10)),'-361-');
INSERT INTO pages VALUES(3337,'mula_ma_01',298,replace(replace('<p class="bodytext">na <a name="M1.0298"></a> meso attā’ti evametaṃ yathābhūtaṃ sammappaññāya disvā anupādā vimutto hoti, yā kāci vedanā…pe… yā kāci saññā…pe… ye keci saṅkhārā…pe… yaṃ kiñci viññāṇaṃ atītānāgatapaccuppannaṃ ajjhattaṃ vā bahiddhā vā oḷārikaṃ vā sukhumaṃ vā hīnaṃ vā <a name="T1.0434"></a> paṇītaṃ vā yaṃ dūre santike vā sabbaṃ viññāṇaṃ ‘netaṃ mama, nesohamasmi, na meso attā’ti evametaṃ yathābhūtaṃ sammappaññāya disvā anupādā vimutto hoti. ettāvatā kho, aggivessana, bhikkhu arahaṃ hoti khīṇāsavo vusitavā katakaraṇīyo ohitabhāro anuppattasadattho parikkhīṇabhavasaṃyojano sammadaññā vimutto. evaṃ vimuttacitto kho, aggivessana, bhikkhu tīhi anuttariyehi samannāgato <a name="T1.0435"></a> hoti – dassanānuttariyena, paṭipadānuttariyena, vimuttānuttariyena. evaṃ vimuttacitto kho, aggivessana, bhikkhu tathāgataññeva sakkaroti garuṃ karoti māneti pūjeti – buddho so bhagavā bodhāya dhammaṃ deseti, danto so bhagavā damathāya dhammaṃ deseti, santo so bhagavā samathāya dhammaṃ deseti, tiṇṇo so bhagavā taraṇāya dhammaṃ deseti, parinibbuto so bhagavā parinibbānāya dhammaṃ desetī"ti.</p>\r\n<p class="bodytext"><a name="para362"></a><a name="para362_mn1"></a><span class="paranum">362</span>. evaṃ vutte, saccako nigaṇṭhaputto bhagavantaṃ etadavoca <a name="P1.0236"></a> – "mayameva, bho gotama, dhaṃsī, mayaṃ pagabbā, ye mayaṃ bhavantaṃ gotamaṃ vādena vādaṃ āsādetabbaṃ amaññimha. siyā hi, bho gotama, hatthiṃ pabhinnaṃ āsajja purisassa sotthibhāvo, na tveva bhavantaṃ gotamaṃ āsajja siyā purisassa sotthibhāvo. siyā hi, bho gotama, pajjalitaṃ <span class="note">[jalantaṃ (sī. pī.)]</span> aggikkhandhaṃ āsajja purisassa sotthibhāvo<a name="V1.0302"></a>, na tveva bhavantaṃ gotamaṃ āsajja siyā purisassa sotthibhāvo. siyā hi, bho gotama, āsīvisaṃ ghoravisaṃ āsajja purisassa sotthibhāvo, na tveva bhavantaṃ gotamaṃ āsajja siyā purisassa sotthibhāvo. mayameva, bho gotama, dhaṃsī, mayaṃ pagabbā, ye mayaṃ bhavantaṃ gotamaṃ vādena vādaṃ āsādetabbaṃ amaññimha. adhivāsetu <span class="note">[adhivāsetu ca (pī. ka.)]</span> me bhavaṃ gotamo svātanāya bhattaṃ saddhiṃ bhikkhusaṅghenā"ti. adhivāsesi bhagavā tuṇhībhāvena.</p>\r\n<p class="bodytext"><a name="para363"></a><a name="para363_mn1"></a><span class="paranum">363</span>. atha kho saccako nigaṇṭhaputto bhagavato adhivāsanaṃ viditvā te licchavī āmantesi – "suṇantu me bhonto licchavī,</p>\r\n','\r',char(13)),'\n',char(10)),'-362-363-');
INSERT INTO pages VALUES(3338,'mula_ma_01',299,replace(replace('<p class="bodytext">samaṇo <a name="M1.0299"></a> me gotamo nimantito svātanāya saddhiṃ <a name="T1.0436"></a> bhikkhusaṅghena. tena me abhihareyyātha yamassa patirūpaṃ maññeyyāthā"ti. atha kho te licchavī tassā rattiyā accayena saccakassa nigaṇṭhaputtassa pañcamattāni thālipākasatāni bhattābhihāraṃ abhihariṃsu. atha kho nigaṇṭhaputto sake ārāme paṇītaṃ khādanīyaṃ bhojanīyaṃ paṭiyādāpetvā bhagavato kālaṃ ārocāpesi – "kālo, bho gotama, niṭṭhitaṃ bhatta"nti. atha kho bhagavā pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya yena saccakassa nigaṇṭhaputtassa ārāmo tenupasaṅkami, upasaṅkamitvā paññatte āsane nisīdi saddhiṃ bhikkhusaṅghena. atha kho saccako nigaṇṭhaputto buddhappamukhaṃ bhikkhusaṅghaṃ paṇītena khādanīyena bhojanīyena sahatthā santappesi sampavāresi. atha kho saccako nigaṇṭhaputto bhagavantaṃ bhuttāviṃ onītapattapāṇiṃ aññataraṃ nīcaṃ āsanaṃ gahetvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho saccako nigaṇṭhaputto bhagavantaṃ etadavoca – "yamidaṃ, bho gotama, dāne puññañca puññamahī ca taṃ dāyakānaṃ sukhāya hotū"ti. "yaṃ kho, aggivessana, tādisaṃ dakkhiṇeyyaṃ āgamma avītarāgaṃ avītadosaṃ avītamohaṃ, taṃ <a name="P1.0237"></a> dāyakānaṃ bhavissati. yaṃ kho, aggivessana, mādisaṃ dakkhiṇeyyaṃ āgamma vītarāgaṃ vītadosaṃ vītamohaṃ, taṃ tuyhaṃ bhavissatī"ti.</p>\r\n<p class="centered">cūḷasaccakasuttaṃ niṭṭhitaṃ pañcamaṃ.</p>\r\n<p class="subhead">6. mahāsaccakasuttaṃ</p>\r\n<p class="bodytext"><a name="para364"></a><a name="para364_mn1"></a><span class="paranum">364</span>. evaṃ <a name="T1.0437"></a><a name="V1.0303"></a> me sutaṃ – ekaṃ samayaṃ bhagavā vesāliyaṃ viharati mahāvane kūṭāgārasālāyaṃ. tena kho pana samayena bhagavā pubbaṇhasamayaṃ sunivattho hoti pattacīvaramādāya vesāliṃ piṇḍāya pavisitukāmo <span class="note">[pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya… pavisitukāmo hoti (sī.)]</span>. atha kho saccako nigaṇṭhaputto jaṅghāvihāraṃ anucaṅkamamāno anuvicaramāno yena mahāvanaṃ kūṭāgārasālā tenupasaṅkami. addasā kho āyasmā ānando saccakaṃ nigaṇṭhaputtaṃ dūratova āgacchantaṃ. disvāna bhagavantaṃ etadavoca – "ayaṃ, bhante,</p>\r\n','\r',char(13)),'\n',char(10)),'-364-');
INSERT INTO pages VALUES(3339,'mula_ma_01',300,replace(replace('<p class="bodytext">saccako <a name="M1.0300"></a> nigaṇṭhaputto āgacchati bhassappavādako paṇḍitavādo sādhusammato bahujanassa. eso kho, bhante, avaṇṇakāmo buddhassa, avaṇṇakāmo dhammassa, avaṇṇakāmo saṅghassa. sādhu, bhante, bhagavā muhuttaṃ nisīdatu anukampaṃ upādāyā"ti. nisīdi bhagavā paññatte āsane. atha kho saccako nigaṇṭhaputto yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavatā saddhiṃ sammodi, sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho saccako nigaṇṭhaputto bhagavantaṃ etadavoca –</p>\r\n<p class="bodytext"><a name="para365"></a><a name="para365_mn1"></a><span class="paranum">365</span>. santi, bho gotama, eke samaṇabrāhmaṇā kāyabhāvanānuyogamanuyuttā viharanti, no cittabhāvanaṃ. phusanti hi te, bho gotama, sārīrikaṃ dukkhaṃ vedanaṃ. bhūtapubbaṃ, bho gotama, sārīrikāya dukkhāya vedanāya phuṭṭhassa sato ūrukkhambhopi nāma bhavissati, hadayampi nāma <a name="T1.0438"></a> phalissati, uṇhampi lohitaṃ mukhato uggamissati, ummādampi pāpuṇissati <span class="note">[pāpuṇissanti (syā. kaṃ.)]</span> cittakkhepaṃ. tassa kho etaṃ, bho gotama, kāyanvayaṃ cittaṃ hoti, kāyassa vasena vattati. taṃ kissa hetu? abhāvitattā <a name="P1.0238"></a> cittassa. santi pana, bho gotama, eke samaṇabrāhmaṇā cittabhāvanānuyogamanuyuttā viharanti, no kāyabhāvanaṃ. phusanti hi te, bho gotama, cetasikaṃ dukkhaṃ vedanaṃ. bhūtapubbaṃ, bho gotama, cetasikāya dukkhāya vedanāya phuṭṭhassa sato ūrukkhambhopi nāma bhavissati, hadayampi nāma phalissati, uṇhampi lohitaṃ mukhato uggamissati, ummādampi pāpuṇissati cittakkhepaṃ. tassa kho eso, bho gotama, cittanvayo kāyo hoti, cittassa vasena vattati. taṃ kissa hetu? abhāvitattā kāyassa<a name="V1.0304"></a>. tassa mayhaṃ, bho gotama, evaṃ hoti – ‘addhā bhoto gotamassa sāvakā cittabhāvanānuyogamanuyuttā viharanti, no kāyabhāvana"’nti.</p>\r\n<p class="bodytext"><a name="para366"></a><a name="para366_mn1"></a><span class="paranum">366</span>. kinti pana te, aggivessana, kāyabhāvanā sutā"ti? "seyyathidaṃ – nando vaccho, kiso saṃkicco, makkhali gosālo – etehi, bho gotama, acelakā muttācārā hatthāpalekhanā naehibhaddantikā natiṭṭhabhaddantikā <span class="note">[naehibhadantikā, natiṭṭhabhadantikā (sī. syā. kaṃ. pī. ka.)]</span> na abhihaṭaṃ na uddissakataṃ na nimantanaṃ sādiyanti, te na</p>\r\n','\r',char(13)),'\n',char(10)),'-365-366-');
INSERT INTO pages VALUES(3340,'mula_ma_01',301,replace(replace('<p class="bodytext">kumbhimukhā <a name="M1.0301"></a> paṭiggaṇhanti na kaḷopimukhā paṭiggaṇhanti na eḷakamantaraṃ na daṇḍamantaraṃ na musalamantaraṃ na dvinnaṃ bhuñjamānānaṃ na gabbhiniyā na pāyamānāya na purisantaragatāya na saṅkittīsu na yattha sā upaṭṭhito hoti na yattha makkhikā saṇḍasaṇḍacārinī<a name="T1.0439"></a>, na macchaṃ na maṃsaṃ na suraṃ na merayaṃ na thusodakaṃ pivanti. te ekāgārikā vā honti ekālopikā, dvāgārikā vā honti dvālopikā…pe… sattāgārikā vā honti sattālopikā. ekissāpi dattiyā yāpenti, dvīhipi dattīhi yāpenti…pe… sattahipi dattīhi yāpenti. ekāhikampi āhāraṃ āhārenti, dvīhikampi āhāraṃ āhārenti…pe… sattāhikampi āhāraṃ āhārenti. iti evarūpaṃ addhamāsikampi pariyāyabhattabhojanānuyogamanuyuttā viharantī"ti.</p>\r\n<p class="bodytext">kiṃ pana te, aggivessana, tāvatakeneva yāpentī"ti? "no hidaṃ, bho gotama. appekadā, bho gotama, uḷārāni uḷārāni khādanīyāni khādanti, uḷārāni uḷārāni bhojanāni bhuñjanti, uḷārāni uḷārāni sāyanīyāni sāyanti, uḷārāni uḷārāni pānāni pivanti. te imaṃ kāyaṃ balaṃ gāhenti nāma, brūhenti nāma, medenti nāmā"ti.</p>\r\n<p class="bodytext">yaṃ kho te, aggivessana, purimaṃ pahāya pacchā upacinanti, evaṃ imassa kāyassa ācayāpacayo hoti. kinti pana te, aggivessana, cittabhāvanā sutā"ti? cittabhāvanāya kho <a name="P1.0239"></a> saccako nigaṇṭhaputto bhagavatā puṭṭho samāno na sampāyāsi.</p>\r\n<p class="bodytext"><a name="para367"></a><a name="para367_mn1"></a><span class="paranum">367</span>. atha kho bhagavā saccakaṃ nigaṇṭhaputtaṃ etadavoca – "yāpi kho te esā, aggivessana, purimā kāyabhāvanā bhāsitā sāpi ariyassa vinaye no dhammikā kāyabhāvanā. kāyabhāvanampi <span class="note">[kāyabhāvanaṃ hi (sī. pī. ka.)]</span> kho tvaṃ, aggivessana, na aññāsi, kuto pana tvaṃ cittabhāvanaṃ <a name="V1.0305"></a> jānissasi<a name="T1.0440"></a>? api ca, aggivessana, yathā abhāvitakāyo ca hoti abhāvitacitto ca, bhāvitakāyo ca hoti bhāvitacitto ca. taṃ suṇāhi, sādhukaṃ manasi karohi, bhāsissāmī"ti. "evaṃ, bho"ti kho saccako nigaṇṭhaputto bhagavato paccassosi. bhagavā etadavoca –</p>\r\n','\r',char(13)),'\n',char(10)),'-367-');
INSERT INTO pages VALUES(3341,'mula_ma_01',302,replace(replace('<p class="bodytext"><a name="para368"></a><a name="para368_mn1"></a><span class="paranum">368</span>. kathañca<a name="M1.0302"></a>, aggivessana, abhāvitakāyo ca hoti abhāvitacitto ca? idha, aggivessana, assutavato puthujjanassa uppajjati sukhā vedanā. so sukhāya vedanāya phuṭṭho samāno sukhasārāgī ca hoti sukhasārāgitañca āpajjati. tassa sā sukhā vedanā nirujjhati. sukhāya vedanāya nirodhā uppajjati dukkhā vedanā. so dukkhāya vedanāya phuṭṭho samāno socati kilamati paridevati urattāḷiṃ kandati sammohaṃ āpajjati. tassa kho esā, aggivessana, uppannāpi sukhā vedanā cittaṃ pariyādāya tiṭṭhati abhāvitattā kāyassa, uppannāpi dukkhā vedanā cittaṃ pariyādāya tiṭṭhati abhāvitattā cittassa. yassa kassaci, aggivessana, evaṃ ubhatopakkhaṃ uppannāpi sukhā vedanā cittaṃ pariyādāya tiṭṭhati abhāvitattā kāyassa, uppannāpi dukkhā vedanā cittaṃ pariyādāya tiṭṭhati abhāvitattā cittassa, evaṃ kho, aggivessana, abhāvitakāyo ca hoti abhāvitacitto ca.</p>\r\n<p class="bodytext"><a name="para369"></a><a name="para369_mn1"></a><span class="paranum">369</span>. kathañca, aggivessana, bhāvitakāyo ca hoti bhāvitacitto ca? idha, aggivessana, sutavato ariyasāvakassa uppajjati sukhā vedanā. so sukhāya vedanāya phuṭṭho samāno na sukhasārāgī <a name="T1.0441"></a> ca hoti, na sukhasārāgitañca āpajjati. tassa sā sukhā vedanā nirujjhati. sukhāya vedanāya nirodhā uppajjati dukkhā vedanā. so dukkhāya vedanāya phuṭṭho samāno na socati na kilamati na paridevati na urattāḷiṃ kandati na sammohaṃ āpajjati. tassa kho esā, aggivessana, uppannāpi sukhā vedanā cittaṃ na pariyādāya tiṭṭhati bhāvitattā kāyassa, uppannāpi dukkhā vedanā cittaṃ na pariyādāya tiṭṭhati bhāvitattā cittassa. yassa kassaci, aggivessana, evaṃ ubhatopakkhaṃ uppannāpi sukhā vedanā cittaṃ <a name="P1.0240"></a> na pariyādāya tiṭṭhati bhāvitattā kāyassa, uppannāpi dukkhā vedanā cittaṃ na pariyādāya tiṭṭhati bhāvitattā cittassa. evaṃ kho, aggivessana, bhāvitakāyo ca hoti bhāvitacitto cā"ti.</p>\r\n<p class="bodytext"><a name="para370"></a><a name="para370_mn1"></a><span class="paranum">370</span>. evaṃ pasanno ahaṃ bhoto gotamassa! bhavañhi gotamo bhāvitakāyo ca hoti bhāvitacitto cā"ti<a name="V1.0306"></a>. "addhā kho te ayaṃ, aggivessana, āsajja upanīya vācā bhāsitā, api ca te ahaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-368-369-370-');
INSERT INTO pages VALUES(3342,'mula_ma_01',303,replace(replace('<p class="bodytext">byākarissāmi<a name="M1.0303"></a>. yato kho ahaṃ, aggivessana, kesamassuṃ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anagāriyaṃ pabbajito, taṃ vata me uppannā vā sukhā vedanā cittaṃ pariyādāya ṭhassati, uppannā vā dukkhā vedanā cittaṃ pariyādāya ṭhassatīti netaṃ ṭhānaṃ <span class="note">[netaṃ khoṭhānaṃ (sī. pī.)]</span> vijjatī"ti.</p>\r\n<p class="bodytext">na hi nūna <span class="note">[na hanūna (sī. syā. kaṃ. pī.)]</span> bhoto gotamassa uppajjati tathārūpā sukhā vedanā yathārūpā uppannā sukhā vedanā cittaṃ pariyādāya tiṭṭheyya, na hi nūna bhoto gotamassa uppajjati <a name="T1.0442"></a> tathārūpā dukkhā vedanā yathārūpā uppannā dukkhā vedanā cittaṃ pariyādāya tiṭṭheyyā"ti.</p>\r\n<p class="bodytext"><a name="para371"></a><a name="para371_mn1"></a><span class="paranum">371</span>. kiñhi no siyā, aggivessana? idha me, aggivessana, pubbeva sambodhā anabhisambuddhassa bodhisattasseva sato etadahosi – ‘sambādho gharāvāso rajāpatho, abbhokāso pabbajjā. nayidaṃ sukaraṃ agāraṃ ajjhāvasatā ekantaparipuṇṇaṃ ekantaparisuddhaṃ saṅkhalikhitaṃ brahmacariyaṃ carituṃ. yaṃnūnāhaṃ kesamassuṃ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anagāriyaṃ pabbajeyya’nti. so kho ahaṃ, aggivessana, aparena samayena daharova samāno, susukāḷakeso bhadrena yobbanena samannāgato paṭhamena vayasā, akāmakānaṃ mātāpitūnaṃ assumukhānaṃ rudantānaṃ, kesamassuṃ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anagāriyaṃ pabbajiṃ. so evaṃ pabbajito samāno kiṃkusalagavesī anuttaraṃ santivarapadaṃ pariyesamāno yena āḷāro kālāmo tenupasaṅkamiṃ, upasaṅkamitvā āḷāraṃ kālāmaṃ etadavocaṃ – ‘icchāmahaṃ, āvuso kālāma, imasmiṃ dhammavinaye brahmacariyaṃ caritu’nti. evaṃ vutte, aggivessana, āḷāro kālāmo maṃ etadavoca – ‘viharatāyasmā, tādiso ayaṃ dhammo yattha viññū puriso nacirasseva sakaṃ ācariyakaṃ sayaṃ abhiññā sacchikatvā upasampajja vihareyyā’ti. so kho ahaṃ, aggivessana, nacirasseva khippameva taṃ dhammaṃ pariyāpuṇiṃ. so kho ahaṃ, aggivessana, tāvatakeneva oṭṭhapahatamattena lapitalāpanamattena ñāṇavādañca <a name="T1.0443"></a> vadāmi theravādañca, ‘jānāmi passāmī’ti ca paṭijānāmi, ahañceva aññe ca. tassa mayhaṃ, aggivessana, etadahosi – ‘na kho āḷāro kālāmo imaṃ dhammaṃ kevalaṃ saddhāmattakena sayaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-371-');
INSERT INTO pages VALUES(3343,'mula_ma_01',304,replace(replace('<p class="bodytext">abhiññā <a name="M1.0304"></a> sacchikatvā upasampajja viharāmīti pavedeti, addhā āḷāro kālāmo imaṃ dhammaṃ jānaṃ passaṃ viharatī"’ti.</p>\r\n<p class="bodytext">atha khvāhaṃ, aggivessana, yena āḷāro kālāmo tenupasaṅkamiṃ, upasaṅkamitvā āḷāraṃ <a name="V1.0307"></a> kālāmaṃ etadavocaṃ – ‘kittāvatā no, āvuso kālāma, imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharāmīti pavedesī’ti? evaṃ vutte, aggivessana, āḷāro kālāmo ākiñcaññāyatanaṃ pavedesi. tassa mayhaṃ, aggivessana, etadahosi – ‘na kho āḷārasseva kālāmassa atthi saddhā, mayhaṃpatthi saddhā, na kho āḷārasseva kālāmassa atthi vīriyaṃ, mayhaṃpatthi vīriyaṃ, na kho āḷārasseva kālāmassa atthi sati, mayhaṃpatthi sati, na kho āḷārasseva kālāmassa atthi samādhi, mayhaṃpatthi samādhi, na kho āḷārasseva kālāmassa atthi paññā, mayhaṃpatthi paññā, yaṃnūnāhaṃ yaṃ dhammaṃ āḷāro kālāmo sayaṃ abhiññā sacchikatvā upasampajja viharāmīti pavedeti tassa dhammassa sacchikiriyāya padaheyya’nti. so kho ahaṃ, aggivessana, nacirasseva khippameva taṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja vihāsiṃ.</p>\r\n<p class="bodytext">atha khvāhaṃ, aggivessana, yena āḷāro kālāmo tenupasaṅkamiṃ, upasaṅkamitvā āḷāraṃ kālāmaṃ etadavocaṃ – ‘ettāvatā no, āvuso kālāma<a name="T1.0444"></a>, imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja pavedesī’ti? ‘ettāvatā kho ahaṃ, āvuso, imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja pavedemī’ti. ‘ahampi kho, āvuso, ettāvatā imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharāmī’ti. ‘lābhā no, āvuso, suladdhaṃ no, āvuso, ye mayaṃ āyasmantaṃ tādisaṃ sabrahmacāriṃ passāma. iti yāhaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja pavedemi taṃ tvaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharasi, yaṃ tvaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharasi tamahaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja pavedemi. iti yāhaṃ dhammaṃ jānāmi taṃ tvaṃ dhammaṃ jānāsi, yaṃ tvaṃ dhammaṃ jānāsi tamahaṃ dhammaṃ jānāmi. iti yādiso ahaṃ tādiso tuvaṃ, yādiso tuvaṃ tādiso ahaṃ. ehi dāni, āvuso, ubhova santā imaṃ gaṇaṃ pariharāmā’ti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3344,'mula_ma_01',305,replace(replace('<p class="bodytext">iti <a name="M1.0305"></a> kho, aggivessana, āḷāro kālāmo ācariyo me samāno (attano) <span class="note">[( ) natthi (sī. pī.)]</span> antevāsiṃ maṃ samānaṃ attanā samasamaṃ ṭhapesi, uḷārāya ca maṃ pūjāya pūjesi. tassa mayhaṃ, aggivessana, etadahosi – ‘nāyaṃ dhammo nibbidāya na virāgāya na nirodhāya na upasamāya na abhiññāya na sambodhāya na nibbānāya saṃvattati, yāvadeva ākiñcaññāyatanūpapattiyā’ti. so kho ahaṃ, aggivessana, taṃ dhammaṃ analaṅkaritvā tasmā dhammā nibbijja apakkamiṃ.</p>\r\n<p class="bodytext"><a name="para372"></a><a name="para372_mn1"></a><span class="paranum">372</span>. so kho ahaṃ, aggivessana, kiṃkusalagavesī anuttaraṃ santivarapadaṃ <a name="T1.0445"></a> pariyesamāno <a name="V1.0308"></a> yena udako rāmaputto tenupasaṅkamiṃ, upasaṅkamitvā udakaṃ rāmaputtaṃ etadavocaṃ – ‘icchāmahaṃ, āvuso <span class="note">[passa ma. ni. 1.278 pāsarāsisutte]</span> imasmiṃ dhammavinaye brahmacariyaṃ caritu’nti. evaṃ vutte, aggivessana, udako rāmaputto maṃ etadavoca – ‘viharatāyasmā, tādiso ayaṃ dhammo yattha viññū puriso nacirasseva sakaṃ ācariyakaṃ sayaṃ abhiññā sacchikatvā upasampajja vihareyyā’ti. so kho ahaṃ, aggivessana, nacirasseva khippameva taṃ dhammaṃ pariyāpuṇiṃ. so kho ahaṃ, aggivessana, tāvatakeneva oṭṭhapahatamattena lapitalāpanamattena ñāṇavādañca vadāmi theravādañca, ‘jānāmi passāmī’ti ca paṭijānāmi, ahañceva aññe ca. tassa mayhaṃ, aggivessana, etadahosi – ‘na kho rāmo imaṃ dhammaṃ kevalaṃ saddhāmattakena sayaṃ abhiññā sacchikatvā upasampajja viharāmīti pavedesi. addhā rāmo imaṃ dhammaṃ jānaṃ passaṃ vihāsī’ti. atha khvāhaṃ, aggivessana, yena udako rāmaputto tenupasaṅkamiṃ, upasaṅkamitvā udakaṃ rāmaputtaṃ etadavocaṃ – ‘kittāvatā no, āvuso rāmo, imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharāmīti pavedesī’ti? evaṃ vutte, aggivessana, udako rāmaputto nevasaññānāsaññāyatanaṃ pavedesi. tassa mayhaṃ, aggivessana, etadahosi – ‘na kho rāmasseva ahosi saddhā, mayhaṃpatthi saddhā, na kho rāmasseva ahosi vīriyaṃ, mayhaṃpatthi vīriyaṃ, na kho rāmasseva ahosi sati, mayhaṃpatthi sati, na kho rāmasseva ahosi samādhi, mayhaṃpatthi <a name="T1.0446"></a> samādhi, na kho rāmasseva ahosi paññā, mayhaṃpatthi paññā, yaṃnūnāhaṃ yaṃ dhammaṃ rāmo sayaṃ abhiññā sacchikatvā upasampajja viharāmīti pavedesi tassa dhammassa</p>\r\n','\r',char(13)),'\n',char(10)),'-372-');
INSERT INTO pages VALUES(3345,'mula_ma_01',306,replace(replace('<p class="bodytext">sacchikiriyāya <a name="M1.0306"></a> padaheyya’nti. so kho ahaṃ, aggivessana, nacirasseva khippameva taṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja vihāsiṃ.</p>\r\n<p class="bodytext">atha khvāhaṃ, aggivessana, yena udako rāmaputto tenupasaṅkamiṃ, upasaṅkamitvā udakaṃ rāmaputtaṃ etadavocaṃ – ‘ettāvatā no, āvuso, rāmo imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja pavedesī’ti? ‘ettāvatā kho, āvuso, rāmo imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja pavedesī’ti. ‘ahampi kho, āvuso, ettāvatā imaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharāmī’ti. ‘lābhā no, āvuso, suladdhaṃ no, āvuso, ye mayaṃ āyasmantaṃ tādisaṃ sabrahmacāriṃ passāma. iti yaṃ dhammaṃ rāmo sayaṃ abhiññā sacchikatvā upasampajja pavedesi, taṃ tvaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharasi, yaṃ tvaṃ dhammaṃ sayaṃ abhiññā sacchikatvā upasampajja viharasi, taṃ dhammaṃ rāmo sayaṃ abhiññā sacchikatvā upasampajja pavedesi. iti yaṃ dhammaṃ rāmo abhiññāsi taṃ tvaṃ dhammaṃ jānāsi, yaṃ tvaṃ dhammaṃ jānāsi taṃ dhammaṃ rāmo abhiññāsi. iti <a name="V1.0309"></a> yādiso rāmo ahosi tādiso tuvaṃ, yādiso tuvaṃ tādiso rāmo ahosi. ehi dāni, āvuso, tuvaṃ imaṃ gaṇaṃ pariharā’ti. iti kho, aggivessana, udako <a name="T1.0447"></a> rāmaputto sabrahmacārī me samāno ācariyaṭṭhāne ca maṃ ṭhapesi, uḷārāya ca maṃ pūjāya pūjesi. tassa mayhaṃ, aggivessana, etadahosi – ‘nāyaṃ dhammo nibbidāya na virāgāya na nirodhāya na upasamāya na abhiññāya na sambodhāya na nibbānāya saṃvattati, yāvadeva nevasaññānāsaññāyatanūpapattiyā’ti. so kho ahaṃ, aggivessana, taṃ dhammaṃ analaṅkaritvā tasmā dhammā nibbijja apakkamiṃ.</p>\r\n<p class="bodytext"><a name="para373"></a><a name="para373_mn1"></a><span class="paranum">373</span>. so kho ahaṃ, aggivessana, kiṃkusalagavesī anuttaraṃ santivarapadaṃ pariyesamāno magadhesu anupubbena cārikaṃ caramāno yena uruvelā senānigamo tadavasariṃ. tatthaddasaṃ ramaṇīyaṃ bhūmibhāgaṃ, pāsādikañca vanasaṇḍaṃ, nadiñca sandantiṃ setakaṃ supatitthaṃ ramaṇīyaṃ, samantā ca gocaragāmaṃ. tassa mayhaṃ, aggivessana, etadahosi – ‘ramaṇīyo vata, bho, bhūmibhāgo, pāsādiko ca vanasaṇḍo, nadī ca sandati setakā supatitthā ramaṇīyā, samantā ca gocaragāmo. alaṃ vatidaṃ kulaputtassa</p>\r\n','\r',char(13)),'\n',char(10)),'-373-');
INSERT INTO pages VALUES(3346,'mula_ma_01',307,replace(replace('<p class="bodytext">padhānatthikassa <a name="M1.0307"></a> padhānāyā’ti. so kho ahaṃ, aggivessana, tattheva nisīdiṃ ‘alamidaṃ padhānāyā’ti.</p>\r\n<p class="bodytext"><a name="para374"></a><a name="para374_mn1"></a><span class="paranum">374</span>. apissumaṃ, aggivessana, tisso upamā paṭibhaṃsu anacchariyā pubbe assutapubbā. seyyathāpi, aggivessana, allaṃ kaṭṭhaṃ sasnehaṃ udake nikkhittaṃ. atha puriso āgaccheyya uttarāraṇiṃ ādāya – ‘aggiṃ abhinibbattessāmi, tejo pātukarissāmī’ti. taṃ kiṃ maññasi, aggivessana, api nu so puriso amuṃ allaṃ kaṭṭhaṃ sasnehaṃ, udake nikkhittaṃ<a name="T1.0448"></a>, uttarāraṇiṃ ādāya abhimanthento aggiṃ abhinibbatteyya, tejo pātukareyyā"ti? "no hidaṃ, bho gotama". "taṃ kissa hetu"? "aduñhi, bho gotama, allaṃ kaṭṭhaṃ sasnehaṃ, tañca pana udake <a name="P1.0241"></a> nikkhittaṃ. yāvadeva ca pana so puriso kilamathassa vighātassa bhāgī assā"ti. "evameva kho, aggivessana, ye hi keci samaṇā vā brāhmaṇā vā kāyena ceva cittena ca kāmehi avūpakaṭṭhā viharanti, yo ca nesaṃ kāmesu kāmacchando kāmasneho kāmamucchā kāmapipāsā kāmapariḷāho, so ca ajjhattaṃ na suppahīno hoti, na suppaṭippassaddho, opakkamikā cepi te bhonto samaṇabrāhmaṇā dukkhā tibbā kharā kaṭukā vedanā vedayanti, abhabbāva te ñāṇāya dassanāya anuttarāya sambodhāya. no cepi te bhonto <a name="V1.0310"></a> samaṇabrāhmaṇā opakkamikā dukkhā tibbā kharā kaṭukā vedanā vedayanti, abhabbāva te ñāṇāya dassanāya anuttarāya sambodhāya. ayaṃ kho maṃ, aggivessana, paṭhamā upamā paṭibhāsi anacchariyā pubbe assutapubbā.</p>\r\n<p class="bodytext"><a name="para375"></a><a name="para375_mn1"></a><span class="paranum">375</span>. aparāpi kho maṃ, aggivessana, dutiyā upamā paṭibhāsi anacchariyā pubbe assutapubbā. seyyathāpi, aggivessana, allaṃ kaṭṭhaṃ sasnehaṃ, ārakā udakā thale nikkhittaṃ. atha puriso āgaccheyya uttarāraṇiṃ ādāya – ‘aggiṃ abhinibbattessāmi, tejo pātukarissāmī’ti. taṃ kiṃ maññasi, aggivessana, api nu so puriso amuṃ allaṃ kaṭṭhaṃ sasnehaṃ, ārakā udakā thale nikkhittaṃ, uttarāraṇiṃ ādāya <a name="T1.0449"></a> abhimanthento aggiṃ abhinibbatteyya tejo pātukareyyā"ti? "no hidaṃ, bho gotama". "taṃ kissa hetu"? "aduñhi, bho gotama, allaṃ kaṭṭhaṃ sasnehaṃ, kiñcāpi ārakā udakā thale nikkhittaṃ. yāvadeva ca pana so puriso kilamathassa</p>\r\n','\r',char(13)),'\n',char(10)),'-374-375-');
INSERT INTO pages VALUES(3347,'mula_ma_01',308,replace(replace('<p class="bodytext">vighātassa <a name="M1.0308"></a> bhāgī assāti. evameva kho, aggivessana, ye hi keci samaṇā vā brāhmaṇā vā kāyena ceva cittena ca kāmehi vūpakaṭṭhā viharanti, yo ca nesaṃ kāmesu kāmacchando kāmasneho kāmamucchā kāmapipāsā kāmapariḷāho so ca ajjhattaṃ na suppahīno hoti, na suppaṭippassaddho, opakkamikā cepi te bhonto samaṇabrāhmaṇā dukkhā tibbā kharā kaṭukā vedanā vedayanti, abhabbāva te ñāṇāya dassanāya anuttarāya sambodhāya. no cepi te bhonto samaṇabrāhmaṇā opakkamikā dukkhā tibbā kharā kaṭukā vedanā vedayanti, abhabbāva te ñāṇāya dassanāya anuttarāya sambodhāya. ayaṃ kho maṃ, aggivessana, dutiyā upamā paṭibhāsi anacchariyā pubbe assutapubbā".</p>\r\n<p class="bodytext"><a name="para376"></a><a name="para376_mn1"></a><span class="paranum">376</span>. aparāpi kho maṃ, aggivessana, tatiyā upamā paṭibhāsi anacchariyā <a name="P1.0242"></a> pubbe assutapubbā. seyyathāpi, aggivessana, sukkhaṃ kaṭṭhaṃ koḷāpaṃ, ārakā udakā thale nikkhittaṃ. atha puriso āgaccheyya uttarāraṇiṃ ādāya – ‘aggiṃ abhinibbattessāmi, tejo pātukarissāmī’ti. taṃ kiṃ maññasi, aggivessana, api nu so puriso amuṃ sukkhaṃ kaṭṭhaṃ koḷāpaṃ, ārakā udakā thale nikkhittaṃ, uttarāraṇiṃ ādāya <a name="T1.0450"></a> abhimanthento aggiṃ abhinibbatteyya, tejo pātukareyyā"ti? "evaṃ, bho gotama". "taṃ kissa hetu"? "aduñhi, bho gotama, sukkhaṃ kaṭṭhaṃ koḷāpaṃ, tañca pana ārakā udakā thale nikkhitta"nti<a name="V1.0311"></a>. "evameva kho, aggivessana, ye hi keci samaṇā vā brāhmaṇā vā kāyena ceva cittena ca kāmehi vūpakaṭṭhā viharanti, yo ca nesaṃ kāmesu kāmacchando kāmasneho kāmamucchā kāmapipāsā kāmapariḷāho, so ca ajjhattaṃ suppahīno hoti suppaṭippassaddho, opakkamikā cepi te bhonto samaṇabrāhmaṇā dukkhā tibbā kharā kaṭukā vedanā vedayanti, bhabbāva te ñāṇāya dassanāya anuttarāya sambodhāya. no cepi te bhonto samaṇabrāhmaṇā opakkamikā dukkhā tibbā kharā kaṭukā vedanā vedayanti, bhabbāva te ñāṇāya dassanāya anuttarāya sambodhāya. ayaṃ kho maṃ, aggivessana, tatiyā upamā paṭibhāsi anacchariyā pubbe assutapubbā. imā kho maṃ, aggivessana, tisso upamā paṭibhaṃsu anacchariyā pubbe assutapubbā."</p>\r\n','\r',char(13)),'\n',char(10)),'-376-');
INSERT INTO pages VALUES(3348,'mula_ma_01',309,replace(replace('<p class="bodytext"><a name="para377"></a><a name="para377_mn1"></a><span class="paranum">377</span>. tassa <a name="M1.0309"></a> mayhaṃ, aggivessana, etadahosi – ‘yaṃnūnāhaṃ dantebhi dantamādhāya <span class="note">[passa ma. ni. 1.221 vitakkasaṇṭhānasutte]</span>, jivhāya tāluṃ āhacca, cetasā cittaṃ abhiniggaṇheyyaṃ abhinippīḷeyyaṃ abhisantāpeyya’nti. so kho ahaṃ, aggivessana, dantebhi dantamādhāya, jivhāya tāluṃ āhacca, cetasā cittaṃ abhiniggaṇhāmi abhinippīḷemi abhisantāpemi. tassa mayhaṃ, aggivessana, dantebhi dantamādhāya jivhāya tāluṃ āhacca cetasā cittaṃ abhiniggaṇhato abhinippīḷayato <a name="T1.0451"></a> abhisantāpayato kacchehi sedā muccanti. seyyathāpi, aggivessana, balavā puriso dubbalataraṃ purisaṃ sīse vā gahetvā khandhe vā gahetvā abhiniggaṇheyya abhinippīḷeyya abhisantāpeyya, evameva kho me, aggivessana, dantebhi dantamādhāya, jivhāya tāluṃ āhacca, cetasā cittaṃ abhiniggaṇhato abhinippīḷayato abhisantāpayato kacchehi sedā muccanti. āraddhaṃ kho pana me, aggivessana, vīriyaṃ hoti asallīnaṃ, upaṭṭhitā sati asammuṭṭhā, sāraddho ca pana me kāyo <a name="P1.0243"></a> hoti appaṭippassaddho teneva dukkhappadhānena padhānābhitunnassa sato. evarūpāpi kho me, aggivessana, uppannā dukkhā vedanā cittaṃ na pariyādāya tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para378"></a><a name="para378_mn1"></a><span class="paranum">378</span>. tassa mayhaṃ, aggivessana, etadahosi – ‘yaṃnūnāhaṃ appāṇakaṃyeva jhānaṃ jhāyeyya’nti. so kho ahaṃ, aggivessana, mukhato ca nāsato ca assāsapassāse uparundhiṃ. tassa mayhaṃ, aggivessana, mukhato ca nāsato ca assāsapassāsesu uparuddhesu kaṇṇasotehi vātānaṃ nikkhamantānaṃ adhimatto saddo hoti. seyyathāpi nāma kammāragaggariyā dhamamānāya adhimatto saddo hoti, evameva kho me, aggivessana, mukhato ca nāsato ca assāsapassāsesu <a name="V1.0312"></a> uparuddhesu kaṇṇasotehi vātānaṃ nikkhamantānaṃ adhimatto saddo hoti. āraddhaṃ kho pana me, aggivessana, vīriyaṃ hoti asallīnaṃ upaṭṭhitā sati asammuṭṭhā. sāraddho ca pana me <a name="T1.0452"></a> kāyo hoti appaṭippassaddho teneva dukkhappadhānena padhānābhitunnassa sato. evarūpāpi kho me, aggivessana, uppannā dukkhā vedanā cittaṃ na pariyādāya tiṭṭhati.</p>\r\n<p class="bodytext">tassa mayhaṃ, aggivessana, etadahosi – ‘yaṃnūnāhaṃ appāṇakaṃyeva jhānaṃ jhāyeyya’nti. so kho ahaṃ, aggivessana, mukhato ca nāsato ca</p>\r\n','\r',char(13)),'\n',char(10)),'-377-378-');
INSERT INTO pages VALUES(3349,'mula_ma_01',310,replace(replace('<p class="bodytext">kaṇṇato <a name="M1.0310"></a> ca assāsapassāse uparundhiṃ. tassa mayhaṃ, aggivessana, mukhato ca nāsato ca kaṇṇato ca assāsapassāsesu uparuddhesu adhimattā vātā muddhani ūhananti <span class="note">[ūhanti (sī.), ohananti (syā. kaṃ.), uhananti (ka.)]</span>. seyyathāpi, aggivessana, balavā puriso tiṇhena sikharena muddhani abhimattheyya <span class="note">[muddhānaṃ abhimantheyya (sī. pī.), muddhānaṃ abhimattheyya (syā. kaṃ.)]</span>, evameva kho me, aggivessana, mukhato ca nāsato ca kaṇṇato ca assāsapassāsesu uparuddhesu adhimattā vātā muddhani ūhananti. āraddhaṃ kho pana me, aggivessana, vīriyaṃ hoti asallīnaṃ upaṭṭhitā sati asammuṭṭhā. sāraddho ca pana me kāyo hoti appaṭippassaddho teneva dukkhappadhānena padhānābhitunnassa sato. evarūpāpi kho me, aggivessana, uppannā dukkhā vedanā cittaṃ na pariyādāya tiṭṭhati.</p>\r\n<p class="bodytext">tassa mayhaṃ, aggivessana, etadahosi – ‘yaṃnūnāhaṃ appāṇakaṃyeva jhānaṃ jhāyeyya’nti. so kho ahaṃ, aggivessana, mukhato ca nāsato ca kaṇṇato ca assāsapassāse uparundhiṃ. tassa mayhaṃ, aggivessana, mukhato ca nāsato ca kaṇṇato ca assāsapassāsesu <a name="T1.0453"></a> uparuddhesu adhimattā sīse sīsavedanā honti. seyyathāpi, aggivessana, balavā puriso daḷhena <a name="P1.0244"></a> varattakkhaṇḍena <span class="note">[varattakabandhanena (sī.)]</span> sīse sīsaveṭhaṃ dadeyya, evameva kho me, aggivessana, mukhato ca nāsato ca kaṇṇato ca assāsapassāsesu uparuddhesu adhimattā sīse sīsavedanā honti. āraddhaṃ kho pana me, aggivessana, vīriyaṃ hoti asallīnaṃ upaṭṭhitā sati asammuṭṭhā. sāraddho ca pana me kāyo hoti appaṭippassaddho teneva dukkhappadhānena padhānābhitunnassa sato. evarūpāpi kho me, aggivessana, uppannā dukkhā vedanā cittaṃ na pariyādāya tiṭṭhati.</p>\r\n<p class="bodytext">tassa mayhaṃ, aggivessana, etadahosi – ‘yaṃnūnāhaṃ appāṇakaṃyeva jhānaṃ jhāyeyya’nti. so kho ahaṃ, aggivessana, mukhato ca nāsato ca kaṇṇato ca assāsapassāse uparundhiṃ. tassa mayhaṃ, aggivessana, mukhato ca nāsato ca kaṇṇato ca assāsapassāsesu uparuddhesu adhimattā <a name="V1.0313"></a> vātā kucchiṃ parikantanti. seyyathāpi, aggivessana, dakkho goghātako vā goghātakantevāsī vā</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3350,'mula_ma_01',311,replace(replace('<p class="bodytext">tiṇhena <a name="M1.0311"></a> govikantanena kucchiṃ parikanteyya, evameva kho me, aggivessana, mukhato ca nāsato ca kaṇṇato ca assāsapassāsesu uparuddhesu adhimattā vātā kucchiṃ parikantanti. āraddhaṃ kho pana me, aggivessana, vīriyaṃ hoti asallīnaṃ upaṭṭhitā sati asammuṭṭhā. sāraddho ca pana me kāyo hoti appaṭippassaddho teneva dukkhappadhānena <a name="T1.0454"></a> padhānābhitunnassa sato. evarūpāpi kho me, aggivessana, uppannā dukkhā vedanā cittaṃ na pariyādāya tiṭṭhati.</p>\r\n<p class="bodytext">tassa mayhaṃ, aggivessana, etadahosi – ‘yaṃnūnāhaṃ appāṇakaṃyeva jhānaṃ jhāyeyya’nti. so kho ahaṃ, aggivessana, mukhato ca nāsato ca kaṇṇato ca assāsapassāse uparundhiṃ. tassa mayhaṃ, aggivessana, mukhato ca nāsato ca kaṇṇato ca assāsapassāsesu uparuddhesu adhimatto kāyasmiṃ ḍāho hoti. seyyathāpi, aggivessana, dve balavanto purisā dubbalataraṃ purisaṃ nānābāhāsu gahetvā aṅgārakāsuyā santāpeyyuṃ samparitāpeyyuṃ, evameva kho me, aggivessana, mukhato ca nāsato ca kaṇṇato ca assāsapassāsesu uparuddhesu adhimatto kāyasmiṃ ḍāho hoti. āraddhaṃ kho pana me, aggivessana, vīriyaṃ hoti asallīnaṃ upaṭṭhitā sati asammuṭṭhā. sāraddho ca pana me kāyo hoti appaṭippassaddho teneva dukkhappadhānena padhānābhitunnassa sato. evarūpāpi kho me, aggivessana, uppannā dukkhā vedanā cittaṃ na pariyādāya tiṭṭhati. apissu maṃ, aggivessana, devatā <a name="P1.0245"></a> disvā evamāhaṃsu – ‘kālaṅkato samaṇo gotamo’ti. ekaccā devatā evamāhaṃsu – ‘na kālaṅkato samaṇo gotamo, api ca kālaṅkarotī’ti. ekaccā devatā evamāhaṃsu – ‘na kālaṅkato samaṇo gotamo, napi kālaṅkaroti, arahaṃ samaṇo gotamo, vihārotveva so <span class="note">[vihārotveveso (sī.)]</span> arahato evarūpo hotī’ti <span class="note">[vihārotveveso arahato"ti (?)]</span>.</p>\r\n<p class="bodytext"><a name="para379"></a><a name="para379_mn1"></a><span class="paranum">379</span>. tassa <a name="T1.0455"></a> mayhaṃ, aggivessana, etadahosi – ‘yaṃnūnāhaṃ sabbaso āhārupacchedāya paṭipajjeyya’nti. atha kho maṃ, aggivessana, devatā</p>\r\n','\r',char(13)),'\n',char(10)),'-379-');
INSERT INTO pages VALUES(3351,'mula_ma_01',312,replace(replace('<p class="bodytext">upasaṅkamitvā <a name="M1.0312"></a> etadavocuṃ – ‘mā kho tvaṃ, mārisa, sabbaso āhārupacchedāya paṭipajji. sace kho tvaṃ, mārisa, sabbaso āhārupacchedāya paṭipajjissasi, tassa te mayaṃ dibbaṃ ojaṃ lomakūpehi ajjhohāressāma <span class="note">[ajjhoharissāma (syā. kaṃ. pī. ka.)]</span>, tāya tvaṃ yāpessasī’ti. tassa mayhaṃ, aggivessana, etadahosi – ‘ahañceva kho pana sabbaso ajajjitaṃ <span class="note">[ajaddhukaṃ (sī. pī.), jaddhukaṃ (syā. kaṃ.)]</span> paṭijāneyyaṃ, imā ca me devatā dibbaṃ ojaṃ lomakūpehi ajjhohāreyyuṃ <span class="note">[ajjhohareyyuṃ (syā. kaṃ. pī. ka.)]</span>, tāya <a name="V1.0314"></a> cāhaṃ yāpeyyaṃ, taṃ mamassa musā’ti. so kho ahaṃ, aggivessana, tā devatā paccācikkhāmi, ‘hala’nti vadāmi.</p>\r\n<p class="bodytext"><a name="para380"></a><a name="para380_mn1"></a><span class="paranum">380</span>. tassa mayhaṃ, aggivessana, etadahosi – ‘yaṃnūnāhaṃ thokaṃ thokaṃ āhāraṃ āhāreyyaṃ, pasataṃ pasataṃ, yadi vā muggayūsaṃ, yadi vā kulatthayūsaṃ, yadi vā kaḷāyayūsaṃ, yadi vā hareṇukayūsa’nti. so kho ahaṃ, aggivessana, thokaṃ thokaṃ āhāraṃ āhāresiṃ, pasataṃ pasataṃ, yadi vā muggayūsaṃ, yadi vā kulatthayūsaṃ, yadi vā kaḷāyayūsaṃ, yadi vā hareṇukayūsaṃ. tassa mayhaṃ, aggivessana, thokaṃ thokaṃ āhāraṃ āhārayato, pasataṃ pasataṃ, yadi vā muggayūsaṃ, yadi vā kulatthayūsaṃ, yadi vā kaḷāyayūsaṃ, yadi vā hareṇukayūsaṃ, adhimattakasimānaṃ patto kāyo hoti. seyyathāpi nāma āsītikapabbāni vā kāḷapabbāni vā, evamevassu <a name="T1.0456"></a> me aṅgapaccaṅgāni bhavanti tāyevappāhāratāya. seyyathāpi nāma oṭṭhapadaṃ, evamevassu me ānisadaṃ hoti tāyevappāhāratāya. seyyathāpi nāma vaṭṭanāvaḷī, evamevassu me piṭṭhikaṇṭako uṇṇatāvanato hoti tāyevappāhāratāya. seyyathāpi nāma jarasālāya gopāṇasiyo oluggaviluggā bhavanti, evamevassu me phāsuḷiyo oluggaviluggā bhavanti tāyevappāhāratāya. seyyathāpi nāma gambhīre udapāne udakatārakā gambhīragatā okkhāyikā dissanti, evamevassu me akkhikūpesu akkhitārakā gambhīragatā okkhāyikā dissanti tāyevappāhāratāya. seyyathāpi nāma tittakālābu <a name="P1.0246"></a> āmakacchinno vātātapena saṃphuṭito hoti sammilāto, evamevassu me sīsacchavi saṃphuṭitā hoti</p>\r\n','\r',char(13)),'\n',char(10)),'-380-');
INSERT INTO pages VALUES(3352,'mula_ma_01',313,replace(replace('<p class="bodytext">sammilātā <a name="M1.0313"></a> tāyevappāhāratāya.</p>\r\n<p class="bodytext">so kho ahaṃ, aggivessana, udaracchaviṃ parimasissāmīti piṭṭhikaṇṭakaṃyeva pariggaṇhāmi, piṭṭhikaṇṭakaṃ parimasissāmīti udaracchaviṃyeva pariggaṇhāmi, yāvassu me, aggivessana, udaracchavi piṭṭhikaṇṭakaṃ allīnā hoti tāyevappāhāratāya. so kho ahaṃ, aggivessana, vaccaṃ vā muttaṃ vā karissāmīti tattheva avakujjo papatāmi tāyevappāhāratāya. so kho ahaṃ, aggivessana, imameva kāyaṃ assāsento pāṇinā gattāni anumajjāmi. tassa mayhaṃ, aggivessana, pāṇinā gattāni anumajjato pūtimūlāni lomāni kāyasmā papatanti tāyevappāhāratāya. apissu maṃ, aggivessana, manussā <a name="T1.0457"></a> disvā evamāhaṃsu – ‘kāḷo samaṇo gotamo’ti. ekacce manussā evamāhaṃsu – ‘na kāḷo samaṇo gotamo, sāmo samaṇo gotamo’ti. ekacce manussā evamāhaṃsu – ‘na kāḷo samaṇo gotamo<a name="V1.0315"></a>, napi sāmo, maṅguracchavi samaṇo gotamo’ti. yāvassu me, aggivessana, tāva parisuddho chavivaṇṇo pariyodāto upahato hoti tāyevappāhāratāya.</p>\r\n<p class="bodytext"><a name="para381"></a><a name="para381_mn1"></a><span class="paranum">381</span>. tassa mayhaṃ, aggivessana, etadahosi – ‘ye kho keci atītamaddhānaṃ samaṇā vā brāhmaṇā vā opakkamikā dukkhā tibbā kharā kaṭukā vedanā vedayiṃsu, etāvaparamaṃ, nayito bhiyyo. yepi hi keci anāgatamaddhānaṃ samaṇā vā brāhmaṇā vā opakkamikā dukkhā tibbā kharā kaṭukā vedanā vedayissanti, etāvaparamaṃ, nayito bhiyyo. yepi hi keci etarahi samaṇā vā brāhmaṇā vā opakkamikā dukkhā tibbā kharā kaṭukā vedanā vedayanti, etāvaparamaṃ, nayito bhiyyo. na kho panāhaṃ imāya kaṭukāya dukkarakārikāya adhigacchāmi uttari manussadhammā alamariyañāṇadassanavisesaṃ. siyā nu kho añño maggo bodhāyā’ti? tassa mayhaṃ, aggivessana, etadahosi – ‘abhijānāmi kho panāhaṃ pitu sakkassa kammante sītāya jambucchāyāya nisinno vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharitā. siyā nu kho eso maggo bodhāyā’ti? tassa mayhaṃ, aggivessana, satānusāri <a name="T1.0458"></a> viññāṇaṃ ahosi – ‘eseva maggo bodhāyā’ti. tassa mayhaṃ, aggivessana, etadahosi – ‘kiṃ nu kho ahaṃ <a name="P1.0247"></a> tassa sukhassa bhāyāmi, yaṃ taṃ sukhaṃ aññatreva kāmehi</p>\r\n','\r',char(13)),'\n',char(10)),'-381-');
INSERT INTO pages VALUES(3353,'mula_ma_01',314,replace(replace('<p class="bodytext">aññatra <a name="M1.0314"></a> akusalehi dhammehī’ti? tassa mayhaṃ, aggivessana, etadahosi – ‘na kho ahaṃ tassa sukhassa bhāyāmi, yaṃ taṃ sukhaṃ aññatreva kāmehi aññatra akusalehi dhammehī’ti.</p>\r\n<p class="bodytext"><a name="para382"></a><a name="para382_mn1"></a><span class="paranum">382</span>. tassa mayhaṃ, aggivessana, etadahosi – ‘na kho taṃ sukaraṃ sukhaṃ adhigantuṃ evaṃ adhimattakasimānaṃ pattakāyena, yaṃnūnāhaṃ oḷārikaṃ āhāraṃ āhāreyyaṃ odanakummāsa’nti. so kho ahaṃ, aggivessana, oḷārikaṃ āhāraṃ āhāresiṃ odanakummāsaṃ. tena kho pana maṃ, aggivessana, samayena pañca <span class="note">[pañcavaggiyā (aññasuttesu)]</span> bhikkhū paccupaṭṭhitā honti – ‘yaṃ kho samaṇo gotamo dhammaṃ adhigamissati, taṃ no ārocessatī’ti. yato kho ahaṃ, aggivessana, oḷārikaṃ āhāraṃ āhāresiṃ odanakummāsaṃ, atha me te pañca bhikkhū nibbijja pakkamiṃsu – ‘bāhulliko <span class="note">[bāhuliko (sī. pī.) saṃghabhedasikkhāpadaṭīkāya sameti]</span> samaṇo gotamo, padhānavibbhanto, āvatto bāhullāyā’ti.</p>\r\n<p class="bodytext"><a name="para383"></a><a name="para383_mn1"></a><span class="paranum">383</span>. so <a name="V1.0316"></a> kho ahaṃ, aggivessana, oḷārikaṃ āhāraṃ āhāretvā, balaṃ gahetvā, vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja vihāsiṃ. evarūpāpi kho me, aggivessana, uppannā sukhā vedanā cittaṃ na pariyādāya tiṭṭhati. vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja vihāsiṃ. evarūpāpi kho me, aggivessana, uppannā sukhā vedanā cittaṃ na pariyādāya tiṭṭhati. pītiyā ca virāgā upekkhako ca vihāsiṃ, sato ca sampajāno. sukhañca kāyena paṭisaṃvedesiṃ yaṃ taṃ ariyā ācikkhanti – ‘upekkhako satimā sukhavihārī’ti tatiyaṃ jhānaṃ <a name="T1.0459"></a> upasampajja vihāsiṃ. evarūpāpi kho me, aggivessana, uppannā sukhā vedanā cittaṃ na pariyādāya tiṭṭhati. sukhassa ca pahānā dukkhassa ca pahānā, pubbeva somanassadomanassānaṃ atthaṅgamā, adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja vihāsiṃ. evarūpāpi kho me, aggivessana, uppannā sukhā vedanā cittaṃ na pariyādāya tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para384"></a><a name="para384_mn1"></a><span class="paranum">384</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte pubbenivāsānussatiñāṇāya <a name="P1.0248"></a> cittaṃ abhininnāmesiṃ. so anekavihitaṃ pubbenivāsaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-382-383-384-');
INSERT INTO pages VALUES(3354,'mula_ma_01',315,replace(replace('<p class="bodytext">anussarāmi<a name="M1.0315"></a>, seyyathidaṃ – ekampi jātiṃ…pe… iti sākāraṃ sauddesaṃ anekavihitaṃ pubbenivāsaṃ anussarāmi. ayaṃ kho me, aggivessana, rattiyā paṭhame yāme paṭhamā vijjā adhigatā, avijjā vihatā, vijjā uppannā, tamo vihato, āloko uppanno, yathā taṃ appamattassa ātāpino pahitattassa viharato. evarūpāpi kho me, aggivessana, uppannā sukhā vedanā cittaṃ na pariyādāya tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para385"></a><a name="para385_mn1"></a><span class="paranum">385</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte sattānaṃ cutūpapātañāṇāya cittaṃ abhininnāmesiṃ. so dibbena cakkhunā visuddhena atikkantamānusakena satte passāmi cavamāne upapajjamāne hīne paṇīte suvaṇṇe dubbaṇṇe sugate duggate yathākammūpage satte pajānāmi…pe… ayaṃ kho me, aggivessana, rattiyā majjhime yāme dutiyā vijjā adhigatā, avijjā vihatā, vijjā uppannā, tamo <a name="P1.0249"></a> vihato, āloko uppanno, yathā taṃ appamattassa ātāpino pahitattassa viharato<a name="V1.0317"></a>. evarūpāpi kho me, aggivessana<a name="T1.0460"></a>, uppannā sukhā vedanā cittaṃ na pariyādāya tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para386"></a><a name="para386_mn1"></a><span class="paranum">386</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte āsavānaṃ khayañāṇāya cittaṃ abhininnāmesiṃ. so ‘idaṃ dukkha’nti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ dukkhasamudayo’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ dukkhanirodho’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ dukkhanirodhagāminī paṭipadā’ti yathābhūtaṃ abbhaññāsiṃ. ‘ime āsavā’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ āsavasamudayo’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ āsavanirodho’ti yathābhūtaṃ abbhaññāsiṃ, ‘ayaṃ āsavanirodhagāminī paṭipadā’ti yathābhūtaṃ abbhaññāsiṃ. tassa me evaṃ jānato evaṃ passato kāmāsavāpi cittaṃ vimuccittha, bhavāsavāpi cittaṃ vimuccittha, avijjāsavāpi cittaṃ vimuccittha. vimuttasmiṃ vimuttamiti ñāṇaṃ ahosi. ‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti abbhaññāsiṃ. ayaṃ kho me, aggivessana, rattiyā pacchime yāme tatiyā vijjā adhigatā, avijjā vihatā, vijjā uppannā, tamo vihato, āloko uppanno,</p>\r\n','\r',char(13)),'\n',char(10)),'-385-386-');
INSERT INTO pages VALUES(3355,'mula_ma_01',316,replace(replace('<p class="bodytext">yathā <a name="M1.0316"></a> taṃ appamattassa ātāpino pahitattassa viharato. evarūpāpi kho me, aggivessana, uppannā sukhā vedanā cittaṃ na pariyādāya tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para387"></a><a name="para387_mn1"></a><span class="paranum">387</span>. abhijānāmi kho panāhaṃ, aggivessana, anekasatāya parisāya dhammaṃ desetā. apissu maṃ ekameko evaṃ maññati – ‘mamevārabbha samaṇo gotamo dhammaṃ desetī’ti. ‘na kho panetaṃ, aggivessana, evaṃ daṭṭhabbaṃ, yāvadeva viññāpanatthāya tathāgato paresaṃ dhammaṃ <a name="T1.0461"></a> deseti. so kho ahaṃ, aggivessana, tassāyeva kathāya pariyosāne, tasmiṃyeva purimasmiṃ samādhinimitte ajjhattameva cittaṃ saṇṭhapemi sannisādemi ekodiṃ karomi samādahāmi, yena sudaṃ niccakappaṃ viharāmī"’ti.</p>\r\n<p class="bodytext">okappaniyametaṃ bhoto gotamassa yathā taṃ arahato sammāsambuddhassa. abhijānāti kho pana bhavaṃ gotamo divā supitā"ti? "abhijānāmahaṃ, aggivessana, gimhānaṃ pacchime māse pacchābhattaṃ piṇḍapātapaṭikkanto catugguṇaṃ saṅghāṭiṃ paññapetvā dakkhiṇena passena sato sampajāno niddaṃ okkamitā"ti. "etaṃ kho, bho gotama, eke samaṇabrāhmaṇā sammohavihārasmiṃ <a name="V1.0318"></a> vadantī"ti<a name="P1.0250"></a>? "na kho, aggivessana, ettāvatā sammūḷho vā hoti asammūḷho vā. api ca, aggivessana, yathā sammūḷho ca hoti asammūḷho ca, taṃ suṇāhi, sādhukaṃ manasi karohi, bhāsissāmī"ti. "evaṃ, bho"ti kho saccako nigaṇṭhaputto bhagavato paccassosi. bhagavā etadavoca –</p>\r\n<p class="bodytext"><a name="para388"></a><a name="para388_mn1"></a><span class="paranum">388</span>. yassa kassaci, aggivessana, ye āsavā saṃkilesikā ponobbhavikā sadarā dukkhavipākā āyatiṃ jātijarāmaraṇiyā appahīnā, tamahaṃ ‘sammūḷho’ti vadāmi. āsavānañhi, aggivessana, appahānā sammūḷho hoti. yassa kassaci, aggivessana, ye āsavā saṃkilesikā ponobbhavikā sadarā dukkhavipākā āyatiṃ jātijarāmaraṇiyā pahīnā, tamahaṃ ‘asammūḷho’ti vadāmi. āsavānañhi, aggivessana, pahānā asammūḷho hoti.</p>\r\n<p class="bodytext">tathāgatassa <a name="T1.0462"></a> kho, aggivessana, ye āsavā saṃkilesikā ponobbhavikā sadarā dukkhavipākā āyatiṃ jātijarāmaraṇiyā pahīnā ucchinnamūlā tālāvatthukatā anabhāvaṃkatā āyatiṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-387-388-');
INSERT INTO pages VALUES(3356,'mula_ma_01',317,replace(replace('<p class="bodytext">anuppādadhammā<a name="M1.0317"></a>. seyyathāpi, aggivessana, tālo matthakacchinno abhabbo puna virūḷhiyā, evameva kho, aggivessana, tathāgatassa ye āsavā saṃkilesikā ponobbhavikā sadarā dukkhavipākā āyatiṃ jātijarāmaraṇiyā pahīnā ucchinnamūlā tālāvatthukatā anabhāvaṃkatā āyatiṃ anuppādadhammā"ti.</p>\r\n<p class="bodytext"><a name="para389"></a><a name="para389_mn1"></a><span class="paranum">389</span>. evaṃ vutte, saccako nigaṇṭhaputto bhagavantaṃ etadavoca – "acchariyaṃ, bho gotama, abbhutaṃ, bho gotama! yāvañcidaṃ bhoto gotamassa evaṃ āsajja āsajja vuccamānassa, upanītehi vacanappathehi samudācariyamānassa, chavivaṇṇo ceva pariyodāyati, mukhavaṇṇo ca vippasīdati, yathā taṃ arahato sammāsambuddhassa. abhijānāmahaṃ, bho gotama, pūraṇaṃ kassapaṃ vādena vādaṃ samārabhitā. sopi mayā vādena vādaṃ samāraddho aññenaññaṃ paṭicari, bahiddhā kathaṃ apanāmesi, kopañca dosañca appaccayañca pātvākāsi. bhoto pana <span class="note">[bhoto kho pana (sī.)]</span> gotamassa evaṃ āsajja āsajja vuccamānassa, upanītehi vacanappathehi samudācariyamānassa, chavivaṇṇo ceva pariyodāyati, mukhavaṇṇo ca vippasīdati, yathā taṃ arahato sammāsambuddhassa. abhijānāmahaṃ, bho gotama, makkhaliṃ gosālaṃ…pe… ajitaṃ kesakambalaṃ… pakudhaṃ kaccāyanaṃ… sañjayaṃ belaṭṭhaputtaṃ… nigaṇṭhaṃ nāṭaputtaṃ vādena vādaṃ samārabhitā<a name="V1.0319"></a>. sopi mayā <a name="T1.0463"></a> vādena vādaṃ samāraddho aññenaññaṃ <a name="P1.0251"></a> paṭicari, bahiddhā kathaṃ apanāmesi, kopañca dosañca appaccayañca pātvākāsi. bhoto pana gotamassa evaṃ āsajja āsajja vuccamānassa, upanītehi vacanappathehi samudācariyamānassa, chavivaṇṇo ceva pariyodāyati, mukhavaṇṇo ca vippasīdati, yathā taṃ arahato sammāsambuddhassa. handa ca dāni mayaṃ, bho gotama, gacchāma. bahukiccā mayaṃ, bahukaraṇīyā"ti. "yassadāni tvaṃ, aggivessana, kālaṃ maññasī"ti.</p>\r\n<p class="bodytext">atha kho saccako nigaṇṭhaputto bhagavato bhāsitaṃ abhinanditvā anumoditvā uṭṭhāyāsanā pakkāmīti.</p>\r\n<p class="centered">mahāsaccakasuttaṃ niṭṭhitaṃ chaṭṭhaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-389-');
INSERT INTO pages VALUES(3357,'mula_ma_01',318,replace(replace('<p class="subhead">7. cūḷataṇhāsaṅkhayasuttaṃ</p>\r\n<p class="bodytext"><a name="para390"></a><a name="para390_mn1"></a><span class="paranum">390</span>. evaṃ <a name="M1.0318"></a><a name="V1.0320"></a><a name="T1.0464"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati pubbārāme migāramātupāsāde. atha kho sakko devānamindo yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ aṭṭhāsi. ekamantaṃ ṭhito kho sakko devānamindo bhagavantaṃ etadavoca – "kittāvatā nu kho, bhante, bhikkhu saṃkhittena taṇhāsaṅkhayavimutto hoti accantaniṭṭho accantayogakkhemī accantabrahmacārī accantapariyosāno seṭṭho devamanussāna"nti?</p>\r\n<p class="bodytext">idha, devānaminda, bhikkhuno sutaṃ hoti – ‘sabbe dhammā nālaṃ abhinivesāyā’ti. evañcetaṃ, devānaminda, bhikkhuno sutaṃ hoti – ‘sabbe dhammā nālaṃ abhinivesāyā’ti. so sabbaṃ dhammaṃ abhijānāti, sabbaṃ dhammaṃ abhiññāya sabbaṃ dhammaṃ parijānāti, sabbaṃ dhammaṃ pariññāya yaṃ kiñci vedanaṃ vedeti – sukhaṃ vā dukkhaṃ vā adukkhamasukhaṃ vā, so tāsu vedanāsu aniccānupassī viharati, virāgānupassī viharati, nirodhānupassī viharati, paṭinissaggānupassī viharati. so tāsu vedanāsu aniccānupassī viharanto, virāgānupassī viharanto, nirodhānupassī viharanto, paṭinissaggānupassī viharanto na kiñci loke upādiyati. anupādiyaṃ na paritassati, aparitassaṃ paccattaññeva parinibbāyati <a name="P1.0252"></a> – ‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti <a name="T1.0465"></a> pajānāti. ettāvatā kho, devānaminda, bhikkhu saṃkhittena taṇhāsaṅkhayavimutto hoti accantaniṭṭho accantayogakkhemī accantabrahmacārī accantapariyosāno seṭṭho devamanussāna"nti.</p>\r\n<p class="bodytext">atha kho sakko devānamindo bhagavato bhāsitaṃ abhinanditvā anumoditvā bhagavantaṃ abhivādetvā padakkhiṇaṃ katvā tatthevantaradhāyi.</p>\r\n<p class="bodytext"><a name="para391"></a><a name="para391_mn1"></a><span class="paranum">391</span>. tena kho pana samayena āyasmā mahāmoggallāno bhagavato avidūre nisinno hoti. atha kho āyasmato mahāmoggallānassa etadahosi – "kiṃ nu kho so yakkho bhagavato <a name="V1.0321"></a> bhāsitaṃ abhisamecca anumodi udāhu no, yaṃnūnāhaṃ taṃ yakkhaṃ jāneyyaṃ – yadi vā so yakkho bhagavato bhāsitaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-390-391-');
INSERT INTO pages VALUES(3358,'mula_ma_01',319,replace(replace('<p class="bodytext">abhisamecca <a name="M1.0319"></a> anumodi yadi vā no"ti? atha kho āyasmā mahāmoggallāno – seyyathāpi nāma balavā puriso samiñjitaṃ vā bāhaṃ pasāreyya, pasāritaṃ vā bāhaṃ samiñjeyya, evameva – pubbārāme migāramātupāsāde antarahito devesu tāvatiṃsesu pāturahosi. tena kho pana samayena sakko devānamindo ekapuṇḍarīke uyyāne dibbehi pañcahi tūriyasatehi <span class="note">[turiyasatehi (sī. syā. kaṃ. pī.)]</span> samappito samaṅgībhūto paricāreti. addasā kho sakko devānamindo āyasmantaṃ mahāmoggallānaṃ dūratova āgacchantaṃ. disvāna tāni dibbāni pañca tūriyasatāni paṭippaṇāmetvā yenāyasmā mahāmoggallāno tenupasaṅkami, upasaṅkamitvā āyasmantaṃ mahāmoggallānaṃ <a name="T1.0466"></a> etadavoca – "ehi kho, mārisa moggallāna, svāgataṃ, mārisa moggallāna! cirassaṃ kho, mārisa moggallāna, imaṃ pariyāyaṃ akāsi yadidaṃ idhāgamanāya. nisīda, mārisa moggallāna, idamāsanaṃ paññatta"nti. nisīdi kho āyasmā mahāmoggallāno paññatte āsane. sakkopi kho devānamindo aññataraṃ nīcaṃ āsanaṃ gahetvā ekamantaṃ nisīdi. ekamantaṃ nisinnaṃ kho sakkaṃ devānamindaṃ āyasmā mahāmoggallāno etadavoca – "yathā kathaṃ pana kho, kosiya, bhagavā saṃkhittena taṇhāsaṅkhayavimuttiṃ abhāsi? sādhu mayampi etissā kathāya bhāgino assāma savanāyā"ti.</p>\r\n<p class="bodytext"><a name="para392"></a><a name="para392_mn1"></a><span class="paranum">392</span>. mayaṃ kho, mārisa moggallāna, bahukiccā bahukaraṇīyā – appeva sakena karaṇīyena, api ca devānaṃyeva tāvatiṃsānaṃ karaṇīyena. api ca, mārisa moggallāna, sussutaṃyeva hoti suggahitaṃ sumanasikataṃ <a name="P1.0253"></a> sūpadhāritaṃ, yaṃ no khippameva antaradhāyati. bhūtapubbaṃ, mārisa moggallāna, devāsurasaṅgāmo samupabyūḷho <span class="note">[samūpabyuḷho (syā. kaṃ.), samūpabbūḷho (sī.)]</span> ahosi. tasmiṃ kho pana, mārisa moggallāna, saṅgāme devā jiniṃsu, asurā parājiniṃsu. so kho ahaṃ, mārisa moggallāna, taṃ saṅgāmaṃ abhivijinitvā vijitasaṅgāmo tato paṭinivattitvā vejayantaṃ nāma pāsādaṃ māpesiṃ. vejayantassa kho, mārisa moggallāna, pāsādassa ekasataṃ niyyūhaṃ. ekekasmiṃ niyyūhe satta satta kūṭāgārasatāni. ekamekasmiṃ kūṭāgāre satta satta accharāyo<a name="T1.0467"></a>. ekamekissā accharāya satta satta paricārikāyo. iccheyyāsi no</p>\r\n','\r',char(13)),'\n',char(10)),'-392-');
INSERT INTO pages VALUES(3359,'mula_ma_01',320,replace(replace('<p class="bodytext">tvaṃ<a name="M1.0320"></a>, mārisa moggallāna, vejayantassa pāsādassa rāmaṇeyyakaṃ daṭṭhu"nti? adhivāsesi kho āyasmā mahāmoggallāno tuṇhībhāvena.</p>\r\n<p class="bodytext"><a name="para393"></a><a name="para393_mn1"></a><span class="paranum">393</span>. atha <a name="V1.0322"></a> kho sakko ca devānamindo vessavaṇo ca mahārājā āyasmantaṃ mahāmoggallānaṃ purakkhatvā yena vejayanto pāsādo tenupasaṅkamiṃsu. addasaṃsu kho sakkassa devānamindassa paricārikāyo āyasmantaṃ mahāmoggallānaṃ dūratova āgacchantaṃ, disvā ottappamānā hirīyamānā sakaṃ sakaṃ ovarakaṃ pavisiṃsu. seyyathāpi nāma suṇisā sasuraṃ disvā ottappati hirīyati, evameva sakkassa devānamindassa paricārikāyo āyasmantaṃ mahāmoggallānaṃ disvā ottappamānā hirīyamānā sakaṃ sakaṃ ovarakaṃ pavisiṃsu. atha kho sakko ca devānamindo vessavaṇo ca mahārājā āyasmantaṃ mahāmoggallānaṃ vejayante pāsāde anucaṅkamāpenti anuvicarāpenti – "idampi, mārisa moggallāna, passa vejayantassa pāsādassa rāmaṇeyyakaṃ, idampi, mārisa moggallāna, passa vejayantassa pāsādassa rāmaṇeyyaka"nti. "sobhati idaṃ āyasmato kosiyassa, yathā taṃ pubbe katapuññassa. manussāpi kiñcideva rāmaṇeyyakaṃ disvā <span class="note">[diṭṭhā (sī. pī. ka.)]</span> evamāhaṃsu – ‘sobhati vata bho yathā devānaṃ tāvatiṃsāna’nti. tayidaṃ āyasmato kosiyassa sobhati, yathā taṃ pubbe katapuññassā"ti. atha kho āyasmato mahāmoggallānassa <a name="T1.0468"></a> etadahosi – "atibāḷhaṃ kho ayaṃ yakkho pamatto viharati. yaṃnūnāhaṃ imaṃ yakkhaṃ saṃvejeyya"nti. atha kho āyasmā mahāmoggallāno tathārūpaṃ iddhābhisaṅkhāraṃ abhisaṅkhāsi <span class="note">[abhisaṅkhāresi (ka.), abhisaṅkhāreti (syā. kaṃ.)]</span> yathā vejayantaṃ pāsādaṃ pādaṅguṭṭhakena saṅkampesi sampakampesi sampavedhesi<a name="P1.0254"></a>. atha kho sakko ca devānamindo, vessavaṇo ca mahārājā, devā ca tāvatiṃsā acchariyabbhutacittajātā ahesuṃ – "acchariyaṃ vata, bho, abbhutaṃ vata, bho, samaṇassa mahiddhikatā mahānubhāvatā, yatra hi nāma dibbabhavanaṃ pādaṅguṭṭhakena saṅkampessati sampakampessati sampavedhessatī"ti! atha kho āyasmā mahāmoggallāno sakkaṃ devānamindaṃ saṃviggaṃ lomahaṭṭhajātaṃ viditvā sakkaṃ devānamindaṃ etadavoca – "yathā kathaṃ pana kho, kosiya, bhagavā saṃkhittena taṇhāsaṅkhayavimuttiṃ abhāsi? sādhu mayampi etissā kathāya bhāgino assāma savanāyā"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-393-');
INSERT INTO pages VALUES(3360,'mula_ma_01',321,replace(replace('<p class="bodytext"><a name="para394"></a><a name="para394_mn1"></a><span class="paranum">394</span>. idhāhaṃ<a name="M1.0321"></a>, mārisa moggallāna, yena bhagavā tenupasaṅkamiṃ, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ aṭṭhāsiṃ. ekamantaṃ ṭhito kho ahaṃ, mārisa moggallāna, bhagavantaṃ etadavocaṃ – ‘kittāvatā nu kho, bhante, bhikkhu saṃkhittena taṇhāsaṅkhayavimutto hoti accantaniṭṭho accantayogakkhemī accantabrahmacārī accantapariyosāno seṭṭho devamanussāna"’nti?</p>\r\n<p class="bodytext">evaṃ <a name="V1.0323"></a> vutte, mārisa moggallāna, bhagavā maṃ etadavoca – ‘idha, devānaminda, bhikkhuno sutaṃ hoti – sabbe dhammā nālaṃ <a name="T1.0469"></a> abhinivesāyā’ti. evaṃ cetaṃ devānaminda bhikkhuno sutaṃ hoti ‘sabbe dhammā nālaṃ abhinivesāyā’ti. so sabbaṃ dhammaṃ abhijānāti, sabbaṃ dhammaṃ abhiññāya sabbaṃ dhammaṃ parijānāti, sabbaṃ dhammaṃ pariññāya yaṃ kiñci vedanaṃ vedeti sukhaṃ vā dukkhaṃ vā adukkhamasukhaṃ vā. so tāsu vedanāsu aniccānupassī viharati, virāgānupassī viharati, nirodhānupassī viharati, paṭinissaggānupassī viharati. so tāsu vedanāsu aniccānupassī viharanto, virāgānupassī viharanto, nirodhānupassī viharanto, paṭinissaggānupassī viharanto na kiñci loke upādiyati, anupādiyaṃ na paritassati, aparitassaṃ paccattaññeva parinibbāyati – ‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti pajānāti. ettāvatā kho, devānaminda, bhikkhu saṃkhittena taṇhāsaṅkhayavimutto hoti accantaniṭṭho accantayogakkhemī accantabrahmacārī accantapariyosāno seṭṭho devamanussānanti. evaṃ kho me, mārisa moggallāna, bhagavā saṃkhittena taṇhāsaṅkhayavimuttiṃ abhāsī"ti.</p>\r\n<p class="bodytext">atha kho āyasmā mahāmoggallāno sakkassa devānamindassa bhāsitaṃ <a name="P1.0255"></a> abhinanditvā anumoditvā – seyyathāpi nāma balavā puriso samiñjitaṃ vā bāhaṃ pasāreyya, pasāritaṃ vā bāhaṃ samiñjeyya evameva – devesu tāvatiṃsesu antarahito pubbārāme migāramātupāsāde pāturahosi. atha kho sakkassa devānamindassa paricārikāyo acirapakkante āyasmante mahāmoggallāne sakkaṃ devānamindaṃ <a name="T1.0470"></a> etadavocuṃ – "eso nu te, mārisa, so bhagavā satthā"ti? "na kho me, mārisa, so bhagavā satthā. sabrahmacārī me eso āyasmā mahāmoggallāno"ti. "lābhā te, mārisa, (suladdhaṃ te, mārisa) <span class="note">[( ) natthi (sī. pī.)]</span> yassa te sabrahmacārī evaṃmahiddhiko evaṃmahānubhāvo! aho nūna te so bhagavā satthā"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-394-');
INSERT INTO pages VALUES(3361,'mula_ma_01',322,replace(replace('<p class="bodytext"><a name="para395"></a><a name="para395_mn1"></a><span class="paranum">395</span>. atha <a name="M1.0322"></a> kho āyasmā mahāmoggallāno yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho āyasmā mahāmoggallāno bhagavantaṃ etadavoca – "abhijānāti no, bhante, bhagavā ahu <span class="note">[ahunaññeva (sī. syā. kaṃ.)]</span> ñātaññatarassa mahesakkhassa yakkhassa saṃkhittena taṇhāsaṅkhayavimuttiṃ bhāsitā"ti <span class="note">[abhāsitthāti (ka.)]</span>? "abhijānāmahaṃ, moggallāna, idha sakko devānamindo yenāhaṃ tenupasaṅkami, upasaṅkamitvā maṃ abhivādetvā ekamantaṃ aṭṭhāsi. ekamantaṃ ṭhito kho, moggallāna, sakko devānamindo maṃ etadavoca – ‘kittāvatā nu kho, bhante<a name="V1.0324"></a>, bhikkhu saṃkhittena taṇhāsaṅkhayavimutto hoti accantaniṭṭho accantayogakkhemī accantabrahmacārī accantapariyosāno seṭṭho devamanussāna"nti.</p>\r\n<p class="bodytext">evaṃ vutte ahaṃ, moggallāna, sakkaṃ devānamindaṃ etadavocaṃ "idha devānaminda bhikkhuno sutaṃ hoti ‘sabbe dhammā nālaṃ abhinivesāyā’ti. evaṃ cetaṃ devānaminda bhikkhuno sutaṃ hoti ‘sabbe dhammā nālaṃ abhinivesāyā’ti. so sabbaṃ dhammaṃ abhijānāti, sabbaṃ dhammaṃ abhiññāya sabbaṃ dhammaṃ parijānāti<a name="T1.0471"></a>, sabbaṃ dhammaṃ pariññāya yaṃ kiñci vedanaṃ vedeti sukhaṃ vā dukkhaṃ vā adukkhamasukhaṃ vā. so tāsu vedanāsu aniccānupassī viharati, virāgānupassī viharati, nirodhānupassī viharati, paṭinissaggānupassī viharati. so tāsu vedanāsu aniccānupassī viharanto, virāgānupassī viharanto, nirodhānupassī viharanto, paṭinissaggānupassī viharanto na kiñci loke upādiyati, anupādiyaṃ na paritassati, aparitassaṃ paccattaññeva parinibbāyati – ‘khīṇā jāti, vusitaṃ brahmacariyaṃ<a name="P1.0256"></a>, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti pajānāti. ettāvatā kho, devānaminda, bhikkhu saṃkhittena taṇhāsaṅkhayavimutto hoti accantaniṭṭho accantayogakkhemī accantabrahmacārī accantapariyosāno seṭṭho devamanussānanti. evaṃ kho ahaṃ, moggallāna, abhijānāmi sakkassa devānamindassa saṃkhittena taṇhāsaṅkhayavimuttiṃ bhāsitā"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamano āyasmā mahāmoggallāno bhagavato bhāsitaṃ abhinandīti.</p>\r\n<p class="centered">cūḷataṇhāsaṅkhayasuttaṃ niṭṭhitaṃ sattamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-395-');
INSERT INTO pages VALUES(3362,'mula_ma_01',323,replace(replace('<p class="subhead">8. mahātaṇhāsaṅkhayasuttaṃ</p>\r\n<p class="bodytext"><a name="para396"></a><a name="para396_mn1"></a><span class="paranum">396</span>. evaṃ <a name="T1.0472"></a><a name="M1.0323"></a><a name="V1.0325"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tena kho pana samayena sātissa nāma bhikkhuno kevaṭṭaputtassa evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ hoti – "tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā tadevidaṃ viññāṇaṃ sandhāvati saṃsarati anañña"nti. assosuṃ kho sambahulā bhikkhū – "sātissa kira nāma bhikkhuno kevaṭṭaputtassa evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ – ‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā tadevidaṃ viññāṇaṃ sandhāvati saṃsarati, anañña"’nti. atha kho te bhikkhū yena sāti bhikkhu kevaṭṭaputto tenupasaṅkamiṃsu, upasaṅkamitvā sātiṃ bhikkhuṃ kevaṭṭaputtaṃ etadavocuṃ – "saccaṃ kira te, āvuso sāti, evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ – ‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā tadevidaṃ viññāṇaṃ sandhāvati saṃsarati, anañña"’nti? "evaṃ byā kho ahaṃ, āvuso, bhagavatā dhammaṃ desitaṃ ājānāmi yathā tadevidaṃ viññāṇaṃ sandhāvati saṃsarati, anañña"nti. atha kho te bhikkhū sātiṃ bhikkhuṃ kevaṭṭaputtaṃ etasmā pāpakā diṭṭhigatā vivecetukāmā samanuyuñjanti samanugāhanti samanubhāsanti – "mā evaṃ, āvuso sāti, avaca, mā bhagavantaṃ abbhācikkhi, na hi sādhu bhagavato abbhakkhānaṃ, na hi bhagavā evaṃ vadeyya. anekapariyāyenāvuso <a name="T1.0473"></a> sāti, paṭiccasamuppannaṃ viññāṇaṃ vuttaṃ <a name="P1.0257"></a> bhagavatā, aññatra paccayā natthi viññāṇassa sambhavo"ti. evampi kho sāti bhikkhu kevaṭṭaputto tehi bhikkhūhi samanuyuñjiyamāno samanugāhiyamāno samanubhāsiyamāno tadeva pāpakaṃ diṭṭhigataṃ thāmasā parāmāsā abhinivissa voharati – "evaṃ byā kho ahaṃ, āvuso, bhagavatā dhammaṃ desitaṃ ājānāmi yathā tadevidaṃ viññāṇaṃ sandhāvati saṃsarati anañña"nti.</p>\r\n<p class="bodytext"><a name="para397"></a><a name="para397_mn1"></a><span class="paranum">397</span>. yato kho te bhikkhū nāsakkhiṃsu sātiṃ bhikkhuṃ kevaṭṭaputtaṃ etasmā pāpakā diṭṭhigatā vivecetuṃ, atha kho te bhikkhū yena bhagavā tenupasaṅkamiṃsu, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu. ekamantaṃ nisinnā kho te bhikkhū bhagavantaṃ etadavocuṃ – "sātissa nāma, bhante, bhikkhuno kevaṭṭaputtassa evarūpaṃ pāpakaṃ diṭṭhigataṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-396-397-');
INSERT INTO pages VALUES(3363,'mula_ma_01',324,replace(replace('<p class="bodytext">uppannaṃ <a name="M1.0324"></a> – ‘tathāhaṃ bhagavatā <a name="V1.0326"></a> dhammaṃ desitaṃ ājānāmi yathā tadevidaṃ viññāṇaṃ sandhāvati saṃsarati, anañña’nti. assumha kho mayaṃ, bhante, sātissa kira nāma bhikkhuno kevaṭṭaputtassa evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ – ‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā tadevidaṃ viññāṇaṃ sandhāvati saṃsarati, anañña’nti. atha kho mayaṃ, bhante, yena sāti bhikkhu kevaṭṭaputto tenupasaṅkamimha, upasaṅkamitvā sātiṃ bhikkhuṃ kevaṭṭaputtaṃ etadavocumha – ‘saccaṃ kira te, āvuso sāti, evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ – "tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā tadevidaṃ viññāṇaṃ sandhāvati saṃsarati, anañña"nti? evaṃ vutte, bhante, sāti bhikkhu kevaṭṭaputto amhe etadavoca – ‘evaṃ byā kho ahaṃ, āvuso, bhagavatā dhammaṃ desitaṃ ājānāmi <a name="T1.0474"></a> yathā tadevidaṃ viññāṇaṃ sandhāvati saṃsarati, anañña’nti. atha kho mayaṃ, bhante, sātiṃ bhikkhuṃ kevaṭṭaputtaṃ etasmā pāpakā diṭṭhigatā vivecetukāmā samanuyuñjimha samanugāhimha samanubhāsimha – ‘mā evaṃ, āvuso sāti, avaca, mā bhagavantaṃ abbhācikkhi, na hi sādhu bhagavato abbhakkhānaṃ, na hi bhagavā evaṃ vadeyya. anekapariyāyenāvuso sāti, paṭiccasamuppannaṃ viññāṇaṃ vuttaṃ bhagavatā, aññatra paccayā natthi viññāṇassa sambhavo’ti. evampi kho, bhante, sāti bhikkhu kevaṭṭaputto amhehi samanuyuñjiyamāno samanugāhiyamāno samanubhāsiyamāno tadeva pāpakaṃ diṭṭhigataṃ thāmasā parāmasā abhinivissa voharati – ‘evaṃ byā kho ahaṃ, āvuso, bhagavatā dhammaṃ desitaṃ ājānāmi yathā tadevidaṃ viññāṇaṃ sandhāvati saṃsarati, anañña’nti. yato kho mayaṃ, bhante, nāsakkhimha sātiṃ bhikkhuṃ kevaṭṭaputtaṃ etasmā pāpakā diṭṭhigatā vivecetuṃ, atha mayaṃ etamatthaṃ bhagavato ārocemā"ti.</p>\r\n<p class="bodytext"><a name="para398"></a><a name="para398_mn1"></a><span class="paranum">398</span>. atha kho bhagavā aññataraṃ bhikkhuṃ āmantesi – "ehi tvaṃ <a name="P1.0258"></a> bhikkhu, mama vacanena sātiṃ bhikkhuṃ kevaṭṭaputtaṃ āmantehi – ‘satthā taṃ, āvuso sāti, āmantetī"’ti. "evaṃ, bhante"ti kho so bhikkhu bhagavato paṭissutvā yena sāti bhikkhu kevaṭṭaputto tenupasaṅkami, upasaṅkamitvā sātiṃ bhikkhuṃ kevaṭṭaputtaṃ etadavoca – "satthā taṃ, āvuso sāti, āmantetī"ti. "evamāvuso"ti kho sāti bhikkhu kevaṭṭaputto <a name="T1.0475"></a> tassa bhikkhuno paṭissutvā yena bhagavā tenupasaṅkami, upasaṅkamitvā</p>\r\n','\r',char(13)),'\n',char(10)),'-398-');
INSERT INTO pages VALUES(3364,'mula_ma_01',325,replace(replace('<p class="bodytext">bhagavantaṃ <a name="M1.0325"></a> abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinnaṃ kho sātiṃ bhikkhuṃ kevaṭṭaputtaṃ bhagavā etadavoca – "saccaṃ kira, te, sāti, evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ – ‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi yathā tadevidaṃ viññāṇaṃ sandhāvati saṃsarati, anañña"’nti? "evaṃ byā kho ahaṃ, bhante, bhagavatā dhammaṃ desitaṃ ājānāmi yathā tadevidaṃ viññāṇaṃ <a name="V1.0327"></a> sandhāvati saṃsarati, anañña"nti. "katamaṃ taṃ, sāti, viññāṇa"nti? "yvāyaṃ, bhante, vado vedeyyo tatra tatra kalyāṇapāpakānaṃ kammānaṃ vipākaṃ paṭisaṃvedetī"ti. "kassa nu kho nāma tvaṃ, moghapurisa, mayā evaṃ dhammaṃ desitaṃ ājānāsi? nanu mayā, moghapurisa, anekapariyāyena paṭiccasamuppannaṃ viññāṇaṃ vuttaṃ, aññatra paccayā natthi viññāṇassa sambhavoti? atha ca pana tvaṃ, moghapurisa, attanā duggahitena amhe ceva abbhācikkhasi, attānañca khaṇasi, bahuñca apuññaṃ pasavasi. tañhi te, moghapurisa, bhavissati dīgharattaṃ ahitāya dukkhāyā"ti.</p>\r\n<p class="bodytext"><a name="para399"></a><a name="para399_mn1"></a><span class="paranum">399</span>. atha kho bhagavā bhikkhū āmantesi – "taṃ kiṃ maññatha, bhikkhave, api nāyaṃ sāti bhikkhu kevaṭṭaputto usmīkatopi imasmiṃ dhammavinaye"ti? "kiñhi siyā bhante? no hetaṃ, bhante"ti. evaṃ vutte, sāti bhikkhu kevaṭṭaputto tuṇhībhūto maṅkubhūto pattakkhandho adhomukho pajjhāyanto appaṭibhāno nisīdi. atha kho bhagavā sātiṃ <a name="T1.0476"></a> bhikkhuṃ kevaṭṭaputtaṃ tuṇhībhūtaṃ maṅkubhūtaṃ pattakkhandhaṃ adhomukhaṃ pajjhāyantaṃ appaṭibhānaṃ viditvā sātiṃ bhikkhuṃ kevaṭṭaputtaṃ etadavoca – "paññāyissasi kho tvaṃ, moghapurisa, etena sakena pāpakena diṭṭhigatena. idhāhaṃ bhikkhū paṭipucchissāmī"ti. atha kho bhagavā bhikkhū āmantesi – "tumhepi me, bhikkhave, evaṃ dhammaṃ desitaṃ ājānātha yathāyaṃ sāti bhikkhu kevaṭṭaputto <a name="P1.0259"></a> attanā duggahitena amhe ceva abbhācikkhati, attānañca khaṇati, bahuñca apuññaṃ pasavatī"ti? "no hetaṃ, bhante! anekapariyāyena hi no, bhante, paṭiccasamuppannaṃ viññāṇaṃ vuttaṃ bhagavatā, aññatra paccayā natthi viññāṇassa sambhavo"ti. "sādhu sādhu, bhikkhave! sādhu kho me tumhe, bhikkhave, evaṃ dhammaṃ desitaṃ ājānātha. anekapariyāyena hi vo, bhikkhave, paṭiccasamuppannaṃ viññāṇaṃ vuttaṃ mayā, aññatra paccayā natthi viññāṇassa sambhavoti. atha ca panāyaṃ sāti bhikkhu kevaṭṭaputto attanā duggahitena amhe ceva abbhācikkhati, attānañca khaṇati, bahuñca apuññaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-399-');
INSERT INTO pages VALUES(3365,'mula_ma_01',326,replace(replace('<p class="bodytext">pasavati <a name="M1.0326"></a> pasavati. tañhi tassa moghapurisassa bhavissati dīgharattaṃ ahitāya dukkhāya.</p>\r\n<p class="bodytext"><a name="para400"></a><a name="para400_mn1"></a><span class="paranum">400</span>. yaṃ yadeva, bhikkhave, paccayaṃ paṭicca uppajjati viññāṇaṃ, tena teneva viññāṇaṃtveva saṅkhyaṃ gacchati <span class="note">[saṅkhaṃ gacchati (sī. pī.)]</span>. cakkhuñca paṭicca rūpe ca uppajjati viññāṇaṃ, cakkhuviññāṇaṃtveva <a name="V1.0328"></a> saṅkhyaṃ gacchati, sotañca paṭicca sadde ca uppajjati viññāṇaṃ, sotaviññāṇaṃtveva saṅkhyaṃ gacchati, ghānañca paṭicca gandhe ca uppajjati viññāṇaṃ, ghānaviññāṇaṃtveva saṅkhyaṃ <a name="T1.0477"></a> gacchati, jivhañca paṭicca rase ca uppajjati viññāṇaṃ, jivhāviññāṇaṃtveva saṅkhyaṃ gacchati, kāyañca paṭicca phoṭṭhabbe ca uppajjati viññāṇaṃ, kāyaviññāṇaṃtveva saṅkhyaṃ gacchati, manañca paṭicca dhamme ca uppajjati viññāṇaṃ, manoviññāṇaṃtveva saṅkhyaṃ gacchati.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, yaṃ yadeva paccayaṃ paṭicca aggi jalati tena teneva saṅkhyaṃ gacchati. kaṭṭhañca paṭicca aggi jalati, kaṭṭhaggitveva saṅkhyaṃ gacchati, sakalikañca paṭicca aggi jalati, sakalikaggitveva saṅkhyaṃ gacchati, tiṇañca paṭicca aggi jalati, tiṇaggitveva saṅkhyaṃ gacchati, gomayañca paṭicca aggi jalati, gomayaggitveva saṅkhyaṃ gacchati, thusañca paṭicca aggi jalati, thusaggitveva saṅkhyaṃ gacchati, saṅkārañca paṭicca aggi jalati, saṅkāraggitveva saṅkhyaṃ gacchati. evameva kho, bhikkhave, yaṃ yadeva paccayaṃ paṭicca uppajjati viññāṇaṃ, tena teneva saṅkhyaṃ gacchati. cakkhuñca paṭicca rūpe ca uppajjati viññāṇaṃ, cakkhuviññāṇaṃtveva saṅkhyaṃ gacchati, sotañca paṭicca sadde ca uppajjati viññāṇaṃ, sotaviññāṇaṃtveva saṅkhyaṃ gacchati, ghānañca paṭicca gandhe ca uppajjati viññāṇaṃ<a name="P1.0260"></a>, ghāṇaviññāṇaṃtveva saṅkhyaṃ gacchati, jivhañca paṭicca rase ca uppajjati viññāṇaṃ, jivhāviññāṇaṃtveva saṅkhyaṃ gacchati. kāyañca paṭicca phoṭṭhabbe ca uppajjati viññāṇaṃ, kāyaviññāṇaṃtveva saṅkhyaṃ gacchati. manañca paṭicca dhamme ca uppajjati viññāṇaṃ, manoviññāṇaṃtveva saṅkhyaṃ gacchati.</p>\r\n<p class="bodytext"><a name="para401"></a><a name="para401_mn1"></a><span class="paranum">401</span>. bhūtamidanti<a name="T1.0478"></a>, bhikkhave, passathā"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">tadāhārasambhavanti, bhikkhave, passathā"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">tadāhāranirodhā <a name="V1.0329"></a> yaṃ bhūtaṃ, taṃ nirodhadhammanti, bhikkhave, passathā"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">bhūtamidaṃ nossūti, bhikkhave, kaṅkhato uppajjati vicikicchā"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">tadāhārasambhavaṃ nossūti,</p>\r\n','\r',char(13)),'\n',char(10)),'-400-401-');
INSERT INTO pages VALUES(3366,'mula_ma_01',327,replace(replace('<p class="bodytext">bhikkhave<a name="M1.0327"></a>, kaṅkhato uppajjati vicikicchā"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">tadāhāranirodhā yaṃ bhūtaṃ, taṃ nirodhadhammaṃ nossūti, bhikkhave, kaṅkhato uppajjati vicikicchā"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">bhūtamidanti, bhikkhave, yathābhūtaṃ sammappaññāya passato yā vicikicchā sā pahīyatī"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">tadāhārasambhavanti, bhikkhave, yathābhūtaṃ sammappaññāya passato yā vicikicchā sā pahīyatī"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">tadāhāranirodhā yaṃ bhūtaṃ, taṃ nirodhadhammanti, bhikkhave, yathābhūtaṃ sammappaññāya passato yā vicikicchā sā pahīyatī"ti?</p>\r\n<p class="bodytext">evaṃ<a name="V1.0330"></a>, bhante".</p>\r\n<p class="bodytext">bhūtamidanti, bhikkhave, itipi vo ettha nibbicikicchā"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">tadāhārasambhavanti, bhikkhave, itipi vo ettha nibbicikicchā"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">tadāhāranirodhā yaṃ bhūtaṃ taṃ nirodhadhammanti, bhikkhave, itipi vo ettha nibbicikicchā"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">bhūtamidanti, bhikkhave, yathābhūtaṃ sammappaññāya sudiṭṭha"nti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">tadāhārasambhavanti, bhikkhave, yathābhūtaṃ sammappaññāya sudiṭṭha"nti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">tadāhāranirodhā yaṃ bhūtaṃ taṃ nirodhadhammanti, bhikkhave, yathābhūtaṃ sammappaññāya sudiṭṭha"nti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">imaṃ <a name="T1.0479"></a> ce tumhe, bhikkhave, diṭṭhiṃ evaṃ parisuddhaṃ evaṃ pariyodātaṃ allīyetha kelāyetha dhanāyetha <a name="V1.0331"></a> mamāyetha, api nu me tumhe, bhikkhave, kullūpamaṃ dhammaṃ desitaṃ ājāneyyātha nittharaṇatthāya no gahaṇatthāyā"ti?</p>\r\n<p class="bodytext">no hetaṃ, bhante".</p>\r\n<p class="bodytext">imaṃ ce tumhe, bhikkhave, diṭṭhiṃ evaṃ parisuddhaṃ evaṃ pariyodātaṃ <a name="P1.0261"></a> na allīyetha na kelāyetha na dhanāyetha na mamāyetha, api nu me tumhe, bhikkhave, kullūpamaṃ dhammaṃ desitaṃ ājāneyyātha nittharaṇatthāya no gahaṇatthāyā"ti?</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext"><a name="para402"></a><a name="para402_mn1"></a><span class="paranum">402</span>. cattārome, bhikkhave, āhārā bhūtānaṃ vā sattānaṃ ṭhitiyā, sambhavesīnaṃ vā anuggahāya. katame cattāro? kabaḷīkāro āhāro oḷāriko vā sukhumo vā, phasso dutiyo, manosañcetanā tatiyā, viññāṇaṃ catutthaṃ.</p>\r\n<p class="bodytext">ime ca, bhikkhave, cattāro āhārā kiṃnidānā kiṃsamudayā kiṃjātikā kiṃpabhavā?</p>\r\n<p class="bodytext">ime cattāro āhārā taṇhānidānā taṇhāsamudayā taṇhājātikā taṇhāpabhavā.</p>\r\n<p class="bodytext">taṇhā cāyaṃ, bhikkhave, kiṃnidānā kiṃsamudayā kiṃjātikā kiṃpabhavā?</p>\r\n<p class="bodytext">taṇhā vedanānidānā vedanāsamudayā vedanājātikā vedanāpabhavā.</p>\r\n<p class="bodytext">vedanā cāyaṃ, bhikkhave, kiṃnidānā kiṃsamudayā kiṃjātikā kiṃpabhavā?</p>\r\n<p class="bodytext">vedanā phassanidānā phassasamudayā phassajātikā</p>\r\n','\r',char(13)),'\n',char(10)),'-402-');
INSERT INTO pages VALUES(3367,'mula_ma_01',328,replace(replace('<p class="bodytext">phassapabhavā<a name="M1.0328"></a>.</p>\r\n<p class="bodytext">phasso cāyaṃ, bhikkhave, kiṃnidāno kiṃsamudayo kiṃjātiko kiṃpabhavo?</p>\r\n<p class="bodytext">phasso <a name="V1.0332"></a> saḷāyatananidāno saḷāyatanasamudayo saḷāyatanajātiko saḷāyatanapabhavo.</p>\r\n<p class="bodytext">saḷāyatanaṃ cidaṃ, bhikkhave, kiṃnidānaṃ kiṃsamudayaṃ <a name="T1.0480"></a> kiṃjātikaṃ kiṃpabhavaṃ?</p>\r\n<p class="bodytext">saḷāyatanaṃ nāmarūpanidānaṃ nāmarūpasamudayaṃ nāmarūpajātikaṃ nāmarūpapabhavaṃ.</p>\r\n<p class="bodytext">nāmarūpaṃ cidaṃ, bhikkhave, kiṃnidānaṃ kiṃsamudayaṃ kiṃjātikaṃ kiṃpabhavaṃ?</p>\r\n<p class="bodytext">nāmarūpaṃ viññāṇanidānaṃ viññāṇasamudayaṃ viññāṇajātikaṃ viññāṇapabhavaṃ.</p>\r\n<p class="bodytext">viññāṇaṃ cidaṃ, bhikkhave, kiṃnidānaṃ kiṃsamudayaṃ kiṃjātikaṃ kiṃpabhavaṃ?</p>\r\n<p class="bodytext">viññāṇaṃ saṅkhāranidānaṃ saṅkhārasamudayaṃ saṅkhārajātikaṃ saṅkhārapabhavaṃ.</p>\r\n<p class="bodytext">saṅkhārā cime, bhikkhave, kiṃnidānā kiṃsamudayā kiṃjātikā kiṃpabhavā?</p>\r\n<p class="bodytext">saṅkhārā avijjānidānā avijjāsamudayā avijjājātikā avijjāpabhavā.</p>\r\n<p class="bodytext">iti kho, bhikkhave, avijjāpaccayā saṅkhārā, saṅkhārapaccayā viññāṇaṃ, viññāṇapaccayā nāmarūpaṃ, nāmarūpapaccayā saḷāyatanaṃ, saḷāyatanapaccayā phasso, phassapaccayā vedanā, vedanāpaccayā taṇhā, taṇhāpaccayā upādānaṃ, upādānapaccayā bhavo, bhavapaccayā jāti, jātipaccayā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā sambhavanti. evametassa kevalassa dukkhakkhandhassa samudayo hoti."’</p>\r\n<p class="bodytext"><a name="para403"></a><a name="para403_mn1"></a><span class="paranum">403</span>. jātipaccayā jarāmaraṇanti iti kho panetaṃ vuttaṃ, jātipaccayā nu kho, bhikkhave, jarāmaraṇaṃ, no vā, kathaṃ vā ettha <span class="note">[kathaṃ vā vo ettha (?)]</span> hotī"ti? "jātipaccayā, bhante, jarāmaraṇaṃ, evaṃ no ettha hoti <span class="note">[evaṃ no ettha hotīti (ka.)]</span> – jātipaccayā jarāmaraṇa"nti. "bhavapaccayā jātīti iti kho panetaṃ vuttaṃ, bhavapaccayā nu kho, bhikkhave, jāti, no vā, kathaṃ vā ettha hotī"ti? "bhavapaccayā, bhante<a name="P1.0262"></a>, jāti, evaṃ no ettha hoti – bhavapaccayā jātī"ti<a name="T1.0481"></a>. "upādānapaccayā <a name="V1.0333"></a> bhavoti iti kho panetaṃ vuttaṃ, upādānapaccayā nu kho, bhikkhave, bhavo, no vā, kathaṃ vā ettha hotī"ti? "upādānapaccayā, bhante, bhavo, evaṃ no ettha hoti – upādānapaccayā bhavo"ti. "taṇhāpaccayā upādānanti iti kho panetaṃ vuttaṃ, taṇhāpaccayā nu kho, bhikkhave, upādānaṃ, no vā, kathaṃ vā ettha hotī"ti? "taṇhāpaccayā, bhante, upādānaṃ, evaṃ no ettha hoti – taṇhāpaccayā upādāna"nti.</p>\r\n','\r',char(13)),'\n',char(10)),'-403-');
INSERT INTO pages VALUES(3368,'mula_ma_01',329,replace(replace('<p class="bodytext">vedanāpaccayā <a name="M1.0329"></a> taṇhāti iti kho panetaṃ vuttaṃ, vedanāpaccayā nu kho, bhikkhave, taṇhā, no vā, kathaṃ vā ettha hotī"ti? "vedanāpaccayā, bhante, taṇhā, evaṃ no ettha hoti – vedanāpaccayā taṇhā"ti. "phassapaccayā vedanāti iti kho panetaṃ vuttaṃ, phassapaccayā nu kho, bhikkhave, vedanā, no vā, kathaṃ vā ettha hotī"ti? "phassapaccayā, bhante, vedanā, evaṃ no ettha hoti – phassapaccayā vedanā"ti. "saḷāyatanapaccayā phassoti iti kho panetaṃ vuttaṃ, saḷāyatanapaccayā nu kho, bhikkhave, phasso, no vā, kathaṃ vā ettha hotī"ti? "saḷāyatanapaccayā, bhante, phasso, evaṃ no ettha hoti – saḷāyatanapaccayā phasso"ti. "nāmarūpapaccayā saḷāyatananti iti kho panetaṃ vuttaṃ, nāmarūpapaccayā nu kho, bhikkhave, saḷāyatanaṃ, no vā, kathaṃ vā ettha hotī"ti? "nāmarūpapaccayā, bhante, saḷāyatanaṃ, evaṃ no ettha hoti – nāmarūpapaccayā saḷāyatana"nti. "viññāṇapaccayā nāmarūpanti <a name="T1.0482"></a> iti kho panetaṃ vuttaṃ, viññāṇapaccayā nu kho, bhikkhave, nāmarūpaṃ, no vā, kathaṃ vā ettha hotī"ti? "viññāṇapaccayā, bhante, nāmarūpaṃ, evaṃ no ettha hoti – viññāṇapaccayā nāmarūpa"nti. "saṅkhārapaccayā viññāṇanti iti kho panetaṃ vuttaṃ, saṅkhārapaccayā nu kho, bhikkhave, viññāṇaṃ, no vā, kathaṃ vā ettha hotī"ti? "saṅkhārapaccayā, bhante, viññāṇaṃ, evaṃ no ettha hoti – saṅkhārapaccayā viññāṇa"nti. "avijjāpaccayā saṅkhārāti iti kho panetaṃ vuttaṃ, avijjāpaccayā nu kho, bhikkhave, saṅkhārā, no vā, kathaṃ vā ettha hotī"ti? "avijjāpaccayā, bhante, saṅkhārā, evaṃ no ettha hoti – avijjāpaccayā saṅkhārā"ti.</p>\r\n<p class="bodytext"><a name="para404"></a><a name="para404_mn1"></a><span class="paranum">404</span>. sādhu, bhikkhave. iti kho, bhikkhave, tumhepi evaṃ vadetha, ahampi evaṃ vadāmi – imasmiṃ sati idaṃ hoti, imassuppādā <a name="P1.0263"></a> idaṃ uppajjati, yadidaṃ – avijjāpaccayā saṅkhārā, saṅkhārapaccayā viññāṇaṃ, viññāṇapaccayā nāmarūpaṃ, nāmarūpapaccayā saḷāyatanaṃ, saḷāyatanapaccayā phasso, phassapaccayā vedanā, vedanāpaccayā taṇhā, taṇhāpaccayā upādānaṃ, upādānapaccayā <a name="V1.0334"></a> bhavo, bhavapaccayā jāti, jātipaccayā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā sambhavanti. evametassa kevalassa dukkhakkhandhassa samudayo hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-404-');
INSERT INTO pages VALUES(3369,'mula_ma_01',330,replace(replace('<p class="bodytext">avijjāyatveva <a name="M1.0330"></a> asesavirāganirodhā saṅkhāranirodho, saṅkhāranirodhā viññāṇanirodho, viññāṇanirodhā nāmarūpanirodho, nāmarūpanirodhā saḷāyatananirodho<a name="T1.0483"></a>, saḷāyatananirodhā phassanirodho, phassanirodhā vedanānirodho, vedanānirodhā taṇhānirodho, taṇhānirodhā upādānanirodho, upādānanirodhā bhavanirodho, bhavanirodhā jātinirodho, jātinirodhā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā nirujjhanti. evametassa kevalassa dukkhakkhandhassa nirodho hoti.</p>\r\n<p class="bodytext"><a name="para405"></a><a name="para405_mn1"></a><span class="paranum">405</span>. jātinirodhā jarāmaraṇanirodhoti iti kho panetaṃ vuttaṃ, jātinirodhā nu kho, bhikkhave, jarāmaraṇanirodho, no vā, kathaṃ vā ettha hotī"ti? "jātinirodhā, bhante, jarāmaraṇanirodho, evaṃ no ettha hoti – jātinirodhā jarāmaraṇanirodho"ti. "bhavanirodhā jātinirodhoti iti kho panetaṃ vuttaṃ, bhavanirodhā nu kho, bhikkhave, jātinirodho, no vā, kathaṃ vā ettha hotī"ti? "bhavanirodhā, bhante, jātinirodho, evaṃ no ettha hoti – bhavanirodhā jātinirodho"ti. "upādānanirodhā bhavanirodhoti iti kho panetaṃ vuttaṃ, upādānanirodhā nu kho, bhikkhave, bhavanirodho, no vā, kathaṃ vā ettha hotī"ti? "upādānanirodhā, bhante, bhavanirodho, evaṃ no ettha hoti – upādānanirodhā bhavanirodho"ti. "taṇhānirodhā upādānanirodhoti iti kho panetaṃ vuttaṃ, taṇhānirodhā nu kho, bhikkhave, upādānanirodho, no vā, kathaṃ vā ettha hotī"ti? "taṇhānirodhā, bhante, upādānanirodho, evaṃ no ettha hoti – taṇhānirodhā upādānanirodho"ti. "vedanānirodhā <a name="T1.0484"></a> taṇhānirodhoti iti kho panetaṃ vuttaṃ, vedanānirodhā nu kho, bhikkhave, taṇhānirodho, no vā, kathaṃ vā ettha hotī"ti? "vedanānirodhā, bhante, taṇhānirodho, evaṃ no ettha hoti – vedanānirodhā taṇhānirodho"ti. "phassanirodhā vedanānirodhoti iti kho panetaṃ vuttaṃ, phassanirodhā <a name="P1.0264"></a> nu kho, bhikkhave, vedanānirodho, no vā, kathaṃ vā ettha hotī"ti? "phassanirodhā, bhante, vedanānirodho, evaṃ no ettha hoti – phassanirodhā vedanānirodho"ti. "saḷāyatananirodhā phassanirodhoti iti kho panetaṃ vuttaṃ, saḷāyatananirodhā nu kho, bhikkhave, phassanirodho, no</p>\r\n','\r',char(13)),'\n',char(10)),'-405-');
INSERT INTO pages VALUES(3370,'mula_ma_01',331,replace(replace('<p class="bodytext">vā<a name="M1.0331"></a>, kathaṃ vā ettha hotīti? saḷāyatananirodhā, bhante, phassanirodho, evaṃ no ettha hoti – saḷāyatananirodhā <a name="V1.0335"></a> phassanirodho"ti. "nāmarūpanirodhā saḷāyatananirodhoti iti kho panetaṃ vuttaṃ, nāmarūpanirodhā nu kho, bhikkhave, saḷāyatananirodho, no vā, kathaṃ vā ettha hotī"ti? "nāmarūpanirodhā, bhante, saḷāyatananirodho, evaṃ no ettha hoti – nāmarūpanirodhā saḷāyatananirodho"ti. "viññāṇanirodhā nāmarūpanirodhoti iti kho panetaṃ vuttaṃ, viññāṇanirodhā nu kho, bhikkhave, nāmarūpanirodho, no vā, kathaṃ vā ettha hotī"ti? "viññāṇanirodhā, bhante, nāmarūpanirodho, evaṃ no ettha hoti – viññāṇanirodhā nāmarūpanirodho"ti. "saṅkhāranirodhā viññāṇanirodhoti iti kho panetaṃ vuttaṃ, saṅkhāranirodhā nu kho, bhikkhave, viññāṇanirodho, no vā, kathaṃ vā ettha hotī"ti? "saṅkhāranirodhā, bhante<a name="T1.0485"></a>, viññāṇanirodho, evaṃ no ettha hoti – saṅkhāranirodhā viññāṇanirodho"ti. "avijjānirodhā saṅkhāranirodhoti iti kho panetaṃ vuttaṃ, avijjānirodhā nu kho, bhikkhave, saṅkhāranirodho, no vā, kathaṃ vā ettha hotī"ti? "avijjānirodhā, bhante, saṅkhāranirodho, evaṃ no ettha hoti – avijjānirodhā saṅkhāranirodho"ti.</p>\r\n<p class="bodytext"><a name="para406"></a><a name="para406_mn1"></a><span class="paranum">406</span>. sādhu, bhikkhave. iti kho, bhikkhave, tumhepi evaṃ vadetha, ahampi evaṃ vadāmi – imasmiṃ asati idaṃ na hoti, imassa nirodhā idaṃ nirujjhati, yadidaṃ – avijjānirodhā saṅkhāranirodho, saṅkhāranirodhā viññāṇanirodho, viññāṇanirodhā nāmarūpanirodho, nāmarūpanirodhā saḷāyatananirodho, saḷāyatananirodhā phassanirodho, phassanirodhā vedanānirodho, vedanānirodhā taṇhānirodho, taṇhānirodhā upādānanirodho, upādānanirodhā bhavanirodho, bhavanirodhā jātinirodho, jātinirodhā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā nirujjhanti. evametassa kevalassa dukkhakkhandhassa nirodho hoti.</p>\r\n<p class="bodytext"><a name="para407"></a><a name="para407_mn1"></a><span class="paranum">407</span>. api nu tumhe, bhikkhave, evaṃ jānantā evaṃ passantā pubbantaṃ <a name="T1.0486"></a><a name="P1.0265"></a> vā paṭidhāveyyātha – ‘ahesumha nu kho mayaṃ atītamaddhānaṃ, nanu kho ahesumha atītamaddhānaṃ, kiṃ nu kho ahesumha atītamaddhānaṃ, kathaṃ nu kho ahesumha atītamaddhānaṃ, kiṃ hutvā kiṃ ahesumha nu kho mayaṃ atītamaddhāna"’nti?</p>\r\n<p class="bodytext">no hetaṃ, bhante".</p>\r\n<p class="bodytext">api <a name="V1.0336"></a> nu tumhe, bhikkhave, evaṃ jānantā evaṃ passantā</p>\r\n','\r',char(13)),'\n',char(10)),'-406-407-');
INSERT INTO pages VALUES(3371,'mula_ma_01',332,replace(replace('<p class="bodytext">aparantaṃ <a name="M1.0332"></a> vā paṭidhāveyyātha – bhavissāma nu kho mayaṃ anāgatamaddhānaṃ, nanu kho bhavissāma anāgatamaddhānaṃ, kiṃ nu kho bhavissāma anāgatamaddhānaṃ, kathaṃ nu kho bhavissāma anāgatamaddhānaṃ, kiṃ hutvā kiṃ bhavissāma nu kho mayaṃ anāgatamaddhāna"nti?</p>\r\n<p class="bodytext">no hetaṃ, bhante".</p>\r\n<p class="bodytext">api nu tumhe, bhikkhave, evaṃ jānantā evaṃ passantā etarahi vā paccuppannamaddhānaṃ ajjhattaṃ kathaṃkathī assatha – ahaṃ nu khosmi, no nu khosmi, kiṃ nu khosmi, kathaṃ nu khosmi, ayaṃ nu kho satto kuto āgato, so kuhiṃgāmī bhavissatī"ti?</p>\r\n<p class="bodytext">no hetaṃ, bhante".</p>\r\n<p class="bodytext">api nu tumhe, bhikkhave, evaṃ jānantā evaṃ passantā evaṃ vadeyyātha – satthā no garu, satthugāravena ca mayaṃ evaṃ vademā"ti?</p>\r\n<p class="bodytext">no hetaṃ, bhante".</p>\r\n<p class="bodytext">api nu tumhe, bhikkhave, evaṃ jānantā evaṃ passantā evaṃ vadeyyātha – samaṇo evamāha, samaṇā ca nāma mayaṃ evaṃ vademā"ti?</p>\r\n<p class="bodytext">no hetaṃ, bhante".</p>\r\n<p class="bodytext">api nu tumhe, bhikkhave, evaṃ jānantā evaṃ passantā aññaṃ satthāraṃ <a name="T1.0487"></a> uddiseyyāthā"ti?</p>\r\n<p class="bodytext">no hetaṃ, bhante".</p>\r\n<p class="bodytext">api nu tumhe, bhikkhave, evaṃ jānantā evaṃ passantā yāni tāni puthusamaṇabrāhmaṇānaṃ vata kotūhalamaṅgalāni tāni sārato paccāgaccheyyāthā"ti?</p>\r\n<p class="bodytext">no <a name="V1.0337"></a> hetaṃ, bhante".</p>\r\n<p class="bodytext">nanu, bhikkhave, yadeva tumhākaṃ sāmaṃ ñātaṃ sāmaṃ diṭṭhaṃ sāmaṃ viditaṃ, tadeva tumhe vadethā"ti.</p>\r\n<p class="bodytext">evaṃ, bhante".</p>\r\n<p class="bodytext">sādhu, bhikkhave, upanītā kho me tumhe, bhikkhave, iminā sandiṭṭhikena dhammena akālikena ehipassikena opaneyyikena paccattaṃ veditabbena viññūhi. sandiṭṭhiko ayaṃ, bhikkhave, dhammo akāliko ehipassiko opaneyyiko paccattaṃ veditabbo viññūhi – iti yantaṃ vuttaṃ, idametaṃ paṭicca vutta"nti.</p>\r\n<p class="bodytext"><a name="para408"></a><a name="para408_mn1"></a><span class="paranum">408</span>. tiṇṇaṃ kho pana, bhikkhave, sannipātā gabbhassāvakkanti hoti. idha mātāpitaro ca sannipatitā honti, mātā ca na utunī hoti, gandhabbo ca na paccupaṭṭhito hoti, neva tāva gabbhassāvakkanti <a name="P1.0266"></a> hoti. idha mātāpitaro ca sannipatitā honti, mātā ca utunī hoti, gandhabbo ca na paccupaṭṭhito hoti, neva tāva gabbhassāvakkanti hoti. yato ca kho, bhikkhave, mātāpitaro ca sannipatitā honti, mātā ca utunī hoti, gandhabbo ca paccupaṭṭhito hoti – evaṃ tiṇṇaṃ sannipātā gabbhassāvakkanti hoti. tamenaṃ, bhikkhave, mātā nava vā dasa vā māse gabbhaṃ kucchinā pariharati mahatā saṃsayena garubhāraṃ <span class="note">[garumbhāraṃ (sī. pī.)]</span>. tamenaṃ, bhikkhave,</p>\r\n','\r',char(13)),'\n',char(10)),'-408-');
INSERT INTO pages VALUES(3372,'mula_ma_01',333,replace(replace('<p class="bodytext">mātā <a name="M1.0333"></a> navannaṃ vā <a name="T1.0488"></a> dasannaṃ vā māsānaṃ accayena vijāyati mahatā saṃsayena garubhāraṃ. tamenaṃ jātaṃ samānaṃ sakena lohitena poseti. lohitañhetaṃ, bhikkhave, ariyassa vinaye yadidaṃ mātuthaññaṃ. sa kho so, bhikkhave, kumāro vuddhimanvāya indriyānaṃ paripākamanvāya yāni tāni kumārakānaṃ kīḷāpanakāni tehi kīḷati, seyyathidaṃ – vaṅkakaṃ ghaṭikaṃ mokkhacikaṃ ciṅgulakaṃ pattāḷhakaṃ rathakaṃ dhanukaṃ. sa kho so, bhikkhave, kumāro vuddhimanvāya indriyānaṃ paripākamanvāya pañcahi kāmaguṇehi samappito samaṅgībhūto paricāreti – cakkhuviññeyyehi rūpehi iṭṭhehi kantehi manāpehi piyarūpehi kāmūpasaṃhitehi rajanīyehi, sotaviññeyyehi saddehi… ghānaviññeyyehi gandhehi… jivhāviññeyyehi rasehi… kāyaviññeyyehi phoṭṭhabbehi iṭṭhehi kantehi manāpehi piyarūpehi kāmūpasaṃhitehi rajanīyehi.</p>\r\n<p class="bodytext"><a name="para409"></a><a name="para409_mn1"></a><span class="paranum">409</span>. so <a name="V1.0338"></a> cakkhunā rūpaṃ disvā piyarūpe rūpe sārajjati, appiyarūpe rūpe byāpajjati, anupaṭṭhitakāyasati ca viharati parittacetaso. tañca cetovimuttiṃ paññāvimuttiṃ yathābhūtaṃ nappajānāti – yatthassa te pāpakā akusalā dhammā aparisesā nirujjhanti. so evaṃ anurodhavirodhaṃ samāpanno yaṃ kiñci vedanaṃ vedeti sukhaṃ vā dukkhaṃ vā adukkhamasukhaṃ vā, so taṃ vedanaṃ abhinandati abhivadati ajjhosāya tiṭṭhati. tassa taṃ vedanaṃ abhinandato abhivadato ajjhosāya tiṭṭhato uppajjati nandī<a name="T1.0489"></a>. yā vedanāsu nandī tadupādānaṃ, tassupādānapaccayā bhavo, bhavapaccayā jāti, jātipaccayā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā sambhavanti. evametassa kevalassa dukkhakkhandhassa samudayo hoti. sotena saddaṃ sutvā…pe… ghānena gandhaṃ ghāyitvā…pe… jivhāya rasaṃ sāyitvā…pe… kāyena phoṭṭhabbaṃ phusitvā…pe… manasā dhammaṃ viññāya piyarūpe dhamme <a name="P1.0267"></a> sārajjati, appiyarūpe dhamme byāpajjati, anupaṭṭhitakāyasati ca viharati parittacetaso. tañca cetovimuttiṃ paññāvimuttiṃ yathābhūtaṃ nappajānāti – yatthassa te pāpakā akusalā dhammā aparisesā nirujjhanti. so evaṃ anurodhavirodhaṃ samāpanno yaṃ kiñci vedanaṃ vedeti sukhaṃ vā dukkhaṃ vā adukkhamasukhaṃ vā, so taṃ vedanaṃ abhinandati abhivadati ajjhosāya tiṭṭhati. tassa taṃ vedanaṃ abhinandato abhivadato ajjhosāya tiṭṭhato uppajjati nandī. yā vedanāsu nandī tadupādānaṃ,</p>\r\n','\r',char(13)),'\n',char(10)),'-409-');
INSERT INTO pages VALUES(3373,'mula_ma_01',334,replace(replace('<p class="bodytext">tassupādānapaccayā <a name="M1.0334"></a> bhavo, bhavapaccayā jāti, jātipaccayā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā sambhavanti. evametassa kevalassa dukkhakkhandhassa samudayo hoti.</p>\r\n<p class="bodytext"><a name="para410"></a><a name="para410_mn1"></a><span class="paranum">410</span>. idha, bhikkhave, tathāgato loke uppajjati arahaṃ sammāsambuddho vijjācaraṇasampanno sugato lokavidū anuttaro purisadammasārathi satthā devamanussānaṃ buddho bhagavā. so imaṃ lokaṃ sadevakaṃ samārakaṃ sabrahmakaṃ sassamaṇabrāhmaṇiṃ pajaṃ sadevamanussaṃ sayaṃ abhiññā <a name="T1.0490"></a> sacchikatvā pavedeti. so dhammaṃ deseti ādikalyāṇaṃ majjhekalyāṇaṃ pariyosānakalyāṇaṃ sātthaṃ sabyañjanaṃ, kevalaparipuṇṇaṃ parisuddhaṃ brahmacariyaṃ pakāseti. taṃ dhammaṃ suṇāti gahapati vā gahapatiputto vā aññatarasmiṃ vā kule paccājāto. so taṃ dhammaṃ sutvā tathāgate saddhaṃ paṭilabhati. so tena saddhāpaṭilābhena samannāgato iti paṭisañcikkhati – ‘sambādho gharāvāso rajāpatho, abbhokāso pabbajjā. nayidaṃ sukaraṃ agāraṃ ajjhāvasatā ekantaparipuṇṇaṃ ekantaparisuddhaṃ saṅkhalikhitaṃ brahmacariyaṃ carituṃ. yaṃnūnāhaṃ kesamassuṃ <a name="V1.0339"></a> ohāretvā, kāsāyāni vatthāni acchādetvā, agārasmā anagāriyaṃ pabbajeyya"’nti. so aparena samayena appaṃ vā bhogakkhandhaṃ pahāya, mahantaṃ vā bhogakkhandhaṃ pahāya, appaṃ vā ñātiparivaṭṭaṃ pahāya, mahantaṃ vā ñātiparivaṭṭaṃ pahāya, kesamassuṃ ohāretvā, kāsāyāni vatthāni acchādetvā, agārasmā anagāriyaṃ pabbajati.</p>\r\n<p class="bodytext"><a name="para411"></a><a name="para411_mn1"></a><span class="paranum">411</span>. so evaṃ pabbajito samāno bhikkhūnaṃ sikkhāsājīvasamāpanno pāṇātipātaṃ pahāya pāṇātipātā paṭivirato hoti, nihitadaṇḍo nihitasattho lajjī dayāpanno sabbapāṇabhūtahitānukampī viharati.</p>\r\n<p class="bodytext">adinnādānaṃ pahāya adinnādānā paṭivirato <a name="P1.0268"></a> hoti, dinnādāyī dinnapāṭikaṅkhī athenena sucibhūtena attanā viharati.</p>\r\n<p class="bodytext">abrahmacariyaṃ pahāya brahmacārī hoti, ārācārī virato methunā gāmadhammā.</p>\r\n','\r',char(13)),'\n',char(10)),'-410-411-');
INSERT INTO pages VALUES(3374,'mula_ma_01',335,replace(replace('<p class="bodytext">musāvādaṃ <a name="M1.0335"></a> pahāya musāvādā paṭivirato hoti, saccavādī saccasandho <a name="T1.0491"></a> theto paccayiko avisaṃvādako lokassa.</p>\r\n<p class="bodytext">pisuṇaṃ vācaṃ pahāya pisuṇāya vācāya paṭivirato hoti – ito sutvā na amutra akkhātā imesaṃ bhedāya, amutra vā sutvā na imesaṃ akkhātā amūsaṃ bhedāya. iti bhinnānaṃ vā sandhātā, sahitānaṃ vā anuppadātā samaggārāmo samaggarato samagganandī, samaggakaraṇiṃ vācaṃ bhāsitā hoti.</p>\r\n<p class="bodytext">pharusaṃ vācaṃ pahāya pharusāya vācāya paṭivirato hoti – yā sā vācā nelā kaṇṇasukhā pemanīyā hadayaṅgamā porī bahujanakantā bahujanamanāpā tathārūpiṃ vācaṃ bhāsitā hoti.</p>\r\n<p class="bodytext">samphappalāpaṃ pahāya samphappalāpā paṭivirato hoti, kālavādī bhūtavādī atthavādī dhammavādī vinayavādī, nidhānavatiṃ vācaṃ bhāsitā kālena, sāpadesaṃ pariyantavatiṃ atthasaṃhitaṃ.</p>\r\n<p class="bodytext">so <a name="V1.0340"></a> bījagāmabhūtagāmasamārambhā paṭivirato hoti, ekabhattiko hoti rattūparato, virato vikālabhojanā. naccagītavāditavisūkadassanā paṭivirato hoti, mālāgandhavilepanadhāraṇamaṇḍanavibhūsanaṭṭhānā paṭivirato hoti, uccāsayanamahāsayanā paṭivirato hoti, jātarūparajatapaṭiggahaṇā paṭivirato hoti, āmakadhaññapaṭiggahaṇā paṭivirato hoti, āmakamaṃsapaṭiggahaṇā paṭivirato hoti, itthikumārikapaṭiggahaṇā paṭivirato hoti, dāsidāsapaṭiggahaṇā paṭivirato hoti, ajeḷakapaṭiggahaṇā paṭivirato hoti, kukkuṭasūkarapaṭiggahaṇā paṭivirato hoti, hatthigavāssavaḷavapaṭiggahaṇā paṭivirato <a name="T1.0492"></a> hoti, khettavatthupaṭiggahaṇā paṭivirato hoti, dūteyyapahiṇagamanānuyogā paṭivirato hoti, kayavikkayā paṭivirato hoti, tulākūṭakaṃsakūṭamānakūṭā paṭivirato hoti, ukkoṭanavañcana-nikati-sāciyogā paṭivirato hoti, chedana-vadhabandhanaviparāmosa-ālopa-sahasākārā paṭivirato hoti <span class="note">[passa ma. ni. 1.293 cūḷahatthipadopame]</span>.</p>\r\n<p class="bodytext">so santuṭṭho hoti kāyaparihārikena cīvarena kucchiparihārikena piṇḍapātena. so yena yeneva pakkamati samādāyeva</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3375,'mula_ma_01',336,replace(replace('<p class="bodytext">pakkamati<a name="M1.0336"></a>. seyyathāpi nāma pakkhī sakuṇo yena yeneva ḍeti sapattabhārova ḍeti, evameva bhikkhu santuṭṭho hoti kāyaparihārikena cīvarena, kucchiparihārikena piṇḍapātena. so yena yeneva pakkamati samādāyeva pakkamati. so iminā ariyena <a name="P1.0269"></a> sīlakkhandhena samannāgato ajjhattaṃ anavajjasukhaṃ paṭisaṃvedeti.</p>\r\n<p class="bodytext">so cakkhunā rūpaṃ disvā na nimittaggāhī hoti nānubyañjanaggāhī. yatvādhikaraṇamenaṃ cakkhundriyaṃ asaṃvutaṃ viharantaṃ abhijjhādomanassā pāpakā akusalā dhammā anvāssaveyyuṃ tassa saṃvarāya paṭipajjati, rakkhati cakkhundriyaṃ, cakkhundriye saṃvaraṃ āpajjati. sotena saddaṃ sutvā…pe… ghānena gandhaṃ ghāyitvā…pe… jivhāya rasaṃ sāyitvā…pe… kāyena phoṭṭhabbaṃ phusitvā…pe… manasā dhammaṃ viññāya na nimittaggāhī hoti nānubyañjanaggāhī. yatvādhikaraṇamenaṃ manindriyaṃ asaṃvutaṃ viharantaṃ abhijjhādomanassā pāpakā akusalā dhammā anvāssaveyyuṃ tassa saṃvarāya paṭipajjati, rakkhati manindriyaṃ manindriye saṃvaraṃ āpajjati. so iminā ariyena indriyasaṃvarena samannāgato ajjhattaṃ abyāsekasukhaṃ <a name="T1.0493"></a> paṭisaṃvedeti.</p>\r\n<p class="bodytext">so abhikkante paṭikkante sampajānakārī hoti, ālokite vilokite sampajānakārī hoti<a name="V1.0341"></a>, samiñjite pasārite sampajānakārī hoti, saṅghāṭipattacīvaradhāraṇe sampajānakārī hoti, asite pīte khāyite sāyite sampajānakārī hoti, uccārapassāvakamme sampajānakārī hoti, gate ṭhite nisinne sutte jāgarite bhāsite tuṇhībhāve sampajānakārī hoti.</p>\r\n<p class="bodytext"><a name="para412"></a><a name="para412_mn1"></a><span class="paranum">412</span>. so iminā ca ariyena sīlakkhandhena samannāgato, (imāya ca ariyāya santuṭṭhiyā samannāgato) <span class="note">[passa ma. ni. 1.296 cūḷahatthipadopame]</span>, iminā ca ariyena indriyasaṃvarena samannāgato, iminā ca ariyena satisampajaññena samannāgato, vivittaṃ senāsanaṃ bhajati – araññaṃ rukkhamūlaṃ pabbataṃ kandaraṃ giriguhaṃ susānaṃ vanapatthaṃ abbhokāsaṃ palālapuñjaṃ. so pacchābhattaṃ piṇḍapātapaṭikkanto nisīdati pallaṅkaṃ ābhujitvā, ujuṃ kāyaṃ paṇidhāya, parimukhaṃ satiṃ upaṭṭhapetvā. so abhijjhaṃ loke pahāya vigatābhijjhena cetasā viharati, abhijjhāya cittaṃ parisodheti, byāpādapadosaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-412-');
INSERT INTO pages VALUES(3376,'mula_ma_01',337,replace(replace('<p class="bodytext">pahāya <a name="M1.0337"></a> abyāpannacitto viharati, sabbapāṇabhūtahitānukampī, byāpādapadosā cittaṃ parisodheti, thīnamiddhaṃ pahāya vigatathīnamiddho viharati ālokasaññī, sato sampajāno, thīnamiddhā cittaṃ parisodheti, uddhaccakukkuccaṃ pahāya anuddhato viharati ajjhattaṃ vūpasantacitto, uddhaccakukkuccā cittaṃ parisodheti, vicikicchaṃ pahāya tiṇṇavicikiccho viharati akathaṃkathī kusalesu dhammesu, vicikicchāya cittaṃ parisodheti.</p>\r\n<p class="bodytext"><a name="para413"></a><a name="para413_mn1"></a><span class="paranum">413</span>. so <a name="T1.0494"></a><a name="P1.0270"></a> ime pañca nīvaraṇe pahāya cetaso upakkilese paññāya dubbalīkaraṇe, vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati. puna caparaṃ, bhikkhave, bhikkhu vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ…pe… tatiyaṃ jhānaṃ…pe… catutthaṃ jhānaṃ upasampajja viharati.</p>\r\n<p class="bodytext"><a name="para414"></a><a name="para414_mn1"></a><span class="paranum">414</span>. so cakkhunā rūpaṃ disvā piyarūpe rūpe na sārajjati, appiyarūpe rūpe na byāpajjati, upaṭṭhitakāyasati ca viharati appamāṇacetaso. tañca cetovimuttiṃ paññāvimuttiṃ yathābhūtaṃ pajānāti – yatthassa te pāpakā akusalā dhammā aparisesā nirujjhanti. so evaṃ anurodhavirodhavippahīno <a name="V1.0342"></a> yaṃ kiñci vedanaṃ vedeti, sukhaṃ vā dukkhaṃ vā adukkhamasukhaṃ vā, so taṃ vedanaṃ nābhinandati nābhivadati nājjhosāya tiṭṭhati. tassa taṃ vedanaṃ anabhinandato anabhivadato anajjhosāya tiṭṭhato yā vedanāsu nandī sā nirujjhati. tassa nandīnirodhā upādānanirodho, upādānanirodhā bhavanirodho, bhavanirodhā jātinirodho, jātinirodhā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā nirujjhanti. evametassa kevalassa dukkhakkhandhassa nirodho hoti. sotena saddaṃ sutvā…pe… ghānena gandhaṃ ghāyitvā…pe… jivhāya rasaṃ sāyitvā…pe… kāyena phoṭṭhabbaṃ phusitvā…pe… manasā dhammaṃ viññāya piyarūpe <a name="T1.0495"></a> dhamme na sārajjati, appiyarūpe dhamme na byāpajjati, upaṭṭhitakāyasati ca viharati appamāṇacetaso, tañca cetovimuttiṃ paññāvimuttiṃ yathābhūtaṃ pajānāti – yatthassa te pāpakā akusalā dhammā aparisesā nirujjhanti. so evaṃ anurodhavirodhavippahīno yaṃ kiñci vedanaṃ vedeti, sukhaṃ vā dukkhaṃ vā adukkhamasukhaṃ vā, so taṃ vedanaṃ nābhinandati</p>\r\n','\r',char(13)),'\n',char(10)),'-413-414-');
INSERT INTO pages VALUES(3377,'mula_ma_01',338,replace(replace('<p class="bodytext">nābhivadati <a name="M1.0338"></a> nājjhosāya tiṭṭhati. tassa taṃ vedanaṃ anabhinandato anabhivadato anajjhosāya tiṭṭhato yā vedanāsu nandī sā nirujjhati. tassa nandīnirodhā upādānanirodho, upādānanirodhā bhavanirodho, bhavanirodhā jātinirodho, jātinirodhā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā nirujjhanti. evametassa kevalassa dukkhakkhandhassa nirodho hoti. imaṃ kho me tumhe, bhikkhave, saṃkhittena taṇhāsaṅkhayavimuttiṃ dhāretha, sātiṃ pana bhikkhuṃ <a name="P1.0271"></a> kevaṭṭaputtaṃ mahātaṇhājālataṇhāsaṅghāṭappaṭimukka"nti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">mahātaṇhāsaṅkhayasuttaṃ niṭṭhitaṃ aṭṭhamaṃ.</p>\r\n<p class="subhead">9. mahāassapurasuttaṃ</p>\r\n<p class="bodytext"><a name="para415"></a><a name="para415_mn1"></a><span class="paranum">415</span>. evaṃ <a name="T1.0496"></a><a name="V1.0343"></a> me sutaṃ – ekaṃ samayaṃ bhagavā aṅgesu viharati assapuraṃ nāma aṅgānaṃ nigamo. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext">samaṇā samaṇāti vo, bhikkhave, jano sañjānāti. tumhe ca pana ‘ke tumhe’ti puṭṭhā samānā ‘samaṇāmhā’ti paṭijānātha, tesaṃ vo, bhikkhave, evaṃsamaññānaṃ sataṃ evaṃpaṭiññānaṃ sataṃ ‘ye dhammā samaṇakaraṇā ca brāhmaṇakaraṇā ca te dhamme samādāya vattissāma, evaṃ no ayaṃ amhākaṃ samaññā ca saccā bhavissati paṭiññā ca bhūtā. yesañca mayaṃ cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhāraṃ paribhuñjāma, tesaṃ te kārā amhesu mahapphalā bhavissanti mahānisaṃsā, amhākañcevāyaṃ pabbajjā avañjhā bhavissati saphalā saudrayā’ti. evañhi vo, bhikkhave, sikkhitabbaṃ.</p>\r\n<p class="bodytext"><a name="para416"></a><a name="para416_mn1"></a><span class="paranum">416</span>. katame ca, bhikkhave, dhammā samaṇakaraṇā ca brāhmaṇakaraṇā ca? ‘hirottappena samannāgatā bhavissāmā’ti evañhi vo, bhikkhave, sikkhitabbaṃ. siyā kho pana, bhikkhave, tumhākaṃ evamassa – ‘hirottappenamha</p>\r\n','\r',char(13)),'\n',char(10)),'-415-416-');
INSERT INTO pages VALUES(3378,'mula_ma_01',339,replace(replace('<p class="bodytext">samannāgatā<a name="M1.0339"></a>, alamettāvatā katamettāvatā, anuppatto no sāmaññattho, natthi no kiñci uttariṃ karaṇīya’nti tāvatakeneva tuṭṭhiṃ āpajjeyyātha. ārocayāmi vo, bhikkhave<a name="T1.0497"></a>, paṭivedayāmi vo, bhikkhave – ‘mā vo sāmaññatthikānaṃ sataṃ sāmaññattho parihāyi, sati uttariṃ karaṇīye’.</p>\r\n<p class="bodytext"><a name="para417"></a><a name="para417_mn1"></a><span class="paranum">417</span>. kiñca, bhikkhave, uttariṃ karaṇīyaṃ? ‘parisuddho no kāyasamācāro <a name="P1.0272"></a> bhavissati uttāno vivaṭo na ca chiddavā saṃvuto ca. tāya ca pana parisuddhakāyasamācāratāya nevattānukkaṃsessāma na paraṃ vambhessāmā’ti <span class="note">[nevattānukkaṃsissāma na paraṃ vambhissāmāti (sabbattha)]</span> evañhi vo, bhikkhave, sikkhitabbaṃ. siyā kho pana, bhikkhave, tumhākaṃ evamassa – ‘hirottappenamha samannāgatā, parisuddho no kāyasamācāro, alamettāvatā katamettāvatā, anuppatto no sāmaññattho, natthi no <a name="V1.0344"></a> kiñci uttariṃ karaṇīya’nti tāvatakeneva tuṭṭhiṃ āpajjeyyātha. ārocayāmi vo, bhikkhave, paṭivedayāmi vo, bhikkhave – ‘mā vo sāmaññatthikānaṃ sataṃ sāmaññattho parihāyi, sati uttariṃ karaṇīye’.</p>\r\n<p class="bodytext"><a name="para418"></a><a name="para418_mn1"></a><span class="paranum">418</span>. kiñca, bhikkhave, uttariṃ karaṇīyaṃ? ‘parisuddho no vacīsamācāro bhavissati uttāno vivaṭo na ca chiddavā saṃvuto ca. tāya ca pana parisuddhavacīsamācāratāya nevattānukkaṃsessāma na paraṃ vambhessāmā’ti evañhi vo, bhikkhave, sikkhitabbaṃ. siyā kho pana, bhikkhave, tumhākaṃ evamassa – ‘hirottappenamha samannāgatā, parisuddho no kāyasamācāro, parisuddho vacīsamācāro, alamettāvatā katamettāvatā, anuppatto no sāmaññattho, natthi no kiñci uttariṃ karaṇīya’nti tāvatakeneva tuṭṭhiṃ āpajjeyyātha. ārocayāmi vo<a name="T1.0498"></a>, bhikkhave, paṭivedayāmi vo, bhikkhave – ‘mā vo sāmaññatthikānaṃ sataṃ sāmaññattho parihāyi, sati uttariṃ karaṇīye’.</p>\r\n<p class="bodytext"><a name="para419"></a><a name="para419_mn1"></a><span class="paranum">419</span>. kiñca, bhikkhave, uttariṃ karaṇīyaṃ? ‘parisuddho no manosamācāro bhavissati uttāno vivaṭo na ca chiddavā saṃvuto ca. tāya ca</p>\r\n','\r',char(13)),'\n',char(10)),'-417-418-419-');
INSERT INTO pages VALUES(3379,'mula_ma_01',340,replace(replace('<p class="bodytext">pana <a name="M1.0340"></a> parisuddhamanosamācāratāya nevattānukkaṃsessāma na paraṃ vambhessāmā’ti evañhi vo, bhikkhave, sikkhitabbaṃ. siyā kho pana, bhikkhave, tumhākaṃ evamassa – ‘hirottappenamha samannāgatā, parisuddho no kāyasamācāro, parisuddho vacīsamācāro, parisuddho manosamācāro, alamettāvatā katamettāvatā, anuppatto no sāmaññattho, natthi no kiñci uttariṃ karaṇīya’nti tāvatakeneva tuṭṭhiṃ āpajjeyyātha. ārocayāmi vo, bhikkhave, paṭivedayāmi vo, bhikkhave – ‘mā vo sāmaññatthikānaṃ sataṃ sāmaññattho parihāyi, sati uttariṃ karaṇīye’.</p>\r\n<p class="bodytext"><a name="para420"></a><a name="para420_mn1"></a><span class="paranum">420</span>. kiñca, bhikkhave, uttariṃ karaṇīyaṃ? ‘parisuddho no ājīvo bhavissati uttāno vivaṭo na ca chiddavā saṃvuto ca. tāya ca pana parisuddhājīvatāya nevattānukkaṃsessāma na paraṃ vambhessāmā’ti evañhi vo, bhikkhave, sikkhitabbaṃ. siyā kho pana, bhikkhave, tumhākaṃ evamassa – ‘hirottappenamha samannāgatā, parisuddho no kāyasamācāro, parisuddho vacīsamācāro, parisuddho manosamācāro, parisuddho ājīvo, alamettāvatā <a name="P1.0273"></a> katamettāvatā, anuppatto no sāmaññattho<a name="T1.0499"></a>, natthi no kiñci uttariṃ karaṇīya’nti <a name="V1.0345"></a> tāvatakeneva tuṭṭhiṃ āpajjeyyātha. ārocayāmi vo, bhikkhave, paṭivedayāmi vo, bhikkhave – ‘mā vo sāmaññatthikānaṃ sataṃ sāmaññattho parihāyi, sati uttariṃ karaṇīye’.</p>\r\n<p class="bodytext"><a name="para421"></a><a name="para421_mn1"></a><span class="paranum">421</span>. kiñca, bhikkhave, uttariṃ karaṇīyaṃ? ‘indriyesu guttadvārā bhavissāma, cakkhunā rūpaṃ disvā na nimittaggāhī nānubyañjanaggāhī. yatvādhikaraṇamenaṃ cakkhundriyaṃ asaṃvutaṃ viharantaṃ abhijjhādomanassā pāpakā akusalā dhammā anvāssaveyyuṃ, tassa saṃvarāya paṭipajjissāma, rakkhissāma cakkhundriyaṃ, cakkhundriye saṃvaraṃ āpajjissāma. sotena saddaṃ sutvā…pe… ghānena gandhaṃ ghāyitvā…pe… jivhāya rasaṃ sāyitvā…pe… kāyena phoṭṭhabbaṃ phusitvā…pe… manasā dhammaṃ viññāya na nimittaggāhī nānubyañjanaggāhī. yatvādhikaraṇamenaṃ manindriyaṃ asaṃvutaṃ viharantaṃ abhijjhādomanassā pāpakā akusalā dhammā anvāssaveyyuṃ, tassa saṃvarāya paṭipajjissāma, rakkhissāma manindriyaṃ, manindriye saṃvaraṃ āpajjissāmā’ti evañhi vo, bhikkhave, sikkhitabbaṃ. siyā kho pana, bhikkhave, tumhākaṃ evamassa –</p>\r\n','\r',char(13)),'\n',char(10)),'-420-421-');
INSERT INTO pages VALUES(3380,'mula_ma_01',341,replace(replace('<p class="bodytext">‘hirottappenamha <a name="M1.0341"></a> samannāgatā, parisuddho no kāyasamācāro, parisuddho vacīsamācāro, parisuddho manosamācāro, parisuddho ājīvo, indriyesumha guttadvārā, alamettāvatā katamettāvatā, anuppatto no sāmaññattho, natthi no kiñci uttariṃ karaṇīya’nti tāvatakeneva tuṭṭhiṃ <a name="T1.0500"></a> āpajjeyyātha. ārocayāmi vo, bhikkhave, paṭivedayāmi vo, bhikkhave – ‘mā vo sāmaññatthikānaṃ sataṃ sāmaññattho parihāyi, sati uttariṃ karaṇīye’.</p>\r\n<p class="bodytext"><a name="para422"></a><a name="para422_mn1"></a><span class="paranum">422</span>. kiñca, bhikkhave, uttariṃ karaṇīyaṃ? ‘bhojane mattaññuno bhavissāma, paṭisaṅkhā yoniso āhāraṃ āharissāma, neva davāya na madāya na maṇḍanāya na vibhūsanāya yāvadeva imassa kāyassa ṭhitiyā yāpanāya, vihiṃsūparatiyā, brahmacariyānuggahāya, iti purāṇañca vedanaṃ paṭihaṅkhāma navañca vedanaṃ na uppādessāma, yātrā ca no bhavissati, anavajjatā ca, phāsu vihāro cā’ti evañhi vo, bhikkhave, sikkhitabbaṃ. siyā kho pana, bhikkhave, tumhākaṃ evamassa – ‘hirottappenamha samannāgatā, parisuddho no kāyasamācāro, parisuddho vacīsamācāro, parisuddho manosamācāro, parisuddho ājīvo, indriyesumha guttadvārā, bhojane mattaññuno, alamettāvatā katamettāvatā, anuppatto no sāmaññattho, natthi no kiñci uttariṃ karaṇīya’nti tāvatakeneva tuṭṭhiṃ āpajjeyyātha. ārocayāmi vo, bhikkhave, paṭivedayāmi vo, bhikkhave – ‘mā vo, sāmaññatthikānaṃ sataṃ sāmaññattho parihāyi sati uttariṃ karaṇīye’.</p>\r\n<p class="bodytext"><a name="para423"></a><a name="para423_mn1"></a><span class="paranum">423</span>. kiñca<a name="V1.0346"></a>, bhikkhave, uttariṃ karaṇīyaṃ? ‘jāgariyaṃ anuyuttā bhavissāma, divasaṃ caṅkamena nisajjāya āvaraṇīyehi dhammehi cittaṃ parisodhessāma. rattiyā paṭhamaṃ yāmaṃ caṅkamena <a name="P1.0274"></a> nisajjāya āvaraṇīyehi dhammehi <a name="T1.0501"></a> cittaṃ parisodhessāma. rattiyā majjhimaṃ yāmaṃ dakkhiṇena passena sīhaseyyaṃ kappessāma pāde pādaṃ accādhāya, sato sampajāno uṭṭhānasaññaṃ manasi karitvā. rattiyā pacchimaṃ yāmaṃ paccuṭṭhāya caṅkamena nisajjāya āvaraṇīyehi dhammehi cittaṃ parisodhessāmā’ti, evañhi vo, bhikkhave, sikkhitabbaṃ. siyā kho pana, bhikkhave, tumhākaṃ evamassa – ‘hirottappenamha samannāgatā, parisuddho no kāyasamācāro, parisuddho vacīsamācāro,</p>\r\n','\r',char(13)),'\n',char(10)),'-422-423-');
INSERT INTO pages VALUES(3381,'mula_ma_01',342,replace(replace('<p class="bodytext">parisuddho <a name="M1.0342"></a> manosamācāro, parisuddho ājīvo, indriyesumha guttadvārā, bhojane mattaññuno, jāgariyaṃ anuyuttā, alamettāvatā katamettāvatā, anuppatto no sāmaññattho, natthi no kiñci uttariṃ karaṇīya’nti, tāvatakeneva tuṭṭhiṃ āpajjeyyātha. ārocayāmi vo, bhikkhave, paṭivedayāmi vo, bhikkhave – ‘mā vo, sāmaññatthikānaṃ sataṃ sāmaññattho parihāyi sati uttariṃ karaṇīye’.</p>\r\n<p class="bodytext"><a name="para424"></a><a name="para424_mn1"></a><span class="paranum">424</span>. kiñca, bhikkhave, uttariṃ karaṇīyaṃ? ‘satisampajaññena samannāgatā bhavissāma, abhikkante paṭikkante sampajānakārī, ālokite vilokite sampajānakārī, samiñjite pasārite sampajānakārī, saṅghāṭipattacīvaradhāraṇe sampajānakārī, asite pīte khāyite sāyite sampajānakārī, uccārapassāvakamme sampajānakārī, gate ṭhite nisinne sutte jāgarite bhāsite tuṇhībhāve sampajānakārī’ti, evañhi vo, bhikkhave, sikkhitabbaṃ. siyā kho pana, bhikkhave, tumhākaṃ evamassa <a name="T1.0502"></a> – ‘hirottappenamha samannāgatā, parisuddho no kāyasamācāro, parisuddho vacīsamācāro, parisuddho manosamācāro, parisuddho ājīvo, indriyesumha guttadvārā, bhojane mattaññuno, jāgariyaṃ anuyuttā, satisampajaññena samannāgatā, alamettāvatā katamettāvatā, anuppatto no sāmaññattho, natthi no kiñci uttariṃ karaṇīya’nti tāvatakeneva tuṭṭhiṃ āpajjeyyātha. ārocayāmi vo, bhikkhave, paṭivedayāmi vo, bhikkhave – ‘mā vo, sāmaññatthikānaṃ sataṃ sāmaññattho parihāyi sati uttariṃ karaṇīye’.</p>\r\n<p class="bodytext"><a name="para425"></a><a name="para425_mn1"></a><span class="paranum">425</span>. kiñca, bhikkhave, uttariṃ karaṇīyaṃ? idha, bhikkhave, bhikkhu vivittaṃ senāsanaṃ bhajati – araññaṃ rukkhamūlaṃ pabbataṃ kandaraṃ giriguhaṃ susānaṃ vanappatthaṃ abbhokāsaṃ palālapuñjaṃ. so pacchābhattaṃ piṇḍapātapaṭikkanto nisīdati pallaṅkaṃ ābhujitvā, ujuṃ kāyaṃ paṇidhāya <a name="V1.0347"></a> parimukhaṃ satiṃ upaṭṭhapetvā. so abhijjhaṃ loke pahāya vigatābhijjhena cetasā viharati, abhijjhāya cittaṃ parisodheti, byāpādapadosaṃ pahāya abyāpannacitto viharati, sabbapāṇabhūtahitānukampī<a name="P1.0275"></a>, byāpādapadosā cittaṃ parisodheti, thīnamiddhaṃ pahāya vigatathīnamiddho viharati, ālokasaññī sato sampajāno, thīnamiddhā cittaṃ parisodheti, uddhaccakukkuccaṃ pahāya anuddhato</p>\r\n','\r',char(13)),'\n',char(10)),'-424-425-');
INSERT INTO pages VALUES(3382,'mula_ma_01',343,replace(replace('<p class="bodytext">viharati<a name="M1.0343"></a>, ajjhattaṃ vūpasantacitto, uddhaccakukkuccā cittaṃ parisodheti, vicikicchaṃ pahāya tiṇṇavicikiccho viharati, akathaṃkathī kusalesu dhammesu, vicikicchāya cittaṃ parisodheti.</p>\r\n<p class="bodytext"><a name="para426"></a><a name="para426_mn1"></a><span class="paranum">426</span>. seyyathāpi<a name="T1.0503"></a>, bhikkhave, puriso iṇaṃ ādāya kammante payojeyya. tassa te kammantā samijjheyyuṃ <span class="note">[sampajjeyyuṃ (syā. kaṃ. ka.)]</span>. so yāni ca porāṇāni iṇamūlāni tāni ca byantī <span class="note">[byantiṃ (ka.), byanti (pī.)]</span> kareyya, siyā cassa uttariṃ avasiṭṭhaṃ dārabharaṇāya. tassa evamassa – ‘ahaṃ kho pubbe iṇaṃ ādāya kammante payojesiṃ, tassa me te kammantā samijjhiṃsu. sohaṃ yāni ca porāṇāni iṇamūlāni tāni ca byantī akāsiṃ, atthi ca me uttariṃ avasiṭṭhaṃ dārabharaṇāyā’ti. so tatonidānaṃ labhetha pāmojjaṃ, adhigaccheyya somanassaṃ.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, puriso ābādhiko assa dukkhito bāḷhagilāno, bhattañcassa nacchādeyya, na cassa kāye balamattā. so aparena samayena tamhā ābādhā mucceyya, bhattañcassa chādeyya, siyā cassa kāye balamattā. tassa evamassa – ‘ahaṃ kho pubbe ābādhiko ahosiṃ dukkhito bāḷhagilāno, bhattañca me nacchādesi, na ca me āsi kāye balamattā, somhi etarahi tamhā ābādhā mutto, bhattañca me chādeti, atthi ca me kāye balamattā’ti. so tatonidānaṃ labhetha pāmojjaṃ, adhigaccheyya somanassaṃ.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, puriso bandhanāgāre baddho assa. so aparena samayena tamhā bandhanā mucceyya sotthinā abbhayena <span class="note">[abyayena (sī. pī.)]</span>, na cassa kiñci bhogānaṃ vayo. tassa evamassa – ‘ahaṃ kho pubbe bandhanāgāre baddho ahosiṃ, somhi etarahi <a name="T1.0504"></a> tamhā bandhanā mutto, sotthinā abbhayena, natthi ca me kiñci bhogānaṃ vayo’ti. so tatonidānaṃ labhetha pāmojjaṃ, adhigaccheyya somanassaṃ.</p>\r\n<p class="bodytext">seyyathāpi<a name="V1.0348"></a>, bhikkhave, puriso dāso assa anattādhīno parādhīno na yenakāmaṃgamo. so aparena samayena tamhā dāsabyā mucceyya attādhīno aparādhīno bhujisso yenakāmaṃgamo. tassa evamassa – ‘ahaṃ kho pubbe dāso</p>\r\n','\r',char(13)),'\n',char(10)),'-426-');
INSERT INTO pages VALUES(3383,'mula_ma_01',344,replace(replace('<p class="bodytext">ahosiṃ <a name="M1.0344"></a> anattādhīno parādhīno na yenakāmaṃgamo, somhi etarahi tamhā dāsabyā mutto attādhīno aparādhīno <a name="P1.0276"></a> bhujisso yenakāmaṃgamo’ti. so tatonidānaṃ labhetha pāmojjaṃ, adhigaccheyya somanassaṃ.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, puriso sadhano sabhogo kantāraddhānamaggaṃ paṭipajjeyya <span class="note">[sīlakkhandhavaggapāḷiyā kiñci visadisaṃ]</span>. so aparena samayena tamhā kantārā nitthareyya sotthinā abbhayena, na cassa kiñci bhogānaṃ vayo. tassa evamassa – ‘ahaṃ kho pubbe sadhano sabhogo kantāraddhānamaggaṃ paṭipajjiṃ. somhi etarahi tamhā kantārā nitthiṇṇo sotthinā abbhayena, natthi ca me kiñci bhogānaṃ vayo’ti. so tatonidānaṃ labhetha pāmojjaṃ, adhigaccheyya somanassaṃ.</p>\r\n<p class="bodytext">evameva kho, bhikkhave, bhikkhu yathā iṇaṃ yathā rogaṃ yathā bandhanāgāraṃ yathā dāsabyaṃ yathā kantāraddhānamaggaṃ, ime pañca nīvaraṇe appahīne attani samanupassati. seyyathāpi, bhikkhave, āṇaṇyaṃ yathā ārogyaṃ yathā bandhanāmokkhaṃ yathā bhujissaṃ yathā khemantabhūmiṃ, evameva bhikkhu ime pañca nīvaraṇe <a name="T1.0505"></a> pahīne attani samanupassati.</p>\r\n<p class="bodytext"><a name="para427"></a><a name="para427_mn1"></a><span class="paranum">427</span>. so ime pañca nīvaraṇe pahāya cetaso upakkilese paññāya dubbalīkaraṇe, vivicceva kāmehi vivicca akusalehi dhammehi, savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati. so imameva kāyaṃ vivekajena pītisukhena abhisandeti parisandeti paripūreti parippharati, nāssa kiñci sabbāvato kāyassa vivekajena pītisukhena apphuṭaṃ hoti. seyyathāpi, bhikkhave, dakkho nhāpako <span class="note">[nahāpako (sī. syā. kaṃ. pī.)]</span> vā nhāpakantevāsī vā kaṃsathāle nhānīyacuṇṇāni <span class="note">[nahānīyacuṇṇāni (sī. syā. kaṃ. pī.)]</span> ākiritvā udakena paripphosakaṃ paripphosakaṃ sanneyya. sāyaṃ nhānīyapiṇḍi snehānugatā snehaparetā santarabāhirā, phuṭā snehena na ca pagghariṇī. evameva kho, bhikkhave, bhikkhu imameva kāyaṃ vivekajena pītisukhena abhisandeti parisandeti paripūreti parippharati, nāssa kiñci sabbāvato kāyassa vivekajena pītisukhena apphuṭaṃ hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-427-');
INSERT INTO pages VALUES(3384,'mula_ma_01',345,replace(replace('<p class="bodytext"><a name="para428"></a><a name="para428_mn1"></a><span class="paranum">428</span>. puna <a name="M1.0345"></a> caparaṃ, bhikkhave, bhikkhu vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ <a name="V1.0349"></a> cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ upasampajja viharati. so imameva kāyaṃ samādhijena pītisukhena abhisandeti parisandeti paripūreti parippharati, nāssa kiñci sabbāvato kāyassa samādhijena pītisukhena apphuṭaṃ hoti. seyyathāpi, bhikkhave, udakarahado ubbhidodako <a name="P1.0277"></a> <span class="note">[ubbhitodako (ka.)]</span>. tassa <a name="T1.0506"></a> nevassa puratthimāya disāya udakassa āyamukhaṃ, na pacchimāya disāya udakassa āyamukhaṃ, na uttarāya disāya udakassa āyamukhaṃ, na dakkhiṇāya disāya udakassa āyamukhaṃ, devo ca na kālena kālaṃ sammādhāraṃ anuppaveccheyya. atha kho tamhāva udakarahadā sītā vāridhārā ubbhijjitvā tameva udakarahadaṃ sītena vārinā abhisandeyya parisandeyya paripūreyya paripphareyya, nāssa kiñci sabbāvato udakarahadassa sītena vārinā apphuṭaṃ assa. evameva kho, bhikkhave, bhikkhu imameva kāyaṃ samādhijena pītisukhena abhisandeti parisandeti paripūreti parippharati, nāssa kiñci sabbāvato kāyassa samādhijena pītisukhena apphuṭaṃ hoti.</p>\r\n<p class="bodytext"><a name="para429"></a><a name="para429_mn1"></a><span class="paranum">429</span>. puna caparaṃ, bhikkhave, bhikkhu pītiyā ca virāgā upekkhako ca viharati, sato ca sampajāno, sukhañca kāyena paṭisaṃvedeti, yaṃ taṃ ariyā ācikkhanti – ‘upekkhako satimā sukhavihārī’ti tatiyaṃ jhānaṃ upasampajja viharati. so imameva kāyaṃ nippītikena sukhena abhisandeti parisandeti paripūreti parippharati, nāssa kiñci sabbāvato kāyassa nippītikena sukhena apphuṭaṃ hoti. seyyathāpi, bhikkhave, uppaliniyaṃ vā paduminiyaṃ vā puṇḍarīkiniyaṃ vā appekaccāni uppalāni vā padumāni vā puṇḍarīkāni vā udake jātāni udake saṃvaḍḍhāni udakānuggatāni antonimuggaposīni, tāni yāva caggā yāva ca mūlā <a name="T1.0507"></a> sītena vārinā abhisannāni parisannāni paripūrāni paripphuṭāni, nāssa <span class="note">[na nesaṃ (sī.)]</span> kiñci sabbāvataṃ uppalānaṃ vā padumānaṃ vā puṇḍarīkānaṃ vā sītena vārinā apphuṭaṃ assa. evameva kho, bhikkhave, bhikkhu imameva kāyaṃ nippītikena sukhena abhisandeti parisandeti paripūreti parippharati, nāssa kiñci sabbāvato kāyassa nippītikena sukhena apphuṭaṃ hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-428-429-');
INSERT INTO pages VALUES(3385,'mula_ma_01',346,replace(replace('<p class="bodytext"><a name="para430"></a><a name="para430_mn1"></a><span class="paranum">430</span>. puna <a name="M1.0346"></a> caparaṃ, bhikkhave, bhikkhu sukhassa ca pahānā dukkhassa ca pahānā, pubbeva somanassadomanassānaṃ atthaṅgamā, adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja viharati. so imameva kāyaṃ parisuddhena cetasā pariyodātena pharitvā nisinno hoti<a name="V1.0350"></a>, nāssa kiñci sabbāvato kāyassa parisuddhena cetasā pariyodātena apphuṭaṃ hoti. seyyathāpi, bhikkhave, puriso odātena vatthena sasīsaṃ pārupetvā nisinno assa, nāssa kiñci sabbāvato <a name="P1.0278"></a> kāyassa odātena vatthena apphuṭaṃ assa. evameva kho, bhikkhave, bhikkhu imameva kāyaṃ parisuddhena cetasā pariyodātena pharitvā nisinno hoti, nāssa kiñci sabbāvato kāyassa parisuddhena cetasā pariyodātena apphuṭaṃ hoti.</p>\r\n<p class="bodytext"><a name="para431"></a><a name="para431_mn1"></a><span class="paranum">431</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte pubbenivāsānussatiñāṇāya cittaṃ abhininnāmeti. so anekavihitaṃ pubbenivāsaṃ <a name="T1.0508"></a> anussarati, seyyathidaṃ – ekampi jātiṃ, dvepi jātiyo…pe… iti sākāraṃ sauddesaṃ anekavihitaṃ pubbenivāsaṃ anussarati. seyyathāpi, bhikkhave, puriso sakamhā gāmā aññaṃ gāmaṃ gaccheyya, tamhāpi gāmā aññaṃ gāmaṃ gaccheyya, so tamhā gāmā sakaṃyeva gāmaṃ paccāgaccheyya. tassa evamassa – ‘ahaṃ kho sakamhā gāmā amuṃ gāmaṃ agacchiṃ <span class="note">[agacchiṃ (sī. syā. kaṃ. pī.)]</span>, tatrapi evaṃ aṭṭhāsiṃ evaṃ nisīdiṃ evaṃ abhāsiṃ evaṃ tuṇhī ahosiṃ, tamhāpi gāmā amuṃ gāmaṃ agacchiṃ, tatrapi evaṃ aṭṭhāsiṃ evaṃ nisīdiṃ evaṃ abhāsiṃ evaṃ tuṇhī ahosiṃ, somhi tamhā gāmā sakaṃyeva gāmaṃ paccāgato’ti. evameva kho, bhikkhave, bhikkhu anekavihitaṃ pubbenivāsaṃ anussarati, seyyathidaṃ – ekampi jātiṃ dvepi jātiyo…pe… iti sākāraṃ sauddesaṃ anekavihitaṃ pubbenivāsaṃ anussarati.</p>\r\n<p class="bodytext"><a name="para432"></a><a name="para432_mn1"></a><span class="paranum">432</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite āneñjappatte sattānaṃ cutūpapātañāṇāya cittaṃ abhininnāmeti. so dibbena cakkhunā visuddhena atikkantamānusakena satte passati cavamāne upapajjamāne hīne</p>\r\n','\r',char(13)),'\n',char(10)),'-430-431-432-');
INSERT INTO pages VALUES(3386,'mula_ma_01',347,replace(replace('<p class="bodytext">paṇīte <a name="M1.0347"></a> suvaṇṇe dubbaṇṇe, sugate duggate, yathākammūpage satte pajānāti…pe… <a name="P1.0279"></a> seyyathāpi, bhikkhave, dve agārā sadvārā <span class="note">[sannadvārā (ka.)]</span>. tattha cakkhumā puriso majjhe ṭhito passeyya manusse gehaṃ pavisantepi nikkhamantepi, anucaṅkamantepi anuvicarantepi<a name="T1.0509"></a>. evameva kho, bhikkhave, bhikkhu dibbena cakkhunā visuddhena atikkantamānusakena satte passati cavamāne upapajjamāne hīne paṇīte suvaṇṇe dubbaṇṇe, sugate duggate yathākammūpage satte pajānāti…pe….</p>\r\n<p class="bodytext"><a name="para433"></a><a name="para433_mn1"></a><span class="paranum">433</span>. so evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye <a name="V1.0351"></a> ṭhite āneñjappatte āsavānaṃ khayañāṇāya cittaṃ abhininnāmeti. so ‘idaṃ dukkha’nti yathābhūtaṃ pajānāti, ‘ayaṃ dukkhasamudayo’ti yathābhūtaṃ pajānāti, ‘ayaṃ dukkhanirodho’ti yathābhūtaṃ pajānāti, ‘ayaṃ dukkhanirodhagāminī paṭipadā’ti yathābhūtaṃ pajānāti. ‘ime āsavā’ti yathābhūtaṃ pajānāti, ‘ayaṃ āsavasamudayo’ti yathābhūtaṃ pajānāti, ‘ayaṃ āsavanirodho’ti yathābhūtaṃ pajānāti, ‘ayaṃ āsavanirodhagāminī paṭipadā’ti yathābhūtaṃ pajānāti. tassa evaṃ jānato evaṃ passato kāmāsavāpi cittaṃ vimuccati, bhavāsavāpi cittaṃ vimuccati, avijjāsavāpi cittaṃ vimuccati. vimuttasmiṃ vimuttamiti ñāṇaṃ hoti – ‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti pajānāti.</p>\r\n<p class="bodytext">seyyathāpi, bhikkhave, pabbatasaṅkhepe udakarahado accho vippasanno anāvilo. tattha cakkhumā puriso tīre ṭhito passeyya sippisambukampi <span class="note">[sippikasambukampi (syā. kaṃ. ka.)]</span> sakkharakathalampi macchagumbampi, carantampi tiṭṭhantampi. tassa evamassa – ‘ayaṃ kho udakarahado accho vippasanno anāvilo. tatrime sippisambukāpi <a name="P1.0280"></a> sakkharakathalāpi macchagumbāpi carantipi tiṭṭhantipīti<a name="T1.0510"></a>. evameva kho, bhikkhave, bhikkhu ‘idaṃ dukkha’nti yathābhūtaṃ pajānāti…pe… nāparaṃ itthattāyāti pajānāti.</p>\r\n<p class="bodytext"><a name="para434"></a><a name="para434_mn1"></a><span class="paranum">434</span>. ayaṃ vuccati, bhikkhave, bhikkhu ‘samaṇo’ itipi ‘brāhmaṇo’itipi ‘nhātako’itipi ‘vedagū’itipi ‘sottiyo’itipi ‘ariyo’itipi ‘arahaṃ’itipi. kathañca, bhikkhave, bhikkhu samaṇo hoti? samitāssa honti pāpakā akusalā dhammā, saṃkilesikā, ponobbhavikā, sadarā,</p>\r\n','\r',char(13)),'\n',char(10)),'-433-434-');
INSERT INTO pages VALUES(3387,'mula_ma_01',348,replace(replace('<p class="bodytext">dukkhavipākā<a name="M1.0348"></a>, āyatiṃ, jātijarāmaraṇiyā. evaṃ kho, bhikkhave, bhikkhu samaṇo hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu brāhmaṇo hoti? bāhitāssa honti pāpakā akusalā dhammā<a name="V1.0352"></a>, saṃkilesikā, ponobbhavikā, sadarā, dukkhavipākā, āyatiṃ, jātijarāmaraṇiyā. evaṃ kho, bhikkhave, bhikkhu brāhmaṇo hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu nhātako <span class="note">[nahātako (sī. syā. kaṃ. pī.)]</span> hoti? nhātāssa honti pāpakā akusalā dhammā, saṃkilesikā, ponobbhavikā, sadarā, dukkhavipākā, āyatiṃ, jātijarāmaraṇiyā. evaṃ kho, bhikkhave, bhikkhu nhātako hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu vedagū hoti? viditāssa honti pāpakā akusalā dhammā, saṃkilesikā, ponobbhavikā, sadarā, dukkhavipākā, āyatiṃ, jātijarāmaraṇiyā. evaṃ kho, bhikkhave, bhikkhu vedagū hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu sottiyo hoti? nissutāssa honti pāpakā akusalā dhammā, saṃkilesikā, ponobbhavikā, sadarā, dukkhavipākā, āyatiṃ, jātijarāmaraṇiyā. evaṃ kho, bhikkhave, bhikkhu sottiyo hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu ariyo hoti<a name="T1.0511"></a>? ārakāssa honti pāpakā akusalā dhammā, saṃkilesikā, ponobbhavikā, sadarā, dukkhavipākā, āyatiṃ, jātijarāmaraṇiyā. evaṃ kho, bhikkhave, bhikkhu ariyo hoti.</p>\r\n<p class="bodytext">kathañca, bhikkhave, bhikkhu arahaṃ hoti? ārakāssa honti pāpakā akusalā dhammā, saṃkilesikā, ponobbhavikā, sadarā, dukkhavipākā, āyatiṃ, jātijarāmaraṇiyā. evaṃ kho, bhikkhave, bhikkhu arahaṃ hotī"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">mahāassapurasuttaṃ niṭṭhitaṃ navamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3388,'mula_ma_01',349,replace(replace('<p class="subhead">10. cūḷaassapurasuttaṃ</p>\r\n<p class="bodytext"><a name="para435"></a><a name="para435_mn1"></a><span class="paranum">435</span>. evaṃ <a name="M1.0349"></a><a name="V1.0353"></a><a name="P1.0281"></a><a name="T1.0512"></a> me sutaṃ – ekaṃ samayaṃ bhagavā aṅgesu viharati assapuraṃ nāma aṅgānaṃ nigamo. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca – "samaṇā samaṇāti vo, bhikkhave, jano sañjānāti. tumhe ca pana ‘ke tumhe’ti puṭṭhā samānā ‘samaṇāmhā’ti paṭijānātha. tesaṃ vo, bhikkhave, evaṃsamaññānaṃ sataṃ evaṃpaṭiññānaṃ sataṃ – ‘yā samaṇasāmīcippaṭipadā taṃ paṭipajjissāma, evaṃ no ayaṃ amhākaṃ samaññā ca saccā bhavissati paṭiññā ca bhūtā, yesañca mayaṃ cīvarapiṇḍapātasenāsanagilānappaccayabhesajjaparikkhāraṃ paribhuñjāma, tesaṃ te kārā amhesu mahapphalā bhavissanti mahānisaṃsā, amhākañcevāyaṃ pabbajjā avañjhā bhavissati saphalā saudrayā’ti. evañhi vo, bhikkhave, sikkhitabbaṃ.</p>\r\n<p class="bodytext"><a name="para436"></a><a name="para436_mn1"></a><span class="paranum">436</span>. kathañca, bhikkhave, bhikkhu na samaṇasāmīcippaṭipadaṃ paṭipanno hoti? yassa kassaci, bhikkhave, bhikkhuno abhijjhālussa abhijjhā appahīnā hoti, byāpannacittassa byāpādo appahīno hoti, kodhanassa kodho appahīno hoti, upanāhissa upanāho appahīno hoti, makkhissa makkho appahīno hoti, paḷāsissa paḷāso appahīno hoti, issukissa issā appahīnā hoti, maccharissa macchariyaṃ appahīnaṃ hoti<a name="T1.0513"></a>, saṭhassa sāṭheyyaṃ appahīnaṃ hoti, māyāvissa māyā appahīnā hoti, pāpicchassa pāpikā icchā appahīnā hoti, micchādiṭṭhikassa micchādiṭṭhi appahīnā hoti – imesaṃ kho ahaṃ, bhikkhave, samaṇamalānaṃ samaṇadosānaṃ samaṇakasaṭānaṃ āpāyikānaṃ ṭhānānaṃ duggativedaniyānaṃ appahānā ‘na samaṇasāmīcippaṭipadaṃ paṭipanno’ti vadāmi. seyyathāpi, bhikkhave, matajaṃ nāma āvudhajātaṃ ubhatodhāraṃ pītanisitaṃ. tadassa saṅghāṭiyā sampārutaṃ sampaliveṭhitaṃ. tathūpamāhaṃ, bhikkhave, imassa bhikkhuno pabbajjaṃ vadāmi.</p>\r\n<p class="bodytext"><a name="para437"></a><a name="para437_mn1"></a><span class="paranum">437</span>. nāhaṃ, bhikkhave, saṅghāṭikassa saṅghāṭidhāraṇamattena sāmaññaṃ vadāmi. nāhaṃ, bhikkhave, acelakassa acelakamattena sāmaññaṃ vadāmi. nāhaṃ, bhikkhave, rajojallikassa rajojallikamattena sāmaññaṃ vadāmi. nāhaṃ,</p>\r\n','\r',char(13)),'\n',char(10)),'-435-436-437-');
INSERT INTO pages VALUES(3389,'mula_ma_01',350,replace(replace('<p class="bodytext">bhikkhave<a name="M1.0350"></a><a name="V1.0354"></a>, udakorohakassa udakorohaṇamattena <span class="note">[udakorohakamattena (sī. pī.)]</span> sāmaññaṃ vadāmi. nāhaṃ, bhikkhave, rukkhamūlikassa rukkhamūlikamattena <a name="P1.0282"></a> sāmaññaṃ vadāmi. nāhaṃ, bhikkhave, abbhokāsikassa abbhokāsikamattena sāmaññaṃ vadāmi. nāhaṃ, bhikkhave, ubbhaṭṭhakassa ubbhaṭṭhakamattena sāmaññaṃ vadāmi. nāhaṃ, bhikkhave, pariyāyabhattikassa pariyāyabhattikamattena sāmaññaṃ vadāmi. nāhaṃ, bhikkhave, mantajjhāyakassa mantajjhāyakamattena sāmaññaṃ vadāmi. nāhaṃ, bhikkhave, jaṭilakassa jaṭādhāraṇamattena sāmaññaṃ vadāmi.</p>\r\n<p class="bodytext">saṅghāṭikassa ce, bhikkhave, saṅghāṭidhāraṇamattena abhijjhālussa <a name="T1.0514"></a> abhijjhā pahīyetha, byāpannacittassa byāpādo pahīyetha, kodhanassa kodho pahīyetha, upanāhissa upanāho pahīyetha, makkhissa makkho pahīyetha, paḷāsissa paḷāso pahīyetha, issukissa issā pahīyetha, maccharissa macchariyaṃ pahīyetha, saṭhassa sāṭheyyaṃ pahīyetha, māyāvissa māyā pahīyetha, pāpicchassa pāpikā icchā pahīyetha, micchādiṭṭhikassa micchādiṭṭhi pahīyetha, tamenaṃ mittāmaccā ñātisālohitā jātameva naṃ saṅghāṭikaṃ kareyyuṃ, saṅghāṭikattameva <span class="note">[saṃghāṭīkatte ceva (ka.)]</span> samādapeyyuṃ – ‘ehi tvaṃ, bhadramukha, saṅghāṭiko hohi, saṅghāṭikassa te sato saṅghāṭidhāraṇamattena abhijjhālussa abhijjhā pahīyissati, byāpannacittassa byāpādo pahīyissati, kodhanassa kodho pahīyissati, upanāhissa upanāho pahīyissati, makkhissa makkho pahīyissati, paḷāsissa paḷāso pahīyissati, issukissa issā pahīyissati, maccharissa macchariyaṃ pahīyissati, saṭhassa sāṭheyyaṃ pahīyissati, māyāvissa māyā pahīyissati, pāpicchassa pāpikā icchā pahīyissati, micchādiṭṭhikassa micchādiṭṭhi pahīyissatī’ti. yasmā ca kho ahaṃ, bhikkhave, saṅghāṭikampi idhekaccaṃ passāmi abhijjhāluṃ byāpannacittaṃ kodhanaṃ upanāhiṃ makkhiṃ paḷāsiṃ issukiṃ macchariṃ saṭhaṃ māyāviṃ pāpicchaṃ micchādiṭṭhikaṃ, tasmā na saṅghāṭikassa saṅghāṭidhāraṇamattena sāmaññaṃ vadāmi.</p>\r\n<p class="bodytext">acelakassa ce, bhikkhave…pe… rajojallikassa ce, bhikkhave…pe… udakorohakassa ce, bhikkhave…pe… rukkhamūlikassa <a name="T1.0515"></a> ce, bhikkhave…pe… abbhokāsikassa ce, bhikkhave…pe… ubbhaṭṭhakassa ce, bhikkhave…pe… pariyāyabhattikassa ce, bhikkhave…pe… mantajjhāyakassa ce, bhikkhave…pe…</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3390,'mula_ma_01',351,replace(replace('<p class="bodytext">jaṭilakassa <a name="M1.0351"></a> ce, bhikkhave, jaṭādhāraṇamattena abhijjhālussa abhijjhā pahīyetha, byāpannacittassa byāpādo pahīyetha<a name="V1.0355"></a>, kodhanassa kodho pahīyetha, upanāhissa upanāho pahīyetha, makkhissa makkho pahīyetha, paḷāsissa paḷāso pahīyetha, issukissa issā pahīyetha, maccharissa macchariyaṃ pahīyetha, saṭhassa sāṭheyyaṃ pahīyetha, māyāvissa māyā pahīyetha, pāpicchassa pāpikā icchā pahīyetha, micchādiṭṭhikassa micchādiṭṭhi pahīyetha, tamenaṃ mittāmaccā ñātisālohitā jātameva naṃ jaṭilakaṃ kareyyuṃ, jaṭilakattameva <span class="note">[jaṭilakatte ceva (ka.)]</span> samādapeyyuṃ – ‘ehi tvaṃ, bhadramukha, jaṭilako hohi, jaṭilakassa te sato jaṭādhāraṇamattena abhijjhālussa abhijjhā pahīyissati byāpannacittassa <a name="P1.0283"></a> byāpādo pahīyissati, kodhanassa kodho pahīyissati…pe… pāpicchassa pāpikā icchā pahīyissati micchādiṭṭhikassa micchādiṭṭhi pahīyissatī’ti. yasmā ca kho ahaṃ, bhikkhave, jaṭilakampi idhekaccaṃ passāmi abhijjhāluṃ byāpannacittaṃ kodhanaṃ upanāhiṃ makkhiṃ palāsiṃ issukiṃ macchariṃ saṭhaṃ māyāviṃ pāpicchaṃ micchādiṭṭhiṃ, tasmā <a name="T1.0516"></a> na jaṭilakassa jaṭādhāraṇamattena sāmaññaṃ vadāmi.</p>\r\n<p class="bodytext"><a name="para438"></a><a name="para438_mn1"></a><span class="paranum">438</span>. kathañca, bhikkhave, bhikkhu samaṇasāmīcippaṭipadaṃ paṭipanno hoti? yassa kassaci, bhikkhave, bhikkhuno abhijjhālussa abhijjhā pahīnā hoti, byāpannacittassa byāpādo pahīno hoti, kodhanassa kodho pahīno hoti, upanāhissa upanāho pahīno hoti, makkhissa makkho pahīno hoti, paḷāsissa paḷāso pahīno hoti, issukissa issā pahīnā hoti, maccharissa macchariyaṃ pahīnaṃ hoti, saṭhassa sāṭheyyaṃ pahīnaṃ hoti, māyāvissa māyā pahīnā hoti, pāpicchassa pāpikā icchā pahīnā hoti, micchādiṭṭhikassa micchādiṭṭhi pahīnā hoti – imesaṃ kho ahaṃ, bhikkhave, samaṇamalānaṃ samaṇadosānaṃ samaṇakasaṭānaṃ āpāyikānaṃ ṭhānānaṃ duggativedaniyānaṃ pahānā ‘samaṇasāmīcippaṭipadaṃ paṭipanno’ti vadāmi. so sabbehi imehi pāpakehi akusalehi dhammehi visuddhamattānaṃ samanupassati ( ) <span class="note">[(vimuttamattānaṃ samanupassati) (sī. syā. kaṃ. pī.)]</span>. tassa sabbehi imehi pāpakehi akusalehi dhammehi visuddhamattānaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-438-');
INSERT INTO pages VALUES(3391,'mula_ma_01',352,replace(replace('<p class="bodytext">samanupassato <a name="M1.0352"></a> ( ) <span class="note">[(vimuttamattānaṃ samanupassato) (sī. syā. kaṃ. pī.)]</span> pāmojjaṃ jāyati, pamuditassa pīti jāyati, pītimanassa kāyo passambhati, passaddhakāyo sukhaṃ vedeti, sukhino cittaṃ samādhiyati.</p>\r\n<p class="bodytext">so mettāsahagatena cetasā ekaṃ disaṃ pharitvā viharati, tathā dutiyaṃ, tathā tatiyaṃ, tathā catutthaṃ. iti uddhamadho tiriyaṃ sabbadhi sabbattatāya sabbāvantaṃ lokaṃ mettāsahagatena cetasā vipulena mahaggatena appamāṇena <a name="T1.0517"></a> averena abyābajjhena pharitvā viharati. karuṇāsahagatena <a name="V1.0356"></a> cetasā…pe… muditāsahagatena cetasā…pe… upekkhāsahagatena cetasā ekaṃ disaṃ pharitvā viharati, tathā dutiyaṃ, tathā tatiyaṃ, tathā catutthaṃ. iti uddhamadho tiriyaṃ sabbadhi sabbattatāya sabbāvantaṃ lokaṃ upekkhāsahagatena cetasā vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā viharati. seyyathāpi, bhikkhave, pokkharaṇī acchodakā sātodakā sītodakā setakā supatitthā ramaṇīyā. puratthimāya <a name="P1.0284"></a> cepi disāya puriso āgaccheyya ghammābhitatto ghammapareto kilanto tasito pipāsito. so taṃ pokkharaṇiṃ āgamma vineyya udakapipāsaṃ vineyya ghammapariḷāhaṃ…pe… pacchimāya cepi disāya puriso āgaccheyya…pe… uttarāya cepi disāya puriso āgaccheyya…pe… dakkhiṇāya cepi disāya puriso āgaccheyya. yato kuto cepi naṃ puriso āgaccheyya ghammābhitatto ghammapareto, kilanto tasito pipāsito. so taṃ pokkharaṇiṃ āgamma vineyya udakapipāsaṃ, vineyya ghammapariḷāhaṃ. evameva kho, bhikkhave, khattiyakulā cepi agārasmā anagāriyaṃ pabbajito hoti, so ca tathāgatappaveditaṃ dhammavinayaṃ āgamma, evaṃ mettaṃ karuṇaṃ muditaṃ upekkhaṃ bhāvetvā labhati ajjhattaṃ <span class="note">[tamahaṃ (ka.)]</span> vūpasamaṃ <span class="note">[tamahaṃ (ka.)]</span>. ajjhattaṃ vūpasamā ‘samaṇasāmīcippaṭipadaṃ paṭipanno’ti vadāmi. brāhmaṇakulā cepi…pe… vessakulā cepi…pe… suddakulā cepi…pe… yasmā kasmā cepi kulā agārasmā anagāriyaṃ pabbajito hoti<a name="T1.0518"></a>, so ca tathāgatappaveditaṃ dhammavinayaṃ āgamma, evaṃ mettaṃ karuṇaṃ muditaṃ upekkhaṃ bhāvetvā labhati ajjhattaṃ vūpasamaṃ. ajjhattaṃ vūpasamā ‘samaṇasāmīcippaṭipadaṃ paṭipanno’ti vadāmi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3392,'mula_ma_01',353,replace(replace('<p class="bodytext">khattiyakulā <a name="M1.0353"></a> cepi agārasmā anagāriyaṃ pabbajito hoti. so ca āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja viharati. āsavānaṃ khayā samaṇo hoti. brāhmaṇakulā cepi…pe… vessakulā cepi… suddakulā cepi… yasmā kasmā cepi kulā agārasmā anagāriyaṃ pabbajito hoti, so ca āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ <a name="V1.0357"></a> diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja viharati. āsavānaṃ khayā samaṇo hotī"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">cūḷaassapurasuttaṃ niṭṭhitaṃ dasamaṃ.</p>\r\n<p class="bodytext">tassuddānaṃ –</p>\r\n<p class="gatha1">giñjakasālavanaṃ pariharituṃ, paññavato puna saccakanisedho,</p>\r\n<p class="gathalast">mukhavaṇṇapasīdanatāpindo, kevaṭṭaassapurajaṭilena.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3393,'mula_ma_01',354,replace(replace('<p class="chapter">5. cūḷayamakavaggo</p>\r\n<p class="subhead">1. sāleyyakasuttaṃ</p>\r\n<p class="bodytext"><a name="para439"></a><a name="para439_mn1"></a><span class="paranum">439</span>. evaṃ <a name="T1.0519"></a><a name="M1.0354"></a><a name="V1.0358"></a><a name="P1.0285"></a> me sutaṃ – ekaṃ samayaṃ bhagavā kosalesu cārikaṃ caramāno mahatā bhikkhusaṅghena saddhiṃ yena sālā nāma kosalānaṃ brāhmaṇagāmo tadavasari. assosuṃ kho sāleyyakā brāhmaṇagahapatikā – "samaṇo khalu, bho, gotamo sakyaputto sakyakulā pabbajito kosalesu cārikaṃ caramāno mahatā bhikkhusaṅghena saddhiṃ sālaṃ anuppatto. taṃ kho pana bhavantaṃ gotamaṃ evaṃ kalyāṇo kittisaddo abbhuggato – ‘itipi so bhagavā arahaṃ sammāsambuddho vijjācaraṇasampanno sugato lokavidū anuttaro purisadammasārathi satthā devamanussānaṃ buddho bhagavā. so imaṃ lokaṃ sadevakaṃ samārakaṃ sabrahmakaṃ sassamaṇabrāhmaṇiṃ pajaṃ sadevamanussaṃ sayaṃ abhiññā sacchikatvā pavedeti. so dhammaṃ deseti ādikalyāṇaṃ majjhekalyāṇaṃ pariyosānakalyāṇaṃ sātthaṃ sabyañjanaṃ, kevalaparipuṇṇaṃ parisuddhaṃ brahmacariyaṃ pakāseti’. sādhu kho pana tathārūpānaṃ arahataṃ dassanaṃ hotī"ti.</p>\r\n<p class="bodytext">atha kho sāleyyakā brāhmaṇagahapatikā yena bhagavā tenupasaṅkamiṃsu, upasaṅkamitvā appekacce bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu, appekacce bhagavatā saddhiṃ sammodiṃsu, sammodanīyaṃ kathaṃ <a name="T1.0520"></a> sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdiṃsu, appekacce yena bhagavā tenañjaliṃ paṇāmetvā ekamantaṃ nisīdiṃsu, appekacce bhagavato santike nāmagottaṃ sāvetvā ekamantaṃ nisīdiṃsu, appekacce tuṇhībhūtā ekamantaṃ nisīdiṃsu. ekamantaṃ nisinnā kho sāleyyakā brāhmaṇagahapatikā bhagavantaṃ etadavocuṃ – "ko nu kho, bho gotama, hetu, ko paccayo, yena midhekacce sattā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti<a name="V1.0359"></a>? ko pana, bho gotama, hetu, ko paccayo, yena midhekacce sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjantī"ti?</p>\r\n<p class="bodytext">adhammacariyāvisamacariyāhetu kho, gahapatayo, evamidhekacce sattā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-439-');
INSERT INTO pages VALUES(3394,'mula_ma_01',355,replace(replace('<p class="bodytext">dhammacariyāsamacariyāhetu <a name="M1.0355"></a> kho, gahapatayo, evamidhekacce sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjantī"ti<a name="P1.0286"></a>.</p>\r\n<p class="bodytext">na kho mayaṃ imassa bhoto gotamassa saṃkhittena bhāsitassa, vitthārena atthaṃ avibhattassa, vitthārena atthaṃ ājānāma. sādhu no bhavaṃ gotamo tathā dhammaṃ desetu, yathā mayaṃ imassa bhoto gotamassa saṃkhittena bhāsitassa, vitthārena atthaṃ avibhattassa, vitthārena atthaṃ ājāneyyāmā"ti. "tena hi, gahapatayo, suṇātha, sādhukaṃ manasi karotha, bhāsissāmī"ti. "evaṃ, bho"ti kho sāleyyakā brāhmaṇagahapatikā bhagavato paccassosuṃ. bhagavā <a name="T1.0521"></a> etadavoca –</p>\r\n<p class="bodytext"><a name="para440"></a><a name="para440_mn1"></a><span class="paranum">440</span>. tividhaṃ kho, gahapatayo, kāyena adhammacariyāvisamacariyā hoti, catubbidhaṃ vācāya adhammacariyāvisamacariyā hoti, tividhaṃ manasā adhammacariyāvisamacariyā hoti.</p>\r\n<p class="bodytext">kathañca, gahapatayo, tividhaṃ kāyena adhammacariyāvisamacariyā hoti? idha, gahapatayo, ekacco pāṇātipātī hoti, luddo <span class="note">[luddo dāruṇo (ka.) ṭīkā oloketabbā]</span> lohitapāṇi hatappahate niviṭṭho adayāpanno pāṇabhūtesu <span class="note">[sabbapāṇabhūtesu (syā. kaṃ. ka.)]</span>.</p>\r\n<p class="bodytext">adinnādāyī kho pana hoti. yaṃ taṃ parassa paravittūpakaraṇaṃ, gāmagataṃ vā araññagataṃ vā, taṃ adinnaṃ theyyasaṅkhātaṃ ādātā hoti.</p>\r\n<p class="bodytext">kāmesumicchācārī kho pana hoti. yā tā māturakkhitā piturakkhitā mātāpiturakkhitā bhāturakkhitā bhaginirakkhitā ñātirakkhitā gottarakkhitā dhammarakkhitā sassāmikā saparidaṇḍā antamaso mālāguḷaparikkhittāpi, tathārūpāsu cārittaṃ āpajjitā hoti. evaṃ kho, gahapatayo, tividhaṃ kāyena adhammacariyāvisamacariyā hoti.</p>\r\n<p class="bodytext">kathañca<a name="V1.0360"></a>, gahapatayo, catubbidhaṃ vācāya adhammacariyāvisamacariyā hoti? idha, gahapatayo, ekacco musāvādī hoti. sabhāgato vā parisāgato vā, ñātimajjhagato vā pūgamajjhagato vā rājakulamajjhagato vā, abhinīto sakkhipuṭṭho – ‘ehambho purisa, yaṃ jānāsi taṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-440-');
INSERT INTO pages VALUES(3395,'mula_ma_01',356,replace(replace('<p class="bodytext">vadehī’ti<a name="M1.0356"></a>, so ajānaṃ vā āha – ‘jānāmī’ti, jānaṃ vā āha – ‘na jānāmī’ti, apassaṃ vā āha – ‘passāmī’ti, passaṃ vā āha – ‘na passāmī’ti <span class="note">[so āha ajānaṃ vā ahaṃ jānāmīti jānaṃ vā ahaṃ na jānāmīti apassaṃ vā ahaṃ passāmīti passaṃ vā ahaṃ na passāmīti (ka.)]</span>. iti attahetu vā parahetu vā āmisakiñcikkhahetu vā sampajānamusā bhāsitā hoti.</p>\r\n<p class="bodytext">pisuṇavāco kho pana hoti. ito sutvā amutra akkhātā <a name="T1.0522"></a> imesaṃ bhedāya, amutra vā sutvā imesaṃ akkhātā amūsaṃ bhedāya. iti samaggānaṃ vā bhettā <span class="note">[bhedakā (ka.), bhedetā (syā. kaṃ.), tadaṭṭhakathāyaṃ pana bhettāti dissati]</span>, bhinnānaṃ vā anuppadātā, vaggārāmo vaggarato vagganandī vaggakaraṇiṃ vācaṃ bhāsitā hoti.</p>\r\n<p class="bodytext">pharusavāco kho pana hoti. yā sā vācā aṇḍakā <span class="note">[kaṇḍakā (ka.)]</span> kakkasā parakaṭukā parābhisajjanī kodhasāmantā asamādhisaṃvattanikā<a name="P1.0287"></a>, tathārūpiṃ vācaṃ bhāsitā hoti.</p>\r\n<p class="bodytext">samphappalāpī kho pana hoti. akālavādī abhūtavādī anatthavādī adhammavādī avinayavādī. anidhānavatiṃ vācaṃ bhāsitā hoti akālena anapadesaṃ apariyantavatiṃ anatthasaṃhitaṃ. evaṃ kho, gahapatayo, catubbidhaṃ vācāya adhammacariyāvisamacariyā hoti.</p>\r\n<p class="bodytext">kathañca, gahapatayo, tividhaṃ manasā adhammacariyāvisamacariyā hoti? idha, gahapatayo, ekacco abhijjhālu hoti, yaṃ taṃ parassa paravittūpakaraṇaṃ taṃ abhijjhātā hoti – ‘aho vata yaṃ parassa taṃ mamassā"’ti!</p>\r\n<p class="bodytext">byāpannacitto kho pana hoti paduṭṭhamanasaṅkappo – ‘ime sattā haññantu vā vajjhantu vā ucchijjantu vā vinassantu vā mā vā ahesu"’nti <span class="note">[mā vā ahesuṃ iti vāti (sī. pī. ka.)]</span>.</p>\r\n<p class="bodytext">micchādiṭṭhiko kho pana hoti viparītadassano – ‘natthi dinnaṃ natthi yiṭṭhaṃ natthi hutaṃ, natthi sukatadukkaṭānaṃ kammānaṃ phalaṃ vipāko, natthi ayaṃ loko natthi paro loko, natthi mātā natthi pitā, natthi sattā</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3396,'mula_ma_01',357,replace(replace('<p class="bodytext">opapātikā<a name="M1.0357"></a>, natthi loke samaṇabrāhmaṇā sammaggatā sammāpaṭipannā <a name="V1.0361"></a> ye imañca lokaṃ parañca lokaṃ sayaṃ abhiññā sacchikatvā pavedentī’ti. evaṃ kho, gahapatayo, tividhaṃ manasā adhammacariyāvisamacariyā <a name="T1.0523"></a> hoti.</p>\r\n<p class="bodytext">evaṃ adhammacariyāvisamacariyāhetu kho, gahapatayo, evamidhekacce sattā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti.</p>\r\n<p class="bodytext"><a name="para441"></a><a name="para441_mn1"></a><span class="paranum">441</span>. tividhaṃ kho, gahapatayo, kāyena dhammacariyāsamacariyā hoti, catubbidhaṃ vācāya dhammacariyāsamacariyā hoti, tividhaṃ manasā dhammacariyāsamacariyā hoti.</p>\r\n<p class="bodytext">kathañca, gahapatayo, tividhaṃ kāyena dhammacariyāsamacariyā hoti? idha, gahapatayo, ekacco pāṇātipātaṃ pahāya pāṇātipātā paṭivirato hoti, nihitadaṇḍo nihitasattho lajjī dayāpanno sabbapāṇabhūtahitānukampī viharati.</p>\r\n<p class="bodytext">adinnādānaṃ pahāya adinnādānā paṭivirato hoti. yaṃ taṃ parassa paravittūpakaraṇaṃ, gāmagataṃ vā araññagataṃ vā, taṃ nādinnaṃ theyyasaṅkhātaṃ ādātā hoti.</p>\r\n<p class="bodytext">kāmesumicchācāraṃ pahāya kāmesumicchācārā paṭivirato hoti. yā tā māturakkhitā piturakkhitā mātāpiturakkhitā bhāturakkhitā bhaginirakkhitā ñātirakkhitā gottarakkhitā dhammarakkhitā sassāmikā saparidaṇḍā antamaso mālāguḷaparikkhittāpi, tathārūpāsu na cārittaṃ āpajjitā hoti. evaṃ kho, gahapatayo, tividhaṃ kāyena dhammacariyāsamacariyā <a name="P1.0288"></a> hoti.</p>\r\n<p class="bodytext">kathañca, gahapatayo, catubbidhaṃ vācāya dhammacariyāsamacariyā hoti? idha, gahapatayo, ekacco musāvādaṃ pahāya musāvādā paṭivirato hoti. sabhāgato vā parisāgato vā, ñātimajjhagato vā pūgamajjhagato vā rājakulamajjhagato vā, abhinīto sakkhipuṭṭho – ‘ehambho purisa, yaṃ jānāsi taṃ vadehī’ti, so <a name="T1.0524"></a> ajānaṃ vā āha – ‘na jānāmī’ti, jānaṃ vā āha – ‘jānāmī’ti, apassaṃ vā āha – ‘na passāmī’ti, passaṃ vā</p>\r\n','\r',char(13)),'\n',char(10)),'-441-');
INSERT INTO pages VALUES(3397,'mula_ma_01',358,replace(replace('<p class="bodytext">āha <a name="M1.0358"></a> – ‘passāmī’ti. iti attahetu vā parahetu vā āmisakiñcikkhahetu vā na sampajānamusā bhāsitā hoti.</p>\r\n<p class="bodytext">pisuṇaṃ <a name="V1.0362"></a> vācaṃ pahāya pisuṇāya vācāya paṭivirato hoti, ito sutvā na amutra akkhātā imesaṃ bhedāya, amutra vā sutvā na imesaṃ akkhātā amūsaṃ bhedāya. iti bhinnānaṃ vā sandhātā, sahitānaṃ vā anuppadātā, samaggārāmo samaggarato samagganandī samaggakaraṇiṃ vācaṃ bhāsitā hoti.</p>\r\n<p class="bodytext">pharusaṃ vācaṃ pahāya pharusāya vācāya paṭivirato hoti. yā sā vācā nelā kaṇṇasukhā pemanīyā hadayaṅgamā porī bahujanakantā bahujanamanāpā – tathārūpiṃ vācaṃ bhāsitā hoti.</p>\r\n<p class="bodytext">samphappalāpaṃ pahāya samphappalāpā paṭivirato hoti. kālavādī bhūtavādī atthavādī dhammavādī vinayavādī nidhānavatiṃ vācaṃ bhāsitā hoti kālena sāpadesaṃ pariyantavatiṃ atthasaṃhitaṃ. evaṃ kho, gahapatayo, catubbidhaṃ vācāya dhammacariyāsamacariyā hoti.</p>\r\n<p class="bodytext">kathañca, gahapatayo, tividhaṃ manasā dhammacariyāsamacariyā hoti? idha, gahapatayo, ekacco anabhijjhālu hoti, yaṃ taṃ parassa paravittūpakaraṇaṃ taṃ nābhijjhātā hoti – ‘aho vata yaṃ parassa taṃ mamassā’ti!</p>\r\n<p class="bodytext">abyāpannacitto kho pana hoti appaduṭṭhamanasaṅkappo – ‘ime sattā averā abyābajjhā anīghā sukhī attānaṃ pariharantū’ti.</p>\r\n<p class="bodytext">sammādiṭṭhiko kho pana hoti aviparītadassano – ‘atthi dinnaṃ atthi yiṭṭhaṃ <a name="T1.0525"></a> atthi hutaṃ, atthi sukatadukkaṭānaṃ kammānaṃ phalaṃ vipāko, atthi ayaṃ loko atthi paro loko, atthi mātā atthi pitā, atthi sattā opapātikā, atthi loke samaṇabrāhmaṇā sammaggatā sammāpaṭipannā ye imañca lokaṃ parañca lokaṃ sayaṃ abhiññā sacchikatvā pavedentī’ti. evaṃ kho, gahapatayo, tividhaṃ manasā dhammacariyāsamacariyā hoti.</p>\r\n<p class="bodytext">evaṃ dhammacariyāsamacariyāhetu kho, gahapatayo, evamidhekacce sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3398,'mula_ma_01',359,replace(replace('<p class="bodytext"><a name="para442"></a><a name="para442_mn1"></a><span class="paranum">442</span>. ākaṅkheyya <a name="M1.0359"></a><a name="V1.0363"></a><a name="P1.0289"></a> ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā khattiyamahāsālānaṃ sahabyataṃ upapajjeyya’nti, ṭhānaṃ kho panetaṃ vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā khattiyamahāsālānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkheyya ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā brāhmaṇamahāsālānaṃ…pe… gahapatimahāsālānaṃ sahabyataṃ upapajjeyya’nti, ṭhānaṃ kho panetaṃ vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā gahapatimahāsālānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkheyya ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā cātumahārājikānaṃ devānaṃ sahabyataṃ upapajjeya’nti, ṭhānaṃ kho panetaṃ <a name="T1.0526"></a> vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā cātumahārājikānaṃ devānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkheyya ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā tāvatiṃsānaṃ devānaṃ…pe… yāmānaṃ devānaṃ… tusitānaṃ devānaṃ… nimmānaratīnaṃ devānaṃ… paranimmitavasavattīnaṃ devānaṃ… brahmakāyikānaṃ devānaṃ sahabyataṃ upapajjeyya’nti, ṭhānaṃ kho panetaṃ vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā brahmakāyikānaṃ devānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkheyya ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā ābhānaṃ devānaṃ sahabyataṃ upapajjeyya’nti, ṭhānaṃ kho panetaṃ vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā ābhānaṃ devānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkheyya ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā parittābhānaṃ devānaṃ…pe… appamāṇābhānaṃ devānaṃ… ābhassarānaṃ devānaṃ… parittasubhānaṃ <a name="V1.0364"></a> devānaṃ… appamāṇasubhānaṃ devānaṃ… subhakiṇhānaṃ devānaṃ… vehapphalānaṃ devānaṃ…</p>\r\n','\r',char(13)),'\n',char(10)),'-442-');
INSERT INTO pages VALUES(3399,'mula_ma_01',360,replace(replace('<p class="bodytext">avihānaṃ <a name="M1.0360"></a> devānaṃ… atappānaṃ devānaṃ… sudassānaṃ devānaṃ… sudassīnaṃ devānaṃ… akaniṭṭhānaṃ devānaṃ… ākāsānañcāyatanūpagānaṃ devānaṃ… viññāṇañcāyatanūpagānaṃ devānaṃ <a name="T1.0527"></a>… ākiñcaññāyatanūpagānaṃ devānaṃ… nevasaññānāsaññāyatanūpagānaṃ devānaṃ sahabyataṃ upapajjeyya’nti, ṭhānaṃ kho panetaṃ vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā nevasaññānāsaññāyatanūpagānaṃ devānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkheyya ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja vihareyya’nti, ṭhānaṃ kho panetaṃ vijjati, yaṃ so āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja vihareyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī"ti.</p>\r\n<p class="bodytext"><a name="para443"></a><a name="para443_mn1"></a><span class="paranum">443</span>. evaṃ <a name="P1.0290"></a> vutte, sāleyyakā brāhmaṇagahapatikā bhagavantaṃ etadavocuṃ – "abhikkantaṃ, bho gotama, abhikkantaṃ, bho gotama! seyyathāpi, bho gotama, nikkujjitaṃ vā ukkujjeyya, paṭicchannaṃ vā vivareyya, mūḷhassa vā maggaṃ ācikkheyya, andhakāre vā telapajjotaṃ dhāreyya, cakkhumanto rūpāni dakkhantīti. evamevaṃ bhotā gotamena anekapariyāyena dhammo pakāsito. ete mayaṃ bhavantaṃ gotamaṃ saraṇaṃ gacchāma dhammañca bhikkhusaṅghañca. upāsake no bhavaṃ gotamo dhāretu ajjatagge pāṇupete <span class="note">[pāṇupetaṃ (ka.)]</span> saraṇaṃ gate"ti.</p>\r\n<p class="centered">sāleyyakasuttaṃ niṭṭhitaṃ paṭhamaṃ.</p>\r\n<p class="subhead">2. verañjakasuttaṃ</p>\r\n<p class="bodytext"><a name="para444"></a><a name="para444_mn1"></a><span class="paranum">444</span>. evaṃ <a name="T1.0528"></a><a name="V1.0365"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tena kho pana samayena verañjakā brāhmaṇagahapatikā sāvatthiyaṃ paṭivasanti kenacideva karaṇīyena.</p>\r\n','\r',char(13)),'\n',char(10)),'-443-444-');
INSERT INTO pages VALUES(3400,'mula_ma_01',361,replace(replace('<p class="bodytext">assosuṃ <a name="M1.0361"></a> kho verañjakā brāhmaṇagahapatikā – "samaṇo khalu, bho, gotamo sakyaputto sakyakulā pabbajito sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. taṃ kho pana bhavantaṃ gotamaṃ evaṃ kalyāṇo kittisaddo abbhuggato – ‘itipi so bhagavā arahaṃ sammāsambuddho vijjācaraṇasampanno sugato lokavidū anuttaro purisadammasārathi satthā devamanussānaṃ buddho bhagavā. so imaṃ lokaṃ sadevakaṃ samārakaṃ sabrahmakaṃ sassamaṇabrāhmaṇiṃ pajaṃ sadevamanussaṃ sayaṃ abhiññā sacchikatvā pavedeti. so dhammaṃ deseti ādikalyāṇaṃ majjhekalyāṇaṃ pariyosānakalyāṇaṃ sātthaṃ sabyañjanaṃ, kevalaparipuṇṇaṃ parisuddhaṃ brahmacariyaṃ pakāseti’. sādhu kho pana tathārūpānaṃ arahataṃ dassanaṃ hotī"ti.</p>\r\n<p class="bodytext">atha kho verañjakā brāhmaṇagahapatikā yena bhagavā tenupasaṅkamiṃsu, upasaṅkamitvā appekacce bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu, appekacce bhagavatā saddhiṃ sammodiṃsu, sammodanīyaṃ kathaṃ <a name="P1.0291"></a> sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdiṃsu, appekacce yena bhagavā tenañjaliṃ paṇāmetvā ekamantaṃ nisīdiṃsu, appekacce bhagavato santike <a name="T1.0529"></a> nāmagottaṃ sāvetvā ekamantaṃ nisīdiṃsu, appekacce tuṇhībhūtā ekamantaṃ nisīdiṃsu. ekamantaṃ nisinnā kho verañjakā brāhmaṇagahapatikā bhagavantaṃ etadavocuṃ – "ko nu kho, bho gotama, hetu, ko paccayo yena midhekacce sattā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti? ko pana, bho gotama, hetu, ko paccayo yena midhekacce sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjantī"ti?</p>\r\n<p class="bodytext">adhammacariyāvisamacariyāhetu kho, gahapatayo, evamidhekacce sattā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti. dhammacariyāsamacariyāhetu kho, gahapatayo<a name="V1.0366"></a>, evamidhekacce sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjantī"ti.</p>\r\n<p class="bodytext">na kho mayaṃ imassa bhoto gotamassa saṃkhittena bhāsitassa, vitthārena atthaṃ avibhattassa, vitthārena atthaṃ ājānāma. sādhu no bhavaṃ gotamo tathā dhammaṃ desetu yathā mayaṃ imassa bhoto gotamassa saṃkhittena bhāsitassa, vitthārena atthaṃ avibhattassa, vitthārena</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3401,'mula_ma_01',362,replace(replace('<p class="bodytext">atthaṃ <a name="M1.0362"></a> ājāneyyāmā"ti. "tena hi, gahapatayo, suṇātha sādhukaṃ manasi karotha, bhāsissāmī"ti. "evaṃ bho"ti kho verañjakā brāhmaṇagahapatikā bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext"><a name="para445"></a><a name="para445_mn1"></a><span class="paranum">445</span>. tividhaṃ kho, gahapatayo, kāyena adhammacārī visamacārī hoti, catubbidhaṃ vācāya adhammacārī visamacārī hoti, tividhaṃ manasā adhammacārī visamacārī hoti.</p>\r\n<p class="bodytext">kathañca, gahapatayo<a name="T1.0530"></a>, tividhaṃ kāyena adhammacārī visamacārī hoti? idha, gahapatayo, ekacco pāṇātipātī hoti. luddo lohitapāṇi hatappahate niviṭṭho adayāpanno pāṇabhūtesu. adinnādāyī kho pana hoti. yaṃ taṃ parassa paravittūpakaraṇaṃ… taṃ adinnaṃ theyyasaṅkhātaṃ ādātā hoti. kāmesumicchācārī kho pana hoti. yā tā māturakkhitā… tathārūpāsu cārittaṃ āpajjitā hoti. evaṃ kho, gahapatayo, tividhaṃ kāyena adhammacārī visamacārī hoti.</p>\r\n<p class="bodytext">kathañca, gahapatayo, catubbidhaṃ vācāya adhammacārī visamacārī hoti? idha, gahapatayo, ekacco musāvādī hoti. sabhāgato vā… sampajānamusā bhāsitā hoti. pisuṇavāco kho pana hoti. ito sutvā amutra akkhātā… vaggakaraṇiṃ vācaṃ bhāsitā hoti. pharusavāco kho pana hoti. yā sā vācā aṇḍakā kakkasā… tathārūpiṃ vācaṃ bhāsitā hoti. samphappalāpī kho pana hoti. akālavādī… apariyantavatiṃ anatthasaṃhitaṃ. evaṃ kho, gahapatayo, catubbidhaṃ vācāya adhammacārī visamacārī hoti.</p>\r\n<p class="bodytext">kathañca, gahapatayo, tividhaṃ manasā adhammacārī visamacārī hoti? idha, gahapatayo, ekacco abhijjhālu hoti…pe… taṃ mamassā’ti. byāpannacitto kho pana hoti paduṭṭhamanasaṅkappo – ime sattā haññantu vā… mā vā ahesu’nti. micchādiṭṭhiko kho pana <a name="V1.0367"></a> hoti viparītadassano – ‘natthi dinnaṃ, natthi yiṭṭhaṃ… sacchikatvā pavedentī’ti. evaṃ <a name="T1.0531"></a> kho, gahapatayo, tividhaṃ manasā adhammacārī visamacārī hoti.</p>\r\n<p class="bodytext">evaṃ adhammacariyāvisamacariyāhetu kho, gahapatayo, evamidhekacce sattā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-445-');
INSERT INTO pages VALUES(3402,'mula_ma_01',363,replace(replace('<p class="bodytext"><a name="para446"></a><a name="para446_mn1"></a><span class="paranum">446</span>. tividhaṃ <a name="M1.0363"></a> kho, gahapatayo, kāyena dhammacārī samacārī hoti, catubbidhaṃ vācāya dhammacārī samacārī hoti, tividhaṃ manasā dhammacārī samacārī hoti.</p>\r\n<p class="bodytext">kathañca, gahapatayo, tividhaṃ kāyena dhammacārī samacārī hoti? idha, gahapatayo, ekacco pāṇātipātaṃ pahāya pāṇātipātā paṭivirato hoti, nihitadaṇḍo nihitasattho lajjī dayāpanno sabbapāṇabhūtahitānukampī viharati. adinnādānaṃ pahāya adinnādānā paṭivirato hoti, yaṃ taṃ parassa… taṃ nādinnaṃ theyyasaṅkhātaṃ ādātā hoti. kāmesumicchācāraṃ pahāya… tathārūpāsu na cārittaṃ āpajjitā hoti. evaṃ kho, gahapatayo, tividhaṃ kāyena dhammacārī samacārī hoti.</p>\r\n<p class="bodytext">kathañca, gahapatayo, catubbidhaṃ vācāya dhammacārī samacārī hoti? idha, gahapatayo, ekacco musāvādaṃ pahāya musāvādā paṭivirato hoti. sabhāgato vā…pe… na sampajānamusā bhāsitā hoti. pisuṇaṃ vācaṃ pahāya… samaggakaraṇiṃ vācaṃ bhāsitā hoti. pharusaṃ vācaṃ pahāya… tathārūpiṃ vācaṃ bhāsitā hoti. samphappalāpaṃ pahāya… kālena sāpadesaṃ pariyantavatiṃ atthasaṃhitaṃ<a name="T1.0532"></a>. evaṃ kho, gahapatayo, catubbidhaṃ vācāya dhammacārī samacārī hoti.</p>\r\n<p class="bodytext">kathañca, gahapatayo, tividhaṃ manasā dhammacārī samacārī hoti? idha, gahapatayo, ekacco anabhijjhālu hoti. yaṃ taṃ parassa paravittūpakaraṇaṃ taṃ nābhijjhātā hoti ‘aho vata yaṃ parassa, taṃ mamassā’ti. abyāpannacitto kho pana hoti appaduṭṭhamanasaṅkappo – ‘ime sattā averā abyābajjhā anīghā sukhī attānaṃ pariharantū’ti. sammādiṭṭhiko kho pana hoti aviparītadassano – ‘atthi dinnaṃ, atthi yiṭṭhaṃ… sayaṃ abhiññā sacchikatvā pavedentī’ti. evaṃ kho, gahapatayo, tividhaṃ manasā dhammacārī samacārī hoti.</p>\r\n<p class="bodytext">evaṃ <a name="V1.0368"></a> dhammacariyāsamacariyāhetu kho, gahapatayo, evamidhekacce sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjanti.</p>\r\n<p class="bodytext"><a name="para447"></a><a name="para447_mn1"></a><span class="paranum">447</span>. ākaṅkheyya ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā khattiyamahāsālānaṃ sahabyataṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-446-447-');
INSERT INTO pages VALUES(3403,'mula_ma_01',364,replace(replace('<p class="bodytext">upapajjeyya’nti<a name="M1.0364"></a>, ṭhānaṃ kho panetaṃ vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā khattiyamahāsālānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkheyya ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā brāhmaṇamahāsālānaṃ gahapatimahāsālānaṃ sahabyataṃ upapajjeyya’nti, ṭhānaṃ kho panetaṃ vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā gahapatimahāsālānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu<a name="T1.0533"></a>? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkeyya ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā cātumahārājikānaṃ devānaṃ sahabyataṃ upapajjeyya’nti, ṭhānaṃ kho panetaṃ vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā cātumahārājikānaṃ devānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkheyya ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā tāvatiṃsānaṃ devānaṃ… yāmānaṃ devānaṃ… tusitānaṃ devānaṃ… nimmānaratīnaṃ devānaṃ… paranimmitavasavattīnaṃ devānaṃ… brahmakāyikānaṃ devānaṃ sahabyataṃ upapajjeyya’nti, ṭhānaṃ kho panetaṃ vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā brahmakāyikānaṃ devānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkheyya ce, gahapatayo, dhammacārī samacārī – ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā ābhānaṃ devānaṃ sahabyataṃ upapajjeyya’nti, ṭhānaṃ kho panetaṃ vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā ābhānaṃ devānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkheyya <a name="V1.0369"></a> ce, gahapatayo, dhammacārī samacārī ‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā parittābhānaṃ devānaṃ…pe… appamāṇābhānaṃ devānaṃ… ābhassarānaṃ devānaṃ… parittasubhānaṃ devānaṃ… appamāṇasubhānaṃ devānaṃ… subhakiṇhānaṃ devānaṃ <a name="T1.0534"></a>… vehapphalānaṃ devānaṃ… avihānaṃ devānaṃ… atappānaṃ devānaṃ… sudassānaṃ devānaṃ… sudassīnaṃ devānaṃ… akaniṭṭhānaṃ devānaṃ… ākāsānañcāyatanūpagānaṃ devānaṃ…</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3404,'mula_ma_01',365,replace(replace('<p class="bodytext">viññāṇañcāyatanūpagānaṃ <a name="M1.0365"></a> devānaṃ… ākiñcaññāyatanūpagānaṃ devānaṃ… nevasaññānāsaññāyatanūpagānaṃ devānaṃ sahabyataṃ upapajjeyya’nti, ṭhānaṃ kho panetaṃ vijjati, yaṃ so kāyassa bhedā paraṃ maraṇā nevasaññānāsaññāyatanūpagānaṃ devānaṃ sahabyataṃ upapajjeyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī.</p>\r\n<p class="bodytext">ākaṅkheyya ce gahapatayo dhammacārī samacārī – ‘aho vatāhaṃ āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja vihareyya’nti, ṭhānaṃ kho panetaṃ vijjati, ‘yaṃ so āsavānaṃ khayā anāsavaṃ cetovimuttiṃ paññāvimuttiṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja vihareyya. taṃ kissa hetu? tathā hi so dhammacārī samacārī"’ti.</p>\r\n<p class="bodytext"><a name="para448"></a><a name="para448_mn1"></a><span class="paranum">448</span>. evaṃ vutte, verañjakā brāhmaṇagahapatikā bhagavantaṃ etadavocuṃ – "abhikkantaṃ, bho gotama, abhikkantaṃ bho gotama! seyyathāpi, bho gotama, nikkujjitaṃ vā ukkujjeyya, paṭicchannaṃ vā vivareyya, mūḷhassa vā maggaṃ ācikkheyya, andhakāre vā telapajjotaṃ dhāreyya, cakkhumanto rūpāni dakkhantīti, evamevaṃ bhotā gotamena anekapariyāyena dhammo pakāsito. ete mayaṃ bhavantaṃ gotamaṃ saraṇaṃ gacchāma <a name="T1.0535"></a> dhammañca bhikkhusaṅghañca. upāsake no bhavaṃ gotamo dhāretu ajjatagge pāṇupete saraṇaṃ gate"ti.</p>\r\n<p class="centered">verañjakasuttaṃ niṭṭhitaṃ dutiyaṃ.</p>\r\n<p class="subhead">3. mahāvedallasuttaṃ</p>\r\n<p class="bodytext"><a name="para449"></a><a name="para449_mn1"></a><span class="paranum">449</span>. evaṃ <a name="T1.0536"></a><a name="V1.0370"></a><a name="P1.0292"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. atha kho āyasmā mahākoṭṭhiko sāyanhasamayaṃ paṭisallānā vuṭṭhito yenāyasmā sāriputto tenupasaṅkami, upasaṅkamitvā āyasmatā sāriputtena saddhiṃ sammodi. sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho āyasmā mahākoṭṭhiko āyasmantaṃ sāriputtaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-448-449-');
INSERT INTO pages VALUES(3405,'mula_ma_01',366,replace(replace('<p class="bodytext">etadavoca <a name="M1.0366"></a> –</p>\r\n<p class="bodytext">‘duppañño duppañño’ti, āvuso, vuccati. kittāvatā nu kho, āvuso, duppaññoti vuccatī"ti?</p>\r\n<p class="bodytext">‘nappajānāti nappajānātī’ti kho, āvuso, tasmā duppaññoti vuccati.</p>\r\n<p class="bodytext">kiñca nappajānāti? ‘idaṃ dukkha’nti nappajānāti, ‘ayaṃ dukkhasamudayo’ti nappajānāti, ‘ayaṃ dukkhanirodho’ti nappajānāti, ‘ayaṃ dukkhanirodhagāminī paṭipadā’ti nappajānāti. ‘nappajānāti nappajānātī’ti kho, āvuso, tasmā duppaññoti vuccatī"ti.</p>\r\n<p class="bodytext">‘sādhāvuso’ti kho āyasmā mahākoṭṭhiko āyasmato sāriputtassa bhāsitaṃ abhinanditvā anumoditvā āyasmantaṃ sāriputtaṃ uttariṃ pañhaṃ apucchi –</p>\r\n<p class="bodytext">‘paññavā paññavā’ti, āvuso, vuccati. kittāvatā nu kho, āvuso, paññavāti vuccatī"ti?</p>\r\n<p class="bodytext">‘pajānāti pajānātī’ti kho, āvuso, tasmā paññavāti vuccati.</p>\r\n<p class="bodytext">kiñca pajānāti? ‘idaṃ dukkha’nti pajānāti, ‘ayaṃ dukkhasamudayo’ti pajānāti, ‘ayaṃ dukkhanirodho’ti <a name="T1.0537"></a><a name="V1.0371"></a> pajānāti, ‘ayaṃ dukkhanirodhagāminī paṭipadā’ti pajānāti. ‘pajānāti pajānātī’ti kho, āvuso, tasmā paññavāti vuccatī"ti.</p>\r\n<p class="bodytext">‘viññāṇaṃ viññāṇa’nti, āvuso, vuccati. kittāvatā nu kho, āvuso, viññāṇanti vuccatī"ti?</p>\r\n<p class="bodytext">‘vijānāti vijānātī’ti kho, āvuso, tasmā viññāṇanti vuccati.</p>\r\n<p class="bodytext">kiñca vijānāti? sukhantipi vijānāti, dukkhantipi vijānāti, adukkhamasukhantipi vijānāti. ‘vijānāti vijānātī’ti kho, āvuso, tasmā viññāṇanti vuccatī"ti.</p>\r\n<p class="bodytext">yā cāvuso, paññā yañca viññāṇaṃ – ime dhammā saṃsaṭṭhā udāhu visaṃsaṭṭhā? labbhā ca panimesaṃ dhammānaṃ vinibbhujitvā <span class="note">[vinibbhujjitvā vinibbhujjitvā (ka.)]</span> vinibbhujitvā nānākaraṇaṃ paññāpetu"nti? "yā cāvuso, paññā yañca viññāṇaṃ – ime dhammā saṃsaṭṭhā, no visaṃsaṭṭhā. na ca labbhā imesaṃ dhammānaṃ vinibbhujitvā vinibbhujitvā nānākaraṇaṃ paññāpetuṃ. yaṃ hāvuso <span class="note">[yañcāvuso (syā. kaṃ. ka.)]</span>, pajānāti taṃ vijānāti, yaṃ vijānāti taṃ pajānāti. tasmā <a name="P1.0293"></a> ime dhammā saṃsaṭṭhā, no visaṃsaṭṭhā. na ca labbhā imesaṃ dhammānaṃ vinibbhujitvā vinibbhujitvā nānākaraṇaṃ paññāpetu"nti.</p>\r\n<p class="bodytext">yā cāvuso, paññā yañca viññāṇaṃ – imesaṃ dhammānaṃ saṃsaṭṭhānaṃ no visaṃsaṭṭhānaṃ kiṃ nānākaraṇa"nti? "yā cāvuso, paññā yañca viññāṇaṃ –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3406,'mula_ma_01',367,replace(replace('<p class="bodytext">imesaṃ <a name="M1.0367"></a> dhammānaṃ saṃsaṭṭhānaṃ no visaṃsaṭṭhānaṃ paññā bhāvetabbā, viññāṇaṃ pariññeyyaṃ. idaṃ nesaṃ nānākaraṇa"nti.</p>\r\n<p class="bodytext"><a name="para450"></a><a name="para450_mn1"></a><span class="paranum">450</span>. ‘vedanā vedanā’ti, āvuso, vuccati. kittāvatā nu kho, āvuso<a name="T1.0538"></a>, vedanāti vuccatī"ti?</p>\r\n<p class="bodytext">‘vedeti vedetī’ti kho, āvuso, tasmā vedanāti vuccati.</p>\r\n<p class="bodytext">kiñca <a name="V1.0372"></a> vedeti? sukhampi vedeti, dukkhampi vedeti, adukkhamasukhampi vedeti. ‘vedeti vedetī’ti kho, āvuso, tasmā vedanāti vuccatī"ti.</p>\r\n<p class="bodytext">‘saññā saññā’ti, āvuso, vuccati. kittāvatā nu kho, āvuso, saññāti vuccatī"ti?</p>\r\n<p class="bodytext">‘sañjānāti sañjānātī’ti kho, āvuso, tasmā saññāti vuccati.</p>\r\n<p class="bodytext">kiñca sañjānāti? nīlakampi sañjānāti, pītakampi sañjānāti, lohitakampi sañjānāti, odātampi sañjānāti. ‘sañjānāti sañjānātī’ti kho, āvuso, tasmā saññāti vuccatī"ti.</p>\r\n<p class="bodytext">yā cāvuso, vedanā yā ca saññā yañca viññāṇaṃ – ime dhammā saṃsaṭṭhā udāhu visaṃsaṭṭhā? labbhā ca panimesaṃ dhammānaṃ vinibbhujitvā vinibbhujitvā nānākaraṇaṃ paññāpetu"nti? "yā cāvuso, vedanā yā ca saññā yañca viññāṇaṃ – ime dhammā saṃsaṭṭhā, no visaṃsaṭṭhā. na ca labbhā imesaṃ dhammānaṃ vinibbhujitvā vinibbhujitvā nānākaraṇaṃ paññāpetuṃ. yaṃ hāvuso <span class="note">[yañcāvuso (syā. kaṃ. ka.)]</span>, vedeti taṃ sañjānāti, yaṃ sañjānāti taṃ vijānāti. tasmā ime dhammā saṃsaṭṭhā no visaṃsaṭṭhā. na ca labbhā imesaṃ dhammānaṃ vinibbhujitvā vinibbhujitvā nānākaraṇaṃ paññāpetu"nti.</p>\r\n<p class="bodytext"><a name="para451"></a><a name="para451_mn1"></a><span class="paranum">451</span>. nissaṭṭhena hāvuso <span class="note">[nissaṭṭhena panāvuso (?)]</span>, pañcahi indriyehi parisuddhena manoviññāṇena kiṃ neyya"nti?</p>\r\n<p class="bodytext">nissaṭṭhena āvuso, pañcahi indriyehi parisuddhena manoviññāṇena ‘ananto ākāso’ti ākāsānañcāyatanaṃ <a name="T1.0539"></a> neyyaṃ, ‘anantaṃ viññāṇa’nti viññāṇañcāyatanaṃ neyyaṃ, ‘natthi kiñcī’ti ākiñcaññāyatanaṃ neyya"nti.</p>\r\n<p class="bodytext">neyyaṃ panāvuso, dhammaṃ kena pajānātī"ti?</p>\r\n<p class="bodytext">neyyaṃ <a name="V1.0373"></a> kho, āvuso, dhammaṃ paññācakkhunā pajānātī"ti.</p>\r\n<p class="bodytext">paññā panāvuso, kimatthiyā"ti?</p>\r\n<p class="bodytext">paññā kho, āvuso, abhiññatthā pariññatthā pahānatthā"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-450-451-');
INSERT INTO pages VALUES(3407,'mula_ma_01',368,replace(replace('<p class="bodytext"><a name="para452"></a><a name="para452_mn1"></a><span class="paranum">452</span>. kati <a name="M1.0368"></a><a name="P1.0294"></a> panāvuso, paccayā sammādiṭṭhiyā uppādāyā"ti?</p>\r\n<p class="bodytext">dve kho, āvuso, paccayā sammādiṭṭhiyā uppādāya – parato ca ghoso, yoniso ca manasikāro. ime kho, āvuso, dve paccayā sammādiṭṭhiyā uppādāyā"ti.</p>\r\n<p class="bodytext">katihi panāvuso, aṅgehi anuggahitā sammādiṭṭhi cetovimuttiphalā ca hoti cetovimuttiphalānisaṃsā ca, paññāvimuttiphalā ca hoti paññāvimuttiphalānisaṃsā cā"ti?</p>\r\n<p class="bodytext">pañcahi kho, āvuso, aṅgehi anuggahitā sammādiṭṭhi cetovimuttiphalā ca hoti cetovimuttiphalānisaṃsā ca, paññāvimuttiphalā ca hoti paññāvimuttiphalānisaṃsā ca. idhāvuso, sammādiṭṭhi sīlānuggahitā ca hoti, sutānuggahitā ca hoti, sākacchānuggahitā ca hoti, samathānuggahitā ca hoti, vipassanānuggahitā ca hoti. imehi kho, āvuso, pañcahaṅgehi anuggahitā sammādiṭṭhi cetovimuttiphalā ca hoti cetovimuttiphalānisaṃsā ca, paññāvimuttiphalā ca hoti paññāvimuttiphalānisaṃsā cā"ti.</p>\r\n<p class="bodytext"><a name="para453"></a><a name="para453_mn1"></a><span class="paranum">453</span>. kati panāvuso, bhavā"ti?</p>\r\n<p class="bodytext">tayome, āvuso, bhavā – kāmabhavo<a name="T1.0540"></a>, rūpabhavo, arūpabhavo"ti.</p>\r\n<p class="bodytext">kathaṃ panāvuso, āyatiṃ punabbhavābhinibbatti hotī"ti?</p>\r\n<p class="bodytext">avijjānīvaraṇānaṃ <a name="V1.0374"></a> kho, āvuso, sattānaṃ taṇhāsaṃyojanānaṃ tatratatrābhinandanā – evaṃ āyatiṃ punabbhavābhinibbatti hotī"ti.</p>\r\n<p class="bodytext">kathaṃ panāvuso, āyatiṃ punabbhavābhinibbatti na hotī"ti?</p>\r\n<p class="bodytext">avijjāvirāgā kho, āvuso, vijjuppādā taṇhānirodhā – evaṃ āyatiṃ punabbhavābhinibbatti na hotī"ti.</p>\r\n<p class="bodytext"><a name="para454"></a><a name="para454_mn1"></a><span class="paranum">454</span>. katamaṃ panāvuso, paṭhamaṃ jhāna"nti?</p>\r\n<p class="bodytext">idhāvuso, bhikkhu vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati – idaṃ vuccati, āvuso, paṭhamaṃ jhāna"nti.</p>\r\n<p class="bodytext">paṭhamaṃ panāvuso, jhānaṃ katiaṅgika"nti?</p>\r\n<p class="bodytext">paṭhamaṃ kho, āvuso, jhānaṃ pañcaṅgikaṃ. idhāvuso, paṭhamaṃ jhānaṃ samāpannassa bhikkhuno vitakko ca vattati, vicāro ca pīti ca sukhañca cittekaggatā ca. paṭhamaṃ kho, āvuso, jhānaṃ evaṃ pañcaṅgika"nti.</p>\r\n<p class="bodytext">paṭhamaṃ panāvuso, jhānaṃ kataṅgavippahīnaṃ kataṅgasamannāgata"nti?</p>\r\n<p class="bodytext">paṭhamaṃ kho, āvuso, jhānaṃ pañcaṅgavippahīnaṃ, pañcaṅgasamannāgataṃ. idhāvuso, paṭhamaṃ jhānaṃ samāpannassa bhikkhuno kāmacchando</p>\r\n','\r',char(13)),'\n',char(10)),'-452-453-454-');
INSERT INTO pages VALUES(3408,'mula_ma_01',369,replace(replace('<p class="bodytext">pahīno <a name="M1.0369"></a> hoti, byāpādo pahīno hoti, thīnamiddhaṃ pahīnaṃ hoti, uddhaccakukkuccaṃ pahīnaṃ <a name="P1.0295"></a> hoti, vicikicchā pahīnā hoti, vitakko ca vattati, vicāro ca pīti ca sukhañca cittekaggatā ca. paṭhamaṃ kho, āvuso, jhānaṃ evaṃ pañcaṅgavippahīnaṃ pañcaṅgasamannāgata"nti.</p>\r\n<p class="bodytext"><a name="para455"></a><a name="para455_mn1"></a><span class="paranum">455</span>. pañcimāni<a name="T1.0541"></a>, āvuso, indriyāni nānāvisayāni nānāgocarāni, na aññamaññassa <a name="V1.0375"></a> gocaravisayaṃ paccanubhonti, seyyathidaṃ – cakkhundriyaṃ, sotindriyaṃ, ghānindriyaṃ, jivhindriyaṃ, kāyindriyaṃ. imesaṃ kho, āvuso, pañcannaṃ indriyānaṃ nānāvisayānaṃ nānāgocarānaṃ, na aññamaññassa gocaravisayaṃ paccanubhontānaṃ, kiṃ paṭisaraṇaṃ, ko ca nesaṃ gocaravisayaṃ paccanubhotī"ti?</p>\r\n<p class="bodytext">pañcimāni, āvuso, indriyāni nānāvisayāni nānāgocarāni, na aññamaññassa gocaravisayaṃ paccanubhonti, seyyathidaṃ – cakkhundriyaṃ, sotindriyaṃ, ghānindriyaṃ, jivhindriyaṃ, kāyindriyaṃ. imesaṃ kho, āvuso, pañcannaṃ indriyānaṃ nānāvisayānaṃ nānāgocarānaṃ, na aññamaññassa gocaravisayaṃ paccanubhontānaṃ, mano paṭisaraṇaṃ, mano ca nesaṃ gocaravisayaṃ paccanubhotī"ti.</p>\r\n<p class="bodytext"><a name="para456"></a><a name="para456_mn1"></a><span class="paranum">456</span>. pañcimāni, āvuso, indriyāni, seyyathidaṃ – cakkhundriyaṃ, sotindriyaṃ, ghānindriyaṃ, jivhindriyaṃ, kāyindriyaṃ. imāni kho, āvuso, pañcindriyāni kiṃ paṭicca tiṭṭhantī"ti?</p>\r\n<p class="bodytext">pañcimāni, āvuso, indriyāni, seyyathidaṃ – cakkhundriyaṃ, sotindriyaṃ, ghānindriyaṃ, jivhindriyaṃ, kāyindriyaṃ. imāni kho, āvuso, pañcindriyāni āyuṃ paṭicca tiṭṭhantī"ti.</p>\r\n<p class="bodytext">āyu panāvuso, kiṃ paṭicca tiṭṭhatī"ti?</p>\r\n<p class="bodytext">āyu usmaṃ paṭicca tiṭṭhatī"ti.</p>\r\n<p class="bodytext">usmā panāvuso, kiṃ paṭicca tiṭṭhatī"ti?</p>\r\n<p class="bodytext">usmā āyuṃ paṭicca tiṭṭhatī"ti.</p>\r\n<p class="bodytext">idāneva kho mayaṃ, āvuso, āyasmato sāriputtassa bhāsitaṃ evaṃ ājānāma – ‘āyu <a name="V1.0376"></a> usmaṃ paṭicca tiṭṭhatī’ti. idāneva pana mayaṃ, āvuso, āyasmato <a name="T1.0542"></a> sāriputtassa bhāsitaṃ evaṃ ājānāma – ‘usmā āyuṃ paṭicca tiṭṭhatī’ti.</p>\r\n<p class="bodytext">yathā kathaṃ panāvuso, imassa bhāsitassa attho daṭṭhabbo"ti?</p>\r\n<p class="bodytext">tena hāvuso, upamaṃ te karissāmi, upamāyapidhekacce viññū purisā bhāsitassa atthaṃ ājānanti. seyyathāpi, āvuso, telappadīpassa jhāyato acciṃ paṭicca ābhā paññāyati, ābhaṃ paṭicca acci paññāyati, evameva kho, āvuso, āyu usmaṃ paṭicca tiṭṭhati, usmā āyuṃ paṭicca tiṭṭhatī"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-455-456-');
INSERT INTO pages VALUES(3409,'mula_ma_01',370,replace(replace('<p class="bodytext"><a name="para457"></a><a name="para457_mn1"></a><span class="paranum">457</span>. teva <a name="M1.0370"></a> nu kho, āvuso, āyusaṅkhārā, te vedaniyā dhammā udāhu aññe āyusaṅkhārā aññe vedaniyā dhammā"ti? "na kho<a name="P1.0296"></a>, āvuso, teva āyusaṅkhārā te vedaniyā dhammā. te ca hāvuso, āyusaṅkhārā abhaviṃsu te vedaniyā dhammā, na yidaṃ saññāvedayitanirodhaṃ samāpannassa bhikkhuno vuṭṭhānaṃ paññāyetha. yasmā ca kho, āvuso, aññe āyusaṅkhārā aññe vedaniyā dhammā, tasmā saññāvedayitanirodhaṃ samāpannassa bhikkhuno vuṭṭhānaṃ paññāyatī"ti.</p>\r\n<p class="bodytext">yadā nu kho, āvuso, imaṃ kāyaṃ kati dhammā jahanti, athāyaṃ kāyo ujjhito avakkhitto seti, yathā kaṭṭhaṃ acetana"nti?</p>\r\n<p class="bodytext">yadā kho, āvuso, imaṃ kāyaṃ tayo dhammā jahanti – āyu usmā ca viññāṇaṃ, athāyaṃ kāyo ujjhito avakkhitto seti, yathā kaṭṭhaṃ acetana"nti.</p>\r\n<p class="bodytext">yvāyaṃ, āvuso, mato kālaṅkato, yo cāyaṃ bhikkhu saññāvedayitanirodhaṃ samāpanno – imesaṃ kiṃ nānākaraṇa"nti?</p>\r\n<p class="bodytext">yvāyaṃ, āvuso, mato kālaṅkato tassa kāyasaṅkhārā niruddhā paṭippassaddhā<a name="T1.0543"></a>, vacīsaṅkhārā niruddhā paṭippassaddhā, cittasaṅkhārā niruddhā paṭippassaddhā, āyu parikkhīṇo, usmā vūpasantā, indriyāni paribhinnāni. yo cāyaṃ bhikkhu saññāvedayitanirodhaṃ samāpanno tassapi kāyasaṅkhārā niruddhā paṭippassaddhā, vacīsaṅkhārā niruddhā paṭippassaddhā, cittasaṅkhārā niruddhā paṭippassaddhā, āyu na parikkhīṇo, usmā avūpasantā, indriyāni vippasannāni. yvāyaṃ, āvuso, mato kālaṅkato, yo cāyaṃ bhikkhu saññāvedayitanirodhaṃ samāpanno – idaṃ nesaṃ nānākaraṇa"nti.</p>\r\n<p class="bodytext"><a name="para458"></a><a name="para458_mn1"></a><span class="paranum">458</span>. kati <a name="V1.0377"></a> panāvuso, paccayā adukkhamasukhāya cetovimuttiyā samāpattiyā"ti?</p>\r\n<p class="bodytext">cattāro kho, āvuso, paccayā adukkhamasukhāya cetovimuttiyā samāpattiyā. idhāvuso, bhikkhu sukhassa ca pahānā dukkhassa ca pahānā pubbeva somanassadomanassānaṃ atthaṅgamā adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja viharati. ime kho, āvuso, cattāro paccayā adukkhamasukhāya cetovimuttiyā samāpattiyā"ti.</p>\r\n<p class="bodytext">kati panāvuso, paccayā animittāya cetovimuttiyā samāpattiyā"ti?</p>\r\n<p class="bodytext">dve kho, āvuso, paccayā animittāya cetovimuttiyā</p>\r\n','\r',char(13)),'\n',char(10)),'-457-458-');
INSERT INTO pages VALUES(3410,'mula_ma_01',371,replace(replace('<p class="bodytext">samāpattiyā <a name="M1.0371"></a> – sabbanimittānañca amanasikāro, animittāya ca dhātuyā manasikāro. ime kho, āvuso, dve paccayā animittāya cetovimuttiyā samāpattiyā"ti.</p>\r\n<p class="bodytext">kati panāvuso, paccayā animittāya cetovimuttiyā ṭhitiyā"ti?</p>\r\n<p class="bodytext">tayo kho, āvuso, paccayā animittāya cetovimuttiyā <a name="T1.0544"></a> ṭhitiyā <a name="P1.0297"></a> – sabbanimittānañca amanasikāro, animittāya ca dhātuyā manasikāro, pubbe ca abhisaṅkhāro. ime kho, āvuso, tayo paccayā animittāya cetovimuttiyā ṭhitiyā"ti.</p>\r\n<p class="bodytext">kati panāvuso, paccayā animittāya cetovimuttiyā vuṭṭhānāyā"ti?</p>\r\n<p class="bodytext">dve kho, āvuso, paccayā animittāya cetovimuttiyā vuṭṭhānāya – sabbanimittānañca manasikāro, animittāya ca dhātuyā amanasikāro. ime kho, āvuso, dve paccayā animittāya cetovimuttiyā vuṭṭhānāyā"ti.</p>\r\n<p class="bodytext"><a name="para459"></a><a name="para459_mn1"></a><span class="paranum">459</span>. yā cāyaṃ, āvuso, appamāṇā cetovimutti, yā ca ākiñcaññā cetovimutti, yā ca suññatā cetovimutti, yā ca animittā cetovimutti – ime dhammā nānātthā ceva nānābyañjanā ca udāhu ekatthā byañjanameva nāna"nti?</p>\r\n<p class="bodytext">yā cāyaṃ, āvuso, appamāṇā cetovimutti, yā ca ākiñcaññā cetovimutti, yā ca suññatā cetovimutti, yā ca animittā cetovimutti – atthi kho, āvuso, pariyāyo yaṃ pariyāyaṃ āgamma ime dhammā nānātthā ceva nānābyañjanā ca, atthi ca kho, āvuso, pariyāyo yaṃ pariyāyaṃ āgamma ime dhammā ekatthā, byañjanameva nānaṃ".</p>\r\n<p class="bodytext">katamo <a name="V1.0378"></a> cāvuso, pariyāyo yaṃ pariyāyaṃ āgamma ime dhammā nānātthā ceva nānābyañjanā ca"?</p>\r\n<p class="bodytext">idhāvuso, bhikkhu mettāsahagatena cetasā ekaṃ disaṃ pharitvā viharati, tathā dutiyaṃ, tathā tatiyaṃ, tathā catutthaṃ. iti uddhamadho tiriyaṃ sabbadhi sabbattatāya sabbāvantaṃ lokaṃ mettāsahagatena <a name="T1.0545"></a> cetasā vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā viharati. karuṇāsahagatena cetasā…pe… muditāsahagatena cetasā… upekkhāsahagatena cetasā ekaṃ disaṃ pharitvā viharati, tathā dutiyaṃ, tathā tatiyaṃ, tathā catutthaṃ. iti</p>\r\n','\r',char(13)),'\n',char(10)),'-459-');
INSERT INTO pages VALUES(3411,'mula_ma_01',372,replace(replace('<p class="bodytext">uddhamadho <a name="M1.0372"></a> tiriyaṃ sabbadhi sabbattatāya sabbāvantaṃ lokaṃ upekkhāsahagatena cetasā vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā viharati. ayaṃ vuccatāvuso, appamāṇā cetovimutti".</p>\r\n<p class="bodytext">katamā cāvuso, ākiñcaññā cetovimutti"?</p>\r\n<p class="bodytext">idhāvuso, bhikkhu sabbaso viññāṇañcāyatanaṃ samatikkamma natthi kiñcīti ākiñcaññāyatanaṃ upasampajja viharati. ayaṃ vuccatāvuso, ākiñcaññā cetovimutti".</p>\r\n<p class="bodytext">katamā cāvuso, suññatā cetovimutti"?</p>\r\n<p class="bodytext">idhāvuso, bhikkhu araññagato vā rukkhamūlagato vā suññāgāragato vā iti paṭisañcikkhati – ‘suññamidaṃ attena vā attaniyena vā’ti. ayaṃ <a name="P1.0298"></a> vuccatāvuso, suññatā cetovimutti".</p>\r\n<p class="bodytext">katamā cāvuso, animittā cetovimutti"?</p>\r\n<p class="bodytext">idhāvuso, bhikkhu sabbanimittānaṃ amanasikārā animittaṃ cetosamādhiṃ upasampajja viharati. ayaṃ vuccatāvuso, animittā cetovimutti. ayaṃ kho, āvuso, pariyāyo yaṃ pariyāyaṃ āgamma ime dhammā nānātthā ceva nānābyañjanā ca".</p>\r\n<p class="bodytext">katamo cāvuso, pariyāyo yaṃ pariyāyaṃ āgamma ime dhammā ekatthā byañjanameva nānaṃ"?</p>\r\n<p class="bodytext">rāgo kho, āvuso, pamāṇakaraṇo, doso pamāṇakaraṇo, moho pamāṇakaraṇo. te khīṇāsavassa bhikkhuno pahīnā <a name="T1.0546"></a> ucchinnamūlā tālāvatthukatā anabhāvaṃkatā āyatiṃ anuppādadhammā. yāvatā kho, āvuso, appamāṇā cetovimuttiyo, akuppā tāsaṃ cetovimutti aggamakkhāyati. sā kho panākuppā cetovimutti suññā rāgena, suññā dosena, suññā mohena. rāgo kho, āvuso, kiñcano, doso kiñcano, moho kiñcano. te khīṇāsavassa bhikkhuno pahīnā ucchinnamūlā tālāvatthukatā anabhāvaṃkatā āyatiṃ anuppādadhammā. yāvatā kho, āvuso, ākiñcaññā cetovimuttiyo, akuppā tāsaṃ cetovimutti aggamakkhāyati. sā kho panākuppā cetovimutti suññā rāgena, suññā dosena<a name="V1.0379"></a>, suññā mohena. rāgo kho, āvuso, nimittakaraṇo, doso nimittakaraṇo, moho nimittakaraṇo.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3412,'mula_ma_01',373,replace(replace('<p class="bodytext">te <a name="M1.0373"></a> khīṇāsavassa bhikkhuno pahīnā ucchinnamūlā tālāvatthukatā anabhāvaṃkatā āyatiṃ anuppādadhammā. yāvatā kho, āvuso, animittā cetovimuttiyo, akuppā tāsaṃ cetovimutti aggamakkhāyati. sā kho panākuppā cetovimutti suññā rāgena, suññā dosena, suññā mohena. ayaṃ kho, āvuso, pariyāyo yaṃ pariyāyaṃ āgamma ime dhammā ekatthā byañjanameva nāna"nti.</p>\r\n<p class="bodytext">idamavocāyasmā sāriputto. attamano āyasmā mahākoṭṭhiko āyasmato sāriputtassa bhāsitaṃ abhinandīti.</p>\r\n<p class="centered">mahāvedallasuttaṃ niṭṭhitaṃ tatiyaṃ.</p>\r\n<p class="subhead">4. cūḷavedallasuttaṃ</p>\r\n<p class="bodytext"><a name="para460"></a><a name="para460_mn1"></a><span class="paranum">460</span>. evaṃ <a name="T1.0547"></a><a name="V1.0380"></a><a name="P1.0299"></a> me sutaṃ – ekaṃ samayaṃ bhagavā rājagahe viharati veḷuvane kalandakanivāpe. atha kho visākho upāsako yena dhammadinnā bhikkhunī tenupasaṅkami, upasaṅkamitvā dhammadinnaṃ bhikkhuniṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho visākho upāsako dhammadinnaṃ bhikkhuniṃ etadavoca – "‘sakkāyo sakkāyo’ti, ayye, vuccati. katamo nu kho, ayye, sakkāyo vutto bhagavatā"ti? "pañca kho ime, āvuso visākha, upādānakkhandhā sakkāyo vutto bhagavatā, seyyathidaṃ – rūpupādānakkhandho, vedanupādānakkhandho, saññupādānakkhandho, saṅkhārupādānakkhandho, viññāṇupādānakkhandho. ime kho, āvuso visākha, pañcupādānakkhandhā sakkāyo vutto bhagavatā"ti.</p>\r\n<p class="bodytext">sādhayye"ti kho visākho upāsako dhammadinnāya bhikkhuniyā bhāsitaṃ abhinanditvā anumoditvā dhammadinnaṃ bhikkhuniṃ uttariṃ pañhaṃ apucchi – "‘sakkāyasamudayo sakkāyasamudayo’ti, ayye, vuccati. katamo nu kho, ayye, sakkāyasamudayo vutto bhagavatā"ti? "yāyaṃ, āvuso visākha, taṇhā ponobbhavikā nandīrāgasahagatā tatratatrābhinandinī, seyyathidaṃ – kāmataṇhā bhavataṇhā vibhavataṇhā, ayaṃ kho, āvuso visākha, sakkāyasamudayo vutto bhagavatā"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-460-');
INSERT INTO pages VALUES(3413,'mula_ma_01',374,replace(replace('<p class="bodytext">‘sakkāyanirodho <a name="M1.0374"></a> sakkāyanirodho’ti, ayye, vuccati. katamo nu kho, ayye, sakkāyanirodho vutto bhagavatā"ti?</p>\r\n<p class="bodytext">yo <a name="T1.0548"></a> kho, āvuso visākha, tassāyeva taṇhāya asesavirāganirodho cāgo paṭinissaggo mutti anālayo, ayaṃ kho, āvuso visākha, sakkāyanirodho vutto bhagavatā"ti.</p>\r\n<p class="bodytext">‘sakkāyanirodhagāminī paṭipadā sakkāyanirodhagāminī paṭipadā’ti, ayye, vuccati. katamā nu kho, ayye, sakkāyanirodhagāminī paṭipadā vuttā bhagavatā"ti?</p>\r\n<p class="bodytext">ayameva kho, āvuso visākha, ariyo aṭṭhaṅgiko maggo sakkāyanirodhagāminī paṭipadā vuttā bhagavatā, seyyathidaṃ <a name="V1.0381"></a> – sammādiṭṭhi sammāsaṅkappo sammāvācā sammākammanto sammāājīvo sammāvāyāmo sammāsati sammāsamādhī"ti.</p>\r\n<p class="bodytext">taññeva nu kho, ayye, upādānaṃ te <span class="note">[teva (sī.)]</span> pañcupādānakkhandhā udāhu aññatra pañcahupādānakkhandhehi upādāna"nti? "na kho, āvuso visākha, taññeva upādānaṃ te pañcupādānakkhandhā, nāpi <a name="P1.0300"></a> aññatra pañcahupādānakkhandhehi upādānaṃ. yo kho, āvuso visākha, pañcasu upādānakkhandhesu chandarāgo taṃ tattha upādāna"nti.</p>\r\n<p class="bodytext"><a name="para461"></a><a name="para461_mn1"></a><span class="paranum">461</span>. kathaṃ panāyye, sakkāyadiṭṭhi hotī"ti? "idhāvuso visākha, assutavā puthujjano, ariyānaṃ adassāvī ariyadhammassa akovido ariyadhamme avinīto, sappurisānaṃ adassāvī sappurisadhammassa akovido sappurisadhamme avinīto, rūpaṃ attato samanupassati, rūpavantaṃ vā attānaṃ, attani vā rūpaṃ, rūpasmiṃ vā attānaṃ. vedanaṃ…pe… saññaṃ… saṅkhāre… viññāṇaṃ attato samanupassati, viññāṇavantaṃ vā attānaṃ, attani vā viññāṇaṃ, viññāṇasmiṃ vā attānaṃ. evaṃ kho<a name="T1.0549"></a>, āvuso visākha, sakkāyadiṭṭhi hotī"ti.</p>\r\n<p class="bodytext">kathaṃ panāyye, sakkāyadiṭṭhi na hotī"ti?</p>\r\n<p class="bodytext">idhāvuso visākha, sutavā ariyasāvako, ariyānaṃ dassāvī ariyadhammassa kovido ariyadhamme suvinīto, sappurisānaṃ dassāvī sappurisadhammassa kovido sappurisadhamme suvinīto, na rūpaṃ attato samanupassati, na rūpavantaṃ vā attānaṃ, na attani vā rūpaṃ, na rūpasmiṃ vā attānaṃ. na vedanaṃ…pe… na saññaṃ… na saṅkhāre…pe… na viññāṇaṃ attato samanupassati, na viññāṇavantaṃ vā</p>\r\n','\r',char(13)),'\n',char(10)),'-461-');
INSERT INTO pages VALUES(3414,'mula_ma_01',375,replace(replace('<p class="bodytext">attānaṃ<a name="M1.0375"></a>, na attani vā viññāṇaṃ, na viññāṇasmiṃ vā attānaṃ. evaṃ kho, āvuso visākha, sakkāyadiṭṭhi na hotī"ti.</p>\r\n<p class="bodytext"><a name="para462"></a><a name="para462_mn1"></a><span class="paranum">462</span>. katamo panāyye, ariyo aṭṭhaṅgiko maggo"ti?</p>\r\n<p class="bodytext">ayameva kho, āvuso visākha, ariyo aṭṭhaṅgiko maggo, seyyathidaṃ – sammādiṭṭhi sammāsaṅkappo sammāvācā sammākammanto <a name="V1.0382"></a> sammāājīvo sammāvāyāmo sammāsati sammāsamādhī"ti. "ariyo panāyye, aṭṭhaṅgiko maggo saṅkhato udāhu asaṅkhato"ti?</p>\r\n<p class="bodytext">ariyo kho, āvuso visākha, aṭṭhaṅgiko maggo saṅkhato"ti<a name="P1.0301"></a>.</p>\r\n<p class="bodytext">ariyena nu kho, ayye, aṭṭhaṅgikena maggena tayo khandhā saṅgahitā udāhu tīhi khandhehi ariyo aṭṭhaṅgiko maggo saṅgahito"ti?</p>\r\n<p class="bodytext">na kho, āvuso visākha, ariyena aṭṭhaṅgikena maggena tayo khandhā saṅgahitā, tīhi ca kho, āvuso visākha, khandhehi ariyo aṭṭhaṅgiko maggo saṅgahito. yā cāvuso visākha, sammāvācā yo ca sammākammanto yo ca sammāājīvo ime dhammā <a name="T1.0550"></a> sīlakkhandhe saṅgahitā. yo ca sammāvāyāmo yā ca sammāsati yo ca sammāsamādhi ime dhammā samādhikkhandhe saṅgahitā. yā ca sammādiṭṭhi yo ca sammāsaṅkappo, ime dhammā paññākkhandhe saṅgahitā"ti.</p>\r\n<p class="bodytext">katamo panāyye, samādhi, katame dhammā samādhinimittā, katame dhammā samādhiparikkhārā, katamā samādhibhāvanā"ti?</p>\r\n<p class="bodytext">yā kho, āvuso visākha, cittassa ekaggatā ayaṃ samādhi, cattāro satipaṭṭhānā samādhinimittā, cattāro sammappadhānā samādhiparikkhārā. yā tesaṃyeva dhammānaṃ āsevanā bhāvanā bahulīkammaṃ, ayaṃ ettha samādhibhāvanā"ti.</p>\r\n<p class="bodytext"><a name="para463"></a><a name="para463_mn1"></a><span class="paranum">463</span>. kati panāyye, saṅkhārā"ti?</p>\r\n<p class="bodytext">tayome, āvuso visākha, saṅkhārā – kāyasaṅkhāro, vacīsaṅkhāro, cittasaṅkhāro"ti.</p>\r\n<p class="bodytext">katamo panāyye, kāyasaṅkhāro, katamo vacīsaṅkhāro, katamo cittasaṅkhāro"ti?</p>\r\n<p class="bodytext">assāsapassāsā kho, āvuso visākha, kāyasaṅkhāro, vitakkavicārā vacīsaṅkhāro, saññā ca vedanā ca cittasaṅkhāro"ti.</p>\r\n<p class="bodytext">kasmā panāyye, assāsapassāsā kāyasaṅkhāro, kasmā vitakkavicārā vacīsaṅkhāro, kasmā saññā ca vedanā ca cittasaṅkhāro"ti?</p>\r\n<p class="bodytext">assāsapassāsā kho,</p>\r\n','\r',char(13)),'\n',char(10)),'-462-463-');
INSERT INTO pages VALUES(3415,'mula_ma_01',376,replace(replace('<p class="bodytext">āvuso <a name="M1.0376"></a> visākha, kāyikā ete dhammā kāyappaṭibaddhā, tasmā assāsapassāsā kāyasaṅkhāro. pubbe kho, āvuso visākha, vitakketvā vicāretvā pacchā vācaṃ bhindati, tasmā vitakkavicārā vacīsaṅkhāro. saññā ca vedanā ca cetasikā ete dhammā <a name="T1.0551"></a> cittappaṭibaddhā, tasmā saññā ca vedanā ca cittasaṅkhāro"ti.</p>\r\n<p class="bodytext"><a name="para464"></a><a name="para464_mn1"></a><span class="paranum">464</span>. kathaṃ <a name="V1.0383"></a> panāyye, saññāvedayitanirodhasamāpatti hotī"ti?</p>\r\n<p class="bodytext">na kho, āvuso visākha, saññāvedayitanirodhaṃ samāpajjantassa bhikkhuno evaṃ hoti – ‘ahaṃ saññāvedayitanirodhaṃ samāpajjissa’nti vā, ‘ahaṃ saññāvedayitanirodhaṃ samāpajjāmī’ti vā, ‘ahaṃ saññāvedayitanirodhaṃ samāpanno’ti vā. atha khvāssa pubbeva tathā cittaṃ bhāvitaṃ hoti yaṃ taṃ tathattāya upanetī"ti.</p>\r\n<p class="bodytext">saññāvedayitanirodhaṃ samāpajjantassa <a name="P1.0302"></a> panāyye, bhikkhuno katame dhammā paṭhamaṃ nirujjhanti – yadi vā kāyasaṅkhāro, yadi vā vacīsaṅkhāro, yadi vā cittasaṅkhāro"ti? "saññāvedayitanirodhaṃ samāpajjantassa kho, āvuso visākha, bhikkhuno paṭhamaṃ nirujjhati vacīsaṅkhāro, tato kāyasaṅkhāro, tato cittasaṅkhāro"ti.</p>\r\n<p class="bodytext">kathaṃ panāyye, saññāvedayitanirodhasamāpattiyā vuṭṭhānaṃ hotī"ti?</p>\r\n<p class="bodytext">na kho, āvuso visākha, saññāvedayitanirodhasamāpattiyā vuṭṭhahantassa bhikkhuno evaṃ hoti – ‘ahaṃ saññāvedayitanirodhasamāpattiyā vuṭṭhahissa’nti vā, ‘ahaṃ saññāvedayitanirodhasamāpattiyā vuṭṭhahāmī’ti vā, ‘ahaṃ saññāvedayitanirodhasamāpattiyā vuṭṭhito’ti vā. atha khvāssa pubbeva tathā cittaṃ bhāvitaṃ hoti yaṃ taṃ tathattāya upanetī"ti.</p>\r\n<p class="bodytext">saññāvedayitanirodhasamāpattiyā vuṭṭhahantassa panāyye, bhikkhuno katame dhammā paṭhamaṃ uppajjanti – yadi vā kāyasaṅkhāro, yadi vā vacīsaṅkhāro, yadi <a name="T1.0552"></a> vā cittasaṅkhāro"ti? "saññāvedayitanirodhasamāpattiyā vuṭṭhahantassa kho, āvuso visākha, bhikkhuno paṭhamaṃ uppajjati cittasaṅkhāro, tato kāyasaṅkhāro, tato vacīsaṅkhāro"ti.</p>\r\n<p class="bodytext">saññāvedayitanirodhasamāpattiyā vuṭṭhitaṃ panāyye, bhikkhuṃ kati phassā phusantī"ti? "saññāvedayitanirodhasamāpattiyā vuṭṭhitaṃ kho,</p>\r\n','\r',char(13)),'\n',char(10)),'-464-');
INSERT INTO pages VALUES(3416,'mula_ma_01',377,replace(replace('<p class="bodytext">āvuso <a name="M1.0377"></a> visākha, bhikkhuṃ tayo phassā phusanti – suññato phasso, animitto phasso, appaṇihito phasso"ti.</p>\r\n<p class="bodytext">saññāvedayitanirodhasamāpattiyā vuṭṭhitassa panāyye, bhikkhuno kiṃninnaṃ cittaṃ hoti kiṃpoṇaṃ kiṃpabbhāra"nti? "saññāvedayitanirodhasamāpattiyā vuṭṭhitassa kho, āvuso visākha, bhikkhuno vivekaninnaṃ cittaṃ hoti, vivekapoṇaṃ vivekapabbhāra"nti.</p>\r\n<p class="bodytext"><a name="para465"></a><a name="para465_mn1"></a><span class="paranum">465</span>. kati <a name="V1.0384"></a> panāyye, vedanā"ti?</p>\r\n<p class="bodytext">tisso kho imā, āvuso visākha, vedanā – sukhā vedanā, dukkhā vedanā, adukkhamasukhā vedanā"ti.</p>\r\n<p class="bodytext">katamā panāyye, sukhā vedanā, katamā dukkhā vedanā, katamā adukkhamasukhā vedanā"ti?</p>\r\n<p class="bodytext">yaṃ kho, āvuso visākha, kāyikaṃ vā cetasikaṃ vā sukhaṃ sātaṃ vedayitaṃ – ayaṃ sukhā vedanā. yaṃ kho, āvuso visākha, kāyikaṃ vā cetasikaṃ vā dukkhaṃ asātaṃ vedayitaṃ – ayaṃ dukkhā vedanā. yaṃ kho, āvuso visākha, kāyikaṃ vā cetasikaṃ vā neva sātaṃ nāsātaṃ vedayitaṃ – ayaṃ adukkhamasukhā <a name="P1.0303"></a> vedanā"ti.</p>\r\n<p class="bodytext">sukhā panāyye, vedanā kiṃsukhā kiṃdukkhā, dukkhā vedanā kiṃsukhā kiṃdukkhā, adukkhamasukhā vedanā kiṃsukhā kiṃdukkhā"ti?</p>\r\n<p class="bodytext">sukhā kho, āvuso visākha, vedanā ṭhitisukhā vipariṇāmadukkhā, dukkhā vedanā ṭhitidukkhā vipariṇāmasukhā<a name="T1.0553"></a>, adukkhamasukhā vedanā ñāṇasukhā aññāṇadukkhā"ti.</p>\r\n<p class="bodytext">sukhāya panāyye, vedanāya kiṃ anusayo anuseti, dukkhāya vedanāya kiṃ anusayo anuseti, adukkhamasukhāya vedanāya kiṃ anusayo anusetī"ti?</p>\r\n<p class="bodytext">sukhāya kho, āvuso visākha, vedanāya rāgānusayo anuseti, dukkhāya vedanāya paṭighānusayo anuseti, adukkhamasukhāya vedanāya avijjānusayo anusetī"ti.</p>\r\n<p class="bodytext">sabbāya nu kho, ayye, sukhāya vedanāya rāgānusayo anuseti, sabbāya dukkhāya vedanāya paṭighānusayo anuseti, sabbāya adukkhamasukhāya vedanāya avijjānusayo anusetī"ti?</p>\r\n<p class="bodytext">na kho, āvuso visākha, sabbāya sukhāya vedanāya rāgānusayo anuseti, na sabbāya dukkhāya vedanāya paṭighānusayo anuseti, na sabbāya adukkhamasukhāya vedanāya avijjānusayo anusetī"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-465-');
INSERT INTO pages VALUES(3417,'mula_ma_01',378,replace(replace('<p class="bodytext">sukhāya <a name="M1.0378"></a> panāyye, vedanāya kiṃ pahātabbaṃ, dukkhāya vedanāya kiṃ pahātabbaṃ, adukkhamasukhāya vedanāya kiṃ pahātabba"nti?</p>\r\n<p class="bodytext">sukhāya kho, āvuso visākha, vedanāya rāgānusayo pahātabbo, dukkhāya vedanāya paṭighānusayo pahātabbo, adukkhamasukhāya vedanāya avijjānusayo pahātabbo"ti.</p>\r\n<p class="bodytext">sabbāya <a name="V1.0385"></a> nu kho, ayye, sukhāya vedanāya rāgānusayo pahātabbo, sabbāya dukkhāya vedanāya paṭighānusayo pahātabbo, sabbāya adukkhamasukhāya vedanāya avijjānusayo pahātabbo"ti?</p>\r\n<p class="bodytext">na kho, āvuso visākha, sabbāya sukhāya vedanāya rāgānusayo pahātabbo, na sabbāya dukkhāya vedanāya paṭighānusayo pahātabbo<a name="T1.0554"></a>, na sabbāya adukkhamasukhāya vedanāya avijjānusayo pahātabbo. idhāvuso visākha, bhikkhu vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati. rāgaṃ tena pajahati, na tattha rāgānusayo anuseti. idhāvuso visākha, bhikkhu iti paṭisañcikkhati – ‘kudāssu nāmāhaṃ tadāyatanaṃ upasampajja viharissāmi yadariyā etarahi āyatanaṃ upasampajja viharantī’ti? iti anuttaresu vimokkhesu pihaṃ upaṭṭhāpayato <a name="P1.0304"></a> uppajjati pihāppaccayā domanassaṃ. paṭighaṃ tena pajahati, na tattha paṭighānusayo anuseti. idhāvuso visākha, bhikkhu sukhassa ca pahānā, dukkhassa ca pahānā, pubbeva somanassadomanassānaṃ atthaṅgamā, adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja viharati. avijjaṃ tena pajahati, na tattha avijjānusayo anusetī"ti.</p>\r\n<p class="bodytext"><a name="para466"></a><a name="para466_mn1"></a><span class="paranum">466</span>. sukhāya panāyye, vedanāya kiṃ paṭibhāgo"ti?</p>\r\n<p class="bodytext">sukhāya kho, āvuso visākha, vedanāya dukkhā vedanā paṭibhāgo"ti.</p>\r\n<p class="bodytext">dukkhāya pannāyye, vedanāya kiṃ paṭibhāgo"ti?</p>\r\n<p class="bodytext">dukkhāya kho, āvuso visākha, vedanāya sukhā vedanā paṭibhāgo"ti.</p>\r\n<p class="bodytext">adukkhamasukhāya panāyye, vedanāya kiṃ paṭibhāgo"ti?</p>\r\n<p class="bodytext">adukkhamasukhāya kho, āvuso visākha, vedanāya avijjā paṭibhāgo"ti.</p>\r\n<p class="bodytext">avijjāya panāyye, kiṃ paṭibhāgo"ti?</p>\r\n<p class="bodytext">avijjāya kho, āvuso visākha, vijjā paṭibhāgo"ti.</p>\r\n<p class="bodytext">vijjāya panāyye, kiṃ paṭibhāgo"ti?</p>\r\n<p class="bodytext">vijjāya kho, āvuso visākha, vimutti paṭibhāgo"ti.</p>\r\n<p class="bodytext">vimuttiyā panāyye<a name="T1.0555"></a>, kiṃ paṭibhāgo"ti?</p>\r\n<p class="bodytext">vimuttiyā kho, āvuso visākha, nibbānaṃ paṭibhāgo"ti.</p>\r\n<p class="bodytext">nibbānassa panāyye, kiṃ paṭibhāgo"ti? "accayāsi, āvuso <span class="note">[accasarāvuso (sī. pī.), accassarāvuso (syā. kaṃ.)]</span> visākha, pañhaṃ, nāsakkhi pañhānaṃ pariyantaṃ gahetuṃ. nibbānogadhañhi, āvuso visākha, brahmacariyaṃ, nibbānaparāyanaṃ nibbānapariyosānaṃ. ākaṅkhamāno ca tvaṃ, āvuso visākha, bhagavantaṃ upasaṅkamitvā etamatthaṃ puccheyyāsi, yathā ca te bhagavā byākaroti tathā naṃ dhāreyyāsī"ti.</p>\r\n<p class="bodytext"><a name="para467"></a><a name="para467_mn1"></a><span class="paranum">467</span>. atha kho visākho upāsako dhammadinnāya bhikkhuniyā bhāsitaṃ abhinanditvā anumoditvā uṭṭhāyāsanā dhammadinnaṃ bhikkhuniṃ abhivādetvā padakkhiṇaṃ katvā yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho visākho upāsako yāvatako ahosi dhammadinnāya bhikkhuniyā saddhiṃ kathāsallāpo taṃ sabbaṃ bhagavato ārocesi. evaṃ vutte, bhagavā visākhaṃ upāsakaṃ etadavoca – "paṇḍitā, visākha, dhammadinnā bhikkhunī, mahāpaññā, visākha, dhammadinnā bhikkhunī. maṃ cepi tvaṃ, visākha, etamatthaṃ puccheyyāsi, ahampi taṃ evamevaṃ byākareyyaṃ, yathā <a name="P1.0305"></a> taṃ dhammadinnāya bhikkhuniyā byākataṃ. eso cevetassa <span class="note">[esovetassa (syā. kaṃ.)]</span> attho. evañca naṃ <span class="note">[evametaṃ (sī. syā. kaṃ.)]</span> dhārehī"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamano visākho upāsako bhagavato bhāsitaṃ abhinandīti.</p>\r\n<p class="centered">cūḷavedallasuttaṃ niṭṭhitaṃ catutthaṃ.</p>\r\n<p class="subhead">5. cūḷadhammasamādānasuttaṃ</p>\r\n<p class="bodytext"><a name="para468"></a><a name="para468_mn1"></a><span class="paranum">468</span>. evaṃ <a name="T1.0556"></a><a name="V1.0387"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca – "cattārimāni, bhikkhave, dhammasamādānāni. katamāni cattāri? atthi, bhikkhave, dhammasamādānaṃ paccuppannasukhaṃ āyatiṃ dukkhavipākaṃ, atthi,</p>\r\n','\r',char(13)),'\n',char(10)),'-466-467-468-');
INSERT INTO pages VALUES(3419,'mula_ma_01',380,replace(replace('<p class="bodytext">bhikkhave<a name="M1.0380"></a>, dhammasamādānaṃ paccuppannadukkhañceva āyatiñca dukkhavipākaṃ, atthi, bhikkhave, dhammasamādānaṃ paccuppannadukkhaṃ āyatiṃ sukhavipākaṃ, atthi, bhikkhave, dhammasamādānaṃ paccuppannasukhañceva āyatiñca sukhavipākaṃ".</p>\r\n<p class="bodytext"><a name="para469"></a><a name="para469_mn1"></a><span class="paranum">469</span>. katamañca, bhikkhave, dhammasamādānaṃ paccuppannasukhaṃ āyatiṃ dukkhavipākaṃ? santi, bhikkhave, eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino – ‘natthi kāmesu doso’ti. te kāmesu pātabyataṃ āpajjanti. te kho moḷibaddhāhi <span class="note">[moḷibandhāhi (syā. kaṃ. ka.)]</span> paribbājikāhi paricārenti. te evamāhaṃsu – ‘kiṃsu nāma te bhonto samaṇabrāhmaṇā kāmesu anāgatabhayaṃ sampassamānā kāmānaṃ pahānamāhaṃsu, kāmānaṃ pariññaṃ paññapenti? sukho imissā paribbājikāya taruṇāya mudukāya lomasāya bāhāya samphasso’ti te kāmesu pātabyataṃ āpajjanti. te kāmesu pātabyataṃ āpajjitvā kāyassa bhedā paraṃ maraṇā apāyaṃ <a name="T1.0557"></a> duggatiṃ vinipātaṃ nirayaṃ upapajjanti. te tattha dukkhā tibbā kharā kaṭukā vedanā vedayanti. te evamāhaṃsu – ‘idaṃ kho te bhonto samaṇabrāhmaṇā kāmesu anāgatabhayaṃ sampassamānā kāmānaṃ pahānamāhaṃsu, kāmānaṃ pariññaṃ paññapenti, ime hi mayaṃ kāmahetu kāmanidānaṃ <a name="P1.0306"></a> dukkhā tibbā kharā kaṭukā vedanā vedayāmā’ti. seyyathāpi, bhikkhave, gimhānaṃ pacchime māse māluvāsipāṭikā phaleyya. atha kho taṃ, bhikkhave, māluvābījaṃ aññatarasmiṃ sālamūle nipateyya. atha kho, bhikkhave, yā tasmiṃ sāle adhivatthā devatā sā bhītā saṃviggā santāsaṃ āpajjeyya. atha kho, bhikkhave, tasmiṃ sāle adhivatthāya devatāya mittāmaccā ñātisālohitā ārāmadevatā vanadevatā rukkhadevatā osadhitiṇavanappatīsu adhivatthā devatā saṅgamma samāgamma evaṃ samassāseyyuṃ – ‘mā bhavaṃ bhāyi, mā bhavaṃ bhāyi, appeva nāmetaṃ māluvābījaṃ <a name="V1.0388"></a> moro vā gileyya <span class="note">[moro vā gileyya, godhā vā khādeyya (ka.)]</span>, mago vā khādeyya, davaḍāho <span class="note">[vanadāho (ka.)]</span> vā ḍaheyya, vanakammikā vā uddhareyyuṃ, upacikā vā uṭṭhaheyyuṃ <span class="note">[udrabheyyuṃ (sī. pī. ka.)]</span>, abījaṃ vā panassā’ti. atha kho taṃ, bhikkhave, māluvābījaṃ neva moro gileyya, na mago khādeyya, na davaḍāho ḍaheyya, na vanakammikā uddhareyyuṃ, na upacikā uṭṭhaheyyuṃ, bījañca panassa taṃ pāvussakena meghena</p>\r\n','\r',char(13)),'\n',char(10)),'-469-');
INSERT INTO pages VALUES(3420,'mula_ma_01',381,replace(replace('<p class="bodytext">abhippavuṭṭhaṃ <a name="M1.0381"></a> sammadeva viruheyya. sāssa māluvālatā taruṇā mudukā lomasā vilambinī, sā taṃ sālaṃ upaniseveyya. atha kho, bhikkhave, tasmiṃ sāle adhivatthāya devatāya evamassa – ‘kiṃsu nāma te <a name="T1.0558"></a> bhonto mittāmaccā ñātisālohitā ārāmadevatā vanadevatā rukkhadevatā osadhitiṇavanappatīsu adhivatthā devatā māluvābīje anāgatabhayaṃ sampassamānā saṅgamma samāgamma evaṃ samassāsesuṃ <span class="note">[samassāseyyuṃ (ka.)]</span> – "mā bhavaṃ bhāyi mā bhavaṃ bhāyi, appeva nāmetaṃ māluvābījaṃ moro vā gileyya, mago vā khādeyya, davaḍāho vā ḍaheyya, vanakammikā vā uddhareyyuṃ, upacikā vā uṭṭhaheyyuṃ, abījaṃ vā panassā"ti, sukho imissā māluvālatāya taruṇāya mudukāya lomasāya vilambiniyā samphasso’ti. sā taṃ sālaṃ anuparihareyya. sā taṃ sālaṃ anupariharitvā upari viṭabhiṃ <span class="note">[viṭapaṃ (syā. ṭṭha.)]</span> kareyya. upari viṭabhiṃ karitvā oghanaṃ janeyya. oghanaṃ janetvā ye tassa sālassa mahantā mahantā khandhā te padāleyya. atha kho, bhikkhave, tasmiṃ sāle adhivatthāya devatāya evamassa – ‘idaṃ kho te bhonto mittāmaccā ñātisālohitā ārāmadevatā vanadevatā rukkhadevatā osadhitiṇavanappatīsu adhivatthā devatā māluvābīje anāgatabhayaṃ sampassamānā saṅgamma samāgamma evaṃ samassāsesuṃ <span class="note">[samassāseyyuṃ (ka.)]</span> – "mā bhavaṃ bhāyi mā bhavaṃ bhāyi, appeva nāmetaṃ māluvābījaṃ moro vā gileyya, mago <a name="P1.0307"></a> vā khādeyya, davaḍāho vā ḍaheyya, vanakammikā vā uddhareyyuṃ, upacikā vā uṭṭhaheyyuṃ abījaṃ vā panassā"ti. yañcāhaṃ <span class="note">[yaṃ vāhaṃ (ka.), svāhaṃ (syā. kaṃ.)]</span> māluvābījahetu dukkhā tibbā kharā kaṭukā vedanā vedayāmī’ti. evameva kho, bhikkhave, santi eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino ‘natthi kāmesu doso’ti<a name="T1.0559"></a>. te kāmesu pātabyataṃ āpajjanti. te moḷibaddhāhi paribbājikāhi paricārenti. te evamāhaṃsu – ‘kiṃsu nāma te bhonto samaṇabrāhmaṇā kāmesu anāgatabhayaṃ sampassamānā kāmānaṃ pahānamāhaṃsu, kāmānaṃ pariññaṃ paññapenti? sukho imissā paribbājikāya taruṇāya mudukāya lomasāya bāhāya samphasso’ti. te kāmesu pātabyataṃ āpajjanti. te kāmesu pātabyataṃ āpajjitvā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti. te tattha dukkhā tibbā</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3421,'mula_ma_01',382,replace(replace('<p class="bodytext">kharā <a name="M1.0382"></a> kaṭukā vedanā vedayanti. te evamāhaṃsu – ‘idaṃ kho te bhonto samaṇabrāhmaṇā kāmesu anāgatabhayaṃ sampassamānā kāmānaṃ pahānamāhaṃsu, kāmānaṃ pariññaṃ paññapenti. ime hi <a name="V1.0389"></a> mayaṃ kāmahetu kāmanidānaṃ dukkhā tibbā kharā kaṭukā vedanā vedayāmā’ti. idaṃ vuccati, bhikkhave, dhammasamādānaṃ paccuppannasukhaṃ āyatiṃ dukkhavipākaṃ.</p>\r\n<p class="bodytext"><a name="para470"></a><a name="para470_mn1"></a><span class="paranum">470</span>. katamañca, bhikkhave, dhammasamādānaṃ paccuppannadukkhañceva āyatiñca dukkhavipākaṃ? idha, bhikkhave, ekacco acelako hoti muttācāro hatthāpalekhano, naehibhaddantiko, natiṭṭhabhaddantiko, nābhihaṭaṃ, na uddissakataṃ, na nimantanaṃ sādiyati, so na kumbhimukhā paṭiggaṇhāti, na kaḷopimukhā paṭiggaṇhāti, na eḷakamantaraṃ, na daṇḍamantaraṃ, na musalamantaraṃ, na dvinnaṃ bhuñjamānānaṃ, na gabbhiniyā, na pāyamānāya, na purisantaragatāya, na saṅkittīsu, na yattha sā upaṭṭhito <a name="T1.0560"></a> hoti, na yattha makkhikā saṇḍasaṇḍacārinī, na macchaṃ, na maṃsaṃ, na suraṃ, na merayaṃ, na thusodakaṃ pivati. so ekāgāriko vā hoti ekālopiko, dvāgāriko vā hoti dvālopiko…pe… sattāgāriko vā hoti sattālopiko. ekissāpi dattiyā yāpeti, dvīhipi dattīhi yāpeti… sattahipi dattīhi yāpeti. ekāhikampi āhāraṃ āhāreti, dvīhikampi āhāraṃ āhāreti… sattāhikampi āhāraṃ āhāreti. iti evarūpaṃ addhamāsikampi pariyāyabhattabhojanānuyogamanuyutto viharati. so sākabhakkho vā hoti<a name="P1.0308"></a>, sāmākabhakkho vā hoti, nīvārabhakkho vā hoti, daddulabhakkho vā hoti, haṭabhakkho vā hoti, kaṇabhakkho vā hoti, ācāmabhakkho vā hoti, piññākabhakkho vā hoti, tiṇabhakkho vā hoti, gomayabhakkho vā hoti, vanamūlaphalāhāro yāpeti pavattaphalabhojī. so sāṇānipi dhāreti, masāṇānipi dhāreti, chavadussānipi dhāreti, paṃsukūlānipi dhāreti, tirīṭānipi dhāreti, ajinampi dhāreti, ajinakkhipampi dhāreti, kusacīrampi dhāreti, vākacīrampi dhāreti, phalakacīrampi dhāreti, kesakambalampi dhāreti, vāḷakambalampi dhāreti, ulūkapakkhampi dhāreti, kesamassulocakopi hoti, kesamassulocanānuyogamanuyutto, ubbhaṭṭhakopi hoti, āsanapaṭikkhitto, ukkuṭikopi hoti ukkuṭikappadhānamanuyutto, kaṇṭakāpassayikopi hoti, kaṇṭakāpassaye</p>\r\n','\r',char(13)),'\n',char(10)),'-470-');
INSERT INTO pages VALUES(3422,'mula_ma_01',383,replace(replace('<p class="bodytext">seyyaṃ <a name="M1.0383"></a> kappeti <span class="note">[passa ma. ni. 1.155 mahāsīhanādasutte]</span>, sāyatatiyakampi udakorohanānuyogamanuyutto viharati. iti evarūpaṃ anekavihitaṃ kāyassa ātāpanaparitāpanānuyogamanuyutto viharati<a name="T1.0561"></a>. so kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjati. idaṃ vuccati, bhikkhave, dhammasamādānaṃ paccuppannadukkhañceva āyatiñca dukkhavipākaṃ.</p>\r\n<p class="bodytext"><a name="para471"></a><a name="para471_mn1"></a><span class="paranum">471</span>. katamañca, bhikkhave, dhammasamādānaṃ paccuppannadukkhaṃ āyatiṃ sukhavipākaṃ? idha<a name="V1.0390"></a>, bhikkhave, ekacco pakatiyā tibbarāgajātiko hoti, so abhikkhaṇaṃ rāgajaṃ dukkhaṃ domanassaṃ paṭisaṃvedeti, pakatiyā tibbadosajātiko hoti, so abhikkhaṇaṃ dosajaṃ dukkhaṃ domanassaṃ paṭisaṃvedeti, pakatiyā tibbamohajātiko hoti, so abhikkhaṇaṃ mohajaṃ dukkhaṃ domanassaṃ paṭisaṃvedeti. so sahāpi dukkhena, sahāpi domanassena, assumukhopi rudamāno paripuṇṇaṃ parisuddhaṃ brahmacariyaṃ carati. so kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjati. idaṃ vuccati, bhikkhave, dhammasamādānaṃ paccuppannadukkhaṃ āyatiṃ sukhavipākaṃ.</p>\r\n<p class="bodytext"><a name="para472"></a><a name="para472_mn1"></a><span class="paranum">472</span>. katamañca, bhikkhave, dhammasamādānaṃ paccuppannasukhañceva āyatiñca sukhavipākaṃ? idha, bhikkhave, ekacco pakatiyā na tibbarāgajātiko hoti, so na abhikkhaṇaṃ rāgajaṃ dukkhaṃ domanassaṃ paṭisaṃvedeti, pakatiyā na tibbadosajātiko hoti, so na abhikkhaṇaṃ dosajaṃ dukkhaṃ domanassaṃ paṭisaṃvedeti, pakatiyā na tibbamohajātiko hoti<a name="P1.0309"></a>, so na abhikkhaṇaṃ mohajaṃ dukkhaṃ domanassaṃ paṭisaṃvedeti. so vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ <a name="T1.0562"></a> pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati. vitakkavicārānaṃ vūpasamā ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ dutiyaṃ jhānaṃ…pe… tatiyaṃ jhānaṃ… catutthaṃ jhānaṃ upasampajja viharati. so kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjati. idaṃ vuccati, bhikkhave, dhammasamādānaṃ paccuppannasukhañceva āyatiñca sukhavipākaṃ. imāni kho, bhikkhave, cattāri dhammasamādānānī"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-471-472-');
INSERT INTO pages VALUES(3423,'mula_ma_01',384,replace(replace('<p class="bodytext">idamavoca <a name="M1.0384"></a> bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">cūḷadhammasamādānasuttaṃ niṭṭhitaṃ pañcamaṃ.</p>\r\n<p class="subhead">6. mahādhammasamādānasuttaṃ</p>\r\n<p class="bodytext"><a name="para473"></a><a name="para473_mn1"></a><span class="paranum">473</span>. evaṃ <a name="V1.0391"></a><a name="T1.0563"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca – "yebhuyyena, bhikkhave, sattā evaṃkāmā evaṃchandā evaṃadhippāyā – ‘aho vata aniṭṭhā akantā amanāpā dhammā parihāyeyyuṃ, iṭṭhā kantā manāpā dhammā abhivaḍḍheyyu’nti. tesaṃ, bhikkhave, sattānaṃ evaṃkāmānaṃ evaṃchandānaṃ evaṃadhippāyānaṃ aniṭṭhā akantā amanāpā dhammā abhivaḍḍhanti, iṭṭhā kantā manāpā dhammā parihāyanti. tatra tumhe, bhikkhave, kaṃ hetuṃ paccethā"ti? "bhagavaṃmūlakā <a name="P1.0310"></a> no, bhante, dhammā, bhagavaṃnettikā, bhagavaṃpaṭisaraṇā. sādhu vata, bhante, bhagavantaññeva paṭibhātu etassa bhāsitassa attho, bhagavato sutvā bhikkhū dhāressantī"ti. "tena hi, bhikkhave, suṇātha, sādhukaṃ manasi karotha, bhāsissāmī"ti. "evaṃ, bhante"ti kho te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext"><a name="para474"></a><a name="para474_mn1"></a><span class="paranum">474</span>. idha, bhikkhave, assutavā puthujjano, ariyānaṃ adassāvī ariyadhammassa akovido ariyadhamme avinīto, sappurisānaṃ adassāvī sappurisadhammassa akovido sappurisadhamme avinīto, sevitabbe dhamme na jānāti asevitabbe dhamme na jānāti, bhajitabbe <a name="T1.0564"></a> dhamme na jānāti abhajitabbe dhamme na jānāti. so sevitabbe dhamme ajānanto asevitabbe dhamme ajānanto, bhajitabbe dhamme ajānanto abhajitabbe dhamme ajānanto, asevitabbe dhamme sevati sevitabbe dhamme na sevati, abhajitabbe dhamme bhajati bhajitabbe dhamme na bhajati. tassa asevitabbe dhamme sevato sevitabbe dhamme asevato, abhajitabbe dhamme bhajato bhajitabbe dhamme abhajato aniṭṭhā akantā amanāpā</p>\r\n','\r',char(13)),'\n',char(10)),'-473-474-');
INSERT INTO pages VALUES(3424,'mula_ma_01',385,replace(replace('<p class="bodytext">dhammā <a name="M1.0385"></a> abhivaḍḍhanti, iṭṭhā kantā manāpā dhammā parihāyanti. taṃ kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ aviddasuno.</p>\r\n<p class="bodytext">sutavā ca kho, bhikkhave, ariyasāvako, ariyānaṃ dassāvī ariyadhammassa kovido ariyadhamme <a name="V1.0392"></a> suvinīto, sappurisānaṃ dassāvī sappurisadhammassa kovido sappurisadhamme suvinīto, sevitabbe dhamme jānāti asevitabbe dhamme jānāti, bhajitabbe dhamme jānāti abhajitabbe dhamme jānāti. so sevitabbe dhamme jānanto asevitabbe dhamme jānanto, bhajitabbe dhamme jānanto abhajitabbe dhamme jānanto, asevitabbe dhamme na sevati sevitabbe dhamme sevati, abhajitabbe dhamme na bhajati bhajitabbe dhamme bhajati. tassa asevitabbe dhamme asevato sevitabbe dhamme sevato, abhajitabbe dhamme abhajato bhajitabbe dhamme bhajato, aniṭṭhā akantā amanāpā dhammā parihāyanti, iṭṭhā kantā manāpā dhammā abhivaḍḍhanti. taṃ <a name="T1.0565"></a> kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ viddasuno.</p>\r\n<p class="bodytext"><a name="para475"></a><a name="para475_mn1"></a><span class="paranum">475</span>. cattārimāni, bhikkhave, dhammasamādānāni. katamāni cattāri? atthi, bhikkhave, dhammasamādānaṃ paccuppannadukkhañceva āyatiñca dukkhavipākaṃ, atthi, bhikkhave, dhammasamādānaṃ <a name="P1.0311"></a> paccuppannasukhaṃ āyatiṃ dukkhavipākaṃ, atthi, bhikkhave, dhammasamādānaṃ paccuppannadukkhaṃ āyatiṃ sukhavipākaṃ, atthi, bhikkhave, dhammasamādānaṃ paccuppannasukhañceva āyatiñca sukhavipākaṃ.</p>\r\n<p class="bodytext"><a name="para476"></a><a name="para476_mn1"></a><span class="paranum">476</span>. tatra, bhikkhave, yamidaṃ <span class="note">[yadidaṃ (sī.)]</span> dhammasamādānaṃ paccuppannadukkhañceva āyatiñca dukkhavipākaṃ, taṃ avidvā avijjāgato yathābhūtaṃ nappajānāti – ‘idaṃ kho dhammasamādānaṃ paccuppannadukkhañceva āyatiñca dukkhavipāka’nti. taṃ avidvā avijjāgato yathābhūtaṃ appajānanto taṃ sevati, taṃ na parivajjeti. tassa taṃ sevato, taṃ aparivajjayato, aniṭṭhā akantā amanāpā dhammā abhivaḍḍhanti, iṭṭhā kantā manāpā dhammā parihāyanti. taṃ kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ aviddasuno.</p>\r\n<p class="bodytext">tatra, bhikkhave, yamidaṃ dhammasamādānaṃ paccuppannasukhaṃ āyatiṃ dukkhavipākaṃ taṃ avidvā avijjāgato yathābhūtaṃ nappajānāti – ‘idaṃ kho dhammasamādānaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-475-476-');
INSERT INTO pages VALUES(3425,'mula_ma_01',386,replace(replace('<p class="bodytext">paccuppannasukhaṃ <a name="M1.0386"></a> āyatiṃ dukkhavipāka’nti. taṃ avidvā avijjāgato yathābhūtaṃ appajānanto taṃ sevati, taṃ na parivajjeti. tassa taṃ sevato, taṃ <a name="T1.0566"></a> aparivajjayato, aniṭṭhā akantā amanāpā dhammā abhivaḍḍhanti, iṭṭhā kantā manāpā dhammā parihāyanti. taṃ kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ aviddasuno.</p>\r\n<p class="bodytext">tatra<a name="V1.0393"></a>, bhikkhave, yamidaṃ dhammasamādānaṃ paccuppannadukkhaṃ āyatiṃ sukhavipākaṃ, taṃ avidvā avijjāgato yathābhūtaṃ nappajānāti – ‘idaṃ kho dhammasamādānaṃ paccuppannadukkhaṃ āyatiṃ sukhavipāka’nti. taṃ avidvā avijjāgato yathābhūtaṃ appajānanto taṃ na sevati, taṃ parivajjeti. tassa taṃ asevato, taṃ parivajjayato, aniṭṭhā <a name="P1.0312"></a> akantā amanāpā dhammā abhivaḍḍhanti, iṭṭhā kantā manāpā dhammā parihāyanti. taṃ kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ aviddasuno.</p>\r\n<p class="bodytext">tatra, bhikkhave, yamidaṃ dhammasamādānaṃ paccuppannasukhañceva āyatiñca sukhavipākaṃ, taṃ avidvā avijjāgato yathābhūtaṃ nappajānāti – ‘idaṃ kho dhammasamādānaṃ paccuppannasukhañceva āyatiñca sukhavipāka’nti. taṃ avidvā avijjāgato yathābhūtaṃ appajānanto taṃ na sevati, taṃ parivajjeti. tassa taṃ asevato, taṃ parivajjayato, aniṭṭhā akantā amanāpā dhammā abhivaḍḍhanti, iṭṭhā kantā manāpā dhammā parihāyanti. taṃ kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ aviddasuno.</p>\r\n<p class="bodytext"><a name="para477"></a><a name="para477_mn1"></a><span class="paranum">477</span>. tatra, bhikkhave, yamidaṃ dhammasamādānaṃ paccuppannadukkhañceva āyatiñca dukkhavipākaṃ taṃ vidvā vijjāgato yathābhūtaṃ pajānāti – ‘idaṃ <a name="T1.0567"></a> kho dhammasamādānaṃ paccuppannadukkhañceva āyatiñca dukkhavipāka’nti. taṃ vidvā vijjāgato yathābhūtaṃ pajānanto taṃ na sevati, taṃ parivajjeti. tassa taṃ asevato, taṃ parivajjayato, aniṭṭhā akantā amanāpā dhammā parihāyanti, iṭṭhā kantā manāpā dhammā abhivaḍḍhanti. taṃ kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ viddasuno.</p>\r\n<p class="bodytext">tatra, bhikkhave, yamidaṃ dhammasamādānaṃ paccuppannasukhaṃ āyatiṃ dukkhavipākaṃ taṃ vidvā vijjāgato yathābhūtaṃ pajānāti – ‘idaṃ kho dhammasamādānaṃ paccuppannasukhaṃ āyatiṃ dukkhavipāka’nti. taṃ vidvā vijjāgato yathābhūtaṃ pajānanto taṃ na sevati, taṃ parivajjeti. tassa taṃ asevato, taṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-477-');
INSERT INTO pages VALUES(3426,'mula_ma_01',387,replace(replace('<p class="bodytext">parivajjayato<a name="M1.0387"></a>, aniṭṭhā akantā amanāpā dhammā parihāyanti, iṭṭhā kantā manāpā dhammā abhivaḍḍhanti. taṃ kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ viddasuno.</p>\r\n<p class="bodytext">tatra<a name="V1.0394"></a>, bhikkhave, yamidaṃ dhammasamādānaṃ paccuppannadukkhaṃ āyatiṃ sukhavipākaṃ taṃ vidvā vijjāgato yathābhūtaṃ pajānāti – ‘idaṃ kho dhammasamādānaṃ paccuppannadukkhaṃ āyatiṃ sukhavipāka’nti. taṃ vidvā vijjāgato yathābhūtaṃ pajānanto taṃ sevati, taṃ na parivajjeti. tassa taṃ sevato, taṃ aparivajjayato, aniṭṭhā akantā amanāpā dhammā parihāyanti, iṭṭhā kantā manāpā dhammā abhivaḍḍhanti. taṃ kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ viddasuno.</p>\r\n<p class="bodytext">tatra, bhikkhave, yamidaṃ dhammasamādānaṃ paccuppannasukhañceva āyatiñca sukhavipākaṃ <a name="T1.0568"></a> taṃ vidvā vijjāgato yathābhūtaṃ pajānāti – ‘idaṃ kho dhammasamādānaṃ paccuppannasukhañceva āyatiñca sukhavipāka’nti. taṃ vidvā vijjāgato yathābhūtaṃ pajānanto taṃ sevati, taṃ na parivajjeti. tassa taṃ sevato, taṃ aparivajjayato, aniṭṭhā akantā amanāpā dhammā parihāyanti, iṭṭhā kantā manāpā dhammā abhivaḍḍhanti. taṃ kissa hetu? evañhetaṃ, bhikkhave, hoti yathā taṃ viddasuno.</p>\r\n<p class="bodytext"><a name="para478"></a><a name="para478_mn1"></a><span class="paranum">478</span>. katamañca<a name="P1.0313"></a>, bhikkhave, dhammasamādānaṃ paccuppannadukkhañceva āyatiñca dukkhavipākaṃ? idha, bhikkhave, ekacco sahāpi dukkhena sahāpi domanassena pāṇātipātī hoti, pāṇātipātapaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena adinnādāyī hoti, adinnādānapaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena kāmesu micchācārī hoti, kāmesu micchācārapaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena musāvādī hoti, musāvādapaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena pisuṇavāco hoti, pisuṇavācāpaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena pharusavāco hoti, pharusavācāpaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena samphappalāpī hoti, samphappalāpapaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti,</p>\r\n','\r',char(13)),'\n',char(10)),'-478-');
INSERT INTO pages VALUES(3427,'mula_ma_01',388,replace(replace('<p class="bodytext">sahāpi <a name="M1.0388"></a> dukkhena sahāpi domanassena abhijjhālu <a name="T1.0569"></a> hoti, abhijjhāpaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena byāpannacitto hoti, byāpādapaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena micchādiṭṭhi hoti, micchādiṭṭhipaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti. so <a name="V1.0395"></a> kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjati. idaṃ vuccati, bhikkhave, dhammasamādānaṃ paccuppannadukkhañceva āyatiñca dukkhavipākaṃ.</p>\r\n<p class="bodytext"><a name="para479"></a><a name="para479_mn1"></a><span class="paranum">479</span>. katamañca, bhikkhave, dhammasamādānaṃ paccuppannasukhaṃ āyatiṃ dukkhavipākaṃ? idha, bhikkhave, ekacco sahāpi sukhena sahāpi somanassena pāṇātipātī hoti, pāṇātipātapaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena adinnādāyī hoti, adinnādānapaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena kāmesumicchācārī hoti, kāmesumicchācārapaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena musāvādī hoti, musāvādapaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena pisuṇavāco hoti, pisuṇavācāpaccayā ca sukhaṃ <a name="P1.0314"></a> somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena pharusavāco hoti, pharusavācāpaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena samphappalāpī hoti, samphappalāpapaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi <a name="T1.0570"></a> sukhena sahāpi somanassena abhijjhālu hoti, abhijjhāpaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena byāpannacitto hoti, byāpādapaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena micchādiṭṭhi hoti, micchādiṭṭhipaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti. so kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjati. idaṃ vuccati, bhikkhave, dhammasamādānaṃ paccuppannasukhaṃ āyatiṃ dukkhavipākaṃ.</p>\r\n<p class="bodytext"><a name="para480"></a><a name="para480_mn1"></a><span class="paranum">480</span>. katamañca, bhikkhave, dhammasamādānaṃ paccuppannadukkhaṃ āyatiṃ sukhavipākaṃ? idha, bhikkhave, ekacco sahāpi dukkhena sahāpi domanassena pāṇātipātā paṭivirato hoti, pāṇātipātā veramaṇīpaccayā ca dukkhaṃ domanassaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-479-480-');
INSERT INTO pages VALUES(3428,'mula_ma_01',389,replace(replace('<p class="bodytext">paṭisaṃvedeti<a name="M1.0389"></a>, sahāpi dukkhena sahāpi domanassena adinnādānā paṭivirato hoti, adinnādānā veramaṇīpaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena kāmesumicchācārā paṭivirato hoti, kāmesumicchācārā veramaṇīpaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena musāvādā paṭivirato hoti, musāvādā veramaṇīpaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena pisuṇāya vācāya paṭivirato hoti<a name="V1.0396"></a>, pisuṇāya vācāya veramaṇīpaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti<a name="T1.0571"></a>, sahāpi dukkhena sahāpi domanassena pharusāya vācāya paṭivirato hoti, pharusāya vācāya veramaṇīpaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena samphappalāpā paṭivirato hoti, samphappalāpā veramaṇīpaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena anabhijjhālu hoti, anabhijjhāpaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena sahāpi domanassena abyāpannacitto hoti, abyāpādapaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti, sahāpi dukkhena <a name="P1.0315"></a> sahāpi domanassena sammādiṭṭhi hoti, sammādiṭṭhipaccayā ca dukkhaṃ domanassaṃ paṭisaṃvedeti. so kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjati. idaṃ vuccati, bhikkhave, dhammasamādānaṃ paccuppannadukkhaṃ āyatiṃ sukhavipākaṃ.</p>\r\n<p class="bodytext"><a name="para481"></a><a name="para481_mn1"></a><span class="paranum">481</span>. katamañca, bhikkhave, dhammasamādānaṃ paccuppannasukhañceva āyatiñca sukhavipākaṃ? idha, bhikkhave, ekacco sahāpi sukhena sahāpi somanassena pāṇātipātā paṭivirato hoti, pāṇātipātā veramaṇīpaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena adinnādānā paṭivirato hoti, adinnādānā veramaṇīpaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena kāmesumicchācārā paṭivirato hoti, kāmesumicchācārā veramaṇīpaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena <a name="T1.0572"></a> sahāpi somanassena musāvādā paṭivirato hoti, musāvādā veramaṇīpaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena pisuṇāya vācāya paṭivirato hoti, pisuṇāya</p>\r\n','\r',char(13)),'\n',char(10)),'-481-');
INSERT INTO pages VALUES(3429,'mula_ma_01',390,replace(replace('<p class="bodytext">vācāya <a name="M1.0390"></a> veramaṇīpaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena pharusāya vācāya paṭivirato hoti, pharusāya vācāya veramaṇīpaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena samphappalāpā paṭivirato hoti, samphappalāpā veramaṇīpaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena anabhijjhālu hoti, anabhijjhāpaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena abyāpannacitto hoti, abyāpādapaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti, sahāpi sukhena sahāpi somanassena sammādiṭṭhi hoti, sammādiṭṭhipaccayā ca sukhaṃ somanassaṃ paṭisaṃvedeti. so kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjati. idaṃ, vuccati, bhikkhave, dhammasamādānaṃ paccuppannasukhañceva <a name="V1.0397"></a> āyatiñca sukhavipākaṃ. imāni kho, bhikkhave, cattāri dhammasamādānāni.</p>\r\n<p class="bodytext"><a name="para482"></a><a name="para482_mn1"></a><span class="paranum">482</span>. seyyathāpi, bhikkhave, tittakālābu visena saṃsaṭṭho. atha puriso āgaccheyya jīvitukāmo amaritukāmo sukhakāmo dukkhappaṭikūlo. tamenaṃ evaṃ vadeyyuṃ – ‘ambho purisa, ayaṃ tittakālābu visena saṃsaṭṭho, sace ākaṅkhasi piva <span class="note">[pipa (sī. pī.)]</span>. tassa <a name="P1.0316"></a> te pivato <span class="note">[pipato (sī. pī.)]</span> ceva nacchādessati <a name="T1.0573"></a> vaṇṇenapi gandhenapi rasenapi, pivitvā <span class="note">[pītvā (sī.)]</span> ca pana maraṇaṃ vā nigacchasi maraṇamattaṃ vā dukkha’nti. so taṃ appaṭisaṅkhāya piveyya, nappaṭinissajjeyya. tassa taṃ pivato ceva nacchādeyya vaṇṇenapi gandhenapi rasenapi, pivitvā ca pana maraṇaṃ vā nigaccheyya maraṇamattaṃ vā dukkhaṃ. tathūpamāhaṃ, bhikkhave, imaṃ dhammasamādānaṃ vadāmi, yamidaṃ dhammasamādānaṃ paccuppannadukkhañceva āyatiñca dukkhavipākaṃ.</p>\r\n<p class="bodytext"><a name="para483"></a><a name="para483_mn1"></a><span class="paranum">483</span>. seyyathāpi, bhikkhave, āpānīyakaṃso vaṇṇasampanno gandhasampanno rasasampanno. so ca kho visena saṃsaṭṭho. atha puriso āgaccheyya jīvitukāmo amaritukāmo sukhakāmo dukkhappaṭikūlo. tamenaṃ evaṃ vadeyyuṃ – ‘ambho purisa, ayaṃ āpānīyakaṃso vaṇṇasampanno gandhasampanno rasasampanno. so ca kho visena saṃsaṭṭho, sace ākaṅkhasi piva. tassa te pivatohi <span class="note">[pivatopi (ka.)]</span> kho chādessati vaṇṇenapi gandhenapi rasenapi, pivitvā ca pana maraṇaṃ vā nigacchasi maraṇamattaṃ vā dukkha’nti. so taṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-482-483-');
INSERT INTO pages VALUES(3430,'mula_ma_01',391,replace(replace('<p class="bodytext">appaṭisaṅkhāya <a name="M1.0391"></a> piveyya, nappaṭinissajjeyya. tassa taṃ pivatohi kho chādeyya vaṇṇenapi gandhenapi rasenapi, pivitvā ca pana maraṇaṃ vā nigaccheyya maraṇamattaṃ vā dukkhaṃ. tathūpamāhaṃ, bhikkhave, imaṃ dhammasamādānaṃ vadāmi, yamidaṃ dhammasamādānaṃ paccuppannasukhaṃ āyatiṃ dukkhavipākaṃ.</p>\r\n<p class="bodytext"><a name="para484"></a><a name="para484_mn1"></a><span class="paranum">484</span>. seyyathāpi, bhikkhave, pūtimuttaṃ nānābhesajjehi saṃsaṭṭhaṃ. atha <a name="T1.0574"></a> puriso āgaccheyya paṇḍukarogī. tamenaṃ evaṃ vadeyyuṃ – ‘ambho purisa, idaṃ pūtimuttaṃ nānābhesajjehi saṃsaṭṭhaṃ, sace ākaṅkhasi piva. tassa te pivatohi kho nacchādessati vaṇṇenapi gandhenapi rasenapi, pivitvā ca pana sukhī bhavissasī’ti. so taṃ paṭisaṅkhāya piveyya, nappaṭinissajjeyya. tassa taṃ pivatohi kho nacchādeyya vaṇṇenapi gandhenapi rasenapi, pivitvā <a name="V1.0398"></a> ca pana sukhī assa. tathūpamāhaṃ, bhikkhave, imaṃ dhammasamādānaṃ vadāmi, yamidaṃ dhammasamādānaṃ paccuppannadukkhaṃ āyatiṃ sukhavipākaṃ.</p>\r\n<p class="bodytext"><a name="para485"></a><a name="para485_mn1"></a><span class="paranum">485</span>. seyyathāpi, bhikkhave, dadhi ca madhu ca sappi ca phāṇitañca ekajjhaṃ saṃsaṭṭhaṃ. atha puriso āgaccheyya lohitapakkhandiko. tamenaṃ evaṃ vadeyyuṃ – ‘ambho purisa, idaṃ <a name="P1.0317"></a> dadhiṃ ca madhuṃ ca sappiṃ ca phāṇitañca ekajjhaṃ saṃsaṭṭhaṃ, sace ākaṅkhasi piva. tassa te pivato ceva chādessati vaṇṇenapi gandhenapi rasenapi, pivitvā ca pana sukhī bhavissasī’ti. so taṃ paṭisaṅkhāya piveyya, nappaṭinissajjeyya. tassa taṃ pivato ceva chādeyya vaṇṇenapi gandhenapi rasenapi, pivitvā ca pana sukhī assa. tathūpamāhaṃ, bhikkhave, imaṃ dhammasamādānaṃ vadāmi, yamidaṃ dhammasamādānaṃ paccuppannasukhañceva āyatiñca sukhavipākaṃ.</p>\r\n<p class="bodytext"><a name="para486"></a><a name="para486_mn1"></a><span class="paranum">486</span>. seyyathāpi, bhikkhave, vassānaṃ pacchime māse saradasamaye viddhe vigatavalāhake deve ādicco nabhaṃ abbhussakkamāno sabbaṃ ākāsagataṃ tamagataṃ abhivihacca bhāsate ca tapate ca virocate ca, evameva <a name="T1.0575"></a> kho, bhikkhave, yamidaṃ dhammasamādānaṃ paccuppannasukhañceva āyatiñca sukhavipākaṃ tadaññe puthusamaṇabrāhmaṇaparappavāde abhivihacca bhāsate ca tapate ca virocate cā"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">mahādhammasamādānasuttaṃ niṭṭhitaṃ chaṭṭhaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-484-485-486-');
INSERT INTO pages VALUES(3431,'mula_ma_01',392,replace(replace('<p class="subhead">7. vīmaṃsakasuttaṃ</p>\r\n<p class="bodytext"><a name="para487"></a><a name="para487_mn1"></a><span class="paranum">487</span>. evaṃ <a name="T1.0576"></a><a name="M1.0392"></a><a name="V1.0399"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca – "vīmaṃsakena, bhikkhave, bhikkhunā parassa cetopariyāyaṃ ajānantena <span class="note">[ājānantena (pī. ka.), ajānantena kinti (?)]</span> tathāgate samannesanā kātabbā ‘sammāsambuddho vā no vā’ iti viññāṇāyā"ti. "bhagavaṃmūlakā no, bhante, dhammā, bhagavaṃnettikā bhagavaṃpaṭisaraṇā, sādhu vata, bhante, bhagavantaṃyeva paṭibhātu etassa bhāsitassa attho, bhagavato sutvā bhikkhū dhāressantī"ti. "tena hi, bhikkhave, suṇātha, sādhukaṃ manasi karotha, bhāsissāmī"ti<a name="P1.0318"></a>. evaṃ, bhante"ti kho te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext"><a name="para488"></a><a name="para488_mn1"></a><span class="paranum">488</span>. vīmaṃsakena, bhikkhave, bhikkhunā parassa cetopariyāyaṃ ajānantena dvīsu dhammesu tathāgato samannesitabbo cakkhusotaviññeyyesu dhammesu – ‘ye saṃkiliṭṭhā cakkhusotaviññeyyā dhammā, saṃvijjanti vā te tathāgatassa no vā’ti? tamenaṃ samannesamāno evaṃ jānāti – ‘ye saṃkiliṭṭhā cakkhusotaviññeyyā dhammā, na te tathāgatassa saṃvijjantī’ti.</p>\r\n<p class="bodytext">yato naṃ samannesamāno evaṃ jānāti – ‘ye saṃkiliṭṭhā cakkhusotaviññeyyā dhammā, na te tathāgatassa saṃvijjantī’ti, tato naṃ uttariṃ samannesati – ‘ye vītimissā cakkhusotaviññeyyā dhammā, saṃvijjanti vā te tathāgatassa no <a name="T1.0577"></a> vā’ti? tamenaṃ samannesamāno evaṃ jānāti – ‘ye vītimissā cakkhusotaviññeyyā dhammā, na te tathāgatassa saṃvijjantī’ti.</p>\r\n<p class="bodytext">yato naṃ samannesamāno evaṃ jānāti – ‘ye vītimissā cakkhusotaviññeyyā dhammā, na te tathāgatassa saṃvijjantī’ti, tato naṃ uttariṃ samannesati – ‘ye vodātā cakkhusotaviññeyyā <a name="V1.0400"></a> dhammā, saṃvijjanti vā te tathāgatassa no vā’ti? tamenaṃ samannesamāno</p>\r\n','\r',char(13)),'\n',char(10)),'-487-488-');
INSERT INTO pages VALUES(3432,'mula_ma_01',393,replace(replace('<p class="bodytext">evaṃ <a name="M1.0393"></a> jānāti – ‘ye vodātā cakkhusotaviññeyyā dhammā, saṃvijjanti te tathāgatassā’ti.</p>\r\n<p class="bodytext">yato naṃ samannesamāno evaṃ jānāti – ‘ye vodātā cakkhusotaviññeyyā dhammā, saṃvijjanti te tathāgatassā’ti, tato naṃ uttariṃ samannesati – ‘dīgharattaṃ samāpanno ayamāyasmā imaṃ kusalaṃ dhammaṃ, udāhu ittarasamāpanno’ti? tamenaṃ samannesamāno evaṃ jānāti – ‘dīgharattaṃ samāpanno ayamāyasmā imaṃ kusalaṃ dhammaṃ, nāyamāyasmā ittarasamāpanno’ti.</p>\r\n<p class="bodytext">yato naṃ samannesamāno evaṃ jānāti – ‘dīgharattaṃ samāpanno ayamāyasmā imaṃ kusalaṃ dhammaṃ, nāyamāyasmā ittarasamāpanno’ti, tato naṃ uttariṃ samannesati – ‘ñattajjhāpanno ayamāyasmā bhikkhu yasappatto, saṃvijjantassa idhekacce ādīnavā’ti? na tāva, bhikkhave, bhikkhuno idhekacce ādīnavā saṃvijjanti yāva na ñattajjhāpanno hoti yasappatto. yato ca kho, bhikkhave, bhikkhu ñattajjhāpanno hoti yasappatto<a name="T1.0578"></a>, athassa idhekacce ādīnavā saṃvijjanti. tamenaṃ samannesamāno evaṃ jānāti – ‘ñattajjhāpanno ayamāyasmā bhikkhu yasappatto, nāssa idhekacce ādīnavā saṃvijjantī’ti.</p>\r\n<p class="bodytext">yato naṃ samannesamāno evaṃ jānāti – ‘ñattajjhāpanno ayamāyasmā <a name="P1.0319"></a> bhikkhu yasappatto, nāssa idhekacce ādīnavā saṃvijjantī’ti, tato naṃ uttariṃ samannesati – ‘abhayūparato ayamāyasmā, nāyamāyasmā bhayūparato, vītarāgattā kāme na sevati khayā rāgassā’ti? tamenaṃ samannesamāno evaṃ jānāti – ‘abhayūparato ayamāyasmā, nāyamāyasmā bhayūparato, vītarāgattā kāme na sevati khayā rāgassā’ti. tañce, bhikkhave, bhikkhuṃ pare evaṃ puccheyyuṃ – ‘ke panāyasmato ākārā, ke anvayā, yenāyasmā evaṃ vadesi – abhayūparato ayamāyasmā, nāyamāyasmā bhayūparato, vītarāgattā kāme na sevati khayā rāgassā’ti. sammā byākaramāno, bhikkhave, bhikkhu evaṃ byākareyya – ‘tathā hi pana ayamāyasmā saṅghe vā viharanto eko vā viharanto, ye ca tattha sugatā ye ca tattha duggatā, ye ca tattha gaṇamanusāsanti, ye ca idhekacce āmisesu saṃdissanti, ye ca idhekacce āmisena anupalittā, nāyamāyasmā taṃ tena</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3433,'mula_ma_01',394,replace(replace('<p class="bodytext">avajānāti<a name="M1.0394"></a>. sammukhā kho pana metaṃ bhagavato <a name="V1.0401"></a> sutaṃ sammukhā paṭiggahitaṃ – abhayūparatohamasmi, nāhamasmi bhayūparato, vītarāgattā kāme na sevāmi khayā rāgassā’ti.</p>\r\n<p class="bodytext"><a name="para489"></a><a name="para489_mn1"></a><span class="paranum">489</span>. tatra<a name="T1.0579"></a>, bhikkhave, tathāgatova uttariṃ paṭipucchitabbo – ‘ye saṃkiliṭṭhā cakkhusotaviññeyyā dhammā, saṃvijjanti vā te tathāgatassa no vā’ti? byākaramāno, bhikkhave, tathāgato evaṃ byākareyya – ‘ye saṃkiliṭṭhā cakkhusotaviññeyyā dhammā, na te tathāgatassa saṃvijjantī"’ti.</p>\r\n<p class="bodytext">ye vītimissā cakkhusotaviññeyyā dhammā, saṃvijjanti vā te tathāgatassa no vāti? byākaramāno, bhikkhave, tathāgato evaṃ byākareyya – ‘ye vītimissā cakkhusotaviññeyyā dhammā, na te tathāgatassa saṃvijjantī’ti.</p>\r\n<p class="bodytext">ye vodātā cakkhusotaviññeyyā dhammā, saṃvijjanti vā te tathāgatassa no vāti? byākaramāno, bhikkhave, tathāgato evaṃ byākareyya – ‘ye vodātā cakkhusotaviññeyyā dhammā, saṃvijjanti te tathāgatassa, etaṃpathohamasmi, etaṃgocaro <span class="note">[etapathohamasmi etagocaro (sī. syā. kaṃ. pī.)]</span>, no ca tena tammayo’ti.</p>\r\n<p class="bodytext">evaṃvādiṃ kho, bhikkhave, satthāraṃ arahati sāvako upasaṅkamituṃ dhammassavanāya. tassa satthā dhammaṃ deseti uttaruttariṃ paṇītapaṇītaṃ kaṇhasukkasappaṭibhāgaṃ. yathā yathā kho, bhikkhave, bhikkhuno satthā dhammaṃ deseti uttaruttariṃ paṇītapaṇītaṃ kaṇhasukkasappaṭibhāgaṃ tathā tathā so tasmiṃ dhamme abhiññāya idhekaccaṃ dhammaṃ dhammesu <a name="P1.0320"></a> niṭṭhaṃ gacchati, satthari pasīdati – ‘sammāsambuddho bhagavā, svākkhāto bhagavatā dhammo, suppaṭipanno saṅgho’ti. tañce, bhikkhave, bhikkhuṃ pare evaṃ puccheyyuṃ – ‘ke panāyasmato ākārā, ke anvayā, yenāyasmā evaṃ vadesi – sammāsambuddho bhagavā<a name="T1.0580"></a>, svākkhāto bhagavatā dhammo, suppaṭipanno saṅgho’ti? sammā byākaramāno, bhikkhave, bhikkhu evaṃ byākareyya – ‘idhāhaṃ, āvuso, yena bhagavā tenupasaṅkamiṃ dhammassavanāya. tassa me bhagavā dhammaṃ deseti uttaruttariṃ paṇītapaṇītaṃ kaṇhasukkasappaṭibhāgaṃ. yathā yathā me,</p>\r\n','\r',char(13)),'\n',char(10)),'-489-');
INSERT INTO pages VALUES(3434,'mula_ma_01',395,replace(replace('<p class="bodytext">āvuso<a name="M1.0395"></a>, bhagavā dhammaṃ deseti uttaruttariṃ paṇītapaṇītaṃ kaṇhasukkasappaṭibhāgaṃ tathā tathāhaṃ tasmiṃ dhamme abhiññāya idhekaccaṃ dhammaṃ dhammesu niṭṭhamagamaṃ, satthari pasīdiṃ – sammāsambuddho bhagavā, svākkhāto bhagavatā, dhammo, suppaṭipanno saṅgho’ti.</p>\r\n<p class="bodytext"><a name="para490"></a><a name="para490_mn1"></a><span class="paranum">490</span>. yassa <a name="V1.0402"></a> kassaci, bhikkhave, imehi ākārehi imehi padehi imehi byañjanehi tathāgate saddhā niviṭṭhā hoti mūlajātā patiṭṭhitā, ayaṃ vuccati, bhikkhave, ākāravatī saddhā dassanamūlikā, daḷhā, asaṃhāriyā samaṇena vā brāhmaṇena vā devena vā mārena vā brahmunā vā kenaci vā lokasmiṃ. evaṃ kho, bhikkhave, tathāgate dhammasamannesanā hoti. evañca pana tathāgato dhammatāsusamanniṭṭho hotī"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">vīmaṃsakasuttaṃ niṭṭhitaṃ sattamaṃ.</p>\r\n<p class="subhead">8. kosambiyasuttaṃ</p>\r\n<p class="bodytext"><a name="para491"></a><a name="para491_mn1"></a><span class="paranum">491</span>. evaṃ <a name="T1.0581"></a><a name="V1.0403"></a> me sutaṃ – ekaṃ samayaṃ bhagavā kosambiyaṃ viharati ghositārāme. tena kho pana samayena kosambiyaṃ bhikkhū bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṃ mukhasattīhi vitudantā viharanti. te na ceva aññamaññaṃ saññāpenti na ca saññattiṃ upenti, na ca aññamaññaṃ nijjhāpenti, na ca nijjhattiṃ upenti. atha kho aññataro <a name="P1.0321"></a> bhikkhu yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. ekamantaṃ nisinno kho so bhikkhu bhagavantaṃ etadavoca – "idha, bhante, kosambiyaṃ bhikkhū bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṃ mukhasattīhi vitudantā viharanti, te na ceva aññamaññaṃ saññāpenti, na ca saññattiṃ upenti, na ca aññamaññaṃ nijjhāpenti, na ca nijjhattiṃ upentī"ti.</p>\r\n<p class="bodytext">atha kho bhagavā aññataraṃ bhikkhuṃ āmantesi – "ehi tvaṃ, bhikkhu, mama vacanena te bhikkhū āmantehi – ‘satthā vo āyasmante āmantetī"’ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-490-491-');
INSERT INTO pages VALUES(3435,'mula_ma_01',396,replace(replace('<p class="bodytext">evaṃ<a name="M1.0396"></a>, bhante"ti kho so bhikkhu bhagavato paṭissutvā yena te bhikkhū tenupasaṅkami, upasaṅkamitvā te bhikkhū etadavoca – "satthā āyasmante āmantetī"ti. "evamāvuso"ti kho te bhikkhū tassa bhikkhuno paṭissutvā yena bhagavā tenupasaṅkamiṃsu, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu. ekamantaṃ <a name="T1.0582"></a> nisinne kho te bhikkhū bhagavā etadavoca – "saccaṃ kira tumhe, bhikkhave, bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṃ mukhasattīhi vitudantā viharatha, te na ceva aññamaññaṃ saññāpetha, na ca saññattiṃ upetha, na ca aññamaññaṃ nijjhāpetha, na ca nijjhattiṃ upethā"ti? "evaṃ, bhante". "taṃ kiṃ maññatha, bhikkhave, yasmiṃ tumhe samaye bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṃ <a name="P1.0322"></a> mukhasattīhi vitudantā viharatha, api nu tumhākaṃ tasmiṃ samaye mettaṃ kāyakammaṃ paccupaṭṭhitaṃ hoti sabrahmacārīsu āvi ceva raho ca, mettaṃ vacīkammaṃ…pe… mettaṃ manokammaṃ paccupaṭṭhitaṃ hoti sabrahmacārīsu āvi ceva raho cā"ti? "no hetaṃ, bhante". "iti kira, bhikkhave, yasmiṃ tumhe samaye bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṃ mukhasattīhi vitudantā viharatha, neva tumhākaṃ <a name="V1.0404"></a> tasmiṃ samaye mettaṃ kāyakammaṃ paccupaṭṭhitaṃ hoti sabrahmacārīsu āvi ceva raho ca, na mettaṃ vacīkammaṃ…pe… na mettaṃ manokammaṃ paccupaṭṭhitaṃ hoti sabrahmacārīsu āvi ceva raho ca. atha kiñcarahi tumhe, moghapurisā, kiṃ jānantā kiṃ passantā bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṃ mukhasattīhi vitudantā viharatha, te na ceva aññamaññaṃ saññāpetha, na ca saññattiṃ upetha, na ca aññamaññaṃ nijjhāpetha, na ca nijjhattiṃ upetha<a name="T1.0583"></a>? tañhi tumhākaṃ, moghapurisā, bhavissati dīgharattaṃ ahitāya dukkhāyā"ti.</p>\r\n<p class="bodytext"><a name="para492"></a><a name="para492_mn1"></a><span class="paranum">492</span>. atha kho bhagavā bhikkhū āmantesi – "chayime, bhikkhave, dhammā sāraṇīyā piyakaraṇā garukaraṇā saṅgahāya avivādāya sāmaggiyā ekībhāvāya saṃvattanti. katame cha? idha, bhikkhave, bhikkhuno mettaṃ kāyakammaṃ paccupaṭṭhitaṃ hoti sabrahmacārīsu āvi ceva raho ca. ayampi dhammo sāraṇīyo piyakaraṇo garukaraṇo saṅgahāya avivādāya sāmaggiyā ekībhāvāya saṃvattati.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhuno mettaṃ vacīkammaṃ paccupaṭṭhitaṃ hoti sabrahmacārīsu āvi ceva raho ca. ayampi dhammo sāraṇīyo</p>\r\n','\r',char(13)),'\n',char(10)),'-492-');
INSERT INTO pages VALUES(3436,'mula_ma_01',397,replace(replace('<p class="bodytext">piyakaraṇo <a name="M1.0397"></a> garukaraṇo saṅgahāya avivādāya sāmaggiyā ekibhāvāya saṃvattati.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhuno mettaṃ manokammaṃ paccupaṭṭhitaṃ hoti sabrahmacārīsu āvi ceva raho ca. ayampi dhammo sāraṇīyo piyakaraṇo garukaraṇo saṅgahāya avivādāya sāmaggiyā ekībhāvāya saṃvattati.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu ye te lābhā dhammikā dhammaladdhā antamaso pattapariyāpannamattampi, tathārūpehi lābhehi appaṭivibhattabhogī hoti sīlavantehi sabrahmacārīhi sādhāraṇabhogī. ayampi dhammo sāraṇīyo piyakaraṇo garukaraṇo saṅgahāya avivādāya sāmaggiyā ekībhāvāya saṃvattati.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu yāni tāni sīlāni akhaṇḍāni acchiddāni asabalāni akammāsāni bhujissāni viññuppasatthāni <a name="T1.0584"></a> aparāmaṭṭhāni samādhisaṃvattanikāni tathārūpesu sīlesu sīlasāmaññagato viharati sabrahmacārīhi āvi ceva raho ca. ayampi dhammo <a name="V1.0405"></a> sāraṇīyo piyakaraṇo garukaraṇo saṅgahāya avivādāya sāmaggiyā ekībhāvāya saṃvattati.</p>\r\n<p class="bodytext">puna caparaṃ, bhikkhave, bhikkhu yāyaṃ diṭṭhi ariyā niyyānikā niyyāti takkarassa sammā dukkhakkhayāya tathārūpāya diṭṭhiyā diṭṭhisāmaññagato viharati sabrahmacārīhi āvi ceva raho ca. ayampi dhammo sāraṇīyo piyakaraṇo garukaraṇo saṅgahāya avivādāya sāmaggiyā ekībhāvāya saṃvattati.</p>\r\n<p class="bodytext">ime kho, bhikkhave, cha sāraṇīyā dhammā piyakaraṇā garukaraṇā saṅgahāya avivādāya sāmaggiyā ekībhāvāya saṃvattanti. imesaṃ kho, bhikkhave, channaṃ sāraṇīyānaṃ dhammānaṃ etaṃ aggaṃ etaṃ saṅgāhikaṃ <span class="note">[saṅgāhakaṃ (?)]</span> etaṃ saṅghāṭanikaṃ – yadidaṃ yāyaṃ diṭṭhi ariyā niyyānikā niyyāti takkarassa sammā dukkhakkhayāya. seyyathāpi, bhikkhave, kūṭāgārassa etaṃ aggaṃ etaṃ saṅgāhikaṃ etaṃ saṅghāṭanikaṃ yadidaṃ kūṭaṃ, evameva kho, bhikkhave<a name="P1.0323"></a>, imesaṃ channaṃ sāraṇīyānaṃ dhammānaṃ etaṃ aggaṃ etaṃ saṅgāhikaṃ etaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3437,'mula_ma_01',398,replace(replace('<p class="bodytext">saṅghāṭanikaṃ <a name="M1.0398"></a> yadidaṃ yāyaṃ diṭṭhi ariyā niyyānikā niyyāti takkarassa sammā dukkhakkhayāya.</p>\r\n<p class="bodytext"><a name="para493"></a><a name="para493_mn1"></a><span class="paranum">493</span>. kathañca, bhikkhave, yāyaṃ diṭṭhi ariyā niyyānikā niyyāti takkarassa sammā dukkhakkhayāya? idha, bhikkhave, bhikkhu araññagato vā rukkhamūlagato vā suññāgāragato vā iti paṭisañcikkhati – ‘atthi nu <a name="T1.0585"></a> kho me taṃ pariyuṭṭhānaṃ ajjhattaṃ appahīnaṃ, yenāhaṃ pariyuṭṭhānena pariyuṭṭhitacitto yathābhūtaṃ nappajāneyyaṃ na passeyya’nti? sace, bhikkhave, bhikkhu kāmarāgapariyuṭṭhito hoti, pariyuṭṭhitacittova hoti. sace, bhikkhave, bhikkhu byāpādapariyuṭṭhito hoti, pariyuṭṭhitacittova hoti. sace, bhikkhave, bhikkhu thīnamiddhapariyuṭṭhito hoti, pariyuṭṭhitacittova hoti. sace, bhikkhave, bhikkhu uddhaccakukkuccapariyuṭṭhito hoti, pariyuṭṭhitacittova hoti. sace, bhikkhave, bhikkhu vicikicchāpariyuṭṭhito hoti, pariyuṭṭhitacittova hoti. sace, bhikkhave, bhikkhu idhalokacintāya pasuto hoti, pariyuṭṭhitacittova hoti. sace, bhikkhave, bhikkhu paralokacintāya pasuto hoti, pariyuṭṭhitacittova hoti. sace, bhikkhave, bhikkhu bhaṇḍanajāto kalahajāto vivādāpanno aññamaññaṃ mukhasattīhi vitudanto viharati, pariyuṭṭhitacittova hoti<a name="V1.0406"></a>. so evaṃ pajānāti – ‘natthi kho me taṃ pariyuṭṭhānaṃ ajjhattaṃ appahīnaṃ, yenāhaṃ pariyuṭṭhānena pariyuṭṭhitacitto yathābhūtaṃ nappajāneyyaṃ na passeyyaṃ. suppaṇihitaṃ me mānasaṃ saccānaṃ bodhāyā’ti. idamassa paṭhamaṃ ñāṇaṃ adhigataṃ hoti ariyaṃ lokuttaraṃ asādhāraṇaṃ puthujjanehi.</p>\r\n<p class="bodytext"><a name="para494"></a><a name="para494_mn1"></a><span class="paranum">494</span>. puna caparaṃ, bhikkhave, ariyasāvako iti paṭisañcikkhati – ‘imaṃ nu kho ahaṃ diṭṭhiṃ āsevanto bhāvento bahulīkaronto labhāmi paccattaṃ samathaṃ, labhāmi paccattaṃ nibbuti’nti? so evaṃ pajānāti – ‘imaṃ kho ahaṃ diṭṭhiṃ āsevanto bhāvento bahulīkaronto labhāmi paccattaṃ <a name="T1.0586"></a> samathaṃ, labhāmi paccattaṃ nibbuti’nti. idamassa dutiyaṃ ñāṇaṃ adhigataṃ hoti ariyaṃ lokuttaraṃ asādhāraṇaṃ puthujjanehi.</p>\r\n<p class="bodytext"><a name="para495"></a><a name="para495_mn1"></a><span class="paranum">495</span>. puna caparaṃ, bhikkhave, ariyasāvako iti paṭisañcikkhati – ‘yathā rūpāyāhaṃ diṭṭhiyā samannāgato, atthi nu kho ito bahiddhā añño</p>\r\n','\r',char(13)),'\n',char(10)),'-493-494-495-');
INSERT INTO pages VALUES(3438,'mula_ma_01',399,replace(replace('<p class="bodytext">samaṇo <a name="M1.0399"></a> vā brāhmaṇo vā <a name="P1.0324"></a> tathārūpāya diṭṭhiyā samannāgato’ti? so evaṃ pajānāti – ‘yathārūpāyāhaṃ diṭṭhiyā samannāgato, natthi ito bahiddhā añño samaṇo vā brāhmaṇo vā tathārūpāya diṭṭhiyā samannāgato’ti. idamassa tatiyaṃ ñāṇaṃ adhigataṃ hoti ariyaṃ lokuttaraṃ asādhāraṇaṃ puthujjanehi.</p>\r\n<p class="bodytext"><a name="para496"></a><a name="para496_mn1"></a><span class="paranum">496</span>. puna caparaṃ, bhikkhave, ariyasāvako iti paṭisañcikkhati – ‘yathārūpāya dhammatāya diṭṭhisampanno puggalo samannāgato, ahampi tathārūpāya dhammatāya samannāgato’ti. kathaṃrūpāya ca, bhikkhave, dhammatāya diṭṭhisampanno puggalo samannāgato? dhammatā esā, bhikkhave, diṭṭhisampannassa puggalassa – ‘kiñcāpi tathārūpiṃ āpattiṃ āpajjati, yathārūpāya āpattiyā vuṭṭhānaṃ paññāyati, atha kho naṃ khippameva satthari vā viññūsu vā sabrahmacārīsu deseti vivarati uttānīkaroti, desetvā vivaritvā uttānīkatvā āyatiṃ saṃvaraṃ āpajjati’. seyyathāpi, bhikkhave, daharo kumāro mando uttānaseyyako hatthena vā pādena vā aṅgāraṃ akkamitvā khippameva paṭisaṃharati, evameva kho, bhikkhave, dhammatā esā diṭṭhisampannassa puggalassa – ‘kiñcāpi tathārūpiṃ <a name="T1.0587"></a> āpattiṃ āpajjati yathārūpāya āpattiyā vuṭṭhānaṃ paññāyati, atha kho naṃ khippameva satthari vā viññūsu vā sabrahmacārīsu deseti vivarati <a name="V1.0407"></a> uttānīkaroti, desetvā vivaritvā uttānīkatvā āyatiṃ saṃvaraṃ āpajjati’. so evaṃ pajānāti – ‘yathārūpāya dhammatāya diṭṭhisampanno puggalo samannāgato, ahampi tathārūpāya dhammatāya samannāgato’ti. idamassa catutthaṃ ñāṇaṃ adhigataṃ hoti ariyaṃ lokuttaraṃ asādhāraṇaṃ puthujjanehi.</p>\r\n<p class="bodytext"><a name="para497"></a><a name="para497_mn1"></a><span class="paranum">497</span>. puna caparaṃ, bhikkhave, ariyasāvako iti paṭisañcikkhati – ‘yathārūpāya dhammatāya diṭṭhisampanno puggalo samannāgato, ahampi tathārūpāya dhammatāya samannāgato’ti. kathaṃrūpāya ca, bhikkhave, dhammatāya diṭṭhisampanno puggalo samannāgato? dhammatā esā, bhikkhave, diṭṭhisampannassa puggalassa – ‘kiñcāpi yāni tāni sabrahmacārīnaṃ uccāvacāni kiṃkaraṇīyāni tattha ussukkaṃ āpanno hoti, atha khvāssa tibbāpekkhā hoti adhisīlasikkhāya adhicittasikkhāya adhipaññāsikkhāya’. seyyathāpi, bhikkhave, gāvī taruṇavacchā thambañca ālumpati vacchakañca apacinati, evameva kho,</p>\r\n','\r',char(13)),'\n',char(10)),'-496-497-');
INSERT INTO pages VALUES(3439,'mula_ma_01',400,replace(replace('<p class="bodytext">bhikkhave<a name="M1.0400"></a>, dhammatā esā diṭṭhisampannassa puggalassa – ‘kiñcāpi yāni tāni sabrahmacārīnaṃ uccāvacāni kiṃkaraṇīyāni tattha ussukkaṃ āpanno hoti, atha khvāssa tibbāpekkhā hoti adhisīlasikkhāya adhicittasikkhāya adhipaññāsikkhāya’. so evaṃ pajānāti – ‘yathārūpāya dhammatāya diṭṭhisampanno puggalo <a name="T1.0588"></a> samannāgato, ahampi tathārūpāya dhammatāya samannāgato’ti. idamassa pañcamaṃ ñāṇaṃ adhigataṃ hoti ariyaṃ lokuttaraṃ asādhāraṇaṃ puthujjanehi.</p>\r\n<p class="bodytext"><a name="para498"></a><a name="para498_mn1"></a><span class="paranum">498</span>. puna <a name="P1.0325"></a> caparaṃ, bhikkhave, ariyasāvako iti paṭisañcikkhati – ‘yathārūpāya balatāya diṭṭhisampanno puggalo samannāgato, ahampi tathārūpāya balatāya samannāgato’ti. kathaṃrūpāya ca, bhikkhave, balatāya diṭṭhisampanno puggalo samannāgato? balatā esā, bhikkhave, diṭṭhisampannassa puggalassa yaṃ tathāgatappavedite dhammavinaye desiyamāne aṭṭhiṃkatvā manasikatvā sabbacetasā <span class="note">[sabbacetaso (sī. syā. kaṃ. pī.), sabbaṃ cetasā (ka.)]</span> samannāharitvā ohitasoto dhammaṃ suṇāti. so evaṃ pajānāti – ‘yathārūpāya balatāya diṭṭhisampanno puggalo samannāgato, ahampi tathārūpāya balatāya samannāgato’ti. idamassa chaṭṭhaṃ ñāṇaṃ adhigataṃ hoti ariyaṃ lokuttaraṃ asādhāraṇaṃ puthujjanehi.</p>\r\n<p class="bodytext"><a name="para499"></a><a name="para499_mn1"></a><span class="paranum">499</span>. puna caparaṃ, bhikkhave, ariyasāvako iti paṭisañcikkhati – ‘yathārūpāya balatāya <a name="V1.0408"></a> diṭṭhisampanno puggalo samannāgato, ahampi tathārūpāya balatāya samannāgato’ti. kathaṃrūpāya ca, bhikkhave, balatāya diṭṭhisampanno puggalo samannāgato? balatā esā, bhikkhave, diṭṭhisampannassa puggalassa yaṃ tathāgatappavedite dhammavinaye desiyamāne labhati atthavedaṃ, labhati dhammavedaṃ, labhati dhammūpasaṃhitaṃ pāmojjaṃ. so evaṃ pajānāti – ‘yathārūpāya balatāya diṭṭhisampanno puggalo <a name="T1.0589"></a> samannāgato, ahampi tathārūpāya balatāya samannāgato’ti. idamassa sattamaṃ ñāṇaṃ adhigataṃ hoti ariyaṃ lokuttaraṃ asādhāraṇaṃ puthujjanehi.</p>\r\n<p class="bodytext"><a name="para500"></a><a name="para500_mn1"></a><span class="paranum">500</span>. evaṃ sattaṅgasamannāgatassa kho, bhikkhave, ariyasāvakassa dhammatā susamanniṭṭhā hoti sotāpattiphalasacchikiriyāya. evaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-498-499-500-');
INSERT INTO pages VALUES(3440,'mula_ma_01',401,replace(replace('<p class="bodytext">sattaṅgasamannāgato <a name="M1.0401"></a> kho, bhikkhave, ariyasāvako sotāpattiphalasamannāgato hotī"ti.</p>\r\n<p class="bodytext">idamavoca bhagavā. attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p>\r\n<p class="centered">kosambiyasuttaṃ niṭṭhitaṃ aṭṭhamaṃ.</p>\r\n<p class="subhead">9. brahmanimantanikasuttaṃ</p>\r\n<p class="bodytext"><a name="para501"></a><a name="para501_mn1"></a><span class="paranum">501</span>. evaṃ <a name="T1.0590"></a><a name="V1.0409"></a><a name="P1.0326"></a> me sutaṃ – ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. tatra kho bhagavā bhikkhū āmantesi – "bhikkhavo"ti. "bhadante"ti te bhikkhū bhagavato paccassosuṃ. bhagavā etadavoca –</p>\r\n<p class="bodytext">ekamidāhaṃ, bhikkhave, samayaṃ ukkaṭṭhāyaṃ viharāmi subhagavane sālarājamūle. tena kho pana, bhikkhave, samayena bakassa brahmuno evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ hoti – ‘idaṃ niccaṃ, idaṃ dhuvaṃ, idaṃ sassataṃ, idaṃ kevalaṃ, idaṃ acavanadhammaṃ, idañhi na jāyati na jīyati na mīyati na cavati na upapajjati, ito ca panaññaṃ uttari nissaraṇaṃ natthī’ti. atha khvāhaṃ, bhikkhave, bakassa brahmuno cetasā cetoparivitakkamaññāya – seyyathāpi nāma balavā puriso samiñjitaṃ vā bāhaṃ pasāreyya, pasāritaṃ vā bāhaṃ samiñjeyya, evameva – ukkaṭṭhāyaṃ subhagavane sālarājamūle antarahito tasmiṃ brahmaloke pāturahosiṃ. addasā kho maṃ, bhikkhave, bako brahmā dūratova āgacchantaṃ, disvāna maṃ etadavoca – ‘ehi kho, mārisa, svāgataṃ, mārisa! cirassaṃ kho, mārisa, imaṃ pariyāyamakāsi yadidaṃ idhāgamanāya. idañhi, mārisa, niccaṃ, idaṃ dhuvaṃ, idaṃ sassataṃ, idaṃ kevalaṃ, idaṃ acavanadhammaṃ, idañhi na jāyati na jīyati na mīyati na cavati na upapajjati. ito ca panaññaṃ uttari nissaraṇaṃ natthī"’ti.</p>\r\n<p class="bodytext">evaṃ vutte, ahaṃ, bhikkhave, bakaṃ brahmānaṃ etadavocaṃ <a name="T1.0591"></a> – "avijjāgato vata, bho, bako brahmā, avijjāgato vata, bho, bako brahmā, yatra hi nāma aniccaṃyeva samānaṃ niccanti vakkhati, addhuvaṃyeva samānaṃ dhuvanti vakkhati, asassataṃyeva samānaṃ sassatanti vakkhati, akevalaṃyeva</p>\r\n','\r',char(13)),'\n',char(10)),'-501-');
INSERT INTO pages VALUES(3441,'mula_ma_01',402,replace(replace('<p class="bodytext">samānaṃ <a name="M1.0402"></a> kevalanti vakkhati, cavanadhammaṃyeva samānaṃ acavanadhammanti vakkhati, yattha ca pana jāyati jīyati mīyati cavati upapajjati tañca vakkhati – ‘idañhi na jāyati na jīyati na mīyati na cavati na upapajjatī’ti, santañca panaññaṃ uttari nissaraṇaṃ ‘natthaññaṃ uttari nissaraṇa’nti vakkhatī"ti.</p>\r\n<p class="bodytext"><a name="para502"></a><a name="para502_mn1"></a><span class="paranum">502</span>. atha <a name="V1.0410"></a> kho, bhikkhave, māro pāpimā aññataraṃ brahmapārisajjaṃ anvāvisitvā maṃ etadavoca – ‘bhikkhu, bhikkhu, metamāsado metamāsado, eso hi, bhikkhu, brahmā mahābrahmā <a name="P1.0327"></a> abhibhū anabhibhūto aññadatthudaso vasavattī issaro kattā nimmātā seṭṭho sajitā <span class="note">[sajjitā (syā. kaṃ. ka.), sañjitā (sī. pī.)]</span> vasī pitā bhūtabhabyānaṃ. ahesuṃ kho ye, bhikkhu, tayā pubbe samaṇabrāhmaṇā lokasmiṃ pathavīgarahakā pathavījigucchakā, āpagarahakā āpajigucchakā, tejagarahakā tejajigucchakā, vāyagarahakā vāyajigucchakā, bhūtagarahakā bhūtajigucchakā, devagarahakā devajigucchakā, pajāpatigarahakā pajāpatijigucchakā, brahmagarahakā brahmajigucchakā – te kāyassa bhedā pāṇupacchedā hīne kāye patiṭṭhitā ahesuṃ. ye pana, bhikkhu, tayā pubbe samaṇabrāhmaṇā lokasmiṃ pathavīpasaṃsakā pathavābhinandino, āpapasaṃsakā āpābhinandino, tejapasaṃsakā tejābhinandino, vāyapasaṃsakā <a name="T1.0592"></a> vāyābhinandino, bhūtapasaṃsakā bhūtābhinandino, devapasaṃsakā devābhinandino, pajāpatipasaṃsakā pajāpatābhinandino, brahmapasaṃsakā brahmābhinandino – te kāyassa bhedā pāṇupacchedā paṇīte kāye patiṭṭhitā. taṃ tāhaṃ, bhikkhu, evaṃ vadāmi – ‘iṅgha tvaṃ, mārisa, yadeva te brahmā āha tadeva tvaṃ karohi, mā tvaṃ brahmuno vacanaṃ upātivattittho’. sace kho tvaṃ, bhikkhu, brahmuno vacanaṃ upātivattissasi, seyyathāpi nāma puriso siriṃ āgacchantiṃ daṇḍena paṭippaṇāmeyya, seyyathāpi vā pana, bhikkhu, puriso narakappapāte papatanto hatthehi ca pādehi ca pathaviṃ virādheyya, evaṃ sampadamidaṃ, bhikkhu, tuyhaṃ bhavissati. ‘iṅghaṃ tvaṃ, mārisa, yadeva te brahmā āha tadeva tvaṃ karohi, mā tvaṃ brahmuno vacanaṃ upātivattittho. nanu tvaṃ, bhikkhu, passasi brahmaparisaṃ sannipatita’nti? iti kho maṃ, bhikkhave, māro pāpimā brahmaparisaṃ upanesi.</p>\r\n','\r',char(13)),'\n',char(10)),'-502-');
INSERT INTO pages VALUES(3442,'mula_ma_01',403,replace(replace('<p class="bodytext">evaṃ <a name="M1.0403"></a> vutte, ahaṃ, bhikkhave, māraṃ pāpimantaṃ etadavocaṃ – ‘jānāmi kho tāhaṃ, pāpima, mā tvaṃ maññittho – na maṃ jānātī’ti. māro tvamasi, pāpima. yo ceva, pāpima, brahmā, yā ca brahmaparisā, ye ca brahmapārisajjā, sabbeva tava hatthagatā sabbeva tava vasaṃgatā. tuyhañhi, pāpima, evaṃ hoti – ‘esopi me assa hatthagato, esopi me assa vasaṃgato’ti. ahaṃ kho pana, pāpima, neva tava hatthagato neva tava vasaṃgato"ti.</p>\r\n<p class="bodytext"><a name="para503"></a><a name="para503_mn1"></a><span class="paranum">503</span>. evaṃ <a name="T1.0593"></a> vutte, bhikkhave, bako brahmā maṃ etadavoca – ‘ahañhi, mārisa, niccaṃyeva <a name="V1.0411"></a> samānaṃ niccanti vadāmi, dhuvaṃyeva <a name="P1.0328"></a> samānaṃ dhuvanti vadāmi, sassataṃyeva samānaṃ sassatanti vadāmi, kevalaṃyeva samānaṃ kevalanti vadāmi, acavanadhammaṃyeva samānaṃ acavanadhamma’nti vadāmi, yattha ca pana na jāyati na jīyati na mīyati na cavati na upapajjati tadevāhaṃ vadāmi – ‘idañhi na jāyati na jīyati na mīyati na cavati na upapajjatī’ti. asantañca panaññaṃ uttari nissaraṇaṃ ‘natthaññaṃ uttari nissaraṇa’nti vadāmi. ahesuṃ kho, bhikkhu, tayā pubbe samaṇabrāhmaṇā lokasmiṃ yāvatakaṃ tuyhaṃ kasiṇaṃ āyu tāvatakaṃ tesaṃ tapokammameva ahosi. te kho evaṃ jāneyyuṃ – ‘santañca panaññaṃ uttari nissaraṇaṃ atthaññaṃ uttari nissaraṇanti, asantaṃ vā aññaṃ uttari nissaraṇaṃ natthaññaṃ uttari nissaraṇa’nti. taṃ tāhaṃ, bhikkhu, evaṃ vadāmi – ‘na cevaññaṃ uttari nissaraṇaṃ dakkhissasi, yāvadeva ca pana kilamathassa vighātassa bhāgī bhavissasi. sace kho tvaṃ, bhikkhu, pathaviṃ ajjhosissasi, opasāyiko me bhavissasi vatthusāyiko, yathākāmakaraṇīyo bāhiteyyo. sace āpaṃ… tejaṃ… vāyaṃ… bhūte… deve… pajāpatiṃ… brahmaṃ ajjhosissasi, opasāyiko me bhavissasi vatthusāyiko, yathākāmakaraṇīyo bāhiteyyo’ti.</p>\r\n<p class="bodytext">ahampi kho evaṃ, brahme, jānāmi – sace pathaviṃ ajjhosissāmi, opasāyiko te bhavissāmi vatthusāyiko, yathākāmakaraṇīyo <a name="T1.0594"></a> bāhiteyyo. ‘sace āpaṃ… tejaṃ… vāyaṃ… bhūte… deve… pajāpatiṃ… brahmaṃ ajjhosissāmi, opasāyiko te bhavissāmi vatthusāyiko, yathākāmakaraṇīyo bāhiteyyo’ti api ca te ahaṃ, brahme, gatiñca pajānāmi, jutiñca pajānāmi – evaṃ mahiddhiko bako brahmā, evaṃ mahānubhāvo bako brahmā, evaṃ mahesakkho bako brahmā"ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-503-');
INSERT INTO pages VALUES(3443,'mula_ma_01',404,replace(replace('<p class="bodytext">yathākathaṃ <a name="M1.0404"></a> pana me tvaṃ, mārisa, gatiñca pajānāsi, jutiñca pajānāsi – ‘evaṃ mahiddhiko bako brahmā, evaṃ mahānubhāvo bako brahmā, evaṃ mahesakkho bako brahmā’ti?</p>\r\n<p class="gatha1">yāvatā candimasūriyā, pariharanti disā bhanti virocanā,</p>\r\n<p class="gathalast">tāva sahassadhā loko, ettha te vattate <span class="note">[vattatī (sī. syā. kaṃ. pī.)]</span> vaso.</p>\r\n<p class="gatha1">paroparañca <a name="V1.0412"></a> <span class="note">[parovarañca (sī. pī.)]</span> jānāsi, atho rāgavirāginaṃ,</p>\r\n<p class="gathalast">itthabhāvaññathābhāvaṃ, sattānaṃ āgatiṃ gati"nti.</p>\r\n<p class="bodytext">evaṃ kho te ahaṃ, brahme, gatiñca pajānāmi jutiñca pajānāmi – ‘evaṃ mahiddhiko bako brahmā, evaṃ mahānubhāvo bako <a name="P1.0329"></a> brahmā, evaṃ mahesakkho bako brahmā’ti.</p>\r\n<p class="bodytext"><a name="para504"></a><a name="para504_mn1"></a><span class="paranum">504</span>. atthi kho, brahme, añño kāyo, taṃ tvaṃ na jānāsi na passasi, tamahaṃ jānāmi passāmi. atthi kho, brahme, ābhassarā nāma kāyo yato tvaṃ cuto idhūpapanno. tassa te aticiranivāsena sā sati pamuṭṭhā, tena taṃ tvaṃ na jānāsi na passasi, tamahaṃ jānāmi passāmi. evampi kho ahaṃ, brahme, neva te samasamo abhiññāya, kuto nīceyyaṃ? atha <a name="T1.0595"></a> kho ahameva tayā bhiyyo. atthi kho, brahme, subhakiṇho nāma kāyo, vehapphalo nāma kāyo, abhibhū nāma kāyo, taṃ tvaṃ na jānāsi na passasi, tamahaṃ jānāmi passāmi. evampi kho ahaṃ, brahme, neva te samasamo abhiññāya, kuto nīceyyaṃ? atha kho ahameva tayā bhiyyo. pathaviṃ kho ahaṃ, brahme, pathavito abhiññāya yāvatā pathaviyā pathavattena ananubhūtaṃ tadabhiññāya pathaviṃ nāpahosiṃ, pathaviyā nāpahosiṃ, pathavito nāpahosiṃ, pathaviṃ meti nāpahosiṃ, pathaviṃ nābhivadiṃ. evampi kho ahaṃ, brahme, neva te samasamo abhiññāya, kuto nīceyyaṃ? atha kho ahameva tayā bhiyyo. āpaṃ kho ahaṃ, brahme…pe… tejaṃ kho ahaṃ, brahme…pe… vāyaṃ kho ahaṃ, brahme…pe… bhūte kho ahaṃ, brahme…pe… deve kho ahaṃ, brahme…pe… pajāpatiṃ kho ahaṃ, brahme…pe… brahmaṃ kho ahaṃ, brahme…pe… ābhassare kho ahaṃ, brahme…pe… subhakiṇhe kho ahaṃ,</p>\r\n','\r',char(13)),'\n',char(10)),'-504-');
INSERT INTO pages VALUES(3444,'mula_ma_01',405,replace(replace('<p class="bodytext">brahme… <a name="M1.0405"></a> …pe… vehapphale kho ahaṃ, brahme…pe… abhibhuṃ kho ahaṃ, brahme…pe… sabbaṃ kho ahaṃ, brahme, sabbato abhiññāya yāvatā sabbassa sabbattena ananubhūtaṃ tadabhiññāya sabbaṃ nāpahosiṃ sabbasmiṃ nāpahosiṃ sabbato nāpahosiṃ sabbaṃ meti nāpahosiṃ, sabbaṃ nābhivadiṃ. evampi kho ahaṃ, brahme, neva te samasamo abhiññāya, kuto nīceyyaṃ? atha kho ahameva tayā bhiyyo"ti.</p>\r\n<p class="bodytext">sace <a name="V1.0413"></a> kho, mārisa, sabbassa sabbattena ananubhūtaṃ, tadabhiññāya mā heva te rittakameva ahosi, tucchakameva ahosī"ti<a name="T1.0596"></a>.</p>\r\n<p class="bodytext">‘viññāṇaṃ anidassanaṃ anantaṃ sabbato pabhaṃ’, taṃ pathaviyā pathavattena ananubhūtaṃ, āpassa āpattena ananubhūtaṃ, tejassa tejattena ananubhūtaṃ, vāyassa vāyattena ananubhūtaṃ, bhūtānaṃ bhūtattena ananubhūtaṃ, devānaṃ devattena ananubhūtaṃ, pajāpatissa pajāpatittena ananubhūtaṃ, brahmānaṃ brahmattena ananubhūtaṃ, ābhassarānaṃ ābhassarattena ananubhūtaṃ, subhakiṇhānaṃ subhakiṇhattena ananubhūtaṃ, vehapphalānaṃ vehapphalatte <a name="P1.0330"></a> ananubhūtaṃ, abhibhussa abhibhuttena ananubhūtaṃ, sabbassa sabbattena ananubhūtaṃ".</p>\r\n<p class="bodytext">handa carahi <span class="note">[handa ca hi (sī. pī.)]</span> te, mārisa, passa antaradhāyāmī"ti. ‘handa carahi me tvaṃ, brahme, antaradhāyassu, sace visahasī’ti. atha kho, bhikkhave, bako brahmā ‘antaradhāyissāmi samaṇassa gotamassa, antaradhāyissāmi samaṇassa gotamassā’ti nevassu me sakkoti antaradhāyituṃ.</p>\r\n<p class="bodytext">evaṃ vutte, ahaṃ, bhikkhave, bakaṃ brahmānaṃ etadavocaṃ – ‘handa carahi te brahme antaradhāyāmī’ti. ‘handa carahi me tvaṃ, mārisa, antaradhāyassu sace visahasī’ti. atha kho ahaṃ, bhikkhave, tathārūpaṃ iddhābhisaṅkhāraṃ abhisaṅkhāsiṃ – ‘ettāvatā brahmā ca brahmaparisā ca brahmapārisajjā ca saddañca me sossanti <span class="note">[saddameva suyyanti (ka.)]</span>, na ca maṃ dakkhantī’ti. antarahito imaṃ gāthaṃ abhāsiṃ –</p>\r\n<p class="gatha1">bhavevāhaṃ bhayaṃ disvā, bhavañca vibhavesinaṃ,</p>\r\n<p class="gathalast">bhavaṃ nābhivadiṃ kiñci, nandiñca na upādiyi"nti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3445,'mula_ma_01',406,replace(replace('<p class="bodytext">atha <a name="T1.0597"></a><a name="M1.0406"></a> kho, bhikkhave, brahmā ca brahmaparisā ca brahmapārisajjā ca acchariyabbhutacittajātā ahesuṃ – ‘acchariyaṃ vata bho, abbhutaṃ vata bho! samaṇassa gotamassa mahiddhikatā mahānubhāvatā, na ca vata no ito pubbe diṭṭho vā, suto vā, añño <a name="V1.0414"></a> samaṇo vā brāhmaṇo vā evaṃ mahiddhiko evaṃ mahānubhāvo yathāyaṃ samaṇo gotamo sakyaputto sakyakulā pabbajito. bhavarāmāya vata, bho, pajāya bhavaratāya bhavasammuditāya samūlaṃ bhavaṃ udabbahī’ti.</p>\r\n<p class="bodytext"><a name="para505"></a><a name="para505_mn1"></a><span class="paranum">505</span>. atha kho, bhikkhave, māro pāpimā aññataraṃ brahmapārisajjaṃ anvāvisitvā maṃ etadavoca – ‘sace kho tvaṃ, mārisa, evaṃ pajānāsi, sace tvaṃ evaṃ anubuddho, mā sāvake upanesi, mā pabbajite, mā sāvakānaṃ dhammaṃ desesi, mā pabbajitānaṃ, mā sāvakesu gedhimakāsi, mā pabbajitesu. ahesuṃ kho, bhikkhu, tayā pubbe samaṇabrāhmaṇā lokasmiṃ arahanto sammāsambuddhā paṭijānamānā<a name="P1.0331"></a>. te sāvake upanesuṃ pabbajite, sāvakānaṃ dhammaṃ desesuṃ pabbajitānaṃ, sāvakesu gedhimakaṃsu pabbajitesu, te sāvake upanetvā pabbajite, sāvakānaṃ dhammaṃ desetvā pabbajitānaṃ, sāvakesu gedhitacittā pabbajitesu, kāyassa bhedā pāṇupacchedā hīne kāye patiṭṭhitā. ahesuṃ ye pana, bhikkhu, tayā pubbe samaṇabrāhmaṇā lokasmiṃ arahanto sammāsambuddhā paṭijānamānā. te na sāvake upanesuṃ na pabbajite, na sāvakānaṃ dhammaṃ desesuṃ na pabbajitānaṃ, na sāvakesu <a name="T1.0598"></a> gedhimakaṃsu na pabbajitesu, te na sāvake upanetvā na pabbajite, na sāvakānaṃ dhammaṃ desetvā na pabbajitānaṃ, na sāvakesu gedhitacittā na pabbajitesu, kāyassa bhedā pāṇupacchedā paṇīte kāye patiṭṭhitā. taṃ tāhaṃ, bhikkhu, evaṃ vadāmi – iṅgha tvaṃ, mārisa, appossukko diṭṭhadhammasukhavihāramanuyutto viharassu, anakkhātaṃ kusalañhi, mārisa, mā paraṃ ovadāhī’ti.</p>\r\n<p class="bodytext">evaṃ vutte, ahaṃ, bhikkhave, māraṃ pāpimantaṃ etadavocaṃ – ‘jānāmi kho tāhaṃ, pāpima, mā tvaṃ maññittho – na maṃ jānātī’ti. māro tvamasi, pāpima. na maṃ tvaṃ, pāpima, hitānukampī evaṃ vadesi, ahitānukampī maṃ tvaṃ, pāpima, evaṃ vadesi. tuyhañhi, pāpima, evaṃ hoti – ‘yesaṃ samaṇo gotamo dhammaṃ desessati, te me visayaṃ upātivattissantī’ti. asammāsambuddhāva pana</p>\r\n','\r',char(13)),'\n',char(10)),'-505-');
INSERT INTO pages VALUES(3446,'mula_ma_01',407,replace(replace('<p class="bodytext">te<a name="M1.0407"></a>, pāpima, samānā sammāsambuddhāmhāti paṭijāniṃsu. ahaṃ kho pana, pāpima, sammāsambuddhova samāno sammāsambuddhomhīti paṭijānāmi. desentopi hi, pāpima, tathāgato sāvakānaṃ dhammaṃ tādisova adesentopi hi, pāpima, tathāgato sāvakānaṃ dhammaṃ tādisova. upanentopi hi, pāpima, tathāgato sāvake <a name="V1.0415"></a> tādisova, anupanentopi hi, pāpima, tathāgato sāvake tādisova. taṃ kissa hetu? tathāgatassa, pāpima, ye āsavā saṃkilesikā ponobbhavikā sadarā dukkhavipākā āyatiṃ jātijarāmaraṇiyā – te pahīnā ucchinnamūlā tālāvatthukatā anabhāvaṃkatā āyatiṃ <a name="T1.0599"></a> anuppādadhammā. seyyathāpi, pāpima, tālo matthakacchinno abhabbo puna virūḷhiyā, evameva kho, pāpima, tathāgatassa ye āsavā saṃkilesikā ponobbhavikā sadarā dukkhavipākā āyatiṃ jātijarāmaraṇiyā – te pahīnā ucchinnamūlā tālāvatthukatā anabhāvaṃkatā āyatiṃ anuppādadhammāti.</p>\r\n<p class="bodytext">iti hidaṃ mārassa ca anālapanatāya brahmuno ca abhinimantanatāya, tasmā imassa veyyākaraṇassa brahmanimantanikaṃteva adhivacana"nti.</p>\r\n<p class="centered">brahmanimantanikasuttaṃ niṭṭhitaṃ navamaṃ.</p>\r\n<p class="subhead">10. māratajjanīyasuttaṃ</p>\r\n<p class="bodytext"><a name="para506"></a><a name="para506_mn1"></a><span class="paranum">506</span>. evaṃ <a name="T1.0600"></a><a name="V1.0416"></a><a name="P1.0332"></a> me sutaṃ – ekaṃ samayaṃ āyasmā mahāmoggallāno bhaggesu viharati susumāragire bhesakaḷāvane migadāye. tena kho pana samayena āyasmā mahāmoggallāno abbhokāse caṅkamati. tena kho pana samayena māro pāpimā āyasmato mahāmoggallānassa kucchigato hoti koṭṭhamanupaviṭṭho. atha kho āyasmato mahāmoggallānassa etadahosi – "kiṃ nu kho me kucchi garugaro viya <span class="note">[garu garu viya (sī. pī. ṭīkāyaṃ pāṭhantaraṃ)]</span>? māsācitaṃ maññe"ti. atha kho āyasmā mahāmoggallāno caṅkamā orohitvā vihāraṃ pavisitvā paññatte āsane nisīdi. nisajja kho āyasmā mahāmoggallāno paccattaṃ yoniso manasākāsi. addasā kho āyasmā mahāmoggallāno māraṃ pāpimantaṃ kucchigataṃ koṭṭhamanupaviṭṭhaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-506-');
INSERT INTO pages VALUES(3447,'mula_ma_01',408,replace(replace('<p class="bodytext">disvāna <a name="M1.0408"></a> māraṃ pāpimantaṃ etadavoca – "nikkhama, pāpima, nikkhama, pāpima! mā tathāgataṃ vihesesi, mā tathāgatasāvakaṃ. mā te ahosi dīgharattaṃ ahitāya dukkhāyā"ti. atha kho mārassa pāpimato etadahosi – "ajānameva kho maṃ ayaṃ samaṇo apassaṃ evamāha – ‘nikkhama, pāpima, nikkhama, pāpima! mā tathāgataṃ vihesesi, mā tathāgatasāvakaṃ. mā te ahosi dīgharattaṃ ahitāya dukkhāyā’ti. yopissa so satthā sopi maṃ neva khippaṃ jāneyya, kuto pana <span class="note">[kuto ca pana (syā.)]</span> maṃ ayaṃ sāvako jānissatī"ti? atha kho āyasmā mahāmoggallāno <a name="T1.0601"></a> māraṃ pāpimantaṃ etadavoca – "evampi kho tāhaṃ, pāpima, jānāmi, mā tvaṃ maññittho – ‘na maṃ jānātī’ti. māro tvamasi, pāpima, tuyhañhi, pāpima, evaṃ hoti – ‘ajānameva kho maṃ ayaṃ samaṇo apassaṃ evamāha – nikkhama, pāpima, nikkhama, pāpima! mā tathāgataṃ vihesesi, mā tathāgatasāvakaṃ. mā te ahosi dīgharattaṃ ahitāya dukkhāyāti. yopissa so satthā sopi maṃ neva khippaṃ jāneyya, kuto pana maṃ ayaṃ sāvako jānissatī"’ti?</p>\r\n<p class="bodytext">atha kho mārassa pāpimato etadahosi – "jāname kho maṃ ayaṃ samaṇo passaṃ evamāha – ‘nikkhama, pāpima, nikkhama, pāpima! mā tathāgataṃ vihesesi, mā tathāgatasāvakaṃ. mā te ahosi dīgharattaṃ ahitāya dukkhāyā"’ti. atha kho māro pāpimā āyasmato <a name="P1.0333"></a> mahāmoggallānassa mukhato uggantvā paccaggaḷe aṭṭhāsi.</p>\r\n<p class="bodytext"><a name="para507"></a><a name="para507_mn1"></a><span class="paranum">507</span>. addasā <a name="V1.0417"></a> kho āyasmā mahāmoggallāno māraṃ pāpimantaṃ paccaggaḷe ṭhitaṃ, disvāna māraṃ pāpimantaṃ etadavoca – ‘etthāpi kho tāhaṃ, pāpima, passāmi, mā tvaṃ maññittho "na maṃ passatī"ti. eso tvaṃ, pāpima, paccaggaḷe ṭhito. bhūtapubbāhaṃ, pāpima, dūsī nāma māro ahosiṃ, tassa me kāḷī nāma bhaginī. tassā tvaṃ putto. so me tvaṃ bhāgineyyo ahosi. tena kho pana, pāpima, samayena kakusandho bhagavā arahaṃ sammāsambuddho loke uppanno hoti. kakusandhassa kho pana, pāpima, bhagavato arahato <a name="T1.0602"></a> sammāsambuddhassa vidhurasañjīvaṃ nāma sāvakayugaṃ ahosi aggaṃ bhaddayugaṃ. yāvatā kho pana, pāpima, kakusandhassa</p>\r\n','\r',char(13)),'\n',char(10)),'-507-');
INSERT INTO pages VALUES(3448,'mula_ma_01',409,replace(replace('<p class="bodytext">bhagavato <a name="M1.0409"></a> arahato sammāsambuddhassa sāvakā. tesu na ca koci āyasmatā vidhurena samasamo hoti yadidaṃ dhammadesanāya. iminā kho evaṃ <span class="note">[etaṃ (sī. syā. pī.)]</span>, pāpima, pariyāyena āyasmato vidhurassa vidhuroteva <span class="note">[vidhurassa vidhuro vidhurotveva (sī. syā. kaṃ. pī.)]</span> samaññā udapādi.</p>\r\n<p class="bodytext">āyasmā pana, pāpima, sañjīvo araññagatopi rukkhamūlagatopi suññāgāragatopi appakasireneva saññāvedayitanirodhaṃ samāpajjati. bhūtapubbaṃ, pāpima, āyasmā sañjīvo aññatarasmiṃ rukkhamūle saññāvedayitanirodhaṃ samāpanno nisinno hoti. addasaṃsu kho, pāpima, gopālakā pasupālakā kassakā pathāvino āyasmantaṃ sañjīvaṃ aññatarasmiṃ rukkhamūle saññāvedayitanirodhaṃ samāpannaṃ nisinnaṃ, disvāna tesaṃ etadahosi – ‘acchariyaṃ vata, bho, abbhutaṃ vata, bho! ayaṃ samaṇo nisinnakova kālaṅkato! handa naṃ dahāmā’ti. atha kho te, pāpima, gopālakā pasupālakā kassakā pathāvino tiṇañca kaṭṭhañca gomayañca saṃkaḍḍhitvā āyasmato sañjīvassa kāye upacinitvā aggiṃ datvā pakkamiṃsu. atha kho, pāpima, āyasmā sañjīvo tassā rattiyā accayena tāya samāpattiyā vuṭṭhahitvā cīvarāni papphoṭetvā pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya gāmaṃ piṇḍāya pāvisi. addasaṃsu kho te, pāpima, gopālakā pasupālakā kassakā pathāvino āyasmantaṃ sañjīvaṃ piṇḍāya carantaṃ, disvāna nesaṃ <a name="T1.0603"></a> etadahosi – ‘acchariyaṃ vata, bho, abbhutaṃ vata, bho! ayaṃ samaṇo nisinnakova kālaṅkato, svāyaṃ paṭisañjīvito’ti<a name="P1.0334"></a>. iminā kho evaṃ, pāpima, pariyāyena āyasmato sañjīvassa sañjīvoteva <span class="note">[sañjīvo sañjīvotveva (sī. syā. kaṃ. pī.)]</span> samaññā udapādi.</p>\r\n<p class="bodytext"><a name="para508"></a><a name="para508_mn1"></a><span class="paranum">508</span>. atha kho, pāpima, dūsissa mārassa etadahosi – ‘imesaṃ kho ahaṃ bhikkhūnaṃ sīlavantānaṃ kalyāṇadhammānaṃ neva jānāmi āgatiṃ vā gatiṃ vā. yaṃnūnāhaṃ brāhmaṇagahapatike anvāviseyyaṃ <a name="V1.0418"></a> – etha, tumhe bhikkhū sīlavante kalyāṇadhamme akkosatha paribhāsatha rosetha vihesetha. appeva nāma tumhehi akkosiyamānānaṃ paribhāsiyamānānaṃ rosiyamānānaṃ vihesiyamānānaṃ siyā cittassa aññathattaṃ, yathā taṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-508-');
INSERT INTO pages VALUES(3449,'mula_ma_01',410,replace(replace('<p class="bodytext">dūsī <a name="M1.0410"></a> māro labhetha otāra’nti. atha kho te, pāpima, dūsī māro brāhmaṇagahapatike anvāvisi – ‘etha, tumhe bhikkhū sīlavante kalyāṇadhamme akkosatha paribhāsatha rosetha vihesetha. appeva nāma tumhehi akkosiyamānānaṃ paribhāsiyamānānaṃ rosiyamānānaṃ vihesiyamānānaṃ siyā cittassa aññathattaṃ, yathā taṃ dūsī māro labhetha otāra’nti.</p>\r\n<p class="bodytext">atha kho te, pāpima, brāhmaṇagahapatikā anvāvisiṭṭhā dūsinā mārena bhikkhū sīlavante kalyāṇadhamme akkosanti paribhāsanti rosenti vihesenti – ‘ime pana muṇḍakā samaṇakā ibbhā kiṇhā <span class="note">[kaṇhā (syā. kaṃ. ka.)]</span> bandhupādāpaccā "jhāyinosmā jhāyinosmā"ti pattakkhandhā adhomukhā madhurakajātā jhāyanti pajjhāyanti nijjhāyanti apajjhāyanti. seyyathāpi nāma ulūko <a name="T1.0604"></a> rukkhasākhāyaṃ mūsikaṃ maggayamāno jhāyati pajjhāyati nijjhāyati apajjhāyati, evamevime muṇḍakā samaṇakā ibbhā kiṇhā bandhupādāpaccā "jhāyinosmā jhāyinosmā"ti pattakkhandhā adhomukhā madhurakajātā jhāyanti pajjhāyanti nijjhāyanti apajjhāyanti. seyyathāpi nāma kotthu nadītīre macche maggayamāno jhāyati pajjhāyati nijjhāyati apajjhāyati, evamevime muṇḍakā samaṇakā ibbhā kiṇhā bandhupādāpaccā "jhāyinosmā jhāyinosmā"ti pattakkhandhā adhomukhā madhurakajātā jhāyanti pajjhāyanti nijjhāyanti apajjhāyanti. seyyathāpi nāma biḷāro sandhisamalasaṅkaṭīre mūsikaṃ maggayamāno jhāyati pajjhāyati nijjhāyati apajjhāyati, evamevime muṇḍakā samaṇakā ibbhā kiṇhā bandhupādāpaccā "jhāyinosmā jhāyinosmā"ti pattakkhandhā adhomukhā madhurakajātā jhāyanti pajjhāyanti nijjhāyanti apajjhāyanti. seyyathāpi nāma gadrabho vahacchinno sandhisamalasaṅkaṭīre jhāyati pajjhāyati nijjhāyati apajjhāyati, evamevime muṇḍakā samaṇakā ibbhā kiṇhā bandhupādāpaccā "jhāyinosmā jhāyinosmā"ti pattakkhandhā adhomukhā madhurakajātā jhāyanti pajjhāyanti nijjhāyanti apajjhāyantī"ti.</p>\r\n<p class="bodytext">ye kho pana, pāpima, tena samayena manussā kālaṅkaronti yebhuyyena kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3450,'mula_ma_01',411,replace(replace('<p class="bodytext"><a name="para509"></a><a name="para509_mn1"></a><span class="paranum">509</span>. atha <a name="M1.0411"></a><a name="V1.0419"></a><a name="P1.0335"></a> kho, pāpima, kakusandho bhagavā arahaṃ sammāsambuddho bhikkhū <a name="T1.0605"></a> āmantesi – ‘anvāviṭṭhā kho, bhikkhave, brāhmaṇagahapatikā dūsinā mārena – etha, tumhe bhikkhū sīlavante kalyāṇadhamme akkosatha paribhāsatha rosetha vihesetha, appeva nāma tumhehi akkosiyamānānaṃ paribhāsiyamānānaṃ rosiyamānānaṃ vihesiyamānānaṃ siyā cittassa aññathattaṃ, yathā taṃ dūsī māro labhetha otāra’nti. etha, tumhe, bhikkhave, mettāsahagatena cetasā ekaṃ disaṃ pharitvā viharatha, tathā dutiyaṃ, tathā tatiyaṃ, tathā catutthaṃ. iti uddhamadho tiriyaṃ sabbadhi sabbattatāya sabbāvantaṃ lokaṃ mettāsahagatena cetasā vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā viharatha. karuṇāsahagatena cetasā…pe… muditāsahagatena cetasā…pe… upekkhāsahagatena cetasā ekaṃ disaṃ pharitvā viharatha, tathā dutiyaṃ, tathā tatiyaṃ, tathā catutthaṃ. iti uddhamadho tiriyaṃ sabbadhi sabbattatāya sabbāvantaṃ lokaṃ upekkhāsahagatena cetasā vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā viharathā’ti.</p>\r\n<p class="bodytext">atha kho te, pāpima, bhikkhū kakusandhena bhagavatā arahatā sammāsambuddhena evaṃ ovadiyamānā evaṃ anusāsiyamānā araññagatāpi rukkhamūlagatāpi suññāgāragatāpi mettāsahagatena cetasā ekaṃ disaṃ pharitvā vihariṃsu, tathā dutiyaṃ, tathā tatiyaṃ, tathā catutthaṃ. iti uddhamadho tiriyaṃ sabbadhi sabbattatāya sabbāvantaṃ lokaṃ mettāsahagatena cetasā vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā vihariṃsu. karuṇāsahagatena cetasā…pe… muditāsahagatena cetasā…pe… upekkhāsahagatena cetasā ekaṃ disaṃ pharitvā vihariṃsu, tathā dutiyaṃ, tathā tatiyaṃ, tathā catutthaṃ. iti uddhamadho tiriyaṃ sabbadhi sabbattatāya sabbāvantaṃ lokaṃ upekkhāsahagatena cetasā vipulena mahaggatena appamāṇena averena abyābajjhena pharitvā vihariṃsu<a name="T1.0606"></a>.</p>\r\n<p class="bodytext"><a name="para510"></a><a name="para510_mn1"></a><span class="paranum">510</span>. atha kho, pāpima, dūsissa mārassa etadahosi – ‘evampi kho ahaṃ karonto imesaṃ bhikkhūnaṃ sīlavantānaṃ kalyāṇadhammānaṃ neva jānāmi āgatiṃ vā gatiṃ vā, yaṃnūnāhaṃ brāhmaṇagahapatike anvāviseyyaṃ – etha, tumhe bhikkhū sīlavante kalyāṇadhamme sakkarotha garuṃ karotha</p>\r\n','\r',char(13)),'\n',char(10)),'-509-510-');
INSERT INTO pages VALUES(3451,'mula_ma_01',412,replace(replace('<p class="bodytext">mānetha <a name="M1.0412"></a> pūjetha<a name="P1.0336"></a>, appeva nāma tumhehi sakkariyamānānaṃ garukariyamānānaṃ māniyamānānaṃ pūjiyamānānaṃ siyā cittassa aññathattaṃ, yathā taṃ dūsī māro labhetha otāra’nti<a name="V1.0420"></a>. atha kho te, pāpima, dūsī māro brāhmaṇagahapatike anvāvisi – ‘etha, tumhe bhikkhū sīlavante kalyāṇadhamme sakkarotha garuṃ karotha mānetha pūjetha, appeva nāma tumhehi sakkariyamānānaṃ garukariyamānānaṃ māniyamānānaṃ pūjiyamānānaṃ siyā cittassa aññathattaṃ, yathā taṃ dūsī māro labhetha otāra’nti. atha kho te, pāpima, brāhmaṇagahapatikā anvāviṭṭhā dūsinā mārena bhikkhū sīlavante kalyāṇadhamme sakkaronti garuṃ karonti mānenti pūjenti.</p>\r\n<p class="bodytext">ye kho pana, pāpima, tena samayena manussā kālaṅkaronti yebhuyyena kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjanti.</p>\r\n<p class="bodytext"><a name="para511"></a><a name="para511_mn1"></a><span class="paranum">511</span>. atha <a name="T1.0607"></a> kho, pāpima, kakusandho bhagavā arahaṃ sammāsambuddho bhikkhū āmantesi – ‘anvāviṭṭhā kho, bhikkhave, brāhmaṇagahapatikā dūsinā mārena – etha, tumhe bhikkhū sīlavante kalyāṇadhamme sakkarotha garuṃ karotha mānetha pūjetha, appeva nāma tumhehi sakkariyamānānaṃ garukariyamānānaṃ māniyamānānaṃ pūjiyamānānaṃ siyā cittassa aññathattaṃ, yathā taṃ dūsī māro labhetha otāranti. etha, tumhe, bhikkhave, asubhānupassino kāye viharatha, āhāre paṭikūlasaññino, sabbaloke anabhiratisaññino <span class="note">[anabhiratasaññīno (sī. syā. kaṃ. pī.)]</span>, sabbasaṅkhāresu aniccānupassino’ti.</p>\r\n<p class="bodytext">atha kho te, pāpima, bhikkhū kakusandhena bhagavatā arahatā sammāsambuddhena evaṃ ovadiyamānā evaṃ anusāsiyamānā araññagatāpi rukkhamūlagatāpi suññāgāragatāpi asubhānupassino kāye vihariṃsu, āhāre paṭikūlasaññino, sabbaloke anabhiratisaññino, sabbasaṅkhāresu aniccānupassino.</p>\r\n<p class="bodytext"><a name="para512"></a><a name="para512_mn1"></a><span class="paranum">512</span>. atha kho, pāpima, kakusandho bhagavā arahaṃ sammāsambuddho pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya āyasmatā vidhurena pacchāsamaṇena gāmaṃ piṇḍāya pāvisi. atha kho, pāpima, dūsī māro aññataraṃ</p>\r\n','\r',char(13)),'\n',char(10)),'-511-512-');
INSERT INTO pages VALUES(3452,'mula_ma_01',413,replace(replace('<p class="bodytext">kumārakaṃ <a name="M1.0413"></a> <span class="note">[kumāraṃ (sī. pī.)]</span> anvāvisitvā sakkharaṃ gahetvā āyasmato vidhurassa sīse pahāramadāsi, sīsaṃ vobhindi <span class="note">[sīsaṃ te bhindissāmīti (ka.)]</span>. atha kho, pāpima, āyasmā vidhuro bhinnena sīsena lohitena gaḷantena kakusandhaṃyeva bhagavantaṃ <a name="P1.0337"></a> arahantaṃ sammāsambuddhaṃ piṭṭhito piṭṭhito anubandhi. atha kho<a name="T1.0608"></a>, pāpima, kakusandho bhagavā arahaṃ sammāsambuddho nāgāpalokitaṃ apalokesi <a name="V1.0421"></a> – ‘na vāyaṃ dūsī māro mattamaññāsī’ti. sahāpalokanāya ca pana, pāpima, dūsī māro tamhā ca ṭhānā cavi mahānirayañca upapajji.</p>\r\n<p class="bodytext">tassa kho pana, pāpima, mahānirayassa tayo nāmadheyyā honti – chaphassāyataniko itipi, saṅkusamāhato itipi, paccattavedaniyo itipi. atha kho maṃ, pāpima, nirayapālā upasaṅkamitvā etadavocuṃ – yadā kho te <span class="note">[yato te (ka.)]</span>, mārisa, saṅkunā saṅku hadaye samāgaccheyya. atha naṃ tvaṃ jāneyyāsi – ‘vassasahassaṃ me niraye paccamānassā’ti. so kho ahaṃ, pāpima, bahūni vassāni bahūni vassasatāni bahūni vassasahassāni tasmiṃ mahāniraye apacciṃ. dasavassasahassāni tasseva mahānirayassa ussade apacciṃ vuṭṭhānimaṃ nāma vedanaṃ vediyamāno. tassa mayhaṃ, pāpima, evarūpo kāyo hoti, seyyathāpi manussassa. evarūpaṃ sīsaṃ hoti, seyyathāpi macchassa.</p>\r\n<p class="hangnum"><a name="para513"></a><a name="para513_mn1"></a><span class="paranum">513</span>.</p>\r\n<p class="gatha1"> "kīdiso nirayo āsi, yattha dūsī apaccatha,</p>\r\n<p class="gathalast">vidhuraṃ sāvakamāsajja, kakusandhañca brāhmaṇaṃ.</p>\r\n<p class="gatha1">sataṃ āsi ayosaṅkū, sabbe paccattavedanā,</p>\r\n<p class="gatha2">īdiso nirayo āsi, yattha dūsī apaccatha,</p>\r\n<p class="gathalast">vidhuraṃ sāvakamāsajja, kakusandhañca brāhmaṇaṃ.</p>\r\n<p class="gatha1">yo etamabhijānāti, bhikkhu buddhassa sāvako,</p>\r\n<p class="gathalast">tādisaṃ <a name="T1.0609"></a> bhikkhumāsajja, kaṇha dukkhaṃ nigacchasi.</p>\r\n<p class="gatha1">majjhe sarassa tiṭṭhanti, vimānā kappaṭṭhāyino,</p>\r\n<p class="gatha2">veḷuriyavaṇṇā rucirā, accimanto pabhassarā,</p>\r\n<p class="gathalast">accharā tattha naccanti, puthu nānattavaṇṇiyo.</p>\r\n','\r',char(13)),'\n',char(10)),'-513-');
INSERT INTO pages VALUES(3453,'mula_ma_01',414,replace(replace('<p class="gatha1">yo <a name="M1.0414"></a><a name="V1.0422"></a><a name="P1.0338"></a> etamabhijānāti, bhikkhu buddhassa sāvako,</p>\r\n<p class="gathalast">tādisaṃ bhikkhumāsajja, kaṇha dukkhaṃ nigacchasi.</p>\r\n<p class="gatha1">yo ve buddhena codito, bhikkhu saṅghassa pekkhato,</p>\r\n<p class="gathalast">migāramātupāsādaṃ, pādaṅguṭṭhena kampayi.</p>\r\n<p class="gatha1">yo etamabhijānāti, bhikkhu buddhassa sāvako,</p>\r\n<p class="gathalast">tādisaṃ bhikkhumāsajja, kaṇha dukkhaṃ nigacchasi.</p>\r\n<p class="gatha1">yo vejayantaṃ pāsādaṃ, pādaṅguṭṭhena kampayi,</p>\r\n<p class="gathalast">iddhibalenupatthaddho, saṃvejesi ca devatā.</p>\r\n<p class="gatha1">yo etamabhijānāti, bhikkhu buddhassa sāvako,</p>\r\n<p class="gathalast">tādisaṃ bhikkhumāsajja, kaṇha dukkhaṃ nigacchasi.</p>\r\n<p class="gatha1">yo vejayantapāsāde, sakkaṃ so paripucchati,</p>\r\n<p class="gatha2">api vāsava jānāsi, taṇhākkhayavimuttiyo,</p>\r\n<p class="gathalast">tassa sakko viyākāsi, pañhaṃ puṭṭho yathātathaṃ.</p>\r\n<p class="gatha1">yo etamabhijānāti, bhikkhu buddhassa sāvako,</p>\r\n<p class="gathalast">tādisaṃ bhikkhumāsajja, kaṇha dukkhaṃ nigacchasi.</p>\r\n<p class="gatha1">yo brahmaṃ paripucchati, sudhammāyābhito sabhaṃ,</p>\r\n<p class="gatha2">ajjāpi <a name="T1.0610"></a> tyāvuso diṭṭhi, yā te diṭṭhi pure ahu,</p>\r\n<p class="gathalast">passasi vītivattantaṃ, brahmaloke pabhassaraṃ.</p>\r\n<p class="gatha1">tassa <a name="V1.0423"></a> brahmā viyākāsi, anupubbaṃ yathātathaṃ,</p>\r\n<p class="gathalast">na me mārisa sā diṭṭhi, yā me diṭṭhi pure ahu.</p>\r\n<p class="gatha1">passāmi vītivattantaṃ, brahmaloke pabhassaraṃ,</p>\r\n<p class="gathalast">sohaṃ ajja kathaṃ vajjaṃ, ahaṃ niccomhi sassato.</p>\r\n<p class="gatha1">yo etamabhijānāti, bhikkhu buddhassa sāvako,</p>\r\n<p class="gathalast">tādisaṃ bhikkhumāsajja, kaṇha dukkhaṃ nigacchasi.</p>\r\n<p class="gatha1">yo mahāmeruno kūṭaṃ, vimokkhena aphassayi,</p>\r\n<p class="gathalast">vanaṃ pubbavidehānaṃ, ye ca bhūmisayā narā.</p>\r\n<p class="gatha1">yo etamabhijānāti, bhikkhu buddhassa sāvako,</p>\r\n<p class="gathalast">tādisaṃ bhikkhumāsajja, kaṇha dukkhaṃ nigacchasi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(3454,'mula_ma_01',415,replace(replace('<p class="gatha1">na <a name="M1.0415"></a> ve aggi cetayati <span class="note">[veṭhayati (sī.)]</span>, ‘ahaṃ bālaṃ ḍahāmī’ti,</p>\r\n<p class="gathalast">bālo ca jalitaṃ aggiṃ, āsajja naṃ sa ḍayhati.</p>\r\n<p class="gatha1">evameva tuvaṃ māra, āsajja naṃ tathāgataṃ,</p>\r\n<p class="gathalast">sayaṃ ḍahissasi attānaṃ, bālo aggiṃva saṃphusaṃ.</p>\r\n<p class="gatha1">apuññaṃ pasavī māro, āsajja naṃ tathāgataṃ,</p>\r\n<p class="gathalast">kinnu maññasi pāpima, na me pāpaṃ vipaccati.</p>\r\n<p class="gatha1">karoto cīyati pāpaṃ, cirarattāya antaka,</p>\r\n<p class="gathalast">māra nibbinda buddhamhā, āsaṃ mākāsi bhikkhusu.</p>\r\n<p class="gatha1">iti <a name="T1.0611"></a> māraṃ atajjesi, bhikkhu bhesakaḷāvane,</p>\r\n<p class="gathalast">tato so dummano yakkho, natatthevantaradhāyathā"ti.</p>\r\n<p class="centered">māratajjanīyasuttaṃ niṭṭhitaṃ dasamaṃ.</p>\r\n<p class="centered">cūḷayamakavaggo niṭṭhito pañcamo.</p>\r\n<p class="bodytext">tassuddānaṃ <a name="V1.0424"></a> –</p>\r\n<p class="gatha1">sāleyya verañjaduve ca tuṭṭhi, cūḷamahādhammasamādānañca,</p>\r\n<p class="gathalast">vīmaṃsakā kosambi ca brāhmaṇo, dūsī ca māro dasamo ca vaggo.</p>\r\n<p class="bodytext">idaṃ vaggānamuddānaṃ –</p>\r\n<p class="gatha1">mūlapariyāyo ceva, sīhanādo ca uttamo,</p>\r\n<p class="gathalast">kakaco ceva gosiṅgo, sāleyyo ca ime pañca.</p>\r\n','\r',char(13)),'\n',char(10)),'');