INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="nikaya">Suttantapiṭaka</p>\r\n<p class="book">Majjhimanikāya</p>\r\n<p class="chapter">Mūlapaṇṇāsapāḷi</p>\r\n<p class="subhead">Saṃgāyanassa pucchā vissajjanā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',1,replace(replace('<p class="bodytext">paṭhamamahāsaṃgītikāle <a name="M1.0001"></a> āvuso dhammasaṃgāhakā mahākassapādayo mahātheravarā dīghanikāyaṃ saṃgāyitvā tadanantaraṃ kiṃ nāma pāvacanaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paṭhamamahāsaṃgītikāle bhante dhammasaṃgāhakā mahākassapādayo mahātheravarā dīghanikāyaṃ saṃgāyitvā tadanantaraṃ majjhimaṃ nāma nikāyaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',2,replace(replace('<p class="bodytext">majjhimanikāyo <a name="M1.0002"></a> nāma āvuso mūlapaṇṇāsako majjhima paṇṇāsako uparipaṇṇāsakoti paṇṇāsakavasena tividho, tattha kataraṃ paṇṇāsakaṃ paṭhamaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tīsu bhante paṇṇāsakesu mūlapaṇṇāsakaṃ nāma pāvacanaṃ dhammasaṃgāhakā mahātheravarā paṭhamaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',3,replace(replace('<p class="bodytext">mūlapaṇṇāsakepi <a name="M1.0003"></a> āvuso pañcavaggā paṇṇāsa ca suttāni, tesu kataraṃ vaggaṃ katarañca suttaṃ paṭhamaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> mūlapaṇṇāsake bhante pañcasu vaggesu paṭhamaṃ mūlapariyāyavaggaṃ paṇṇāsakesu ca suttesu paṭhamaṃ mūlapariyāyasuttaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext">Sādhu āvuso mayampi dāni tatoyeva paṭṭhāya saṃgītipubbaṅgamāni pucchāvissajjanakiccāni kātuṃ samārabhāma.</p>\r\n<p class="subhead">Mūlapariyāyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tena āvuso bhagavatā jānatā passatā arahatā sammāsambuddhena mūlapariyāyasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ukkaṭṭhāyaṃ bhante pañcasate brāhmaṇakulā pabbajite ārabbha bhāsitaṃ, pañcasatā bhante brāhmaṇakulā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',4,replace(replace('<p class="bodytext">pabbajitā <a name="M1.0004"></a> bhikkhū pariyattiṃ nissāya mānaṃ uppādesuṃ, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> taṃ panāvuso suttaṃ bhagavatā katihi vārehi katihi ca antogadhapadehi vibhajitvā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',5,replace(replace('<p class="bodytext">taṃ <a name="M1.0005"></a> pana bhante mūlapariyāyasuttaṃ bhagavatā aṭṭhahi ca vārehi catuvīsatiyā ca antogadhapadehi vibhajitvā desitaṃ.</p>\r\n<p class="subhead">Sabbāsavasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> dutiyaṃ panāvuso sabbāsavasuttaṃ bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sabbāsavasuttaṃ pana bhante bhagavatā sāvatthiyaṃ sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',6,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0006"></a> bhagavatā āsavā katihi pakārehi vibhajitvā dassitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sattahi bhante pakārehi vibhajitvā āsavā bhagavatā pakāsitā.</p>\r\n<p class="subhead">Dhammadāyādasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',7,replace(replace('<p class="bodytext">dhammadāyādasuttaṃ <a name="M1.0007"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ, bhagavato ca bhante bhikkhusaṅghassa ca tadā mahālābhasakkāro udapādi, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',9,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0009"></a> dve anusandhayo, tesu paṭhame anusandhimhi kathaṃ bhagavatā bhikkhūnaṃ ovādo dinno.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paṭhame bhante anusandhimhi dhammadāyādā me bhikkhave bhavatha mā āmisadāyādā, atthi me tumhesu anukampā, kinti me sāvakā dhammadāyādā bhaveyyuṃ no āmisadāyādāti evamādinā bhagavatā bhikkhūnaṃ ovādo dinno.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',11,replace(replace('<p class="bodytext">Dhammadāyādā <a name="M1.0011"></a> me bhikkhave bhavatha mā āmisadāyādā, atthi me tumhesu anukampā kinti me sāvakā dhammadāyādā bhaveyyuṃ, no āmisadāyādā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',12,replace(replace('<p class="bodytext">dutiye <a name="M1.0012"></a> panāvuso anusandhimhi āyasmatā sāriputtattherena dhammasenāpatinā kīdisī dhammadesanā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dutiye pana bhante anusandhimhi āyasmatā sāriputtattherena dhammasenāpatinā satthu pavivittassa viharato sāvakānaṃ vivekaṃ ananusikkhataṃ tīhi ṭhānehi gārayutaṃ, anusikkhantānañca tīhi ṭhānehi pāsaṃsataṃ, soḷasa ca pāpake dhamme tesañca pahānāya majjhimā paṭipadā vibhajitvā pakāsitā.</p>\r\n<p class="subhead">Bhayabheravasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',13,replace(replace('<p class="bodytext">bhayabheravasuttaṃ <a name="M1.0013"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante jāṇussoṇiṃ brāhmaṇaṃ ārabbha bhāsitaṃ, jāṇussoṇi bhante brāhmaṇo bhagavantaṃ upasaṅkamitvā etadavoca ‘‘ye me bho gotama kulaputtā bhavantaṃ gotamaṃ uddissa saddhā agārasmā anagāriyaṃ pabbajitā, bhavaṃ tesaṃ gotamo pubbaṅgamo, bhavaṃ</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',14,replace(replace('<p class="bodytext">Tesaṃ <a name="M1.0014"></a> gotamo bahukāro, bhavaṃ tesaṃ gotamo samādapetā, bhoto ca pana gotamassa sā janatā diṭṭhānugatiṃ āpajjatī’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Anaṅgaṇasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',15,replace(replace('<p class="bodytext">anaṅgaṇasuttaṃ <a name="M1.0015"></a> panāvuso kattha kaṃ ārabbha kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>\r\n<p class="subhead">Ākaṅkheyyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ākaṅkheyyasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Vatthasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',16,replace(replace('<p class="bodytext">vatthasuttaṃ <a name="M1.0016"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Sallekhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',17,replace(replace('<p class="bodytext">sallekhasuttaṃ <a name="M1.0017"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ mahācundaṃ ārabbha bhāsitaṃ, āyasmā mahācundo bhante bhagavantaṃ upasaṅkamitvā etadavoca ‘‘yā imā bhante anekavihitā diṭṭhiyo loke uppajjanti, attavādapaṭisaṃyuttā vā lokavādapaṭisaṃyuttā vā, ādimeva nu kho bhante bhikkhuno manasikaroto evametāsaṃ diṭṭhīnaṃ pahānaṃ hoti, evametāsaṃ diṭṭhīnaṃ paṭinissaggo hotī’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',18,replace(replace('<p class="bodytext">tattha <a name="M1.0018"></a> ca āvuso kati pariyāyā kati ca antogadhapadāni bhagavatā vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattha bhante pañca pariyāyā catucattālīsa ca antogadhapadāni bhagavatā vitthārena bhāsitāni.</p>\r\n<p class="subhead">Sammāṭṭhisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',19,replace(replace('<p class="bodytext">sammādiṭṭhisuttaṃ <a name="M1.0019"></a> panāvuso kattha kaṃ ārabbha kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kassa āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="subhead">Mahāsatipaṭṭhānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',20,replace(replace('<p class="bodytext">mahāsatipaṭṭhānasuttaṃ <a name="M1.0020"></a> panāvuso yogāvacarānaṃ bahupakārattā dīghanikāye ca idha cāti dvīsu nikāyesu porāṇakehi saṃgītikārehi dvikkhattuṃ saṃgāyitvā vitthārena patiṭṭhāpitaṃ, taṃ amhehi dīghanikāye yathānuppattavasena pucchitañca vissajjitañca. Tathāpi yogāvacarānaṃ bahupakārattāyeva </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',21,replace(replace('<p class="bodytext">taṃ <a name="M1.0021"></a> idānipi yathānuppattavasena puna pucchissāmi, taṃ panetaṃ āvuso mahāsatipaṭṭhānasuttaṃ bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kurūsu bhante kammāsadhamme nāma kurūnaṃ nigame sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',22,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0022"></a> tattha ānāpānassati kāyānupassanā bhagavatā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha bhikkhave bhikkhu araññagato vā rukkhamūlagato vā suññāgāragato vā nisīdati pallaṅkaṃ ābhujitvā ujuṃ kāyaṃ paṇidhāya parimukhaṃ satiṃ upaṭṭhapetvā so satova assasati, satova passasati, dīghaṃ vā assasanto ‘dīghaṃ assasāmī’ti pajānāti, dīghaṃ vā passasanto ‘dīghaṃ passasāmī’ti pajānāti, evamādinā bhante tattha ānāpānassati kāyānupassanā bhagavatā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',23,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0023"></a> tattha iriyāpathakāyānupassanā bhagavatā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> puna caparaṃ bhikkhave bhikkhu gacchanto vā gacchāmīti pajānāti, ṭhito vā ṭhitomhīti pajānāti, nisinno vā nisinnomhīti pajānāti, sayāno vā sayānomhīti pajānāti, evamādinā bhante bhagavatā iriyāpathakāyānupassanā bhāvanā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',24,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0024"></a> tattha sampajaññakāyānupassanā bhagavatā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> puna caparaṃ bhikkhave bhikkhu abhikkante paṭikkante sampajānakārī hoti, ālokite vilokite sampajānakārī hoti, samiñjite pasārite sampajānakārī hoti, saṅghāṭipattacīvaradhāraṇe sampajānakārī hoti, asite pīte khāyite sāyite sampajānakārī hoti, uccārapassāvakamme sampajānakārī hoti, gate ṭhite nisinne sutte jāgarite bhāsite tuṇhībhāve sampajānakārī hoti, evamādinā bhante bhagavatā tattha sampajaññakāyānupassanā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',26,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0026"></a> tattha paṭikūlamanasikārakāyānupassanā bhagavatā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> puna caparaṃ bhikkhave bhikkhu imameva kāyaṃ uddhaṃ pādatalā adho kesamatthakā tacapariyantaṃ pūraṃ nānappakārassa asucino paccavekkhati atthi imasmiṃ kāye kesālomā nakhā dantā taco evamādinā bhante tattha bhagavatā paṭikūlamanasikārakāyānupassanā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',27,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0027"></a> tattha dhātumanasikārakāyānupassanā bhagavatā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> puna caparaṃ bhikkhave bhikkhu imameva kāyaṃ yathāṭhitaṃ yathāpaṇihitaṃ dhātuso paccavekkhati atthi imasmiṃ kāye pathavīdhātu āpodhātu tejodhātu vāyodhātu evamādinā bhante bhagavatā dhātumanasikārakāyānupassanā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',28,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0028"></a> tattha nava sivathikakāyānupassanā bhagavatā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> puna caparaṃ bhikkhave bhikkhu seyyathāpi passeyya sarīraṃ sivathikāya chaḍḍitaṃ ekāhamataṃ vā dvīhamataṃ vā tīhamataṃ vā uddhumātakaṃ vinīlakaṃ vipubbakajātaṃ so imameva kāyaṃ upasaṃharati ‘‘ayampi kho kāyo evaṃdhammo evaṃbhāvī evaṃanatīto’’ti evamādinā bhante tattha bhagavatā nava sivathikakāyānupassanā bhāvanā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',29,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0029"></a> tattha vedanānupassanā bhagavatā vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha bhikkhave bhikkhu sukhaṃ vā vedanaṃ vedayamāno ‘‘sukhaṃ vedanaṃ vedayāmī’’ti pajānāti, dukkhaṃ vā vedanaṃ vedayamāno ‘‘dukkha vedanaṃ vedayāmī’’ti pajānāti, adukkhamasukhaṃ vā vedanaṃ vedayamāno ‘‘adukkhamasukhaṃ vedanaṃ vedayāmī’’ti pajānāti, evamādinā bhante tattha bhagavatā vedanānupassanā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',30,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0030"></a> tattha cittānupassanā bhagavatā vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha bhikkhave bhikkhu sarāgaṃ vā cittaṃ ‘‘sarāgaṃ citta’’nti pajānāti, vītarāgaṃ vā cittaṃ ‘‘vītarāgaṃ citta’’nti pajānāti, sadosaṃ vā vītadosaṃ vā samohaṃ vā vītamohaṃ vā saṃkhittaṃ vā cittaṃ ‘‘saṃkhittaṃ citta’’nti pajānāti, vikkhittaṃ vā cittaṃ ‘‘vikkhittaṃ citta’’nti pajānāti, evamādinā tattha bhagavatā cittānupassanā vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',31,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0031"></a> tattha dhammānupassanā bhagavatā vibhajitvā desitā, taṃ saṅkhepamatteneva vissajjehi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha bhikkhave bhikkhu dhammesu dhammānupassī viharati, pañcasu nīvaraṇesu evamādinā bhante tattha bhagavatā pañcahi pabbehi dhammānupassanā vibhajitvā pakāsitā.</p>\r\n<p class="subhead">Cūḷasīhanādasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',32,replace(replace('<p class="bodytext">cūḷanasīhanādasuttaṃ <a name="M1.0032"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante parihīnalābhasakkāre nānātitthiye ārabbha bhāsitaṃ, nānātitthiyā bhante parihīnalābhasakkārā tesu tesu ṭhānesu parideviṃsu, catasso ca bhante parisā bhagavato ekamatthaṃ ārocesuṃ, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Mahāsīhanādasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',34,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0034"></a> bhagavatā jānatā passatā arahatā sammāsambuddhena mahāsīhanādasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante sunakkhattaṃ licchaviputtaṃ ārabbha bhāsitaṃ, sanukkhatto bhante licchaviputto acirapakkanto hoti imasmā dhammavinayā, so vesāliyaṃ parisati evaṃ vācaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',35,replace(replace('<p class="bodytext">bhāsati <a name="M1.0035"></a> ‘‘natthi samaṇassa gotamassa uttarimanussadhammā alamariyañāṇadassanaviseso, takkapariyāhataṃ samaṇo gotamo dhammaṃ deseti vīmaṃsānucaritaṃ sayaṃ paṭibhānaṃ. Yassa ca khvāssa atthāya dhammo desito, so niyyāti takkarassa sammā dukkhakkhayāyā’’ti, etamatthaṃ bhante āyasmā sāriputto bhagavato ārocesi, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',36,replace(replace('<p class="bodytext">kati <a name="M1.0036"></a> panāvuso tattha bhagavatā tathāgatassa tathāgatabalāni vibhajitvā pakāsitāni, yehi balehi samannāgato tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasa bhante tathāgatassa tathāgatabalāni bhagavatā vibhajitvā pakāsitāni, yehi balehi samannāgato tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',39,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0039"></a> tattha bhagavatā catuverajjañāṇāni vibhajitvā pakāsitāni, yehi vesārajjehi samannāgato tathāgato āsabhaṃ ṭhānaṃ paṭijānāti, parisāsu sīhanādaṃ nadati, brahmacakkaṃ pavatteti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> khīṇāsavassa te paṭijānato ime āsavā aparikkhīṇāti tatra vata maṃ samaṇo vā brāhmaṇo vā devo vā māro vā brahmā vā koci vā lokasmiṃ sahadhammena paṭicodessatīti nimittametaṃ sāriputta na samanupassāmi, etamahaṃ sāriputta nimittaṃ asamanupassanto khemappatto abhayappatto vesārajjappatto viharāmi. Evamādinā bhante tattha catuvesārajjañāṇāni bhagavatā vitthārena vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',41,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0041"></a> tattha bhagavatā pañcagatiparicchedañāṇaṃ vibhajitvā pakāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañca kho imā sāriputta gatiyo, katamā pañca, nirayo tiracchānayoni pettivisayo manussā devā, evamādinā bhante bhagavatā tattha pañcagatiparicchedakañāṇaṃ vibhajitvā pakāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',43,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0043"></a> tattha tathāgato attano paññāveyyattiyā aparihāniṃ pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> santi kho pana sāriputta eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino yāvadevāyaṃ bhavaṃ puriso daharo hoti, evamādinā bhante tattha tathāgato attano paññāveyyattiyā aparihāniṃ pakāsesi.</p>\r\n<p class="subhead">Mahādukkhakkhandhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',45,replace(replace('<p class="bodytext">mahādukkhakkhandhasuttaṃ <a name="M1.0045"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññatitthiye ārabbha bhāsitaṃ, sambahulā bhante aññatitthiyā bhikkhū etadavocuṃ ‘‘samaṇo āvuso gotamo kāmānaṃ pariññaṃ paññapeti, mayampi kāmānaṃ pariññaṃ paññapema, samaṇo āvuso gotamo </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',46,replace(replace('<p class="bodytext">rūpānaṃ <a name="M1.0046"></a> vedanānaṃ pariññaṃ paññapeti, mayampi rūpānaṃ vedanānaṃ pariññaṃ paññapema, idha no āvuso ko viseso, ko adhippayāso, kiṃ nānākaraṇaṃ samaṇassa vā gotamassa amhākaṃ vā yadidaṃ dhammadesanāya vā dhammadesanaṃ anusāsaniyā vā anusāsani’’nti, etamatthaṃ bhikkhū bhagavato ārocesuṃ, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Cūḷadukkhakkhandhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',47,replace(replace('<p class="bodytext">cūḷadukkhakkhandhasuttaṃ <a name="M1.0047"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante kapilavatthusmiṃ mahānāmaṃ sakkaṃ ārabbha bhāsitaṃ, mahānāmo bhante sakko bhagavantaṃ upasaṅkamitvā etadavoca ‘‘dīgharattāhaṃ bhante bhagavatā evaṃ dhammaṃ desitaṃ ājānāmi ‘lobho cittassa upakkileso, doso cittassa upakkileso, moho cittassa upakkileso’ti, evañcāhaṃ bhante bhagavatā dhammaṃ desitaṃ ājānāmi ‘lobho cittassa upakkileso, doso cittassa upakkileso, moho cittassa upakkileso’ti. Atha ca pana me ekadā lobhadhammāpi cittaṃ pariyādāya tiṭṭhanti, dosadhammāpi cittaṃ pariyādāya tiṭṭhanti, mohadhammāpi cittaṃ pariyādāya tiṭṭhanti, tassa mayhaṃ bhante evaṃ hoti ‘ko su nāma me dhammo ajjhattaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',48,replace(replace('<p class="bodytext">appahino<a name="M1.0048"></a>, yena me ekadā lobhadhammāpi cittaṃ pariyādāya tiṭṭhanti, dosadhammāpi cittaṃ pariyādāya tiṭṭhanti, mohadhammāpi cittaṃ pariyādāya tiṭṭhantī’ti’’, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Anumānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',49,replace(replace('<p class="bodytext">anumānasuttaṃ <a name="M1.0049"></a> panāvuso kattha kaṃ ārabbha kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhaggesu bhante susumāragire bhesakaḷāvane āyasmatā mahāmoggallānattherena sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Cetokhilasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',50,replace(replace('<p class="bodytext">cetokhilasuttaṃ <a name="M1.0050"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Vanapatthasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',53,replace(replace('<p class="bodytext">vanapatthasuttaṃ <a name="M1.0053"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Madhupiṇḍikasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> madhupiṇḍikasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante daṇḍapāṇiṃ sakkaṃ ārabbha bhāsitaṃ, daṇḍapāṇi bhante sakko bhagavantaṃ upasaṅkamitvā etadavoca ‘‘kiṃ vādī samaṇo kimakkhāyī’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Dvedhāvitakkasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',56,replace(replace('<p class="bodytext">dvedhāvitakkasuttaṃ <a name="M1.0056"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Vitakkasaṇṭhānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',57,replace(replace('<p class="bodytext">vitakkasaṇṭhānasuttaṃ <a name="M1.0057"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Kakacūpamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',58,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0058"></a> bhagavatā…pe… sammāsambuddhena kakacūpamasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ moḷiyaphaggunaṃ ārabbha bhāsitaṃ, āyasmā bhante moḷiyaphagguno bhikkhunīhi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',59,replace(replace('<p class="bodytext">saddhiṃ <a name="M1.0059"></a> ativelaṃ saṃsaṭṭho viharati, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',61,replace(replace('<p class="bodytext">atha <a name="M1.0061"></a> kho āvuso āyasmato moḷiyaphaggunassa taṃ bhagavato ovādaṃ sutvā kathaṃ cittaṃ uppannaṃ, kathañja bhagavā uttari bhikkhūna ovādamadāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha kho bhante moḷiyaphaggunassa bhagavato imaṃ ovādaṃ sutvā bhikkhunisaṃsaggato oramissāmi viramissāmītipi cittaṃ na uppannaṃ, asaṃvarameva bhante cittaṃ uppannaṃ, bhagavā ca bhante ārādhayiṃsu vata me bhikkhave bhikkhū ekaṃ samayaṃ cittaṃ, evamādinā uttari bhikkhūnaṃ ovādamadāsi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',64,replace(replace('<p class="bodytext">Ubhato <a name="M1.0064"></a> daṇḍakena cepi bhikkhave kakacena corā ocarakā aṅgamaṅgāni okanteyyuṃ.</p>\r\n<p class="subhead">Alagaddūpamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',65,replace(replace('<p class="bodytext">alagaddūpamasuttaṃ <a name="M1.0065"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ ārabbha bhāsitaṃ, ariṭṭhassa bhante bhikkhuno gaddhabādhipubbassa evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ ‘‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājādāmi, yathā yeme antarāyikā dhammā vuttā bhagavatā, te paṭisevato nālaṃ antarāyāyā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',68,replace(replace('<p class="bodytext">atha <a name="M1.0068"></a> kho āvuso āyasmato ariṭṭhassa gaddhabādhipubbassa kathaṃ cetaso parivitakko udapādi, kathañca bhagavā uttari bhikkhunaṃ dhammadesanaṃ pavattesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha kho bhante ariṭṭhassa bhikkhuno gaddhabādhipubbassa ‘‘kiñcāpi maṃ bhagavā moghapurisavādena vadesi, na kho pana me maggaphalānaṃ upanissayo na hoti, svāhaṃ ārabhitvā ghaṭṭetvā maggaphalāni nibbattessāmī’’ti, evaṃ kho bhante cetaso parivitakko udapādi, bhagavā ca bhante duppaññassa alagaddūpamaṃ dassetvā alagaddūpamaṃ pariyattiñca dassetvā paññavato alagaddūpamāya ca kullūpamāya ca paññavato nissaraṇapariyattiṃ dassetvā cha ca diṭṭhiṭṭhānāni, tesañca channaṃ diṭṭhiṭṭhānānaṃ viniveṭhanākāraṃ dassetvā pariyosāne ca khandhakammaṭṭhānaṃ arahattanikūṭena dassetvā uttari bhikkhūnaṃ dhammakathaṃ pavattesi.</p>\r\n<p class="subhead">Vammikasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',73,replace(replace('<p class="bodytext">dhammikasuttaṃ <a name="M1.0073"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ kumārakassapaṃ ārabbha bhāsitaṃ. Āyasmā bhante kumārakassapo bhagavantaṃ upasaṅkamitvā vammikapañhaṃ pucchi, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Rathavinītasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',74,replace(replace('<p class="bodytext">rathavinītasuttaṃ <a name="M1.0074"></a> panāvuso kattha kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmatā ca sāriputtattherena dhammasenāpatinā āyasmatā ca puṇṇena mantāṇiputtena aññamaññaṃ pucchāvissajjanavasena bhāsitaṃ.</p>\r\n<p class="subhead">Nivāpasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> nivāpasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',77,replace(replace('<p class="bodytext">imasmiṃ <a name="M1.0077"></a> āvuso sutte ko nivāpo ko nevāpiko kā nevāpikaparisā kā migajātā kathañcetissā upamāya attho daṭṭhabbo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imasmiṃ bhante sutte nivāpoti kho bhante pañcannetaṃ kāmaguṇānaṃ adhivacanaṃ, nevāpikoti kho bhante mārassetaṃ pāpimato adhivacanaṃ, nevāpikaparisāti kho bhante māraparisāyetaṃ adhivacanaṃ, migajātāti kho bhante samaṇabrāhmaṇānametaṃ adhivacanaṃ, imasmiṃ bhante sutte etassa attho evaṃ daṭṭhabbo.</p>\r\n<p class="subhead">Pāsarāsisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',79,replace(replace('<p class="bodytext">kenāvuso <a name="M1.0079"></a> bhagavatā…pe… sammāsambuddhena pāsarāsisuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ, sambahulā bhante bhikkhū rammakassa brāhmaṇassa assame bhagavantaṃ ārabbha dhammiyā kathāya sannisīdiṃsu, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',81,replace(replace('<p class="bodytext">atha <a name="M1.0081"></a> kho āvuso tesaṃ sambahulānaṃ bhikkhūnaṃ dhammiyā kathāya sannisinnānaṃ kīdisaṃ dhammakathaṃ kathesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sādhu bhikkhave etaṃ kho bhikkhave tumhākaṃ patirūpaṃ kulaputtānaṃ saddhā agārasmā anagāriyaṃ pabbajitānaṃ, yaṃ tumhe dhammiyā kathāya sannisīdeyyātha, sannipatitānaṃ vo bhikkhave dvayaṃ karaṇīyaṃ dhammī vā kathā ariyo vā tuṇhībhāvo. Dvemā bhikkhave pariyesanā ariyā ca pariyesanā anariyā ca pariyesanā. Evamādinā bhante bhagavā sannipatitānaṃ tesaṃ bhikkhūnaṃ ariyapariyesanañca anariyapariyesanañca vibhajitvā desesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',83,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0083"></a> bhagavā attanāpi anariyapariyesanaṃ pahāya ariyapariyesanāya pariyesitabhāvaṃ pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ahampi sudaṃ bhikkhave pubbeva sambodhā anabhisambuddho bodhisattova samāno attanā jātidhammo samāno jātidhammaṃyeva pariyesāmi, attanā jarādhammo byādhidhammo maraṇadhammo sokadhammo saṃkilesadhammo samāno saṃkilesadhammaṃyeva pariyesāmi. Evamādinā bhante bhagavā attanāpi anariyapariyesanaṃ pahāya ariyapariyesanāya pariyesitabhāvaṃ pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',85,replace(replace('<p class="bodytext">evaṃ <a name="M1.0085"></a> paṭhamābhisambuddhassa āvuso bhagavato ajapālanigrodharukkhamūle nisinnassa dhammadesanāya katasanniṭṭhānassa kīdiso</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',86,replace(replace('<p class="bodytext">Cetaso <a name="M1.0086"></a> parivitakko udapādi, kathañca dhammadesanāya cārikā ahosi, kathañca paṭhamā dhammadesanā ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘kassa nu kho ahaṃ paṭhamaṃ dhammaṃ deseyyaṃ, ko imaṃ dhammaṃ khippameva ājānissatī’’ti evaṃ kho bhante bhagavato paṭhamābhisambuddhassa ajapālanigrodhamūle dhammadesanāya katasanniṭṭhānassa parivitakko udapādi, atha bhante bhagavā uruvelāyaṃ yathābhirantaṃ viharitvā yena bārāṇasī, tena padasāyeva cārikaṃ pakkāmi dhammadesanāya, dveme bhikkhave antā pabbajitena na sevitabbā, katame dve, yo cāyaṃ kāmesu kāmasukhallikānuyogo hīno gāmmo pothujjaniko anariyo anattasaṃhito, evamādinā bhante bhagavato paṭhamā dhammadesanā ahosi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',89,replace(replace('<p class="gatha1">Sabbābhibhū <a name="M1.0089"></a> sabbavidūhamasmi,</p>\r\n<p class="gatha2">Sabbadhammesu anūpalitto;</p>\r\n<p class="gatha3">Sabbañjaho taṇhākkhaye vimutto,</p>\r\n<p class="gathalast">Sayaṃ abhiññāya kamuddiseyyaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',92,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0092"></a> bhagavā pāsarāsiupamāya taṃ desanaṃ pariniṭṭhāpesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcime bhikkhave kāmaguṇā, katame pañca, cakkhuviññeyyā rūpā iṭṭhā kantā manāpā piyarūpā kāmūpasaṃhitā rajanīyā. Sotaviññeyyā saddā. Ghānaviññeyyā gandhā. Jivhāviññeyyā rasā. Kāyaviññeyyā phoṭṭhabbā iṭṭhā kantā manāpā piyarūpā kāmūpasaṃhitā rajanīyā. Ime kho bhikkhave pañca kāmaguṇā. Ye hi keci bhikkhave samaṇā vā brāhmaṇā vā ime kāmaguṇe gathitā mucchitā ajjhopannā anādīnavadassāvino anissaraṇapaññā paribhuñjanti, te evamassu veditabbā ‘‘anayamāpannā byāsanamāpannā yathākāmakaraṇīyā pāpimato’’, evamādinā bhante bhagavā pāsarāsiupamāya dhammadesanaṃ pariniṭṭhāpesi.</p>\r\n<p class="subhead">Cūḷahatthipadopamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',94,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0094"></a> jānatā…pe… sammāsambuddhena cūḷahatthipadopamasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante jāṇussoṇiṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Jāṇussoṇi bhante brāhmaṇo bhagavantaṃ upasaṅkamitvā yāvatako ahosi pilotikena paribbājakena saddhiṃ kathāsallāpo, taṃ sabbaṃ bhagavato ārocesi, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',98,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0098"></a> bhagavā hatthipadopamaṃ vitthārena paripūretvā desesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘na kho brāhmaṇa ettāvatā hatthipadopamo vitthārena paripūro hoti, api ca brāhmaṇa yathā hatthipadopamo vitthārena paripūro hoti, taṃ suṇāhi sādhukaṃ manasikarohi bhāsissāmī’’ti, evamādinā bhante bhagavā hatthipadopamaṃ paripūretvā brāhmaṇassa jāṇussoṇissa desesi.</p>\r\n<p class="subhead">Mahāhatthipadopamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',102,replace(replace('<p class="bodytext">mahāhatthipadopamasuttaṃ <a name="M1.0102"></a> panāvuso kattha kaṃ ārabbha kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>\r\n<p class="subhead">Mahāsāropamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',103,replace(replace('<p class="bodytext">mahāsāropamasuttaṃ <a name="M1.0103"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante devadattaṃ ārabbha bhāsitaṃ, devadatto bhante saṅghaṃ bhinditvā ruhiruppādakammaṃ katvā acirapakkanto hoti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Cūḷasāropamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',105,replace(replace('<p class="bodytext">cūḷasāropamasuttaṃ <a name="M1.0105"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante piṅgalakocchaṃ brāhmaṇaṃ ārabbha bhāsitaṃ, piṅgalakoccho bhante brāhmaṇo bhagavantaṃ upasaṅkamitvā pañhaṃ pucchi, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Cūḷagosiṅgasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',106,replace(replace('<p class="bodytext">cūḷagosiṅgasuttaṃ <a name="M1.0106"></a> panāvuso bhagavatā kattha kena saddhiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> nābhike bhante gosiṅgasālavanadāye āyasmatā anuruddhattherena saddhiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Mahāgosiṅgasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> mahāgosiṅgasuttaṃ panāvuso bhagavatā kattha kena saddhiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> gosiṅgasālavanadāye bhante āyasmatā ca sāriputtattherena dhammasenāpatinā āyasmatā ca mahāmoggallānattherena saddhiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Mahāgopālakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',107,replace(replace('<p class="bodytext">mahāgopālakasuttaṃ <a name="M1.0107"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Cūḷagopālakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',109,replace(replace('<p class="bodytext">cūḷagopālakasuttaṃ <a name="M1.0109"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vajjīsu bhante ukkacelāyaṃ gaṅgāya nadiyā tīre sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Cūḷasaccakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',110,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0110"></a> jānatā…pe… sammāsambuddhena cūḷasaccakasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante saccakaṃ nigaṇṭhaputtaṃ ārabbha bhāsitaṃ, saccako nigaṇṭhaputto mahatiyā licchaviparisāya saddhiṃ yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ etadavoca ‘‘kathaṃ pana bhavaṃ gotamo sāvake vineti, kathaṃ bhāgā ca pana bhoto gotamassa sāvakesu anusāsanī bahulā pavattatī’’ti, atha bhante bhagavatā aniccavāde ca anattavāde ca pakāsite saccako nigaṇṭhaputto pathavīupamaṃ dassetvā attano attavādaṃ pakāsesi, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',114,replace(replace('<p class="bodytext">evaṃ <a name="M1.0114"></a> āvuso saccakena nigaṇṭhaputtena mahāpathavīupamaṃ dassetvā attavāde pakāsite kathaṃ bhagavā taṃ attavādaṃ puna patiṭṭhāpetvā samanuyuñji samanugāhi samanubhāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘nanu tvaṃ aggivessana evaṃ vadesi, rūpaṃ me attā, vedanā me attā, saññā me attā, saṅkhārā me attā, viññāṇaṃ me attā’’ti, evaṃ kho bhante bhagavā saccakaṃ nigaṇṭhaputtaṃ taṃ attavādaṃ patiṭṭhāpesi. Patiṭṭhāpetvā ca pana bhante bhagavā ‘‘tena hi aggivessana taññevettha paṭipucchissāmi, yathā te khameyya, tathā taṃ byākareyyāsī’’ti evamādinā bhante bhagavā saccakaṃ nigaṇṭhaputtaṃ samanuyuñji samanugāhi samanubhāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',119,replace(replace('<p class="bodytext">evaṃ <a name="M1.0119"></a> kho āvuso saccake nigaṇṭhaputte tuṇhībhūte adhomukhe pajjhāyante appaṭibhāne nisinne dummukho nāma licchaviputto bhagavantaṃ kiṃ vacanaṃ avoca.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> evaṃ bhante saccake nigaṇṭhaputte tuṇhībhūte maṅkubhūte pattakkhandhe adhomukhe pajjhāyante appaṭibhāne dummukho </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',120,replace(replace('<p class="bodytext">licchaviputto <a name="M1.0120"></a> bhagavantaṃ etadavoca ‘‘upamā maṃ bhagavā paṭibhātī’’ti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',121,replace(replace('<p class="bodytext">atha <a name="M1.0121"></a> kho āvuso saccako nigaṇṭhaputto bhagavantaṃ kīdisaṃ pañhaṃ pucchi, kathañca taṃ bhagavā byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha kho bhante saccako nigaṇṭhaputto dummukhaṃ licchaviṃ apasādetvā bhagavantaṃ sekhañca asekhañca pañhaṃ pucchi, bhagavā ca bhante ‘‘idha aggivessana mama sāvako yaṃ kiñci rūpaṃ atītānāgatapaccuppannaṃ ajjhattaṃ vā bahiddhā vā oḷārikaṃ vā sukhumaṃ vā hīnaṃ vā paṇītaṃ vā yaṃ dūre santike vā’’ evamādinā sekhañca asekhañca puggalaṃ vibhajitvā byākāsi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',122,replace(replace('<p class="bodytext">Buddho <a name="M1.0122"></a> so bhagavā bodhāya dhammaṃ deseti.</p>\r\n<p class="bodytext">Danto so bhagavā damathāya dhammaṃ deseti.</p>\r\n<p class="bodytext">Santo so bhagavā samathāya dhammaṃ deseti.</p>\r\n<p class="bodytext">Tiṇṇo so bhagavā taraṇāya dhammaṃ deseti.</p>\r\n<p class="bodytext">Parinibbuto so bhagavā parinibbānāya dhammaṃ deseti.</p>\r\n<p class="subhead">Mahāsaccakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',123,replace(replace('<p class="bodytext">mahāsaccakasuttaṃ <a name="M1.0123"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante saccakaṃyeva nigaṇṭhaputtaṃ ārabbha bhāsitaṃ. Saccako bhante nigaṇṭhaputto aparadivase bhagavantaṃ upasaṅkamitvā bhagavato sāvake āsajja bhāvanādvayapaṭisaṃyuttaṃ vācaṃ bhāsati, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',124,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0124"></a> bhagavā saccakaṃ nigaṇṭhaputtaṃ paṭipucchitvā bhāvanādvayaṃ vibhajja kathesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha kho bhante bhagavā ‘‘kinti pana te aggivessana kāyabhāvanā sutā’’ti evamādinā saccakaṃ nigaṇṭhaputtaṃ paṭipucchitvā, kathañca aggivessana abhāvitakāyo ca hoti abhāvitacitto ca evamādinā bhāvanādvayaṃ vibhajitvā byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',127,replace(replace('<p class="bodytext">atha <a name="M1.0127"></a> kho āvuso saccako nigaṇṭhaputto bhagavantaṃ kiṃ vacanaṃ avoca, kathañca bhagavā padhānakāle attanāanubhūtapubbā paramukkaṃsagatā sukhadukkhavedanāyo pakāsesi, yāpi bhagavato cittaṃ na pariyādāya aṭṭhaṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha kho bhante saccako nigaṇṭhaputto bhagavantaṃ etadavoca ‘‘na hi nūna bhoto gotamassa uppajjati, tathā rūpā sukhāvedanā, yathārūpā uppannā sukhāvedanā cittaṃ pariyādāya tiṭṭheyya, na hi nūna bhoto gotamassa uppajjati tathārūpā dukkhā vedanā, yathārūpā uppannā dukkhā vedanā cittaṃ pariyādāya tiṭṭheyyā’’ti atha kho bhagavā ‘‘kiñhi no siyā aggivessana, idha </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',128,replace(replace('<p class="bodytext">me <a name="M1.0128"></a> aggivessena pubbeva sambodhā anabhisambuddhassa bodhisattasseva sato etadahosi, evamādinā bhante bhagavā padhānakāle attanānubhūtapubbā paramukkaṃsagatā sukhadukkhavedanāyo vitthārena, yāpi bhagavato cittaṃ na pariyādāya aṭṭhaṃsu.</p>\r\n<p class="subhead">Cūḷataṇhāsaṅkhayasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',132,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0132"></a> bhagavatā jānatā…pe… sammāsambuddhena cūḷataṇhāsaṅkhayasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sakkaṃ devānamindaṃ ārabbha bhāsitaṃ. Sakko bhante devānamindo bhagavantaṃ upasaṅkamitvā etadavoca ‘‘kittāvatānukho bhante bhikkhu saṃkhittena taṇhāsaṅkhayavimutto hoti accantaniṭṭho </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',133,replace(replace('<p class="bodytext">accantayogakkhemi <a name="M1.0133"></a> accantabrahmacārī accantapariyosāno seṭṭho devamanussāna’’nti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',134,replace(replace('<p class="bodytext">Kittāvatā <a name="M1.0134"></a> nu kho bhante bhikkhu saṃkhittena taṇhāsaṅkhayavimutto hoti accantaniṭṭho accantayogakkhemī accantabrahmacārī accantapariyāsāno seṭṭho devamanussānaṃ –</p>\r\n<p class="subhead">Mahātaṇhāsaṅkhayasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> mahātaṇhāsaṅkhayasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sātiṃ bhikkhuṃ kevaṭṭaputtaṃ ārabbha bhāsitaṃ. Sātissa bhante bhikkhuno kevaṭṭaputtassa evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ hoti ‘‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi, yathā tadevidaṃ viññāṇaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',135,replace(replace('<p class="bodytext">sandhāvati <a name="M1.0135"></a> saṃsarati anañña’’nti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',136,replace(replace('<p class="bodytext">atha <a name="M1.0136"></a> kho āvuso bhagavā kathaṃ bhikkhū āmantetvā ca paṭipucchitvā ca anattabhāvadīpikaṃ dhammakathaṃ kathesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha bhante bhagavā bhikkhū ‘‘taṃ kiṃ maññatha bhikkhave, apināyaṃ sāti bhikkhu kevaṭṭaputto usmīkatopi ismiṃ dhammavinaye’’ti āmantetvā, tumhepi me bhikkhave evaṃ dhammaṃ desitaṃ ājānāthatyādinā bhikkhū paṭipucchitvā ca yaṃ yadeva bhikkhave paccayaṃ paṭicca uppajjati viññāṇaṃ, tena teneva viññāṇaṃ tveva saṅkhyaṃ gacchati, cakkhuñca paṭicca </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',137,replace(replace('<p class="bodytext">rūpe <a name="M1.0137"></a> ca uppajjati viññāṇaṃ cakkhuviññāṇaṃ tveva saṅkhyaṃ gacchati, evamādinā bhante anattatādīpikaṃ dhammiṃ kathaṃ kathesi.</p>\r\n<p class="subhead">Mahāassapurasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',139,replace(replace('<p class="bodytext">mahāassapurasuttaṃ <a name="M1.0139"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṅgesu bhante assapure nāma aṅgānaṃ nigame bahū manusse saddhe pasanne ārabbbha bhāsitaṃ. Bahū bhante manussā saddhā pasannā bhikkhusaṅghaṃ sakkaccaṃ upaṭṭhahiṃsu, sabbakālañca ratanattayapaṭisaṃyuttaṃ vaṇṇakathaṃyeva kathayiṃsu. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',140,replace(replace('<p class="bodytext">katame <a name="M1.0140"></a> āvuso tattha bhagavatā dhammā samaṇakaraṇā ca brāhmaṇakaraṇā ca uttaruttari paṇītapaṇītā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',141,replace(replace('<p class="bodytext">hirottappā <a name="M1.0141"></a> parisuddhakāyasamācāro parisuddhavacīsamācāro parisuddhamanosamācāro parisuddhājīvo indriyesu guttadvāratā bhojanemattaññutā jāgariyānuyogo satisampajaññaṃ nīvaraṇappahānaṃ cattāri ca jhānāni tisso ca vijjā ime kho bhante tattha bhagavatā samaṇakaraṇā ca brāhmaṇakaraṇā ca uttaruttari paṇītapaṇītā dhammā desitā.</p>\r\n<p class="subhead">Cūḷaassapurasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',143,replace(replace('<p class="bodytext">cūḷaassapurasuttaṃ <a name="M1.0143"></a> panāvuso bhagavatā katta kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tasmiṃyeva bhante assapure nigame teyeva manusse saddhe pasanne ārabbha bhāsitaṃ, tasmiṃyeva bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',144,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0144"></a> tattha bhagavatā samaṇasāmīcippaṭipadā ca assamaṇasāmīcippaṭipadā ca vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘kathañca bhikkhave bhikkhu na samaṇasāmīcippaṭipadaṃ paṭipanno hoti, yassa kassaci bhikkhave bhikkhuno abhijjhālussa abhijjhā appahīnā hoti, byāpannacittassa byāpādo appahīno hotī’’ti evamādinā ca. Kathañca bhikkhave bhikkhu samaṇasāmīcippaṭipadaṃ paṭipanno hoti, yassa kassaci bhikkhave bhikkhuno abhijjhālussa abhijjhā pahīnā hoti, byāpannacittassa byāpādo pahīno hoti evamādinā ca bhante bhagavatā tattha samaṇasāmīcippaṭipadā ca assamaṇasāmīcippaṭipadā ca vitthārena vibhajitvā desitā.</p>\r\n<p class="subhead">Sāleyyakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',146,replace(replace('<p class="bodytext">sāleyyakasuttaṃ <a name="M1.0146"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante sālāyaṃ nāma brāhmaṇagāme sāleyyake brāhmaṇagahapatike ārabbha bhāsitaṃ. Sāleyyakā bhante brāhmaṇagahapatikā bhagavantaṃ upasaṅkamitvā etadavocuṃ ‘‘ko nu kho bho gotama hetu ko paccayo, yena midhekacce sattā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti, ko pana bho gotama hetu ko paccayo, yena midhekacce sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjantī’’ti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Verañjakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',148,replace(replace('<p class="bodytext">verañjakasuttaṃ <a name="M1.0148"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante verañjake brāhmaṇagahapatike ārabbha bhāsitaṃ. Verañjakā bhante brāhmaṇagahapatikā bhagavantaṃ upasaṅkamitvā etadavocuṃ ‘‘ko nu kho bho gotama hetu ko paccayo, yena midhekacce sattā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti, ko pana bho gotama hetu ko paccayo, yena midhekacce sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjantī’’ti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Mahāvedallasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> mahāvedallasuttaṃ panāvuso kattha kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmatā mahākoṭṭhikena puṭṭhena āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>\r\n<p class="subhead">Cūḷavedallasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',149,replace(replace('<p class="bodytext">cūḷavedallasuttaṃ <a name="M1.0149"></a> panāvuso kattha kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante visākhena upāsakena puṭṭhāya dhammadinnāya theriyā bhāsitaṃ.</p>\r\n<p class="subhead">Cūḷadhammasamādānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',150,replace(replace('<p class="bodytext">cūḷadhammasamādānasuttaṃ <a name="M1.0150"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Mahādhammasamādānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> mahādhammasamādānasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Vīmaṃsakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',151,replace(replace('<p class="bodytext">vīmaṃsakasuttaṃ <a name="M1.0151"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Kosambiyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kosambiyasuttaṃ panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante kosambike bhikkhū ārabbha bhāsitaṃ. Kosambikā bhante bhikkhū bhaṇḍanajātā kalahajātā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',152,replace(replace('<p class="bodytext">vivādāpannā <a name="M1.0152"></a> aññamaññaṃ mukhasattīhi vitudantā viharanti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Brahmanimantanikasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> brahmanimantanikasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Māratajjaniyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',153,replace(replace('<p class="bodytext">māratajjanīyasuttaṃ <a name="M1.0153"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante susumāragire bhesakaḷāvane migadāye āyasmatā mahāmoggallānena māraṃ pāpimantaṃ ārabbha bhāsitaṃ. Māro bhante pāpimā āyasmato mahāmoggallānassa kucchigato hoti koṭṭhamanupaviṭṭho. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="chapter">Majjhimapaṇṇāsapāḷi</p>\r\n<p class="subhead">Kandarakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',155,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0155"></a> jānatā…pe… sammāsambuddhena kandarakasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> campāyaṃ bhante gaggarāya pokkharaṇiyā tīre pessañca hatthārohaputtaṃ kandarakañca paribbājakaṃ ārabbha bhāsitaṃ, kandarako bhante paribbājako bhagavato ca bhikkhusaṅghassa ca vaṇṇaṃ abhāsi, pesso ca bhante hatthārohaputto bhagavato ca dhammadesanāya ca vaṇṇaṃ abhāsi. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',156,replace(replace('<p class="bodytext">evaṃ <a name="M1.0156"></a> kho āvuso kandarakena paribbājakena bhagavato ca bhikkhusaṅghassa ca abhippasannena vaṇṇe bhāsite kathaṃ bhagavā taṃ samanujānitvā dhammadesanārambhaṃ ārabhi, kathañca pesso hatthārohaputto bhagavato ca dhammadesanāya ca vaṇṇaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',157,replace(replace('<p class="bodytext">abhāsi<a name="M1.0157"></a>, kathañca pessassa hatthārohaputtassa ajjhāsayānurūpaṃ dhammaṃ desesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> evaṃ kho bhante kandarakena paribbājakena bhagavato ca bhikkhusaṅghassa ca abhippasannena vaṇṇe bhāsite ‘‘evametaṃ kandaraka evametaṃ kandaraka’’ evamādinā bhagavā taṃ samanujānitvā ‘‘santi hi kandaraka bhikkhū imasmiṃ bhikkhusaṅghe arahanto khīṇāsavā vusitavanto katakaraṇīyā ohitabhārā anuppattasadatthā parikkhīṇabhavasaṃyojanā sammadaññā vimuttā’’ti evamādinā dhammadesanaṃ samārabhi. Atha kho bhante pesso hatthārohaputto ‘‘acchariyaṃ bhante abbhutaṃ bhante yāva supaññattā cime bhante bhagavatā cattāro satipaṭṭhānā sattānaṃ visuddhiyā sokaparidevānaṃ samatikkamāya dukkhadomanassānaṃ atthaṅgamāya ñāyassa adhigamāya nibbānassa sacchikiriyāyā’’ti evamādinā bhagavato ca dhammadesanāya vaṇṇaṃ abhāsi, bhagavāpi bhante ‘‘gahanañhetaṃ pessa yadidaṃ manussā, uttānakañhetaṃ pessa yadidaṃ pasavo’’tiādinā pessassa hatthārohaputtassa ajjhāsayānurūpaṃ dhammakathaṃ desesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',162,replace(replace('<p class="bodytext">taṃ <a name="M1.0162"></a> panāvuso dhammadesanaṃ sutvā pessassa hatthārohaputtassa kīdiso ānisaṃso adhigato, kathañca bhagavā taṃ puggalacatukkadesanaṃ saṃkhittena bhāsitaṃ, bhikkhūnaṃ vitthārena vibhajitvā desesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> taṃ kho pana bhante dhammaṃ sutvā pessassa hatthārohaputtassa dve ānisaṃsā adhigatā saṅghe ca pasādo </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',163,replace(replace('<p class="bodytext">satipaṭṭhānapariggahaṇūpāyo <a name="M1.0163"></a> ca abhinavo. Bhagavā ca bhante taṃ puggalacatukkadesanaṃ bhikkhūhi yācito ‘‘katamo ca bhikkhave puggalo attaparitāpanānuyogamanuyutto’’tiādinā saṃkhittena bhāsitaṃ, vitthārena atthaṃ avibhattaṃ. Vitthārena vibhajitvā desesi.</p>\r\n<p class="subhead">Aṭṭhakanāgarasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',166,replace(replace('<p class="bodytext">aṭṭhakanāgarasuttaṃ <a name="M1.0166"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante veḷuvagāmake gahapatiṃ aṭṭhakanāgaraṃ ārabbha āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ. Dasamo bhante gahapati aṭṭhakanāgaro āyasmantaṃ ānandaṃ upasaṅkamitvā etadavoca ‘‘atthi nu kho bhante ānanda tena bhagavatā jānatā passatā arahatā sammāsambuddhena ekadhammo akkhāto, yattha bhikkhuno appamattassa ātāpino pahitattassa viharato avimuttañceva cittaṃ vimuccati, aparikkhiṇā ca āsavā parikkhayaṃ gacchanti, ananuppattañca anuttaraṃ yogakkhemaṃ anupāpuṇātī’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Sekhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',168,replace(replace('<p class="bodytext">sekhasuttaṃ <a name="M1.0168"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante kapilavatthusmiṃ santhāgāre āyasmatā ānandattherena dhammabhaṇḍāgārikena kāpilavatthave sakye ārabbha bhāsitaṃ. Bhagavā bhante kāpilavatthave sakye bahudevarattiṃ dhammiyā kathāya sandassetvā samādapetvā samuttejetvā sampahaṃsetvā āyasmantaṃ ānandaṃ āmantesi ‘‘paṭibhātu taṃ ānanda kāpilavatthavānaṃ sakyānaṃ sekho pāṭipado, piṭṭhi me āgilāyati, tamahaṃ āyamissāmī’’ti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Potaliyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',170,replace(replace('<p class="bodytext">potaliyasuttaṃ <a name="M1.0170"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṅguttarāpesu bhante āpaṇe nāma aṅguttarāpānaṃ nigame potaliyaṃ gahapatiṃ paṭikkhittasabbakammantaṃ ārabbha bhāsitaṃ. Potaliyo bhante gahapati paṭikkhittasabbakammanto yena bhagavā tenupasaṅkami, upasaṅkamanto bhagavatā gahapativādena samudācariyamāno kupito anattamano bhagavantaṃ etadavoca ‘‘tayidaṃ bho gotama nacchannaṃ tayidaṃ nappatirūpaṃ, yaṃ maṃ tvaṃ gahapativādena samudācarasī’’ti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',172,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0172"></a> bhagavā kāmānaṃ ādīnavaṃ vitthārena pakāsetvā ariyassa vinaye vohārasamucchedaṃ dassesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha bhante bhagavā ariyassa vinaye vohārasamucchedāya aṭṭha dhamme vibhajitvā ‘‘seyyathāpi gahapati kukkuro jighacchādubbalyapareto goghātakasūnaṃ paccupaṭṭhito assa’’ evamādinā kāmesu ādīnavaṃ dassetvā pariyosāne tīhi vijjāhi ariyassa vinaye vohārasamucchedaṃ sabbenasabbaṃ sabbathāsabbaṃ vohārasamucchedaṃ vitthārena vibhajitvā desesi.</p>\r\n<p class="subhead">Jīvakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',173,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0173"></a> bhagavatā…pe… sammāsambuddhena jīvakasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',174,replace(replace('<p class="bodytext">rājagahe <a name="M1.0174"></a> bhante jīvakaṃ komārabhaccaṃ ārabbha bhāsitaṃ. Jīvako bhante komārabhacco bhagavantaṃ upasaṅkamitvā etadavoca ‘‘sutaṃ metaṃ bhante samaṇaṃ gotamaṃ uddissa pāṇaṃ ārabhanti, taṃ samaṇo gotamo jānaṃ uddissa kataṃ maṃsaṃ paribhuñjati paṭicca kammanti. Ye te bhante evamāhaṃsu ‘samaṇaṃ gotamaṃ uddissa pāṇaṃ ārabhanti, taṃ samaṇo gotamo jānaṃ uddissa kataṃ maṃsaṃ paribhuñjati paṭicca kamma’nti, kacci te bhante bhagavato vuttavādino na ca bhagavantaṃ abhūtena abbhācikkhanti, dhammassa cānudhammaṃ byākaronti, na ca koci sahadhammiko vādānuvādo gārayhaṃ ṭhānaṃ āgacchatī’’ti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Upālisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',177,replace(replace('<p class="bodytext">upālisuttaṃ <a name="M1.0177"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> nāḷandāyaṃ bhante upāliṃ gahapatiṃ ārabbha bhāsitaṃ. Upāli bhante gahapati bhagavantaṃ upasaṅkamitvā etadavoca ‘‘āgamā nukhvidha bhante dīghatapassī nigaṇṭho’’ti, evamādinā ca bhante upāli gahapati bhagavato paṭisandhāraṃ katvā attano ācariyassa nigaṇṭhassa nāṭaputtassa vādaṃ pakāsesi, vādaṃ vaṇṇesi. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',183,replace(replace('<p class="bodytext">atha <a name="M1.0183"></a> kho āvuso bhagavā upāliṃ gahapatiṃ tasmiṃ vāde kathaṃ samanuyuñjī samanugāhī samanubhāsī, kathañcassa upāyaṃ dassetvā yathābhūtaṃ atthaṃ ñāpesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha kho bhante bhagavā upāliṃ gahapatiṃ ‘‘sace kho tvaṃ gahapati sacce patiṭṭhāya manteyyāsi, siyā no ettha kathāsallāpo’’ti upāliṃ gahapatiṃ kathaṃ samuṭṭhāpetvā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',184,replace(replace('<p class="bodytext">‘‘taṃ <a name="M1.0184"></a> kiṃmaññasi gahapati, idhassa nigaṇṭho ābādhiko dukkhito bāñhagilāno sītodakaparikkhitto uṇhodakapaṭisevī, so sītodakaṃ alabhamāno kālaṅkareyyā’’ti evamādinā bhante bhagavā cattāro upāye dassetvā upāliṃ gahapatiṃ yathābhūtamatthaṃ ñāpesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',190,replace(replace('<p class="bodytext">evaṃ <a name="M1.0190"></a> kho āvuso upāli gahapati bhagavatā saññāpito imasmiṃ dhammavinaye pasanno kīdisaṃ pasannākāraṃ akāsi, kathañca bhagavā taṃ puna anusāsitvā uttari vinesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘purimenevāhaṃ bhante opammena bhagavato attamano abhiraddho, apicāhaṃ imāni bhagavato vicitrāni pañhapaṭibhānāni sotukāmo, evāhaṃ bhagavantaṃ paccanīkaṃ kātabbaṃ amaññissa’’nti, evamādinā bhante upāli gahapati bhagavatā viññāpito imasmiṃ dhammavinaye pasanno pasannākāraṃ akāsi. Tattha bhagavā ca bhante ‘‘anuviccakāraṃ kho gahapati karohi, anuviccakāro tumhādisānaṃ ñātamanussānaṃ sādhu hotī’’ti evamādinā puna anusāsitvā uttari yāva dhammacakkhupaṭilābhā vinesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',194,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0194"></a> nigaṇṭhassa nāṭaputtassa upālissa gahapatissa nivesanaṃ gantvā āvīmaṃsanā ca upālissa gahapatissa paccuttarā kathā ca ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘ummattosi tvaṃ gahapati dattosi tvaṃ gahapatī’’ti evamādinā bhante nigaṇṭhassa nāṭaputtassa upālissa gahapatissa nivesanaṃ gantvā vīmaṃsā ca ‘‘bhaddikā bhante āvaṭṭanīmāyā kalyāṇī bhante āvaṭṭanīmāyāti’’ evamādinā upālissa gahapatissa paccuttarakathā ca ahosi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',198,replace(replace('<p class="gatha1">Dhīrassa <a name="M1.0198"></a> vigatamohassa,</p>\r\n<p class="gatha2">Pabhinnakhīlassa vijitavijayassa;</p>\r\n<p class="gatha3">Anīghassa susamacittassa,</p>\r\n<p class="gatha2">Vuddhasīlassa sādhupaññassa;</p>\r\n<p class="gatha3">Vesamantarassa vimalassa,</p>\r\n<p class="gathalast">Bhagavato tassa sāvakohamasmiṃ.</p>\r\n<p class="gatha1">Akathaṃ kathissa tusitassa,</p>\r\n<p class="gatha2">Vantalokāmisassa muditassa;</p>\r\n<p class="gatha3">Katasamaṇassa manujassa,</p>\r\n<p class="gathalast">Antimasārīrassa narassa.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',199,replace(replace('<p class="gatha1">Anopamassa <a name="M1.0199"></a> virajassa,</p>\r\n<p class="gathalast">Bhagavato tassa sāvako hamasmi –</p>\r\n<p class="gatha1">Taṇhacchidassa buddhassa,</p>\r\n<p class="gatha2">Vītadhūmassa anupalittassa.</p>\r\n<p class="gatha1">Āhuneyyassa yakkhassa,</p>\r\n<p class="gatha2">Uttamapuggalassa atulassa;</p>\r\n<p class="gatha3">Mahato yasaggapattassa,</p>\r\n<p class="gathalast">Bhagavato tassa sāvako hamasmi –</p>\r\n<p class="subhead">Kukkuravatikasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',200,replace(replace('<p class="bodytext">kukkuravatikasuttaṃ <a name="M1.0200"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> koliyesu bhante haliddavasane nāma koliyānaṃ nigame puṇṇañca koliyaputtaṃ govatikaṃ acelañca seniyaṃ kukkuravatikaṃ ārabbha bhāsitaṃ. Puṇṇo bhante koliya putto govatiko bhagavantaṃ etadavoca ‘‘ayaṃ bhante acelo seniyo kukkuravatiko dukkarakārako chamānikkhittaṃ bhojanaṃ bhuñjati, tassa taṃ kukkuravataṃ dīgharattaṃ samattaṃ samādinnaṃ, tassa kā gati, ko abhisamparāyo’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Abhayarājakumārasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',201,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0201"></a> jānatā…pe… sammāsambuddhena abhayarājakumārasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe me bhante abhayaṃ rājakumāraṃ ārabbha bhāsitaṃ. Abhayo bhante rājakumāro bhagavantaṃ upasaṅkamitvā etadavoca ‘‘bhāseyya nu kho bhante tathāgato taṃ vācaṃ, yā sā vācā paresaṃ appiyā amanāpā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Bahuvedanīyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',206,replace(replace('<p class="bodytext">bahuvedanīyasuttaṃ <a name="M1.0206"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantañca udāyiṃ pañcakaṅgañca thapatiṃ ārabbha bhāsitaṃ, pañcakaṅgo bhante thapati āyasmantaṃ udāyiṃ upasaṅkamitvā vedanaṃ pucchi, āyasmā bhante udāyī pañcakaṅgaṃ thapatiṃ etadavoca ‘‘tisso kho thapati vedanā vuttā bhagavatā sukhā vedanā dukkhā vedanā adukkhamasukhā vedanā’’ti. Evaṃ vutte bhante pañcakaṅgo thapati āyasmantaṃ udāyiṃ etadavoca ‘‘na kho bhante udāyi tisso vedanā vuttā bhagavatā, dve vedanā vuttā bhagavatā sukhā vedanā dukkhā vedanā, yāyaṃ bhante adukkhamasukhā vedanā, santasmiṃ esā paṇīte sukhe vuttā bhagavatā’’ti. Neva asakkhi kho bhante āyasmā udāyī pañcakaṅgaṃ thapatiṃ saññāpetuṃ, na pana asakkhi pañcakaṅgo thapati āyasmantaṃ udāyiṃ saññāpetuṃ. Assosi kho āyasmā ānando āyasmato udāyissa pañcakaṅgena thapatinā saddhiṃ imaṃ kathāsallāpaṃ, atha kho bhante āyasmā ānando yāvatako </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',207,replace(replace('<p class="bodytext">ahosi <a name="M1.0207"></a> āyasmato udāyissa pañcakaṅgena thapatinā saddhiṃ kathāsallāpo, taṃ sabbaṃ bhagavato ārocesi. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Apaṇṇakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> apaṇṇakasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante sālāyaṃ nāma kosalānaṃ brāhmaṇagāme sāleyyake brāhmaṇagahapatike ārabbha bhāsitaṃ. Sambahulā bhante aññatitthiyā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',208,replace(replace('<p class="bodytext">sāleyyakānaṃ <a name="M1.0208"></a> brāhmaṇagahapatikānaṃ attano attano micchādiṭṭhiyo paṭiggaṇhāpesuṃ uggaṇhāpesuṃ. Te pana bhante sāleyyakā brāhmaṇagahapatikā ekadiṭṭhiyampi patiṭṭhātuṃ na sakkhiṃsu. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',209,replace(replace('<p class="bodytext">tatra <a name="M1.0209"></a> āvuso bhagavatā paṭhamaṃ micchāvādo ca sammāvādo ca kathaṃ vibhajitvā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tatra bhante bhagavatā ‘‘santi gahapatayo eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino natthi dinnaṃ natthi yiṭṭhaṃ natthi hutaṃ natthi sukatadukkaṭānaṃ kammānaṃ phalaṃ vipāko’’ti evamādinā paṭhamaṃ micchāvādo ca sammāvādo ca vibhajitvā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',210,replace(replace('<p class="bodytext">tattha <a name="M1.0210"></a> āvuso bhagavatā micchāvādīnañca doso sammāvādīnañca guṇo kathaṃ vicāretvā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattha bhante bhagavatā ‘‘tatra gahapatayo yete samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino natthi dinnaṃ natthi yiṭṭhaṃ natthi hutaṃ natthi sukatadukkaṭānaṃ kammānaṃ phalaṃ vipāko’’ti evamādinā bhante micchāvādīnañca doso sammāvādīnañca guṇo bhagavatā vicāretvā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',212,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0212"></a> tattha bhagavatā viññuno purisapuggalassa paṭisañcikkhaṇā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',213,replace(replace('<p class="bodytext">tatra <a name="M1.0213"></a> gahapatayo viññū puriso iti paṭisañcikkhatīti evamādinā bhante tattha bhagavatā viññuno purisapuggalassa paṭisañcikkhaṇā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',215,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0215"></a> bhagavatā aññesupi micchāvādasammāvādesu apaṇṇakapaṭipadā vicāretvā pakāsitā, taṃ saṅkhepato kathesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yatheva bhante paṭhame vāde, evameva kho bhante akiriyavādādīsu catūsu ca micchāvādesu dosaṃ kiriyavādādīsu ca catūsu sammāvādesu guṇaṃ, tattha ca viññuno purisassa paṭisañcikkhaṇākāraṃ dassetvā bhagavatā apaṇṇakapaṭipadā pakāsitā.</p>\r\n<p class="subhead">Ambalaṭṭhikarāhulovādasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',216,replace(replace('<p class="bodytext">ambalaṭṭhikarāhulovādasuttaṃ <a name="M1.0216"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantaṃ rāhulaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Mahārāhulovādasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',217,replace(replace('<p class="bodytext">mahārāhulovādasuttaṃ <a name="M1.0217"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ rāhulaṃ ārabbha bhāsitaṃ. Āyasmā bhante rāhulo bhagavato ceva attano ca attabhāvasampattiṃ nissāya gehassitaṃ chandarāgaṃ uppādesi. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Cūḷamālukyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',218,replace(replace('<p class="bodytext">cūḷamālukyasuttaṃ <a name="M1.0218"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ mālukyaputtaṃ ārabbha bhāsitaṃ. Āyasmā bhante mālukyaputto attano pavivittassa rahogatassa paṭisallīnassa cetaso parivitakkaṃ bhagavato ārocesi. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Mahāmālukyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',221,replace(replace('<p class="bodytext">mahāmālukyasuttaṃ <a name="M1.0221"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tasmiṃyeva bhante sāvatthiyaṃ āyasmantaṃ mahāmālukyaputtaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Bhaddālisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',222,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0222"></a> bhagavatā…pe… sammāsambuddhena bhaddālisuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ bhaddāliṃ ārabbha bhāsitaṃ. Āyasmā bhante bhaddāli bhagavatā sikkhāpade paññāpiyamāne bhikkhusaṅghe sikkhaṃ samādiyamāne anussāhaṃ pavedesi. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',223,replace(replace('<p class="bodytext">Accayo <a name="M1.0223"></a> maṃ bhante accagamā yathābālaṃ yathāmūḷhaṃ yathāakusalaṃ, yohaṃ bhagavatā sikkhāpade paññāpiyamāne bhikkhusaṅghe sikkhaṃ samādiyamāne anussāhaṃ pavedesiṃ, tassa me bhante bhagavā accayaṃ accayato paṭiggaṇhātu āyatiṃ saṃvarāya –</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',225,replace(replace('<p class="bodytext">atha <a name="M1.0225"></a> kho āvuso āyasmā bhaddāli bhagavantaṃ kīdisaṃ pañhaṃ pucchi, kathañcassa bhagavā taṃ vibhajitvā byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha kho bhante āyasmā bhaddāli ‘‘ko nu kho bhante hetu, ko paccayo, yena midhekaccaṃ bhikkhuṃ pasayu pasayu kāraṇaṃ karontī’’ti evamādinā bhagavantaṃ pañhaṃ apucchi. Bhagavā ca bhante ‘‘idha bhaddāli ekacco bhikkhu abhiṇhāpattiko hoti, āpattibahulo’’ evamādinā āyasmato bhaddālissa vibhajitvā byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',228,replace(replace('<p class="bodytext">tadāpi <a name="M1.0228"></a> kho āvuso āyasmā bhaddāli punapi bhagavantaṃ kīdisaṃ pañhaṃ pucchi, kathañcassa bhagavā tampi vibhajitvā byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tadāpi bhante āyasmā bhaddāli ‘‘ko nu kho bhante hetu, ko paccayo, yena pubbe appatarāni ceva sikkhāpadāni ahesuṃ, bahutarā ca bhikkhū aññāya saṇḍahiṃsu. Ko pana bhante hetu, ko paccayo yena etarahi bahutarāni ceva sikkhāpadāni honti appatarā ca bhikkhū aññāya saṇḍahantī’’ti bhagavantaṃ punapi pañhaṃ apucchi. Bhagavā ca bhante ‘‘evametaṃ bhaddāli hoti, sattesu hāyamānesu saddhamme antaradhāyamāne bahutarāni honti appatarā ca bhikkhū aññāya saṇḍahantī’’ti, evamādinā āyasmato bhaddālissa vibhajitvā vibhajitvā byākatā.</p>\r\n<p class="subhead">Laṭukikopamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',1,replace(replace('<p class="bodytext">laṭukikopamasuttaṃ <a name="M2.0001"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṅguttarāpesu bhante āpaṇe nāma aṅguttarāpānaṃ nigame āyasmantaṃ udāyiṃ ārabbha bhāsitaṃ. Āyasmā bhante udāyī bhagavantaṃ upasaṅkamitvā etadavoca ‘‘idha mayhaṃ bhante rahogatassa paṭisallīnassa evaṃ cetaso parivitakko udapādi ‘‘bahūnaṃ vata no bhagavā dukkhadhammānaṃ apahattā, bahūnaṃ vata no bhagavā sukhadhammānaṃ upahattā, bahūnaṃ vata no bhagavā akusalānaṃ dhammānaṃ apahattā, bahūnaṃ vata no bhagavā kusalānaṃ dhammānaṃ upahattā’ti’’. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',4,replace(replace('<p class="bodytext">atha <a name="M2.0004"></a> kho āvuso bhagavā kīdisī upamāyo dassetvā bhikkhūnaṃ ovādaṃ adāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha kho bhante bhagavā laṭukikopamaṃ hatthināgopamaṃ daliddapurisopamaṃ gahapatikopamanti catasso upamāyo dassetvā bhikkhūnaṃ ovādamadāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',8,replace(replace('<p class="bodytext">evañcāvuso <a name="M2.0008"></a> bhagavā catūhi upamāhi bhikkhūnaṃ ovādaṃ datvā kathaṃ uttari dhammadesanaṃ pavaḍḍhesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> evaṃ kho bhante bhagavā catūhi upamāhi bhikkhūnaṃ ovādaṃ datvā ‘‘cattāro me udāyi puggalā santo saṃvijjamānā lokasmiṃ’’nti evamādinā uttari bhikkhūnaṃ dhammakathaṃ pavaḍḍhesi.</p>\r\n<p class="subhead">Cātumasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',10,replace(replace('<p class="bodytext">cātumasuttaṃ <a name="M2.0010"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> cātumāyaṃ bhante āyasmantānaṃ sāriputtamoggalānattherānaṃ saddhivihārike adhunā pabbajite sambahule bhikkhū ārabbha bhāsitaṃ. Sāriputtamoggallānappamukhāni bhante pañcamattāni bhikkhusatāni cātumaṃ anuppattāni honti bhagavantaṃ dassanāya, te ca āgantukā bhikkhū nevāsikehi bhikkhūhi saddhiṃ paṭisammodamānā senāsanāni paññāpayamānā pattacīvarāni paṭisāmayamānā uccāsaddā mahāsaddā ahesuṃ. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',13,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0013"></a> tattha bhagavā bhikkhūnaṃ ovādaṃ adāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> cattārimāni bhikkhave bhayāni udakorohante pātikaṅkhitabbānīti evamādinā bhante bhagavā tattha bhikkhūnaṃ ovādaṃ adāsi.</p>\r\n<p class="subhead">Naḷakapānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',17,replace(replace('<p class="bodytext">naḷakapānasuttaṃ <a name="M2.0017"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> naḷakapāne bhante anuruddhattherappamukhe sambahule abhiññāte abhiññāte kulaputte ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Goliyānisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',18,replace(replace('<p class="bodytext">goliyānisuttaṃ <a name="M2.0018"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantaṃ goliyāniṃ ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ. Āyasmā bhante goliyāni āraññiko padasamācāro saṅghamajjhe osaṭo hoti kenacideva karaṇīyena, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Kīṭāgirisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',19,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0019"></a> bhagavatā…pe… sammāsambuddhena kīṭāgirisuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kāsīsu bhante kīṭāgirismiṃ kāsīnaṃ nigame assajipunabbasuke bhikkhū ārabbha bhāsitaṃ. Assajipunabbasukā bhante bhikkhū bhikkhūhi sahadhammikaṃ vuccamānā evamāhaṃsu ‘‘mayaṃ kho āvuso sāyañceva bhuñjāma pāto ca divā ca vikāle, te mayaṃ sāyañceva bhuñjamānā pāto ca divā ca vikāle appābādhatañca sañjānāma appātaṅkatañca lahuṭṭhānañca balañca phāsuvihārañca, te mayaṃ kiṃ sandiṭṭhikaṃ hitvā kālikaṃ anudhāvissāma, sāyañceva mayaṃ bhuñjissāma pāto ca divā ca vikāle’’ti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Tevijjavacchasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',21,replace(replace('<p class="bodytext">tevijjavacchasuttaṃ <a name="M2.0021"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante vacchagottaṃ paribbājakaṃ ārabbha bhāsitaṃ. Vacchagotto bhante paribbājako bhagavantaṃ etadavoca ‘‘sutaṃ me bhante samaṇo gotamo sabbaññū sabbadassāvī aparisesaṃ ñāṇadassanaṃ paṭijānāti, carato ca me tiṭṭhato ca suttassa ca jāgarassa ca satataṃ samitaṃ ñāṇadassanaṃ paccupaṭṭhita’’nti. Ye te bhante evamāhaṃsu ‘‘samaṇo gotamo sabbaññū sabbadassāvī aparisesaṃ ñāṇadassanaṃ paṭijānāti, carato ca me tiṭṭhato ca suttassa ca jāgarassa ca satataṃ samitaṃ ñāṇadassanaṃ paccupaṭṭhita’’nti. Kacci te bhante bhagavato vuttavādino, na ca bhagavantaṃ abhūtena abbhācikkhanti, dhammassa cānudhammaṃ byākaronti, na ca koci sahadhammiko vādānuvādo gārayhaṃ ṭhānaṃ āgacchatī’’ti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Aggivacchasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',23,replace(replace('<p class="bodytext">aggivacchasuttaṃ <a name="M2.0023"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante vacchagottaṃ paribbājakaṃ bhāsitaṃ. Vacchagotto bhante paribbājako bhagavantaṃ upasaṅkamitvā ‘‘kiṃ nu kho bho gotama ‘sassato loko idameva saccaṃ moghamañña’nti evaṃdiṭṭhi bhavaṃ gotamo’’ti evamādikaṃ pañhaṃ apucchi. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Mahāvacchasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> mahāvacchasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante vacchagottaṃyeva paribbājakaṃ ārabbha bhāsitaṃ. Vacchagotto bhante paribbājako bhagavantaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',24,replace(replace('<p class="bodytext">upasaṅkamitvā <a name="M2.0024"></a> etadavoca ‘‘dīgharattāhaṃ bhotā gotamena sahakathī, sādhu me bhavaṃ gotamo saṃkhittena kusalākusalaṃ desetū’’ti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',25,replace(replace('<p class="bodytext">evaṃ <a name="M2.0025"></a> vutte kho āvuso vacchagotto paribbājako imasmiṃ dhammavinaye pasanno hutvā kīdisaṃ pasannākāraṃ akāsi, kathañcassa dhammābhisamayo ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> evaṃ vutte bhante vacchagotto paribbājako ‘‘sace hi bho gotama imaṃ dhammaṃ bhavaṃyeva gotamo ārādhako abhavissa, no ca kho bhikkhū ārādhakā abhavissaṃsu, evamidaṃ brahmacariyaṃ aparipūraṃ abhavissa tenaṅgenā’’ti evamādinā imasmiṃ dhammavinaye pasanno pasannākāramakāsi. Yāva arahattañcassa dhammābhisamayo ahosi.</p>\r\n<p class="subhead">Dīghanakhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',28,replace(replace('<p class="bodytext">dīghanakhasuttaṃ <a name="M2.0028"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante dīghanakhaṃ paribbājakaṃ ārabbha bhāsitaṃ. Dīghanakho bhante paribbājako bhagavantaṃ upasaṅkamitvā etadavoca ‘‘ahañhi bho gotama evaṃvādī evaṃdiṭṭhi sabbaṃ me nakkhamatī’’ti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',31,replace(replace('<p class="bodytext">imasmiṃ <a name="M2.0031"></a> ca panāvuso veyyākaraṇasmiṃ bhaññamāne kesaṃ puggalānaṃ visesādhigamo ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imasmiñca pana bhante veyyākaraṇasmiṃ bhaññamāne āyasmato sāriputtassa anupādāya āsavehi cittaṃ vimucci, dīghanakhassa pana paribbājakassa tasmiṃyeva āsane virajaṃ vītamalaṃ dhammacakkhuṃ udapādi yaṃkiñci samudayadhammaṃ, sabbaṃ taṃ nirodhadhamma’’nti.</p>\r\n<p class="subhead">Māgaṇḍiyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',33,replace(replace('<p class="bodytext">māgaṇḍiyasuttaṃ <a name="M2.0033"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kurūsu bhante kammāsadhamme nāma kurūnaṃ nigame bhāradvāja gottassa brāhmaṇassa agyāgāre tiṇasanthārake māgaṇḍiyaṃ paribbājakaṃ ārabbha bhāsitaṃ. Māgaṇḍiyo bhante paribbājako bhagavantaṃ ‘‘bhūnahu samaṇo gotamo’’ti vadesi, tasmiṃ bhante vaṭṭhusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',36,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0036"></a> bhagavā tattha māgaṇḍiyassa paribbājakassa samanuyuñjitvā samanuyuñjitvā dhammaṃ desesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘cakkhuṃ kho māgaṇḍiya rūpārataṃ rūpasammuditaṃ, taṃ tathāgatassa dantaṃ guttaṃ rakkhitaṃ saṃvutaṃ, tassa ca saṃvarāya dhammaṃ desetī’’ti evamādinā bhante tattha bhagavā māgaṇḍiyassa paribbājakassa samanuyuñjitvā samanuyuñjitvā dhammaṃ desesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',39,replace(replace('<p class="bodytext">evaṃ <a name="M2.0039"></a> kho āvuso māgaṇḍiyena paribbājakena ‘‘nakiñci bho gotamā’’ti yathābhūtaṃ paṭissute kathaṃ bhagavā attanopi na kiñci kenacipi vattabbataṃ pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> evaṃ kho bhante māgaṇḍiyena paribbājakena nakiñci bhogotamāti yathābhūtaṃ paṭissute ‘‘ahaṃ kho pana māgaṇḍiya pubbe agāriyabhūto samāno pañcahi kāmaguṇehi samappito samaṅgībhūto paricāresiṃ cakkhuviññeyyahi rūpehi iṭṭhehi manāpehi piyarūpehi kāmūpasaṃhitehi rajanīyehī’’ti evamādinā bhagavā attanopi na kiñci kenacipi vattabbataṃ pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',42,replace(replace('<p class="bodytext">imasmiṃ <a name="M2.0042"></a> sutte pariyosānapucchaṃ pucchissāmi imañca panāvuso dhammadesanaṃ sutvā māgaṇḍiyo paribbājako imasmiṃ dhammavinaye pasanno hutvā kīdisaṃ pasannākāramakāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',43,replace(replace('<p class="bodytext">imaṃ <a name="M2.0043"></a> ca pana bhante dhammadesanaṃ sutvā māgaṇḍiyo paribbājako ‘abhikkantaṃ bho gotama abhikkantaṃ bho gotamā’’ti evamādinā imasmiṃ dhammavinaye pasanno pasannākāramakāsi.</p>\r\n<p class="subhead">Sandakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sandakasuttaṃ panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',44,replace(replace('<p class="bodytext">kosambiyaṃ <a name="M2.0044"></a> bhante sandakaṃ paribbājakaṃ ārabbha āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ. Sandako bhante paribbājako āyasmantaṃ ānandaṃ etadavoca ‘‘sādhuvata bhavantaṃyeva ānandaṃ paṭibhātu sake ācariya ke dhammikathā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',45,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0045"></a> tattha cattāro abrahmacariyavāsā āyasmatā ānandattherena dhammabhaṇḍāgārikena vicāretvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha sandaka ekacco satthā evaṃvādī hoti evaṃdiṭṭhi natthi dinnaṃ, natthi yiṭṭhaṃ, natthi hutaṃ, natthi sukatadukkaṭānaṃ kammānaṃ phalaṃvipāko, natthi ayaṃloko, natthi paroloko, natthi mātā, natthi pitā, natthi sattā opapātikāti</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',46,replace(replace('<p class="bodytext">Evamādinā <a name="M2.0046"></a> bhante tattha āyasmatā ānandattherena dhammabhaṇḍāgārikena cattāro abrahmacariyavāsā vicāretvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',49,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0049"></a> tattha cattāri anassāsikāni brahmacariyāni āyasmatā ānandattherena dhammabhaṇḍāgārikena vicāretvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',50,replace(replace('<p class="bodytext">idha <a name="M2.0050"></a> sandaka ekacco satthā sabbaññū sabbadassāvī aparisesaṃ ñāṇadassanaṃ paṭijānāti ‘‘carato ca me tiṭṭhato ca suttassa ca jāgarassa ca satataṃ samitaṃ ñāṇadassanaṃ paccupaṭṭhita’’nti evamādinā bhante tattha āyasmatā ānandattherena dhammabhaṇḍāgārikena cattāri anassāsikāni brahmacariyāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atha</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',54,replace(replace('<p class="bodytext"><a name="M2.0054"></a> Kho āvuso sandako paribbājako āyasmantaṃ ānandattheraṃ kathaṃ pucchi, tathañcassāyasmānandatthero byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha kho bhante sandako paribbājako ‘‘so pana bho ānanda satthā kiṃvādī kiṃ akkhāyī’’ti evamādinā āyasmantaṃ ānandattheraṃ pucchi, āyasmā ca bhante ānandatthero ‘‘idha sandaka tathāgato loke uppajjati arahaṃ sammāsambuddho vijjācaraṇasampanno’’ti evamādinā sandakassa paribbājakassa vibhajitvā vibhajitvā byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',56,replace(replace('<p class="bodytext">imañca <a name="M2.0056"></a> panāvuso dhammadesanaṃ sutvā sandako paribbājako imasmiṃ dhammavinaye pasanno kīdisaṃ pasannākāramakāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',57,replace(replace('<p class="bodytext">imañca <a name="M2.0057"></a> bhante dhammadesanaṃ sutvā sandako paribbājako ‘‘acchariyaṃ bho ānanda, abbhutaṃ bho ānanda, na ca nāma sadhammokkaṃsanā bhavissati, na paradhammavambhanā, āyatane ca dhammadesanā, tāva bahukā ca niyyātāro paññāyissantī’’ti evamādinā imasmiṃ dhammavinaye pasanno pasannākāramakāsi.</p>\r\n<p class="subhead">Mahāsakuludāyīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',58,replace(replace('<p class="bodytext">mahāsakuludāyisuttaṃ <a name="M2.0058"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sakuludāyiṃ paribbājakaṃ ārabbha bhāsitaṃ. Sakuludāyī bhante paribbājako purimāni divasāni purimatarāni kotūhalasālāyaṃ nānātitthiyānaṃ samaṇabrāhmaṇānaṃ yāvatako ahosi bhagavantañca cha ca satthāro ārabbha kathāsallāpo, taṃ sabbaṃ bhagavato ārocesi. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Samaṇamuṇḍikasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',62,replace(replace('<p class="bodytext">samaṇamuṇḍikasuttaṃ <a name="M2.0062"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante uggāhamāna paribbājakaṃ samaṇamuṇḍikāputtaṃ ārabbha bhāsitaṃ. Uggāhamāno bhante paribbājako samaṇamuṇḍikāputto pañcakaṅgaṃ tapatiṃ etadavoca ‘‘catūhi kho ahaṃ gahapati dhammehi samannāgataṃ purisapuggalaṃ paññapemi sampannakusalaṃ paramakusalaṃ uttamapattipattaṃ samaṇaṃ ayojjha’’nti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Cūḷasakuludāyīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',63,replace(replace('<p class="bodytext">cūḷasakuludāyīsuttaṃ <a name="M2.0063"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',64,replace(replace('<p class="bodytext">rājagahe <a name="M2.0064"></a> bhante sakuludāyiṃ paribbājakaṃ ārabbha bhāsitaṃ, sakuludāyī bhante paribbājako bhagavantaṃ etadavoca ‘‘yadāhaṃ bhante imaṃ parisaṃ anupasaṅkanto homi, athāyaṃ parisā anekavihitaṃ tiracchānakathaṃ kathenti nisinnā hoti. Yadā ca kho ahaṃ bhante imaṃ parisaṃ upasaṅkamanto homi, athāyaṃ parisā mamaññeva mukhaṃ ullokentī nisinnā hoti ‘yaṃ no samaṇo udāyī dhammaṃ bhāsissati, taṃ sossāmā’ti, yadā pana bhante bhagavā imaṃ parisaṃ upasaṅkanto hoti, athāhañceva ayañca parisā bhagavato mukhaṃ ullokentā nisinnā homa yaṃ no bhagavā dhammaṃ bhāsissati, taṃ sossāmā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Vekhanasasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',65,replace(replace('<p class="bodytext">vekhanasasuttaṃ <a name="M2.0065"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante vekhanasaṃ paribbājakaṃ ārabbha bhāsitaṃ, vekhanaso bhante paribbājako bhagavato santike udānaṃ udānesi ‘‘ayaṃ paramo vaṇṇo ayaṃ paramo vaṇṇo’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Ghaṭikārasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',66,replace(replace('<p class="bodytext">ghaṭikārasuttaṃ <a name="M2.0066"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante ghaṭikārassa kumbhakārassa gharavatthupadese āyasmantaṃ ānandaṃ ārabbha bhāsitaṃ. Bhagavā bhante tasmiṃ bhūmipadese sitaṃ pātvākāsi, āyasmā ca ānando bhagavantaṃ etadavoca ‘‘ko nu kho bhante hetu ko paccayo bhagavato sitassa pātukammāya, na akāraṇena tathāgatā sitaṃ pātukarontī’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Raṭṭhapālasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',67,replace(replace('<p class="bodytext">raṭṭhapālasuttaṃ <a name="M2.0067"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kurūsu bhante rājānaṃ korabyaṃ ārabbha āyasmatā raṭṭhapālattherena bhāsitaṃ. Rājā bhante korabyo āyasmantaṃ raṭṭhapālaṃ etadavoca ‘‘cattārimāni bho raṭṭhapāla pārijuññāni, yehi pārijuññehi samannāgatā idhekacce kesamassuṃ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anāgāriyaṃ pabbajantī’’ti evamādikaṃ vacanaṃ avoca, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',72,replace(replace('<p class="bodytext">atha <a name="M2.0072"></a> kho āvuso āyasmā raṭṭhapālatthero rañño korabyassa kathaṃ paṭhamaṃ dhammuddesaṃ vitthāretvā pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> atha kho bhante āyasmā raṭṭhapālatthero ‘‘taṃ kiṃ maññasi mahārāja tvaṃ vīsativassuddesikopi paṇṇavīsativassuddesikopi hatthismimpi katāvī assasmimpi katāvī rathasmimpi katāvī dhanusmimpi katāvī tharusmimpi katāvī ūrubalī bāhubalī alamatto saṅgāmāvacaro’’ti evamādinā paṭhamaṃ dhammuddesaṃ vitthāretvā pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',74,replace(replace('<p class="bodytext">kathaṃ <a name="M2.0074"></a> panāvuso āyasmā raṭṭhapālatthero rañño korabyassa dutiyampi dhammuddesaṃ vitthāretvā pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',75,replace(replace('<p class="bodytext">‘‘taṃ <a name="M2.0075"></a> kiṃ maññasi mahārāja, atthi te koci anusāyiko ābādho’’ti evamādinā bhante āyasmā raṭṭhapālo rañño korabyassa dutiyaṃ dhammuddesaṃ vitthāretvā pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',76,replace(replace('<p class="bodytext">kathaṃ <a name="M2.0076"></a> panāvuso āyasmā raṭṭhapālatthero rañño korabyassa tatiyampi dhammuddesaṃ vitthāretvā pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',77,replace(replace('<p class="bodytext">‘‘taṃ <a name="M2.0077"></a> kiṃ maññasi mahārāja, yathā tvaṃ etarahi pañcahi kāmaguṇehi samappito samaṅgībhūto paricāresi, lacchasi tvaṃ paratthāpi evamevāhaṃ imeheva pañcahi kāmaguṇehi samappito samaṅgībhūto paricāremī’’ti evamādinā bhante āyasmā raṭṭhapālo rañño korabyassa tatiyaṃ dhammuddesaṃ vitthāretvā pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',78,replace(replace('<p class="bodytext">catutthampi <a name="M2.0078"></a> kho āvuso dhammuddesaṃ āyasmā raṭṭhapālatthero rañño korabyassa kathaṃ vitthāretvā pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘taṃ kiṃ maññasi mahārāja, phītaṃ kuruṃ ajjhāvasasī’’ti evamādinā bhante āyasmā raṭṭhapālatthero rañño korabyassa catutthaṃ dhammuddesaṃ vitthāretvā pakāsesi.</p>\r\n<p class="subhead">Maghadevasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',80,replace(replace('<p class="bodytext">maghadevasuttaṃ <a name="M2.0080"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> mithilāyaṃ bhante maghadevaambavane āyasmantaṃ ānandaṃ ārabbha bhāsitaṃ. Bhagavā bhante aññatarasmiṃ padese sitaṃ pātvākāsi, āyasmā ānando bhagavantaṃ etadavoca ‘‘ko nu kho bhante ko paccayo bhagavato sitassa pātukammāya, na akāraṇena tathāgatā sitaṃ pātukarontī’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Madhurasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',81,replace(replace('<p class="bodytext">madhurasuttaṃ <a name="M2.0081"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> madhurāyaṃ bhante rājānaṃ mādhuraṃ avantiputtaṃ ārabbha āyasmatā mahākaccānattherena bhāsitaṃ, rājā bhante mādhuro avantiputto āyasmantaṃ mahākaccānaṃ etadavoca ‘‘brahmaṇā bho kaccāna evamāhaṃsu brāhmaṇova seṭṭho vaṇṇo, hīno añño vaṇṇo. Brāhmaṇova sukko vaṇṇo, kaṇho añño vaṇṇo. Brāhmaṇāva </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',82,replace(replace('<p class="bodytext">sujjhanti<a name="M2.0082"></a>, no abrāhmaṇā. Brāhmaṇāva brahmuno puttā orasā mukhato jātā brahmajā brahmanimmitā brahmadāyādāti. Idha bhavaṃ kaccāno kimakkhāyī’’ti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Bodhirājakumārasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',83,replace(replace('<p class="bodytext">bodhirājakumārasuttaṃ <a name="M2.0083"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhaggesu bhante susumāragire bodhiṃ rājakumāraṃ ārabbha bhāsitaṃ. Bodhi bhante rājakumāro bhagavantaṃ etadavoca ‘‘mayaṃ kho bhante evaṃ hoti. Na kho sukhena sukhaṃ adhigantabbaṃ, dukkhena kho sukhaṃ adhigantabba’’nti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',84,replace(replace('<p class="bodytext">evaṃ <a name="M2.0084"></a> vutte kho āvuso bodhi rājakumāro bhagavantaṃ kathaṃ pucchi, kathañcassa bhagavā byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> evaṃ vutte bhante bodhirājakumāro bhagavantaṃ etadavoca ‘‘kīva cirena nu kho bhante bhikkhu tathāgataṃ vināyakaṃ labhamāno yassatthāya kulaputtā sammadeva agārasmā anagāriyaṃ pabbajanti, tadanuttaraṃ brahmacariyapariyosānaṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja vihareyyā’’ti, bhagavā ca bhante ‘‘tena hi rājakumāra taṃyevettha paṭipucchissāmi, yathā te khameyya, tathā naṃ byākareyyāsī’’ti, evamādinā vibhajitvā byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',88,replace(replace('<p class="bodytext">imañca <a name="M2.0088"></a> panāvuso dhammadesanaṃ sutvā bodhirājakumāro imissaṃ dhammadesanāyaṃ pasanno kīdisaṃ pasannākāramakāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imañca pana bhante dhammadesanaṃ sutvā bodhirājakumāro ‘‘aho buddho aho dhammo aho dhammassa svākkhātatā, yatra hi nāma sāyamanusiṭṭho, pāto visesaṃ adhigamissati, pātamanusiṭṭho sāyaṃ visesaṃ adhigamissatī’’ti evamādinā imissaṃ dhammadesanāyaṃ pasanno pasannākāramakāsi.</p>\r\n<p class="subhead">Aṅgulimālasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',91,replace(replace('<p class="bodytext">aṅgulimālasuttaṃ <a name="M2.0091"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ aṅgulimālattheraṃ ārabbha bhāsitaṃ. Āyasmā bhante aṅgulimālatthero bhagavantaṃ etadavoca ‘‘idhāhaṃ bhante pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya sāvatthiṃ piṇḍāya pāvisiṃ, addasaṃ kho ahaṃ bhante sāvatthiyaṃ sapadānaṃ piṇḍāya caramāno aññataraṃ itthiṃ mūḷhagabbhaṃ vighātagabbhaṃ disvāna mayhaṃ etadahosi ‘‘kilissanti vata bho sattā, kilissanti vata bho sattā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Piyajātikasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',92,replace(replace('<p class="bodytext">piyajātikasuttaṃ <a name="M2.0092"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ gahapatiṃ ārabbha bhāsitaṃ, sāvatthiyaṃ bhante aññatarassa gahapatissa ekaputtako piyo manāpo kālaṅkato hoti, tassa kālaṃ kiriyāya neva kammantā paṭibhanti, na bhattaṃ paṭibhāti, so āḷāhanaṃ gantvā kandati ‘‘kahaṃ ekaputtaka kahaṃ ekaputtakā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Bāhitikasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',93,replace(replace('<p class="bodytext">bāhitika <a name="M2.0093"></a> suttaṃ panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante rājānaṃ pasenadiṃ kosalaṃ ārabbha āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ. Rājā bhante pasenadi kosalo āyasmantaṃ ānandaṃ etadavoca ‘‘kiṃ nu kho bhante ānanda sobhagavā tathārūpaṃ kāyasamācāraṃ samācareyya, yvassa kāyasamācāro opārambho samaṇehi brāhmaṇehī’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Dhammacetiyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',94,replace(replace('<p class="bodytext">dhammacetiyasuttaṃ <a name="M2.0094"></a> panāvuso bhagavatā kattha kena saddhiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante medāḷupe nāma sakyānaṃ nigame raññā pasenadinā kosalena saddhiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Kaṇṇakatthalasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',95,replace(replace('<p class="bodytext">kaṇṇakatthalasuttaṃ <a name="M2.0095"></a> panāvuso bhagavatā kattha kena saddhiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> uruññāyaṃ bhante kaṇṇakatthale migadāye raññā pasenadinā kosalena saddhiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Brahmāyusutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tenāvuso bhagavatā…pe… sammāsambuddhena brahmāyusuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',96,replace(replace('<p class="bodytext">videhesu <a name="M2.0096"></a> bhante mithilāyaṃ maghadevaambavane brahmāyuṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Brahmāyu bhante brāhmaṇo bhagavantaṃ upasaṅkamitvā aṭṭhapañhāni pucchi, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',97,replace(replace('<p class="bodytext">tattha <a name="M2.0097"></a> āvuso uttaro māṇavo kathaṃ bhagavato dvattiṃsamahāpurisalakkhaṇehi samannāgatataṃ attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattha bhante uttaro māṇavo ‘‘suppatiṭṭhitapādo kho pana so bhavaṃ gotamo, idampi tassa bhoto gotamassa mahāpurisassa mahāpurisalakkhaṇaṃ bhavatī’’ti evamādinā bhagavato dvattiṃsamahāpurisalakkhaṇehi samannāgatataṃ attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',101,replace(replace('<p class="bodytext">aparampi <a name="M2.0101"></a> āvuso uttaro māṇavo bhagavato gamanakāle kīdisaṃ pāsādikaṃ ākāraṃ disvā ca sallakkhetvā ca attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aparampi bhante uttaro māṇavo bhagavato gamanakāle paṭhamaṃ dakkhiṇaṃ pāduddharaṇādikaṃ ākāraṃ disvā ca sallakkhetvā ca attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',103,replace(replace('<p class="bodytext">aparampi <a name="M2.0103"></a> āvuso uttaro māṇavo bhagavato antaragharaṃ pavisanakāle kīdisaṃ pāsādikaṃ ākāraṃ disvā ca sallakkhetvā ca attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aparampi bhante uttaro māṇavo bhagavato antaragharaṃ pavisanakāle na kāyassa unnamanādikaṃ pāsādikaṃ disvā ca sallakkhetvā ca attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',104,replace(replace('<p class="bodytext">aparampi <a name="M2.0104"></a> āvuso uttaro māṇavo bhagavato bhojanakāle kīdisaṃ pāsādikaṃ ākāraṃ disvā ca sallakkhetvā ca attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aparampi bhante uttaro māṇavo bhagavato bhojanakāle pattodakādikaṃ paṭiggahaṇādikāle na pattassa unnamanādikaṃ bhagavato pāsādikaṃ ākāraṃ disvā ca sallakkhetvā ca attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',106,replace(replace('<p class="bodytext">aparampi <a name="M2.0106"></a> āvuso uttaro māṇavo bhagavato bhuttāvikāle kīdisaṃ pāsādikaṃ ākāraṃ disvā ca sallakkhetvā ca attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aparampi bhante uttaro māṇavo bhagavato bhuttāvikāle pattodanādikaṃ paṭiggaṇhādikāle na pattassa unnamanādikaṃ pāsādikaṃ ākāraṃ disvā ca sallakkhetvā ca attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',107,replace(replace('<p class="bodytext">aparampi <a name="M2.0107"></a> āvuso uttaro māṇavo bhagavato cīvaradhāraṇe ca ārāmagatakāle ca dhammadesanākāle ca sabbairiyāpathesu ca kīdisaṃ pāsādikaṃ ākāraṃ disvā ca sallakkhetvā ca attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',108,replace(replace('<p class="bodytext">aparampi <a name="M2.0108"></a> bhante uttaro māṇavo bhagavato cīvaradhāraṇakāle ca ārāmagatakāle ca sabbesu ca iriyāpathesu na accukkaṭṭhādikaṃ pāsādikaṃ ākāraṃ disvā ca sallakkhetvā ca attano ācariyassa brahmāyussa brāhmaṇassa sampaṭivedesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',110,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0110"></a> brahmāyu brahmaṇo bhagavantaṃ pucchi, kathañcassa bhagavā byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘kathaṃ kho brāhmaṇo hoti, kathaṃ bhavati vedagū. Tevijjo bho kathaṃ hoti, sottiyo kinti vuccati. Arahaṃ bho kathaṃ hoti, kathaṃ bhavati kevalī. Muni ca bho kathaṃ hoti, buddho kinti pavuccatī’’ti – evaṃ kho bhante brahmāyu brāhmaṇo bhagavantaṃ pañhaṃ apucchi. Bhagavā bhante–</p>\r\n<p class="gatha1">‘‘Pubbenivāsaṃ yo vedi, saggāpāyañca passati;</p>\r\n<p class="gatha2">Atho jātikkhayaṃ patto, abhiññāvosito muni;</p>\r\n<p class="gatha3">Cittaṃ visuddhaṃ jānāti, muttaṃ rāgehi sabbaso;</p>\r\n<p class="gatha2">Pahīnajātimaraṇo, brahmacariyassa kevalī;</p>\r\n<p class="gathalast">Pāragū sabbadhammānaṃ, buddho tādī pavuccatī’’ti –</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavā brahmāyussa brāhmaṇassa vibhajitvā byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',111,replace(replace('<p class="bodytext">imañca <a name="M2.0111"></a> panāvuso bhagavato byākaraṇaṃ sutvā brahmāyu brāhmaṇo bhagavati kīdisaṃ nipaccakāraṃ akāsi, kathañcassa bhagavā punapi anupubbiṃ dhammakathaṃ kathesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',112,replace(replace('<p class="bodytext">imañca <a name="M2.0112"></a> pana bhante bhagavato byākaraṇaṃ sutvā brahmāyu brāhmaṇo uṭṭhāyāsanā ekaṃsaṃ uttarāsaṅgaṃ karitvā bhagavato pādesu sirasā nipatitvā bhagavato pādāni mukhena ca paricumbati, pāṇīhi ca parisambāhati, nāmañca sāveti ‘‘brahmāyu ahaṃ bho gotama brāhmaṇo, brahmāyu ahaṃ bho gotama brāhmaṇo’’ti, evaṃ paramanipaccakāraṃ akāsi, bhagavā ca bhante dānakathaṃ sīlakathaṃ saggakathaṃ kāmānaṃ ādīnavaṃ okāraṃ saṃkilesaṃ nekkhamme ānisaṃsaṃ pakāsesi, evaṃ kho bhante brahmāyussa brāhmaṇassa bhagavā anupubbiṃ kathaṃ kathesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',113,replace(replace('<p class="bodytext">imañca <a name="M2.0113"></a> panāvuso dammadesanaṃ sutvā brahmāyussa brāhmaṇassa kīdiso dhammābhisamayo ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',114,replace(replace('<p class="bodytext">imañca <a name="M2.0114"></a> pana bhante dhammadesanaṃ sutvā brahmāyussa brāhmaṇassa tasmiṃyeva āsane virajaṃ vītamalaṃ dhammacakkhuṃ udapādi ‘‘yaṃkiñci samudayadhammaṃ sabbaṃ taṃ nirodhadhamma’’nti.</p>\r\n<p class="subhead">Selasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> selasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṅguttarāpesu bhante āpaṇe nāma aṅguttarāpānaṃ nigameselaṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Selo bhante brāhmaṇo sapariso bhagavantaṃ upasaṅkamitvā bhagavantaṃ sammukhā sāruppāhi gāthāhi abhitthavi, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',116,replace(replace('<p class="gatha1">Paripuṇṇakāyo <a name="M2.0116"></a> suruci, sujāto cārudassano;</p>\r\n<p class="gatha2">Suvaṇṇavaṇṇosi bhagavā, susukkadāṭhosi vīriyavā;</p>\r\n<p class="gatha3">Narassa hi sujātassa, ye bhavanti viyañcanā;</p>\r\n<p class="gathalast">Sabbe te tava kāyasmiṃ, mahāpurisalakkhaṇā.</p>\r\n<p class="subhead">Assalāyanasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> assalāyanasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante assalāyanaṃ māṇavaṃ ārabbha bhāsitaṃ, assalāyano bhante māṇavo mahatā brāhmaṇagaṇena saddhiṃ bhagavantaṃ upasaṅkamitvā etadavoca ‘‘brāhmaṇā bho gotama evamāhaṃsu ‘brāhmaṇova seṭṭho vaṇṇo, hīno añño vaṇṇo. Brāhmaṇova sukko vaṇṇo, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',117,replace(replace('<p class="bodytext">kaṇho <a name="M2.0117"></a> añño vaṇṇo. Brāhmaṇāva sujjhanti, no abrāhmaṇā. Brāhmaṇāva brahmuno puttā orasā mukhato jātā brahmajā brahmanimmitā brahmadāyādā’ti, idha bhavaṃ gotamo kimāhā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Ghoṭamukhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',119,replace(replace('<p class="bodytext">ghoṭamukhasuttaṃ <a name="M2.0119"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bārāṇasiyaṃ bhante khemiyambavane ghoṭamukhaṃ brāhmaṇaṃ ārabbha āyasmatā udenena bhāsitaṃ. Ghoṭamukho bhante brāhmaṇo āyasmantaṃ udenaṃ caṅkamantaṃ anucaṅkamamāno evamāha ‘‘ambho samaṇa natthi dhammiko paribbajo, evaṃ me ettha hoti, tañca kho bhavantarūpānaṃ vā adassanā, yo vā panettha dhammā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Caṅkīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',120,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0120"></a> bhagavatā…pe… sammāsambuddhena caṅkīsuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante opāsāde nāma kosalānaṃ nigame kāpaṭikaṃ māṇavaṃ ārabbha bhāsitaṃ, kāpaṭiko bhante māṇavo bhagavantaṃ etadavoca ‘‘yadidaṃ bho gotama brāhmaṇānaṃ porāṇaṃ mantapadaṃ itihitihaparamparāya piṭakasampadāya, tattha ca brāhmaṇā ekaṃsena niṭṭhaṃ gacchanti ‘idameva saccaṃ moghamañña’nti, idha bhavaṃ gotamo kimāhā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Esukārīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',122,replace(replace('<p class="bodytext">esukārīsuttaṃ <a name="M2.0122"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante esukāriṃ brāhmaṇaṃ ārabbha bhāsitaṃ, esukārī bhante brāhmaṇo bhagavantaṃ upasaṅkamitvā etadavoca ‘‘brāhmaṇā bho gotama catasso pāricariyā paññapenti, brāhmaṇassa pāricariyaṃ paññapenti, khattiyassa pāricariyaṃ paññapenti, vessassa pāricariyaṃ paññapenti, suddassa pāricariyaṃ paññapentī’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Dhanañjānisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',125,replace(replace('<p class="bodytext">dhanañjānisuttaṃ <a name="M2.0125"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante dhanañjāniṃ brāhmaṇaṃ ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ, dhanañjāni bhante brāhmaṇo pamādavihāraṃ vihāsi, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',126,replace(replace('<p class="bodytext">atha <a name="M2.0126"></a> kho āvuso āyasmā sāriputtatthero dhammasenāpati kathaṃ dhanañjāniṃ brāhmaṇaṃ pucchi, kathañca so āyasmato sāriputtattherassa dhammasenāpatissa ārocesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘kaccāsi dhanañjāni appamatto’’ti, evaṃ kho bhante āyasmā sāriputtatthero dhammasenāpati dhanañjāniṃ brāhmaṇaṃ pucchi, dhanañjāni ca bhante brāhmaṇo ‘‘kuto bho sāriputta amhākaṃ appamādo, yesaṃ no mātāpitaro posetabbā’’ti, evamādinā āyasmato sāriputtassa dhammasenāpatissa ārocesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',128,replace(replace('<p class="bodytext">evaṃ <a name="M2.0128"></a> vutte kho āvuso āyasmā sāriputtatthero dhammasenāpati dhanañjānissa brāhmaṇassa kīdisaṃ dhammakathaṃ kathesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> evaṃ vutte bhante āyasmā sāriputtatthero dhammasenāpati ‘‘taṃ kiṃ maññasi dhanañjāni, idhekacco mātāpitūnaṃ hetu adhammacārī visamacārī assa, tamenaṃ adhammacariyā visamacariyāhetu nirayaṃ nirayapālā upakaḍḍheyyu’’nti, evamādinā dhanañjānissa brāhmaṇassa dhammakathaṃ kathesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',130,replace(replace('<p class="bodytext">puna <a name="M2.0130"></a>pi āvuso āyasmā sāriputtatthero dhammasenāpati kathaṃ dhanañjānissa brāhmaṇassa aparenapi pariyāyena anusāsaniṃ adāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> punapi bhante āyasmā sāriputtatthero dhammasenāpati ‘‘taṃ kiṃ maññasi dhanañjāni, yo vā mātāpitūnaṃ hetu adhammacārī visamacārī assa, yo vā mātāpitūnaṃ hetu dhammacārī samacārī assa, katamaṃ seyyo’’ti, evamādinā dhanañjānissa brāhmaṇassa aparenapi pariyāyena ovādamadāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',132,replace(replace('<p class="bodytext">aparabhāge <a name="M2.0132"></a>pi āvuso āyasmā sāriputtatthero dhammasenāpati kathaṃ dhanañjānissa brāhmaṇassa maraṇasamaye dhammakathaṃ kathesi, kathañcassa abhisamparāyo ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',133,replace(replace('<p class="bodytext">aparabhāge <a name="M2.0133"></a>pi bhante āyasmā sāriputtatthero dhammasenāpati dhanañjānissa brāhmaṇassa maraṇasamayepi cattāro brahmavihāre desesi, dhanañjāni ca bhante brāhmaṇo kāyassa bhedā paraṃ maraṇā brahmalokūpago ahosi.</p>\r\n<p class="subhead">Vāseṭṭhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',135,replace(replace('<p class="bodytext">vāseṭṭhasuttaṃ <a name="M2.0135"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> icchānaṅgale bhante vāseṭṭhaṃ māṇavaṃ ārabbha bhāsitaṃ. Vāseṭṭho bhante māṇavo bhagavantaṃ gāthāhi ajjhabhāsi –</p>\r\n<p class="gatha1">‘‘Anuññātapaṭiññātā, tevijjā mayamasmubho;</p>\r\n<p class="gathalast">Ahaṃ pokkharasātissa, tārukkhassāyaṃ māṇavo.</p>\r\n<p class="gatha1">Tevijjānaṃ yadakkhātaṃ, tatra kevalinesmase;</p>\r\n<p class="gatha2">Padakasmā veyyākaraṇā, jappe ācariyasādisā;</p>\r\n<p class="gathalast">Tesaṃ no jātivādasmiṃ, vivādo atthi gotama.</p>\r\n<p class="gatha1">Jātiyā brāhmaṇo hoti, bhāradvājo iti bhāsati;</p>\r\n<p class="gathalast">Ahañca kammunā brūmi, evaṃ jānāhi cakkhuma.</p>\r\n<p class="gatha1">Tena sakkoma ñāpetuṃ, aññaṃmaññaṃ mayaṃ ubho;</p>\r\n<p class="gathalast">Bhavantaṃ puṭṭhumāgamā, sambuddhaṃ iti vissutaṃ.</p>\r\n<p class="gatha1">Candaṃ yathā khayātītaṃ, pecca pañjalikā janā;</p>\r\n<p class="gathalast">Vandanā namassanti, evaṃ lokasmiṃ gotamaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',136,replace(replace('<p class="gatha1">Cakkhuṃ <a name="M2.0136"></a> loke samuppannaṃ, mayaṃ pucchāma gotamaṃ;</p>\r\n<p class="gatha2">Jātiyā brāhmaṇo hoti, udāhu bhavati kammunā;</p>\r\n<p class="gathalast">Ajānataṃ no pabrūhi, yathā jānemu brāhmaṇanti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Subhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',137,replace(replace('<p class="bodytext">subhasuttaṃ <a name="M2.0137"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante subhaṃ māṇavaṃ todeyyaputtaṃ ārabbha bhāsitaṃ. Subho bhante māṇavo todeyyaputto bhagavantaṃ upasaṅkamitvā etadavoca ‘‘brāhmaṇā bho gotama evamāhaṃsu’ gahaṭṭho ārādhako hoti ñāyaṃ dhammaṃ kusalaṃ, na pabbajito ārādhako hoti ñāyaṃ dhammaṃ kusalanti, idha bhavaṃ gotamo kimāhā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',138,replace(replace('<p class="bodytext">puna <a name="M2.0138"></a>pi āvuso subho māṇavo todeyyaputto kathaṃ bhagavantaṃ pucchi, kathañca bhagavā vibhajja byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘brāhmaṇā bho gotama evamāhaṃsu ‘mahaṭṭhamidaṃ mahākiccaṃ mahādhikaraṇaṃ mahāsamārambhaṃ, gharāvāsakammaṭṭhānaṃ mahapphalaṃ hoti. Appaṭṭhamidaṃ appakiccaṃ appādhikaraṇaṃ appasamārambhaṃ, pabbajjā kammaṭṭhānaṃ appaphalaṃ hotī’ti. Idha bhavaṃ gotamo kimāhā’’ti. Evaṃ kho bhante subho māṇavo todeyyaputto punapi bhagavantaṃ pucchi. Bhagavā ca bhante ‘‘etthāpi kho ahaṃ māṇava vibhajjavādā, nāhamettha ekaṃsavādoti’’ evamādinā vibhajja byākāsi.</p>\r\n<p class="subhead">Saṅgāravasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',141,replace(replace('<p class="bodytext">saṅgāravasuttaṃ <a name="M2.0141"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante cañcalikappe nāma gāme saṅgāravaṃ māṇavaṃ ārabbha bhāsitaṃ, saṅgāravo bhante māṇavo bhagavantaṃ etadavoca ‘‘santi kho bho gotama eke samaṇabrāhmaṇā diṭṭhadhammābhiññāvosānapāramippattā ādibrahmacariyaṃ paṭijānanti. Tatra bho gotama ye te samaṇabrāhmaṇā diṭṭhadhammābhiññāvosānapāramippattā ādibrahmacariyaṃ paṭijānanti, tesaṃ bhavaṃ gotamo katamo’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Devadahasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',145,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0145"></a> bhagavatā jānatā passatā arahatā sammāsambuddhena devadahasuttaṃ kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante devadahe nāma sakyānaṃ nigame samma hule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Pañcattayasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> pañcattayasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Kintisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',146,replace(replace('<p class="bodytext">kintisuttaṃ <a name="M2.0146"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pisinārāyaṃ bhante baliharaṇe nāma vanasaṇḍe sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',147,replace(replace('<p class="bodytext">tattha <a name="M2.0147"></a> āvuso bhagavā kathaṃ paṭhamaṃ bhikkhū paṭipucchitvā ovādamadāsi, yo bahujanassa atthāya hitāya sukhāya saṃvattati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattha bhante bhagavā ‘‘kinti vo bhikkhave mayi hoti, cīvarahetu vā samaṇo gotamo dhammaṃ deseti, piṇḍapātahetu vā, senāsanahetu vā, itibhavābhavahetu vā samaṇo gotamo dhammaṃ desetī’’ti bhikkhū pucchitvā ‘‘tasmātiha bhikkhave ye vo mayā dhammā abhiññā desitā. Seyyathidaṃ, cattāro satipaṭṭhānā cattāro sammappadhānā cattāro iddhipādā pañcindriyāni pañca balāni satta bojjhaṅgā ariyo aṭṭhaṅgiko maggo. Tattha sabbeheva samaggehi sammodamānehi avivadamānehi sikkhitabbaṃ’’ evamādinā bhante bhikkhūnaṃ ovādamadāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',150,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0150"></a> bhagavā tattha dutiyampi bhikkhūnaṃ ovādaṃ adāsi. Yo bahujanassa atthāya hitāya sukhāya saṃvattati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattha bhante bhagavā ‘‘tesañca vo bhikkhave samaggānaṃ sammodamānānaṃ avivadamānānaṃ sikkhataṃ siyā aññatarassa bhikkhuno āpatti, siyā vītikkamo. Tatra bhikkhave na codanāya taritabbaṃ puggalo upaparikkhitabbo’’ti evamādinā bhikkhūnaṃ dutiyampi ovādamadāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',152,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0152"></a> bhagavā tattha tatiyampi bhikkhūnaṃ ovādaṃ adāsi, yo bahujanassa atthāya hitāya sukhāya saṃvattati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘tesañca vo bhikkhave samaggānaṃ sammodamānānaṃ avivadamānānaṃ sikkhataṃ aññamaññassa vacīsaṃhāro uppajjeyya diṭṭhipaḷāso cetaso āghāto appaccayo anabhiraddhī’’ti evamādinā bhante bhagavā tattha tatiyampi bhikkhūnaṃ ovādamadāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',154,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0154"></a> bhikkhunā evaṃ satthu ovādānusāsanikārinā āyasmā nu te bhikkhu akusalā vuṭṭhāpetvā kusale patiṭṭhāpetīti parehi puṭṭhena kathaṃ sammā byākaramānena byākātabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘idhāhaṃ āvuso yena bhagavā tenupasaṅkamiṃ, tassa me bhagavā dhammaṃ desesi, tāhaṃ dhammaṃ sutvā tesaṃ bhikkhūnaṃ abhāsiṃ, taṃ te bhikkhū dhammaṃ sutvā akusalā vuṭṭhahiṃsu kusale patiṭṭhahiṃsū’’ti. Evaṃ kho bhante tena bhikkhunā evaṃ satthu ovādānusāsanikārinā parehi puṭṭhena sammā byākaramānena byākātabbaṃ.</p>\r\n<p class="subhead">Sāmagāmasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',155,replace(replace('<p class="bodytext">sāmagāmasuttaṃ <a name="M2.0155"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante sāmagāme nāma sakyānaṃ nigame āyasmantañca ānandaṃ āyasmantañca cundaṃ ārabbha bhāsitaṃ, āyasmā ca bhante ānando āyasmā ca cundo yena bhagavā tenupasaṅkamisu, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu, ekamantaṃ nisinno kho āyasmā ānando bhagavantaṃ etadavoca ‘‘ayaṃ bhante cundo samaṇuddeso evamāha ‘nigaṇṭho bhante nāṭaputto pāvāyaṃ adhunā kālaṅkato, tassa kālaṅkiriyāya bhinnā nigaṇṭhā dvedhikajātā bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṃ mukhasattīhi vitudantā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',156,replace(replace('<p class="bodytext">viharanti…pe… <a name="M2.0156"></a> bhinnathūpe appaṭisaraṇe’ti. Tassa mayhaṃ bhante evaṃ hoti ‘māheva bhagavato accayena saṅghe vivādo uppajji, svāssa vivādo bahujanaahitāya bahujanaasukhāya bahuno janassa anatthāya ahitāya dukkhāya devamanussāna’nti’’. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Sunakkhattasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',158,replace(replace('<p class="bodytext">sunakkhattasuttaṃ <a name="M2.0158"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante sunakkhattaṃ licchaviputtaṃ ārabbha bhāsitaṃ, sunakkhatto bhante licchaviputto bhagavantaṃ upasaṅkamitvā etadavoca ‘‘sutaṃ metaṃ bhante sambahulehi kira bhikkhūhi bhagavato santike aññā byākatā khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyāti pajānāmāti. Kacci te bhante bhikkhū sammadeva aññaṃ byākaṃsu, udāhu santetthekacce bhikkhū adhimānena aññaṃ byākaṃsū’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Āneñjasappāyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',161,replace(replace('<p class="bodytext">āneñjasappāyasuttaṃ <a name="M2.0161"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kurūsu bhante kammāsadhamme nāma kurūnaṃ nigame sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Gaṇakammoggallānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',162,replace(replace('<p class="bodytext">gaṇakamoggallānasuttaṃ <a name="M2.0162"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante gaṇakamoggallānaṃ brāhmaṇaṃ ārabbha bhāsitaṃ, gaṇakamoggallāno bhante brāhmaṇo bhagavantaṃ upasaṅkamitvā etadavoca ‘‘seyyāthāpi bho gotama imassa migāramātupāsādassa dissati anupubbasikkhā anupubbakiriyā anupubbapaṭipadā yadidaṃ yāva pacchimasopānakaḷevarā…pe… sakkā nukho bho gotama imasmimpi dhammavinaye evameva anupubbasikkhā anupubbakiriyā anupubbapaṭipadā paññapetu’’nti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',165,replace(replace('<p class="bodytext">evaṃ <a name="M2.0165"></a> vutte kho āvuso gaṇakamoggallāno brāhmaṇo bhagavantaṃ kiṃ avoca, kathañcassa bhagavā byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> evaṃ vutte bhante gaṇakamoggallāno brāhmaṇo bhagavantaṃ etadavoca ‘‘kiṃ nu kho bhoto gotamassa sāvakā bhotā gotamena evaṃ ovadīyamānā evaṃ anusāsīyamānā sabbe accantaṃ niṭṭhaṃ nibbānaṃ ārodhenti, udāhu ekacce nārādhentī’’ti, bhagavā ca bhante ‘‘appekacce kho brāhmaṇa mama sāvakā mayā evaṃ ovadīyamānā evaṃ anusāsīyamānā accantaṃ niṭṭhaṃ nibbānaṃ ārādhenti, ekacce nārādhentī’’ti evamādinā gaṇakamoggallānassa brāhmaṇassa byākāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',168,replace(replace('<p class="bodytext">imañca <a name="M2.0168"></a> panāvuso dhammadesanaṃ sutvā gaṇako moggallāno imasmiṃ dhammavinaye pasanno kīdisaṃ pasannākāramakāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imañca pana bhante dhammadesanaṃ sutvā gaṇakamoggallāno brāhmaṇo ‘‘ye me bho gotama puggalā asaddhā jīvikatthā na saddhā agārasmā anagāriyaṃ pabbajitā’’ti evamādinā imasmiṃ dhammavinaye pasanno pasannākāramakāsi.</p>\r\n<p class="subhead">Gopakamoggallānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',170,replace(replace('<p class="bodytext">gopakamoggallānasuttaṃ <a name="M2.0170"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante gopakamoggallānaṃ brāhmaṇaṃ ārabbha āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ, gopakamoggallāno bhante brāhmaṇo āyasmantaṃ ānandaṃ etadavoca ‘‘atthi nu kho bho ānanda ekabhikkhupi tehi dhammehi sabbena sabbaṃ sabbathā sabbaṃ samannāgato, yehi dhammehi samannāgato so bhavaṃ gotamo ahosi arahaṃ sammāsambuddho’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',173,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0173"></a> tattha āyasmatā ānandattherena dhammabhaṇḍāgārikena bhikkhuno pasādanīyā dhammā pakāsitā, ye tena bhagavatā jānatā passatā arahatā sammāsambuddhena akkhātā. Yehi ca samannāgataṃ bhikkhuṃ aññe bhikkhū sakkareyyuṃ garuṃ kareyyuṃ māneyyuṃ pūjeyyuṃ sakkatvā garuṃ katvā upanissāya vihareyyuṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha brāhmaṇa bhikkhu sīlavā hoti, pātimokkhasaṃvarasaṃvuto viharati ācāragocarasampanno aṇumattesu vajjesu bhayadassāvī, samādāya sikkhati sikkhāpadesu, bahussuto hoti sutadharo sutasannicayo, yeme dhammā ādikalyāṇā majjhekalyāṇā pariyosānakalyāṇā sātthaṃ sabyañjanaṃ kevalaparipuṇṇaṃ parisuddhaṃ brahmacariyaṃ abhivadanti, tathārūpassa dhammā bahussutā honti dhātā vacasā paricitā manasānupekkhitā diṭṭhiyā suppaṭividdhāti evamādinā bhante tattha āyasmatā ānandattherena dhammabhaṇḍāgārikena dasa pasādanīyā dhammā pakāsitā. Ye tena bhagatā jānatā passatā arahatā sammāsambuddhena akkhātā, yehi samannāgataṃ bhikkhuṃ etarahi bhikkhū sakkareyyuṃ garuṃ kareyyuṃ māneyyuṃ pūjeyyuṃ sakkatvā garuṃ katvā upanissāya vihareyyuṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',178,replace(replace('<p class="bodytext">evaṃ <a name="M2.0178"></a> vutte kho āvuso vassakāro brāhmaṇo magadhamahāmatto kīdisaṃ pasaṃsāvacanaṃ kathesi, kathañcassa āyasmā ānandatthero dhammabhaṇḍāgāriko taṃ vacanaṃ paṭisodhetvā pakāsesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',179,replace(replace('<p class="bodytext">evaṃ <a name="M2.0179"></a> vutte vassakāro brāhmaṇo magadhamahāmatto upanandaṃ senāpatiṃ āmantesi ‘‘taṃ kiṃ maññasi bhavaṃ senāpati, yadi me bhonto sakkātabbaṃ sakkaronti, garuṃ kātabbaṃ garuṃ karonti, mānetabbaṃ mānenti, pūjetabbaṃ pūjentī’’ti evamādikaṃ pasaṃsāvacanaṃ kathesi, āyasmā ca bhante ānando ‘‘na ca kho brāhmaṇa so bhagavā sabbaṃ jhānaṃ vaṇṇesi, napi so bhagavā sabbaṃ jhānaṃ na vaṇṇesī’’ti evamādinā taṃ vacanaṃ paṭisodhetvā pakāsesi.</p>\r\n<p class="subhead">Mahāpuṇṇamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',182,replace(replace('<p class="bodytext">mahāpuṇṇamasuttaṃ <a name="M2.0182"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ saṭṭhimattānaṃ padhānīyabhikkhūnaṃ saṅghattheraṃ bhikkhuṃ ārabbha bhāsitaṃ, aññataro bhante</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',183,replace(replace('<p class="bodytext">Saṭṭhimattānaṃ <a name="M2.0183"></a> padhānīyabhikkhūnaṃ saṅghatthero bhikkhu uṭṭhāyāsanā ekaṃsaṃ cīvaraṃ katvā yena bhagavā tenañjaliṃ paṇāmetvā bhagavantaṃ etadavoca ‘‘puccheyyāhaṃ bhante bhagavantaṃ kiñcideva desaṃ, sace me bhagavā okāsaṃ karoti pañhassa veyyākaraṇāyā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Cūḷapuṇṇamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',184,replace(replace('<p class="bodytext">cūḷapuṇṇamasuttaṃ <a name="M2.0184"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',185,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0185"></a> tattha bhagavatā asappurisaaṅgāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> asappuriso bhikkhave assaddhammasamannāgato hoti, asappurisabhatti hoti, asappurisacintī hoti, asappurisamantī hoti, asappurisavāco hoti, asappurisakammanto hoti, asappurisadiṭṭhi hoti, asappurisadānaṃ detīti evamādinā bhante tattha bhagavatā aṭṭha asappurisaaṅgāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',186,replace(replace('<p class="bodytext">kathaṃ <a name="M2.0186"></a> panāvuso bhagavatā tattha sappurisaaṅgāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sappuriso bhikkhave saddhammasamannāgato hoti, sappurisabhatti hoti, sappurisacintī hoti, sappurisamantī hoti, sappurisavāco hoti, sappurisakammanto hoti, sappurisadiṭṭhi hoti, sappurisadānaṃ detīti evamādinā bhante bhagavatā tattha aṭṭhavidhāni sappurisaṅgāni vibhajitvā pakāsitāni.</p>\r\n<p class="subhead">Anupadasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',188,replace(replace('<p class="bodytext">anupadasuttaṃ <a name="M2.0188"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ, sambahulā bhante bhikkhū āyasmato sāriputtassa dhammasenāpatissa sabhāgā tasmiṃ samaye sannipatiṃsu, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',189,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0189"></a> tattha bhagavatā āyasmato sāriputtassa dhammasenāpatissa anupadadhammavipassanā vitthārena vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paṇḍito bhikkhave sāriputto mahāpañño bhikkhave sāriputto puthupañño bhikkhave sāriputto </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',190,replace(replace('<p class="bodytext">hāsapañño <a name="M2.0190"></a> bhikkhave sāriputto tikkhapañño bhikkhave sāriputto javanapañño bhikkhave sāriputto nibbedhikapañño bhikkhave sāriputto sāriputto bhikkhave aḍḍhamāsaṃ anupadadhammavipassanaṃ vipassatīti evamādinā bhante tattha bhagavatā āyasmato sāriputtassa dhammasenāpatissa anupadadhammavipassanā vitthārena pakāsitā.</p>\r\n<p class="subhead">Chabbisodhanasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',192,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M2.0192"></a> sammāsambuddhena chabbisodhanasuttaṃ kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Sappurisasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',193,replace(replace('<p class="bodytext">sappurisasuttaṃ <a name="M2.0193"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Sevitabbāsevitabbasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',194,replace(replace('<p class="bodytext">sevitabbāsevitabbasuttaṃ <a name="M2.0194"></a> panāvuso bhagavatā kattha kaṃ ārabbha kena saddhiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha āyasmatā sāriputtattherena dhammasenāpatinā saddhiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Bahudhātukasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',195,replace(replace('<p class="bodytext">bahudhātukasuttaṃ <a name="M2.0195"></a> panāvuso kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Isigilisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> isigilisuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante isigilismiṃ pabbate sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Mahācattārīsakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',196,replace(replace('<p class="bodytext">mahācattārīsakasuttaṃ <a name="M2.0196"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Ānāpānassatisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',197,replace(replace('<p class="bodytext">ānāpānassatisuttaṃ <a name="M2.0197"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ, therā bhante bhikkhū nave bhikkhū ovadanti anusāsanti, appekacce therā bhikkhū dasapi bhikkhū ovadanti anusāsanti, appekacce therā bhikkhū vīsampi bhikkhū ovadanti anusāsanti, appekacce therā bhikkhū tiṃsampi bhikkhū ovadanti anusāsanti, appekacce therā bhikkhū cattārīsampi bhikkhū ovadanti anusāsanti, te ca bhante navā bhikkhū therehi bhikkhūhi ovadiyamānā anusāsiyamānā uḷāraṃ pubbenāparaṃ visesaṃ jānanti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Kāyagatāsatisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kāyagatāsatisuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ, sambahulānaṃ bhante bhikkhūnaṃ pacchābhattaṃ piṇḍapātapaṭikkantānaṃ upaṭṭhānasālāyaṃ sannisinnānaṃ sannipatitānaṃ ayamantarakathā udapādi ‘‘acchariyaṃ āvuso, abbhutaṃ āvuso, yāvañcidaṃ tena bhagavatā jānatā passatā arahatā sammāsambuddhena kāyagatāsati bhāvitā bahulīkatā mahapphalā vuttā mahānisaṃsā’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Saṅkhārūpapattisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',200,replace(replace('<p class="bodytext">saṅkhārūpapattisuttaṃ <a name="M2.0200"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathañcāvuso tattha bhagavatā saṅkhārūpapattiyo vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha bhikkhave bhikkhu saddhāya samannāgato hoti, sīlena samannāgato hoti, sutena samannāgato hoti, cāgena samannāgato hoti, paññāya samannāgato hoti, tassa evaṃ hoti ‘‘aho vatāhaṃ kāyassa bhedā paraṃ maraṇā khattiyamahāsālānaṃ sahabyataṃ upapajjeyya’’nti evamādinā bhante tattha bhagavatā saṅkhārūpapattiyo vibhajitvā pakāsitā.</p>\r\n<p class="subhead">Cūḷasuññatasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',202,replace(replace('<p class="bodytext">cūḷasuññatasuttaṃ <a name="M2.0202"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ ānandaṃ ārabbha bhāsitaṃ, āyasmā bhante ānando sāyanhasamayaṃ paṭisallānā vuṭṭhito bhagavantaṃ etadavoca ‘‘ekamidaṃ bhante samayaṃ bhagavā sakkesu viharati nagarakaṃ nāma sakyānaṃ nigamo, tattha me bhante bhagavato sammukhā sutaṃ, sammukhā paṭiggahitaṃ, suññatāvihārenāhaṃ ānanda etarahi bahulaṃ viharāmīti kacci metaṃ bhante sussutaṃ suggahitaṃ sumanasikataṃ sūpadhārita’’nti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Mahāsuññatasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',204,replace(replace('<p class="bodytext">mahāsuññatasuttaṃ <a name="M2.0204"></a> panāvuto bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante kapilavatthusmiṃ āyasmantaṃ ānandaṃ ārabbha bhāsitaṃ, āyasmā bhante ānando sambahulehi bhikkhūhi saddhiṃ ghaṭāya sakkassa vihāre cīvarakammaṃ karoti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Acchariyaabbhutasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',205,replace(replace('<p class="bodytext">acchariyaabbhutasuttaṃ <a name="M2.0205"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena saddhiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha āyasmatā ānandattherena dhammabhaṇḍāgārikena saddhiṃ bhāsitaṃ. Sambahulānaṃ bhante bhikkhūnaṃ pacchābhattaṃ piṇḍapātapaṭikkantānaṃ upaṭṭhānasālāyaṃ sannisinnānaṃ sannipatitānaṃ ayamantarakathā udapādi ‘‘acchariyaṃ āvuso, abbhutaṃ āvuso tathāgatassa mahiddhikatā mahānubhāvatā. Yatra hi nāma tathāgato atīte buddhe parinibbute chinnapapañce chinnavaṭume pariyādinnavaṭṭe sabbadukkhavītivatte jānissati. Evaṃjaccā te bhagavanto ahesuṃ itipi, evaṃnāmā te bhagavanto ahesuṃ itipi, evaṃgottā te bhagavanto ahesuṃ itipi, evaṃsīlā te bhagavanto ahesuṃ itipi, evaṃdhammā te bhagavanto ahesuṃ itipi, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',206,replace(replace('<p class="bodytext">evaṃpaññā <a name="M2.0206"></a> te bhagavanto ahesuṃ itipi, evaṃvihārī te bhagavanto ahesuṃ itipi, evaṃvimuttā te bhagavanto ahesuṃ itipī’’ti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Bākulasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',207,replace(replace('<p class="bodytext">bākulasuttaṃ <a name="M2.0207"></a> panāvuso kattha kaṃ ārabbha kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante acelaṃ kassapaṃ ārabbha āyasmatā bākulattherena bhāsitaṃ.</p>\r\n<p class="subhead">Dantabhūmisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> dantabhūmisuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante jayasenaṃ rājakumāraṃ ārabbha bhāsitaṃ. Jayaseno bhante rājakumāro aciravataṃ samaṇuddesaṃ upasaṅkamitvā etadavoca ‘‘sutaṃ metaṃ bho aggivessana idha bhikkhu appamatto ātāpī pahitatto viharanto </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',208,replace(replace('<p class="bodytext">phuseyya <a name="M2.0208"></a> cittassa ekaggata’’nti, tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',210,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0210"></a> tattha bhagavatā aciravatassa samaṇuddesassa samassāsetvā dhammadesanānayo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> taṃ kutettha aggivessana labbhā, yaṃ taṃ nekkhammena ñātabbaṃ nekkhammena daṭṭhabbaṃ nekkhammena pattabbaṃ nekkhammena sacchikātabbaṃ, taṃ vata jayaseno rājakumāro kāmamajjhe vasanto kāme paribhuñjanto kāmavitakkehi khajjamāno kāmapariḷāhena pariḍayhamāno kāmapariyosanāya ussuko ussati vā dakkhati vā sacchi vā karissatīti netaṃ ṭhānaṃ vijjatīti evaṃ kho bhante bhagavato aciravatassa samaṇuddesassa samassāsetvā, seyyathāpissu aggivessana dve hatthidammā vā assadammāvā godammā vā sudantā suvinītāti evamādinā bhante bhagavatā aciravatassa samaṇuddesassa desanānayo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',214,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0214"></a> tattha bhagavā dve upamāyo dassetvā tatuttari dhammadesanaṃ pavaḍḍheti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> seyyathāpi aggivessana rājākhattiyo muddhāvasitto nāgavanikaṃ āmantetīti evamādinā bhante bhagavā uttaripi dhammadesanaṃ pavaḍḍhesi.</p>\r\n<p class="subhead">Bhūmijasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',216,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M2.0216"></a> sammāsambuddhena bhūmijasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante jayasenaṃ rājakumāraṃ ārabbha bhāsitaṃ, jayaseno bhante rājakumāro āyasmantaṃ bhūmijaṃ etadavoca ‘‘santi bho bhūmija eke samaṇabrāhmaṇā evaṃvādino evaṃdiṭṭhino ‘āsañcepi karitvā brahmacariyaṃ caranti, abhabbā phalassa adhigamāya. Anāsañcepi karitvā brahmacariyaṃ caranti, abhabbā phalassa adhigamāya. Āsañca anāsañcepi karitvā brahmacariyaṃ caranti, abhabbā phalassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',217,replace(replace('<p class="bodytext">adhigamāya<a name="M2.0217"></a>. Nevāsaṃ nānāsañcepi karitvā brahmacariyaṃ caranti, ābhabbā phalassa adhigamāyā’ti. Idha bhoto bhūmijassa satthā kiṃvādī kimakkhāyī’’ti tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',219,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0219"></a> tattha bhagavatā āyasmato bhūmijattherassa taṃ byākaraṇaṃ samanujānitvā tatuttari dhammadesanānayo paripūretvā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> taggha tvaṃ bhūmija evaṃ puṭṭho evaṃ byākaramāno vuttavādī ceva me hosi, na ca maṃ abhūtena abbhācikkhasi, evamādinā bhante bhagavatā āyasmato bhūmijassa taṃ vacanaṃ samanujānitvā ‘‘yehi kehici bhūmija samaṇā vā brāhmaṇā vā micchādiṭṭhino micchāsaṅkappā micchāvācā micchākammantā micchāājīvā micchāvāyāmā micchāsatī micchāsamādhino, te āsañcepi karitvā brahmacariyaṃ caranti, abhabbā phalassa adhigamāyā’’ti evamādinā bhante bhagavatā āyasmato bhūmijassa uttari desanānayo paripūretvā pakāsito.</p>\r\n<p class="subhead">Anuruddhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',224,replace(replace('<p class="bodytext">anuruddhasuttaṃ <a name="M2.0224"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante pañcakaṅgaṃ thapatiṃ ārabbha āyasmatā anuruddhattherena bhāsitaṃ, pañcakaṅgo bhante thapati āyasmantaṃ anuruddhaṃ etadavoca ‘‘idha maṃ bhante therā bhikkhū upasaṅkamitvā evamāhaṃsu ‘appamāṇaṃ gahapati cetovimuttiṃ bhāvehī’ti, ekacce therā evamāhaṃsu ‘mahaggataṃ gahapati cetovimuttiṃ bhāvehī’’ti, yā cāyaṃ bhante appamāṇā cetovimutti yā ca mahaggatā cetovimutti, ime dhammā nānatthā ceva nānābyañjanā ca, udāhu ekatthā byañjanameva nānanti’’ tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="subhead">Upakkilesasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',225,replace(replace('<p class="bodytext">upakkilesasuttaṃ <a name="M2.0225"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pācīnavaṃsadāye bhante āyasmantaṃ anuruddhattheraṃ ārabbha bhāsitaṃ.</p>\r\n<p class="subhead">Bālapaṇḍitasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',226,replace(replace('<p class="bodytext">bālapaṇḍitasuttaṃ <a name="M2.0226"></a> panāvuso bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathañcāvuso tattha bhagavatā bālassa bālalakkhaṇāni ca bālassa diṭṭheva dhamme dukkhadomanassappaṭisaṃvedanā ca pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tīṇimāni bhikkhave bālassa bālalakkhaṇāni bālanimittāni bālāpadānāni. Katamāni tīṇi. Idha bhikkhave bālo </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',227,replace(replace('<p class="bodytext">duccintitacintī <a name="M2.0227"></a> ca hoti dubbhāsitabhāsī ca dukkaṭakammakārī cāti evamādinā bhante tattha bhagavatā bālassa bālalakkhaṇāni ca bālassa diṭṭheva dhamme dukkhadomanassappaṭisaṃvedanā ca pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',230,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0230"></a> tattha bhagavatā bālassa samparāyo ca tattha niraye bālassa dukkhadomanassapaṭisaṃvedanā ca pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sa kho so bhikkhave bālo kāyena duccaritaṃ caritvā vācāya duccaritaṃ caritvā manasā duccaritaṃ caritvā kāyassa bhedāparaṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjatīti evamādinā bhante tattha bhagavatā bālassa samparāyo ca tattha ca bālassa niraye dukkhadomanassappaṭisaṃvedanā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',234,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0234"></a> tattha bhagavatā bālassa tiracchānayoniyaṃ dukkhadomanassapaṭisaṃvedanā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> santi bhikkhave tiracchānagatā pāṇā tiṇabhakkhā te allānipi tiṇāni sukkhānipi tiṇāni dantullehakaṃ khādantīti evamādinā bhante tattha bhagavatā bālassa tiracchānayoniyaṃ dukkhadomanassappaṭisaṃvedanā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',235,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0235"></a> tattha bhagavatā bālena sakiṃ vinipātagatena puna manussattadullabhatā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> seyyathāpi bhikkhave puriso ekacchiggalaṃ yugaṃ mahāsamudde pakkhipeyya, tamenaṃ puratthimo vāto pacchimena saṃhareyyāti evamādinā bhante tattha bhagavatā bālena sakiṃ vinipātagatena puna manussattassa dullabhatā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',237,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0237"></a> tattha bhagavatā bālassa kadāci karahaci dīghassa addhuno accayena manussattaṃ āgatassapi dukkhabahulatā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sa kho so bhikkhave bālo sace kadāci karahaci dīghassa addhuno accayena manussattaṃ āgacchatīti evamādinā bhante tattha bhagavatā bālassa kadāci karahaci dīghassa addhuno accayena manussattaṃ āgatassapi dukkhabahulatā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',238,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0238"></a> tattha bhagavatā paṇḍitassa paṇḍitalakkhaṇāni ca paṇḍitassa diṭṭheva dhamme sukhasomanassappaṭisaṃvedanā ca pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tīṇimāni bhikkhave paṇḍitassa paṇḍitalakkhaṇāni paṇḍitanimittāni paṇḍitāpadānāni. Katamāni tīṇi. Idha bhikkhave paṇḍito sucintitacintī ca hoti subhāsitabhāsī ca sukatakammakārī cāti evamādinā bhante tattha bhagavatā</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',239,replace(replace('<p class="bodytext">Paṇḍitassa <a name="M2.0239"></a> paṇḍitalakkhaṇāni ca diṭṭheva dhamme paṇḍitassa sukhasomanassapaṭisaṃvedanā ca pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',240,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0240"></a> tattha bhagavatā paṇḍitassa samparāyo ca tattha sagge sukhasomanassappaṭisaṃvedanā ca pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sa kho so bhikkhave paṇḍito kāyena sucaritaṃ caritvā vācāya manasā sucaritaṃ caritvā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjatīti evamādinā bhante tattha bhagavatā paṇḍitassa samparāyo ca sagge ca paṇḍitassa sukhasomanassappanisaṃvedanā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',242,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0242"></a> tattha bhagavatā paṇḍitassa kadāci karahaci dīghassa addhuno accayena manussattaṃ āgatassapi sukhabahulatā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sa kho so bhikkhave paṇḍito sace kadāci karahaci dīghassa addhuno accayena manussattaṃ āgacchati, yāni tāni uccākulāni khattiyamahāsālakulaṃ vā brāhmaṇamahāsālakulaṃ vā gahapatimahāsālakulaṃ vā tathārūpe kule paccājāyatīti evamādinā bhante tattha bhagavatā paṇḍitassa kadāci karahaci dīghassa addhuno accayena manussattaṃ āgatassapi sukhabahulatā pakāsitā.</p>\r\n<p class="subhead">Cūḷakammavibhaṅgasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',244,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0244"></a> jānatā passatā…pe… sammāsambuddhena cūḷakammavibhaṅgasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante subhaṃ māṇavaṃ todeyyaputtaṃ ārabbha bhāsitaṃ. Subho bhante māṇavo todeyyaputto bhagavantaṃ upasaṅkamitvā etadavoca ‘‘ko nu kho bho gotama hetu ko paccayo, yena manussānaṃyeva sataṃ manussabhūtānaṃ dissanti hīnappaṇītatā. Dissanti hi bho gotama manussā appāyukā, dissanti dīghāyukā. Dissanti bavhābādhā, dissanti appābādhā. Dissanti dubbaṇṇā, dissanti vaṇṇavanto. Dissanti appesakkhā, dissanti mahesakkhā. Dissanti appabhogā, dissanti mahābhogā. Dissanti nīcakulīnā, dissanti uccākulīnā. Dissanti duppaññā, dissanti paññavanto. Ko nu kho bho gotama hetu ko paccayo, yena manussānaṃyeva sataṃ manussabhūtānaṃ dissanti hīnappaṇītatā’’ti tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',246,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0246"></a> tattha bhagavatā sattānaṃ dīghāyukaappāyukasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha māṇava ekacco itthī vā puriso vā pāṇātipātī hoti, luddo lohitapāṇi hatapahate niviṭṭho adayāpanno pāṇabhūtesūti evamādinā bhante tattha bhagavatā appāyukadīghāyukasaṃvattanakāni kammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',247,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0247"></a> tattha bhagavatā sattānaṃ appābādhabavhābādhasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',248,replace(replace('<p class="bodytext">idha <a name="M2.0248"></a> māṇava ekacco itthī vā puriso vā sattānaṃ viheṭṭhakajātiko hoti pāṇinā vā leḍḍunāvā daṇḍenavā satthena vāti evamādinā bhante tattha bhagavatā sattānaṃ appābādhabavhābādhasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',249,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0249"></a> tattha bhagavatā sattānaṃ suvaṇṇadubbaṇṇasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha māṇava ekacco itthī vā puriso vā kodhano hoti upāyāsabahulo, appampi vutto samāno abhisajjati kuppati byāpajjati patiṭṭhiyati, kopañca dosañca appaccayañca pātukarotīti evamādinā bhante tattha bhagavatā sattānaṃ suvaṇṇadubbaṇṇasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',251,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0251"></a> tattha bhagavatā sattānaṃ mahesakkha appesakkhasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha māṇava ekacco itthī vā puriso vā issāmanako hoti, paralābhasakkāra garukāra mānanavandana pūjāsu issati upadussati issaṃ bandhatīti evamādinā bhante tattha bhagavatā sattānaṃ appesakkhamahesakkhasaṃvattanakāni kammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',253,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0253"></a> tattha bhagavatā mahābhogaappabhogasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha māṇava ekacco itthī vā puriso vā na dātā hoti, samaṇassa vā brāhmaṇassa vā annaṃ pānaṃ vattaṃ yānaṃ mālāgandha vilepanaṃ seyyavasathapadīpeyyanti evamādinā bhante tattha bhagavatā mahābhogaappabhogasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',255,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0255"></a> tattha bhagavatā sattānaṃ uccākulīna nīcakulīnasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha māṇava ekacco itthī vā puriso vā thaddho hoti atimāni abhivādetabbaṃ na abhivādeti, paccuṭṭhātabbaṃ na paccuṭṭheti, āsanārahassa na āsanaṃ deti, maggārahassa na maggaṃ deti, sakkātabbaṃ na sakkaroti, garukātabbaṃ na garukaroti, mānetabbaṃ māneti, pūjetabbaṃ na pūjetīti evamādinā bhante tattha bhagavatā sattānaṃ uccākulīna nīcakulīnasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',257,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0257"></a> tattha bhagavatā sattānaṃ mahāpaññaduppaññasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha māṇava ekacco itthī vā puriso vā samaṇaṃ vā brāhmaṇaṃ vā upasaṅkamitvā na paripucchitā hoti ‘‘kiṃ bhante kusalaṃ, kiṃ akusalaṃ. Kiṃ sāvajjaṃ, kiṃ anavajjaṃ. Kiṃ sevitabbaṃ, kiṃ na sevitabbaṃ. Kiṃ me karīyamānaṃ dīgharattaṃ ahitāya dukkhāya hoti, kiṃ vā pana me </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',258,replace(replace('<p class="bodytext">karīyamānaṃ <a name="M2.0258"></a> dīgharattaṃ hitāya sukhāya hotī’’ti evamādinā bhante tattha bhagavatā sattānaṃ mahāpaññaduppaññasaṃvattanakakammāni vibhajitvā pakāsitāni.</p>\r\n<p class="subhead">Indriyabhāvanāsu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_69',260,replace(replace('<p class="bodytext">indriyabhāvanāsuttaṃ <a name="M2.0260"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> gajaṅgalāyaṃ bhante uttaraṃ nāma māṇavaṃ pārāsiviyantevāsiṃ ārabbha bhāsitaṃ, uttaro bhante māṇavo pārāsiviyantevāsī bhagavatā puṭṭho bhagavantaṃ etadavoca ‘‘idha bho gotama cakkhunā rūpaṃ na passati, sotena saddaṃ na suṇāti. Evaṃ kho bho gotama deseti pārāsiviyo brāhmaṇo sāvakānaṃ indriyabhāvana’’nti. Tasmiṃ bhante vatthusmiṃ bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
