INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',25,replace(replace('<p class="chapter">Aṭṭhakathā</p>\r\n<p class="subhead">Saṃgāyanassa pucchā-vissajjanā</p>\r\n<p class="gatha1">Sadevakopi <a name="M1.0025"></a> ce loko, āgantvā tāsayeyya maṃ;</p>\r\n<p class="gathalast">Na me paṭibalo assa, janetuṃ bhayabheravaṃ.</p>\r\n<p class="gatha1">Sacepi tvaṃ mahiṃ sabbaṃ, sasamuddaṃ sapabbataṃ;</p>\r\n<p class="gatha2">Ukkhipitvā mahānāga, khipeyyāsi mamūpari;</p>\r\n<p class="gatha3">Neva me sakkuṇeyyāsi, janetuṃ bhayabheravaṃ;</p>\r\n<p class="gathalast">Aññadatthu tavevassa, vighāto uragādhipa.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',26,replace(replace('<p class="gatha1">Mā <a name="M1.0026"></a> dāni kodhaṃ janayittha, ito uddhaṃ yathā pure;</p>\r\n<p class="gatha2">Sassaghātañca mākattha, sukhakāmā hi pāṇino;</p>\r\n<p class="gathalast">Karotha mettaṃ sattesu, vasantu manujā sukhaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',30,replace(replace('<p class="bodytext">Cattāro <a name="M1.0030"></a> āsīvisā uggatejā ghoravisāti kho bhikkhave catunnetaṃ mahābhūtānaṃ adhivacanaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',33,replace(replace('<p class="bodytext">Pañca <a name="M1.0033"></a> vadhakā paccatthikāti kho bhikkhave pañcannetaṃ upādānakkhandhānaṃ adhivacanaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',35,replace(replace('<p class="bodytext">Chaṭṭho <a name="M1.0035"></a> antaracaro vadhako ukkhittāsikoti kho bhikkhave nandīrāgassetaṃ adhivacanaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',36,replace(replace('<p class="bodytext">Suñño <a name="M1.0036"></a> gāmoti kho bhikkhave channetaṃ ajjhattikānaṃ āyatanānaṃ adhivacanaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',38,replace(replace('<p class="bodytext">Corā <a name="M1.0038"></a> gāmaghātakāti kho bhikkhave channetaṃ bāhirānaṃ āyatanānaṃ adhivacanaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',41,replace(replace('<p class="bodytext">Mahāudakaṇṇavo <a name="M1.0041"></a> kho bhikkhave catunnetaṃ oghānaṃ adhivacanaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',42,replace(replace('<p class="bodytext">Orimaṃ <a name="M1.0042"></a> tīraṃ sāsaṅkaṃ sappaṭibhayanti kho bhikkhave sakkāyassetaṃ adhivacanaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',44,replace(replace('<p class="bodytext">Pārimaṃ <a name="M1.0044"></a> tīraṃ khemaṃ appaṭibhayanti kho bhikkhave nibbānassetaṃ adhivacanaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',45,replace(replace('<p class="bodytext">Vīriyārambhassetaṃ <a name="M1.0045"></a> adhivacanaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',46,replace(replace('<p class="gatha1">Gantvā <a name="M1.0046"></a> kasmīragandhāraṃ, isi majjhantikā tadā;</p>\r\n<p class="gathalast">Duṭṭhaṃ nāgaṃ pasādetvā, mocesi bandhanā bahū.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',56,replace(replace('<p class="bodytext">Punapi <a name="M1.0056"></a> bhante dakkhemu saṅgati ce bhavissati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',63,replace(replace('<p class="gatha1">Ajjāpi <a name="M1.0063"></a> santānamayaṃ, mālaṃ ganthenti nandane;</p>\r\n<p class="gathalast">Devaputto javo nāma, yo me mālaṃ paṭicchati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',64,replace(replace('<p class="gatha1">Muhuttoviya <a name="M1.0064"></a> so dibbo, idha vassāni soḷasa;</p>\r\n<p class="gathalast">Rattindivo ca so dibbo, mānusiṃ sarado sataṃ.</p>\r\n<p class="gatha1">Iti kammāni anventi, asaṅkheyyāpi jātiyo;</p>\r\n<p class="gathalast">Kalyāṇaṃ yadi vā pāpaṃ, na hi kammaṃ vinassati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',65,replace(replace('<p class="gatha1">Yo <a name="M1.0065"></a> icche puriso hotuṃ, jāti jāti punappunaṃ;</p>\r\n<p class="gathalast">Paradāraṃ vivajjeyya, dhotapādova kaddamaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',66,replace(replace('<p class="gatha1">Yā <a name="M1.0066"></a> icche puriso hotuṃ, jāti jāti punappunaṃ;</p>\r\n<p class="gathalast">Sāmikaṃ apacāyeyya, indaṃva paricārikā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',67,replace(replace('<p class="gatha1">Yo <a name="M1.0067"></a> icche dibbabhogañca, dibbamāyuṃ yasaṃ sukhaṃ;</p>\r\n<p class="gathalast">Pāpāni parivajjetvā, tividhaṃ dhammamācare.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',73,replace(replace('<p class="bodytext">Yaṃ <a name="M1.0073"></a> bhikkhave sadevakassa lokassa samārakassa sabrahmakassa sassamaṇabrāhmaṇiyā pajāya sadevamanussāya diṭṭhaṃ sutaṃ mutaṃ viññātaṃ pattaṃ pariyesitaṃ anuvicaritaṃ manasā, tamahaṃ jānāmi tamahaṃ abhiññāsiṃ, taṃ tathāgatassa viditaṃ, taṃ tathāgato na upaṭṭhāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',74,replace(replace('<p class="bodytext">Iti <a name="M1.0074"></a> kho bhikkhave tathāgato diṭṭhasutamutaviññātabbesu dhammesu tādīyeva tādī, tumhā ca pana tādimhā añño tādī uttaritaro vā paṇītataro vā natthīti vadāmi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',81,replace(replace('<p class="gatha1">Suvaṇṇabhūmiṃ <a name="M1.0081"></a> gantvāna, soṇuttarā mahiddhikā;</p>\r\n<p class="gathalast">Pisāce niddhametvāna, brahmajālamadesisuṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',86,replace(replace('<p class="gatha1">Samaṇā <a name="M1.0086"></a> mayaṃ mahārāja, dhammarājassa sāvakā;</p>\r\n<p class="gathalast">Taveva anukampāya, jambudīpā idhāgatā.</p>\r\n<p class="gatha1">Ahaṃ buddhañca dhammañca, saṅghañca saraṇaṃ gato,</p>\r\n<p class="gathalast">Upāsakattaṃ desesiṃ, sakyaputtassa sāsane.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',92,replace(replace('<p class="bodytext">Tena <a name="M1.0092"></a> samayena buddho bhagavā verañjāyaṃ viharati naḷerupuci mandamūle.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',125,replace(replace('<p class="gatha1">Saggārohaṇasopāṇaṃ<a name="M1.0125"></a>, aññaṃ sīlasamaṃ kuto;</p>\r\n<p class="gathalast">Dvāraṃ vā pana nibbāna, nagarassa pavesane.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',133,replace(replace('<p class="gatha1">Alameva <a name="M1.0133"></a> kātuṃ kalyāṇaṃ, dānaṃ dātuṃ yathārahaṃ;</p>\r\n<p class="gathalast">Pāṇiṃ kāmadadaṃ disvā, ko puññaṃ nakarissati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',134,replace(replace('<p class="gatha1">Dassāmannañca <a name="M1.0134"></a> pānañca, vatthasenāsanāni ca;</p>\r\n<p class="gathalast">Papañca udapānañca, dugge saṅkamanāni ca.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',147,replace(replace('<p class="gatha1">Mahāaṭṭhakathañceva<a name="M1.0147"></a>, mahāpaccarimevaca;</p>\r\n<p class="gatha2">Kurundiñcāti tissopi, sīhaḷaṭṭhakathā imā;</p>\r\n<p class="gatha3">Buddhamittoti nāmena, visutassa yasassino;</p>\r\n<p class="gathalast">Vinayaññussa dhīrassa, sutvā therassa santike.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',149,replace(replace('<p class="gatha1">Upaddavākule <a name="M1.0149"></a> loke, nirupaddavato ayaṃ;</p>\r\n<p class="gatha2">Ekasaṃvacchareneva, yathā niṭṭhaṃ upāgatā;</p>\r\n<p class="gatha3">Evaṃ sabbassa lokassa, niṭṭhaṃ dhammūpasaṃhitā;</p>\r\n<p class="gathalast">Sīghaṃ gacchantu ārambhā, sabbepi nirupaddavā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',151,replace(replace('<p class="gatha1">Ciraṭṭhitatthaṃ <a name="M1.0151"></a> dhammassa, karontena mayā imaṃ;</p>\r\n<p class="gatha2">Saddhammabahumānena, yañca puññaṃ samācitaṃ;</p>\r\n<p class="gatha3">Sabbassa ānubhāvena, tassa sabbepi pāṇino;</p>\r\n<p class="gathalast">Bhavantu dhammarājassa, saddhammarasasevino.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',152,replace(replace('<p class="gatha1">Ciraṃ <a name="M1.0152"></a> tiṭṭhatu saddhammo, kāle vassaṃ ciraṃ pajaṃ;</p>\r\n<p class="gathalast">Tappetu devo dhammena, rājā rakkhatu mediniṃ.</p>\r\n<p class="gatha1">Tāva tiṭṭhatu lokasmiṃ, lokanittharaṇesinaṃ;</p>\r\n<p class="gatha2">Dassentī kulaputtānaṃ, nayaṃ sīlavisuddhiyā;</p>\r\n<p class="gatha3">Yāva buddhoti nāmampi, suddhacittassa tādino;</p>\r\n<p class="gathalast">Lokamhi lokajeṭṭhassa, pavattati mahesino.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',155,replace(replace('<p class="gatha1">Karuṇāsītalahadayaṃ<a name="M1.0155"></a>, paññāpajjotavihatamohatamaṃ;</p>\r\n<p class="gathalast">Sanarāmaralokagaruṃ, vande sugataṃ gativimuttaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',157,replace(replace('<p class="gatha1">Buddhopi <a name="M1.0157"></a> buddhabhāvaṃ, bhāvetvā ceva sacchikatvāca;</p>\r\n<p class="gathalast">Yaṃ upagato gatamalaṃ, vande tamanuttaraṃ dhammaṃ.</p>\r\n<p class="gatha1">Sugatassa orasānaṃ, puttānaṃ mārasenamathanānaṃ;</p>\r\n<p class="gathalast">Aṭṭhannampi samūhaṃ, sirasā vande ariyasaṅghaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',158,replace(replace('<p class="gatha1">Dīghassa <a name="M1.0158"></a> dīghasuttaṅkitassa, nipuṇassa āgamavarassa;</p>\r\n<p class="gathalast">Buddhānubuddhasaṃvaṇṇitassa, saddhāvahaguṇassa.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',159,replace(replace('<p class="bodytext">Sammāsambuddheneva <a name="M1.0159"></a> hi tiṇṇampi piṭakānaṃ atthavaṇṇanākkamo bhāsito, yāpakiṇṇakadesanāti vuccati, tato saṃgāyanādivasena sāvakehīti ācariyā vadanti.</p>\r\n<p class="gatha1">Atthappakāsanatthaṃ, aṭṭhakathā ādito vasisatehi;</p>\r\n<p class="gathalast">Pañcahi yāsaṅgītā, anusaṅgītā ca pacchāpi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',161,replace(replace('<p class="gatha1">Majjhe <a name="M1.0161"></a> visuddhimaggā, esa catunnampi āgamānañhi;</p>\r\n<p class="gatha2">Ṭhatvā pakāsayissati, tattha yathā bhāsitamatthaṃ;</p>\r\n<p class="gatha3">Icceva kato tasmā, tampi gahetvāna saddhimetāya;</p>\r\n<p class="gathalast">Aṭṭhakathāya vijānātha, dīghāgamanissitaṃ atthaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',165,replace(replace('<p class="gatha1">Atthānaṃ <a name="M1.0165"></a> sūcanato, suvuttato savanatothasūdanato,</p>\r\n<p class="gathalast">Suttāṇā suttasabhāgatoca, suttanti akkhātaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',177,replace(replace('<p class="gatha1">Sududdasaṃ <a name="M1.0177"></a> sunipuṇaṃ, yatthakāmanipātinaṃ;</p>\r\n<p class="gathalast">Cittaṃ rakkhetha medhāvī, cittaṃ guttaṃ sukhāvahaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',180,replace(replace('<p class="bodytext">So <a name="M1.0180"></a> evaṃ samāhite citte parisuddhe pariyodāte anaṅgaṇe vigatūpakkilese mudubhūte kammaniye ṭhite ānañjappatte ñāṇadassanāya cittaṃ abhinīharati abhininnāmeti –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',206,replace(replace('<p class="bodytext">Yaṃhitaṃ <a name="M1.0206"></a> bhikkhave sammāvadamāne vadeyya samantapāso mārassāti, mātugāmaṃyeva sammā vadamāno vadeyya samantapāso mārassāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',209,replace(replace('<p class="bodytext">Yo <a name="M1.0209"></a> bhikkhu divasaṃ caṅkamena nisajjāya ca āvaraṇīyehi dhammehi cittaṃ parisodhetvā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',228,replace(replace('<p class="bodytext">Iti <a name="M1.0228"></a> cittakiriyavāyodhātuvipphāravasena ayaṃ kāyasammato aṭṭhisaṅghāto abhikkamati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',231,replace(replace('<p class="gatha1">Karuṇā <a name="M1.0231"></a> viya sattesu, paññā yassa mahesino;</p>\r\n<p class="gathalast">Ñeyyadhammesu sabbesu, pavattittha yathāruci.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',235,replace(replace('<p class="gatha1">Dasāya <a name="M1.0235"></a> tāya sattesu, samussāhitamānaso;</p>\r\n<p class="gatha2">Pāṭihīrāvasānamhi, vasanto tidasālaye;</p>\r\n<p class="gatha3">Pāricchattakamūlamhi, paṇḍukambalanāmake;</p>\r\n<p class="gathalast">Silāsane sannisinno, ādiccova yugandhare.</p>\r\n<p class="gatha1">Cakkavāḷasahassehi, dasāhāgamma sabbaso;</p>\r\n<p class="gatha2">Sannisinnena devānaṃ, gaṇena parivārito;</p>\r\n<p class="gatha3">Mātaraṃ pamukhaṃ katvā, tassā paññāya tejasā;</p>\r\n<p class="gathalast">Abhidhammakathāmaggaṃ, devānaṃ sampavattayi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',236,replace(replace('<p class="gatha1">Tassa <a name="M1.0236"></a> pāde namassitvā, sambuddhassa sirīmato;</p>\r\n<p class="gathalast">Saddhammañcassa pūjetvā, katvā saṅghassa cañjaliṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',237,replace(replace('<p class="gatha1">Yaṃ <a name="M1.0237"></a> devadevo devānaṃ, desetvā nayato puna;</p>\r\n<p class="gathalast">Therassa sāriputtassa, samācikkhi vināyako.</p>\r\n<p class="gatha1">Anotattadahe katvā, upaṭṭhānaṃ mahesino;</p>\r\n<p class="gathalast">Yañca sutvāna so thero, āharitvā mahītalaṃ.</p>\r\n<p class="gatha1">Bhikkhūnaṃ parirudāhāsi, iti bhikkhūhi dhārito;</p>\r\n<p class="gathalast">Saṅgītikāle saṃṅgīto, vedehamuninā puna.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',238,replace(replace('<p class="gatha1">Yaṃ <a name="M1.0238"></a> karomasi brahmuno, samaṃ devehi mārisa;</p>\r\n<p class="gathalast">Tadajja tuyhaṃ kassāma, handa sāmaṃ karoma te.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',241,replace(replace('<p class="gatha1">Atthaṃ <a name="M1.0241"></a> pakāsayissāmi, āgamaṭṭhakathāsupi;</p>\r\n<p class="gathalast">Gahetabbaṃ gahetvāna, tosayanto vicakkhaṇe.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',242,replace(replace('<p class="gatha1">Itime <a name="M1.0242"></a> bhāsamānassa, abhidhammakathaṃ imaṃ;</p>\r\n<p class="gathalast">Avikkhittā nisāmetha, dullabhāhiayaṃkathā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',263,replace(replace('<p class="bodytext">Etthete <a name="M1.0263"></a> pāpakā akusalā dhammā aparisesā nirujjhanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',266,replace(replace('<p class="bodytext">Cakkhuṃ <a name="M1.0266"></a> cāvuso paṭicca rūpeca uppajjati cakkhuviññāṇaṃ, tiṇṇaṃ saṅgati phasso, phassapaccayā vedanā, yaṃ vedeti, taṃ sañjānāti. Yaṃ sañjānāti, taṃ vitakketi, yaṃ vitakketi, taṃ papañceti, yaṃ papañceti, tato nidānaṃ purisaṃ papañcasaññāsaṅkhā samudācaranti atītānāgatapaccuppannesu cakkhuviññeyyasu rūpesu –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',274,replace(replace('<p class="bodytext">Purimā <a name="M1.0274"></a> bhikkhave koṭi na paññāyati avijjāya.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',277,replace(replace('<p class="gatha1">Attheva <a name="M1.0277"></a> gambhīragataṃ sudubbudhaṃ,</p>\r\n<p class="gatha2">Sayaṃ abhiññāya sahetusambhavaṃ;</p>\r\n<p class="gatha3">Yathānupubbaṃ nikhilena desitaṃ,</p>\r\n<p class="gathalast">Mahesinā rūpagataṃva passati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',286,replace(replace('<p class="bodytext">Abhikkamissāmi <a name="M1.0286"></a> paṭikkamissāmīti hi cittaṃ uppajjamānaṃ rūpaṃ samuṭṭhāpeti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',308,replace(replace('<p class="gatha1">Na <a name="M1.0308"></a> antalikkhena na samuddamajjhe,</p>\r\n<p class="gatha2">Na pabbatānaṃ vivaraṃ pavissa;</p>\r\n<p class="gatha3">Na vijjate so jagatippadeso,</p>\r\n<p class="gathalast">Yatthaṭṭhito mucceyya pāpakammā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',311,replace(replace('<p class="gatha1">Yaṃ <a name="M1.0311"></a> pattaṃ kusalaṃ tassa, ānubhāvena pāṇino;</p>\r\n<p class="gatha2">Sabbe saddhammarājassa, ñatvā dhammaṃ sukhāvahaṃ;</p>\r\n<p class="gatha3">Pāpuṇantu visuddhāya, sukhāya paṭipattiyā;</p>\r\n<p class="gathalast">Asokamanupāyāsaṃ, nibbānasukhamuttamaṃ.</p>\r\n<p class="gatha1">Ciraṃ tiṭṭhatu saddhammo, dhamme hontu sagāravā;</p>\r\n<p class="gatha2">Sabbepi sattā kālena, sammā devo pavassatu;</p>\r\n<p class="gatha3">Yathā rakkhiṃsu porāṇā, surājāno tathevimaṃ;</p>\r\n<p class="gathalast">Rājā rakkhatu dhammena, attanova pajaṃpajaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',312,replace(replace('<p class="gatha1">Tāva <a name="M1.0312"></a> tiṭṭhatu lokasmiṃ, lokanittharaṇesinaṃ;</p>\r\n<p class="gatha2">Dassentī kulaputtānaṃ, nayaṃ paññāvisuddhiyā;</p>\r\n<p class="gatha3">Yāva buddhoti nāmampi, suddhacittassa tādino;</p>\r\n<p class="gathalast">Lokamhi lokajeṭṭhassa, pavattati mahesino.</p>\r\n<p class="subhead">Dutiyasannipāta</p>\r\n<p class="subhead">Mahāpadānasutta aṭṭhakathā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',1,replace(replace('<p class="bodytext">aṭṭhakathā <a name="M2.0001"></a> saṃgītiyā āvuso paṭhame sannipāte sakalā ca vinayasaṃvaṇṇanā dīghanikāye ca sīlakkhandhavaggavaṇṇanā abhidhamme ca dhammasaṅgahasaṃvaṇṇanā saṃgītā therehi chaṭṭhasaṃgītikārehi. Idāni pana dutiye sannipāte dīghanikāye mahāvaggavaṇṇanāto paṭṭhāya tadavasesānaṃ yathāvavatthitasaṃvaṇṇanānaṃ saṃgāyanokāso anuppatto. Tasmā imissā dutiyasannipātasaṃgītiyā pubbakiccavasena yathānuppattāya mahāvagge mahāpadānasuttasaṃvaṇṇanāya pucchāvissajjanaṃ kātuṃ samārabhāma. Mahāpadānasuttassa āvuso nidāne pariyāpannassa ‘‘karerikuṭikāya’’ntipadassa attho kathaṃ aṭṭhakathācariyena kathito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> mahāpadānasuttassa bhante nidāne pariyāpannassa karerikuṭikāyantipadassa attho ‘‘karerikuṭikāya’’nti karerīti varuṇarukkhassa nāmaṃ. Karerimaṇḍapo tassā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',2,replace(replace('<p class="bodytext">kuṭikāya <a name="M2.0002"></a> dvāre ṭhito, tasmā karerikuṭikāti vuccati. Yathā kosambarukkhassa dvāreṭhitattā kosambakuṭikāti evamādinā bhante aṭṭhakathācariyena kathito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',3,replace(replace('<p class="bodytext">Karerikuṭikāyanti <a name="M2.0003"></a> karerīti varuṇarukkhassa nāmaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',4,replace(replace('<p class="bodytext">Karerimaṇḍapo <a name="M2.0004"></a> tassā kuṭikāya dvāre ṭhito, tasmā karerikuṭikāti vuccati. Yathā kosambarukkhassa dvāre ṭhitattā ekāsambakuṭikāti.</p>\r\n<p class="bodytext">Antojetavane kira karerikuṭi kosambakuṭi gandhakuṭi salaḷāgāranti cattāri mahāgehāni –</p>\r\n<p class="bodytext">Ekekaṃ satasahassapariccāgena nipphannaṃ.</p>\r\n<p class="bodytext">Tesu salaḷāgāraṃ raññā pasenadinā kāritaṃ.</p>\r\n<p class="subhead">Pubbenivāsa</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',5,replace(replace('<p class="bodytext">tattheva <a name="M2.0005"></a> āvuso nidāne pubbenivāsapaṭisaṃyuttā dhammīkathā udapādītivacanassa attho kathaṃ aṭṭhakathācariyena kathito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante nidāne pubbenivāsapaṭisaṃyuttā dhammīkathā udapādīti vacanassa attho pubbenivāsapaṭisaṃyuttāti ekampijātiṃ dvepi jātiyoti evaṃ nibaddhena pubbenivuṭṭhakhandhasantānasaṅkhātena pubbenivāsena saddhiṃ yojetvā pavattitā. Dhammīti dhammasaṃyuttā. Udapādīti aho acchariyaṃ dasabalassa pubbenivāsañāṇaṃ. Pubbenivāsaṃ nāma ke anussaranti ke nānussarantīti diṭṭhiyā anussaranti, sāvakā sa paccekabuddhā ca buddhā ca anussaranti. Kataradiṭṭhiyo anussaranti. Ye aggapattā kammavādino tepi cattālīsaṃyeva kappe anussaranti, na tato paranti evamādinā bhante aṭṭhakathācariyena kathito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',6,replace(replace('<p class="bodytext">Pubbenivāsapaṭisaṃyuttāti <a name="M2.0006"></a> ekampi jātiṃ dvepi jātiyoti evaṃ nibaddhena pubbenivuṭṭhakhandhasantānasaṅkhātena pubbenivāsena saddhiṃ yojetvā pavattitā.</p>\r\n<p class="subhead">Kappaparicchedavāra</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',7,replace(replace('<p class="bodytext">tasmiṃ <a name="M2.0007"></a> āvuso mahāpadānasutte uddesavāre navasu paricchedavāresu paṭhame kappaparicchedavāre bhaddakappeti padassa attho aṭṭhakathācariyena kathaṃ kathito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',8,replace(replace('<p class="bodytext">tasmiṃ <a name="M2.0008"></a> bhante mahāpadānasutte uddesavāre navasu paricchedavāresu paṭhame kappaparicchedavāre bhaddakappeti padassa attho bhaddakappeti pañcabuddhuppādapaṭimaṇḍitattā sundarakappe sārakappeti bhagavā imaṃ kappaṃ thomento evamāha. Yato paṭṭhāya kira amhākaṃ bhagavā abhinīhāro kato, ekasmiṃ antare ekasmimpi kappe pañcabuddhā nibbattā nāma natthīti evamādinā bhante aṭṭhakathācariyena kathito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',9,replace(replace('<p class="bodytext">Vipassissa <a name="M2.0009"></a> bhikkhave bhagavato ito so bhikkhave chanavutikappe yaṃ vipassī bhagavā arahaṃ sammāsambuddho loke udapādi –</p>\r\n<p class="bodytext">Saddakappeti pañcabuddhuppādapaṭimaṇḍitattā sundarakappe sārakappeti bhagavā imaṃ kappaṃ thomento evamāha –</p>\r\n<p class="bodytext">‘‘Yato paṭṭhāya kira amhākaṃ bhagavatābhinīhāro kato, etasmiṃ antare ekasmimpi kappe pañcabuddhā nibbattā nāma natthi’’ –</p>\r\n<p class="subhead">Āyuparicchedavāra</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',10,replace(replace('<p class="bodytext">catutthe <a name="M2.0010"></a> panāvuso āyuparicchedavāre appaṃ vā bhiyyoti etesaṃ padānaṃ attho kathaṃ aṭṭhakathācariyena kathito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> catutthe pana bhante āyuparicchedavāre appaṃ vā bhiyyoti etesaṃ padānaṃ attho ‘‘appaṃ vā bhiyyoti vassasatato vā upari appaṃ, aññaṃ vassasataṃ apatvā vīsaṃ vā tiṃsaṃ vā cattālīsaṃ vā paṇṇāsaṃ vā saṭṭhi vā vassāni jīvati. Evaṃ dīghāyuko pana atidullabho, asuko kira evaṃ ciraṃ jīvatīti tattha tattha gantvā daṭṭhabbo hotī’’ti evamādinā bhante tattha aṭṭhakathācariyena kathito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',11,replace(replace('<p class="bodytext">Mayhaṃ <a name="M2.0011"></a> bhikkhave etarahi appakaṃ āyuppamāṇaṃ parittaṃ lahukaṃ yo ciraṃ jīvati so vassasataṃ appaṃ vā bhiyyo.</p>\r\n<p class="bodytext">Appaṃ vā bhiyyoti vassasatato vā upari appaṃ, ayyaṃ vassasataṃ apatvā vīsaṃ vā tiṃsaṃ vā cattālīsaṃvā vā paṇṇāsaṃ vā saṭṭhi vā vassāni jīvati–</p>\r\n<p class="subhead">Upaṭṭhākaparicchedavāra</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',12,replace(replace('<p class="bodytext">aṭṭhame <a name="M2.0012"></a> panāvuso upaṭṭhākaparicchedavāre ānandotipade kathaṃ aṭṭhakathācariyena vaṇṇito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṭṭhame pana bhante upaṭṭhākaparicchede pana ānandoti nibaddhupaṭṭhākabhāvaṃ sandhāya vuttanti evamādinā bhante aṭṭhakathācariyena vaṇṇito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',13,replace(replace('<p class="bodytext">Mayhaṃ <a name="M2.0013"></a> bhikkhave etarahi ānando nāma bhikkhu upaṭṭhāko ahosi aggupaṭṭhāko–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',14,replace(replace('<p class="bodytext">Bhagavatohi <a name="M2.0014"></a> paṭhamabodhiyaṃ anibaddhā upaṭṭhākā ahesuṃ.</p>\r\n<p class="bodytext">Ekadā nāgasamālo pattacīvaraṃ gahetvā vicari.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',15,replace(replace('<p class="bodytext">Idameva <a name="M2.0015"></a> te kāraṇaṃ sallakkhetvā nivārayimha.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',16,replace(replace('<p class="bodytext">Ekacce <a name="M2.0016"></a> bhikkhū iminā maggena gacchāmāti vutte aññena gacchanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',17,replace(replace('<p class="bodytext">Uṭṭhehi <a name="M2.0017"></a> āvuso ānanda, uṭṭhehi āvuso ānanda.</p>\r\n<p class="bodytext">Kaṃ panettha ānanda ādīnavaṃ passasi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',18,replace(replace('<p class="bodytext">Kaṃ <a name="M2.0018"></a> panettha ānanda ānisaṃsaṃ passasi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',28,replace(replace('<p class="gatha1">Samavattakkhandho <a name="M2.0028"></a> atulo, suppabuddho ca uttaro;</p>\r\n<p class="gathalast">Sattavāho vijitaseno, rāhulo bhavati sattamo –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',29,replace(replace('<p class="gatha1">Sutanā <a name="M2.0029"></a> sabbakāmāca, sucittā atha rocinī;</p>\r\n<p class="gathalast">Rucaggatī sunandāca, bimbā bhavati sattamā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',37,replace(replace('<p class="gatha1">Anekajātisaṃsāraṃ<a name="M2.0037"></a>, sandhāvissaṃ anibbisaṃ;</p>\r\n<p class="gatha2">Gahakāraṃ gavesanto, dukkhā jāti punappunaṃ;</p>\r\n<p class="gathalast">Gahakāraka diṭṭhosi, puna gehaṃ na kāhasi.</p>\r\n<p class="gatha1">Sabbā te phāsukā bhaggā, gahakūṭaṃ visaṅkhataṃ;</p>\r\n<p class="gathalast">Visaṅkhāragataṃ cittaṃ, taṇhānaṃ khayamajjhagā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',40,replace(replace('<p class="gatha1">Ayoghanahatasseva<a name="M2.0040"></a>, jalato jātavedaso;</p>\r\n<p class="gatha2">Anupubbūpasantassa, yathā na ñāñate gati;</p>\r\n<p class="gatha3">Evaṃ sammāvimuttānaṃ, kāmabandhoghatārinaṃ;</p>\r\n<p class="gathalast">Paññāpetuṃ gati natthi, pattānaṃ acalaṃ sukhaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',44,replace(replace('<p class="bodytext">Na <a name="M2.0044"></a> pana bhagavā milakkhusadiso hoti nāpi āmuttamaṇikuṇḍalo.</p>\r\n<p class="bodytext">Te pana attano samānasaṇṭhānameva passanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',48,replace(replace('<p class="bodytext">Na <a name="M2.0048"></a> kho ānanda ettāvatā tathāgato sakkato vā hoti garukato vā mānito vā pūjito vā apacito vā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',50,replace(replace('<p class="bodytext">Kasmā <a name="M2.0050"></a> pana bhagavā aññattha ekaṃ umāpupphamattampi gahetvā buddha guṇe āvajjetvā katāya pūjāya buddhañāṇenāpi aparicchinnaṃ vipākaṃ vaṇṇetvā–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',55,replace(replace('<p class="bodytext">Yo <a name="M2.0055"></a> kho ānanda bhikkhu vā bhikkhunī vā upāsako vā upāsikā vā dhammānudhammappaṭipanno viharati sāmīcippaṭipanno anudhammacārī so tathā gataṃ sakkaroti garukaroti māneti pūjeti apaciyati paramāya –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',59,replace(replace('<p class="bodytext">Siyā <a name="M2.0059"></a> kho panānanda tumhākaṃ evamassa ‘atītasatthukaṃ pāvacanaṃ, natthi no satthā’ti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',60,replace(replace('<p class="bodytext">Na <a name="M2.0060"></a> kho panetaṃ ānanda evaṃ daṭṭhabbaṃ.</p>\r\n<p class="bodytext">Dhammopi desito ceva paññattoca, vinayopi desito ceva paññatto ca–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',67,replace(replace('<p class="bodytext">Assosi <a name="M2.0067"></a> kho rājā māgadho ajātasattu vedehiputto bhagavā kira kusinārāyaṃ parinibbutoti.</p>\r\n<p class="bodytext">Satthā nāma parinibbuto, na so sakkā puna āharituṃ, pothujjanikasaddhāya pana amhākaṃ raññā sadiso natthi, sace esa imināva niyāmena suṇissati, hadayamassa phalissati. Rājā kho pana amhehi anurakkhitabboti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',68,replace(replace('<p class="bodytext">Deva <a name="M2.0068"></a> amhehi supinako diṭṭho, tassa paṭighātatthaṃ tumhehi dukūladupaṭṭaṃ nivāsetvā yathā nāsāpuṭamattaṃ paññāyati, evaṃ catumadhuradoṇiyā nipajjituṃ vaṭṭatīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',69,replace(replace('<p class="bodytext">Deva <a name="M2.0069"></a> maraṇato muccanakasatto nāma natthi, amhākaṃ āyuvaḍḍhano cetiyaṭṭhānaṃ puññakkhettaṃ abhisekasiñcako so bhagavā satthā kusinārāya parinibbutoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',71,replace(replace('<p class="bodytext">Bhagavā <a name="M2.0071"></a> sabbaññu nanu imasmiṃ ṭhāne nisīditvā dhammaṃ desayittha, sokasallaṃ me vinodayittha, tumhe mayhaṃ sokasallaṃ nīharittha, ahaṃ tumhākaṃ saraṇaṃ gato, idāni pana me paṭivacanampi na detha bhagavāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',72,replace(replace('<p class="bodytext">Nanu <a name="M2.0072"></a> bhagavā ahaṃ aññadā evarūpe kāle tumhe mahābhikkhu saṅghaparivārā jambudīpatale cārikaṃ carathāti suṇomi.</p>\r\n<p class="bodytext">Mama parideviteneva na sijjhati, dasabalassa dhātuyo āharāpessāmīti evaṃ assosi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',73,replace(replace('<p class="bodytext">Sace <a name="M2.0073"></a> dassanti, sundaraṃ. No ce dassanti, āharaṇupāyena āharissāmīti caturaṅginiṃ senaṃ sannayhitvā sayampi nikkhantoyeva.</p>\r\n<p class="bodytext">Yathā ca ajātasattu, evaṃ licchavī ādayopi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',74,replace(replace('<p class="bodytext">Amhākaṃ <a name="M2.0074"></a> dhātuyo vā dentu, yuddhaṃ vāti kusinārānagaraṃ parivāretvā ṭhite etaṃ bhagavā amhākaṃ gāmakkhettehi paṭivacanaṃ avocuṃ.</p>\r\n<p class="bodytext">Na mayaṃ satthu sāsanaṃ pahiṇimha, nāpi gantvā ānayimha. Satthā pana sayameva āgantvā sāsanaṃ pesetvā amhe pakkosāpesi. Tumhepi kho pana yaṃ tumhākaṃ gāmakkhette ratanaṃ uppajjati. Na taṃ amhākaṃ detha. Sadevake ca loke buddharatanasamaṃ ratanaṃ nāma </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',75,replace(replace('<p class="bodytext">natthi<a name="M2.0075"></a>, evarūpaṃ uttamaratanaṃ labhitvā mayaṃ nadassāma, na kho pana tumhehiyeva mātuthanato khīraṃ pītaṃ. Amhehipi mātuthanato khīraṃ pītaṃ, na tumheyeva purisā, amhepi purisā. Hotūti aññamaññaṃ ahaṃkāraṃ katvā sāsanapaṭisāsanaṃ pesenti. Aññamaññaṃ mānagajjitaṃ gajjanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',76,replace(replace('<p class="bodytext">Ete <a name="M2.0076"></a> rājāno bhagavato parinibbutaṭṭhāne vivādaṃ karonti, na kho panetaṃ patirūpaṃ, alaṃ iminā kalahena, vūpasamessāmi nanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',77,replace(replace('<p class="bodytext">Ācariyassa <a name="M2.0077"></a> viya bho saddo, ācariyassa viya bho saddoti sabbe niravā ahesuṃ.</p>\r\n<p class="bodytext">(1)</p>\r\n<p class="gatha1">Suṇantu bhonto mama ekavācaṃ,</p>\r\n<p class="gatha2">Amhāka buddho ahu khantivādo;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',78,replace(replace('<p class="gatha3">Nahi <a name="M2.0078"></a> sādhuyaṃ uttamapuggalassa,</p>\r\n<p class="gathalast">Sarīrabhāge siyā sampahāro.</p>\r\n<p class="bodytext">(2)</p>\r\n<p class="gatha1">Sabbeva bhonto sahitā samaggā,</p>\r\n<p class="gatha2">Sammodamānā karomaṭṭhabhāge;</p>\r\n<p class="gatha3">Vitthārikā hontu, disāsu thūpā;</p>\r\n<p class="gathalast">Bahū janā cakkhumato pasannā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',79,replace(replace('<p class="bodytext">Na <a name="M2.0079"></a> hi sādhukaṃ uttamapuggalassa, sarīrabhāge siyā sampahāroti na hi sādhuyanti na hi sādhu ayaṃ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',81,replace(replace('<p class="bodytext">Bhagavā <a name="M2.0081"></a> sabbasu pubbe mayaṃ tumhākaṃ dvattiṃsamahāpurisalakkhaṇapaṭimaṇḍitaṃ chabbaṇṇabuddharasmikhacitaṃ asītianubyañjanasamujjalitasobhaṃ suvaṇṇavaṇṇaṃ sarīraṃ addasāma, idāni pana suvaṇṇavaṇṇāva dhātuyo avasiṭṭhā jātā, nayuttamidaṃ bhagavā tumhākanti parideviṃsu.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',82,replace(replace('<p class="bodytext">Kena <a name="M2.0082"></a> nu kho sadevakassa lokassa kaṅkhacchedanatthāya catusaccakathāya paccayabhūtā bhagavato dakkhiṇadāṭhā gahitāti olokento brāhmaṇena gahitāti disvā brāhmaṇopi daṭhāya anucchavikaṃ sakkāraṃ nasakkhissati gaṇhāmi natthi veṭṭhantarato gahetvā suvaṇṇacaṅkoṭake ṭhapetvā devalokaṃ netvā cūḷāmaṇicetiye patiṭṭhapesi –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',84,replace(replace('<p class="bodytext">Rājagahe <a name="M2.0084"></a> bhagavato sarīrānaṃ thūpañca mahañca akāsi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',85,replace(replace('<p class="bodytext">Samaṇassa <a name="M2.0085"></a> gotamassa parinibbutakālato paṭṭhāya balakkārena sādhukīḷitāya upaddutumha, sabbe no kammantā naṭṭhāti.</p>\r\n<p class="bodytext">Mahājano manaṃ padosetvā apāye nibbattīti disvā sakkaṃ devarājānaṃ dhātuāharaṇūpāyaṃ kāressāmāti tassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',86,replace(replace('<p class="bodytext">santikaṃ <a name="M2.0086"></a> gantvā tamatthaṃ ārocetvā dhātuāharaṇūpāyaṃ karomi mahārājāti āhaṃsu.</p>\r\n<p class="bodytext">Bhante puthujjano nāma ajātasattunā samo saddho nāma natthi, na so mama vacanaṃ karissati, apica kho māravibhiṃ sakasadisaṃ vibhiṃsakaṃ dassessāmi mahāsaddaṃ sāvessāmi yakkhagāhakakhipitakaarocake karissāmi, tumhe amanussā mahārāja kupitā dhātuyo āharāpethāti vadeyyātha, evaṃ so āharāpessatīti–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',87,replace(replace('<p class="bodytext">Mahārāja <a name="M2.0087"></a> amanussā kupitā, dhātuyo āharāpehīti bhaṇiṃsu.</p>\r\n<p class="bodytext">Na tāva bhante mayhaṃ cittaṃ tussati, evaṃ santepi āharantūti āha.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',88,replace(replace('<p class="bodytext">Mahārāja <a name="M2.0088"></a> ekaṃ dhātunidhānaṃ kātuṃ vaṭṭatīti āha.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',89,replace(replace('<p class="bodytext">Anāgate <a name="M2.0089"></a> laṅkādīpe mahāvihāre mahācetiyamhi nidahissanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',90,replace(replace('<p class="subhead">Mahāsāvaka cetī</p>\r\n<p class="bodytext">Imasmiṃ <a name="M2.0090"></a> ṭhāne yo pāsāṇo atthi, so antaradhāyatu, paṃsu suvisuddhā hotu, udakaṃ mā uṭṭhahatūti adhiṭṭhāsi.</p>\r\n<p class="bodytext">Idha rājā kiṃ kāretīti pucchantānampi mahāsāvakānaṃ cetiyānīti vadanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',93,replace(replace('<p class="bodytext">Mālā <a name="M2.0093"></a> mā milāyantu, gandhā mā vinassantu, dīpā mā vijjhāyantūti adhiṭṭhahitvā suvaṇṇapaṭṭe akkharāni chindāpesi –</p>\r\n<p class="bodytext">Anāgate piyadoso nāma kumāro chattaṃ ussāpetvā asoko dhammarājā bhavissati, so imā dhātuyo vitthārikā karissatīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',94,replace(replace('<p class="bodytext">Anāgate <a name="M2.0094"></a> daliddarājā imaṃ maṇiṃ gahetvā dhātūnaṃ sakkāraṃ karotūti akkharaṃ chindāpesi.</p>\r\n<p class="bodytext">Tāta ajātasattunā dhātunidhānaṃ kataṃ, ettha ārakkhaṃ paṭṭhapehīti pahiṇi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',99,replace(replace('<p class="bodytext">Parikkhīṇodāni <a name="M2.0099"></a> me āyūti aññāsi.</p>\r\n<p class="bodytext">Yesañca devaputtānaṃ maraṇanimittāni āvi bhavanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',100,replace(replace('<p class="bodytext">Nassati <a name="M2.0100"></a> vata bho me ayaṃ sampattīti bhayābhibhūto ahosi.</p>\r\n<p class="bodytext">Atthi nu kho koci samaṇo vā brāhmaṇo vā lokapitā maho mahābrahmāvā, yo me hadayanissitaṃ sokasallaṃ samuddharitvā imaṃ sampattiṃ thāvaraṃ kareyyāti olokento kañci adisvā puna addasa mādisānaṃ satasahassānampi uppannaṃ sokasallaṃ sammāsambuddho uddharituṃ paṭibaloti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',102,replace(replace('<p class="bodytext">Aparipakkaṃ <a name="M2.0102"></a> tāvassa ñāṇaṃ, katipāhaṃ pana atikkamitvā mayi indasālaguhāyaṃ viharante pañca pubbanimittāni disvā maraṇabhayabhīto dvīsu devalokesu devatāhi saddhiṃ upasaṅkamitvā cuddasa pañhe pucchitvā upekkhāpañhavissajjanāvasāne asītiyā devatāsahassehi saddhiṃ sotāpattiphale patiṭṭhahissatīti cintetvā okāsaṃ nākāsi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',103,replace(replace('<p class="bodytext">Mama <a name="M2.0103"></a> pubbepi ekakassa gatattā satthārā okāso nakato, addhā me natthi maggaphalassa upanissayo, ekassa pana upanissaye sati cakkavāḷapariyantāyapi parisāya bhagavā dhammaṃ desetiyeva. Avassaṃ kho pana dvīsu devalokesu kassaci devassa upanissayo bhavissati, taṃ sandhāya satthā dammaṃ desessati. Taṃ sutvā ahampi attano domanassaṃ vūpasamessāmīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',104,replace(replace('<p class="bodytext">Dvīsu <a name="M2.0104"></a> devalokesu devatā gahetvā dhurena paharantassa viya satthāraṃ upasaṅkamituṃ nayuttaṃ, ayaṃpana pañcasikho dasabalassa upaṭṭhāko <a name="M2.0105"></a> vallabho icchiticchitakkhaṇe gantvā pañhaṃ pucchitvā dhammaṃ suṇāti, imaṃ purato pesetvā okāsaṃ kāretvā iminā katokāse upasaṅkamitvā pañhaṃ pucchissāmīti okāsakaraṇatthaṃ āmantesi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',105,replace(replace('<p class="bodytext">upaṭṭhāko <a name="M2.0105"></a> vallabho icchiticchitakkhaṇe gantvā pañhaṃ pucchitvā dhammaṃ suṇāti, imaṃ purato pesetvā okāsaṃ kāretvā iminā katokāse upasaṅkamitvā pañhaṃ pucchissāmīti okāsakaraṇatthaṃ āmantesi.</p>\r\n<p class="bodytext">Evaṃ mahārāja hotu, bhaddaṃ tava yo tvaṃ maṃ ehi mārisa uyyāna kīḷādīni vā naṭasamajjādīni vā dassanāya gacchāmāti avatvā buddhaṃ passissāma dhammaṃ sossāmāti vadasīti daḷhataraṃ upatthambhento devānamindassa vacanaṃ paṭissutvā anucariyaṃ sahacaraṇaṃ ekato gamanaṃ upāgami.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',113,replace(replace('<p class="gatha1">Vande <a name="M2.0113"></a> te pitaraṃ bhadde, timbaruṃ sūriyavacchase;</p>\r\n<p class="gathalast">Yena jātāsi kalyāṇī, ānandajananī mama.</p>\r\n<p class="bodytext">Evaṃ vutte bhagavā pañcasikhaṃ gandhabbadevaputtaṃ etadavoca. Saṃsandati kho te pañcasikha tantissaro gītassarena gītassaro ca tantissarena, naca pana te pañcasikha tantissaro gītassaraṃ ativattati gītassaro ca tantissaraṃ. Kadā saṃyūḷhā pana te pañcasikha vmi gāthā buddhūpasañhitā dhammūpasañhitā saṃyūpasañhitā arahantūpasañhitā kāmūpasañhitāti –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',116,replace(replace('<p class="bodytext">Bhagavatā <a name="M2.0116"></a> pañcasikhassa okāso nakatoti devatā gahetvā tatova paṭinivatteyya. Tato mahājāniyo bhaveyya, vaṇṇe pana kathite kato bhagavatā pañcasikhassa okāsoti devatāhi saddhiṃ upasaṅkamitvā pañhaṃ pucchitvā vissajjanāvasāne asītiyā devatāsahassehi saddhiṃ sotāpattiphale patiṭṭhahissatīti ñatvā vaṇṇaṃ kathesi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',118,replace(replace('<p class="bodytext">Atha <a name="M2.0118"></a> kho bhagavā sakkaṃ devānamindaṃ etadavoca ‘‘acchariyamidaṃ āyasmato kosiyassa, abbhutamidaṃ āyasmato kosiyassa tāva bahukiccassa bahukaraṇīyassa yadidaṃ idhāgamana’’nti.</p>\r\n<p class="bodytext">Cirapaṭikāhaṃ bhante bhagavantaṃ dassanāya upasaṅkamitukāmo, api ca devānaṃ tāvatiṃsānaṃ kehici kehici kiccakaraṇīyehi byāvaṭo, evāhaṃ nāsakkhiṃ bhagavantaṃ dassanāya upasaṅkamituṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',120,replace(replace('<p class="bodytext">Te <a name="M2.0120"></a> ‘‘tava santakā, mama santakā’’ti nicchetuṃ asakkontā aḍḍaṃ karonti, sakkaṃ devarājānaṃ pucchanti, so ‘‘yassa vimānaṃ āsannataraṃ, tassa santakā’’ti vadati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',121,replace(replace('<p class="bodytext">Yassa <a name="M2.0121"></a> vimānaṃ olokentī ṭhitā, tassa santakāti vadati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',122,replace(replace('<p class="bodytext">Idheva <a name="M2.0122"></a> bhante kapilavatthusmiṃ gopikā nāma sakyadhītā ahosi buddhe pasannā dhamme pasannā saṅghe pasannā sīlesu paripūrakārinī, sā itthittaṃ virājetvā purisattaṃ bhāvetvā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapannā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',128,replace(replace('<p class="gatha1">Tassa <a name="M2.0128"></a> dhammassa pattiyā, āgatamhāsi mārisa;</p>\r\n<p class="gathalast">Katāvakāsā bhagavatā, pañhaṃ pucchemu mārisa.</p>\r\n<p class="gatha1">Puccha vāsava maṃ pañhaṃ, yaṃ kiñci manasicchasi;</p>\r\n<p class="gathalast">Tassa tasseva pañhassa, ahaṃ antaṃ karomi te.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',129,replace(replace('<p class="bodytext">Kiṃ <a name="M2.0129"></a> saṃyojanā nu kho mārisa devā manussā asurā nāgā gandhabbā ye caññe santi puthukāyā, te averā adaṇḍā asapattā abyāpajjā viharemu averinoti iti ca nesaṃ hoti. Atha ca pana saverā sadaṇḍā sasapattā sabyāpajjā viharanti saverinoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',130,replace(replace('<p class="bodytext">Issāmacchariyasaṃyojanā <a name="M2.0130"></a> kho devānaminda devā manussā asurā nāgā gandhabbā yecaññe santi puthukāyā, te averā adaṇḍā asapattā abyāpajjā viharemu averinoti iti ca nesaṃhoti, atha ca pana saverā sadaṇḍā sasapattā sabyāpajjā viharanti saverinoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',132,replace(replace('<p class="bodytext">Issatīti <a name="M2.0132"></a> issā, sā parasampattīnaṃ usūyanalakkhaṇā, tattheva anabhiratirasā, tato vimukhabhāvapaccupaṭṭhānā, parasampatti padaṭṭhānā, saṃyojananti daṭṭhabbā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',133,replace(replace('<p class="bodytext">Maccherabhāvo <a name="M2.0133"></a> macchariyaṃ, taṃladdhānaṃ vā labhitabbānaṃ vā attano sampattīnaṃ nigūhanalakkhaṇaṃ, tāsaṃyeva parehi sādhāraṇabhāva akkhamanarasaṃ, saṅkocanapaccupaṭṭhānaṃ, kaṭukañcukatā paccupaṭṭhānaṃ vā, attasampattipadaṭṭhānaṃ, cetaso virūpabhāvoti daṭṭhabbaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',136,replace(replace('<p class="bodytext">Kiṃ <a name="M2.0136"></a> vaṇṇo esoti taṃ taṃ dosaṃ vadanto pariyattiñca kassaci kiñci adento dubbaṇṇo ceva eḷamūgoca hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',138,replace(replace('<p class="bodytext">Yāva <a name="M2.0138"></a> taṃ nappahīyati, tāva devamanussā averatādīni patthayantāpi verādīni naparimuccantiyeva.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',139,replace(replace('<p class="bodytext">Atha <a name="M2.0139"></a> kho sakko devānamindo bhagavantaṃ uttari pañhaṃ apucchi–</p>\r\n<p class="bodytext">Issāmacchariyaṃ pana mārisa kiṃ nidānaṃ kiṃ samudayaṃ kiṃ jātikaṃ kiṃ pabhavaṃ, kismiṃ sati issāmacchariyaṃ hoti, kismiṃ asati issāmacchariyaṃ na hotīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',140,replace(replace('<p class="bodytext">Issāmacchariyaṃ <a name="M2.0140"></a> kho devānaminda piyāppiyanidānaṃ piyāppiyasamudayaṃ piyāppiyajātikaṃ piyāppiyapabhavaṃ, piyāppiye sati issāmacchariyaṃ hoti, piyāppiye asati issāmacchariyaṃ nahotīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',141,replace(replace('<p class="bodytext">Na <a name="M2.0141"></a> sakkā dātuṃ, kilamissati vā ukkaṇṭhissati vātiādīni vatvā macchariyaṃ karoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',142,replace(replace('<p class="bodytext">Aho <a name="M2.0142"></a> vatassa evarūpaṃ na bhaveyyāti issaṃ karoti.</p>\r\n<p class="bodytext">Mayampetaṃ mamāyantā na paribhuñjāma, na sakkā dātunti macchariya karoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',143,replace(replace('<p class="bodytext">Ṭhapetvā <a name="M2.0143"></a> maṃ ko añño evarūpassa lābhīti issaṃ vā karoti, yācito tāvakālikampi adadamāno macchariyaṃ vā karoti.</p>\r\n<p class="bodytext">Piyāppiyaṃ kho pana mārisa kiṃnidānaṃ kiṃsamudayaṃ kiṃjātikaṃ kiṃpabhavaṃ, kismiṃ sati piyāppiyaṃ hoti, kismiṃ asati piyāppiyaṃ na hotīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',144,replace(replace('<p class="bodytext">Piyāppiyaṃ <a name="M2.0144"></a> kho devānaminda chandanidānaṃ chandasamudayaṃ chandajātikaṃ chandapabhavaṃ, chande sati piyāppiyaṃ hoti, chande asati piyāppiyaṃ na hotīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',145,replace(replace('<p class="bodytext">Chandanidānantiettha <a name="M2.0145"></a> pariyesanachando, paṭilābhachando, paribhoga chando, sannidhichando, vissajjanachandoti pañcavidho chando.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',147,replace(replace('<p class="bodytext">Ime <a name="M2.0147"></a> maṃ rakkhissanti gopissanti mamāyissanti samparivārayissantīti.</p>\r\n<p class="bodytext">Chando kho pana mārisa kiṃnidāno kiṃsamudayo kiṃjātiko kiṃpabhavo, kismiṃ sati chando hoti, kismiṃ asati chando nahotīti. Chando kho devānaminda vitakkanidāno vitakkasamudayo vitakkajātiko vitakkapabhavo, vitakke sati chando hoti, vitakke asati chando na hotīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',150,replace(replace('<p class="bodytext">Ettakaṃ <a name="M2.0150"></a> rūpassa bhavissati, ettakaṃ saddassa, ettakaṃ gandhassa, ettakaṃ rasassa, ettakaṃ phoṭṭhabbassa bhavissati, ettakaṃ mayhaṃ bhavissati, ettakaṃ parassa bhavissati, ettakaṃ nidahissāmi, ettakaṃ parassa dassāmīti vavatthānaṃ vitakkavinicchayena hoti. Tenāha chando kho devānaminda vitakkanidānoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',152,replace(replace('<p class="bodytext">Kathaṃ <a name="M2.0152"></a> paṭipanno pana mārisa bhikkhu papañcasaññāsaṅkhānirodhasāruppagāminiṃ paṭipadaṃ paṭipanno hotīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',153,replace(replace('<p class="bodytext">Somanassaṃpāhaṃ <a name="M2.0153"></a> devānaminda duvidhena vadāmi sevitabbaṃpi asevitabbaṃpi. Domanassaṃpāhaṃ devānaminda duvidhena vadāmi sevitabbaṃpi asevitabbaṃpi. Upekkhaṃpāhaṃ devānaminda duvidhena vadāmi sevitabbaṃpi asevitabbaṃpi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',154,replace(replace('<p class="bodytext">Kiṃ <a name="M2.0154"></a> pana bhagavatā pucchitaṃ kathitaṃ apucchitaṃ, sānusandhikaṃ ananusandhikanti.</p>\r\n<p class="bodytext">Pucchitameva kathitaṃ no apucchitaṃ. Sānusandhikameva no ananusandhikaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',158,replace(replace('<p class="bodytext">Ime <a name="M2.0158"></a> pañcakkhandhā kiṃ hetukāti upaparikkhandho avijjādihetukāti passati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',160,replace(replace('<p class="bodytext">Ahosukhaṃ <a name="M2.0160"></a> ahosukhanti vācaṃ nicchārayamānavasena uppajjati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',161,replace(replace('<p class="bodytext">Ahodukkhaṃ <a name="M2.0161"></a> ahodukkhanti vippalāpayamānameva uppajjati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',163,replace(replace('<p class="bodytext">‘‘Somanassaṃpāhaṃ <a name="M2.0163"></a> devānaminda duvidhena vadāmi sevitabbaṃpi asevitabbaṃpī’’ti itikho panetaṃ vuttaṃ, kiñcetaṃ paṭicca vuttaṃ. Tattha yaṃ jaññā somanassaṃ ‘‘imaṃ kho me somanassaṃ sevato akusalā dhammā abhivaḍḍhanti, kusalā dhammā parihāyantī’’ti, evarūpaṃ somanassaṃ na <a name="M2.0164"></a> sevitabbaṃ. Tattha yaṃ jaññā somanassaṃ ‘‘imaṃ kho me somanassaṃ sevato akusalā dhammā parihāyanti, kusalā dhammā abhivaḍḍhantī’’ti, evarūpaṃ somanassaṃ sevitabbaṃ. Tattha yaṃ ce savitakkaṃ savicāraṃ, yaṃce avitakkaṃ avicāraṃ, ye avitakke avicāre, te paṇītatare. ‘‘Somanassaṃpāhaṃ devānaminda duvidhena vadāmi sevitabbaṃpi asevitabbaṃpī’’ti iti yaṃ taṃ vuttaṃ, idametaṃ paṭicca vuttaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',164,replace(replace('<p class="bodytext">na <a name="M2.0164"></a> sevitabbaṃ. Tattha yaṃ jaññā somanassaṃ ‘‘imaṃ kho me somanassaṃ sevato akusalā dhammā parihāyanti, kusalā dhammā abhivaḍḍhantī’’ti, evarūpaṃ somanassaṃ sevitabbaṃ. Tattha yaṃ ce savitakkaṃ savicāraṃ, yaṃce avitakkaṃ avicāraṃ, ye avitakke avicāre, te paṇītatare. ‘‘Somanassaṃpāhaṃ devānaminda duvidhena vadāmi sevitabbaṃpi asevitabbaṃpī’’ti iti yaṃ taṃ vuttaṃ, idametaṃ paṭicca vuttaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',176,replace(replace('<p class="bodytext">Na <a name="M2.0176"></a> laddhaṃ vata me sabrahmacārīhi saddhiṃ visuddhipavāraṇaṃ pavāretunti āvajjato domanassaṃ uppajjati, assudhārā pavattanti gāmanta pabbhāravāsī mahāsīvattherassa viya.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',177,replace(replace('<p class="bodytext">Amhākaṃ <a name="M2.0177"></a> ācariyo aññesaṃ avassayo hoti, attano bhavituṃ na sakkoti, ovādamassa dassāmīti ākāsena gantvā vihārasamīpe otaritvā divāṭṭhāne nisinnaṃ ācariyaṃ upasaṅkamitvā vattaṃ dassetvā ekamantaṃ nisīdi.</p>\r\n<p class="bodytext">Kiṃ kāraṇā āgatosi piṇḍapātikāti āha.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',179,replace(replace('<p class="bodytext">Na <a name="M2.0179"></a> te tumhākaṃ anumodanāya atthoti ākāse uppatitvā agamāsi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',180,replace(replace('<p class="bodytext">Imassa <a name="M2.0180"></a> bhikkhuno pariyattiyā kammaṃ natthi, mayhaṃ pana aṅkusako bhavissāmīti āgatoti ñatvā idāni okāso na bhavissati, paccūsakāle gamissāmīti pattacīvaraṃ samīpe katvā sabbaṃ divasabhāgaṃ paṭhamayāma majjhimayāmañca dhammaṃ vācetvā pacchimayāme ekasmiṃ there uddesaṃ gahetvā nikkhante pattacīvaraṃ gahetvā teneva saddhiṃ nikkhanto.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',182,replace(replace('<p class="bodytext">Tato <a name="M2.0182"></a> ‘‘na mañce mayhaṃ catūhi iriyāpathehi maggaphalaṃ uppajjissati arahattaṃ apatvā neva mañce piṭṭhiṃ pasāressāmi, na pāde dhovissāmīti mañcaṃ ussāpetvā ṭhapesi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',183,replace(replace('<p class="bodytext">Idāni <a name="M2.0183"></a> me tiṃsavassāni samaṇadhammaṃ karontassa, nāsakkhiṃ arahattaṃ pāpuṇituṃ, addhā me imasmiṃ attabhāve maggovā phalaṃvā natthi, na me laddhaṃ sabrahmacārīhi saddhiṃ visuddhipavāraṇaṃ pavāretunti cintesi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',184,replace(replace('<p class="bodytext">Bho <a name="M2.0184"></a> mahāsīvatthera devatāpi tayā saddhiṃ keḷiṃ karonti, anucchavikaṃ nu kho te etanti vipassanaṃ vaḍḍhetvā saha paṭisambhidāhi arahattaṃ aggahesi. So idāni nipajjissāmīti senāsanaṃ paṭijaggitvā mañcakaṃ paññapetvā udakaṭṭhāne udakaṃ paccupaṭṭhapetvā pāde dhovissāmīti sopānaphalake nisīdi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',186,replace(replace('<p class="bodytext">Apetha <a name="M2.0186"></a> bhante mātugāmoti okāsaṃ kāretvā theraṃ upasaṅkamitvā vanditvā purato ukkuṭiko nisīditvā pāde dhovissāmīti āha.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',194,replace(replace('<p class="gatha1">Te <a name="M2.0194"></a> paṇītatarā devā,</p>\r\n<p class="gatha2">Akaniṭṭhā yasassino;</p>\r\n<p class="gatha3">Antime vattamānamhi,</p>\r\n<p class="gathalast">So nivāso bhavissati;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',197,replace(replace('<p class="bodytext">Dhammadāyādā <a name="M2.0197"></a> me bhikkhave bhavatha, mā āmisadāyādā, atthi me tumhesu anukampā, kinti me sāvakā dhammadāyādā bhaveyyuṃ, no āmisadāyādāti. Tumhe ca bhikkhave āmisadāyādā bhaveyyātha, no dhammadāyādā. Tumhepi tena ādiyā bhaveyyātha, āmisa dāyādā satthusāvakā viharanti, no dhammadāyādāti. Ahampitena ādiyo bhaveyyaṃ āmisadāyādā satthu sāvakā viharanti, no dhammadāyādāti. Tumhe ca me bhikkhave dhammadāyādā bhaveyyātha no āmisadāyādā. Tumhepi tena ādiyā bhaveyyātha dhammadāyādā satthu sāvakā viharanti, no āmisadāyādāti. Ahampi tena na ādiyo bhaveyyaṃ. Dhammadāyādā satthu sāvakā viharanti, no āmisadāyādāti. Tasmātiha me bhikkhave dhammadāyādā bhavatha, mā āmisadāyādā, atthime tumhesu anukampā, kinti me sāvakā dhammadāyādā bhaveyyuṃ, no āmisadāyādāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',201,replace(replace('<p class="bodytext">Kahaṃ <a name="M2.0201"></a> buddho kahaṃ bhagavā kahaṃ devadevo narāsabho purisasīhoti bhagavantaṃ pariyesanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',202,replace(replace('<p class="bodytext">Vuttampicetaṃ <a name="M2.0202"></a> ‘‘tena kho pana samayena bhagavā sakkato hoti garukato mānito pūjito apacito lābhī cīvara piṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṃ, bhikkhusaṅghopi kho sakkato hoti…pe… parikkhārāna’’nti.</p>\r\n<p class="bodytext">Yāvatā kho cunda etarahi saṅgho vā gaṇo vā loke uppanno, nāhaṃ cunda aññaṃ ekasaṅghampi samanupassāmi, evaṃ lābhagga yasaggapattaṃ, yathariva cunda bhikkhusaṅghoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',203,replace(replace('<p class="bodytext">Amhākaṃ <a name="M2.0203"></a> ācariyassa detha, upajjhāyassa dethāti uccāsadda mahāsaddaṃ karonti. Sā ca nesaṃ pavatti bhagavatopi pākaṭā ahosi. Tato bhagavā ananucchavikanti dhammasaṃvegaṃ uppādetvā cintesi.</p>\r\n<p class="bodytext">Paccayā akappiyāti na sakkā sikkhāpadaṃ paññapetuṃ. Paccayapaṭibaddhā hi kulaputtānaṃ samaṇadhammavutti. Handāhaṃ dhammadāyādapaṭipadaṃ desemi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',204,replace(replace('<p class="bodytext">Sā <a name="M2.0204"></a> sikkhākāmānaṃ kulaputtānaṃ sikkhāpadapaññatti viya bhavissati nagaradvāre ṭhapitasabbakāyikaādāso viya ca, yathā hi nagara dvāre ṭhapite sabbakāyike ādāse cattāro vaṇṇā attano chāyaṃ disvā vajjaṃ pahāya niddosā honti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',207,replace(replace('<p class="bodytext">So <a name="M2.0207"></a> hi brāhmaṇa bhagavā anuppannassa maggassa uppādetā, asañjātassa maggassa sañjanetā, anakkhātassa maggassa akkhātā, maggaññū maggavidū maggakovidotica. So hāvuso bhagavā jānaṃ jānāti, passaṃ passati cakkhubhūto ñāṇabhūto dhammabhūto brahmabhūto vattā pavattā atthassa ninnetā amatassa dātā dhammassāmī tathāgatotica, ….</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',222,replace(replace('<p class="bodytext">Paṇītena <a name="M2.0222"></a> khādanīyena bhojanīyena sahatthā santappesi sampavāresi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',234,replace(replace('<p class="bodytext">Are <a name="M2.0234"></a> tvaṃ sugatātirittampi piṇḍapātaṃ paṭikkhipitvā īdisaṃ icchaṃ uppādesi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',236,replace(replace('<p class="bodytext">Dubbharo <a name="M2.0236"></a> bhikkhu na sakkā posituṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',237,replace(replace('<p class="bodytext">Amhākaṃ <a name="M2.0237"></a> bhadanto subharo thokenapi tussati, mayameva naṃ posissāmāti paṭiññaṃ katvā posenti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',238,replace(replace('<p class="bodytext">‘‘Are <a name="M2.0238"></a> tvaṃ nāma tadā sugatātirittampi piṇḍapātaṃ paṭikkhipitvā tathā jighacchādubbalyaparetopi samaṇadhammaṃ katvā ajja kosajjamanuyuñjasi’’ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',239,replace(replace('<p class="bodytext">Yā <a name="M2.0239"></a> ca kho ānandakathā abhisallekhitā cetovinīvaraṇasappāyā ekantanibbidāya virāgāya nirodhāya upasamāya abhiññāya sambodhāya nibbānāya saṃvattati. Seyyathidaṃ appicchakathāti …,.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',243,replace(replace('<p class="bodytext">Idamavoca <a name="M2.0243"></a> bhagavā, idaṃ vatvāna sugato uṭṭhāyāsanā vihāraṃ pāvisi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',245,replace(replace('<p class="bodytext">Imaṃ <a name="M2.0245"></a> mayā saṃkhittena uddesaṃ uddiṭṭhaṃ vitthārena avibhattaṃ, dhammapaṭiggāhakā bhikkhū uggahetvā ānandaṃ vā kaccānaṃ vā upasaṅkamitvā pucchissanti, te mayhaṃ ñāṇena saṃsandetvā kathessanti, tato dhammapaṭiggāhakā puna maṃ pucchissanti, tesaṃ ahaṃ sukathitaṃ bhikkhave ānandena, sukathitaṃ kaccānena, maṃ cepi tumhe ekamatthaṃ puccheyyātha, ahampi naṃ evameva byākareyyanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',249,replace(replace('<p class="subhead">Cūḷagosiṅgasutta</p>\r\n<p class="bodytext">Evaṃ <a name="M2.0249"></a> me sutaṃ ekaṃ samayaṃ bhagavā nātike viharati giñjakāvasathe, tena kho pana samayena āyasmā ca anuruddho āyasmā ca nandiyo āyasmā ca kimilo gosiṅgasālavanadāye viharanti. Athakho bhagavā sāyanhasamayaṃ paṭisallānā vuṭṭhito yena gosiṅgasālavanadāyo, tenupasaṅkami. Addasā kho dāyapālo bhagavantaṃ dūratova āgacchantaṃ. Disvāna bhagavantaṃ etadavoca ‘‘mā mahāsamaṇa etaṃ dāyaṃ pāvisi, santettha tayo kulaputtā attakāmarūpā viharanti, mā tesaṃ aphāsukamakāsī’’ti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',253,replace(replace('<p class="bodytext">Ahaṃ <a name="M2.0253"></a> ime kulaputte paggaṇhitvā ukkaṃsitvā paṭisanthāraṃ katvā dhammaṃ nesaṃ desessāmīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',258,replace(replace('<p class="bodytext">Assosi <a name="M2.0258"></a> kho āyasmā anuruddho dāyapālassa bhagavatā saddhiṃ mantayamānassa. Sutvāna dāyapālaṃ etadavoca, mā āvuso dāyapāla bhagavantaṃ vāresi, satthā no bhagavā anuppattoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',259,replace(replace('<p class="bodytext">Mayaṃ <a name="M2.0259"></a> tayo janā idha viharāma, aññe pabbajitā nāma natthi, ayañca dāyapālo pabbajitena viya saddhiṃ katheti, ko nu kho bhavissatīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',260,replace(replace('<p class="bodytext">Ayaṃ <a name="M2.0260"></a> dāyapālo phaṇakataṃ āsivisaṃ gīvāya gahetuṃ hatthaṃ pasārentoviya loke aggapuggalena saddhiṃ kathentova na jānāti, aññatarabhikkhunā viya saddhiṃ kathetīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',261,replace(replace('<p class="bodytext">Mayaṃ <a name="M2.0261"></a> tayo janā samaggavāyaṃ vasāma. Sacāhaṃ ekakova paccuggamanaṃ karissāmi. Samaggavāso nāma na bhavissatīti piyamitte gahetvāva paccuggamanaṃ karissāmi. Yathā ca bhagavā mayhaṃ piyo, evaṃ sahāyānampi me piyoti, tehi saddhiṃ paccuggamanaṃ kātukāmo sayaṃ akatvāva upasaṅkami.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',271,replace(replace('<p class="bodytext">Tassa <a name="M2.0271"></a> mayhaṃ bhante evaṃ hoti, yaṃ nūnāhaṃ sakaṃ cittaṃ nikkhipitvā imesaṃyeva āyasmantānaṃ cittassa vasena vatteyyanti. So kho ahaṃ bhante sakaṃ cittaṃ nikkhipitvā imesaṃyeva āyasmantānaṃ cittassa vasena vattāmi, nānāhi kho no bhante kāyā ekañca pana maññe cittanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',274,replace(replace('<p class="bodytext">Sādhu <a name="M2.0274"></a> sādhu anuruddhā, kacci pana vo anuruddhā appamattā ātāpino pahitattā viharathāti.</p>\r\n<p class="bodytext">Taggha mayaṃ bhante appamattā ātāpino pahitattā viharāmāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',275,replace(replace('<p class="bodytext">Yathā <a name="M2.0275"></a> kathaṃ pana tumhe anuruddhā appamattā ātāpino pahitattā viharathāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',284,replace(replace('<p class="bodytext">Atthi <a name="M2.0284"></a> pana vo anuruddhā evaṃ appamattānaṃ ātāpīnaṃ pahitattānaṃ viharantānaṃ uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāroti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',285,replace(replace('<p class="bodytext">Kiṃ <a name="M2.0285"></a> hi no siyā bhante. Idha mayaṃ bhante yāvadeva ākaṅkhāma, vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharāma, ayaṃ kho no bhante amhākaṃ appamattānaṃ ātāpīnaṃ pahitattānaṃ viharantānaṃ uttarimanussadhammā alamariyañāṇadassanaviseso adhigato phāsuvihāroti.</p>\r\n<p class="subhead">Sagāthāvaggasaṃyutta aṭṭhakathā</p>\r\n<p class="subhead">Yakkhasaṃyutta</p>\r\n<p class="subhead">Āḷavakasutta</p>\r\n<p class="bodytext">Evaṃ <a name="M3.0001"></a> me sutaṃ ekaṃ samayaṃ bhagavā āḷaviyaṃ viharati āḷavakassa yakkhassa bhavane.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',3,replace(replace('<p class="bodytext">Nāsakkhi <a name="M3.0003"></a> migaṃ gahetunti apavādamocanatthaṃ kājenādāya āgacchanto nagarassāvidūre bahalapattapalāsaṃ mahānigrodhaṃ disvā parissamavinodanatthaṃ tassa mūlamupagato.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',5,replace(replace('<p class="bodytext">Ye <a name="M3.0005"></a> ye vajjā honti, te te sandhāya ‘‘yo jīvitatthiko, so nikkhamatū’’ti bhaṇati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',6,replace(replace('<p class="bodytext">Rājā <a name="M3.0006"></a> core gahetvā yakkhassa detīti manussā corakamma to paṭiviratā. Tato aparena samayena navacorānaṃ abhāvena purāṇacorānañca parikkhayena bandhanāgārāni suññāni ahesuṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',7,replace(replace('<p class="bodytext">Rājā <a name="M3.0007"></a> amhākaṃ pitaraṃ amhākaṃ pitāmahaṃ pesetīti manussā khobhaṃ karissanti, mā vo etaṃ ruccīti vāresi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',8,replace(replace('<p class="bodytext">Natthi <a name="M3.0008"></a> deva nagare dārako ṭhapetvā antepure tava puttaṃ āḷavakakumāranti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',15,replace(replace('<p class="bodytext">Atha <a name="M3.0015"></a> kho āḷavako yakkho yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ etadavoca nikkhama samaṇāti. Sādhāvusoti bhagavā nikkhami. Pavisa samaṇāti. Sādhāvusoti bhagavā pāvisi. Dutiyampi kho oḷavako yakkho bhagavantaṃ etadavoca nikkhama samaṇāti. Sādhāvusoti bhagavā nikkhami. Pavisa samaṇāti. Sādhāvusoti bhagavā pāvisi. Tatiyampi kho āḷavako yakkho bhagavantaṃ etadavoca nikkhama samaṇāti. Sādhāvusoti bhagavā nikkhami. Pavisa samaṇāti. Sādhāvusoti bhagavā pāvisi. Catutthampi kho āḷavako yakkho bhagavantaṃ etadavoca nikkhama samaṇāti. Na khvāhaṃ taṃ āvuso nikkhamissāmi. Yaṃ te karaṇīyaṃ, taṃ karohīti. Pañhaṃ taṃ samaṇa pucchissāmi, sace me na byākarissasi, cittaṃ vā te khipissāmi, hadayaṃ vā te phālessāmi, pādesu vā gahetvā pāragaṅgāya khipissāmīti. Na khvāhaṃ taṃ āvuso passāmi sadevake loke samārake sabrahmake sassamaṇabrāhmaṇiyā pajāya sadevamanussāya, yo me cittaṃ vā khipeyya hadayaṃ vā phāleyya pādesu vā gahetvā pāragaṅgāya khipeyya. Apica tvaṃ āvuso puccha yadā kaṅkhasīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',19,replace(replace('<p class="bodytext">Ko <a name="M3.0019"></a> so bhagavānāma, yo mama bhavanaṃ paviṭṭhoti āha.</p>\r\n<p class="bodytext">Na tvaṃ āvuso jānāsi bhagavantaṃ amhākaṃ satthāraṃ, yo tusitabhavane ṭhito pañcamahāvilokitaṃ viloketvātiādinā nayena yāva dammacakkapavattanā kathentā paṭisandhiādīsu dvattiṃsapubbanimittāni vatvā imānipi tvaṃ āvuso acchariyāni nāddasāti codesuṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',21,replace(replace('<p class="bodytext">Passatha <a name="M3.0021"></a> dāni tumhākaṃ vā satthā mahānubhāvo, ahaṃ vāti dakkhiṇapādena saṭṭhiyojanamattaṃ kelāsapabbatakūṭaṃ akkami.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',27,replace(replace('<p class="bodytext">Yaṃ <a name="M3.0027"></a> nūnāhaṃ kenaci ajeyyaṃ dussāvudhaṃ muñceyyanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',30,replace(replace('<p class="bodytext">Idaṃ <a name="M3.0030"></a> kāraṇaṃ mettāvihārayutto samaṇo, handa naṃ rosetvā mettāya viyojemīti iminā sambandhenetaṃ vuttaṃ, atha kho āḷavako yakkho yena bhagavā…pe… nikkhama samaṇāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',31,replace(replace('<p class="bodytext">‘‘Subbaco <a name="M3.0031"></a> vatāyaṃ samaṇo ekavacaneneva nikkhanto, evaṃ nāma nikkhametuṃ sukhaṃ samaṇaṃ akāraṇenevāhaṃ sakalarattiṃ yuddhena abbhuyyāsinti muducitto hutvā puna cintesi –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',33,replace(replace('<p class="bodytext">Subbaco <a name="M3.0033"></a> ayaṃ samaṇo nikkhamāti vutto nikkhamati. Pavisāti vutto pavisati. Yaṃnūnāhaṃ imaṃ samaṇaṃ evameva sakalarattiṃ kilametvā pāde gahetvā pāragaṅgāya khipeyyanti pāpakaṃ cittaṃ uppādetvā catutthavāraṃ āha nikkhama samaṇāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',37,replace(replace('<p class="gatha1">Kiṃ <a name="M3.0037"></a> sūdha vittaṃ purisassa seṭṭhaṃ,</p>\r\n<p class="gatha2">Kiṃsu suciṇṇaṃ sukhamāvahāti;</p>\r\n<p class="gatha3">Kiṃsu have sādutaraṃ rasānaṃ,</p>\r\n<p class="gathalast">Kathaṃ jīviṃ jīvitamāhu seṭṭhanti;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',39,replace(replace('<p class="gatha1">Saddhīdha <a name="M3.0039"></a> vittaṃ purisassa seṭṭhaṃ,</p>\r\n<p class="gatha2">Dhammo suciṇṇo sukhamāvahāti;</p>\r\n<p class="gatha3">Saccaṃ have sādutaraṃ rasānaṃ,</p>\r\n<p class="gathalast">Paññājīviṃ jīvitamāhu seṭṭhanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',41,replace(replace('<p class="gatha1">Saddho <a name="M3.0041"></a> sīlena sampanno,</p>\r\n<p class="gatha2">Yaso bhogasamappito;</p>\r\n<p class="gatha3">Yaṃ yaṃ padesaṃ bhajati,</p>\r\n<p class="gathalast">Tattha tattheva pūjitoti vacanato –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',43,replace(replace('<p class="gatha1">Saccena <a name="M3.0043"></a> vācenudakampi dhāvati,</p>\r\n<p class="gatha2">Visampi saccena hananti paṇḍitā;</p>\r\n<p class="gatha3">Saccena devo thanayaṃ pavassati,</p>\r\n<p class="gathalast">Sacce ṭhitā nibbutiṃ patthayanti.</p>\r\n<p class="gatha1">Yekecime atthi rasā pathabyā,</p>\r\n<p class="gatha2">Saccaṃ tesaṃ sādutaraṃ rasānaṃ;</p>\r\n<p class="gatha3">Sacce ṭhitā samaṇabrāhmaṇā ca,</p>\r\n<p class="gathalast">Taranti jātimaraṇassa pāranti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',46,replace(replace('<p class="gatha1">Kathaṃsu <a name="M3.0046"></a> tarati oghaṃ, kathaṃsu tarati aṇṇavaṃ;</p>\r\n<p class="gathalast">Kathaṃsu dukkhamacceti, kathaṃsu parisujjhati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',47,replace(replace('<p class="gatha1">Saddhāya <a name="M3.0047"></a> tarati oghaṃ, appamādena aṇṇavaṃ;</p>\r\n<p class="gathalast">Vīriyena dukkhamacceti, paññāya parisujjhati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',50,replace(replace('<p class="gatha1">Kathaṃsu <a name="M3.0050"></a> labhate paññaṃ, kathaṃsu vindate dhanaṃ;</p>\r\n<p class="gatha2">Kathaṃsu kittiṃ pappoti, kathaṃ mittāni ganthati;</p>\r\n<p class="gathalast">Asmā lokā paraṃ lokaṃ, kathaṃ pecca na socatīti–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',51,replace(replace('<p class="gatha1">Saddahāno <a name="M3.0051"></a> arahataṃ, dhammaṃ nibbānapattiyā;</p>\r\n<p class="gathalast">Sussūsaṃ labhate paññaṃ, appamatto vicakkhaṇo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',54,replace(replace('<p class="gatha1">Patirūpakārī <a name="M3.0054"></a> dhuravā, uṭṭhātā vindate dhanaṃ;</p>\r\n<p class="gathalast">Saccena kittiṃ pappoti, dadaṃ mittāni ganthati.</p>\r\n<p class="bodytext">Asmā loko paraṃ lokaṃ, evaṃ pecca na socati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',56,replace(replace('<p class="gatha1">Yassete <a name="M3.0056"></a> caturo dhammā,</p>\r\n<p class="gatha2">Saddhassa gharamesino;</p>\r\n<p class="gatha3">Saccaṃ dammo dhiti cāgo,</p>\r\n<p class="gathalast">Sa ve pecca na socati;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',58,replace(replace('<p class="gatha1">Iṅgha <a name="M3.0058"></a> aññepi pucchassu,</p>\r\n<p class="gatha2">Puthū samaṇabrāhmaṇe;</p>\r\n<p class="gatha3">Yadi saccā dhammā cāgā,</p>\r\n<p class="gathalast">Khantyā bhiyyodha vijjhati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',60,replace(replace('<p class="gatha1">Kathaṃ <a name="M3.0060"></a> nu dāni puccheyyaṃ, puthū samaṇabrāhmaṇe;</p>\r\n<p class="gathalast">Yohaṃ ajja pajānāmi, yo attho samparāyiko.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',62,replace(replace('<p class="gatha1">Atthāya <a name="M3.0062"></a> vata me buddho, vāsāyāḷavimāgamā;</p>\r\n<p class="gathalast">Yohaṃ ajja pajānāmi, yattha dinnaṃ mahapphalaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',63,replace(replace('<p class="gatha1">So <a name="M3.0063"></a> ahaṃ vicarissāmi, gāmā gāmaṃ purā puraṃ;</p>\r\n<p class="gathalast">Namassamāno sambuddhaṃ, dhammassa ca sudhammataṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',65,replace(replace('<p class="gatha1">Imaṃ <a name="M3.0065"></a> kumāraṃ satapuññalakkhaṇaṃ,</p>\r\n<p class="gatha2">Sabbaṅgupetaṃ paripuṇṇabyañjanaṃ;</p>\r\n<p class="gatha3">Udaggacitto sumano dadāmi te,</p>\r\n<p class="gathalast">Paṭiggaha lokahitāya cakkhumāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',66,replace(replace('<p class="gatha1">Dīghāyuko <a name="M3.0066"></a> hotu ayaṃ kumāro,</p>\r\n<p class="gatha2">Tuvañca yakkha sukhito bhavāhi;</p>\r\n<p class="gatha3">Abyādhitā lokahitāya tiṭṭhatha;</p>\r\n<p class="gathalast">Ayaṃ kumāro saraṇamupeti buddhaṃ…pe… dhammaṃ…pe… saṅghaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',71,replace(replace('<p class="subhead">Mahāvaggasaṃyutta aṭṭhakathā</p>\r\n<p class="bodytext">Ekaṃ <a name="M3.0071"></a> samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. Tena kho pana samayena āyasmā sāriputto magadhesu viharati nālakagāmake ābādhiko dukkhito bāḷhagilāno. Cundo ca samaṇuddeso āyasmato sāriputtassa upaṭṭhāko hoti.</p>\r\n<p class="bodytext">Atha kho āyasmā sāriputto teneva ābādhena parinibbāyi. Atha kho cundo samaṇuddeso āyasmato sāriputtassa pattacīvaramādāya yena sāvatthi jetavanaṃ anāthapiṇḍikassa ārāmo, yenā yasmā ānando tenupasaṅkami, upasaṅkamitvā āyasmantaṃ ānandaṃ abhivādetvā ekamantaṃ nisīdi, ekamantaṃ nisinno kho cundo samaṇuddeso āyasmantaṃ ānandaṃ etadavoca ‘‘āyasmā bhante sāriputto parinibbuto, idamassa pattacīvara’’nti. Atthi kho idaṃ āvuso cunda kathā pābhataṃ bhagavantaṃ dassanāya, āyāmāvuso cunda, yena bhagavā tenupasaṅkamissāma, upasaṅkamitvā bhagavato etamatthaṃ ārocessāmāti. ‘‘Evaṃ bhante’’ti kho cundo samaṇuddeso āyasmato ānandassa paccassosi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',75,replace(replace('<p class="bodytext">Buddhā <a name="M3.0075"></a> nu kho paṭhamaṃ parinibbāyanti, udāhu aggasāvakāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',78,replace(replace('<p class="bodytext">Anujānātu <a name="M3.0078"></a> me bhante bhagavā, anujānātu sugato. Parinibbāna kālo me, ossaṭṭho me āyusaṅkhāroti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',79,replace(replace('<p class="bodytext">Kattha <a name="M3.0079"></a> parinibbāyissasi sāriputtāti.</p>\r\n<p class="bodytext">Atthi bhante magadhesu nālakagāme jātovarako, tatthāhaṃ parinibbāyissāmīti.</p>\r\n<p class="bodytext">Yassa dāni tvaṃ sāriputta kālaṃ maññasi, idāni pana te jeṭṭhakaniṭṭha bhātikānaṃ tādisassa bhikkhuno dassanaṃ dullabhaṃ bhavissati, desehi nesaṃ dhammanti āha.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',103,replace(replace('<p class="bodytext">Athakho <a name="M3.0103"></a> āyasmā ca ānando cundo ca samaṇuddeso yena bhagavā tenupasaṅkamiṃsu, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu, ekamantaṃ nisinno kho āyasmā ānando bhagavantaṃ etadavoca ‘‘ayaṃ bhante cundo samaṇuddeso ‘evamāha āyasmā bhante sāriputto <a name="M3.0104"></a> parinibbuto, idamassa pattacīvara’nti. Apica me bhante ‘madhuraka jāto viya kāyo, disāpi me na pakkhāyanti, dhammāpi maṃ nappaṭibhanti āyasmā sāriputto parinibbuto’ti’’ sutvā–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',104,replace(replace('<p class="bodytext">sāriputto <a name="M3.0104"></a> parinibbuto, idamassa pattacīvara’nti. Apica me bhante ‘madhuraka jāto viya kāyo, disāpi me na pakkhāyanti, dhammāpi maṃ nappaṭibhanti āyasmā sāriputto parinibbuto’ti’’ sutvā–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',105,replace(replace('<p class="gatha1">Appakenapi <a name="M3.0105"></a> medhāvī, pābhatena vicakkhaṇo;</p>\r\n<p class="gathalast">Samuṭṭhāpeti attānaṃ, aṇuṃ aggiṃva sandhamanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',110,replace(replace('<p class="gatha1">Yo <a name="M3.0110"></a> pabbajī jātisatāni pañca,</p>\r\n<p class="gatha2">Pahāya kāmāni manoramāni;</p>\r\n<p class="gatha3">Taṃvītarāgaṃ susamāhitindriyaṃ;</p>\r\n<p class="gathalast">Parinibbutaṃ vandatha sāriputtaṃ.</p>\r\n<p class="gatha1">Khantibalo pathavisamo na kuppati,</p>\r\n<p class="gatha2">Nacāpi cittassa vasena vattati;</p>\r\n<p class="gatha3">Anukampako kāruṇiko ca nibbuto,</p>\r\n<p class="gathalast">Parinibbutaṃ vandatha sāriputtaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',111,replace(replace('<p class="gatha1">Caṇḍālaputto <a name="M3.0111"></a> yathā nagaraṃ paviṭṭho,</p>\r\n<p class="gatha2">Nīcamano carati kaḷopihattho;</p>\r\n<p class="gatha3">Tathā ayaṃ viharati sāriputto,</p>\r\n<p class="gathalast">Parinibbutaṃ vandatha sāriputtaṃ.</p>\r\n<p class="gatha1">Usabho yathā chinnavisāṇako,</p>\r\n<p class="gatha2">Aheṭṭhayanto carati purantare vane;</p>\r\n<p class="gatha3">Tathā ayaṃ viharati sāriputto;</p>\r\n<p class="gathalast">Parinibbutaṃ vandatha sāriputtanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',112,replace(replace('<p class="bodytext">Apica <a name="M3.0112"></a> me bhante madhurakajāto viya kāyo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',113,replace(replace('<p class="bodytext">Kiṃ <a name="M3.0113"></a> nukho te ānanda sāriputto sīlakkhandhaṃ vā ādāya parinibbuto, samādhikkhandhaṃ vā ādāya parinibbuto, paññākkhandhaṃ vā ādāya parinibbuto, vimuttikkhandhaṃ vā ādāya parinibbuto, vimutti ñāṇadassanakkhandhaṃ vā ādāya parinibbutoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',116,replace(replace('<p class="bodytext">Nanu <a name="M3.0116"></a> taṃ ānanda mayā paṭikacceva akkhātaṃ sabbehi piyehi manā pehi nānābhāvo vinābhāvo aññathābhāvo, taṃ kutettha ānanda labbhā ‘‘yaṃ taṃ jātaṃ bhūtaṃ saṅkhataṃ palokadhammaṃ, taṃ vata māpalujjī’’ti netaṃ ṭhānaṃ vijjati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',124,replace(replace('<p class="subhead">Dasakaniddesa</p>\r\n<p class="bodytext">Tattha <a name="M3.0124"></a> katamaṃ tathāgatassa ṭhānañca ṭhānato aṭṭhānañca aṭṭhānato yathābhūtaṃ ñāṇaṃ. Idha tathāgato ‘‘aṭṭhānametaṃ anavakāso yaṃ diṭṭhisampanno puggalo kañci saṅkhāraṃ niccato upagaccheyya netaṃ ṭhānaṃ vijjatī’’ti pajānāti, ‘‘ṭhānañca kho etaṃ vijjati yaṃ puthujjano kañci saṅkhāraṃ niccato upagaccheyya ṭhānametaṃ vijjatī’’ti –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',135,replace(replace('<p class="gatha1">Kammato <a name="M3.0135"></a> dvārato ceva;</p>\r\n<p class="gatha2">Kappaṭṭhitiyato tathā;</p>\r\n<p class="gatha3">Pākasādhāraṇādīhi;</p>\r\n<p class="gathalast">Viññātabbo vinicchayo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',154,replace(replace('<p class="bodytext">Mayā <a name="M3.0154"></a> bhante uppanno coro na sakkā paṭibāhituṃ, saṅgho parasamuddaṃ gacchatu, ahaṃ samuddārakkhaṃ karissāmīti.</p>\r\n<p class="bodytext">Āvuso mahāsoṇa abhiruha mahāulumpanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',155,replace(replace('<p class="bodytext">Nāhaṃ <a name="M3.0155"></a> bhante tumhesu agacchantesu gamissāmīti yāvatatiyaṃ kathetvāpi theraṃ āropetuṃ asakkontā nivattiṃsu.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',156,replace(replace('<p class="bodytext">Evarūpassa <a name="M3.0156"></a> nāma lābhaggayasaggappattassa sarīradhātunicayaṭṭhānaṃ anāthaṃ jātanti cintayamāno nisīdi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',158,replace(replace('<p class="bodytext">Bhante <a name="M3.0158"></a> etaṃ paṇṇakhādakamanussānaṃ vasanaṭṭhānaṃ, dhūmo paññāyati, ahaṃ purato gamissāmīti theraṃ vanditvā attano bhavanaṃ agamāsi. Thero sabbampi bhayakālaṃ paṇṇakhādakamanusse nissāya vasi.</p>\r\n<p class="bodytext">Āvuso mahāsoṇa bhikkhāhāro paññāyatīti vatvā pattacīvaraṃ āropetvā cīvaraṃ pārupitvā pattaṃ nīharitvā aṭṭhāsi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',161,replace(replace('<p class="bodytext">Mahāsoṇatthero <a name="M3.0161"></a> pañcabhikkhusataparivāro maṇḍalārāmavihāraṃ patto, ekeko navahatthasāṭakena saddhiṃ ekekakahāpaṇagghanakaṃ piṇḍapātaṃ detūti manusse avocuṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',165,replace(replace('<p class="bodytext">Anāyatane <a name="M3.0165"></a> naṭṭhānaṃ attabhāvānaṃ pamāṇaṃ natthi, buddhānaṃ upaṭṭhānaṃ karissāmīti cetiyaṅgaṇaṃ gantvā appaharitaṃ karoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',168,replace(replace('<p class="bodytext">Mayaṃ <a name="M3.0168"></a> tāva mahallakā, idaṃ nāma bhavissatīti na sakkā jānituṃ, tuvaṃ attānaṃ rakkheyyāsīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',169,replace(replace('<p class="bodytext">Kahaṃ <a name="M3.0169"></a> vattabbakanigrodhatthero, kahaṃ vattabbakanigrodhattheroti pucchitvā tassa santikaṃ agamaṃsu.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',171,replace(replace('<p class="bodytext">Kiṃ <a name="M3.0171"></a> thero piyāyatīti.</p>\r\n<p class="bodytext">Kīdiso theroti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',172,replace(replace('<p class="bodytext">Dhammasakaṭassa <a name="M3.0172"></a> akkho bhijjati akkho bhijjatīti paridevamāno vicari.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',179,replace(replace('<p class="subhead">8. Sammappadhānavibhaṅga</p>\r\n<p class="subhead">1. Suttantabhājanīyavaṇṇanā</p>\r\n<p class="bodytext">Cattārome <a name="M3.0179"></a> āvuso sammappadhānā. Katame cattāro. Idhāvuso bhikkhu anuppannā me pāpakā akusalā dhammā uppajjamānā anatthāya saṃvatteyyunti ātappaṃ karoti, uppannā me pāpakā akusalā dhammā appahīyamānā anatthāya saṃvatteyyunti ātappaṃ karoti, anuppannā me kusalā dhammā anuppajjamānā anatthāya saṃvatteyyunti ātappaṃ karoti, anuppannā me kusalā dhammā anuppajjamānā anatthāya saṃvatteyyunti ātappaṃ karoti, uppannā me kusalā dhammā nirujjhamānā anatthāya saṃvatteyyunti ātappaṃ karotīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',181,replace(replace('<p class="bodytext">Upajjhāyo <a name="M3.0181"></a> me ativiya pasannacitto vandati, kiṃ nu kho pupphāni labhitvā pūjaṃ kareyyāsīti cintesi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',182,replace(replace('<p class="bodytext">‘‘Kadā <a name="M3.0182"></a> āgatosīti pucchi’’</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',188,replace(replace('<p class="bodytext">Tvaṃ <a name="M3.0188"></a> amhe uccākulāti sallakkhesi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',191,replace(replace('<p class="subhead">16. Ñāṇavibhaṅga</p>\r\n<p class="subhead">10. Dasakaniddesavaṇṇanā</p>\r\n<p class="subhead">Ekissā lokadhātuyā</p>\r\n<p class="bodytext">Aṭṭhānametaṃ <a name="M3.0191"></a> anavakāso yaṃ ekissā lokadhātuyā dve arahanto sammāsambuddhā apubbaṃ acarimaṃ uppajjeyyuṃ, netaṃ ṭhānaṃ vijjatīti pajānāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',197,replace(replace('<p class="bodytext">Ajja <a name="M3.0197"></a> satthā parinibbāyati, ajja sāsanaṃ osakkati, pacchimadassanaṃ dāni idaṃ amhākanti dasabalassa parinibbutadivasato mahantataraṃ kāruññaṃ karissanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',199,replace(replace('<p class="bodytext">Ekapuggalo <a name="M3.0199"></a> bhikkhave loke uppajjamāno uppajjati acchariyamanusso, katamo ekapuggalo tathāgato bhikkhave arahaṃ sammāsambuddhoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',201,replace(replace('<p class="subhead">Jhānavibhaṅga</p>\r\n<p class="subhead">Suttantabhājanīya</p>\r\n<p class="bodytext">Abhikkante <a name="M3.0201"></a> paṭikkante sampajānakārī hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',202,replace(replace('<p class="gatha1">Sato <a name="M3.0202"></a> sampajāno abhikkamati,</p>\r\n<p class="gathalast">Sato sampajāno paṭikkamatīti vuttaṃ.</p>\r\n<p class="bodytext">Kinnū me ettha gatena attho atthi natthīti atthānatthaṃ pariggahetvā atthapariggaṇhanaṃ sātthakasampajaññaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',203,replace(replace('<p class="bodytext">Āmisatopi <a name="M3.0203"></a> vaḍḍhi atthoyeva, taṃ nissāya brahmacariyā nuggahāya paṭipannattāti vadanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',205,replace(replace('<p class="bodytext">Mayā <a name="M3.0205"></a> pabbajitadivasato paṭṭhāya bhikkhunā saddhiṃ dvekathā nāma nakathitapubbā, aññasmiṃ divase uparivisesaṃ nibbattessāmīti cintetvā ‘‘kiṃ bhante’’ti paṭivacanaṃ adāsi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',209,replace(replace('<p class="bodytext">Bhante <a name="M3.0209"></a> ete manussā tumhākaṃ kiṃhonti, mātipakkhato sambandhā pitipakkhatoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',210,replace(replace('<p class="bodytext">Āvusoyaṃ <a name="M3.0210"></a> mātāpitūhipi dukkaraṃ, taṃ ete manussā amhākaṃ karonti, pattacīvarampi no etesaṃ santakameva, etesaṃ ānubhāvena neva bhaye bhayaṃ na chātake chātakaṃ jānāma, edisānāma amhākaṃ upakārino natthīti tesaṃ guṇe kathento gacchati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',212,replace(replace('<p class="bodytext">Kammaṭṭhānaṃ <a name="M3.0212"></a> nāma atthītipi saññaṃ akatvā gāmaṃ piṇḍāya pavisitvā ananulomikena gihisaṃsaggena saṃsaṭṭho caritvā ca bhuñjitvā ca tuccho nikkhamati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',213,replace(replace('<p class="bodytext">Āvuso <a name="M3.0213"></a> tumhe na iṇaṭṭā na bhayaṭṭā na ājīvikā pakatā pabbajitā, dukkhā muccitukāmā panettha pabbajitā. Tasmā gamane uppannakilesaṃ gamaneyeva nigaṇhatha. Ṭhāne, nisajjāya, sayane uppannakilesaṃ sayaneyeva niggaṇhathāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',214,replace(replace('<p class="bodytext">Ayaṃ <a name="M3.0214"></a> bhikkhu tuyhaṃ uppannaṃ vitakkaṃ jānāti, ananucchavikaṃ te etanti attānaṃ paṭicodetvā vipassanaṃ vaḍḍhetvā ariyabhūmiṃ okkamati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',215,replace(replace('<p class="bodytext">Ayaṃ <a name="M3.0215"></a> thero punappunaṃ nivattitvā gacchati, kiṃ nukho maggamūḷho udāhu kiñci pamuṭṭhoti samullapanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',218,replace(replace('<p class="bodytext">Kinnū <a name="M3.0218"></a> kho ete amheheva saddhiṃ na sallapanti udāhu añña maññampi, yadi aññamaññampi na sallapanti, addhā vivādajātā bhavissanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',224,replace(replace('<p class="gatha1">Aññaṃ <a name="M3.0224"></a> uppajjate cittaṃ, aññaṃ cittaṃ nirujjhati;</p>\r\n<p class="gathalast">Avīci manusambandho, nadī sotova vattati.</p>\r\n<p class="bodytext">Ālokite vilokite sampajānakārī hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',225,replace(replace('<p class="bodytext">Sace <a name="M3.0225"></a> bhikkhave nandassa puratthimā disā āloketabbā hoti, sabbaṃ cetasā samannāharitvā nando puratthimaṃ disaṃ āloketi ‘evaṃ me puratthimaṃ disaṃ ālokayato nābhijjhā domanassā pāpakā akusalā dhammā anvāssavissantī’ti. Itiha sātthaka sampajāno hoti. Sace bhikkhave nandassa pacchimā disā, uttarā disā, dakkhiṇā disā, uddhaṃ, adho, anudisā anuviloketabbā hoti, sabbaṃ cetasā samannāharitvā nando anudisaṃ anuviloketi ‘evaṃ me anudisaṃ anuvilokayato…pe… sampajāno hotīti….</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',227,replace(replace('<p class="gatha1">Bhavaṅgā <a name="M3.0227"></a> vajjanañceva, dassanaṃ sampaṭicchanaṃ;</p>\r\n<p class="gathalast">Santīraṇaṃ voṭṭhabbanaṃ, javanaṃ bhavati sattamaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',231,replace(replace('<p class="bodytext">Samiñjite <a name="M3.0231"></a> pasārite sampajānakārī hoti.</p>\r\n<p class="bodytext">Samiñjite pahāriteti pabbānaṃ samiñjana pasāraṇe.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',233,replace(replace('<p class="bodytext">Kasmā <a name="M3.0233"></a> bhante sahasā hatthaṃ samiñjitvā puna yathāṭhāne ṭhapetvā saṇikaṃ samiñjitthāti.</p>\r\n<p class="bodytext">Yato paṭṭhāya mayā āvuso kammaṭṭhānaṃ manasikātuṃ āraddho, na me kammaṭṭhānaṃ muñjitvā hattho samiñjitapubbo, idāni pana me tumhehi saddhiṃ kathayamānena kammaṭṭhānaṃ muñcitvā samiñjito, tasmā puna yathā ṭhāne ṭhapetvā samiñjesinti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',234,replace(replace('<p class="bodytext">Sādhu <a name="M3.0234"></a> bhante bhikkhunā nāma evarūpena bhavitabbanti.</p>\r\n<p class="bodytext">Saṅghāṭipattacīvaradhāraṇe sampajānakārī hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',243,replace(replace('<p class="subhead">Puggalapaññattiaṭṭhakathā</p>\r\n<p class="subhead">Ekakaniddesa</p>\r\n<p class="bodytext">Katamo <a name="M3.0243"></a> ca puggalo samayavimutto, idhekacco puggalo kālena kālaṃ samayena samayaṃ aṭṭha vimokkhe kāyena phusitvā viharati, paññāya cassa disvā ekacce āsavā parikkhīṇā honti. Ayaṃ vuccati puggalo samayavimutto.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',247,replace(replace('<p class="subhead">Asamayavimutta</p>\r\n<p class="bodytext">Katamo <a name="M3.0247"></a> ca puggalo asamayavimutto, idhekacco puggalo naheva kho kālena kālaṃ samayena samayaṃ aṭṭhavimokkhe kāyena phusitvā viharati, paññāya cassa disvā āsavā parikkhīṇā honti. Ayaṃ vuccati puggalo asamayavimutto. Sabbepi ariya puggalā ariye vimokkhe asamayavimuttā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',250,replace(replace('<p class="subhead">Kuppākuppaniddesa</p>\r\n<p class="bodytext">Katamo <a name="M3.0250"></a> ca puggalo kuppadhammo, idhekacco puggalo lābhī hoti rūpasahagatānaṃ vā arūpasahagatānaṃ vā samāpattīnaṃ, so ca kho na nikāmalābhī hoti na akicchalābhī na akasira lābhī, na yatthicchakaṃ yadicchakaṃ yāvaticchakaṃ samāpajjatipi vuṭṭhātipi, ṭhānaṃ kho panetaṃ vijjhati, yaṃ tassa puggalassa pamādamāgamma tā samāpattiyo kuppeyyuṃ. Ayaṃ vuccati puggalo kuppadhammo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',252,replace(replace('<p class="subhead">Sattakkhattuparama sotāpanna</p>\r\n<p class="bodytext">Katamo <a name="M3.0252"></a> ca puggalo sattakkhattuparamo, idhekacco puggalo tiṇṇaṃ saṃyojanānaṃ parikkhayā sotāpanno hoti avinipāta dhammo niyato sambodhiparāyaṇo, so sattakkhattuṃ deve ca mānuse ca sandhāvitvā saṃsaritvā dukkhassantaṃ karoti, ayaṃ vuccati puggalo sattakkhattuparamo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',253,replace(replace('<p class="subhead">Sotāpanna adhippāyya</p>\r\n<p class="bodytext">Soto <a name="M3.0253"></a> sototi hidaṃ sāriputta vuccati, katamo nukho sāriputta sototi. Ayameva hi bhante ariyo aṭṭhaṅgiko maggo soto. Seyyathidaṃ, sammādiṭṭhi…pe… sammāsamādhīti. Sotāpanno sotāpannoti hidaṃ sāriputta vuccati, katamo nukho sāriputta sotāpannoti. Yo hi bhante iminā ariyena aṭṭhaṅgikena maggena samannāgato, ayaṃ vuccati sotāpanno, svāyaṃ āyasmā evaṃnāmo evaṃgotto iti vāti, itivā….</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',255,replace(replace('<p class="subhead">Kolaṃ kola sotāpanna</p>\r\n<p class="bodytext">Katamo <a name="M3.0255"></a> ca puggalo kolaṃ kolo. Idhekacco puggalo tiṇṇaṃ saṃyojanānaṃ parikkhayā sotāpanno hoti avinipātadhammo niyato sambodhiparāyaṇo, so dve vā tīṇi vā kulāni sandhāvitvā saṃsaritvā dukkhassantaṃ karoti, ayaṃ vuccati kolaṃ kolo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',256,replace(replace('<p class="subhead">Ekabījī sotāpanna</p>\r\n<p class="bodytext">Katamo <a name="M3.0256"></a> ca puggalo ekabījī, idhekacco puggalo tiṇṇaṃ saṃyojanānaṃ parikkhayā sotāpanno hoti avinipātadhammo niyato sambodhiparāyaṇo, so ekaṃyeva mānusakaṃ bhavaṃ nibbattetvā dukkhassantaṃ karoti, ayaṃ vuccati puggalo ekabījī.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_40',260,replace(replace('<p class="subhead">Sakadāgāminiddesa</p>\r\n<p class="bodytext">Katamo <a name="M3.0260"></a> ca puggalo sakadāgāmī, idhe kacco puggalo tiṇṇaṃ saṃyojanānaṃ parikkhayā rāgadosamohānaṃ tanuttā sakadāgāmī hoti sakideva imaṃ lokaṃ āgantvā dukkhassantaṃ karoti. Ayaṃ vuccati puggalo sakadāgāmī.</p>','\r',char(13)),'\n',char(10)),'');