INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="chapter">Namakkārapāḷi</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',1,replace(replace('<p class="gatha1">Sugataṃ <a name="M0.0001"></a> sugataṃ seṭṭhaṃ, kusalaṃkusalaṃ jahaṃ;</p>\r\n<p class="gathalast">Amataṃ amataṃ santaṃ, asamaṃ asamaṃ dadaṃ.</p>\r\n<p class="gatha1">Saraṇaṃ saraṇaṃ lokaṃ, araṇaṃ araṇaṃ karaṃ;</p>\r\n<p class="gathalast">Abhayaṃ abhayaṃ ṭhānaṃ, nāyakaṃ nāyakaṃ name.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Nayanasubhagakāyaṅgaṃ,</p>\r\n<p class="gatha2">Madhuravarasaropetaṃ;</p>\r\n<p class="gatha3">Amitaguṇagaṇādhāraṃ,</p>\r\n<p class="gathalast">Dasabalamatulaṃ vande.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Yo buddho dhitimāññadhārako,</p>\r\n<p class="gatha2">Saṃsāre anubhosi kāyikaṃ;</p>\r\n<p class="gatha3">Dukkhaṃ cetasikañca lokato,</p>\r\n<p class="gathalast">Taṃ vande naradevamaṅgalaṃ.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Bāttiṃsatilakkhaṇacitradehaṃ,</p>\r\n<p class="gatha2">Dehajjutiniggatapajjalantaṃ;</p>\r\n<p class="gatha3">Paññādhitisīlaguṇoghavindaṃ,</p>\r\n<p class="gathalast">Vande munimantimajātiyuttaṃ.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Pātodayaṃ bāladivākaraṃva,</p>\r\n<p class="gatha2">Majjhe yatīnaṃ lalitaṃ sirīhi;</p>\r\n<p class="gatha3">Puṇṇindusaṅkāsamukhaṃ anejaṃ,</p>\r\n<p class="gathalast">Vandāmi sabbaññumahaṃ munindaṃ.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-3-4-5-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',2,replace(replace('<p class="gatha1">Upetapuñño <a name="M0.0002"></a> varabodhimūle,</p>\r\n<p class="gatha2">Sasenamāraṃ sugato jinitvā;</p>\r\n<p class="gatha3">Abojjhi bodhiṃ aruṇodayamhi,</p>\r\n<p class="gathalast">Namāmi taṃ mārajinaṃ abhaṅgaṃ.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Rāgādichedāmalañāṇakhaggaṃ,</p>\r\n<p class="gatha2">Satīsamaññāphalakābhigāhaṃ;</p>\r\n<p class="gatha3">Sīloghalaṅkāravibhūsitaṃ taṃ,</p>\r\n<p class="gathalast">Namāmibhiññāvaramiddhupetaṃ.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Dayālayaṃ sabbadhi dukkaraṃ karaṃ,</p>\r\n<p class="gatha2">Bhavaṇṇavātikkamamaggataṃ gataṃ;</p>\r\n<p class="gatha3">Tilokanāthaṃ susamāhitaṃ hitaṃ,</p>\r\n<p class="gathalast">Samantacakkhuṃ paṇamāmi taṃmitaṃ.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Tahiṃ tahiṃ pāramisañcayaṃ cayaṃ,</p>\r\n<p class="gatha2">Gataṃ gataṃ sabbhi sukhappadaṃ padaṃ;</p>\r\n<p class="gatha3">Narānarānaṃ sukhasambhavaṃ bhavaṃ,</p>\r\n<p class="gathalast">Namānamānaṃ jinapuṅgavaṃ gavaṃ.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> Maggaṅganāvaṃ munidakkhanāviko,</p>\r\n<p class="gatha2">Īhāphiyaṃ ñāṇakarena gāhako;</p>\r\n<p class="gatha3">Āruyha yo tāya bahū bhavaṇṇavā,</p>\r\n<p class="gathalast">Tāresi taṃ buddhamaghappahaṃ name.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Samatiṃsatipāramisambharaṇaṃ,</p>\r\n<p class="gatha2">Varabodhidume catusaccadasaṃ;</p>\r\n<p class="gatha3">Varamiddhigataṃ naradevahitaṃ,</p>\r\n<p class="gathalast">Tibhavūpasamaṃ paṇamāmi jinaṃ.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> Satapuññajalakkhaṇikaṃ virajaṃ,</p>\r\n<p class="gatha2">Gaganūpamadhiṃ dhitimerusamaṃ;</p>\r\n<p class="gatha3">Jalajūpamasītalasīlayutaṃ,</p>\r\n<p class="gathalast">Pathavīsahanaṃ paṇamāmi jinaṃ.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-8-9-10-11-12-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',3,replace(replace('<p class="gatha1">Yo <a name="M0.0003"></a> buddho sumati dive divākarova,</p>\r\n<p class="gatha2">Sobhanto ratijanane silāsanamhi;</p>\r\n<p class="gatha3">Āsīno sivasukhadaṃ adesi dhammaṃ,</p>\r\n<p class="gathalast">Devānaṃ tamasadisaṃ namāmi niccaṃ.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> Yo pādapaṅkajamuduttalarājikehi,</p>\r\n<p class="gatha2">Lokehi tīhivikalehi nirākulehi;</p>\r\n<p class="gatha3">Sampāpuṇe nirupameyyatameva nātho,</p>\r\n<p class="gathalast">Taṃ sabbalokamahitaṃ asamaṃ namāmi.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Buddhaṃ narānarasamosaraṇaṃ dhitattaṃ,</p>\r\n<p class="gatha2">Paññāpadīpajutiyā vihatandhakāraṃ;</p>\r\n<p class="gatha3">Atthābhikāmanaradevahitāvahaṃ taṃ,</p>\r\n<p class="gathalast">Vandāmi kāruṇikamaggamanantañāṇaṃ.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> Akhilaguṇanidhāno yo munindopagantvā,</p>\r\n<p class="gatha2">Vanamisipatanavhaṃ saññatānaṃ niketaṃ;</p>\r\n<p class="gatha3">Tahimakusalachedaṃ dhammacakkaṃ pavatto,</p>\r\n<p class="gathalast">Tamatulamabhikantaṃ vandaneyyaṃ namāmi.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> Suciparivāritaṃ surucirappabhāhi rattaṃ,</p>\r\n<p class="gatha2">Sirivisarālayaṃ gupitamindriyehupetaṃ;</p>\r\n<p class="gatha3">Ravisasimaṇḍalappabhutilakkhaṇopacittaṃ,</p>\r\n<p class="gathalast">Suranarapūjitaṃ sugatamādaraṃ namāmi.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> Maggoḷumpena muhapaṭighāsādiullolavīciṃ,</p>\r\n<p class="gatha2">Saṃsāroghaṃ tari tamabhayaṃ pārapattaṃ pajānaṃ;</p>\r\n<p class="gatha3">Tāṇaṃ leṇaṃ asamasaraṇaṃ ekatitthaṃ patiṭṭhaṃ,</p>\r\n<p class="gathalast">Puññakkhettaṃ paramasukhadaṃ dhammarājaṃ namāmi.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> Kaṇḍambaṃmūle parahitakaro yo munindo nisinno,</p>\r\n<p class="gatha2">Accheraṃ sīghaṃ nayanasubhagaṃ ākulaṇṇaggijālaṃ;</p>\r\n<p class="gatha3">Dujjāladdhaṃsaṃ munibhijahitaṃ pāṭiheraṃ akāsi,</p>\r\n<p class="gathalast">Vande taṃ seṭṭhaṃ paramaratijaṃ iddhidhammehupetaṃ.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-15-16-17-18-19-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',4,replace(replace('<p class="gatha1">Munindakko <a name="M0.0004"></a> yveko dayudayaruṇo ñāṇavitthiṇṇabimbo,</p>\r\n<p class="gatha2">Vineyyappāṇoghaṃ kamalakathitaṃ dhammaraṃsīvarehi;</p>\r\n<p class="gatha3">Subodhesī suddhe tibhavakuhare byāpitakkittinañca,</p>\r\n<p class="gathalast">Tilokekaccakkhuṃ dukhamasahanaṃ taṃ mahesiṃ namāmi.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> Yo jino anekajātiyaṃ saputtadāramaṅgajīvitampi,</p>\r\n<p class="gatha2">Bodhipemato alaggamānaso adāsiyeva atthikassa;</p>\r\n<p class="gatha3">Dānapāramiṃ tato paraṃ apūri sīlapāramādikampi,</p>\r\n<p class="gathalast">Tāsamiddhiyopayātamaggataṃ tamekadīpakaṃ namāmi.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> Devādevātidevaṃ nidhanavapudharaṃ mārabhaṅgaṃ abhaṅgaṃ,</p>\r\n<p class="gatha2">Dīpaṃ dīpaṃ pajānaṃ jayavarasayane bodhipattaṃdhipattaṃ;</p>\r\n<p class="gatha3">Brahmābrahmāgatānaṃ varagirakathikaṃ pāpahīnaṃ pahīnaṃ,</p>\r\n<p class="gathalast">Lokālokābhirāmaṃ satatamabhiname taṃ munindaṃ munindaṃ.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> Buddho nigrodhabimbo mudukaracaraṇo brahmaghoseṇijaṅgho,</p>\r\n<p class="gatha2">Kosacchādaṅgajāto punarapi sugato suppatiṭṭhitapādo;</p>\r\n<p class="gatha3">Mūdodātuṇṇalomo athamapi sugato brahmujuggattabhāvo,</p>\r\n<p class="gathalast">Nīlakkhī dīghapaṇhī sukhumamalachavī thomyarasaggasaggī.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> Cattālīsaggadanto samakalapanajo antaraṃsappapīno,</p>\r\n<p class="gathalast">Cakkenaṅkitapādo aviraḷadasano mārajussaṅkhapādo.</p>','\r',char(13)),'\n',char(10)),'-21-22-23-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',5,replace(replace('<p class="gatha1">Tiṭṭhanto <a name="M0.0005"></a> nonamantobhayakaramudunā jaṇṇukānāmasanto,</p>\r\n<p class="gathalast">Vaṭṭakkhandho jino gotaruṇapakhumako sīhapubbaḍḍhakāyo.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> Sattappīno ca dīghaṅguli matha sugato lomakūpekalomo,</p>\r\n<p class="gathalast">Sampannodātadāṭho kanakasamataco nīlamuddhaggalomo.</p>\r\n<p class="gatha1">Sambuddho thūlajivho atha sīhahanuko jālikappādahattho,</p>\r\n<p class="gathalast">Nātho uṇhīsasīso itiguṇasahitaṃ taṃ mahesiṃ namāmi.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Buddhobuddhotighoso atidulabhataro kā kathā buddhabhāvo,</p>\r\n<p class="gathalast">Loke tasmā vibhāvī vividhahitasukhaṃ sādhavo patthayantā.</p>\r\n<p class="gatha1">Iṭṭhaṃ atthaṃ vahantaṃ suranaramahitaṃ nibbhayaṃ dakkhiṇeyyaṃ,</p>\r\n<p class="gathalast">Lokānaṃ nandivaḍḍhaṃ dasabalamasamaṃ taṃ namassantu niccaṃ.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> Puññenetena sohaṃ nipuṇamati sato samparāye ca titto,</p>\r\n<p class="gathalast">Dakkho diṭṭhujjupañño avikalavīriyo bhogavā saṃvibhāgī.</p>\r\n<p class="gatha1">Tikkho sūro dhitatto saparahitacaro dīghajīvī arogo,</p>\r\n<p class="gathalast">Dhañño vaṇṇo yasassī atibalavadharo kittimā khantupeto.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-26-27-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',6,replace(replace('<p class="gatha1">Saddho <a name="M0.0006"></a> dātaṅgupeto paramasiridharo diṭṭhadhamme viratto,</p>\r\n<p class="gathalast">Lajjī kalyāṇamitto abhiratakusalo pañcasīlādirakkho.</p>\r\n<p class="gatha1">Appiccho appakodho ativujuhadayo iddhimā appameyyo,</p>\r\n<p class="gathalast">Pāsaṃso pemavāco sujanaguṇavidū māmako so bhaveyyaṃ.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> Itthaṃ asaṅkhaye nātha, guṇe lakkhaṇadīpite;</p>\r\n<p class="gathalast">Gāthāsu sūcakāsveka, gāthampi sarate budho.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> Caturāpāyamutto so, sādhakatthadvayassa ca;</p>\r\n<p class="gathalast">Hatūpaddavajālo ca, lābhī hitasukhassa ca.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> Adhipo naradevānaṃ, catudīpissaropi vā;</p>\r\n<p class="gathalast">Bhaveyya antime dehe, tamaññaṃ setachattakaṃ.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> Bhāvanāyānamāruyha, samamessati subbato;</p>\r\n<p class="gathalast">Imasmiṃ attabhāvepi, arogo dīghajīviko.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> Pūjito sabbalokehi, bhāvanābhiratīmano;</p>\r\n<p class="gathalast">Janappiyo manāpo ca, kā kathākhiladhāraṇe.</p>\r\n<p class="centered">Namakkārapāḷi niṭṭhitā.</p>\r\n<p class="chapter">Namakkāraṭīkā</p>\r\n<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-30-31-32-33-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',7,replace(replace('<p class="gatha1">Sugatantyādivaṇṇehi<a name="M0.0007"></a>, pasatthañca sukhaddadaṃ;</p>\r\n<p class="gathalast">Vandantānaṃ jinaṃ natvā, dhammaṃ suddhaṃ gaṇuttamaṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Namakkāraṃ hitatthīhi, porāṇācariyehi ca;</p>\r\n<p class="gathalast">Likhitvā ṭhapitaṃ puñña, buddhiyā tena vandinaṃ.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Tassatthavaṇṇanaṃ kassaṃ, gambhīratthasubodhanaṃ;</p>\r\n<p class="gathalast">Movipunāmena santena, sīlupetena yācito.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Vaṇṇantopi ca tassatthaṃ, taṃ taṃ aṭṭhakathādisu;</p>\r\n<p class="gathalast">Vuttañāyena kassāmi, saddhā suṇātha sādhavo.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Namanaṃ namatītī vā, namatī tena vā namo;</p>\r\n<p class="gathalast">Tassa kāronti tāhīti, namakkāroti vuccati.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Tañca buddhaghosācari, yena pubbasutaṃ kataṃ;</p>\r\n<p class="gathalast">Buddhaghosuppattiyañca, taṃ taṃ pana kathādisu.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Na diṭṭhaṃ katakāraṇaṃ, tasmā hitatthikehi ca;</p>\r\n<p class="gatha2">Porāṇācariyehī ca, ṭhapitanti mayā vuttaṃ;</p>\r\n<p class="gathalast">Taṃ kāraṇaṃ viññūhī ca, vicāretvā vijānitanti.</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> Sugataṃ sugataṃ seṭṭhaṃ, kusalaṃkusalaṃ jahaṃ;</p>\r\n<p class="gathalast">Amataṃ amataṃ santaṃ, asamaṃ asamaṃ dadaṃ.</p>\r\n<p class="gatha1">Saraṇaṃ saraṇaṃ lokaṃ, araṇaṃ araṇaṃ karaṃ;</p>\r\n<p class="gathalast">Abhayaṃ abhayaṃ ṭhānaṃ, nāyakaṃ nāyakaṃ name.</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span>. [Ka] </p>','\r',char(13)),'\n',char(10)),'-2-3-4-5-6-7-1-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',8,replace(replace('<p class="bodytext">idāni <a name="M0.0008"></a> paṭhamaṃ tāva sugatantyādīhi aṭṭhārasahi guṇehi thometvā buddhassa vanditukāmena sugatantiādi gāthādvayaṃ āraddhaṃ. Ayaṃ pana sabbapādādiyamakapathyā vattagāthāti daṭṭhabbaṃ. Taṃ pana yamakaṃ <span class="bld">subodhālaṅkāre</span> kiliṭṭhadosanti vuttaṃ.</p>\r\n<p class="bodytext">Vuttañhi tattha –</p>\r\n<p class="gatha1">‘‘Yaṃ kiliṭṭhapadaṃ mandā, bhidheyyaṃ yamakādikaṃ;</p>\r\n<p class="gathalast">Kiliṭṭhapadadoseva, tampi anto karīyatī’’ti.</p>\r\n<p class="bodytext">Ayaṃ panettha patītasaddaracitattāyeva pasādaguṇa saṅkhātena saddālaṅkārena saṃyuttā hoti. Tasmā taṃ yamakaṃ suvuttanti daṭṭhabbaṃ. Evaṃ parāsu yamakagāthāsu. Tena vuttaṃ punā <span class="bld">laṅkāre</span> –</p>\r\n<p class="gatha1">‘‘Patītasaddaracitaṃ, siliṭṭhapadasandhikaṃ;</p>\r\n<p class="gathalast">Pasādaguṇasaṃyuttaṃ, yamakaṃ matamedisa’’nti.</p>\r\n<p class="bodytext">Tattha <span class="bld">sugata</span>nti sobhanagamanattā sundaraṃ ṭhānaṃ gatattā sammā gatattā ca sugato, so hi sobhanaṃ ariyamaggena gacchatīti sugatoti vuccati, supubbo gamudhātu ta. Susaddo sobhanattho. Ariyamaggagamanena parisuddhaṃ anavajjaṃ khemaṃ disaṃ asajjamāno gatoti attho. Tañca gamanaṃ duvidhaṃ hoti kāyagamanaṃ, ñāṇagamanañcāti. Tattha bhagavato veneyyajanupasaṅkamanaṃ ekantena tesaṃ hitasukhassa nipphāda nato sobhanaṃ kāyena gamanaṃ kāyagamanaṃ nāma. Sayambhū ñāṇena pana sakalampi lokaṃ pariññābhisamayavasena parijānanato ñāṇena gamanaṃ ñāṇagamanaṃ nāma. Idha pana ñāṇagamanaṃ adhippetaṃ. <span class="bld">Aṅguttaraṭīkāyaṃ</span> pana ‘‘sobhanaṃ gataṃ gamanaṃ etassāti sugato’’ti vuttaṃ. Sundaraṃ ṭhānaṃ gacchatīti sugatoti ca vuccati, ārammaṇakaraṇavasena amataṃ nibbānaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',9,replace(replace('<p class="bodytext">gatoti <a name="M0.0009"></a> attho. Sammā aviparītaṃ cesa gacchatīti sugatoti vuccati. Susaddo sammāsaddattho. Tena tena maggena ye kilesā pahīnā, te kilese na puneti na pacceti na paccāgacchatīti attho. Atha vā sammā āgacchatīti sugatoti ca vuccati, dīpaṅkarapādamūlato pabhuti yāva bodhimaṇḍā tāva samatiṃsapāramipūrikāya sammāpaṭipattiyā sabbalokassa hitasukhameva karonto sassataṃ ucchedaṃ kāmasukhaṃ attakilamathañcāti ime cante anupagacchanto āgatoti attho. Taṃ sugataṃ buddhaṃ name namāmīti sambandho.</p>\r\n<p class="bodytext">Puna <span class="bld">sugata</span>nti sammā gadattā sugato. So hi sammā gadati bhāsatīti sugatoti vuccati. Supubbagadadhātu bhāsāyaṃ viyattiyaṃ vācāyaṃ vā, ta, dassa to. Chasu vācāsu yuttaṭṭhāne yuttameva dvivācaṃ bhāsatīti attho. Vuttañhi majjhimapaṇṇāsapāḷiyaṃ <span class="bld">abhayarājakumārasutte</span> –</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span></p>\r\n<p class="bodytext">. ‘‘Rājakumāra yaṃ tathāgato taṃ vācaṃ jānāti abhūtaṃ atacchaṃ anatthasaṃhitaṃ, sā ca paresaṃ appiyā amanāpā, na taṃ tathāgato vācaṃ bhāsati. 2. Yampi tathāgato vācaṃ jānāti bhūtaṃ tacchaṃ anatthasaṃhitaṃ, sā ca paresaṃ appiyā amanāpā. Tampi tathāgato vācaṃ na bhāsati. 3. Yañca kho tathāgato vācaṃ jānāti bhūtaṃ tacchaṃ atthasaṃhitaṃ, sā ca paresaṃ appiyā amanāpā, tatrakālaññū tathāgato hoti tassā vācāya veyyākaraṇāya. 4. Yaṃ tathāgato vācaṃ jānāti abhūtaṃ atacchaṃ anatthasaṃhitaṃ, sā ca paresaṃ piyā manāpā, na taṃ tathāgato vācaṃ bhāsati. 5. Yampi tathāgato jānāti bhūtaṃ tacchaṃ anatthasaṃhitaṃ, sā ca paresaṃ piyā manāpā, tampi tathāgato vācaṃ na bhāsati. 6. Yañca kho tathāgato vācaṃ jānāti bhūtaṃ tacchaṃ atthasaṃhitaṃ, sā ca paresaṃ piyā </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',10,replace(replace('<p class="bodytext">manāpā<a name="M0.0010"></a>. Tatra kālaññū tathāgato hoti tassā vācāya veyyākaraṇāyā’’ti.</p>\r\n<p class="bodytext">Ettha pāḷiyaṃ panesā sukhaggahaṇatthaṃ saṅkhepena veditabbā. Kathaṃ hesā abhūtā anatthasaṃhitā appiyā vācā ca, bhūtā anatthasaṃhitā appiyā vācā ca, bhūtā attha saṃhitā appiyā vācā ca, abhūtā anatthasaṃhitā piyā vācā ca, bhūtā anatthasaṃhitā piyā vācā ca, bhūtā atthasaṃhitā piyā vācā cāti chabbidhā hotīti. Tattha acoraṃyeva coro ayantiādivasena vattabbavācā paṭhamavācā nāma. Coraṃyeva coro ayantiādivasena vattabbavācā dutiyavācā nāma. Akatapuññatāya duggato dubbaṇṇo appesakkho, idha pana ṭhatvāpi puna puññaṃ na karoti, dutiyacittavāre kathaṃ catūhi apāyehi muccissatīti evaṃ mahājanassa atthapurekkhārena dhammapurekkhārena anusāsanīpurekkhārena vattabbavācā tatiyavācā nāma. Eko dhutto āha-mayhaṃ bho mātu mayi kucchigate kapiṭṭhaphaladohaḷo ahosi. Sā aññaṃ kapiṭṭhahārakaṃ alabhamānā maṃyeva pesesi. Ahaṃ gantvā rukkhaṃ abhiruhituṃ asakkonto attanāva attānaṃ pāde gahetvā muggaraṃ viya rukkhassa upari khipiṃ. Atha sākhato sākhaṃ vicaranto phalāni gahetvā otarituṃ asakkonto gharaṃ gantvā nisseṇiṃ āharitvā oruyha mātu santike gantvā phalāni mātuyā adāsiṃ. Tāni pana mahantāni honti cāṭippa māṇāni. Tato me mātarā ekāsane nisinnāya samasaṭṭhiphalāni khāditānīti. Evamādivasena vattabbavācā catutthavācā nāma. Āmisahetu cāṭukamyatādivasena nānappakārakaṃ paresaṃ thomanā vācā ceva rājakathaṃ corakathantiādinayappavattā tiracchānakathā ca pañcamavācā nāma. Ariyasaccasannissitā dhammakathā chaṭṭhamavācā nāma. Taṃ pana vassasatampi suṇantā paṇḍitā neva tittiṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',11,replace(replace('<p class="bodytext">gacchanti<a name="M0.0011"></a>. Tāsu pana bhagavā tatiyachaṭṭhamā dvivācāeva bhāsati. Tasmā sappurisehi sāyeva dvivācā vattabbāti. Ayaṃ tadaṭṭhakathāyaṃ āgato saṅkhepanayo. Visuddhimagga mahāṭīkāyaṃ pana sāratthadīpanīṭīkāyañca tadaññākārena vaṇṇito. Tāsu pana taṃ oloketvā gahetabbanti. Ayaṃ panettha saṅgahagāthā –</p>\r\n<p class="gatha1">‘‘Yo bhūtamatthasaṃhītaṃ, paresamappiyā vācaṃ;</p>\r\n<p class="gatha2">Bhūtamatthasahītañca, paresaṃ piyavācanti;</p>\r\n<p class="gatha3">Dvivācaṃ yuttaṭṭhāneva, vadatīti sugatoti;</p>\r\n<p class="gathalast">Vuccatī ca dakārassa, katvā takāraviññunā’’ti.</p>\r\n<p class="bodytext"><span class="bld">Seṭṭha</span>nti guṇamahantattā pasatthataraṃ. So hi pasatthānaṃ visesena pasatthoti seṭṭhoti vuccati. Pasatthapadaṃ, visesataddhite iṭṭhapaccayo. Pasatthasaddassa so, pasatthānaṃ paccekabuddhāriyasāvakādīnaṃ sīlasamādhipaññādi guṇehi visesena pasatthoti attho. Atha vā sundare satipaṭṭhānādibhede dhamme esati gavesati esiṃ gavesinti vā seṭṭho, buddho. Supubboesa gavesane ta. Tassa raṭṭho. Susaddo sundarattho. Santehi sappurisehi esitabbo gavesitabboti vā seṭṭho. Santasaddūpapado isa gavesane ta, santasaddassa so, ikārasse, tassa ṭṭho. Santehi sappurisehi icchiyati kantiya tīti vā seṭṭho. Santasaddūpapado isu icchākantīsu ta. Atha vā santānaṃ sappurisānaṃ hitasukhaṃ icchatīti seṭṭho, buddho. Kattusādhanoyaṃ. Sundare satipaṭṭhānā dibhede dhamme eseti buddhetīti vā seṭṭho. Hetu kattusādhanoyaṃ. Supubbaesadhātu buddhiyaṃ ta. Taṃ seṭṭhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Kusalaṃkusalaṃ jaha</span>nti ettha kusalaṃ akusalaṃ jahanti padavibhāgo kātabbo. Kucchitaṃ pāpadhammaṃ salayati </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',12,replace(replace('<p class="bodytext">calayati <a name="M0.0012"></a> kampeti viddhaṃsetīti kusalaṃ, catubhūmikakusalaṃ labbhati. Kupubbo saladhātu a. Tattha kāmāvacarakusalaṃ tadaṅgappahānena pāpadhammaṃ salayati calayati, mahaggata kusalaṃ vikkhambhanappahānena salayati kampeti, lokuttara kusalaṃ samucchedappahānena salayati viddhaṃsetīti attho.</p>\r\n<p class="bodytext">Atha vā kucchitaṃ apāyadvāraṃ salanti saṃvaranti pidahanti sādhavo etenāti kusalaṃ, karaṇasādhanoyaṃ. Idha pana jahanti vuttattā tebhūmakakusalamevādhippetaṃ. Taṃ pana anavajjasukhavipākalakkhaṇaṃ, akusalaviddhaṃsanarasaṃ, vodāna paccupaṭṭhānaṃ, iṭṭhavipākapaccupaṭṭhānaṃ vā, yonisomanasikāra padaṭṭhānaṃ. Na kusalaṃ akusalaṃ, mittapaṭipakkho amitto viya pahāyakapahātabbabhāvena kusalapaṭipakkhanti attho. Taṃ pana sāvajjadukkhavipākalakkhaṇaṃ, sāvajjāniṭṭhavipāka lakkhaṇaṃ vā, anatthajananarasaṃ, aniṭṭhavipākapaccupaṭṭhānaṃ, ayonisomanasikārapadaṭṭhānaṃ. Puññābhisaṅkhāraāneñjā bhisaṅkhārasaṅkhātaṃ kusalaṃ apuññābhisaṅkhārasaṅkhātaṃ akusalañca kusalaṃkusalaṃ. Taṃ pana padaṃ jahantipade kammaṃ.</p>\r\n<p class="bodytext">Jahanti maggakkhaṇe samucchedappahānena phalakkhaṇe paṭippassambhanappahānena pajahantaṃ viddhaṃsentanti attho. Tañhi arahattamaggañāṇena jahati pajahati, phalañāṇena jahiṃ pajahiṃ paṭippassambhinti vā jaho, buddho. Hādhātu cāge a. Kiñcāpi panettha kusalaṃ maggena pahātabbadhamme na vuttaṃ, vaṭṭamūlabhūte avijjātaṇhādikilese pana arahattamagga ñāṇena jahite lokiyakusaluppattiyā mūlabhūtāya avijjātaṇhāya abhāvā puññābhisaṅkhāro saṃsāre puna paṭisandhiyā nibbattetuṃ na sakkoti, tasmā vaṭṭamūlabhūte kilese jahite phalūpacārena kusalampi jahitaṃ nāma hoti. Rūpaṃ bhikkhave na tumhākaṃ taṃ pajahathātiādīsu </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',13,replace(replace('<p class="bodytext">viya<a name="M0.0013"></a>. Arahantena hi abhisamācārikavattaṃ katampi kusalanti na vuttaṃ, kriyāti pana vuttanti veditabbaṃ.</p>\r\n<p class="bodytext">Vuttañhi <span class="bld">nidānavaggasaṃyuttaṭṭhakathāyaṃ</span> ‘‘khīṇāsavena hi kataṃ kammaṃ neva kusalaṃ hoti nākusalaṃ, avipākaṃ hutvā kriyamatte tiṭṭhatī’’ti.</p>\r\n<p class="bodytext"><span class="bld">Aṅguttaraṭṭhakathāyañca</span> ‘‘arahattamaggo ca kusalākusala kammakkhayāya saṃvattatīti evaṃ tena taṃ bhijjatī’’ti vuttaṃ.</p>\r\n<p class="bodytext">Tena vuttaṃ <span class="bld">cūḷaniddesapāḷiyaṃ</span> ‘‘arahattamaggañāṇena abhisaṅkhāraviññāṇassa nirodhena ye uppajjeyyuṃ nāmañca rūpañca, etthete nirujjhanti vūpasamanti atthaṃ gacchanti paṭippassambhantī’’ti.</p>\r\n<p class="bodytext"><span class="bld">Amata</span>nti maraṇavirahitaṃ. So jino hi na mato amato, maraṇadhammassa nibbattakānaṃ kilesānaṃ arahatta maggena samucchinditattā puna maraṇato virahitoti vā amato. Atha vā nibbāne ārammaṇakaraṇavasena adhigate natthi mataṃ maraṇaṃ yassa jinassāti amatoti vuccati. Jinena hi khandhamaccumāraṃ ajitopi kilesamāraṃ jayitvā tesaṃ avassaṃ jiyamānattā phalūpacārena amatoti vuccatīti veditabbaṃ.</p>\r\n<p class="bodytext">Puna <span class="bld">amata</span>nti amatanibbānasamannāgataṃ. Natthi mataṃ maraṇaṃ etthāti amataṃ, nibbānaṃ. Taṃ pana santilakkhaṇaṃ, accutarasaṃ, animittapaccupaṭṭhānaṃ, nissaraṇapaccupaṭṭhānaṃ vā, nibbānassa pana padaṭṭhānaṃ na labbhati.</p>\r\n<p class="bodytext">Vuttañhi <span class="bld">milindapañhe</span> nāgasenattherena ‘‘na kenaci kāraṇena nibbānaṃ uppajjatī’’ti.</p>\r\n<p class="bodytext">Taṃ assa atthīti amato, jino. Saddhādito ṇāti suttena assathyatthe ṇapaccayo. Saddho paññotiādīsu </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',14,replace(replace('<p class="bodytext">viya<a name="M0.0014"></a>. Assa jinassa catumaggañāṇena sacchikataṃ amataṃ nibbānaṃ atthīti attho, taṃ amataṃ.</p>\r\n<p class="bodytext"><span class="bld">Santa</span>nti sabbakilesadarathehi ca vaṭṭadukkhehi ca suvūpasantaṃ. So hi buddho sabbakilesadarathavaṭṭadukkhehi samittha vūpasamitthāti santoti vuccati. Samudhātu upasame ta. Dhātvantassa lopaṃ tapaccayassantoādesaṃ katvā padasiddhi veditabbā. Saṃsāravaṭṭe darathakārakānaṃ kilesānaṃ bodhimaṇḍe arahattamaggañāṇena samucchinditattā sītibhūto santakāyacittoti vuttaṃ hoti, taṃ santaṃ.</p>\r\n<p class="bodytext"><span class="bld">Asama</span>nti sīlādiguṇehi kenaci puggalena asadisaṃ, so hi buddho natthi sīlādiguṇehi samo sadiso etassāti asamoti vuccati. Imasmiṃ sattaloke sīla samādhipaññādiguṇehi buddhena sadiso koci manusso vā devo vā brahmā vā natthīti attho. Bhagavato hi asīti anubyañjanapaṭimaṇḍita bāttiṃsa mahāpurisalakkhaṇehi vicitrarūpakāyo ca sabbākārehi parisuddhasīlakkhandhādiguṇa ratanasamiddho dhammakāyo ca puññamahattathāmamahatta iddhimahattayasamahattapaññāmahattādiguṇehi ca paramukkaṃsagato ca atthi, tasmā bhagavā sīlādiguṇehi kenaci asamo asadisoti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Asamaṃ dada</span>nti lokiyadhammena asadisaṃ maggaphalanibbāna dhammaṃ veneyyānaṃ sattānaṃ desanāñāṇena dāyakaṃ. Ettha ca asamanti natthi ettha etesaṃ vā samo sadiso lokiyadhammoti asamo, maggaphalanibbānaṃ labbhati. Atha vā asamanti aññesaṃ dhammānaṃ aggabhāvato catubbidhena ariya maggena sacchikātabbaṃ nibbānaṃ vuccati. Vuttañhetaṃ bhagavatā ‘‘yāvatā bhikkhave dhammā saṅkhatā vā asaṅkhatā vā, virāgo tesaṃ aggamakkhāyatī’’ti. Tassa pana padassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',15,replace(replace('<p class="bodytext">dadantipadena <a name="M0.0015"></a> kammabhāvena sambandho. Dadātīti dado, buddho. Dādhātu a, so kenaci lokiyadhammena asamaṃ asadisaṃ maggaphalanibbānasaṅkhātaṃ lokuttaradhammaṃ mahākaruṇāppadhānena amatādhigamapaṭipattidassanena desanāñāṇena veneyyānaṃ sattānaṃ dātāti attho. Taṃ asamaṃ dadaṃ.</p>\r\n<p class="bodytext">(Kha) dutiyagāthāyaṃ pana <span class="bld">saraṇa</span>nti sabbasattalokassa paṭisaraṇabhūto parāyaṇo leṇo tāṇoti vuttaṃ hoti. So hi buddho saranti paṭisaranti ettha sattalokāti saraṇoti vuccati. Saradhātu yu. Atha vā saranti osaranti samosaranti ettha sattalokāti saraṇoti vuccati. Saranti cintenti ettha saddhā pasannā devamanussā saraṇamidanti saraṇoti ca. Saranti paṭivijjhanti etthupagatā devamanussā saddhammanti saraṇoti ca vuccati. Sara cintāyaṃ yu. Saraṇagamanaṭṭhāne pana sarati hiṃsati bhayaṃ santāpaṃ dukkhaṃ kilesañcāti saraṇaṃ. Ratanatta yanti aṭṭhakathāsu vuttaṃ. Taṃ saraṇaṃ.</p>\r\n<p class="bodytext">Puna <span class="bld">saraṇaṃ loka</span>nti sattasaṅkhārokāsasaṅkhataṃ lokaṃ sarantaṃ jānantaṃ. Ettha hi lokayati patiṭṭhayati ettha puññāpuññañca tabbipāko cāti loko, satta loko labbhati. Lokadhātu patiṭṭhāyaṃ ṇa. Lujjati palujjatīti loko, saṅkhāraloko. Lujadhātuṇa. Lokiyati vicittākārena dissatīti loko, lokiyanti patiṭṭhiyanti ettha vā jaṅgamā ca thāvarā cāti loko, cakkavāḷasaṅkhato okāsaloko labbhati. Lokadhātuṇa. Ettha ca sassato lokoti vā asassato lokoti vāti āgataṭṭhāne indriyabaddhānaṃ rūpādīnaṃ khandhānaṃ samūho santāno ca sattoyeva loko sattalokoti vacanatthena sattalokoti veditabbo. Eko loko sabbe sattā āhā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',16,replace(replace('<p class="bodytext">raṭṭhitikāti <a name="M0.0016"></a> āgataṭṭhāne indriyabaddhānaṃ vā anindriyabaddhānaṃ vā khandhānaṃ samūho santāno ca paccayehi saṅkhariyantīti saṅkhārā, saṅkhārāeva loko saṅkhāralokoti vacanatthena saṅkhāralokoti.</p>\r\n<p class="gatha1">‘‘Yāvatā candimasūriyā pariharanti,</p>\r\n<p class="gatha2">Disā bhanti virocamānā;</p>\r\n<p class="gatha3">Tāva sahassadhā loko,</p>\r\n<p class="gathalast">Ettha te vattati vaso’’ti.</p>\r\n<p class="bodytext">Āgataṭṭhāne anindriyabaddhānaṃ rūpādīnaṃ samūho santāno ca avakasanti patiṭṭhahanti etthāti okāso, soyeva loko okāsalokoti vacanatthena okāsa lokoti veditabbo, taṃ lokaṃ. Sarati jānāti ajānīti vā saraṇo, buddho. Sara cintāyaṃ yu. Lokaṃ saraṇanti sambandho. Buddho pana sattalokaṃ, saṅkhāralokaṃ, okāsalokaṃ, kilesalokaṃ, bhavalokaṃ, indriya lokaṃ, khandhalokaṃ, āyatanalokaṃ dhātulokañcātiādimanekalokaṃ anāvaraṇañāṇena paṭivijjhatīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Araṇa</span>nti nikkilesaṃ, kilesasaṅkhātaraṇavirahitaṃ vā. Raṇasaddo ca kilesesu ca yuddhe cuṇṇavicuṇṇakaraṇe ca dissati. Tathā hi saraṇā dhammā araṇā dhammātiādīsu kilesesu dissati. Te hi raṇanti kandanti sattā etehīti raṇāti vuccanti.</p>\r\n<p class="gatha1">‘‘Dhanuggaho asadiso, rājaputto mahabbalo;</p>\r\n<p class="gathalast">Sabbāmitte raṇaṃ katvā, saṃyamaṃ ajjhupāgamī’’ti.</p>\r\n<p class="bodytext">Asadisajātake yuddhe dissati. Raṇaṃ katvāti hi yuddhaṃ katvā, saṃyamanti saṃyataṃ isipabbajjaṃ ajjhupāgamīti attho. Tiṇañca kaṭṭhañca raṇaṃ karonto, dhāviṃsu te </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',17,replace(replace('<p class="bodytext">aṭṭhadisā <a name="M0.0017"></a> samantatoti chaddantajātake cuṇṇavicuṇṇakaraṇe dissati. Raṇaṃ karontoti hi cuṇṇaṃ vicuṇṇaṃ karonto. Teti sabbe aṭṭhasahassanāgāti attho. Idha pana kilese daṭṭhabbo. Ayaṃ panettha vacanattho. Raṇanti satte rāgādayo cuṇṇenti pīḷentīti raṇā, raṇanti sattā etehi kandanti paridevantīti vā raṇā, kilesā. Raṇadhātu sadde a. Natthi raṇā kilesā etassāti araṇoti vuccati. Jinassa kilesamārassa bodhimūle jiyamānattā raṇā kilesā na santīti attho. Taṃ araṇaṃ.</p>\r\n<p class="bodytext"><span class="bld">Araṇaṃ kara</span>nti veneyyasantāne raṇasaṅkhatakilesassa abhāvakaraṇaṃ. Ettha ca raṇanti etaṃ yehi abhibhūtā sattā kandanti paridavanti etesaṃ rāgādīnaṃ adhivacanaṃ. Tesaṃ abhāvaṃ veneyyasantāne desanāñāṇena karoti attho. Atha vā karotīti karo, buddho. Taṃ karaṃ.</p>\r\n<p class="bodytext"><span class="bld">Abhaya</span>nti jātibhayādīnaṃ abhāvato nibbhayaṃ. Ettha hi bhayaṃ cittutrāsabhayaṃ, ñāṇabhayaṃ, ārammaṇabhayaṃ, ottappa bhayañcāti catubbidhaṃ hoti. Tattha bhāyati cittenāti bhayaṃ, cittutrāsabhayaṃ. Ñāṇena tīretvā bhāyatīti bhayaṃ, ñāṇaṃ. Bhāyati etasmāti bhayaṃ, ārammaṇabhayaṃ. Bhāyati etena pāpatoti bhayaṃ, ottappabhayaṃ. Bhīdhātu bhaye ṇa.</p>\r\n<p class="bodytext"><span class="bld">Mahāniddesapāḷiyaṃ</span> pana āgatāni jātibhayaṃ, jarābhayaṃ, byādhibhayaṃ, maraṇabhayaṃ, rājabhayaṃ, corabhayaṃ, aggibhayaṃ, udakabhayaṃ, attānuvādabhayaṃ, parānuvādabhayaṃ, daṇḍabhayaṃ, duggatibhayaṃ, ūmibhayaṃ, kumbhīḷabhayaṃ, āvaṭṭabhayaṃ, susukabhayaṃ, ājīvikabhayaṃ, asilokabhayaṃ, parisayasārajjabhayaṃ, madana bhayaṃ, duggatibhayantiādīni gahitāni. Natthi bhayāni etassāti abhayo, buddho. Tassa bhayānaṃ kāraṇa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',18,replace(replace('<p class="bodytext">bhūtānaṃ <a name="M0.0018"></a> sabbakilesānaṃ arahattamaggena samucchinditattā sabbabhayaṃ natthīti attho.</p>\r\n<p class="bodytext">Puna <span class="bld">abhayaṃ ṭhānaṃ nāyaka</span>nti ettha abhayantipadaṃ ṭhānantipade visesanaṃ. Ṭhānantipadaṃ nāyakantipade kammaṃ. Bhayasaddassa attho heṭṭhā vuttoyeva. Natthi sabbabhayaṃ ettha nibbāneti abhayaṃ, nibbānaṃ. Buddhapaccekabuddha arahantā ṭhanti uppajjanti etthāti ṭhānaṃ, nibbānaṃ. Veneyyasatte netīti nāyako, buddho. Nīdhātu nayane ṇvu. So buddho bhabbe kulaputte maggādhi gamappattiyā dhammadesanāya maggaphalañāṇaṃ adhigamento nibbānaṭṭhānaṃ netīti attho, taṃ nāyakaṃ.</p>\r\n<p class="bodytext">Puna <span class="bld">nāyakaṃ name</span>ti nāyakaṃ buddhaṃ ahaṃ name namāmīti sambandho. Tattha nāyakanti veneyyasatte saṃsāraṇṇavato nibbānapāraṃ netīti nāyako, buddho. So hi yathā nāviko jane nāvāya orimatīrato pārimatīraṃ neti, evaṃ satte maggaṅganāvāya saṃsāraṇṇavato nibbāna pāraṃ neti pāpuṇetīti attho. Atha vā nāyakanti tiṇṇaṃ lokānaṃ jeṭṭhabhāvagamanattā nāyako jeṭṭha seṭṭhavuḍḍhataro. So hi jeṭṭhabhāvaṃ neti gacchati pāpuṇātīti nāyakoti vuccati. Nīdhātu gatimhi ṇvu. So pana idha loke avijjaṇḍagatānaṃ sabbapajānaṃ paṭhamataraṃ avijjaṇḍakosaṃ arahattamaggena padāletvā ariyāya jātiyā jātattā sabbasattānaṃ seṭṭhajeṭṭhavuḍḍhatarabhāvaṃ gatoti attho. Taṃ nāyakaṃ. Nameti ahaṃ tīhi dvārehi sakkaccaṃ namāmīti attho. Ettha hi vattamānamivibhattiyā kvacidhātutyādisuttena eādeso hoti. Ayaṃ panettha vāccatthādinayo. Nametipadassa ākhyātapadattā vacanattho na kātabbo. Vuttañhi <span class="bld">saddavidūhi</span> –</p>\r\n<p class="gatha1">‘‘Ruḷhīkhyātaṃ nipātañcu, pasaggālapanaṃ tathā;</p>\r\n<p class="gathalast">Sabbanāmantimetesu, na kato viggaho chasū’’ti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',19,replace(replace('<p class="bodytext">Taṃ <a name="M0.0019"></a> pana padaṃ namudhātuvattamānaamhayogaparassapadamivibhatyantaṃ ākhyātapadaṃ. Namudhātuyā vāccattho namanakriyā saṅkhāto kusalacittuppādo padhānavasena labbhati. Mivibhattiyā vāccattho tena cittuppādena upalakkhito uppāda vanto tadākāro puggalo appadhānavasena labbhati. Padasamudāyassa pana vāccattho kattukammabhāvesu kattubhūto atītādikālesu paccuppannakālabhūto dhātvattha kiriyāvisesova labbhati. Ayamettha saṅkhepo.</p>\r\n<p class="bodytext">Vitthāro panevaṃ veditabbo. Namudhātuyā vaccaṃ sugatādi buddhaguṇaṃ ārammaṇaṃ katvā vandanākārappavatto kāyavacī viññattisamuṭṭhāpako saddhācetanāpaññāpadhāno kusala cittuppādo ca dve viññattiyo asamuṭṭhāpetvā manodvāre vandanākārappavatto kusalacittuppādo ca mukhyena labbhati. Buddhaguṇānussaraṇavasena pavatto yonisomanasikā rādipurecarānucarakusalacittuppādo phalūpacārena. Diṭṭhadhammi kasamparāyikasaṅkhātaṃ pacchimapacchimaphalaṃ rogādiantarāyānaṃ abhāvañca vā saggamokkhasampattisukhañca kāraṇūpacārena, pure pure vandanā sadisyūpacārena, vandanassa nāmapaññatti ṭhānūpacārena, vatthudvārārammaṇaṃ ṭhānūpacārena, vandana guṇena lakkhitaṃ puggaladabbaṃ guṇūpacārena. Ukkaṭṭhamajjha muduttamattabhāvo guṇyūpacārena, avayavabhūtā vandanā ekadesūpacārena. Samūhabhūtā vandanā ekadesyūpacārena, pubbapubbakusalacittuppādo samīpūpacārena, pacchimapacchima kusalacittuppādo samīpyūpacārena, paṇāmakusalacittuppādabhūtānaṃ paramatthadhammānaṃ nibyāpārabhāvo taddhammūpacārena. Tesaṃ evaṃ dhammatābhāvo ataddhammūpacārena, vandanākāro abhedabhedūpacārena. Vandanasatti bhedaabhedūpacārena labbhati. Mivibhattiyā vāccaṃ taṃmukhyādīhi lakkhito kattu kriyāvisesena ca vattamānavibhattisaṅkhātena vibhattivise </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',20,replace(replace('<p class="bodytext">sena <a name="M0.0020"></a> ca parassapadasaṅkhātena padavisesena ca uttamapurisa visesena ca amhayogavisesena ca sabbadhātuvisesena ca ekavacanavisesena ca paccuppannakālavisesena ca dhātvatthakriyāvisesena ca aliṅga visesena ca bhūvādigaṇa visesena ca novikaraṇavisesena ca bhāvakammakattūsukattuvisesena ca lakkhito ahaṃsaṅkhāto khandhasaṅkhato santāno appadhānena labbhati. Kriyāpadassa kriyāya padhānena vācakattā dhātvatthova padhānena labbhati. Apaccayassa vāccaṃ bhūvādigaṇajotakā rūpasiddhimattā pada siliṭṭhatā labbhati.</p>\r\n<p class="bodytext">Atha vā yo ahaṃ namāmīti so name. Sāmaññattha viggahoyaṃ. Vāccattho pana vuttanayena veditabbo. Namanañcettha buddhassa sīlādianantaguṇesu ninnapabbhāratāvasena paṇāmakriyābhinipphādikā kusalacetanā. Sā ca vandaneyya vandanakānaṃ khettajjhāsayasampadāhi diṭṭhadhammavedanīyabhūtā yathāladdhasampattinimittakassa purimakammassa anubalappadāna vasena tannibbattitavipākasantatiyā antarāyakarāni upapīḷakaupacchedakakammāni paṭibāhitvā pavattikāle rogādiantarāyānamappavattiṃ sādheti. Samparāye ca upapajjavedanīyabhūtā saggamokkhasampattisukhaṃ nibbattetīti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">dassanatthanayo</span>. Nametiiminā buddhassa sugatādiguṇaṃ ārabbha pavattā tividhā paṇāmakusala cetanā mukhyena dasseti. Taṃ dassanena tassā kāraṇabhūtā yonisomanasikārādayo ca catucakkasampattiñca buddhuppādanavamakhettañca pubbabhāge pavattaṃ saddhāpaññāpadhānaṃ kusalacittuppādañca phalūpacārena dīpeti. Taddīpanena tassā phalabhūtaṃ diṭṭhadhammarogādiantarāyavisosanañca saggamokkha sukhañca paṭhamakāraṇūpacārena pakāseti. Tappakāsanena buddhassa paṇāmārahabhāvaṃ dutiyakāraṇūpacārena </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',21,replace(replace('<p class="bodytext">vibhāveti<a name="M0.0021"></a>. Taṃvibhāvanena vandakassa me khettaṅgatabhāvañca paṇāme tabbabhāvañca tatiyakāraṇūpacārena anubhāveti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">alaṅkāranayo</span>. Nametivācakapadassa vāccabhūtā tividhā buddhapaṇāmakusalacetanā samaṭṭhāyī bhāvo nāma. Kasmā, sarīrattā. Atha vā tividhāya buddhapaṇā makusalacetanāya sampayutto samādhippadhāno mahā kusalacittuppādo samaṭṭhāyībhāvo nāma. Kasmā, buddhassa sugatādiguṇārammaṇe ekaggattā samāhitacittattā vā. Soyeva pana santaraso nāma. Kasmā, buddhapaṇāmacetanā kusalaṅgena akusalaṅgassa pahīnattā. Buddhantipāṭhasesa padassa vāccabhūto buddhadabbo ārammaṇavibhāvo nāma. Kasmā, tassa paṇāmakusalacittuppādassa ārammaṇapaccayena upakārattā. Buddhassa sugatantyādiguṇo uddīpanavibhāvo nāma. Kasmā, paṇāmassa kāraṇattā. Kāraṇabhūtaṃ taṃ samaṭṭhāyībhāvaṃ upādāya jānitabbo diṭṭhadhammarogā diantarāyābhāvo ca samparāye saggamokkhasukhasampatti ca ānubhāvo nāma. Kasmā, ānisaṃsaphalattā.</p>\r\n<p class="bodytext">Tattha hi kiñcāpi ānubhāvadīpakaṃ padaṃ natthi. Kāraṇassa pana phalena vinābhāvato kāraṇassa phalaṃ viññeyyanti. Atha vā nametipadassa sāmatthiyato ānubhāvo dassitoyevāti daṭṭhabbo. Āvibhūto ussuko ca buddhapaṇāmassa ānisaṃsapajānanā mati ca alasābhāvo ca cittappasādo ca samādhityādi ca byabhicārībhāvo nāma. Lomahaṃsādibhāvo satvaṃkābhāvo nāmāti alaṅkāra nayo veditabbo. Evaṃ pacchimapacchimagāthāsūti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Sugataṃ sobhanagataṃ sundaranibbānaṭṭhānaṃ gataṃ vā sammā gataṃ vā, sugataṃ sammā vacanaṃ gadaṃ bhāsanaṃ, seṭṭhaṃ pasatthataraṃ satipaṭṭhānādibhede </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',22,replace(replace('<p class="bodytext">dhamme <a name="M0.0022"></a> gavesantaṃ vā santehi gavesitabbaṃ vā sattānaṃ hitasukhaṃ icchitaṃ vā, kusalaṃkusalaṃ kusalaṃakusalaṃ, jahaṃ jahitaṃ, amataṃ maraṇavirahitaṃ amataṃ amatanibbānasamannā gataṃ, santaṃ kilesadarathavūpasamaṃ, asamaṃ lokiyadhammena asadisaṃ maggaphalanibbānadhammaṃ. Dadaṃ dātaṃ dāyakaṃ vā, saraṇaṃ sabbasattalokassa paṭisaraṇabhūtaṃ. Lokaṃ sattasaṅkha rokāsasaṅkhataṃ tilokaṃ, saraṇaṃ jānantaṃ, araṇaṃ nikkilesaṃ, kilesasaṅkhātaraṇavirahitaṃ vā. Araṇaṃ sattesu kilesarajassābhāvaṃ, karaṃ desanāñāṇena kataṃ, abhayaṃ nibbhayaṃ, abhayaṃ nibbhayaṭṭhānaṃ nibbānadisaṃ, nāyakaṃ satte nibbānapāraṃ nentaṃ, nāyakaṃ tilokānaṃ jeṭṭhaṃ pāmokkhaṃ buddhaṃ sakkaccaṃ tīhi dvārehi ahaṃ name namāmīti.</p>\r\n<p class="centered">Paṭhamavandanagāthādvayavaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Nayanasubhagakāyaṅgaṃ,</p>\r\n<p class="gatha2">Madhuravarasaropetaṃ;</p>\r\n<p class="gatha3">Amitaguṇagaṇādhāraṃ,</p>\r\n<p class="gathalast">Dasabalamatulaṃ vande.</p>\r\n<p class="bodytext"><a name="para2"></a><span class="paranum">2</span>. Evaṃ paṭhamaṃ gāthādvayena buddhassa namassitvā idāni nayanasubhagakāyaṅgantyādīhi pañcahi guṇehi thomitvā buddhaṃ vandituṃ <span class="bld">nayanasubhagakāyaṅga</span>ntiādigāthamāha. Ayaṃ pana na na magaṇayuttattā navakkharehi lakkhitā bhujagasu sugāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘bhujagasusumatā nāmo’’ti.</p>\r\n<p class="bodytext">Ayaṃ panettha yojanā. Yassaṃ paṭipādaṃ nā ca dve nagaṇāmo ca magaṇo ca yadi siyā. Sā bhujagasusu gāthāti matā ñātabbāti. Tattha pana <span class="bld">nayanasubhagakāyaṅga</span>nti sabbasattānaṃ cakkhussa sobhanaṃ gatena kāyaṅgenasamannāgataṃ. </p>','\r',char(13)),'\n',char(10)),'-2-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',23,replace(replace('<p class="bodytext">Ettha <a name="M0.0023"></a> hi nayananti samavisamaṃ dassentaṃ attabhāvaṃ netīti nayanaṃ, cakkhu labbhati. Nīdhātu yu. Subhaṃ sobhanaṃ gacchatīti subhagaṃ, kāyaṅgaṃ labbhati. Subhasaddūpapado gamudhātu kvi. Kāyassa aṅgaṃ avayavaṃ kāyaṅgaṃ. Bhagavato kāyaṅgaṃ labbhati. Chaṭṭhītappurisasamāsoyaṃ. Nayanassa subhagaṃ nayanasubhagaṃ, taṃ kāyaṅgaṃ yassa buddhassāti nayanasubhaga kāyaṅgo, buddho. So hi rūpakāyassa dassanā nuttariyaguṇayogato sabbasattānaṃ pasādacakkhussa sobhanaṃ gatena dvattiṃsamahāpurisalakkhaṇehi ca asītyā nubyañjanalakkhaṇehi ca lakkhitena kāyaṅgena samannāgatoti attho.</p>\r\n<p class="bodytext"><span class="bld">Madhuravarasaropeta</span>nti piyapemanīyauttamasaddena samannāgataṃ, so hi bhagavā madhiyati piyati pemiyati vāti madhuro. Madhadhātu uro. Vuttañhi <span class="bld">majjhimapaṇṇāsaṭīkāyaṃ</span> ‘‘madhuroti piyo pemanīyo apalibuddho’’ti. Atha vā madhiyati assādiyatīti madhuro, madhati sabbasattānaṃ pītiṃ undeti vaḍḍhetīti madhuro, madhadhātu unde uro. Tena vuttaṃ <span class="bld">saddanītiyaṃ</span> ‘‘madha unde’’ti. Varitabbo icchitabbo patthitabbo vāti varo, saro. Varadhātu icchāyaṃ a. Sarati gacchati sotaviññāṇārammaṇabhāvanti saro, saddo. Saradhātu gatiyaṃ a. Sarati gacchati suyyamānatanti vā saro, sariyati suṇiyatīti vā saro, bhagavato aṭṭhaṅgasamannāgato saddo labbhati. Upasampajjatīti upeto, upapubbaidhātu ta. Madhuro ca varo ca so saro cāti madhuravarasaro, tena upeto sampanno samannāgatoti madhuravarasaropetoti vuccati. Bhagavato pana hadaya vatthuno suvisuddhattā pittasemhādīhi apalibuddhattā deva manussehi madhurena piyena manāpiyena varena icchitapatthi tena aṭṭhaṅgasamannāgatena sarena saddena upeto sampanno samannāgatoti vuttaṃ hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',24,replace(replace('<p class="bodytext">Ettha <a name="M0.0024"></a> ca kiñcāpi saropetanti sāmaññavasena vuttaṃ, bhagavato saro pana visaṭṭho ca, viññeyyo, mañju, savanīyo, bindu, avisārī, gambhīro, ninnādi cāti visesena aṭṭhaṅgasamannāgatoti attho daṭṭhabbo. Vuttañhi majjhima paṇṇāse <span class="bld">brahmāyusutte</span> ‘‘aṭṭhaṅgasamannāgato kho panassa gotamassa mukhato ghoso niccharati visaṭṭho ca, viññeyyo ca, mañju ca, savanīyo ca, bindu ca, avisārī ca, gambhīro ca, ninnādi ca. Yathāparisaṃ kho pana so bhavaṃ gotamo sarena viññāpeti, na cassa bahiddhā parisāya ghoso na niccharatī’’ti.</p>\r\n<p class="bodytext">Tassāṭṭhakathāyaṃ pana visaṭṭhoti siniddho apalibuddho. Viññeyyoti viññāpanīyo pākaṭo. Visaṭṭhattāyeva cesa viññeyyo hoti. Mañjūti madhuro. Savanīyoti sotasukho, madhurattāyeva cesa savanīyo hoti. Bindūti sampiṇḍito. Avisārīti avisaṭo. Binduttāyeva cesa avisārī hoti. Gambhīroti gambhīrasamuṭṭhito. Ninnādīti ninnadavā. Gambhīrattāyeva cesa ninnādi hoti. Yathāparisanti cakkavāḷapariyantāpi ekabaddhaṃ parisaṃ viññāpeti. Bahiddhāti aṅgulimattampi parisato bahiddhā na gacchati. Kasmā. So evarūpo madhurasaro akāraṇā mā nassīti. Iti bhagavato ghoso parisāya mattakeneva caratīti vaṇṇitoti. <span class="bld">Abhidhānappadīpikāyañca</span> vuttā saṅgahagāthā –</p>\r\n<p class="gatha1">‘‘Visaṭṭhamañjuviññeyyā, savanīyāvisārino;</p>\r\n<p class="gathalast">Bindugambhīraninnādi, tyevamaṭṭhaṅgiko saro’’ti.</p>\r\n<p class="bodytext">Yathā hi brahmuno saro pittasemhehi apalibuddhattā visuddho madhuro hoti. Yathā ca sīhabyagghādīhi vāḷamigehi anubandhā dhāvantā khuddakamigā karavīkasakuṇassa vikūjamānaṃ saddaṃ sutvā sarassa madhurattā maraṇabhayaṃ hitvā adhāvitvā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',25,replace(replace('<p class="bodytext">ukkhittapādaṃ <a name="M0.0025"></a> anikkhipitvā tiṭṭhantā suṇanti, evaṃ karavīkassa saddo dhammatābhāvena madhuro piyo manāpo hoti. Evaṃ bhagavatā kataṃ kammaṃ vatthuṃ sodheti, vatthussa suddhattā pittasemhehi ca apalibuddhattā bhagavato nābhito samuṭṭhahanto saropi aṭṭhaṅgasamannāgato suvisuddho atimadhuro atipiyo atimanāpo hoti. Sā panesā hīnūpamā hoti, bhagavato hi saro tehi saddehi satabhāgena sahassabhāgena atimadhuro atipiyo atimanāpo hoti, tathā hi dhammāso karañño asandhimittā devī karavīkasakuṇassa madhurasaddaṃ sutvā cintesi ‘‘imassa tiracchānassa madhuro saddo, ko nāma sabbaññutañāṇasiriṃ pattassa bhagavato atimadhuro saddo’’ti, pītiṃ uppādetvā taṃ pītiṃ avijahitvā sattahi jaṅghasatehi saddhiṃ sotāpattiphale patiṭṭhāti, tasmā bhagavato madhuravarasaropetanti thometīti.</p>\r\n<p class="bodytext"><span class="bld">Amitaguṇagaṇādhāra</span>nti aparimiṇitabbasīlādiguṇasamūhānaṃ patiṭṭhānabhūtaṃ. Ettha ca samitoti parimiṇitabboti mito, mādhātu parimāṇe ta, na mito amito. Guṇoti sīlādayo dhammā. Te hi guṇenti taṃsamaṅgīpuggalaṃ pakāsentīti guṇāti vuccanti. Guṇadhātu pakāsane a. <span class="bld">Maṇisāramañjūsāṭīkāyaṃ</span> pana ‘‘guṇanti attano ādhāraṃ pākaṭaṃ karontīti guṇā’’ti vuttaṃ.</p>\r\n<p class="bodytext"><span class="bld">Saddanītidhātumālāyañca</span> ‘‘guṇa āmantane, guṇoti sīlādayo dhammā, kenaṭṭhena te guṇā, goṇāpiyati āmantāpiyati attani patiṭṭhito puggalo daṭṭhuṃ sotuṃ pūjituñca icchantehi janehīti guṇo. Ettha kiñcāpi sīlādi dhammānaṃ āmantāpanaṃ natthi, tathāpi taṃhetu āmantanaṃ nimantanañca teyeva karonti nāmāti evaṃ vuttaṃ. Aññe pana guñjante abyayante iti guṇāti atthaṃ vadanti. Tadanurūpaṃ pana dhātusaddaṃna </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',26,replace(replace('<p class="bodytext">passāma<a name="M0.0026"></a>. Guṇa āmantane icceva passāma. Vicāretvā gahetabba’’nti vuttaṃ. Amito guṇo amitaguṇo, tesaṃ gaṇo samūho amitaguṇagaṇo, tesaṃ. Ādhārati tiṭṭhati etthāti amitaguṇagaṇādhāro, jino labbhati. So pana samuddodakaṃ nāḷipattena aparimitaṃ viya kenaci aparimitānaṃ sīlādiguṇasamūhānaṃ ādhāro patiṭṭhānabhūtoti attho.</p>\r\n<p class="bodytext"><span class="bld">Dasabala</span>nti aññehi asādhāraṇaṃ tathāgatadasabalasampannaṃ. Ettha hi balanti kehici viruddhehi kāraṇe hi balanti na kampenti thirantīti balaṃ, dasa balāni yassa soti dasabalo, buddho. Taṃ pana duvidhaṃ hoti tathā gatassa dasabalaṃ kāyadasabalañca, ñāṇadasabalañcāti. Tesu hi kāyabalaṃ hatthikulānusārena veditabbaṃ. Vuttañhetaṃ <span class="bld">porāṇehi</span> –</p>\r\n<p class="gatha1">‘‘Kāḷā vakañca gaṅgeyyaṃ, paṇḍaraṃ tambapiṅgalaṃ;</p>\r\n<p class="gathalast">Gandha maṅgalahemañca, uposathaṃ chaddantime dasā’’ti.</p>\r\n<p class="bodytext">Imāni hi dasa hatthikulāni. Tattha kāḷāvakanti pakatihatthikulaṃ daṭṭhabbaṃ. Yaṃ dasannaṃ purisānaṃ kāyabalaṃ, taṃ ekassa kāḷāvakahatthino. Yaṃ dasannaṃ kāḷāvakānaṃ balaṃ, taṃ ekassa gaṅgeyyassa. Yaṃ dasannaṃ gaṅgeyyānaṃ, taṃ ekassa paṇḍarassa. Yaṃ dasannaṃ paṇḍarānaṃ, taṃ ekassa tambassa. Yaṃ dasannaṃ tambānaṃ, taṃ ekassa piṅgalassa. Yaṃ dasannaṃ piṅgalānaṃ, taṃ ekassa gandhahatthino. Yaṃ dasannaṃ gandhahatthīnaṃ, taṃ ekassa maṅgalassa. Yaṃ dasannaṃ maṅgalānaṃ, taṃ ekassa hemavatassa. Yaṃ dasannaṃ hemavatānaṃ, taṃ uposathassa. Yaṃ dasannaṃ uposathānaṃ, taṃ ekassa chaddantassa. Yaṃ dasannaṃ chaddantānaṃ, taṃ ekassa tathāgatassa, nārāyanasaṅghātabalantipi idameva vuccati. Tadetaṃ pakatihatthīnaṃ gaṇanāya </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',27,replace(replace('<p class="bodytext">hatthikoṭisahassānaṃ<a name="M0.0027"></a>. Purisagaṇanāya dasannaṃ purisakoṭisahassānaṃ balaṃ hoti. Idaṃ tathāgatassa kāyabalaṃ hoti. Idaṃ pana tāsu tāsu pāḷīsu na āgataṃ. Aṭṭhakathāsuyeva āgataṃ. Tena vuttaṃ dasaka nipāta <span class="bld">aṅguttaraṭīkāyaṃ</span> ‘‘dasa balañāṇaṃ pana pāḷiyaṃ āgatameva. Na kāyabalaṃ viya aṭṭha kathārūḷhamevāti adhippāyo’’ti. Ettha ca nārāyana saṅghāta balanti ettha nārā vuccanti rasmiyo, tā bahū nānāvidhā ito uppajjantīti nārāyanaṃ, vajiraṃ. Tasmā nārāyanasaṅghāta balanti vajirasaṅghātabalanti atthoti.</p>\r\n<p class="bodytext">Ñāṇadasabalaṃ pana vibhaṅgapāḷiyañca mūlapaṇṇāse mahā sīhanādasutte dasakanipātaaṅguttarapāḷiyañca vitthārena āgatameva. Sukhaggahaṇatthaṃ pana saṅkhepena dassayissāmi. Seyyathidaṃ. Ṭhānāṭṭhānañāṇañca, kammasamādānānaṃ vipākañāṇaṃ, sabbatthagāminipaṭipadāñāṇaṃ, anekadhātu nānādhātu loka ñāṇaṃ, sattānaṃ nānādhimuttikataṃ ñāṇaṃ, parasattānaṃ parapuggalānaṃ indriyaparopariyattiñāṇaṃ, jhānavimokkhasamādhisamāpattīnaṃ saṃkilesaṃ vodānaṃ vuṭṭhānaṃ ñāṇaṃ, pubbenivāsānussatiñāṇaṃ, sattānaṃ cutūpapātaṃ ñāṇaṃ, āsavānaṃ khayañāṇañcāti. Ayaṃ pana kamo abhidhammavibhaṅgapāḷiyaṃ āgatakkamo.</p>\r\n<p class="bodytext">Cutūpapātañāṇaṭṭhāne pana mūlapaṇṇāsapāḷiyaṃ mahāsīhanādasutte ca dasakanipātaaṅguttarapāḷiyaṃ dibbacakkhuñāṇaṃ āgataṃ. Atthato pana dibbacakkhuñāṇena cutūpapātassa passitattā sadisamevāti. Tattha hi ṭhānāṭṭhānañāṇanti kāraṇākāraṇaṃ yathābhūtaṃ jānanañāṇaṃ. Kāraṇañhi yasmā tattha phalaṃ tiṭṭhati tadāyattavuttitāya uppajjati ceva pavattati ca, tasmā ṭhānanti vuccati. Kammasamādānānaṃ vipākañāṇanti samādiyitvā katānaṃ atītānāgata paccuppannānaṃ kusalākusalakammānaṃ sugatiduggatipaṭisandhisaṅkhātaṃ vipākaṃ nibbānaphalañca yathābhūtaṃ pajānanañāṇaṃ. Sabbattha </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',28,replace(replace('<p class="bodytext">gāminipaṭipadaṃ <a name="M0.0028"></a> ñāṇanti sabbagatigāminiñca paṭipadaṃ maggaṃ yathābhūtaṃ pajānanañāṇaṃ. Tattha ca paṭipadanti paṭipajjati pāpuṇāti etenāti paṭipadaṃ, maggaṃ. Gatigāmininti nirayādipañcagatigāminiṃ. Agatigāmininti nibbānagāminiṃ. Vuttañhi ‘‘nibbānañcāhaṃ sāriputta pajānāmi nibbānagāminiñca paṭipada’’nti.</p>\r\n<p class="bodytext">Anekadhātunānādhātulokaṃ ñāṇanti ettha aneka dhātūti cakkhudhātuādīhi kāmadhātūhi vā bahudhātuṃ. Nānādhātūti tāsaṃyeva dhātūnaṃ vilakkhaṇattā visadisa sabhāvattā nānāppakāradhātuṃ. Lokanti khandhāyatana dhātulokaṃ. Ñāṇanti yathābhūtaṃ pajānanañāṇaṃ. Sattānaṃ adhimuttikataṃ ñāṇanti sattānaṃ hīnādīhi adhimuttīhi nānādhimuttikabhāvaṃ yathābhūtaṃ pajānanañāṇaṃ. Parasattānaṃ parapuggalānaṃ indriyaparopariyattiñāṇanti ettha parasattānanti padhānasattānaṃ. Parapuggalānanti tato paresaṃ hīnasattānaṃ. Ekatthameva vā etaṃ padadvayaṃ veneyyavasena dvedhā vuttaṃ. Indriyaparopariyattiñāṇanti saddhādīnaṃ indriyānaṃ parabhāvaṃ vaḍḍhañca aparabhāvaṃ hāniñca yathābhūtaṃ pajānanañāṇaṃ.</p>\r\n<p class="bodytext">Jhānavimokkhasamādhisamāpattīnaṃ saṃkilesaṃ vodānaṃ vuṭṭhānaṃ ñāṇanti ettha jhānavimokkhasamādhisamāpattīnanti paṭhamādīnaṃ catunnaṃ jhānānaṃ, rūpī rūpāni passatītiādīnaṃ aṭṭhannaṃ vimokkhānaṃ, savitakkasavicārādīnaṃ tiṇṇaṃ samādhīnaṃ, paṭhamajjhāna samāpattiādīnañca navannaṃ anupubbasamāpattīnaṃ. Saṃkilesanti hānabhāgiyadhammaṃ. Vodānanti visesabhāgiyadhammaṃ. Vuṭṭhānanti yena kāraṇena jhānādīhi vuṭṭhahanti, taṃ kāraṇaṃ. Ñāṇanti yathābhūtaṃ pajānanañāṇaṃ. Pubbenivāsānussatiñāṇanti pubbe nivutthakkhandhānussaraṇasaṅkhātaṃ pajānanañāṇaṃ. Sattānaṃ cutūpapātaṃ ñāṇanti sattānaṃ cutiñca upapātañca pajānanaṃ dibbacakkhuñāṇaṃ. Āsavānaṃ khayañāṇanti kāmāsavādīnaṃ khayasaṅkhātaṃ āsavanirodhaṃ nibbānaṃ yathābhūtaṃ pajānana </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',29,replace(replace('<p class="bodytext">ñāṇanti<a name="M0.0029"></a>. Imāni pana ñāṇāni aññehi asādhāraṇāni tathāgatassa dasabalañāṇānīti daṭṭhabbāni.</p>\r\n<p class="bodytext">Honti cettha –</p>\r\n<p class="gatha1">‘‘Ṭhānāṭṭhānaṃ vipākañca, ñāṇaṃ paṭipadaṃ ñāṇaṃ;</p>\r\n<p class="gathalast">Anekadhātulokañca, sattānaṃ adhimuttikaṃ. [sā panesā setavavipulāgāthāti daṭṭhabbā;]</p>\r\n<p class="gatha1">Indriyaparāparañca, jhānādinaṃ saṃkilesaṃ;</p>\r\n<p class="gatha2">Vodānaṃ vuṭṭhānaṃ ñāṇaṃ, pubbenivāsānussatiṃ;</p>\r\n<p class="gatha3">Sattānaṃ cutūpapātaṃ, āsavakkhayaṃ ñāṇanti;</p>\r\n<p class="gathalast">Dasimāni buddhassa cā, sādhāraṇāni ñāṇānī’’ti. [sā panesā sāmaññagāthāti daṭṭhabbā;]</p>\r\n<p class="bodytext">Nanu dasabalañāṇaṃ nāma pāṭiyekkaṃ natthi, sabbaññutañāṇameva hoti, atha kasmā taṃ ñāṇaṃ dasabhedena vuttanti. Vuccate. Aññameva hi dasabalañāṇaṃ, aññaṃ sabbaññutañāṇaṃ, dasabalañhi sakasakakiccameva jānāti. Sabbaññutañāṇaṃ pana tampi tato avasesampi pajānāti. Dasabalañāṇesu hi paṭhamaṃ kāraṇākāraṇameva jānāti. Dutiyaṃ kammantara vipākantarameva. Tatiyaṃ kammaparicchedameva. Catutthaṃ dhātu nānattakāraṇameva. Pañcamaṃ sattānaṃ ajjhāsayādhimuttimeva. Chaṭṭhaṃ indriyānaṃ tikkhamudubhāvameva. Sattamaṃ jhānādīhi saddhiṃ tesaṃ saṃkilesādimeva. Aṭṭhamaṃ pubbe nivutthakhandhasantatimeva. Navamaṃ sattānaṃ cutipaṭisandhimeva. Dasamaṃ saccaparicchedameva. Sabbaññutañāṇaṃ pana etehi jānitabbañca tato uttariñca pajānāti. Etesaṃ pana kiccaṃ na sabbaṃ karoti. Tañhi jhānaṃ hutvā appetuṃ na sakkoti. Iddhi hutvā vikubbituṃ na sakkoti. Maggo hutvā kilese khepetuṃ na sakkoti. Evaṃ tesaṃ ñāṇadvayānaṃ visesoti. Taṃ pana idha ñāṇadvayaṃ adhippetanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',30,replace(replace('<p class="bodytext"><span class="bld">Atulanti</span> <a name="M0.0030"></a> sīlādīhi guṇehi kenaci puggalena asadisaṃ. Ayaṃ panettha vacanattho. Tulanaṃ ummānaṃ tulā, tula sadisyaṃ. <span class="bld">Saddanītiya</span>ñhi ‘‘tula ummāne’’ti vuttaṃ. Atha vā tuliyati pamiyati etāyāti tulā, pakatitulā. Tulā viyāti tulā, ñāṇaṃ. Tāya sammito tulo. Sammita taddhite apaccayo. Tulasadisañāṇena sammito puggalo. Na tulo atulo. Kammadhārayamissakatappurisa samāsoyaṃ. Sīlādiguṇehi kenaci puggalena asadiso bhagavā labbhati. Atha vā natthi tulo sadiso puggalo etassāti atulo, bhagavā. Atagguṇasaṃviññāṇoyaṃ bahubbīhi. Sīlādiguṇehi kenaci asadiso aggapuggaloti attho. Sadevake loke aggapuggalabhāvato. Yathāha ‘‘yāvatā bhikkhave sattā apadā vā dvipadā vā catuppadā vā, tathāgato tesaṃ aggamakkhāyati, virāgo seṭṭho dhammānaṃ dvipadānañca cakkhumā’’ti. Taṃ atulaṃ buddhanti sambandho. <span class="bld">Vande</span>ti buddhassa nayanasubhagakāyaṅgādiguṇaṃ anussaritvā tīhi dvārehi sakkaccaṃ ahaṃ vandāmīti attho. Ettha ca mivibhattiyā mahāvisayasuttena ekārā desoti daṭṭhabbo. Vandanā pana tividhā hoti kāyavacīmanovandanāvasenāti. Yathāha ‘‘tisso imā bhikkhave vandanā. Katamā tisso. Kāyena vandati, vācāya vandati, manasā vandatī’’ti.</p>\r\n<p class="bodytext">Tattha yoniso manasikāracittaṃ paccupaṭṭhāpetvā jāṇudvayakapparadvayanalāṭasaṅkhātāni pañca aṅgāni bhūmiyaṃ patiṭṭhāpetvā vandaneyyānaṃ abhimukhaṃ nipatanto kāyena vandati nāma. Yaṃ sandhāya ‘‘pañcapatiṭṭhitena vanditvā’’ti aṭṭha kathādīsu vuttaṃ. Buddhaguṇapadāni vācāya pavattento vācāya vandati nāma. Vipassissa namatthu, sikhissāpi namatthu, vessa bhussa namatthu, kakusandhassa namatthu, koṇāgamanassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',31,replace(replace('<p class="bodytext">namatthu<a name="M0.0031"></a>, kassapassa namatthu, aṅgīrassassa namatthu tyādīsupi vācāvandanāyeva. Buddhādi guṇāni anussaranto manasā vandati nāmāti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Nayanasubhagakāyaṅgaṃ sabbasattānaṃ cakkhussa sobhanaṃ gatena kāyaṅgena samannāgataṃ, madhuravarasaropetaṃ piyapemanīyena assādi yena uttamasaddena samannāgataṃ, amitaguṇagaṇādhāraṃ aparimiṇitabbasīlādiguṇasamūhānaṃ patiṭṭhānabhūtaṃ, dasabalaṃ anaññasādhāraṇena tathāgatassa kāyañāṇadasa balena samannāgataṃ, atulaṃ sīlādiguṇehi kenaci asadisaṃ buddhaṃ tīhi dvārehi sakkaccaṃ ahaṃ vande vandāmīti.</p>\r\n<p class="centered">Dutiyavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Yo buddho dhitimāññadhārako,</p>\r\n<p class="gatha2">Saṃsāre anubhosi kāyikaṃ;</p>\r\n<p class="gatha3">Dukkhaṃ cetasikañca lokato,</p>\r\n<p class="gathalast">Taṃ vande naradeva maṅgalaṃ.</p>\r\n<p class="bodytext"><a name="para3"></a><span class="paranum">3</span>. Evaṃ tatiyagāthāya buddhassa vanditvā idāni samādhi ādīhi catūhi guṇehi thomitvā vanditukāmo <span class="bld">yo buddho dhitimāññadhārako</span> tiādigāthamāha. Ayaṃ pana masajagaṇa garuyuttattā dasakkharehi lakkhitā suddhavirā jitagāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span>-msā jgā suddhavirā jitaṃ matanti. Tassattho. Yassaṃ paṭipādaṃ msā masagaṇā ca jgā jagaṇagarukā ca ce siyyuṃ, taṃ vuttaṃ suddhavirājitanti mataṃ ñātabbanti. Dhitimā aññadhārako yo buddho lokato saṃsāre kāyikaṃ dukkhañca cetasikaṃ dukkhañca anubhosi naradevamaṅgalaṃ, taṃ buddhaṃ ahaṃ vandeti sambandho.</p>','\r',char(13)),'\n',char(10)),'-3-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',32,replace(replace('<p class="bodytext">Tattha <a name="M0.0032"></a> hi <span class="bld">yo</span>ti aniyamaniddeso. Yo yādisoti attho. Tassa ca aniyamavācaka sabbanāmapadattā vacanattho na kātabbo. Tassa pana ya oti padavibhāgo kātabbo. Tesu ca yasaddassa vāccaṃ aniyamo buddhadabbo appadhānena labbhati, tassa yādisayuttajaccanāmagottasīlavihārī gocarasaṅkhātaṃ aniyamasāmaññākārattaguṇameva padhānena labbhati. Paṭhamāsivibhattiyā kāriyabhūtassa okārassa vāccaṃ pakatūpanissayasaṅkhātaṃ kattukārakekavacanasaṅkhyaṃ labbhati. Atha vā sabbanāmabhāvaṃ yāti gacchatīti yo, aniyamavācakabhāvaṃ yāti gacchatīti vā yo, sabbanāma bhāvena yāti pavattatīti vā yo, sabbanāmabhāvena yātabbo ñātabboti vā yo, yātabbo ñātabbo etena vā aniyamatthoti yo.</p>\r\n<p class="bodytext">Tenāhu <span class="bld">porāṇā</span> –</p>\r\n<p class="gatha1">‘‘Nānākāraṇaṃ paṭicca, dhātupaccayaviggahaṃ;</p>\r\n<p class="gathalast">Cintakānaṃ pabhāvova, padhānaṃ hoti cintane’’ti.</p>\r\n<p class="bodytext"><span class="bld">Buddho</span>ti paṭividdhacatusaccadhammo. Ayaṃ panettha vacanattho. Bujjhati cattāri saccānīti buddho, bhagavā. Suddhakattusādhanoyaṃ. Budhadhātu ta. Bodhetā pajāyāti vā buddho, hetu kattusādhanoyaṃ. Aññesaṃ cattāri saccāni bodhetīti attho. Atha vā anaññabodhito hutvā savāsanāya sammohaniddāya bujjhati jāgarotīti buddho, dinakarakiraṇa samāgamena paramarucirasirīsobhaggappattiyā vikasitamiva padumaṃ aggamaggañāṇasamāgamena aparimitaguṇagaṇālaṅkata sabbaññutañāṇappattiyā bujjhati vikasatīti vā buddho, nibbāna saṅkhātaṃ ekāyanamaggaṃ bujjhati gacchatīti vā buddho. Budhasaddo hi ñāṇādīsu catūsvatthesu vattati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',33,replace(replace('<p class="bodytext">Tena <a name="M0.0033"></a> vuttaṃ –</p>\r\n<p class="gatha1">‘‘Ñāṇe vikasane ceva, gamane cāpi jāgare;</p>\r\n<p class="gathalast">Catūsvetesu atthesu, budhasaddo pavattatī’’ti.</p>\r\n<p class="bodytext">Atha vā sabbañeyyadhamme bujjhati jānātīti buddho, pārami bhāvitāya paññāya sabbe saṅkhatāsaṅkhatadhamme bujjhati abujjhi bujjhissatīti vā buddho, sammāsambuddho vata so bhagavāti adhigatañāṇaguṇavisesehi bujjhitabboti vā buddho, bhagavatā bujjhitabbaṃ ñātabbanti buddhaṃ. Kiṃ taṃ, sabbaññuta ñāṇaṃ, tadassatthīti buddho, bhagavā. Atthato pana pāramitā bhāvito sayambhūñāṇena saha vāsanāya vihataviddhasta niravasesakleso mahākaruṇāsabbaññutañāṇādiapari meyyaguṇagaṇādhāro khandhasantāno buddho. Yathāha ‘‘buddhoti yo so bhagavā sayambhū anācariyako pubbe ananussutesu dhammesu sāmaṃ saccāni abhisambujjhi, tattha ca sabbaññutaṃ patto, balesu ca vasībhāva’’nti.</p>\r\n<p class="bodytext">Ettha ca buddho nāma sabbaññubuddha, paccekabuddha, catusaccabuddha, suta buddhavasena catubbidho hoti. Tattha kappasatasahassādhikāni cattāri asaṅkhyeyyāni samatiṃsapāramiyo pūretvā sambodhippatto sabbaññubuddho nāma. Kappasatasahassādhikāni dve asaṅkhyeyyāni pāramiyo pūretvā sayambhūpatto paccekabuddho nāma. Avasesā khīṇāsavā catusaccabuddho nāma. Bahussuto sutabuddho nāma. Idha pana sabbaññubuddho adhippeto.</p>\r\n<p class="bodytext"><span class="bld">Dhitimāññadhārako</span>ti ettha dhitimā aññadhārakoti padacchedo kātabbo. Dhitimāti samādhivanto, sampayutta dhamme ekārammaṇe dhāretīti dhiti, samādhi. Dhādhātuti. Ākārassa ikārādeso. Dhiyati ṭhapeti ekārammaṇe sampayuttadhammeti dhiti, cittena yogeneveso ekā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',34,replace(replace('<p class="bodytext">rammaṇe <a name="M0.0034"></a> patiṭṭhāpanavasena cittassa ārammaṇe niccalabhāvena patiṭṭhāpetīti attho. Dhiti assa atthīti dhitimā, buddho. So hi yathā mahāmeru catūhi disāhi bhusaṃ āgatehi vātehi na samiñjati, evaṃ paṭipakkhehi micchāvāda vātehi ca iṭṭhāniṭṭhārammaṇehi ca lābhālābha yasāyasa nindā pasaṃsa sukhadukkhasaṅkhātehi lokadhammehi ca na samiñjati, samādhivantoyeva hotīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Aññadhārako</span>ti arahattaphalahārako. Vimuttisaṅkhātaṃ arahattaphalasetacchattaṃ dhārento vā. Jānāti paṭhama maggena diṭṭhasaccassa āmariyādaṃ anatikkamitvā vāti aññaṃ, arahattaphalaṃ. Āpubbañādhātu kvi. Āsaddo mariyādattho. Saṃyogaparattā ākārassa rasso, taṃ dhāretīti aññadhārako. Aññasaddūpapadadharadhātu ṇvu. <span class="bld">Saṃsāre</span>ti khandhadhātuāyatanānaṃ abbocchinnaṃ pavattamāne saṃsāravaṭṭe.</p>\r\n<p class="bodytext">Vuttañhi <span class="bld">aṭṭhakathāyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Khandhānañca paṭipāṭi, dhātuāyatanāna ca;</p>\r\n<p class="gathalast">Abbocchinnaṃ vattamānā, saṃsāroti pavuccatī’’ti.</p>\r\n<p class="bodytext">Khandhadhātuāyatanānaṃ aññamaññasambandhena saṃpunappunaṃ sarati gacchati pavattatīti saṃsāro. Saṃpubbasaradhātu gatimhi ṇa. Atha vā khandhāyatanadhātuyo saṃ abbocchinnaṃ saranti gacchanti pavattanti etthāti saṃsāroti vacanattho kātabbo, tasmiṃ. <span class="bld">Anubhosī</span>ti anupunappunaṃ abhuñji, avedayīti attho. <span class="bld">Kāyika</span>nti kāye sarīre pavattaṃ kāyikaṃ. <span class="bld">Cetasika</span>nti cetasi pavattaṃ cetasikaṃ. Taṃ pana padadvayaṃ dukkhantipade visesanaṃ. <span class="bld">Dukkha</span>nti vaṭṭadukkhaṃ. Dukkhanaṃ dukkhaṃ, atha vā dukkhayatīti dukkhaṃ, dukkhadhātu a. Idaṃ pana nibbacanaṃ kāritavasena vuttaṃ. Yassa uppajjati, taṃ dukkhitaṃ karotīti vuttaṃ hoti. <span class="bld">Lokato</span>ti sattalokahetu. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',35,replace(replace('<p class="bodytext">Lokiyanti <a name="M0.0035"></a> pavattanti ettha puññāpuññāni tabbipāko cāti loko. Lokadhātu ṇa. Atha vā kusalādīni lāti gaṇhātīti loko. Lādhātu okapaccayo. Satta lokova labbhati. Dhitimā aññadhārako yo buddho saṃsāravaṭṭe nimuggānaṃ sattānamanudayāya saṃsāravaṭṭadukkhato pamocetukāmo kappasatasahassādhikāni cattāri asaṅkhyeyyāni atidukkarāni samatiṃsapāramiyo pañca mahā pariccāge ca karonto sattalokahetu kāyikaṃ cetasikaṃ saṃsāravaṭṭadukkhaṃ anubhavasīti vuttaṃ hoti. <span class="bld">Ta</span>nti tādisaṃ buddhaṃ. <span class="bld">Vande</span>ti vandāmi.</p>\r\n<p class="bodytext"><span class="bld">Naradevamaṅgala</span>nti naradevānaṃ maṅgalabhūtaṃ. Ayaṃ panettha vacanatto. Nirayaṃ neti pāpuṇātīti naro, manusso. Nidhātu pāpunane aro. Ariyamanusso pana bhūtapubbagatika nayena naro nāmāti. Atha vā seṭṭhabhāvaṃ netīti naro, uttamaṃ pabbajjaṃ netīti attho. <span class="bld">Vimānavatthuaṭṭhakathāyaṃ</span> pana jeṭṭhabhāvaṃ netīti naro. Puttabhātubhūtopi hi puggalo mātujeṭṭhabhaginīnaṃ pituṭṭhāne tiṭṭhati, pageva bhattubhūto itarāsanti vuttaṃ. <span class="bld">Visuddhimaggamahāṭīkāyaṃ</span> pana naratīti naro, puriso. Yathā hi paṭhamapakatibhūto satto itarāya pakatiyā seṭṭhaṭṭhena puriucce ṭhāne seti pavattatīti purisoti vuccati, evaṃ nayanaṭṭhena naroti vuccati. Puttabhātu bhūtopi hi puggalo mātujeṭṭhabhaginīnaṃ pituṭhāne tiṭṭhati. Pageva itarā itarāsanti vuttaṃ. Tasmā idha pana naranti sāmaññena vuttampi visesena purisanaranti viññāyati padhānanayena gahaṇassa yuttattā. Dibbanti kāmaguṇa jhānābhiññācittissariyādīhi kīḷantīti devā. Divudhātu ṇa. Atha vā sarīrālaṅkārajutiyā dibbantīti devā. Devā ca nāma tividhā honti sammutidevā, upapattidevā, visuddhidevā cāti. Tattha mahāsammatakālato paṭṭhāya </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',36,replace(replace('<p class="bodytext">lokena <a name="M0.0036"></a> devāti sammatattā rājāno deviyo rājakumārā ca sammutidevā nāma. Devaloke upapannā upapattidevā nāma. Buddhapaccekabuddhakhīṇāsavā visuddhidevā nāma. Idha pana upapattidevā adhippetā.</p>\r\n<p class="bodytext"><span class="bld">Maṅgala</span>nti maṅgalabhūtaṃ. Tantipade visesanaṃ. Maṅganti vaḍḍhenti sabbasampattīhi sattānaṃ etenāti maṅgalo, buddho. Maṅgala dhātu vaḍḍhane alo. Atha vā maṅgaṃ vuccati pāpaṃ, taṃ veneyyānaṃ desanāya lunāti chindatīti maṅgalo, buddho. Maṅgasaddūpapadaludhātu kvi. Narā ca devā ca naradevā, tesaṃ maṅgalo naradevamaṅgalo, buddho. So pana manussadevabrahmānaṃ pūjanīyattā ca saṅkhe, cakkaṃ, puṇṇakumbho, garā, sirī, setacchattaṃ, aṅkuso, dhajaṃ, sovattikañcāti aṭṭhahi lokiyamaṅgalehi seṭṭhattā uttamattā ca maṅgalo nāmāti vuccati, tasmā buddhassa naradevamaṅgalanti thometīti adhippāyoti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Dhitimā samādhivanto aññadhārako arahattaphaladhārako arahattaphalase tacchattaṃ dhārento vā, yo yādiso buddho, saṃsāre saṃsāravaṭṭe, kāyikaṃ kāye pavattaṃ dukkhañca vaṭṭadukkhañca cetasi pavattaṃ dukkhañca vaṭṭadukkhañca, lokato lokahetu, anubhosi punappunaṃ abhavasi avedayi, naradevamaṅgalaṃ naradevānaṃ maṅgalabhūtaṃ, taṃ tādisaṃ buddhaṃ ahaṃ vande vandāmīti.</p>\r\n<p class="centered">Tatiyavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Bāttiṃsatilakkhaṇacitradehaṃ,</p>\r\n<p class="gatha2">Dehajjutiniggatapajjalantaṃ;</p>\r\n<p class="gatha3">Paññādhitisīlaguṇoghavindaṃ,</p>\r\n<p class="hangnum">Vande munimantimajātiyuttaṃ.</p>\r\n<p class="gathalast"></p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',37,replace(replace('<p class="bodytext"><a name="para4"></a><span class="paranum">4</span>. Evaṃ <a name="M0.0037"></a> tatiyagāthāya buddhassa natvā idāni bāttiṃsatilakkhaṇacitradehantyādīhi chahi guṇehi thomitvā munino vanditukāmo <span class="bld">bāttiṃsati lakkhaṇa citra deha</span> ntyādigāthamāha. Ayaṃ pana ta ja ja gaṇa garudvaya yuttattā ekādasakkharehi lakkhitā upaṭṭhitā gāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> tjā jgā garunāyamupaṭṭhitā sā’’ti. Tassattho. Yassaṃ paṭipādaṃ tjā tajagaṇā ca jgājagaṇagarū ca garunā pādantagarunā ce yuttā, sā ayaṃ gāthā upaṭṭhitā nāmāti. Ettha ca eka garuke satipi dvigarukepi lakkhaṇasamānattā upaṭṭhitā gāthā nāmāti daṭṭhabbā. Tattha <span class="bld">bāttiṃsati lakkhaṇa citra deha</span>nti dvattiṃsamahāpurisalakkhaṇehi vicitraṃ sarīravantaṃ. Ettha ca dvīhi adhikaṃ tiṃsaṃ bāttiṃsati. Dvisaddassa bādeso. Lakkhiyati lakkhitabbaṃ buddhasarīraṃ etenāti lakkhaṇaṃ. Dihati upacayati vaḍḍhati vā ettha kusalā kusalanti dehaṃ, sarīraṃ. Dihadhātu upacaye vaḍḍhane vā ṇa. Bāttiṃsati mahāpurisalakkhaṇehi citraṃ vicitraṃ dehaṃ sarīraṃ yassa soti bāttiṃsatilakkhaṇacitradeho, muni labbhati.</p>\r\n<p class="bodytext">Idaṃ panettha dvattiṃsamahāpurisalakkhaṇasarūpassa <span class="bld">saṅkhepa dassanaṃ.</span> Suppatiṭṭhitapādo ca, āyatapaṇhi, dīghaṅguli, muduta luṇahatthapādo, jālahatthapādo, ussaṅkhapādo, eṇijaṅgho, ṭhitakova anonamanto ubhohi pāṇitalehi jāṇu kāni parimasati parimajjati, kosohitavatthaguyho, suvaṇṇa vaṇṇo kañcanasannibhattaco, sukhumacchavī, ekekalomo, uddhaggalomo, brahmujugatto, sattussado, sīhapubbaddha kāyo, citantaraṃso, nigrodhaparimaṇḍalo, samavaṭṭakkhandho, rasaggarasī, sīhahanu, cattālīsadanto, samadanto, aviraḷadanto, susukkadāṭho, pahūtajivho, brahmassaro karavīkabhāṇi, atinīlanetto, gopakhumo, uṇṇā bhamu </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',38,replace(replace('<p class="bodytext">kantare <a name="M0.0038"></a> jāto, uṇhīsasīso cāti dvattiṃsavidhāni honti mahāpurisalakkhaṇāni.</p>\r\n<p class="bodytext">Tena vuttaṃ pātheyyavagge <span class="bld">lakkhaṇasutte</span> ‘‘katamāni tāni bhikkhave dvattiṃsamahāpurisassa mahāpurisalakkhaṇāni, yehi samannāgatassa mahāpurisassa dve gatiyo bhavanti, anaññā. Sace agāraṃ ajjhāvasati rājā hoti cakkavatti…pe… sace khopana agārasmā anagāriyaṃ pabbajati, arahaṃ hoti sammā sambuddho loke vivaṭacchado 1. idha bhikkhave mahā puriso suppatiṭṭhitapādo hoti. Yampi bhikkhave mahā puriso suppatiṭṭhitapādo hoti. Idampi bhikkhave mahā purisassa mahāpurisalakkhaṇaṃ bhavati 2. puna caparaṃ bhikkhave mahāpurisassa heṭṭhāpādatalesu cakkāni jātāni honti sahassārāni sanemikāni sanābhikāni sabbā kāraparipūrāni. Idampi bhikkhave mahāpurisassa mahāpurisa lakkhaṇaṃ bhavati 3. puna caparaṃ bhikkhave mahāpuriso āyatapaṇhī hoti 4. dīghaṅgulī hoti 5. mudutaluṇa hatthapādo hoti 6. jālahatthapādo hoti 7. ussaṅkhapādo hoti 8. eṇijaṅgho hoti 9. ṭhitakova anonamanto ubhohi pāṇitalehi jāṇukāni parimasati parimajjati 10. kosohitavatthaguyho hoti 11. suvaṇṇavaṇṇo kañcanasannibhattaco 12. sukhumacchavī hoti sukhumattā chaviyā rajojallaṃ kāye na upalimpati 13. ekekalomo hoti, ekekāni lomāni lomakūpesu jātāni 14. uddhaggalomo hoti. Uddhaggāni lomāni jātāni nīlāni añjanavaṇṇāni kuṇḍalā vattāni padakkhiṇāvattakajātāni 15. brahmujugatto hoti 16. sattussado hoti 17. sīhapubbaddha kāyo hoti 18. citantaraṃso hoti 19. nigrodhaparimaṇḍalo hoti, yāvatakvassa byāmo, tāva </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',39,replace(replace('<p class="bodytext">takvassa <a name="M0.0039"></a> kāyo 20. samavaṭṭakkhandho hoti 21. rasagga saggī hoti 22. sīhahanu hoti 23. cattālīsadanto hoti 24. samadanto hoti 25. aviraḷadanto hoti 26. susukkadāṭho hoti 27. pahūtajivho hoti 28. brahmassaro hoti karavīkabhāṇi 29. atinīlanetto hoti 30. gopakhumo hoti 31. uṇṇā bhamukantare jātā hoti odātā mudutūla sannibhā. Yampi bhikkhave mahāpurisassa uṇṇā bhamukantare jātā hoti odātā mudutūlasannibhā. Idampi bhikkhave mahāpurisassa mahāpurisalakkhaṇaṃ bhavati 32. puna caparaṃ bhikkhave mahāpuriso uṇhīsasīso hoti. Yampi bhikkhave mahāpuriso uṇhīsasīso hoti. Idampi bhikkhave mahāpurisassa mahāpurisalakkhaṇaṃ bhavati. Imāni kho tāni bhikkhave dvattiṃsamahāpurisassa mahāpurisa lakkhaṇānī’’ti. Etesaṃ pana dvattiṃsamahāpurisalakkhaṇānaṃ attho pacchā āvibhavissatīti.</p>\r\n<p class="bodytext"><span class="bld">Dehajjutiniggatapajjalanta</span>nti bhagavato rūpakāyato nikkhantehi chabbaṇṇaraṃsīsaṅkhātehi obhāsehi parisamantato vijjotavantaṃ. Javati dibbatīti juti. Judhātuti. Nigacchati niccharatīti niggatā, rasmi. Nipubbagamudhātu ta. Pakārena jalate dibbate pajjalantaṃ. Ettha ca dehaniggatajutīti vattabbe chandānurakkhaṇatthaṃ padavipariyāyena dehajjutiniggatanti vuttaṃ. Tasmā dehato niggatā jutīti samāso daṭṭhabbo. Dehato rūpakāyato niggatehi niccharehi jutīhi raṃsīhi pajjalantaṃ yassa soti dehajjuti niggatapajjalanto, muni labbhati. Munino hi puratthimakāyato chabbaṇṇarasmiyo uṭṭhahitvā asītihatthaṭṭhānaṃ agga hesi. Pacchimakāyato dakkhiṇahatthato vāmahatthato chabbaṇṇā rasmiyo uṭṭhahitvā asītihatthaṭṭhānaṃ aggahesi. Upari kesantato paṭṭhāya sabbakesavattehi moragīvavaṇṇā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',40,replace(replace('<p class="bodytext">rasmi <a name="M0.0040"></a> uṭṭhahitvā gaganatale asītihatthaṭṭhānaṃ aggahesi. Heṭṭhāpādatalehi pavāḷavaṇṇā rasmi uṭṭhahitvā ghanapathaviyaṃ asītihatthaṭṭhānaṃ aggahesi, evaṃ samantā asītihatthamattaṃ ṭhānaṃ chabbaṇṇā buddharasmiyo vijjotamānā vipphandamānā kañcanadaṇḍadīpikāhi niccharitvā ākāsaṃ pakkhandajālā viya catuddīpika mahāmeghato nikkhantavijjulatā viya vidhāviṃsūti vuttaṃ hoti. Tasmā dehajjutiniggatapajjalantanti thometīti.</p>\r\n<p class="bodytext"><span class="bld">Paññādhiti sīla guṇoghavinda</span>nti ettha paññāti sabbaññuta ñāṇaṃ. Dhitīti jhānamaggaphalasampayuttasamādhi. Sīlanti pacceka buddhāriyasāvakehi asādhāraṇaṃ suvisuddhaṃ catupārisuddhisīlaṃ. Oghanti samūhaṃ. Vindanti laddhaṃ. Pakārena yathāsabhāvaṃ sabbañeyyadhammaṃ vā jānāti paṭivijjhatīti paññā. Sabbaññuta ñāṇaṃ. Papubbañādhātu a. Sā panesā pajānanalakkhaṇā paṭivedhalakkhaṇā obhāsanalakkhaṇā ca, visayobhāsa rasā, asammohapaccupaṭṭhānā, samādhipadaṭṭhānā. Dhitisaddassa vacanattho heṭṭhā vuttoyeva. Sīlatīti sīlaṃ. Kāyavacīkammāni sammā dahati sammā ṭhapetīti attho. Atha vā sīlati samādhiyati kāyakammādīnaṃ susilya bhāvena na vippakiratīti sīlaṃ, sīlanti samādahanti cittaṃ etenāti vā sīlaṃ, catupārisuddhisīlaṃ. Sīladhātu a. Idaṃ pana patiṭṭhāpanalakkhaṇaṃ, dussilyaviddhaṃsanarasaṃ anavajjarasaṃ vā, soceyyapaccupaṭṭhānaṃ, hiriottappapadaṭṭhānaṃ. Guṇasaddassa vacanattho heṭṭhā vuttoyeva. Uhanati samūhaṃ karoti ettha avayavanti ogho, samūho. Upubbahanadhātu ṇa. Ussa o. Hanassa gho. <span class="bld">Abhidhānappadīpikāṭīkāyaṃ</span> pana ‘‘avayavaṃ byapiyati gacchatīti ogho’’ti vuttaṃ. Vindanaṃlabhanaṃ vindaṃ, vindiyittha labbhitthāti vā vindaṃ, paññā ca dhiti ca sīlañca taṃ guṇañcāti paññādhitisīlaguṇā, tesaṃ ogho samūhoti tathā, taṃ vindaṃ labhanaṃ yassāti paññādhitisīlaguṇoghavindo, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',41,replace(replace('<p class="bodytext">muni<a name="M0.0041"></a>. Atha vā vindaṃ labhitabbaṃ paññādhitisīlaguṇoghaṃ yenāti paññādhitisīlaguṇoghavindo. Tatiyābahubbīhi samāsoyaṃ. Vindānaṃ labhitabbānaṃ paññādhitisīla saṅkhātānaṃ guṇānaṃ samūho atthīti attho. Taṃ.</p>\r\n<p class="bodytext"><span class="bld">Vande</span>ti ahaṃ vandāmi. <span class="bld">Muni</span>nti buddhaṃ. Munāti jānāti paricchindati vā hitāhitanti muni, buddho. Munadhātu ñāṇe i. Atha vā munāti jānāti ubho attheti muni, khandhādi loke tulaṃ āropetvā minanto viya ime ajjhattikā khandhā ime bāhirātiādinā nayena idhalokatthañca paralokatthañca bhagavā jānātīti attho. So panesa agāriyamuni, anāgāriyamuni, sekhamuni, asekhamuni, paccekamuni, munimuni cāti anekavidho. Tattha agāriyamunīti gihipi āgataphalo viññātasāsano. Anāgāriyamunīti tathārūpova pabbajito. Sekhamunīti satta sekkhā. Asekhamunīti khīṇāsavo. Paccekamunīti paccekabuddho. Munimunīti sammāsambuddho. Tesu pana idha munimuni adhippeto, taṃ muniṃ. <span class="bld">Antimajātiyutta</span>nti antimena carimabhavena yuttaṃ. Avasānaṃ amati gacchatīti antaṃ. Amadhātu ta. Massa no. Ante bhavo antimo, carimabhavo. Jānanaṃ nibbattanaṃ jāti, khandhānaṃ pavatti. Kammena janiyati nibbattiyatīti vā jāti. Upapattibhavo labbhati. Jānidhātuti. Dhātvanta lopo. Antimajātiyā yuttoti antimajātiyutto, muni. Āyatiñhi tīsu bhavesu paṭisandhijanakānaṃ sabba kilesānaṃ bodhipallaṅkeyeva aggamaggena samucchinditattā bhagavato anāgate punabbhavo natthi, tasmā ayaṃ ariyajāti pacchimattabhāvayuttoti vuttaṃ hoti.</p>\r\n<p class="bodytext">Tena vuttaṃ <span class="bld">dhammacakkapavattanasutte</span> –</p>\r\n<p class="bodytext">‘‘Ñāṇañca medassanaṃ udapādi, akuppāme vimutti, ayamantimā jāti, natthidāni punabbhavo’’ti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',42,replace(replace('<p class="bodytext">Ayaṃ <a name="M0.0042"></a> panettha <span class="bld">saṅkhepayojanā</span>. Bāttiṃsatilakkhaṇacitraṃ dehaṃ dvattiṃsamahāpurisalakkhaṇehi vicitraṃ rūpakāyavantaṃ, dehajjutiniggata pajjalantaṃ rūpakāyato niggatehi chabbaṇṇaraṃ sīhi parisamantato vijjotavantaṃ, paññādhitisīlaguṇo ghavindaṃ paññādhitisīlasaṅkhatānaṃ guṇānaṃ samūhaṃ labhanavantaṃ, vinditabbānaṃ paññādhitisīlasaṅkhātānaṃ guṇānaṃ samūhavantaṃ vā, antimajātiyuttaṃ carimabhavena carimattabhāvena vā yuttaṃ muniṃ buddhaṃ ahaṃ tīhi dvārehi sakkaccaṃ vande vandāmīti.</p>\r\n<p class="centered">Catutthavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Pātodayaṃ bāladivākaraṃva,</p>\r\n<p class="gatha2">Majjhe yatīnaṃ lalitaṃ sirīhi;</p>\r\n<p class="gatha3">Puṇṇindusaṅkāsamukhaṃ anejaṃ,</p>\r\n<p class="gathalast">Vandāmi sabbaññumahaṃ munindaṃ.</p>\r\n<p class="bodytext"><a name="para5"></a><span class="paranum">5</span>. Evaṃ catutthagāthāya buddhaṃ vanditvā idāni sirīhi lalitantyādīhi catūhi guṇehi thomitvā vanditukāmo <span class="bld">pātodaya</span>ntyādigāthamāha. Ayaṃ pana ta ta ja gaṇadvigarūhi yuttattā ekādasakkharehi lakkhitā indavajirā gāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘indādikā tā vajirā jagāgo’’ti. Tassattho, yassaṃ paṭipādaṃ tā dveta gaṇā ca jagā jagaṇa garu ca go garu ca ce bhavanti, sā indā dikā indasaddaādikā vajirā vajirāgāthā nāmāti.</p>\r\n<p class="bodytext">Tattha <span class="bld">pātodaya</span>nti ettha pāto udayanti padacchedo kātabbo. Page pubbaṇhasamaye pācīnalokadhātuto uggatanti attho. Pāto page pācīnalokadhātuto udeti uggacchatīti pātodayo, sūriyo. Pātopubba udadhātu uggamane ayo, taṃ pātodayaṃ. <span class="bld">Bāladivā </p>','\r',char(13)),'\n',char(10)),'-5-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',43,replace(replace('<p class="bodytext">karaṃ <a name="M0.0043"></a> <span class="bld">vā</span>ti bāladivākaraṃ ivātipadacchedo. Bāladivākaraṃ vāti niggatābhinavasūriyaṃ viya. Ettha ca bāloti sampati jāto taruṇadārako potako vā. So hi balati assasitapassasitamattena jīvati na seṭṭhena paññājīvitenāti bālo, taruṇadārako. Baladhātu pāṇane jīvāyaṃ vā. Sūriyaraṃsinā divati dibbatīti divā. Divudhātu a. Atha vā dīyati khīyati andhakāro etenāti divā. Dīdhātu khaye a, vāgamo. Taṃ karotīti divākaro, divā divase vā karo ābhā yassāti divākaro, sūriyo. Bālasadiso divākaro bāladivākaro. Atha vā bālo iva divākaroti bāladivākaro, navuggatasūriyo. So pana ākāse nakkhattatārānaṃ majjhe sahassaraṃsīhi lalati vilāsati viyāti attho.</p>\r\n<p class="bodytext"><span class="bld">Yatīna</span>nti bhikkhūnaṃ. Te hi yatanti uṭṭhahanti ghaṭanti vāyamanti kusale dhammeti yatīti vuccanti. Yata dhātu patiyatane vāyāme vā i. Atha vā yamanti uparamanti akusale dhammeti yatīti vuccanti. Yamudhātu uparame i. Dhātvantalopo. Tesaṃ yatīnaṃ. <span class="bld">Lalita</span>nti vilāsitaṃ. Lalati vilāsetīti lalito, muni. Laladhātu vilāse ta. Taṃ. <span class="bld">Sirīhī</span>ti bhagavato kāyaṅgasobhaggapattehi sirīhi, bāttiṃsalakkhaṇāsītyā nubyañjanachabbaṇṇaraṃsīsaṅkhatehi sampattisirīhi vā. Katapuññe nissayatīti sirī. Sidhātu sevāyaṃ rapaccayo itthi liṅgajotakaīpaccayo ca. Katapuññehi seviyatīti vā sirī, sampatti. <span class="bld">Mūlaṭīkāyaṃ</span> pana ‘‘sirīti paññāpuññāna metaṃ adhivacana’’nti vuttaṃ. Yatīnaṃ bhikkhūnaṃ majjhe sirīhi lalitaṃ vilāsitanti sambandho. Ettha ca idaṃ vuttaṃ hoti, yathā pāto udito uggato sūriyo ākāse </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',44,replace(replace('<p class="bodytext">nakkhattatārānaṃ <a name="M0.0044"></a> majjhe sahassaraṃsīhi lalati vilāsati, evaṃ muni loke yatīnaṃ bhikkhūnaṃ majjhe bāttiṃsalakkhaṇā sītyānubyañjanachabbaṇṇaraṃsīhi alaṅkatehi sarīrasobhagga pattīhi sampattisirīhi lalati vilāsatīti.</p>\r\n<p class="bodytext"><span class="bld">Puṇṇindusaṅkāsamukha</span>nti puṇṇacandena sadisaṃ ānanasampannaṃ. Yathā hi cando kāḷapakkhe pāṭipadadivasato paṭṭhāya dine dine ūno hoti, evaṃ na munino mukhaṃ ānanaṃ ūnaṃ. Bhagavato pana mukhaṃ niccaṃ paripuṇṇacandamaṇḍalaṃ viya anūnaṃ hutvā sobhati, tasmā puṇṇasaddena visesetvā puṇṇindusaṅkāsamukhanti thometi. Soḷasakalehi pūretīti puṇṇaṃ. Pūradhātu ta, tassa ṇṇo. Idati nakkhattatārānaṃ issariyaṃ karotīti indu, ididhātu issariyaṃ u. Puṇṇaṃ paripuṇṇaṃ indu puṇṇindu, cando. Saṅkāsanti sadisaṃ. Saṅkāsasaddo hi upamāvācako nipāto. Vuttañhi</p>\r\n<p class="bodytext"><span class="bld">Subodhālaṅkāre</span> –</p>\r\n<p class="gatha1">‘‘Ivādi iva vā tulya, samānanibhasannibhā;</p>\r\n<p class="gathalast">Yathā saṅkāsa tulita, ppakāsa patirūpakā’’tyādi.</p>\r\n<p class="bodytext">Puṇṇacandaṃ viya mukhati sobhatīti mukhaṃ, ānanaṃ. Mukhadhātu a. Puṇṇindunā saṅkāsaṃ sadisaṃ mukhaṃ ānanaṃ yassa soti puṇṇindusaṅkāsamukho, munindo. Taṃ. Munindassa pana puṇṇa candassa sahassaraṃsīhi sassiriko viya bhikkhūnaṃ majjhe bāttiṃsa mahāpurisa lakkhaṇāsītyānubyañjana paṭimaṇḍitehi sirīhi ca byāmappabhāparikkhittehi āveḷāveḷā yamakāyamakā gumbāgumbā hutvā viniccharantīhi ghanabuddharasmisaṅkhātehi alaṅkito sassirikamukho hotīti vuttaṃ hoti. Tasmā puṇṇindusaṅkāsamukhanti thometi.</p>\r\n<p class="bodytext"><span class="bld">Aneja</span>nti nittaṇhaṃ. Lābhālābhādiṃ paṭicca ejati kampatīti ejā, taṇhā. Ejadhātu kampane a, natthi ejā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',45,replace(replace('<p class="bodytext">yassāti <a name="M0.0045"></a> anejo, munindo. Vuttañhi <span class="bld">mahāniddesapāḷiyaṃ</span> ‘‘lābhālābha yasāyasa nindāninda sukhadukkhehi na iñjatīti anejo, bhagavā labbhatī’’ti.</p>\r\n<p class="bodytext"><span class="bld">Sabbaññu</span>nti paṭividdha sabbañeyyadhammaṃ. Saṅkharavikāralakkhaṇanibbāna paññattivasena pañcavidhaṃ sabbaṃ ñeyyadhammaṃ jānāti paṭivijjhati sīlenāti sabbaññū, munindo. Sabbasaddūpapada ñādhātu rū. Ettha ca sabbe saṅkhatadhammā saṅkharā nāma sakasakapaccayehi saṅkhariyantīti katvā. Viññattidvayaṃ lahutādittayañca vikāro nāma catūhi mahābhūtehi virūpaṃ kariyatīti katvā. Viññattidvayassa yathākkamaṃ cittajavāyodhātu cittajapathavī dhātūnaṃ visesaguṇattā, lahutādittayassa ca citto tvāhārajanipphannarūpānaṃ visesaguṇattā. Phassādidhammānaṃ phusanādilakkhaṇañca pathavīdhātvādīnaṃ kakkhaḷādilakkhaṇañca, cittassa ārammaṇavijānanalakkhaṇañca, nibbānassa santilakkhaṇañca, rūpassa ruppanalakkhaṇañca, nāmassa namanalakkhaṇañca, aniccatādittayāni ca upacayādicattāri lakkhaṇarūpāni ca lakkhaṇāni nāma lakkhiyanti etena lakkhitabbā dhammāti katvā. Asaṅkhatadhātunibbānaṃ nāma vānaṃ vuccati taṇhā, tato nikkhantanti katvā. Paricchedarūpaṃ itthipurisādighaṭapaṭādayo sabba vohāratthā ca paññatti nāma paññāpiyati pakārena ñāpiyatīti katvā. Ettha ca saṅkhāranibbānāni paramatthadhammā honti. Vikārādīsu pana vikāralakkhaṇāni paññattivisesattā visuṃ vuttāni. Imehi dvīhi vinimuttā pana paricchedarūpā sabbavohāratthā paññattīti vuttāti veditabbā. <span class="bld">Apadāna aṭṭhakathāyaṃ</span> pana ‘‘sabbaṃ atītādibhedaṃ hatthāmalakaṃ viya jānātīti sabbaññū’’ti vuttaṃ.</p>\r\n<p class="bodytext">Sabbaññūti ca kamasabbaññū, sakiṃ sabbaññū, satatasabbaññū, satti sabbaññū, ñātasabbaññū cāti pañcavidhā hoti sabbaññū. Tattha yathākkamaṃ sabbaṃ jānanadhammo kamasabbaññū nāma. Ekāvajjanena </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',46,replace(replace('<p class="bodytext">javanavārena <a name="M0.0046"></a> jānitabbaṃ catusaccadhammaṃ jānanadhammo sakiṃsabbaññū nāma. Cakkhuviññāṇādīnaṃ niyatārammaṇattaṃ jānanadhammo satatasabbaññū nāma. Ekappahārena sabbadhammaṃ jānanasamattho dhammo sattisabbaññū nāma. Āvajjanābhedena jānitabbaṃ sabbadhammaṃ jānanadhammo ñātasabbaññū nāma. Tesu ca sakiṃ sabbaññū sattisabbaññūvasena dve dhammā maggañāṇāniyeva. Tadavasesā sabbaññutañāṇāni honti. Taṃ sabbaññuṃ.</p>\r\n<p class="bodytext"><span class="bld">Muninda</span>nti ettha munisaddassa attho heṭṭhā vuttoyeva. Munīnaṃ indo issaro munindo, taṃ munindaṃ ahaṃ vandāmīti sambandhoti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Pātodayaṃ pageva udayaṃ uggataṃ, bāladivākaraṃva navuggatasūriyamāṇavoiva, yatīnaṃ bhikkhūnaṃ majjhe sirīhi bāttiṃsamahāpurisalakkhaṇā sītyānubyañjanasampattisaṅkhātehi sirīhi lalitaṃ vilāsitaṃ, puṇṇindusaṅkāsamukhaṃ puṇṇacandena sadisaṃ ānanavantaṃ, anejaṃ nittaṇhaṃ, lokadhammehi aniñjanaṃ vā, sabbaññuṃ sabbadhamma vijānantaṃ, munindaṃ buddhaṃ sakkaccaṃ tīhi dvārehi ahaṃ vandāmīti.</p>\r\n<p class="centered">Pañcamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Upetapuñño varabodhimūle,</p>\r\n<p class="gatha2">Sasenamāraṃ sugato jinitvā;</p>\r\n<p class="gatha3">Abojjhi bodhiṃ aruṇodayamhi,</p>\r\n<p class="gathalast">Namāmi taṃ mārajinaṃ abhaṅgaṃ.</p>\r\n<p class="bodytext"><a name="para6"></a><span class="paranum">6</span>. Evaṃ pañcamagāthāya munindaṃ vanditvā idāni mārajinaguṇena thomitvā vanditukāmo <span class="bld">upetapuñño</span> tyādigāthamāha. Ayaṃ pana ja ta ja gaṇa dvigaruyuttattā ekādasakkharehi lakkhitā upendavajirāgāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> </p>','\r',char(13)),'\n',char(10)),'-6-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',47,replace(replace('<p class="bodytext">‘‘upādikā <a name="M0.0047"></a> sāva jatā jagā go’’ti. Tassattho, yassaṃ paṭipādaṃ jatā jagaṇa tagaṇā ca jagā jagaṇagarū ca go garu ca ce siyā, sāva sāgāthāeva upādikā upasaddādikā indavajirāgāthā nāmāti.</p>\r\n<p class="bodytext">Upetapuñño yo sugato varabodhimūle sasena māraṃ jinitvā aruṇodayamhi bodhiṃ abojjhi mārajinaṃ abhaṅgaṃ, taṃ sugataṃ ahaṃ namāmīti sambandho. Tattha <span class="bld">upetapuñño</span>ti sampannadānasīlādipāramīpuññavā. Dānasīlādi pāramīpuññena samannāgato vā. Dānasīlādipāramīpuññena upeto samupeto upagato samupagato sampanno samannāgatoti attho. Ayaṃ panettha vacanattho, upanno sampanno hutvā eti gacchati pavattatīti upetaṃ, puññaṃ. Upapubbaidhātu gatiyaṃ ta. Punāti attano kārakanti puññaṃ, pāramīpuññaṃ labbhati. Pudhātu pīṇane sodhane vā ṇyo. Tassa ño. Tattha attano kārakanti punappunaṃ pavattiyā sacittasantatiñca saṃjanitapaṇītarūpavisaṭena sanissaya rūpasantatiñca punāti pīṇeti sodheti vāti attho. Ayaṃ <span class="bld">paṭisambhidāmaggagaṇṭhipade</span> vutto. Pūreti ajjhāsayanti vā puññaṃ. Pūradhātu ṇya. Yo puggalo puññaṃ karoti tassa ajjhāsayaṃ pūretīti attho.</p>\r\n<p class="bodytext">Atha vā pujjañca bhavaṃ nibbattetīti puññaṃ. Nibbattitaddhitāyaṃ. Pūja padaṃ ṇyapaccayo. Yo puññaṃ karoti, tassa pūjitabbaṃ bhavaṃ pavattetīti attho. Tena vuttaṃ <span class="bld">sammohavinodanī aṭṭhakathāyaṃ</span> ‘‘punāti attano kārakaṃ pūreti cassa ajjhāsayaṃ pujjañca bhavaṃ nibbattetīti puñño’’ti. <span class="bld">Mūlaṭīkāyañca</span> ‘‘punātīti sodheti apuññaphalato dukkhasaṃkilesato ca hitajjhāsayena puññaṃ karotīti taṃ nipphādanena kārakassajjhāsayaṃ pūretīti puñño. Pūrako pujjanibbattako ca niruttilakkhaṇena puññoti veditabbo’’ti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',48,replace(replace('<p class="bodytext">vuttaṃ<a name="M0.0048"></a>. <span class="bld">Visuddhimaggamahāṭīkāyañca</span>- ‘‘punāti attano santānaṃ apuññaphalato dukkhasaṃkilesato ca visodhetīti puññaṃ. Hitasukhajjhāsayena puññaṃ karotīti taṃnipphādanena kārakassa ajjhāsayaṃ pūreti, pujjabhavaṃ nibbattetīti vā puññaṃ. Iti pūrako pujjanibbattako ca niruttinayena puññanti veditabbo’’ti vuttaṃ. Upetaṃ sampannaṃ puññaṃ pāramīpuññaṃ yassa soti upetapuñño, sugato labbhati.</p>\r\n<p class="bodytext"><span class="bld">Varabodhimūle</span>ti uttamassa bodhirukkhassa samīpe. Tattha <span class="bld">varo</span>ti varitabbo patthitabbo icchitabboti vā varo. <span class="bld">Bodhimūle</span>ti ettha bodhīti vuccati rukkhopi, maggopi, sabbaññutañāṇampi, nibbānampi. Tattha hi bodhirukkhamūle paṭhamābhi sambuddhoti ca, antarā ca bodhiṃ antarā ca gayanti ca āgataṭṭhāne rukkho. Bodhīti vuccati catūsu maggesu ñāṇanti āgataṭṭhāne maggo. Pappoti bodhiṃ varabhūri medhasoti āgataṭṭhāne sabbaññutañāṇaṃ. Patvāna bodhiṃ amataṃ asaṅkhatanti āgataṭṭhāne nibbānaṃ bodhīti vuccati. Idha pana bodhirukkhoti adhippeto. Ayaṃ pana tesaṃ vacanattho. Bujjhati abujjhi bujjhissati cattāri ariyasaccāni sabbaṃ ñeyyadhammampi etthāti bodhi, bodhirukkho. Budhadhātu ṇi. Bujjhati cattāri saccānīti bodhi, maggo. Bujjhati sabbañeyyadhammanti bodhi, sabbaññutañāṇaṃ. Ariyena bujjhitabbanti bodhi, nibbānaṃ labbhati. Varo uttamo bodhirukkhoti varabodhi, tassa mūlaṃ samīpaṃ varabodhimūlaṃ, tasmiṃ.</p>\r\n<p class="bodytext"><span class="bld">Sasenamāra</span>nti saha senāya pavattaṃ mārarājaṃ. Tattha siyati bandhiyatīti senā. Sidhātu bandhaneyu. Tāya saha pavattatīti saseno, mārarājā. Anatthe niyojento guṇamāraṇena satte māretīti māro. Maradhātu ṇa. Sattānaṃ kusalaṃ māreti vināsetīti vā māro. Saseno ca so māro cāti sasenamāro, mārarājā. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',49,replace(replace('<p class="bodytext">Taṃ<a name="M0.0049"></a>. Jinitvāti pade kammaṃ. Upetapuñño yo sugatoti sambandho. Sugatasaddassa attho heṭṭhā vuttoyeva.</p>\r\n<p class="bodytext"><span class="bld">Jinitvā</span>ti visākhapuṇṇamiyaṃ buddhadine anatthaṅgateyeva sūriye pāramīpuññatejena vijayitvāti attho. <span class="bld">Abojjhī</span>ti paṭivijjhi adhigami vā. <span class="bld">Bodhi</span>nti anuttaraṃ sammā sambodhiṃ, arahattamaggasabbaññutañāṇanti attho. Sabbaññuta ñāṇapadaṭṭhānañhi maggañāṇaṃ, maggañāṇapadaṭṭhānañca sabbaññutañāṇaṃ bodhīti vuccati. Taṃ bodhiṃ.</p>\r\n<p class="bodytext"><span class="bld">Aruṇodayamhī</span>ti aruṇaudayamhīti padacchedo. Aruṇuggamanavelāyanti attho. Tattha āsamantato ālokaṃ karonto unati gacchatīti aruṇo. Āpubbaunadhātu gatimhi ṇa. Ākārassa rasso. Rakārāgamo. Nassa ṇo. Sūriyassa udayato pubbe uggataraṃsi aruṇo nāmāti attho. Vuttañhi dasakanipāta <span class="bld">aṅguttarapāḷiyaṃ</span> ‘‘sūriyassa bhikkhave udayato etaṃ pubbaṅgamaṃ etaṃ pubbanimittaṃ yadidaṃ aruṇuggaṃ, evameva kho bhikkhave kusalānaṃ dhammānaṃ etaṃ pubbaṅgamaṃ etaṃ pubbanimittaṃ yadidaṃ sammādiṭṭhī’’ti. Yaṃ sandhāya vuttaṃ <span class="bld">abhidhānappadīpikāyaṃ</span> ‘‘sūrassodayato pubbu, ṭṭhitaraṃsi siyāruṇo’’ti. <span class="bld">Abhidhānappadīpikāṭīkāyaṃ</span> pana ‘‘aruṇavaṇṇatāya arati gacchatīti aruṇo’’ti vuttaṃ. Udeti uggacchatīti udayo, sūriyo. Udidhātu a. Udayassa sūriyassa aruṇo uggataraṃsīti aruṇodayo. Tassaṃ aruṇuggamanavelāyaṃ. Bodhiṃ abojjhīti sambandho.</p>\r\n<p class="bodytext">Bhagavā hi visākhapuṇṇamiyaṃ anatthaṅgateyeva sūriye mārabalaṃ vidhametvā cīvarūpari patamānehi bodhirukkhaṅkarehi rattapavāḷapallavehi viya pūjayamāno rattiyā paṭhamayāme pubbenivāsaṃ anussaritvā majjhimayāme dibbacakkhuṃ visodhetvā pacchimayāme paṭiccasamuppādaṃ anulomapaṭilomaṃ manasikaritvā ānāpānacatutthajjhānaṃ nibbattetvā tadeva pādakaṃ katvā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',50,replace(replace('<p class="bodytext">mahāvajirañāṇasaṅkhātaṃ <a name="M0.0050"></a> vipassanaṃ vaḍḍhetvā maggapaṭipāṭiyā adhigatena catutthamaggañāṇena sabbakilese khepetvā yadā aruṇo uggamissati, tadā sabbaññutañāṇādisabba buddhaguṇe paṭivijjhi adhigamīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Mārajina</span>nti pañcamārānaṃ vijitāviṃ. Devaputta kilesābhi saṅkhārakhandhamaccusaṅkhāte pañcamāre jināti ajini jinissatīti mārajino, sugato. Devaputtakilesābhisaṅkharasaṅkhataṃ mārattayaṃ jināti ajini, khandhamaccusaṅkhataṃ māradvayaṃ avassaṃ jinissatīti attho. Yadi evaṃ kasmā vijitāvinti vuttanti. Aviraddhaṃ ekantena jiyamānattāti. <span class="bld">Abhaṅga</span>nti sasenamārassa bhañjituṃ viddhaṃsituṃ asamatthaṃ. Yassa kassaci bhañjituṃ viddhaṃsituṃ asamatthaṃ vā. Tatrāyaṃ vacanattho. Mārena yena kenaci bhañjituṃ na sakkotīti abhaṅgo, tena vā na bhañjiyati na viddhaṃsiyatīti abhaṅgo, sugato. Napubba bhanjadhātu ta. Tassa dhātvantena gakārādeso. Atha vā bhanjati viddhaṃsatīti bhaṅgo, māro. Yo koci vā. Natthi bhaṅgo māro yo koci vā yassāti abhaṅgo, sugato. Taṃ abhaṅgaṃ. <span class="bld">Ta</span>nti sugataṃ. Mārajinaṃ abhaṅgaṃ taṃ sugataṃ ahaṃ namāmīti sambandho.</p>\r\n<p class="bodytext">Imissaṃ pana gāthāyaṃ <span class="bld">ekanipātajātakaṭṭhakathāyaṃ, apadānaṭṭhakathāyañca</span> āgatanayena buddhassa māravijayādiṃ sotujanānaṃ bahussutatthaṃ saṅkhepena veditabbaṃ. Bodhi satto hi mahāsampattiṃ anubhavitvā hatthagataṃ cakkavattirajjaṃ kheḷapiṇḍaṃ viya anapekkho chaṭṭetvā ekūnatiṃsavasse āsaḷhipuṇṇamāya uttarasaḷhanakkhattena yutte mahābhinikkhamaṃ nikkhamitvā anomanadītīre ghaṭīkārabrahmunā dinnaṃ arahatta dhajabhūtaṃ aṭṭhaparikkhāraṃ paṭiggahetvā nivāsetvā samaṇapabbajjaṃ katvā chabbassāni dukkarakārikaṃ caritvā nigrodharukkhamūle senanigame senakuṭumbikassa dhītarāya sujātāya saha </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',51,replace(replace('<p class="bodytext">satasahassagghiyā <a name="M0.0051"></a> suvaṇṇapātiyā dinnaṃ sabbaṃ appodakaṃ madhupāyāsaṃ ekaṭṭhitālapakkappamāṇaṃ ekūnapaññāsālopaṃ paribhuñjitvā sāyanhasamaye devatāhi alaṅkatena maggena bodhirukkhābhimukho gacchante antaramagge sotthiyena nāma tiṇahārakena dinnaṃ aṭṭhamuṭṭhikatiṇaṃ gahetvā bodhimaṇḍaṃ āruyha idaṃ ṭhānaṃ sabbabuddhānaṃ avijahitaṃ acalaṭṭhānaṃ kilesabhañjanaṭṭhānanti ñatvā tāni tiṇāni agge gahetvā cālesi. Tāvadeva cuddasahattho pallaṅko ahosi. Tānipi tiṇāni tathārūpena saṇṭhānena santhariṃsu saṇṭhahiṃsu vā. Yathārūpaṃ sukusalopi vicittakāro vā potthatāro vā ālikhitumpi samattho natthi. Bodhi satto bodhikhandhaṃ piṭṭhito katvā puratthābhimukho daḷhamānaso hutvā –</p>\r\n<p class="gatha1">‘‘Kāmaṃ taco ca nhāru ca, aṭṭhi ca avasussatu;</p>\r\n<p class="gathalast">Upasussatu nisesaṃ, sarīre maṃsalohita’’nti.</p>\r\n<p class="bodytext">Na tveva sammāsambodhiṃ apatvā imaṃ pallaṅkaṃ bhindissāmīti asanisatasannipātenapi abhejjarūpaṃ taṃ pallaṅkaṃ abhañjitvā nisīdi. Tasmiṃ samaye māro devaputto siddhatthakumāro ‘‘mayhaṃ vasaṃ atikkamitukāmo, na dāni assa atikkamituṃ dassāmī’’ti mārabalassa santikaṃ gantvā etamatthaṃ ārocetvā māraghosanaṃ nāma ghosāpetvā mārabalaṃ ādāya nikkhami. Sā mārasenā mārassa purato dvādasa yojanā honti. Dakkhiṇato ca vāmato ca dvādasa yojanā. Pacchato cakkavāḷapariyantaṃ katvā ṭhitā, uddhaṃ navayojanubbedhā. Yassā unnādantiyā unnādasaddo yojanasahassato paṭṭhāya pathavīundriyasaddo viya suyyati. Atha māro devaputto diyaḍḍhayojanasatikaṃ girimekhalaṃ nāma hatthiṃ abhirūhitvā bāhusahassaṃ māpetvā nānāvudhāni aggahesi. Avasesāyapi māraparisāya dve janā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',52,replace(replace('<p class="bodytext">ekasadisaṃ <a name="M0.0052"></a> āvudhaṃ na gaṇhiṃsu. Nānappakāravaṇṇā nānappakāra mukhā hutvā mahāsattaṃ ajjhottharamānā āgamaṃsu.</p>\r\n<p class="bodytext">Dasasahassacakkavāḷadevatā pana mahāsattassa thuti yo vadamānā aṭṭhaṃsu. Sakko devarājā vijayuttarasaṅkhaṃ dhamamāno aṭṭhāsi. So kira saṅkho vīsahatthasatiko hoti. Sakiṃ vātaṃ gāhāpetvā dhamanto cattāro māse saddaṃ karitvā nissaddo hoti. Mahākāḷanāga rājā atirekapadasatena vaṇṇaṃ vadanto aṭṭhāsi. Mahābrahmā setacchattaṃ dhārayamāno aṭṭhāsi. Māra bale pana bodhimaṇḍalaṃ upasaṅkamante tesaṃ ekopi ṭhātuṃ nāsakkhi. Sammukha sammukhaṭṭhāneneva palāyiṃsu. Kāḷo nāma nāgarājā pathaviṃ nimujjitvā pañcayojanasatikaṃ mantirakanāgabhavanaṃ gantvā ubhohi hatthehi mukhaṃ pidahitvā nipanno. Sakko vijayuttarasaṅkhaṃ piṭṭhiyaṃ katvā cakkavāḷamukha vaṭṭiyaṃ aṭṭhāsi. Mahābrahmā setacchattaṃ koṭiyaṃ gahetvā brahmalokameva agamāsi. Ekadevatāpi ṭhātuṃ samattho nāma nāhosi. Mahāpuriso ekakova nisīdi. Māropi attano parisaṃ āha ‘‘tāta suddhodanaputtena siddhatthena sadiso añño puriso nāma natthi, mayaṃ sammukhā yuddhaṃ dātuṃ na sakkhissāma, pacchā bhāgena dassāmā’’ti. Mahāpurisopi tīṇi passāni oloketvā sabbadevatānaṃ palāyitattā suññāni addasa. Puna uttarapassena mārabalaṃ ajjhottharamānaṃ disvā ‘‘ayaṃ ettako jano maṃ ekakaṃ sandhāya mahantaṃ vāyāmaṃ karoti, imasmiṃ ṭhāne mayhaṃ mātā vā pitā vā bhātā vā añño koci ñātako vā natthi, imā pana dasa pāramiyova mayhaṃ dīgharattaṃ puttaparijanasadisā, tasmā mayā pāramiyova phalakaṃ katvā pāramisattheneva paharitvā ayaṃ balakāyo mayā viddhaṃsetuṃ vaṭṭatī’’ti dasa pāramiyo āvajjamāno nisīdi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',53,replace(replace('<p class="bodytext">Atha <a name="M0.0053"></a> māro devaputto eteneva siddhatthaṃ palāpessāmīti vātamaṇḍalaṃ samuṭṭhāpesi. Taṅkhaṇaññeyeva puratthimā dibhedā vātā samuṭṭhahitvā aḍḍhayojanaekayojana dviyojanatiyojanappamāṇāni pabbatakūṭāni padāletvā vanagaccharukkhādīni uddhaṃ mūlāni katvā sāmantā gāmanigame cuṇṇavicuṇṇaṃ kātuṃ samatthāpi mahāpurisassa puññatejena mahānubhāvena bodhisattaṃ patvā cīvarakaṇṇamattampi cāletuṃ nāsakkhiṃsu. Tato udakena ajjhottharitvā māressāmāti mahāvassaṃ samuṭṭhāpesi. Tassānubhāvena uparūpari satapaṭalasahassapaṭalādibhedā vaḷāhakā uṭṭhahitvā vassiṃsu. Vuṭṭhidhārāvegena pathavī bhinnā ahosi. Vanarukkhādīnaṃ uparibhāgena mahāmegho āgantvā mahā sattassa cīvare ussāvabinduggahaṇamattampi temetuṃ nāsakkhi. Tato pāsāṇavassaṃ samuṭṭhāpesi. Mahantāni pabbatakūṭāni dhūmāyantāni pajjalantāni ākāsenāgantvā bodhisattassa pādamūle dibbamālāguḷabhāvaṃ āpajjiṃsu. Tato paharavassaṃ samuṭṭhāpesi. Ekatodhārā ubhatodhārā asisattikhurappādayo dhūmāyantā pajjalantā ākāsenāgantvā bodhisattaṃ patvā dibbapupphāni ahesuṃ. Tato aṅgāravassaṃ samuṭṭhāpesi. Kiṃsukavaṇṇā aṅgārā ākāsenāgantvā bodhisattassa pādamūle dibba pupphāni hutvā vikiriṃsu. Tato kukkuḷavassaṃ samuṭṭhāpesi. Accuṇho aggivaṇṇo kukkuḷo ākāsenāgantvā bodhisattassa pādamūle dibbacandanacuṇṇaṃ hutvā nipatati. Tato vāḷukavassaṃ samuṭṭhāpesi. Atisukhumavāḷikā dhūmāyantā pajjalantā ākāsenāgantvā bodhisattassa pādamūle dibbapupphāni hutvā nipatiṃsu. Tato kalalavassaṃ samuṭṭhāpesi. Taṃ kalalavassaṃ dhūmāyantaṃ pajjalantaṃ ākāse nāgantvā bodhisattassa pādamūle dibbavilepanaṃ hutvā patati. Tato iminā bhiṃsetvā siddhatthaṃ palāpessāmīti andhakāraṃ <a name="M0.0054"></a> samuṭṭhāpesi. Taṃ caturaṅgasamannāgataṃ andhakāraṃ viya mahātamaṃ hutvā bodhisattaṃ patvā sūriyappabhā vihataṃ viya antaradhāyati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',54,replace(replace('<p class="bodytext">andhakāraṃ <a name="M0.0054"></a> samuṭṭhāpesi. Taṃ caturaṅgasamannāgataṃ andhakāraṃ viya mahātamaṃ hutvā bodhisattaṃ patvā sūriyappabhā vihataṃ viya antaradhāyati.</p>\r\n<p class="bodytext">Evaṃ māro imāhi navahi vātavassapāsāṇapaharaaṅgāra kukkuḷavāḷuka kalalaandhakāravuṭṭhīhi bodhisattaṃ palāpetuṃ asakkonto ‘‘kiṃ bhaṇe tiṭṭhatha imaṃ siddhatthakumāraṃ gaṇhatha hanatha palāpethā’’ti parisaṃ āṇāpetvā sayampi girimekhalassa hatthino khandhe nisinno cakkāvudhaṃ ādāya bodhisattaṃ upasaṅkamitvā ‘‘siddhattha uṭṭhāhi etasmā pallaṅkā, nāyaṃ tumhākaṃ pāpuṇāti, mayhaṃeva pāpuṇātī’’ti āha. Mahāsatto tassa vacanaṃ sutvā avoca ‘‘māra neva tayā dasapāramiyo pūritā, na upapāramiyo na paramattha pāramiyo nāpi pañcamahāpariccāgā pariccattā na ñātatthacariyā na lokatthacariyā na buddhatthacariyā pūritā, sabbā tā mayāyeva pūritā, tasmā nāyaṃ pallaṅko tuyhaṃ pāpuṇāti, mayhameva pāpuṇātī’’ti. Māro kuddho kodhavegaṃ asahanto mahāpurisassa cakkāvudhaṃ vissajjesi. Taṃ tassa dasapāramiyo āvajjantassa uparibhāge mālāvitānaṃ hutvā aṭṭhāsi. Taṃ kira khuracakkāvudhaṃ aññadā tena kuddhena vissaṭṭhaṃ ekaghanapāsāṇa thambhe vaṃsakaḷīre viya chindantaṃ gacchati. Idāni pana tasmiṃ mālāvitānaṃ hutvā ṭhite avasesā māraparisā idāni pallaṅkato vuṭṭhāya palāyissatīti mahantamahantāni selakūṭāni vissajjesuṃ. Tānipi mahāpurisassa pāramiyo āvajjantassa mālāguḷabhāvaṃ āpajjitvā bhūmiyaṃ patiṃsu.</p>\r\n<p class="bodytext">Devatā cakkavāḷamukhavaṭṭiyaṃ ṭhitā gīvaṃ pasāretvā sīsaṃ ukkhipitvā ‘‘naṭṭho vata bho siddhatthakumārassa rūpaggappatto attabhāvo, kiṃ nu kho karissatī’’ti olokenti. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',55,replace(replace('<p class="bodytext">Tato <a name="M0.0055"></a> mahāpuriso pūritapāramīnaṃ bodhisattānaṃ abhi sambujjhanadivase pattapallaṅko mayhaṃva pāpuṇātīti vatvā ṭhitaṃ māraṃ āha ‘‘māra tuyhaṃ dānassa dinnabhāve ko sakkhī’’ti. Māro ‘‘ettakā janā sakkhino’’ti māra balābhimukhaṃ hatthaṃ pasāreti. Tasmiṃ khaṇe māraparisāya ‘‘ahaṃ sakkhī ahaṃ sakkhī’’ti pavattasaddo pathavīundriyasaddasadiso ahosi. Atha kho māro mahāpurisaṃ āha ‘‘siddhattha tuyhaṃ dānassa dinnabhāve ko sakkhī’’ti. Mahāpuriso ‘‘tuyhaṃ tāva dānassa dinnabhāve sacetanā sakkhino. Mayhaṃ pana imasmiṃ ṭhāne sacetano koci sakkhī nāma natthi. Tiṭṭhatu tāva me avasesattabhāvesu dinnadānaṃ, vessantarattabhāve pana ṭhatvā mayhaṃ sattasattaka mahādānassa dinnabhāve acetanāpi ghanamahāpathavī sakkhī’’ti cīvaragabbhantarato dakkhiṇahatthaṃ nīharitvā vessantarattabhāve mayhaṃ sattasattakamahādānassa dinnabhāve tvaṃ sakkhi na sakkhīti mahāpathaviyābhimukhaṃ hatthaṃ pasāresi. Mahā pathavī ‘‘ahaṃ te dānassa sakkhī’’ti viravasahassena viravasatasahassena mārabalaṃ avattharamānā viya unnadi.</p>\r\n<p class="bodytext">Tato mahāpurise dinnaṃ te siddhattha mahādānaṃ uttama dānanti vessantaradānaṃ sammasante diyaḍḍhayojanasatiko girimekhalo hatthī jāṇukehi patiṭṭhāsi. Māraparisā disāvidisā palāyiṃsu. Dve ekamaggena gatā nāma natthi. Sīsābharaṇāni ceva vatthābharaṇāni ca pahāya sammukha sammukhaṭṭhāne disāvidisāhiyeva palāyiṃsu. Tato devasaṅgho palāyamānaṃ mārabalaṃ disvā mārassa parājayo jāto. Siddhatthakumārassa jayo. Jayapūjaṃ karissāmāti nāgā nāgānaṃ, supaṇṇā supaṇṇānaṃ, devatā devatānaṃ, brahmāno brahmānaṃ pesetvā gandhamālādi hatthā mahāpurisassa santikaṃ bodhipallaṅkaṃ āgamaṃsu. Evaṃ gatesu ca panetesu –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',56,replace(replace('<p class="gatha1">‘‘Jayo <a name="M0.0056"></a> hi buddhassa sirīmato ayaṃ,</p>\r\n<p class="gatha2">Mārassa ca pāpimato parājayo;</p>\r\n<p class="gatha3">Ugghosisuṃ bodhimaṇḍe pamoditā,</p>\r\n<p class="gathalast">Jayaṃ tadā nāgagaṇā mahesino.</p>\r\n<p class="gatha1">Jayo hi buddhassa sirīmato ayaṃ,</p>\r\n<p class="gatha2">Mārassa ca pāpimato parājayo;</p>\r\n<p class="gatha3">Ugghosisuṃ bodhimaṇḍe pamoditā,</p>\r\n<p class="gathalast">Jayaṃ tadā supaṇṇasaṅghā mahesino.</p>\r\n<p class="gatha1">Jayo hi buddhassa sirīmato ayaṃ,</p>\r\n<p class="gatha2">Mārassa ca pāpimato parājayo;</p>\r\n<p class="gatha3">Ugghosisuṃ bodhimaṇḍe pamoditā,</p>\r\n<p class="gathalast">Jayaṃ tadā devagaṇā mahesino.</p>\r\n<p class="gatha1">Jayo hi buddhassa sirīmato ayaṃ,</p>\r\n<p class="gatha2">Mārassa ca pāpimato parājayo;</p>\r\n<p class="gatha3">Ugghosisuṃ bodhimaṇḍe pamoditā,</p>\r\n<p class="gathalast">Jayaṃ tadā brahmagaṇāpi tādino’’ti.</p>\r\n<p class="bodytext">Avasesesu dasasu cakkavāḷasahassesu devatā mālāgandhavilepanehi ca pūjayamānā nānappakāra thutiyo ca vadamānā aṭṭhaṃsu. Evaṃ anatthaṅgateyeva sūriye mahāpuriso mārabalaṃ vidhametvā cīvarūpari pata mānehi bodhirukkhaṅkurehi rattapavāḷapallavehi viya pūjayamāno paṭhamayāme pubbenivāsañāṇaṃ anussaritvā majjhimayāme dibbacakkhuṃ visodhetvā pacchimayāme paṭicca samuppāde ñāṇaṃ otāresi. Athassa dvādasapadikaṃ paccayā kāraṃ vaṭṭavivaṭṭavasena anulomato paṭilomato sammasantassa dasasahassilokadhātu udakapariyantaṃ katvā dvādasakkhattuṃ sampakampi. Mahāpurisena pana dasasahassi lokadhātuṃ unnādetvā aruṇuggamanavelāyaṃ sabbaññuta </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',57,replace(replace('<p class="bodytext">ñāṇaṃ <a name="M0.0057"></a> paṭivijjhīti vuttaṃ hoti. Idha pana vuttāvasesaṃ tāsu ca oloketvā gahetabbanti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Upetapuñño sampanna dānādipāramīpuññavā dānādipāramīpuññena sampanno vā, yo sugato varabodhimūle uttamassa bodhirukkhassa samīpe, sasenamāraṃ senāya saha pavattaṃ mārarājaṃ, jinitvā pāramīpuññatejena vijayitvā, aruṇodayamhi visākhapuṇṇamiyaṃ aruṇuggamanavelāyaṃ, bodhiṃ arahattamaggañāṇa sabbaññutañāṇasaṅkhātaṃ bodhiṃ, abojjhi paṭivijjhi adhigami vā, mārajinaṃ pañcamārānaṃ vijitāviṃ, abhaṅgaṃ sasenamāro yo koci vā bhindituṃ asamatthaṃ taṃ sugataṃ ahaṃ namāmīti.</p>\r\n<p class="centered">Chaṭṭhamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Rāgādi chedāmalañāṇakhaggaṃ,</p>\r\n<p class="gatha2">Satīsamaññāphalakābhigāhaṃ;</p>\r\n<p class="gatha3">Sīloghalaṅkāravibhūsitaṃ taṃ,</p>\r\n<p class="gathalast">Namāmibhiññāvaramiddhupetaṃ.</p>\r\n<p class="bodytext"><a name="para7"></a><span class="paranum">7</span>. Evaṃ chaṭṭhamagāthāya sugataṃ vanditvā idāni arahatta maggañāṇādīhi catūhi guṇehi thomitvā vanditukāmo <span class="bld">rāgādi</span>tyādigāthamāha. Ayaṃ pana indavajira upendavajira gāthādvayassa lakkhaṇassa missakattā ekādasakkharehi lakkhitā upajātigāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘anantarodīritalakkhaṇā ce, pādāvimissā upajātiyo tā’’ti. Tassattho. Yassā vuttiyā anantaro dīritalakkhaṇā anantare udīritaṃ vuttaṃ indavajiropenda vajiragāthānaṃ lakkhaṇavantā pādā vimissā saṃsaṭṭhā ce bhavanti, tā vuttiyo upajātiyo nāmāti.</p>','\r',char(13)),'\n',char(10)),'-7-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',58,replace(replace('<p class="bodytext">Tattha <a name="M0.0058"></a> <span class="bld">rāgādichedāmala ñāṇakhagga</span>nti ettha rāgaādicheda amalañāṇakhaggantipadacchedo. Rāgādikilesaṃ chindantaṃ malavira hitaṃ arahattamaggañāṇasaṅkhātaṃ asivantanti attho. Tatrāyaṃ vacanattho. Rūpādīsu rañjatīti rāgo. Ranjadhātu rāge ṇa. Rāgo ādi yesanteti rāgādayo, kilesā. Te chindatīti rāgādichedaṃ, arahattamaggañāṇaṃ. Apāyaṃ malati dhāretīti malaṃ, rāgādi. Atha vā malati saṃkilissati etenāti malaṃ. Maladhātu saṃkilesane a. Malato virahitaṃ amalaṃ, ñāṇaṃ. Cattāri saccāni jānāti paṭivijjhatīti ñāṇaṃ, arahattamaggañāṇaṃ. Rāgādichedañca amalañca taṃ ñāṇañcāti rāgādichedāmalañāṇaṃ. Rukkhādiṃ khaggati chindati etenāti vā khaggaṃ, pakatiasi. Khaggaṃ viyāti khaggaṃ, ñāṇaṃ. Rāgādichedāmalañāṇasaṅkhataṃ khaggaṃ yassa soti rāgādichedāmalañāṇakhaggo, buddho. Rāgādikilesaṃ chindantena malavira hitena aggamaggañāṇasaṅkhatena asinā samannāgatoti attho. Taṃ.</p>\r\n<p class="bodytext"><span class="bld">Satīsamaññā phalakābhigāha</span>nti ettha satisamaññāphalaka abhigāhantipadacchedo. Sammāsatināmikaṃ phalakaṃ daḷhagāha kanti attho. Tatrāyaṃ vacanattho. Saraṇaṃ sati, sarati sampayuttadhammā etāyāti, saratīti vā sati. Saradhātuti. Atha vā pamādaṃ sarati hiṃsatīti sati, sammāsati. Saradhātu hiṃsāyaṃti. Sammā ājānāti etāyāti samaññā, satiiti samaññā satisamaññā, asiādisatthaṃ phalati nivāretīti phalakaṃ, kheṭakaṃ. Phaladhātu nivāraṇe ṇvu. Satisamaññaṃ phalakaṃ abhidaḷhaṃ gaṇhatīti satisamaññāphala kābhigāho, buddho labbhati. Yathā hi senāya phalakaṃ asiādisatthaṃ rakkhati, evaṃ satisaṃvaro cakkhādi dvāresu rūpādiārammaṇaṃ pavisituṃ adatvā rakkhati. Tasmā bhagavā satidhammo sabbākusalānaṃ rakkhakattā sabbakusala dhammānaṃ upakārattā sabbatthānaṃ sādhakattā ca satiphalakaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',59,replace(replace('<p class="bodytext">daḷhaṃ <a name="M0.0059"></a> gaṇhatīti vuttaṃ hoti. Vuttañhi ‘‘ye keci kusalā dhammā, sabbe te appamādamūlakā appamādasamosaraṇā, appamādo tesaṃ dhammānaṃ aggamakkhāyatī’’ti ca. ‘‘Satiṃ khvāhaṃ bhikkhave sabbatthikaṃ vadāmī’’ti ca.</p>\r\n<p class="bodytext"><span class="bld">Sīloghalaṅkāravibhūsita</span>nti sīlaoghaalaṅkāra vibhū sitanti padacchedo. Visuddhasīlānaṃ samūhasaṅkhātena alaṅkārena vibhūsitanti attho. Tattha sīlanti lokuttara sīlaṃ. Tassa attho heṭṭhā vuttoyeva. Taṃ pana cetanā sīlaṃ, cetasikaṃ sīlaṃ, saṃvaro sīlaṃ, avītikkamo sīlañcāti catubbidhaṃ hoti. Tattha cetanā sīlaṃ nāma pāṇātipātā dīhi vā viramantassa vattapaṭipattiṃ vā pūrentassa cetanā. Cetasikasīlaṃ nāma pāṇātipātādīhi viramantassa virati. Saṃvaro sīlanti ettha saṃvaro pātimokkhasaṃvaro, sati saṃvaro, ñāṇasaṃvaro, khantisaṃvaro, vīriyasaṃvaro cāti pañcavidho hoti, sabbametaṃ saṃvaro sīlaṃ nāma. Avītikkamo sīlaṃ nāma samādinnasīlassa kāyikavācasiko anatikkamoti. Vuttañhetaṃ <span class="bld">paṭisambhidāyaṃ</span> ‘‘kiṃsīlanticetanā sīlaṃ, cetasikaṃ sīlaṃ, saṃvaro sīlaṃ, avītikkamo sīla’’nti. Idha pana avītikkamo sīlaṃ nādhippetaṃ. Kasmā. Bhagavato samādinnasikkhāpadassa abhāvato. Buddhānañhi sikkhāpadaṃ nāma natthīti. <span class="bld">Ogho</span>ti samūho. Oghasaddo hi samūhavācako. Tena vuttaṃ <span class="bld">abhidhānappadīpikāyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Samūho gaṇasaṅghātā, samudāyo ca sañcayo;</p>\r\n<p class="gathalast">Sandoho nivaho ogho, visaro nikaro cayo’’tyādi.</p>\r\n<p class="bodytext">Avahanati gacchati pavattati avayavaṃ etthāti ogho, samūho. Avapubbahanadhātu gatimhi ṇo. Avassa o, hanassa gho. <span class="bld">Abhidhānappadīpikāṭīkāyaṃ</span> pana ‘‘avayavaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',60,replace(replace('<p class="bodytext">katvā <a name="M0.0060"></a> byāpiyati gacchatīti ogho’’ti vuttaṃ. Sīlānaṃ ogho samūho sīlogho. <span class="bld">Alaṅkāro</span>ti piḷandhano. Alaṃ vibhusaṃ taṃ samaṅgīpuggalaṃ karoti anenāti alaṅkāro, sīlaṃ. Alaṃsaddūpapadakaradhātu ṇo. Sīlogho eva alaṅkāro sīloghālaṅkāro, tena vibhū siyatīti sīloghalaṅkāravibhūsito, buddho. Tena pana aññehi asādhāraṇena lokuttarasīlālaṅkārena vibhūsito. Na cīvarādinālaṅkārenāti attho. Sīlālaṅkārena hi sadiso alaṅkāro nāma natthi. Taṃ sīloghalaṅkāravibhūsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Namāmibhiññāvaramiddhupeta</span>nti ettha namāmi abhiññāvaraṃ iddhi upetanti padacchedo. Tattha namāmīti vandāmi. Abhiññāvaramiddhupetanti abhiññāsaṅkhātāya uttamāya iddhiyā samannāgatanti attho. Abhivisesena jānātīti abhiññā, chabbidhaṃ ñāṇaṃ. Tañhi iddhividhadibbasota paracitta vijānana pubbenivāsānussati cutūpapāta āsavakkhaya ñāṇavasena chabbidhaṃ hoti. Tattha iddhividhañāṇanti ekopi hutvā bahudhā hotītiādinayappavatte iddhividhe jānana ñāṇaṃ. Dibbasotañāṇanti dūrasantikādibhedasaddārammaṇāya dibbasotadhātuyā jānanañāṇaṃ. Paracittavijānanañāṇanti parasattānaṃ cittaparicchedassa vijānanañāṇaṃ. Pubbenivāsā nussatiñāṇanti attasantāne vutthavasena ceva gocara nivāsavasena ca pubbe atītabhavesu khandhādīnaṃ anussaraṇa satiyā sampayuttaṃ ñāṇaṃ. Cutūpapātañāṇanti sattānaṃ cutiyañca upapāte ca jānanañāṇaṃ. Ettha ca aṭṭhakathāsu cutūpapātañāṇaṭṭhāne tassa sādhakattā dibbacakkhuñāṇanti vuttaṃ. Āsavakkhayañāṇanti saccaparicchedassa jānanaṃ arahatta maggañāṇanti. Varanti uttamaṃ. Iddhīti ijjhanaṃ samijjhanaṃ. Ijjhati samijjhatīti vā iddhi. Idhadhātuti. Abhiññāsaṅkhātāya </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',61,replace(replace('<p class="bodytext">varāya <a name="M0.0061"></a> iddhiyā upeto samupeto samannāgatoti abhiññāvaramiddhupeto, buddho. Taṃ abhiññāvaramiddhupetaṃ. Taṃ buddhaṃ ahaṃ namāmīti sambandho.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Rāgādichedāmalañāṇa khaggaṃ rāgādikilesaṃ chindantaṃ malavirahitaṃ arahattamaggañāṇa saṅkhātaṃ asivantaṃ, satīsamaññāphalakābhigāhaṃ sammā satināmikaṃ phalakaṃ daḷhagahaṇaṃ, sīloghalaṅkāravibhūsitaṃ sīlānaṃ samūhasaṅkhātena alaṅkārena vibhūsitaṃ, abhiññā varamiddhupetaṃ abhiññāsaṅkhātāya varāya uttamāya iddhiyā upetaṃ samupetaṃ samannāgataṃ taṃ buddhaṃ ahaṃ namāmīti.</p>\r\n<p class="centered">Sattamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Dayālayaṃ sabbadhi dukkaraṃ karaṃ,</p>\r\n<p class="gatha2">Bhavaṇṇavātikkamamaggataṃ gataṃ;</p>\r\n<p class="gatha3">Tilokanāthaṃ susamāhitaṃ hitaṃ,</p>\r\n<p class="gathalast">Samantacakkhuṃ paṇamāmi taṃmitaṃ.</p>\r\n<p class="bodytext"><a name="para8"></a><span class="paranum">8</span>. Evaṃ sattamagāthāya buddhaṃ vanditvā idāni mahākaruṇā dīhi navahi guṇehi thomitvā vanditukāmo <span class="bld">dayālaya</span>ntyādigāthamāha. Ayaṃ pana ja ta ja ragaṇehi yuttattā dvādasakkharehi lakkhitā catupādantayamakā vaṃsaṭṭhagāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘vadanti vaṃsaṭṭhamidaṃ jatā jarā’’ti. Tassattho, yassaṃ paṭipādaṃ jatā jatagaṇā ca jarā jaragaṇā ca ce bhavanti, idaṃ vuttaṃ vaṃsaṭṭhaṃ vaṃsaṭṭhagāthā nāmāti munayo isayo vadantīti.</p>\r\n<p class="bodytext">Tattha <span class="bld">dayālaya</span>nti mahākaruṇāya ca, mettāya ca ādhāraṃ. Dayāsaddo hi karuṇāya ca, mettāya ca vattati. Adayāpannoti ettha hi karuṇāya vattati. </p>','\r',char(13)),'\n',char(10)),'-8-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',62,replace(replace('<p class="bodytext">Nikkaruṇikataṃ <a name="M0.0062"></a> āpannoti attho. Dayāpannoti ettha mettāya mettacittataṃ āpannoti attho. Idha pana tadubhayampi vaṭṭati. Tasmā dayā ca dayā ca dayāti saddasarūpekaseso kātabbo. Vacanattho panettha evaṃ veditabbo. Dayati hiṃsati kāruṇikanti dayā, mahākaruṇā. Dayadhātu hiṃsāyaṃ a. Yā karuṇā yāva yathādhippetaṃ parassa hitanipphattiṃ na pāpuṇāti, tāva kāruṇikaṃ hiṃsatīti attho. Dayati hiṃsati apaneti paradukkhanti vā dayā. Atha vā dayati attano sukhampi pahāya khedaṃ gaṇhāti sajjano etāyāti dayā, dayati anuggaṇhāti pāpajanampi etāyāti vā dayā, dayanti gaṇhanti bodhisattā sammāsambodhiṃ etāyāti vā dayā, bodhisattā buddhabhāvāya abhinīhārakaraṇa kāle hatthagatampi aggaphalaṃ chaṭṭetvā mahākaruṇāya saṃsārasāgarato satte samuddharitukāmā anassāsa karaṃ mahantaṃ saṃsāradukkhañca pacchimabhave saha amata dhātupaṭilābhena anekaguṇasamalaṅkataṃ sabbaññutañāṇañca gaṇhantīti attho. Dayati dadāti sattānaṃ abhayaṃ etāyāti dayā, mettā. Dayadhātu dāne a. Dayati gacchati vibhāgaṃ akatvā sattesu samaṃ pavattatīti dayā. Yathā udakaṃ sabbasattesu sītena samaṃ pharati rajomalañca pavāheti, evaṃ mettā pāpakalyāṇajanesu vibhāgaṃ akatvā samaṃ pavattatīti attho.</p>\r\n<p class="bodytext">Aparo nayo. Dayanti anurakkhanti satte etāya, sayaṃ vā anuddayati anuddayamattameva vā etanti dayā, karuṇāmettā labbhanti. Tāya ālayati patiṭṭhati etthāti dayālayo, buddho. Bhagavā hi devadattādīsu virodhi sattesu ca avirodhisattesu orase rāhule ca samaṃ niccaṃ hitajjhāsayatāya mahākaruṇāya mettāya ca </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',63,replace(replace('<p class="bodytext">ṭhito<a name="M0.0063"></a>, tasmā dayālayanti thometīti vuttaṃ hoti. Vuttañhetaṃ <span class="bld">apadānapāḷiyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Vadhake devadatte ca, core aṅgulimālake;</p>\r\n<p class="gathalast">Dhanapāle rāhule ca, samacitto mahāmunī’’ti.</p>\r\n<p class="bodytext"><span class="bld">Sabbadhī</span>ti sabbasmiṃ attabhāve. Buddhabhāvāya abhinīhārato paṭṭhāya sabbattabhāvesūti attho. <span class="bld">Dukkara</span>nti dukkhena karitabbaṃ pañcamahāpariccāgādikammaṃ dukkhena karitabbanti dukkaraṃ, pañcamahāpariccāgādikammaṃ labbhati. Ettha ca pañcamahā pariccāgoti dhanapariccāgo, aṅgapariccāgo, puttapariccāgo, bhariyapariccāgo, jīvitapariccāgo cāti pañcavidho hoti. Tattha hatthiassa dāsidāsādi saviññāṇakassa dhanassa vā muttāmaṇirajatādi aviññāṇakassa dhanassa vā pariccāgo dhanapariccāgo nāma. Hatthapādacakkhukaṇṇanāsādisarīrāva yavassa pariccāgo aṅgapariccāgo nāma. Puttassa ca dhītuyā ca pariccāgo puttapariccāgo nāma. Bhariyāya pariccāgo bhariyapariccāgo nāma. Jīvitindriyapaṭibaddhassa sarīrassa pariccāgo jīvitapariccāgo nāma. Kiñcāpi pana dhanapariccāgā dayo dānapāramiyaṃyeva antogadhā, tathāpi pariccāga visesabhāvadassanatthañceva sudukkarabhāvadassanatthañca mahāpariccāgavasena visuṃ katanti adhippāyo. <span class="bld">Mahāpadānasuttaṭīkāyaṃ</span> pana ‘‘aṅgapariccāgo, nayanapariccāgo, attapariccāgo, rajjapariccāgo, puttadārapariccāgoti ime pañca mahāpariccāgā. Tatthapi kāmaṃ aṅgapariccāgādayopi dānapāramīyeva. Tathāpi pariccāgavisesabhāvadassanatthañceva sudukkarabhāvadassanatthañca mahāpariccāgānaṃ visuṃ gahaṇaṃ. Tatoyeva ca aṅgapariccāgatopi visuṃ nayanapariccāgaggahaṇaṃ. Pariccāgabhāvasāmaññepi rajjapariccāga puttadārapariccāgaggahaṇaṃ kata’’nti vuttaṃ. Taṃ dukkaraṃ. Idaṃ pana karanti etassa kammapadaṃ. <span class="bld">Kara</span>nti karontaṃ, karotīti karo, buddho. Taṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',64,replace(replace('<p class="bodytext"><span class="bld">Bhavaṇṇavātikkamamaggata</span>nti <a name="M0.0064"></a> ettha bhavaaṇṇavaatikkamaṃ aggatanti padacchedo. Bhavaṇṇavātikkamanti kāmarūpaarūpabhavasaṅkhatā aṇṇavā samuddato atikkantaṃ. Bhavanti sattā etthāti bhavo. Aṇṇo vuccati udakaṃ. Taṃ etasmiṃ atthīti aṇṇavā, samuddo. Bhavasaṅkhāto aṇṇavoti bhavaṇṇavā, mahāsamuddo. Bahūhi ūmivegādīhi bhayehi okiṇṇo viya tividhopi bhavo bahūhi jātijarāmaraṇādīhi dukkhabhayehi okiṇṇo hoti, tasmā bhavaṇṇavāti vuccati. Taṃ atikkamittha uttaritthāti bhavaṇṇavātikkamo, buddho. Tassa hi gambhīre mahāudakakkhandhe mahāsamudde udakaṃ abbocchinnaṃ pavattati sandatiiva tibhavasaṅkhate saṃsāre paṭisandhicutiyo abbocchinnaṃ nibbattakānaṃ diyaḍḍhasahassakilesānaṃ bodhimaṇḍe arahattamaggañāṇena asesaṃ viddhaṃsitattā tīsu bhavesu punabbhavo natthi. Tasmā buddhaṃ bhavaṇṇavātikkamanti abhitthavituṃ arahatīti vuttaṃ hoti. Taṃ bhavaṇṇavātikkamaṃ.</p>\r\n<p class="bodytext"><span class="bld">Aggata</span>nti sabbasattānaṃ seṭṭhabhāvaṃ. Ayaṃ panettha aggasaddo ādikoṭikoṭṭhāsaseṭṭhesu dissati. Tathā hesa ajjatagge pāṇupetaṃ saraṇaṃ gatantiādīsu ādimhi dissati. Teneva aṅgulaggena taṃ aṅgulaggaṃ parāmaseyyātiādīsu koṭiyaṃ. Anujānāmi bhikkhave vihāraggena vā pariveṇaggena vā bhājetuntiādīsu koṭṭhāse. Aggo hamasmi lokassātiādīsu seṭṭhe. Svāyamidhapi soyeva daṭṭhabbo. Devamanussānaṃ seṭṭhabhāvaṃ ajati gacchatīti aggo, buddho. Asadisaṭṭhena guṇavisiṭṭhaṭṭhena asamasamaṭṭhena ca aggo seṭṭho nāmāti attho. Atha vā dullabhapātu bhāvato acchariyamanussabhāvato bahujanahitasukhāvahato adutiyaasahāyādibhāvato ca bhagavā loke aggo seṭṭho nāmāti attho. Vuttañhi <span class="bld">aṅguttarapāḷiyaṃ</span> ‘‘yāvatā bhikkhave sattā apadā vā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',65,replace(replace('<p class="bodytext">dvipadā <a name="M0.0065"></a> vā catuppadā vā bahuppadā vā, tathāgato tesaṃ aggamakkhāyatī’’ti. Aggassa bhāvo aggataṃ. Taṃ pana gatantietassa kammapadaṃ. <span class="bld">Gata</span>nti pattaṃ, pāpuṇantanti attho.</p>\r\n<p class="bodytext"><span class="bld">Tilokanātha</span>nti kāmarūpaarūpasaṅkhatānaṃ tiṇṇaṃ lokānaṃ paṭisaraṇaṃ. Ettha ca lokasaddassa attho heṭṭhā vuttoyeva. Tayo lokā tilokā. Nāthasaddo pana nipphannānipphannavasena duvidho hoti. Tattha anipphannanāthasaddo paṭisaraṇattho hoti saṅketavasena. Tathā hi vuttaṃ <span class="bld">aṭṭhakathāyaṃ</span> ‘‘saṅketavacanaṃ saccaṃ, lokasammutikāraṇa’’nti. Tilokānaṃ nātho paṭisaraṇoti tilokanātho, buddho. Sabbānatthaparihāra pubbaṅgamāya niravasesahitasukhavidhānatapparāyaniratisayāya payogasampattiyā ca sadevamanussāya pajāya accantupakāritāya aparimitanirupamappabhāvaguṇavisesasamaṅgitāya ca sabbasattuttamo bhagavā aparimāṇāsu lokadhātūsu aparimāṇānaṃ sattānaṃ ekapaṭisaraṇo nāmāti attho.</p>\r\n<p class="bodytext">Nipphannanāthasaddo pana yācane, upatāpe, issariye, āsīse cāti catūsvatthesu vattati. Tattha yācane nāthati yācatīti nātho, buddho. Nāthadhātu a. So pana veneyyasatte attano hitakaraṇe yācitvāpi niyojetīti attho. Atha vā bhagavā sādhu bhikkhave bhikkhu kālena kālaṃ attasampattiṃ paccavekkheyyātiādinā sattānaṃ taṃ taṃ hitapaṭipattiṃ yācitvāpi karuṇāya samussāhito te tattha niyojeti. Tasmā nātho nāmāti attho. Upatāpe nāthati veneyyagate kilese upatāpetīti nātho, buddho. Dhammadesanāya veneyyasantāne kilese viddhaṃsāpetīti attho. Issariye nāthati issariyati satteti nātho. Paramena </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',66,replace(replace('<p class="bodytext">pana <a name="M0.0066"></a> cittissariyena samannāgato bhagavā sabbasatte dhammena isati abhibhavatīti attho. Āsīse pana nāthati āsīsatīti nātho. Veneyyānaṃ hitasukhaṃ mettāya navasena patthetīti attho. Tilokānaṃ nātho tilokanātho, buddho. Taṃ tilokanāthaṃ.</p>\r\n<p class="bodytext"><span class="bld">Susamāhita</span>nti lābhālābhādilokadhammehi acala mānattā sundarasamādhivantaṃ. Saṃsamaṃ ādhiyati ekārammaṇe sampayuttadhamme ṭhapeti etenāti samādhi. Saṃpubba dhā dhātu i. Sundaro pasattho vā samādhi susamādhi. Taṃ assa atthīti susamāhito, buddho. Assathyatthe to. Pabbatotiādīsu viya. Dhassa ho. Catūhi disāhi āgatehi vātehi acalamāno mahāmerupabbato viya bhagavā lābhālābhādilokadhammehi ca micchāvāda vātehi ca acalamānena samādhinā samannāgatoti attho. Taṃ susamāhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Hita</span>nti manussadevabrahmasaṅkhātānaṃ sabbasattānaṃ payojana dhārentaṃ. Kāraṇānurūpaṃ hinoti pavattatīti hitaṃ, payojanaṃ. Hidhātu gatiyaṃ ta. Taṃ dhāretīti hito, buddho. Kitanta samāsoyaṃ. Hitadharanti vattabbe chandānurakkhaṇatthāya uttara padalopena hitanti vattabbaṃ. Yathā samajjanī padīpoti. Atha vā dukkhaṃ hiṃsatīti hitaṃ. Hiṃsadhātu hiṃsāyaṃ tapaccayo. Yathā ca byādhiyā osamaṃ rogīnaṃ hitanti vuccati, evaṃ dukkhahananato hitaṃ nāma hoti. Sukhaṃ vidadhātīti vā hitaṃ. Dhādhātu dhāraṇe ta. Yathā ca rudhiramaṃsādi dhātu vaḍḍhanaṃ rasāyatanaṃ kissānaṃ hitanti vuccati, evaṃ sukhavahanato hitanti vuccati. Payojanaṃ taṃ dhāretīti hito, buddho. So pana dhammadesanāya veneyyajanānaṃ dukkhāpanayanampi ñāṇaparipācanampi katvā lokiyalokuttarahitasukhaṃ dhāretīti vuttaṃ hoti. Taṃ hitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',67,replace(replace('<p class="bodytext"><span class="bld">Samantacakkhu</span>nti <a name="M0.0067"></a> ettha cakkhu nāma buddhacakkhu, dhammacakkhu, ñāṇa cakkhu, samantacakkhu, dibbacakkhūti pañcavidhaṃ hoti. Tattha addasaṃ kho ahaṃ bhikkhave buddhacakkhunā lokaṃ volokentoti idaṃ buddhacakkhu nāma. Virajaṃ vītamalaṃ dhammacakkhunti idaṃ heṭṭhimamaggatta yasaṅkhātaṃ dhammacakkhu nāma. Cakkhuṃ udapādīti idaṃ ñāṇacakkhu nāma. Samantacakkhu vuccati sabbaññutañāṇanti idaṃ samantacakkhu nāma. Addasaṃ kho ahaṃ bhikkhave dibbena cakkhunā visuddhenāti idaṃ dibbacakkhu nāma. Idha pana samantacakkhu adhippetaṃ. Parisamantato sabbañeyyadhammaṃ passantaṃ sabbaññutañāṇavantanti attho. Ayaṃ panettha vacanattho. Samantato sabbañeyyadhammaṃ cakkhati passatīti samantacakkhu, sabbaññutañāṇaṃ. Cakkhadhātu u. Tena vuttaṃ <span class="bld">mahāniddesapāḷiyaṃ</span> ‘‘samantacakkhu vuccati sabbaññutañāṇa’’nti. Samantacakkhu assa atthīti samantacakkhu, buddho. Vuttañhetaṃ <span class="bld">paṭisambhidāmagge</span> –</p>\r\n<p class="gatha1">‘‘Na tassa addiṭṭhamidhatthi kiñci,</p>\r\n<p class="gatha2">Atho aviññātamajānitabbaṃ;</p>\r\n<p class="gatha3">Sabbaṃ abhiññāsi yadatthi neyyaṃ,</p>\r\n<p class="gathalast">Tathāgato tena samantacakkhū’’ti.</p>\r\n<p class="bodytext">Tassattho. Tassa tathāgatassa addiṭṭhaṃ apassantaṃ kiñci dhammajātaṃ idhaloke na atthi. Atho aviññātaṃ aviditaṃ apākaṭaṃ vā ajānitabbaṃ dhammajātaṃ na atthi. Neyyaṃ ñātabbaṃ yaṃ dhammajātaṃ atthi. Sabbaṃ taṃ dhammajātaṃ tathāgato abhiññāsi paṭivijjhi. Tena tasmā tathāgato samantacakkhu nāmāti. Taṃ samantacakkhuṃ.</p>\r\n<p class="bodytext"><span class="bld">Paṇamāmi taṃmita</span>nti ettha paṇamāmi taṃ amitanti padacchedo. Amitaṃ taṃ buddhaṃ ahaṃ paṇamāmīti sambandho. Tattha amitanti aparimeyyaṃ ñāṇena aparicchinnaṃ vā, sīlasamādhi paññādiguṇavantaṃ. Amitabbaṃ ñāṇena aparicchinditabbanti vā amitaṃ, sīlādiguṇaṃ. Napubba mādhātu parimāṇe ta. Taṃ assa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',68,replace(replace('<p class="bodytext">atthīti <a name="M0.0068"></a> amito, buddho. Yathā hi mahāsamudde na sukaraṃ udakassa pamāṇaṃ gaṇetuṃ ettakāni udakāḷhakānīti vā, ettakāni udakāḷhakasatānīti vā, ettakāni udakāḷhakasahassānīti vā, ettakāni udakāḷhakasatasahassānīti vāti. Atha kho asaṅkhyeyyo appameyyo mahāudakakkhandhotveva saṅkhyaṃ gacchati, evameva buddhassa na sukaraṃ sīlādiguṇassa pamāṇaṃ gaṇetuṃ ettakaṃ sīlaguṇaṃ, ettako samādhiguṇo, ettakā paññāguṇātiādināti. Atha kho asaṅkhyeyyo appameyyo mahāguṇakkhandhotveva saṅkhyaṃ gacchati. Tenavuttaṃ <span class="bld">apadānapāḷiyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Buddhopi buddhassa bhaṇeyya vaṇṇaṃ,</p>\r\n<p class="gatha2">Kappampi ce aññamabhāsamāno;</p>\r\n<p class="gatha3">Khīyetha kappo ciradīghamantare,</p>\r\n<p class="gathalast">Vaṇṇo na khīyetha tathāgatāna’’nti ca.</p>\r\n<p class="gatha1">‘‘Asaṅkhyeyyāni nāmāni, saguṇena mahesino;</p>\r\n<p class="gathalast">Guṇena nāmamuddheyyaṃ, api nāmasahassato’’ti ca.</p>\r\n<p class="bodytext">Tena buddhaṃ amitanti thometīti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Dayālayaṃ mahākaruṇāya mettāya ādhāraṃ. Sabbadhi sabbasmiṃ attabhāve, dukkaraṃ dukkhena karitabbaṃ pañcamahāpariccāgādikammaṃ karaṃ karontaṃ, bhavaṇṇavātikkamaṃ tibhavasaṅkhātasamuddato atikkantaṃ, aggataṃ sabbasattānaṃ seṭṭhabhāvaṃ, gataṃ pattaṃ, tilokanāthaṃ kāmarūpa arūpasaṅkhātassa tilokassa paṭisaraṇaṃ, susamāhitaṃ sundara samādhivantaṃ, hitaṃ sabbasattānaṃ lokiyalokuttarasaṅkhātaṃ atthadhāraṇaṃ, samantacakkhuṃ parisamantato sabbañeyyadhammaṃ passantaṃ sabbaññutañāṇavantaṃ, amitaṃ aparimeyyaṃ sīlādiguṇavantaṃ, taṃ buddhaṃ ahaṃ paṇamāmi vandāmīti.</p>\r\n<p class="centered">Aṭṭhamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',69,replace(replace('<p class="gatha1">Tahiṃ <a name="M0.0069"></a> tahiṃ pāramisañcayaṃ cayaṃ,</p>\r\n<p class="gatha2">Gataṃ gataṃ sabbhi sukhappadaṃ padaṃ;</p>\r\n<p class="gatha3">Narānarānaṃ sukhasambhavaṃ bhavaṃ,</p>\r\n<p class="gathalast">Namānamānaṃ jinapuṅgavaṃ gavaṃ.</p>\r\n<p class="bodytext"><a name="para9"></a><span class="paranum">9</span>. Evaṃ aṭṭhamagāthāya buddhaṃ vanditvā idāni pāramisañcayaṃ cayantyādīhi pañcahi guṇehi thomitvā vanditukāmo <span class="bld">tahiṃ tahiṃ pāramisañcayaṃcaya</span>ntyādigāthamāha. Ayaṃ pana jatajara gaṇehi yuttattā dvādasakkharehi lakkhitā sabbapādādyantayamakā vaṃsaṭṭhagāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘vadanti vaṃsaṭṭhamidaṃ jatā jarā’’ti. Tassattho heṭṭhā vuttoyeva.</p>\r\n<p class="bodytext">Tattha <span class="bld">tahiṃ tahi</span>nti tasmiṃ tasmiṃ bhave attabhāve vā, buddhavaraṃ paṇidhānato paṭṭhāya tesu tesu bhavesu attabhāvesu vāti attho. <span class="bld">Pāramisañcaya</span>nti dānādipā ramīnaṃ samūhaṃ. Ettha ca pāramī nāma dānasīlanekkhammapaññā vīriya khantisaccaadhiṭṭhānamettāupekkhāvasena dasavidhā hoti. Vuttañca <span class="bld">buddhavaṃse</span> –</p>\r\n<p class="gatha1">‘‘Dānaṃ sīlañca nekkhammaṃ, paññā vīriyena pañcamaṃ;</p>\r\n<p class="gathalast">Khantisaccamadhiṭṭhānaṃ, mettupekkhāti te dasā’’ti.</p>\r\n<p class="bodytext">Ayaṃ pana pāramīsaddassa vacanattho. Dānasīlādiguṇa visesayogena sattuttamatāya mahābodhisatto paramo. Tassa ayaṃ bhāvo kammanti vā pāramī, dānādi kriyāva labbhati. Santakabhāvakammataddhite ṇapaccayo. Itthiliṅgajotaka īpaccayo ca. Atha vā dānādīni pūretīti paramo. Dānādiguṇānaṃ pūrako pālako ca bodhisatto. Pūradhātu amapaccayo. Pare satte mavati attani bandhati guṇavisesayogenāti vā paramo, bodhisatto. Parasaddūpapada mudhātu bandhane kvi. Paraṃ atirekaṃ majjati kilesamalatoti vā paramo. Parasaddūpa </p>','\r',char(13)),'\n',char(10)),'-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',70,replace(replace('<p class="bodytext">pada <a name="M0.0070"></a> majjadhātu saṃsuddhiyaṃ kvi. Parasaddo atirekattho. Paraṃ seṭṭhaṃ nibbānaṃ visesena mayati gacchatīti vā paramo. Parasaddūpapada mayadhātu gatiyaṃ kvi. Dhātvantalopo. Para saddo seṭṭhattho. Paraṃ lokaṃ pamāṇabhūtena ñāṇa visesena idhalokamiva munāti paricchindatīti vā paramo. Parasaddūpa pada munadhātu paricchindane kvi. Paraṃ ati viya sīlādiguṇagaṇaṃ attano santāne minoti pakkhipatīti vā paramo. Parasaddūpapada midhātu pakkhipane kvi. Parasaddo atirekavācako. Paraṃ attabhūtadhammakāyato aññaṃ paṭipakkhaṃ vā tadanatthakaraṃ kilesacoragaṇaṃ mināti hiṃsatīti vā paramo, mahābodhisatto. Parasaddūpapada midhātu hiṃsāyaṃ kvi. Parasaddo aññavācako, paṭipakkhavācako vā. Paramassa ayantiādinā vuttanayena pāramīti. Api ca pāre nibbāne majjati sujjhatīti pāramī, mahābodhi satto. Suddhakattusādhanoyaṃ. Pārasaddūpapada majadhātu saṃsuddhiyaṃ kvi. Ī ca. Pāre nibbāne satte majjati sodhetīti vā pāramī, hetukattusādhanoyaṃ.</p>\r\n<p class="bodytext">Pārenibbāne satte mavati bandhati yojetīti vā pāramī. Pāraṃ nibbānaṃ mayati gacchati satte ca māyeti gameti vā pāramī. Pāraṃ nibbānaṃ yāthāvato munāti paricchindatīti vā pāramī. Pāre nibbāne satte minoti pakkhipatīti vā pāramī. Midhātu pakkhipane ī. Pāre nibbāne sattānaṃ mināti hiṃsatīti vā pāramī, mahābodhisatto. Tassa ayaṃ bhāvo kammanti dānādikriyā pāramīti. Iminā vuttanayena pāramīsaddassa vacanattho veditabbo. Kiñcāpi pana pāramīsaddassa nibbacanaṃ saṅkhepena vuttaṃ. Sotūnaṃ pana niruttinayakosallatthaṃ vitthārena vuttanti daṭṭhabbaṃ.</p>\r\n<p class="bodytext">Sā pana dasapāramī, dasaupapāramī, dasaparamatthapāramī cāti tividhā hoti. Tattha saviññāṇāviññāṇakaṃ dhanaṃ pariccāgaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',71,replace(replace('<p class="bodytext">katvā <a name="M0.0071"></a> pūritā bodhisambhārā dasapāramiyo nāma. Hattha pāda kaṇṇa nāsādiaṅgapaccaṅgapariccāgaṃ katvā pūritā bodhisambhārā dasaupapāramiyo nāma. Jīvitindriyapariccāgaṃ katvā pūritā bodhisambhārā dasa paramatthapāramiyo nāmāti. Dānādipāramīnaṃ pana attho cariyapiṭakaaṭṭhakathādīsu gahetabbo. Avayavaṃ saṅgahavasena cayati gacchati pavattatīti sañcayo, saṃpubba cidhātu gatiyaṃ ṇa. Sañcinoti avayavanti vā sañcayo, samūho. Pāramīnaṃ sañcayo samūhoti pāramisañcayo, taṃ pāramisañcayaṃ. Taṃ pana cayanti etassa kammaṃ. <span class="bld">Caya</span>nti upacitaṃ, parisambhūtanti attho. Cayati upacinātīti cayo, ciyittha upaciyitthāti vā cayo, buddho. Cidhātu upacaye ṇa. Taṃ cayaṃ. Sabbhigatanti sambandho.</p>\r\n<p class="bodytext">Tattha <span class="bld">sabbhī</span>ti santehi sappurisehi. Rāgādikilese samenti upasamentīti santā, sappurisā. Samudhātu upasameta, antapaccayo vā. Santehīti vattabbe smāhismiṃnaṃ mhā bhimhivāti suttena hivibhattiyā bhikāraṃ santasaddassa so bhe bo canteti suttena bhakāre pare santasaddassa sakāraṃ katvā ante ca bakārāgamaṃ katvā sabbhīti vuttaṃ. Tehi sabbhi. Taṃ pana gatanti etassa avuttakattā. <span class="bld">Gata</span>nti upagamitabbaṃ. Tañhi devamanussehi gamiyati upagamiyati upasaṅkamiyatīti gatoti vuccati. Taṃ buddhaṃ atthakāmehi sappurisehi devamanussehi sīlādianantaguṇikattā saraṇanti abhimānā payirupāsitabboti vuttaṃ hoti. Taṃ gataṃ. Sukhappadaṃ padaṃ gatanti sambandho. Tattha <span class="bld">sukhappada</span>nti accantasukhassa patiṭṭhānabhūtaṃ. Sukhanaṃ sukhaṃ, tassa pajjati tiṭṭhati etthāti sukhappadaṃ, nibbānaṃ. <span class="bld">Pada</span>nti nibbānaṃ. Tañhi buddhādīhi ariyehi pajjiyati gamiyatīti padanti vuccati. Gatanti agaminti gato, buddho. Dukkhādiasammissassa ekantasukhassa patiṭṭhānabhūtaṃ nibbānaṃ ārammaṇakaraṇavasena apāpuṇinti vuttaṃ hoti. Taṃ gataṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',72,replace(replace('<p class="bodytext"><span class="bld">Narānarāna</span>nti <a name="M0.0072"></a> naraanarānanti padacchedo. Manussadevānanti attho. Pabbajjādiseṭṭhabhāvaṃ narati netīti naro, narīyati sakena kammena niyatīti vā naro, manusso, satto vā. Naradhātu nayane a. Na naro anaro, devo. Naro ca anaro ca narānaro, tesaṃ narānarānaṃ. Taṃ pana sukhasambhavanti pade sambandho.</p>\r\n<p class="bodytext"><span class="bld">Sukhasambhava</span>nti lokiyalokuttarasukhassa kāraṇaṃ. Tividhasukhassa labhanakāraṇabhūtaṃ puññaṃ vā. Sukhayatīti sukhaṃ. Tividhasukhaṃ. Yassa uppajjati, taṃ sukhitaṃ karotīti attho. Atha vā suṭṭhu dukkhaṃ khanatīti sukhaṃ. Supubbakhanudhātu avadhāraṇe a. Sambhavati sukhasaṅkhātaṃ phalaṃ anena puññenāti sambhavaṃ, puññakaraṇaṃ. Saṃpubbabhūdhātu sattāyaṃṇa. Sukhassa sambhavaṃ sukhasambhavaṃ, taṃ pana bhavantipade kammaṃ. <span class="bld">Bhava</span>nti nibbattentaṃ. Taṃ sukhasambhavaṃ bhāveti nibbattetīti bhavo, buddho. Taṃ bhavaṃ. So hi mahākaruṇāpubbaṅgamāya dhammadesanāya devamanussānaṃ lokiyalokuttarasukhassa labhanakāraṇabhūtaṃ dānasīlabhāvanāsaṅkhātaṃ puññaṃ kāreti, tasmā bhagavato narānarānaṃ sukhasambhavaṃ bhavanti thometīti daṭṭhabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Namānamāna</span>nti padassa name anamānanti padacchedo. Nameti ahaṃ namāmīti attho. Ettha ca mivibhattiyā ekārādesoti veditabbo. Anamānanti anamentānaṃ avandantānaṃ anādarānanti attho. Taṃ pana gavantietassa visesanaṃ. <span class="bld">Gava</span>nti goṇasadisānaṃ bālānaṃ. Gavanti hi chaṭṭhībahuvacanantanāmapadaṃ. Tato namaṃ patimhālutte ca samāseti sutte samabhiniviṭṭhena caggahaṇena naṃvacanassa aṃ ādesañca goiccetassa okārassa avādesaṃ katvā rūpasiddhi veditabbā. Gacchantīti gāvo, goṇā. Gāvo viyāti gāvo, tesaṃ gavaṃ bālānaṃ. Taṃ pana jinapuṅgavantipade chaṭṭhīkammaṃ. <span class="bld">Jinapuṅgava</span>nti jayantapurisuttamaṃ buddhaṃ name namāmīti sambandho. Ettha ca puṅgava </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',73,replace(replace('<p class="bodytext">saddo <a name="M0.0073"></a> kammadhārayasamāse seṭṭhatthavācako. Munipuṅgavotiādīsu viya. Vuttañca <span class="bld">amarakose</span> –</p>\r\n<p class="gatha1">‘‘Uttarasmiṃ pade byaggha, puṅgavosabhakuñjarā;</p>\r\n<p class="gathalast">Sīhasaddūlanāgādyā, pume seṭṭhatthagocarā’’ti.</p>\r\n<p class="bodytext">Tassattho. Kammadhārayasamāse uttarapade vattamānā byagghapuṅgavosabhakuñjarā byagghapuṅgavausabhakuñjarasaddā ca sīha saddūlanāgādyā sīha saddūla nāgāsaddā ca pume seṭṭhattha vācakāti. Ayaṃ panettha vacanattho. Go viya buddhaguṇe ajānante anamante aññadiṭṭhike bāle jināti ajinīti vā jino. Pakaṭṭhabhāvaṃ padhānabhāvaṃ vā uddhaṃ gacchatīti puṅgavo. Paupubba gamudhātu gatiyaṃ avapaccayo. Niggahitā gamo. Tassa kavaggantaṃ dhātvantalopañca yadādinā katvā rūpasiddhi veditabbā. Jino ca so puṅgavo cāti jinapuṅgavo, buddho. So pana gunnaṃ viya buddhānaṃ sīlādike appameyyaguṇe ajānantānaṃ anamantānaṃ agāravantānaṃ avandantānaṃ añña diṭṭhigatikānaṃ bālānaṃ asatthena pāramīpuññatejena ajinīti vuttaṃ hoti. Taṃ jinapuṅgavanti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Tahiṃ tahiṃ tasmiṃ tasmiṃ bhave, attabhāve vā, pāramisañcayaṃ dānādipāramīnaṃ samūhaṃ, cayaṃ upacitaṃ parisambhūtaṃ, sabbhi santehi sappurisehi, gataṃ upagamitabbaṃ payirupāsitabbaṃ, sukhappadaṃ accantasukhassa patiṭṭhāna bhūtaṃ, padaṃ nibbānaṃ gataṃ pattaṃ agamiṃ, narānarānaṃ manussadevānaṃ, sukhasambhavaṃ lokiyalokuttarasukhassa kāraṇaṃ tividha sukhassa labhanakāraṇabhūtaṃ puññaṃ vā bhavaṃ nibbattentaṃ, anamānaṃ anamantānaṃ anādarānaṃ avandantānaṃ vā, gavaṃ goṇasadisānaṃ bālānaṃ jinapuṅgavaṃ jayantapurisuttamaṃ buddhaṃ ahaṃ name namāmīti.</p>\r\n<p class="centered">Navamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',74,replace(replace('<p class="gatha1">Maggaṅganāvaṃ <a name="M0.0074"></a> munidakkhanāviko,</p>\r\n<p class="gatha2">Īhāphiyaṃ ñāṇakarena gāhako;</p>\r\n<p class="gatha3">Āruyha yo tāya bahū bhavaṇṇavā,</p>\r\n<p class="gathalast">Tāresi taṃ buddhamaghappahaṃ name.</p>\r\n<p class="bodytext"><a name="para10"></a><span class="paranum">10</span>. Evaṃ navamagāthāya buddhaṃ natvā idāni bahū satte tibhavasaṅkhātā saṃsārasamuddato tārentena guṇena ca sattānaṃ dukkhappahānena guṇena ca thomitvā buddhaṃ vanditu kāmo <span class="bld">maggaṅganāva</span>ntyādigāthamāha. Ayaṃ pana tata jara gaṇehi racitattā dvādasakkharehi yuttā indavaṃsa gāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘sāyindavaṃsā khalu yattha tā jarā’’ti.</p>\r\n<p class="bodytext">Tassattho. Yattha yassaṃ gāthāyaṃ tā tagaṇadvayañca jarā jagaṇa ragaṇā ca ce siyuṃ, sā gāthā khalu ekantaṃ indavaṃsagāthā nāmāti.</p>\r\n<p class="bodytext">Tattha yo munidakkhanāvikoti sambandho. Yo buddha saṅkhāto cheko nāvājeṭṭhakoti attho. Tattha <span class="bld">munī</span>ti ubho loke munāti jānātīti muni, bhagavā. Munadhātu ñāṇe i. Atha vā monaṃ vuccati sabbaññutañāṇaṃ, taṃ assa atthīti muni, bhagavā. Assathyatthe ipaccayo. Vuttañhi ekanipāte <span class="bld">vaṇṇapathajātakaṭṭhakathāyaṃ</span> ‘‘munīti monaṃ vuccati ñāṇaṃ. Kāya moneyyādīsu vā aññatarena samannāgatattā puggalo munīti vuccati. So panesa agāriyamuni, anagāriyamuni, sekkhamuni, asekkhamuni, paccekamuni, munimunīti anekavidho. Tattha agāriyamunīti gihi āgataphalo viññātasāsano. Anagāriyamunīti tathārūpova pabbajito. Sekkhamunīti sattasekkhā. Asekkhamunīti khīṇāsavo. Paccekamunīti paccekasambuddho. Munimunīti sammāsambuddho’’ti. Idha pana munimuni adhippeto.</p>','\r',char(13)),'\n',char(10)),'-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',75,replace(replace('<p class="bodytext"><span class="bld">Dakkho</span>ti <a name="M0.0075"></a> cheko. Samuddataraṇe dakkhati kusalattaṃ gacchatīti dakkho, nāviko. Dakkhadhātu gatiyaṃ a. Atha vā dakkhati kusalakamme aññasmiñca kiccākicce adandhatāya sīghaṃ gacchatīti dakkho, yokoci kusalo puggalo. Dakkhadhātu sīghatthe a. Dakkho viyāti dakkho, buddho. Nītatthena pana buddho sattānaṃ saṃsārasamuddato uddhāraṇassa kāraṇaṃ dakkhati paricchindati ñāṇenāti dakkhoti vuccati. <span class="bld">Nāviko</span>ti samuddatīraṃ nāvāya taratīti nāviko, nāvājeṭṭhako. Taratitaddhite ṇiko. Nāviko viyāti nāviko, buddho. Nītatthena pana buddho nibbānaṃ pāraṃ aṭṭhamaggaṅganāvāya taratīti nāvikoti vuccati. Dakkho nāviko dakkhanāviko, muni ca so dakkhanāvikocāti munidakkhanāviko, kammadhārayasamāsoyaṃ. Atha vā dakkhanāviko viya dakkho munīti munidakkhanāviko, buddho. Upamākammadhārayasamāsoyaṃ. Yathā hi samuddataraṇe cheko nāviko manusse samuddapāraṃ neti, evaṃ buddho veneyyasatte nibbānapāraṃ netīti vuttaṃ hoti.</p>\r\n<p class="bodytext">Maggaṅganāvaṃ āruyhāti sambandho. <span class="bld">Maggaṅganāva</span>nti aṭṭhamaggaṅga saṅkhātaṃ nāvaṃ. <span class="bld">Āruyhā</span>ti ārohitvāti attho. Kilese mārento ārammaṇakaraṇavasena nibbānaṃ gacchatīti maggo. Mārasaddūpapada gamudhātu kvi. Mārenta goti vattabbe niruttinayena maggoti vuttaṃ. Atha vā nibbānaṃ maggati gavesatīti maggo. Maggadhātu gavesane a. Nibbānatthikehi maggiyati gavesiyatīti vā maggo. Ekantato jāti jarābyādhimaraṇadukkhādīhi pīḷitehi sattehi dukkhakkhayaṃ nibbānaṃ pāpuṇatthāya maggitabbo gavesitabboti maggo, catumaggo. Vuttañhi ‘‘maggoti kenaṭṭhena nibbānaṃ magganaṭṭhena nibbānatthikehi magganīyaṭṭhena cā’’ti. Aṅgati gacchati phalanibbānaṃ etenāti aṅgaṃ. Agidhātu gamane a. Maggassa aṅgaṃ kāraṇanti maggaṅgaṃ, sammādiṭṭhi sammāsaṅkappa sammāvācā sammākammanta sammāājīva </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',76,replace(replace('<p class="bodytext">sammāvāyāma <a name="M0.0076"></a> sammāsati sammāsamādhivasena aṭṭhamaggaṅgaṃ labbhati. Nuyati thavīyatīti nāvā. Nudhātu thaviyaṃ avo. Atha vā orato pāraṃ netīti nāvā, bhāraṃ nayanti etāyāti vā nāvā. Nidhātu nayane avo. Maggaṅgasaṅkhātā nāvā maggaṅganāvā. Atha vā nāvā viya maggaṅganti maggaṅga nāvā. Yathā hi nāvā attani āruḷhe manusse samudda nadīpāraṃ neti, evaṃ aṭṭhamaggaṅgadhammā attānaṃ paṭipajjante nibbāna pāraṃ nentīti vuttaṃ hoti. Taṃ maggaṅganāvaṃ.</p>\r\n<p class="bodytext"><span class="bld">Īhāphiya</span>nti vīriyasaṅkhātaṃ dabbisārakaṃ. Vuttañhi <span class="bld">suttani pātaaṭṭhakathāyaṃ</span> ‘‘phiyenāti dabbisārakenā’’ti. Īhati ārabbhatīti īhā. Īhati vāyamati cetayati etenāti vā īhā, vīriyaṃ. <span class="bld">Saddanītiyaṃ</span> pana ‘‘īha cetāya’’nti vuttaṃ. Phāti vaḍḍheti udakaṃ etenāti phiyaṃ. Phādhātu vaḍḍhiyaṃ iyo. Phāti ghaṭṭeti udakanti vā phiyaṃ. Atha vā phāyati gacchati nāvaṃ etenāti phiyaṃ. Dabbisārakaṃ. Phāyi dhātu gatiyaṃ a. Ākārassa i. Īhāsaṅkhātaṃ phiyanti īhāphiyaṃ. Phiyaṃ viya īhāti īhāphiyaṃ. Yathā hi phiyena nāvā icchitaṭṭhānaṃ gacchati, evaṃ īhāsaṅkhātena vīriyena taṃ taṃ kiccaṃ sādheti, icchitaṃ nibbānaṭṭhānaṃ gacchatīti adhippāyo. Taṃ pana gāhakoti pade kammaṃ. <span class="bld">Ñāṇakarenā</span>ti sabbaññutañāṇasaṅkhātena hatthena. Sabbaṃ ñeyyadhammaṃ jānātīti ñāṇaṃ, sabbaññutañāṇaṃ. Taṃ taṃ kammaṃ karotīti karo, hattho. Ñāṇasaṅkhāto karo ñāṇa karo, karaṃ viya ñāṇanti vā ñāṇakaro, tena ñāṇakarena. <span class="bld">Gāhako</span>ti daḷhagaṇhamāno hutvā. <span class="bld">Tāyā</span>ti aṭṭhamaggaṅga saṅkhātāya tāya nāvāya, <span class="bld">bahū</span>ti aneke veneyyasatte. <span class="bld">Bhavaṇṇavā</span>ti tibhavasaṅkhātā saṃsāravaṭṭasaṅkhātā vā mahāsamuddato. Tassa pana vacanattho heṭṭhā vuttoyeva.</p>\r\n<p class="bodytext"><span class="bld">Tāresī</span>ti otiṇṇesi, atikkamesīti attho. <span class="bld">Buddha</span>nti paṭividdhacatusaccadhammaṃ. <span class="bld">Aghappaha</span>nti sattānaṃ dukkhappaja </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',77,replace(replace('<p class="bodytext">hantaṃ<a name="M0.0077"></a>. Dukkhaviddhaṃsentanti attho. Atha vā aghappahanti sattānaṃ kilesappajahantaṃ. Kilesaviddhaṃsentanti attho. Ettha hi aghasaddo dukkhe kilese ca vattati. Tattha hi dukkhaṃ aghayati dukkhayatīti aghanti ca, aghayati dukkhākārena pavattatīti vā aghanti ca vuccati. Kileso pana aghayanti pāpaṃ karonti sattā etenāti aghoti vuccati. Aghadhātu pāpakaraṇe a. Aghaṃ pajahati viddhaṃsetīti aghappaho, buddho. Aghasaddūpapadapapubbahādhātu cāge. Buddho veneyyasattānaṃ dukkhañca kilesañca dhammadesanāya apanetīti attho. Ettha ca paṭhamavikappe dukkhassa appahātabbadhammattā maggena appahātabbampi phalūpacārena dukkhappajahantanti vuttaṃ. <span class="bld">Ta</span>nti munidakkhanāvikaṃ ahaṃ name namāmīti sambandhoti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Yo munidakkhanāviko yo buddhasaṅkhāto cheko nāvājeṭṭhako, maggaṅganāvaṃ aṭṭhamaggaṅgasaṅkhātaṃ nāvaṃ āruyha ārohitvā īhāphiyaṃ vīriyasaṅkhātaṃ dabbisārakaṃ, ñāṇakarena sabbaññutañāṇasaṅkhātena hatthena, gāhako daḷhaṃ gaṇhamāno hutvā tāya aṭṭhamaggaṅganāvāya, bahū aneke veneyyasatte bhavaṇṇavā tibhavasaṅkhātamahāsamuddato, tāresi otāresi, buddhaṃ paṭividdhacatusaccadhammaṃ aghappahaṃ sattānaṃ dukkhaviddhaṃsentaṃ kilesa viddhaṃsentaṃ vā taṃ munidakkhanāvikaṃ ahaṃ name namāmīti.</p>\r\n<p class="centered">Dasamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Samatiṃsatipāramisambharaṇaṃ,</p>\r\n<p class="gatha2">Varabodhidume catusaccadasaṃ;</p>\r\n<p class="gatha3">Varamiddhigataṃ naradevahitaṃ,</p>\r\n<p class="gathalast">Tibhavūpasamaṃ paṇamāmi jinaṃ.</p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',78,replace(replace('<p class="bodytext"><a name="para11"></a><span class="paranum">11</span>. Evaṃ <a name="M0.0078"></a> dasamagāthāya buddhaṃ vanditvā idāni samatiṃ satipāramisambharaṇādīhi pañcahi guṇehi thomitvā jinaṃ vanditukāmo <span class="bld">samatiṃsatipāramisambharaṇa</span>ntyādigāthamāha. Ayaṃ pana catūhi sagaṇehi racitattā dvādasakkharehi yuttā toṭakagāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘iha toṭaka mambudhisehi mita’’nti. Tassattho. Yassaṃ paṭipādaṃ ambudhisehi catūhi sagaṇehi mitaṃ pamitaṃ racitaṃ lakkhitaṃ vā vuttaṃ idha jagatichande toṭakaṃ nāmāti.</p>\r\n<p class="bodytext">Tattha pana <span class="bld">samatiṃsatipāramisambharaṇa</span>nti dasapāramiyo, dasa upapāramiyo, dasa paramatthapāramiyo cāti samatiṃsapāramiyo sambhūtaṃ, paripūritanti attho. Tāsaṃ pana viseso pāramisaddassa ca vacanattho heṭṭhā vuttoyeva. Sambharittha paripūrayitthāti sambharaṇo, jino. Saṃpubbabharadhātu pūraṇe yu. Samatiṃsapāramiyo sambharaṇoti samatiṃsatipāramisambharaṇo, jino. Dīpaṅkarabuddhassa pādamūle laddhabyākaraṇato paṭṭhāya kappasatasahassādhikāni cattāri asaṅkhyeyyāni samatiṃsapāramiyo pūrayitthāti attho. Taṃ samatiṃsatipāramisambharaṇaṃ. <span class="bld">Varabodhidume</span>ti uttamabodhirukkhamūle. Ettha ca varabodhisaddassa vacanattho heṭṭhā vuttoyeva. Davati gacchati mūlakhandhatacasākha viṭapa pallava phalehi vuḍḍhiṃ virūḷiṃ vepullaṃ rajata suvaṇṇamaṇi santā rucīhi atisobhanaṃ vā pāpuṇātīti dumo, bodhisattassa vijātadivaseyeva bodhisattena saddhiṃ saha jāto satahatthubbedho asatthadumarājā labbhati. Dudhātu gatimhi amo. <span class="bld">Apadānaṭṭhakathāyaṃ</span> pana ‘‘dunāti kampatīti dumo. Dumati pūreti ākāsatalanti vā dumo’’ti vuttaṃ. Varo bodhi varabodhi, varabodhi ca so dumo cāti varabodhidumo, tasmiṃ.</p>\r\n<p class="bodytext"><span class="bld">Catusaccadasa</span>nti cattāri ariyasaccāni maggañāṇena paṭivijjhitaṃ. Ettha ca saccasaddo anekesvatthesu dissati. Seyyathidaṃ. </p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',79,replace(replace('<p class="bodytext">Saccaṃ <a name="M0.0079"></a> bhaṇe na kujjheyyātiādīsu vācāsacce. Sacce ṭhitā samaṇabrāhmaṇātiādīsu viratisacce. Kasmā nu saccāni vadanti nānā pavādiyāse kusalāvadānātiādīsu diṭṭhisacce. Ekañhi saccaṃ na dutiyamatthītiādīsu paramatthasacce nibbāne ceva magge ca, catunnaṃ saccānaṃ kati kusalātiādīsu ariyasacce. Svāyamidhāpi ariyasacce vattatīti veditabbo. Sathanaṃ saccaṃ, avitathaṃ. Sathadhātu avitathe ṇyo. Yathā hi aggīnaṃ sabhāvo ekantaṃ uṇhoyeva hoti, na sītalo, evaṃ dukkhādīnaṃ pīḷanādi sabhāvaṃ ekantaṃ saccaṃ tathaṃ avitathaṃ anaññathaṃ hoti. Taṃ pana catubbidhaṃ hoti dukkhasaccaṃ, samudayasaccaṃ, nirodhasaccaṃ, maggasaccañcāti. Tattha dukkhasaccanti dukkhacchitaṃ khaṃ tucchanti dukkhaṃ. Kammadhārayasamāsoyaṃ. Vuttañhetaṃ <span class="bld">saccavibhaṅgaṭṭhakathāyaṃ</span> ‘‘duiti ayaṃsaddo kucchite dissati. Kucchitañhi puttaṃ duputtoti vadanti. Khaṃsaddo pana tucche. Tucchañhi ākāsaṃ khanti vuccati. Idañca paṭhamasaccaṃ kucchitaṃ. Anekaupaddavādhiṭṭhānato, tucchaṃbālajanaparikappitadhuvasubhasukhattabhāvavirahitato. Tasmā kucchitattā tucchattā ca dukkhanti vuccatī’’ti. Dukkhañca taṃ saccañcāti dukkhasaccaṃ. Tebhūmikavaṭṭaṃ labbhati.</p>\r\n<p class="bodytext">Samudayasaccanti ayati pavattati dukkhaphalaṃ etenāti ayo, lobho. Ayadhātu gatimhi a. Saṃ avasesa paccayasaṃyoge sati u dukkhuppattiyā ayo kāraṇoti samudayo. Chaṭṭhītappurisasamāsoyaṃ. Vutteñhetaṃ <span class="bld">sacca vibhaṅgaṭṭhakathāyaṃ</span> ‘‘saṃiti ca ayaṃsaddo samāgamo sametantiādīsu saṃyogaṃ dīpeti. Uiti ayaṃsaddo uppannaṃ uditantiādīsu uppattiṃ. Ayasaddo pana kāraṇaṃ dīpeti. Idañcāpi dutiyasaccaṃ avasesapaccayasamāyoge sati dukkhassuppattikāraṇaṃ. Iti dukkhassa saṃyoge uppattikāraṇattā dukkhasamudayanti vuccatī’’ti. Samudayo ca so saccañcāti samudayasaccaṃ, lobhasaṅkhātā taṇhā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',80,replace(replace('<p class="bodytext">labbhati<a name="M0.0080"></a>. Nirodhasaccanti rodhati carati pavattatīti rodho, tebhūmikavaṭṭadhammo. Rodhadhātu caraṇe ṇa. Natthi tebhūmikavaṭṭadhammassa rodho caraṇaṃ etthāti nirodho, nibbānaṃ. Atha vā nirujjhati saṃsāradukkhaṃ etthāti nirodho, nibbānaṃ. Rudhidhātu āvaraṇe ṇa. Vuttañhetaṃ <span class="bld">saccavibhaṅgaṭṭha kathāyaṃ</span> ‘‘tatiyasaccaṃ pana yasmā nisaddo abhāvaṃ. Rodha saddo cārakaṃ dīpeti. Tasmā abhāvo ettha saṃsāra cārakasaṅkhātassa dukkharodhassa sabbagatisuññattā. Samadhi gate vā tasmiṃ saṃsāracārakasaṅkhātassa dukkharodhassa abhāvo hoti tappaṭipakkhattātipi dukkhanirodhanti vuccatī’’ti. Dukkhassa vā anuppādanirodhapaccayattā dukkhanirodhanti. Nirodho ca so saccañcāti nirodhasaccaṃ. Nibbānaṃ. Maggasaccanti dukkhanirodhaṃ nibbānaṃ maggati gacchati etenāti maggo. Aṭṭhaṅgiko maggo. Maggadhātu gatiyaṃ a. Vuttañhetaṃ <span class="bld">sacca vibhaṅgaṭṭhakathāyaṃ</span> ‘‘catutthasaccaṃ pana yasmā etaṃ dukkhanirodhaṃ gacchati ārammaṇavasena tadabhimukhabhūtattā paṭipadā ca hoti dukkhanirodhappattiyā. Tasmā dukkhanirodhagāminipaṭipadāti vuccatī’’ti. Maggo ca so saccañcāti maggasaccaṃ. Aṭṭhaṅgiko maggo labbhati. Yasmā panetāni buddhādayo ariyā paṭivijjhanti, tasmā ariyasaccānītipi vuccanti. Atha vā buddhādīhi ariyehi paṭivijjhiyanti, tasmā ariyasaccānītipi vuccanti.</p>\r\n<p class="bodytext">Tesaṃ pana lakkhaṇādīni evaṃ veditabbāni. Ettha hi bādhanalakkhaṇaṃ dukkhasaccaṃ, santāpanarasaṃ, pavattipaccupaṭṭhānaṃ, pabhavalakkhaṇaṃ samudayasaccaṃ, anupacchedakaraṇarasaṃ, palibodhapaccupaṭṭhānaṃ. Santilakkhaṇaṃ nirodhasaccaṃ, accutirasaṃ, animittapaccupaṭṭhānaṃ. Niyyānalakkhaṇaṃ maggasaccaṃ, kilesappahāna karaṇarasaṃ, vuṭṭhānapaccupaṭṭhānanti. Etesu pana catūsu saccesu paṭhamatatiyasaccāni phalāni honti. Dutiya catutthasaccāni hetūni honti. Purimāni dvesaccāni dudda sattā gambhīrāni. Pacchimāni dvesaccāni gambhīrattā duddasāni.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',81,replace(replace('<p class="bodytext">Api <a name="M0.0081"></a> ca kho pana dukkhanirodhaṃ ariyasaccaṃ gambhīrañceva duddasañcāti veditabbaṃ. Ekekassa pana saccassa catunnaṃ catunnaṃ atthānaṃ vasena soḷasa saccaṭṭhā honti. Tena vuttaṃ <span class="bld">paṭisambhidā maggapāḷiyaṃ</span> ‘‘dukkhassa pīḷanaṭṭho saṅkhataṭṭho santāpaṭṭho vipariṇāmaṭṭho, ime cattāro dukkhassa dukkhaṭṭhā tathā avitathā anaññathā. Evaṃ dukkhaṃ tathaṭṭhena saccaṃ. Samudayassa āyūhanaṭṭho nidānaṭṭho saṃyogaṭṭho palibodhaṭṭho, ime cattāro samudayassa samudayaṭṭhā tathā avitathā anaññathā. Evaṃ samudayo tathaṭṭhena saccaṃ. Nirodhassa nissaraṇaṭṭho vivekaṭṭho asaṅkhataṭṭho amataṭṭho, ime cattāro nirodhassa nirodhaṭṭhā tathā avitathā anaññathā, evaṃ nirodho tathaṭṭhena saccaṃ. Maggassa niyyānaṭṭho hetvaṭṭho dassanaṭṭho adhipateyyaṭṭho, ime cattāro maggassa maggaṭṭhā tathā avitathā anaññathā, evaṃ maggo tathaṭṭhena sacca’’nti.</p>\r\n<p class="bodytext">Dassanaṃ dasaṃ, disati passatīti vā dasaṃ, catumaggañāṇaṃ. Disadhātu apekkhane a. Catunnaṃ saccānaṃ dassanaṃ assāti catusaccadaso, jino. Atha vā cattāri saccāni adassi apassīti catusaccadaso, jino. So cattāri saccāni catumaggañāṇena appaṭivijjhīti attho. Taṃ catusaccadasaṃ. Catusaccañāṇaṃ pana duvidhaṃ hoti anubodhañāṇañca, paṭivedha ñāṇañcāti. Tattha anubodhañāṇaṃ lokiyaṃ anussavādivasena nirodhe magge ca pavattati, paṭivedhañāṇaṃ lokuttaraṃ nirodhamārammaṇaṃ katvā kiccato cattāripi saccāni paṭivijjhati. Yathāha yo bhikkhave dukkhaṃ passati, dukkhasamudayampi so passati. Dukkhanirodhampi passati, dukkhanirodhagāminipaṭi padampi passatīti sabbaṃ vattabbaṃ. Catusaccapaṭivedhañāṇaṃ pana anubodhañāṇena vinā na pavattati, tasmā anubodha ñāṇatthāya pubbe catusaccakammaṭṭhānaṃ paṇḍitena samārambhi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',82,replace(replace('<p class="bodytext">tabbanti<a name="M0.0082"></a>. Nanu ca ariyasāvakā cattāri saccāni paṭivijjhanti. Atha kasmā jinoyeva catusaccadasanti thomitoti. Bhagavā pana ariyasāvakehi visiṭṭhena anaññopadesena arahattamaggañāṇena cattāri saccāni paṭivijjhanti. Tasmā bhagavāyeva catusaccadasanti abhitthavitoti. Tattha pana ṭhapetvā taṇhañca nibbānañca avasesadhammā dukkhasaccaṃ nāma. Aṭṭhasatataṇhāvicaritā samudayasaccaṃ nāma. Nibbānaṃ nirodha saccaṃ nāma. Aṭṭhaṅgiko maggo maggasaccaṃ nāma. Vuttaṃ pana <span class="bld">abhidhammatthasaṅgahe</span> –</p>\r\n<p class="gatha1">‘‘Dukkhaṃ tebhūmakaṃ vaṭṭaṃ, taṇhā samudayo bhave;</p>\r\n<p class="gathalast">Nirodho nāma nibbānaṃ, maggo lokuttaro mato’’ti.</p>\r\n<p class="bodytext"><span class="bld">Varamiddhigata</span>nti varaṃ iddhigataṃ. Uttamaṃ atītabuddhānaṃ iddhiṃ pattanti attho. Atha vā uttamānaṃ iddhīnaṃ patiṭṭhānabhūtanti attho. Ijjhanaṃ samijjhanaṃ iddhi, ijjhati samijjhati nippajjatīti vā iddhi. Iddhividhañāṇañca arahattamaggo ca labbhati. Vuttañhi <span class="bld">visuddhimagge</span> ‘‘tathā nekkhammaṃ ijjhatīti iddhīti ca arahattamaggo ijjhatīti iddhī’’ti ca. Atha vā ijjhanti sattā etāya iddhā vuddhā ukkaṃsagatā hontīti iddhi, aṭṭhasamāpattiyo ca cattāro maggā ca labbhanti. Idhadhātu vuḍḍhiyanti paccayo. Atha vā tapaccayo itthiliṅgajotako īpaccayo ca. Iddhiṃ agaminti iddhigato, jino. Atha vā gacchati patiṭṭhahati etthāti gato, varānaṃ uttamānaṃ iddhīnaṃ gato patiṭṭhānabhūtoti varamiddhigato, jino. Taṃ varamiddhigataṃ. Kiñcāpi pana ariyasāvakā iddhippattā honti, buddhā pana visesato etāya abhiññāya iddhā samiddhā vuddhā atiukkaṃsagatā honti, tasmā buddhoyeva varamiddhigatanti abhitthavituṃ arahatīti veditabbo.</p>\r\n<p class="bodytext"><span class="bld">Naradevahita</span>nti manussadevānaṃ lokiyalokuttara saṅkhātaṃ hitaṃ payojanaṃ dhāraṇaṃ. Tesaṃ pana attho </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',83,replace(replace('<p class="bodytext">heṭṭhā <a name="M0.0083"></a> vuttoyeva. <span class="bld">Tibhavūpasama</span>nti tibhavaupasamaṃ. Punabbhavābhinibbattiyā abhāvato kāmarūpaarūpasaṅkhātehi tīhi bhavehi tīsu bhavesu punabbhavābhinibbattito vā upasamitaṃ nibbutaṃ niruddhanti attho. Bhavanti sattā etthāti bhavo, tayo bhavā tibhavā, tehi upasamitthāti tibhavūpasamo, jino. Taṃ tibhavūpasamaṃ. Bhagavato pana bodhipallaṅkeva kāmarūpārūpasaṅkhātesu tīsu bhavesu paṭisandhijanakānaṃ avijjātaṇhāmūlakānaṃ puññādisaṅkhārānaṃ arahattamaggañāṇena niravasesaṃ pahīnattā punabbhavābhinibbatti natthi. Tasmā bhagavantaṃyeva visesena tibhavūpasamanti thometīti vuttaṃ hoti. <span class="bld">Jina</span>nti pañcamārajayantaṃ buddhaṃ. <span class="bld">Paṇamāmī</span>ti ahaṃ ñāṇasampayuttacittena ādaraṃ vandāmīti yojanā.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Samatiṃsati sambharaṇaṃ samatiṃsapāramiyo sambhūtaṃ paripūritaṃ, varabodhidume uttamabodhirukkhamūle, catusaccadasaṃ cattāri ariyasaccāni maggañāṇena passantaṃ paṭivijjhantaṃ, varamiddhigataṃ uttamaṃ atīta buddhānaṃ iddhipattaṃ uttamānaṃ iddhīnaṃ patiṭṭhānabhūtaṃ vā, naradevahitaṃ manussadevānaṃ lokiyalokuttarasaṅkhātaṃ payojanaṃ dhāraṇaṃ, tibhavūpasamaṃ kāmarūpaarūpasaṅkhātehi tīhi bhavehi upasamitaṃ, kāmarūpārūpasaṅkhātesu tīsu bhavesu punabbhavā bhinibbattito upasamitaṃ nibbutaṃ vā, jinaṃ pañcamārajayantaṃ buddhaṃ paṇamāmi ahaṃ ñāṇasampayuttacittena ādaraṃ vandāmīti.</p>\r\n<p class="centered">Ekādasamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> Satapuññajalakkhaṇikaṃ virajaṃ,</p>\r\n<p class="gatha2">Gaganūpamadhiṃ dhitimerusamaṃ;</p>\r\n<p class="gatha3">Jalajūpamasītalasīlayutaṃ,</p>\r\n<p class="gathalast">Pathavīsahanaṃ paṇamāmi jinaṃ.</p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',84,replace(replace('<p class="bodytext"><a name="para12"></a><span class="paranum">12</span>. Evaṃ <a name="M0.0084"></a> ekādasamagāthāya jinaṃ vanditvā idāni satapuññajalakkhaṇikantyādīhi chahi guṇehi thomitvā jinaṃ vanditu kāmo <span class="bld">satapuññajalakkhaṇika</span>ntyādigāthamāha. Ayaṃ pana catūhi sa gaṇehi racitattā dvādasakkharehi yuttā toṭakagāthāti daṭṭhabbā. Sādhakaṃ pana anantaragāthāvaṇṇanāyaṃ vuttameva.</p>\r\n<p class="bodytext">Tattha <span class="bld">satapuññajalakkhaṇika</span>nti anekapāramīpuññatejena jātena dvattiṃsamahāpurisalakkhaṇena sampannaṃ. Ettha ca satasaddo anekasaṅkhyāvācako. Satatejo sahassaraṃ sītiādīsu viya. Satena anekena pāramīpuññatejena jātanti satapuññajaṃ, lakkhiyati lakkhitabbaṃ buddhadabbaṃ etehīti lakkhaṇaṃ, dvattiṃsamahāpurisalakkhaṇādi. Lakkhadhātu dassanaṅkesuyu. Satapuññajañca taṃ lakkhaṇañcāti satapuññaja lakkhaṇaṃ. Taṃ assa atthīti satapuññajalakkhaṇiko, jino. Taṃ. Bhagavā hi anantesu cakkavāḷesu sabbe sattā ekekaṃ puññakammaṃ satakkhattuṃ kareyyuṃ. Ettakehi janehi kataṃ puññakammaṃ bodhisatto ekova ekekaṃ sataguṇaṃ katvā nibbattena pāramīpuññatejena jātehi dvattiṃsa mahāpurisalakkhaṇehi ca asītianubyañjana lakkhaṇe hi ca sampannoti satapuññajalakkhaṇikanti thometīti adhippāyo. Vuttañhi <span class="bld">buddhavaṃsaṭṭhakathāyaṃ</span> –</p>\r\n<p class="bodytext">‘‘Satapuñña lakkhaṇoti anantesu cakkavāḷesu sabbe sattā ekekaṃ puññakammaṃ satakkhattuṃ kareyyuṃ, ettakehi janehi katakammaṃ bodhisatto sayameva eko sataguṇaṃ katvā nibbatto. Tasmā satapuññalakkhaṇoti vuccati. Keci pana satena satena puññakammena nibbattaṃ ekekalakkhaṇoti vadanti. Evaṃ sante yo koci buddho bhaveyyāti. Taṃ aṭṭhakathāsu paṭikkhitta’’nti. <span class="bld">Pātheyyaṭṭhakathāyaṃ</span> pana ‘‘sata puññalakkhaṇanti satena satena puññakammena nibbattaṃ ekekaṃ </p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',85,replace(replace('<p class="bodytext">lakkhaṇaṃ<a name="M0.0085"></a>. Evaṃ sante yo koci buddho bhaveyyāti na rocayiṃsu. Anantesu pana cakkavāḷesu sabbe sattā ekekaṃ kammaṃ satakkhattuṃ kareyyuṃ. Ettakehi janehi kataṃ kammaṃ bodhisatto ekova ekekaṃ sataguṇaṃ katvā nibbatto. Tasmā satapuññalakkhaṇoti imamatthaṃ rocayiṃsū’’ti vuttaṃ. Dvattiṃsamahāpurisalakkhaṇaṃ pana upari āvibhavissatīti.</p>\r\n<p class="bodytext"><span class="bld">Viraja</span>nti rāgarajādīnaṃ abhāvā vigatarajaṃ. Rañjati sañjati rūpādiārammaṇesūti rajo, rāgādirajo. Ranjadhātu rāge a. Vigato rajo assāti virajo, jino. Taṃ virajaṃ. Jino hi bodhipallaṅkeyeva sabbakilesamāra rajassa jayitattā vigatarajoti attho. <span class="bld">Gaganūpamadhi</span>nti ettha dhī vuccati sabbaññutañāṇaṃ. Tasmā ākāsūpamasabbaññuta ñāṇavantanti attho. Gacchanti ettha vihaṅgamādayoti gaganaṃ, ākāso. Gamudhātu gatiyaṃ yu. Makārassa go. Upamiyate upamā. Bhāvasādhanoyaṃ. Upapubbamādhātu parimāne a. Sabbañeyyadhammaṃ dharati jānātīti dhī, sabbaññuta ñāṇaṃ. Dharadhātu ñāṇe kvi, itthiliṅgajotakaīpaccayo ca. Gaganena ākāsena yuttā upamā gaganūpamā, taṃ sadisaṃ dhī sabbaññutañāṇaṃ yassāti gaganūpamadhi, jino. Taṃ gaganūpamadhiṃ. Ayaṃ pana anantādiguṇahīnattā dhammahīnopamā nāma. Vuttañhi <span class="bld">subodhālaṅkāre</span> ‘‘dhammahīnāmukhambhoja, sadisaṃ munino itī’’ti. Yathā hi ākāso anantoti vuttattā ākāso ananto hutvā vittharati, evaṃ bhagavato anantaṃ buddhañāṇanti vuttattā sabbaññutañāṇassa anantārammaṇattā ca ñāṇaṃ anantaṃ hutvā vittharati. Tasmā gaganūpamadhinti thometīti.</p>\r\n<p class="bodytext"><span class="bld">Dhitimerusama</span>nti ettha dhiti vuccati samādhi. Sinerupabbata sadisaṃ acalasamādhivantanti attho. Dhiyati ṭhapiyati </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',86,replace(replace('<p class="bodytext">sampayuttadhamme <a name="M0.0086"></a> ekārammaṇe etenāti dhiti, bhagavato lokiyalokuttarajhānasamādhi. Dhidhātu dhāraṇeti. Raṃsiyā andhakāraṃ mināti hiṃsatīti meru, pabbatarājā. Mīdhātu hiṃsāyaṃ rupaccayo. Imaṃ vacanatthañhi sinerussa pācīnapassaṃ rajatamayaṃ, dakkhiṇapassaṃ maṇimayaṃ, pacchimapassaṃ phallikamayaṃ, uttarapassaṃ suvaṇṇamayaṃ hotīti vuttavacanaṃ sandhāya vuttaṃ. Atha vā mināti hiṃsati sabbe pabbate attano uccataraṭṭhenāti meru, merunā samo dhiti samādhi yassāti dhitimerusamo, jino. Taṃ dhitimeru samaṃ. Yathā hi merurājā aṭṭhahi disāhi āgatehi vātehi na kampati na calati, evaṃ jino kāmacchandādinīvaraṇe pajahitvā lokuttarajjhānasamādhinā samannāgatattā aṭṭhahi lokadhammehi ca micchādiṭṭhivātehi ca na kampati na calatīti dhitimerusamanti thometi.</p>\r\n<p class="bodytext"><span class="bld">Jalajūpamasītalasīlayuta</span>nti udakajātappadumasadisena atisītalena asaṅkhyeyyasīlena saṃyuttaṃ. Vuttañhi <span class="bld">apadāna pāḷiyaṃ</span> ‘‘sīlaṃ tassa asaṅkhyeyya’’nti. <span class="bld">Tadaṭṭhakathāyañca</span> ‘‘bhagavato pana sīlaṃ asaṅkhyeyyamevā’’ti. Jalati dibbatīti jalaṃ, udakaṃ. Jaladhātu dittiyaṃ a. Jale udake jātaṃ jalajaṃ, padumaṃ. Jalajena padumena upamaṃ sadisanti jalajūpamaṃ, sīlaṃ. Sukhatthikehi seviyatīti sītalaṃ, sīlaṃ. Sidhātu sevāyaṃ talapaccayo. Atha vā sītaguṇaṃ lāti gaṇhātīti sītalaṃ, sīlaṃ. Sītasaddūpapada lādhātu gahaṇe kvi. Udake pana jātaṃ padumaṃ udakena uṇhassa samitattā sītalaṃ viya. Sīlaṃ pana buddhassa kilesatā panassa sametattā accantasītalaṃ hoti. Surakkhitañhi visuddhaṃ sīlaṃ kilesappahānena sattānaṃ kilesapariḷāhaṃ upasameti na haritacandanaṃ muttāhāro maṇi udakaṃ vāto candakiraṇo ca. Tasmā sīlameva sītalanti accantasītalanti vuccati. Tena vuttaṃ <span class="bld">visuddhimagge</span> –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',87,replace(replace('<p class="gatha1">‘‘Na <a name="M0.0087"></a> taṃ sajaladā vātā, na cāpi haricandanaṃ;</p>\r\n<p class="gatha2">Neva hārā na maṇayo, na candakiraṇaṅkurā;</p>\r\n<p class="gatha3">Samayantīdha sattānaṃ, pariḷāhaṃ surakkhitaṃ;</p>\r\n<p class="gathalast">Yaṃ sametidaṃ ariyaṃ, sīlaṃ accantasītala’’nti.</p>\r\n<p class="bodytext">Ayaṃ pana tassa gāthādvayassattho. Surakkhitaṃ suṭṭhurakkhitaṃ ariyaṃ visuddhaṃ accantasītalaṃ idaṃ sīlaṃ idhaloke sattānaṃ yaṃ pariḷāhaṃ sameti, taṃ pariḷāhaṃ sajaladā udakadadena meghena saha pavattā vātā sītavātā na samayanti haritacandanañcāpi na samayati. Hārā muttāhārā neva samayanti. Maṇayo veḷuriyādimaṇiyo na samayanti. Candakiraṇaṅkurā candassa raṃsiaṅkurā na samayantīti. Sīlati samādhiyati kāyakammādīnaṃ susīlyavasena na vippakiratīti sīlaṃ. Sīladhātu samādhimhi a. Atha vā sīlanti samādahanti cittaṃ etenāti sīlaṃ, sīlenti vā etena kusale dhamme upadhārenti ābhuso dhārenti sādhavoti sīlaṃ, cetanādhammo, kusalacittuppādo vā labbhati. Sīladhātu upadhāraṇe a. Vuttañhetaṃ <span class="bld">visuddhimaggaṭṭhakathāyaṃ</span> ‘‘kenaṭṭhena sīlanti sīlanaṭṭhena sīlaṃ. Kimidaṃ sīlaṃ nāma, samādhānaṃ vā, kāyakammādīnaṃ susīlyavasena avippakiṇṇatāti attho. Upadhāraṇaṃ vā, kusalānaṃ dhammānaṃ patiṭṭhānavasena ādhārabhāvoti attho’’ti. Jalajūpamena padumasadisena sītalena accantasītalena sīlena asaṅkhyeyyasīlena yuttoti jalajūpamasītalasīlayuto, jino. Taṃ jalajūpamasītalasīlayutaṃ. Yathā hi udake jātaṃ padumaṃ jalena sītalaṃ hoti, evaṃ bhagavato maggasīlena kilesupatāpanassa pahīnattā taṃ sītalaṃ accantasītalaṃ. Tena sampayuttoti jalajūpamasītalasīla yutanti thometīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Pathavīsahana</span>nti pathavī viya khantisampannaṃ khantivantaṃ vā. Pathanti patiṭṭhahanti ettha tarupabbatādayoti pathavī, bhūmi. Pathadhātu </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',88,replace(replace('<p class="bodytext">patiṭṭhāyaṃ <a name="M0.0088"></a> vīpaccayo. Atha vā puthu hutvā jātanti pathavī. Jātataddhitatthe vīpaccayo. Sahayate khamiyate sahanaṃ, sahati khamatīti vā sahanaṃ, adhivāsanakhanti. Sahadhātu khantiyaṃ yu. Pathavī viya sahanaṃ khamanaṃ yassa soti pathavīsahano, jino. Taṃ pathavīsahanaṃ. Yathā hi pathavī attano upari pakkhittaṃ sucimpi asucimpi sabbaṃ sahati, na paṭighānunayaṃ karoti, evaṃ jino iṭṭhārammaṇampi aniṭṭhārammaṇampi sabbaṃ sahati, na tattha paṭighānunayaṃ karotīti pathavīsahananti thometīti adhippāyo. Tena vuttaṃ <span class="bld">apadānapāḷiyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Lābhālābhe na sajjanti, sammānanavimānane;</p>\r\n<p class="gathalast">Pathavīsadisā buddhā, tasmā te na virādhiyā’’ti.</p>\r\n<p class="bodytext">Ahaṃ jinaṃ paṇamāmi vandāmīti sambandho.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Satapuññajalakkhaṇikaṃ anekapāramīpuññatejena jātena dvattiṃsamahāpurisalakkhaṇena sampannaṃ, virajaṃ rāgarajādīnaṃ abhāvā vigatarajaṃ, gaganūpamadhiṃ ākāsūpamasabbaññutañāṇavantaṃ, dhitimerusamaṃ sinerupabbatasadisaṃ acalasamādhivantaṃ, jalajūpamasītala sīlayutaṃ. Udakajātappadumasadisena accantaatisītalena asaṅkhyeyyasīlena sampayuttaṃ, pathavīsahanaṃ pathavī viya khantisampannaṃ, khantivantaṃ vā, jinaṃ pañcamāravijayaṃ buddhaṃ ahaṃ sakkaccaṃ paṇamāmi vandāmīti.</p>\r\n<p class="centered">Dvādasamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> Yo buddho sumati dive divākarova,</p>\r\n<p class="gatha2">Sobhanto ratijanane silāsanamhi;</p>\r\n<p class="gatha3">Āsīno sivasukhadaṃ adesi dhammaṃ,</p>\r\n<p class="gathalast">Devānaṃ tamasadisaṃ namāmi niccaṃ.</p>','\r',char(13)),'\n',char(10)),'-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',89,replace(replace('<p class="bodytext"><a name="para13"></a><span class="paranum">13</span>. Evaṃ <a name="M0.0089"></a> dvādasamagāthāya jinaṃ vanditvā idāni tāvatiṃsapure mātudevaputtappamukhānaṃ upapattidevānaṃ abhidhammaṃ desaka guṇena abhitthavitvā vanditukāmo <span class="bld">yo buddho sumati</span>tyādigāthamāha. Ayaṃ pana ma na ja ra gaṇehi ca ekagarunā ca tīhi yatīhi ca dasahi yatīhi ca racitattā terasakkhara saṃyuttā pahassiṇīgāthāti daṭṭhabbā. Vuttañhetaṃ <span class="bld">vuttodaye</span> ‘‘mnā jarā go tidasayatippahassiṇī sā’’ti.</p>\r\n<p class="bodytext">Tassattho. Yassaṃ paṭipādaṃ mnā magaṇa nagaṇā ca jarā ja gaṇa ragaṇā ca go garu ca tidasayati tiyati ca dasayati ca ce siyā, sā gāthā pahassiṇīgāthā nāmāti. Tattha sumati yo buddho dhammaṃ devānaṃ adesīti sambandho. <span class="bld">Yo</span>ti aniyamavācakasabbanāmapadaṃ. Tassa vacanattho na kātabbo. Vuttañhi <span class="bld">saddavidūhi</span> –</p>\r\n<p class="gatha1">‘‘Ruḷhīkhyātaṃ nipātañcu, pasaggālapanaṃ tathā;</p>\r\n<p class="gathalast">Sabbanāmanti etesu, na kato viggaho chasū’’ti.</p>\r\n<p class="bodytext">Atha vā sabbanāmabhāvaṃ yātīti yo, aniyamavācaka bhāvaṃ yātīti vā yo. Sabbanāmabhāvena yāti pavattatīti vā yo. Aniyamavācakabhāvena yāti pavattatīti vā yo. Sabbanāmabhāvena yātabbo ñātabboti vā yo, yātabbo ñātabbo etena aniyamatthoti vā yo. Sabbanāmasaṅkhātaṃ yaitipadaṃ labbhati. Yo yādi soti attho. <span class="bld">Buddho</span>ti paṭividdhacatusaccadhammo, paṭividdhasabbañeyyadhammo vā. Tassa pana vacanattho heṭṭhā vuttoyeva. <span class="bld">Sumatī</span>ti sundarasabbaññutañāṇavāto, desanāñāṇavāto vā. Adesi dhammaṃ devānanti vuccamānattā hi idha sabbaññuta ñāṇaṃ sumatīti vuccati. Manati jānāti sabbañeyyadhammaṃ desetabbadhammaṃ vāti mati. Manadhātu ñāṇeti. Sundarā mati sumati, sabbañeyyadhammānaṃ yathāsabhāvajānanasamatthaṃ tesaṃ dese </p>','\r',char(13)),'\n',char(10)),'-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',90,replace(replace('<p class="bodytext">tabbappakārajānanasamatthaṃ <a name="M0.0090"></a> bodhetabbapuggalānaṃ āsayādhi muttiyādivibhāvanasamatthañcāti tisamatthasampannaṃ sabbaññutañāṇaṃ labbhati. Sumatīti iminā hi sabbaññutañāṇameva abhidhammakathāya samuṭṭhānabhāve samatthaṃ. Nāññanti imamatthaṃ dīpento abhidhammakathāya anaññasādhāraṇabhāvaṃ dasseti. Sundarā mati assa asmiṃ vā atthīti sumati, buddho. Samāsanta taddhitāyaṃ. Tadassatthītivī cāti sutte casaddena apaccayo. Yathā pītisukhanti. Vuttañhi <span class="bld">pārājikaṇḍaṭṭhakathāyaṃ</span> ‘‘ayañca pīti idañca sukhaṃ assa jhānassa asmiṃ vā jhāne atthīti idaṃ jhānaṃ pītisukhanti vuccatī’’ti.</p>\r\n<p class="bodytext"><span class="bld">Dive</span>ti divase. Sūriyobhāsena dibbati etthāti divo. Divudhātu jutiyaṃ a. Tasmiṃ dive. <span class="bld">Divā karovā</span>ti sūriyo iva. Divādinaṃ karotīti divākaro, sūriyo. Divāsaddūpa pada karadhātu karaṇe a. Atha vā divā divase karo raṃsi assāti divākaro. Karasaddo hi raṃsivācako. <span class="bld">Sobhanto</span>ti virocamāno hutvā. Subhati virocati chabbaṇṇaraṃsiādīhīti sobhanto, buddho. Subhadhātu sobhe, dittiyaṃ vā antapaccayo. Bhagavā hi chabbaṇṇaraṃsīhi dvattiṃsamahāpurisalakkhaṇehi asītyānubyañjanalakkhaṇehi ca devānaṃ majjhe sobhati virocatīti attho. <span class="bld">Ratijanane</span>ti devānaṃ atappakapītisomanassuppādane, pemuppādane vā. Ramanaṃ rati. Ramudhātu kīḷāyanti. Taṃ janeti uppādetīti ratijananaṃ. Silāsanaṃ. Ratisaddūpapada janadhātu janane yu. Tasmiṃ ratijanane.</p>\r\n<p class="bodytext"><span class="bld">Silāsanamhī</span>ti tidasālaye, pāricchattakamūle jāte nikkhitte vā dīghato saṭṭhiyojane vitthārato paṇṇāsa yojane puthulato pannarasayojane vaṇṇato jayasumanapupphasadise rattakampalavaṇṇe vā saṇhasukhume paṇḍukampa lanāmake silāsane. Sakkena seviyatīti silaṃ. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',91,replace(replace('<p class="bodytext">Pāsāṇo<a name="M0.0091"></a>. Sidhātu sevāyaṃ alapaccayo. Āsiyati etthāti āsanaṃ. Atha vā āgantvā seviyati etthāti āsanaṃ. Āpubbasidhātu sevāyaṃ yu. Silaṃeva āsanaṃ silāsanaṃ. Paṇḍukampalasilāsanaṃ labbhati. Tasmiṃ silāsanamhi. <span class="bld">Āsīno</span>ti dasahi cakkavāḷasahassehi āgamma sannisinnānaṃ devānaṃ gaṇena parivārito hutvā nisīdanto. Āsīdanaṃ nisajjanaṃ āsīno. Āsamantato sīdati vasatīti vā āsīno. Abhidhammakathābhimukhaṃ sīdati nisajjatīti vā āsīno, buddho. Āpubbasīdadhātu nisajjāyaṃ. Visaraṇa gatyāvasānesu vā tapaccayo. Tassa īṇādeso, ṇassanattaṃ, dhātvantalopo. <span class="bld">Sivasukhada</span>nti nibbānasukhadāyakaṃ. Ariyehi seviyatīti sivaṃ, nibbānaṃ. Sidhātu sevāyaṃ avo. Atha vā rāgaggiādīhi samati upasamati etthāti sivaṃ, nibbānaṃ. Samudhātu upasame rivo. <span class="bld">Abhidhānappadīpikaṭīkāyaṃ</span> pana ‘‘sivaṃ khemabhāvaṃ karotīti sivaṃ, saṃsārabhīrukehi sevitabbattā vā sivaṃ. Yadādinā vapaccayo’’ti vuttaṃ. Sukhayatīti sukhaṃ. Yassuppajjati, taṃ sukhitaṃ karotīti attho. Atha vā suṭṭhu dukkhaṃ khanatīti sukhaṃ. Supubba khanudhātu avadāraṇe kvi. Sivassa nibbānassa sukhaṃ sivasukhaṃ, taṃ dadātīti sivasukhadaṃ, abhidhammadhammo. Sivasukhasaddūpapadadādhātu a. Yo hi abhidhammadhammaṃ suṇāti, so tassa nibbānasukhaṃ dadātīti attho.</p>\r\n<p class="bodytext"><span class="bld">Adesi dhammaṃ devāna</span>nti devānaṃ dhammaṃ adesīti sambandho. Ettha ca <span class="bld">dhamma</span>nti nānānayavicittaṃ gambhīraṃ saṇhasukhumaṃ duddasaṃ abhidhamma dhammaṃ. Attano sabhāvaṃ dhāretīti dhammo, paṇḍitehi dhāriyati na bālehīti vā dhammo, abhidhammapiṭakaṃ. Dharadhātu dhāraṇe rammapaccayo. Taṃ dhammaṃ. <span class="bld">Devāna</span>nti pubbe manussaloke mātubhūtadevaputtappamukhānaṃ upapattidevānaṃ. Vuttañhetaṃ <span class="bld">aṭṭhasālinīaṭṭhakathāyaṃ</span> –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',92,replace(replace('<p class="gatha1">‘‘Mātaraṃ <a name="M0.0092"></a> pamukhaṃ katvā, tassā paññāya tejasā;</p>\r\n<p class="gathalast">Abhidhammakathāmaggaṃ, devānaṃ sampavattayī’’ti.</p>\r\n<p class="bodytext">Ettha ca mātarantipadassa pubbe manussaloke mātubhūtaṃ devaputtanti attho daṭṭhabbo. Vuttañhetaṃ <span class="bld">jinālaṅkārapāḷiyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Tato puttaṃ gahetvāna, gatā mātā sakaṃ gharaṃ;</p>\r\n<p class="gathalast">Sattamiyaṃ cutā gantvā, devaputtattamāgamī’’ti.</p>\r\n<p class="bodytext"><span class="bld">Taṭṭīkāyañca</span> ‘‘bodhisattamātā gabbhavuṭṭhānato sattame divase kālaṅkatvā tusitapure devaputto hutvā nibbattī’’ti vuttaṃ. Ettha ca devaputtasaddena vuttameva. <span class="bld">Kāḷudāyī theragāthāaṭṭhakathāyañca</span> ‘‘parivāritāti ca itthiliṅganiddeso purimattabhāvasiddhaṃ itthibhāvaṃ devatābhāvaṃ vā sandhāya kato. Devūpapatti pana purisabhāvena jātā’’ti vuttaṃ. <span class="bld">Jātakaṭīkāyañca</span> ‘‘devorohaneti mātudevaputtassa temāsaṃ abhidhammapiṭakaṃ desetvā devorohanakāle’’ti mātudevaputtasaddena vuttaṃ. <span class="bld">Abhidhammāvatāraṭīkāyañca</span> ‘‘devānanti tadā dhammapaṭiggāhakānaṃ dasasahassacakkavāḷādhivāsīnaṃ mātudevaputtappamukhānaṃ upapattidevāna’’nti mātudevaputta saddena vuttaṃ. <span class="bld">Sirīmahāmāyāsatthe</span> ca ‘‘sirīmahāmāyā devīpi sattadivaseyeva vasitvā kālaṅkatvā attano puññānubhāvena tusitabhavane devaputto hutvā nibbattī’’ti devaputtasaddena vuttaṃ. <span class="bld">Maṇidīpaṭīkāyañca</span> ‘‘mātaranti pubbe manussaloke mātubhūtaṃ devaputtanti attho. Sirīmahāmāyā hi bodhisattaṃ vijāyitvā sattāhamattaṃ ṭhatvā ito cavitvā tusitabhavane purisabhāveneva nibbatto, na itthibhāvenā’’ti vuttaṃ. Tasmā mātarantipadassa mātubhūtaṃ devaputtanti attho mukhyena daṭṭhabbo.</p>\r\n<p class="gatha1"><span class="bld">Madhusārattha dīpanīṭīkāyaṃ</span> pana –</p>\r\n<p class="gatha2">‘‘Suddhodano nāma pitā mahesino,</p>\r\n<p class="gathalast">Buddhassa mātā pana māyā nāma.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',93,replace(replace('<p class="gatha1">Bodhisattaṃ <a name="M0.0093"></a> parihariya kucchinā,</p>\r\n<p class="gatha2">Kāyassa bhedā tidivasmiṃ modati;</p>\r\n<p class="gatha3">Sā gotamī kālaṃkatā ito cutā,</p>\r\n<p class="gatha2">Dibbehi kāmehi samaṅgibhūtā;</p>\r\n<p class="gatha3">Āmodati kāmaguṇehi pañcahi,</p>\r\n<p class="gathalast">Parivāritā devagaṇehi tehī’’ti; <span class="bld">Therāpāḷi</span>;</p>\r\n<p class="bodytext"><span class="bld">Tadaṭṭhakathāyaṃ</span> ‘‘parivāritāti ca itthiliṅganiddeso purimatta bhāvasiddhaṃ itthibhāvaṃ devatābhāvaṃ vā sandhāya kato. Devūpapatti pana purisabhāvena jātāti vuttattā mātaranti manussattabhāvavasena vutta’’nti vadanti. Theraṭṭhakathā pana nasaṅgahāruḷhā. Aññaṭṭhakathāṭīkāsu ca tādisaṃ vacanaṃ natthi. Patthanākāle ca buddhamātā hutvā pacchā puriso homīti patthanā natthi. Tiṃsadhammatāyañca buddhamātuyā purisabhāvo nāma natthi. Tasmā taṃ vīmaṃsitabbantipi vidū vadantīti vuttaṃ. Taṃ pana na gahetabbaṃ. Kasmā, saṃsayavacanena vīmaṃsitabbanti vidūvadantīti ca vuttattā ca yathāvuttaṭṭhakathāṭīkāvacanehi ujukaṃ viruddhattā ca. Devatāsaddo hi devaputte brahme devadhītāya ca vattati. Tathāhesa ‘‘atha kho aññatarā devatā abhikkantāya rattiyā abhikkantavaṇṇā’’tiādīsu devaputte. Vuttañhi <span class="bld">khuddakapāṭhaaṭṭhakathāyaṃ</span> ‘‘aññatarāti aniyamita niddeso. Sā hi nāmagottato apākaṭā. Tasmā aññatarāti vuttā. Devoyeva devatā. Itthipurisasādhāraṇamevetaṃ. Idha pana purisoeva. So devaputto. Kintusādhāraṇanāmavasena devatāti vutto’’ti. <span class="bld">Sunītattheraapadāna pāḷiyaṃ</span> āgatāsu ‘‘tathā tā devatā sattasatā uḷārā brahmā vimānā abhinikkhamitvā’’tiādīsu brahme. <span class="bld">Padīpavimānavatthupāḷiyaṃ</span> vuttāsu –</p>\r\n<p class="gatha1">‘‘Abhikkantena vaṇṇena, yā tvaṃ tiṭṭhati devate;</p>\r\n<p class="gathalast">Obhāsentī disā sabbā, osadhī viya tārakā’’ti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',94,replace(replace('<p class="bodytext">Ādīsu <a name="M0.0094"></a> devadhītāya. Evaṃ pana parivāritāsaddopi veditabbo. Tasmā ācariyadhammapālattherena katāya theraṭṭhakathāya vuttavacanaṃ jātakaṭīkāya vuttavacanañca ācariyasumaṅgalattherena katāya abhidhammāvatāraṭīkāya vuttavacanaṃ pamāṇaṃ katvā mātudevaputtoti atthoyeva daṭṭhabbo. Etarahi pana keci ācariyā therāpāḷiyaṃ āgataṃ parivāritāsaddañca madhusāratthadīpanīṭīkāvacanañca paṭicca mātudevadhītāti vadanti. Taṃ pana tesaṃ matimattanti daṭṭhabbaṃ. <span class="bld">Adesī</span>ti mahākaruṇāpubbaṅgamena sabbaññutañāṇena kathesi. Asadisaṃ tanti sambandho. Ettha ca <span class="bld">ta</span>nti buddhaṃ. <span class="bld">Asadisa</span>nti appaṭipuggalaṃ. Natthi etassa sadiso paṭipuggaloti asadiso, buddho. Sadevake loke sīlādiguṇena buddhena sadiso koci puggalo natthīti attho. Tenāha <span class="bld">vinayamahāvaggapāḷiyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Na me ācariyo atthi, sadiso me na vijjati;</p>\r\n<p class="gathalast">Sadevakasmiṃ lokasmiṃ, natthi me paṭipuggalo’’ti.</p>\r\n<p class="bodytext"><span class="bld">Nicca</span>ntisatataṃ samitaṃ, sabbakālaṃ nirantaranti attho. Asadisaṃ taṃ buddhaṃ niccaṃ namāmīti sambandhoti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Sumati sundarasabbaññuta ñāṇavāto, desanāñāṇavāto vā. Yo yādiso buddho. Dive divase divākarova sūriyoiva, sobhanto virocamāno hutvā, ratijanane devānaṃ atappakapīti somanassuppādane, pemuppādane vā, silāsanamhi paṇḍu kampalanāmake silāsane, āsīno nisīdanto hutvā sivasukhadaṃ nibbānasukhadāyakaṃ, dhammaṃ abhidhammadhammaṃ, devānaṃ mātu pubbadevaputtappamukhānaṃ upapattidevānaṃ adesi kathesi. Asadisaṃ appaṭipuggalaṃ taṃ buddhaṃ ahaṃ niccaṃ satataṃ namāmi vandāmīti.</p>\r\n<p class="centered">Terasamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',95,replace(replace('<p class="gatha1">Yo <a name="M0.0095"></a> pādapaṅkajamuduttalarājikehi,</p>\r\n<p class="gatha2">Lokehi tīhivikalehi nirākulehi;</p>\r\n<p class="gatha3">Sampāpuṇe nirupameyyatameva nātho,</p>\r\n<p class="gathalast">Taṃ sabbalokamahitaṃ asamaṃ namāmi.</p>\r\n<p class="bodytext"><a name="para14"></a><span class="paranum">14</span>. Evaṃ terasamagāthāya buddhaṃ vanditvā idāni buddhassa pādatale vicittappavattehi aṭṭhasatacakkalakkhaṇehi thomitvā vanditukāmo <span class="bld">yo pādapaṅkaja</span>tyādigāthamāha. Ayaṃ pana ta bha ja ja gaṇehi ca garudvayena racitattā cuddasakkharehi yuttā vasantatilakāgāthāti daṭṭhabbā. Vuttañhetaṃ <span class="bld">vuttodaye</span> ‘‘vuttā vasantatilakā ta bha jā jagā go’’ti. Tassāyamattho. Yassaṃ paṭipādaṃ ta bha jā ta bha ja gaṇā ca jagā jagaṇagarukā ca go garu ca ce bhavanti, sā gāthā vasantatilakāgāthāti chandācariyehi vuttāti.</p>\r\n<p class="bodytext">Tattha pana yo nātho avikalehi nirākulehi pādapaṅkajamuduttalehitīhi lokehi nirupameyyataṃ sampāpuṇeeva sabbalokamahitaṃ asamaṃ taṃ nāthaṃ ahaṃ namāmīti sambandho. Ettha pana saddakkamena adassetvā atthakkamena vaṇṇanaṃ dassayissāma. <span class="bld">Yo nātho</span>ti yo yādiso sabbasattānaṃ paṭisaraṇo buddho. Taṃ pana padaṃ sampāpuṇeevātipade kattubhāvena sambandhitabbaṃ. Nāthassa pana vacanattho heṭṭhā vuttoyeva. <span class="bld">Avikalehī</span>ti kenaci avekallehi sabbapādacakkalakkhaṇehi sampuṇṇehīti attho. Virūpaṃ kalati gacchatīti vikalo. Vipubbakaladhātu gatiyaṃ a. Natthi kiñci vikalaṃ etissā rājiyāti avikalā, pādarāji. Pādadvaye paribyattaṃ saṇṭhānarūpaṃ kiñci vikalaṃ natthīti attho. Tehi avikalehi.</p>','\r',char(13)),'\n',char(10)),'-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',96,replace(replace('<p class="bodytext"><span class="bld">Nirākulehī</span>ti <a name="M0.0096"></a> ākulavirahitehi. Pādacakkalakkhaṇānaṃ aññamaññaṃ asaṃmissehi asaṃsaṭṭhehīti attho. Ābhuso kulati bandhatīti ākulaṃ, āpubba kuladhātu bandhane a. Atha vā ābhuso kulati āvarati sobhananti ākulaṃ, natthi ākulaṃ aññamaññamissaṃ etissā pādarāji yāti nirākulā, pādarāji. Tehi nirākulehi. Taṃ pana padadvayaṃ pādapaṅkajamuduttalarājikehītipade visesanaṃ. <span class="bld">Pādapaṅkajamuduttalarājikehī</span>ti pādasaṅkhātapadumassa saṇha tale jātehi aṭṭhasatacakkalakkhaṇarājikehi padati gacchati etenāti pādo. Padadhātu gatimhiṇa. Paṅke kaddame jātanti paṅkajaṃ, padumaṃ. Pādasaṅkhātaṃ paṅkajaṃ pādapaṅkajaṃ. Paṅkajaṃiva pādo vā pādapaṅkajaṃ, pādo. Saṇhabhāvena mudati pavattatīti mudu, mudadhātu gatiyaṃ u. Talati patiṭṭhati etthāti talaṃ. Taladhātu patiṭṭhāyaṃ a. Mudu ca taṃ talañcāti muduttalaṃ, pādapaṅkajassa muduttalaṃ pādapaṅkajamuduttalaṃ, rājati dissatīti rājikā, rājati sobhati etenāti rājikā. Rājadhātu sobhane ṇiko. Pādapaṅkaja muduttale jātaṃ aṭṭhasatacakkalakkhaṇasaṅkhātaṃ rājikanti pādapaṅkajamuduttalarājikaṃ, tehi.</p>\r\n<p class="bodytext">Taṃ pana aṭṭhasatapādacakkalakkhaṇaṃ nāmevaṃ veditabbaṃ. Bhagavato pādapaṅkajamudutalassa majjhe nābhi dissati. Nābhipaṭicchinnā vaṭṭalekhā dissati. Nābhimukhaparikkhepapaṭṭo dissati. Panāḷimukhaṃ dissati. Arā dissanti. Aresu vaṭṭalekhā dissanti. Nemi dissati. Nemimaṇikā dissanti. Idaṃ tāva pāḷiāgatameva. Sampahulavāro pana anāgato, so evaṃ daṭṭhabbo. Satti, sirīvaccho, nandiyāvaṭṭaṃ, sovattiko, vaṭaṃsako, vaḍḍhamānakaṃ, macchayugalaṃ, bhaddapīṭhaṃ, aṅkusaṃ, tomaro, pāsādo, toraṇaṃ, setacchattaṃ, khaggo, tālavaṇḍaṃ, morahatthako, vāḷabījani, uṇhīsaṃ, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',97,replace(replace('<p class="bodytext">patto<a name="M0.0097"></a>, maṇikusumadāmaṃ, nīluppalaṃ, rattuppalaṃ, setuppalaṃ, padumaṃ, puṇḍarikaṃ, puṇṇaghaṭo, puṇṇapāti, samuddo, cakkavāḷaṃ, himavā, sineru, candimasūriyā, nakkhattāni, cattāro mahādīpā, dveparittadīpasahassāni antamaso cakkavatti rañño parisaṃ upādāya sabbo cakkalakkhaṇasseva parivāroti ettakameva <span class="bld">majjhimapaṇṇāsaṭṭhakathāyaṃ</span> vuttanti veditabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Jinālaṅkāraṭīkāyaṃ</span> pana ‘‘heṭṭhāpādatalesu cakkāni jātānīti cakkalakkhaṇaṃ nāma dvīsu pādatalesu dve cakkāni ekekasmiṃ sahassamattā arā yuttā honti. Nemivalayaṃ suvaṇṇavaṇṇaṃ. Nābhi indanīlamaṇivaṇṇā. Tasmiṃ majjhe ca nābhipariyante ca nābhimukhe ca vaṭṭalekhā majjhe chiddaṃ cakkapariyante nemito bahiddhā vaṭṭe. Satti, sirīvaccho, nandiyāvaṭṭaṃ, sovattiko, vaṭaṃsakaṃ, vaḍḍhamānakaṃ, bhaddapīṭhaṃ, aṅkuso, pāsādaṃ, toraṇaṃ, setacchattaṃ, khaggaṃ, tālavaṇḍaṃ, mayūrahatthaṃ, cāmaraṃ, uṇhīsaṃ, pattaṃ, maṇi, sumanadāmaṃ, nīluppalaṃ, rattuppalaṃ, rattapadumaṃ, setapadumaṃ, puṇḍarikaṃ, puṇṇakalasaṃ, puṇṇapattaṃ, samuddo, cakkavāḷapabbato, himavantapabbato, merupabbato, sūriyamaṇḍalaṃ, candamaṇḍalaṃ, nakkhattaṃ, saparivārā catumahādīpā, saparivāro sattaratanasamaṅgī cakkavatti, dakkhiṇāvaṭṭa setasaṅkhaṃ, suvaṇṇamacchayugaḷaṃ, cakkāvudhaṃ, sattamahāgaṅgā, sattakulapabbatā, sattasīdantasāgarā, suvaṇṇa haṃsarājā, saṃsumāro, dhajapaṭāko, suvaṇṇasivikā, suvaṇṇavāḷabījani, kelāsapabbato, sīharājā, byaggharājā, valāhakaassarājā, uposathachaddantahatthirājā, vāsukīnāgarājā, haṃsarājā, usabharājā, erāvaṇa hatthirājā, suvaṇṇamakaraṃ, catumukhasuvaṇṇanāvā, savacchakā dhenu, kiṃpuriso, kinnarī, karavīko, mayūrarājā, koñcarājā, cakkavākarājā, jīvañjīvakarājā cha devalokā, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',98,replace(replace('<p class="bodytext">soḷasa <a name="M0.0098"></a> brahmalokāti ime ettakā aggamaṅgalalakkhaṇā pādatale paññāyantī’’ti vuttaṃ. Iminā ca vacanena aṭṭhasata pādacakkalakkhaṇaṃ paripuṇṇaṃ veditabbanti.</p>\r\n<p class="bodytext">Tīhi lokehīti sambandho. Tīhi kāmarūpārūpa lokehīti attho. Tassa pana vacanattho heṭṭhā vuttoyeva. Taṃ panetaṃ katvatthe karaṇavacanaṃ. Tassa pana padassa nirupameyyāti padena kattubhāvena sambandho. <span class="bld">Nirupameyyata</span>nti appameyyabhāvaṃ. Upamitabbanti upameyyaṃ. Upapubba mādhātu pamāne ṇyo. Upameyyassa bhāvo upameyyataṃ. Natthi upameyyataṃ assa rājikassāti nirupameyyataṃ. Atha vā natthi upameyyaṃ etassa rājikassāti nirupameyyo, pādacakkalakkhaṇarājiko labbhati. Tassa bhāvo nirupameyyataṃ. Taṃ pana padaṃ sampāpuṇe evātipade kammaṃ. Sampāpuṇeevāti sambandho. Sammā gacchatievāti attho. Kasmā pana tīhi lokehi pādapaṅkajamudutalehi nirupameyyataṃ pāpuṇātīti. Buddhato aññassa kassaci devamanussassa pādatalamajjhe pādacakkalakkhaṇassa abhāvato. Bhagavato hi pādapaṅkajamudutale pāramipuññānubhāvena nibbattāni aṭṭhasatapādacakkalakkhaṇāni sampāpuṇanti, na aññesaṃ. Tasmā nirupameyyataṃ pāpuṇātīti.</p>\r\n<p class="bodytext"><span class="bld">Sabbalokamahita</span>nti sabbalokena pūjitaṃ. Sabbena devamanussalokena mahitabbo pūjitabboti sabbaloka mahito, nātho labbhati. Taṃ. Uppanne hi tathāgate ye keci mahesakkhā devamanussā buddhassa pūjenti, pageva aññā. Tathā hi brahmā sahampati sinerumattena ratana dāmena tathāgataṃ pūjeti. Yathābalañca aññepi devā manussā ca bimbisārakosalarājādayo nibbutampi bhagavantaṃ uddissa channavutikoṭidhanaṃ vissajjetvā asoka </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',99,replace(replace('<p class="bodytext">mahārājā <a name="M0.0099"></a> sakalajambudīpe caturāsītivihārasahassāni patiṭṭhāpeti. Kā kathā pana aññesaṃ pūjāti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Asama</span>nti sabbamanussadevabrahmehi asadisaṃ. Appaṭipuggalanti attho. Natthi samo sadiso puggalo assāti asamo, nātho. So pana asītianubyañjanapaṭimaṇḍita dvattiṃsa mahāpurisalakkhaṇa vicitrarūpakāyo. Sabbākāra parisuddha sīlakkhandhādiguṇaratanasamiddhadhammakāyo. Puññamahatta thāmamahattaiddhimahatta yasamahatta paññāmahattānaṃ paramukkaṃsa gato ca hoti, tasmā tathāgatena samo sadiso nāma natthīti attho. Taṃ asamaṃ. <span class="bld">Ta</span>nti nāthaṃ ahaṃ namāmīti sambandho.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Yo nātho yo yādiso sabbasattānaṃ paṭisaraṇo buddho, avikalehi kehici avekallehi, nirākulehi ākulavirahitehi, pādapaṅkaja muduttalarājikehi pādasaṅkhātapadumassa saṇhatale jātehi aṭṭhasatacakkalakkhaṇarājikehi, tīhi lokehi kattubhūtehi, nirupameyyataṃ appameyyabhāvaṃ appamitabba bhāvaṃ vā, sampāpuṇeeva sammā gacchatieva, sabbalokamahitaṃ sabbalokena pūjitaṃ, asamaṃ sabbamanussadevabrahmehi asadisaṃ taṃ nāthaṃ ahaṃ namāmīti.</p>\r\n<p class="centered">Cuddasamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Buddhaṃ narānarasamosaraṇaṃ dhitattaṃ,</p>\r\n<p class="gatha2">Paññāpadīpajutiyā vihatandhakāraṃ;</p>\r\n<p class="gatha3">Atthābhikāmanaradevahitāvahaṃ taṃ,</p>\r\n<p class="gathalast">Vandāmi kāruṇikamaggamanantañāṇaṃ.</p>','\r',char(13)),'\n',char(10)),'-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',100,replace(replace('<p class="bodytext"><a name="para15"></a><span class="paranum">15</span>. Evaṃ <a name="M0.0100"></a> cuddasamagāthāya nāthaṃ natvā idāni narānarasa mosaraṇantyādīhi sattahi guṇehi thomitvā buddhaṃ vanditu kāmo <span class="bld">buddhaṃ narānarasamosaraṇa</span>ntyādigāthamāha. Ayaṃ pana tabhajajagaṇehi garudvayena ca racitattā cuddasakkharehi yuttā vasantatilakāgāthāti daṭṭhabbā. Sādhakaṃ panettha heṭṭhā vuttameva.</p>\r\n<p class="bodytext">Tattha pana <span class="bld">narānarasamosaraṇa</span>nti ettha naraanarasamaṃ osaraṇanti padavibhāgo. Manussadevānaṃ samaṃ osaraṇaṃ sannipatitaṭṭhānanti attho. Narānarasaddassa vacanattho heṭṭhā vuttoyeva. Samaṃ avasaranti sannipatanti etthāti samosaraṇo, buddho. Samaavapubba saradhātu gatiyaṃ yu. Narānarānaṃ manussadevānaṃ samosaraṇo narānarasamosaraṇo, buddho. Yathā hi mahānigrodho phalakāmānaṃ sakuṇa samūhānaṃ samosaraṇaṃ hoti, evaṃ buddho lokiya lokuttarasampattikāmānaṃ manussadevānaṃ samosaraṇo sannipatito paṭisaraṇo vā hoti. Tasmā narānarasamosaraṇanti thometi.</p>\r\n<p class="bodytext"><span class="bld">Dhitatta</span>nti samāhitacittaṃ. Ettha attasaddo attasammā paṇidhi cātiādīsu attasaddo viya cittavācako daṭṭhabbo. Dhiyati ṭhapiyati ettha etena vā ekārammaṇe sampayutta dhammāti dhito, samādhi. Dhādhātu patiṭṭhāyaṃ ta. Ārammaṇaṃ adati ādadātīti attaṃ, cittaṃ. Adadhātu ādāne ta. Dhitaṃ samāhitaṃ attaṃ cittaṃ yassāti dhitatto, buddho. Taṃ dhitattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Paññāpadīpajutiyā</span>ti arahattamaggañāṇasaṅkhātapadīpobhāsena karaṇabhūtena. Pajānātīti paññā, arahattamagga ñāṇaṃ. Yathāsabhāvaṃ sabbappakārehi catusaccadhammaṃ paṭivijjhatīti attho. Paññāva ñeyyāvaraṇappahānato </p>','\r',char(13)),'\n',char(10)),'-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',101,replace(replace('<p class="bodytext">sabbapakārehi <a name="M0.0101"></a> dhammasabhāvaṃ dīpetīti paññāpadīpo. Jotetīti juti, paññāpadīpobhāso. Judhātu dittiyanti. Paññā padīpoeva juti paññāpadīpajuti. Atha vā paññāpadīpassa juti paññāpadīpajuti, ayaṃ pana samāso abhedabhedū pacārena daṭṭhabbo. Tāya paññāpadīpajutiyā.</p>\r\n<p class="bodytext"><span class="bld">Vihatandhakāra</span>nti hanitabbaavijjāsaṅkhātamohatamaṃ. Savāsanakilesappahānato visesena hanitabboti vihato. Vipubbahanadhātu hiṃsāyaṃ ta. Andhaṃ tamaṃ karotīti andhakāro, avijjāsaṅkhātamoho. Visayasabhāvapaṭicchādanato andhakārasarikkhakatāya moho andhakāroti vuccati. Atha vā andhaṃ paññācakkhuandhaṃ karotīti andhakāro, moho. Andhasaddūpapada karadhātu ṇa. Vihato andhakāro yena soti vihatandhakāro, buddho. Taṃ vihatandhakāraṃ. Yathā hi loke padīpo attano jutiyā andhakāraṃ hanati, evaṃ buddhassa arahattamaggapaññāpi avijjandhakāraṃ hanatīti vuttā. Ayaṃ pana hīnūpamāti vuccati. Paññāvato hi ekapallaṅkenapi nisinnassa dasasahassi lokadhātu ekapajjotā hoti. Vuttañhetaṃ bhagavatā ‘‘cattārome bhikkhave pajjotā. Katame cattāro. Candapajjoto, sūriyapajjoto, aggipajjoto, paññā pajjoto. Ime kho bhikkhave cattāro pajjotā. Etadaggaṃ bhikkhave imesaṃ catunnaṃ pajjotānaṃ yadidaṃ paññā pajjoto’’ti.</p>\r\n<p class="bodytext">Paññāpadīpajutiyā vihatandhakāranti ettha pana paññāpadīpa jutiyātiiminā buddhassa arahattamaggañāṇaṃ mukhyena dasseti. Taṃ dassanena pāramīñāṇañca vipassanāñāṇañca heṭṭhimamaggaphala ñāṇañca phalūpacārena dīpeti. Taddīpakena avijjandhakārappahānaṃ paṭhamakāraṇūpacārena pakāseti. Tappakāsanena arahatta phalañāṇalabhanaṃ dutiyakāraṇūpacārena vibhāveti. Taṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',102,replace(replace('<p class="bodytext">vibhāvanena <a name="M0.0102"></a> sabbaññutañāṇādisabbabuddhaguṇalabhanaṃ tatiya kāraṇūpacārena ānubhāveti.</p>\r\n<p class="bodytext">Nanu ca sabbesampi khīṇāsavānaṃ paññāpadīpajutiyā vihatandhakāratā sambhavati, atha kasmā aññasādhāraṇāvisesa guṇena buddhassa thomanā katāti. Savāsanappahānena anaññasādhāraṇavisesatāya sambhavato. Sabbesampi hi khīṇāsavānaṃ satipi paññāpadīpajutiyā vihatandhakāratte ariyasāvakapaccekabuddhānaṃ savāsanāya kilesappahānaṃ na vijjati. Sammāsambuddhānaṃyeva savāsanappahānena kilesappahānassa viseso vijjatīti sātisayavasena avijjandhakārappahānena bhagavantaṃyeva paññāpadīpajutiyā vihatandhakāranti thometīti.</p>\r\n<p class="bodytext"><span class="bld">Atthābhikāma</span>nti sattalokassa lokiyalokuttara saṅkhātaṃ payojanaṃ ativiya icchantaṃ. Kāraṇānurūpaṃ asati bhavatīti atthaṃ, phalaṃ. Asadhātu bhuvimhi tha. Taṃ abhiatirekaṃ kāmeti icchatīti atthābhikāmo, buddho. Atthasaddūpapada abhipubbakamudhātu icchāyaṃ ṇa. Taṃ atthābhikāmaṃ. <span class="bld">Naradevahitāvaha</span>nti manussadevānaṃ hitaṃ dhārakaṃ. Hitaṃ narati netīti naro, manusso. Atha vā nariyati sakena kammena niyyatīti naro, manusso. Naradhātu nayane a. Vuttañhi ‘‘kammena niyyati loko’’ti. Kāmaguṇa jhānābhiññacittissariyādīhi dibbanti kīḷantīti devā, tesu vā viharantīti devā, divudhātu kīḷādīsu ṇa. Narā ca devā ca naradevā. Hinoti vaḍḍhatīti hitaṃ, phalaṃ. Hidhātu vaḍḍhane ta. Naradevānaṃ hitaṃ naradevahitaṃ, taṃ āvahatīti naradevahitāvaho, buddho. Naradevahitasaddūpapadaāpubba vahadhātu haraṇe ṇa. Buddho hi sabbadevamanussānaṃ lokiyalokuttarasampattiatthaṃ dhammadesanāya dhāretīti vuttaṃ hoti, taṃ naradevahitāvahaṃ. <span class="bld">Kāruṇikamaggamananta </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',103,replace(replace('<p class="bodytext">ñāṇanti <a name="M0.0103"></a> ettha kāruṇikaṃ aggaṃ anantañāṇantipadacchedo. Tattha kāruṇikanti sabbasattesu mahākaruṇāya yuttaṃ, mahākaruṇavantaṃ vā. Kiratīti karuṇā. Kiradhātu apanayane uṇapaccayo. Paradukkhaṃ vikkhipati apanetīti attho.</p>\r\n<p class="bodytext">Atha vā kinātīti karuṇā. Kidhātu hiṃsāyaṃ ruṇapaccayo. Paradukkhe sati kāruṇikaṃ hiṃsati vibādhatīti attho. Paradukkhe sati sādhūnaṃ hadayakampanaṃ karotīti vā karuṇā. Karadhātu uṇapaccayo. Atha vā kaṃ vuccati sukhaṃ, taṃ rundhatīti karuṇā. Kaṃsaddūpapada rudhidhātu āvaraṇe yupaccayo. Esā hi paradukkhāpanayanakāmatā lakkhaṇā. Attasukhanirapekkhatāya kāruṇikānaṃ sukhaṃ rundhati vibādhatīti attho. Karuṇāya mahākaruṇāya yutto kāruṇiko. Karuṇā assa atthīti vā kāruṇiko, buddho. Taṃ kāruṇitaṃ. <span class="bld">Agga</span>nti jātipuñña guṇamahattabhāvena sabbasattuttamaṃ seṭṭhabhāvaṃ ajati gacchatīti aggo, buddho. Ajadhātu gatiyaṃ a. Jassa go. Buddho hi asambhinnakhattiyavaṃse jātattā jātivasena ca pūritapāramīpuññattā puññavasena ca aparimitanirupamasīlādi guṇavisesena samannāgatattā guṇavasena ca anantalokadhātūsu sabbasattuttamabhāvaṃ gacchatīti aggo. Nibbānaṃ ajati gacchatīti vā aggo. Atha vā ajeti khepeti kileseti aggo, buddho. Ajadhātu khepane a. Taṃ aggaṃ. <span class="bld">Anantañāṇa</span>nti gaṇanavasena ca sabhāvavasena ca visayavasena ca antavirahitañāṇaṃ. Kenaci puggalena ñāṇena anantaṃ aparicchinnaṃ, sabbaññutañāṇavantaṃ vā. Natthi antaṃ yassāti anantaṃ, ñāṇaṃ. Sabbañeyyadhammaṃ jānātīti ñāṇaṃ, sabbaññutañāṇaṃ. Anantaṃ ñāṇaṃ yassa soti anantañāṇo, buddho. Buddhassa hi ñāṇaṃ koci manusso vā devo vā brahmā vā ettakanti paricchindituṃ na sakkoti, tasmā anantaṃ aparimāṇaṃ hotīti vuttaṃ hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',104,replace(replace('<p class="bodytext">Vuttañhetaṃ <a name="M0.0104"></a> <span class="bld">sāriputtattheraapadāne</span> –</p>\r\n<p class="gatha1">‘‘Sakkā samudde udakaṃ, pametuṃ aḷhakena vā;</p>\r\n<p class="gathalast">Natveva tava sabbaññu, ñāṇaṃ sakkā pametave’’ti.</p>\r\n<p class="bodytext">Kāruṇikamaggamanantañāṇanti ettha ca karuṇaggahaṇena sammāsambodhiyā mūlaṃ dasseti, mahākaruṇāsañcoditamānaso hi bhagavā saṃsārapaṅkato sattānaṃ samuddharaṇatthaṃ dīpaṅkarapādamūle katābhinīhāro kappasatasahassādhikāni cattāri asaṅkhyeyyāni anupubbena pāramiyo pūretvā anuttaraṃ sammāsambodhiṃ adhigatoti karuṇā sammāsambodhiyā mūlanti. Agganti etena bhagavato aparimitanirupamasīlādiguṇasamaṅgītāya aparimāṇāsu lokadhātūsu aparimāṇānaṃ sattānaṃ uttamabhāvaṃ dasseti. Anantañāṇantietena sammāsambodhiṃ dasseti. Sabbaññutañāṇapadaṭṭhānañhi aggamaggañāṇaṃ, aggamaggañāṇapadaṭṭhānañca sabbaññutañāṇaṃ sammāsambodhīti vuccati. Tassa ca sāmatthiyena catupaṭisambhidāñāṇaṃ catuvesārajjañāṇaṃ chaasādhāraṇañāṇāni ca chaabhiññā aṭṭhasu parisāsu akampana ñāṇāni dasabalañāṇāni soḷasañāṇacariyā ca catucattārīsañāṇavatthūni sattasattatiñāṇavatthūni cāti evamādīnaṃ anekesaṃ paññāpabhedānaṃ vasena ñāṇācāraṃ dassetīti. Taṃ buddhaṃ ahaṃ vippasannena cetasā vandāmīti sambandhoti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Narānarasamosaraṇaṃ manussadevānaṃ samaṃ osaraṇaṃ sannipatitaṭṭhānaṃ, dhitattaṃ samāhitacittaṃ, paññāpadīpajutiyā arahattamaggañāṇasaṅkhāta padīpobhāsena karaṇabhūtena vihatandhakāraṃ hanitabba avijjāsaṅkhātamohatamaṃ, atthābhikāmaṃ sattalokassa lokiyalokuttarasaṅkhātapayojanaṃ ativiya icchantaṃ, naradevahitāvahaṃ manussadevānaṃ hitaṃ dhārakaṃ, kāruṇikaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',105,replace(replace('<p class="bodytext">sabbasattesu <a name="M0.0105"></a> mahākaruṇāya yuttaṃ mahākaruṇavantaṃ vā, aggaṃ jātipuññaguṇamahattabhāvena sabbasattuttamaṃ, ananta ñāṇaṃ gaṇavasena ca sabhāvavasena ca visayavasena ca antavirahitañāṇaṃ, anantaṃ sabbaññutañāṇavantaṃ vā taṃ buddhaṃ ahaṃ vippasannena cetasā vandāmīti.</p>\r\n<p class="centered">Pannarasamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> Akhilaguṇanidhāno yo munindopagantvā,</p>\r\n<p class="gatha2">Vanamisipatanavhaṃ saññatānaṃ niketaṃ;</p>\r\n<p class="gatha3">Tahimakusalachedaṃ dhammacakkaṃ pavatto,</p>\r\n<p class="gathalast">Tamatulamabhikantaṃ vandaneyyaṃ namāmi.</p>\r\n<p class="bodytext"><a name="para16"></a><span class="paranum">16</span>. Evaṃ pannarasamagāthāya buddhaṃ vanditvā idāni isipatana migadāye dhammacakkapavattanādiguṇehi thomitvā munindaṃ vanditu kāmo <span class="bld">akhilaguṇanidhāno</span> tyādigāthamāha. Ayaṃ pana nanamayayagaṇehi racitattā pannarasakkharehi yuttā mālinī gāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘nanamayayayutāyaṃ mālinībhogisīhī’’ti. Tassattho. Yassaṃ paṭipādaṃ bhogisīhi aṭṭhasattayatīhi yuttā ca nanamayayayutā nanagaṇamagaṇa yayagaṇehi yuttā ca ce siyuṃ, ayaṃ gāthā mālinīgāthā nāmāti. Ettha ca bhogisīhīti bhogaisīhīti padacchedo. Bhogasaddo aṭṭhasaṅkhyā vācako. Isisaddo sattasaṅkhyāvācakoti daṭṭhabbo.</p>\r\n<p class="bodytext">Tattha <span class="bld">akhilaguṇanidhāno</span>ti sabbalokiyalokuttara saṅkhātānaṃ sīlasamādhipaññādiguṇasamūhānaṃ patiṭṭhāno. Ettha ca akhilasaddo anavasesavācako. Sabboti attho. Vuttañhi <span class="bld">madhuṭīkāyaṃ</span> ‘‘khilasaddo hettha avasesavācako’’ti. Na khilaṃ akhilaṃ. Atha vā na khīyatīti </p>','\r',char(13)),'\n',char(10)),'-16-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',106,replace(replace('<p class="bodytext">akhilaṃ<a name="M0.0106"></a>, sabbaṃ. Napubbakhidhātu khaye lo. Guṇasaddassa vacanattho heṭṭhā vuttoyeva. Akhilāni sabbāni guṇāni akhilaguṇāni, tesaṃ nidhāti tiṭṭhati etthāti akhilaguṇanidhāno, munindo labbhati. <span class="bld">Yo</span>ti yo yādiso. <span class="bld">Munindopagantvā</span>ti ettha munindo upagantvāti padacchedo. <span class="bld">Munindo</span>ti agāriyamuni, anagāriyamuni, sekkhamuni, asekkhamuni, pacceka munivasena pañcannaṃ munīnaṃ indo issaroti attho. Tassa pana vacanattho heṭṭhā vuttoyeva. Akhilaguṇanidhāno yo munindo saññatānaṃ niketaṃ isipatanaavhaṃ vanaṃ upagantvā tahiṃ akusalachedaṃ dhammacakkaṃ pavattoti sambandho.</p>\r\n<p class="bodytext"><span class="bld">Vanamisipatanavha</span>nti vanaṃ isipatanaavhanti padacchedo. Ettha vananti migadāyavanaṃ. Tañhi vivekakāmehi vanitabbaṃ sambhajitabbaṃ etthāti vananti vuccati. Vanadhātu sambhattiyaṃ kvi. Isipatanavhanti isipatananāmikaṃ isīnaṃ patanaṭṭhānattā isipatananti voharitabbaṃ vā, sīlakkhandhādike esanti gavesantīti isi, paccekabuddhādayo. Isadhātu gavesane ipaccayo. Te patanti sannipatanti etthāti isipatanaṃ, vanaṃ. Isisaddūpapadapatadhātu gatiyaṃ yu. Atha vā isīnaṃ patanaṃ isipatanaṃ, vanaṃ. Avhetabbaṃ voharitabbanti avhaṃ. Āpubbavhedhātu avhayane kvi. Saṃyogaparattā ākārassa rassādeso. Isipatananti avhetabbanti isipatanavhaṃ, vanaṃ. <span class="bld">Saññatāna</span>nti kāyavacīmanosaṃyamantānaṃ sādhūnaṃ. Saṃyamantīti saññatā, paccekabuddhādayo sādhū. Saṃpubbayamudhātu uparame ta. Tesaṃ saññatānaṃ. <span class="bld">Niketa</span>nti vasanaṭṭhānaṃ, rammaṇaṭṭhānaṃ vā. Nikitati nivasati rammati vā etthāti niketaṃ, vanaṃ. Nipubba kitadhātu nivāse ṇa. <span class="bld">Upagantvā</span>ti gamitvā. <span class="bld">Tahi</span>nti tasmiṃ isipatananāmake migadāyavane. <span class="bld">Akusalacheda</span>nti akusalassa samucchedaṃ. Akusalaṃ chindati </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',107,replace(replace('<p class="bodytext">samucchindati <a name="M0.0107"></a> etena suttenāti akusalachedaṃ, dhammacakka pavattanasuttaṃ. Akusalasaddūpapadachididhātu dvidhākaraṇe ṇa.</p>\r\n<p class="bodytext"><span class="bld">Dhammacakka</span>nti dhammacakkapavattanasuttaṃ. Ettha hi cakkasaddo sampattiyaṃ, lakkhaṇe, rathaṅge, iriyāpathe, dāne, ratana dhammakhuracakkādīsu ca dissati. Cattārimāni bhikkhave cakkāni, yehi samannāgatānaṃ devamanussānantiādīsu hi sampattiyaṃ dissati. Pādatalesu cakkāni jātānīti ettha lakkhaṇe. Cakkaṃva vahato padanti ettha rathaṅge. Catucakkaṃ navadvāranti ettha iriyāpathe. Dadaṃ bhuñja mā ca pamādo cakkaṃ vattaya sabbapāṇīnanti ettha dāne. Dibbacakkaratanaṃ pāturahosīti ettha ratanacakke. Mayā pavattitaṃ cakkanti ettha dhammacakke. Icchāhatassa posassa cakkaṃ bhamati matthaketi ettha khuracakke. Khurapariyantena cakkenāti ettha paharaṇacakke. Asanivicakkanti ettha asanimaṇḍale. Idha panāyaṃ dhammacakke daṭṭhabbo. Tañhi satipaṭṭhānādidhammaṃ cakketi pavattetīti dhammacakkanti vuccati. Ariyasaccādidhammaṃ vā cakketi pavattetīti cakkanti. Atha vā dhammato anapetattā dhammañca taṃ āṇācakkañcāti dhammacakkaṃ. Dhammena ñāyena cakkanti dhammacakkaṃ, tena vuttaṃ <span class="bld">paṭisambhidāyaṃ</span> ‘‘dhammacakkanti kenaṭṭhena dhammacakkaṃ. Dhammañca pavatteti cakkañcāti dhammacakkaṃ, cakkañca pavatteti dhammañcāti dhammacakkaṃ, dhammena pavattetīti dhammacakkaṃ, dhammacariyāya pavattetīti dhammacakkaṃ, dhamme ṭhito pavattetīti dhammacakka’’ntiādi. Api ca dhammacakkanti paṭivedhañāṇampi desanāñāṇampi. Tattha paṭivedhañāṇaṃ nāma yena ñāṇena bodhipallaṅke nisinno cattāri saccāni soḷasahākārehi saṭṭhinayasahassehi paṭivijjhi. Desanāñāṇaṃ nāma yena ñāṇena tiparivaṭṭaṃ dvādasākāraṃ dhammacakkaṃ pavattesi. Ubhayampetaṃ dasabalassa ure jātañāṇameva. Tesu pana dhammadesanāñāṇaṃ adhippetaṃ. Suttaṃ pana ṭhānūpacārena dhammacakkanti vuccati. <span class="bld">Pavatto</span>ti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',108,replace(replace('<p class="bodytext">pañcavaggiyānaṃ <a name="M0.0108"></a> bhikkhūnaṃ dveme bhikkhave antā pabbajitena na sevitabbātiādinā adesayi.</p>\r\n<p class="bodytext"><span class="bld">Tamatulamabhikanta</span>nti taṃ atulaṃ abhikantanti padacchedo. Ettha tanti munindaṃ. Atulanti sīlādīhi guṇehi kenaci asadisaṃ. Natthi tulo sadiso puggalo etassāti atulo, munindo. Tassa hi aparimāṇāsu loka dhātūsu sīlādīhi guṇehi sadiso puggalo natthi. Asadisehi buddhehi samoti attho. <span class="bld">Abhikanta</span>nti sabbadevamanussehi ativiya kantaṃ kamanīyaṃ sarīravaṇṇavantaṃ. Atirekaṃ tusitabbasarīravaṇṇasampannaṃ vā. Abhikkantavaṇṇanti hi vattabbe chandānurakkhaṇatthaṃ kakāralopañca vaṇṇapadalopaṃ katvā vuttasandhivasena abhikantanti vuttaṃ. Uttarapadalopa vasena vā. Munindassa pana sarīravaṇṇaṃ sabbadevamanussehi ativiya atirekaṃ vā kamiyati tosiyatīti abhikantanti vuccati. Abhipubbakamudhātu icchākantīsu ta. Abhikantaṃ vaṇṇaṃ sarīravaṇṇaṃ yassa soti abhikanto, munindo. Tassa hi dvattiṃsamahāpurisalakkhaṇehi ca asītyānubyañjana lakkhaṇehi ca chabbaṇṇaraṃsīhi ca suvaṇṇavaṇṇena ca samannā gatattā sarīrasobhaggappattito sabbadevamanussehi ativiya kamanīyo tuṭṭho sarīravaṇṇo atthīti abhikantanti thometi. Taṃ abhikantaṃ. <span class="bld">Vandaneyya</span>nti devamanussānaṃ vandanārahaṃ. Tehi vanditabbaṃ vā, vandiyate thomiyate vā vandanaṃ. Bhāvasādhanoyaṃ. Vandadhātu vadidhātu vā abhivādanathutīsu yu. Taṃ arahatīti vandaneyyo, munindo. Vandapadaṃ arahatitaddhite ṇeyyo.</p>\r\n<p class="bodytext">Atha vā devamanussehi vanditabbaṃ thomitabbanti vā vandaneyyo, munindo. So hi ādimajjhapariyosāna guṇehi samannāgatattā sabbadevamanussānaṃ vandanāraho tehi vā vanditabbo hoti. Tattha ca ādiguṇaṃ nāma </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',109,replace(replace('<p class="bodytext">karuṇānidānaṃ <a name="M0.0109"></a> sīlaṃ pāṇātipātādiviratippavattito. Majjha guṇaṃ nāma samādhi jhānattayayogato. Pariyosānaguṇaṃ nāma paññā tato uttari karaṇīyābhāvatoti visesena bhagavato vandaneyyanti thometi. Ahaṃ tīhi dvārehi namāmi vandāmīti sambandho.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Akhilanidhāno sabba lokiyalokuttarasaṅkhātānaṃ sīlasamādhipaññādiguṇa samūhānaṃ patiṭṭhāno yo yādiso munindo pañcannaṃ munīnaṃ indo issaro bhagavā, saññatānaṃ kāyavacīmanosaṃya mantānaṃ sādhūnaṃ, niketaṃ vasanaṭṭhānaṃ rammaṇaṭṭhānaṃ vā, isipatanavhaṃ isipatananāmikaṃ isipatananti voharitabbaṃ vā, vanaṃ migadāyavanaṃ, upagantvā gamitvā tahiṃ tasmiṃ isipatananāmake migadāya vane, akusalassa samucchedaṃ, dhammacakkaṃ dhammacakkappavattanasuttaṃ, pavatto pañcavaggiyānaṃ bhikkhūnaṃ adesayi, atulaṃ sīlādīhi guṇehi kenaci asadisaṃ, abhikantaṃ sabbadevamanussehi ativiya kantaṃ kamanīyaṃ tuṭṭhi sarīravaṇṇavantaṃ, vandaneyyaṃ deva manussānaṃ vandanārahaṃ tehi vā vanditabbaṃ, taṃ munindaṃ ahaṃ tīhi dvārehi namāmi vandāmīti.</p>\r\n<p class="centered">Soḷasamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> Suciparivāritaṃ surucirappabhāhi rattaṃ,</p>\r\n<p class="gatha2">Sirivisarālayaṃ gupitamindriyehupetaṃ;</p>\r\n<p class="gatha3">Ravisasimaṇḍalappabhutilakkhaṇopacittaṃ,</p>\r\n<p class="gathalast">Suranarapūjitaṃ sugatamādaraṃ namāmi.</p>\r\n<p class="bodytext"><a name="para17"></a><span class="paranum">17</span>. Evaṃ soḷasamagāthāya munindaṃ vanditvā idāni suciparivāritantyādīhi sattahi guṇehi thomitvā vanditukāmo <span class="bld">suciparivārita</span>ntyādigāthamāha. Ayaṃ pana na jabhajaragaṇehi ca ekagarunā ca racitattā soḷasakkharehi </p>','\r',char(13)),'\n',char(10)),'-17-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',110,replace(replace('<p class="bodytext">yuttāvāṇinīgāthāti <a name="M0.0110"></a> daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘najabhajarā yadā bhavati vāṇinī gayuttā’’ti. Tassattho. Yassaṃ paṭipādaṃ najabhajarā nagaṇa jagaṇa bhagaṇa jagaṇa ragaṇā yadā gayuttā pādantagarunā saṃyogā ce siyuṃ, tadā sā vuttā vāṇinī vāṇinīgāthā nāma bhavatīti.</p>\r\n<p class="bodytext">Tattha <span class="bld">suciparivārita</span>nti rāgādimalavisuddhāhi ariyasāvakādiparisāhi parikkhāritaṃ kilesamalavisuddhaṃ parikkhāravantaṃ vā. Rāgādimalehi sucati sujjhatīti suci, parisā. Sucadhātu soceyye i. Parisamantato ariyabhikkhuādīhi deva manussehi vāriyati parikkhariyatīti parivāro, sugato. Paripubbavaradhātu āvaraṇe parikkhāre vā ṇa. Sucīhi parisāhi parivārito suciparivārito, sugato. Tatiya tappurisasamāsoyaṃ. Bhagavato hi sadā rāgādimala visuddhehi ariyabhikkhuādīhi catūhi parisāhi aṭṭhahi parisāhi vā parivārito hotīti adhippāyo. Atha vā suciparisuddhaṃ parivāritaṃ yassa soti suciparivārito, sugato. <span class="bld">Surucirappabhāhī</span>ti suṭṭhu kantitasarīranikkhanta byāmamattaraṃsīhi karaṇabhūtehi suṭṭhu ruciyati kamiyati tusiyatīti surucirā, raṃsi. Supubba rucadhātu rocane iro. Pakārena bhāti dibbatīti pabhā, raṃsi. Papubba bhādhātu dibbane kvi. Surucirā ca sā pabhā cāti surucirappabhā, tāhi surucirappabhāhi. <span class="bld">Ratta</span>nti niccaṃ rañjitabbakāyaṃ. Rañjiyatīti ratto, kāyo. Ranjadhātu rāge ta. Ratto kāyo yassa soti ratto, sugato. Uttarapadalopasamā soyaṃ. Rūpassa bhavo rūpaṃ viya, taṃ rattaṃ. Bhagavato hi sarīrato niccaṃ nikkhantehi byāmamattaraṃsīhi rañjitabbo rūpakāyo atthīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Sirivisarālaya</span>nti paññāpuññasaṅkhātalakkhisamūhānaṃ patiṭṭhānaṃ. Ettha ca sirīti paññāpuññānametaṃ adhivacanaṃ. Atha vā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',111,replace(replace('<p class="bodytext">puññanibbattā <a name="M0.0111"></a> sarīrasobhaggādisampatti. Sā hi katapuññe siyati nissayati, katapuññehi vā siyati nissiyatīti sirīti vuccati, paññāpuññaṃ, issariyaṃ vā. Sarīrasobhaggādi sampatti vā labbhati. Vuttañhi <span class="bld">vimānavatthuaṭṭhakathādīsu</span> ‘‘sirīti buddhipuññānaṃ adhivacananti ca. Sirīti issariyanti ca sirīti rūpakāyasampattī’’ti ca. Sidhātu sevāyaṃ, rapaccayo ca, itthiliṅgajotakaīpaccayo ca. Sirīti hi dīghavasena vattabbe gāthābandhattā chandānurakkhaṇatthaṃ rassavasena vuttaṃ. Visarālayanti visaraālayanti padacchedo. Visaroti samūho. Visanti pavisanti avayavā yasmiṃ samūheti visaro, samūho. Visadhātu pavesane arapaccayo. Atha vā visaranti samosaranti avayavā yasminti visaro. Vipubba saradhātu samosaraṇe a. Potthakesu pana visayāla yanti yakārena pāṭho dissati. Na so yujjati. Kasmā, visarasaddasseva samūhavācakattā. Vuttañhi <span class="bld">abhidhānappa dīpikāyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Samūho gaṇasaṅghātā,</p>\r\n<p class="gatha2">Samudāyo ca sañcayo;</p>\r\n<p class="gatha3">Sandoho nivaho ogho,</p>\r\n<p class="gathalast">Visaro nikaro cayo’’tyādi.</p>\r\n<p class="bodytext">Ālayanti patiṭṭhānaṃ. Ālayanti patiṭṭhanti etthāti ālayo, sugato. Āpubba lidhātu alliyane ṇa. Sirīnaṃ paññāsaṅkhātalakkhīnaṃ visaro samūho sirīvisaro, tesaṃ ālayo sirīvisarālayo, sugato. Taṃ sirivisarālayaṃ. <span class="bld">Gupitamindriyehupeta</span>nti gopitabbehi saṃvaritabbehi cakkhādīhi chahi indriyehi upetaṃ samupetaṃ samannāgataṃ. Gupiyanti rakkhiyantīti gupitāni. Gupadhātu rakkhane ta. Idanti sahajātadhamme issariyaṃ karontīti indriyāni. Ididhātu paramissariye iyo. Gupitāni indriyāni </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',112,replace(replace('<p class="bodytext">gupitamindriyāni<a name="M0.0112"></a>, cakkhādiindriyāni labbhanti. Tehi. Upeti sampajjatīti upeto, sugato. Upapubba idhātu gatiyaṃ ta, taṃ upetaṃ. Kiñcāpi buddhato aññehi ariyasāvakādīhi puggalehi cakkhādiindriyāni rakkhitāni. Buddhoyeva pana bodhipallaṅke saha vāsanāya sabba kilesānaṃ arahattamaggena viddhaṃsitattā visiṭṭhena cakkhādi indriyaṃ rakkhito hotīti tassa gupitamindriyehupetanti thometi.</p>\r\n<p class="bodytext"><span class="bld">Ravisasimaṇḍalappabhutilakkhaṇopacitta</span>nti sūriyamaṇḍala candamaṇḍalādīhi pādacakkalakkhaṇehi abhivicitrapādaṃ. Upasaddo cettha bhusaṭṭhavācako. Abhivicitraṃ sūriyamaṇḍala candamaṇḍalādiṃ pādacakkalakkhaṇavantaṃ vā. Ettha ca ravīti sūriyo, so hi ravati sīghaṃ gacchatīti ravīti vuccati. Rudhātu gatiyaṃ ṇi. Vuttañhi <span class="bld">pātheyyaṭṭhakathāyaṃ</span> ‘‘cando ujukaṃ saṇikaṃ gacchati, sūriyassa pana ujukaṃ gamanaṃ sīgha’’nti. Ṇvādi <span class="bld">moggallāne</span> pana ‘‘rusadde i. O avādeso. Ravati gajjatīti ravi. Ādicco’’ti vuttaṃ. <span class="bld">Abhidhāna ṭīkāyañca</span> ‘‘ravanti etena sattā pabhāvitattāti ravī’’ti vuttaṃ. Sasīti cando. So hi sasati tiṇena pāṇati jīvatīti saso, sasapaṇḍito. Sasadhātu pāṇe a. Sasarūpalakkhaṇamettha atthīti sasīti vuccati. Atīte hi sasarājā brāhmaṇavesena āgantvā yācakassa sakkarañño aṅgārarāsimhi laṅghitvā jīvitaṃ pariccajitvā kāyaṃ adāsi. Atha pana sakko rājā tassa sasapaṇḍitassa jīvitapariccajitassa jīvitadānaguṇo sakalakappaṃ pākaṭo hotūti pabbataṃ pīḷetvā pabbatarasaṃ ādāya candamaṇḍale sasarūpalakkhaṇaṃ likhitvā ṭhapesi. Tasmā cando sasīti lokehi vohariyati. Tassa pana vitthāro sasapaṇḍitajātake passitvā gahetabbo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',113,replace(replace('<p class="bodytext">Atha <a name="M0.0113"></a> vā sasati hiṃsati uṇhaguṇanti sasīti vuccati. Sasudhātu hiṃsāyaṃ īpaccayo. Maṇḍiyati vibhūsiyati paricchedakaraṇavasenāti maṇḍalaṃ. Maṇḍadhātu maḍidhātu vā vibhūsane alo. Atha vā maṇḍaṃ vibhūsanaṃ parisamantato lāti gaṇhātīti maṇḍalaṃ. Maṇḍasaddūpapada lādhātu gahaṇe kvi. Ravino maṇḍalaṃ ravimaṇḍalaṃ, sasino maṇḍalaṃ sasimaṇḍalaṃ, ravimaṇḍalañca sasimaṇḍalañca ravisasimaṇḍalaṃ, maṇḍalasaddo ravisaddepi yojetabbo. Maṇḍalasaddassa dvandapadato suyyamānattā. <span class="bld">Pabhutī</span>tiādi. So hi ādyatthavācako abyayanipāto. Ravisasimaṇḍalaṃ pabhutiādi yassāti ravisasimaṇḍalappabhuti, pādacakkalakkhaṇaṃ labbhati. Lakkhiyati lakkhitabbaṃ etenāti lakkhaṇaṃ, pādappa vattacakkalakkhaṇaṃ, ravisasimaṇḍalappabhuti ca taṃ lakkhaṇañcāti ravisasimaṇḍalappabhutilakkhaṇaṃ, tena upacittaṃ ativicitraṃ pādaṃ yassa soti ravisasimaṇḍalappabhutilakkhaṇopa citto, sugato. Atha vā upacittaṃ ativicitraṃ ravisasi maṇḍalappabhutilakkhaṇaṃ pādacakkalakkhaṇaṃ assāti tathā, sugato. Taṃ.</p>\r\n<p class="bodytext"><span class="bld">Suranarapūjita</span>nti devamanussehi pūjitabbaṃ. Suranti issariya kīḷādīhi dibbantīti surā, devā. Suradhātu dibbane a. Atha vā surati isati devissariyaṃ pāpuṇāti virocati cāti surā, devā. Suradhātu issariyadittīsu a. Sundarā rāvācā etesanti vā surā, devā. Khandhasantānaṃ sakakammaṃ vā naranti niyantīti narā, manussā. Naradhātu nayane a. Surā ca narā ca suranarā, tehi pūjitabboti suranara pūjito, sugato. Taṃ. <span class="bld">Sugata</span>nti buddhaṃ. So hi sobhanaṃ gataṃ gamanaṃ etassāti sugatoti vuccati. Bhagavato hi veneyyajanūpasaṅkamanaṃ ekantena tesaṃ hitasukhanipphādanato sobhanaṃ. Tathā lakkhaṇānubyañjanappaṭi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',114,replace(replace('<p class="bodytext">maṇḍitarūpakāyatāya <a name="M0.0114"></a> dutavilambitakhalitānukaḍḍhananippiḷinukkuṭika kuṭilākuṭilatādi dosarahita mavahasita rājahaṃsausabha vāraṇa migarājagamanaṃ kāyagamanaṃ. Ñāṇagamanañca vipula nimmala karuṇā sati vīriyādi guṇavisesa sahita abhinīhārato yāva mahābodhi anavajjatāya sobhanamevāti.</p>\r\n<p class="bodytext">Atha vā sayambhūñāṇena sakalampi lokaṃ pariññābhi samayavasena parijānanto ñāṇena sammā gato avagatoti sugato, tathā lokasamudayaṃ pahānātisamayavasena pajahanto anuppattidhammataṃ āpādento sammā gato avagatoti sugato, lokanirodhaṃ nibbānaṃ sacchikiriyābhisamayavasena sammā gato avagatoti sugato, lokanirodhagāminippaṭipadaṃ bhāvanābhi samayavasena sammā gato paṭipannoti sugato, sotāpattimaggena ye kilesā pahīnā, te kilese na puneti na pacceti na paccāgacchatīti sugatotiādinā nayena ayamattho vibhāvetabbo. Atha vā sundaraṃ ṭhānaṃ sammāsambodhiṃ nibbānameva vā gato adhigatoti sugato. Yasmā vā bhūtaṃ tacchaṃ atthasaṃhitaṃ veneyyānaṃ yathārahaṃ yuttameva ca dhammaṃ bhāsati, tasmā sammā gadatīti sugato dakārassa takāraṃ katvā. Iti sobhana gamanatādīhi sugato, taṃ sugataṃ. <span class="bld">Ādara</span>nti sakkaccaṃ, ādaranaṃ ādaro. Āpubba daradhātu ādare ṇa. Taṃ ādaraṃ. <span class="bld">Namāmī</span>ti ahaṃ vandāmīti atthoti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Suciparivāritaṃ rāgādimala visuddhāhi ariyasāvakādiparisāhi parikkhāritaṃ kilesamalavisuddhaṃ parikkhāravantaṃ vā, surucirappabhāhi suṭṭhu kantita sarīranikkhantabyāmamattaraṃsīhi karaṇabhūtehi, rattaṃ niccaṃ rañjitabbakāyaṃ, sirivisarālayaṃ paññāpuññasaṅkhātalakkhisa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',115,replace(replace('<p class="bodytext">mūhānaṃ <a name="M0.0115"></a> patiṭṭhānaṃ, gupitamindriyehi gupitabbehi saṃvaritabbehi chahi cakkhādiindriyehi, upetaṃ samupetaṃ samannāgataṃ, ravisasimaṇḍalappabhutilakkhaṇopacittaṃ sūriyamaṇḍalacandamaṇḍalādīhi pādacakkalakkhaṇehi abhivicitrapādaṃ, abhivicitraṃ sūriya maṇḍalacandamaṇḍalādiṃ pādacakkalakkhaṇavantaṃ vā, suranarapūjitaṃ devamanussehi pūjitabbaṃ, sugataṃ buddhaṃ ādaraṃ sakkaccaṃ ahaṃ namāmi vandāmīti.</p>\r\n<p class="centered">Sattarasamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> Maggoḷumpena muhapaṭighāsādiullolavīciṃ,</p>\r\n<p class="gatha2">Saṃsāroghaṃ tari tamabhayaṃ pārapattaṃ pajānaṃ;</p>\r\n<p class="gatha3">Tāṇaṃ leṇaṃ asamasaraṇaṃ ekatitthaṃ patiṭṭhaṃ,</p>\r\n<p class="gathalast">Puññakkhettaṃ paramasukhadaṃ dhammarājaṃ namāmi.</p>\r\n<p class="bodytext"><a name="para18"></a><span class="paranum">18</span>. Evaṃ sattarasamagāthāya sugataṃ vanditvā idāni saṃsāroghaṃ taritādīhi navahi guṇehi thomitvā vanditukāmo <span class="bld">maggoḷumpena</span>tyādigāthamāha. Ayaṃ pana ma bha na ta ta gaṇehi ca garudvayena ca racitattā sattarasakkharehi yuttā mandakkantāgāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘mandakkantā ma bha na tata gā go yugutvassa kehī’’ti. Tassā panāyamattho. Yassaṃ paṭipādaṃ yugutvassakehi catu cha sattayatīhi ma bha na ta ta gā magaṇa bhagaṇa nagaṇa tagaṇa tagaṇa garū ca go garu ca ce yuttā, mandakkantāgāthānāmāti. Yugutvassakehīti ettha ca yugasaddassa maggaphalacatubbidhapurisayugaḷavācakattā yugasaddo catusaṅkhyāvācako, utūnaṃ hemantasisira vassanta gimha vassāna sarada utuvasena chabbidhattā utusaddo chasaṅkhyāvācako. Assasaddo sattasaṅkhyāvācakoti </p>','\r',char(13)),'\n',char(10)),'-18-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',116,replace(replace('<p class="bodytext">veditabbo<a name="M0.0116"></a>. <span class="bld">Chappaccayaṭīkādīsu</span> hi ‘‘assasaddassa sattasaṅkhyāvācakabhāvo vutto’’ti.</p>\r\n<p class="bodytext">Tattha pana yo dhammarājā muhapaṭighāsādiullolavīciṃ saṃsāroghaṃ maggoḷumpena tari, abhayaṃ pārapattaṃ pajānaṃ tāṇaṃ leṇaṃ asamasaraṇaṃ ekatitthaṃ patiṭṭhaṃ puññakkhettaṃ paramasukhadaṃ taṃ dhammarājaṃ ahaṃ namāmīti sambandho. Etissaṃ pana atthakkamena vaṇṇayissāmi. <span class="bld">Muhapaṭighāsādi ullolavīci</span>nti mohadosataṇhādisaṅkhātaṃ mahātaraṅga khuddaūmikaṃ. Muyhanti etena sampayuttadhammā, sayaṃ vā muyhati, muyhanamattamevetanti muho, moho. Muhadhātu vekhitte ṇa. Paṭihaññanti sampayuttadhammā, sayaṃ vā paṭihaññati paṭihaññanamattamevetanti paṭigho, doso. Paṭipubba hanadhātu hiṃsāgatīsu a. Hanassa gho. Asanaṃ icchanaṃ āsā, isati icchatīti vā āsā, taṇhā. Isudhātu icchāyaṃ a. Issa ā. Muho ca paṭigho ca āsā ca muhapaṭighāsā, te ādi yesanteti muhapaṭighāsādi, lobhādikilesā labbhanti. Ettha ca ādisaddena māna diṭṭhi vicikicchā thina uddhacca ahirika anottappakilese ca akusalacittuppāde ca saṅgaṇhāti. Uddhaṃ udakaṃ lolati ākuletīti ullolo, mahātaraṅgo. Upubbalola dhātu ummādane ṇa. Tīraṃ ulati gacchatīti vā ullolo. Uladhātu gatiyaṃ olapaccayo. Vimhayaṃ vicittaṃ cinotīti vīci, khuddakaūmi. Vipubba cidhātu caye kvi. Ullolo ca vīci ca ullolavīci. Kesuci potthakesu ca kullolavīcintipāṭho dissati. Na so yujjati, kasmā, kullasaddassa uḷumpatthavācakattā. Ullolasaddoyeva hi mahātaraṅgattha vācako hotīti. Vuttañhi <span class="bld">abhidhānappadīpikāyaṃ</span> ‘‘ullolo tu ca kallolo, mahāvīcisu kathyate’’ti.</p>\r\n<p class="bodytext"><span class="bld">Mūlapaṇṇāsaṭṭhakathāyañca</span> ‘‘kullo nāma taraṇatthāya kalāpaṃ katvā bandho. Pattharitvā baddhā pana padaracāṭi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',117,replace(replace('<p class="bodytext">ādayo <a name="M0.0117"></a> uḷumpo’’ti vutto. Muhapaṭighāsādieva ullolavīci. Sā asmiṃ saṃsāroghe atthīti muhapaṭighāsādiullolavīci. Samāsantataddhitāyaṃ. Pītisukhaṃ, mahāsannaṃ, rattidivantyādīsu viya. Evaṃ panettha vacanattho kātabbo. Pīti ca sukhañca pītisukhaṃ, taṃ assa jhānassa atthi, tasmiṃ vā vijjatīti pītisukhaṃ, jhānaṃ. Mahantaṃ āsanaṃ mahāsanaṃ, tassa bhāvo mahāsannaṃ. Ratti ca divā ca rattidivaṃ. Rattidivaṃ kataṃ rattidivanti. Atha vā ullolavīci viya muhapaṭighāsādi ullolavīci, saṃsāroghaṃ labbhati. Upamā bahubbīhisamāsoyaṃ. Yathā pana mahāsamudde bhāyitabbā mahātaraṅgaūmivegādayo ca saṃsumāra timi tipiṅgalādi macchamakārādayo ca okiṇṇā honti, evaṃ mohādikilesā ca akusalacittuppādā ca tehi nibbattā bhayānakā bahū jātijarābyādhimaraṇadukkhādayo ca khandhādayo ca okiṇṇā hontīti vuttaṃ hoti. <span class="bld">Saṃsārogha</span>nti saṃsārasaṅkhātaṃ oghaṃ samuddajalappavāhaṃ. Saṃpunappunaṃ abbocchinnaṃ vā sarati gacchati pavattatīti saṃsāro. Saṃpubbasaradhātu gatiyaṃ ṇa. Khandhadhātuāyata nānaṃ abbocchinnaṃ pavattatīti attho. Tena vuttaṃ <span class="bld">aṭṭhakathāsu</span> –</p>\r\n<p class="gatha1">‘‘Khandhānañca paṭipāṭi, dhātuāyatanāna ca;</p>\r\n<p class="gathalast">Abbocchinnaṃ vattamānā, saṃsāroti pavuccatī’’ti.</p>\r\n<p class="bodytext">Satte ottharitvā ajjhottharitvā heṭṭhā hanati haratīti ogho, jalappavāho. Avapubba hanadhātu haraṇe ṇa. Hanassa gho. Avasaddo ottharaṇattho. Attani patite satte heṭṭhā katvā haneti osīdāpetīti vā ogho, jalappavāho. Avapubba hanadhātu osīdāpane ṇa. Avasaddo heṭṭhābhāgattho. Ogho viyāti ogho, saṃsāro. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',118,replace(replace('<p class="bodytext">Atha <a name="M0.0118"></a> vā sattasaṅkhāre ottharitvā ajjhottharitvā abhibhavitvā vaṭṭasmiṃ hanati haratīti ogho, ohaneti osīdāpeti vaṭṭasmiṃ sattasaṅkhāreti vā ogho, rāgādi kilesā labbhanti. Saṃsāroeva ogho saṃsārogho, taṃ saṃsāroghaṃ.</p>\r\n<p class="bodytext"><span class="bld">Maggoḷumpenā</span>ti ettha maggaoḷumpenāti padacchedo. Aṭṭhamaggaṅgasaṅkhātena kullenāti attho. Ettha ca maggo vuccati aṭṭhaṅgikamaggo. Magganti gacchanti buddhā ca ariyasāvakā ca agataṃ disaṃ nibbānanti maggo. Aṭṭhaṅgiko maggo. Maggadhātu gatiyaṃ a. Vuttañhi <span class="bld">paṭisambhidāmagge</span> ‘‘etena maggena buddhā ca sāvakā ca agataṃ disaṃ nibbānaṃ gacchantīti aṭṭhaṅgiko maggo’’ti. Atha vā nibbānaṃ maggati gavesatīti maggo, nibbānatthikehi maggiyati gavesiyatīti vā maggo, aṭṭhaṅgiko maggo. Ñāṇena dassiyatīti attho. Maggadhātu gavesane a. Vuttañhetaṃ <span class="bld">mahāvaggasaṃyuttaṭṭhakathāyaṃ</span> ‘‘maggoti kenaṭṭhena maggo, nibbānaṃ magganaṭṭhena nibbānatthikehi magganiyaṭṭhena cā’’ti. Atha vā maggiyati paṭipajjiyatīti maggo, nibbānatthikehi aṭṭhaṅgiko maggo paṭipajjiyatīti attho. Uḷu vuccati udakaṃ, tato pāti rakkhatīti uḷupo, soyeva niggahitāgamavasena uḷumpo, kullo. Uḷusaddūpapadapādhātu rakkhane kvi. Maggoeva uḷumpo maggoḷumpo, maggasaṅkhāto vā uḷumpo tathā, maggasadiso vā uḷumpo tathā, maggo viya vā uḷumpo tathā, maggo ca uḷumpo ca maggoḷumpo. Atha vā uḷumpoeva maggo maggoḷumpo, uḷumpasaṅkhāto vā maggo tathā, uḷumpa sadiso vā maggo tathā, uḷumpo viya vā maggo tathā, uḷumpo ca maggo ca maggoḷumpoti evaṃ rūpakasadisaupamā samupekkhatthavasena ca parapakkha sakapakkhatthavasena ca vacanattho veditabbo. Tena maggoḷumpena. <span class="bld">Tarī</span>ti atari. Dhammarājā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',119,replace(replace('<p class="bodytext">hi <a name="M0.0119"></a> aṭṭhamaggaṅganāvāya saṃsāroghaṃ taritvā nibbānatīraṃ pāpuṇāti. Atītā buddhā pāpuṇiṃsu. Anāgatā buddhā pāpuṇissantīti vuttaṃ hoti. Vuttañhi <span class="bld">mahāvaggasaṃyutte</span></p>\r\n<p class="gatha1">‘‘Ekāyanaṃ jātikhayantadassī,</p>\r\n<p class="gatha2">Maggaṃ pajānāti hitānukampī;</p>\r\n<p class="gatha3">Etena maggena tariṃsu pubbe,</p>\r\n<p class="gathalast">Tarissantiyeva taranti ogha’’nti; <span class="bld">Buddhavaṃse</span> ca</p>\r\n<p class="gatha1">‘‘Saṃsārasotaṃ chinditvā, viddhaṃsetvā tayo bhave;</p>\r\n<p class="gathalast">Dhammanāvaṃ samāruyha, tāremi janataṃ bahu’’nti.</p>\r\n<p class="bodytext"><span class="bld">Abhaya</span>nti jātijarābyādhimaraṇabhayādīnaṃ abhāvato nibbhayaṃ. So hi bhagavā natthi bhayaṃ etassāti abhayoti vuccati. Atha vā abhayanti khemaṃ. Tañhi nibbānaṃ natthi jātiādibhayaṃ etthāti abhayanti vuccati. Etasmiṃ pana dutiyavikappe taṃ padaṃ pārantipade visesanaṃ. <span class="bld">Pārapatta</span>nti ettha pāranti saṃsāravaṭṭassa paratīraṭṭhena vuccati nibbānaṃ. Tañhi udakaṃ pāti rakkhatīti pāraṃ, samuddapāraṃ. Pādhātu rakkhane aro. Atha vā pāreti samattheti taraṅgādayo vāretunti pāraṃ. Pāradhātu samatthiyaṃ a. Pāraṃ viyāti pāranti vuccati. Nītatthena pana evaṃ vacanattho kātabbo. Jāti bhayādiṃ pāti rakkhati etthāti pāraṃ, pāreti samattheti ettha vā jātibhayādayo vāretunti pāraṃ, nibbānaṃ. Taṃ pajjittha adhigamitthāti pārapatto, dhammarājā. Pārasaddūpapada padadhātu gatiyaṃ ta. Atha vā mutto moceyyantiādinā nayena pavattassa mahābhinīhārassa sakalassa saṃsāradukkhassa vā sabbaññutañāṇādiguṇānaṃ vā pāraṃ pariyantaṃ patto gatoti pārapatto, dhammarājā. Pārasaddo hi pariyanta vācako. Taṃ pārapattaṃ. <span class="bld">Pajāna</span>nti sabbasattānaṃ. Tehi kammakilesehi pakārena janiyati nibbattiyatīti pajāti vuccanti. Tesaṃ pajānaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',120,replace(replace('<p class="bodytext"><span class="bld">Tāṇa</span>nti <a name="M0.0120"></a> tāyanaṭṭhena tāṇaṃ. Sattānaṃ saṃsāra bhayato tāyati rakkhatīti tāṇo, dhammarājā. Tādhātu rakkhane yu. Atha vā tāṇanti sattānaṃ saṃsāravaṭṭadukkhato paripālanaṭṭhena tāṇaṃ. So hi dhammarājā sattānaṃ saṃsāravaṭṭadukkhato dhammadesanāya magga phalanibbānasukhaṃ dadanto tāyati pāletīti tāṇoti vuccati. Taṃ tāṇaṃ. <span class="bld">Leṇa</span>nti nilīyanaṭṭhena leṇaṃ, jātibhayādīhi pīḷitehi sattehi līyanti nilīyanti etthāti leṇo, dhammarājā. Līdhātu nilīyane yu. Atha vā leṇanti saṃsārabhayaṃ passantehi nilīyanaṭṭhena leṇaṃ. So hi dhammarājā saṃsāranissaraṇatthikehi līyanti nilīyanti etthāti leṇoti vuccati. Taṃ leṇaṃ. Dhammarājā hi saṃsārabhayehi pīḷitānaṃ sabbasattānaṃ tāṇaṃ leṇaṃ saraṇaṃ parāyanaṃ ṭhānaṃ hotīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Asamasaraṇa</span>nti sabbasattānaṃ asadisaavassayanaṃ, natthi etassa samo sadiso sīlādiguṇenāti asamo, dhammarājā. Sarati gacchati avassayati etthāti saraṇo, dhammarājā. Saradhātu gatimhi yu. Asamo asadiso hutvā saraṇoti asamasaraṇo, dhammarājā. Sambhāva nākammadhārayasamāsoyaṃ. Dhammarājā hi sabbasampattisukhānaṃ dāyakattā sabbasattānaṃ anaññasādhāraṇaṃ asamaṃ saraṇaṃ avassayanaṃ parāyanaṃ hotīti attho. Taṃ asamasaraṇaṃ. <span class="bld">Ekatittha</span>nti nibbānapāragamanānaṃ ekatitthabhūtaṃ. Taranti uttaranti ettha etena vā najjasamuddādinti titthaṃ. Najjasamuddādīnaṃ pārimatīragamanatthaṃ otaraṇaṭṭhānaṃ labbhati. Taradhātu otaraṇe tha. Rassa to, ikārāgamo. Manussānaṃ ekaṃ titthaṃ ekatitthaṃ, taṃ viyāti ekatittho, dhammarājā. Yathā hi titthena najjādiṃ tarantā manussā pārimatīraṃ sukhaṃ pāpuṇanti, na atitthena, evaṃ seṭṭhabhūtena buddhatitthena </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',121,replace(replace('<p class="bodytext">saṃsāroghaṃ <a name="M0.0121"></a> tarantā veneyyasattā nibbānapāraṃ sukhaṃ pāpuṇanti, na atitthena aññatitthiyena, tasmā dhammarājaṃ ekatitthoti thometi. Nītatthena pana eko seṭṭho hutvā nibbānapāraṃ taranti uttaranti etthāti eka tittho, dhammarājā. Khemaṃ nibbānapāraṃ taritukāmā veneyyasattā ettha buddhatitthe desitadhammaṃ sutvā desitadhammānurūpaṃ paṭipajjitvā saṃsāroghaṃ aṭṭhamaggaṅganāvāya chinditvā khemaṃ nibbānapāraṃ sukhaṃ taranti, ititasmā etthātipadena niddiṭṭho so buddho sabbasattānaṃ eko seṭṭho tittho nāma. Na aññatitthiyā ekatitthā nāma hontīti attho. Taṃ ekatitthaṃ.</p>\r\n<p class="bodytext"><span class="bld">Patiṭṭha</span>nti sabbasattānaṃ mahādīpaṃ viya patiṭṭhaṃ ādhārabhūtaṃ. Paṭisaraṇaṃ leṇaṃ parāyananti attho. Patiṭṭhahanti etthāti patiṭṭho, dhammarājā. Patipubbaṭṭhādhātu gatinivattimhi a. Dhammarājā hi sabbasukhatthikānaṃ sattānaṃ mahādīpo viya patiṭṭho paṭisaraṇo leṇo parāyano hotīti attho, taṃ patiṭṭhaṃ. <span class="bld">Puññakkhetta</span>nti nibbānasukhatthikānaṃ puññabījassa khettabhūtaṃ. Puññatthikānaṃ puññabījassa vapanaṭhānaṃ khettabhūtaṃ vā, attano kārakaṃ pavati sodhetīti puññaṃ, kusalaṃ. Pudhātu sodhane ṇyo. Atha vā paraṃ pujjabhāvaṃ janetīti puññaṃ, sadā pūjitaṃ vā janetīti puññaṃ, kusalaṃ. Pūjasaddūpapada janadhātu ṇyo. Niruttinayena saddasiddhi veditabbā. Avasesaṃ apuññaṃ punāti sodhetīti vā puññaṃ, kusalaṃ. Pudhātu sodhane ṇyo. Khittaṃ vuttaṃ bījaṃ tāyati rakkhatīti khettaṃ, kedārādi. Khipasaddūpapada tādhātu rakkhane kvi. Bījāni vapantānaṃ manussānaṃ mahapphalabhāvakaraṇena bījaṃ rakkhatīti attho. Atha vā khipanti vapanti ettha bījānīti khettaṃ. Khipadhātu peraṇe khepe vā ta. Khettaṃ viyāti khetto. Dhammarājā. Nītatthena pana puññabījāni vapantānaṃ sattānaṃ mahapphala </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',122,replace(replace('<p class="bodytext">bhāvakaraṇena <a name="M0.0122"></a> khittaṃ vuttaṃ puññabījaṃ tāyati rakkhatīti khetto. Atha vā nibbānatthikehi puññabījāni khipiyanti vapiyanti etthāti khetto. Dhammarājāti kātabbo. Puññānaṃ khettaṃ puññakkhetto, dhammarājā. Yathā hi rañño vā amaccādīnaṃ vā sālīnaṃ vā yavānaṃ vā virūhanaṭṭhānaṃ rañño vā amaccādīnaṃ vā sālikhettaṃ yavakhettanti vuccati. Evaṃ dhammarājā sabbalokassa puññamahabbalabhāvakaraṇena puññānaṃ virūha naṭṭhānaṃ dhammarājaṃ nissāya sattalokassa nānāppakārahita sukhasaṃvattanikāni puññāni virūhanti, tasmā dhammarājā puññakkhettanti abhitthavitoti vuttaṃ hoti. Taṃ puññakkhettaṃ.</p>\r\n<p class="bodytext"><span class="bld">Paramasukhada</span>nti uttamaṃ nibbānasukhaṃ dadamānaṃ. Vuttañhi ‘‘nibbānaṃ paramaṃ sukha’’nti. Ettha ca paramasaddo uttamatthavācako anipphannapāṭi padiko. Atha vā paraṃ atirekaṃ ariyehi mānitabbanti paramaṃ, nibbānaṃ. Parapubbamādhātu māne a. Pakārena ariyehi ramiyati etthāti vā paramaṃ, nibbānaṃ. Papubbaramudhātu ramane ṇa. <span class="bld">Abhidhānaṭīkāyaṃ</span> pana ‘‘paraṃ paccanīkaṃ māretīti paramaṃ. Pakaṭṭhabhāve ramatīti paramaṃ. Ṇo’’ti vuttaṃ. Tassa sukhaṃ paramasukhaṃ. Paramaṃ uttamaṃ vā sukhaṃ paramasukhaṃ. Taṃ dadātīti paramasukhado, dhammarājā. Paramasukhasaddūpapada dādhātu dāne kvi. Dhammarājā pana veneyyasattānaṃ dhammadesanāya ca catupaccayappaṭiggahaṇena ca nibbānasukhaṃ detīti vuttaṃ hoti. Taṃ paramasukhadaṃ. <span class="bld">Dhammarāja</span>nti buddhaṃ. So hi lokiya lokuttaradhammehi devamanussānaṃ rañjeti tosetīti dhammarājāti. Atha vā pāramīdhammeneva buddhattaṃ patvā rājā jātoti dhammarājāti. Pāramīdhammeneva jāto vā rājāti dhammarājāti ca vuccati. Taṃ dhammarājaṃ ahaṃ namāmīti sambandho.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Yo dhammarājā muha paṭighāsādiullolavīciṃ mohadosataṇhādisaṅkhātaṃ mahātaraṅga khuddakaūmikaṃ, saṃsāroghaṃ saṃsārasaṅkhātaṃ oghaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',123,replace(replace('<p class="bodytext">samuddajalappavāhaṃ<a name="M0.0123"></a>. Maggoḷumpena aṭṭhamaggaṅgasaṅkhātena kullena tari atariṃ, abhayaṃ khemaṃ, jātiādīnaṃ abhāvato nibbhayaṃ vā, pārapattaṃ nibbānapāraṃ pattaṃ adhigataṃ, pajānaṃ sabba sattānaṃ, tāṇaṃ tāyanaṭṭhena tāṇaṃ rakkhaṇaṃ. Leṇaṃ nilīyanaṭṭhena leṇaṃ nilīyanaṃ, asamasaraṇaṃ sabbasattānaṃ asadisaavassayanaṃ, ekatitthaṃ nibbānapāragamanānaṃ ekatittha bhūtaṃ, patiṭṭhaṃ sabbasattānaṃ mahādīpaṃ viya patiṭṭhaṃ ādhārabhūtaṃ, puññakkhettaṃ nibbānasukhatthikānaṃ puññabījassa khettabhūtaṃ, puññatthikānaṃ puññabījassa vapanaṭṭhānaṃ khettabhūtaṃ vā, paramasukhaṃ uttamaṃ nibbānasukhaṃ dadamānaṃ, dhammarājaṃ buddhaṃ lokiyalokuttara dhammehi pajānaṃ rañjāpanaṃ vā, pāramīdhammeneva buddhabhāvaṃ jātaṃ buddhaṃ vā, ahaṃ tīhi dvārehi namāmīti.</p>\r\n<p class="centered">Aṭṭhārasamavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> Kaṇḍambaṃmūle parahitakaro yo munindo nisinno;</p>\r\n<p class="gatha2">Accheraṃ sīghaṃ nayanasubhagaṃ ākulaṇṇaggijālaṃ;</p>\r\n<p class="gatha3">Dujjāladdhaṃsaṃ munibhijahitaṃ pāṭiheraṃ akāsi;</p>\r\n<p class="gathalast">Vande taṃ seṭṭhaṃ paramaratijaṃ iddhidhammehupetaṃ.</p>\r\n<p class="bodytext"><a name="para19"></a><span class="paranum">19</span>. Evaṃ aṭṭhārasamagāthāya dhammarājaṃ vanditvā idāni sāvatthiyaṃ kaṇḍambamūle iddhipāṭiherādīhi guṇehi munindassa thometvā vanditukāmo <span class="bld">kaṇḍambaṃmūle</span>tyādi gāthamāha. Ayaṃ pana matanayayayagaṇehi ca pañca yati chayati sattaya tīhi ca racitattā aṭṭhārasakkharehi yuttā kusumitalatā vellitāgāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘mo to no yo yā kusumitalatā vellitākkhutvisīhī’’ti. Ayaṃ panettha yojanā. Yassaṃ paṭipādaṃ akkhutvisīhi pañcayati chayati sattayatīhi mogaṇo ca togaṇo ca nogaṇo ca yogaṇo ca yā yagaṇadvayañca ce </p>','\r',char(13)),'\n',char(10)),'-19-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',124,replace(replace('<p class="bodytext">yuttā<a name="M0.0124"></a>, sā gāthā kusumitalatā vellitā kusumitalatā vellitā gāthā nāmāti. Ettha ca akkhasaddassa pañcindriyavācakattā akkhasaddo pañcasaṅkhyāvācakoti veditabbo. Utūnaṃ hemantasisīravassantagimhavassānasaradavasena chabbidhattā utusaddo chasaṅkhyāvācakoti. Isisaddo vipassīādīnaṃ buddhānaṃ sattavidhattā sattasaṅkhyāvācakoti veditabboti.</p>\r\n<p class="bodytext">Tattha pana parahitakaro yo munindo kaṇḍambamūle nisinno pāṭiheraṃ sīghaṃ akāsi, taṃ munindaṃ ahaṃ vande vandāmīti sambandho. Atthakkamena panettha vaṇṇayissāmi. <span class="bld">Parahitakaro</span>ti attanā paresaṃ sabbasattānaṃ hitaṃ atthaṃ karonto. Hinoti vaḍḍhati sukhaṃ etenāti hitaṃ, atthaṃ payojanaṃ vā. Hidhātu vaḍḍhane ta. Taṃsamaṅgino lokiyalokuttarasukhaṃ vaḍḍhatīti attho. Paresaṃ hitaṃ parahitaṃ, taṃ karotīti parahitakaro, munindo. So hi dīpaṅkarassa bhagavato pādamūle laddhabyākaraṇato paṭṭhāya attano sukhaṃ pajahanto sattalokassa hitasukhatthāya kappasata sahassādhikāni cattāri asaṅkhyeyyāni samatiṃsapāramiyo pañcamahāpariccāge ca karonto sabbaññubuddhabhāvaṃ pattakāle ca pañcacattālīsavassāni dhamma desanāya sattalokassa lokiyalokuttarahita sukhaṃ karotīti parahitakaroti thometi. <span class="bld">Yo</span>ti yo yādiso. <span class="bld">Munindo</span>ti buddho. So hi agāriya muni, anagāriyamuni, sekkhamuni, asekkhamuni, paccekamunivasena pañcannaṃ munīnaṃ indoti munindoti vuccati. <span class="bld">Kaṇḍambamūle</span>ti ettha kaṇḍaambamūleti padacchedo. Chandānurakkhaṇatthaṃ bindāgamo. Kaṇḍanāmikena pasenadīkosalarañño uyyānapālena ropitassa setambarukkhassa samīpeti attho. Ettha ca kaṇḍanti tasseva nāmaṃ. Atha vā kaṃ vuccati sukhaṃ, taṃ ḍeti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',125,replace(replace('<p class="bodytext">gacchati <a name="M0.0125"></a> pāpuṇātīti kaṇḍo, uyyānapālo. Kaṃsaddūpapadaḍi dhātu gatiyaṃ ṇa. Kaṃ vuccati vā udakaṃ, taṃ rukkhesu siñcanaṃ ṭināti jānātīti kaṇḍo, uyyānapālo uyyāna nāyako vā. Phalakāmehi janehi amiyati gamiyatīti ambo, setambarukkho. Amadhātu gatiyaṃ bo. Kaṇḍanāmikena uyyānapālena ropito ambo kaṇḍambo, tassa mūlaṃ samīpanti kaṇḍambamūlaṃ, tasmiṃ kaṇḍambamūle.</p>\r\n<p class="bodytext"><span class="bld">Nisinno</span>ti vasamāno. Vimhāpaniyaṭṭhena <span class="bld">accheraṃ</span>. Ayañhi accherasaddo vimhayatthavācako anipphannapāṭi padiko. Atha vā ābhuso vimhayo carati pavattatīti accheraṃ, pāṭiheraṃ. Āpubba caradhātu gatiyaṃ a. Caradhātussaccherādeso. Ākārassa rasso. Andhassa pabbatārohanaṃ viya niccaṃ na carati na pavattatīti accheraṃ, pāṭiheraṃ. Napubba caradhātu gatiyaṃ a. Yathā hi andhassa pabbātārohanaṃ niccaṃ na hoti, kadāciyeva hoti, evameva bhagavato pāṭihāriyaṃ niccaṃ na hoti, kadāciyeva hoti. Tasmā taṃ accheranti vuccati. Tena vuttaṃ <span class="bld">nettiaṭṭhakathāyaṃ</span> ‘‘yaṃ abhiṇhaṃ na pavattati, taṃ acchariya’’nti. Atha vā accharaṃ paharituṃ yogganti acchariyaṃ. Yoggataddhite ṇiyapaccayo. Acchariyanti hi vattabbe chandānurakkhaṇatthaṃ iyasaddalopena accheranti vuttaṃ. Vuttañhetaṃ <span class="bld">sīlakkhandhavaggaṭṭhakathāyaṃ</span> ‘‘andhassa pabbatārohanaṃ viya niccaṃ na hotīti acchariyaṃ. Ayaṃ tāva saddanayo. Ayaṃ pana aṭṭhakathānayo, accharāyogganti acchariyaṃ, accharaṃ paharituṃ yuttanti attho’’ti. <span class="bld">Nayanasubhaga</span>nti devamanussānaṃ pasādacakkhussa sobhanapattaṃ. Samavisamaṃ dassentaṃ attabhāvaṃ netīti nayanaṃ, pasādacakkhu. Nīdhātu naye yu. Tassa subhaṃ sobhanaṃ gacchatīti nayanasubhagaṃ, pāṭiheraṃ. Nayanasubha saddūpapadagamudhātu gatiyaṃ kvi. Taṃ nayanasubhagaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',126,replace(replace('<p class="bodytext"><span class="bld">Ākulaṇṇaggijāla</span>nti <a name="M0.0126"></a> ettha ākulaaṇṇaaggijālanti padacchedo. Parikiṇṇa udakadhāra aggisikhāvantaṃ. Atha vā parisamantato kulaṃ pakiṇṇaṃ aññamaññamissakaṃ yamakaṃ yamakaṃ udakajālaaggijālavantanti attho. Ābhuso kulati udakaggijālaṃ aññamaññamissakaṃ karotīti ākulaṃ, udakaggijālaṃ. Āpubbakuladhātu karaṇe a. Ābhusaṃ kulati bandhati aññamaññanti vā ākulaṃ. Atha vā āsamantato kulati yamakaṃ yamakaṃ udakaggijālaṃ pavattatīti ākulaṃ, udakaggijālaṃ. Udakajālaṃ aggijālena aggijālañca udakajālena sammisso hotīti attho. Ananti jīvanti etena sattāti aṇṇaṃ, udakaṃ. Anadhātu jīvāyaṃ a. Nassa ṇo. Tassa dvittaṃ. Dvibhāvo vā. Atha vā arati gacchati taṃ taṃ ṭhānanti aṇṇaṃ. Aradhātu gatimhito. Tassa annādeso, dhātvantalopo, nnassa ṇṇattaṃ. Aṇṇassa dhāraṃ aṇṇaṃ. Uttarapadalopena. Kuṭilabhāvena ajati gacchatīti aggi, ajadhātu gatiyaṃ i. Jassa ggo. Jalati dibbatīti jālaṃ, sikhaṃ. Jaladhātu dittiyaṃ ṇa. Aggino jālaṃ aggijālaṃ. Aṇṇañca aggijālañca aṇṇaggijālaṃ, ākulaṃ parikiṇṇaṃ aṇṇaggijālaṃ assāti ākulaṇṇaggijālaṃ, yamaka pāṭiheraṃ labbhati. Taṃ ākulaṇṇaggijālaṃ.</p>\r\n<p class="bodytext"><span class="bld">Dujjāladdhaṃsa</span>nti duṭṭhu diṭṭhivādasaṅkhātajālaṃ viddhaṃsitaṃ. Duṭṭhu jalati bandhati saṃsāravaṭṭeti dujjālaṃ, diṭṭhivādajālaṃ. Titthiya vādo hi natthi dinnantiādinayapavatto saṃsārato nissaritumappadānavasena jālasadisattā dujjālanti attho. Dupubba jaladhātu bandhane ṇo. Taṃ dhaṃsati viddhaṃsatīti dujjāladdhaṃsaṃ, yamakapāṭihāriyaṃ. <span class="bld">Munibhijahita</span>nti munibhi ajahitanti padacchedo. Tattha munibhīti sabbasammāsambuddhehi. Te hi monaṃ vuccati sabbaññutañāṇaṃ, taṃ etesaṃ atthīti munīti vuccanti. Kāyamoneyya vacīmoneyya manomoneyyaṃ etesaṃ atthīti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',127,replace(replace('<p class="bodytext">vā <a name="M0.0127"></a> muni. Assatthitaddhite ṇīpaccayo. Atha vā idhaloka paralokaṃ attahita parahitaṃ hitāhitañca munanti jānantīti munīti vuccanti. Tehi munībhi.</p>\r\n<p class="bodytext"><span class="bld">Ajahita</span>nti avijahitaṃ apariccattaṃ vā, hātabbaṃ cajitabbanti jahitaṃ. Hādhātu cāge ta. Na jahitaṃ ajahitaṃ, pāṭiheraṃ. Taṃ ajahitaṃ. Bhagavato hi sāvatthinagarassa dakkhiṇadvāre kaṇḍambarukkhamūle kataṃ pāṭihāriyañca bhagavā pana tasmiṃ pāṭihāriyāvasāne tāvatiṃsapuraṃ gato, tasmiṃ pārichattakamūlamhi paṇḍukambalanāmake silāsane sannisinno devānaṃ abhidhammakathāya desanā ca sabbasammāsambuddhehi avijahitā dhammatāyeva hotīti vuttaṃ hoti. <span class="bld">Buddhavaṃsaṭṭhakathāya</span>ñhi samatiṃsadhammatāpakāsane sāvatthi nagaradvāre yamakapāṭihāriyakaraṇaṃ. Tāvatiṃse bhavane abhidhammadesanāti dhammatābhāvena vuttaṃ. Tasmā pāṭiheraṃ munībhijahitanti thometi.</p>\r\n<p class="bodytext"><span class="bld">Pāṭihera</span>nti paccanīkapaṭiharaṇavasena pāṭihāriyaṃ, paṭipakkhe titthiye madditvā hinoti pavattatīti pāṭiheraṃ. Paṭipubbahidhātu gatiyaṃ ṇo. Hissa heraṇideso. Atha vā bhagavato ca sāsanassa ca paṭipakkhe titthiye harati apanetīti pāṭiheraṃ, iddhividhañāṇaṃ. Paṭipubbaharadhātu apanayane ṇo. Paṭiharati apaneti sattānaṃ diṭṭhimānopagatāni cittānīti vā pāṭiheraṃ. Appasannānaṃ pasādaṃ āharatīti vā pāṭiheraṃ. Samāhite citte vigatūpakkilese katakiccena paṭipacchā haritabbaṃ pavattetabbanti vā pāṭiheraṃ. Paṭīti hi ayaṃsaddo pacchāti etassa atthaṃ bodheti. Tasmiṃ paṭipaviṭṭhamhi añño āgacchi brāhmaṇotiādīsu viya. Atha vā paṭipunappunaṃ udakaggijālaṃ abbocchinnaṃ yamakaṃ yamakaṃ hutvā harati pavattatīti pāṭiheraṃ, yamakapāṭihāriyaṃ. Tañca pana iddhipāṭihāriya, ādesanāpāṭihāriya, anusā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',128,replace(replace('<p class="bodytext">sanī <a name="M0.0128"></a> pāṭihāriyavasena tividhaṃ hoti. Tesu pana idha iddhipāṭihāriyaṃ adhippetanti. <span class="bld">Sīgha</span>nti khippaṃ āsuṃ lahuṃ vā. Taṃ pana akāsītipade kriyāvisesanaṃ. Seti lahu hutvā pavattatīti sīghaṃ. Sidhātu gatimhi ghapaccayo. Dīgho. <span class="bld">Akāsī</span>ti akari.</p>\r\n<p class="bodytext">Ettha ca pāṭihāriyamevaṃ veditabbaṃ. Bhagavā hi abhisambodhito sattame saṃvacchare sāvatthinagarassa dakkhiṇadvāre kaṇḍambarukkhamūle devānamindena sakkena āṇāpitena visukammadevaputtena nimmite dvādasayojane ratana maṇḍape bhagavatā nimmite yojanappamāṇe ratanapallaṅke ratanacaṅkame ca nisīditvā tiyojanike dibbasetacchatte dhārayamāne samantato dvādasayojanāya parisāya atthādānaparidīpanaṃ titthiyamaddanaṃ uparimakāyato aggikkhandho pavattati, heṭṭhimakāyato udakadhārā pavattati, heṭṭhimakāyato aggikkhandho, uparimakāyato udakadhārā. Puratthimakāyato aggikkhandho, pacchimakāyato udakadhārā. Pacchimakāyato aggikkhandho, puratthimakāyato udakadhārā, dakkhiṇaakkhito aggikkhandho, vāmaakkhito udakadhārā. Vāmaakkhito aggikkhandho, dakkhiṇaakkhito udakadhārā. Dakkhiṇakaṇṇasotato aggikkhandho, vāmakaṇṇasotato udakadhārā. Vāmakaṇṇasotato aggikkhandho, dakkhiṇakaṇṇasotato udakadhārā. Dakkhiṇa nāsikāsotato aggikkhandho, vāmanāsikāso tato udakadhārā. Vāmanāsikāsotato aggikkhandho, dakkhiṇanāsikāsotato udakadhārā. Dakkhiṇaaṃsakūṭato aggikkhandho, vāmaaṃsakūṭato udakadhārā. Vāmaaṃsakūṭato aggikkhandho, dakkhiṇaaṃsakūṭato udakadhārā. Dakkhiṇahatthato aggikkhandho, vāmahatthato udakadhārā. Vāmahatthato aggikkhandho, dakkhiṇahatthato udakadhārā. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',129,replace(replace('<p class="bodytext">Dakkhiṇapassato <a name="M0.0129"></a> aggikkhandho, vāmapassato udakadhārā. Vāmapassato aggikkhandho, dakkhiṇapassato udakadhārā. Dakkhiṇapādato aggikkhandho, vāmapādato udakadhārā. Vāmapādato aggikkhandho, dakkhiṇapādato udakadhārā. Aṅgulaṅgulehi aggikkhandho, aṅgulantarikāhi udakadhārā. Aṅgulantarikāhi aggikkhandho, aṅgulaṅgulehi udakadhārā. Ekekalomato aggikkhandho, ekekalomato udakadhārā. Lomakūpato lomakūpato aggikkhandho, lomakūpato lomakūpato udakadhārā pavattati. Channaṃ vaṇṇānaṃ nīlānaṃ pītānaṃ lohitakānaṃ odātānaṃ mañjaṭṭhānaṃ pabhassarānaṃ ubbahanabhūtānaṃ yamakā yamakā vaṇṇā pavattanti. Bhagavā caṅkamati, nimmito tiṭṭhati vā nisīdati vā seyyaṃ vā kappeti. Bhagavā tiṭṭhati, nimmito caṅkamati vā nisīdati vā seyyaṃ vā kappeti. Bhagavā nisīdati, nimmito caṅkamati vā tiṭṭhati vā seyyaṃ vā kappeti. Bhagavā seyyaṃ kappeti, nimmito caṅkamati vā tiṭṭhati vā nisīdati vā. Nimmito caṅkamati, bhagavā tiṭṭhati vā nisīdati vā seyyaṃ vā kappeti. Nimmito tiṭṭhati, bhagavā caṅkamati vā nisīdati vā seyyaṃ vā kappeti. Nimmito nisīdati, bhagavā caṅkamati vā tiṭṭhati vā seyyaṃ vā kappeti. Nimmito seyyaṃ kappeti, bhagavā caṅkamati vā tiṭṭhati vā nisīdati vāti vividhaṃ yamakapāṭihāriyaṃ titthiyānaṃ maddanatthāya akāsīti. Ettha ca pana yo koci evarūpaṃ pāṭihāriyaṃ kātuṃ samattho ce. So āgacchatūti codanāsadisattā vuttaṃ atthādānaparidīpananti. Atthādānañhi anuyogo, paṭipakkhassa atthassa ādānaṃ gahaṇanti katvā, tassa paridīpanaṃ atthādānaparidīpanaṃ. Titthiyamaddananti pāṭihāriyaṃ karissāmāti kuhāyanavasena pubbe uṭṭhitānaṃ titthiyānaṃ maddanaṃ. Tañca tathā kātuṃ asamatthatā sampādanameva. Tadetaṃ padadvayaṃ yamakapāṭihāriyanti etena sambandhitabbanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',130,replace(replace('<p class="bodytext"><span class="bld">Seṭṭha</span>nti <a name="M0.0130"></a> pasatthataraṃ. Tassa vacanattho heṭṭhā vuttoyeva. <span class="bld">Paramaratija</span>nti devamanussānaṃ uttamaratiṃ janakaṃ. Paramānaṃ uttamasādhūnaṃ cittaramanaṃ janāpetaṃ vā, ettha ca paṭhamavikappe paramasaddo uttamavācako anipphannapāṭipadiko nipāto. Dutiyavikappe sādhuvācako nipphannapāṭipadiko. Devamanussānaṃ paramaṃ uttamaṃ ratiṃ janetīti paramaratijo, munindo. Paramaratisaddūpapada janadhātu janane kvi. Atha vā pakārena saddhamme ramantīti paramā, sādhū labbhanti. Papubba ramudhātu kīḷāyaṃ a. Tesaṃ cittassa ratiṃ ramanaṃ janetīti paramaratijo, munindo. Taṃ paramaratijaṃ. So hi dhamma desanāya ca pāṭihāriyena iddhiyā sarīrasobhagga sampattiādīhi ca devamanussānaṃ cittassa abhiratiṃ abhikkantaṃ janetīti paramaratijanti thometi.</p>\r\n<p class="bodytext"><span class="bld">Iddhidhammehupeta</span>nti iddhidhammehi upetanti padacchedo. <span class="bld">Iddhi dhammehī</span>ti acinteyyaappameyyehi buddhaiddhisaṅkhātaguṇehi. Dhammasaddo hi guṇe vattati buddhadhammātiādīsu viya. Ijjhanaṃ samijjhanaṃ iddhi. Ijjhati samijjhatīti vā iddhi. Ijjhati samijjhati yathicchitattho etāyāti iddhi. Idhadhātu vuddhiyaṃ i, tipaccayo vā. Bhagavā etāya iddhiyā yathicchitattho iddho vuddho ukkaṃsagato hotīti attho. Dhāreti pakāseti taṃsamaṅgino dabbanti dhammo, guṇo. Iddhieva dhammo iddhidhammo, tehi dhammehi. <span class="bld">Upeta</span>nti samannāgataṃ. Upagacchati sampajjatīti upeto, munindo. Upapubbaidhātu gatiyaṃ ta. Taṃ upetaṃ.</p>\r\n<p class="bodytext">Iddhi pana adhiṭṭhānā iddhi, vikuppanā iddhi, manomayā iddhi, ñāṇavipphārā iddhi, samādhivipphārā iddhi, ariyā iddhi, kammavipākajā iddhi, puññavato iddhi, vijjāmayā iddhi, tattha tattha sammāpayogapaccayā ijjhanaṭṭhena iddhi cāti dasavidhā hoti. Tattha pakatiyā eko bahukaṃ āpajjati. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',131,replace(replace('<p class="bodytext">Sataṃ <a name="M0.0131"></a> vā sahassaṃ vā āvajjitvā ñāṇena adhiṭṭhāti bahuko homīti evaṃ vibhajitvā dassitā iddhi adhiṭṭhāna vasena nipphannattā adhiṭṭhānā iddhi nāma. Pakativaṇṇaṃ vijahitvā kumāravaṇṇaṃ vā dasseti nāgavaṇṇaṃ vā supaṇṇavaṇṇaṃ vā yakkhavaṇṇaṃ vā…pe… vividhampi senābyūhaṃ dassetīti evaṃ āgatā iddhi pakativaṇṇavijahanavikāravasena pavattattā vikuppanā iddhi nāma. Idha bhikkhu imamhā kāyā aññaṃ kāyaṃ abhinimmināti rūpaṃ manomayanti iminā nayena āgatā iddhi sarīrabbhantare aññasseva manomayassa sarīrassa nipphattivasena pavattattā manomayā iddhi nāma. Ñāṇuppattito pubbe vā pacchā vā taṅkhaṇe vā ñāṇānubhāvanibbattā visesā ñāṇavipphārā iddhi nāma. Ettha ca āyasmato bākulattherassa ca āyasmato saṃkiccattherassa ca āyasmato bhūtapālattherassa ca ñāṇavipphārā iddhi veditabbā. Samādhito pubbe vā pacchā vā taṅkhaṇe vā samathānubhāvena nibbattā visesā samādhivipphārā iddhi nāma. Ettha ca āyasmato sāriputtattherassa ca āyasmato sañjīvattherassa ca āyasmato khāṇukoṇḍaññattherassa ca uttarāya upāsikāya ca sāmāvatiyā upāsikāya ca samādhivipphārā iddhi veditabbā.</p>\r\n<p class="bodytext">Paṭikūlādīsu appaṭikūlasaññī vihārādikā pana ariyā iddhi nāma. Pakkhidevādīnaṃ vehāsagamanādikā kamma vipākajā iddhi nāma. Yathāha ‘‘katamā vipākajā iddhi. Sabbesaṃ pakkhīnaṃ sabbesaṃ devānaṃ ekaccānaṃ manussānaṃ ekaccā nañca vinipātikānaṃ ayaṃ kammavipākajā iddhī’’tiādi. Cakkavattiādīnaṃ vehāsagamanādikā pana puññavato iddhi nāma. Yathāha ‘‘katamā puññavato iddhi. Rājā cakkavatti vehāsaṃ gacchati saddhiṃ caturaṅginiyā senāya antamaso </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',132,replace(replace('<p class="bodytext">assabandhagobandhapurise <a name="M0.0132"></a> upādāya. Jotikassa gahapatissa puññavato iddhi. Jaṭilassa gahapatissa puññavato iddhi. Ghosakassa gahapatissa puññavato iddhi. Meṇḍakassa gahapatissa puññavato iddhi. Pañcannaṃ mahāpuññānaṃ puññavato iddhī’’ti. Vijjādharādīnaṃ vehāsagamanādikā pana vijjāmayā iddhi nāma. Ettha ca ādisaddena ākāse antalikkhe hatthimpi dasseti. Assampi…pe… vividhampi senābyūhaṃ dasseti tyādiṃ saṅgaṇhāti. Tena tena sammāpayogena tassa tassa kammassa ijjhanaṃ tattha tattha sammā payogapaccayā ijjhanaṭṭhena iddhi nāma.</p>\r\n<p class="bodytext">Ayaṃ panettha saṅkhepo. Vitthāro pana <span class="bld">paṭisambhidāmagge</span> iddhikathāya ca <span class="bld">visuddhimaggaṭṭhakathāya</span> ca gahetabbo. Tāsu pana iddhīsu bhagavato ṭhapetvā vijjāmayiddhiṃ avasesā atthīti veditabbā. Atha vā bhagavato tīhi vijjāhi ca aṭṭhahi vijjāhi ca samannāgatattā sabbā buddhaiddhiyo santīti vijjāmayiddhipi labbhatīti daṭṭhabbā. Taṃ munindaṃ ahaṃ vande vandāmīti sambandho.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepatthayojanā</span>. Parahitakaro attanā paresaṃ sabbasattānaṃ atthaṃ payojanaṃ karonto, yo munindo yo yādiso buddho, kaṇḍambaṃmūle kaṇḍanāmikena pasenadikosalarañño uyyānapālena ropitassa setambarukkhassa samīpe sannisinno vasamāno accheraṃ vimhāpanīyaṃ nayanasubhagaṃ devamanussānaṃ pasāda cakkhussa sobhanapattaṃ ākulaṇṇaggijālaṃ parikiṇṇaudaka dhāraaggisikhavantaṃ parisamantato kulaṃ parikiṇṇaṃ aññamañña missakaṃ yamakaṃ yamakaṃ udakajālaaggijālavantaṃ vā, dujjāladdhaṃsaṃ duṭṭhuṃ micchādiṭṭhivādasaṅkhātaṃ jālaṃ viddhaṃsitaṃ, munibhijahitaṃ sabbasammāsambuddhehi avijahitaṃ, pāṭiheraṃ paccanīka paṭihārakaṃ pāṭihāriyaṃ udakaggiyamakaṃyamakaṃ pāṭihāriyaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',133,replace(replace('<p class="bodytext">vā<a name="M0.0133"></a>, sīghaṃ khippaṃ akāsi akari, seṭṭhaṃ pasatthataraṃ, paramaratijaṃ devamanussānaṃ uttamaratiṃ janakaṃ uttamānaṃ sādhūnaṃ cittaramanaṃ janāpetaṃ vā, iddhidhammehi acinteyyaappameyyehi buddhaiddhi guṇehi upetaṃ samannāgataṃ, taṃ munindaṃ ahaṃ sirasā vande vandāmīti.</p>\r\n<p class="centered">Ekūnavīsatimavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="bodytext"><a name="para20"></a><span class="paranum">20</span>. Munindakko yveko dayudayaruṇo ñāṇavitthiṇṇabimbo, vineyyappāṇoghaṃ kamalakathitaṃ dhammaraṃsīvarehi. Subodhesī suddhe tibhavakuhare byāpitakkittinañca, tilokekaccakkhuṃ dukhamasahanaṃ taṃ mahesiṃ namāmi.</p>\r\n<p class="bodytext"><a name="para20"></a><span class="paranum">20</span>. Evaṃ ekūnavīsatimāya gāthāya munindaṃ vanditvā idāni vineyyappāṇoghaṃ dhammadesanāya catusaccadhammaṃ bodhaneyyādīhi navahi guṇehi thomitvā munindakkaṃ vanditukāmo <span class="bld">munindakko</span>tyādigāthamāha. Ayaṃ pana ya ma na sa ra ragaṇehi ca ekagarunā ca chayatīhi ca sattayatīhi ca racitattā ekūnavīsatakkharā meghavipphujjitāgāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘rasutvassehi ymā na sa ra ra garū meghavipphujjitā sā’’ti. Ayaṃ panettha yojanā. Yassaṃ paṭipādaṃ rasutvassehi cha cha satta yatīhi ymā yagaṇamagaṇā ca na sa rara garū nagaṇa sagaṇa ragaṇa ragaṇa ekagaru ca ce yuttā, sā gāthā meghavipphujjitā megha vipphujjitā gāthā nāmāti. Ettha ca rasānaṃ kasāva titta madhuralavaṇa ambila kaṭukavasena chabbidhattā rasasaddo chasaṅkhyāvācakoti daṭṭhabbo. Utūnaṃ hemanta sisīra vasanta gimhavassānasaradavasena chabbidhattā utusaddo chasaṅkhyāvācakoti. Assasaddo sattasaṅkhyāvācakoti daṭṭhabbo. Assasaddassa hi sattasaṅkhyāvācakabhāvo <span class="bld">chandaṭīkāsu</span> vuttoti.</p>','\r',char(13)),'\n',char(10)),'-20-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',134,replace(replace('<p class="bodytext">Tattha <a name="M0.0134"></a> pana dayudayaruṇo ñāṇavitthiṇṇabimbo eko yo munindakko suddhe tibhavakuhare kamalakathitaṃ vineyyappāṇoghaṃ dhammaraṃsīvarehi subodhesi byāpitakkitti nañca tilokekaccakkhuṃ dukhamasahanaṃ mahesiṃ taṃ munindakkaṃ namāmīti yojanā.</p>\r\n<p class="bodytext">Imissā gāthāya ca atthakkameneva sukhajānanatthaṃ vaṇṇayissāma, na saddakkamena. <span class="bld">Dayudayaruṇo</span>tipadassa dayāudayaaruṇoti padavibhāgo kātabbo. Mahākaruṇāsaṅkhātaudayapabbatuggatasūriyāruṇo. Atha vā udayapabbatato uggatasūriyāruṇo viya mahākaruṇā vāti attho. Ettha ca dayāsaddo karuṇāmettāsu vattati. Adayāti vuccati nikkaruṇatanti ettha hi karuṇāyaṃ. Dayāpannoti ettha mettāyaṃ vattati. Mettācittataṃ āpannoti attho. Idha pana karuṇā adhippetā.</p>\r\n<p class="bodytext">Ayaṃ pana vacanattho. Dayati hiṃsati kāruṇikaṃ yāva yathādhippetaṃ parassa hitanibbattiṃ na pāpuṇāti tāvāti dayā, mahākaruṇā. Dayadhātu hiṃsāyaṃ a. Dayati anuggaṇhāti pāpajanampi etāyāti vā dayā. Dayadhātu anuggahaṇe. Dayati attano sukhampi pahāya khedaṃ gaṇhātīti vā dayā. Dayadhātu gahaṇe. Atha vā dayanti gaṇhanti etāya bodhisattā saṃsāradukkhañca sabbaññutañāṇañcāti dayā, mahākaruṇā. Mahābodhisattā hi buddhabhāvāya abhinīhārakaraṇakāle hatthagatampi arahatta phalaṃ chaṭṭetvā saṃsārasāgarato satte samuddharitukāmā anassāsakaraṃ atibhayānakaṃ saṃsāradukkhaṃ pacchimabhave ca saha amatadhātupaṭilābhena anekaguṇasamalaṅkataṃ sabbaññutañāṇañca gaṇhantīti attho. Karuṇāmūlakā hi sabbe buddhaguṇā. Tena vuttaṃ <span class="bld">aṅguttaraṭīkāyaṃ</span> ‘‘sabbesañca buddhaguṇānaṃ karuṇā ādi, tannidānabhāvato’’ti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',135,replace(replace('<p class="bodytext">Api <a name="M0.0135"></a> ca dayanti anurakkhanti satte etāya, sayaṃ vā anuddayati anudayamattameva vā etanti dayā. Dayadhātu anurakkhaṇe apaccayo. Udeti vaḍḍhetīti udayo, pabbato. Udidhātu pasavane ṇa. Āsamantato ālokaṃ karonto uṇati gacchatīti aruṇo, sūriyaraṃsī viseso. Āpubbauṇadhātu gatiyaṃ ṇa. Dayāsaṅkhātato udayapabbatato uggatasūriyassa aruṇo yassāti dayudayaruṇo. Atha vā udayapabbatato uggatasūriyāruṇo viya dayā mahākaruṇā yassāti dayudayaruṇo, munindakko. Yathā hi uggatasūriyassa aruṇo ālokassa ādi hoti, evaṃ bodhisatto dīpaṅkarabuddhassa pādamūle karopayātaṃ nibbānasukhamosajja mahākaruṇāya saṃsārasamuddato satte samuddharitukāmo buddhabhāvāya sudukkarā dānādayo dasa pāramiyo ca pañca mahāpariccāge ca kappasatasahassādhikāni cattāri asaṅkhyeyyāni akāsīti karuṇā buddhabhāvāya ādi. Tasmā buddhasseva dayudayaruṇoti abhitthavituṃ arahatīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Ñāṇavitthiṇṇabimbo</span>ti sabbaññutañāṇasaṅkhāto vitthāra parimaṇḍalo. Vitthāracakkavāḷamaṇḍalaṃ viya vipulārammaṇa sabbaññutañāṇavā. Saṅkhatāsaṅkhatabhedaṃ sabbeñeyyadhammaṃ jānātīti ñāṇaṃ, sabbaññutañāṇaṃ. Ñādhātu avabodhaneyu. Visesena tanoti vitthāretīti vitthiṇṇaṃ, ñāṇaṃ. Vipubbatanudhātu vitthāre tapaccayo. Tassa iṇṇādeso. Dhātvantassa lopo, asarūpadvittaṃ. Atha vā vittharati pattharatīti vitthiṇṇaṃ. Tharadhātumevettha viseso. Vamati parimaṇḍalākārena gacchati pavattatīti bimbaṃ, ñāṇamaṇḍalaṃ. Vamadhātu gatiyaṃ bo. Assi. Vassa bo.</p>\r\n<p class="bodytext"><span class="bld">Ṇvādimoggallāne</span> pana ‘‘vama uggiraṇe ba. Vamissa vassa bittaṃ. Vamati uggiratīti bimbaṃ, sarīra’’nti vuttaṃ. Taṃ pana bimbasaddassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',136,replace(replace('<p class="bodytext">sarīravācake <a name="M0.0136"></a> yuttameva. Na parimaṇḍalavācake. Idha pana bimbasaddassa parimaṇḍalavācakattā. Ācariyā pana idha bimbasaddassa atthaṃ cakkamaṇḍalanti vadanti. Taṃ pana vicāretabbaṃ. Kasmā, <span class="bld">abhidhānappadīpikāyaṃ</span> ‘‘bimbaṃ tu paṭibimbe ca, maṇḍale bimbikāphale’’ti ca. <span class="bld">Taṭṭīkāyañca</span> ‘‘maṇḍale paṭimaṇḍale’’ti ca vuttattā. Ñāṇaṃ sabbaññutañāṇasaṅkhātaṃ vitthiṇṇaṃ vitthāraṃ bimbaṃ parimaṇḍalaṃ assāti ñāṇavitthiṇṇabimbo. Atha vā vitthiṇṇaṃ vitthāraṃ bimbaṃ cakkavāḷaparimaṇḍalaṃ viya vipulārammaṇaṃ ñāṇaṃ sabbaññutañāṇaṃ assāti ñāṇavitthiṇṇabimbo, munindakko. Yathā hi cakkavāḷassa anantattā cakkavāḷamaṇḍalaṃ vipulaṃ mahantaṃ hoti, eva meva buddhassa sabbaññutañāṇaṃ anantārammaṇattā vipulaṃ mahantaṃ. Tasmā tassa ñāṇavitthiṇṇabimboti thometīti. <span class="bld">Yveko</span>ti yo ekoti padacchedo. Yekotipi katthaci pāṭho. Tassa pana yo ekoti padacchedo kātabbo. Taṃ pana pāṭhadvayaṃ yuttameva. Yvekoti hi pāṭhe ādesasandhi hoti. Yekoti pāṭhe pana lopasandhīti ayametesaṃ viseso. Yo munindakkoti sambandho.</p>\r\n<p class="bodytext"><span class="bld">Eko</span>ti asahāyo, adutiyako eka buddhabhūtoti attho. Ettha ca ekasaddo aññaseṭṭhāsahāya saṅkhyādīsu dissati. Tathāhesa sassato attā ca loko ca, idameva saccaṃ moghamaññanti ittheke abhivadantītiādīsu aññatthe dissati. Cetaso ekodibhāvantiādīsu seṭṭhe. Eko vūpakaṭṭhotiādīsu asahāye. Ekova kho bhikkhave khaṇo ca samayo ca brahmacariyāyātiādīsu saṅkhyāyaṃ. Idha pana asahāye adhippeto. Ayaṃ panassa vacanattho. Asahāyo hutvā eti gacchati pavattatīti eko, adutiyako hutvā anuttaraṃ sammāsambodhiṃ eti jānāti paṭivijjhatīti vā eko, munindakko. Edhātu idhātu vā ñāṇagatīsu ṇvu. Atha vā eko bhagavā pabbajjasaṅkhātena eko. Adutiyaṭṭhena eko, taṇhāya pahānaṭṭhena </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',137,replace(replace('<p class="bodytext">eko<a name="M0.0137"></a>, ekantavītarāgoti eko, ekantavītadosoti eko, ekantavītamohoti eko, ekantanikkilesoti eko, ekāya maggaṃ gatoti eko, anuttaraṃ sammāsambodhiṃ abhisambuddhoti ekotiādinā āgatena mahāniddesanayenapettha attho veditabbo.</p>\r\n<p class="bodytext">Api ca anantacakkavāḷesuyeva ekova buddho eti uppajjati vikasati vāti eko. Vuttañhi ekanipāta <span class="bld">aṅguttara pāḷiyaṃ</span> ‘‘aṭṭhānametaṃ bhikkhave anavakāso, yaṃ ekissā lokadhātuyā dve arahanto sammāsambuddhā apubbaṃ acarimaṃ uppajjeyyuṃ, netaṃ ṭhānaṃ vijjati. Ṭhānañca kho etaṃ bhikkhave vijjati, yaṃ ekissā lokadhātuyā ekova arahaṃ sammāsambuddho uppajjeyya, ṭhānametaṃ vijjatī’’ti. <span class="bld">Tadaṭṭhakathāyañca</span> ‘‘ekissā lokadhātuyāti dasasahassilokadhātuyā. Tīṇi hi khettāni jātikkhettaṃ āṇakkhettaṃ visayakkhettanti. Tattha jātikkhettaṃ nāma dasasahassilokadhātu. Sā hi tathāgatassa mātukucchiṃ okkamanakāle nikkhamanakāle sambodhikāle dhammacakkappavattane āyu saṅkhāravossajjane parinibbāne ca kampati, koṭisatasahassa cakkavāḷaṃ pana āṇakkhettaṃ nāma. Āṭānāṭiyaparitta moraparittadhajaggaparitta ratanaparittādīnañhi ettha āṇā pavattati. Visayakkhettassa pana parimāṇaṃ natthi. Buddhānañhi yāvatakaṃ ñāṇaṃ, tāvatakaṃ ñeyyaṃ. Yāvatakaṃ ñeyyaṃ, tāvatakaṃ ñāṇaṃ. Ñāṇapariyantikaṃ ñeyyaṃ, ñeyyapariyantikaṃ ñāṇanti vacanato avisayo nāma natthi. Imesu pana tīsu khettesu ṭhapetvā imaṃ cakkavāḷaṃ aññasmiṃ cakkavāḷe buddhā uppajjantīti suttaṃ natthi. Na uppajjantīti pana atthi. Tīṇi hi piṭakāni vinayapiṭakaṃ, suttantapiṭakaṃ, abhidhammapiṭakaṃ. Tisso saṃgītiyo mahākassapattherassa saṃgīti, yasattherassa saṃgīti, moggaliputtattherassa saṃgīti, imā tisso </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',138,replace(replace('<p class="bodytext">saṃgītiyo <a name="M0.0138"></a> āruḷhe tepiṭake buddhavacane imaṃ cakkavāḷaṃ muñcitvā aññattha buddhā uppajjantīti suttaṃ natthi. Na uppajjantīti pana atthī’’ti.</p>\r\n<p class="bodytext">Ettha ca ekissā lokadhātuyāti dasasahassi lokadhātuyāti idaṃ jātikkhettaṃ sandhāya vuttaṃ. Dasasahassilokadhātu hi imaṃ lokadhātuṃ parivāretvā ṭhitā hoti. Tattakānaṃyeva jātikkhettabhāvo dhammatā vasena veditabbo. Imesu pana tīsu khettesūti iminā dasasahassilokadhātusaṅkhāte jātikkhette koṭisatasahassacakkavāḷasaṅkhāte āṇakkhette anantacakkavāḷa saṅkhāte visayakkhette cāti imesu tīsu buddhakkhettesu ito cakkavāḷato aññesu anantacakkavāḷesu buddhā nuppajjantīti dasseti. Tividhañhi buddhakkhettaṃ dassetvā imesu pana tīsu khettesūti ādhārabhāvena vuttaṃ, na niddhāraṇabhāvena. Kasmā, aṭṭhakathāyaṃ niddhāraṇīyapadassa abhāvato. Sati hi aṭṭhakathācariyo tassa padassa niddhāraṇatthe adhippete imesu tīsu khettesu āṇakkhette ṭhapetvā imaṃ cakkavāḷantiādi vucceyya. Na. Tasmā tassa padassa ādhāratthoyeva gahetabbo. Pubbanissayā cariyehi ca tassa padassa ādhārabhāvena atthayojanā katāti. Tatheva pana <span class="bld">abhidhammañāṇavibhaṅgapāḷiyañca,</span> tadaṭṭhakathāyañca uparipaṇṇāse <span class="bld">bahudhātusuttapāḷiyañca,</span> tadaṭṭhakathāyañca <span class="bld">nettipāḷiyañca</span> tadaṭṭhakathāyañca taṃ kāraṇaṃ āgatamevāti. Taṃ <span class="bld">aṅguttaraṭīkāyaṃ</span> pana ‘‘na uppajjantīti pana atthīti na me ācariyo atthi, sadiso me navijjatītiādi. Imissā lokadhātuyā ṭhatvā vadantena bhagavatā kiṃpanāvuso sāriputta atthetarahi añño samaṇo vā brāhmaṇo vā bhagavatā samasamo sambodhiyanti evaṃ puṭṭhohaṃ bhante noti vadeyyanti vatvā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',139,replace(replace('<p class="bodytext">tassa <a name="M0.0139"></a> kāraṇaṃ dassetuṃ aṭṭhānametaṃ anavakāso, yaṃ ekissā lokadhātuyā dve arahanto sammāsambuddhā apubbaṃ acarimaṃ uppajjeyyunti imaṃ suttaṃ āharantena dhamma senāpatinā ca buddhakkhettabhūtaṃ imaṃ lokadhātuṃ ṭhapetvā aññattha anuppatti vuttā hotīti adhippāyo’’ti vuttaṃ. Tatheva pana <span class="bld">ñāṇavibhaṅgamūlaṭīkāyaṃ, uparipaṇṇāsabahudhātu suttaṭīkāyañca</span> vuttaṃ. Ettha ca imaṃ lokadhātuṃ ṭhapetvā aññattha anuppatti vuttā hotīti iminā buddhakkhettabhūtato ito cakkavāḷato aññesu anantacakkavāḷesu buddhānaṃ anuppatti dassitā hoti. Aññatthāti hi sāmaññena vuttepi aṭṭhakathāyaṃ vuttena imesu tīsu khettesūti saddantarasannidhānena anantacakkavāḷabhūto visesattho gahetabbo. Sāmaññavacanopi hi saddo saddantarasannidhānena visesavisayoti.</p>\r\n<p class="bodytext">Ayaṃ panettha aparo nayo. Aṭṭhakathāyaṃ vutte buddhā hi uppajjamānā imasmiṃyeva cakkavāḷe uppajjanti. Uppajjanaṭṭhāne pana vārite ito aññesu cakkavāḷesu nuppajjantīti vāritameva hotītiettha vākye imasmiṃ yevātipade samabhiniviṭṭho evasaddo nivattāpanāvadhāraṇattho. Etena aññesu anantacakkavāḷesu aññesaṃ buddhānaṃ uppattiṃ nivatteti. Yadipi hi aññesu anantacakkavāḷesu aññe buddhā uppajjeyyuṃ. Evaṃ santesu saṅkhatāsaṅkhatabhedaṃ sabbadhammaṃ jānanto passanto amhākaṃ bhagavā aññacakkavāḷesu aññe buddhā uppajjantīti evaṃ vucceyya. Na. Taṇhaṅkarādayo hi aṭṭhavīsati sambuddhā ito cakkavāḷato aññesu cakkavāḷesu uppajjantītivacanaṃ tepiṭake buddhavacane natthi, tathā aṭṭhakathā ṭīkāsu ca. Tasmā ito cakkavāḷato aññesu ananta cakkavāḷesu aññe buddhā nuppajjantīti daṭṭhabbā. Vuttañhetaṃ suttantamahāvaggaṭṭhakathāyaṃ <span class="bld">mahāsamayasuttavaṇṇanāyaṃ</span> –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',140,replace(replace('<p class="bodytext">‘‘Athassa <a name="M0.0140"></a> etadahosi mādiso buddhoyeva sakkuṇeyya. Atthi pana katthaci añño buddhoti anantāsu lokadhātūsu anantañāṇaṃ pattharitvā olokento aññaṃ buddhaṃ na addasa. Anacchariyañcetaṃ, yaṃ idāni attanā samaṃ na passeyya. Yo jātadivasepi brahmajālasuttavaṇṇanāyaṃ vuttanayena attanā samaṃ apassanto aggohamasmi lokassāti appaṭivattiyaṃ sīhanādaṃ nadi. Evaṃ aññaṃ attanā samaṃ apassitvā cintesī’’ti. Tathevetaṃ. Purābhedasutta <span class="bld">mahāniddesaṭṭhakathāyañca,</span> taṃ <span class="bld">mahāsamayasuttaṭīkāyañca</span> ekissā lokadhātuyāti sutte āgatanayena sabbattheva pana apubbaṃ acarimaṃ dve buddhā nāma na honteva. Tenevāha ‘‘anantāsu loka dhātūsu…pe… na addasā’’ti saṃvaṇṇeti.</p>\r\n<p class="bodytext"><span class="bld">Apaṇṇakajātakaṭṭhakathāyañca</span> ‘‘satthā upāsakā heṭṭhā avīciṃ upari bhavaggaṃ paricchedaṃ katvā aparimāṇāsu lokadhātūsu sīlādīhi guṇehi buddhena sadiso nāma natthi. Kuto adhikataro…pe… āhā’’ti vuttaṃ. Imāsu dvīsu aṭṭhakathāsu ca anantāsu lokadhātūsūti ca aparimāṇāsu lokadhātūsūti ca anantaaparimāṇa saddena vuttaṃ. Tehi dvīhi saddehi ṭīkāvacanena ca anantāsu lokadhātūsu aññesaṃ buddhānaṃ anuppattiṃ dasseti.</p>\r\n<p class="bodytext">Sammohavinodanīaṭṭhakathāya yojanāyaṃ pana imesu panātiādinā koṭisatasahassa cakkavāḷato aññasmiṃ cakkavāḷe buddhā na uppajjantīti ñāpeti. Mayaṃ pana evaṃ vitakkayāma. Aññasmiṃ cakkavāḷeti ito cakkavāḷato aññesu sabbesupi tīsu buddhakkhettesūti atthe gayhamāne sati koṭisatasahassacakkavāḷato aññasmiṃ cakkavāḷe buddhā na uppajjantīti viññāyati. Yadi pana aññasmiṃ cakkavāḷeti ito aññasmiṃ jātiāṇākhettabhūte cakkavāḷeti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',141,replace(replace('<p class="bodytext">atthe <a name="M0.0141"></a> gahite koṭisatasahassacakkavāḷato aññasmiṃ cakkavāḷe buddhānaṃ uppatti bhaveyyāti viññāyati. ‘‘Aṭṭhānametaṃ anavakāso, yaṃ ekissā lokadhātuyā dve arahanto sammāsambuddhā’’tiādipāḷiyā ca. ‘‘Aṭṭhānametaṃ anavakāso, yaṃ ekissā lokadhātuyā dve rājāno cakkavattī’’tiādipāḷiyā saddhiṃ kathanato koṭisatasahassacakkavāḷato buddhānaṃ anuppatti viya aññissā lokadhātuyā cakkavattirañño anuppatti bhaveyya. <span class="bld">Kathāvatthuppakaraṇe</span> ca koṭisatasahassacakkavāḷato aññasmiṃ cakkavāḷe buddhānaṃ uppatti na paṭisedhitā. Tasmā āṇakkhettabhūtato koṭisatasahassacakkavāḷato aññasmiṃ cakkavāḷe buddhassa bhaveyyāti ayaṃ amhākaṃ khanti. Vīmaṃsetvā gahetabbanti vuttaṃ.</p>\r\n<p class="bodytext">Ettha ca yaṃ kathāvatthuppakaraṇe ca…pe… na paṭisedhitāti vuttaṃ. Taṃ pana kathāvatthupāḷiyaṃ nattheva. Imissā pana sabbadisākathāyaṃ paravādīnaṃ parikappavacanamattameva atthi. Tena vuttaṃ tassaṃ pāḷiyaṃ ‘‘sabbadisā buddhā tiṭṭhantīti. Āmantā. Puratthimāya disāya buddho tiṭṭhatīti. Nahevaṃ vattabbe…pe… puratthimāya disāya buddho tiṭṭhatīti. Āmantā. Kinnāmo so bhagavā kiṃjacco kiṃgotto, kinnāmā tassa bhagavato mātāpitaro, kinnāmaṃ tassa bhagavato sāvakayugaṃ, ko nāmo tassa bhagavato upaṭṭhako, kiṃdisaṃ cīvaraṃ dhāreti, kiṃdisaṃ pattaṃ dhāreti. Katarasmiṃ gāme vā nigame vā nagare vā raṭṭhe vā janapade vāti. Nahevaṃ vattabbe’’tyādi.</p>\r\n<p class="bodytext"><span class="bld">Tadaṭṭhakathāyaṃ</span> pana ‘‘idāni sabbadisākathā nāma hoti. Tattha catūsu disāsu heṭṭhā uparīti samantato lokadhātusannivesaṃ sabbalokadhātūsu ca buddho atthīti attano vikappasippaṃ uppādetvā sabbadisāsu buddhā tiṭṭhantīti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',142,replace(replace('<p class="bodytext">yesaṃ…pe… <a name="M0.0142"></a> seyyathāpi mahāsaṅghikānaṃ, te sandhāya pucchā sakavādissa. Paṭiññā itarassa. Puratthimāyāti puṭṭho sakyamuniṃ sandhāya paṭikkhipati. Puna puṭṭho laddhivasena aññalokadhātuyaṃ ṭhitaṃ sandhāya paṭijānāti. Kinnāmo so bhagavātiādi sace tvaṃ jānāsi. Nāmādivasena naṃ kathehīti codanatthaṃ vuttaṃ. Iminā upāyena sabbattha attho veditabbo’’ti vuttaṃ. Ettha ca catūsu disāsu…pe… seyyathāpi mahāsaṅghikānanti iminā paravādīnaṃ laddhiṃ dasseti. Tattha pana catūsu disāsu ca heṭṭhā ca upari ca iti evaṃ samantato lokadhātusannivesaṃ atthīti yojanā. Yesaṃ…pe… mahāsaṅghikānanti yesaṃ paravādīnaṃ laddhi hoti. Kesaṃ laddhi viya. Mahāsaṅghikānaṃ laddhi seyyathāpīti attho. Te sandhāyāti te paravādīpuggale sandhāya ñāṇena dhāretvā sallakkhetvāti attho. Pucchā sakavādissāti sakavādissa sabbadisāsu buddhā tiṭṭhantīti pucchā hoti. Paṭiññā itarassāti paravādissa paṭijānanā hoti. Puratthimāyāti puṭṭhoti sakavādinā puratthi māyātiādinā pucchite sati. Sakyamuninti sakyavaṃsa khattiyajātibhūtaṃ amhākaṃ gotamabuddhaṃ sandhāya. Paṭikkhipatīti paravādīpuggalo apaneti. Puna puṭṭhoti sakavādinā puna puratthimāya buddho tiṭṭhatīti pucchite sati. Laddhivasenāti attano aññalokadhātuyaṃ buddho atthi ca uppajjati cāti micchāladdhivasena paṭijānātīti paravādī puggalo paṭijānāti. Yadi pana aññalokadhātuyaṃ buddho tiṭṭhati. Evaṃ sati tassa nāmagottādiṃ tvaṃ kathehīti sakavādissa codanāvacanaṃ dassetuṃ kinnāmo so bhagavātiādimāha. Iminā ca ito cakkavāḷato aññesu anantacakkavāḷesu aññesaṃ buddhānaṃ abhāvaṃ dasseti. Tena vuttaṃ <span class="bld">kathāvatthupāḷiyaṃ</span> ‘‘kinnāmo so bhagavā kiṃjacco…pe… janapadevāti. Nahevaṃ vattabbe’’ti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',143,replace(replace('<p class="bodytext">Ettha <a name="M0.0143"></a> ca pana sakavādinā kinnāmotyādinā pucchite paravādīpuggalo attano laddhiyā sabbalokadhātūsu uppajjamānānaṃ buddhānaṃ nāmādiṃ ajānanto apassanto na hevaṃ vattabbeti paṭikkhipati. Tasmā panettha sakavādī puggalassa laddhiyā aññesu anantacakkavāḷesu aññesaṃ buddhānaṃ uppajjanābhāvo viññāyati. Yadipi hi aññānantacakkavāḷesu aññabuddhā uppajjeyyuṃ. Evaṃ santesu buddhassa matiṃ yathābhūtato jānanto passanto mahāpañño iddhimā dibbacakkhuādīhi chaḷabhiññehi sampanno paṭisambhidā patto satthārā dinnamātikānayena kathāvatthupāḷiṃ desento āyasmā mahāmoggaliputtatthero aññānantacakkavāḷesu aññabuddhānaṃ uppattiṃ katheyya ca. Nāti. Tasmā ayaṃ kathāvatthupāḷi ca tadaṭṭhakathāvacanañca aññānantacakkavāḷesu aññabuddhānaṃ abhāvadīpane sabbaṭṭhakathāṭīkāsu vuttavacanehi daḷhataraṃ padhānavacanameva hotīti daṭṭhabbaṃ.</p>\r\n<p class="bodytext">Na pana buddhāeva aññānantacakkavāḷesu na uppajjeyyuṃ, atha kho laddhabyākaraṇā mahābodhisattā ca paccekabodhi sattā aggasāvakamahāsāvakabodhisattā paripākindriyā sāvakabodhisattā ca buddhakkhettabhūte imasmiṃyeva cakkavāḷe uppajjanti, na aññattha. Kasmā, tesu buddhānaṃ anuppannattā, tesaṃ pana abhinīhāro pāramīsambharaṇaṃ ñāṇaparipācanañca tasmiṃeva samijjhati, na aññattha. Tena vuttaṃ <span class="bld">mahāpadāna suttaṭīkāyaṃ</span> –</p>\r\n<p class="bodytext">‘‘Purimabuddhānaṃ mahābodhisattānaṃ paccekabuddhānañca nibbattiyā sāvakabodhisattānaṃ sāvakabodhiyā abhinīhāro sāvakapāramiyā sambharaṇaṃ paripācanañca buddhakkhettabhūte imasmiṃ cakkavāḷe jambudīpeeva ijjhati, na aññatthā’’ti. Tathevetaṃ <span class="bld">ekanipātaaṅguttaraṭīkāyañcāti</span>.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',144,replace(replace('<p class="bodytext">Etarahi <a name="M0.0144"></a> pana apare ācariyā ‘‘āṇakkhettabhūtato koṭisatasahassacakkavāḷato aññesu cakkavāḷesu buddhā uppajjantī’’ti vadanti. Tesaṃ pana vacanaṃ ayuttameva. Kasmā, aṭṭhakathāyaṃ vutte aññasmiṃ cakkavāḷeti ettha aññasaddassa ṭhapetvā imaṃ cakkavāḷantipadato aññassa apādānassa asambhavato ca anantacakkavāḷesu bahūsu buddhesu uppannesu buddhassa anacchariyattā ca. Acchariyamanusso hi sammāsambuddho. Vuttañhi ekanipāta <span class="bld">aṅguttarapāḷiyaṃ</span></p>\r\n<p class="bodytext">‘‘Ekapuggalo bhikkhave loke uppajjamāno uppajjati acchariyamanusso. Katamo ekapuggalo, tathāgato arahaṃ sammāsambuddho. Ayaṃ kho bhikkhave ekapuggalo loke uppajjati acchariyamanusso’’ti. Aññaṃ pana kāraṇaṃ mayā heṭṭhā vuttanayenapi viññūhi viññātabbanti. Aññe pana ācariyā <span class="bld">buddhaapadānapāḷiyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Disā dasavidhā loke, yāyato natthi antakaṃ;</p>\r\n<p class="gatha2">Tasmiñca disābhāgamhi, buddhakkhette asaṅkhayā;</p>\r\n<p class="gatha3">Pabhā pakittitā mayhaṃ, yamakā raṃsivāhanā;</p>\r\n<p class="gathalast">Etthantare raṃsijālaṃ, āloko vipulo bhave’’ti.</p>\r\n<p class="bodytext">Vuttāsu dvīsu gāthāsu paṭhamagāthāya atthaṃ buddhassa anādhippāyena gahetvā micchāñāṇena aññānantacakkavāḷesu uppajjamānā buddhā asaṅkhayāti vadanti. Tesaṃ panetaṃ ayuttameva asundarameva. Kasmā, pāḷiyā ca aṭṭhakathāṭīkāvacanehi ca viruddhattā ca tassā gāthāya atthaggahaṇassa micchattā ca. Imāya hi pāḷiyā gāthāya vā aññānantacakkavāḷesu aññe buddhā uppajjantīti atthe gayhamāne aṭṭhakathāsu imesu pana tīsu khettesu ṭhapetvā imaṃ cakkavāḷaṃ aññasmiṃ cakkavāḷe buddhā uppajjantīti suttaṃ natthi, na uppajjantīti pana atthīti vuttavacanena ca, tīṇi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',145,replace(replace('<p class="bodytext">hi <a name="M0.0145"></a> piṭakāni…pe… imā tisso saṃgītiyo āruḷhe tepiṭake buddhavacane…pe… suttaṃ natthi. Na uppajjantīti pana atthīti vuttavacanena ca ujukaṃ virujjhati ca. Bhaddantamahā buddhaghosādayo aṭṭhakathācariyā taṃ pāḷiṃ na passantīti attano duggahitena aṭṭhakathācariyānaṃ abbhakkhānaṃ āpajjati ca. Kiṃ panete tipiṭakadharā tikkhavisadajavana gambhīrādiñāṇikā aṭṭhakathācariyā na passeyyuṃ. Passeyyuṃ eva. Tasmā ayuttameva. Aṭṭhakathāsu pana āgataṃ suttaṃ natthītiādivacanameva yuttataraṃ hotīti daṭṭhabbaṃ.</p>\r\n<p class="bodytext">Ettha ca buddhakkhettetipadaṃ tasmiñca disābhāgamhītipade visesanaṃ, asaṅkhayātipadaṃ dutiyagāthāya pabhātipadassa visesananti daṭṭhabbaṃ. Ayaṃ panettha yojanā. Loke cakkavāḷaloke dasavidhā dasa koṭṭhāsā disā honti. Tattha koṭṭhāse yāyato yāyantassa gacchantassa me antakaṃ pariyosānaṃ natthi. Buddhakkhette buddhassa dasasahassisaṅkhātajātikkhettabhūte tasmiñca disābhāgamhi mayhaṃ asaṅkhayā saṅkhārahitā yamakā yugaḷā yugaḷā hutvā raṃsivāhanā raṃsivahantā raṃsimuñcamānā vā pabhā cakkaratanamaṇiratanādīnaṃ ālokā pakittitā pākaṭā pattharitā vā. Etthantare dasasahassacakkavāḷantare raṃsijālaṃ raṃsisamūhena vipulo bahutaro āloko bhave ahosinti. Ayamattho cakkindābhisirīsaddhamma dhajamahādhammarājādhirājagurūtiladdhalañchitena ācariyabudhena likhitoti daṭṭhabbo.</p>\r\n<p class="bodytext">Bhagavā hi imāhi gāthāhi cakkavattirājakāle mayhaṃcakkaratanamaṇīratanādīnaṃ asaṅkhayā pabhā buddhakkhettabhūte dasasahassicakkavāḷe pākaṭā pattharitāti dasseti. Na anantacakkavāḷesu uppajjanabuddhānaṃ asaṅkhayabhāvanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',146,replace(replace('<p class="bodytext">Vuttañhetaṃ <a name="M0.0146"></a> <span class="bld">buddhaapadānaṭṭhakathāyaṃ</span> ‘‘disā dasavidhā loketi cakkavāḷaloke dasavidhā dasakoṭṭhāsā disā honti. Tattha koṭṭhāse yāyato yāyantassa gacchantassa antakaṃ natthīti attho. Cakkavattikāle tasmiṃ mayā gataṭṭhāne disābhāge vā buddhakkhette buddhavisayā asaṅkhayā saṅkhārahitā. Pabhā pakittitāti tadā cakkavatti rājakāle mayhaṃ pabhā cakkaratanamaṇiratanādīnaṃ pabhā ālokā yamakā yugaḷā yugaḷā hutvā raṃsivāhanā raṃsimuñcamānā pakittitā pākaṭā. Etthantare dasasahassacakkavāḷantare raṃsijālaṃ raṃsisamūhaṃ āloko vipulo bahutaro bhave ahosinti attho’’ti. Ettha ca paṭhamagāthāya pubbaḍḍhassa ekavākyabhāvato disā dasavidhā loketi cakkavāḷaloke…pe… antakaṃ natthīti atthoti vuttaṃ. Aparaḍḍhassa dutiyagāthāyaṃ antogadhattā cakkavattikāle…pe… asaṅkhayā saṅkhārahitā pabhā pakittitāti tadā cakkavattikāle…pe… bhave ahosinti atthoti vuttaṃ. Tasmā asaṅkhayātipadaṃ dutiyagāthāya pabhātipadassa visesananti daṭṭhabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Jinālaṅkāraṭīkāyaṃ</span> pana evamassa sabbadhammesu appaṭihata ñāṇācāraṃ dassetvā idāni loke tādisassa aññassa abhāvaṃ dassento āha.</p>\r\n<p class="gatha1">‘‘Anantasattesu ca lokadhātusu,</p>\r\n<p class="gathalast">Ekova sabbepi samā na tenā’’ti.</p>\r\n<p class="bodytext">Tattha anantalokadhātūsu ekekissaṃ lokadhātuyā anantasattesu tena tathāgatena samo ekopi natthi. Sace eko pana samo na bhaveyya. Sabbe sattā ekajjhaṃ hutvā samā bhaveyyunti ce. Tathāpi natthi, sabbe ekajjhaṃ hutvāpi iminā ekakena samā na hontīti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',147,replace(replace('<p class="bodytext">adhippāyoti <a name="M0.0147"></a> vuttaṃ. Ettha ca anantalokadhātusūtiādinā ito cakkavāḷato aññesu anantacakkavāḷesu aññesaṃ buddhānaṃ abhāvaṃ dasseti. Evaṃ pana mayā pathavojaṃ uddharitvā dassito viya dassitehi atidaḷhatarehi sādhakavacanehi ito cakkavāḷato aññacakkavāḷesu buddhā uppajjantīti micchāvādo sūriyo andhakāraṃ paṭihanati viya paṭihaniyati apaniyatīti saddhahakaṃ daṭṭhabboti.</p>\r\n<p class="bodytext"><span class="bld">Munindakko</span>ti muninda akkoti padacchedo. Buddhādiccoti attho. Sabbadhamme manati jānātīti muni. Manadhātu ñāṇe i. Makāre assuttaṃ. Atha vā munāti sabbadhamme saka sāmaññalakkhaṇādinā paṭivijjhatīti muni. Munati hitāhitaṃ paricchindatīti vā muni, bhagavā. Munadhātu paṭivijjhane, catusaccadhamme munāti jānātīti vā monaṃ. Catumaggañāṇaṃ. Vuttañhi <span class="bld">saṃyuttaṭṭhakathāyaṃ</span> ‘‘monanti catumaggañāṇaṃ. Tañhi munātīti monaṃ, catusaccadhamme jānātīti attho’’ti. Monaṃ assa atthīti muni, bhagavā. Muni ca so indati veneyyasatte dhammena abhibhavati indo cāti munindo, bhagavā. Atha vā munīnaṃ pañcamunīnaṃ indoti munindo, bhagavā. Na kamati na gacchati pādena attano puññajavimānena gacchatīti akko, ādicco. Napubba kamudhātu gatiyaṃ kvi. Dhātvantalopo. Kassa dvittaṃ. Atha vā yugandharubbedhappamāṇe ākāse arati gacchatīti akko. Aradhātu gamane kapaccayo. Kassa dvittaṃ. Taṃ taṃ rāsito taṃ taṃ rāsiṃ arati yātīti vā akko. Atha vā mahantajutatāya akkiyati abhitthaviyati tappasannehi janehīti akko, ādicco. Akkadhātu thavane a. Munindo ca so akko cāti munindakko, akko ādicco viya vā munindoti munindakko, buddho. Yathā hi sūriyo </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',148,replace(replace('<p class="bodytext">attano <a name="M0.0148"></a> sahassaraṃsiyā ekacakkavāḷe timirandhakāraṃ vidhametvā dine dine navayojanasatasahassappadesaṃ koṭisahassappadesaṃ vā dasadisaṃ vā obhāsetvā virocamāno carati, evameva bhagavāpi attano saddhammaraṃ siyā buddhakkhettabhūtesu dasasahassacakkavāḷesu vineyyānaṃ avijjandhakāraṃ vidhametvā dine dine tasmiṃ tasmiṃ disābhāge veneyyasattasamūhaṃ catusaccadhammobhāsena jotayamāno virocanaṃ caratīti ṭhānānurūpaṃ munindakkoti thometi. Ayaṃ samāsopamā hīnūpamāti daṭṭhabbā.</p>\r\n<p class="bodytext"><span class="bld">Suddhe</span>ti rāgādimalehi visuddhe. Sujjhati visujjhatīti suddho, tibhavo. Sudhadhātu soceyyeti. <span class="bld">Tibhava kuhare</span>ti tibhavasaṅkhātakamalasare. Kamalasaraṃ viya tibhavasmiṃ vā bhavanti sattā etthāti bhavo. Bhūdhātu sattāyaṃ ṇa. Tayo bhavā tibhavā, kāmarūpārūpabhūmi labbhati. Kuṃ vuccati udakaṃ, taṃ haratīti kuharo. Kupubbaharadhātu haraṇe a. Tayo bhavā ca te kuharo cāti tibhavakuharo, kuharo viya tayo bhavāti vā tathā. Tasmiṃ tibhavakuhare. <span class="bld">Kamala kathita</span>nti uppalanti kathetabbaṃ kaṃ jalaṃ alayati vibhūsayatīti kamalaṃ, uppalaṃ. Kaṃsaddūpapada aladhātu vibhūsane a. Atha vā kamiyati icchiyatīti kamalaṃ. Kamudhātu icchāyaṃ alapaccayo. Kamalanti uppalanti kathiyatīti kamalakathitaṃ. <span class="bld">Vineyyappāṇogha</span>nti vineyyapāṇaoghanti padavibhāgo. Vinetabbasattasamūhanti attho. Vinetabbo dametabbo satthārāti vineyyo, satto. Vipubbanidhātu damane ṇyo. Paṇanti jīvanti sattā anenāti pāṇaṃ, jīvitindriyaṃ. Paṇadhātu jīvāyaṃ ṇa. Taṃ assa atthi tasmiṃ vā vijjatīti pāṇo, satto. Assatthitaddhite ṇapaccayo. Vineyyo ca so pāṇo cāti vineyyappāṇo. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',149,replace(replace('<p class="bodytext">Avayavena <a name="M0.0149"></a> saha hanati gacchati pavattatīti ogho, samūho. Avapubba hanadhātu gatiyaṃ ṇo. Avassa o. Hanassa gho. Vineyyappāṇānaṃ ogho samūhoti vineyyappāṇogho, taṃ.</p>\r\n<p class="bodytext"><span class="bld">Dhammaraṃsivarehī</span>ti uttamadhammasaṅkhātaraṃsīhi. Ettha ca dhammoti yathānusiṭṭhaṃ paṭipajjamāne satte catūsu apāyesu vaṭṭadukkhesu ca apatamāne katvā dhāretīti dhammo. Dharadhātu dhāraṇe rammapaccayo. Dhāreti ca apāyādinibbattakakilesaṃ viddhaṃsetīti attho. Caturiyamaggo ca nibbānañca nippariyāyato labbhati. Kasmā, ariyamaggassa kilesānaṃ samucchedappahānavasena samucchedakattā, nibbānassa ca nissaraṇappahānavasena tassa ārammaṇabhāvaṃ patvā tadatthasiddhihetuttā. Caturiyaphalañca pariyattidhammo ca pariyāyato labbhati. Kasmā, phalassa maggena viddhaṃsi tabbānaṃ kilesānaṃ paṭippassambhanappahānavasena maggānukūlappa vattattā. Pariyattidhammassa ca tadaṅgappahānavasena tadadhigama hetuttā. Atha vā kilese dhunāti kampati calati viddhaṃsetīti dhammo, caturiyamaggo. Dhunadhātu kampane rammo. Ariyehi dhāriyati paṭivijjhiyati sacchikariyati ārammaṇa karaṇavasenāti dhammo, nibbānaṃ. Dharadhātu ñāṇe. Dhaṃseti paṭippassambheti maggappahīnakilesavāsananti dhammo, ariyaphalaṃ. Dhaṃsadhātu viddhaṃsane. Attano sabhāvaṃ dhāreti ca dhāriyati ca paṇḍitehi na bālehīti dhammo, pariyattidhammo. Dhammo ca dhammo ca dhammo ca dhammo cāti dhammo eka sesanayena, dasavidho dhammo labbhati. Vuttañhetaṃ <span class="bld">chattavimāne</span> –</p>\r\n<p class="gatha1">‘‘Rāgavirāgamanejamasokaṃ,</p>\r\n<p class="gatha2">Dhammamasaṅkhatamappaṭikūlaṃ;</p>\r\n<p class="gatha3">Madhuramimaṃ paguṇaṃ suvibhattaṃ,</p>\r\n<p class="gathalast">Dhammamimaṃ saraṇattamupehī’’ti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',150,replace(replace('<p class="bodytext">Ayaṃ <a name="M0.0150"></a> panettha yojanā. Māṇava rāgavirāgaṃ rāgavigataṃ imaṃ dhammaṃ maggadhammañca anejaṃ ejāsaṅkhātataṇhāvirahitaṃ asokaṃ sokavirahitaṃ imaṃ dhammaṃ phaladhammañca, asaṅkhataṃ saṅkhatavirahitaṃ imaṃ dhammaṃ nibbānadhammañca. Appaṭikūlaṃ avirodhanaṃ madhuraṃ atthabyañjana sampannattā madhuraṃ, paguṇaṃ pakaṭṭhaguṇaṃ suvibhattaṃ suṭṭhu vibhajitabbaṃ, imaṃ dhammaṃ pariyattidhammañca saraṇattaṃ saraṇabhāvaṃ upehi upagacchāhīti.</p>\r\n<p class="bodytext">Ettha hi kāmarāgādibhedo sabbopi rāgo virajjati pahiyati etenāti rāgavirāgoti maggo kathito. Ejāsaṅkhātāya taṇhāya antonijjhānalakkhaṇassa ca sokassa taduppattiyā sabbaso parikkhīṇattā anejama sokanti phalaṃ kathitaṃ. Kenaci paccayena asaṅkhatattā dhammamasaṅkhatanti nibbānaṃ kathitaṃ. Avirodhadīpanato pana atthabyañjanassa sampannatāya pakaṭṭhaguṇavibhāvanato suṭṭhu vibhajitattā ca appaṭikūlantiādinā sabbopi pariyatti dhammo kathito. Vuttañhi <span class="bld">tadaṭṭhakathāyaṃ</span> ‘‘ettha hi rāgavirāganti maggo kathito. Anejamasokanti phalaṃ. Dhammamasaṅkhatanti nibbānaṃ. Appaṭikūlaṃ madhuramimaṃ paguṇaṃ suvibhattanti piṭakattayena vibhattā sabbadhammakkhandhā’’ti. Idha pana sāmaññato dasavidho dhammo. Visesato pana pariyatti dhammo adhippeto. Kasmā, subodhesīti vuccamānattā. Raṃsīti rasiyanti assādiyantīti raṃsī, rasanti assādenti sattā etāyāti vā raṃsī, rasadhātu assādane a ca ī ca. Atha vā rasiyati sinehiyati taṃ janehīti raṃsī. Rasadhātu sinehe. Niggahītāgamo.</p>\r\n<p class="bodytext"><span class="bld">Varo</span>ti uttamo, varitabbo patthitabboti varo, varati nivāreti hīnanti vā varo. Varadhātu patthane nivāraṇe vā a. Dhammo ca so raṃsi cāti dhammaraṃsi, dhammassa vā raṃsi dhammaraṃsi. Ayaṃ pana samāso abhedūpacārena </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',151,replace(replace('<p class="bodytext">vutto<a name="M0.0151"></a>. Varo ca so dhammaraṃsi cāti dhammaraṃsivaro, tehi dhammaraṃsivarehi. <span class="bld">Subodhesī</span>ti catusaccadhammaṃ paṭivijjhayamānena suṭṭhu pabodhesi vikāsetīti attho. Yathā hi sūriyo attano raṃsiyā phusayitvā kuhara saṅkhāte sare jātaṃ uppalaṃ vikāseti, evameva bhagavāpi attano dhammadesanāsaṅkhātaraṃsiyā kamalasarasaṅkhāte bhavattaye vasantaṃ puññūpanissayaṃ paṭivijjhārahaṃ kamala saṅkhātaṃ vineyyappāṇoghaṃ catusaccadhammaṃ paṭivijjhayamāno phaladhammena pabodhesi vikāsetīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Byāpitakkittinañcā</span>ti tiloke patthaṭaparikittanaṃ. Sīlādiguṇehi patthaṭathutighosikanti attho. Casaddo cettha padapūraṇamattoyeva. Visesena vividhaṃ vā āpati vaḍḍhatīti byāpito. Vipubbaāpadhātu byāpane vaḍḍhane vā ta. Byāpanaṃ vā byāpitaṃ. Kittiyate abhitthaviyate kittinaṃ. Bhāvasādhanoyaṃ. Thutikriyā labbhati. Kittiyati abhitthaviyati devamanussehīti kittinaṃ, thutighoso. Kittadhātu abhitthavane yu. Byāpito patthaṭo kittano thutighoso yassa soti byāpitakkittino, munindakko. Taṃ. Bhagavato hi tiloke devamanussehi sīlādiguṇehi ca arahaṃ sammāsambuddhotyādīhi guṇehi ca thomito kittisaddo yāva bhavaggaṃ abbhuggacchatīti byāpitakkittinanti thometīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Tilokekaccakkhu</span>nti tiloke ekacakkhūti padacchedo. Kāmarūpa arūpasaṅkhātānaṃ tilokānaṃ ekaṃ paññācakkhubhūtanti attho. Tayo lokā tilokaṃ. Tassa cakkhu viya eka bhūtaṃpaññācakkhu yassa soti tilokekaccakkhu, munindakko. Ettha ca ekacakkhusaddassa lopo hoti brahmassarotiādīsu viya. Atha vā tilokassa ekaṃ paññācakkhuṃ bhūto pattoti tilokekaccakkhu, munindakko. Tilokekaccakkhubhūtanti vattabbe chandānurakkhaṇatthaṃ bhūtasaddalopaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',152,replace(replace('<p class="bodytext">katvā <a name="M0.0152"></a> tilokekaccakkhunti vuttaṃ. Atha vā tilokassa ekabhūto paññācakkhu hutvā sabbadhamme cakkhati passatīti tilokekaccakkhu, munindakko. Taṃ. Bhagavā hi paññācakkhu mayattā sattesu ca taduppādanato tilokassa cakkhubhūto ñāṇabhūto dhammabhūto brahmabhūto hoti. Vuttañhi <span class="bld">madhupiṇḍikasutte</span> ‘‘so hāvuso bhagavā jānaṃ jānāti, passaṃ passati cakkhubhūto ñāṇabhūto dhammabhūto brahmabhūto vattā pavattā atthassa ninnetā amatassa dātā dhammassāmī tathāgato’’ti. Tasmā tilokekaccakkhunti thometi.</p>\r\n<p class="bodytext"><span class="bld">Dukhamasahana</span>nti puthujjanehi atidukkhamitaṭṭhānaṃ khamanaṃ. Puthujjanehi dukkhena kasirena khamitabbanti dukhamaṃ, iṭṭhāniṭṭhādi ārammaṇaṃ. Dukkhamanti vattabbe gajjabandhattā chandānurakkhaṇatthaṃ kakāralopaṃ katvā dukhamanti vuttaṃ. Taṃ sahati khamatīti dukhamasahano, munindakko. Sahadhātu parisahane yu. Taṃ dukhamasahanaṃ. Bhagavā hi puthujjanehi sudukkhamitaṃ iṭṭhā niṭṭhādiārammaṇaṃ sītuṇhādiṃ lābhālābhayasāyasa nindapasaṃsasukhadukkhasaṅkhātaṃ lokadhammañca ativiya khamati adhivāseti, tasmā dukhamasahananti thometi.</p>\r\n<p class="bodytext"><span class="bld">Mahesi</span>nti mahantaṃ sīlakkhandhādiṃ pariyesamānaṃ mahantaṃ nibbānaṃ gavesamānaṃ vā mahante sīlakkhandhādike esati gavesati pariyesatīti mahesī, munindakko. Mahantasaddūpa pada esadhātu gavesane ī. Vuttañhi <span class="bld">mahāniddese</span> ‘‘mahesīti mahesī. Bhagavā mahantaṃ sīlakkhandhaṃ esi gavesi pariyesīti mahesī. Mahantaṃ samādhikkhandhaṃ mahantaṃ paññākkhandhaṃ mahantaṃ vimuttikkhandhaṃ mahantaṃ vimuttiñāṇadassanakkhandhaṃ esī gavesī pariyesīti mahesī’’ti. Mahante sattatiṃsabodhipakkhiya dhamme esati gavesatīti vā mahesī, mahantaṃ nibbānaṃ esati gavesatīti vā mahesī. Vuttañhi <span class="bld">mahāniddese</span> ‘‘mahante satipaṭṭhāne mahante sammappadhāne mahante iddhipāde mahantāni </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',153,replace(replace('<p class="bodytext">indriyāni <a name="M0.0153"></a> mahantāni balāni mahante bojjhaṅge mahantaṃ ariyaṃ aṭṭhaṅgikaṃ maggaṃ mahantaṃ paramatthaṃ amataṃ nibbānaṃ esī gavesī pariyesīti mahesī’’ti. Atha vā mahesakkhehi sattehi esiyati gavesiyati pariyesiyati amhākaṃ kahaṃ buddhoti mahesī. Vuttañhi <span class="bld">mahāniddese</span> ‘‘mahesakkhehi vā sattehi esito gavesito kahaṃ buddho’’tyādi. Taṃ mahesiṃ. <span class="bld">Ta</span>nti munindakkaṃ. Ahaṃ sirasā namāmīti sambandho.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Dayudayaruṇo mahākaruṇāsaṅkhātaudayapabbatuggatasūriyāruṇo, udayapabba tato uggatasūriyāruṇo viya mahākaruṇā vā, ñāṇa vitthiṇṇabimbo sabbaññutañāṇasaṅkhāto vitthāraparimaṇḍalo, vitthāracakkavāḷamaṇḍalaṃ viya vipulārammaṇasabbaññutañāṇavā, eko asahāyo ekabuddhabhūto, yo yādiso munindakko buddhādicco suddhe rāgādimalehi visuddhe tibhava kuhare tibhavasaṅkhātakamalasare, kamalakathitaṃ uppalanti kathetabbaṃ, vineyyappāṇoghaṃ vinetabbasattasamūhaṃ, dhammaraṃ sīvarehi uttamadhammasaṅkhātaraṃsīhi, subodhesi catusaccadhammaṃ paṭivedhayamānena suṭṭhu pabodhesi vikāsesi, byāpitakkittinaṃ tiloke patthaṭaparikittanaṃ, sīlādiguṇehi patthaṭathutighosikaṃ vā, tilokekaccakkhuṃ kāmarūpaarūpa saṅkhātānaṃ tilokānaṃ ekaṃ paññācakkhubhūtaṃ, dukhamasahanaṃ puthujjanehi atidukkhamitaṭṭhānaṃ khamanaṃ, mahesiṃ mahantaṃ sīlakkhandhādiṃ pariyesamānaṃ mahantaṃ nibbānaṃ gavesamānaṃ vā, taṃ munindakkaṃ ahaṃ sirasā namāmi vandāmīti.</p>\r\n<p class="centered">Vīsatimavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',154,replace(replace('<p class="gatha1">Yo <a name="M0.0154"></a> jino anekajātiyaṃ saputtadāramaṅgajīvitampi;</p>\r\n<p class="gatha2">Bodhipemato alaggamānaso adāsiyeva atthi kassa;</p>\r\n<p class="gatha3">Dānapāramiṃ tato paraṃ apūri sīlapāramādikampi;</p>\r\n<p class="gathalast">Tāsamiddhiyopayātamaggataṃ tamekadīpakaṃ namāmi.</p>\r\n<p class="bodytext"><a name="para21"></a><span class="paranum">21</span>. Evaṃ vīsatimāya gāthāya munindakkaṃ vanditvā idāni pubbe anekajātiyaṃ bodhisattakāle jīvitapariccāgādīhi ca dānādidasapāramiyo pūrentehi guṇehi ca thometvā jinaṃ vanditukāmo <span class="bld">yo jino</span> tyādimāha. Ayaṃ pana raja raja raja gaṇehi ca garulahūhi ca racitattā vīsatakkharehi lakkhitā vuttagāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘vutta mīdisantu nāmato rajā rajā rajā garullahū cā’’ti.</p>\r\n<p class="bodytext">Ayaṃ panettha yojanā. Yassaṃ paṭipādaṃ rajā rajagaṇā ca rajā rajagaṇā ca rajā rajagaṇā ca garullahū ca ce siyyuṃ, īdisantu īdisaṃ pana gāthā nāmato nāmena vuttaṃ vutta gāthā nāmāti.</p>\r\n<p class="bodytext">Tattha pana <span class="bld">yo jino</span>ti yo buddho. So hi devaputtakilesābhisaṅkhārakhandhamaccuvasena pañcamāre ajini jināti jinissatīti jinoti vuccati. Nanu ca buddhasāvakāpi pañcamāre jinattā jinā nāmāti ce. Na bhagavato parūpa nissayavirahaniratisayavasena pañcamārajinattā bhagavāyeva jino nāmāti. Vuttañhi <span class="bld">dhātukathāmūlaṭīkāyaṃ</span> –</p>\r\n<p class="bodytext">‘‘Tattha balavidhamana visayātikkamavasena devaputtamārassa, apavattikaraṇavasena kilesābhisaṅkhāramārānaṃ, samudayappahānapariññāvasena khandhamārassa, maccumārassa, ca bodhimūle eva bhañjitattā parūpanissayarahitaṃ niratisayaṃ taṃ bhañjanaṃ upādāya bhagavāeva mārabhañjanoti thomito’’ti.</p>','\r',char(13)),'\n',char(10)),'-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',155,replace(replace('<p class="bodytext"><span class="bld">Anekajātiya</span>nti <a name="M0.0155"></a> anekasaṃsārabhave. Ettha hi ayaṃ jātisaddo anekattho. Tathā hesa ekampi jātiṃ dvepi jātiyoti ettha bhave āgato. Atthi visākhe nigaṇṭhā nāma samaṇajātīti ettha nikāye. Tiri nāma tiṇajāti nābhiyā uggantvā nabhaṃ āhacca ṭhitā ahosīti ettha paññattiyaṃ. Jāti dvīhi khandhehi saṅgahitāti ettha saṅkhatalakkhaṇe. Yaṃ mātukucchiyaṃ paṭhamaṃ cittaṃ uppannaṃ paṭhamaṃ viññāṇaṃ pātubhūtaṃ. Tadupādāya sāvassa jātīti ettha paṭisandhiyaṃ. Sampatijāto ānanda bodhisattoti ettha pasūtiyaṃ. Anupakuṭṭho jātivādenāti ettha kule. Yatohaṃ bhagini ariyāya jātiyā jātoti ettha ariyasīle. Idha panāyaṃ bhave vattati. Tasmā anekasaṃsārabhaveti attho viññātabbo. So hi jāyati yoni gatiādi vibhāgoti jātīti vuccati. Atha vā jāyanti nibbattanti ettha khandhādayoti jāti, bhavo. Janidhātu nibbattaneti. Na ekā anekā, anekā ca sā jāti cāti aneka jāti. Tassaṃ anekajātiyaṃ.</p>\r\n<p class="bodytext"><span class="bld">Saputtadāra</span>nti puttena ca bhariyāya ca sahitaṃ. Attano kulaṃ punāti pavati sodhetīti vā putto. Pudhātu pavane ta. Mātāpitūnaṃ manaṃ pūretīti vā putto. Pūradhātu pūraṇe ta. Putto ca dhītā ca putto. Virūpeka sesoyaṃ. Sāmikena dhariyateti dāro. Dharadhātu dhāraṇe ṇo. Dhassa do. Atha vā purisena bhogaṃ dīyatīti dāro. Dādhātu dāne rapaccayo. Dākucchitā kārena purisena ramiyati etthāti vā dāro, bhariyā. Dāpubbaramudhātu ramane kvi. Dāsaddo kucchitattho. <span class="bld">Abhidhānaṭīkāyaṃ</span> pana ‘‘dārayante yenāti dāro, dara vidāraṇe. Akattari ca kārake saññāyaṃ ṇo’’ti vuttaṃ. Putto ca dāro ca puttadāro. Samāhāradvandepi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',156,replace(replace('<p class="bodytext">hi <a name="M0.0156"></a> pulliṅgaṃ icchanti ācariyā. Tena saha vattati yassāti saputtadāro, puttadārapariccāgo. Taṃ saputtadāraṃ. <span class="bld">Aṅgajīvitampī</span>ti hatthapādakaṇṇanāsacakkhādisarīrāvayavañca jīvitindriya paṭibaddhasakalasarīrañca aṅgati gacchati kāyeti aṅgo, cakkhādisarīrāvayavo. Agidhātu gatiyaṃ a. Jīvanti sattā etenāti jīvitaṃ, jīvitindriyaṃ. Jīvadhātu pāṇadhāraṇe ta. Aṅgo ca jīvitañca aṅgajīvitaṃ. Ettha ca pisaddo sampiṇḍanattho. Tena dhanaraṭṭhapariccāgaṃ sampiṇḍeti. Vuttañhi <span class="bld">sīlakkhandhaṭṭha kathāyaṃ</span> –</p>\r\n<p class="bodytext">‘‘Aṅgapariccāgaṃ jīvitadhanarajjaputtadārapariccāganti ime pañca mahāpariccāge pariccajitvā’’ti. Atha vā sambhāvanattho. Tena bhagavā sabbaññutañāṇahetupi puttadāraaṅgajīvitampi alaggamānaso pariccajeti, pageva dhanādibāhiravatthu pariccāgepīti sambhāveti.</p>\r\n<p class="bodytext"><span class="bld">Bodhipemato</span>ti arahattamaggasabbaññutañāṇasaṅkhātāya bodhiyā piyamānattā, hetvatthe hi topaccayo. Cattāri saccāni bujjhatīti bodhi, arahattamaggañāṇaṃ. Sabba ñeyyadhamme bujjhatīti bodhi, sabbaññutañāṇaṃ. Budhadhātu ñāṇe ṇa. Itthiliṅgajotako īpaccayo ca. Vuttañhi <span class="bld">abhidhānappadīpikāyaṃ</span> – ‘‘bodhi sabbaññutañāṇe, ariyamagge ca nāriya’’nti. Bodhi ca bodhi ca bodhi sarūpekase savasena. Piyanaṃ pemo, piyāyitabboti vā pemo. Pīdhātu tappanakantīsu manipaccayo. Bodhiyā pemo bodhipemo, tato bodhipemato. Bhagavatā hi puttadāraaṅgajīvitehipi sataguṇena sahassaguṇena sata sahassaguṇenapi bodhiñāṇameva piyanti vuttaṃ hoti. Vuttañhi <span class="bld">vessantarajātake</span> –</p>\r\n<p class="gatha1">‘‘Na me dessā ubho puttā, maddī devī na dessiyā;</p>\r\n<p class="gathalast">Sabbaññutaṃ piyaṃ mayhaṃ, tasmā piye adāsaha’’nti.</p>\r\n<p class="gathalast"></p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',157,replace(replace('<p class="bodytext"><span class="bld">Alaggamānaso</span>ti <a name="M0.0157"></a> puttadāraaṅgajīvitādīsu anallīyana citto hutvā laganaṃ saṅganaṃ laggo, lagati saṅgati allayati vāti laggo. Lagadhātu saṅge allīyane vā gapaccayo. Na laggo alaggo, natthi laggo ettha puttadāra aṅgajīvitādīsūti vā alaggo. Manati jānāti ārammaṇanti mano. Soyeva mānaso. Alaggo mānaso yassa soti alaggamānaso, jino. <span class="bld">Atthikassā</span>ti putta dārādiṃ kāmikassa, yācakassa paṭiggāhakassa vāti attho. Attho kāmo assa atthīti atthiko, ṇiko. Tassa atthikassa. <span class="bld">Adāsiyevā</span>ti pūjānuggahakāmatāya ñāṇasampayuttapariccāgacetanāya adāsievāti attho. Bodhisatto hi pāramīpūraṇa kāle sarāgasadosasamohasseva sato āgatā gatānaṃ yācakānaṃ alaṅkatapaṭiyattaṃ sīsaṃ kantitvā galalohitaṃ nīharitvā suañjitāni akkhīni uppāṭetvā kulavaṃsappadīpaṃ puttaṃ manāpacāriniṃ bhariyañca kheḷapiṇḍamiva anapekkho pariccajitvā dīyamānova sammāsambodhiñāṇaṃ laddhuṃ sakkā, na puttadāraaṅgajīvitampi apariccajitvā. Pañca hi mahāpariccāge apariccajitvā buddhabhūtapubbā nāma natthi. Paccekabuddhāriyasāvakā pana puttadāraaṅgajīvitaṃ apariccajantāpi paccekabuddhāriyasāvakabodhiñāṇaṃ laddhuṃ samatthāva. Tasmāyeva bhagavantaṃyeva saputtadāramaṅgajīvitampi bodhipemato alaggamānaso adāsiyeva atthi kassāti thometi. Vuttañhetaṃ <span class="bld">vessantarajātakaṭṭha kathāyaṃ</span> ‘‘sabbabodhisattā dhanapariccāgaṃ, puttapariccāgaṃ, bhariyapariccāgaṃ, aṅgapariccāgaṃ, jīvitapariccāganti ime pañca mahāpariccāge apariccajitvā buddhabhūtapubbā nāma natthī’’ti.</p>\r\n<p class="bodytext">Evaṃ pañcahi mahāpariccāgaguṇehi bhagavato thomanaṃ katvā idāni dānādipāramīpūraṇaguṇena thomanaṃ kattu kāmo dānapāramintiādimāha. Tattha <span class="bld">dāna</span>nti tividhaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',158,replace(replace('<p class="bodytext">hoti <a name="M0.0158"></a> cāgacetanā ca, virati ca, deyyadhammo cāti. Saddhā hiriyaṃ kusalañca dānanti āgataṭṭhāne hi cāgacetanā dānaṃ nāma. Abhayaṃ detīti āgataṭṭhāne viratī dānaṃ nāma. Dānaṃ deti annaṃ pānanti āgataṭṭhāne deyyadhammo dānaṃ nāma. Tattha cāgacetanā deti vā deyyadhammaṃ denti vā etāya deyyadhammanti dānaṃ. Virati avakhaṇḍanaṭṭhena lavanaṭṭhena vā dānaṃ. Sā hi uppajjamānā bhayabheravasaṅkhātaṃ dussilyaṃ cetanaṃ dāti lunāti cāti dānaṃ, deyyadhammo diyyatīti dānaṃ. Idha pana tividhaṃ dānaṃ adhippetaṃ. Pāramīsaddassa vacanattho heṭṭhā vuttoyeva. Atha vā pāraṃ nibbānaṃ eti gacchati etāyāti pārami. Pārasaddūpapada idhātu gatiyaṃ kvi. Pāraṃ itīti padacchedo kātabbo. Dānameva pāramī dānapāramī, taṃ dānapāramiṃ. <span class="bld">Tato</span>ti dānapāramito. <span class="bld">Para</span>nti aññaṃ. <span class="bld">Sīlapāramādikampī</span>ti sīlatīti sīlaṃ. Sīla dhātu samādhimhi a. Kāyavacīkammāni samādahati samādhapetīti attho. Sīlati samādhiyati kāyakammādīnaṃ susilyavasena na vippakiratīti vā sīlaṃ, sīlanti samādahanti cittaṃ etenāti vā sīlaṃ. Atha vā sīlayati upadhāretīti sīlaṃ, sīladhātu upadhāraṇe a. Kusala dhammānaṃ patiṭṭhānabhāvena ābhuso dhāretīti attho. Sīlenti vā etena kusale dhamme upadhārenti sādhavoti sīlaṃ, sīliyati upadhāriyati sappurisehi hadayamaṃsantaraṃ upanetvāti vā sīlaṃ, sīleti upadhāreti taṃ samaṅgī puggalaṃ apāyesu uppattinīvāraṇavasena suggati nibbānanti vā sīlaṃ, sīlameva pāramī sīlapāramī, sā ādi yāsanteti sīlapāramādikā. Ettha ca ādisaddena nekkhammapaññā vīriyakhantisaccaadhiṭṭhānamettāupekkhāpāramiṃ saṅgaṇhāti. Vuttañhetaṃ <span class="bld">buddhavaṃsapāḷiyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Dānaṃ sīlañca nekkhammaṃ, paññā vīriyena pañcamaṃ;</p>\r\n<p class="gathalast">Khantisaccamadhiṭṭhānaṃ, mettupekkhā imā dasā’’ti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',159,replace(replace('<p class="bodytext">Pisaddo <a name="M0.0159"></a> cettha sampiṇḍanattho. Tena pañca mahāpariccāge sampiṇḍeti.</p>\r\n<p class="bodytext"><span class="bld">Apūrī</span>ti sammāsambodhatthāya pūraṇaṃ akāsīti attho. Bhagavato hi dīpaṅkarapādamūle aṭṭha dhamme samodhānetvā buddhatthāya abhinīhārato paṭṭhāya anekajātīsu sammāsambodhiṃ paṭivijjhituṃ vāyamanto adinnadānaṃ nāma natthi, arakkhitasīlaṃ nāma natthi. Apūritā pāramī nāma natthi. Atibahumpi pūraṇaṃ akāsiyeva. Bhagavā hi bodhisatta bhūto catūsu mahāsamuddesu udakaṃ parājetvā adhikaṃ lohitadānamakāsi. Sakalapathaviyā paṃsuṃ parājetvā adhikaṃ maṃsamadāsi. Sinerupabbatarājaṃ parājetvā adhikaṃ sīsadānamadāsi. Ākāse tārakasamūhaṃ parājetvā adhikaṃ nayanamadāsi.</p>\r\n<p class="bodytext">Tena vuttaṃ <span class="bld">jinālaṅkāre</span> –</p>\r\n<p class="gatha1">‘‘So sāgare jaladhikaṃ rudhiraṃ adāsi,</p>\r\n<p class="gatha2">Bhūmiṃ parājiya samaṃsamadāsi dānaṃ;</p>\r\n<p class="gatha3">Meruppamāṇamadhikañca samoḷisīsaṃ,</p>\r\n<p class="gathalast">Khe tārakādhikataraṃ nayanaṃ adāsī’’ti.</p>\r\n<p class="centered">Vasantatilakagāthā.</p>\r\n<p class="bodytext">Tattha ca soti so evaṃvidho bodhisatto. Sāgareti catūsu mahāsamuddesu. Jaladhikanti udakato adhikaṃ katvā. Rudhiraṃ adāsīti lohitadānamadāsi. Bhūmiṃ parājiyāti paṃsupathaviyā paṃsuṃ parājetvā attano sarīramaṃsaṃ adhikaṃ adāsi. Meruppamāṇamadhikanti yojana aṭṭhasaṭṭhisatasahassubbedhassa girirājassa pamāṇatopi adhikaṃ katvā. Samoḷisīsanti moḷiyā saha abhisittasīsaṃ adāsi. Kheti cakkavāḷapabbatapariyante ākāse. Tārakādhikataranti nakkhattāditārakarūpatopi adhikaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',160,replace(replace('<p class="bodytext">katvā<a name="M0.0160"></a>. Nayanaṃ adāsīti nettadānaṃ adāsīti evamettha attho daṭṭhabbo.</p>\r\n<p class="bodytext"><span class="bld">Tāsamiddhiyopayātamaggata</span>nti tāsaṃ iddhiyā upayātaṃ aggatanti padacchedo. Tāsanti tāsaṃ pāramīnaṃ. Iddhiyāti samiddhiyā, hetvatthe cetaṃ karaṇavacanaṃ. Upayātanti upagataṃ. Aggatanti sabbasattānaṃ uttamabhāvaṃ, seṭṭhabhūtaṃ buddhattanti attho. Taṃ pana padaṃ upayātantipade kammaṃ. Aggasaddo cettha uttamavācako anipphannapāṭipadiko. Atha vā aggati uttamabhāvaṃ seṭṭhabhūtaṃ vā buddhabhāvaṃ pāpuṇātīti aggo, jino. Aggadhātu gatiyaṃ a. Aggassa bhāvo aggataṃ. Bhagavā hi anantalokadhātūsu sabba sattānaṃ aggo uttamataro seṭṭhataroti attho. Vuttañhetaṃ dasanipāta <span class="bld">aṅguttarapāḷiyaṃ</span> –</p>\r\n<p class="bodytext">‘‘Yāvatā bhikkhave sattā apadā vā dvipadā vā catuppadā vā bahuppadā vā rūpino vā arūpino vā saññino vā asaññino vā nevasaññīnāsaññino vā, tathāgato tesaṃ aggamakkhāyati arahaṃ sammāsambuddho’’ti.</p>\r\n<p class="bodytext"><span class="bld">Tamekadīpaka</span>nti taṃ ekadīpakanti padacchedo. Tanti jinaṃ. Ekadīpakanti sabbasattānaṃ ekapaṭisaraṇabhūtaṃ patiṭṭhabhūtaṃ vā. Dīpakasaddo hi paṭisaraṇapatiṭṭhavācako nipphannapāṭipadiko. Vuttañhi <span class="bld">saṃyuttaṭṭhakathāyaṃ</span> ‘‘patiṭṭhāpanaṭṭhena dīpa’’nti. Dīpanti patiṭṭhahanti paṭisaraṇabhāvena sattā etthāti vā dīpako, jino. Dīpadhātu patiṭṭhāyaṃ ako. Ekasaddo saṅkhyāvācako. Eko ekabhūto dīpakoti ekadīpako, jino. Atha vā ekadīpakanti manussānaṃ paṭisaraṇabhūtaṃ patiṭṭhānabhūtaṃ samuddadīpakamiva sabbasattānaṃ paṭisaraṇabhūtaṃ patiṭṭhānabhūtaṃ ekabuddhabhūtaṃ.</p>\r\n<p class="bodytext">Ayaṃ panettha vacanattho. Jalamajjhe dippatīti dīpo. Dvidhā āposandati etthāti vā dīpo. Soyeva dīpako. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',161,replace(replace('<p class="bodytext">Soviya <a name="M0.0161"></a> eko buddho hutvā eti gacchati pavattatīti ekadīpako, jino. Yathā hi mahāsamudde bhinnanāvānaṃ manussānaṃ samuddadīpo paṭisaraṇo patiṭṭho hoti, evaṃ jino saṃsārasāgare alabbhaneyyapatiṭṭhe osīdantānaṃ sabbasattānaṃ ekabuddhabhūto paṭisaraṇo patiṭṭho hotīti ekadīpakanti thometi. Taṃ jinaṃ ahaṃ namāmīti sambandhoti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Yo jino aneka jātiyaṃ anekasaṃsārabhave, saputtadāraṃ puttena ca bhariyāya ca sahitaṃ, aṅgajīvitampi aṅgapaccaṅgajīvitindriyampi. Atha vā hatthapādakaṇṇanāsacakkhādisarīrāvayavañca jīvitindriya paṭibaddhasakalasarīrañca, bodhipemato arahattamagga sabbaññutañāṇasaṅkhātāya bodhiyā piyamānattā, alagga mānasoti puttadāraaṅgajīvitādīsu anallīyanacitto hutvā, atthikassa puttadārādiṃ kāmikassa yācakassa adāsiyeva pariccāgacetanāya adāsieva. Dānapāramimpi tato dānapāramito paraṃ aññaṃ sīlapāramādikampi sīlapāramīādikampi, apūri sammāsambodhatthāya pūraṇaṃ akāsi, tāsaṃ pāramīnaṃ iddhiyā samiddhiyā aggataṃ uttama bhāvaṃ seṭṭhabuddhattaṃ vā, upayātaṃ upagataṃ, ekadīpakaṃ sabbasattānaṃ ekapaṭisaraṇabhūtaṃ ekapatiṭṭhānabhūtaṃ vā, dīpakamiva ekapaṭisaraṇabhūtaṃ patiṭṭhānabhūtaṃ vā taṃ jinaṃ tīhi dvārehi namāmīti.</p>\r\n<p class="centered">Ekavīsatimavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> Devādevātidevaṃ nidhanavapudharaṃ mārabhaṅgaṃ abhaṅgaṃ,</p>\r\n<p class="gatha2">Dīpaṃ dīpaṃ pajānaṃ jayavarasayane bodhipattaṃdhipattaṃ;</p>\r\n<p class="gatha3">Brahmābrahmāgatānaṃ varagirakathikaṃ pāpahīnaṃ pahīnaṃ,</p>\r\n<p class="gathalast">Lokālokābhirāmaṃ satatamabhiname taṃ munindaṃ munindaṃ.</p>','\r',char(13)),'\n',char(10)),'-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',162,replace(replace('<p class="bodytext"><a name="para22"></a><span class="paranum">22</span>. Evaṃ <a name="M0.0162"></a> ekavīsatimāya gāthāya jinaṃ vanditvā idāni devādevātidevantyādīhi dasahi guṇehi thometvā munindaṃ vanditukāmo <span class="bld">devādevātideva</span>ntiādimāha. Ayaṃ pana ma ra bha na ya ya gaṇehi ca timuniyatīhi ca racitattā ekavīsatakkharehi yuttā ādyantayamakā saddharā gāthāti daṭṭhabbā. Vuttañhi <span class="bld">vuttodaye</span> ‘‘mrā bhnā yo yotra yena timuniyatiyutā saddharā kittitāya’’nti.</p>\r\n<p class="bodytext">Ayaṃ panettha yojanā. Yassaṃ paṭipādaṃ mrā maragaṇā ca bhnā bhanagaṇā ca yogaṇo ca yena yagaṇena saha yogaṇo ca timuniyatiyutā sattamakkharantetīhi yatīhi yuttā ca ce siyuṃ. Atra imasmiṃ pakati chande ayaṃ gāthā saddharāgāthāti kittitāti. Ettha ca munisaddo sattasaṅkhyāvācakoti daṭṭhabbo. Ekasmiṃ pāde sattamakkharante sattamakkharante yatīnaṃ tiparivattattā timuniyatiyutāti vuttaṃ. Ekavīsatakkharavantoti vuttaṃ hoti.</p>\r\n<p class="bodytext">Tattha ca <span class="bld">devādevātideva</span>nti rājasaṅkhātasammutideva catumahārājādikaupapattidevānaṃ atiuttamaṃ atiseṭṭhaṃ visuddhidevabhūtaṃ. Ettha hi devāti sammutidevā. Te hi mānussaissariyasampattīhi dibbantīti manussarājadevī kumārā devāti vuccanti. Puna devāti upapattidevā. Te hi dibbaissariyasampattīhi dibbantīti catumahārājikā devā vuccanti, dibbanti kāmaguṇādīhi kīḷanti laḷanti, tesu vā viharanti, vijayasamatthatāyogena paccatthike vijetuṃ icchanti, issariyadhanādisakkāradānaggahaṇaṃ taṃ taṃ atthānusāsanañca karontā voharanti. Puññātisayayogānubhāvappattāya jutiyā jotanti. Yathādhippetañca visayaṃ appaṭighātena gacchanti. Yathicchitanipphādane ca sakkontīti devāti <span class="bld">visuddhimaggamahāṭīkāyaṃ</span> āgatanayenapi veditabbo. </p>','\r',char(13)),'\n',char(10)),'-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',163,replace(replace('<p class="bodytext">Atidevanti <a name="M0.0163"></a> tesaṃ devānaṃ atiuttamaṃ atiseṭṭhaṃ vā visuddhi devabhūtaṃ bhagavantanti attho. So hi tesaṃ sammuti upapattidevānaṃ sīlādiguṇehi atirekattā atireko devoti bhagavā atidevoti vuccati. Devo devānaṃ atireko atiuttamo atiseṭṭho ativisiṭṭho devo devādevātidevo, visuddhidevabhūto munindo labbhati. Taṃ devādevātidevaṃ.</p>\r\n<p class="bodytext">Vuttañhi <span class="bld">dhammahadayavibhaṅge</span> ‘‘devāti tayo devā sammuti devā, upapattidevā, visuddhidevā. Sammutidevā nāma rājāno deviyo rājakumārā. Upapattidevā nāma catumahārājike deve upādāya tadupari devā. Visuddhidevā nāma arahanto vuccantī’’ti.</p>\r\n<p class="bodytext">Bhagavā pana nirātisayāya abhiññākīḷāya uttamehi dibbabrahmaariyavihārehi saparasantānagatapañcavidhamāravija yicchānipphattiyācittissariya sattadhanādi sammāpaṭipatti aveccappasādasakkāradānaggahaṇasaṅkhātena dhammasabhāvapuggalajjhāsayānurūpānusāsanīsaṅkhātena ca vohārātisayena paramāya paññāsarīrappabhāsaṅkhātāya jutiyā anaññasādhāraṇāya ñāṇasarīragatiyā māravijaya sabbaññuguṇaparahita nipphādanesu appaṭihatāya sattiyā ca samannāgatattā sadevakena lokena saraṇanti gamanīyato abhitthavanīyato bhattivasena kamanīyato ca sabbe te deve tehi guṇehi abhibhuyya ṭhitattā tesaṃ devānaṃ atiseṭṭho atiuttamo devoti devādevātidevoti <span class="bld">visuddhimagga mahāṭīkāyaṃ</span> āgatanayenapi veditabbo.</p>\r\n<p class="bodytext"><span class="bld">Nidhanavapudhara</span>nti antimakāyadhāraṇaṃ. Ettha ca nidhanasaddo pariyosānavācako anipphannapāṭipadiko. Atha vā nidheti pariyosānaṃ karoti etenāti nidhanaṃ. Nidheti vināseti bhavanti vā nidhanaṃ, antimakāyo labbhati. Nipubba dhā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',164,replace(replace('<p class="bodytext">dhātu <a name="M0.0164"></a> nāse yu. Vapati kusalākusalabījametthāti vapu, kāyo. Vapadhātu bījasantāne u. Nidhanañca taṃ vapu cāti nidhanavapu, taṃ dhāretīti nidhanavapudharo, munindo. Taṃ nidhanavapudharaṃ. So hi anamataggasaṃsārabhavesu nibbattentānaṃ sabbakilesānaṃ bodhimaṇḍe aggamaggañāṇasatthena samucchindi tattā punabbhavābhinibbatti natthi. Ayañhi buddhabhāvajāti antima bhavo hoti, tasmā visesena munindameva nidhanavapudharanti thometi. Vuttañhetaṃ <span class="bld">aṅguttarapāḷiyaṃ</span> ‘‘buddho antima dharo’’ti.</p>\r\n<p class="bodytext"><span class="bld">Mārabhaṅga</span>nti devaputtādipañcamāre viddhaṃsanaṃ. Mārasaddattho heṭṭhā vuttoyeva. Pañcamāre bhañjati abhañji bhañjissatīti mārabhaṅgo, munindo. Mārasaddūpapada bhanjadhātu avamaddane tapaccayo. Tassa saha dhātvantena goādeso. Nakārassa niggahitādeso. Tassa kavaggantādeso. Taṃ mārabhaṅgaṃ. <span class="bld">Abhaṅga</span>nti mārasenāya bhañjituṃ viddhaṃsituṃ asamatthaṃ, bhañjiyate bhaṅgo. Nabhaṅgo abhaṅgo. Atha vā mārasenāya na bhañjiyatīti abhaṅgo. Mārasenāya bhañjituṃ na arahatīti attho. Mārasenā bhañjituṃ na samatthoti vā abhaṅgo, munindo. Taṃ abhaṅgaṃ.</p>\r\n<p class="bodytext"><span class="bld">Dīpaṃdīpaṃ pajāna</span>nti ettha pajānaṃ dīpaṃ dīpanti sambandho. Tattha pajānanti sattānaṃ. Tehi kammakilesehi pajāyanti nibbattiyanti, pakārena vā jāyanti nibbattanti paṭisandhicutivasenāti pajāti vuccanti. Dīpanti paññāpadīpaṃ. Tañhi kusalākusale dhamme vibhajitvā dīpeti pakāseti jotetīti dīpanti vuccati. Atha vā dippati jotetīti dīpo. Andhakāraṃ vidhametvā ālokaṃ pakāsetīti attho. Dīpadhātu dittiyaṃ a. Dīpamivāti dīpā, paññā. Yathā hi padīpo andhakāraṃ vidhamitvā ālokaṃ pakāseti, evaṃ paññā avijjandhakāraṃ hantvā dhammālokaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',165,replace(replace('<p class="bodytext">pakāsetīti <a name="M0.0165"></a> vuttaṃ hoti. Taṃ dīpaṃ. Puna dīpanti dīpakaṃ pakāsentanti attho. So hi dīpeti pakāsetīti dīpoti vuccati. Munindo hi payirupāsanavasena gatāgatānaṃ vineyyasattānaṃ dhammadesanāya catumaggañāṇādipaññāpadīpaṃ pakāsetīti vuttaṃ hoti. Taṃ dīpaṃ.</p>\r\n<p class="bodytext"><span class="bld">Jayavarasayane</span>ti pañcamāravijayantaṭṭhānabhūte uttama bodhipallaṅkāsane. Pañcamāre jināti etthāti jayo, bodhirukkhapallaṅko. Jidhātu jaye ṇa. Varitabbo patthi tabboti varo, varadhātu patthane a. Varati nivāreti hīnanti vā varo. Varadhātu nivāraṇe a. Buddhena seviyati bhajiyati etthāti sayano. Sidhātu sevāyaṃyu. Varo uttamo sayano varasayano, jayoeva varasayano jayavarasayano, bodhirukkhapallaṅko. Tasmiṃ.</p>\r\n<p class="bodytext"><span class="bld">Bodhipattaṃdhipatta</span>nti bodhipattaṃ adhipattanti padacchedo. Bodhipattanti arahattamaggasabbaññutañāṇaṃ gataṃ. Ettha ca bodhisaddo rukkhamaggañāṇasabbaññutañāṇanibbānesupi vattati. Tathā hesa bodhirukkhamūle paṭhamābhisambuddhotiādīsu rukkhe vattati. Bodhi vuccati catūsu maggesu ñāṇantiādīsu magge. Pappoti bodhiṃ varabhūrī medhasotiādīsu sabbaññutañāṇe. Patvāna bodhiṃ amataṃ asaṅkhatantiādīsu nibbāne. Idha pana bhagavato arahattamaggasabbaññutañāṇaṃ adhippetaṃ. Cattāri saccāni bujjhatīti bodhi, arahatta maggañāṇaṃ. Sabbañeyyadhamme bujjhatīti bodhi, sabbaññutañāṇaṃ. Budhadhātu ñāṇe ṇa ī ca. Bodhi ca bodhi ca bodhi sarūpekasesavasena. Taṃ apajjiṃ agaminti bodhi patto, munindo. Taṃ bodhipattaṃ. Adhipattanti sadevake loke sabbadevamanussānaṃ jeṭṭhabhāvaṃ gatanti attho. Sabbadevamanussānaṃ adhikaṃ jeṭṭhabhāvaṃ vuḍḍhatarabhāvaṃ patto gatoti adhipatto, munindo. Taṃ adhipattaṃ. Yathā hi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',166,replace(replace('<p class="bodytext">kukkuṭapotakānaṃ <a name="M0.0166"></a> yo kukkuṭapotako aṇḍakosaṃ padāletvā abhinibbhido, so tesaṃ jeṭṭhabhāvaṃ vuḍḍhatarabhāvaṃ gato, evameva bhagavā sadevake loke avijjā gatāya pajāya taṃ avijjaṇḍakosaṃ padāletvā paṭhamataraṃ ariyāya jātiyā jātattā ca sīlasamādhipaññādi sabbaguṇehi adhikattā ca appaṭisamattā ca sabbadevamanussānaṃ jeṭṭhabhāvaṃ vuḍḍhatarabhāvaṃ seṭṭhabhāvaṃ appaṭisamabhāvañca patto gatoti vuttaṃ hoti. Tasmā adhipattanti thometi.</p>\r\n<p class="bodytext"><span class="bld">Brahmābrahmāgatāna</span>nti brahma abrahma āgatānanti padacchedo. Āgatānaṃ payirupāsanavasena āgatānaṃ brahma devamanussaparisānanti attho. Tattha brahmāti brahmapārisajjādi brahmā. Te hi jhānādiguṇehi brūhanti vaḍḍhantīti brahmāti vuccanti, abrahmāti devamanussā. Te hi na brahmā abrahmāti vuccanti, āgacchanti payirupāsanavasenāti āgatā, brahmā brahmāno labbhanti. Āpubbagamudhātu gatiyaṃ ta. Āgatā brahmā ca abrahmā cāti brahmābrahmāgatā, tesaṃ. Visesana paranipātapadametaṃ agyāhitotiādīsu viya. Taṃ pana padaṃ varagirakathikantipade sampadānaṃ. Payirupāsanānaṃ brahma devamanussaparisānanti attho.</p>\r\n<p class="bodytext"><span class="bld">Varagirakathika</span>nti uttamaṃ dhammavācaṃ desentaṃ. Seṭṭhavācāya dhammaṃ desamānaṃ vā, gāyati kathiyatīti girā, vācā. Gādhātu sadde īro. Varā seṭṭhā girā vācā varagirā. Tāya dhammaṃ kathetīti varagirakathiko, munindo. Uttamāya manāpabhūtavācāya dhammaṃ desetīti attho. Taṃ.</p>\r\n<p class="bodytext"><span class="bld">Pāpahīna</span>nti lāmakaakusalakilesaṃ. Tañhi apāyādi dukkhaṃ pāpetīti pāpoti vuccati. Akusalakileso. Papubbaapadhātu pāpuṇane. Kusaladhammena hātabbaṃ caji tabbanti hīnanti vuccati. Lāmakadhammo. Hādhātu cāge tapaccayo, tassino. Pāpoeva hīnaṃ pāpahīnaṃ. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',167,replace(replace('<p class="bodytext"><span class="bld">Pahīna</span>nti <a name="M0.0167"></a> arahattamaggañāṇena susamucchinnaṃ. Pajahatīti pahīno, munindo. Papubbahādhātu cāge ino. Sabbe akusaladhamme aggamaggañāṇena samucchindatīti attho.</p>\r\n<p class="bodytext"><span class="bld">Lokālokābhirāma</span>nti lokaalokaabhirāmanti padacchedo. Manussalokadevabrahmalokānaṃ abhirāmaṭṭhānabhūtanti attho. Lokoti manussa loko, so hi lokayati patiṭṭhahati pavattati vā ettha puññāpuññaṃ tabbipāko cāti loko, manussa loko. Lokadhātu patiṭṭhāyaṃ a. Na loko aloko. Devabrahmaloko. Loko ca aloko ca lokālokā, tesaṃ sattalokānaṃ abhiadhikaṃ ramanti etthāti lokālokābhirāmo. Munindo, taṃ. So hi tilokānaṃ pūjānuggahādikaraṇavasena abhirāmaṭṭhānaṃ hotīti lokālokābhirāmanti thometi. <span class="bld">Satata</span>nti niccaṃ. Rattidivaṃ abhiṇhanti attho. <span class="bld">Muninda</span>nti pañcamunissaraṃ, taṃ munindaṃ. <span class="bld">Abhiname</span>ti ahaṃ abhisakkaccaṃ namāmīti sambandhoti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Devādevātidevaṃ sammutidevaupapattidevānaṃ atiuttamaṃ visuddhidevabhūtaṃ nidhanavapudharaṃ antimakāyadhāraṇaṃ, mārabhaṅgaṃ devaputtādipañcamāre viddhaṃsanaṃ abhaṅgaṃ mārasenāya bhañjituṃ asamatthaṃ, mārasenā bhañjanārahaṃ vā, pajānaṃ sattānaṃ dīpaṃ paññāpadīpaṃ, kammabhūtaṃ dīpaṃ dīpakaṃ pakāsentaṃ, jayavarasayane pañcamāravijayantaṭṭhānabhūte uttamabodhipallaṅkāsane ādhārabhūte, bodhipattaṃ arahattamaggasabbaññutañāṇaṃ gataṃ adhipattaṃ sadevake loke sabbadevamanussānaṃ jeṭṭhabhāvaṃ gataṃ, brahmā brahmāgatānaṃ payirupāsanavasena āgatānaṃ brahmadeva manussaparisānaṃ varagirakathikaṃ seṭṭhavācāya dhammaṃ desamānaṃ pāpahīnaṃ lāmakaakusalakilesaṃ pahīnaṃ arahattamagga </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',168,replace(replace('<p class="bodytext">ñāṇena <a name="M0.0168"></a> susamucchinnaṃ lokālokābhirāmaṃ manussa lokadevabrahmalokānaṃ abhirāmabhūtaṃ, munindaṃ pañcamunissaraṃ taṃ munindaṃ satataṃ niccaṃ abhiṇhaṃ ahaṃ abhiname abhisakkaccaṃ namāmīti.</p>\r\n<p class="centered">Dvāvīsatimavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> Buddho nigrodhabimbo mudukaracaraṇo brahmaghoseṇijaṅgho,</p>\r\n<p class="gatha2">Kosacchādaṅgajāto punarapi sugato suppatiṭṭhitapādo;</p>\r\n<p class="gatha3">Mūdodātuṇṇalomo athamapi sugato brahmujuggattabhāvo,</p>\r\n<p class="gathalast">Nīlakkhī dīghapaṇhī sukhumamalachavī thomyarasaggasaggī.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> Cattālīsaggadanto samakalapanajo antaraṃsappapīno,</p>\r\n<p class="gatha2">Cakkenaṅkitapādo aviraḷadasano mārajussaṅkhapādo;</p>\r\n<p class="gatha3">Tiṭṭhanto nonamantobhayakaramudunā jaṇṇukānāmasanto,</p>\r\n<p class="gathalast">Vaṭṭakkhandho jino gotaruṇapakhumako sīhapubbaḍḍhakāyo.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> Sattappīno ca dīghaṅguli matha sugato lomakūpekalomo,</p>\r\n<p class="gatha2">Sampannodātadāṭho kanakasamataco nīlamuddhaggalomo;</p>\r\n<p class="gatha3">Sambuddho thūlajivho atha sīhahanuko jālikappādahattho,</p>\r\n<p class="gathalast">Nātho uṇhīsasīso itiguṇasahitaṃ taṃ mahesiṃ namāmi.</p>','\r',char(13)),'\n',char(10)),'-23-24-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',169,replace(replace('<p class="bodytext"><a name="para23"></a><span class="paranum">23</span>. Evaṃ <a name="M0.0169"></a> dvāvīsatimāya gāthāya munindassa paṇāmaṃ katvā idāni buddho nigrodhabimbotyādīhi tīhi gāthāhi buddhassa dvattiṃsamahāpurisalakkhaṇasampannehi guṇehi thomitvā mahesiṃ vanditukāmo <span class="bld">buddho nigrodhabimbo</span>tyādigāthāttayamāha. Taṃ pana gāthāttayaṃ saddharāgāthāti daṭṭhabbaṃ. Sādhakaṃ pana heṭṭhā vuttameva.</p>\r\n<p class="bodytext">Tattha paṭhamagāthāyaṃ <span class="bld">buddho</span>ti paṭividdhacatusaccadhammo paṭividdhaniravasesasabbadhammo vā. Taṃ pana padaṃ nigrodhabimbo tyādīsu padesu tulyaliṅgattabhāvena sambandhitabbaṃ. So hi cattāri saccāni bujjhati abujjhi bujjhissatīti ca, niravasesasabbadhamme bujjhati abujjhi bujjhissatīti ca buddhoti vuccati. Tesu pana dvīsu vikappesu paṭhamavikappe budhadhātussa vāccaṃ catusaccadhammapaṭivijjhanaṃ arahattamaggañāṇaṃ mukhyena labbhati. Heṭṭhā maggaphalañāṇañca vipassanāñāṇañca pāramīñāṇañca phalūpacārena. Arahattaphalañāṇañca sabbaññutañāṇādipacchimapacchimañāṇāni ca kāraṇūpacārena. Tapaccayassa vāccaṃ tena mukhyopacārena upalakkhito aparimitaguṇagaṇālaṅkāraṃ khandhapañcakaṃ paṭicca buddhobuddhoti paññatto sattaviseso labbhati. Dutiya vikappe budhadhātussa vāccaṃ sabbañeyyadhammapaṭivijjhanasamatthaṃ sabbaññutañāṇaṃ mukhyena labbhati. Catumaggaphalañāṇañca vipassanā pāramīñāṇañca phalūpacārena. Āsayānusayañāṇādipacchima pacchimañāṇāni kāraṇūpacārena. Tapaccayassa vāccaṃ tena mukhyopacārena upalakkhito aparimāṇaguṇoghālaṅkataṃ khandhapañcakaṃ paṭicca buddhobuddhoti paññatto sattaviseso labbhati. Kiṃpanettha budhadhātussa vāccabhūtaṃ ñāṇameva buddho nāma hoti. Udāhu tapaccayassa vāccabhūto khandhasantāno buddho nāmāti. Ñāṇena pana malakara kilesānaṃ savāsanānaṃ niravasesaṃ pahīnattā citta santāno suddho hoti. Tassa pana suddhattā tassa </p>','\r',char(13)),'\n',char(10)),'-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',170,replace(replace('<p class="bodytext">nissayabhūto <a name="M0.0170"></a> rūpakāyopi suddho hoti. Tasmā tapaccayassa vāccabhūto arahattamaggañāṇādīnaṃ ādhārabhūto khandhasantāno nāmapaññattivohārena buddho nāmāti. Tena vuttaṃ <span class="bld">paṭisambhidāmaggaṭṭhakathāyaṃ</span> –</p>\r\n<p class="bodytext">‘‘Sabbesu dhammesu appaṭihatañāṇa nimittānuttaravimokkhādhigamaparibhāvitaṃ khandhasantānaṃ upādāya paṇṇattiko sabbaññutapadaṭṭhānaṃ vā saccābhisambodhimupādāya paṇṇattiko sattaviseso buddho’’ti.</p>\r\n<p class="bodytext">Paramatthabhūte hi khandhasantāne vohārasukhatthaṃ satta puggalādivohāro āropito. Evaṃ vohārappatto satto yāva bodhiñāṇatthāya na vāyamati, paṇidhānaṃ na karoti. Tāva sattotveva saṅkhaṃ gacchati. Yato pana paṭṭhāya bodhiñāṇatthāya vāyamati, paṇidhānaṃ karoti, tadā bodhisatto nāma vohāro hoti. So pana yadā bodhiñāṇaṃ labhati, tadā buddho nāma vohāro hoti. Tasmā paññattisaṅkhāto khandhasantāno buddho nāmāti daṭṭhabbo. Vuttañhi <span class="bld">mahāniddese</span> ‘‘buddhoti netaṃ nāmaṃ mātarā kataṃ, na pitarā kataṃ, na bhātarā kataṃ, na bhaginiyā kataṃ, na mittāmaccehi kataṃ, na ñātisā lohitehi kataṃ, na samaṇabrāhmaṇehi kataṃ, na devatāhi kataṃ, vimokkhantikametaṃ buddhānaṃ bhagavantānaṃ bodhiyā mūle saha sabbaññutañāṇassa paṭilābhā sacchikā paññatti yadidaṃ buddhoti taṃ buddha’’nti. Atha vā paṭṭhānanayena vā nānantarikanayena vā ñāṇappadhāno suvisuddhakhandhasantāno buddho nāmāti atthopi yujjatevāti.</p>\r\n<p class="bodytext"><span class="bld">Nigrodhabimbo</span>ti nigrodharukkhassa parimaṇḍalo viya parimaṇḍalasarīro. Adhobhāgaṃ rundhatīti nigrodho, vaṭarukkho. Nipubbarudhadhātu āvaraṇe ṇa, usso gāgamo. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',171,replace(replace('<p class="bodytext">Vamati <a name="M0.0171"></a> uggiratīti bimbo, sarīro. Vamudhātu uggiraṇe bo. Vamissa vassa bittaṃ. Nigrodhassa parimaṇḍalo viya parimaṇḍalo bimbo sarīro yassāti nigrodhabimbo, buddho. Nigrodhaparimaṇḍalabimboti vattabbe parimaṇḍalasaddalope nevaṃ vuttaṃ. Yathā hi paṇṇāsahatthatāya vā satahatthatāya vā samakkhandhasākho nigrodho dīghatopi vitthāratopi ekappamāṇova hoti, evaṃ kāyatopi byāmatopi ekappamāṇo. Yathā aññesaṃ kāyo vā dīgho hoti, byāmo vā na evaṃ visamappamāṇoti attho. Vuttañhi <span class="bld">brahmāyusutte</span> ‘‘nigrodhaparimaṇḍalo kho pana bhavaṃ gotamo. Yāvatakvāssa kāyo, tāva takvāssa byāmo. Yāvatakvāssa byāmo, tāva takvāssa kāyo’’ti.</p>\r\n<p class="bodytext"><span class="bld">Mudukaracaraṇo</span>ti saṇhahatthapādo. Ettha ca mudusaddo saṇhasukhumavācako anipphannapāṭipadiko. Atha vā mudeti modayati satteti mudu, saṇhahatthapādo. Mudadhātu modane hāse vā u. Taṃ taṃ kammaṃ karoti etenāti karo, hattho carati gacchati etena taṃ taṃ ṭhānanti caraṇo, pādo. Caradhātu gatiyaṃ yu. Mudukaro ca caraṇo ca yassāti mudukaracaraṇo, buddho. Sappi maṇḍe osāritvā ṭhapitaṃ satavāraṃ vihatakappāsapaṭalaṃ viya mudujātamattakumārassa viya ca niccakālaṃ muduhattha pādoti attho.</p>\r\n<p class="bodytext"><span class="bld">Brahmaghoseṇijaṅgho</span>ti brahmaghoso eṇijaṅghoti padacchedo. Brahmaghosoti brahmuno saro viya aṭṭhaṅga sampannasaravantoti attho. Jhānādiguṇehi brūhati vaḍḍhatītibrahmā. Brūhadhātu manipaccayo. Ghusiyati saddiyati kathiyatīti ghoso, saro. Ghusadhātu sadde ṇa. Brahmuno ghoso saro brahmaghoso, so viya </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',172,replace(replace('<p class="bodytext">ghoso <a name="M0.0172"></a> saro yassāti brahmaghoso, buddho. Yathā hi mahābrahmuno pittasemhehi apalibuddhattā saro visuddho hoti, evameva tathāgatenapi kataṃ kammaṃ vatthuṃ sodheti, vatthussa suddhattā nābhito paṭṭhāya samuṭṭhahanto saro visuddho aṭṭhaṅgasamannāgatoti attho. Tasmā brahmaghoso viya aṭṭhaṅgasamannāgatasaravantoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Eṇijaṅgho</span>ti eṇimigassa jaṅghā viya suvaṭṭajāṇuko. Eti gacchatīti eṇi, migo. Idhātu gatiyaṃ ṇi. Jāyati kamanametāyāti jaṅgho, jāṇu. Janadhātu janane gho. Nassa niggahitādeso. Eṇimigassa jaṅgho viya jaṅgho yassa soti eṇijaṅgho, buddho. Yathā hi eṇimigassa jaṅgho suvaṭṭo hoti, evameva buddhassa maṃsussadena paripuṇṇajaṅgho. Na ekato bandhapiṇḍikamaṃso. Samantato samasaṇṭhitena maṃsena parikkhittāhi suvaṭṭitāhi sāli gabbhayavagabbhasadisāhi jaṅghāhi samannāgatoti attho. Tasmā eṇimigajaṅghasadiso suvaṭṭajāṇukoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Kosacchādaṅgajāto</span>ti kosacchādaaṅgajātoti padacchedo. Usabhavāraṇādīnaṃ aṅgajāto viya suvaṇṇapaduma kaṇṇikasadisena vatthakosena paṭicchannena guyhaṅgajātena samannāgatoti attho. Kusati tiṭṭhati etthāti koso. Kusadhātu gatiyaṃ ṇa. Chādiyatīti chādo. Chadadhātu paṭicchanne ṇa. Kosena chādo kosacchādo. Aṅge sarīrāvayave jātanti aṅgajātaṃ. Kosacchādaṃ aṅgajātaṃ byañjanaṃ yassāti kosacchādaṅgajāto, buddho. Bhagavato hi usabhavāraṇādīnaṃ aṅgajāto viya suvaṇṇapadumakaṇṇi kasadisena kosohitavatthaguyhena byañjanena samannāgatoti attho. Tasmā kosacchādaṅgajātasampannoti thometi. <span class="bld">Punarapī</span>ti puna apīti padacchedo. Rakārā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',173,replace(replace('<p class="bodytext">gamo<a name="M0.0173"></a>. Punāti eso atthantaratthe nipāto. Apisaddo sampiṇḍanattho. <span class="bld">Sugato</span>ti buddho. So hi sundaraṃ nibbānaṃ gacchatīti sugatoti vuccati.</p>\r\n<p class="bodytext"><span class="bld">Suppatiṭṭhitapādo</span>ti suvaṇṇapādukatalamiva bhūmiyaṃ samaṃ suṭṭhu patiṭṭhitapādo. Samaṃ suṭṭhu bhūmiyaṃ patiṭṭhatīti suppatiṭṭhito, pādo. Su pati pubbo ṭhā dhātu gatinivattiyaṃ ta. Pajjati gacchati etenāti pādo. Padadhātu gatimhi ṇa. Suppatiṭṭhito pādo yassāti suppatiṭṭhitapādo, sugato. Yathā hi aññesaṃ bhūmiyaṃ pādaṃ ṭhapentānaṃ aggatalaṃ vā paṇhiṃ vā passaṃ vā paṭhamaṃ phusati vemajjhaṃ vā pana chiddaṃ hoti, ukkhipantānampi aggatalādīsu ekakoṭṭhāsova paṭhamaṃ uṭṭhahati, na evaṃ tassa. Tassa pana suvaṇṇapādukatalaṃ viya ekappahāreneva sakalapādatalaṃ bhūmiṃ phusati, bhūmito uṭṭhahati. Sacepi hi bhagavā anekasataporisaṃ narakaṃ akkamissāmīti pādaṃ nīharati, tāvadeva ninnaṭṭhānaṃ vātapūritaṃ viya kammārabhastaṃ unnamitvā pathavīsamaṃ hoti, unnataṭṭhānampi anto pavisati, dūre akkamissāmīti abhinīharantassa sineruppamāṇopi pabbato seditavetthaṅkuro viya namitvā pādasamīpaṃ āgacchati. Tathā hissa yamakapāṭihāriyaṃ katvā yugandharapabbataṃ akkamissāmīti pāde abhinīharato pabbato namitvā pādasamīpaṃ āgato. So taṃ akkamitvā dutiyapādena tāvatiṃsabhavanaṃ akkami. Na hi cakkalakkhaṇena patiṭṭhātabbaṭṭhānaṃ visamaṃ bhavituṃ sakkoti. Khāṇu vā kaṇḍako vā sakkharakathalā vā uccārapassāvo vā kheḷasiṅghaṇikādīni vā purimatarāva apagacchanti. Tattha tattheva pathaviṃ pavisanti. Tathāgatassa hi sīlatejena paññātejena dhammatejena dasannaṃ pāramīnaṃ ānubhāvena ayaṃ mahāpathavī samā mudu pupphākiṇṇā honti. Tatra tathāgato samaṃ pādaṃ nikkhipati. Samaṃ uddharati, sabbāvantehi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',174,replace(replace('<p class="bodytext">pādatalehi <a name="M0.0174"></a> bhūmiṃ phusati, tasmā suppatiṭṭhitapādoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Mūdo dātuṇṇalomo</span>ti muda odāta uṇṇalomoti padacchedo. Bhamukantare jāto saṇhasukhumo seto suddho uṇṇalomavantoti attho. Ettha ca mudasaddo saṇhasukhumavācako anipphannapāṭipadiko. Atha vā mudeti mudayati satteti mudo, sukhumo. Mudadhātu modane a. Avadāyati khaṇḍiyatīti odāto. Avapubba dādhātu avakhaṇḍane ta. Atha vā avadāyati sujjhatīti odāto, yathā vodānanti. Mudo ca odāto ca mudodāto, uṇati uddhaṃ gacchatīti uṇṇā, lomo. Uṇṇasaddo hi loke lomasaddapariyāyoti vadanti ācariyā. Maṃsassa upari pavattatīti attho. Uṇadhātu gatiyaṃ ṇa. Maṃsacammāni lunāti chindatīti lomo. Ludhātu chindane manipaccayo. Lunitabboti vā lomo, uṇṇā ca sā lomo cāti uṇṇalomo, lomaviseso. Mudo dāto uṇṇalomo yassa soti mudodātuṇṇa lomo, sugato. Uṇṇālomaṃ pana bhagavato dvinnaṃ bhamukānaṃ vemajjhe nāsimatthakeyeva jātaṃ. Uggantvā pana nalāṭamajjhe jātaṃ. Sappimaṇḍe osāretvā ṭhapitasata vitatakappāsapaṭalaṃ viya mudaṃ. Parisuddhaosadhitārakavaṇṇaṃ viya ca sippalitūlalatātūlaṃ viya ca vigatamalena odātaṃ uṇṇālomaṃ atthi, na aññesanti attho. Vuttañhetaṃ <span class="bld">brahmāyusutte</span> –</p>\r\n<p class="bodytext">‘‘Uṇṇā kho panassa bhoto gotamassa bhamukantare jātā odātā mudu tūlasannibhā’’ti.</p>\r\n<p class="bodytext">Taṃ panetaṃ koṭiyaṃ gahetvā ākaḍḍhiyamānaṃ upaḍḍha bāhuppamāṇaṃ hoti. Vissaṭṭhaṃ dakkhiṇāvaṭṭavasena āvaṭṭitvā uddhaggā hutvā santiṭṭhati. Suvaṇṇaphalakamajjhe ṭhapitarajata </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',175,replace(replace('<p class="bodytext">pupphuḷakaṃ <a name="M0.0175"></a> viya, suvaṇṇaghaṭato nikkhammamānaṃ khīradhāraṃ viya aruṇappabhārañjite gaganatale osadhitārakaṃ viya ca atimanoharāya siriyā virocati, tasmā mūdodātuṇṇalomoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Athamapī</span>ti tadaññaṃ api. <span class="bld">Sugato</span>ti buddho. So hi sundaravācaṃ gadati vadatīti sugatoti vuccati. <span class="bld">Brahmujuggattabhāvo</span>ti brahmaujuuggataattabhāvoti padavibhāgo. Brahmuno uju uggataattabhāvo viya ujumeva uggataattabhāvo dīghasarīroti attho. <span class="bld">Brahmāyusutte</span> pana ‘‘brahmujugatto’’tipāṭho. Tadaṭṭhakathāyañca brahmujugattoti brahmā viya ujugatto ujumeva uggatadīghasarīroti vivarati. Taṃ pana sandhāya brahmujuggattabhāvoti vuttaṃ.</p>\r\n<p class="bodytext">Ayaṃ panettha vacanattho. Jhānādiguṇehi uparūparibrūhati vaḍḍhatīti brahmā. Brūhadhātu vaḍḍhane ma, arati avaṅkabhāvena pavattatīti uju, avaṅko. Aradhātu gatiyaṃ jupaccayo. Arissa uādeso. Uddhaṃ gacchati pāpuṇātīti uggato. Upubba gamudhātu gatiyaṃ to. Āhito āropito ahaṃ māno etthāti attā, sarīro. Āpubba dhā dhātu ropane manipaccayo tupaccayo vā. Dhassa to. Massa to. Atha vā sukhadukkhaṃ adati anubhavatīti attā. Adadhātu anubhavane manipaccayo. <span class="bld">Kaccāyanasaddasatthe</span> pana ‘‘sukhadukkhaṃ adati bhakkhatīti attā, jātijarāmaraṇādīhi adiyate bhakkhiyateti vā attā. Ātumā’’ti vuttaṃ. Khyādīhi manimaca tovāti suttena padasiddhi veditabbā. Attāti abhidhānaṃ buddhi ca bhavanti etasmāti attabhāvo, rūpakāyo. <span class="bld">Anuṭīkāyaṃ</span> pana ‘‘āhito ahaṃ māno etthāti attā, soeva bhavati uppajjati na paraparikappito viya niccoti attabhāvo. Attāti vā diṭṭhigatikehi gahetabbākārena bhavati pavattatīti attabhāvo’’ti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',176,replace(replace('<p class="bodytext">vuttaṃ<a name="M0.0176"></a>. Brahmuno ujumeva uggato attabhāvo viya ujumeva uggato attabhāvo yassa soti brahmujuggattabhāvo, sugato. Yebhuyyena hi sattā khandhe kaṭiyaṃ jāṇusūti tīsu ṭhānesu namanti. Thokaṃ kaṭiyaṃ namantā pacchato namanti. Itaresu dvīsu ṭhānesu purato. Dīghasarīrā paneke passā vaṅkā honti, eke mukhaṃ unnametvā nakkhattāni gaṇayantā viya caranti, eke appamaṃsalohitā sūlasadisā honti, pavedhamānā gacchanti, tathāgato pana ujumeva uggantvā dīghappamāṇo devanagare ussitasuvaṇṇa toraṇaṃ viya hoti. Tasmā brahmujuggattabhāvoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Nīlakkhī</span>ti nīlaakkhīti padacchedo. Atinīlanettena samannāgatoti attho. Ikkhati passati anena rūpārammaṇaṃ samavisamaṃ vāti akkhi, nettaṃ. Ikkhadhātu dassane i. Issa attaṃ. Nīlā atinīlā akkhi nettā yassāti nīlakkhi, sugato. Bhagavato hi na sakalanīlakkhiva hoti. Nīlayuttaṭṭhāne panassa ummārapupphasadisena ativisuddhena nīlavaṇṇena samannāgatāni akkhīni honti. Pītayuttaṭṭhāne kaṇikārapupphasadisena lohitavaṇṇena. Lohitayuttaṭṭhāne bandhujīvakapupphasadisena lohitavaṇṇena. Seta yuttaṭṭhāne osadhitārakasadisena setavaṇṇena. Kāḷa yuttaṭṭhāne addāriṭṭhakasadisena kāḷavaṇṇena samannāgatāni suvaṇṇavimāne ugghāṭitamaṇisīhapañjarasadisāni khāyanti. Vuttañhi <span class="bld">mahāniddese</span> ‘‘maṃsacakkhumhi bhagavato pañcavaṇṇā saṃvijjanti. Nīlo ca vaṇṇo, pītako ca vaṇṇo, lohitako ca vaṇṇo, kaṇho ca vaṇṇo, odāto ca vaṇṇo akkhilomāni ca bhagavato, yattha ca akkhi lomāni patiṭṭhitāni. Taṃ nīlaṃ hoti sunīlaṃ pāsādikaṃ dassaneyyaṃ ummārapupphasamānaṃ. Tassa parato pītakaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',177,replace(replace('<p class="bodytext">hoti <a name="M0.0177"></a> supītakaṃ suvaṇṇavaṇṇaṃ pāsādikaṃ dassaneyyaṃ kaṇikārapupphasamānaṃ. Ubhato akkhikuṭāni bhagavato lohitakāni honti sulohikāni pāsādikāni dassaneyyāni indagopakasamānāni. Majjhe kaṇhaṃ hoti sukaṇhaṃ alūkhaṃ suddhaṃ pāsādikaṃ dassaneyyaṃ addāriṭṭhaka samānaṃ. Tassa parato odātaṃ hoti suodātaṃ setaṃ paṇḍaraṃ pāsādikaṃ dassaneyyaṃ osadhitārakasamāna’’nti. Bhagavā pana tena maṃsacakkhunā caturaṅgasamannāgate andhakārepi samantā yojanaṃ passati divā ceva rattiñca.</p>\r\n<p class="bodytext">Vuttañhi <span class="bld">mahāniddese</span> –</p>\r\n<p class="bodytext">‘‘Tena bhagavā pākatikena maṃsacakkhunā attabhāva pariyāpannena purimasucaritakammābhinibbattena samantā yojanaṃ passati divā ceva rattiñca. Yadāpi caturaṅgasamannāgato andhakāro hoti, sūriyo ca atthaṅgamito hoti, kāḷapakkho ca uposatho hoti, tibbo ca vanasaṇḍo hoti, mahā ca kāḷamegho abbhuṭṭhito hoti, evarūpepi caturaṅgasamannāgate andhakāre samantā yojanaṃ passati. Natthi so kuṭṭo vā kavāṭaṃ vā pākāro vā pabbato vā gaccho vā latā vā āvaraṇaṃ rūpānaṃ dassanāyā’’ti. Tasmā upalakkhaṇanayena nīlakkhīti thometi.</p>\r\n<p class="bodytext"><span class="bld">Dīghapaṇhī</span>ti āyatapaṇhi. Paripuṇṇapaṇhīti attho. Pasati bādhati anena paresanti paṇhi, pādassa pacchābhāgo. Pasadhātu bādhane ṇi. Sassa ho. <span class="bld">Ṇvādimoggallāne</span> pana ‘‘paṇa byavahārathutīsu hi paṇiyati vohāriyatīti paṇhi, pādassa pacchābhāgo’’ti vuttaṃ. Dīghā paṇhi yassa soti dīghapaṇhi, sugato. Yathā hi aññesaṃ agga pādo dīgho hoti. Paṇhimatthake jaṅghā patiṭṭhāti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',178,replace(replace('<p class="bodytext">paṇhiṃ <a name="M0.0178"></a> tacchetvā ṭhapitā viya hoti. Na evaṃ tathāgatassa. Tathāgatassa pana catūsu koṭṭhāsesu dve koṭṭhāsā aggapādā honti. Tatiye koṭṭhāse jaṅghā patiṭṭhāti. Catutthe koṭṭhāse āraggena vaṭṭetvā ṭhapitā viya rattakambale geṇḍukasadisā paṇhi hoti. Tasmā dīghapaṇhīti thometi.</p>\r\n<p class="bodytext"><span class="bld">Sukhumamalachavī</span>ti sukhuma amala chavīti padacchedo. Saṇhamalavisuddhataco saṇhamalavirahitataco vāti attho. Ettha ca sukhumasaddo saṇhavācako anipphannapāṭipadiko. Atha vā sukhayati saṇhabhāvenāti sukhumaṃ, saṇhaṃ. Sukhadhātu takkariyaṃ umapaccayo. Taṃsamaṅgīpuggalaṃ kiliṭṭhaṃ malati dhāretīti malaṃ, rajādimalaṃ. Maladhātu dhāraṇe a, natthi malametissāti amalā, chavī. Maṃsaṃ chādetīti chavī. Chada dhātu saṃvaraṇe ravapaccayo īpaccayo ca. Sukhumā saṇhā amalā malavirahitā chavī yassāti sukhumamalachavī, sugato. Bhagavato hi suvaṇṇasiniddhasaṇhasarīrattā kañcanasannibha tacattā ca rūpakāye padumapalāse udakabindhu viya rajojallaṃ vā malaṃ vā na limpati. Vuttañhi majjhimapaṇṇāse <span class="bld">brahmāyusutte</span> –</p>\r\n<p class="bodytext">‘‘Sukhumacchavī kho pana bhavaṃ gotamo sukhumattā chaviyā rajojallaṃ kāye na upalimpatī’’ti. Tasmā sukhuma malachavīti thometi.</p>\r\n<p class="bodytext"><span class="bld">Thomyarasaggasaggī</span>ti thomyarasa gasa aggīti padacchedo. Thomitabba rasaharaṇasattasahassanhārū uddhaggikāti attho. Thomiyatīti thomyo, nhāru. Thomadhātu sīlāghāyaṃ ṇyo. Madhurādibhedaṃ rasaṃ gasenti anto pavesentīti rasaggasā, nhāru. Rasasaddūpapadagasadhātu adane a. Atha vā rasaṃ gasati harati etehīti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',179,replace(replace('<p class="bodytext">rasaggasā<a name="M0.0179"></a>, nhāru. Gasadhātu haraṇe a. Dhātusaddo hi anekattho. Rasaggasānaṃ aggāti rasaggasaggā, te ettha santīti rasaggasaggī, nhāru. Assathyatthe īpaccayo. Thomyo rasaggasaggī yassa soti thomyarasaggasaggī, sugato. Tathāgatassa hi sattarasaharaṇisahassāni uddhaggāni hutvā gīvāyameva paṭimukkāni. Tilaphalamattopi āhāro jivhagge ṭhapito sabbaṃ kāyaṃ anupharati. Teneva mahāpadhānaṃ padahantassa ekataṇḍulādīhipi kaḷāyayūsapasatenapi kāyassa yāpanaṃ hoti. Aññesaṃ pana tathā abhāvā na sakalakāyaṃ ojā pharati. Tena te bahvābādhā honti. Idaṃ lakkhaṇaṃ appābādhatāsaṅkhātassa nissandaphalassavasena pākaṭaṃ hoti, tasmā thomyarasaggasaggīti thometi.</p>\r\n<p class="centered">Tevīsatimavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="bodytext"><a name="para24"></a><span class="paranum">24</span>. Dutiyagāthāyaṃ <span class="bld">jino</span>ti pañcamārajitavā buddho. Tassa pana padassa samacattālīsaggadantotyādīhi padehi tulyaliṅgattabhāvena sambandho. <span class="bld">Cattālīsaggadanto</span>ti cattālīsaaggadantoti padacchedo. Samacattālīsa uttamadantoti attho. Ettha ca aggasaddo uttamavācako anipphannapāṭipadiko. Atha vā uttamabhāvaṃ ajati gacchatīti aggo, danto. Daṃsati vidaṃsati bhojjamanenāti danto. Daṃsadhātu daṃsane ta. Tassa nto dhātvantalopo. Samacattālīsaṃ aggo uttamo danto yassāti cattālīsaggadanto, jino. Uparimahanuke patiṭṭhitā dantā vīsati heṭṭhime vīsatīti samacattālīsadantā hontīti attho. Aññesañhi paripuṇṇadantānampi dvattiṃsadantā honti. Tathā gatassa pana cattālīsaṃ. Tasmā cattālīsaggadantoti thometi.</p>','\r',char(13)),'\n',char(10)),'-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',180,replace(replace('<p class="bodytext"><span class="bld">Samakalapanajo</span>ti <a name="M0.0180"></a> kakacena chinditvā ṭhapito viya samadantoti attho. Lapati vadati etenāti lapanaṃ, mukhaṃ. Lapadhātu vadane yu. Tasmiṃ lapane mukhe jātoti lapanajo, danto. Tappurisasamāsoyaṃ, samako lapanajo danto yassāti samakalapanajo, jino. <span class="bld">Pāḷiyaṃ</span> pana ‘‘samadanto’’ti pāṭho atthi. Ayamevattho. Aññesañhi keci dantā uccā, keci nīcāti visamā honti. Tathāgatassa pana ayapaṭṭachinnasaṅkhapaṭalaṃ viya samā honti. Tasmā samakalapanajoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Antaraṃsappapīno</span>ti antaraaṃsapīnoti padavibhāgo. Gāthābandhavasena chandānurakkhaṇatthaṃ pakārāgamaṃ katvā evaṃ vuttaṃ. Dvinnaṃ aṃsānaṃ khandhānaṃ antaraṃ vemajjhe paripuṇṇapiṭṭhitaloti attho. Ettha ca aṃsasaddo khandhatthe vattati. Vuttañhetaṃ <span class="bld">abhidhānappadīpikāyaṃ</span> ‘‘aṃso nitthi bhujasiro, khandho tassandhi jattu ta’’nti.</p>\r\n<p class="bodytext">Anati jīvati etenāti aṃso, khandho. Anadhātu pāṇe sopaccayo. Nassa niggahitādeso. Sarīrassa avayavabhāvena anati gacchati pavattatīti vā aṃso, dvinnaṃ aṃsānaṃ khandhānaṃ antaraṃ vemajjhanti antaraṃso, piṇati pūratīti pīno, piṭṭhitalaṃ. Piṇadhātu pinane pūraṇe vā ṇa. Ṇassa no. Dvinnaṃ aṃsānaṃ khandhānaṃ antaraṃ vemajjhe pīnaṃ paripuṇṇaṃ piṭṭhitalaṃ yassāti antaraṃsappapīno, jino. Uttarapadalopabahubbīhisamāsoyaṃ. Aññesañhi taṃ ṭhānaṃ ninnaṃ hoti, dve piṭṭhikoṭṭā pāṭiekkaṃ paññāyanti. Tathāgatassa pana kaṭito paṭṭhāya maṃsapaṭalaṃ yāva khandhā uggamma samussitasuvaṇṇaphalakaṃ viya piṭṭhiṃ chādetvā patiṭṭhitaṃ. Tasmā antaraṃsappapīnoti thometi. <span class="bld">Pāḷiyaṃ</span> pana ‘‘citantaraṃso’’ti pāṭho. <span class="bld">Tadaṭṭhakathāyañca</span> ‘‘citantaraṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',181,replace(replace('<p class="bodytext">soti <a name="M0.0181"></a> antaraṃsaṃ vuccati dvinnaṃ koṭṭānamantaraṃ. Taṃ paripuṇṇa massāti antaraṃso’’ti vuttaṃ.</p>\r\n<p class="bodytext"><span class="bld">Cakkenaṅkitapādo</span>ti cakkena aṅkitapādoti padacchedo. Aṭṭhasatapādacakkalakkhaṇena lakkhitapādataloti attho. Cakkena aṭṭhasatapādacakkena aṅkito lakkhito pādo yena soti cakkenaṅkitapādo, jino. Tatiyābahubbīhi samāsoyaṃ. Aññesañhi pādatale aṭṭhasatapādacakka lakkhaṇaṃ natthi. Tathāgatassa pādataleeva vicitraṃ aṭṭhasata pādacakkalakkhaṇaṃ atthi. Tasmā cakkenaṅkitapādoti thometi.</p>\r\n<p class="bodytext">Vuttañhi <span class="bld">brahmāyusutte</span> ‘‘heṭṭhā kho pana tassa bhoto gotamassa pādatalesu cakkāni jātāni sahassārāni sanemikāni sanābhikāni sabbākāraparipūrānī’’ti. Aṭṭhasatapādacakkalakkhaṇasarūpaṃ pana yo pādapaṅkajamuduttalarā jikehīti gāthāvaṇṇanāyaṃ vuttamevāti.</p>\r\n<p class="bodytext"><span class="bld">Aviraḷadasano</span>ti avivaradanto. Avivaradantena sampannoti attho. Vivarati phullatīti viraḷo. Varadhātu phullaneḷo. Assi. Na viraḷo aviraḷo, natthi viraḷo etassāti aviraḷo, danto. Daṃsati vidaṃsati khādanīyaṃ vā bhojanīyaṃ vā etenāti dasano, danto. Daṃsadhātu daṃsane yu. Niggahitalopo. Aviraḷo dasano danto yassāti aviraḷadasano, jino. Aññesañhi kumbhilānaṃ viya dantā viraḷā honti. Macchamaṃsādīni khādantānaṃ dantantaraṃ pūrati. Tathāgatassa pana kanakalatāya samussāpitavajira panti viya aviraḷā tulikāya dassitaparicchedā viya dantā honti, tasmā aviraḷadasanoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Mārajussaṅkhapādo</span>ti māraji ussaṅkhapādoti padacchedo. Ettha ca mārajīti pañcamārajitavā buddho kattubhūto. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',182,replace(replace('<p class="bodytext">So <a name="M0.0182"></a> hi pañcamāre jitavāti mārajīti vuccati. Ussaṅkhapādoti pādassa upari ṭhitena saṅkhasadisattā saṅkhanāmikena gopphakena samannāgatoti attho. Saṃsuṭṭhuṃ pathaviṃ khanatīti saṅkhe. Saṃpubbakhanudhātu avadāraṇe kvi. <span class="bld">Apadānaṭṭhakathāyaṃ</span> pana ‘‘saṃsuṭṭhuṃ khādanto khaṇanto gacchatīti saṅkhe. Samuddajalapariyante caramāno gacchati caratīti attho’’ti vuttaṃ. Saṅkhe viyāti saṅkhe, gopphako. Pādassa uddhaṃ upari ṭhitaṃ saṅkhasadisaṃ gopphaka massāti ussaṅkhapādo, jino. Vuttañhi <span class="bld">brahmāyusuttaṭṭhakathāyaṃ</span> ‘‘uddhaṃ patiṭṭhitagopphakattā ussaṅkhā pādā assāti ussaṅkhapādo’’ti. Aññesañhi piṭṭhipāde gopphakā honti. Tena tesaṃ pādā āṇibaddhā viya baddhā honti. Na yathāsukhaṃ parivattanti. Gacchantānaṃ pādatalāni na dissanti. Tathāgatassa pana abhirūhitvā upari gopphakā patiṭṭhahanti. Tenassa nābhito paṭṭhāya uparimakāyo nāvāya ṭhapita suvaṇṇapaṭimā viya niccalo hoti. Adhokāyova iñjati. Sukhena pādā parivattanti. Puratopi pacchato ubhayapassesupi ṭhatvā passantānaṃ pādatalāni paññāyanti. Na hatthīnaṃ viya pacchatoyevāti. Tasmā ussaṅkhapādoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Tiṭṭhanto nonamantobhayakaramudunā jaṇṇukānāma santo</span>ti ettha tiṭṭhanto no namanto ubhaya karamudunā jaṇṇukāni āmasantoti padavibhāgo. <span class="bld">Tiṭṭhanto</span>ti ṭhitakova. <span class="bld">Nonamanto</span>ti anamamāno hutvā. <span class="bld">Ubhaya karamudunā</span>ti sukhumena hatthadvayena. <span class="bld">Jaṇṇukānī</span>ti jaṅghāni. Jāyati gamanāgamanaṃ anenāti jaṇṇu, janadhātu janane ṇu. Soeva jaṇṇukā, jaṅghorūnaṃ sandhi labbhati. Tāni. <span class="bld">Āmasanto</span>ti parimajjituṃ sakkonto. Ettha ca tiṭṭhanto nonamantoti iminā tathāgatassa akhujjaavāmanabhāvo </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',183,replace(replace('<p class="bodytext">dīpito<a name="M0.0183"></a>. Avasesajanā hi khujjā vā honti vāmanā vā. Khujjānaṃ uparimakāyo aparipuṇṇo hoti vāmanānaṃ heṭṭhimakāyo. Te aparipuṇṇakāyattā na sakkonti nonamanto jaṇṇukāni parimajjituṃ. Tathāgato pana paripuṇṇaubhayakāyattā sakkoti, tasmā tiṭṭhanto…pe… jaṇṇukā nāmasantoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Vaṭṭakkhandho</span>ti suvaṇṇāliṅgo viya samavaṭṭitagaliko. Vuttañhi <span class="bld">brahmāyusutte</span> ‘‘samavaṭṭakkhandho’’ti. Parimaṇḍalā kārena vaṭṭati pavattatīti vaṭṭo, parimaṇḍalo. Vaṭṭadhātu vaṭṭane a. Bhojanaṃ khādati etena galenāti khandho, galo. Khādadhātu khādane bhakkhane vā kvipaccayo. Khādāmagamānaṃ khandhandhagandhāti suttena padasiddhi veditabbā. <span class="bld">Abhidhānaṭīkāyaṃ</span> pana ‘‘kaṃ matthakaṃ dadhātīti kandho. Soeva kakārassa khakārakaraṇavasenā’’ti vuttaṃ. Vaṭṭo samaparimaṇḍalo khandho galo yassāti vaṭṭakkhandho, jino. Yathā hi eke koñcā viya bakā viya varāhā viya ca dīghagalā vaṅkagalā puthugalā ca honti. Kathanakāle sirājalaṃ paññāyati. Mando saro nikkhamati, na evaṃ tassa. Tathāgatassa pana suvaṭṭitasuvaṇṇāliṅgasadiso khandho galo hoti. Kathanakāle sirājalaṃ na paññāyati. Meghassa viya gajjato saro mahā hoti. Tasmā vaṭṭakkhandhoti thometi.</p>\r\n<p class="bodytext">Puna jinoti buddho kattubhūto. Tassa pana padassa gotaruṇapakhumakotyādīhi padehi tulyaliṅgattabhāvena sambandho. <span class="bld">Gotaruṇapakhumako</span>ti taṃmuhuttajātaratta vacchassa cakkhubhaṇḍo viya vippasannacakkhubhaṇḍo, vippasannacakkhubhaṇḍena samannāgato vā. Ettha ca pakhumakoti sakalaṃ cakkhubhaṇḍaṃ adhippetaṃ. Cakkhubhaṇḍanti cakkhuparivāraṃ. Cakkhubhaṇḍanti akkhidalanti keci. Akkhidalapattanti vadanti aññe. Akkhidale </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',184,replace(replace('<p class="bodytext">hi <a name="M0.0184"></a> pana saddhiṃ akkhibimbanti veditabbaṃ. Ayaṃ panettha vacanattho. Patati cakkhuṃ parivāreti etenāti pakhumaṃ, pamhādiparivāraṃ. Patadhātu parivāre umo. Takārassa kho. Atha vā akkhino pakkhadvaye jātanti pakhumaṃ, pamhaṃ. Jātataddhite umo. Pakkhumanti vattabbe kakāralopavasena pakhumanti vuccati. Pakhumaṃyeva pakhumako. Gotaruṇassa taṃmuhuttajātarattavacchassa pakhumaṃ viya pakhumaṃ yassa soti gotaruṇapakhumako, jino. Gotaruṇassa taṃmuhuttajātarattavacchassa pakhumasadisaṃ vippasannacakkhubhaṇḍaṃ atthīti attho. Aññesañhi akkhibhaṇḍo aparipuṇṇo hoti. Hatthimūsikakākādīnaṃ akkhisadisehi viniggatehi gambhīrehipi akkhīhi samannāgatā. Tathāgatassa pana dhovitvā majjitvā ṭhapitamaṇiguḷikā viya mudusiniddhanīla sukhumapakhumācitāni akkhīni honti. Tasmā gotaruṇapakhumakoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Sīhapubbaḍḍhakāyo</span>ti sīhassa pubbaḍḍhakāyo viya paripuṇṇakāyo. Vātātapādiparissayaṃ sahati khamatīti sīho, sahadhātu khamane a. Īkārāgamo. Atha vā sūkaramahiṃsādayo satte hiṃsatīti sīho. Hiṃsadhātu hanane ṇa. Hiṃsoti vattabbe akkharavipariyāyena sīhoti vuccati. Atha vā sabbiriyāpathesu daḷhavīriyattā suṭṭhu īhati vāyamatīti sīho, kesarasīho. Supubba īhadhātu cetāyaṃ a. Tena vuttaṃ <span class="bld">saddanītiyaṃ</span> ‘‘sahahiṃsa īhāvasā, sīhasaddagatiṃ vade’’ti. Sīhassa pubbaḍḍhe jāto kāyo viya paripuṇṇakāyo yassāti sīhapubbaḍḍhakāyo, jino. Yathā hi sīhassa pubbaḍḍhakāyova paripuṇṇo hoti, na evaṃ tassa. Tathāgatassa pana kāyo sabbaparipuṇṇoti attho. Sīhassa pana purimakāyova paripuṇṇo hoti, pacchima </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',185,replace(replace('<p class="bodytext">kāyo <a name="M0.0185"></a> aparipuṇṇo. Tathāgatassa pana sīhassa pubbaḍḍhakāyoiva sabbo kāyo paripuṇṇo. Sopi sīhasseva na tattha tattha vinatunnatādivasena dussaṇṭhita visaṇṭhito. Dīghayuttaṭṭhāne pana dīgho. Rassakisathūla anuvaṭṭitayuttaṭṭhānesu tathāvidhova hoti. Vuttañhetaṃ ‘‘manāpiye ca kho bhikkhave kammavipāke paccupaṭṭhite yehi aṅgehi dīghehi sobhati, tāni aṅgāni thūlāni saṇṭhahanti. Yehi aṅgehi kisehi sobhati, tāni aṅgāni kisāni saṇṭhahanti. Yehi aṅgehi vaṭṭehi sobhati, tāni aṅgāni vaṭṭāni saṇṭhahantī’’ti. Iti nānācittena puññacittena cittito dasahi pāramīhi sajjito tathāgatassa attabhāvo tassa loke sabbasippino vā iddhimanto vā patirūpakampi kātuṃ na sakkonti. Tasmā sīhapubbaḍḍhakāyoti thometi.</p>\r\n<p class="centered">Catuvīsatimavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="bodytext"><a name="para25"></a><span class="paranum">25</span>. Tatiya gāthāyaṃ <span class="bld">sugato</span>ti buddho kattubhūto. Tassa pana padassa sattappīnotyādīhi padehi tulyaliṅgattabhāvena sambandho. <span class="bld">Sattappīnoti</span> dvihatthapāda aṃsakūṭa ekagalavasena sattasu ṭhānesu paripuṇṇamaṃsikoti attho. Ācariyā pana paripuṇṇadvihatthapāda aṃsakūṭa khandhasaṅkhātasattaṭṭhānoti atthaṃ vadanti. Maṃsena pīṇati paripūratīti pīno. Pīṇate paripūrate vā pīno. Pīṇadhātu pīṇane, pūraṇe vā ṇa. Ṇassa no. Dve hatthapiṭṭhiyo dve pādapiṭṭhiyo dve aṃsakūṭāni ekagalo cāti sattasu ṭhānesu pīno paripuṇṇo maṃso assāti sattapīno, sugato labbhati. Aññesañhi hatthapādapiṭṭhīsu nhārujālā paññāyanti aṃsakūṭagalesu aṭṭhikoṭiyo. Te manussā petā viya khāyanti. Na tathāgato. Tathāgato pana sattasu </p>','\r',char(13)),'\n',char(10)),'-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',186,replace(replace('<p class="bodytext">ṭhānesu <a name="M0.0186"></a> paripuṇṇamaṃsussadattā niguḷanhārujālehi hatthapiṭṭhādīhi vaṭṭetvā ṭhapitasuvaṇṇavaṇṇāliṅgasadisena galena sīlārūpakaṃ viya cittakammarūpakaṃ viya ca khāyati, tasmā sattappīnoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Brahmāyusutte</span> pana ‘‘sattussado’’ti pāṭho. Tassa pana sattasu ṭhānesu paripuṇṇamaṃsussadoti attho daṭṭhabbo.</p>\r\n<p class="bodytext"><span class="bld">Dīghaṅgulī</span>ti dīghaaṅgulīti padacchedo. Samaāyatahattha pādaṅgulīti attho. Aṅgati uddhaṃ gacchatīti aṅguli. Aṅgati taṃ taṃ kāyaṅgaṃ parāmasati etāyāti vā aṅguli, karasākhā. Agidhātu gatiyaṃ ulipaccayo. Atha vā aṅgaṃ kāyaṅgaṃ ulati gacchati parāmasati etāyāti aṅguli, karasākhā. Aṅga saddūpapadauladhātu gatiyaṃ i. Samadīghā aṅguli hattha pādaṅguli yassa soti dīghaṅguli, sugato. Samadīghā hatthapādaṅguli atthīti attho. Yathā hi aññesaṃ kāci aṅguli dīghā hoti kāci rassā, na evaṃ tathāgatassa. Tathāgatassa pana makkaṭasseva dīghahatthapādaṅguliyo mūle thūlā anupubbena gantvā agge tanukā niyyāsatelena madditvā vaṭṭitaharitālavaṭṭasadisā honti. Tasmā dīghaṅgulīti thometi. <span class="bld">Mathā</span>ti maathāti padacchedo. Makāro hi padasandhikaro. Athāti atthantare nipāto. Tadaññanti attho.</p>\r\n<p class="bodytext"><span class="bld">Lomakūpekalomo</span>ti lomakūpaekalomoti padacchedo. Ekekalomakūpe ekekajātalomoti attho. Cammamaṃsaṃ lunāti chindatīti lomo, vaḍḍhamānaṃ lunitabbanti vā lomo, lūdhātu chedane manipaccayo. Kuvuccati udakaṃ, taṃ pāti rakkhatīti kūpo. Kupubbapādhātu rakkhane kvi. Ussa dīgho. Kūpo viyāti kūpo, atha </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',187,replace(replace('<p class="bodytext">vā <a name="M0.0187"></a> kuvuccati lomaṃ, taṃ pāti rakkhatīti kūpo. <span class="bld">Abhidhānaṭīkāyaṃ</span> pana ‘‘kusadde po. Dīghādi. Kūpo. Kena ubhatīti vā kūpo. Bhassa po’’ti vuttaṃ. Lomassa kūpo lomakūpo. Tasmiṃ tasmiṃ jāto ekeka lomo yassāti lomakūpekalomo, sugato. Yathā hi aññesaṃ ekekasmimpi lomakūpe anekāni lomāni jāyanti, na evaṃ tathāgatassa. Tathāgatassa pana ekekasmiṃ lomakūpe ekekalomo jāyati. Vuttañhi <span class="bld">brahmāyusutte</span> ‘‘ekekalomo kho pana bhavaṃ gotamo. Ekekāni lomāni lomakūpesu jātā nī’’ti. Tasmā lomakūpekalomoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Sampannodātadāṭho</span>ti sampannoodātadāṭhoti padacchedo. Sampannasukkadāṭhoti attho. Sampajjatīti sampanno. Saṃpubbapadadhātu gatiyaṃ ta. Tassa anno. Avadāyati avakhaṇḍiyati aññavaṇṇobhāsanti odāto. Avapubba dādhātu avakhaṇḍane ta. Daṃsati odanādiṃ etāyāti dāṭho, dantaviseso. Daṃsadhātu daṃsane ṭho. Daṃsissa dā. Sampanno ca odāto ca dāṭho yassāti sampanno dātadāṭho, sugato. Aññesañhi pūtidantā uṭṭhahanti. Tena kāci dāṭhā kāḷāpi vivaṇṇāpi honti. Tathāgato pana sukkadāṭho osadhitārakampi atikkamma virocamānāya pabhāya samannāgatadāṭho hoti. <span class="bld">Pāḷiyaṃ</span> pana ‘‘susukkadāṭho’’ti pāṭho. Ayamevattho. Tasmā sampannodātadāṭhoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Kanakasamataco</span>ti suvaṇṇena sadisacammo. Kanati dibbati obhāsenāti kanakaṃ. Kaniyati icchiyati manusse hīti vā kanakaṃ, suvaṇṇaṃ. Kanadhātu dittigatikantīsu akapaccayo, saṃsadisena amati gacchati pavattatīti samaṃ. Saṃpubbaamadhātu gatiyaṃ a. Tacati maṃsaṃ chādeti saṃvarati </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',188,replace(replace('<p class="bodytext">vāti <a name="M0.0188"></a> taco, tacati dibbati suvaṇṇobhāsenāti vā taco, cammaṃ. Tacadhātu saṃvaraṇadittīsu a. Kanakena suvaṇṇena samo sadiso taco yassāti kanaka samataco, sugato. Yathā hi aññesaṃ taco koci nīlataco setataco pītataco hoti, na evaṃ tathāgatassa. Tathāgatassa pana jātihiṅgulakena majjitvā nisadāya ghaṃsitvā gerukaparikammaṃ katvā ṭhapita ghanasuvaṇṇarūpakaṃ viya kañcanasadisataco hoti. <span class="bld">Pāḷiyaṃ</span> pana ‘‘suvaṇṇavaṇṇo kañcanasannibhattaco’’ti vutto. Ettha ca suvaṇṇavaṇṇo viya vaṇṇo yassāti suvaṇṇavaṇṇo, kañcanena sannibhaṃ sadisaṃ tacamassāti kañcanasannibhattacoti vacanattho kātabbo. Ettha ca suvaṇṇavaṇṇanti iminā tathāgatassa ghanasiniddhasaṇhasarīrataṃ dasseti. Kañcanasannibhattacoti iminā chavivaṇṇaṃ dasseti. Atha vā vevacanametaṃ padadvayaṃ. Tasmā kanakasamatacoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Nīlamuddhaggalomo</span>ti nīlaṃ uddhaṃ aggalomoti padacchedo. Nīlaṃ uddhaṃ aggā koṭi hutvā mukhasobhaṃ ullokayamāno viya ṭhitalomo, nīlāni uddhaṃ aggāni koṭīni hutvā ṭhitāni lomāni yassāti nīlamuddhaggalomo, sugato. Yathā hi aññesaṃ lomāni heṭṭhā aggāni hutvā tiṭṭhanti, na evaṃ tathāgatassa. Tathāgatassa pana lomāni āvaṭṭapariyosāne uddhaṃ aggāni hutvā mukhasobhaṃ ullokayamānāni viya tiṭṭhanti. Vuttañhetaṃ <span class="bld">brahmāyusutte</span> ‘‘uddhaggalomo bhavaṃ gotamo. Uddhaggāni lomāni jātāni nīlāni añjanavaṇṇāni kuṇḍalāvaṭṭāni padakkhiṇāvaṭṭakajātānī’’ti. Tasmā nīlamuddhaggalomoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Sambuddho</span>ti tathāgato. So hi anaññabuddho hutvā sāmaṃyeva cattāri saccāni bujjhatīti sambuddhoti vuccati. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',189,replace(replace('<p class="bodytext">Tassa <a name="M0.0189"></a> pana padassa thūlajivhotyādīhi tīhi padehi tulyaliṅgattabhāvena sambandho.</p>\r\n<p class="bodytext"><span class="bld">Thūlajivho</span>ti mududīghaputhula jivhāya sampannoti attho. Gāthābandhavasena hi mududīghasaddalopaṃ katvā evaṃ vuttaṃ. Tena vuttaṃ <span class="bld">aṭṭhakathāyaṃ</span> ‘‘mudujivhā dīghā thūlā vaṇṇa sampannā’’ti. Thūlati brūhatīti thūlā, jivhā. Thūladhātu buddhiyaṃ a. Jīvati pāṇaṃ dhāretīti jivhā, rasanā. Jīvadhātu pāṇadhāraṇe hapaccayo. Jīvanti sattā etena rasenāti jīvitaṃ, chabbidharaso. Taṃ avhāyatīti jivhā, jivhāpasādarūpaṃ. Sambhārajivhā ṭhānūpacārena labbhati. Jīvitasaddūpapada avhadhātu avhāyane a. Jīvitaavhāti vattabbe niruttinayena vitasaddalopaṃ katvā jivhāti vuttaṃ. Thūlā mahantā dīghā mudukā jivhā yassāti thūlajivho, sambuddho. Aññesañhi jivhā thūlāpi hoti kisāpi rassāpi thaddhāpi visamāpi. Tathāgatassa pana mudujivhā dīghā puthulā vaṇṇa sampannā hoti. So taṃ lakkhaṇaṃ pariyesituṃ āgatānaṃ brahmāyubrāhmaṇādīnaṃ kaṅkhāvinodanatthaṃ jivhāya mudukattā taṃ jivhaṃ kathinasūci viya vaṭṭetvā ubhopi nāsikāsotāni parāmasati, dīghattā ubho kaṇṇasotāni parāmasati, puthulattā kesantapariyosānaṃ kevalampi nalāṭaṃ paṭicchādeti, evaṃ tassā mududīghaputhulabhāvaṃ pakāsento kaṅkhāvinodeti, evaṃ tilakkhaṇasampannaṃ jivhaṃ sandhāya thūla jivhoti thometi. <span class="bld">Pāḷiyaṃ</span> pana ‘‘pahūtajivho’’ti pāṭho. Ayamevattho.</p>\r\n<p class="bodytext"><span class="bld">Athā</span>ti atthantaratthe nipāto. Tadaññanti attho. <span class="bld">Sīhahanuko</span>ti sīhassa heṭṭhā hanu viya paripuṇṇa hanuko. Sīhasaddattho heṭṭhā vuttoyeva. Hanati odanādīnaṃ vaṇṇavisesaṃ nāsetīti hanu. Hanadhātu hiṃsāyaṃ u. Sīhassa heṭṭhā hanu viya hanu yassāti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',190,replace(replace('<p class="bodytext">sīhahanuko<a name="M0.0190"></a>, sambuddho. Samāsante ko. Sīhassa hi heṭṭhimahanumeva paripuṇṇaṃ hoti, na uparimaṃ. Tathāgatassa pana sīhassa heṭṭhimaṃ viya dvepi paripuṇṇāni dvādasiyaṃ pakkhassa candasadisāni honti. Tasmā sīhahanukoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Jālikappādahattho</span>ti kusalena vaḍḍhakinā vātapāne sammāyojitajālaṃ viya cammena appaṭibaddhahatthapādaṅgulantaro. Jalati dibbatīti jālaṃ, jaladhātu dittiyaṃ ṇa. Jālaṃ assa atthi tasmiṃ vā vijjatīti jālikaṃ, vātapānaṃ. Assathyatthe ṇiko. Atha vā jālaṃ eva jālikaṃ. Pajjati gacchati etenāti pādo, caraṇo. Padadhātu gamane ṇa. Taṃ taṃ bhāraṃ haratīti hattho, karo. Haradhātu haraṇe tho. Jālikaṃ viya hatthapādaṅgulantaraṃ yassāti jālikappādahattho, sambuddho. Kusalena vaḍḍhakinā vātapāne sammāyojitāni jālasadisāni hatthapādaṅgulantarāni honti. Na ca cammena paṭibaddhaaṅgulantarānīti attho. Ettha jālikappādahatthaṅgulantaranti vattabbe uttarapadalopena evaṃ vuttaṃ. Cammena paṭibaddhahatthapādaṅgulantaro hi phaṇahatthako purisadosena upahato pabbajjampi na labhati. Tathāgatassa pana catasso hatthaṅguliyo pañcapi pādaṅguliyo ekappamāṇā honti. Tāsaṃ ekappamāṇa tāya yavalakkhaṇaṃ aññamaññaṃ paṭivijjhitvā tiṭṭhati. Athassa hatthapādā kusalena vaḍḍhakinā sammāyojitajālavāta pānasadisā honti, tasmā jālikappādahatthoti thometi. <span class="bld">Pāḷiyaṃ</span> pana ‘‘jālahatthapādo’’ti pāṭho. Ayamevattho.</p>\r\n<p class="bodytext"><span class="bld">Nātho</span>ti buddho kattuvasena. So hi nāthati vineyyānaṃ hitapaṭipattiṃ yācati, parasantānagataṃ vā kilesabyasaṃ upatāpeti, paramena cittissariyena samannā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',191,replace(replace('<p class="bodytext">gato<a name="M0.0191"></a>. Sabbasatte vā guṇehi īsati abhibhavati, veneyyānaṃ hitasukhaṃ vā āsīsatīti nāthoti vuccati. Vuttañhi <span class="bld">saddanītiyaṃ</span> ‘‘nāthadhātu yācane upatāpe issariye āsisane cāti catūsvatthesu pavattatī’’ti.</p>\r\n<p class="bodytext">Tassa pana padassa uṇhīsasīsotipadena tulyaliṅgattabhāvena sambandho. <span class="bld">Uṇhīsasīso</span>ti rañño baddhauṇhīsa paṭṭo viya virocamāno pakatimaṃsapiṇḍasaṅkhāto uṇhīsapaṭṭasīso. Nalāṭe nahiyati bandhiyatīti uṇhīso. Upubbanahadhātu bandhane, īso. Vaṇṇavikāro. Vuttañhi <span class="bld">jāliniyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Vaṇṇāgamo vaṇṇavipariyāyo,</p>\r\n<p class="gatha2">Dve cāpare vaṇṇavikāranāsā;</p>\r\n<p class="gatha3">Dhātūnamatthātisayena yogo,</p>\r\n<p class="gathalast">Taduccate pañcavidhaṃ nirutti’’nti.</p>\r\n<p class="bodytext">Tattha ca avijjamānassa āgamā vaṇṇāgamo nāma. Vijjamānaakkharānaṃ heṭṭhupariyāyavasena vaṇṇavipariyāyo nāma. Aññakkharassa aññakkharāpajjanatā vaṇṇavikāro nāma. Vijjamānakkharavināso vaṇṇavināso nāma. Tattha tattha yathāyogaṃ visesatthayogo dhātūnaṃ atthātisaya yogo nāmāti adhippāyo. Ettha pana nakārassa ṇakāraṃ katattā vaṇṇavikāroti daṭṭhabbo. Mālādiṃ siyati bandhiyatīti sīso, uttamaṅgo. Sidhātu bandhane īso. Rājūnaṃ uṇhīsapaṭṭena veṭhitasīso viya sīso yassāti uṇhīsasīso. Atha vā rājūnaṃ uṇhīsaṃ viya sabbattha parimaṇḍalasīso yassāti uṇhīsasīso, nātho.</p>\r\n<p class="bodytext">Vuttañhi <span class="bld">brahmāyusuttaṭṭhakathāyaṃ</span> ‘‘uṇhīsasīsoti idaṃ paripuṇṇanalāṭañceva paripuṇṇasīsañcāti dve atthavase paṭicca vuttaṃ. Tathāgatassa hi dakkhiṇakaṇṇacūḷikato paṭṭhāya </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',192,replace(replace('<p class="bodytext">maṃsapaṭaṃ <a name="M0.0192"></a> uṭṭhahitvā sakalaṃ nalāṭaṃ chādayamānaṃ pūrayamānaṃ gantvā vāmakaṇṇacūḷikāya patiṭṭhitaṃ. Rañño baddhauṇhīsapaṭṭo viya virocati. Pacchimabhavikabodhisattānaṃ kira imaṃ lakkhaṇaṃ viditvā rājūnaṃ uṇhīsapaṭṭaṃ akaṃsu. Ayaṃ tāva eko attho. Aññe pana janā aparipuṇṇasīsā honti. Keci kapisīsā, keci phalasīsā, keci aṭṭhisīsā, keci tumba sīsā, keci pabbhārasīsā. Tathāgatassa pana āraggena vaṭṭetvā ṭhapitaṃ viya suparipuṇṇaṃ udakapupphulasadisaṃ sīsaṃ hoti. Tattha purimanayena uṇhīsaveṭhitasīso viyāti uṇhīsasīso. Dutiyanayena uṇhīsaṃ viya sabbattha parimaṇḍala sīsoti uṇhīsasīso’’ti. <span class="bld">Taṭṭīkāyañca</span> ‘‘dve attha vase paṭicca vuttanti, yasmā buddhā cakkavattino ca paripuṇṇa nalāṭatāya paripuṇṇasīsabimbatāya uṇhīsasīsāti vuccanti. Tasmā te dve atthavase paṭicca uṇhīsasīsoti vutta’’nti vaṇṇeti. Tasmā uṇhīsasīsoti thometi.</p>\r\n<p class="bodytext"><span class="bld">Itiguṇasahita</span>nti iminā vuttappakārena nigrodhabimbotyādinā dvattiṃsamahāpurisalakkhaṇaguṇena sampannaṃ. Itievaṃ vuttappakāraṃ guṇaṃ itiguṇaṃ. Tena saha ito pattoti itiguṇasahito, mahesī. Aññe hi dvattiṃsa mahāpurisalakkhaṇehi sampannā na honti, cakkavattirājāno mahāpurisalakkhaṇehi sampannāpi ṭhapetvā kosohitavattha guyhalakkhaṇaṃ avasesehi sampannā honti. Tathāgatoyeva sabbamahāpurisalakkhaṇehi samannāgato. Tasmā bhagavantaṃyeva itiguṇasahitanti thomanaṃ arahatīti vuttaṃ hoti. <span class="bld">Taṃ mahesi</span>nti sīlakkhandhādike ca nibbānañca esati gavesatīti mahesī. Taṃ mahesiṃ ahaṃ tīhi dvārehi namāmi vandāmīti sambandho.</p>\r\n<p class="centered">Pañcavīsatimavandanagāthāvaṇṇanā samattā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',193,replace(replace('<p class="bodytext"><a name="para23"></a><span class="paranum">23</span>. Ayaṃ <a name="M0.0193"></a> pana imāsu tīsu gāthāsu <span class="bld">saṅkhepatthayojanā</span>. Buddho kattubhūto nigrodhabimbo nigrodhassa parimaṇḍalo viya parimaṇḍalasarīro mudukaracaraṇo saṇhahatthapādo, brahmaghoso brahmuno saro viya aṭṭhaṅgasampannasaravanto, eṇijaṅghā eṇimigassa jaṅghā viya suvaṭṭajāṇuko, kosacchādaṅgajāto usabhavāraṇā dīnaṃ aṅgajāto viya suvaṇṇapadumakaṇṇikasadisena vatthakosena paṭicchannena guyhaṅgajātena samannāgato, punarapi punaapi, sugato buddho suppatiṭṭhitapādo suvaṇṇa pādukatalamiva bhūmiyaṃ samaṃ suṭṭhu patiṭṭhitapādo, mūdo dātuṇṇalomo bhamukantare jāto saṇhasukhumo seto suddho uṇṇalomavanto, athamapi tadaññaṃapi, sugato buddho, brahmujuggattabhāvo brahmuno ujuuggata attabhāvo viya ujumeva attabhāvo, nīlakkhī atinīlanettena samannāgato, dīghapaṇhi āyatapaṇhi paripuṇṇapaṇhi vā, sukhumamalachavī saṇhamalavisuddhataco saṇhamala virahitataco vā, thomyarasaggasaggī thomitabbasattara saharaṇasahassanhāruuddhaggikā.</p>\r\n<p class="bodytext"><a name="para24"></a><span class="paranum">24</span>. Jino pañcamārajitavā buddho, cattālīsaggadanto samacattālīsauttamadanto, samakalapanajo kakacena chinditvā ṭhapito viya samadanto, antaraṃsappapīno dvinnaṃ aṃsānaṃ khandhānaṃ antaraṃ vemajjhe paripuṇṇapiṭṭhitalo, cakkenaṅkitapādo aṭṭhasatapādacakkalakkhaṇena lakkhita pādatalo, aviraḷadasano avivaradantena sampanno, māraji pañcamārajitavā buddho, ussaṅkhapādo pādassa upari ṭhitena saṅkhasadisattā saṅkhanāmikena gopphakena samannāgato, tiṭṭhanto ṭhitakova nonamanto anamamāno hutvā ubhayakaramudunā sukhumena hatthadvayena jaṇṇukāni jaṅghāni āmasanto parimajjituṃ sakkonto, vaṭṭakkhandho </p>','\r',char(13)),'\n',char(10)),'-23-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',194,replace(replace('<p class="bodytext">suvaṇṇāliṅgo <a name="M0.0194"></a> viya samavaṭṭitagaliko, gotaruṇa pakhumako taṃmuhuttajātarattavacchassa cakkhubhaṇḍo viya vippasannacakkhubhaṇḍo, vippasannacakkhubhaṇḍena samannāgato vā, sīhapubbaḍḍhakāyo sīhassa pubbaḍḍhakāyo viya paripuṇṇakāyo.</p>\r\n<p class="bodytext"><a name="para25"></a><span class="paranum">25</span>. Sugato buddho, sattapīno dvihatthapādaaṃsakūṭaekagalavasena sattasu ṭhānesu paripuṇṇamaṃsiko, dīghaṅguli samaāyatahatthapādaṅguli, atha tadaññaṃ loma kūpekalomo ekekalomakūpe ekekajātalomo, sampannodātadāṭho sampannasukkadāṭho, kanaka samataco suvaṇṇena sadisacammo nīlamuddhaggalomo nīlaṃ uddhaṃ aggā koṭi hutvā mukhasobhaṃ ullokayamāno viya ṭhitalomo, sambuddho tathāgato, thūlajivho mududīghaputhulajivhāya sampanno, atha tadaññaṃ, sīhahanuko sīhassa heṭṭhāhanu viya paripuṇṇahanuko, jālikappāda hattho kusalena vaḍḍhakinā vātapāne sammāyojita jālaṃ viya cammena appaṭibaddhahatthapādaṅgulantaro, nātho buddho, uṇhīsasīso rañño uṇhīsapaṭṭo viya virocamāno pakatimaṃsapiṇḍasaṅkhāto uṇhīsapaṭṭasīso, iti guṇasahitaṃ evaṃ iminā vuttappakārena dvattiṃsamahāpurisalakkhaṇaguṇena samannāgataṃ taṃ mahesiṃ buddhaṃ tīhi dvārehi ahaṃ namāmi vandāmīti.</p>\r\n<p class="bodytext">Tīsu gāthāsu saṅkhepatthayojanā samattā.</p>\r\n<p class="bodytext">Imissaṃ pana namakkārapāḷiyaṃ dvattiṃsamahāpurisalakkhaṇaṃ buddhavacanapāḷiyaṃ āgatanayānukkamena na vuttaṃ. Gāthābandhavasena chandānurakkhaṇatthameva pana nigrodhabimbotyādinā akkamena vuttaṃ. Tasmā pātheyyavagge <span class="bld">lakkhaṇasutte</span> vuttaṃ </p>','\r',char(13)),'\n',char(10)),'-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',195,replace(replace('<p class="bodytext">dvattiṃsamahāpurisalakkhaṇakkamaṃ <a name="M0.0195"></a> saṅkhepena dassayissāma bahussutapaññāvaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span></p>\r\n<p class="bodytext">. Buddho suppatiṭṭhitapādo 2. heṭṭhāpādatalesu cakkajāto 3. āyatapaṇhi 4. dīghaṅguli 5. mudutaluna hatthapādo 6. jālahatthapādo 7. ussaṅkhapādo 8. eṇijaṅgho 9. ṭhitakova anonamanto ubhohi pāṇitalehi jāṇukāni parimasati 10. kosohita vatthaguyho 11. suvaṇṇavaṇṇo kañcanasannibhattaco 12. sukhumacchavī 13. ekekalomakūpesu ekekalomo 14. nīlamuddhaggalomo 15. brahmujugatto 16. sattussado 17. sīhapubbaḍḍhakāyo 18. citantaraṃso 19. nigrodhaparimaṇḍalo 20. samavaṭṭakkhandho 21. rasaggasaggī 22. sīhahanuko 23. cattālīsadanto 24. samadanto 25. aviraḷadanto 26. susukkadāṭho 27. pahuta jivho 28. brahmasaro 29. abhinīlanetto 30. gopa khumo 31. uṇṇā bhamukantare jātā odātā mudutūla sannibhā 32. uṇhīsasīsoti dvattiṃsamahāpurisalakkhaṇassa kamo veditabbo. Imāni pana bhagavato dvattiṃsamahāpurisalakkhaṇāni pāramīpuññakammena yathārahaṃ kammasarikkhakaṃ labbhantīti veditabbāni. Tāni pana kammāni pātheyyavagge lakkhaṇasutte vuttāni, tāni panettha oloketvā gahetabbānīti.</p>\r\n<p class="centered">Tisso vandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Buddhobuddhotighoso atidulabhataro kā kathā buddhabhāvo,</p>\r\n<p class="gatha2">Loke tasmā vibhāvī vividhahitasukhaṃ sādhavo patthayantā;</p>\r\n<p class="gatha3">Iṭṭhaṃ atthaṃ vahantaṃ suranaramahitaṃ nibbhayaṃ dakkhiṇeyyaṃ,</p>\r\n<p class="gatha2">Lokānaṃ nandivaḍḍhaṃ dasabalamasamaṃ taṃ namassantu niccaṃ.</p>','\r',char(13)),'\n',char(10)),'-1-26-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',196,replace(replace('<p class="bodytext"><a name="para26"></a><span class="paranum">26</span>. Evaṃ <a name="M0.0196"></a> sugatantiādīhi pañcavīsativandanagāthāhi buddhassa paṇāmaṃ katvā idāni tassa vandane uyyojanaṃ karonto āha <span class="bld">buddho buddhotighoso</span> tyādigāthaṃ. Ayaṃ pana saddharāgāthāti daṭṭhabbo.</p>\r\n<p class="bodytext">Tattha pana loke buddho buddhoti ghoso atidulabhataro buddhabhāvo kā kathāti sambandho. Ettha ca <span class="bld">loke</span>ti manussaloke tiloke vā anantacakkavāḷa loke vā. <span class="bld">Buddho buddhotighoso</span>ti buddho buddho iti kittisaddo thutisaddo vā. Ghusanaṃ ghoso. Ghusiyati kathiyatīti vā ghoso, kittisaddo. Ghusadhātu sadde ṇa. Atha vā ghusiyati thaviyatīti ghoso, thutisaddo. Tena vuttaṃ <span class="bld">pārājikaṇḍaṭṭhakathāyaṃ</span> ‘‘kittisaddoti kittieva. Thutighoso vā’’ti. Buddho buddho iti pavatto ghoso thutighosoti buddhobuddhotighoso. <span class="bld">Atidulabhataro</span>ti ativiya dullabhatamo. Dullabhataroti hi vattabbe chandānurakkhaṇatthaṃ lakāralopena dulabhataroti vuttaṃ. Dukkhena labhiyatīti dullabho. Dupubbalabhadhātu labhane a. Atha vā labhassa asamiddhanti dullabho, dubbhijāneyya abyayībhāvasamāsoyaṃ. Dusaddo asamiddhattho, yathā dubbhikkhoti. Ati viya dullabhoti atidulabho. Tesaṃ visesena atidullabhoti atidulabhataro. Buddhā hi loke kadāci karahaci uppajjanti. Tasmā buddho buddhoti ghoso atidulabhataroti vuttaṃ. Vuttañhetaṃ <span class="bld">cūḷavaggakhandhake</span> ‘‘ghosopi kho eso gahapati dullabho lokasmiṃ, yadidaṃ buddho buddho’’ti. Yathā ca hi loke udumbarikapupphaṃ dullabhaṃ. Candamhi sasakaṃ hatthena gaṇhituṃ. Vāyasānaṃ khīraṃ, evaṃ buddhabhāvo atidullabhataroti vuttaṃ hoti. Ayaṃ pana hīnūpamāti daṭṭhabbā. Vuttañhetaṃ <span class="bld">apadānapāḷiyaṃ</span> –</p>','\r',char(13)),'\n',char(10)),'-26-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',197,replace(replace('<p class="gatha1">‘‘Udumbarikapupphaṃva<a name="M0.0197"></a>, candamhi sasakaṃ yathā;</p>\r\n<p class="gatha2">Vāyasānaṃ yathā khīraṃ, dullabhaṃ lokanāyaka’’nti ca;</p>\r\n<p class="gathalast">Dullabho buddhuppādo lokasmi’’nti ca;</p>\r\n<p class="bodytext">Kiñcāpi hi loke cakkavattirājūnaṃ uppajjamāno dullabho. Ekasmimpi pana kappe cakkavattirājāno anekā uppajjanti. Tathāgatā pana loke uppajjamānāpi ekasmiṃ kappe eka dviti catu pañca buddhāyeva uppajjanti, na taduttari. Yasmā hi asaṅkhyeyyepi kappe tathāgatasuñño hoti. Tasmā tathāgatasseva kadāci karahaci uppajjanato buddhobuddhotighoso atidulabhataro kā kathā buddhabhāvoti vutto. Vuttampi cetaṃ bhagavatā mahāparinibbānasamaye ‘‘devatā ānanda ujjhāyanti. Durāvatamhā āgatā tathāgataṃ dassanāya kadāci karahaci tathāgatā loke uppajjanti arahanto sammāsambuddhā. Ajja rattiyā paccūsasamaye tathāgatassa parinibbānaṃ bhavissati. Ayañca mahesakkho bhikkhu bhagavato purato ṭhito ovārento otarento vā na mayaṃ labhāma pacchime kāle tathāgataṃ dassanāyā’’ti.</p>\r\n<p class="bodytext"><span class="bld">Buddhabhāvo</span>ti buddhassa bhāvo. <span class="bld">Kā kathā</span>ti kiṃ vattabboevāti attho. Kasmā pana buddho atidullabhataroti ca, buddho kadāci karahaci uppajjantīti ca vuttaṃ. Nanu buddhā loke anekā uppajjantīti. Saccametaṃ. Buddho nāma pana anekāsaṅkhyeyyavārena vā anekāsaṅkhyeyyadivasamāsasaṃvaccharaṃ vā anekāsaṅkhyeyyakappaṃ vā dasapāramiyo ca pañcamahāpariccāge ca pūretvāva bhavituṃ sakkoti, na ekekavārādinā. Atha kho pana antamaso kappasatasahassādhikāni cattāri asaṅkhyeyyāni dasapāramiyo ca pañca mahāpariccāge ca pūretvāva </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',198,replace(replace('<p class="bodytext">buddho <a name="M0.0198"></a> bhavituṃ sakkoti. Tasmā buddho atidullabhataroti ca, buddhā kadāci karahaci uppajjantīti ca vuttanti.</p>\r\n<p class="bodytext">Evaṃ buddhassa atidullabhatarattā ca kadāci karahaci uppannattā caeva buddhobuddhoti kittayantassa mahapphalaṃ hoti. Vuttañhetaṃ <span class="bld">apadāne</span> –</p>\r\n<p class="gatha1">‘‘Buddhoti kittayantassa, kāye bhavati yā pīti;</p>\r\n<p class="gathalast">Varameva hi sā pīti, kasiṇenapi jambudīpassā’’ti.</p>\r\n<p class="bodytext"><span class="bld">Tasmā</span>ti yasmā buddho buddhoti ghoso atidullabhataro, buddhabhāvo kā kathā. Tasmā. <span class="bld">Vibhāvī</span>ti medhāvī. Te hi hitāhitaṃ kāraṇākāraṇaṃ vā visesena bhāveti pakāsetīti vibhāvā, paññā. Vipubba bhūdhātu sattāyaṃ ṇa. Sā etesaṃ atthīti vibhāvīti vuccanti, sādhavo labbhati. Taṃ pana sādhavoti pade visesanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vividhahitasukha</span>nti nānappakāraṃ lokiyalokuttara saṅkhātaṃ hitasukhaṃ. Tassa pana attho heṭṭhā vuttoyeva. Taṃ pana patthayantātipade kammaṃ. <span class="bld">Sādhavo</span>ti sappurisā. Te hi saparahitaṃ sādheti nibbattetīti sādhavoti vuccati. Sādhadhātu saṃsiddhiyaṃ avo. Tassa pana padassa namassantūti padena kattubhāvena sambandho. <span class="bld">Patthayantā</span>ti yācantā. Vividhahitasukhaṃ patthayanti yācantīti patthayantā, sādhavo. Patthadhātu yācane anto. Yakārāgamo. Taṃ pana sādhavotipade visesanaṃ. <span class="bld">Iṭṭha</span>nti kāmayamānaṃ. Icchitabbaṃ kamitabbanti iṭṭhaṃ. Isudhātu icchā kantīsu tapaccayo. Tassa raṭṭho, dhātvantalopo. <span class="bld">Attha</span>nti diṭṭhadhammikasamparāyikādiatthaṃ. Atthiyati patthiyatīti attho, vipākānisaṃsaphalaṃ. Atthadhātu yācanāyaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',199,replace(replace('<p class="bodytext">a<a name="M0.0199"></a>. Atha vā asati bhavati phalabhāvenāti attho. Asadhātu bhuvimhi tha. Taṃ pana padadvayaṃ vahantantipade kammaṃ. <span class="bld">Vahanta</span>nti dhārakaṃ. Vahati haratīti vahanto, buddho. Vahadhātu vahane pāpuṇane vā anto. Buddho hi sattānaṃ lokiyalokuttarahitasukhaṃ dhammadesanāya dhāretīti attho.</p>\r\n<p class="bodytext"><span class="bld">Suranaramahita</span>nti devehi ca manussehi ca catupaccayādīhi pūjitaṃ. Idaṃ panettha nibbacanaṃ. Issariyakīḷādīhi suranti dibbantīti surā, devā. Suradhātu dittiyaṃ. Atha vā suranti īsanti devissariyaṃ pāpuṇanti virocanti cāti surā, devā. Sundarā rā vācā etesanti vā surā, devā. Khandhasantānaṃ naranti nentīti narā, manussā. Naradhātu nayane a. Surā ca narā ca suranarā. Tehi mahitabbā pūjitabbāti suranaramahito, buddho. Mahadhātu pūjāyaṃ ta. Taṃ suranaramahitaṃ. <span class="bld">Nibbhaya</span>nti abhayaṃ. Bhiyate bhayaṃ. Natthi bhayaṃ etassāti nibbhayo, buddho. Taṃ nibbhayaṃ. Tathāgatassa aṭṭhasu parisāsu kenaci kāraṇena bhayaṃ hadayacalanaṃ kampanaṃ natthīti attho.</p>\r\n<p class="bodytext"><span class="bld">Dakkhiṇeyya</span>nti paralokaṃ saddahitvā dātabbauttamadānārahaṃ. Dātabbāti dakkhiṇā, deyyadhammavatthu. Dādhātu dānekkhiṇapaccayo. Atha vā dakkhanti vaḍḍhanti etāya sattā yathādhippetāhi sampattīhīti dakkhiṇā, paralokaṃ saddahitvā dātabbadānaṃ. Dakkhadhātu vaḍḍhaneyu, taṃ arahatīti dakkhiṇeyyo, buddho. Arahatitaddhite ṇeyyo. Atha vā dakkhiṇāya hitaṃ anurūpaṃ mahapphalakaraṇatāya visodhetīti dakkhiṇeyyo, buddho. Visodhetitaddhitāyaṃ. Buddhoyeva hi dakkhiṇāya mahapphalakaraṇavasena ca, visodhanavasena ca, devasakkabrahmehi ca, kosalarājādīhi ca, anāthapiṇḍikaseṭṭhādīhi ca paralokaṃ saddahitvā dātabbaṃ uttamadānaṃ visesena arahati, tasmā dakkhiṇeyyanti thometi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',200,replace(replace('<p class="bodytext"><span class="bld">Lokāna</span>nti <a name="M0.0200"></a> sattalokānaṃ. Tassa pana attho heṭṭhā vuttoyeva. <span class="bld">Nandivaḍḍha</span>nti pītiyā vaḍḍhāpentaṃ. Nandiyate nandi. Bhāvasādhanoyaṃ. Nandadhātu samiddhiyaṃ nandane vā i. Pītiyā adhivacanametaṃ. Sā vaḍḍheti buddhetīti nandivaḍḍho, buddho. Nandisaddūpapadavaḍḍhadhātu vaḍḍhane a. Buddho hi dvattiṃsamahāpurisalakkhaṇāsītānubyañjanalakkhaṇasampannena ca sabbaññutañāṇādiguṇasampannena dhammadesanāya ca loke sabbasattānaṃ pītiṃ vaḍḍhetīti attho. Sabbasattānañhi buddhadassane dhammasavane ca titto natthi, tasmā lokānaṃ nandivaḍḍhanti thometi. Vuttañhetaṃ <span class="bld">apadānaṭṭhakathāyaṃ</span> ‘‘pakatiyāeva hi buddhadassane puṇṇacandasamuddarājadassane atitto lokoti. Kathaṃ pana sabbasattānaṃ buddhadassane atitto hotī’’ti. Buddhassa pana bāttiṃsamahāpurisalakkhaṇādīhi sīlādiasādhāraṇaguṇehi ca adhika sampanno hutvā sabbadevamanussehi ativirocamānattā. Vuttañhetaṃ bhagavatā tāvatiṃse –</p>\r\n<p class="gatha1">‘‘Na koci devo vaṇṇena, sambuddhaṃ atirocati;</p>\r\n<p class="gathalast">Sabbe deve adhiggayha, sambuddhova virocatī’’ti.</p>\r\n<p class="bodytext"><span class="bld">Dasabala</span>nti aññehi paccekabuddhāriyasāvakehi asādhāraṇena dasakāyabalena ca dasañāṇabalena sampannaṃ. Tañhi kehici viruddhehi kāraṇehi balanti na kampentīti balanti vuccati. Atha vā balanti jīvitaṃ kappenti etenāti balaṃ, kāyabalaṃ. Baladhātu pāṇe a. Balanti bhāraṃ vahituṃ sakkuṇantīti vā balaṃ, balanti jānanti sabbadhammanti balaṃ, ñāṇa balaṃ. Dasabalañca dasabalañca dasabalāni ekasesanayena. Tāni yassa soti dasabalo, buddho. Idha pana sāmaññavasena vuttopi visesaṭṭhāniyattā bhagavato dasakāyabalāni ca, dasañāṇabalāni ca adhippetāni. Tesaṃ pana sarū patthaṃ nayanasubhagakāyaṅgantyādi gāthāvaṇṇanāyaṃ vuttameva.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',201,replace(replace('<p class="bodytext"><span class="bld">Asama</span>nti <a name="M0.0201"></a> sīlādiguṇehi kenaci asadisaṃ. Natthi samo sadiso puggalo etassāti asamo, buddho. Taṃ asamaṃ. <span class="bld">Ta</span>nti tādisaṃ guṇasampannaṃ buddhaṃ. <span class="bld">Nicca</span>nti satataṃ samitaṃ sabbakālaṃ nirantaranti attho. <span class="bld">Namassantū</span>ti abhivandantūti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Loke tiloke manussaloke vā, buddhobuddhotighoso buddho buddhoiti kittisaddo thutisaddo vā, atidulabhataro ativiya dullabhatamo, buddhabhāvo buddhassa bhāvo, kā kathā kiṃvattabbo eva, tasmā buddhobuddhoti ghosassa atidullabhatarattā, vibhāvī medhāvī vividhahitasukhaṃ nānappakāraṃ lokiyalokuttarasaṅkhātaṃ hitasukhaṃ patthayantā yācantā, sādhavo sappurisā iṭṭhaṃ kāmayamānaṃ, atthaṃ diṭṭhadhammikasamparāyikādiatthaṃ, vahantaṃ dhārakaṃ, suranaramahitaṃ devamanussehi pūjitaṃ, nibbhayaṃ abhayaṃ asantāsaṃ vā, dakkhiṇeyyaṃ paralokaṃ saddahitvā dātabbauttamadānārahaṃ, lokānaṃ sattalokānaṃ nandivaḍḍhaṃ pītiyā vaḍḍhāpanentaṃ. Dasabalaṃ asādhāraṇena dasakāyañāṇabalena sampannaṃ, asamaṃ sīlādiguṇehi kenaci asadisaṃ taṃ tādisaṃ guṇasampannaṃ buddhaṃ niccaṃ satataṃ samitaṃ sabbakālaṃ nirantaraṃ, namassantu abhivandantūti.</p>\r\n<p class="centered">Chabbīsatimavandanagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> Puññenetena sohaṃ nipuṇamati sato samparāye ca titto,</p>\r\n<p class="gatha2">Dakkho diṭṭhujjupañño avikalavīriyo bhogavā saṃvibhāgī;</p>','\r',char(13)),'\n',char(10)),'-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',202,replace(replace('<p class="gatha3">Tikkho <a name="M0.0202"></a> sūro dhitatto saparahitadharo dīghajīvī arogo,</p>\r\n<p class="gathalast">Dhañño vaṇṇo yasassī atibalavadharo kittimā khantupeto.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> Saddho dātaṅgupeto paramasiridharo diṭṭhadhamme viratto,</p>\r\n<p class="gatha2">Lajjī kalyāṇamitto abhiratakusalo pañcasīlādirakkho;</p>\r\n<p class="gatha3">Appiccho appakodho ativujuhadayo iddhimā appameyyo,</p>\r\n<p class="gathalast">Pāsaṃso pemavāco sujanaguṇavidū māmako so bhaveyyaṃ.</p>\r\n<p class="bodytext"><a name="para27"></a><span class="paranum">27</span>. Evaṃ buddho buddhoti ghosotyādigāthāya buddhavandane uyyojanaṃ katvā idāni sugatantyādīhi gāthāhi buddhaṃ vanditvā paṇidhiṃ karonto āha <span class="bld">puññenetena soha</span>ntyādi gāthādvayaṃ. Ayaṃ pana gāthādvayampi saddharāgāthāti daṭṭhabbā. Tattha paṭhamagāthāyaṃ soahaṃ etena puññena samparāye nipuṇamati ca sato ca…pe… khantupeto ca bhaveyyanti sambandho. <span class="bld">Soha</span>nti so ahaṃ. Buddhassa namakkāro soahanti attho. <span class="bld">Etena puññenā</span>ti iminā buddhavandanapuññakammena attano kārakaṃ pavati sodhetīti puññaṃ. Pudhātu sodhane ṇya. Puññanti ca idha pana buddhapaṇāmakriyābhinipphādikā kusalacetanā adhippetā. Sā ca vandaneyyavandakānaṃ khettajjhāsayasampadāhi diṭṭhadhamma vedanīyabhūtā yathāladdhasampattinimittakassa kammassa anubalappadānavasena tannibbattitavipākasantatiyā antarāyakarāni upapīḷakaupacchedakakammāni paṭibāhitvā tannidānānaṃ yathādhippetasiddhivibandhakānaṃ rogādiantarāyānamappavattiṃsādheti cāti attho ca veditabbo. <span class="bld">Samparāye cā</span>ti </p>','\r',char(13)),'\n',char(10)),'-28-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',203,replace(replace('<p class="bodytext">ettha <a name="M0.0203"></a> casaddo aṭṭhānayogo. So ca nipuṇamatityādīsu padesu yojetabbo. <span class="bld">Samparāye</span>ti paraloke. So hi samparāyitabbā upagamitabbā ettha sattehīti ca, kammakilesavasena sattehi samparāyitabbā pāpuṇitabbāti vā etthāti ca samparāyoti vuccati. Saṃparipubba ayadhātu hi gatiyaṃ ṇa. Atha vā kammavasena abhimukho sampareti gacchati pavattatīti samparāyoti vuccati. Saṃpubbaparadhātu gatiyaṃ ṇya. Iti kho ānanda kusalāni sīlāni anupubbena aggāya parentītiādīsu viya hi curādi gaṇavasena parasaddaṃ gatiyamicchanti saddavidū. Tasmiṃ samparāye.</p>\r\n<p class="bodytext"><span class="bld">Nipuṇamatī</span>ti saṇhasukhumapaññavā ca. Saṇhasukhumaṃ atthantaraṃ paṭivijjhanasamatthāya paññāya sampanno ca vā. Ettha ca nipuṇasaddo saṇhasukhumavācako anipphannapāṭipadiko. Hitāhitaṃ manati jānātīti mati, paññā. Manadhātu ñāṇeti. Nipuṇā saṇhā mati paññā yassa meti nipuṇamati. <span class="bld">Sato</span>ti satimā ca. Saratīti sati, saradhātu cintāyanti. Sā assa me atthīti sato. <span class="bld">Titto</span>ti tappano ca. Yathāladdhavatthunā santuṭṭho cāti attho. Tisanaṃ tappanaṃ titto. Tisadhātu tappane tapaccayo. Sakārassa toādeso. Yathālābha santoso, yathābalasantoso, yathāsāruppasanto so cāti tividhena santosena santuṭṭhoti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Dakkho</span>ti taṃ taṃ kicce cheko ca kusalo ca vā. So hi kusalakamme aññasmiñca kiccākicce adandhatāya sīghaṃ gacchatīti dakkho. Dakkhadhātu sīghatthe a. <span class="bld">Diṭṭhujjupañño</span>ti diṭṭhiujupaññoti padacchedo. Uju diṭṭhipaññāya samannāgatoti attho. Kesuci potthakesu diṭṭhijju paññoti pāṭho. Diṭṭhiujupaññoti padacchedo, soye </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',204,replace(replace('<p class="bodytext">vattho<a name="M0.0204"></a>. Ujūti akuṭilabhāvena bhavatīti uju, ajjavo. Ujudhātu ajjave kvi. Atha vā arati akuṭilabhāvena pavattatīti uju, avaṅko. Aradhātu gatiyaṃ jupaccayo. Arissa uādeso. Sammā passatīti diṭṭhi, mati. Disadhātu passane tapaccayo. Tassa raṭṭhādeso. Dhātvantalopo ca ī ca. Uju ajjavā diṭṭhi mati diṭṭhuju visesanaparanipātavasena. Pakārena jānāti aniccādivasena avabujjhatīti paññā. Papubbañādhātu avabodhane a. Ujudiṭṭhi ca ujupaññā ca yassa meti diṭṭhujjupañño, puggalo. <span class="bld">Avikalavīriyo</span>ti anūnavīriyo ca paripuṇṇavīriyo cāti attho. Vihīnaṃ karotīti vikalo. Vipubbakaradhātu karaṇe a. Rassalo. Vīrānaṃ kammaṃ vīriyaṃ. Avikalaṃ anūnaṃ vīriyaṃ assameti avikalavīriyo. Na sakkā kusīteneva navalokuttaradhammā laddhuṃ, āraddhavīriyeneva sakkā. Tasmā avikalavīriyoti patthetīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Bhogavā</span>ti bhuñjitabbasampattisukhena sampanno ca. Bhuñjitabboti bhogo, sampatti. Bhujadhātu pālanabyavaharaṇesu ṇa. Bhogo assa me atthīti bhogavā. <span class="bld">Saṃvibhāgī</span>ti saṃvibhajanasīlo ca saṃvibhajanasampanno ca vā, saṃvibhajati sīlenāti saṃvibhāgī. Saṃvibhajanasīloti vā saṃvibhāgī, saṃvibhajituṃ sīlamassāti vā saṃvibhāgī. Saṃvibhāge sīlamassāti saṃvibhāgī. Ayaṃ tassīlattho. Saṃvibhajati dhammenāti saṃvibhajanadhammoti vā. Saṃvibhajituṃ dhammo assāti vā, saṃvibhajane dhammo assāti vā saṃvibhāgī. Ayaṃ taddhammattho. Saṃvibhajanaṃ sādhukāro assa atthi. Sādhukārinā saṃvibhajatīti vā, saṃvibhajituṃ sādhukārīti vā, saṃvibhajane sādhukārīti vā saṃvibhāgī. Ayaṃ tassā dhukārattho. Saṃvipubba bhajadhātu vibhāge ṇa. Jassa go. Atha vā saṃvibhajanaṃ saṃvibhāgo. So me assa atthīti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',205,replace(replace('<p class="bodytext">saṃvibhāgī<a name="M0.0205"></a>. Assathyatthe ī. Paresaṃ yācakānaṃ annādidānavatthuṃ niccaṃ pariccajanakārīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Tikkho</span>ti tikkhappaññavā ca nisānapaññavā vā. Tijanaṃ nisanaṃ tikkhā, paññā. Tijadhātu nisāne kha. Jassa ko. Tikkhā paññā me assa atthīti tikkho. Assathyatthe ṇo. Tikkha paññāya sampanno cāti attho. Paññāya ca sabba kusalānaṃ dhammānaṃ seṭṭhattā mūlakāraṇattā ca tikkhoti patthanaṃ karoti. Vuttañhi <span class="bld">milindapañhe</span> –</p>\r\n<p class="gatha1">‘‘Paññā pasaṭṭhā lokasmiṃ, katā saddhammaṭṭhitiyā;</p>\r\n<p class="gathalast">Paññāya vimatiṃ hantvā, santiṃ pappoti paṇḍito’’ti.</p>\r\n<p class="bodytext"><span class="bld">Sūro</span>ti catūsu parisāsu aṭṭhasu parisāsu ca vīro ca. Sattivanto nibbhayoti attho. Sūrayate virayate sūro. Bhāvasādhanoyaṃ. Sūradhātu vikkantiyaṃ vīre vā a. Atha vā abhiruachambhianutrāsabhāvena sūrati gacchati pavattatīti sūro. Sūradhātu gatiyaṃ a. Sīho viya parisamajjhe sūravīrasampanno cāti attho. Sāsani kehi pana saddhammavidūhi sūrasaddassa nibbacanaṃ na dassitaṃ. Kevalaṃ pana sūroti visiṭṭhasūroti atthavivaraṇamattameva dassitaṃ. <span class="bld">Dhitatto</span>ti ettha dhita attoti padacchedo. Dhitoti samādhi. Attanti cittaṃ. Ekaggacittasampanno cāti attho. Dhāti tiṭṭhati ekārammaṇehi dhito, ekārammaṇe sampayuttadhamme dhāretīti vā dhito, samādhi. Dhādhātu dhāraṇe ta. Ārammaṇaṃ arati jānātīti attaṃ, cittaṃ. Aradhātu gatiyaṃ ta. Rassa to. Dhito samādhi attaṃ cittaṃ assa meti dhitatto.</p>\r\n<p class="bodytext"><span class="bld">Saparahitacaro</span>ti attahitaparahitacaro, attano ca parassa ca atthaṃ paṭipannako cāti attho. Attano ca parassa ca hitaṃ atthaṃ payojanaṃ carati paṭipajjatīti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',206,replace(replace('<p class="bodytext">saparahitacaro<a name="M0.0206"></a>. Saparahitasaddūpapada caradhātu caraṇe a. <span class="bld">Dīghajīvī</span>ti dīghāyuko ca. Dīghena jīvitindriyena sampanno vā. Dīgho jīvo jīvitaṃ assa meti dīghajīvi. Āyukappampi dīghajīvitasampanno cāti attho. <span class="bld">Arogo</span>ti rogaviraho ca ārogyaṃ vā. Rujjati bhañjati aṅgapaccaṅgānīti rogo, byādhi. Rujadhātu bhaṅge ṇa. Atha vā rujjati hiṃsatīti rogo. Rujadhātu hiṃsāyaṃ ṇa. Natthi rogo tassa meti arogo, channavutirogānaṃ abhāvo bhaveyyanti attho. <span class="bld">Dhañño</span>ti sirīpuññapaññālakkhaṇasampanno ca. Dhaññasaddo hi sirīpuññapaññālakkhaṇavācakoti daṭṭhabbo. Vuttañhi <span class="bld">vessantarajātake</span> ‘‘dhaññaṃ maṅgala sammata’’nti ca. <span class="bld">Temiyajātake</span> ‘‘dhaññapuññalakkhaṇasampannaṃ puttaṃ vijāyī’’ti ca. Idaṃ pana nibbacanaṃ. Dhananaṃ sampajjanaṃ dhaññaṃ, sirīpaññāpuññalakkhaṇānaṃ sampannanti attho. Dhanadhātu sampanne. Dhātūnaṃ anekatthattā ṇyapaccayo. Ṇakārānubandhe lope kate yapaccayena saddhiṃ nakārassa ñakāraṃ katvā dhaññanti rūpasiddhi veditabbā. Atha vā dhanaṃ sirīpaññā puññalakkhaṇasampadaṃ assa me atthīti dhañño. Assathyatthe ṇyo.</p>\r\n<p class="bodytext"><span class="bld">Vaṇṇo</span>ti vaṇṇasampanno ca. Ettha ca vaṇṇo duvidho hoti sarīravaṇṇaguṇavaṇṇavasena. Tattha sarīra vaṇṇo nāma rūpakāyassa pāsādikarūpasampannatā. Guṇa vaṇṇo nāma sīlasamādhipaññādiguṇena ca dhutaṅgādipaṭipadāguṇena ca sampannatā. Idaṃ pana nibbacanaṃ. Vaṇṇeti vikāramāpannānaṃ hadayaṅgatabhāvaṃ pakāsetīti vaṇṇo, sarīrarūpaṃ. Vaṇṇeti attano dabbaṃ pakāsetīti vaṇṇo, guṇo. Vaṇṇadhātu pakāsane a. Idha pana tadubhayampi yujjati. Vaṇṇaṃ assa me atthīti vaṇṇo. Assathyatthe a. <span class="bld">Yasassī</span>ti parivārasampanno kittivanto vā. Ettha </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',207,replace(replace('<p class="bodytext">ca <a name="M0.0207"></a> duvidho hoti yaso parivārakittiyasavasena. Tattha yajiyati pūjiyati etena parivārenāti yaso, parivāro. Yajadhātu pūjāyaṃ a. Jassa so. Atha vā yāti gacchati pāpuṇāti sāmikassa parivāranti yaso, parivāro. Yādhātu gatiyaṃ sopaccayo. Yajiyati pūjiyati etenāti yaso, kittisaddo. Yajadhātu devapūjāsaṅgahakaraṇadānadhammesu a. Jassa so. Atha vā yāti gacchati pāpuṇāti sabbattha byāpananti yaso, kittighoso thutisaddo vā. Yādhātu gatiyaṃ sopaccayo. Yaso ca yaso ca yaso ekasesanayena. So assa me atthīti yasassī, assathyatthe sīpaccayo.</p>\r\n<p class="bodytext"><span class="bld">Atibalavadharo</span>ti atibalavantadhārako ca ativiya kāyabalañāṇabalavantaṃ dhāretīti atibalavadharo. Ajātasatturājādayo viya pañcahatthi dasahatthibalassa dhāraṇasamattho ca, sāriputtattherādayo viya ñāṇabalassa dhāraṇasamattho ca bhaveyyanti attho. <span class="bld">Kittimā</span>ti kittighosavanto ca. Kittiyate kathiyate guṇanti kitti, thutighoso. Kittadhātu sadde i. Kittighoso assa me atthīti kittimā. Assathyatthe mantupaccayo. <span class="bld">Khantupeto</span>ti khantiyā upeto samupeto samannāgato ca. Khamanaṃ sahanaṃ khanti. Khamudhātu sahane tapaccayo. Tassanti tāya upeto samupeto samannāgatoti khantupeto. Khantivādatāpaso viya khantupeto ca bhaveyyanti attho. Khantiyā hi dukkarattā sabbakusala dhammānaṃ padhānattā seṭṭhatapattā ca khantupetoti patthanaṃ karoti. Vuttañhetaṃ bhagavatā ‘‘khantī paramaṃ tapo titikkhā’’ti ca. ‘‘Khantyā bhiyyo na vijjatī’’ti ca. ‘‘Khantībalaṃ balānīka’’nti ca.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',208,replace(replace('<p class="gatha1">‘‘Sīlasamādhipaññānaṃ<a name="M0.0208"></a>, khantippadhānakāraṇaṃ;</p>\r\n<p class="gathalast">Sabbepi kusalā dhammā, khantyāyattāva vaḍḍhare’’ti ca.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. Sohaṃ so ahaṃ, etena puññena iminā buddhavandanapuññakammena samparāye paraloke, nipuṇamati ca saṇhasukhumapaññavā ca, sato satimā ca titto tappano yathāladdhavatthunā santuṭṭho ca, dakkho taṃtaṃkicce cheko ca, diṭṭhujjupañño ujudiṭṭhi paññāya sampanno ca avīkalavīriyo anūnavīriyo ca paripuṇṇavīriyo vā, bhogavābhuñjitabbasampattisukhena sampanno ca saṃvibhāgī saṃvibhajanasīlo ca tikkho tikkhapaññavā ca, sūroti sattivanto nibbhayo ca, dhitatto samāhita citto ca ekaggacitto vā, saparahitacaro attahita parahitapaṭipannako ca dīghajīvī dīghāyuko ca arogo rogaviraho ca dhañño sirīpuññapaññālakkhaṇasampanno ca vaṇṇo vaṇṇasampanno ca yasassī parivārasampanno ca atibalavadharo atibalavantadhārako ca kittimā kitti ghosavanto ca khantupeto khantiyā upeto samupeto samannāgato ca bhaveyyanti.</p>\r\n<p class="centered">Sattavīsatimagāthāvaṇṇanā samattā.</p>\r\n<p class="bodytext"><a name="para28"></a><span class="paranum">28</span>. Evaṃ puññenetenatyādigāthāya samparāye paṇidhiṃ katvā idāni diṭṭhadhamme patthanaṃ kattukāmo <span class="bld">saddho dātaṅgupeto</span> tyādigāthamāha. Tattha <span class="bld">saddho</span>ti kammādi saddahanalakkhaṇāya saddhāya sampanno ca. Saddahanaṃ saddhā, saddahatīti vā saddhā. Saddahanti sampayuttadhammā etāyāti saddhā. Saṃpubbadhādhātu dhāraṇe ta. Sā pana kammādīsu saddahanalakkhaṇā. Okappanalakkhaṇā vā. Pasādarasā. Udakappasādakamaṇi viya. Pakkhandanarasā vā. Oghuttaraṇo </p>','\r',char(13)),'\n',char(10)),'-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',209,replace(replace('<p class="bodytext">viya<a name="M0.0209"></a>. Akālussiyapaccupaṭṭhānā adhimuttipaccupaṭṭhānā vā, saddheyyavatthupadaṭṭhānā sotāpattiyaṅgapadaṭṭhānā vā. Sā hatthavittabījāni viya daṭṭhabbā. Bhagavatā hi sā saddhā ‘‘saddhāhattho mahānāgo’’ti ca. ‘‘Saddhīdha vittaṃ purisassa seṭṭha’’nti ca. ‘‘Saddhābījaṃ tapo vuṭṭhī’’ti ca ‘‘saddhāya tarati ogha’’nti ca ‘‘saddhā bandhati pātheyyaṃ, saddhā dutiyā purisassa hotī’’ti ca, ‘‘saddhesiko bhikkhave ariyasāvako’’ti ca, ‘‘saddhādhana’’nti ca.</p>\r\n<p class="gatha1">‘‘Yesaṃ atthi saddhā buddhe, dhamme ca saṅgharatane;</p>\r\n<p class="gathalast">Te maṃ ativirocanti, āyunā yasasā siriyā’’ti.</p>\r\n<p class="bodytext">Ca. ‘‘Saddhaṃ upanissāya dānaṃ deti, sīlaṃ samādiyati, uposathakammaṃ karoti, jhānaṃ uppādeti, vipassanaṃ uppādeti, maggaṃ uppādeti, abhiññaṃ uppādeti, samāpattiṃ uppādetī’’ti ca. ‘‘Dullabhā saddhāsampattī’’ti ca vuttā. Tasmā saddho ca bhaveyyanti patthanaṃ karoti.</p>\r\n<p class="bodytext">Sā ca pana catubbidhā hoti āgamasaddhā, adhigamasaddhā, okappanasaddhā, pasādasaddhā cāti. Tattha sabbaññubodhisattānaṃ abhinīhārato āgatattā āgamasaddhā nāma. Ariyasāvakānaṃ saccapaṭivedhena adhigatattā adhigamasaddhā nāma. Buddho dhammo saṅghoti vutte acalabhāvena okappanaṃ okappanasaddhā nāma. Buddhādīsu pasāduppattimattā pasādasaddhā nāma. Idha pana paṇidhānaṭṭhānattā sabbasaddhā adhippetā. Saddhā assa me atthīti saddho. Assathyatthe ṇa.</p>\r\n<p class="bodytext"><span class="bld">Dātaṅgupeto</span>ti dātassa aṅgāya kāraṇabhūtāya dhanasampattiyā upeto samupeto samannāgato ca. Dātabbavatthu ca, tappaṭiggāhakapuggalo ca, saddhā cāti tīhi aṅgehi upeto samupeto samannāgato ca vā. Diyate dātaṃ. Bhāvasādhanoyaṃ. Dādhātu dāne a. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',210,replace(replace('<p class="bodytext">Aṅgīyati <a name="M0.0210"></a> ñāyati etenāti aṅgo, dhanasampatti. Dātassa aṅgo dātaṅgo, tena upeto samupeto samannāgatoti dātaṅgupeto, atha vā diyatīti dātaṃ, dānavatthu. Dātassa aṅgo dātaṅgo, tena upeto samupeto samannāgatoti dātaṅgupeto, dātabbavatthu ca, tappaṭiggāhaka puggalo ca, saddhā cāti tīhi aṅgehi sampajjāmīti patthanaṃ karotīti vuttaṃ hoti. Vuttañhi <span class="bld">itivuttaṭṭhakathāyaṃ</span> ‘‘saddhā deyyadhammo paṭiggāhako cāti imesaṃ tiṇṇaṃ samukhībhūtakāleyeva hi dānaṃ sambhavatī’’ti.</p>\r\n<p class="bodytext"><span class="bld">Paramasiridharo</span>ti uttamasirīdhārako ca. Uttamasiriṃ dhāraṇasamattho ca vā. Ettha ca sirīti paññāpuññasarīrasobhaggasampattīnametaṃ adhivacanaṃ. Katapuññehi seviyatīti sirī, katapuññe puggale sevati nissayatīti vā sirī, paññāpuññasarīrasobhaggasampatti labbhati. Sidhātu sevāyaṃ iro ī ca. Paramā uttamā sirī paramasirī, taṃ dhāretīti paramasirīdharo, paramasiriyā dhāraṇasamatthoti attho.</p>\r\n<p class="bodytext"><span class="bld">Diṭṭhadhamme</span>ti paccakkhabhūte attabhāve. Ettha ca diṭṭhadhammo vuccati paccakkhabhūto attabhāvo. So hi passitabboti diṭṭho, attabhāvo. Diṭṭho passitabbo dhammo sabhāvo assāti diṭṭhadhammoti vuccati. Tasmiṃ diṭṭhadhamme. <span class="bld">Viratto</span>ti kāmaguṇesu vigataratto ca, rajjanaṃ ratto. Ranjadhātu rāge. Vigato ratto assa meti viratto. <span class="bld">Lajjī</span>ti pāpahirivanto ca, pāpajigucchanalakkhaṇāya lajjāya sampanno ca vā. Atha vā lajjīti hirottappasampanno ca. Lajjīsaddena hi lakkhaṇāhāranayena ottappampi gahetabbaṃ. Kasmā, kusala lakkhaṇasabhāvena samānattā. Vuttañhetaṃ <span class="bld">nettipāḷiyaṃ</span> –</p>\r\n<p class="gatha1">‘‘Vuttamhi ekadhamme, ye dhammā ekalakkhaṇā keci;</p>\r\n<p class="gathalast">Vuttā bhavanti sabbo, sohāro lakkhaṇo nāmā’’ti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',211,replace(replace('<p class="bodytext">Lajjatīti <a name="M0.0211"></a> lajjā, hiri. Lajjadhātu hiriyaṃ. Lajjā assa me atthīti lajjā, kāyaduccaritādito hirottappasamannāgato cāti attho. <span class="bld">Kalyāṇamitto</span>ti sundaramitta sampanno ca. Kalyāṇasaddo hi sundaravācako anipphanna pāṭipadiko. Atha vā kalyaṃ hitaṃ aṇayati pāpayatīti kalyāṇo, sumitto. Kalyasaddūpapadaaṇadhātu gatiyaṃ ṇa. Mijjati sinehatīti mitto, mijjiyati sinehiyatīti vā mitto, midadhātu sinehe ta. Kalyāṇo mitto assa meti kalyāṇamitto. Hitadharasumittasampanno ca bhaveyyanti attho. Idha pana saddhāsīlasutacāgavīriyasati samādhipaññāguṇehi samannāgato kalyāṇamitto adhippeto. Taṃ pana sandhāya bhagavatā <span class="bld">siṅgālasutte</span> –</p>\r\n<p class="gatha1">‘‘Upakāro ca yo mitto, sukhadukkhe ca yo sakhā;</p>\r\n<p class="gatha2">Atthakkhāyī ca yo mitto, yo ca mittānukampako;</p>\r\n<p class="gatha3">Etepi mitte cattāro, iti viññāya paṇḍito; Sakkaccaṃ</p>\r\n<p class="gathalast">Payirupāseyya, mātā puttaṃva orasa’’nti vutto.</p>\r\n<p class="bodytext"><span class="bld">Visuddhimagge ca</span> –</p>\r\n<p class="bodytext">‘‘Piyo garu bhāvanīyo, vattā ca vacanakkhamo. Gambhīrañca kathaṃ kattā, no cāṭṭhāne niyojaye’’ti vutto. Iti evarūpassa ca kalyāṇamittassa dullabhattā kalyāṇamittena sampajjāmīti patthanaṃ karotīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Abhiratakusalo</span>ti dānādipuññakusaladhammesu abhiramaṇo ca. Kucchitaṃ apāyadvāraṃ salanti saṃvaranti pidahanti sādhavo etenāti kusalaṃ, dānādipuññadhammā. Kupubba saladhātu saṃvaraṇe a. Tesu abhiatirekaṃ ramatīti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',212,replace(replace('<p class="bodytext">abhiratakusalo<a name="M0.0212"></a>, kitantasamāsoyaṃ. Kusalābhiratoti vattabbe chandānurakkhaṇatthaṃ padavipariyāyena evaṃ vuttanti. Atha vā abhiatirekaṃ ramanaṃ abhirato, kusalesu abhirato yassa meti abhiratakusalo, niccaṃ kusalapuññassa karaṇe abhiratacittasampanno cāti attho.</p>\r\n<p class="bodytext"><span class="bld">Pañcasīlādirakkho</span>ti pañcasīlādīnaṃ rakkhituṃ samattho ca. Ettha ca ādisaddena gihīnaṃ aṭṭhaṅguposathasīlaṃ, sāmaṇerānaṃ dasaṅgasīlañca, bhikkhūnaṃ catupārisuddhisīlañca saṅgaṇhāti. Pañcasīlaṃ ādi yesaṃ teti pañcasīlādi, taṃ rakkhati rakkhituṃ samatthetīti pañcasīlādirakkho. Gahaṭṭhānaṃ niccasīlavasena pañcasīlaṃ sati ussāhasamatthe uposathaṅgavasena aṭṭhaṅgasīlaṃ dasaṅgasīlañca akhaṇḍaṃ acchiddaṃ asabalaṃ akammāsaṃ katvā rakkhituṃ sāmaṇerasīlavasena dasasīlañca, bhikkhusīlavasena catupārisuddhisīlañca rakkhituṃ samattho cāti attho. Bhagavatā hi ‘‘sīlavipattihetu vā bhikkhave sattā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjantī’’ti ca. ‘‘Sīlasampadāhetu vā bhikkhave sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjantī’’ti ca. ‘‘Sīlasampanno mahantaṃ bhogakkhandhaṃ adhigacchati kalyāṇo kittisaddo abbhuggacchati. Catuparisaṃ visārado upasaṅkamati amaṅkubhūto. Asammūḷho kālaṃ karoti, kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjatī’’ti ca vuttattā ca. <span class="bld">Aṭṭhakathāyañca</span> ‘‘sīlālaṅkārasadiso alaṅkāro natthi, sīlasadisaṃ pupphaṃ natthi, sīlagandhasadiso gandho natthī’’ti ca. ‘‘Sīla sampattiñhi nissāya tisso vijjā pāpuṇātī’’ti ca. ‘‘Ādimhi sīlaṃ visodheyyā’’ti ca. ‘‘Sattā sīlena visujjhantī’’ti ca. ‘‘Sīlaṃ sāsanassa ādī’’ti ca. ‘‘Sattasu </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',213,replace(replace('<p class="bodytext">visuddhīsu <a name="M0.0213"></a> sīlavisuddhi sarīraṃ nāmā’’ti ca. ‘‘Sīlaṃ nāmetaṃ avassayo patiṭṭhā ārammaṇaṃ tāṇaṃ leṇaṃ gati parāyana’’nti ca vuttattā pañcasīlādirakkhoti patthanaṃ karotīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Appiccho</span>ti aniccho ca, rūpādikāmaguṇesu anicchantoti attho. Abhāvatthohettha appasaddo. Appaḍaṃsamakasavā tātapātiādīsu viya icchanaṃ icchā, rūpādikāmaguṇaṃ icchatīti vā icchā, kāmataṇhā. Isudhātu icchākantīsu a. Dhātvantassaccho. Natthi icchā yassa meti appiccho, rūpādīsu kāmesu anicchanto cāti attho. Appiccho pana catubbidho hoti paccayaappiccho, pariyattiappiccho, dhutaṅgaappiccho, paṭivedhaappiccho cāti. Tattha catūsu paccayesu appiccho paccayaappiccho. Yo pana paccaye bahuṃ dente appaṃ gaṇhati, appaṃ dente appataraṃ vā gaṇhati na vā gaṇhati. Na anavasesagāhī hoti. Ayaṃ paccayaappiccho nāma. Yo pana tepiṭakopi samāno na bahussutabhāvaṃ jānāpetukāmo hoti, ayaṃ pariyattiappiccho nāma. Dhutaṅgasamādānassa pana dhutaṅgapariharaṇabhāvaṃ aññesaṃ jānituṃ na deti, ayaṃ dhutaṅgaappiccho nāma. Yo pana sotāpannādīsu aññataro hutvā sotāpannādibhāvaṃ jānāpetuṃ na icchati, ayaṃ adhigamaappiccho nāma. Imehi appicchaguṇehi sampanno ca ahaṃ bhaveyyanti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Appakodho</span>ti dussanalakkhaṇadosato virahito ca. So hi kujjhatīti kodhoti vuccati. Dosassetaṃ adhivacanaṃ. Natthi kodho assa meti appakodho.</p>\r\n<p class="gatha1">Kuddho atthaṃ na jānāti, kuddho dhammaṃ na passati;</p>\r\n<p class="gathalast">Andhatamaṃ tadā hoti, yaṃ kodho sahate naranti.</p>\r\n<p class="bodytext">Vuttadesanaṃ manasikaritvā appakodhoti patthanaṃ karotīti vuttaṃ hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',214,replace(replace('<p class="bodytext"><span class="bld">Ativujuhadayo</span>ti <a name="M0.0214"></a> atieva ujucittasamannāgato ca. Arati gacchati pavattati akuṭilabhāvenāti uju, ajjavaṃ. Aradhātu gatiyaṃ ju. Arissa u. Ārammaṇaṃ haratīti hadayaṃ, cittaṃ. Haradhātu haraṇe aya. Rassa do. Atha vā ārammaṇaṃ harantaṃ ayati gacchati pavattatīti hadayaṃ, cittaṃ. Harasaddūpapada ayadhātu gatiyaṃ a. Uju ca taṃ hadayañcāti ujuhadayaṃ. Atieva uju hadayaṃ assa meti ativujuhadayo. Gomuttavaṅko ca candalekhāvaṅko ca naṅgalakoṭi vaṅko cāti tīhi vaṅkehi vigato hutvā ativiya ujucittasamannāgato ca bhaveyyanti attho. Tattha ca yo pāpaṃ katvāva na karomīti bhāsati. So gantvā paccosakkanatāya gomuttavaṅko nāma. Yo pāpaṃ karontova bhāyāmahaṃ pāpassāti bhāsati. So yebhuyyena kuṭilatāya candalekhā vaṅko nāma. Yo pāpaṃ karontova ko pāpassa na bhāyeyyāti bhāsati, so nātikuṭilatāya naṅgalakoṭivaṅko nāma hoti. Yassa vā tīṇi kammadvārāni asuddhāni. So gomuttavaṅko nāma hoti. Yassa yāni kānici dve. So candalekhā vaṅko nāma. Yassa yaṃkiñci ekaṃ. So naṅgalakoṭivaṅko nāmāti veditabbo.</p>\r\n<p class="bodytext"><span class="bld">Iddhimā</span>ti iddhiyā samannāgato ca, iddhisaddassa vacanattho heṭṭhā vuttoyeva. Iddhi assa me atthīti iddhimā. Assathyatthe mantupaccayo. Mānussikāle manussiddhiyā devuppattikāle deviddhiyā samaṇuppattikāle samaṇiddhiyā ca sampuṇṇeyyanti attho.</p>\r\n<p class="bodytext"><span class="bld">Appameyyo</span>ti appametabbehi sīlādiguṇehi sampanno ca. Pametabboti pameyyo. Papubbamādhātu pamāṇe ṇya. Na pameyyo appameyyo, appameyyo sīlādiguṇo me assa atthīti appameyyo. Assathyatthe ṇa. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',215,replace(replace('<p class="bodytext">Appameyyehi <a name="M0.0215"></a> sīlādiguṇehi ca bhogasampattīhi ca sampajjāmīti patthanaṃ karotīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Pāsaṃso</span>ti sīlādiguṇehi buddhādisappurisānaṃ pasaṃsāraho ca, pasaṃsiyate thaviyate pasaṃso. Bhāva sādhanoyaṃ. Papubbasaṃsadhātu thutiyaṃ, taṃ arahatīti pāsaṃso. Arahatitaddhite ṇa. Buddhādisappurisehi pasaṃsi tabbehi dānasīlādiguṇehi sampanno ca bhaveyyanti vuttaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Pemavāco</span>ti atthabyañjanamadhuratāya buddhādisappurisehi pemitabbavācāya sampanno ca. Piyateti pemā, vācā. Pīdhātu tappanakantīsu imo. Vacitabbāti vācā. Vacadhātu viyattiyaṃ vācāyaṃ ṇa. Pemā vācā assa meti pema vāco. Buddhādisappurisehi pemiyā caturaṅgasamannāgata vācāya sampanno cāti attho. Sappurisānaṃ pana vācā caturaṅgasamannāgatā hoti. Subhāsitavācā ca dhammavācā piyavācā saccavācāti. Tathāhetaṃ vuttaṃ <span class="bld">sagāthāvagga saṃyutte</span> –</p>\r\n<p class="gatha1">Subhāsitaṃ uttamamāhu santo,</p>\r\n<p class="gatha2">Dhammaṃ bhaṇe nādhammaṃ taṃ dutiyaṃ;</p>\r\n<p class="gatha3">Piyaṃ bhaṇe nāppiyaṃ taṃ tatiyaṃ,</p>\r\n<p class="gathalast">Saccaṃ bhaṇe nālikaṃ taṃ catutthanti.</p>\r\n<p class="bodytext">Ayaṃ panettha yojanā. Santo sappurisā uttamaṃ subhāsitaṃ suṭṭhu bhāsitaṃ vācaṃ āhu, dhammaṃ bhaṇe bhaṇeyya adhammaṃ na bhaṇe na bhaṇeyya, dutiyaṃ taṃ vācaṃ āhu, piyaṃ bhaṇe bhaṇeyya, appiyaṃ na bhaṇe na bhaṇeyya, tatiyaṃ taṃ vācaṃ āhu, saccaṃ bhaṇe bhaṇeyya alikaṃ na bhaṇe na bhaṇeyya, catutthaṃ taṃ vācaṃ āhūti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',216,replace(replace('<p class="bodytext"><span class="bld">Sujanaguṇavidū</span>ti <a name="M0.0216"></a> buddhādisappurisānaṃ sīlādiguṇaṃ vijānako ca. Sundaro jano sujano. Guṇati pakāseti sīlādiguṇasamaṅgitaṃ puggalanti guṇaṃ, sīlādiguṇaṃ. Guṇadhātu pakāsane a. Sujanānaṃ buddhādisappurisānaṃ guṇo sujanaguṇo, taṃ vidati jānātīti sujanaguṇavidū. Sujana guṇasaddūpapadavidadhātu ñāṇe ū. Buddhādisappurisānaṃ sīlasamādhipaññādiguṇaṃ jānituṃ samattho ca bhaveyyanti attho.</p>\r\n<p class="bodytext"><span class="bld">Māmako</span>ti buddhādisajjanānaṃ māmako ca. Mamāyatīti māmako. Mamadhātu mānane peme vā ṇvu. Tassa ako. Buddhādiratanattayañca saddhammañca mānanto ca bhaveyyanti attho. <span class="bld">So</span>ti so ahaṃ. <span class="bld">Bhaveyya</span>nti bhavāmīti.</p>\r\n<p class="bodytext">Ayaṃ panettha <span class="bld">saṅkhepayojanā</span>. So ahaṃ diṭṭhadhamme paccakkhabhūte attabhāve, saddho saddhāya samannāgato ca, dātaṅgupeto dātassa aṅgāya kāraṇabhūtāya dhana sampattiyā upeto samupeto samannāgato ca, dātabba vatthu ca, tappaṭiggāhakapuggalo ca, saddhā cāti tīhi aṅgehi upeto samupeto samannāgato ca vā, paramasiridharo uttamasirīdhārako ca, uttamasiriṃ dhāraṇasamattho ca vā, viratto kāmaguṇesu vigataratto ca, lajjī pāpahirivanto ca, pāpajigucchanalakkhaṇāya lajjāya sampanno ca vā, hirottappasamannāgato ca vā, kalyāṇamitto sundaramitta sampanno ca, abhiratakusalo dānādipuññakusaladhammesu abhiramaṇo ca, pañcasīlādirakkho pañcasīlādīni rakkhituṃ samattho ca, appiccho aniccho ca, rūpādikāmaguṇesu anicchanto ca vā, appakodho dussanalakkhaṇadosato virahito ca, ativujuhadayo atieva ujucittasamannāgato ca, iddhiyā samannāgato ca, appameyyo appameyyehi sīlādiguṇehi sampanno ca, pāsaṃso sīlādiguṇehi buddhādisappurisānaṃ pasaṃsāraho ca, pemavāco </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',217,replace(replace('<p class="bodytext">atthabyañjanamadhuratāya <a name="M0.0217"></a> buddhādisappurisehi pemitabbavācāya sampanno ca, sujanaguṇavidū buddhādisappurisānaṃ sīlādiguṇaṃ vijānako ca, māmako buddhādisajjanānaṃ māmako ca, buddhādiratanattayañca saddhammañca mānanto ca vā, bhaveyyaṃ bhavāmīti.</p>\r\n<p class="centered">Aṭṭhavīsatimagāthāvaṇṇanā samattā.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> Itthaṃ asaṅkhaye nātha, guṇe lakkhaṇadīpite;</p>\r\n<p class="gathalast">Gāthāsu sūcakāsveka, gāthampi sarate budho.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> Caturāpāyamutto so, sādhakatthadvayassa ca;</p>\r\n<p class="gathalast">Hatūpaddavajālo ca, lābhī hitasukhassa ca.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> Adhipo naradevānaṃ, catudīpissaropi vā;</p>\r\n<p class="gathalast">Bhaveyya antime dehe, tamaññaṃ setachattakaṃ.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> Bhāvanāyānamāruyha, samamessati subbato;</p>\r\n<p class="gathalast">Imasmiṃ attabhāvepi, arogo dīghajīviko.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> Pūjito sabbalokehi, bhāvanābhiratīmano;</p>\r\n<p class="gathalast">Janappiyo manāpo ca, kā kathākhiladhāraṇe.</p>\r\n<p class="bodytext"><a name="para29"></a><span class="paranum">29</span>. Evaṃ puññenetenatyādi gāthādvayena paṇidhiṃ katvā idāni buddhavandanassa phalānisaṃsaṃ dassento <span class="bld">ittha</span>ntyādi gāthāpañcakaṃ āha. Taṃ pana mūlācariyehi na vuttaṃ, pacchā pana hitakāmehi paṇḍitehi pakkhittaṃ gāthāpañcakanti daṭṭhabbaṃ. Tā pana gāthāyo pathyāvattagāthāti daṭṭhabbā.</p>\r\n<p class="bodytext">Tāsu pana paṭhamagāthāyaṃ itthaṃ asaṅkhaye lakkhaṇadīpite nāthaguṇe sūcakāsu gāthāsu ekagāthampi yo budho sarateti sambandho. <span class="bld">Ittha</span>nti iminā sugatantiādinā vuttappakārena. <span class="bld">Asaṅkhaye</span>ti ettakā buddhaguṇāti gaṇetuṃ asamatthe. Saṅkhiyate saṅkhayā. Saṅkhiyati gaṇiyatīti vā </p>','\r',char(13)),'\n',char(10)),'-29-30-31-32-33-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',218,replace(replace('<p class="bodytext">saṅkhayā<a name="M0.0218"></a>. Sakhidhātu saṅkhyāyaṃ yapaccayo. Atha vā saṅkhituṃ na sakkontīti asaṅkhayā. Ettakā buddhaguṇā ettakā buddhaguṇāti niravasesato ekabuddhopi anekavassa satasahassakoṭiyāpi gaṇetuṃ asamatthova. Pageva pana aññeti attho. Vuttampi cetaṃ <span class="bld">apadāne</span> –</p>\r\n<p class="gatha1">‘‘Buddhopi buddhassa bhaṇeyya vaṇṇaṃ,</p>\r\n<p class="gatha2">Kappampi ce aññamabhāsamāno;</p>\r\n<p class="gatha3">Khīyetha kappo ciradīghamantare,</p>\r\n<p class="gathalast">Vaṇṇo na khīyetha tathāgatassā’’ti ca.</p>\r\n<p class="gatha1">‘‘Sakkā samudde udakaṃ, pametuṃ āḷhakena vā;</p>\r\n<p class="gathalast">Natveva tava sabbaññu, ñāṇaṃ sakkā pametave’’ti ca.</p>\r\n<p class="bodytext"><span class="bld">Mahāvaggasaṃyuttaṭṭhakathāyañca</span> ‘‘appamāṇā hi buddhā tesaṃ buddhāpi anavasesato vaṇṇaṃ vattuṃ asamatthā, pageva itarā pajā’’ti ca. <span class="bld">Lakkhaṇadīpite</span>ti upalakkhaṇa nayena pakāsitabbe. Ettha ca padhānanayo nidassana nayo ca labbhati. <span class="bld">Nāthaguṇe</span>ti buddhassa sīlasamādhi paññādiguṇe. Tassa pana padassa vacanattho heṭṭhā vuttoyeva. <span class="bld">Sūcakāsvekagāthampī</span>ti sūcakāsu ekagāthampīti padacchedo. <span class="bld">Sūcakāsū</span>ti pakāsakāsu. <span class="bld">Gāthāsū</span>ti sugatantyādinā vuttāsu chabbīsativandanagāthāsu. Niddhāraṇe cetaṃ bhummavacanaṃ. Kavīhi gāyati kathiyatīti gāthā. Pajjaviseso. Gedhātu sadde tha. Gedhātussa gādeso. Atha vā gaṃ vuccati ñāṇaṃ. Taṃ tiṭṭhati etthāti gāthā. Gaṃsaddūpapadathādhātu gatinivattiyaṃ. Gaṃ vuccati ñāṇaṃ, tena kavīhi bandhitvā thiyati thapiyatīti vā gāthā, tāsu gāthāsu. <span class="bld">Ekagāthampī</span>ti ekapajjampi. Ettha ca pisaddo garahattho. Tena anekagāthāya buddhaguṇasaraṇe kā kathāvāti sambhāveti. <span class="bld">Budho</span>ti yo paṇḍito. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',219,replace(replace('<p class="bodytext">So <a name="M0.0219"></a> hi atthānatthaṃ budhati jānātītibudhoti vuccati. <span class="bld">Sarate</span>ti sarati. Chandānurakkhaṇatthañhi parassapadativibhattiyā attanopadatevibhattādeso.</p>\r\n<p class="bodytext"><a name="para30"></a><span class="paranum">30</span>. <span class="bld">So</span>ti so budho bhaveyyāti sambandho. <span class="bld">Caturāpāyamutto</span>ti nirayatiracchānapetaasurakāyasaṅkhātehi catūhi apāyehi vimutto ca. Puññasammatā ayāyebhuyyena apagatoti apāyo. Ayato sukhato apagatoti vā apāyo. Abyayībhāvasamāsoyaṃ. Atha vā tisso sampattiyo ayanti gacchanti pavattanti etenāti ayo, puññaṃ. Ayadhātu gatiyaṃ a. Puññato ayati gacchati pavattatīti vā, puññakārīhi ayitabbo pavattetabboti vā ayo, sukhaṃ. Tato apagatoti apāyo. Caturo apāyo caturāpāyo, tehi mutto vimuttoti caturāpāyamutto. <span class="bld">Sādhakatthadvayassa cā</span>ti sādhako atthadvayassa cāti padacchedo. <span class="bld">Atthadvayassā</span>ti diṭṭhadhammikasamparāyikasaṅkhātassa atthadvayassa. Ettha casaddo aṭṭhānayogo. So pana sādhako tyādīsu padesu yutto. <span class="bld">Sādhako</span>ti nipphādako ca. So hi sādheti nipphādetīti sādhakoti vuccati.</p>\r\n<p class="bodytext"><span class="bld">Hatūpaddavajālo cā</span>ti hato upaddavasaṅkhātajālo ca. Hanayittha apanayitthāti hato. Hanadhātu hiṃsāyaṃ ta. Upagantvā davati hiṃsatīti upaddavo, jātibhayādi soḷasaupaddavo. Upapubbadudhātu hiṃsāyaṃ, upaddavā nāma pana soḷasavidhā honti jātibhayaṃ, jarābhayaṃ, byādhibhayaṃ, maraṇabhayaṃ, rājabhayaṃ, corabhayaṃ, aggibhayaṃ, udakabhayaṃ, ūmibhayaṃ, kumbhilabhayaṃ, āvaṭṭabhayaṃ, susukābhayaṃ, attānuvādabhayaṃ, parānuvādabhayaṃ, daṇḍabhayaṃ, duggatibhayañcāti. Jalati hiṃsati macche etenāti jālaṃ, macchajālaṃ. </p>','\r',char(13)),'\n',char(10)),'-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',220,replace(replace('<p class="bodytext">Jālaṃ <a name="M0.0220"></a> viyāti jālo, upaddavo. Nitatthena pana jalati hiṃsati satte etenāti jālo. Upaddavo. Jaladhātu hiṃsāyaṃ ṇa. Hato upaddavasaṅkhātajālo yena soti hatūpaddavajālo. Buddhapaṇāmacetanāya pahito upaddavasaṅkhātajālo bhaveyyāti attho. <span class="bld">Lābhī hitasukhassa cā</span>ti hitassa ca sukhassa ca lābhī ca. Labhanaṃ lābho, taṃ assa atthīti lābhī, hinoti vaḍḍhatīti hitaṃ, atthaṃ. Sukhayatīti sukhaṃ. Yassuppajjati, taṃ sukhitaṃ karotīti attho. Atha vā suṭṭhu dukkhaṃ khanatīti sukhaṃ. Supubba khanudhātu avadhāraṇe kvi. Hitañca sukhañca hitasukhaṃ, tassa lābhī bhaveyyāti attho.</p>\r\n<p class="bodytext"><a name="para31-32"></a><span class="paranum">31-32</span>. Naradevānaṃ adhipo ca catudīpissaropi vā bhaveyyāti sambandho. <span class="bld">Naradevāna</span>nti narā ca devā ca naradevā, tesaṃ. Kammatthe cetaṃ chaṭṭhīvacanaṃ. <span class="bld">Adhipo</span>ti issaro ca. So hi attādhīnaṃ pāti rakkhati dhammena vā adhiabhibhavitvā pāti rakkhatīti adhipoti vuccati. Adhipubbapādhātu rakkhane a, narānaṃ adhipo issaro manussarājā ca devānaṃ adhipo issaro devarājā ca bhaveyyāti attho. <span class="bld">Catudīpissaropi vā</span>ti ettha pisaddo sambhāvanatthe nipāto. Tena pageva ekadīpissararājabhāvo ca, pade sissararājabhāvo cāti sambhāveti. Vā saddo padapūraṇa mattoyeva. Catudīpissaroti pubbavideho, aparago yāno, jambudīpo, uttarakuru cāti catunnaṃ dīpānaṃ issaro cakkavattirājāpi bhaveyyāti attho. Tattha pubbavidehoti sineruno pure bhavaṃ pubbaṃ, vedena paññāya īhanti vāyamanti etthāti vedeho, vidasaddūpapada īhadhātu vāyāme ṇa. Soyeva videho. Imaṃ dīpamupādāya sineruno pubbadisabhāgattā pubbo ca so videho cāti pubbavideho, pubbadīpo.</p>','\r',char(13)),'\n',char(10)),'-31-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',221,replace(replace('<p class="bodytext">Aparagoyānoti <a name="M0.0221"></a> sineruno apare pacchime bhavo aparo. Gavena yanti gacchanti etthāti goyāno. Gosaddūpapada yādhātu gamane yu. Sineruno pacchima disabhāgattā aparo ca so goyāno cāti aparagoyāno, pacchimadīpo. Jambudīpoti jāyatīti jambū. Yojanasatubbedho rukkho. Janadhātu janane ū. Nassa mo. Bakārāgamo. Jalamajjhe dippatīti dīpo, dippanti jotanti ettha saddhammāti vā dīpo. Dīpadhātu dittiyaṃ a. Tassa pana dviāpoti padacchedaṃ katvā dvidhā āpo sandati etthāti dīpoti bahubbīhisamāsopi yujjati. Jambuyā lakkhito paññāto vā dīpo jambudīpo, dakkhiṇadīpo. Uttarakurūti sineruno uttare bhavā uttarā. Dhammatāsiddhassa pañcasīlassa ānubhāvena kaṃ sukhaṃ urumahantaṃ etthāti kuru. Bahubbīhisamāsoyaṃ. Kuṃ vuccati pāpaṃ. Taṃ rundhanti etthāti vā kuru. Kupubbarudhidhātu āvaraṇe kvi. Dhātvantassa lopo. Sineruno uttaradisabhāgattā uttaro ca so kuru cāti uttarakuru. Uttaradīpoti catunnaṃ dīpānaṃ issaro catudīpissaro, cakkavattirājā labbhati.</p>\r\n<p class="bodytext">Evaṃ lokiyahitaṃ dassetvā idāni lokuttarahitaṃ dassento āha <span class="bld">antime dehe…pe… subbato</span>ti antime dehe subbato bhāvanāyānaṃ āruyha aññaṃ taṃ setachattakaṃ dhārento samaṃ essatīti sambandho. Tattha pana atthakkamena vaṇṇayissāma. <span class="bld">Antime</span>ti carime pacchime vā. Ante pariyosāne bhavo antimo, pacchimasarīro. Tasmiṃ. <span class="bld">Dehe</span>ti sarīre. Dihati upaciyati vaḍḍhatīti deho, sarīro. Dihadhātu upacaye ṇa. Atha vā dihati upacayati etena puññāpuññanti deho, pacchimabhavabhūte bhabbakulaputtabhūte vā sarīreti attho. <span class="bld">Subbato</span>ti suṭṭhu paṭipannako </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',222,replace(replace('<p class="bodytext">hutvā<a name="M0.0222"></a>. Suṭṭhu vataṃ paṭipannaṃ assāti subbato. Vakārassabbo. <span class="bld">Bhāvanāyānamāruyhā</span>ti bhāvanāyānaṃ āruyhāti padacchedo. Vipassanābhāvanāsaṅkhātayānaṃ āruhitvāti attho. Bhāvetabbā vaḍḍhetabbāti bhāvanā. Tebhūmaka saṅkhārārammaṇaṃ. Atha vā tadārammaṇaṃ bhāveti vaḍḍhetīti bhāvanā, vipassanāpaññā. Bhūdhātu sattāyaṃ vaḍḍhane vā yu. Sā ca samathavipassanā bhāvanāvasena duvidhā hoti. Tattha idha pana aññaṃ setachattakanti saddantarasanniṭṭhānena vipassanābhāvanā adhippetā. Yanti pāpuṇanti etena nibbānanti yānaṃ, aniccādivipassanāñāṇaṃ. Yādhātu gatiyaṃ yu. Bhāvanāsaṅkhātaṃ yānaṃ bhāvanāyānaṃ, bhāvanā ca sā yānañcāti vā bhāvanāyānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Āruyhā</span>ti ārohitvā. Pakatiyānasadisaṃ anicca dukkhaanattayānaṃ āruhitvāti attho. Aniccādilakkhaṇena hi vinā maggaphalanibbānaṃ patto nāma natthi. Tasmā bhāvanāyānamāruyhāti vuttaṃ. <span class="bld">Añña</span>nti arahattaphalaṃ. Tañhi paṭhamamaggena ādiṭṭhamariyādaṃ anatikkamitvā catusaccadhammaṃ avabujjhatīti aññanti vuccati. Āpubbañādhātu avabodhane kvi. Āsaddo mariyādattho. <span class="bld">Setachattaka</span>nti odātātapattaṃ. Seviyatīti setaṃ, dhavalaṃ. Sidhātu sevāyaṃ ta. Kilesatapaṃ chādetīti chattaṃ. Chadadhātu saṃvaraṇe ta. Taṃyeva chattakaṃ. Setañca taṃ chattakañcāti setachattakaṃ. Arahattaphalaātapattaṃ labbhati. Aññaṃ arahattaphalasaṅkhātaṃ setachattakaṃ dhārento hutvāti sambandho. <span class="bld">Sama</span>nti nibbānaṃ. Tañhi sabbaṃ vaṭṭadukkhaṃ samati vūpasamati nirujjhati etthāti samanti vuccati. <span class="bld">Essatī</span>ti pāpuṇissatīti.</p>\r\n<p class="bodytext">Idāni diṭṭhadhammikatthaṃ dassento <span class="bld">imasmiṃ attabhāvepī</span>tiādimāha. Tattha imasmiṃ attabhāvepi arogo ca dīghāyuko ca sabbalokehi pūjito ca bhāvanābhi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',223,replace(replace('<p class="bodytext">ratimano <a name="M0.0223"></a> ca janappiyo ca manāpo ca bhaveyyāti yojanā. <span class="bld">Attabhāvepī</span>ti ettha pisaddo sampiṇḍanattho. Tena samparāyikatthaṃ sampiṇḍeti. Attāti bhavati etena abhidhānaṃ buddhi cāti attabhāvo. Khandhapañcakassetamadhivacanaṃ. <span class="bld">Arogo</span>ti sabbabyādhiviraho ca. So hi natthi rogo assāti arogoti vuccati. <span class="bld">Dīghajīviko</span>ti dīghāyuko ca. So hi dīghā jīvi āyu assāti dīghajīvikoti vuccati.</p>\r\n<p class="bodytext"><a name="para33"></a><span class="paranum">33</span>. <span class="bld">Sabbalokehī</span>ti sabbadevamanussehi. <span class="bld">Pūjito</span>ti pūjitabbo ca. <span class="bld">Bhāvanābhiratimano</span>ti samathavipassanā saṅkhātāya bhāvanāya abhiramaṇacitto ca. <span class="bld">Janappiyo</span>ti sabbajanehi piyāyitabbo ca. <span class="bld">Manāpo</span>ti sabbasattānaṃ cittavaḍḍhanako ca bhaveyyāti sambandho.</p>\r\n<p class="bodytext">Evaṃ chabbīsatiyā vandanagāthāsu ekagāthāmattampi buddhaguṇānussaraṇe phalānisaṃsaṃ dassetvā idāni sabbagāthāyo vacasā dhāretvā buddhavandane anekāni phalānisaṃsāni dassetuṃ <span class="bld">kā kathākhiladhāraṇe</span>ti āha. Tattha <span class="bld">kā kathākhiladhāraṇe</span>ti kā kathā akhiladhāraṇeti padacchedo. Sabbabuddhavandanagāthāya vacasā dhāraṇe kiṃ vattabbā yevāti attho.</p>\r\n<p class="bodytext">Ettha ca akhilasaddo sabbavācako anipphannapāṭipadiko. Atha vā khiyanaṃ khilaṃ. Khidhātu khaye lapaccayo. Khayaṃ lāti gaṇhātīti vā khilaṃ, appakaṃ. Khayasaddūpapadalādhātu gahaṇe a. Na khilaṃ akhilaṃ, sabbagāthāyo. Dhāriyate dhāraṇaṃ, akhilāya sabbāya gāthāya dhāraṇaṃ akhiladhāraṇaṃ, tasmiṃ. Chabbīsatiyā buddhavandanagāthāsu ekaṃ gāthaṃ vacasā dhārayitvā buddhavandane yathāvuttaphalānisaṃsāni labbhanti, pageva pana sabbabuddhavandanagāthāyo vacasā dhārayitvā dine dine </p>','\r',char(13)),'\n',char(10)),'-33-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',224,replace(replace('<p class="bodytext">buddhavandaneti <a name="M0.0224"></a> atthoti. Iminā pana gāthāpañcakena yathāvutta phalānisaṃsaṃ ñatvā saddahako sammādiṭṭhiko kulaputto sabbabuddhavandanagāthāyo vacasā dhārayitvā rattindivaṃ pubbaṇhakāle ca sāyanhakāle ca rattiyā paṭhamayāme ca pacchimayāme ca satataṃ samitaṃ nirantaraṃ buddhaṃ vandeyyāti uyyojanaṃ dassetīti.</p>\r\n<p class="centered">Pañcakagāthāvaṇṇanā samattā.</p>\r\n<p class="bodytext">Iti porāṇācariyehi vanditassa namakkārassa saddatthanayehi chandālaṅkāranayehi ca saṇhasukhumasududdasa gambhīrassa buddhassa duppaṭividhaguṇaparidīpakassa sappurisānaṃ cittapasannajanakassa sāṭṭhakathe piṭakattaye asaṃhira visāradañāṇacārinā sāsanasodhakena bhaddantareva tattheravarena racitā buddhaguṇappadīpikanāmikā vaṇṇanā.</p>\r\n<p class="subhead">Nigamana</p>\r\n<p class="bodytext">Idaṃ pana puraṃ nimmitena sirīpavaravijayānantayasapaṇḍita tribhavanādityādhipatimahādhammarājādhirājāti lañchitanāmikena piṭakattayapāḷiṃ silārūḷhaṃ kārayitvā pañcama saṅgītikaraṇena timahādhammarājena ratanāpuñjanti sammate rājadhānībhūte nānākulasaṃkiṇṇe puññadhaññavantānaṃ sādhūnañca sīlasamādhipaññavantānaṃ bhikkhūnañca nivāsabhūte buddhadhammasaṅgharatanākare candimasūriyā viya buddhasāsanujjotane <span class="bld">mantale</span> nagare iti parikittite mahārāme dese vasantena upāsaka guṇasampannena nāmikena upāsakena ca tassupaṭṭhākāya nāmikāya upāsikāya ca samakusalacchandaṃ katvā nibbānassa sacchikaraṇatthāya kārite manorame suvaṇṇena limpitattā suvaṇṇavatī </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',225,replace(replace('<p class="bodytext">nāmike <a name="M0.0225"></a> dvibhummike mahiṭṭhakavihāre aṭṭhahi disāhi āgatānaṃ sutesanabhikkhūnaṃ sāṭṭhakathaṃ piṭakattayaṃ dine dine satataṃ nirantaraṃ vācentena adhivasantena sāṭṭhakathe piṭakattaye asaṃhiravisāradañāṇacārinā mantale pure pariyattisāsanahitadhammācariyagaṇassa jeṭṭhukkaṭṭhabhūtena revatābhipaṇḍitadhajasāsanavaṃsamahādhammarājagurūti ca <span class="bld">agga mahāpaṇḍitā</span>ti ca laddhalañchitena mūladvādasasāsana karamahāgaṇassa jeṭṭhamahānāyakabhūtena <span class="bld">sāsanāpruñño</span> ācariyoti vissutena sāsanasodhaka mahānāyakattheravarena porāṇehi tattha tattha pāḷiyaṃ bhagavatā vuttehi buddhaguṇappakāsakehi buddhaguṇapadehi thomitvā vanditassa atthabyañjanena chandālaṅkāranayena ca pathavīsandhārakassa udakakkhandhassa viya gambhīrassa pabbatena paṭicchādetvā ṭhapitassa vatthussa viya atiduddasassa satadhā bhinnassa vālassa aggakoṭiyā viya atisaṇha sukhumassa puthujjanañāṇena buddhassa duppaṭividhaguṇaparidīpakassa namakkārassa līnatthappakāsanā buddhaguṇappadīpikanāmikā saṃvaṇṇanā tāsu tāsu aṭṭhakathāṭīkāsu āgataṃ saṃvaṇṇanānayaṃ nissāya paññāpāṭavatthāya sāsanavuddhiyā ca katā.</p>\r\n<p class="indent">Ettāvatā ca –</p>\r\n<p class="gatha1">Sampatte cakkavasse sā, dvisahasse catūsate;</p>\r\n<p class="gathalast">Navādhikāsitīyā ca, sakkarāje ca sampatte.</p>\r\n<p class="gatha1">Sattatisatasahassa, vasse phussamāsasseva;</p>\r\n<p class="gathalast">Kālapakkhasattame ca, buddhadine suniṭṭhitā; Sāmañña.</p>\r\n<p class="gatha1">Esā ca saṅkharontena, yaṃ puññaṃ pasutaṃ mayā;</p>\r\n<p class="gathalast">Aññampi tena puññena, patvā sambodhimuttamaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0601n',226,replace(replace('<p class="gatha1">Saṃsārato <a name="M0.0226"></a> bahū satte, tārayitvā taṇhakkhayaṃ;</p>\r\n<p class="gathalast">Nibbānaṃ sacchikāreyyaṃ, maggañāṇuttarena ca.</p>\r\n<p class="gatha1">Sadā rakkhantu rājāno, dhammeneva pajaṃ imaṃ;</p>\r\n<p class="gathalast">Sabbe sattā sukhī hontu, jotentu jinasāsanaṃ.</p>\r\n<p class="gatha1">Mrammaraṭṭhe manussā ca, sabbadā nirupaddavā;</p>\r\n<p class="gathalast">Niccaṃ kalyāṇasaṅkappā, pappontu amataṃ padanti.</p>\r\n<p class="centered">Buddhaguṇappadīpikanāmikā</p>\r\n<p class="centered">Namakkāraṭīkā samattā.</p>','\r',char(13)),'\n',char(10)),'');
