INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="book">Lakkhaṇāto</p>\r\n<p class="chapter">Buddhathomanāgāthā</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',19,replace(replace('<p class="gatha1">Bhavābhavesu <a name="M0.0019"></a> nekesu,</p>\r\n<p class="gatha2">Pūresi tiṃsapāramī;</p>\r\n<p class="gatha3">Cariyāyoca sambuddho,</p>\r\n<p class="gathalast">Pariccāgejahaṃsadā.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',20,replace(replace('<p class="gatha1">Vessantarattabhāvamhi<a name="M0.0020"></a>,</p>\r\n<p class="gatha2">Ṭhitomaddiṃ pisoadā;</p>\r\n<p class="gatha3">Nijaṃkaṇhājinaṃjāliṃ,</p>\r\n<p class="gathalast">Viyaṃvaṅkatapabbate.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Tatocavitvātusite,</p>\r\n<p class="gatha2">Pureuppajjipuññavā;</p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',21,replace(replace('<p class="gatha3">Rūpādidasaṭhānehi<a name="M0.0021"></a>,</p>\r\n<p class="gathalast">Aññedeveatikkami.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Cakkavāḷasahassehi,</p>\r\n<p class="gatha2">Dasahāgammasabbaso;</p>\r\n<p class="gatha3">Yācitodevabrahmūhi,</p>\r\n<p class="gathalast">Buddhabhāvamanonato.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',22,replace(replace('<p class="gatha1">Vilokanānisopañca<a name="M0.0022"></a>,</p>\r\n<p class="gatha2">Viloketvāsirindharo;</p>\r\n<p class="gatha3">Tatocavitvāmāyāya,</p>\r\n<p class="gathalast">Uppajjisākiyekule.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',23,replace(replace('<p class="gatha1">Visākhapuṇṇameseṭṭhe<a name="M0.0023"></a>,</p>\r\n<p class="gatha2">Vijāyilumbinīvane;</p>\r\n<p class="gatha3">Sīhanādaṃpanādento,</p>\r\n<p class="gathalast">Sattapādaṃgatotadā.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',24,replace(replace('<p class="gatha1">Virociṃsutadākāse<a name="M0.0024"></a>,</p>\r\n<p class="gatha2">Tārocandadivākarā;</p>\r\n<p class="gatha3">Ratanānicabhūmaṭṭhā,</p>\r\n<p class="gathalast">Nānāpupphācapupphare.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Sītāyanti </p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',25,replace(replace('<p class="gatha1">avīcaggī<a name="M0.0025"></a>,</p>\r\n<p class="gatha2">Khuppipāsācasohitā;</p>\r\n<p class="gatha3">Lokantaremahāpabhā,</p>\r\n<p class="gathalast">Andhāpassantitāvade.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',26,replace(replace('<p class="gatha1">Aghaṭṭitāvāditāni<a name="M0.0026"></a>,</p>\r\n<p class="gatha2">Tūriyānicaambare;</p>\r\n<p class="gatha3">Madhurenapanādiṃsu,</p>\r\n<p class="gathalast">Yasaṃsarenatāvade.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> Thutigītānigāyantā,</p>\r\n<p class="gatha2">Devācakamalāsanā;</p>','\r',char(13)),'\n',char(10)),'-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',27,replace(replace('<p class="gatha3">Sammodentāvaaṭṭhaṃsu<a name="M0.0027"></a>,</p>\r\n<p class="gathalast">Buddhaṅkurassasantike.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Kumārocārudhātīhi,</p>\r\n<p class="gatha2">Tositārājamandire;</p>\r\n<p class="gatha3">Vuddhosisusukhaṃkhemaṃ,</p>\r\n<p class="gathalast">Sakyakaññānamantare.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',28,replace(replace('<p class="gatha1">Soḷasavassikosakya<a name="M0.0028"></a>,</p>\r\n<p class="gatha2">Nandanopitarāsatā;</p>\r\n<p class="gatha3">Rājatteabhisittosi,</p>\r\n<p class="gathalast">Modesituvanattaye.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',29,replace(replace('<p class="gatha1">Nandanānaṃsovaṇṇa<a name="M0.0029"></a>,</p>\r\n<p class="gatha2">Vaṇṇasobhaṃyasodharaṃ;</p>\r\n<p class="gatha3">Sokāresimahesittaṃ,</p>\r\n<p class="gathalast">Lokanettarasāyanaṃ.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> Ekūnatiṃsamevasse,</p>\r\n<p class="gatha2">Visākhapuṇṇamevare;</p>\r\n<p class="gatha3">Sahasenāyauyyāsi,</p>\r\n<p class="gathalast">Yānenuyyānamaṇḍalaṃ.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',30,replace(replace('<p class="gatha1">Nimittecaturodisvā<a name="M0.0030"></a>,</p>\r\n<p class="gatha2">Puttassacavijāyanaṃ;</p>\r\n<p class="gatha3">Sutvāubbiggacittena,</p>\r\n<p class="gathalast">Naramitthapuretahiṃ.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-16-');
