INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',32,replace(replace('<p class="gatha1">Sundarītaṃpurakkhitvā<a name="M0.0032"></a>,</p>\r\n<p class="gatha2">Naccagītenatosayuṃ;</p>\r\n<p class="gatha3">Evaṃpisonaramitvā,</p>\r\n<p class="gathalast">Āhunikkhamamānaso.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> Pathamaṃrāhulaṃputtaṃ,</p>\r\n<p class="gatha2">Passitvānikkhamāmahaṃ;</p>\r\n<p class="gatha3">Iticintiyamāneso,</p>\r\n<p class="gatha2">Agājāyānivesanaṃ;</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-19-');
