INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',66,replace(replace('<p class="gatha1">neso<a name="M0.0066"></a>,</p>\r\n<p class="gatha2">Vijāyi mātukucchito;</p>\r\n<p class="gatha3">Sukkavāre lumbiniyaṃ,</p>\r\n<p class="gathalast">Vande taṃ lokapūjitaṃ.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Cakkavattisiriṃ hitvā,</p>\r\n<p class="gatha2">Mahāsinikkhamaṃ sudhī;</p>\r\n<p class="gatha3">Nikkhamī candavāramhi,</p>\r\n<p class="gathalast">Name taṃ munikuñjaraṃ.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',67,replace(replace('<p class="gatha1">Assatthabodhimūlamhi<a name="M0.0067"></a>,</p>\r\n<p class="gatha2">Pallaṅke aparājite;</p>\r\n<p class="gatha3">Patto sabbaññutaṃ nātho,</p>\r\n<p class="gathalast">Name taṃ budhavāsare.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',68,replace(replace('<p class="gatha1">Pañcavaggiyamukhānaṃ<a name="M0.0068"></a>,</p>\r\n<p class="gatha2">Devānaṃ migadāvane;</p>\r\n<p class="gatha3">Sorivāre dhammacakkaṃ,</p>\r\n<p class="gathalast">Vattesi taṃ jinaṃ name.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Sabbasaṅkhatadhammānaṃ,</p>\r\n<p class="gatha2">Pakāsiyaaniccataṃ;</p>\r\n<p class="gatha3">Nibbutaṃaṅgavāramhi,</p>\r\n<p class="gathalast">Namādhiamatandadaṃ.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Sūriyavāre </p>','\r',char(13)),'\n',char(10)),'-6-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',69,replace(replace('<p class="gatha1">soṇṇāya<a name="M0.0069"></a>,</p>\r\n<p class="gatha2">Doṇiyā jinaviggaho;</p>\r\n<p class="gatha3">Agginujjalito tassa,</p>\r\n<p class="gathalast">Name dhātusarīrakaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',71,replace(replace('<p class="chapter">Uṇṇālomikanātha vandanā</p>\r\n<p class="gatha1">Uṇṇālodhikanāthassa<a name="M0.0071"></a>,</p>\r\n<p class="gatha2">Uṇṇāya bhamukantare;</p>\r\n<p class="gatha3">Vajirāviya sobhanti,</p>\r\n<p class="gathalast">Nikkhanti yo supaṇḍarā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0603n',72,replace(replace('<p class="gatha1">Vajiragghanakāyassa<a name="M0.0072"></a>,</p>\r\n<p class="gatha2">Nāthassa dehato subhā;</p>\r\n<p class="gatha3">Vajireyyā niccharanti;</p>\r\n<p class="gathalast">Vijjūva gagaṇantare.</p>\r\n<p class="gatha1">Rinindamandahāsena,</p>\r\n<p class="gatha2">Dāṭhāya niccharā pabhā;</p>\r\n<p class="gatha3">Vipphūrisā disāsabbā,</p>\r\n<p class="gathalast">Vajirāyanti paṇḍarā.</p>','\r',char(13)),'\n',char(10)),'');
