INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="chapter">Suttavandanā</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',13,replace(replace('<p class="gatha1">Dhammattha <a name="M0.0013"></a> desanā vedha,</p>\r\n<p class="gatha2">Catugambhīra duddasaṃ;</p>\r\n<p class="gatha3">Dhammaṃ lokassa desentaṃ,</p>\r\n<p class="gathalast">Dhammarājaṃ namāmahaṃ;</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',14,replace(replace('<p class="gatha1">Buddho <a name="M0.0014"></a> paccekabuddho ca,</p>\r\n<p class="gatha2">Arahā cakkavattīti;</p>\r\n<p class="gatha3">Thūpāthūpārahaṃ vande,</p>\r\n<p class="gathalast">Catuthūpārahesu taṃ;</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',15,replace(replace('<p class="gatha1">Petaseyyo <a name="M0.0015"></a> kāmabhogī,</p>\r\n<p class="gatha2">Sīhaseyyo tathāgato;</p>\r\n<p class="gatha3">Sayantaṃ catuseyyesu,</p>\r\n<p class="gathalast">Catutthena name jinaṃ;</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Aṇḍajā jalābujā ca;</p>\r\n<p class="gatha2">Saṃsedajo papātikā;</p>\r\n<p class="gatha3">Catuyoni pariccheda,</p>\r\n<p class="gathalast">Ñāṇavantaṃ name jinaṃ;</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',16,replace(replace('<p class="gatha1">Sutabuddhonubuddho <a name="M0.0016"></a> ca,</p>\r\n<p class="gatha2">Pacceko jinabuddhoti;</p>\r\n<p class="gatha3">Catutthaṃ catubuddhesu,</p>\r\n<p class="gathalast">Vandāmi lokanāyakaṃ;</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',17,replace(replace('<p class="gatha1">Rāhuggaṃ <a name="M0.0017"></a> attabhāgīnaṃ,</p>\r\n<p class="gatha2">Mandhātā kāmabhogīnaṃ;</p>\r\n<p class="gatha3">Māro adhipateyyānaṃ,</p>\r\n<p class="gatha2">Buddho loke sadevake;</p>\r\n<p class="gatha3">Catuagga paññattīnaṃ,</p>\r\n<p class="gathalast">Aggapattaṃ name jinaṃ.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',18,replace(replace('<p class="gatha1">Dānaṃ <a name="M0.0018"></a> piyakathā attha,</p>\r\n<p class="gatha2">Cariyā sadisattatā;</p>\r\n<p class="gatha3">Catusaṅgaha vatthūhi,</p>\r\n<p class="gatha2">Asaṅkhiyesu jātisu;</p>\r\n<p class="gatha3">Lokassa saṅgahetāraṃ,</p>\r\n<p class="gathalast">Vandāmi lokanāyakaṃ;</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Lobho doso ca mohoti,</p>\r\n<p class="gatha2">Anto paccatthike tayo;</p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',19,replace(replace('<p class="gatha3">Dujjaye <a name="M0.0019"></a> sabbaverīhi,</p>\r\n<p class="gathalast">Sañjitaṃpi sudhiṃ name;</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Jāti puñña mahattāni,</p>\r\n<p class="gatha2">Guṇamahattamuttamaṃ;</p>\r\n<p class="gatha3">Timahatthehi sampannaṃ,</p>\r\n<p class="gathalast">Vandāmi lokanāyakaṃ;</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',20,replace(replace('<p class="gatha1">Hetu <a name="M0.0020"></a> phala sampadāyo,</p>\r\n<p class="gatha2">Sattupakārasampadā;</p>\r\n<p class="gatha3">Tisampadāhi sampannaṃ,</p>\r\n<p class="gathalast">Vandāmi lokanāyakaṃ.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Ñāṇappahānānubhāva,</p>\r\n<p class="gatha2">Surūpakārasampadā;</p>\r\n<p class="gatha3">Catuphala sampadāhi,</p>\r\n<p class="gathalast">Taṃ sampannaṃ name jinaṃ.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',21,replace(replace('<p class="gatha1">Āsayo <a name="M0.0021"></a> ca payogoti,</p>\r\n<p class="gatha2">Sattupakāra sampadā;</p>\r\n<p class="gatha3">Duvidhā yassa sampannā,</p>\r\n<p class="gathalast">Sammā maṃ pātu so jino.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> Mahosadhe vessantare,</p>\r\n<p class="gatha2">Pacchimabhavike tathā;</p>\r\n<p class="gatha3">Tijātīsu name vācaṃ,</p>\r\n<p class="gathalast">Bhāsentaṃ jāyanakkhaṇe.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',22,replace(replace('<p class="gatha1">Nemimhi <a name="M0.0022"></a> sādhine ceva,</p>\r\n<p class="gatha2">Mandhātari ca guttile;</p>\r\n<p class="gatha3">Catūsu manujattena,</p>\r\n<p class="gathalast">Devalokaṃ gataṃ name.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Saṅkhāro rūpavikāro,</p>\r\n<p class="gatha2">Lakkhaṇaṃ vānanissaṭaṃ;</p>','\r',char(13)),'\n',char(10)),'-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',23,replace(replace('<p class="gatha3">Nibbānaṃ <a name="M0.0023"></a> paññatticeti,</p>\r\n<p class="gathalast">Pañca ñeyyantaguṃ name.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> Putta dāra pariccāgā,</p>\r\n<p class="gatha2">Rajjaṅgaṃ jīva cāgīti;</p>\r\n<p class="gatha3">Pañca mahāpariccāge,</p>\r\n<p class="gathalast">Karaṃ sudukkare name.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-16-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',24,replace(replace('<p class="gatha1">Rūpañca <a name="M0.0024"></a> vedanā saññā,</p>\r\n<p class="gatha2">Saṅkhārā viññāṇantime;</p>\r\n<p class="gatha3">Pañcakkhandhe parijānaṃ,</p>\r\n<p class="gathalast">Tipariññāhi taṃ name.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> Manussa deva gatiyo,</p>\r\n<p class="gatha2">Peto ca nirayo tathā;</p>\r\n<p class="gatha3">Tiracchānoti pañcetā,</p>\r\n<p class="gathalast">Chindantaṃ gatiyo name;</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',25,replace(replace('<p class="gatha1">Devaputto <a name="M0.0025"></a> kileso ca,</p>\r\n<p class="gatha2">Abhisaṅkhāra mārako;</p>\r\n<p class="gatha3">Khandho maccūti pañcete,</p>\r\n<p class="gathalast">Māre vijitavaṃ name.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> Purebhattaṃ pacchābhattaṃ,</p>\r\n<p class="gatha2">Purimañceva majjhimaṃ;</p>\r\n<p class="gatha3">Pacchimanti sadā pañca,</p>\r\n<p class="gathalast">Buddhakiccaṃ karaṃ name.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',26,replace(replace('<p class="gatha1">Rūpā <a name="M0.0026"></a> saddā gandhā rasā,</p>\r\n<p class="gatha2">Phoṭṭhabbā ca manoramā;</p>\r\n<p class="gatha3">Pañca kāmaguṇe hitvā,</p>\r\n<p class="gathalast">Kheḷaṃva niggataṃ name.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',27,replace(replace('<p class="gatha1">Rāgasallaṃ <a name="M0.0027"></a> dosasallaṃ,</p>\r\n<p class="gatha2">Mohā māno ca diṭṭhīti;</p>\r\n<p class="gatha3">Vande vajjātivajjantaṃ,</p>\r\n<p class="gathalast">Pañcasallapanūdanaṃ.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> Dīgho majjhima saṃyuttā,</p>\r\n<p class="gatha2">Aṅguttaro ca khuddako;</p>\r\n<p class="gatha3">Iti pañca nikāyehi,</p>\r\n<p class="gathalast">Suvinentaṃ pajaṃ name.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-23-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',28,replace(replace('<p class="gatha1">Issariyaṃ <a name="M0.0028"></a> yaso dhammo,</p>\r\n<p class="gatha2">Kāmo sirī payattīti;</p>\r\n<p class="gatha3">Bhagyehi chahi sampannaṃ,</p>\r\n<p class="gathalast">Vandāmi lokanāyakaṃ.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',29,replace(replace('<p class="gatha1">Piyāpiyaṃ <a name="M0.0029"></a> bhūtābhūtaṃ,</p>\r\n<p class="gatha2">Atthānatthaṃ yathārahaṃ;</p>\r\n<p class="gatha3">Missā chasu suvācaṃva,</p>\r\n<p class="gathalast">Bhāsantaṃ atthakaṃ name.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Alaso ca pamādo ca,</p>\r\n<p class="gatha2">Anuṭṭhānaṃ asaṃyamo;</p>\r\n<p class="gatha3">Niddā tantīti chiddehi,</p>\r\n<p class="gathalast">Chahi muttaṃ sadā name.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-26-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',30,replace(replace('<p class="gatha1">Mahākaruṇāsamāpatti<a name="M0.0030"></a>,</p>\r\n<p class="gatha2">Yamakappāṭihāriyaṃ;</p>\r\n<p class="gatha3">Āsayānusaye ñāṇaṃ,</p>\r\n<p class="gatha2">Indriyāna paropare;</p>\r\n<p class="gatha3">Sabbaññutā nāvaraṇaṃ,</p>\r\n<p class="gathalast">Chā sādhāraṇikaṃ name.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',31,replace(replace('<p class="gatha1">Kāmacchando <a name="M0.0031"></a> ca byāpādo,</p>\r\n<p class="gatha2">Thinamiddhañca saṃsayo;</p>\r\n<p class="gatha3">Avijjuddhacca kukkuccaṃ,</p>\r\n<p class="gathalast">Chavinīvaraṇaṃ name.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> Iddhividhaṃ dibbasotaṃ,</p>\r\n<p class="gatha2">Paracitta vijānanā;</p>','\r',char(13)),'\n',char(10)),'-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',32,replace(replace('<p class="gatha3">Pubbenivāsānussati<a name="M0.0032"></a>,</p>\r\n<p class="gatha2">Dibbacakkhāsavakkhayo;</p>\r\n<p class="gatha3">Chaḷabhiññāhi sampannaṃ,</p>\r\n<p class="gathalast">Vandāmi purisuttamaṃ.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> Nīla pītā ca odātā,</p>\r\n<p class="gatha2">Mañjiṭṭhā ca pabhassarā;</p>\r\n<p class="gatha3">Lohitāti cha raṃsīhī,</p>\r\n<p class="gathalast">Vijjotantaṃ sadā name.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',33,replace(replace('<p class="gatha1">Assādādīnavo <a name="M0.0033"></a> ceva,</p>\r\n<p class="gatha2">Nissaraṇaphalaṃ tathā;</p>\r\n<p class="gatha3">Upāyāṇattīti chadhā,</p>\r\n<p class="gathalast">Saddhammadesakaṃ name.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',34,replace(replace('<p class="gatha1">Rāgo <a name="M0.0034"></a> doso ca moho ca,</p>\r\n<p class="gatha2">Vitakkacariyā tathā;</p>\r\n<p class="gatha3">Saddhā buddhīti bhājentaṃ,</p>\r\n<p class="gathalast">Tā chaḷecariyā name.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> Rajataṃ kanakaṃ muttā,</p>\r\n<p class="gatha2">Maṇi veḷuriyāni ca;</p>\r\n<p class="gatha3">Vajirañca pavāḷanti,</p>\r\n<p class="gathalast">Seṭṭhi sattadhano viya.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> Saddhāsīlaṃ sutaṃcāgo,</p>\r\n<p class="gatha2">Hirī ottappiyaṃ dhanaṃ,</p>\r\n<p class="gatha3">Paññādhananti sambuddhaṃ,</p>\r\n<p class="gathalast">Vande sattamahādhanaṃ.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-34-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',35,replace(replace('<p class="gatha1">Cakkaṃ <a name="M0.0035"></a> hatthi asso maṇi,</p>\r\n<p class="gatha2">Gahapatitthiyo tathā;</p>\r\n<p class="gatha3">Supariṇāyakocāti,</p>\r\n<p class="gathalast">Sattaratano cakkavattīva.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> Sati dhammavicayo ca,</p>\r\n<p class="gatha2">Tatheva vīriyaṃ pīti;</p>','\r',char(13)),'\n',char(10)),'-36-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',36,replace(replace('<p class="gatha3">Passaddhi <a name="M0.0036"></a> samādhupekkhā,</p>\r\n<p class="gatha2">Saddhamma cakkavattikaṃ;</p>\r\n<p class="gatha3">Satta bojjhaṅgaratanaṃ,</p>\r\n<p class="gathalast">Namāmi purisuttamaṃ.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',37,replace(replace('<p class="gatha1">Yugandharo <a name="M0.0037"></a> īsadharo,</p>\r\n<p class="gatha2">Karavīko sudassano;</p>\r\n<p class="gatha3">Nemindharo vinatako,</p>\r\n<p class="gatha2">Assakaṇṇo mahānage;</p>\r\n<p class="gatha3">Sattete hattharūpaṃva,</p>\r\n<p class="gathalast">Sampassantaṃ jinaṃ name.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> Kaṇṇamuṇḍo anotatto,</p>\r\n<p class="gatha2">Kuṇālo rathakārako;</p>\r\n<p class="gatha3">Sīhappapāta chaddantā,</p>\r\n<p class="gatha2">Mandākinī mahāsare;</p>','\r',char(13)),'\n',char(10)),'-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',38,replace(replace('<p class="gatha3">Sattete <a name="M0.0038"></a> sabbathā passaṃ,</p>\r\n<p class="gathalast">Diṭṭheva pātikaṃ name.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> Manussattaṃ liṅgasampatti,</p>\r\n<p class="gatha2">Hetu satthāradassanaṃ;</p>\r\n<p class="gatha3">Pabbajjā guṇasampatti,</p>\r\n<p class="gatha2">Adhikāro ca chandatā;</p>\r\n<p class="gatha3">Aṭṭhadhamma samodhānā,</p>\r\n<p class="gathalast">Bhinīhārantaguṃ name.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',39,replace(replace('<p class="gatha1">Visaṭṭhaṃ <a name="M0.0039"></a> mañju viññeyyaṃ,</p>\r\n<p class="gatha2">Savanīyāvisāriṇā;</p>\r\n<p class="gatha3">Gambhīro binduninnādo,</p>\r\n<p class="gathalast">Aṭṭhaṅgikasaraṃ name.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',40,replace(replace('<p class="gatha1">Cattāri <a name="M0.0040"></a> rūpajhānāni,</p>\r\n<p class="gatha2">Tathevārūpajhānāni;</p>\r\n<p class="gatha3">Tā samāpattiyo aṭṭha,</p>\r\n<p class="gathalast">Sevantaṃ ruciyā name.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> Aṇimā laṅghimā kammaṃ,</p>\r\n<p class="gatha2">Mahimā pattimā tathā;</p>','\r',char(13)),'\n',char(10)),'-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',41,replace(replace('<p class="gatha3">Īsitā <a name="M0.0041"></a> vasitā ceva,</p>\r\n<p class="gatha2">Yatthakāmāvasāyitā;</p>\r\n<p class="gatha3">Aṭṭhissariya puṇṇattā,</p>\r\n<p class="gathalast">Issarātissaraṃ name.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',42,replace(replace('<p class="gatha1">Manomayiddhiñāṇañca<a name="M0.0042"></a>,</p>\r\n<p class="gatha2">Chaḷabhiññā vipassanā;</p>\r\n<p class="gatha3">Aṭṭhavijjāhi sampannaṃ,</p>\r\n<p class="gathalast">Vande tilokaketukaṃ.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> Jāti jarā rujā kālo,</p>\r\n<p class="gatha2">Caturāpāyadukkhāti;</p>\r\n<p class="gatha3">Aṭṭha saṃvegavatthūni,</p>\r\n<p class="gathalast">Bhāsantaṃ vividhaṃ name.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-44-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',43,replace(replace('<p class="gatha1">Suttaṅgaṃ <a name="M0.0043"></a> veyyākaraṇaṃ,</p>\r\n<p class="gatha2">Geyyaṃ gāthā ca jātakaṃ;</p>\r\n<p class="gatha3">Abbhūtadhamma vedallā,</p>\r\n<p class="gatha2">Udānamiti vuttakaṃ;</p>\r\n<p class="gatha3">Navaṅgaṃ sabbalokassa,</p>\r\n<p class="gathalast">Dadantaṃ sāsanaṃ name.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',44,replace(replace('<p class="gatha1">Yā <a name="M0.0044"></a> samāpattiyo aṭṭha,</p>\r\n<p class="gatha2">Nirodho cāti te nava,</p>\r\n<p class="gatha3">Anupubbavihāre taṃ,</p>\r\n<p class="gathalast">Sevantaṃ ruciyā name.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> Ekattañceva nānattaṃ,</p>\r\n<p class="gatha2">Kāyasaññīhi yogato,</p>\r\n<p class="gatha3">Cattāro tathāruppā ca,</p>\r\n<p class="gatha2">Asaññīcāti te nava,</p>\r\n<p class="gatha3">Sattavāse vibhajitvā,</p>\r\n<p class="gathalast">Pakāsentaṃ name jinaṃ.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-47-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',45,replace(replace('<p class="gatha1">Seyyassa <a name="M0.0045"></a> sadiso seyyo,</p>\r\n<p class="gatha2">Hīnoti tividho vidho,</p>\r\n<p class="gatha3">Tathā sadisahīnānaṃ,</p>\r\n<p class="gathalast">Navavidhe nudaṃ name.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-49-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',46,replace(replace('<p class="gatha1">Anatthaṃ <a name="M0.0046"></a> me carati ca,</p>\r\n<p class="gatha2">Tathā cari carissati,</p>\r\n<p class="gatha3">Mittassa arinotvatthaṃ,</p>\r\n<p class="gathalast">Navāghāte jahaṃ name.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> Dānaṃ sīlañca nikkhamaṃ,</p>\r\n<p class="gatha2">Paññā vīriyapañcamaṃ,</p>','\r',char(13)),'\n',char(10)),'-50-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',47,replace(replace('<p class="gatha3">Khantī <a name="M0.0047"></a> saccamadhiṭṭhānaṃ,</p>\r\n<p class="gatha2">Mettupekkhāti tādasa,</p>\r\n<p class="gatha3">Pūretvā pāramī vande,</p>\r\n<p class="gathalast">Sammāsambodhimajjhavaṃ;</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> Kāḷāvakañca gaṅgeyyaṃ,</p>\r\n<p class="gatha2">Paṇḍaraṃ tamba piṅgalaṃ,</p>\r\n<p class="gatha3">Gandha maṅgala hemañca,</p>\r\n<p class="gathalast">Uposatha chaddantime.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> Dhāreti dasaposānaṃ,</p>\r\n<p class="gatha2">Nāgo kāḷāvako balaṃ;</p>','\r',char(13)),'\n',char(10)),'-51-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',48,replace(replace('<p class="gatha3">Sahassakoṭiposānaṃ<a name="M0.0048"></a>;</p>\r\n<p class="gathalast">Chaddantovāranuttamo.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> Dasa sahassakoṭīnaṃ,</p>\r\n<p class="gatha2">Posānaṃ baladhāraṇaṃ;</p>\r\n<p class="gatha3">Chaddantānaṃ dasannañca,</p>\r\n<p class="gathalast">Dasakāyabalaṃ name.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-53-54-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',49,replace(replace('<p class="gatha1">Ṭhānāṭhāne <a name="M0.0049"></a> vipāke ca,</p>\r\n<p class="gatha2">Magge sabbatthagāminaṃ;</p>\r\n<p class="gatha3">Nānādhātūsu lokesu,</p>\r\n<p class="gathalast">Adhimuttimhi pāṇinaṃ;</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> Paropariyatte ñāṇaṃ,</p>\r\n<p class="gatha2">Indriyānañca pāṇinaṃ;</p>\r\n<p class="gatha3">Jhānādīsu ñāṇaṃ pubbe,</p>\r\n<p class="gatha2">Nivāse dibbacakkhu ca;</p>\r\n<p class="gatha3">Āsavakkhayañāṇanti,</p>\r\n<p class="gathalast">Dasabalañāṇaṃ name.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-55-56-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',50,replace(replace('<p class="gatha1">Mūlagandho <a name="M0.0050"></a> sāro pheggu,</p>\r\n<p class="gatha2">Taco papaṭiko raso;</p>\r\n<p class="gatha3">Pattaṃ pupphaṃ phalañceva,</p>\r\n<p class="gatha2">Gandhagandhoti saṃvaraṃ;</p>\r\n<p class="gatha3">Sugandhaṃ dasagandhehi,</p>\r\n<p class="gathalast">Vilimpantaṃ sadā name.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-57-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',51,replace(replace('<p class="gatha1">Samādhi <a name="M0.0051"></a> ñāṇavipphārā,</p>\r\n<p class="gatha2">Adhiṭṭhānaṃ vikubbanā;</p>\r\n<p class="gatha3">Manomayāriyā iddhi,</p>\r\n<p class="gathalast">Tathā kammavipākajā.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> Vijjāmayā puññavato;</p>\r\n<p class="gatha2">Payoga paccayiddhiti;</p>\r\n<p class="gatha3">Aggapatta dasiddhīnaṃ,</p>\r\n<p class="gathalast">Pāṭiheraṃ karaṃ name.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-58-59-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',52,replace(replace('<p class="gatha1">Muttā <a name="M0.0052"></a> maṇi veḷuriyaṃ,</p>\r\n<p class="gatha2">Saṅkho sallo pavāḷakaṃ;</p>\r\n<p class="gatha3">Suvaṇṇaṃ rajataṃ lohi,</p>\r\n<p class="gatha2">Taṅko masāragallanti;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',53,replace(replace('<p class="gatha3">Dasadhā <a name="M0.0053"></a> ratanehiggaṃ,</p>\r\n<p class="gathalast">Bhogiṃ paññāmaṇiṃ name.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> Sithilaṃ dhanitaṃ dīghaṃ,</p>\r\n<p class="gatha2">Rassañca garukaṃ lahu;</p>\r\n<p class="gatha3">Niggahitaṃ vimuttañca,</p>\r\n<p class="gatha2">Sambandhañca vavatthitaṃ;</p>\r\n<p class="gatha3">Vivarantaṃ name dhammaṃ,</p>\r\n<p class="gathalast">Dasabyañjanabheditaṃ.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-60-61-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',54,replace(replace('<p class="gatha1">Pasādojo <a name="M0.0054"></a> madhuratā,</p>\r\n<p class="gatha2">Samatā sukhumālatā;</p>\r\n<p class="gatha3">Silesodāratā kanti,</p>\r\n<p class="gatha2">Atthabyatti samādhayo;</p>\r\n<p class="gatha3">Dasa saddaguṇopetaṃ,</p>\r\n<p class="gathalast">Dhammaṃ pātu karaṃ name.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-62-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',55,replace(replace('<p class="gatha1">Lobho <a name="M0.0055"></a> doso moho māno,</p>\r\n<p class="gatha2">Uddhaccaṃ diṭṭhi saṃsayo;</p>\r\n<p class="gatha3">Ahirikamanottappaṃ,</p>\r\n<p class="gatha2">Thinanti sabba dāhake;</p>\r\n<p class="gatha3">Kilese dasa te saddhiṃ,</p>\r\n<p class="gathalast">Vāsanāya jahaṃ name.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',56,replace(replace('<p class="gatha1">Saterā <a name="M0.0056"></a> daṇḍamaṇikā,</p>\r\n<p class="gatha2">Macchavilolagaggarā;</p>\r\n<p class="gatha3">Sukkhāsani kapisisā,</p>\r\n<p class="gatha2">Vicakkā sañña kukkuṭā;</p>\r\n<p class="gatha3">Navāsanīhi phālentaṃ,</p>\r\n<p class="gathalast">Tibbaṃ paññāsaniṃ name.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-64-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',57,replace(replace('<p class="gatha1">Rodanā <a name="M0.0057"></a> kodhāvudho ca,</p>\r\n<p class="gatha2">Ujjhantissariyaṃ tathā;</p>\r\n<p class="gatha3">Paṭisaṅkhānaṃ,</p>\r\n<p class="gatha2">Khanti aṭṭhabalesu taṃ;</p>\r\n<p class="gatha3">Pathavīsadisaṃ vande,</p>\r\n<p class="gathalast">Aṭṭhamena vināyakaṃ.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-65-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',58,replace(replace('<p class="gatha1">Kāmarogo <a name="M0.0058"></a> ca paṭigho,</p>\r\n<p class="gatha2">Māno diṭṭhi ca saṃsayo;</p>\r\n<p class="gatha3">Bhavarāgo avijjāti,</p>\r\n<p class="gathalast">Sattānusayinaṃ name.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> Titthiyāpakatimahāsāvakā,</p>\r\n<p class="gatha2">Aggasāvakā paccekabuddhasambuddhā;</p>','\r',char(13)),'\n',char(10)),'-66-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',59,replace(replace('<p class="gatha3">Visiṭṭhaṃ <a name="M0.0059"></a> sabbathā chasu,</p>\r\n<p class="gatha2">Pubbe nivāsa viññūsu;</p>\r\n<p class="gathalast">Tilokamakuṭaṃ name.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> Buddhacakkhu samantā ca,</p>\r\n<p class="gatha2">Ñāṇaṃ yaṃ dibbacakkhu ca;</p>\r\n<p class="gatha3">Dhammoti pañca cakkhūhi,</p>\r\n<p class="gathalast">Dassāviṃ mārajiṃ name.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-67-68-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',60,replace(replace('<p class="gatha1">Khuddakā <a name="M0.0060"></a> khaṇikā okkantikā ca,</p>\r\n<p class="gatha2">Pharaṇā tathā ubbegā;</p>\r\n<p class="gatha3">Pañca pītīhi pinentaṃ,</p>\r\n<p class="gathalast">Ruciyā ca namāmahaṃ.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> Dukkhaṃ samudayasaccaṃ,</p>\r\n<p class="gatha2">Nirodho maggasaccakaṃ;</p>','\r',char(13)),'\n',char(10)),'-69-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',61,replace(replace('<p class="gatha3">Catusaccamabhijānaṃ<a name="M0.0061"></a>,</p>\r\n<p class="gathalast">Catukiccehi taṃ name.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> Pīḷanā saṅkhatattho ca,</p>\r\n<p class="gatha2">Tapo vipariṇāmanā;</p>\r\n<p class="gatha3">Dukkhasaccaṃ catukkehi,</p>\r\n<p class="gathalast">Vibhattāviṃ muniṃ name.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-70-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',62,replace(replace('<p class="gatha1">Āyūhanaṃ <a name="M0.0062"></a> nidānañca,</p>\r\n<p class="gatha2">Saṃyogo palibodhanaṃ;</p>\r\n<p class="gatha3">Catukkehi samudayaṃ,</p>\r\n<p class="gathalast">Vibhattāviṃ muniṃ name.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> Nissaraṇañca viveko,</p>\r\n<p class="gatha2">Asaṅkhatāmataṃ tathā;</p>\r\n<p class="gatha3">Nirodhañca catukkehi,</p>\r\n<p class="gathalast">Vibhattāviṃ muniṃ name.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-72-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',63,replace(replace('<p class="gatha1">Niyyāniko <a name="M0.0063"></a> ca hetvattho,</p>\r\n<p class="gatha2">Dassanādhipateyyakaṃ;</p>\r\n<p class="gatha3">Maggasaccaṃ catukkehi,</p>\r\n<p class="gathalast">Vibhattāviṃ muniṃ name.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-74-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',64,replace(replace('<p class="gatha1">Satthako <a name="M0.0064"></a> ca asammoho,</p>\r\n<p class="gatha2">Sappāyo ceva gocaro;</p>\r\n<p class="gatha3">Catusampajaññā vinā,</p>\r\n<p class="gathalast">Bhāviṃ name tathāgataṃ.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> Pahinākhila dukkhāhaṃ,</p>\r\n<p class="gatha2">Bhavasāgara pāraguṃ;</p>\r\n<p class="gatha3">Vande sāraguṇopetaṃ,</p>\r\n<p class="gathalast">Tenamhi bhavapārago.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-75-76-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',65,replace(replace('<p class="gatha1">Rūpārūpavilāsagga<a name="M0.0065"></a>,</p>\r\n<p class="gatha2">Rūpācinteyya saṃyuttaṃ;</p>\r\n<p class="gatha3">Vande sāraguṇopetaṃ,</p>\r\n<p class="gathalast">Tenamhātularūpavā.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-77-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',66,replace(replace('<p class="gatha1">Iddhi <a name="M0.0066"></a> iddhi vilāsagga,</p>\r\n<p class="gatha2">Iddhi cinteyya saṃyuttaṃ;</p>\r\n<p class="gatha3">Vande sāraguṇepetaṃ,</p>\r\n<p class="gathalast">Tenamhā tulaiddhimā.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> Vācā vācā vilāsagga,</p>\r\n<p class="gatha2">Vācā cinteyya saṃyuttaṃ;</p>\r\n<p class="gatha3">Vande sāraguṇopetaṃ,</p>\r\n<p class="gathalast">Tenamhā tulavācako.</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-78-79-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',67,replace(replace('<p class="gatha1">Ñāṇa <a name="M0.0067"></a> ñāṇa vilāsagga,</p>\r\n<p class="gatha2">Ñāṇācinteyya saṃyuttaṃ;</p>\r\n<p class="gatha3">Vande sāraguṇopetaṃ,</p>\r\n<p class="gathalast">Tenamhā tulañāṇavā.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> Sajjhaṃ hemañca ratanaṃ,</p>\r\n<p class="gatha2">Gehaṃ vatthañca bhojanaṃ;</p>','\r',char(13)),'\n',char(10)),'-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',68,replace(replace('<p class="gatha3">Tadaññepi <a name="M0.0068"></a> yathācittaṃ,</p>\r\n<p class="gathalast">Māpeyyāhaṃ name jinaṃ.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> Sajjhaṃ hemañca ratanaṃ,</p>\r\n<p class="gatha2">Gehaṃ vatthañca bhojanaṃ;</p>\r\n<p class="gatha3">Tadaññepi yathācittaṃ,</p>\r\n<p class="gathalast">Māpeyyaṃ kammajiddhiyā.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-81-82-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',69,replace(replace('<p class="gatha1">Sattabojjhaṅga <a name="M0.0069"></a> ratano,</p>\r\n<p class="gatha2">Saddhādiratano muni;</p>\r\n<p class="gatha3">Satippabhutiratano,</p>\r\n<p class="gathalast">Vande taṃ purisuttamaṃ.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> Cakkādisattaratanaṃ,</p>\r\n<p class="gatha2">Muttādiratanaṃ subhaṃ;</p>\r\n<p class="gatha3">Vatthappabhutiratanaṃ,</p>\r\n<p class="gathalast">Chandakkhaṇe labhāmahaṃ.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-83-84-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',70,replace(replace('<p class="gatha1">Sampuṇṇacittasaṅkappo<a name="M0.0070"></a>,</p>\r\n<p class="gatha2">Yathākaṅkhitamāpako,</p>\r\n<p class="gatha3">Kenacinabhibhūto yo,</p>\r\n<p class="gathalast">Sabbābhibhū name jinaṃ.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-85-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',71,replace(replace('<p class="gatha1">Sampuṇṇacittasaṅkappo<a name="M0.0071"></a>,</p>\r\n<p class="gatha2">Yathākaṅkhitamāpako,</p>\r\n<p class="gatha3">Kenacinabhibhūtamhi,</p>\r\n<p class="gathalast">Sabbābhibhū bhave bhave.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> Dasa chaddantarājāva,</p>\r\n<p class="gatha2">Dasa kāyabalo muni;</p>\r\n<p class="gatha3">Dasa ñāṇabbalotulyo,</p>\r\n<p class="gathalast">Vande taṃ samaṇuttamaṃ.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-86-87-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',72,replace(replace('<p class="gatha1">Atikāyajavo <a name="M0.0072"></a> buddho,</p>\r\n<p class="gatha2">Raññājavanahaṃsato;</p>\r\n<p class="gatha3">Kā kathā ñāṇavegassa,</p>\r\n<p class="gathalast">Vande taṃ samaṇuttamaṃ.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> Chaddantanāgarājāva,</p>\r\n<p class="gatha2">Bhave bhave mahabbalo;</p>\r\n<p class="gatha3">Rājājavanahaṃsova,</p>\r\n<p class="gathalast">Paramaggajavo bhave.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-88-89-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',73,replace(replace('<p class="gatha1">Udakā <a name="M0.0073"></a> kāsacārī ca,</p>\r\n<p class="gatha2">Mahinimujjako jino;</p>\r\n<p class="gatha3">Māpako ca yathā cittaṃ,</p>\r\n<p class="gathalast">Vande taṃ ñeyyapāraguṃ.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> Udakā kāsacārī ca,</p>\r\n<p class="gatha2">Mahinimujjako bhave;</p>\r\n<p class="gatha3">Māpako ca yathācittaṃ,</p>\r\n<p class="gathalast">Bhavaso kammajiddhiyā.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-90-91-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',74,replace(replace('<p class="gatha1">Gāme <a name="M0.0074"></a> vane ca sabbattha,</p>\r\n<p class="gatha2">Devehi manussehi ca;</p>\r\n<p class="gatha3">Ābhatānantalābhassa,</p>\r\n<p class="gatha2">Sadā yassa mahesino;</p>\r\n<p class="gatha3">Pattalābhaggataṃ lokaṃ,</p>\r\n<p class="gathalast">Taṃ vande munipuṅgavaṃ.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-92-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',75,replace(replace('<p class="gatha1">Gāme <a name="M0.0075"></a> vane ca sabbattha,</p>\r\n<p class="gatha2">Devehi manussehi ca;</p>\r\n<p class="gatha3">Ābhatā nantalābho me,</p>\r\n<p class="gathalast">Sadā hotu bhavābhave.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> Puññassimassa tejena,</p>\r\n<p class="gatha2">Yathā cittaṃ samijjhatu;</p>','\r',char(13)),'\n',char(10)),'-93-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',76,replace(replace('<p class="gatha3">Sabbicchā <a name="M0.0076"></a> sabbacintā ca,</p>\r\n<p class="gathalast">Khippaṃ me jātijātiyaṃ.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> Natthīti vacanaṃ dukkhaṃ,</p>\r\n<p class="gatha2">Dehīti vacanaṃ tathā;</p>\r\n<p class="gatha3">Tasmā natthīti dehīti,</p>\r\n<p class="gathalast">Mā me hotu bhavābhave.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-94-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',77,replace(replace('<p class="gatha1">Sabbaṃ <a name="M0.0077"></a> paravasaṃ dukkhaṃ,</p>\r\n<p class="gatha2">Sabbamissariyaṃ sukhaṃ;</p>\r\n<p class="gatha3">Sabbaṃ paravasamatthu,</p>\r\n<p class="gathalast">Sabbamissariyaṃ bhave.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> Vitakkento bhajjakāyo,</p>\r\n<p class="gatha2">Sabbāvudha vāraṇo ca;</p>\r\n<p class="gatha3">Chaddanta vāraṇabalo,</p>\r\n<p class="gathalast">Bhaveyyaṃ jātijā tiyaṃ.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-96-97-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',78,replace(replace('<p class="gatha1">Subhalakkhaṇasampanno<a name="M0.0078"></a>,</p>\r\n<p class="gatha2">Suvaṇṇavaṇṇavā bhave;</p>\r\n<p class="gatha3">Brahmassaro karavika,</p>\r\n<p class="gathalast">Bhāṇī ca jātijātiyaṃ.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> Bhūrimapañño sippānaṃ,</p>\r\n<p class="gatha2">Sabbesaṃ kusalo bhave;</p>\r\n<p class="gatha3">Visajjetuṃ samatthova,</p>\r\n<p class="gathalast">Sabbapucchānaṃ ṭhānaso.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-98-99-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',79,replace(replace('<p class="gatha1">Verādhaṃsīya <a name="M0.0079"></a> bhogā ca,</p>\r\n<p class="gatha2">Anantākhīṇa bhogavā;</p>\r\n<p class="gatha3">Anantābhajja pariso,</p>\r\n<p class="gathalast">Bhavaso pāpuṇe sivaṃ.</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-100-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',80,replace(replace('<p class="gatha1">Mā <a name="M0.0080"></a> nasseyyaṃ pasayheyyaṃ,</p>\r\n<p class="gatha2">Ussukkehi pyahaṃ saha;</p>\r\n<p class="gatha3">Pañcaverehi koṭīhi,</p>\r\n<p class="gathalast">Bhaṇḍaṃ vā akātukāmitaṃ.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> Meghaṃ vātañca ratanaṃ,</p>\r\n<p class="gatha2">Dhaññaṃ vatthañca bhojanaṃ;</p>\r\n<p class="gatha3">Sabbicchitaṃ tadaññampi,</p>\r\n<p class="gathalast">Māpeyyaṃ kammajiddhiyā.</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-101-102-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',81,replace(replace('<p class="gatha1">Puññenetena <a name="M0.0081"></a> nibbānaṃ,</p>\r\n<p class="gatha2">Santaṃ pappomi tāvatā;</p>\r\n<p class="gatha3">Bhaveyyaṃ sabbajātīsu,</p>\r\n<p class="gatha2">Catusampattiyā sadā;</p>\r\n<p class="gatha3">Catucakkena sampanno,</p>\r\n<p class="gathalast">Saddhammehi ca sattahi.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-103-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',82,replace(replace('<p class="gatha1">Sammādiṭṭhi <a name="M0.0082"></a> vasupete,</p>\r\n<p class="gatha2">Kulamhi seṭṭhasammate,</p>\r\n<p class="gatha3">Sabbasakkata saṃsuddhe;</p>\r\n<p class="gathalast">Bhave tihetusandhiko.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> Ghāsacchādanaṃ bhogañca,</p>\r\n<p class="gatha2">Neva hatthena kātuna,</p>\r\n<p class="gatha3">Bhuñjeyyamiddhiyātveva,</p>\r\n<p class="gathalast">Māpetvā yāvadatthakaṃ.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-104-105-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',83,replace(replace('<p class="gatha1">Manussānaṃ <a name="M0.0083"></a> atikkamma,</p>\r\n<p class="gatha2">Devānaṃ viya bhojanaṃ,</p>\r\n<p class="gatha3">Vatthaṃ bhogo ca me hontu,</p>\r\n<p class="gathalast">Parivārātisundarā.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> Dasāsuttaṃpi me bhogaṃ,</p>\r\n<p class="gatha2">Pañcaverā bhave bhave;</p>\r\n<p class="gatha3">Māgaṇheyyuñca nasseyyuṃ,</p>\r\n<p class="gathalast">Asoko bhogahetume.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-106-107-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',84,replace(replace('<p class="gatha1">Sabbaṅgasubha <a name="M0.0084"></a> sampanno,</p>\r\n<p class="gatha2">Siṅgīnikkha savaṇṇa vā;</p>\r\n<p class="gatha3">Bāttiṃsa lakkhaṇūpeto,</p>\r\n<p class="gathalast">Atappana rūpa vā bhave.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> Kāyo candanagandho ca,</p>\r\n<p class="gatha2">Mukhaṃ uppalagandhikaṃ;</p>','\r',char(13)),'\n',char(10)),'-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',85,replace(replace('<p class="gatha3">Aṭṭhaṅgiko <a name="M0.0085"></a> karavika,</p>\r\n<p class="gathalast">Mañjūghoso ca me hotu.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> Tikkha gambhīra pañño ca,</p>\r\n<p class="gatha2">Hāsātijavapaññavā,</p>\r\n<p class="gatha3">Bhūri nibbedha pañño ca;</p>\r\n<p class="gathalast">Sabbapañhavisajjano.</p>\r\n<p class="hangnum"><a name="para111"></a><span class="paranum">111</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-109-111-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_gantha_sanggaho_e0604n',86,replace(replace('<p class="gatha1">Anto <a name="M0.0086"></a> soḷasavassassa,</p>\r\n<p class="gatha2">Tipeṭakadharo bhave;</p>\r\n<p class="gatha3">Sabbakammesu sippesu,</p>\r\n<p class="gathalast">Vijjāṭhānesu pāragū.</p>','\r',char(13)),'\n',char(10)),'');
