INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',1,replace(replace('<p class="centered">Namo tassa bhagavato arahato sammāsambuddhassa.</p>\r\n<p class="chapter">Vessantarāgīti</p>\r\n<p class="bodytext">[Ka]</p>\r\n<p class="gatha1">Bāttiṃsalakkhaṇūpeta<a name="M0.0001"></a>,</p>\r\n<p class="gatha2">Dātabbaṃ dadataṃ vara;</p>\r\n<p class="gatha3">Vessantaratta māpādi,</p>\r\n<p class="gathalast">Sañcaraṃ bhavasāgare.</p>\r\n<p class="bodytext">[Kha]</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',2,replace(replace('<p class="gatha1">Dvandaṃdvandaṃ <a name="M0.0002"></a> sañcarantī,</p>\r\n<p class="gatha2">Janānande yasodhīre;</p>\r\n<p class="gatha3">Maddittaṃ samupāgañchi,</p>\r\n<p class="gathalast">Tvaṃpitena bhavaṇṇave.</p>\r\n<p class="bodytext">[Ga]</p>\r\n<p class="gatha1">Tumhaṃdāni pavakkhāmi,</p>\r\n<p class="gatha2">Upattiṃ nandiniṃ tahiṃ;</p>\r\n<p class="gatha3">Dajjahvovo mamokāsaṃ,</p>\r\n<p class="gathalast">Kavayo pyābhinandatha.</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> Ekanavutehito kappe,</p>\r\n<p class="gatha2">Vipassīnāma nāyako;</p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',3,replace(replace('<p class="gatha3">Vineyye <a name="M0.0003"></a> tārayaṃkhemaṃ,</p>\r\n<p class="gathalast">Loke uppajjicakkhumā.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Khemārāmamhi sambuddho,</p>\r\n<p class="gatha2">Migadāye manorame;</p>\r\n<p class="gatha3">Nissāya bandhumatiṃso,</p>\r\n<p class="gathalast">Vihāsi nagaraṃtadā.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Rajjaṃdhammena kāresi,</p>\r\n<p class="gatha2">Nagaretamhi bandhumā;</p>\r\n<p class="gatha3">Rājātassa duvedhītā,</p>\r\n<p class="gathalast">Āsuṃ sabbaṅgasobhaṇā.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-3-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',4,replace(replace('<p class="gatha1">Ekohi <a name="M0.0004"></a> ekadārājā,</p>\r\n<p class="gatha2">Bandhumassa supesayi;</p>\r\n<p class="gatha3">Sahassagghanikaṃ soṇṇa-</p>\r\n<p class="gathalast">Mālañcā naggha candanaṃ.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Adāsi bandhumārājā,</p>\r\n<p class="gatha2">Pitājeṭṭhāya candanaṃ;</p>\r\n<p class="gatha3">Soṇṇamālaṃ kaniṭṭhāya,</p>\r\n<p class="gathalast">Tahiṃ viyāya dhītuyā.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',5,replace(replace('<p class="gatha1">Ubhotāpīti <a name="M0.0005"></a> cintesuṃ,</p>\r\n<p class="gatha2">Natthatthovata tehino;</p>\r\n<p class="gatha3">Bhagyavantassa pūjema,</p>\r\n<p class="gathalast">Saṃpasādena cetasā.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Tatosācandanaṃ cuṇṇaṃ-</p>\r\n<p class="gatha2">Kārāpetvāna jeṭṭhakā;</p>\r\n<p class="gatha3">Gantvāna vihāraṃ soṇṇa,</p>\r\n<p class="gathalast">Vaṇṇaṃpūjesi nāyakaṃ.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Vikiritvā tu sāgandha,</p>\r\n<p class="gatha2">Kuṭiyaṃ sesacandanaṃ;</p>\r\n<p class="gatha3">Evañhi patthanaṃ kāsi,</p>\r\n<p class="gathalast">Buddhamātā bhavāmahaṃ.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-8-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',6,replace(replace('<p class="gatha1">Kāretvā <a name="M0.0006"></a> soṇṇamālaṃtu,</p>\r\n<p class="gatha2">Uracchada pasādanaṃ;</p>\r\n<p class="gatha3">Pūjesi hemavaṇṇaṃsā,</p>\r\n<p class="gathalast">Bhagavantaṃ kaniṭṭhakā.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> Evampi patthanaṃkāsi,</p>\r\n<p class="gatha2">Idaṃmama pasādanaṃ;</p>\r\n<p class="gatha3">Sarīre niccalā ṭhātu,</p>\r\n<p class="gathalast">Carantiyā bhavābhavaṃ.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',7,replace(replace('<p class="gatha1">Ubhopi <a name="M0.0007"></a> rājakaññātā,</p>\r\n<p class="gatha2">Ṭhatvāna yāvatā yukaṃ;</p>\r\n<p class="gatha3">Upapajjiṃsu sovaggaṃ,</p>\r\n<p class="gathalast">Cutāsukha pamodanaṃ.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> Manussadeva lokesu,</p>\r\n<p class="gatha2">Saṃsariṃsu sukhaṃduve;</p>\r\n<p class="gatha3">Ekanavuti kappāni,</p>\r\n<p class="gathalast">Muccitvā pāyadukkhato.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> Kassapassa kāletāsu,</p>\r\n<p class="gatha2">Kaniṭṭhā kikirājino;</p>\r\n<p class="gatha3">Dhītā uracchadānāma,</p>\r\n<p class="gathalast">Hutvāna parinibbutā.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-13-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',8,replace(replace('<p class="gatha1">Jeṭṭhakā <a name="M0.0008"></a> pana dhītāsi,</p>\r\n<p class="gatha2">Kikissa rājino tahiṃ;</p>\r\n<p class="gatha3">Dhammānāma damitā sā,</p>\r\n<p class="gathalast">Komāra brahmacārinī.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Tatoca vitvāna sā deva,</p>\r\n<p class="gatha2">Manussesu punappunaṃ;</p>\r\n<p class="gatha3">Saṃsarantī mahesīsi,</p>\r\n<p class="gathalast">Pūrindadassa ekadā.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',9,replace(replace('<p class="gatha1">Rattacandana <a name="M0.0009"></a> lepena,</p>\r\n<p class="gatha2">Limpita dehinī hisā;</p>\r\n<p class="gatha3">Katapuñña visesena,</p>\r\n<p class="gathalast">Nāmena phussatīhvitā.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> Devindo aññadādisvā,</p>\r\n<p class="gatha2">Parikkhīṇāyukaṃ viyaṃ;</p>\r\n<p class="gatha3">Deviṃ taṃ parivārena,</p>\r\n<p class="gathalast">Mahatā nesinandanaṃ.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> Sirīsayana piṭṭhamhi,</p>\r\n<p class="gatha2">Taṃsayāviya māghavā;</p>','\r',char(13)),'\n',char(10)),'-17-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',10,replace(replace('<p class="gatha3">Ṭhito <a name="M0.0010"></a> sayanapassamhi,</p>\r\n<p class="gathalast">Phussatiṃ etadabravī.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> Varetedasadassāmi,</p>\r\n<p class="gatha2">Phussatevaravaṇṇike;</p>\r\n<p class="gatha3">Tvaṃñhiicchasiyeladdhuṃ,</p>\r\n<p class="gathalast">Labhāpessāmitevare.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> Sutvātaṃ phussatīdevī,</p>\r\n<p class="gatha2">Lomahaṃ sanarūpinī;</p>\r\n<p class="gatha3">Khīṇāyutamajānantī,</p>\r\n<p class="gathalast">Attano etadabravī.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-20-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',11,replace(replace('<p class="gatha1">Pāpaṃnu <a name="M0.0011"></a> tvayi deviṃnda,</p>\r\n<p class="gatha2">Kataṃme appiyāthate;</p>\r\n<p class="gatha3">Rammāme cavanaṃ icchaṃ,</p>\r\n<p class="gathalast">Maññebhaṇaṃti īdisaṃ.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> Natebhadde kataṃpāpaṃ,</p>\r\n<p class="gatha2">Nacame appiyātuvaṃ;</p>\r\n<p class="gatha3">Puññaṃtute parikkhīṇaṃ,</p>\r\n<p class="gathalast">Tasmā te vaṃvadāmahaṃ.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',12,replace(replace('<p class="gatha1">Santike <a name="M0.0012"></a> maraṇaṃ tuyhaṃ,</p>\r\n<p class="gatha2">Vinābhāvo bhavissati;</p>\r\n<p class="gatha3">Paṭiggaṇhāhi me ete,</p>\r\n<p class="gathalast">Varedasa pavacchato.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> Sakkassavacanaṃ sutvā,</p>\r\n<p class="gatha2">Ñātvāmaraṇa mattano;</p>\r\n<p class="gatha3">Varesā laddhumicchantī,</p>\r\n<p class="gathalast">Devindameta dabravī.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> Ito cutāsahassakkhi,</p>\r\n<p class="gatha2">Sīvirājanivesane;</p>\r\n<p class="gatha3">Mahesī phussatīnāma,</p>\r\n<p class="gathalast">Nīlanettā yathāmigī.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-24-25-26-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',13,replace(replace('<p class="gatha1">Assaṃ <a name="M0.0013"></a> nīlabhamukāhaṃ,</p>\r\n<p class="gatha2">Labheyyaṃ puttamuttamaṃ;</p>\r\n<p class="gatha3">Dhārenti yācamegabbhaṃ,</p>\r\n<p class="gathalast">Majjhimaṅgaṃ anunnataṃ.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> Palitāna virūhantu,</p>\r\n<p class="gatha2">Alambāca payoro;</p>\r\n<p class="gatha3">Kāyerajo nalimpetha,</p>\r\n<p class="gathalast">Vajjhañcāpi pamocaye.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-27-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',14,replace(replace('<p class="gatha1">Yeyācitā <a name="M0.0014"></a> varābhadde,</p>\r\n<p class="gatha2">Tayādasa mayātava;</p>\r\n<p class="gatha3">Dinnāte tuṭṭhacittena,</p>\r\n<p class="gathalast">Sabbelacchasi mānuse.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> Tatocavitvāna sādeva-</p>\r\n<p class="gatha2">Lokā nibbatti icchiyaṃ;</p>\r\n<p class="gatha3">Mahesiyā paṭisandhi-</p>\r\n<p class="gathalast">Vasena maddarājino.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',15,replace(replace('<p class="gatha1">Dasamā <a name="M0.0015"></a> saccayenesā,</p>\r\n<p class="gatha2">Dhītaraṃ maddarājinī;</p>\r\n<p class="gatha3">Heṭṭhāsusetacchattassa,</p>\r\n<p class="gathalast">Vijāyi seṭṭharūpiniṃ.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> Rattacandana lepena,</p>\r\n<p class="gatha2">Esālimpi tadehinī;</p>\r\n<p class="gatha3">Tenassā brāhmaṇānāmaṃ,</p>\r\n<p class="gathalast">Akaṃsu phussatīiti.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> Saññākāra doḷāruḷa-</p>\r\n<p class="gatha2">Maṅgalādīni kātuna;</p>\r\n<p class="gatha3">Mahatā parihārena,</p>\r\n<p class="gathalast">Posiṃsu khattiyāniyo.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-31-32-33-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',16,replace(replace('<p class="gatha1">Madhūrakhīra <a name="M0.0016"></a> sampannā,</p>\r\n<p class="gatha2">Siniddhapīṇa yobbanā;</p>\r\n<p class="gatha3">Dhātiyo khattiyānītaṃ,</p>\r\n<p class="gathalast">Khīraṃpāyiṃsu yāpanaṃ.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> Saṅkamantīva vuddhāsā,</p>\r\n<p class="gatha2">Aṅkāaṅkaṃ karākaraṃ;</p>\r\n<p class="gatha3">Rājakaññāca tosiṃsu,</p>\r\n<p class="gathalast">Naccagītehi nāṭakī.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> Devaccharāvarūpena,</p>\r\n<p class="gatha2">Subhāsoḷasa vassikā;</p>','\r',char(13)),'\n',char(10)),'-34-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',17,replace(replace('<p class="gatha3">Harantī <a name="M0.0017"></a> nettarasaṃsā,</p>\r\n<p class="gathalast">Passantānaṃsukhedhitā.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> Siviraṭṭhe tadārajjaṃ,</p>\r\n<p class="gatha2">Sivirājā jetuttare;</p>\r\n<p class="gatha3">Kāresisiñcayonāma,</p>\r\n<p class="gathalast">Puttotassa mahāyaso.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> Vayappatto bhirūposi,</p>\r\n<p class="gatha2">Devaputtova siñcayo;</p>\r\n<p class="gatha3">Aroha pariṇāhena,</p>\r\n<p class="gathalast">Nānāsibbesu kovido.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-36-37-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',18,replace(replace('<p class="gatha1">Siñcayassa <a name="M0.0018"></a> niyyādesi,</p>\r\n<p class="gatha2">Rajjaṃputtassa attano;</p>\r\n<p class="gatha3">Jīvanto passituṃrāja-</p>\r\n<p class="gathalast">Siriṃicchaṃ mahārahaṃ.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> Ānetvā maddarājassa,</p>\r\n<p class="gatha2">Dhītaraṃ phussatiṃtahiṃ;</p>\r\n<p class="gatha3">Soḷasitthi sahassānaṃ,</p>\r\n<p class="gathalast">Mahesiṃkāsi jeṭṭhikaṃ.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',19,replace(replace('<p class="gatha1">Phussatīsiñcayoññoñña- <a name="M0.0019"></a></p>\r\n<p class="gatha2">Mukhaṃdvepiyacakkhunā;</p>\r\n<p class="gatha3">Passamānāvaacchiṃsu,</p>\r\n<p class="gathalast">Aññamaññapiyaṃvadā.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> Athacinti yadevindo,</p>\r\n<p class="gatha2">Dinnāphussatiyāmayā;</p>\r\n<p class="gatha3">Varādasa tesuputta-</p>\r\n<p class="gathalast">Varonahisamijjhati.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> Samijjhā pesāmietaṃti,</p>\r\n<p class="gatha2">Ajeyyebhidasālaye;</p>\r\n<p class="gatha3">Upadhāritthatissāya,</p>\r\n<p class="gathalast">Puttabhāvārahaṃmaruṃ.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-42-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',20,replace(replace('<p class="gatha1">Cavitvāhuuddhaṃgāmī<a name="M0.0020"></a>,</p>\r\n<p class="gatha2">Bodhidevotatotahiṃ;</p>\r\n<p class="gatha3">Brahmaṃgantvāsahassakkhī,</p>\r\n<p class="gathalast">Tassataṃetadabravī.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> Ito bho sugatiṃgaccha,</p>\r\n<p class="gatha2">Anukampāhimarisa;</p>\r\n<p class="gatha3">Siñcayaṃdeviyaṃtassa,</p>\r\n<p class="gathalast">Sandhiṃ gaṇhajutindhara.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-44-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',21,replace(replace('<p class="gatha1">Aññepi <a name="M0.0021"></a> saṭṭhisahassa-</p>\r\n<p class="gatha2">Deve cavana dhammine;</p>\r\n<p class="gatha3">Upasaṅkamma soyāci,</p>\r\n<p class="gathalast">Gacchahvo sugatiṃiti.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> Bodhisattoca aññeca,</p>\r\n<p class="gatha2">Sādhūti sampaṭicchisuṃ;</p>\r\n<p class="gatha3">Tatocutā catedevā,</p>\r\n<p class="gathalast">Āguṃ mānusakaṃimaṃ.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-46-47-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',22,replace(replace('<p class="gatha1">Phussatyaṃhi <a name="M0.0022"></a> mahāsatto,</p>\r\n<p class="gatha2">Nibbatti paṭisandhiyā;</p>\r\n<p class="gatha3">Kulesvaññeca maccānaṃ,</p>\r\n<p class="gathalast">Nibbattiṃsu visuṃvisuṃ.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> Kucchiṅgate mahāsatte,</p>\r\n<p class="gatha2">Devī do haḷinīahu;</p>\r\n<p class="gatha3">Dātukāmā mahādānaṃ,</p>\r\n<p class="gathalast">Visajjiya buhuṃdhanaṃ.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> Rājamandiradvāramhi,</p>\r\n<p class="gatha2">Vemajjhenagarassaca;</p>\r\n<p class="gatha3">Catudvāresuchaddāna-</p>\r\n<p class="gathalast">Sālāyo deyyapūritā.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-48-49-50-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',23,replace(replace('<p class="gatha1">Kārāpetvāna <a name="M0.0023"></a> chassata-</p>\r\n<p class="gatha2">Sahassāni dinedine;</p>\r\n<p class="gatha3">Visajjitvāna sabbesaṃ,</p>\r\n<p class="gathalast">Icchantī āsidātave.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> Utvātaṃ kāraṇaṃrājā,</p>\r\n<p class="gatha2">Pakkosāviya brāhmaṇe;</p>\r\n<p class="gatha3">Nimitta pāṭhakepucchi,</p>\r\n<p class="gathalast">Kiṃ bhavissatītiñātave.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> Jānāma no mahārāja,</p>\r\n<p class="gatha2">Gatokucchimhi deviyā;</p>','\r',char(13)),'\n',char(10)),'-51-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',24,replace(replace('<p class="gatha3">Hehitya <a name="M0.0024"></a> bhiratodāne,</p>\r\n<p class="gathalast">Evaṃvadiṃsu brāhmaṇā.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> Brāhmaṇānaṃ vacosutvā,</p>\r\n<p class="gatha2">Narindo tuṭṭhamānaso;</p>\r\n<p class="gatha3">Dānaṃadāsi chaddāna-</p>\r\n<p class="gathalast">Sālāyo suṭṭhumāviya.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> Paṭisandhiggahaṇamhā,</p>\r\n<p class="gatha2">Bodhisattassa viññuno;</p>\r\n<p class="gatha3">Paṭṭhāyaṇṇava vegova,</p>\r\n<p class="gathalast">Lābhoḷāro anappako.</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-53-54-55-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',25,replace(replace('<p class="gatha1">Paṇṇākāraṃ <a name="M0.0025"></a> pahiṇiṃsu,</p>\r\n<p class="gatha2">Rājino jambumaṇḍale;</p>\r\n<p class="gatha3">Bodhisattā nubhāvena,</p>\r\n<p class="gathalast">Nānāraṭṭhindakhattiyā.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> Parihāraṃ pavacchesi,</p>\r\n<p class="gatha2">Mahesiṃ paṭisandhiniṃ;</p>\r\n<p class="gatha3">Rājāsukhaṃ vasāpesi,</p>\r\n<p class="gathalast">Kāresiicchiticchitaṃ.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> Buddhaṅkurānubhāvena,</p>\r\n<p class="gatha2">Janāsuṃ modamānasā;</p>\r\n<p class="gatha3">Kālevassatimeghopi,</p>\r\n<p class="gathalast">Subhikkhosamayoahu.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-56-57-58-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',26,replace(replace('<p class="gatha1">Saddhiṃputtena <a name="M0.0026"></a> juṇhāya,</p>\r\n<p class="gatha2">Kiḷantiyāvamātuyā;</p>\r\n<p class="gatha3">Mahantīratisabbesaṃ,</p>\r\n<p class="gathalast">Janānaṃvipulātahiṃ.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> Gabbhaṃhi dhārentīdevī,</p>\r\n<p class="gatha2">Dasamāsamhipūrite;</p>\r\n<p class="gatha3">Nagaraṃdaṭṭhu kāmāsi,</p>\r\n<p class="gathalast">Tadārocesirājino.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-59-60-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',27,replace(replace('<p class="gatha1">Sādhubhaddeti <a name="M0.0027"></a> vatvāna,</p>\r\n<p class="gatha2">Puraṃdeva puraṃviya;</p>\r\n<p class="gatha3">Rājā alaṅkarāpesi,</p>\r\n<p class="gathalast">Deviyā pitivaḍḍhanaṃ.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> Āropiya tatodeviṃ,</p>\r\n<p class="gatha2">Vicittaṃ lakkhikaṃrathaṃ;</p>\r\n<p class="gatha3">Padakkhiṇaṃ kārāpesi,</p>\r\n<p class="gathalast">Nagaraṃ sabbalaṅkataṃ.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-61-62-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',28,replace(replace('<p class="gatha1">Vessānaṃ <a name="M0.0028"></a> vīthiyāmajjhe,</p>\r\n<p class="gatha2">Ṭhānāṭhānaṃ pathāpathaṃ;</p>\r\n<p class="gatha3">Caliṃsu kammajāvātā,</p>\r\n<p class="gathalast">Carantiyāva deviyā.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> Utvāmaccāpavattiṃtaṃ,</p>\r\n<p class="gatha2">Raññorociṃsusīghaso;</p>\r\n<p class="gatha3">Sutigehaṃkārāpesi,</p>\r\n<p class="gathalast">Taṃvīthiyaṃvasiñcayo.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> Tatthasāvasamānāva,</p>\r\n<p class="gatha2">Vijāyiakutobhayā;</p>\r\n<p class="gatha3">Nimmalaṃsusuddhaṃputtaṃ,</p>\r\n<p class="gathalast">Suvaṇṇakkhandhasannibhaṃ.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-63-64-65-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',29,replace(replace('<p class="gatha1">Akkhīniummilitvāna<a name="M0.0029"></a>,</p>\r\n<p class="gatha2">Nikkhantomātu kucchito;</p>\r\n<p class="gatha3">Pasannappiyākārena,</p>\r\n<p class="gathalast">Passittamātuyāmukhaṃ.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> Amma dānaṃ dadissāmi,</p>\r\n<p class="gatha2">Atthinu kiñci tedhanaṃ;</p>\r\n<p class="gatha3">Hattaṃso pasāretvāna,</p>\r\n<p class="gathalast">Itibravitahiṃ muhuṃ.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-66-67-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',30,replace(replace('<p class="gatha1">Sutvāmodiya <a name="M0.0030"></a> taṃmātā,</p>\r\n<p class="gatha2">Dehidānaṃ yathicchitaṃ;</p>\r\n<p class="gatha3">Tātāti tassapādāsi,</p>\r\n<p class="gathalast">Sahassatthavikaṃtahiṃ.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> Mahosadha vessantara-</p>\r\n<p class="gatha2">Mahāsiddhattha jātisu;</p>\r\n<p class="gatha3">Kathesi mātarābodhi-</p>\r\n<p class="gathalast">Sasatto jātakkhaṇetisu.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> Acchera mabbhutaṃdisvā,</p>\r\n<p class="gatha2">Janāmodiṃ sutāvade;</p>\r\n<p class="gatha3">Apphoṭento hasantāca,</p>\r\n<p class="gathalast">Jayanādaṃ panādayuṃ.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-68-69-70-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',31,replace(replace('<p class="gatha1">Vessantaroti <a name="M0.0031"></a> hvāyiṃsu,</p>\r\n<p class="gatha2">Nāmaggahaṇa maṅgalaṃ;</p>\r\n<p class="gatha3">Saṅkhatā brāhmaṇātassa,</p>\r\n<p class="gathalast">Jātattā vessavīthiyaṃ.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> Vijāta divasetassa,</p>\r\n<p class="gatha2">Ekāākāsacārinī;</p>\r\n<p class="gatha3">Hatthinīhi sabbasetaṃ,</p>\r\n<p class="gathalast">Ekaṃ kuñjaraposakaṃ.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> Bodhisattā nubhāvena,</p>\r\n<p class="gatha2">Ānetvā maṅgalasammataṃ;</p>\r\n<p class="gatha3">Thapetvāna pakkāmi,</p>\r\n<p class="gathalast">Hatthisālāya sādhukaṃ.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-71-72-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',32,replace(replace('<p class="gatha1">Uppannattāmahāsattaṃ<a name="M0.0032"></a>,</p>\r\n<p class="gatha2">Tassakatvānapaccayaṃ;</p>\r\n<p class="gatha3">Akaṃsunāgarānāmaṃ,</p>\r\n<p class="gathalast">Samaggāpaccayoiti.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> Taddinevavijāyiṃsu,</p>\r\n<p class="gatha2">Amaccānaṃkumārakā;</p>\r\n<p class="gatha3">Gehesusaṭhisahassā,</p>\r\n<p class="gathalast">Mahāsattasajātakā.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-74-75-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',33,replace(replace('<p class="gatha1">Atidīghādayodose<a name="M0.0033"></a>,</p>\r\n<p class="gatha2">Vivajjetvānasiñcayo;</p>\r\n<p class="gatha3">Kumāraṃcatussaṭhikā,</p>\r\n<p class="gathalast">Upaṭṭhāpesidhātiyo.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> Alambatthaniyocetā,</p>\r\n<p class="gatha2">Bhirūpāyobbaneṭhitā;</p>\r\n<p class="gatha3">Āsuṃmadhurathaññāyo,</p>\r\n<p class="gathalast">Saṃsuddhāseṭṭhakoliyo.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-76-77-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',34,replace(replace('<p class="gatha1">Sususaṭṭhisahassānaṃ<a name="M0.0034"></a>,</p>\r\n<p class="gatha2">Ekekādhātiyotathā;</p>\r\n<p class="gatha3">Buddhaṅkurasajātānaṃ,</p>\r\n<p class="gathalast">Upaṭṭhāpesisuṃndarā.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> Saddhiṃ saṭṭhisahassehi,</p>\r\n<p class="gatha2">Kumārehimahāyaso;</p>\r\n<p class="gatha3">Mahatāparivārena,</p>\r\n<p class="gathalast">Saṃvaḍḍhibodhisattavo.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> Rājāsatasahassaggha-</p>\r\n<p class="gatha2">Nikaṃ susupiḷandhanaṃ;</p>\r\n<p class="gatha3">Kārāpetvāvibhūsesi,</p>\r\n<p class="gathalast">Attajaṃdānanandanaṃ.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-78-76-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',35,replace(replace('<p class="gatha1">Omuñcitvānadhātīnaṃ<a name="M0.0035"></a>,</p>\r\n<p class="gatha2">Taṃcatuppañcavassiko;</p>\r\n<p class="gatha3">Adāsisomahāsatto,</p>\r\n<p class="gathalast">Bodhiṃ apekkhamānaso.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> Punataṃtāhidhātīhi,</p>\r\n<p class="gatha2">Diyyamānaṃpisādaraṃ;</p>\r\n<p class="gatha3">Nasogaṇhitthakumāro,</p>\r\n<p class="gathalast">Dinnesuanapekhavā.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-81-82-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',36,replace(replace('<p class="gatha1">Ārociṃsupavattiṃtaṃ<a name="M0.0036"></a>,</p>\r\n<p class="gatha2">Raññotādhātiyotadā;</p>\r\n<p class="gatha3">Sutvāmuditacittena,</p>\r\n<p class="gathalast">Taṃsothomitthaattajaṃ.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> Puttenamamadinnaṃtaṃ,</p>\r\n<p class="gatha2">Brahmadeyyaṃpiḷandhanaṃ;</p>\r\n<p class="gatha3">Tumhākaṃsantakaṃhotu,</p>\r\n<p class="gathalast">Iccāpibravisiñcayo.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> Punāparaṃpikāretvā,</p>\r\n<p class="gatha2">Pādāsiputtanandano;</p>\r\n<p class="gatha3">Puttassadātukāmassa,</p>\r\n<p class="gathalast">Suvicittapiḷandhanaṃ.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-83-84-85-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',37,replace(replace('<p class="gatha1">Omuñcitvānaetaṃpi<a name="M0.0037"></a>,</p>\r\n<p class="gatha2">Dhātīnaṃdāsidāyako;</p>\r\n<p class="gatha3">Dārakotuṭṭhacittena,</p>\r\n<p class="gathalast">Sabbaññutappiyāyano.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> Evaṃvutta nayeneva,</p>\r\n<p class="gatha2">Nava vāre piḷandhanaṃ;</p>\r\n<p class="gatha3">Daharodāsi dhātīnaṃ,</p>\r\n<p class="gathalast">Nāthānātha parāyano.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-86-87-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',38,replace(replace('<p class="gatha1">Aṭṭhavassi <a name="M0.0038"></a> kakāletu,</p>\r\n<p class="gatha2">Evaṃcintesi buddhimā;</p>\r\n<p class="gatha3">Nisinno sirisayano,</p>\r\n<p class="gathalast">Pāsādo parimaṇḍale.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> Demibāhira dānaṃva,</p>\r\n<p class="gatha2">Natāva mama mānasaṃ;</p>\r\n<p class="gatha3">Parito setievañhi,</p>\r\n<p class="gathalast">Ajjhattikaṃdadāmahaṃ.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> Sacemaṃ kociyāceyya,</p>\r\n<p class="gatha2">Sīsaṃsa moḷimuttadhaṃ;</p>\r\n<p class="gatha3">Chijja taṃ tassadajjeyyaṃ,</p>\r\n<p class="gathalast">Naheyyaṃ līnacetaso.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-88-89-90-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',39,replace(replace('<p class="gatha1">Hadayaṃmama <a name="M0.0039"></a> yāceyya,</p>\r\n<p class="gatha2">Bhinditvā asināuraṃ;</p>\r\n<p class="gatha3">Nīharitvānataṃtassa,</p>\r\n<p class="gathalast">Dajjeyyaṃ tuṭṭhamānasā.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> Akkhīni mamayāceyya,</p>\r\n<p class="gatha2">Uppāṭetvāna tāvade;</p>\r\n<p class="gatha3">Satthena tassadajjeyyaṃ,</p>\r\n<p class="gathalast">Salohitāni tānime.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-91-92-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',40,replace(replace('<p class="gatha1">Hatthepādecakaṇṇeca<a name="M0.0040"></a>,</p>\r\n<p class="gatha2">Yāceyyamamasādhukaṃ;</p>\r\n<p class="gatha3">Chinditvā tānidajjeyyaṃ,</p>\r\n<p class="gathalast">Tassapūre manorathaṃ.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> Maṃsaṃmekociyāceyya,</p>\r\n<p class="gatha2">Sīghaṃsabbasarīrato;</p>\r\n<p class="gatha3">Khaṇḍākhaṇḍaṃ chinditvāna,</p>\r\n<p class="gathalast">Dajje maṃsaṃ salohitaṃ.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> Hohitvaṃmamadāsoti,</p>\r\n<p class="gatha2">Vadeyyakocicetahiṃ;</p>\r\n<p class="gatha3">Sayaṃāmātisāvetvā,</p>\r\n<p class="gathalast">Attānaṃpi dadāmahaṃ.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-93-94-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',41,replace(replace('<p class="gatha1">Tassevaṃ <a name="M0.0041"></a> cintayantassa,</p>\r\n<p class="gatha2">Kampittha pathavīmahā;</p>\r\n<p class="gatha3">Samantāgajjamānāyaṃ,</p>\r\n<p class="gathalast">Mattova kuñjarovane.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> Merurājo namitvāna-</p>\r\n<p class="gatha2">Bhimukhova jetuttaraṃ;</p>\r\n<p class="gatha3">Nagaraṃ ṭhāsi serita-</p>\r\n<p class="gathalast">Vettaṅkurova tāvade.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-96-97-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',42,replace(replace('<p class="gatha1">Mahīsaddasantāsena<a name="M0.0042"></a>,</p>\r\n<p class="gatha2">Meghopāvassi tāvade;</p>\r\n<p class="gatha3">Ghanavassaṃmahādhāraṃ,</p>\r\n<p class="gathalast">Gajjamāno disodisaṃ.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> Nicchariṃsu bahūvijju-</p>\r\n<p class="gatha2">Latāyoca samantato;</p>\r\n<p class="gatha3">Indacāpā uṭṭhahiṃsu,</p>\r\n<p class="gathalast">Ukkāpāto tadā ahu.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> Apphoṭesi tahiṃdeva-</p>\r\n<p class="gatha2">Rājātuṭṭho sujampati;</p>\r\n<p class="gatha3">Sādhusādhūti sāvesi,</p>\r\n<p class="gathalast">Mahābrahmā katañcalī.</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-98-99-100-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',43,replace(replace('<p class="gatha1">Brahmalokā <a name="M0.0043"></a> tadāyāva,</p>\r\n<p class="gatha2">Itopathavi maṇḍalā;</p>\r\n<p class="gatha3">Accheraṃ abbhutaṃ āsi,</p>\r\n<p class="gathalast">Ekakolāhalaṃ mahā.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> Evaṃñhi nekabbhūtāni,</p>\r\n<p class="gatha2">Janetvāna mahājanaṃ;</p>\r\n<p class="gatha3">Hāsayaṃ susukhaṃvaḍḍha-</p>\r\n<p class="gathalast">Mānosoḷasa vassiko.</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-101-102-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',44,replace(replace('<p class="gatha1">Kumāro <a name="M0.0044"></a> sabbasibbesu,</p>\r\n<p class="gatha2">Kovidoca pāraṅgato;</p>\r\n<p class="gatha3">Phullasālova sobhanto,</p>\r\n<p class="gathalast">Vaṇṇena vayasā tadā.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> Athassa dātukāmova,</p>\r\n<p class="gatha2">Rajjaṃmantiya deviyā;</p>\r\n<p class="gatha3">Pitāsi siñcirājatte,</p>\r\n<p class="gathalast">Puttaṃsoḷasa vassikaṃ.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> Maddaraṭṭhe tadāmadda-</p>\r\n<p class="gatha2">Rājakulā bhivaṇṇiniṃ;</p>\r\n<p class="gatha3">Netvāmahe sittemaddiṃ-</p>\r\n<p class="gathalast">Bhisiñci dhammacāriniṃ.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-103-104-105-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',45,replace(replace('<p class="gatha1">Maddīmātuladhītāsā<a name="M0.0045"></a>,</p>\r\n<p class="gatha2">Bhaginī mandahāsinī;</p>\r\n<p class="gatha3">Vessantarakumārassa,</p>\r\n<p class="gathalast">Seṭṭhalakkhaṇadhārinī.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> Pubbauṭṭhāyinī maddī,</p>\r\n<p class="gatha2">Niccaṃ pacchānipātinī;</p>\r\n<p class="gatha3">Manāpacārinī devī,</p>\r\n<p class="gathalast">Kiṃkāra paṭisāvinī.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-106-107-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',46,replace(replace('<p class="gatha1">Soḷasitthi <a name="M0.0046"></a> sahassātaṃ,</p>\r\n<p class="gatha2">Maddiṃsabbaṅga sobhaṇaṃ;</p>\r\n<p class="gatha3">Naccavādita gītehi,</p>\r\n<p class="gathalast">Paricāriṃsu sabbadā.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> Vessantaro mahārājā,</p>\r\n<p class="gatha2">Rajjebhi sittakālato;</p>\r\n<p class="gatha3">Paṭhāya dānasālāyo,</p>\r\n<p class="gathalast">Chaddānaṃ dāsikāriya.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> Mahādānaṃ pavattesi,</p>\r\n<p class="gatha2">Vissajjanto dinedine;</p>\r\n<p class="gatha3">Sammodamāno chassata-</p>\r\n<p class="gathalast">Sahassāni dhanāniso.</p>\r\n<p class="hangnum"><a name="para110"></a><span class="paranum">110</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-108-109-110-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',47,replace(replace('<p class="gatha1">Evaṃdāne <a name="M0.0047"></a> ramantassa,</p>\r\n<p class="gatha2">Mahesītassarājino;</p>\r\n<p class="gatha3">Puttaṃvijāyiapara-</p>\r\n<p class="gathalast">Bhāgemaddī sudassanaṃ.</p>\r\n<p class="hangnum"><a name="para111"></a><span class="paranum">111</span>.</p>\r\n<p class="gatha1"> Sampaṭicchiṃsu vijāta-</p>\r\n<p class="gatha2">Kāletaṃ soṇṇasannitaṃ;</p>\r\n<p class="gatha3">Soṇṇa jālenatenassa,</p>\r\n<p class="gathalast">Nāmaṃjālīti voharuṃ.</p>\r\n<p class="hangnum"><a name="para112"></a><span class="paranum">112</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-111-112-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',48,replace(replace('<p class="gatha1">Padasā <a name="M0.0048"></a> gamane kāle,</p>\r\n<p class="gatha2">Puttassa tassajālino;</p>\r\n<p class="gatha3">Vijāyi dhītaraṃekaṃ,</p>\r\n<p class="gathalast">Nārīnaṃ seṭṭhaṃsammataṃ.</p>\r\n<p class="hangnum"><a name="para113"></a><span class="paranum">113</span>.</p>\r\n<p class="gatha1"> Kaṇhājinena dhītaṃtaṃ,</p>\r\n<p class="gatha2">Sampaṭicchiṃsu sādaraṃ;</p>\r\n<p class="gatha3">Tenassā dhituyānāmaṃ,</p>\r\n<p class="gathalast">Kaṇhājināti voharuṃ.</p>\r\n<p class="hangnum"><a name="para114"></a><span class="paranum">114</span>.</p>\r\n<p class="gatha1"> Mahatā parivārena,</p>\r\n<p class="gatha2">Vuddhiṃnvāyiṃsu teduve;</p>\r\n<p class="gatha3">Sayantā naccagītena,</p>\r\n<p class="gathalast">Naccagīta pabodhanā.</p>\r\n<p class="hangnum"><a name="para115"></a><span class="paranum">115</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-113-114-115-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',49,replace(replace('<p class="gatha1">Mahāsattohi <a name="M0.0049"></a> māsassa,</p>\r\n<p class="gatha2">Chakkhattuṃ dānamaṇḍape;</p>\r\n<p class="gatha3">Olokesi alaṅkata-</p>\r\n<p class="gathalast">Hatthikkhandha vareṭhito.</p>\r\n<p class="hangnum"><a name="para116"></a><span class="paranum">116</span>.</p>\r\n<p class="gatha1"> Tahiṃ kaliṅkaraṭṭhamhi,</p>\r\n<p class="gatha2">Dubbuṭṭhikā bhayānakā;</p>\r\n<p class="gatha3">Sassāni nasampajjiṃsu,</p>\r\n<p class="gathalast">Dubbhikkho samayoahu.</p>\r\n<p class="hangnum"><a name="para117"></a><span class="paranum">117</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-116-117-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',50,replace(replace('<p class="gatha1">Corakammaṃ <a name="M0.0050"></a> akariṃsu,</p>\r\n<p class="gatha2">Asakkontāva jīvituṃ;</p>\r\n<p class="gatha3">Janā nigamagāmesu,</p>\r\n<p class="gathalast">Vilumpanti dāmarikā.</p>\r\n<p class="hangnum"><a name="para118"></a><span class="paranum">118</span>.</p>\r\n<p class="gatha1"> Kaliṅkaraṭṭhaṃ sakalaṃ,</p>\r\n<p class="gatha2">Dubbhikkhabhayapīḷitaṃ;</p>\r\n<p class="gatha3">Ghaṭe vilolapārīva,</p>\r\n<p class="gathalast">Saṅkhumbhittha anāthakaṃ.</p>\r\n<p class="hangnum"><a name="para119"></a><span class="paranum">119</span>.</p>\r\n<p class="gatha1"> Negamā jānapadāsabbe,</p>\r\n<p class="gatha2">Nāgarāca samaggatā;</p>\r\n<p class="gatha3">Rājaṅgaṇe ukkosiṃsu,</p>\r\n<p class="gathalast">Dubbhikkhabhaya coditā.</p>\r\n<p class="hangnum"><a name="para120"></a><span class="paranum">120</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-118-119-120-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',51,replace(replace('<p class="gatha1">Tadāsutvāna <a name="M0.0051"></a> taṃrājā,</p>\r\n<p class="gatha2">Sīghaṃubbigga mānasā;</p>\r\n<p class="gatha3">Koyaṃsaddoti pucchittha,</p>\r\n<p class="gathalast">Mahāmacce sakantike.</p>\r\n<p class="hangnum"><a name="para121"></a><span class="paranum">121</span>.</p>\r\n<p class="gatha1"> Ārocayiṃsu rājānaṃ,</p>\r\n<p class="gatha2">Pavattiṃ taṃasesato;</p>\r\n<p class="gatha3">Rājāpi paṭijānāsi,</p>\r\n<p class="gathalast">Meghaṃ vassāpaye-iti.</p>\r\n<p class="hangnum"><a name="para122"></a><span class="paranum">122</span>.</p>\r\n<p class="gatha1"> Tatoso patthanaṃkāsi,</p>\r\n<p class="gatha2">Samādiya uposathaṃ;</p>\r\n<p class="gatha3">Devaṃ vassāpanatthāya,</p>\r\n<p class="gathalast">Sattāhaṃsattakāruṇo.</p>\r\n<p class="hangnum"><a name="para123"></a><span class="paranum">123</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-121-122-123-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',52,replace(replace('<p class="gatha1">Nahievaṃpisosakkhi<a name="M0.0052"></a>,</p>\r\n<p class="gatha2">Vassāpetuṃ narissaro;</p>\r\n<p class="gatha3">Meghaṃkaliṅka raṭṭhamhi,</p>\r\n<p class="gathalast">Dubbuṭṭhikāva jāyahi.</p>\r\n<p class="hangnum"><a name="para124"></a><span class="paranum">124</span>.</p>\r\n<p class="gatha1"> Tatoso sannipātetvā,</p>\r\n<p class="gatha2">Nāgare etadabravī;</p>\r\n<p class="gatha3">Vasāpetuṃ nasakkomi,</p>\r\n<p class="gathalast">Devaṃ kiṃ nukare-iti.</p>\r\n<p class="hangnum"><a name="para125"></a><span class="paranum">125</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-124-125-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',53,replace(replace('<p class="gatha1">Tadāevamavociṃsu<a name="M0.0053"></a>,</p>\r\n<p class="gatha2">Narākaliṅkarājino;</p>\r\n<p class="gatha3">Devaṃ vassāpanatthāya,</p>\r\n<p class="gathalast">Subhikkhakālakāmino.</p>\r\n<p class="hangnum"><a name="para126"></a><span class="paranum">126</span>.</p>\r\n<p class="gatha1"> Siviraṭṭhemahārāja,</p>\r\n<p class="gatha2">Rajjedhammenayoṭhito;</p>\r\n<p class="gatha3">Jetuttaremahāvessa-</p>\r\n<p class="gathalast">Ntaro siñcayaoraso.</p>\r\n<p class="hangnum"><a name="para127"></a><span class="paranum">127</span>.</p>\r\n<p class="gatha1"> Dānesubhiratohesa,</p>\r\n<p class="gatha2">Kiramaṅgalakuñjaro;</p>\r\n<p class="gatha3">Tassatthipaccayonāma,</p>\r\n<p class="gathalast">Pavarosabbapaṇḍaro.</p>\r\n<p class="hangnum"><a name="para128"></a><span class="paranum">128</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-126-127-128-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',54,replace(replace('<p class="gatha1">Gataṭhānekuñjarassa<a name="M0.0054"></a>,</p>\r\n<p class="gatha2">Tassekantenakhattiya;</p>\r\n<p class="gatha3">Pāvassikiragajjanto,</p>\r\n<p class="gathalast">Mahāmeghosavijjuko.</p>\r\n<p class="hangnum"><a name="para129"></a><span class="paranum">129</span>.</p>\r\n<p class="gatha1"> Pesetvābrāhmāṇerāja,</p>\r\n<p class="gatha2">Yācāpetuṃsupaṇḍaraṃ;</p>\r\n<p class="gatha3">Hatthiṃvaṭṭatitaṃtena,</p>\r\n<p class="gathalast">Sīghaṃpesehibrāhmaṇe.</p>\r\n<p class="hangnum"><a name="para130"></a><span class="paranum">130</span>.</p>\r\n<p class="gatha1"> Sampaṭicchiyasādhūti,</p>\r\n<p class="gatha2">Sutvātaṃtuṭṭhamānaso;</p>\r\n<p class="gatha3">Rājaṅgaṇamhirājāso,</p>\r\n<p class="gathalast">Sannipātesibrāhmaṇe.</p>\r\n<p class="hangnum"><a name="para131"></a><span class="paranum">131</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-129-130-131-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',55,replace(replace('<p class="gatha1">Guṇavaṇṇehisampanne<a name="M0.0055"></a>,</p>\r\n<p class="gatha2">Vicinitvānabrahmaṇe;</p>\r\n<p class="gatha3">Aṭṭhatesurājātesaṃ,</p>\r\n<p class="gathalast">Nekaṃdāsiparibbayaṃ.</p>\r\n<p class="hangnum"><a name="para132"></a><span class="paranum">132</span>.</p>\r\n<p class="gatha1"> Ānethakuñjaraṃbhonto,</p>\r\n<p class="gatha2">Vessantarassasantikaṃ;</p>\r\n<p class="gatha3">Gantvāyāciya-evaṃte,</p>\r\n<p class="gathalast">Rājāvatvānapesayiṃ.</p>\r\n<p class="hangnum"><a name="para133"></a><span class="paranum">133</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-132-133-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',56,replace(replace('<p class="gatha1">Brāhmāṇā <a name="M0.0056"></a> aṭṭharājānaṃ,</p>\r\n<p class="gatha2">Tekatvāna padakkhiṇaṃ;</p>\r\n<p class="gatha3">Gañchiṃsu jetuttaraṃ nāgaṃ,</p>\r\n<p class="gathalast">Nethuṃ sabbaṅgasundaraṃ.</p>\r\n<p class="hangnum"><a name="para134"></a><span class="paranum">134</span>.</p>\r\n<p class="gatha1"> Anupubbena gantvāna,</p>\r\n<p class="gatha2">Tepāpuṇiṃsu brāhmaṇā;</p>\r\n<p class="gatha3">Jetuttaraṃ bhuñjamānā,</p>\r\n<p class="gathalast">Dānasālāsu acchayuṃ.</p>\r\n<p class="hangnum"><a name="para135"></a><span class="paranum">135</span>.</p>\r\n<p class="gatha1"> Tatopuṇṇa madinamhi,</p>\r\n<p class="gatha2">Katvāsarīramattano;</p>\r\n<p class="gatha3">Tepaṃsumakkhitaṃāguṃ,</p>\r\n<p class="gathalast">Pācīnadvāramaṇḍalaṃ.</p>\r\n<p class="hangnum"><a name="para136"></a><span class="paranum">136</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-134-135-136-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',57,replace(replace('<p class="gatha1">Saṃyācissāmanāganti<a name="M0.0057"></a>,</p>\r\n<p class="gatha2">Cintamānāvarājino;</p>\r\n<p class="gatha3">Āgamentāāgamanaṃ,</p>\r\n<p class="gathalast">Tatthaacchiṃ subrāhmāṇā.</p>\r\n<p class="hangnum"><a name="para137"></a><span class="paranum">137</span>.</p>\r\n<p class="gatha1"> Dānaggaṃ olekeyyanti,</p>\r\n<p class="gatha2">Tadāsura sabhojano;</p>\r\n<p class="gatha3">Pātonūtvā mahāsatto,</p>\r\n<p class="gathalast">Alaṅkārena laṅkari.</p>\r\n<p class="hangnum"><a name="para138"></a><span class="paranum">138</span>.</p>\r\n<p class="gatha1"> Alaṅkata hatthikkhandhaṃ,</p>\r\n<p class="gatha2">Aruyha agamātato;</p>','\r',char(13)),'\n',char(10)),'-137-138-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',58,replace(replace('<p class="gatha3">Rājā <a name="M0.0058"></a> puratthimaṃ dvāraṃ,</p>\r\n<p class="gathalast">Parisāya mahantiyā.</p>\r\n<p class="hangnum"><a name="para139"></a><span class="paranum">139</span>.</p>\r\n<p class="gatha1"> Mahantiṃ parisaṃdisvā,</p>\r\n<p class="gatha2">Bhitā ubbigga mānasā;</p>\r\n<p class="gatha3">Tatthokāsaṃ nalabhiṃsu,</p>\r\n<p class="gathalast">Yācituṃ brāhmaṇā tahiṃ.</p>\r\n<p class="hangnum"><a name="para140"></a><span class="paranum">140</span>.</p>\r\n<p class="gatha1"> Tamhā dakkhiṇadvāraṃ te,</p>\r\n<p class="gatha2">Saṅkamitvāna unnate;</p>\r\n<p class="gatha3">Padese seṭṭharājānaṃ,</p>\r\n<p class="gathalast">Āgamesuṃ ṭhitā tato.</p>\r\n<p class="hangnum"><a name="para141"></a><span class="paranum">141</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-139-140-141-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',59,replace(replace('<p class="gatha1">Oloketvāna <a name="M0.0059"></a> pācīna-</p>\r\n<p class="gatha2">Dāramhi dānamaṇḍape;</p>\r\n<p class="gatha3">Hatthikkhandhagato rājā,</p>\r\n<p class="gathalast">Dvāramā gañcidakkhiṇaṃ.</p>\r\n<p class="hangnum"><a name="para142"></a><span class="paranum">142</span>.</p>\r\n<p class="gatha1"> Rājānaṃ āgataṃ disvā,</p>\r\n<p class="gatha2">Pasāretvāna brāhmaṇā;</p>\r\n<p class="gatha3">Hatthe ‘‘vessantarojetu’’</p>\r\n<p class="gathalast">Tikkhattuṃ itibhāsiṃsu.</p>\r\n<p class="hangnum"><a name="para143"></a><span class="paranum">143</span>.</p>\r\n<p class="gatha1"> Jayasaddaṃ panādante,</p>\r\n<p class="gatha2">Rājādisvāna brāhmaṇe;</p>\r\n<p class="gatha3">Hatthiṃ tesaṃ ṭhitaṭhānaṃ,</p>\r\n<p class="gathalast">Pājesi dānamānasā.</p>\r\n<p class="hangnum"><a name="para144"></a><span class="paranum">144</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-142-143-144-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',60,replace(replace('<p class="gatha1">Hatthikkhandhe <a name="M0.0060"></a> nisinnova,</p>\r\n<p class="gatha2">Pasannena mukhenaso;</p>\r\n<p class="gatha3">Mhitapubbaṃ tatovoca,</p>\r\n<p class="gathalast">Girevaṃ hadayaṅgamaṃ.</p>\r\n<p class="hangnum"><a name="para145"></a><span class="paranum">145</span>.</p>\r\n<p class="gatha1"> Dakkhiṇāvaṭṭasaṅkhena,</p>\r\n<p class="gatha2">Bāhuṃpaggayha dakkhiṇa;</p>\r\n<p class="gatha3">Purato mama tiṭṭhantā,</p>\r\n<p class="gathalast">Kiṃ maṃyācanti brāhmaṇā.</p>\r\n<p class="hangnum"><a name="para146"></a><span class="paranum">146</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-145-146-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',61,replace(replace('<p class="gatha1">Evaṃsīghama <a name="M0.0061"></a> vociṃsu,</p>\r\n<p class="gatha2">Pahaṭṭhā brāhmaṇā tahiṃ;</p>\r\n<p class="gatha3">Ratanaṃrāja yācāma,</p>\r\n<p class="gathalast">Kuñjaraṃ sabbapaṇḍaraṃ.</p>\r\n<p class="hangnum"><a name="para147"></a><span class="paranum">147</span>.</p>\r\n<p class="gatha1"> Idaṃsutvānasocinti,</p>\r\n<p class="gatha2">Ajjhattikaṃ pidātave;</p>\r\n<p class="gatha3">Ahamicchāmitedāni,</p>\r\n<p class="gathalast">Mamaṃ yācanti bāhiraṃ.</p>\r\n<p class="hangnum"><a name="para148"></a><span class="paranum">148</span>.</p>\r\n<p class="gatha1"> Idāni pūrayissāmi,</p>\r\n<p class="gatha2">Yācantānaṃ manorathaṃ;</p>','\r',char(13)),'\n',char(10)),'-147-148-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',62,replace(replace('<p class="gatha3">Evaṃcintiyasobhāsi<a name="M0.0062"></a>,</p>\r\n<p class="gathalast">Hatthikkhandhaṭhito iti.</p>\r\n<p class="hangnum"><a name="para149"></a><span class="paranum">149</span>.</p>\r\n<p class="gatha1"> Demitaṃ navikampāmi,</p>\r\n<p class="gatha2">Yaṃmaṃyācanti brāhmaṇā;</p>\r\n<p class="gatha3">Kelāsa sadisaṃ setaṃ,</p>\r\n<p class="gathalast">Alaṅkataṃ gajuttamaṃ.</p>\r\n<p class="hangnum"><a name="para150"></a><span class="paranum">150</span>.</p>\r\n<p class="gatha1"> Hatthikkhandhā tato ruyha,</p>\r\n<p class="gatha2">Rājā cāgādhimānaso;</p>\r\n<p class="gatha3">Olokesitikkhattuṃtaṃ,</p>\r\n<p class="gathalast">Katvā nāgaṃ padakkhiṇaṃ.</p>\r\n<p class="hangnum"><a name="para151"></a><span class="paranum">151</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-149-150-151-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',63,replace(replace('<p class="gatha1">Gahetvā <a name="M0.0063"></a> soṇṇabhiṅgāraṃ,</p>\r\n<p class="gatha2">Sugandhodaka pūritaṃ;</p>\r\n<p class="gatha3">Ahvāyi etha bhontoti,</p>\r\n<p class="gathalast">Kuñjaraṃesa dātave.</p>\r\n<p class="hangnum"><a name="para152"></a><span class="paranum">152</span>.</p>\r\n<p class="gatha1"> Gahetvā rajatadāma-</p>\r\n<p class="gatha2">Sadisaṃ seta hattino;</p>\r\n<p class="gatha3">Soṇḍaṃṭṭhapiya hatthesu,</p>\r\n<p class="gathalast">Brāhmaṇānaṃ gajissaro.</p>\r\n<p class="hangnum"><a name="para153"></a><span class="paranum">153</span>.</p>\r\n<p class="gatha1"> Sālaṅkataṃ mahānāgaṃ,</p>\r\n<p class="gatha2">Pātetvā dakkhiṇodakaṃ;</p>\r\n<p class="gatha3">Adāsi seṭṭhadantiṃtaṃ,</p>\r\n<p class="gathalast">Asallinena cetasā.</p>\r\n<p class="hangnum"><a name="para154"></a><span class="paranum">154</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-152-153-154-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',64,replace(replace('<p class="gatha1">Saddhiṃ <a name="M0.0064"></a> vīsatilakkhena,</p>\r\n<p class="gatha2">Catulakkhāni agghati;</p>\r\n<p class="gatha3">Alaṅkārohi nāgassa,</p>\r\n<p class="gathalast">Nānāratanacittito.</p>\r\n<p class="hangnum"><a name="para155"></a><span class="paranum">155</span>.</p>\r\n<p class="gatha1"> Athāpianagghā honti,</p>\r\n<p class="gatha2">Chaḷeva aṅkusādisu;</p>\r\n<p class="gatha3">Maṇayo vāraṇocāpi,</p>\r\n<p class="gathalast">Anaṅgho sattanagghikā.</p>\r\n<p class="hangnum"><a name="para156"></a><span class="paranum">156</span>.</p>\r\n<p class="gatha1"> Yatāvutta ratanehi,</p>\r\n<p class="gatha2">Saddhiṃ adāsi kuñjaraṃ;</p>\r\n<p class="gatha3">Nāgopiyopi teneva,</p>\r\n<p class="gathalast">Tato sabbaññutaṃ piyaṃ.</p>\r\n<p class="hangnum"><a name="para156"></a><span class="paranum">156</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-155-156-156-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',65,replace(replace('<p class="gatha1">Athāpi <a name="M0.0065"></a> pañcasatāni,</p>\r\n<p class="gatha2">Paricārāni hatthino;</p>\r\n<p class="gatha3">Kulāni hatthigopehi,</p>\r\n<p class="gathalast">Saddhiṃ adāsi sādaraṃ.</p>\r\n<p class="hangnum"><a name="para158"></a><span class="paranum">158</span>.</p>\r\n<p class="gatha1"> Vessantarena dinnamhi,</p>\r\n<p class="gatha2">Seta maṅgala kuñjare;</p>\r\n<p class="gatha3">Acetanāpinādentī,</p>\r\n<p class="gathalast">Medanī sampa kampatha.</p>\r\n<p class="hangnum"><a name="para159"></a><span class="paranum">159</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-158-159-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',66,replace(replace('<p class="gatha1">Tahiṃ <a name="M0.0066"></a> bhīsanakaṃ āsi,</p>\r\n<p class="gatha2">Nānaṃ lomahaṃsanaṃ;</p>\r\n<p class="gatha3">Mahanto vipulo ghoso,</p>\r\n<p class="gathalast">Khumbhittha sakalaṃ puraṃ.</p>\r\n<p class="hangnum"><a name="para160"></a><span class="paranum">160</span>.</p>\r\n<p class="gatha1"> Mahīsaddasantāsena,</p>\r\n<p class="gatha2">Megho pāvassi tāvade;</p>\r\n<p class="gatha3">Ghanavassaṃ mahādhāraṃ,</p>\r\n<p class="gathalast">Gajjamāno diso disaṃ.</p>\r\n<p class="hangnum"><a name="para161"></a><span class="paranum">161</span>.</p>\r\n<p class="gatha1"> Nicchariṃsu bahūvijju-</p>\r\n<p class="gatha2">Latā yoca samantato;</p>\r\n<p class="gatha3">Indacāpā uṭṭhahīṃsu,</p>\r\n<p class="gathalast">Ukkāpāto tadā ahu.</p>\r\n<p class="hangnum"><a name="para162"></a><span class="paranum">162</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-160-161-162-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',67,replace(replace('<p class="gatha1">Hatthiṃ <a name="M0.0067"></a> laddhānate tuṭṭhā,</p>\r\n<p class="gatha2">Brāhmaṇā kiradakkhiṇā-</p>\r\n<p class="gatha3">Dvārā nagara majjhena,</p>\r\n<p class="gathalast">Gajakkhandha gatānayuṃ.</p>\r\n<p class="hangnum"><a name="para163"></a><span class="paranum">163</span>.</p>\r\n<p class="gatha1"> Mahājanaparivāre,</p>\r\n<p class="gatha2">Gajāruḷheṭṭha brāhmaṇe;</p>\r\n<p class="gatha3">Passiṃ sunāgarā sabbe,</p>\r\n<p class="gathalast">Tadāte etadabravuṃ.</p>\r\n<p class="hangnum"><a name="para164"></a><span class="paranum">164</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-163-164-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',68,replace(replace('<p class="gatha1">Amhākaṃ <a name="M0.0068"></a> kuñjaraṃ hambho,</p>\r\n<p class="gatha2">Āruḷhā vo idhāgatā;</p>\r\n<p class="gatha3">Dinnoyaṃ kena tumhākaṃ,</p>\r\n<p class="gathalast">Kadā laddho kuto-iti.</p>\r\n<p class="hangnum"><a name="para165"></a><span class="paranum">165</span>.</p>\r\n<p class="gatha1"> Vessantarena dinno no,</p>\r\n<p class="gatha2">Kathaṃtumhe vadissatha;</p>\r\n<p class="gatha3">Nakatheyyāma vitthāraṃ,</p>\r\n<p class="gathalast">Gacchema nagaraṃ mayaṃ.</p>\r\n<p class="hangnum"><a name="para166"></a><span class="paranum">166</span>.</p>\r\n<p class="gatha1"> Evaṃvatvā nikkhamiṃsu,</p>\r\n<p class="gatha2">Dvārena uttarenate;</p>','\r',char(13)),'\n',char(10)),'-165-166-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',69,replace(replace('<p class="gatha3">Cittaṃvilola <a name="M0.0069"></a> mānāva,</p>\r\n<p class="gathalast">Nāgarānaṃ kaliṅkajā.</p>\r\n<p class="hangnum"><a name="para167"></a><span class="paranum">167</span>.</p>\r\n<p class="gatha1"> Tadāhi nāgarā sabbe,</p>\r\n<p class="gatha2">Bodhisattassa kujjhitā;</p>\r\n<p class="gatha3">Rājadvāre sannipacca,</p>\r\n<p class="gathalast">Upakkosamakaṃsu te.</p>\r\n<p class="hangnum"><a name="para168"></a><span class="paranum">168</span>.</p>\r\n<p class="gatha1"> Adhammenevabhonttono,</p>\r\n<p class="gatha2">Nāgaṃ raṭṭhassa pūjitaṃ;</p>\r\n<p class="gatha3">Adāvessantaroduṭṭha-</p>\r\n<p class="gathalast">Brāhmaṇānamalaṅkataṃ.</p>\r\n<p class="hangnum"><a name="para169"></a><span class="paranum">169</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-167-168-169-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',70,replace(replace('<p class="gatha1">Saṅkhumbhitacittā <a name="M0.0070"></a> sabbe,</p>\r\n<p class="gatha2">Tatonagaravāsino;</p>\r\n<p class="gatha3">Gantvā siñcayarājassa,</p>\r\n<p class="gathalast">Santikaṃ evamabravuṃ.</p>\r\n<p class="hangnum"><a name="para170"></a><span class="paranum">170</span>.</p>\r\n<p class="gatha1"> Vidhamaṃdevateraṭṭhaṃ,</p>\r\n<p class="gatha2">Puttovessantarotava;</p>\r\n<p class="gatha3">Kataṃsokuñjaraṃdāsi,</p>\r\n<p class="gathalast">Siviraṭṭhassapūjitaṃ.</p>\r\n<p class="hangnum"><a name="para171"></a><span class="paranum">171</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-170-171-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',71,replace(replace('<p class="gatha1">Kathaṃso <a name="M0.0071"></a> vāraṇaṃdāsi,</p>\r\n<p class="gatha2">Setaṃ kelāsasannibhaṃ;</p>\r\n<p class="gatha3">Paṇḍukampalasañchannaṃ,</p>\r\n<p class="gathalast">Jaya bhūmi vijanānaṃ.</p>\r\n<p class="hangnum"><a name="para172"></a><span class="paranum">172</span>.</p>\r\n<p class="gatha1"> Sace so dātumiccheyya,</p>\r\n<p class="gatha2">Annaṃpāṇañcabhojanaṃ;</p>\r\n<p class="gatha3">Vatthaṃ senāsanaṃ soṇṇaṃ,</p>\r\n<p class="gathalast">Yuttaṃdātuṃ yathicchitaṃ.</p>\r\n<p class="hangnum"><a name="para173"></a><span class="paranum">173</span>.</p>\r\n<p class="gatha1"> Sacetvaṃ nakarissasi,</p>\r\n<p class="gatha2">Sivīnaṃ vacanaṃidaṃ;</p>\r\n<p class="gatha3">Maññetaṃ sahaputtena,</p>\r\n<p class="gathalast">Sivīhatthe karissare.</p>\r\n<p class="hangnum"><a name="para174"></a><span class="paranum">174</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-172-173-174-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',72,replace(replace('<p class="gatha1">Tajjamānāhi <a name="M0.0072"></a> evañhi,</p>\r\n<p class="gatha2">Sutvā sivīhi bhāsitaṃ;</p>\r\n<p class="gatha3">Maññittha siñcayo puttaṃ,</p>\r\n<p class="gathalast">Māretuṃ icchareiti.</p>\r\n<p class="hangnum"><a name="para175"></a><span class="paranum">175</span>.</p>\r\n<p class="gatha1"> Evaṃbyākāsi teneva,</p>\r\n<p class="gatha2">Sīghaṃvibbhanta mānaso;</p>\r\n<p class="gatha3">Puttapema codito so,</p>\r\n<p class="gathalast">Sokasalla samappito.</p>\r\n<p class="hangnum"><a name="para176"></a><span class="paranum">176</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-175-176-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',73,replace(replace('<p class="gatha1">Kāmaṃ <a name="M0.0073"></a> janapado sabbaṃ,</p>\r\n<p class="gatha2">Raṭṭhañcāpi vinassatu;</p>\r\n<p class="gatha3">Nāhaṃ sivīnaṃvacanā,</p>\r\n<p class="gathalast">Pabbājeyyaṃ mamatrajaṃ.</p>\r\n<p class="hangnum"><a name="para177"></a><span class="paranum">177</span>.</p>\r\n<p class="gatha1"> Kathaṃ haṃ tamhi dubbheyyaṃ,</p>\r\n<p class="gatha2">Santoso suddhacetaso;</p>\r\n<p class="gatha3">Puttaṃkathañca niddosaṃ,</p>\r\n<p class="gathalast">Satthena ghātaye mama.</p>\r\n<p class="hangnum"><a name="para178"></a><span class="paranum">178</span>.</p>\r\n<p class="gatha1"> Sivayo tassa sutvāna,</p>\r\n<p class="gatha2">Bhāsitaṃ puttagiddhino;</p>\r\n<p class="gatha3">Avocuṃ visaṭṭhā vīta-</p>\r\n<p class="gathalast">Bhahāevaṃ yathātathaṃ.</p>\r\n<p class="hangnum"><a name="para179"></a><span class="paranum">179</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-177-178-179-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',74,replace(replace('<p class="gatha1">Mānaṃ <a name="M0.0074"></a> daṇḍena satthena,</p>\r\n<p class="gatha2">Nāpi so bandhanāraho;</p>\r\n<p class="gatha3">Pabbājehi vanaṃ raṭṭhā,</p>\r\n<p class="gathalast">Vaṅke vasatu pabbate.</p>\r\n<p class="hangnum"><a name="para180"></a><span class="paranum">180</span>.</p>\r\n<p class="gatha1"> Tadācintayi sorājā,</p>\r\n<p class="gatha2">Nayuttaṃ panūdetave;</p>\r\n<p class="gatha3">Chandaṃ hi sivinaṃ dāni,</p>\r\n<p class="gathalast">Tato voca subhāsitaṃ.</p>\r\n<p class="hangnum"><a name="para181"></a><span class="paranum">181</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-180-181-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',75,replace(replace('<p class="gatha1">Yathāchandaṃ <a name="M0.0075"></a> kareyyāmi,</p>\r\n<p class="gatha2">Raṭṭha pabbājayetha taṃ;</p>\r\n<p class="gatha3">Ekokāsañca yācāma,</p>\r\n<p class="gathalast">Rattiṃ so vasataṃ imaṃ.</p>\r\n<p class="hangnum"><a name="para182"></a><span class="paranum">182</span>.</p>\r\n<p class="gatha1"> Amantetu yathākāmaṃ,</p>\r\n<p class="gatha2">Kaṇhājināya jālinā;</p>\r\n<p class="gatha3">Tathāca maddiyā saddhiṃ,</p>\r\n<p class="gathalast">Kāmeca paribhuñjatu.</p>\r\n<p class="hangnum"><a name="para183"></a><span class="paranum">183</span>.</p>\r\n<p class="gatha1"> Tato ratyā vivasāne,</p>\r\n<p class="gatha2">Sūriyuggamanesati;</p>\r\n<p class="gatha3">Samaggā sivayo hutvā,</p>\r\n<p class="gathalast">Raṭṭhā pabbajayantu taṃ.</p>\r\n<p class="hangnum"><a name="para184"></a><span class="paranum">184</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-182-183-184-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',76,replace(replace('<p class="gatha1">Paccāgañchuṃ <a name="M0.0076"></a> tadā sabbe,</p>\r\n<p class="gatha2">Sivayo tuṭṭhamānasā;</p>\r\n<p class="gatha3">Sampaṭicchitvāna sādhūti,</p>\r\n<p class="gathalast">Vacanaṃ sivirājino.</p>\r\n<p class="hangnum"><a name="para185"></a><span class="paranum">185</span>.</p>\r\n<p class="gatha1"> Tatoso siñcayoekaṃ,</p>\r\n<p class="gatha2">Kattāraṃ puttasantikaṃ;</p>\r\n<p class="gatha3">Sāsanaṃ pesanatthāya,</p>\r\n<p class="gathalast">Turitaṃ evamabravī.</p>\r\n<p class="hangnum"><a name="para186"></a><span class="paranum">186</span>.</p>\r\n<p class="gatha1"> Uṭṭhehi katte taramāno,</p>\r\n<p class="gatha2">Gantvā vassantaraṃ vada;</p>\r\n<p class="gatha3">Sivayo deva tekuddhā,</p>\r\n<p class="gathalast">Negamāca samāgatā.</p>\r\n<p class="hangnum"><a name="para187"></a><span class="paranum">187</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-185-186-187-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',77,replace(replace('<p class="gatha1">Asmā <a name="M0.0077"></a> ratyā vivasāne,</p>\r\n<p class="gatha2">Sūriyuggamane sati;</p>\r\n<p class="gatha3">Samaggā sivayo hutvā,</p>\r\n<p class="gathalast">Raṭṭhā pabbājayantitaṃ.</p>\r\n<p class="hangnum"><a name="para188"></a><span class="paranum">188</span>.</p>\r\n<p class="gatha1"> Sakattā taramānova,</p>\r\n<p class="gatha2">Sivirājena pesito;</p>\r\n<p class="gatha3">Upāgami puraṃrammaṃ,</p>\r\n<p class="gathalast">Vessantara nivesanaṃ.</p>\r\n<p class="hangnum"><a name="para189"></a><span class="paranum">189</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-188-189-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',78,replace(replace('<p class="gatha1">Tatthaddasa <a name="M0.0078"></a> kuraṃso,</p>\r\n<p class="gatha2">Ramamānaṃ sake pure;</p>\r\n<p class="gatha3">Parikiṇṇaṃ amaccehi,</p>\r\n<p class="gathalast">Tidasānaṃva vāsavaṃ.</p>\r\n<p class="hangnum"><a name="para190"></a><span class="paranum">190</span>.</p>\r\n<p class="gatha1"> Vanditvā rodamānoso,</p>\r\n<p class="gatha2">Kattā vessantaraṃ bravī;</p>\r\n<p class="gatha3">Dukkhaṃ te vedayissāmi,</p>\r\n<p class="gathalast">Māmekujjha rathesabha.</p>\r\n<p class="hangnum"><a name="para191"></a><span class="paranum">191</span>.</p>\r\n<p class="gatha1"> Asmāratyā vivasāne,</p>\r\n<p class="gatha2">Sūriyuggamane sati;</p>\r\n<p class="gatha3">Sivayo deva tekuddhā,</p>\r\n<p class="gathalast">Raṭṭhā pabbājayantitaṃ.</p>\r\n<p class="hangnum"><a name="para192"></a><span class="paranum">192</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-190-191-192-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',79,replace(replace('<p class="gatha1">Sutvāna <a name="M0.0079"></a> bhāsitaṃ etaṃ,</p>\r\n<p class="gatha2">Hasamāno pamodayaṃ;</p>\r\n<p class="gatha3">Evaṃpucchittha kattāraṃ,</p>\r\n<p class="gathalast">Vessantaro sudhītimā.</p>\r\n<p class="hangnum"><a name="para193"></a><span class="paranum">193</span>.</p>\r\n<p class="gatha1"> Kismiṃ me sivayo kuddhā,</p>\r\n<p class="gatha2">Yaṃ napassāmi dukkaṭaṃ;</p>\r\n<p class="gatha3">Taṃme katte viyācikkha,</p>\r\n<p class="gathalast">Kasmā pabbājayanti maṃ.</p>\r\n<p class="hangnum"><a name="para194"></a><span class="paranum">194</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-193-194-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',80,replace(replace('<p class="gatha1">Tahiṃ <a name="M0.0080"></a> kattā idaṃvoca,</p>\r\n<p class="gatha2">Hatthidānena kujjhare;</p>\r\n<p class="gatha3">Khīyanti sivayo rāja,</p>\r\n<p class="gathalast">Tasmā pabbājayanti taṃ.</p>\r\n<p class="hangnum"><a name="para195"></a><span class="paranum">195</span>.</p>\r\n<p class="gatha1"> Taṃsutvāna mahāsatto,</p>\r\n<p class="gatha2">Dānesu thīramānaso;</p>\r\n<p class="gatha3">Vacanaṃ mhitapubbaṃ so,</p>\r\n<p class="gathalast">Kattāraṃ etadabravī.</p>\r\n<p class="hangnum"><a name="para196"></a><span class="paranum">196</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-195-196-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',81,replace(replace('<p class="gatha1">Hadayaṃ <a name="M0.0081"></a> cakkhumahaṃ dajjaṃ,</p>\r\n<p class="gatha2">Bāhuṃpi dakkhiṇaṃ mama;</p>\r\n<p class="gatha3">Hirañña maṇisoṇṇādiṃ,</p>\r\n<p class="gathalast">Nakiṃ bāhirakaṃ dhanaṃ.</p>\r\n<p class="hangnum"><a name="para197"></a><span class="paranum">197</span>.</p>\r\n<p class="gatha1"> Kāmaṃmaṃsi vayosabbe,</p>\r\n<p class="gatha2">Pabbājentu hanantuvā;</p>\r\n<p class="gatha3">Nevadānā viramissaṃ,</p>\r\n<p class="gathalast">Kāmaṃchindantu sattadhā.</p>\r\n<p class="hangnum"><a name="para198"></a><span class="paranum">198</span></p>\r\n<p class="gatha1"> Tatoso devatāviṭṭho,</p>\r\n<p class="gatha2">Kattāmaggama desayi;</p>\r\n<p class="gatha3">Pabbājitānaṃ sabbesaṃ,</p>\r\n<p class="gathalast">Gatapubbaṃ purāṇakaṃ.</p>\r\n<p class="hangnum"><a name="para199"></a><span class="paranum">199</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-197-198-199-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',82,replace(replace('<p class="gatha1">Kontīmārāya <a name="M0.0082"></a> tīrena,</p>\r\n<p class="gatha2">Gīrimā rañcaraṃ pati;</p>\r\n<p class="gatha3">Yenapabbājitāyanti,</p>\r\n<p class="gathalast">Tenagacchatu subbato.</p>\r\n<p class="hangnum"><a name="para200"></a><span class="paranum">200</span>.</p>\r\n<p class="gatha1"> Taṃsutvāna bodhisatto,</p>\r\n<p class="gatha2">Sādhūti sampaṭicchiya;</p>\r\n<p class="gatha3">Ekokāsaṃ nāgarānaṃ,</p>\r\n<p class="gathalast">Yācetuṃ evamabravī.</p>\r\n<p class="hangnum"><a name="para201"></a><span class="paranum">201</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-200-201-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',83,replace(replace('<p class="gatha1">Dānaṃsattasatakaṃhaṃ<a name="M0.0083"></a>,</p>\r\n<p class="gatha2">Kattedajjaṃ suvetato;</p>\r\n<p class="gatha3">Parasve nikkhamissāmi,</p>\r\n<p class="gathalast">Rattiṃdivaṃ khamantume.</p>\r\n<p class="hangnum"><a name="para202"></a><span class="paranum">202</span>.</p>\r\n<p class="gatha1"> Sādhudevapavakkhāmi,</p>\r\n<p class="gatha2">Khamāpetuṃnisādivaṃ;</p>\r\n<p class="gatha3">Nāgarānaṃtivatvāna,</p>\r\n<p class="gathalast">Tadākattāpipakkami.</p>\r\n<p class="hangnum"><a name="para203"></a><span class="paranum">203</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-202-203-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',84,replace(replace('<p class="gatha1">Mahāsattomahāyena- <a name="M0.0084"></a></p>\r\n<p class="gatha2">Kuttaṃsakalakammikaṃ;</p>\r\n<p class="gatha3">Pakkosāviyasvedānaṃ,</p>\r\n<p class="gathalast">Evaṃbyākāsidātave.</p>\r\n<p class="hangnum"><a name="para204"></a><span class="paranum">204</span>.</p>\r\n<p class="gatha1"> Hatthīasserateitthī,</p>\r\n<p class="gatha2">Dāsīdāsecadhenuyo;</p>\r\n<p class="gatha3">Paṭiyādehitvaṃsatta-</p>\r\n<p class="gathalast">Satecāgāyamārisa.</p>\r\n<p class="hangnum"><a name="para205"></a><span class="paranum">205</span>.</p>\r\n<p class="gatha1"> Athonānappakārāni,</p>\r\n<p class="gatha2">Annapānānisabbaso;</p>\r\n<p class="gatha3">Suraṃpi paṭiyādehi,</p>\r\n<p class="gathalast">Sabbaṃdātabbayuttakaṃ.</p>\r\n<p class="hangnum"><a name="para206"></a><span class="paranum">206</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-204-205-206-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',85,replace(replace('<p class="gatha1">Evaṃsobyākaritvāna<a name="M0.0085"></a>,</p>\r\n<p class="gatha2">Pemacittenacodito;</p>\r\n<p class="gatha3">Ekovamaddiyārammaṃ,</p>\r\n<p class="gathalast">Pāsādamabhirūhatha.</p>\r\n<p class="hangnum"><a name="para207"></a><span class="paranum">207</span>.</p>\r\n<p class="gatha1"> Rattacandana gandhehi,</p>\r\n<p class="gatha2">Gandhodakehivāsitaṃ;</p>\r\n<p class="gatha3">Sopāvekkhisirīgabbhaṃ,</p>\r\n<p class="gathalast">Maddīdevīnivāsanaṃ.</p>\r\n<p class="hangnum"><a name="para208"></a><span class="paranum">208</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-207-208-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',86,replace(replace('<p class="gatha1">Maddīdisvānaāyantaṃ<a name="M0.0086"></a>,</p>\r\n<p class="gatha2">Mhitānanenasāmikaṃ;</p>\r\n<p class="gatha3">Uṭṭhāsiāsanāsīghaṃ,</p>\r\n<p class="gathalast">Reṇumattāvakinnarī.</p>\r\n<p class="hangnum"><a name="para209"></a><span class="paranum">209</span>.</p>\r\n<p class="gatha1"> Uṭṭhāhitvānasāmaddī,</p>\r\n<p class="gatha2">Vāmahatthesu rājino;</p>\r\n<p class="gatha3">Dakkhiṇe nasahatthena,</p>\r\n<p class="gathalast">Gaṇhitthamandahāsinī.</p>\r\n<p class="hangnum"><a name="para210"></a><span class="paranum">210</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-209-210-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',87,replace(replace('<p class="gatha1">Tatosirisayanamhi<a name="M0.0087"></a>,</p>\r\n<p class="gatha2">Nasinnaṃsakasāmikaṃ;</p>\r\n<p class="gatha3">Bījayantīvasāṭṭhāsi,</p>\r\n<p class="gathalast">Maddīsaññatavāsinī.</p>\r\n<p class="hangnum"><a name="para211"></a><span class="paranum">211</span>.</p>\r\n<p class="gatha1"> Maddiṃaṅkeṭṭhapetvātha,</p>\r\n<p class="gatha2">Gaṇhaṃhattesudeviyā;</p>\r\n<p class="gatha3">Mukhaṃmukhenakatvāna,</p>\r\n<p class="gathalast">Rājāmandamabhāsatha.</p>\r\n<p class="hangnum"><a name="para212"></a><span class="paranum">212</span>.</p>\r\n<p class="gatha1"> Yaṃtesacemayādinnaṃ,</p>\r\n<p class="gatha2">Yañcatepettikaṃdhanaṃ;</p>\r\n<p class="gatha3">Sabbaṃtaṃnidaheyyāsi,</p>\r\n<p class="gathalast">Bhaddekomārapemike.</p>\r\n<p class="hangnum"><a name="para213"></a><span class="paranum">213</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-211-212-213-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',88,replace(replace('<p class="gatha1">Tahiṃnavuttapubbaṃme<a name="M0.0088"></a>,</p>\r\n<p class="gatha2">Sāmikenamamīdisaṃ;</p>\r\n<p class="gatha3">Kadācietthakaṃkālaṃ,</p>\r\n<p class="gathalast">Maddīevañhicintayi.</p>\r\n<p class="hangnum"><a name="para214"></a><span class="paranum">214</span>.</p>\r\n<p class="gatha1"> Tato ubbiggacittena,</p>\r\n<p class="gatha2">Maddīeva mabhāsatha;</p>\r\n<p class="gatha3">Kuhiṃ deva nidahāmi,</p>\r\n<p class="gathalast">Taṃme akkhāhi pucchito.</p>\r\n<p class="hangnum"><a name="para215"></a><span class="paranum">215</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-214-215-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',89,replace(replace('<p class="gatha1">Sīlavantesu <a name="M0.0089"></a> dujjāsi,</p>\r\n<p class="gatha2">Dānaṃ maddī yathārahaṃ;</p>\r\n<p class="gatha3">Nahidānā paraṃatthi,</p>\r\n<p class="gathalast">Patiṭṭhā sabbapāṇinaṃ.</p>\r\n<p class="hangnum"><a name="para216"></a><span class="paranum">216</span>.</p>\r\n<p class="gatha1"> Nidhānaṃ nāma etaṃva,</p>\r\n<p class="gatha2">Dhanānaṃ nandivaḍḍhane;</p>\r\n<p class="gatha3">Dānaṃhi nidhi sattānaṃ,</p>\r\n<p class="gathalast">Evaṃbyākāsi khattiyo.</p>\r\n<p class="hangnum"><a name="para217"></a><span class="paranum">217</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-216-217-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',90,replace(replace('<p class="gatha1">Maddīvatvāna <a name="M0.0090"></a> sādhūti,</p>\r\n<p class="gatha2">Onamitvā siruttamaṃ;</p>\r\n<p class="gatha3">Sampaṭicchi tato rājā,</p>\r\n<p class="gathalast">Punāpi evamabravī.</p>\r\n<p class="hangnum"><a name="para218"></a><span class="paranum">218</span>.</p>\r\n<p class="gatha1"> Jālimhimaddidayesi,</p>\r\n<p class="gatha2">Sādhukaṇhājināyaca;</p>\r\n<p class="gatha3">Gāravaṃnivātaṃkāsi,</p>\r\n<p class="gathalast">Sassuyāsassuramhica.</p>\r\n<p class="hangnum"><a name="para219"></a><span class="paranum">219</span>.</p>\r\n<p class="gatha1"> Yohitaṃ pema cittena,</p>\r\n<p class="gatha2">Mayāvippavasena te;</p>\r\n<p class="gatha3">Icche cebhavituṃ attā,</p>\r\n<p class="gathalast">Sakkaccaṃ taṃupaṭṭhahe.</p>\r\n<p class="hangnum"><a name="para220"></a><span class="paranum">220</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-218-219-220-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',91,replace(replace('<p class="gatha1">Sace <a name="M0.0091"></a> evaṃ nabhaveyya,</p>\r\n<p class="gatha2">Bhattāraṃ mādisaṃ viyaṃ;</p>\r\n<p class="gatha3">Parire sehi aññaṃ tvaṃ,</p>\r\n<p class="gathalast">Māki ssittho mayāvinā.</p>\r\n<p class="hangnum"><a name="para221"></a><span class="paranum">221</span>.</p>\r\n<p class="gatha1"> Tadāhi dummukhī maddī,</p>\r\n<p class="gatha2">Rājāna meva mabravī;</p>\r\n<p class="gatha3">Dussutaṃ vata suṇomi,</p>\r\n<p class="gathalast">Īdisaṃ kinnubhāsasi.</p>\r\n<p class="hangnum"><a name="para222"></a><span class="paranum">222</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-221-222-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',92,replace(replace('<p class="gatha1">Viyācikkhāmi <a name="M0.0092"></a> mebha dde,</p>\r\n<p class="gatha2">Sakalaṃ dāni kāriṇaṃ;</p>\r\n<p class="gatha3">Hatthidānenakujjhanti,</p>\r\n<p class="gathalast">Samaggā sivayo mama.</p>\r\n<p class="hangnum"><a name="para223"></a><span class="paranum">223</span>.</p>\r\n<p class="gatha1"> Pabbājenti mamaṃ tena,</p>\r\n<p class="gatha2">Sivayo siviraṭṭhato;</p>\r\n<p class="gatha3">Mahādānaṃ dadissāmi,</p>\r\n<p class="gathalast">Suve komārasaṅgame.</p>\r\n<p class="hangnum"><a name="para224"></a><span class="paranum">224</span>.</p>\r\n<p class="gatha1"> Parasve nikkhamissāmi,</p>\r\n<p class="gatha2">Raṭṭhā ekova sobhaṇe;</p>\r\n<p class="gatha3">Nānābhayehi saṃkiṇṇaṃ</p>\r\n<p class="gathalast">Vaṅkaṃ gacchāmi pabbata.</p>\r\n<p class="hangnum"><a name="para225"></a><span class="paranum">225</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-223-224-225-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',93,replace(replace('<p class="gatha1">Pāṇīkatapiye <a name="M0.0093"></a> jāliṃ,</p>\r\n<p class="gatha2">Kaṇhājinañca attajaṃ;</p>\r\n<p class="gatha3">Tañcaohāya gaccheyyaṃ,</p>\r\n<p class="gathalast">Vaseyyaṃ ekakovane.</p>\r\n<p class="hangnum"><a name="para226"></a><span class="paranum">226</span>.</p>\r\n<p class="gatha1"> Rañño sutvāna saṅkampi,</p>\r\n<p class="gatha2">Vacanaṃsokavaḍḍhanaṃ;</p>\r\n<p class="gatha3">Māluteritapattaṃva,</p>\r\n<p class="gathalast">Maddīyā hadayaṃ tadā.</p>\r\n<p class="hangnum"><a name="para227"></a><span class="paranum">227</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-226-227-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',94,replace(replace('<p class="gatha1">Māmamevamavacuttha<a name="M0.0094"></a>,</p>\r\n<p class="gatha2">Kampesi hadayaṃ mama;</p>\r\n<p class="gatha3">Tattatelenasittaṃva,</p>\r\n<p class="gathalast">Sariraṃ rāja dayhate.</p>\r\n<p class="hangnum"><a name="para228"></a><span class="paranum">228</span>.</p>\r\n<p class="gatha1"> Nesadhammo mahārāja,</p>\r\n<p class="gatha2">Yaṃ tvaṃ gaccheyya ekako;</p>\r\n<p class="gatha3">Ahaṃpi tena gacchāmi,</p>\r\n<p class="gathalast">Yenagacchasi khattiya.</p>\r\n<p class="hangnum"><a name="para229"></a><span class="paranum">229</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-228-229-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',95,replace(replace('<p class="gatha1">Maraṇaṃvā <a name="M0.0095"></a> tayā saddhiṃ,</p>\r\n<p class="gatha2">Jīvitaṃvā tayā vinā;</p>\r\n<p class="gatha3">Tadeva maraṇaṃ seyyo,</p>\r\n<p class="gathalast">Yañcejīve tayāpinā.</p>\r\n<p class="hangnum"><a name="para230"></a><span class="paranum">230</span>.</p>\r\n<p class="gatha1"> Aggiṃ ujjālayitvāna,</p>\r\n<p class="gatha2">Ekajāla samāhitaṃ;</p>\r\n<p class="gatha3">Tatthe vamaraṇaṃ seyyo,</p>\r\n<p class="gathalast">Yañce jīve tayā vinā.</p>\r\n<p class="hangnum"><a name="para231"></a><span class="paranum">231</span>.</p>\r\n<p class="gatha1"> Carantā raññanāgaṃva,</p>\r\n<p class="gatha2">Duggesvanvetti hatthinī;</p>','\r',char(13)),'\n',char(10)),'-230-231-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',96,replace(replace('<p class="gatha3">Evaṃtaṃ <a name="M0.0096"></a> anugacchāmi,</p>\r\n<p class="gathalast">Putteādāyapacchato.</p>\r\n<p class="hangnum"><a name="para232"></a><span class="paranum">232</span>.</p>\r\n<p class="gatha1"> Vimaṃsetuṃ tadā rājā,</p>\r\n<p class="gatha2">Mānasaṃ maddideviyā;</p>\r\n<p class="gatha3">Nānābhayaṃ pakāsento,</p>\r\n<p class="gathalast">Evaṃbravi bhayāpayaṃ.</p>\r\n<p class="hangnum"><a name="para233"></a><span class="paranum">233</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-232-233-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',97,replace(replace('<p class="gatha1">Viyācikkhāmi <a name="M0.0097"></a> tesaccaṃ,</p>\r\n<p class="gatha2">Vanamhi mudumānase;</p>\r\n<p class="gatha3">Nānābhayaṃ kharaṃ ghoraṃ,</p>\r\n<p class="gathalast">Hesmaṃ hadayakampanaṃ.</p>\r\n<p class="hangnum"><a name="para234"></a><span class="paranum">234</span>.</p>\r\n<p class="gatha1"> Kesarīnāma yebhadde,</p>\r\n<p class="gatha2">Migarājā kharassarā;</p>\r\n<p class="gatha3">Hiṃ santiṃ te sasaddena,</p>\r\n<p class="gathalast">Migetikkhagga dāṭhino.</p>\r\n<p class="hangnum"><a name="para235"></a><span class="paranum">235</span>.</p>\r\n<p class="gatha1"> Dīpībyagghākaṇhācchāca,</p>\r\n<p class="gatha2">Tikkhagga nakha dhārino;</p>','\r',char(13)),'\n',char(10)),'-234-235-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',98,replace(replace('<p class="gatha3">Khaggā <a name="M0.0098"></a> vanamahiṃsāca,</p>\r\n<p class="gathalast">Tikkhaggasiṅgadhārino.</p>\r\n<p class="hangnum"><a name="para236"></a><span class="paranum">236</span>.</p>\r\n<p class="gatha1"> Tikkhagga nakha siṅgehi,</p>\r\n<p class="gatha2">Ete posaṃpi chindiya;</p>\r\n<p class="gatha3">Khaṇḍākhaṇḍaṃ karitvāna,</p>\r\n<p class="gathalast">Mūlālaṃviya bhakkhare.</p>\r\n<p class="hangnum"><a name="para237"></a><span class="paranum">237</span>.</p>\r\n<p class="gatha1"> Yācayakkhiniyoyakkhā,</p>\r\n<p class="gatha2">Bahūmanussakhādakā;</p>\r\n<p class="gatha3">Naraṃnāriṃ gavesanti,</p>\r\n<p class="gathalast">Dummukhā khaggapāṇino.</p>\r\n<p class="hangnum"><a name="para238"></a><span class="paranum">238</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-236-237-238-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',99,replace(replace('<p class="gatha1">Atho <a name="M0.0099"></a> ajagarāsappā,</p>\r\n<p class="gatha2">Ghoravīsāca vijjare;</p>\r\n<p class="gatha3">Viṃsācā rakkhasāvāḷā,</p>\r\n<p class="gathalast">Sajjulohitabhojanā.</p>\r\n<p class="hangnum"><a name="para139"></a><span class="paranum">139</span>.</p>\r\n<p class="gatha1"> Aññepi bahavosanti,</p>\r\n<p class="gatha2">Bhayābhesmakārakā;</p>\r\n<p class="gatha3">Kiṃnabhāyasi etesaṃ,</p>\r\n<p class="gathalast">Bhayānaṃ bhayamānase.</p>\r\n<p class="hangnum"><a name="para240"></a><span class="paranum">240</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-139-240-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',100,replace(replace('<p class="gatha1">Mā <a name="M0.0100"></a> mamaṃ tvaṃ nugacchāhi,</p>\r\n<p class="gatha2">Gacchantaṃ bhayasaṃyutaṃ;</p>\r\n<p class="gatha3">Vanaṃ idheva acchāhi,</p>\r\n<p class="gathalast">Vuttehisahabhīruke.</p>\r\n<p class="hangnum"><a name="para141"></a><span class="paranum">141</span>.</p>\r\n<p class="gatha1"> Tvekadāsirigabbhamhi,</p>\r\n<p class="gatha2">Vasantī sahame subhe;</p>\r\n<p class="gatha3">Sutvāgavesitāṇaṅke,</p>\r\n<p class="gathalast">Kiṃme mañjāragajjitaṃ.</p>\r\n<p class="hangnum"><a name="para242"></a><span class="paranum">242</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-141-242-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',101,replace(replace('<p class="gatha1">Tvekadākiṃvibodhesi<a name="M0.0101"></a>,</p>\r\n<p class="gatha2">Bhemibhemīti bhāsiya;</p>\r\n<p class="gatha3">Rattiyaṃ hi sayantaṃ maṃ,</p>\r\n<p class="gathalast">Sutvā nuluṅkavassitaṃ.</p>\r\n<p class="hangnum"><a name="para243"></a><span class="paranum">243</span>.</p>\r\n<p class="gatha1"> Tvekadā meghasaddaṃhi,</p>\r\n<p class="gatha2">Sutvādivā mamantikaṃ;</p>\r\n<p class="gatha3">Dhāvitvāmaṃ parissajja,</p>\r\n<p class="gathalast">Kiṃ mucchilomahaṃsīnī.</p>\r\n<p class="hangnum"><a name="para144"></a><span class="paranum">144</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-243-144-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',102,replace(replace('<p class="gatha1">Evaṃbhaya <a name="M0.0102"></a> cañcalāya,</p>\r\n<p class="gatha2">Sukhumālāya deviyā;</p>\r\n<p class="gatha3">Nālaṃhi vasituṃraññe,</p>\r\n<p class="gathalast">Bhesme avanavāsike.</p>\r\n<p class="hangnum"><a name="para245"></a><span class="paranum">245</span>.</p>\r\n<p class="gatha1"> Maddīsutvāna rājassa,</p>\r\n<p class="gatha2">Bhāsittaṃ thīramānasā;</p>\r\n<p class="gatha3">Visāradena cittena,</p>\r\n<p class="gathalast">Sāmikaṃ evamabravī.</p>\r\n<p class="hangnum"><a name="para246"></a><span class="paranum">246</span>.</p>\r\n<p class="gatha1"> Tayime pema cittañca,</p>\r\n<p class="gatha2">Araññe bhayamānasaṃ;</p>','\r',char(13)),'\n',char(10)),'-245-246-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',103,replace(replace('<p class="gatha3">Tayitesumahārāja<a name="M0.0103"></a>,</p>\r\n<p class="gathalast">Sutikkhaṃ pema cetasaṃ.</p>\r\n<p class="hangnum"><a name="para247"></a><span class="paranum">247</span>.</p>\r\n<p class="gatha1"> Sandate sīghasotaṃme,</p>\r\n<p class="gatha2">Tayisāgara sannibhe;</p>\r\n<p class="gatha3">Niccaṃpemodakaṃrāja,</p>\r\n<p class="gathalast">Gaṅgodakaṃva sāgare.</p>\r\n<p class="hangnum"><a name="para248"></a><span class="paranum">248</span>.</p>\r\n<p class="gatha1"> Maraṇaṃpemacittena,</p>\r\n<p class="gatha2">Jīvitaṃ bhaya cetasā;</p>\r\n<p class="gatha3">Tadeva maraṇaṃseyyo,</p>\r\n<p class="gathalast">Yañcetaṃ jīvitaṃciraṃ.</p>\r\n<p class="hangnum"><a name="para249"></a><span class="paranum">249</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-247-248-249-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',104,replace(replace('<p class="gatha1">Pākāraṃ <a name="M0.0104"></a> mamūraṃrāja,</p>\r\n<p class="gatha2">Katvāna puratovanaṃ;</p>\r\n<p class="gatha3">Nānābhayaṃ nivārentī,</p>\r\n<p class="gathalast">Gacchaṃ haṃverahiṃsinī.</p>\r\n<p class="hangnum"><a name="para250"></a><span class="paranum">250</span>.</p>\r\n<p class="gatha1"> Tayihaṃ pemacittāsiṃ,</p>\r\n<p class="gatha2">Gahettvā vaṅkapabbate;</p>\r\n<p class="gatha3">Phalāphalaṃgavesantī,</p>\r\n<p class="gathalast">Vaseyyāmi tayāsaha.</p>\r\n<p class="hangnum"><a name="para251"></a><span class="paranum">251</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-250-251-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',105,replace(replace('<p class="gatha1">Evaṃñhi <a name="M0.0105"></a> sūrabhāvaṃ sā,</p>\r\n<p class="gatha2">Dassayitvāna attano;</p>\r\n<p class="gatha3">Himavanta vāsinīva-</p>\r\n<p class="gathalast">Rājānaṃ vītitosayi.</p>\r\n<p class="hangnum"><a name="para252"></a><span class="paranum">252</span>.</p>\r\n<p class="gatha1"> Yadādakkhasinaccante,</p>\r\n<p class="gatha2">Kumāre māladhārine;</p>\r\n<p class="gatha3">Kīḷante assameramme,</p>\r\n<p class="gathalast">Narajjassa sarissasi.</p>\r\n<p class="hangnum"><a name="para253"></a><span class="paranum">253</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-252-253-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',106,replace(replace('<p class="gatha1">Yadādakkhasigāyante<a name="M0.0106"></a>,</p>\r\n<p class="gatha2">Aññoññamukhadassine;</p>\r\n<p class="gatha3">Kumārevanagumbamhi,</p>\r\n<p class="gathalast">Narajjassa sarissasi.</p>\r\n<p class="hangnum"><a name="para254"></a><span class="paranum">254</span>.</p>\r\n<p class="gatha1"> Yadādakkhasimātaṅgaṃ,</p>\r\n<p class="gatha2">Sāyaṃpātaṃbrahāvane;</p>\r\n<p class="gatha3">Asahāyaṃvicarantaṃ,</p>\r\n<p class="gathalast">Narajjassa sarissasi.</p>\r\n<p class="hangnum"><a name="para255"></a><span class="paranum">255</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-254-255-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',107,replace(replace('<p class="gatha1">Nādaṃkareṇusaṅghassa<a name="M0.0107"></a>,</p>\r\n<p class="gatha2">Nadamānassapūrato;</p>\r\n<p class="gatha3">Nāgassavajatosutvā,</p>\r\n<p class="gathalast">Narajjassasarissasi.</p>\r\n<p class="hangnum"><a name="para256"></a><span class="paranum">256</span>.</p>\r\n<p class="gatha1"> Migaṃdisvānasāyanhaṃ,</p>\r\n<p class="gatha2">Pañcamālinamāgataṃ;</p>\r\n<p class="gatha3">Kiṃ pūrisecanaccante,</p>\r\n<p class="gathalast">Narajjhassasarissasi.</p>\r\n<p class="hangnum"><a name="para257"></a><span class="paranum">257</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-256-257-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',108,replace(replace('<p class="gatha1">Yadāsussasinigghosaṃ<a name="M0.0108"></a>,</p>\r\n<p class="gatha2">Sandamānāyasindhuyā;</p>\r\n<p class="gatha3">Gītaṃkiṃpurisānañca,</p>\r\n<p class="gathalast">Narajjassasarissasi.</p>\r\n<p class="hangnum"><a name="para258"></a><span class="paranum">258</span>.</p>\r\n<p class="gatha1"> Saratassacasīhassa,</p>\r\n<p class="gatha2">Byagghassacchassadivino;</p>\r\n<p class="gatha3">Saddaṃ sutvānakhaggassa,</p>\r\n<p class="gathalast">Narajjassasarissasi.</p>\r\n<p class="hangnum"><a name="para259"></a><span class="paranum">259</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-258-259-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',109,replace(replace('<p class="gatha1">Yadāmorīhiparikiṇṇaṃ<a name="M0.0109"></a>,</p>\r\n<p class="gatha2">Vicitrapucchapakkhinaṃ;</p>\r\n<p class="gatha3">Moraṃdakkhasinaccantaṃ,</p>\r\n<p class="gathalast">Narajjassa sarissasi.</p>\r\n<p class="hangnum"><a name="para260"></a><span class="paranum">260</span>.</p>\r\n<p class="gatha1"> Yadādakkhasi hemante,</p>\r\n<p class="gatha2">Pupphitedharaṇīruhe;</p>\r\n<p class="gatha3">Surabbhisampavāyante,</p>\r\n<p class="gathalast">Narajjassa sarissasi.</p>\r\n<p class="hangnum"><a name="para261"></a><span class="paranum">261</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-260-261-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',110,replace(replace('<p class="gatha1">Yadā <a name="M0.0110"></a> hemanti kemāse,</p>\r\n<p class="gatha2">Haritaṃ dakkhasi medaniṃ;</p>\r\n<p class="gatha3">Indagopaka sañchannaṃ,</p>\r\n<p class="gathalast">Narajjassa sarissasi.</p>\r\n<p class="hangnum"><a name="para162"></a><span class="paranum">162</span>.</p>\r\n<p class="gatha1"> Tadāhiphussatīdevī,</p>\r\n<p class="gatha2">Ṭhitāsi puttasokinī;</p>\r\n<p class="gatha3">Sīrīpagabbhassadvāramhi,</p>\r\n<p class="gathalast">Vimaṃsantīkathākathaṃ.</p>\r\n<p class="hangnum"><a name="para263"></a><span class="paranum">263</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-162-263-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',111,replace(replace('<p class="gatha1">Kalunaṃparidevittha<a name="M0.0111"></a>,</p>\r\n<p class="gatha2">Aññoññābhāsitaṃgiraṃ;</p>\r\n<p class="gatha3">Sutvānaphussatīdevī,</p>\r\n<p class="gathalast">Vuttassasuṇisāyaca.</p>\r\n<p class="hangnum"><a name="para264"></a><span class="paranum">264</span>.</p>\r\n<p class="gatha1"> Visaṃmekhāditaṃseyyo,</p>\r\n<p class="gatha2">Pabbatāca papātanaṃ;</p>\r\n<p class="gatha3">Matañcarajjuyābajjha,</p>\r\n<p class="gathalast">Natthatthojīvitename.</p>\r\n<p class="hangnum"><a name="para265"></a><span class="paranum">265</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-264-265-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',112,replace(replace('<p class="gatha1">Ajjhāyakaṃ <a name="M0.0112"></a> dāna patiṃ,</p>\r\n<p class="gatha2">Yasassinaṃ amacchariṃ;</p>\r\n<p class="gatha3">Kasmā vessaraṃ puttaṃ,</p>\r\n<p class="gathalast">Pabbājanti adūsakaṃ.</p>\r\n<p class="hangnum"><a name="para266"></a><span class="paranum">266</span>.</p>\r\n<p class="gatha1"> Pūjitaṃ patirā jūhi,</p>\r\n<p class="gatha2">Sabbalokahi tesinaṃ;</p>\r\n<p class="gatha3">Kasmā vessanttaraṃ vuttaṃ,</p>\r\n<p class="gathalast">Pabbājentiadūsakaṃ.</p>\r\n<p class="hangnum"><a name="para267"></a><span class="paranum">267</span>.</p>\r\n<p class="gatha1"> Kalunaṃ pari devitvā,</p>\r\n<p class="gatha2">Assā setvāna phussatī;</p>\r\n<p class="gatha3">Vuttañca suṇisaṃ sīghaṃ,</p>\r\n<p class="gathalast">Agā siñcaya santikaṃ.</p>\r\n<p class="hangnum"><a name="para268"></a><span class="paranum">268</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-266-267-268-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',113,replace(replace('<p class="gatha1">Tatotaṃ <a name="M0.0113"></a> siñcayaṃdevī,</p>\r\n<p class="gatha2">Visaṭṭhā etadabravī;</p>\r\n<p class="gatha3">Nānūpāyaṃ pakāsentī,</p>\r\n<p class="gathalast">Vicitta vāda viññunī.</p>\r\n<p class="hangnum"><a name="para269"></a><span class="paranum">269</span>.</p>\r\n<p class="gatha1"> Madhūniva palā tāni,</p>\r\n<p class="gatha2">Ambāvapati tāchamā;</p>\r\n<p class="gatha3">Evaṃhe ssatite raṭṭhaṃ,</p>\r\n<p class="gathalast">Pabbājiteadūsake.</p>\r\n<p class="hangnum"><a name="para270"></a><span class="paranum">270</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-269-270-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',114,replace(replace('<p class="gatha1">Haṃso <a name="M0.0114"></a> nikhīṇa pattova,</p>\r\n<p class="gatha2">Pallasmiṃ anūdake;</p>\r\n<p class="gatha3">Apaviṭṭho amaccehi,</p>\r\n<p class="gathalast">Ekorājāvihiyyasi.</p>\r\n<p class="hangnum"><a name="para270"></a><span class="paranum">270</span>.</p>\r\n<p class="gatha1"> Tantaṃbrūmi mahārāja,</p>\r\n<p class="gatha2">Atthotemā upaccagā;</p>\r\n<p class="gatha3">Mānaṃsivīnaṃvacanā,</p>\r\n<p class="gathalast">Pabbājesiadūsakaṃ.</p>\r\n<p class="hangnum"><a name="para172"></a><span class="paranum">172</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-270-172-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',115,replace(replace('<p class="gatha1">Deviyāvacanaṃsutvā<a name="M0.0115"></a>,</p>\r\n<p class="gatha2">Dhammarājādhammañcaro;</p>\r\n<p class="gatha3">Dhammenadhammikaṃbrūsi,</p>\r\n<p class="gathalast">Mahesiṃ puttasokiniṃ.</p>\r\n<p class="hangnum"><a name="para273"></a><span class="paranum">273</span>.</p>\r\n<p class="gatha1"> Esovessantarobhadde,</p>\r\n<p class="gatha2">Pāṇāpiyatarohime;</p>\r\n<p class="gatha3">Tathāpinaṃ pabbājemi,</p>\r\n<p class="gathalast">Dhammasatthavasānugo.</p>\r\n<p class="hangnum"><a name="para274"></a><span class="paranum">274</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-273-274-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',116,replace(replace('<p class="gatha1">Sīviraṭṭhamhiporāṇa- <a name="M0.0116"></a></p>\r\n<p class="gatha2">Rājūnaṃdhammatantiyā;</p>\r\n<p class="gatha3">Ahañhipacitiṃkummi,</p>\r\n<p class="gathalast">Vinayantomamorasaṃ.</p>\r\n<p class="hangnum"><a name="para275"></a><span class="paranum">275</span>.</p>\r\n<p class="gatha1"> Raññotaṃ vacanaṃsutvā,</p>\r\n<p class="gatha2">Khinnāhadayakampinī;</p>\r\n<p class="gatha3">Punasāparidevantī,</p>\r\n<p class="gathalast">Evaṃvilaviphussatī.</p>\r\n<p class="hangnum"><a name="para276"></a><span class="paranum">276</span>.</p>\r\n<p class="gatha1"> Yassapubbedhajaggāni,</p>\r\n<p class="gatha2">Kaṇikārāvapupphitā;</p>\r\n<p class="gatha3">Yāyantamanuyāyanti,</p>\r\n<p class="gathalast">Svajjekovagamissati.</p>\r\n<p class="hangnum"><a name="para277"></a><span class="paranum">277</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-275-276-277-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',117,replace(replace('<p class="gatha1">Indagopakavaṇṇābhā<a name="M0.0117"></a>,</p>\r\n<p class="gatha2">Gandhārā paṇḍukampalā;</p>\r\n<p class="gatha3">Yāyantamanuyāyanti,</p>\r\n<p class="gathalast">Svajjekovagamissasi.</p>\r\n<p class="hangnum"><a name="para278"></a><span class="paranum">278</span>.</p>\r\n<p class="gatha1"> Yopubbehatthināyāti,</p>\r\n<p class="gatha2">Sivikāyarathenaca;</p>\r\n<p class="gatha3">Svajjavessarorājā,</p>\r\n<p class="gathalast">Kathaṃgacchatipatthikā.</p>\r\n<p class="hangnum"><a name="para279"></a><span class="paranum">279</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-278-279-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',118,replace(replace('<p class="gatha1">Kathaṃcandanalittaṅgo<a name="M0.0118"></a>,</p>\r\n<p class="gatha2">Naccagītapabodhano;</p>\r\n<p class="gatha3">Khurājinaṃ pharusañca,</p>\r\n<p class="gathalast">Khārikājañcahāhiti.</p>\r\n<p class="hangnum"><a name="para280"></a><span class="paranum">280</span>.</p>\r\n<p class="gatha1"> Pavīsanto brahāraññaṃ,</p>\r\n<p class="gatha2">Kāsāvaṃ ajinā nivā;</p>\r\n<p class="gatha3">Kharaṃ kusa mayaṃ cīraṃ,</p>\r\n<p class="gathalast">Kathaṃ pari dahissati.</p>\r\n<p class="hangnum"><a name="para281"></a><span class="paranum">281</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-280-281-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',119,replace(replace('<p class="gatha1">Kāsiyānica <a name="M0.0119"></a> dharetvā,</p>\r\n<p class="gatha2">Khomako ṭumparānica;</p>\r\n<p class="gatha3">Kusacīrāni dhārentī,</p>\r\n<p class="gathalast">Kathaṃmaddīkarissati.</p>\r\n<p class="hangnum"><a name="para282"></a><span class="paranum">282</span>.</p>\r\n<p class="gatha1"> Vayhāhi pariyāyitvā,</p>\r\n<p class="gatha2">Sivikāya rathenaca,</p>\r\n<p class="gatha3">Sākathajja anujjhaṅgī,</p>\r\n<p class="gathalast">Pathaṃ gacchati patthikā.</p>\r\n<p class="hangnum"><a name="para283"></a><span class="paranum">283</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-282-283-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',120,replace(replace('<p class="gatha1">Sukhedhitāhihirañña- <a name="M0.0120"></a></p>\r\n<p class="gatha2">Pādukāruḷhagāminī,</p>\r\n<p class="gatha3">Sākathajjasukhānandī,</p>\r\n<p class="gathalast">Pathaṃgacchatipatthikā.</p>\r\n<p class="hangnum"><a name="para284"></a><span class="paranum">284</span>.</p>\r\n<p class="gatha1"> Gantvāitthisahassānaṃ,</p>\r\n<p class="gatha2">Pūratoyāhimālinī;</p>\r\n<p class="gatha3">Sākathajjayasānandī,</p>\r\n<p class="gathalast">Vanaṃgacchatiekikā.</p>\r\n<p class="hangnum"><a name="para285"></a><span class="paranum">285</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-284-285-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',121,replace(replace('<p class="gatha1">Saddaṃsivāyasutvāyā<a name="M0.0121"></a>,</p>\r\n<p class="gatha2">Muhuṃuttassatepure;</p>\r\n<p class="gatha3">Vasantīsābrahāraññe,</p>\r\n<p class="gathalast">Kathaṃ vacchatibhīrukā.</p>\r\n<p class="hangnum"><a name="para289"></a><span class="paranum">289</span>.</p>\r\n<p class="gatha1"> Saddaṃsutvānuluṅkassa,</p>\r\n<p class="gatha2">Muhuṃuttasatepure;</p>\r\n<p class="gatha3">Vasantīsābrahāraññe,</p>\r\n<p class="gathalast">Kathaṃvacchatibhīrukā.</p>\r\n<p class="hangnum"><a name="para287"></a><span class="paranum">287</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-289-287-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',122,replace(replace('<p class="gatha1">Sakuṇīhata <a name="M0.0122"></a> puttāva,</p>\r\n<p class="gatha2">Suññaṃdisvā kulāvakaṃ;</p>\r\n<p class="gatha3">Cīraṃdukkhena jhāyissaṃ,</p>\r\n<p class="gathalast">Suññaṃāgammimaṃpuraṃ.</p>\r\n<p class="hangnum"><a name="para288"></a><span class="paranum">288</span>.</p>\r\n<p class="gatha1"> Kururī hatachāpāva,</p>\r\n<p class="gatha2">Suññaṃdisvā kulāvakaṃ;</p>\r\n<p class="gatha3">Tenatena padhāvissaṃ,</p>\r\n<p class="gathalast">Piyeputteapassatī.</p>\r\n<p class="hangnum"><a name="para289"></a><span class="paranum">289</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-288-289-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',123,replace(replace('<p class="gatha1">Evaṃmevila <a name="M0.0123"></a> pantiyā,</p>\r\n<p class="gatha2">Rājaputtaṃ adūsakaṃ;</p>\r\n<p class="gatha3">Pabbājesi vanaṃraṭṭhā,</p>\r\n<p class="gathalast">Maññehissāmijīvitaṃ.</p>\r\n<p class="hangnum"><a name="para290"></a><span class="paranum">290</span>.</p>\r\n<p class="gatha1"> Phussatyā lavitaṃsutvā,</p>\r\n<p class="gatha2">Sabbā siñcayarā jino;</p>\r\n<p class="gatha3">Bāhāpaggayha pakkanduṃ,</p>\r\n<p class="gathalast">Sivikaññā samāgatā.</p>\r\n<p class="hangnum"><a name="para291"></a><span class="paranum">291</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-290-291-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',124,replace(replace('<p class="gatha1">Pakkanditaravaṃtāsaṃ<a name="M0.0124"></a>,</p>\r\n<p class="gatha2">Sutvā sokapamadditā;</p>\r\n<p class="gatha3">Sivikaññāca pakkanduṃ,</p>\r\n<p class="gathalast">Vessantaranivesane.</p>\r\n<p class="hangnum"><a name="para292"></a><span class="paranum">292</span>.</p>\r\n<p class="gatha1"> Orodhāca kumārāca,</p>\r\n<p class="gatha2">Vesiyānā brahmaṇā;</p>\r\n<p class="gatha3">Bāhāpaggayhapakkanduṃ,</p>\r\n<p class="gathalast">Vessanttaranivesane.</p>\r\n<p class="hangnum"><a name="para293"></a><span class="paranum">293</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-292-293-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',125,replace(replace('<p class="gatha1">Hatthārohāanīkaṭṭhā<a name="M0.0125"></a>,</p>\r\n<p class="gatha2">Rathikā patthikārakā;</p>\r\n<p class="gatha3">Bāhāpaggayha pakkanduṃ,</p>\r\n<p class="gathalast">Vessantara nivesane.</p>\r\n<p class="hangnum"><a name="para294"></a><span class="paranum">294</span>.</p>\r\n<p class="gatha1"> Tassāratyā accayena,</p>\r\n<p class="gatha2">Sūriye uggatesati;</p>\r\n<p class="gatha3">Ārocayiṃsu rājānaṃ,</p>\r\n<p class="gathalast">Dānaggaṃ upagantave.</p>\r\n<p class="hangnum"><a name="para295"></a><span class="paranum">295</span>.</p>\r\n<p class="gatha1"> Athapātova sorājā,</p>\r\n<p class="gatha2">Nhatvābhūsana bhūsito;</p>\r\n<p class="gatha3">Dānasāla mupāgañchi,</p>\r\n<p class="gathalast">Mahājana purakkhato.</p>\r\n<p class="hangnum"><a name="para296"></a><span class="paranum">296</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-294-295-296-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',126,replace(replace('<p class="gatha1">Dānasālāsu <a name="M0.0126"></a> dātabbaṃ,</p>\r\n<p class="gatha2">Disvāna paṭiyāditaṃ;</p>\r\n<p class="gatha3">Amacceva māṇāpesi,</p>\r\n<p class="gathalast">Rājābhi tuṭṭhamānato.</p>\r\n<p class="hangnum"><a name="para297"></a><span class="paranum">297</span>.</p>\r\n<p class="gatha1"> Vattānivatthakāmānaṃ,</p>\r\n<p class="gatha2">Soṇḍānaṃ dethavāruṇiṃ;</p>\r\n<p class="gatha3">Bhojanaṃ bhojanatthinaṃ,</p>\r\n<p class="gathalast">Sammadeva pavacchatha.</p>\r\n<p class="hangnum"><a name="para298"></a><span class="paranum">298</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-297-298-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',127,replace(replace('<p class="gatha1">Yeyaṃyaṃ <a name="M0.0127"></a> laddhumicchanti,</p>\r\n<p class="gatha2">Taṃtaṃtassapavacchatha;</p>\r\n<p class="gatha3">Māyācake nivāretha,</p>\r\n<p class="gathalast">Dānasālāsu āgate.</p>\r\n<p class="hangnum"><a name="para299"></a><span class="paranum">299</span>.</p>\r\n<p class="gatha1"> Tadāsiyaṃ bhīsanakaṃ,</p>\r\n<p class="gatha2">Tadāsi lomahaṃsanaṃ;</p>\r\n<p class="gatha3">Mahādāne padinnamhi</p>\r\n<p class="gathalast">Medinī sampakampatha.</p>\r\n<p class="hangnum"><a name="para300"></a><span class="paranum">300</span>.</p>\r\n<p class="gatha1"> Parideviṃsu vessantta-</p>\r\n<p class="gatha2">Rarājaṃ patitāvade;</p>\r\n<p class="gatha3">Pakittetvāna nānappa-</p>\r\n<p class="gathalast">Kāraṃ evaṃ vaṇibbakā.</p>\r\n<p class="hangnum"><a name="para301"></a><span class="paranum">301</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-299-300-301-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',128,replace(replace('<p class="gatha1">Amhehitvāna <a name="M0.0128"></a> vessanta-</p>\r\n<p class="gatha2">Rarājā dānadāyako;</p>\r\n<p class="gatha3">Acire neva raṭṭhamhā,</p>\r\n<p class="gathalast">Nikkhamissatikānanaṃ.</p>\r\n<p class="hangnum"><a name="para302"></a><span class="paranum">302</span>.</p>\r\n<p class="gatha1"> Sattahatthisatedatvā,</p>\r\n<p class="gatha2">Sabbālaṅkāra bhūsite;</p>\r\n<p class="gatha3">Esavessantaro samhā,</p>\r\n<p class="gathalast">Raṭṭhamhā nikkhamissati.</p>\r\n<p class="hangnum"><a name="para303"></a><span class="paranum">303</span>.</p>\r\n<p class="gatha1"> Sattaassa sate datvā,</p>\r\n<p class="gatha2">Sindhave sīgha vāhane;</p>\r\n<p class="gatha3">Esa vessantarosamhā,</p>\r\n<p class="gathalast">Raṭṭhamhā nikkhamissati.</p>\r\n<p class="hangnum"><a name="para304"></a><span class="paranum">304</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-302-303-304-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',129,replace(replace('<p class="gatha1">Rathesattasatedatvā<a name="M0.0129"></a>,</p>\r\n<p class="gatha2">Sannandhe ussitaddhaje;</p>\r\n<p class="gatha3">Esa vessantarosamhā,</p>\r\n<p class="gathalast">Raṭṭhamhā nikkhamissati.</p>\r\n<p class="hangnum"><a name="para305"></a><span class="paranum">305</span>.</p>\r\n<p class="gatha1"> Sattakaññā satedatvā,</p>\r\n<p class="gatha2">Surūpinī vibhūsitā;</p>\r\n<p class="gatha3">Esa vessantarosamhā,</p>\r\n<p class="gathalast">Raṭṭhamhā nikkhamissati.</p>\r\n<p class="hangnum"><a name="para306"></a><span class="paranum">306</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-305-306-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',130,replace(replace('<p class="gatha1">Sattadhenu <a name="M0.0130"></a> sate datvā,</p>\r\n<p class="gatha2">Sabbākaṃsu padhāraṇā;</p>\r\n<p class="gatha3">Esavessantarorājā,</p>\r\n<p class="gathalast">Samhāraṭṭhāniracchati.</p>\r\n<p class="hangnum"><a name="para307"></a><span class="paranum">307</span>.</p>\r\n<p class="gatha1"> Sattadāsi sate datvā,</p>\r\n<p class="gatha2">Sattadāsa satānica;</p>\r\n<p class="gatha3">Esavessantarorājā,</p>\r\n<p class="gathalast">Samhāraṭṭhāniracchati.</p>\r\n<p class="hangnum"><a name="para308"></a><span class="paranum">308</span>.</p>\r\n<p class="gatha1"> Athetthavattatesaddo,</p>\r\n<p class="gatha2">Tumulobhe ravomahā;</p>\r\n<p class="gatha3">Samākulaṃ puraṃāsi,</p>\r\n<p class="gathalast">Ahosi lomahaṃsanaṃ.</p>\r\n<p class="hangnum"><a name="para309"></a><span class="paranum">309</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-307-308-309-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',131,replace(replace('<p class="gatha1">Sodatvāna <a name="M0.0131"></a> mahādānaṃ,</p>\r\n<p class="gatha2">Vessantaro amaccharī;</p>\r\n<p class="gatha3">Purakkhato amaccehi,</p>\r\n<p class="gathalast">Agāsakanivesanaṃ.</p>\r\n<p class="hangnum"><a name="para310"></a><span class="paranum">310</span>.</p>\r\n<p class="gatha1"> Tatohi maddiyāsaddhiṃ,</p>\r\n<p class="gatha2">Mātāpitūna santikaṃ;</p>\r\n<p class="gatha3">Vandanatthāya agañchi,</p>\r\n<p class="gathalast">Alaṅkata rathenaso.</p>\r\n<p class="hangnum"><a name="para311"></a><span class="paranum">311</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-310-311-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',132,replace(replace('<p class="gatha1">Vanditvā <a name="M0.0132"></a> pitaraṃbodhi-</p>\r\n<p class="gatha2">Sattobravikatañjalī;</p>\r\n<p class="gatha3">Evaṃāvīkarontova,</p>\r\n<p class="gathalast">Gamissamānaañjasaṃ.</p>\r\n<p class="hangnum"><a name="para312"></a><span class="paranum">312</span>.</p>\r\n<p class="gatha1"> Mamaṃtāta pabbājesi,</p>\r\n<p class="gatha2">Yamhāraṭṭhā adūsakaṃ;</p>\r\n<p class="gatha3">Sivīnaṃ vacanatthena,</p>\r\n<p class="gathalast">Tenaṃ-gacchāmi kānanaṃ.</p>\r\n<p class="hangnum"><a name="para313"></a><span class="paranum">313</span>.</p>\r\n<p class="gatha1"> Suveahaṃ mahārāja,</p>\r\n<p class="gatha2">Sūriyugga mane sati,</p>\r\n<p class="gatha3">Nikkhamissāmi raṭṭhamhā,</p>\r\n<p class="gathalast">Vaṅkaṃ gacchāmi pabbataṃ.</p>\r\n<p class="hangnum"><a name="para314"></a><span class="paranum">314</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-312-313-314-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',133,replace(replace('<p class="gatha1">Vanevāḷa <a name="M0.0133"></a> migākiṇṇe,</p>\r\n<p class="gatha2">Khaggadīpiniise vite;</p>\r\n<p class="gatha3">Ahaṃpuññāni karomi,</p>\r\n<p class="gathalast">Tumhe paṅkamhī sīdatha.</p>\r\n<p class="hangnum"><a name="para315"></a><span class="paranum">315</span>.</p>\r\n<p class="gatha1"> Tatosotaramānova,</p>\r\n<p class="gatha2">Gantvānamākusantikaṃ;</p>\r\n<p class="gatha3">Vandamānoārocitta,</p>\r\n<p class="gathalast">Evaṃñhisakamātuyā.</p>\r\n<p class="hangnum"><a name="para316"></a><span class="paranum">316</span>.</p>\r\n<p class="gatha1"> Anujānāhimaṃammā,</p>\r\n<p class="gatha2">Gacchāmivaṅkapabbataṃ;</p>','\r',char(13)),'\n',char(10)),'-315-316-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',134,replace(replace('<p class="gatha3">Vanepuññānikāhāmi<a name="M0.0134"></a>,</p>\r\n<p class="gathalast">Pabbajjāmamaruccati.</p>\r\n<p class="hangnum"><a name="para317"></a><span class="paranum">317</span>.</p>\r\n<p class="gatha1"> Taṃsuttvāruṇṇamukhena,</p>\r\n<p class="gatha2">Jananīevamabravī;</p>\r\n<p class="gatha3">Anujānāmitaṃvuttaṃ,</p>\r\n<p class="gathalast">Pabbajjātesamijjhabhu.</p>\r\n<p class="hangnum"><a name="para318"></a><span class="paranum">318</span>.</p>\r\n<p class="gatha1"> Ayañhivutta memaddī,</p>\r\n<p class="gatha2">Suṇhā sukhumavaddhinī;</p>\r\n<p class="gatha3">Acchataṃ saha vuttehi,</p>\r\n<p class="gathalast">Kiṃ araññekarissati.</p>\r\n<p class="hangnum"><a name="para319"></a><span class="paranum">319</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-317-318-319-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',135,replace(replace('<p class="gatha1">Mātuyāvacanaṃsutvā<a name="M0.0135"></a>,</p>\r\n<p class="gatha2">Vessantaro katañjalī;</p>\r\n<p class="gatha3">Mhitapubbamabhā sitta,</p>\r\n<p class="gathalast">Ñāpentoattanomatiṃ.</p>\r\n<p class="hangnum"><a name="para320"></a><span class="paranum">320</span>.</p>\r\n<p class="gatha1"> Nāhaṃakā mādāsaṃvi,</p>\r\n<p class="gatha2">Araññaṃnetu mussahe;</p>\r\n<p class="gatha3">Saceiccha tianvetu,</p>\r\n<p class="gathalast">Sacenicchati acchatu.</p>\r\n<p class="hangnum"><a name="para321"></a><span class="paranum">321</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-320-321-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',136,replace(replace('<p class="gatha1">Tatosuṇhaṃmahārājā<a name="M0.0136"></a>,</p>\r\n<p class="gatha2">Yācituṃpaṭipajjatha;</p>\r\n<p class="gatha3">Mācandanasamācāre,</p>\r\n<p class="gathalast">Rajojallaṃmadhārayi.</p>\r\n<p class="hangnum"><a name="para322"></a><span class="paranum">322</span>.</p>\r\n<p class="gatha1"> Kāsiyānicadhāretvā,</p>\r\n<p class="gatha2">Kusacīramadhārayi;</p>\r\n<p class="gatha3">Dukkhovāsoaraññasmiṃ,</p>\r\n<p class="gathalast">Māhitvaṃlakkhaṇegami.</p>\r\n<p class="hangnum"><a name="para323"></a><span class="paranum">323</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-322-323-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',137,replace(replace('<p class="gatha1">Tato <a name="M0.0137"></a> patibbatā suṇhā,</p>\r\n<p class="gatha2">Sassuraṃ evamabravī;</p>\r\n<p class="gatha3">Nāhaṃ taṃsukhamicchayyaṃ,</p>\r\n<p class="gathalast">Yaṃme vessantaraṃ vinā.</p>\r\n<p class="hangnum"><a name="para324"></a><span class="paranum">324</span>.</p>\r\n<p class="gatha1"> Tato suṇhaṃ mahārājā,</p>\r\n<p class="gatha2">Dussahāni brahāvane;</p>\r\n<p class="gatha3">Nānābhayāni ñāpento,</p>\r\n<p class="gathalast">Vanaṃ yāci agantave.</p>\r\n<p class="hangnum"><a name="para325"></a><span class="paranum">325</span>.</p>\r\n<p class="gatha1"> Yāna santi mahārāja,</p>\r\n<p class="gatha2">Bhayānitāni kānane;</p>','\r',char(13)),'\n',char(10)),'-324-325-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',138,replace(replace('<p class="gatha3">Sabbāni <a name="M0.0138"></a> haṃ sahissami,</p>\r\n<p class="gathalast">Anugacchāmi mepatiṃ.</p>\r\n<p class="hangnum"><a name="para326"></a><span class="paranum">326</span>.</p>\r\n<p class="gatha1"> Pūratohaṃ gamissāmi,</p>\r\n<p class="gatha2">Dadantī bhatthuno pathaṃ;</p>\r\n<p class="gatha3">Urasā panudahitvāna,</p>\r\n<p class="gathalast">Kusa naḷa vanādayo.</p>\r\n<p class="hangnum"><a name="para327"></a><span class="paranum">327</span>.</p>\r\n<p class="gatha1"> Bahūhi vatavariyāhi,</p>\r\n<p class="gatha2">Kumārī vindate patiṃ;</p>\r\n<p class="gatha3">Vedhabyaṃ kaṭukaṃ loke,</p>\r\n<p class="gathalast">Gacchaññeva rathesabha.</p>\r\n<p class="hangnum"><a name="para328"></a><span class="paranum">328</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-326-327-328-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',139,replace(replace('<p class="gatha1">Nānākārehi <a name="M0.0139"></a> pīḷenti,</p>\r\n<p class="gatha2">Anāthaṃvidhavaṃ janā;</p>\r\n<p class="gatha3">Ativākyena bhāsanti,</p>\r\n<p class="gathalast">Mittācāpi salohitā.</p>\r\n<p class="hangnum"><a name="para329"></a><span class="paranum">329</span>.</p>\r\n<p class="gatha1"> Naggā nadī anūdakā,</p>\r\n<p class="gatha2">Naggaṃ raṭṭhaṃ arājakaṃ;</p>\r\n<p class="gatha3">Itthīpi vidhavā naggā,</p>\r\n<p class="gathalast">Yassāsuṃdasa bhātaro.</p>\r\n<p class="hangnum"><a name="para330"></a><span class="paranum">330</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-329-330-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',140,replace(replace('<p class="gatha1">Dhajo <a name="M0.0140"></a> rathassa paññānaṃ,</p>\r\n<p class="gatha2">Dhumo paññā namaggino;</p>\r\n<p class="gatha3">Rājā raṭṭhassa paññānaṃ,</p>\r\n<p class="gathalast">Bhattā paññānamitthiyā.</p>\r\n<p class="hangnum"><a name="para331"></a><span class="paranum">331</span>.</p>\r\n<p class="gatha1"> Yā daliddassa posassa,</p>\r\n<p class="gatha2">Daliddī bhariyā siyā;</p>\r\n<p class="gatha3">Aḍḍhā aḍḍhassa rājinda,</p>\r\n<p class="gathalast">Taṃ ve devā pasaṃsare.</p>\r\n<p class="hangnum"><a name="para332"></a><span class="paranum">332</span>.</p>\r\n<p class="gatha1"> Kathaṃ nutāsaṃ hadayaṃ,</p>\r\n<p class="gatha2">Sukharāvata itthiyo;</p>','\r',char(13)),'\n',char(10)),'-331-332-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',141,replace(replace('<p class="gatha3">Yā <a name="M0.0141"></a> sāmike dukkhitamhi,</p>\r\n<p class="gathalast">Sukhamicchanti attano.</p>\r\n<p class="hangnum"><a name="para333"></a><span class="paranum">333</span>.</p>\r\n<p class="gatha1"> Apisāgarapariyantaṃ,</p>\r\n<p class="gatha2">Bahuvittadharaṃmahiṃ;</p>\r\n<p class="gatha3">Nānāratanaparipūraṃ,</p>\r\n<p class="gathalast">Nicchevessantaraṃvinā.</p>\r\n<p class="hangnum"><a name="para334"></a><span class="paranum">334</span>.</p>\r\n<p class="gatha1"> Sāmikaṃ anugacchissaṃ,</p>\r\n<p class="gatha2">Ahaṃ kāsāyavāsinī;</p>','\r',char(13)),'\n',char(10)),'-333-334-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',142,replace(replace('<p class="gatha3">Dhīratthutaṃ <a name="M0.0142"></a> nissīrikaṃ,</p>\r\n<p class="gathalast">Vedhabyaṃyañca nāriyā.</p>\r\n<p class="hangnum"><a name="para335"></a><span class="paranum">335</span>.</p>\r\n<p class="gatha1"> Suṇhāya bhāsitaṃ sutvā,</p>\r\n<p class="gatha2">Sassuro nikkhipetave;</p>\r\n<p class="gatha3">Duve idheva nattāro,</p>\r\n<p class="gathalast">Suṇhaṃso eva mabravī.</p>\r\n<p class="hangnum"><a name="para336"></a><span class="paranum">336</span>.</p>\r\n<p class="gatha1"> Imete daharāputtā,</p>\r\n<p class="gatha2">Jālīkaṇhājinā cubho;</p>\r\n<p class="gatha3">Nikkhippa lakkhaṇe gaccha,</p>\r\n<p class="gathalast">Mayaṃte posisāmase.</p>\r\n<p class="hangnum"><a name="para337"></a><span class="paranum">337</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-335-336-337-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',143,replace(replace('<p class="gatha1">Piyāme <a name="M0.0143"></a> puttakā deva,</p>\r\n<p class="gatha2">Jālī kaṇhājinā cubho;</p>\r\n<p class="gatha3">Tyamhaṃ tattha ramessanti,</p>\r\n<p class="gathalast">Araññe jīviso kinaṃ.</p>\r\n<p class="hangnum"><a name="para338"></a><span class="paranum">338</span>.</p>\r\n<p class="gatha1"> Puttenetvāna gacchāma,</p>\r\n<p class="gatha2">Dvemayaṃ vaṅkapabbataṃ,</p>\r\n<p class="gatha3">Dakkhamānā vasissāma,</p>\r\n<p class="gathalast">Ete pamodamānasā.</p>\r\n<p class="hangnum"><a name="para339"></a><span class="paranum">339</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-338-339-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',144,replace(replace('<p class="gatha1">Suṇamānā <a name="M0.0144"></a> vasissāma,</p>\r\n<p class="gatha2">Gītañca viyabhāṇinaṃ;</p>\r\n<p class="gatha3">Etesaṃ naccamānānaṃ,</p>\r\n<p class="gathalast">Araññe māladhārinaṃ.</p>\r\n<p class="hangnum"><a name="para340"></a><span class="paranum">340</span>.</p>\r\n<p class="gatha1"> Suṇhāya bhāsitaṃsutvā,</p>\r\n<p class="gatha2">Sassurokhinna mānaso;</p>\r\n<p class="gatha3">Evañhiso vilavittha,</p>\r\n<p class="gathalast">Paṭiccadārake duve.</p>\r\n<p class="hangnum"><a name="para341"></a><span class="paranum">341</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-340-341-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',145,replace(replace('<p class="gatha1">Sālinaṃ <a name="M0.0145"></a> odanaṃ bhutvā,</p>\r\n<p class="gatha2">Suciṃ maṃsupasecanaṃ;</p>\r\n<p class="gatha3">Rukkhaphalāni bhuñjantā,</p>\r\n<p class="gathalast">Kathaṃkāhanti dārakā.</p>\r\n<p class="hangnum"><a name="para342"></a><span class="paranum">342</span>.</p>\r\n<p class="gatha1"> Bhutvā satapale kaṃse,</p>\r\n<p class="gatha2">Sovaṇṇe satarājite;</p>\r\n<p class="gatha3">Rukkhapattesu bhuñjantā,</p>\r\n<p class="gathalast">Kathaṃkāhanti dārakā.</p>\r\n<p class="hangnum"><a name="para343"></a><span class="paranum">343</span>.</p>\r\n<p class="gatha1"> Kāsiyānica dhāretvā,</p>\r\n<p class="gatha2">Khoma koṭumparānica;</p>','\r',char(13)),'\n',char(10)),'-342-343-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',146,replace(replace('<p class="gatha3">Kusacīrāni <a name="M0.0146"></a> dhārentā,</p>\r\n<p class="gathalast">Kathaṃ kāhanti dārakā.</p>\r\n<p class="hangnum"><a name="para344"></a><span class="paranum">344</span>.</p>\r\n<p class="gatha1"> Vayhāhipariyāyitvā,</p>\r\n<p class="gatha2">Sivikāya rathenaca;</p>\r\n<p class="gatha3">Patthikā paridhāvantā,</p>\r\n<p class="gathalast">Kathaṃ kāhanti dārakā.</p>\r\n<p class="hangnum"><a name="para345"></a><span class="paranum">345</span>.</p>\r\n<p class="gatha1"> Kuṭāgāre sayitvāna,</p>\r\n<p class="gatha2">Nivāte phusitaggale;</p>\r\n<p class="gatha3">Sayantā rukkhamūlasmiṃ,</p>\r\n<p class="gathalast">Kathaṃkāhanti dārakā.</p>\r\n<p class="hangnum"><a name="para346"></a><span class="paranum">346</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-344-345-346-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',147,replace(replace('<p class="gatha1">Pallaṅkesu <a name="M0.0147"></a> sayitvāna,</p>\r\n<p class="gatha2">Gonake cittasanthate;</p>\r\n<p class="gatha3">Sayantā tiṇasanthāre,</p>\r\n<p class="gathalast">Kathaṃkāhanti dārakā.</p>\r\n<p class="hangnum"><a name="para347"></a><span class="paranum">347</span>.</p>\r\n<p class="gatha1"> Gandhakena vilimpetvā,</p>\r\n<p class="gatha2">Agalucandanenaca;</p>\r\n<p class="gatha3">Rajojallāni dhārentā,</p>\r\n<p class="gathalast">Kathaṃkāhanti dārakā.</p>\r\n<p class="hangnum"><a name="para348"></a><span class="paranum">348</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-347-348-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',148,replace(replace('<p class="gatha1">Cāmarī <a name="M0.0148"></a> mora hatthehi,</p>\r\n<p class="gatha2">Bījitaṅgā sukhedhitā;</p>\r\n<p class="gatha3">Phuṭṭhā ḍaṃsehi makasehi,</p>\r\n<p class="gathalast">Kathaṃkāhanti dārakā.</p>\r\n<p class="hangnum"><a name="para349"></a><span class="paranum">349</span>.</p>\r\n<p class="gatha1"> Tahiṃvaṃbravi rājānaṃ,</p>\r\n<p class="gatha2">Vilapantaṃ sokaṭṭitaṃ;</p>\r\n<p class="gatha3">Paṭicca dārake suṇhā,</p>\r\n<p class="gathalast">Vessantarapiyañjanā.</p>\r\n<p class="hangnum"><a name="para350"></a><span class="paranum">350</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-349-350-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',149,replace(replace('<p class="gatha1">Mā <a name="M0.0149"></a> deva paridevesi,</p>\r\n<p class="gatha2">Māca tvaṃ vimanoahu;</p>\r\n<p class="gatha3">Yathāmayaṃ bhavissāma,</p>\r\n<p class="gathalast">Tathā hessanti dārakā.</p>\r\n<p class="hangnum"><a name="para351"></a><span class="paranum">351</span>.</p>\r\n<p class="gatha1"> Evañhi sallapantānaṃ,</p>\r\n<p class="gatha2">Tesaṃ khattiyajātinaṃ;</p>\r\n<p class="gatha3">Aññoññaṃ vibhātā ratti,</p>\r\n<p class="gathalast">Samuggañchittha sūriyo.</p>\r\n<p class="hangnum"><a name="para352"></a><span class="paranum">352</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-351-352-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',150,replace(replace('<p class="gatha1">Tadānetvā <a name="M0.0150"></a> ṭhapayiṃsu,</p>\r\n<p class="gatha2">Catusindhavayuñjitaṃ;</p>\r\n<p class="gatha3">Alaṅkata rathaṃrāja-</p>\r\n<p class="gathalast">Dvāre maṅgalasammataṃ.</p>\r\n<p class="hangnum"><a name="para353"></a><span class="paranum">353</span>.</p>\r\n<p class="gatha1"> Vanditvā sassure maddī,</p>\r\n<p class="gatha2">Ādāya puttake duve;</p>\r\n<p class="gatha3">Bhattuno purato gantvā,</p>\r\n<p class="gathalast">Pathamaṃ rathamāruhi.</p>\r\n<p class="hangnum"><a name="para354"></a><span class="paranum">354</span>.</p>\r\n<p class="gatha1"> Tatovessantaromātā-</p>\r\n<p class="gatha2">Pitaro ativandiya;</p>\r\n<p class="gatha3">Padakkhiṇañca katvāna,</p>\r\n<p class="gathalast">Sīghaso rathamāruhi.</p>\r\n<p class="hangnum"><a name="para355"></a><span class="paranum">355</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-353-354-355-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',151,replace(replace('<p class="gatha1">Tato <a name="M0.0151"></a> maṅgaladvārena,</p>\r\n<p class="gatha2">Rathaṃ pesesi laṅkataṃ;</p>\r\n<p class="gatha3">Tosāpayaṃ mahāsatto,</p>\r\n<p class="gathalast">Maddiṃ jālissa mātaraṃ.</p>\r\n<p class="hangnum"><a name="para356"></a><span class="paranum">356</span>.</p>\r\n<p class="gatha1"> Pathantesu bhivandanti,</p>\r\n<p class="gatha2">Sakkaccaṃ bahavo janā;</p>\r\n<p class="gatha3">Vessantarañca maddiñca,</p>\r\n<p class="gathalast">Passamānā katañcalī.</p>\r\n<p class="hangnum"><a name="para357"></a><span class="paranum">357</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-356-357-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',152,replace(replace('<p class="gatha1">Āpucchantoca <a name="M0.0152"></a> pakkāmi,</p>\r\n<p class="gatha2">Gacchissāmīhi patthayaṃ;</p>\r\n<p class="gatha3">Nidukkhā sukhitāhotha,</p>\r\n<p class="gathalast">Itiso vandake jane.</p>\r\n<p class="hangnum"><a name="para358"></a><span class="paranum">358</span>.</p>\r\n<p class="gatha1"> Rathe ṭhitova sorājā,</p>\r\n<p class="gatha2">Ovadanto apakkami;</p>\r\n<p class="gatha3">Dānaādīni puññāni,</p>\r\n<p class="gathalast">Karothāti mahājanaṃ.</p>\r\n<p class="hangnum"><a name="para359"></a><span class="paranum">359</span>.</p>\r\n<p class="gatha1"> Gacchante bodhisattamhi,</p>\r\n<p class="gatha2">Mātā evañhi cintayi;</p>\r\n<p class="gatha3">Dātukāmosi mevutto,</p>\r\n<p class="gathalast">Paṭṭhāya jātakālato.</p>\r\n<p class="hangnum"><a name="para360"></a><span class="paranum">360</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-358-359-360-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',153,replace(replace('<p class="gatha1">Tatodānaṃ <a name="M0.0153"></a> dadopetuṃ,</p>\r\n<p class="gatha2">Puttaṃratana pūrite;</p>\r\n<p class="gatha3">Puttassubhosu passesu,</p>\r\n<p class="gathalast">Pesesi sakaṭebahū.</p>\r\n<p class="hangnum"><a name="para361"></a><span class="paranum">361</span>.</p>\r\n<p class="gatha1"> Vessantarohi samparohi sampatta-</p>\r\n<p class="gatha2">Yācakānaṃ asesakaṃ;</p>\r\n<p class="gatha3">Aṭṭhārasavāre dāsi,</p>\r\n<p class="gathalast">Kāyāruḷhaṃpiattano.</p>\r\n<p class="hangnum"><a name="para362"></a><span class="paranum">362</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-361-362-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',154,replace(replace('<p class="gatha1">Nagarā <a name="M0.0154"></a> nikkhamantamhi,</p>\r\n<p class="gatha2">Vessantaresa pettikaṃ;</p>\r\n<p class="gatha3">Nagaraṃ daṭṭhukāmosi,</p>\r\n<p class="gathalast">Tadā saṅkampi medanī.</p>\r\n<p class="hangnum"><a name="para363"></a><span class="paranum">363</span>.</p>\r\n<p class="gatha1"> Tahiṃ rathappamāṇamhi,</p>\r\n<p class="gatha2">Ṭhāne bhijjiya medanī,</p>\r\n<p class="gatha3">Parivattittha kulāla-</p>\r\n<p class="gathalast">Cakkaṃvanagarāmukhī.</p>\r\n<p class="hangnum"><a name="para364"></a><span class="paranum">364</span>.</p>\r\n<p class="gatha1"> Nagaraṃ oloketvāna,</p>\r\n<p class="gatha2">Nagarābhimukhe rathe,</p>\r\n<p class="gatha3">Ṭhitomaddiṃpi dakkhetuṃ,</p>\r\n<p class="gathalast">Evaṃsobravi hāsayaṃ.</p>\r\n<p class="hangnum"><a name="para365"></a><span class="paranum">365</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-363-364-365-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',155,replace(replace('<p class="gatha1">Iṅgha <a name="M0.0155"></a> maddi nisāmehi,</p>\r\n<p class="gatha2">Rammarūpaṃva dissati;</p>\r\n<p class="gatha3">Āvāso siviseṭṭhassa,</p>\r\n<p class="gathalast">Pettikaṃ bhavanaṃ mama.</p>\r\n<p class="hangnum"><a name="para366"></a><span class="paranum">366</span>.</p>\r\n<p class="gatha1"> Sahajātaamacceca,</p>\r\n<p class="gatha2">Nivattetvā mahājanaṃ;</p>\r\n<p class="gatha3">Rathaṃ pājesi so sīghaṃ,</p>\r\n<p class="gathalast">Modantomaddiyātato.</p>\r\n<p class="hangnum"><a name="para367"></a><span class="paranum">367</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-366-367-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',156,replace(replace('<p class="gatha1">Anvāgamiṃsu <a name="M0.0156"></a> pājentaṃ,</p>\r\n<p class="gatha2">Cattāro brahmaṇā tahiṃ;</p>\r\n<p class="gatha3">Yācituṃ sindhave ete,</p>\r\n<p class="gathalast">Maddī passittha tāvade.</p>\r\n<p class="hangnum"><a name="para368"></a><span class="paranum">368</span>.</p>\r\n<p class="gatha1"> Maddīpi mandasaddena,</p>\r\n<p class="gatha2">Yācakā viya āgatā;</p>\r\n<p class="gatha3">Ityārocesi bhattāraṃ,</p>\r\n<p class="gathalast">Gaṇhantī tassa piṭṭhiyaṃ.</p>\r\n<p class="hangnum"><a name="para369"></a><span class="paranum">369</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-368-369-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',157,replace(replace('<p class="gatha1">Sādhu <a name="M0.0157"></a> bhaddeti vatvāna,</p>\r\n<p class="gatha2">Rathassāgamanaṃ akā;</p>\r\n<p class="gatha3">Brahmaṇā upagantvāna,</p>\r\n<p class="gathalast">Yāciṃsu sindhave tahiṃ.</p>\r\n<p class="hangnum"><a name="para370"></a><span class="paranum">370</span>.</p>\r\n<p class="gatha1"> Modamānova sodāsi,</p>\r\n<p class="gatha2">Brahmaṇānaṃ susindhave;</p>\r\n<p class="gatha3">Brahmaṇādāya gañchiṃsu,</p>\r\n<p class="gathalast">Assesaka nivesanaṃ.</p>\r\n<p class="hangnum"><a name="para371"></a><span class="paranum">371</span>.</p>\r\n<p class="gatha1"> Assesu pana dinnesu,</p>\r\n<p class="gatha2">Cattāro devaputtakā;</p>\r\n<p class="gatha3">Rohiccamigavaṇṇena,</p>\r\n<p class="gathalast">Rathaṃ vahiya gañchisuṃ.</p>\r\n<p class="hangnum"><a name="para372"></a><span class="paranum">372</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-370-371-372-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',158,replace(replace('<p class="gatha1">Devā <a name="M0.0158"></a> rohiccavaṇṇena,</p>\r\n<p class="gatha2">Vahantīti subuddhimā;</p>\r\n<p class="gatha3">Vijānitvāna maddiṃpi,</p>\r\n<p class="gathalast">Ñāpento evamabravī.</p>\r\n<p class="hangnum"><a name="para373"></a><span class="paranum">373</span>.</p>\r\n<p class="gatha1"> Iṅghapmaddi nisāmehi,</p>\r\n<p class="gatha2">Cittarūpaṃva dissati;</p>\r\n<p class="gatha3">Migaro hiccavaṇṇena,</p>\r\n<p class="gathalast">Dakkhiṇassāvahantimaṃ.</p>\r\n<p class="hangnum"><a name="para374"></a><span class="paranum">374</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-373-374-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',159,replace(replace('<p class="gatha1">Maddīcevaṃ <a name="M0.0159"></a> nicchāresi,</p>\r\n<p class="gatha2">Giraṃ accherarūpinī;</p>\r\n<p class="gatha3">Vessantarassatejena,</p>\r\n<p class="gathalast">Rathaṃvahantidevatā.</p>\r\n<p class="hangnum"><a name="para375"></a><span class="paranum">375</span>.</p>\r\n<p class="gatha1"> Āgantvāna rathaṃ yāci,</p>\r\n<p class="gatha2">Aparo brāhmaṇo tato;</p>\r\n<p class="gatha3">Bodhisattopisodāsi,</p>\r\n<p class="gathalast">Rathaṃ tassa anigghiyaṃ.</p>\r\n<p class="hangnum"><a name="para376"></a><span class="paranum">376</span>.</p>\r\n<p class="gatha1"> Sakappiye dadantamhi,</p>\r\n<p class="gatha2">Maddī pahaṭṭhamānasā;</p>\r\n<p class="gatha3">Sādhukāraṃ pavattesi,</p>\r\n<p class="gathalast">Sadā maccherahiṃsinī.</p>\r\n<p class="hangnum"><a name="para377"></a><span class="paranum">377</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-375-376-377-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',160,replace(replace('<p class="gatha1">Dinne <a name="M0.0160"></a> rathamhi antara-</p>\r\n<p class="gatha2">Dhāriyiṃsu devaputtakā;</p>\r\n<p class="gatha3">Sabbe te patthikā āsuṃ,</p>\r\n<p class="gathalast">Rājāmaddiṃ tadābravī.</p>\r\n<p class="hangnum"><a name="para378"></a><span class="paranum">378</span>.</p>\r\n<p class="gatha1"> Tvaṃ maddi kaṇhaṃgaṇhāhi,</p>\r\n<p class="gatha2">Lahuesā kaniṭṭhakā;</p>\r\n<p class="gatha3">Ahaṃ jāliṃ gahessāmi,</p>\r\n<p class="gathalast">Garuko bhātikohiso.</p>\r\n<p class="hangnum"><a name="para379"></a><span class="paranum">379</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-378-379-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',161,replace(replace('<p class="gatha1">Rājā <a name="M0.0161"></a> kumāramādāya,</p>\r\n<p class="gatha2">Rājaputtīca dārikaṃ;</p>\r\n<p class="gatha3">Sammodamānā pakkāmuṃ,</p>\r\n<p class="gathalast">Aññamañña piyaṃ vadā.</p>\r\n<p class="hangnum"><a name="para380"></a><span class="paranum">380</span>.</p>\r\n<p class="gatha1"> Āgacchante paṭipathaṃ,</p>\r\n<p class="gatha2">Disvāna addhikejane;</p>\r\n<p class="gatha3">Evaṃ pucchiyagañchiṃsu,</p>\r\n<p class="gathalast">Kuhiṃ vaṅkatapabbato.</p>\r\n<p class="hangnum"><a name="para381"></a><span class="paranum">381</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-380-381-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',162,replace(replace('<p class="gatha1">Kaluṇaṃ <a name="M0.0162"></a> paridevitvā,</p>\r\n<p class="gatha2">Pucchitā addhikājanā;</p>\r\n<p class="gatha3">Evaṃ te paṭivedesuṃ,</p>\r\n<p class="gathalast">Dūrevaṅkatapabbato.</p>\r\n<p class="hangnum"><a name="para382"></a><span class="paranum">382</span>.</p>\r\n<p class="gatha1"> Maggāsannesu passantā,</p>\r\n<p class="gatha2">Dārakā phaline dume;</p>\r\n<p class="gatha3">Tesaṃphalānaṃ hetumhi,</p>\r\n<p class="gathalast">Pitaro uparodare.</p>\r\n<p class="hangnum"><a name="para383"></a><span class="paranum">383</span>.</p>\r\n<p class="gatha1"> Rodante dārake disvā,</p>\r\n<p class="gatha2">Ubbiggā vipulā dumā;</p>\r\n<p class="gatha3">Yasamevonamitvāna,</p>\r\n<p class="gathalast">Upagacchanti dārake.</p>\r\n<p class="hangnum"><a name="para384"></a><span class="paranum">384</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-382-383-384-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',163,replace(replace('<p class="gatha1">Idaṃ <a name="M0.0163"></a> accherakaṃ disvā,</p>\r\n<p class="gatha2">Maddī sasaṅkasannibhā;</p>\r\n<p class="gatha3">Pitipuṇṇena kāyena,</p>\r\n<p class="gathalast">Evaṃgāyittha nandanā.</p>\r\n<p class="hangnum"><a name="para385"></a><span class="paranum">385</span>.</p>\r\n<p class="gatha1"> Accheraṃ vata lokasmiṃ,</p>\r\n<p class="gatha2">Abbhūtaṃ lomahaṃsanaṃ,</p>\r\n<p class="gatha3">Vessantarassa tejena;</p>\r\n<p class="gathalast">Sayame vonatā dumā.</p>\r\n<p class="hangnum"><a name="para386"></a><span class="paranum">386</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-385-386-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',164,replace(replace('<p class="gatha1">Saṃkhipiṃsu <a name="M0.0164"></a> pathaṃ yakkhā,</p>\r\n<p class="gatha2">Anukampāya dārake;</p>\r\n<p class="gatha3">Nikkhantadivaseneva,</p>\r\n<p class="gathalast">Jetaraṭṭhamupāgamuṃ.</p>\r\n<p class="hangnum"><a name="para387"></a><span class="paranum">387</span>.</p>\r\n<p class="gatha1"> Ekāhenevate tiṃsa-</p>\r\n<p class="gatha2">Yojanāni atikkamuṃ;</p>\r\n<p class="gatha3">Sāyanhe mātulaṃnāma,</p>\r\n<p class="gathalast">Sampattā nagaraṃ subhaṃ.</p>\r\n<p class="hangnum"><a name="para388"></a><span class="paranum">388</span>.</p>\r\n<p class="gatha1"> Nagarassassa dvāramhi,</p>\r\n<p class="gatha2">Sālāyaṃ nisidiṃ sute;</p>\r\n<p class="gatha3">Khedaṃ vinodamānāva,</p>\r\n<p class="gathalast">Tosentā dārake duve.</p>\r\n<p class="hangnum"><a name="para389"></a><span class="paranum">389</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-387-388-389-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',165,replace(replace('<p class="gatha1">Maddīpi <a name="M0.0165"></a> bodhisattassa,</p>\r\n<p class="gatha2">Rajaṃ pādesu puñchiya;</p>\r\n<p class="gatha3">Sambahitvāna pādeca,</p>\r\n<p class="gathalast">Vijayantī ṭhitā tadā.</p>\r\n<p class="hangnum"><a name="para390"></a><span class="paranum">390</span>.</p>\r\n<p class="gatha1"> Sālāya nikkhamitvāna,</p>\r\n<p class="gatha2">Bhattucakkhupathe ṭhitā;</p>\r\n<p class="gatha3">Disodisaṃ olokesi,</p>\r\n<p class="gathalast">Maddī kantārakhedinī.</p>\r\n<p class="hangnum"><a name="para391"></a><span class="paranum">391</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-390-391-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',166,replace(replace('<p class="gatha1">Jetiyo <a name="M0.0166"></a> parivāriṃsu,</p>\r\n<p class="gatha2">Disvāna asahāyikaṃ</p>\r\n<p class="gatha3">Maddiṃ evaṃugghosiṃsu,</p>\r\n<p class="gathalast">Itthī iccherarūvinī.</p>\r\n<p class="hangnum"><a name="para392"></a><span class="paranum">392</span>.</p>\r\n<p class="gatha1"> Vayhāhi pariyāyitvā,</p>\r\n<p class="gatha2">Sivikāya rathenaca;</p>\r\n<p class="gatha3">Sājja maddīaraññasmi,</p>\r\n<p class="gathalast">Patthikā paridhāvati.</p>\r\n<p class="hangnum"><a name="para393"></a><span class="paranum">393</span>.</p>\r\n<p class="gatha1"> Anāthāgamanaṃ maddiṃ,</p>\r\n<p class="gatha2">Bhattārā puttakehica,</p>\r\n<p class="gatha3">Disvāgantvānā cikkhaṃsu,</p>\r\n<p class="gathalast">Jetindānaṃ khaṇena tā.</p>\r\n<p class="hangnum"><a name="para394"></a><span class="paranum">394</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-392-393-394-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',167,replace(replace('<p class="gatha1">Taṃ <a name="M0.0167"></a> sutvā jetapāmokkhā,</p>\r\n<p class="gatha2">Rodamānā upāgamuṃ;</p>\r\n<p class="gatha3">Vessantarassa pādesa,</p>\r\n<p class="gathalast">Nipacca iti pucchisuṃ.</p>\r\n<p class="hangnum"><a name="para395"></a><span class="paranum">395</span>.</p>\r\n<p class="gatha1"> Kiccinu deva kusalaṃ,</p>\r\n<p class="gatha2">Kicci deva anāmayaṃ;</p>\r\n<p class="gatha3">Kiccipitā arogote,</p>\r\n<p class="gatha2">Sivīnañca anāmayaṃ;</p>\r\n<p class="gatha3">Ko te balaṃ mahārāja,</p>\r\n<p class="gathalast">Konu te rathamaṇḍalaṃ.</p>\r\n<p class="hangnum"><a name="para396"></a><span class="paranum">396</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-395-396-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',168,replace(replace('<p class="gatha1">Anassako <a name="M0.0168"></a> arathako,</p>\r\n<p class="gatha2">Dīghamaddhāna māgato;</p>\r\n<p class="gatha3">Kiccāmittehi pakato,</p>\r\n<p class="gathalast">Anuppatto simaṃdisaṃ.</p>\r\n<p class="hangnum"><a name="para397"></a><span class="paranum">397</span>.</p>\r\n<p class="gatha1"> Atha vessantarorājā,</p>\r\n<p class="gatha2">Āgatahetumattano;</p>\r\n<p class="gatha3">Ñāpetuṃ jetarājūnaṃ,</p>\r\n<p class="gathalast">Imāgāthā abhāsatha.</p>\r\n<p class="hangnum"><a name="para398"></a><span class="paranum">398</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-397-398-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',169,replace(replace('<p class="gatha1">Kusalañceva <a name="M0.0169"></a> mesamma,</p>\r\n<p class="gatha2">Atho samma anāmayaṃ;</p>\r\n<p class="gatha3">Atho vibhā arogome,</p>\r\n<p class="gathalast">Sivīnañca ānāmayaṃ.</p>\r\n<p class="hangnum"><a name="para399"></a><span class="paranum">399</span>.</p>\r\n<p class="gatha1"> Ahañhikuñjaraṃ dajjaṃ,</p>\r\n<p class="gatha2">Sabbasetaṃ gajuttamaṃ;</p>\r\n<p class="gatha3">Brāhmaṇānaṃ sālaṅkāraṃ,</p>\r\n<p class="gathalast">Khettaññuṃ raṭhapūjitaṃ.</p>\r\n<p class="hangnum"><a name="para400"></a><span class="paranum">400</span>.</p>\r\n<p class="gatha1"> Tasmiṃ me sivayokuddhā,</p>\r\n<p class="gatha2">Vibhā upahatomano;</p>\r\n<p class="gatha3">Avaruddhasi maṃ rājā,</p>\r\n<p class="gathalast">Vaṅkaṃgacchāmipabbataṃ.</p>\r\n<p class="hangnum"><a name="para401"></a><span class="paranum">401</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-399-400-401-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',170,replace(replace('<p class="gatha1">Tadā <a name="M0.0170"></a> te jetarājāno,</p>\r\n<p class="gatha2">Balānuppadamānasā;</p>\r\n<p class="gatha3">Evaṃ tosiṃsu rājānaṃ,</p>\r\n<p class="gathalast">Nikkhantaṃ sakaraṭhato.</p>\r\n<p class="hangnum"><a name="para402"></a><span class="paranum">402</span>.</p>\r\n<p class="gatha1"> Svāgatante mahārāja,</p>\r\n<p class="gatha2">Athoteadurāgataṃ;</p>\r\n<p class="gatha3">Issaro sianuppatto,</p>\r\n<p class="gathalast">Yaṃidhatthipavedaya.</p>\r\n<p class="hangnum"><a name="para403"></a><span class="paranum">403</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-402-403-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',171,replace(replace('<p class="gatha1">Idheva <a name="M0.0171"></a> tāva acchassu,</p>\r\n<p class="gatha2">Jetaraṭṭhe rathesabha;</p>\r\n<p class="gatha3">Yāva jetā gamissanti,</p>\r\n<p class="gathalast">Rañño santika yācituṃ.</p>\r\n<p class="hangnum"><a name="para404"></a><span class="paranum">404</span>.</p>\r\n<p class="gatha1"> Tadā vessantaro āha,</p>\r\n<p class="gatha2">Māvogamittha santikaṃ;</p>\r\n<p class="gatha3">Yācituṃ mama pitussa,</p>\r\n<p class="gathalast">Rājāpi tattha nissaro.</p>\r\n<p class="hangnum"><a name="para405"></a><span class="paranum">405</span>.</p>\r\n<p class="gatha1"> Accuggatāhi sivayo,</p>\r\n<p class="gatha2">Balaggāne gamācaye,</p>\r\n<p class="gatha3">Tepaadhaṃsi tumicchanti,</p>\r\n<p class="gathalast">Rājānaṃ mama kāraṇā.</p>\r\n<p class="hangnum"><a name="para406"></a><span class="paranum">406</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-404-405-406-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',172,replace(replace('<p class="gatha1">Saceevaṃ <a name="M0.0172"></a> mahārāja,</p>\r\n<p class="gatha2">Siyā jetuttare pure;</p>\r\n<p class="gatha3">Idhevarajjaṃ kārehi,</p>\r\n<p class="gathalast">Jetehi parivārito.</p>\r\n<p class="hangnum"><a name="para407"></a><span class="paranum">407</span>.</p>\r\n<p class="gatha1"> Iddhaṃ phitañcidaṃraṭṭhaṃ,</p>\r\n<p class="gatha2">Iddho janapado mahā;</p>\r\n<p class="gatha3">Matiṃ karohi tvaṃ deva,</p>\r\n<p class="gathalast">Rajjassa manussatituṃ.</p>\r\n<p class="hangnum"><a name="para408"></a><span class="paranum">408</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-407-408-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',173,replace(replace('<p class="gatha1">Alaṃ <a name="M0.0173"></a> me rajjasukhena,</p>\r\n<p class="gatha2">Pabbājitassa raṭṭhato;</p>\r\n<p class="gatha3">Atuṭṭhā sivayocāsuṃ,</p>\r\n<p class="gathalast">Mañcerajjebhisecayyūṃ.</p>\r\n<p class="hangnum"><a name="para409"></a><span class="paranum">409</span>.</p>\r\n<p class="gatha1"> Asammodanī yampivoassa,</p>\r\n<p class="gatha2">Accantaṃ mamakāraṇā;</p>\r\n<p class="gatha3">Sivīhi bhaṇḍanañcāvi,</p>\r\n<p class="gathalast">Viggaho me naruccati.</p>\r\n<p class="hangnum"><a name="para410"></a><span class="paranum">410</span>.</p>\r\n<p class="gatha1"> Paṭiggahitaṃ yaṃ dinnaṃ,</p>\r\n<p class="gatha2">Sabbassa agghiyaṃ kataṃ;</p>\r\n<p class="gatha3">Avaruddhasi maṃ rājā,</p>\r\n<p class="gathalast">Vaṅka gacchāmipabbataṃ.</p>\r\n<p class="hangnum"><a name="para411"></a><span class="paranum">411</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-409-410-411-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',174,replace(replace('<p class="gatha1">Aneka <a name="M0.0174"></a> pariyāyena,</p>\r\n<p class="gatha2">Jetarājūhi yācito;</p>\r\n<p class="gatha3">Anicchanto paṭikkhīpi,</p>\r\n<p class="gathalast">Rajjaṃ so jetaraṭṭhake.</p>\r\n<p class="hangnum"><a name="para412"></a><span class="paranum">412</span>.</p>\r\n<p class="gatha1"> Nagaraṃ apavīsitvā,</p>\r\n<p class="gatha2">Sālaṃ sāṇiparikkhipaṃ;</p>\r\n<p class="gatha3">Kāretvā ekarattiṃso,</p>\r\n<p class="gathalast">Sayī saputtadārako.</p>\r\n<p class="hangnum"><a name="para413"></a><span class="paranum">413</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-412-413-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',175,replace(replace('<p class="gatha1">Pubbaṇhasamaye <a name="M0.0175"></a> bhutvā,</p>\r\n<p class="gatha2">Nānaggaṃ rasabhojanaṃ;</p>\r\n<p class="gatha3">Nūtvā so jetarājūhi,</p>\r\n<p class="gathalast">Parivutova nikkhami.</p>\r\n<p class="hangnum"><a name="para414"></a><span class="paranum">414</span>.</p>\r\n<p class="gatha1"> Jetarājā pannarasa-</p>\r\n<p class="gatha2">Yojanaṃ jetaraṭṭhato;</p>\r\n<p class="gatha3">Gantvāna vanadvāramhi</p>\r\n<p class="gathalast">Ṭhitāmagga mabhāsisuṃ.</p>\r\n<p class="hangnum"><a name="para415"></a><span class="paranum">415</span>.</p>\r\n<p class="gatha1"> Esaselomahārāja,</p>\r\n<p class="gatha2">Pabbato gandhamādano;</p>\r\n<p class="gatha3">Yatta tvaṃ saha puttehi,</p>\r\n<p class="gathalast">Sahabhariyāya vacchasi.</p>\r\n<p class="hangnum"><a name="para416"></a><span class="paranum">416</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-414-415-416-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',176,replace(replace('<p class="gatha1">Taṃ <a name="M0.0176"></a> jetā anusāsiṃsu,</p>\r\n<p class="gatha2">Assunettā rudammukhā;</p>\r\n<p class="gatha3">Ito gacchaṃmahārāja,</p>\r\n<p class="gathalast">Ujuṃ yenuttarābhimukho.</p>\r\n<p class="hangnum"><a name="para417"></a><span class="paranum">417</span>.</p>\r\n<p class="gatha1"> Atha dakkhasi bhaddante,</p>\r\n<p class="gatha2">Vepullaṃnāma pabbataṃ;</p>\r\n<p class="gatha3">Nānādumagaṇākiṇṇaṃ,</p>\r\n<p class="gathalast">Sītacchāyaṃ manoramaṃ.</p>\r\n<p class="hangnum"><a name="para418"></a><span class="paranum">418</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-417-418-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',177,replace(replace('<p class="gatha1">Tamatikkamabhaddante<a name="M0.0177"></a>,</p>\r\n<p class="gatha2">Athadakkhasiāpagaṃ;</p>\r\n<p class="gatha3">Nadiṃketumadiṃnāma,</p>\r\n<p class="gathalast">Gambhīraṃgirigabbharaṃ.</p>\r\n<p class="hangnum"><a name="para419"></a><span class="paranum">419</span>.</p>\r\n<p class="gatha1"> Puthulomacchaākiṇṇaṃ,</p>\r\n<p class="gatha2">Suppatitthaṃmahodakaṃ;</p>\r\n<p class="gatha3">Thattanūtvāpivitvāca,</p>\r\n<p class="gathalast">Assāsetvāsaputtake.</p>\r\n<p class="hangnum"><a name="para420"></a><span class="paranum">420</span>.</p>\r\n<p class="gatha1"> Athadakkhasibhaddante,</p>\r\n<p class="gatha2">Nagrodhaṃmadhuvipphalaṃ;</p>\r\n<p class="gatha3">Rammake sikharejātaṃ,</p>\r\n<p class="gathalast">Sītacchāyaṃmanoramaṃ.</p>\r\n<p class="hangnum"><a name="para421"></a><span class="paranum">421</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-419-420-421-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',178,replace(replace('<p class="gatha1">Atha <a name="M0.0178"></a> dakkhasi bhaddante,</p>\r\n<p class="gatha2">Nāḷikaṃnāma pabbataṃ;</p>\r\n<p class="gatha3">Nānādija gaṇākiṇṇaṃ,</p>\r\n<p class="gathalast">Selaṃ kiṃ purisāyutaṃ.</p>\r\n<p class="hangnum"><a name="para422"></a><span class="paranum">422</span>.</p>\r\n<p class="gatha1"> Tassauttarapubbena,</p>\r\n<p class="gatha2">Mucalindonāmasosaro;</p>\r\n<p class="gatha3">Puṇḍarikehisañchanno,</p>\r\n<p class="gathalast">Setasogandhikehica.</p>\r\n<p class="hangnum"><a name="para423"></a><span class="paranum">423</span>.</p>\r\n<p class="gatha1"> Sovanaṃ meghasaṅkāsaṃ,</p>\r\n<p class="gatha2">Dhuvaṃ haritasaddalaṃ;</p>\r\n<p class="gatha3">Sīhovā misapekkhīva,</p>\r\n<p class="gathalast">Vanasaṇḍaṃ vigāhaya.</p>\r\n<p class="hangnum"><a name="para424"></a><span class="paranum">424</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-422-423-424-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',179,replace(replace('<p class="gatha1">Tattha <a name="M0.0179"></a> bindussarā vaggū,</p>\r\n<p class="gatha2">Nānāvaṇṇā bahūdijā;</p>\r\n<p class="gatha3">Kūjanti mupakūjanti,</p>\r\n<p class="gathalast">Utusaṃpupphite dume.</p>\r\n<p class="hangnum"><a name="para425"></a><span class="paranum">425</span>.</p>\r\n<p class="gatha1"> Gantvā girividuggānaṃ,</p>\r\n<p class="gatha2">Nadīnaṃ pabhavānica;</p>\r\n<p class="gatha3">So addasa pokkharaṇiṃ,</p>\r\n<p class="gathalast">Karañca-ka kudhāyutaṃ.</p>\r\n<p class="hangnum"><a name="para426"></a><span class="paranum">426</span>.</p>\r\n<p class="gatha1"> Puthulo macchaākiṇṇaṃ,</p>\r\n<p class="gatha2">Suppatitthaṃ mahodakaṃ;</p>\r\n<p class="gatha3">Samañca caturaṃ sañca,</p>\r\n<p class="gathalast">Sāduṃ appatigandhiyaṃ.</p>\r\n<p class="hangnum"><a name="para427"></a><span class="paranum">427</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-425-426-427-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',180,replace(replace('<p class="gatha1">Tassā <a name="M0.0180"></a> uttarapubbena,</p>\r\n<p class="gatha2">Paṇṇasālaṃ amāpaya;</p>\r\n<p class="gatha3">Paṇṇasālaṃ amāpetvā,</p>\r\n<p class="gathalast">Uñchācariyāya īhatha.</p>\r\n<p class="hangnum"><a name="para428"></a><span class="paranum">428</span>.</p>\r\n<p class="gatha1"> Evañhi jetarājāno,</p>\r\n<p class="gatha2">Ācikkhitvāna añjasaṃ;</p>\r\n<p class="gatha3">Ekaṃ jetaputtaṃ byattaṃ,</p>\r\n<p class="gathalast">Āmantesuṃ susikkhitaṃ.</p>\r\n<p class="hangnum"><a name="para429"></a><span class="paranum">429</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-428-429-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',181,replace(replace('<p class="gatha1">Gacchantecā <a name="M0.0181"></a> gacchanteca,</p>\r\n<p class="gatha2">Pariggaṇhassu luddhaka;</p>\r\n<p class="gatha3">Iccevaṃ saññāpetvāna,</p>\r\n<p class="gathalast">Vanadvāre ṭhapiṃ sutaṃ.</p>\r\n<p class="hangnum"><a name="para430"></a><span class="paranum">430</span>.</p>\r\n<p class="gatha1"> Nānābhaya vinodāya,</p>\r\n<p class="gatha2">Vessantarassa rājino;</p>\r\n<p class="gatha3">Ārakkhaṃ te ṭhapetvāna,</p>\r\n<p class="gathalast">Āgamiṃsu sakaṃpuraṃ.</p>\r\n<p class="hangnum"><a name="para431"></a><span class="paranum">431</span>.</p>\r\n<p class="gatha1"> Vessantaroca gacchanto,</p>\r\n<p class="gatha2">Gandha mādanapabbataṃ;</p>','\r',char(13)),'\n',char(10)),'-430-431-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',182,replace(replace('<p class="gatha3">Saputta <a name="M0.0182"></a> dārako patto;</p>\r\n<p class="gathalast">Vissamī divasaṃ tahiṃ.</p>\r\n<p class="hangnum"><a name="para432"></a><span class="paranum">432</span>.</p>\r\n<p class="gatha1"> Tatottarāhi mukhoso,</p>\r\n<p class="gatha2">Gaccha vepulla pabbataṃ;</p>\r\n<p class="gatha3">Patvā tappāda maggena,</p>\r\n<p class="gathalast">Patto ketumatiṃ nadiṃ.</p>\r\n<p class="hangnum"><a name="para433"></a><span class="paranum">433</span>.</p>\r\n<p class="gatha1"> Tissā tīre nisīditvā,</p>\r\n<p class="gatha2">Maṃsaṃkhādiya sundaraṃ;</p>\r\n<p class="gatha3">Dinnamekena luddhena,</p>\r\n<p class="gathalast">Nūtvā pitvāca vissamī.</p>\r\n<p class="hangnum"><a name="para434"></a><span class="paranum">434</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-432-433-434-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',183,replace(replace('<p class="gatha1">Soṇṇasūciṃ <a name="M0.0183"></a> luddhakassa,</p>\r\n<p class="gatha2">Datvāna so tatoparaṃ;</p>\r\n<p class="gatha3">Gacchanto dakkhinigrodhaṃ,</p>\r\n<p class="gathalast">Pabbatasikhare ṭhitaṃ.</p>\r\n<p class="hangnum"><a name="para435"></a><span class="paranum">435</span>.</p>\r\n<p class="gatha1"> Tassamūle nisīditvā,</p>\r\n<p class="gatha2">Thokaṃphalāni bhuñjiya;</p>\r\n<p class="gatha3">Tatoparaṃ sa gacchanto,</p>\r\n<p class="gathalast">Patto nāḷikapabbataṃ.</p>\r\n<p class="hangnum"><a name="para436"></a><span class="paranum">436</span>.</p>\r\n<p class="gatha1"> Nāḷikaṃ pariharitvā,</p>\r\n<p class="gatha2">Gacchanto so mahāsaraṃ;</p>\r\n<p class="gatha3">Mucalindaṃ upāgañchi,</p>\r\n<p class="gathalast">Nānākamala bhūsitaṃ.</p>\r\n<p class="hangnum"><a name="para437"></a><span class="paranum">437</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-435-436-437-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',184,replace(replace('<p class="gatha1">Tassa <a name="M0.0184"></a> pubbauttarena,</p>\r\n<p class="gatha2">Khuddamaggena gacchatā;</p>\r\n<p class="gatha3">Diṭṭhaṃ brahāvanaṃ rammaṃ,</p>\r\n<p class="gathalast">Phala pupphāna pūritaṃ.</p>\r\n<p class="hangnum"><a name="para438"></a><span class="paranum">438</span>.</p>\r\n<p class="gatha1"> Tamatikkamma gacchanto,</p>\r\n<p class="gatha2">Anupubbena khatthiyo;</p>\r\n<p class="gatha3">Caturaṃsa pokkharaṇiṃ,</p>\r\n<p class="gathalast">Sampatto, si mahodakaṃ.</p>\r\n<p class="hangnum"><a name="para439"></a><span class="paranum">439</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-438-439-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',185,replace(replace('<p class="gatha1">Vessantaroca <a name="M0.0185"></a> maddīca,</p>\r\n<p class="gatha2">Jālīkaṇhājinā cubho;</p>\r\n<p class="gatha3">Tattheva te vissamiṃsu,</p>\r\n<p class="gathalast">Aññamaññapiyaṃvadā.</p>\r\n<p class="hangnum"><a name="para440"></a><span class="paranum">440</span>.</p>\r\n<p class="gatha1"> Āvajjanto tadā sakko,</p>\r\n<p class="gatha2">Passittha devakuñjaro;</p>\r\n<p class="gatha3">Himavantaṃ mahāsattaṃ,</p>\r\n<p class="gathalast">Paviṭṭhaṃbhayabheravaṃ.</p>\r\n<p class="hangnum"><a name="para441"></a><span class="paranum">441</span>.</p>\r\n<p class="gatha1"> Vissukammaṃ devaputtaṃ,</p>\r\n<p class="gatha2">Pakkosāpiya so tato;</p>\r\n<p class="gatha3">Assamaṃ māpanatthāya,</p>\r\n<p class="gathalast">Pesesi vaṅkapabbataṃ.</p>\r\n<p class="hangnum"><a name="para442"></a><span class="paranum">442</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-440-441-442-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',186,replace(replace('<p class="gatha1">Vissukammopi <a name="M0.0186"></a> so sīghaṃ,</p>\r\n<p class="gatha2">Otaritvā devalokato;</p>\r\n<p class="gatha3">Vaṅkapabbata kucchimhi,</p>\r\n<p class="gathalast">Māpesi assame duve.</p>\r\n<p class="hangnum"><a name="para443"></a><span class="paranum">443</span>.</p>\r\n<p class="gatha1"> Duveca caṅkameratti-</p>\r\n<p class="gatha2">Divā ṭhānānimāpayi;</p>\r\n<p class="gatha3">Paṭirūpesu ṭhānesu,</p>\r\n<p class="gathalast">Pupphite phalite dume.</p>\r\n<p class="hangnum"><a name="para444"></a><span class="paranum">444</span>.</p>\r\n<p class="gatha1"> Paṭiyādiyitvā samaṇa-</p>\r\n<p class="gatha2">Parikkhāre asesato;</p>\r\n<p class="gatha3">Akkharānipi sokāsi,</p>\r\n<p class="gathalast">Evaṃassama bhittiyaṃ.</p>\r\n<p class="hangnum"><a name="para445"></a><span class="paranum">445</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-443-444-445-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',187,replace(replace('<p class="gatha1">Pabbajjaṃ <a name="M0.0187"></a> icchamānāce,</p>\r\n<p class="gatha2">Yote ime gaṇhantuve;</p>\r\n<p class="gatha3">Pabbajitvāna acchantu,</p>\r\n<p class="gathalast">Imamhiassame sukhaṃ.</p>\r\n<p class="hangnum"><a name="para446"></a><span class="paranum">446</span>.</p>\r\n<p class="gatha1"> Evaṃlikhitvāna sodevo,</p>\r\n<p class="gatha2">Amanusseca bherave;</p>\r\n<p class="gatha3">Mige khage palāpetvā,</p>\r\n<p class="gathalast">Sakaṭhāna gato ahu.</p>\r\n<p class="hangnum"><a name="para447"></a><span class="paranum">447</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-446-447-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',188,replace(replace('<p class="gatha1">So <a name="M0.0188"></a> ekapadikaṃ maggaṃ,</p>\r\n<p class="gatha2">Disvā vessantaro tato;</p>\r\n<p class="gatha3">Pabbajitāna māvāso,</p>\r\n<p class="gathalast">Ayaṃ hehīti cintiya.</p>\r\n<p class="hangnum"><a name="para448"></a><span class="paranum">448</span>.</p>\r\n<p class="gatha1"> Maddiṃ assama dvāramhi,</p>\r\n<p class="gatha2">Ṭhapetvā puttakepica;</p>\r\n<p class="gatha3">Assamaṃ pavisitvāna,</p>\r\n<p class="gathalast">Passittha akkharāniso.</p>\r\n<p class="hangnum"><a name="para449"></a><span class="paranum">449</span>.</p>\r\n<p class="gatha1"> Idaṃsakkena dinnaṃti,</p>\r\n<p class="gatha2">Utvā so tuṭṭhamānaso;</p>\r\n<p class="gatha3">Paṇṇasālaṃ pavisittha,</p>\r\n<p class="gathalast">Ekako pabbajetave.</p>\r\n<p class="hangnum"><a name="para450"></a><span class="paranum">450</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-448-449-450-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',189,replace(replace('<p class="gatha1">Tahiṃ <a name="M0.0189"></a> dhanuñca khaggañca,</p>\r\n<p class="gatha2">Apanetvā sasāṭake;</p>\r\n<p class="gatha3">Omuñcitvāna soratta-</p>\r\n<p class="gathalast">Vākacīraṃ nivāsayi.</p>\r\n<p class="hangnum"><a name="para451"></a><span class="paranum">451</span>.</p>\r\n<p class="gatha1"> Aṃse katvāna ajina-</p>\r\n<p class="gatha2">Cammañca sakkadattiyaṃ;</p>\r\n<p class="gatha3">Gaṇhittha isivesaṃso,</p>\r\n<p class="gathalast">Bandhitvā jaṭamaṇḍalaṃ.</p>\r\n<p class="hangnum"><a name="para452"></a><span class="paranum">452</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-451-452-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',190,replace(replace('<p class="gatha1">Daṇḍamādāya <a name="M0.0190"></a> nikkhamma,</p>\r\n<p class="gatha2">Paṇṇasālāya caṅkamaṃ;</p>\r\n<p class="gatha3">Āruyha uda, netvānaṃ,</p>\r\n<p class="gathalast">Caṅkamī aparāparaṃ.</p>\r\n<p class="hangnum"><a name="para453"></a><span class="paranum">453</span>.</p>\r\n<p class="gatha1"> Tatoso puttadārānaṃ,</p>\r\n<p class="gatha2">Santikaṃ saṃvutindriyo;</p>\r\n<p class="gatha3">Āgañchi saṭṭhi vassova,</p>\r\n<p class="gathalast">Thero oruyha caṅkamā.</p>\r\n<p class="hangnum"><a name="para454"></a><span class="paranum">454</span>.</p>\r\n<p class="gatha1"> Maddīdisvā mahāsattaṃ,</p>\r\n<p class="gatha2">Kalunaṃ paridevayi;</p>\r\n<p class="gatha3">Nipacca tassa pādesu,</p>\r\n<p class="gathalast">Purāṇāni anutthunaṃ.</p>\r\n<p class="hangnum"><a name="para455"></a><span class="paranum">455</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-453-454-455-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',191,replace(replace('<p class="gatha1">Tato <a name="M0.0191"></a> sā bodhisattena,</p>\r\n<p class="gatha2">Saddhiṃ pavisi assamaṃ;</p>\r\n<p class="gatha3">Gantvā sakapaṇṇasālaṃ,</p>\r\n<p class="gathalast">Isivesaggahā ahu.</p>\r\n<p class="hangnum"><a name="para456"></a><span class="paranum">456</span>.</p>\r\n<p class="gatha1"> Pacchāputtepi tāpasa-</p>\r\n<p class="gatha2">Kumārake kariṃsute;</p>\r\n<p class="gatha3">Vasiṃsu vaṅkakucchimhi,</p>\r\n<p class="gathalast">Cattāro khattiyājanā.</p>\r\n<p class="hangnum"><a name="para457"></a><span class="paranum">457</span>.</p>\r\n<p class="gatha1"> Athamaddī varaṃyāci,</p>\r\n<p class="gatha2">Mahāsattaṃ mahāisiṃ;</p>\r\n<p class="gatha3">Mātumhe devagañchittha,</p>\r\n<p class="gathalast">Phalāphala gavesanaṃ.</p>\r\n<p class="hangnum"><a name="para458"></a><span class="paranum">458</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-456-457-458-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',192,replace(replace('<p class="gatha1">Idhaputte <a name="M0.0192"></a> gahetvāna,</p>\r\n<p class="gatha2">Vaseyyātha yathāsukhaṃ;</p>\r\n<p class="gatha3">Ahamevā harissāmi,</p>\r\n<p class="gathalast">Mūlāleca phalāphale.</p>\r\n<p class="hangnum"><a name="para459"></a><span class="paranum">459</span>.</p>\r\n<p class="gatha1"> Evaṃvaraṃ yācitvāna,</p>\r\n<p class="gatha2">Araññato phalāphalaṃ;</p>\r\n<p class="gatha3">Āharitvāna posesi,</p>\r\n<p class="gathalast">Jaṭinīsā tayojane.</p>\r\n<p class="hangnum"><a name="para460"></a><span class="paranum">460</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-459-460-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',193,replace(replace('<p class="gatha1">Bodhisattopi <a name="M0.0193"></a> yācitta,</p>\r\n<p class="gatha2">Varaṃmaddiṃ sucāriniṃ;</p>\r\n<p class="gatha3">Māgaccha ākālebhadde,</p>\r\n<p class="gathalast">Paṭṭhāyito mamantikaṃ.</p>\r\n<p class="hangnum"><a name="para461"></a><span class="paranum">461</span>.</p>\r\n<p class="gatha1"> Itthīca brahmacariya-</p>\r\n<p class="gatha2">Malaṃnāma sumedhase;</p>\r\n<p class="gatha3">Brahmacāriṃhi nāsenti,</p>\r\n<p class="gathalast">Tasmā yācāmi maṃvaraṃ.</p>\r\n<p class="hangnum"><a name="para462"></a><span class="paranum">462</span>.</p>\r\n<p class="gatha1"> Sampaṭicchittha sādhūti,</p>\r\n<p class="gatha2">Sāmaddī brahmacārinī;</p>\r\n<p class="gatha3">Sakaṭṭhāne te vasiṃsu,</p>\r\n<p class="gathalast">Bhāventā piya bhāvanaṃ.</p>\r\n<p class="hangnum"><a name="para464"></a><span class="paranum">464</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-461-462-464-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',194,replace(replace('<p class="gatha1">Mettāya <a name="M0.0194"></a> ānubhāvena,</p>\r\n<p class="gatha2">Mahesissa tiyojane;</p>\r\n<p class="gatha3">Aññamaññaṃ mettāyiṃsu,</p>\r\n<p class="gathalast">Samantā sabbapāṇino.</p>\r\n<p class="hangnum"><a name="para464"></a><span class="paranum">464</span>.</p>\r\n<p class="gatha1"> Pātovuṭṭhāya maddīpi,</p>\r\n<p class="gatha2">Upaṭṭhāpiya pāniyaṃ;</p>\r\n<p class="gatha3">Paribhojaniyañceva,</p>\r\n<p class="gathalast">Mukhodakañca āhari.</p>\r\n<p class="hangnum"><a name="para465"></a><span class="paranum">465</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-464-465-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',195,replace(replace('<p class="gatha1">Dantakaṭṭhañca <a name="M0.0195"></a> pādāsi,</p>\r\n<p class="gatha2">Tato assamamaṇḍalaṃ;</p>\r\n<p class="gatha3">Sammajitvāna dveputte,</p>\r\n<p class="gathalast">Ṭhapetvā pitusantike.</p>\r\n<p class="hangnum"><a name="para466"></a><span class="paranum">466</span>.</p>\r\n<p class="gatha1"> Khaṇittiṃ pacchi mādāya,</p>\r\n<p class="gatha2">Aṅkusañcāpi gaṇhiya;</p>\r\n<p class="gatha3">Vanamūlaphalatthāya,</p>\r\n<p class="gathalast">Pāvekkhi ekikāvanaṃ.</p>\r\n<p class="hangnum"><a name="para497"></a><span class="paranum">497</span>.</p>\r\n<p class="gatha1"> Sāyanhasamaye pacchiṃ,</p>\r\n<p class="gatha2">Mūlālehi phalehica;</p>\r\n<p class="gatha3">Pūrāpetvāna sāgañchi,</p>\r\n<p class="gathalast">Paṇṇasālaṃ araññato.</p>\r\n<p class="hangnum"><a name="para468"></a><span class="paranum">468</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-466-497-468-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',196,replace(replace('<p class="gatha1">Phalapacchiṃ <a name="M0.0196"></a> nikkhipetvā,</p>\r\n<p class="gatha2">Sayaṃ nhātvāna puttake;</p>\r\n<p class="gatha3">Nhāpetvāca bhuñjiṃsu,</p>\r\n<p class="gathalast">Modentā caturojanā.</p>\r\n<p class="hangnum"><a name="para469"></a><span class="paranum">469</span>.</p>\r\n<p class="gatha1"> Bhutvā nallāpa sallāpaṃ,</p>\r\n<p class="gatha2">Katvāna puttake duve;</p>\r\n<p class="gatha3">Maddīādāya pāvīsi,</p>\r\n<p class="gathalast">Sāpaṇṇasālamattano.</p>\r\n<p class="hangnum"><a name="para470"></a><span class="paranum">470</span>.</p>\r\n<p class="gatha1"> Evaṃvuttaniyāmena,</p>\r\n<p class="gatha2">Cattārokhattiyājanā;</p>\r\n<p class="gatha3">Vaṅkapabbatakucchimhi,</p>\r\n<p class="gathalast">Sattamāsevasiṃsute.</p>\r\n<p class="hangnum"><a name="para471"></a><span class="paranum">471</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-469-470-471-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',197,replace(replace('<p class="gatha1">Tadākaliṅgaraṭṭhamhi<a name="M0.0197"></a>,</p>\r\n<p class="gatha2">Gāmamhidunniviṭṭhake;</p>\r\n<p class="gatha3">Brahmaṇojūcakonāma,</p>\r\n<p class="gathalast">Bhikkhācāro alakkhiko.</p>\r\n<p class="hangnum"><a name="para472"></a><span class="paranum">472</span>.</p>\r\n<p class="gatha1"> Kahāpaṇasataṃladdhā,</p>\r\n<p class="gatha2">Bhikkhācārenabrāhmaṇo;</p>\r\n<p class="gatha3">Ekabrāhmaṇakulamhi,</p>\r\n<p class="gathalast">Thapetvāpunasogato.</p>\r\n<p class="hangnum"><a name="para473"></a><span class="paranum">473</span>.</p>\r\n<p class="gatha1"> Anāgantvācirāyante,</p>\r\n<p class="gatha2">Jūcakamhigahāpaṇaṃ,</p>\r\n<p class="gatha3">Khīṇaṃbrāhmaṇagehamhi,</p>\r\n<p class="gathalast">Valañjetvāasesato.</p>\r\n<p class="hangnum"><a name="para474"></a><span class="paranum">474</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-472-473-474-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',198,replace(replace('<p class="gatha1">Codesigahāpaṇaṃso<a name="M0.0198"></a>,</p>\r\n<p class="gatha2">Pacchāgantvānajūcako;</p>\r\n<p class="gatha3">Kahāpaṇassakhīṇattā,</p>\r\n<p class="gathalast">Dātuṃnāsakkhibrāhmaṇo.</p>\r\n<p class="hangnum"><a name="para475"></a><span class="paranum">475</span>.</p>\r\n<p class="gatha1"> Dhītā tassa brāhmaṇassa,</p>\r\n<p class="gatha2">Nāmenāmittatāpanā;</p>\r\n<p class="gatha3">Yuvatīrūpavantīca,</p>\r\n<p class="gathalast">Jūcakassaadaṃsutaṃ.</p>\r\n<p class="hangnum"><a name="para476"></a><span class="paranum">476</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-475-476-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',199,replace(replace('<p class="gatha1">Vasitthadunniviṭṭhamhi<a name="M0.0199"></a>,</p>\r\n<p class="gatha2">Ādāyāmittatāpanaṃ;</p>\r\n<p class="gatha3">Dahariṃjiṇṇakopāpo,</p>\r\n<p class="gathalast">Jūcakoyācajīvako.</p>\r\n<p class="hangnum"><a name="para477"></a><span class="paranum">477</span>.</p>\r\n<p class="gatha1"> Amittatāpanāsammā,</p>\r\n<p class="gatha2">Garuṃkatvānabrāhmaṇaṃ;</p>\r\n<p class="gatha3">Paṭijaggatiindaṃva,</p>\r\n<p class="gathalast">Sūjāuṭṭhānasīlinī.</p>\r\n<p class="hangnum"><a name="para478"></a><span class="paranum">478</span>.</p>\r\n<p class="gatha1"> Yuvānobrāhmaṇāaññe,</p>\r\n<p class="gatha2">Disvānāmittatāpanaṃ;</p>\r\n<p class="gatha3">Jāyāvattenasampannaṃ,</p>\r\n<p class="gathalast">Sadāreititajjare.</p>\r\n<p class="hangnum"><a name="para479"></a><span class="paranum">479</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-477-478-479-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',200,replace(replace('<p class="gatha1">Ayaṃmahallakaṃsammā<a name="M0.0200"></a>,</p>\r\n<p class="gatha2">Paṭijaggatijūcakaṃ;</p>\r\n<p class="gatha3">Kiṃ pamajjatha amhākaṃ,</p>\r\n<p class="gathalast">Yuvānānaṃtu vo sataṃ.</p>\r\n<p class="hangnum"><a name="para480"></a><span class="paranum">480</span>.</p>\r\n<p class="gatha1"> Paṭijaggathanobālā,</p>\r\n<p class="gatha2">Passitvā mittatāpanaṃ;</p>\r\n<p class="gatha3">Nocepaṭijaggeyyātha,</p>\r\n<p class="gathalast">Daṇḍessāmakharaṃbhusaṃ.</p>\r\n<p class="hangnum"><a name="para481"></a><span class="paranum">481</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-480-481-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',201,replace(replace('<p class="gatha1">Tajjitā <a name="M0.0201"></a> bhariyāsabbā,</p>\r\n<p class="gatha2">Patīhi bhayamānasā;</p>\r\n<p class="gatha3">Sannisinnā mantaliṃsu;</p>\r\n<p class="gathalast">Ārabbhāmittatāpanaṃ.</p>\r\n<p class="hangnum"><a name="para482"></a><span class="paranum">482</span>.</p>\r\n<p class="gatha1"> Palāpessāma ayyāyo,</p>\r\n<p class="gatha2">Imamhādunni viṭṭhato;</p>\r\n<p class="gatha3">Evaṃsukhaṃ vihissāma,</p>\r\n<p class="gathalast">Jūcakassa pajāpatiṃ.</p>\r\n<p class="hangnum"><a name="para483"></a><span class="paranum">483</span>.</p>\r\n<p class="gatha1"> Mantayitvānatā evaṃ,</p>\r\n<p class="gatha2">Nadiṃ udakahāriyā;</p>\r\n<p class="gatha3">Samāgantā ujjhāyiṃsu,</p>\r\n<p class="gathalast">Titthe amittatāpanaṃ.</p>\r\n<p class="hangnum"><a name="para484"></a><span class="paranum">484</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-482-483-484-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',202,replace(replace('<p class="gatha1">Amittānunatemātā<a name="M0.0202"></a>,</p>\r\n<p class="gatha2">Amittonunatepitā;</p>\r\n<p class="gatha3">Yesaṃjiṇṇassapādaṃsu,</p>\r\n<p class="gathalast">Evaṃ dahariyaṃ satiṃ.</p>\r\n<p class="hangnum"><a name="para485"></a><span class="paranum">485</span>.</p>\r\n<p class="gatha1"> Ahitaṃvatateññātī,</p>\r\n<p class="gatha2">Mantayiṃsu rahogatā;</p>\r\n<p class="gatha3">Yetaṃjiṇṇassa pādaṃsu,</p>\r\n<p class="gathalast">Evaṃ dahariyaṃsatiṃ.</p>\r\n<p class="hangnum"><a name="para486"></a><span class="paranum">486</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-485-486-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',203,replace(replace('<p class="gatha1">Amanāpavāsaṃvasati<a name="M0.0203"></a>,</p>\r\n<p class="gatha2">Jiṇṇenapatināsaha;</p>\r\n<p class="gatha3">Yātvaṃvasasijiṇṇassa,</p>\r\n<p class="gathalast">Matantejīvitāvaraṃ.</p>\r\n<p class="hangnum"><a name="para487"></a><span class="paranum">487</span>.</p>\r\n<p class="gatha1"> Nahinunatevindiṃsu,</p>\r\n<p class="gatha2">Mātāpitācasobhaṇe;</p>\r\n<p class="gatha3">Tuyhatthāyajūcakamhā,</p>\r\n<p class="gathalast">Patiṃaññaṃsuyobbanaṃ.</p>\r\n<p class="hangnum"><a name="para488"></a><span class="paranum">488</span>.</p>\r\n<p class="gatha1"> Duyiṭṭhaṃtenavamiyā,</p>\r\n<p class="gatha2">Akataṃaggihutakaṃ;</p>\r\n<p class="gatha3">Yetaṃjiṇṇassapādaṃsu,</p>\r\n<p class="gathalast">Evaṃdahariyaṃsatiṃ.</p>\r\n<p class="hangnum"><a name="para489"></a><span class="paranum">489</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-487-488-489-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',204,replace(replace('<p class="gatha1">Samaṇe <a name="M0.0204"></a> brāhmaṇe suddhe,</p>\r\n<p class="gatha2">Akkosi brāhmacārine;</p>\r\n<p class="gatha3">Yenatenadaharītvaṃ,</p>\r\n<p class="gathalast">Maññejiṇṇenasaṅgamī.</p>\r\n<p class="hangnum"><a name="para490"></a><span class="paranum">490</span>.</p>\r\n<p class="gatha1"> Nadukkhaṃahinādaṭṭhaṃ,</p>\r\n<p class="gatha2">Nadukkhaṃsattiyāhataṃ;</p>\r\n<p class="gatha3">Tañcadukkhañcatibbañca,</p>\r\n<p class="gathalast">Yaṃpassejiṇṇakaṃpatiṃ.</p>\r\n<p class="hangnum"><a name="para491"></a><span class="paranum">491</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-490-491-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',205,replace(replace('<p class="gatha1">Natthikhiṭṭānatthirati<a name="M0.0205"></a>,</p>\r\n<p class="gatha2">Jiṇṇenapatināsaha;</p>\r\n<p class="gatha3">Natthiallāpasallāpo,</p>\r\n<p class="gathalast">Jagghituṃvinasobhati.</p>\r\n<p class="hangnum"><a name="para492"></a><span class="paranum">492</span>.</p>\r\n<p class="gatha1"> Yadādaharodaharī,</p>\r\n<p class="gatha2">Mantayiṃsurahogatā;</p>\r\n<p class="gatha3">Sabbasokāvinassanti,</p>\r\n<p class="gathalast">Yekecihadayassitā.</p>\r\n<p class="hangnum"><a name="para493"></a><span class="paranum">493</span>.</p>\r\n<p class="gatha1"> Daharītvaṃrūpātī,</p>\r\n<p class="gatha2">Purisātvamabhipatthitā;</p>\r\n<p class="gatha3">Gaccha ñātikule accha,</p>\r\n<p class="gathalast">Kiṃ jiṇṇo ramayissati.</p>\r\n<p class="hangnum"><a name="para494"></a><span class="paranum">494</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-492-493-494-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',206,replace(replace('<p class="gatha1">Rodamānāvaāgañchi<a name="M0.0206"></a>,</p>\r\n<p class="gatha2">Sāgharaṃparibhāsitā;</p>\r\n<p class="gatha3">Brāhmaṇīhi hirāyantī,</p>\r\n<p class="gathalast">Ghaṭamādāyasīghaso.</p>\r\n<p class="hangnum"><a name="para495"></a><span class="paranum">495</span>.</p>\r\n<p class="gatha1"> Rodantiṃ āgataṃjāyaṃ,</p>\r\n<p class="gatha2">Disvākiṃ bhotirodiya;</p>\r\n<p class="gatha3">Āgaccha sītipucchittha,</p>\r\n<p class="gathalast">Paccuggacchaṃ vajūcako.</p>\r\n<p class="hangnum"><a name="para496"></a><span class="paranum">496</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-495-496-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',207,replace(replace('<p class="gatha1">Natebrāhmaṇa <a name="M0.0207"></a> gacchāmi,</p>\r\n<p class="gatha2">Nadiṃ udakahāriyā;</p>\r\n<p class="gatha3">Thiyomaṃparibhāsiṃsu,</p>\r\n<p class="gathalast">Tayājiṇṇenabrāhmaṇa.</p>\r\n<p class="hangnum"><a name="para497"></a><span class="paranum">497</span>.</p>\r\n<p class="gatha1"> Brāhmaṇyābhāsamānāya,</p>\r\n<p class="gatha2">Kathaṃsutvānabrāhmaṇo;</p>\r\n<p class="gatha3">Tosentobhariyaṃruṇṇaṃ,</p>\r\n<p class="gathalast">Jiṇṇosoetadabravī.</p>\r\n<p class="hangnum"><a name="para498"></a><span class="paranum">498</span>.</p>\r\n<p class="gatha1"> Māmetvaṃakarākammaṃ,</p>\r\n<p class="gatha2">Mameudakamāhari;</p>\r\n<p class="gatha3">Ahaṃudakamāhissaṃ,</p>\r\n<p class="gathalast">Mābhotikupitāahu.</p>\r\n<p class="hangnum"><a name="para499"></a><span class="paranum">499</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-497-498-499-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',208,replace(replace('<p class="gatha1">Nāhaṃtamhikulejātā<a name="M0.0208"></a>,</p>\r\n<p class="gatha2">Yaṃtvaṃudakamāhari;</p>\r\n<p class="gatha3">Evaṃbrāhmaṇajānāhi,</p>\r\n<p class="gathalast">Natevacchāmihaṃghare.</p>\r\n<p class="hangnum"><a name="para500"></a><span class="paranum">500</span>.</p>\r\n<p class="gatha1"> Sacemedāsaṃdāsiṃ vā,</p>\r\n<p class="gatha2">Nānayissasibrāhmaṇa;</p>\r\n<p class="gatha3">Evaṃbrāhmaṇajānāhi,</p>\r\n<p class="gathalast">Natevacchāmasantike.</p>\r\n<p class="hangnum"><a name="para501"></a><span class="paranum">501</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-500-501-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',209,replace(replace('<p class="gatha1">Natthime <a name="M0.0209"></a> sibbaṭṭhānaṃvā,</p>\r\n<p class="gatha2">Dhanadhaññañca brahmaṇi;</p>\r\n<p class="gatha3">Kutotaṃ dāsaṃdāsiṃ vā,</p>\r\n<p class="gatha2">Ānayissāmi bhotiyā,</p>\r\n<p class="gatha3">Ahaṃbhotiṃ upaṭṭhissaṃ,</p>\r\n<p class="gathalast">Mābhotiku pitāahu.</p>\r\n<p class="hangnum"><a name="para502"></a><span class="paranum">502</span>.</p>\r\n<p class="gatha1"> Ehite aha makkhissaṃ,</p>\r\n<p class="gatha2">Yathāmevacanaṃsutaṃ;</p>\r\n<p class="gatha3">Esavessantaro rājā,</p>\r\n<p class="gathalast">Vaṅkevasati pabbate.</p>\r\n<p class="hangnum"><a name="para503"></a><span class="paranum">503</span>.</p>\r\n<p class="gatha1"> Taṃtvaṃgantvāna yācassu,</p>\r\n<p class="gatha2">Dāsaṃdāsiñca brahmaṇa;</p>\r\n<p class="gatha3">Sotedassati yācito,</p>\r\n<p class="gathalast">Dāsaṃdāsiñca khatthiyo.</p>\r\n<p class="hangnum"><a name="para504"></a><span class="paranum">504</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-502-503-504-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',210,replace(replace('<p class="gatha1">Jiṇṇohasmi <a name="M0.0210"></a> dubbalo,</p>\r\n<p class="gatha2">Dīghocaddhāsu duggamo;</p>\r\n<p class="gatha3">Kathaṃgantuṃ sakkhissami,</p>\r\n<p class="gathalast">Upaṭhissāmi taṃ ahaṃ.</p>\r\n<p class="hangnum"><a name="para505"></a><span class="paranum">505</span>.</p>\r\n<p class="gatha1"> Yathāagantvā saṅgāmaṃ,</p>\r\n<p class="gatha2">Ayuddhova parājito,</p>\r\n<p class="gatha3">Evameva tuvaṃbrahme,</p>\r\n<p class="gathalast">Agantvāva parājito.</p>\r\n<p class="hangnum"><a name="para506"></a><span class="paranum">506</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-505-506-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',211,replace(replace('<p class="gatha1">Saceme <a name="M0.0211"></a> dāsaṃdāsiṃ vā,</p>\r\n<p class="gatha2">Nānayissasi brahmaṇa;</p>\r\n<p class="gatha3">Evaṃ brahmaṇa jānāhi,</p>\r\n<p class="gatha2">Natevacchā mahaṃghare;</p>\r\n<p class="gatha3">Amanāpaṃ tekarissāmi,</p>\r\n<p class="gathalast">Taṃtedukkhaṃ bhavissati.</p>\r\n<p class="hangnum"><a name="para507"></a><span class="paranum">507</span>.</p>\r\n<p class="gatha1"> Nakkhatte utupupphesu,</p>\r\n<p class="gatha2">Yadāmaṃdakkhasi laṅkataṃ;</p>\r\n<p class="gatha3">Aññehi saddhiṃ ramamānaṃ,</p>\r\n<p class="gathalast">Tantedukkhaṃ bhavissati.</p>\r\n<p class="hangnum"><a name="para508"></a><span class="paranum">508</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-507-508-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',212,replace(replace('<p class="gatha1">Adassanena <a name="M0.0212"></a> mayhaṃte,</p>\r\n<p class="gatha2">Jiṇṇassa paridevato;</p>\r\n<p class="gatha3">Bhiyyovaṅkā palitāca,</p>\r\n<p class="gathalast">Bahūhessanti brahmaṇa.</p>\r\n<p class="hangnum"><a name="para509"></a><span class="paranum">509</span>.</p>\r\n<p class="gatha1"> Tato so brahmaṇo bhīto,</p>\r\n<p class="gatha2">Brahmaṇiyāva sānugo;</p>\r\n<p class="gatha3">Aṭṭito kāmarāgena,</p>\r\n<p class="gathalast">Brahmaṇiṃ etadabravī.</p>\r\n<p class="hangnum"><a name="para510"></a><span class="paranum">510</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-509-510-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',213,replace(replace('<p class="gatha1">Pātheyyaṃmekarohitvaṃ<a name="M0.0213"></a>,</p>\r\n<p class="gatha2">Saṃkulyāsakalānica;</p>\r\n<p class="gatha3">Madhupiṇḍikācasukatāyo,</p>\r\n<p class="gathalast">Satthu bhattañcabrahmaṇi.</p>\r\n<p class="hangnum"><a name="para511"></a><span class="paranum">511</span>.</p>\r\n<p class="gatha1"> Ānayissaṃ methunake,</p>\r\n<p class="gatha2">Ubhodāsakumārake;</p>\r\n<p class="gatha3">Tetaṃparicarissanti,</p>\r\n<p class="gathalast">Rattiṃdivamatanditā.</p>\r\n<p class="hangnum"><a name="para512"></a><span class="paranum">512</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-511-512-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',214,replace(replace('<p class="gatha1">Sākhippaṃpaṭiyādesi<a name="M0.0214"></a>,</p>\r\n<p class="gatha2">Pātheyyaṃmittatāpanā;</p>\r\n<p class="gatha3">Paṭiyāditanti ācikkhi,</p>\r\n<p class="gathalast">Brahmaṇassaalakkhino.</p>\r\n<p class="hangnum"><a name="para513"></a><span class="paranum">513</span>.</p>\r\n<p class="gatha1"> Gehedubbalaṭhānaṃso,</p>\r\n<p class="gatha2">Thiraṃkatvānabrahmaṇo;</p>\r\n<p class="gatha3">Bhariyāyagopanatthaṃ,</p>\r\n<p class="gathalast">Guttadvārañcasaṅkhari.</p>\r\n<p class="hangnum"><a name="para514"></a><span class="paranum">514</span>.</p>\r\n<p class="gatha1"> Dārūniāharitvāna,</p>\r\n<p class="gatha2">Ghaṭepūresivārinā;</p>\r\n<p class="gatha3">Jāyāyasukhavāsatthaṃ,</p>\r\n<p class="gathalast">Anikkhantuṃcagehato.</p>\r\n<p class="hangnum"><a name="para515"></a><span class="paranum">515</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-513-514-515-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',215,replace(replace('<p class="gatha1">Paccakkheyeva <a name="M0.0215"></a> jāyāya,</p>\r\n<p class="gatha2">Isivesaṃsamādiya;</p>\r\n<p class="gatha3">Passitvābhariyaṃevaṃ,</p>\r\n<p class="gathalast">Ovadesisajūcako.</p>\r\n<p class="hangnum"><a name="para516"></a><span class="paranum">516</span>.</p>\r\n<p class="gatha1"> Itopaṭhāyatvaṃbhadde,</p>\r\n<p class="gatha2">Vikālemāssunikkhami;</p>\r\n<p class="gatha3">Mamaāgamanāyāva,</p>\r\n<p class="gathalast">Vasesiappamattakā.</p>\r\n<p class="hangnum"><a name="para517"></a><span class="paranum">517</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-516-517-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_pakinaka_gantha_sanggaho_e1103n',216,replace(replace('<p class="gatha1">Idaṃvatvānalaggitvā<a name="M0.0216"></a>,</p>\r\n<p class="gatha2">Sopātheyyapasibbahaṃ;</p>\r\n<p class="gatha3">Upāhanañcaāruyha,</p>\r\n<p class="gathalast">Gaṇhīkattaradaṇḍakaṃ.</p>\r\n<p class="hangnum"><a name="para518"></a><span class="paranum">518</span>.</p>\r\n<p class="gatha1"> Tatosoruṇṇamukhena,</p>\r\n<p class="gatha2">Bhariyaṃkatvāpadakkhiṇaṃ;</p>\r\n<p class="gatha3">Socamāno vapakkāmi,</p>\r\n<p class="gathalast">Dāsake pariyesituṃ.</p>','\r',char(13)),'\n',char(10)),'-518-');