INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="book">Lokanīti</p>\r\n<p class="chapter">1. Paṇḍitakaṇḍo</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',1,replace(replace('<p class="gatha1">Lokanītiṃ <a name="M0.0001"></a> pavakkhāmi,</p>\r\n<p class="gatha2">Nānāsatthasamuddhaṭaṃ;</p>\r\n<p class="gatha3">Māgadheneva saṅkhepaṃ,</p>\r\n<p class="gathalast">Vanditvā ratanatthayaṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Nītiloke purisassa sāro,</p>\r\n<p class="gatha2">Mātā pitā ācariyoca mitto;</p>\r\n<p class="gatha3">Tasmā hi nītiṃ puriso vijaññā,</p>\r\n<p class="gathalast">Ñāṇī mahā hoti bahussutoca.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Alasassa kuto sippaṃ,</p>\r\n<p class="gatha2">Asippassa kuto dhanaṃ;</p>\r\n<p class="gatha3">Adhanassa kuto mittaṃ,</p>\r\n<p class="gatha2">Amittassa kuto sukhaṃ;</p>','\r',char(13)),'\n',char(10)),'-2-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',2,replace(replace('<p class="gatha3">Asukhassa <a name="M0.0002"></a> kuto puññaṃ,</p>\r\n<p class="gathalast">Apuññassa kuto varaṃ.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Sippā samaṃ dhanaṃ natthi,</p>\r\n<p class="gatha2">Sippaṃ corā nagaṇhare;</p>\r\n<p class="gatha3">Idha loke sippaṃ mittaṃ,</p>\r\n<p class="gathalast">Paraloke sukhāvahaṃ.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Appakaṃ nātimaññeyya,</p>\r\n<p class="gatha2">Citte sutaṃ nidhāpaye;</p>\r\n<p class="gatha3">Vammikodakabindūva,</p>\r\n<p class="gathalast">Cirena paripūrati.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Khuddoti nātimaññeyya,</p>\r\n<p class="gatha2">Vijjaṃ vā sippameva vā;</p>\r\n<p class="gatha3">Ekampi pariyodātaṃ,</p>\r\n<p class="gathalast">Jīvitakappakāraṇaṃ.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Sele sele na māṇikaṃ,</p>\r\n<p class="gatha2">Gaje gaje na muttikaṃ;</p>\r\n<p class="gatha3">Vane vane na candanaṃ,</p>\r\n<p class="gathalast">Ṭhāne ṭhāne na paṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Paṇḍito sutasampanno,</p>\r\n<p class="gatha2">Yattha atthīti ce suto;</p>','\r',char(13)),'\n',char(10)),'-4-5-6-7-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',3,replace(replace('<p class="gatha3">Mahussāhena <a name="M0.0003"></a> taṃ ṭhānaṃ,</p>\r\n<p class="gathalast">Gantabbaṃva sutesinā.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Sine sippaṃ sine dhanaṃ,</p>\r\n<p class="gatha2">Sine pabbatamāruhaṃ;</p>\r\n<p class="gatha3">Sine kāmassa kodhassa,</p>\r\n<p class="gathalast">Ime pañca sine sine.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> Suti sammuti saṅkhyāca,</p>\r\n<p class="gatha2">Yogā nīti visesakā;</p>\r\n<p class="gatha3">Gandhabbā gaṇikā ceva,</p>\r\n<p class="gathalast">Dhanu bedā ca pūraṇā.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Tikicchā itihāsā ca,</p>\r\n<p class="gatha2">Joti māyā ca chandati;</p>\r\n<p class="gatha3">Ketu mantā ca saddā ca,</p>\r\n<p class="gathalast">Sippāṭṭhārasakā ime.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> Apuṭṭho paṇḍito bherī,</p>\r\n<p class="gatha2">Pajjunno me hoti pucchito;</p>\r\n<p class="gatha3">Bālo puṭṭho apuṭṭhopi,</p>\r\n<p class="gathalast">Bahumpi bhaṇate sadā.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> Potthakesu ca yaṃ sippaṃ,</p>\r\n<p class="gatha2">Parahatthesu yaṃ dhanaṃ;</p>','\r',char(13)),'\n',char(10)),'-9-10-11-12-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',4,replace(replace('<p class="gatha3">Yathākicce <a name="M0.0004"></a> samuppanne,</p>\r\n<p class="gathalast">Na taṃ sippaṃ na taṃ dhanaṃ.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> Jalappamāṇaṃ kumuddanālaṃ,</p>\r\n<p class="gatha2">Kulappamāṇaṃ vinayo pamāṇaṃ;</p>\r\n<p class="gatha3">Byattippamāṇaṃ kathītavākyaṃ,</p>\r\n<p class="gathalast">Pathaviyā pamāṇaṃ tiṇa milātaṃ.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Appassuto sutaṃ appaṃ,</p>\r\n<p class="gatha2">Bahuṃ maññati mānavā;</p>\r\n<p class="gatha3">Sindhūdakaṃ apassanto,</p>\r\n<p class="gathalast">Kūpe toyaṃva maṇḍuko.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> Pathamaṃ parājaye sippaṃ,</p>\r\n<p class="gatha2">Dutiyaṃ parājaye dhanaṃ;</p>\r\n<p class="gatha3">Tatiyaṃ parājaye dhammaṃ,</p>\r\n<p class="gathalast">Catutthaṃ kiṃ karissati.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> Byatta putra kimalaso,</p>\r\n<p class="gatha2">Abyatto bhārahārako;</p>\r\n<p class="gatha3">Byattako pūjito loke,</p>\r\n<p class="gathalast">Byatta putra dine dine.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> Mātā verī pitā satru,</p>\r\n<p class="gatha2">Kena bāle na sikkhitā;</p>','\r',char(13)),'\n',char(10)),'-14-15-16-17-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',5,replace(replace('<p class="gatha3">Sabhāmajjhe <a name="M0.0005"></a> na sobhanti,</p>\r\n<p class="gathalast">Haṃsamajjhe bakoyathā.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> Kaṇṭakaṃ giri ko tikkhati,</p>\r\n<p class="gatha2">Ko añjanaṃ migakkhikaṃ;</p>\r\n<p class="gatha3">Uppathaṃ pallale ko sugandhaṃ,</p>\r\n<p class="gatha2">Kula-putta-rūpo ko pavattati;</p>\r\n<p class="gathalast">Sāmaṃ-bhāvo.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> Na rasaṃ akotambulaṃ,</p>\r\n<p class="gatha2">Adhanassa, laṅkatampi;</p>\r\n<p class="gatha3">Alonakantu byañjanaṃ,</p>\r\n<p class="gathalast">Byākaraṇaṃ asippassa.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> Sussusā sutasampanno,</p>\r\n<p class="gatha2">Sutāpaññāya pavaḍḍhati;</p>\r\n<p class="gatha3">Paññāya atthaṃ jānāti,</p>\r\n<p class="gathalast">Ñāto attho sukhāvaho.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> Bhojanaṃ methunaṃ niddā,</p>\r\n<p class="gatha2">Goṇe posepi vijjati;</p>\r\n<p class="gatha3">Vijjā viseso posassa,</p>\r\n<p class="gathalast">Taṃ hīno gosamo bhave.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> Natthi vijjāsamaṃmittaṃ,</p>\r\n<p class="gatha2">Naca byādhisamo ripu;</p>','\r',char(13)),'\n',char(10)),'-19-20-21-22-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',6,replace(replace('<p class="gatha3">Naca <a name="M0.0006"></a> attasamaṃ pemaṃ,</p>\r\n<p class="gathalast">Naca kammasamaṃ balaṃ.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> Haṃso majjhe na kākānaṃ,</p>\r\n<p class="gatha2">Sīho gunnaṃ na sobhate;</p>\r\n<p class="gatha3">Gadrabhamajjhe turaṅgo,</p>\r\n<p class="gathalast">Bālamajjhe ca paṇḍito.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> Yāvajīvampi ce bālo,</p>\r\n<p class="gatha2">Paṇḍitaṃ payirupāsati;</p>\r\n<p class="gatha3">Na so dhammaṃ vijānāti,</p>\r\n<p class="gathalast">Dabbi sūparasaṃ yathā.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Muhuttamapi ce viññū,</p>\r\n<p class="gatha2">Paṇḍitaṃ payirupāsati;</p>\r\n<p class="gatha3">Khippaṃ dhammaṃ vijānāti,</p>\r\n<p class="gathalast">Jivhā sūparasaṃ yathā.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> Vinā satthaṃ na gaccheyya,</p>\r\n<p class="gatha2">Sūro saṅgāmabhūmiyaṃ;</p>\r\n<p class="gatha3">Paṇḍitvāddhagū vāṇijo,</p>\r\n<p class="gathalast">Videsagamano tathā.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> Dhananāsaṃ manotāpaṃ,</p>\r\n<p class="gatha2">Ghare duccaritāni ca;</p>','\r',char(13)),'\n',char(10)),'-24-25-26-27-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',7,replace(replace('<p class="gatha3">Vañcanañca <a name="M0.0007"></a> avamānaṃ,</p>\r\n<p class="gathalast">Paṇḍito na pakāsaye.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> Pattānurūpakaṃ vākyaṃ,</p>\r\n<p class="gatha2">Sabhāvarūpakaṃ piyaṃ;</p>\r\n<p class="gatha3">Attānurūpakaṃ kodhaṃ,</p>\r\n<p class="gathalast">Yo jānāti sa paṇḍito.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> A-dhanassa rasaṃ khādā,</p>\r\n<p class="gatha2">A-balassa hathā narā;</p>\r\n<p class="gatha3">A-paññassa vākya-kathā,</p>\r\n<p class="gathalast">Ummattaka-samā ime.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> Anavhāyaṃ gamayanto,</p>\r\n<p class="gatha2">A-pucchā bahu-bhāsako;</p>\r\n<p class="gatha3">Atta-guṇaṃ pakāsento,</p>\r\n<p class="gathalast">Ti-vidhaṃ hīna-lakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> Appa-rūpo bahuṃ bhāso,</p>\r\n<p class="gatha2">Appa-pañño pakāsito;</p>\r\n<p class="gatha3">Appa-pūro ghaṭo khobhe,</p>\r\n<p class="gathalast">Appa-khīrā gāvī cathe.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> Maṇḍūkepi ukre sīhe,</p>\r\n<p class="gatha2">Kākaggahe piye piye;</p>','\r',char(13)),'\n',char(10)),'-29-30-31-32-33-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',8,replace(replace('<p class="gatha3">A-paṇḍīpi <a name="M0.0008"></a> paṇḍī hutvā,</p>\r\n<p class="gathalast">Dhīrā pucche vaye vaye.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> Maṇḍūkepi ukre sīhe,</p>\r\n<p class="gatha2">Sūkarepi uhe dīpe;</p>\r\n<p class="gatha3">Biḷāre sadise byagghe,</p>\r\n<p class="gathalast">Sabba dhīre sippa-same.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> Na titti rājā dhanamhi,</p>\r\n<p class="gatha2">Paṇḍitopi su-bhāsite;</p>\r\n<p class="gatha3">Cakkhuṃpi piya-dassane,</p>\r\n<p class="gathalast">Jale sāgaro na titti.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> Rūpa-yobbana-sampannā,</p>\r\n<p class="gatha2">Visātha-kutha-sambhavā;</p>\r\n<p class="gatha3">Vijjā-hīnā na sobhanti,</p>\r\n<p class="gathalast">Nigandhā iva kiṃsukā.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> Hīne putto rājāmacco,</p>\r\n<p class="gatha2">Bāla-putto ca paṇḍito;</p>\r\n<p class="gatha3">A-dhanassa dhanaṃ bahu,</p>\r\n<p class="gathalast">Purisānaṃ na maññatha.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> Yo sippa-lobhena,</p>\r\n<p class="gatha2">Bahuṃ gaṇhāti taṃ sippaṃ;</p>','\r',char(13)),'\n',char(10)),'-34-35-36-37-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',9,replace(replace('<p class="gatha3">Mūgova <a name="M0.0009"></a> supinaṃ passaṃ,</p>\r\n<p class="gathalast">Kathetumpi na ussahe.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> Bhijjetuṃ kumbhakāro,</p>\r\n<p class="gatha2">Sobhetuṃ kumbha ghaṭṭati;</p>\r\n<p class="gatha3">Na khipituṃ apāyesu,</p>\r\n<p class="gathalast">Sissānaṃ vuḍḍhi-kāraṇā.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> Taggarañca palāsena,</p>\r\n<p class="gatha2">Yo naro upanayhati;</p>\r\n<p class="gatha3">Pattāpi surabhi vāyanti,</p>\r\n<p class="gathalast">Evaṃ dhīrūpasevanā.</p>\r\n<p class="centered">Paṇḍitakaṇḍo niṭṭhito.</p>\r\n<p class="chapter">Sujanakaṇḍo</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> Sabbhireva samāsetha,</p>\r\n<p class="gatha2">Sabbhi kubbetha santhavaṃ;</p>\r\n<p class="gatha3">Sataṃ saddhammamaññāya,</p>\r\n<p class="gathalast">Seyyo hoti na pāpiyo.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> Caja dujjana saṃsaggaṃ,</p>\r\n<p class="gatha2">Bhaja sādhu samāgamaṃ;</p>\r\n<p class="gatha3">Kara puññamahorattiṃ,</p>\r\n<p class="gathalast">Sara niccamaniccataṃ.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-40-41-42-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',10,replace(replace('<p class="gatha1">Yathā <a name="M0.0010"></a> udumbarapakkā,</p>\r\n<p class="gatha2">Bahirattakamevaca;</p>\r\n<p class="gatha3">Anto kimīhi sampuṇṇā,</p>\r\n<p class="gathalast">Evaṃ dujjanahaddayā.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> Yathāpi panasāpakkā,</p>\r\n<p class="gatha2">Bahi kaṇḍakameva ca;</p>\r\n<p class="gatha3">Anto amatasampannā,</p>\r\n<p class="gathalast">Evaṃ sujanahadayā.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> Sukkhopi candanataru na jahāti gandhaṃ,</p>\r\n<p class="gatha2">Nāgo gato naramukhe na jahāti līḷaṃ;</p>\r\n<p class="gatha3">Yantāgato madhurasaṃ na jahāti ucchu,</p>\r\n<p class="gathalast">Dukkhopi paṇḍitajano na jahāti dhammaṃ.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> Sīho nāma jighacchāpi,</p>\r\n<p class="gatha2">Paṇṇādīni na khādati;</p>\r\n<p class="gatha3">Sīho nāma kisocāpi,</p>\r\n<p class="gathalast">Nāgamaṃsaṃ na khādati.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> Kula-jāto kula-putto,</p>\r\n<p class="gatha2">Kula-vaṃsa-surakkhato;</p>\r\n<p class="gatha3">Attanā dukkha-pattopi,</p>\r\n<p class="gathalast">Hīna-kammaṃ na kāraye.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-44-45-46-47-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',11,replace(replace('<p class="gatha1">Candanaṃ <a name="M0.0011"></a> sītalaṃ loke,</p>\r\n<p class="gatha2">Tato candaṃva sītalaṃ;</p>\r\n<p class="gatha3">Canda-candanasītamhā,</p>\r\n<p class="gathalast">Sādhu vākyaṃ subhāsitaṃ.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> Udeyya bhāṇu pacchime,</p>\r\n<p class="gatha2">Merurājā nameyyapi;</p>\r\n<p class="gatha3">Sītalā narakaggipi,</p>\r\n<p class="gatha2">Pabbatagge ca uppalaṃ.</p>\r\n<p class="gatha3">Vikase na viparītaṃ,</p>\r\n<p class="gathalast">Sādhuvāyyaṃ kudācanaṃ.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> Sukhā rukkhassa chāyāva,</p>\r\n<p class="gatha2">Tato ñāti mātā pitu;</p>\r\n<p class="gatha3">Tato ācariyo rañño,</p>\r\n<p class="gathalast">Tato buddhassanekadhā.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> Bhamarā pupphamicchanti,</p>\r\n<p class="gatha2">Guṇamicchanti sujanā;</p>\r\n<p class="gatha3">Makkhikā pūtimicchanti,</p>\r\n<p class="gathalast">Dosamicchanti dujjanā.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> Mātāhīnassa dubbhāsā,</p>\r\n<p class="gatha2">Pitāhīnassa dukriyā;</p>','\r',char(13)),'\n',char(10)),'-49-50-51-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',12,replace(replace('<p class="gatha3">Ubho <a name="M0.0012"></a> mātā pitā hīnā,</p>\r\n<p class="gathalast">Dubbhasāca dukīriyā.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> Mātā seṭṭhassa subhāsā,</p>\r\n<p class="gatha2">Pitā seṭṭhassa sukriyā;</p>\r\n<p class="gatha3">Ubho mātā pitā seṭṭhā,</p>\r\n<p class="gathalast">Subhāsāca sukīriyā.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> Saṅgāme sūra-micchanti,</p>\r\n<p class="gatha2">Mantīsu akūtūhalaṃ;</p>\r\n<p class="gatha3">Piyañca annapānesu,</p>\r\n<p class="gathalast">Atthe jāte ca paṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> Sunakho sunakhaṃ disvā,</p>\r\n<p class="gatha2">Dantaṃ dasseti hiṃsituṃ;</p>\r\n<p class="gatha3">Dujjano sujanaṃ disvā,</p>\r\n<p class="gathalast">Rosayaṃ hiṃsamicchati.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> Mā ca vegena kiccāni,</p>\r\n<p class="gatha2">Karosi kārāpesi vā;</p>\r\n<p class="gatha3">Sahasā kāritaṃ kammaṃ,</p>\r\n<p class="gathalast">Mando pacchānutappati.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> Kodhaṃ vihitvā na kadācinasoce,</p>\r\n<p class="gatha2">Makkhappahānaṃ isayo avaṇṇayuṃ;</p>','\r',char(13)),'\n',char(10)),'-53-54-55-56-57-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',13,replace(replace('<p class="gatha3">Sabbesa <a name="M0.0013"></a> phārusa-vacaṃ khametha,</p>\r\n<p class="gathalast">Etaṃ khantiṃ uttamamāhu santo.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> Dukkho nivāso sambādhe,</p>\r\n<p class="gatha2">Ṭhāne asucisaṅkate;</p>\r\n<p class="gatha3">Tato arimhi appiye,</p>\r\n<p class="gathalast">Tatopi akataññunā.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> Ovādeyyānusāseyya,</p>\r\n<p class="gatha2">Gāpakā ca nivāraye;</p>\r\n<p class="gatha3">Satañhi so piyo hoti,</p>\r\n<p class="gathalast">Asataṃ hoti appiyo.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> Uttamattanivātena,</p>\r\n<p class="gatha2">Sūraṃ bhedena nijjaye;</p>\r\n<p class="gatha3">Nīcaṃ appaka dānena,</p>\r\n<p class="gathalast">Vīriyena samaṃ jaye.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> Na visaṃ visamiccāhu,</p>\r\n<p class="gatha2">Dhanaṃ saṅghassa uccate;</p>\r\n<p class="gatha3">Visaṃ ekaṃva hanati,</p>\r\n<p class="gathalast">Sabbaṃ saṅghassa santakaṃ.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> Javane bhadraṃ jānanti,</p>\r\n<p class="gatha2">Baliddañca vāhenā;</p>','\r',char(13)),'\n',char(10)),'-58-59-60-61-62-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',14,replace(replace('<p class="gatha3">Duhena <a name="M0.0014"></a> dhenuṃ jānanti,</p>\r\n<p class="gathalast">Bhāsamānena paṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> Dhanamappampi sādhūnaṃ,</p>\r\n<p class="gatha2">Kūpe vāriva nissayo;</p>\r\n<p class="gatha3">Bahuṃ api asādhūnaṃ,</p>\r\n<p class="gathalast">Naca vārīva aṇṇave.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> Najjo pivanti no āpaṃ,</p>\r\n<p class="gatha2">Rukkhā khādanti no phalaṃ;</p>\r\n<p class="gatha3">Vassanti kvaci no meghā,</p>\r\n<p class="gathalast">Paratthāya sataṃ dhanaṃ.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> Apattheyyaṃ na pattheyya,</p>\r\n<p class="gatha2">A cinteyyaṃ na cintaye;</p>\r\n<p class="gatha3">Dhammameva sucinteyya,</p>\r\n<p class="gathalast">Kālaṃ moghaṃ na accaye.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> Acintitampi bhavati,</p>\r\n<p class="gatha2">Cintitampi vinassati;</p>\r\n<p class="gatha3">Na hi cintāmayā bhogā,</p>\r\n<p class="gathalast">Itthiyā purisassavā.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> Asantassa piyo hoti,</p>\r\n<p class="gatha2">Sante na kurute piyaṃ;</p>','\r',char(13)),'\n',char(10)),'-63-64-65-66-67-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',15,replace(replace('<p class="gatha3">Asataṃ <a name="M0.0015"></a> dhammaṃ roceti,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>\r\n<p class="centered">Sujanakaṇḍo niṭṭhito.</p>\r\n<p class="chapter">4. Bāladujjanakaṇḍo</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> Atippiyo na kātabbo,</p>\r\n<p class="gatha2">Khalo kotūhalaṃ karo;</p>\r\n<p class="gatha3">Sirasā vahyamānopi,</p>\r\n<p class="gathalast">Aḍḍhapūro ghaṭo yathā.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> Sappo duṭṭho khalo duṭṭho,</p>\r\n<p class="gatha2">Sappo duṭṭhataro khalo;</p>\r\n<p class="gatha3">Mantosadhehi so sappo,</p>\r\n<p class="gathalast">Khalo kenupasammati.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> Yo bālo maññati bālyaṃ,</p>\r\n<p class="gatha2">Paṇḍito vāpi tena so;</p>\r\n<p class="gatha3">Bālova paṇḍitamānī,</p>\r\n<p class="gathalast">So ve bāloti vuccati.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> Madhuṃva maññatī bālo,</p>\r\n<p class="gatha2">Yāva pāpaṃ na paccati;</p>','\r',char(13)),'\n',char(10)),'-68-69-70-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',16,replace(replace('<p class="gatha3">Yadāca <a name="M0.0016"></a> paccatī pāpaṃ,</p>\r\n<p class="gathalast">Atha dukkhaṃ nigacchati.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> Na sādhu balavā bālo,</p>\r\n<p class="gatha2">Sahasā vindate dhanaṃ;</p>\r\n<p class="gatha3">Kāyassa bhedā duppañño,</p>\r\n<p class="gathalast">Nirayaṃ sopapajjati.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> Ghare duṭṭho ca mūsīko ca,</p>\r\n<p class="gatha2">Vane duṭṭho ca vānaro;</p>\r\n<p class="gatha3">Sakuṇe ca duṭṭho kāko,</p>\r\n<p class="gathalast">Nare duṭṭhoca brāhmaṇo.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> Dīghā jāgarato ratti,</p>\r\n<p class="gatha2">Dīghaṃ santassa yojanaṃ;</p>\r\n<p class="gatha3">Dīgho bālāna saṃsāro,</p>\r\n<p class="gathalast">Saddhammaṃ a-vijānataṃ.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> Tila mattaṃ paresaṃva,</p>\r\n<p class="gatha2">Appa dosañca passati;</p>\r\n<p class="gatha3">Nāḷikerampi sadosaṃ,</p>\r\n<p class="gathalast">Khala-jāto na passati.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> Nattadosaṃ pare jaññā,</p>\r\n<p class="gatha2">Jaññā dosaṃ parassatu;</p>','\r',char(13)),'\n',char(10)),'-72-73-74-75-76-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',17,replace(replace('<p class="gatha3">Guyhe <a name="M0.0017"></a> kummo aṅgāni,</p>\r\n<p class="gathalast">Para dosañca lakkhaye.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> Paṇḍitassa pasaṃsāya,</p>\r\n<p class="gatha2">Daṇḍo bālena dīyate;</p>\r\n<p class="gatha3">Paṇḍito paṇḍiteneva,</p>\r\n<p class="gathalast">Vaṇṇitova suvaṇṇito.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> Luddhaṃ atthena gaṇheyya,</p>\r\n<p class="gatha2">Thaddhaṃ añjali kammunā;</p>\r\n<p class="gatha3">Chandānuvattiyā mūḷhaṃ,</p>\r\n<p class="gathalast">Yathābhūtena paṇḍitaṃ.</p>\r\n<p class="centered">Bāladujjanakaṇḍo niṭṭhito.</p>\r\n<p class="chapter">4. Mittakaṇḍo</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> Hitakāro paro bandhu,</p>\r\n<p class="gatha2">Bandhūpi ahito paro;</p>\r\n<p class="gatha3">A hito dehajo byādhi,</p>\r\n<p class="gathalast">Hitaṃ araññamosadhaṃ.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> Parokkhe guṇa-hantāraṃ,</p>\r\n<p class="gatha2">Paccakkhe piyavādinaṃ;</p>','\r',char(13)),'\n',char(10)),'-77-78-79-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',18,replace(replace('<p class="gatha3">Vajjeyya <a name="M0.0018"></a> tādisaṃ mittaṃ,</p>\r\n<p class="gathalast">Visakumbhe yathā madhuṃ.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> Dhanahīne caje mitto,</p>\r\n<p class="gatha2">Puttadārā sahodarā;</p>\r\n<p class="gatha3">Dhanavantaṃva sevanti,</p>\r\n<p class="gathalast">Dhanaṃ loke mahā sakhā.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> Jāneyya pesena bhaccaṃ,</p>\r\n<p class="gatha2">Bandhuṃ vāpi bhayāgate;</p>\r\n<p class="gatha3">Appakāsu tathā mittaṃ,</p>\r\n<p class="gathalast">Dārañca vibhavakkhaye.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> So bandhu yo hite yutto,</p>\r\n<p class="gatha2">Pitaro honti posako;</p>\r\n<p class="gatha3">Taṃ mittaṃ yattha vissāso,</p>\r\n<p class="gathalast">Sā bhariyā ca yassa nibbūti.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> Na vissase a-vissattaṃ,</p>\r\n<p class="gatha2">Mittañcāpi na vissase;</p>\r\n<p class="gatha3">Kadāci kupito mito,</p>\r\n<p class="gathalast">Sabbaṃ dosaṃ pakāsaye.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> Sakiṃ duṭṭhañca yo mittaṃ,</p>\r\n<p class="gatha2">Puna sandhitumicchati;</p>','\r',char(13)),'\n',char(10)),'-81-82-83-84-85-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',19,replace(replace('<p class="gatha3">So <a name="M0.0019"></a> maccuṃ upagaṇhāti,</p>\r\n<p class="gathalast">Gabbhamassatarī yathā.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> Vahe a-mittaṃ khandhena,</p>\r\n<p class="gatha2">Yāva kālo anāgato;</p>\r\n<p class="gatha3">Tameva āgate kāle,</p>\r\n<p class="gathalast">Sele bhinde ghaṭaṃ iva.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> Iṇaseso aggiseso,</p>\r\n<p class="gatha2">Satruseso tatheva ca;</p>\r\n<p class="gatha3">Punappunaṃ vivaḍḍhanti,</p>\r\n<p class="gathalast">Tasmā sesaṃ na kāraye.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> Padumaṃva mukhaṃ yassa,</p>\r\n<p class="gatha2">Vācā candana sītalā;</p>\r\n<p class="gatha3">Tādisaṃ nopaseveyya,</p>\r\n<p class="gathalast">Hadayetu halāhalaṃ.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> Na seve pharusaṃ sāmiṃ,</p>\r\n<p class="gatha2">Naca seveyya macchariṃ;</p>\r\n<p class="gatha3">Tato apaggaṇhaṃ sāmiṃ,</p>\r\n<p class="gathalast">Neva niggahitaṃ tato.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> Siṅgī paññāsa hatthena,</p>\r\n<p class="gatha2">Vajje satena vājinaṃ;</p>','\r',char(13)),'\n',char(10)),'-86-87-88-89-90-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',20,replace(replace('<p class="gatha3">Hatthiṃ <a name="M0.0020"></a> dantiṃ sahassena,</p>\r\n<p class="gathalast">Desa cāgena dujjanaṃ.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> Kudesañca kumittañca,</p>\r\n<p class="gatha2">Kukulañca kubandhavaṃ;</p>\r\n<p class="gatha3">Kudārañca kudāsañca,</p>\r\n<p class="gathalast">Dūrato parivajjaye.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> Rogāture ca dubbhikkhe,</p>\r\n<p class="gatha2">Byasane sattu viggahe;</p>\r\n<p class="gatha3">Rājadvāre susāne ca,</p>\r\n<p class="gathalast">Ye tiṭṭhanti sumittakā.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> Sītavāco bahumitto,</p>\r\n<p class="gatha2">Pharuso appamittako;</p>\r\n<p class="gatha3">Upamaṃ ettha ñātabbā,</p>\r\n<p class="gathalast">Canda-sūriya-rājūnaṃ.</p>\r\n<p class="centered">Mittakaṇḍo niṭṭhito.</p>\r\n<p class="chapter">5. Itthikaṇḍo</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> Kokilānaṃ saddaṃ rūpaṃ,</p>\r\n<p class="gatha2">Nārīrūpaṃ patibbatā;</p>','\r',char(13)),'\n',char(10)),'-91-92-93-94-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',21,replace(replace('<p class="gatha3">Vijjā <a name="M0.0021"></a> rūpaṃ a-rūpānaṃ,</p>\r\n<p class="gathalast">Khamā rūpaṃ tapassinaṃ.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> Itthīnañca dhanaṃ rūpaṃ,</p>\r\n<p class="gatha2">Purisānaṃ vijjā dhanaṃ;</p>\r\n<p class="gatha3">Bhikkhūnañca dhanaṃ sīlaṃ,</p>\r\n<p class="gathalast">Rājānañca dhanaṃ balaṃ.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> Tapassino kisā sobhā,</p>\r\n<p class="gatha2">Thūlā sobhā catuppadā;</p>\r\n<p class="gatha3">Purisā vijjavā sobhā,</p>\r\n<p class="gathalast">Itthī sobhāsa sāmikā.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> Pañca ratyā sugandhabbā,</p>\r\n<p class="gatha2">Satta ratyā dhanuggahā;</p>\r\n<p class="gatha3">Eka māsā subhariyā,</p>\r\n<p class="gathalast">Aḍḍha māsā sissā malā.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> Hiṃ ramati paṅa,</p>\r\n<p class="gatha2">Haṅa ramati poka.</p>\r\n<p class="gatha3">Thī ramati pu,</p>\r\n<p class="gathalast">Khu ramati dhaṃ.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> Jiṇṇamannaṃ pasaṃseyya,</p>\r\n<p class="gatha2">Dārañca gatayobbanaṃ;</p>\r\n<p class="gatha3">Raṇā punāgatā sūraṃ,</p>\r\n<p class="gathalast">Sassañca gehamāgataṃ.</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-95-96-97-98-99-100-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',22,replace(replace('<p class="gatha1">Dvatti-patikā <a name="M0.0022"></a> nārī ca,</p>\r\n<p class="gatha2">Bhikkhu dvatti-vihāriko;</p>\r\n<p class="gatha3">Dvatti-pāsa-mutto pakkhī,</p>\r\n<p class="gathalast">Kata-māyā bahūtharaṃ.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> Dujjanaṃ pahārādame,</p>\r\n<p class="gatha2">Mittaṃ dame a-bhāṇikā;</p>\r\n<p class="gatha3">Itthiñca byasanā dame,</p>\r\n<p class="gathalast">Rāginaṃ appa bhojanā.</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> Na ratti vinā candimā,</p>\r\n<p class="gatha2">Vīciṃ vinā ca aṇṇavo;</p>\r\n<p class="gatha3">Haṃsaṃ vinā pokkharaṇī,</p>\r\n<p class="gathalast">Patiṃ kaññāca sobhate.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> Patinā janito bhogo,</p>\r\n<p class="gatha2">Itthiyāva saṃgoppito;</p>\r\n<p class="gatha3">Purisova hi padhāno,</p>\r\n<p class="gathalast">Itthī suttaṃva sūciyā.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> Sabbānadī vaṅkanadī,</p>\r\n<p class="gatha2">Sabbe kaṭṭhamayā vanā;</p>\r\n<p class="gatha3">Sabbitthiyo kare pāpaṃ,</p>\r\n<p class="gathalast">Labhamāne nivātake.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-101-102-103-104-105-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',23,replace(replace('<p class="gatha1">Vivādasīliṃ <a name="M0.0023"></a> usūyabhāṇiniṃ,</p>\r\n<p class="gatha2">Sampassataṇhiṃ bahupākabhuttiniṃ;</p>\r\n<p class="gatha3">Aggantabhuttiṃ paragehavāsiniṃ,</p>\r\n<p class="gathalast">Nāriṃ caje puttasatampi pūmā.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> Bhuttesu maṇḍesu janīva kantinī,</p>\r\n<p class="gatha2">Guyheca ṭhāne bhaginīva hiriṇī;</p>\r\n<p class="gatha3">Kammesu pattesu karoti dāsīva,</p>\r\n<p class="gatha2">Bhayesu mantī sayanesu rāmaye;</p>\r\n<p class="gatha3">Rūpīsu sikkhī kupanesu khantinī,</p>\r\n<p class="gatha2">Sā nārī seṭṭhāti vadanti paṇḍitā;</p>\r\n<p class="gathalast">Kāyassa bhedāca divebhaveyya sā.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> Sāmā migakkhī tanumajjhagattā,</p>\r\n<p class="gatha2">Sūrū sukesī samadantapantī;</p>\r\n<p class="gatha3">Gambhīranābhī yuvatī susīlī,</p>\r\n<p class="gathalast">Hīne kule jātāpi vivāhyā.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> Saradaṃratu-kālānaṃ,</p>\r\n<p class="gatha2">Bhariyānaṃ rūpavatī;</p>\r\n<p class="gatha3">Jeṭṭho padhānaṃ puttānaṃ,</p>\r\n<p class="gathalast">Disānaṃ uttarādisā.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> Yā icche puriso hotuṃ,</p>\r\n<p class="gatha2">Jāti jāti punappunaṃ;</p>','\r',char(13)),'\n',char(10)),'-106-107-108-109-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',24,replace(replace('<p class="gatha3">Sāmikaṃ <a name="M0.0024"></a> apacāyeyya,</p>\r\n<p class="gathalast">Indaṃva pāricārikā.</p>\r\n<p class="hangnum"><a name="para110"></a><span class="paranum">110</span>.</p>\r\n<p class="gatha1"> Yo icche puriso hotuṃ,</p>\r\n<p class="gatha2">Jāti jāti punappunaṃ;</p>\r\n<p class="gatha3">Paradānaṃ vivajjeyya,</p>\r\n<p class="gathalast">Dhotapādova kaddamaṃ.</p>\r\n<p class="hangnum"><a name="para111"></a><span class="paranum">111</span>.</p>\r\n<p class="gatha1"> Atikkanta vayo poso,</p>\r\n<p class="gatha2">Āneti timbaruttaniṃ;</p>\r\n<p class="gatha3">Tassā issā asaddhāti,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>\r\n<p class="centered">Itthikaṇḍo niṭṭhito.</p>\r\n<p class="chapter">6. Rājakaṇḍo</p>\r\n<p class="hangnum"><a name="para112"></a><span class="paranum">112</span>.</p>\r\n<p class="gatha1"> Ekayāmaṃ saye rājā,</p>\r\n<p class="gatha2">Dviyāmaññeva paṇḍito;</p>\r\n<p class="gatha3">Gharāvāso tiyāmaṃva,</p>\r\n<p class="gathalast">Catuyāmaṃ tu yācako.</p>\r\n<p class="hangnum"><a name="para113"></a><span class="paranum">113</span>.</p>\r\n<p class="gatha1"> Dhanavā sutavā rājā,</p>\r\n<p class="gatha2">Nadī vejjo cimepañca;</p>','\r',char(13)),'\n',char(10)),'-110-111-112-113-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',25,replace(replace('<p class="gatha3">Yattha <a name="M0.0025"></a> dese na vijjanti,</p>\r\n<p class="gathalast">Na tattha divasaṃ vase.</p>\r\n<p class="hangnum"><a name="para114"></a><span class="paranum">114</span>.</p>\r\n<p class="gatha1"> Yasmiṃ padese na māno,</p>\r\n<p class="gatha2">Na pemaṃ naca bandhavā;</p>\r\n<p class="gatha3">Naca vijjāgamo koci,</p>\r\n<p class="gathalast">Na tattha divasaṃ vase.</p>\r\n<p class="hangnum"><a name="para115"></a><span class="paranum">115</span>.</p>\r\n<p class="gatha1"> Aputtakaṃ gharaṃ suññaṃ,</p>\r\n<p class="gatha2">Raṭṭhaṃ suññaṃ arājakaṃ;</p>\r\n<p class="gatha3">A sippassa mukhaṃ suññaṃ,</p>\r\n<p class="gathalast">Sabba suññaṃ daliddakā.</p>\r\n<p class="hangnum"><a name="para116"></a><span class="paranum">116</span>.</p>\r\n<p class="gatha1"> Dhanamiccheyya vāṇijjo,</p>\r\n<p class="gatha2">Vijjamicche bhajesutaṃ;</p>\r\n<p class="gatha3">Puttamicche taruṇitthiṃ,</p>\r\n<p class="gathalast">Rājāmaccaṃ vasaṃ game.</p>\r\n<p class="hangnum"><a name="para117"></a><span class="paranum">117</span>.</p>\r\n<p class="gatha1"> Naṭṭhoyati asantuṭṭho,</p>\r\n<p class="gatha2">Santuṭṭho ca mahīpati;</p>\r\n<p class="gatha3">Lajjā ca gaṇikā naṭṭhā,</p>\r\n<p class="gathalast">Nillajjā kuladhītikā.</p>\r\n<p class="hangnum"><a name="para118"></a><span class="paranum">118</span>.</p>\r\n<p class="gatha1"> Pakkhīnaṃ balamākāso,</p>\r\n<p class="gatha2">Macchānamudakaṃ balaṃ;</p>','\r',char(13)),'\n',char(10)),'-114-115-116-117-118-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',26,replace(replace('<p class="gatha3">Dubbalassa <a name="M0.0026"></a> balaṃ rājā,</p>\r\n<p class="gathalast">Kumārānaṃ rudaṃ balaṃ.</p>\r\n<p class="hangnum"><a name="para119"></a><span class="paranum">119</span>.</p>\r\n<p class="gatha1"> Khamā jāgariyuṭṭhānaṃ,</p>\r\n<p class="gatha2">Saṃvibhāgo dayikkhaṇā;</p>\r\n<p class="gatha3">Nāyakassa guṇā ete,</p>\r\n<p class="gathalast">Icchitabbā sataṃ guṇā.</p>\r\n<p class="hangnum"><a name="para120"></a><span class="paranum">120</span>.</p>\r\n<p class="gatha1"> Sakiṃ vadanti rājāno,</p>\r\n<p class="gatha2">Sakiṃ samaṇabrāhmaṇā;</p>\r\n<p class="gatha3">Sakiṃ sappurisā loke,</p>\r\n<p class="gathalast">Esa dhammo sanantano.</p>\r\n<p class="hangnum"><a name="para121"></a><span class="paranum">121</span>.</p>\r\n<p class="gatha1"> Alaso gihī kāmabhogī na sādhu,</p>\r\n<p class="gatha2">Asaññato pabbajito na sādhu;</p>\r\n<p class="gatha3">Rājā anisammakārī na sādhu,</p>\r\n<p class="gathalast">Paṇḍito kodhano taṃpi na sādhu.</p>\r\n<p class="hangnum"><a name="para122"></a><span class="paranum">122</span>.</p>\r\n<p class="gatha1"> Bahavo yattha nettāro,</p>\r\n<p class="gatha2">Sabbe paṇḍitamānino;</p>\r\n<p class="gatha3">Sabbe mahattamicchanti,</p>\r\n<p class="gathalast">Tesaṃ kammaṃ vinassati.</p>\r\n<p class="hangnum"><a name="para123"></a><span class="paranum">123</span>.</p>\r\n<p class="gatha1"> Āyaṃ khayaṃ sayaṃ jaññā,</p>\r\n<p class="gatha2">Rājā sayaṃ katākataṃ;</p>','\r',char(13)),'\n',char(10)),'-119-120-121-122-123-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',27,replace(replace('<p class="gatha3">Niggahe <a name="M0.0027"></a> niggahetabbaṃ,</p>\r\n<p class="gathalast">Paggahe paggahārahaṃ.</p>\r\n<p class="hangnum"><a name="para124"></a><span class="paranum">124</span>.</p>\r\n<p class="gatha1"> Piṭṭhitokkaṃ niseveyya,</p>\r\n<p class="gatha2">Kucchināva hutāsanaṃ;</p>\r\n<p class="gatha3">Sāmikaṃ sabbabhāgena,</p>\r\n<p class="gathalast">Paralokaṃ amohavā.</p>\r\n<p class="hangnum"><a name="para125"></a><span class="paranum">125</span>.</p>\r\n<p class="gatha1"> Aggi āpo itthimūḷho,</p>\r\n<p class="gatha2">Sappo rāja-kulānica;</p>\r\n<p class="gatha3">Apayantena gantabbā,</p>\r\n<p class="gathalast">Acceka-pāṇahārakā.</p>\r\n<p class="hangnum"><a name="para126"></a><span class="paranum">126</span>.</p>\r\n<p class="gatha1"> Paduṭṭha-bhariya-saṃvāso,</p>\r\n<p class="gatha2">Paduṭṭha citta dāsako;</p>\r\n<p class="gatha3">Sa-sappe ca ghare vāso,</p>\r\n<p class="gathalast">Maccu eva na saṃsayo.</p>\r\n<p class="hangnum"><a name="para127"></a><span class="paranum">127</span>.</p>\r\n<p class="gatha1"> Mūḷha sisso padesena,</p>\r\n<p class="gatha2">Kunārī bharaṇena ca;</p>\r\n<p class="gatha3">Asatā sampayogena,</p>\r\n<p class="gathalast">Paṇḍitoppavasīdati.</p>\r\n<p class="hangnum"><a name="para128"></a><span class="paranum">128</span>.</p>\r\n<p class="gatha1"> Mātā puttakaraṃ pāpaṃ,</p>\r\n<p class="gatha2">Sissapāpaṃ gurukatā;</p>','\r',char(13)),'\n',char(10)),'-124-125-126-127-128-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',28,replace(replace('<p class="gatha3">Rājā <a name="M0.0028"></a> raṭṭhakaraṃ pāpaṃ,</p>\r\n<p class="gathalast">Rājapāpaṃ purohito.</p>\r\n<p class="hangnum"><a name="para129"></a><span class="paranum">129</span>.</p>\r\n<p class="gatha1"> Akodhena jine kodhaṃ,</p>\r\n<p class="gatha2">Asādhuṃ sādhunā jine;</p>\r\n<p class="gatha3">Jine macchariṃ dānena,</p>\r\n<p class="gathalast">Saccenālīkavādinaṃ;</p>\r\n<p class="hangnum"><a name="para130"></a><span class="paranum">130</span>.</p>\r\n<p class="gatha1"> Adantaṃ damanaṃ dānaṃ,</p>\r\n<p class="gatha2">Dānaṃ sabbattha sādhakaṃ;</p>\r\n<p class="gatha3">Dānena piya vācāya,</p>\r\n<p class="gathalast">Unnamanti namanti ca;</p>\r\n<p class="hangnum"><a name="para131"></a><span class="paranum">131</span>.</p>\r\n<p class="gatha1"> Dānaṃ sinehabhesajjaṃ,</p>\r\n<p class="gatha2">Maccheraṃ dosanosadhaṃ;</p>\r\n<p class="gatha3">Dānaṃ yasassī bhesajjaṃ,</p>\r\n<p class="gathalast">Maccheraṃ kapaṇosadhaṃ.</p>\r\n<p class="hangnum"><a name="para132"></a><span class="paranum">132</span>.</p>\r\n<p class="gatha1"> Bahūnamappasārānaṃ,</p>\r\n<p class="gatha2">Sāmaggiyā jayaṃ jaye;</p>\r\n<p class="gatha3">Tiṇehi vattate yottaṃ,</p>\r\n<p class="gathalast">Tena nāgopi bajjhate.</p>\r\n<p class="hangnum"><a name="para133"></a><span class="paranum">133</span>.</p>\r\n<p class="gatha1"> Sahāyo asamatthopi,</p>\r\n<p class="gatha2">Tejasā kiṃkarissati;</p>','\r',char(13)),'\n',char(10)),'-129-130-131-132-133-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',29,replace(replace('<p class="gatha3">Nivāte <a name="M0.0029"></a> jalito aggi,</p>\r\n<p class="gathalast">Sayame vūpasampati.</p>\r\n<p class="hangnum"><a name="para134"></a><span class="paranum">134</span>.</p>\r\n<p class="gatha1"> Na raññā samakaṃ bhuñje,</p>\r\n<p class="gatha2">Kāmabhogaṃ kudācanaṃ;</p>\r\n<p class="gatha3">Ākappaṃ rasa bhuttiṃvā,</p>\r\n<p class="gatha2">Mālā gandha vilepanaṃ;</p>\r\n<p class="gatha3">Vatthaṃ sabbaalaṅkāraṃ,</p>\r\n<p class="gathalast">Na raññā sadisaṃ kare.</p>\r\n<p class="hangnum"><a name="para135"></a><span class="paranum">135</span>.</p>\r\n<p class="gatha1"> Na me rājā sakhā hoti,</p>\r\n<p class="gatha2">Na rājā hoti methuno;</p>\r\n<p class="gatha3">Eso sāmiko mayhanti,</p>\r\n<p class="gathalast">Citte niṭṭhaṃ suthāpaye.</p>\r\n<p class="hangnum"><a name="para136"></a><span class="paranum">136</span>.</p>\r\n<p class="gatha1"> Nātidūre bhaje rañño,</p>\r\n<p class="gatha2">Nāccāsannopavātake;</p>\r\n<p class="gatha3">Ujuke nātininne ca,</p>\r\n<p class="gatha2">Na bhaje uccamāsane.</p>\r\n<p class="gatha3">Chadose vajje sevako,</p>\r\n<p class="gathalast">Tiṭṭhe aggiṃva saṃyato.</p>\r\n<p class="hangnum"><a name="para137"></a><span class="paranum">137</span>.</p>\r\n<p class="gatha1"> Guṇī sabbaññu tulyopi,</p>\r\n<p class="gatha2">Nasobhati anissayo;</p>','\r',char(13)),'\n',char(10)),'-134-135-136-137-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',30,replace(replace('<p class="gatha3">Anagghamopi <a name="M0.0030"></a> maṇiseṭṭho,</p>\r\n<p class="gathalast">Hemaṃ nissāya sobhati.</p>\r\n<p class="centered">Rājakaṇḍo niṭṭhito.</p>\r\n<p class="chapter">7. Pakiṇṇakakaṇḍo</p>\r\n<p class="hangnum"><a name="para138"></a><span class="paranum">138</span>.</p>\r\n<p class="gatha1"> Itthimisse kutosīlaṃ,</p>\r\n<p class="gatha2">Maṃsa bhakkhe kutodayā;</p>\r\n<p class="gatha3">Surā pāne kutosaccaṃ,</p>\r\n<p class="gatha2">Mahālobhe kutohirī;</p>\r\n<p class="gatha3">Mahātande kutosippaṃ,</p>\r\n<p class="gathalast">Mahā kodhe kutodhanaṃ.</p>\r\n<p class="hangnum"><a name="para139"></a><span class="paranum">139</span>.</p>\r\n<p class="gatha1"> Surā yogo vikālo ca,</p>\r\n<p class="gatha2">Samajja caraṇālasaṃ;</p>\r\n<p class="gatha3">Khiḍḍādhutto pāpamitto,</p>\r\n<p class="gathalast">Bhoganāsamukhā ime.</p>\r\n<p class="hangnum"><a name="para140"></a><span class="paranum">140</span>.</p>\r\n<p class="gatha1"> Divā nādikkhā vattabbaṃ,</p>\r\n<p class="gatha2">Ratto nāvacanena ca;</p>\r\n<p class="gatha3">Sañcareyya bhayā bhīto,</p>\r\n<p class="gathalast">Vane vanacarī yathā.</p>\r\n<p class="hangnum"><a name="para141"></a><span class="paranum">141</span>.</p>\r\n<p class="gatha1"> Jīvantāpi matāpañca,</p>\r\n<p class="gatha2">Byāsena parikittitā;</p>','\r',char(13)),'\n',char(10)),'-138-139-140-141-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',31,replace(replace('<p class="gatha3">Dukkhito <a name="M0.0031"></a> byādhitomūḷho,</p>\r\n<p class="gathalast">Iṇavā nityasevako.</p>\r\n<p class="hangnum"><a name="para142"></a><span class="paranum">142</span>.</p>\r\n<p class="gatha1"> Anāgataṃ bhayaṃ disvā,</p>\r\n<p class="gatha2">Dūrato parivajjaye;</p>\r\n<p class="gatha3">Āgatañca bhayaṃ disvā,</p>\r\n<p class="gathalast">A bhīto hoti paṇḍito.</p>\r\n<p class="hangnum"><a name="para143"></a><span class="paranum">143</span>.</p>\r\n<p class="gatha1"> Niddāluko pamattoca,</p>\r\n<p class="gatha2">Sukhatto rogavālaso;</p>\r\n<p class="gatha3">Mahiccho kammārāmoca,</p>\r\n<p class="gathalast">Satte te satthavajjitā.</p>\r\n<p class="hangnum"><a name="para144"></a><span class="paranum">144</span>.</p>\r\n<p class="gatha1"> Duggataṃ gaccha he lābha,</p>\r\n<p class="gatha2">Lābhī lābhena pūrati;</p>\r\n<p class="gatha3">Thale pavassa pajjunna,</p>\r\n<p class="gatha2">Sindhu āpena pūrati;</p>\r\n<p class="gathalast">Natthidaṃ kammappadhānakaṃ.</p>\r\n<p class="hangnum"><a name="para145"></a><span class="paranum">145</span>.</p>\r\n<p class="gatha1"> Na hi koci kate kicce,</p>\r\n<p class="gatha2">Kattāraṃ samupekkhate;</p>\r\n<p class="gatha3">Tasmā sabbāni kiccāni,</p>\r\n<p class="gathalast">Sāva sesena kāraye.</p>\r\n<p class="hangnum"><a name="para146"></a><span class="paranum">146</span>.</p>\r\n<p class="gatha1"> Tūlaṃ sallahukaṃ loke,</p>\r\n<p class="gatha2">Tato cāpalla-jātiko;</p>','\r',char(13)),'\n',char(10)),'-142-143-144-145-146-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',32,replace(replace('<p class="gatha3">Tato <a name="M0.0032"></a> vuḍḍha manovādo,</p>\r\n<p class="gathalast">Pamatto buddhasāsane.</p>\r\n<p class="hangnum"><a name="para147"></a><span class="paranum">147</span>.</p>\r\n<p class="gatha1"> Pāsāṇachattaṃ garukaṃ,</p>\r\n<p class="gatha2">Tato devānacikkhaṇaṃ;</p>\r\n<p class="gatha3">Tato vuḍḍhānamovādo,</p>\r\n<p class="gathalast">Tato buddhassa sāsanaṃ.</p>\r\n<p class="hangnum"><a name="para148"></a><span class="paranum">148</span>.</p>\r\n<p class="gatha1"> Kāyassa dakkhiṇa hattho,</p>\r\n<p class="gatha2">Doso ettha kaniṭṭhako;</p>\r\n<p class="gatha3">Kaṇṇa ghānāna-makkhīnaṃ,</p>\r\n<p class="gathalast">Vāmo tu pāda-pāsako.</p>\r\n<p class="hangnum"><a name="para149"></a><span class="paranum">149</span>.</p>\r\n<p class="gatha1"> Tambūlassa majjha patte,</p>\r\n<p class="gatha2">Kuvero rakkhatī sadā;</p>\r\n<p class="gatha3">Mūlamhi rakkhati yakkho,</p>\r\n<p class="gatha2">Aggamhi kālakaṇṇikā;</p>\r\n<p class="gatha3">Tāni bhuñjeyya chinditvā,</p>\r\n<p class="gathalast">Sirī evaṃ pavaḍḍhati.</p>\r\n<p class="hangnum"><a name="para150"></a><span class="paranum">150</span>.</p>\r\n<p class="gatha1"> Sampuṇṇarakkho brahmāva,</p>\r\n<p class="gatha2">Accurakkho ca bissaṇo;</p>\r\n<p class="gatha3">Tasmā hi te pūjayantu,</p>\r\n<p class="gathalast">Sadā mānenti taṃ naraṃ.</p>\r\n<p class="hangnum"><a name="para151"></a><span class="paranum">151</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-147-148-149-150-151-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',33,replace(replace('<p class="gatha1">Goṇā <a name="M0.0033"></a> hi sabbagihīnaṃ,</p>\r\n<p class="gatha2">Posakā bhogadāyakā;</p>\r\n<p class="gatha3">Tasmā hi mātā pitūva,</p>\r\n<p class="gathalast">Mānaye sakkareyya ca.</p>\r\n<p class="hangnum"><a name="para152"></a><span class="paranum">152</span>.</p>\r\n<p class="gatha1"> Yeca khādanti gomaṃsaṃ,</p>\r\n<p class="gatha2">Mātu maṃsaṃva khādare;</p>\r\n<p class="gatha3">Matesu tesu gijjhānaṃ,</p>\r\n<p class="gathalast">Dade sote ca vāhaye.</p>\r\n<p class="hangnum"><a name="para153"></a><span class="paranum">153</span>.</p>\r\n<p class="gatha1"> Gurusiddho sippārambho,</p>\r\n<p class="gatha2">Ravi sokrā ca majjhimo;</p>\r\n<p class="gatha3">Na sippo buddhacandaro,</p>\r\n<p class="gathalast">Sorī aṅgāca maraṇaṃ.</p>\r\n<p class="hangnum"><a name="para154"></a><span class="paranum">154</span>.</p>\r\n<p class="gatha1"> Aṭṭhamiyaṃ guruṃ hanti,</p>\r\n<p class="gatha2">Sissaṃ hanti catuddasiṃ;</p>\r\n<p class="gatha3">Sippaṃ hanti dasa sippaṃ,</p>\r\n<p class="gathalast">Mātāpitā ca puṇṇamiṃ.</p>\r\n<p class="hangnum"><a name="para155"></a><span class="paranum">155</span>.</p>\r\n<p class="gatha1"> Nāḷikaṃ satta nabhuñje,</p>\r\n<p class="gatha2">Na lābuṃ navamaṃ tathā;</p>\r\n<p class="gatha3">Dvādasa prinnaṃtriminaṃ,</p>\r\n<p class="gathalast">Bhuñje sippaṃ vinassati.</p>\r\n<p class="hangnum"><a name="para156"></a><span class="paranum">156</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-152-153-154-155-156-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',34,replace(replace('<p class="gatha1">Ekaṃ <a name="M0.0034"></a> caje kulaatthaṃ,</p>\r\n<p class="gatha2">Gāmassatthaṃ kuvaṃ caje;</p>\r\n<p class="gatha3">Gāma caje janapadatthaṃ,</p>\r\n<p class="gathalast">Attatthaṃ pathaviṃ caje.</p>\r\n<p class="hangnum"><a name="para157"></a><span class="paranum">157</span>.</p>\r\n<p class="gatha1"> Desaṃ ossajja gacchanti,</p>\r\n<p class="gatha2">Sīho sappuriso gajo;</p>\r\n<p class="gatha3">Tattheva nidhanaṃ yanti,</p>\r\n<p class="gathalast">Kāko kāpuriso migo.</p>\r\n<p class="hangnum"><a name="para158"></a><span class="paranum">158</span>.</p>\r\n<p class="gatha1"> Yamhi padese na māno,</p>\r\n<p class="gatha2">Na pemaṃ na ca bandhavā;</p>\r\n<p class="gatha3">Na ca vijjāgāho koci,</p>\r\n<p class="gathalast">Na tattha vasanaṃ kare.</p>\r\n<p class="hangnum"><a name="para159"></a><span class="paranum">159</span>.</p>\r\n<p class="gatha1"> Caratyekena pādena,</p>\r\n<p class="gatha2">Tiṭṭhatyekena paṇḍito;</p>\r\n<p class="gatha3">A nisamma paraṃ ṭhānaṃ,</p>\r\n<p class="gathalast">Na pubbamālayaṃ jahe.</p>\r\n<p class="hangnum"><a name="para160"></a><span class="paranum">160</span>.</p>\r\n<p class="gatha1"> Dhana dhañña payogesu,</p>\r\n<p class="gatha2">Tathā vijjāgamesu ca;</p>\r\n<p class="gatha3">Dūtesu apacāresu,</p>\r\n<p class="gathalast">Cajjā lajjā tadā bhave.</p>\r\n<p class="hangnum"><a name="para161"></a><span class="paranum">161</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-157-158-159-160-161-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',35,replace(replace('<p class="gatha1">Dvi <a name="M0.0035"></a> guṇo thīnamāhāro,</p>\r\n<p class="gatha2">Buddhicāpi catugguṇo;</p>\r\n<p class="gatha3">Chagguṇo hoti vāyāmo,</p>\r\n<p class="gathalast">Kāmotvaṭṭha-guṇo bhave.</p>\r\n<p class="hangnum"><a name="para162"></a><span class="paranum">162</span>.</p>\r\n<p class="gatha1"> Pabbe pabbe kamenucchu,</p>\r\n<p class="gatha2">Visesarasavāggato;</p>\r\n<p class="gatha3">Tathā sumettiko sādhu,</p>\r\n<p class="gathalast">Viparītova dujjano.</p>\r\n<p class="hangnum"><a name="para163"></a><span class="paranum">163</span>.</p>\r\n<p class="gatha1"> Kassako vāṇijo macco,</p>\r\n<p class="gatha2">Samaṇo suta sīlavā;</p>\r\n<p class="gatha3">Tesu vipula jātesu,</p>\r\n<p class="gathalast">Raṭṭhampi vipulaṃ siyā.</p>\r\n<p class="hangnum"><a name="para164"></a><span class="paranum">164</span>.</p>\r\n<p class="gatha1"> Asajjhāya malā mantā,</p>\r\n<p class="gatha2">Anuṭṭhāna malā gharā;</p>\r\n<p class="gatha3">Malaṃ vaṇṇassa kosajjaṃ,</p>\r\n<p class="gathalast">Pamādo rakkhato malaṃ.</p>\r\n<p class="hangnum"><a name="para165"></a><span class="paranum">165</span>.</p>\r\n<p class="gatha1"> Hīnānaṃ gacchate vittaṃ,</p>\r\n<p class="gatha2">Vīrānaṃ santakattanaṃ;</p>\r\n<p class="gatha3">Vadanti ca hīnā janā,</p>\r\n<p class="gathalast">Pubba-kammappadhānakā.</p>\r\n<p class="hangnum"><a name="para166"></a><span class="paranum">166</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-162-163-164-165-166-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1005n',36,replace(replace('<p class="gatha1">Na <a name="M0.0036"></a> vadanti cevaṃdhīrā,</p>\r\n<p class="gatha2">Vāyamiṃsu sabbakamme;</p>\r\n<p class="gatha3">Na ce sijjhati taṃ kammaṃ,</p>\r\n<p class="gathalast">A-phalaṃ eva ko doso.</p>\r\n<p class="hangnum"><a name="para167"></a><span class="paranum">167</span>.</p>\r\n<p class="gatha1"> Nīcaṃ kulaṃ nipaññaṃ vā,</p>\r\n<p class="gatha2">Nirūpaṃ nibalaṃ samaṃ;</p>\r\n<p class="gatha3">Imaṃ kālaṃ chuttakālaṃ,</p>\r\n<p class="gathalast">Dhanameva visesakaṃ.</p>\r\n<p class="centered">Pakiṇṇakakaṇḍo niṭṭhito.</p>\r\n<p class="gatha1">Paṇḍito sujano kaṇḍo,</p>\r\n<p class="gatha2">Dujjano mitta-itthi ca;</p>\r\n<p class="gatha3">Rājā pakiṇṇako cāti,</p>\r\n<p class="gathalast">Satta-kaṇḍa-vibhūsitaṃ.</p>\r\n<p class="gatha1">Visuddhā cāra-therena,</p>\r\n<p class="gatha2">Visuddhārāma-vāsinā;</p>\r\n<p class="gatha3">Sabba-kulānamatthāya,</p>\r\n<p class="gathalast">Visodhitaṃ pathakkhaye.</p>','\r',char(13)),'\n',char(10)),'-167-');
