INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="chapter">Suttantanīti</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',3,replace(replace('<p class="gatha1">Parā <a name="M0.0003"></a> bhavantaṃ purisaṃ,</p>\r\n<p class="gatha2">Mayaṃ pucchāma gotamaṃ;</p>\r\n<p class="gatha3">Bhavantaṃ puṭṭhu māgamma,</p>\r\n<p class="gathalast">Kiṃ parābhavato mukhaṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',4,replace(replace('<p class="gatha1">Suvijāno <a name="M0.0004"></a> bhavaṃhoti,</p>\r\n<p class="gatha2">Duvijāno parābhavo;</p>\r\n<p class="gatha3">Dhammakāmo bhavaṃ hoti,</p>\r\n<p class="gathalast">Dhammadessī parābhavo.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',8,replace(replace('<p class="gatha1">Itihe <a name="M0.0008"></a> taṃ vijānāma,</p>\r\n<p class="gatha2">Pathamo so parābhavo;</p>\r\n<p class="gatha3">Dutīyaṃ bhagavā brūhi,</p>\r\n<p class="gathalast">Kiṃ parābhavato mukhaṃ.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',9,replace(replace('<p class="gatha1">Asantassa <a name="M0.0009"></a> piyo hoti,</p>\r\n<p class="gatha2">Sante na kurute piyaṃ;</p>\r\n<p class="gatha3">Asataṃ dhammaṃ roceti,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',12,replace(replace('<p class="gatha1">Kammāparādhasattānaṃ<a name="M0.0012"></a>,</p>\r\n<p class="gatha2">Vināse paccupaṭṭhite;</p>\r\n<p class="gatha3">Anayo nayarūpena,</p>\r\n<p class="gathalast">Buddhimākamya tiṭṭhati.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',13,replace(replace('<p class="gatha1">Niddāsīlī <a name="M0.0013"></a> sabhāsīlī,</p>\r\n<p class="gatha2">Anuṭṭhātā ca yo naro;</p>\r\n<p class="gatha3">Alaso kodhapaññāṇo,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',25,replace(replace('<p class="gatha1">Yo <a name="M0.0025"></a> mātaraṃ pitaraṃ vā,</p>\r\n<p class="gatha2">Jiṇṇakaṃ gatayobbanaṃ;</p>\r\n<p class="gatha3">Pahusanto na bharati,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',30,replace(replace('<p class="gatha1">Yo <a name="M0.0030"></a> brāhmaṇaṃ samaṇaṃ vā,</p>\r\n<p class="gatha2">Aññaṃ vāpi vaṇibbakaṃ;</p>\r\n<p class="gatha3">Musāvādena vañceti,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',33,replace(replace('<p class="gatha1">Pahutavitto <a name="M0.0033"></a> puriso,</p>\r\n<p class="gatha2">Sahirañño sabhojano;</p>\r\n<p class="gatha3">Eko bhuñjati sādūni,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',36,replace(replace('<p class="gatha1">Jātithaddho <a name="M0.0036"></a> dhanathaddho,</p>\r\n<p class="gatha2">Gottathaddho ca yo naro;</p>\r\n<p class="gatha3">Sañātiṃ atimaññeti,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',40,replace(replace('<p class="gatha1">Itthidhutto <a name="M0.0040"></a> surādhutto,</p>\r\n<p class="gatha2">Akkhadhutto ca yo naro;</p>\r\n<p class="gatha3">Laddhaṃladdhaṃ vināseti,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',44,replace(replace('<p class="gatha1">Na <a name="M0.0044"></a> bāḷhaṃ itthiṃ gaccheyya,</p>\r\n<p class="gatha2">Sampassaṃ tejasaṅkhayaṃ;</p>\r\n<p class="gatha3">Kāsaṃ sāsaṃ daraṃ bālyaṃ,</p>\r\n<p class="gathalast">Khīṇamedo nigacchati.</p>\r\n<p class="bodytext">(Ka)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',45,replace(replace('<p class="gatha1">Māyācetā <a name="M0.0045"></a> marīcī ca,</p>\r\n<p class="gatha2">Soko rogo upaddavo;</p>\r\n<p class="gatha3">Kharā ca bandhanācetā,</p>\r\n<p class="gathalast">Maccupāso guhāsayo.</p>\r\n<p class="bodytext">(Kha)</p>\r\n<p class="gatha1">Balavanto dubbalā honti,</p>\r\n<p class="gatha2">Thāmavantopi hāyare;</p>\r\n<p class="gatha3">Cakkhumā andhakā honti,</p>\r\n<p class="gathalast">Mātugāmavasaṃgatā.</p>\r\n<p class="bodytext">(Ga)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',46,replace(replace('<p class="gatha1">Guṇavanto <a name="M0.0046"></a> nigguṇā honti,</p>\r\n<p class="gatha2">Paññavantopi hāyare;</p>\r\n<p class="gatha3">Pamuttā bandhanā senti,</p>\r\n<p class="gathalast">Mātugāmavasaṃgatā.</p>\r\n<p class="bodytext">(Gha)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',47,replace(replace('<p class="gatha1">Yasaṃ <a name="M0.0047"></a> kittiṃ dhitiṃ sūraṃ;</p>\r\n<p class="gatha2">Bāhussaccaṃ pajānanaṃ;</p>\r\n<p class="gatha3">Hāpayanti pamattassa;</p>\r\n<p class="gathalast">Kaṭṭhapuñcaṃva pāvako.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',51,replace(replace('<p class="gatha1">Sehi <a name="M0.0051"></a> dārehi santuṭṭho,</p>\r\n<p class="gatha2">Vesiyāsu padussati;</p>\r\n<p class="gatha3">Dussati paradāresu,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>\r\n<p class="bodytext">(Ka)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',60,replace(replace('<p class="gatha1">Mayañca <a name="M0.0060"></a> bhariyaṃ nātikkamāma,</p>\r\n<p class="gatha2">Amheca bhariyā nātikkamanti;</p>\r\n<p class="gatha3">Aññatra tāhi brahmacariyaṃ carāma,</p>\r\n<p class="gathalast">Tasmā hi amhaṃ daharā na miyyare.</p>\r\n<p class="bodytext">(Kha)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',63,replace(replace('<p class="gatha1">Etāsu <a name="M0.0063"></a> jāyare suttamāsu,</p>\r\n<p class="gatha2">Medhāvino honti pahutapaññā;</p>\r\n<p class="gatha3">Bahussutā theraguṇā ca honti,</p>\r\n<p class="gathalast">Tasmā hi amhaṃ daharā na miyyare.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',65,replace(replace('<p class="gatha1">Atītayobbano <a name="M0.0065"></a> poso,</p>\r\n<p class="gatha2">Āneti timbarutthaniṃ;</p>\r\n<p class="gatha3">Tassa issā na supati,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>\r\n<p class="bodytext">(Ka)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',69,replace(replace('<p class="gatha1">Na <a name="M0.0069"></a> dukkhaṃ ahinā daṭṭhaṃ,</p>\r\n<p class="gatha2">Na dukkhaṃ sattiyā hataṃ;</p>\r\n<p class="gatha3">Tañca dukkhañca tibbañca,</p>\r\n<p class="gathalast">Yaṃ passe jiṇṇakaṃ patiṃ.</p>\r\n<p class="bodytext">(Kha)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',70,replace(replace('<p class="gatha1">Natthi <a name="M0.0070"></a> khiṭṭā natthi rati,</p>\r\n<p class="gatha2">Jiṇṇena patinā saha;</p>\r\n<p class="gatha3">Natthi allāpasallāpo,</p>\r\n<p class="gathalast">Jagghituṃpi na sobhati.</p>\r\n<p class="bodytext">(Ga)</p>\r\n<p class="gatha1">Yadā ca daharo daharī,</p>\r\n<p class="gatha2">Mantayiṃsu rahogatā;</p>\r\n<p class="gatha3">Sabbe sokā vinassanti,</p>\r\n<p class="gathalast">Yekeci hadayassitā.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',75,replace(replace('<p class="gatha1">Itthiṃ <a name="M0.0075"></a> soṇḍiṃ vikiraṇiṃ,</p>\r\n<p class="gatha2">Purisaṃ vāpi tādisaṃ;</p>\r\n<p class="gatha3">Issariyasmiṃ thapeti,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',76,replace(replace('<p class="gatha1">Appabhogo <a name="M0.0076"></a> mahātaṇho,</p>\r\n<p class="gatha2">Khattiye jāyate kule;</p>\r\n<p class="gatha3">So ca rajjaṃ patthayati,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',81,replace(replace('<p class="gatha1">‘‘Suvijāno <a name="M0.0081"></a> bhavaṃhoti,</p>\r\n<p class="gatha2">Duvijāno parābhavo’’;</p>\r\n<p class="gathalast">Appabhogo mahātaṇho.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',82,replace(replace('<p class="gatha1">Ete <a name="M0.0082"></a> parābhave loke,</p>\r\n<p class="gatha2">Paṇḍito samavekkhiya;</p>\r\n<p class="gatha3">Ariyo dassanasampanno,</p>\r\n<p class="gathalast">Sa lokaṃ bhajate sivaṃ.</p>\r\n<p class="chapter">Vasalasutta</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',87,replace(replace('<p class="gatha1">Kodhano <a name="M0.0087"></a> upanāhīca,</p>\r\n<p class="gatha2">Pāpamakkhī ca yo naro;</p>\r\n<p class="gatha3">Vipannadiṭṭhī māyāvī,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',89,replace(replace('<p class="gatha1">Ekajaṃ <a name="M0.0089"></a> vā dvijaṃ vāpi,</p>\r\n<p class="gatha2">Yodha pāṇaṃ vihiṃsati;</p>\r\n<p class="gatha3">Yassa pāṇe dayā natthi,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',91,replace(replace('<p class="gatha1">Yo <a name="M0.0091"></a> hanti uparundhati,</p>\r\n<p class="gatha2">Gāmāni nigamāni ca;</p>\r\n<p class="gatha3">Niggāhako samaññāto,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',92,replace(replace('<p class="gatha1">Gāme <a name="M0.0092"></a> vā yadi vā raññe,</p>\r\n<p class="gatha2">Yaṃ paresaṃ mamāyitaṃ;</p>\r\n<p class="gatha3">Theyyā adinnaṃ ādeti,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',94,replace(replace('<p class="gatha1">Yo <a name="M0.0094"></a> have iṇamādāya,</p>\r\n<p class="gatha2">Vuccamāno palāyati;</p>\r\n<p class="gatha3">Na hi te iṇamatthīti,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Yodha kiñcikkhakamyatā,</p>\r\n<p class="gatha2">Pathasmiṃ vajataṃ janaṃ;</p>\r\n<p class="gatha3">Hantvā kiñcikkha mādeti;</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',98,replace(replace('<p class="gatha1">Yo <a name="M0.0098"></a> attahetu parahetu,</p>\r\n<p class="gatha2">Dhanahetu ca yo naro;</p>\r\n<p class="gatha3">Sakkhipuṭṭho musābrūti,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',102,replace(replace('<p class="gatha1">Yo <a name="M0.0102"></a> ñātīnaṃ sakhīnaṃ vā,</p>\r\n<p class="gatha2">Dāresu paṭidissati;</p>\r\n<p class="gatha3">Sahasā sampiyena vā,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',10,replace(replace('<p class="gatha1">Yo <a name="M0.0010"></a>4 mātaraṃ pitaraṃ vā,</p>\r\n<p class="gatha2">Jiṇṇakaṃ gatayobbanaṃ;</p>\r\n<p class="gatha3">Pahusanto na bharati,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',105,replace(replace('<p class="gatha1">Yo <a name="M0.0105"></a> mātaraṃ pitaraṃ vā,</p>\r\n<p class="gatha2">Bhātaraṃ bhaginiṃ sassuṃ;</p>\r\n<p class="gatha3">Hanti roseti vācāya,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',107,replace(replace('<p class="gatha1">Yo <a name="M0.0107"></a> atthaṃ pucchito santo,</p>\r\n<p class="gatha2">Anattha manusāsati;</p>\r\n<p class="gatha3">Paṭicchannena manteti,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',111,replace(replace('<p class="gatha1">Yo <a name="M0.0111"></a> katvā pāpakaṃ kammaṃ,</p>\r\n<p class="gatha2">Māmaṃ jaññāti icchati;</p>\r\n<p class="gatha3">Yo paṭicchannakammanto,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',114,replace(replace('<p class="gatha1">Yo <a name="M0.0114"></a> ve parakulaṃ gantvā,</p>\r\n<p class="gatha2">Bhutvāna sucibhojanaṃ;</p>\r\n<p class="gatha3">Āgataṃ nappaṭipūjeti,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',117,replace(replace('<p class="gatha1">Yo <a name="M0.0117"></a> samaṇaṃ vā brāhmaṇaṃ,</p>\r\n<p class="gatha2">Aññaṃ vāpi vaṇibbakaṃ;</p>\r\n<p class="gatha3">Musāvādena vañceti,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Yo samaṇaṃ vā brāhmaṇaṃ,</p>\r\n<p class="gatha2">Bhattakāle upaṭṭhitaṃ;</p>\r\n<p class="gatha3">Roseti vā na ca deti,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',120,replace(replace('<p class="gatha1">Asantaṃ <a name="M0.0120"></a> yodha pabrūti,</p>\r\n<p class="gatha2">Mohena paliguṇṭhito;</p>\r\n<p class="gatha3">Kiñcanaṃ nijigīsāno,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',122,replace(replace('<p class="gatha1">Yocattānaṃ <a name="M0.0122"></a> samukkaṃse,</p>\r\n<p class="gatha2">Pareca mavajānāti;</p>\r\n<p class="gatha3">Nihīno sena mānena,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',124,replace(replace('<p class="gatha1">Rosako <a name="M0.0124"></a> kadarīyo ca,</p>\r\n<p class="gatha2">Pāpiccho maccharī saṭho;</p>\r\n<p class="gatha3">Ahirīko anottappī,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',127,replace(replace('<p class="gatha1">Yo <a name="M0.0127"></a> buddhaṃ paribhāsati,</p>\r\n<p class="gatha2">Athavā tassa sāvakaṃ;</p>\r\n<p class="gatha3">Paribbajaṃ gahaṭṭhaṃ vā,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',129,replace(replace('<p class="bodytext">Aṭṭhahi <a name="M0.0129"></a> bhikkhave aṅgehi sampannāgatassa upāsakassa patto nikujjitabbo. Bhikkhūnaṃ alābhāya parisakkati, bhikkhūnaṃ anatthāya parisakkati, bhikkhūnaṃ anāvāsāya parisakkati, bhikkhūnaṃ akkosati paribhāsati, bhikkhūbhikkhūhi bhedeti, buddhassa avaṇṇaṃ bhāsati, dhammassa avaṇṇaṃ bhāsati, saṅghassa avaṇṇaṃ bhāsati, anujānāmi bhikkhave imehi aṭṭhahi aṅgehi sampannāgatassa upāsakassa pattaṃ nikujjituṃ.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',132,replace(replace('<p class="gatha1">Yo <a name="M0.0132"></a> anarahaṃ santo,</p>\r\n<p class="gatha2">Arahāti paṭijānāti;</p>\r\n<p class="gatha3">Coro sabrahmake loke,</p>\r\n<p class="gathalast">Taṃ jaññā vasalo iti.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',135,replace(replace('<p class="gatha1">Ete <a name="M0.0135"></a> kho vasalā vuttā,</p>\r\n<p class="gatha2">Mayā ye te pakāsitā;</p>\r\n<p class="gatha3">Na jaccā vasalo hoti,</p>\r\n<p class="gathalast">Na jaccā hoti brāhmaṇo.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> Kammunā vasalo hoti,</p>\r\n<p class="gathalast">Kammunā hoti brāhmaṇo.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',136,replace(replace('<p class="gatha1">Tadamināpi <a name="M0.0136"></a> jānātha,</p>\r\n<p class="gatha2">Yathāhetaṃ nidassanaṃ;</p>\r\n<p class="gatha3">Caṇḍālaputto sopāko,</p>\r\n<p class="gathalast">Mātaṅgo iti vissuto.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',137,replace(replace('<p class="gatha1">So <a name="M0.0137"></a> yasaṃ paramaṃ patto,</p>\r\n<p class="gatha2">Mātaṅgo yaṃ sudullabhaṃ;</p>\r\n<p class="gatha3">Āgacchuṃ tassupaṭṭhānaṃ,</p>\r\n<p class="gathalast">Khattiyā brāhmaṇā bahū.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',138,replace(replace('<p class="gatha1">So <a name="M0.0138"></a> devayānaṃ abhiruyha,</p>\r\n<p class="gatha2">Virajaṃ so mahāpathaṃ;</p>\r\n<p class="gatha3">Kāmarāgaṃ virājetvā,</p>\r\n<p class="gathalast">Brahmalokūpago ahu.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',166,replace(replace('<p class="gatha1">Giriṃ <a name="M0.0166"></a> nakhena khaṇasi,</p>\r\n<p class="gatha2">Ayo dantebhi khādasi;</p>\r\n<p class="gatha3">Jātavedaṃ padahasi,</p>\r\n<p class="gatha3">Yo isiṃ paribhāsati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',167,replace(replace('<p class="gatha1">Āvelitaṃ <a name="M0.0167"></a> piṭṭhito uttamaṅgaṃ,</p>\r\n<p class="gatha2">Bāhuṃ pasāreti akampaṇeyyaṃ;</p>\r\n<p class="gatha3">Setāni akkhīni yathā matassa,</p>\r\n<p class="gathalast">Ko me imaṃ puttamakāsi evaṃ.</p>','\r',char(13)),'\n',char(10)),'');
