INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="book">Sūrassatīnīti</p>\r\n<p class="subhead">Paṭhamo bhāgo</p>\r\n<p class="chapter">Paṇāmagāthā</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',1,replace(replace('<p class="gatha1">Mukhamhā <a name="M1.0001"></a> bhagavantassa,</p>\r\n<p class="gatha2">Sugandhakamalā subhā;</p>\r\n<p class="gatha3">Sañjātaṃ uttamaṃ vāṇiṃ,</p>\r\n<p class="gathalast">Vandāmi vittamānasā.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Samajjhiṭṭho racissāmi,</p>\r\n<p class="gatha2">Dakkhena rājamantinā;</p>\r\n<p class="gatha3">Nāmena santena,</p>\r\n<p class="gatha2">Nītiṃ lokahitāvahaṃ;</p>\r\n<p class="gatha3">Sālaṅkāraṃ sopadesaṃ,</p>\r\n<p class="gathalast">Nāmenāhaṃ svarassatiṃ.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Pekkhantvimaṃ nītiṃ santā,</p>\r\n<p class="gatha2">Mañjūsarukkhasannibhaṃ;</p>\r\n<p class="gatha3">Nānopadesasaṃpuṇṇaṃ,</p>\r\n<p class="gathalast">Sadatthakusalā sadā.</p>\r\n<p class="chapter">Sūrassatīnīti</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-3-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',3,replace(replace('<p class="gatha1">Kataññutā <a name="M1.0003"></a> ca saccañca,</p>\r\n<p class="gatha2">Lokasārā hi te duve;</p>\r\n<p class="gatha3">Lokāpi tehi tiṭṭhanti,</p>\r\n<p class="gathalast">Raṭṭhaṃ akaṃsu issaraṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',8,replace(replace('<p class="gatha1">Kākāca <a name="M1.0008"></a> dujjanā loke,</p>\r\n<p class="gatha2">Malībhūtāva sabbadā;</p>\r\n<p class="gatha3">Iṭṭhaṃ guṇaṃ nāsayanti,</p>\r\n<p class="gathalast">Te ve lokassa verino.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',10,replace(replace('<p class="gatha1">Migānaṃ <a name="M1.0010"></a> siṅgālo anto,</p>\r\n<p class="gathalast">Pakkhīnaṃ pana vāyaso.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',12,replace(replace('<p class="gatha1">Akāraṇaverī <a name="M1.0012"></a> honti,</p>\r\n<p class="gatha2">Macchānaṃ dhīvarā yathā;</p>\r\n<p class="gatha3">Guṇīnaṃ sajjanānañca,</p>\r\n<p class="gathalast">Dujjanā niccaverino.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',14,replace(replace('<p class="gatha1">Siriṃ <a name="M1.0014"></a> bhonto suposetha,</p>\r\n<p class="gatha2">Siri mūlā hi sampadā;</p>\r\n<p class="gatha3">Siriya idha jotanti,</p>\r\n<p class="gathalast">Sirī sā sabbasiddhikā.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',20,replace(replace('<p class="gatha1">Musā <a name="M1.0020"></a> tamokarā loke,</p>\r\n<p class="gatha2">Saccaṃ mīditikārakaṃ;</p>\r\n<p class="gatha3">Musātamena dukkhanti,</p>\r\n<p class="gathalast">Saccābhāya sukhantive.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',23,replace(replace('<p class="gatha1">Suvijānaṃ <a name="M1.0023"></a> siṅgālānaṃ,</p>\r\n<p class="gatha2">Sakuṇānañca vassitaṃ;</p>\r\n<p class="gatha3">Manussavassitaṃ rāja,</p>\r\n<p class="gathalast">Dubbijānataraṃ tato.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',25,replace(replace('<p class="gatha1">Kappaggisadisā <a name="M1.0025"></a> issā,</p>\r\n<p class="gatha2">Jhāpeti sabbasampadaṃ;</p>\r\n<p class="gatha3">Muditā kappameghova,</p>\r\n<p class="gathalast">Ropeti sabbasampadaṃ.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',30,replace(replace('<p class="gatha1">Yathā <a name="M1.0030"></a> asanthirā thambhā,</p>\r\n<p class="gatha2">Thusarāsimhi ussitā;</p>\r\n<p class="gatha3">Tatheva kapicittānaṃ,</p>\r\n<p class="gathalast">Kammantā cañcalaṅgatā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',34,replace(replace('<p class="gatha1">Haliddirāgaṃ <a name="M1.0034"></a> kapicittaṃ;</p>\r\n<p class="gatha2">Purisaṃ rāgavirāginaṃ,</p>\r\n<p class="gatha3">Edisaṃ tāta māsevi;</p>\r\n<p class="gathalast">Nimmanussampi ce siyā.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>. [Ka]</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',35,replace(replace('<p class="gatha1">Pupphacumbi <a name="M1.0035"></a> cittapattī,</p>\r\n<p class="gatha2">Sakavaṇṇena majjito;</p>\r\n<p class="gatha3">Vikkamī pupphato pupphaṃ,</p>\r\n<p class="gathalast">Nassāgāraṃ na saṅgamo.</p>\r\n<p class="bodytext">[Kha]</p>\r\n<p class="gatha1">Kippilikā dubbaṇṇāpi,</p>\r\n<p class="gatha2">Samaggāca parakkamā;</p>\r\n<p class="gatha3">Mā hotha pupphacumbīva,</p>\r\n<p class="gathalast">Hotha vo pacikā yathā.</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',40,replace(replace('<p class="gatha1">Ye <a name="M1.0040"></a> maranti kīḷantā te,</p>\r\n<p class="gatha2">Svānā aññoññamoditā;</p>\r\n<p class="gatha3">Disvāna chaṭṭitaṃ bhattaṃ,</p>\r\n<p class="gathalast">Sīghaverī vihiṃsare.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',41,replace(replace('<p class="gatha1">Tathekepi <a name="M1.0041"></a> janā dāni,</p>\r\n<p class="gatha2">Samaggā ññoññamoditā;</p>\r\n<p class="gatha3">Dhanahetu vihiṃsanti,</p>\r\n<p class="gathalast">Dhīratthu sīghaverikā.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',45,replace(replace('<p class="gatha1">Vasante <a name="M1.0045"></a> hemante gimhe;</p>\r\n<p class="gatha2">Neva tālā visesino;</p>\r\n<p class="gatha3">Thiracittā janā santā;</p>\r\n<p class="gathalast">Sukhadukkhesu niccalā.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',50,replace(replace('<p class="gatha1">Yathā <a name="M1.0050"></a> pavaṭṭamānamhi,</p>\r\n<p class="gatha2">Suṭṭhu tiṭṭhati geṇṭhuke;</p>\r\n<p class="gatha3">Appavaṭṭe bhūmyaṃ seti,</p>\r\n<p class="gathalast">Tatheva geṇṭhuko jano.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',55,replace(replace('<p class="gatha1">Agghāpetuṃ <a name="M1.0055"></a> nasakkonti,</p>\r\n<p class="gatha2">Kālañhi kālikā janā;</p>\r\n<p class="gatha3">Vajirādiñca sakkonti,</p>\r\n<p class="gathalast">Tena kālo anagghiko.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>. [Ka]</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',59,replace(replace('<p class="gatha1">Asano <a name="M1.0059"></a> hi dīghaddhāno,</p>\r\n<p class="gatha2">Sārasāro sugandhiko;</p>\r\n<p class="gatha3">Niggandho tveva nissāro,</p>\r\n<p class="gathalast">Dīghaddhānopi simbalī.</p>\r\n<p class="bodytext">[Kha]</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',60,replace(replace('<p class="gatha1">Tatheveke <a name="M1.0060"></a> janā loke,</p>\r\n<p class="gatha2">Dīghaddhānā susārakā;</p>\r\n<p class="gatha3">Nissārā keci pheggūva,</p>\r\n<p class="gathalast">Dīghaddhānāpi goyathā.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',64,replace(replace('<p class="gatha1">Upakāro <a name="M1.0064"></a> cāpakāro,</p>\r\n<p class="gatha2">Yasmiṃ gacchati naṭṭhataṃ;</p>\r\n<p class="gatha3">Pāsāṇahadayassassa,</p>\r\n<p class="gathalast">Jīvatītyā bhidhāmudhā.</p>\r\n<p class="gatha1">Pasādo nipphalo yassa,</p>\r\n<p class="gatha2">Kopocāpi niratthako;</p>\r\n<p class="gatha3">Na taṃ saṅgantu miccheyya,</p>\r\n<p class="gathalast">Thīpumāva napuṃsakaṃ.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',69,replace(replace('<p class="gatha1">Upacāro <a name="M1.0069"></a> hi kātabbo,</p>\r\n<p class="gatha2">Na yāva sohadaṃ bhave;</p>\r\n<p class="gatha3">Upacāro sumittamhi,</p>\r\n<p class="gathalast">Māyā ca hoti koṭilaṃ.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',75,replace(replace('<p class="gatha1">Kamena <a name="M1.0075"></a> aggato ucchu,</p>\r\n<p class="gatha2">Raso sādutaro yathā;</p>\r\n<p class="gatha3">Tatheva sumitto loke,</p>\r\n<p class="gathalast">Dummitto pana nediso.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',80,replace(replace('<p class="gatha1">Sokārāti <a name="M1.0080"></a> parittāṇaṃ,</p>\r\n<p class="gatha2">Vissāsapītibhājanaṃ;</p>\r\n<p class="gatha3">Ratanābhiratanaṃ icche,</p>\r\n<p class="gathalast">Sumittaṃ akkharattayaṃ.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',85,replace(replace('<p class="gatha1">Dampatīnaṃ <a name="M1.0085"></a> sumittānaṃ,</p>\r\n<p class="gatha2">Mukhaṃ aññoññadappaṇaṃ,</p>\r\n<p class="gatha3">Sukhe sukhaṃ dukkhe dukkhaṃ,</p>\r\n<p class="gathalast">Paṭicchāyeva dappaṇe.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',88,replace(replace('<p class="gatha1">Kathaṃ <a name="M1.0088"></a> nu tāsaṃ hadayaṃ,</p>\r\n<p class="gatha2">Sukharāvata itthiyo;</p>\r\n<p class="gatha3">Yāsāmike dukkhitamhi,</p>\r\n<p class="gathalast">Sukhamicchanti attano.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',90,replace(replace('<p class="gatha1">Nivātañca <a name="M1.0090"></a> pure katvā,</p>\r\n<p class="gatha2">Mānaṃ katvāna pacchato;</p>\r\n<p class="gatha3">Sakatthaṃ dhāraye dhīro,</p>\r\n<p class="gathalast">Atthabhañjo hi muḷhatā.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',94,replace(replace('<p class="gatha1">Purecāraṃ <a name="M1.0094"></a> satiṃ katvā,</p>\r\n<p class="gatha2">Saddhaṃ kareyya pacchato;</p>\r\n<p class="gatha3">Turaṃ na saddahe dhīro,</p>\r\n<p class="gathalast">Sīghasaddho hi mandako.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> Vane 0.0099 bahūni kaṭṭhāni;</p>\r\n<p class="gatha2">Dullabhaṃ rattacandanaṃ;</p>\r\n<p class="gatha3">Tathā janā bahū loke;</p>\r\n<p class="gathalast">Pumā jañño sudullabho.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',103,replace(replace('<p class="gatha1">Tiṇakaṭṭhapalāsehi<a name="M1.0103"></a>;</p>\r\n<p class="gatha2">Sukkhehi dayhate vanaṃ;</p>\r\n<p class="gatha3">Etādīhi asārehi;</p>\r\n<p class="gathalast">Loko janehi dayhate.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',107,replace(replace('<p class="gatha1">Antovasse <a name="M1.0107"></a> timāsamhi,</p>\r\n<p class="gatha2">Puññakammena moditā;</p>\r\n<p class="gatha3">Sukhaṃ vasiṃsu porāṇā,</p>\r\n<p class="gathalast">Buddhasāsanamāmakā.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',111,replace(replace('<p class="gatha1">Migamadena <a name="M1.0111"></a> ekena,</p>\r\n<p class="gatha2">Taṃ vanaṃ surabhigandhikaṃ;</p>\r\n<p class="gatha3">Tathā janena taṃ raṭṭhaṃ,</p>\r\n<p class="gathalast">Guṇinā hi sirīmatā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',115,replace(replace('<p class="gatha1">Sarīraṃ <a name="M1.0115"></a> khaṇaviddhaṃsī,</p>\r\n<p class="gathalast">Kappantaṭṭhāyino guṇā.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',116,replace(replace('<p class="gatha1">Kharānaṃ <a name="M1.0116"></a> sīhabyagghānaṃ,</p>\r\n<p class="gatha2">Saṅgamo no hisabbadā;</p>\r\n<p class="gatha3">Tatheva byagghacittānaṃ,</p>\r\n<p class="gathalast">Sajātikā khayonatā.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',121,replace(replace('<p class="gatha1">Saka <a name="M1.0121"></a> sādhupi no sādhū,</p>\r\n<p class="gatha2">Yo ceñña duṭṭhakārako;</p>\r\n<p class="gatha3">Bahūnaṃ sādhū pāyena,</p>\r\n<p class="gathalast">Sa ve sādhūti vuccate.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-26-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',126,replace(replace('<p class="gatha1">Bahūdake <a name="M1.0126"></a> samuddepi,</p>\r\n<p class="gatha2">Jalaṃ nattheva pātave;</p>\r\n<p class="gatha3">Khuddake khatakūpamhi,</p>\r\n<p class="gathalast">Sāduṃ atthi bahuṃ dakaṃ.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',130,replace(replace('<p class="gatha1">Mā <a name="M1.0130"></a> sīghaṃ vivareyyātha,</p>\r\n<p class="gatha2">Nindituñca pasaṃsituṃ;</p>\r\n<p class="gatha3">Mukhañhi vo kathādvāraṃ,</p>\r\n<p class="gathalast">Nirundheyyātha sabbadā.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',134,replace(replace('<p class="gatha1">Mā <a name="M1.0134"></a> sīghaṃ vivareyyātha,</p>\r\n<p class="gatha2">Cakkhuṃ vo dassituṃ piyaṃ;</p>\r\n<p class="gatha3">Saṇikañhi piyalābhaṃ,</p>\r\n<p class="gathalast">Dhanalābhaṃ turaṃ kare.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',138,replace(replace('<p class="gatha1">Anārambho <a name="M1.0138"></a> hi kammānaṃ,</p>\r\n<p class="gatha2">Paṭhamaṃ buddhilakkhaṇaṃ;</p>\r\n<p class="gatha3">Niṭṭhaṅgataṃ āraddhassa,</p>\r\n<p class="gathalast">Dutiyaṃ buddhilakkhaṇaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',140,replace(replace('<p class="gatha1">Asamekkhitakammantaṃ<a name="M1.0140"></a>,</p>\r\n<p class="gatha2">Turitābhinipātinaṃ;</p>\r\n<p class="gatha3">Sāni kammāni tappenti,</p>\r\n<p class="gathalast">Uṇhaṃ vajjhohaṭaṃ mukhe.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',143,replace(replace('<p class="gatha1">Aphalāni <a name="M1.0143"></a> durantāni,</p>\r\n<p class="gatha2">Janatā ninditāni ca;</p>\r\n<p class="gatha3">Asakyāni ca kammāni,</p>\r\n<p class="gathalast">Nārabhetha vicakkhaṇo.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',147,replace(replace('<p class="gatha1">Ativirodhabhītānaṃ<a name="M1.0147"></a>,</p>\r\n<p class="gatha2">Saṅkitānaṃ pade pade;</p>\r\n<p class="gatha3">Parappavādatāsānaṃ,</p>\r\n<p class="gathalast">Dūrato yanti sampadā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',150,replace(replace('<p class="gatha1">Saddamattaṃ <a name="M1.0150"></a> na bhetabbaṃ,</p>\r\n<p class="gatha2">Loko saddassa gocaro;</p>\r\n<p class="gatha3">Yo ca saddaparittāso,</p>\r\n<p class="gathalast">Vane bhantamigo hi so.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',151,replace(replace('<p class="gatha1">Dvinnaṃ <a name="M1.0151"></a> taṇḍulathūsānaṃ,</p>\r\n<p class="gatha2">Viseso suṭṭhu khāyati;</p>\r\n<p class="gatha3">Randhitopi siniddho no,</p>\r\n<p class="gatha2">Thuso virasaphāruso;</p>\r\n<p class="gatha3">Taṇḍulaṃ siniddhaṃ rasaṃ,</p>\r\n<p class="gathalast">Evaṃ lokepi ñāyate.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',155,replace(replace('<p class="gatha1">Eraṇḍaṃ <a name="M1.0155"></a> nissitā valli,</p>\r\n<p class="gatha2">Ruhate kiṃ yathābalaṃ;</p>\r\n<p class="gatha3">Mahāsālaṃ sunissāya,</p>\r\n<p class="gathalast">Ruhate brahataṃ gatā.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-34-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',160,replace(replace('<p class="gatha1">Mettā <a name="M1.0160"></a> hi sīmasambhedā,</p>\r\n<p class="gatha2">Pakkhapāta vighātikā;</p>\r\n<p class="gatha3">Pakkhapātena dukkhanti,</p>\r\n<p class="gathalast">Nippakkho vasate sukhaṃ.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',164,replace(replace('<p class="gatha1">Narā <a name="M1.0164"></a> paññā ca laṅkārā,</p>\r\n<p class="gatha2">Yathāṭhāne niyujjare;</p>\r\n<p class="gatha3">No hi cūḷāmaṇi pāde,</p>\r\n<p class="gathalast">Pādukā ca siropari.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',167,replace(replace('<p class="gatha1">Ukkuṭṭhe <a name="M1.0167"></a> sūra micchanti;</p>\r\n<p class="gatha2">Mantīsu akutūhalaṃ;</p>\r\n<p class="gatha3">Viyañca annapānamhi;</p>\r\n<p class="gathalast">Atthe jāte ca paṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-36-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',169,replace(replace('<p class="gatha1">Pamādo <a name="M1.0169"></a> hi tamo loke,</p>\r\n<p class="gatha2">Kālo coro bhayānako;</p>\r\n<p class="gatha3">Kāyagehaṃ bahuchiddaṃ,</p>\r\n<p class="gathalast">Kālacorassa coritaṃ.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',173,replace(replace('<p class="gatha1">Cañcalo <a name="M1.0173"></a> kāladāso hi,</p>\r\n<p class="gatha2">Dhītimā kālaissaro;</p>\r\n<p class="gatha3">Kālissaro raṭṭhissaraṃ,</p>\r\n<p class="gathalast">Ativattati sabbaso.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',17,replace(replace('<p class="gatha1">Viluppanti <a name="M1.0017"></a>8 dhanaṃ eke,</p>\r\n<p class="gatha2">Kālameke anekkhakā;</p>\r\n<p class="gatha3">Tesu kālavilopāva,</p>\r\n<p class="gathalast">Bhayānakā tikakkhaḷā.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',182,replace(replace('<p class="gatha1">Raññā <a name="M1.0182"></a> raṭṭhahitaṃ kattā,</p>\r\n<p class="gatha2">Rañño hitaṃ janehi ve;</p>\r\n<p class="gatha3">Desso attahitaṃ dassī,</p>\r\n<p class="gathalast">Gārayho kinnu kārako.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',189,replace(replace('<p class="gatha1">Attadatthaṃ <a name="M1.0189"></a> paratthena;</p>\r\n<p class="gathalast">Bahunāpi na hāpaye.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> Yassa upakāro dinno,</p>\r\n<p class="gatha2">Upakāraṃ dade puna;</p>\r\n<p class="gatha3">Tato pakāraṃ niccheyya,</p>\r\n<p class="gathalast">Kataññū dullabho idha.</p>','\r',char(13)),'\n',char(10)),'-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',190,replace(replace('<p class="gatha1">Saccaṃ <a name="M1.0190"></a> kireva māhaṃsu,</p>\r\n<p class="gatha2">Narā ekacciyā idha;</p>\r\n<p class="gatha3">Kaṭṭhaṃ niplavitaṃ seyyo,</p>\r\n<p class="gathalast">Natveve kacciyo naro.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',191,replace(replace('<p class="gatha1">Kāruko <a name="M1.0191"></a> sakapaññāya,</p>\r\n<p class="gatha2">Mahagghaṃ dārukaṃ kare;</p>\r\n<p class="gatha3">Tathā janopi attānaṃ,</p>\r\n<p class="gathalast">Mahaggho lokamānito.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',196,replace(replace('<p class="gatha1">Appaggho <a name="M1.0196"></a> hi ayo hemaṃ,</p>\r\n<p class="gatha2">Mahagghaṃ chindate yathā;</p>\r\n<p class="gatha3">Nigguṇo saguṇaṃ loke,</p>\r\n<p class="gathalast">Alakkhīca siriṃ tathā.</p>\r\n<p class="title">Sūrassatīnīti</p>\r\n<p class="subhead">Dutiyo bhāgo</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',1,replace(replace('<p class="gatha1">Dhanassa <a name="M2.0001"></a> dubbidhaṃ kiccaṃ,</p>\r\n<p class="gatha2">Pāpeti uṇṇataṃ dhaniṃ;</p>\r\n<p class="gatha3">Adhaniṃ oṇataṃ loke,</p>\r\n<p class="gathalast">Sañcine tena taṃ dhanaṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',6,replace(replace('<p class="gatha1">Vaṭṭate <a name="M2.0006"></a> satataṃ sīghaṃ,</p>\r\n<p class="gatha2">Kālacakkaṃ avāritaṃ;</p>\r\n<p class="gatha3">Tena ghaṭī dinaṃ māso,</p>\r\n<p class="gathalast">Vasso bhavatya cīrato.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',11,replace(replace('<p class="gatha1">Sattunā <a name="M2.0011"></a> na hi sandheyya,</p>\r\n<p class="gatha2">Ekadā so bhayaṃ karo;</p>\r\n<p class="gatha3">Sutattamapi pānīyaṃ,</p>\r\n<p class="gathalast">Samayate nu pāvakaṃ.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',16,replace(replace('<p class="gatha1">Vijahaṃ <a name="M2.0016"></a> pakatiṃ yo hi,</p>\r\n<p class="gatha2">Vikatiṃ puna gacchati;</p>\r\n<p class="gatha3">Sabhāvena ākārena,</p>\r\n<p class="gatha2">Vippallāsaṃ sa gacchati;</p>\r\n<p class="gatha3">Saṃsumāra gatā godhā,</p>\r\n<p class="gathalast">Yathā thī pumavesikā.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',20,replace(replace('<p class="gatha1">Pakkhaṃ <a name="M2.0020"></a> laddhāna uḍḍenti,</p>\r\n<p class="gatha2">Upacikā hi vammikā;</p>\r\n<p class="gatha3">Nikkhantā maraṇaṃ yanti,</p>\r\n<p class="gatha1">Uppatā nippataṃ gatā.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',23,replace(replace('<p class="gatha1">Sabbaṃpiyassa <a name="M2.0023"></a> dajjeyya,</p>\r\n<p class="gatha2">Nissesaṃ piyamānasaṃ;</p>\r\n<p class="gatha3">Saddhācittaṃ tu no viññū,</p>\r\n<p class="gathalast">Saddhāyiko pakkhalito.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',27,replace(replace('<p class="gatha1">Sakkoti <a name="M2.0027"></a> laṅghituṃ byāmaṃ,</p>\r\n<p class="gatha2">Mahussāhena yo hi so;</p>\r\n<p class="gatha3">Tadaḍḍhaṃ anussāhena,</p>\r\n<p class="gatha2">Nossāho tesu thomito;</p>\r\n<p class="gatha3">Mahussāho dukkho loke,</p>\r\n<p class="gathalast">Anussāho sadā sukho.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',32,replace(replace('<p class="gatha1">Dukkhamaṃ <a name="M2.0032"></a> akkhamanto yo,</p>\r\n<p class="gatha2">Piṭṭhikārīca dukkaraṃ,</p>\r\n<p class="gatha3">Kadā labheyya so loke,</p>\r\n<p class="gatha2">Sukhamaṃ sukaraṃ mudhā;</p>\r\n<p class="gatha3">Paccakkhañhi so kareyya,</p>\r\n<p class="gathalast">Dukkhamañcāpi dukkaraṃ.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',36,replace(replace('<p class="gatha1">Anaggho <a name="M2.0036"></a> manusso loke,</p>\r\n<p class="gatha2">Tosanāposanādinā;</p>\r\n<p class="gatha3">Tena so mahagghaṃ kammaṃ,</p>\r\n<p class="gathalast">Kare lokahitāyutaṃ.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',41,replace(replace('<p class="gatha1">Vātena <a name="M2.0041"></a> nappabhijjanti,</p>\r\n<p class="gatha2">Ninnā veḷū kasā naḷā;</p>\r\n<p class="gatha3">Yathāvātaṃ nagacchanti,</p>\r\n<p class="gathalast">Tathā care jane kadā.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',44,replace(replace('<p class="gatha1">Purato <a name="M2.0044"></a> ca pacchato ce,</p>\r\n<p class="gatha2">Nissayo natthi passato;</p>\r\n<p class="gatha3">Adhikaṃ vīriyaṃ hoti,</p>\r\n<p class="gathalast">Attanātho tadā bhave.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',48,replace(replace('<p class="gatha1">Khaṇaṃ <a name="M2.0048"></a> ākhubilaṃ sīho,</p>\r\n<p class="gatha2">Pāsāṇasakalākulaṃ;</p>\r\n<p class="gatha3">Pappoti nakhabhaṅgaṃvā,</p>\r\n<p class="gathalast">Phalaṃvā mūsiko bhave.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',52,replace(replace('<p class="gatha1">Maggamuḷhā <a name="M2.0052"></a> janā andhā,</p>\r\n<p class="gatha2">Amaggā maggasaññino;</p>\r\n<p class="gatha3">Tatheva duppaññā muḷhā,</p>\r\n<p class="gathalast">Tathatthaṃ nāva bujjhare.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',56,replace(replace('<p class="gatha1">Piyarūpaṃ <a name="M2.0056"></a> vīrarūpaṃ,</p>\r\n<p class="gatha2">Duvidhā rūpasampadā,</p>\r\n<p class="gatha3">Nāriṃ icche piyarūpiṃ,</p>\r\n<p class="gathalast">Purisaṃ vīrarūpakaṃ.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',60,replace(replace('<p class="gatha1">Pajjalanti <a name="M2.0060"></a> hi khajjotā,</p>\r\n<p class="gatha2">Pakkhacālanakammunā;</p>\r\n<p class="gatha3">Kusitā supitā nete,</p>\r\n<p class="gathalast">Tathā janāpi kammikā.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',65,replace(replace('<p class="gatha1">Kummasaṅkocamopamya<a name="M2.0065"></a>,</p>\r\n<p class="gatha2">Niggahamapi saṃkhame;</p>\r\n<p class="gatha3">Pattakāle tu nītiñño,</p>\r\n<p class="gathalast">Kaṇhasappova uṭṭhahe.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',69,replace(replace('<p class="gatha1">Bhakkhasesaṃ <a name="M2.0069"></a> nakhādanti,</p>\r\n<p class="gatha2">Sīhā unnatacetasā;</p>\r\n<p class="gatha3">Paraṃpi napaṇāmenti,</p>\r\n<p class="gathalast">Vuddhikāmā tathā care.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',72,replace(replace('<p class="gatha1">Na <a name="M2.0072"></a>va vasso samuppanno,</p>\r\n<p class="gatha2">Khīṇo purāṇahāyano;</p>\r\n<p class="gatha3">Navavasse navā mettā,</p>\r\n<p class="gathalast">Bhāvitabbā hitesinā.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',76,replace(replace('<p class="gatha1">Santāpayanti <a name="M2.0076"></a> kamayāpyabhujaṃ na rogā,</p>\r\n<p class="gatha2">Dummantinaṃ kamupayanti na nītidosā;</p>\r\n<p class="gatha3">Kaṃ srī na mānayati kaṃ na ca hanti maccu,</p>\r\n<p class="gatha1">Kaṃ thīkatā na visayā paripīḷayanti.</p>\r\n<p class="bodytext">(Vasantatilakāgāthā.)</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> (Ka) </p>','\r',char(13)),'\n',char(10)),'-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',81,replace(replace('<p class="gatha1">byāmamattena <a name="M2.0081"></a> daṇḍena,</p>\r\n<p class="gatha2">Yolumbya udakaṃ mine;</p>\r\n<p class="gatha3">Agambhīraṃ gambhīraṃvā,</p>\r\n<p class="gathalast">Agādhe maññi gambhīraṃ.</p>\r\n<p class="bodytext">(Kha)</p>\r\n<p class="gatha1">Tathā mando saññāṇena,</p>\r\n<p class="gatha2">Agādhe maññi paṇḍitaṃ;</p>\r\n<p class="gatha3">Samāsamaṃ na jānāti,</p>\r\n<p class="gathalast">Bahvappaṃ tikkhamandataṃ.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',86,replace(replace('<p class="gatha1">Duṭṭhakamme <a name="M2.0086"></a> saṅgamanti,</p>\r\n<p class="gatha2">Chekakamme ca no idha;</p>\r\n<p class="gatha3">Maccuṃ vahanti sīsena,</p>\r\n<p class="gathalast">Te muḷhā muḷhasaṅgamā.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',91,replace(replace('<p class="gatha1">Duvidho <a name="M2.0091"></a> saṅgamo loke,</p>\r\n<p class="gatha2">Ujuko kuṭilo bhave;</p>\r\n<p class="gatha3">Ujukova pasaṃseyyo,</p>\r\n<p class="gathalast">Nohyañño sājasaṅgamo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',93,replace(replace('<p class="bodytext">Te <a name="M2.0093"></a> iminā upāyena samaggā sammodamānā mahā bhittipiṭṭhikāya vasanti. (Mahosadhajātaka aṭṭhakathā)</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',95,replace(replace('<p class="gatha1">Yūthikā <a name="M2.0095"></a> pupphate nohi,</p>\r\n<p class="gatha2">Siñcitāpi punappunaṃ;</p>\r\n<p class="gatha3">Pupphate sampatte kāle,</p>\r\n<p class="gathalast">Evaṃ dhāretha vīriyaṃ.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',101,replace(replace('<p class="gatha1">Dhanuccayo <a name="M2.0101"></a> dhanakkhepo,</p>\r\n<p class="gatha2">Duvidhā hi dhanākati;</p>\r\n<p class="gatha3">Dhanuccaye nayo atthi,</p>\r\n<p class="gathalast">Dhanakkhepamhi no idha.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',105,replace(replace('<p class="gatha1">Amātā <a name="M2.0105"></a> pitarasaṃ vaḍḍhaṃ,</p>\r\n<p class="gatha2">Jūtakārañca cañcalaṃ;</p>\r\n<p class="gatha3">Nālapeyya visesaññū,</p>\r\n<p class="gathalast">Yadicche siddhi mattano.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',109,replace(replace('<p class="gatha1">Haliddirāgaṃ <a name="M2.0109"></a> kapicittaṃ,</p>\r\n<p class="gatha2">Purisaṃ rāgavirāginaṃ;</p>\r\n<p class="gatha3">Edisaṃ tāta māsevi,</p>\r\n<p class="gathalast">Nimmanussaṃpi ce siyā.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Guṇā guṇaññūsu guṇā bhavanti,</p>\r\n<p class="gatha2">Te nigguṇaṃ patvā bhavanti dosā;</p>\r\n<p class="gatha3">Āsādyatoyā pabhavanti najjo,</p>\r\n<p class="gathalast">Samudramāsajja bhavantyapeyyā.</p>\r\n<p class="bodytext">(Upajātigāthā)</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-26-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',113,replace(replace('<p class="gatha1">Silārūpaṃ <a name="M2.0113"></a> nimminanti,</p>\r\n<p class="gatha2">Koṭṭetvāna punappunaṃ;</p>\r\n<p class="gatha3">Koṭṭakova tathā bālā,</p>\r\n<p class="gathalast">Sādhuṃ ovajja nimmitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',116,replace(replace('<p class="chapter">Cāṇakyanītilā gāthā</p>\r\n<p class="gatha1">Lālane <a name="M2.0116"></a> bahavo dosā,</p>\r\n<p class="gatha3">Tāḷane bahavo guṇā;</p>\r\n<p class="gatha3">Tasmā puttañca sissañca,</p>\r\n<p class="gathalast">Tāḷaye na ca lālaye.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',117,replace(replace('<p class="gatha1">Atītassa <a name="M2.0117"></a> hi mittassa,</p>\r\n<p class="gatha2">Yo ce dosaṃ pakāsaye;</p>\r\n<p class="gatha3">So have paccuppannassa,</p>\r\n<p class="gathalast">Dosaṃ bhāseti ñāyati.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',121,replace(replace('<p class="gatha1">Latāviya <a name="M2.0121"></a> sevakā te,</p>\r\n<p class="gatha2">Ye nissayaṃ palambare;</p>\r\n<p class="gatha3">Nissayassa vināsena,</p>\r\n<p class="gathalast">Bhūmyaṃ senti anāthakā.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',125,replace(replace('<p class="gatha1">Dosasiṅgehi <a name="M2.0125"></a> vijjhanto,</p>\r\n<p class="gatha2">Mānakhūrehi akkamaṃ;</p>\r\n<p class="gatha3">Bhayaṃ karoti lokamhi,</p>\r\n<p class="gathalast">Gova bālo vihiṃsako.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',128,replace(replace('<p class="gatha1">Ādo <a name="M2.0128"></a> upari lokoyaṃ,</p>\r\n<p class="gatha2">Ujulekhāya tiṭṭhati;</p>\r\n<p class="gatha3">Musāvātehi taṃlokaṃ,</p>\r\n<p class="gathalast">Nipātesi anajjavaṃ.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',132,replace(replace('<p class="gatha1">Sughaṭaṃ <a name="M2.0132"></a> kumbhakārena,</p>\r\n<p class="gatha2">Nāraho paribhuñjituṃ;</p>\r\n<p class="gatha3">Tathūpamāya vekkheyya,</p>\r\n<p class="gathalast">Sakammaparakammani.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',136,replace(replace('<p class="gatha1">Anantaraṃsī <a name="M2.0136"></a> sūropi,</p>\r\n<p class="gatha2">Nasakkoti ghanaṃ tamaṃ;</p>\r\n<p class="gatha3">Vijjhituṃ raṃsiyā loke,</p>\r\n<p class="gatha2">Tathā madanamohitā;</p>\r\n<p class="gatha3">Nasakkonti madaṃ bhetvā,</p>\r\n<p class="gathalast">Paññābhāya pabhāsituṃ.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-34-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',139,replace(replace('<p class="gatha1">Khedaveraṃ <a name="M2.0139"></a> daliddamhi,</p>\r\n<p class="gatha2">Bhogimhi rogupaddavaṃ;</p>\r\n<p class="gatha3">Dessaverañca āṇimhi,</p>\r\n<p class="gathalast">Passe lokassa veritaṃ.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',144,replace(replace('<p class="gatha1">Saṃladdhena <a name="M2.0144"></a> subhogena,</p>\r\n<p class="gatha2">Jīvaṃ suddhaṃ kare nijaṃ;</p>\r\n<p class="gatha3">Seṭṭho so tena jīvena,</p>\r\n<p class="gathalast">Jeguccho malajīviko.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-36-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',148,replace(replace('<p class="gatha1">Vajira <a name="M2.0148"></a> puppharāgānaṃ,</p>\r\n<p class="gatha2">Visesaṃ yo nabujjhati;</p>\r\n<p class="gatha3">Kathañhi so vikkīṇeyya,</p>\r\n<p class="gathalast">Kīṇeyya vā yathātathaṃ.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',152,replace(replace('<p class="gatha1">Kippīli <a name="M2.0152"></a> kopi cintetvā,</p>\r\n<p class="gatha2">Pabbataṃ bhettu mussahaṃ;</p>\r\n<p class="gatha3">Abalā tanumajjhattā,</p>\r\n<p class="gathalast">Cintā hasyāva sā mudhā.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',155,replace(replace('<p class="gatha1">Jātamattaṃ <a name="M2.0155"></a> na yo sattuṃ,</p>\r\n<p class="gatha2">Rogañcūpasamaṃ naye;</p>\r\n<p class="gatha3">Mahābalopi teneva,</p>\r\n<p class="gathalast">Vuddhiṃpatvā sa haññate.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',159,replace(replace('<p class="gatha1">Sajīvamaṃsabhakkhehi<a name="M2.0159"></a>,</p>\r\n<p class="gatha2">Sadāṭhīhi mukhehi bho;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',160,replace(replace('<p class="gatha3">Biḷārabyagghasīhānaṃ<a name="M2.0160"></a>,</p>\r\n<p class="gatha2">Nihīnāni anekadhā;</p>\r\n<p class="gatha3">Tikkhāni kharavādāni,</p>\r\n<p class="gathalast">Manussānaṃ mukhāni ve.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',163,replace(replace('<p class="gatha1">Viluppantā <a name="M2.0163"></a> vidhāvanti,</p>\r\n<p class="gatha2">Sajīvavuttikammunā;</p>\r\n<p class="gatha3">Janā tena vihaññanti,</p>\r\n<p class="gathalast">Caranti dhammavemukhā.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',167,replace(replace('<p class="gatha1">Sulabhaṃ <a name="M2.0167"></a> lokiyaṃ loke,</p>\r\n<p class="gatha2">Sāsanīyaṃva dullabhaṃ;</p>\r\n<p class="gatha3">Dullabhaṃ taṃ vamaññanto,</p>\r\n<p class="gathalast">Eso bālatamo bhave.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',172,replace(replace('<p class="gatha1">Yo <a name="M2.0172"></a> patittha agyāvāṭaṃ,</p>\r\n<p class="gatha2">Mohā taṃ upakārituṃ;</p>\r\n<p class="gatha3">Aññorohi tadā vāṭaṃ,</p>\r\n<p class="gathalast">Dutīyo muḷhamuḷhako.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',176,replace(replace('<p class="gatha1">Byaggho <a name="M2.0176"></a> āvudhaviddho hi,</p>\r\n<p class="gatha2">Akā duṭṭhāni ninnadaṃ;</p>\r\n<p class="gatha3">Tatheva sādhusatthena,</p>\r\n<p class="gathalast">Viddho bālo pakuppito.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-44-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',180,replace(replace('<p class="gatha1">Pivanti <a name="M2.0180"></a> lohitaṃ ḍaṃsā,</p>\r\n<p class="gatha2">Anto tuṇḍena makkhikā;</p>\r\n<p class="gatha3">Bahiddhā parivārenti,</p>\r\n<p class="gathalast">Jano tena ḍaṃsāyaye.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1007n',184,replace(replace('<p class="gatha1">Adhanassa <a name="M2.0184"></a> khaṇo appo,</p>\r\n<p class="gatha2">Saddhammo appakālino;</p>\r\n<p class="gatha3">Appako tena yuñjeyyuṃ,</p>\r\n<p class="gathalast">Khaṇaṃ bahuṃ labhetave.</p>','\r',char(13)),'\n',char(10)),'');
