INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="chapter">Dhammanīti</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> Vanditvā ratanaṃ </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',1,replace(replace('<p class="gatha1">seṭṭhaṃ<a name="M0.0001"></a>, nissāya pubbake garuṃ;</p>\r\n<p class="gathalast">Nitidhammaṃpavakkhāmi, sabbalokasukhāvahaṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Ācariyocasippañca, paññāsutaṃkathādhanaṃ;</p>\r\n<p class="gathalast">Desocanissayomittaṃ, dujjanosujanobalaṃ.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',2,replace(replace('<p class="gatha1">Itthīputtocadāsoca<a name="M0.0002"></a>, gharāvāsokatākato;</p>\r\n<p class="gatha2">Ñātabbocaalaṅkāro, rājadhammopasevako;</p>\r\n<p class="gathalast">Dukādimissakoceva, pakiṇṇakotimātikā.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',3,replace(replace('<p class="gatha1">Kintehipādasussasā<a name="M0.0003"></a>, yesaṃnatthigarūniha;</p>\r\n<p class="gathalast">Yetappādarajokiṇṇā, tevasādhūvivekino.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Vināgarūpadesantaṃ, bālolaṅkattumicchati;</p>\r\n<p class="gathalast">Sampāpuṇenaviññūhi, hasabhāvaṃkathaṃnuso.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Uṭṭhānāupaṭṭhānāca, sussusāparicārikā;</p>\r\n<p class="gathalast">Sakkaccaṃsippuggahaṇaṃ, garuṃārādhayebudho.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-6-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',4,replace(replace('<p class="gatha1">Upajjhācariyānañca<a name="M0.0004"></a>, mātāpitūnamevaca;</p>\r\n<p class="gathalast">Sakkaccaṃyonupaṭṭhāti, sutopitassatādiso.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Upajjhācariyānañca, mātāpitūnamevaca;</p>\r\n<p class="gathalast">Sakkaccaṃyoupaṭṭhāti, sutopitassatādiso;</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Sammāupaparikkhitvā;</p>\r\n<p class="gatha2">Akkharesupadesuca;</p>\r\n<p class="gatha3">Coraghātosissosiyā,</p>\r\n<p class="gathalast">Garucoraṭṭakārako.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-8-9-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',5,replace(replace('<p class="gatha1">Paṇḍitosutasampanno<a name="M0.0005"></a>, yatthaatthīti cesuto;</p>\r\n<p class="gathalast">Mahussāhenataṃṭhānaṃ, gantabbaṃvasutesinā.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Sukhaṃrukkhassachāyāva, tatoñātimātāpitu;</p>\r\n<p class="gathalast">Tatoācariyorañño, tatojinassasāsanaṃ.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> Pāsāṇachattaṃgarukaṃ, tatodevānacikkhanaṃ;</p>\r\n<p class="gathalast">Tatovuḍḍhānamovādo, tatojinassasāsanaṃ.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-12-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',6,replace(replace('<p class="gatha1">Tulaṃsallahukaṃloke<a name="M0.0006"></a>, tatocapalajātiko;</p>\r\n<p class="gathalast">Tatovuḍḍhānanovādo, yatidhammepamādako.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> Sutisammutisaṅkhyāca, yogānitivisesakā;</p>\r\n<p class="gathalast">Gandhabbāgaṇikāceva, dhanubedācapūraṇā.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',7,replace(replace('<p class="gatha1">Tikicchāitihāsāca<a name="M0.0007"></a>, jotimāyācachandati;</p>\r\n<p class="gathalast">Ketumantācasaddāca, sippāṭṭhārasakāime.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> Alasassakutosippaṃ, asippassakutodhanaṃ;</p>\r\n<p class="gatha2">Adhanassakutomittaṃ, amittassakutosukhaṃ;</p>\r\n<p class="gathalast">Asukhassakutopuññaṃ, apuññassakutonibbānaṃ.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> Sippaṃsamaṃdhanaṃnatthi, sippaṃcorānagaṇhanti;</p>\r\n<p class="gathalast">Idhalokesippaṃmittaṃ, paralokesukhāvahaṃ.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-16-17-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',8,replace(replace('<p class="gatha1">Bodhaputrasadāvityaṃ<a name="M0.0008"></a>, mākhedācariyaṃgaruṃ;</p>\r\n<p class="gathalast">Sadesepūjitorājā, budhosabbatthapūjito.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> Bodhaputrakimālasse, abodhobhāravāhako;</p>\r\n<p class="gathalast">Bodhakopūjito loke, bodhaputradinedine.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> Rūpayobbannasampannā, visālakulasambhavā;</p>\r\n<p class="gathalast">Vityāhīnānasobhanti, nigandhāivakiṃsukā.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-20-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',9,replace(replace('<p class="gatha1">Mātāsatrurupitāca<a name="M0.0009"></a>, bālakālenasikkhita;</p>\r\n<p class="gathalast">Na sobhatisabhāmajjhe, haṃsamajjhebakoyathā.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> Guṇoseṭṭhaṅgataṃyāti, nauccesayanevase;</p>\r\n<p class="gathalast">Pāsādasīkharevāso, kākokiṃgaruḷosiyā.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> Sabbasutamadhiyate, hīnamukkaṭṭhamajjhime;</p>\r\n<p class="gathalast">Sabbassaatthaṃjāneyya, nacasabbaṃpayojaye.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-23-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',10,replace(replace('<p class="gatha1">Nalokesobhatemuḷho<a name="M0.0010"></a>,</p>\r\n<p class="gatha2">Kevalattapasaṃsako;</p>\r\n<p class="gatha3">Apisampihitokūpe,</p>\r\n<p class="gathalast">Katavijjopakāsito.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> Madantadamanaṃsatthaṃ, khalānaṃkurutemadaṃ;</p>\r\n<p class="gathalast">Cakkhusaṅkhārakaṃtejaṃ, ulūkānamivandhakaṃ.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Bhojanaṃmethunaṃniddā,</p>\r\n<p class="gatha2">Gaveposecavijjati;</p>\r\n<p class="gatha3">Vijjāvisesoposassa,</p>\r\n<p class="gathalast">Taṃhīnogosamobhave.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-26-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',11,replace(replace('<p class="gatha1">Yosissosippalobhena<a name="M0.0011"></a>, bahuṃbahuṃvagaṇhāti;</p>\r\n<p class="gathalast">Mugovasupinaṃpassaṃ, nasakkākathituṃ paraṃ.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> Sussusāsutavaḍḍhanā, paññāyavaḍḍhanaṃsutaṃ;</p>\r\n<p class="gathalast">Paññāyaatthaṃjānanti, ñātoatthosukhāvaho.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-28-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',12,replace(replace('<p class="gatha1">Anāgataṃbhayaṃdisvā<a name="M0.0012"></a>, dūratoparivajjaye;</p>\r\n<p class="gathalast">Āgatañcabhayaṃdisvā, abhitohotipaṇḍito.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> Lobhaṃkodhaṃmadaṃmānaṃ, tandiṃissaṃpamattakaṃ;</p>\r\n<p class="gathalast">Soṇḍaṃniddālukaṃmakkhaṃ, maccherañcajahebudho.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> Saddhāhiricaottappaṃ, bāhusaccaṃviraṃsati;</p>\r\n<p class="gathalast">Paññācasattadhammehi, sampannopaṇḍitomato.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-31-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',13,replace(replace('<p class="gatha1">Diṭṭhedhammecayoattho<a name="M0.0013"></a>,</p>\r\n<p class="gatha2">Yocatthosamparāyiko;</p>\r\n<p class="gatha3">Atthābhisamayādhīro,</p>\r\n<p class="gathalast">Paṇḍitotipavuccati.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> Sabhāvasadisaṃvākyaṃ, sabhāvasadisaṃpiyaṃ;</p>\r\n<p class="gathalast">Sabhāvasadisaṃkodhaṃ, yojānātisapaṇḍito.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> Bhūpālopaṇḍitoniccaṃ, nevatulyokudācanaṃ;</p>\r\n<p class="gathalast">Sadesepūjitorājā, budhosabbatthapūjito.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-34-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',14,replace(replace('<p class="gatha1">Paṇḍitassapasaṃsāya<a name="M0.0014"></a>, daṇḍobālenadiyyate;</p>\r\n<p class="gathalast">Paṇḍitopaṇḍiteneva, vaṇṇitovasuvaṇṇito.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> Attanāyadiekena, vinithenamahājanā;</p>\r\n<p class="gathalast">Vinayaṃyantisabbepi, kodhaṃnāseyyapaṇḍito.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> Sarīrassaguṇānañca, dūramaccantamantaraṃ;</p>\r\n<p class="gathalast">Sarīraṃkhaṇaviddhaṃsi, guṇātukappaṭhāyino.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> Atthaṃmahantamāpajjaṃ, vijjaṃsampattimevaca;</p>\r\n<p class="gathalast">Vicareyyāmānathaddho, paṇḍitosopavuccati.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-36-37-38-39-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',15,replace(replace('<p class="gatha1">Nālabbhamatipatthenti<a name="M0.0015"></a>, naṭṭhampinacasocare;</p>\r\n<p class="gathalast">Vippatyañcanamuyhanti, yenarātevapaṇḍitā.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> Gaṇṭhiṭhāneekapade, nātimaññeyyapaṇḍito;</p>\r\n<p class="gathalast">Kimakkoveḷupabbhāro, timahādīpabhānudo.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> Guṇadosesunekena, atthikocivivajjito;</p>\r\n<p class="gathalast">Sukhumālapadumassa, naḷaṃbhavatikakkhaḷaṃ.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> Sumahantānisatthāni, dhārayantābahussutā;</p>\r\n<p class="gathalast">Chedāyosaṃsayānantu, klissantilobhamohitā.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-40-41-42-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',16,replace(replace('<p class="gatha1">Dosaṃpisaguṇedisvā<a name="M0.0016"></a>, guṇavādīvadantina;</p>\r\n<p class="gathalast">Nalokovijjamānampicandepassatilañjanaṃ.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> Sakiṃpiviññūdhīrena, karotisahasaṅgamaṃ;</p>\r\n<p class="gathalast">Attatthañcaparatthañca, nibbānantaṃsukhaṃlabhe.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> Nadītīreṭṭhitekūpe, araṇitālavaṇḍake;</p>\r\n<p class="gathalast">Navadāpādinatthīti, namukhevacanaṃtathā.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-44-45-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',17,replace(replace('<p class="gatha1">Paṇḍitoapuṭṭhobherī<a name="M0.0017"></a>, pajjunnohotipucchito;</p>\r\n<p class="gathalast">Bālopuṭṭhoapuṭṭhopi, bahuṃvikatthatesadā.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> Guṇasampannalaṅkāro, sabbasattahitāvaho;</p>\r\n<p class="gathalast">Parattatthaṃnacareyya, kutosopaṇḍitobhave.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> Saparatthaṃcaredhīro, asakkontosakaṃcare;</p>\r\n<p class="gathalast">Tampicevaasakkonto, pāpāttānaṃviyojaye.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-47-48-49-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',18,replace(replace('<p class="gatha1">Sabbaṃsuṇātisotena<a name="M0.0018"></a>, sabbaṃpassaticakkhunā;</p>\r\n<p class="gathalast">Nacadiṭṭhaṃsutaṃdhīro, sabbicchitumarahati.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> Cakkhumāssayathāandho, sotavābadhiroyathā;</p>\r\n<p class="gatha2">Paññāvāpiyathāmugo, balavādubbaloriva;</p>\r\n<p class="gathalast">Athaatthesamuppanne, sayeyyamatasāyitaṃ.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> Atijātaṃmanujātaṃ, puttamicchantipaṇḍitā;</p>\r\n<p class="gathalast">Avajātaṃnaicchanti, yohotikulachinnako.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-50-51-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',19,replace(replace('<p class="gatha1">Tayovapaṇḍitāsatthe<a name="M0.0019"></a>, ahamevātivādica;</p>\r\n<p class="gathalast">Ahamapitivādīca, nāhanticaimetayo;</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> Nasāsabhāyatthanasantisanto,</p>\r\n<p class="gatha2">Natesantoyenavadantidhammaṃ;</p>\r\n<p class="gatha3">Rāgañcadosañcapahāyamohaṃ,</p>\r\n<p class="gathalast">Dhammaṃbhaṇantāvabhavantisanto.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> Bālecummattakebhūpe, gurumātāpitūsvapi;</p>\r\n<p class="gathalast">Saṅghejeṭhecabhātari, nadosākariyābudhā.</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-53-54-55-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',20,replace(replace('<p class="gatha1">Attanāmanotāpañca<a name="M0.0020"></a>, ghareduccaritānica;</p>\r\n<p class="gathalast">Vañcanañcaavamānaṃ, matimānapakāsaye.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> Paradāraṃjanettiṃva, leḍḍuṃvaparasantakaṃ;</p>\r\n<p class="gathalast">Attāvasabbasattānaṃ, yopassatisapaṇḍito.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> Saṭhenamittaṃkalusenadhammaṃ,</p>\r\n<p class="gatha2">Paropatāpenasamiddhibhāvaṃ;</p>\r\n<p class="gatha3">Sukhenavijjaṃpharusenanāriṃ,</p>\r\n<p class="gathalast">Icchantiyetenacevapaṇḍitā.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-56-57-58-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',21,replace(replace('<p class="gatha1">Nipuṇesuhameseyya<a name="M0.0021"></a>, vicinitvāsutatthiko;</p>\r\n<p class="gathalast">Sattaṃhukkhaliyaṃpakkaṃ, bhājanepitathābhave.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> Vasuṃgaṇhantidūraṭṭhā, pabbateratanocite;</p>\r\n<p class="gathalast">Namilakkhāsamīpaṭṭhā, evaṃbālābahussute.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-59-60-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',22,replace(replace('<p class="gatha1">Hiraññenamigānaṃva<a name="M0.0022"></a>, susīlenaasīlino;</p>\r\n<p class="gathalast">Adhammikassadhamena, bālānampisutenakiṃ.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> Appassutāyaṃpuriso, balibaddhovajīvati;</p>\r\n<p class="gathalast">Maṃsānitassavaḍḍhanti, paññātassanavaḍḍhati.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> Appassutosutaṃappaṃ, bahumaññatimāniko;</p>\r\n<p class="gathalast">Sindhūdakaṃapassanto, kūpesoyaṃvamaṇḍuko.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-61-62-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',23,replace(replace('<p class="gatha1">Tadamināpijānātha<a name="M0.0023"></a>, sobbhesupatīresuca;</p>\r\n<p class="gathalast">Saṇantāyantikusobbhā, tuṇhīyantimahodadhī.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> Subhāsitaṃuttamamāhusanto,</p>\r\n<p class="gatha2">Dhammaṃbhaṇenādhammaṃ, taṃdutiyaṃ;</p>\r\n<p class="gatha3">Piyaṃbhaṇenāpiyaṃtaṃtatiyaṃ,</p>\r\n<p class="gathalast">Saccaṃbhaṇenālikaṃtaṃcatutthaṃ.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-64-65-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',24,replace(replace('<p class="gatha1">Sīhamedāsuvaṇṇeva<a name="M0.0024"></a>, nacatiṭṭhantiracate;</p>\r\n<p class="gathalast">Paṇḍitānaṃkathāvākyaṃ nacatiṭṭhatidujjane.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> Mahātejopitejoyaṃ, mattikaṃnamuduṃkare;</p>\r\n<p class="gathalast">Āpopāpetimudukaṃ, sādhuvācācakakkhaḷaṃ;</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> Mudunāvaripuṃjeti, mudunājetidāruṇaṃ;</p>\r\n<p class="gathalast">Nāsiddhimudunākiñci, yatotomudunājaye.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-66-67-68-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',25,replace(replace('<p class="gatha1">Candanaṃ <a name="M0.0025"></a> sītalaṃ loke, tato candaṃva sītalaṃ;</p>\r\n<p class="gathalast">Candana canda sītamhā, sādhuvākyaṃ subhāsitaṃ.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> Sītavāco bahumitto, pharusotu amittako;</p>\r\n<p class="gathalast">Upamaṃ ettha ñātabbā, candasūriyarājunaṃ.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> Pattakalloditaṃappaṃ, vākyaṃsubhāsitaṃbhave;</p>\r\n<p class="gathalast">Khudditassakadannampi, bhuttaṃsādurasobhave.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-69-70-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',26,replace(replace('<p class="gatha1">Satthakāpibahuvācā<a name="M0.0026"></a>, nādarābahubhāṇino;</p>\r\n<p class="gathalast">Sopakārampyudāsīnaṃ, nanudiṭṭhaṃnadījalaṃ.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> Nātivelaṃpabhāseyya, natuṇhisabbadāsiyā;</p>\r\n<p class="gathalast">Avikiṇṇaṃmitaṃvākyaṃ, pattakāleudīraye.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> Icchitabbesukammesu, vācāyakusalaṃmūlaṃ;</p>\r\n<p class="gathalast">Vācāyakusalenaṭṭhe, icchitabbaṃnasijjhati.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> Hatthapādāsiropiṭṭhi, kucchipañcaimejanā;</p>\r\n<p class="gathalast">Mukhamevūpasevanti, sadāvaanusāsitā.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-72-73-74-75-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',27,replace(replace('<p class="gatha1">Saddhādhanaṃsīladhanaṃ<a name="M0.0027"></a>, hiriottappiyaṃdhanaṃ;</p>\r\n<p class="gathalast">Sutadhanañcacāgoca, paññāvesattamaṃdhanaṃ.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> Itthīnañcadhanaṃrūpaṃ, purisānaṃdhanaṃkulaṃ;</p>\r\n<p class="gatha2">Uragānaṃdhanaṃvisaṃ, bhūpālānaṃdhanaṃbalaṃ;</p>\r\n<p class="gathalast">Bhikkhūnañcadhanaṃsīlaṃ, brahmaṇānaṃdhanaṃvijjā.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-76-77-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',28,replace(replace('<p class="gatha1">Narūpaṃnacapaññāṇaṃ<a name="M0.0028"></a>, nacakulañcasambhavo;</p>\r\n<p class="gathalast">Kālavippattisampatte, dhanamevavisesakaṃ.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> Dhanahīnaṃcajemittā, puttadārāsahodarā;</p>\r\n<p class="gathalast">Dhanavantaṃvasevanti, dhanaṃlokemahāsakhā.</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> Sattāsadūpasevanti, sodakaṃvāpiādikaṃ;</p>\r\n<p class="gathalast">Sabhogaṃsadhanañceva, tucchātecejahantite.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-78-79-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',29,replace(replace('<p class="gatha1">Attanāvakatālakkhī<a name="M0.0029"></a>, alakkhīattanākatā;</p>\r\n<p class="gathalast">Nahilakkhiṃalakkhiñca, aññoaññassakārito.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> Dhanavājotiyorājā, nadīvejjotathāime;</p>\r\n<p class="gathalast">Pañcayatthanavijjanti, natatthadivasaṃvase.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-81-82-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',30,replace(replace('<p class="gatha1">Yatthadesenasamāno<a name="M0.0030"></a>, napītinacabandhavo;</p>\r\n<p class="gathalast">Nacavijjāgamokoci, natatthadivasaṃvase.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> Amānanāyatthasiyā, santānaṃavamānanaṃ;</p>\r\n<p class="gathalast">Hīnasamānanāvāpi, natatthadivasaṃvase.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> Desamosajjagacchanti, sīhāsappurisāgajā;</p>\r\n<p class="gathalast">Tattevanidhanaṃyanti, kākākāpurisāmigā.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-83-84-85-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',31,replace(replace('<p class="gatha1">Yatthālasocadakkhoca<a name="M0.0031"></a>, sūrobhirusamapūjā;</p>\r\n<p class="gathalast">Nasantotatthavasanti, avisesakarenako.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> Calatyekenapādena, tiṭṭhatyekenapaṇḍito;</p>\r\n<p class="gathalast">Nāsamikkhyaparaṃṭhānaṃ, pubbamāyatanaṃcaje.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> Ṭhānabhaṭṭhānasobhante, dantākesānakhānarā;</p>\r\n<p class="gathalast">Itiviññāyamatimā, saṭhānaṃnalahuṃcaje.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> Guṇosabbaññutulyopi, sīdatyekoanissayo;</p>\r\n<p class="gathalast">Anagghaṃratanaṃmaṇi, hemaṃnissāyasobhate.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> Nasevepharusaṃsāmiṃ, taṃpisevenamacchariṃ;</p>\r\n<p class="gathalast">Tatoniggaṇhikaṃsāmiṃ, nevāpaggaṇhikaṃtato.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-86-87-88-89-90-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',33,replace(replace('<p class="gatha1">Parokkheguṇahantāraṃ<a name="M0.0033"></a>, paccakkhepiyabhāṇinaṃ;</p>\r\n<p class="gathalast">Tādisaṃnopaseveyya, visakumbhaṃpalobhitaṃ.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> Piṭṭhitokkaṃniseveyya, niseveaggikucchinā;</p>\r\n<p class="gathalast">Sāmīnaṃsabbakāyena, paralokaṃamuḷhako.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> Nasevekatapāpamhi, na sevālikavādane;</p>\r\n<p class="gathalast">Naseveattadatthamhi, naseveatisantake.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-91-92-93-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',34,replace(replace('<p class="gatha1">Mahantaṃnissayaṃkatvā<a name="M0.0034"></a>, khuddakopimahābhave;</p>\r\n<p class="gathalast">Hemapabbataṃnissāya, hemapakkhībhavantite.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> Asahāyosamattopi, tejasīkiṃkarissati;</p>\r\n<p class="gathalast">Nivātasaṇṭhitoaggi, sayamevūpasammati.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> Rukkhāsubhūminissāya, pupphaphalaṃpavaḍḍhati;</p>\r\n<p class="gathalast">Sappūrisūpanissāya, mahāpuññaṃpavaḍḍhati.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-94-95-96-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',35,replace(replace('<p class="gatha1">Analasoacaṇḍikko<a name="M0.0035"></a>, asaṭṭhosucisaccavā;</p>\r\n<p class="gathalast">Aluddhoatthakāmoca, tamuttaṃuttamonaro.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> Ahitepaṭisedhoca, hitesucaniyojako;</p>\r\n<p class="gathalast">Byasanecāpariccāgo, saṅkhepaṃmittalakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-97-98-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',36,replace(replace('<p class="gatha1">Āturebyasanesace<a name="M0.0036"></a>, dubbhikkheparaviggahe;</p>\r\n<p class="gathalast">Rājadvāresusāneca, saṅkhepaṃmittalakkhiṇaṃ.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> Hitesanosumittoca, viññūcadullabhojano;</p>\r\n<p class="gathalast">Yathosadhañcasāduñca, rogahārīcasajjano.</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> Yodhuvāniparicajja, adhuvānopasevati;</p>\r\n<p class="gathalast">Dhuvānitassanassanti, adhuvesukathāvakā.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-99-100-101-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',37,replace(replace('<p class="gatha1">Luddhamatthenagaṇheyya<a name="M0.0037"></a>, thaddhamañjathikammunā;</p>\r\n<p class="gathalast">Chandānuvattiyāmuḷhaṃ, yathābhūtenapaṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> Saccābhikkhaṇasaṃsaggā, asamosaraṇenaca;</p>\r\n<p class="gathalast">Etenamittājiranti, akāleyācanenaca.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> Tasmānābhikkhaṇaṃgacche, nacagacchecirāciraṃ;</p>\r\n<p class="gathalast">Kālenayācaṃyāceyya, evaṃmittānajīyare.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-102-103-104-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',38,replace(replace('<p class="gatha1">Yenamittenasaṃsaggā<a name="M0.0038"></a>, yogakkhemovihīyati;</p>\r\n<p class="gathalast">Pubbevajjhabhavaṃtassa, rakkheyyaggiṃvapaṇḍito.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> Yenamittenasaṃsaggā, yogakkhemopavaḍḍhati;</p>\r\n<p class="gathalast">Kareyyattasamaṃvuttiṃ, sabbakiccesupaṇḍito.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> Pabbepabbekamenucchu, visesarasavāggato;</p>\r\n<p class="gathalast">Tathāsumettikosādhu, viparittovadujjano.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-105-106-107-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',39,replace(replace('<p class="gatha1">Tenevamunināvuttaṃ<a name="M0.0039"></a>, dhammāyekecilokiyā;</p>\r\n<p class="gathalast">Tatālokuttarāceva, dhammānibbānagāmino.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> Kalyāṇamittamāgamma, sabbetehontipāṇinaṃ;</p>\r\n<p class="gathalast">Tasmākalyāṇamittesu, kātabbaṃhisadādaraṃ.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> Yohavekataññūkatavedidhīro,</p>\r\n<p class="gatha2">Kalyāṇamittedaḷhabhatticahoti;</p>\r\n<p class="gatha3">Dukkhitassasakkaccaṃkarotikiccaṃ,</p>\r\n<p class="gathalast">Tabbhāvaṃsappurisaṃvadantiloke.</p>\r\n<p class="hangnum"><a name="para110"></a><span class="paranum">110</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-108-109-110-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',40,replace(replace('<p class="gatha1">Hitakāroparobandhu<a name="M0.0040"></a>, bandhūpiahitoparo;</p>\r\n<p class="gathalast">Ahitodehajobyādhi, hitamāraññamosadhaṃ.</p>\r\n<p class="hangnum"><a name="para111"></a><span class="paranum">111</span>.</p>\r\n<p class="gatha1"> Padumaṃvamukhayassa, vācācandanasītalaṃ;</p>\r\n<p class="gatha2">Madhutiṭṭhatijivhagge, hadayesuhalāhalaṃ;</p>\r\n<p class="gathalast">Tādisaṃnopaseveyya, taṃmittaṃparivajjaye.</p>\r\n<p class="hangnum"><a name="para112"></a><span class="paranum">112</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-111-112-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',41,replace(replace('<p class="gatha1">Katvānakusalaṃkammaṃ<a name="M0.0041"></a>, katvāna, kusalaṃpure;</p>\r\n<p class="gathalast">Sukhitaṃdukkhitaṃhontaṃ, sobāloyonapassati.</p>\r\n<p class="hangnum"><a name="para113"></a><span class="paranum">113</span>.</p>\r\n<p class="gatha1"> Kālakkhepenahāpeti, dānasīlādikaṃjaḷo;</p>\r\n<p class="gathalast">Athiraṃpithiraṃmaññe, attānaṃsassatīsamaṃ.</p>\r\n<p class="hangnum"><a name="para114"></a><span class="paranum">114</span>.</p>\r\n<p class="gatha1"> Bālodhapāpakaṃkatvā, nataṃchaṭṭitumussahe;</p>\r\n<p class="gathalast">Kiṃbyagghaādigacchanto, padaṃmakkhetumussahe.</p>\r\n<p class="hangnum"><a name="para115"></a><span class="paranum">115</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-113-114-115-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',42,replace(replace('<p class="gatha1">Niddhanopicakāmeti<a name="M0.0042"></a>, dubbaloverikaṃkaro;</p>\r\n<p class="gathalast">Mandasatthovivādatthī, tividhaṃmuḷhalakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para116"></a><span class="paranum">116</span>.</p>\r\n<p class="gatha1"> Anavhāyaṃgamayanto, apucchābahubhāsako;</p>\r\n<p class="gathalast">Attagguṇaṃpasaṃsanti, tividhaṃhīnalakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para117"></a><span class="paranum">117</span>.</p>\r\n<p class="gatha1"> Yathācuṭṭamparāpakkā, bahirattakamevaca;</p>\r\n<p class="gathalast">Antokimilapūraṇo, evaṃdujjanadhammatā.</p>\r\n<p class="hangnum"><a name="para118"></a><span class="paranum">118</span>.</p>\r\n<p class="gatha1"> Yadūnakaṃsaṇatitaṃ, yaṃpūraṃsantamevataṃ;</p>\r\n<p class="gathalast">Aḍḍhakumbhupamobālo, yaṃpūrakumbhovapaṇḍito.</p>\r\n<p class="hangnum"><a name="para119"></a><span class="paranum">119</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-116-117-118-119-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',43,replace(replace('<p class="gatha1">Budhehisāsamānopi<a name="M0.0043"></a>, khalobahutaketavo;</p>\r\n<p class="gathalast">Ghaṃsiyamānopaṅgāro, nilamattaṃnigacchati.</p>\r\n<p class="hangnum"><a name="para120"></a><span class="paranum">120</span>.</p>\r\n<p class="gatha1"> Muḷhasissopadesena, kunārībharaṇenaca;</p>\r\n<p class="gathalast">Khalasattūhisaṃyogā, paṇḍitopyavasīdati.</p>\r\n<p class="hangnum"><a name="para121"></a><span class="paranum">121</span>.</p>\r\n<p class="gatha1"> Cārutāparadārāya, dhanaṃlokatapattiyā;</p>\r\n<p class="gathalast">Pasutaṃsādhunāsāya, khalekhalatarāguṇā.</p>\r\n<p class="hangnum"><a name="para122"></a><span class="paranum">122</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-120-121-122-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',44,replace(replace('<p class="gatha1">Itohāsataraṃloke<a name="M0.0044"></a>, kiñcitassanavijjati;</p>\r\n<p class="gathalast">Dujjanoticayaṃāha, sujanaṃdujjanosayaṃ.</p>\r\n<p class="hangnum"><a name="para123"></a><span class="paranum">123</span>.</p>\r\n<p class="gatha1"> Navināparavādena, ramantidujjanākhalu;</p>\r\n<p class="gathalast">Nasāsabbarasebhutvā, vināsuddhenatussati.</p>\r\n<p class="hangnum"><a name="para124"></a><span class="paranum">124</span>.</p>\r\n<p class="gatha1"> Tappateyātisambandhaṃ, dravaṃbhavatyavanataṃ;</p>\r\n<p class="gathalast">Mududujjanacittaṃna kiṃlohenapamīyate.</p>\r\n<p class="hangnum"><a name="para125"></a><span class="paranum">125</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-123-124-125-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',45,replace(replace('<p class="gatha1">Tasmādujjanasaṃsaggaṃ<a name="M0.0045"></a>, āsivīsamivoragaṃ;</p>\r\n<p class="gathalast">Ārakāparivajjeyya, bhūtakāmovicakkhaṇo.</p>\r\n<p class="hangnum"><a name="para126"></a><span class="paranum">126</span>.</p>\r\n<p class="gatha1"> Dujjanenahisaṃsaggaṃ, sattutāpinayujjati;</p>\r\n<p class="gathalast">Tattovaḍayhatyaṅgāro, santokāḷāyataṃkaro;</p>\r\n<p class="hangnum"><a name="para127"></a><span class="paranum">127</span>.</p>\r\n<p class="gatha1"> Dujjanovajjanīyova, vijjāyālaṅkatopice;</p>\r\n<p class="gathalast">Maṇinālaṅkatosanto, sabbokiṃnubhayaṃkaro.</p>\r\n<p class="hangnum"><a name="para128"></a><span class="paranum">128</span>.</p>\r\n<p class="gatha1"> Agginodahatodāyaṃ, sakhābhavatimāluto;</p>\r\n<p class="gathalast">Soevadīpaṃnāseti, khalenatthevamittatā.</p>\r\n<p class="hangnum"><a name="para129"></a><span class="paranum">129</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-126-127-128-129-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',46,replace(replace('<p class="gatha1">Sabboduṭṭhokhaloduṭṭho<a name="M0.0046"></a>, sabbāduṭṭhatarokhalo;</p>\r\n<p class="gathalast">Mantosadhehisosabbo, khalokenupasammati.</p>\r\n<p class="hangnum"><a name="para130"></a><span class="paranum">130</span>.</p>\r\n<p class="gatha1"> Hadayaṭṭhenasutena, khalonevasusīlavā;</p>\r\n<p class="gathalast">Madhunākoṭaraṭṭhena, nimbokiṃmadhurobhave.</p>\r\n<p class="hangnum"><a name="para131"></a><span class="paranum">131</span>.</p>\r\n<p class="gatha1"> Asataṃsampayogena, santopiasantobhave;</p>\r\n<p class="gathalast">Maggokacavarayutto, ujumpiasādhubhave.</p>\r\n<p class="hangnum"><a name="para132"></a><span class="paranum">132</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-130-131-132-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',47,replace(replace('<p class="gatha1">Putimacchaṃkusaggena<a name="M0.0047"></a>, yonaroupanayhati;</p>\r\n<p class="gathalast">Kusāpiputiṃvāyanti, evaṃbālupasevanā.</p>\r\n<p class="hangnum"><a name="para133"></a><span class="paranum">133</span>.</p>\r\n<p class="gatha1"> Bālaṃnapassenasuṇe, nacabāsenasaṃvase;</p>\r\n<p class="gathalast">Bālenālāpasallāpaṃ, nakarenacarocaye.</p>\r\n<p class="hangnum"><a name="para134"></a><span class="paranum">134</span>.</p>\r\n<p class="gatha1"> Anayaṃnetidummedho, adhurāyaniyuñjati;</p>\r\n<p class="gatha2">Dunnayoseyyasohoti, sammāvuttopikuppati;</p>\r\n<p class="gathalast">Vinayaṃsonajānāti, sādhusenaadassanaṃ.</p>\r\n<p class="hangnum"><a name="para135"></a><span class="paranum">135</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-133-134-135-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',48,replace(replace('<p class="gatha1">Yāvajīvaṃpicebālo<a name="M0.0048"></a>, paṇḍitaṃpayirūpāsi;</p>\r\n<p class="gathalast">Nasodhammaṃvijānāti, dabbisūparasaṃyathā.</p>\r\n<p class="hangnum"><a name="para136"></a><span class="paranum">136</span>.</p>\r\n<p class="gatha1"> Phalaṃvekadaliṃhanti, hantiveḷunaḷaṃphalaṃ;</p>\r\n<p class="gathalast">Sakkārokāpurisaṃhanti, gabbhoassatariṃyathā.</p>\r\n<p class="hangnum"><a name="para137"></a><span class="paranum">137</span>.</p>\r\n<p class="gatha1"> Sunakhosunakhaṃdisvā, dantaṃdassetihiṃsituṃ;</p>\r\n<p class="gathalast">Dujjanodujjanaṃdisvā, rosayaṃhiṃsamicchati.</p>\r\n<p class="hangnum"><a name="para138"></a><span class="paranum">138</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-136-137-138-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',49,replace(replace('<p class="gatha1">Maṇḍūkopinukkosīho<a name="M0.0049"></a>, sukaropinukkodīpi;</p>\r\n<p class="gathalast">Bilārosadisobyaggho, duppaññopinapaññavā.</p>\r\n<p class="hangnum"><a name="para139"></a><span class="paranum">139</span>.</p>\r\n<p class="gatha1"> Maṇḍūkopisīhoviya,</p>\r\n<p class="gatha2">Kākogaṇhepiññepiññe;</p>\r\n<p class="gatha3">Bālocapaṇḍitoviya,</p>\r\n<p class="gathalast">Dhīropucchevayevaye.</p>\r\n<p class="hangnum"><a name="para140"></a><span class="paranum">140</span>.</p>\r\n<p class="gatha1"> Kākoduṭṭhosakuṇesu, ghareduṭṭhocamūsiko;</p>\r\n<p class="gathalast">Vānarocavaneduṭṭho, manussesucabrahmaṇo.</p>\r\n<p class="hangnum"><a name="para141"></a><span class="paranum">141</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-139-140-141-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',50,replace(replace('<p class="gatha1">Tiṇānibhūmicodakaṃ<a name="M0.0050"></a>, catutthaṃvākyasuṭṭhutaṃ;</p>\r\n<p class="gathalast">Etānihisataṃgehe, nochijjantekadācipi.</p>\r\n<p class="hangnum"><a name="para142"></a><span class="paranum">142</span>.</p>\r\n<p class="gatha1"> Ambuṃpivantinonajjo, rukkhākhādantinophalaṃ;</p>\r\n<p class="gathalast">Meghokadācinosassaṃ, paratthāyasataṃdhanaṃ.</p>\r\n<p class="hangnum"><a name="para143"></a><span class="paranum">143</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-142-143-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',51,replace(replace('<p class="gatha1">Guṇākubbantidūtattaṃ<a name="M0.0051"></a>, dūrepivasataṃsataṃ;</p>\r\n<p class="gathalast">Keṭakegandhamāghāya, gacchantibhamarāsayaṃ.</p>\r\n<p class="hangnum"><a name="para144"></a><span class="paranum">144</span>.</p>\r\n<p class="gatha1"> Ākiṇṇopiasabbhīdha, asaṃsaṭṭhovabhaddako;</p>\r\n<p class="gathalast">Bahunāsannajātena, gacche naummattakena.</p>\r\n<p class="hangnum"><a name="para145"></a><span class="paranum">145</span>.</p>\r\n<p class="gatha1"> Pāpamittevivajjetvā, bhajeyyuttamapuggalaṃ;</p>\r\n<p class="gathalast">Ovādecassatiṭṭheyya, patthantoacalaṃsukhaṃ.</p>\r\n<p class="hangnum"><a name="para146"></a><span class="paranum">146</span>.</p>\r\n<p class="gatha1"> Yathācapanasāpakkā, bahisaṇḍakamevaca;</p>\r\n<p class="gathalast">Antoamatapūraṇo, evaṃ sujanadhammatā.</p>\r\n<p class="hangnum"><a name="para147"></a><span class="paranum">147</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-144-145-146-147-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',52,replace(replace('<p class="gatha1">Taggarañjapalāsena<a name="M0.0052"></a>, yonaroupanayhati;</p>\r\n<p class="gathalast">Pattāpigandhaṃvāyanti, evaṃdhīrupasevanā.</p>\r\n<p class="hangnum"><a name="para148"></a><span class="paranum">148</span>.</p>\r\n<p class="gatha1"> Dhīraṃpassesuṇedhīraṃ, dhīrenasahasaṃvase;</p>\r\n<p class="gathalast">Dhīrenālāpasallāpaṃ, taṃkaretañcarocaye.</p>\r\n<p class="hangnum"><a name="para149"></a><span class="paranum">149</span>.</p>\r\n<p class="gatha1"> Nayaṃnayati medhāvī, adhurāyanayujjati;</p>\r\n<p class="gatha2">Sunayoseyyasohoti, sammāvuttonakuppati;</p>\r\n<p class="gathalast">Vinayaṃsopajānāti, sādhutenasamāgamo.</p>\r\n<p class="hangnum"><a name="para150"></a><span class="paranum">150</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-148-149-150-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',53,replace(replace('<p class="gatha1">Appakenapiceviññū<a name="M0.0053"></a>, paṇḍitaṃpayirūpāsi;</p>\r\n<p class="gathalast">Khippaṃdhammaṃvijānāti, jivhāsūparasaṃyathā.</p>\r\n<p class="hangnum"><a name="para151"></a><span class="paranum">151</span>.</p>\r\n<p class="gatha1"> Bāhubalañcaamaccaṃ, bhogaṃabhijaccaṃbalaṃ;</p>\r\n<p class="gathalast">Imehicatubalehi, paññāveseṭhataṃbalaṃ.</p>\r\n<p class="hangnum"><a name="para152"></a><span class="paranum">152</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-151-152-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',54,replace(replace('<p class="gatha1">Balaṃpakkhīnamākāso<a name="M0.0054"></a>, macchānaṃudakaṃbalaṃ;</p>\r\n<p class="gathalast">Dubbalassabalaṃrājā, kumārānaṃrudaṃbalaṃ.</p>\r\n<p class="hangnum"><a name="para153"></a><span class="paranum">153</span>.</p>\r\n<p class="gatha1"> Balaṃcandobalaṃsūro, balaṃsamaṇabrahmaṇā;</p>\r\n<p class="gathalast">Balaṃvelaṃsamuddassa, balaṃtibalamitthiyā.</p>\r\n<p class="hangnum"><a name="para154"></a><span class="paranum">154</span>.</p>\r\n<p class="gatha1"> Sapādānaṃbalaṃsīho, tatopuḷuvakobalaṃ;</p>\r\n<p class="gathalast">Tatokippilikonaro, rājāsabbesamantato.</p>\r\n<p class="hangnum"><a name="para155"></a><span class="paranum">155</span>.</p>\r\n<p class="gatha1"> Gatimigānaṃpavanaṃ, ākāsopakkhinaṃgati;</p>\r\n<p class="gathalast">Virāgogatidhammānaṃ, nibbānārahataṃgati.</p>\r\n<p class="hangnum"><a name="para156"></a><span class="paranum">156</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-153-154-155-156-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',55,replace(replace('<p class="gatha1">Careyyakulajaṃpañño<a name="M0.0055"></a>, virūpamavikaññakaṃ;</p>\r\n<p class="gathalast">Hīnāyapisurūpāya, vivāhasadisaṃkare.</p>\r\n<p class="hangnum"><a name="para157"></a><span class="paranum">157</span>.</p>\r\n<p class="gatha1"> Sāmāmigakkhīsukesī, tanumajjhimadantavā;</p>\r\n<p class="gatha2">Dassanīyāmukhavaṇṇā, gambhīranābhivācakā;</p>\r\n<p class="gathalast">Susīlāvāyamatica, hīnakulaṃpivāhaye.</p>\r\n<p class="hangnum"><a name="para158"></a><span class="paranum">158</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-157-158-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',56,replace(replace('<p class="gatha1">Suttomātāvabhojesi<a name="M0.0056"></a>, bharaṇesucadhātiyo;</p>\r\n<p class="gathalast">Kammesusakapanti ca, katakammesudhātinaṃ.</p>\r\n<p class="hangnum"><a name="para159"></a><span class="paranum">159</span>.</p>\r\n<p class="gatha1"> Dhammesupatiṭṭhāniccaṃ, sayanesucavaṇṇibhā;</p>\r\n<p class="gathalast">Kulesubhātaraṃvācī, yānārīseṭṭhasammatā.</p>\r\n<p class="hangnum"><a name="para160"></a><span class="paranum">160</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-159-160-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',57,replace(replace('<p class="gatha1">Yonaṃbharatisabbadā<a name="M0.0057"></a>, niccaṃātāpiussuko;</p>\r\n<p class="gathalast">Sabbakāmaharaṃposaṃ, bhattāraṃnātimaññati.</p>\r\n<p class="hangnum"><a name="para161"></a><span class="paranum">161</span>.</p>\r\n<p class="gatha1"> Nacāpisvatthibhattāraṃ, icchācārenarosaye;</p>\r\n<p class="gathalast">Bhattucagarunosabbe, patipūjetipaṇḍitā.</p>\r\n<p class="hangnum"><a name="para162"></a><span class="paranum">162</span>.</p>\r\n<p class="gatha1"> Uṭṭhāhikāanalassā, saṅgāhikāparijane;</p>\r\n<p class="gathalast">Bhattumanāpaṃcarati, sambhatamanurakkhati.</p>\r\n<p class="hangnum"><a name="para163"></a><span class="paranum">163</span>.</p>\r\n<p class="gatha1"> Evaṃvattatiyānārī, bhattuchandavasānugā;</p>\r\n<p class="gathalast">Manāpānāmatedevā, yatthasāupapajjati.</p>\r\n<p class="hangnum"><a name="para164"></a><span class="paranum">164</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-161-162-163-164-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',58,replace(replace('<p class="gatha1">Itthiyekacciyovāpi<a name="M0.0058"></a>, seyyāvuttāvamuninā;</p>\r\n<p class="gathalast">Bhaṇḍānaṃuttamāitthī, aggupaṭṭhāyikātipi.</p>\r\n<p class="hangnum"><a name="para165"></a><span class="paranum">165</span>.</p>\r\n<p class="gatha1"> Mātarādhītarāvāpi, bhaginiyāvicakkhaṇo;</p>\r\n<p class="gathalast">Navivittāsanemante, nārīmāyāvinīnanu.</p>\r\n<p class="hangnum"><a name="para166"></a><span class="paranum">166</span>.</p>\r\n<p class="gatha1"> Vijjutānañcalolattaṃ, satthānañcātitikkhaṇaṃ;</p>\r\n<p class="gathalast">Siṅghataṃvāyutejānaṃ, anukubbantināriyo.</p>\r\n<p class="hangnum"><a name="para167"></a><span class="paranum">167</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-165-166-167-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',59,replace(replace('<p class="gatha1">Diguṇothīnamāhāro<a name="M0.0059"></a>,</p>\r\n<p class="gatha2">Buddhicāpicatugguṇo;</p>\r\n<p class="gatha3">Chaguṇohotivāyāmo,</p>\r\n<p class="gathalast">Kāmotvaṭṭhaguṇobhave.</p>\r\n<p class="hangnum"><a name="para168"></a><span class="paranum">168</span>.</p>\r\n<p class="gatha1"> Ekamekāyaitthiyā, aṭṭhaaṭṭhapatinosiyuṃ;</p>\r\n<p class="gatha2">Sūrocapalavantoca, sabbakāmarasāharā;</p>\r\n<p class="gathalast">Kareyyanavamechandaṃ, unnattāhinapūrati.</p>\r\n<p class="hangnum"><a name="para169"></a><span class="paranum">169</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-168-169-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',60,replace(replace('<p class="gatha1">Vivādasīlīussuyā<a name="M0.0060"></a>, passantataṇhikāgatā;</p>\r\n<p class="gathalast">Amitābhuñjanāniddā, sataṃputtaṃpitaṃjahe.</p>\r\n<p class="hangnum"><a name="para170"></a><span class="paranum">170</span>.</p>\r\n<p class="gatha1"> Lapantīsaddhimaññena, passantaññaṃsavibbhamā;</p>\r\n<p class="gathalast">Cittakaṃcintayantaññaṃ, nārīnaṃnāmakopiyo.</p>\r\n<p class="hangnum"><a name="para171"></a><span class="paranum">171</span>.</p>\r\n<p class="gatha1"> Gaṇheyyavātaṃjālena, sāgaramekapāṇinā;</p>\r\n<p class="gatha2">Osiñceyyacatāḷena, sakenajanayeravaṃ;</p>\r\n<p class="gathalast">Pamādāsuvisajjeyya, itthiyesāvadhammatā.</p>\r\n<p class="hangnum"><a name="para172"></a><span class="paranum">172</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-170-171-172-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',61,replace(replace('<p class="gatha1">Jivhāsahassikoyohi<a name="M0.0061"></a>, jīvevassasataṃnaro;</p>\r\n<p class="gathalast">Tenanikammunāvutto, thidosokiṃkhayaṃgato.</p>\r\n<p class="hangnum"><a name="para173"></a><span class="paranum">173</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-173-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',62,replace(replace('<p class="gatha1">Pañcaṭhānānisampassaṃ<a name="M0.0062"></a>, puttamicchantipaṇḍitā;</p>\r\n<p class="gathalast">Bhatovānobharissati, kiccaṃvānokarissati.</p>\r\n<p class="hangnum"><a name="para174"></a><span class="paranum">174</span>.</p>\r\n<p class="gatha1"> Kulavaṃsociraṃtiṭṭhe, dāyajjaṃpatipajjati;</p>\r\n<p class="gathalast">Athavāpanapetānaṃ, dakkhiṇānupadassati.</p>\r\n<p class="hangnum"><a name="para175"></a><span class="paranum">175</span>.</p>\r\n<p class="gatha1"> Atijātamanujātaṃ, puttamicchantipaṇḍitā;</p>\r\n<p class="gathalast">Avajātaṃnaicchanti, yohotikulachinnako.</p>\r\n<p class="hangnum"><a name="para176"></a><span class="paranum">176</span>.</p>\r\n<p class="gatha1"> Ekūdarasamuppannā, nabhavantisamassamā;</p>\r\n<p class="gathalast">Nānāvaṇṇānānācarā, yathābadarakaṇḍakā.</p>\r\n<p class="hangnum"><a name="para177"></a><span class="paranum">177</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-174-175-176-177-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',63,replace(replace('<p class="gatha1">Adammebahavodose<a name="M0.0063"></a>, dammetubahavoguṇe;</p>\r\n<p class="gathalast">Tasmāputtañcasissañca, dammakālevadammaye.</p>\r\n<p class="hangnum"><a name="para178"></a><span class="paranum">178</span>.</p>\r\n<p class="gatha1"> Ovādeyyānusāseyya,</p>\r\n<p class="gatha2">Asappāyānivāraye;</p>\r\n<p class="gatha3">Dantohiyopiyohoti,</p>\r\n<p class="gathalast">Adantohotiappiyo.</p>\r\n<p class="hangnum"><a name="para179"></a><span class="paranum">179</span>.</p>\r\n<p class="gatha1"> Puttaṃvābhātaraṃduṭṭhaṃ, anusāseyyanojahe;</p>\r\n<p class="gathalast">Kiṃnuchejjaṃhatthapādaṃ, littaasucināsiyā.</p>\r\n<p class="hangnum"><a name="para180"></a><span class="paranum">180</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-178-179-180-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',64,replace(replace('<p class="gatha1">Antojātodhanakkito<a name="M0.0064"></a>, dāsabyopagatosayaṃ;</p>\r\n<p class="gathalast">Dāsākaramarānito, iccevaṃcatudhāsiyuṃ.</p>\r\n<p class="hangnum"><a name="para181"></a><span class="paranum">181</span>.</p>\r\n<p class="gatha1"> Dāsāpañcevacorayya, sakhañātattasadisā;</p>\r\n<p class="gathalast">Tathāviññūhiviññeyya, mittādārācabandhavā.</p>\r\n<p class="hangnum"><a name="para182"></a><span class="paranum">182</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-181-182-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',65,replace(replace('<p class="gatha1">Duṭṭhadārenaamityā<a name="M0.0065"></a>, dāsocuttaravācako;</p>\r\n<p class="gathalast">Sasappecagharevāso, maccumevanasaṃsayo.</p>\r\n<p class="hangnum"><a name="para183"></a><span class="paranum">183</span>.</p>\r\n<p class="gatha1"> Yasaṃlābhaṃpatthayantaṃ, naraṃvajjantidūrato;</p>\r\n<p class="gathalast">Tasmāanapekkhitvāna, taṃmaggaṃmaggayebudho.</p>\r\n<p class="hangnum"><a name="para184"></a><span class="paranum">184</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-183-184-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',66,replace(replace('<p class="gatha1">Khalaṃsālaṃpasuṃkhettaṃ<a name="M0.0066"></a>, gantācassaabhikkhaṇaṃ;</p>\r\n<p class="gathalast">Mitaṃdhaññaṃnidhāpeyya, mitañcapācayeghare.</p>\r\n<p class="hangnum"><a name="para185"></a><span class="paranum">185</span>.</p>\r\n<p class="gatha1"> Añjanānaṃkhayaṃdisvā, vammikānañcasañcayaṃ;</p>\r\n<p class="gathalast">Madhūnañcasamāhāraṃ, paṇḍitogharamāvase.</p>\r\n<p class="hangnum"><a name="para186"></a><span class="paranum">186</span>.</p>\r\n<p class="gatha1"> Sayaṃāyaṃvayaṃjaññā, sayaṃjaññākatākataṃ;</p>\r\n<p class="gathalast">Niggaṇheniggaṇhārahaṃ, paggaṇhepaggaṇhārahaṃ.</p>\r\n<p class="hangnum"><a name="para187"></a><span class="paranum">187</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-185-186-187-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',67,replace(replace('<p class="gatha1">Ekayāmonarādhippo<a name="M0.0067"></a>,</p>\r\n<p class="gatha2">Dviyāmopaṇḍitonaro;</p>\r\n<p class="gatha3">Tayoyāmogharāvāsoca,</p>\r\n<p class="gathalast">Catuyāmocaduggato.</p>\r\n<p class="hangnum"><a name="para188"></a><span class="paranum">188</span>.</p>\r\n<p class="gatha1"> Yocabandhuhiteyutto,</p>\r\n<p class="gatha2">Sopitāyocaposako;</p>\r\n<p class="gatha3">Taṃmittaṃyatthavisāso,</p>\r\n<p class="gathalast">Bhariyāyatthanibbuti.</p>\r\n<p class="hangnum"><a name="para189"></a><span class="paranum">189</span>.</p>\r\n<p class="gatha1"> Saddhāpemesusantesu, nagaṇemāsakaṃsataṃ;</p>\r\n<p class="gathalast">Saddhāpemeasantesu, māsakaṃpisataṃgaṇe.</p>\r\n<p class="hangnum"><a name="para190"></a><span class="paranum">190</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-188-189-190-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',68,replace(replace('<p class="gatha1">Yācakoapiyohoti<a name="M0.0068"></a>, yācaṃadadamappiyaṃ;</p>\r\n<p class="gathalast">Tasmāseṭṭhanaroloke, dhanaṃsippaṃpariggahe.</p>\r\n<p class="hangnum"><a name="para191"></a><span class="paranum">191</span>.</p>\r\n<p class="gatha1"> Sabbadāpidhanaṃrakkhe, dāraṃrakkhedhanaṃpica;</p>\r\n<p class="gathalast">Dāraṃdhanañcaattānaṃ, rakkhāyevasadābhave.</p>\r\n<p class="hangnum"><a name="para192"></a><span class="paranum">192</span>.</p>\r\n<p class="gatha1"> Nasādhāraṇadārassa, nabhuñjesādumekako;</p>\r\n<p class="gathalast">Nasevelokāyatikaṃ, netaṃpaññāyavaḍḍhanaṃ.</p>\r\n<p class="hangnum"><a name="para193"></a><span class="paranum">193</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-191-192-193-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',69,replace(replace('<p class="gatha1">Sīlavāvattasampanno<a name="M0.0069"></a>, appamattovicakkhaṇo;</p>\r\n<p class="gathalast">Nivātavuttiathaddho, sūratosakhilomudu.</p>\r\n<p class="hangnum"><a name="para194"></a><span class="paranum">194</span>.</p>\r\n<p class="gatha1"> Saṅgahetāvamittānaṃ, saṃvibhāgīvijānavā;</p>\r\n<p class="gathalast">Tappeyyaannapānena, sadāsamaṇabrahmaṇā.</p>\r\n<p class="hangnum"><a name="para195"></a><span class="paranum">195</span>.</p>\r\n<p class="gatha1"> Dhammakāmosutaddharo, bhaveyyaparipucchāko;</p>\r\n<p class="gathalast">Sakkaccaṃpayirūpāse, sīlavantebahussute.</p>\r\n<p class="hangnum"><a name="para196"></a><span class="paranum">196</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-194-195-196-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',70,replace(replace('<p class="gatha1">Pubbasirosimedhāvī<a name="M0.0070"></a>, dīghāyudakkhiṇaṃsiro;</p>\r\n<p class="gathalast">Pacchimocittasantosi, maraṇaṃuttarobhave.</p>\r\n<p class="hangnum"><a name="para197"></a><span class="paranum">197</span>.</p>\r\n<p class="gatha1"> Āyumāpācinaṃmūkhaṃ, dhanavādakkhiṇaṃbhave;</p>\r\n<p class="gathalast">Pacchimaṃyasasībhuñje, nobhuñjeuttaraṃmukhaṃ;</p>\r\n<p class="hangnum"><a name="para198"></a><span class="paranum">198</span>.</p>\r\n<p class="gatha1"> Bhutvānisīdanaṃthūlaṃ, tiṭṭhantobalavaḍḍhano;</p>\r\n<p class="gathalast">Āyumācaṅkamosiyā, dhāvantorogavajjito.</p>\r\n<p class="hangnum"><a name="para199"></a><span class="paranum">199</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-197-198-199-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',71,replace(replace('<p class="gatha1">Yocasitañcauṇhañca<a name="M0.0071"></a>, tiṇābhiyyonamaññati;</p>\r\n<p class="gathalast">Karaṃpurisakiccāni, sosukhānavihāyati.</p>\r\n<p class="hangnum"><a name="para200"></a><span class="paranum">200</span>.</p>\r\n<p class="gatha1"> Visamhāmatamādeyya, asuddhamhāpikañcanaṃ;</p>\r\n<p class="gathalast">Nīcamhāpyuttamovijjaṃ, ratanathiṃpidukkulā.</p>\r\n<p class="hangnum"><a name="para201"></a><span class="paranum">201</span>.</p>\r\n<p class="gatha1"> Guyhamatthamasambuddhaṃ, sambodhayatiyonaro;</p>\r\n<p class="gathalast">Mantabhedabhayātassa, dāsabhūtotitikkhati.</p>\r\n<p class="hangnum"><a name="para202"></a><span class="paranum">202</span>.</p>\r\n<p class="gatha1"> Aññātavāsavasatā, jātavedasamenapi;</p>\r\n<p class="gathalast">Khamitabbaṃsapaññena, apidāsassatajjitaṃ.</p>\r\n<p class="hangnum"><a name="para203"></a><span class="paranum">203</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-200-201-202-203-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',72,replace(replace('<p class="gatha1">Dhanadhaññapayogesu<a name="M0.0072"></a>, tathāvijjāgamesuca;</p>\r\n<p class="gathalast">Dūtecabyāhāresu, cattolajjaṃsadābhave.</p>\r\n<p class="hangnum"><a name="para204"></a><span class="paranum">204</span>.</p>\r\n<p class="gatha1"> Nahikocikatekicce, kattāraṃsamapekkhate;</p>\r\n<p class="gathalast">Tasmāsabbānikammāni, sāvasesānikāraye.</p>\r\n<p class="hangnum"><a name="para205"></a><span class="paranum">205</span>.</p>\r\n<p class="gatha1"> Upakāraṃhiteneva, sattunāsattumuddhare;</p>\r\n<p class="gathalast">Pādalaggaṃkaraṭṭhena, kaṇḍakenevakaṇḍakaṃ.</p>\r\n<p class="hangnum"><a name="para206"></a><span class="paranum">206</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-204-205-206-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',73,replace(replace('<p class="gatha1">Namenamantassabhajebhajantaṃ<a name="M0.0073"></a>,</p>\r\n<p class="gatha2">Kiccānikubbassakareyyakiccaṃ;</p>\r\n<p class="gatha3">Nānatthakāmassakareyyaatthaṃ,</p>\r\n<p class="gathalast">Asambhajantaṃpinasambhajeyya.</p>\r\n<p class="hangnum"><a name="para207"></a><span class="paranum">207</span>.</p>\r\n<p class="gatha1"> Vajecajantisinehākiriyā,</p>\r\n<p class="gatha2">Apemacittena nasambhajeyya;</p>\r\n<p class="gatha3">Dijovadumaṃkhiṇaphalaṃñatvā,</p>\r\n<p class="gathalast">Aññaṃapekkheyyamahāhiloko.</p>\r\n<p class="hangnum"><a name="para208"></a><span class="paranum">208</span>.</p>\r\n<p class="gatha1"> Cajeekaṃkulassatthaṃ, gāmassatthaṃkulaṃcaje,</p>\r\n<p class="gathalast">Gāmaṃjanapadassatthaṃ, attatthaṃpathaviṃcaje.</p>\r\n<p class="hangnum"><a name="para209"></a><span class="paranum">209</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-207-208-209-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',74,replace(replace('<p class="gatha1">Dhanaṃcajeaṅgaṃvarassahetu<a name="M0.0074"></a>,</p>\r\n<p class="gatha2">Aṅgaṃcajejīvitaṃrakkhamāno;</p>\r\n<p class="gatha3">Dhanaṃaṅgaṃjīvitañcāpisabbaṃ,</p>\r\n<p class="gathalast">Cajenarodhammamanussaranto.</p>\r\n<p class="hangnum"><a name="para210"></a><span class="paranum">210</span>.</p>\r\n<p class="gatha1"> Ahāgacchantihāyantā, sattānamihajīvitaṃ;</p>\r\n<p class="gathalast">Tasmāhimāpamattataṃ, gacchantujinasāsane.</p>\r\n<p class="hangnum"><a name="para211"></a><span class="paranum">211</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-210-211-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',75,replace(replace('<p class="gatha1">Etesiyyosamāyanti<a name="M0.0075"></a>, santitesaṃnajīrati;</p>\r\n<p class="gathalast">Yoadhippannaṃsahati, yocajānātidesanaṃ.</p>\r\n<p class="hangnum"><a name="para212"></a><span class="paranum">212</span>.</p>\r\n<p class="gatha1"> Aggiāpoitthīmuḷho, sapporājakulānica;</p>\r\n<p class="gathalast">Payatanovagantabbo, maccupāṇaharāniti.</p>\r\n<p class="hangnum"><a name="para213"></a><span class="paranum">213</span>.</p>\r\n<p class="gatha1"> Satthaṃsunicchitamapītivicintanīyaṃ,</p>\r\n<p class="gatha2">Sārādhitopyavanipoparisaṅkanīyo;</p>\r\n<p class="gatha3">Hatthaṅgatāpiyuvatīparirakkhanīyā,</p>\r\n<p class="gathalast">Satthāvanīpayuvatīsukutovasītaṃ.</p>\r\n<p class="hangnum"><a name="para214"></a><span class="paranum">214</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-212-213-214-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',76,replace(replace('<p class="gatha1">Ussāhoripuvamittaṃ<a name="M0.0076"></a>, alasomittaṃvaripu,</p>\r\n<p class="gathalast">Visaṃviyāmataṃvijjā, pamādoamataṃvisaṃ.</p>\r\n<p class="hangnum"><a name="para215"></a><span class="paranum">215</span>.</p>\r\n<p class="gatha1"> Vaheamittaṃkhandhena, yāvakāleanāgate;</p>\r\n<p class="gathalast">Tamevaāgatekāle, bhindekumbhaṃvasilāyaṃ.</p>\r\n<p class="hangnum"><a name="para216"></a><span class="paranum">216</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-215-216-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',77,replace(replace('<p class="gatha1">Siṅgiṃpaññāsahatthena<a name="M0.0077"></a>, satenavāhanaṃcaje;</p>\r\n<p class="gathalast">Hatthīnantusahassena, desacajjenadujjanaṃ.</p>\r\n<p class="hangnum"><a name="para217"></a><span class="paranum">217</span>.</p>\r\n<p class="gatha1"> Paccakkhegaravosaṃse, parokkhemittabandhave;</p>\r\n<p class="gathalast">Kammantecadāsabhacce, puttadāresaṃsemate.</p>\r\n<p class="hangnum"><a name="para218"></a><span class="paranum">218</span>.</p>\r\n<p class="gatha1"> Sinesippaṃsinedhanaṃ, sinepabbatamāruhe;</p>\r\n<p class="gathalast">Sinekāmocakodhoca, imepañcasinesine.</p>\r\n<p class="hangnum"><a name="para219"></a><span class="paranum">219</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-217-218-219-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',78,replace(replace('<p class="gatha1">Sataṃcakkhusataṃkaṇṇā<a name="M0.0078"></a>, nāyakassasutosadā;</p>\r\n<p class="gathalast">Tathāpiandhabadhiro, esānāyakadhammatā.</p>\r\n<p class="hangnum"><a name="para220"></a><span class="paranum">220</span>.</p>\r\n<p class="gatha1"> Bahūnaṃappasārānaṃ, sāmaggiyātidujjayo;</p>\r\n<p class="gathalast">Tiṇenavaṭṭaterajju, tenanāgopibandhate.</p>\r\n<p class="hangnum"><a name="para221"></a><span class="paranum">221</span>.</p>\r\n<p class="gatha1"> Uppajjatesacekodho, āvajjekakacūpamaṃ;</p>\r\n<p class="gathalast">Uppajjaterasetaṇhā, puttamaṃsūpamaṃsare.</p>\r\n<p class="hangnum"><a name="para222"></a><span class="paranum">222</span>.</p>\r\n<p class="gatha1"> Dānaṃsinehabhesajjaṃ, maccheraṃdosanosadhaṃ;</p>\r\n<p class="gathalast">Dānaṃyasassibhesajjaṃ, maccheraṃkappanosadhaṃ.</p>\r\n<p class="hangnum"><a name="para223"></a><span class="paranum">223</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-220-221-222-223-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',79,replace(replace('<p class="gatha1">Dhanamicchevaṇijjeyya<a name="M0.0079"></a>, sippamicchebahussute;</p>\r\n<p class="gathalast">Puttamicchenārikaññe, rājāmaccaṃicchāgate.</p>\r\n<p class="hangnum"><a name="para224"></a><span class="paranum">224</span>.</p>\r\n<p class="gatha1"> Mahantaṃvaṭṭarukkhādiṃ, khuddabījaṃbahupphalaṃ;</p>\r\n<p class="gathalast">Sakkhiṃkatvāudikkheyya, puññapāpaṃkaronaro.</p>\r\n<p class="hangnum"><a name="para225"></a><span class="paranum">225</span>.</p>\r\n<p class="gatha1"> Garukātabbaposesu, nīcavuttiṃkarotiyo;</p>\r\n<p class="gathalast">Nīcattaṃsopahantvāna, uttamatthepatiṭṭhati.</p>\r\n<p class="hangnum"><a name="para226"></a><span class="paranum">226</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-224-225-226-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',80,replace(replace('<p class="gatha1">Uttamaṃpaṇipātena<a name="M0.0080"></a>, sūraṃbhedenavijaye;</p>\r\n<p class="gathalast">Hīnamappapadānena, vikkamenasamaṃjaye.</p>\r\n<p class="hangnum"><a name="para227"></a><span class="paranum">227</span>.</p>\r\n<p class="gatha1"> Nattadosaṃparejaññā, jaññādosaṃparassatu;</p>\r\n<p class="gathalast">Kummoguyhāivaṅgāni, parabhāvañcalakkhaye.</p>\r\n<p class="hangnum"><a name="para228"></a><span class="paranum">228</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-227-228-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',81,replace(replace('<p class="gatha1">Ussūraseyyaṃālasyaṃ<a name="M0.0081"></a>, caṇḍikkaṃdīghasuttiyaṃ;</p>\r\n<p class="gatha2">Ekassaddhānagamanaṃ, paradārupasevanaṃ;</p>\r\n<p class="gathalast">Etaṃbrahmaṇassevassu, anatthāyabhavissati.</p>\r\n<p class="hangnum"><a name="para229"></a><span class="paranum">229</span>.</p>\r\n<p class="gatha1"> Surāyogovikāloca, samajjānagharaṅgato;</p>\r\n<p class="gatha2">Khiḍḍadhuttopāpamitto, alasosoimejanā;</p>\r\n<p class="gathalast">Mahābhogāvinassanti, hīnabhāvassidaṃphalaṃ.</p>\r\n<p class="hangnum"><a name="para230"></a><span class="paranum">230</span>.</p>\r\n<p class="gatha1"> Atisītaṃatiuṇhaṃ, atisāyamidaṃahu;</p>\r\n<p class="gathalast">Itivissaṭṭhakammante, khaṇāaccantimāṇave.</p>\r\n<p class="hangnum"><a name="para231"></a><span class="paranum">231</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-229-230-231-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',82,replace(replace('<p class="gatha1">Paranāsanatonaṭṭho<a name="M0.0082"></a>, purevaparanāsako;</p>\r\n<p class="gathalast">Siṅghaṃvaānasaṃyāti, tiṇopāsādajhāpako.</p>\r\n<p class="hangnum"><a name="para232"></a><span class="paranum">232</span>.</p>\r\n<p class="gatha1"> Navisāseamittassa, mittañcāpinavisāse;</p>\r\n<p class="gathalast">Kadācikuppitomitto, sabbadosapakāsako.</p>\r\n<p class="hangnum"><a name="para233"></a><span class="paranum">233</span>.</p>\r\n<p class="gatha1"> Kudesañcakumittañca, kusambandhaṃkubandhavaṃ;</p>\r\n<p class="gathalast">Kudārañcakurājānaṃ, dūratoparivajjaye.</p>\r\n<p class="hangnum"><a name="para234"></a><span class="paranum">234</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-232-233-234-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',83,replace(replace('<p class="gatha1">Kakkaṭoasīsoyāti<a name="M0.0083"></a>, sabbāpadovagacchati;</p>\r\n<p class="gathalast">Athanīkukkuṭīputtā, purisenāvamaññare.</p>\r\n<p class="hangnum"><a name="para235"></a><span class="paranum">235</span>.</p>\r\n<p class="gatha1"> Hīnaputtorājāmacco, bālaputtopipaṇḍito;</p>\r\n<p class="gathalast">Adhanassaputtoseṭṭhi, purisenāvamaññare.</p>\r\n<p class="hangnum"><a name="para236"></a><span class="paranum">236</span>.</p>\r\n<p class="gatha1"> Yenamicchatisambandhaṃ, tenatīṇinakāraye;</p>\r\n<p class="gathalast">Vivādamatthasambandhaṃ, parokkhedāradassanaṃ.</p>\r\n<p class="hangnum"><a name="para237"></a><span class="paranum">237</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-235-236-237-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',84,replace(replace('<p class="gatha1">Iṇasesoaggiseso<a name="M0.0084"></a>,</p>\r\n<p class="gatha2">Satrusesotathevaca;</p>\r\n<p class="gatha3">Punappunampivaḍḍhanti,</p>\r\n<p class="gathalast">Tasmāsesaṃnakāraye.</p>\r\n<p class="hangnum"><a name="para238"></a><span class="paranum">238</span>.</p>\r\n<p class="gatha1"> Kulajātokulaputto, kulavaṃsasurakkhito;</p>\r\n<p class="gathalast">Attanādukkhapattopi, hīnakammaṃnakāraye.</p>\r\n<p class="hangnum"><a name="para239"></a><span class="paranum">239</span>.</p>\r\n<p class="gatha1"> Samiddhodhanadhaññena, nakaṭṭhodatiṇaggihi;</p>\r\n<p class="gathalast">Sabbatoduggatonaṭṭho, tasmānadukkaṭaṃkare.</p>\r\n<p class="hangnum"><a name="para240"></a><span class="paranum">240</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-238-239-240-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',85,replace(replace('<p class="gatha1">Nagaṇassaggathogacche<a name="M0.0085"></a>, siddhekammesamaṃphalaṃ;</p>\r\n<p class="gathalast">Kammavippatticehoti, pharusaṃtassabhāsaye.</p>\r\n<p class="hangnum"><a name="para241"></a><span class="paranum">241</span>.</p>\r\n<p class="gatha1"> Bālakkopetadhūmoca, vuḍḍhitthipallalodakaṃ;</p>\r\n<p class="gathalast">Āyukkhayakaraṃniccaṃ, rattocadadhibhojanaṃ.</p>\r\n<p class="hangnum"><a name="para242"></a><span class="paranum">242</span>.</p>\r\n<p class="gatha1"> Itthīnaṃdujjanānañca, visāsonopapajjate;</p>\r\n<p class="gathalast">Visesigimhinadiyaṃ, rogerājakulamhica.</p>\r\n<p class="hangnum"><a name="para243"></a><span class="paranum">243</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-241-242-243-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',86,replace(replace('<p class="gatha1">Ayuttakammārabhanaṃvirodho<a name="M0.0086"></a>,</p>\r\n<p class="gatha2">Saṅghassayuddhañcamahābalehi;</p>\r\n<p class="gatha3">Visāsakammaṃpamadāsuniccaṃ,</p>\r\n<p class="gathalast">Dvārānimaccussavadantividvā.</p>\r\n<p class="hangnum"><a name="para244"></a><span class="paranum">244</span>.</p>\r\n<p class="gatha1"> Thiyoseveyyanaccantaṃ, sādubhuñjeyyanāhitaṃ;</p>\r\n<p class="gathalast">Pūjayemānayevuḍḍhe, garuṃmāyāyanobhaje.</p>\r\n<p class="hangnum"><a name="para245"></a><span class="paranum">245</span>.</p>\r\n<p class="gatha1"> Vināsatthaṃnagaccheyya, sūrosaṅgāmabhūmiyaṃ;</p>\r\n<p class="gathalast">Paṇḍitvaddhagūvāṇijjo, videsagamanotathā.</p>\r\n<p class="hangnum"><a name="para246"></a><span class="paranum">246</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-244-245-246-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',87,replace(replace('<p class="gatha1">Dehītivacanadvārā<a name="M0.0087"></a>, dehaṭṭhāpañcadevatā;</p>\r\n<p class="gathalast">Sajjaniyyantidhikitti, matihīrisirīpica.</p>\r\n<p class="hangnum"><a name="para247"></a><span class="paranum">247</span>.</p>\r\n<p class="gatha1"> Natthītivacanaṃdukkhaṃ, dehītivacanaṃtathā;</p>\r\n<p class="gathalast">Vākyanatthītidehīti, mābhaveyyabhavābhave.</p>\r\n<p class="hangnum"><a name="para248"></a><span class="paranum">248</span>.</p>\r\n<p class="gatha1"> Yatthavosaṃnajānanti, jātiyāvinayenavā;</p>\r\n<p class="gathalast">Natatthamānaṃkiriyā, janevasamaññātake.</p>\r\n<p class="hangnum"><a name="para249"></a><span class="paranum">249</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-247-248-249-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',88,replace(replace('<p class="gatha1">Mātāhīnassadubbhāsā<a name="M0.0088"></a>, pitāhīnassadukriyā;</p>\r\n<p class="gathalast">Ubhomātāpitāhīnā, dubbhāsācadukkiriyā.</p>\r\n<p class="hangnum"><a name="para250"></a><span class="paranum">250</span>.</p>\r\n<p class="gatha1"> Mātāseṭṭhassasubhāsā,</p>\r\n<p class="gatha2">Pitāseṭṭhassasukiriyā;</p>\r\n<p class="gatha3">Ubhomātāpitāseṭṭhā,</p>\r\n<p class="gathalast">Subhāsācasukiriyā.</p>\r\n<p class="hangnum"><a name="para251"></a><span class="paranum">251</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-250-251-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',89,replace(replace('<p class="gatha1">Atidīghomahāmuḷho<a name="M0.0089"></a>, majjhimocavicakkhaṇo;</p>\r\n<p class="gathalast">Vāsudevaṃpurekkhitvā, sabbevāmanakāsaṭhā.</p>\r\n<p class="hangnum"><a name="para252"></a><span class="paranum">252</span>.</p>\r\n<p class="gatha1"> Ācārokulamakkhāti, desamakkhatibhāsitaṃ;</p>\r\n<p class="gathalast">Sambhavopemamakkhāti, dehamakkhātibhojanaṃ.</p>\r\n<p class="hangnum"><a name="para253"></a><span class="paranum">253</span>.</p>\r\n<p class="gatha1"> Jalappamāṇaṃkumudanāḷaṃ,</p>\r\n<p class="gatha2">Kulappamāṇaṃkaraṇakammaṃ;</p>\r\n<p class="gatha3">Paññāpamāṇaṃkathitavākyaṃ,</p>\r\n<p class="gathalast">Bhūmippamāṇaṃbhajjalatiṇaṃ.</p>\r\n<p class="hangnum"><a name="para254"></a><span class="paranum">254</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-252-253-254-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',90,replace(replace('<p class="gatha1">Javenabhadraṃjānanti<a name="M0.0090"></a>, vahenacabalibaddhaṃ;</p>\r\n<p class="gathalast">Duhenadhenujānanti, bhāsamānenapaṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para255"></a><span class="paranum">255</span>.</p>\r\n<p class="gatha1"> Jāneyyapesanebhaccaṃ bandhavaṃpibhayāgame,</p>\r\n<p class="gathalast">Byasanecatathāmittaṃ, dārañcavibhavakkhaye.</p>\r\n<p class="hangnum"><a name="para256"></a><span class="paranum">256</span>.</p>\r\n<p class="gatha1"> Vināsatthaṃnajānanti, kālaṃsabbepijotikā;</p>\r\n<p class="gathalast">Kukkuṭāpanajānanti, tatorukkhātatobhvāpā.</p>\r\n<p class="hangnum"><a name="para257"></a><span class="paranum">257</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-255-256-257-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',91,replace(replace('<p class="gatha1">Pathavībhūsanaṃmeru<a name="M0.0091"></a>, rattiyābhūsanaṃsasī;</p>\r\n<p class="gathalast">Janānaṃbhūsanaṃrājā, senānaṃbhūsanaṃgajo.</p>\r\n<p class="hangnum"><a name="para258"></a><span class="paranum">258</span>.</p>\r\n<p class="gatha1"> Sīlatāsobhaterūpaṃ,</p>\r\n<p class="gatha2">Cāritāsobhatekulaṃ;</p>\r\n<p class="gatha3">Sapupphāsobhateraññaṃ,</p>\r\n<p class="gathalast">Sagajāsobhatebalaṃ.</p>\r\n<p class="hangnum"><a name="para259"></a><span class="paranum">259</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-258-259-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',92,replace(replace('<p class="gatha1">Kokilānaṃsaddaṃrūpaṃ<a name="M0.0092"></a>, nārīrūpaṃpatibbataṃ;</p>\r\n<p class="gathalast">Vijjārūpaṃarūpānaṃ, khamārūpaṃtapassinaṃ.</p>\r\n<p class="hangnum"><a name="para260"></a><span class="paranum">260</span>.</p>\r\n<p class="gatha1"> Kisāsobhātapassīca,</p>\r\n<p class="gatha2">Thūlāsobhācatuppadā;</p>\r\n<p class="gatha3">Vijjāsobhāmanussāca,</p>\r\n<p class="gathalast">Itthīsobhācasāmikā.</p>\r\n<p class="hangnum"><a name="para261"></a><span class="paranum">261</span>.</p>\r\n<p class="gatha1"> Rattihīnonacandaro, ūmihīnonasāgaro;</p>\r\n<p class="gathalast">Haṃsahīnonasaṃphullo, itthihīnonapuriso.</p>\r\n<p class="hangnum"><a name="para262"></a><span class="paranum">262</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-260-261-262-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',93,replace(replace('<p class="gatha1">Vatthahīnaṃnalaṅkāraṃ<a name="M0.0093"></a>, patihīnānanārikā;</p>\r\n<p class="gathalast">Sippahīnonapuriso, dhenuhīnaṃnabhojanaṃ.</p>\r\n<p class="hangnum"><a name="para263"></a><span class="paranum">263</span>.</p>\r\n<p class="gatha1"> Dīpakedīpakocando, nārikedīpakopati;</p>\r\n<p class="gathalast">Tilokedīpakodhammo, suputtokuladīpako.</p>\r\n<p class="hangnum"><a name="para264"></a><span class="paranum">264</span>.</p>\r\n<p class="gatha1"> Aputtakaṃgharaṃsuññaṃ, desaṃsuññaṃarājikaṃ;</p>\r\n<p class="gathalast">Apaññassamukhaṃsuññaṃ, sabbasuññaṃdaliddakaṃ.</p>\r\n<p class="hangnum"><a name="para265"></a><span class="paranum">265</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-263-264-265-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',94,replace(replace('<p class="gatha1">Sotaṃsutenevanakuṇḍalena<a name="M0.0094"></a>,</p>\r\n<p class="gatha2">Dānenapāṇīnatukaṅkaṇena;</p>\r\n<p class="gatha3">Ābhātikāyopurisuttamassa,</p>\r\n<p class="gathalast">Paropakārenanacandanena.</p>\r\n<p class="hangnum"><a name="para266"></a><span class="paranum">266</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-266-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',95,replace(replace('<p class="gatha1">Dānaṃsīlaṃpariccāgaṃ<a name="M0.0095"></a>, ajjavaṃmaddavaṃtapaṃ;</p>\r\n<p class="gatha2">Akodhaṃavihiṃsañca, khantīcaavirodhanaṃ;</p>\r\n<p class="gathalast">Dasetedhammerājāno, appamattenadhāreyyuṃ.</p>\r\n<p class="hangnum"><a name="para267"></a><span class="paranum">267</span>.</p>\r\n<p class="gatha1"> Dānaṃatthacariyāpiya, vācāattasamaṃpica;</p>\r\n<p class="gathalast">Saṅgahācaturoime, munindenapakāsitā.</p>\r\n<p class="hangnum"><a name="para268"></a><span class="paranum">268</span>.</p>\r\n<p class="gatha1"> Vanemigānalabhanti, matābhayāniddasukhaṃ;</p>\r\n<p class="gatha2">Rājānopinalabhanti, uttarathāmabhītato;</p>\r\n<p class="gathalast">Saṃsārabhayabhitena, naramantiyepaṇḍitā.</p>\r\n<p class="hangnum"><a name="para269"></a><span class="paranum">269</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-267-268-269-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',96,replace(replace('<p class="gatha1">Khamājāgariyuṭṭhānaṃ<a name="M0.0096"></a>, saṃvibhāgodayikkhanā;</p>\r\n<p class="gathalast">Nāyakassaguṇāete, icchitabbāhitatthino.</p>\r\n<p class="hangnum"><a name="para270"></a><span class="paranum">270</span>.</p>\r\n<p class="gatha1"> Paribhūtomuduhoti, atitikkhañcaveravā;</p>\r\n<p class="gathalast">Etañcaubhayaṃñatvā, anumajjhaṃsamācare.</p>\r\n<p class="hangnum"><a name="para271"></a><span class="paranum">271</span>.</p>\r\n<p class="gatha1"> Nekantamudunāsakkā, ekantatikhiṇenavā;</p>\r\n<p class="gathalast">Mahatteṭṭhapituattaṃ, tasmāubhayamācare.</p>\r\n<p class="hangnum"><a name="para272"></a><span class="paranum">272</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-270-271-272-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',97,replace(replace('<p class="gatha1">Kassakovāṇijomacco<a name="M0.0097"></a>, samaṇosutasīlavā;</p>\r\n<p class="gathalast">Tesuvipulajātesu, raṭṭhaṃpivipulaṃsiyā.</p>\r\n<p class="hangnum"><a name="para273"></a><span class="paranum">273</span>.</p>\r\n<p class="gatha1"> Tesudubbalajātesu, raṭṭhaṃpidubbalaṃsiyā;</p>\r\n<p class="gathalast">Saraṭṭhaṃvipulaṃtasmā, dhāreyyaraṭṭhabhāravā.</p>\r\n<p class="hangnum"><a name="para274"></a><span class="paranum">274</span>.</p>\r\n<p class="gatha1"> Mahārukkhassaphalino, āmaṃchindatiyophalaṃ;</p>\r\n<p class="gathalast">Rasañcassanajānāti, bījañcassavinassati.</p>\r\n<p class="hangnum"><a name="para275"></a><span class="paranum">275</span>.</p>\r\n<p class="gatha1"> Mahārukkhūpamaṃraṭṭhaṃ, adhammenapasāsati;</p>\r\n<p class="gathalast">Rasañcassanajānāti, raṭṭhañcāpivinassati.</p>\r\n<p class="hangnum"><a name="para276"></a><span class="paranum">276</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-273-274-275-276-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',98,replace(replace('<p class="gatha1">Mahārukkhassaphalino<a name="M0.0098"></a>, pakkaṃchindatiyophalaṃ;</p>\r\n<p class="gathalast">Rasañcassavijānāti, bījañcassananassati.</p>\r\n<p class="hangnum"><a name="para277"></a><span class="paranum">277</span>.</p>\r\n<p class="gatha1"> Mahārukkhūpamaṃraṭṭhaṃ, dhammena yopasāsati;</p>\r\n<p class="gathalast">Rasañcassavijānāti, raṭṭhañcāpinanassati.</p>\r\n<p class="hangnum"><a name="para278"></a><span class="paranum">278</span>.</p>\r\n<p class="gatha1"> Yocarājājanapadaṃ, adhammenapasāsati;</p>\r\n<p class="gathalast">Sabbosadhīhisorājā, viruddhohotikhattiyo.</p>\r\n<p class="hangnum"><a name="para279"></a><span class="paranum">279</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-277-278-279-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',99,replace(replace('<p class="gatha1">Tathevanegamehiṃsaṃ<a name="M0.0099"></a>, yeyuttākayavikkaye;</p>\r\n<p class="gathalast">Ojādānabalikāre, sakosenavirujjhati.</p>\r\n<p class="hangnum"><a name="para280"></a><span class="paranum">280</span>.</p>\r\n<p class="gatha1"> Pahāravarakhettaññū, saṅgāmekatanissame;</p>\r\n<p class="gathalast">Ussitehiṃsayaṃrājā, sabalenavirujjhati.</p>\r\n<p class="hangnum"><a name="para281"></a><span class="paranum">281</span>.</p>\r\n<p class="gatha1"> Tathevaisayohiṃsaṃ, saṃyamebrahmacāriyo;</p>\r\n<p class="gathalast">Adhammacārikhattiyo, sasaggenavirujjhati.</p>\r\n<p class="hangnum"><a name="para282"></a><span class="paranum">282</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-280-281-282-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',100,replace(replace('<p class="gatha1">Sayaṃkatānaparena<a name="M0.0100"></a>, mahānajjojuvaṅkatā;</p>\r\n<p class="gathalast">Issarenatathāraññā, saraṭṭheadhipaccattā.</p>\r\n<p class="hangnum"><a name="para283"></a><span class="paranum">283</span>.</p>\r\n<p class="gatha1"> Puttopāpaṃkatomātā,</p>\r\n<p class="gatha2">Sissopāpaṃkatogaru;</p>\r\n<p class="gatha3">Nāgarehikatorājā,</p>\r\n<p class="gathalast">Rājāpāpaṃpurohito.</p>\r\n<p class="hangnum"><a name="para284"></a><span class="paranum">284</span>.</p>\r\n<p class="gatha1"> Puññāpuññaṃkarontesu, chabhāgoekadesakaṃ;</p>\r\n<p class="gatha2">Rājālabhatisabbehi, tasmāpāpānivāraye;</p>\r\n<p class="gathalast">Puññamevapavaḍḍhento, janakāyaṃpasāsaye.</p>\r\n<p class="hangnum"><a name="para285"></a><span class="paranum">285</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-283-284-285-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',101,replace(replace('<p class="gatha1">Bālassajīvitaṃappaṃ<a name="M0.0101"></a>, paṇḍitassabahutaraṃ;</p>\r\n<p class="gathalast">Janakāyassarājāva, rājadhammovarājunaṃ.</p>\r\n<p class="hangnum"><a name="para286"></a><span class="paranum">286</span>.</p>\r\n<p class="gatha1"> Anāyakāvinassanti, nassantibahunāyakā;</p>\r\n<p class="gathalast">Thināyakāvinassanti, nassantisusunāyakā.</p>\r\n<p class="hangnum"><a name="para287"></a><span class="paranum">287</span>.</p>\r\n<p class="gatha1"> Kacchapīnañcamacchīnaṃ, kukkuṭīnañcadhenunaṃ;</p>\r\n<p class="gathalast">Puttaposoyathāhoti, tathāmaccesurājunaṃ.</p>\r\n<p class="hangnum"><a name="para288"></a><span class="paranum">288</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-286-287-288-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',102,replace(replace('<p class="gatha1">Nahirājakulaṃpatto<a name="M0.0102"></a>, aññātolabhateyasaṃ;</p>\r\n<p class="gathalast">Nāsūronātidummedho, napamattokudācanaṃ.</p>\r\n<p class="hangnum"><a name="para289"></a><span class="paranum">289</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-289-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',103,replace(replace('<p class="gatha1">Yadāsīlañcapaññañca<a name="M0.0103"></a>, soceyyañcādhigacchati;</p>\r\n<p class="gathalast">Athavisāsitotamhi, guyhañcassanarakkhati.</p>\r\n<p class="hangnum"><a name="para290"></a><span class="paranum">290</span>.</p>\r\n<p class="gatha1"> Divāvāyadivārattiṃ, rājakiccesupaṇḍito;</p>\r\n<p class="gathalast">Ajjhiṭṭhonavikappeyya, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para291"></a><span class="paranum">291</span>.</p>\r\n<p class="gatha1"> Naraññāsamakaṃvatthaṃ, namālaṃnavilepanaṃ;</p>\r\n<p class="gathalast">Ākappaṃsarakuttiṃvā, naraññāsadisamācare.</p>\r\n<p class="hangnum"><a name="para292"></a><span class="paranum">292</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-290-291-292-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',104,replace(replace('<p class="gatha1">Kiḷerājāamaccehi<a name="M0.0104"></a>, bhariyāparivārito;</p>\r\n<p class="gathalast">Nāmaccorājabhariyā, bhāvaṃkubbethapaṇḍito.</p>\r\n<p class="hangnum"><a name="para293"></a><span class="paranum">293</span>.</p>\r\n<p class="gatha1"> Anuddhatoacapalo, nipakosaṃvutindriyo;</p>\r\n<p class="gathalast">Manopaṇidhisampanno, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para294"></a><span class="paranum">294</span>.</p>\r\n<p class="gatha1"> Nāssabhariyākiḷeyya, namanteyyarahogato;</p>\r\n<p class="gathalast">Nāssakosedhanaṃgaṇhe, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para295"></a><span class="paranum">295</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-293-294-295-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',105,replace(replace('<p class="gatha1">Na <a name="M0.0105"></a> niddaṃbahuṃmaññeyya, namadāyasuraṃpive;</p>\r\n<p class="gathalast">Nāsadāyemigehaññe, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para296"></a><span class="paranum">296</span>.</p>\r\n<p class="gatha1"> Nāssapiṭṭhaṃnapallaṅkaṃ, nakocchaṃnanāvaṃrathaṃ;</p>\r\n<p class="gathalast">Sammatomhītiāruḷhe, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para297"></a><span class="paranum">297</span>.</p>\r\n<p class="gatha1"> Nātidūrebhajerañño, naccāsannevicakkhaṇo;</p>\r\n<p class="gathalast">Samukhācassatiṭṭheyya, santasantosabhattuno.</p>\r\n<p class="hangnum"><a name="para298"></a><span class="paranum">298</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-296-297-298-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',106,replace(replace('<p class="gatha1">Namerājāsakhāhoti <a name="M0.0106"></a>6, narājāhotimethuno;</p>\r\n<p class="gathalast">Khippaṃkujjhantirājāno, sulenakkhivaghaṭṭitaṃ.</p>\r\n<p class="hangnum"><a name="para299"></a><span class="paranum">299</span>.</p>\r\n<p class="gatha1"> Napūjitomaññamāno, medhāvīpaṇḍitonaro;</p>\r\n<p class="gathalast">Pharusaṃpatimanteyya, rājānaṃparisaṃgataṃ.</p>\r\n<p class="hangnum"><a name="para300"></a><span class="paranum">300</span>.</p>\r\n<p class="gatha1"> Laddhadvārolabhedvāraṃ, nevarājūsuvīsaye;</p>\r\n<p class="gathalast">Aggiṃvasaṃyatotiṭṭhe, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para301"></a><span class="paranum">301</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-299-300-301-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',107,replace(replace('<p class="gatha1">Puttaṃvābhātaraṃvāpi<a name="M0.0107"></a>, sampaggaṇhātikhattiyo;</p>\r\n<p class="gatha2">Gāmehinigamehivā, raṭṭhehijanapadehi;</p>\r\n<p class="gathalast">Tuṇhibhūtovudikkheyya, nabhaṇechekapāpakaṃ.</p>\r\n<p class="hangnum"><a name="para302"></a><span class="paranum">302</span>.</p>\r\n<p class="gatha1"> Hatthāroheanikaṭṭhe, rathikepattikārake;</p>\r\n<p class="gatha2">Tesaṃkammāvadhānena, rājāvaḍḍhetivettanaṃ;</p>\r\n<p class="gathalast">Natesaṃantarāgacche, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para303"></a><span class="paranum">303</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-302-303-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',108,replace(replace('<p class="gatha1">Cāpovūnūdarocassa<a name="M0.0108"></a>, vaṃsovāpipakampayye;</p>\r\n<p class="gathalast">Paṭilomaṃnavatteyya, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para304"></a><span class="paranum">304</span>.</p>\r\n<p class="gatha1"> Cāpovūnūdarocassa, macchovassaajivhako;</p>\r\n<p class="gathalast">Abhāsaṃnipakosūro, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para305"></a><span class="paranum">305</span>.</p>\r\n<p class="gatha1"> Nabāḷhaṃitthiṃgaccheyya, sampassaṃ tejasaṅkhayaṃ;</p>\r\n<p class="gathalast">Kāsaṃsāsaṃthaddhābalaṃ, khīṇamedhonigacchati.</p>\r\n<p class="hangnum"><a name="para306"></a><span class="paranum">306</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-304-305-306-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',109,replace(replace('<p class="gatha1">Nātivelaṃpabhāseyya<a name="M0.0109"></a>, natuṇhisabbadāsiyā;</p>\r\n<p class="gathalast">Avitiṇṇaṃmitaṃvācaṃ, patthakāleudīraye.</p>\r\n<p class="hangnum"><a name="para307"></a><span class="paranum">307</span>.</p>\r\n<p class="gatha1"> Akodhanoasaṅghaṭṭo,</p>\r\n<p class="gatha2">Saccosaṇhoapesuṇo;</p>\r\n<p class="gatha3">Samphaṃgiraṃnabhāseyya,</p>\r\n<p class="gathalast">Sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para308"></a><span class="paranum">308</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-307-308-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',110,replace(replace('<p class="gatha1">Mātāpitubharoassa<a name="M0.0110"></a>, kulejeṭṭhāpacāyiko;</p>\r\n<p class="gathalast">Hiriottappasampanno, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para309"></a><span class="paranum">309</span>.</p>\r\n<p class="gatha1"> Vinitosippavādanto, yatattoniyatomudu;</p>\r\n<p class="gathalast">Appamattosucidakkho, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para310"></a><span class="paranum">310</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-309-310-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',111,replace(replace('<p class="gatha1">Nivātavuttivuḍḍhesu<a name="M0.0111"></a>, sappatissosagāravo;</p>\r\n<p class="gathalast">Sūratosukhasaṃvāso, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para311"></a><span class="paranum">311</span>.</p>\r\n<p class="gatha1"> Ārakāparivajjeyya, saññituṃpahitaṃjanaṃ;</p>\r\n<p class="gathalast">Bhattāramevudikkheyya, nacaaññassarājino.</p>\r\n<p class="hangnum"><a name="para312"></a><span class="paranum">312</span>.</p>\r\n<p class="gatha1"> Samaṇebrahmaṇecāpi, sīlavantebahussute;</p>\r\n<p class="gatha2">Sakkaccaṃpayirūpāse, annapānenatappayye;</p>\r\n<p class="gathalast">Āsajjapañhepuccheyya, ākaṅkhaṃvuḍḍhimattano.</p>\r\n<p class="hangnum"><a name="para313"></a><span class="paranum">313</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-311-312-313-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',112,replace(replace('<p class="gatha1">Dinnapubbaṃnahāpeyya<a name="M0.0112"></a>, dānaṃsamaṇabrahmaṇe;</p>\r\n<p class="gathalast">Nacakiñcinivāreyya, dānakālevaṇibbake.</p>\r\n<p class="hangnum"><a name="para314"></a><span class="paranum">314</span>.</p>\r\n<p class="gatha1"> Paññāvāvuḍḍhisampanno, vidhānavidhikovido;</p>\r\n<p class="gathalast">Kālaññūsamayaññūca, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para315"></a><span class="paranum">315</span>.</p>\r\n<p class="gatha1"> Uṭṭhātākammaceresu, appamattovicakkhaṇo;</p>\r\n<p class="gathalast">Susaṃvihitakammanto, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para316"></a><span class="paranum">316</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-314-315-316-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',113,replace(replace('<p class="gatha1">Khalaṃsālaṃpasuṃkhettaṃ<a name="M0.0113"></a>, gantācassaabhikkhaṇaṃ;</p>\r\n<p class="gathalast">Mitaṃdhaññaṃnidhāpeyya, mitañcapācayeghare.</p>\r\n<p class="hangnum"><a name="para317"></a><span class="paranum">317</span>.</p>\r\n<p class="gatha1"> Puttaṃvābhātaraṃvāpi, sīlesuasamāhitaṃ;</p>\r\n<p class="gatha2">Anaṅgavāhitebālā, yathāpetātathevate;</p>\r\n<p class="gathalast">Coḷañcanesaṃpiṇḍañca, āsanañcapadāpare.</p>\r\n<p class="hangnum"><a name="para318"></a><span class="paranum">318</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-317-318-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',114,replace(replace('<p class="gatha1">Dāsekammakarepose<a name="M0.0114"></a>, sīlesususamāhite;</p>\r\n<p class="gathalast">Dakkheuṭṭhānasampanne, adhipaccamhiṭhāpaye.</p>\r\n<p class="hangnum"><a name="para319"></a><span class="paranum">319</span>.</p>\r\n<p class="gatha1"> Sīlavācaalobhoca, anuruttocarājino;</p>\r\n<p class="gathalast">Āvīrahohitocassa, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para320"></a><span class="paranum">320</span>.</p>\r\n<p class="gatha1"> Chandaññūrājinoassa, cittaṭṭhocassarājino;</p>\r\n<p class="gathalast">Asaṅkusakavuttissa, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para321"></a><span class="paranum">321</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-319-320-321-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',115,replace(replace('<p class="gatha1">Ucchādanenhāpaneca<a name="M0.0115"></a>, dhotepādeadosiraṃ;</p>\r\n<p class="gathalast">Āhatopinakuppeyya, sarājavasatiṃvase.</p>\r\n<p class="hangnum"><a name="para322"></a><span class="paranum">322</span>.</p>\r\n<p class="gatha1"> Kumbhiñhipañjaliṃkriyā, cātañcāpipadakkhiṇaṃ;</p>\r\n<p class="gathalast">Kimevasabbakāmānaṃ, nadādaṃdhīramuttamaṃ.</p>\r\n<p class="hangnum"><a name="para323"></a><span class="paranum">323</span>.</p>\r\n<p class="gatha1"> Yodetisayanaṃvatthaṃ, yānaṃāvasataṃgharaṃ;</p>\r\n<p class="gathalast">Pajjunnorivabhūtānaṃ, bhogehiabhivassati.</p>\r\n<p class="hangnum"><a name="para324"></a><span class="paranum">324</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-322-323-324-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',116,replace(replace('<p class="gatha1">Dvevimekaṇḍakātikkhā<a name="M0.0116"></a>, sarīraparisositā;</p>\r\n<p class="gathalast">Kāmetiniddhanoyoca, yocakuppatyanissaro.</p>\r\n<p class="hangnum"><a name="para325"></a><span class="paranum">325</span>.</p>\r\n<p class="gatha1"> Adhanassarasaṃkhādā, abalassahatāhatā;</p>\r\n<p class="gathalast">Apaññassakathāvākyā, tividhaṃhīnalakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para326"></a><span class="paranum">326</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-325-326-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',117,replace(replace('<p class="gatha1">Pathabyāmadhurātīṇi<a name="M0.0117"></a>, ucchunārīsubhāsitaṃ;</p>\r\n<p class="gathalast">Ucchunārīsutappanti, natappantisubhāsitaṃ.</p>\r\n<p class="hangnum"><a name="para327"></a><span class="paranum">327</span>.</p>\r\n<p class="gatha1"> Pathabyātīṇiratanāni, saṅgahānimahītale;</p>\r\n<p class="gathalast">Sippaṃdhaññañcamittañca, bhavantiratanāime.</p>\r\n<p class="hangnum"><a name="para328"></a><span class="paranum">328</span>.</p>\r\n<p class="gatha1"> Kalyāṇamittaṃkantāraṃ, yuddhaṃsabhāyabhāsituṃ;</p>\r\n<p class="gathalast">Asatthāgantumicchanti, muḷhātecaturojanā.</p>\r\n<p class="hangnum"><a name="para329"></a><span class="paranum">329</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-327-328-329-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',118,replace(replace('<p class="gatha1">Jīvantopimatāpañca<a name="M0.0118"></a>, byāsenaparikittitā;</p>\r\n<p class="gathalast">Dukkhitobyādhitopakkho, iṇavānityasevako.</p>\r\n<p class="hangnum"><a name="para330"></a><span class="paranum">330</span>.</p>\r\n<p class="gatha1"> Cakkhudvārādikaṃchakkaṃ, saṃvutosapaññonaro;</p>\r\n<p class="gathalast">Chabbidhohotisīlena, asīlenāpichabbidho.</p>\r\n<p class="hangnum"><a name="para331"></a><span class="paranum">331</span>.</p>\r\n<p class="gatha1"> Niddālukopamādoca, sukhitorogavālaso;</p>\r\n<p class="gathalast">Nicchandocakammārāmo, sattetesatthavajjitā.</p>\r\n<p class="hangnum"><a name="para332"></a><span class="paranum">332</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-330-331-332-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',119,replace(replace('<p class="gatha1">Kulajopaññavāchando<a name="M0.0119"></a>, hirottapposutaddharo;</p>\r\n<p class="gathalast">Atthakāmosurakkhoca, aṭṭhetesatthayujjitā.</p>\r\n<p class="hangnum"><a name="para333"></a><span class="paranum">333</span>.</p>\r\n<p class="gatha1"> Kulaseṭṭhosapaññoca, vuḍḍhisūrocasīlavā;</p>\r\n<p class="gatha2">Bahussutovuṭṭhānoca, mīrosugatigāmiko;</p>\r\n<p class="gathalast">Navetesujanāseṭṭhā, pāpāttānaṃnivāraye.</p>\r\n<p class="hangnum"><a name="para334"></a><span class="paranum">334</span>.</p>\r\n<p class="gatha1"> Buddhopaccekabuddhoca, arahāaggasāvako;</p>\r\n<p class="gatha2">Mātāpitāgarusatthā, dāyakodhammadesako;</p>\r\n<p class="gathalast">Paṇḍitehiimedasa, na dubbhantītijāniyā.</p>\r\n<p class="hangnum"><a name="para335"></a><span class="paranum">335</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-333-334-335-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',120,replace(replace('<p class="gatha1">Dhammatthakāmamokkhānaṃ<a name="M0.0120"></a>, pāṇosaṃsiddhikāraṇaṃ;</p>\r\n<p class="gathalast">Taṃnighātokiṃnihato, rakkhitokiṃnarakkhati.</p>\r\n<p class="hangnum"><a name="para336"></a><span class="paranum">336</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-336-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',121,replace(replace('<p class="gatha1">Sathaṃdīghāyukosabba<a name="M0.0121"></a>, sattānaṃsukhakāraṇaṃ;</p>\r\n<p class="gathalast">Asathaṃpanasabbesaṃ, dukkhahetunasaṃsayo.</p>\r\n<p class="hangnum"><a name="para337"></a><span class="paranum">337</span>.</p>\r\n<p class="gatha1"> Yantagatoucchurasaṃ, najahātigajotathā;</p>\r\n<p class="gathalast">Saṅgāmesugatoliḷaṃ, sussutenāpicandanaṃ.</p>\r\n<p class="hangnum"><a name="para338"></a><span class="paranum">338</span>.</p>\r\n<p class="gatha1"> Sāragandhaṃnajahāti, dukkhapattopipaṇḍito;</p>\r\n<p class="gathalast">Najahātisataṃdhammaṃ, sukhakālekathāvakā.</p>\r\n<p class="hangnum"><a name="para339"></a><span class="paranum">339</span>.</p>\r\n<p class="gatha1"> Attābandhumanussānaṃ, ripuattāvajantunaṃ;</p>\r\n<p class="gathalast">Attāvaniyatoñāti, attāvaniyatoripu.</p>\r\n<p class="hangnum"><a name="para340"></a><span class="paranum">340</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-337-338-339-340-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',122,replace(replace('<p class="gatha1">Attānaṃpariccāgena<a name="M0.0122"></a>, yaṃnissitānurakkhanaṃ;</p>\r\n<p class="gathalast">Karontisajjanāyeva, nataṃnitimātāmataṃ.</p>\r\n<p class="hangnum"><a name="para341"></a><span class="paranum">341</span>.</p>\r\n<p class="gatha1"> Satthakabbavicārena, kālogacchatidhīmataṃ;</p>\r\n<p class="gathalast">Byasanenaasādhūnaṃ, niddāyakalahenavā.</p>\r\n<p class="hangnum"><a name="para342"></a><span class="paranum">342</span>.</p>\r\n<p class="gatha1"> Bhamarāpupphamicchanti, putimicchantimakkhikā;</p>\r\n<p class="gathalast">Sujānāguṇamicchanti, dosamicchantidujjanā.</p>\r\n<p class="hangnum"><a name="para343"></a><span class="paranum">343</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-341-342-343-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',123,replace(replace('<p class="gatha1">Namantiphalinorukkhā<a name="M0.0123"></a>, namatevabudhājanā;</p>\r\n<p class="gathalast">Sukkhakaṭṭhañcamuḷhoca, nevanamantibhijjate.</p>\r\n<p class="hangnum"><a name="para344"></a><span class="paranum">344</span>.</p>\r\n<p class="gatha1"> Sacesantovivādati, khippasandhiyarepuna;</p>\r\n<p class="gathalast">Bālopattāvabhijjanti, natesamatamāgamuṃ.</p>\r\n<p class="hangnum"><a name="para345"></a><span class="paranum">345</span>.</p>\r\n<p class="gatha1"> Appampisādhūnaṃdhanaṃ, kūpāvārivanissayo;</p>\r\n<p class="gathalast">Bahukaṃpiasādhūnaṃ, nacavārivaaṇṇave.</p>\r\n<p class="hangnum"><a name="para346"></a><span class="paranum">346</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-344-345-346-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',124,replace(replace('<p class="gatha1">Sokaṭhānasahassāni<a name="M0.0124"></a>, bhayaṭhānasatānica;</p>\r\n<p class="gathalast">Divasedivasemuḷhaṃ, āvīsantinapaṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para347"></a><span class="paranum">347</span>.</p>\r\n<p class="gatha1"> Duṭṭhacittopanāhissa, kodhopāsāṇalekhito;</p>\r\n<p class="gathalast">Kucchitabbosujanassa, jalelekhāciraṭṭhitā.</p>\r\n<p class="hangnum"><a name="para348"></a><span class="paranum">348</span>.</p>\r\n<p class="gatha1"> Nidulukoasantuṭṭho, akataññūcabhiruko;</p>\r\n<p class="gathalast">Sakkontinasamācāraṃ, sikkhituṃtekadācipi.</p>\r\n<p class="hangnum"><a name="para349"></a><span class="paranum">349</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-347-348-349-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',125,replace(replace('<p class="gatha1">Sādhuttaṃsujanasamāgamākhalānaṃ<a name="M0.0125"></a>,</p>\r\n<p class="gatha2">Sādhūnaṃnakhalasamāgamākhalattaṃ;</p>\r\n<p class="gatha3">Āmodaṃkusumabhavaṃdadhātibhūmi,</p>\r\n<p class="gathalast">Bhūgandhaṃnacakusumānidhārayanti.</p>\r\n<p class="hangnum"><a name="para350"></a><span class="paranum">350</span>.</p>\r\n<p class="gatha1"> Guṇamaddhisamaṃmakkhe, parenakalahesati;</p>\r\n<p class="gathalast">Addhisamaṃpakāsentaṃ, anumattaṃpidosakaṃ.</p>\r\n<p class="hangnum"><a name="para351"></a><span class="paranum">351</span>.</p>\r\n<p class="gatha1"> Dosaṃparassapassanti, attadosaṃnapassati;</p>\r\n<p class="gathalast">Tilamattaṃparadosaṃ, nāḷikeraṃnapassati.</p>\r\n<p class="hangnum"><a name="para352"></a><span class="paranum">352</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-350-351-352-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',126,replace(replace('<p class="gatha1">Kodhoatthaṃnajānāti<a name="M0.0126"></a>, kodhodhammaṃnapassati;</p>\r\n<p class="gathalast">Andhatamaṃtadāhoti, yaṃkodhosahatenaraṃ.</p>\r\n<p class="hangnum"><a name="para353"></a><span class="paranum">353</span>.</p>\r\n<p class="gatha1"> Kodhoabbhantarejāto, dhuvaṃnāsetikodhanaṃ;</p>\r\n<p class="gathalast">Vatthālaṅkārapuṇṇāyaṃ, mañjusāyaṃsikhīyathā.</p>\r\n<p class="hangnum"><a name="para354"></a><span class="paranum">354</span>.</p>\r\n<p class="gatha1"> Rāgonāmamanosallaṃ, guṇavarattacorako;</p>\r\n<p class="gathalast">Rāhuvijjāsasaṅkissa, tapodhanahutāsano.</p>\r\n<p class="hangnum"><a name="para355"></a><span class="paranum">355</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-353-354-355-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',127,replace(replace('<p class="gatha1">Natittirājādhanena<a name="M0.0127"></a>, paṇḍitopisubhāsite;</p>\r\n<p class="gathalast">Cakkhūpipiyadassane, sāgaropimahājale.</p>\r\n<p class="hangnum"><a name="para356"></a><span class="paranum">356</span>.</p>\r\n<p class="gatha1"> Asantuṭṭhoyatinaṭṭho, santuṭṭhopimahīpati;</p>\r\n<p class="gathalast">Sasajjāgaṇikānaṭṭhā, nilajjāsukulagatā.</p>\r\n<p class="hangnum"><a name="para357"></a><span class="paranum">357</span>.</p>\r\n<p class="gatha1"> Bhūpāṇṇavaggithīsippī, abhijjhālucapuggalo;</p>\r\n<p class="gathalast">Etesaṃmahicchantānaṃ, mahicchatāanicchitā.</p>\r\n<p class="hangnum"><a name="para358"></a><span class="paranum">358</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-356-357-358-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',128,replace(replace('<p class="gatha1">Ārogyaṃparamaṃlābhaṃ<a name="M0.0128"></a>, santuṭṭhīparamaṃdhanaṃ;</p>\r\n<p class="gathalast">Visāsoparamaṃñāti, nibbānaṃparamaṃsukhaṃ.</p>\r\n<p class="hangnum"><a name="para359"></a><span class="paranum">359</span>.</p>\r\n<p class="gatha1"> Duggataṃgacchabholābha, lābholābhenapūrati;</p>\r\n<p class="gathalast">Thalepavuṭṭhapajjunna, āpoāpenapūrati.</p>\r\n<p class="hangnum"><a name="para360"></a><span class="paranum">360</span>.</p>\r\n<p class="gatha1"> Bodhayantinayācanti, dehītipacchimājanā;</p>\r\n<p class="gathalast">Passavatthuṃadānassa, mābhavatūtiīdiso.</p>\r\n<p class="hangnum"><a name="para361"></a><span class="paranum">361</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-359-360-361-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',129,replace(replace('<p class="gatha1">Seleselenamāṇikaṃ<a name="M0.0129"></a>, gajegajenamuttikaṃ;</p>\r\n<p class="gathalast">Vanevanenacandanaṃ, ṭhāneṭhānenapaṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para362"></a><span class="paranum">362</span>.</p>\r\n<p class="gatha1"> Satesujāyatesūro, sahassesucapaṇḍito;</p>\r\n<p class="gathalast">Vākyaṃsatasahassesu, cāgobhavativānavā.</p>\r\n<p class="hangnum"><a name="para363"></a><span class="paranum">363</span>.</p>\r\n<p class="gatha1"> Jinenaāgataṃsūraṃ, dhanañcagehamāgataṃ;</p>\r\n<p class="gathalast">Jiṇṇaannaṃpasaṃseyya, dārañcagatayobbanaṃ.</p>\r\n<p class="hangnum"><a name="para364"></a><span class="paranum">364</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-362-363-364-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',130,replace(replace('<p class="gatha1">Potthakesucayaṃsippaṃ<a name="M0.0130"></a>, parahatthesuyaṃdhanaṃ;</p>\r\n<p class="gathalast">Yadāicchesamuppanne, nataṃsippaṃnataṃdhanaṃ.</p>\r\n<p class="hangnum"><a name="para365"></a><span class="paranum">365</span>.</p>\r\n<p class="gatha1"> Vācāvudhācarājāno, saccāvudhācasamaṇā;</p>\r\n<p class="gathalast">Dhanāvudhāseṭṭhinoca, goṇāvudhādaliddakā.</p>\r\n<p class="hangnum"><a name="para366"></a><span class="paranum">366</span>.</p>\r\n<p class="gatha1"> Ukkaṭṭhesūramicchanti, kolāhalesubhāsitaṃ;</p>\r\n<p class="gathalast">Piyaṃannañcapānañca, atthakiccesupaṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para367"></a><span class="paranum">367</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-365-366-367-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',131,replace(replace('<p class="gatha1">Kapaṇetārayemittaṃ<a name="M0.0131"></a>, dubbhikkhedhaññaṃdhāraye;</p>\r\n<p class="gathalast">Sabhāyaṃdhārayesippaṃ, saṅgahānimahītale.</p>\r\n<p class="hangnum"><a name="para368"></a><span class="paranum">368</span>.</p>\r\n<p class="gatha1"> Dubbhikkheannadānañca, subhikkhecahiraññadaṃ;</p>\r\n<p class="gathalast">Bhayecabhayadhātāraṃ, sabbesaṃvaramaṃvaraṃ.</p>\r\n<p class="hangnum"><a name="para369"></a><span class="paranum">369</span>.</p>\r\n<p class="gatha1"> Haṃsomajjhenakākānaṃ, sīhogunnaṃnasobhate;</p>\r\n<p class="gathalast">Gadrabhānaṃnaturaṅgo, bālānañcanapaṇḍito.</p>\r\n<p class="hangnum"><a name="para370"></a><span class="paranum">370</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-368-369-370-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',132,replace(replace('<p class="gatha1">Nasorājāyoajeyyaṃ<a name="M0.0132"></a>, jinātinasosakhāraṃ;</p>\r\n<p class="gatha2">Yoayuttenajināti, nasābhariyāpatino;</p>\r\n<p class="gathalast">Virodhatinateputtā, yenabharanti jiṇṇa.</p>\r\n<p class="hangnum"><a name="para371"></a><span class="paranum">371</span>.</p>\r\n<p class="gatha1"> Natthivijjāsamaṃmittaṃ, natthibyādhisamoripu;</p>\r\n<p class="gathalast">Natthiattasamaṃpemaṃ, natthikammaparaṃbalaṃ.</p>\r\n<p class="hangnum"><a name="para372"></a><span class="paranum">372</span>.</p>\r\n<p class="gatha1"> Itthimissokutosīlaṃ,</p>\r\n<p class="gatha2">Maṃsabhakkhokutodayaṃ;</p>\r\n<p class="gatha3">Surāpānokutosaccaṃ,</p>\r\n<p class="gathalast">Mahākodhokutotapaṃ.</p>\r\n<p class="hangnum"><a name="para373"></a><span class="paranum">373</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-371-372-373-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',133,replace(replace('<p class="gatha1">Kvātibhārosamatthānaṃ<a name="M0.0133"></a>, kiṃdūrobyavahārinaṃ;</p>\r\n<p class="gathalast">Kovidesosavijjānaṃ, koparopiyavādinaṃ.</p>\r\n<p class="hangnum"><a name="para374"></a><span class="paranum">374</span>.</p>\r\n<p class="gatha1"> Dubbhikkhokasinonatthi, santānaṃnatthipāpako;</p>\r\n<p class="gathalast">Mugassakalahonatthi, natthijāgaratobhayaṃ.</p>\r\n<p class="hangnum"><a name="para375"></a><span class="paranum">375</span>.</p>\r\n<p class="gatha1"> Bālitthīmakkhikātuṇḍi, isīnañcakamaṇḍalu;</p>\r\n<p class="gathalast">Setambuphalaṃtambulaṃ, nojjhiṭṭhamupajāyato.</p>\r\n<p class="hangnum"><a name="para376"></a><span class="paranum">376</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-374-375-376-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',134,replace(replace('<p class="gatha1">Pañcaratyāsugandhabbā<a name="M0.0134"></a>, sattaratyādhanuggahā;</p>\r\n<p class="gathalast">Ekamāsāsubhariyā, aḍḍhamāsāsissāmalā.</p>\r\n<p class="hangnum"><a name="para377"></a><span class="paranum">377</span>.</p>\r\n<p class="gatha1"> Malitthiyāduccaritaṃ, maccheraṃdadatomalaṃ;</p>\r\n<p class="gatha2">Malāvelāmakādhammā, asmiṃlokeparamhica;</p>\r\n<p class="gathalast">Malaṃmalataraṃtato, avijjāparamaṃmalaṃ.</p>\r\n<p class="hangnum"><a name="para378"></a><span class="paranum">378</span>.</p>\r\n<p class="gatha1"> Sutassarakkhāsabbadābhiyogo,</p>\r\n<p class="gatha2">Kulassavatthaṃpurisassavijjā;</p>\r\n<p class="gatha3">Raññopamādopasamodhanassa,</p>\r\n<p class="gathalast">Itthīnantunatthevajāturakkhā.</p>\r\n<p class="hangnum"><a name="para379"></a><span class="paranum">379</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-377-378-379-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',135,replace(replace('<p class="gatha1">Sattānaṃjaratāhanti<a name="M0.0135"></a>, taṇhāhantisabbasukhaṃ;</p>\r\n<p class="gathalast">Sabbabalaṃcintāhanti, dayāhantisakaṃdhanaṃ.</p>\r\n<p class="hangnum"><a name="para380"></a><span class="paranum">380</span>.</p>\r\n<p class="gatha1"> Nīcevāsosiriṃhanti, hantigaruṃcayācako;</p>\r\n<p class="gathalast">Pasaṃsāsuguṇaṃhanti, hanticittaṃasaññatā.</p>\r\n<p class="hangnum"><a name="para381"></a><span class="paranum">381</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-380-381-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',136,replace(replace('<p class="gatha1">Asanaṃbhayamantānaṃ<a name="M0.0136"></a>, maccānaṃmaraṇaṃbhayaṃ;</p>\r\n<p class="gathalast">Uttamānantusabbesaṃ, avamānaṃparaṃbhayaṃ.</p>\r\n<p class="hangnum"><a name="para382"></a><span class="paranum">382</span>.</p>\r\n<p class="gatha1"> Sūriyotapanaṃtapo, nasantiparivāritā;</p>\r\n<p class="gatha2">Candaraṃsītalaṃjātaṃ, tārakāparivāritā;</p>\r\n<p class="gathalast">Upamāetthañātabbā, sūriyacandarājunaṃ.</p>\r\n<p class="hangnum"><a name="para383"></a><span class="paranum">383</span>.</p>\r\n<p class="gatha1"> Alasomandabuddhica, sukhitorogapīḷito;</p>\r\n<p class="gathalast">Niddāromaṃsavaḍḍhano, subhakkhocaviluddhako.</p>\r\n<p class="hangnum"><a name="para384"></a><span class="paranum">384</span>.</p>\r\n<p class="gatha1"> Pamādojāyatemadā, pamādājāyatekhayo;</p>\r\n<p class="gathalast">Khayādosāpavaḍḍhanti, madaṃkiṃnajahebudho.</p>\r\n<p class="hangnum"><a name="para385"></a><span class="paranum">385</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-382-383-384-385-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',137,replace(replace('<p class="gatha1">Yādisaṃvappatebījaṃ<a name="M0.0137"></a>, tādisaṃphalaṃsampatto;</p>\r\n<p class="gathalast">Kalyāṇakārikalyāṇaṃ, pāpakārīcapāpakaṃ.</p>\r\n<p class="hangnum"><a name="para386"></a><span class="paranum">386</span>.</p>\r\n<p class="gatha1"> Puññāpāpaphalaṃyoce, nasaddahatisaccato;</p>\r\n<p class="gathalast">Sovesakānanaṃkhippaṃ, ādāsatalamānaye.</p>\r\n<p class="hangnum"><a name="para387"></a><span class="paranum">387</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-386-387-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',138,replace(replace('<p class="gatha1">Samparāyikattheyo<a name="M0.0138"></a>, nasaddahaticepiso;</p>\r\n<p class="gathalast">Āvāsesappagāmīnaṃ, mokkhabhekiṃnapassati.</p>\r\n<p class="hangnum"><a name="para388"></a><span class="paranum">388</span>.</p>\r\n<p class="gatha1"> Saddhāhiricaottappaṃ, bāhusaccaṃviraṃsati;</p>\r\n<p class="gathalast">Paññācasattadhammehi, sampannopaṇḍitomato.</p>\r\n<p class="hangnum"><a name="para389"></a><span class="paranum">389</span>.</p>\r\n<p class="gatha1"> Ravimūlaṃsasīkhandhaṃ, soriaṅgācapattikaṃ;</p>\r\n<p class="gathalast">Buddhaṃpupphaṃgarubījaṃ, bharaguphalamevaca.</p>\r\n<p class="hangnum"><a name="para390"></a><span class="paranum">390</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-388-389-390-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',139,replace(replace('<p class="gatha1">Potthakādīnikhettaṃva<a name="M0.0139"></a>, lekhāniyuganaṅgalaṃ;</p>\r\n<p class="gathalast">Akkharānibījaṃkatvā, carantopaṇḍitobhave.</p>\r\n<p class="hangnum"><a name="para391"></a><span class="paranum">391</span>.</p>\r\n<p class="gatha1"> Akkharaṃekamekañca, buddharūpaṃsamaṃsiyā;</p>\r\n<p class="gathalast">Tasmāhipaṇḍitoposo, likheyyapiṭakattayaṃ.</p>\r\n<p class="hangnum"><a name="para392"></a><span class="paranum">392</span>.</p>\r\n<p class="gatha1"> Duggatiṃnābhijāyeyya, piṭakattayakārako;</p>\r\n<p class="gathalast">Bahukkhattuṃcakkavatti, rājācatudīpādhipo.</p>\r\n<p class="hangnum"><a name="para393"></a><span class="paranum">393</span>.</p>\r\n<p class="gatha1"> Padesarajjaṃvipulaṃ, gaṇanātoasaṅkhyeyo;</p>\r\n<p class="gathalast">Chakāmāvacarodeva, rājāhotibahukkhattuṃ.</p>\r\n<p class="hangnum"><a name="para394"></a><span class="paranum">394</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-391-392-393-394-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',140,replace(replace('<p class="gatha1">Dānādīnicapuññāni<a name="M0.0140"></a>, karontobodhiaṅkuro;</p>\r\n<p class="gathalast">Bhavāsabbaṅgasampanno, tilokapūjitobhave.</p>\r\n<p class="hangnum"><a name="para395"></a><span class="paranum">395</span>.</p>\r\n<p class="gatha1"> Addhemahaddhanephite, jāyarekulamuttame;</p>\r\n<p class="gathalast">Uttamenevasaṃvāso, piṭakattayavācako.</p>\r\n<p class="hangnum"><a name="para396"></a><span class="paranum">396</span>.</p>\r\n<p class="gatha1"> Ekakkharaphalenahi, piṭakattayakārako;</p>\r\n<p class="gathalast">Caturāsītisahassaṃ, labhantipavaraṃsukhaṃ.</p>\r\n<p class="hangnum"><a name="para397"></a><span class="paranum">397</span>.</p>\r\n<p class="gatha1"> Appakenāpimedhāvī, pābhatenavicakkhaṇo;</p>\r\n<p class="gathalast">Samuṭṭhāpetiattānaṃ, anumaggivasandhamaṃ.</p>\r\n<p class="hangnum"><a name="para398"></a><span class="paranum">398</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-395-396-397-398-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',141,replace(replace('<p class="gatha1">Dukkhaṃpāpassapuññassa<a name="M0.0141"></a>, sukhaṃmissassamissakaṃ;</p>\r\n<p class="gathalast">Sabbaṃsadisakaṃyāti, ñātabbaṃkammunophalaṃ.</p>\r\n<p class="hangnum"><a name="para399"></a><span class="paranum">399</span>.</p>\r\n<p class="gatha1"> Codentocatubhāgāca, kammakārātayobhāgā;</p>\r\n<p class="gathalast">Sāminosamabhāgāca, ekabhāgānumodanā.</p>\r\n<p class="hangnum"><a name="para400"></a><span class="paranum">400</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-399-400-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',142,replace(replace('<p class="gatha1">Anattassavākyāparamaṃtuṇhi<a name="M0.0142"></a>,</p>\r\n<p class="gatha2">Asantamittāparamaṃekaṃ;</p>\r\n<p class="gatha3">Surūpadārāvaramandhā,</p>\r\n<p class="gathalast">Dūrekalābhāvaramassasukkhaṃ.</p>\r\n<p class="hangnum"><a name="para401"></a><span class="paranum">401</span>.</p>\r\n<p class="gatha1"> Hīnacajjopice hoti, uṭṭhātādhītimānaro;</p>\r\n<p class="gathalast">Sīlaācārasampanno, niseaggivabhāsati.</p>\r\n<p class="hangnum"><a name="para402"></a><span class="paranum">402</span>.</p>\r\n<p class="gatha1"> Nacajjavasalohoti,</p>\r\n<p class="gatha2">Nacajjahotibrahmaṇo;</p>\r\n<p class="gatha3">Kammunāvasalohoti,</p>\r\n<p class="gathalast">Kammunāhotibrahmaṇo.</p>\r\n<p class="hangnum"><a name="para403"></a><span class="paranum">403</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-401-402-403-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',143,replace(replace('<p class="gatha1">Pathavīveḷukaṃpattaṃ<a name="M0.0143"></a>, cakkavāḷaṃsucipphalaṃ;</p>\r\n<p class="gathalast">Sineruvammikaṃkhuddaṃ, samuddopātitaṃyathā.</p>\r\n<p class="hangnum"><a name="para404"></a><span class="paranum">404</span>.</p>\r\n<p class="gatha1"> Ekenevacakappena, mātukhīraṃnasañcayaṃ;</p>\r\n<p class="gathalast">Tatotusamuddocāpi, atirekataraṃbahuṃ.</p>\r\n<p class="hangnum"><a name="para405"></a><span class="paranum">405</span>.</p>\r\n<p class="gatha1"> Brahmātimātāpītaro, pubbācariyāvuccate;</p>\r\n<p class="gathalast">Āhuneyyācaputtānaṃ, pajānamanukampakā.</p>\r\n<p class="hangnum"><a name="para406"></a><span class="paranum">406</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-404-405-406-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',144,replace(replace('<p class="gatha1">Tasmāhinenamasseyya<a name="M0.0144"></a>, sakkareyyacapaṇḍito;</p>\r\n<p class="gathalast">Annenaathopānena, vatthenasayanenaca.</p>\r\n<p class="hangnum"><a name="para407"></a><span class="paranum">407</span>.</p>\r\n<p class="gatha1"> Ucchādanenanhāpena, pādānaṃdhovanenaca;</p>\r\n<p class="gatha2">Uṭṭhāyapādacariyā, upaṭṭhāpeyyapaṇḍito;</p>\r\n<p class="gathalast">Idhevanaṃpasaṃsanti, paccasaggepamodati.</p>\r\n<p class="hangnum"><a name="para408"></a><span class="paranum">408</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-407-408-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',145,replace(replace('<p class="gatha1">Ekassekenakappena<a name="M0.0145"></a>, puggalassaṭṭhisañcayo;</p>\r\n<p class="gathalast">Samaṃpabbatarāsimhi, itivuttaṃmahesinā.</p>\r\n<p class="hangnum"><a name="para409"></a><span class="paranum">409</span>.</p>\r\n<p class="gatha1"> Sabbadānaṃdhammadānaṃjināti;</p>\r\n<p class="gatha2">Sabbarasaṃdhammarasojināti,</p>\r\n<p class="gatha3">Sabbaratiṃdhammaratijināti;</p>\r\n<p class="gathalast">Sabbadukkhaṃtaṇhakkhayojināti.</p>\r\n<p class="hangnum"><a name="para410"></a><span class="paranum">410</span>.</p>\r\n<p class="gatha1"> Appamādaratāhotha, sacittamanurakkhatha;</p>\r\n<p class="gathalast">Dukkhāuddharathattānaṃ, paṅkesannaṃvakuñjaraṃ.</p>\r\n<p class="hangnum"><a name="para411"></a><span class="paranum">411</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-409-410-411-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',146,replace(replace('<p class="gatha1">Cajadujjanasaṃsaggaṃ<a name="M0.0146"></a>, bhajasādhusamāgamaṃ;</p>\r\n<p class="gathalast">Karapuññamahorattiṃ, saraniccamaniccataṃ.</p>\r\n<p class="hangnum"><a name="para412"></a><span class="paranum">412</span>.</p>\r\n<p class="gatha1"> Aniccāvatasaṅkhārā, uppādavayadhammino;</p>\r\n<p class="gathalast">Uppajjitvānirujjhanti, tesaṃvūpasamosukho.</p>\r\n<p class="hangnum"><a name="para413"></a><span class="paranum">413</span>.</p>\r\n<p class="gatha1"> Nahidhammoadhammoca, ubhosamavipākino;</p>\r\n<p class="gathalast">Adhammonirayaṃneti, dhammopāpetisuggatiṃ.</p>\r\n<p class="hangnum"><a name="para414"></a><span class="paranum">414</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-412-413-414-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1003n',147,replace(replace('<p class="gatha1">Samasīsaṃsamapādaṃ<a name="M0.0147"></a>, antarañcasamaṃsamaṃ;</p>\r\n<p class="gathalast">Idaṃmanasinidhāya, likheyyapiṭakattayanti.</p>','\r',char(13)),'\n',char(10)),'');
