INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="chapter">Nītimañjarī</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',1,replace(replace('<p class="gatha1">Kulakkhaye <a name="M0.0001"></a> vinassanti,</p>\r\n<p class="gatha2">Kuladhammā sanantanā;</p>\r\n<p class="gatha3">Dhamme naṭṭhe kulaṃ sabbaṃ,</p>\r\n<p class="gathalast">Adhammo abhibhū khalaṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Adhammābhibhavā dantā,</p>\r\n<p class="gatha2">Padussanti kulitthiyo;</p>\r\n<p class="gatha3">Thīsu duṭṭhā sva dhammena,</p>\r\n<p class="gathalast">Jāyate vaṇṇasaṅkaro.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',11,replace(replace('<p class="gatha1">Piyaṃ <a name="M0.0011"></a> bhāse guṇaggāho,</p>\r\n<p class="gatha2">Sūro siyā vikantano;</p>\r\n<p class="gatha3">Dātā candasamā nārī,</p>\r\n<p class="gathalast">Diṭṭhaṃ diṭṭhaṃ nahāsaye.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',16,replace(replace('<p class="gatha1">Kutotthi <a name="M0.0016"></a> kumitte saccaṃ,</p>\r\n<p class="gatha2">Kudāre rativaḍḍhanaṃ;</p>\r\n<p class="gatha3">Kudesamhi mano rammaṃ,</p>\r\n<p class="gathalast">Kurāje bhogasampadaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',17,replace(replace('<p class="gatha1">Saṅketeva <a name="M0.0017"></a> amittasmiṃ,</p>\r\n<p class="gatha2">Mittasmiṃ pi navissase;</p>\r\n<p class="gatha3">Abhayā bhaya muppannaṃ,</p>\r\n<p class="gathalast">Api mūlāni kantati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',18,replace(replace('<p class="gatha1">Adiṭṭhova <a name="M0.0018"></a> paro seyyo,</p>\r\n<p class="gathalast">Dummitto no vissāsiko.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',23,replace(replace('<p class="gatha1">Aggihomaphalaṃ <a name="M0.0023"></a> vedo,</p>\r\n<p class="gatha2">Satthaṃsīlaphalaṃ mataṃ;</p>\r\n<p class="gatha3">Ratiputtaphalaṃ nārī,</p>\r\n<p class="gathalast">Dānabhuttiphalaṃ dhanaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',24,replace(replace('<p class="gatha1">Asaccaṃ <a name="M0.0024"></a> sāhasaṃ māyā,</p>\r\n<p class="gatha2">Mūḷhatta ma tilobhatā;</p>\r\n<p class="gatha3">Asocaṃ niddayattañca,</p>\r\n<p class="gathalast">Thīnaṃ dosā sabhāvajā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',26,replace(replace('<p class="gatha1">Jāyāya <a name="M0.0026"></a> bhattuno bhāro,</p>\r\n<p class="gatha2">Sissena guruno kato;</p>\r\n<p class="gatha3">Amaccakehi rājassa,</p>\r\n<p class="gathalast">Pitarānaṃ nijenaca.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',27,replace(replace('<p class="gatha1">Uyyamena <a name="M0.0027"></a> hi sijjhanti,</p>\r\n<p class="gatha2">Kammāni na manorathā;</p>\r\n<p class="gatha3">Na hi suttassa sīhassa,</p>\r\n<p class="gathalast">Pavīsanti mukhe migā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',31,replace(replace('<p class="gatha1">Atisītaṃ <a name="M0.0031"></a> atiuṇhaṃ,</p>\r\n<p class="gatha2">Atisāyamidaṃ ahu;</p>\r\n<p class="gatha3">Iti visaṭṭhakammante,</p>\r\n<p class="gathalast">Khaṇā accenti māṇave.</p>\r\n<p class="gatha1">Ādānassa padānassa,</p>\r\n<p class="gatha2">Kattabbassa ca kammuno;</p>\r\n<p class="gatha3">Khippaṃ akayyamānassa,</p>\r\n<p class="gathalast">Kāle pivati sampadaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',32,replace(replace('<p class="gatha1">Nādabbe <a name="M0.0032"></a> nihitā kāci,</p>\r\n<p class="gatha2">Kriyā phalavatī bhave;</p>\r\n<p class="gatha3">Nabyāpārasatenāpi,</p>\r\n<p class="gathalast">Sukova pāṭhate bako.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',34,replace(replace('<p class="gatha1">Yo <a name="M0.0034"></a> dandhakāle tarati,</p>\r\n<p class="gatha2">Taraṇīye ca dandhaye;</p>\r\n<p class="gatha3">Sukkhapaṇṇaṃ va akkamma,</p>\r\n<p class="gathalast">Atthaṃ bhañjati attano.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',35,replace(replace('<p class="gatha1">Yaṃ <a name="M0.0035"></a> dadāti yaṃ bhuñjati,</p>\r\n<p class="gatha2">Tadeva dhanino dhanaṃ;</p>\r\n<p class="gatha3">Aññe matassa kīḷanti,</p>\r\n<p class="gathalast">Dārehipi dhanehipi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',39,replace(replace('<p class="gatha1">Dānopabhogahīnena<a name="M0.0039"></a>,</p>\r\n<p class="gatha2">Dhanena dhanino sukhaṃ;</p>\r\n<p class="gatha3">Ko viseso daliddassa,</p>\r\n<p class="gathalast">Adhikaṃ dhanarakkhaṇaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',40,replace(replace('<p class="gatha1">Nijasokhyaṃ <a name="M0.0040"></a> nirundhanto,</p>\r\n<p class="gatha2">Nīcabhogo mitampaco;</p>\r\n<p class="gatha3">Dhanaṃ sañcayate yo so,</p>\r\n<p class="gathalast">Parabhāravaho pasu.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',41,replace(replace('<p class="gatha1">Yaṃ <a name="M0.0041"></a> ussukā saṅkharonti,</p>\r\n<p class="gatha2">Alakkhikā bahuṃ dhanaṃ;</p>\r\n<p class="gatha3">Sippavanto asippāvā,</p>\r\n<p class="gathalast">Lakkhi vā tāni bhuñjati.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',48,replace(replace('<p class="gatha1">Sampatyaṃ <a name="M0.0048"></a> mahataṃ cittaṃ,</p>\r\n<p class="gatha2">Bhave uppale komalaṃ;</p>\r\n<p class="gatha3">Vipatyaṃca mahāsela,</p>\r\n<p class="gathalast">Silāsaṅghātakakkasaṃ.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',53,replace(replace('<p class="gatha1">Asambhabyaguṇaṃ <a name="M0.0053"></a> thutvā,</p>\r\n<p class="gatha2">Khedo mudhāva jāyate;</p>\r\n<p class="gatha3">Avhāyaṃ canda mu llokya,</p>\r\n<p class="gathalast">Nacandota mu pāgamī.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',59,replace(replace('<p class="gatha1">Saccaṃ <a name="M0.0059"></a> mukhamhi dhāreyya,</p>\r\n<p class="gatha2">Kaṇṇe sutaṃ bhuje jayaṃ;</p>\r\n<p class="gatha3">Hadayamhi khamaṃ vīraṃ,</p>\r\n<p class="gathalast">Lokādāsaṃca locane.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',63,replace(replace('<p class="gatha1">Saddamattaṃ <a name="M0.0063"></a> naphandeyya,</p>\r\n<p class="gatha2">Aññatvā saddakāraṇaṃ;</p>\r\n<p class="gatha3">Saddahetuṃ pariññāya,</p>\r\n<p class="gathalast">Pamodo vā bhayo tathā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',64,replace(replace('<p class="gatha1">Sabbasuta <a name="M0.0064"></a> ma dhīyeyya,</p>\r\n<p class="gathalast">Hīnamukkaṭṭhamajjhimaṃ.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',68,replace(replace('<p class="gatha1">Dunnāriyā <a name="M0.0068"></a> kulaṃ suddhaṃ,</p>\r\n<p class="gatha2">Putto nassati lālanā;</p>\r\n<p class="gatha3">Samiddhi anayā bandhu,</p>\r\n<p class="gathalast">Pavāsā madanā hirī.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',75,replace(replace('<p class="gatha1">Lālaye <a name="M0.0075"></a> pañcavassāni,</p>\r\n<p class="gatha2">Dasavassāni tālaye;</p>\r\n<p class="gatha3">Pattetu soḷasevasse,</p>\r\n<p class="gathalast">Puttaṃ mittaṃva ācare.</p>\r\n<p class="gatha1">Lālane bahavo dosā,</p>\r\n<p class="gathalast">Lālane bahavo guṇā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',77,replace(replace('<p class="gatha1">Pāpā <a name="M0.0077"></a> nivārayati yojayate hitāya,</p>\r\n<p class="gatha2">Guyhāni gūhati guṇaṃ pakaṭīkaroti;</p>\r\n<p class="gatha3">Āpattikañca najahāti dadāti kāle,</p>\r\n<p class="gathalast">Sammitta lakkhaṇamidaṃ pavadanti santo.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',80,replace(replace('<p class="gatha1">Dujjano <a name="M0.0080"></a> jīyate yutyā,</p>\r\n<p class="gatha2">Niggahena nadhīmatā;</p>\r\n<p class="gatha3">Nipātyate mahārukkho,</p>\r\n<p class="gathalast">Tassamīpa khatikkhayā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',83,replace(replace('<p class="gatha1">Vane <a name="M0.0083"></a> migāca luddhānaṃ,</p>\r\n<p class="gatha2">Dujjanānañca sajjanā;</p>\r\n<p class="gatha3">Akāraṇaverī honti,</p>\r\n<p class="gathalast">Tiṇabhakkhā supesalā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',86,replace(replace('<p class="gatha1">Pādalaggaṃ <a name="M0.0086"></a> karaṭṭhena,</p>\r\n<p class="gathalast">Kaṇḍakeneva kaṇḍakaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',87,replace(replace('<p class="gatha1">Bālaṃ <a name="M0.0087"></a> napasse nasuṇe,</p>\r\n<p class="gatha2">Nacabālena saṃvase;</p>\r\n<p class="gatha3">Bālenāllāpasallāpaṃ,</p>\r\n<p class="gathalast">Nakare nacarocaye.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> Upa kattuṃ yathā khuddo,</p>\r\n<p class="gatha2">Samattho natathāmahā;</p>\r\n<p class="gatha3">Kūpo hi hanti pipāsaṃ,</p>\r\n<p class="gathalast">Natu pāyo mahambudhi.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',95,replace(replace('<p class="gatha1">Ādānassa <a name="M0.0095"></a> padānassa,</p>\r\n<p class="gatha2">Kattabbassaca kammuno;</p>\r\n<p class="gatha3">Khippaṃ akaramānassa,</p>\r\n<p class="gathalast">Kālo bhakkhati taṃ rasaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',103,replace(replace('<p class="gatha1">Nakkhattaṃ <a name="M0.0103"></a> paṭimānentaṃ,</p>\r\n<p class="gatha2">Attho bālaṃ upajjhagā;</p>\r\n<p class="gatha3">Attho atthassa nakkhattaṃ,</p>\r\n<p class="gathalast">Kiṃ karissanti tārakā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',105,replace(replace('<p class="gatha1">Ajarāmarova <a name="M0.0105"></a> pañño,</p>\r\n<p class="gatha2">Vijjamatthañca cintaye;</p>\r\n<p class="gatha3">Gahitoviya kesesu,</p>\r\n<p class="gathalast">Maccunā dhammamācare.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> Vajjā gurūca mantīca,</p>\r\n<p class="gatha2">Tayo raṭṭhābhisaṅkhatā;</p>\r\n<p class="gatha3">Jīvīta dakkha kosānaṃ,</p>\r\n<p class="gathalast">Vaḍḍhanā nāsanāca te.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',117,replace(replace('<p class="gatha1">Thirena <a name="M0.0117"></a> kammaṃ vaḍḍhati,</p>\r\n<p class="gatha2">Athirena turena no;</p>\r\n<p class="gatha3">Phalanti samaye rukkhā,</p>\r\n<p class="gathalast">Sittāpi bahuvārinā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',120,replace(replace('<p class="gatha1">Vāyāmetheva <a name="M0.0120"></a> puriso,</p>\r\n<p class="gathalast">Nanibbindeyya paṇḍito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',122,replace(replace('<p class="gatha1">Payatano <a name="M0.0122"></a> tādiso neva,</p>\r\n<p class="gatha2">Kayyo yena phalaṃ nahi;</p>\r\n<p class="gatha3">Selagge kūpakhaṇanā,</p>\r\n<p class="gathalast">Kathaṃ toyasamāgamo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',123,replace(replace('<p class="gatha1">Ñāṇaṅkusena <a name="M0.0123"></a> sammaggaṃ,</p>\r\n<p class="gathalast">Niyyatyussāhakuñjaro.</p>\r\n<p class="gatha1">Asamekkhitakammantaṃ,</p>\r\n<p class="gatha2">Turitābhi nipātinaṃ;</p>\r\n<p class="gatha3">Tānikammāni tappenti,</p>\r\n<p class="gathalast">Uṇhaṃ va jjhohitaṃ mukhe.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',126,replace(replace('<p class="gatha1">Chaddosā <a name="M0.0126"></a> puriseneha,</p>\r\n<p class="gatha2">Hātabbā bhūtimicchantā;</p>\r\n<p class="gatha3">Niddā majjaṃ bhayaṃ kodho,</p>\r\n<p class="gathalast">Ālasyaṃ dīghasuttatā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',130,replace(replace('<p class="gatha1">Na <a name="M0.0130"></a> divā suppasīlena,</p>\r\n<p class="gatha2">Rattimuṭṭhānadessinā;</p>\r\n<p class="gatha3">Niccasoṇḍena mattena,</p>\r\n<p class="gathalast">Sakkā āvasituṃ gharaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',137,replace(replace('<p class="gatha1">Abhetabbamhi <a name="M0.0137"></a> bhāyanti,</p>\r\n<p class="gatha2">Bhāyitabbe nabhāyare;</p>\r\n<p class="gatha3">Bhayābhaya vimuḷhā te,</p>\r\n<p class="gathalast">Jimhānugā ujuñjahā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',139,replace(replace('<p class="gatha1">Yassa <a name="M0.0139"></a> manussabhūtassa,</p>\r\n<p class="gatha2">Natthi bhogāca sippakaṃ;</p>\r\n<p class="gatha3">Kiṃ phalaṃ tassa mānussaṃ,</p>\r\n<p class="gathalast">Dvipādaṭṭho hi so migo.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',142,replace(replace('<p class="gatha1">Nānopāyova <a name="M0.0142"></a> kattabbo,</p>\r\n<p class="gatha2">Sace bhaveyya attano;</p>\r\n<p class="gatha3">Atthasiddhi yathākāmaṃ,</p>\r\n<p class="gathalast">Upāyo hi hitañjaso.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',150,replace(replace('<p class="gatha1">Lañjadānabālisena<a name="M0.0150"></a>,</p>\r\n<p class="gatha2">Kūṭaḍḍakāradhīvarā;</p>\r\n<p class="gatha3">Vinicchayamahāmacchaṃ,</p>\r\n<p class="gathalast">Oṭṭenti lobhasāgare.</p>\r\n<p class="gatha1">Yassete caturo dhammā,</p>\r\n<p class="gatha2">Vānarinda yathātava;</p>\r\n<p class="gatha3">Saccaṃ dhammo dhīti cāgo,</p>\r\n<p class="gathalast">Diṭṭhaṃ so ativattati.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',162,replace(replace('<p class="gatha1">Vidvāca <a name="M0.0162"></a> ratanaṃ nārī,</p>\r\n<p class="gatha2">Vīṇā sātthaṃ giraṃmahī;</p>\r\n<p class="gatha3">Guṇavisesa māgamma,</p>\r\n<p class="gathalast">Guṇāni aguṇānica.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',164,replace(replace('<p class="gatha1">Dhanavā <a name="M0.0164"></a> balavā loke,</p>\r\n<p class="gathalast">Dhanā bhavati paṇḍito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',165,replace(replace('<p class="gatha1">Sumane <a name="M0.0165"></a> nissito kīṭo,</p>\r\n<p class="gatha2">Nigguṇo hīnako sayaṃ;</p>\r\n<p class="gatha3">Taṃ pupphehi maṇḍentānaṃ,</p>\r\n<p class="gathalast">Raññaṃ siropi rohati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',168,replace(replace('<p class="gatha1">Alakkhikehi <a name="M0.0168"></a> sañcītā,</p>\r\n<p class="gatha2">Dhanabhogāca cintitā;</p>\r\n<p class="gatha3">Lakkhikassa bhavantete,</p>\r\n<p class="gathalast">Lakkhivā suṭṭhubhuñjati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',171,replace(replace('<p class="gatha1">Khattiyo <a name="M0.0171"></a> seṭṭho jane tasmiṃ,</p>\r\n<p class="gatha2">Yo gottapaṭisārino;</p>\r\n<p class="gatha3">Vijjācaraṇasampanno,</p>\r\n<p class="gathalast">So seṭṭho devamānuse.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',182,replace(replace('<p class="gatha1">Visāpi <a name="M0.0182"></a> amataṃ gaṇhe,</p>\r\n<p class="gatha2">Gūthato maṇimuttamaṃ;</p>\r\n<p class="gatha3">Kaṇṭakapādapā pupphaṃ,</p>\r\n<p class="gathalast">Thirataṃ dukkulā varaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',189,replace(replace('<p class="gatha1">Dhanissarādiguṇommi <a name="M0.0189"></a>-</p>\r\n<p class="gathalast">Vegena vāhitā pajā.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> Yassa tthi satataṃ mettā,</p>\r\n<p class="gatha2">Sabbalokasuvallabhā;</p>\r\n<p class="gatha3">Kūpāyate samuddopi,</p>\r\n<p class="gathalast">Aggi tassa jalāyate.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> Sakkharāyati merūpi,</p>\r\n<p class="gatha2">Visabhakkho sudhāyate;</p>\r\n<p class="gatha3">Sasāyate migarāja,</p>\r\n<p class="gatha2">Byālo mālāguṇāyate;</p>\r\n<p class="gatha3">Dolāyate chamācālo,</p>\r\n<p class="gathalast">Nānāvudhā tiṇāyare.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-20-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',202,replace(replace('<p class="gatha1">Sameva <a name="M0.0202"></a> sati ussāhe,</p>\r\n<p class="gatha2">Sukhavāho hitaṅkaro;</p>\r\n<p class="gatha3">Ūne-dhike tathā nohi,</p>\r\n<p class="gathalast">Majjhago sādhu sabbadā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1002n',208,replace(replace('<p class="gatha1">Sādhu <a name="M0.0208"></a> kho paṇḍitonāma,</p>\r\n<p class="gathalast">Natveva atipaṇḍito.</p>','\r',char(13)),'\n',char(10)),'');
