INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="book">Naradakkhadīpanī</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',8,replace(replace('<p class="gatha1">Pālito <a name="M0.0008"></a> yeva saddhammo,</p>\r\n<p class="gatha2">Pālitena varena ca;</p>\r\n<p class="gatha3">Pālite suṭṭhu yaṃ sīlaṃ,</p>\r\n<p class="gathalast">Pālitaṃ dhamma-sundaraṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',10,replace(replace('<p class="gatha1">Sammā <a name="M0.0010"></a> āraddhaṃ sabba-sampattīnaṃ,</p>\r\n<p class="gathalast">Mūlaṃ hotīti daṭṭhabbaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',11,replace(replace('<p class="gatha1">Vīriyārabbho <a name="M0.0011"></a> bhikkhave,</p>\r\n<p class="gathalast">Mahato atthāya saṃvattatīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',12,replace(replace('<p class="gatha1">Tasmā <a name="M0.0012"></a> vīriyameva kattabbaṃ,</p>\r\n<p class="gathalast">Vīriyavato hi acintitaṃpi hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',21,replace(replace('<p class="gatha1">Yathā <a name="M0.0021"></a> hi tacchakānaṃ suttaṃ,</p>\r\n<p class="gatha2">Pamāṇaṃ hoti;</p>\r\n<p class="gathalast">Eva metampi viññūnaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',24,replace(replace('<p class="gatha1">Suvijāno <a name="M0.0024"></a> bhavaṃ hoti,</p>\r\n<p class="gatha2">Dubbijāno parābhavo;</p>\r\n<p class="gatha3">Dhammakāmo bhavaṃ hoti,</p>\r\n<p class="gathalast">Dhammadessī parābhavo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',26,replace(replace('<p class="gatha1">Ayaṃ <a name="M0.0026"></a> dhammatāti ayaṃ sabhāvo,</p>\r\n<p class="gathalast">Ayaṃ niyāmoti vuttaṃ hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',28,replace(replace('<p class="gatha1">Ye <a name="M0.0028"></a> dhammā hetupabhavā,</p>\r\n<p class="gathalast">Tesaṃ hetuṃ tathāgato.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',35,replace(replace('<p class="gatha1">Ratanattayaṃ<a name="M0.0035"></a>, santataṃ, ahaṃ vandāmi;</p>\r\n<p class="gatha2">Ācariyaṃ, so ahaṃ, niccaṃ namāmi;</p>\r\n<p class="gathalast">‘‘Hotu sabbaṃ, maṅgalaṃ, mamaṃ sabbadhi’’.</p>\r\n<p class="title">Naradakkhadīpanī</p>\r\n<p class="centered">Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="chapter">Paṇāma</p>\r\n<p class="chapter">Naradakkhadīpakassa,</p>\r\n<p class="gatha1">Namo samantacakkhuno;</p>\r\n<p class="gatha2">Saṃsārā vippamuttassa,</p>\r\n<p class="gatha3">Saṃsārā vippamuttassa,</p>\r\n<p class="gathalast">Sa-saddhammassa saṅghino.</p>\r\n<p class="chapter">Matthanā</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',36,replace(replace('<p class="gatha1">Pālitehi <a name="M0.0036"></a> varuttamaṃ,</p>\r\n<p class="gatha2">Pālitaṃ sīla-pāramiṃ;</p>\r\n<p class="gatha3">Pāletu varasambuddho,</p>\r\n<p class="gathalast">Pālitaṃ gantha-kāraṇaṃ.</p>\r\n<p class="chapter">Āsīsa</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',37,replace(replace('<p class="gatha1">Bahussuto <a name="M0.0037"></a> ca medhāvī,</p>\r\n<p class="gatha2">Sīlesuca samāhito;</p>\r\n<p class="gatha3">Cetosamathānuyutto,</p>\r\n<p class="gathalast">Api muddhani tiṭṭhatu.</p>\r\n<p class="chapter">Abhiyācaka</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',38,replace(replace('<p class="gatha1">Dhamma-saṅghaṃ <a name="M0.0038"></a> vanditvāna,</p>\r\n<p class="gatha2">Sabba-lokassa nāyakaṃ;</p>\r\n<p class="gatha3">Yācito tikakrapa-therena,</p>\r\n<p class="gathalast">Māṇavena ca dhīmatā.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Uttānameva saṅkhepaṃ,</p>\r\n<p class="gatha2">Nānā-sattha-sudhāritaṃ;</p>\r\n<p class="gatha3">Nara-dakkhaṃ likhissami,</p>\r\n<p class="gathalast">Passantu dhīra-māmakā.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',41,replace(replace('<p class="gatha1">Kosajjaṃ <a name="M0.0041"></a> bhayato disvā,</p>\r\n<p class="gatha2">Vīriyañjāpi khemato;</p>\r\n<p class="gatha3">Āraddhavīriyā hotha;</p>\r\n<p class="gathalast">Esā buddhānusāsanī.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',42,replace(replace('<p class="gatha1">Vīriyavā <a name="M0.0042"></a> kho bhikkhave ariyasāvako,</p>\r\n<p class="gatha2">Akusalaṃ pajahati, kusalaṃ bhāveti;</p>\r\n<p class="gatha3">Sāvajjaṃ pajahati, anavajjaṃ bhāveti,</p>\r\n<p class="gathalast">Suddhamattānaṃ pariharatīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',43,replace(replace('<p class="gatha1">Vīriyavato <a name="M0.0043"></a> kiṃ nāma kammaṃ na sijjhati;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',44,replace(replace('<p class="gatha2">Purisakāro <a name="M0.0044"></a> nāma na nassati,</p>\r\n<p class="gathalast">Sukhe patiṭṭhāpetīti jānāmi.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',46,replace(replace('<p class="gatha1">Yathā <a name="M0.0046"></a> khittaṃ nabhe leḍḍu,</p>\r\n<p class="gatha2">Dhuvaṃ patati bhūmiyaṃ;</p>\r\n<p class="gatha3">Tatheva buddha-seṭṭhānaṃ,</p>\r\n<p class="gathalast">Vacanaṃ dhuva-sassataṃ.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',48,replace(replace('<p class="gatha1">A-dvejjhavacanā <a name="M0.0048"></a> buddhā,</p>\r\n<p class="gathalast">A-moghavacanā jinā.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',49,replace(replace('<p class="gatha1">Sussusā <a name="M0.0049"></a> labhate paññaṃ,</p>\r\n<p class="gatha2">Uṭṭhātā vindate dhanaṃ;</p>\r\n<p class="gatha3">Tasmā pāḷiṃ guruṃ katvā,</p>\r\n<p class="gathalast">Imaṃ passāhi sobhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',51,replace(replace('<p class="gatha1">Sussusā <a name="M0.0051"></a> suta-buddhinī,</p>\r\n<p class="gatha2">Sutaṃ paññāya vaḍḍhanaṃ;</p>\r\n<p class="gatha3">Paññāya atthaṃ jānāti,</p>\r\n<p class="gathalast">Ñāto attho sukhāvaho.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',52,replace(replace('<p class="gatha1">Satataṃjjhāyanaṃ <a name="M0.0052"></a> vāda,</p>\r\n<p class="gatha2">Para-tantavalokanaṃ;</p>\r\n<p class="gatha3">Sabbijjācera-sevāca,</p>\r\n<p class="gathalast">Buddhi-mati-karo guṇo.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',53,replace(replace('<p class="gatha1">Ati-dīghova <a name="M0.0053"></a> nīgho hi,</p>\r\n<p class="gatha2">Kusīto hīna-vīriyo;</p>\r\n<p class="gatha3">Tasmā vīriyaṃ katvāna,</p>\r\n<p class="gathalast">Vijjaṃ esantu sādhavo.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',55,replace(replace('<p class="gatha1">Suporiso <a name="M0.0055"></a> tāva sippaṃ,</p>\r\n<p class="gatha2">Uggaṇheyya paraṃ dhanaṃ;</p>\r\n<p class="gatha3">Gaveseyya tato mantaṃ,</p>\r\n<p class="gathalast">Katheyya sacca-bhāsitaṃ.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',56,replace(replace('<p class="gatha1">Pathamaṃ <a name="M0.0056"></a> na parājaye sippaṃ,</p>\r\n<p class="gatha2">Dutīyaṃ na parājaye dhanaṃ;</p>\r\n<p class="gatha3">Tatīyaṃ na parājaye dhanaṃ,</p>\r\n<p class="gathalast">Catutthamatthaṃ kiṃ karissati.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',58,replace(replace('<p class="gatha1">Sobhanti <a name="M0.0058"></a> a-milātāni,</p>\r\n<p class="gatha2">Pupphāniva pilandhituṃ;</p>\r\n<p class="gatha3">Tathā sobhanti dārakā,</p>\r\n<p class="gathalast">Yobbaneyeva sikkhituṃ.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',59,replace(replace('<p class="gatha1">Tasmā <a name="M0.0059"></a> have guṇādhāraṃ,</p>\r\n<p class="gatha2">Paññā-vaḍḍhanamuttamaṃ;</p>\r\n<p class="gatha3">Sikkheyya matimā poso,</p>\r\n<p class="gathalast">Patthento hitamattano.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',60,replace(replace('<p class="gatha1">Alaṃ <a name="M0.0060"></a> vāyamituṃ sippe,</p>\r\n<p class="gatha2">Attha-kāmena jantunā;</p>\r\n<p class="gatha3">Kataṃ vijaññā vijjādi,</p>\r\n<p class="gathalast">Vayo te mā upajjhagā.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',62,replace(replace('<p class="gatha1">Vijjaṃ <a name="M0.0062"></a> sikkhe, care sīlaṃ,</p>\r\n<p class="gatha2">Dhīrena saha saṃvase;</p>\r\n<p class="gatha3">Dhanācaye, kare kammaṃ,</p>\r\n<p class="gathalast">Piyaṃ vācañca saṃvade.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',64,replace(replace('<p class="gatha1">Na <a name="M0.0064"></a> tveva supituṃ hoti,</p>\r\n<p class="gatha2">Ratti nakkhatta-mālinī;</p>\r\n<p class="gatha3">Paṭijaggitumevesā,</p>\r\n<p class="gathalast">Ratti hoti vijānataṃ.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',65,replace(replace('<p class="gatha1">Uṭṭhāhatha <a name="M0.0065"></a> nisīdatha,</p>\r\n<p class="gatha2">Ko attho supitena vo;</p>\r\n<p class="gatha3">Sādhu kho sippa-vijjāhvā,</p>\r\n<p class="gathalast">Vijjaṃ sikkhatha santataṃ.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',67,replace(replace('<p class="gatha1">Ārabbhatha <a name="M0.0067"></a> sadā puttā,</p>\r\n<p class="gatha2">Bahussutaṃ gavesituṃ;</p>\r\n<p class="gatha3">Yasmā loke sippavantā,</p>\r\n<p class="gathalast">Sabbā-disāsu pākaṭā.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',68,replace(replace('<p class="gatha1">Sakyarūpaṃ <a name="M0.0068"></a> pure santaṃ,</p>\r\n<p class="gatha2">Mayā sippaṃ na sikkhitaṃ;</p>\r\n<p class="gatha3">Kicchā vutti a-sippassa,</p>\r\n<p class="gathalast">Iti pacchā nutappati.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',75,replace(replace('<p class="gatha1">Lokatthaṃ <a name="M0.0075"></a> loka-kammantaṃ,</p>\r\n<p class="gatha2">Icchanto pariyesituṃ;</p>\r\n<p class="gatha3">Niccameva vīriyañca,</p>\r\n<p class="gathalast">Atthaṃ mantañja cintaye.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',76,replace(replace('<p class="gatha1">Dhanavā <a name="M0.0076"></a> guṇavā loke,</p>\r\n<p class="gatha2">Sabbā-disāya pākaṭo;</p>\r\n<p class="gatha3">Sīlavā paññavā macco,</p>\r\n<p class="gathalast">Sabba-lokehi pūjito.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-26-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',77,replace(replace('<p class="gatha1">Sajīvati <a name="M0.0077"></a> yaso yassa,</p>\r\n<p class="gatha2">Kitti yassa sajīvati;</p>\r\n<p class="gatha3">Yasa-kitti vihīnassa,</p>\r\n<p class="gathalast">Jīvantopi matopamā.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',79,replace(replace('<p class="gatha1">Saddhīdha <a name="M0.0079"></a> vittaṃ purisassa seṭṭhaṃ,</p>\r\n<p class="gatha2">Dhammo suciṇṇo sukhamāvahāti;</p>\r\n<p class="gatha3">Saccaṃ have sādutaraṃ rasānaṃ,</p>\r\n<p class="gathalast">Paññājīviṃ jīvitamāhu seṭṭhanti.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',81,replace(replace('<p class="gatha1">Satimato <a name="M0.0081"></a> sadā bhaddaṃ,</p>\r\n<p class="gatha2">Satimā sukhamedhati;</p>\r\n<p class="gatha3">Satimato suve seyyo,</p>\r\n<p class="gathalast">Verā ca parimuccati.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',83,replace(replace('<p class="gatha1">Mā <a name="M0.0083"></a> vo khaṇaṃ virādhetha,</p>\r\n<p class="gatha2">Khaṇātītā hi socare;</p>\r\n<p class="gatha3">Sadatthe vāyameyyātha,</p>\r\n<p class="gathalast">Khaṇo vo paṭipādito.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',84,replace(replace('<p class="gatha1">Yathicchitaṃ <a name="M0.0084"></a> na pappoti,</p>\r\n<p class="gatha2">A-phiyo nāvikoṇṇave;</p>\r\n<p class="gatha3">Tathevāvīriyopettha,</p>\r\n<p class="gathalast">Tasmārabheyya sāsanaṃ.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',87,replace(replace('<p class="gatha1">Vāyametheva <a name="M0.0087"></a> puriso,</p>\r\n<p class="gatha2">Yāva atthassa nipphadā;</p>\r\n<p class="gatha3">Nipphannasobhaṇo attho,</p>\r\n<p class="gathalast">Khantā bhiyyo na vijjati.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',88,replace(replace('<p class="gatha1">Sameva <a name="M0.0088"></a> ñāṇa-vāyāme,</p>\r\n<p class="gatha2">Sukhāvaho su-maṅgalo;</p>\r\n<p class="gatha3">Ñunedhike tathā no hi,</p>\r\n<p class="gathalast">Dvayena sādhu sampadā.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',90,replace(replace('<p class="gatha1">Kāya-kammāni <a name="M0.0090"></a> sijjhanti,</p>\r\n<p class="gatha2">Vacī-kammāni vīriyaṃ;</p>\r\n<p class="gatha3">Na hi kiccāni cintāhi,</p>\r\n<p class="gathalast">Kareyyāthīdha vāyamaṃ.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-34-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',91,replace(replace('<p class="gatha1">Paṭikacceva <a name="M0.0091"></a> kareyya,</p>\r\n<p class="gatha2">Taṃ jaññā hitamattano;</p>\r\n<p class="gatha3">Na sākaṭikacintāya,</p>\r\n<p class="gathalast">Mandā dhīro parakkame.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',95,replace(replace('<p class="gatha1">Thirena <a name="M0.0095"></a> saka-kammena,</p>\r\n<p class="gatha2">Vaḍḍhatiyeva saṃ phalaṃ;</p>\r\n<p class="gatha3">A-thirena alasena,</p>\r\n<p class="gathalast">Kara-kammaṃ phalañca no.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-36-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',96,replace(replace('<p class="gatha1">Āsīsetheva <a name="M0.0096"></a> puriso,</p>\r\n<p class="gatha2">Na nibbindeyya paṇḍito;</p>\r\n<p class="gatha3">Passāmi vohaṃ attānaṃ,</p>\r\n<p class="gathalast">Yathā icchiṃ tathā ahu.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',98,replace(replace('<p class="gatha1">Aṇaṇo <a name="M0.0098"></a> ñātīnaṃ hoti,</p>\r\n<p class="gatha2">Devānaṃ pitunañca so;</p>\r\n<p class="gatha3">Karaṃ purisa-kiccāni,</p>\r\n<p class="gathalast">Na ca pacchānutappati.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',99,replace(replace('<p class="gatha1">Hiyyoti <a name="M0.0099"></a> hiyyati poso,</p>\r\n<p class="gatha2">Karissāmi pareti yo;</p>\r\n<p class="gatha3">Ajja kattabba kammaṃ sve,</p>\r\n<p class="gathalast">So tato parihāyati.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',101,replace(replace('<p class="gatha1">So <a name="M0.0101"></a> a-ppamatto a-kuddho,</p>\r\n<p class="gatha2">Tāta kiccāni kāraya;</p>\r\n<p class="gatha3">Vāyāmassu sa-kiccesu,</p>\r\n<p class="gathalast">Nālaso vindate sukhaṃ.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',102,replace(replace('<p class="gatha1">Hīna-jaccopi <a name="M0.0102"></a> ce hoti,</p>\r\n<p class="gatha2">Uṭṭhātā dhitimā naro;</p>\r\n<p class="gatha3">Ācāra-sīla-sampanno,</p>\r\n<p class="gathalast">Nise aggīva bhāsati.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',104,replace(replace('<p class="gatha1">Yo <a name="M0.0104"></a> pubbe karaṇīyāni,</p>\r\n<p class="gatha2">Pacchā so kātumicchati;</p>\r\n<p class="gatha3">Varuṇakaṭṭhabhañjova,</p>\r\n<p class="gathalast">Sa pacchā anutappati.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',105,replace(replace('<p class="gatha1">Uṭṭhānakālamhi <a name="M0.0105"></a> anuṭṭhahāno,</p>\r\n<p class="gatha2">Yuvā balī ālasiyaṃ upeto;</p>\r\n<p class="gatha3">A-puṇṇasaṅkappamano kusīto,</p>\r\n<p class="gathalast">Paññāya maggaṃ alaso na vindati.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',107,replace(replace('<p class="gatha1">Dummedho <a name="M0.0107"></a> puriso loke,</p>\r\n<p class="gatha2">Kusīto hīna-vīriyo;</p>\r\n<p class="gatha3">Appassuto anācāro,</p>\r\n<p class="gathalast">Parihāyati vuḍḍhiyā.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-44-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',108,replace(replace('<p class="gatha1">Appassutāyaṃ <a name="M0.0108"></a> puriso,</p>\r\n<p class="gatha2">Balībaddova jīrati;</p>\r\n<p class="gatha3">Maṃsāni tassa vaḍḍhanti,</p>\r\n<p class="gathalast">Paññā tassa na vaḍḍhati.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',109,replace(replace('<p class="gatha1">Yassa <a name="M0.0109"></a> manussa-bhūtassa,</p>\r\n<p class="gatha2">Natthi bhogā ca sippakaṃ;</p>\r\n<p class="gatha3">Kiṃphalaṃ tassa mānussaṃ,</p>\r\n<p class="gathalast">Dvipādaṭṭho hi so migo.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',111,replace(replace('<p class="gatha1">Yo <a name="M0.0111"></a> ca vassasataṃ jīve,</p>\r\n<p class="gatha2">Kusīto hīna-vīriyo;</p>\r\n<p class="gatha3">Ekāhaṃ jīvitaṃ seyyo,</p>\r\n<p class="gathalast">Vīriyārabbhato daḷaṃ.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-47-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',112,replace(replace('<p class="gatha1">Yo <a name="M0.0112"></a> ca dhamma-vibhaṅgaññū,</p>\r\n<p class="gatha2">Kāluṭṭhāyī a-tandito;</p>\r\n<p class="gatha3">Anuṭṭhahati kālena,</p>\r\n<p class="gathalast">Phalaṃ tassa samijjhati.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',126,replace(replace('<p class="gatha1">A-caritvā <a name="M0.0126"></a> brahmacariyaṃ,</p>\r\n<p class="gatha2">A-laddhā yobbane dhanaṃ;</p>\r\n<p class="gatha3">Jiṇṇakoñcāva jhāyanti,</p>\r\n<p class="gathalast">Khīṇamaccheva pallale.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-49-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',127,replace(replace('<p class="gatha1">A-caritvā <a name="M0.0127"></a> brahmacariyaṃ,</p>\r\n<p class="gatha2">A-laddhā yobbane dhanaṃ;</p>\r\n<p class="gatha3">Sentti cāpātikhīṇāva,</p>\r\n<p class="gathalast">Purāṇāni anutthunaṃ.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-50-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',132,replace(replace('<p class="gatha1">Appakenāpi <a name="M0.0132"></a> medhāvī,</p>\r\n<p class="gatha2">Pābhatena vicakkhaṇo;</p>\r\n<p class="gatha3">Samuṭṭhāpeti attānaṃ,</p>\r\n<p class="gathalast">Aṇuṃ aggiṃva sandhamaṃ.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-51-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',138,replace(replace('<p class="gatha1">Vāyāmetheva <a name="M0.0138"></a> puriso,</p>\r\n<p class="gatha2">Na nibbindeyya paṇḍito;</p>\r\n<p class="gatha3">Vāyāmassa phalaṃ passa,</p>\r\n<p class="gathalast">Bhuttā ambā anītihaṃ.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',140,replace(replace('<p class="gatha1">Anuṭṭhahaṃ <a name="M0.0140"></a> a-vāyāmaṃ,</p>\r\n<p class="gatha2">Sukhaṃ yatrādhi gacchati;</p>\r\n<p class="gatha3">Suvira tattha gacchāhi,</p>\r\n<p class="gathalast">Mañca tattheva pāpaya.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-53-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',142,replace(replace('<p class="gatha1">Yatthālaso <a name="M0.0142"></a> anuṭṭhātā,</p>\r\n<p class="gatha2">Accantaṃ sukhamedhati;</p>\r\n<p class="gatha3">Suvira tattha gacchāhi,</p>\r\n<p class="gathalast">Mañca ttattheva pāpaya.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-54-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',143,replace(replace('<p class="gatha1">Adhippāya-phalaṃ <a name="M0.0143"></a> loke,</p>\r\n<p class="gatha2">Dhītimanttassa sijjhati;</p>\r\n<p class="gatha3">Vīriyameva kattabbaṃ,</p>\r\n<p class="gathalast">Etaṃ buddhehi vaṇṇitaṃ.</p>\r\n<p class="chapter">Paññā-niddesa</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-55-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',144,replace(replace('<p class="gatha1">Paññaṃ <a name="M0.0144"></a> pathamamesehi,</p>\r\n<p class="gatha2">Paññā-balaṃ bahuttamaṃ;</p>\r\n<p class="gatha3">Kula-putta balaṃ paññā,</p>\r\n<p class="gathalast">Kiṃhināma na sādhyati.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-56-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',146,replace(replace('<p class="gatha1">Aneka-saṃsayucchedi<a name="M0.0146"></a>,</p>\r\n<p class="gatha2">Parokkhatthassa dassakaṃ;</p>\r\n<p class="gatha3">Sabbassa locanaṃ satthaṃ,</p>\r\n<p class="gathalast">Yassa natthyandhameva so.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-57-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',148,replace(replace('<p class="gatha1">Paññā <a name="M0.0148"></a> sutaṃ vinicchindi,</p>\r\n<p class="gatha2">Kitti-siloka-vaḍḍhanī;</p>\r\n<p class="gatha3">Paññāsahito naro idha,</p>\r\n<p class="gathalast">Api sukhānivindati.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-58-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',149,replace(replace('<p class="gatha1">Sabbaññubuddha-pacceka<a name="M0.0149"></a>,</p>\r\n<p class="gatha2">Catusacca-sutā iti;</p>\r\n<p class="gatha3">Catu-buddhesu ekova,</p>\r\n<p class="gathalast">Bahussuto naro bhave.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-59-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',150,replace(replace('<p class="gatha1">Lekhacheko <a name="M0.0150"></a> vācacheko,</p>\r\n<p class="gatha2">Ganthacheko suvācako;</p>\r\n<p class="gatha3">Vidhāyakacheko sūro,</p>\r\n<p class="gathalast">Niddukkhova sakammani.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-60-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',152,replace(replace('<p class="gatha1">Paññā <a name="M0.0152"></a> hi seṭṭhā kusalā vadanti,</p>\r\n<p class="gatha2">Nakkhatta-rājāriva tārakānaṃ;</p>\r\n<p class="gatha3">Sīlaṃ sirīcāpi satañca dhammo,</p>\r\n<p class="gathalast">Anvāyikā paññāvato bhavanti.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-61-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',154,replace(replace('<p class="gatha1">Sevetha <a name="M0.0154"></a> buddhe nipuṇe bahussutte,</p>\r\n<p class="gatha2">Uggāhako ca paripucchako;</p>\r\n<p class="gatha3">Suṇeyya sakkaccaṃ subhāsitāni,</p>\r\n<p class="gathalast">Evaṃ karo paññavā hoti macco.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-62-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',156,replace(replace('<p class="gatha1">Vayena <a name="M0.0156"></a> yasa-pucchāhi,</p>\r\n<p class="gatha2">Tiṭṭha-vāsena yoniso;</p>\r\n<p class="gatha3">Sākacchā snehasaṃsevā,</p>\r\n<p class="gathalast">Patirūpa-vāsenaca.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> Etāni aṭṭhaṭhānāni,</p>\r\n<p class="gatha2">Buddhi-visada-kāraṇā;</p>\r\n<p class="gatha3">Yesaṃ etāni sambhonti,</p>\r\n<p class="gathalast">Tesaṃ buddhi pabhijjati.</p>','\r',char(13)),'\n',char(10)),'-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',158,replace(replace('<p class="bodytext"><a name="para64"></a><span class="paranum">64</span>. Cattārome <a name="M0.0158"></a> bhikkhave dhammā paññāvuddhiyā saṃvattanti. Katame cattāro. Sappurisasaṃsevo saddhammasavanaṃ yoniso manasikāro dhammānudhamma-paṭipatti. Ime kho bhikkhave cattāro dhammā paññā-vuddhiyā saṃvattantītti.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-64-65-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',160,replace(replace('<p class="gatha1">Cakkhupasāda-sampanno<a name="M0.0160"></a>,</p>\r\n<p class="gatha2">Acchimantañca passati;</p>\r\n<p class="gatha3">Andhaṃ kāṇaṃ su-passantaṃ,</p>\r\n<p class="gathalast">Andho sabbaṃ na passati.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-66-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',161,replace(replace('<p class="gatha1">Passatti <a name="M0.0161"></a> passo passantaṃ,</p>\r\n<p class="gatha2">A-passantañca passati;</p>\r\n<p class="gatha3">A-passanto a-passantaṃ,</p>\r\n<p class="gathalast">Passantañca na passati.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-67-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',162,replace(replace('<p class="gatha1">Pākaṭaṃ <a name="M0.0162"></a> a-paṭicchannaṃ,</p>\r\n<p class="gatha2">Rūpaṃ pasāda-cakkhunā;</p>\r\n<p class="gatha3">Nāññaṃ passatti sabbaṃpi,</p>\r\n<p class="gathalast">Tathato ñāṇa-cakkhunā.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-68-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',164,replace(replace('<p class="gatha1">Sujanāsujanā <a name="M0.0164"></a> sabbe,</p>\r\n<p class="gatha2">Guṇenāpi vivekino;</p>\r\n<p class="gatha3">Vivekaṃ na samāyanti,</p>\r\n<p class="gathalast">A-vivekījanantike.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-69-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',165,replace(replace('<p class="gatha1">Yo <a name="M0.0165"></a> ca uppatitaṃ atthaṃ,</p>\r\n<p class="gatha2">Na khippamanubujjhati;</p>\r\n<p class="gatha3">A-mittavasamanveti,</p>\r\n<p class="gathalast">Pacchā ca anutappati.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-70-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',166,replace(replace('<p class="gatha1">Evaṃ <a name="M0.0166"></a> mahiddhikā paññā,</p>\r\n<p class="gatha2">Nipuṇā sādhucintinī;</p>\r\n<p class="gatha3">Diṭṭhadhamma-hitatthāya,</p>\r\n<p class="gathalast">Samparāya-sukhāya vā.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',168,replace(replace('<p class="gatha1">Taṃ <a name="M0.0168"></a> balānaṃ balaṃ seṭṭha,</p>\r\n<p class="gatha2">Agga paññābalaṃ balaṃ;</p>\r\n<p class="gatha3">Paññābalenupatthaddho,</p>\r\n<p class="gathalast">Etthaṃ vindati paṇḍitto.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-72-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',170,replace(replace('<p class="gatha1">Yena <a name="M0.0170"></a> ñāṇena bujjhanti,</p>\r\n<p class="gatha2">Ariyā kata-kiccataṃ;</p>\r\n<p class="gatha3">Taṃ ñāṇa-ratanaṃ laddhaṃ,</p>\r\n<p class="gathalast">Vāyāmetha jinorasā.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',171,replace(replace('<p class="gatha1">Paññāratanamālassa<a name="M0.0171"></a>,</p>\r\n<p class="gatha2">Na ciraṃ vattate bhavo;</p>\r\n<p class="gatha3">Khippaṃ dasseti amataṃ,</p>\r\n<p class="gathalast">Na ca so rocate bhave.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-74-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',173,replace(replace('<p class="gatha1">Pamādamanuyuñjanti<a name="M0.0173"></a>,</p>\r\n<p class="gatha2">Bālā dummedhino janā;</p>\r\n<p class="gatha3">Appamādañca medhāvī,</p>\r\n<p class="gathalast">Dhanaṃ seṭṭhaṃva rakkhati.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-75-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',174,replace(replace('<p class="gatha1">Dhana-puñña-dhī-lābhena<a name="M0.0174"></a>,</p>\r\n<p class="gatha2">Kālaṃ khiyyati paṇḍito;</p>\r\n<p class="gatha3">Kīḷanena ca dummedho,</p>\r\n<p class="gathalast">Niddāya kalahena vā.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-76-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',176,replace(replace('<p class="gatha1">Pamādaṃ <a name="M0.0176"></a> appamādena,</p>\r\n<p class="gatha2">Yadā nūdati paṇḍito;</p>\r\n<p class="gatha3">Paññāpāsāda-māruyha,</p>\r\n<p class="gatha2">A-soko sokiniṃ pajaṃ,</p>\r\n<p class="gatha3">Pabbataṭṭhova bhūmaṭṭhe,</p>\r\n<p class="gathalast">Dhīro bāle avekkhati.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-77-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',178,replace(replace('<p class="gatha1">Natti <a name="M0.0178"></a> attasamaṃ pemaṃ,</p>\r\n<p class="gatha2">Natthi dhaññasamaṃ dhanaṃ;</p>\r\n<p class="gatha3">Natthi paññāsamā ābhā,</p>\r\n<p class="gathalast">Vuṭṭhi ve paramā sarā.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-78-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',180,replace(replace('<p class="gatha1">Diṭṭhe <a name="M0.0180"></a> dhamme ca yo attho,</p>\r\n<p class="gatha2">Yo cattho samparāyiko;</p>\r\n<p class="gatha3">Atthābhisamayā dhīro,</p>\r\n<p class="gathalast">Paṇḍitoti pavuccati.</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-79-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',181,replace(replace('<p class="gatha1">Na <a name="M0.0181"></a> tena paṇḍito hoti,</p>\r\n<p class="gatha2">Yāvatā bahu bhāsati;</p>\r\n<p class="gatha3">Khemī a-verī a-bhayo,</p>\r\n<p class="gathalast">Paṇḍitotti pavuccati.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',183,replace(replace('<p class="gatha1">Yamhi <a name="M0.0183"></a> saccañca dhammo ca,</p>\r\n<p class="gatha2">A-hiṃsā saṃyamo damo;</p>\r\n<p class="gatha3">Sa ve vantamalo dhīro,</p>\r\n<p class="gathalast">Thero iti pavuccatti.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-81-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',185,replace(replace('<p class="gatha1">Saka-guṇaṃ <a name="M0.0185"></a> saka-dosaṃ,</p>\r\n<p class="gatha2">Yo jānāti sapaṇḍito;</p>\r\n<p class="gatha3">Para-guṇaṃ para-dosaṃ,</p>\r\n<p class="gathalast">Yo jānāti sapaṇḍito.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-82-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',186,replace(replace('<p class="gatha1">Sati-vīriya-paññāya<a name="M0.0186"></a>,</p>\r\n<p class="gatha2">Yo karoti iriyāpathe;</p>\r\n<p class="gatha3">So paṇḍito have bhave,</p>\r\n<p class="gathalast">Ubhayattha-pariggaho.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-83-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',187,replace(replace('<p class="gatha1">Kataññū <a name="M0.0187"></a> vijjā-sampanno,</p>\r\n<p class="gatha2">Jātimā dhanavā have;</p>\r\n<p class="gatha3">So vicāraṇa-sīlo ca,</p>\r\n<p class="gathalast">Niddukkho paṇḍito bhave.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',189,replace(replace('<p class="gatha1">Sabbe <a name="M0.0189"></a> kammassakā sattā,</p>\r\n<p class="gathalast">Kammaṃ satte vibhajjati.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> Yo passati paccakkhatthaṃ,</p>\r\n<p class="gatha2">Yo ca saṃsāratthaṃ tesu;</p>\r\n<p class="gatha3">Pacchimova pūjanīyo,</p>\r\n<p class="gathalast">Ubhayattha-sudiṭṭhattā.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-84-85-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',191,replace(replace('<p class="gatha1">Appena <a name="M0.0191"></a> anavajjena,</p>\r\n<p class="gatha2">Santuṭṭho sulabhena ca;</p>\r\n<p class="gatha3">Mattaññū subharo hutvā,</p>\r\n<p class="gathalast">Careyya paṇḍito naro.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-86-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',192,replace(replace('<p class="gatha1">Attānameva <a name="M0.0192"></a> pathamaṃ,</p>\r\n<p class="gatha2">Patirūpe nivesaye;</p>\r\n<p class="gatha3">Athaññamanusāseyya,</p>\r\n<p class="gathalast">Na kilisseyya paṇḍito.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-87-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',193,replace(replace('<p class="gatha1">Uttama-parisāya <a name="M0.0193"></a> ve,</p>\r\n<p class="gatha2">Utttamaṃ vācamuttamo;</p>\r\n<p class="gatha3">Bhaṇeyyākhepa-vitthāraṃ,</p>\r\n<p class="gathalast">Sā anagghyā lokattaye.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-88-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',194,replace(replace('<p class="gatha1">Paṇḍitassa <a name="M0.0194"></a> subhāsittaṃ,</p>\r\n<p class="gatha2">Paṇḍitova sujāniyā;</p>\r\n<p class="gatha3">Dummedho taṃ na jānāti,</p>\r\n<p class="gathalast">Dhīro dhīraṃ mamāyati.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-89-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',196,replace(replace('<p class="gatha1">Yena <a name="M0.0196"></a> kenaci vaṇṇena,</p>\r\n<p class="gatha2">Paro labhati rūppanaṃ;</p>\r\n<p class="gatha3">Attho vācāya ce hoti,</p>\r\n<p class="gathalast">Taṃ na bhāseyya paṇḍito.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-90-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',197,replace(replace('<p class="gatha1">Bhuñjanatthaṃ <a name="M0.0197"></a> kathanatthaṃ,</p>\r\n<p class="gatha2">Mukhaṃ hotītti no vade;</p>\r\n<p class="gatha3">Yaṃvā taṃvā mukhāruḷhaṃ,</p>\r\n<p class="gathalast">Vacanaṃ paṇḍito naro.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-91-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',198,replace(replace('<p class="gatha1">Para-sattitto <a name="M0.0198"></a> sa-sattiṃ,</p>\r\n<p class="gatha2">Dujjāno hi naro mite;</p>\r\n<p class="gatha3">Ce jāne saka-sattiṃca,</p>\r\n<p class="gathalast">Kā kathā para-sattiyā.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-92-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',199,replace(replace('<p class="gatha1">Katta-guṇaṃ <a name="M0.0199"></a> paresaṃ yo,</p>\r\n<p class="gatha2">Paṭikaroti paṇḍito;</p>\r\n<p class="gatha3">Jānāti so ācikkhati,</p>\r\n<p class="gathalast">Na bālo guṇa-māmako.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-93-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',200,replace(replace('<p class="gatha1">Pabhūtaṃ <a name="M0.0200"></a> neva kātabbaṃ,</p>\r\n<p class="gatha2">Bhavissaṃ neva cintaye;</p>\r\n<p class="gatha3">Vattamānena kālena,</p>\r\n<p class="gathalast">Vicaranti vicakkhaṇā.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-94-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',201,replace(replace('<p class="gatha1">Dhammesu <a name="M0.0201"></a> sati icchitā,</p>\r\n<p class="gatha2">Rasesu loṇamicchitaṃ;</p>\r\n<p class="gatha3">Rāja-kiccesu amaccaṃ,</p>\r\n<p class="gathalast">Sabba-ṭhānesu paṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',203,replace(replace('<p class="gatha1">Khattiyo <a name="M0.0203"></a> seṭṭho jane tasmiṃ,</p>\r\n<p class="gatha2">Ye gottapaṭisārino;</p>\r\n<p class="gatha3">Vijjā-caraṇa-sampanno,</p>\r\n<p class="gathalast">So seṭṭho deva-mānuse.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-96-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',204,replace(replace('<p class="gatha1">Sambuddho <a name="M0.0204"></a> dvipadaṃ seṭṭho,</p>\r\n<p class="gatha2">Ājānīyo catuppadaṃ;</p>\r\n<p class="gatha3">Sussusā seṭṭhā bhariyānaṃ,</p>\r\n<p class="gathalast">Yo ca puttānamassavo.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-97-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',207,replace(replace('<p class="gatha1">Sātthako <a name="M0.0207"></a> ca a-sammoho,</p>\r\n<p class="gatha2">Sappāyo gocaro tathā;</p>\r\n<p class="gatha3">Cattārimāni sikkheyyuṃ,</p>\r\n<p class="gathalast">Sampajaññābhivaḍḍhakā.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-98-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',208,replace(replace('<p class="gatha1">Paññañca <a name="M0.0208"></a> kho a-sussusaṃ,</p>\r\n<p class="gatha2">Na koci adhigacchati;</p>\r\n<p class="gatha3">Bahussutaṃ anāgamma,</p>\r\n<p class="gathalast">Dhammaṭṭhaṃ a-vinibbhajaṃ.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-99-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',210,replace(replace('<p class="gatha1">Adhippāyo <a name="M0.0210"></a> sudubbodho,</p>\r\n<p class="gatha2">Yasmā vijjati pāḷiyaṃ;</p>\r\n<p class="gatha3">Tasmā upaṭṭhahaṃ gaṇhe,</p>\r\n<p class="gathalast">Garuṃ garumataṃ vidū.</p>\r\n<p class="chapter">Cāritta-niddesa</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-100-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',213,replace(replace('<p class="gatha1">Attā <a name="M0.0213"></a> hi attano nātho,</p>\r\n<p class="gatha2">Ko hi nātho paro siyā;</p>\r\n<p class="gatha3">Attanā hi sudantena,</p>\r\n<p class="gathalast">Nāthaṃ labhati dullabhaṃ.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-101-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',214,replace(replace('<p class="gatha1">Attānañce <a name="M0.0214"></a> piyaṃ jaññā,</p>\r\n<p class="gatha2">Rakkheyya naṃ surakkhitaṃ;</p>\r\n<p class="gatha3">Tiṇṇamaññataraṃ yāmaṃ,</p>\r\n<p class="gathalast">Paṭijaggeyya paṇḍito.</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-102-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',216,replace(replace('<p class="gatha1">Attanā <a name="M0.0216"></a> kurute lakkhiṃ,</p>\r\n<p class="gatha2">A-lakkhiṃ kurutettanā;</p>\r\n<p class="gatha3">Na hi lakkhiṃ a-lakkhiṃ vā,</p>\r\n<p class="gathalast">Añño aññassa kārako.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-103-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',218,replace(replace('<p class="gatha1">Na <a name="M0.0218"></a> pīḷitā atta-dukkhena dhīrā,</p>\r\n<p class="gatha2">Sukhapphalaṃ kammaṃ pariccajanti;</p>\r\n<p class="gatha3">Sammohitāvāpi sukhena mattā,</p>\r\n<p class="gathalast">Na pāpa-kammañca samācaranti.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-104-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',219,replace(replace('<p class="gatha1">Yo <a name="M0.0219"></a> neva nindaṃ nappasaṃsaṃ,</p>\r\n<p class="gatha2">Ādiyati garahaṃ nopi pūjaṃ;</p>\r\n<p class="gatha3">Sirīca lakkhīca apeti tamhā,</p>\r\n<p class="gathalast">Āpo suvuṭṭhīva yathā thalamhā.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-105-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',221,replace(replace('<p class="gatha1">Sādhu <a name="M0.0221"></a> dhammaruci rājā,</p>\r\n<p class="gatha2">Sādhu paññāṇavā naro;</p>\r\n<p class="gatha3">Sādhu mittānama-ddubbho,</p>\r\n<p class="gathalast">Pāpassa a-karaṃ sukhaṃ.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-106-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',222,replace(replace('<p class="gatha1">Kalyāṇakārī <a name="M0.0222"></a> kalyāṇaṃ,</p>\r\n<p class="gatha2">Pāpakārī ca pāpakaṃ;</p>\r\n<p class="gatha3">Yādisaṃ vapatte bījaṃ,</p>\r\n<p class="gathalast">Tādisaṃ vahate phalaṃ.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-107-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',224,replace(replace('<p class="gatha1">Jīvaṃ <a name="M0.0224"></a> paññāya rakkheyya,</p>\r\n<p class="gatha2">Dhanaṃ kammena rakkhaye;</p>\r\n<p class="gatha3">Evaṃ hyarogo sukhito,</p>\r\n<p class="gathalast">Porāṇaka-vaco idaṃ.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',226,replace(replace('<p class="gatha1">Anupubbena <a name="M0.0226"></a> medhāvī,</p>\r\n<p class="gatha2">Thokaṃ thokaṃ khaṇe khaṇe;</p>\r\n<p class="gatha3">Kammāro rajatasseva,</p>\r\n<p class="gathalast">Niddame malamattano.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-109-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',228,replace(replace('<p class="gatha1">Vācānurakkhī <a name="M0.0228"></a> manasā susaṃvuto,</p>\r\n<p class="gatha2">Kāyena ca nākusalaṃ kayirā;</p>\r\n<p class="gatha3">Etetayo kammapathevisodhaye,</p>\r\n<p class="gathalast">Ārādhaye maggamisi-ppaveditaṃ.</p>\r\n<p class="hangnum"><a name="para110"></a><span class="paranum">110</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-110-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',230,replace(replace('<p class="gatha1">A-sante <a name="M0.0230"></a> nopaseveyya,</p>\r\n<p class="gatha2">Sante seveyya paṇḍito;</p>\r\n<p class="gatha3">A-santo nirayaṃ yanti,</p>\r\n<p class="gathalast">Santo pāpenti suggatiṃ.</p>\r\n<p class="hangnum"><a name="para111"></a><span class="paranum">111</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-111-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',232,replace(replace('<p class="gatha1">A-karontopi <a name="M0.0232"></a> ce pāpaṃ,</p>\r\n<p class="gatha2">Karontaṃ mupasevati;</p>\r\n<p class="gatha3">Saṅkiyo hoti pāpasmiṃ,</p>\r\n<p class="gathalast">A-vaṇṇo cassa rūhati.</p>\r\n<p class="hangnum"><a name="para112"></a><span class="paranum">112</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-112-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',233,replace(replace('<p class="gatha1">Saṅghāgato <a name="M0.0233"></a> aniṭṭhehi,</p>\r\n<p class="gatha2">Ambopi madhurapphalo;</p>\r\n<p class="gatha3">Tittapubbova pā eva,</p>\r\n<p class="gathalast">Manusso tu sa-jīvako.</p>\r\n<p class="hangnum"><a name="para113"></a><span class="paranum">113</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-113-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',234,replace(replace('<p class="gatha1">Nihīna-sevito <a name="M0.0234"></a> poso,</p>\r\n<p class="gatha2">Nihīyati ca sabbadā;</p>\r\n<p class="gatha3">Kadāci na ca hāyetha,</p>\r\n<p class="gathalast">Tulyasevīpi attanā.</p>\r\n<p class="hangnum"><a name="para114"></a><span class="paranum">114</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-114-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',237,replace(replace('<p class="gatha1">Vaṇṇa-gandha-rasopeto<a name="M0.0237"></a>,</p>\r\n<p class="gatha2">Amboyaṃ ahuvā pure;</p>\r\n<p class="gatha3">Tameva pūjaṃ labhamāno;</p>\r\n<p class="gathalast">Kenambo kaṭukapphalo.</p>\r\n<p class="hangnum"><a name="para115"></a><span class="paranum">115</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-115-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',238,replace(replace('<p class="gatha1">Pucimanda-parivāro<a name="M0.0238"></a>,</p>\r\n<p class="gatha2">Ambo te dadhivāhana;</p>\r\n<p class="gatha3">Mūlaṃ mūlena saṃsaṭṭhaṃ,</p>\r\n<p class="gatha3">Sākhā sākhaṃ nisevare;</p>\r\n<p class="gatha2">Asātta-sannivāsena,</p>\r\n<p class="gathalast">Tenambo kaṭukapphalo.</p>\r\n<p class="hangnum"><a name="para116"></a><span class="paranum">116</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-116-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',240,replace(replace('<p class="gatha1">Pāmokkha-bhajanaṃ <a name="M0.0240"></a> khippaṃ,</p>\r\n<p class="gatha2">Attha-kāmo su-vuḍḍhiyaṃ;</p>\r\n<p class="gatha3">Bhaje uttari attanā,</p>\r\n<p class="gathalast">Tasmā udeti paṇḍito.</p>\r\n<p class="hangnum"><a name="para117"></a><span class="paranum">117</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-117-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',241,replace(replace('<p class="gatha1">Kāco <a name="M0.0241"></a> kañcana-saṃsaggā,</p>\r\n<p class="gatha2">Dhatte māra-katiṃ jutiṃ;</p>\r\n<p class="gatha3">Tathā saṃsannidhānena,</p>\r\n<p class="gathalast">Mūḷho yāti pavīṇataṃ.</p>\r\n<p class="hangnum"><a name="para118"></a><span class="paranum">118</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-118-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',243,replace(replace('<p class="gatha1">Kīṭopi <a name="M0.0243"></a> sumano-saṅgā,</p>\r\n<p class="gatha2">Ārohati sataṃ siro;</p>\r\n<p class="gatha3">Asmāpi yāti devatvaṃ,</p>\r\n<p class="gathalast">Mahabbhi suppatiṭṭhito.</p>\r\n<p class="hangnum"><a name="para119"></a><span class="paranum">119</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-119-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',245,replace(replace('<p class="gatha1">Sabbhireva <a name="M0.0245"></a> samāsetha,</p>\r\n<p class="gatha2">Sabbhi kubbetha sandhavaṃ;</p>\r\n<p class="gatha3">Sataṃ saddhammamaññāya,</p>\r\n<p class="gathalast">Paññaṃ labhati nāññato.</p>\r\n<p class="hangnum"><a name="para120"></a><span class="paranum">120</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-120-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',246,replace(replace('<p class="gatha1">Sabbhireva <a name="M0.0246"></a> samāsetha,</p>\r\n<p class="gatha2">Sabbhi kubbetha sandhavaṃ;</p>\r\n<p class="gatha3">Sataṃ saddhammamaññāya,</p>\r\n<p class="gathalast">Sabba-dukkhā pamuccatti.</p>\r\n<p class="hangnum"><a name="para121"></a><span class="paranum">121</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-121-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',247,replace(replace('<p class="gatha1">Sakiṃdeva <a name="M0.0247"></a> kulaputta,</p>\r\n<p class="gatha2">Sabbhi hoti samāgamo;</p>\r\n<p class="gatha3">Sā naṃ saṅgati pāleti,</p>\r\n<p class="gathalast">Nāsabbhi bahu saṅgamo.</p>\r\n<p class="hangnum"><a name="para122"></a><span class="paranum">122</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-122-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',249,replace(replace('<p class="gatha1">Seyyo <a name="M0.0249"></a> a-mitto medhāvī,</p>\r\n<p class="gathalast">Yañce bālānukampako.</p>\r\n<p class="hangnum"><a name="para123"></a><span class="paranum">123</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-123-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',250,replace(replace('<p class="gatha1">Sīlavantaṃ <a name="M0.0250"></a> paññavantaṃ,</p>\r\n<p class="gatha2">Divā nissaya-dāyakaṃ;</p>\r\n<p class="gatha3">Bahussutaṃ gavesanto,</p>\r\n<p class="gathalast">Bhajeyya attha-māmako.</p>\r\n<p class="hangnum"><a name="para124"></a><span class="paranum">124</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-124-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',251,replace(replace('<p class="gatha1">Pāpa-mitte <a name="M0.0251"></a> vivajjetvā,</p>\r\n<p class="gatha2">Bhajeyyuttama-puggalaṃ;</p>\r\n<p class="gatha3">Ovāde cassa tiṭṭheyya,</p>\r\n<p class="gathalast">Patthento a-calaṃ sukhaṃ.</p>\r\n<p class="hangnum"><a name="para125"></a><span class="paranum">125</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-125-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',253,replace(replace('<p class="gatha1">Na <a name="M0.0253"></a> bhaje pāpake mitte,</p>\r\n<p class="gatha2">Na bhaje purisādhame;</p>\r\n<p class="gatha3">Bhajetha mittte kalyāṇe,</p>\r\n<p class="gathalast">Bhajetha purisuttame.</p>\r\n<p class="hangnum"><a name="para126"></a><span class="paranum">126</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-126-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',254,replace(replace('<p class="gatha1">Anavajjaṃ <a name="M0.0254"></a> mukhamboja,</p>\r\n<p class="gatha2">Manavajjā ca bhāratī;</p>\r\n<p class="gatha3">Alaṅkatāva sobhante,</p>\r\n<p class="gathalast">Kiṃsu te niralaṅkatā.</p>\r\n<p class="hangnum"><a name="para127"></a><span class="paranum">127</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-127-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',256,replace(replace('<p class="gatha1">Na <a name="M0.0256"></a> hi vaṇṇena sampannā,</p>\r\n<p class="gatha2">Mañjukā piya-dassinā;</p>\r\n<p class="gatha3">Kharā vācā piyā hoti,</p>\r\n<p class="gathalast">Asmiṃ loke paramhi ca.</p>\r\n<p class="hangnum"><a name="para128"></a><span class="paranum">128</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-128-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',257,replace(replace('<p class="gatha1">Nanu <a name="M0.0257"></a> passasimaṃ kāḷiṃ,</p>\r\n<p class="gatha2">Dubbaṇṇaṃ tilakāhataṃ;</p>\r\n<p class="gatha3">Kolilaṃ saṇha-vācena,</p>\r\n<p class="gathalast">Bahūnaṃ pāṇinaṃ piyaṃ.</p>\r\n<p class="hangnum"><a name="para129"></a><span class="paranum">129</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-129-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',257,replace(replace('<p class="gatha1">Tasmā <a name="M0.0257"></a> sakhila-vācāya,</p>\r\n<p class="gatha2">Manta-bhāṇī anuddhato;</p>\r\n<p class="gatha3">Atthaṃ dhammañca dīpeti,</p>\r\n<p class="gathalast">Madhuraṃ tassa bhāsitaṃ.</p>\r\n<p class="hangnum"><a name="para130"></a><span class="paranum">130</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-130-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',260,replace(replace('<p class="gatha1">Tameva <a name="M0.0260"></a> vācaṃ bhāseyya,</p>\r\n<p class="gatha2">Yā sattānaṃ na tāpaye;</p>\r\n<p class="gatha3">Pare ca na vihiṃseyya,</p>\r\n<p class="gathalast">Sā ve vācā subhāsitā.</p>\r\n<p class="hangnum"><a name="para131"></a><span class="paranum">131</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-131-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',261,replace(replace('<p class="gatha1">Piyaṃ <a name="M0.0261"></a> vācaṃva bhāseyya,</p>\r\n<p class="gatha2">Yā vācā paṭinanditā;</p>\r\n<p class="gatha3">Yaṃ anādāya pāpāni;</p>\r\n<p class="gathalast">Paresaṃ bhāsate piyaṃ.</p>\r\n<p class="hangnum"><a name="para132"></a><span class="paranum">132</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-132-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',262,replace(replace('<p class="gatha1">Saccaṃ <a name="M0.0262"></a> ve amatā vācā,</p>\r\n<p class="gatha2">Esa dhammo sanantano;</p>\r\n<p class="gatha3">Sacce atthe ca dhamme ca,</p>\r\n<p class="gathalast">Āhu santo patiṭṭhitā.</p>\r\n<p class="hangnum"><a name="para133"></a><span class="paranum">133</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-133-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',264,replace(replace('<p class="gatha1">Subhāsitañca <a name="M0.0264"></a> dhammañca,</p>\r\n<p class="gatha2">Piyañca saccameva ca;</p>\r\n<p class="gatha3">Catu-aṅgehi sampannaṃ,</p>\r\n<p class="gathalast">Vācaṃ bhāseyya paṇḍito.</p>\r\n<p class="hangnum"><a name="para134"></a><span class="paranum">134</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-134-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',265,replace(replace('<p class="gatha1">Manāpameva <a name="M0.0265"></a> bhāseyya,</p>\r\n<p class="gatha2">Nāmanāpaṃ kudācanaṃ;</p>\r\n<p class="gatha3">Manāpaṃ bhāsamānassa,</p>\r\n<p class="gathalast">Siddhaṃ piyosadhaṃ bhave.</p>\r\n<p class="hangnum"><a name="para135"></a><span class="paranum">135</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-135-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',266,replace(replace('<p class="gatha1">Yaṃ <a name="M0.0266"></a> vadeyya taṃ kareyya,</p>\r\n<p class="gatha2">Yaṃ na vade na taṃ kare;</p>\r\n<p class="gatha3">A-karontaṃ bhāsamānaṃ,</p>\r\n<p class="gathalast">Parijānanti paṇḍitā.</p>\r\n<p class="hangnum"><a name="para136"></a><span class="paranum">136</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-136-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',268,replace(replace('<p class="gatha1">Rahovādaṃ <a name="M0.0268"></a> na bhāseyya,</p>\r\n<p class="gatha2">Na sammukhā khiṇaṃ bhaṇe;</p>\r\n<p class="gatha3">A-taramānova bhaṇeyya,</p>\r\n<p class="gathalast">Taramānova no bhaṇe.</p>\r\n<p class="hangnum"><a name="para137"></a><span class="paranum">137</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-137-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',269,replace(replace('<p class="gatha1">Māvoca <a name="M0.0269"></a> pharusaṃ kiñci,</p>\r\n<p class="gatha2">Vuttā paṭivadeyyuṃ taṃ;</p>\r\n<p class="gatha3">Dukkhā hi sārambhakathā,</p>\r\n<p class="gathalast">Paṭidaṇḍā phuseyyuṃ taṃ.</p>\r\n<p class="hangnum"><a name="para138"></a><span class="paranum">138</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-138-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',270,replace(replace('<p class="gatha1">Saka-yuttaṃ <a name="M0.0270"></a> kare kammaṃ,</p>\r\n<p class="gatha2">Saka-yuttaṃ vaciṃ bhaṇe;</p>\r\n<p class="gatha3">A-yuttake dhanaṃ naṭṭhaṃ,</p>\r\n<p class="gathalast">A-yutte jīvitaṃ khaye.</p>\r\n<p class="hangnum"><a name="para139"></a><span class="paranum">139</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-139-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',272,replace(replace('<p class="gatha1">Ye <a name="M0.0272"></a> vuḍḍhamapacāyanti,</p>\r\n<p class="gatha2">Narā dhammassa dhakāvidā;</p>\r\n<p class="gatha3">Diṭṭhe dhammeva pāsaṃsā,</p>\r\n<p class="gathalast">Samparāye ca suggatiṃ.</p>\r\n<p class="hangnum"><a name="para140"></a><span class="paranum">140</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-140-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',274,replace(replace('<p class="gatha1">Porī-kathaṃ-va <a name="M0.0274"></a> bhāseyya,</p>\r\n<p class="gatha2">Yuttā kathā hi pūrino;</p>\r\n<p class="gatha3">Bhāti-mattañca bhātāti,</p>\r\n<p class="gathalast">Pitu-mattaṃ pitā iti.</p>\r\n<p class="hangnum"><a name="para141"></a><span class="paranum">141</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-141-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',275,replace(replace('<p class="gatha1">Dānañca <a name="M0.0275"></a> piya-vajjañca,</p>\r\n<p class="gatha2">Attha-cariyā ca yā idha;</p>\r\n<p class="gatha3">Samānattatā dhammesa,</p>\r\n<p class="gathalast">Tattha tattha yathārahaṃ.</p>\r\n<p class="hangnum"><a name="para142"></a><span class="paranum">142</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-142-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',277,replace(replace('<p class="gatha1">Sakiṃ <a name="M0.0277"></a> vadanti rājāno,</p>\r\n<p class="gatha2">Sakiṃṃ samaṇa-brāhmaṇā;</p>\r\n<p class="gatha3">Sakiṃva purisā loke,</p>\r\n<p class="gathalast">Esadhammo sanantano.</p>\r\n<p class="hangnum"><a name="para143"></a><span class="paranum">143</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-143-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',278,replace(replace('<p class="gatha1">Eka-vācaṃva <a name="M0.0278"></a> dvevācaṃ,</p>\r\n<p class="gatha2">Bhaṇeyya anukampako;</p>\r\n<p class="gatha3">Taduttari na bhāseyya,</p>\r\n<p class="gathalast">Dāsovayyassa santike.</p>\r\n<p class="hangnum"><a name="para144"></a><span class="paranum">144</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-144-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',279,replace(replace('<p class="gatha1">Tasseva <a name="M0.0279"></a> tena pāpiyo,</p>\r\n<p class="gatha2">Yo kuddhaṃ paṭikujjhatti;</p>\r\n<p class="gatha3">Kuddhaṃ a-paṭikujjhanto,</p>\r\n<p class="gathalast">Saṅgāmaṃ jeti dujjayaṃ.</p>\r\n<p class="hangnum"><a name="para145"></a><span class="paranum">145</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-145-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',281,replace(replace('<p class="gatha1">Ubhinnamatthaṃ <a name="M0.0281"></a> carati,</p>\r\n<p class="gatha2">Attano ca parassa ca;</p>\r\n<p class="gatha3">Paraṃ saṃkupitaṃ ñatvā,</p>\r\n<p class="gathalast">Yo sato upasammati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',282,replace(replace('<p class="gatha1">Attānaṃ <a name="M0.0282"></a> rakkhanto paraṃ rakkhati,</p>\r\n<p class="gathalast">Paraṃ rakkhanto attānaṃ rakkhati.</p>\r\n<p class="hangnum"><a name="para146"></a><span class="paranum">146</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-146-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',283,replace(replace('<p class="gatha1">Sīla-samādhi-paññānaṃ<a name="M0.0283"></a>,</p>\r\n<p class="gatha2">Khantī padhāna-kāraṇaṃ;</p>\r\n<p class="gatha3">Sabbepi kusalā dhammā,</p>\r\n<p class="gathalast">Khantyāyattāva vaḍḍhare.</p>\r\n<p class="hangnum"><a name="para147"></a><span class="paranum">147</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-147-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',284,replace(replace('<p class="gatha1">Khamā-khagga-karetassa<a name="M0.0284"></a>,</p>\r\n<p class="gatha2">Dujjano kiṃ karissati;</p>\r\n<p class="gatha3">A-tiṇe patito vahni,</p>\r\n<p class="gathalast">Sayamevūpasammati.</p>\r\n<p class="hangnum"><a name="para148"></a><span class="paranum">148</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-148-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',285,replace(replace('<p class="gatha1">Sussusā <a name="M0.0285"></a> savaṇañceva,</p>\r\n<p class="gatha2">Gahaṇaṃ dhāraṇaṃ tathā;</p>\r\n<p class="gatha3">Uhāpohatthaviññāṇaṃ,</p>\r\n<p class="gathalast">Tatva-ññāṇañcadhīguṇaṃ.</p>\r\n<p class="hangnum"><a name="para149"></a><span class="paranum">149</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-149-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',286,replace(replace('<p class="gatha1">Yassete <a name="M0.0286"></a> caturo dhammā,</p>\r\n<p class="gatha2">Atthi posesu paṇḍita;</p>\r\n<p class="gatha3">Saccaṃ dhammo dhīti cāgo,</p>\r\n<p class="gathalast">Diṭṭhaṃ so ativattati.</p>\r\n<p class="hangnum"><a name="para150"></a><span class="paranum">150</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-150-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',288,replace(replace('<p class="gatha1">Vejjo <a name="M0.0288"></a> purohito mantī,</p>\r\n<p class="gatha2">Vedaññotra catutthako;</p>\r\n<p class="gatha3">Pabhāta-kāle daṭṭhabbā,</p>\r\n<p class="gathalast">Niccaṃ sva-sīrimicchatā.</p>\r\n<p class="hangnum"><a name="para151"></a><span class="paranum">151</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-151-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',289,replace(replace('<p class="gatha1">Mittānaṃ <a name="M0.0289"></a> santikaṃ gacche,</p>\r\n<p class="gatha2">Kāle na rattiyaṃ kisaṃ;</p>\r\n<p class="gatha3">Ce bahuṃ bhijje cinitvā,</p>\r\n<p class="gathalast">Taṃ mittesu samākare.</p>\r\n<p class="hangnum"><a name="para152"></a><span class="paranum">152</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-152-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',291,replace(replace('<p class="gatha1">Na <a name="M0.0291"></a> paresaṃ vilomāni,</p>\r\n<p class="gatha2">Na paresaṃ katākataṃ;</p>\r\n<p class="gatha3">Attanova avekkheyya,</p>\r\n<p class="gathalast">Katāni a-katāni ca.</p>\r\n<p class="hangnum"><a name="para153"></a><span class="paranum">153</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-153-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',293,replace(replace('<p class="gatha1">Desa-jāti-pubbe-carā<a name="M0.0293"></a>,</p>\r\n<p class="gatha2">Anu-carā jano kare;</p>\r\n<p class="gatha3">Paresaṃ vedhakaṃ māyaṃ,</p>\r\n<p class="gathalast">Taṃ jānitvā sakhaṃ kare.</p>\r\n<p class="hangnum"><a name="para154"></a><span class="paranum">154</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-154-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',294,replace(replace('<p class="gatha1">Parittaṃ <a name="M0.0294"></a> dārumāruyha,</p>\r\n<p class="gatha2">Yathā sīde mahaṇṇave;</p>\r\n<p class="gatha3">Evaṃ kusītamāgamma,</p>\r\n<p class="gatha2">Sādhu-jīvīpi sīdati;</p>\r\n<p class="gatha3">Tasmā taṃ parivajjeyya,</p>\r\n<p class="gathalast">Kusītaṃ hīna-vīriyaṃ.</p>\r\n<p class="hangnum"><a name="para155"></a><span class="paranum">155</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-155-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',296,replace(replace('<p class="gatha1">Alasañca <a name="M0.0296"></a> pamādo ca,</p>\r\n<p class="gatha2">Anuṭṭhānaṃ a-saṃyamo;</p>\r\n<p class="gatha3">Niddā tandi ca te chidde,</p>\r\n<p class="gathalast">Sabbaso taṃ vivajjaye.</p>\r\n<p class="hangnum"><a name="para156"></a><span class="paranum">156</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-156-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',297,replace(replace('<p class="gatha1">Cajeyya <a name="M0.0297"></a> dummittaṃ bālaṃ,</p>\r\n<p class="gatha2">Āsīvisaṃva māṇavo;</p>\r\n<p class="gatha3">Bhañjeyya pāpakaṃ kammaṃ,</p>\r\n<p class="gathalast">Naḷāgāraṃva kuñjaro.</p>\r\n<p class="hangnum"><a name="para157"></a><span class="paranum">157</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-157-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',299,replace(replace('<p class="gatha1">Na <a name="M0.0299"></a> hi aññañña-cittānaṃ,</p>\r\n<p class="gatha2">Itthīnaṃ purisāna vā;</p>\r\n<p class="gatha3">Nānāvīkatvā saṃsaggaṃ,</p>\r\n<p class="gathalast">Tādisaṃ pica nāsmase.</p>\r\n<p class="hangnum"><a name="para158"></a><span class="paranum">158</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-158-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',300,replace(replace('<p class="gatha1">Nāsmase <a name="M0.0300"></a> kata-pāpamhi,</p>\r\n<p class="gatha2">Nāsmase alika-vādine;</p>\r\n<p class="gatha3">Nāsmate attatthapaññamhi,</p>\r\n<p class="gathalast">Atta-santepi nāsmate.</p>\r\n<p class="hangnum"><a name="para159"></a><span class="paranum">159</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-159-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',301,replace(replace('<p class="gatha1">Ghatāsanaṃ <a name="M0.0301"></a> kuñjaraṃ kaṇha-sappaṃ,</p>\r\n<p class="gatha2">Muddhā-bhisittaṃ pamadā ca sabbā;</p>\r\n<p class="gatha3">Ete naro niccasato bhajetha,</p>\r\n<p class="gathalast">Tesaṃ have dubbidū sabba-bhāvo.</p>\r\n<p class="hangnum"><a name="para160"></a><span class="paranum">160</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-160-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',303,replace(replace('<p class="gatha1">Itthīnaṃ <a name="M0.0303"></a> dujjanāna-ñca,</p>\r\n<p class="gatha2">Vissāso no-pa pajjate;</p>\r\n<p class="gatha3">Vise siṅgimhi nadiyaṃ,</p>\r\n<p class="gathalast">Roge rāja-kulamhi ca.</p>\r\n<p class="hangnum"><a name="para161"></a><span class="paranum">161</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-161-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',304,replace(replace('<p class="gatha1">Itthi-dhutto <a name="M0.0304"></a> surā-dhutto,</p>\r\n<p class="gatha2">Akkha-dhutto ca yo naro;</p>\r\n<p class="gatha3">Laddhaṃ laddhaṃ vināseti,</p>\r\n<p class="gathalast">Taṃ parābhavato mukhaṃ.</p>\r\n<p class="hangnum"><a name="para162"></a><span class="paranum">162</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-162-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',306,replace(replace('<p class="gatha1">Pāpa-mitto <a name="M0.0306"></a> pāpa-sakho,</p>\r\n<p class="gatha2">Pāpa-ācāra gocaro;</p>\r\n<p class="gatha3">Asmā lokā paramhā ca,</p>\r\n<p class="gathalast">Ubhayā dhaṃsate naro.</p>\r\n<p class="hangnum"><a name="para163"></a><span class="paranum">163</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-163-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',307,replace(replace('<p class="gatha1">Maccherena <a name="M0.0307"></a> yasaṃ hataṃ,</p>\r\n<p class="gatha2">Kuppanena guṇo hato;</p>\r\n<p class="gatha3">Kūṭena nassate saccaṃ,</p>\r\n<p class="gathalast">Khuddena dhamma-rakkhanaṃ.</p>\r\n<p class="hangnum"><a name="para164"></a><span class="paranum">164</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-164-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',309,replace(replace('<p class="gatha1">Akkha-devī <a name="M0.0309"></a> dhanāni ca,</p>\r\n<p class="gatha2">Vināso hoti āpadā;</p>\r\n<p class="gatha3">Ṭhiti hatā pamādo ca,</p>\r\n<p class="gathalast">Dvijaṃ bhikkhuñca nassati.</p>\r\n<p class="hangnum"><a name="para165"></a><span class="paranum">165</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-165-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',310,replace(replace('<p class="gatha1">Pesuññena <a name="M0.0310"></a> kulaṃ hataṃ,</p>\r\n<p class="gatha2">Mānena hitamattano;</p>\r\n<p class="gatha3">Duccaritena mānuso,</p>\r\n<p class="gathalast">Daliddāyādaro hato.</p>\r\n<p class="hangnum"><a name="para166"></a><span class="paranum">166</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-166-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',312,replace(replace('<p class="gatha1">A-mānanā <a name="M0.0312"></a> yattha siyā,</p>\r\n<p class="gatha2">Santānaṃpi vimānanā;</p>\r\n<p class="gatha3">Hīna-sammānanāvāpi,</p>\r\n<p class="gathalast">Na tattha vasatiṃ vase.</p>\r\n<p class="hangnum"><a name="para167"></a><span class="paranum">167</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-167-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',313,replace(replace('<p class="gatha1">Yatthālaso <a name="M0.0313"></a> ca dakkho ca,</p>\r\n<p class="gatha2">Sūro bhīru ca pūjiyā;</p>\r\n<p class="gatha3">Na tattha santo vasanti,</p>\r\n<p class="gathalast">A-visesa-kare nare.</p>\r\n<p class="hangnum"><a name="para168"></a><span class="paranum">168</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-168-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',315,replace(replace('<p class="gatha1">No <a name="M0.0315"></a> ce assa sakā-buddhi,</p>\r\n<p class="gatha2">Vinayo vā su-sikkhito;</p>\r\n<p class="gatha3">Vane andha-mahiṃsova,</p>\r\n<p class="gathalast">Careyya bahuko jano.</p>\r\n<p class="hangnum"><a name="para169"></a><span class="paranum">169</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-169-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',316,replace(replace('<p class="gatha1">Phalaṃ <a name="M0.0316"></a> ve kadaliṃ hanti,</p>\r\n<p class="gatha2">Phalaṃ veḷuṃ phalaṃ naḷaṃ;</p>\r\n<p class="gatha3">Sakkāro kā-purisaṃ hanti,</p>\r\n<p class="gathalast">Gabbho assatariṃ yathā.</p>\r\n<p class="hangnum"><a name="para170"></a><span class="paranum">170</span>.</p>\r\n<p class="gatha1"> Vajjañca vajjato ñatvā,</p>\r\n<p class="gatha2">A-vajjañca a-vajjato;</p>\r\n<p class="gatha3">Sammādiṭṭhi-samādānā,</p>\r\n<p class="gathalast">Sattā gacchantti suggatiṃ.</p>\r\n<p class="chapter">Gharāvāsa-niddesa</p>\r\n<p class="hangnum"><a name="para171"></a><span class="paranum">171</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-170-171-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',319,replace(replace('<p class="gatha1">Dukkhaṃ <a name="M0.0319"></a> gahabbataṃ sādhu,</p>\r\n<p class="gatha2">Saṃvibhajjañca bhojanaṃ;</p>\r\n<p class="gatha3">A-hāso attha-lobhesu,</p>\r\n<p class="gathalast">Attha-byāpatti abyatho.</p>\r\n<p class="hangnum"><a name="para172"></a><span class="paranum">172</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-172-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',321,replace(replace('<p class="gatha1">Yodha <a name="M0.0321"></a> sītañca uṇhañca,</p>\r\n<p class="gatha2">Tiṇā bhiyyo na maññati;</p>\r\n<p class="gatha3">Karaṃ purisa-kiccāni,</p>\r\n<p class="gathalast">So sukhā na vihāyati.</p>\r\n<p class="hangnum"><a name="para173"></a><span class="paranum">173</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-173-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',322,replace(replace('<p class="gatha1">Paṇḍito <a name="M0.0322"></a> sīla-sampanno,</p>\r\n<p class="gatha2">Saṇhā ca paṭibhānavā;</p>\r\n<p class="gatha3">Nivāta-vutti atthaddho,</p>\r\n<p class="gathalast">Tādiso labhate yasaṃ.</p>\r\n<p class="hangnum"><a name="para174"></a><span class="paranum">174</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-174-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',324,replace(replace('<p class="gatha1">Uṭṭhānako <a name="M0.0324"></a> analaso,</p>\r\n<p class="gatha2">Āpadāsu na vedhati;</p>\r\n<p class="gatha3">Acchinnavutti medhāvī,</p>\r\n<p class="gathalast">Tādiso labhate yasaṃ.</p>\r\n<p class="hangnum"><a name="para175"></a><span class="paranum">175</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-175-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',325,replace(replace('<p class="gatha1">Saṅgāhako <a name="M0.0325"></a> mitta-karo,</p>\r\n<p class="gatha2">Vadaññū vītta-maccharo;</p>\r\n<p class="gatha3">Netā vi-netā anu-netā,</p>\r\n<p class="gathalast">Tādiso labhate yasaṃ.</p>\r\n<p class="hangnum"><a name="para176"></a><span class="paranum">176</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-176-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',327,replace(replace('<p class="gatha1">Uṭṭhānavako <a name="M0.0327"></a> satīmato,</p>\r\n<p class="gatha2">Suci-kammassa nisammakārino;</p>\r\n<p class="gatha3">Saññatassa dhamma-jīvino,</p>\r\n<p class="gathalast">A-ppamattassa yasobhi-vaḍḍhati.</p>\r\n<p class="hangnum"><a name="para177"></a><span class="paranum">177</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-177-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',329,replace(replace('<p class="gatha1">Dveva <a name="M0.0329"></a> tāta padākāni,</p>\r\n<p class="gatha2">Yattha sabbaṃ patiṭṭhitaṃ;</p>\r\n<p class="gatha3">A-laddhassa ca yo lābho,</p>\r\n<p class="gathalast">Laddhassa anurakkhaṇā.</p>\r\n<p class="hangnum"><a name="para178"></a><span class="paranum">178</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-178-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',330,replace(replace('<p class="gatha1">Catudhā <a name="M0.0330"></a> vibhaje bhoge,</p>\r\n<p class="gatha2">Paṇḍito gharamāvasaṃ;</p>\r\n<p class="gatha3">Ekena bhogaṃ bhuñjeyya,</p>\r\n<p class="gatha3">Dvīhi kammaṃ payojaye;</p>\r\n<p class="gatha2">Catutthañca nidhāpeyya,</p>\r\n<p class="gathalast">Āpadāsu bhavissati.</p>\r\n<p class="hangnum"><a name="para179"></a><span class="paranum">179</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-179-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',332,replace(replace('<p class="gatha1">Añjanānaṃ <a name="M0.0332"></a> khayaṃ disvā,</p>\r\n<p class="gatha2">Upacikānañca ācayaṃ;</p>\r\n<p class="gatha3">Madhūnañca samāhāraṃ,</p>\r\n<p class="gathalast">Paṇḍitto gharamāvase.</p>\r\n<p class="hangnum"><a name="para180"></a><span class="paranum">180</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-180-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',335,replace(replace('<p class="gatha1">Vibhavaṃ <a name="M0.0335"></a> rakkhato laddhaṃ,</p>\r\n<p class="gatha2">Parihāni na vijjati;</p>\r\n<p class="gatha3">Ārakkhamhi a-santamhi,</p>\r\n<p class="gathalast">Laddhaṃ laddhaṃ vinassati.</p>\r\n<p class="hangnum"><a name="para181"></a><span class="paranum">181</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-181-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',337,replace(replace('<p class="gatha1">Paññā <a name="M0.0337"></a> natthi dhanaṃ natthi,</p>\r\n<p class="gatha2">Yassa loke na vindati;</p>\r\n<p class="gatha3">Putta-dārā na pīyanti,</p>\r\n<p class="gathalast">Tassa mittaṃ sukhāvahaṃ.</p>\r\n<p class="hangnum"><a name="para182"></a><span class="paranum">182</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-182-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',339,replace(replace('<p class="gatha1">Cattāro <a name="M0.0339"></a> ca veditabbā,</p>\r\n<p class="gatha2">Mittā ceva suhadā ca;</p>\r\n<p class="gatha3">Upakāro suhadopi,</p>\r\n<p class="gatha2">Samāna-sukha-dukkho ca;</p>\r\n<p class="gatha3">Atthakkhāyīnukampako,</p>\r\n<p class="gathalast">Tathā mitto veditabbo.</p>\r\n<p class="hangnum"><a name="para183"></a><span class="paranum">183</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-183-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',342,replace(replace('<p class="gatha1">Bhogā <a name="M0.0342"></a> naṭṭhena jiṇṇena,</p>\r\n<p class="gatha2">A-mitena ca bhojane;</p>\r\n<p class="gatha3">Na tiṭṭhanti ciraṃ disvā,</p>\r\n<p class="gathalast">Taṃ paṇḍito ghare vase.</p>\r\n<p class="hangnum"><a name="para184"></a><span class="paranum">184</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-184-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',343,replace(replace('<p class="gatha1">Ati-sītaṃ <a name="M0.0343"></a> ati-uṇhaṃ,</p>\r\n<p class="gatha2">Ati-sāyamidaṃ ahu;</p>\r\n<p class="gatha3">Iti vissaṭṭha-kammantte,</p>\r\n<p class="gathalast">Atthā accenti māṇave.</p>\r\n<p class="hangnum"><a name="para185"></a><span class="paranum">185</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-185-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',345,replace(replace('<p class="gatha1">Na <a name="M0.0345"></a> divā suppa-sīlena,</p>\r\n<p class="gatha2">Rattinaṭṭhānadessinā;</p>\r\n<p class="gatha3">Niccaṃ mattena soṇḍena,</p>\r\n<p class="gathalast">Sakkā āvasituṃ gharaṃ.</p>\r\n<p class="hangnum"><a name="para186"></a><span class="paranum">186</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-186-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',346,replace(replace('<p class="gatha1">Hananti <a name="M0.0346"></a> bhogā dummedhaṃ,</p>\r\n<p class="gatha2">No ce pāra-gavesino;</p>\r\n<p class="gatha3">Bhoga-taṇhāya dummedho,</p>\r\n<p class="gathalast">Hanti aññeva attanaṃ.</p>\r\n<p class="hangnum"><a name="para187"></a><span class="paranum">187</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-187-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',348,replace(replace('<p class="gatha1">Dujjīvitamajīvimhā<a name="M0.0348"></a>,</p>\r\n<p class="gatha2">Yesaṃ no na dadāmase;</p>\r\n<p class="gatha3">Vijjamānesu bhogesu,</p>\r\n<p class="gathalast">Dīpaṃ nā kamha mattano.</p>\r\n<p class="hangnum"><a name="para188"></a><span class="paranum">188</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-188-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',349,replace(replace('<p class="gatha1">Saṭṭhi-vassa-sahassāni<a name="M0.0349"></a>,</p>\r\n<p class="gatha2">Paripuṇṇāni sabbaso;</p>\r\n<p class="gatha3">Niraye paccamānānaṃ,</p>\r\n<p class="gathalast">Kadā anto bhavissati.</p>\r\n<p class="hangnum"><a name="para189"></a><span class="paranum">189</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-189-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',350,replace(replace('<p class="gatha1">Natthi <a name="M0.0350"></a> anto kuto anto,</p>\r\n<p class="gatha2">Na anto patidissati;</p>\r\n<p class="gatha3">Tadā hi pakataṃ pāpaṃ,</p>\r\n<p class="gathalast">Mama tuyhañce mārisā;</p>\r\n<p class="hangnum"><a name="para190"></a><span class="paranum">190</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-190-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',351,replace(replace('<p class="gatha1">Sohaṃ <a name="M0.0351"></a> nūna ito gantā,</p>\r\n<p class="gatha2">Yoni laddhāna mānusaṃ;</p>\r\n<p class="gatha3">Vadaññū sīla-sampanno,</p>\r\n<p class="gathalast">Kāhāmi kusalaṃ bahuṃ.</p>\r\n<p class="hangnum"><a name="para191"></a><span class="paranum">191</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-191-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',352,replace(replace('<p class="gatha1">Mā <a name="M0.0352"></a> gijjhe paccaye macco,</p>\r\n<p class="gatha2">Bahu-dosā hi paccayā;</p>\r\n<p class="gatha3">Caranto paccaye ñāyā,</p>\r\n<p class="gathalast">Ubhayatthāpi vaḍḍhati.</p>\r\n<p class="hangnum"><a name="para192"></a><span class="paranum">192</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-192-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',354,replace(replace('<p class="gatha1">A-laddhā <a name="M0.0354"></a> vittaṃ tappati,</p>\r\n<p class="gatha2">Pubbe a-samudānitaṃ;</p>\r\n<p class="gatha3">Na pubbe dhanamesissaṃ,</p>\r\n<p class="gathalast">Iti pacchānutappati.</p>\r\n<p class="hangnum"><a name="para193"></a><span class="paranum">193</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-193-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',356,replace(replace('<p class="gatha1">Kūṭavedī <a name="M0.0356"></a> pure āsiṃ,</p>\r\n<p class="gatha2">Pisuṇo piṭṭhi-maṃsiko;</p>\r\n<p class="gatha3">Caṇḍo ca pharuso cāpi,</p>\r\n<p class="gathalast">Iti pacchānutappati.</p>\r\n<p class="hangnum"><a name="para194"></a><span class="paranum">194</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-194-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',357,replace(replace('<p class="gatha1">Pāṇātipātī <a name="M0.0357"></a> pure āsiṃ,</p>\r\n<p class="gatha2">Luddo cāpi anariyo;</p>\r\n<p class="gatha3">Bhūtānaṃ nānukampiyaṃ,</p>\r\n<p class="gathalast">Iti pacchānutappati.</p>\r\n<p class="hangnum"><a name="para195"></a><span class="paranum">195</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-195-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',359,replace(replace('<p class="gatha1">Bahūsu <a name="M0.0359"></a> vata santīsu,</p>\r\n<p class="gatha2">Anāpādāsu itthisu;</p>\r\n<p class="gatha3">Para-dāraṃ asevissaṃ,</p>\r\n<p class="gathalast">Iti pacchānutappati.</p>\r\n<p class="hangnum"><a name="para196"></a><span class="paranum">196</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-196-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',360,replace(replace('<p class="gatha1">Bahumhi <a name="M0.0360"></a> tava santamhi,</p>\r\n<p class="gatha2">Anna-pāne upaṭṭhite;</p>\r\n<p class="gatha3">Na pubbe adadaṃ dānaṃ,</p>\r\n<p class="gathalast">Iti pacchānutappati.</p>\r\n<p class="hangnum"><a name="para197"></a><span class="paranum">197</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-197-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',362,replace(replace('<p class="gatha1">Mātaraṃ <a name="M0.0362"></a> pitarañcāpi,</p>\r\n<p class="gatha2">Jiṇṇakaṃ gata-yobbanaṃ;</p>\r\n<p class="gatha3">Pahu santo na posissaṃ,</p>\r\n<p class="gathalast">Iti pacchānukappati.</p>\r\n<p class="hangnum"><a name="para198"></a><span class="paranum">198</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-198-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',363,replace(replace('<p class="gatha1">Ācariyamanusatthāraṃ<a name="M0.0363"></a>,</p>\r\n<p class="gatha2">Sabba-kāma-rasāharaṃ;</p>\r\n<p class="gatha3">Pitaraṃ atimaññissaṃ,</p>\r\n<p class="gathalast">Iti pacchānutappati.</p>\r\n<p class="hangnum"><a name="para199"></a><span class="paranum">199</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-199-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',365,replace(replace('<p class="gatha1">Samaṇe <a name="M0.0365"></a> brāhmaṇe cāpi,</p>\r\n<p class="gatha2">Sīlavante bahussute;</p>\r\n<p class="gatha3">Na pubbe payirupāsissaṃ,</p>\r\n<p class="gathalast">Iti pacchānutappati.</p>\r\n<p class="hangnum"><a name="para200"></a><span class="paranum">200</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-200-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',366,replace(replace('<p class="gatha1">Sādhu <a name="M0.0366"></a> hoti tapo ciṇṇo,</p>\r\n<p class="gatha2">Santo ca payirupāsito;</p>\r\n<p class="gatha3">Na pubbeva tapociṇṇo,</p>\r\n<p class="gathalast">Iti pacchānutappati.</p>\r\n<p class="hangnum"><a name="para201"></a><span class="paranum">201</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-201-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',368,replace(replace('<p class="gatha1">Yo <a name="M0.0368"></a> ca etāni ṭhānāni,</p>\r\n<p class="gatha2">Yoniso paṭipajjati;</p>\r\n<p class="gatha3">Karaṃ purisa-kiccāni,</p>\r\n<p class="gathalast">Sa pacchā nānutappati.</p>\r\n<p class="hangnum"><a name="para202"></a><span class="paranum">202</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-202-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',370,replace(replace('<p class="gatha1">Na <a name="M0.0370"></a> sādhāraṇa-dārassa,</p>\r\n<p class="gatha2">Na bhuñje sādhumekako;</p>\r\n<p class="gatha3">Na seve lokāyatikaṃ,</p>\r\n<p class="gathalast">Netaṃ paññāya vaḍḍhanaṃ.</p>\r\n<p class="hangnum"><a name="para203"></a><span class="paranum">203</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-203-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',372,replace(replace('<p class="gatha1">Sīlavā <a name="M0.0372"></a> vatta-sampanno,</p>\r\n<p class="gatha2">A-ppamatto vicakkhaṇo;</p>\r\n<p class="gatha3">Nivātta-vutti atthaddho,</p>\r\n<p class="gathalast">Surato sakhito mudu.</p>\r\n<p class="hangnum"><a name="para204"></a><span class="paranum">204</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-204-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',374,replace(replace('<p class="gatha1">Saṅgahetā <a name="M0.0374"></a> ca mittānaṃ,</p>\r\n<p class="gatha2">Saṃvibhāgī vīdhānavā;</p>\r\n<p class="gatha3">Tappeyya anna-pānena,</p>\r\n<p class="gathalast">Sadā samaṇa-brāhmaṇe.</p>\r\n<p class="hangnum"><a name="para205"></a><span class="paranum">205</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-205-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',376,replace(replace('<p class="gatha1">Dhamma-kāmo <a name="M0.0376"></a> sutā-dhāro,</p>\r\n<p class="gatha2">Bhaveyya paripucchako;</p>\r\n<p class="gatha3">Sakkaccaṃ payirupāseyya,</p>\r\n<p class="gathalast">Sīlavante bahussutte.</p>\r\n<p class="hangnum"><a name="para206"></a><span class="paranum">206</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-206-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',377,replace(replace('<p class="gatha1">Gharamāvasamānassa<a name="M0.0377"></a>,</p>\r\n<p class="gatha2">Gahaṭṭhassa sakaṃ gharaṃ;</p>\r\n<p class="gatha3">Khemā vutti siyā evaṃ,</p>\r\n<p class="gathalast">Evaṃ nu assa saṅgaho.</p>\r\n<p class="hangnum"><a name="para207"></a><span class="paranum">207</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-207-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',378,replace(replace('<p class="gatha1">A-byapajjo <a name="M0.0378"></a> siyā evaṃ,</p>\r\n<p class="gatha2">Sacca-vādī ca māṇavo;</p>\r\n<p class="gatha3">Asmā lokā paraṃ lokaṃ,</p>\r\n<p class="gathalast">Evaṃ pecca na socati.</p>\r\n<p class="hangnum"><a name="para208"></a><span class="paranum">208</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-208-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',379,replace(replace('<p class="gatha1">Sukhā <a name="M0.0379"></a> matteyyatā loke,</p>\r\n<p class="gatha2">Atho petteyyatā sukhā;</p>\r\n<p class="gatha3">Sukhā sāmaññatā loke,</p>\r\n<p class="gathalast">Atho brahmaññatā sukhā.</p>\r\n<p class="hangnum"><a name="para209"></a><span class="paranum">209</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-209-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',381,replace(replace('<p class="gatha1">Pathavī <a name="M0.0381"></a> veḷukaṃ pattaṃ,</p>\r\n<p class="gatha2">Cakkavāḷaṃ sucipphalaṃ;</p>\r\n<p class="gatha3">Sineru vammiko khuddo,</p>\r\n<p class="gathalast">Samuddo pātiko yathā.</p>\r\n<p class="hangnum"><a name="para210"></a><span class="paranum">210</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-210-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',382,replace(replace('<p class="gatha1">Brahmāti <a name="M0.0382"></a> mātā-pitaro,</p>\r\n<p class="gatha2">Pubbācariyāti vuccare;</p>\r\n<p class="gatha3">Āhuneyyā ca puttānaṃ,</p>\r\n<p class="gathalast">Pajānamanukampakā.</p>\r\n<p class="hangnum"><a name="para211"></a><span class="paranum">211</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-211-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',384,replace(replace('<p class="gatha1">Tasmā <a name="M0.0384"></a> hi ne namaseyya,</p>\r\n<p class="gatha2">Sakkareyya ca paṇḍito;</p>\r\n<p class="gatha3">Annena atho pānena,</p>\r\n<p class="gathalast">Vatthena sayanena ca.</p>\r\n<p class="chapter">Sādhujana-niddesa</p>\r\n<p class="hangnum"><a name="para212"></a><span class="paranum">212</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-212-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',386,replace(replace('<p class="gatha1">Kāya-kammaṃ <a name="M0.0386"></a> suci tesaṃ,</p>\r\n<p class="gatha2">Vācā-kammaṃ anāvilaṃ;</p>\r\n<p class="gatha3">Mano-kammaṃ suci-suddhaṃ,</p>\r\n<p class="gathalast">Tādisā sujanā narā.</p>\r\n<p class="hangnum"><a name="para213"></a><span class="paranum">213</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-213-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',387,replace(replace('<p class="gatha1">Seṭṭha-vittaṃ <a name="M0.0387"></a> sutaṃ paññā,</p>\r\n<p class="gatha2">Saddhanaṃ sattadhā hotti;</p>\r\n<p class="gatha3">Saddhā sīlaṃ sutaṃ cāgo,</p>\r\n<p class="gathalast">Paññā ceva hirottappaṃ.</p>\r\n<p class="hangnum"><a name="para214"></a><span class="paranum">214</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-214-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',389,replace(replace('<p class="gatha1">Saddhammāpi <a name="M0.0389"></a> ca satteva,</p>\r\n<p class="gatha2">Saddhā hirī ca ottappaṃ;</p>\r\n<p class="gatha3">Bāhussaccaṃ dhiro ceva,</p>\r\n<p class="gathalast">Sati paññā ca iccevaṃ.</p>\r\n<p class="hangnum"><a name="para215"></a><span class="paranum">215</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-215-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',390,replace(replace('<p class="gatha1">Hirī-ottappa-sampannā<a name="M0.0390"></a>,</p>\r\n<p class="gatha2">Suktka-dhamma-samāhitā;</p>\r\n<p class="gatha3">Santo sappurisā loke,</p>\r\n<p class="gathalast">Deva-dhammāti vuccare.</p>\r\n<p class="hangnum"><a name="para216"></a><span class="paranum">216</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-216-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',392,replace(replace('<p class="gatha1">Saddho <a name="M0.0392"></a> hirimā ottappī,</p>\r\n<p class="gatha2">Vīro pañño sa-gāravo;</p>\r\n<p class="gatha3">Bhabbo āpajjituṃ buddhiṃ,</p>\r\n<p class="gathalast">Virūḷhiñca vipullataṃ.</p>\r\n<p class="hangnum"><a name="para217"></a><span class="paranum">217</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-217-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',393,replace(replace('<p class="gatha1">Yo <a name="M0.0393"></a> ve kataññū kata-vedi dhīro,</p>\r\n<p class="gatha2">Kalyāṇa-mitto daḷhaa-bhatto ca hoti;</p>\r\n<p class="gatha3">Dukkhittassa sakkaccaṃ karoti kiccaṃ,</p>\r\n<p class="gathalast">Tathāvidhaṃ sappurisaṃ vadanti.</p>\r\n<p class="hangnum"><a name="para218"></a><span class="paranum">218</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-218-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',395,replace(replace('<p class="gatha1">Mātā-petti-bharaṃ <a name="M0.0395"></a> jantuṃ,</p>\r\n<p class="gatha2">Kule jeṭṭhāpacāyinaṃ;</p>\r\n<p class="gatha3">Saṇhaṃ sakhila-sambhāsaṃ,</p>\r\n<p class="gathalast">Pesuṇeyappahāyinaṃ.</p>\r\n<p class="hangnum"><a name="para219"></a><span class="paranum">219</span>.</p>\r\n<p class="gatha1"> Macchera-vinaye yuttaṃ,</p>\r\n<p class="gatha2">Saccaṃ kodhābhituṃ naraṃ;</p>\r\n<p class="gatha3">Taṃ ve devā tāvatiṃsā,</p>\r\n<p class="gathalast">Āhu sappuriso iti.</p>\r\n<p class="hangnum"><a name="para220"></a><span class="paranum">220</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-219-220-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',398,replace(replace('<p class="gatha1">A-ppamādena <a name="M0.0398"></a> maghavā,</p>\r\n<p class="gatha2">Devānaṃ seṭṭhataṃ gato;</p>\r\n<p class="gatha3">A-ppamādaṃ pasaṃsanti,</p>\r\n<p class="gathalast">Pamādo garahito sadā.</p>\r\n<p class="hangnum"><a name="para221"></a><span class="paranum">221</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-221-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',399,replace(replace('<p class="gatha1">Dānaṃ <a name="M0.0399"></a> sīlaṃ pariccāgaṃ,</p>\r\n<p class="gatha2">Ājjavaṃ maddavaṃ tapaṃ;</p>\r\n<p class="gatha3">A-kodhaṃ a-vihiṃsañca,</p>\r\n<p class="gathalast">Khantīca a-virodhanaṃ.</p>\r\n<p class="hangnum"><a name="para222"></a><span class="paranum">222</span>.</p>\r\n<p class="gatha1"> Iccete kusale dhamme,</p>\r\n<p class="gatha2">Ṭhite passāmi attani;</p>\r\n<p class="gatha3">Tato me jāyate pīti,</p>\r\n<p class="gathalast">Somanassañcanappakaṃ.</p>\r\n<p class="hangnum"><a name="para223"></a><span class="paranum">223</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-222-223-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',402,replace(replace('<p class="gatha1">Nanu <a name="M0.0402"></a> teyeva santā no,</p>\r\n<p class="gatha2">Sāgarā na kulācalā;</p>\r\n<p class="gatha3">Manaṃpi mariyādaṃ ye,</p>\r\n<p class="gathalast">Saṃvaṭṭepi jahanti no.</p>\r\n<p class="hangnum"><a name="para224"></a><span class="paranum">224</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-224-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',403,replace(replace('<p class="gatha1">Na <a name="M0.0403"></a> puppha-gandho paṭivātameti,</p>\r\n<p class="gatha2">Na candanaṃ taggara mallikā vā;</p>\r\n<p class="gatha3">Satañca gandho paṭivātameti,</p>\r\n<p class="gathalast">Sabbā disā sappuriso pavāyati.</p>\r\n<p class="hangnum"><a name="para225"></a><span class="paranum">225</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-225-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',405,replace(replace('<p class="gatha1">Tepi <a name="M0.0405"></a> loka-hitā sattā,</p>\r\n<p class="gatha2">Sūriyo candimā api;</p>\r\n<p class="gatha3">Atthaṃ passa gamissanti,</p>\r\n<p class="gathalast">Niyamo kena laṅghate.</p>\r\n<p class="hangnum"><a name="para226"></a><span class="paranum">226</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-226-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',406,replace(replace('<p class="gatha1">Satthā <a name="M0.0406"></a> deva-manussānaṃ,</p>\r\n<p class="gatha2">Vasī sopi munissaro;</p>\r\n<p class="gatha3">Gatova nibbutiṃ sabbe,</p>\r\n<p class="gathalast">Saṅkhārā na hi sassatā.</p>\r\n<p class="hangnum"><a name="para227"></a><span class="paranum">227</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-227-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',407,replace(replace('<p class="gatha1">Kareyya <a name="M0.0407"></a> kusalaṃ sabbaṃ,</p>\r\n<p class="gatha2">Sivaṃ nibbānamāvahaṃ;</p>\r\n<p class="gatha3">Sareyyaa a-niccaṃ khandhaṃ,</p>\r\n<p class="gathalast">Nibbidā-ñāṇa-gocaraṃ.</p>\r\n<p class="hangnum"><a name="para228"></a><span class="paranum">228</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-228-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',408,replace(replace('<p class="gatha1">Yātānuyāyī <a name="M0.0408"></a> ca bhavāhi māṇava,</p>\r\n<p class="gatha2">Allañca pāṇiṃ parivajjayassu;</p>\r\n<p class="gatha3">Mā cassu mittesu kadāci dubbhi,</p>\r\n<p class="gathalast">Mā ca vasaṃ a-satīnaṃ gaccha.</p>\r\n<p class="hangnum"><a name="para229"></a><span class="paranum">229</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-229-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',411,replace(replace('<p class="gatha1">A-sandhavaṃ <a name="M0.0411"></a> nāpi ca diṭṭha-pubbaṃ,</p>\r\n<p class="gatha2">Yo āsanenāpi nimantayeyya;</p>\r\n<p class="gatha3">Tasseva atthaṃ puriso kareyya,</p>\r\n<p class="gathalast">Yātānuyāyītitamāhupaṇḍitā.</p>\r\n<p class="hangnum"><a name="para230"></a><span class="paranum">230</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-230-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',412,replace(replace('<p class="gatha1">Yassekarattipi <a name="M0.0412"></a> ghare vaseyya,</p>\r\n<p class="gatha2">Yatthanna-pānaṃ puriso labheyya;</p>\r\n<p class="gatha3">Na tassa pāpaṃ manasāpi cinteyya,</p>\r\n<p class="gathalast">A-dubbha-pāṇi dahate mitta-dubbho.</p>\r\n<p class="subhead">Tatīya sādhunara</p>\r\n<p class="hangnum"><a name="para231"></a><span class="paranum">231</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-231-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',414,replace(replace('<p class="gatha1">Yassa <a name="M0.0414"></a> rukkhassa chāyāya,</p>\r\n<p class="gatha2">Nisīdeyya sayeyya vā;</p>\r\n<p class="gatha3">Na tassa sākhaṃ bhañjeyya,</p>\r\n<p class="gathalast">Mitta-dubbho hi pāpako.</p>\r\n<p class="subhead">Catuttha sādhunara</p>\r\n<p class="hangnum"><a name="para232"></a><span class="paranum">232</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-232-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',415,replace(replace('<p class="gatha1">Puṇṇaṃpi <a name="M0.0415"></a> ce maṃ pathaviṃ dhanena,</p>\r\n<p class="gatha2">Dajjitthiyā puriso sammatāya;</p>\r\n<p class="gatha3">Saddhā khaṇaṃ atimaññeyya taṃpi,</p>\r\n<p class="gathalast">Tāsaṃ vasaṃ a-satīnaṃ na gacche.</p>\r\n<p class="hangnum"><a name="para233"></a><span class="paranum">233</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-233-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',417,replace(replace('<p class="gatha1">Evaṃ <a name="M0.0417"></a> kho yātaṃ anuyāyī hoti,</p>\r\n<p class="gatha2">Allañca pāṇiṃ dahate punevaṃ;</p>\r\n<p class="gatha3">A-satī ca sā so pana mittaṃ-dubbho,</p>\r\n<p class="gathalast">So dhammiko hohi jahassu a-dhammaṃ.</p>\r\n<p class="chapter">Kāyakhamanīya-niddesa</p>\r\n<p class="hangnum"><a name="para234"></a><span class="paranum">234</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-234-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',420,replace(replace('<p class="gatha1">Abhivādana-sīlissa<a name="M0.0420"></a>,</p>\r\n<p class="gatha2">Niccaṃ vuḍḍhāpacāyino;</p>\r\n<p class="gatha3">Cattāro dhammā vaḍḍhanti,</p>\r\n<p class="gathalast">Āyu vaṇṇo sukhaṃ balaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',421,replace(replace('<p class="bodytext"><a name="para235"></a><span class="paranum">235</span>. Pañcime <a name="M0.0421"></a> bhikkhave dhammā āyussā, katame pañca. Sappāya-kārī hoti. Sappāyeca mattaṃ jānāti. Pariṇattabhojī ca hoti. Kāla-cārī ca, brahma-cārīca. Ime kho bhikkhave pañca dhammā āyussāti.</p>','\r',char(13)),'\n',char(10)),'-235-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',424,replace(replace('<p class="bodytext"><a name="para236"></a><span class="paranum">236</span>. Pañcime <a name="M0.0424"></a> bhikkhave dhammā āyussā, katame pañca. Sappāya-kārī hoti. Sappāyeca mattaṃ jānāti. Pariṇatabhojīcahoti. Sīlavāca, kalyāṇa mittoca. Ime kho bhikkhave pañca dhammā āyussāti.</p>\r\n<p class="hangnum"><a name="para237"></a><span class="paranum">237</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-236-237-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',427,replace(replace('<p class="gatha1">Pañca-sīlaṃ <a name="M0.0427"></a> samādāya,</p>\r\n<p class="gatha2">Samaṃ katvā dine dine;</p>\r\n<p class="gatha3">Satimā paññavā hutvā,</p>\r\n<p class="gathalast">Care sabbiriyāpathe.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',428,replace(replace('<p class="bodytext"><a name="para238"></a><span class="paranum">238</span>. Pañcime <a name="M0.0428"></a> bhikkhave caṅkame ānisaṃsā, katame pañca, addhānakkhamo hoti. Padhānakkhamo hoti. Appābādho hoti. Asitaṃpītaṃkhāyitaṃsāyitaṃ sammā pariṇāmaṃ gacchati. Caṅkamādhigato samādhi ciraṭṭhitiko hoti. Imekho bhikkhave pañca caṅkame āni saṃsāti.</p>\r\n<p class="hangnum"><a name="para239"></a><span class="paranum">239</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-238-239-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',431,replace(replace('<p class="gatha1">Parissāvana-dānañca<a name="M0.0431"></a>,</p>\r\n<p class="gatha2">Āvāsa-dānameva ca;</p>\r\n<p class="gatha3">Gilāna-vatthu-dānañca,</p>\r\n<p class="gathalast">Dātabbaṃ manujādhipa.</p>\r\n<p class="hangnum"><a name="para240"></a><span class="paranum">240</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-240-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',432,replace(replace('<p class="gatha1">Kātabbaṃ <a name="M0.0432"></a> jiṇṇakāvāsaṃ,</p>\r\n<p class="gatha2">Paṭisaṅkharaṇaṃ tathā;</p>\r\n<p class="gatha3">Pañca-sīla-samādānaṃ,</p>\r\n<p class="gatha2">Katvā taṃ sādhu-rakkhitaṃ;</p>\r\n<p class="gatha3">Uposathopavāso ca,</p>\r\n<p class="gathalast">Kātabboposathe iti.</p>\r\n<p class="hangnum"><a name="para241"></a><span class="paranum">241</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-241-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',434,replace(replace('<p class="gatha1">Ati-bhottā <a name="M0.0434"></a> roga-mūlaṃ,</p>\r\n<p class="gatha2">Āyukkhayaṃ karoti ve;</p>\r\n<p class="gatha3">Tasmā taṃ ati-bhuttiṃva,</p>\r\n<p class="gathalast">Parihareyya paṇḍito.</p>\r\n<p class="hangnum"><a name="para242"></a><span class="paranum">242</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-242-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',435,replace(replace('<p class="gatha1">A-jiṇṇe <a name="M0.0435"></a> bhojanaṃ visaṃ,</p>\r\n<p class="gatha2">Dulladdhe a-vicārake;</p>\r\n<p class="gatha3">Jiṇṇe su-laddhe vicāre,</p>\r\n<p class="gathalast">Na vajjaṃ sabba-bhojanaṃ.</p>\r\n<p class="hangnum"><a name="para243"></a><span class="paranum">243</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-243-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',437,replace(replace('<p class="gatha1">Cattāro <a name="M0.0437"></a> pañca ālope,</p>\r\n<p class="gatha2">Ābhutvā udakaṃ pive;</p>\r\n<p class="gatha3">Alaṃ phāsu-vihārāya;</p>\r\n<p class="gathalast">Pahitattassa bhikkhuno.</p>\r\n<p class="hangnum"><a name="para244"></a><span class="paranum">244</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-244-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',438,replace(replace('<p class="gatha1">Manujassa <a name="M0.0438"></a> sadā satimato,</p>\r\n<p class="gatha2">Mattaṃ jānato laddha-bhojanaṃ;</p>\r\n<p class="gatha3">Tanukassa bhavanti vedanā,</p>\r\n<p class="gathalast">Sanikaṃ jīrati āyu pālayaṃ.</p>\r\n<p class="hangnum"><a name="para245"></a><span class="paranum">245</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-245-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',440,replace(replace('<p class="gatha1">Garūnaṃ <a name="M0.0440"></a> aḍḍha-sohiccaṃ,</p>\r\n<p class="gatha2">Lahūnaṃ nāti-kittiyā;</p>\r\n<p class="gatha3">Mattā-pamāṇaṃ niddiṭṭhaṃ,</p>\r\n<p class="gathalast">Sukhaṃ jīrati tāvatā.</p>\r\n<p class="hangnum"><a name="para246"></a><span class="paranum">246</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-246-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',441,replace(replace('<p class="gatha1">Toyābhāve <a name="M0.0441"></a> pipāsattā,</p>\r\n<p class="gatha2">Khaṇā pāṇehi muccate;</p>\r\n<p class="gatha3">Tasmā sabbāsuvatthāsu,</p>\r\n<p class="gathalast">Deyyaṃ vāriṃ pipāsaye.</p>\r\n<p class="hangnum"><a name="para247"></a><span class="paranum">247</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-247-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',442,replace(replace('<p class="gatha1">Sītodakaṃ <a name="M0.0442"></a> payo khuddaṃ,</p>\r\n<p class="gatha2">Ghatamekekaso dviso;</p>\r\n<p class="gatha3">Tisso samaggamatha vā,</p>\r\n<p class="gathalast">Page pitaṃ yuvattadaṃ.</p>\r\n<p class="hangnum"><a name="para248"></a><span class="paranum">248</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-248-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',444,replace(replace('<p class="gatha1">Annaṃ <a name="M0.0444"></a> brahmā rase viṇhu,</p>\r\n<p class="gatha2">Bhutte ceva mahesaro;</p>\r\n<p class="gatha3">Evaṃ ñatvātu yo bhuñje,</p>\r\n<p class="gathalast">Anna-dosaṃ na limpate.</p>\r\n<p class="hangnum"><a name="para249"></a><span class="paranum">249</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-249-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',445,replace(replace('<p class="gatha1">Kattikassantimo <a name="M0.0445"></a> bhāgo,</p>\r\n<p class="gatha2">Yaṃ cādo miga-māsajo;</p>\r\n<p class="gatha3">Tāvubho yama-dāṭhākhyo,</p>\r\n<p class="gathalast">Laghvāhārova jīvati.</p>\r\n<p class="hangnum"><a name="para250"></a><span class="paranum">250</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-250-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',447,replace(replace('<p class="gatha1">Satthānukula-cariyā<a name="M0.0447"></a>,</p>\r\n<p class="gatha2">Cittaññāvasavattinā;</p>\r\n<p class="gatha3">Buddhi-rakkhilitatthena,</p>\r\n<p class="gathalast">Paripuṇṇaṃ rasāyanaṃ.</p>\r\n<p class="hangnum"><a name="para250"></a><span class="paranum">250</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-250-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',448,replace(replace('<p class="gatha1">A-jātiyā <a name="M0.0448"></a> a-jātānaṃ,</p>\r\n<p class="gatha2">Jātānaṃ vinivattiyā;</p>\r\n<p class="gatha3">Rogānaṃ yo vidhi diṭṭho,</p>\r\n<p class="gathalast">Taṃ sukhatthī samācare.</p>\r\n<p class="hangnum"><a name="para252"></a><span class="paranum">252</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-252-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',449,replace(replace('<p class="gatha1">Ārogyaṃ <a name="M0.0449"></a> paramā lābhā,</p>\r\n<p class="gatha2">Santuṭṭhi paramaṃ dhanaṃ;</p>\r\n<p class="gatha3">Vissāsā paramā ñāti,</p>\r\n<p class="gathalast">Nibbānaṃ paramaṃ sukhaṃ.</p>\r\n<p class="chapter">Pakiṇṇaka-niddesa</p>\r\n<p class="hangnum"><a name="para253"></a><span class="paranum">253</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-253-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',460,replace(replace('<p class="gatha1">Kumudaṃ <a name="M0.0460"></a> ko pabodhayi,</p>\r\n<p class="gatha2">Nātho ravindu paṇḍito;</p>\r\n<p class="gatha3">Kamalaṃ ko kumudaṃ ko,</p>\r\n<p class="gathalast">Narapaṃ ko pabodhayi.</p>\r\n<p class="hangnum"><a name="para254"></a><span class="paranum">254</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-254-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',462,replace(replace('<p class="gatha1">Cittena <a name="M0.0462"></a> niyyati loko,</p>\r\n<p class="gatha2">Cittena parikassati;</p>\r\n<p class="gatha3">Cittassa eka-dhammassa,</p>\r\n<p class="gathalast">Sabbeva vasamanvagū.</p>\r\n<p class="hangnum"><a name="para255"></a><span class="paranum">255</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-255-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',464,replace(replace('<p class="gatha1">Samaṇo <a name="M0.0464"></a> rājānurājā,</p>\r\n<p class="gatha2">Senāpati mahā-matto;</p>\r\n<p class="gatha3">Dhammaṭṭho paṇḍito disvā,</p>\r\n<p class="gathalast">Paccakkhatthaṃ na kāriyā.</p>\r\n<p class="hangnum"><a name="para256"></a><span class="paranum">256</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-256-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',465,replace(replace('<p class="gatha1">Dīpo <a name="M0.0465"></a> nava-disaṃ tejo,</p>\r\n<p class="gatha2">Na heṭṭhā ca tathā sakaṃ;</p>\r\n<p class="gatha3">Para-vajjaṃ vidū passe,</p>\r\n<p class="gathalast">Saka-vajjaṃpi passatu.</p>\r\n<p class="hangnum"><a name="para257"></a><span class="paranum">257</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-257-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',467,replace(replace('<p class="gatha1">Sa-phalaṃ <a name="M0.0467"></a> paṇḍito loke,</p>\r\n<p class="gatha2">Sa-kāraṇaṃ vacaṃ bhaṇe;</p>\r\n<p class="gatha3">A-kāraṇaṃphalaṃ bālo,</p>\r\n<p class="gathalast">Idaṃ ubhaya-lakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para258"></a><span class="paranum">258</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-258-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',468,replace(replace('<p class="gatha1">Tassa <a name="M0.0468"></a> vācāya jāneyya,</p>\r\n<p class="gatha2">Kuṭilaṃ bāla-paṇḍitaṃ;</p>\r\n<p class="gatha3">Vācā-rūpaṃ mittaṃ kare,</p>\r\n<p class="gathalast">Vācā-rūpaṃ dhuvaṃ jahe.</p>\r\n<p class="hangnum"><a name="para259"></a><span class="paranum">259</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-259-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',470,replace(replace('<p class="gatha1">Duccintitassa <a name="M0.0470"></a> cintā ca,</p>\r\n<p class="gatha2">Dubbhāsitassa bhāsanā;</p>\r\n<p class="gatha3">Dukkammassa katañcāti,</p>\r\n<p class="gathalast">Etaṃ bālassa lakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para260"></a><span class="paranum">260</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-260-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',471,replace(replace('<p class="gatha1">Su-cintitassa <a name="M0.0471"></a> cintā ca,</p>\r\n<p class="gatha2">Su-bhāsitassa bhāsanā;</p>\r\n<p class="gatha3">Su-kammassa katañcāti,</p>\r\n<p class="gathalast">Etaṃ dhīrassa lakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para261"></a><span class="paranum">261</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-261-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',473,replace(replace('<p class="gatha1">A-nayaṃ <a name="M0.0473"></a> nayati dummedho,</p>\r\n<p class="gatha2">A-dhurāyaṃ ni-yuñjati;</p>\r\n<p class="gatha3">Dunnayo seyyaso hoti,</p>\r\n<p class="gatha2">Sammā vutto pakuppati;</p>\r\n<p class="gatha3">Vinayaṃ so na jānāti,</p>\r\n<p class="gathalast">Sādhu tassa a-dassanaṃ.</p>\r\n<p class="hangnum"><a name="para262"></a><span class="paranum">262</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-262-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',476,replace(replace('<p class="gatha1">Nayaṃ <a name="M0.0476"></a> nayati medhāvī,</p>\r\n<p class="gatha2">A-dhurāyaṃ na yuñjati;</p>\r\n<p class="gatha3">Su-nayo seyyaso hoti,</p>\r\n<p class="gatha2">Sammā vutto na kuppati;</p>\r\n<p class="gatha3">Vinayaṃ so pajānāti,</p>\r\n<p class="gathalast">Sādhu tena samāgamo.</p>\r\n<p class="hangnum"><a name="para263"></a><span class="paranum">263</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-263-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',479,replace(replace('<p class="gatha1">A-nāyakā <a name="M0.0479"></a> vinassanti,</p>\r\n<p class="gatha2">Nassanti bahu-nāyakā;</p>\r\n<p class="gatha3">Thī-nāyakā vinassanti,</p>\r\n<p class="gathalast">Nassanti susu-nāyakā.</p>\r\n<p class="hangnum"><a name="para264"></a><span class="paranum">264</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-264-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',481,replace(replace('<p class="gatha1">Jeṭṭho <a name="M0.0481"></a> kammesu nīcānaṃ,</p>\r\n<p class="gatha2">Jānaṃjānaṃva ācare;</p>\r\n<p class="gatha3">A-jānevaṃ kare jānaṃ,</p>\r\n<p class="gathalast">Nīco eti bhayaṃ piyaṃ.</p>\r\n<p class="hangnum"><a name="para265"></a><span class="paranum">265</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-265-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',482,replace(replace('<p class="gatha1">Kammaṃ <a name="M0.0482"></a> dujjana-sāruppaṃ,</p>\r\n<p class="gatha2">Dudhā sujana-sāruppaṃ;</p>\r\n<p class="gatha3">Dujjanaṃ tesu dukkamme,</p>\r\n<p class="gathalast">Su-kamme sujanaṃ icche.</p>\r\n<p class="hangnum"><a name="para266"></a><span class="paranum">266</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-266-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',484,replace(replace('<p class="gatha1">Paṇḍito <a name="M0.0484"></a> verī bālo ca,</p>\r\n<p class="gatha2">Dujjayo bāla-verito;</p>\r\n<p class="gatha3">Paṇḍitaṃ-verī pamādena,</p>\r\n<p class="gathalast">Na taṃ jayo hi sabbadā.</p>\r\n<p class="hangnum"><a name="para267"></a><span class="paranum">267</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-267-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',485,replace(replace('<p class="gatha1">Guyhassa <a name="M0.0485"></a> hi guyhameva sādhu,</p>\r\n<p class="gatha2">Na hi guyhassa pasatthamāvi-kammaṃ;</p>\r\n<p class="gatha3">A-nipphannatāya saheyya dhīro,</p>\r\n<p class="gathalast">Nipphannatthova yathā-sukhaṃ bhaṇeyya.</p>\r\n<p class="hangnum"><a name="para268"></a><span class="paranum">268</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-268-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',486,replace(replace('<p class="gatha1">Guyhamatthaṃ <a name="M0.0486"></a> na vivareyya,</p>\r\n<p class="gatha2">Rakkheyya naṃ yathā nidhiṃ;</p>\r\n<p class="gatha3">Na hī pātukato sādhu,</p>\r\n<p class="gathalast">Guyho attho pajānatā.</p>\r\n<p class="hangnum"><a name="para269"></a><span class="paranum">269</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-269-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',487,replace(replace('<p class="gatha1">Thiyā <a name="M0.0487"></a> guyhaṃ na saṃseyya,</p>\r\n<p class="gatha2">A-mittassa ca paṇḍito;</p>\r\n<p class="gatha3">Yo cāmisena saṃhīro,</p>\r\n<p class="gathalast">Hadaya-ttheno ca yo naro.</p>\r\n<p class="hangnum"><a name="para270"></a><span class="paranum">270</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-270-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',489,replace(replace('<p class="gatha1">Vivicca <a name="M0.0489"></a> bhāseyya divā rahassaṃ,</p>\r\n<p class="gatha2">Rattiṃ giraṃ nāti-velaṃ pamuñce;</p>\r\n<p class="gatha3">Upassutikā hi suṇanti mantaṃ,</p>\r\n<p class="gathalast">Tasmā hi manto khippamupeti bhedaṃ.</p>\r\n<p class="hangnum"><a name="para271"></a><span class="paranum">271</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-271-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',491,replace(replace('<p class="gatha1">Na <a name="M0.0491"></a> pakāsati guyhaṃ yo,</p>\r\n<p class="gatha2">So guyhaṃ paṭiguyhati;</p>\r\n<p class="gatha3">Bhayesu na jahe kicce,</p>\r\n<p class="gathalast">Su-mittonucaro bhave.</p>\r\n<p class="hangnum"><a name="para272"></a><span class="paranum">272</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-272-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',492,replace(replace('<p class="gatha1">Karoti <a name="M0.0492"></a> dukkaraṃ sādhuṃ,</p>\r\n<p class="gatha2">Ujuṃ khamati dukkhamaṃ;</p>\r\n<p class="gatha3">Duddadaṃ sāmaṃ dadāti,</p>\r\n<p class="gathalast">Yo su-mitto have bhave.</p>\r\n<p class="hangnum"><a name="para273"></a><span class="paranum">273</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-273-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',494,replace(replace('<p class="gatha1">Piya-vācā <a name="M0.0494"></a> sadā mitto,</p>\r\n<p class="gatha2">Piya-vatthuṃ na yācanā;</p>\r\n<p class="gatha3">Icchāgatena dānena,</p>\r\n<p class="gatha2">Su-daḷho su-ppiyo have;</p>\r\n<p class="gatha3">Tadaṅgato ca hīnena,</p>\r\n<p class="gathalast">A-ppiyo bhijjano bhave.</p>\r\n<p class="hangnum"><a name="para274"></a><span class="paranum">274</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-274-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',496,replace(replace('<p class="gatha1">Dehīti <a name="M0.0496"></a> yācane hirī,</p>\r\n<p class="gatha2">Sirī ca kāya-devatā;</p>\r\n<p class="gatha3">Palāyanti siricchito,</p>\r\n<p class="gathalast">Na yāce para-santakaṃ.</p>\r\n<p class="hangnum"><a name="para275"></a><span class="paranum">275</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-275-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',497,replace(replace('<p class="gatha1">Svāno <a name="M0.0497"></a> laddhāna nimmaṃsaṃ,</p>\r\n<p class="gatha2">Aṭṭhiṃ tuṭṭho pamodati;</p>\r\n<p class="gatha3">Sakantikaṃ migaṃ sīho,</p>\r\n<p class="gathalast">Hitvā hatthiṃnudhāvati.</p>\r\n<p class="hangnum"><a name="para276"></a><span class="paranum">276</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-276-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',499,replace(replace('<p class="gatha1">Evaṃ <a name="M0.0499"></a> chandānurūpena,</p>\r\n<p class="gatha2">Jano āsīsate phalaṃ;</p>\r\n<p class="gatha3">Mahā chandā mahantaṃva,</p>\r\n<p class="gathalast">Hīnaṃva hīna-chandakā.</p>\r\n<p class="hangnum"><a name="para277"></a><span class="paranum">277</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-277-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',501,replace(replace('<p class="gatha1">Nānā-chandā <a name="M0.0501"></a> mahārāja,</p>\r\n<p class="gatha2">Ekāgāre vasāmase;</p>\r\n<p class="gatha3">Ahaṃ gāma-varaṃ icche,</p>\r\n<p class="gathalast">Brāhmaṇī ca gavaṃ sataṃ.</p>\r\n<p class="hangnum"><a name="para278"></a><span class="paranum">278</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-278-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',502,replace(replace('<p class="gatha1">Putto <a name="M0.0502"></a> ca ājañña-rathaṃ,</p>\r\n<p class="gatha2">Kaññā ca maṇi-kuṇḍalaṃ;</p>\r\n<p class="gatha3">Yā cesā puṇṇakā jammī,</p>\r\n<p class="gathalast">Ujukkhalaṃbhi-kaṅkhati.</p>\r\n<p class="hangnum"><a name="para279"></a><span class="paranum">279</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-279-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',504,replace(replace('<p class="gatha1">Ṭhānaṃ <a name="M0.0504"></a> mitte dhane kamme,</p>\r\n<p class="gatha2">Satussāhe su-labbhitaṃ;</p>\r\n<p class="gatha3">Taṃ daḷhaṃ dukkaraṃ kare,</p>\r\n<p class="gathalast">Paññā-sati-samādhinā.</p>\r\n<p class="hangnum"><a name="para280"></a><span class="paranum">280</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-280-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',505,replace(replace('<p class="gatha1">Bhesajje <a name="M0.0505"></a> vihite suddha,</p>\r\n<p class="gatha2">Buddhādi-ratanattaye;</p>\r\n<p class="gatha3">Pasādamācare niccaṃ,</p>\r\n<p class="gathalast">Sajjane sa-guṇepi ca.</p>\r\n<p class="hangnum"><a name="para281"></a><span class="paranum">281</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-281-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',507,replace(replace('<p class="gatha1">Rājā <a name="M0.0507"></a> raṭṭhena dhātuso,</p>\r\n<p class="gatha2">Bālo pāpehi dummano,</p>\r\n<p class="gatha3">Alaṅkārena itthīpi,</p>\r\n<p class="gathalast">Kāmehi ca na tappati.</p>\r\n<p class="hangnum"><a name="para282"></a><span class="paranum">282</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-282-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',508,replace(replace('<p class="gatha1">Appiccho <a name="M0.0508"></a> ca dhutaṅgena,</p>\r\n<p class="gatha2">Āraddho vīriyena hi;</p>\r\n<p class="gatha3">Visārado parisāya,</p>\r\n<p class="gatha2">Pariccāgena dāyako;</p>\r\n<p class="gatha3">Savanena su-dhammaṃpi,</p>\r\n<p class="gathalast">Na tappativa paṇḍito.</p>\r\n<p class="hangnum"><a name="para283"></a><span class="paranum">283</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-283-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',510,replace(replace('<p class="gatha1">Jeṭṭhassa <a name="M0.0510"></a> sitaṃ hasitaṃ,</p>\r\n<p class="gatha2">Majjhassa madhurassaraṃ;</p>\r\n<p class="gatha3">Loke aṃsa-siro-kampaṃ,</p>\r\n<p class="gatha2">Jammassa apa-hassitaṃ;</p>\r\n<p class="gatha3">Etesaṃ ati-hassitaṃ,</p>\r\n<p class="gathalast">Hāso hoti yathākkamaṃ.</p>\r\n<p class="hangnum"><a name="para284"></a><span class="paranum">284</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-284-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',513,replace(replace('<p class="gatha1">Natthi <a name="M0.0513"></a> duṭṭhe nayo atthi,</p>\r\n<p class="gatha2">Na dhammo na su-bhāsitaṃ;</p>\r\n<p class="gatha3">Nikkamaṃ duṭṭhe yuñjeyya,</p>\r\n<p class="gathalast">So hi sabbhiṃ na rañjati.</p>\r\n<p class="hangnum"><a name="para285"></a><span class="paranum">285</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-285-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',515,replace(replace('<p class="gatha1">Dullabhaṃ <a name="M0.0515"></a> pakatiṃ vācaṃ,</p>\r\n<p class="gatha2">Dullabho khemako suto;</p>\r\n<p class="gatha3">Dullabhā sadisī jāyā,</p>\r\n<p class="gathalast">Dullabho sa-jano piyo.</p>\r\n<p class="hangnum"><a name="para286"></a><span class="paranum">286</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-286-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',516,replace(replace('<p class="gatha1">Dhajo <a name="M0.0516"></a> rathassa paññāṇaṃ,</p>\r\n<p class="gatha2">Dhūmo paññāṇamaggino;</p>\r\n<p class="gatha3">Rājā raṭṭhassa paññāṇaṃ,</p>\r\n<p class="gathalast">Bhattā paññāṇamitthiyā.</p>\r\n<p class="hangnum"><a name="para287"></a><span class="paranum">287</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-287-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',518,replace(replace('<p class="gatha1">Dunnāriyā <a name="M0.0518"></a> kulaṃ suddhaṃ,</p>\r\n<p class="gatha2">Putto nassati lālanā;</p>\r\n<p class="gatha3">Samiddhi a-nayā bandhu,</p>\r\n<p class="gathalast">Pavāsā madanā hirī.</p>\r\n<p class="hangnum"><a name="para288"></a><span class="paranum">288</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-288-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',520,replace(replace('<p class="gatha1">Mātā <a name="M0.0520"></a> pitā ca puttānaṃ,</p>\r\n<p class="gatha2">Novāde bahu-sāsannaṃ;</p>\r\n<p class="gatha3">Paṇḍitā mātaro appaṃ,</p>\r\n<p class="gathalast">Vadeyyuṃ vajja-dīpanaṃ.</p>\r\n<p class="hangnum"><a name="para289"></a><span class="paranum">289</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-289-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',522,replace(replace('<p class="gatha1">Lāḷaye <a name="M0.0522"></a> pañcha-vassāni,</p>\r\n<p class="gatha2">Dasa-vassāni tāḷaye;</p>\r\n<p class="gatha3">Patte tu soḷase vasse,</p>\r\n<p class="gathalast">Puttaṃ mittaṃvadācare.</p>\r\n<p class="hangnum"><a name="para290"></a><span class="paranum">290</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-290-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',523,replace(replace('<p class="gatha1">Lālane <a name="M0.0523"></a> dhītaraṃ dosā,</p>\r\n<p class="gatha2">Pālane bahavo guṇā;</p>\r\n<p class="gatha3">Dhītuyā kiriyaṃ niccaṃ,</p>\r\n<p class="gathalast">Passantu suṭṭhu mātaro.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',525,replace(replace('<p class="centered">Iti <a name="M0.0525"></a> pakiṇṇaka-niddeso nāma</p>\r\n<p class="centered">Sattamā paricchedo.</p>\r\n<p class="chapter">Sīla-niddesa</p>\r\n<p class="hangnum"><a name="para291"></a><span class="paranum">291</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-291-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',526,replace(replace('<p class="gatha1">Pamādaṃ <a name="M0.0526"></a> bhayato disvā,</p>\r\n<p class="gatha2">A-ppamādañca khemato;</p>\r\n<p class="gatha3">Bhāvetha aṭṭhaṅgikaṃ maggaṃ,</p>\r\n<p class="gathalast">Esā buddhānusāsanī.</p>\r\n<p class="hangnum"><a name="para292"></a><span class="paranum">292</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-292-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',528,replace(replace('<p class="gatha1">Hīnena <a name="M0.0528"></a> brahma-cariyena,</p>\r\n<p class="gatha2">Khattiye upapajjati;</p>\r\n<p class="gatha3">Majjhimena ca devattaṃ,</p>\r\n<p class="gathalast">Uttamena vi-sujjhati.</p>\r\n<p class="hangnum">Ka.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',532,replace(replace('<p class="gatha1">Nagare <a name="M0.0532"></a> bandhumatiyā,</p>\r\n<p class="gatha2">Bandhumā nāma khattiyo;</p>\r\n<p class="gatha3">Divase puṇṇamāya so,</p>\r\n<p class="gathalast">Upagacchi uposathaṃ.</p>\r\n<p class="hangnum">Kha.</p>\r\n<p class="gatha1"> Ahaṃ tena samayena,</p>\r\n<p class="gatha2">Gumbha-dāsī ahaṃ tahiṃ;</p>\r\n<p class="gatha3">Disvā sa-rājakaṃ senaṃ,</p>\r\n<p class="gathalast">Evāhaṃ cintayiṃ tadā.</p>\r\n<p class="hangnum">Ga.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',533,replace(replace('<p class="gatha1">Rājāpi <a name="M0.0533"></a> rajjaṃ chaṭṭetvā,</p>\r\n<p class="gatha2">Upagacchi uposathaṃ;</p>\r\n<p class="gatha3">Sa-phalaṃ nūna taṃ kammaṃ,</p>\r\n<p class="gathalast">Jana-kāyo pamodito.</p>\r\n<p class="hangnum">Gha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',534,replace(replace('<p class="gatha1">Yoniso <a name="M0.0534"></a> paccavekkhitvā,</p>\r\n<p class="gatha2">Duggaccañca daliddataṃ;</p>\r\n<p class="gatha3">Mānasaṃ sampahaṃsitvā,</p>\r\n<p class="gathalast">Upagacchimu posathaṃ.</p>\r\n<p class="hangnum">Ṅa.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',535,replace(replace('<p class="gatha1">Ahaṃ <a name="M0.0535"></a> uposathaṃ katvā,</p>\r\n<p class="gatha2">Sammā-sambuddhasāsane;</p>\r\n<p class="gatha3">Tena kammena su-katena,</p>\r\n<p class="gathalast">Tāvatiṃsaṃ agacchahaṃ.</p>\r\n<p class="hangnum">Ca.</p>\r\n<p class="gatha1"> Tattha me su-kataṃ byamhaṃ,</p>\r\n<p class="gatha2">Ubbha-yojanamuggataṃ;</p>\r\n<p class="gatha3">Kūṭāgāra-varūpetaṃ,</p>\r\n<p class="gathalast">Mahāsanasu-bhūsitaṃ.</p>\r\n<p class="hangnum">Cha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',536,replace(replace('<p class="gatha1">Accharā <a name="M0.0536"></a> sata-sahassā,</p>\r\n<p class="gatha2">Upatiṭṭhanti maṃ sadā;</p>\r\n<p class="gatha3">Aññe deve atikkamma,</p>\r\n<p class="gathalast">Atirocāmi sabbadā.</p>\r\n<p class="hangnum">Ja.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',537,replace(replace('<p class="gatha1">Catusaṭṭhi-deva-rājūnaṃ<a name="M0.0537"></a>,</p>\r\n<p class="gatha2">Mahesittamakārayiṃ;</p>\r\n<p class="gatha3">Tesaṭṭhi-cakkavattinaṃ,</p>\r\n<p class="gathalast">Mahesittamakārayiṃ.</p>\r\n<p class="hangnum">Jha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',538,replace(replace('<p class="gatha1">Suvaṇṇa-vaṇā <a name="M0.0538"></a> hutvāna,</p>\r\n<p class="gatha2">Bhavesu saṃsarāmahaṃ;</p>\r\n<p class="gatha3">Sabbattha pavarā homi,</p>\r\n<p class="gathalast">Uposathassidaṃ phalaṃ.</p>\r\n<p class="hangnum">Ña.</p>\r\n<p class="gatha1"> Hatthi-yānaṃ assa-yānaṃ,</p>\r\n<p class="gatha2">Ratha-yānañca sivikaṃ;</p>\r\n<p class="gatha3">Labhāmi sabbametampi,</p>\r\n<p class="gathalast">Uposathassidaṃ phalaṃ.</p>\r\n<p class="hangnum">Ṭa.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',539,replace(replace('<p class="gatha1">Soṇṇa-mayaṃ <a name="M0.0539"></a> rūpi-mayaṃ,</p>\r\n<p class="gatha2">Athopi phalikā-mayaṃ;</p>\r\n<p class="gatha3">Lohitaṅga-mayañceva,</p>\r\n<p class="gathalast">Sabbaṃ paṭilabhāmahaṃ.</p>\r\n<p class="hangnum">Ṭha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',540,replace(replace('<p class="gatha1">Koseyya-kambaliyāni<a name="M0.0540"></a>,</p>\r\n<p class="gatha2">Khoma-kappāsikāni ca;</p>\r\n<p class="gatha3">Mahagghāni ca vatthāni,</p>\r\n<p class="gathalast">Sabbaṃ paṭilabhāmahaṃ.</p>\r\n<p class="hangnum">Ḍa.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',541,replace(replace('<p class="gatha1">Annaṃ <a name="M0.0541"></a> pānaṃ khādanīyaṃ,</p>\r\n<p class="gatha2">Vatthaṃ senāsanāni ca;</p>\r\n<p class="gatha3">Sabbametaṃ paṭilabhe,</p>\r\n<p class="gathalast">Uposathassidaṃ phalaṃ.</p>\r\n<p class="hangnum">Ḍha.</p>\r\n<p class="gatha1"> Vara-gandhañca mālañca,</p>\r\n<p class="gatha2">Cuṇṇakañcha vilepanaṃ;</p>\r\n<p class="gatha3">Sabbametaṃ paṭilabhe,</p>\r\n<p class="gathalast">Uposathassidaṃ phalaṃ.</p>\r\n<p class="hangnum">Ṇa.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',542,replace(replace('<p class="gatha1">Kūṭāgārañca <a name="M0.0542"></a> pāsādaṃ,</p>\r\n<p class="gatha2">Maṇḍapaṃ hammiyaṃ guhaṃ;</p>\r\n<p class="gatha3">Sabbametaṃ paṭilabhe,</p>\r\n<p class="gathalast">Uposathassidaṃ phalaṃ.</p>\r\n<p class="hangnum">Ta.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',543,replace(replace('<p class="gatha1">Jātiyā <a name="M0.0543"></a> satta-vassāhaṃ,</p>\r\n<p class="gatha2">Pabbajiṃ ana-gāriyaṃ;</p>\r\n<p class="gatha3">Aḍḍha-māse a-sappatte,</p>\r\n<p class="gathalast">Ara hattaṃ apāpuṇiṃ.</p>\r\n<p class="hangnum">Tha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',544,replace(replace('<p class="gatha1">Kilesā <a name="M0.0544"></a> jhāpitā mayhaṃ,</p>\r\n<p class="gatha2">Bhavā sabbe samūhatā;</p>\r\n<p class="gatha3">Sabbāsava-parikkhīṇā,</p>\r\n<p class="gathalast">Natthi dāni puna-bbhavo.</p>\r\n<p class="hangnum">Da.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',545,replace(replace('<p class="gatha1">Eka-navutito <a name="M0.0545"></a> kappe,</p>\r\n<p class="gatha2">Yaṃ kammamakariṃ tadā;</p>\r\n<p class="gatha3">Duggatiṃ nābhijānāmi,</p>\r\n<p class="gathalast">Uposathassidaṃ phalaṃ.</p>\r\n<p class="hangnum">Dha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',546,replace(replace('<p class="gatha1">Svāgataṃ <a name="M0.0546"></a> vata me āsi,</p>\r\n<p class="gatha2">Mama buddhassa santike;</p>\r\n<p class="gatha3">Tisso vijjā anu-pattā,</p>\r\n<p class="gathalast">Kataṃ buddhassa sāsanaṃ.</p>\r\n<p class="hangnum">Na.</p>\r\n<p class="gatha1"> Paṭisambhidā catasso,</p>\r\n<p class="gatha2">Vimokāpi ca aṭṭhime;</p>\r\n<p class="gatha3">Chaḷābhiññā sacchikatā,</p>\r\n<p class="gathalast">Kataṃ buddhassa sāsanaṃ.</p>\r\n<p class="hangnum"><a name="para293"></a><span class="paranum">293</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-293-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',547,replace(replace('<p class="gatha1">Ñātīnañca <a name="M0.0547"></a> piyo hoti,</p>\r\n<p class="gatha2">Mittesu ca virocati;</p>\r\n<p class="gatha3">Kāyassa bhedā su-gatiṃ,</p>\r\n<p class="gathalast">Upapajjati sīlavā.</p>\r\n<p class="hangnum"><a name="para294"></a><span class="paranum">294</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-294-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',549,replace(replace('<p class="gatha1">Nibbānaṃ <a name="M0.0549"></a> patthayantena samādinnaṃ,</p>\r\n<p class="gatha2">Pañca-sīlampi adhi-sīlaṃ;</p>\r\n<p class="gathalast">Dasa-sīlampi adhi-sīlameva.</p>\r\n<p class="hangnum">Ka.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',554,replace(replace('<p class="gatha1">Nagare <a name="M0.0554"></a> candavatiyā,</p>\r\n<p class="gatha2">Bhaṭako āsahaṃ tadā;</p>\r\n<p class="gatha3">Para-kammāyane yutto,</p>\r\n<p class="gathalast">Pabbajjaṃ na labhāmahaṃ.</p>\r\n<p class="hangnum">Kha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',555,replace(replace('<p class="gatha1">Mahandhakāra-pihitā<a name="M0.0555"></a>,</p>\r\n<p class="gatha2">Tividhaggīhi ḍayhare;</p>\r\n<p class="gatha3">Kena nukho upāyena,</p>\r\n<p class="gathalast">Vi-saṃyutto bhave ahaṃ.</p>\r\n<p class="hangnum">Ga.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',556,replace(replace('<p class="gatha1">Deyyadhammo <a name="M0.0556"></a> ca me natthi,</p>\r\n<p class="gatha2">Bhaṭako dukkhito ahaṃ;</p>\r\n<p class="gatha3">Yaṃ nūnāhaṃ pañca-sīlaṃ,</p>\r\n<p class="gathalast">Rakkheyyaṃ paripūrayaṃ.</p>\r\n<p class="hangnum">Gha.</p>\r\n<p class="gatha1"> Anomadassissa munino,</p>\r\n<p class="gatha2">Nisabho nāma sāvako;</p>\r\n<p class="gatha3">Tamahaṃ upasaṅkamma,</p>\r\n<p class="gathalast">Pañca-sikkhāpadaggahiṃ.</p>\r\n<p class="hangnum">Ṅa.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',557,replace(replace('<p class="gatha1">Vassa-sata-sahassāni<a name="M0.0557"></a>,</p>\r\n<p class="gatha2">Āyu vijjati tāvade;</p>\r\n<p class="gatha3">Tāvatā pañca-sīlāni,</p>\r\n<p class="gathalast">Paripuṇṇāni gopayiṃ.</p>\r\n<p class="hangnum">Ca.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',558,replace(replace('<p class="gatha1">Maccu-kālamhi <a name="M0.0558"></a> sampatte,</p>\r\n<p class="gatha2">Devā assāsayanti maṃ;</p>\r\n<p class="gatha3">Ratho sahassa-yutto te,</p>\r\n<p class="gathalast">Mārisassa upaṭṭhito.</p>\r\n<p class="hangnum">Cha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',559,replace(replace('<p class="gatha1">Vattante <a name="M0.0559"></a> carime citte,</p>\r\n<p class="gatha2">Mama sīlaṃ anussariṃ;</p>\r\n<p class="gatha3">Tena kammena su-katena,</p>\r\n<p class="gathalast">Tāvatiṃsaṃ agacchahaṃ.</p>\r\n<p class="hangnum">Ja.</p>\r\n<p class="gatha1"> Tiṃsakhattuñca devindo,</p>\r\n<p class="gatha2">Deva-rajjamakārayiṃ;</p>\r\n<p class="gatha3">Dibba-sukhaṃ anubhaviṃ,</p>\r\n<p class="gathalast">Accharāhi purakkhatto.</p>\r\n<p class="hangnum">Jha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',560,replace(replace('<p class="gatha1">Pañca-sattatikhatttu-ñca<a name="M0.0560"></a>,</p>\r\n<p class="gatha2">Cakkavattī ahosahaṃ;</p>\r\n<p class="gatha3">Padesa-rajjaṃ vipulaṃ,</p>\r\n<p class="gathalast">Gaṇanāto a-saṅkhayaṃ.</p>\r\n<p class="hangnum">Ña.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',561,replace(replace('<p class="gatha1">Deva-lokā <a name="M0.0561"></a> cavitvāna,</p>\r\n<p class="gatha2">Sukka-mūlena codito;</p>\r\n<p class="gatha3">Pure vesāliyaṃ jāto,</p>\r\n<p class="gathalast">Mahā-kule su-aḍḍhake.</p>\r\n<p class="hangnum">Ṭa.</p>\r\n<p class="gatha1"> Vassūpanāyike kāle,</p>\r\n<p class="gatha2">Dibbante jina-sāsane;</p>\r\n<p class="gatha3">Mātā ca me pitā ceva,</p>\r\n<p class="gathalast">Pañca-sikkhāpadaggahuṃ.</p>\r\n<p class="hangnum">Ṭha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',562,replace(replace('<p class="gatha1">Saha <a name="M0.0562"></a> sutvānahaṃ sīlaṃ,</p>\r\n<p class="gatha2">Mama sīlaṃ anussariṃ;</p>\r\n<p class="gatha3">Ekāsane nisīditvā,</p>\r\n<p class="gathalast">Arahattamapāpuṇiṃ.</p>\r\n<p class="hangnum">Ḍa.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',563,replace(replace('<p class="gatha1">Jātiyā <a name="M0.0563"></a> pañca-vassena,</p>\r\n<p class="gatha2">Arahattamapāpuṇiṃ;</p>\r\n<p class="gatha3">Upasampādayi buddho,</p>\r\n<p class="gathalast">Guṇamaññāya cakkhumā.</p>\r\n<p class="hangnum">Ḍha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',564,replace(replace('<p class="gatha1">Paripuṇṇāni <a name="M0.0564"></a> gopetvā,</p>\r\n<p class="gatha2">Pañca-sikkhāpadānahaṃ;</p>\r\n<p class="gatha3">A-parimeyyito kappe,</p>\r\n<p class="gathalast">Vinipātaṃ na gacchahaṃ.</p>\r\n<p class="hangnum">Ṇa.</p>\r\n<p class="gatha1"> Svāhaṃ yasamanubhaviṃ,</p>\r\n<p class="gatha2">Tesaṃ sīlāna vāhasā;</p>\r\n<p class="gatha3">Kappa-koṭipi kittento,</p>\r\n<p class="gathalast">Kittaye eka-desakaṃ.</p>\r\n<p class="hangnum">Ta.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',565,replace(replace('<p class="gatha1">Pañca-sīlāni <a name="M0.0565"></a> gopetvā,</p>\r\n<p class="gatha2">Tayo hetū labhāmahaṃ;</p>\r\n<p class="gatha3">Dīghāyuko mahā-bhogo,</p>\r\n<p class="gathalast">Tikkha-pañño bhavāmahaṃ.</p>\r\n<p class="hangnum">Tha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',566,replace(replace('<p class="gatha1">Saṃkittento <a name="M0.0566"></a> ca sabbesaṃ,</p>\r\n<p class="gatha2">Adhi-mattañca porisaṃ;</p>\r\n<p class="gatha3">Bhavābhave saṃsaritvā,</p>\r\n<p class="gathalast">Ete ṭhāne labhamahaṃ.</p>\r\n<p class="hangnum">Da.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',567,replace(replace('<p class="gatha1">A-parimeyya-sīlesu<a name="M0.0567"></a>,</p>\r\n<p class="gatha2">Vattantā jina-sāvakā;</p>\r\n<p class="gatha3">Bhavesu yadi rajjeyyuṃ,</p>\r\n<p class="gathalast">Vipāko kīdiso bhave.</p>\r\n<p class="hangnum">Dha.</p>\r\n<p class="gatha1"> Su-ciṇṇaṃ me pañca-sīlaṃ,</p>\r\n<p class="gatha2">Bhaṭakena tapassinā;</p>\r\n<p class="gatha3">Tena sīlenahaṃ ajja,</p>\r\n<p class="gathalast">Mocayiṃ sabba-bandhanā.</p>\r\n<p class="hangnum">Na.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',568,replace(replace('<p class="gatha1">A-parimeyyito <a name="M0.0568"></a> kappe,</p>\r\n<p class="gatha2">Pañca-sīlāni gopayiṃ;</p>\r\n<p class="gatha3">Duggatiṃ nābhijānāmi,</p>\r\n<p class="gathalast">Pañca-sīlānidaṃ phalaṃ.</p>\r\n<p class="hangnum">Pa.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',569,replace(replace('<p class="gatha1">Paṭisambhidā <a name="M0.0569"></a> catasso,</p>\r\n<p class="gatha2">Vimokkhāpi ca aṭṭhime;</p>\r\n<p class="gatha3">Chaḷābhiññā sacchikatā,</p>\r\n<p class="gathalast">Kataṃ buddhassa sāsanaṃ.</p>\r\n<p class="hangnum">Ka.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',572,replace(replace('<p class="gatha1">Taṃ <a name="M0.0572"></a> namassanti te vijjā,</p>\r\n<p class="gatha2">Sabbe bhūmā ca khattiyā;</p>\r\n<p class="gatha3">Cattāro ca mahā-rājā,</p>\r\n<p class="gatha2">Tidasā ca yasassino;</p>\r\n<p class="gatha3">Atha ko nāma so yakkho,</p>\r\n<p class="gathalast">Yaṃ tvaṃ sakka namassasi.</p>\r\n<p class="hangnum">Kha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',574,replace(replace('<p class="gatha1">Maṃ <a name="M0.0574"></a> namassanti te-vijjā,</p>\r\n<p class="gatha2">Sabbe bhūmā ca khattiyā;</p>\r\n<p class="gatha3">Cattāro ca mahā-rājā,</p>\r\n<p class="gathalast">Tidasā ca yasassino.</p>\r\n<p class="hangnum">Ga.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',575,replace(replace('<p class="gatha1">Ahañca <a name="M0.0575"></a> sīla-sampanne,</p>\r\n<p class="gatha2">Ciraratta-samāhite;</p>\r\n<p class="gatha3">Sammā pabbajite vande,</p>\r\n<p class="gathalast">Brahma-cariya parāyane.</p>\r\n<p class="hangnum">Gha.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',576,replace(replace('<p class="gatha1">Ye <a name="M0.0576"></a> gahaṭṭhā puñña-karā,</p>\r\n<p class="gatha2">Sīlavanto upāsakā;</p>\r\n<p class="gatha3">Dhammena dāraṃ posenti,</p>\r\n<p class="gathalast">Te namassāmi mātali.</p>\r\n<p class="hangnum">Ṅa.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',577,replace(replace('<p class="gatha1">Seṭṭhā <a name="M0.0577"></a> hi kira lokasmiṃ,</p>\r\n<p class="gatha2">Ye tvaṃ sakka namassasi;</p>\r\n<p class="gatha3">Ahampi te namassāmi,</p>\r\n<p class="gathalast">Ye namassasi vāsava.</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',580,replace(replace('<p class="gatha1">Pālitattheranāgena<a name="M0.0580"></a>,</p>\r\n<p class="gatha2">Visuddhārāmavāsinā;</p>\r\n<p class="gatha3">Suticchitānamatthāya,</p>\r\n<p class="gathalast">Katā naradakkhadīpanī.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',581,replace(replace('<p class="gatha1">Pubbācariya-sīhānaṃ<a name="M0.0581"></a>,</p>\r\n<p class="gatha2">Ālambitvāna nissayaṃ;</p>\r\n<p class="gatha3">Pālito nāma yo thero,</p>\r\n<p class="gatha2">Imaṃ gantha su-lekhanī;</p>\r\n<p class="gatha3">Sundarameva passituṃ,</p>\r\n<p class="gathalast">Yuñjeyyāthīdha sādhave.</p>\r\n<p class="bodytext">‘‘Chappadikā’’.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',582,replace(replace('<p class="gatha1">Imaṃ <a name="M0.0582"></a> ganthaṃ vācuggato,</p>\r\n<p class="gatha2">Sace bhavasi māṇava;</p>\r\n<p class="gatha3">Puṇnamāyaṃ yathā cando,</p>\r\n<p class="gatha2">Ati-suddho virocati;</p>\r\n<p class="gatha3">Tatheva tvaṃ puṇṇa-mano,</p>\r\n<p class="gathalast">Viroca siriyā dhuvaṃ.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',583,replace(replace('<p class="gatha1">Su-niṭṭhito <a name="M0.0583"></a> ayaṃ gantho,</p>\r\n<p class="gatha2">Sakkarāje dajhamphiye;</p>\r\n<p class="gatha3">Poṭṭhapādamhi sūramhi,</p>\r\n<p class="gathalast">Kālapakkhe catuddasiṃ.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',584,replace(replace('<p class="gatha1">Sañcitetaṃ <a name="M0.0584"></a> mayā puññaṃ,</p>\r\n<p class="gatha2">Taṃ-kammena varena ca;</p>\r\n<p class="gatha3">Ciraṃ tiṭṭhatu saddhammo,</p>\r\n<p class="gathalast">A-verā hontu pāṇino.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',585,replace(replace('<p class="gatha1">Imaṃ <a name="M0.0585"></a> ganthaṃ passitvāna,</p>\r\n<p class="gatha2">Hontu sabbepi jantuno;</p>\r\n<p class="gatha3">Sukhitā dhammikā ñāṇī,</p>\r\n<p class="gathalast">Dhammaṃ pāletu patthivo.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',586,replace(replace('<p class="gatha1">Nibbānaṃ <a name="M0.0586"></a> patthayantena,</p>\r\n<p class="gatha2">Sīlaṃ rakkhantu sajjanā;</p>\r\n<p class="gatha3">Ñatvā dhammaṃ sukhāvahaṃ,</p>\r\n<p class="gathalast">Pāpuṇantu anāsavaṃ.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',587,replace(replace('<p class="gatha1">Aṭṭha-kaṇḍa-maṇḍitāya<a name="M0.0587"></a>,</p>\r\n<p class="gatha2">Dakkhaya attha-dīpako;</p>\r\n<p class="gatha3">Nara-sāro ayaṃ gantho,</p>\r\n<p class="gathalast">Cira-kālaṃ patiṭṭhatu.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Yāvatā canda-sūriyā,</p>\r\n<p class="gatha2">Nāgaccheyyuṃ mahī-tale;</p>\r\n<p class="gatha3">Pamoditā imaṃ ganthaṃ,</p>\r\n<p class="gathalast">Dissantu naya-kovidā.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',588,replace(replace('<p class="gatha1">Sammā <a name="M0.0588"></a> chandenimaṃ gantha,</p>\r\n<p class="gatha2">Vācentā pariyāpuṇā;</p>\r\n<p class="gatha3">Pasannenānāyāsena,</p>\r\n<p class="gathalast">Patvā sukhena kovidaṃ.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',589,replace(replace('<p class="gatha1">Candādiccāva <a name="M0.0589"></a> ākāse,</p>\r\n<p class="gatha2">Bahussutehi sampadā;</p>\r\n<p class="gatha3">Visesa-puggalā hutvā,</p>\r\n<p class="gathalast">Pappontu amataṃ padaṃ.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',590,replace(replace('<p class="gatha1">Ukkaṭṭha-dhamma-dānena<a name="M0.0590"></a>,</p>\r\n<p class="gatha2">Pāpuṇeyyamanuttaraṃ;</p>\r\n<p class="gatha3">Liṅga-sampatti-medhāvī,</p>\r\n<p class="gathalast">Takkī-paññā su-pesalī.</p>\r\n<p class="chapter">Naradakkha thomanā āsīsa</p>\r\n<p class="subhead">3. Gāthā</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',592,replace(replace('<p class="gatha1">Pālito <a name="M0.0592"></a> pāḷiyā cheko,</p>\r\n<p class="gatha2">Tvaṃsi gambhīra-ñāṇavā;</p>\r\n<p class="gatha3">Pāliyāva pālitassa,</p>\r\n<p class="gathalast">Dadāmidāni bho ahaṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',593,replace(replace('<p class="gatha1">Dakkhāvādesu <a name="M0.0593"></a> kusalo,</p>\r\n<p class="gatha2">Pālito sāsanandharī;</p>\r\n<p class="gatha3">Piṭakesu ajjhogāyha,</p>\r\n<p class="gathalast">Naradakkhaṃbhisaṅkharī.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',595,replace(replace('<p class="gatha1">Suta-dharena <a name="M0.0595"></a> racitaṃ,</p>\r\n<p class="gatha2">Etaṃ sāra-gavesino;</p>\r\n<p class="gatha3">Atandikā su-dakkhantu,</p>\r\n<p class="gathalast">Aggagga-sāsane ratā.</p>\r\n<p class="title">Naradakkha thomanā āsīsa</p>\r\n<p class="subhead">2. Gāthā</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',596,replace(replace('<p class="gatha1">Ñuṃ <a name="M0.0596"></a> pālitodha jāto yo,</p>\r\n<p class="gatha2">Thero so abbhutova ñuṃ;</p>\r\n<p class="gatha3">Ñuṃ mahā-pālito santo,</p>\r\n<p class="gathalast">Nikāya-pālito ca ñuṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1009n',598,replace(replace('<p class="gatha1">Ñuṃ <a name="M0.0598"></a> nara-dakkha-ganthaṃ yaṃ,</p>\r\n<p class="gatha2">Sovakā nara-dakkha-daṃ;</p>\r\n<p class="gatha3">Narā dakkhantu sammā ca,</p>\r\n<p class="gathalast">Dakkhattaṃ pāpuṇantu ñuṃ.</p>','\r',char(13)),'\n',char(10)),'');
