INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="book">Caturārakkhadīpanī</p>\r\n<p class="subhead">Kāyapaccavekkhaṇā</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span>. Dussīlakathā,</p>\r\n<p class="bodytext"><a name="para2"></a><span class="paranum">2</span>. Sīlānisaṃsakathā,</p>\r\n<p class="bodytext"><a name="para3"></a><span class="paranum">3</span>. Anūsāsanakathā,</p>\r\n<p class="bodytext"><a name="para4"></a><span class="paranum">4</span>. Āvāsikācārakathā,</p>\r\n<p class="bodytext"><a name="para5"></a><span class="paranum">5</span>. Paccayanissaggakathā,</p>\r\n<p class="bodytext"><a name="para6"></a><span class="paranum">6</span>. Pātimokkhakathā,</p>\r\n<p class="bodytext"><a name="para7"></a><span class="paranum">7</span>. Dāyakovādakathā,</p>\r\n<p class="subhead">Paṇāmapaṭiñā</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-2-3-4-5-6-7-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',1,replace(replace('<p class="gatha1">Cakkavāḷa <a name="M0.0001"></a> nahutā ga, devāli gaṇa cumbito;</p>\r\n<p class="gathalast">Buddha pādambujo ṭhātu, sīse dayā tigandhajo.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',2,replace(replace('<p class="gatha1">Nanta <a name="M0.0002"></a> cakkavāḷa bbhugga, guṇa sanniccitaṃ jinaṃ;</p>\r\n<p class="gathalast">Vande tappūjitaṃ dhammaṃ, tajjaṃ saṅghañca nimmalaṃ.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',3,replace(replace('<p class="gatha1">Vakkhāmi <a name="M0.0003"></a> caturārakkhaṃ, sambuddha vacana nvayaṃ;</p>\r\n<p class="gathalast">Appamādāvahaṃ etaṃ, sottabbaṃ bhavabhīruhi.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',4,replace(replace('<p class="gatha1">Buddhānussati <a name="M0.0004"></a> maraṇā, bhubhā mettāca bhāvanā;</p>\r\n<p class="gathalast">Appamādāya ārakkhā, catasso mānitā sataṃ.</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span>. Buddhānūssati bhāvanā,</p>\r\n<p class="bodytext"><a name="para2"></a><span class="paranum">2</span>. Maraṇassati bhāvanā,</p>\r\n<p class="bodytext"><a name="para3"></a><span class="paranum">3</span>. Asubha bhāvanā,</p>\r\n<p class="bodytext"><a name="para4"></a><span class="paranum">4</span>. Mettā bhāvanā,</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-2-3-4-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',5,replace(replace('<p class="gatha1">Buddhovādaṃ <a name="M0.0005"></a> saritvāva, maccubbiggā sukhesino;</p>\r\n<p class="gathalast">Sītā sītataraṃ yanti, subhamettambusi-ṭṭhitā.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Maraṇaggi vāraṇambu, sambuddhavacanaṃ yidaṃ;</p>\r\n<p class="gathalast">Bahū tadaggi santattā, sītāvāsuṃ tadambunā.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',6,replace(replace('<p class="gatha1">Saddhaṃ <a name="M0.0006"></a> buddhena tejetvā, mānaṃ maraṇacintayā;</p>\r\n<p class="gathalast">Asubhāya hane rāgaṃ, dosaṃ mettāya paññavā.</p>\r\n<p class="chapter">1. Buddhānussati niddesa</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',7,replace(replace('<p class="gatha1">Arahaṃ <a name="M0.0007"></a> sammāsambuddho, vijjakkhi caraṇappado;</p>\r\n<p class="gathalast">Sugato sugado satthā, sabbaññū bhagavādamo.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',9,replace(replace('<p class="gatha1">Ārakattārihantattā<a name="M0.0009"></a>, pāpākārakatoraho;</p>\r\n<p class="gathalast">Hata cakkārato pūjā, rahattā cārahaṃ name.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',11,replace(replace('<p class="gatha1">Samutte <a name="M0.0011"></a> jiya gihīnaṃ, anupubbikatho jino;</p>\r\n<p class="gathalast">Adāsi paramaṃ tuṭṭhiṃ, saccāni dassayaṃ divā.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',12,replace(replace('<p class="gatha1">Bhikkhūnaṃ <a name="M0.0012"></a> paṭhame yāme, pāyesi amatāgadaṃ;</p>\r\n<p class="gathalast">Jātikhettāga devānaṃ, kaṅkhacchedo sa majjhime;</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Ādo phalasukhaṃ vedi, majjhe seyya makā jino;</p>\r\n<p class="gathalast">Veneyyo lokanaṃ ante, pacchimepi tidhā kate.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',14,replace(replace('<p class="gatha1">Khedaṃ <a name="M0.0014"></a> agaṇayaṃ nātho, pañca buddhakataṃ vahaṃ;</p>\r\n<p class="gathalast">Satthasiddho paratthaṃva, byāvaṭo sumahādayo.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Caṅkamitvā nisīditvā, rattiṃdivañca jhāyituṃ;</p>\r\n<p class="gathalast">Supituṃ majjhayāmeva, buddho bhikkhūna movadi.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',15,replace(replace('<p class="gatha1">Nāla <a name="M0.0015"></a> mālasituṃ tassa, mahāvīrassa sāsane;</p>\r\n<p class="gathalast">Pamādāya munindassa, kataññū sādhu sammato.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Anaññātassa ñātāya, apattassaca pattiyā;</p>\r\n<p class="gathalast">Ārabhetuṃva no yutto, appamatto rahogato.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',16,replace(replace('<p class="gatha1">Ārabbhatha <a name="M0.0016"></a> nikkamatha, yuñjatha buddhasāsane;</p>\r\n<p class="gathalast">Dhunātha maccuno senaṃ, naḷāgāraṃva kuñjaro.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',17,replace(replace('<p class="gatha1">Yo <a name="M0.0017"></a> imasmiṃ dhammavinaye, appamatto vihissati;</p>\r\n<p class="gathalast">Pahāya jāti saṃsāraṃ, dukkhassantaṃ karissati;</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',18,replace(replace('<p class="gatha1">Iti <a name="M0.0018"></a> uyyojanaṃ mhākaṃ, muttassa muttiyā saraṃ;</p>\r\n<p class="gathalast">Nayuttova pamādāya, mahādayassa satthuno.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> Suttena dubbitakkena, akicca karaṇenavā;</p>\r\n<p class="gathalast">Mogha kālakkhayo mando, dukkhassantaṃ kathaṃkare.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',19,replace(replace('<p class="gatha1">Ākāsaṃ <a name="M0.0019"></a> cakkavāḷañca, sattā buddhaguṇā pica;</p>\r\n<p class="gathalast">Anantānāma cattāro, paricchedo navijjati.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Yathāpi nabha mākāsaṃ, aṅgularajjuyaṭṭhibhi;</p>\r\n<p class="gathalast">Minetuṃ neva sakkoti, evaṃ kenaci tagguṇaṃ.</p>\r\n<p class="chapter">2. Maraṇassatiniddesa</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',23,replace(replace('<p class="gatha1">Maraṇassati <a name="M0.0023"></a> micchanto, tāva buddhavaco suṇa;</p>\r\n<p class="gathalast">Avikkhittena cittena, sambuddha vacanaṃ hidaṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',24,replace(replace('<p class="gatha1">Animitta <a name="M0.0024"></a> manaññātaṃ, maccānaṃ idha jīvitaṃ;</p>\r\n<p class="gathalast">Kasirañca parittañca, tañca dukkhena saṃyutaṃ.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Na hi so pakkamo atthi, yena jātā namiyyare;</p>\r\n<p class="gathalast">Jarampi patvā maraṇaṃ, evaṃdhammā hi pāṇino.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',25,replace(replace('<p class="gatha1">Phalāna <a name="M0.0025"></a> miva pakkānaṃ, pāto patanato bhayaṃ;</p>\r\n<p class="gathalast">Evaṃ jātāna maccānaṃ, niccaṃ maraṇa to bhayaṃ.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Yathāpi kumbhakārassa, katā mattikabhājanā;</p>\r\n<p class="gathalast">Sabbe bhedapariyantā, evaṃ maccāna jīvitaṃ.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',26,replace(replace('<p class="gatha1">Daharāca <a name="M0.0026"></a> mahantāca, yebālā yeca paṇḍitā;</p>\r\n<p class="gathalast">Sabbe maccuvasaṃ yanti, sabbe maccuparāyanā.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Tesaṃ maccuparetānaṃ, gacchataṃ paralokato;</p>\r\n<p class="gathalast">Napitā tāyate puttaṃ, ñātivāpana ñātake;</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',27,replace(replace('<p class="gatha1">Pekkhataññeva <a name="M0.0027"></a> ñātīnaṃ, passa lālappataṃ puthu;</p>\r\n<p class="gathalast">Ekamekova maccānaṃ, go vajjhoviya niyyati.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Eva mabbhāhato loko, maccunāca jarāyaca;</p>\r\n<p class="gathalast">Tasmā dhīrā nasocanti, viditvā loka pariyāyaṃ.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',28,replace(replace('<p class="gatha1">Aññepi <a name="M0.0028"></a> passa gamane, yathā kammupage nare;</p>\r\n<p class="gathalast">Maccuno vasamāgamma, phandantevidha pāṇino.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Yena yenahi maññanti,</p>\r\n<p class="gatha2">Tatotassa hi aññathā;</p>\r\n<p class="gatha3">Etādiso vinābhāvo,</p>\r\n<p class="gathalast">Passa lokassa pariyāyanti.</p>\r\n<p class="bodytext">[Sulla sutte vuttaṃ.]</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',29,replace(replace('<p class="gatha1">Yathāpi <a name="M0.0029"></a> selā vipulā,</p>\r\n<p class="gatha2">Nabhaṃ āhacca paccatā;</p>\r\n<p class="gatha3">Samantā anupari yeyyuṃ,</p>\r\n<p class="gathalast">Nippothentā catuddisā.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> Evaṃ jarāca maccuca,</p>\r\n<p class="gatha2">Adhivattanti pāṇine;</p>\r\n<p class="gatha3">Khattiyebrāhmaṇe vesse,</p>\r\n<p class="gatha2">Sudde caṇḍāla pakkuse;</p>\r\n<p class="gatha3">Nakiñci parivajjeti,</p>\r\n<p class="gathalast">Sabbamevā bhimaddati.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',30,replace(replace('<p class="gatha1">Na <a name="M0.0030"></a> tattha hatthi naṃ bhummi, na rathānaṃ napattiyā;</p>\r\n<p class="gathalast">Na cāpi mantayuddhena, sakkā jetuṃ dhanenavā.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',31,replace(replace('<p class="gatha1">Tasmāhi <a name="M0.0031"></a> paṇḍito poso,</p>\r\n<p class="gatha2">Sampassaṃ attha mattano;</p>\r\n<p class="gatha3">Buddhe dhamme ca saṅgheca,</p>\r\n<p class="gathalast">Dhīro saddaṃ nivesaye.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> Yo dhammacārī kāyena, vācāya uda cetasā;</p>\r\n<p class="gathalast">Idhevanaṃ pasaṃsanti, paccasagge pamodatīti.</p>\r\n<p class="bodytext">[Pabbatū mama sutte vuttaṃ.]</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-16-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',32,replace(replace('<p class="gatha1">Yathā <a name="M0.0032"></a> vārivaho pūro, vahe rukkhe pakūlaje;</p>\r\n<p class="gathalast">Evaṃ jarāmaraṇena, vuyhante sabba pāṇino.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> Daharāpi hi miyyanti,</p>\r\n<p class="gatha2">Narāca athanāriyo;</p>\r\n<p class="gatha3">Tattha ko visāseposo,</p>\r\n<p class="gathalast">Daharo mhītijīvite.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',33,replace(replace('<p class="gatha1">Sāya <a name="M0.0033"></a> meke nidissanti, pāto diṭṭhā bahujjanā;</p>\r\n<p class="gathalast">Pāto eke nadissanti, sāyaṃ diṭṭhā bahujjanā.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> Ajjeva kiccaṃ ātappaṃ, ko jaññā maraṇaṃ suve;</p>\r\n<p class="gathalast">Nahi no saṅkaraṃ tena, mahāsenana maccunāti.</p>\r\n<p class="bodytext">[Jātakesuvuttaṃ.]</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',34,replace(replace('<p class="gatha1">Natthetthañño <a name="M0.0034"></a> nusāsanto, sayaṃvattāna movada;</p>\r\n<p class="gathalast">Jineritā nusārena, bhikkhu saṃsāra bhīruko.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',35,replace(replace('<p class="gatha1">Ahivāpi <a name="M0.0035"></a> maṃ ḍaṃseyya, aññepi visadhārino;</p>\r\n<p class="gathalast">Apiyāpica ghāteyyuṃ, uppajjeyyuṃ rujāpime.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> Maccusenā vudhāsaṅkhyā, bāhirajjhattu paddavā;</p>\r\n<p class="gathalast">Tehāyu pīḷitaṃchejjaṃ, marissa majjavā suve.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-23-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',36,replace(replace('<p class="gatha1">Aḷakkā <a name="M0.0036"></a> hi gavādīhi, corādīhi arīhipi;</p>\r\n<p class="gathalast">Abhiṇha sannipātehi, rujā chanavutīhipi.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> Bahūna mupakārehi, annodakādikehipi;</p>\r\n<p class="gathalast">Marissaṃ pīḷito niccaṃ, nirujjheyyāyu ajjavā.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-26-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',37,replace(replace('<p class="gatha1">Bahvāvudhe <a name="M0.0037"></a> visajjeti, nilleṇaṃ maccuniddayo;</p>\r\n<p class="gathalast">Vasantaṃ bhavasaṅgāme, namutto koci āvudhā;</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> Mahabbalā mahāpaññā, mahiddhikā mahaddhanā;</p>\r\n<p class="gathalast">Namuttā sammāsambuddho, sabbalokādhipo api;</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-27-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',38,replace(replace('<p class="gatha1">Mayā <a name="M0.0038"></a> samā navā vuddhā, tadāvudhehi te matā;</p>\r\n<p class="gathalast">Tathā hampi marissāmi, leṇaṃ puññaṃva me kataṃ.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> Pathabyāpādayo dhātthū,</p>\r\n<p class="gatha2">Āhārā bhojanādayo;</p>\r\n<p class="gatha3">Sītuṇha mutunāmetaṃ,</p>\r\n<p class="gathalast">Dosā pittasemhānilā.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',39,replace(replace('<p class="gatha1">Dhātvāhāru <a name="M0.0039"></a> tudosānaṃ, samatte vāyu tiṭṭhati;</p>\r\n<p class="gathalast">Visame taṅkhaṇaññeva, chejja mappaṃ parādhinaṃ.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> Dhātvā hārutu dosānaṃ, visamā svepyakallako;</p>\r\n<p class="gathalast">Ussāhe kallakāleva, kiṃkareyyaakallako;</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-31-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',40,replace(replace('<p class="gatha1">Uppajjeyyuṃ <a name="M0.0040"></a> rujā svepi, asāto dukkhamā kharā;</p>\r\n<p class="gathalast">Puretaraṃva ārabbhe, māpacchā anutāpanaṃ.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',41,replace(replace('<p class="gatha1">Sattānaṃ <a name="M0.0041"></a> nīcakammānaṃ, saraṇopi bhayaṅkaro;</p>\r\n<p class="gathalast">Nīcānīcaṃ najānāmi, maraṇāsannatampica.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> Kamma pīḷita sattānaṃ, taṅkhaṇampi bhayubbhavo;</p>\r\n<p class="gathalast">Mareyya majjavā svevā, nayuttova pamajjituṃ.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> Puññakkhīṇā pajā khippaṃ, ahetunāpi nassaye;</p>\r\n<p class="gathalast">Suddhacitto marissāmi, na kiliṭṭhena cetasā.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-34-35-36-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',42,replace(replace('<p class="gatha1">Upacchedāpi <a name="M0.0042"></a> me santi, bhavābhavacitā bahū;</p>\r\n<p class="gathalast">Chejjaṃ tehāyu ajjāpi, sādhvāsumārabhe mataṃ;</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> Kammā parādha sattānaṃ, vināse paccupaṭṭhite;</p>\r\n<p class="gathalast">Anayo nayarūpena, buddhimākamya tiṭṭhati.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-37-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',43,replace(replace('<p class="gatha1">Maraṇāsanna <a name="M0.0043"></a> taññeva, cinteyya paññavā sato;</p>\r\n<p class="gathalast">Evaṃ cintayanto santo, napāpaṃ kattu mussahe.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> Maraṇāsanna taññeva, cinteyya buddhasāvako;</p>\r\n<p class="gathalast">Evaṃ cintayanto santo, kadācipi anuṇṇato.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',44,replace(replace('<p class="gatha1">Addhāga <a name="M0.0044"></a> maraṇaṃ pañño, puretaraṃva cintaye;</p>\r\n<p class="gathalast">Kare kātabba kammañca, evaṃ so nānusocati.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> Maraṇāsanna saññī so, appamatto vicakkhaṇo;</p>\r\n<p class="gathalast">Pattepi maraṇe kāle, na sammuḷho nasokavā.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',45,replace(replace('<p class="gatha1">Maraṇā <a name="M0.0045"></a> sannasaññī so, sodheti attano malaṃ;</p>\r\n<p class="gathalast">Nimmalena cuto bhikkhu, natvevā pāya gāmiko.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> Sati āsanna maraṇe,</p>\r\n<p class="gatha2">Dūrasaññī pamāda vā;</p>\r\n<p class="gatha3">Yo karoti akātabbaṃ,</p>\r\n<p class="gathalast">Tadā so atisocati.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-43-44-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',46,replace(replace('<p class="gatha1">Sati <a name="M0.0046"></a> āsannamaraṇe, dūrasaññī pamādavā;</p>\r\n<p class="gathalast">Ākiṇṇo pāpadhammehi, pajjhāyi dummano tadā.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> Sati āsanna maraṇe, duṭṭho dosoti thaddhavā;</p>\r\n<p class="gathalast">Abhinandati sādeti, tadāso atidukkhito.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-45-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',47,replace(replace('<p class="gatha1">Sati <a name="M0.0047"></a> āsannamaraṇe, tuvaṭaṃ na cikicchati;</p>\r\n<p class="gathalast">Sañciccāpatti māpanno, tadā so paridevati.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> Sati āsanna maraṇe, gihīhi navakehica;</p>\r\n<p class="gathalast">Saṃsaṭṭho na nulomehi, svativassu mukho tadā.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-47-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',48,replace(replace('<p class="gatha1">Sati <a name="M0.0048"></a> āsanna maraṇe, kuhako kuladūsako;</p>\r\n<p class="gathalast">Micchājīva samāpanno, dunnimittova so cuto.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-49-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',49,replace(replace('<p class="gatha1">Sasokī <a name="M0.0049"></a> sahanandīca, dukkhe dukkho sukhe sukho;</p>\r\n<p class="gathalast">Gihikammesu ussukko, passaṃ gijjhakūṭaṃ cuto.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> Marissanti anāvajja, kilesātura pīḷito;</p>\r\n<p class="gathalast">Kimikhajjavaṇo sāva, bhanto kiṃ sugatiṃ vaje.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-50-51-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',50,replace(replace('<p class="gatha1">Marissanti <a name="M0.0050"></a> anāvajja, dhuradvayaṃ na pūrati;</p>\r\n<p class="gathalast">Ganthaṃ vipassanaṃ tandī, kāruññoyeva so cuto.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> Marissanti anāvajja, kilesānaṃ vasānugo;</p>\r\n<p class="gathalast">Sambuddhā-ṇaṃ vītikkanto, kāruñño natthi tassamo.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-52-53-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',51,replace(replace('<p class="gatha1">Marissanti <a name="M0.0051"></a> anāvajjaṃ, dhanamesī adhammato;</p>\r\n<p class="gathalast">Puññe cintāpi nuppajji, nirayaṃ so manaṃ gato.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> Mareyyanti anāvajjaṃ, dhanaṃ cini adhammato;</p>\r\n<p class="gathalast">Citaṃ citaṃ ihevetaṃ, tasmiṃgiddho sa pettiko.</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-54-55-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',52,replace(replace('<p class="gatha1">Atthā <a name="M0.0052"></a> gehe nivattante, susāne mittabandhavā;</p>\r\n<p class="gathalast">Sukataṃ dukkataṃ kammaṃ, gacchanta manugacchati.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> Mareyyanti anāvajjaṃ, ihatthaṃ vā nuyuñjati;</p>\r\n<p class="gathalast">Samparāya manapekkho, sujuṃ vā pāyagāmiko.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-56-57-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',53,replace(replace('<p class="gatha1">Mareyyanti <a name="M0.0053"></a> samāvajja, dhammato dhana mesati;</p>\r\n<p class="gathalast">Puññakārī suladdhena, maraṇepi sa modati.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> Pañcasīla sadārakkho, yathābalañca dāyako;</p>\r\n<p class="gathalast">Kāle uposathāvāso, so niccaṃ sugatiṃ vajje.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-58-59-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',54,replace(replace('<p class="gatha1">Dhovāpattimalaṃ <a name="M0.0054"></a> khippaṃ, maccu addhā gamissati;</p>\r\n<p class="gathalast">Micchāvitakka mucchijja, kara kātabbabhāvanaṃ.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> Atikkantā bahū ratyo, khepetvā mama jīvitaṃ;</p>\r\n<p class="gathalast">Mandāyunā pamādena, yutto viharituṃ kathaṃ.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-60-61-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',55,replace(replace('<p class="gatha1">Hāsantaṃ <a name="M0.0055"></a> nandi mattānaṃ, maccusandhīhi tacchaye;</p>\r\n<p class="gathalast">Kucchi meyyacuto ajja, ko hāsananditabbako.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> Maccusenāvudhā saṅkhyā, maraṇābhimukho ahaṃ;</p>\r\n<p class="gathalast">Accāyitabba kālo yaṃ, ikkhitabba mudikkhatu.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-62-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',56,replace(replace('<p class="gatha1">Puremarāmi <a name="M0.0056"></a> daṭṭhabbaṃ,</p>\r\n<p class="gatha2">Dakkheyyaṃ maccu essati;</p>\r\n<p class="gatha3">Accāyitabba kālo yaṃ,</p>\r\n<p class="gathalast">Nokāso hāsatuṭṭhiyā.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-64-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',57,replace(replace('<p class="gatha1">Ākiṇṇamaccusenānaṃ<a name="M0.0057"></a>, ajja svevā vināsinaṃ;</p>\r\n<p class="gathalast">Addhā pahāya gāmīnaṃ, kiṃ pamāda vihārinā.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> Khaṇamattova paccakkho, ajja svevā atissati;</p>\r\n<p class="gathalast">Samparāyo atidīgho, paramparo anantiko.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-65-66-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',58,replace(replace('<p class="gatha1">Khaṇamattova <a name="M0.0058"></a> paccakkho, maccunā taṃ jahissati;</p>\r\n<p class="gathalast">Pahāya gamanīye-smiṃ, mahussāho niratthako.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> Samparāyo atidīgho, apātheyye suduttaro;</p>\r\n<p class="gathalast">Mahussāhena kātabbo, tadattho dīghadassinā.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-67-68-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',59,replace(replace('<p class="gatha1">Saddhā <a name="M0.0059"></a> bandhatu pātheyyaṃ, tadesanaṃ iheva hi;</p>\r\n<p class="gathalast">Bhavantare nalabbheyya, apātheyya tidukkhito.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> Saṃsāra taraṇatthāya, maholumpāni bandhatha;</p>\r\n<p class="gathalast">Bhāvanā dāna sīlehi, tivittiṇṇo bhavaṇṇavo.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-69-70-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',60,replace(replace('<p class="gatha1">Bhuñjaṃ <a name="M0.0060"></a> bhuñjaṃ janaṃ kāme,</p>\r\n<p class="gatha2">Kālākālā-budhontako;</p>\r\n<p class="gatha3">Kantekanteti maṃsāso,</p>\r\n<p class="gathalast">Pivaṃpivaṃva kaṃ migaṃ.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> Kāme kāmesanāyeyya,</p>\r\n<p class="gatha2">Kālokālo mateṭṭhiyā;</p>\r\n<p class="gatha3">Pūre pūretabbaṃ dhammaṃ,</p>\r\n<p class="gathalast">Addhā addhāna saṃsaraṃ.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-71-72-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',61,replace(replace('<p class="gatha1">Mareyyanti <a name="M0.0061"></a> anubbiggo, pāpakaṃ kattumussahe;</p>\r\n<p class="gathalast">Kareyya hāsanandiñca, cāpallañca pamāda vā.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',62,replace(replace('<p class="gatha1">Mareyyamiti <a name="M0.0062"></a> saṃviggo, leṇameva gavesati;</p>\r\n<p class="gathalast">Na hāsi nevanandīca, na cāpallo kadācipi.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> Accuṭṭhita rujaggīhi, accāyāse bhayānake;</p>\r\n<p class="gathalast">Nosadhe maraṇāsanne, katapuññaṃva sāta-daṃ.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-74-75-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',63,replace(replace('<p class="gatha1">Ñātisaṅghā <a name="M0.0063"></a> viyojentā, maraṇanta bhusāturā;</p>\r\n<p class="gathalast">Sabbaṃ pahāya gantāpi, nanditabbāni puññino.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> Passantā sunimittāni, pākaṭāni sakammunā;</p>\r\n<p class="gathalast">Sukhanti maraṇe kāle, numodantā katānica.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-76-77-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',64,replace(replace('<p class="gatha1">Sāta-dātāni <a name="M0.0064"></a> puññāni, evaṃ mahabbhaye api;</p>\r\n<p class="gathalast">Sugatiṃ lahunetāni, kātabbāni puretaraṃ.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-78-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',65,replace(replace('<p class="gatha1">Devadūte <a name="M0.0065"></a> pakāsetvā, yamapuṭṭho sayaṃkataṃ;</p>\r\n<p class="gathalast">Puññaṃ sarati ce satto, tadeva sugatiṃ vaje.</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-79-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',66,replace(replace('<p class="gatha1">Pāpa <a name="M0.0066"></a> kaḍḍhampi niraye, manaṃ dukkhagataṃ pajaṃ;</p>\r\n<p class="gathalast">Dukkhā moceti yaṃpuññaṃ, sadā kātabbameva taṃ.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> Pahāyakaṃva puññañhi, pahātabbaṃva pāpakaṃ;</p>\r\n<p class="gathalast">Taṃ padīpandhakāraṃva, dvayaṃ otvā khukaṃ viya.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-80-81-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',67,replace(replace('<p class="gatha1">Ñātisaṅghā <a name="M0.0067"></a> viyojentā, maraṇanta bhusāturā;</p>\r\n<p class="gathalast">Sabbaṃ pahāya gantāro, bhayānakāni pāpino.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> Passantā dunnimittāni, pākaṭāni sakammunā;</p>\r\n<p class="gathalast">Maraṇe atidukkhanti, nutāpentā katānica.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-82-83-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',68,replace(replace('<p class="gatha1">Paṭipīḷāni <a name="M0.0068"></a> pāpāni, evaṃ mahabbhaye sati;</p>\r\n<p class="gathalast">Duggatiṃ lahunetāni, yuttova parivajjituṃ.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-84-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',69,replace(replace('<p class="gatha1">Devadūte <a name="M0.0069"></a> pakāsetvā, yamarājena pucchito;</p>\r\n<p class="gathalast">Pamādassanti cikkhanto, mahaggimhi turaṃ pati.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> Puññaṃ akarivā māvā, yamarājinda pucchito;</p>\r\n<p class="gathalast">Pamādassanti cikkhanto, mahādukkhaṃ turaṃ gami.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-85-86-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',70,replace(replace('<p class="gatha1">Pāpaṃ <a name="M0.0070"></a> akarivā māvā, pucchito yamasāminā;</p>\r\n<p class="gathalast">Pamādassanti cikkhanto, tattaṃ guḷaṃ turaṃ gili.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> Jātamattā tijiṇṇāca, āturāca matā vudhā;</p>\r\n<p class="gathalast">Devadūte ime pañca, disvā saṃviggataṃ vaje.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-87-88-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',71,replace(replace('<p class="gatha1">Coditā <a name="M0.0071"></a> devadūtehi, ye pamajjanti mānavā;</p>\r\n<p class="gathalast">Te dīgharattaṃ socanti, hina kāyū pagānarā.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-89-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',72,replace(replace('<p class="gatha1">Dhuradvaya <a name="M0.0072"></a> manārabbha, gihikammādike rato;</p>\r\n<p class="gathalast">Kathaṃ gijjhakūṭaṃ sesaṃ, petāvāsaṃ atissati.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> Pariyatti masikkhanto,</p>\r\n<p class="gatha2">Nāraddho paṭipattiyaṃ;</p>\r\n<p class="gatha3">Alaso dubbitakko so,</p>\r\n<p class="gathalast">Kiṃ taṃselaṃ atissati.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-90-92-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',73,replace(replace('<p class="gatha1">Mocanatthāya <a name="M0.0073"></a> pabbajja, saṃkiliṭṭhā pamādino;</p>\r\n<p class="gathalast">Sugatyāpica te bhaṭṭhā, atidūrāva muttito.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-93-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',74,replace(replace('<p class="gatha1">Sīdanteva <a name="M0.0074"></a> jale khittā, silā mahāva khuddakā;</p>\r\n<p class="gathalast">Patanti khuddakenāpi, apāyaṃ pāpa kammunā.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> Patantā khuddakeneva, bahūhi puna pīḷitā;</p>\r\n<p class="gathalast">Mokkhokāsaṃ navindanti, pāpaṃ khuddampi nācare.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-94-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',75,replace(replace('<p class="gatha1">Jegucchitthūdarāgamma<a name="M0.0075"></a>, punāpi tattha niccagū;</p>\r\n<p class="gathalast">Dukkhāti dukkha saṃkiṇṇo, haṭṭhuṃ tuṭṭhuṃ nasakkuṇe.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-96-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',76,replace(replace('<p class="gatha1">Atibyāpiguṇo <a name="M0.0076"></a> puñño,</p>\r\n<p class="gatha2">Mahāyaso sirindharo;</p>\r\n<p class="gatha3">Kucchiyaṃ retasi vāso,</p>\r\n<p class="gathalast">Atīva lajjitabbako.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> Maccudukkhaṃ khaṇaṃyeva, atidukkhaṃ taduttari;</p>\r\n<p class="gathalast">Mātugāmudare sandhi, patiṭṭhānaṃ bhayānakaṃ.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-97-98-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',77,replace(replace('<p class="gatha1">Maccudukkhaṃ <a name="M0.0077"></a> khaṇaṃyeva, atidukkhaṃ cirattanaṃ;</p>\r\n<p class="gathalast">Āma pakkantare sandhi, patiṭṭhānaṃ bhayānakaṃ.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> Duggatyaṃṭhātu taṃdukkhaṃ, suṇa uccakuleapi;</p>\r\n<p class="gathalast">Kucchiyaṃ atisambādhe, jalābumhi jigucchite.</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> Miḷha semhādi saṃkiṇṇe, ati duggandha vāsite;</p>\r\n<p class="gathalast">Gūthakūpe kimīviya, tamejā mūlakammato.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> Paramāṇukāyo ṭhāti, dukkhī nerayiko viya;</p>\r\n<p class="gathalast">Dhuvāturo sukhāmisso, āma pakkāsayantare;</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> Vedanaṭṭova saṃvaḍḍho, acittoviya niccalo;</p>\r\n<p class="gathalast">Dasamāsantare kacce, bahū maranti pāṇino.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-99-100-101-102-103-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',79,replace(replace('<p class="gatha1">Paripakko <a name="M0.0079"></a> pamuñcho so,</p>\r\n<p class="gatha2">Atisambādha yonito;</p>\r\n<p class="gatha3">Malākiṇṇena gattena,</p>\r\n<p class="gathalast">Accāyāso vijāyati.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-104-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',80,replace(replace('<p class="gatha1">Evaṃ <a name="M0.0080"></a> maccuñca sandhiñca, vijāyanañca bheravaṃ;</p>\r\n<p class="gathalast">Passaṃ nibbindanto santo, virajjeyya bhavanduke.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> Evaṃ maccuñca sandiñca, anussara mabhiṇhaso;</p>\r\n<p class="gathalast">Rājaseṭṭhi bhavādimpi, naiccheyya tadanvitaṃ.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-105-106-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',81,replace(replace('<p class="gatha1">Bhave <a name="M0.0081"></a> dukkha macintetvā, bhavāsāya pavattitaṃ;</p>\r\n<p class="gathalast">Puññaṃ punappunaṃ deti, sandhiṃ na nibbutiṃ varaṃ.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> Bhave dukkhaṃ vibhāyitvā, nibbindena pavattitaṃ;</p>\r\n<p class="gathalast">Puññaṃ bhava matikkamma, nibbānaṃ deti nibbutiṃ.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-107-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',82,replace(replace('<p class="gatha1">Bhave <a name="M0.0082"></a> dukkhaṃ saritvāna, maccusandhi sayādikaṃ;</p>\r\n<p class="gathalast">Tibhavesu virajjeyyā, ditta geheva sāmiko.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> Santo puññāni karonto, sandhidukkha manussaraṃ;</p>\r\n<p class="gathalast">Nibbinda yutta cittena, vajjeyya bhavasāta to.</p>\r\n<p class="hangnum"><a name="para110"></a><span class="paranum">110</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-109-110-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',83,replace(replace('<p class="gatha1">Puñña <a name="M0.0083"></a> nibbatta ṭhānepi, jegucche sandhisambhavo;</p>\r\n<p class="gathalast">Bhava sāta vasā tasmā, dhīro taṃ lagganaṃ caje.</p>\r\n<p class="chapter">3. Asubhabhāvanā niddesa</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',84,replace(replace('<p class="gatha1">Sirimaṃ <a name="M0.0084"></a> gaṇikaṃ disvā, dametuṃ rattacetasaṃ;</p>\r\n<p class="gathalast">Dassetvā matasārīraṃ, tassā jino idaṃ bravi.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',89,replace(replace('<p class="gatha1">Caraṃvā <a name="M0.0089"></a> yadivā tiṭṭhaṃ, nisinno udavā sayaṃ;</p>\r\n<p class="gathalast">Samañcheti pasāreti, esā kāyassa iñjanā.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Aṭṭhi nhārūhi saṃyutto, taca maṃsāva lepano;</p>\r\n<p class="gathalast">Chaviyā kāyo paṭicchanno, yathābhūtaṃ nadissati.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',90,replace(replace('<p class="gatha1">Antapūro <a name="M0.0090"></a> darapūro, yakana peḷassa vatthino;</p>\r\n<p class="gathalast">Hadayassa papphāsassa, vakkassa pihakassaca.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Siṅghānikāya kheḷassa, sedassaca medassaca;</p>\r\n<p class="gathalast">Lohitassa lasikāya, pittassaca vasāyaca.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',91,replace(replace('<p class="gatha1">Athassa <a name="M0.0091"></a> navahi sotehi, asuci savati sabbadā;</p>\r\n<p class="gathalast">Akkhimhā akkhigūthako, kaṇṇamhā kaṇṇagūthako.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Siṅghānikāca nāsato, mukhato vamati ekadā;</p>\r\n<p class="gathalast">Pittaṃ semhañca vamati, kāyamhā sedajallikā.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',92,replace(replace('<p class="gatha1">Athassa <a name="M0.0092"></a> susiraṃ sīsaṃ,</p>\r\n<p class="gatha2">Matthaluṅgassa pūritaṃ;</p>\r\n<p class="gatha3">Subhato naṃ maññati bālo,</p>\r\n<p class="gathalast">Avijjāya purakkhato.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Yadāca so mato seti,</p>\r\n<p class="gatha2">Uddhumāto vinīlako;</p>\r\n<p class="gatha3">Apaviddho susānasmiṃ,</p>\r\n<p class="gathalast">Anapekkhā honti ñātayo.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',93,replace(replace('<p class="gatha1">Khādanti <a name="M0.0093"></a> naṃ suvānāca, siṅgālakāca kimiyo;</p>\r\n<p class="gathalast">Kākā gijjhāca khādanti, yecaññe santi pāṇakā.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',94,replace(replace('<p class="gatha1">Sutvāna <a name="M0.0094"></a> buddhavacanaṃ, bhikkhu paññāṇavā idha;</p>\r\n<p class="gathalast">Sokho naṃ parijānāti, yathābhūtañhi passati.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> Yathāidaṃ tathāetaṃ, yathāetaṃ tathāidaṃ;</p>\r\n<p class="gathalast">Ajjhattañca bahiddhāca, kāye chandaṃ virājaye.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',95,replace(replace('<p class="gatha1">Chanda <a name="M0.0095"></a> rāga viratto so, bhikkhu paññāṇavā idha;</p>\r\n<p class="gathalast">Ajjhagā amataṃ santiṃ, nibbānaṃ pada maccutaṃ.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> Dvipādako yaṃ asuci, duggandho parihārati;</p>\r\n<p class="gathalast">Nānākuṇapa paripūro, vissavanto tatotato.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',96,replace(replace('<p class="gatha1">Etādisena <a name="M0.0096"></a> kāyena, yo maññe unnametave;</p>\r\n<p class="gatha2">Paraṃvā avajāneyya, kimaññatra adassanāti;</p>\r\n<p class="gathalast">Kāya vicchandanīyasuttaṃ, vijayasuttantipi vattabbaṃ.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',97,replace(replace('<p class="gatha1">Ra-akkharo <a name="M0.0097"></a> siyāggimhi, rova aggiva āgato;</p>\r\n<p class="gathalast">Tasmā rāgoti vattabbo, taṇhāva niccatāpikā.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> Abhiṇhameva rāgaggi, dayhate subhasaññinaṃ;</p>\r\n<p class="gathalast">Kimikhajjavaṇo sāva, dukkhī rāgī sa sabbadā.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-17-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',98,replace(replace('<p class="gatha1">Dukkhī <a name="M0.0098"></a> piya maladdhāna, laddhāpya paripuṇṇato;</p>\r\n<p class="gathalast">Natthi rāgaggikhandhassa, piyindhena hi puṇṇatā.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',99,replace(replace('<p class="gatha1">Natthi <a name="M0.0099"></a> rāgasamo aggi,</p>\r\n<p class="gatha2">Iti vuttaṃ mahesinā;</p>\r\n<p class="gatha3">Tena rāgagginā daḍḍho,</p>\r\n<p class="gathalast">Sabbo loko tidukkhito.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> Ekassa pivitaṃ khīraṃ, catū dadhi jalā bahu;</p>\r\n<p class="gathalast">Rāgahetu bhave sandhi, ṭṭhānaṃ anamataggikaṃ.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',100,replace(replace('<p class="gatha1">Catūdami <a name="M0.0100"></a> jalā bhiyyo, sīsacchedana lohitaṃ;</p>\r\n<p class="gathalast">Rāgahetu bhave macca, bhayaṃ anamataggikaṃ.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> Ekassa rudato assu, catū dadhi jalā bahu;</p>\r\n<p class="gathalast">Dukkhaṃ anamataggaṃva, taṃhetu paridevanaṃ.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',101,replace(replace('<p class="gatha1">Tattāyo <a name="M0.0101"></a> guḷa gilita, vadhaggi dayhanā dikaṃ;</p>\r\n<p class="gathalast">Asaṅkhyeyyaṃ mahādukkhaṃ, taṃhetu niraye labhi.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> Eka dvitti catu pañca, buddhuppādepya mocitaṃ;</p>\r\n<p class="gathalast">Khuppipāsita nijjhāmaṃ, labhi taṃhetu pettikaṃ.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-24-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',102,replace(replace('<p class="gatha1">Tiracchāne <a name="M0.0102"></a> asūreca, dukkhaṃ nānāvidhaṃ labhi;</p>\r\n<p class="gathalast">Namataggika saṃsāre, sabbantaṃ rāgahetukaṃ.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Ekassekena kappena, puggalassaṭṭhi sañcayo;</p>\r\n<p class="gathalast">Sace saṃhārito assa, vepulla pabbatādhiko.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> Eka kappe idaṃ dukkhaṃ, nādikappesu kākathā;</p>\r\n<p class="gathalast">Rāgo nanu mahāverī, bālo jano ta micchati.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-26-27-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',103,replace(replace('<p class="gatha1">Rāgasuddhi <a name="M0.0103"></a> asokoca, niddukkho ñāyapattica;</p>\r\n<p class="gathalast">Nibbānaṃ pañca paccakkhā, asubhāya phalā matā.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',104,replace(replace('<p class="gatha1">Asubhaggahaṇaṃ <a name="M0.0104"></a> jhāyī, mitā sindriya saṃvaro;</p>\r\n<p class="gathalast">Somitya mukkaṭṭhāvācā, chaḷime rāga suddhiyā.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> Niccuggarāga rogīnaṃ, asubhā vātulosadhā;</p>\r\n<p class="gathalast">Rāgayakkhābhi gayhānaṃ, asubhā manta muttaraṃ.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',105,replace(replace('<p class="gatha1">Sajīvakāca <a name="M0.0105"></a> nijjīvā, asubhā duvidhā matā;</p>\r\n<p class="gathalast">Sajīvā kesalomādi, dasevime ajīvakā.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',106,replace(replace('<p class="gatha1">Uddhumātaka <a name="M0.0106"></a> vīnīlaṃ, vipubbakaṃ vichiddakaṃ;</p>\r\n<p class="gathalast">Vikkhāyitaka vikkhittaṃ, hativikkhitta lohitaṃ.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> Puḷuva ṭṭhika miccesu, laddhā aññataraṃ sato;</p>\r\n<p class="gathalast">Ratana vānapasseyya, yathā cetasi pākaṭaṃ.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-34-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',107,replace(replace('<p class="gatha1">Mataṃ <a name="M0.0107"></a> khajjaṃ sa maṃsañca, nilohitaṃ nimaṃsakaṃ;</p>\r\n<p class="gathalast">Vikkhittaṃ seta puñjaṭṭhiṃ, navadhā putimikkhaye.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',108,replace(replace('<p class="gatha1">Maccuto <a name="M0.0108"></a> parimuccāmi, paṭivatti yimā yiti;</p>\r\n<p class="gathalast">Payojana samāvajja, moditabbaṃ jigucchake.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> Sajīvake jigucchatthaṃ, nijjīvā subha mīritaṃ;</p>\r\n<p class="gathalast">Tathūpamo ayaṃkāyo, evameva bhavissati.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-36-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',109,replace(replace('<p class="gatha1">Evaṃdhammo <a name="M0.0109"></a> ayaṃkāyo, evaṃbhāvī natikkamo;</p>\r\n<p class="gathalast">Iccupa saṃhare disvā, ekadviha matādikaṃ.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',110,replace(replace('<p class="gatha1">Yathā <a name="M0.0110"></a> idaṃ tathāetaṃ, yathāetaṃ tathā idaṃ;</p>\r\n<p class="gathalast">Jegucchaṃ paṭikūlyañca, kāye iccupa saṃhare.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> Uddhumāta vinīlādi,</p>\r\n<p class="gatha2">Paṭikūlyo jigucchito;</p>\r\n<p class="gatha3">Tathevāyampi me kāyo,</p>\r\n<p class="gathalast">Viseso nāyu-sāyuva.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',111,replace(replace('<p class="gatha1">Uddhumāta <a name="M0.0111"></a> vinīlādo, sobhaṇaṃ natthi kiñcipi;</p>\r\n<p class="gathalast">Imasmiṃpi me kāye, gavesantopi sabbaso.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> Paṭikūlavasā dhātu, vasāca dvippakārato;</p>\r\n<p class="gathalast">Paccavekkheyyimaṃ kāyaṃ, icchaṃ virāga mattani.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',112,replace(replace('<p class="gatha1">Vaṇṇa <a name="M0.0112"></a> saṇṭhāna gandhehi, āsayo kāsatopica;</p>\r\n<p class="gathalast">Jeguccha paṭikūlyāca, kesā na tuṭṭhamānitā.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> Iti kesesu ikkheyya,</p>\r\n<p class="gatha2">Lomā dīsupyayaṃ nayo;</p>\r\n<p class="gatha3">Dvattiṃsevañhi koṭṭhāse,</p>\r\n<p class="gathalast">Paccavekkhe visuṃvisuṃ.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-43-44-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',113,replace(replace('<p class="gatha1">Kāyato <a name="M0.0113"></a> bahinikkhantaṃ, paṭikūlyaṃ jigucchitaṃ;</p>\r\n<p class="gathalast">Anikkhantampi jegucchaṃ, paṭikūlyaṃva tassamaṃ.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> Saṅkhatampi yathā vaccaṃ, manuññataṃ na pāpuṇe;</p>\r\n<p class="gathalast">Upakkama sahassehi, evaṃ kesādikampica.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-45-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',114,replace(replace('<p class="gatha1">Sabhāva <a name="M0.0114"></a> paṭikūlyaṃva, ekampi vacca puñjakaṃ;</p>\r\n<p class="gathalast">Nanu jegucchitā bhiyyo, dvattiṃsa vaccapuñjakā.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-47-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',115,replace(replace('<p class="gatha1">Paccekampi <a name="M0.0115"></a> paṭikūlyaṃ, kesādikaṃ sabhāvato;</p>\r\n<p class="gathalast">Kesādidvattiṃsa puñjo, bhiyyo jegucchito nanu.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> Puñjitesveva kantesu, kantohoti sa puñjako;</p>\r\n<p class="gathalast">Puñjitesu akantesa, akantova sa puñjako.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-48-49-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',116,replace(replace('<p class="gatha1">Paccekaṃ <a name="M0.0116"></a> vinibhuttesa, kesa loma nakhādisu;</p>\r\n<p class="gathalast">Natthi taññā kumārīvā, mukhahatthādikānivā.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> Sampiṇḍi tesu tesveva,</p>\r\n<p class="gatha2">Kuto tā tāni āgatā;</p>\r\n<p class="gatha3">Paññatti matta mevesā,</p>\r\n<p class="gathalast">Jigucchaññā na kācipi.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-50-51-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',117,replace(replace('<p class="gatha1">Santaṃ <a name="M0.0117"></a> cinteyya nāsantaṃ, santa cintayato sukhaṃ;</p>\r\n<p class="gathalast">Asantaṃ parikappento, nānādukkhehi tappati.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> Nāvajja santajegucchaṃ, saññaṃ asati kātuna;</p>\r\n<p class="gathalast">Subhā itthīti gāraggi, uppajji subhasaññino.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-52-53-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',118,replace(replace('<p class="gatha1">Asantaṃva <a name="M0.0118"></a> abhūtaṃva, passe rāgaggijotiyā;</p>\r\n<p class="gathalast">Tāya santañca bhūtañca, na passati kadācipi.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-54-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',119,replace(replace('<p class="gatha1">Ekassa <a name="M0.0119"></a> pivitaṃ khīraṃ, sīsacchedana lohitaṃ;</p>\r\n<p class="gathalast">Rudato assu taṃhetu, catūdadhi jalā bahu.</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> Āyatimpi atīteva, saṃsarantassa hessati;</p>\r\n<p class="gathalast">Rāgaṃ hantu manīhoce, khīraṃ assuca lohitaṃ.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-55-56-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',120,replace(replace('<p class="gatha1">Subhasaññāya <a name="M0.0120"></a> so vaḍḍho,</p>\r\n<p class="gatha2">Tadabhāve sa nassati;</p>\r\n<p class="gatha3">Thiraṃ hantuṃ na taṃsaññaṃ,</p>\r\n<p class="gathalast">Sakkā sithila vīriyo.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-57-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',121,replace(replace('<p class="gatha1">Ussoḷhi <a name="M0.0121"></a> vīriyo hutvā, brūheyyāsubha bhāvanaṃ;</p>\r\n<p class="gathalast">Subhasaññāppa hānāya, pariccajjāpi jīvitaṃ.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> Aññakicca mupekkhāya, brūheyyāsubha bhāvanaṃ;</p>\r\n<p class="gathalast">Mandi hutveha rāgaggi, nibbāyissati āyatiṃ.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> Kiccaṃ me idameveti, brūheyyāsubha bhāvanaṃ;</p>\r\n<p class="gathalast">Dāni mandaggi hutvāna, pāmojjaṃ ve labhissati.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-58-59-60-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',122,replace(replace('<p class="gatha1">Kāye <a name="M0.0122"></a> daṭṭhabba jegucchaṃ, apassanto pamādavā;</p>\r\n<p class="gathalast">Aladdhā kiñci pāmojjaṃ, pabbajjampi na modati.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> Pure marāmi kāye smiṃ, passā mi passitabbakaṃ;</p>\r\n<p class="gathalast">Iccā raddho vītiṃladdhā, pabbajjaṃ atimodati.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-61-62-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',123,replace(replace('<p class="gatha1">Kāye <a name="M0.0123"></a> daṭṭhabba jegucchaṃ, apassanto pamāda vā;</p>\r\n<p class="gathalast">Moghaṃva dullabhātīto, mahājānīyataṃ gato.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> Santaṃ bhūtañca jegucchaṃ, rāgagginā apassiyaṃ;</p>\r\n<p class="gathalast">Paññāpadīpajotena, samikkheyya abhiṇhaso.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-63-64-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',124,replace(replace('<p class="gatha1">Santaṃ <a name="M0.0124"></a> bhūtañca kāye smiṃ, daṭṭhukāmo sadāsato;</p>\r\n<p class="gathalast">Paññāpadīpakeneva, dakkhe na rāgīsīkhinā.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> Jegucchitena kāyena, nikkhantena jigucchato;</p>\r\n<p class="gathalast">Ajja svevā vinaṭṭhena, nāla munnamituṃ sato.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-65-66-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',125,replace(replace('<p class="gatha1">Kīdisaṃ <a name="M0.0125"></a> maṃ tuvaṃ maññi, ahaṃ sabba jegucchako;</p>\r\n<p class="gathalast">Jegucchatoca nikkhanto, icceva vattu marahati.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> Kāye jegucchasaññaṃva, kare sabbiriyā pathe;</p>\r\n<p class="gathalast">Tasmiṃ tuṭṭhabbakaṃ natthi, piyāyitaṃ mamāyitaṃ.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-67-68-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',126,replace(replace('<p class="gatha1">Subhāya <a name="M0.0126"></a> nava mattānaṃ, asubhā paripācaye;</p>\r\n<p class="gathalast">Santo pakkassa saṃsāro, nanto navassa rāgino.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> Kāye asubha saññāya, paripakka sabhāvino;</p>\r\n<p class="gathalast">Ālambesu acāpallā, thirā sambuddha sāsane.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-69-70-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',127,replace(replace('<p class="gatha1">Anto <a name="M0.0127"></a> gocarikā pakkā, bahi gocarikā navā;</p>\r\n<p class="gathalast">Pakkā nāsāya uccā te, nīcāyeva navā sino.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',128,replace(replace('<p class="gatha1">Navānavā <a name="M0.0128"></a> subhābhogī, nīcānīcā bhigāmino;</p>\r\n<p class="gathalast">Pakkā pakkāva dhījhāyī, santāsantā virāgino.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> Sakkā sakkā na dassetuṃ, subhaṃsubhaṃ sataṃsataṃ;</p>\r\n<p class="gathalast">Dhīrādhīrāga mujjhanti, kāye kāye kriyekriye.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-72-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',129,replace(replace('<p class="gatha1">Sakkā <a name="M0.0129"></a> sakkāpi taṃ kātuṃ, subhaṃsubhaṃ na dhīmayaṃ;</p>\r\n<p class="gathalast">Santosantojigucchaññū, na vānavā subhesako.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-74-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',130,replace(replace('<p class="gatha1">Dukāyaṃ <a name="M0.0130"></a> suti cintetvā, mamāyantā mahātapā;</p>\r\n<p class="gathalast">Tapaṃ nibbāyituṃ icchaṃ, dukāyaṃ duti cintaye.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> Yvāsubhaṃ subhato maññi, konubālotaduttari;</p>\r\n<p class="gathalast">Andho ummattakovā so, nattānaṃ maññate tathā.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-75-76-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',131,replace(replace('<p class="gatha1">Saritabbaka <a name="M0.0131"></a> mevetaṃ, kāye jeguccha puñjataṃ;</p>\r\n<p class="gathalast">Mandarāgo manosītaṃ, labheyya tamanussanaṃ.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> Gihibhāve apāyeca, rāgayakkhandha ninnitā;</p>\r\n<p class="gathalast">Maṃpi nessati soyakkho, sādemice tadāgataṃ.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-77-78-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',132,replace(replace('<p class="gatha1">Abhiṇha <a name="M0.0132"></a> gāhinaṃ rāga, yakkhaṃ ananta dukkha daṃ;</p>\r\n<p class="gathalast">Asubhā tula mantena, vārehi taṃ sa bhāyati.</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> Rāgayakkho bahumāyo, saddhāmettā divesavā;</p>\r\n<p class="gathalast">Rāgampi kusalaṃ maññi, jano teneva vañcito.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-79-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',133,replace(replace('<p class="gatha1">Āturaṃ <a name="M0.0133"></a> asuciṃ putiṃ, passa nande samussayaṃ;</p>\r\n<p class="gathalast">Uggharantaṃ paggharantaṃ, bālānaṃ abhipatthitaṃ.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> Subhato naṃ maññati bālo, avijjāya purakkhato;</p>\r\n<p class="gathalast">Iccāha bhagavā nindi, bāloti subhasaññinaṃ.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-81-82-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',134,replace(replace('<p class="gatha1">Nhāruṭṭhi <a name="M0.0134"></a> taca maṃsāni, saraṃ sataṃ na nindito;</p>\r\n<p class="gathalast">Buddhanindāya mocetuṃ, tānārabbha anussare.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> Maññitvā attano bālyaṃ, asubhe subhadassino;</p>\r\n<p class="gathalast">Vāyāmeyya abālāya, kāyaṃ asubhato saraṃ.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-83-84-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',135,replace(replace('<p class="gatha1">Attānaṃ <a name="M0.0135"></a> garahitvāna, bālaṃ viparidassinaṃ;</p>\r\n<p class="gathalast">Subhasaññaṃ pahinneyya, kareyyāsubha saññitaṃ.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> Visa bhesajjarukkhaṭṭho, ahi ḍaṃseyyasosato;</p>\r\n<p class="gathalast">Yathā tasseva paṇṇādiṃ, khādetvā visa mujjahe.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> Evaṃ rāgo samuppajje, kāye gandhādi vāsite;</p>\r\n<p class="gathalast">Anto tasseva jegucchaṃ, cintetvā rāga mujjahe.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-85-86-87-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',136,replace(replace('<p class="gatha1">Jigucchitena <a name="M0.0136"></a> kāyena, apassanto jigucchataṃ;</p>\r\n<p class="gathalast">Unnameti avaññāti, avijjāya purakkhato.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-88-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',137,replace(replace('<p class="gatha1">Āyatiṃ <a name="M0.0137"></a> maggalābhāya, bījaṃ kareyya bhāvanaṃ;</p>\r\n<p class="gathalast">Bījā bhāve kuto maggo, maggabījā hi bhāvanā.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> Maggabījo apāyepi, nimmuggo samaye gate;</p>\r\n<p class="gathalast">Ummujjitvāva buddhānaṃ, maggaṃ labheyya santike.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-89-90-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',138,replace(replace('<p class="gatha1">Ajījassa <a name="M0.0138"></a> tu saṃsāro, dīghoyeva anantiko;</p>\r\n<p class="gathalast">Tasmāhi bhāvanābījaṃ, kareyya mocanatthiko.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> Abhiṇha pīḷitaṃ rāgaṃ, asubhāya nivāraye;</p>\r\n<p class="gathalast">Mandīhutvā pahīyeyya, rāgo asubha bhīruko.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-91-92-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',139,replace(replace('<p class="gatha1">Mājegucchaṃ <a name="M0.0139"></a> mamāyetha, sāva jegucchamāmako;</p>\r\n<p class="gathalast">Ananta dukkha māpādi, jegucchita mamāyanā.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> Maṃsalaggo tacacchanno,</p>\r\n<p class="gatha2">Nhārubandho ṭṭhipuñjako;</p>\r\n<p class="gatha3">Moheti chaviyā lokaṃ,</p>\r\n<p class="gathalast">Mahādukkho sa mohito.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-93-94-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',140,replace(replace('<p class="gatha1">Nhāruṭṭhi <a name="M0.0140"></a> taca maṃsehi, rāgavaḍḍhaki saṅkhate;</p>\r\n<p class="gathalast">Gehe rogā putī pāpā, vasanti kucchitā sadā.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> Luṅgantā vīsa bhūdhātū, pittādī dvādasambuva;</p>\r\n<p class="gathalast">Tāpaṃ jiraṃ dahaṃ pakkaṃ, caturaggi chavāyukā.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> Adhoddhaṃ kucchi koṭṭhāsā,</p>\r\n<p class="gatha2">Aṅgacārīca pāṇakā;</p>\r\n<p class="gatha3">Dhātuyoyeva kāyesmiṃ,</p>\r\n<p class="gathalast">Dvitālīsa anaññakā.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-95-96-97-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',142,replace(replace('<p class="gatha1">Yathā <a name="M0.0142"></a> bahi tathā ajjhattaṃ, dhātū bhvāpā nalānilā;</p>\r\n<p class="gathalast">Name nāhaṃ naattāti, saṃmaseyya punappunanti.</p>\r\n<p class="subhead">4. Mettābhāvanāniddesa</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',145,replace(replace('<p class="gatha1">Mettā <a name="M0.0145"></a> bhāvana micchampi, suṇa buddhavaco yidaṃ;</p>\r\n<p class="gathalast">Dosa niggahaṇatthāya, doso mettāyaverihi.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Akkocchimaṃ avadhimaṃ, ajinimaṃ ahāsime;</p>\r\n<p class="gathalast">Yeca taṃ upanayhanti, veraṃ tesaṃ nasammati.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',146,replace(replace('<p class="gatha1">Akkocchimaṃ <a name="M0.0146"></a> avadhimaṃ, ajinimaṃ ahāsime;</p>\r\n<p class="gathalast">Yecataṃ nupanayhanti, veraṃ tesaṃ upasammati.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Nahiverena verāni, sammantidha kudācana;</p>\r\n<p class="gathalast">Averenaca sammanti, esadhammo sanantano.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',147,replace(replace('<p class="gatha1">Pareca <a name="M0.0147"></a> navijānanti, maya mettha yamāmase;</p>\r\n<p class="gathalast">Yeca tattha vijānanti, tato sammanti medhagā.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Kuddho atthaṃ najānāti, kuddho dhammaṃ napassati;</p>\r\n<p class="gathalast">Sadā andhatamaṃ hoti, yaṃkodho sahatenaraṃ.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',148,replace(replace('<p class="gatha1">Ubhinna <a name="M0.0148"></a> matthaṃ carati, attanoca parassaca;</p>\r\n<p class="gathalast">Paraṃ saṃkuppitaṃ ñatvā, yo sato upasammati.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Tasseva tena pāpiyyo, yo kuddhaṃ paṭikujjhati;</p>\r\n<p class="gathalast">Kuddhaṃ apaṭikujjhanto, saṅgāmaṃ jeti dujjayaṃ.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-8-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',149,replace(replace('<p class="gatha1">Khantī <a name="M0.0149"></a> paramaṃ tapo titikkhā,</p>\r\n<p class="gatha2">Nibbānaṃ paramaṃ vadanti buddhā;</p>\r\n<p class="gatha3">Nahi pabbajito parūpaghātī,</p>\r\n<p class="gathalast">Nasamaṇo hoti paraṃ viheṭhayanto.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> Akodhena jine kodhaṃ, asādhuṃ sādhunā jine;</p>\r\n<p class="gathalast">Jine kadariyaṃ dānena, saccenā likavādinaṃ.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',150,replace(replace('<p class="gatha1">Yo <a name="M0.0150"></a> ve uppatitaṃ kodhaṃ, rathaṃ bhantaṃva vāraye;</p>\r\n<p class="gathalast">Ta mahaṃ sārathī brūmi, rasmiggāho itarojano.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',151,replace(replace('<p class="gatha1">Purisassa <a name="M0.0151"></a> hi jātassa, kudhārī jāyate mukhe;</p>\r\n<p class="gathalast">Yāya chindati attānaṃ, bālo dubbhāsitaṃbhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> Selo yathā ekagghano, vātena nasamīrati;</p>\r\n<p class="gathalast">Evaṃ nindā pasaṃsāsu, nasamiñjanti paṇḍitā.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',152,replace(replace('<p class="gatha1">Samānabhāgaṃ <a name="M0.0152"></a> krubbetha, gāme akkuṭṭha vanditaṃ;</p>\r\n<p class="gathalast">Manopadosaṃ rakkheyya, santo anuṇṇato siyā.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Naparo paraṃ nikuppetha, nātimaññetha katthaci nakiñci;</p>\r\n<p class="gathalast">Byārosanā paṭighasaññā, naññamaññassa dukkha miccheyya.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',153,replace(replace('<p class="gatha1">Mātā <a name="M0.0153"></a> yathā niyaṃ putta,</p>\r\n<p class="gatha2">Māyusā ekaputta manurakkhe;</p>\r\n<p class="gatha3">Evampi sabba bhūtesu,</p>\r\n<p class="gathalast">Mānasaṃ bhāvaye aparimāṇaṃ.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> Sutvāna dusito bahuṃ vācaṃ,</p>\r\n<p class="gatha2">Samaṇānaṃvā puthujanānaṃ;</p>\r\n<p class="gatha3">Pharusena hi na paṭivajjā,</p>\r\n<p class="gathalast">Na hi santo paṭiseniṃ karonti.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-17-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',154,replace(replace('<p class="gatha1">Saccaṃ <a name="M0.0154"></a> bhaṇe nakujjheyya, dajjā appampi yācito;</p>\r\n<p class="gathalast">Etehi tīhi ṭhānehi, gacche devāna santike.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> Na paresaṃ vilomāni, na paresaṃ katākataṃ;</p>\r\n<p class="gathalast">Attanova avekkheyya, katāni akatānica.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',155,replace(replace('<p class="gatha1">Su <a name="M0.0155"></a> dasaṃ vajjamaññesaṃ, attano pana duddasaṃ;</p>\r\n<p class="gatha2">Paresañhi so vajjāni, ophunāti yathā bhusaṃ;</p>\r\n<p class="gathalast">Attano pana chādeti, kaliṃva kitavā saṭṭho.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',156,replace(replace('<p class="gatha1">Nidhīnaṃva <a name="M0.0156"></a> pavattānaṃ, yaṃ passe vajjadassinaṃ;</p>\r\n<p class="gathalast">Niggayhavādiṃ medhāviṃ, tādisaṃ paṇḍitaṃ bhaje.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> Tādisaṃ bhajamānassa,</p>\r\n<p class="gatha2">Seyyohoti napāpiyo;</p>\r\n<p class="gatha3">Iti vuttaṃ munindena,</p>\r\n<p class="gathalast">Tilokaggena satthunā.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',157,replace(replace('<p class="gatha1">Mettā <a name="M0.0157"></a> gandhena vāsento,</p>\r\n<p class="gatha2">Dosaṃ dūrekare budho;</p>\r\n<p class="gatha3">Dūrāsannesu sabbesu,</p>\r\n<p class="gathalast">Attano verikesupi.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> Hane dosū panāhāni, anattha kārakāni hi;</p>\r\n<p class="gathalast">Tesva santesu sabbesu, mettāhoti sunimmalā.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-24-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',158,replace(replace('<p class="gatha1">Sataṃ <a name="M0.0158"></a> dujjana vākyehi, namano yāti vikriyaṃ;</p>\r\n<p class="gathalast">Nahitāpayituṃ sakkā, gaṅgānadiṃ tiṇukkayā.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Nahi nindā pasaṃsāhi, sataṃ manovikāratā;</p>\r\n<p class="gathalast">Na kadācipi kampeyya, vātehi selapabbato.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-26-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',159,replace(replace('<p class="gatha1">Nadiyaṃ <a name="M0.0159"></a> khuddakā nāvā, vicīhi unnatonatā;</p>\r\n<p class="gathalast">Mahānāvā nakampanti, mahantīhi vicīhipi.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> Loke pasaṃsa nindāhi, dujjanovunnatonato;</p>\r\n<p class="gathalast">Santopañño nacalati, mahānindā thutīhipi.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-28-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',160,replace(replace('<p class="gatha1">Seloselo <a name="M0.0160"></a> nileheva,</p>\r\n<p class="gatha2">Vaṇṇāvaṇṇā asassatā;</p>\r\n<p class="gatha3">Lābhālābhā sukhādukkhā,</p>\r\n<p class="gathalast">Yasāyasā nakampati.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> Khamādhagga karetassa, dujjano kiṃ karissati;</p>\r\n<p class="gathalast">Atiṇe patito aggi, sayameva pasambhati.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',161,replace(replace('<p class="gatha1">Sayameva <a name="M0.0161"></a> sakattānaṃ, maccubbhayena tacchatu;</p>\r\n<p class="gathalast">Māññe tacchatu dosena, kimatthaṃ aññatacchanaṃ.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> Māññe taccha tudosena, naseyyo aññatacchanaṃ;</p>\r\n<p class="gathalast">Māñño taṃ ahibyaggheva, domanassena bhāyatu.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-32-33-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',162,replace(replace('<p class="gatha1">Nissāya <a name="M0.0162"></a> garukātabbaṃ, bahūnaṃ pāpamocanaṃ;</p>\r\n<p class="gathalast">Acāpallena santena, garukātabbataṃ vaje.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> Nissāya garukātabbaṃ, bahūnaṃ puññavaḍḍhanaṃ;</p>\r\n<p class="gathalast">Garukātabbataṃ gacche, dhītiyā sīla guttiyā.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-34-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',163,replace(replace('<p class="gatha1">Santaṃ <a name="M0.0163"></a> hi sīlavaṃ dhītiṃ, hirottappena bhāyati;</p>\r\n<p class="gathalast">Dujjanaṃ domanassena, ahibyaggheva bhāyati.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> Napharusāya vācāya, aññe dameyya paṇḍito;</p>\r\n<p class="gathalast">Attānaṃva dametvāna, aññe saṇhena ovade.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-36-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',164,replace(replace('<p class="gatha1">Citte <a name="M0.0164"></a> saṇhe asaṇhāpi, navācāpharusā bhave;</p>\r\n<p class="gathalast">Tasmā ovā danādīsu, rakkheyya thaddhacittato.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> Attāna movadatthāya, sikkheyya buddhabhāsitaṃ;</p>\r\n<p class="gathalast">Parampi anukampāya, icchanto anusāsaye.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> Aññaṃ naniggahe kiñci, sutena paṭipattiyā;</p>\r\n<p class="gathalast">Attaniggahaṇaṃ seyyo, nunnameyya jinoraso.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-38-39-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',165,replace(replace('<p class="gatha1">Nāvīkareyya <a name="M0.0165"></a> dosaṃvā, lobhaṃ mānaṃ sakaṃmalaṃ;</p>\r\n<p class="gathalast">Māññe maññantu taṃ disvā, cirappabba jito nuti.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> Kakacena ttachedente, verikepinadosaye;</p>\r\n<p class="gathalast">Iccovādaṃ munindassa, sampaṭiccha jinoraso.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',166,replace(replace('<p class="gatha1">Verī <a name="M0.0166"></a> accupanāhīpi, rūpeva dukkhakārako;</p>\r\n<p class="gathalast">Na tva tabbisaye nāme, dukkhaṃ mākari cetasi.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> Verī tibandha veropi, iheva dukkhakārako;</p>\r\n<p class="gathalast">Bhavantaraṃ naanveti, sakammunā gato hiso.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-43-44-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',167,replace(replace('<p class="gatha1">Dosotu <a name="M0.0167"></a> iha pīḷetvā,</p>\r\n<p class="gatha2">Dukkhāvaho bhavebhave;</p>\r\n<p class="gatha3">Mahānattha karaṃ dosaṃ,</p>\r\n<p class="gathalast">Kasmā vaḍḍheti cetasi.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> Mettāsītambusekena, jinovāda manussaraṃ;</p>\r\n<p class="gathalast">Mahā nattha karaṃ dosaṃ, nibbāyatu sa cetasi.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-45-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',168,replace(replace('<p class="gatha1">Chaddanto <a name="M0.0168"></a> luddakaṃ pāpaṃ, bhūridattohi tuṇḍikaṃ;</p>\r\n<p class="gathalast">Dhammapālo khami tātaṃ, kapindo kandaro pataṃ.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> Asaṅkhyeyya ttabhāvesu, paravajjaṃ titikkhato;</p>\r\n<p class="gathalast">Nāthassa pāramiṃ khantiṃ, saraṃ dhīro titikkhatu.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-47-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',169,replace(replace('<p class="gatha1">Sāsane <a name="M0.0169"></a> ciravāsena, evaṃ niddosakā iti;</p>\r\n<p class="gathalast">Tuvaṃ paṭicca maññantu, sāsane sappayojanaṃ.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> Sāsane cira vāsāpi, mādisāva ime iti;</p>\r\n<p class="gathalast">Tamāgamma namaññantu, sāsane nippayojanaṃ.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> Dve usetīti doso so, saparaṃ dayhate dvayaṃ;</p>\r\n<p class="gathalast">Pahātabbo sa sabbesu, parattha sattha micchatā.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-49-50-51-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',170,replace(replace('<p class="gatha1">Paradinnehi <a name="M0.0170"></a> noāyu, tiṭṭhate nāttano vasā;</p>\r\n<p class="gathalast">Paravajjaṃ khametabbaṃ, nasādhu aññavirodhito.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',171,replace(replace('<p class="gatha1">Jeguccha <a name="M0.0171"></a> kkosa nindāni, bālo gaṇhāti akkhamo;</p>\r\n<p class="gathalast">Khamantotu nagaṇhāti, jānaṃ jegucchitāniti.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> Paradinnāni vaccāni, pābhatanti nakocipi;</p>\r\n<p class="gathalast">Gaṇheyyevaṃ duruttāni, agaṇhanto khame sato.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-53-54-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',172,replace(replace('<p class="gatha1">Nadi <a name="M0.0172"></a> kallola viciyo, tīraṃ patvā samantidha;</p>\r\n<p class="gathalast">Sabbe uppatitā dosā, khantipatvā samanti te.</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> Dosummattaka vācāya, nummatto kiṃkarissati;</p>\r\n<p class="gathalast">Bhave yyummattako sova, tādisaṃ vacanaṃ bhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> Kodhano akkhamo añña, duṭṭhasaññī bhayāluko;</p>\r\n<p class="gathalast">Gāmamajjhe aḷakkova, tathā māhohi taṃ jaha.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-55-56-57-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',173,replace(replace('<p class="gatha1">Mettāluko <a name="M0.0173"></a> khamāsīlo,</p>\r\n<p class="gatha2">Sabbaṭṭhānesunibbhayo;</p>\r\n<p class="gatha3">Parattha sattha micchanto,</p>\r\n<p class="gathalast">Khanti mettañca bhāvaye.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> Parakkosāni nindāni, taṃva paccenti nāññagū;</p>\r\n<p class="gathalast">Khittaṃpaṃsuva vātuddhaṃ, garukaṃ kiṃ khamāyate.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-58-59-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',174,replace(replace('<p class="gatha1">Akkosantoca <a name="M0.0174"></a> nindīca, pīḷito saka kammunā;</p>\r\n<p class="gathalast">Idha peccaca nīceyyo, naṃnayaṃ gaṇhi akkhamo.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-60-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',175,replace(replace('<p class="gatha1">Akkoso <a name="M0.0175"></a> maṃ naāgacchi,</p>\r\n<p class="gatha2">Tassevā natthakārako;</p>\r\n<p class="gatha3">Iti ñatvāva sappañño,</p>\r\n<p class="gathalast">Akkosaṃ na garuṃ kare.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> Vikārāpatti micchanto, verī bahu mupakkami;</p>\r\n<p class="gathalast">Māmittavasa manvehi, nibbikāro tuvaṃbhava.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> Mettambunā saddosoca, paradosoca sammati;</p>\r\n<p class="gathalast">Mettāsekena sabbesu, sabbatoggiṃ nipāraye.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-61-62-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',176,replace(replace('<p class="gatha1">Sadosa <a name="M0.0176"></a> paradosaggiṃ, sabbato disato ṭṭhitaṃ;</p>\r\n<p class="gathalast">Mettā toyena vāreyya, siyā nibbuti sabbadhi.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> Nagame attano aggiṃ, paraggiṃvāpi nāgame;</p>\r\n<p class="gathalast">Mettambunāva nibbātu, saparaggi dvayaṃ bhusaṃ.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-64-65-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',177,replace(replace('<p class="gatha1">Guṇī <a name="M0.0177"></a> guṇī nandindāya, pasaṃsāya guṇī guṇī;</p>\r\n<p class="gathalast">Nindaṃnindaṃ nakuppeyya, nasādiye thutiṃ thutiṃ.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> Guṇaṃ nindāya nāsetuṃ, nasakkā koci kussako;</p>\r\n<p class="gathalast">Vaḍḍhetuṃvā pasaṃsāya, garuṃkare na taṃdvayaṃ.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-66-67-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',178,replace(replace('<p class="gatha1">Dosabbhā <a name="M0.0178"></a> mala sañchanno, mettācando na rocati;</p>\r\n<p class="gathalast">Taṃmuttassa tu etassa, atissaya pabhāvato.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> Su sutta buddha supinā, dvepiyā gutti nākkamo;</p>\r\n<p class="gathalast">Samādhi sumukhā muḷhā, brahmā tyekā dasa gguṇā.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-68-69-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',179,replace(replace('<p class="gatha1">Sītaṃ <a name="M0.0179"></a> karotu mettāya, cakkhuṃ lābhetu paññāya;</p>\r\n<p class="gathalast">Mākāsinippabhe caññe, cando hohi gategate.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-70-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',180,replace(replace('<p class="gatha1">Dūrāsannesu <a name="M0.0180"></a> sabbesu, mettaṃ pesetu pābhataṃ;</p>\r\n<p class="gathalast">Dhammaṃ desetu pattānaṃ, cando hohi gategate.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> Sampattānaṃ malaṃ dhova, sītaṃkare sadādayo;</p>\r\n<p class="gathalast">Uccanīce navisese, jalassamo gategate.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-71-72-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',181,replace(replace('<p class="gatha1">Asaassatesu <a name="M0.0181"></a> phuṭṭhesu, lokadhammesu aṭṭhasu;</p>\r\n<p class="gathalast">Patiṭṭho nibbikāro tvaṃ, pathavīsadiso bhava.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> Nākāsi kalahaṃ silā, sadā kenaci niccalā;</p>\r\n<p class="gathalast">Mettāyanto khamāyanto, mahāsilaṃ guruṃkare.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-73-74-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',182,replace(replace('<p class="gatha1">Silāva <a name="M0.0182"></a> sīlavā hotu, duruttāni titikkhatu;</p>\r\n<p class="gathalast">Paccutte dosasaṃvaḍḍho, anuttova pasambhati.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> Sabbe ahaṃva icchanti, sattā sukhanti ñātuna;</p>\r\n<p class="gathalast">Bhāveyya kamato mettaṃ, piya majjhatta verike.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-75-76-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',183,replace(replace('<p class="gatha1">Sabbe <a name="M0.0183"></a> tasanti daṇḍassa, sabbe bhāyanti maccuno;</p>\r\n<p class="gathalast">Attānaṃ upamaṃ katvā, nahaneyya naghātaye.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> Sukha kāmāni bhūtāni,</p>\r\n<p class="gatha2">Yodaṇḍena vihiṃsati;</p>\r\n<p class="gatha3">Attano sukha mesāno,</p>\r\n<p class="gathalast">Pecca so nalabhesukhaṃ.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-77-78-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',184,replace(replace('<p class="gatha1">Averā <a name="M0.0184"></a> byāpajjā nīgho, sukhī cassaṃ ahaṃva me;</p>\r\n<p class="gathalast">Hitakāmā tathā assu, majjhattā verinopica.</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-79-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',185,replace(replace('<p class="gatha1">Mātaro <a name="M0.0185"></a> bhātaro ñātī, dāyako pāsakāpica;</p>\r\n<p class="gathalast">Sukhīhontūti bhāveyya, caje tesuca lagganaṃ.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> Doso mettāya dūrāri, taṇhā āsanna verikā;</p>\r\n<p class="gathalast">Taṇhaṃ piyesu vāreyya, dosaṃ verīsu mettiko.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-80-81-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',186,replace(replace('<p class="gatha1">Ekuddese <a name="M0.0186"></a> kakammāca, sissā ācariyā sukhī;</p>\r\n<p class="gathalast">Hontu sabrahmacārīca, teca ññoñña hitāvahā.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> Rājānoca amaccāca, gāme issariyā sukhī;</p>\r\n<p class="gathalast">Bhavantu devatāyoca, tehi surakkhito sukho.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-82-83-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',187,replace(replace('<p class="gatha1">Mayaṃ <a name="M0.0187"></a> yena suguttāva,</p>\r\n<p class="gatha2">Sukhitā raṭṭhavāsino;</p>\r\n<p class="gatha3">Sukhī kalla tthu sorājā,</p>\r\n<p class="gathalast">Tejavanto cirāyuko.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-84-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',188,replace(replace('<p class="gatha1">Raṭṭha <a name="M0.0188"></a> piṇḍena jīvāma, raṭṭhavāsī sukhantuti;</p>\r\n<p class="gathalast">Bhāveyyevaṃ amoghaṃva, raṭṭhapiṇḍaṃ subhuñjati.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> Āpāyikā bahū santi, mātāpitādi pubbakā;</p>\r\n<p class="gathalast">Tecaññeca sukhīnīghā, ssva byāpajjā averino.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-85-86-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',189,replace(replace('<p class="gatha1">Sattā <a name="M0.0189"></a> bhūtāca pāṇāca, puggalā attabhāvikā;</p>\r\n<p class="gathalast">Thī pū riyā nariyāca, devānarā nipātikā.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> Averā hontu byāpajjā, anīghāca sukhī ime;</p>\r\n<p class="gathalast">Attānaṃ parihārantu, catudhā iti bhāvaye.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-87-88-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',191,replace(replace('<p class="gatha1">Puratthimāya <a name="M0.0191"></a> disāya, sabbesattā averino;</p>\r\n<p class="gathalast">Abyāpajjā sukhīnīghā, hontūti tāva bhāvaye.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> Puratthimāya disāya, sabbepāṇātiādinā;</p>\r\n<p class="gathalast">Dvādasakkhattuṃ bhāveyya, sesāsupi ayaṃnayo.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-89-90-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',192,replace(replace('<p class="gatha1">Catuddisā <a name="M0.0192"></a> nudisā dho, uddhaṃ sattāca pāṇino;</p>\r\n<p class="gathalast">Bhūtāca puggalā atta, bhāvī sabbe thi pūrisā.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> Ariyā ariyā devā, narāca vinipātikā;</p>\r\n<p class="gathalast">Averā byāpajjā nīghā, sukhattāca bhavantu te.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-91-92-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',193,replace(replace('<p class="gatha1">Catuddisā <a name="M0.0193"></a> nudisā dho, uddhanti dasakedisi;</p>\r\n<p class="gathalast">Dvādase te paricchijja, bhāveyya puggale budho.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-93-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',194,replace(replace('<p class="gatha1">Mettā <a name="M0.0194"></a> vassena temetu, pajjuntoviya sabbadhi;</p>\r\n<p class="gathalast">Mākiñci parivajjehi, evaṃ mettā subhāvitā.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> Pañcā nodhi sattodhisā, siyuṃ dvādasapuggalā;</p>\r\n<p class="gathalast">Ntu catūhesu bhāvetvā, aṭṭhatālīsakā siyuṃ.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-94-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',195,replace(replace('<p class="gatha1">Dasakedisi <a name="M0.0195"></a> tāmettā, catussata asītiyo;</p>\r\n<p class="gathalast">Aṭṭhatālīsāhi pañca, satā ṭṭhavīsa sādhikā.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-96-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',196,replace(replace('<p class="gatha1">Dukkhite <a name="M0.0196"></a> karuṇaṃ brūhe, muditaṃ sukhite jane;</p>\r\n<p class="gathalast">Mettāceva upekkhāca, ubho ubhosu bhāvitā.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> Brahmavāsīti vattabbo, tesvaññatara vāsito;</p>\r\n<p class="gathalast">Gandhabhūtesu so loke, brahmāviya virocati.</p>\r\n<p class="chapter">Appamādāvaha pakiṇṇakaniddesa</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-97-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',200,replace(replace('<p class="gatha1">Saṃvijjanti <a name="M0.0200"></a> dha lokasmiṃ,</p>\r\n<p class="gatha2">Bahū jīvitakappanā;</p>\r\n<p class="gatha3">Gahetvā patta muñcho yo,</p>\r\n<p class="gathalast">Jīvikānaṃ sa lāmako.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Sukulāca tadupagā, kāmabhogā napekkhino;</p>\r\n<p class="gathalast">Na bhayaṭṭā na iṇaṭṭā, neva ājīva kāraṇā.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',201,replace(replace('<p class="gatha1">Nālaṃva <a name="M0.0201"></a> gihinā brahma, cariyāya akhaṇḍitaṃ;</p>\r\n<p class="gathalast">Gharāvāso tisambādho, pabbajjāva nirālayā.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',202,replace(replace('<p class="gatha1">Bhavapaṅkā <a name="M0.0202"></a> pamuccāma, tivittiṇṇā bhayānakā;</p>\r\n<p class="gathalast">Paṭipatti yimāyāti, katvā tadupagā ime.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Uttiṭṭhe nappamajjeyya, dhammaṃ caritaṃ sucare;</p>\r\n<p class="gathalast">Dhammacārī sukhaṃseti, asmiṃloke paramhica.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',203,replace(replace('<p class="gatha1">Svāgatā <a name="M0.0203"></a> vata tebhikkhū, pattā sambuddhaputta taṃ;</p>\r\n<p class="gathalast">Gihi bandhana pucchijja, sukhitā sāsane ratā.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Katapuñña visesāva, ete suladdha dullabhā;</p>\r\n<p class="gathalast">Chaṭṭetvāpi mahārajjaṃ, nedisaṃ laddha maññadā.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',204,replace(replace('<p class="gatha1">Svāgatā <a name="M0.0204"></a> sugatī hontu, māduggatī pamādino;</p>\r\n<p class="gathalast">Dussīlā ce gamissanti, apāyaṃ tibhayānakaṃ.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',205,replace(replace('<p class="gatha1">Gihibhogā <a name="M0.0205"></a> parīhinno, sāmaññattañca dūbhato;</p>\r\n<p class="gathalast">Paridhaṃsamāno pakireti, chavālātaṃva nassati.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> Kuso yathā duggahito, hatthamevā nukantati;</p>\r\n<p class="gathalast">Sāmaññaṃ dupparāmaṭṭhaṃ, nirayā yupa kaḍḍhati.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',206,replace(replace('<p class="gatha1">Yaṃkiñci <a name="M0.0206"></a> sithilaṃ kammaṃ, saṃkiliṭṭhañca yaṃkataṃ;</p>\r\n<p class="gathalast">Saṅkassaraṃ brahmacariyaṃ, nataṃhoti mahapphalaṃ.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> Kariyāce kariyā venaṃ, daḷhamenaṃ parakkame;</p>\r\n<p class="gathalast">Sithilohi paribbajo, bhiyyo ākirate rajaṃ.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',207,replace(replace('<p class="gatha1">Iti <a name="M0.0207"></a> vuttaṃ munindena, nussaraṃ ani vattito;</p>\r\n<p class="gathalast">Sadā alina cittena, careyya buddhasāvako.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> Rāgaṃ asubhacintāya, dosaṃ mettāya vāraye;</p>\r\n<p class="gathalast">Maraṇena dhajaṃmānaṃ, sambuddhe tikkha saddhiko.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Asubhā kāmavitakkaṃ, mettā byāpāda takkitaṃ;</p>\r\n<p class="gathalast">Vihiṃsaṃ karuṇāyeva, nivāreyya sadāsato.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-15-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',208,replace(replace('<p class="gatha1">Buddhāṇatti <a name="M0.0208"></a> sadātīto, micchāvitakka pīḷito;</p>\r\n<p class="gathalast">Pāpadhammehi saṃkiṇṇo, soniccāpāya gāmiko.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> Dhovitvā pattimalāni, punātikkama saṃvuto;</p>\r\n<p class="gathalast">Micchāvitakka sañchedī, dūro apāya gāmito.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-17-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',209,replace(replace('<p class="gatha1">Khīṇāsavatta <a name="M0.0209"></a> buddhattaṃ, niyyānika ntarā yikaṃ;</p>\r\n<p class="gathalast">Sīhanādaṃ catuṭṭhāne, vesārajjo jino nadi.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',210,replace(replace('<p class="gatha1">Sīlaṃ <a name="M0.0210"></a> niyyānikaṃ nāma, āpatti antarāyikaṃ;</p>\r\n<p class="gathalast">Antarāya manāpajja, niyyāneva patiṭṭhatu.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',211,replace(replace('<p class="gatha1">Niyyānikāca <a name="M0.0211"></a> asubhā, subhasaññā ntarāyikā;</p>\r\n<p class="gathalast">Antarāya manāpajja, niyyāneva patiṭṭhatu.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',212,replace(replace('<p class="gatha1">Nānāpatti <a name="M0.0212"></a> pakiṇṇopi, pārājikā vasesako;</p>\r\n<p class="gathalast">So mityatta paṇīdhīhi, lajjīyeva visodhako.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> Alajjīkamma kiṇṇopi,</p>\r\n<p class="gatha2">Saṃvejetvā sumittiko;</p>\r\n<p class="gatha3">Lajjīyeva visodhento,</p>\r\n<p class="gathalast">Matakova asodhako.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',213,replace(replace('<p class="gatha1">Yo <a name="M0.0213"></a> pubbeva pamajjitvā, pacchāso nappamajjati;</p>\r\n<p class="gathalast">Somaṃ lokaṃ pabhāseti, abbhāmuttovacandimā.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',214,replace(replace('<p class="gatha1">Dhuraṃkatvā <a name="M0.0214"></a> dhipatayo, yo puññesu parakkame;</p>\r\n<p class="gathalast">Tassa niyyānikaṃ kammaṃ, kiṃnāmakaṃ nasijjhate.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',215,replace(replace('<p class="gatha1">Paccate <a name="M0.0215"></a> munino bhattaṃ, thokaṃthokaṃ ghareghare;</p>\r\n<p class="gathalast">Piṇḍikāyeva jīvantu, māpajjantu anesanaṃ.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Dhoveyyā pattimagāni, vuṭṭhāna desanambuhi;</p>\r\n<p class="gathalast">Saṃvarissanti cittena, sīlaṃ dhotassa nimmalaṃ.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-26-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',216,replace(replace('<p class="gatha1">Buddhāṇātikkamāpatti<a name="M0.0216"></a>, niggahe rāgadosake;</p>\r\n<p class="gathalast">Natthi sañcicca āpatti, lajīva so pavuccati.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> Vāṇijja kasikādīhi, nāhāreṭṭhi dhasāsane;</p>\r\n<p class="gathalast">Dhura dvayaṃva kiccaṃ taṃ, nāññakiccehi hāpaye.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-28-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',217,replace(replace('<p class="gatha1">Niggaṇheyya <a name="M0.0217"></a> sakaṃcittaṃ, kiṭṭhādiṃ viya duppasuṃ;</p>\r\n<p class="gathalast">Satimā sampajānoca, care sabbiriyāpathe.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',218,replace(replace('<p class="gatha1">Yathā <a name="M0.0218"></a> thambhe nibandheyya, vacchaṃ damaṃ naro idha;</p>\r\n<p class="gathalast">Bandheyyevaṃ sakaṃcittaṃ, satiyā rammaṇe daḷhaṃ.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> Adhisīlādhicittānaṃ, adhipaññāya sikkhanaṃ;</p>\r\n<p class="gathalast">Bhikkhu kiccattayaṃ etaṃ, karontova subhikkhuko.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-31-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',219,replace(replace('<p class="gatha1">Pañcāṭṭha <a name="M0.0219"></a> dasa sīlāni, nādhisīlaṃ taduttari;</p>\r\n<p class="gathalast">Pātimokkhaṃ adhisīlaṃ, pabbatā dhika meruva.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',220,replace(replace('<p class="gatha1">Pātimokkhaṃ <a name="M0.0220"></a> visodhento, appeva jīvitaṃ jahe;</p>\r\n<p class="gathalast">Paññattaṃ lokanāthena, nabhinde sīlasaṃvaraṃ.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> Sīlenā tikkamaṃ thullaṃ, pariyuṭṭhaṃ samādhinā;</p>\r\n<p class="gathalast">Paññāyā nusayaṃ saṇhaṃ, kilesaṃ bhikkhu bhindati.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-34-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',221,replace(replace('<p class="gatha1">Sāsanassādi <a name="M0.0221"></a> sīlaṃva, majjhe tassa samādhiva;</p>\r\n<p class="gathalast">Paññāva pariyosānaṃ, kalyāṇāva imetayo.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-36-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',222,replace(replace('<p class="gatha1">Mahāpuññe <a name="M0.0222"></a> ṭhitaṃ sīlaṃ, samādhi appanā gataṃ;</p>\r\n<p class="gathalast">Catumagga yutā paññā, etaṃ sikkhattayaṃ mataṃ.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> Sīlanalakkhaṇaṃ sīlaṃ, dussīlya dhaṃsanaṃ rasaṃ;</p>\r\n<p class="gathalast">Hirottappa padaṭṭhānaṃ, suci paccupaṭṭhānakaṃ.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-37-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',223,replace(replace('<p class="gatha1">Sasīlagutti <a name="M0.0223"></a> nāthoca, dunnigaho visārado;</p>\r\n<p class="gathalast">Dhammaṭṭhītīti pañcete, guṇā venayike matā.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',224,replace(replace('<p class="gatha1">Ādi <a name="M0.0224"></a> kalrāṇa saṃvedī, sīlamattaṭṭha bhikkhavo;</p>\r\n<p class="gathalast">Uddhaṃ kalyāṇa lābhāya, alino anivattiko.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> Dhovitvā pattimalāni, vuṭṭhāna desana mbuhi;</p>\r\n<p class="gathalast">Suddhasīle ṭhitoyeva, evaṃ cinteyya paññavā.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-40-41-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',225,replace(replace('<p class="gatha1">Sampuddhorasa <a name="M0.0225"></a> puttāva, buddhurojā nussāvanā;</p>\r\n<p class="gathalast">Sambhūtā pitu dāyādā, puttānāma sabhāvato.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> Khīraṃ pitvāva jīvanti, jātāpi idha puttakā;</p>\r\n<p class="gathalast">Pariyatti jinakkhīraṃ, pitvāva jinaputtakā.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-42-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',226,replace(replace('<p class="gatha1">Dāyoca <a name="M0.0226"></a> nāma buddhassa, dhammāmisa vasādvidhā;</p>\r\n<p class="gathalast">Maggañāṇā dayo dhammo, cattāro paccayāmisā.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> Cira māmisa dāyādā, rājapūjādi gāhino;</p>\r\n<p class="gathalast">Dāyā missaggahaṃ nicchi, saddhamma garuko jino.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-44-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',227,replace(replace('<p class="gatha1">Lakkha <a name="M0.0227"></a> kappa catussaṅkhya, kālaṃ vicita niccitaṃ;</p>\r\n<p class="gathalast">Dhammadāyaṃ navindamhā, buddhaputtāpi yemayaṃ.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> Buddhavārita dāyādā, saddhammadāya bāhirā;</p>\r\n<p class="gathalast">Puttāpi satthudāsābhā, bhuttamattā hi dāsakā.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-46-47-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',228,replace(replace('<p class="gatha1">Bhaddanta <a name="M0.0228"></a> rāhulasseva, dāyaṃ nopi adā jino;</p>\r\n<p class="gathalast">Nādiyimhā pamādāya, taṃ dāyaṃ kusalantakaṃ.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> Dhammadāyādā mebhikkhave tumhebhavatha,</p>\r\n<p class="gatha2">Māāmisa dāyādā;</p>\r\n<p class="gatha3">Iti vuttaṃ munindena,</p>\r\n<p class="gathalast">Sāvakesu dayāvatā.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-48-49-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',229,replace(replace('<p class="gatha1">Imāya <a name="M0.0229"></a> buddhavācāya, buddhasantaka taṃ sare;</p>\r\n<p class="gathalast">Dvinnaṃ āmisaa dāyāda, bhāvassaca nivāraṇaṃ.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> Rajje caṇḍālaputtāva, saddhammacakka vattino;</p>\r\n<p class="gathalast">Puttā hontāpi dāyesmiṃ, nirāsā tiva ninditā.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-50-51-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',230,replace(replace('<p class="gatha1">Micchājīva <a name="M0.0230"></a> samāpannā, accāsā paccayāmise;</p>\r\n<p class="gathalast">Mahājānīya sampattā, moghakatvā tidullabhaṃ.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',231,replace(replace('<p class="gatha1">Gihikāme <a name="M0.0231"></a> pahāyāgo, paravantosu laggito;</p>\r\n<p class="gathalast">Gaṅgātiṇṇo taḷākamhi, nimuggovā tinindito.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-53-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',232,replace(replace('<p class="gatha1">Cīvare <a name="M0.0232"></a> piṇḍapāteca, paccaye sayanāsane;</p>\r\n<p class="gathalast">Etesu taṇha mākāsi, mālokaṃ punarāgami.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> Itivuttā nusārena, paccavekkhaṇa suddhiyā;</p>\r\n<p class="gathalast">Āmisesu hane āsaṃ, puttamaṃsu pamaṃ saraṃ.</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-54-55-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',233,replace(replace('<p class="gatha1">Seyyo <a name="M0.0233"></a> ayoguḷo bhutto,</p>\r\n<p class="gatha2">Tatto aggisikhūpamo;</p>\r\n<p class="gatha3">Yañce bhuñjeyya dussīlo,</p>\r\n<p class="gathalast">Raṭṭhapiṇḍaṃ asaññato.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-56-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',234,replace(replace('<p class="gatha1">Itivuttaṃ <a name="M0.0234"></a> nucintento, vajje dussīla bhāvato;</p>\r\n<p class="gathalast">Sīle ṭhitova bhuñjeyya, māditta guḷakaṃ gili.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> Annāna matho pānānaṃ,</p>\r\n<p class="gatha2">Khādanīyāna mathopi vatthānaṃ;</p>\r\n<p class="gatha3">Laddhāna sannidhiṃ kariyā,</p>\r\n<p class="gathalast">Naca parittase tāni alabhamāno.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-57-58-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',235,replace(replace('<p class="gatha1">Aññāhi <a name="M0.0235"></a> lābhupanisā, aññā nibbāna gāminī;</p>\r\n<p class="gathalast">Sakkāraṃ nābhinandeyya, viveka manubrūhaye.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-59-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',236,replace(replace('<p class="gatha1">Akatvā <a name="M0.0236"></a> āmise āsaṃ,</p>\r\n<p class="gatha2">Saddhammeyeva āsiko;</p>\r\n<p class="gatha3">Appamatto samāraddho,</p>\r\n<p class="gathalast">Dhammadāyaṃ labhissati.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> Pariyattiṃ vinā seyyaṃ, nalabhanti budhāapi;</p>\r\n<p class="gathalast">Seyyatthikova sikkheyya, neva pūjādi kāraṇā.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-60-61-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',237,replace(replace('<p class="gatha1">Bhavanissaraṇatthaṃva<a name="M0.0237"></a>, sikkhe nā laggadūpamo;</p>\r\n<p class="gathalast">Tathūpamāya sikkhanto, apāyesu patissati.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> Sikkhitena amānatthaṃ, nasādhu mānathaddhiko;</p>\r\n<p class="gathalast">Mudubhāvāya sikkhitvā, damento muduko bhave.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-62-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',238,replace(replace('<p class="gatha1">Rāgaṃ <a name="M0.0238"></a> dosaṃ dhajaṃmānaṃ, sikkhantopi vivajjaye;</p>\r\n<p class="gathalast">Daharāpi hi miyyanti, natthi vassaggato mataṃ.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> Saddhaṃtikkheyya buddhena, rāgaṃ asubha cintayā;</p>\r\n<p class="gathalast">Maraṇena dhajaṃmānaṃ, dosaṃ mettāya vāraye.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-64-65-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',239,replace(replace('<p class="gatha1">Etehi <a name="M0.0239"></a> caturakkhehi, ganthaṃ sikkheyya saṃ vuto;</p>\r\n<p class="gathalast">Sikkhantassehi rakkhehi, nakoci saṃkilesiko.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-66-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',240,replace(replace('<p class="gatha1">Buddhavācampi <a name="M0.0240"></a> sajjhāya, etepi manasīkara;</p>\r\n<p class="gathalast">Vutto dhammavihārīti, ediso sāsane varo.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> Garūna mupadesena,</p>\r\n<p class="gatha2">Caturakkho susīlavā;</p>\r\n<p class="gatha3">Appassutopi pāsaṃso,</p>\r\n<p class="gathalast">Bhiyyoyeva bahussuto.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-67-68-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',241,replace(replace('<p class="gatha1">Sātaṃ <a name="M0.0241"></a> sevakkhaṇevappaṃ, taṃhetvā nantādukkhanti;</p>\r\n<p class="gathalast">Dhīro āsaṃ hane kāme, khuradhāramadhūpame.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> Yodha kāme sukhaṃmaññi,</p>\r\n<p class="gatha2">Na so dukkhā vimuccati;</p>\r\n<p class="gatha3">Mātāhi byaggha manvento,</p>\r\n<p class="gathalast">Vacho mutto kathaṃbhayā.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-69-70-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',242,replace(replace('<p class="gatha1">Tiracchā <a name="M0.0242"></a> peta laddhabbe, nāsaṃ kāmasukhe kare;</p>\r\n<p class="gathalast">Bhāyitabba sukhaṃ tañhi, tasmiṃ laggā mahātapā.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> Laddhā kāmasukhaṃ bālā, pamodanti napaṇḍitā;</p>\r\n<p class="gathalast">Pasupakkhībhi laddhabbaṃ, anantadukkha kāraṇaṃ.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-71-72-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',243,replace(replace('<p class="gatha1">Laddhā <a name="M0.0243"></a> dhammaratiṃviññū, modanti na apaṇḍitā;</p>\r\n<p class="gathalast">Anoma satta paribhogaṃ, bhaganissaraṇāvahaṃ.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',244,replace(replace('<p class="gatha1">Hīnakammaṃ <a name="M0.0244"></a> paṭicchannaṃ, kāmassādaṃ napatthaye;</p>\r\n<p class="gathalast">Dhamme pītiñca pāmojjaṃ, pattheyya sādhusampato.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> Pariggaṇhanti yekāme, hiṃ santite tadatthikā;</p>\r\n<p class="gathalast">Pariccattaṃ na hiṃ santi, muttaṃ vaṇṇenti sādhavo.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-74-75-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',245,replace(replace('<p class="gatha1">Niccupakkamma <a name="M0.0245"></a> puṭṭhopi, kāyo verīvasā nugo;</p>\r\n<p class="gathalast">Aciraṃyeva bhūsāyī, yuttova ta mupekkhituṃ.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> Rakkhitopi aguttova, kāyo bhayamukhe ṭhito;</p>\r\n<p class="gathalast">Tasmā kāya mupekkhitvā, caredhamma machambhito.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-76-77-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',246,replace(replace('<p class="gatha1">Puṭṭho <a name="M0.0246"></a> puṭṭhopi yaṃkāyo, bhuvi rogāsayīsayī;</p>\r\n<p class="gathalast">Kataṃkataṃ mudhāto na, tadatthaṃ duccare care.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-78-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',247,replace(replace('<p class="gatha1">Pāpaṃ <a name="M0.0247"></a> karoti yobālo,</p>\r\n<p class="gatha2">Puṭṭhuṃ kāyaṃ tidubbharaṃ;</p>\r\n<p class="gatha3">Bhūmyaṃ kāyaṃ ṭhapetvāna,</p>\r\n<p class="gathalast">Anātho so apāyiko.</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> Verīvasā nugaṃ kāyaṃ,</p>\r\n<p class="gatha2">Bālo poseti duccaro;</p>\r\n<p class="gatha3">Posento niraye pakko,</p>\r\n<p class="gathalast">Kāyo bhūmyaṃ vikāragū.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-79-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',248,replace(replace('<p class="gatha1">Pāpaṃ <a name="M0.0248"></a> mākara kāyatthaṃ, kāyo verī vasānugo;</p>\r\n<p class="gathalast">Bhūmyaṃ sessati vekārī, pāpiko nirayaṃ gato.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> Amayhaṃ mayhasaññāya,</p>\r\n<p class="gatha2">Kāyaṃ rogavasānugaṃ;</p>\r\n<p class="gatha3">Posaṃ patto mahājāniṃ,</p>\r\n<p class="gathalast">Nokāso dhamma mikkhituṃ.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-81-82-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',249,replace(replace('<p class="gatha1">Kāyāpekkhāya <a name="M0.0249"></a> nokāso,</p>\r\n<p class="gatha2">Dhammaṃ daṭṭhuṃ rahogato;</p>\r\n<p class="gatha3">Upekkhāyeva okāso,</p>\r\n<p class="gathalast">Dukkhitā mha apekkhayā.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-83-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',250,replace(replace('<p class="gatha1">Citta <a name="M0.0250"></a> saṃsodhakā pakkā, kāyasaṃsodhakā navā;</p>\r\n<p class="gathalast">Sodhe cittaṃva pakkatthaṃ, nakāyaṃ bhavabhīruko.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-84-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',251,replace(replace('<p class="gatha1">Cittasaṅkharaṇaṃ <a name="M0.0251"></a> sādhu, taṃ saṅkhataṃ pabhassaraṃ;</p>\r\n<p class="gathalast">Nasādhu kāyasaṅkhāro, saṅkhatopyasubhova so.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> Sabhāva malinaṃ kāyaṃ, nimmalāya kathaṃ kare;</p>\r\n<p class="gathalast">Āgantumalinaṃ cittaṃ, sakkā kātuṃ sunimmalaṃ.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-85-86-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',252,replace(replace('<p class="gatha1">Ādhibyādhi <a name="M0.0252"></a> parotāya, ajjasvevā vināsinā;</p>\r\n<p class="gathalast">Kohināma sarīrāya, dhammāpetaṃ samācare.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> Sabhāvajegucchaṃ kāyaṃ, sobhetuṃnevasakkuṇe;</p>\r\n<p class="gathalast">Cittaṃ vā laṅkataṃ sobhaṃ, sīlādi gandhavāsitaṃ.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-87-88-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',253,replace(replace('<p class="gatha1">Sace <a name="M0.0253"></a> bhāyatha dukkhassa, sace vo dukkha mappiyaṃ;</p>\r\n<p class="gathalast">Mākattha pāpakaṃ kammaṃ, āvivā yadivā raho.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> Kilesā gantumalaṃcittaṃ, pabhassara sabhāvikaṃ;</p>\r\n<p class="gathalast">Tadāgantumalaṃ dhova, cittaṃ dhote pabhassaraṃ.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-89-90-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',254,replace(replace('<p class="gatha1">Kilesā <a name="M0.0254"></a> gantumalaṃcittaṃ, upakkamena sodhaye;</p>\r\n<p class="gathalast">Suvisuddha manāyeva, uttariṃsu bhavaṇṇavā.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> Kāye malamupekkhāya, citte malaṃva dhovatu;</p>\r\n<p class="gathalast">Citte hi nimmalesanto, pūtikāyopi pūjito.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-91-92-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',255,replace(replace('<p class="gatha1">Kāyarogaṃ <a name="M0.0255"></a> titikkhāya, cittarogaṃ cikicchatu;</p>\r\n<p class="gathalast">Sukhito kāyarogīpi, citte nirāmaye sati.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> Kāyaroge bahū vejjā, buddhuttiva manogade;</p>\r\n<p class="gathalast">Idhāpi kāyiko santo, anantāva manorujā.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-93-94-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',256,replace(replace('<p class="gatha1">Sīsadaḍḍha <a name="M0.0256"></a> mupekkhāya, nibbātu rāgapāvakaṃ;</p>\r\n<p class="gathalast">Khippaṃ asubha saññāya, niccadaḍḍhaṃ bhavebhave.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> Subhāya uṭṭhitaṃ rāgaṃ, asubhāya nivāraye;</p>\r\n<p class="gathalast">Sorāgo sāditaṃ jantuṃ, catvāpāyaṃ nayissati.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-95-96-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',257,replace(replace('<p class="gatha1">Paṇḍitānaṃ <a name="M0.0257"></a> malaṃ māno, sottukkaṃsena pākaṭo;</p>\r\n<p class="gathalast">Mākho attāna mukkaṃse, māvibhāve sakaṃmalaṃ.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> Guṇaṃ paṭicca guṇīnaṃ, ahaṃmāno samuṭṭhahe;</p>\r\n<p class="gathalast">Maraṇaṃ anucintāya, dhajaṃmānaṃ nipātaya.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-97-98-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',258,replace(replace('<p class="gatha1">Eko <a name="M0.0258"></a> kāyavivekesī, katvā kilesaniggahaṃ;</p>\r\n<p class="gathalast">Vase cittavivekesī, ubho padhi vivekādā.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> Adiṭṭhe asute ṭhāne, vaseyya mocanatthiko;</p>\r\n<p class="gathalast">Assādaṃhi nivāretuṃ, diṭṭhe sute tidukkaraṃ.</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-99-100-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',259,replace(replace('<p class="gatha1">Adiṭṭhe <a name="M0.0259"></a> asute raññe, vaseyyi ndriyagopako;</p>\r\n<p class="gathalast">Vāretuṃ visayākiṇṇe, cakkhusotaṃ tidukkaraṃ.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> Rāgaṃ asati uppannaṃ, santābhujena vāraye;</p>\r\n<p class="gathalast">Bāhire rāga muppannaṃ, anto asubhacintayā.</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-101-102-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',260,replace(replace('<p class="gatha1">Rāgaṃ <a name="M0.0260"></a> chindāti buddhāṇaṃ, saraṃ bhikkhu rahogato;</p>\r\n<p class="gathalast">Passaṃ kāyedha jegucchaṃ, labheyyā siṭṭhamocanaṃ.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> Kāyaṃ asubhatopassa, kallakāleva dassanaṃ;</p>\r\n<p class="gathalast">Moghaṃ kālaṃ nakhīyeyya, bhaveyyuṃsvepiāturā.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-103-104-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',261,replace(replace('<p class="gatha1">Kāyaṃ <a name="M0.0261"></a> jegucchatopassa, bālyanto paccavekkhiya;</p>\r\n<p class="gathalast">Ādo kiñci jigucchāya, jiguccheyyāyatiṃ bhusaṃ.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> Kāyādinava mikkheyya, dāni kiñcipi dassanaṃ;</p>\r\n<p class="gathalast">Āyatiṃ maggalābhāya, bhaveyya upanissa yo.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-105-106-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',262,replace(replace('<p class="gatha1">Itthīna <a name="M0.0262"></a> maṅgamaṅgāni, napasseyya nacintaye;</p>\r\n<p class="gathalast">Tadāsā ubhato bhaṭṭhā, sugatyā sāsanāpica.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> Itthirūpa sarākaḍḍhā, bhaṭṭhā bahūva sāsanā;</p>\r\n<p class="gathalast">Ihāpi dukkhitā hutvā, te pecca atidukkhino.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-107-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',263,replace(replace('<p class="gatha1">Puṃmano <a name="M0.0263"></a> pariyādāya, itthirūpasarā ṭhitā;</p>\r\n<p class="gathalast">Tassama mañña mekampi, navijjateva sabbadhi.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-109-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',264,replace(replace('<p class="gatha1">Sallape <a name="M0.0264"></a> asihatthena, pisācenāpi sallape;</p>\r\n<p class="gatha2">Āsade āsivisepi, aggikkhandhepi āsade;</p>\r\n<p class="gathalast">Natveva mātugāmena, ekekāya supesalo.</p>\r\n<p class="hangnum"><a name="para110"></a><span class="paranum">110</span>.</p>\r\n<p class="gatha1"> Kāmaṃ asubhacintāya, byāpādaṃ snehacetasā;</p>\r\n<p class="gathalast">Vihiṃsaṃ karuṇāyehi, vitakkaggī tayosame.</p>\r\n<p class="hangnum"><a name="para111"></a><span class="paranum">111</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-110-111-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',265,replace(replace('<p class="gatha1">Asameta <a name="M0.0265"></a> vitakkaggī, thusarāsimhi khāṇuva;</p>\r\n<p class="gathalast">Athirā sāsane tāpī, tepacchāatitāpino.</p>\r\n<p class="hangnum"><a name="para112"></a><span class="paranum">112</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-112-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',266,replace(replace('<p class="gatha1">Asubhā <a name="M0.0266"></a> pagame lokā, taṃ mettāyupasaṅkame;</p>\r\n<p class="gathalast">Subhāvitāhi etāhi, jaheloke piyāpiyaṃ.</p>\r\n<p class="hangnum"><a name="para113"></a><span class="paranum">113</span>.</p>\r\n<p class="gatha1"> Gataṭṭhitādo uppanne, vitakkaggī tayo same;</p>\r\n<p class="gathalast">Ātāpī pahitattoti, evaṃbhūto pavuccati.</p>\r\n<p class="hangnum"><a name="para114"></a><span class="paranum">114</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-113-114-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',267,replace(replace('<p class="gatha1">Vivādappatto <a name="M0.0267"></a> dutīyo, keneko vivadissati;</p>\r\n<p class="gathalast">Tassate saggakāmassa, ekatta muparocitaṃ.</p>\r\n<p class="hangnum"><a name="para115"></a><span class="paranum">115</span>.</p>\r\n<p class="gatha1"> Sinihappatto dutīyo, kamekā sinihissati;</p>\r\n<p class="gathalast">Tassate mokkhakāmassa, ekatta muparocitaṃ.</p>\r\n<p class="hangnum"><a name="para116"></a><span class="paranum">116</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-115-116-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',268,replace(replace('<p class="gatha1">Purato <a name="M0.0268"></a> pacchatovāpi, aparo ce navijjati;</p>\r\n<p class="gathalast">Tasseva phāsu bhavati, ekassa vasato vane.</p>\r\n<p class="hangnum"><a name="para117"></a><span class="paranum">117</span>.</p>\r\n<p class="gatha1"> Sukhañca kāma mayikaṃ, dukkhañca pavivekikaṃ;</p>\r\n<p class="gathalast">Pavivekaṃ dukkhaṃ seyyo, yañce kāmamayaṃ sukhaṃ.</p>\r\n<p class="hangnum"><a name="para118"></a><span class="paranum">118</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-117-118-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',269,replace(replace('<p class="gatha1">Yoca <a name="M0.0269"></a> vassasataṃ jīve, apassaṃ udayabbayaṃ;</p>\r\n<p class="gathalast">Ekāhaṃ jīvitaṃ seyyo, passato udayabbayaṃ.</p>\r\n<p class="hangnum"><a name="para119"></a><span class="paranum">119</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-119-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',270,replace(replace('<p class="gatha1">Suññāgāraṃ <a name="M0.0270"></a> paviṭṭhassa, santacittassa bhikkhuno;</p>\r\n<p class="gathalast">Amānussī rati hoti, sammādhammaṃ vipassato.</p>\r\n<p class="hangnum"><a name="para120"></a><span class="paranum">120</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-120-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',271,replace(replace('<p class="gatha1">Yatoyato <a name="M0.0271"></a> sammasati, khandhānaṃ udayabbayaṃ;</p>\r\n<p class="gathalast">Labhati pītipāmojjaṃ, amataṃ taṃ vijānataṃ.</p>\r\n<p class="hangnum"><a name="para121"></a><span class="paranum">121</span>.</p>\r\n<p class="gatha1"> Iccuttaṃ dhammapāmojjaṃ, vivekajaṃ rasādhikaṃ;</p>\r\n<p class="gathalast">Icchanto sīlavā bhikkhu, anivattita vīriyo.</p>\r\n<p class="hangnum"><a name="para122"></a><span class="paranum">122</span>.</p>\r\n<p class="gatha1"> Vanā vāse vasitvāna, appicchādiguṇāvaho;</p>\r\n<p class="gathalast">Pali bodhe samucchijja, bhāveyyevaṃrahogato.</p>\r\n<p class="hangnum"><a name="para123"></a><span class="paranum">123</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-121-122-123-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',272,replace(replace('<p class="gatha1">Kāye <a name="M0.0272"></a> jegucchapuñjāni, rūpaṃ ruppanabhāvato;</p>\r\n<p class="gathalast">Tassitā vedanā saññā, saṅkhārāca tatopare.</p>\r\n<p class="hangnum"><a name="para124"></a><span class="paranum">124</span>.</p>\r\n<p class="gatha1"> Viññāṇañca imepañca, khandhā rāsatthato matā;</p>\r\n<p class="gathalast">Tecānicca dukkhā nattā, upādā vayadhammino.</p>\r\n<p class="hangnum"><a name="para125"></a><span class="paranum">125</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-124-125-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',274,replace(replace('<p class="gatha1">Pheṇapiṇḍū <a name="M0.0274"></a> pamaṃ rūpaṃ, vedanā pupphuḷūpamā;</p>\r\n<p class="gathalast">Marīcikūpamā saññā, saṅkhārā kadalūpamā.</p>\r\n<p class="hangnum"><a name="para126"></a><span class="paranum">126</span>.</p>\r\n<p class="gatha1"> Māyūpamanti viññāṇaṃ, dassite sabba dassinā;</p>\r\n<p class="gathalast">Upamāhi samasse yya, pañcakkhandhe asārake.</p>\r\n<p class="hangnum"><a name="para127"></a><span class="paranum">127</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-126-127-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',275,replace(replace('<p class="gatha1">Yāva <a name="M0.0275"></a> byāti nimmissati,</p>\r\n<p class="gatha2">Koṭilakkhātahiṃkhaṇe;</p>\r\n<p class="gatha3">Khandhā bhijjanti hutvāna,</p>\r\n<p class="gathalast">Aniccānāma te tato.</p>\r\n<p class="hangnum"><a name="para128"></a><span class="paranum">128</span>.</p>\r\n<p class="gatha1"> Bhaya pīḷitato dukkhā, anattā avidheyyato;</p>\r\n<p class="gathalast">Khandhāva honti bhijjanti, añño koci nalabbhati.</p>\r\n<p class="hangnum"><a name="para129"></a><span class="paranum">129</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-128-129-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',276,replace(replace('<p class="gatha1">Khandhā <a name="M0.0276"></a> niccā khayaṭṭhena, bhayaṭṭhena dukhācate;</p>\r\n<p class="gathalast">Anattā sārakaṭṭhena, iti passe punappunaṃ.</p>\r\n<p class="hangnum"><a name="para130"></a><span class="paranum">130</span>.</p>\r\n<p class="gatha1"> Bhāṇūdaye kayaṃ enti, hemante patitussavā;</p>\r\n<p class="gathalast">Rāgā mānāca sabbevaṃ, satyā niccānupassane.</p>\r\n<p class="hangnum"><a name="para131"></a><span class="paranum">131</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-130-131-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',277,replace(replace('<p class="gatha1">Sīhanādaṃ <a name="M0.0277"></a> vanesutvā, saṃvejenti sasotakā;</p>\r\n<p class="gathalast">Vehapphalāpi lokevaṃ, jinerita tilakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para132"></a><span class="paranum">132</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-132-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',278,replace(replace('<p class="gatha1">Vedanādīni <a name="M0.0278"></a> nāmāni, nāmarūpadvayaṃva te;</p>\r\n<p class="gathalast">Taṇhāvijjāca kammādi, nāmarūpassa paccayā.</p>\r\n<p class="hangnum"><a name="para133"></a><span class="paranum">133</span>.</p>\r\n<p class="gatha1"> Nāmarūpaṃ pariggayha, tato tassaca paccayaṃ;</p>\r\n<p class="gathalast">Hutvā abhāvato niccā, udayabbaya pīḷanā.</p>\r\n<p class="hangnum"><a name="para134"></a><span class="paranum">134</span>.</p>\r\n<p class="gatha1"> Dukkhā avasavattittā, anattātitilakkhaṇaṃ;</p>\r\n<p class="gathalast">Āropetvāva saṅkhāre, sammasanto punappunaṃ.</p>\r\n<p class="hangnum"><a name="para135"></a><span class="paranum">135</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-133-134-135-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',279,replace(replace('<p class="gatha1">Pāpuṇeyyā <a name="M0.0279"></a> nupubbena,</p>\r\n<p class="gatha2">Sabbasaṃyojana kkhayaṃ;</p>\r\n<p class="gatha3">Tampatto arahā bhikkhu,</p>\r\n<p class="gathalast">Bhavatiṇṇo sunibbuto.</p>\r\n<p class="hangnum"><a name="para136"></a><span class="paranum">136</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-136-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',280,replace(replace('<p class="gatha1">Natumhaṃ <a name="M0.0280"></a> bhikkhave rūpaṃ, taṃ jahethāti vuttato;</p>\r\n<p class="gathalast">Memetanti upādānaṃ, pañcakkhandhe vināsaye.</p>\r\n<p class="hangnum"><a name="para137"></a><span class="paranum">137</span>.</p>\r\n<p class="gatha1"> Puttā matthi dhanā matthi, iti bālo vihaññati;</p>\r\n<p class="gathalast">Attāpi attano natthi, kutoputtokutodhanaṃ.</p>\r\n<p class="hangnum"><a name="para138"></a><span class="paranum">138</span>.</p>\r\n<p class="gatha1"> Iccutta manucintāya, attāti atthimetivā;</p>\r\n<p class="gathalast">Saññaṃ nāseyya khandhāva, atthīti ābhuje budho.</p>\r\n<p class="hangnum"><a name="para139"></a><span class="paranum">139</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-137-138-139-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',281,replace(replace('<p class="gatha1">Khandhanāsa <a name="M0.0281"></a> manābhujja, mato me puttako iti;</p>\r\n<p class="gathalast">Socanti paridevanti puttonatthi nasomato.</p>\r\n<p class="hangnum"><a name="para140"></a><span class="paranum">140</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-140-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',282,replace(replace('<p class="gatha1">Bhijjamānesu <a name="M0.0282"></a> khandhesu, attasaññī anattesu;</p>\r\n<p class="gathalast">Nādikāla viparitā, mahājānīyataṃ gatā.</p>\r\n<p class="hangnum"><a name="para141"></a><span class="paranum">141</span>.</p>\r\n<p class="gatha1"> Bhijjamānesu khandhesu, laggā rattā mamāyitā;</p>\r\n<p class="gathalast">Nārīpumādi saññāya, viparetā anādike.</p>\r\n<p class="hangnum"><a name="para142"></a><span class="paranum">142</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-141-142-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',283,replace(replace('<p class="gatha1">Nādikāla <a name="M0.0283"></a> viparito, attasaññī anattani;</p>\r\n<p class="gathalast">Bhijjamānesu khandhesu, jaha ttāti mamāyanaṃ.</p>\r\n<p class="hangnum"><a name="para143"></a><span class="paranum">143</span>.</p>\r\n<p class="gatha1"> Abhiṇhuppattiyāyeva, bhijjamāno naññayati;</p>\r\n<p class="gathalast">Aniccalakkhaṇaṃ channaṃ, taṃ cinteyya supaññavā.</p>\r\n<p class="hangnum"><a name="para144"></a><span class="paranum">144</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-143-144-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',284,replace(replace('<p class="gatha1">Asanteyeva <a name="M0.0284"></a> laggantā, namuccanti bhavattayā;</p>\r\n<p class="gathalast">Natthi santesu laggantā, rūpakkhandhā dike svidha.</p>\r\n<p class="hangnum"><a name="para145"></a><span class="paranum">145</span>.</p>\r\n<p class="gatha1"> Taṇhā gijjhati metanti, māno ahanti maññati;</p>\r\n<p class="gathalast">Diṭṭhi gaṇhāti attāti, ete papañcakā tayo.</p>\r\n<p class="hangnum"><a name="para146"></a><span class="paranum">146</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-145-146-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',285,replace(replace('<p class="gatha1">Mame <a name="M0.0285"></a> ta maha mattāti, papañcānaṃ vasānugo;</p>\r\n<p class="gathalast">Gaṇhanto bhava paṅkamhi, nimmuggova bhayānake.</p>\r\n<p class="hangnum"><a name="para147"></a><span class="paranum">147</span>.</p>\r\n<p class="gatha1"> Name nāhaṃ naattāti, etehi vivadaṃ kare;</p>\r\n<p class="gathalast">Vivadantāva muccanti, bhavapaṅkā bhayānakā.</p>\r\n<p class="hangnum"><a name="para148"></a><span class="paranum">148</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-147-148-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',286,replace(replace('<p class="gatha1">Name <a name="M0.0286"></a> nāhaṃ naattāti, daṭṭhabbanti jineritaṃ;</p>\r\n<p class="gathalast">Tatheva sabbadā maññe, mā papañca vasānugo.</p>\r\n<p class="hangnum"><a name="para149"></a><span class="paranum">149</span>.</p>\r\n<p class="gatha1"> Loko vivadi buddhena, nalokena kadāciso;</p>\r\n<p class="gathalast">Anattāti jinuddiṭṭhaṃ, loko attāti maññati.</p>\r\n<p class="hangnum"><a name="para150"></a><span class="paranum">150</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-149-150-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',287,replace(replace('<p class="gatha1">Mo <a name="M0.0287"></a> lokena samo hotu,</p>\r\n<p class="gatha2">Tassamo kiṃtaduttare;</p>\r\n<p class="gatha3">Andhibhūto ayaṃloko,</p>\r\n<p class="gathalast">Sambuddhassa virodhiko.</p>\r\n<p class="hangnum"><a name="para151"></a><span class="paranum">151</span>.</p>\r\n<p class="gatha1"> Sambuddhassa vasaṃ nvetu, sanjhādi bhayatajjito;</p>\r\n<p class="gathalast">Tabbasaṃyeva anvento, bhavatiṇṇo bhavissati.</p>\r\n<p class="hangnum"><a name="para152"></a><span class="paranum">152</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-151-152-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',288,replace(replace('<p class="gatha1">Anattāti <a name="M0.0288"></a> girā saccā, attāti vacanaṃ musā;</p>\r\n<p class="gathalast">Musāya vivade loko, buddhena sacca vādinā.</p>\r\n<p class="hangnum"><a name="para153"></a><span class="paranum">153</span>.</p>\r\n<p class="gatha1"> Turaṅgavajagāmamhā, purime camakya kānane;</p>\r\n<p class="gathalast">Vasatā aggadhammena, therena racito ayaṃ.</p>\r\n<p class="hangnum"><a name="para154"></a><span class="paranum">154</span>.</p>\r\n<p class="gatha1"> Arimatteyya buddhassa,</p>\r\n<p class="gatha2">Dhamma ssutakkhaṇe bhave;</p>\r\n<p class="gatha3">Khīṇāsavo mahāpañño,</p>\r\n<p class="gathalast">Puññena tena sāvakoti.</p>\r\n<p class="chapter">Kāyapaccavekkhaṇā</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-153-154-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',291,replace(replace('<p class="gatha1">Nādikāla <a name="M0.0291"></a> viparita, jana bhūtattha dassino;</p>\r\n<p class="gathalast">Dātume kantikassādaṃ, satthu pādo timānito.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',292,replace(replace('<p class="gatha1">Sīse <a name="M0.0292"></a> pilandiyā modī, sambuddha caraṇa mbujaṃ;</p>\r\n<p class="gathalast">Sādhu tuṭṭhikaraṃ brūmi, sukāya paccavekkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Yoniso manasīkatvā, nādā viruddha maññitaṃ;</p>\r\n<p class="gathalast">Sādhavo ta mudikkhantu, mayāpi mandabuddhino.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',293,replace(replace('<p class="gatha1">Icchitabbāna <a name="M0.0293"></a> māyattā, tesañca cayabhāvato;</p>\r\n<p class="gathalast">Tadākārena vattitā, kāyo jeguccha puñjako.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Punappunaṃva okkama, ñāṇa manto pavesiya;</p>\r\n<p class="gathalast">Bāhiraṃva anālamba, ikkhaṇā paccavekkhaṇā.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',294,replace(replace('<p class="gatha1">Maṃsacchanna <a name="M0.0294"></a> ṭṭhirūpeva, manoja vāyu cālite;</p>\r\n<p class="gathalast">Nārī gatāti yācintā, neva sāpaccavekkhaṇā.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Saññādiṭṭhica cittañca, vipallāsā imetayo;</p>\r\n<p class="gathalast">Ta dākārena vattanti, avijjo ttharitā bhusaṃ.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',295,replace(replace('<p class="gatha1">Āsā <a name="M0.0295"></a> viparite yesaṃ,</p>\r\n<p class="gatha2">Vipallāsāti tematā;</p>\r\n<p class="gatha3">Āsā āsisanā vuttā,</p>\r\n<p class="gathalast">Taṇhāyeva sabhāvato.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Asubheva subhamiti, anicceeva niccato;</p>\r\n<p class="gathalast">Dukkheyeva sukhaṃvāti, anattaniva attato.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> Saññāṇaṃ dassanaṃ cintā,</p>\r\n<p class="gatha2">Dvādasā kārato tayo;</p>\r\n<p class="gatha3">Diṭṭhisā cādi maggena,</p>\r\n<p class="gathalast">Sesā sesehi vajjhitā.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-10-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',297,replace(replace('<p class="gatha1">Taṇhā <a name="M0.0297"></a> tassi mametanti, māno maññi ahantica;</p>\r\n<p class="gathalast">Yassi diṭṭhica attāti, papañcā nāmime tayo.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> Papañcanti saṃsāraṃ, tasmā papañcanāmakā;</p>\r\n<p class="gathalast">Bhavayante payojentā, mokkhaṃ nādaṃsu te ciraṃ.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> Bhavapaṅke ni mujjantā, papañcānaṃ vasānugā;</p>\r\n<p class="gathalast">Cirassaṃ dukkhitā honti, ārā nibbānato tiva.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-13-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',298,replace(replace('<p class="gatha1">Name <a name="M0.0298"></a> nāhaṃ naattāti, etehi vivadaṃ kare;</p>\r\n<p class="gathalast">Bhaṇḍantā vivadantā te, nibbānato adūrino.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Vipallāse papañceca, dvepiete pahātave;</p>\r\n<p class="gathalast">Sopaccavekkhitabbe vaṃ, kāyo jegucchapuñjako.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',299,replace(replace('<p class="gatha1">Kesā <a name="M0.0299"></a> lomā nakhādantā,</p>\r\n<p class="gatha2">Taṇhāyāpica gocarā;</p>\r\n<p class="gatha3">Tasmā te daṭṭhukāmena,</p>\r\n<p class="gathalast">Taṇhā nivāritā sadā.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> Laggikā chaviyaṃyeva, taṇhā bāhiragocarā;</p>\r\n<p class="gathalast">Tasmā ettha tacovāha, sambuddho na bahicchaviṃ.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-17-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',300,replace(replace('<p class="gatha1">Esā <a name="M0.0300"></a> tacapariyanta, padenāpi nivāritā;</p>\r\n<p class="gathalast">Ato chavi manālamba, tacasīva manekare.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> Jigucchitāni chādeti, aṭṭhi maṃsa tacādini;</p>\r\n<p class="gathalast">Ñāṇena chindi tabbāca, tasmā chavīti vuccati.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',301,replace(replace('<p class="gatha1">Chaviṃ <a name="M0.0301"></a> chetvā tacaṃ passe,</p>\r\n<p class="gatha2">Taṃ chetvā maṃsakādayo;</p>\r\n<p class="gatha3">Gabbhevatthūni dīpena,</p>\r\n<p class="gathalast">Yathā paññāpadīpiko.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> Jeguccho chaviyā kāyo,</p>\r\n<p class="gatha2">Asubhova subhāyate;</p>\r\n<p class="gatha3">Nicchavā tacamattena,</p>\r\n<p class="gathalast">Kathaṃ subhāyate ayaṃ.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-21-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',302,replace(replace('<p class="gatha1">Nhārubandho <a name="M0.0302"></a> ṭṭhisaṅghāto, maṃsalohi ta limpito;</p>\r\n<p class="gathalast">Chaviyāva vimoheti, tacacchanno imaṃ pajaṃ.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> Vaṇṇa saṇṭhāna toceva,</p>\r\n<p class="gatha2">Gandho kāsā sayehica;</p>\r\n<p class="gatha3">Jegucchā paṭikulyāca,</p>\r\n<p class="gathalast">Kesānāma na mepiyā.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-23-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',303,replace(replace('<p class="gatha1">Ekekaṃ <a name="M0.0303"></a> manasīkatvā, naye nicceva mādinā;</p>\r\n<p class="gathalast">Bhāvetabbā samārambha, yathāpaññāyate tathā.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> Pūritaṃ matthaluṅgassa, sīsaṭṭhipi jigucchitaṃ;</p>\r\n<p class="gathalast">Mukha nāsakkhi kaṇṇādi, chiddā vachidda duddasaṃ.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-26-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',304,replace(replace('<p class="gatha1">Pūti <a name="M0.0304"></a> vāyu vicarita, kucchiṭṭhantāni lohitaṃ;</p>\r\n<p class="gathalast">Pittaṃ semhañca papphāsaṃ, hadayaṃ yakanampi dhī.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> Anna pānaṃ manuññampi, kheḷa tinta madhopari;</p>\r\n<p class="gathalast">Dantehi pisitaṃ svāna, vamathūva jigucchitaṃ.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-27-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',305,replace(replace('<p class="gatha1">Yāvatāyu <a name="M0.0305"></a> adhotevā, māsaye gilitaṃ ṭhitaṃ;</p>\r\n<p class="gathalast">Kimikūla samākiṇṇe, tahimevā sitāsitaṃ.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> Etaṃ udariyaṃ nāma, tamhā pakkāsayaṃ gataṃ;</p>\r\n<p class="gathalast">Dinaccaye karīsantaṃ, sā sayaṃ taṃdvayampi dhī.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',306,replace(replace('<p class="gatha1">Pakāsetvā <a name="M0.0306"></a> paveseti, annapānaṃ mahārahaṃ;</p>\r\n<p class="gathalast">Paṭicchanno niharati, tameva nto ṭhitaṃ jano.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> Pavese taṃ parivato,</p>\r\n<p class="gatha2">Nihareko raho lino;</p>\r\n<p class="gatha3">Manuññaṃva pavīsante,</p>\r\n<p class="gathalast">Nikkhamante jigucchitaṃ.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-31-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',307,replace(replace('<p class="gatha1">Jeguccha <a name="M0.0307"></a> paṭikulyāni, maṃsanhāru tacaṭṭhini;</p>\r\n<p class="gathalast">Napiyāni na tuṭṭhāni, nevaitthī napūpiso.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> Hattha pāda mukhādīni,</p>\r\n<p class="gatha2">Natthaññāni jigucchitā;</p>\r\n<p class="gatha3">Tatthā kumārikā kaññā,</p>\r\n<p class="gathalast">Mohena atthisaññitā.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-34-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',308,replace(replace('<p class="gatha1">Paccekaṃ <a name="M0.0308"></a> vinibhuttesa, kesa loma nakhādisu;</p>\r\n<p class="gathalast">Natthikaññā kumārīvā, sampiṇḍitesu sā kuto.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',309,replace(replace('<p class="gatha1">Ākāsoyeva <a name="M0.0309"></a> kāyāṅkhyo,</p>\r\n<p class="gatha2">Tacādi parivārito;</p>\r\n<p class="gatha3">Tathāsīsaṃ mukhaṃhattho,</p>\r\n<p class="gathalast">Pādoru kaṭiādayo.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> Thambhādīsviva gehoti,</p>\r\n<p class="gatha2">Piṇḍite svesu sammuti;</p>\r\n<p class="gatha3">Kāyoti itthiposoti,</p>\r\n<p class="gathalast">Saṃmuḷho tāyarajjati.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-36-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',310,replace(replace('<p class="gatha1">Santaṃ <a name="M0.0310"></a> cinteyya nāsantaṃ, santaṃ cintayato sukhaṃ;</p>\r\n<p class="gathalast">Asantaṃ anucintento, nānādukkhehi tappati.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> Javatyā vijjamāneva, nāvijjā vijjamānake;</p>\r\n<p class="gathalast">Tasmātaṃnāmako moho, taṇhāpica tadanvitā.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-38-39-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',311,replace(replace('<p class="gatha1">Puṃkāyovāthīkāyovā<a name="M0.0311"></a>, malāsucijigucchito;</p>\r\n<p class="gathalast">Tassamaṃ natthigārayhaṃ, yvāmalampi malaṃkare.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',312,replace(replace('<p class="gatha1">Natthi <a name="M0.0312"></a> kāyasamoverī,</p>\r\n<p class="gatha2">Mahānatthakaro cīraṃ;</p>\r\n<p class="gatha3">Natthi kāyasamo vañco,</p>\r\n<p class="gathalast">Asubhova subhāyate.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> Thīpuṃ saparakāyoti, passatipi napassati;</p>\r\n<p class="gathalast">Jeguccha paṭikulyoti, sammā passati passati.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',313,replace(replace('<p class="gatha1">Subhosubhoti <a name="M0.0313"></a> maññantā,</p>\r\n<p class="gatha2">Dhīti dhīti jinerite;</p>\r\n<p class="gatha3">Lokālokā nadhīyesaṃ,</p>\r\n<p class="gathalast">Bhavā bhavā vacārino.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> Bhiyyobhiyyova rāgaggi,</p>\r\n<p class="gatha2">Subhosubhotipassato;</p>\r\n<p class="gatha3">Mandomandova soaggi,</p>\r\n<p class="gathalast">Dhīvadhīvavipassato.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-43-44-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',314,replace(replace('<p class="gatha1">Bahussutopi <a name="M0.0314"></a> bālova, asubhe subhamaññako;</p>\r\n<p class="gathalast">Asubhoti vipassanto, appassutopipaṇḍito.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> Yoca sippāni jāneyya, satāni sahassānipi;</p>\r\n<p class="gathalast">Kāyekajānanaṃ seyyo, yañce añña vijānaṃnaṃ.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-45-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',315,replace(replace('<p class="gatha1">Kāyamekampi <a name="M0.0315"></a> naññāmi, buddhāladdhanayo api;</p>\r\n<p class="gathalast">Sutāca paṇḍitātyamhā, yuttoyevā tilajjituṃ.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> Subhatoyeva maññāmi, evaṃ jigucchitampinaṃ;</p>\r\n<p class="gathalast">Mañca ñe paṇḍito tyāhu, alamevātilajjituṃ.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-47-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',316,replace(replace('<p class="gatha1">Kāye <a name="M0.0316"></a> asubhasaññaṃyo, nalabhāmi kadācipi;</p>\r\n<p class="gathalast">Suladdha sugato vādo, svārahovātilajjituṃ.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> Kāyena saṃsarantopi, tadākāraṃ yathātathaṃ;</p>\r\n<p class="gathalast">Bhavebhave ajānanto, mamāyitvāva taṃ cajiṃ.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-49-50-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',317,replace(replace('<p class="gatha1">Kāyena <a name="M0.0317"></a> saṃsarantopi, naññā kāya jiguccataṃ;</p>\r\n<p class="gathalast">Niccupādā mamāyanto, piyāyitvāva taṃ cajiṃ.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> Kubhāraṃ sārasaññāya, piyāyitvāva hiṃsakaṃ;</p>\r\n<p class="gathalast">Anantadukkha māpādiṃ, vipallāso bhavebhave.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-51-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',318,replace(replace('<p class="gatha1">Mahājānīya <a name="M0.0318"></a> pattoti, saṃvejetvā sakaṃmanaṃ;</p>\r\n<p class="gathalast">Dirokata jinovādo, anivattita vīriyo.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> Adiṭṭhapubba metassa, tathākāraṃva passatu;</p>\r\n<p class="gathalast">Kicca mañña mupekkhāya, saṃsāra bhaya bhīruko.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-53-54-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',319,replace(replace('<p class="gatha1">Yañhikiccaṃ <a name="M0.0319"></a> apaviṭṭhaṃ, akiccaṃ pana kayirā;</p>\r\n<p class="gathalast">Unnaḷānaṃ pamattānaṃ, tesaṃ vaḍḍhanti āsavā.</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> Yesañca susamāraddhā, niccaṃ kāyagatā sati;</p>\r\n<p class="gatha2">Akiccaṃ te nasevanti, kicce sātata kārino;</p>\r\n<p class="gathalast">Satānaṃ sampajānānaṃ, atthaṃ gacchanti āsavāti.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-55-56-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',320,replace(replace('<p class="gatha1">Thomentā <a name="M0.0320"></a> soṇṇaṃ kāyora,</p>\r\n<p class="gatha2">Mukhakkhi tyādinā imaṃ;</p>\r\n<p class="gatha3">Ratte muṭṭhe karontete,</p>\r\n<p class="gathalast">Aññejane sayaṃviya.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> Kāyasobhya pakāsetā,</p>\r\n<p class="gatha2">Vācā ve māradesanā;</p>\r\n<p class="gatha3">Tadasobhya pakāsetā,</p>\r\n<p class="gathalast">Vācā sammuddha desanā.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-57-58-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',321,replace(replace('<p class="gatha1">Asubhoti <a name="M0.0321"></a> jinuddiṭṭhaṃ, kāyaṃ subhoti gāhino;</p>\r\n<p class="gathalast">Saṃmuḷhāte na muccanti, bhavā buddha virodhino.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> Asubhoti jinuddiṭṭhaṃ, kāyaṃ tatheva gāhino;</p>\r\n<p class="gathalast">Paṇḍitā teva muccanti, bhavā buddhamatānugā.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-59-60-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',322,replace(replace('<p class="gatha1">Sodhentelaṅkaronteva<a name="M0.0322"></a>, malāsavantikāyato;</p>\r\n<p class="gathalast">Alaṃ kāyavisodhena, bālova taṃ garuṃ karo.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> Gopenteva arogāya,</p>\r\n<p class="gatha2">Kāyo rogenasaṃvase;</p>\r\n<p class="gatha3">Gāyaguttaṃ mudhāyeva,</p>\r\n<p class="gathalast">Cittaguttaṃva sātthakaṃ.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-61-62-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',323,replace(replace('<p class="gatha1">Candanādi <a name="M0.0323"></a> vilittopi,</p>\r\n<p class="gatha2">Muttomaṇi vibhūsito;</p>\r\n<p class="gatha3">Taṃsabhāvova sokāyo,</p>\r\n<p class="gathalast">Vissavanto tatotato.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',324,replace(replace('<p class="gatha1">Patiteca <a name="M0.0324"></a> apatite,</p>\r\n<p class="gatha2">Viseso natthi kiñcipi;</p>\r\n<p class="gatha3">Kāyo cemanuñño tamhā,</p>\r\n<p class="gathalast">Patitopi tathāsiyā.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> Kāyo manussajātīnaṃ, tiracchāna ttabhāvato;</p>\r\n<p class="gathalast">Jegucchita tarohoti, dubbisodhoca dubbharo.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-64-65-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',325,replace(replace('<p class="gatha1">Yathājātena <a name="M0.0325"></a> kāyena, sakkā viharituṃ naca;</p>\r\n<p class="gathalast">Paccahaṃ sodhanīyoca, dhovana majjanādibhi.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> Rattaṃ pātuṃ chaviṃ chetvā, sakkā ḍaṃsādayopinaṃ;</p>\r\n<p class="gathalast">Chetvā maṃsa ṭṭhikādīni, dhīro nālambituṃ kathaṃ.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-66-67-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',326,replace(replace('<p class="gatha1">Lagganti <a name="M0.0326"></a> chavimatte ye, makkhikā sedapā yathā;</p>\r\n<p class="gathalast">Thīpuṃ mukhādi saññāya, te pamuḷhā mahātapā.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> Cārī agocare kāme, laggālepe kapīriva;</p>\r\n<p class="gathalast">Bahūhi pīḷitā rīhi, maranti atidukkhino.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-68-69-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',327,replace(replace('<p class="gatha1">Rāgāriṃ <a name="M0.0327"></a> dujjayaṃ jeyyuṃ, jayabhummāsubhe carā;</p>\r\n<p class="gathalast">Sītānissita laṭukī, senakaṃva mahabbalaṃ.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-70-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',328,replace(replace('<p class="gatha1">Kāyadhi <a name="M0.0328"></a> ggocaro veso,</p>\r\n<p class="gatha2">Jayabhūbuddha duttiyā;</p>\r\n<p class="gatha3">Ettheva gocarā hontu,</p>\r\n<p class="gathalast">Mābho kāme jayatthikā.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',329,replace(replace('<p class="gatha1">Kāyā <a name="M0.0329"></a> subhaṃ vipassantu, dibba kkhināpya passiyaṃ;</p>\r\n<p class="gathalast">Āyatiṃ maggalābhāya, taṃ dassanaṃ bhavissati.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> Dhīcakkhunāva dhikkāyaṃ, passe na maṃsacakkhunā;</p>\r\n<p class="gathalast">Ummilitvāva dhīcakkhuṃ, vivekaṭṭho udikkhatu.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-72-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',330,replace(replace('<p class="gatha1">Pañcaṅgāni <a name="M0.0330"></a> yathā kummo, cakkhādīni nigūhaye;</p>\r\n<p class="gathalast">Verī labhatu mokāsaṃ, pañcadvārā arakkhitā.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-74-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',331,replace(replace('<p class="gatha1">Cakkhurūpena <a name="M0.0331"></a> saṃvāsā, rāgaputtaṃ vijāyati;</p>\r\n<p class="gathalast">Mahānatthakaro soca, saṃvāsaṃ tena vāraye.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> Rūpādīsusañjantīti, sattā itthyādi saññāya;</p>\r\n<p class="gathalast">Natveva khandhasaññāya, taṃsaññihi virāgino.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-75-76-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',332,replace(replace('<p class="gatha1">Sakāyeparakāyeca<a name="M0.0332"></a>,</p>\r\n<p class="gatha2">Āsaṃ chindeyya paṇḍito;</p>\r\n<p class="gatha3">Āsaṃ chetvā sukhaṃseti,</p>\r\n<p class="gathalast">Āsāya dukkhitā pajā.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> Dassane savane kāya,</p>\r\n<p class="gatha2">Saṃsagge methunepica;</p>\r\n<p class="gatha3">Nirāso sukhito hoti,</p>\r\n<p class="gathalast">Anirāsotidukkhito.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-77-78-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',333,replace(replace('<p class="gatha1">Bahīva <a name="M0.0333"></a> sodhitaṃ yassa, na vanto jeguccha puñjakaṃ;</p>\r\n<p class="gathalast">Taṃkāyaṃ asutaṃjāna, tanurāgo siyāttani.</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> Kāyevirāga micchanto, nupasseyya tadantaraṃ;</p>\r\n<p class="gathalast">Antodassī atappanto, labhe saṃsāramocanaṃ.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-79-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',334,replace(replace('<p class="gatha1">Sattā <a name="M0.0334"></a> sattā bahiṭṭhevā, sāraṃsāraṃ mamāyino;</p>\r\n<p class="gathalast">Santosanto vipassanto, navānavāyatiṃbhave.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-81-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',335,replace(replace('<p class="gatha1">Alaṃ <a name="M0.0335"></a> alaṃ katvā kāyaṃ, malāmalāsavantito;</p>\r\n<p class="gathalast">Sobhaṃ sobhaṃ naye ṭhānaṃ, manaṃ manaṃ pyalaṃ kataṃ.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> Saṃsaggajātassa bhavanti snehā,</p>\r\n<p class="gatha2">Snehānvayaṃ dukkha midaṃ pahoti;</p>\r\n<p class="gatha3">Ādinavaṃ snehajaṃpekkha māno,</p>\r\n<p class="gathalast">Eko carekhagga visāṇa kappo.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-82-83-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',336,replace(replace('<p class="gatha1">Khiṭṭā <a name="M0.0336"></a> rati hoti sahāya majjhe,</p>\r\n<p class="gatha2">Puttesuca vipulaṃ hoti pemaṃ;</p>\r\n<p class="gatha3">Piyavippayogaṃ vijigucchamāno,</p>\r\n<p class="gathalast">Eko care khaggavisāṇa kappo.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-84-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',337,replace(replace('<p class="gatha1">Vaṃso <a name="M0.0337"></a> visāloyathā visatto,</p>\r\n<p class="gatha2">Puttesu dāresuca yāapekkhā;</p>\r\n<p class="gatha3">Vaṃsakaḷirova asajjamāno,</p>\r\n<p class="gathalast">Eko care khaggavisāṇa kappo.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-85-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',338,replace(replace('<p class="gatha1">Kāmaṃ <a name="M0.0338"></a> kāmaya mānassa, tassacetaṃ samijjhati;</p>\r\n<p class="gathalast">Addhā pītimano hoti, macco laddhā yadicchati.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> Tassace kāmayānassa, chandajātassa jantuno;</p>\r\n<p class="gathalast">Tekāmā parihāyanti, sallaviddhova ruppati.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-86-87-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',339,replace(replace('<p class="gatha1">Yokāme <a name="M0.0339"></a> parivajjeti, sappasseva padā siro;</p>\r\n<p class="gathalast">Somaṃ visattikaṃ loke, sato samativattati.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> Khettaṃ vatthuṃ taḷākaṃvā, gavassaṃ dāsaporisaṃ;</p>\r\n<p class="gathalast">Thiyo bandhū puthukāme, yonaro anugijjhati.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> Abalā naṃ balīyanti, maddantenaṃ parissayā;</p>\r\n<p class="gathalast">Tatonaṃ dukkhamanveti, nāvaṃ bhinna mivodakaṃ.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-88-89-90-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',340,replace(replace('<p class="gatha1">Tasmājantu <a name="M0.0340"></a> sadāsato, kāmāni parivajjaye;</p>\r\n<p class="gathalast">Te pahāya tare oghaṃ, nāvaṃ sitvāva pāragū.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-91-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',341,replace(replace('<p class="gatha1">Kāmato <a name="M0.0341"></a> jāyate soko,</p>\r\n<p class="gatha2">Kāmato jāyate bhayaṃ;</p>\r\n<p class="gatha3">Kāmato vippamuttassa,</p>\r\n<p class="gathalast">Natthi soko kuto bhayaṃ.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-92-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',342,replace(replace('<p class="gatha1">Subhānupassiṃ <a name="M0.0342"></a> viharantaṃ, indriyesu asaṃvutaṃ;</p>\r\n<p class="gatha2">Bhojanamhi amattaññuṃ, kusitaṃ hīna vīriyaṃ;</p>\r\n<p class="gathalast">Taṃve pasahati māro, vāto rukkhaṃva dubbalaṃ.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> Asubhānupassiṃ viharantaṃ, indriyesu susaṃvutaṃ;</p>\r\n<p class="gatha3">Bhojanamhica mattaññuṃ, saddhaṃ āraddha vīriyaṃ;</p>\r\n<p class="gathalast">Taṃve nappasahati māro, vāto selaṃva pabbataṃ.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-93-94-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',343,replace(replace('<p class="gatha1">Yathā <a name="M0.0343"></a> agāraṃ ducchannaṃ, vuṭṭhi samati vijjhati;</p>\r\n<p class="gathalast">Evaṃ abhāvitaṃ cittaṃ, rāgo samativijjhati.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',344,replace(replace('<p class="gatha1">Tadevaṃ <a name="M0.0344"></a> paccavekkhanti, ye te rāgaggidubbalā;</p>\r\n<p class="gathalast">Patiṭṭhaṃ sāsane laddhā, nukkaṇṭhā nalasā ratā.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-96-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',345,replace(replace('<p class="gatha1">Buddhāvādaṃ <a name="M0.0345"></a> labhitvāpi, nāhaṃsakkā navomhiti;</p>\r\n<p class="gatha2">Dosaṃ taṇhaṃ anāsento, paripakko kadābhave;</p>\r\n<p class="gathalast">Puññakammaṃ akaronto, paravajjaṃ akhamanto.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> Kāya saṅkhārikā taṇhā, nīcānīcakarāca sā;</p>\r\n<p class="gathalast">Cittasaṅkhārikā saddhā, uccā uccakarāca sā.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-97-98-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',346,replace(replace('<p class="gatha1">Dassanīye <a name="M0.0346"></a> ratā taṇhā, saddhāsvācārabhattikā;</p>\r\n<p class="gathalast">Vikiṇṇacārikā taṇhā, saddhā visadacārinī.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> Manokilesikā taṇhā, sānugānanta dukkhadā;</p>\r\n<p class="gathalast">Cittappasādikā saddhā, attānuga sukhāvahā.</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-99-100-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',347,replace(replace('<p class="gatha1">Taṇhā <a name="M0.0347"></a> saddhāna miccevaṃ, visesaṃ jāna tatvato;</p>\r\n<p class="gathalast">Ñatvā taṇhaṃ vināseyya, saddhaṃbhāveyya cetasi.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> Ucchukaṃ yantapattampi, sañcuṇṇitampi candanaṃ;</p>\r\n<p class="gathalast">Madhuraṃva sugandhaṃva, mettiva hiṃsitopi saṃ.</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-101-102-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',348,replace(replace('<p class="gatha1">Attacchedampi <a name="M0.0348"></a> vāseti, sugandheniva candanaṃ;</p>\r\n<p class="gathalast">Santo mettāsugandhena, attahiṃsampi vāsaye.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> Kadācipi na duggandhi, sukkhaṃ cuṇṇampi candanaṃ;</p>\r\n<p class="gathalast">Tatheva dukkhapattopi, na santo pāpakārako.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-103-104-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',349,replace(replace('<p class="gatha1">Khame <a name="M0.0349"></a> vajjaṃ kareyyatthaṃ, buddhakhanti manussaraṃ;</p>\r\n<p class="gathalast">Mettātintena verīpi, nupanāho siyattani.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> Nagacchati ta makkoso, mamevā natthakārako;</p>\r\n<p class="gathalast">Iti ñatvāva sappañño, neva kkoseyya kiñcanaṃ.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-105-106-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',350,replace(replace('<p class="gatha1">Akkoso <a name="M0.0350"></a> maṃ naāgacche,</p>\r\n<p class="gatha2">Tassevā natthakārako;</p>\r\n<p class="gatha3">Iti ñatvā titikkheyya,</p>\r\n<p class="gathalast">Na paccakkosanaṃ kare.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> Akkosaka nayaṃ gaṇhi,</p>\r\n<p class="gatha2">Paccakkoso na so varo;</p>\r\n<p class="gatha3">Budho taṃ nānugāheyya,</p>\r\n<p class="gathalast">Mā sova pāpiyo bhave.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-107-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',351,replace(replace('<p class="gatha1">Taṇhāvijjāca <a name="M0.0351"></a> mūlādve, saṃsāravisapādape;</p>\r\n<p class="gathalast">Sabbhatti saddhammassutaṃ, dveyeva madhurā phalā.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> Sodhe citta mupakkamma, suddhaṃ upakkamena taṃ;</p>\r\n<p class="gathalast">Vahe sukhaṃ asaṅkheyyaṃ, dukkhaṃ asodhitaṃ mali.</p>\r\n<p class="hangnum"><a name="para110"></a><span class="paranum">110</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-109-110-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',352,replace(replace('<p class="gatha1">Sodhitaṃ <a name="M0.0352"></a> sugatiṃneti, duggatiṃva asodhitaṃ;</p>\r\n<p class="gathalast">Cittaṃ sodhetu mālimpe, rāgadosa malehi taṃ.</p>\r\n<p class="hangnum"><a name="para111"></a><span class="paranum">111</span>.</p>\r\n<p class="gatha1"> Dosejā nāsitā yena,</p>\r\n<p class="gatha2">Sāsanevatthi sonayo;</p>\r\n<p class="gatha3">Natthaññattha tamādāya,</p>\r\n<p class="gathalast">Budho nāsetu taṃdvayaṃ.</p>\r\n<p class="hangnum"><a name="para112"></a><span class="paranum">112</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-111-112-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1010n',353,replace(replace('<p class="gatha1">Ranakunavāsi <a name="M0.0353"></a> katāvāse, daguṃcetī puratthime;</p>\r\n<p class="gathalast">Vasatā aggadhammena, therena racito ayanti.</p>','\r',char(13)),'\n',char(10)),'');
