INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="chapter">Cāṇakyanītipāḷi</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',1,replace(replace('<p class="gatha1">Nānāsattho <a name="M0.0001"></a> ddhataṃ vakkhe,</p>\r\n<p class="gatha2">Rāja nīti samuccayaṃ;</p>\r\n<p class="gatha3">Sabba bīja midaṃ satthaṃ,</p>\r\n<p class="gathalast">Cāṇakyaṃ sārasaṅgahaṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Mūlasuttaṃ pavakkhāmi,</p>\r\n<p class="gatha2">Cāṇakyena yathoditaṃ;</p>\r\n<p class="gatha3">Yassa viññāna mattena,</p>\r\n<p class="gathalast">Mūḷho bhavati paṇḍito.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',2,replace(replace('<p class="gatha1">Viduttaṃ <a name="M0.0002"></a> narapaccañca,</p>\r\n<p class="gatha2">Nevatulyaṃ kudācanaṃ;</p>\r\n<p class="gatha3">Sadese pujjate rājā,</p>\r\n<p class="gathalast">Vidū sabbattha pujjate.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',3,replace(replace('<p class="gatha1">Paṇḍite <a name="M0.0003"></a> ca guṇā sabbe,</p>\r\n<p class="gatha2">Mūḷhe dosā hi kevalaṃ;</p>\r\n<p class="gatha3">Tasmā mūḷhasahassesu,</p>\r\n<p class="gathalast">Pañño eko visisyate.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',4,replace(replace('<p class="gatha1">Mātariva <a name="M0.0004"></a> paradāresu,</p>\r\n<p class="gatha2">Paradabbesu leṭṭuva;</p>\r\n<p class="gatha3">Attaniva sabbabhūtesu,</p>\r\n<p class="gathalast">Yo passati sapaṇḍito.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Kiṃkulena visālena,</p>\r\n<p class="gatha2">Guṇahīno tu yonaro;</p>\r\n<p class="gatha3">Akulinopi satthañño,</p>\r\n<p class="gathalast">Devatāhipi pujjate.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',5,replace(replace('<p class="gatha1">Rūpayobbanasampannā<a name="M0.0005"></a>,</p>\r\n<p class="gatha2">Visālakulasambhavā;</p>\r\n<p class="gatha3">Vijjāhīnā nasobhante,</p>\r\n<p class="gathalast">Niggandhā iva kiṃsukā.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',6,replace(replace('<p class="gatha1">Tārānaṃ <a name="M0.0006"></a> bhūsaṇaṃ cando,</p>\r\n<p class="gatha2">Nārīnaṃ bhūsaṇaṃ pati;</p>\r\n<p class="gatha3">Puthabyā bhūsaṇaṃ rājā,</p>\r\n<p class="gathalast">Vijjā sabbassa bhūsaṇaṃ.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Mātā sattu pitā verī,</p>\r\n<p class="gatha2">Yena bālo napāṭṭhito;</p>\r\n<p class="gatha3">Na sobhate sabhāmajjhe,</p>\r\n<p class="gathalast">Haṃsamajjhe bakoyathā.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',7,replace(replace('<p class="gatha1">Varameko <a name="M0.0007"></a> guṇī putto,</p>\r\n<p class="gatha2">Na ca mūḷhasatehipi;</p>\r\n<p class="gatha3">Eko cando tamo hanti,</p>\r\n<p class="gathalast">Na ca tāragaṇehipi.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',8,replace(replace('<p class="gatha1">Lālaye <a name="M0.0008"></a> pañcavassāni,</p>\r\n<p class="gatha2">Dasavassāni tālaye;</p>\r\n<p class="gatha3">Patte tu soḷase vasse,</p>\r\n<p class="gathalast">Puttaṃ mittaṃva ācare.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',9,replace(replace('<p class="gatha1">Lālane <a name="M0.0009"></a> bahavo dosā,</p>\r\n<p class="gatha2">Tālane bahavo guṇā;</p>\r\n<p class="gatha3">Tasmā puttañca sissañca,</p>\r\n<p class="gathalast">Tālaye na tu lālaye.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',10,replace(replace('<p class="gatha1">Ekenāpi <a name="M0.0010"></a> suvakkhena,</p>\r\n<p class="gatha2">Pupphitena sugandhinā;</p>\r\n<p class="gatha3">Vāsitassa vanaṃ sabbaṃ,</p>\r\n<p class="gathalast">Suputtena kulaṃyathā.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',11,replace(replace('<p class="gatha1">Ekassāpi <a name="M0.0011"></a> kuvakkhassa,</p>\r\n<p class="gatha2">Koṭaraṭṭhena agginā;</p>\r\n<p class="gatha3">Dayhate hi vanaṃ sabbaṃ,</p>\r\n<p class="gathalast">Kuputtena kulaṃ yathā.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Dūrato sobhate mūḷho,</p>\r\n<p class="gatha2">Lambamāna paṭāvuto;</p>\r\n<p class="gatha3">Tāvañca sobhate mūḷho,</p>\r\n<p class="gathalast">Yāva kiñci nabhāsate.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',12,replace(replace('<p class="gatha1">Visato <a name="M0.0012"></a> amataṃ gāyhaṃ,</p>\r\n<p class="gatha2">Amejjhāapi kañcanaṃ;</p>\r\n<p class="gatha3">Nīcato uttamā vijjā,</p>\r\n<p class="gathalast">Thīratnaṃ dukkulāapi.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',13,replace(replace('<p class="gatha1">Ussave <a name="M0.0013"></a> byasaneceva,</p>\r\n<p class="gatha2">Dubbhikkhe sattuviggahe;</p>\r\n<p class="gatha3">Rājadvāre susāneca,</p>\r\n<p class="gathalast">Yo tiṭṭhati sabandhavo.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> Parokkhe kiccahantāraṃ,</p>\r\n<p class="gatha2">Paccakkhe piyavādinaṃ;</p>\r\n<p class="gatha3">Vajjaye tādisaṃ mittaṃ,</p>\r\n<p class="gathalast">Visakumbhaṃ payomukhaṃ.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',14,replace(replace('<p class="gatha1">Sakiṃ <a name="M0.0014"></a> duṭṭhañca mittaṃ yo,</p>\r\n<p class="gatha2">Puna sandhātu micchati;</p>\r\n<p class="gatha3">Samaccu mupagaṇhāti,</p>\r\n<p class="gathalast">Gabbha massatarī yathā.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',15,replace(replace('<p class="gatha1">Na <a name="M0.0015"></a> vissase avissatthaṃ,</p>\r\n<p class="gatha2">Mittañcāpi na vissase;</p>\r\n<p class="gatha3">Kadāci kupitaṃ mittaṃ,</p>\r\n<p class="gathalast">Sabbadosaṃ pakāsaye.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',16,replace(replace('<p class="gatha1">Jāniyā <a name="M0.0016"></a> pesane bhacce,</p>\r\n<p class="gatha2">Bandhave byasanāgame;</p>\r\n<p class="gatha3">Mittañcā padikāleca,</p>\r\n<p class="gathalast">Bhariyañca vibhavakkhaye.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> Upakāraggahitena,</p>\r\n<p class="gatha2">Sattunāsattumuddhare;</p>\r\n<p class="gatha3">Pādalaggaṃ karaṭṭhena,</p>\r\n<p class="gathalast">Kaṇṭakeneva kaṇṭakaṃ.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',17,replace(replace('<p class="gatha1">Na <a name="M0.0017"></a> mittaṃ koci kassaci,</p>\r\n<p class="gatha2">Na koci ripu kassaci;</p>\r\n<p class="gatha3">Kāraṇena hi ñāyati,</p>\r\n<p class="gathalast">Mittāni ca ripū tathā.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',18,replace(replace('<p class="gatha1">Dujjano <a name="M0.0018"></a> piyavādī ca,</p>\r\n<p class="gatha2">Netaṃ vissāsakāraṇaṃ;</p>\r\n<p class="gatha3">Madhu tiṭṭhati jivhagge,</p>\r\n<p class="gathalast">Hadaye tu halāhalaṃ.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> Dujjano parihantabbo,</p>\r\n<p class="gatha2">Vijjāyā laṅkatopi saṃ;</p>\r\n<p class="gatha3">Maṇinā bhūsito sappo,</p>\r\n<p class="gathalast">Kime so nabhayaṃ karo.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-26-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',19,replace(replace('<p class="gatha1">Sappo <a name="M0.0019"></a> kūro khalo kūro,</p>\r\n<p class="gatha2">Sappā kūrataro khalo;</p>\r\n<p class="gatha3">Manto sadhivaso sappo,</p>\r\n<p class="gathalast">Khalo kena nivāyyate.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',20,replace(replace('<p class="gatha1">Nakhīnañca <a name="M0.0020"></a> nadīnañca,</p>\r\n<p class="gatha2">Siṅgīnaṃ satthapāṇinaṃ;</p>\r\n<p class="gatha3">Vissāso nevakātabbo,</p>\r\n<p class="gathalast">Thīsu rājakulesu ca.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',21,replace(replace('<p class="gatha1">Hatthī <a name="M0.0021"></a> hatthasahassena,</p>\r\n<p class="gatha2">Satahatthena vājino;</p>\r\n<p class="gatha3">Siṅgino dasahatthena,</p>\r\n<p class="gathalast">Ṭhānacāgena dujjano.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',22,replace(replace('<p class="gatha1">Āpadatthaṃ <a name="M0.0022"></a> dhanaṃ rakkhe,</p>\r\n<p class="gatha2">Dāraṃ rakkhe dhanehipi;</p>\r\n<p class="gatha3">Attānaṃ satataṃ rakkhe,</p>\r\n<p class="gathalast">Dārehipi dhanehipi.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> Paradāraṃ paradabbaṃ,</p>\r\n<p class="gatha2">Parivādaṃ parassa ca;</p>\r\n<p class="gatha3">Parihāsaṃ guruṭṭhāne,</p>\r\n<p class="gathalast">Cāpalyañca vivajjaye.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',23,replace(replace('<p class="gatha1">Caje <a name="M0.0023"></a> ekaṃ kulassatthe,</p>\r\n<p class="gatha2">Gāmassatthe kulaṃ caje;</p>\r\n<p class="gatha3">Gāma janapadassatthe,</p>\r\n<p class="gathalast">Attatthe pathaviṃ caje.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',24,replace(replace('<p class="gatha1">Calatyekena <a name="M0.0024"></a> pādena,</p>\r\n<p class="gatha2">Tiṭṭha tyekena buddhimā;</p>\r\n<p class="gatha3">Nāsamikkhya paraṃ ṭhānaṃ,</p>\r\n<p class="gathalast">Pubbamāyatanaṃ caje.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> Luddha matthena gaṇheyya,</p>\r\n<p class="gatha2">Thaddha mañjalī kammunā;</p>\r\n<p class="gatha3">Mūḷhaṃ chando nuvattena,</p>\r\n<p class="gathalast">Tathā tathena paṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-34-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',25,replace(replace('<p class="gatha1">Atthanāsaṃ <a name="M0.0025"></a> manotāpaṃ,</p>\r\n<p class="gatha2">Gehe duccaritāni ca;</p>\r\n<p class="gatha3">Vañcanañca pamāṇañca,</p>\r\n<p class="gathalast">Matimā na pakāsaye.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',26,replace(replace('<p class="gatha1">Dhanadhaññappayogesu<a name="M0.0026"></a>,</p>\r\n<p class="gatha2">Tathā vijjāgamesu ca;</p>\r\n<p class="gatha3">Āhāre byavahāre ca,</p>\r\n<p class="gathalast">Cattalajjo sadā bhave.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> Dhanino sotthiyo rājā,</p>\r\n<p class="gatha2">Nadī vejjo tu pañcamo;</p>\r\n<p class="gatha3">Pañca yatra navijjante,</p>\r\n<p class="gathalast">Tatra vāsaṃ nakāraye.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-36-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',27,replace(replace('<p class="gatha1">Yasmiṃdese <a name="M0.0027"></a> na sammānaṃ,</p>\r\n<p class="gatha2">Na pīti naca bandhavā;</p>\r\n<p class="gatha3">Na ca vijjāgamo koci,</p>\r\n<p class="gathalast">Taṃdesaṃ parivajjaye.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',28,replace(replace('<p class="gatha1">Manasā <a name="M0.0028"></a> cintitaṃ kammaṃ,</p>\r\n<p class="gatha2">Vacasā napakāsaye;</p>\r\n<p class="gatha3">Aññalakkhita kāriyassa,</p>\r\n<p class="gathalast">Yato siddhi najāyate.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',29,replace(replace('<p class="gatha1">Kudesañca <a name="M0.0029"></a> kuvuttiñca,</p>\r\n<p class="gatha2">Kubhariyaṃ kunadiṃ tathā;</p>\r\n<p class="gatha3">Kudabbañca kubhojjañca,</p>\r\n<p class="gathalast">Vajjaye tu vicakkhaṇo.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> Iṇasesoggi seso ca,</p>\r\n<p class="gatha2">Byādhiseso tatheva ca;</p>\r\n<p class="gatha3">Puna ca vaḍḍhate yasmā,</p>\r\n<p class="gathalast">Tasmā sesaṃ nakāraye.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-40-41-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',30,replace(replace('<p class="gatha1">Cintā <a name="M0.0030"></a> jaro manussānaṃ,</p>\r\n<p class="gatha2">Vatthānaṃ ātapo jaro;</p>\r\n<p class="gatha3">Asobhagyaṃ jaro thīnaṃ,</p>\r\n<p class="gathalast">Assānaṃ methunaṃ jaro.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',31,replace(replace('<p class="gatha1">Atthi <a name="M0.0031"></a> putto vase yassa,</p>\r\n<p class="gatha2">Bhacco bhariyā tatheva ca;</p>\r\n<p class="gatha3">Abhāve pyatisantoso,</p>\r\n<p class="gathalast">Saggaṭṭho so mahītale.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> Duṭṭhā bhariyā saṭhaṃ mittaṃ,</p>\r\n<p class="gatha2">Bhacco cuttaradāyako;</p>\r\n<p class="gatha3">Sa sappeca gahe vāso,</p>\r\n<p class="gathalast">Maccureva nasaṃsayo.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-43-44-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',32,replace(replace('<p class="gatha1">Mātā <a name="M0.0032"></a> yassa gehe natthi,</p>\r\n<p class="gatha2">Bhariyācā piyavādinī;</p>\r\n<p class="gatha3">Araññaṃ tena gantabbaṃ,</p>\r\n<p class="gathalast">Yathā raññaṃ tathāgahaṃ.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',33,replace(replace('<p class="gatha1">Iṇakattā <a name="M0.0033"></a> pitā sattu,</p>\r\n<p class="gatha2">Mātā ca byabhicārinī;</p>\r\n<p class="gatha3">Bhariyā rūpavatī sattu,</p>\r\n<p class="gathalast">Putto sattu apaṇḍito.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',34,replace(replace('<p class="gatha1">Kokilānaṃ <a name="M0.0034"></a> saro rūpaṃ,</p>\r\n<p class="gatha2">Nārī rūpaṃ patibbatā;</p>\r\n<p class="gatha3">Vijjā rūpaṃ kurūpānaṃ,</p>\r\n<p class="gathalast">Khamā rūpaṃ tapassinaṃ.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> Avijjaṃ jīvanaṃ suññaṃ,</p>\r\n<p class="gatha2">Disā suññā abandhavā;</p>\r\n<p class="gatha3">Puttahīnaṃ gahaṃ suññaṃ,</p>\r\n<p class="gathalast">Sabbasuññā daliddatā.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-47-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',35,replace(replace('<p class="gatha1">Adātā<a name="M0.0035"></a> vaṃsadosena,</p>\r\n<p class="gatha2">Kammadosā daliddatā;</p>\r\n<p class="gatha3">Ummādā mātudosena,</p>\r\n<p class="gathalast">Pitudosena mūḷhatā.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-49-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',36,replace(replace('<p class="gatha1">Guru <a name="M0.0036"></a> aggi dvijādīnaṃ,</p>\r\n<p class="gatha2">Vaṇṇānaṃ brāhmaṇo guru;</p>\r\n<p class="gatha3">Pati reko gurutthīnaṃ,</p>\r\n<p class="gathalast">Sabbassābhyāgato guru.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-50-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',37,replace(replace('<p class="gatha1">Atidabbe <a name="M0.0037"></a> hatā laṅkā,</p>\r\n<p class="gatha2">Atimāne ca koravā;</p>\r\n<p class="gatha3">Atidāne balībaddho,</p>\r\n<p class="gathalast">Sabbamaccanta gahitaṃ.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-51-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',38,replace(replace('<p class="gatha1">Vatthahīno <a name="M0.0038"></a> tvalaṅkāro,</p>\r\n<p class="gatha2">Ghatahīnañca bhojanaṃ;</p>\r\n<p class="gatha3">Thanahīnā ca yānārī,</p>\r\n<p class="gathalast">Vijjāhīnañca jīvanaṃ.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> Bhojjaṃ bhojanasatti ca,</p>\r\n<p class="gatha2">Ratisatti varā thiyo;</p>\r\n<p class="gatha3">Vibhavo dānasatti ca,</p>\r\n<p class="gathalast">Nāppassa tapaso phalaṃ.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-52-53-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',39,replace(replace('<p class="gatha1">Puttappayojanā <a name="M0.0039"></a> dārā,</p>\r\n<p class="gatha2">Putto piṇḍappayojano;</p>\r\n<p class="gatha3">Hitappayojanaṃ mittaṃ,</p>\r\n<p class="gathalast">Dhanaṃ sabbappayojanaṃ.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-54-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',40,replace(replace('<p class="gatha1">Dullabhaṃ <a name="M0.0040"></a> pākatikaṃ vākyaṃ,</p>\r\n<p class="gatha2">Dullabho khemakaro suto;</p>\r\n<p class="gatha3">Dullabhā sadisī jāyā,</p>\r\n<p class="gathalast">Dullabho sajano piyo.</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-55-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',41,replace(replace('<p class="gatha1">Selesele <a name="M0.0041"></a> namāṇikkaṃ,</p>\r\n<p class="gatha2">Muttikaṃ na gajegaje;</p>\r\n<p class="gatha3">Sādhavo na hi sabbatra,</p>\r\n<p class="gathalast">Candanaṃ na vanevane.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-56-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',42,replace(replace('<p class="gatha1">Asoco <a name="M0.0042"></a> niddhano pañño,</p>\r\n<p class="gatha2">Asoco paṇḍitabandhavo;</p>\r\n<p class="gatha3">Asocā vidhavā nārī,</p>\r\n<p class="gathalast">Puttanatta patiṭṭhitā.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> Avijjo puriso soco,</p>\r\n<p class="gatha2">Socaṃ methuna mappajaṃ;</p>\r\n<p class="gatha3">Nirāhārā pajā socā,</p>\r\n<p class="gathalast">Socaṃ rajja marājakaṃ.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-57-58-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',43,replace(replace('<p class="gatha1">Kulehi <a name="M0.0043"></a> saha sampakkaṃ,</p>\r\n<p class="gatha2">Paṇḍitehi ca mittataṃ;</p>\r\n<p class="gatha3">Ñātībhi ca samaṃ melaṃ,</p>\r\n<p class="gathalast">Kubbāno navinassati.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-59-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',44,replace(replace('<p class="gatha1">Kaṭṭhā <a name="M0.0044"></a> vutti parādhinā,</p>\r\n<p class="gatha2">Kaṭṭho vāso nirāsayo;</p>\r\n<p class="gatha3">Niddhano byavasāyo ca,</p>\r\n<p class="gathalast">Sabbakaṭṭhā daliddatā.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> Takkarassa kuto dhammo,</p>\r\n<p class="gatha2">Dujjanassa kuto khamā;</p>\r\n<p class="gatha3">Vesiyā ca kuto sneho,</p>\r\n<p class="gathalast">Kuto saccañca kāminaṃ.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-60-61-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',45,replace(replace('<p class="gatha1">Pesitassa <a name="M0.0045"></a> kuto mānaṃ,</p>\r\n<p class="gatha2">Kopanassa kuto sukhaṃ;</p>\r\n<p class="gatha3">Thīnaṃ kuto satittañca,</p>\r\n<p class="gathalast">Kuto mettī khalassa ca.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-62-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',46,replace(replace('<p class="gatha1">Dubbalassa <a name="M0.0046"></a> balaṃ rājā,</p>\r\n<p class="gatha2">Bālānaṃ rodanaṃ balaṃ;</p>\r\n<p class="gatha3">Balaṃmūḷhassa monittaṃ,</p>\r\n<p class="gathalast">Corānaṃ atathaṃ balaṃ.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',47,replace(replace('<p class="gatha1">Yo <a name="M0.0047"></a> dhuvāni pariccajja,</p>\r\n<p class="gatha2">Adhuvaṃ parisevati;</p>\r\n<p class="gatha3">Dhuvāni tassa nassanti,</p>\r\n<p class="gathalast">Adhuvaṃ naṭṭhameva ca.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> Sukkaṃ maṃsaṃ thiyo vuddhā,</p>\r\n<p class="gatha2">Bālakka taruṇaṃ dadhi;</p>\r\n<p class="gatha3">Pabhāte methunaṃ niddā,</p>\r\n<p class="gathalast">Sajju pāṇaharāni cha;</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-64-65-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',48,replace(replace('<p class="gatha1">Sajju <a name="M0.0048"></a> maṃsaṃ navannañca,</p>\r\n<p class="gatha2">Bālā thī khīrabhojanaṃ;</p>\r\n<p class="gatha3">Ghatamuṇhodakañceva,</p>\r\n<p class="gathalast">Sajju pāṇakarāni cha.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-66-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',49,replace(replace('<p class="gatha1">Sīhādekaṃ <a name="M0.0049"></a> bakādekaṃ,</p>\r\n<p class="gatha2">Cha sunā tīṇi gadrabhā;</p>\r\n<p class="gatha3">Vāyasā catu sikkhetha,</p>\r\n<p class="gathalast">Cattāri kukkuṭādapi.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-67-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',50,replace(replace('<p class="gatha1">Pabhūtamappakiccaṃ <a name="M0.0050"></a> vā,</p>\r\n<p class="gatha2">Yonaro kattumicchati;</p>\r\n<p class="gatha3">Saṃyatanena kattabbaṃ,</p>\r\n<p class="gathalast">Sīhādekaṃ pakittitaṃ.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> Sabbindriyāni saṃyama,</p>\r\n<p class="gatha2">Bakova patito jano;</p>\r\n<p class="gatha3">Kāladesopapannāni,</p>\r\n<p class="gathalast">Sabbakiccāni sādhaye.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-68-69-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',51,replace(replace('<p class="gatha1">Bahvāsī <a name="M0.0051"></a> sāppasantuṭṭho,</p>\r\n<p class="gatha2">Suniddo sīghacetano;</p>\r\n<p class="gatha3">Pabhubhatto ca sūro ca,</p>\r\n<p class="gathalast">Ñātabbā cha sunā guṇā.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-70-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',52,replace(replace('<p class="gatha1">Avissāmaṃ <a name="M0.0052"></a> vahe bhāraṃ,</p>\r\n<p class="gatha2">Sītuṇhañca navindati;</p>\r\n<p class="gatha3">Sa santoso tathā niccaṃ,</p>\r\n<p class="gathalast">Tīṇi sikkhetha gadrabhā.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> Guḷhamethunadhammañca,</p>\r\n<p class="gatha2">Kālekāle ca saṅgahaṃ;</p>\r\n<p class="gatha3">Appamādamanālasyaṃ,</p>\r\n<p class="gathalast">Catu sikkhetha vāyasā.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-71-72-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',53,replace(replace('<p class="gatha1">Yuddhañca <a name="M0.0053"></a> pātaruṭṭhānaṃ,</p>\r\n<p class="gatha2">Bhojanaṃ saha bandhuhi;</p>\r\n<p class="gatha3">Thiyaṃ āpadaggataṃ rakkhe,</p>\r\n<p class="gathalast">Catu sikkhetha kukkuṭā.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',54,replace(replace('<p class="gatha1">Kotibhāro <a name="M0.0054"></a> samatthānaṃ,</p>\r\n<p class="gatha2">Kiṃdūraṃ byavasāyinaṃ;</p>\r\n<p class="gatha3">Ko videso savijjānaṃ,</p>\r\n<p class="gathalast">Ko paro piyavādinaṃ.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> Bhayassa kathito pantho,</p>\r\n<p class="gatha2">Indriyānamasaṃyamo;</p>\r\n<p class="gatha3">Tajjayo sampadāmaggo,</p>\r\n<p class="gathalast">Yeniṭṭhaṃ tena gamyate.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-74-75-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',55,replace(replace('<p class="gatha1">Na <a name="M0.0055"></a> ca vijjāsamo bandhu,</p>\r\n<p class="gatha2">Na ca byādhisamo ripu;</p>\r\n<p class="gatha3">Nacāpaccasamo sneho,</p>\r\n<p class="gathalast">Na ca devā paraṃ balaṃ.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-76-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',56,replace(replace('<p class="gatha1">Samuddāvaraṇā <a name="M0.0056"></a> bhūmi,</p>\r\n<p class="gatha2">Pākārāvaraṇaṃ gahaṃ;</p>\r\n<p class="gatha3">Narindāvaraṇā desā,</p>\r\n<p class="gathalast">Cārittāvaraṇā thiyo.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> Ghatakumbhasamā nārī,</p>\r\n<p class="gatha2">Tattaṅgāra samo pumā;</p>\r\n<p class="gatha3">Tasmā ghatañca aggiñca,</p>\r\n<p class="gathalast">Nekatra thapaye budho.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-77-78-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',57,replace(replace('<p class="gatha1">Āhāro <a name="M0.0057"></a> dviguṇo thīnaṃ,</p>\r\n<p class="gatha2">Buddhi tāsaṃ catugguṇo;</p>\r\n<p class="gatha3">Chaguṇo byavasāyo ca,</p>\r\n<p class="gathalast">Kāmocaṭṭhaguṇo mato.</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-79-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',58,replace(replace('<p class="gatha1">Jiṇṇamannaṃ <a name="M0.0058"></a> pasaṃseyya,</p>\r\n<p class="gatha2">Bhariyaṃ gatayobbanaṃ;</p>\r\n<p class="gatha3">Raṇā paccāgataṃ sūraṃ,</p>\r\n<p class="gathalast">Sassañca gehamāgataṃ.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',59,replace(replace('<p class="gatha1">Asantuṭṭhā <a name="M0.0059"></a> dvijā naṭṭhā,</p>\r\n<p class="gatha2">Santuṭṭhāiva pāthivā;</p>\r\n<p class="gatha3">Salajjā gaṇikā naṭṭhā,</p>\r\n<p class="gathalast">Nillajjā ca kulitthiyo.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> Avaṃsapatito rājā,</p>\r\n<p class="gatha2">Mūḷhaputto ca paṇḍito;</p>\r\n<p class="gatha3">Adhanena dhanaṃ pāpya,</p>\r\n<p class="gathalast">Tiṇaṃva maññate janaṃ.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-81-82-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',60,replace(replace('<p class="gatha1">Brahmahāpi <a name="M0.0060"></a> naro pujjo,</p>\r\n<p class="gatha2">Yassatthi vipulaṃ dhanaṃ;</p>\r\n<p class="gatha3">Sasino tulyavaṃsopi,</p>\r\n<p class="gathalast">Niddhano paribhūyate.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-83-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',61,replace(replace('<p class="gatha1">Potthakaṭṭhā <a name="M0.0061"></a> tu yāvijjā,</p>\r\n<p class="gatha2">Parahatthagataṃ dhanaṃ;</p>\r\n<p class="gatha3">Kiccakāle samuppanne,</p>\r\n<p class="gathalast">Na sāvijjā na taddhanaṃ.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> Pādapānaṃ bhayaṃ vātā,</p>\r\n<p class="gatha2">Padmānaṃ sisirā bhayaṃ;</p>\r\n<p class="gatha3">Pabbatānaṃ vajīramhā,</p>\r\n<p class="gathalast">Sādhūnaṃ dujjanā bhayaṃ.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-84-85-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',62,replace(replace('<p class="gatha1">Paññe <a name="M0.0062"></a> niyujjamāne tu,</p>\r\n<p class="gatha2">Santi rañño tayoguṇā;</p>\r\n<p class="gatha3">Yaso sagganivāso ca,</p>\r\n<p class="gathalast">Vipulo ca dhanāgamo.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-86-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',63,replace(replace('<p class="gatha1">Mūḷhe <a name="M0.0063"></a> niyujjamānetu,</p>\r\n<p class="gatha2">Khattiyassāguṇā tayo;</p>\r\n<p class="gatha3">Ayaso catthanāso ca,</p>\r\n<p class="gathalast">Narake gamanaṃ tathā.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> Bahūmūḷhasaṅghātehi,</p>\r\n<p class="gatha2">Aññoññapasuvuttibhi;</p>\r\n<p class="gatha3">Pacchādyante guṇā sabbe,</p>\r\n<p class="gathalast">Meghehiva divākaro.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> Yassa khettaṃ nadītīre,</p>\r\n<p class="gatha2">Bhariyāpi parappiyā;</p>\r\n<p class="gatha3">Puttassa vinayo natthi,</p>\r\n<p class="gathalast">Maccureva nasaṃsayo.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-87-88-89-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',65,replace(replace('<p class="gatha1">Asambhābyaṃ <a name="M0.0065"></a> navattabbaṃ,</p>\r\n<p class="gatha2">Paccakkhamapi dissate;</p>\r\n<p class="gatha3">Silā tarati pānīyaṃ,</p>\r\n<p class="gathalast">Gītaṃ gāyati vānaro.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-90-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',66,replace(replace('<p class="gatha1">Subhikkhaṃ <a name="M0.0066"></a> kasake niccaṃ,</p>\r\n<p class="gatha2">Niccaṃ sukha marogike;</p>\r\n<p class="gatha3">Bhariyā bhattu piyā yassa,</p>\r\n<p class="gathalast">Tassa niccossavaṃ gahaṃ.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> Helassa kammanāsāya,</p>\r\n<p class="gatha2">Buddhināsāya niddhanaṃ;</p>\r\n<p class="gatha3">Yācanā mānanāsāya,</p>\r\n<p class="gathalast">Kulanāsāya bhojanaṃ.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-91-92-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',67,replace(replace('<p class="gatha1">Sevitabbo <a name="M0.0067"></a> mahāvakkho,</p>\r\n<p class="gatha2">Phalacchāyā samanvito;</p>\r\n<p class="gatha3">Yadi devā phalaṃ natthi,</p>\r\n<p class="gathalast">Chāyā kena nivāraye.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-93-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',68,replace(replace('<p class="gatha1">Paṭhame <a name="M0.0068"></a> najjitā vijjā,</p>\r\n<p class="gatha2">Dutīye najjitaṃ dhanaṃ;</p>\r\n<p class="gatha3">Tatīye najjitaṃ puññaṃ,</p>\r\n<p class="gathalast">Catutthe kiṃkarissati.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> Nadīkūleca ye vakkhā,</p>\r\n<p class="gatha2">Parahatthagataṃ dhanaṃ;</p>\r\n<p class="gatha3">Kiccaṃ thīgocaraṃ yassa,</p>\r\n<p class="gathalast">Sabbaṃ taṃ viphalaṃ bhave.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-94-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',69,replace(replace('<p class="gatha1">Kudesamāsajja <a name="M0.0069"></a> kutotthasañcayo,</p>\r\n<p class="gatha2">Kuputtamāsajja kuto jalañjalī;</p>\r\n<p class="gatha3">Kugehiniṃ pāpya gahe kuto sukhaṃ,</p>\r\n<p class="gathalast">Kusissamajjhāpayato kuto yaso.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-96-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',70,replace(replace('<p class="gatha1">Kūpodakaṃ <a name="M0.0070"></a> vaṭacchāyā,</p>\r\n<p class="gatha2">Sāmā thīciṭṭhakālayaṃ;</p>\r\n<p class="gatha3">Sītakāle bhave uṇhaṃ,</p>\r\n<p class="gathalast">Gimhakāle ca sītalaṃ.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-97-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',71,replace(replace('<p class="gatha1">Visaṃ <a name="M0.0071"></a> caṅkamanaṃ rattiṃ,</p>\r\n<p class="gatha2">Visaṃ raññonukulatā;</p>\r\n<p class="gatha3">Visaṃ thīpi aññāsattā,</p>\r\n<p class="gathalast">Visaṃ byādhi avekkhito.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-98-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',72,replace(replace('<p class="gatha1">Duradhītā <a name="M0.0072"></a> visaṃ vijjā,</p>\r\n<p class="gatha2">Ajiṇṇe bhojanaṃ visaṃ;</p>\r\n<p class="gatha3">Visaṃ goṭṭhī daliddassa,</p>\r\n<p class="gathalast">Vuddhassa taruṇī visaṃ.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-99-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',73,replace(replace('<p class="gatha1">Padose <a name="M0.0073"></a> nihato pantho,</p>\r\n<p class="gatha2">Patitā nihatā thiyo;</p>\r\n<p class="gatha3">Appabījaṃ hataṃ khettaṃ,</p>\r\n<p class="gathalast">Bhaccadosā hato pabhū.</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> Hatamasottiyaṃ saddhaṃ,</p>\r\n<p class="gatha2">Hato yañño tvadakkhiṇo;</p>\r\n<p class="gatha3">Hatā rūpavatī vañjhā,</p>\r\n<p class="gathalast">Hataṃ senamanāyakaṃ.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-100-101-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',74,replace(replace('<p class="gatha1">Vedavedaṅga <a name="M0.0074"></a> tattañño,</p>\r\n<p class="gatha2">Japahomaparāyano;</p>\r\n<p class="gatha3">Āsīvādavacoyutto,</p>\r\n<p class="gathalast">Esa rājapurohito.</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> Kulasīlaguṇopeto,</p>\r\n<p class="gatha2">Sabbadhammaparāyano;</p>\r\n<p class="gatha3">Pavīṇo pesanādyakkho,</p>\r\n<p class="gathalast">Dhammādyakkho vidhīyate.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> Ayubbedakatābhyāso,</p>\r\n<p class="gatha2">Sabbesaṃ piyadassano;</p>\r\n<p class="gatha3">Ariyasīlaguṇopeto,</p>\r\n<p class="gathalast">Esa vajjo vidhīyate.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-102-103-104-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',76,replace(replace('<p class="gatha1">Sakiṃdutta <a name="M0.0076"></a> gahitattho,</p>\r\n<p class="gatha2">Lahuhattho jitakkharo;</p>\r\n<p class="gatha3">Sabbasattha samālokī,</p>\r\n<p class="gathalast">Pakaṭṭho nāma lekhako.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> Samattanītisattañño,</p>\r\n<p class="gatha2">Vāhane pūjitassamo;</p>\r\n<p class="gatha3">Sūravīraguṇopeto,</p>\r\n<p class="gathalast">Senādhyakkho vidhīyate.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-105-106-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',78,replace(replace('<p class="gatha1">Sucī <a name="M0.0078"></a> vākyapaṭuppañño,</p>\r\n<p class="gatha2">Paracittopalakkhako;</p>\r\n<p class="gatha3">Dhīro yathāttha vādī ca,</p>\r\n<p class="gathalast">Esa dūto vidhīyate.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> Puttanatta guṇopeto,</p>\r\n<p class="gatha2">Satthañño piṭṭhapācako;</p>\r\n<p class="gatha3">Sūro ca kathinoceva,</p>\r\n<p class="gathalast">Sūpakāro sa uccate.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-107-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',79,replace(replace('<p class="gatha1">Iṅgitā <a name="M0.0079"></a> kāratattañño,</p>\r\n<p class="gatha2">Balavā piyadassano;</p>\r\n<p class="gatha3">Appamādī sadā dakkho,</p>\r\n<p class="gathalast">Patihāro sa uccate.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-109-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1008n',80,replace(replace('<p class="gatha1">Yassa <a name="M0.0080"></a> natthi sayaṃ paññā,</p>\r\n<p class="gatha2">Satthaṃ tassa karoti kiṃ;</p>\r\n<p class="gatha3">Locanehi vihīnassa,</p>\r\n<p class="gathalast">Dappaṇo kiṃkarissati.</p>\r\n<p class="hangnum"><a name="para110"></a><span class="paranum">110</span>.</p>\r\n<p class="gatha1"> Kiṃkarissanti vattāro,</p>\r\n<p class="gatha2">Sotaṃ yattha navijjate;</p>\r\n<p class="gatha3">Naggakapaṇake dese,</p>\r\n<p class="gathalast">Rajaṇo kiṃkarissati.</p>','\r',char(13)),'\n',char(10)),'-110-');