INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="chapter">Suttavandanā</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',13,replace(replace('<p class="gatha1">Dhammattha <a name="M0.0013"></a> desanā vedha,</p>\r\n<p class="gatha2">Catugambhīra duddasaṃ;</p>\r\n<p class="gatha3">Dhammaṃ lokassa desentaṃ,</p>\r\n<p class="gathalast">Dhammarājaṃ namāmahaṃ;</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',14,replace(replace('<p class="gatha1">Buddho <a name="M0.0014"></a> paccekabuddho ca,</p>\r\n<p class="gatha2">Arahā cakkavattīti;</p>\r\n<p class="gatha3">Thūpāthūpārahaṃ vande,</p>\r\n<p class="gathalast">Catuthūpārahesu taṃ;</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',15,replace(replace('<p class="gatha1">Petaseyyo <a name="M0.0015"></a> kāmabhogī,</p>\r\n<p class="gatha2">Sīhaseyyo tathāgato;</p>\r\n<p class="gatha3">Sayantaṃ catuseyyesu,</p>\r\n<p class="gathalast">Catutthena name jinaṃ;</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Aṇḍajā jalābujā ca;</p>\r\n<p class="gatha2">Saṃsedajo papātikā;</p>\r\n<p class="gatha3">Catuyoni pariccheda,</p>\r\n<p class="gathalast">Ñāṇavantaṃ name jinaṃ;</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',16,replace(replace('<p class="gatha1">Sutabuddhonubuddho <a name="M0.0016"></a> ca,</p>\r\n<p class="gatha2">Pacceko jinabuddhoti;</p>\r\n<p class="gatha3">Catutthaṃ catubuddhesu,</p>\r\n<p class="gathalast">Vandāmi lokanāyakaṃ;</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',17,replace(replace('<p class="gatha1">Rāhuggaṃ <a name="M0.0017"></a> attabhāgīnaṃ,</p>\r\n<p class="gatha2">Mandhātā kāmabhogīnaṃ;</p>\r\n<p class="gatha3">Māro adhipateyyānaṃ,</p>\r\n<p class="gatha2">Buddho loke sadevake;</p>\r\n<p class="gatha3">Catuagga paññattīnaṃ,</p>\r\n<p class="gathalast">Aggapattaṃ name jinaṃ.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',18,replace(replace('<p class="gatha1">Dānaṃ <a name="M0.0018"></a> piyakathā attha,</p>\r\n<p class="gatha2">Cariyā sadisattatā;</p>\r\n<p class="gatha3">Catusaṅgaha vatthūhi,</p>\r\n<p class="gatha2">Asaṅkhiyesu jātisu;</p>\r\n<p class="gatha3">Lokassa saṅgahetāraṃ,</p>\r\n<p class="gathalast">Vandāmi lokanāyakaṃ;</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Lobho doso ca mohoti,</p>\r\n<p class="gatha2">Anto paccatthike tayo;</p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',19,replace(replace('<p class="gatha3">Dujjaye <a name="M0.0019"></a> sabbaverīhi,</p>\r\n<p class="gathalast">Sañjitaṃpi sudhiṃ name;</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Jāti puñña mahattāni,</p>\r\n<p class="gatha2">Guṇamahattamuttamaṃ;</p>\r\n<p class="gatha3">Timahatthehi sampannaṃ,</p>\r\n<p class="gathalast">Vandāmi lokanāyakaṃ;</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',20,replace(replace('<p class="gatha1">Hetu <a name="M0.0020"></a> phala sampadāyo,</p>\r\n<p class="gatha2">Sattupakārasampadā;</p>\r\n<p class="gatha3">Tisampadāhi sampannaṃ,</p>\r\n<p class="gathalast">Vandāmi lokanāyakaṃ.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Ñāṇappahānānubhāva,</p>\r\n<p class="gatha2">Surūpakārasampadā;</p>\r\n<p class="gatha3">Catuphala sampadāhi,</p>\r\n<p class="gathalast">Taṃ sampannaṃ name jinaṃ.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',21,replace(replace('<p class="gatha1">Āsayo <a name="M0.0021"></a> ca payogoti,</p>\r\n<p class="gatha2">Sattupakāra sampadā;</p>\r\n<p class="gatha3">Duvidhā yassa sampannā,</p>\r\n<p class="gathalast">Sammā maṃ pātu so jino.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> Mahosadhe vessantare,</p>\r\n<p class="gatha2">Pacchimabhavike tathā;</p>\r\n<p class="gatha3">Tijātīsu name vācaṃ,</p>\r\n<p class="gathalast">Bhāsentaṃ jāyanakkhaṇe.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',22,replace(replace('<p class="gatha1">Nemimhi <a name="M0.0022"></a> sādhine ceva,</p>\r\n<p class="gatha2">Mandhātari ca guttile;</p>\r\n<p class="gatha3">Catūsu manujattena,</p>\r\n<p class="gathalast">Devalokaṃ gataṃ name.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Saṅkhāro rūpavikāro,</p>\r\n<p class="gatha2">Lakkhaṇaṃ vānanissaṭaṃ;</p>','\r',char(13)),'\n',char(10)),'-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',23,replace(replace('<p class="gatha3">Nibbānaṃ <a name="M0.0023"></a> paññatticeti,</p>\r\n<p class="gathalast">Pañca ñeyyantaguṃ name.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> Putta dāra pariccāgā,</p>\r\n<p class="gatha2">Rajjaṅgaṃ jīva cāgīti;</p>\r\n<p class="gatha3">Pañca mahāpariccāge,</p>\r\n<p class="gathalast">Karaṃ sudukkare name.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-16-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',24,replace(replace('<p class="gatha1">Rūpañca <a name="M0.0024"></a> vedanā saññā,</p>\r\n<p class="gatha2">Saṅkhārā viññāṇantime;</p>\r\n<p class="gatha3">Pañcakkhandhe parijānaṃ,</p>\r\n<p class="gathalast">Tipariññāhi taṃ name.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> Manussa deva gatiyo,</p>\r\n<p class="gatha2">Peto ca nirayo tathā;</p>\r\n<p class="gatha3">Tiracchānoti pañcetā,</p>\r\n<p class="gathalast">Chindantaṃ gatiyo name;</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',25,replace(replace('<p class="gatha1">Devaputto <a name="M0.0025"></a> kileso ca,</p>\r\n<p class="gatha2">Abhisaṅkhāra mārako;</p>\r\n<p class="gatha3">Khandho maccūti pañcete,</p>\r\n<p class="gathalast">Māre vijitavaṃ name.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> Purebhattaṃ pacchābhattaṃ,</p>\r\n<p class="gatha2">Purimañceva majjhimaṃ;</p>\r\n<p class="gatha3">Pacchimanti sadā pañca,</p>\r\n<p class="gathalast">Buddhakiccaṃ karaṃ name.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',26,replace(replace('<p class="gatha1">Rūpā <a name="M0.0026"></a> saddā gandhā rasā,</p>\r\n<p class="gatha2">Phoṭṭhabbā ca manoramā;</p>\r\n<p class="gatha3">Pañca kāmaguṇe hitvā,</p>\r\n<p class="gathalast">Kheḷaṃva niggataṃ name.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',27,replace(replace('<p class="gatha1">Rāgasallaṃ <a name="M0.0027"></a> dosasallaṃ,</p>\r\n<p class="gatha2">Mohā māno ca diṭṭhīti;</p>\r\n<p class="gatha3">Vande vajjātivajjantaṃ,</p>\r\n<p class="gathalast">Pañcasallapanūdanaṃ.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> Dīgho majjhima saṃyuttā,</p>\r\n<p class="gatha2">Aṅguttaro ca khuddako;</p>\r\n<p class="gatha3">Iti pañca nikāyehi,</p>\r\n<p class="gathalast">Suvinentaṃ pajaṃ name.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-23-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',28,replace(replace('<p class="gatha1">Issariyaṃ <a name="M0.0028"></a> yaso dhammo,</p>\r\n<p class="gatha2">Kāmo sirī payattīti;</p>\r\n<p class="gatha3">Bhagyehi chahi sampannaṃ,</p>\r\n<p class="gathalast">Vandāmi lokanāyakaṃ.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',29,replace(replace('<p class="gatha1">Piyāpiyaṃ <a name="M0.0029"></a> bhūtābhūtaṃ,</p>\r\n<p class="gatha2">Atthānatthaṃ yathārahaṃ;</p>\r\n<p class="gatha3">Missā chasu suvācaṃva,</p>\r\n<p class="gathalast">Bhāsantaṃ atthakaṃ name.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Alaso ca pamādo ca,</p>\r\n<p class="gatha2">Anuṭṭhānaṃ asaṃyamo;</p>\r\n<p class="gatha3">Niddā tantīti chiddehi,</p>\r\n<p class="gathalast">Chahi muttaṃ sadā name.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-26-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',30,replace(replace('<p class="gatha1">Mahākaruṇāsamāpatti<a name="M0.0030"></a>,</p>\r\n<p class="gatha2">Yamakappāṭihāriyaṃ;</p>\r\n<p class="gatha3">Āsayānusaye ñāṇaṃ,</p>\r\n<p class="gatha2">Indriyāna paropare;</p>\r\n<p class="gatha3">Sabbaññutā nāvaraṇaṃ,</p>\r\n<p class="gathalast">Chā sādhāraṇikaṃ name.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',31,replace(replace('<p class="gatha1">Kāmacchando <a name="M0.0031"></a> ca byāpādo,</p>\r\n<p class="gatha2">Thinamiddhañca saṃsayo;</p>\r\n<p class="gatha3">Avijjuddhacca kukkuccaṃ,</p>\r\n<p class="gathalast">Chavinīvaraṇaṃ name.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> Iddhividhaṃ dibbasotaṃ,</p>\r\n<p class="gatha2">Paracitta vijānanā;</p>','\r',char(13)),'\n',char(10)),'-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',32,replace(replace('<p class="gatha3">Pubbenivāsānussati<a name="M0.0032"></a>,</p>\r\n<p class="gatha2">Dibbacakkhāsavakkhayo;</p>\r\n<p class="gatha3">Chaḷabhiññāhi sampannaṃ,</p>\r\n<p class="gathalast">Vandāmi purisuttamaṃ.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> Nīla pītā ca odātā,</p>\r\n<p class="gatha2">Mañjiṭṭhā ca pabhassarā;</p>\r\n<p class="gatha3">Lohitāti cha raṃsīhī,</p>\r\n<p class="gathalast">Vijjotantaṃ sadā name.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',33,replace(replace('<p class="gatha1">Assādādīnavo <a name="M0.0033"></a> ceva,</p>\r\n<p class="gatha2">Nissaraṇaphalaṃ tathā;</p>\r\n<p class="gatha3">Upāyāṇattīti chadhā,</p>\r\n<p class="gathalast">Saddhammadesakaṃ name.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',34,replace(replace('<p class="gatha1">Rāgo <a name="M0.0034"></a> doso ca moho ca,</p>\r\n<p class="gatha2">Vitakkacariyā tathā;</p>\r\n<p class="gatha3">Saddhā buddhīti bhājentaṃ,</p>\r\n<p class="gathalast">Tā chaḷecariyā name.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> Rajataṃ kanakaṃ muttā,</p>\r\n<p class="gatha2">Maṇi veḷuriyāni ca;</p>\r\n<p class="gatha3">Vajirañca pavāḷanti,</p>\r\n<p class="gathalast">Seṭṭhi sattadhano viya.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> Saddhāsīlaṃ sutaṃcāgo,</p>\r\n<p class="gatha2">Hirī ottappiyaṃ dhanaṃ,</p>\r\n<p class="gatha3">Paññādhananti sambuddhaṃ,</p>\r\n<p class="gathalast">Vande sattamahādhanaṃ.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-34-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',35,replace(replace('<p class="gatha1">Cakkaṃ <a name="M0.0035"></a> hatthi asso maṇi,</p>\r\n<p class="gatha2">Gahapatitthiyo tathā;</p>\r\n<p class="gatha3">Supariṇāyakocāti,</p>\r\n<p class="gathalast">Sattaratano cakkavattīva.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> Sati dhammavicayo ca,</p>\r\n<p class="gatha2">Tatheva vīriyaṃ pīti;</p>','\r',char(13)),'\n',char(10)),'-36-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',36,replace(replace('<p class="gatha3">Passaddhi <a name="M0.0036"></a> samādhupekkhā,</p>\r\n<p class="gatha2">Saddhamma cakkavattikaṃ;</p>\r\n<p class="gatha3">Satta bojjhaṅgaratanaṃ,</p>\r\n<p class="gathalast">Namāmi purisuttamaṃ.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',37,replace(replace('<p class="gatha1">Yugandharo <a name="M0.0037"></a> īsadharo,</p>\r\n<p class="gatha2">Karavīko sudassano;</p>\r\n<p class="gatha3">Nemindharo vinatako,</p>\r\n<p class="gatha2">Assakaṇṇo mahānage;</p>\r\n<p class="gatha3">Sattete hattharūpaṃva,</p>\r\n<p class="gathalast">Sampassantaṃ jinaṃ name.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> Kaṇṇamuṇḍo anotatto,</p>\r\n<p class="gatha2">Kuṇālo rathakārako;</p>\r\n<p class="gatha3">Sīhappapāta chaddantā,</p>\r\n<p class="gatha2">Mandākinī mahāsare;</p>','\r',char(13)),'\n',char(10)),'-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',38,replace(replace('<p class="gatha3">Sattete <a name="M0.0038"></a> sabbathā passaṃ,</p>\r\n<p class="gathalast">Diṭṭheva pātikaṃ name.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> Manussattaṃ liṅgasampatti,</p>\r\n<p class="gatha2">Hetu satthāradassanaṃ;</p>\r\n<p class="gatha3">Pabbajjā guṇasampatti,</p>\r\n<p class="gatha2">Adhikāro ca chandatā;</p>\r\n<p class="gatha3">Aṭṭhadhamma samodhānā,</p>\r\n<p class="gathalast">Bhinīhārantaguṃ name.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',39,replace(replace('<p class="gatha1">Visaṭṭhaṃ <a name="M0.0039"></a> mañju viññeyyaṃ,</p>\r\n<p class="gatha2">Savanīyāvisāriṇā;</p>\r\n<p class="gatha3">Gambhīro binduninnādo,</p>\r\n<p class="gathalast">Aṭṭhaṅgikasaraṃ name.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',40,replace(replace('<p class="gatha1">Cattāri <a name="M0.0040"></a> rūpajhānāni,</p>\r\n<p class="gatha2">Tathevārūpajhānāni;</p>\r\n<p class="gatha3">Tā samāpattiyo aṭṭha,</p>\r\n<p class="gathalast">Sevantaṃ ruciyā name.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> Aṇimā laṅghimā kammaṃ,</p>\r\n<p class="gatha2">Mahimā pattimā tathā;</p>','\r',char(13)),'\n',char(10)),'-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',41,replace(replace('<p class="gatha3">Īsitā <a name="M0.0041"></a> vasitā ceva,</p>\r\n<p class="gatha2">Yatthakāmāvasāyitā;</p>\r\n<p class="gatha3">Aṭṭhissariya puṇṇattā,</p>\r\n<p class="gathalast">Issarātissaraṃ name.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',42,replace(replace('<p class="gatha1">Manomayiddhiñāṇañca<a name="M0.0042"></a>,</p>\r\n<p class="gatha2">Chaḷabhiññā vipassanā;</p>\r\n<p class="gatha3">Aṭṭhavijjāhi sampannaṃ,</p>\r\n<p class="gathalast">Vande tilokaketukaṃ.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> Jāti jarā rujā kālo,</p>\r\n<p class="gatha2">Caturāpāyadukkhāti;</p>\r\n<p class="gatha3">Aṭṭha saṃvegavatthūni,</p>\r\n<p class="gathalast">Bhāsantaṃ vividhaṃ name.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-44-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',43,replace(replace('<p class="gatha1">Suttaṅgaṃ <a name="M0.0043"></a> veyyākaraṇaṃ,</p>\r\n<p class="gatha2">Geyyaṃ gāthā ca jātakaṃ;</p>\r\n<p class="gatha3">Abbhūtadhamma vedallā,</p>\r\n<p class="gatha2">Udānamiti vuttakaṃ;</p>\r\n<p class="gatha3">Navaṅgaṃ sabbalokassa,</p>\r\n<p class="gathalast">Dadantaṃ sāsanaṃ name.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',44,replace(replace('<p class="gatha1">Yā <a name="M0.0044"></a> samāpattiyo aṭṭha,</p>\r\n<p class="gatha2">Nirodho cāti te nava,</p>\r\n<p class="gatha3">Anupubbavihāre taṃ,</p>\r\n<p class="gathalast">Sevantaṃ ruciyā name.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> Ekattañceva nānattaṃ,</p>\r\n<p class="gatha2">Kāyasaññīhi yogato,</p>\r\n<p class="gatha3">Cattāro tathāruppā ca,</p>\r\n<p class="gatha2">Asaññīcāti te nava,</p>\r\n<p class="gatha3">Sattavāse vibhajitvā,</p>\r\n<p class="gathalast">Pakāsentaṃ name jinaṃ.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-47-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',45,replace(replace('<p class="gatha1">Seyyassa <a name="M0.0045"></a> sadiso seyyo,</p>\r\n<p class="gatha2">Hīnoti tividho vidho,</p>\r\n<p class="gatha3">Tathā sadisahīnānaṃ,</p>\r\n<p class="gathalast">Navavidhe nudaṃ name.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-49-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',46,replace(replace('<p class="gatha1">Anatthaṃ <a name="M0.0046"></a> me carati ca,</p>\r\n<p class="gatha2">Tathā cari carissati,</p>\r\n<p class="gatha3">Mittassa arinotvatthaṃ,</p>\r\n<p class="gathalast">Navāghāte jahaṃ name.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> Dānaṃ sīlañca nikkhamaṃ,</p>\r\n<p class="gatha2">Paññā vīriyapañcamaṃ,</p>','\r',char(13)),'\n',char(10)),'-50-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',47,replace(replace('<p class="gatha3">Khantī <a name="M0.0047"></a> saccamadhiṭṭhānaṃ,</p>\r\n<p class="gatha2">Mettupekkhāti tādasa,</p>\r\n<p class="gatha3">Pūretvā pāramī vande,</p>\r\n<p class="gathalast">Sammāsambodhimajjhavaṃ;</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> Kāḷāvakañca gaṅgeyyaṃ,</p>\r\n<p class="gatha2">Paṇḍaraṃ tamba piṅgalaṃ,</p>\r\n<p class="gatha3">Gandha maṅgala hemañca,</p>\r\n<p class="gathalast">Uposatha chaddantime.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> Dhāreti dasaposānaṃ,</p>\r\n<p class="gatha2">Nāgo kāḷāvako balaṃ;</p>','\r',char(13)),'\n',char(10)),'-51-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',48,replace(replace('<p class="gatha3">Sahassakoṭiposānaṃ<a name="M0.0048"></a>;</p>\r\n<p class="gathalast">Chaddantovāranuttamo.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> Dasa sahassakoṭīnaṃ,</p>\r\n<p class="gatha2">Posānaṃ baladhāraṇaṃ;</p>\r\n<p class="gatha3">Chaddantānaṃ dasannañca,</p>\r\n<p class="gathalast">Dasakāyabalaṃ name.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-53-54-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',49,replace(replace('<p class="gatha1">Ṭhānāṭhāne <a name="M0.0049"></a> vipāke ca,</p>\r\n<p class="gatha2">Magge sabbatthagāminaṃ;</p>\r\n<p class="gatha3">Nānādhātūsu lokesu,</p>\r\n<p class="gathalast">Adhimuttimhi pāṇinaṃ;</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> Paropariyatte ñāṇaṃ,</p>\r\n<p class="gatha2">Indriyānañca pāṇinaṃ;</p>\r\n<p class="gatha3">Jhānādīsu ñāṇaṃ pubbe,</p>\r\n<p class="gatha2">Nivāse dibbacakkhu ca;</p>\r\n<p class="gatha3">Āsavakkhayañāṇanti,</p>\r\n<p class="gathalast">Dasabalañāṇaṃ name.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-55-56-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',50,replace(replace('<p class="gatha1">Mūlagandho <a name="M0.0050"></a> sāro pheggu,</p>\r\n<p class="gatha2">Taco papaṭiko raso;</p>\r\n<p class="gatha3">Pattaṃ pupphaṃ phalañceva,</p>\r\n<p class="gatha2">Gandhagandhoti saṃvaraṃ;</p>\r\n<p class="gatha3">Sugandhaṃ dasagandhehi,</p>\r\n<p class="gathalast">Vilimpantaṃ sadā name.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-57-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',51,replace(replace('<p class="gatha1">Samādhi <a name="M0.0051"></a> ñāṇavipphārā,</p>\r\n<p class="gatha2">Adhiṭṭhānaṃ vikubbanā;</p>\r\n<p class="gatha3">Manomayāriyā iddhi,</p>\r\n<p class="gathalast">Tathā kammavipākajā.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> Vijjāmayā puññavato;</p>\r\n<p class="gatha2">Payoga paccayiddhiti;</p>\r\n<p class="gatha3">Aggapatta dasiddhīnaṃ,</p>\r\n<p class="gathalast">Pāṭiheraṃ karaṃ name.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-58-59-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',52,replace(replace('<p class="gatha1">Muttā <a name="M0.0052"></a> maṇi veḷuriyaṃ,</p>\r\n<p class="gatha2">Saṅkho sallo pavāḷakaṃ;</p>\r\n<p class="gatha3">Suvaṇṇaṃ rajataṃ lohi,</p>\r\n<p class="gatha2">Taṅko masāragallanti;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',53,replace(replace('<p class="gatha3">Dasadhā <a name="M0.0053"></a> ratanehiggaṃ,</p>\r\n<p class="gathalast">Bhogiṃ paññāmaṇiṃ name.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> Sithilaṃ dhanitaṃ dīghaṃ,</p>\r\n<p class="gatha2">Rassañca garukaṃ lahu;</p>\r\n<p class="gatha3">Niggahitaṃ vimuttañca,</p>\r\n<p class="gatha2">Sambandhañca vavatthitaṃ;</p>\r\n<p class="gatha3">Vivarantaṃ name dhammaṃ,</p>\r\n<p class="gathalast">Dasabyañjanabheditaṃ.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-60-61-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',54,replace(replace('<p class="gatha1">Pasādojo <a name="M0.0054"></a> madhuratā,</p>\r\n<p class="gatha2">Samatā sukhumālatā;</p>\r\n<p class="gatha3">Silesodāratā kanti,</p>\r\n<p class="gatha2">Atthabyatti samādhayo;</p>\r\n<p class="gatha3">Dasa saddaguṇopetaṃ,</p>\r\n<p class="gathalast">Dhammaṃ pātu karaṃ name.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-62-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',55,replace(replace('<p class="gatha1">Lobho <a name="M0.0055"></a> doso moho māno,</p>\r\n<p class="gatha2">Uddhaccaṃ diṭṭhi saṃsayo;</p>\r\n<p class="gatha3">Ahirikamanottappaṃ,</p>\r\n<p class="gatha2">Thinanti sabba dāhake;</p>\r\n<p class="gatha3">Kilese dasa te saddhiṃ,</p>\r\n<p class="gathalast">Vāsanāya jahaṃ name.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',56,replace(replace('<p class="gatha1">Saterā <a name="M0.0056"></a> daṇḍamaṇikā,</p>\r\n<p class="gatha2">Macchavilolagaggarā;</p>\r\n<p class="gatha3">Sukkhāsani kapisisā,</p>\r\n<p class="gatha2">Vicakkā sañña kukkuṭā;</p>\r\n<p class="gatha3">Navāsanīhi phālentaṃ,</p>\r\n<p class="gathalast">Tibbaṃ paññāsaniṃ name.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-64-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',57,replace(replace('<p class="gatha1">Rodanā <a name="M0.0057"></a> kodhāvudho ca,</p>\r\n<p class="gatha2">Ujjhantissariyaṃ tathā;</p>\r\n<p class="gatha3">Paṭisaṅkhānaṃ,</p>\r\n<p class="gatha2">Khanti aṭṭhabalesu taṃ;</p>\r\n<p class="gatha3">Pathavīsadisaṃ vande,</p>\r\n<p class="gathalast">Aṭṭhamena vināyakaṃ.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-65-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',58,replace(replace('<p class="gatha1">Kāmarogo <a name="M0.0058"></a> ca paṭigho,</p>\r\n<p class="gatha2">Māno diṭṭhi ca saṃsayo;</p>\r\n<p class="gatha3">Bhavarāgo avijjāti,</p>\r\n<p class="gathalast">Sattānusayinaṃ name.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> Titthiyāpakatimahāsāvakā,</p>\r\n<p class="gatha2">Aggasāvakā paccekabuddhasambuddhā;</p>','\r',char(13)),'\n',char(10)),'-66-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',59,replace(replace('<p class="gatha3">Visiṭṭhaṃ <a name="M0.0059"></a> sabbathā chasu,</p>\r\n<p class="gatha2">Pubbe nivāsa viññūsu;</p>\r\n<p class="gathalast">Tilokamakuṭaṃ name.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> Buddhacakkhu samantā ca,</p>\r\n<p class="gatha2">Ñāṇaṃ yaṃ dibbacakkhu ca;</p>\r\n<p class="gatha3">Dhammoti pañca cakkhūhi,</p>\r\n<p class="gathalast">Dassāviṃ mārajiṃ name.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-67-68-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',60,replace(replace('<p class="gatha1">Khuddakā <a name="M0.0060"></a> khaṇikā okkantikā ca,</p>\r\n<p class="gatha2">Pharaṇā tathā ubbegā;</p>\r\n<p class="gatha3">Pañca pītīhi pinentaṃ,</p>\r\n<p class="gathalast">Ruciyā ca namāmahaṃ.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> Dukkhaṃ samudayasaccaṃ,</p>\r\n<p class="gatha2">Nirodho maggasaccakaṃ;</p>','\r',char(13)),'\n',char(10)),'-69-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',61,replace(replace('<p class="gatha3">Catusaccamabhijānaṃ<a name="M0.0061"></a>,</p>\r\n<p class="gathalast">Catukiccehi taṃ name.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> Pīḷanā saṅkhatattho ca,</p>\r\n<p class="gatha2">Tapo vipariṇāmanā;</p>\r\n<p class="gatha3">Dukkhasaccaṃ catukkehi,</p>\r\n<p class="gathalast">Vibhattāviṃ muniṃ name.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-70-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',62,replace(replace('<p class="gatha1">Āyūhanaṃ <a name="M0.0062"></a> nidānañca,</p>\r\n<p class="gatha2">Saṃyogo palibodhanaṃ;</p>\r\n<p class="gatha3">Catukkehi samudayaṃ,</p>\r\n<p class="gathalast">Vibhattāviṃ muniṃ name.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> Nissaraṇañca viveko,</p>\r\n<p class="gatha2">Asaṅkhatāmataṃ tathā;</p>\r\n<p class="gatha3">Nirodhañca catukkehi,</p>\r\n<p class="gathalast">Vibhattāviṃ muniṃ name.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-72-73-');
