INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',64,replace(replace('<p class="gatha1">Satthako <a name="M0.0064"></a> ca asammoho,</p>\r\n<p class="gatha2">Sappāyo ceva gocaro;</p>\r\n<p class="gatha3">Catusampajaññā vinā,</p>\r\n<p class="gathalast">Bhāviṃ name tathāgataṃ.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> Pahinākhila dukkhāhaṃ,</p>\r\n<p class="gatha2">Bhavasāgara pāraguṃ;</p>\r\n<p class="gatha3">Vande sāraguṇopetaṃ,</p>\r\n<p class="gathalast">Tenamhi bhavapārago.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-75-76-');
