INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',67,replace(replace('<p class="gatha1">Ñāṇa <a name="M0.0067"></a> ñāṇa vilāsagga,</p>\r\n<p class="gatha2">Ñāṇācinteyya saṃyuttaṃ;</p>\r\n<p class="gatha3">Vande sāraguṇopetaṃ,</p>\r\n<p class="gathalast">Tenamhā tulañāṇavā.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> Sajjhaṃ hemañca ratanaṃ,</p>\r\n<p class="gatha2">Gehaṃ vatthañca bhojanaṃ;</p>','\r',char(13)),'\n',char(10)),'-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',68,replace(replace('<p class="gatha3">Tadaññepi <a name="M0.0068"></a> yathācittaṃ,</p>\r\n<p class="gathalast">Māpeyyāhaṃ name jinaṃ.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> Sajjhaṃ hemañca ratanaṃ,</p>\r\n<p class="gatha2">Gehaṃ vatthañca bhojanaṃ;</p>\r\n<p class="gatha3">Tadaññepi yathācittaṃ,</p>\r\n<p class="gathalast">Māpeyyaṃ kammajiddhiyā.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-81-82-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',69,replace(replace('<p class="gatha1">Sattabojjhaṅga <a name="M0.0069"></a> ratano,</p>\r\n<p class="gatha2">Saddhādiratano muni;</p>\r\n<p class="gatha3">Satippabhutiratano,</p>\r\n<p class="gathalast">Vande taṃ purisuttamaṃ.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> Cakkādisattaratanaṃ,</p>\r\n<p class="gatha2">Muttādiratanaṃ subhaṃ;</p>\r\n<p class="gatha3">Vatthappabhutiratanaṃ,</p>\r\n<p class="gathalast">Chandakkhaṇe labhāmahaṃ.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-83-84-');
