INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',71,replace(replace('<p class="gatha1">Sampuṇṇacittasaṅkappo<a name="M0.0071"></a>,</p>\r\n<p class="gatha2">Yathākaṅkhitamāpako,</p>\r\n<p class="gatha3">Kenacinabhibhūtamhi,</p>\r\n<p class="gathalast">Sabbābhibhū bhave bhave.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> Dasa chaddantarājāva,</p>\r\n<p class="gatha2">Dasa kāyabalo muni;</p>\r\n<p class="gatha3">Dasa ñāṇabbalotulyo,</p>\r\n<p class="gathalast">Vande taṃ samaṇuttamaṃ.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-86-87-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',72,replace(replace('<p class="gatha1">Atikāyajavo <a name="M0.0072"></a> buddho,</p>\r\n<p class="gatha2">Raññājavanahaṃsato;</p>\r\n<p class="gatha3">Kā kathā ñāṇavegassa,</p>\r\n<p class="gathalast">Vande taṃ samaṇuttamaṃ.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> Chaddantanāgarājāva,</p>\r\n<p class="gatha2">Bhave bhave mahabbalo;</p>\r\n<p class="gatha3">Rājājavanahaṃsova,</p>\r\n<p class="gathalast">Paramaggajavo bhave.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-88-89-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',73,replace(replace('<p class="gatha1">Udakā <a name="M0.0073"></a> kāsacārī ca,</p>\r\n<p class="gatha2">Mahinimujjako jino;</p>\r\n<p class="gatha3">Māpako ca yathā cittaṃ,</p>\r\n<p class="gathalast">Vande taṃ ñeyyapāraguṃ.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> Udakā kāsacārī ca,</p>\r\n<p class="gatha2">Mahinimujjako bhave;</p>\r\n<p class="gatha3">Māpako ca yathācittaṃ,</p>\r\n<p class="gathalast">Bhavaso kammajiddhiyā.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-90-91-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',74,replace(replace('<p class="gatha1">Gāme <a name="M0.0074"></a> vane ca sabbattha,</p>\r\n<p class="gatha2">Devehi manussehi ca;</p>\r\n<p class="gatha3">Ābhatānantalābhassa,</p>\r\n<p class="gatha2">Sadā yassa mahesino;</p>\r\n<p class="gatha3">Pattalābhaggataṃ lokaṃ,</p>\r\n<p class="gathalast">Taṃ vande munipuṅgavaṃ.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-92-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',75,replace(replace('<p class="gatha1">Gāme <a name="M0.0075"></a> vane ca sabbattha,</p>\r\n<p class="gatha2">Devehi manussehi ca;</p>\r\n<p class="gatha3">Ābhatā nantalābho me,</p>\r\n<p class="gathalast">Sadā hotu bhavābhave.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> Puññassimassa tejena,</p>\r\n<p class="gatha2">Yathā cittaṃ samijjhatu;</p>','\r',char(13)),'\n',char(10)),'-93-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',76,replace(replace('<p class="gatha3">Sabbicchā <a name="M0.0076"></a> sabbacintā ca,</p>\r\n<p class="gathalast">Khippaṃ me jātijātiyaṃ.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> Natthīti vacanaṃ dukkhaṃ,</p>\r\n<p class="gatha2">Dehīti vacanaṃ tathā;</p>\r\n<p class="gatha3">Tasmā natthīti dehīti,</p>\r\n<p class="gathalast">Mā me hotu bhavābhave.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-94-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',77,replace(replace('<p class="gatha1">Sabbaṃ <a name="M0.0077"></a> paravasaṃ dukkhaṃ,</p>\r\n<p class="gatha2">Sabbamissariyaṃ sukhaṃ;</p>\r\n<p class="gatha3">Sabbaṃ paravasamatthu,</p>\r\n<p class="gathalast">Sabbamissariyaṃ bhave.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> Vitakkento bhajjakāyo,</p>\r\n<p class="gatha2">Sabbāvudha vāraṇo ca;</p>\r\n<p class="gatha3">Chaddanta vāraṇabalo,</p>\r\n<p class="gathalast">Bhaveyyaṃ jātijā tiyaṃ.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-96-97-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',78,replace(replace('<p class="gatha1">Subhalakkhaṇasampanno<a name="M0.0078"></a>,</p>\r\n<p class="gatha2">Suvaṇṇavaṇṇavā bhave;</p>\r\n<p class="gatha3">Brahmassaro karavika,</p>\r\n<p class="gathalast">Bhāṇī ca jātijātiyaṃ.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> Bhūrimapañño sippānaṃ,</p>\r\n<p class="gatha2">Sabbesaṃ kusalo bhave;</p>\r\n<p class="gatha3">Visajjetuṃ samatthova,</p>\r\n<p class="gathalast">Sabbapucchānaṃ ṭhānaso.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-98-99-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',79,replace(replace('<p class="gatha1">Verādhaṃsīya <a name="M0.0079"></a> bhogā ca,</p>\r\n<p class="gatha2">Anantākhīṇa bhogavā;</p>\r\n<p class="gatha3">Anantābhajja pariso,</p>\r\n<p class="gathalast">Bhavaso pāpuṇe sivaṃ.</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-100-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',80,replace(replace('<p class="gatha1">Mā <a name="M0.0080"></a> nasseyyaṃ pasayheyyaṃ,</p>\r\n<p class="gatha2">Ussukkehi pyahaṃ saha;</p>\r\n<p class="gatha3">Pañcaverehi koṭīhi,</p>\r\n<p class="gathalast">Bhaṇḍaṃ vā akātukāmitaṃ.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> Meghaṃ vātañca ratanaṃ,</p>\r\n<p class="gatha2">Dhaññaṃ vatthañca bhojanaṃ;</p>\r\n<p class="gatha3">Sabbicchitaṃ tadaññampi,</p>\r\n<p class="gathalast">Māpeyyaṃ kammajiddhiyā.</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-101-102-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',81,replace(replace('<p class="gatha1">Puññenetena <a name="M0.0081"></a> nibbānaṃ,</p>\r\n<p class="gatha2">Santaṃ pappomi tāvatā;</p>\r\n<p class="gatha3">Bhaveyyaṃ sabbajātīsu,</p>\r\n<p class="gatha2">Catusampattiyā sadā;</p>\r\n<p class="gatha3">Catucakkena sampanno,</p>\r\n<p class="gathalast">Saddhammehi ca sattahi.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-103-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',82,replace(replace('<p class="gatha1">Sammādiṭṭhi <a name="M0.0082"></a> vasupete,</p>\r\n<p class="gatha2">Kulamhi seṭṭhasammate,</p>\r\n<p class="gatha3">Sabbasakkata saṃsuddhe;</p>\r\n<p class="gathalast">Bhave tihetusandhiko.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> Ghāsacchādanaṃ bhogañca,</p>\r\n<p class="gatha2">Neva hatthena kātuna,</p>\r\n<p class="gatha3">Bhuñjeyyamiddhiyātveva,</p>\r\n<p class="gathalast">Māpetvā yāvadatthakaṃ.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-104-105-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',83,replace(replace('<p class="gatha1">Manussānaṃ <a name="M0.0083"></a> atikkamma,</p>\r\n<p class="gatha2">Devānaṃ viya bhojanaṃ,</p>\r\n<p class="gatha3">Vatthaṃ bhogo ca me hontu,</p>\r\n<p class="gathalast">Parivārātisundarā.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> Dasāsuttaṃpi me bhogaṃ,</p>\r\n<p class="gatha2">Pañcaverā bhave bhave;</p>\r\n<p class="gatha3">Māgaṇheyyuñca nasseyyuṃ,</p>\r\n<p class="gathalast">Asoko bhogahetume.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-106-107-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',84,replace(replace('<p class="gatha1">Sabbaṅgasubha <a name="M0.0084"></a> sampanno,</p>\r\n<p class="gatha2">Siṅgīnikkha savaṇṇa vā;</p>\r\n<p class="gatha3">Bāttiṃsa lakkhaṇūpeto,</p>\r\n<p class="gathalast">Atappana rūpa vā bhave.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> Kāyo candanagandho ca,</p>\r\n<p class="gatha2">Mukhaṃ uppalagandhikaṃ;</p>','\r',char(13)),'\n',char(10)),'-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',85,replace(replace('<p class="gatha3">Aṭṭhaṅgiko <a name="M0.0085"></a> karavika,</p>\r\n<p class="gathalast">Mañjūghoso ca me hotu.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> Tikkha gambhīra pañño ca,</p>\r\n<p class="gatha2">Hāsātijavapaññavā,</p>\r\n<p class="gatha3">Bhūri nibbedha pañño ca;</p>\r\n<p class="gathalast">Sabbapañhavisajjano.</p>\r\n<p class="hangnum"><a name="para111"></a><span class="paranum">111</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-109-111-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_74',86,replace(replace('<p class="gatha1">Anto <a name="M0.0086"></a> soḷasavassassa,</p>\r\n<p class="gatha2">Tipeṭakadharo bhave;</p>\r\n<p class="gatha3">Sabbakammesu sippesu,</p>\r\n<p class="gathalast">Vijjāṭhānesu pāragū.</p>','\r',char(13)),'\n',char(10)),'');