INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_58',1,replace(replace('<p class="centered">Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="chapter">Buddhaguṇagāthāvalī</p>\r\n<p class="gatha1">Yatheva puppharāsimhā, gantheyya mālā sobhanā;</p>\r\n<p class="gathalast">Evaṃ buddhaguṇagāthā, viraciṃ sahassādhikā.</p>\r\n<p class="gatha1">Sopi bhagavā arahaṃ, sammāsambuddho sayambhū;</p>\r\n<p class="gathalast">Vijjācaraṇasampanno, buddhaṃ taṃ paṇamāmyahaṃ.1.</p>\r\n<p class="gatha1">Purisadammasārathī, lokavidū anuttaro;</p>\r\n<p class="gathalast">Satthādevamanussānaṃ, buddhaṃ taṃ paṇamāmyahaṃ.2.</p>\r\n<p class="gatha1">Sugato sugado sāmī, sukhado santipadāyako;</p>\r\n<p class="gathalast">Sabbalokānukampako, buddhaṃ taṃ paṇamāmyahaṃ.3.</p>\r\n<p class="gatha1">Varo varaññū varado, varuttamo varāharo;</p>\r\n<p class="gathalast">Varadhammaṃ adesayi, buddhaṃ taṃ paṇamāmyahaṃ.4.</p>\r\n<p class="gatha1">Mahāmaṅgalamaṅgalyo, maṅgalo maṅgalālayo;</p>\r\n<p class="gathalast">Maṅgalāyatano nātho, buddhaṃ taṃ paṇamāmyahaṃ.5.</p>\r\n<p class="gatha1">Maṅgalindo maṅgaliko, mahāmaṅgalanāyako;</p>\r\n<p class="gathalast">Maṅgaladhammaṃ desesi, buddhaṃ taṃ paṇamāmyahaṃ.6.</p>\r\n<p class="gatha1">Maṅgalaggo maṅgalaññū, maṅgalatthapadāyako;</p>\r\n<p class="gathalast">Maṅgalapanthadassāvī, buddhaṃ taṃ paṇamāmyahaṃ.7.</p>\r\n<p class="gatha1">Maṅgaliccho maṅgaliddho, maṅgalamabhivaddhano;</p>\r\n<p class="gathalast">Maṅgalehi paripuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.8.</p>\r\n<p class="gatha1">Maṅgalābhā ujjotesi, maṅgalatthapabhākaro;</p>\r\n<p class="gathalast">Maṅgalālokamaṇḍito, buddhaṃ taṃ paṇamāmyahaṃ.9.</p>\r\n<p class="gatha1">Maṅgalatthamanuppatto, maṅgalatthaparāyaṇo;</p>\r\n<p class="gathalast">Sabbadā maṅgalakaro, buddhaṃ taṃ paṇamāmyahaṃ.10.</p>\r\n<p class="gatha1">Maṅgalamaggamanvesi, patto maṅgalamuttamaṃ;</p>\r\n<p class="gathalast">Maṅgalāyanaṃ dassetā, buddhaṃ taṃ paṇamāmyahaṃ.11.</p>\r\n<p class="gatha1">Maṅgalamahimādhārī, maṅgalakārī nāyako;</p>\r\n<p class="gathalast">Sabbadhi maṅgaladātā, buddhaṃ taṃ paṇamāmyahaṃ.12.</p>\r\n<p class="gatha1">Maṅgalassa kovido ca, amaṅgalassa kovido;</p>\r\n<p class="gathalast">Paramaṃ maṅgalaṃ laddho, buddhaṃ taṃ paṇamāmyahaṃ.13.</p>\r\n<p class="gatha1">Sabbamaṅgalasampanno, maṅgalaghosaghosako;</p>\r\n<p class="gathalast">Maṅgaluttamaṃ desesi, buddhaṃ taṃ paṇamāmyahaṃ.14.</p>\r\n<p class="gatha1">Mahāmaṅgalatthaṃ maggī, laddho dhammasumaṅgalo;</p>\r\n<p class="gathalast">Maṅgala’ddhānaṃ ñāpesi, buddhaṃ taṃ paṇamāmyahaṃ.15.</p>\r\n<p class="gatha1">Agārikatthaṃ ñāpesi, mahāmaṅgalamuttamaṃ;</p>\r\n<p class="gathalast">Gihī sahāyako sāmī, buddhaṃ taṃ paṇamāmyahaṃ.16.</p>\r\n<p class="gatha1">Mahāmaṅgala’laṅkito, mahāmaṅgalabhūsano;</p>\r\n<p class="gathalast">Mahāmaṅgalasekharo, buddhaṃ taṃ paṇamāmyahaṃ.17.</p>\r\n<p class="gatha1">Mahāmaṅgalaṃsumālī, maṅgalābho samujjalo;</p>\r\n<p class="gathalast">Maṅgalaṃsuṃ ujjotesi, buddhaṃ taṃ paṇamāmyahaṃ.18.</p>\r\n<p class="gatha1">Mahaggha maṅgalodadhi, mahantamaṅgalaṇṇavo;</p>\r\n<p class="gathalast">Mahāmaṅgalasāgaro, buddhaṃ taṃ paṇamāmyahaṃ.19.</p>\r\n<p class="gatha1">Amaṅgalanisāhantā, maṅgalabhānubhassaro;</p>\r\n<p class="gathalast">Maṅgalālokavikiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.20.</p>\r\n<p class="gatha1">Bāhusacco bujjhanako, dhammacakkhuuppādako;</p>\r\n<p class="gathalast">Tibhavamaṅgalaṅkārī, buddhaṃ taṃ paṇamāmyahaṃ.21.</p>\r\n<p class="gatha1">Mahāvattā pavattā ca, atthassa ninnetā pabhū;</p>\r\n<p class="gathalast">Amatassa dātā viññū, buddhaṃ taṃ paṇamāmyahaṃ.22.</p>\r\n<p class="gatha1">Divā tapati ādicco, rattiṃ dippati candimā;</p>\r\n<p class="gathalast">Rattiṃdivaṃ tapi nātho, buddhaṃ taṃ paṇamāmyahaṃ.23.</p>\r\n<p class="gatha1">Sannaddho khattiyo tapati, jhāyī tapati brāhmaṇo;</p>\r\n<p class="gathalast">Nirantaraṃ tapi nātho, buddhaṃ taṃ paṇamāmyahaṃ.24.</p>\r\n<p class="gatha1">Dukkhadomanassanāsī, sokaparidevakkhayo;</p>\r\n<p class="gathalast">Jātimaccujarātigo, buddhaṃ taṃ paṇamāmyahaṃ.25.</p>\r\n<p class="gatha1">Yathāvādī tathākārī, tathavādī tathāgato;</p>\r\n<p class="gathalast">Yathākārī tathāvādī, buddhaṃ taṃ paṇamāmyahaṃ.26.</p>\r\n<p class="gatha1">Bhaggarāgo bhaggadoso, bhaggamoho so bhagavā;</p>\r\n<p class="gathalast">Bhaggamāno bhaggamāyo, buddhaṃ taṃ paṇamāmyahaṃ.27.</p>\r\n<p class="gatha1">Bhaggakāmo bhaggakodho, bhaggakopo bhaggakuho;</p>\r\n<p class="gathalast">Bhaggakasāvo bhaggindho, buddhaṃ taṃ paṇamāmyahaṃ.28.</p>\r\n<p class="gatha1">Bhaggajāti bhaggamaccū, bhaggaloko bhaggabhavo;</p>\r\n<p class="gathalast">Bhaggasaṃsāro bhaggogho, buddhaṃ taṃ paṇamāmyahaṃ.29.</p>\r\n<p class="gatha1">Bhogabhaggo sokabhaggo, rogabhaggo bhaggarajo;</p>\r\n<p class="gathalast">Sūlabhaggo sallabhaggo, buddhaṃ taṃ paṇamāmyahaṃ.30.</p>\r\n<p class="gatha1">Āsābhaggo issābhaggo, ejābhaggo bhaggajaṭā;</p>\r\n<p class="gathalast">Chandabhaggo bandhabhaggo, buddhaṃ taṃ paṇamāmyahaṃ.31.</p>\r\n<p class="gatha1">Vītarāgo vītadoso, vītamoho vītāsavo;</p>\r\n<p class="gathalast">Vītakāmo vītakodho, buddhaṃ taṃ paṇamāmyahaṃ.32.</p>\r\n<p class="gatha1">Vītapāpo vītapuñño, vītabhāro vītamalo;</p>\r\n<p class="gathalast">Vītavikāro vītindho, buddhaṃ taṃ paṇamāmyahaṃ.33.</p>\r\n<p class="gatha1">Vantarāgo vantadoso, vantamoho vantamalo;</p>\r\n<p class="gathalast">Vantakasāvo vantīgho, buddhaṃ taṃ paṇamāmyahaṃ.34.</p>\r\n<p class="gatha1">Vantarāgo vantadoso, vantamoho vantamalo;</p>\r\n<p class="gathalast">Vantakasāvo vantīgho, buddhaṃ taṃ paṇamāmyahaṃ.35.</p>\r\n<p class="gatha1">Sagguru tilokaguru, sabbasattānamaggagurū;</p>\r\n<p class="gathalast">Netāresu mahānetā, buddhaṃ taṃ paṇamāmyahaṃ.36.</p>\r\n<p class="gatha1">Mahāguru naraguru, devaguru guruttamo!</p>\r\n<p class="gathalast">Jeṭṭhaguru seṭṭhaguru, buddhaṃ taṃ paṇamāmyahaṃ.37.</p>\r\n<p class="gatha1">Vissaguru lokaguru, dhammaguru gurūttamo;</p>\r\n<p class="gathalast">Natthi etādiso guru, buddhaṃ taṃ paṇamāmyahaṃ.38.</p>\r\n<p class="gatha1">Mahāsatthā mahāsotthi, mahāmitto mahāsakhā;</p>\r\n<p class="gathalast">Mahākalyāṇamitto yo, buddhaṃ taṃ paṇamāmyahaṃ.39.</p>\r\n<p class="gatha1">Mahāpañño mahāviññū, mahāvidvā mahāvidū;</p>\r\n<p class="gathalast">Mahāmedhāvī sumedho, buddhaṃ taṃ paṇamāmyahaṃ.40.</p>\r\n<p class="gatha1">Mahāsuddho mahābhaddo, mahādayo mahāsayo;</p>\r\n<p class="gathalast">Mahādibbo mahābhabbo, buddhaṃ taṃ paṇamāmyahaṃ.41.</p>\r\n<p class="gatha1">Mahāvīro mahādhīro, mahāsūro mahabbalo;</p>\r\n<p class="gathalast">Mahāmāracamū maddi, buddhaṃ taṃ paṇamāmyahaṃ.42.</p>\r\n<p class="gatha1">Mahājeṭṭho mahāseṭṭho, mahaggo mahānāyako;</p>\r\n<p class="gathalast">Mahācheko mahādakkho, buddhaṃ taṃ paṇamāmyahaṃ.43.</p>\r\n<p class="gatha1">Mahābyatto mahāñāto, mahākhyāto mahāyasī;</p>\r\n<p class="gathalast">Mahiddho mahāvikhyāto, buddhaṃ taṃ paṇamāmyahaṃ.44.</p>\r\n<p class="gatha1">Mahātuṭṭho mahāhaṭṭho, mahājavo mahājayo;</p>\r\n<p class="gathalast">Mahāsantuṭṭho visiṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.45.</p>\r\n<p class="gatha1">Mahānātho mahāsāmī, mahāpabhū mahāvibhū;</p>\r\n<p class="gathalast">Mahissaro bodhissaro, buddhaṃ taṃ paṇamāmyahaṃ.46.</p>\r\n<p class="gatha1">Mahāvaṇṇo mahākanto, manopiyo manāpiko;</p>\r\n<p class="gathalast">Mahāsobhano manuñño, buddhaṃ taṃ paṇamāmyahaṃ.47.</p>\r\n<p class="gatha1">Mahājhāyī mahāñāṇī, mahādhīmā mahāsudhī;</p>\r\n<p class="gathalast">Mahāvibhāvī medhāvī, buddhaṃ taṃ paṇamāmyahaṃ.48.</p>\r\n<p class="gatha1">Mahākhantī ca titikkho, mahānikkaṅkho dhammagū;</p>\r\n<p class="gathalast">Mahāsaṃyato saṃvuto, buddhaṃ taṃ paṇamāmyahaṃ.49.</p>\r\n<p class="gatha1">Mahāthomito pūjito, mahāmānito vandito;</p>\r\n<p class="gathalast">Mahābhivādito bhiyyo, buddhaṃ taṃ paṇamāmyahaṃ.50.</p>\r\n<p class="gatha1">Mahatattho mahatthiko, mahantattho mahattaro;</p>\r\n<p class="gathalast">Mahanto mahattappatto, buddhaṃ taṃ paṇamāmyahaṃ.51.</p>\r\n<p class="gatha1">Mahālābhī mahākārī, mahādāyī mahādhanī;</p>\r\n<p class="gathalast">Mahāvijjo mahāpujjo, buddhaṃ taṃ paṇamāmyahaṃ.52.</p>\r\n<p class="gatha1">Mahābuddhi mahābudhā, mahāpabodhipuṅgavo;</p>\r\n<p class="gathalast">Mahābhavaṇṇavaṃ tiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.53.</p>\r\n<p class="gatha1">Mahānāgo mahābhāgo, mahābāho mahiddhiko;</p>\r\n<p class="gathalast">Mahāyodho mahaggato, buddhaṃ taṃ paṇamāmyahaṃ.54.</p>\r\n<p class="gatha1">Mahādanto mahāsanto, mahāgaṇī mahāguṇī;</p>\r\n<p class="gathalast">Mahāsantipadāyako, buddhaṃ taṃ paṇamāmyahaṃ.55.</p>\r\n<p class="gatha1">Mahāsīlo mahācitto, mahābudho mahesino;</p>\r\n<p class="gathalast">Mahāmohodadhiṃ tiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.56.</p>\r\n<p class="gatha1">Mahātapassī dhammesī, mahāyasassī nāyako;</p>\r\n<p class="gathalast">Mahāpatāpī tejassī, buddhaṃ taṃ paṇamāmyahaṃ.57.</p>\r\n<p class="gatha1">Mahāābho mahāpabho, mohandhakārabhindako;</p>\r\n<p class="gathalast">Ñāṇapadīpaṃ dīpesi, buddhaṃ taṃ paṇamāmyahaṃ.58.</p>\r\n<p class="gatha1">Mahāpassaddhidāyako, munirājā munissaro;</p>\r\n<p class="gathalast">Santidado sukhadado, buddhaṃ taṃ paṇamāmyahaṃ.59.</p>\r\n<p class="gatha1">Mahānīvaraṇā’tīto, mahāmohasamūhato;</p>\r\n<p class="gathalast">Mahoghatiṇṇo mokkhako, buddhaṃ taṃ paṇamāmyahaṃ.60.</p>\r\n<p class="gatha1">Mahābhavopadhicatto, lobhadosavināsako;</p>\r\n<p class="gathalast">Mahāripuṃ nimaddesi, buddhaṃ taṃ paṇamāmyahaṃ.61.</p>\r\n<p class="gatha1">Mahā’bhiññābalappatto, mahāmaññitanāsako;</p>\r\n<p class="gathalast">Mahāsaddhamaṃ niddisi, buddhaṃ taṃ paṇamāmyahaṃ.62.</p>\r\n<p class="gatha1">Ahorattiṃ sadāsuddho, mahālokagganāyako;</p>\r\n<p class="gathalast">Samattavissavissuto, buddhaṃ taṃ paṇamāmyahaṃ.63.</p>\r\n<p class="gatha1">Mahāavijjāucchinno, mahāāsattiriñcako;</p>\r\n<p class="gathalast">Mahājaṭā vijaṭesi, buddhaṃ taṃ paṇamāmyahaṃ.64.</p>\r\n<p class="gatha1">Mahāsūlavivattako, mahāsallavinodano;</p>\r\n<p class="gathalast">Mahāsavaṃ byantikaro, buddhaṃ taṃ paṇamāmyahaṃ.65.</p>\r\n<p class="gatha1">Mahāparakkamī sūro, sabbathā aparājayo;</p>\r\n<p class="gathalast">Accantaabhayo vīro, buddhaṃ taṃ paṇamāmyahaṃ.66.</p>\r\n<p class="gatha1">Mahātaṇhāvītivatto, mahāsokapanūdano;</p>\r\n<p class="gathalast">Mahādukkhamatikkanto, buddhaṃ taṃ paṇamāmyahaṃ.67.</p>\r\n<p class="gatha1">Mahāmānamatikkamo, mārasenanimmaddano;</p>\r\n<p class="gathalast">Kammaklesaṃ nijjaresi, buddhaṃ taṃ paṇamāmyahaṃ.68.</p>\r\n<p class="gatha1">Mahābhavodadhittiṇṇo, pāpaṇṇavapāraṅgato;</p>\r\n<p class="gathalast">Mahāsaṃsaraṇātigo, buddhaṃ taṃ paṇamāmyahaṃ.69.</p>\r\n<p class="gatha1">Mahāsaṃyojanātīto, mahāvaṭṭavināsako;</p>\r\n<p class="gathalast">Mahāsaṃsāroghatiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.70.</p>\r\n<p class="gatha1">Mahārāgavirañjako, mahādosanimmaddako;</p>\r\n<p class="gathalast">Mahāsotaṃ visosesi, buddhaṃ taṃ paṇamāmyahaṃ.71.</p>\r\n<p class="gatha1">Mahāmettāvihārī yo, mahāpuñño mahāraho;</p>\r\n<p class="gathalast">Mahākaruṇāsāgaro, buddhaṃ taṃ paṇamāmyahaṃ.72.</p>\r\n<p class="gatha1">Mahābhavasindhulaṅghī, bhavamutto bhavantagū;</p>\r\n<p class="gathalast">Sokasūlasamucchinno, buddhaṃ taṃ paṇamāmyahaṃ.73.</p>\r\n<p class="gatha1">Mahāvanapathātīto, mahākantārapāragū;</p>\r\n<p class="gathalast">Mahābhavoghanittiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.74.</p>\r\n<p class="gatha1">Mahāmaggaphalappatto, supatto amatodadhī;</p>\r\n<p class="gathalast">Bhavacakkaṃ vibhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.75.</p>\r\n<p class="gatha1">Mahāsubodhisampanno, muttimaggasubhāvito;</p>\r\n<p class="gathalast">Bhavabandhanaṃ bhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.76.</p>\r\n<p class="gatha1">Mahānandīsamucchinno, mahākilesanissaṭo;</p>\r\n<p class="gathalast">Mahāpāpoghamuttiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.77.</p>\r\n<p class="gatha1">Mahāpihāparikkhīṇo, mahāsaṃyojane nudo;</p>\r\n<p class="gathalast">Sabbāsave vinodesi, buddhaṃ taṃ paṇamāmyahaṃ.78.</p>\r\n<p class="gatha1">Mohatimiraṃ bhañjesi, suriyo’va pabhaṅkaro;</p>\r\n<p class="gathalast">Mahājutiṃ pabhāsesi, buddhaṃ taṃ paṇamāmyahaṃ.79.</p>\r\n<p class="gatha1">Mahābhayaṃ padālesi, chambhacchinno bhayanudo;</p>\r\n<p class="gathalast">Mahādukkhakkhandhanudo, buddhaṃ taṃ paṇamāmyahaṃ.80.</p>\r\n<p class="gatha1">Mahāsattuṃ vināsesi, sattidhārī mahābalī;</p>\r\n<p class="gathalast">Mahāsiddho mahesakkho, buddhaṃ taṃ paṇamāmyahaṃ.81.</p>\r\n<p class="gatha1">Mahātamaṃ panudi ca, mahājotipabhāsako;</p>\r\n<p class="gathalast">Ādicco viya dīpesi, buddhaṃ taṃ paṇamāmyahaṃ.82.</p>\r\n<p class="gatha1">Mahābhogapariccāgī, tiṇṇamahātaṇhaṇṇavo;</p>\r\n<p class="gathalast">Mohudadhiṃ pāraṅgato, buddhaṃ taṃ paṇamāmyahaṃ.83.</p>\r\n<p class="gatha1">Mahābhiññābalappatto, mahāpadhānanāyako;</p>\r\n<p class="gathalast">Mahābandhanavimutto, buddhaṃ taṃ paṇamāmyahaṃ.84.</p>\r\n<p class="gatha1">Mahābodhimahaṇṇavo, mahāsambodhisāgaro;</p>\r\n<p class="gathalast">Mahāñāṇamahodadhi, buddhaṃ taṃ paṇamāmyahaṃ.85.</p>\r\n<p class="gatha1">Mahābhisakko yatīndo, taṇhārogatikicchako;</p>\r\n<p class="gathalast">Dukkhato bahū mocesi, buddhaṃ taṃ paṇamāmyahaṃ.86.</p>\r\n<p class="gatha1">Mahāmahimāmaṇḍito, chabbaṇṇaraṃsidhārako;</p>\r\n<p class="gathalast">Mahāpabhāya byāpako, buddhaṃ taṃ paṇamāmyahaṃ.87.</p>\r\n<p class="gatha1">Mahākaruṇāsampatto, sadā mettāya’bhirato;</p>\r\n<p class="gathalast">Mahājanahitarato, buddhaṃ taṃ paṇamāmyahaṃ.88.</p>\r\n<p class="gatha1">Mahāmicchādiṭṭhiṃ hantā, mahāsaddhammuddesako;</p>\r\n<p class="gathalast">Mahāantakupacchinno, buddhaṃ taṃ paṇamāmyahaṃ.89.</p>\r\n<p class="gatha1">Mahāsokasallakatto, mahākhīlavisodhano;</p>\r\n<p class="gathalast">Mahāsantikaro loke, buddhaṃ taṃ paṇamāmyahaṃ.90.</p>\r\n<p class="gatha1">Mahāpāpapaṅkaṃ dhotā, mahādukkhavimocako;</p>\r\n<p class="gathalast">Mahāsajjanaṃ sodhesi, buddhaṃ taṃ paṇamāmyahaṃ.91.</p>\r\n<p class="gatha1">Mahāantakahantā yo, maccusenavināsako;</p>\r\n<p class="gathalast">Mahātaṇhaṃ vinodesi, buddhaṃ taṃ paṇamāmyahaṃ.92.</p>\r\n<p class="gatha1">Mahādiṭṭhiṃ viddhaṃsesi, pāpapuññasamūhato;</p>\r\n<p class="gathalast">Mānaṃ māyañca maddesi, buddhaṃ taṃ paṇamāmyahaṃ.93.</p>\r\n<p class="gatha1">Mahāmohaṃ vināsesi, mahārāganirodhako;</p>\r\n<p class="gathalast">Mahādosaṃ niddhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.94.</p>\r\n<p class="gatha1">Vicitradhammakathiko, abbhutamagguddesako;</p>\r\n<p class="gathalast">Mahakkhāto mahākathī, buddhaṃ taṃ paṇamāmyahaṃ.95.</p>\r\n<p class="gatha1">Mahāvādī kammavādī, mahāsakyamunīvaro;</p>\r\n<p class="gathalast">Munipuṅgavo munindo, buddhaṃ taṃ paṇamāmyahaṃ.96.</p>\r\n<p class="gatha1">Mahāmohanisānāsī, mahāpabhā vitthārako;</p>\r\n<p class="gathalast">Mahājuṇhā pajjotesi, buddhaṃ taṃ paṇamāmyahaṃ.97.</p>\r\n<p class="gatha1">Mahā’vijjānisā hantā, vijjāraṃsivibhūsito;</p>\r\n<p class="gathalast">Mahādhammābhā vikari, buddhaṃ taṃ paṇamāmyahaṃ.98.</p>\r\n<p class="gatha1">Mahāmohaṃ nimmaddesi, māyājālavimocano;</p>\r\n<p class="gathalast">Micchāvādaṃ pamaddesi, buddhaṃ taṃ paṇamāmyahaṃ.99.</p>\r\n<p class="gatha1">Āsavasotaṃ sosesi, mahāvyāpādamaddano;</p>\r\n<p class="gathalast">Mohamahodadhitiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.100.</p>\r\n<p class="gatha1">Mahāmohamatikkanto, mahāsaṅkāvinodano;</p>\r\n<p class="gathalast">Mahābhavoghanittiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.101.</p>\r\n<p class="gatha1">Pāṇātipātā virato, cattadaṇḍo ahiṃsako;</p>\r\n<p class="gathalast">Amitamettavāridhi, buddhaṃ taṃ paṇamāmyahaṃ.102.</p>\r\n<p class="gatha1">Adinnādānā virato, aniccho apariggaho;</p>\r\n<p class="gathalast">Sabbassa cāgī virāgī, buddhaṃ taṃ paṇamāmyahaṃ.103.</p>\r\n<p class="gatha1">Abrahmacariyātīto, kāmabhogapariccajo;</p>\r\n<p class="gathalast">Māradhītāmānaṃ maddi, buddhaṃ taṃ paṇamāmyahaṃ.104.</p>\r\n<p class="gatha1">Kāyānurakkhī sudanto, vācānurakkhī subbato;</p>\r\n<p class="gathalast">Mahāsīlena sampanno, buddhaṃ taṃ paṇamāmyahaṃ.105.</p>\r\n<p class="gatha1">Sammāājīvasampanno, suddhācaraṇacārako;</p>\r\n<p class="gathalast">Sīlasiromaṇi sāmī, buddhaṃ taṃ paṇamāmyahaṃ.106.</p>\r\n<p class="gatha1">Subharo sīlasampanno, suddhācārasiromaṇī;</p>\r\n<p class="gathalast">Thāmavā javasampanno, buddhaṃ taṃ paṇamāmyahaṃ.107.</p>\r\n<p class="gatha1">Avikappo avitakko, avicāro acintako;</p>\r\n<p class="gathalast">Cittaekaggatāppatto, buddhaṃ taṃ paṇamāmyahaṃ.108.</p>\r\n<p class="gatha1">Indriyāni surakkhesi, sato saṃvaramānaso;</p>\r\n<p class="gathalast">Sammāsamāhitacitto, buddhaṃ taṃ paṇamāmyahaṃ.109.</p>\r\n<p class="gatha1">Pabalapañño paññaññū, gambhīrapaññāsobhano;</p>\r\n<p class="gathalast">Sampannapañño paññakkho, buddhaṃ taṃ paṇamāmyahaṃ.110.</p>\r\n<p class="gatha1">Pahūtapañño supañño, puthulapañño paññavā;</p>\r\n<p class="gathalast">Paguṇañāṇo ñāṇakkho, buddhaṃ taṃ paṇamāmyahaṃ.111.</p>\r\n<p class="gatha1">Tikkhapañño khippapañño, puṇṇapañño paññāpabhū;</p>\r\n<p class="gathalast">Paññānātho paññāsāmī, buddhaṃ taṃ paṇamāmyahaṃ.112.</p>\r\n<p class="gatha1">Dhuvapañño ṭhitapañño, thirapañño paññādado;</p>\r\n<p class="gathalast">Pariyodātapañño yo, buddhaṃ taṃ paṇamāmyahaṃ.113.</p>\r\n<p class="gatha1">Paññāya pāramīppatto, anantapaññasekharo;</p>\r\n<p class="gathalast">Puññapāramīsampanno, buddhaṃ taṃ paṇamāmyahaṃ.114.</p>\r\n<p class="gatha1">Paññādhanī paññābalī, paññāsīlasamāhito;</p>\r\n<p class="gathalast">Paññāpati paññādhārī, buddhaṃ taṃ paṇamāmyahaṃ.115.</p>\r\n<p class="gatha1">Paññavanto sudhīmanto, sīdhapañño paññānidhi;</p>\r\n<p class="gathalast">Paññāvāridhi paññagū, buddhaṃ taṃ paṇamāmyahaṃ.116.</p>\r\n<p class="gatha1">Paguṇapaññākusalo, anantapaññavā vibhū;</p>\r\n<p class="gathalast">Seṭṭhapañño jeṭṭhapañño, buddhaṃ taṃ paṇamāmyahaṃ.117.</p>\r\n<p class="gatha1">Paripuṇṇapañño puṇṇo, pavarapaññapāragū;</p>\r\n<p class="gathalast">Paññissaro ñāṇissaro, buddhaṃ taṃ paṇamāmyahaṃ.118.</p>\r\n<p class="gatha1">Accantaamalapañño, sampuṇṇapaṭibhānavā;</p>\r\n<p class="gathalast">Paṭivedhapaññādhārī, buddhaṃ taṃ paṇamāmyahaṃ.119.</p>\r\n<p class="gatha1">Anomapañño anomo, bhūripañño paññavaro;</p>\r\n<p class="gathalast">Parisuddhapañño suddho, buddhaṃ taṃ paṇamāmyahaṃ.120.</p>\r\n<p class="gatha1">Nibbedhikapañño nātho, paṭibodhapañño paṭū;</p>\r\n<p class="gathalast">Visuddhapañño saṃsuddho, buddhaṃ taṃ paṇamāmyahaṃ.121.</p>\r\n<p class="gatha1">Paññācakkhu ñāṇacakkhu, buddhacakkhu sucakkhumā;</p>\r\n<p class="gathalast">Samantacakkhu sampuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.122.</p>\r\n<p class="gatha1">Pabalapaññāsaṃyutto, tikkhamedho medhāmayo;</p>\r\n<p class="gathalast">Puṇṇābhiññā aññātāvī, buddhaṃ taṃ paṇamāmyahaṃ.123.</p>\r\n<p class="gatha1">Paññāvudhena sampanno, ajeyyo ajito pabhū;</p>\r\n<p class="gathalast">Jino ajini pāpimaṃ, buddhaṃ taṃ paṇamāmyahaṃ.124.</p>\r\n<p class="gatha1">Asaṅkhatamanuppatto, paññā’bharaṇabhūsito;</p>\r\n<p class="gathalast">Paramatthaṃ paññāpesi, buddhaṃ taṃ paṇamāmyahaṃ.125.</p>\r\n<p class="gatha1">Paññavā paññaṃ pasāresi, ñāṇī ñāṇasaṃvaḍḍhano;</p>\r\n<p class="gathalast">Adhammadhaṃsako dhammī, buddhaṃ taṃ paṇamāmyahaṃ.126.</p>\r\n<p class="gatha1">Paññāsekharo paññindo, ñāṇindo ñāṇasekharo;</p>\r\n<p class="gathalast">Dhammasekharo dhammindo, buddhaṃ taṃ paṇamāmyahaṃ.127.</p>\r\n<p class="gatha1">Javanapañño jitatto, sabbaganthappamocano;</p>\r\n<p class="gathalast">Gaṇṭhimutto guttadvāro, buddhaṃ taṃ paṇamāmyahaṃ.128.</p>\r\n<p class="gatha1">Bahupañño bahukārī, bahuñāṇī bahuguṇo;</p>\r\n<p class="gathalast">Bahulaguṇasampanno, buddhaṃ taṃ paṇamāmyahaṃ.129.</p>\r\n<p class="gatha1">Paññāvimuttisampanno, mohātīto dukkhātigo;</p>\r\n<p class="gathalast">Kilesasallakantako, buddhaṃ taṃ paṇamāmyahaṃ.130.</p>\r\n<p class="gatha1">Bodhihadayo sambuddho, paññindo paññamānaso;</p>\r\n<p class="gathalast">Ñāṇacetaso ñāṇābho, buddhaṃ taṃ paṇamāmyahaṃ.131.</p>\r\n<p class="gatha1">Dhuvasīlo dhuvacitto, dhuvamedho dhuvaṅgato;</p>\r\n<p class="gathalast">Dhuvamutto dhuvaladdho, buddhaṃ taṃ paṇamāmyahaṃ.132.</p>\r\n<p class="gatha1">Sīlabalī cittabalī, paññābalī aññābalī;</p>\r\n<p class="gathalast">Dhammabalī dhīrabalī, buddhaṃ taṃ paṇamāmyahaṃ.133.</p>\r\n<p class="gatha1">Sīladhanī cittadhanī, paññādhanī aññādhanī;</p>\r\n<p class="gathalast">Dhammadhanī dhīradhanī, buddhaṃ taṃ paṇamāmyahaṃ.134.</p>\r\n<p class="gatha1">Kāyāsaṃvarasaṃyato, saṃyatavācāsaṃvaro;</p>\r\n<p class="gathalast">Cittasaṃvara saṃyato, buddhaṃ taṃ paṇamāmyahaṃ.135.</p>\r\n<p class="gatha1">Sīlaggo samādhippatto, paññappatto subhāvito;</p>\r\n<p class="gathalast">Dhammasudhārasaṃ pāyī, buddhaṃ taṃ paṇamāmyahaṃ.136.</p>\r\n<p class="gatha1">Susīlo samādhippatto, paññālaṅkāra’laṅkato;</p>\r\n<p class="gathalast">Dhammābharaṇabhūsito, buddhaṃ taṃ paṇamāmyahaṃ.137.</p>\r\n<p class="gatha1">Sammāsamādhi’laṅkito, sīlapaññavibhūsato;</p>\r\n<p class="gathalast">Sabbābhiññābalappatto, buddhaṃ taṃ paṇamāmyahaṃ.138.</p>\r\n<p class="gatha1">Suddhasīlo suddhacitto, suddhapañño suddhamano;</p>\r\n<p class="gathalast">Suddhadhammaṃ pakāsesi, buddhaṃ taṃ paṇamāmyahaṃ.139.</p>\r\n<p class="gatha1">Sīlavā susamāhito, paññavā yasavā isi;</p>\r\n<p class="gathalast">Lokavissuto vidito, buddhaṃ taṃ paṇamāmyahaṃ.140.</p>\r\n<p class="gatha1">Sīlasamādhisampanno, vidhuro paññapuṅgavo;</p>\r\n<p class="gathalast">Sabbesaṃ sabbaṃ bodhesi, buddhaṃ taṃ paṇamāmyahaṃ.141.</p>\r\n<p class="gatha1">Anantañāṇī nijjhānī, ñāṇasīsacūḷāmaṇi;</p>\r\n<p class="gathalast">Ñāṇakkho narapāmokkho, buddhaṃ taṃ paṇamāmyahaṃ.142.</p>\r\n<p class="gatha1">Nāyakānaṃ varo nātho, ñāṇiko ñāṇapuṇṇiko;</p>\r\n<p class="gathalast">Ñāṇasamattho ñāṇaṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.143.</p>\r\n<p class="gatha1">Ñāṇarāmo ñāṇarato, gambhīrañāṇakovido;</p>\r\n<p class="gathalast">Ñāṇadassanasampanno, buddhaṃ taṃ paṇamāmyahaṃ.144.</p>\r\n<p class="gatha1">Ñāṇasobhito ñāṇaggū, ñāṇa’laṅkāra’laṅkato;</p>\r\n<p class="gathalast">Ñāṇasiromaṇi ñāṇī, buddhaṃ taṃ paṇamāmyahaṃ.145.</p>\r\n<p class="gatha1">Ñāṇavanto ñāṇavaro, ñāṇaneru suññāṇavā;</p>\r\n<p class="gathalast">Ñāṇasindhu ñāṇodadhi, buddhaṃ taṃ paṇamāmyahaṃ.146.</p>\r\n<p class="gatha1">Ñāṇamoli ñāṇamuddho, ñāṇadīpo ñāṇasikho;</p>\r\n<p class="gathalast">Ñāṇameru ñāṇasiṅgo, buddhaṃ taṃ paṇamāmyahaṃ.147.</p>\r\n<p class="gatha1">Gambhīraññāṇī medhāvī, tikkhaññāṇī vicakkhaṇo;</p>\r\n<p class="gathalast">Aññāṇamūlaṃ bhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.148.</p>\r\n<p class="gatha1">Sabbañāṇī sabbaññāto, satthā sammapavattako;</p>\r\n<p class="gathalast">Suddhadhammaṃ vitthāresi, buddhaṃ taṃ paṇamāmyahaṃ.149.</p>\r\n<p class="gatha1">Sabbaññutañāṇapatto, sabbaviññū vināyako;</p>\r\n<p class="gathalast">Nibbānasukhasampatto, buddhaṃ taṃ paṇamāmyahaṃ.150.</p>\r\n<p class="gatha1">Sabbaññū sabbañāṇiko, sabbato maññanājaho;</p>\r\n<p class="gathalast">Sabbamathitavikkhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.151.</p>\r\n<p class="gatha1">Sabbañāṇādhipatiko, samattho pavaro pabhū;</p>\r\n<p class="gathalast">Sabbato ñāṇadassāvī, buddhaṃ taṃ paṇamāmyahaṃ.152.</p>\r\n<p class="gatha1">Dasabalādhipo nāgo, amitañāṇādhipati;</p>\r\n<p class="gathalast">Bahuṃ iddhiṃ anuppatto, buddhaṃ taṃ paṇamāmyahaṃ.153.</p>\r\n<p class="gatha1">Paṭivedhañāṇayutto, māyāpaṭapacchedako;</p>\r\n<p class="gathalast">Avijjaṃ paridhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.154.</p>\r\n<p class="gatha1">Muttiñāṇaṃ gavesi yo, maggasaccapakāsako;</p>\r\n<p class="gathalast">Magga’kkhāyī maggavidū, buddhaṃ taṃ paṇamāmyahaṃ.155.</p>\r\n<p class="gatha1">Sabbāññāṇaṃ vināsesi, mahāpaññānamuttamo;</p>\r\n<p class="gathalast">Bodhiñāṇamahāsindhu, buddhaṃ taṃ paṇamāmyahaṃ.156.</p>\r\n<p class="gatha1">Sammādassanasampatto, sammāñāṇapatiṭṭhito;</p>\r\n<p class="gathalast">Sammāvimuttijitatto, buddhaṃ taṃ paṇamāmyahaṃ.157.</p>\r\n<p class="gatha1">Arahā vijjāsampanno, ñāṇasikharasekharo;</p>\r\n<p class="gathalast">Paramaṃ sukhaṃ sampatto, buddhaṃ taṃ paṇamāmyahaṃ.158.</p>\r\n<p class="gatha1">Paramañāṇasampanno, pavarapaññāpuṇṇiko;</p>\r\n<p class="gathalast">Sammāvimuttiṃ sampatto, buddhaṃ taṃ paṇamāmyahaṃ.159.</p>\r\n<p class="gatha1">Sabbadassāvī suññāṇī, sabbakiccesu paṇḍito;</p>\r\n<p class="gathalast">Sabbadhi suguṇupeto, buddhaṃ taṃ paṇamāmyahaṃ.160.</p>\r\n<p class="gatha1">Saṃvedanā saṃveditvā, sammāsambodhiṃ bodhayi;</p>\r\n<p class="gathalast">Sokavigato sumato, buddhaṃ taṃ paṇamāmyahaṃ.161.</p>\r\n<p class="gatha1">Vedanāsu vītagijjho, vedanāmutto sabbathā;</p>\r\n<p class="gathalast">Vedagū ca vedantagū, buddhaṃ taṃ paṇamāmyahaṃ.162.</p>\r\n<p class="gatha1">Vissāsabhūmi sattānaṃ, andhānaṃ nayanūpamo;</p>\r\n<p class="gathalast">Arakkheyyo ārakkhako, buddhaṃ taṃ paṇamāmyahaṃ.163.</p>\r\n<p class="gatha1">Dukkhasakkhī dukkhakkhīṇo, dukkhavidū dukkhantagū;</p>\r\n<p class="gathalast">Dukkhappahīno dukkhaññū, buddhaṃ taṃ paṇamāmyahaṃ.164.</p>\r\n<p class="gatha1">Kāyasakkhī cittasakkhī, vedanānusakkhī sukhī;</p>\r\n<p class="gathalast">Dhammānusakkhī susakkhī, buddhaṃ taṃ paṇamāmyahaṃ.165.</p>\r\n<p class="gatha1">Santakāyo santavāco, santacitto samāhito;</p>\r\n<p class="gathalast">Sabbūpadhivūpasanto, buddhaṃ taṃ paṇamāmyahaṃ.166.</p>\r\n<p class="gatha1">Sammāsamāhitacitto, sammāsīle patiṭṭhito;</p>\r\n<p class="gathalast">Sammāpaññāparipuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.167.</p>\r\n<p class="gatha1">Nikhilaniṭṭha’dhigamo, accāraddhavipassako;</p>\r\n<p class="gathalast">Samattasantisampatto, buddhaṃ taṃ paṇamāmyahaṃ.168.</p>\r\n<p class="gatha1">Sabbakāyaṃ saṃveditvā, sabbadhammavipassako;</p>\r\n<p class="gathalast">Bhavasaṃsāraṃ riñcesi, buddhaṃ taṃ paṇamāmyahaṃ.169.</p>\r\n<p class="gatha1">Sabbadā satisampanno, sampajaññarato sadā;</p>\r\n<p class="gathalast">Santataṃ samaṇo sāmī, buddhaṃ taṃ paṇamāmyahaṃ.170.</p>\r\n<p class="gatha1">Saccadassanadassāvī, saccadhammavipassako;</p>\r\n<p class="gathalast">Saccacakkaṃ pavattesi, buddhaṃ taṃ paṇamāmyahaṃ.171.</p>\r\n<p class="gatha1">Jhānasokhummasampanno, attapaṇidhipāragū;</p>\r\n<p class="gathalast">Bhavādīnavadassāvī, buddhaṃ taṃ paṇamāmyahaṃ.172.</p>\r\n<p class="gatha1">Cakkhumanto vatavanto, sīlavanto susīlavā;</p>\r\n<p class="gathalast">Medhāvanto paññāvanto, buddhaṃ taṃ paṇamāmyahaṃ.173.</p>\r\n<p class="gatha1">Vaṇṇavanto guṇavanto, jutimanto jutidharo;</p>\r\n<p class="gathalast">Yasavanto kittimanto, buddhaṃ taṃ paṇamāmyahaṃ.174.</p>\r\n<p class="gatha1">Satimanto yati santo, matimanto medhāvino;</p>\r\n<p class="gathalast">Patāpavanto dhīmanto, buddhaṃ taṃ paṇamāmyahaṃ.175.</p>\r\n<p class="gatha1">Yogavanto khemavanto, paṭibhānavanto pabhū;</p>\r\n<p class="gathalast">Hirīmano sirīmano, buddhaṃ taṃ paṇamāmyahaṃ.176.</p>\r\n<p class="gatha1">Attadanto anussado, dhīradhārī dhurandharo;</p>\r\n<p class="gathalast">Dhitimanto dhīsampanno, buddhaṃ taṃ paṇamāmyahaṃ.177.</p>\r\n<p class="gatha1">Thāmavanto iddhimanto, dayāvanto dayālayo;</p>\r\n<p class="gathalast">Kantimanto rūpavanto, buddhaṃ taṃ paṇamāmyahaṃ.178.</p>\r\n<p class="gatha1">Khantimanto santimanto, bhagavanto sukevalī;</p>\r\n<p class="gathalast">Ñāyavanto nayavanto, buddhaṃ taṃ paṇamāmyahaṃ.179.</p>\r\n<p class="gatha1">Bodhimanto buddhimanto, bodhiñāṇo bodhiguṇo;</p>\r\n<p class="gathalast">Bodhidhammamanuppatto, buddhaṃ taṃ paṇamāmyahaṃ.180.</p>\r\n<p class="gatha1">Muttadoso mettāvanto, karuṇāvanto modito;</p>\r\n<p class="gathalast">Sabbathā upekkhāvanto, buddhaṃ taṃ paṇamāmyahaṃ.181.</p>\r\n<p class="gatha1">Yatavanto yatacārī, khamavanto sukhemino;</p>\r\n<p class="gathalast">Tapavanto tathavanto, buddhaṃ taṃ paṇamāmyahaṃ.182.</p>\r\n<p class="gatha1">Pabhāvavanto puriso, patāpī paṭibhānavā;</p>\r\n<p class="gathalast">Sadatthapaṭidīpako, buddhaṃ taṃ paṇamāmyahaṃ.183.</p>\r\n<p class="gatha1">Saṅgachinno raṅgachinno, rāgachinno chinnaratī;</p>\r\n<p class="gathalast">Nandīchinno taṇhāchinno, buddhaṃ taṃ paṇamāmyahaṃ.184.</p>\r\n<p class="gatha1">Chinnāsaṅko chinnātaṅko, chinnākaṅkho chinnaṅgaṇo;</p>\r\n<p class="gathalast">Chinnasaṃyojanā sabbe, buddhaṃ taṃ paṇamāmyahaṃ.185.</p>\r\n<p class="gatha1">Chinnāsaṅgo chinnāsatto, chinnādāno chinnāvilo;</p>\r\n<p class="gathalast">Chinnalitto chinnālambo, buddhaṃ taṃ paṇamāmyahaṃ.186.</p>\r\n<p class="gatha1">Chinnakāmo chinnakodho, chinnakopo chinnamado;</p>\r\n<p class="gathalast">Chinnakleso chinnakhobho, buddhaṃ taṃ paṇamāmyahaṃ.187.</p>\r\n<p class="gatha1">Chinnapāpo chinnatāpo, chinniccho chinnasaṃsayo;</p>\r\n<p class="gathalast">Chinnasoto chinnasneho, buddhaṃ taṃ paṇamāmyahaṃ.188.</p>\r\n<p class="gatha1">Chinnabhogo chinnayogo, chinnabhīti chinnabhayo;</p>\r\n<p class="gathalast">Chinnakhandho chinnachando, buddhaṃ taṃ paṇamāmyahaṃ.189.</p>\r\n<p class="gathalast">Bhinnāsaṅko bhinnātaṅko, bhinnākaṅkho bhinnussuko;</p>\r\n<p class="gathalast">Bhinnābhirato bhinnāso, buddhaṃ taṃ paṇamāmyahaṃ.190.</p>\r\n<p class="gatha1">Bhinnakhobho bhinnadukkho, bhinnakleso bhinnakhilo;</p>\r\n<p class="gathalast">Bhinnābhimāno bhinnejo, buddhaṃ taṃ paṇamāmyahaṃ.191.</p>\r\n<p class="gatha1">Lobhabhinno lolabhinno, rosabhinno bhinnaraṇo;</p>\r\n<p class="gathalast">Nehabhinno khedabhinno, buddhaṃ taṃ paṇamāmyahaṃ.192.</p>\r\n<p class="gatha1">Bhogabhinno sokabhinno, rogabhinno bhinnarajo;</p>\r\n<p class="gathalast">Sūlabhinno sallabhinno, buddhaṃ taṃ paṇamāmyahaṃ.193.</p>\r\n<p class="gatha1">Saṅgabhinno raṅgabhinno, rāgabhinno bhinnaratī;</p>\r\n<p class="gathalast">Nandībhinno taṇhābhinno, buddhaṃ taṃ paṇamāmyahaṃ.194.</p>\r\n<p class="gatha1">Sabbabhavābādhabhinno, bhinnamahāmohatamo;</p>\r\n<p class="gathalast">Ādhibhinno vyādhibhinno, buddhaṃ taṃ paṇamāmyahaṃ.195.</p>\r\n<p class="gatha1">Khinnagiddho khinnamuddho, khinnābhilāso khinniccho;</p>\r\n<p class="gathalast">Khinnalobho khinnābhijjho, buddhaṃ taṃ paṇamāmyahaṃ.196.</p>\r\n<p class="gatha1">Āsākhinno issākhinno, ejākhinno khinnaiṇo;</p>\r\n<p class="gathalast">Chandakhinno bandhakhinno, buddhaṃ taṃ paṇamāmyahaṃ.197.</p>\r\n<p class="gatha1">Kappakhinno kālakhinno, kilesakhinno sabbathā;</p>\r\n<p class="gathalast">Mūlakhinno sūlakhinno, buddhaṃ taṃ paṇamāmyahaṃ.198.</p>\r\n<p class="gatha1">Tiṇṇapāpo tiṇṇapuñño, tiṇṇamoho tiṇṇamalo;</p>\r\n<p class="gathalast">Tiṇṇavikāro tiṇṇīṇo, buddhaṃ taṃ paṇamāmyahaṃ.199.</p>\r\n<p class="gatha1">Tiṇṇadoso tiṇṇadoho, tiṇṇarāgo tiṇṇamamo;</p>\r\n<p class="gathalast">Tiṇṇatāpo tiṇṇatāso, buddhaṃ taṃ paṇamāmyahaṃ.200.</p>\r\n<p class="gatha1">Tiṇṇajāti tiṇṇamaccu, tiṇṇaloko tiṇṇabhavo;</p>\r\n<p class="gathalast">Tiṇṇogho tiṇṇasaṃsāro, buddhaṃ taṃ paṇamāmyahaṃ.201.</p>\r\n<p class="gatha1">Cuṇṇamoho cuṇṇamakkho, cuṇṇamāno cuṇṇamado;</p>\r\n<p class="gathalast">Cuṇṇarāgo cuṇṇadoso, buddhaṃ taṃ paṇamāmyahaṃ.202.</p>\r\n<p class="gatha1">Cuṇṇakodho cuṇṇakopo, cuṇṇakhobho cuṇṇabhayo;</p>\r\n<p class="gathalast">Cuṇṇasūlo cuṇṇasallo, buddhaṃ taṃ paṇamāmyahaṃ.203.</p>\r\n<p class="gatha1">Pāpacuṇṇo puññacuṇṇo, ahaṃcuṇṇo cuṇṇamamo;</p>\r\n<p class="gathalast">Kaṅkhācuṇṇo saṅkācuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.204.</p>\r\n<p class="gatha1">Saṃsāracakkaṃ cuṇṇesi, bhavarajjūnikantako;</p>\r\n<p class="gathalast">Sabbasaṅkhāravicuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.205.</p>\r\n<p class="gatha1">Nimmamo nirahaṃkāro, nirālambo nirālayo;</p>\r\n<p class="gathalast">Nippapañco nirārambho, buddhaṃ taṃ paṇamāmyahaṃ.206.</p>\r\n<p class="gatha1">Nirāsatto nirāsaṃso, nissaṃsayo nirussuko;</p>\r\n<p class="gathalast">Nirāsavo nirapekkho, buddhaṃ taṃ paṇamāmyahaṃ.207.</p>\r\n<p class="gatha1">Nirāsaṅko nirātaṅko, nirākaṅkho niraṅgaṇo;</p>\r\n<p class="gathalast">Nissaṅkiliṭṭho nikkaṅkho, buddhaṃ taṃ paṇamāmyahaṃ.208.</p>\r\n<p class="gatha1">Nhāto dhoto niddhoto, nibbuto nittharaṇiko;</p>\r\n<p class="gathalast">Nimmalo nirodhappatto, buddhaṃ taṃ paṇamāmyahaṃ.209.</p>\r\n<p class="gatha1">Nibbhayo naranisabho, nikkampo narakesarī;</p>\r\n<p class="gathalast">Narakuñjaro nicchambhī, buddhaṃ taṃ paṇamāmyahaṃ.210.</p>\r\n<p class="gatha1">Nirāgo cāpi nikkopo, nicchuddho cāpi nimmado;</p>\r\n<p class="gathalast">Niddukkho cāpi nissoko, buddhaṃ taṃ paṇamāmyahaṃ.211.</p>\r\n<p class="gatha1">Nāsako titthiye nānā, nānāvādaviddhaṃsako;</p>\r\n<p class="gathalast">Nānādiṭṭhiṃ nivāresi, buddhaṃ taṃ paṇamāmyahaṃ.212.</p>\r\n<p class="gatha1">Nandikkhayo nibbanatho, niricchako nirindhano;</p>\r\n<p class="gathalast">Nissaṭṭhamāno nittaṇho, buddhaṃ taṃ paṇamāmyahaṃ.213.</p>\r\n<p class="gatha1">Nippaṭibaddho nibaddho, nirābādho nirabbudo;</p>\r\n<p class="gathalast">Niraggalo nikkaṇṭako, buddhaṃ taṃ paṇamāmyahaṃ.214.</p>\r\n<p class="gatha1">Nirupatāpo nippāpo, nippipāso nirākulo;</p>\r\n<p class="gathalast">Nippariphando niriñjo, buddhaṃ taṃ paṇamāmyahaṃ.215.</p>\r\n<p class="gatha1">Nihatamāno nepuñño, nayaniyāmañāṇiko;</p>\r\n<p class="gathalast">Nittiṇṇakaṅkho nissaṅko, buddhaṃ taṃ paṇamāmyahaṃ.216.</p>\r\n<p class="gatha1">Nippagabbho nippaṭigho, nātimāno nirāvilo;</p>\r\n<p class="gathalast">Nibbānapatto nikhilo, buddhaṃ taṃ paṇamāmyahaṃ.217.</p>\r\n<p class="gatha1">Nirosamāno nivero, nikkodho ca nikkupito;</p>\r\n<p class="gathalast">Niyāmappatto pamudo, buddhaṃ taṃ paṇamāmyahaṃ.218.</p>\r\n<p class="gatha1">Sunipuṇo nayaladdho, nirāgu nibbānagato;</p>\r\n<p class="gathalast">Namucidheyyamaccago, buddhaṃ taṃ paṇamāmyahaṃ.219.</p>\r\n<p class="gatha1">Nillobhamāno nisneho, nikkamano nikkāmano;</p>\r\n<p class="gathalast">Nirabhimānī nilluddho, buddhaṃ taṃ paṇamāmyahaṃ.220.</p>\r\n<p class="gatha1">Nirāgamāno ninneho, nandīrāgavināsako;</p>\r\n<p class="gathalast">Niricchamāno nillaggo, buddhaṃ taṃ paṇamāmyahaṃ.221.</p>\r\n<p class="gatha1">Niddosamāno nimmakkho, bhavanettinikantako;</p>\r\n<p class="gathalast">Nikkasāvo nikkaluso, buddhaṃ taṃ paṇamāmyahaṃ.222.</p>\r\n<p class="gatha1">Nikkilissako nikkuppo, niccalo ca niccañcalo;</p>\r\n<p class="gathalast">Niccapalo ca nillolo, buddhaṃ taṃ paṇamāmyahaṃ.223.</p>\r\n<p class="gatha1">Nikkuho ca nikkuṭilo, nibbikāro nibbijjano;</p>\r\n<p class="gathalast">Niggaṇṭho nirāparādho, buddhaṃ taṃ paṇamāmyahaṃ.224.</p>\r\n<p class="gatha1">Nikkukkucco niruddhacco, nirāso ca bhavābhave;</p>\r\n<p class="gathalast">Nibbicikiccho nibbijjo, buddhaṃ taṃ paṇamāmyahaṃ.225.</p>\r\n<p class="gatha1">Namucisenasūdako, animittarato yatī;</p>\r\n<p class="gathalast">Nipuṇo nittiṇṇaogho, buddhaṃ taṃ paṇamāmyahaṃ.226.</p>\r\n<p class="gatha1">Nikkhīlo cāpi nissallo, nissūlo nibbānarato;</p>\r\n<p class="gathalast">Nippamādo nibbandhano, buddhaṃ taṃ paṇamāmyahaṃ.227.</p>\r\n<p class="gatha1">Nirabhimāno nimmāno, nittaṇhamāno nicchalo;</p>\r\n<p class="gathalast">Nikkaṅkhamāno nicchando, buddhaṃ taṃ paṇamāmyahaṃ.228.</p>\r\n<p class="gatha1">Naravīro naradhīro, narājañño naravaro;</p>\r\n<p class="gathalast">Naranāgo narasīho, buddhaṃ taṃ paṇamāmyahaṃ.229.</p>\r\n<p class="gatha1">Narajeṭṭho naraseṭṭho, narusabho naruttamo;</p>\r\n<p class="gathalast">Naradammasusārathī, buddhaṃ taṃ paṇamāmyahaṃ.230.</p>\r\n<p class="gatha1">Varanātho naranātho, lokanātho lokajino;</p>\r\n<p class="gathalast">Devanātho brahmanātho, buddhaṃ taṃ paṇamāmyahaṃ.231.</p>\r\n<p class="gatha1">Jhānanirato nijjhāyī, kilesavisanāsako;</p>\r\n<p class="gathalast">Sukhumajhānasampanno, buddhaṃ taṃ paṇamāmyahaṃ.232.</p>\r\n<p class="gatha1">Nittaṇhamānaso nātho, sadā nimmalamānaso;</p>\r\n<p class="gathalast">Alittamānaso cāgī, buddhaṃ taṃ paṇamāmyahaṃ.233.</p>\r\n<p class="gatha1">Nunnajāti nunnamaccu, nunnaloko nunnabhavo;</p>\r\n<p class="gathalast">Nunnavibhavo nunnogho, buddhaṃ taṃ paṇamāmyahaṃ.234.</p>\r\n<p class="gatha1">Nunnāsaṅko nunnātaṅko, nunnāso ca nunnussuko;</p>\r\n<p class="gathalast">Nunnābhirato nunniccho, buddhaṃ taṃ paṇamāmyahaṃ.235.</p>\r\n<p class="gatha1">Nunnānutāpo nunnāgu, nunnadāho nunnāsavo;</p>\r\n<p class="gathalast">Nunnamānābhimāno yo, buddhaṃ taṃ paṇamāmyahaṃ.236.</p>\r\n<p class="gatha1">Lobhanudo lolanudo, rosanudo nunnamado;</p>\r\n<p class="gathalast">Disanudo dessanudo, buddhaṃ taṃ paṇamāmyahaṃ.237.</p>\r\n<p class="gatha1">Kammanudo klesanudo, kalinudo nunnamalo;</p>\r\n<p class="gathalast">Sabbathā avijjānudo, buddhaṃ taṃ paṇamāmyahaṃ.238.</p>\r\n<p class="gatha1">Sabbabhavābādhanudo, nunnamahāmohatamo;</p>\r\n<p class="gathalast">Ādhinudo vyādhinudo, buddhaṃ taṃ paṇamāmyahaṃ.239.</p>\r\n<p class="gatha1">Lahupañño paññaṇṇavo, kāruṇo karuṇaṇṇavo;</p>\r\n<p class="gathalast">Medhaṇṇavo mettaṇṇavo, buddhaṃ taṃ paṇamāmyahaṃ.240.</p>\r\n<p class="gatha1">Ñāṇanidhi ñāṇaṇṇavo, dhammanidhi dhammaṇṇavo;</p>\r\n<p class="gathalast">Guṇanidhi guṇaṇṇavo, buddhaṃ taṃ paṇamāmyahaṃ.241.</p>\r\n<p class="gatha1">Muttāsaṅgo muttāsaṅko, muttālambo muttālayo;</p>\r\n<p class="gathalast">Muttāsaṃso muttāsatto, buddhaṃ taṃ paṇamāmyahaṃ.242.</p>\r\n<p class="gatha1">Muttalālaso muttindho, muttachambho muttadaro;</p>\r\n<p class="gathalast">Muttamaccharo mutticcho, buddhaṃ taṃ paṇamāmyahaṃ.243.</p>\r\n<p class="gatha1">Muttakāmo muttataṇho, muttarāgo muttapiho;</p>\r\n<p class="gathalast">Muttachando muttanandī, buddhaṃ taṃ paṇamāmyahaṃ.244.</p>\r\n<p class="gatha1">Muttagiddho muttaluddho, muttābhilāso muttiñjo;</p>\r\n<p class="gathalast">Muttalobho muttābhijjho, buddhaṃ taṃ paṇamāmyahaṃ.245.</p>\r\n<p class="gatha1">Jālamutto’va sakuṇo, muttāso yo bhavābhave;</p>\r\n<p class="gathalast">Saṃyojanehi vimutto, buddhaṃ taṃ paṇamāmyahaṃ.246.</p>\r\n<p class="gatha1">Cetovimutto jhānavā, paññāvimutto paññavā;</p>\r\n<p class="gathalast">Ubhatobhāga vimutto, buddhaṃ taṃ paṇamāmyahaṃ.247.</p>\r\n<p class="gatha1">Sabbāyatanehi mutto, munindo parinibbuto;</p>\r\n<p class="gathalast">Jātisaṃsāravimutto, buddhaṃ taṃ paṇamāmyahaṃ.248.</p>\r\n<p class="gatha1">Sabbakilesā vimutto, sabbadomanassacuto;</p>\r\n<p class="gathalast">Sabbadohavinimutto, buddhaṃ taṃ paṇamāmyahaṃ.249.</p>\r\n<p class="gatha1">Kāmesanā vinimutto, mutto cāpi bhavesanā;</p>\r\n<p class="gathalast">Vibhavesanā vimutto, buddhaṃ taṃ paṇamāmyahaṃ.250.</p>\r\n<p class="gatha1">Kāmiñjanā vinimutto, mutto cāpi bhaviñjanā;</p>\r\n<p class="gathalast">Vibhaviñjanā vimutto, buddhaṃ taṃ paṇamāmyahaṃ.251.</p>\r\n<p class="gatha1">Kāmataṇhā vinimutto, bhavataṇhāvibhañjako;</p>\r\n<p class="gathalast">Vibhavataṇhāvikkhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.252.</p>\r\n<p class="gatha1">Sabbakāmarogamutto, bhavarogabhesajjagū;</p>\r\n<p class="gathalast">Rāgarogavinimutto, buddhaṃ taṃ paṇamāmyahaṃ.253.</p>\r\n<p class="gatha1">Dukkhamutto sukhappatto, attamutto anattagū;</p>\r\n<p class="gathalast">Aniccamutto niccaññū, buddhaṃ taṃ paṇamāmyahaṃ.254.</p>\r\n<p class="gatha1">Kodhamutto kopamutto, klesamutto muttakuho;</p>\r\n<p class="gathalast">Chandamutto chalamutto, buddhaṃ taṃ paṇamāmyahaṃ.255.</p>\r\n<p class="gatha1">Māyāmutto mohamutto, makkhamutto muttamado;</p>\r\n<p class="gathalast">Mānamutto mucchāmutto, buddhaṃ taṃ paṇamāmyahaṃ.256.</p>\r\n<p class="gatha1">Dosamutto dohamutto, dessamutto muttadiso;</p>\r\n<p class="gathalast">Diṭṭhimutto aghamutto, buddhaṃ taṃ paṇamāmyahaṃ.257.</p>\r\n<p class="gatha1">Lobhamutto lolamutto, rosamutto muttaraṇo;</p>\r\n<p class="gathalast">Khobhamutto khedamutto, buddhaṃ taṃ paṇamāmyahaṃ.258.</p>\r\n<p class="gatha1">Bhogamutto sokamutto, rogamutto muttarajo;</p>\r\n<p class="gathalast">Sūlamutto sallamutto, buddhaṃ taṃ paṇamāmyahaṃ.259.</p>\r\n<p class="gatha1">Saṅgamutto raṅgamutto, rāgamutto muttaratī;</p>\r\n<p class="gathalast">Nandīmutto taṇhāmutto, buddhaṃ taṃ paṇamāmyahaṃ.260.</p>\r\n<p class="gatha1">Iñjāmutto indhāmutto, icchāmutto muttapiho;</p>\r\n<p class="gathalast">Edhamutto eḷamutto, buddhaṃ taṃ paṇamāmyahaṃ.261.</p>\r\n<p class="gatha1">Ītimutto bhītimutto, chambhamutto muttabhayo;</p>\r\n<p class="gathalast">Ādhimutto vyādhimutto, buddhaṃ taṃ paṇamāmyahaṃ.262.</p>\r\n<p class="gatha1">Gabbamutto dambhamutto, kūṭamutto muttadaro;</p>\r\n<p class="gathalast">Ḍāhamutto dāhamutto, buddhaṃ taṃ paṇamāmyahaṃ.263.</p>\r\n<p class="gatha1">Kamyamutto kiccamutto, bhantimutto muttabhamo;</p>\r\n<p class="gathalast">Vaṭṭamutto oghamutto, buddhaṃ taṃ paṇamāmyahaṃ.264.</p>\r\n<p class="gatha1">Jātimutto maccumutto, lokamutto muttabhavo;</p>\r\n<p class="gathalast">Nehamutto nettimutto, buddhaṃ taṃ paṇamāmyahaṃ.265.</p>\r\n<p class="gatha1">Āsāmutto issāmutto, ejāmutto muttaiṇo;</p>\r\n<p class="gathalast">Vānamutto pihāmutto, buddhaṃ taṃ paṇamāmyahaṃ.266.</p>\r\n<p class="gatha1">Sotamutto yogamutto, phassamutto muttaraso;</p>\r\n<p class="gathalast">Mūlamutto bhāramutto, buddhaṃ taṃ paṇamāmyahaṃ.267.</p>\r\n<p class="gatha1">Oramutto pāramutto, ghoramutto muttagaho;</p>\r\n<p class="gathalast">Kappamutto kālamutto, buddhaṃ taṃ paṇamāmyahaṃ.268.</p>\r\n<p class="gathalast">Pāpamutto puññamutto, ahaṃmutto muttamamo;</p>\r\n<p class="gathalast">Kaṅkhāmutto saṅkāmutto, buddhaṃ taṃ paṇamāmyahaṃ.269.</p>\r\n<p class="gathalast">Thīnamutto middhamutto, tāpamutto muttupayo;</p>\r\n<p class="gathalast">Muttuttāpo muttuddhacco, buddhaṃ taṃ paṇamāmyahaṃ.270.</p>\r\n<p class="gatha1">Khandhamutto bandhamutto, kalimutto muttamalo;</p>\r\n<p class="gathalast">Sabbathā pipāsāmutto, buddhaṃ taṃ paṇamāmyahaṃ.271.</p>\r\n<p class="gatha1">Vicāravitakkamutto, sadā sammāsamāhito;</p>\r\n<p class="gathalast">Sammāpaṇihitacitto, buddhaṃ taṃ paṇamāmyahaṃ.272.</p>\r\n<p class="gatha1">Anvāhatacittamutto, mutto sabbabhayehi ca;</p>\r\n<p class="gathalast">Bhīmamutto bhesmamutto, buddhaṃ taṃ paṇamāmyahaṃ.273.</p>\r\n<p class="gatha1">Yo paññattiṃ ṭhapetvāna, paramatthassa dassano;</p>\r\n<p class="gathalast">Vipallāsā vippamutto, buddhaṃ taṃ paṇamāmyahaṃ.274.</p>\r\n<p class="gatha1">Abhāvitacittamutto, mutto sabbadukkhehi ca;</p>\r\n<p class="gathalast">Sabbūpasaggā vimutto, buddhaṃ taṃ paṇamāmyahaṃ.275.</p>\r\n<p class="gatha1">Ākulacittavimutto, sabbadā thiramānaso;</p>\r\n<p class="gathalast">Aggalacittavimutto, buddhaṃ taṃ paṇamāmyahaṃ.276.</p>\r\n<p class="gatha1">Abbudacittavimutto, sadā bhāvitamānaso;</p>\r\n<p class="gathalast">Pamādacittapamutto, buddhaṃ taṃ paṇamāmyahaṃ.277.</p>\r\n<p class="gatha1">Upadhicittavimutto, sadā niyyānamānaso;</p>\r\n<p class="gathalast">Ussukacittavimutto, buddhaṃ taṃ paṇamāmyahaṃ.278.</p>\r\n<p class="gatha1">Apekkhācittavimutto, sadā cittanirālayo;</p>\r\n<p class="gathalast">Ārambhacittavimutto, buddhaṃ taṃ paṇamāmyahaṃ.279.</p>\r\n<p class="gatha1">Sammāvimutto sambuddho, suvimuttacitto munī;</p>\r\n<p class="gathalast">Sammadaññāparimutto, buddhaṃ taṃ paṇamāmyahaṃ.280.</p>\r\n<p class="gatha1">Asaṅkiliṭṭhacitto yo, mutto sabbabhavehi ca;</p>\r\n<p class="gathalast">Abhinivesābhimutto, buddhaṃ taṃ paṇamāmyahaṃ.281.</p>\r\n<p class="gatha1">Tilokatimirā mutto, paññappabhāpabhāsako;</p>\r\n<p class="gathalast">Tibhavataṇhā nittiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.282.</p>\r\n<p class="gatha1">Pamadappamutto byatto, bhāvanāratamānaso;</p>\r\n<p class="gathalast">Bhavaṇṇavavinimutto, buddhaṃ taṃ paṇamāmyahaṃ.283.</p>\r\n<p class="gatha1">Saṃsārasaṅkhārā mutto, vikāramuttamānaso;</p>\r\n<p class="gathalast">Navasamussayamutto, buddhaṃ taṃ paṇamāmyahaṃ.284.</p>\r\n<p class="gatha1">Jātivyādhijarāmutto, maraṇamutto mārajī;</p>\r\n<p class="gathalast">Punabbhavavinimutto, buddhaṃ taṃ paṇamāmyahaṃ.285.</p>\r\n<p class="gatha1">Asuciasuddhimutto, niccanimmalamānaso;</p>\r\n<p class="gathalast">Subhāvitacitto suddho, buddhaṃ taṃ paṇamāmyahaṃ.286.</p>\r\n<p class="gatha1">Avijjāndhakāramutto, vijjālokapakāsako;</p>\r\n<p class="gathalast">Anavasesañāṇaññū, buddhaṃ taṃ paṇamāmyahaṃ.287.</p>\r\n<p class="gatha1">Āsattimutto alitto, āsajjāmuttacetaso;</p>\r\n<p class="gathalast">Mohamānavinimutto, buddhaṃ taṃ paṇamāmyahaṃ.288.</p>\r\n<p class="gatha1">Bhavabhogavippamutto, bhavasaṃyojanacchido;</p>\r\n<p class="gathalast">Bhavabhītibhayātīto, buddhaṃ taṃ paṇamāmyahaṃ.289.</p>\r\n<p class="gatha1">Kopakodhavinimutto, dosadohaṃ visosayī;</p>\r\n<p class="gathalast">Sokasallaṃ vicuṇṇesi, buddhaṃ taṃ paṇamāmyahaṃ.290.</p>\r\n<p class="gatha1">Bījamutto balayutto, subujjhitā subuddhimā;</p>\r\n<p class="gathalast">Gedhāmutto medhāyutto, buddhaṃ taṃ paṇamāmyahaṃ.291.</p>\r\n<p class="gatha1">Sabbabhavābādhamutto, muttamahāmohatamo;</p>\r\n<p class="gathalast">Bālyasaṭhatāvimutto, buddhaṃ taṃ paṇamāmyahaṃ.292.</p>\r\n<p class="gatha1">Tasamutto tāsamutto, santāsamutto sabbathā;</p>\r\n<p class="gathalast">Sabbatassanavimutto, buddhaṃ taṃ paṇamāmyahaṃ.293.</p>\r\n<p class="gatha1">Bhavanettiyā vimutto, bhavoghamuttamānaso;</p>\r\n<p class="gathalast">Sabbattha saṃyogamutto, buddhaṃ taṃ paṇamāmyahaṃ.294.</p>\r\n<p class="gatha1">Sabbakāmayogamutto, sabbakāmagginibbuto;</p>\r\n<p class="gathalast">Sabbakāmaratimutto, buddhaṃ taṃ paṇamāmyahaṃ.295.</p>\r\n<p class="gatha1">Sabbesānaṃ jaṭāmutto, vimutto sabbāsattiyā;</p>\r\n<p class="gathalast">Sabbānusayāmutto, buddhaṃ taṃ paṇamāmyahaṃ.296.</p>\r\n<p class="gatha1">Sabbāsavā vinimutto, sadā alīnamānaso;</p>\r\n<p class="gathalast">Sabbāsajjā vippamutto, buddhaṃ taṃ paṇamāmyahaṃ.297.</p>\r\n<p class="gatha1">Sabbātaṅkavinimutto, sīho abhayamānaso;</p>\r\n<p class="gathalast">Sabbathā tassanamutto, buddhaṃ taṃ paṇamāmyahaṃ.298.</p>\r\n<p class="gatha1">Sabbāsaṅkā vinimutto, anavassutamānaso;</p>\r\n<p class="gathalast">Sabbaṭhāne kicchāmutto, buddhaṃ taṃ paṇamāmyahaṃ.299.</p>\r\n<p class="gatha1">Sabbālambavinimutto, sabbathā bhavabhañjako;</p>\r\n<p class="gathalast">Sabbānusayā vicchinno, buddhaṃ taṃ paṇamāmyahaṃ.300.</p>\r\n<p class="gatha1">Sabbāpekkhā vippamutto, sadā alittacetaso;</p>\r\n<p class="gathalast">Sabbābhijjhā vinimutto, buddhaṃ taṃ paṇamāmyahaṃ.301.</p>\r\n<p class="gatha1">Iñjanāindhanāmutto, muttomohamadehi ca;</p>\r\n<p class="gathalast">Sabbalobhavinimutto, buddhaṃ taṃ paṇamāmyahaṃ.302.</p>\r\n<p class="gatha1">Sabbākkosā vinimutto, sampuṇṇamettamānaso;</p>\r\n<p class="gathalast">Kuppanakujjhanamutto, buddhaṃ taṃ paṇamāmyahaṃ.303.</p>\r\n<p class="gatha1">Sabbathā saṅkopamutto, byāpādamutto sabbadā;</p>\r\n<p class="gathalast">Sabbadhi paṭighamutto, buddhaṃ taṃ paṇamāmyahaṃ.304.</p>\r\n<p class="gatha1">Sabbābhilāsā vimutto, ākaṅkhāmutto sabbathā;</p>\r\n<p class="gathalast">Sabbalālasā vimutto, buddhaṃ taṃ paṇamāmyahaṃ.305.</p>\r\n<p class="gatha1">Virodhānurodhamutto, saṅkhobhamutto sabbadā;</p>\r\n<p class="gathalast">Sabbānutāpā sumutto, buddhaṃ taṃ paṇamāmyahaṃ.306.</p>\r\n<p class="gatha1">Sabbadā sammosamutto, sammohamutto sabbathā;</p>\r\n<p class="gathalast">Sabbadhi avijjāmutto, buddhaṃ taṃ paṇamāmyahaṃ.307.</p>\r\n<p class="gatha1">Sabbaṭhāne vibbhamamutto, vibbhantimutto sabbadhi;</p>\r\n<p class="gathalast">Sabbavipallāsamutto, buddhaṃ taṃ paṇamāmyahaṃ.308.</p>\r\n<p class="gatha1">Kaṭukakasāyāmutto, kakkasamutto sabbathā;</p>\r\n<p class="gathalast">Sabbadā kalusāmutto, buddhaṃ taṃ paṇamāmyahaṃ.309.</p>\r\n<p class="gatha1">Sabbassādanā vimutto, saṃsaṭṭhamutto sabbadā;</p>\r\n<p class="gathalast">Āsajjanā vinimutto, buddhaṃ taṃ paṇamāmyahaṃ.310.</p>\r\n<p class="gatha1">Ahaṅkārā vinimutto, mamaṅkārā mutto munī;</p>\r\n<p class="gathalast">Mānāvamānā vimutto, buddhaṃ taṃ paṇamāmyahaṃ.311.</p>\r\n<p class="gatha1">Sabbaganthavinimutto, palighamutto sabbadā;</p>\r\n<p class="gathalast">Sabbābhibhū sabbamutto, buddhaṃ taṃ paṇamāmyahaṃ.312.</p>\r\n<p class="gatha1">Sabbasārambhasumutto, papañcamutto sabbadā;</p>\r\n<p class="gathalast">Sabbūpadhi vinimutto, buddhaṃ taṃ paṇamāmyahaṃ.313.</p>\r\n<p class="gatha1">Kohaññakoṭillamutto, cāpallamutto sabbathā;</p>\r\n<p class="gathalast">Sabbathā viddesamutto, buddhaṃ taṃ paṇamāmyahaṃ.314.</p>\r\n<p class="gatha1">Domanassā vinimutto, paḷāsamuttamānaso;</p>\r\n<p class="gathalast">Sabbasantāpā sumutto, buddhaṃ taṃ paṇamāmyahaṃ.315.</p>\r\n<p class="gatha1">Sabbābhimānā vimutto, sabbatāpehi muccako;</p>\r\n<p class="gathalast">Sabbadhi vikkhobhamutto, buddhaṃ taṃ paṇamāmyahaṃ.316.</p>\r\n<p class="gatha1">Sabbanīvaraṇāmutto, siddho ekaggamānaso;</p>\r\n<p class="gathalast">Sammāpañño sammāmutto, buddhaṃ taṃ paṇamāmyahaṃ.317.</p>\r\n<p class="gatha1">Sabbāvilā vippamutto, suddho saṃsuddhacetaso;</p>\r\n<p class="gathalast">Kasāvakalaṅkamutto, buddhaṃ taṃ paṇamāmyahaṃ.318.</p>\r\n<p class="gatha1">Sabbabyāpādappamutto, sadā pasādamānaso;</p>\r\n<p class="gathalast">Sabbūpanāhā vimutto, buddhaṃ taṃ paṇamāmyahaṃ.319.</p>\r\n<p class="gatha1">Sabbathāpamādamutto, sampajāno satiyuto;</p>\r\n<p class="gathalast">Bhāvanābhirato yogī, buddhaṃ taṃ paṇamāmyahaṃ.320.</p>\r\n<p class="gatha1">Sabbābhisajjanā mutto, sadā kāruññamānaso;</p>\r\n<p class="gathalast">Sabbupārambhavimutto, buddhaṃ taṃ paṇamāmyahaṃ.321.</p>\r\n<p class="gatha1">Sabbadhi rosarahito, sabbadhi karuṇānidhī;</p>\r\n<p class="gathalast">Sabbasāvajjā vimutto, buddhaṃ taṃ paṇamāmyahaṃ.322.</p>\r\n<p class="gatha1">Sabbabādhāparimutto, bandhamutto ca sabbathā;</p>\r\n<p class="gathalast">Kaṅkhicchākaṇṭakamutto, buddhaṃ taṃ paṇamāmyahaṃ.323.</p>\r\n<p class="gatha1">Sabbārambhavinimutto, sadā samitamānaso;</p>\r\n<p class="gathalast">Khambhanākhobhanā mutto, buddhaṃ taṃ paṇamāmyahaṃ.324.</p>\r\n<p class="gatha1">Sabbasaṅkhatā sumutto, asaṅkhata sudassano;</p>\r\n<p class="gathalast">Uddhaccakukkuccāmutto, buddhaṃ taṃ paṇamāmyahaṃ.325.</p>\r\n<p class="gatha1">Sabbabyasanavimutto, abyāsatta sumānaso;</p>\r\n<p class="gathalast">Sabbasaṃsaraṇamutto, buddhaṃ taṃ paṇamāmyahaṃ.326.</p>\r\n<p class="gatha1">Sabbupādānapamutto, mutto sabbamadehi ca;</p>\r\n<p class="gathalast">Sabbāyatanehi suñño, buddhaṃ taṃ paṇamāmyahaṃ.327.</p>\r\n<p class="gatha1">Sabbapāsehi pamutto, yo vippasannamānaso;</p>\r\n<p class="gathalast">Sabbamānānusayāmutto, buddhaṃ taṃ paṇamāmyahaṃ.328.</p>\r\n<p class="gatha1">Sabbanehavinimutto, sadā alittacetaso;</p>\r\n<p class="gathalast">Sabbāsativinimutto, buddhaṃ taṃ paṇamāmyahaṃ.329.</p>\r\n<p class="gatha1">Cittiñjanā vinimutto, sadā cittanirindhano;</p>\r\n<p class="gathalast">Sabbāsavā vippamutto, buddhaṃ taṃ paṇamāmyahaṃ.330.</p>\r\n<p class="gatha1">Sabbadā akampacitto, thiravimuttamānaso;</p>\r\n<p class="gathalast">Sabbavikkhepavimutto, buddhaṃ taṃ paṇamāmyahaṃ.331.</p>\r\n<p class="gatha1">Bhavasnehā vippamutto, vimutto tibhavodadhi;</p>\r\n<p class="gathalast">Sabbānugiddhisumutto, buddhaṃ taṃ paṇamāmyahaṃ.332.</p>\r\n<p class="gatha1">Bhavacakkavinimutto, sudanto bahusaṃyato;</p>\r\n<p class="gathalast">Visaṅkhāragatacitto, buddhaṃ taṃ paṇamāmyahaṃ.333.</p>\r\n<p class="gatha1">Sabbathā bhaviñjāmutto, vantalokāmiso isi;</p>\r\n<p class="gathalast">Dhāresi antimaṃ dehaṃ, buddhaṃ taṃ paṇamāmyahaṃ.334.</p>\r\n<p class="gatha1">Sabbābhijjhāvinimutto, santuṭṭho suddhamānaso;</p>\r\n<p class="gathalast">Saṃsārasāgarā mutto, buddhaṃ taṃ paṇamāmyahaṃ.335.</p>\r\n<p class="gatha1">Sabbadhi sabbato mutto, sabbaññāto sabbañjayo;</p>\r\n<p class="gathalast">Sabbathā mutto subhaddo, buddhaṃ taṃ paṇamāmyahaṃ.336.</p>\r\n<p class="gatha1">Sabbantarāyavimutto, aggalamutto sabbadhi;</p>\r\n<p class="gathalast">Sabbabyavadhānamutto, buddhaṃ taṃ paṇamāmyahaṃ.337.</p>\r\n<p class="gatha1">Sabbatāpavinimutto, sītalo sītimānaso;</p>\r\n<p class="gathalast">Bhavarāgaggivimutto, buddhaṃ taṃ paṇamāmyahaṃ.338.</p>\r\n<p class="gatha1">Vaṅkamutto paṅkamutto,?..Dhamutto muttamano;</p>\r\n<p class="gathalast">Sabbadā ātaṅkamutto, buddhaṃ taṃ paṇamāmyahaṃ.339.</p>\r\n<p class="gatha1">Sabbathā saṃsayamutto, kampamutto visārado;</p>\r\n<p class="gathalast">Aggiidhumavimutto, buddhaṃ taṃ paṇamāmyahaṃ.340.</p>\r\n<p class="gatha1">Sabbadā caṇḍikkamutto, dussanamutto sabbathā;</p>\r\n<p class="gathalast">Sabbadhi sampuṇṇamutto, buddhaṃ taṃ paṇamāmyahaṃ.341.</p>\r\n<p class="gatha1">Ujucitto sujūcitto, samacitto samācaro;</p>\r\n<p class="gathalast">Dhīticitto vatacitto, buddhaṃ taṃ paṇamāmyahaṃ.342.</p>\r\n<p class="gatha1">Alobhaadosacitto, alaggacitto sobhano;</p>\r\n<p class="gathalast">Amohacitto dhīcitto, buddhaṃ taṃ paṇamāmyahaṃ.343.</p>\r\n<p class="gatha1">Sādhucitto suddhacitto, sotthicitto satiyuto;</p>\r\n<p class="gathalast">Cārucitto sivacitto, buddhaṃ taṃ paṇamāmyahaṃ.344.</p>\r\n<p class="gatha1">Dhammacitto atthacitto, dhīracitto cittuttamo;</p>\r\n<p class="gathalast">Hiricitto siricitto, buddhaṃ taṃ paṇamāmyahaṃ.345.</p>\r\n<p class="gatha1">Hitacitto sukhacitto, kalyāṇacitto kāruñño;</p>\r\n<p class="gathalast">Pīticitto sīticitto, buddhaṃ taṃ paṇamāmyahaṃ.346.</p>\r\n<p class="gatha1">Paramavisuddhacitto, muttacitto yativaro;</p>\r\n<p class="gathalast">Yatacitto vatacitto, buddhaṃ taṃ paṇamāmyahaṃ.347.</p>\r\n<p class="gatha1">Haṭṭhacitto tuṭṭhacitto, seṭṭhacitto suṭṭhuttaro;</p>\r\n<p class="gathalast">Santuṭṭhacitto pahaṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.348.</p>\r\n<p class="gatha1">Saticitto yaticitto, sampajañño viriyavā;</p>\r\n<p class="gathalast">Sumaticitto sappañño, buddhaṃ taṃ paṇamāmyahaṃ.349.</p>\r\n<p class="gatha1">Mettacitto dayācitto, karuṇacitto kevalī;</p>\r\n<p class="gathalast">Modito maddavacitto, buddhaṃ taṃ paṇamāmyahaṃ.350.</p>\r\n<p class="gatha1">Saccacitto tathacitto, suguṇacitto sīlavā;</p>\r\n<p class="gathalast">Nillobhacitto sucitto, buddhaṃ taṃ paṇamāmyahaṃ.351.</p>\r\n<p class="gatha1">Niddosacitto niddhoto, nimmohacitto nillīno;</p>\r\n<p class="gathalast">Nimmalacitto nissaṅgo, buddhaṃ taṃ paṇamāmyahaṃ.352.</p>\r\n<p class="gatha1">Dibbacitto bhabbacitto, dammacitto dasabalo;</p>\r\n<p class="gathalast">Bhabbabhāvarato bhabbo, buddhaṃ taṃ paṇamāmyahaṃ.353.</p>\r\n<p class="gatha1">Ñāṇacitto medhācitto, vijjācitto vijjādhanī;</p>\r\n<p class="gathalast">Paññācitto aññācitto, buddhaṃ taṃ paṇamāmyahaṃ.354.</p>\r\n<p class="gatha1">Ditticitto juticitto, juṇhacitto ālokado;</p>\r\n<p class="gathalast">Kanticitto khanticitto, buddhaṃ taṃ paṇamāmyahaṃ.355.</p>\r\n<p class="gatha1">Amalo odātacitto, pasannacitto pesalo;</p>\r\n<p class="gathalast">Pāvano punītacitto, buddhaṃ taṃ paṇamāmyahaṃ.356.</p>\r\n<p class="gatha1">Javacitto balacitto, thāmacitto visārado;</p>\r\n<p class="gathalast">Ṭhitacitto thiracitto, buddhaṃ taṃ paṇamāmyahaṃ.357.</p>\r\n<p class="gatha1">Ojacitto tejacitto, yodhacitto avicalo;</p>\r\n<p class="gathalast">Nibbhayacitto nipphando, buddhaṃ taṃ paṇamāmyahaṃ.358.</p>\r\n<p class="gatha1">Paguṇo supaññacitto, saṃyatacitto saṃvuto;</p>\r\n<p class="gathalast">Pasannacitto passaddho, buddhaṃ taṃ paṇamāmyahaṃ.359.</p>\r\n<p class="gatha1">Bhaddacitto mokkhacitto, sabbaseṭṭho sattuttamo;</p>\r\n<p class="gathalast">Somanassacitto santo, buddhaṃ taṃ paṇamāmyahaṃ.360.</p>\r\n<p class="gatha1">Santacitto dantacitto, vasīcitto vusitavā;</p>\r\n<p class="gathalast">Sucicitto rucicitto, buddhaṃ taṃ paṇamāmyahaṃ.361.</p>\r\n<p class="gatha1">Guttacitto muttacitto, pasannacitto cittujū;</p>\r\n<p class="gathalast">Sumanacitto sucitto, buddhaṃ taṃ paṇamāmyahaṃ.362.</p>\r\n<p class="gatha1">Santacitto sadāsanto, sadā santusito isi;</p>\r\n<p class="gathalast">Sabbattha saṃvuto sāmī, buddhaṃ taṃ paṇamāmyahaṃ.363.</p>\r\n<p class="gatha1">Rittakāmo rittataṇho, rittarāgo rittarajo;</p>\r\n<p class="gathalast">Rittachando rittanandī, buddhaṃ taṃ paṇamāmyahaṃ.364.</p>\r\n<p class="gatha1">Rittavero rittaroso, rittuttāpo rittāvilo;</p>\r\n<p class="gathalast">Rittakodho rittakopo, buddhaṃ taṃ paṇamāmyahaṃ.365.</p>\r\n<p class="gatha1">Saṅgaritto raṅgaritto, rāgaritto rittaratī;</p>\r\n<p class="gathalast">Nandīritto taṇhāritto, buddhaṃ taṃ paṇamāmyahaṃ.366.</p>\r\n<p class="gatha1">Sabbabhavābādharitto, rittamahāmohatamo;</p>\r\n<p class="gathalast">Sabbathā avijjāritto, buddhaṃ taṃ paṇamāmyahaṃ.367.</p>\r\n<p class="gatha1">Nandīsaṃyojanaritto, sabbasaṅgapariccajo;</p>\r\n<p class="gathalast">Uttamatthaṃ hatthagato, buddhaṃ taṃ paṇamāmyahaṃ.368.</p>\r\n<p class="gatha1">Iñjāritto indhāritto, icchāritto rittaagho;</p>\r\n<p class="gathalast">Edharitto gedharitto, buddhaṃ taṃ paṇamāmyahaṃ.369.</p>\r\n<p class="gatha1">Antagato antappatto, pārappatto pāraṅgato;</p>\r\n<p class="gathalast">Koṭigato koṭippatto, buddhaṃ taṃ paṇamāmyahaṃ.370.</p>\r\n<p class="gatha1">Khemappatto khemaṅgato, aggappatto aggaṅgato;</p>\r\n<p class="gathalast">Sivappatto sivaṅgato, buddhaṃ taṃ paṇamāmyahaṃ.371.</p>\r\n<p class="gatha1">Padappatto dhuvappatto, parisuddhippatto pabhū;</p>\r\n<p class="gathalast">Maggappatto phalappatto, buddhaṃ taṃ paṇamāmyahaṃ.372.</p>\r\n<p class="gatha1">Sārappatto pāragato, pariyantappatto varo;</p>\r\n<p class="gathalast">Pariyodātasampatto, buddhaṃ taṃ paṇamāmyahaṃ.373.</p>\r\n<p class="gatha1">Vimalo vodātappatto, nibbānappatto nimmalo;</p>\r\n<p class="gathalast">Amalo amatappatto, buddhaṃ taṃ paṇamāmyahaṃ.374.</p>\r\n<p class="gatha1">Dhammappatto dhammayogī, kusalappatto kevalī;</p>\r\n<p class="gathalast">Lokuttarappatto pujjo, buddhaṃ taṃ paṇamāmyahaṃ.375.</p>\r\n<p class="gatha1">Pattavijjo pattapañño, patto sambodhimuttamaṃ;</p>\r\n<p class="gathalast">Pattasanti pattasukho, buddhaṃ taṃ paṇamāmyahaṃ.376.</p>\r\n<p class="gatha1">Pattadhammo pattabodhi, pattamaggo pattaphalo;</p>\r\n<p class="gathalast">Pattaamato pattattho, buddhaṃ taṃ paṇamāmyahaṃ.377.</p>\r\n<p class="gatha1">Paramatthasaccaṃ patto, pattakhemo pattasivo;</p>\r\n<p class="gathalast">Pattaaccuto pattaggo, buddhaṃ taṃ paṇamāmyahaṃ.378.</p>\r\n<p class="gatha1">Pattaanomo pattiṭṭho, pattadhuvo pattakkhayo;</p>\r\n<p class="gathalast">Pattalokuttaradhammo, buddhaṃ taṃ paṇamāmyahaṃ.379.</p>\r\n<p class="gatha1">Pattañāṇo pattamedho, pattadhammaniyāmako;</p>\r\n<p class="gathalast">Pattaananto pattanto, buddhaṃ taṃ paṇamāmyahaṃ.380.</p>\r\n<p class="gatha1">Pattavisuddhi pattiddhi, pattapparamatthapado;</p>\r\n<p class="gathalast">Pattanipuṇatthadhammo, buddhaṃ taṃ paṇamāmyahaṃ.381.</p>\r\n<p class="gatha1">Pattakanti pattavaṇṇo, pattakitti pattayaso;</p>\r\n<p class="gathalast">Pattapassiddhi pakhyāto, buddhaṃ taṃ paṇamāmyahaṃ.382.</p>\r\n<p class="gatha1">Sabbakappaparikkhīṇo, parimutto patāpavā;</p>\r\n<p class="gathalast">Saṃsāroghapārappatto, buddhaṃ taṃ paṇamāmyahaṃ.383.</p>\r\n<p class="gatha1">Nayappatto vasīppatto, sacchikaraṇapāragū;</p>\r\n<p class="gathalast">Niyyānaṃ susacchikato, buddhaṃ taṃ paṇamāmyahaṃ.384.</p>\r\n<p class="gatha1">Bhārabhañjako bhadanto, bhuvanabhogabhindako;</p>\r\n<p class="gathalast">Bhavamahaṇṇavaṃ tiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.385.</p>\r\n<p class="gatha1">Dukkare bodhissambhāre, pūretvāna asesato;</p>\r\n<p class="gathalast">Patto sabbaññutaṃ ñāṇaṃ, buddhaṃ taṃ paṇamāmyahaṃ.386.</p>\r\n<p class="gatha1">Santipatto santibhūto, sītalībhūto sabbadhi;</p>\r\n<p class="gathalast">Paramaṃ sukhaṃ sampatto, buddhaṃ taṃ paṇamāmyahaṃ.387.</p>\r\n<p class="gatha1">Pannāsajjo pannāsatto, pannālambo pannālayo;</p>\r\n<p class="gathalast">Pannasūlo pannasallo, buddhaṃ taṃ paṇamāmyahaṃ.388.</p>\r\n<p class="gatha1">Pannāsaṅko pannātaṅko, pannākaṅkho pannaṅgaṇo;</p>\r\n<p class="gathalast">Pannāpekkho pannābhijjho, buddhaṃ taṃ paṇamāmyahaṃ.389.</p>\r\n<p class="gatha1">Pannabhāro katakicco, puṇṇaabhiññā vosito;</p>\r\n<p class="gathalast">Sabbavosita vosānaṃ, buddhaṃ taṃ paṇamāmyahaṃ.390.</p>\r\n<p class="gatha1">Manogutto vacīgutto, kāyagutto guttivaro;</p>\r\n<p class="gathalast">Guttakammo guttadhammo, buddhaṃ taṃ paṇamāmyahaṃ.391.</p>\r\n<p class="gatha1">Cittagutto cittadanto, saṃtata citta saṃyato;</p>\r\n<p class="gathalast">Cittamutto cittasanto, buddhaṃ taṃ paṇamāmyahaṃ.392.</p>\r\n<p class="gatha1">Indriyagutto sugutto, attapaccakkho āsabho;</p>\r\n<p class="gathalast">Pacurapaññāsampanno, buddhaṃ taṃ paṇamāmyahaṃ.393.</p>\r\n<p class="gatha1">Kāyagutto vacīgutto, cittagutto yo guttatto;</p>\r\n<p class="gathalast">Sabbindriyagutto sāmī, buddhaṃ taṃ paṇamāmyahaṃ.394.</p>\r\n<p class="gatha1">Dhīrahadayo dhorayho, saṃvuto saṃyatamano;</p>\r\n<p class="gathalast">Guttindriyo guttamāno, buddhaṃ taṃ paṇamāmyahaṃ.395.</p>\r\n<p class="gatha1">Dhitiyutto matiyutto, nītiyutto yuttanayo;</p>\r\n<p class="gathalast">Maggayutto phalayutto, buddhaṃ taṃ paṇamāmyahaṃ.396.</p>\r\n<p class="gatha1">Maggaladdho aggaladdho, antaladdho laddhapatho;</p>\r\n<p class="gathalast">Paramatthasaccaladdho, buddhaṃ taṃ paṇamāmyahaṃ.397.</p>\r\n<p class="gatha1">Iṭṭhaladdho siddhaladdho, sītiladdho laddhasivo;</p>\r\n<p class="gathalast">Dullabhanibbānaladdho, buddhaṃ taṃ paṇamāmyahaṃ.398.</p>\r\n<p class="gatha1">Dhammaladdho atthaladdho, muttiladdho laddhatatho;</p>\r\n<p class="gathalast">Paladdhatiṃsapāramī, buddhaṃ taṃ paṇamāmyahaṃ.399.</p>\r\n<p class="gatha1">Santiladdho khantiladdho, kantiladdho laddhayaso;</p>\r\n<p class="gathalast">Dittiladdho kittiladdho, buddhaṃ taṃ paṇamāmyahaṃ.400.</p>\r\n<p class="gatha1">Dibbaladdho bhabbaladdho, sabbaladdho laddhabalo;</p>\r\n<p class="gathalast">Sududdasaṃ sivaṃ laddho, buddhaṃ taṃ paṇamāmyahaṃ.401.</p>\r\n<p class="gatha1">Suddhiladdho buddhiladdho, iddhiladdho laddhanidhī;</p>\r\n<p class="gathalast">Upaladdhachaḷabhiñño, buddhaṃ taṃ paṇamāmyahaṃ.402.</p>\r\n<p class="gatha1">Kalyāṇaṃ kusalaṃ laddho, pītiladdho laddhasukho;</p>\r\n<p class="gathalast">Upaladdhaamatasindhū, buddhaṃ taṃ paṇamāmyahaṃ.403.</p>\r\n<p class="gatha1">Upaladdhayogakkhemo, sotthiladdho laddhadhuvo;</p>\r\n<p class="gathalast">Dasabalaladdho vīro, buddhaṃ taṃ paṇamāmyahaṃ.404.</p>\r\n<p class="gatha1">Bodhiladdho ñāṇaladdho, medhāladdho laddhanayo;</p>\r\n<p class="gathalast">Paññāladdho vijjāladdho, buddhaṃ taṃ paṇamāmyahaṃ.405.</p>\r\n<p class="gatha1">Sumatiladdho dhīladdho, laddhavimuttisampattī;</p>\r\n<p class="gathalast">Parama’pavaggaladdho, buddhaṃ taṃ paṇamāmyahaṃ.406.</p>\r\n<p class="gatha1">Punītaṃ pavittaṃladdho, laddhauttamamaṅgalo;</p>\r\n<p class="gathalast">Paramaṃ avayaṃ laddho, buddhaṃ taṃ paṇamāmyahaṃ.407.</p>\r\n<p class="gatha1">Ukkaṭṭhaṃ kevalaṃ laddho, sumuttiladdho sabbato;</p>\r\n<p class="gathalast">Ananta’nītikaṃ laddho, buddhaṃ taṃ paṇamāmyahaṃ.408.</p>\r\n<p class="gatha1">Sattabojjhaṅgupaladdho, laddhaabhiñño sabbato;</p>\r\n<p class="gathalast">Bhavataṇhakkhayaṃ laddho, buddhaṃ taṃ paṇamāmyahaṃ.409.</p>\r\n<p class="gatha1">Parisuddhapanthaladdho, saccaladdho anūpamo;</p>\r\n<p class="gathalast">Paripuṇṇadhammaladdho, buddhaṃ taṃ paṇamāmyahaṃ.410.</p>\r\n<p class="gatha1">Sampuṇṇapariññāladdho, aññāladdho sabbuttamo;</p>\r\n<p class="gathalast">Paṭisambhidāsuladdho, buddhaṃ taṃ paṇamāmyahaṃ.411.</p>\r\n<p class="gatha1">Paccakkhavimuttiladdho, laddhachaḷabhiññāvaro;</p>\r\n<p class="gathalast">Paripuṇṇasantiladdho, buddhaṃ taṃ paṇamāmyahaṃ.412.</p>\r\n<p class="gatha1">Yathābhūtasaccaladdho, tathatāladdho sabbadhi;</p>\r\n<p class="gathalast">Samantasamatāladdho, buddhaṃ taṃ paṇamāmyahaṃ.413.</p>\r\n<p class="gatha1">Nimmalacakkhupaladdho, vimalavijjāvāridhi;</p>\r\n<p class="gathalast">Nipuṇatthadhammaladdho, buddhaṃ taṃ paṇamāmyahaṃ.414.</p>\r\n<p class="gatha1">Thāmaladdho javaladdho, samaladdho patāpavā;</p>\r\n<p class="gathalast">Lokuttarasaccaladdho, buddhaṃ taṃ paṇamāmyahaṃ.415.</p>\r\n<p class="gatha1">Dhitiladdho suciladdho, saṃsuddhiladdho suddhimā;</p>\r\n<p class="gathalast">Sudhāladdho sumedhāvī, buddhaṃ taṃ paṇamāmyahaṃ.416.</p>\r\n<p class="gatha1">Laddhaaddho bhaddaladdho, modaladdho laddhasubho;</p>\r\n<p class="gathalast">Sabbattha santosaladdho, buddhaṃ taṃ paṇamāmyahaṃ.417.</p>\r\n<p class="gatha1">Tīraladdho pāraladdho, sāraladdho laddhaphalo;</p>\r\n<p class="gathalast">Maṅgalamuttamaṃ laddho, buddhaṃ taṃ paṇamāmyahaṃ.418.</p>\r\n<p class="gatha1">Aṃsuladdho raṃsiladdho, ojaladdho laddhajutī;</p>\r\n<p class="gathalast">Uggaaggaābhāladdho, buddhaṃ taṃ paṇamāmyahaṃ.419.</p>\r\n<p class="gatha1">Pasaṃsāpasiddhiladdho, visesavissavissuto;</p>\r\n<p class="gathalast">Pūjanaṃ thomanaṃ laddho, buddhaṃ taṃ paṇamāmyahaṃ.420.</p>\r\n<p class="gatha1">Mohandhakāravimutto, ālokaladdho lokajī;</p>\r\n<p class="gathalast">Appameyyappabhāladdho, buddhaṃ taṃ paṇamāmyahaṃ.421.</p>\r\n<p class="gatha1">Visallo kusalo satthā, kammākammassakovido;</p>\r\n<p class="gathalast">Pāragū sabbakammānaṃ, buddhaṃ taṃ paṇamāmyahaṃ.422.</p>\r\n<p class="gatha1">Kusalassa kovido ca, akusalassa kovido;</p>\r\n<p class="gathalast">Kusalassupasampanno, buddhaṃ taṃ paṇamāmyahaṃ.423.</p>\r\n<p class="gatha1">Dhammassa kovido cāpi, adhammassāpi kovido;</p>\r\n<p class="gathalast">Pāragū sabbadhammānaṃ, buddhaṃ taṃ paṇamāmyahaṃ.424.</p>\r\n<p class="gatha1">Puññassa kovido cāpi, apuññassāpi kovido;</p>\r\n<p class="gathalast">Pāpapuññamatikkanto, buddhaṃ taṃ paṇamāmyahaṃ.425.</p>\r\n<p class="gatha1">Maggassa kovido cāpi, amaggassāpi kovido;</p>\r\n<p class="gathalast">Aggamaggamugghāṭesi, buddhaṃ taṃ paṇamāmyahaṃ.426.</p>\r\n<p class="gatha1">Sukhassa kovido cāpi, dukkhassa cāpi kovido;</p>\r\n<p class="gathalast">Paramasukhadassāvī, buddhaṃ taṃ paṇamāmyahaṃ.427.</p>\r\n<p class="gatha1">Iṭṭhassa kovido cāpi, aniṭṭhassāpi kovido;</p>\r\n<p class="gathalast">Paramiṭṭhaṃ paññāpesi, buddhaṃ taṃ paṇamāmyahaṃ.428.</p>\r\n<p class="gatha1">Antassa kovido cāpi, anantassāpi kovido;</p>\r\n<p class="gathalast">Anantaobhāsaṃ sakkhī, buddhaṃ taṃ paṇamāmyahaṃ.429.</p>\r\n<p class="gatha1">Matassa kovido cāpi, amatassāpi kovido;</p>\r\n<p class="gathalast">Amatassasindhuladdho, buddhaṃ taṃ paṇamāmyahaṃ.430.</p>\r\n<p class="gatha1">Niccassa kovido cāpi, aniccassāpi kovido;</p>\r\n<p class="gathalast">Niccasaccaṃ pakāsesi, buddhaṃ taṃ paṇamāmyahaṃ.431.</p>\r\n<p class="gatha1">Bhavassa kovido cāpi, vibhavassāpi kovido;</p>\r\n<p class="gathalast">Sabbaso bhavaṃ bhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.432.</p>\r\n<p class="gatha1">Rāgassa kovido cāpi, arāgassāpi kovido;</p>\r\n<p class="gathalast">Sabbarāgaṃ virañjesi, buddhaṃ taṃ paṇamāmyahaṃ.433.</p>\r\n<p class="gatha1">Dosassa kovido cāpi, adosassāpi kovido;</p>\r\n<p class="gathalast">Sabbadosaṃ viddhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.434.</p>\r\n<p class="gatha1">Mohassa kovido cāpi, amohassāpi kovido;</p>\r\n<p class="gathalast">Sabbamohavītivatto, buddhaṃ taṃ paṇamāmyahaṃ.435.</p>\r\n<p class="gatha1">Verassa kovido cāpi, averassāpi kovido;</p>\r\n<p class="gathalast">Sabbaveramatikkanto, buddhaṃ taṃ paṇamāmyahaṃ.436.</p>\r\n<p class="gatha1">Klesassa kovido cāpi, niklesassāpi kovido;</p>\r\n<p class="gathalast">Sabbakilesaṃ jhāpesi, buddhaṃ taṃ paṇamāmyahaṃ.437.</p>\r\n<p class="gatha1">Tamassa kovido cāpi, ālokassāpi kovido;</p>\r\n<p class="gathalast">Loke ālokaṃ jotesi, buddhaṃ taṃ paṇamāmyahaṃ.438.</p>\r\n<p class="gatha1">Neḷassa kovido cāpi, aneḷassāpi kovido;</p>\r\n<p class="gathalast">Aneḷako akkileso, buddhaṃ taṃ paṇamāmyahaṃ.439.</p>\r\n<p class="gatha1">Saṅgassa kovido cāpi, asaṅgassāpi kovido;</p>\r\n<p class="gathalast">Sabbasaṅgavinimutto, buddhaṃ taṃ paṇamāmyahaṃ.440.</p>\r\n<p class="gatha1">Kovido ca vedanāya, avedanāya kovido;</p>\r\n<p class="gathalast">Sabbassa vedanātīto, buddhaṃ taṃ paṇamāmyahaṃ.441.</p>\r\n<p class="gatha1">Ārambhassa kovido ca, anārambhassa kovido;</p>\r\n<p class="gathalast">Sabbārambhapariccāgī, buddhaṃ taṃ paṇamāmyahaṃ.442.</p>\r\n<p class="gatha1">Ālayassa kovido ca, anālayassa kovido;</p>\r\n<p class="gathalast">Sabbālayavinimutto, buddhaṃ taṃ paṇamāmyahaṃ.443.</p>\r\n<p class="gatha1">Ālambassa kovido ca, anālambassa kovido;</p>\r\n<p class="gathalast">Sabbālambanā vigato, buddhaṃ taṃ paṇamāmyahaṃ.444.</p>\r\n<p class="gatha1">Taṇhāya kovido cāpi, vītataṇhāya kovido;</p>\r\n<p class="gathalast">Taṇhājālaṃ vidālesi, buddhaṃ taṃ paṇamāmyahaṃ.445.</p>\r\n<p class="gatha1">Mānassa kovido cāpi, avamānassa kovido;</p>\r\n<p class="gathalast">Mānāvamānasamako, buddhaṃ taṃ paṇamāmyahaṃ.446.</p>\r\n<p class="gatha1">Kāmassa kovido cāpi, nikkāmassāpi kovido;</p>\r\n<p class="gathalast">Sabbakāmaparikkhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.447.</p>\r\n<p class="gatha1">Kopassa kovido cāpi, nikkopassāpi kovido;</p>\r\n<p class="gathalast">Sabbakopagginibbuto, buddhaṃ taṃ paṇamāmyahaṃ.448.</p>\r\n<p class="gatha1">Kodhassa kovido cāpi, nikkodhassāpi kovido;</p>\r\n<p class="gathalast">Kodhānalaṃ nibbāpesi, buddhaṃ taṃ paṇamāmyahaṃ.449.</p>\r\n<p class="gatha1">Chandassa kovido cāpi, nicchandassāpi kovido;</p>\r\n<p class="gathalast">Sabbabhavacchandacchinno, buddhaṃ taṃ paṇamāmyahaṃ.450.</p>\r\n<p class="gatha1">Lobhassa kovido cāpi, nillobhassāpi kovido;</p>\r\n<p class="gathalast">Lobhalālasānillitto, buddhaṃ taṃ paṇamāmyahaṃ.451.</p>\r\n<p class="gatha1">Sokassa kovido cāpi, nissokassāpi kovido;</p>\r\n<p class="gathalast">Sokasūlaṃ abbāhesi, buddhaṃ taṃ paṇamāmyahaṃ.452.</p>\r\n<p class="gatha1">Snehassa kovido cāpi, nisnehassāpi kovido;</p>\r\n<p class="gathalast">Sabbasnehā vinimutto, buddhaṃ taṃ paṇamāmyahaṃ.453.</p>\r\n<p class="gatha1">Khobhassa kovido cāpi, nikkhobhassāpi kovido;</p>\r\n<p class="gathalast">Sabbakammakhobhakhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.454.</p>\r\n<p class="gatha1">Sallassa kovido cāpi, visallassāpi kovido;</p>\r\n<p class="gathalast">Sabbasallaṃ sandālesi, buddhaṃ taṃ paṇamāmyahaṃ.455.</p>\r\n<p class="gatha1">Khayassa kovido cāpi, akkhayassāpi kovido;</p>\r\n<p class="gathalast">Sabbathā akkhayaṃ patto, buddhaṃ taṃ paṇamāmyahaṃ.456.</p>\r\n<p class="gatha1">Lokassa kovido cāpi, lokantassāpi kovido;</p>\r\n<p class="gathalast">Lokuttaraṃ sacchikato, buddhaṃ taṃ paṇamāmyahaṃ.457.</p>\r\n<p class="gatha1">Suddhassa kovido cāpi, asuddhassāpi kovido;</p>\r\n<p class="gathalast">Suddhimaggaṃ visodhesi, buddhaṃ taṃ paṇamāmyahaṃ.458.</p>\r\n<p class="gatha1">Kālassa kovido cāpi, akālassāpi kovido;</p>\r\n<p class="gathalast">Kālānusārī desesi, buddhaṃ taṃ paṇamāmyahaṃ.459.</p>\r\n<p class="gatha1">Chalassa kovido cāpi, nicchalassāpi kovido;</p>\r\n<p class="gathalast">Sabbathā chalavimutto, buddhaṃ taṃ paṇamāmyahaṃ.460.</p>\r\n<p class="gatha1">Sumanassa kovido ca, dummanassāpi kovido;</p>\r\n<p class="gathalast">Sabbadhi sumano santo, buddhaṃ taṃ paṇamāmyahaṃ.461.</p>\r\n<p class="gatha1">Kovido lokacakkassa, dhammacakkassa kovido;</p>\r\n<p class="gathalast">Lokacakkaṃ viddhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.462.</p>\r\n<p class="gatha1">Tīrassa kovido cāpi, pārassa cāpi kovido;</p>\r\n<p class="gathalast">Ghoraṃ oghaṃ pāraṅgato, buddhaṃ taṃ paṇamāmyahaṃ.463.</p>\r\n<p class="gatha1">Bodhassa kovido cāpi, dubbodhassāpi kovido;</p>\r\n<p class="gathalast">Sabbathā sabbaṃ bodhesi, buddhaṃ taṃ paṇamāmyahaṃ.464.</p>\r\n<p class="gatha1">Yogassa kovido cāpi, khemassa cāpi kovido;</p>\r\n<p class="gathalast">Yogakkhemamanuppatto, buddhaṃ taṃ paṇamāmyahaṃ.465.</p>\r\n<p class="gatha1">Āvilassa kovido ca, anāvilassa kovido;</p>\r\n<p class="gathalast">Sabbāvilaṃ vidhopesi, buddhaṃ taṃ paṇamāmyahaṃ.466.</p>\r\n<p class="gatha1">Saṃsayassa kovido ca, asaṃsayassa kovido;</p>\r\n<p class="gathalast">Sabbattha saṃsayamutto, buddhaṃ taṃ paṇamāmyahaṃ.467.</p>\r\n<p class="gatha1">Sārajjassa kovido ca, visārajjassa kovido;</p>\r\n<p class="gathalast">Vesārajjañāṇupeto, buddhaṃ taṃ paṇamāmyahaṃ.468.</p>\r\n<p class="gatha1">Vikārassa kovido ca, nibbikārassa kovido;</p>\r\n<p class="gathalast">Vikārāni vikkhambhesi, buddhaṃ taṃ paṇamāmyahaṃ.469.</p>\r\n<p class="gatha1">Sammohassa kovido ca, asammohassa kovido;</p>\r\n<p class="gathalast">Sabbasammohā vimutto, buddhaṃ taṃ paṇamāmyahaṃ.470.</p>\r\n<p class="gatha1">Santāpassa kovido ca, asantāpassa kovido;</p>\r\n<p class="gathalast">Santāpānaṃ santikaro, buddhaṃ taṃ paṇamāmyahaṃ.471.</p>\r\n<p class="gatha1">Paṭighassa kovido ca, appaṭighassa kovido;</p>\r\n<p class="gathalast">Paṭighasaññāsuñño yo, buddhaṃ taṃ paṇamāmyahaṃ.472.</p>\r\n<p class="gatha1">Āsavassa kovido ca, anāsavassa kovido;</p>\r\n<p class="gathalast">Sabbāsavaparikkhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.473.</p>\r\n<p class="gatha1">Suguṇassa kovido ca, dugguṇassāpi kovido;</p>\r\n<p class="gathalast">Sabbasuguṇasaṃyutto, buddhaṃ taṃ paṇamāmyahaṃ.474.</p>\r\n<p class="gatha1">Kalyāṇassa kovido ca, akalyāṇassa kovido;</p>\r\n<p class="gathalast">Kalyāṇatthamanuppatto, buddhaṃ taṃ paṇamāmyahaṃ.475.</p>\r\n<p class="gatha1">Vipulapañño pabalo, maggāmaggassa kovido;</p>\r\n<p class="gathalast">Paramatthadhammaladdho, buddhaṃ taṃ paṇamāmyahaṃ.476.</p>\r\n<p class="gatha1">Cetosamathakovido, jhānasamādhikovido;</p>\r\n<p class="gathalast">Samāpattisamāpanno, buddhaṃ taṃ paṇamāmyahaṃ.477.</p>\r\n<p class="gatha1">Maggakovido maggaññū, dhammaññū dhammakovido;</p>\r\n<p class="gathalast">Aggakovido aggaññū, buddhaṃ taṃ paṇamāmyahaṃ.478.</p>\r\n<p class="gatha1">Sampajaññassa kovido, sammāsatiyā kovido;</p>\r\n<p class="gathalast">Satipaṭṭhāne kovido, buddhaṃ taṃ paṇamāmyahaṃ.479.</p>\r\n<p class="gatha1">Sattabojjhaṅgakovido, sabbavibhaṅgakovido;</p>\r\n<p class="gathalast">Vijjākovido tevijjo, buddhaṃ taṃ paṇamāmyahaṃ.480.</p>\r\n<p class="gatha1">Cetovimuttikovido, paññāvimuttikovido;</p>\r\n<p class="gathalast">Ubhatovimuttippatto, buddhaṃ taṃ paṇamāmyahaṃ.481.</p>\r\n<p class="gatha1">Iddhipādesu kovido, cetopariyakovido;</p>\r\n<p class="gathalast">Chaḷabhiññāhi sampanno, buddhaṃ taṃ paṇamāmyahaṃ.482.</p>\r\n<p class="gatha1">Appamaññāsu kovido, aṭṭhaṅgamaggaṃ pāragū;</p>\r\n<p class="gathalast">Sabbathā nikkhayappatto, buddhaṃ taṃ paṇamāmyahaṃ.483.</p>\r\n<p class="gatha1">Dhammaniyāme kovido, dhammatāyapi kovido;</p>\r\n<p class="gathalast">Dhammaṭṭhitiyā kovido, buddhaṃ taṃ paṇamāmyahaṃ.484.</p>\r\n<p class="gatha1">Kāyasaṃvarasampanno, cittasaṃvarakovido;</p>\r\n<p class="gathalast">Vacīsaṃvarasaṃvuto, buddhaṃ taṃ paṇamāmyahaṃ.485.</p>\r\n<p class="gatha1">Lokajeṭṭho lokaseṭṭho, tilokajeṭṭho tevijjo;</p>\r\n<p class="gathalast">Purisajeṭṭho visiṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.486.</p>\r\n<p class="gatha1">Sabbaseṭṭho sudhammaṭṭho, yoniso sabbaniggaho;</p>\r\n<p class="gathalast">Visaṃsaggo visaṃsaṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.487.</p>\r\n<p class="gatha1">Sabbajeṭṭho sabbaseṭṭho, sabbaggo sabbanāyako;</p>\r\n<p class="gathalast">Sabbanātho sabbasāmī, buddhaṃ taṃ paṇamāmyahaṃ.488.</p>\r\n<p class="gatha1">Maccujayī mārajayī, māradheyyaviddhaṃsako;</p>\r\n<p class="gathalast">Mārapāsaṃ vināsesi, buddhaṃ taṃ paṇamāmyahaṃ.489.</p>\r\n<p class="gatha1">Maccuhāyī mārahāyī, maccudheyyaviddhaṃsako;</p>\r\n<p class="gathalast">Maccupāsaṃ vināsesi, buddhaṃ taṃ paṇamāmyahaṃ.490.</p>\r\n<p class="gatha1">Mārajayo maccujayo, antakadheyyaddhaṃsako;</p>\r\n<p class="gathalast">Maccukhettaṃ padālesi, buddhaṃ taṃ paṇamāmyahaṃ.491.</p>\r\n<p class="gatha1">Parakkamapuṇṇo vīro, māracamūnimmaddano;</p>\r\n<p class="gathalast">Maccutiṇṇo maccujito, buddhaṃ taṃ paṇamāmyahaṃ.492.</p>\r\n<p class="gatha1">Māramāyaṃ vimaddesi, mohanamāraghātako;</p>\r\n<p class="gathalast">Māramadanamaddano, buddhaṃ taṃ paṇamāmyahaṃ.493.</p>\r\n<p class="gatha1">Pabalo māraṃ maddesi, sabalo maccumaddako;</p>\r\n<p class="gathalast">Namucināsako nāgo, buddhaṃ taṃ paṇamāmyahaṃ.494.</p>\r\n<p class="gatha1">Pāpīmārabalānīkaṃ, nimmaddesi mahājino;</p>\r\n<p class="gathalast">Paraṃ sukhaṃ adhippatto, buddhaṃ taṃ paṇamāmyahaṃ.495.</p>\r\n<p class="gatha1">Pāpajayo puññajayo, ahaṃjayo jitamamo;</p>\r\n<p class="gathalast">Kaṅkhājayo saṅkājayo, buddhaṃ taṃ paṇamāmyahaṃ.496.</p>\r\n<p class="gatha1">Jitamoho jitamāyo, jitamakkho mārañjito;</p>\r\n<p class="gathalast">Jitamaccharo jiticcho, buddhaṃ taṃ paṇamāmyahaṃ.497.</p>\r\n<p class="gatha1">Jitakodho jitakāmo, jitalobho nandījito;</p>\r\n<p class="gathalast">Jitataṇho jitarāgo, buddhaṃ taṃ paṇamāmyahaṃ.498.</p>\r\n<p class="gatha1">Jitavero jitasneho, jitamāno jitamado;</p>\r\n<p class="gathalast">Jitātaṅko jitāpekkho, buddhaṃ taṃ paṇamāmyahaṃ.499.</p>\r\n<p class="gatha1">Sabbadā vijayī vīro, sabbaji samaṇuttamo;</p>\r\n<p class="gathalast">Khemadāyako khemindo, buddhaṃ taṃ paṇamāmyahaṃ.500.</p>\r\n<p class="gatha1">Sabbajito sabbavasī, sabbavijayamaṅgalo;</p>\r\n<p class="gathalast">Sabbajayo sakkasīho, buddhaṃ taṃ paṇamāmyahaṃ.501.</p>\r\n<p class="gatha1">Dantathīno dantamiddho, dantapamādo dantiñjo;</p>\r\n<p class="gathalast">Dantārambho dantalīno, buddhaṃ taṃ paṇamāmyahaṃ.502.</p>\r\n<p class="gatha1">Dantachambho dantadaro, dantabhīti dantabhayo;</p>\r\n<p class="gathalast">Dantabhesmo dantabhīmo, buddhaṃ taṃ paṇamāmyahaṃ.503.</p>\r\n<p class="gatha1">Dohadanto dosadanto, dessadanto dantadiso;</p>\r\n<p class="gathalast">Ḍāhadanto dāhadanto, buddhaṃ taṃ paṇamāmyahaṃ.504.</p>\r\n<p class="gatha1">Sammāpadhāno vikkanto, mahāvikkamamānaso;</p>\r\n<p class="gathalast">Parakkamī mārañjayo, buddhaṃ taṃ paṇamāmyahaṃ.505.</p>\r\n<p class="gatha1">Sammāpadhānakusalo, saṃvuto atisaṃyato;</p>\r\n<p class="gathalast">Pāramitāparipuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.506.</p>\r\n<p class="gatha1">Hirottappī ātāpī ca, sudanto atisaṃvuto;</p>\r\n<p class="gathalast">Sakyaseṭṭho sabbajeṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.507.</p>\r\n<p class="gatha1">Dasabaladhārī daḷho, dhīro asayhasāhino;</p>\r\n<p class="gathalast">Purisapuṅgavo sūro, buddhaṃ taṃ paṇamāmyahaṃ.508.</p>\r\n<p class="gatha1">Dvipaduttamo damako, danto daḷhaparakkamo;</p>\r\n<p class="gathalast">Dosāpagato dosaññū, buddhaṃ taṃ paṇamāmyahaṃ.509.</p>\r\n<p class="gatha1">Upakkamī parakkamī, viriyārambho vikkamī;</p>\r\n<p class="gathalast">Pāramī paripūresi, buddhaṃ taṃ paṇamāmyahaṃ.510.</p>\r\n<p class="gatha1">Patāpī pāramīppatto, padhāniko paggāhako;</p>\r\n<p class="gathalast">Pāpīmāraṃ parājesi, buddhaṃ taṃ paṇamāmyahaṃ.511.</p>\r\n<p class="gatha1">Javasattisusampanno, sūro vikkamasekharo;</p>\r\n<p class="gathalast">Dhitidhārako sudhīro, buddhaṃ taṃ paṇamāmyahaṃ.512.</p>\r\n<p class="gatha1">Vikkamo duratikkamo, akilanto parakkamo;</p>\r\n<p class="gathalast">Amatāgadhaṃ sampatto, buddhaṃ taṃ paṇamāmyahaṃ.513.</p>\r\n<p class="gatha1">Satisātaccasampanno, sammappadhāna pāragū;</p>\r\n<p class="gathalast">Ariyadassanadassī, buddhaṃ taṃ paṇamāmyahaṃ.514.</p>\r\n<p class="gatha1">Sammāviriyasampanno, sammāpadhānapāragū;</p>\r\n<p class="gathalast">Sabbatthappatto siddhattho, buddhaṃ taṃ paṇamāmyahaṃ.515.</p>\r\n<p class="gatha1">Sūro vijitasaṅgāmo, vīro vijitaveriko;</p>\r\n<p class="gathalast">Dhīro purisadhoreyyo, buddhaṃ taṃ paṇamāmyahaṃ.516.</p>\r\n<p class="gatha1">Pūresi pāramī sabbā, narāsabho parakkamī;</p>\r\n<p class="gathalast">Vimuttajātimaraṇo, buddhaṃ taṃ paṇamāmyahaṃ.517.</p>\r\n<p class="gatha1">Vikkamo ca parakkamo, pāpakammaṃ pahāṇako;</p>\r\n<p class="gathalast">Paggāhako upakkamo, buddhaṃ taṃ paṇamāmyahaṃ.518.</p>\r\n<p class="gatha1">Sabbasattahitatthāya, parikkāmesi vikkamī;</p>\r\n<p class="gathalast">Muttāyanaṃ anvesi, buddhaṃ taṃ paṇamāmyahaṃ.519.</p>\r\n<p class="gatha1">Bhavavaṭṭaṃ suacchecchi, narasaddūlo niddaro;</p>\r\n<p class="gathalast">Paramiṭṭhaṃ susampatto, buddhaṃ taṃ paṇamāmyahaṃ.520.</p>\r\n<p class="gatha1">Hatamoho hatamāyo, hatamakkho hatamado;</p>\r\n<p class="gathalast">Hatamāno hatamāro, buddhaṃ taṃ paṇamāmyahaṃ.521.</p>\r\n<p class="gatha1">Dambhahantā gabbahantā, vaṭṭahantā arahanto;</p>\r\n<p class="gathalast">Bījahantā bādhāhantā, buddhaṃ taṃ paṇamāmyahaṃ.522.</p>\r\n<p class="gatha1">Bhavanettiṃ sammāhantā, saṅkhatamuttamānaso;</p>\r\n<p class="gathalast">Sabbadhi avijjāhantā, buddhaṃ taṃ paṇamāmyahaṃ.523.</p>\r\n<p class="gatha1">Saṃvutatto saṃyatatto, samitatto samāhito;</p>\r\n<p class="gathalast">Vimuttatto virattatto, buddhaṃ taṃ paṇamāmyahaṃ.524.</p>\r\n<p class="gatha1">Pāpaghacco puññaghacco, ahaṃghacco ghaccamamo;</p>\r\n<p class="gathalast">Kaṅkhāghacco saṅkāghacco, buddhaṃ taṃ paṇamāmyahaṃ.525.</p>\r\n<p class="gatha1">Kappaghacco kālaghacco, kilesaghacco sabbathā;</p>\r\n<p class="gathalast">Mūlaghacco sūlaghacco, buddhaṃ taṃ paṇamāmyahaṃ.526.</p>\r\n<p class="gatha1">Ghaccajāti ghaccamaccu, ghaccaloko ghaccabhavo;</p>\r\n<p class="gathalast">Ghaccavibhavo ghaccogho, buddhaṃ taṃ paṇamāmyahaṃ.527.</p>\r\n<p class="gatha1">Ghaccakhobho ghaccadukkho, ghaccasallo ghaccakhilo;</p>\r\n<p class="gathalast">Ghaccābhimāno ghaccedho, buddhaṃ taṃ paṇamāmyahaṃ.528.</p>\r\n<p class="gatha1">Dosabhañjo dohabhañjo, kāmabhañjo bhañjatamo;</p>\r\n<p class="gathalast">Diṭṭhibhañjo dukkhabhañjo, buddhaṃ taṃ paṇamāmyahaṃ.529.</p>\r\n<p class="gatha1">Bhogabhañjo sokabhañjo, rogabhañjo bhañjarajo;</p>\r\n<p class="gathalast">Sūlabhañjo sallabhañjo, buddhaṃ taṃ paṇamāmyahaṃ.530.</p>\r\n<p class="gatha1">Bhañjathīno bhañjamiddho, bhañjabandho bhañjaggalo;</p>\r\n<p class="gathalast">Bhañjārambho bhañjalīno, buddhaṃ taṃ paṇamāmyahaṃ.531.</p>\r\n<p class="gatha1">Bhañjāsaṅko bhañjātaṅko, bhañjāsaṃso bhañjussuko;</p>\r\n<p class="gathalast">Bhañjābhirato bhañjiccho, buddhaṃ taṃ paṇamāmyahaṃ.532.</p>\r\n<p class="gatha1">Bhañjānutāpo bhañjāgu, bhañjaṅgaṇo bhañjāsavo;</p>\r\n<p class="gathalast">Mānābhimānavibhañjo, buddhaṃ taṃ paṇamāmyahaṃ.533.</p>\r\n<p class="gatha1">Sallabhañjo sūlabhañjo, mūlabhañjo bhañjamalo;</p>\r\n<p class="gathalast">Sabbabhavabandhabhañjo, buddhaṃ taṃ paṇamāmyahaṃ.534.</p>\r\n<p class="gatha1">Kappakkhayo kālakkhayo, kilesakkhayo sabbathā;</p>\r\n<p class="gathalast">Mūlakkhayo sūlakkhayo, buddhaṃ taṃ paṇamāmyahaṃ.535.</p>\r\n<p class="gatha1">Dosakkhayo dohakkhayo, kāmakkhayo khīṇamado;</p>\r\n<p class="gathalast">Diṭṭhikkhayo dukkhakkhayo, buddhaṃ taṃ paṇamāmyahaṃ.536.</p>\r\n<p class="gatha1">Kodhakhīṇo kopakhīṇo, klesakhīṇo khīṇakuho;</p>\r\n<p class="gathalast">Verakhīṇo dessakhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.537.</p>\r\n<p class="gatha1">Lobhakhīṇo lolakhīṇo, rosakhīṇo khīṇaraṇo;</p>\r\n<p class="gathalast">Kicchākhīṇo icchākhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.538.</p>\r\n<p class="gatha1">Khīṇarāgo khīṇadoso, khīṇamoho khīṇabhavo;</p>\r\n<p class="gathalast">Khīṇamāno khīṇamakkho, buddhaṃ taṃ paṇamāmyahaṃ.539.</p>\r\n<p class="gatha1">Khīṇagedho khīṇalobho, khīṇakāmo ca khīṇiñjo;</p>\r\n<p class="gathalast">Khīṇasārambho khīṇindho, buddhaṃ taṃ paṇamāmyahaṃ.540.</p>\r\n<p class="gatha1">Khīṇāsayo khīṇabījo, aviruḷhichando vasī;</p>\r\n<p class="gathalast">Khīṇataṇho khīṇabhijjho, buddhaṃ taṃ paṇamāmyahaṃ.541.</p>\r\n<p class="gatha1">Khayāsaṅko khayātaṅko, khayākaṅkho khayaṅgaṇo;</p>\r\n<p class="gathalast">Khayāpekkho khayābhijjho, buddhaṃ taṃ paṇamāmyahaṃ.542.</p>\r\n<p class="gatha1">Khayataṇho khayalobho, khayasallo khayakhilo;</p>\r\n<p class="gathalast">Khayabhogo khayarogo, buddhaṃ taṃ paṇamāmyahaṃ.543.</p>\r\n<p class="gatha1">Khayachambho khayadaro, khayabhismā khayabhayo;</p>\r\n<p class="gathalast">Khayasaṃsayo khayiñjo, buddhaṃ taṃ paṇamāmyahaṃ.544.</p>\r\n<p class="gatha1">Khayasaṃsaṭṭho khayoko, khayāgāro khayagaho;</p>\r\n<p class="gathalast">Khayasūlo khayamūlo, buddhaṃ taṃ paṇamāmyahaṃ.545.</p>\r\n<p class="gatha1">Ahaṅkhayo mamaṅkhayo, saṅkhatakkhayo sabbadhi;</p>\r\n<p class="gathalast">Saṅkilesakkhayappatto, buddhaṃ taṃ paṇamāmyahaṃ.546.</p>\r\n<p class="gatha1">Suññataṇho suññarāgo, suññaraṅgo suññarajo;</p>\r\n<p class="gathalast">Suññachando suññanandī, buddhaṃ taṃ paṇamāmyahaṃ.547.</p>\r\n<p class="gatha1">Suññaroso suññadoso, suññadoho suññadaho;</p>\r\n<p class="gathalast">Suññasāpo suññatāpo, buddhaṃ taṃ paṇamāmyahaṃ.548.</p>\r\n<p class="gatha1">Suññakodho suññakopo, suññakleso suññakuho;</p>\r\n<p class="gathalast">Suññaverī suññasattu, buddhaṃ taṃ paṇamāmyahaṃ.549.</p>\r\n<p class="gatha1">Suññajāti suññamaccu, suññaloko suññabhavo;</p>\r\n<p class="gathalast">Suññatā’bhirato sugato, buddhaṃ taṃ paṇamāmyahaṃ.550.</p>\r\n<p class="gatha1">Ītisuñño bhītisuñño, chambhasuñño suññabhayo;</p>\r\n<p class="gathalast">Sabbathā sārajjasuñño, buddhaṃ taṃ paṇamāmyahaṃ.551.</p>\r\n<p class="gatha1">Suññagantho suññarajju, sabbathā suññabandhano;</p>\r\n<p class="gathalast">Suññadiṭṭhi suññanetti, buddhaṃ taṃ paṇamāmyahaṃ.552.</p>\r\n<p class="gatha1">Sallasuñño sūlasuñño, mūlasuñño suññamalo;</p>\r\n<p class="gathalast">Sabbānusayehi suñño, buddhaṃ taṃ paṇamāmyahaṃ.553.</p>\r\n<p class="gatha1">Sabbadhi santāpasuñño, santāsasuñño sabbathā;</p>\r\n<p class="gathalast">Tasasuñño tāsasuñño, buddhaṃ taṃ paṇamāmyahaṃ.554.</p>\r\n<p class="gatha1">Āsāsuñño issāsuñño, ejāsuñño suññaiṇo;</p>\r\n<p class="gathalast">Chandasuñño bandhasuñño, buddhaṃ taṃ paṇamāmyahaṃ.555.</p>\r\n<p class="gatha1">Pāpasuñño puññasuñño, ahaṃsuñño suññamamo;</p>\r\n<p class="gathalast">Kaṅkhāsuñño saṅkāsuñño, buddhaṃ taṃ paṇamāmyahaṃ.556.</p>\r\n<p class="gatha1">Sabbakammajaho cāgī, cattābhilāso sabbadhi;</p>\r\n<p class="gathalast">Sabbathā cattasaṃsaṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.557.</p>\r\n<p class="gatha1">Dosacāgī dohacāgī, kujjhanacāgī cattindho;</p>\r\n<p class="gathalast">Kodhacāgī kopacāgī, buddhaṃ taṃ paṇamāmyahaṃ.558.</p>\r\n<p class="gatha1">Sabbarājabhogacāgī, mahāoghamummujjako;</p>\r\n<p class="gathalast">Paramatthadhammadassī, buddhaṃ taṃ paṇamāmyahaṃ.559.</p>\r\n<p class="gatha1">Cattakāmo cattakleso, cattāsatto cattāsavo;</p>\r\n<p class="gathalast">Cattapāpo cattuttāpo, buddhaṃ taṃ paṇamāmyahaṃ.560.</p>\r\n<p class="gatha1">Īticuto bhīticuto, chambhacuto cuttabhayo;</p>\r\n<p class="gathalast">Ḍāhacuto dāhacuto, buddhaṃ taṃ paṇamāmyahaṃ.561.</p>\r\n<p class="gatha1">Sabbacatto sabbacajo, sabbacāgī sabbaccago;</p>\r\n<p class="gathalast">Sabbaritto sabbañjaho, buddhaṃ taṃ paṇamāmyahaṃ.562.</p>\r\n<p class="gatha1">Anāsaṅko anātaṅko, anākaṅkho anaṅgaṇo;</p>\r\n<p class="gathalast">Sadā asaṅgacetaso, buddhaṃ taṃ paṇamāmyahaṃ.563.</p>\r\n<p class="gatha1">Anusūyo anāsaṃso, anāso ca anussuko;</p>\r\n<p class="gathalast">Anāsavo anapekkho, buddhaṃ taṃ paṇamāmyahaṃ.564.</p>\r\n<p class="gatha1">Ākāso viya paññāya, alitto anilo yathā;</p>\r\n<p class="gathalast">Anissito vihāsi yo, buddhaṃ taṃ paṇamāmyahaṃ.565.</p>\r\n<p class="gatha1">Āraddhaviriyo ātāpī, uyyujjanto padhāniko;</p>\r\n<p class="gathalast">Akusīto analaso, buddhaṃ taṃ paṇamāmyahaṃ.566.</p>\r\n<p class="gatha1">Anupamo satthā jeṭṭho, sabbaseṭṭho ovādako;</p>\r\n<p class="gathalast">Ariyadhammaṃ aññāsi, buddhaṃ taṃ paṇamāmyahaṃ.567.</p>\r\n<p class="gatha1">Amatena atappi lokaṃ, dhammameghapavassako;</p>\r\n<p class="gathalast">Assāsesi yathā cando, buddhaṃ taṃ paṇamāmyahaṃ.568.</p>\r\n<p class="gatha1">Avikampī anabhīto, asantāsī anāturo;</p>\r\n<p class="gathalast">Abhīruko yo acchambhī, buddhaṃ taṃ paṇamāmyahaṃ.569.</p>\r\n<p class="gatha1">Amaccharī amāyāvī, ajeguccho aniṭṭhuro;</p>\r\n<p class="gathalast">Appaduṭṭho akuṭilo, buddhaṃ taṃ paṇamāmyahaṃ.570.</p>\r\n<p class="gatha1">Anabhijjho apihālu, akuhako alolupo;</p>\r\n<p class="gathalast">Anupavajjo amatto, buddhaṃ taṃ paṇamāmyahaṃ.571.</p>\r\n<p class="gatha1">Appagabbho appaṭigho, appāvilo amucchito;</p>\r\n<p class="gathalast">Apesuṇiko adoso, buddhaṃ taṃ paṇamāmyahaṃ.572.</p>\r\n<p class="gatha1">Akopo cāpi aduṭṭho, vītussuko adūsako;</p>\r\n<p class="gathalast">Vītakopo vītakuho, buddhaṃ taṃ paṇamāmyahaṃ.573.</p>\r\n<p class="gatha1">Akampito akampako, avero akutobhayo;</p>\r\n<p class="gathalast">Sabbadā chambharahito, buddhaṃ taṃ paṇamāmyahaṃ.574.</p>\r\n<p class="gatha1">Anatimānī akhilo, akuppano akujjhano;</p>\r\n<p class="gathalast">Anabhisajjo akkodho, buddhaṃ taṃ paṇamāmyahaṃ.575.</p>\r\n<p class="gatha1">Akakkaso ca akkopo, akaṭuko akūṭako;</p>\r\n<p class="gathalast">Appadesso anunnalo, buddhaṃ taṃ paṇamāmyahaṃ.576.</p>\r\n<p class="gatha1">Anapekkhī anupayo, anāsaṅgo anāvilo;</p>\r\n<p class="gathalast">Analitto anālambo, buddhaṃ taṃ paṇamāmyahaṃ.577.</p>\r\n<p class="gatha1">Aggamahattatto muni, abhinibbuttatto yatī;</p>\r\n<p class="gathalast">Visuddho abhisambuddho, buddhaṃ taṃ paṇamāmyahaṃ.578.</p>\r\n<p class="gatha1">Abbūḷhasallo arajo, akkhadakkhī anāsavo;</p>\r\n<p class="gathalast">Anomadakkhī anaṇo, buddhaṃ taṃ paṇamāmyahaṃ.579.</p>\r\n<p class="gatha1">Atthapatiṭṭho atthagū, anappaatthapāragū;</p>\r\n<p class="gathalast">Accanto atthakusalo, buddhaṃ taṃ paṇamāmyahaṃ.580.</p>\r\n<p class="gatha1">Ariyaddhānaṃ sampatto, atthatto atthapaṇḍito;</p>\r\n<p class="gathalast">Mocesi bandhanāsatte, buddhaṃ taṃ paṇamāmyahaṃ.581.</p>\r\n<p class="gatha1">Asajjamānaso alīno, sadā asaṅgamānaso;</p>\r\n<p class="gathalast">Appamādarato nātho, buddhaṃ taṃ paṇamāmyahaṃ.582.</p>\r\n<p class="gatha1">Asaṅgacitto akleso, āsā yassa na vijjati;</p>\r\n<p class="gathalast">Asaṅkiliṭṭho akkuho, buddhaṃ taṃ paṇamāmyahaṃ.583.</p>\r\n<p class="gatha1">Upekkhacitto acalo, upādānakkhayo ujū;</p>\r\n<p class="gathalast">Abhinibbido appiñjo, buddhaṃ taṃ paṇamāmyahaṃ.584.</p>\r\n<p class="gatha1">Abhiññāpāramīpatto, abhipañño abhigato;</p>\r\n<p class="gathalast">Anupādā vimutto yo, buddhaṃ taṃ paṇamāmyahaṃ.585.</p>\r\n<p class="gatha1">Aviruddho asāratto, santiladdho anappako;</p>\r\n<p class="gathalast">Amitaamatappatto, buddhaṃ taṃ paṇamāmyahaṃ.586.</p>\r\n<p class="gatha1">Ākaṅkhiñjāritto, anupādāno uttamo;</p>\r\n<p class="gathalast">Appaneho ca appiho, buddhaṃ taṃ paṇamāmyahaṃ.587.</p>\r\n<p class="gatha1">Atisīlo atisanto, ativiññū arahato;</p>\r\n<p class="gathalast">Aggatthapatto aggatto, buddhaṃ taṃ paṇamāmyahaṃ.588.</p>\r\n<p class="gatha1">Adhisīlo adhicitto, adhipañño adhigato;</p>\r\n<p class="gathalast">Apalokitadassāvī, buddhaṃ taṃ paṇamāmyahaṃ.589.</p>\r\n<p class="gatha1">Attadīpo attasaraṇo, attanātho adhivaro;</p>\r\n<p class="gathalast">Accantuttamo aggabhū, buddhaṃ taṃ paṇamāmyahaṃ.590.</p>\r\n<p class="gatha1">Anabhikaṅkho aniccho, arāgo anapekkhako;</p>\r\n<p class="gathalast">Ananugiddho alobho, buddhaṃ taṃ paṇamāmyahaṃ.591.</p>\r\n<p class="gatha1">Anabhilāso anedho, aviruḷhitaṇho isi;</p>\r\n<p class="gathalast">Ucchinnarāgo alaggo, buddhaṃ taṃ paṇamāmyahaṃ.592.</p>\r\n<p class="gatha1">Naṭṭharāgo naṭṭhadoso, naṭṭhamoho naṭṭhamado;</p>\r\n<p class="gathalast">Naṭṭhakodho naṭṭhakopo, buddhaṃ taṃ paṇamāmyahaṃ.593.</p>\r\n<p class="gatha1">Vinaṭṭhiñjo vinaṭṭhindho, vinaṭṭhiccho vinaṭṭhiṇo;</p>\r\n<p class="gathalast">Vinaṭṭhāsā vinaṭṭhissā, buddhaṃ taṃ paṇamāmyahaṃ.594.</p>\r\n<p class="gatha1">Naṭṭhakaṅkhā naṭṭhasaṅkā, naṭṭhabhanti naṭṭhabhamo;</p>\r\n<p class="gathalast">Naṭṭhamāyā, naṭṭhāvijjā, buddhaṃ taṃ paṇamāmyahaṃ.595.</p>\r\n<p class="gatha1">Amāyo aparicchinno, ujuko ativissuto;</p>\r\n<p class="gathalast">Anomacitto aneḷo, buddhaṃ taṃ paṇamāmyahaṃ.596.</p>\r\n<p class="gatha1">Atthapassī atthacaro, atthakkhāyī atthakaro;</p>\r\n<p class="gathalast">Atthakāmo attamano, buddhaṃ taṃ paṇamāmyahaṃ.597.</p>\r\n<p class="gatha1">Abbhuto ca acchariyo, cittakathī ovādako!</p>\r\n<p class="gathalast">Aggamaggaṃ suakkhāsi, buddhaṃ taṃ paṇamāmyahaṃ.598.</p>\r\n<p class="gatha1">Akalaṅko akaluso, akasāvo apaṇṇako;</p>\r\n<p class="gathalast">Akukkucco ca akaṅkhī, buddhaṃ taṃ paṇamāmyahaṃ.599.</p>\r\n<p class="gatha1">Sabbaso vijayo sūro, abhayo abhayappado;</p>\r\n<p class="gathalast">Anākulo appabhīto, buddhaṃ taṃ paṇamāmyahaṃ.600.</p>\r\n<p class="gatha1">Upekkhako upasanto, anusayaritto isi;</p>\r\n<p class="gathalast">Appaṭibaddho abaddho, buddhaṃ taṃ paṇamāmyahaṃ.601.</p>\r\n<p class="gatha1">Atthacārī atthakārī, atthasārī attharato;</p>\r\n<p class="gathalast">Atthañāṇī atthabhāṇī, buddhaṃ taṃ paṇamāmyahaṃ.602.</p>\r\n<p class="gatha1">Anadhivaro asoko, usabho isipuṅgavo;</p>\r\n<p class="gathalast">Anomanāmo anomo, buddhaṃ taṃ paṇamāmyahaṃ.603.</p>\r\n<p class="gatha1">Anavassutahadayo, ananvāhatacetaso;</p>\r\n<p class="gathalast">Anabhirato aniñjo, buddhaṃ taṃ paṇamāmyahaṃ.604.</p>\r\n<p class="gatha1">Appāsaṅko appātaṅko, appākaṅkho appaṅgaṇo;</p>\r\n<p class="gathalast">Appalolo appakampī, buddhaṃ taṃ paṇamāmyahaṃ.605.</p>\r\n<p class="gatha1">Atthaviññū atthavidū, atthakusalo atthavā;</p>\r\n<p class="gathalast">Kusalatthamanuppatto, buddhaṃ taṃ paṇamāmyahaṃ.606.</p>\r\n<p class="gatha1">Avicalito akuppo, kesarī’va achambhito;</p>\r\n<p class="gathalast">Avikampito abbhayo, buddhaṃ taṃ paṇamāmyahaṃ.607.</p>\r\n<p class="gatha1">Akiñcano anādāno, amataññū amatogadho;</p>\r\n<p class="gathalast">Aggaphalamanuppatto, buddhaṃ taṃ paṇamāmyahaṃ.608.</p>\r\n<p class="gatha1">Amitaanuggahaṃ katvā, akālikadhammadado;</p>\r\n<p class="gathalast">Accantupakārī nātho, buddhaṃ taṃ paṇamāmyahaṃ.609.</p>\r\n<p class="gatha1">Anupalitto lokena, toyena padumaṃ yathā;</p>\r\n<p class="gathalast">Nissaṅgacitto nissatto, buddhaṃ taṃ paṇamāmyahaṃ.610.</p>\r\n<p class="gatha1">Abbūḷhesiko isi, ukkhittapaligho ujū;</p>\r\n<p class="gathalast">Anuddhato atandito, buddhaṃ taṃ paṇamāmyahaṃ.611.</p>\r\n<p class="gatha1">Agiddho alittacitto, ādīnavavidālako;</p>\r\n<p class="gathalast">Pakatatto pahitatto, buddhaṃ taṃ paṇamāmyahaṃ.612.</p>\r\n<p class="gatha1">Aggapuñño aggaviññū, aggīsi aggasekharo;</p>\r\n<p class="gathalast">Aggamaggamanuppatto, buddhaṃ taṃ paṇamāmyahaṃ.613.</p>\r\n<p class="gatha1">Aggagocaro aggagū, aggattho aggadesako;</p>\r\n<p class="gathalast">Aggañāṇī aggabhāṇī, buddhaṃ taṃ paṇamāmyahaṃ.614.</p>\r\n<p class="gatha1">Aggapañño aggaṭṭhito, aggasiddho aggayatī;</p>\r\n<p class="gathalast">Aggabhūto aggapati, buddhaṃ taṃ paṇamāmyahaṃ.615.</p>\r\n<p class="gatha1">Aggasamaṇo lokaggo, paññaggo aggamaggagū;</p>\r\n<p class="gathalast">Aggadhammaṃ ugghāṭesi, buddhaṃ taṃ paṇamāmyahaṃ.616.</p>\r\n<p class="gatha1">Aggadhammamanuppatto, aggadhammappakāsako;</p>\r\n<p class="gathalast">Apāyadukkhaṃ nāsesi, buddhaṃ taṃ paṇamāmyahaṃ.617.</p>\r\n<p class="gatha1">Aggapuggalo aggaggo, ñāṇaggo ñāṇapuṅgavo;</p>\r\n<p class="gathalast">Dhammapuggalo dhammaggo, buddhaṃ taṃ paṇamāmyahaṃ.618.</p>\r\n<p class="gatha1">Aggapassī aggadassī, aggapanthapakāsako;</p>\r\n<p class="gathalast">Aggesī aggadassāvī, buddhaṃ taṃ paṇamāmyahaṃ.619.</p>\r\n<p class="gatha1">Anantajhānī jhānindo, anantapaññavāridhī;</p>\r\n<p class="gathalast">Anantamedhāsampanno, buddhaṃ taṃ paṇamāmyahaṃ.620.</p>\r\n<p class="gatha1">Anantavijjāvāridhi, anantapaññāsāgaro;</p>\r\n<p class="gathalast">Anantakaruṇāsindhu, buddhaṃ taṃ paṇamāmyahaṃ.621.</p>\r\n<p class="gatha1">Sabbuttamo sattuttamo, sumano samaṇuttamo;</p>\r\n<p class="gathalast">Januttamo jinuttamo, buddhaṃ taṃ paṇamāmyahaṃ.622.</p>\r\n<p class="gatha1">Kalyāṇamitto kalyāṇo, maṅgalamitto maṅgalo;</p>\r\n<p class="gathalast">Dhammamitto dhammadado, buddhaṃ taṃ paṇamāmyahaṃ.623.</p>\r\n<p class="gatha1">Dhammiṭṭho cāpi dhammaṭṭho, paññiṭṭho cāpi paññaṭṭho;</p>\r\n<p class="gathalast">Suddhiṭṭho cāpi suddhaṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.624.</p>\r\n<p class="gatha1">Kāyāsakkhī cittasakkhī, dhammasakkhī sakkhīvaro;</p>\r\n<p class="gathalast">Vedanāsakkhī susakkhī, buddhaṃ taṃ paṇamāmyahaṃ.625.</p>\r\n<p class="gatha1">Sīghapañño sunipuṇo, santipatto sukhādhipo;</p>\r\n<p class="gathalast">Kalyāṇakārī kusalo, buddhaṃ taṃ paṇamāmyahaṃ.626.</p>\r\n<p class="gatha1">Susīlaggo sucittaggo, supaññaggo susobhito;</p>\r\n<p class="gathalast">Aggamatthamanuppatto, buddhaṃ taṃ paṇamāmyahaṃ.627.</p>\r\n<p class="gatha1">Upariṭṭho ujjugato, ukkaṭṭho amatāvaho;</p>\r\n<p class="gathalast">Appadambho appagabbo, buddhaṃ taṃ paṇamāmyahaṃ.628.</p>\r\n<p class="gatha1">Appasoko apparogo, appabhīti appabhayo;</p>\r\n<p class="gathalast">Appapāpo appatāpo, buddhaṃ taṃ paṇamāmyahaṃ.629.</p>\r\n<p class="gatha1">Apparoso appadoso, appadoho appindhano;</p>\r\n<p class="gathalast">Appābhisajjo appīgho, buddhaṃ taṃ paṇamāmyahaṃ.630.</p>\r\n<p class="gatha1">Accantakaruṇākārī, accantamettacetaso;</p>\r\n<p class="gathalast">Accantamudito santo, buddhaṃ taṃ paṇamāmyahaṃ.631.</p>\r\n<p class="gatha1">Ekappatto ekanātho, eko appaṭipuggalo;</p>\r\n<p class="gathalast">Ekova sabbalokasmiṃ, buddhaṃ taṃ paṇamāmyahaṃ.632.</p>\r\n<p class="gatha1">Atuliyo atitulo, adutiyo anuttaro;</p>\r\n<p class="gathalast">Appameyyo appaṭimo, buddhaṃ taṃ paṇamāmyahaṃ.633.</p>\r\n<p class="gatha1">Atuladassī atulo, asarikkho anupamo;</p>\r\n<p class="gathalast">Asamo yo asadiso, buddhaṃ taṃ paṇamāmyahaṃ.634.</p>\r\n<p class="gatha1">Amito aparimito, amitañāṇasāgaro;</p>\r\n<p class="gathalast">Aparitto appamāṇo, buddhaṃ taṃ paṇamāmyahaṃ.635.</p>\r\n<p class="gatha1">Asīmo asamasamo, atulyo amitaguṇo;</p>\r\n<p class="gathalast">Anupameyyo agādho, buddhaṃ taṃ paṇamāmyahaṃ.636.</p>\r\n<p class="gatha1">Nipparicchinno nissīmo, nappamañño jinavaro;</p>\r\n<p class="gathalast">Nappamāṇo nirupamo, buddhaṃ taṃ paṇamāmyahaṃ.637.</p>\r\n<p class="gatha1">Natthi añño etādiso, nipako ekapuggalo;</p>\r\n<p class="gathalast">Ekantasukhasaṃvedī, buddhaṃ taṃ paṇamāmyahaṃ.638.</p>\r\n<p class="gatha1">Aggavasabho asamo, asamapaṭipuggalo;</p>\r\n<p class="gathalast">Ekako ekapuriso, buddhaṃ taṃ paṇamāmyahaṃ.639.</p>\r\n<p class="gatha1">Sabbattha āsattisuñño, sabbathāmuttamānaso;</p>\r\n<p class="gathalast">Sabbadā ekasadiso, buddhaṃ taṃ paṇamāmyahaṃ.640.</p>\r\n<p class="gatha1">Pāpasūlaṃ vibhañjesi, pāpamūlassa chedako;</p>\r\n<p class="gathalast">Pāparajjuṃ vikantesi, buddhaṃ taṃ paṇamāmyahaṃ.641.</p>\r\n<p class="gatha1">Pāpanettiṃ nijjaresi, pāpakammavināsako;</p>\r\n<p class="gathalast">Sabbapāpaparikkhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.642.</p>\r\n<p class="gatha1">Samūlaṃ khaṇito pāpaṃ, pāpavāridhipāragū;</p>\r\n<p class="gathalast">Pāpaganthiṃ vimocesi, buddhaṃ taṃ paṇamāmyahaṃ.643.</p>\r\n<p class="gatha1">Pāpacakkaṃ vicuṇṇetvā, dhammacakkaṃ pavattayī;</p>\r\n<p class="gathalast">Bahūjane uddhāresi, buddhaṃ taṃ paṇamāmyahaṃ.644.</p>\r\n<p class="gatha1">Nijjaresi pāpakammaṃ, mahāpāpoghapāragū;</p>\r\n<p class="gathalast">Sabbapāpavītivatto, buddhaṃ taṃ paṇamāmyahaṃ.645.</p>\r\n<p class="gatha1">Sabbapāpamatikkanto, sabbapāpasamūhato;</p>\r\n<p class="gathalast">Sabbapāpasamucchinno, buddhaṃ taṃ paṇamāmyahaṃ.646.</p>\r\n<p class="gatha1">Pāpasotaṃ visosesi, pāpatāpapanūdano;</p>\r\n<p class="gathalast">Pāpachandaṃ vicchindesi, buddhaṃ taṃ paṇamāmyahaṃ.647.</p>\r\n<p class="gatha1">Pāpatāsavinimutto, pāpapāvakanibbuto;</p>\r\n<p class="gathalast">Pāpabandhaṃ vimocesi, buddhaṃ taṃ paṇamāmyahaṃ.648.</p>\r\n<p class="gatha1">Pāpasallaṃ vicuṇṇesi, pāpacakkavidālako;</p>\r\n<p class="gathalast">Pāparogasokakhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.649.</p>\r\n<p class="gatha1">Pāpamohaparimutto, pāpasaṃtāsanāsako;</p>\r\n<p class="gathalast">Pāpakhobhaṃ vikkhambhesi, buddhaṃ taṃ paṇamāmyahaṃ.650.</p>\r\n<p class="gatha1">Kaṇhakalusaṃ dhopesi, pāpamalapakkhālako;</p>\r\n<p class="gathalast">Sabbaklesaṃ vodāpesi, buddhaṃ taṃ paṇamāmyahaṃ.651.</p>\r\n<p class="gatha1">Pāpapariḷāhamutto, vimuttapāpatassanā;</p>\r\n<p class="gathalast">Pāpasantāpavimutto, buddhaṃ taṃ paṇamāmyahaṃ.652.</p>\r\n<p class="gatha1">Pāpaupadhimucchinno, pāpapalighabhaggavā;</p>\r\n<p class="gathalast">Pāpadheyyavītivatto, buddhaṃ taṃ paṇamāmyahaṃ.653.</p>\r\n<p class="gatha1">Pāpapuññapanūdako, bhavabandhanabhañjako;</p>\r\n<p class="gathalast">Pāpakaṇṭakakantako, buddhaṃ taṃ paṇamāmyahaṃ.654.</p>\r\n<p class="gatha1">Sabbapāpaṃ padālesi, sabbapāpaṃ pariccajī;</p>\r\n<p class="gathalast">Sabbapāpehi nissaṭo, buddhaṃ taṃ paṇamāmyahaṃ.655.</p>\r\n<p class="gatha1">Sabbapāpaṃ pavāhesi, sabbapāpajigucchako;</p>\r\n<p class="gathalast">Sabbapāpehi vimutto, buddhaṃ taṃ paṇamāmyahaṃ.656.</p>\r\n<p class="gatha1">Sabbapāpaṃ virajjesi, sabbapāpaniddhotako;</p>\r\n<p class="gathalast">Sabbapāpaṃ niddhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.657.</p>\r\n<p class="gatha1">Pāpaṇṇavasamuttiṇṇo, chinnabhavasaṃyojano;</p>\r\n<p class="gathalast">Punabbhavaparikkhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.658.</p>\r\n<p class="gatha1">Pāpapaṅkaṃ pakkhālesi, dhammasotapavāhako;</p>\r\n<p class="gathalast">Saddhammaṃ patiṭṭhāpesi, buddhaṃ taṃ paṇamāmyahaṃ.659.</p>\r\n<p class="gatha1">Pāputtāpaṃ samucchinno, mohuttāpaṃ sītikaro;</p>\r\n<p class="gathalast">Sabbuttāpaṃ nibbāpesi, buddhaṃ taṃ paṇamāmyahaṃ.660.</p>\r\n<p class="gatha1">Pāpahārī tāpahārī, sukhakārī khemaṅkaro;</p>\r\n<p class="gathalast">Mettāvihārī mārārī, buddhaṃ taṃ paṇamāmyahaṃ.661.</p>\r\n<p class="gatha1">Saṃyojanaparikkhīṇo, pāpatāpaviddhaṃsako;</p>\r\n<p class="gathalast">Antimabhavasampatto, buddhaṃ taṃ paṇamāmyahaṃ.662.</p>\r\n<p class="gatha1">Paṭipadāpaṭipanno, dukkhadāhapanūdano;</p>\r\n<p class="gathalast">Pāpatāpaparikkhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.663.</p>\r\n<p class="gatha1">Puññapāpaparikkhīṇo, sokasallavinodano;</p>\r\n<p class="gathalast">Padhānapahitatto yo, buddhaṃ taṃ paṇamāmyahaṃ.664.</p>\r\n<p class="gatha1">Cakkhumā sabbadhammesu, sabbapāpappabhañjako;</p>\r\n<p class="gathalast">Sabbasaṃyojanahantā, buddhaṃ taṃ paṇamāmyahaṃ.665.</p>\r\n<p class="gatha1">Sabbapāpaṃ pakkhālesi, sabbakilesasodhako;</p>\r\n<p class="gathalast">Sabbattha vimalo suddho, buddhaṃ taṃ paṇamāmyahaṃ.666.</p>\r\n<p class="gatha1">Sabbapāpaṃ nimmaddesi, sabbatāpasamūhato;</p>\r\n<p class="gathalast">Sabbasantāpaṃ hāpesi, buddhaṃ taṃ paṇamāmyahaṃ.667.</p>\r\n<p class="gatha1">Sabbūpadhīnaṃ nissaggo, sabbāsānaṃ virajjako;</p>\r\n<p class="gathalast">Sabbapāpaṃ pabhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.668.</p>\r\n<p class="gatha1">Pāragū pārasampatto, āsavasotasosako;</p>\r\n<p class="gathalast">Sabbapāpaṃ pahāsi yo, buddhaṃ taṃ paṇamāmyahaṃ.669.</p>\r\n<p class="gatha1">Sabbāsavaṃ viddhaṃsesi, sabbamohasamūhato;</p>\r\n<p class="gathalast">Sabbapāpaṃ vivajjesi, buddhaṃ taṃ paṇamāmyahaṃ.670.</p>\r\n<p class="gatha1">Sabbāsā samatikkanto, sabbarāgaggi nibbuto;</p>\r\n<p class="gathalast">Sabbapāpaṃ parimaddi, buddhaṃ taṃ paṇamāmyahaṃ.671.</p>\r\n<p class="gatha1">Sabbakāmabhavatiṇṇo, sabbavibhavavajjito;</p>\r\n<p class="gathalast">Sabbapāputtāpakkhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.672.</p>\r\n<p class="gatha1">Sabbanīvaraṇātīto, sabbapāsappamocano;</p>\r\n<p class="gathalast">Māramarīcikā bhañji, buddhaṃ taṃ paṇamāmyahaṃ.673.</p>\r\n<p class="gatha1">Saraṇadāyako sāmī, parittāṇappadāyako;</p>\r\n<p class="gathalast">Ārakkhaṇadātā satthā, buddhaṃ taṃ paṇamāmyahaṃ.674.</p>\r\n<p class="gatha1">Saccassa kovido cāpi, asaccassāpi kovido;</p>\r\n<p class="gathalast">Catusaccaṃ ugghosesi, buddhaṃ taṃ paṇamāmyahaṃ.675.</p>\r\n<p class="gatha1">Bhaddassa kovido cāpi, abhaddassāpi kovido;</p>\r\n<p class="gathalast">Sabbato bhaddaṃ dassāvī, buddhaṃ taṃ paṇamāmyahaṃ.676.</p>\r\n<p class="gatha1">Ākaṅkhāya kovido ca, anākaṅkhāya kovido;</p>\r\n<p class="gathalast">Sabbesu ca nirākaṅkho, buddhaṃ taṃ paṇamāmyahaṃ.677.</p>\r\n<p class="gatha1">Lolassa kovido cāpi, alolassāpi kovido;</p>\r\n<p class="gathalast">Nillobho api nillolo, buddhaṃ taṃ paṇamāmyahaṃ.678.</p>\r\n<p class="gatha1">Papañcassa kovido ca, nippapañcassa kovido;</p>\r\n<p class="gathalast">Nippapañco ca nicchalo, buddhaṃ taṃ paṇamāmyahaṃ.679.</p>\r\n<p class="gatha1">Sabbe dhamme pabodhesi, sabbapāpehi mocako;</p>\r\n<p class="gathalast">Sabbadukkhavītivatto, buddhaṃ taṃ paṇamāmyahaṃ.680.</p>\r\n<p class="gatha1">Viduro sabbadhammesu, pāpappahānapāragū;</p>\r\n<p class="gathalast">Sabbasaṅkhārūpasamo, buddhaṃ taṃ paṇamāmyahaṃ.681.</p>\r\n<p class="gatha1">Saccadhammaṃ vitthāresi, sabbapāpavināsako;</p>\r\n<p class="gathalast">Pāpāyatanā vimutto, buddhaṃ taṃ paṇamāmyahaṃ.682.</p>\r\n<p class="gatha1">Sabbadhammānupassī yo, sabbapāpaṃ visosayī;</p>\r\n<p class="gathalast">Sabbataṇhā pahāsi yo, buddhaṃ taṃ paṇamāmyahaṃ.683.</p>\r\n<p class="gatha1">Pāpapāsaṃ pamocesi, sabbadhammāna vedagū;</p>\r\n<p class="gathalast">Sabbupadhīhi vimutto, buddhaṃ taṃ paṇamāmyahaṃ.684.</p>\r\n<p class="gatha1">Cakkavattī dhammarājā, dhammacakkavibhūsito;</p>\r\n<p class="gathalast">Adhammacakkaṃ cuṇṇesī, buddhaṃ taṃ paṇamāmyahaṃ.685.</p>\r\n<p class="gatha1">Saddhammamuddhābhisito, dhammamahārañño budho;</p>\r\n<p class="gathalast">Dhammasāsana ṭhāpesi, buddhaṃ taṃ paṇamāmyahaṃ.686.</p>\r\n<p class="gatha1">Dhammanātho dhammassāmī, dhammīso dhammasāsako;</p>\r\n<p class="gathalast">Dhammasūro dhammasatthā, buddhaṃ taṃ paṇamāmyahaṃ.687.</p>\r\n<p class="gatha1">Dhammarañño dhammapati, dhammavattī dhammissaro;</p>\r\n<p class="gathalast">Dhammabhūpo dhammādhipo, buddhaṃ taṃ paṇamāmyahaṃ.688.</p>\r\n<p class="gatha1">Dhammamahārājādhipo, sabbathā dhammasammato;</p>\r\n<p class="gathalast">Dhammasamattho dhammīso, buddhaṃ taṃ paṇamāmyahaṃ.689.</p>\r\n<p class="gatha1">Dhammatāṇo dhammaleṇo, dhammasaraṇadāyako;</p>\r\n<p class="gathalast">Dhammasakko dhammāsayo, buddhaṃ taṃ paṇamāmyahaṃ.690.</p>\r\n<p class="gatha1">Dhammadātā dhammatātā, dhammakkhātā dhammasakhā;</p>\r\n<p class="gathalast">Dhammasukho dhammahito, buddhaṃ taṃ paṇamāmyahaṃ.691.</p>\r\n<p class="gatha1">Dhammanāgo dhammasīho, dhammājañño dhammusabho;</p>\r\n<p class="gathalast">Dhammadhurandharo dhīro, buddhaṃ taṃ paṇamāmyahaṃ.692.</p>\r\n<p class="gatha1">Dhammasiṅgo dhammatuṅgo, dhammameru dhammagiri;</p>\r\n<p class="gathalast">Dhammakūṭo dhammasīso, buddhaṃ taṃ paṇamāmyahaṃ.693.</p>\r\n<p class="gatha1">Dhammādicco dhammappabho, dibbo dhammavibhākaro;</p>\r\n<p class="gathalast">Dhammabhākaro dhammābho, buddhaṃ taṃ paṇamāmyahaṃ.694.</p>\r\n<p class="gatha1">Dhammasiromaṇi sāmī, dhammasikharasekharo;</p>\r\n<p class="gathalast">Dhammacūḷāmaṇi cāru, buddhaṃ taṃ paṇamāmyahaṃ.695.</p>\r\n<p class="gatha1">Dhammabhūsanabhūsito, dhammālaṅkāra’laṅkito;</p>\r\n<p class="gathalast">Dhammābharaṇasobhito, buddhaṃ taṃ paṇamāmyahaṃ.696.</p>\r\n<p class="gatha1">Dhammadīpo dhammobhāso, dhammaloko dhammajuti;</p>\r\n<p class="gathalast">Dhammapakāso dhammaṃsu, buddhaṃ taṃ paṇamāmyahaṃ.697.</p>\r\n<p class="gatha1">Dhammagutto pāpamutto, dhammayutto dhammālayo;</p>\r\n<p class="gathalast">Dhammañatto dhammasañño, buddhaṃ taṃ paṇamāmyahaṃ.698.</p>\r\n<p class="gatha1">Dhammadassī dhammapassī, dhammapekkhī vicakkhaṇo;</p>\r\n<p class="gathalast">Dhammavipassī supassī, buddhaṃ taṃ paṇamāmyahaṃ.699.</p>\r\n<p class="gatha1">Dhammadakkhī dhammasakkhī, dhammarakkhī dhammadharo;</p>\r\n<p class="gathalast">Saddhammapekkhī upekkhī, buddhaṃ taṃ paṇamāmyahaṃ.700.</p>\r\n<p class="gatha1">Dhammasañño dhammapañño, dhammaabhiñño dhammañño;</p>\r\n<p class="gathalast">Dhañño dhammaparāyaṇo, buddhaṃ taṃ paṇamāmyahaṃ.701.</p>\r\n<p class="gatha1">Dhammañāṇī dhammabhāṇī, dhammavācī dhammavidū;</p>\r\n<p class="gathalast">Mantabhāṇī madhubhāṇī, buddhaṃ taṃ paṇamāmyahaṃ.702.</p>\r\n<p class="gatha1">Dhammavā dhammasampanno, ñāṇavā ñāṇasāgaro;</p>\r\n<p class="gathalast">Paññavā paññāvāridhi, buddhaṃ taṃ paṇamāmyahaṃ.703.</p>\r\n<p class="gatha1">Dhammakkho dhammarakkhako, buddhakkho bodhirakkhako;</p>\r\n<p class="gathalast">Paññakkho cāpi medhakkho, buddhaṃ taṃ paṇamāmyahaṃ.704.</p>\r\n<p class="gatha1">Adhammamutto dhammabhā, aññāṇamutto ñāṇabhā;</p>\r\n<p class="gathalast">Avijjāmutto vijjābhā, buddhaṃ taṃ paṇamāmyahaṃ.705.</p>\r\n<p class="gatha1">Dhammasindhu dhammodadhi, anantadhammasāgaro;</p>\r\n<p class="gathalast">Saddhammaratnākaro, buddhaṃ taṃ paṇamāmyahaṃ.706.</p>\r\n<p class="gatha1">Dhammapaṭibhānappatto, dhammappaṭipadāvidū;</p>\r\n<p class="gathalast">Paṭisambhidāmaggaññū, buddhaṃ taṃ paṇamāmyahaṃ.707.</p>\r\n<p class="gatha1">Tuṭṭho dhammasantuṭṭhito, dhīro dhammapatiṭṭhito;</p>\r\n<p class="gathalast">Aggapatiṭṭhito aggo, buddhaṃ taṃ paṇamāmyahaṃ.708.</p>\r\n<p class="gatha1">Mokkhaddhajaṃ unnāmesi, māraddhajaṃ onāmako;</p>\r\n<p class="gatha1">Pāpahārī sokahārī, buddhaṃ taṃ paṇamāmyahaṃ.709.</p>\r\n<p class="gatha1">Sabbāsavaṃ vivajjitvā, pattadhammaniyyāniko;</p>\r\n<p class="gathalast">Samiddhiguṇasampanno, buddhaṃ taṃ paṇamāmyahaṃ.710.</p>\r\n<p class="gatha1">Dhammadhārā pavāhesi, pāpadhārānirodhako;</p>\r\n<p class="gathalast">Atittānaṃ sutosesi, buddhaṃ taṃ paṇamāmyahaṃ.711.</p>\r\n<p class="gatha1">Dhammamegho paññāmegho, dhammāmataṃ pavassayī;</p>\r\n<p class="gathalast">Rajojallaṃ vidhovesi, buddhaṃ taṃ paṇamāmyahaṃ.712.</p>\r\n<p class="gatha1">Dhammañatto dhammakkhāyī, dhammamaggassa desako;</p>\r\n<p class="gathalast">Dhammiṃ dhammaṃ vitthāresi, buddhaṃ taṃ paṇamāmyahaṃ.713.</p>\r\n<p class="gatha1">Sabbañeyyadhammaṃ ñatvā, ñattadhammaṃ suvitarī;</p>\r\n<p class="gathalast">Abhiññāñāṇasampuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.714.</p>\r\n<p class="gatha1">Sabbasaddhammasampatto, tiṇṇasaṃsārasāgaro;</p>\r\n<p class="gathalast">Sabbapāpaparimutto, buddhaṃ taṃ paṇamāmyahaṃ.715.</p>\r\n<p class="gatha1">Siddhadhammo siddhaattho, sabbasaṃsārapūjito;</p>\r\n<p class="gathalast">Sabbuttamo sammacārī, buddhaṃ taṃ paṇamāmyahaṃ.716.</p>\r\n<p class="gatha1">Dhammakārī dhammadhārī, dhammacārī dhammayuto;</p>\r\n<p class="gathalast">Dhammasārī dhammabhāvī, buddhaṃ taṃ paṇamāmyahaṃ.717.</p>\r\n<p class="gatha1">Dhammajeṭṭho dhammaseṭṭho, dhammasuddho dhammasucī;</p>\r\n<p class="gathalast">Dhammaketu dhammaddhajo, buddhaṃ taṃ paṇamāmyahaṃ.718.</p>\r\n<p class="gatha1">Dhammakāmo dhammabhūto, dhammābhiramanto muni;</p>\r\n<p class="gathalast">Dhammānurāgī virāgī, buddhaṃ taṃ paṇamāmyahaṃ.719.</p>\r\n<p class="gatha1">Dhammañāto pāpanudo, dhammavihārī dhammiko;</p>\r\n<p class="gathalast">Dassesi amataṃ dhammaṃ, buddhaṃ taṃ paṇamāmyahaṃ.720.</p>\r\n<p class="gatha1">Dhammakāyo dhammarūpo, dhammanāmo dhammamudo;</p>\r\n<p class="gathalast">Dhammapatiṭṭho muttiṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.721.</p>\r\n<p class="gatha1">Dhammadhārako dhoreyho, dhammapuṇṇo’va puṇṇindu;</p>\r\n<p class="gathalast">Amatogadhaṃ sampatto, buddhaṃ taṃ paṇamāmyahaṃ.722.</p>\r\n<p class="gatha1">Dhammatikicchako dakkho, visallo sallakantako;</p>\r\n<p class="gathalast">Mahopakārī bhisakko, buddhaṃ taṃ paṇamāmyahaṃ.723.</p>\r\n<p class="gatha1">Dhammasakko dhammasiddho, dhammamakuṭasobhano;</p>\r\n<p class="gathalast">Dhammakanto dhammasanto, buddhaṃ taṃ paṇamāmyahaṃ.724.</p>\r\n<p class="gatha1">Dhammāñāṇī dhammakathī, dhammadāyī maggadado;</p>\r\n<p class="gathalast">Dhammavibhū dhammapabhū, buddhaṃ taṃ paṇamāmyahaṃ.725.</p>\r\n<p class="gatha1">Amatadhammaṃ ñāpesi, santāresi bahujanaṃ;</p>\r\n<p class="gathalast">Āturānaṃ tikicchako, buddhaṃ taṃ paṇamāmyahaṃ.726.</p>\r\n<p class="gatha1">Dhammabhāgī pāpacāgī, dhammabhajī pāpaccajo;</p>\r\n<p class="gathalast">Dhammarāgī dhammaramo, buddhaṃ taṃ paṇamāmyahaṃ.727.</p>\r\n<p class="gatha1">Dhammaviññū dhammavidvā, dhammavijjo dhammayuto;</p>\r\n<p class="gathalast">Dhammavattā dhammavyatto, buddhaṃ taṃ paṇamāmyahaṃ.728.</p>\r\n<p class="gatha1">Dhammānudhammadhārako, tāsānutāsanāsako;</p>\r\n<p class="gathalast">Bhayānubhayabhañjako, buddhaṃ taṃ paṇamāmyahaṃ.729.</p>\r\n<p class="gatha1">Visuddhadhammadhārako, sabbadhi upakārako;</p>\r\n<p class="gathalast">Cārucaraṇacārako, buddhaṃ taṃ paṇamāmyahaṃ.730.</p>\r\n<p class="gatha1">Dhammamakarandapāyī, muducitto madhukaro;</p>\r\n<p class="gathalast">Saddhammasudhāmathano, buddhaṃ taṃ paṇamāmyahaṃ.731.</p>\r\n<p class="gatha1">Ehipassikaṃ dhammadado, sandiṭṭhikamakālikaṃ;</p>\r\n<p class="gathalast">Opanayikaṃ svākkhātaṃ, buddhaṃ taṃ paṇamāmyahaṃ.732.</p>\r\n<p class="gatha1">Suddhadhammāmataṃ pāyī, subhuñjesi muttiphalaṃ;</p>\r\n<p class="gathalast">Santibhojī sukhabhakkhī, buddhaṃ taṃ paṇamāmyahaṃ.733.</p>\r\n<p class="gatha1">Mānito mahimāvanto, mahiddhiko mahīyato;</p>\r\n<p class="gathalast">Mahāsaddhammādhipati, buddhaṃ taṃ paṇamāmyahaṃ.734.</p>\r\n<p class="gatha1">Attasaraṇamanusāsi, nepakko anusāsako;</p>\r\n<p class="gathalast">Dhammasaraṇamanusāsi, buddhaṃ taṃ paṇamāmyahaṃ.735.</p>\r\n<p class="gatha1">Bodhipakkhiyadhammānaṃ, bhāvito bahulīkato;</p>\r\n<p class="gathalast">Sammāsambodhisampatto, buddhaṃ taṃ paṇamāmyahaṃ.736.</p>\r\n<p class="gatha1">Matimā dhitisampanno, dhīravaro sudhammiko;</p>\r\n<p class="gathalast">Dhammaṭṭhappaṭisaṃyutto, buddhaṃ taṃ paṇamāmyahaṃ.737.</p>\r\n<p class="gatha1">Dhamma’jjhāsayakusalo, dhammesu akathaṃkathī;</p>\r\n<p class="gathalast">Paramatthamanuppatto, buddhaṃ taṃ paṇamāmyahaṃ.738.</p>\r\n<p class="gatha1">Dhitibalo dhitidharo, dhitidhārī dhīsekharo;</p>\r\n<p class="gathalast">Uttamadhammesī dhīro, buddhaṃ taṃ paṇamāmyahaṃ.739.</p>\r\n<p class="gatha1">Sabbasantāpaṃ dhaṃsesi, sabbapāpavinodano;</p>\r\n<p class="gathalast">Sabbadhammaphalappatto, buddhaṃ taṃ paṇamāmyahaṃ.740.</p>\r\n<p class="gatha1">Virato sabbapāpehi, sabbādhammehi muñcito;</p>\r\n<p class="gathalast">Sabbadukkhabyantikaro, buddhaṃ taṃ paṇamāmyahaṃ.741.</p>\r\n<p class="gatha1">Sabbadhi pāpavimutto, suddhadhamme patiṭṭhito;</p>\r\n<p class="gathalast">Sabbadukkhā pamocesi, buddhaṃ taṃ paṇamāmyahaṃ.742.</p>\r\n<p class="gatha1">Sabbapāpappahīno yo, sabbadhammāna pāragū;</p>\r\n<p class="gathalast">Ghoradukkhoghauttiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.743.</p>\r\n<p class="gatha1">Sabbādhammaṃ viddhaṃsesi, sabbapāpakkhayaṅkaro;</p>\r\n<p class="gathalast">Kattako sabbabandhānaṃ, buddhaṃ taṃ paṇamāmyahaṃ.744.</p>\r\n<p class="gatha1">Sabbādhidhammaṃ pālesi, sabbapāpapanūdano;</p>\r\n<p class="gathalast">Sabbadukkhaṃ niddhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.745.</p>\r\n<p class="gatha1">Dukkhamocako mokkhado, samattho satthavāhako;</p>\r\n<p class="gathalast">Pāpanāsī dhammabhāsī, buddhaṃ taṃ paṇamāmyahaṃ.746.</p>\r\n<p class="gatha1">Dhammabhajo pāpaccajo, rāgaccajo cāgabhajo;</p>\r\n<p class="gathalast">Sukhabhajo dukkhaccajo, buddhaṃ taṃ paṇamāmyahaṃ.747.</p>\r\n<p class="gatha1">Sammājīvī dhammājīvī, suddhājīvī sucijīvī;</p>\r\n<p class="gathalast">Sīlajīvī saccājīvī, buddhaṃ taṃ paṇamāmyahaṃ.748.</p>\r\n<p class="gatha1">Paññājīvī aññājīvī, ñāṇājīvī varajīvī;</p>\r\n<p class="gathalast">Vijjājīvī viññājīvī, buddhaṃ taṃ paṇamāmyahaṃ.749.</p>\r\n<p class="gatha1">Khantijīvī santijīvī, sūjujīvī ujujīvī;</p>\r\n<p class="gathalast">Pītijīvī pītibhojī, buddhaṃ taṃ paṇamāmyahaṃ.750.</p>\r\n<p class="gatha1">Dibbājīvī bhabbājīvī, bhaddājīvī samajīvī;</p>\r\n<p class="gathalast">Modajīvī mettājīvī, buddhaṃ taṃ paṇamāmyahaṃ.751.</p>\r\n<p class="gatha1">Guttājīvī muttājīvī, dantājīvī yatajīvī;</p>\r\n<p class="gathalast">Dakkhājīvī seṭṭhājīvī, buddhaṃ taṃ paṇamāmyahaṃ.752.</p>\r\n<p class="gatha1">Hitajīvī sukhajīvī, jhānajīvī vatajīvī;</p>\r\n<p class="gathalast">Sudhājīvī subhājīvī, buddhaṃ taṃ paṇamāmyahaṃ.753.</p>\r\n<p class="gatha1">Visuddhajīvī saṃsuddho, santuṭṭhajīvī sutuṭṭho;</p>\r\n<p class="gathalast">Pavarajīvī pakaṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.754.</p>\r\n<p class="gatha1">Mettādhārī mettācārī, mettākārī mettālayo;</p>\r\n<p class="gathalast">Mettāsayo mettāsārī, buddhaṃ taṃ paṇamāmyahaṃ.755.</p>\r\n<p class="gatha1">Santidhārī santikārī, santicārī santimano;</p>\r\n<p class="gathalast">Santisārī santicitto, buddhaṃ taṃ paṇamāmyahaṃ.756.</p>\r\n<p class="gatha1">Khantidhārī khantikārī, khanticārī khantimano;</p>\r\n<p class="gathalast">Khantiyutto khantisārī, buddhaṃ taṃ paṇamāmyahaṃ.757.</p>\r\n<p class="gatha1">Sukhakārī sukhacārī, sukhadhārī sukhāvaho;</p>\r\n<p class="gathalast">Sukhasārī sukhacitto, buddhaṃ taṃ paṇamāmyahaṃ.758.</p>\r\n<p class="gatha1">Hitakārī hitadhārī, hitacārī hitāvaho;</p>\r\n<p class="gathalast">Hitasārī hitesino, buddhaṃ taṃ paṇamāmyahaṃ.759.</p>\r\n<p class="gatha1">Paññācārī paññāsārī, paññādhārī paññālayo;</p>\r\n<p class="gathalast">Paññābhānu paññādicco, buddhaṃ taṃ paṇamāmyahaṃ.760.</p>\r\n<p class="gatha1">Medhācārī medhāsārī, medhādhārī medhālayo;</p>\r\n<p class="gathalast">Medhācando medhāindo, buddhaṃ taṃ paṇamāmyahaṃ.761.</p>\r\n<p class="gatha1">Mettaṅkaro modaṅkaro, kusalo kusalaṅkaro;</p>\r\n<p class="gathalast">Medhācārī medhaṅkaro, buddhaṃ taṃ paṇamāmyahaṃ.762.</p>\r\n<p class="gatha1">Pabhaṅkaro divaṅkaro, kāruñño karuṇaṅkaro;</p>\r\n<p class="gathalast">Nibbhīto nibbhayaṅkaro, buddhaṃ taṃ paṇamāmyahaṃ.763.</p>\r\n<p class="gatha1">Sivaṅkaro subhaṅkaro, sukhaṅkaro hitaṅkaro;</p>\r\n<p class="gathalast">Sallaharo sūlaharo, buddhaṃ taṃ paṇamāmyahaṃ.764.</p>\r\n<p class="gatha1">Mahākhemaṅkaro khemī, mahāpaññaṅkaro pabhū;</p>\r\n<p class="gathalast">Mahāsantikaro santo, buddhaṃ taṃ paṇamāmyahaṃ.765.</p>\r\n<p class="gatha1">Anāthānaṃ bhavanātho, bhītānaṃ abhayaṅkaro;</p>\r\n<p class="gathalast">Saraṇaṅkaro dīnānaṃ, buddhaṃ taṃ paṇamāmyahaṃ.766.</p>\r\n<p class="gatha1">Dūradassī dīghadassī, antadassī bhavantagū;</p>\r\n<p class="gathalast">Tīradassī pāradassī, buddhaṃ taṃ paṇamāmyahaṃ.767.</p>\r\n<p class="gatha1">Sāradassī saccadassī, sivadassī sudassiko;</p>\r\n<p class="gathalast">Yogadassī khemadassī, buddhaṃ taṃ paṇamāmyahaṃ.768.</p>\r\n<p class="gatha1">Santidassī sukhadassī, suddhidassī suddhamano;</p>\r\n<p class="gathalast">Aggadassī dhuvadassī, buddhaṃ taṃ paṇamāmyahaṃ.769.</p>\r\n<p class="gatha1">Lokuttaradhammadassī, pariyantadassī isī;</p>\r\n<p class="gathalast">Maggadassī phaladassī, buddhaṃ taṃ paṇamāmyahaṃ.770.</p>\r\n<p class="gatha1">Atthadassī pathadassī, anomadassī sudassī;</p>\r\n<p class="gathalast">Paramapassī supassī, buddhaṃ taṃ paṇamāmyahaṃ.771.</p>\r\n<p class="gatha1">Khayadassī vayadassī, nayadassī sudassano;</p>\r\n<p class="gathalast">Akkhayadassī sudassī, buddhaṃ taṃ paṇamāmyahaṃ.772.</p>\r\n<p class="gatha1">Amatadassī paccakkho, pekkhako parisodhako;</p>\r\n<p class="gathalast">Paṭisambhidāsampatto, buddhaṃ taṃ paṇamāmyahaṃ.773.</p>\r\n<p class="gatha1">Sabbadhi sabbadassāvī, sabbapassī sabbavidū;</p>\r\n<p class="gathalast">Sabbaṃ sammā abhaññāsi, buddhaṃ taṃ paṇamāmyahaṃ.774.</p>\r\n<p class="gatha1">Hantvā kāmabhavataṇhā, vibhavataṇhābhañjako;</p>\r\n<p class="gathalast">Sabbataṇhā vicuṇṇesi, buddhaṃ taṃ paṇamāmyahaṃ.775.</p>\r\n<p class="gatha1">Taṇhāsaṃyojanakkhayo, taṇhāsallavicuṇṇako;</p>\r\n<p class="gathalast">Taṇhāpihā padāḷesi, buddhaṃ taṃ paṇamāmyahaṃ.776.</p>\r\n<p class="gatha1">Kāmataṇhāparikkhīṇo, bhavataṇhāupaccago;</p>\r\n<p class="gathalast">Vibhavataṇhāvinaṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.777.</p>\r\n<p class="gatha1">Tuṭṭhimante tosavante, atassante sutappayī;</p>\r\n<p class="gathalast">Taṇhātīto sadātitto, buddhaṃ taṃ paṇamāmyahaṃ.778.</p>\r\n<p class="gatha1">Kilesajālaviddhaṃsī, taṇhāmalapakkhālako;</p>\r\n<p class="gathalast">Saṃsārasotaṃ sosesi, buddhaṃ taṃ paṇamāmyahaṃ.779.</p>\r\n<p class="gatha1">Upadhipaṭinissaggo, taṇhājālappabhedako;</p>\r\n<p class="gathalast">Nīvaraṇe pavijjhesi, buddhaṃ taṃ paṇamāmyahaṃ.780.</p>\r\n<p class="gatha1">Guhāsayaparikkhīṇo, pahīnataṇhānissayo;</p>\r\n<p class="gathalast">Chinnaavijjānusayo, buddhaṃ taṃ paṇamāmyahaṃ.781.</p>\r\n<p class="gatha1">Taṇhāsallaṃ hanitvāna, tevijjo maccuhāyano;</p>\r\n<p class="gathalast">Sambodhimuttamaṃ patto, buddhaṃ taṃ paṇamāmyahaṃ.782.</p>\r\n<p class="gatha1">Taṇhājaṭā vijaṭesi, sabbaṭhāne nehanudo;</p>\r\n<p class="gathalast">Nandīrāgaṃ viddhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.783.</p>\r\n<p class="gatha1">Sabbāsāsamatikkanto, taṇhāsotavisosako;</p>\r\n<p class="gathalast">Dhammasotaṃ pavāhesi, buddhaṃ taṃ paṇamāmyahaṃ.784.</p>\r\n<p class="gatha1">Sabbataṇhakkhayaṃ patto, sabbadukkhappanūdano;</p>\r\n<p class="gathalast">Sabbupadhi sandālesi, buddhaṃ taṃ paṇamāmyahaṃ.785.</p>\r\n<p class="gatha1">Sabbataṇhā vikkhambhesi, paṭippassaddhasabbiccho;</p>\r\n<p class="gathalast">Sabbiñjā samucchindesi, buddhaṃ taṃ paṇamāmyahaṃ.786.</p>\r\n<p class="gatha1">Sabbiñjā paribhañjesi, sabbataṇhātigo yatī;</p>\r\n<p class="gathalast">Sabbicchā paricajjesi, buddhaṃ taṃ paṇamāmyahaṃ.787.</p>\r\n<p class="gatha1">Taṇhāsāgaranittiṇṇo, sabbamicchamanicchako;</p>\r\n<p class="gathalast">Cittasallaṃ vimocesi, buddhaṃ taṃ paṇamāmyahaṃ.788.</p>\r\n<p class="gatha1">Kammavaṭṭaṃ vivajjesi, kammaklesā apagato;</p>\r\n<p class="gathalast">Sabbattha saṃvuto sūro, buddhaṃ taṃ paṇamāmyahaṃ.789.</p>\r\n<p class="gatha1">Asatto upasantatto, kāmakodhabhayātigo;</p>\r\n<p class="gathalast">Bhavakammajaho cheko, buddhaṃ taṃ paṇamāmyahaṃ.790.</p>\r\n<p class="gatha1">Vigatasārado sīho, chinnachambho chinnadaro;</p>\r\n<p class="gathalast">Bhavakammakkhayappatto, buddhaṃ taṃ paṇamāmyahaṃ.791.</p>\r\n<p class="gatha1">Kammabījāni jhāpesi, mohamūlapalikhaṇo;</p>\r\n<p class="gathalast">Nijjaresi anusaye, buddhaṃ taṃ paṇamāmyahaṃ.792.</p>\r\n<p class="gatha1">Sabbakammaparikkhīṇo, sabbavipākanijjaro;</p>\r\n<p class="gathalast">Sabbapuññapāpañjaho, buddhaṃ taṃ paṇamāmyahaṃ.793.</p>\r\n<p class="gatha1">Sabbakammaṃ kilese ca, asesamabhivāhayī;</p>\r\n<p class="gathalast">Sabbagaṇṭhiṃ vimocesi, buddhaṃ taṃ paṇamāmyahaṃ.794.</p>\r\n<p class="gatha1">Sabbakammehi vimutto, sabbakammapariccajo;</p>\r\n<p class="gathalast">Sabbakāmaguṇā’peto, buddhaṃ taṃ paṇamāmyahaṃ.795.</p>\r\n<p class="gatha1">Avijjāmūlaṃ bhindesi, kammayantavighātako;</p>\r\n<p class="gathalast">Sabbakammakkhayaṃ patto, buddhaṃ taṃ paṇamāmyahaṃ.796.</p>\r\n<p class="gatha1">Natthikavādaṃ madditvā, atthikavādaṃ tiṭṭhayi;</p>\r\n<p class="gathalast">Sammādiṭṭhiṃ viññāpesi, buddhaṃ taṃ paṇamāmyahaṃ.797.</p>\r\n<p class="gatha1">Ajjhattanhātako nātho, tibhavoghapāraṅgato;</p>\r\n<p class="gathalast">Subhago sabbathā suddho, buddhaṃ taṃ paṇamāmyahaṃ.798.</p>\r\n<p class="gatha1">Nāgavaro sīhavaro, satthavāho satthuvaro;</p>\r\n<p class="gathalast">Dhīsāgaro dhīradharo, buddhaṃ taṃ paṇamāmyahaṃ.799.</p>\r\n<p class="gatha1">Tāpanudo dukkhanudo, sabbahitasukhadado;</p>\r\n<p class="gathalast">Vagguvado piyavado, buddhaṃ taṃ paṇamāmyahaṃ.800.</p>\r\n<p class="gatha1">Caraṇayutto tevijjo, mūlaghaccasamūhato;</p>\r\n<p class="gathalast">Pacchimajātisampatto, buddhaṃ taṃ paṇamāmyahaṃ.801.</p>\r\n<p class="gatha1">Tibhavaddhaja’cchariyo, tilokaketuabbhuto;</p>\r\n<p class="gathalast">Saddhammapatākāseṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.802.</p>\r\n<p class="gatha1">Yatindriyo santindriyo, pasannapasādindriyo;</p>\r\n<p class="gathalast">Subhāvitindriyo santo, buddhaṃ taṃ paṇamāmyahaṃ.803.</p>\r\n<p class="gatha1">Nabhūpamamedhāyutto, dhitiyutto selasamo;</p>\r\n<p class="gathalast">Khantiyutto dharāsamo, buddhaṃ taṃ paṇamāmyahaṃ.804.</p>\r\n<p class="gatha1">Appameyyasirimanto, santilaṅkāra’laṅkato;</p>\r\n<p class="gathalast">Sukhābharaṇabhūsito, buddhaṃ taṃ paṇamāmyahaṃ.805.</p>\r\n<p class="gatha1">Tiṃsapāramī sañcayi, suttiṇṇo tibhavaṇṇavā;</p>\r\n<p class="gathalast">Antimajātiyutto yo, buddhaṃ taṃ paṇamāmyahaṃ.806.</p>\r\n<p class="gatha1">Bhavāsavapariññāto, pāputtāpavūpasamo;</p>\r\n<p class="gathalast">Paramasukhadhigato, buddhaṃ taṃ paṇamāmyahaṃ.807.</p>\r\n<p class="gatha1">Bhaddappatto bhaddakkhāyī, bhaddamaggappakāsako;</p>\r\n<p class="gathalast">Bhaddakkho bhaddadassāvī, buddhaṃ taṃ paṇamāmyahaṃ.808.</p>\r\n<p class="gatha1">Mahāmahiddhiko dhīmā, bhavābhavaṃ byantikato;</p>\r\n<p class="gathalast">Yo amatapphalaṃ patto, buddhaṃ taṃ paṇamāmyahaṃ.809.</p>\r\n<p class="gatha1">Nibbānabhāgī bhagavā, santippatto subhāgyavā;</p>\r\n<p class="gathalast">Paramaṃ sivaṃ sampatto, buddhaṃ taṃ paṇamāmyahaṃ.810.</p>\r\n<p class="gatha1">Ācāraguṇasampanno, sabbaguṇānamākaro;</p>\r\n<p class="gathalast">Puṇṇindu viya sampuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.811.</p>\r\n<p class="gatha1">Nandīrāgaparikkhīṇo, upādānappamocano;</p>\r\n<p class="gathalast">Narottamo yogakkhemī, buddhaṃ taṃ paṇamāmyahaṃ.812.</p>\r\n<p class="gatha1">Kiccakārī katakicco, kiccākiccappakāsako;</p>\r\n<p class="gathalast">Kiccādhikaraṇadakkho, buddhaṃ taṃ paṇamāmyahaṃ.813.</p>\r\n<p class="gatha1">Supassaddho munivaro, paguṇo munipuṅgavo;</p>\r\n<p class="gathalast">Isipuṅgavo uttamo, buddhaṃ taṃ paṇamāmyahaṃ.814.</p>\r\n<p class="gatha1">Pahīnabhayabheravo, parisāsu visārado;</p>\r\n<p class="gathalast">Narasīhanādaṃ nadi, buddhaṃ taṃ paṇamāmyahaṃ.815.</p>\r\n<p class="gatha1">Pāragāmī pāragato, pavaro pariyantagū;</p>\r\n<p class="gathalast">Lokavidū lokantagū, buddhaṃ taṃ paṇamāmyahaṃ.816.</p>\r\n<p class="gatha1">Paramatthaparipuṇṇo, paramasaccadassano;</p>\r\n<p class="gathalast">Nibbānaṃ yo sacchikato, buddhaṃ taṃ paṇamāmyahaṃ.817.</p>\r\n<p class="gatha1">Lābhālābhe yasāyase, sammānaavamānane;</p>\r\n<p class="gathalast">Sabbattha samako sāmī, buddhaṃ taṃ paṇamāmyahaṃ.818.</p>\r\n<p class="gatha1">Paramatthappatto dhīro, paramanimmalottamo;</p>\r\n<p class="gathalast">Bhavabandhaṃ pamocesi, buddhaṃ taṃ paṇamāmyahaṃ.819.</p>\r\n<p class="gatha1">Paṭirūpo paṭibuddho, saṃsārapaṭimocako;</p>\r\n<p class="gathalast">Paṭipassaddhisaṃyutto, buddhaṃ taṃ paṇamāmyahaṃ.820.</p>\r\n<p class="gatha1">Pañcakkhandhapariññāto, paññāya paṭibhāvito;</p>\r\n<p class="gathalast">Sabbamohaṃ nimmaddesi, buddhaṃ taṃ paṇamāmyahaṃ.821.</p>\r\n<p class="gatha1">Padhāno purisavaro, pāragavesī pāragū;</p>\r\n<p class="gathalast">Pavivekamanuppatto, buddhaṃ taṃ paṇamāmyahaṃ.822.</p>\r\n<p class="gatha1">Sabbaklesakkhayaṃ patto, sabbānusayaddhaṃsito;</p>\r\n<p class="gathalast">Sabbāsave pahāsi yo, buddhaṃ taṃ paṇamāmyahaṃ.823.</p>\r\n<p class="gatha1">Sabbāvijjā sañchindesi, tikkhavijjāvudhandharo;</p>\r\n<p class="gathalast">Sabbāsayaṃ vināsesi, buddhaṃ taṃ paṇamāmyahaṃ.824.</p>\r\n<p class="gatha1">Sabbasokamatikkanto, sabbadukkhavināsako;</p>\r\n<p class="gathalast">Sabbupāyāsaucchinno, buddhaṃ taṃ paṇamāmyahaṃ.825.</p>\r\n<p class="gatha1">Muttimaggaṃ pakāsesi, muttimaggagavesako;</p>\r\n<p class="gathalast">Sabbabhavabyādhimutto, buddhaṃ taṃ paṇamāmyahaṃ.826.</p>\r\n<p class="gatha1">Yo na limpati kāmesu, āraggeriva sāsapo;</p>\r\n<p class="gathalast">Bhavāsattivisaṃyutto, buddhaṃ taṃ paṇamāmyahaṃ.827.</p>\r\n<p class="gatha1">Muni moneyya sampanno, bāhitapāpo brāhmaṇo;</p>\r\n<p class="gathalast">Samaṇo samatāvino, buddhaṃ taṃ paṇamāmyahaṃ.828.</p>\r\n<p class="gatha1">Bhavāsavā vidhūpito, kāmāsavā vūpasamo;</p>\r\n<p class="gathalast">Avijjāsavā vigato, buddhaṃ taṃ paṇamāmyahaṃ.829.</p>\r\n<p class="gatha1">Pariññeyyaṃ pariññāto, bhāvetabbaṃ ca bhāvito;</p>\r\n<p class="gathalast">Pahātabbaṃ pahīnaṃ yo, buddhaṃ taṃ paṇamāmyahaṃ.830.</p>\r\n<p class="gatha1">Santisukhamanuppatto, muttisotapavāhako;</p>\r\n<p class="gathalast">Bahū satte pamocesi, buddhaṃ taṃ paṇamāmyahaṃ.831.</p>\r\n<p class="gatha1">Pariññāpāragū cāpi, pahānapāragū pabhū;</p>\r\n<p class="gathalast">Bhāvanāpāragū vibhū, buddhaṃ taṃ paṇamāmyahaṃ.832.</p>\r\n<p class="gatha1">Āsattidosarahito, vippamutto nirupadhi;</p>\r\n<p class="gathalast">Onaddhamutto omutto, buddhaṃ taṃ paṇamāmyahaṃ.833.</p>\r\n<p class="gatha1">Nibbānapatto siddhattho, pagabbho papañcajaho;</p>\r\n<p class="gathalast">Pabuddho paṭisaraṇo, buddhaṃ taṃ paṇamāmyahaṃ.834.</p>\r\n<p class="gatha1">Maggaṭṭho api phalaṭṭho, mohajālasuphālako;</p>\r\n<p class="gathalast">Suphāsuko sumuttimā, buddhaṃ taṃ paṇamāmyahaṃ.835.</p>\r\n<p class="gatha1">Bodhiratanasampanno, pavarabhūrimedhaso;</p>\r\n<p class="gathalast">Brāhmaṇo samaṇo sādhu, buddhaṃ taṃ paṇamāmyahaṃ.836.</p>\r\n<p class="gatha1">Micchādiṭṭhiṃ padālesi, micchācāranimmaddako;</p>\r\n<p class="gathalast">Micchāñāṇaṃ nivāresi, buddhaṃ taṃ paṇamāmyahaṃ.837.</p>\r\n<p class="gatha1">Mohajālamatikkanto, duṭṭhantakassa antako;</p>\r\n<p class="gathalast">Māramadaṃ nimmaddesi, buddhaṃ taṃ paṇamāmyahaṃ.838.</p>\r\n<p class="gatha1">Siddhattho sadatthapatto, paripuṇṇamanoratho;</p>\r\n<p class="gathalast">Katakicco vūpasanto, buddhaṃ taṃ paṇamāmyahaṃ.839.</p>\r\n<p class="gatha1">Yassa māyā ca māno ca, moho makkho ca pātito;</p>\r\n<p class="gathalast">Vāri pokkharapattā’va, buddhaṃ taṃ paṇamāmyahaṃ.840.</p>\r\n<p class="gatha1">Uḷurājā’va vimalo, suddhacitto anāvilo;</p>\r\n<p class="gathalast">Asatto sugato nātho, buddhaṃ taṃ paṇamāmyahaṃ.841.</p>\r\n<p class="gatha1">Kāmabhavapparikkhīṇo, rūpabhavamupaccago;</p>\r\n<p class="gathalast">Arūpabhavaṃ bhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.842.</p>\r\n<p class="gatha1">Yasī yasaābhūsito, mahesī isikuñjaro;</p>\r\n<p class="gathalast">Hitesī hitasambhavo, buddhaṃ taṃ paṇamāmyahaṃ.843.</p>\r\n<p class="gatha1">Tapassī cāpi tejassī, yasassī ca yasodharo;</p>\r\n<p class="gathalast">Vipassī cāpi vidassī, buddhaṃ taṃ paṇamāmyahaṃ.844.</p>\r\n<p class="gatha1">Aggamaggagavesī ca, sukhesi sabbapāṇinaṃ;</p>\r\n<p class="gathalast">Mahāmuni mahesī ca, buddhaṃ taṃ paṇamāmyahaṃ.845.</p>\r\n<p class="gatha1">Atthavidū saccavidū, lokavidū lokantagū;</p>\r\n<p class="gathalast">Addhagū addhānagato, buddhaṃ taṃ paṇamāmyahaṃ.846.</p>\r\n<p class="gatha1">Sokasallasatticuṇṇo, sabbupādānajhāyako;</p>\r\n<p class="gathalast">Mohaṇṇavatiṇṇo vīro, buddhaṃ taṃ paṇamāmyahaṃ.847.</p>\r\n<p class="gatha1">Bhayabheravamatikkanto, abhīto vītasārado;</p>\r\n<p class="gathalast">Sabbathā chambhavimutto, buddhaṃ taṃ paṇamāmyahaṃ.848.</p>\r\n<p class="gatha1">Nippapañcarato nātho, nippapañcaṃ niddesayi;</p>\r\n<p class="gathalast">Sabbappapañcaṃ hāpesi, buddhaṃ taṃ paṇamāmyahaṃ.849.</p>\r\n<p class="gatha1">Ovādako viññāpako, tārako bahupāṇinaṃ;</p>\r\n<p class="gathalast">Desanākusalo satthā, buddhaṃ taṃ paṇamāmyahaṃ.850.</p>\r\n<p class="gatha1">Suddhahadayo saṃsuddho, vimalo virajamānaso;</p>\r\n<p class="gathalast">Nhātako nimmalacitto, buddhaṃ taṃ paṇamāmyahaṃ.851.</p>\r\n<p class="gatha1">Vikkhīṇajātisaṃsāro, antimadehadhārako;</p>\r\n<p class="gathalast">Saṃsāre asaṃsaranto, buddhaṃ taṃ paṇamāmyahaṃ.852.</p>\r\n<p class="gatha1">Dīpaṅkarapādamūle, muttiṃ hatthagataṃ caji;</p>\r\n<p class="gathalast">Sammāsambodhimākaṅkhī, buddhaṃ taṃ paṇamāmyahaṃ.853.</p>\r\n<p class="gatha1">Piyavācī piyabhāṇī, piyavādī vacīvaro;</p>\r\n<p class="gathalast">Vagguvācī vaggubhāṇī, buddhaṃ taṃ paṇamāmyahaṃ.854.</p>\r\n<p class="gatha1">Thiravāco khemavāco, pītivāco santivado;</p>\r\n<p class="gathalast">Acchabhāṇī tacchabhāṇī, buddhaṃ taṃ paṇamāmyahaṃ.855.</p>\r\n<p class="gatha1">Ñāṇabhāsī ñāṇavidū, kālabhāsī kālavidū;</p>\r\n<p class="gathalast">Saccabhāsī saccavidū, buddhaṃ taṃ paṇamāmyahaṃ.856.</p>\r\n<p class="gatha1">Karavīkabhāṇī ñāṇī, gambhīradhammadesako;</p>\r\n<p class="gathalast">Citrakathiko vicitto, buddhaṃ taṃ paṇamāmyahaṃ.857.</p>\r\n<p class="gatha1">Sammāsambodhisampanno, saddhammabalasobhito;</p>\r\n<p class="gathalast">Sampannavijjācaraṇo, buddhaṃ taṃ paṇamāmyahaṃ.858.</p>\r\n<p class="gatha1">Yassāsavā na vijjanti, vimuttacitto sabbadā;</p>\r\n<p class="gathalast">Paramaṃ padaṃ sampatto, buddhaṃ taṃ paṇamāmyahaṃ.859.</p>\r\n<p class="gatha1">Samathappatto samaṇo, brahmavihārī brāhmaṇo;</p>\r\n<p class="gathalast">Sabbamalaṃ pabbājesi, buddhaṃ taṃ paṇamāmyahaṃ.860.</p>\r\n<p class="gatha1">Nandībhavaparikkhīṇo, bhavoghamohamaccago;</p>\r\n<p class="gathalast">Sabbottamamanuppatto, buddhaṃ taṃ paṇamāmyahaṃ.861.</p>\r\n<p class="gatha1">Pañcanīvaraṇātīto, sattavisuddhidhāraṇo;</p>\r\n<p class="gathalast">Sattabojjhaṅgakusalo, buddhaṃ taṃ paṇamāmyahaṃ.862.</p>\r\n<p class="gatha1">Sabbapāramī pūretvā, suddhadhammagavesayī;</p>\r\n<p class="gathalast">Mokkhamaggaṃ pakāsesi, buddhaṃ taṃ paṇamāmyahaṃ.863.</p>\r\n<p class="gatha1">Lokābhibhū lokajito, lokaññū lokanāyako;</p>\r\n<p class="gathalast">Tilokanātho lokīso, buddhaṃ taṃ paṇamāmyahaṃ.864.</p>\r\n<p class="gatha1">Suvatthināmo sunāmo, saccanāmo sukhāvaho;</p>\r\n<p class="gathalast">Saccasandho saccavādo, buddhaṃ taṃ paṇamāmyahaṃ.865.</p>\r\n<p class="gatha1">Chandarāgavippahīno, amamo ca anāsayo;</p>\r\n<p class="gathalast">Asatto sugato suddho, buddhaṃ taṃ paṇamāmyahaṃ.866.</p>\r\n<p class="gatha1">Puṇṇavositavosāno, abhiññābalavosito;</p>\r\n<p class="gathalast">Jātimaccukkhayaṃ patto, buddhaṃ taṃ paṇamāmyahaṃ.867.</p>\r\n<p class="gatha1">Māyāmohasamucchinno, dosadohavidālako;</p>\r\n<p class="gathalast">Lobhalālasā dhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.868.</p>\r\n<p class="gatha1">Dosasallasamucchinno, appaṭibaddhamānaso;</p>\r\n<p class="gathalast">Daḷhacitto asantāpī, buddhaṃ taṃ paṇamāmyahaṃ.869.</p>\r\n<p class="gatha1">Paphullamānaso satthā, sadā pasannamānaso;</p>\r\n<p class="gathalast">Muditamānaso sāmī, buddhaṃ taṃ paṇamāmyahaṃ.870.</p>\r\n<p class="gatha1">Nirajamānaso nātho, sadā vimalamānaso;</p>\r\n<p class="gathalast">Visuddhamānaso suddho, buddhaṃ taṃ paṇamāmyahaṃ.871.</p>\r\n<p class="gatha1">Tejassī ca tejadhano, tapassī ca tapodhano;</p>\r\n<p class="gathalast">Ñāṇesī ca ñāṇadhano, buddhaṃ taṃ paṇamāmyahaṃ.872.</p>\r\n<p class="gatha1">Pamodito pamodesi, tosesi paritosako;</p>\r\n<p class="gathalast">Parirakkhako rakkhesi, buddhaṃ taṃ paṇamāmyahaṃ.873.</p>\r\n<p class="gatha1">Puṇṇavositavosāno, abhiññābalavosito;</p>\r\n<p class="gathalast">Jātimaccukkhayaṃ patto, buddhaṃ taṃ paṇamāmyahaṃ.874.</p>\r\n<p class="gatha1">Māyāmohasamucchinno, dosadohavidālako;</p>\r\n<p class="gathalast">Lobhalālasā dhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.875.</p>\r\n<p class="gatha1">Dosasallasamucchinno, appaṭibaddhamānaso;</p>\r\n<p class="gathalast">Daḷhacitto asantāpī, buddhaṃ taṃ paṇamāmyahaṃ.876.</p>\r\n<p class="gatha1">Sahetudhammaṃ ñāpesi, ātāpī jhāyī brāhmaṇo;</p>\r\n<p class="gathalast">Sabbā kaṅkhā vinodesi, buddhaṃ taṃ paṇamāmyahaṃ.877.</p>\r\n<p class="gatha1">Sabbā kaṅkhāyo vāpesi, ātāpī jhāyī brāhmaṇo;</p>\r\n<p class="gathalast">Paccayānaṃ khayaṃ vidvā, buddhaṃ taṃ paṇamāmyahaṃ.878.</p>\r\n<p class="gatha1">Mārasenā parājitvā, suriyo’va obhāsasi;</p>\r\n<p class="gathalast">Saccadhammamanuppatto, buddhaṃ taṃ paṇamāmyahaṃ.879.</p>\r\n<p class="gatha1">Tiṃsapāramīpūresi, sabbalokahitaṅkaro;</p>\r\n<p class="gathalast">Catusaccaṃ anvesayi, buddhaṃ taṃ paṇamāmyahaṃ.880.</p>\r\n<p class="gatha1">Catusaccaṃ sacchikatvā, taṇhānaṃ khayamajjhagā;</p>\r\n<p class="gathalast">Visaṅkhāragatacitto, buddhaṃ taṃ paṇamāmyahaṃ.881.</p>\r\n<p class="gatha1">Bhaddakāyo bhaddavāco, bhaddacitto bhaddāsayo;</p>\r\n<p class="gathalast">Samantabhaddo subhaddo, buddhaṃ taṃ paṇamāmyahaṃ.882.</p>\r\n<p class="gatha1">Santakāyo santavāco, santavā santacetaso;</p>\r\n<p class="gathalast">Santisamuddaṃ sampatto, buddhaṃ taṃ paṇamāmyahaṃ.883.</p>\r\n<p class="gatha1">Lokabandhu lokasāmī, lokādhipo lokissaro;</p>\r\n<p class="gathalast">Lokamitto lokasakhā, buddhaṃ taṃ paṇamāmyahaṃ.884.</p>\r\n<p class="gatha1">Akkharānaṃ sannipāte, pubbāparānaṃ pāragū;</p>\r\n<p class="gathalast">Niruttipadakovido, buddhaṃ taṃ paṇamāmyahaṃ.885.</p>\r\n<p class="gatha1">Santisukhapadāyako, vijjānidhi vināyako;</p>\r\n<p class="gathalast">Bāhūjane sahāyako, buddhaṃ taṃ paṇamāmyahaṃ.886.</p>\r\n<p class="gatha1">Vijayanto bodhimūḷe, patto sambodhimuttamaṃ;</p>\r\n<p class="gathalast">Antakassa antakaro, buddhaṃ taṃ paṇamāmyahaṃ.887.</p>\r\n<p class="gatha1">Vinayavādī viratto, dhammavādī dhammagato;</p>\r\n<p class="gathalast">Atthavādī atthappatto, buddhaṃ taṃ paṇamāmyahaṃ.888.</p>\r\n<p class="gatha1">Santivādī muduvādī, saccavādī saccarato;</p>\r\n<p class="gathalast">Bhūtavādī bhaddavādī, buddhaṃ taṃ paṇamāmyahaṃ.889.</p>\r\n<p class="gatha1">Yatakāyo yatavāco, yatacitto yatindriyo;</p>\r\n<p class="gathalast">Sabbavidhe yato yati, buddhaṃ taṃ paṇamāmyahaṃ.890.</p>\r\n<p class="gatha1">Niriccho ceva chinniccho, vīticcho icchāucchinno;</p>\r\n<p class="gathalast">Gaticcho icchānicchāto, buddhaṃ taṃ paṇamāmyahaṃ.891.</p>\r\n<p class="gatha1">Acchacitto acchacārī, anacchaicchāriñcako;</p>\r\n<p class="gathalast">Sabbicchā’nicchā ucchinno, buddhaṃ taṃ paṇamāmyahaṃ.892.</p>\r\n<p class="gatha1">Accito accisajjito, accucco ca accuttamo;</p>\r\n<p class="gathalast">Accutaṃ akataṃ patto, buddhaṃ taṃ paṇamāmyahaṃ.893.</p>\r\n<p class="gatha1">Suddho maggaṃ visodhesi, siddho samaṇasekharo;</p>\r\n<p class="gathalast">Buddho bodhiṃ pakāsesi, buddhaṃ taṃ paṇamāmyahaṃ.894.</p>\r\n<p class="gatha1">Paṇīto ca punīto ca, pavitto cāpi pāvano;</p>\r\n<p class="gathalast">Pesalo pavaro mejjho, buddhaṃ taṃ paṇamāmyahaṃ.895.</p>\r\n<p class="gatha1">Khemo khīṇapariḷāho, khemayutto khemakaro;</p>\r\n<p class="gathalast">Khemakkhetto khemaṭṭhāno, buddhaṃ taṃ paṇamāmyahaṃ.896.</p>\r\n<p class="gatha1">Gatabhogo gatarogo, gatasoko gatāvilo;</p>\r\n<p class="gathalast">Gatānutāpo gatindho, buddhaṃ taṃ paṇamāmyahaṃ.897.</p>\r\n<p class="gatha1">Pāragū sabbadukkhānaṃ, pariññāṇañca pāragū;</p>\r\n<p class="gathalast">Nirodhaṃ susacchikato, buddhaṃ taṃ paṇamāmyahaṃ.898.</p>\r\n<p class="gatha1">Sattavisuddhisampanno, suddho visuddhamānaso;</p>\r\n<p class="gathalast">Vimuttamānaso vidvā, buddhaṃ taṃ paṇamāmyahaṃ.899.</p>\r\n<p class="gatha1">Bhavābhave anubhavitvā, patto sumuttimuttamaṃ;</p>\r\n<p class="gathalast">Brahmacakkaṃ pavattesi, buddhaṃ taṃ paṇamāmyahaṃ.900.</p>\r\n<p class="gatha1">Bhūripañño bhūrimedho, bhūribodhipakāsako;</p>\r\n<p class="gathalast">Bhūriñāṇaṃ pasāresi, buddhaṃ taṃ paṇamāmyahaṃ.901.</p>\r\n<p class="gatha1">Bhāranikkhepako vīro, bhavasinehanāsako;</p>\r\n<p class="gathalast">Bhavanettiṃ vibhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.902.</p>\r\n<p class="gatha1">Bhāvitatto bhavamutto, bhāvito bhāvanārato;</p>\r\n<p class="gathalast">Bhāvitindriyo bhagavā, buddhaṃ taṃ paṇamāmyahaṃ.903.</p>\r\n<p class="gatha1">Mahābhisakko bhesajjo, bhavarogatikicchako;</p>\r\n<p class="gathalast">Bhavoghatārako bhaddo, buddhaṃ taṃ paṇamāmyahaṃ.904.</p>\r\n<p class="gatha1">Bhavābhavataṇhābhaggo, bhavāsattivibhañjako;</p>\r\n<p class="gathalast">Bhavasaṃyojanaṃ chindesi, buddhaṃ taṃ paṇamāmyahaṃ.905.</p>\r\n<p class="gatha1">Bhavantadassī subhaddo, bhavaggātīto nibbuto;</p>\r\n<p class="gathalast">Bhavantagū bhagavanto, buddhaṃ taṃ paṇamāmyahaṃ.906.</p>\r\n<p class="gatha1">Bhāvito ariyamaggo, bhavabandhanasamūhato;</p>\r\n<p class="gathalast">Bhayabheravaṃ bhedesi, buddhaṃ taṃ paṇamāmyahaṃ.907.</p>\r\n<p class="gatha1">Bhāvesi kusalaṃ dhammaṃ, bojjhaṅgaratanissaro;</p>\r\n<p class="gathalast">Bhavasotaṃ tiṇṇo nātho, buddhaṃ taṃ paṇamāmyahaṃ.908.</p>\r\n<p class="gatha1">Bhavasaṃyojanaṃ chetvā, bhavasaṅgātigo isi;</p>\r\n<p class="gathalast">Bhavasāgaramuttiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.909.</p>\r\n<p class="gatha1">Bhavayogavītivatto, bhavasantāpanibbuto;</p>\r\n<p class="gathalast">Bhavamohodadhitiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.910.</p>\r\n<p class="gatha1">Bhavabhogavisaṃyutto, bhāramutto bhavajayo;</p>\r\n<p class="gathalast">Bhavayogavippamutto, buddhaṃ taṃ paṇamāmyahaṃ.911.</p>\r\n<p class="gatha1">Bhavabhogoghanittiṇṇo, bhavajālasandālako;</p>\r\n<p class="gathalast">Bhavasaṃsaraṇātīto, buddhaṃ taṃ paṇamāmyahaṃ.912.</p>\r\n<p class="gatha1">Bhīrutāṇo bhīruleṇo, bhīrusaraṇadāyako;</p>\r\n<p class="gathalast">Bhīruārakkhako bhiyyo, buddhaṃ taṃ paṇamāmyahaṃ.913.</p>\r\n<p class="gatha1">Sabbāsaraṇasaraṇo, tāṇo leṇo surakkhako;</p>\r\n<p class="gathalast">Janānaṃ nandako bhiyyo, buddhaṃ taṃ paṇamāmyahaṃ.914.</p>\r\n<p class="gatha1">Sabbābādhaṃ accagamo, taṇhātimirantakaro;</p>\r\n<p class="gathalast">Kāmāsavaṃ pajahi yo, buddhaṃ taṃ paṇamāmyahaṃ.915.</p>\r\n<p class="gatha1">Sabbadhammābhisambuddho, mokkhamaggaganvesako;</p>\r\n<p class="gathalast">Paramatthadhammappatto, buddhaṃ taṃ paṇamāmyahaṃ.916.</p>\r\n<p class="gatha1">Sabbadhammaṃ sambodhesi, antakantakaro jino;</p>\r\n<p class="gathalast">Pariyantadhammappatto, buddhaṃ taṃ paṇamāmyahaṃ.917.</p>\r\n<p class="gatha1">Sabbalokaṃ pariññāsi, sabbalokapanūdano;</p>\r\n<p class="gathalast">Lokuttaradhammappatto, buddhaṃ taṃ paṇamāmyahaṃ.918.</p>\r\n<p class="gatha1">Sabbalokahitatthāya, bodhesi karuṇāpatī;</p>\r\n<p class="gathalast">Nipuṇatthadhammappatto, buddhaṃ taṃ paṇamāmyahaṃ.919.</p>\r\n<p class="gatha1">Sabbaiddhī abhiññāsi, sabbataṇhā nirodhako;</p>\r\n<p class="gathalast">Nirodhaṃ sakkhiṃ akāsi, buddhaṃ taṃ paṇamāmyahaṃ.920.</p>\r\n<p class="gatha1">Sabbasaccaṃ abhiññāsi, sabbaññū samaṇuttamo;</p>\r\n<p class="gathalast">Paṇḍito sabbadhammesu, buddhaṃ taṃ paṇamāmyahaṃ.921.</p>\r\n<p class="gatha1">Sabbasiddhatthasiddho ca, samantabhaddo sabbathā;</p>\r\n<p class="gathalast">Sabbuttamaṃ dhammaṃ laddho, buddhaṃ taṃ paṇamāmyahaṃ.922.</p>\r\n<p class="gatha1">Upeto bodhidhammehi, vimutto sabbabhavehi ca;</p>\r\n<p class="gathalast">Iṭṭhapatto muttā’niṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.923.</p>\r\n<p class="gatha1">Sabbāhārapariññāto, sabbāhāramanissito;</p>\r\n<p class="gathalast">Sabbādhārapariccāgī, buddhaṃ taṃ paṇamāmyahaṃ.924.</p>\r\n<p class="gatha1">Sabbamohanisā hantvā, sabbaṃ rāgaṃ dosaṃ nudo;</p>\r\n<p class="gathalast">Suddhidhammaṃ pakāsesi, buddhaṃ taṃ paṇamāmyahaṃ.925.</p>\r\n<p class="gatha1">Sabbakilesaṃ sosesi, sabbādānapanūdano;</p>\r\n<p class="gathalast">Sabbasokaṃ vināsesi, buddhaṃ taṃ paṇamāmyahaṃ.926.</p>\r\n<p class="gatha1">Sabbindriyagutto sāmī, sabbakaṅkhicchāsaṃvuto;</p>\r\n<p class="gathalast">Sabbalokesanācāgī, buddhaṃ taṃ paṇamāmyahaṃ.927.</p>\r\n<p class="gatha1">Sabbāsavapariccāgī, jātimaccu nivārayī;</p>\r\n<p class="gathalast">Bhavadukkhaṃ viddhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.928.</p>\r\n<p class="gatha1">Sabbakammaklesajaho, vītasaṅkhāracetaso;</p>\r\n<p class="gathalast">Antimasārīrappatto, buddhaṃ taṃ paṇamāmyahaṃ.929.</p>\r\n<p class="gatha1">Sabbabandhaṃ vighātesi, sabbantarāyanāsako;</p>\r\n<p class="gathalast">Bodhaññū dubbuddhimutto, buddhaṃ taṃ paṇamāmyahaṃ.930.</p>\r\n<p class="gatha1">Sabbamohaparikkhīṇo, sabbaññū sabbakovido;</p>\r\n<p class="gathalast">Sabbābhibhū sabbavidū, buddhaṃ taṃ paṇamāmyahaṃ.931.</p>\r\n<p class="gatha1">Sabbābhinivesā suñño, sabbaguṇapatiṭṭhito;</p>\r\n<p class="gathalast">Sabbaklese visodhesi, buddhaṃ taṃ paṇamāmyahaṃ.932.</p>\r\n<p class="gatha1">Sabbarāgaṃ virājesi, sabbadosaviddhaṃsako;</p>\r\n<p class="gathalast">Sabbamohavinimutto, buddhaṃ taṃ paṇamāmyahaṃ.933.</p>\r\n<p class="gatha1">Sabbadukkhapariññāto, sabbadukkhakkhayaṅkaro;</p>\r\n<p class="gathalast">Sabbadhi bhavabhañjano, buddhaṃ taṃ paṇamāmyahaṃ.934.</p>\r\n<p class="gatha1">Sabbadukkhamatikkanto, sabbadukkhassa antagū;</p>\r\n<p class="gathalast">Sabbadukkhappahīno ca, buddhaṃ taṃ paṇamāmyahaṃ.935.</p>\r\n<p class="gatha1">Saṃsārasāgaruttiṇṇo, sabbabhavāna pāragū;</p>\r\n<p class="gathalast">Antimadehaṃ dhāresi, buddhaṃ taṃ paṇamāmyahaṃ.936.</p>\r\n<p class="gatha1">Sabbathā ākaṅkhātīto, sabbasaṅgātigo sudhī;</p>\r\n<p class="gathalast">Sabbesu anūpalitto, buddhaṃ taṃ paṇamāmyahaṃ.937.</p>\r\n<p class="gatha1">Giddhiñjaho taṇhakkhayo, sabbacāgesu saṇṭhito;</p>\r\n<p class="gathalast">Sabbattha upasammato, buddhaṃ taṃ paṇamāmyahaṃ.938.</p>\r\n<p class="gatha1">Sabbāmitte vasīkatvā, sabbajino sabbābhibhū;</p>\r\n<p class="gathalast">Sabbaveravippamutto, buddhaṃ taṃ paṇamāmyahaṃ.939.</p>\r\n<p class="gatha1">Sabbabhogamatikkanto, sabbakāmaraticajo;</p>\r\n<p class="gathalast">Sabbakaṅkhicchā ucchinno, buddhaṃ taṃ paṇamāmyahaṃ.940.</p>\r\n<p class="gatha1">Sabbītiyo vītivatto, sabbabhītivināsako;</p>\r\n<p class="gathalast">Sabbadosadohadanto, buddhaṃ taṃ paṇamāmyahaṃ.941.</p>\r\n<p class="gatha1">Sabbakopakodhakhīṇo, kāmaklesamatikkamo;</p>\r\n<p class="gathalast">Sabbamohamāyāmutto, buddhaṃ taṃ paṇamāmyahaṃ.942.</p>\r\n<p class="gatha1">Sabbasaṃyojane chetvā, sabbasaṃsaya’pagato;</p>\r\n<p class="gathalast">Sabbupādānupacchinno, buddhaṃ taṃ paṇamāmyahaṃ.943.</p>\r\n<p class="gatha1">Sabbābhilāsā hāpesi, sabbaklesavisodhako;</p>\r\n<p class="gathalast">Sabbiñjā paṭippassaddho, buddhaṃ taṃ paṇamāmyahaṃ.944.</p>\r\n<p class="gatha1">Sabbādānapariccāgī, sabbasaṃyojanātigo;</p>\r\n<p class="gathalast">Sabbasaṅgavisaṃyutto, buddhaṃ taṃ paṇamāmyahaṃ.945.</p>\r\n<p class="gatha1">Sabbattha sumano sāmī, sabbasotthiṃ padāyako;</p>\r\n<p class="gathalast">Sabbesaṃ sampasīdesi, buddhaṃ taṃ paṇamāmyahaṃ.946.</p>\r\n<p class="gatha1">Sabbānalaṃ nibbāpesi, sabbasantāpamaddako;</p>\r\n<p class="gathalast">Sabbadhi sumutto santo, buddhaṃ taṃ paṇamāmyahaṃ.947.</p>\r\n<p class="gatha1">Sabbāsavaṃ parijāni, sabbabyādhivināsako;</p>\r\n<p class="gathalast">Sabbasokakkhayaṃ patto, buddhaṃ taṃ paṇamāmyahaṃ.948.</p>\r\n<p class="gatha1">Sabbarāgatamaṃ dhaṃsī, sabbadosatamaṃ nudo;</p>\r\n<p class="gathalast">Sabbamohatamaṃ hantā, buddhaṃ taṃ paṇamāmyahaṃ.949.</p>\r\n<p class="gatha1">Sabbadhi sabbatthappatto, sabbatthadassāvī isi;</p>\r\n<p class="gathalast">Sabbapāsaṇḍaṃ maddesi, buddhaṃ taṃ paṇamāmyahaṃ.950.</p>\r\n<p class="gatha1">Sabbapāramīsambhūto, sabbapariññāpūrito;</p>\r\n<p class="gathalast">Sabbaabhiññāsampuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.951.</p>\r\n<p class="gatha1">Sabbadukkhakkhayaṃ patto, sabbasokamatikkamo;</p>\r\n<p class="gathalast">Saṃsārasindhunittiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.952.</p>\r\n<p class="gatha1">Sīlasamādhisaṃyutto, vijjāvāridhi paññavā;</p>\r\n<p class="gathalast">Sabbāsavāchinnabhinno, buddhaṃ taṃ paṇamāmyahaṃ.953.</p>\r\n<p class="gatha1">Sabbatāpavippamutto, samucchinnasabbūpadhi;</p>\r\n<p class="gathalast">Sabbapapañcūpasamo, buddhaṃ taṃ paṇamāmyahaṃ.954.</p>\r\n<p class="gatha1">Sabbaahaṅkāramutto, sabbamamaṅkārakkhayo;</p>\r\n<p class="gathalast">Sabbāsatti vippamutto, buddhaṃ taṃ paṇamāmyahaṃ.955.</p>\r\n<p class="gatha1">Vijitasabbasaṅgāmo, sabbattha aparājito;</p>\r\n<p class="gathalast">Sabbe vattesi sabbaso, buddhaṃ taṃ paṇamāmyahaṃ.956.</p>\r\n<p class="gatha1">Sabbaññū tilokaseṭṭho, sabbasaṃyojanā nudo;</p>\r\n<p class="gathalast">Sabbaoghe nittharesi, buddhaṃ taṃ paṇamāmyahaṃ.957.</p>\r\n<p class="gatha1">Sabbabandhavinimutto, sabbagaṇṭhivikhaṇḍito;</p>\r\n<p class="gathalast">Sabbapāsehi mocesi, buddhaṃ taṃ paṇamāmyahaṃ.958.</p>\r\n<p class="gatha1">Sabbaganthasamucchinno, sabbayogavisaṃyuto;</p>\r\n<p class="gathalast">Hatakkhobho hatālayo, buddhaṃ taṃ paṇamāmyahaṃ.959.</p>\r\n<p class="gatha1">Sabbasaṃyojanā suñño, sabbavaṭṭavināsako;</p>\r\n<p class="gathalast">Sabbajaṭā vijaṭesi, buddhaṃ taṃ paṇamāmyahaṃ.960.</p>\r\n<p class="gatha1">Sabbesu anicchāsaññī, sabbābhijjhātigo isi;</p>\r\n<p class="gathalast">Sabbasaṃyoga’saṃyogo, buddhaṃ taṃ paṇamāmyahaṃ.961.</p>\r\n<p class="gatha1">Sabbasaṅkhārehi ritto, sabbāsatti panūdano;</p>\r\n<p class="gathalast">Sabbākusalapamutto, buddhaṃ taṃ paṇamāmyahaṃ.962.</p>\r\n<p class="gatha1">Sabbapakārasampanno, sabbasamantabhaddako;</p>\r\n<p class="gathalast">Sabbākāraparipuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.963.</p>\r\n<p class="gatha1">Sabba vijjā anuppatto, sabbasuguṇasaṅgaho;</p>\r\n<p class="gathalast">Bhaddako subhaddakārī, buddhaṃ taṃ paṇamāmyahaṃ.964.</p>\r\n<p class="gatha1">Sabbāsave pariññāto, sabbāsave byantikato;</p>\r\n<p class="gathalast">Sabbesaṃ paricajjesi, buddhaṃ taṃ paṇamāmyahaṃ.965.</p>\r\n<p class="gatha1">Sabbabandhavippamutto, sabbasaṅkappapūrito;</p>\r\n<p class="gathalast">Sabbakkhemaṃ vikāsesi, buddhaṃ taṃ paṇamāmyahaṃ.966.</p>\r\n<p class="gatha1">Sabbattha kusalo satthā, sabbattha kovido vidū;</p>\r\n<p class="gathalast">Sabbattha vimalo suddho, buddhaṃ taṃ paṇamāmyahaṃ.967.</p>\r\n<p class="gatha1">Sabbocca sabbatobhaddo, sabbathā maññitaṃ cajo;</p>\r\n<p class="gathalast">Sammādassanasampanno, buddhaṃ taṃ paṇamāmyahaṃ.968.</p>\r\n<p class="gatha1">Anavasesañāṇaññū, sabbato suvijānako;</p>\r\n<p class="gathalast">Sabbaaññāṇamucchinno, buddhaṃ taṃ paṇamāmyahaṃ.969.</p>\r\n<p class="gatha1">Sabbaññāsampatto nātho, āsattiritto sabbadhi;</p>\r\n<p class="gathalast">Sabbattha sabbavisiṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.970.</p>\r\n<p class="gatha1">Sabbakilesehi suñño, sabbaganthippamocako;</p>\r\n<p class="gathalast">Sabbabandhaṃ vicchindesi, buddhaṃ taṃ paṇamāmyahaṃ.971.</p>\r\n<p class="gatha1">Sabbabhavaupacchinno, ucchinnalokabandhano;</p>\r\n<p class="gathalast">Sabbaoghaṃ nittharaṇo, buddhaṃ taṃ paṇamāmyahaṃ.972.</p>\r\n<p class="gatha1">Nittiṇṇabhavasāgaro, katakicco yatissaro;</p>\r\n<p class="gathalast">Sabbaso sītalībhūto, buddhaṃ taṃ paṇamāmyahaṃ.973.</p>\r\n<p class="gatha1">Sabbalokamabhibhūto, sabbalokavidū isi;</p>\r\n<p class="gathalast">Sabbalokaṃ sudassāvī, buddhaṃ taṃ paṇamāmyahaṃ.974.</p>\r\n<p class="gatha1">Sabbalokavisaṃyutto, sabbalokavirajjako;</p>\r\n<p class="gathalast">Sabbalokaṃ nirodhesi, buddhaṃ taṃ paṇamāmyahaṃ.975.</p>\r\n<p class="gatha1">Sabbaloke anāsaṃso, sabbaloke anūpayo;</p>\r\n<p class="gathalast">Sabbaloke saṅgasuñño, buddhaṃ taṃ paṇamāmyahaṃ.976.</p>\r\n<p class="gatha1">Sabbaabhiññā sampatto, sabbadhi sumativaro;</p>\r\n<p class="gathalast">Sabbasaccaṃ vitthāresi, buddhaṃ taṃ paṇamāmyahaṃ.977.</p>\r\n<p class="gatha1">Sabbapāramīsampanno, sabbaguṇānupāgato;</p>\r\n<p class="gathalast">Sabbathā pariññāpatto, buddhaṃ taṃ paṇamāmyahaṃ.978.</p>\r\n<p class="gatha1">Bhavasotaṃ visosesi, sabbaklesamupaccago;</p>\r\n<p class="gathalast">Sabbāsavaṃ vikkhālesi, buddhaṃ taṃ paṇamāmyahaṃ.979.</p>\r\n<p class="gatha1">Sabbathā sampaṭinissajji, sabbaṃ santarabāhiraṃ;</p>\r\n<p class="gathalast">Sabbaṃ sotthiṃ sacchikato, buddhaṃ taṃ paṇamāmyahaṃ.980.</p>\r\n<p class="gatha1">Sabbappapañcaṃ pahāsi yo, pariccāgesu saṇṭhito;</p>\r\n<p class="gathalast">Sabbasaṅgaṃ vivajjesi, buddhaṃ taṃ paṇamāmyahaṃ.981.</p>\r\n<p class="gatha1">Sabba’ntarāyavihato, sabba’ghānalanibbuto;</p>\r\n<p class="gathalast">Sabbasārambhaṃ bhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.982.</p>\r\n<p class="gatha1">Sabbābhilāsā ucchinno, sabbābhimānabhindako;</p>\r\n<p class="gathalast">Sabbā’nusayaṃ nissaṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.983.</p>\r\n<p class="gatha1">Sabbasaṃsaṭṭhavicchinno, sabbasaṃyojanā cajo;</p>\r\n<p class="gathalast">Sabbagedhapabhedako, buddhaṃ taṃ paṇamāmyahaṃ.984.</p>\r\n<p class="gatha1">Sabbupādānūpasamo, sabbākaṅkhāvikhaṇḍito;</p>\r\n<p class="gathalast">Sabbasallaṃ vicuṇṇesi, buddhaṃ taṃ paṇamāmyahaṃ.985.</p>\r\n<p class="gatha1">Sabbamohaṃ vinodesi, sabbamānanimaddako;</p>\r\n<p class="gathalast">Sabbabhītiṃ vītivatto, buddhaṃ taṃ paṇamāmyahaṃ.986.</p>\r\n<p class="gatha1">Kāmāsattiṃ byantikato, sabbachandasañchindako;</p>\r\n<p class="gathalast">Sabbā icchā ucchedesi, buddhaṃ taṃ paṇamāmyahaṃ.987.</p>\r\n<p class="gatha1">Sabbasammohaṃ maddesi, sabbasotavisosako;</p>\r\n<p class="gathalast">Sabbaganthiṃ niddhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.988.</p>\r\n<p class="gatha1">Sabbissā samatikkanto, sabbato iñjā riñcako;</p>\r\n<p class="gathalast">Sabbadosadohaṃ hantā, buddhaṃ taṃ paṇamāmyahaṃ.989.</p>\r\n<p class="gatha1">Sabbakodhamatikkanto, sabbakāmanimaddako;</p>\r\n<p class="gathalast">Sabbamohasamucchinno, buddhaṃ taṃ paṇamāmyahaṃ.990.</p>\r\n<p class="gatha1">Bhavanettiṃ sañchindesi, sabbabhogajigucchako;</p>\r\n<p class="gathalast">Sabbabodhiguṇupeto, buddhaṃ taṃ paṇamāmyahaṃ.991.</p>\r\n<p class="gatha1">Sabbatthadohātīto yo, sabbamānātigo muni;</p>\r\n<p class="gathalast">Sabbadosaṃ vippamutto, buddhaṃ taṃ paṇamāmyahaṃ.992.</p>\r\n<p class="gatha1">Sabbasaṃsayā vimutto, sabbasaṃyojanācuto;</p>\r\n<p class="gathalast">Sabbasaṃsāroghaṃ tiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.993.</p>\r\n<p class="gatha1">Sabbamhi anūpalitto, sabbavidū sabbābhibhū;</p>\r\n<p class="gathalast">Sabbantarāyavikkhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.994.</p>\r\n<p class="gatha1">Sabbūpadhimatikkanto, sabbāsavakkhayaṅkaro;</p>\r\n<p class="gathalast">Sīho’va anutrāso, buddhaṃ taṃ paṇamāmyahaṃ.995.</p>\r\n<p class="gatha1">Sabbamamaṅkāramutto, sabbaahaṃkārakkhayo;</p>\r\n<p class="gathalast">Sabbadosamatikkanto, buddhaṃ taṃ paṇamāmyahaṃ.996.</p>\r\n<p class="gatha1">Sabbabhavapathaṃ khinno, sabbasaṅkhāranibbuto;</p>\r\n<p class="gathalast">Sabbabandhanaṃ bhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.997.</p>\r\n<p class="gatha1">Sabbīghā samugghātesi, sabbindhā parinissago;</p>\r\n<p class="gathalast">Sabbissā paṭinissaṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.998.</p>\r\n<p class="gatha1">Sabbagiddhāgijjhācāgī, sabbamohamāyānudo;</p>\r\n<p class="gathalast">Sabbābhijjhā vibhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.999.</p>\r\n<p class="gatha1">Sabbakopakodhātīto, sabbadosaklesaccajo;</p>\r\n<p class="gathalast">Sabbadohamatikkanto, buddhaṃ taṃ paṇamāmyahaṃ.1000.</p>\r\n<p class="gatha1">Sabbarāgaṃ virajjesi, sabbadosavināsako;</p>\r\n<p class="gathalast">Sabbamohamatikkanto, buddhaṃ taṃ paṇamāmyahaṃ.1001.</p>\r\n<p class="gatha1">Sabbamalaṃ pakkhālesi, sabbasallasandālako;</p>\r\n<p class="gathalast">Sabbakhilaṃ viddālesi, buddhaṃ taṃ paṇamāmyahaṃ.1002.</p>\r\n<p class="gatha1">Sabbabhayamatikkanto, sabbabhītibyantikato;</p>\r\n<p class="gathalast">Sabbachambhisamucchinno, buddhaṃ taṃ paṇamāmyahaṃ.1003.</p>\r\n<p class="gatha1">Sabbalobhaabbuḷhanto, sabbarajaṃ virājesī;</p>\r\n<p class="gathalast">Sabbanandī vicchindesi, buddhaṃ taṃ paṇamāmyahaṃ.1004.</p>\r\n<p class="gatha1">Sabbasokasamucchinno, sabbadosavisosako;</p>\r\n<p class="gathalast">Sabbanettiṃ nivāresi, buddhaṃ taṃ paṇamāmyahaṃ.1005.</p>\r\n<p class="gatha1">Sabbajātiṃ nijjaresi, sabbabhavagginibbuto;</p>\r\n<p class="gathalast">Sabbalokamatikkanto, buddhaṃ taṃ paṇamāmyahaṃ.1006.</p>\r\n<p class="gatha1">Sabbasaṃsaṭṭhavirato, sabbasaṃsaggaārako;</p>\r\n<p class="gathalast">Sabbāsattinirattako, buddhaṃ taṃ paṇamāmyahaṃ.1007.</p>\r\n<p class="gatha1">Sabbākaṅkhāparikkhīṇo, sabbāpekkhāvikkhambhako;</p>\r\n<p class="gathalast">Sabbābhilāsā bhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.1008.</p>\r\n<p class="gatha1">Sabbāsaṅkā nivāresi, sabbātaṅkāvicchindako;</p>\r\n<p class="gathalast">Sabbāvilaṃ vikkhālesi, buddhaṃ taṃ paṇamāmyahaṃ.1009.</p>\r\n<p class="gatha1">Sabbālayasamucchinno, sabbāsayaanissito;</p>\r\n<p class="gathalast">Sabbāsave visosesi, buddhaṃ taṃ paṇamāmyahaṃ.1010.</p>\r\n<p class="gatha1">Sabbupadhiṃ niddhovesi, sabbārammaṇariñcako;</p>\r\n<p class="gathalast">Sabbābhimānaṃ bhañjesi, buddhaṃ taṃ paṇamāmyahaṃ.1011.</p>\r\n<p class="gatha1">Sabbupāyāsaṃ ukkhitto, sabbupādānaṃ ujjhito;</p>\r\n<p class="gathalast">Sabbābhijjhā ucchedesi, buddhaṃ taṃ paṇamāmyahaṃ.1012.</p>\r\n<p class="gatha1">Sabbasaṃyojanaṃ chetvā, suddhājīvī suddhācaro;</p>\r\n<p class="gathalast">Saṅgātigo oghatiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1013.</p>\r\n<p class="gatha1">Sabbadomanassasanto, sabbasaṃyojanā hato;</p>\r\n<p class="gathalast">Sabbāsaṅkāvippamutto, buddhaṃ taṃ paṇamāmyahaṃ.1014.</p>\r\n<p class="gatha1">Sabbasokavītivatto, sabbanīvaraṇānudo;</p>\r\n<p class="gathalast">Sabbadomanassamutto, buddhaṃ taṃ paṇamāmyahaṃ.1015.</p>\r\n<p class="gatha1">Sabbabhītibhayātīto, chambhanasuñño sabbaso;</p>\r\n<p class="gathalast">Sabbākulatā nāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1016.</p>\r\n<p class="gatha1">Sabbabhoge pahāsi yo, sabbāsattipariccajo;</p>\r\n<p class="gathalast">Sabbakāmamatikkamo, buddhaṃ taṃ paṇamāmyahaṃ.1017.</p>\r\n<p class="gatha1">Sabbottamayogakkhemī, sabbottamaṃ adhigamo;</p>\r\n<p class="gathalast">Sabbottamasantimanto, buddhaṃ taṃ paṇamāmyahaṃ.1018.</p>\r\n<p class="gatha1">Sabbavositavosāno, katakicco yatindriyo;</p>\r\n<p class="gathalast">Mahāmohaṇṇavuttiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1019.</p>\r\n<p class="gatha1">Dukkhakkhandhaṃ pariññāto, taṇhāpāsavicchedako;</p>\r\n<p class="gathalast">Sabbasantāsasumutto, buddhaṃ taṃ paṇamāmyahaṃ.1020.</p>\r\n<p class="gatha1">Sabbayogavītivatto, sītibhūto nirūpadhi;</p>\r\n<p class="gathalast">Bhavarāgaparikkhīṇo, buddhaṃ taṃ paṇamāmyahaṃ.1021.</p>\r\n<p class="gatha1">Sabbejā samatikkanto, sabbadhiṃ samatāṭhito;</p>\r\n<p class="gathalast">Sadā sumatidāyako, buddhaṃ taṃ paṇamāmyahaṃ.1022.</p>\r\n<p class="gatha1">Bhavena nirāsattiko, paramoyaṃ samussayo;</p>\r\n<p class="gathalast">Pahāsi sabbasaṅkhāre, buddhaṃ taṃ paṇamāmyahaṃ.1023.</p>\r\n<p class="gatha1">Sabbadukkhaparimutto, dosadohapadhaṃsako;</p>\r\n<p class="gathalast">Bhavaganthaṃ padālesi, buddhaṃ taṃ paṇamāmyahaṃ.1024.</p>\r\n<p class="gatha1">Aṃsumālī accimālī, raṃsimālī dhammaravi;</p>\r\n<p class="gathalast">Moha’māvasī nāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1025.</p>\r\n<p class="gatha1">Candimā’va santikaro, candimā’va pabhākaro;</p>\r\n<p class="gathalast">Santipabhā saṇṭhapesi, buddhaṃ taṃ paṇamāmyahaṃ.1026.</p>\r\n<p class="gatha1">Aggikkhandho’va suppabho, bhabbatejo sukhālayo;</p>\r\n<p class="gathalast">Obhāsito ukkādhārī, buddhaṃ taṃ paṇamāmyahaṃ.1027.</p>\r\n<p class="gatha1">Accimā’va pabhassaro, sabbaloke ālokadā;</p>\r\n<p class="gathalast">Atiruciro obhāso, buddhaṃ taṃ paṇamāmyahaṃ.1028.</p>\r\n<p class="gatha1">Byāmappabhāya suppabho, karuṇāruṇaujjalo;</p>\r\n<p class="gathalast">Rucirābhāya sampanno, buddhaṃ taṃ paṇamāmyahaṃ.1029.</p>\r\n<p class="gatha1">Induviya’mbaramajjhe, saṅghamajjhe virocayi;</p>\r\n<p class="gathalast">Ñāṇālokaṃ vikiresi, buddhaṃ taṃ paṇamāmyahaṃ.1030.</p>\r\n<p class="gatha1">Ādiccabandhu ātāpī, dibbarūpo virocano;</p>\r\n<p class="gathalast">Sabbalokaṃ pajjotesi, buddhaṃ taṃ paṇamāmyahaṃ.1031.</p>\r\n<p class="gatha1">Majjhe samaṇasaṅghassa, ādicco’va virocayī;</p>\r\n<p class="gathalast">Vijjālokakaro loke, buddhaṃ taṃ paṇamāmyahaṃ.1032.</p>\r\n<p class="gatha1">Tārā’va samaṇamajjhe, puṇṇindusamasobhito;</p>\r\n<p class="gathalast">Sabbasattuttamo sāmī, buddhaṃ taṃ paṇamāmyahaṃ.1033.</p>\r\n<p class="gatha1">Cāruñāṇasikhādhārī, sobhājotisamujjalo;</p>\r\n<p class="gathalast">Pabhassaraṃ pabhāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1034.</p>\r\n<p class="gatha1">Dinakaro tamonudo, ditto timiraddhaṃsako;</p>\r\n<p class="gathalast">Chabbaṇṇaraṃsīsobhito, buddhaṃ taṃ paṇamāmyahaṃ.1035.</p>\r\n<p class="gatha1">Indu’va nimmalo suddho, paramapuriso yasī;</p>\r\n<p class="gathalast">Varalakkhaṇasampuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1036.</p>\r\n<p class="gatha1">Puṇṇindasadiso juṇho, sampuṇṇasantamānaso;</p>\r\n<p class="gathalast">Sabbato sītalībhūto, buddhaṃ taṃ paṇamāmyahaṃ.1037.</p>\r\n<p class="gatha1">Cārudassī piyadassī, aṅgīraso ālokito;</p>\r\n<p class="gathalast">Pabhaṅkaro’va ujjoto, buddhaṃ taṃ paṇamāmyahaṃ.1038.</p>\r\n<p class="gatha1">Indu’va ambaratale, abhivaṇṇo susobhito;</p>\r\n<p class="gathalast">Mahādhammappabhāyutto, buddhaṃ taṃ paṇamāmyahaṃ.1039.</p>\r\n<p class="gatha1">Ārocito obhāsito, suriyo’va virocito;</p>\r\n<p class="gathalast">Atulatejo tejassī, buddhaṃ taṃ paṇamāmyahaṃ.1040.</p>\r\n<p class="gatha1">Sahassaraṃsī bhagavā, padumāmalasucchavī;</p>\r\n<p class="gathalast">Dhammappabhāparivuto, buddhaṃ taṃ paṇamāmyahaṃ.1041.</p>\r\n<p class="gatha1">Sobhito sālarājā’va, puṇṇamāyaṃ’va candimā;</p>\r\n<p class="gathalast">Obhāsesi disā sabbā, buddhaṃ taṃ paṇamāmyahaṃ.1042.</p>\r\n<p class="gatha1">Raṃsijālaparikkhitto, padumānanalocano;</p>\r\n<p class="gathalast">Kanakaṃ’va virocesi, buddhaṃ taṃ paṇamāmyahaṃ.1043.</p>\r\n<p class="gatha1">Mettāpabhāparivuto, dhammabhānupabhāsito;</p>\r\n<p class="gathalast">Paññappabhā vikāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1044.</p>\r\n<p class="gatha1">Paridhoto parisuddho, candimā iva puṇṇako;</p>\r\n<p class="gathalast">Saddhamaṃ parisodhesi, buddhaṃ taṃ paṇamāmyahaṃ.1045.</p>\r\n<p class="gatha1">Ñāṇaraṃsiṃ vikiresi, dhammādiccasubhāsuro;</p>\r\n<p class="gathalast">Muttipanthaṃ pakāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1046.</p>\r\n<p class="gatha1">Antalikkhe puṇṇindu’va, sītalapabhādāyako;</p>\r\n<p class="gathalast">Suddhadhammajotikaro, buddhaṃ taṃ paṇamāmyahaṃ.1047.</p>\r\n<p class="gatha1">Saddhammajotiṃ jotesi, jutivanto jutindharo;</p>\r\n<p class="gathalast">Sabbalokaṃ pajjotesi, buddhaṃ taṃ paṇamāmyahaṃ.1048.</p>\r\n<p class="gatha1">Pabhassaro tilokaggo, saddhammassa sudīpako;</p>\r\n<p class="gathalast">Ariyañāṇaṃ vitthāresi, buddhaṃ taṃ paṇamāmyahaṃ.1049.</p>\r\n<p class="gatha1">Candimā’va gaganatale, sītābhā suppakāsako;</p>\r\n<p class="gathalast">Anūnasukhadāyako, buddhaṃ taṃ paṇamāmyahaṃ.1050.</p>\r\n<p class="gatha1">Jalitappadīpaṃ ditto, tilokassatimiraharo;</p>\r\n<p class="gathalast">Ariyamaggaṃ jotesi, buddhaṃ taṃ paṇamāmyahaṃ.1051.</p>\r\n<p class="gatha1">Jutiṅkaro jotidharo, pabhaṅkaro pabhādharo;</p>\r\n<p class="gathalast">Ābhādharo ābhākaro, buddhaṃ taṃ paṇamāmyahaṃ.1052.</p>\r\n<p class="gatha1">Avijjātamaṃ dhaṃsesi, vijjābhānusamujjalo;</p>\r\n<p class="gathalast">Dhammavibhā vibhāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1053.</p>\r\n<p class="gatha1">Tilokatimiraṃ hantā, medhāmuddhāsamujjalo;</p>\r\n<p class="gathalast">Lokālokakaro nātho, buddhaṃ taṃ paṇamāmyahaṃ.1054.</p>\r\n<p class="gatha1">Mohatimiraṃ dhaṃsesi, pabhākaro padīpako;</p>\r\n<p class="gathalast">Saccadhammaṃ pakāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1055.</p>\r\n<p class="gatha1">Karuṇāruṇo āloko, paññāraṃsipakāsako;</p>\r\n<p class="gathalast">Avijjāvaraṇabhinno, buddhaṃ taṃ paṇamāmyahaṃ.1056.</p>\r\n<p class="gatha1">Karuṇākaro paññābho, tibhave ālokaṅkaro;</p>\r\n<p class="gathalast">Santirasmī vipphāresi, buddhaṃ taṃ paṇamāmyahaṃ.1057.</p>\r\n<p class="gatha1">Puṇṇindu viya suditto, ñāṇindu ñāṇapuṇṇiko;</p>\r\n<p class="gathalast">Saddhammābhā pabhāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1058.</p>\r\n<p class="gatha1">Mohatimisikāchinno, dīpaṅkaro sudīpito;</p>\r\n<p class="gathalast">Bodhippabhā pabhāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1059.</p>\r\n<p class="gatha1">Avijjānisā nāsesi, tiloke dhammasūriyo;</p>\r\n<p class="gathalast">Dhammaraṃsiṃ vikiresi, buddhaṃ taṃ paṇamāmyahaṃ.1060.</p>\r\n<p class="gatha1">Avijjācchādite loke, vijjālokaṃ vikāsayī;</p>\r\n<p class="gathalast">Dhammappabhā visajjesi, buddhaṃ taṃ paṇamāmyahaṃ.1061.</p>\r\n<p class="gatha1">Mohanisā vināsesi, dhammujjalo divākaro;</p>\r\n<p class="gathalast">Lokālokaṃ ālokesi, buddhaṃ taṃ paṇamāmyahaṃ.1062.</p>\r\n<p class="gatha1">Tibhavassa tamohantā, tejassī jinasūriyo;</p>\r\n<p class="gathalast">Ābhassaro ābhāpuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1063.</p>\r\n<p class="gatha1">Bhaddatejo mahātejo, tibbatejo tejabahū;</p>\r\n<p class="gathalast">Jhānatejo ñāṇatejo, buddhaṃ taṃ paṇamāmyahaṃ.1064.</p>\r\n<p class="gatha1">Uggatejo aggatejo, puṇṇatejo tejissaro;</p>\r\n<p class="gathalast">Brahmatejo dhammatejo, buddhaṃ taṃ paṇamāmyahaṃ.1065.</p>\r\n<p class="gatha1">Sabbadisā pabhāsesi, sabbatthaṃ sampakāsako;</p>\r\n<p class="gathalast">Mahāpabhassaro uggo, buddhaṃ taṃ paṇamāmyahaṃ.1066.</p>\r\n<p class="gatha1">Bhūripañño pabhaṅkaro, sabbathā tamanāsako;</p>\r\n<p class="gathalast">Ariyasaccaṃ jotesi, buddhaṃ taṃ paṇamāmyahaṃ.1067.</p>\r\n<p class="gatha1">Raṃsimanto raṃsidharo, jotimanto jotikaro;</p>\r\n<p class="gathalast">Sahassaraṃsi jotindo, buddhaṃ taṃ paṇamāmyahaṃ.1068.</p>\r\n<p class="gatha1">Puṇṇindu sadisaditto, paññāobhāso bhāsuro;</p>\r\n<p class="gathalast">Mahāmohatamaṃ bhinno, buddhaṃ taṃ paṇamāmyahaṃ.1069.</p>\r\n<p class="gatha1">Mohaamāvasīnāsī, mahādicco mahappabho;</p>\r\n<p class="gathalast">Sabbalokaṃ pabhāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1070.</p>\r\n<p class="gatha1">Aṅgāriva accimanto, vijjādīpo rasmidharo;</p>\r\n<p class="gathalast">Mohatimisaṃ dhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.1071.</p>\r\n<p class="gatha1">Tamacchanne bhave sabbe, ñāṇālokena mocayī;</p>\r\n<p class="gathalast">Dhammajotiṃ ujjotesi, buddhaṃ taṃ paṇamāmyahaṃ.1072.</p>\r\n<p class="gatha1">Avijjāndhakārahantā, sabbaññū sabbato pabho;</p>\r\n<p class="gathalast">Vijjāyanaṃ pabhāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1073.</p>\r\n<p class="gatha1">Sabbatamantarahito, sabbathā sabbato pabho;</p>\r\n<p class="gathalast">Sabbasaccaṃ pakāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1074.</p>\r\n<p class="gatha1">Sabbalokānukampāya, sabbaloke jotiṅkaro;</p>\r\n<p class="gathalast">Sabbaloke tamoharo, buddhaṃ taṃ paṇamāmyahaṃ.1075.</p>\r\n<p class="gatha1">Sabbaññutaṃ pakāsesi, sabbassa dassāvī isi;</p>\r\n<p class="gathalast">Mohamūlaṃ viddhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.1076.</p>\r\n<p class="gatha1">Jalanto ñāṇatejena, tikkhatejo atisayo;</p>\r\n<p class="gathalast">Ñāṇaṃsumālī ātāpī, buddhaṃ taṃ paṇamāmyahaṃ.1077.</p>\r\n<p class="gatha1">Mohatamaṃ vināsesi, dhammajotipakāsako;</p>\r\n<p class="gathalast">Bahujane pahāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1078.</p>\r\n<p class="gatha1">Ghoratamācchannaloke, mahāñāṇena mocayī;</p>\r\n<p class="gathalast">Dhammālokaṃ pakāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1079.</p>\r\n<p class="gatha1">Karuṇāruṇo jotido, sabbasokapanūdano;</p>\r\n<p class="gathalast">Taṇhātimiraṃ dhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.1080.</p>\r\n<p class="gatha1">Tamācchanne sabbaloke, dhammālokakaro pabhū;</p>\r\n<p class="gathalast">Avijjābhantibhañjako, buddhaṃ taṃ paṇamāmyahaṃ.1081.</p>\r\n<p class="gatha1">Avijjāvaraṇaṃ chetvā, vijjālokapabhāsako;</p>\r\n<p class="gathalast">Paññāppabhā pabhaṃkārī, buddhaṃ taṃ paṇamāmyahaṃ.1082.</p>\r\n<p class="gatha1">Ghoratamaṃ niddhaṃsesi, viddhaṃsesi tayo bhave;</p>\r\n<p class="gathalast">Narādicco varādicco, buddhaṃ taṃ paṇamāmyahaṃ.1083.</p>\r\n<p class="gatha1">Ñāṇajotiṃ pajjotesi, mohāvaraṇanāsako;</p>\r\n<p class="gathalast">Dhammappabhaṃ pabhāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1084.</p>\r\n<p class="gatha1">Dhammajotiṃ vijotesi, pāpatamaniddhaṃsako;</p>\r\n<p class="gathalast">Mokkhāyanaṃ pakāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1085.</p>\r\n<p class="gatha1">Dhammadīpaṃ padīpesi, pāpāvaraṇa chedako;</p>\r\n<p class="gathalast">Muttipathaṃ paññāpesi, buddhaṃ taṃ paṇamāmyahaṃ.1086.</p>\r\n<p class="gatha1">Paññābhānu paññāppabho, uddhataṃ andhatamaṃ haro;</p>\r\n<p class="gathalast">Lokālokaṃ pakāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1087.</p>\r\n<p class="gatha1">Dhammadīpapajjalito, mohatamasandālako;</p>\r\n<p class="gathalast">Varadhammamujjotesi, buddhaṃ taṃ paṇamāmyahaṃ.1088.</p>\r\n<p class="gatha1">Paññā’lokaṃ pajjalesi, tamakkhandhappadālako;</p>\r\n<p class="gathalast">Pañcakkhandhaṃ nirodhesi, buddhaṃ taṃ paṇamāmyahaṃ.1089.</p>\r\n<p class="gatha1">Dhammabhānu bhabbappabho, sabbañāṇatamaṃ hato;</p>\r\n<p class="gathalast">Ujjalo jotijjalito, buddhaṃ taṃ paṇamāmyahaṃ.1090.</p>\r\n<p class="gatha1">Paññājotippabhandharo, sabbalokālokakaro;</p>\r\n<p class="gathalast">Mokkhadhammaṃ pabhāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1091.</p>\r\n<p class="gatha1">Avijjāndhakāraṃ hantā, dhammālokappakāsako;</p>\r\n<p class="gathalast">Pabhākārī ābhākārī, buddhaṃ taṃ paṇamāmyahaṃ.1092.</p>\r\n<p class="gatha1">Ojassī ojasampanno, tapassī tapasekharo;</p>\r\n<p class="gathalast">Tejabhūsito tejassī, buddhaṃ taṃ paṇamāmyahaṃ.1093.</p>\r\n<p class="gatha1">Dhammamaṅgalasammato, dhammābhāparimaṇḍito;</p>\r\n<p class="gathalast">Dhammaṃ suṭṭhuparivutto, buddhaṃ taṃ paṇamāmyahaṃ.1094.</p>\r\n<p class="gatha1">Paññābhāparimaṇḍito, paññābhāparidīpako;</p>\r\n<p class="gathalast">Paññaṃsumā paññaccimā, buddhaṃ taṃ paṇamāmyahaṃ.1095.</p>\r\n<p class="gatha1">Aṃsumanto ābhāvanto, tejavanto jutikaro;</p>\r\n<p class="gathalast">Pabhāvanto sobhāvanto, buddhaṃ taṃ paṇamāmyahaṃ.1096.</p>\r\n<p class="gatha1">Koṭibhānusamappabho, candimā’va samujjalo;</p>\r\n<p class="gathalast">Abhikkanto adhikkanto, buddhaṃ taṃ paṇamāmyahaṃ.1097.</p>\r\n<p class="gatha1">Sabbakaṅkhā nivāresi, mārasena vidhūpako;</p>\r\n<p class="gathalast">Sabbalokaṃ obhāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1098.</p>\r\n<p class="gatha1">Amitābho atulābho, loke amitālokadā;</p>\r\n<p class="gathalast">Amitojo atulojo, buddhaṃ taṃ paṇamāmyahaṃ.1099.</p>\r\n<p class="gatha1">Sabbalokaṃ pabhāsesi, abbhāmutto’va candimā;</p>\r\n<p class="gathalast">Suddhaṃ saddhammaṃ dīpesi, buddhaṃ taṃ paṇamāmyahaṃ.1100.</p>\r\n<p class="gatha1">Aggi yathā pajjalito, devatā’va virocayi;</p>\r\n<p class="gathalast">Mahā’vijjā viddhaṃsesi, buddhaṃ taṃ paṇamāmyahaṃ.1101.</p>\r\n<p class="gatha1">Santidhammaṃ pakāsesi, suriyo tamacchaddako;</p>\r\n<p class="gathalast">Santataṃ santiṃ pasāresi, buddhaṃ taṃ paṇamāmyahaṃ.1102.</p>\r\n<p class="gatha1">Lokamitto lokahito, lokabandhu lokasakhā;</p>\r\n<p class="gathalast">Tiloke ālokakaro, buddhaṃ taṃ paṇamāmyahaṃ.1103.</p>\r\n<p class="gatha1">Pītipassaddhikhāyī ca, pītirasapāyī bahū;</p>\r\n<p class="gathalast">Pītibhakkhī pītimano, buddhaṃ taṃ paṇamāmyahaṃ.1104.</p>\r\n<p class="gatha1">Pītipāmojjajanako, paramaṃ sukhaṃ bhojako;</p>\r\n<p class="gathalast">Paṭisallānanirato, buddhaṃ taṃ paṇamāmyahaṃ.1105.</p>\r\n<p class="gatha1">Bodhitejo bodhiraṃsi, bodhippabhāya maṇḍito;</p>\r\n<p class="gathalast">Brahmatejo brahmaraṃsi, buddhaṃ taṃ paṇamāmyahaṃ.1106.</p>\r\n<p class="gatha1">Brahmakāyo brahmarūpo, brahmadhammo yo brāhmaṇo;</p>\r\n<p class="gathalast">Brahmapatto brahmabhūto, buddhaṃ taṃ paṇamāmyahaṃ.1107.</p>\r\n<p class="gatha1">Brahmapañño brahmacārī, brahmavihārī brahmaññū;</p>\r\n<p class="gathalast">Brahmacakkhu dhammacakkhu, buddhaṃ taṃ paṇamāmyahaṃ.1108.</p>\r\n<p class="gatha1">Bahūpakārī mettāvā, mahākāruññamānaso;</p>\r\n<p class="gathalast">Muttinayaṃ niddesesi, buddhaṃ taṃ paṇamāmyahaṃ.1109.</p>\r\n<p class="gatha1">Bahū bhavoghā tāresi, sudakkho nāviko yathā;</p>\r\n<p class="gathalast">Mettāvegaparakkamī, buddhaṃ taṃ paṇamāmyahaṃ.1110.</p>\r\n<p class="gatha1">Kalyāṇakāmī nibbano, anantakaruṇālayo;</p>\r\n<p class="gathalast">Anūnaka kalyāṇaññū, buddhaṃ taṃ paṇamāmyahaṃ.1111.</p>\r\n<p class="gatha1">Cakkhudado ñāṇadado, sabbalokānukampako;</p>\r\n<p class="gathalast">Karuṇāpuṇṇamānaso, buddhaṃ taṃ paṇamāmyahaṃ.1112.</p>\r\n<p class="gatha1">Kalyāṇacitto acaṇḍo, mettāmano akkodhano;</p>\r\n<p class="gathalast">Visuddhacitto akkoso, buddhaṃ taṃ paṇamāmyahaṃ.1113.</p>\r\n<p class="gatha1">Kalyāṇakārī kalyāṇo, kalyāṇapathanāyako;</p>\r\n<p class="gathalast">Kalyāṇapāramīppatto, buddhaṃ taṃ paṇamāmyahaṃ.1114.</p>\r\n<p class="gatha1">Nissīmakaruṇākārī, ussannakaruṇānidhī;</p>\r\n<p class="gathalast">Gaṃbhīrakaruṇālayo, buddhaṃ taṃ paṇamāmyahaṃ.1115.</p>\r\n<p class="gatha1">Mahākāruṇiko dhīro, karuṇāñāṇasāgaro;</p>\r\n<p class="gathalast">Atīvakaruṇākārī, buddhaṃ taṃ paṇamāmyahaṃ.1116.</p>\r\n<p class="gatha1">Kalyāṇakārī kāruñño, kalyāṇamitto mettavā;</p>\r\n<p class="gathalast">Kalyāṇaṃ dhammaṃ desesi, buddhaṃ taṃ paṇamāmyahaṃ.1117.</p>\r\n<p class="gatha1">Karuṇāhadayo nātho, khemino khemamānaso;</p>\r\n<p class="gathalast">Muditacitto mādako, buddhaṃ taṃ paṇamāmyahaṃ.1118.</p>\r\n<p class="gatha1">Cakkavattiṃ vivajjitvā, patto sabbaññutaṃ budho;</p>\r\n<p class="gathalast">Nissesaṃ karuṇāsindhu, buddhaṃ taṃ paṇamāmyahaṃ.1119.</p>\r\n<p class="gatha1">Kodhadosamahāaggiṃ, mettodakena siñcayī;</p>\r\n<p class="gathalast">Sabbalokassa hitakārī, buddhaṃ taṃ paṇamāmyahaṃ.1120.</p>\r\n<p class="gatha1">Karuṇāsītalacitto, sabbasattānukampako;</p>\r\n<p class="gathalast">Kalyāṇadhammena yutto, buddhaṃ taṃ paṇamāmyahaṃ.1121.</p>\r\n<p class="gatha1">Dayodadhi dayānidhi, dayālu dayāsāgaro;</p>\r\n<p class="gathalast">Dayādhipo dayānātho, buddhaṃ taṃ paṇamāmyahaṃ.1122.</p>\r\n<p class="gatha1">Mahādayo mahodayo, mahāsayo mahesi yo;</p>\r\n<p class="gathalast">Sadādayo sadāsayo, buddhaṃ taṃ paṇamāmyahaṃ.1123.</p>\r\n<p class="gatha1">Hitado sabbasattānaṃ, sabbesānaṃ sukhadado;</p>\r\n<p class="gathalast">Santido sabbapāṇānaṃ, buddhaṃ taṃ paṇamāmyahaṃ.1124.</p>\r\n<p class="gatha1">Pajjunnoriva bhūtāni, dhammameghena vassitā;</p>\r\n<p class="gathalast">Santisukhakaro loke, buddhaṃ taṃ paṇamāmyahaṃ.1125.</p>\r\n<p class="gatha1">Parapekkhī parasevī, parasukhakārī isi;</p>\r\n<p class="gathalast">Parahitesī paratthī, buddhaṃ taṃ paṇamāmyahaṃ.1126.</p>\r\n<p class="gatha1">Catusaccaṃ pakāsesi, anukampāya pāṇinaṃ;</p>\r\n<p class="gathalast">Bahūjane santāresi, buddhaṃ taṃ paṇamāmyahaṃ.1127.</p>\r\n<p class="gatha1">Sadā santo santidāyī, sukhito sukhadāyako;</p>\r\n<p class="gathalast">Dhammameghaṃ pavassesi, buddhaṃ taṃ paṇamāmyahaṃ.1128.</p>\r\n<p class="gatha1">Bahujanahitatthāya, anekabhavaṃ saṃsari;</p>\r\n<p class="gathalast">Bahujanaṃ uddhāresi, buddhaṃ taṃ paṇamāmyahaṃ.1129.</p>\r\n<p class="gatha1">Bahūnaṃ hitasukhāya, paripūresi pāramī;</p>\r\n<p class="gathalast">Mokkhamaggaṃ gavesesi, buddhaṃ taṃ paṇamāmyahaṃ.1130.</p>\r\n<p class="gatha1">Sabbattha samattacitto, sabbapāṇānukampako;</p>\r\n<p class="gathalast">Sabbadā karuññacitto, buddhaṃ taṃ paṇamāmyahaṃ.1131.</p>\r\n<p class="gatha1">Sabbamitto sabbasakho, sabbabhūtānukampako;</p>\r\n<p class="gathalast">Sabbasattahitakaro, buddhaṃ taṃ paṇamāmyahaṃ.1132.</p>\r\n<p class="gatha1">Sabbesānaṃ hitacintī, sabbesānaṃ sukhāvaho;</p>\r\n<p class="gathalast">Sabbesānaṃ anukampī, buddhaṃ taṃ paṇamāmyahaṃ.1133.</p>\r\n<p class="gatha1">Sampuṇṇasukumāraṅgo, aṅgapaccaṅga sobhano;</p>\r\n<p class="gathalast">Vaṇṇanīyo vandanīyo, buddhaṃ taṃ paṇamāmyahaṃ.1134.</p>\r\n<p class="gatha1">Sabbasobhā susobhito, sabbamahimāmaṇḍito;</p>\r\n<p class="gathalast">Sabbapajānaṃ vallabho, buddhaṃ taṃ paṇamāmyahaṃ.1135.</p>\r\n<p class="gatha1">Ruciro abhiruciro, abhirūpo surūpavā;</p>\r\n<p class="gathalast">Anomavaṇṇo suvaṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1136.</p>\r\n<p class="gatha1">Kantavaṇṇo kantarūpo, kantakitti kantayaso;</p>\r\n<p class="gathalast">Kantajuṇho kantajoti, buddhaṃ taṃ paṇamāmyahaṃ.1137.</p>\r\n<p class="gatha1">Chaḷabhiññāppatto dhīmā, chavivaṇṇo sudassano;</p>\r\n<p class="gathalast">Chabbaṇṇaraṃsī sobhano, buddhaṃ taṃ paṇamāmyahaṃ.1138.</p>\r\n<p class="gatha1">Kañcanagghiyasaṅkāso, niddoso kanakattaco;</p>\r\n<p class="gathalast">Soṇṇānano suruciro, buddhaṃ taṃ paṇamāmyahaṃ.1139.</p>\r\n<p class="gatha1">Pabhāhi anurañjesi, mokkhapanthapakāsako;</p>\r\n<p class="gathalast">Dhammarasmiparikkhitto, buddhaṃ taṃ paṇamāmyahaṃ.1140.</p>\r\n<p class="gatha1">Sīhahanu’sabhakkhandho, ñāṇanibhāmaṇḍito;</p>\r\n<p class="gathalast">Sumukho sundaro eso, buddhaṃ taṃ paṇamāmyahaṃ.1141.</p>\r\n<p class="gatha1">Kantiyutto kantidatto, manojo manamodano;</p>\r\n<p class="gathalast">Sabbajane pamodesi, buddhaṃ taṃ paṇamāmyahaṃ.1142.</p>\r\n<p class="gatha1">Suvaṇṇo suvaṇṇavaṇṇo, hemavaṇṇo vaṇṇuttamo;</p>\r\n<p class="gathalast">Hiraññavaṇṇo hemāṃsu, buddhaṃ taṃ paṇamāmyahaṃ.1143.</p>\r\n<p class="gatha1">Kantakāyo kantasobho, kantaābho kantapabho;</p>\r\n<p class="gathalast">Kantadassano kantimā, buddhaṃ taṃ paṇamāmyahaṃ.1144.</p>\r\n<p class="gatha1">Mududhavaluṇṇo cāpi, aviraḷadantāvalī;</p>\r\n<p class="gathalast">Ussaṅkhapādo bhagavā, buddhaṃ taṃ paṇamāmyahaṃ.1145.</p>\r\n<p class="gatha1">Dīghatanu dīghajivho, dīghabāhu dīghaṅgulī;</p>\r\n<p class="gathalast">Sudaḷhahatthacaraṇo, buddhaṃ taṃ paṇamāmyahaṃ.1146.</p>\r\n<p class="gatha1">Brahmaghoso eṇijaṅgho, ujudeho brahmāsamo;</p>\r\n<p class="gathalast">Rasaññū rasaggasaggī, buddhaṃ taṃ paṇamāmyahaṃ.1147.</p>\r\n<p class="gatha1">Nīlakkhī dīghapaṇhī ca, kanakatuṅganāsiko;</p>\r\n<p class="gathalast">Cakkavaraṅkitapādo, buddhaṃ taṃ paṇamāmyahaṃ.1148.</p>\r\n<p class="gatha1">Cattālīsasamadanto, taruṇavaccha pakhumo;</p>\r\n<p class="gathalast">Sīho’va pubbaddhakāyo, buddhaṃ taṃ paṇamāmyahaṃ.1149.</p>\r\n<p class="gatha1">Lomakūpekalomo ca, kañcanasadisattaco;</p>\r\n<p class="gathalast">Odātadāḍhāsampanno, buddhaṃ taṃ paṇamāmyahaṃ.1150.</p>\r\n<p class="gatha1">Sunīlamuddhaggalomo, byāmappabhāsumaṇḍito;</p>\r\n<p class="gathalast">Jālikahatthacaraṇo, buddhaṃ taṃ paṇamāmyahaṃ.1151.</p>\r\n<p class="gatha1">Kosohitavatthaguyho, suppatiṭṭhitacaraṇo;</p>\r\n<p class="gathalast">Sīhahanu’ṇhīsasīso, buddhaṃ taṃ paṇamāmyahaṃ.1152.</p>\r\n<p class="gatha1">Sabbamahāpurisaṅgo, battiṃsalakkhaṇadharo;</p>\r\n<p class="gathalast">Asītānubyañjano yo, buddhaṃ taṃ paṇamāmyahaṃ.1153.</p>\r\n<p class="gatha1">Ativiya manuñño ca, ativiya manoramo;</p>\r\n<p class="gathalast">Ativiya manohārī, buddhaṃ taṃ paṇamāmyahaṃ.1154.</p>\r\n<p class="gatha1">Accantakantimā kanto, sobhano piyadassano;</p>\r\n<p class="gathalast">Battiṃsalakkhaṇapuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1155.</p>\r\n<p class="gatha1">Kantābho kañcanavaṇṇo, kañcanānanalocano;</p>\r\n<p class="gathalast">Kañcanācalasaṅkāso, buddhaṃ taṃ paṇamāmyahaṃ.1156.</p>\r\n<p class="gatha1">Kamanīyo kantanīyo, kañcanaṃ’va jutikaro;</p>\r\n<p class="gathalast">Padumapattakkho rūpī, buddhaṃ taṃ paṇamāmyahaṃ.1157.</p>\r\n<p class="gatha1">Kalyāṇadassano juṇho, ditto’va kanakācalo;</p>\r\n<p class="gathalast">Jutimā dibbadassano, buddhaṃ taṃ paṇamāmyahaṃ.1158.</p>\r\n<p class="gatha1">Varalakkhaṇalaṅkito, sabbato sabbasundaro;</p>\r\n<p class="gathalast">Somma sabbaṅgasobhano, buddhaṃ taṃ paṇamāmyahaṃ.1159.</p>\r\n<p class="gatha1">Guṇānamākaro pujjo, karavīkarutassaro;</p>\r\n<p class="gathalast">Varalakkhaṇaākiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1160.</p>\r\n<p class="gatha1">Mahākāyo brahākāyo, brahmakāyo kāyaujū;</p>\r\n<p class="gathalast">Kantikāyo santikāyo, buddhaṃ taṃ paṇamāmyahaṃ.1161.</p>\r\n<p class="gatha1">Accantasukhumālaṅgo, accantamudulatanu;</p>\r\n<p class="gathalast">Accantasundaro sāmī, buddhaṃ taṃ paṇamāmyahaṃ.1162.</p>\r\n<p class="gatha1">Jananetto janamoḷi, pasannanayanānano;</p>\r\n<p class="gathalast">Kumudānanalocano, buddhaṃ taṃ paṇamāmyahaṃ.1163.</p>\r\n<p class="gatha1">Pasannacitto pasādo, paṇīto atisobhito;</p>\r\n<p class="gathalast">Pariyodāto paramo, buddhaṃ taṃ paṇamāmyahaṃ.1164.</p>\r\n<p class="gatha1">Bhabbarūpo bhaddarūpo, bhaddabhāṇī bhaddamukho;</p>\r\n<p class="gathalast">Bhassaro bhāsuro bhiyyo, buddhaṃ taṃ paṇamāmyahaṃ.1165.</p>\r\n<p class="gatha1">Mañjughoso mañjuvāṇī, mañjubhāṇī mañjussaro;</p>\r\n<p class="gathalast">Mañjubhāsī mudubhāsī, buddhaṃ taṃ paṇamāmyahaṃ.1166.</p>\r\n<p class="gatha1">Mudukāyo muducitto, muduko mudulakkhaṇo;</p>\r\n<p class="gathalast">Mudumano mudubhaṇo, buddhaṃ taṃ paṇamāmyahaṃ.1167.</p>\r\n<p class="gatha1">Varalakkhaṇasampanno, sabbaṅgasamannāgato;</p>\r\n<p class="gathalast">Sabbaloke sabbuttamo, buddhaṃ taṃ paṇamāmyahaṃ.1168.</p>\r\n<p class="gatha1">Susobhito sobhāyutto, santibhūsanabhūsito;</p>\r\n<p class="gathalast">Tilokassa tuṅgaketuṃ, buddhaṃ taṃ paṇamāmyahaṃ.1169.</p>\r\n<p class="gatha1">Ghorasaṃsāroghatiṇṇo, mokkhalaṅkāra’laṅkato;</p>\r\n<p class="gathalast">Susobhayutto suguṇo, buddhaṃ taṃ paṇamāmyahaṃ.1170.</p>\r\n<p class="gatha1">Sabbaguṇasusampanno, sīlālaṅkāra’laṅkato;</p>\r\n<p class="gathalast">Saddhammaratanaseṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.1171.</p>\r\n<p class="gatha1">Anantakittivaṇṇo yo, sabbasaṃsāravandito;</p>\r\n<p class="gathalast">Pasiddho vissuto pujjo, buddhaṃ taṃ paṇamāmyahaṃ.1172.</p>\r\n<p class="gatha1">Suvandito sabbaloke, sabbaloke sambhāvito;</p>\r\n<p class="gathalast">Sammānito sabbaloke, buddhaṃ taṃ paṇamāmyahaṃ.1173.</p>\r\n<p class="gatha1">Devādhidevavandito, mahādevavināyako;</p>\r\n<p class="gathalast">Tibhavavallabho khyāto, buddhaṃ taṃ paṇamāmyahaṃ.1174.</p>\r\n<p class="gatha1">Dassaneyyo thomaneyyo, pasaṃsaneyyo pāmokkho;</p>\r\n<p class="gathalast">Vaṇṇanīyo vandanīyo, buddhaṃ taṃ paṇamāmyahaṃ.1175.</p>\r\n<p class="gatha1">Pasādanīyo pasīdo, pāsādiko passaddhiko;</p>\r\n<p class="gathalast">Pūjanīyo accanīyo, buddhaṃ taṃ paṇamāmyahaṃ.1176.</p>\r\n<p class="gatha1">Apacito sakkārito, pathito abhivādito;</p>\r\n<p class="gathalast">Nissīmasilāghāppatto, buddhaṃ taṃ paṇamāmyahaṃ.1177.</p>\r\n<p class="gatha1">Tibhavavandito bhiyyo, oraṃ tīraṃ pāraṅgato;</p>\r\n<p class="gathalast">Anagho paññasekharo, buddhaṃ taṃ paṇamāmyahaṃ.1178.</p>\r\n<p class="gatha1">Balavanto phalavanto, bahūhi bahumānito;</p>\r\n<p class="gathalast">Bahūpakārī bodhindo, buddhaṃ taṃ paṇamāmyahaṃ.1179.</p>\r\n<p class="gatha1">Sabbaloke namassito, sabbaloke sammānito;</p>\r\n<p class="gathalast">Manujāmarasakkato, buddhaṃ taṃ paṇamāmyahaṃ.1180.</p>\r\n<p class="gatha1">Lokānaṃ uttamo pujjo, susakkato sagāravo;</p>\r\n<p class="gathalast">Vaṇṇakittibhato kanto, buddhaṃ taṃ paṇamāmyahaṃ.1181.</p>\r\n<p class="gatha1">Khyāto pakhyāto sukhyāto, vissavikhyāto vandito;</p>\r\n<p class="gathalast">Vaṇṇādhiko vitthāriko, buddhaṃ taṃ paṇamāmyahaṃ.1182.</p>\r\n<p class="gatha1">Sabbaseṭṭho sabbajeṭṭho, sabbasuddho sabbuttamo;</p>\r\n<p class="gathalast">Sabbavandito mānito, buddhaṃ taṃ paṇamāmyahaṃ.1183.</p>\r\n<p class="gatha1">Sabbe devenuvattesi, sabbadevavināyako;</p>\r\n<p class="gathalast">Tilokamahito nātho, buddhaṃ taṃ paṇamāmyahaṃ.1184.</p>\r\n<p class="gatha1">Sabbalokassa vinetā, sabbalokatikicchako;</p>\r\n<p class="gathalast">Sabbesaṃ seṭṭho dhammaṭṭho, buddhaṃ taṃ paṇamāmyahaṃ.1185.</p>\r\n<p class="gatha1">Sabbalokābhibhū vīro, sabbalokuttamo jino;</p>\r\n<p class="gathalast">Vissuto sabbalokamhi, buddhaṃ taṃ paṇamāmyahaṃ.1186.</p>\r\n<p class="gatha1">Sabbābhiññāparipuṇṇo, sabbalokasmiṃ vissuto;</p>\r\n<p class="gathalast">Samantacakkhū pasiddho, buddhaṃ taṃ paṇamāmyahaṃ.1187.</p>\r\n<p class="gatha1">Sabbalokahito nātho, sabbalokasukhāvaho;</p>\r\n<p class="gathalast">Supūjito sabbaloke, buddhaṃ taṃ paṇamāmyahaṃ.1188.</p>\r\n<p class="gatha1">Sabbicchaṃ anicchanto, ucchinnachando sabbadhi;</p>\r\n<p class="gathalast">Mahātaṇhāṇṇavuttiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1189.</p>\r\n<p class="gatha1">Yathāpekkhī tathā’kkhāsi, sabbathā saccamānaso;</p>\r\n<p class="gathalast">Avitathaṃ viññāpesi, buddhaṃ taṃ paṇamāmyahaṃ.1190.</p>\r\n<p class="gatha1">Ñāṇaññū ñāṇasampanno, paññāsampanno paññavā;</p>\r\n<p class="gathalast">Dhammojo dhammappasanto, buddhaṃ taṃ paṇamāmyahaṃ.1191.</p>\r\n<p class="gatha1">Suguṇesu susampanno, sādhu sappuriso sudhī;</p>\r\n<p class="gathalast">Mahāsumatisāgaro, buddhaṃ taṃ paṇamāmyahaṃ.1192.</p>\r\n<p class="gatha1">Nāmadassī rūpadassī, tapassī ca taponidhi;</p>\r\n<p class="gathalast">Yathābhūtaṃ vipassī ca, buddhaṃ taṃ paṇamāmyahaṃ.1193.</p>\r\n<p class="gatha1">Upeto buddhadhammehi, aṭṭhārasahi nāyako;</p>\r\n<p class="gathalast">Iddhippatto mahāsiddho, buddhaṃ taṃ paṇamāmyahaṃ.1194.</p>\r\n<p class="gatha1">Sīlañca samādhiṃ patto, patto paññaṃ niyyānikaṃ;</p>\r\n<p class="gathalast">Paripuṇṇadhammappatto, buddhaṃ taṃ paṇamāmyahaṃ.1195.</p>\r\n<p class="gatha1">Parisuddhadhammappatto, sabbavipallāsanudo;</p>\r\n<p class="gathalast">Tibhavāṇṇavanittiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1196.</p>\r\n<p class="gatha1">Rāgānalaṃ nibbāpesi, sītibhūto sītaṅkaro;</p>\r\n<p class="gathalast">Santisudhā vassāpesi, buddhaṃ taṃ paṇamāmyahaṃ.1197.</p>\r\n<p class="gatha1">Titthiye sunimmadditvā, ṭhapetvā micchāmaññitaṃ;</p>\r\n<p class="gathalast">Saccadhamme patiṭṭhesi, buddhaṃ taṃ paṇamāmyahaṃ.1198.</p>\r\n<p class="gatha1">Vimutto mānusāsattiṃ, dibbāsattiṃ upaccago;</p>\r\n<p class="gathalast">Sabbāsattivinimmutto, buddhaṃ taṃ paṇamāmyahaṃ.1199.</p>\r\n<p class="gatha1">Tibhavetimiraharo, bodhiñāṇappabhandharo;</p>\r\n<p class="gathalast">Dhammappadīpako bhabbo, buddhaṃ taṃ paṇamāmyahaṃ.1200.</p>\r\n<p class="gatha1">Jhāniko jhānasampanno, aññāṇadheyyadhaṃsako;</p>\r\n<p class="gathalast">Suṭṭhujhānī mahājhānī, buddhaṃ taṃ paṇamāmyahaṃ.1201.</p>\r\n<p class="gatha1">Taṇhakkhayappatto nāgo, bhavanettipacchindako;</p>\r\n<p class="gathalast">Āsattiṃ parimaddesi, buddhaṃ taṃ paṇamāmyahaṃ.1202.</p>\r\n<p class="gatha1">Sabbakammakilesāni, anavasesa vāhayī;</p>\r\n<p class="gathalast">Dhammagaṅgā pavāhesi, buddhaṃ taṃ paṇamāmyahaṃ.1203.</p>\r\n<p class="gatha1">Damappatto yamappatto, samappatto samācaro;</p>\r\n<p class="gathalast">Dhuvaṃ sassataṃ sampatto, buddhaṃ taṃ paṇamāmyahaṃ.1204.</p>\r\n<p class="gatha1">Buddhimā mutimā ceva, matimā ca matissaro;</p>\r\n<p class="gathalast">Muttimā mettamānaso, buddhaṃ taṃ paṇamāmyahaṃ.1205.</p>\r\n<p class="gatha1">Ṭhānāṭhānesu kusalo, dhammesu atikovido;</p>\r\n<p class="gathalast">Laddhamedho sumedhāvī, buddhaṃ taṃ paṇamāmyahaṃ.1206.</p>\r\n<p class="gatha1">Bhavadukkhoghanittiṇṇo, taṇhāpahānapāragū;</p>\r\n<p class="gathalast">Aññāṇanisā nāsesi, buddhaṃ taṃ paṇamāmyahaṃ.1207.</p>\r\n<p class="gatha1">Dhīrahadayo dhorayho, saṃvuto saṃyatamano;</p>\r\n<p class="gathalast">Guttindriyo guttamāno, buddhaṃ taṃ paṇamāmyahaṃ.1208.</p>\r\n<p class="gatha1">Susaṃvuto santindriyo, santuṭṭho susamāhito;</p>\r\n<p class="gathalast">Vijjācaraṇasampuṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1209.</p>\r\n<p class="gatha1">Jāticakkaṃ vicuṇṇesi, natthi dāni punabbhavo;</p>\r\n<p class="gathalast">Tilokaoghanittiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1210.</p>\r\n<p class="gatha1">Asaṃsaṭṭho agārehi, anāgārehi cūbhayaṃ;</p>\r\n<p class="gathalast">Anokasārī asaṅgo, buddhaṃ taṃ paṇamāmyahaṃ.1211.</p>\r\n<p class="gatha1">Pahīnajātimaraṇo, anupādāya nibbuto;</p>\r\n<p class="gathalast">Saṃsārasāgaraṃ tiṇṇo, buddhaṃ taṃ paṇamāmyahaṃ.1212.</p>\r\n<p class="gatha1">Maggagavesī maggagū, maggakkhāto tathāgato;</p>\r\n<p class="gathalast">Amatamaggaṃ dassāvī, buddhaṃ taṃ paṇamāmyahaṃ.1213.</p>\r\n<p class="gatha1">Yathā adakkhi akkhāsi, bhagavā bhūrimedhaso;</p>\r\n<p class="gathalast">Nikkāmo nimmalo nātho, buddhaṃ taṃ paṇamāmyahaṃ.1214.</p>\r\n<p class="gatha1">Moneyyaseṭṭho munindo, ratiṃ ca aratiṃ cuto;</p>\r\n<p class="gathalast">Bhayātīto bhavātīto, buddhaṃ taṃ paṇamāmyahaṃ.1215.</p>\r\n<p class="gatha1">Pabuddhapariṇāyako, atiuccupakārako;</p>\r\n<p class="gathalast">Sabboccasukhadāyako, buddhaṃ taṃ paṇamāmyahaṃ.1216.</p>\r\n<p class="gatha1">Amalino amaliccho, nimmalo malamajjano;</p>\r\n<p class="gathalast">Madamaddo vītamado, buddhaṃ taṃ paṇamāmyahaṃ.1217.</p>\r\n<p class="gatha1">Apacito namassito, pathito abhivādito;</p>\r\n<p class="gathalast">Devamanujaaccito, buddhaṃ taṃ paṇamāmyahaṃ.1218.</p>\r\n<p class="gatha1">Byāmappabhābhirucito, dvattiṃsalakkhaṇaddharo;</p>\r\n<p class="gathalast">Anubyañjanasampanno, buddhaṃ taṃ paṇamāmyahaṃ.1219.</p>\r\n<p class="gatha1">Adaṇḍena asatthena, dhammena anusāsayi;</p>\r\n<p class="gathalast">Uddharesi bahū satte, buddhaṃ taṃ paṇamāmyahaṃ.1220.</p>\r\n<p class="gatha1">Dhañño dhammasudhā pāyī, taṇhāvisaviddhaṃsako;</p>\r\n<p class="gathalast">Sabbāvijjaṃ vicuṇṇesi, buddhaṃ taṃ paṇamāmyahaṃ.1221.</p>\r\n<p class="gatha1">Dhañño nātho! Dhañño sāmī! Dhañño mārābhibhū munī!</p>\r\n<p class="gathalast">Dhañño vijitasaṅgāmo! Buddhaṃ taṃ paṇamāmyahaṃ.1222.</p>\r\n<p class="gatha1">Jhānārāmo jhānarato, dhammārāmo dhammarato;</p>\r\n<p class="gathalast">Aho! Aho! Pāraṅgato, buddhaṃ taṃ paṇamāmyahaṃ.1223.</p>\r\n<p class="gatha1">Puṇḍarīko’va nillitto, passa tassa visuddhataṃ;</p>\r\n<p class="gathalast">Alaggamānaso eko, buddhaṃ taṃ paṇamāmyahaṃ.1224.</p>\r\n<p class="gatha1">Aho! Buddho! Aho! Suddho! Aho! Saṃsuddhamānaso!</p>\r\n<p class="gathalast">Aho! Aho! Mettāsindhu! Buddhaṃ taṃ paṇamāmyahaṃ.1225.</p>\r\n<p class="gatha1">Sīlavisuddho, cittavisuddho, diṭṭhivisuddho namo namo;</p>\r\n<p class="gathalast">Dhammavihārī, maṅgalakārī, janahitakārī namo namo.</p>\r\n<p class="gatha1">Mahātapassī, dhammavipassī, akkhayadassī namo namo;</p>\r\n<p class="gathalast">Maggagavesī, lokahitesī, buddhamahesī namo namo.</p>','\r',char(13)),'\n',char(10)),'');