INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',6,replace(replace('<p class="bodytext">Aṃmo <a name="M0.0006"></a> niggahitaṃ jhalapehi.</p>\r\n<p class="bodytext"><a name="para83"></a><span class="paranum">83</span>, 67. Saralopo mādesapaccayādimhi saralope tu pakati.</p>\r\n<p class="bodytext"><a name="para84"></a><span class="paranum">84</span>, 144. Agho rassamekavacanayosvapi ca.</p>\r\n<p class="bodytext"><a name="para85"></a><span class="paranum">85</span>, 150. Na sismimanapuṃsakāni.</p>\r\n<p class="bodytext"><a name="para86"></a><span class="paranum">86</span>, 227. Ubhādito naminnaṃ.</p>\r\n<p class="bodytext"><a name="para87"></a><span class="paranum">87</span>, 231. Iṇṇamiṇṇannaṃ tīhi saṅkhyāhi.</p>\r\n<p class="bodytext"><a name="para88"></a><span class="paranum">88</span>, 147. Yāsu katanikāralopesu dīghaṃ.</p>\r\n<p class="bodytext"><a name="para89"></a><span class="paranum">89</span>, 87. Sunaṃhisu ca.</p>\r\n<p class="bodytext"><a name="para90"></a><span class="paranum">90</span>, 252. Pañcādīnamattaṃ.</p>\r\n<p class="bodytext"><a name="para91"></a><span class="paranum">91</span>, 194. Patissinīmhi.</p>\r\n<p class="bodytext"><a name="para92"></a><span class="paranum">92</span>, 100. Ntussanto yosu ca.</p>\r\n<p class="bodytext"><a name="para93"></a><span class="paranum">93</span>, 106. Sabbassa vā aṃsesu.</p>\r\n<p class="bodytext"><a name="para94"></a><span class="paranum">94</span>, 105. Simhi vā.</p>\r\n<p class="bodytext"><a name="para95"></a><span class="paranum">95</span>, 145. Aggissini.</p>\r\n<p class="bodytext"><a name="para96"></a><span class="paranum">96</span>, 148. Yosvakatarasso jho.</p>\r\n<p class="bodytext"><a name="para97"></a><span class="paranum">97</span>, 156. Vevosu lo ca.</p>\r\n<p class="bodytext"><a name="para98"></a><span class="paranum">98</span>, 189. Mātulādīnamānattamīkāre.</p>\r\n<p class="bodytext"><a name="para99"></a><span class="paranum">99</span>, 81. Smāhismi