INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',9,replace(replace('<p class="bodytext">Tumhamhākaṃ <a name="M0.0009"></a> tayi mayi.</p>\r\n<p class="bodytext"><a name="para140"></a><span class="paranum">140</span>, 232. Tvamahaṃ simhi ca.</p>\r\n<p class="bodytext"><a name="para141"></a><span class="paranum">141</span>, 241. Tava mama se.</p>\r\n<p class="bodytext"><a name="para142"></a><span class="paranum">142</span>, 242. Tuyhaṃ mayhañca.</p>\r\n<p class="bodytext"><a name="para143"></a><span class="paranum">143</span>, 235. Taṃmamaṃmhi.</p>\r\n<p class="bodytext"><a name="para144"></a><span class="paranum">144</span>, 234. Tavaṃmamañca na vā.</p>\r\n<p class="bodytext"><a name="para145"></a><span class="paranum">145</span>, 238. Nāmhi tayā mayā.</p>\r\n<p class="bodytext"><a name="para146"></a><span class="paranum">146</span>, 236. Tumhassa tuvaṃtvamamhi.</p>\r\n<p class="bodytext"><a name="para147"></a><span class="paranum">147</span>, 246. Padato dutiyācatutthīchaṭṭhīsu vo no.</p>\r\n<p class="bodytext"><a name="para148"></a><span class="paranum">148</span>, 247. Hemekavacanesu ca.</p>\r\n<p class="bodytext"><a name="para149"></a><span class="paranum">149</span>, 248. Na aṃmhi.</p>\r\n<p class="bodytext"><a name="para150"></a><span class="paranum">150</span>, 249. Vā tatiye ca.</p>\r\n<p class="bodytext"><a name="para151"></a><span class="paranum">151</span>, 250. Bahuvacanesu vo no.</p>\r\n<p class="bodytext"><a name="para152"></a><span class="paranum">152</span>, 136. Pumantassā simhi.</p>\r\n<p class="bodytext"><a name="para153"></a><span class="paranum">153</span>, 138. Amālapanekavacane.</p>\r\n<p class="bodytext"><a name="para154"></a><span class="paranum">154</span>, 0. Samāse ca vibhāsā.</p>\r\n<p class="bodytext"><a name="para155"></a><span class="paranum">155</span>, 137. Yosvāno.</p>\r\n<p class="bodytext"><a name="para156"></a><span class="paranum">156</span>, 142. Āne smiṃmhi vā.</p>\r\n<p class="